const hazhar: Translation = {
  info: {
    name: 'قورئانی پیرۆز وەرگێراوی هەژار موکریانی',
    author: 'هەژار موکریانی',
  },
  ayahs: [
    '\u0648\u06d5\u0646\u0627\u0648 \u062e\u0648\u062f\u0627 \u06a9\u06d5 \u062f\u06d5\u0647\u06d5\u0646\u062f\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5',
    '\u0633\u067e\u0627\u0633 \u0628\u06c6 \u062e\u0648\u0627 \u06a9\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062c\u06cc\u0647\u0627\u0646\u06d5.',
    '\u062f\u06d5\u0647\u0646\u062f\u06d5\u06cc\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u0627\u0648\u06d5\u0646\u06cc \u0695\u06c6\u0698\u06cc \u067e\u06d5\u0633\u06b5\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u062a\u06c6 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646 \u0648 \u067e\u0634\u062a\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u062a\u06c6 \u062f\u06d5\u0628\u06d5\u0633\u062a\u06cc\u0646.',
    '\u0644\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0634\u0627\u0631\u06d5\u0632\u0627\u0646 \u06a9\u06d5.',
    '\u0695\u06ce\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0686\u0627\u06a9\u06d5\u062a \u0648\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u0646\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0648\u06d5\u0628\u06d5\u0631 \u062a\u0648\u0695\u06d5\u06cc\u06cc \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u061b \u0646\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u0627\u0695\u06ce \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0645\u06cc\u0645.',
    '\u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0647\u06cc\u0686 \u06af\u06c6\u0645\u0627\u0646\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06d5\u061b \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u0646 \u0695\u06ce \u0634\u0627\u0646\u062f\u06d5\u0631\u06d5:',
    '\u0626\u06d5\u0648 \u0643\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u06d5\u062f\u06cc\u0627\u0631 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u0648\u06ce\u0698 \u062f\u06d5\u0643\u06d5\u0646 \u0648 \u0644\u06d5\u0648 \u0628\u0698\u06ce\u0648\u06d5\u0634 \u0643\u06d5 \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0648\u0646 \u062f\u06d5\u0628\u06d5\u062e\u0634\u0646',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0643\u06d5\u0628\u06d5\u0648 \u0643\u062a\u06ce\u0628\u06d5\u06cc \u0644\u06d5\u0645\u06d5 \u0695\u0627 \u0628\u06c6\u062a\u06c6 \u0647\u0627\u062a\u0648\u06d5\u0648 \u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0628\u06d5\u0631 \u0644\u06d5\u062a\u06c6 \u0628\u06d5\u0695\u06ce \u0643\u0631\u0627\u0648\u06d5 \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0644\u06d5 \u0647\u0627\u062a\u0646\u06cc \u0695\u06c6\u0698\u06cc \u067e\u06d5\u0633\u06b5\u0627\u0646\u06cc \u06af\u06c6\u0645\u0627\u0646\u06ce\u0643\u06cc\u0627\u0646 \u0646\u06cc\u06d5',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0695\u06ce\u0628\u06d5\u0631\u06cc\u0627\u0646\u06d5\u0648 \u062f\u06cc\u0627\u0631\u06d5 \u06af\u0631\u06c6\u06cc \u0695\u0632\u06af\u0627\u0631\u0627\u0646\u0646. ',
    '\u06af\u06d5\u0644\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc\u0634 \u0628\u062a\u0631\u0633\u06ce\u0646\u06cc\u060c \u06cc\u0627\u0646 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631\u0646\u06d5\u0647\u06ce\u0646\u06cc\u060c \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u06d5\u061b \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0647\u06ce\u0646\u0627\u0646 \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u062f\u06b5 \u0648 \u06af\u0648\u06ce\u06cc\u0627\u0646 \u0645\u06c6\u0631 \u06a9\u0631\u0627\u0648\u06d5 \u0648 \u067e\u06d5\u0631\u062f\u06d5\u06cc\u06d5\u06a9\u06cc\u0634 \u0628\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u06a9\u0634\u0627\u0648\u06d5 \u0648 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06d5.',
    '\u0644\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u06d5\u062f\u0627 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0626\u06ce\u0698\u0646: \u0628\u0627\u0648\u06d5\u0695\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0628\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0634\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646 \u062e\u0648\u062f\u0627 \u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0641\u0631\u06cc\u0648 \u062f\u06d5\u062f\u06d5\u0646\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0641\u0631\u06cc\u0648\u062f\u0627\u0648\u06d5 \u0648 \u067e\u06ce\u0634 \u0646\u0627\u062d\u06d5\u0633\u0646. [\u062d\u06d5\u0633\u06cc\u0646: \u067e\u06ce\u0632\u0627\u0646\u06cc\u0646\u061b \u0647\u06d5\u0633\u062a\u067e\u06ce\u06a9\u0631\u062f\u0646]',
    '\u062f\u06b5\u06cc\u0627\u0646 \u062f\u06d5\u063a\u06d5\u0632\u06ce\u06a9\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u062e\u0648\u0627 \u062f\u06d5\u063a\u06d5\u0632\u06cc \u067e\u062a\u0631 \u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0645 \u062f\u0631\u06c6 \u06a9\u0631\u062f\u0646\u06d5\u060c \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06d5. [\u062f\u06d5\u063a\u06d5\u0632: \u0646\u06d5\u062e\u06c6\u0634\u06cc\u061b \u0646\u0627\u0633\u0627\u063a\u06cc]',
    '\u0626\u06d5\u06af\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u067e\u06ce\u06cc\u0627\u0646 \u0628\u06ce\u0698\u06ce: \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0645\u06d5\u06a9\u06d5\u0646! \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u06cc\u0646. [\u0647\u06d5\u0631\u062f: \u0639\u06d5\u0631\u062f\u061b \u0632\u06d5\u0645\u06cc\u0646\u061b \u0632\u06d5\u0648\u06cc]',
    '\u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646\u060c \u06a9\u06d5 \u062e\u0631\u0627\u067e\u06d5\u06a9\u0627\u0631 \u0626\u06d5\u0648\u0627\u0646\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5\u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631 \u06af\u0627\u0634 \u06cc\u06d5\u06a9\u06ce \u067e\u06ce\u06cc\u0627\u0646 \u0628\u06ce\u0698\u06ce: \u0626\u06ce\u0648\u06d5\u0634 \u0633\u0627\u06cc\u0631\u06cc \u0626\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u06d5 \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646\u060c \u0626\u06ce\u0698\u0646: \u062c\u0627 \u0686\u06c6\u0646 \u0626\u06ce\u0645\u06d5 \u0648\u06d5\u06a9 \u0626\u06d5\u0648 \u0646\u06d5\u0641\u0627\u0645\u0627\u0646\u06d5 \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u06cc\u0646\u061f \u0628\u0627 \u0628\u0632\u0627\u0646\u0646\u060c \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u06d5\u0641\u0627\u0645\u06d5\u06a9\u0627\u0646\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a\u0627\u06cc\u06d5\u06a9 \u062f\u06d5\u06af\u06d5\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u060c \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06ce\u0645\u06d5\u0634 \u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0646. \u0647\u06d5\u0631 \u06af\u0627\u0634 \u0686\u0648\u0648\u0646\u06d5 \u0644\u0627\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646\u060c \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0648\u06d5\u0644 \u062e\u06c6\u062a\u0627\u0646\u06cc\u0646\u061b \u0647\u06d5\u0631 \u06af\u06d5\u0645\u06d5 \u0628\u06d5\u0645\u0627\u0646\u06d5 \u062f\u06d5\u06a9\u06d5\u06cc\u0646. [\u0648\u06d5\u0644: \u062f\u06d5\u06af\u06d5\u06b5\u061b \u0644\u06d5\u06af\u06d5\u06b5]',
    '\u062e\u0648\u062f\u0627 \u06af\u0627\u06b5\u062a\u06d5 \u0628\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0648 \u0628\u06d5\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0631\u0647\u06d5\u06b5\u062f\u0627 \u062f\u06d5\u06a9\u0627\u060c \u062a\u0627 \u0644\u06d5 \u0626\u06d5\u0648 \u0633\u06d5\u0631\u06a9\u06ce\u0634\u06cc\u06cc\u06d5\u062f\u0627 \u0633\u06d5\u0631\u06af\u06d5\u0631\u062f\u0627\u0646 \u0628\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u0648 \u0644\u06d5 \u0633\u06d5\u0648\u062f\u0627\u062f\u0627 \u062a\u06ce\u0634\u06a9\u0627\u0648\u0646 \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc\u0627\u0646 \u0628\u06d5\u062f\u06cc \u0646\u06d5\u06a9\u0631\u062f.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0695\u0627\u0633\u062a \u0628\u06d5 \u0645\u06d5\u0631\u06d5\u062f\u06cc \u06a9\u06d5\u0633\u0627\u0646\u06ce \u0686\u0648\u0648\u0646\u060c \u06a9\u06d5 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u067e\u06ce\u06a9\u0631\u062f\u0646\u06cc \u0626\u0627\u06af\u0631\u06ce\u06a9 \u0628\u0648\u0648\u0646\u061b \u0647\u06d5\u0631 \u06a9\u06d5 \u0695\u06c6\u0634\u0646\u0627\u06cc\u06cc \u0626\u0627\u06af\u0631 \u062f\u06d5\u0648\u0631\u0648\u0628\u06d5\u0631\u06cc \u0695\u06c6\u0634\u0646 \u06a9\u0631\u062f\u0646\u061b \u062e\u0648\u062f\u0627 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc\u06cc\u06d5\u06a9\u06d5\u06cc \u0628\u0631\u062f\u0646 \u0648 \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u06cc\u06cc\u06d5\u06a9\u06cc \u0648\u0627\u062f\u0627 \u0648\u06ce\u06b5\u06cc \u06a9\u0631\u062f\u0646\u060c \u0647\u06cc\u0686 \u0626\u0627\u0634\u062a\u06d5\u0628\u0627\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u062f\u06cc\u062a. [\u0645\u06d5\u0631\u06d5\u062f: \u062d\u0627\u06b5\u061b \u0628\u0627\u0631\u061b \u0645\u06d5\u0631\u06d5]',
    '\u06a9\u06d5\u0695\u0646\u060c \u06a9\u0648\u06ce\u0631\u0646\u060c \u06b5\u0627\u06b5 \u0648 \u067e\u0627\u06b5\u0646\u061b \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u067e\u0634\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u06cc\u0627 \u0646\u06d5 [\u0647\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u06a9\u06d5] \u0628\u0627\u0631\u0627\u0646\u06cc \u0628\u06d5 \u0644\u0648\u0648\u0632\u06d5\u0648\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0647\u06d5\u0648\u0631 \u0648 \u0686\u06d5\u062e\u0645\u0627\u062e\u06d5\u062f\u0627\u0646 \u0644\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5 \u0628\u06c6 \u0628\u0628\u0627\u0631\u06ce \u0648 \u0628\u06a9\u06d5\u0648\u0646\u06d5 \u0646\u0627\u0648 \u062a\u0627\u0631\u06cc\u06a9\u0627\u06cc\u06cc\u061b \u0644\u06d5 \u062a\u0631\u0633\u06cc \u0647\u06d5\u0648\u0631\u06d5\u062a\u0631\u06cc\u0634\u0642\u06d5\u060c \u062a\u0644\u06cc \u06af\u0648\u06ce\u06cc\u0627\u0646 \u0695\u0627\u0628\u06a9\u06d5\u0646\u060c \u0646\u06d5\u06a9\u0627 \u0628\u0645\u0631\u0646. \u062e\u0648\u062f\u0627 \u062f\u06d5\u0648\u0631\u06cc \u0644\u06d5\u0645 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5 \u062f\u0627\u0648\u06d5. [\u0644\u0648\u0648\u0632\u06d5\u0648: \u062a\u06d5\u0648\u0698\u0645\u061b \u0695\u06ce\u0698\u0646\u06d5] [\u062a\u0644\u06cc: \u0626\u06d5\u0646\u06af\u0648\u0633\u062a\u061b \u0642\u0627\u0645\u06a9\u061b \u062a\u0644\u0647\u06cc]',
    '\u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0628\u0631\u0648\u0648\u0633\u06a9\u06d5\u06cc \u0647\u06d5\u0648\u0631 \u06a9\u0648\u06ce\u0631\u06cc\u0627\u0646 \u0628\u06a9\u0627. \u0647\u06d5\u0631 \u0634\u0631\u06cc\u062e\u06d5\u06cc \u0686\u06d5\u062e\u0645\u0627\u062e\u06d5 \u0647\u0627\u062a\u060c \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0695\u06ce \u0648 \u0647\u06d5\u0631 \u06af\u0627\u0644\u06ce\u06cc\u0627\u0646 \u062a\u0627\u0631\u06cc\u06a9 \u062f\u0627\u0647\u0627\u062a\u060c \u0695\u0627\u062f\u06d5\u0648\u06d5\u0633\u062a\u0646. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u0628\u06cc\u0648\u06cc\u0633\u062a\u0627\u06cc\u06d5\u060c \u06af\u0648\u06ce \u0648 \u0686\u0627\u0648\u06cc \u0644\u06d5 \u06a9\u0627\u0631 \u062f\u06d5\u062e\u0633\u062a\u0646\u061b \u062e\u0648\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u06cc \u067e\u06ce \u062f\u06d5\u06a9\u0631\u06ce.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062e\u0648\u0627\u06cc\u06d5 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0648 \u0632\u06c6\u0631\u06cc \u062a\u0631\u06cc\u0634\u06cc \u067e\u06ce\u0634 \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5. \u0628\u06d5\u0634\u06a9\u0648\u0648 \u062e\u06c6\u062a\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0647\u06d5\u0631\u062f\u06cc \u0628\u06c6 \u06a9\u0631\u062f\u0648\u0648\u0646 \u0628\u06d5 \u0695\u0627\u06cc\u06d5\u062e\u061b \u0626\u0627\u0633\u0645\u0627\u0646\u06cc \u0628\u06d5\u0631\u0632 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u0644\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5\u0648\u06d5 \u0628\u06c6 \u0628\u0627\u0631\u0627\u0646\u062f\u0646\u061b \u0647\u06d5\u0645\u0648\u0648 \u062c\u0648\u0648\u0631\u06d5 \u0628\u06d5\u0631 \u0648 \u0628\u0648\u0648\u06cc - \u0628\u06d5\u0647\u06c6\u06cc \u0626\u06d5\u0648 \u0626\u0627\u0648\u06d5 - \u0628\u06c6 \u0695\u0648\u0627\u0646\u062f\u0646. \u062f\u06d5\u06cc\u0633\u0627 \u06a9\u06d5 \u0626\u06d5\u0648\u06d5\u0648 \u0632\u0627\u0646\u06cc\u0648\u06d5\u060c \u0686\u06c6\u0646 \u0634\u06d5\u0631\u06cc\u06a9\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u062e\u0648\u0627\u06cc\u06d5 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0645\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631 \u0628\u06d5 \u06af\u0648\u0645\u0627\u0646\u0646 \u0648 \u067e\u06ce\u0634\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0628\u06c6 \u0686\u0648\u0648\u0646\u060c \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0648\u06ce\u0695\u0627\u06cc \u0647\u0627\u0648\u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0628\u06d5 \u06af\u06d5\u0644\u06d5\u06a9\u06c6\u0645\u06d5\u06a9\u06cc \u06cc\u06d5\u06a9\u062a\u0631 - \u062c\u06af\u06d5 \u0644\u06d5 \u062e\u0648\u0627 - \u0633\u0648\u0648\u0631\u06d5\u062a\u06ce\u06a9\u06cc \u0648\u06d5\u06a9 \u0626\u06d5\u0648 \u0628\u06ce\u0698\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0646\u06d5\u0648\u06a9\u0631\u062f - \u06a9\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u06c6\u0634\u062a\u0627\u0646 \u0646\u0627\u06a9\u0631\u06ce - \u0633\u0627 \u0628\u062a\u0631\u0633\u0646 \u0644\u06d5\u0648 \u0626\u0627\u06af\u0631\u06d5\u06cc \u062f\u06d5\u0633\u062a\u06d5\u0686\u06cc\u0644\u06d5\u06cc \u062a\u06d5\u0648\u06d5\u0646 \u0648 \u0644\u06d5\u0634\u06cc \u0645\u0631\u06c6\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0647\u06d5\u06b5\u06a9\u0631\u0627\u0648\u06d5. [\u062a\u06d5\u0648\u06d5\u0646: \u0628\u06d5\u0631\u062f\u061b \u06a9\u0648\u0686\u06a9\u061b \u06a9\u06d5\u06a4\u0631] [\u0647\u06d5\u06b5\u06a9\u0631\u0627\u0648\u06d5: \u0633\u0627\u0632 \u06a9\u0631\u0627\u0648\u06d5]',
    '\u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631 \u0686\u0627\u06a9\u0646\u060c \u0645\u0648\u0698\u062f\u06d5 \u0628\u062f\u06d5: \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06cc \u0648\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce\u060c \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0644\u06d5 \u0647\u06d5\u0631 \u0645\u06cc\u0648\u06d5\u06cc\u06d5\u06a9 \u0628\u0686\u06ce\u0698\u0646\u060c \u0626\u06ce\u0698\u0646: \u0628\u06d5\u0631\u06ce \u0626\u06d5\u0645\u06d5 \u06a9\u0631\u0627\u0628\u0648\u0648\u06d5 \u0628\u06d5\u0634\u06cc \u0645\u06d5 \u0648 \u0626\u06ce\u0633\u062a\u0627 \u0648\u06ce\u0646\u06d5\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5. \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06ce \u0632\u06c6\u0631 \u067e\u0627\u06a9 \u0648 \u062e\u0627\u0648\u06ce\u0646\u0646\u061b \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5\u0634 \u0647\u06d5\u0631 \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646.',
    '\u062e\u0648\u062f\u0627 \u0644\u06d5\u0648\u06d5 \u0634\u06d5\u0631\u0645 \u0646\u0627\u06a9\u0627 \u0645\u06ce\u0634\u0648\u0648\u0644\u06d5\u0648 \u0644\u06d5 \u067e\u06ce\u0634\u06a9\u06d5\u0634 \u06a9\u06d5\u0645\u062a\u0631 \u0628\u06d5 \u0646\u0645\u0648\u0648\u0646\u06d5 \u0628\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5. \u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u0695\u0648\u0648\u0646 \u062f\u06d5\u0632\u0627\u0646\u0646 \u0626\u06d5\u0645\u06d5 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0695\u0627\u06cc\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 \u0626\u06ce\u0698\u0646: \u062e\u0648\u062f\u0627 \u0644\u06d5\u0645 \u0646\u0645\u0648\u0648\u0646\u06d5\u0647\u06ce\u0646\u0627\u0646\u06d5\u0648\u06d5 \u0686\u06cc \u06af\u06d5\u0631\u06d5\u06a9 \u0628\u0648\u0648\u061f \u0632\u06c6\u0631\u0627\u0646\u06cc \u067e\u06ce \u06af\u0648\u0645\u0695\u0627 \u062f\u06d5\u06a9\u0627 \u0648 \u0641\u0631\u06d5\u0634\u06cc\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627 \u062f\u06d5\u06a9\u0627. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06ce\u06cc \u062a\u0648\u0648\u0634\u06cc \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u062f\u06d5\u0628\u0646 \u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u0627\u06cc\u06d5 \u0644\u0627 \u062f\u06d5\u062f\u06d5\u0646. [\u067e\u06ce\u0634\u06a9\u06d5: \u0645\u06ce\u0634\u0648\u0648\u0644\u06d5\u061b \u062a\u06c6\u0641\u0627\u0646\u06d5\u061b \u0628\u06d5\u062e\u0634\u06d5\u061b \u067e\u06ce\u0634\u06cc]',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u0627 \u062f\u06d5\u0628\u06d5\u0633\u062a\u0646 \u0648 \u062f\u06d5\u06cc\u0634\u06a9\u06ce\u0646\u0646\u060c \u0626\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0648\u06cc\u0633\u062a\u0648\u0648\u06cc\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0633\u0627\u063a\u06cc \u0628\u0647\u06ce\u06b5\u0646\u06d5\u0648\u06d5\u060c \u062f\u06d5\u067e\u0633\u06ce\u0646\u0646 \u0648 \u0644\u06d5 \u0639\u06d5\u0631\u062f\u0627 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631 \u062f\u06d5\u0628\u0646.',
    '\u0686\u06c6\u0646 \u0644\u06d5\u0648 \u062e\u0648\u0627\u06cc\u06d5 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u0646\u06d5\u0628\u0648\u0648\u060c \u0698\u06cc\u0627\u0646\u06cc \u062f\u0627\u0648\u06d5 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0648 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u062a\u0627\u0646\u0645\u0631\u06ce\u0646\u06ce \u0648 \u062f\u06cc\u0633\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648\u0648 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u06af\u0634\u062a\u0648\u0648 \u0628\u06c6\u0644\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5\u061f',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u067e\u06d5\u0695\u0698\u0627\u06cc\u06d5 \u0628\u06d5\u0631\u0632\u0627\u06cc\u06cc \u0648 \u062d\u06d5\u0648\u062a \u0646\u0647\u0648\u0648\u0645\u06cc \u0644\u06ce \u062f\u0631\u0648\u0633\u062a \u06a9\u0631\u062f. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0626\u0627\u06af\u0627\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0647\u06d5\u06cc\u06d5. [\u067e\u06d5\u0695\u0698\u0627\u0646: \u062e\u06d5\u0631\u06cc\u06a9 \u0628\u0648\u0648\u0646]',
    '\u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0628\u06d5 \u0641\u0631\u06cc\u0634\u062a\u0627\u0646\u06cc \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f: \u0645\u0646 \u062f\u06d5\u0645\u06d5\u0648\u06ce \u0628\u0631\u06cc\u06a9\u0627\u0631\u06ce\u06a9 \u0644\u06d5\u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646 \u062f\u06cc\u0627\u0631\u06cc \u0628\u06a9\u06d5\u0645\u061b \u06af\u0648\u062a\u06cc\u0627\u0646: \u062a\u06c6 \u06a9\u06d5\u0633\u06ce \u0648\u0627 \u062f\u06ce\u0646\u06cc \u0644\u06d5\u0648\u06ce\u062f\u0627 \u062e\u0631\u0627\u067e\u06d5 \u0628\u06a9\u0627 \u0648 \u062e\u0648\u06ce\u0646 \u0628\u0695\u06ce\u0698\u06ce\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u06cc \u062a\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u067e\u06d5\u0633\u0646\u06cc \u067e\u06cc\u0631\u06c6\u0632\u06cc \u062a\u06c6 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u061f \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u06cc \u0645\u0646 \u062f\u06d5\u06cc\u0632\u0627\u0646\u0645\u060c \u0626\u06ce\u0648\u06d5 \u0626\u0627\u06af\u0627\u062a\u0627\u0646 \u0644\u06ce \u0646\u06cc\u06cc\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0646\u0627\u0648\u06ce\u06a9\u06cc \u0641\u06ce\u0631\u06cc \u0626\u0627\u062f\u06d5\u0645 \u06a9\u0631\u062f. \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc \u0644\u06d5\u0628\u06d5\u0631 \u0641\u0631\u06cc\u0634\u062a\u06d5\u06a9\u0627\u0646 \u0695\u0627\u0646\u0627 \u0648 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u062a\u06cc \u0628\u06c6 \u0686\u0648\u0648\u0646\u060c \u0646\u0627\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0645 \u0628\u06c6 \u0647\u06d5\u06b5\u062f\u06d5\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0647\u06d5\u0631 \u062a\u06c6\u06cc \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06d5 \u062f\u0648\u0648\u0631\u06cc\u061b \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u062a\u06c6 \u0641\u06ce\u0631\u062a \u06a9\u0631\u062f\u0648\u0648\u06cc\u0646\u060c \u0647\u06cc\u0686 \u0646\u0627\u0632\u0627\u0646\u06cc\u0646. \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u062a \u0632\u0627\u0646\u0627\u06cc \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0626\u0627\u062f\u06d5\u0645! \u062f\u06d5\u0633\u0627 \u062a\u06c6 \u0646\u0627\u0648\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u06d5\u06b5\u0628\u062f\u06d5\u060c \u0648\u06d5\u062e\u062a\u06ce \u0626\u0627\u062f\u06d5\u0645 \u0647\u06d5\u0645\u0648\u0648 \u0646\u0627\u0648\u06ce\u06a9\u06cc \u0628\u06c6 \u0647\u06d5\u06b5\u062f\u0627\u0646\u060c \u06af\u0648\u062a\u06cc: \u0645\u0646 \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u0645 \u0646\u06d5\u0648\u062a \u06a9\u06d5 \u0647\u06d5\u0631 \u062e\u06c6\u0645\u0645 \u0647\u06d5\u0631 \u0646\u0647\u06ce\u0646\u06cc \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u06cc\u0634\u06ce\u0631\u0646 \u0648 \u0628\u06cc\u062f\u0631\u06a9\u06ce\u0646\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u0632\u0627\u0646\u0645\u061f',
    '\u0641\u06d5\u0631\u0645\u0627\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0641\u0631\u06cc\u0634\u062a\u0627\u0646 \u062f\u0627\u060c \u06a9\u0695\u0646\u06c6\u0634 \u0644\u06d5\u0628\u06d5\u0631 \u0626\u0627\u062f\u06d5\u0645 \u0628\u06d5\u0631\u0646. \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06cc\u0628\u0644\u06cc\u0633 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0633\u0648\u0698\u062f\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u0631\u062f\u061b \u0626\u06d5\u0648 \u06a9\u06d5 \u062e\u06c6\u06cc \u0628\u06d5 \u0632\u0644\u062a\u0631 \u0632\u0627\u0646\u06cc\u060c \u0633\u0648\u0698\u062f\u06d5\u06cc \u0646\u06d5\u0628\u0631\u062f \u0648 \u0633\u067e\u06b5\u06d5 \u0628\u0648\u0648.',
    '\u06af\u0648\u062a\u0645\u0627\u0646: \u0626\u0627\u062f\u06d5\u0645! \u062e\u06c6\u062a \u0648 \u0698\u0646\u062a\u060c \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0646\u06cc\u0634\u062a\u06d5\u062c\u06ce \u0628\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0647\u06d5\u0631 \u0634\u0648\u06ce\u0646\u06ce \u062f\u06d5\u062e\u06c6\u0646\u060c \u0646\u06c6\u0634\u06cc \u06af\u06cc\u0627\u0646\u0648\u0648\u061b \u0647\u06d5\u0631 \u062a\u062e\u0648\u0648\u0646\u06cc \u0626\u06d5\u0648 \u062f\u0627\u0631[\u06cc \u0647\u0627\u0646\u0627] \u0645\u06d5\u06a9\u06d5\u0648\u0646! \u0686\u0648\u0646 [\u0628\u06d5\u0645 \u06a9\u0627\u0631\u06d5] \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u062f\u06d5\u0628\u0646. [\u0647\u0627\u0646\u0627: \u0661. \u067e\u06d5\u0646\u0627\u061b \u0644\u06d5\u0628\u06d5\u0631 \u067e\u0627\u0695\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u062f\u0627\u062f\u067e\u0631\u0633\u06cc\u0646 \u0662. \u0626\u0627\u0648\u0647\u0627\u061b \u0626\u06d5\u0648 \u062c\u06c6\u0631\u06d5. \u062f\u0627\u0631\u06cc \u0647\u0627\u0646\u0627: \u062f\u0627\u0631\u06cc \u0642\u06d5\u062f\u06d5\u063a\u06d5\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a \u0628\u06c6 \u0626\u0627\u062f\u06d5\u0645.]',
    '\u0634\u06d5\u06cc\u062a\u0627\u0646 \u0647\u06d5\u06b5\u06cc\u062e\u06d5\u06b5\u06d5\u062a\u0627\u0646\u062f\u0646 \u0648 \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u062f\u06d5\u0631\u06cc\u067e\u06d5\u0695\u0627\u0646\u062f\u0646. \u06af\u0648\u062a\u0645\u0627\u0646: \u062f\u0627\u0648\u06d5\u0632\u0646\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5! \u062e\u06c6\u062a\u0627\u0646 \u062f\u0648\u0698\u0645\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646\u0646. \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06ce \u062f\u06cc\u0627\u0631\u06cc\u06a9\u0631\u0627\u0648\u060c \u0644\u06d5\u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646 \u0695\u0627\u06cc\u0628\u0628\u0648\u06ce\u0631\u0646.',
    '\u0626\u0627\u062f\u06d5\u0645 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0646\u062f\u06d5\u06cc\u0695\u0627 \u0686\u06d5\u0646\u062f \u0648\u0634\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u06ce \u06af\u06d5\u06cc\u06cc\u0634\u062a. \u0626\u06d5\u0648\u0633\u0627 \u062a\u06c6\u0628\u06d5\u06a9\u06d5\u06cc \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0631\u062f. \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5. [\u062f\u0644\u06c6\u06a4\u0627\u0646: \u062e\u0627\u0648\u06d5\u0646 \u0628\u06d5\u0632\u06d5\u06cc\u06cc.]',
    '\u06af\u0648\u062a\u0645\u0627\u0646: \u06af\u0634\u062a\u0648\u0648 \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0628\u0695\u06c6\u0646\u06d5 \u062e\u0648\u0627\u0631\u06ce! \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u0627\u06cc \u0645\u0646\u06d5\u0648\u06d5 \u0695\u06ce\u0646\u0648\u06ce\u0646\u06cc\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06a9\u06d5\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0695\u06ce\u0646\u0648\u06ce\u0646\u06cc\u0645\u060c \u0646\u06d5 \u0644\u06d5 \u0647\u06cc\u0686 \u0634\u062a\u06ce \u062f\u06d5\u062a\u0631\u0633\u0646 \u0646\u06d5 \u062e\u06d5\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u0627\u0646\u06cc\u0634 \u062e\u06c6\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u0647\u06d5\u06b5\u06d5 \u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646 \u062d\u0627\u0634\u0627\u06cc\u06d5 \u0628\u06a9\u06d5\u0646\u060c \u062f\u06d5\u0628\u0646 \u0628\u06d5 \u06cc\u0627\u0631\u06cc \u0626\u0627\u06af\u0631 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u062a\u06ce\u062f\u0627 \u062f\u06d5\u0628\u0646.',
    '\u0626\u06d5\u06cc \u0632\u0627\u0631\u0648\u0632\u06cc\u0686\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644! \u0644\u06d5 \u0628\u06cc\u0631\u0648\u0648 \u0628\u06ce \u0686\u06d5\u0646\u062f\u06d5\u0645 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0648\u0648\u0646. \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0645\u0646 \u0628\u06d5\u062c\u06ce \u0628\u06ce\u0646\u0646\u061b \u0645\u0646\u06cc\u0634 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06ce \u067e\u06ce\u0645 \u062f\u0627\u0648\u0646\u060c \u067e\u06ce\u06a9 \u062f\u06ce\u0646\u0645 \u0648 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u0647\u0627 \u0644\u06d5 \u062e\u06c6\u0645 \u0628\u062a\u0631\u0633\u0646. [\u0632\u0627\u0631\u0648\u0632\u06cc\u0686 \u06cc\u0627\u0646 \u0632\u0627\u0631\u0648\u0632\u06ce\u0686: \u062a\u06c6\u0631\u06d5\u0645\u06d5\u060c \u062e\u06ce\u0632\u0627\u0646 \u0628\u06d5\u06af\u0634\u062a\u06cc.]',
    '\u0628\u0695\u0648\u0627\u0648 \u0647\u06d5\u0628\u06ce \u0628\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0646\u0627\u0631\u062f\u0648\u0648\u0645\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u06d5\u0634 \u0628\u06d5 \u0695\u0627\u0633\u062a \u062f\u06d5\u0632\u0627\u0646\u06ce \u0648\u0627 \u0626\u06ce\u0633\u062a\u0627 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5. \u0645\u06d5\u0628\u0646\u06d5 \u067e\u06ce\u0634\u06d5\u0646\u06af\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062d\u0627\u0634\u0627 \u0644\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u062f\u06d5\u06a9\u06d5\u0646. \u0646\u0627\u0634\u06ce \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646 \u0628\u06d5 \u0646\u0631\u062e\u06ce\u06a9\u06cc \u06a9\u06d5\u0645 \u0628\u0641\u0695\u06c6\u0634\u0646. \u0633\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u0645 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce.',
    '\u0695\u0627\u0633\u062a\u06cc \u0695\u06d5\u0648\u0627 \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u062f\u0627\u0645\u06d5\u067e\u06c6\u0634\u0646. \u062d\u0627\u0634\u0627\u0648 \u0644\u06d5\u0648 \u0695\u0627\u0633\u062a\u06cc\u06cc\u06d5 \u0646\u06d5\u0628\u06ce\u060c \u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646 \u0646\u0627\u0633\u06cc\u0648\u062a\u0627\u0646\u06d5.',
    '\u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u062f\u06d5\u0646\u061b \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0695\u0646\u06c6\u0634\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u062f\u0627 \u06a9\u0695\u0646\u06c6\u0634 \u0628\u06d5\u0631\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u06a9\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5\u0648 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u062f\u06d5\u062f\u06d5\u0646 \u0686\u0627\u06a9\u06d5\u06cc \u0628\u06a9\u06d5\u0646\u060c \u062e\u06c6\u06cc\u0627\u0646\u06cc \u0644\u06ce \u0647\u06d5\u06b5\u062f\u0627\u0648\u06ce\u0631\u0646 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u0634\u0648\u0648 \u06a9\u062a\u06ce\u0628 \u062e\u0648\u06ce\u0646\u06d5\u0648\u06d5\u0646\u061f \u062f\u06d5\u0633\u0627 \u0686\u06cc\u062a\u0627\u0646\u06d5 \u062a\u06ce\u0646\u0627\u06af\u06d5\u0646\u061f [\u0647\u06d5\u06b5\u0648\u0627\u0631\u062f\u0646: \u062c\u06cc\u0627\u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5.]',
    '\u0628\u06c6 \u062e\u06c6\u0695\u0627\u06af\u0631\u06cc\u0648\u0648 \u0628\u06c6 \u0646\u0648\u06ce\u0698\u0627\u0646 \u0647\u0627\u0646\u0627 \u0628\u06d5\u0631\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0632\u0644 \u0646\u0627\u0632\u0627\u0646\u0646\u061b',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0633\u0648\u0648\u0631 \u062f\u06d5\u0632\u0627\u0646\u0646 \u062a\u0648\u0648\u0634\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0646 \u0648 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u062e\u06c6\u06cc. [\u0633\u0648\u0648\u0631 \u0632\u0627\u0646\u06cc\u0646: \u062f\u06b5\u0646\u06cc\u0627\u0628\u0648\u0648\u0646.]',
    '\u0626\u06d5\u06cc \u0632\u0627\u0631\u0648\u0632\u06ce\u0686\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644! \u0628\u06cc\u0631 \u0644\u06d5\u0648 \u0686\u0627\u06a9\u06d5 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u0645\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u062a\u0627\u0646\u0645 \u06a9\u0631\u062f\u061b \u0644\u06d5 \u0686\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062f\u0646\u06cc\u0627 \u067e\u062a\u0631 \u0695\u06ce\u0632\u0645 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u0627.',
    '\u062e\u06c6\u0634 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc \u06a9\u06d5 \u06a9\u06d5\u0633 \u062a\u0627\u0648\u0627\u0646\u06cc \u0646\u0627\u0686\u06ce\u062a\u06d5 \u0633\u06d5\u0631 \u06a9\u06d5\u0633\u06cc \u062a\u0631\u061b \u0646\u06d5 \u0628\u0627\u0631\u0645\u062a\u06d5\u06cc \u062a\u06ce\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u0647\u06cc\u0686 \u062e\u06c6\u06a9\u0695\u06cc\u0646\u06d5\u0648\u06d5\u06cc\u06d5\u06a9\u061b \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc \u06a9\u06d5\u0633 \u0646\u0627\u06cc\u06d5. [\u0628\u0627\u0631\u0645\u062a\u06d5: \u06af\u0631\u06c6\u061b \u0634\u062a\u06ce\u06a9 \u0644\u06d5\u0628\u0627\u062a\u06cc \u0634\u062a\u06ce\u06a9\u06cc \u062a\u0631\u060c \u0628\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5\u062a \u062f\u0627\u0646\u0627\u0646].',
    '\u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0695\u0632\u06af\u0627\u0631\u0645 \u06a9\u0631\u062f\u0646 \u0644\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u062f\u0627\u0631\u0648\u062f\u06d5\u0633\u062a\u06d5\u06cc\u060c \u06a9\u06d5 \u0633\u06d5\u0631\u06d5\u0695\u0627\u06cc \u062c\u0632\u06cc\u0627\u062f\u0627\u0646\u0648\u0648\u060c \u06a9\u0648\u0695\u0627\u0646\u06cc \u0644\u06ce \u0633\u06d5\u0631 \u062f\u06d5\u0628\u0695\u06cc\u0646 \u0648 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0633\u0627\u063a \u062f\u06d5\u0647\u06ce\u0634\u062a\u0646\u061b \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06cc \u0632\u06c6\u0631 \u0645\u06d5\u0632\u0646 \u0647\u06d5\u0628\u0648\u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0646\u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u0645\u0627\u0646 \u0628\u06c6 \u0642\u06d5\u06b5\u0627\u0634\u06a9\u0631\u062f\u0646 \u0648 \u0646\u06d5\u062c\u0627\u062a\u0646 \u062f\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u06d5\u0644 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0628\u0648\u0648\u0646 \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0646\u06a9\u0627\u0646\u062f\u0646 \u062f\u0627 \u0648 \u0626\u06ce\u0648\u06d5 \u062a\u0645\u0627\u0634\u0627\u0648 \u062f\u06d5\u06a9\u0631\u062f\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u0648\u0648\u0633\u0627\u060c \u0698\u0648\u0627\u0646\u06cc \u0686\u0644 \u0634\u06d5\u0648\u0645\u0627\u0646 \u062f\u0627\u0646\u0627\u060c \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06d5\u062a\u0627\u0646 \u06a9\u0631\u062f: \u067e\u0627\u0634 \u0626\u06d5\u0648 \u06af\u0648\u06ce\u0631\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0645\u06d5\u0634 \u0644\u06ce\u062a\u0627\u0646 \u062e\u06c6\u0634 \u0628\u0648\u0648\u06cc\u0646\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631 \u0628\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627 \u06a9\u062a\u06ce\u0628 \u0648 \u0647\u06c6\u06cc \u0628\u0698\u0627\u0631\u062f\u0646 \u062f\u0627\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0626\u06ce\u0648\u06d5 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u0645\u0648\u0648\u0633\u0627 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f: \u06af\u06d5\u0644\u06c6! \u0628\u06d5 \u06af\u06c6\u0644\u06a9 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc\u062a\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u06a9\u0631\u062f\u061b \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u0644\u06d5 \u0644\u0627\u06cc \u0628\u06d5\u062f\u06cc\u0647\u06ce\u0646\u06d5\u0631\u062a\u0627\u0646\u06d5\u0648\u06d5 \u062f\u06d5\u0631\u0628\u0695\u0646\u061b \u0633\u0627 \u0648\u06d5\u0631\u0646 \u06cc\u06d5\u06a9\u062a\u0631 \u0628\u06a9\u0648\u0648\u0698\u0646! \u0644\u0627\u06cc \u0628\u06d5\u062f\u06cc\u0647\u06ce\u0646\u06d5\u0631\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0628\u06c6\u062a\u0627\u0646 \u0686\u0627\u062a\u0631\u06d5\u061b \u06a9\u06d5 \u062a\u06c6\u0628\u06d5\u06cc \u0644\u06ce \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0631\u062f\u0646. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0644\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u0627\u0646 \u062e\u06c6\u0634 \u062f\u06d5\u0628\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u06a9\u06d5 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627\u062a\u0627\u0646 \u06af\u0648\u062a: \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627\u062a \u067e\u06ce \u0646\u0627\u0647\u06ce\u0646\u06cc\u0646 \u062a\u0627 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062e\u0648\u0627 \u0646\u06d5\u0628\u06cc\u0646\u06cc\u0646\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5\u0628\u06d5\u0631 \u0686\u0627\u0648\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u0631\u0648\u0648\u0633\u06a9\u06d5\u062a\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0647\u0627\u062a.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0645\u0631\u062f\u0628\u0648\u0648\u0646 \u0633\u06d5\u0631\u0644\u06d5\u0646\u0648\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648\u0646 \u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5\u061b \u0634\u0627\u06cc\u06d5\u062a \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631 \u0628\u0646.',
    '\u0647\u06d5\u0648\u0631\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0633\u06d5\u06cc\u0648\u0627\u0646\u0648\u0648\u061b \u06af\u06d5\u0632\u06c6 \u0648 \u0645\u06d5\u0644\u06cc \u0634\u06ce\u0644\u0627\u0642\u06d5\u0634\u0645\u0627\u0646 \u0628\u06c6 \u0647\u06d5\u0646\u0627\u0631\u062f\u0646: \u0628\u062e\u06c6\u0646 \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5 \u067e\u0627\u06a9\u06d5\u06cc \u0628\u06d5\u0634\u0645\u0627\u0646 \u062f\u0627\u0648\u0646! \u0626\u06d5\u0648\u0627\u0646 \u0646\u0627\u062d\u06d5\u0642\u06cc\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0646\u06d5\u06a9\u0631\u062f\u061b \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u062d\u06d5\u0642 \u0628\u0648\u0648\u0646. [\u0633\u06d5\u06cc\u0648\u0627\u0646: \u0633\u0627\u0628\u0627\u062a\u061b \u0633\u0627\u06cc\u06d5\u0628\u0627\u0646. \u0634\u06ce\u0644\u0627\u0642\u06d5: \u06a9\u06d5\u0695\u06d5\u0648\u0627\u06b5\u06d5].',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u067e\u06ce\u0645\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646: \u0628\u0695\u06c6\u0646\u06d5 \u0646\u0627\u0648 \u0634\u0627\u0631\u06d5\u06a9\u06d5\u0648\u06d5\u061b \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u062f\u06d5\u062e\u06c6\u0646 \u0648 \u0686\u06cc \u062f\u06d5\u062e\u06c6\u0646\u06d5\u0648\u06d5\u060c \u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646. \u0647\u06d5\u0631 \u0644\u06d5 \u062f\u06d5\u0631\u06af\u0627 \u0686\u0648\u0648\u0646\u06d5 \u0626\u06d5\u0648\u062f\u06cc\u0648\u060c \u0633\u0648\u0698\u062f\u06d5 \u0628\u06d5\u0631\u0646 \u0648 \u0628\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0626\u06d5\u0631\u06a9\u06cc \u06af\u0631\u0627\u0646\u0645\u0627\u0646 \u0645\u06d5\u062e\u06d5 \u0633\u06d5\u0631. \u0626\u06d5\u0648\u0633\u0627 \u0645\u06d5 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u0648\u0648 \u062e\u06c6\u0634 \u062f\u06d5\u0628\u06cc\u0646 \u0648 \u067e\u0627\u062f\u0627\u0634\u0645\u0627\u0646 \u0644\u06d5\u0645\u06d5\u0695 \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u067e\u062a\u0631 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06cc\u062a\u0631 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u2013 \u0647\u06d5\u0631\u0686\u06cc \u0628\u06d5\u0648\u0627\u0646 \u0633\u067e\u06ce\u0631\u0627\u0628\u0648\u0648 \u2013 \u0628\u06d5\u0631\u0627\u0648\u06d5\u0698\u0648\u0648 \u0695\u06d5\u0641\u062a\u0627\u0631\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0644\u06d5 \u062a\u06c6\u06b5\u06d5\u06cc \u0626\u06d5\u0648 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc\u06d5\u062f\u0627\u060c \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646.',
    '\u0626\u06d5\u0648 \u0647\u06d5\u0695\u06d5\u062a\u06d5\u0634 \u06a9\u06d5 \u0648\u0627 \u0645\u0648\u0648\u0633\u0627 \u0626\u0627\u0648\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u0627\u0646\u06cc \u062e\u0648\u0627\u0633\u062a. \u06af\u0648\u062a\u0645\u0627\u0646 \u0628\u06d5 \u062f\u0627\u0631 \u0639\u0627\u0633\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u062a \u0644\u06d5\u0648 \u0628\u06d5\u0631\u062f\u06d5 \u062f\u06d5! \u062f\u0648\u0627\u0632\u062f\u06d5 \u06a9\u0627\u0646\u06cc \u0644\u06ce \u0647\u06d5\u06b5\u0642\u0648\u06b5\u06cc\u061b \u0647\u06d5\u0631 \u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9 \u062c\u06ce\u06cc \u0648\u06d5\u062e\u0648\u0627\u0631\u062f\u0646\u06cc \u062e\u06c6\u06cc \u0632\u0627\u0646\u06cc. \u0633\u0627 \u0644\u06d5\u0648 \u0628\u06d5\u0634\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u0627\u0648\u06cc\u06d5 \u0628\u062e\u06c6\u0646\u060c \u0648\u06d5\u062e\u06c6\u0646\u060c \u0644\u06d5 \u0639\u06d5\u0631\u0632\u0627 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0645\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u0634\u062a\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u06ce \u06a9\u06d5 \u06af\u0648\u062a\u062a\u0627\u0646: \u0645\u0648\u0648\u0633\u0627! \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u06cc\u06d5\u06a9 \u062c\u06c6\u0631\u06d5 \u062e\u06c6\u0631\u0627\u06a9 \u0647\u06d5\u062f\u0627 \u0646\u0627\u062f\u06d5\u06cc\u0646\u061b \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u062e\u0648\u0627\u0632\u06d5 \u0644\u06d5\u0648\u06d5\u06cc \u0632\u06d5\u0648\u06cc \u062f\u06d5\u06cc\u0695\u0648\u06ce\u0646\u06ce\u060c \u0648\u06d5\u06a9 \u0633\u06d5\u0648\u0632\u06d5\u0648\u0627\u062a \u0648 \u062e\u06d5\u06cc\u0627\u0631 \u0648 \u0646\u06cc\u0633\u06a9 \u0648 \u0633\u06cc\u0631 \u0648 \u067e\u06cc\u0648\u0627\u0632\u060c \u0628\u06c6\u0645\u0627\u0646 \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0628\u06ce\u0646\u06ce. \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u062e\u0631\u0627\u067e \u0628\u06d5 \u0686\u0627\u06a9 \u062f\u06d5\u06af\u06c6\u0695\u0646\u06d5\u0648\u06d5\u061f \u0633\u06d5\u0631\u0628\u06d5\u0631\u06d5\u0698\u06ce\u0631 \u0628\u0686\u0646\u06d5 \u0634\u0627\u0631\u06ce\u06a9 \u062f\u06b5\u062e\u0648\u0627\u0632\u06cc \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648\u06ce\u06d5. \u0628\u06d5\u06b5\u0627\u0645 \u0686\u0648\u0646\u06a9\u06d5 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u060c \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0648\u0634\u062a\u060c \u06cc\u0627\u063a\u06cc \u0628\u0648\u0648\u0646 \u0648 \u0645\u0644\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06d5\u0633 \u062f\u0631\u06ce\u0698\u06cc \u062f\u06d5\u0646\u0627\u060c \u0648\u06d5\u0628\u06d5\u0631 \u063a\u06d5\u0632\u06d5\u0628\u06cc \u062e\u0648\u0627 \u06a9\u06d5\u0648\u062a\u0646 \u0648 \u0686\u0627\u0631\u06d5\u0695\u06d5\u0634\u06cc \u0648 \u0632\u06d5\u0628\u0648\u0648\u0646\u06cc\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u0695\u06cc\u0627\u0631 \u062f\u0631\u0627.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062c\u0648\u0648\u0644\u06d5\u06a9\u06d5 \u0648 \u0641\u06d5\u0644\u06d5 \u0648 \u0633\u0627\u06cc\u0628\u06cc \u0645\u06d5\u0632\u06d5\u0628 \u0628\u0648\u0648\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0631\u06c6\u0698\u06cc \u067e\u06d5\u0633\u06b5\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06ce \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0646\u060c \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646\u0695\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06af\u0627 \u0648 \u0646\u06d5 \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631\u06d5\u060c \u0646\u06d5 \u062e\u06d5\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u0645\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u06af\u0631\u062a\u0646 \u0648 \u06a9\u06ce\u0648\u06cc \u062a\u0648\u0648\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06b5\u0646\u062f \u06a9\u0631\u062f\u061b \u06af\u0648\u062a\u0645\u0627\u0646: \u0647\u06d5\u0631\u0686\u06cc \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0648\u0646 \u062a\u0648\u0646\u062f\u06cc \u0628\u06af\u0631\u0646\u060c \u0686\u06cc \u062a\u06ce\u062f\u0627 \u0644\u06d5 \u0628\u06cc\u0631\u06cc \u0645\u06d5\u06a9\u06d5\u0646. \u0634\u0627\u06cc\u06d5\u062a\u0627 \u062e\u06c6 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0644\u06ce \u0628\u0648\u0627\u0631\u062f. \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u0627 \u062f\u06d5\u0647\u0627\u0646\u0627\u062a\u0627\u0646 \u0646\u06d5\u0647\u0627\u062a\u0627\u06cc\u06d5\u060c \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u0644\u06d5 \u062d\u0627\u06b5\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634\u062a\u0627\u0646 \u062e\u06d5\u0628\u06d5\u0631\u062f\u0627\u0631 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0648\u0627 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0634\u06d5\u0645\u0645\u06d5\u062f\u0627 \u0644\u06d5 \u0695\u0627\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u060c \u067e\u06ce\u0645\u0627\u0646 \u06af\u0648\u062a\u0646: \u0645\u06d5\u06cc\u0645\u0648\u0648\u0646\u06cc \u062a\u0695\u06c6\u06a9\u0631\u0627\u0648 \u0628\u0646.',
    '\u0628\u06d5 \u067e\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u0648\u0627\u0645\u0627\u0646 \u0628\u0631\u062f\u0646\u060c \u0647\u0627\u0648\u0686\u06d5\u0631\u062e\u0627\u0646 \u0648 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634\u060c \u0686\u0627\u0648\u062a\u0631\u0633\u06ce\u0646 \u06a9\u0627 \u0648 \u062f\u06d5\u0631\u0633\u06ce\u06a9 \u0628\u06ce \u0628\u06c6 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u0627\u0646.',
    '\u0628\u06cc\u0631 \u0644\u06d5\u0648 \u062c\u0627\u0631\u06d5\u0634 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u06af\u0648\u062a: \u062e\u0648\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u0648\u0648 \u067e\u06ce \u062f\u06d5\u062f\u0627 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0686\u06ce\u06b5\u06ce\u06a9 \u0633\u06d5\u0631\u0698\u06ce \u06a9\u06d5\u0646. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u062f\u06d5\u062a\u06d5\u0648\u06ce \u06af\u0627\u06b5\u062a\u06d5\u0646 \u067e\u06ce \u0628\u062f\u06d5\u06cc\u061f \u06af\u0648\u062a\u06cc: \u067e\u06d5\u0646\u0627 \u062f\u06d5\u0628\u06d5\u0645 \u0628\u06d5 \u062e\u0648\u0627 \u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0646\u06d5\u0632\u0627\u0646\u0627\u0646 \u0628\u0645.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a \u067e\u0631\u0633\u06cc\u0627\u0631 \u0628\u06a9\u06d5\u060c \u0686\u06c6\u0646 \u0686\u06ce\u06b5\u06ce\u06a9\u06d5\u061f \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0698\u06ce \u0626\u06d5\u0648 \u0686\u06ce\u06b5\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0646\u06d5 \u067e\u06cc\u0631\u06cc \u067e\u06d5\u06a9 \u06a9\u06d5\u0648\u062a\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u06a9\u06d5\u0645 \u062a\u06d5\u0645\u06d5\u0646\u061b \u0646\u0627\u0648\u06d5\u0646\u062c\u06cc\u06cc\u06d5. \u0628\u06d5 \u0686\u06cc\u062a\u0627\u0646 \u0641\u06d5\u0631\u0645\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5 \u062f\u06d5\u0628\u06ce \u0628\u06cc\u06a9\u06d5\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a \u067e\u0631\u0633\u06ce \u0628\u06a9\u06d5 \u067e\u06ce\u0645\u0627\u0646 \u0628\u06ce\u0698\u06ce \u0686 \u0695\u06d5\u0646\u06af\u06ce\u06a9\u06d5\u061f \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0698\u06ce \u0686\u06ce\u06b5\u06ce\u06a9\u06cc \u0647\u06ce\u0646\u062f\u06d5 \u0695\u06d5\u0646\u06af \u0632\u06d5\u0631\u062f\u06d5\u060c \u062a\u0648\u0627\u0634\u0627\u0686\u06cc \u062f\u06b5\u062e\u06c6\u0634 \u062f\u06d5\u06a9\u0627.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062f\u06cc\u0633\u0627\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u067e\u0631\u0633\u06d5 \u0628\u0627 \u067e\u06ce\u0646 \u0628\u06b5\u06ce \u0686\u06c6\u0646 \u0686\u06c6\u0646\u06cc\u06cc\u06d5\u061f \u0645\u0627\u0646\u06af\u0627 \u0633\u06d5\u0631\u06cc \u0644\u06ce \u062a\u06ce\u06a9 \u062f\u0627\u0648\u06cc\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06ce\u0698\u06ce \u0646\u06d5 \u0686\u06ce\u06b5\u06ce\u06a9\u06cc \u0647\u06d5\u06a4\u0648\u0648\u062a\u06cc\u06cc\u06d5\u060c \u062c\u0648\u0648\u062a\u06cc \u0628\u06a9\u0627 \u0648 \u0646\u06d5 \u0626\u0627\u0648 \u062f\u06d5\u06a9\u06ce\u0634\u06ce \u0628\u06c6 \u0632\u06d5\u0648\u06cc\u060c \u0647\u06cc\u0686 \u067e\u06d5\u06b5\u06d5\u0634\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5. \u06af\u0648\u062a\u06cc\u0627\u0646 \u0626\u06ce\u0633\u062a\u0627 \u0695\u0627\u0633\u062a\u06d5\u06a9\u06d5\u062a \u0628\u06c6 \u062e\u0633\u062a\u06cc\u0646\u06d5 \u0695\u0648\u0648. \u0626\u06cc\u062a\u0631 \u0626\u06d5\u0648\u0633\u0627 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0698\u06ce \u06a9\u0631\u062f\u061b \u0648\u06d5\u062e\u062a\u06cc\u0634 \u0628\u0648\u0648 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0646\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06cc\u06d5\u06a9\u06ce\u06a9\u06cc\u0627\u0646 \u06a9\u0648\u0634\u062a\u0628\u0648\u0648\u060c[ \u0644\u06d5 \u0633\u06d5\u0631 \u06af\u0648\u0698\u06d5\u0631 ]\u06cc\u06d5\u06a9\u062a\u0631\u06cc \u062a\u0627\u0646 \u062a\u06ce\u0648\u06d5\u062f\u06d5\u062f\u0627\u060c \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06c6\u06cc \u0648\u06d5\u062f\u06d5\u0631\u062e\u0633\u062a\u0646 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u062a\u0627\u0646\u0634\u0627\u0631\u062f\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06af\u0648\u062a\u0645\u0627\u0646: \u067e\u0627\u0698\u06ce\u06a9 \u0644\u06d5 \u0644\u0627\u0634\u06cc \u0686\u06ce\u06b5\u06d5\u06a9\u06d5 \u0644\u06d5 \u0644\u0627\u06a9\u06cc \u06a9\u0648\u0698\u0631\u0627\u0648\u06d5\u06a9\u06d5 \u062f\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u0627 \u0645\u0631\u062f\u0648\u0648 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u0648\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u062a\u0648\u0627\u0646\u0646 \u062a\u06ce\u0628\u06af\u06d5\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0645\u06d5\u0634 \u0628\u0648\u0648\u0646\u06d5 \u062f\u06b5\u0695\u06d5\u0642\u06ce\u06a9\u06cc \u0648\u06d5\u0647\u0627 \u0644\u06d5 \u0686\u06d5\u0634\u0646\u06cc \u0628\u06d5\u0631\u062f\u061b \u0628\u06af\u0631\u06d5 \u0695\u06d5\u0642\u062a\u0631\u06cc\u0634 \u0628\u0648\u0648 \u0644\u06d5 \u0628\u06d5\u0631\u062f\u061b \u0686\u0648\u0646\u06a9\u06d5 \u062a\u06d5\u062e\u062a\u06d5\u0628\u06d5\u0631\u062f\u06cc \u0648\u0627 \u0647\u06d5\u0646 \u0686\u06d5\u0646\u062f \u062c\u06c6\u0628\u0627\u0631\u06cc \u0644\u06d5 \u0628\u06d5\u0631 \u062f\u06d5\u0695\u06c6\u0646\u061b \u0647\u06d5\u06cc\u0634\u06d5 \u062a\u0631\u06d5\u06a9 \u062a\u0631\u06d5\u06a9 \u062f\u06d5\u0628\u06ce \u0648 \u0626\u0627\u0648\u06cc \u0644\u06ce\u0648\u06d5 \u0641\u06cc\u0686\u0642\u06d5 \u062f\u06d5\u06a9\u0627\u061b \u0647\u06cc \u0648\u0627\u0634 \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u062a\u0631\u0633\u06cc \u062e\u0648\u0627 \u062c\u0627\u06b5\u06d5 \u062f\u06d5\u0628\u06ce. \u062e\u0648\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0648 \u0647\u06cc\u0648\u0627\u06cc\u06d5\u0646 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u067e\u06ce \u0628\u06ce\u0646\u06cc\u0646\u060c \u06a9\u06d5\u0686\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646 \u0648\u062a\u0627\u0631\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0633\u062a \u0648 \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5 \u062a\u06ce\u06cc \u062f\u06d5\u06af\u06d5\u06cc\u06cc\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u06af\u06c6\u0695\u06cc \u0648 \u062f\u06d5\u0634\u06cc\u0627\u0646\u0632\u0627\u0646\u06cc \u06a9\u06d5 \u062f\u06d5\u06cc\u06af\u06c6\u0695\u0646\u061f',
    '\u0647\u06d5\u0631\u06af\u0627\u06cc\u06d5\u06a9\u06cc\u0634 \u062a\u0648\u0648\u0634\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u062f\u06d5\u0628\u0646\u060c \u0626\u06ce\u0698\u0646: \u0628\u0627\u0648\u06d5\u0695\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0647\u06d5\u0631\u06af\u0627 \u062f\u06d5\u06af\u06d5\u0646\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648 \u0632\u0627\u0646\u0633\u062a\u06d5\u06cc \u062e\u0648\u0627 \u067e\u06ce\u062f\u0627\u0648\u0646 \u0644\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u062f\u06d5\u062f\u0631\u06a9\u06ce\u0646\u0646\u060c \u062a\u0627 \u0628\u06cc\u06a9\u06d5\u0646\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0644\u062c\u06d5\u0645 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u061f \u062f\u06d5\u0626\u0627\u062e\u0631 \u062a\u0627 \u06a9\u06d5\u06cc \u062a\u06ce\u0646\u0627\u06af\u06d5\u0646!\u061f',
    '\u0628\u06c6\u0686\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646 \u0647\u06d5\u0631\u0686\u06cc \u0646\u0647\u06ce\u0646\u06cc \u062f\u06b5\u06cc\u0627\u0646\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u06d5\u06cc\u06b5\u06ce\u0646\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce\u061f',
    '\u0644\u06d5\u0648\u0627\u0646\u06cc\u0634\u062f\u0627 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u0646 \u06a9\u06d5 \u0646\u06d5\u062e\u0648\u06ce\u0646\u062f\u06d5\u0646 \u0648 \u0628\u06ce\u0698\u06af\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u060c \u0644\u06d5 \u06a9\u062a\u06ce\u0628 \u0633\u06d5\u0631 \u062f\u06d5\u0631\u0646\u0627\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06d5\u06cc\u0627\u06b5\u0627\u062a \u0648 \u06af\u0648\u0645\u0627\u0646\u062f\u0627\u0646.',
    '\u0633\u0627 \u0647\u0627\u0648\u0627\u0631\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6 \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u06d5 \u062f\u06d5\u0646\u0648\u0648\u0633\u0646 \u0648 \u067e\u0627\u0634\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u062a\u0627 \u0628\u06d5 \u0646\u0631\u062e\u06cc \u06a9\u06d5\u0645 \u0628\u06cc\u0641\u0631\u06c6\u0634\u0646. \u0647\u0627\u0648\u0627\u0631 \u0644\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0648\u06d5\u06cc \u0628\u06d5 \u062f\u06d5\u0633 \u0646\u0648\u0648\u0633\u06cc\u0648\u06cc\u0627\u0646\u06d5 \u0648 \u0647\u0627\u0648\u0627\u0631 \u0644\u06d5\u0648\u0627\u0646 \u0628\u06c6 \u0646\u0631\u062e\u06ce \u06af\u06cc\u0631\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0648\u0648\u06d5.',
    '\u062f\u06d5\u0634\u06b5\u06ce\u0646: \u0626\u0627\u06af\u0631 \u062a\u0648\u0648\u0634\u0645\u0627\u0646 \u0646\u0627\u0628\u06ce\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0686\u06d5\u0646\u062f \u0695\u06c6\u0698\u06ce\u060c \u0626\u06d5\u0648\u06cc\u0634 \u06a9\u06d5\u0645. \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u062e\u0648\u062f\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u067e\u06ce \u062f\u0627\u0648\u0646 \u2013 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u06c6\u06cc \u067e\u0627\u0634\u06af\u06d5\u0632 \u0646\u0627\u0628\u06ce \u2013 \u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u06d5\u0632\u0627\u0646\u06cc\u06d5\u0648\u06d5 \u0642\u0633\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062e\u06c6\u06cc \u062a\u0648\u0648\u0634\u06cc \u062e\u0631\u0627\u067e\u06d5 \u0628\u06a9\u0627 \u0648 \u062e\u0631\u0627\u067e\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u0648\u0631\u06cc \u062f\u0627\u0628\u06ce\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06cc\u0627\u0631\u06cc \u0626\u0627\u06af\u0631\u0646 \u0648 \u062a\u0627 \u0647\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u062a\u06ce\u06cc\u062f\u0627 \u062f\u06d5\u0628\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646\u060c \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u0646 \u0648 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u0633\u06d5\u0631\u062f\u06d5\u0645\u06d5\u0634 \u06a9\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646\u0646 \u0644\u06d5 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644 \u0648\u06d5\u0631\u06af\u0631\u062a: \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0646\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646 \u0648 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u0627\u06a9 \u0648 \u0628\u0627\u0628 \u0648 \u0646\u0632\u06cc\u06a9\u0627\u0646 \u0648 \u0647\u06d5\u062a\u06cc\u0648 \u0648 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646 \u06a9\u06d5\u0646 \u0648 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0642\u0633\u06d5\u06cc \u0628\u0627\u0634 \u0628\u06a9\u06d5\u0646\u061b \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u062f\u06d5\u0646\u061b \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5 \u2013 \u06a9\u06d5\u0645\u06ce\u06a9 \u0646\u06d5\u0628\u06ce \u2013 \u067e\u0634\u062a\u0648\u0648 \u062a\u06ce\u06a9\u0631\u062f \u0648 \u062e\u06c6\u0648 \u0644\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u06a9\u06d5 \u0628\u0648\u0627\u0631\u062f.',
    '\u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u0644\u06ce\u062a\u0627\u0646 \u0626\u06d5\u0633\u062a\u0627\u0646\u062f \u06a9\u06d5: \u0647\u0648\u0648\u0646\u06cc \u06cc\u06d5\u06a9\u062a\u0631 \u0645\u06d5\u0695\u06ce\u0698\u0646 \u0648 \u062e\u06c6\u062a\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u0632\u06ce\u062f\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0648\u06d5\u062f\u06d5\u0631 \u0645\u06d5\u0646\u06ce\u0646. \u067e\u0627\u0634\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u0634\u062a\u0627\u0646 \u0628\u0695\u06cc\u0627\u0631\u0648\u0648 \u062f\u0627 \u0648 \u0628\u06c6 \u062e\u06c6\u0634\u062a\u0627\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u06d5\u062f\u06d5\u0646.',
    '\u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5\u0634 \u0647\u06d5\u0631 \u062e\u06c6\u062a\u0627\u0646 \u0628\u0648\u0648\u0646 \u06a9\u0648\u0634\u062a\u0627\u0631\u0648\u0648 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u062f\u06d5\u06a9\u0631\u062f\u061b \u0628\u06d5 \u067e\u0634\u062a\u06cc\u0648\u0627\u0646\u06cc \u062e\u06d5\u06b5\u06a9\u06cc \u062a\u0631 \u2013 \u0628\u06d5 \u062a\u0627\u0648\u0627\u0646 \u0648 \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06cc \u2013 \u0647\u06ce\u0646\u062f\u06ce \u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u0646\u060c \u0644\u06d5 \u0648\u06b5\u0627\u062a\u06cc\u0627\u0646 \u0648\u06d5\u062f\u06d5\u0631 \u062f\u06d5\u0646\u06ce\u0646. \u06a9\u06d5 \u0644\u06ce\u0634\u0648\u0648 \u0628\u06d5 \u062f\u06cc\u0644 \u062f\u06d5\u06af\u06cc\u0631\u06ce\u060c \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u062e\u0648\u06ce\u0646\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u062f\u06d5\u0646\u061b \u06a9\u06d5 \u0647\u06cc\u0686 \u0628\u06c6\u0634\u062a\u0627\u0646 \u0695\u06d5\u0648\u0627 \u0646\u06cc\u06cc\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce \u0644\u06d5 \u0646\u0627\u0648\u06d5\u0631\u06c6\u06a9\u06cc \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u06d5\u0648 \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0644\u06d5\u0646\u06af\u06c6 \u0626\u06d5\u0648\u06d5 \u0628\u06a9\u0627 \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627\u062f\u0627 \u0695\u06cc\u0633\u0648\u0627 \u062f\u06d5\u0628\u06ce \u0648 \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627\u0634\u0627 \u0632\u06c6\u0631 \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u062f\u0631\u06ce. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u0627\u0646 \u06a9\u0695\u06cc\u0648\u06d5 \u0648 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0631\u062e\u06cc \u062f\u0627\u0648\u06d5\u060c \u0626\u06cc\u062a\u0631 \u0646\u06d5 \u0628\u0627\u0631\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0633\u0648\u0648\u06a9 \u062f\u06d5\u06a9\u0631\u06ce \u0648 \u0646\u06d5 \u06a9\u06d5\u0633 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u06a9\u062a\u06ce\u0628\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0648 \u0686\u06d5\u0646\u062f \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062a\u0631\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648 \u0646\u0627\u0631\u062f. \u0686\u06d5\u0646\u062f \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0632\u06c6\u0631 \u0626\u0627\u0634\u06a9\u0631\u0627\u0634\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0639\u06cc\u0633\u0627 \u06a9\u0648\u0695\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645\u060c \u0644\u06d5 \u06af\u06cc\u0627\u0646\u06cc \u067e\u06cc\u0631\u06c6\u0632\u06cc \u062e\u06c6\u0634\u0645\u0627\u0646 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0646 \u062f\u0627. \u0647\u06d5\u062a\u0627 \u0626\u06ce\u0633\u062a\u0627 \u0647\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u0648\u0648 \u0628\u06c6 \u0647\u0627\u062a \u0648 \u0634\u062a\u06ce \u0648\u0627\u06cc \u06af\u0648\u062a \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06ce\u062a\u0627\u0646 \u062e\u06c6\u0634 \u0646\u06d5\u0647\u0627\u062a\u060c \u062e\u06c6\u062a\u0627\u0646 \u0644\u06d5\u0648 \u0628\u06d5 \u0632\u0644\u062a\u0631 \u0632\u0627\u0646\u06cc \u0648 \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u062a\u0627\u0646 \u06af\u0648\u062a \u062f\u0631\u06c6\u0632\u0646 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646\u0648\u0648 \u0647\u06d5\u0631 \u06a9\u0648\u0634\u062a\u0648\u0648\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062f\u06b5\u0645\u0627\u0646 \u0633\u06d5\u0631\u067e\u06c6\u0634\u06cc \u067e\u06ce\u062f\u0627 \u062f\u0631\u0627\u0648\u06d5. \u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0647\u06c6\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0644\u0627\u062f\u0627\u0646\u06cc\u0627\u0646\u060c \u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646 \u0648 \u0628\u06d5 \u062f\u06d5\u06af\u0645\u06d5\u0646 \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0628\u06c6 \u062f\u06ce \u0648 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u0634 \u062f\u06d5\u0633\u06d5\u0644\u0645\u06ce\u0646\u06ce \u06a9\u06d5 \u0626\u06ce\u0633\u062a\u0627 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648 \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u0634\u062f\u0627 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u0648\u0648 \u0628\u06d5\u0633\u06d5\u0631 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u062f\u0627 \u0632\u0627\u06b5\u06cc\u0627\u0646 \u0628\u06a9\u0627\u060c \u06a9\u06d5\u0686\u06cc \u0648\u06d5\u062e\u062a\u06ce \u0647\u0627\u062a \u0648 \u0646\u0627\u0633\u06cc\u06cc\u0627\u0646\u060c \u0628\u06d5 \u06af\u0698\u06cc\u062f\u0627 \u062f\u06ce\u0646. \u062f\u06d5\u0633\u0627 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u062f\u0698 \u0628\u06d5\u0648\u0646\u060c \u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u062e\u0648\u0627 \u06a9\u06d5\u0648\u0646.',
    '\u0626\u0627\u06cc \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u0631\u062e\u06ce\u06a9\u06cc \u062e\u0631\u0627\u067e \u0641\u0631\u06c6\u0634\u062a! \u0647\u06d5\u0631 \u0686\u0648\u0646\u06a9\u06d5 \u062e\u0648\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u0639\u06d5\u0628\u062f\u06ce \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0648\u0627 \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u0628\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u0628\u062e\u0648\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06d5\u0648 \u0628\u0633\u067e\u06ce\u0631\u06ce\u060c \u0626\u06ce\u0631\u06d5\u06cc\u06cc \u0628\u06d5\u0648 \u062f\u06d5\u0631\u062f\u06d5\u06cc \u0628\u0631\u062f\u0646 \u06a9\u06d5 \u0644\u06ce\u06cc \u0628\u06a9\u06d5\u0648\u0646\u06d5 \u062d\u0627\u0634\u0627\u06cc\u06d5\u061b \u0628\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u06cc\u0627\u0646 \u062f\u0648\u0648 \u062c\u0627\u0631 \u0648\u06d5\u0628\u06d5\u0631 \u063a\u06d5\u0632\u06d5\u0628 \u06a9\u06d5\u0648\u062a\u0646\u061b \u0626\u06d5\u0648 \u0686\u0627\u0648\u0686\u0646\u06c6\u06a9\u0627\u0646\u06d5 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0626\u0627\u0628\u0695\u0648\u0648\u0628\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u062a\u0648\u0648 \u067e\u06ce\u06cc\u0627\u0646 \u0628\u06ce\u0698\u0646: \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u0628\u0695\u0648\u0627\u0648 \u0647\u06d5\u0628\u06ce\u060c \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u0646 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u060c \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u061b \u06a9\u062a\u06ce\u0628\u06cc \u062a\u0631 \u2013 \u0628\u0627 \u0628\u0695\u0648\u0627\u0634\u06cc \u0628\u06d5 \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0628\u06ce \u2013 \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0646\u0627\u0632\u0627\u0646\u06cc\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u0648 \u0647\u06d5\u06cc\u06d5\u060c \u0626\u06d5\u062f\u06cc \u0628\u06c6\u0686\u06cc \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 [\u0644\u06d5 \u062e\u0648\u062a\u0648\u062e\u06c6\u0695\u0627\u06cc\u06cc] \u062f\u06d5\u06a9\u0648\u0634\u062a\u061f',
    '\u06a9\u0627\u062a\u06ce \u0645\u0648\u0648\u0633\u0627\u0634 \u0628\u06d5 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u067e\u062a\u06d5\u0648 \u0647\u0627\u062a\u06d5 \u0644\u0627\u062a\u0627\u0646\u060c \u06a9\u06d5\u0686\u06cc \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648 \u06af\u0648\u06ce\u0631\u06d5\u06a9\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u060c \u062f\u06d5\u062a\u0627\u0646\u067e\u06d5\u0631\u0633\u062a.',
    '\u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634\u062f\u0627 \u06a9\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06d5\u0633\u062a\u0646 \u0648 \u06a9\u06ce\u0648\u06cc \u062a\u0648\u0648\u0631\u0645\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0628\u06d5\u0631\u0632 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u067e\u06ce\u0645\u0627\u0646 \u06af\u0648\u062a\u0646: \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u0695\u0627 \u0628\u06c6 \u062f\u06ce \u062a\u0648\u0646\u062f\u06cc \u0628\u06af\u0631\u0646 \u0648 \u0628\u06cc\u0628\u06cc\u0633\u0646\u061b \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06d5\u06b5\u06ce\u060c \u0628\u06cc\u0633\u062a\u0645\u0627\u0646 \u0648 \u06af\u0648\u06ce \u0646\u0627\u062f\u06d5\u06cc\u0646\u06ce. \u0626\u06d5\u0648\u06cc\u0646\u06cc \u06af\u06c6\u0644\u06a9 \u067e\u06d5\u0631\u0633\u062a\u06cc \u0648\u06d5\u0647\u0627\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06b5 \u0686\u06d5\u0642\u06cc\u0628\u0648\u0648 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u0631\u062f\u0646\u06d5\u0648\u06d5. \u0628\u06ce\u0698\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0646\u060c \u0628\u0695\u0648\u0627\u06a9\u06d5\u062a\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u06a9\u0627\u0631\u06cc \u06af\u06d5\u0644\u06ce \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0695\u0627\u0633\u062a\u0627\u0646\u06d5 \u0648 \u067e\u06ce\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0645\u0627\u06b5\u06cc \u0626\u06d5\u0648\u0644\u0627 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062a\u06d5\u0631\u062e\u0627\u0646 \u06a9\u0631\u0627\u0648\u06d5 \u0648 \u0628\u06d5\u0634\u06cc \u06a9\u06d5\u0633\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5\u060c \u062f\u06d5\u0633\u0627 \u062f\u0627\u0648\u0627 \u0628\u06a9\u06d5\u0646 \u0628\u0645\u0631\u0646.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u0626\u06d5\u0648 \u062f\u0627\u0648\u0627\u06cc\u06d5 \u0646\u0627\u06a9\u06d5\u0646\u060c \u0686\u0648\u0646 \u062f\u06d5\u0632\u0627\u0646\u0646 \u0686\u06cc\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u062e\u0648\u062f\u0627\u0634 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u062f\u06d5\u0646\u0627\u0633\u06ce.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u0628\u06cc\u0646\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0628\u06c6 \u0698\u06cc\u0627\u0646\u06ce \u067e\u0627\u067e\u0627\u062a\u0631\u0646\u061b \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a\u06cc \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u0627 \u062f\u06d5\u0646\u0627\u0633\u0646. \u0647\u06d5\u0631\u06a9\u0627\u0645 \u0644\u06d5\u0648\u0627\u0646 \u067e\u06ce \u062e\u06c6\u0634\u06d5 \u0647\u06d5\u0632\u0627\u0631 \u0633\u0627\u06b5 \u0628\u0698\u06cc. \u062e\u06c6 \u062a\u06d5\u0645\u06d5\u0646\u06cc \u0647\u06d5\u0632\u0627\u0631 \u0633\u0627\u06b5\u06d5\u0634 \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u0648\u0648\u0631\u06cc \u0646\u0627\u062e\u0627\u062a\u06d5\u0648. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0631\u062f\u06d5\u06cc\u0627\u0646\u06cc \u0644\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u06a9\u06d5 \u062f\u0648\u0698\u0645\u0646\u06cc \u062c\u0648\u0628\u0631\u0627\u0626\u06cc\u0644\u06d5[ \u0628\u0627 \u0628\u0632\u0627\u0646\u06ce: ] \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u06cc \u2013 \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u0627 \u2013 \u062d\u06d5\u0648\u0627\u06b5\u06d5\u06cc \u062f\u06b5\u06cc \u062a\u06c6 \u062f\u06d5\u06a9\u0627 \u0648 \u0628\u06d5 \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u0627\u0646\u06cc \u062f\u06cc\u06a9\u06d5\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06cc\u0634 \u0628\u0627\u0648\u06d5\u0695 \u062f\u06d5\u06a9\u0627 \u0648 \u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631 \u0648 \u0645\u0698\u062f\u06d5 \u062f\u06d5\u0631\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0646.',
    '\u0647\u06d5\u0631\u06a9\u06ce \u062f\u0698\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0648 \u0641\u0631\u06cc\u0634\u062a\u0627\u0646\u06cc \u0648 \u062c\u0648\u0628\u0631\u0627\u0626\u06cc\u0644 \u0648 \u0645\u06cc\u06a9\u0627\u06cc\u0644 \u0628\u06ce\u060c \u062e\u0648\u062f\u0627\u0634 \u062f\u0698 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0645\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0646 \u0628\u06c6 \u062a\u06c6 \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062d\u0627\u0634\u0627\u06cc \u0644\u06ce\u062f\u06d5\u06a9\u06d5\u0646\u060c \u0647\u06d5\u0631 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0626\u06d5\u062f\u06cc \u0628\u06c6\u0686\u06cc \u0647\u06d5\u0631 \u062c\u0627\u0631\u06ce\u06a9\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u0627 \u062f\u06d5\u0628\u06d5\u0633\u062a\u0646\u060c \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u062f\u06ce\u0646 \u0647\u06d5\u06b5\u06cc\u062f\u06d5\u0648\u06d5\u0634\u06ce\u0646\u0646\u06d5\u0648\u06d5. \u0632\u06c6\u0631\u06cc\u0646\u06d5\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc \u067e\u06ce \u0646\u0627\u0647\u06ce\u0646\u0646.',
    '\u062f\u0648\u0627\u06cc \u0626\u06d5\u0645\u06d5\u06cc \u0626\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u060c \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u0634 \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u0627\u06cc\u0627\u0646\u06d5\u061b \u062f\u06cc\u0633\u0627\u0646 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646\u060c \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u062e\u0648\u0627 \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646 \u0648 \u0695\u0648\u0648\u06cc \u062a\u06ce\u0646\u0627\u06a9\u06d5\u0646. \u0647\u06d5\u0631 \u0626\u06ce\u0698\u06cc \u0646\u06d5\u0634\u06cc\u0627\u0646 \u0632\u0627\u0646\u06cc\u0648\u06d5.',
    '\u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0634\u0627\u06cc\u06d5\u062a\u06cc\u0646\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u0627\u0646\u06cc \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646\u062f\u0627 \u0628\u06d5 \u0648\u0631\u062a\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u062e\u0648\u06ce\u0646\u062f\u06d5\u0648\u06d5. \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0646\u06d5\u0628\u0648\u0648\u061b \u0634\u0627\u06cc\u06d5\u062a\u06cc\u0646\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646\u061b \u062e\u06d5\u06b5\u06a9\u06cc\u0627\u0646 \u0641\u06ce\u0631\u06d5 \u062c\u0627\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0631\u062f. \u0626\u06d5\u0648 \u0632\u0627\u0646\u0633\u062a\u06d5\u0634 \u06a9\u06d5 \u0647\u0627\u062a\u0628\u0648\u0648\u06d5 \u0633\u06d5\u0631 \u0647\u0627\u0631\u0648\u0648\u062a \u0648 \u0645\u0627\u0631\u0648\u0648\u062a\u06cc \u062c\u0648\u0648\u062a\u06d5 \u0641\u0631\u0634\u062a\u06d5 \u0644\u06d5 \u0628\u0627\u0628\u0644\u062f\u0627\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u0641\u06ce\u0631\u06cc \u062c\u0627\u062f\u0648\u0648 \u0628\u06a9\u0631\u062f\u0627\u06cc\u06d5\u060c \u067e\u06ce\u0634\u06d5\u06a9\u06cc \u062a\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06cc\u0627\u0646\u062f: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u062c\u06d5\u0695\u0628\u0627\u0646\u062f\u0646\u06cc\u0646\u060c \u0646\u06d5\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627\u062a \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u0686\u06ce\u061b \u0647\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0641\u06ce\u0631 \u062f\u06d5\u0628\u0648\u0648\u0646 \u0698\u0646 \u0648 \u0634\u0648\u0648 \u0644\u06ce\u06a9 \u062c\u06cc\u0627\u0648\u06d5 \u06a9\u06d5\u0646\u061b \u06a9\u06d5 \u062a\u0648\u0627\u0646\u0627\u06cc \u0626\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648 \u2013 \u0628\u06ce \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u0627 \u2013 \u0632\u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce \u0628\u062f\u06d5\u0646. \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0648\u0627 \u0641\u06ce\u0631 \u062f\u06d5\u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0632\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06d5\u06b5\u06a9 \u0628\u062f\u0627 \u0648 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0633\u0648\u0648\u062f\u06ce\u06a9\u06cc \u0646\u06d5\u0628\u06ce\u061b \u062f\u06d5\u0634\u06cc\u0627\u0646\u0632\u0627\u0646\u06cc \u06a9\u0695\u06cc\u0627\u0631\u0627\u0646\u06cc \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u06cc \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u0647\u06cc\u0686 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u0626\u0627\u06cc \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06ce\u06af\u06d5\u06cc\u0634\u062a\u0628\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646\u0632\u0627\u0646\u06cc \u0686\u06c6\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u062e\u0631\u0627\u067e \u062f\u06d5\u0641\u0631\u06c6\u0634\u0646.',
    '\u0626\u06d5\u0645\u0627\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u0627 \u0648 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631 \u0628\u0627\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u0632\u0627\u0646\u06cc \u06a9\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u0686\u0627\u06a9\u062a\u0631 \u0648\u0627 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06ce\u0648\u06d5 \u0645\u06d5\u0628\u06ce\u0698\u0646 \u00ab\u0695\u0627\u0639\u0650\u0646\u0627\u00bb\u061b \u0628\u06ce\u0698\u0646: \u00ab\u062a\u0645\u0627\u0634\u0627\u0645\u0627\u0646 \u0628\u06a9\u06d5\u00bb \u0648 \u06af\u0648\u06ce \u0628\u062f\u06d5\u0646\u06ce. \u0628\u06c6 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646 \u0648 \u062f\u06cc\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06d5\u0633 \u062f\u0627\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627 \u0628\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0634\u06d5\u0631\u06cc\u06a9 \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u062d\u06d5\u0632 \u0646\u0627\u06a9\u06d5\u0646 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc \u0686\u0627\u06a9\u062a\u0627\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648\u0695\u0627 \u0628\u06c6 \u0628\u06ce. \u062e\u0648\u062f\u0627\u0634 \u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc\u060c \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u2013 \u0628\u06d5\u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u2013 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06cc\u062e\u0648\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u062e\u06ce\u0648\u06cc \u0686\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0647\u06d5\u0631\u06d5 \u0632\u06c6\u0631 \u0648 \u0632\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u0627\u06cc\u06d5\u062a\u06ce\u06a9 \u0644\u06d5 \u06a9\u0627\u0631 \u0628\u062e\u06d5\u06cc\u0646 \u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631\u06cc \u0628\u0628\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u060c \u0626\u0627\u06cc\u06d5\u062a\u06ce\u06a9\u06cc \u0644\u06d5\u0648 \u0628\u0627\u0634\u062a\u0631 \u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06cc \u0626\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u062c\u06ce. \u0645\u06d5\u06af\u06d5\u0631 \u0646\u06d5\u062a\u0632\u0627\u0646\u06cc\u0648\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u067e\u06ce \u062f\u06d5\u06a9\u0631\u06ce.',
    '\u0646\u06d5\u062a\u0632\u0627\u0646\u06cc\u0648\u06d5 \u06a9\u06d5 \u0626\u06d5\u0645 \u0639\u06d5\u0631\u0632 \u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0645\u0648\u06b5\u06a9\u06cc \u062e\u0648\u0627\u0646\u060c \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0634 \u062f\u06c6\u0633\u062a \u0648 \u0647\u0627\u0631\u06cc\u06a9\u0627\u0631\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u062a\u0627\u0646 \u0628\u067e\u0631\u0633\u0646\u060c \u0648\u06d5\u06a9\u0648\u0648 \u067e\u06ce\u0634\u0648\u0648 \u0644\u06d5 \u0645\u0648\u0648\u0633\u0627 \u067e\u0631\u0633\u06cc\u0627\u0631 \u06a9\u0631\u0627\u0628\u0648\u0648\u061f \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u06ce \u062e\u0648\u062f\u0627\u06cc\u06cc \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0628\u06af\u06c6\u0695\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0628\u06d5 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u06cc \u0644\u06ce \u0648\u0646 \u0628\u0648\u0648\u06d5.',
    '\u0632\u06c6\u0631 \u06a9\u06d5\u0633 \u0644\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u06a9\u062a\u06ce\u0628\u0627\u0646 \u2013 \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06ce\u0631\u06d5\u06cc\u06cc\u0648 \u067e\u06ce \u062f\u06d5\u0628\u06d5\u0646 \u2013 \u062d\u06d5\u0632\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u0644\u06d5 \u067e\u0627\u0634 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0647\u06ce\u0646\u0627\u0646\u0648\u0648 \u2013 \u06a9\u06d5 \u0644\u06d5 \u0644\u0627\u0634\u06cc\u0627\u0646 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5 \u0695\u0627\u0633\u062a \u06a9\u0627\u0645\u06d5\u06cc\u06d5 \u2013 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u0648\u0648 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5. \u0626\u06ce\u0648\u06d5 \u0686\u0627\u0648\u06cc\u0627\u0646 \u0644\u06ce \u0628\u067e\u06c6\u0634\u0646 \u0648 \u0644\u06d5 \u0647\u06d5\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u0646\u060c \u062a\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06af\u0627\u061b \u062e\u0648\u0627 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06cc \u067e\u06ce \u062f\u06d5\u06a9\u0631\u06ce.',
    '\u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u062f\u06d5\u0646\u061b \u0647\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9 \u0628\u06a9\u06d5\u0646\u06d5 \u067e\u06ce\u0634 \u0628\u0627\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646\u060c \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u067e\u06ce\u0648 \u062f\u06d5\u06af\u0627\u062a\u06d5\u0648\u06d5\u061b \u0647\u06d5\u0631\u0686\u06cc \u0628\u06cc\u06a9\u06d5\u0646 \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u0645\u0648\u0648\u0633\u0627\u06cc\u06cc \u06cc\u0627\u0646 \u0639\u06cc\u0633\u0627\u06cc\u06cc \u062f\u06d5\u0686\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u061b \u0626\u06d5\u0645\u06d5 \u0626\u0627\u0648\u0627\u062a\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06d5. \u0628\u06ce\u0698\u06d5: \u062f\u06d5\u0633\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06ce\u0648 \u0648\u0627\u06cc\u06d5 \u0695\u0627\u0633\u062a \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u06d5\u06b5\u06ce\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u2013 \u0628\u06d5 \u0628\u06ce \u062e\u06d5\u0648\u0634 \u2013 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0695\u0648\u0648\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06ce \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u06ce\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u06ce \u0648 \u0626\u06d5\u0645 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0646\u06d5 \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06ce\u062a\u06d5 \u0633\u06d5\u0631\u060c \u0646\u06d5 \u062e\u06d5\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u062c\u0647\u0648\u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0641\u06d5\u0644\u06d5\u06a9\u0627\u0646 \u0647\u06cc\u0686 \u06a9\u0627\u0631\u06d5\u06cc\u06d5\u06a9 \u0646\u06cc\u0646. \u0641\u06d5\u0644\u06d5\u0634 \u06a9\u0648\u062a\u06cc\u0627\u0646: \u062c\u0648\u0648\u0644\u06d5\u06a9\u06d5\u06a9\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06cc\u0686\u0646\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u062f\u0648\u0648 \u0644\u0627\u0634 \u06a9\u062a\u06ce\u0628 \u062e\u0648\u06ce\u0646\u0646. \u0646\u06d5\u0632\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u062f\u06d5\u06b5\u06ce\u0646 \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646. \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u0628\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc\u0627\u0646\u06d5 \u0695\u0627\u062f\u06d5\u06af\u0627 \u06a9\u06d5 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06cc \u0628\u0648\u0648.',
    '\u06a9\u06ce \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0695\u06ce\u06af\u06d5 \u0646\u06d5\u062f\u0627 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0645\u0632\u06af\u06d5\u0648\u062a\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627\u062f\u0627 \u0628\u0647\u06ce\u0646\u062f\u0631\u06ce \u0648 \u0647\u06d5\u0648\u06b5\u06cc\u0634 \u0628\u062f\u0627 \u0648\u06ce\u0631\u0627\u0646 \u0628\u0628\u0646\u061f \u0646\u06d5\u062f\u06d5\u0628\u0648\u0627\u06cc\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0628\u0686\u0646\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646 \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5 \u062a\u0631\u0633 \u0648 \u0644\u06d5\u0631\u0632\u06d5\u0648\u06d5. \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0633\u0648\u0648\u06a9\u06cc\u06cc\u06d5 \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u0698\u0648\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u062e\u06c6\u0631\u0647\u06d5\u06b5\u0627\u062a \u0648 \u062e\u06c6\u0631\u0626\u0627\u0648\u0627 \u2013 \u0647\u06d5\u0631\u062a\u06a9 \u2013 \u0647\u06cc \u062e\u0648\u062f\u0627\u0646\u061b \u0695\u0648\u0648 \u0628\u06a9\u06d5\u0646\u06d5 \u0647\u06d5\u0631 \u0644\u0627\u06cc\u06d5\u06a9\u06cc\u060c \u062e\u0648\u0627 \u0644\u06d5\u0648\u06ce\u06cc\u06d5\u061b \u0632\u0627\u0646\u0633\u062a\u06cc \u062e\u0648\u0627 \u0628\u06d5\u0631\u0628\u06b5\u0627\u0648\u06d5 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u0648\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0647\u06d5\u06b5\u0628\u0698\u06cc\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u067e\u0627\u06a9\u06d5\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u060c \u06af\u0634 \u0628\u06c6 \u0626\u06d5\u0648\u06d5 \u0648 \u06af\u0634\u062a\u06cc \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u0646.',
    '\u062f\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5 \u0648\u06cc\u0633\u062a\u06cc \u06a9\u0627\u0631\u06ce \u067e\u06ce\u06a9 \u0628\u06ce\u060c \u0641\u06d5\u0631\u0645\u0627\u0646 \u062f\u06d5\u062f\u0627: \u0628\u0628\u06d5! \u062f\u06d5\u0628\u06ce.',
    '\u0644\u06d5 \u0647\u06cc\u0686 \u0646\u06d5\u0632\u0627\u0646\u06d5\u06a9\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06c6\u0686\u06cc \u062e\u0648\u0627 \u0626\u06ce\u0645\u06d5 \u0646\u0627\u062f\u0648\u06ce\u0646\u06ce\u060c \u06cc\u0627\u0646 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u06cc\u06d5\u061f \u0628\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0645 \u0642\u0633\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0628\u0648\u0648\u06d5. \u062f\u06cc\u0627\u0631\u06d5 \u062f\u06b5\u06cc\u0627\u0646 \u0644\u06ce\u06a9\u062a\u0631 \u062f\u06d5\u06a9\u06d5\u0646. \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u0627\u0648\u06d5 \u06a9\u06d5 \u0626\u0627\u0645\u0627\u062f\u06d5\u0646 \u0628\u06cc\u0633\u06d5\u0644\u0645\u06ce\u0646\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u062a\u06ce\u0695\u0627\u062f\u06cc\u0648\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5 \u06a9\u06d5 \u0645\u0632\u06af\u06ce\u0646\u06cc\u0634 \u0628\u062f\u06d5\u06cc \u0648 \u0628\u06cc\u0634\u06cc\u0627\u0646 \u062a\u0631\u0633\u06ce\u0646\u06cc\u061b \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062f\u06c6\u0698\u06d5\u0647\u06cc\u06cc\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0628\u06d5\u0631\u067e\u0631\u0633 \u0646\u06cc\u062a.',
    '\u0626\u06d5\u0645 \u062c\u0648\u0648\u0644\u06d5\u06a9\u0627\u0646\u06d5 \u0648 \u0641\u06d5\u0644\u0627\u0646\u06d5\u060c \u062a\u0627 \u0646\u06d5\u0686\u06cc\u06d5 \u0633\u06d5\u0631 \u062f\u06cc\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u062a\u06c6 \u0646\u0627\u06cc\u06d5. \u0628\u06ce\u0698\u06d5: \u0695\u06ce\u06cc \u0695\u0627\u0633 \u0647\u06d5\u0631 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627\u0633. \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u067e\u0627\u0634 \u0626\u06d5\u0648 \u0632\u0627\u0646\u0633\u062a\u06d5\u06cc \u0628\u06c6\u062a \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06d5\u0648\u06cc\u060c \u0626\u06cc\u062a\u0631 \u062e\u0648\u062f\u0627 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u062a \u0646\u0627\u06a9\u0627 \u0648 \u062a\u0627\u0632\u06d5 \u0646\u0627\u06cc\u06d5\u062a\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u06a9\u062a\u06ce\u0628\u0645\u0627\u0646 \u067e\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5 \u0648 \u0648\u06d5\u06a9 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u062f\u06d5\u06cc\u062e\u0648\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06cc\u0627\u0631\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce\u06cc \u0647\u06d5\u06cc\u06d5\u060c \u0626\u06d5\u0648\u06cc \u0628\u0695\u0648\u0627\u0634\u06cc \u067e\u06ce \u0646\u06d5\u06a9\u06d5\u0646 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u0646.',
    '\u0626\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u06cc \u0633\u06d5\u0631 \u0628\u06d5 \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644! \u0644\u06d5 \u0628\u06cc\u0631 \u0645\u06d5\u06a9\u06d5\u0646 \u0686\u06d5\u0646\u062f\u06d5\u0645 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0646\u060c \u0644\u06d5 \u0686\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062f\u0646\u06cc\u0627\u060c \u067e\u062a\u0631 \u0695\u06ce\u0632\u0645 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u0627.',
    '\u0626\u06d5\u0634\u06ce \u0628\u0634\u062a\u0631\u0633\u0646 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc \u06a9\u06d5 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06d5 \u0648 \u06a9\u0695\u06cc\u0646\u06d5\u0648\u06d5 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0628\u0627\u0648\u06cc \u0646\u06cc\u06cc\u06d5 \u0648 \u062a\u06a9\u0627\u06a9\u0627\u0631\u06cc \u0628\u06d5\u0647\u0631\u06d5 \u0646\u0627\u062f\u0627 \u0648 \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc \u06a9\u06d5\u0633 \u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0686\u06d5\u0646\u062f \u0648\u0634\u06d5 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645\u06cc \u06a9\u0631\u062f\u061b \u067e\u06ce\u06a9\u06cc \u0647\u06ce\u0646\u0627\u0646. \u06af\u0648\u062a\u06cc: \u062f\u06d5\u062a\u06a9\u06d5\u0645 \u0628\u06d5 \u0695\u06ce\u0628\u06d5\u0631\u06cc \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u062f\u06cc \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06a9\u06d5\u0645\u061f \u06af\u0648\u062a\u06cc: \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u06b5\u06ce\u0646\u0645 \u0646\u0627\u06a9\u06d5\u0648\u0646.',
    '\u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634\u0627 \u0628\u0695\u06cc\u0627\u0631\u0646 \u062f\u0627 \u0645\u0627\u06b5\u06cc \u06a9\u0627\u0628\u06d5 \u0645\u06d5\u06a9\u06c6\u06cc\u06d5\u06a9\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0628\u06ce \u0628\u06d5 \u062f\u06b5\u0646\u06cc\u0627\u06cc\u06cc \u0648 \u062c\u06ce\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0628\u06a9\u06d5\u0646 \u0628\u06d5 \u0646\u0648\u06ce\u0698\u06af\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0648 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644\u06cc\u0634\u0645\u0627\u0646 \u0695\u0627\u0633\u067e\u0627\u0631\u062f \u06a9\u06d5 \u0645\u0627\u06b5\u06d5\u06a9\u06d5\u0645 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u06d5\u0648\u0631\u06cc \u062f\u06d5\u0633\u0648\u0648\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u06c6 \u0626\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646 \u0648 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u0695\u0646\u06c6\u0634 \u0648 \u0633\u0648\u0698\u062f\u06d5\u0645 \u0628\u06c6 \u062f\u06d5\u0628\u06d5\u0646\u060c \u067e\u0627\u06a9\u0698 \u0628\u06a9\u06d5\u0646.',
    '\u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0628\u06d5 \u067e\u0627\u0695\u0627\u0646\u06d5\u0648\u06d5 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0626\u06d5\u0645 \u0634\u0627\u0631\u06d5 \u0628\u06ce \u0648\u06d5\u06cc \u0628\u0647\u06ce\u06b5\u06d5\u061b \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u0634\u06cc \u0647\u06d5\u0631\u06a9\u06ce \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u0695\u0648\u0627\u06cc \u0647\u06ce\u0646\u0627\u060c \u0644\u06d5 \u0628\u06d5\u0631\u0648\u0628\u0648\u0648 \u062a\u06d5\u0633\u06d5\u0644\u06cc\u0627\u0646 \u06a9\u06d5. \u06af\u0648\u062a\u06cc: \u0647\u06d5\u0631\u06a9\u06ce\u0634 \u062d\u0627\u0634\u0627 \u0628\u06a9\u0627\u060c \u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0645 \u0633\u06d5\u0631\u062f\u06d5\u0645\u06ce\u06a9\u06cc \u06a9\u06d5\u0645 \u0695\u0627\u06cc \u0628\u0628\u0648\u06ce\u0631\u06ce\u060c \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u062a\u06cc\u0646\u06cc \u0626\u0627\u06af\u0631 \u0648 \u0686\u0627\u0631\u06d5\u0695\u06d5\u0634\u06cc \u0628\u06d5\u0695\u06ce\u06cc \u062f\u06d5\u06a9\u06d5\u0645. \u0626\u0627\u06cc \u0686 \u0626\u06d5\u0646\u062c\u0627\u0645\u06ce \u062e\u0631\u0627\u067e\u06d5!',
    '\u0644\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u0634\u062f\u0627 \u06a9\u06d5 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644 \u0628\u0646\u0627\u063a\u06d5\u06cc \u0645\u0627\u06b5\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06b5\u0646\u062f \u062f\u06d5\u06a9\u0631\u062f\u060c \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0644\u06ce\u0646 \u0642\u0628\u0648\u0648\u06b5 \u06a9\u06d5\u06cc\u060c \u062a\u06c6 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u062a \u0632\u06c6\u0631 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0641\u0631\u06d5 \u0632\u0627\u0646\u0627\u06cc.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u062e\u06c6\u062a \u0648\u0627 \u0628\u06a9\u06d5\u06cc \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u062a \u0628\u06cc\u0646\u061b \u062a\u06c6\u0631\u06d5\u0645\u06d5\u0634\u0646 \u0648\u0627 \u0695\u0627\u0628\u06ce\u0646\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9 \u0628\u0646 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062a\u06c6 \u062f\u06d5\u0631\u0646\u06d5\u0686\u0646 \u0648 \u0695\u06ce\u0648\u0634\u0648\u06ce\u0646\u06cc \u062a\u06c6 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5 \u0648 \u062a\u06c6\u0628\u06d5\u0645\u0627\u0646 \u0644\u06ce \u0642\u0628\u0648\u0648\u06b5 \u0628\u06a9\u06d5. \u062a\u06c6\u06cc \u062a\u06c6\u0628\u06d5 \u0642\u0628\u0648\u0648\u06b5 \u062f\u06d5\u06a9\u06d5\u06cc \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06cc \u0647\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0695\u06ce \u0628\u06a9\u06d5\u060c \u062a\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062a\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u062f\u0627 \u0628\u062e\u0648\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u06a9\u062a\u06ce\u0628 \u0648 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u062f\u0627 \u0695\u0627\u06cc\u0627\u0646 \u0628\u06ce\u0646\u06ce \u0648 \u0644\u06d5 \u0647\u06d5\u06b5\u06d5 \u0628\u06ce \u067e\u06d5\u06b5\u06d5\u06cc\u0627\u0646 \u06a9\u0627. \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u062f\u0627\u0631\u06cc \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u060c \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u062a\u06cc.',
    '\u06a9\u06ce\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0626\u0627\u06cc\u06cc\u0646\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0628\u0646\u06ce\u062a\u06d5 \u0644\u0627\u0648\u06d5\u061f \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u06af\u06d5\u0648\u062c \u0628\u06a9\u0627. \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0645\u0627\u0646 \u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627 \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627\u0634\u0627 \u0648\u0627 \u0644\u06d5 \u0695\u06ce\u0632\u06cc \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646\u0627.',
    '\u0648\u06d5\u062e\u062a\u06ce \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u067e\u06ce\u06cc \u06af\u0648\u062a: \u0648\u06d5\u0631\u06d5 \u062e\u06c6 \u0628\u06d5 \u0645\u0646 \u0628\u0633\u067e\u06ce\u0631\u06d5\u060c \u0695\u06af\u0648\u062a\u06cc \u0648\u0627 \u062e\u06c6\u0645 \u062f\u0627\u06cc\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062f\u0646\u06cc\u0627.',
    '\u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0628\u06c6 \u06a9\u0648\u0695\u06d5\u06a9\u0627\u0646\u06cc \u0648 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628\u06cc\u0634\u06cc \u0642\u06d5\u0648\u06cc\u0646\u062a\u06cc \u06a9\u0631\u062f: \u0641\u0631\u0632\u06d5\u0646\u062f\u0627\u0646\u0645! \u062e\u0648\u0627 \u0626\u06d5\u0645 \u062f\u06cc\u0646\u06d5\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u0648\u0648\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0648\u0627\u06cc\u06d5\u060c \u063a\u06d5\u06cc\u0631 \u0628\u06d5 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u06cc \u0645\u06d5\u0645\u0631\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u06af\u0627\u0648 \u0644\u06ce \u0628\u0648\u0648 \u06a9\u06d5 \u0644\u06d5 \u06af\u06cc\u0627\u0646\u06d5\u06b5\u0627\u062f\u0627 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628 \u0628\u06d5 \u0695\u06c6\u06b5\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u06af\u0648\u062a: \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0645\u0646 \u0686\u06cc \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u061f \u06af\u0648\u062a\u06cc\u0627\u0646: \u0686\u06cc \u062a\u06c6 \u067e\u06d5\u0631\u06d5\u0633\u062a\u0648\u0648\u062a\u06d5 \u062f\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646 \u06a9\u06d5 \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u0627\u0646\u06cc \u062a\u06c6\u060c \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644 \u0648 \u0626\u06cc\u0633\u062d\u0627\u0642\u06cc\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a\u061b \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc \u062a\u0627\u06a9 \u0648 \u062a\u06d5\u0646\u06cc\u0627\u06cc\u06d5 \u0648 \u0626\u06ce\u0645\u06d5\u0634 \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5\u0648 \u062f\u06d5\u0633\u067e\u06ce\u0631\u06cc\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u0627\u0646\u06ce\u06a9 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0695\u0627\u0628\u0648\u0631\u062f\u0648\u0648\u0646\u060c \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646\u06d5. \u0626\u06ce\u0648\u06d5\u0634 \u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646\u061b \u06a9\u06d5\u0633 \u0628\u06c6 \u06a9\u0631\u062f\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646\u0648\u0648 \u0644\u06ce \u0646\u0627\u067e\u0631\u0633\u06ce.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0628\u0646\u06d5 \u062c\u0648\u0648\u060c \u06cc\u0627\u0646 \u0641\u06d5\u0644\u06d5\u060c \u062a\u0627\u06a9\u0648\u0648 \u0695\u06ce\u06af\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u0628\u06a9\u06d5\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u06cc\u0646\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645\u06cc\u0646 \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0628\u06d5\u062c\u06ce \u0647\u06ce\u0634\u062a \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0647\u06d5\u0642 \u0647\u0627\u062a\u061b \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u0634\u06cc \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0646\u06d5\u062f\u06d5\u0646\u0627\u0633\u06cc.',
    '\u0626\u06ce\u0648\u06d5 \u0628\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0628\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06c6\u0645\u0627\u0646 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631 \u0648 \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u0646 \u0628\u06c6 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644 \u0648 \u0626\u06cc\u0633\u062d\u0627\u0642 \u0648 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628 \u0648 \u0646\u06d5\u062a\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc \u0648 \u0626\u06d5\u0648\u06cc \u0628\u06c6 \u0645\u0648\u0648\u0633\u0627\u0634 \u0648 \u0639\u06cc\u0633\u0627\u0634 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u062f\u0631\u0627 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646\u06d5\u0648\u06d5\u060c \u0628\u0627\u0648\u06d5\u0695\u0645\u0627\u0646 \u06a9\u0631\u062f. \u0626\u06ce\u0645\u06d5 \u062c\u06cc\u0627\u0648\u0627\u0632\u06cc \u0646\u0627\u062e\u06d5\u06cc\u0646\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u0647\u06cc\u0686 \u06a9\u0627\u0645\u06ce\u06a9 \u0644\u06d5\u0645\u0627\u0646\u06d5 \u0648 \u062e\u06c6\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5.',
    '\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0628\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646\u060c \u0626\u06d5\u0648\u06d5 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u06d5\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u06ce\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6 \u0628\u0628\u0648\u06ce\u0631\u0646\u060c \u0626\u06d5\u0648\u0627\u0646 \u062f\u0698\u0627\u06cc\u06d5\u062a\u06cc\u0648 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0626\u06d5\u062a\u067e\u0627\u0631\u06ce\u0632\u06ce \u0648 \u0632\u06c6\u0631 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0641\u0631\u06d5 \u0632\u0627\u0646\u0627 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0695\u06d5\u0646\u06af\u06ce\u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0695\u0634\u062a\u0648\u0648\u06cc\u06d5 \u0648 \u06a9\u06ce \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0686\u0627\u06a9\u062a\u0631 \u0695\u06d5\u0646\u06af \u0628\u0695\u06ce\u0698\u06ce\u061f \u0626\u06ce\u0645\u06d5\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0645\u06d5\u0695 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06af\u06d5\u06b5\u0645\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u06a9\u06ce\u0634\u06d5\u061b \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0645\u06d5\u0634 \u0648 \u0626\u06ce\u0648\u06d5\u0634 \u0626\u06d5\u0648\u06d5\u061f \u06a9\u0631\u062f\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u0645\u0627\u0646 \u0648 \u06a9\u0631\u062f\u0627\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646\u061b \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u062f\u06b5 \u062f\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646\u061f',
    '\u06cc\u0627 \u0626\u06ce\u0698\u0646 \u06a9\u06d5: \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644 \u0648 \u0626\u06cc\u0633\u062d\u0627\u0642 \u0648 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628 \u0648 \u0646\u06d5\u0648\u06d5\u06cc \u062c\u0648\u0648\u0644\u06d5\u06a9\u06d5\u060c \u06cc\u0627\u0646 \u0645\u06d5\u0633\u06cc\u062d\u06cc \u0628\u0648\u0648\u0646\u061f \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0686\u0627\u06a9\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06cc\u0627\u0646 \u0626\u06ce\u0648\u06d5\u061f \u06a9\u06ce \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5\u060c \u0634\u0627\u06cc\u06d5\u062a\u06ce\u06a9\u06cc \u0644\u06d5 \u0644\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc \u0628\u0634\u0627\u0631\u06ce\u062a\u06d5\u0648\u06d5\u061f \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0626\u0627\u06a9\u0627\u0631\u0648\u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u0627\u0646\u06ce\u06a9 \u0628\u0648\u0648\u0646 \u0626\u06ce\u0633\u062a\u0627 \u0644\u06d5 \u0628\u06d5\u06cc\u0646\u0627 \u0646\u06d5\u0645\u0627\u0648\u0646\u060c \u0686\u06cc\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0628\u06ce \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u0626\u06ce\u0648\u06d5\u0634 \u06a9\u0627\u0631 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646. \u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06ce \u0628\u06d5\u0631\u067e\u0631\u0633 \u0646\u0627\u0628\u0646.',
    '\u0644\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5\u060c \u0628\u06ce \u0626\u0627\u0648\u06d5\u0632\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0686\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627 \u0644\u06d5\u0648 \u0695\u0648\u0648\u06af\u06d5\u06cc\u0627\u0646\u06d5 \u06a9\u06d5 \u0628\u06d5\u0631\u06ce \u0695\u0648\u0648\u06cc\u0627\u0646 \u062a\u06ce\u062f\u06d5\u06a9\u0631\u062f\u061f \u0628\u06ce\u0698\u06d5: \u062e\u0627\u0648\u06d5\u0631 \u0648 \u062e\u06c6\u0631\u0646\u0634\u06cc\u0646 \u0645\u0648\u06b5\u06a9\u06cc \u062e\u0648\u062f\u0627\u0646\u061b \u062e\u0648\u0627 \u0647\u06d5\u0631\u06a9\u06ce \u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce\u060c \u062f\u06d5\u06cc\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5 \u0695\u06ce\u06cc\u06d5.',
    '\u0628\u06d5\u0648 \u0626\u0627\u0648\u0627\u06cc\u06d5 \u0626\u06ce\u0645\u06d5 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u0626\u06c6\u0645\u06d5\u062a\u06ce\u06a9\u06cc \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u06d5\u061b \u062a\u0627 \u0626\u0627\u06af\u0627\u0648 \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0647\u06d5\u0628\u06ce \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06cc\u062a\u0627\u0646 \u0644\u06ce \u0628\u06a9\u0627. \u0626\u06d5\u0648 \u0695\u0648\u0648\u06af\u06d5\u06cc\u06d5\u06cc \u067e\u06ce\u0634\u0648\u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0645 \u062f\u06d5\u0648\u06d5\u0633\u062a\u0627\u06cc\u060c \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5\u0645\u0627\u0646 \u06af\u06c6\u0695\u06cc \u062a\u0627 \u0628\u0632\u0627\u0646\u06cc\u0646 \u06a9\u06ce \u062f\u0648\u0627\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u06ce \u0648 \u06a9\u06ce\u0647\u06d5 \u067e\u0627\u0634\u06af\u06d5\u0632 \u062f\u06d5\u0628\u06ce\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631 \u0686\u06d5\u0646\u062f \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u062f\u0698\u0648\u0627\u0631 \u0628\u0648\u0648\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06c6 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u06cc \u0648\u0627 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u06a9\u0631\u062f\u0646. \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648 \u0628\u0695\u0648\u0627\u06cc \u0647\u06d5\u062a\u0627\u0646\u06d5 \u062a\u06ce\u06a9\u06cc \u0646\u0627\u062f\u0627. \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0632\u06c6\u0631 \u062f\u06b5\u0633\u06c6\u0632 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062f\u06cc\u062a\u0648\u0648\u0645\u0627\u0646\u06cc \u06a9\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u062d\u06d5\u0648\u0627 \u062f\u06d5\u0695\u0648\u0627\u0646\u06cc\u061b \u0626\u06ce\u0645\u06d5 \u0695\u0648\u0648\u062a \u0628\u06d5\u0631\u06d5\u0648 \u0695\u0648\u0648\u06af\u06d5\u06cc\u06d5\u06a9 \u0648\u06d5\u0631\u062f\u06d5\u06af\u06ce\u0695\u06cc\u0646 \u067e\u06d5\u0633\u06d5\u0646\u062f\u062a \u0628\u06ce. \u0633\u0627 \u0695\u0648\u0648\u062a \u0628\u06d5\u0631\u06d5\u0648 \u00ab\u0645\u0633\u062c\u062f\u0627\u0644\u062d\u0631\u0627\u0645\u00bb \u0648\u06d5\u0631\u06af\u06ce\u0695\u06d5 \u0648 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce\u0634 \u0628\u0646\u060c \u0647\u06d5\u0631 \u0695\u0648\u0648 \u0628\u06a9\u06d5\u0646\u06d5 \u0626\u06d5\u0648\u06ce\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646 \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0695\u0627\u06cc\u06d5. \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u0628\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u0647\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0634\u0627\u0646 \u0628\u062f\u06d5\u06cc \u0695\u0648\u0648 \u062f\u06d5 \u0695\u0648\u0648\u06af\u06d5\u06a9\u06d5\u06cc \u062a\u06c6 \u0646\u0627\u06a9\u06d5\u0646\u061b \u0626\u06d5\u062a\u06c6\u0634 \u0626\u06cc\u062a\u0631 \u0695\u0648\u0648 \u0646\u0627\u06a9\u06d5\u06cc\u062a\u06d5 \u0695\u0648\u0648\u06af\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646. \u0647\u06d5\u0631 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u0647\u06cc\u0686 \u06a9\u0627\u0645\u06cc\u0634\u06cc\u0627\u0646 \u0695\u0648\u0648 \u0646\u0627\u06a9\u06d5\u0646\u06d5 \u0695\u0648\u0648\u06af\u06d5\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u061b \u0626\u06d5\u062a\u06c6\u0634 \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648 \u0632\u0627\u0646\u0633\u062a\u06d5\u06cc \u0628\u06c6\u062a \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u0628\u0695\u06c6\u06cc\u060c \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u062f\u06d5\u0628\u06cc.',
    '\u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u2013 \u0648\u06d5\u06a9 \u06a9\u0648\u0695\u0627\u0646\u06cc \u062e\u06c6 \u062f\u06d5\u0646\u0627\u0633\u0646 \u2013 \u0626\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u062f\u06d5\u0646\u0627\u0633\u0646. \u062f\u06d5\u0633\u06d5\u06cc\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0686\u06d5\u0646\u062f \u062f\u06d5\u0632\u0627\u0646\u0646 \u0647\u06d5\u0642 \u0686\u06cc\u06cc\u06d5\u060c \u062f\u06d5\u06cc\u0634\u0627\u0631\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0645\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0695\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5 \u062f\u0648\u0648\u062f\u06b5\u0627\u0646 \u0645\u06d5\u0628\u06d5.',
    '\u0647\u06d5\u0631 \u0644\u0627\u06cc\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0695\u0648\u0648\u06cc \u062a\u06ce\u062f\u06d5\u06a9\u06d5\u0646. \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u06c6 \u0686\u0627\u06a9\u06d5 \u06a9\u06ce \u0628\u06d5\u0631\u06a9\u06ce \u0628\u06a9\u06d5\u0646. \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce \u0628\u0646\u060c \u062e\u0648\u0627 \u0644\u06ce\u06a9\u0648\u0648 \u06a9\u06c6 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u0647\u06d5\u0631 \u0626\u0627\u0642\u0627\u0631\u06ce\u06a9 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u06cc\u060c \u0695\u0648\u0648 \u0628\u06a9\u06d5 \u00ab\u0645\u0633\u062c\u062f\u0627\u0644\u062d\u0631\u0627\u0645\u00bb\u061b \u0626\u06d5\u0645\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0695\u0627\u06cc\u06d5. \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc\u0634 \u0626\u06ce\u0648\u06d5 \u0628\u06cc\u06a9\u06d5\u0646 \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u0644\u06d5 \u0647\u06d5\u0631 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06d5\u0648\u06d5 \u062f\u06d5\u0631\u0686\u0648\u0648\u06cc\u060c \u0647\u06d5\u0631 \u06a9\u0627\u0628\u06d5 \u0628\u06a9\u06d5 \u0628\u06d5 \u0695\u0648\u0648\u06af\u06d5. \u0644\u06d5 \u0647\u06d5\u0631 \u062c\u06ce \u0628\u0648\u0648\u0646 \u0628\u0627 \u0695\u0648\u0648\u062a\u0627\u0646 \u0647\u06d5\u0631 \u062f\u06d5\u0648\u06ce\u0648\u06d5 \u0628\u06ce\u060c \u062a\u0627 \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062f\u0698\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u062f\u06d5\u0633 \u0645\u06d5\u0631\u062f\u0645\u06d5\u0648\u06d5 \u0646\u06d5\u0628\u06ce\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u0644\u0627\u0633\u0627\u0631\u0646 \u0648 \u0647\u06d5\u0631 \u062d\u06d5\u0632 \u0644\u06d5 \u0644\u0627\u0633\u0627\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0646. \u0645\u0646 \u062f\u06d5\u0645\u06d5\u0648\u06ce \u062a\u06d5\u0648\u0627\u0648 \u0686\u0627\u06a9\u06d5\u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06a9\u06d5\u0645 \u0648 \u062f\u06d5\u0645\u06d5\u0648\u06ce \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u0628\u0646. \u0626\u06ce\u0648\u06d5 \u0647\u06cc\u0686 \u0644\u06d5\u0648\u0627\u0646 \u0645\u06d5\u062a\u0631\u0633\u0646\u061b \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u0644\u06d5 \u062e\u06c6\u0645 \u0628\u062a\u0631\u0633\u0646.',
    '\u0648\u06d5\u06a9\u0648\u0648 \u0626\u06d5\u0645\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0644\u0627\u062a\u0627\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u062a\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u06c6 \u067e\u0627\u06a9\u06cc \u0695\u0627\u062a\u0627\u0646 \u062f\u06d5\u0647\u06ce\u0646\u06ce \u0648 \u0641\u06ce\u0631\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0644\u06d5 \u06a9\u062a\u06ce\u0628 \u0648 \u06a9\u0627\u0631\u0627\u0645\u06d5\u06cc\u06cc \u0633\u06d5\u0631\u062a\u0627\u0646 \u062f\u06d5\u0631\u0686\u06ce \u0648 \u06af\u06d5\u0644\u06ce\u06a9 \u0634\u062a\u06cc \u0648\u0627\u0648 \u0641\u06ce\u0631 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u0646\u06d5\u0648\u062f\u06d5\u0632\u0627\u0646\u06cc\u0646.',
    '\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u06cc\u0627\u062f\u06cc \u0645\u0646 \u0628\u06a9\u06d5\u0646\u060c \u062a\u0627 \u0645\u0646\u06cc\u0634 \u0644\u06d5 \u0628\u06cc\u0631\u062a\u0627\u0646 \u0646\u06d5\u06a9\u06d5\u0645. \u0647\u06d5\u0631 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u06cc \u0645\u0646 \u0628\u0628\u06ce\u0698\u0646 \u0648 \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u0645\u0646 \u062d\u0627\u0634\u0627 \u0645\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u067e\u06d5\u0646\u0627 \u0628\u06d5\u0631\u0646\u06d5 \u0628\u06d5\u0631 \u0646\u0648\u06ce\u0698\u0627\u0646 \u0648 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0646\u061b \u062e\u0648\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06c6\u0695\u0627\u06af\u0631\u0627\u0646\u06d5.',
    '\u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u0627\u0647\u06cc \u062e\u062f\u0627 \u062f\u06d5\u06a9\u0648\u0698\u0631\u06ce\u0646\u060c \u0645\u06d5\u06b5\u06ce\u0646 \u0645\u0631\u062f\u0648\u0648\u0646\u061b \u0628\u06d5\u06b5\u06a9\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648\u0646\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0648\u06d5 \u067e\u06ce \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0645\u06d5 \u062c\u0627\u0631\u0648\u0628\u0627\u0631\u06d5 \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u062a\u0631\u0633 \u0648 \u0628\u0631\u0686\u06cc \u0628\u0648\u0648\u0646 \u0648 \u0644\u06d5 \u0646\u0627\u0648\u0686\u0648\u0648\u0646 \u0648 \u06a9\u06d5\u0645 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u06cc \u0645\u0627\u06b5\u06cc \u062f\u0646\u06cc\u0627 \u0648 \u0628\u06d5\u0631\u0648\u0628\u0648\u0648\u062a\u0627\u0646\u060c \u062f\u06d5\u062a\u0627\u0646 \u0626\u06d5\u0632\u0645\u0648\u0648\u06cc\u0646. \u0645\u0698\u062f\u06d5 \u0628\u062f\u06d5 \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0686\u06c6\u0631\u062a\u0645\u06ce\u06a9 \u062f\u06d5\u0628\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5\u0634 \u0647\u06d5\u0631 \u0626\u06cc \u062e\u0648\u0627\u06cc\u0646 \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u0686\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u062e\u06ce\u0631\u0648\u0628\u06ce\u0631 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0628\u0627\u0631\u06ce \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0644\u06d5 \u0695\u06ce \u0626\u06d5\u0648\u0627\u0646\u0646.',
    '\u0633\u06d5\u0641\u0627 \u0648 \u0645\u06d5\u0631\u0648\u06d5 \u062f\u0648\u0648 \u06a9\u06ce\u0644\u06d5\u06a9\u06cc \u062e\u0648\u0627\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646. \u0647\u06d5\u0631\u06a9\u06ce \u062f\u06d5\u0686\u06ce\u062a\u06d5 \u0632\u06cc\u0627\u0631\u06d5\u062a\u06cc \u0645\u0627\u06b5\u06cc \u062e\u0648\u062f\u0627\u060c \u06cc\u0627\u0646 \u0639\u06d5\u0645\u0631\u06d5 \u0628\u06d5\u062c\u06ce \u062f\u06d5\u0647\u06ce\u0646\u06ce\u060c \u06af\u06d5\u0631 \u062a\u06d5\u0648\u0627\u0641\u06cc \u0626\u06d5\u0648 \u062f\u0648\u0648\u0627\u0646\u06d5\u0634 \u0628\u06a9\u0627 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u06cc \u0646\u06cc\u06cc\u06d5. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06d5 \u0648\u06cc\u0633\u062a\u06cc \u062f\u06b5\u06cc \u062e\u06c6\u06cc \u0626\u06d5\u0648 \u062e\u06ce\u0631\u06d5 \u0628\u06a9\u0627\u060c \u062e\u0648\u0627 \u067e\u06ce \u0632\u0627\u0646\u06d5 \u0648 \u0644\u06d5 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0628\u06c6 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc \u0645\u06d5\u0631\u062f\u0645\u06d5\u060c \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645\u06cc \u062f\u06d5\u0634\u06ce\u0631\u0646\u06d5\u0648\u06d5\u060c \u0648\u06d5\u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0646\u06d5\u0641\u0631\u06cc\u0646 \u0628\u06ce\u0698\u0627\u0646 \u0628\u06a9\u06d5\u0648\u0646\u061b',
    '\u062c\u06cc\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u0627\u0634\u0627\u0646 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc\u0627\u0646 \u0695\u06d5\u0686\u0627\u0648 \u06a9\u0631\u062f \u0648 \u0648\u06d5\u0634\u0627\u0631\u062a\u0648\u0648\u06cc\u0627\u0646 \u0626\u0627\u0634\u06a9\u0631\u0627 \u06a9\u0631\u062f. \u0644\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u0627\u0646 \u062e\u06c6\u0634 \u062f\u06d5\u0628\u0645 \u0648 \u0647\u06d5\u0631 \u0645\u0646\u06cc\u0634\u0645 \u0644\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u0645 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u0645.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u06a9\u0627\u0641\u0631 \u0648 \u0628\u06d5 \u06a9\u0627\u0641\u0631\u06cc \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0686\u0648\u0648\u0646\u060c \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u062e\u0648\u0627 \u0648 \u0641\u0631\u06cc\u0634\u062a\u0627\u0646\u0648\u0648 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc.',
    '\u062a\u0627\u0647\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u06af\u0631\u0641\u062a\u0627\u0631\u06cc \u0626\u06d5\u0648 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06d5\u0646\u061b \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0647\u06cc\u0686 \u0633\u0648\u0648\u06a9 \u0646\u0627\u06a9\u0631\u06ce \u0648 \u0648\u0686\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0646\u0627\u062f\u0631\u06ce.',
    '\u062e\u0648\u062f\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc \u062a\u0627\u06a9 \u0648 \u062a\u06d5\u0646\u06cc\u0627\u06cc\u06d5. \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce\u061b \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc \u062f\u0647\u06d5\u0646\u062f\u06d5\u06cc\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0644\u06d5 \u0633\u0627\u0632\u062f\u0627\u0646\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0647\u0627\u062a\u0648\u0686\u06c6\u06cc \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698\u0627\u0646 \u0648 \u0644\u06d5\u0648 \u06af\u06d5\u0645\u06cc\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u062f\u0627 \u06af\u06d5\u0631\u062f\u0634 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0647\u06d5\u06cc\u06d5 \u0648 \u0644\u06d5 \u062a\u06d5\u06a4\u06af\u06d5\u0695\u06cc \u0628\u0627\u06cc\u06d5\u06a9\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u0647\u06d5\u0648\u0631\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u0648\u0695 \u0639\u06d5\u0631\u0632\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u062f\u0627 \u0695\u0627\u06af\u06cc\u0631\u0627\u0648\u0646 \u0648 \u0644\u06d5\u0648 \u0626\u0627\u0648\u06d5\u06cc \u062e\u0648\u0627 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u062f\u06d5\u06cc\u0628\u0627\u0631\u06ce\u0646\u06ce \u2013 \u06a9\u06d5 \u0628\u06d5\u0647\u06c6\u06cc \u0626\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0644\u06d5 \u067e\u0627\u0634 \u0645\u0631\u062f\u0646\u06ce \u0628\u0648\u0648\u0698\u0627\u0646\u062f\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u062c\u06c6\u0631\u06d5 \u0632\u06cc\u0646\u062f\u06d5\u0648\u06d5\u0631\u06cc \u062a\u06ce\u062f\u0627 \u0628\u06b5\u0627\u0648 \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5 \u2013 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0632\u06c6\u0631 \u0647\u06d5\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646.',
    '\u0644\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0647\u0627\u0648\u0628\u06d5\u0634 \u0628\u06c6 \u062e\u0648\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0628\u06d5\u0642\u06d5\u062f \u062e\u0648\u0627 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0648\u06ce\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0695\u0648\u0627\u0634\u0646 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9 \u062e\u06c6\u0634\u062a\u0631 \u062f\u06d5\u0648\u06ce. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0646\u0627\u0646\u0627\u0633\u0646\u060c \u0648\u06d5\u062e\u062a\u06ce \u0686\u0627\u0648\u06cc\u0627\u0646 \u0628\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062f\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u060c \u0626\u06d5\u0648\u0633\u0627 \u0628\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u06ce: \u0647\u06ce\u0632 \u0648 \u062a\u0648\u0627\u0646 \u0647\u06d5\u0645\u0648\u0648 \u0647\u06cc \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0626\u0627\u0632\u0627\u0631\u06ce \u062e\u0648\u0627 \u062f\u06d5\u06cc\u062f\u0627 \u062a\u0648\u0646\u062f\u0648 \u062a\u06cc\u0698\u06d5.',
    '\u0644\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u062f\u0627 \u06a9\u06d5 \u0626\u0627\u0632\u0627\u0631 \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u0627\u0646 \u062f\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u060c \u0695\u06ce\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u067e\u06d5\u06cc\u0648\u06d5\u0646\u062f\u06cc\u06cc\u0627\u0646 \u0644\u06ce\u06a9 \u067e\u0686\u0695\u0627\u0648\u06d5.',
    '\u067e\u06d5\u06cc\u0695\u06d5\u0648\u06d5\u06a9\u0627\u0646 \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0698\u0646: \u0626\u0627\u06cc \u062e\u06c6\u0632\u06af\u06d5 \u0647\u06d5\u0631 \u062c\u0627\u0631\u06ce\u06a9\u06cc \u062a\u0631 \u062f\u06d5\u06af\u06d5\u0695\u0627\u06cc\u0646\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627\u060c \u0647\u06d5\u062a\u0627 \u0648\u06d5\u06a9 \u0695\u06ce\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0644\u06ce\u0646 \u0628\u06d5 \u062d\u0627\u0634\u0627\u0646\u060c \u0626\u06ce\u0645\u06d5\u0634 \u062d\u0627\u0634\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u0631\u062f\u0646. \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u0627 \u06a9\u0631\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u0627\u062a\u06d5 \u06a9\u0648\u06b5 \u0648 \u06a9\u06d5\u0633\u06d5\u0631\u061b \u0628\u06c6 \u0647\u06d5\u0645\u06cc\u0634\u06d5\u0634 \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631\u062f\u0627 \u062f\u06d5\u0628\u0646.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0644\u06d5\u0648 \u0628\u06d5\u0631\u0648\u0628\u0648\u0648\u06cc \u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u060c \u06a9\u0627\u0645\u06cc \u0695\u06d5\u0648\u0627 \u0648 \u067e\u0627\u06a9\u06d5 \u0628\u062e\u06c6\u0646\u061b \u0628\u06d5\u0634\u0648\u06ce\u0646 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06cc\u0634\u0627 \u0645\u06d5\u0695\u06c6\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0648 \u0628\u06c6\u062a\u0627\u0646 \u062f\u0698\u0645\u0646\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u0648\u0648 \u067e\u06ce \u062f\u06d5\u062f\u0627: \u06a9\u0627\u0631\u06cc \u067e\u06cc\u0633 \u0648 \u0632\u06c6\u0631 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631 \u0695\u06d5\u0686\u0627\u0648 \u06a9\u06d5\u0646 \u0648 \u0634\u062a\u0627\u0646\u06cc \u0648\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u0628\u06ce\u0698\u0646 \u06a9\u06d5 \u0647\u06d5\u0631 \u0626\u0627\u06af\u0627\u0634\u0648\u0648\u0648 \u0644\u06ce \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0628\u06ce\u0698\u0646: \u0647\u06d5\u0631 \u0628\u06d5\u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648\u06d5\u062f\u0627 \u0628\u0695\u06c6\u0646 \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u060c \u0626\u06ce\u0698\u0646: \u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u06a9\u06d5 \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0695\u06d5\u0686\u0627\u0648\u06cc\u0627\u0646 \u06a9\u0631\u062f. \u062c\u0627 \u0628\u0627 \u0628\u0627\u0628\u0648\u0628\u0627\u067e\u06cc\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06cc\u0686\u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06cc\u0686 \u0646\u06d5\u0632\u0627\u0646\u06cc\u0628\u06ce \u0648 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0646\u06d5\u062f\u06cc\u0628\u06ce!',
    '\u0646\u06d5\u0642\u06b5\u06cc \u0626\u06d5\u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5 \u0648\u06d5\u06a9 \u0646\u06d5\u0642\u06b5\u06cc \u0626\u06d5\u0648 \u0626\u0627\u0698\u0627\u06b5\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062f\u06d5\u0646\u06af \u062f\u06d5\u062f\u0631\u06ce\u061b \u062c\u06cc\u0627 \u0644\u06d5 \u062e\u0648\u0695\u06cc\u0646 \u0648 \u0686\u0695\u06cc\u0646\u06ce \u0647\u06cc\u0686 \u0646\u0627\u0628\u06cc\u0633\u0646. \u06a9\u06d5\u0695\u0646\u060c \u0644\u0627\u06b5\u0646\u060c \u0686\u0627\u0648\u06cc\u0627\u0646 \u06a9\u0648\u06ce\u0631\u06d5 \u0648 \u0644\u06d5 \u062a\u06b5\u067e\u0627\u062a\u06cc \u062a\u06d5\u0695\u06cc\u0634 \u0646\u0627\u06af\u06d5\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0648\u0646\u060c \u06a9\u0627\u0645\u06cc \u067e\u0627\u06a9\u06d5 \u0626\u06d5\u0648\u06d5 \u0628\u062e\u06c6\u0646\u061b \u0634\u0648\u06a9\u0631\u06cc \u062e\u0648\u062f\u0627\u0634 \u0628\u06d5\u062c\u06ce \u0628\u06ce\u0646\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062f\u06d5\u067e\u06d5\u0631\u06d5\u0633\u062a\u0646.',
    '\u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u0644\u0627\u06a9\u06cc \u0645\u0631\u062f\u0627\u0631 \u0648 \u06af\u06c6\u0634\u062a \u0628\u06d5\u0631\u0627\u0632 \u0648 \u062e\u0648\u06ce\u0646 \u0648 \u0626\u06d5\u0648\u06d5\u06cc \u0628\u06c6 \u0633\u06d5\u0631 \u0628\u0695\u06cc\u0646 \u0646\u0627\u0648\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627\u06cc \u0644\u06ce \u062f\u06ce\u0646\u0646\u060c \u0644\u06d5 \u0633\u06d5\u0631\u062a\u0627\u0646 \u062d\u06d5\u0631\u0627\u0645 \u06a9\u0631\u0627\u0648\u06d5. \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5 \u0646\u0627\u0686\u0627\u0631\u06cc \u0648 \u0628\u06ce \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u060c \u0647\u06d5\u0631 \u0628\u06d5\u0634\u06cc \u062a\u06ce\u0631 \u0628\u0648\u0648\u0646\u06cc \u0628\u062e\u0648\u0627\u060c \u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u06cc \u0646\u0627\u06af\u0627\u062a\u06ce\u061b \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u062e\u06c6\u0634 \u062f\u06d5\u0628\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0646\u0627\u0648\u06d5\u0631\u06c6\u06a9\u06cc \u06a9\u062a\u06ce\u0628\u06ce \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u060c \u062f\u06d5\u0634\u06ce\u0631\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u0646\u0631\u062e\u06cc \u06a9\u06d5\u0645 \u062f\u06d5\u06cc\u0641\u0631\u06c6\u0634\u0646\u060c \u0648\u0631\u06af\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u0627\u06af\u0631 \u062f\u0627\u062e\u0646\u0646\u061b \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0631\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u062f\u0627 \u0646\u0627\u06cc\u0627\u0646 \u062f\u0648\u06ce\u0646\u06ce \u0648 \u06af\u0648\u0646\u0627\u06cc\u0627\u0646 \u067e\u0627\u06a9 \u0646\u0627\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u06cc\u0627\u0646 \u0628\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u0648 \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062c\u0632\u06cc\u0627 \u06af\u06c6\u0695\u06cc\u06af\u06d5\u0633\u06d5\u0648\u060c \u062f\u06d5\u0628\u06ce \u0686\u06d5\u0646\u062f\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u0627\u06af\u0631 \u062e\u06c6\u0695\u0627\u06af\u0631\u0646\u061f!',
    '\u0626\u06d5\u0648[ \u0626\u0627\u0632\u0627\u0631\u06d5\u0634 [ \u0628\u06c6 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062e\u0648\u0627 \u0628\u06d5 \u062d\u06d5\u0642 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u0647\u06d5\u0646\u0627\u0631\u062f\u06af\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0648\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u0628\u0627\u0633\u06cc \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u06a9\u06ce\u0634\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06af\u06ce\u0631\u06d5\u0648\u06a9\u06ce\u0634\u06d5\u06cc \u062f\u0648\u0648\u0631 \u0648 \u062f\u0631\u06ce\u0698\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634 \u062f\u06d5\u0628\u06ce.',
    '\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0646\u06cc\u06cc\u06d5 \u0695\u0648\u0648 \u062f\u06d5 \u0695\u06c6\u0698\u0647\u06d5\u06b5\u0627\u062a\u06ce \u0628\u06a9\u06d5\u0646 \u06cc\u0627\u0646 \u0695\u06c6\u0698\u0627\u0648\u0627. \u0686\u0627\u06a9 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u06d5 \u0648 \u0641\u0631\u0634\u062a\u0627\u0646 \u0648 \u06a9\u062a\u06ce\u0628 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0644\u06d5\u0648 \u0633\u0627\u0645\u0627\u0646\u06d5\u06cc \u2013 \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u0632\u06c6\u0631\u06cc\u0634\u0648\u0648 \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce \u2013 \u0628\u06d5\u0634\u06ce \u0628\u062f\u06d5\u0646 \u0628\u06d5 \u062e\u0632\u0645\u0627\u0646 \u0648 \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646 \u0648 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646 \u0648 \u0695\u06ce\u0648\u0646\u06af\u0627\u0646 \u0648 \u067e\u0627\u0631\u0633\u06d5\u06a9\u0627\u0646 \u0648 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u067e\u06ce \u0628\u06a9\u0695\u0646\u06d5\u0648\u06d5 \u0648 \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u062f\u06d5\u0646\u061b \u0647\u06d5\u0631\u06af\u0627\u0634 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06ce\u06a9 \u062f\u06d5\u0628\u06d5\u0633\u062a\u0646 \u0646\u06d5\u06cc\u0634\u06a9\u06ce\u0646\u0646\u061b \u0644\u06d5 \u0646\u06d5\u062f\u0627\u0631\u06cc \u06af\u0631\u0641\u062a\u0627\u0631\u06cc \u0648 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5\u062f\u0627 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0646. \u0645\u0631\u06c6\u06cc \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u0648 \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u062a\u0631\u0633\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5\u0645\u06d5\u0695 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u0648\u0698\u0631\u0627\u0648\u0646 \u062a\u06c6\u06b5\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0628\u0695\u06cc\u0627\u0631 \u062f\u0627\u0648\u0646: \u0695\u0632\u06af\u0627\u0631 \u0644\u06d5 \u062a\u06c6\u06b5\u06d5\u06cc \u0695\u0632\u06af\u0627\u0631 \u0648 \u06a9\u06c6\u06cc\u0644\u06d5 \u0644\u06d5 \u062a\u06c6\u06b5\u06d5\u06cc \u06a9\u06c6\u06cc\u0644\u06d5\u062f\u0627 \u0648 \u0645\u06ce\u0648\u06cc\u0646\u06d5 \u0644\u06d5 \u0628\u0631\u06cc \u0645\u06ce\u0648\u06cc\u0646\u06d5\u061b \u0647\u06d5\u0631\u06a9\u06ce\u0634 \u0644\u06d5 \u0628\u0631\u0627\u06cc \u0647\u0627\u0648\u062f\u06cc\u0646\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0633 \u0644\u06d5 \u062e\u0648\u06ce\u0646\u06af\u0631\u06cc \u0647\u06d5\u06b5\u06af\u0631\u06ce\u060c \u0626\u06d5\u0628\u06ce \u0628\u06d5 \u067e\u06ce\u06cc \u062f\u0627\u0628\u06cc \u0695\u06c6\u0698 \u0628\u062c\u0648\u0648\u06b5\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u062e\u06c6\u0634\u06cc \u062e\u06c6\u06cc \u0645\u06d5\u0633\u06b5\u06d5\u062a \u0628\u06a9\u0627. \u0626\u06d5\u0645 \u0628\u0695\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u0695\u0627 \u0633\u0648\u0648\u06a9 \u0628\u0627\u0631\u06cc\u06cc\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06d5 \u062f\u06d5\u0631\u0647\u06d5\u0642 \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u060c \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5\u0645\u06d5 \u062e\u06c6 \u0628\u0628\u0648\u06ce\u0631\u06ce\u060c \u0628\u06d5\u0634\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06b5\u0627\u0646\u06cc \u0648\u0634\u06cc\u0627\u0631! \u0698\u06cc\u0627\u0646\u0648\u0648 \u0628\u06d5 \u062a\u06c6\u06b5\u06d5\u0648\u06d5 \u0628\u06d5\u0646\u062f\u06d5\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u062e\u06c6\u062a\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646.',
    '\u0628\u06c6\u0648 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5: \u0647\u06d5\u0631\u06af\u0627 \u06a9\u06d5\u0633\u06ce\u06a9 \u0644\u06d5\u0646\u06af\u06c6 \u0647\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0645\u0631\u062f\u0646 \u0648 \u0645\u0627\u06b5\u06ce\u06a9\u06cc \u0644\u06d5 \u067e\u0627\u0634 \u062f\u06d5\u0645\u06ce\u0646\u06ce\u060c \u0628\u06d5 \u062a\u06d5\u0631\u0632\u06ce\u06a9\u06cc \u062e\u0648\u062f\u0627\u067e\u06d5\u0633\u0646\u062f \u0648\u06d5\u0633\u06cc\u06d5\u062a \u0628\u06a9\u0627 \u06a9\u06d5 \u0628\u0627\u0628 \u0648 \u062f\u0627\u06cc\u06a9 \u0648 \u062e\u0632\u0645\u0627\u0646\u06cc\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06d5\u0634\u06cc \u062e\u06c6\u06cc \u067e\u06ce \u0628\u06af\u0627. \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u0627\u0646 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0628\u06a9\u06d5\u0646.',
    '\u0626\u06cc\u062a\u0631 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u067e\u0627\u0634 \u0628\u06cc\u0633\u062a\u0646\u06cc \u0648\u06d5\u0633\u06cc\u06d5\u062a\u06d5\u06a9\u06d5 \u062f\u06d5\u0633\u06a9\u0627\u0631\u06cc \u0628\u06a9\u0627 \u0648 \u0628\u06cc\u06af\u06c6\u0695\u06ce\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u06cc\u06af\u06c6\u0695\u0646\u060c \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0646. \u062e\u0648\u062f\u0627 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u06a9\u06d5\u0633\u06ce \u0647\u06d5\u0633\u062a\u06cc \u067e\u06ce\u06a9\u0631\u062f \u06a9\u06d5 \u0648\u06d5\u0633\u06cc\u06d5\u062a\u06a9\u0627\u0631 \u0644\u06d5\u0648 \u0648\u06d5\u0633\u06cc\u06d5\u062a\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06cc \u0646\u0627\u0647\u06d5\u0642 \u0628\u0648\u0648\u06d5 \u0648 \u062e\u06c6\u06cc \u062a\u0648\u0648\u0634\u06cc \u06af\u0648\u0646\u0627\u0647\u0640 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u062a\u06ce\u0628\u06a9\u06c6\u0634\u06ce \u0645\u06cc\u0631\u0627\u062a\u06af\u0631\u0627\u0646\u06cc \u067e\u06ce\u06a9 \u0628\u06ce\u0646\u06ce\u060c \u06af\u0648\u0646\u0627\u062d \u0646\u06cc\u06cc\u06d5. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06c6\u0648 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5: \u0695\u06c6\u0698\u0648\u0648 \u0628\u06af\u0631\u0646 \u2013 \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0644\u06d5\u0633\u06d5\u0631 \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634\u0648\u0648 \u0646\u0648\u0648\u0633\u0631\u0627 \u2013 \u0634\u0627\u06cc\u06d5\u062a \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646.',
    '\u0686\u06d5\u0646\u062f \u0695\u06c6\u0698\u06ce\u06a9\u0646 [\u0646\u06d5 \u0632\u06c6\u0631 \u0632\u06ce\u062f\u06d5]\u060c \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5\u0646\u06af\u06c6 \u0644\u06d5\u0634 \u0628\u06d5\u0628\u0627\u0631\u06d5 \u06cc\u0627\u0646 \u0695\u06ce\u0628\u0648\u0627\u0631\u06d5 \u0628\u0627 \u0686\u06d5\u0646\u062f \u0695\u06c6\u0698\u06cc \u062a\u0631 \u067e\u06ce \u0628\u0628\u06ce. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0648\u0627 \u0632\u06c6\u0631 \u0628\u06d5 \u0633\u06d5\u062e\u062a\u06cc \u0695\u06c6\u0698\u0648\u0648\u06cc\u0627\u0646 \u067e\u06ce \u0626\u06d5\u06af\u06cc\u0631\u06ce\u060c \u0628\u06d5\u062c\u06ce \u0695\u06c6\u0698\u0648\u0648 \u00ab\u0641\u06cc\u062f\u06cc\u06d5\u00bb \u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06d5 \u0648 \u062f\u06d5\u0628\u06ce \u062e\u06c6\u0631\u0627\u06a9\u06cc \u0647\u06d5\u0698\u0627\u0631\u06ce\u06a9 \u0628\u062f\u06d5\u0646. \u0626\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u062e\u06c6\u0631\u0627\u06a9\u06cc \u0632\u06c6\u0631\u062a\u0631 \u0628\u0628\u06d5\u062e\u0634\u06ce\u060c \u0628\u06c6\u06cc \u0628\u0627\u0634\u062a\u0631\u06d5. \u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u062a\u0627\u0646\u0632\u0627\u0646\u06cc\u0628\u0627 \u0628\u06d5\u0695\u06c6\u0698\u0648\u0648 \u0628\u0648\u0648\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u06af\u06d5\u0644\u06ce \u0686\u0627\u062a\u0631\u06d5.',
    '\u0645\u0627\u0646\u06af\u06cc \u0695\u06d5\u0645\u06d5\u0632\u0627\u0646 \u0645\u0627\u0646\u06af\u06ce\u06a9\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646 \u0644\u06d5\u0648\u062f\u0627 \u06a9\u0644 \u06a9\u0631\u0627\u0648\u06d5\u061b \u06a9\u06d5 \u0695\u06ce \u0628\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627 \u0648 \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc \u0648 \u0647\u06c6\u06cc \u0644\u06ce\u06a9\u062a\u0631 \u0647\u06d5\u06b5\u0648\u06d5\u0698\u0627\u0631\u062f\u0646\u06cc \u067e\u0627\u06a9 \u0648 \u0646\u0627\u067e\u0627\u06a9\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5. \u062c\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0645\u0627\u0646\u06af\u06cc \u0695\u06d5\u0645\u06d5\u0632\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u06ce\u060c \u0644\u06d5\u0633\u06d5\u0631\u06cc\u06d5\u062a\u06cc \u0628\u06d5\u0695\u06c6\u0698\u0648\u0648 \u0628\u06ce\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5\u0634 \u0628\u06d5\u0628\u0627\u0631\u06d5 \u06cc\u0627\u0646 \u0695\u06ce\u0628\u0648\u0627\u0631\u06d5\u060c \u0626\u06d5\u0648 \u0698\u0645\u0627\u0631\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u0627\u0646\u06cc \u062f\u06cc\u06a9\u06d5 \u067e\u06ce \u0628\u06ce. \u062e\u0648\u0627 \u0633\u0627\u0646\u0627\u06cc\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u062f\u06d5\u0648\u06ce \u0648 \u0646\u0627\u06cc\u06d5\u0648\u06ce \u0626\u06d5\u0631\u06a9\u0648\u0648 \u06af\u0631\u0627\u0646 \u0628\u06ce. \u0695\u06c6\u0698\u06d5\u06a9\u0627\u0646\u06cc \u2013 \u062a\u06d5\u0648\u0627\u0648 \u2013 \u067e\u06ce \u0628\u0646 \u0648 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0626\u06d5\u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0648\u0646 \u0648 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u06cc \u0628\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627\u06cc\u06d5\u06a9\u06cc \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u0645 \u0644\u06d5\u0645\u06d5\u0695 \u0645\u0646 \u0644\u06d5 \u062a\u06c6 \u062f\u06d5\u067e\u0631\u0633\u0646\u060c \u0645\u0646 \u0646\u0632\u06cc\u06a9\u0645\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0647\u0627\u0646\u0627\u0645 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06ce\u0646\u06ce\u060c \u0628\u06ce \u0648\u06d5\u0631\u0627\u0645\u06cc \u0646\u0627\u0647\u06ce\u06b5\u0645\u06d5\u0648\u06d5. \u0628\u0627 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0645\u0646 \u062f\u06d5\u0631\u0646\u06d5\u0686\u0646\u060c \u0628\u0695\u0648\u0627\u06cc \u062a\u06d5\u0648\u0627\u0648\u06cc\u0627\u0646 \u0628\u06d5 \u0645\u0646 \u0628\u06ce\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0695\u06ce\u06cc \u0695\u0627\u0633\u062a \u0628\u06d5\u062f\u06cc \u0628\u06a9\u06d5\u0646.',
    '\u0633\u06d5\u0631\u062c\u06ce\u06cc\u06cc \u0698\u0646\u0627\u0646 \u06a9\u0631\u062f\u0646\u0648\u0648\u060c \u0644\u06d5 \u0634\u06d5\u0648\u0627\u0646\u06cc \u0695\u06d5\u0645\u06d5\u0632\u0627\u0646\u060c \u0628\u06c6\u062a\u0627\u0646 \u0695\u06d5\u0648\u0627\u06cc\u06d5\u061b \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06d5\u0644 \u067e\u06d5\u0644\u06cc \u0626\u06ce\u0648\u06d5\u0646 \u0648 \u0626\u06ce\u0648\u06d5 \u067e\u06c6\u0634\u06d5\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0631\u06ce \u062f\u06d5\u0631\u0647\u06d5\u0642 \u0628\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u063a\u06d5\u06cc\u0627\u0646 \u0628\u0648\u0648\u0646\u061b \u0628\u06d5\u0648 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u062a\u0627\u0646 \u0639\u0627\u0641\u0648\u0648\u06cc \u06a9\u0631\u062f\u0646 \u0648 \u0628\u06d5\u062e\u0634\u06cc\u0648\u0646\u06cc. \u0647\u06d5\u0631 \u0644\u06d5 \u0626\u06ce\u0633\u062a\u0627\u0648\u06d5 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062a\u06ce\u06a9\u06d5\u06b5 \u06a9\u06d5\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0686\u0627\u0631\u06d5\u062a\u0627\u0646\u06cc \u0646\u0648\u0648\u0633\u06cc\u0648\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u06a9\u06d5\u0648\u0646 \u0648 \u0628\u062e\u06c6\u0646\u060c \u0648\u06d5\u062e\u06c6\u0646 \u062a\u0627 \u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u06cc \u062f\u06d5\u0645\u06d5\u0648 \u0628\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0648\u062f\u0627\u06cc \u0644\u06d5 \u0647\u06d5\u0648\u062f\u0627\u06cc \u0695\u06d5\u0634 \u062f\u06d5\u0646\u0627\u0633\u0646\u06d5\u0648\u06d5. \u067e\u0627\u0634\u0627\u0646 \u0695\u06c6\u0698\u0648\u0648\u062a\u0627\u0646 \u062a\u06d5\u0648\u0627\u0648 \u06a9\u06d5\u0646 \u0647\u06d5\u062a\u0627 \u0634\u06d5\u0648\u06ce. \u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06ce\u06a9\u06cc \u0644\u06d5 \u0645\u0632\u06af\u06d5\u0648\u062a\u0627\u0646\u062f\u0627 \u0645\u0627\u062a \u062f\u06d5\u0628\u0646\u060c \u062a\u062e\u0648\u0648\u0646 \u0698\u0646\u0627\u0646\u062a\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0648\u0646. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0633\u0646\u0648\u0648\u0631\u06cc \u062e\u0648\u062f\u0627\u0646 \u0646\u06d5\u0686\u0646 \u062a\u062e\u0648\u0648\u0646\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0648\u0646. \u0626\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u06d5\u06a9\u0627\u060c \u0634\u0627\u06cc\u06d5\u062a\u0627 \u062e\u06c6 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646.',
    '\u0644\u06d5 \u062e\u0648\u0627\u0631\u062f\u0646\u06cc \u0645\u0627\u06b5\u06cc \u06cc\u06d5\u06a9\u062a\u0631 \u2013 \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u2013 \u062f\u0648\u0648\u0631 \u06a9\u06d5\u0648\u0646\u06d5\u0648\u06d5\u061b \u0645\u06d5\u0634\u06cc \u062f\u06d5\u0646 \u0628\u06d5 \u06a9\u0627\u0631\u0628\u06d5\u062f\u06d5\u0633\u062a\u0627\u0646 \u062a\u0627 \u0695\u06ce\u0648 \u0647\u06d5\u0628\u06ce \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u0645\u0627\u06b5\u06cc \u0645\u06d5\u0631\u062f\u0645\u06cc \u062a\u0631 \u0628\u062e\u06c6\u0646. \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646 \u0626\u06d5\u0645\u06d5 \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0645\u0627\u0646\u06af \u0646\u0648\u06ce \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u0634 \u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646. \u0628\u06ce\u0698\u06d5: \u0628\u06c6 \u0632\u0627\u0646\u06cc\u0646\u06cc \u0648\u06d5\u062e\u062a \u0648 \u0686\u0648\u0648\u0646\u06d5 \u062d\u06d5\u062c\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0628\u06d5\u0647\u0631\u06d5 \u062f\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u06ce. \u0648\u0627\u0634 \u0686\u0627\u06a9 \u0646\u06cc\u06cc\u06d5 \u0644\u06d5 \u067e\u0634\u062a\u06d5\u0648\u06d5 \u0646\u0627\u0648\u0645\u0627\u06b5\u06cc \u062e\u06d5\u06b5\u06a9\u06d5\u0648\u06d5\u061b \u0686\u0627\u06a9 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u0628\u067e\u0631\u06cc\u0646\u06af\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u062f\u0631\u06af\u0627\u0648\u06d5 \u0628\u0686\u0646\u06d5 \u0646\u0627\u0648 \u0645\u0627\u06b5\u0627\u0646\u06d5\u0648\u06d5. \u062a\u0631\u0633\u06cc\u0634\u0648\u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u062a\u0648\u0627\u0646\u0646 \u0695\u0632\u06af\u0627\u0631 \u0628\u0646.',
    '\u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 \u06a9\u06d5 \u0634\u06d5\u0695 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0641\u0631\u06c6\u0634\u0646\u060c \u0644\u06d5 \u0695\u0627\u0647\u06cc \u062e\u0648\u062f\u0627 \u0628\u062c\u06d5\u0646\u06af\u0646. \u0646\u0627\u0628\u06ce \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06cc \u0628\u06a9\u06d5\u0646\u061b \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06cc \u06a9\u06d5\u0631\u0627\u0646 \u062e\u06c6\u0634\u06cc \u0646\u0627\u06cc\u06d5.',
    '\u0644\u06d5 \u0647\u06d5\u0631 \u0634\u0648\u06ce\u0646\u06ce \u062f\u06d5\u0633\u062a\u0648\u0648 \u06a9\u06d5\u0648\u062a\u0646 \u0628\u06cc\u0627\u0646\u06a9\u0648\u0698\u0646. \u0644\u06d5\u0648 \u0634\u0648\u06ce\u0646\u06d5\u0634\u06cc\u0627\u0646 \u0648\u06d5\u062f\u06d5\u0631 \u0628\u0646\u06ce\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u062f\u06d5\u0631 \u0646\u0627. \u0633\u06c6\u062f\u0631\u06d5\u06cc\u06cc\u0634 \u0632\u06c6\u0631 \u0644\u06d5 \u06a9\u0648\u0634\u062a\u0646\u06ce \u062e\u0631\u0627\u062a\u0631\u06d5. \u0644\u06d5 \u062f\u06d5\u0648\u0631\u0648\u0628\u06d5\u0631\u06cc \u00ab\u0645\u0633\u062c\u062f\u0627\u0644\u062d\u0631\u0627\u0645\u00bb \u06cc\u0634\u0627 \u0646\u0627\u0628\u06ce \u0634\u06d5\u0695\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06a9\u06d5\u0646\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0648\u06ce \u0634\u06d5\u0695\u062a\u0627\u0646 \u067e\u06ce \u0628\u0641\u0631\u06c6\u0634\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u0634 \u0634\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u06b5\u06af\u06cc\u0631\u0633\u0627\u0646\u062f\u060c \u0644\u06d5 \u06a9\u0648\u0634\u062a\u0646\u06cc\u0627\u0646 \u0695\u0627\u0645\u06d5\u0648\u06d5\u0633\u062a\u0646\u061b \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0633 \u0628\u06d5\u0631\u062f\u0627\u0631\u06cc\u0634\u062a\u0627\u0646 \u0628\u0648\u0648\u0646\u060c \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0644\u06d5 \u06a9\u0648\u0634\u062a\u0646\u06cc\u0627\u0646 \u0648\u0686\u0627\u0646 \u0645\u06d5\u062f\u06d5\u0646 \u062a\u0627 \u0626\u0627\u0698\u0627\u0648\u06d5 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0628\u0646\u06d5\u0628\u0695 \u062f\u06d5\u0628\u06ce \u0648 \u062f\u06cc\u0646 \u0647\u06d5\u0631 \u062f\u06cc\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06ce\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0633\u0628\u06d5\u0631\u062f\u0627\u0631 \u0628\u0648\u0648\u0646\u060c \u062f\u06d5\u0633\u062a \u062f\u0631\u06ce\u0698\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u062f\u0698\u06cc \u0646\u0627\u0647\u06d5\u0642\u06d5.',
    '\u0645\u0627\u0646\u06af\u06cc \u0634\u06d5\u0695\u0628\u06d5\u0633 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u0645\u0627\u0646\u06af\u06cc \u0634\u06d5\u0695\u0628\u06d5\u0633\u061b \u0634\u06a9\u0627\u0646\u062f\u0646\u06cc \u0647\u06d5\u0631 \u0695\u06ce\u0632\u06ce\u06a9\u06cc\u0634 \u062a\u06c6\u06b5\u06d5\u06cc \u0647\u06d5\u06cc\u06d5. \u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06cc \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u06a9\u0631\u062f\u060c \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06cc \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u062a\u0627\u0646 \u0647\u06d5\u0628\u06ce\u061b \u062e\u0648\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646\u06d5.',
    '\u0645\u0627\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062a\u0627 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0644\u06d5 \u0695\u0627\u0647\u06cc \u062e\u0648\u062f\u0627 \u0628\u0628\u06d5\u062e\u0634\u0646\u061b \u062e\u06c6\u0634\u062a\u0627\u0646 \u0628\u06d5 \u0641\u06d5\u062a\u06d5\u0695\u0627\u062a \u0645\u06d5\u062f\u06d5\u0646 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6. \u0686\u0627\u06a9\u06d5 \u0628\u06a9\u06d5\u0646\u060c \u062e\u0648\u0627 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u06cc \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u0646.',
    '\u062d\u06d5\u062c \u0648 \u0639\u06d5\u0645\u0631\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u0628\u06d5\u062c\u06ce \u0628\u06ce\u0646\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0695\u06ce\u06af\u06d5\u0634\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0627\u0646\u060c \u0626\u06d5\u0648\u0633\u0627 \u0686\u06d5\u0646\u062f\u06cc \u0628\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0644\u0648\u06ce \u0626\u0627\u0698\u06d5\u06b5 \u0628\u06a9\u06d5\u0646\u06d5 \u0642\u0648\u0631\u0628\u0627\u0646\u06cc. \u0647\u06d5\u062a\u0627 \u0626\u0627\u0698\u06d5\u06b5\u06cc \u0642\u0648\u0631\u0628\u0627\u0646\u06cc \u0646\u06d5\u06af\u0627\u062a\u06d5 \u062c\u06ce\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648 \u0633\u06d5\u0631 \u0645\u06d5\u062a\u0627\u0634\u0646. \u06a9\u06ce\u0634 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0634 \u0628\u06d5\u0628\u0627\u0631\u06d5 \u06cc\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0644\u06d5 \u0633\u06d5\u0631\u062f\u0627\u060c \u0644\u06d5 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u0628\u06d5\u0695\u06c6\u0698\u0648\u0648 \u0628\u06ce \u06cc\u0627\u0646 \u062e\u06ce\u0631\u06ce\u06a9 \u0628\u0628\u06d5\u0634\u06ce\u062a\u06d5\u0648\u06d5\u060c \u06cc\u0627\u0646 \u0626\u0627\u0698\u06d5\u06b5\u06ce\u06a9 \u0628\u06a9\u0627\u062a\u06d5 \u062e\u06ce\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627. \u0647\u06d5\u0631\u06a9\u0627\u062a\u06cc\u0634 \u062a\u0631\u0633\u06ce\u06a9\u0648\u0648 \u0646\u06d5\u0645\u0627\u060c \u0647\u06d5\u0631\u06a9\u06ce \u0648\u06cc\u0633\u062a\u06cc \u0628\u06d5\u0647\u0631\u06d5 \u0628\u0628\u0627\u060c \u0644\u06d5 \u0639\u06d5\u0645\u0631\u06d5 \u062a\u0627 \u06a9\u0627\u062a\u06cc \u062d\u06d5\u062c\u06ce\u060c \u0647\u06d5\u0631 \u0686\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062f\u06ce\u060c \u062d\u06d5\u06cc\u0648\u0627\u0646 \u0628\u06a9\u0627\u062a\u06d5 \u0642\u0648\u0631\u0628\u0627\u0646\u06cc. \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06cc\u0634\u06cc \u0646\u06d5\u0695\u06c6\u06cc\u06cc\u0634\u062a\u060c \u0633\u06ce \u0695\u06c6\u0698 \u0644\u06d5 \u062d\u06d5\u062c \u0628\u06d5\u0695\u06c6\u0698\u0648\u0648 \u0628\u06ce\u061b \u0648\u06d5\u062e\u062a\u06ce \u06af\u06d5\u0695\u0627\u0634\u0646\u06d5\u0648\u06d5 \u062d\u06d5\u0648 \u0695\u06c6\u0698\u061b \u06a9\u06d5 \u062a\u06ce\u06a9\u0695\u0627\u06cc\u06cc \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u062f\u06d5 \u0695\u06c6\u0698\u06cc \u062a\u06d5\u0648\u0627\u0648. \u0626\u06d5\u0645 \u0628\u06d5\u0647\u0631\u06d5 \u0628\u0631\u062f\u0646\u06d5\u0634 \u0647\u06d5\u0631 \u0628\u06c6 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u062e\u06c6\u06cc \u0645\u0627\u06b5\u06cc \u0644\u06d5 \u0645\u06d5\u06a9\u06a9\u06d5\u062f\u0627 \u0646\u06d5\u0628\u06ce. \u0626\u06d5\u0628\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646\u060c \u062e\u0648\u0627 \u0644\u06d5 \u062a\u06c6\u06b5\u06d5\u0633\u0627\u0646\u062f\u0646\u0627 \u062a\u0648\u0646\u062f\u0648\u062a\u06cc\u0698\u06d5.',
    '\u0647\u06d5\u0695\u06d5\u062a\u06cc \u062d\u06d5\u062c \u0686\u06d5\u0646\u062f \u0645\u0627\u0646\u06af\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 \u0641\u06d5\u0631\u0632\u06cc \u062d\u06d5\u062c\u06ce \u0628\u06d5\u062c\u06ce \u0628\u06ce\u0646\u06ce\u060c \u062a\u062e\u0648\u0648\u0646\u06cc \u0698\u0646\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0646 \u0648 \u0646\u06ce\u0648\u0648\u0646\u0631\u062a\u06a9\u06d5 \u0644\u06ce\u06a9 \u0647\u06d5\u06b5\u062f\u0627\u0646 \u0648 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627\u062f\u0627\u0646 \u0648 \u0642\u0633\u06d5 \u0628\u06d5 \u0632\u06c6\u0631\u0627\u0646\u06ce\u062f\u0627\u0646 \u0648 \u0634\u06d5\u0695 \u0648 \u0647\u06d5\u0631\u0627 \u2013 \u0628\u0628\u0695\u0627\u06cc \u0628\u0628\u0695\u0627\u06cc \u2013 \u0644\u06d5 \u062d\u06d5\u062c\u062f\u0627 \u0646\u06cc\u0646\u060c \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0686\u0627\u06a9\u06cc\u0634 \u0628\u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce. \u0632\u06d5\u0648\u0627\u062f\u06cc \u0695\u06ce\u06af\u06d5\u0634\u0648\u0648 \u067e\u06ce \u0628\u06ce\u061b \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u062a\u06c6\u0634\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u060c \u062e\u06c6 \u0644\u06d5 \u06af\u0648\u0646\u0627\u062d \u067e\u0627\u0631\u0627\u0632\u062a\u0646\u06d5. \u06af\u06d5\u0631\u06d5\u06a9 \u0644\u06d5 \u0645\u0646 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce \u0626\u06d5\u06cc \u06af\u0631\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632\u0627\u0646!',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0645\u0627\u0648\u06d5\u06cc \u062d\u06d5\u062c\u06cc\u0634\u062f\u0627 \u0695\u06c6\u0632\u06cc \u0644\u06d5 \u062e\u0648\u0627 \u062f\u0627\u0648\u0627 \u0628\u06a9\u06d5\u0646\u060c \u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u0648\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5. \u06af\u06d5\u0631 \u0644\u06d5 \u00ab\u0639\u0631\u0641\u0627\u062a\u00bb \u062f\u0627\u06af\u06d5\u0695\u0627\u0646\u060c \u0644\u06d5 \u067e\u06d5\u0646\u0627 \u00ab\u0645\u0634\u0639\u0631\u0627\u0644\u062d\u0631\u0627\u0645\u00bb \u06cc\u0627\u062f\u06cc \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646\u061b \u06a9\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u0647\u06cc\u0686 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc\u062a\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u060c \u062e\u0633\u062a\u0646\u06cc\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5.',
    '\u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce\u0634\u062f\u0627 \u062e\u06d5\u06b5\u06a9 \u062f\u0627\u06af\u06d5\u0695\u0627\u0646\u060c \u0626\u06ce\u0648\u06d5\u0634 \u0644\u06d5\u0648\u06ce \u062f\u0627\u06af\u06d5\u0695\u06ce\u0646 \u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u062a\u0627\u0646\u0628\u06d5\u062e\u0634\u06ce\u061b \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062a\u0627\u0648\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0626\u06ce\u0648\u06d5 \u0695\u06ce\u0648\u0634\u0648\u06ce\u0646\u06cc \u067e\u06ce\u062f\u0627\u0648\u06cc\u0633\u062a\u0648\u0648 \u0628\u06d5\u062c\u06ce \u0647\u06ce\u0646\u0627\u060c \u0648\u06d5\u06a9 \u06cc\u0627\u062f\u06cc \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646 \u2013 \u0647\u06d5\u0631 \u0626\u0627\u0648\u0627\u0648 \u0644\u06d5\u0648\u06d5 \u067e\u062a\u0631\u06cc\u0634 \u2013 \u06cc\u0627\u062f \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5. \u0644\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5\u062f\u0627 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u06b5\u06ce: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u06a9\u0627\u0645\u0695\u06d5\u0648\u0627\u0645\u0627\u0646 \u06a9\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06cc \u0648\u0627\u0634 \u0647\u06d5\u06cc\u06d5 \u0626\u06ce\u0698\u06ce: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0647\u06d5\u0645 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0648 \u0647\u06d5\u0645 \u0644\u06d5 \u0633\u06d5\u06b5\u0627 \u062a\u0648\u0648\u0634\u06cc \u062e\u06ce\u0631 \u0648 \u062e\u06c6\u0634\u06cc\u0645\u0627\u0646 \u06a9\u06d5 \u0648 \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u0627\u06af\u0631\u06cc\u0634 \u0628\u0645\u0627\u0646\u067e\u0627\u0631\u06ce\u0632\u06d5.',
    '\u062c\u0627 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u067e\u06ce\u06cc \u06a9\u0631\u062f\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce. \u062e\u0648\u0627 \u0628\u06d5 \u067e\u06d5\u0644\u06d5 \u0628\u06d5 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u0644\u06d5 \u0695\u06c6\u0698\u0627\u0646\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627\u0648 \u0644\u06d5 \u0628\u06cc\u0631 \u0646\u06d5\u0686\u06ce\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062f\u0648\u0648 \u0695\u06c6\u0698\u06cc \u067e\u06ce\u0634 \u0628\u062e\u0627 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u06cc \u0646\u0627\u06cc\u06d5\u062a\u06d5 \u0633\u06d5\u0631. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u06d5 \u0648 \u062a\u0631\u0633\u06cc \u062e\u0648\u062f\u0627\u06cc \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5 \u2013 \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634 \u062f\u0648\u0627 \u06a9\u06d5\u0648\u062a \u2013 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5. \u0628\u06cc\u0634\u06cc\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0644\u0627\u06cc \u062e\u0648\u0627\u0648\u06d5 \u062e\u0695 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u06d5\u062f\u0627 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0642\u0633\u06d5 \u0648 \u06af\u0641\u062a\u06cc \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627 \u0628\u06c6 \u062a\u06c6 \u0633\u06d5\u06cc\u0631 \u0648 \u0633\u06d5\u0631\u0633\u0648\u0648\u0695\u06ce\u0646\u06d5 \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u0644\u06d5 \u062f\u06b5\u06cc\u0627\u06cc\u06d5 \u062e\u0648\u0627 \u0628\u06d5 \u0634\u0627\u06cc\u06d5\u062a \u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0644\u06d5 \u062f\u0648\u0698\u0645\u0646\u0627\u062a\u06cc \u06a9\u0631\u062f\u0646\u0627 \u0632\u06c6\u0631 \u0633\u06d5\u0631\u0633\u06d5\u062e\u062a\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5 \u0644\u06d5 \u062a\u06c6 \u062f\u0648\u0648\u0631 \u06a9\u06d5\u0648\u062a\u06d5\u0648\u06d5\u060c \u0645\u0644 \u0644\u06d5 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u062f\u06d5\u0646\u06ce\u062a \u0648 \u0644\u06d5 \u0639\u06d5\u0631\u0632\u0627 \u062e\u0631\u0627\u067e\u06d5 \u062f\u06d5\u06a9\u0627 \u0648 \u06a9\u06ce\u06b5\u06af\u06d5 \u0648 \u0695\u06d5\u0633\u06d5\u0646 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0626\u06d5\u0628\u0627. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5 \u0628\u06d5\u062f\u0641\u06d5\u0695\u0627\u0646 \u062e\u06c6\u0634\u06cc \u0646\u0627\u06cc\u06d5.',
    '\u06a9\u06d5 \u067e\u06ce\u06cc \u0648\u062a\u0631\u0627: \u0626\u06d5\u0634\u06ce \u062a\u0631\u0633\u062a \u0647\u06d5\u0628\u06ce \u0644\u06d5 \u062e\u0648\u0627\u060c \u062f\u06d5\u0645\u0627\u0631\u0632\u0644\u06cc \u067e\u062a\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u06cc\u0628\u0627. \u0628\u0627 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc \u0644\u06ce \u067e\u0695 \u0628\u06ce\u060c \u062c\u06ce \u062d\u06d5\u0633\u0627\u0646\u06d5\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e\u06cc\u0634\u06d5.',
    '\u0647\u06cc \u0648\u0627\u0634 \u0644\u06d5 \u0646\u0627\u0648 \u0645\u06d5\u0631\u062f\u0645 \u0647\u06d5\u06cc\u06d5 \u0628\u06c6 \u0695\u06d5\u0632\u0627\u06cc \u062e\u0648\u0627 \u06af\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u062e\u0634\u0646. \u062e\u0648\u062f\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062e\u06c6\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u062a\u0627\u0646 \u0626\u0627\u0645\u0627\u062f\u06d5\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u0628\u06d5\u0631\u06cc \u0628\u0646. \u067e\u06ce \u0645\u06d5\u0646\u06ce\u0646\u06d5 \u062c\u06ce \u067e\u0627\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u061b \u0626\u0627\u0634\u06a9\u0631\u0627\u0647\u0640 \u06a9\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u067e\u06ce\u062a\u0627\u0646 \u062f\u0648\u0698\u0645\u0646\u06d5.',
    '\u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u06af\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u0627\u0646\u06d5\u062a\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u062a\u0648\u0648 \u0647\u06d5\u06b5\u062e\u0644\u06cc\u0633\u06a9\u06ce\u0646\u060c \u062f\u06d5\u0628\u06ce \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u062e\u0648\u0627 \u062a\u0648\u0627\u0646\u0627 \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0646 \u062e\u0648\u0627 \u0648 \u0641\u0631\u06cc\u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u0633\u06d5\u06cc\u0648\u0627\u0646\u06cc \u0647\u06d5\u0648\u0631\u0627\u0646\u062f\u0627 \u0628\u06ce\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646 \u0648 \u06a9\u0627\u0631\u06d5\u06a9\u06d5 \u06a9\u06c6\u062a\u0627\u06cc\u06cc \u067e\u06ce \u0628\u06ce\u062a\u061f \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u062f\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644 \u0628\u067e\u0631\u0633\u06d5! \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5 \u0628\u06d5\u0648\u0627\u0646\u061f \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06a9\u06d5\u06cc\u0646 \u0648 \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u06ce\u060c \u0628\u0627 \u0628\u0632\u0627\u0646\u06ce \u062e\u0648\u0627 \u0644\u06d5 \u0633\u0632\u0627\u06cc \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u0627\u0646 \u062a\u0648\u0646\u062f\u0648\u062a\u06cc\u0698\u06d5.',
    '\u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 \u062e\u06d5\u0645\u06b5\u06cc\u0648\u06d5 \u0648 \u0695\u0627\u0632\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5 \u0648 \u06af\u06d5\u0645\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u06a9\u06d5\u0686\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u06d5\u06a9\u0627\u0646 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0648\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062d\u06d5\u0632 \u06a9\u0627\u060c \u0628\u06ce \u0698\u0645\u0627\u0631\u06d5 \u0695\u06c6\u0632\u06cc \u062f\u06d5\u0646\u06ce\u0631\u06ce.',
    '\u0628\u06d5\u0631\u06ce\u060c \u0645\u06d5\u0631\u062f\u0645 \u0647\u06d5\u0645\u0648\u0648\u06cc \u0686\u06cc\u0646 \u0648 \u0628\u06d5\u0631\u06d5\u06cc\u06d5\u06a9 \u0628\u0648\u0648\u0646. \u062e\u0648\u062f\u0627 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0646\u0627\u0631\u062f\u0646 \u0645\u0698\u062f\u06d5\u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0628\u0646\u060c \u06a9\u062a\u06ce\u0628\u06cc \u0695\u0627\u0633\u062a \u0648 \u0695\u06d5\u0648\u0627\u0634\u06cc \u0695\u06d5\u06af\u06d5\u06b5 \u062e\u0633\u062a\u0646 \u062a\u0627 \u0626\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u0628\u0648\u0648\u0646\u06d5 \u0628\u0646\u06d5\u0645\u0627\u06cc \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646\u060c \u0628\u06c6\u06cc\u0627\u0646 \u06cc\u06d5\u06a9\u0627\u06b5\u0627 \u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u0648 \u067e\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06ce\u0646\u0646. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u06c6 \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0633\u06d5\u0644\u0645\u0627\u0646\u062f \u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u06a9\u062a\u06ce\u0628 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0647\u0627\u062a\u0628\u0648\u0648\u060c \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0634\u06cc\u0627\u0646 \u0644\u0627\u0628\u0648\u0648\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0631\u06d5\u06cc\u06cc \u0646\u0627\u0648 \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0648\u0627\u06cc \u06a9\u0631\u062f\u060c \u067e\u06ce\u06a9 \u0646\u06d5\u06cc\u06d5\u0646. \u0626\u06cc\u062a\u0631 \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u06c6\u06cc \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0646\u06cc\u0634\u0627\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u062f\u0627 \u0648 \u062a\u06ce\u06cc \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646 \u0626\u06d5\u0648 \u06a9\u06ce\u0634\u06d5 \u0644\u06d5\u0628\u06d5\u0631\u0686\u06cc \u06a9\u0631\u0627. \u062e\u0648\u062f\u0627 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u0627\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5[ \u0628\u06d5 \u0647\u0627\u0633\u0627\u0646\u06cc ] \u062f\u06d5\u0686\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u061f \u06a9\u06d5\u0686\u06cc \u0647\u06ce\u0634\u062a\u0627 \u0626\u06d5\u0648\u06d5\u06cc \u0628\u06d5 \u0633\u06d5\u0631 \u067e\u06ce\u0634\u06cc\u0646\u0627\u0646\u062a\u0627\u0646\u0627 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0628\u06d5 \u0633\u06d5\u0631 \u0626\u06cc \u0648\u0627 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u06af\u06d5\u0644\u06ce \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5 \u0648 \u0626\u0627\u0632\u0627\u0631 \u0648 \u0646\u06d5\u062f\u0627\u0631\u06cc \u0648 \u0686\u06d5\u0648\u0633\u0627\u0646\u06d5\u0648\u06d5 \u0628\u0648\u0648\u0646\u061b \u0632\u06c6\u0631 \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u062a\u06d5\u06a9\u0627\u0646 \u062f\u0631\u0627\u0646\u060c \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634 \u0648 \u06cc\u0627\u0631\u0627\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06cc\u0634\u06cc \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a \u06a9\u06d5: \u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0647\u0627\u0646\u0627\u0645\u0627\u0646 \u062f\u06ce\u061f \u062a\u06c6 \u0628\u0632\u0627\u0646\u06d5 \u06a9\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062e\u0648\u0627 \u0646\u0632\u06cc\u06a9\u06d5.',
    '\u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646: \u0686\u06ce \u0628\u0628\u06d5\u062e\u0634\u0646\u061f \u067e\u06cc\u06cc\u0627\u0646 \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631 \u0634\u062a\u06cc \u0686\u0627\u06a9 \u06a9\u06d5 \u062f\u06d5\u06cc\u0628\u06d5\u062e\u0634\u0646 \u0628\u06cc\u062f\u06d5\u0646 \u0628\u06d5 \u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u0627\u0646 \u0648 \u062e\u0632\u0645\u0627\u0646\u06cc \u0646\u0632\u06cc\u06a9\u062a\u0627\u0646 \u0648 \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646 \u0648 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u06ce\u062f\u0627 \u0645\u0627\u0648\u0646. \u0647\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc\u06ce \u0626\u06ce\u0648\u06d5 \u0628\u06cc\u06a9\u06d5\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u067e\u06ce \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u062e\u06d5\u0632\u0627\u062a\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5\u060c \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f\u06d5 \u062e\u06c6\u0634\u062a\u0627\u0646 \u0644\u06ce\u06cc \u0646\u0627\u06cc\u06d5. \u0695\u06d5\u0646\u06af\u06d5 \u0644\u06d5 \u0634\u062a\u06ce\u06a9 \u062d\u06d5\u0632 \u0646\u06d5\u06a9\u06d5\u0646 \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06c6\u062a\u0627\u0646 \u0648\u06d5\u062e\u06ce\u0631 \u06af\u06d5\u0695\u06ce\u060c \u0695\u06d5\u0646\u06af\u06cc\u0634 \u0647\u06d5\u06cc\u06d5 \u0626\u0627\u0648\u0627\u062a \u0628\u06c6 \u0634\u062a\u06ce \u0648\u0627 \u0628\u062e\u0648\u0627\u0632\u0646 \u06a9\u06d5 \u0626\u0627\u06a9\u0627\u0645\u06d5\u06a9\u06d5\u06cc \u0628\u0627\u0634 \u0646\u06d5\u0628\u06ce\u061b \u062e\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0648 \u0626\u06ce\u0648\u06d5 \u0633\u06d5\u0631\u06cc \u0644\u06ce \u062f\u06d5\u0631\u0646\u0627\u06a9\u06d5\u0646.',
    '\u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646: \u062a\u0648\u0648\u0634\u06cc \u0634\u06d5\u0695 \u0628\u0648\u0648\u0646 \u0644\u06d5 \u0645\u0627\u0646\u06af\u06cc \u0634\u06d5\u0695\u0628\u06d5\u0633\u0627 \u0686\u06c6\u0646\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0634\u06d5\u0695 \u0644\u06d5 \u0647\u06d5\u06cc\u06a4\u06cc \u0648\u0627\u062f\u0627 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06d5 \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u0627 \u0628\u06d5\u0633\u062a\u0646 \u0648 \u062d\u0627\u0634\u0627 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u06a9\u0631\u062f\u0646 \u0648 \u0645\u0627\u06b5\u06cc \u062e\u0648\u0627 \u0628\u06d5\u0695\u06ce\u0632 \u0646\u06d5\u06af\u0631\u062a\u0646\u060c \u0644\u06ce \u0648\u06d5\u062f\u06d5\u0631\u0646\u0627\u0646\u06cc \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u2013 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u2013 \u062a\u0627\u0648\u0627\u0646\u06cc \u0647\u06d5\u0631\u06d5 \u0645\u06d5\u0632\u0646\u06d5\u061b \u0626\u0627\u0698\u0627\u0648\u06d5\u0634 \u0628\u06c6 \u0634\u06d5\u0695 \u0646\u0627\u0646\u06d5\u0648\u06d5 \u0644\u06d5 \u06a9\u0648\u0634\u062a\u0646\u06ce \u062e\u0631\u0627\u067e\u062a\u0631\u06d5. \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0633\u062a\u0648\u0648 \u0644\u06ce \u0628\u06d5\u0631\u0646\u0627\u062f\u06d5\u0646\u060c \u0647\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0634\u06d5\u0695 \u062f\u06ce\u0646 \u0647\u06d5\u062a\u0627 \u2013 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06c6\u06cc\u0627\u0646 \u0628\u0644\u0648\u06ce \u2013 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0648\u06d5\u0631\u0628\u06af\u06ce\u0695\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0644\u06d5 \u062f\u06cc\u0646 \u0648\u06d5\u0631\u06af\u06d5\u0695\u06ce \u0648 \u0628\u06d5 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u0628\u0645\u0631\u06ce\u060c \u0695\u06d5\u0646\u062c \u062e\u06d5\u0633\u0627\u0631\u06cc \u062f\u0646\u06cc\u0627\u0634 \u0648 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u062f\u06d5\u0628\u0646 \u0648 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0646\u0627\u0648 \u062f\u06c6\u0632\u06d5\u0642\u06d5\u0648\u06d5 \u0648 \u062a\u0627\u0647\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u06d5\u062f\u06d5\u0631\u0646 \u0648 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0686\u0648\u0648\u0646\u06d5 \u062e\u06d5\u0632\u0627\u060c \u0628\u06d5 \u0647\u0648\u0645\u06ce\u062f\u0646 \u062e\u0648\u0627 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0648\u06d5\u06cc\u0627\u0646 \u062e\u0648\u06ce\u0646\u06ce\u060c \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0644\u06d5\u0645\u06d5\u0695 \u0634\u06d5\u0631\u0627\u0628 \u0648 \u0642\u0648\u0645\u0627\u0631\u06cc\u0634 \u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646\u061b \u0628\u06ce\u0698\u06d5: \u062a\u0627\u0648\u0627\u0646\u06cc\u0627\u0646 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06d5 \u0648 \u0628\u06c6 \u062e\u06d5\u06b5\u06a9\u06cc\u0634 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061b \u062a\u0627\u0648\u0627\u0646\u06d5\u06a9\u06d5 \u0644\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0632\u06c6\u0631\u062a\u0631\u06d5. \u067e\u0631\u0633\u06cc \u0626\u06d5\u0648\u06d5\u0634\u062a \u067e\u06ce\u062f\u06d5\u06a9\u06d5\u0646\u060c \u0686\u06cc \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u0627\u062f\u0627 \u0628\u06d5\u062e\u062a \u06a9\u06d5\u0646\u061f \u0628\u06ce\u0698\u06d5: \u0632\u06ce\u062f\u06d5 \u0644\u06d5 \u067e\u06ce\u0648\u06cc\u0633\u062a\u0648\u0648. \u0626\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u062e\u0648\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u062a\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627\u060c \u0634\u0627\u06cc\u06d5\u062a \u0626\u06ce\u0648\u06d5 \u0628\u062a\u0648\u0627\u0646\u0646 \u0628\u06cc\u0631 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061b',
    '\u0647\u06d5\u0645 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0648 \u0647\u06d5\u0645 \u0644\u06d5 \u0633\u06d5\u06b5\u0627. \u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646: \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646 \u0686\u06c6\u0646 \u0628\u06cc\u0646\u061f \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u062f\u06b5\u0633\u06c6\u0632\u06cc \u0626\u0627\u06af\u0627\u0648 \u0644\u06ce \u0628\u0646 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0698\u06cc\u0646\u060c \u0648\u06d5\u06a9 \u0628\u0631\u0627 \u0648 \u062e\u0648\u0634\u06a9\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u0646. \u062e\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06ce \u0628\u06d5\u062f\u0641\u06d5\u0695\u06d5 \u0648 \u06a9\u06ce \u06a9\u0627\u0631\u0686\u0627\u06a9\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627 \u0626\u06d5\u0631\u06a9\u06cc \u0644\u06ce \u06af\u0631\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u0646\u061b \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u062e\u0648\u0627\u0633\u062a\u0646 \u0648 \u0634\u0648\u0648 \u067e\u06ce \u06a9\u0631\u062f\u0646\u06cc \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u0627\u0646 \u0628\u06c6 \u0645\u06ce\u0631 \u0648 \u0698\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u0695\u06d5\u0648\u0627 \u0646\u06cc\u06cc\u06d5\u060c \u0628\u0627 \u0632\u06c6\u0631\u06cc\u0634\u06cc\u0627\u0646 \u062d\u06d5\u0632 \u0644\u06ce \u0628\u06a9\u06d5\u0646\u061b \u0647\u06d5\u062a\u0627 \u0646\u06d5\u0628\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u0634\u06ce \u0628\u06cc\u0627\u0646\u06a9\u06d5\u0646\u06d5 \u0647\u0627\u0648\u0633\u06d5\u0631\u062a\u0627\u0646. \u06a9\u06d5\u0646\u06cc\u0632 \u0648 \u0628\u06d5\u0646\u062f\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u0628\u06c6 \u0647\u0627\u0648\u062c\u0648\u0648\u062a\u06cc\u060c \u0644\u06d5 \u0645\u06ce\u0631 \u0648 \u0698\u0646\u06cc \u0626\u0627\u0632\u0627\u062f\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633 \u0647\u06ce\u0698\u0627\u062a\u0631\u0646. \u0626\u06d5\u0648\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0695\u0627\u06a9\u06ce\u0634\u0646\u061b \u062e\u0648\u0627\u0634 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5 \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u06c6\u06cc \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u062a\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u06ce \u0648 \u0628\u062a\u0627\u0646\u06af\u06d5\u06cc\u06ce\u0646\u06ce\u062a\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a. \u062e\u0648\u062f\u0627 \u0626\u06d5\u0645 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06d5\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0695\u0648\u0648\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u0645\u06d5\u0695 \u0639\u0648\u0632\u0631\u06cc \u0698\u0646\u0627\u0646\u06d5\u0648\u06d5\u0634 \u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646\u060c \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648\u06cc\u0634 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06d5\u061b \u0626\u06d5\u0648 \u0686\u06d5\u0646\u062f \u0695\u06c6\u0698\u06d5\u06cc \u062e\u0648\u06ce\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u0633\u06d5\u0631 \u062c\u06ce\u06cc\u06cc \u0698\u0646\u0627\u0646\u0648\u0648 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0695\u0627\u0648\u06d5\u0633\u062a\u0646 \u062a\u0627 \u067e\u0627\u06a9 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5. \u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u062e\u0648\u06ce\u0646 \u067e\u0627 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u060c \u0644\u06d5\u0648 \u062c\u06ce\u06cc\u06d5\u0648\u06d5 \u06a9\u06d5 \u062e\u0648\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce\u062f\u0627\u0648\u0646 \u0628\u06c6\u06cc\u0627\u0646 \u0648\u06d5\u0631\u0646. \u062e\u0648\u0627 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u062e\u06c6\u0634\u06cc \u062f\u06ce \u06a9\u06d5 \u0644\u06d5 \u06af\u0648\u0646\u0627\u062d \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u0646 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0628\u06d5 \u067e\u0627\u06a9 \u0648 \u062e\u0627\u0648\u06ce\u0646\u06cc \u0628\u0698\u06cc\u0646.',
    '\u0698\u0646\u0627\u0646\u0648\u0648 \u06a9\u06ce\u06b5\u06af\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646\u0646\u061b \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce\u0648\u06d5 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce\u060c \u0628\u0686\u0646\u06d5 \u06a9\u06ce\u06b5\u06af\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646\u06d5\u0648\u06d5\u061b \u062a\u06c6\u0634\u06d5\u06cc\u06d5\u06a9\u06cc\u0634 \u0648\u06d5\u067e\u06ce\u0634 \u062e\u06c6 \u062e\u06d5\u0646. \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627\u0634 \u0628\u062a\u0631\u0633\u0646\u060c \u0628\u06cc\u0634\u06cc\u0632\u0627\u0646\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u0627 \u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648 \u062f\u06d5\u0628\u0646. \u0645\u0698\u062f\u06d5\u0634 \u0628\u062f\u06d5 \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646.',
    '\u062e\u0648\u062f\u0627\u06cc \u0645\u06d5\u06a9\u06d5\u0646 \u0628\u06d5 \u06a9\u06d5\u0631\u0633\u062a\u06d5\u06cc \u0633\u0648\u06ce\u0646\u062f\u06cc \u062e\u06c6\u062a\u0627\u0646\u061b \u06a9\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0686\u0627\u06a9\u06d5\u0648 \u0644\u06ce \u0628\u0628\u0695\u06ce \u0648 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u06cc\u0648 \u067e\u06ce \u0646\u06d5\u0647\u06ce\u06b5\u06ce \u0648 \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0695\u0627\u0646\u06d5\u06af\u06d5\u0646\u061b \u062e\u0648\u062f\u0627 \u0626\u06d5\u0698\u0646\u06d5\u0648\u06ce \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u062e\u0648\u0627 \u0628\u06c6 \u0633\u0648\u06ce\u0646\u062f\u06cc \u0633\u06d5\u0631\u0632\u0627\u0631\u06d5\u06a9\u06cc \u06af\u0648\u0646\u0627\u0647\u0648\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u0627\u0646\u0648\u0648\u0633\u06ce\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u062a\u06d5\u0647\u06cc \u062f\u06b5\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06ce\u060c \u062e\u0648\u062f\u0627 \u0644\u06ce\u0648 \u062f\u06d5\u067e\u0631\u0633\u06ce\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u06b5\u06d5 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u0644\u06d5\u0633\u06d5\u0631\u062e\u06c6\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0633\u0648\u06ce\u0646\u062f\u06ce \u0644\u06d5 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u062e\u06c6\u0646 \u06a9\u06d5 \u0626\u06cc\u062a\u0631 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0648\u0648\u0646\u060c \u062f\u06d5\u0628\u06ce \u0686\u0648\u0627\u0631 \u0645\u0627\u0646\u06af \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u06af\u06d5\u0695\u0627\u0634\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u06af\u0648\u0646\u0627\u0647\u0640 \u0628\u06d5\u062e\u0634 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06d5\u06b5\u0627\u0642\u06cc\u0634 \u0628\u0695\u06cc\u0627\u0631\u06cc\u0627\u0646 \u062f\u0627\u060c \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06cc\u0633\u06ce \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0634\u06d5.',
    '\u062a\u06d5\u06b5\u0627\u0642 \u062f\u0631\u06cc\u0627 \u0648 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u06d5 \u0647\u06d5\u062a\u0627 \u0633\u06ce \u062c\u0627\u0631 \u0644\u06d5 \u0628\u06ce \u0646\u0648\u06ce\u0698\u06cc \u067e\u0627\u06a9 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u0633\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0647\u06d5\u06cc\u06d5\u060c \u0695\u06d5\u0648\u0627 \u0646\u06cc\u06cc\u06d5 \u0626\u06d5\u0648 \u062a\u06c6\u0644\u06d5\u06cc \u062e\u0648\u0627 \u062e\u0633\u062a\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u0632\u06af\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06cc\u0634\u0627\u0631\u0646\u06d5\u0648\u06d5. \u0644\u06d5\u0645 \u0628\u06d5\u06cc\u0646\u06d5\u062f\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0695\u06ce\u06a9 \u06a9\u06d5\u0648\u0646\u060c \u0628\u06c6 \u062f\u06cc\u0633\u0627\u0646 \u0647\u06ce\u0646\u0627\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646\u060c \u0645\u06ce\u0631\u062f\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0634\u06cc\u0627\u0648\u062a\u0631\u0646. \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0626\u06d5\u0631\u06a9\u06cc \u0698\u0646\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0634\u0627\u0646\u06d5\u060c \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0645\u0627\u0641\u06cc \u0695\u06d5\u0648\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5. \u067e\u06cc\u0627\u0648\u06cc\u0634 \u0644\u06d5 \u0698\u0646\u0627\u0646 \u067e\u0644\u06d5\u06cc\u06d5\u06a9 \u0628\u06d5\u0631\u06d5 \u0698\u0648\u0648\u0631\u062a\u0631\u0646\u060c \u062e\u0648\u062f\u0627\u0634 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u062a\u06d5\u06b5\u0627\u0642\u06cc \u0698\u0646 \u062f\u0648\u0648 \u06a9\u06d5\u0695\u06d5\u062a\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 \u06cc\u0627\u0646 \u0695\u0627\u06af\u0631\u062a\u0646\u06cc \u0628\u0627\u0634 \u0648 \u062e\u0648\u062f\u0627\u067e\u06d5\u0633\u0646\u062f\u060c \u06cc\u0627\u0646 \u0628\u06d5 \u062e\u06c6\u0634\u06cc \u0648 \u0628\u06ce \u0647\u06d5\u0631\u0627\u060c \u062f\u06d5\u0633 \u0644\u06ce\u06a9 \u0628\u06d5\u0631\u062f\u0627\u0646\u06d5. \u0644\u06d5\u0648 \u0645\u0627\u06b5\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u062f\u0627\u0648\u0646 \u0695\u06d5\u0648\u0627 \u0646\u06cc\u06cc\u06d5 \u0647\u06cc\u0686\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u0628\u06af\u0631\u0646\u06d5\u0648\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06d5 \u0647\u06d5\u0628\u06ce \u06a9\u06d5 \u0646\u06d5\u062a\u0648\u0627\u0646\u0646 \u0633\u0646\u0648\u0648\u0631\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062a\u0631\u0633\u062a\u0627\u0646 \u0644\u06d5\u0648\u06d5 \u0647\u06d5\u0628\u0648\u0648 \u06a9\u06d5 \u0647\u06d5\u0631\u062f\u0648\u0648\u06a9\u06cc\u0627\u0646 \u0633\u0646\u0648\u0648\u0631\u06cc \u062e\u0648\u062f\u0627 \u0646\u0627\u067e\u0627\u0631\u06ce\u0632\u0646\u060c \u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5 \u0698\u0646\u06d5\u06a9\u06d5 \u062e\u06c6\u06cc \u0628\u06a9\u0695\u06ce\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0645\u0627\u0646\u06d5\u0646 \u0633\u0646\u0648\u0648\u0631\u06cc \u062e\u0648\u062f\u0627\u060c \u0644\u06d5 \u0633\u0646\u0648\u0648\u0631\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0645\u06d5\u0686\u0646\u061b \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0633\u0646\u0648\u0648\u0631\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u062f\u06d5\u0686\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u063a\u06d5\u062f\u0631\u06ce \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0645 \u062f\u0648\u0648 \u062c\u0627\u0631\u06d5\u0634 \u062a\u06d5\u06b5\u0627\u0642\u06cc \u062f\u0627\u060c \u0698\u0646\u06d5\u06a9\u06d5\u06cc \u0628\u06c6\u062d\u06d5\u06b5\u0627\u0644 \u0646\u06cc\u06cc\u06d5 \u062a\u0627 \u0698\u0646\u06d5\u06a9\u06d5 \u062f\u06d5\u0686\u06ce \u0645\u06ce\u0631\u062f\u06ce\u06a9\u06cc \u062a\u0631 \u062f\u06d5\u06a9\u0627\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0648 \u0645\u06ce\u0631\u062f\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u0634\u06cc\u06d5 \u062a\u06d5\u06b5\u0627\u0642 \u062f\u0631\u0627\u060c \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5 \u062f\u0648\u0648\u0628\u0627\u0631\u06d5 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u061b \u0628\u06d5\u0648 \u0645\u06d5\u0631\u062c\u06d5 \u06a9\u06d5 \u0648\u0627\u0628\u0632\u0627\u0646\u0646 \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9\u06cc\u0627\u0646 \u0628\u06d5 \u062a\u06d5\u0631\u0632\u06ce \u0695\u06d5\u0648\u0627 - \u0648\u06d5\u06a9 \u062e\u0648\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062f\u0627\u0648\u06d5 - \u0648\u06ce\u06a9 \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0633\u0646\u0648\u0648\u0631\u06cc \u062e\u0648\u062f\u0627\u0646\u060c \u0695\u0648\u0648\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0648\u0627 \u062d\u0627\u06b5\u06cc \u062f\u06d5\u0628\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0698\u0646\u0648\u0648 \u062a\u06d5\u06b5\u0627\u0642 \u062f\u0627\u0646 \u0648 \u0645\u0627\u0648\u06d5\u06cc \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc\u06d5\u060c \u06cc\u0627\u0646 \u0628\u06d5 \u062a\u06d5\u0631\u0632\u06ce \u062e\u0648\u062f\u0627\u067e\u06d5\u0633\u0646\u062f \u0695\u0627\u06cc\u0627\u0646\u06af\u0631\u0646\u060c \u06cc\u0627\u0646 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u06cc\u0627\u0633\u0627\u06cc \u062f\u06cc\u0646 \u0626\u0627\u0632\u0627\u062f\u06cc\u0627\u0646 \u06a9\u06d5\u0646. \u0646\u06d5\u06a9\u0627 \u0628\u06d5 \u0642\u06d5\u0633 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06cc\u0627\u0646\u060c \u0695\u0627\u06cc\u0627\u0646\u06af\u0631\u0646 \u0648 \u0644\u06d5 \u0647\u06d5\u0642 \u0644\u0627\u062f\u06d5\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0648\u0627 \u0695\u06d5\u0641\u062a\u0627\u0631 \u0628\u06a9\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u062e\u06c6\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u060c \u0646\u0627\u0634\u06ce \u0628\u06d5 \u06af\u06d5\u0645\u06d5\u06cc \u0628\u0632\u0627\u0646\u0646. \u0628\u0627 \u0644\u06d5 \u0628\u06cc\u0631\u0648\u0648 \u0628\u06ce \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0686\u06d5\u0646\u062f\u06cc \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0646. \u0644\u06d5 \u0628\u06cc\u0631 \u0645\u06d5\u06a9\u06d5\u0646 \u062e\u0648\u0627 \u0626\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u0628\u06c6 \u0646\u0627\u0631\u062f\u0648\u0648\u0646 \u06a9\u06d5 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0633\u06d5\u0631\u0695\u0627\u0633\u062a \u062f\u06d5\u0628\u0646 \u0648 \u0628\u06d5\u0648 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627. \u0626\u06d5\u0634\u06ce \u0628\u062a\u0631\u0633\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0698\u0646\u0627\u0646\u062a\u0627\u0646 \u062a\u06d5\u06b5\u0627\u0642 \u062f\u0627 \u0648 \u06a9\u0627\u062a\u06cc \u062d\u06d5\u06cc\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0686\u0648\u0648 - \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u062a\u06d5\u0631\u0632\u06ce\u06a9\u06cc \u0695\u06d5\u0648\u0627 \u0648\u06d5\u0644 \u0626\u0627\u0648\u062c\u0648\u0648\u062a\u06cc \u067e\u06ce\u0634\u0648\u0648\u06cc\u0627\u0646\u0627 \u0695\u06ce\u06a9 \u06a9\u06d5\u0648\u062a\u0646 - \u0626\u06ce\u0648\u06d5 \u0645\u06d5\u0628\u0646\u06d5 \u0628\u06d5\u0631\u0647\u06d5\u06b5\u0633\u062a\u06cc \u0634\u0648\u0648 \u06a9\u0631\u062f\u0646\u06cc\u0627\u0646. \u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u0647\u06d5\u0628\u06ce\u060c \u0626\u06d5\u0645 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u06d5 \u067e\u06ce\u06a9 \u062f\u06ce\u0646\u06ce\u061b \u0626\u06d5\u0645\u06d5 \u0628\u06d5 \u0633\u0648\u0648\u062f\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5 \u0648 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0628\u06c6 \u067e\u0627\u06a9\u06cc\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5. \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0648 \u0626\u06ce\u0648\u06d5 \u0633\u06d5\u0631\u06cc \u0644\u06ce \u062f\u06d5\u0631\u0646\u0627\u06a9\u06d5\u0646.',
    '\u062f\u0627\u06cc\u06a9\u0627\u0646 \u062f\u06d5\u0628\u06ce \u0628\u06d5 \u0645\u0646\u062f\u0627\u06b5\u06cc\u0627\u0646 \u062f\u0648\u0648 \u0633\u0627\u06b5\u06cc \u0695\u06d5\u0628\u06d5\u0642 \u0634\u06cc\u0631 \u0628\u062f\u06d5\u0646. \u0628\u06c6 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u06a9\u06d5 \u0628\u06cc\u06cc\u06d5\u0648\u06ce \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u0634\u06cc\u0631\u06ce \u0628\u062f\u0627\u060c \u0644\u06d5\u0645 \u0645\u0627\u0648\u06d5\u062f\u0627 \u062c\u0644\u06a9 \u0648 \u0628\u0698\u06cc\u0648\u06cc \u062f\u0627\u06cc\u06a9\u06d5\u06a9\u06d5 - \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0627\u0628\u06cc \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u0627\u0646\u06cc \u0626\u06d5\u0648 \u0634\u0648\u06ce\u0646\u06d5 - \u0644\u06d5\u0633\u06d5\u0631 \u0628\u0627\u0648\u06a9\u06d5. \u06a9\u06d5\u0633\u06cc\u0634 \u0644\u06d5 \u062a\u0648\u0627\u0646\u0627 \u0628\u06d5\u062f\u06d5\u0631\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5. \u0647\u06cc\u0686 \u062f\u0627\u06cc\u06a9 \u0648 \u0628\u0627\u0628\u06ce \u0646\u0627\u0628\u06ce \u0644\u06d5\u0628\u06d5\u0631 \u0633\u0627\u0648\u0627\u06cc\u0627\u0646 \u0632\u06cc\u0627\u0646 \u0628\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u0628\u06af\u06d5\u06cc\u0646\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0646\u062f\u0627\u06b5 \u0628\u0627\u0628\u06cc \u0646\u06d5\u0628\u0648\u0648\u060c \u0645\u06cc\u0631\u0627\u062a\u06af\u0631\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u06d5 \u062c\u0644\u06a9 \u0648 \u0628\u0698\u06cc\u0648\u06cc \u062f\u0627\u06cc\u06a9\u06d5\u06a9\u06d5\u06cc \u0641\u06d5\u0631\u0627\u0647\u06d5\u0645 \u06a9\u06d5\u0646. \u062c\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u062f\u0627\u06a9 \u0648 \u0628\u0627\u0628 \u0628\u06d5 \u062e\u06c6\u0634\u06cc \u0648 \u0695\u0627\u0648\u06ce\u0698 \u0648\u06cc\u0633\u062a\u06cc\u0627\u0646[ \u0632\u0648\u0648\u062a\u0631] \u0645\u0646\u062f\u0627\u06b5 \u0644\u06d5 \u0634\u06cc\u0631 \u0628\u0628\u0695\u0646\u06d5\u0648\u06d5\u060c \u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u0627\u06af\u0627\u062a\u06ce. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0628\u06ce \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062a\u0631 \u0634\u06cc\u0631 \u0628\u062f\u0627\u062a\u06d5 \u0645\u0646\u062f\u0627\u06b5\u0627\u0646\u062a\u0627\u0646 - \u0628\u06d5\u0648 \u0645\u06d5\u0631\u062c\u06d5 \u0628\u06d5 \u067e\u06ce \u062f\u0627\u0628\u06cc \u0695\u06c6\u0698\u060c \u06a9\u0631\u06ce\u06cc \u062a\u06d5\u0648\u0627\u0648\u06cc \u0628\u062f\u06d5\u0646\u06ce - \u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u062a\u0627\u0646 \u0646\u0627\u06cc\u06d5\u062a\u06d5 \u0633\u06d5\u0631. \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627\u0634 \u062a\u0631\u0633\u0648\u0648 \u0628\u06ce. \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0634 \u062f\u06d5\u0628\u06ce \u0628\u0632\u0627\u0646\u0646 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06ce\u062a\u0627\u0646 \u062f\u06d5\u0645\u0631\u0646 \u0648 \u0698\u0646\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06d5\u062c\u06ce \u062f\u06d5\u0645\u06ce\u0646\u06ce\u060c \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u062a\u0627 \u0686\u0648\u0627\u0631 \u0645\u0627\u0646\u06af \u0648 \u062f\u06d5 \u0695\u06c6\u0698\u0627\u0646 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646\u061b \u0644\u06d5\u0648\u06d5 \u0628\u06d5\u0648\u0644\u0627 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646 - \u0647\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u0646\u06d5\u0628\u06ce - \u0628\u06ce\u06b5\u0646 \u0628\u06cc\u06a9\u06d5\u0646\u061b \u0626\u06ce\u0648\u06d5 \u06af\u0648\u0646\u0627\u062d\u062a\u0627\u0646 \u0646\u0627\u06af\u0627\u062a\u06ce\u061b \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u06cc\u0634\u062a\u0627\u0646 \u0646\u0627\u06af\u0627\u062a\u06ce \u06a9\u06d5 \u062e\u0648\u0627\u0633\u062a\u0646\u06cc \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0634\u0648\u0648 \u0645\u0631\u062f\u0648\u0648\u062a\u0627\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u0628\u06ce \u0648 \u0646\u0627\u0695\u0627\u0633\u062a\u06d5\u0648\u062e\u06c6 \u067e\u06ce\u06cc\u0627\u0646 \u0628\u06ce\u0698\u0646\u060c \u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u062f\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646\u062f\u0627 \u0628\u0695\u06cc\u0627\u0631 \u0628\u062f\u06d5\u0646 \u062f\u06d5\u06cc\u0627\u0646\u062e\u0648\u0627\u0632\u0646 \u062e\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06d5 \u0628\u0627\u0633\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u062f\u0631\u06a9\u06ce\u0646\u0646. \u0628\u06d5\u06b5\u0627\u0645 \u0646\u06d5\u06a9\u06d5\u0646 \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0628\u0686\u0646\u06d5 \u0698\u0648\u0648\u0627\u0646\u06ce\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5\u0648\u06ce\u0634 \u062e\u0648\u0627\u067e\u06d5\u0633\u0646\u062f\u0627\u0646\u06d5 \u0628\u06cc\u0627\u0646 \u062f\u0648\u06ce\u0646\u0646. \u062a\u0627 \u0645\u0627\u0648\u06d5\u06a9\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u0646\u06d5\u0686\u06ce \u0628\u0627\u0633\u06cc \u0645\u0627\u0631\u06d5 \u06a9\u0631\u062f\u0646 \u0645\u06d5\u06a9\u06d5\u0646. \u062f\u06d5\u0628\u06ce \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0634 \u0628\u0632\u0627\u0646\u0646 \u062e\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0626\u06ce\u0648\u06d5 \u0686\u06cc\u062a\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5\u061b \u0644\u06ce\u06cc \u0628\u062a\u0631\u0633\u0646 \u0648 \u0628\u06cc\u0634\u06cc\u0632\u0627\u0646\u0646 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u0644\u06d5\u0633\u06d5\u0631\u062e\u06c6\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0698\u0646\u0648\u0648 \u062a\u06d5\u06b5\u0627\u0642 \u062f\u06d5\u062f\u06d5\u0646\u060c \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u0628\u0646\u0648\u0648\u0646 \u0648 \u0645\u0627\u0631\u06d5\u06cc\u06cc\u062a\u0627\u0646 \u0647\u06ce\u0634\u062a\u0627 \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u0646\u060c \u0686\u06cc\u06cc\u0627\u0646 \u0646\u0627\u06a9\u06d5\u0648\u06ce \u0648 \u0626\u06ce\u0648\u06d5 \u06af\u0648\u0646\u0627\u0647\u062a\u0627\u0646 \u0646\u0627\u06af\u0627\u062a\u06ce. \u0628\u06d5\u06b5\u0627\u0645 \u0648\u0627 \u0686\u0627\u06a9\u06d5 \u0634\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u062f\u06d5\u0646\u06ce\u061b \u062f\u0627\u0631\u0627 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u062e\u06c6\u06cc \u0648 \u0647\u06d5\u0698\u0627\u0631\u06cc\u0634 \u0628\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u062e\u06c6\u06cc\u061b \u0648\u0627 \u0686\u0627\u06a9\u06d5 \u0628\u06c6 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u0627\u0646.',
    '\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0627\u0631\u06d5\u06cc\u06cc\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u0628\u0695\u06cc\u0628\u06c6\u0648\u06d5 \u0648 \u0647\u06ce\u0634\u062a\u0627 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u0646\u06d5\u062e\u06d5\u0648\u062a\u0648\u0648\u0646\u060c \u062a\u06d5\u06b5\u0627\u0642\u062a\u0627\u0646 \u062f\u0627\u0646\u060c \u0646\u06cc\u0648\u06d5\u06cc \u0645\u0627\u0631\u06d5\u06cc\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u060c \u0645\u06d5\u06af\u06cc\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce\u0648 \u0628\u0628\u06d5\u062e\u0634\u0646 \u06cc\u0627\u0646 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u067e\u06ce\u0648 \u0628\u0628\u06d5\u062e\u0634\u06ce \u06a9\u06d5 \u0645\u0627\u0631\u06d5\u0628\u0695\u06cc\u0646 \u0628\u06d5 \u062f\u06d5\u0633\u062a \u0626\u06d5\u0648\u06d5\u061b \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc\u0634 \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0631\u06cc \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u06cc \u0646\u0632\u06cc\u06a9\u062a\u0631\u06d5. \u0644\u06d5 \u0628\u06cc\u0631 \u0645\u06d5\u06a9\u06d5\u0646 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u06cc\u06d5\u06a9\u062a\u0631 \u0628\u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06d5.',
    '\u0644\u06d5 \u0646\u0648\u06ce\u0698\u0627\u0646 \u0648 \u0628\u06d5\u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u0626\u06d5\u0648 \u0646\u0648\u06ce\u0698\u06d5\u06cc \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0646\u061b \u0626\u06d5\u0634\u06ce \u0628\u06c6 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0695\u0627\u0628\u0648\u0648\u06d5\u0633\u062a\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u0631\u0633\u06ce\u06a9\u0648\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0628\u0648\u0648 - \u0628\u06d5 \u067e\u06cc\u0627\u062f\u06d5\u06cc\u06cc \u06cc\u0627\u0646 \u0628\u06d5 \u0633\u0648\u0627\u0631\u06cc - \u0647\u06d5\u0631\u06af\u0627 \u062a\u0631\u0633\u06cc\u0634\u0648\u0648 \u0628\u06d5\u0633\u06d5\u0631 \u0686\u0648\u0648 - \u0686\u06c6\u0646\u06cc \u062e\u0648\u062f\u0627 \u0641\u06ce\u0631\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646 - \u06cc\u0627\u062f\u06cc \u0628\u06a9\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u0646\u06d5\u0648\u062f\u06d5\u0632\u0627\u0646\u06cc.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0646\u06af\u06c6 \u062f\u06d5\u0645\u0631\u0646 \u0648 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5\u062c\u06ce \u062f\u06ce\u06b5\u0646\u060c \u0695\u0627\u0628\u0633\u067e\u06ce\u0631\u0646: \u0647\u06d5\u062a\u0627 \u0633\u0627\u06b5\u06ce\u06a9 \u0645\u0627\u06cc\u06d5\u06cc \u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u062f\u0627\u0628\u06cc\u0646 \u0628\u06a9\u06d5\u0646 \u0648 \u062f\u06d5\u0631\u06cc \u0646\u06d5\u06a9\u06d5\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627\u0633\u062a\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0686\u0648\u0648\u0646\u06d5 \u062f\u06d5\u0631\u06ce - \u0628\u06c6 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u062e\u0648\u062f\u0627\u067e\u06d5\u0633\u0646\u062f - \u0626\u06ce\u0648\u06d5 \u06af\u0648\u0646\u0627\u0647\u062a\u0627\u0646 \u0646\u0627\u06af\u0627\u062a\u06ce. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0698\u0646\u0627\u0646\u06ce \u06a9\u06d5 \u062a\u06d5\u06b5\u0627\u0642 \u0626\u06d5\u062f\u0631\u06ce\u0646\u060c \u0628\u06d5\u0634\u06cc \u0695\u06d5\u0648\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061b \u06a9\u06d5 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627\u0646\u06cc \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u060c \u0628\u0695\u06cc\u0627\u0631\u06d5 \u0628\u06c6 \u062e\u0648\u062f\u0627\u062a\u0631\u0633\u0627\u0646.',
    '\u0647\u06d5\u0631 \u0628\u06d5\u0648 \u067e\u06ce\u06cc\u06d5\u0634 \u062e\u0648\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0695\u0648\u0648\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0626\u06ce\u0648\u06d5 \u062a\u06ce\u0628\u06af\u06d5\u0646.',
    '\u0626\u06d5\u062f\u06cc \u062a\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062a \u0646\u06d5\u062f\u06cc \u0628\u06d5 \u0647\u06d5\u0632\u0627\u0631\u0627\u0646 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u0631\u0633\u06cc \u0645\u0631\u062f\u0646 \u0648\u0627\u06cc \u0644\u06ce\u06a9\u0631\u062f\u0646 \u0632\u06ce\u062f\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5\u062c\u06ce \u0628\u06ce\u06b5\u0646\u061f \u062e\u0648\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce\u062f\u0627\u0646: \u0628\u0645\u0631\u0646! \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc \u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06a9\u0627\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0644\u06d5 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627 \u0648 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u062e\u0648\u0627 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u06a9\u06ce \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u0648\u0627\u0648 \u062f\u06d5\u062f\u0627 \u0628\u06d5 \u062e\u0648\u0627\u060c \u0647\u06d5\u062a\u0627 \u0626\u06d5\u0648\u06cc\u0634 \u0686\u06d5\u0646\u062f \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc \u0628\u062f\u0627\u062a\u06d5\u0648\u06d5\u061f \u06a9\u06d5\u0645 \u0628\u0698\u06cc\u0648\u06cc \u0648 \u0641\u06d5\u0631\u062d\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0633 \u0626\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0645\u0648\u0648\u0634 \u0647\u06d5\u0631 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648.',
    '\u0646\u06d5\u062a \u0632\u0627\u0646\u06cc\u0648\u06d5 \u0686\u06d5\u0646\u062f \u06a9\u06d5\u0633 \u0644\u06d5 \u0646\u06d5\u062c\u06cc\u0645 \u0632\u0627\u062f\u06d5\u06a9\u0627\u0646 - \u06a9\u06d5 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644 \u0628\u0648\u0648\u0646 - \u0644\u06d5 \u067e\u0627\u0634 \u0645\u0648\u0648\u0633\u0627\u060c \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648\u062a: \u0634\u0627\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u0628\u06a9\u06d5 \u062a\u0627 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u0627 \u062e\u06d5\u0632\u0627 \u0628\u06a9\u06d5\u06cc\u0646. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06d5\u0632\u0627\u0648 \u0628\u06c6 \u062f\u06d5\u0631\u0686\u06cc\u060c \u067e\u06ce\u0648 \u0648\u0627\u0646\u06cc\u06cc\u06d5 \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646\u061f \u06af\u0648\u062a\u06cc\u0627\u0646: \u062c\u0627 \u0686\u06c6\u0646 \u0644\u06d5 \u062e\u06d5\u0632\u0627\u06cc\u06d5- \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u0627 \u0628\u06ce- \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u06cc\u0646\u060c \u06a9\u06d5 \u062f\u06d5\u0634\u0628\u06cc\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0632\u06ce\u062f\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0648\u06d5\u062f\u06d5\u0631\u0646\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u0639\u06d5\u0648\u0644\u0627\u062f\u06cc \u062e\u06c6\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u0648\u0648\u0631 \u062e\u0633\u062a\u0648\u0648\u06cc\u0646\u06d5\u0648\u06d5\u061f \u06a9\u06d5 \u0628\u0695\u06cc\u0627\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a \u0628\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627\u060c \u067e\u0634\u062a\u06cc\u0627\u0646 \u062a\u06ce\u06a9\u0631\u062f \u0648 \u0695\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0645\u06ce\u06a9\u06cc\u0627\u0646 \u0645\u0627\u0628\u0648\u0648\u0646\u06d5\u0648\u06d5. \u062e\u0648\u0627 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u0628\u0627\u0634 \u062f\u06d5\u0646\u0627\u0633\u06ce.',
    '\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0627\u0646 \u067e\u06ce\u0627\u0646\u06cc \u0648\u062a: \u062e\u0648\u062f\u0627 \u0628\u06c6 \u067e\u0627\u0634\u0627\u06cc\u06d5\u062a\u06cc\u062a\u0627\u0646 \u062a\u0627\u0644\u0648\u0648\u062a\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5. \u06af\u0648\u062a\u06cc\u0627\u0646: \u06a9\u0648\u0627 \u0626\u06d5\u0648 \u0634\u0627\u06cc\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5\u06cc\u06d5\u061f \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0632\u06c6\u0631 \u0644\u06d5\u0648 \u0634\u06cc\u0627\u0648\u062a\u0631 \u0648 \u0628\u06d5 \u062f\u0627\u0631\u0627\u06cc\u06cc\u0634 \u0632\u06ce\u062f\u06d5\u062a\u0631\u06cc\u0646. \u06af\u0648\u062a\u06cc: \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u06a9\u0631\u062f\u0648\u0648\u0646\u06d5\u062a\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u062f\u06d5\u061b \u0632\u0627\u0646\u0633\u062a \u0648 \u0642\u06d5\u06b5\u0627\u0641\u06d5\u062a\u06cc\u0634\u06cc \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u067e\u062a\u0631 \u067e\u06ce\u062f\u0627\u0648\u06d5. \u062e\u0648\u0627 \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u067e\u0627\u0634\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u062f\u0627 \u062f\u06d5\u06cc\u062f\u0627\u061b \u0632\u0627\u0646\u0633\u062a\u06cc \u062e\u0648\u0627 \u0628\u06d5\u0631\u0628\u06b5\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0648\u062a\u06cc: \u062f\u0631\u06c6\u0634\u0645\u06cc \u067e\u0627\u0634\u0627\u06cc\u06d5\u062a\u06cc \u0626\u06d5\u0648 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648\u0695\u0627\u060c \u062a\u0627\u0628\u0648\u0648\u062a\u06ce\u06a9\u06d5 \u062f\u06b5\u0646\u06cc\u0627 \u0628\u0648\u0648\u0646\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5\u061b \u062f\u06d5\u06af\u06d5\u06b5 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u06a9\u06d5\u0644\u06d5\u067e\u06c6\u0695\u06cc \u062e\u0627\u0646\u06d5\u062f\u0627\u0646\u06cc \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u0627\u0631\u0648\u0648\u0646\u060c \u0641\u0631\u06cc\u0634\u062a\u06d5 \u0647\u06d5\u06b5\u06cc\u0627\u0646 \u06af\u0631\u062a\u0648\u0648\u06d5. \u0626\u06d5\u0645\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u0648 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0648\u06d5\u062e\u062a\u06ce \u062a\u0627\u0644\u0648\u0648\u062a \u0644\u06d5\u0634\u06a9\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u06ce\u060c \u06af\u0648\u062a\u06cc: \u062e\u0648\u062f\u0627 \u0628\u06d5 \u062c\u06c6\u0628\u0627\u0631\u06ce\u06a9 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5 \u062a\u0627\u0642\u06cc\u0648 \u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0644\u06ce\u06cc \u0628\u062e\u0648\u0627\u062a\u06d5\u0648\u06d5 \u0644\u06d5 \u0645\u0646 \u0646\u06cc\u06cc\u06d5\u061b \u0647\u06d5\u0631\u06a9\u06ce\u0634 \u0686\u06cc \u0644\u06ce \u0646\u06d5\u062e\u0648\u0627\u062a\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u0628\u06d5 \u0645\u0646\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0633\u06ce \u0647\u06d5\u0631 \u0628\u06d5 \u0644\u0627\u0648\u06ce\u0686 \u0644\u06ce\u06cc \u0647\u06d5\u06b5\u06ce\u0646\u062c\u06ce. \u06a9\u06d5\u0645\u06ce\u06a9 \u0646\u06d5\u0628\u06ce \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0644\u06ce\u06cc\u0627\u0646 \u062e\u0648\u0627\u0631\u062f\u06d5\u0648\u06d5. \u0626\u06d5\u0648\u0633\u0627 \u062e\u06c6\u06cc \u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06cc \u06cc\u0627\u0631\u0627\u0646\u06cc \u0644\u06d5 \u062c\u06c6\u06cc\u06d5\u06a9\u06d5 \u067e\u06d5\u0695\u06cc\u0646\u06d5\u0648\u06d5. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u0695\u06c6 \u0644\u06d5 \u0648\u0632\u06d5\u06cc \u0626\u06ce\u0645\u06d5\u062f\u0627 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u062f\u06d5\u0631\u0648\u06d5\u0633\u062a\u06cc \u062c\u0627\u0644\u0648\u0648\u062a \u0648 \u0644\u06d5\u0634\u06a9\u0631\u06d5\u06a9\u06d5\u06cc \u0628\u06ce\u06cc\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0647\u06cc\u0648\u0627 \u0628\u0648\u0648\u0646 \u0628\u06d5 \u062f\u06cc\u062f\u0627\u0631\u06cc \u062e\u0648\u062f\u0627 \u0628\u06af\u06d5\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u062c\u0627\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u06a9\u06d5\u0645 - \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u0627 - \u0628\u06d5 \u0633\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u0632\u06c6\u0631\u062f\u0627 \u0648\u06d5\u0633\u06d5\u0631 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646. \u062e\u0648\u0627 \u06cc\u0627\u0631\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631\u0627\u0646\u06d5.',
    '\u0644\u06d5 \u06a9\u0627\u062a\u06ce\u06a9\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u062c\u0627\u0644\u0648\u0648\u062a \u0648 \u0644\u06d5\u0634\u06a9\u0631\u06cc \u062a\u0648\u0648\u0634\u06cc \u06cc\u06d5\u06a9 \u0628\u0648\u0648\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0648\u0627\u0628\u06a9\u06d5 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06cc\u0646 \u0648 \u0644\u06d5 \u0645\u06d5\u06cc\u062f\u0627\u0646\u06ce\u062f\u0627 \u0628\u0686\u06d5\u0642\u06cc\u0646 \u0648 \u0644\u06d5 \u06a9\u06d5\u0633\u0627\u0646\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0648\u06d5\u0633\u06d5\u0631\u0645\u0627\u0646 \u0628\u062e\u06d5.',
    '\u0626\u06cc\u062a\u0631 - \u0645\u06d5\u06cc\u0644\u06cc \u062e\u0648\u062f\u0627\u06cc \u0644\u06d5 \u0633\u06d5\u0631 - \u062f\u0648\u0698\u0645\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0695\u06d5\u062a\u0627\u0646\u062f \u0648 \u062c\u0627\u0644\u0648\u0648\u062a \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062f\u0627\u0648\u0648\u062f \u0686\u0648\u0648. \u062e\u0648\u062f\u0627 \u067e\u0627\u0634\u0627\u06cc\u06d5\u062a\u06cc \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u067e\u06ce\u062f\u0627 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0628\u06c6 \u062e\u06c6\u06cc \u067e\u06ce\u0648\u06cc\u0633\u062a\u06cc \u067e\u06ce \u0628\u0648\u0648\u060c \u0641\u06ce\u0631\u06cc \u06a9\u0631\u062f. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u06cc \u0644\u06d5\u0648 \u0626\u0627\u067e\u06c6\u0631\u06d5 \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u062a\u06d5\u0631\u062a \u0648 \u062a\u0648\u0648\u0646\u0627 \u06a9\u0631\u062f\u0627\u06cc\u06d5\u060c \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u062e\u0631\u0627 \u0628\u0628\u0648\u0648\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646 \u06a9\u06d5 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0647\u06d5\u0631 \u0628\u06c6 \u062a\u06c6\u06cc \u062f\u06d5\u062e\u0648\u06ce\u0646\u06cc\u0646\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u06cc\u06d5\u06a9\u06ce\u06a9 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646\u062f\u0627 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u0628\u06d5\u0695\u06ce\u0632\u062a\u0631 \u06af\u0631\u062a\u061b \u062c\u0627 \u0644\u06d5\u0648\u0627\u0646\u062f\u0627 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u0628\u0648\u0648\u06d5 \u06a9\u06d5 \u062e\u0648\u0627 \u0626\u06d5\u0648\u06cc \u062f\u0648\u0627\u0646\u062f\u0648\u0648\u06d5\u061b \u067e\u0644\u06d5\u0648\u067e\u0627\u06cc\u06d5\u06cc \u0628\u0695\u06ce\u06a9\u06cc\u0634\u06cc \u0628\u0631\u062f\u06c6\u062a\u06d5 \u0633\u06d5\u0631. \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u0646 \u062f\u0627 \u0628\u06d5 \u0639\u06cc\u0633\u0627\u06cc \u06a9\u0648\u0695\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645\u061b \u0628\u06d5 \u06af\u06cc\u0627\u0646\u06cc \u067e\u06cc\u0631\u06c6\u0632\u06cc \u062e\u06c6\u0634\u0645\u0627\u0646 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0646 \u062f\u0627. \u0626\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u0627\u0646\u06d5 \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0645\u06d5\u06cc \u0626\u06d5\u0648 \u06af\u0634 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u0647\u0627\u062a\u0646\u060c \u0633\u06d5\u0631\u06d5\u0695\u0627\u06cc \u0626\u06d5\u0648 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06d5\u06cc \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0647\u0627\u062a - \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627 - \u0634\u06d5\u0695\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u062f\u0627 \u067e\u06ce\u06a9 \u0646\u06d5\u0647\u0627\u062a\u0646\u061b \u0628\u0627\u0632\u06ce\u06a9 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u060c \u0628\u0627\u0632\u06ce\u06a9\u06cc \u062a\u0631 \u06a9\u06d5\u0648\u062a\u0646\u06d5 \u0633\u06d5\u0631 \u0628\u0627\u0631 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627\u060c \u0646\u06d5\u06cc\u062f\u06d5\u0647\u06ce\u0634\u062a \u062f\u06d5\u06af\u0698 \u06cc\u06d5\u06a9 \u0695\u0627\u0686\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0628\u06d5 \u062e\u0648\u0627\u0633\u062a\u06cc \u062e\u06c6\u06cc \u06a9\u0627\u0631\u0627\u0646 \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u0645 \u062f\u0627\u0648\u0646 \u0628\u06d5\u062e\u062a \u0628\u06a9\u06d5\u0646\u060c \u067e\u06ce\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0695\u06c6\u0698\u06d5\u06a9\u06d5 \u062f\u0627\u0628\u06ce\u061b \u06a9\u06d5 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0646\u06d5 \u0633\u06d5\u0648\u062f\u0627 \u06a9\u0631\u062f\u0646 \u062f\u06d5\u0645\u06ce\u0646\u06ce \u0648 \u0646\u06d5 \u062a\u06a9\u0627 \u0648 \u0646\u06d5 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u061b \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce. \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648\u06d5 \u0648 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0695\u0627\u062f\u06d5\u06af\u0627. \u0646\u06d5 \u062e\u06d5\u0648\u0646\u0648\u0648\u0686\u06a9\u06d5 \u062f\u06d5\u06cc\u0628\u0627\u062a\u06d5\u0648\u06d5 \u0646\u06d5 \u0695\u0627\u062f\u06d5\u0632\u06ce. \u0647\u06d5\u0631\u0686\u06cc \u0647\u06d5\u0628\u06ce \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5. \u06a9\u06ce\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0647\u06d5\u062a\u0627 - \u0628\u06c6 \u062e\u06c6\u06cc \u0626\u06cc\u0632\u0646\u06cc \u0646\u06d5\u062f\u0627 - \u0628\u062a\u0648\u0627\u0646\u06ce \u062a\u06a9\u0627\u06cc \u0644\u0627 \u0628\u06a9\u0627\u061f \u0644\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0633\u062a\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u062f\u06cc\u0627\u0631\u06d5\u060c \u0644\u06d5\u0648\u06d5\u0634 \u067e\u06ce\u0634 \u0626\u06d5\u0645\u0627\u0646\u06cc\u0634 \u0628\u0648\u0648\u06af\u06d5 \u0648 \u0628\u06d5\u0631\u067e\u06ce\u0648\u0627\u0631\u06d5\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5. \u0646\u0627\u0634\u062a\u0648\u0627\u0646\u0646 \u0628\u06d5 \u0647\u06cc\u0686\u06ce\u06a9 \u0644\u06d5 \u0632\u0627\u0646\u0633\u062a\u06cc \u0695\u0627\u06af\u06d5\u0646\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce. \u0632\u0627\u0646\u0633\u062a\u06cc \u0626\u06d5\u0648\u060c \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u062f\u0627\u06af\u0631\u062a\u0648\u0648\u06d5 \u0648 \u067e\u0627\u0631\u0627\u0633\u062a\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u0626\u06d5\u0631\u06a9\u06ce \u06af\u0631\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u0647\u06d5\u0631\u06d5 \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632 \u0648 \u0645\u06d5\u0632\u0646\u060c \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0644\u06d5\u0645 \u062f\u06cc\u0646\u06d5\u062f\u0627 \u0628\u06d5 \u0632\u06c6\u0631 \u062f\u0627\u0633\u06d5\u067e\u0627\u0646\u062f\u0646 \u0646\u06cc\u06cc\u06d5. \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc \u0644\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u0647\u0627\u0648\u06ce\u0631 \u06a9\u0631\u0627. \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5 \u062a\u0627\u063a\u0648\u0648\u062a \u0646\u06d5\u0628\u0627\u0646\u06d5 \u0648 \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0647\u06ce\u0646\u06ce\u060c \u0647\u06d5\u0646\u06af\u06b5\u06ce\u06a9\u06cc \u0647\u06ce\u0646\u062f\u06d5 \u062a\u0648\u0646\u062f\u06cc \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u067e\u0633\u0627\u0646\u06cc \u0628\u06c6 \u0646\u06cc\u06cc\u06d5. \u062e\u0648\u062f\u0627\u0634 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u062e\u0648\u062f\u0627 \u06cc\u0627\u0631\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06d5 \u0648 \u0644\u06d5 \u0646\u0627\u0648 \u062a\u0627\u0631\u06cc\u06a9\u0633\u062a\u0627\u0646\u06d5\u0648\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u0628\u0627 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc. \u062f\u06c6\u0633\u062a\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u062a\u0627\u063a\u0648\u0648\u062a\u06d5\u061b \u0644\u06d5 \u0695\u06c6\u0634\u0646\u06cc \u062f\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06d5\u062e\u06d5\u0646\u061b \u0628\u06d5\u0631\u06d5\u0648 \u0695\u0648\u0648\u06cc \u062a\u0627\u0631\u06cc\u06a9\u0633\u062a\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u06d5\u0646. \u062f\u06d5\u0628\u06ce \u0628\u0686\u0646\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u062f\u06d5\u0648\u06ce\u062f\u0627 \u0628\u0646.',
    '\u062a\u06c6 \u0626\u06d5\u0648 \u06a9\u0627\u0628\u0631\u0627\u06cc\u06d5\u062a \u0646\u06d5\u062f\u06cc\u0628\u0648\u0648 \u06a9\u06d5 \u0686\u06c6\u0646 \u062e\u0648\u062f\u0627 \u0647\u06ce\u0632 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u067e\u06ce \u062f\u0627\u060c \u062c\u0648\u0627\u0628\u06d5\u062c\u06d5\u0646\u06af\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645\u062f\u0627 \u062f\u06d5\u06a9\u0631\u062f\u060c \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u061f \u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u06af\u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u0698\u06cc\u0646\u06ce \u062f\u06d5\u062f\u0627 \u0648 \u062f\u06d5\u0634\u0645\u0631\u06ce\u0646\u06ce. \u06af\u0648\u062a\u06cc: \u0645\u0646\u06cc\u0634 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06ce\u06b5\u0645\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0645\u0631\u06ce\u0646\u0645. \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0648\u0627\u062a: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u062e\u06c6\u0631 \u0644\u06d5 \u062e\u06c6\u0631\u0647\u06d5\u06b5\u0627\u062a \u0695\u0627\u062f\u06ce\u0646\u06cc\u060c [\u062f\u0627\u062f\u06d5\u06cc] \u0626\u06d5\u062a\u06c6 \u0644\u06d5 \u062e\u06c6\u0631\u0646\u0634\u06cc\u0646\u06d5\u0648\u06d5\u06cc \u0628\u06ce\u0646\u06d5. \u06a9\u0627\u0648\u0631\u0627\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u062d\u06d5\u067e\u06d5\u0633\u0627. \u062e\u0648\u0627 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0646\u0627\u06a9\u0627.',
    '\u06cc\u0627\u0646 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0628\u06d5 \u0644\u0627\u06cc \u06af\u0648\u0646\u062f\u06ce\u06a9\u0627 \u0695\u0627\u062f\u06d5\u0628\u0631\u062f \u06a9\u06d5 \u0628\u06d5 \u0633\u06d5\u0631\u06cc\u06d5\u06a9\u062f\u0627 \u062a\u06d5\u067e\u06cc\u0628\u0648\u0648. \u06af\u0648\u062a\u06cc: \u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0626\u06d5\u0645\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u067e\u0627\u0634 \u0645\u0631\u062f\u0646\u06ce\u061f \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u0628\u06c6 \u0645\u0627\u0648\u06d5\u06cc \u0633\u06d5\u062f \u0633\u0627\u06b5 \u0645\u0631\u0627\u0646\u062f\u06cc \u0648 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5. \u0644\u06ce\u06cc \u067e\u0631\u0633\u06cc: \u062a\u06c6 \u0686\u06d5\u0646\u062f\u062a \u067e\u06ce \u0686\u0648\u0648\u061f \u06af\u0648\u062a\u06cc: \u0695\u06c6\u0698\u06ce\u06a9 \u06cc\u0627 \u062e\u06c6 \u06a9\u06d5\u0645\u062a\u0631\u06cc\u0634 \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u06a9. \u06af\u0648\u062a\u06cc: \u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06d5\u0648\u0627 \u062a\u06c6 \u0633\u06d5\u062f \u0633\u0627\u06b5\u06d5 \u0645\u0631\u062f\u0648\u0648\u06cc\u061b \u062f\u06d5\u0628\u0695\u0648\u0627\u0646\u06d5 \u062e\u0648\u0627\u0631\u062f\u06d5\u0645\u06d5\u0646\u06cc \u0648 \u062e\u0648\u0627\u0631\u062f\u0646\u06d5\u0648\u06d5\u062a \u0646\u06d5\u06af\u06c6\u0695\u0627\u0648\u06d5\u061b \u062a\u0645\u0627\u0634\u0627\u06cc \u06a9\u06d5\u0631\u06d5\u06a9\u06d5\u0634\u062a \u06a9\u06d5! \u06af\u06d5\u0631\u06d5\u06a9\u0645\u0627\u0646\u06d5 \u0628\u062a\u06a9\u06d5\u06cc\u0646\u06d5 \u067e\u06d5\u0646\u062f\u06ce\u06a9 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645. \u0644\u06d5\u0645 \u0626\u06ce\u0633\u0642\u0627\u0646\u0627\u0646\u06d5 \u0628\u0695\u0648\u0627\u0646\u06d5 \u0686\u0644\u06c6\u0646 \u062a\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0646\u0627\u0648 \u06af\u06c6\u0634\u062a\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u06ce\u0634\u06cc\u0646. \u06a9\u0627\u062a\u06ce \u0626\u06d5\u0645\u06d5\u06cc \u0628\u06c6 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u060c \u06af\u0648\u062a\u06cc: \u0648\u0627 \u0626\u06ce\u0633\u062a\u0627 \u062f\u06d5\u0632\u0627\u0646\u0645 \u062e\u0648\u0627 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06cc \u067e\u06ce\u062f\u06d5\u06a9\u0631\u06ce.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u06a9\u06d5 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u06af\u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0626\u06d5\u062a\u06c6 \u0645\u0631\u06cc\u0627\u0646 \u0686\u0644\u06c6\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u06d5\u06cc\u062a\u06d5\u0648\u06d5\u061f \u0646\u06cc\u0634\u0627\u0646\u0645 \u062f\u06d5. \u06af\u0648\u062a\u06cc: \u0626\u0627\u062e\u06c6 \u062a\u06c6 \u0647\u06ce\u0634\u062a\u0627 \u0628\u0627\u0648\u06d5\u0695\u062a \u0646\u06cc\u06cc\u06d5\u061f! \u06af\u0648\u062a\u06cc: \u0628\u06d5\u06b5\u06ce\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0645\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u0645\u06d5 \u0626\u06cc\u062a\u0631 \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u062f\u06b5\u0646\u06cc\u0627 \u0628\u0645. \u06af\u0648\u062a\u06cc: \u062f\u06d5\u0633\u0627 \u0686\u0648\u0627\u0631 \u0645\u06d5\u0644 \u0628\u06af\u0631\u06d5 \u0648 \u067e\u06d5\u0644 \u067e\u06d5\u0644\u06cc\u0627\u0646 \u06a9\u06d5 \u0648 \u0628\u06cc\u0627\u0646\u06a9\u06d5 \u0686\u0648\u0627\u0631 \u06a9\u0648\u062a\u061b \u0647\u06d5\u0631 \u06a9\u0648\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631 \u06a9\u06d5\u0698\u06ce\u06a9\u06d5\u0648\u06d5 \u062f\u0627\u0646\u06ce \u0648 \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u060c \u0628\u06d5 \u0641\u0695\u06a9\u06d5 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u062a. \u0628\u0634\u0632\u0627\u0646\u06d5 \u062e\u0648\u0627 \u0686\u06d5\u0646\u062f\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0645\u0627\u06b5\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u0627\u0647\u06cc \u062e\u0648\u062f\u0627\u062f\u0627 \u0628\u06d5\u062e\u062a \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0648\u06d5\u06a9 \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u06cc \u062f\u06d5\u0646\u06a9\u06ce\u06a9\u06d5 \u062d\u06d5\u0648\u062a \u06af\u0648\u06b5 \u062f\u06d5\u062f\u0627 \u0648 \u0647\u06d5\u0631 \u06af\u0648\u06b5\u06d5\u06cc \u0633\u06d5\u062f \u062f\u0627\u0646\u06d5\u06cc \u062a\u06ce\u062f\u0627. \u062e\u0648\u062f\u0627 \u0686\u06d5\u0646\u062f \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u067e\u0627\u062f\u0627\u0634 \u062f\u06d5\u062f\u0627 \u0628\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u06cc\u06cc\u06d5\u0648\u06ce. \u062e\u0648\u062f\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u06cc\u06d5\u06a9\u062c\u0627\u0631 \u0632\u06c6\u0631\u06d5 \u0648 \u0632\u06c6\u0631 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0645\u0627\u06b5 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u0627 \u062f\u06d5\u0628\u06d5\u062e\u0634\u0646 \u0648 \u0628\u06d5 \u0686\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0646\u0627\u062f\u06d5\u0646\u06d5\u0648\u06d5 \u0648 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u0627\u06a9\u06d5\u0646\u060c \u062e\u0648\u0627 \u062a\u06ce\u06cc \u0647\u06d5\u06b5\u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0646\u06d5 \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06d5 \u0648 \u0646\u06d5 \u062e\u06d5\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u062f\u0648\u0627\u0646\u062f\u0646\u06cc \u0646\u06d5\u0631\u0645\u0648\u0646\u06cc\u0627\u0646 \u0648 \u0686\u0627\u0648 \u0644\u06ce \u067e\u06c6\u0634\u06cc\u0646\u060c \u0644\u06d5\u0648 \u062e\u06ce\u0631 \u06a9\u0631\u062f\u0646\u06d5 \u0686\u0627\u06a9\u062a\u0631\u06d5 \u06a9\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5 \u0634\u0648\u06ce\u0646\u0627 \u0628\u06ce. \u062e\u0648\u062f\u0627 \u0626\u0627\u062a\u0627\u062c\u06cc \u06a9\u06d5\u0633 \u0646\u06cc\u06cc\u06d5 \u0648 [\u0644\u06d5 \u06a9\u0627\u0631\u062f\u0627] \u0628\u06d5 \u06a9\u0627\u0648\u06d5\u062e\u06c6\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u062e\u06ce\u0631\u06d5\u06a9\u0627\u0646\u0648\u0648 \u0628\u06d5 \u0645\u0646\u06d5\u062a \u0644\u06d5 \u0633\u06d5\u0631 \u06a9\u0631\u062f\u0646 \u0648 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u060c \u0628\u06d5\u0641\u06cc\u0695\u06c6 \u0645\u06d5\u062f\u06d5\u0646. \u0646\u06d5\u0686\u0646 \u0648\u06d5\u06a9 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0628\u0646 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0646\u06cc\u06cc\u06d5 \u0648 \u0628\u06c6 \u0695\u0648\u0648\u0628\u06cc\u0646\u06cc \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u062e\u0634\u0646\u061b \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06d5 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u062a\u0627\u062a\u06d5\u0628\u06d5\u0631\u062f\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0644\u0648\u0648\u0633 \u0647\u06ce\u0646\u062f\u06ce \u062e\u06c6\u06b5\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0628\u0627\u0631\u06d5 \u0648 \u0628\u0627\u0631\u0627\u0646\u06cc \u062a\u0648\u0646\u062f\u06cc \u0648\u06ce \u062f\u06d5\u06a9\u06d5\u0648\u06ce \u0648 \u0628\u06cc\u0634\u0648\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u062a\u0627\u062a\u06ce\u06a9\u06cc \u0695\u0648\u0648\u062a \u0648\u06d5\u0645\u06ce\u0646\u06ce. \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0648 \u0628\u06d5\u062e\u0634\u06cc\u0646\u06d5 \u062f\u06d5\u0633\u06af\u06cc\u0631 \u0646\u0627\u0628\u06ce \u0648 \u062e\u0648\u062f\u0627 \u06af\u0631\u06c6\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0646\u0627\u06a9\u0627.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646 \u06a9\u0631\u062f\u0646 \u062f\u0627\u060c \u0628\u06d5 \u0647\u0648\u0645\u06ce\u062f\u0646 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062e\u06c6 \u0695\u0627\u0632\u06cc \u06a9\u06d5\u0646 \u0648 \u062f\u06b5\u06cc\u0627\u0646 \u0628\u06d5\u0628\u0695\u0648\u0627 \u067e\u062a\u06d5\u0648 \u0628\u06ce\u060c \u062e\u06ce\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0648\u06d5\u06a9 \u0628\u0627\u063a\u06ce\u06a9\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u06af\u0631\u062f\u06ce\u06a9 \u0648 \u0628\u0627\u0631\u0627\u0646\u06cc \u067e\u06d5\u06b5\u06d5\u06cc \u0644\u06ce \u0628\u062f\u0627 \u0648 \u0628\u06d5\u0631\u0648\u0628\u0648\u0648\u06cc \u062f\u0648\u0648 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0628\u06ce\u061b \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u06b5\u06d5\u0634\u06cc \u0644\u06ce \u0646\u06d5\u062f\u0627\u060c \u0646\u0645\u06d5 \u0628\u0627\u0631\u0627\u0646 \u062a\u06ce\u0631\u0627\u0648\u06cc \u06a9\u0627. \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u06a9\u06ce\u0648 \u062d\u06d5\u0632 \u062f\u06d5\u06a9\u0627 \u0628\u0628\u06ce\u062a\u06d5 \u062e\u0627\u0648\u06d5\u0646\u06cc \u0628\u0627\u063a\u06ce\u06a9 \u0644\u06d5 \u06af\u0634 \u062c\u06c6\u0631\u06d5 \u062e\u0648\u0631\u0645\u0627 \u0648 \u0695\u06d5\u0632\u06ce\u06a9\u060c \u0634\u06d5\u0642\u06d5\u062c\u06c6\u06cc \u0628\u06d5 \u0646\u0627\u0648\u062f\u0627 \u0628\u0695\u0648\u0627 \u0648 \u0647\u06d5\u0645\u0648\u0648\u0634\u06cc\u0627\u0646 \u0647\u0627\u062a\u0628\u0646\u06d5 \u0628\u06d5\u0631\u0648\u067e\u06cc\u0631\u06cc\u0634 \u0628\u0695\u0633\u062a\u06cc \u0628\u0695\u06cc\u0628\u06ce \u0648 \u0632\u0627\u0631\u06c6\u06cc \u0633\u06d5\u0631\u0648\u067e\u06ce\u0686\u06a9\u06d5\u06cc \u0647\u06d5\u0628\u0646\u061b \u0644\u06d5 \u0646\u0627\u062e\u0627\u0641\u06b5 \u06af\u0695\u06d5\u0628\u0627\u06cc\u06d5\u06a9\u06cc \u0644\u06ce \u0647\u06d5\u06b5\u06a9\u0627 \u0648 \u0628\u06cc\u0633\u0648\u0648\u062a\u06ce\u0646\u06ce\u061f \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0646\u06cc\u0634\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u062f\u0627\u060c \u0634\u0627\u06cc\u06d5\u062a \u0628\u06cc\u0631\u06cc \u0644\u06ce \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0648\u0646 \u0648 \u067e\u06d5\u06cc\u062f\u0627\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06d5\u062e\u062a \u0628\u06a9\u06d5\u0646\u061b \u0646\u06d5\u06a9 \u0644\u06d5 \u0634\u062a\u06ce \u0647\u06ce\u0646\u062f\u06d5 \u0686\u0631\u0648\u0648\u06a9 \u06a9\u06d5 \u0628\u06c6\u062e\u06c6\u0634\u062a\u0627\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u0648\u067e\u06c6\u0634\u06cc \u0644\u06ce \u0646\u06d5\u06a9\u06d5\u0646\u060c \u0646\u0627\u062a\u0627\u0646\u06d5\u0648\u06ce. \u0628\u06cc\u0634\u06cc\u0632\u0627\u0646\u0646 \u062e\u0648\u062f\u0627 \u0626\u0627\u062a\u0627\u062c\u06cc \u06a9\u06d5\u0633 \u0646\u06cc\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u062a\u0627\u0631\u06cc\u0641\u06cc \u0628\u06a9\u0631\u06ce.',
    '\u0634\u06d5\u06cc\u062a\u0627\u0646 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0646\u06d5\u062f\u0627\u0631\u06cc \u062f\u06d5\u062a\u0631\u0633\u06ce\u0646\u06ce \u0648 \u0628\u06c6 \u062e\u0631\u0627\u067e\u06d5 \u0647\u0627\u0646\u0648\u0648 \u062f\u06d5\u062f\u0627. \u062e\u0648\u0627\u0634 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0644\u06ce \u062e\u06c6\u0634 \u0628\u0648\u0648\u0646 \u0648 \u062e\u06c6\u0634\u06cc \u0698\u06cc\u0627\u0646\u0648\u0648 \u067e\u06ce \u062f\u06d5\u062f\u0627. \u0641\u06d5\u0631\u062d\u0627\u0646\u06cc \u0648\u0627 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u062e\u06c6\u06cc \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u062f\u06d5\u062f\u0627 \u0628\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u06cc\u06cc\u06d5\u0648\u06ce. \u0628\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0628\u062f\u0631\u06ce\u060c \u0626\u06d5\u0648\u06d5 \u062e\u06ce\u0631\u06cc \u0632\u06c6\u0631\u06cc \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5. \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u0634 \u0628\u06d5\u0647\u0631\u06d5 \u0646\u0627\u062f\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06c6 \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632\u0627\u0646.',
    '\u0647\u06d5\u0631\u0645\u0627\u06b5\u06ce\u06a9\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u06d5 \u062e\u06ce\u0631\u060c \u06cc\u0627\u0646 \u062f\u06d5\u0642\u06d5\u0628\u06d5\u06b5 \u062e\u0648\u062f\u0627\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 \u0647\u06cc\u0686 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062f\u06d5\u0631\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u06af\u06d5\u0631 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062e\u06ce\u0631 \u0628\u06a9\u06d5\u0646 \u06a9\u0627\u0631\u06ce \u062e\u0627\u0633\u06d5\u061b \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc\u0634 \u0628\u06ce \u0648 \u062f\u06d5\u0633\u062a\u06cc \u0646\u06d5\u062f\u0627\u0631\u06cc \u067e\u06ce \u0628\u06af\u0631\u0646\u060c \u0628\u0627\u0634\u062a\u0631\u06cc\u0634\u06d5 \u0648 \u06af\u0648\u0646\u0627\u0647\u062a\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u0627\u062f\u06d5\u0628\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0631\u06a9\u06cc \u0633\u06d5\u0631\u0634\u0627\u0646\u06cc \u062a\u06c6 \u0646\u06cc\u06cc\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u062e\u06d5\u06cc\u06d5 \u0633\u06d5\u0631 \u0695\u06ce\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062e\u0648\u062f\u0627 \u0628\u06c6\u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc \u062f\u06d5\u06a9\u0627. \u0647\u06d5\u0631\u0686\u06cc \u0628\u06d5 \u062e\u06ce\u0631 \u062f\u06d5\u06cc\u0628\u06d5\u0634\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646\u06d5 \u0648 \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u0695\u06d5\u0632\u0627\u0645\u06d5\u0646\u062f\u06cc \u062e\u0648\u062f\u0627 \u062e\u06ce\u0631\u06ce \u062f\u06d5\u06a9\u06d5\u0646. \u0647\u06d5\u0631 \u0634\u062a\u06ce \u0628\u06d5 \u062e\u06ce\u0631\u06cc \u062f\u06d5\u062f\u06d5\u0646 \u062a\u06d5\u0648\u0627\u0648\u06cc \u067e\u06ce\u0648 \u062f\u06d5\u062f\u0631\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06cc\u0686 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0648 \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u062e\u06ce\u0631\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0634\u06ce \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u067e\u06d5\u0631\u06d5\u0633\u062a\u06cc\u062f\u0627 \u0628\u06ce \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646 \u0645\u0627\u0648\u0646\u06d5\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06ce\u0632\u06cc \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0648\u06b5\u06ce \u0628\u062f\u06d5\u0646\u061b \u0626\u06d5\u0648\u06cc \u0626\u0627\u06af\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0646\u06cc\u06cc\u06d5\u060c \u0648\u0627\u062f\u06d5\u0632\u0627\u0646\u0646 \u0647\u06cc\u0686 \u0626\u0627\u062a\u0627\u062c \u0646\u06cc\u0646\u061b \u0686\u0648\u0646\u06a9\u06d5 \u062d\u06d5\u06cc\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u06c6\u06cc\u06d5. \u0644\u06d5 \u0628\u06cc\u0686\u0645\u06cc\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0633\u0646\u06d5\u0648\u06d5\u061b \u062f\u06d5\u0646\u0627 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0648\u06d5\u06a9\u0648\u0648 \u067e\u0627\u0631\u0633\u06d5\u06a9\u06cc \u062f\u06cc\u06a9\u06d5 \u0646\u06cc\u0646 \u06af\u06ce\u0631\u06d5 \u0628\u06a9\u06d5\u0646. \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0628\u06d5 \u062e\u06ce\u0631 \u0644\u06d5\u0648 \u0628\u0627\u0631\u06d5 \u0628\u06d5\u062e\u062a \u06a9\u06d5\u0646\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0634\u06d5\u0648 \u06cc\u0627\u0646 \u0628\u06d5 \u0695\u06c6\u0698\u060c \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0648 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062e\u06ce\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06d5 \u0648 \u0646\u06d5 \u062e\u06d5\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0646\u0632\u0648\u0648\u0644 \u062f\u06d5\u062e\u06c6\u0646\u060c \u0695\u0627\u0633\u062a\u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627\u060c \u0648\u06d5\u06a9 \u0695\u0627\u067e\u06d5\u0695\u06cc\u0646\u06cc \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646 \u062f\u06d5\u0633\u062a\u06cc \u0644\u06ce \u0648\u06d5\u0634\u0627\u0646\u062f\u0628\u06ce\u060c \u0648\u06d5\u0647\u0627\u06cc\u06d5. \u0647\u06d5\u0631 \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0626\u06ce\u0698\u0646: \u0645\u0627\u0645\u06d5\u06b5\u06d5\u0634 \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0646\u0632\u0648\u0648\u0644\u06d5. \u062e\u0648\u062f\u0627 \u0633\u06d5\u0648\u062f\u0627\u06cc \u0695\u06d5\u0648\u0627 \u062f\u06cc\u0648\u06d5 \u0648 \u0646\u0632\u0648\u0648\u0644\u06cc \u067e\u06ce \u0646\u0627\u0695\u06d5\u0648\u0627\u06cc\u06d5. \u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06cc\u06d5 \u0648 \u062f\u06d5\u0633 \u0647\u06d5\u06b5 \u062f\u06d5\u06af\u0631\u06ce\u060c \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0645\u06d5\u0648\u0628\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06cc \u062f\u0627\u0648\u06d5\u062a\u06ce \u062f\u06d5\u0628\u06d5\u062e\u0634\u0631\u06ce \u0648 \u062e\u0648\u062f\u0627 \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0695\u0627\u062f\u06d5\u06af\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062f\u06cc\u0633\u0627\u0646 \u0628\u0686\u06ce\u062a\u06d5\u0648\u06d5 \u0633\u06d5\u0631\u06cc\u060c \u062f\u06d5\u0628\u0646 \u0628\u06d5 \u06cc\u0627\u0631\u06cc \u0626\u0627\u06af\u0631 \u0648 \u062a\u0627\u0647\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646.',
    '\u062e\u0648\u062f\u0627 \u067e\u06cc\u062a\u06cc \u0646\u0632\u0648\u0648\u0644 \u0647\u06d5\u0631 \u06a9\u06d5\u0645 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u067e\u06cc\u062a\u06cc \u062e\u06ce\u0631\u0627\u062a \u067e\u062a\u0631 \u062f\u06d5\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646 \u0648 \u06af\u0648\u0646\u0627\u0628\u0627\u0631\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u0646\u0648\u06ce\u0698 \u06a9\u0631\u062f\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u0627\u0646 \u0633\u0633\u062a\u06cc \u0646\u0627\u06a9\u06d5\u0646\u060c \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u0648\u0627 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0646\u06d5\u0647\u06cc\u0686 \u062a\u0631\u0633\u06cc\u0627\u0646 \u062f\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u06ce \u0648 \u0646\u06d5 \u062e\u06d5\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u0628\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u0695\u0627\u0633\u062a \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0646\u060c \u0626\u06d5\u0648 \u0633\u0648\u0648\u062a\u06cc \u0647\u06ce\u0634\u062a\u0627 \u0645\u0627\u0648\u062a\u0627\u0646\u06d5 \u0646\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06cc \u0644\u06ce \u0647\u06d5\u06b5\u0646\u06d5\u06af\u0631\u0646\u060c \u0633\u0627 \u0628\u0632\u0627\u0646\u0646 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc\u062f\u0627 \u0634\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06cc \u0644\u06ce \u0647\u06d5\u06b5\u06af\u0631\u0646\u060c \u062f\u06d5\u0633\u0645\u0627\u06cc\u06d5\u06cc \u062e\u06c6\u0648 \u0628\u0633\u062a\u06ce\u0646\u0646\u06d5\u0648\u06d5\u061b \u0626\u06cc\u062a\u0631 \u0626\u06d5\u0648\u0633\u0627 \u0646\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u0646\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u062a\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u0631\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0642\u06d5\u0631\u0632\u0627\u0631\u06cc\u0634 \u0646\u06d5\u062f\u0627\u0631 \u0628\u0648\u0648\u060c \u0645\u0627\u0648\u06d5\u06cc \u0628\u062f\u06d5\u0646 \u0647\u06d5\u062a\u0627 \u062f\u06d5\u06cc\u0628\u06ce. \u0626\u06d5\u0631 \u0628\u0634\u0632\u0627\u0646\u0646\u060c \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc \u0628\u06c6\u0648 \u0686\u0627\u062a\u0631\u06d5.',
    '\u062a\u0631\u0633\u06cc\u0634\u0648\u0648 \u0644\u06d5\u0648 \u0631\u06c6\u0698\u06d5 \u0647\u06d5\u0628\u06ce \u06a9\u06d5 \u062f\u06d5\u0648\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u061b \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0647\u06d5\u0631\u0686\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06cc - \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc- \u062f\u06ce\u062a\u06d5\u0648\u06d5 \u0695\u06ce\u06cc \u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u06a9\u06d5\u0633 \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0631\u06af\u0627 \u0628\u06c6 \u0645\u0627\u0648\u06d5\u06cc\u06ce \u062f\u06cc\u0627\u0631\u06cc\u060c \u0642\u06d5\u0631\u0632\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u06cc\u06d5\u06a9\u062a\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06cc\u0646\u0648\u0648\u0633\u0646 \u0648 \u0628\u0627 \u0646\u0648\u0648\u0633\u06d5\u0631\u06ce \u062e\u0648\u062f\u0627\u067e\u06d5\u0633\u0646\u062f \u0628\u06c6\u062a\u0627\u0646 \u0628\u062e\u0627\u062a\u06d5 \u0646\u0627\u0648 \u0642\u0627\u0642\u06d5\u0632. \u0646\u0627\u0634\u06ce \u0646\u0648\u0648\u0633\u06d5\u0631 \u0644\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u062a\u06d5\u0646\u06d5\u062e\u06cc \u0628\u06a9\u0627\u061b \u0628\u0627 \u0628\u0646\u0648\u0648\u0633\u06ce \u0648\u06d5\u06a9 \u062e\u0648\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce\u062f\u0627\u0648\u06d5. \u0647\u06d5\u0631\u0686\u06cc \u0642\u06d5\u0631\u0632\u062f\u0627\u0631 \u0628\u06c6\u06cc \u062f\u06d5\u06b5\u06ce\u060c \u0626\u06d5\u0648 \u0628\u06cc\u0646\u0648\u0648\u0633\u06ce \u0648 \u0626\u06d5\u0628\u06ce \u062a\u0631\u0633\u06cc \u062e\u0648\u062f\u0627\u06cc \u0647\u06d5\u0628\u06ce \u0648 \u0626\u06d5\u0648\u0644\u0627\u0648\u0626\u06d5\u0645\u0644\u0627\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06d5\u06a9\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0642\u06d5\u0631\u062f\u0627\u0631\u06d5\u060c \u06a9\u06d5\u0645 \u0626\u0627\u0648\u06d5\u0632\u06d5\u060c \u06cc\u0627\u0646 \u0628\u06ce \u0647\u06ce\u0632\u06d5\u060c \u06cc\u0627\u0646 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u0628\u0646\u0648\u0648\u0633\u0631\u06ce \u062f\u06d5\u0631\u06cc \u0628\u0628\u0695\u06ce\u060c \u0628\u0627 \u0633\u06d5\u0631\u067e\u06d5\u0631\u0633\u062a\u06cc \u0695\u06d5\u0648\u0627\u06cc \u0626\u06d5\u0648 \u0644\u06d5\u0628\u0627\u062a\u06cc \u0626\u06d5\u0648 \u0628\u06cc\u062f\u0631\u06a9\u06ce\u0646\u06ce \u0648 \u062f\u0648\u0648 \u067e\u06cc\u0627\u0648 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0634\u0627\u06cc\u06d5\u062a \u0628\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062f\u0648\u0648 \u067e\u06cc\u0627\u0648 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u0646\u060c \u0628\u0627 \u067e\u06cc\u0627\u0648\u06ce\u06a9 \u0648 \u062f\u0648\u0648 \u0698\u0646\u06cc \u0648\u0627 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u0634 \u0644\u06ce\u06cc\u0627\u0646 \u0695\u0627\u0632\u06cc \u0628\u0646\u060c \u0644\u06d5 \u06a9\u0627\u0631 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u06a9\u0631\u06ce\u0646\u061b \u062a\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5\u0645 \u062f\u0648\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u0628\u06cc\u0631\u06cc \u0686\u0648\u0648\u060c \u0626\u06d5\u0648\u06cc \u062a\u0631 \u0628\u06cc\u0631\u06cc \u062e\u0627\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06ce\u06a9\u06cc \u0628\u06c6 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u0627\u0646 \u0628\u0627\u0646 \u06a9\u0631\u0627\u0646\u060c \u0695\u06d5\u0648\u0627 \u0646\u06cc\u06cc\u06d5 \u062e\u06c6 \u0628\u0628\u0648\u06ce\u0631\u0646. \u0644\u06d5 \u0646\u0648\u0648\u0633\u06cc\u0646\u06cc \u0645\u0627\u0648\u06d5\u06cc \u0642\u06d5\u0631\u0632 - \u06a9\u06d5\u0645 \u0628\u06ce \u06cc\u0627\u0646 \u0632\u06c6\u0631 - \u0646\u0627\u0628\u06ce \u0644\u06d5\u0634\u06af\u0631\u0627\u0646\u06cc \u0628\u06a9\u06d5\u0646 \u0648 \u0647\u06d5\u0631 \u0628\u06cc\u0646\u0648\u0648\u0633\u0646 \u0648 \u0645\u06d5\u0648\u062f\u0627\u06a9\u06d5\u0634\u06cc \u062f\u06cc\u0627\u0631\u06cc \u0628\u06a9\u06d5\u0646. \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0695\u06d5\u0648\u0627\u062a\u0631\u06d5 \u0648 \u0628\u06c6 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0644\u06d5\u0633\u06d5\u0631\u062f\u0627\u0646\u06ce \u0644\u06d5\u0628\u0627\u0631\u062a\u0631\u06d5 \u0648 \u0644\u06d5 \u062f\u0648\u0648\u062f\u06b5\u06cc \u0646\u06d5\u0647\u06ce\u0634\u062a\u0646\u06ce \u0646\u0632\u06cc\u06a9\u062a\u0631\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0648\u06d5\u062e\u062a\u06ce \u0626\u0627\u06b5\u0648\u06af\u06c6\u0695\u062a\u0627\u0646 \u062f\u06d5\u0633\u062a \u0628\u06d5\u062f\u06d5\u0633\u062a \u0648 \u0628\u06d5 \u0646\u06d5\u063a\u062f \u0628\u06ce\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0646\u06d5\u0634\u06cc \u0646\u0648\u0648\u0633\u0646 \u0642\u06d5\u06cc\u062f\u06ce \u0646\u0627\u06a9\u0627. \u0628\u06d5\u06b5\u0627\u0645 \u062f\u06cc\u0633\u0627\u0646 \u0644\u06d5 \u0648\u06d5\u062e\u062a\u06cc \u062f\u0627\u062f\u0648\u0633\u062a\u06d5\u062f\u062f\u0627 \u0634\u0627\u06cc\u06d5\u062a \u0647\u06d5\u0628\u0646. \u0646\u0627\u0634\u06ce \u0646\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0646\u06d5 \u0646\u0648\u0648\u0633\u06d5\u0631 \u0632\u06cc\u0627\u0646\u06ce \u0628\u06a9\u06d5\u0646\u061b \u06af\u06d5\u0631 \u0648\u0627\u0646\u06d5\u06a9\u06d5\u0646 \u062a\u0627\u0648\u0627\u0646 \u0644\u06d5 \u0645\u0644\u06cc \u062e\u06c6\u062a\u0627\u0646\u06d5 \u0648 \u0626\u06d5\u0634\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646. \u062e\u0648\u062f\u0627 \u0641\u06ce\u0631\u06cc \u06a9\u0627\u0631\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0648 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u0633\u06d5\u0641\u06d5\u0631 \u0628\u0648\u0648\u0646 \u0648 \u0646\u0648\u0648\u0633\u06d5\u0631\u062a\u0627\u0646 \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u060c \u0626\u06d5\u0634\u06ce \u0628\u0627\u0631\u0645\u062a\u06d5 \u0648\u06d5\u0631\u06af\u0631\u0646\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06ce\u06a9 \u062e\u0627\u062a\u0631\u062c\u06d5\u0645 \u0628\u0646 \u06a9\u06d5 \u0626\u06d5\u0648\u0633\u0627 \u0628\u0627\u0631\u0645\u062a\u06d5\u06cc \u0646\u0627\u0648\u06ce. \u0628\u0627 \u0642\u06d5\u0631\u0632\u062f\u0627\u0631 \u062a\u0631\u0633\u06cc \u062e\u0648\u062f\u0627\u06cc \u0644\u06d5 \u062f\u06b5\u062f\u0627 \u0628\u06ce \u0648 \u0642\u06d5\u0631\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0628\u062f\u0627\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0634\u0627\u06cc\u06d5\u062a\u06cc\u0634\u0646 \u062e\u06c6 \u0644\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0646\u06d5\u0628\u0648\u06ce\u0631\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062e\u06c6 \u0628\u0628\u0648\u06ce\u0631\u06ce\u060c \u0628\u06d5 \u062f\u06b5 \u062e\u06c6\u06cc \u062a\u0648\u0648\u0634 \u06af\u0648\u0646\u0627\u062d \u062f\u06d5\u06a9\u0627 \u0648 \u062e\u0648\u0627 \u0628\u06d5 \u06af\u0634 \u06a9\u0627\u0631\u062a\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0633\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627\u06cc\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5. \u0626\u06d5\u0648\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u062f\u06b5\u062a\u0627\u0646\u062f\u0627\u06cc\u06d5 - \u0686 \u062f\u06d5\u0631\u06cc \u062e\u06d5\u0646\u060c \u0686 \u0648\u06d5\u06cc\u0634\u0627\u0631\u0646 - \u062e\u0648\u0627 \u0644\u06d5\u0648 \u0628\u0627\u0631\u06d5 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u067e\u0631\u0633\u06ce\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631\u06a9\u06ce \u0628\u06c6 \u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce\u060c \u0644\u06ce\u06cc \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u062c\u0632\u06cc\u0627\u06cc \u062f\u06d5\u062f\u0627. \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u06d5\u0644 \u0626\u06d5\u0648 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0644\u06ce\u06cc\u0627\u0646 \u0695\u0648\u0648\u0646\u06d5 \u06a9\u06d5 \u0626\u06d5\u0648 \u0634\u062a\u06d5 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce \u0628\u06c6 \u0644\u0627\u06cc\u0627\u0646\u061b \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc \u0648 \u06a9\u062a\u06ce\u0628\u0627\u0646\u06cc \u0648 \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0648 \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0647\u06cc\u0686 \u0644\u0627 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0641\u06d5\u0631\u0642 \u0648 \u062c\u06cc\u0627\u0648\u0627\u0632\u06cc \u062f\u0627\u0646\u0627\u0646\u06ce\u06cc\u0646\u061b \u0641\u06d5\u0631\u0645\u0648\u0648\u062f\u06d5\u06cc \u062e\u0648\u062f\u0627\u0645\u0627\u0646 \u0628\u06cc\u0633\u062a \u0648 \u067e\u06ce\u06a9\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u060c \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646! \u0628\u0645\u0627\u0646 \u0628\u06d5\u062e\u0634\u06d5 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u06cc\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062a\u06c6.',
    '\u062e\u0648\u0627 \u0626\u06d5\u0631\u06a9\u06cc \u0644\u06d5 \u062a\u0648\u0627\u0646\u0627 \u0628\u06d5\u062f\u06d5\u0631\u060c \u0628\u06d5 \u0633\u06d5\u0631 \u06a9\u06d5\u0633\u062f\u0627 \u0646\u0627\u0633\u06d5\u067e\u06ce\u0646\u06ce\u061b \u0686\u0627\u06a9\u06d5\u06cc \u0628\u06a9\u0627 \u0628\u06c6 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0648 \u062e\u0631\u0627\u067e\u06d5\u0634\u06cc \u062f\u0698\u06cc \u062e\u06c6\u06cc\u06d5. \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u0644\u0627\u0645\u0627\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u0686\u0648\u0648\u060c \u06cc\u0627\u0646 \u062a\u06cc\u0627 \u0628\u06d5 \u0647\u06d5\u06b5\u06d5 \u0686\u0648\u0648\u06cc\u0646\u060c \u0644\u06ce\u0645\u0627\u0646 \u0645\u06d5\u06af\u0631\u06d5. \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646! \u0626\u06d5\u0631\u06a9\u06cc \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u06af\u0631\u0627\u0646\u0645\u0627\u0646 \u0645\u06d5\u062e\u06d5 \u0633\u06d5\u0631\u0634\u0627\u0646 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u0628\u0648\u0648. \u0626\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u0645\u0627\u0646! \u0626\u06d5\u0631\u06a9\u06cc \u0648\u0627\u0645\u0627\u0646 \u067e\u06ce \u0645\u06d5\u0633\u067e\u06ce\u0631\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062a\u0648\u0627\u0646\u0627\u0645\u0627\u0646 \u0628\u06d5\u062f\u06d5\u0631 \u0628\u06ce. \u062a\u06c6 \u0647\u06d5\u0631 \u062e\u06c6\u062a \u0644\u06ce\u0645\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u06d5 \u0648 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u062a\u0645\u0627\u0646 \u0628\u062e\u06d5 \u0648 \u0628\u0645\u0627\u0646\u0628\u06d5\u062e\u0634\u06d5. \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06ce\u06a9\u06cc \u0645\u06d5 \u0628\u06d5 \u062f\u06d5\u0633 \u062a\u06c6\u06cc\u06d5 \u0648 \u0633\u06d5\u0631\u06cc\u0634\u0645\u0627\u0646\u062e\u06d5\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0627\u062a\u0646\u0627\u0633\u0646.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0645\u06cc\u0645.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce\u061b \u0647\u06d5\u0631 \u0632\u06cc\u0646\u062f\u0648\u0648\u06d5 \u0648 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u06cc - \u0628\u06d5\u0695\u0627\u0633\u062a - \u06a9\u06d5 \u0628\u06c6 \u062a\u06c6 \u0646\u0627\u0631\u062f\u0648\u0648\u06d5. \u0628\u0627\u0648\u06d5\u0695 \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u0627\u0646\u06d5\u0634 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5\u0648\u06cc \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u0627\u0648\u0646\u061b \u062a\u06d5\u0648\u0631\u0627\u062a \u0648 \u0626\u06cc\u0646\u062c\u06cc\u0644\u06cc\u0634\u06cc \u0644\u06d5 \u0628\u06d5\u0631\u0627 \u0647\u06d5\u0646\u0627\u0631\u062f\u061b',
    '\u062a\u0627 \u0695\u06ce\u0646\u06cc\u0634\u0627\u0646\u062f\u06d5\u0631 \u0628\u0646 \u0628\u06c6 \u062e\u06d5\u06b5\u06a9. \u0641\u0648\u0631\u0642\u0627\u0646\u06cc\u0634\u06cc [\u0644\u06d5\u0648\u06d5\u0648\u062f\u0648\u0627] \u0646\u0627\u0631\u062f. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u062a\u06c6\u06b5\u06d5\u0633\u062a\u06ce\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u06cc\u0627\u0646 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0628\u06ce\u060c \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0648\u06d5\u0634\u06ce\u0631\u0627\u0648 \u0646\u0627\u0628\u06ce.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0647\u06d5\u0631\u0686\u06c6\u0646\u06ce \u062d\u06d5\u0632 \u06a9\u0627 - \u0647\u06d5\u0631 \u0644\u06d5 \u062a\u06c6\u0644\u06cc \u062f\u0627\u06cc\u06a9\u0627\u0646\u0648\u0648\u062f\u0627 - \u0628\u06cc\u0686\u0645\u062a\u0627\u0646 \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0628\u06ce\u061b \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u062f\u0627\u0631\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u0628\u06c6 \u062a\u06c6 \u0628\u06d5 \u062e\u06d5\u06b5\u0627\u062a \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u06a9\u06d5 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0626\u0627\u06cc\u06d5\u062a\u06af\u06d5\u0644\u06ce\u06a9\u06cc \u067e\u062a\u06d5\u0648 \u0647\u06d5\u0646 \u06a9\u06d5 \u0645\u0627\u06a9\u0646 \u0628\u06c6 \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u06d5\u061b \u062a\u06ce\u0634\u06cc\u062f\u0627\u06cc\u06d5 \u0648\u06d5\u06a9 \u06cc\u06d5\u06a9 \u062f\u06d5\u0686\u0646 \u0648 \u0645\u0627\u0646\u0627\u06cc \u062c\u06c6\u0631\u0627\u0648\u062c\u06c6\u0631 \u0647\u06d5\u06b5\u062f\u06d5\u06af\u0631\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06b5\u06cc \u062f\u06d5\u063a\u06d5\u0632\u062f\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062d\u06d5\u0632 \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u067e\u06ce \u0647\u06d5\u06b5 \u062f\u06d5\u062e\u06d5\u06b5\u06d5\u062a\u06ce \u0648 \u0647\u06d5\u0631\u0686\u06c6\u0646\u06cc \u062f\u06b5\u06cc\u0627\u0646 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u0645\u0627\u0646\u0627\u06cc\u0627\u0646 \u0644\u06ce\u06a9 \u062f\u06d5\u062f\u06d5\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627 \u0646\u0627\u06cc\u0627\u0646\u0632\u0627\u0646\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0632\u06c6\u0631 \u0632\u0627\u0646\u0627\u0646\u060c \u0626\u06d5\u06b5\u06ce\u0646: \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u067e\u06ce \u0647\u06d5\u06cc\u06d5 \u0648 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06af\u0634 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646\u0695\u0627\u06cc\u06d5 \u0648 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u062f\u06b5 \u0648\u0634\u06cc\u0627\u0631\u0627\u0646\u06d5\u060c \u0628\u06d5 \u0648\u0631\u062f\u06cc \u0628\u06cc\u0631 \u0646\u0627\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af \u0644\u0627\u0645\u0627\u0646! \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u062a \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0648\u06cc\u0646\u060c \u062f\u06b5\u0645\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u06c6\u062a \u0644\u0627 \u0645\u06d5\u062f\u06d5 \u0648 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u062a\u0645\u0627\u0646 \u0628\u062e\u06d5. \u0628\u06d5\u062e\u0634\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0645\u06d5\u0632\u0646 \u062a\u06c6\u06cc.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0647\u06d5\u0631 \u062a\u06c6\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0644\u06d5\u0648 \u0631\u06c6\u0698\u06d5\u062f\u0627 - \u06a9\u06d5 \u06af\u0648\u0645\u0627\u0646\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5 - \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u06a9\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u06ce \u0633\u06c6 \u062e\u0648\u0627 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u0646\u0627\u0634\u06a9\u06ce\u0646\u06ce.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0645\u0627\u06b5 \u0648 \u0639\u06d5\u0648\u0644\u0627\u062f\u060c \u062f\u06d5 \u0647\u0627\u0646\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u06cc\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5 \u0648 \u0686\u0627\u0631\u06cc \u0646\u0627\u0686\u0627\u0631 \u062f\u06d5\u0628\u0646\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u0686\u06cc\u0644\u06d5\u06cc \u0626\u0627\u06af\u0631\u061b',
    '\u0648\u06d5\u06a9 \u062f\u0627\u0631\u0648\u062f\u06d5\u0633\u062a\u06d5\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0628\u0648\u0648\u0646\u061b \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0627\u0646\u06cc\u06cc \u0648 \u062e\u0648\u0627 \u0644\u06d5 \u0633\u0632\u0627\u06cc \u062a\u0627\u0648\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06af\u0631\u062a\u0646\u06cc. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062a\u06c6\u06b5\u06d5\u0633\u0627\u0646\u062f\u0646\u0627 \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631\u06d5.',
    '\u0628\u06d5\u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5 \u0628\u06ce\u0698\u06d5: \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u0698\u06ce\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u0647\u06d5\u0631\u0628\u06cc\u0646\u0627 \u0695\u06d5\u067e\u06ce\u0686\u06d5\u06a9 \u062f\u0631\u0627\u0646 \u0628\u06c6 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u061b \u06a9\u06d5 \u062c\u06ce \u062e\u06d5\u0648\u06ce\u06a9\u06cc \u062e\u0631\u0627\u067e\u06d5!',
    '\u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0631\u0633\u06ce\u06a9 \u0628\u0648\u0648 \u06a9\u06d5 \u062f\u0648\u0648 \u062f\u06d5\u0633\u062a\u06d5 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u06af\u0698\u06cc\u06d5\u06a9 \u0695\u0627\u0686\u0648\u0648\u0646\u061b \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0634\u06d5\u0695 \u062f\u06d5\u0647\u0627\u062a\u0646 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06a9\u06d5\u06cc \u062a\u0631 \u0644\u06d5\u062f\u0698\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u062c\u06d5\u0646\u06af\u06cc\u0646 \u0648 \u0632\u06d5\u0642 \u062f\u06d5\u06cc\u0627\u0646\u062f\u06cc \u06a9\u06d5 \u062e\u0648\u0627\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u062f\u0648\u0648 \u0647\u06ce\u0646\u062f\u06d5\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u0633\u06d5\u0631\u06cc \u062f\u06d5\u062e\u0627\u061b \u0626\u06d5\u0645\u06d5\u0634 \u067e\u06d5\u0646\u062f\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0641\u0627\u0645\u0646.',
    '\u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0698\u0646\u0627\u0646 \u06a9\u0631\u062f\u0646 \u0648 \u06a9\u0648\u0695\u06cc \u0632\u06c6\u0631 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u0646 \u0648 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u0646\u06cc \u06a9\u06c6\u0645\u0627\u06cc \u0632\u0644\u06cc \u0632\u06ce\u0648 \u0648 \u0632\u06ce\u0695\u0627\u0646 \u0648 \u0695\u06d5\u0648\u06d5\u0626\u06d5\u0633\u067e\u06cc \u0628\u06d5 \u062f\u0631\u06c6\u0634\u0645 \u0648 \u0648\u06b5\u0633\u0627\u062a \u0648 \u0632\u06d5\u0648\u06cc \u06a9\u06ce\u06b5\u0627\u0646\u06ce\u060c \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0695\u0627\u0632\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5\u062f\u0627 \u0645\u0627\u06cc\u06d5\u06cc \u062e\u06c6\u0634\u06cc\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0648\u06d5\u062e\u062a\u06ce \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06d5\u0631\u062f\u06d5\u0633\u062a\u06cc \u062e\u0648\u0627\u060c \u067e\u06ce\u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0632\u06c6\u0631 \u0644\u06d5\u0648\u06d5 \u0628\u0627\u0634\u062a\u0631 \u0647\u06d5\u06cc\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0628\u06ce\u0698\u0645 \u0644\u06d5\u0645\u06d5 \u0628\u0627\u0634\u062a\u0631\u06d5 \u06a9\u06d5 \u0686\u06c6\u0646\u06d5\u061f \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0647\u06d5\u06b5\u06d5 \u062e\u06c6\u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646\u060c \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06cc \u0648\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce\u060c \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646 \u0648 \u0686\u06d5\u0646\u062f \u0698\u0646\u06cc \u067e\u0627\u06a9 \u0648 \u0628\u06ce \u06af\u06d5\u0631\u062f \u0648 \u0633\u06d5\u0631\u06d5\u0695\u0627\u06cc \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06c6\u0634\u06cc\u06cc\u06d5\u0634\u060c \u0695\u06d5\u0632\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u06af\u0627. \u062e\u0648\u062f\u0627 \u0686\u0627\u0648\u06cc \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u067e\u06ce\u062a \u0647\u06d5\u06cc\u06d5\u061b \u0633\u0627 \u062a\u06c6 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0646 \u0628\u0628\u0648\u0648\u0631\u06d5 \u0648 \u0644\u06d5 \u062c\u0632\u06cc\u0627\u062f\u0627\u0646\u0645\u0627\u0646 - \u0628\u06d5 \u0626\u0627\u06af\u0631 - \u0628\u067e\u0627\u0631\u06ce\u0632\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631\u0646 \u0648 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc\u0627\u0646\u06d5 \u0648 \u0626\u0627\u0645\u0627\u062f\u06d5\u06cc \u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06ce\u06a9\u0646 \u0648 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u0627 \u0628\u06d5\u062e\u062a \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0695\u06c6\u0698\u06ce - \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u06cc\u0627\u0646 - \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u06ce\u06cc\u0627\u0646 \u062e\u06c6\u0634 \u0628\u06ce.',
    '\u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u0648 \u0632\u0627\u0646\u0627\u06cc\u0627\u0646\u06cc \u0628\u06d5 \u0626\u06cc\u0646\u0633\u0627\u0641\u06cc\u0634 \u0628\u0627\u0634 \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u060c \u0634\u06cc\u0627\u0648\u06cc \u067e\u06d5\u0631\u0633\u062a\u0646\u06ce \u0646\u06cc\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646.',
    '\u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u062f\u06cc\u0646 \u0626\u06cc\u0633\u0644\u0627\u0645\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646 \u0644\u06d5\u0648 \u0695\u0627\u0633\u062a\u06d5\u0642\u06cc\u0646\u06d5 \u062f\u06d5\u0632\u0627\u0646\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631 \u0644\u06d5\u0628\u06d5\u0631 \u0626\u06ce\u0631\u06d5\u06cc\u06cc - \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5 - \u067e\u06ce\u06a9 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 - \u0628\u06d5\u0647\u06d5\u0631 \u062c\u06c6\u0631\u06ce - \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062d\u0627\u0634\u0627 \u0628\u06a9\u0627\u060c \u0628\u0627 \u062a\u06ce\u0628\u06af\u06d5\u0646 \u06a9\u06d5 \u062e\u0648\u0627 \u0644\u06d5 \u0644\u06ce \u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5 \u0632\u06c6\u0631 \u0628\u06d5\u0644\u06d5\u0632\u06d5 \u0648 \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u06a9\u06ce\u0634\u06d5\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u062a\u06c6 \u0647\u06d5\u0631 \u0628\u06ce\u0698\u06d5: \u0645\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06d5\u06a9\u0627\u0646\u0645 \u0628\u06d5 \u062f\u06b5\u06cc \u067e\u0627\u06a9 \u0695\u0648\u0648\u0645\u0627\u0646 \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5. \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u0648 \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0646\u06d5\u0632\u0627\u0646\u0646\u060c \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5\u0634 \u0648\u06d5\u06a9 \u0645\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627 \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5\u061f \u062c\u0627 \u0626\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0633\u067e\u0627\u0631\u062f\u0628\u0648\u0648\u060c \u0626\u06d5\u0648\u0627 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u06d5 \u0633\u06d5\u0631 \u0695\u06ce\u06af\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u06cc\u060c \u062a\u06c6 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u062a \u0644\u06d5\u0633\u06d5\u0631\u06d5 \u0695\u0627\u0628\u06af\u06d5\u06cc\u06ce\u0646\u06cc \u0648 \u062e\u0648\u062f\u0627\u0634 \u0628\u06c6 \u062e\u06c6\u06cc \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u062d\u0627\u0634\u0627 \u06a9\u0631\u062f \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 - \u0644\u06d5 \u062e\u0648\u062a \u0648 \u062e\u06c6\u0695\u0627\u06cc\u06cc - \u062f\u06d5\u06a9\u0648\u0634\u062a \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u062f\u06d5\u0628\u0631\u062f \u06a9\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u062f\u06d5\u062f\u0627\u060c \u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062f\u0627\u0646\u06cc \u0628\u06d5\u0698\u0627\u0646 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0648 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634\u0627 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u0628\u06cc\u06a9\u06d5\u0646 \u0641\u06cc\u0695\u06c6\u06cc\u06d5 \u0648 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u062e\u06c6 \u062a\u06c6 \u062f\u06cc\u062a\u062a\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u06a9\u06d5 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u0626\u06ce\u0645\u06d5\u062f\u0627 \u0628\u06d5\u0634\u06ce\u06a9\u06cc\u0627\u0646 \u067e\u06ce \u06af\u06d5\u06cc\u0634\u062a\u0628\u0648\u0648\u060c \u062e\u06c6\u06cc\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06d5 \u0647\u06d5\u0631 \u062d\u0648\u06a9\u0645\u06ce \u062e\u0648\u062f\u0627 \u0628\u06cc\u062f\u0627 \u0626\u06ce\u0645\u06d5 \u0695\u0627\u0632\u06cc\u0646. \u06a9\u06d5\u0686\u06cc \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06ce\u06cc \u0695\u0627\u0632\u06cc \u0646\u06d5\u0628\u0648\u0648\u0646.',
    '\u0647\u06d5\u0631 \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0634\u0645\u0627\u0646\u062e\u06d5\u0646\u06d5 \u062f\u06c6\u0698\u06d5\u0647\u06d5\u0648\u06d5\u060c \u0686\u06d5\u0646\u062f \u0695\u06c6\u0698\u06ce\u06a9 \u067e\u062a\u0631 \u067e\u06ce \u0646\u0627\u0686\u06ce\u060c \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u0628\u06cc\u0646\u061b \u0628\u06d5\u0648 \u062f\u0631\u06c6\u06cc\u06d5 \u0628\u06c6 \u062f\u06cc\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06cc\u0627\u0646 \u0628\u06d5\u0633\u062a\u060c \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06d5\u06b5\u062e\u06d5\u06b5\u06d5\u062a\u0627\u0646\u062f\u06d5\u0648\u06d5.',
    '\u0633\u0627 \u0626\u06d5\u0648 \u0631\u06c6\u0698\u06d5 \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u0686 \u0628\u0627\u0631\u06ce\u06a9\u062f\u0627 \u0628\u0646\u060c \u06a9\u06d5 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0648\u06d5\u062f\u06cc \u062f\u06ce \u0648 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u06a9\u06c6\u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0647\u06d5\u0631\u0686\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06cc \u062f\u06ce\u062a\u06d5\u0648\u06d5 \u0695\u06ce\u06cc \u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u06a9\u06d5\u0633 \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0628\u06ce\u0698\u06d5: \u062e\u0648\u0627\u06cc\u06d5! \u0626\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0647\u06ce\u0632 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a! \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u06c6 \u062e\u06c6\u062a \u0645\u06d5\u06cc\u0644\u062a \u0644\u06ce \u0628\u06ce \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u067e\u06ce \u062f\u06d5\u062f\u06d5\u06cc \u0648 \u06a9\u06ce\u0634 \u062a\u06c6 \u062d\u06d5\u0632 \u06a9\u06d5\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06ce \u062f\u06d5\u0633\u062a\u06ce\u0646\u06cc\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0648\u06d5\u0642\u06d5\u062f\u0631 \u062e\u06d5\u06cc \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0632\u06d5\u0628\u0648\u0648\u0646\u06cc \u0628\u06a9\u06d5\u06cc\u060c \u062e\u06c6\u062a \u062f\u06d5\u06cc\u0632\u0627\u0646\u06cc\u061b \u0686\u0627\u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5 \u062e\u06c6\u062a\u06d5 \u0648 \u062a\u06c6 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc.',
    '\u0634\u06d5\u0648 \u062f\u06d5\u0646\u0627\u0648 \u0695\u06c6\u0698 \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u06ce\u0634\u06cc\u060c \u0695\u06c6\u0698 \u062f\u06d5\u062e\u06d5\u06cc\u06d5 \u0646\u0627\u0648 \u0634\u06d5\u0648\u06d5\u0648\u06d5\u061b \u0632\u06cc\u0646\u062f\u0648\u0648 \u0644\u06d5 \u0645\u0631\u062f\u0648\u0648 \u062f\u06d5\u0631\u062f\u06d5\u062e\u06d5\u06cc\u060c \u0645\u0631\u062f\u0648\u0648 \u0644\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u0631\u062f\u06ce\u0646\u06cc\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u062a \u0645\u06d5\u06cc\u0644\u062a \u0644\u06ce \u0628\u06ce\u060c \u0628\u06ce \u0647\u06d5\u0698\u0645\u0627\u0631 \u0628\u0698\u06cc\u0648\u06cc \u062f\u06d5\u062f\u06d5\u06cc.',
    '\u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u060c \u0628\u0627 \u062c\u06af\u06d5 \u0644\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0646\u06d5\u06af\u0631\u0646 \u0628\u06d5 \u062f\u06c6\u0633\u062a. \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0644\u06d5\u0645 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5 \u062f\u06d5\u0631\u0686\u06ce\u060c \u0647\u06cc\u0686 \u0628\u06d5\u0634\u06cc \u0628\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0646\u06cc\u06cc\u06d5. \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06ce\u06cc\u0627\u0646 \u0628\u062a\u0631\u0633\u0646 \u0648 \u0644\u06d5 \u0646\u0627\u0686\u0627\u0631\u06cc \u067e\u06ce\u06cc\u0627\u0646 \u0628\u06b5\u06ce\u0646: \u062f\u06c6\u0633\u062a\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u0646. \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u0647\u06d5\u0631 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0644\u06d5\u0648 \u0628\u062a\u0631\u0633\u0646 \u0648 \u067e\u0627\u0634\u06d5\u0631\u06c6\u0698\u062a\u0627\u0646 \u0647\u06d5\u0631 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648\u06d5\u06cc \u0648\u0627 \u0644\u06d5 \u062f\u06b5\u062a\u0627\u0646\u0627\u06cc\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06cc\u0634\u0627\u0631\u0646\u06d5\u0648\u06d5 \u06cc\u0627 \u062f\u06d5\u0631\u06cc\u062e\u06d5\u0646\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0648 \u0644\u06d5 \u0647\u06cc\u0686\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0646\u062f\u0627\u0633\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0631\u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u0686\u0627\u06a9 \u0648 \u062e\u0631\u0627\u067e\u06cc \u0644\u06d5\u0628\u06d5\u0631 \u0686\u0627\u0648\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0628\u06cc\u0646\u06ce\u060c \u0626\u0627\u0648\u0627\u062a \u062e\u0648\u0627\u0632\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u062e\u06c6\u06cc \u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc\u062f\u0627 \u0645\u06d5\u0648\u062f\u0627\u06cc\u06d5\u06a9\u06cc \u062f\u0648\u0648\u0631 \u0628\u0628\u0648\u0648\u0627\u06cc\u06d5. \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06ce \u062e\u06c6\u062a\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646\u061b \u062e\u0648\u0627 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062e\u06c6\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0628\u06ce\u0698\u06d5 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627\u062a\u0627\u0646 \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u060c \u0634\u0648\u06ce\u0646 \u0645\u0646 \u06a9\u06d5\u0648\u0646 \u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627\u0634 \u062e\u06c6\u0634\u06cc \u0628\u0648\u06ce\u0646 \u0648 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u06cc\u0634\u0648\u0648 \u0628\u0628\u0648\u0648\u0631\u06ce. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u062e\u06d5\u062a\u0627\u0628\u06d5\u062e\u0634 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u062f\u06d5\u0628\u06ce \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0628\u0646. \u062c\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646 \u062f\u06d5\u0631\u0686\u0648\u0648\u0646\u060c \u0626\u06cc\u062a\u0631 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u062d\u06d5\u0632 \u0646\u0627\u06a9\u0627.',
    '\u0626\u0627\u062f\u06d5\u0645 \u0648 \u0646\u0648\u0648\u062d \u0648 \u0646\u06d5\u062a\u06d5\u0648\u06d5\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0648 \u0628\u06d5\u0631\u06d5\u06cc \u0639\u06cc\u0645\u0631\u0627\u0646\u060c \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062c\u06cc\u0647\u0627\u0646 \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u06d5\u0646\u061b \u062e\u0648\u0627 \u067e\u062a\u0631 \u0695\u06ce\u0632\u06cc \u0644\u06d5\u0648\u0627\u0646 \u0646\u0627.',
    '\u0647\u06d5\u0645\u0648\u0648 \u0644\u0627 \u0647\u06d5\u0631 \u0633\u06d5\u0631 \u0628\u06d5 \u06cc\u06d5\u06a9\u0646 \u0648 \u062e\u0648\u0627 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u06a9\u0627\u062a\u06ce \u06a9\u06d5\u06cc\u0648\u0627\u0646\u0648\u0648\u06d5\u06a9\u06d5\u06cc \u0639\u06cc\u0645\u0631\u0627\u0646 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0626\u06d5\u0648 \u0645\u0646\u0627\u06b5\u06d5\u06cc \u06a9\u06d5\u0648\u062a\u06c6\u062a\u06d5 \u062a\u06c6\u0644\u06cc \u0645\u0646\u06d5\u0648\u06d5\u060c \u0645\u0646 \u062f\u06d5\u0642\u06d5\u0628\u06d5\u06b5\u06cc \u062a\u06c6\u06cc \u062f\u06d5\u06a9\u06d5\u0645. \u0628\u06d5\u0647\u06cc\u0648\u0627\u0645 \u0644\u06ce\u0645 \u0642\u0628\u0648\u0648\u06b5 \u0628\u06a9\u06d5\u06cc\u060c \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0647\u06d5\u0631 \u062a\u06c6\u06cc.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0628\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u062f\u0627\u0646\u0627\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0626\u06d5\u0648\u0627 \u0645\u0646 \u0645\u06ce\u0648\u06cc\u0646\u06d5\u0645 \u062f\u06cc\u0648\u06d5 - \u062e\u0648\u062f\u0627\u0634 \u0686\u0627\u062a\u0631 \u062e\u06d5\u0648\u06d5\u0631 \u062f\u0627\u0631\u06d5 \u0644\u06d5\u0648\u06d5\u06cc \u062f\u06cc\u0648\u06cc\u06d5 - \u062f\u06cc\u0627\u0631\u06d5 \u06a9\u0686\u06cc\u0634 \u0648\u06d5\u06a9 \u06a9\u0648\u0695 \u0646\u06cc\u06cc\u06d5. \u0645\u0646 \u0646\u0627\u0648\u0645 \u0644\u06ce \u0646\u0627\u0648\u06d5 \u0645\u0631\u06cc\u06d5\u0645\u060c \u0626\u06d5\u0648\u0627 \u0626\u06d5\u0648\u06cc\u0634 \u0648 \u0628\u06d5\u0631\u06cc\u0634\u06cc\u0645 \u0628\u06d5 \u062a\u06c6 \u0633\u067e\u0627\u0631\u062f\u061b \u06a9\u06d5 \u0644\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06cc \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u0628\u06cc\u0627\u0646\u067e\u0627\u0631\u06ce\u0632\u06cc.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u060c \u0626\u06d5\u0648 \u067e\u06ce\u0634\u06a9\u06d5\u0634\u06d5\u06cc \u06af\u06d5\u0644\u06ce \u0628\u06d5\u0628\u0627\u0634\u06cc \u0644\u06ce \u0648\u06d5\u0631\u06af\u0631\u062a\u061b \u06a9\u06cc\u0698\u06d5\u06a9\u06d5\u06cc \u0628\u0627\u0634 \u0628\u06d5 \u062e\u0648\u062f\u0627\u0646 \u06a9\u0631\u062f \u0648 \u0632\u06d5\u06a9\u06d5\u0631\u06cc\u06cc\u0627\u06cc \u0628\u06c6 \u0633\u06d5\u0631\u067e\u06d5\u0631\u0634\u062a\u06cc \u0648\u06ce \u062f\u0627\u0646\u0627. \u0647\u06d5\u0631 \u062c\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06d5\u06a9\u06d5\u0631\u06cc\u06cc\u0627 \u0644\u06d5 \u0645\u06cc\u0639\u0631\u0627\u0648\u062f\u0627 \u0633\u06d5\u0631\u06cc \u062f\u06d5\u062f\u0627\u060c \u062f\u06d5\u06cc\u062f\u06cc \u0628\u0698\u06cc\u0648\u06cc \u0644\u06d5 \u0644\u0627\u06cc\u06d5. \u06af\u0648\u062a\u06cc: \u0645\u0631\u06cc\u06d5\u0645! \u062a\u06c6 \u0626\u06d5\u0645 \u0628\u0698\u06cc\u0648\u06d5\u062a \u0644\u06d5 \u06a9\u0648\u06ce \u0628\u0648\u0648\u061f \u06af\u0648\u062a\u06cc: \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0628\u06c6\u0645 \u0647\u0627\u062a\u0648\u0648\u06d5\u061b \u062e\u0648\u062f\u0627 \u062d\u06d5\u0632 \u06a9\u0627 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06ce \u0647\u06d5\u0698\u0645\u0627\u0631 \u062f\u06d5\u0646\u06ce\u0631\u06ce.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0648\u06d5\u06cc\u0695\u0627 \u0632\u06d5\u06a9\u06d5\u0631\u06cc\u06cc\u0627 \u06af\u0627\u0632\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06a9\u0631\u062f: \u0626\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u0645! \u0632\u0627\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u0686\u0627\u06a9 \u0648 \u067e\u0627\u06a9\u0645 \u067e\u06ce \u0628\u0628\u06d5\u062e\u0634\u06d5\u061b \u062f\u06d5\u0632\u0627\u0646\u0645 \u062a\u06c6 \u067e\u0627\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0645\u0646 \u062f\u06d5\u0628\u06cc\u0633\u06cc.',
    '\u0644\u06d5 \u06a9\u0627\u062a\u06ce\u06a9\u0627 \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0645\u06cc\u062d\u0631\u0627\u0628\u06d5\u06a9\u06d5\u062f\u0627 \u0646\u0648\u06ce\u0698\u06cc \u062f\u06d5\u06a9\u0631\u062f\u060c \u0641\u0631\u06cc\u0634\u062a\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0646\u06af\u06cc \u06a9\u0631\u062f: \u062e\u0648\u062f\u0627 \u0645\u0632\u06af\u06ce\u0646\u06cc\u062a \u067e\u06ce \u062f\u06d5\u062f\u0627\u060c \u06a9\u0648\u0695\u06ce\u06a9\u06cc \u0646\u0627\u0648 \u00ab\u06cc\u06d5\u062d\u06cc\u0627\u00bb \u062a \u062f\u06d5\u0628\u06ce\u060c \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0641\u06d5\u0631\u0645\u0648\u0648\u062f\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0633\u06d5\u0644\u0645\u06ce\u0646\u06ce \u0648 \u0645\u0631\u06c6\u06a4\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5 \u0648 \u062e\u06c6\u0695\u0627\u06af\u0631 [\u0644\u06d5\u0698\u0646] \u0648\u060c \u06cc\u06d5\u06a9\u06ce\u06a9 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u062c\u0627\u0686\u06c6\u0646 \u0626\u06d5\u0645\u0646 \u06a9\u0648\u0695\u0645 \u062f\u06d5\u0628\u06ce\u060c \u0645\u0646\u06cc \u0648\u0627 \u067e\u06cc\u0631 \u0648 \u0632\u0648\u0631\u0647\u0627\u0646 \u0648 \u0698\u0646\u06cc\u0634\u0645 \u0644\u06d5 \u0632\u06af \u0648\u06ce\u0633\u062a\u0627\u0648\u06d5\u061f \u06af\u0648\u062a\u06cc: \u062e\u0648\u062f\u0627\u060c \u0686\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u0628\u06ce \u0648\u0627\u062f\u06d5\u06a9\u0627.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0645\u0646! \u062e\u06c6\u062a \u0646\u06cc\u0634\u0627\u0646\u06d5\u0645 \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u06d5. \u06af\u0648\u062a\u06cc: \u0646\u06cc\u0634\u0627\u0646 \u0628\u06d5\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0647\u06d5\u062a\u0627 \u0633\u06ce \u0695\u06c6\u0698 \u0646\u0627\u062a\u0648\u0627\u0646\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0628\u062f\u0648\u06ce\u0646\u06cc\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5\u0647\u06cc\u0645\u0627 \u0648 \u0626\u0627\u0634\u06cc\u0631\u06d5\u062a. \u0644\u06d5 \u0626\u06ce\u0648\u0627\u0631\u06d5 \u0648 \u0628\u06d5\u06cc\u0627\u0646\u0627\u0646 \u062f\u0627\u060c \u06cc\u0627\u062f\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06a9\u06d5 \u0648 \u0644\u06d5 \u067e\u06d5\u0633\u0646\u062f\u0627\u0646\u06cc \u0645\u06d5\u0648\u06ce\u0633\u062a\u06d5.',
    '\u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634\u062f\u0627 \u0641\u0631\u06cc\u0634\u062a\u06d5 \u0628\u06d5 \u0645\u06d5\u0631\u06cc\u06d5\u0645\u06cc \u06af\u0648\u062a: \u062e\u0648\u062f\u0627 \u062a\u06c6\u06cc \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u067e\u0627\u06a9 \u0648 \u062e\u0627\u0648\u06ce\u0646\u06cc \u0695\u0627\u06af\u0631\u062a\u0648\u0648\u06cc\u061b \u062a\u06c6\u06cc \u0644\u06d5 \u0686\u0627\u0648 \u0698\u0646\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627 \u0628\u06d5\u0631\u06ce\u0632\u062a\u0631 \u06af\u0631\u062a.',
    '\u0626\u06d5\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645! \u062a\u06c6 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a \u062f\u06d5\u0631\u0645\u06d5\u0686\u06c6\u061b \u0628\u06c6\u06cc \u0628\u0686\u06c6\u0631\u06d5 \u0633\u06d5\u0631 \u0633\u0648\u0698\u062f\u06d5 \u0648 \u062a\u06c6\u0634 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0695\u0646\u06c6\u0634 \u0628\u06d5\u0631\u0627\u0646\u062f\u0627 \u06a9\u0695\u0646\u06c6\u0634 \u0628\u06d5\u0631\u06d5.',
    '\u0626\u06d5\u0645\u06d5 \u0644\u06d5\u0648 \u062f\u06d5\u0646\u06af \u0648 \u0628\u0627\u0633\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062a\u06c6 \u0626\u0627\u06af\u0627\u062a \u0644\u06ce \u0646\u06d5\u0628\u0648\u0648\u0646 \u0648 \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u06cc\u062e\u06d5\u06cc\u0646\u06d5 \u062f\u06b5\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u062a\u06c6 \u0644\u06d5\u0648\u06ce \u0646\u06d5\u0628\u0648\u0648\u06cc \u06a9\u06d5 \u0642\u06d5\u06b5\u06d5\u0645\u06cc\u0627\u0646 \u062a\u0648\u0648\u0695\u0647\u06d5\u06b5\u062f\u06d5\u062f\u0627\u0646 \u06a9\u06ce \u0633\u06d5\u0631\u067e\u06d5\u0631\u0634\u062a\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645 \u0628\u06ce\u061b \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u060c \u062a\u06c6 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u06cc.',
    '\u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u062f\u0627 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0645\u0631\u06cc\u06d5\u0645! \u062e\u0648\u062f\u0627 \u0645\u0698\u062f\u06d5\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u06ce\u06a9\u06cc \u062e\u06c6\u062a \u067e\u06ce\u062f\u06d5\u062f\u0627\u060c \u06a9\u0648\u0695\u062a \u062f\u06d5\u0628\u06ce \u0648 \u0628\u06d5 \u0646\u0627\u0648 \u0645\u06d5\u0633\u06cc\u062d\u06cc \u06a9\u0648\u0695 \u0645\u06d5\u0631\u06cc\u06d5\u0645 \u0646\u0627\u0648 \u062f\u06d5\u0631\u062f\u06d5\u06a9\u0627 \u0648 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u0634 \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u0642\u06d5\u062f\u0631\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u06cc\u06d5\u06a9\u06ce\u06a9 \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u0646\u0632\u06cc\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627.',
    '\u0644\u06d5 \u0628\u06ce\u0634\u06a9\u06d5\u062f\u0627 \u0648 \u0628\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06cc\u0634\u060c \u0642\u0633\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u06d5\u0631\u062f\u0645 \u062f\u06d5\u06a9\u0627 \u0648 \u06cc\u06d5\u06a9\u06ce\u06a9 \u0644\u06d5 \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u062c\u0627 \u0686\u0644\u06c6\u0646 \u06a9\u0648\u0695\u0645 \u062f\u06d5\u0628\u06ce\u060c \u0647\u06cc\u0686 \u0645\u0631\u06c6\u06cc\u06d5\u06a9 \u062a\u0627 \u0648\u06ce\u0633\u062a\u0627 \u062a\u062e\u0648\u0648\u0646\u0645 \u0646\u06d5\u0628\u0648\u0648\u06d5\u061f! \u06af\u0648\u062a\u06cc: \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u0686\u06cc \u062d\u06d5\u0632 \u06a9\u0627 \u0628\u06d5\u062f\u06cc \u062f\u06ce\u0646\u06ce\u061b \u0647\u06d5\u0631\u06a9\u06d5 \u0648\u06cc\u0633\u062a\u06cc \u06a9\u0627\u0631\u06ce\u06a9 \u0628\u0628\u06ce\u060c \u0647\u06d5\u0631 \u067e\u06ce\u06cc \u0626\u06ce\u0698\u06ce: \u0628\u0628\u06d5! \u062f\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc \u0641\u06ce\u0631\u06cc \u06a9\u062a\u06ce\u0628 \u0648\u06d5\u062e\u0648\u06ce\u0646\u062f\u0646 \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0648 \u062a\u06d5\u0648\u0631\u0627\u062a \u0648 \u0626\u06cc\u0646\u062c\u06cc\u0644\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u062f\u06d5\u0634\u06cc\u06a9\u0627 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u0628\u06c6 \u0633\u06d5\u0631 \u0646\u06d5\u062a\u06d5\u0648\u06d5\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644. [\u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u06ce:] \u0626\u06d5\u0648\u0627 \u0645\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648\u0695\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u0645 \u0628\u06c6 \u0647\u06ce\u0646\u0627\u0648\u0646. \u0647\u06d5\u0631 \u0644\u06d5 \u062d\u06d5\u0695\u06cc \u0634\u06a9\u0644\u06cc \u0645\u06d5\u0644\u06ce\u06a9 \u0633\u0627\u0632 \u062f\u06d5\u06a9\u06d5\u0645 \u0648 \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u0627 \u0641\u0648\u0648\u06cc \u062a\u06ce\u062f\u06d5\u06a9\u06d5\u0645\u060c \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0628\u0627\u06b5\u062f\u0627\u0631\u06cc \u0695\u0627\u0633\u062a \u0648 \u062c\u06af\u06d5 \u0644\u06d5\u0648\u06d5\u0634 \u0632\u06af\u0645\u0627\u06af \u06a9\u0648\u06ce\u0631 \u0648 \u067e\u06ce\u0633\u062a \u0628\u06d5\u06b5\u06d5\u06a9\u0627\u0646 \u0686\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u0645 \u0648 \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u062f\u0627 \u0645\u0631\u062f\u0648\u0648 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u06d5\u0645\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0645\u0627\u06b5\u0648\u0648\u062f\u0627 \u062f\u06d5\u06cc\u062e\u06c6\u0646\u060c \u0647\u06d5\u0686\u06cc \u062a\u06ce\u0634\u06cc\u062f\u0627 \u0695\u0627\u062f\u06d5\u06af\u0631\u0646 \u067e\u06ce\u062a\u0627\u0646 \u062f\u06d5\u06b5\u06ce\u0645. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06d5\u06b5\u06af\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u0646 \u0628\u06c6\u062a\u0627\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u062f\u0627\u0631 \u0628\u0646.',
    '\u0628\u06d5\u0648 \u062a\u06d5\u0648\u0631\u0627\u062a\u06d5\u06cc - \u06a9\u06d5 \u0644\u06d5 \u0644\u0627\u0645\u06d5 - \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695\u0645 \u0648 \u0628\u06d5 \u0626\u06d5\u0631\u06a9\u06cc \u062e\u06c6\u0645\u06cc \u062f\u06d5\u0632\u0627\u0646\u0645 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0634\u062a\u0648\u0648 \u0628\u06c6 \u062d\u06d5\u06b5\u0627\u06b5 \u06a9\u06d5\u0645 \u06a9\u06d5 \u0644\u06ce\u062a\u0627\u0646 \u062d\u06d5\u0631\u0627\u0645 \u06a9\u0631\u0627\u0648\u06d5. \u0645\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u0645 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u0695\u0627 \u0628\u06c6 \u0647\u06ce\u0646\u0627\u0648\u0646\u061b \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0645\u0646 \u062f\u06d5\u0631\u0645\u06d5\u0686\u0646 \u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u062a\u0627\u0646 \u0647\u06d5\u0628\u06ce.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0645\u0646 \u0648 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u062f\u06d5\u0633\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0626\u06d5\u0645\u06d5 \u0695\u06ce\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0633\u06d5\u0631\u0695\u0627\u0633\u06d5.',
    '\u0639\u06cc\u0633\u0627 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u062a\u06ce\u06af\u06d5\u06cc\u06cc\u0634\u062a \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u067e\u06ce \u0646\u0627\u0647\u06ce\u0646\u0646\u060c \u06af\u0648\u062a\u06cc: \u06a9\u06ce \u0628\u06ce \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u06cc\u0627\u0631\u06cc\u0645 \u0628\u06a9\u0627\u061f \u062d\u06d5\u0648\u0627\u0631\u06cc\u06cc\u06d5\u06a9\u0627\u0646 \u062c\u0648\u0627\u0628\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5\u060c \u06a9\u06d5: \u062e\u06c6\u0645\u0627\u0646\u06cc\u0646 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u06cc\u0646. \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06ce\u0646\u0627 \u0648 \u062a\u06c6\u0634 \u0634\u0627\u06cc\u06d5\u062a \u0628\u06d5\u060c \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5\u0648 \u062f\u06d5\u0633\u067e\u06ce\u0631\u06cc\u0646.',
    '\u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af \u0644\u0627\u0645\u0627\u0646! \u0628\u06d5\u0648\u0634\u062a\u06d5\u06cc \u062a\u06c6 \u0628\u06c6 \u0626\u06ce\u0645\u06d5\u062a \u0646\u0627\u0631\u062f\u060c \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u062f \u0648 \u06a9\u06d5\u0648\u062a\u0648\u0648\u06cc\u0646\u06d5 \u0634\u0648\u06ce\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u062a\u061b \u062a\u06c6 \u062f\u06d5\u06af\u06d5\u06b5 \u0634\u0627\u06cc\u06d5\u062a\u06d5\u06a9\u0627\u0646\u062f\u0627 \u0628\u0645\u0627\u0646 \u0646\u0648\u0648\u0633\u06d5.',
    '\u0626\u06d5\u0645\u0627\u0646 \u0641\u06ce\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0648 \u062e\u0648\u062f\u0627\u0634 \u0641\u06ce\u06b5\u06cc \u062e\u06c6\u06cc \u0644\u06ce\u06a9\u0631\u062f\u0646\u061b \u0641\u06ce\u06b5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0641\u06ce\u06b5\u06ce\u06a9\u062f\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u0627 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0639\u06cc\u0633\u0627! \u0645\u0646 \u0628\u06c6 \u062e\u06c6\u0645\u062a \u062f\u06d5\u0628\u06d5\u0645\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u0645 \u062c\u06ce \u0628\u06d5\u0631\u0632 \u062f\u06d5\u0628\u06cc \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5\u062a \u067e\u0627\u06a9 \u062f\u06d5\u06a9\u06d5\u0645\u06d5\u0648\u06d5 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u062a\u06c6\u0646\u060c \u0647\u06d5\u062a\u0627 \u0642\u06cc\u0627\u0645\u06d5\u062a \u0632\u0627\u06b5\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0645 \u0628\u06d5 \u0633\u06d5\u0631 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646\u062f\u0627. \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5 \u06af\u0634\u062a\u0648\u0648 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u0645 \u0648 \u0628\u06d5\u0648 \u06a9\u06ce\u0634 \u06d5\u0648 \u06af\u06ce\u0631\u06d5 \u0695\u0627\u062f\u06d5\u06af\u06d5\u0645 \u06a9\u06d5 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u062a\u0627\u0646\u062f\u0627 \u0695\u0648\u0648\u06cc \u062f\u0627.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u0647\u06ce\u0646\u0627\u06cc\u060c \u0644\u06d5 \u062f\u0646\u06cc\u0627\u0634 \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u0633\u0632\u0627\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0645\u061b \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc \u062a\u06d5\u0648\u0627\u0648\u06cc\u0627\u0646 \u0647\u06d5\u0628\u0648\u0648\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646\u060c \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u0627 \u0648 \u062e\u0648\u0627\u060c \u0633\u062a\u06d5\u0645\u06a9\u0627\u0631\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce\u0646.',
    '\u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06c6 \u062a\u06c6\u0645\u0627\u0646 \u062e\u0648\u06ce\u0646\u062f\u06c6\u062a\u06d5\u0648\u06d5\u060c \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u0646 \u0648 \u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631 \u0646 \u0628\u06c6 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc.',
    '\u0646\u06d5\u0642\u06b5\u06cc \u0639\u06cc\u0633\u0627\u0634 - \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 - \u0644\u06d5 \u0686\u06d5\u0634\u0646\u06cc \u0646\u06d5\u0642\u06b5\u06cc \u0626\u0627\u062f\u06d5\u0645\u06d5\u061b \u0647\u06d5\u0631 \u0644\u06d5\u06af\u06b5 \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f \u0648 \u067e\u0627\u0634\u0627\u0646 \u067e\u06ce\u06cc \u06af\u0648\u062a: \u0628\u0628\u06d5 \u0628\u06d5 \u0626\u0627\u062f\u06d5\u0645! \u0626\u06d5\u0648\u06cc\u0634 \u0628\u0648\u0648.',
    '\u0647\u06d5\u0642 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0695\u0627\u06cc\u06d5 \u0648 \u062e\u06c6\u062a \u0644\u06d5 \u0695\u06ce\u0632\u06cc \u062f\u06b5 \u0628\u06d5\u06af\u0648\u0645\u0627\u0646\u0627\u0646 \u062f\u06d5\u0631\u0627\u0648\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u067e\u0627\u0634 \u0626\u06d5\u0648 \u0632\u0627\u0646\u0633\u062a\u06d5\u06cc \u0628\u06c6 \u062a\u06c6 \u0647\u0627\u062a\u060c \u0648\u06cc\u0633\u062a\u06cc \u06a9\u06ce\u0634\u06d5\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06a9\u0627\u060c \u0628\u06ce\u0698\u06d5: \u0648\u06d5\u0631\u0646 \u0628\u0627 \u06a9\u0648\u0695\u0627\u0646\u0645\u0627\u0646 \u0648 \u0698\u0646\u0627\u0646\u0645\u0627\u0646 \u0648 \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0646 \u0628\u0627\u0646 \u06a9\u06d5\u06cc\u0646 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u062f\u06d5\u0633 \u06a9\u06d5\u06cc\u0646\u06d5 \u0644\u0627\u06b5\u06d5 \u0648 \u0646\u0632\u0627 \u0648 \u0646\u06d5\u0641\u0631\u06cc\u0646\u06cc \u062e\u0648\u0627 \u0628\u0646\u06ce\u0631\u06cc\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u0627 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u0647\u06cc\u0686 \u06af\u0648\u0645\u0627\u0646\u062a \u062a\u06ce\u062f\u0627 \u0646\u06d5\u0628\u06ce\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06c6\u062a \u0628\u0627\u0633 \u06a9\u0631\u0627\u0648\u0646\u060c \u0647\u06d5\u0645\u0648\u0648 \u0695\u0627\u0633\u062a\u0646. \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc\u0634 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0646\u0627\u0628\u06ce \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0628\u06cc\u067e\u06d5\u0631\u0633\u062a\u06ce\u061b \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u062e\u0627\u0648\u06d5\u0646\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5 \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0648\u0627\u0631\u062f\u06cc\u060c \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u062e\u0631\u0627\u067e\u06d5\u06a9\u0627\u0631\u0627\u0646 \u062f\u06d5\u0646\u0627\u0633\u06ce.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628! \u062f\u06d5\u0633\u0627 \u0648\u06d5\u0631\u0646 \u0626\u06ce\u0645\u06d5 \u0648 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0642\u0633\u06d5\u06cc\u06d5 \u0633\u0648\u0648\u0631 \u0628\u06cc\u0646\u060c \u06a9\u06d5 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u0648\u06d5\u06a9 \u06cc\u06d5\u06a9\u06d5: \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627 \u0646\u06d5\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646\u060c \u0647\u0627\u0648\u06a9\u0627\u0631\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u0646\u06d5\u0686\u06cc\u0646 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0645\u0627\u0646 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 - \u062c\u06cc\u0627 \u0644\u06d5 \u062e\u0648\u0627 - \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0647\u06d5\u06b5\u0628\u0698\u06ce\u0631\u06cc\u0646. \u062c\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0648\u0627\u0631\u062f\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u06d5\u06af\u0631\u0645\u060c \u06a9\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0633\u067e\u06ce\u0631\u06cc\u0646.',
    '\u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628! \u0626\u06ce\u0648\u06d5 \u0628\u06c6\u0686\u06cc \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645\u06d5\u0648\u06d5 \u06a9\u06ce\u0634\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u061f \u06a9\u06d5 \u062f\u06d5\u0634\u0632\u0627\u0646\u0646 \u062a\u06d5\u0648\u0631\u0627\u062a \u0648 \u0626\u06cc\u0646\u062c\u06cc\u0644\u06cc\u0634 - \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9 - \u0644\u06d5 \u067e\u0627\u0634 \u0632\u06d5\u0645\u0627\u0646\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0647\u0627\u062a\u0648\u0648\u0646\u06d5 \u062e\u0648\u0627\u0631. \u062f\u06d5 \u0626\u0627\u062e\u0631 \u0628\u06c6\u0686\u06cc \u062a\u06ce\u0646\u0627\u06af\u06d5\u0646\u061f!',
    '\u0648\u0627\u0645\u0627\u0646 \u062f\u0627\u0646\u0627 \u06a9\u06d5 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u06a9\u06ce\u0634\u06d5 \u0628\u06a9\u06d5\u0646 \u06a9\u06d5 \u062f\u06d5\u0632\u0627\u0646\u0646. \u062f\u06d5\u06cc\u0633\u0627 \u0686\u06c6\u0646\u06d5 \u06a9\u06ce\u0634\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0634\u062a\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u0647\u06cc\u0686\u06cc \u0644\u06ce \u062d\u0627\u06b5\u06cc \u0646\u06d5\u0628\u0648\u0648\u0646\u061f \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0632\u0627\u0646\u0627\u06cc\u06d5 \u0628\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0648 \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06cc\u0686\u06cc \u0644\u06ce \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0646\u06d5 \u0645\u0648\u0648\u0633\u0627\u06cc\u06cc \u0628\u0648\u0648 \u0646\u06d5 \u0645\u06d5\u0633\u06cc\u062d\u06cc\u061b \u0628\u06d5\u06b5\u0627\u0645 \u06cc\u06d5\u06a9\u062a\u0627\u067e\u06d5\u0631\u06d5\u0633\u062a\u06ce\u06a9 \u0628\u0648\u0648 \u062e\u06c6\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0633\u067e\u0627\u0631\u062f\u0628\u0648\u0648\u061b \u0647\u06cc\u0686 \u0647\u0627\u0648\u06a9\u0627\u0631\u06cc \u0628\u06c6 \u062e\u0648\u062f\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633 \u0644\u06d5 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0646\u0632\u06cc\u06a9\u062a\u0631\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0626\u06d5\u0648\u0633\u0627 \u0634\u0648\u06ce\u0646\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0648\u062a\u0646 \u0648 \u0626\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u06d5. \u062e\u0648\u062f\u0627\u0634 \u06cc\u0627\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06d5.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u06a9\u062a\u06ce\u0628\u0627\u0646 \u0628\u06d5\u062a\u06d5\u0645\u0627 \u0628\u0648\u0648\u0646 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0695\u06ce \u06a9\u0644\u0627 \u0628\u06a9\u06d5\u0646\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0647\u06d5\u0631 \u0695\u06ce \u0644\u06d5 \u062e\u06c6 \u062f\u06d5\u06af\u06c6\u0695\u0646.',
    '\u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628! \u0628\u06c6\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u062d\u0627\u0634\u0627 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f\u06d5 \u062f\u06d5\u0634\u0632\u0627\u0646\u0646 \u0695\u0627\u0633\u062a\u0646.',
    '\u0626\u06ce\u0648\u06d5 \u06a9\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u06a9\u062a\u06ce\u0628\u0646! \u0686\u06c6\u0646 \u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u062f\u0627\u062f\u06d5\u067e\u06c6\u0634\u0646 \u0648 \u0695\u0627\u0633\u062a \u0648 \u0695\u06d5\u0648\u0627 \u062f\u06d5\u0634\u06ce\u0631\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0634\u0632\u0627\u0646\u0646 \u0686 \u06a9\u0627\u0631\u06ce\u06a9 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062f\u0627\u0646\u06cc \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0628\u06b5\u06ce\u0646 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0628\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u061b \u0633\u06d5\u0631\u0644\u06d5\u0646\u06af\u06c6\u0631\u06ce \u0644\u06ce\u06cc\u0627\u0646 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0628\u0628\u0646\u06d5\u0648\u06d5\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0648\u06d5\u06af\u06d5\u0695\u06ce\u0646.',
    '\u067e\u06ce\u0634\u06cc\u0627\u0646 \u0648\u062a\u0648\u0648\u0646: \u0628\u0627\u0648\u06d5\u0695 \u0647\u06d5\u0631 \u0628\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06a9\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u062f\u06cc\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646\u0646. \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631 \u062e\u0648\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627 \u0648 \u0626\u06d5\u0648 \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce - \u0648\u06d5\u06a9 \u067e\u06ce\u0634\u0648\u0648 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u06a9\u0631\u062f\u0646 - \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062a\u0631\u06cc\u0634 \u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5 \u0634\u0627\u0631\u06d5\u0632\u0627 \u06a9\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u0648\u062a\u06cc\u0627\u0646: \u06a9\u06ce\u0634\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u062f\u06d5\u0628\u06d5\u06cc\u0646\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646\u061b \u0628\u06ce\u0698\u06d5: \u0686\u0627\u06a9\u06d5 \u0628\u06d5 \u062f\u06d5\u0633 \u062e\u0648\u0627\u06cc\u06d5\u060c \u06a9\u06ce\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06cc\u062e\u0648\u06ce\u0646\u06d5\u062a\u06d5\u0648\u06d5 \u0648 \u062e\u0627\u0648\u06d5\u0646 \u0686\u0627\u06a9\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0632\u0627\u0646\u0627 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0626\u06d5\u0648 \u0628\u062e\u0648\u0627\u0632\u06ce \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u062e\u0627 \u0648 \u062e\u0648\u062f\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u0641\u0631\u06d5 \u0648 \u06af\u0631\u06cc\u0646\u06af\u06cc \u0644\u06d5 \u0644\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u062f\u0627 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u06a9\u0644\u0648\u0648\u0631\u06ce\u06a9\u06cc \u0644\u06d5 \u0644\u0627 \u062f\u0627\u0646\u06ce\u060c \u0628\u06d5 \u062f\u06d5\u0633\u067e\u0627\u06a9\u06cc \u062f\u06d5\u062a\u062f\u0627\u062a\u06d5\u0648\u06d5\u061b \u0626\u06cc \u0648\u0627\u0634 \u0647\u06d5\u06cc\u06d5 \u062f\u06cc\u0646\u0627\u0631\u06ce\u06a9\u06cc \u067e\u06ce \u0628\u0633\u067e\u06ce\u0631\u06d5\u060c \u0646\u0627\u062a\u062f\u0627\u062a\u06d5\u0648\u06d5\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5\u062c\u06ce\u06cc \u0646\u06d5\u0647\u06ce\u06b5\u06cc \u0648 \u0647\u06d5\u06b5\u06cc\u067e\u06ce\u0686\u06cc. \u0628\u06d5\u0647\u0627\u0646\u06d5\u06a9\u06d5\u0634\u06cc\u0627\u0646 \u0626\u06d5\u0645\u06d5\u06cc\u06d5: \u0632\u06cc\u0627\u0646 \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646 \u0628\u06d5\u0648 \u0646\u06d5\u062e\u0648\u06ce\u0646\u062f\u06d5\u0648\u0627\u0631\u0627\u0646\u06d5\u060c \u0628\u06c6 \u0645\u06d5 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u06cc \u0646\u06cc\u06cc\u06d5. \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u0627\u0634 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646\u060c \u062f\u06d5\u0634\u0632\u0627\u0646\u0646 \u062f\u0631\u06c6\u06cc\u06d5 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062a\u0631\u0633\u06ce\u06a9\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0628\u06d5\u0631\u06ce\u062a\u06d5 \u0633\u06d5\u0631\u06ce\u060c \u062e\u0648\u0627 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u06cc \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 \u0648 \u0633\u0648\u06ce\u0646\u062f\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u0631\u062e\u06ce\u06a9\u06cc \u06a9\u06d5\u0645 \u062f\u06d5\u0641\u0631\u06c6\u0634\u0646\u060c \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u0647\u06cc\u0686 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5 \u0648 \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u0627 \u0646\u0627\u06cc\u0627\u0646 \u062f\u0648\u06ce\u0646\u06ce \u0648 \u0646\u0627\u06cc\u0627\u0646 \u062f\u06ce\u0646\u06ce \u0648 \u06af\u0648\u0646\u0627\u06cc\u0627\u0646 \u067e\u0627\u06a9 \u0646\u0627\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u062f\u0627 \u0647\u06cc \u0648\u0627\u0634 \u0647\u06d5\u06cc\u06d5\u060c \u062c\u06c6\u0631\u06ce\u06a9 \u0632\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u0695\u06ce \u06af\u06c6\u06cc\u0627 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0646\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u06d5\u0634\u062f\u0627 \u0646\u06cc\u06cc\u06d5. \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06d5\u0645\u06d5 \u062e\u0648\u0627 \u06af\u0648\u062a\u0648\u0648\u06cc\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u062e\u0648\u062f\u0627\u0634 \u0646\u06d5\u06cc\u06af\u0648\u062a\u0648\u0648\u06d5. \u0628\u06c6 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0626\u06d5\u0648\u06d5 \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u062f\u0631\u06c6\u06cc\u06d5 \u0648 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u0627\u0648\u06d5\u06cc \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646.',
    '\u0647\u06cc\u0686 \u0645\u0631\u06c6\u06cc\u06d5\u06a9 \u0626\u06d5\u0645\u06d5\u06cc \u062f\u06d5\u062e\u06c6\u0695\u0627 \u0646\u06d5\u062f\u06cc\u0648\u06d5\u060c \u06a9\u06d5 \u062e\u0648\u0627 \u06a9\u062a\u06ce\u0628\u06cc \u062f\u0627\u0628\u06ce\u062a\u06ce \u0648 \u0641\u06ce\u0631\u06cc \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u06a9\u0631\u062f\u0628\u06ce \u0648 \u06a9\u0631\u062f\u0628\u06ce\u062a\u06cc \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u060c \u067e\u0627\u0634\u0627\u0646 \u0686\u0648\u0648\u0628\u06ce \u0648 \u0628 \u06d5\u062e\u06d5\u06b5\u06a9\u06cc \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0628\u06ce \u06a9\u06d5: \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0628\u0627\u062a\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0639\u06d5\u0628\u062f\u0627\u06cc\u06d5\u062a\u06cc \u0645\u0646 \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u06af\u0648\u062a\u0648\u06cc\u0627\u0646\u06d5: \u0626\u06ce\u0648\u06d5 \u06a9\u06d5 \u0641\u06ce\u0631\u06cc \u06a9\u062a\u06ce\u0628 \u0648 \u062e\u0648\u06ce\u0646\u062f\u0646\u06d5\u0648\u06d5\u06cc \u0628\u0648\u0648\u0646\u060c \u0644\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u0628\u0646\u0627\u0633\u0646.',
    '\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0628\u06d5\u0648\u06d5 \u0646\u06d5\u062f\u0627\u0648\u06d5 \u06a9\u06d5 \u0641\u0631\u0634\u062a\u06d5 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646. \u0626\u0627\u062e\u0631 \u0686\u06c6\u0646 \u062f\u06d5\u0628\u06ce \u0648\u06d5\u062e\u062a\u06ce\u06a9\u06cc \u062f\u06cc\u0646\u06cc \u062e\u0648\u062f\u0627\u0648 \u0633\u06d5\u0644\u0645\u0627\u0646\u062f\u0628\u06ce\u060c \u067e\u06ce\u062a\u0627\u0646 \u0628\u06b5\u06ce\u0646: \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u0644\u06d5 \u062f\u06cc\u0646 \u0648\u06d5\u0631\u06af\u06d5\u0695\u06ce\u0646\u061f',
    '[\u0628\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06cc\u0631] \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5 \u062e\u0648\u0627 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0628\u06d5\u0633\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u0627: \u0626\u06d5\u0631 \u0645\u0646 \u06a9\u062a\u06ce\u0628 \u0648 \u0632\u0627\u0646\u0633\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u0645 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u0627 \u0648 \u067e\u0627\u0634\u0627\u0646\u06d5 \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u0647\u0627\u062a\u06d5 \u0646\u0627\u0648\u062a\u0627\u0646 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u0628\u06d5\u0695\u0627\u0633\u062a \u0632\u0627\u0646\u06cc\u060c \u062f\u06d5\u0628\u06ce \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce \u0628\u06ce\u0646\u0646 \u0648 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062f\u06d5\u0646. \u0626\u06d5\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u0628\u06d5\u06b5\u06ce\u060c \u0626\u06d5\u0648\u0627 \u0648\u06d5\u0639\u06c6\u062f\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u06af\u0631\u062a. \u06af\u0648\u062a\u06cc: \u062f\u06d5\u0633\u0627 \u0628\u06d5 \u0634\u0627\u06cc\u06d5\u062a \u0628\u0646\u061b \u0626\u06d5\u0648\u0627 \u0645\u0646\u06cc\u0634 \u062f\u06d5\u06af\u06d5\u06b5\u062a\u0627\u0646\u0645.',
    '\u06a9\u06d5\u0633\u0627\u0646\u06ce \u0644\u06d5\u0645\u06d5 \u0628\u06d5\u0648\u0644\u0627\u0648\u06d5 \u0644\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u067e\u0627\u0634\u06af\u06d5\u0632 \u0628\u0628\u0646\u06d5\u0648\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u0646.',
    '\u0626\u0627\u062e\u06c6 \u062f\u06cc\u0646\u06ce\u06a9 \u0628\u06ce\u0698\u06af\u06d5 \u062f\u06cc\u0646\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0648\u06ce\u061f \u06a9\u06d5\u0686\u06cc \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u062f\u0627 - \u0686\u0627\u0631 \u0648 \u0646\u0627\u0686\u0627\u0631 - \u0645\u0644\u06a9\u06d5\u0686\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0648\u06cc\u0646 \u0648 \u062f\u06d5\u0634\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u0627\u0648\u06d5\u0695\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0628\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5\u0648\u0695\u0627 \u0628\u06c6\u0645\u0627\u0646 \u0647\u0627\u062a\u0648\u0648\u06d5 \u0648 \u0628\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648\u06d5 \u0628\u06c6 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644 \u0648 \u0626\u06cc\u0633\u062d\u0627\u0642 \u0648 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628 \u0647\u0627\u062a\u0628\u0648\u0648\u061b \u0628\u06c6 \u0646\u06d5\u062a\u06d5\u0648\u06d5\u06a9\u06d5\u0634 \u0647\u06d5\u0631\u0648\u06d5\u062a\u0631\u061b \u0628\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06c6\u0639\u06cc\u0633\u0627 \u0648 \u0645\u0648\u0648\u0633\u0627 \u0648 \u06af\u0634 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u062f\u06cc \u0647\u0627\u062a. \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u0641\u06d5\u0631\u0642 \u0648 \u062c\u0648\u062f\u0627\u06cc\u06cc\u0645\u0627\u0646 \u0646\u06cc\u06cc\u06d5 \u0648 \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0633\u067e\u06ce\u0631\u06cc\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5 \u0626\u06cc\u0633\u0644\u0627\u0645\u060c \u0647\u06d5\u0631 \u062f\u06cc\u0646\u06ce\u06a9\u06cc \u062f\u06cc\u06a9\u06d5\u06cc \u0647\u06d5\u0628\u06ce\u060c \u0642\u0628\u0648\u0648\u06b5 \u0646\u06cc\u06cc\u06d5 \u0648 \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u0627 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06d5.',
    '\u062c\u0627 \u0686\u06c6\u0646 \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06af\u06d5 \u06a9\u06d5 \u067e\u0627\u0634 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0647\u06ce\u0646\u0627\u0646 \u0648 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u0627\u0646 \u06a9\u06d5: \u00ab\u0626\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u0695\u0627\u0633\u062a\u06d5\u00bb \u0648 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06cc\u0648\u06d5\u060c \u0633\u06d5\u0631 \u0644\u06d5\u0646\u0648\u06ce \u062d\u0627\u0634\u0627\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u061f \u062e\u0648\u062f\u0627 \u0642\u06d5\u062a \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u06a9\u0627.',
    '\u062c\u06d5\u0632\u0627\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06d5\u0641\u0631\u06cc\u0646\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u0641\u0631\u0634\u062a\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u0648\u06d5\u061b',
    '\u062a\u0627 \u0647\u06d5\u062a\u0627\u06cc\u06d5 \u0644\u06d5\u0645 \u0646\u06d5\u0641\u0631\u06cc\u0646\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0633\u0648\u0648\u06a9 \u0646\u0627\u0628\u06ce \u0648 \u0645\u0627\u0648\u06d5 \u067e\u06ce\u062f\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u0648\u0648 \u062c\u0627\u0631 - \u0628\u06c6 \u062e\u06c6 - \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u06af\u0648\u0646\u0627\u0647\u0640 \u0628\u06d5\u062e\u0634\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0648\u0627\u06cc \u0628\u0695\u0648\u0627\u0647\u06ce\u0646\u0627\u0646 \u0648\u06d5\u0631\u06af\u06d5\u0695\u0627\u0646 \u0648 \u0647\u06d5\u0631 \u0648\u0627 \u0645\u0627\u0646 \u0648 \u067e\u06ce\u0648\u06d5 \u0686\u0648\u0648\u0646\u060c \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u062a\u0627\u0632\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u0627\u062f\u0627 \u0648 \u0686\u0648\u0648\u0646\u06d5\u062a\u06d5 \u0646\u0627\u0648 \u06af\u0648\u0645\u0695\u0627\u0647\u06cc\u06cc\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646 \u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u0645\u0631\u062f\u0648\u0648\u0646\u060c \u0647\u06d5\u0631\u06a9\u0627\u0645\u06ce\u06a9\u06cc\u0627\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u067e\u0695\u06cc \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0632\u06ce\u0695\u06cc\u0634 \u0628\u06d5 \u0628\u0627\u0631\u0645\u062a\u06d5 \u0628\u062f\u0627\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u067e\u06ce \u0646\u0627\u06af\u06d5\u06cc\u0646\u06ce\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce\u062a \u0648 \u06a9\u06d5\u0633\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0647\u0627\u0646\u0627 \u0646\u0627\u06cc\u06d5.',
    '\u062a\u0627 \u0644\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062e\u06c6\u0634\u062a\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06d5\u060c \u0646\u06d5\u0628\u06d5\u062e\u0634\u0646 \u0644\u06d5\u0695\u06ce\u06cc \u062e\u0648\u062f\u0627\u062f\u0627\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0647\u06cc\u0686 \u062e\u06ce\u0631\u0648\u0648 \u067e\u06ce \u0646\u0627\u06af\u0627. \u0647\u06d5\u0631\u0634\u062a\u06ce\u06a9\u06cc\u0634 \u0628\u06d5 \u062e\u06ce\u0631 \u0628\u06cc\u062f\u06d5\u0646 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627\u062f\u0627\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u0647\u06d5\u0645\u0648\u0648 \u062c\u06c6\u0631\u06ce \u062e\u0648\u0627\u0631\u062f\u06d5\u0645\u06d5\u0646\u06cc \u0628\u06c6 \u0646\u06d5\u062a\u06d5\u0648\u06d5\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644 \u0695\u06d5\u0648\u0627 \u06a9\u0631\u0627\u0648\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644 \u0628\u06d5\u0631 \u0644\u06d5\u0645\u06d5 \u062a\u06d5\u0648\u0631\u0627\u062a\u06cc \u0628\u0648\u0648\u0628\u06ce\u060c \u062e\u06c6\u06cc \u067e\u0627\u0631\u06ce\u0632\u06cc \u0644\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u062a\u0646\u060c \u062a\u06d5\u0648\u0631\u0627\u062a \u0628\u06ce\u0646\u0646 \u0628\u06cc\u062e\u0648\u06ce\u0646\u0646\u06d5\u0648\u06d5.',
    '\u0633\u0627 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u0644\u06d5\u0645\u06d5 \u0628\u06d5\u0648\u0644\u0627 \u062f\u0631\u06c6 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06a9\u06d5\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631\u0686\u06cc \u062e\u0648\u062f\u0627 \u06af\u0648\u062a\u0648\u0648\u06cc\u06d5\u060c \u0626\u06d5\u0648 \u0695\u0627\u0633\u062a\u06d5\u061b \u0626\u06cc\u062a\u0631 \u0626\u06ce\u0648\u06d5 \u0634\u0648\u06ce\u0646 \u062f\u06cc\u0646\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u06a9\u06d5\u0648\u0646\u061b \u06a9\u06d5 \u062e\u0648\u0627\u06cc \u062a\u0627\u0642\u0627\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0633\u062a \u0648 \u0647\u06cc\u0686 \u0647\u0627\u0648\u06a9\u0627\u0631\u06cc \u0628\u06c6 \u0646\u06d5\u0646\u0627\u0633\u06cc.',
    '\u06cc\u06d5\u06a9\u06d5\u0645\u06cc\u0646 \u0645\u0627\u06b5 \u06a9\u06d5 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0633\u0627\u0632 \u06a9\u0631\u0627\u0648\u06d5\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u0648\u0627 \u0644\u06d5 \u0645\u06d5\u06a9\u06a9\u06d5\u061b \u06a9\u06d5 \u0632\u06c6\u0631 \u0628\u06d5 \u067e\u06cc\u062a \u0648 \u067e\u06cc\u0631\u06c6\u0632\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u0627\u0633\u06cc\u0646\u06d5 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062f\u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0634\u0648\u06ce\u0646\u06d5\u0648\u0627\u0631\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u067e\u06d5\u0646\u0627\u06cc \u067e\u06ce \u0628\u06d5\u0631\u06ce\u060c \u0646\u0627\u0634\u06ce \u0644\u06d5 \u0647\u06cc\u0686 \u0634\u062a \u0628\u062a\u0631\u0633\u06ce. \u062e\u0648\u062f\u0627 \u062d\u06d5\u062c\u06cc \u0628\u0695\u06cc\u0627\u0631 \u062f\u0627\u0648\u06d5 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0628\u06c6\u06cc \u0628\u0644\u0648\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062d\u0627\u0634\u0627 \u0628\u06a9\u0627\u060c \u062e\u0648\u0627 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628! \u0628\u06c6\u0686\u06cc \u0644\u06d5\u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u0648\u0627 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628! \u0644\u06d5\u0628\u06d5\u0631\u0686\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u0627 \u0644\u0627\u062f\u06d5\u062f\u06d5\u0646 \u0648 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 \u0626\u06d5\u0645 \u0695\u06ce\u06af\u0627\u06cc\u06d5 \u0644\u0627\u0631 \u0648 \u0646\u0627\u0695\u06ce\u06a9 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u0646\u061f! \u062e\u06c6\u0634\u062a\u0627\u0646 \u0628\u0627\u0634 \u0626\u0627\u06af\u0627\u062a\u0627\u0646 \u0644\u06ce\u06cc\u06d5 \u0648 \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u062e\u0627\u0641\u06b5 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u0642\u0633\u06d5\u06cc \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646\u060c \u062f\u06d5\u0633\u062e\u06d5\u0631\u06c6 \u0628\u0646\u060c \u0644\u06d5 \u067e\u0627\u0634 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0647\u06ce\u0646\u0627\u0646\u062a\u0627\u0646 \u062f\u06d5\u062a\u0627\u0646\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633.',
    '\u062c\u0627 \u0686\u06c6\u0646 \u0626\u06ce\u0648\u06d5 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646! \u06a9\u06d5\u0686\u06cc \u0626\u0627\u06cc\u06d5\u062a\u0627\u0646\u06cc \u062e\u0648\u0627\u062a\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u062a\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u062f\u0627\u06cc\u06d5\u061f \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u067e\u0634\u062a \u0628\u06d5 \u062e\u0648\u0627 \u0628\u0628\u06d5\u0633\u062a\u06ce\u060c \u06a9\u06d5\u0648\u062a\u06c6\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06af\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0686\u06c6\u0646 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5\u060c \u0626\u0627\u0648\u0627 \u0644\u06d5 \u062e\u0648\u0627 \u0628\u062a\u0631\u0633\u0646 \u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u06cc \u0628\u0645\u0631\u0646.',
    '\u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u062a\u0627\u0646 \u062f\u06d5\u0633 \u062f\u06d5 \u0695\u0634\u062a\u06d5\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0648\u06d5\u0631\u06ce\u0646\u0646 \u0648 \u0628\u06b5\u0627\u0648 \u0645\u06d5\u0628\u0646. \u0628\u06cc\u0631 \u0644\u06d5\u0648 \u0686\u0627\u06a9\u06d5 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u06a9\u0631\u062f\u0646: \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u0698 \u0628\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u0628\u0648\u0648\u0646\u060c \u0628\u06d5\u062f\u06b5 \u06a9\u0631\u062f\u0648\u0648\u0646\u06cc \u0628\u06d5 \u0647\u06c6\u06af\u0631\u061b \u0644\u06d5 \u0633\u0627\u06cc\u06d5\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u0626\u06d5\u0645\u06d5\u0648\u06d5\u060c \u0626\u06ce\u0633\u062a\u0627 \u0644\u06ce\u06a9\u062a\u0631 \u0628\u0648\u0648\u0646\u06d5 \u0628\u0631\u0627 \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u06ce\u0648\u0627\u0631\u06cc \u0626\u0627\u06af\u0631 \u0628\u0648\u0648\u0646\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u0648\u0648 \u0695\u0632\u06af\u0627\u0631\u06cc \u06a9\u0631\u062f\u0646. \u062e\u0648\u0627 \u0626\u0627\u0648\u0627 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0695\u0648\u0648\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0695\u06ce\u06af\u0627 \u0628\u062f\u06c6\u0632\u0646\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u0628\u06ce \u0644\u06d5 \u0646\u06ce\u0648 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9 \u0628\u0628\u0646\u060c \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0628\u062f\u06d5\u0646 \u0648 \u0695\u06ce \u0644\u06d5 \u062e\u0631\u0627\u067e\u06d5 \u0628\u0628\u06d5\u0633\u062a\u0646\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u0628\u0646.',
    '\u0646\u06d5\u06a9\u0627 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06a9\u06d5\u0646 \u06a9\u06d5 \u0644\u06ce\u06a9\u062a\u0631 \u062c\u06cc\u0627\u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u0627\u0646 \u062f\u06cc\u062a\u060c \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u062a\u06ce\u06a9\u06d5\u0648\u062a. \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5 \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0686\u06d5\u0631\u0645\u06af\u06d5 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u062a\u0631 \u0695\u06d5\u0634 \u062f\u0627\u06af\u06d5\u0695\u0627\u0648\u0646\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0695\u0648\u0648\u0695\u06d5\u0634\u06cc \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u0646\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0648\u06d5 \u062f\u0648\u0627\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc \u0647\u06ce\u0646\u0627\u0646 \u0686\u06c6\u0646 \u0628\u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u061f \u0633\u0627 \u0644\u06d5\u0648 \u062d\u0627\u0634\u0627\u06a9\u0631\u062f\u0646\u06d5\u062a\u0627\u0646 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u06af\u06c6\u0695\u06cc \u06af\u06cc\u0627\u0646\u0648\u0648 \u0628\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0695\u0648\u0648 \u0686\u06d5\u0631\u0645\u0648\u0648\u06af\u0646\u060c \u0628\u06c6 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u062a\u0627 \u0647\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u060c \u0628\u06d5\u0695\u0627\u0633\u062a\u0646 \u0648 \u0626\u06ce\u0645\u06d5 \u0628\u06c6\u062a\u06cc \u0648\u06d5\u062f\u06d5\u062e\u0648\u06ce\u0646\u06cc\u0646. \u0647\u06cc\u0686 \u06af\u0627 \u062e\u0648\u062f\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0646\u0627\u06a9\u0627.',
    '\u0647\u06d5\u0631\u0686\u06ce \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5 - \u06af\u0634 \u0644\u06d5 \u06af\u0634\u062a\u06cc - \u0633\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627\u06cc\u06d5\u061b \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631\u06cc \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0648\u06d5 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u06a9\u06c6\u0645\u06d5\u06b5\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0645\u06d5\u0631\u062f\u0645\u06cc \u062f\u0646\u06cc\u0627\u062f\u0627 \u0633\u06d5\u0631\u0648\u0648 \u0647\u06d5\u06b5\u062f\u0627\u061b \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0695\u06ce \u0644\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u062f\u06d5\u0628\u06d5\u0633\u062a\u0646. \u0628\u0627\u0648\u06d5\u0695\u06cc\u0634\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u06cc\u0634 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u0647\u06ce\u0646\u0627\u0628\u0627\u060c \u0628\u06c6\u06cc\u0627\u0646 \u0686\u0627\u06a9\u062a\u0631 \u0628\u0648\u0648. \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0626\u06cc \u0648\u0627\u06cc\u0627\u0646 \u062a\u06ce\u062f\u0627 \u06a9\u06d5 \u0628\u06d5\u0628\u0695\u0648\u0627\u0646\u061b \u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631\u06cc\u0634 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 - \u0645\u06d5\u06af\u06cc\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0645 - \u062f\u06d5\u0646\u0627 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0628\u06c6\u062a\u0627\u0646 \u0628\u06d5\u0632\u06d5\u0695 \u0628\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u06af\u0698\u06cc\u0634\u0648\u0648 \u0695\u0627\u0628\u0686\u0646\u060c \u0632\u06c6\u0631 \u0632\u0648\u0648 \u067e\u0634\u062a\u0648\u0648 \u062a\u06ce \u062f\u06d5\u06a9\u06d5\u0646. \u0695\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u0644\u06d5 \u0647\u06d5\u0631 \u062c\u06ce\u0634 \u0628\u0646\u060c \u0647\u06d5\u0631 \u0698\u06ce\u0631 \u0686\u06d5\u067e\u06c6\u06a9\u06cc \u0648 \u0632\u06d5\u0628\u0648\u0648\u0646\u06cc \u0628\u06c6\u062a\u06d5 \u0628\u06d5\u0634\u06cc\u0627\u0646\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0647\u06d5\u0631 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 \u0648 \u062e\u06d5\u06b5\u06a9\u0627 \u0628\u06af\u0627\u062a\u06d5 \u0641\u0631\u06cc\u0627\u06cc\u0627\u0646\u06d5\u0648\u06d5. \u062a\u0648\u0648\u0634\u06cc \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u062e\u0648\u0627 \u0628\u0648\u0648\u0646 \u0648 \u0645\u06c6\u0631\u06cc \u0632\u06d5\u0628\u0648\u0648\u0646\u06cc\u06cc\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u062f\u0627 \u0646\u0631\u0627\u0648\u06d5\u061b \u0644\u06d5\u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5 \u062d\u0627\u0634\u0627 \u0628\u0648\u0648\u0646 \u0648 \u0628\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0648\u0634\u062a \u0648 \u0647\u06d5\u0631 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0648 \u062f\u06d5\u0633 \u062f\u0631\u06ce\u0698\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u06af\u0634 \u0648\u06d5\u06a9 \u06cc\u06d5\u06a9 \u0646\u06cc\u0646\u061b \u06a9\u06c6\u0645\u06d5\u06b5\u06cc \u0648\u0627\u06cc\u0627\u0646 \u062a\u06ce\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0695\u0648\u0648 \u0628\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u0648\u06ce\u0633\u062a\u0646 \u0648 \u0628\u06d5 \u0634\u06d5\u0648\u0627 \u0626\u0627\u06cc\u06d5\u062a\u0627\u0646\u06cc \u062e\u0648\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0648 \u0633\u06d5\u0631 \u062f\u06d5\u062e\u06d5\u0646\u06d5 \u0633\u06d5\u0631 \u0633\u0648\u0698\u062f\u06d5\u0648\u06d5\u061b',
    '\u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0695\u06ce\u06af\u06d5 \u0644\u06d5 \u062e\u06d5\u0631\u0627\u067e\u06d5 \u062f\u06d5\u0628\u0695\u0646 \u0648 \u0628\u06c6 \u062e\u06ce\u0631 \u06a9\u0631\u062f\u0646 \u0645\u0644\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u06a9\u06d5\u0646\u060c \u0628\u06ce \u0628\u06d5\u0647\u0631\u06d5 \u0646\u0627\u0645\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u062e\u0648\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06ce\u06cc \u062f\u06d5\u062a\u0631\u0633\u0646\u060c \u0628\u0627\u0634 \u062f\u06d5\u0646\u0627\u0633\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0646\u06d5\u0646\u0627\u0633\u0646 \u062f\u0627\u0631\u0627\u06cc\u06cc \u0648 \u0645\u0627\u06b5 \u0648 \u0645\u0646\u0627\u06b5\u06cc\u0627\u0646 \u0647\u06cc\u0686 \u062f\u06d5\u0631\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0645\u0627\u0646 \u0646\u0627\u06a9\u0627 \u0648 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5\u0647\u0631\u06d5 \u0646\u0627\u062f\u0627\u061b \u062c\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u062c\u06d5\u062d\u06d5\u0646\u062f\u06d5\u0645\u06d5 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0695\u0648\u0648\u0628\u06cc\u0646\u06cc \u0644\u06d5 \u0698\u06cc\u0627\u0646\u062f\u0627 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u062e\u0634\u0646\u060c \u0647\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0634\u06d5\u062e\u062a\u06d5 \u0648\u0627\u06cc\u06d5 \u06a9\u06d5 \u0628\u06c6 \u06a9\u06ce\u06b5\u06af\u06d5\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0648\u06d5\u06cc\u0634\u0648\u0648\u0645\u06d5\u06cc\u06d5. \u062e\u0648\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06ce\u0698\u06af\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u06d5\u0633\u0627 \u0645\u06d5\u0628\u0646\u06d5 \u0647\u0627\u0645\u0695\u0627\u0632. \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u06a9\u0627\u0631 \u0644\u06ce \u062a\u06ce\u06a9\u062f\u0627\u0646\u0648\u0648 \u062f\u0631\u06ce\u063a \u0646\u0627\u06a9\u06d5\u0646\u061b \u0647\u06d5\u0631 \u062d\u06d5\u0632 \u062f\u06d5\u06a9\u06d5\u0646 \u062a\u0648\u0648\u0634\u06cc \u0646\u0627\u0695\u06d5\u062d\u06d5\u062a\u06cc\u062a\u0627\u0646 \u06a9\u06d5\u0646. \u062c\u06af\u06d5 \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u062f\u0698\u0627\u06cc\u06d5\u062a\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0645\u062f\u0627 \u062f\u06ce\u062a\u060c \u063a\u06d5\u0631\u06d5\u0632\u06ce\u06a9\u06cc \u0644\u06d5 \u062f\u06b5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u0627\u06cc\u06d5 \u0641\u0631\u06d5\u062a\u0631\u06d5. \u0626\u06d5\u0631 \u062a\u06ce\u0628\u06af\u06d5\u0646\u060c \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0631\u062e\u0633\u062a\u0648\u0648\u06d5.',
    '\u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0646! \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648\u0627\u0646\u062a\u0627\u0646 \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce \u0648 \u0626\u06d5\u0648\u0627\u0646 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u06cc. \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u0634\u062a\u0627\u0646 \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5 - \u06af\u0634\u062a\u06cc - \u0647\u06d5\u0633. \u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06ce\u06a9\u06cc\u0634 \u067e\u06ce\u062a\u0627\u0646 \u062f\u06d5\u06af\u06d5\u0646\u060c \u0626\u06ce\u0698\u0646: \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631 \u0644\u06ce\u0648\u0648 \u062a\u06d5\u06a9\u06cc\u0646\u06d5\u0648\u06d5 - \u0644\u06d5 \u06a9\u06cc\u0646\u06d5\u06cc\u06ce \u06a9\u06d5 \u0647\u06d5\u06cc\u0627\u0646\u06d5 - \u0633\u06d5\u0631\u067e\u06d5\u0646\u062c\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u0632\u0646. \u0628\u06ce\u0698\u06d5: \u062f\u06d5\u06a9 \u0644\u06d5 \u0695\u06a9\u0627\u0646 \u0628\u0645\u0631\u0646! \u062e\u0648\u0627 \u0628\u06d5\u0648\u06d5\u06cc \u0648\u0627 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5 \u0628\u0627\u0634 \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u062a\u0627 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0628\u06ce\u062a\u06d5 \u0695\u06ce\u060c \u062e\u06d5\u0645\u06ce \u062f\u06d5\u062e\u06c6\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u0646\u0627\u062e\u06c6\u0634\u06cc \u0628\u0646\u060c \u06a9\u06d5\u06cc\u0641\u06ce \u062f\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0648 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631 \u0628\u0646\u060c \u06af\u0632\u06cc \u0648 \u0641\u06ce\u06b5\u06cc\u0627\u0646 \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u062f\u0627. \u062e\u0648\u0627 \u0628\u06d5 \u06a9\u0631\u062f\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u062a\u06c6 - \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u06cc\u0627\u0646 - \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06cc \u062e\u06c6\u062a \u062c\u06ce \u0647\u06ce\u0634\u062a \u0648 \u062c\u06ce\u06af\u06d5\u062a \u0628\u06c6 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0644\u06d5 \u0634\u06d5\u0695\u06af\u06d5\u062f\u0627 \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u06a9\u0631\u062f\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0628\u06cc\u0633\u062a \u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06cc.',
    '\u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634\u062f\u0627 \u06a9\u06d5 \u062f\u0648\u0648 \u062f\u06d5\u0633\u062a\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u0648\u0631\u06d5\u0628\u06d5\u0631\u062f\u0627\u0646 \u0628\u0648\u0648\u0646\u060c \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0647\u0627\u062a. \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627 \u0628\u0633\u067e\u06ce\u0631\u0646.',
    '\u0648\u0627 \u062f\u06cc\u062a\u06cc\u0634\u0648\u0648 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0634\u06d5\u0695\u06cc \u0628\u06d5\u062f\u0631\u0627 \u0633\u06d5\u0631\u06cc \u062e\u0633\u062a\u0646\u061b \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u06a9\u06d5\u0645 \u0648 \u0628\u06ce \u0647\u06ce\u0632\u06cc\u0634 \u0628\u0648\u0648\u0646. \u0633\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u0648\u0648 \u0628\u06ce\u061b \u0628\u06d5\u0634\u06a9\u06d5\u0645 \u0634\u0648\u06a9\u0631\u06cc \u0628\u06d5\u062c\u06ce \u0628\u06ce\u0646\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u062a\u06c6 \u0628\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646\u062a \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f: \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0633\u06ce \u0647\u06d5\u0632\u0627\u0631 \u0644\u06d5 \u0641\u0631\u06cc\u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u0646\u06ce\u0631\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631\u060c \u0628\u06ce\u0646\u06d5 \u06cc\u0627\u0631\u06cc\u0648\u060c \u0628\u06d5\u0633\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u0628\u06d5\u06b5\u06ce \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u0695\u0627\u06af\u0631\u0646 \u0648 \u062a\u0631\u0633\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce\u060c \u0644\u06d5 \u062c\u06d5\u0646\u06af\u06d5\u06cc \u067e\u06d5\u0644\u0627\u0645\u0627\u0631\u062f\u0627\u0646\u06cc \u062f\u0648\u0698\u0645\u0646\u0648\u0648\u062f\u0627\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u067e\u06ce\u0646\u062c \u0647\u06d5\u0632\u0627\u0631 \u0644\u06d5 \u0641\u0631\u0634\u062a\u0627\u0646\u06cc\u060c \u0628\u06d5\u062f\u0631\u06c6\u0634\u0645 \u0648 \u0628\u06d5\u06a9\u06c6\u0645\u06d5\u06a9\u06d5\u0648\u06d5 \u062f\u06d5\u0646\u06ce\u0631\u06ce.',
    '\u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5 \u0645\u0632\u06af\u06ce\u0646\u06ce\u06a9 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u062f\u0627\u062a \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5 \u062f\u06b5\u0646\u06cc\u0627 \u0628\u0646\u060c \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc \u06a9\u0631\u062f. \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062e\u0648\u062f\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u062e\u0648\u0627 \u062f\u06d5\u06cc\u06d5\u0648\u06cc\u0633\u062a \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u062e\u0648\u06ce\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06af\u06d5\u0648\u0632\u0646 \u0648 \u0626\u06d5\u0648\u06cc \u0645\u0627\u0634\u0646 \u062f\u06d5\u0645\u06a9\u0648\u062a \u0628\u0646 \u0648 \u0628\u06d5 \u0646\u0627\u0647\u0648\u0645\u06ce\u062f\u06cc \u0648\u06d5\u06af\u06d5\u0695\u06ce\u0646.',
    '\u0626\u06cc\u062a\u0631 \u0644\u06d5 \u0634\u06d5\u0695 \u062f\u06c6\u0695\u0627\u0648\u06d5\u06a9\u0627\u0646 - \u0686 \u0644\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u0646 \u0648 \u062e\u0648\u0627 \u0628\u06cc\u0627\u0646 \u0628\u0648\u0648\u0631\u06ce\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u0695\u06cc\u0627\u0631 \u0628\u062f\u0627 - \u062a\u06c6 \u06a9\u0627\u0631\u06ce\u06a9\u062a \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u0646\u06cc\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u062f\u0627\u06cc\u06d5\u060c \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5. \u06a9\u06ce \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u0644\u06ce\u06cc \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u06a9\u06ce\u0634 \u062d\u06d5\u0632 \u06a9\u0627 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u062f\u0627\u060c \u062e\u06c6\u06cc \u062f\u06d5\u0632\u0627\u0646\u06ce. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5 \u0633\u0648\u0648\u062a \u062e\u0648\u0627\u0631\u062f\u0646 \u0628\u062a\u06d5\u06a9\u0646\u06d5\u0648\u06d5 \u0648 \u0686\u06cc\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0686\u06cc\u0646 \u0648\u06d5\u0631\u06cc \u0645\u06d5\u06af\u0631\u0646. \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u062a\u0627\u0646 \u0647\u06d5\u0628\u06ce\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06af\u06d5\u0646\u06d5 \u0695\u0632\u06af\u0627\u0631\u06cc.',
    '\u062e\u06c6\u0634\u062a\u0627\u0646 \u062f\u0648\u0648\u0631\u06d5\u067e\u06d5\u0631\u06ce\u0632 \u0628\u06af\u0631\u0646 \u0644\u06d5\u0648 \u0626\u0627\u06af\u0631\u06d5\u06cc \u0646\u06ce\u06b5 \u062f\u0631\u0627\u0648\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646.',
    '\u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0626\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u0634 \u0628\u0646\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u06a9\u06d5\u0648\u0646.',
    '\u067e\u06d5\u0644\u06d5 \u0628\u06a9\u06d5\u0646 \u0628\u06c6 \u06af\u06d5\u06cc\u0634\u062a\u0646 \u0628\u06d5 \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646 \u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06ce \u06a9\u06d5 \u0647\u06d5\u0631\u0627\u0648\u06cc \u0628\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0646\u06d5 \u0648 \u0633\u0627\u0632 \u06a9\u0631\u0627\u0648\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062e\u0648\u0627\u062a\u0631\u0633\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5 \u0648 \u0641\u06d5\u0631\u062d\u0627\u0646\u06cc\u062f\u0627\u060c \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06d5\u062e\u0634\u0646 \u0648 \u0695\u06a9\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062e\u06c6\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0686\u0627\u0648\u067e\u06c6\u0634\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u061b \u062e\u0648\u0627 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u06cc \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0647\u06d5\u0631\u06af\u0627 \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06cc\u0627\u0646 \u0646\u0627\u062d\u06d5\u0642\u06cc \u0644\u06d5 \u062e\u06c6 \u062f\u06d5\u06a9\u06d5\u0646 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0628\u06cc\u0631 \u0646\u0627\u0628\u06d5\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5\u0628\u06d5\u0631\u06cc \u062f\u06d5\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5: \u0644\u06d5 \u062e\u06d5\u062a\u0627\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u06ce\u061b \u0628\u06ce\u0698\u06af\u06d5 \u062e\u0648\u062f\u0627\u0634 \u06a9\u06ce \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce\u061f \u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u06a9\u06d5 \u0632\u0627\u0646\u06cc\u0648\u06cc\u0627\u0646\u06d5 \u0647\u06d5\u06b5\u06d5\u06cc\u06d5\u060c \u067e\u06ce \u062f\u0627\u06af\u0631 \u0646\u06cc\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0648\u0648\u0631\u062f\u0646\u06d5 \u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06d5 \u06a9\u06d5 \u062c\u06c6\u0628\u0627\u0631\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u06cc\u0627\u0646\u062f\u0627 \u062f\u06d5\u0695\u06c6\u0646 \u0648 \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u0628\u0627\u063a\u0627\u062a\u06d5 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5. \u0628\u06d5\u06d5! \u0686\u06d5\u0646\u062f\u06d5 \u062c\u0648\u0627\u0646\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646.',
    '\u0628\u06d5\u0631 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0632\u06c6\u0631 \u0695\u06ce\u06af\u0627 \u0648 \u0634\u0648\u06ce\u0646 \u062a\u06ce\u067e\u06d5\u0695\u06cc\u0648\u0646. \u062c\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 \u06af\u06d5\u0634\u062a\u06ce \u0628\u06a9\u06d5\u0646 \u0648 \u0633\u06d5\u0631\u0646\u062c \u0628\u062f\u06d5\u0646 \u06a9\u06d5 \u0626\u0627\u062e\u0631\u06cc \u06a9\u0627\u0631\u06cc \u0647\u06d5\u0642 \u0628\u06d5\u062f\u0631\u06c6\u0632\u0627\u0646\u0627\u0646 \u0628\u06d5 \u06a9\u0648\u06ce \u06af\u06d5\u06cc\u0634\u062a\u061f',
    '\u0626\u06d5\u0645\u06d5 \u067e\u06d5\u0646\u062f\u06cc \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9 \u062a\u06ce\u062f\u0627\u06cc\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0648 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632 \u0628\u0646\u060c \u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631\u06d5.',
    '\u0628\u0627 \u0633\u0633\u062a\u06cc \u0695\u0648\u0648\u062a\u0627\u0646 \u062a\u06ce \u0646\u06d5\u06a9\u0627 \u0648 \u062e\u06d5\u0645\u06cc\u0634 \u0645\u06d5\u062e\u06c6\u0646. \u0647\u06d5\u0631 \u0626\u06ce\u0648\u06d5\u0646 \u06a9\u06d5 \u0633\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u0648 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u0631\u06cc\u0646\u062f\u0627\u0631 \u0628\u0648\u0648\u0646\u060c \u062f\u0648\u0698\u0645\u0646\u06cc\u0634\u062a\u0627\u0646 \u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5 \u0628\u0631\u06cc\u0646\u062f\u0627\u0631 \u0628\u0648\u0648\u06d5. \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u0627 \u0632\u06d5\u0645\u0627\u0646\u06d5 \u062f\u06d5\u0633\u062a\u0627\u0648\u062f\u06d5\u0633 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0628\u0646\u0627\u0633\u06ce \u0648 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u06cc\u0634 \u0628\u06c6 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u0628\u06d5\u06cc\u0646\u062a\u0627\u0646\u0627 \u0647\u06d5\u06b5\u0628\u0698\u06ce\u0631\u06ce. \u062e\u0648\u062f\u0627 \u062e\u06c6\u0634\u06cc \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0647\u06d5\u06b5\u0627\u0648\u06ce\u0631\u06ce \u0648 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u06d5\u0631\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u0627\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u0633\u06d5\u0631\u0628\u06d5\u062e\u06c6 \u062f\u06d5\u0686\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u061f \u06a9\u06d5\u0686\u06cc \u0647\u06ce\u0634\u062a\u0627 \u062e\u0648\u062f\u0627 \u062e\u06d5\u0628\u0627\u062a\u06a9\u0627\u0631\u0627\u0646 \u0648 \u062e\u06c6\u0695\u0627\u06af\u0631\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u062f\u06cc\u0627\u0631\u06cc \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0645\u0631\u062f\u0646 \u0628\u0646\u060c \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0645\u0631\u062f\u0646\u0648\u0648 \u062f\u06d5\u06a9\u0631\u062f. \u0633\u0627 \u0626\u06d5\u0648\u0627 \u0645\u06d5\u0631\u06af\u06d5 \u0647\u0627\u062a\u0648\u0648\u06d5 \u0648 \u0626\u06ce\u0648\u06d5 \u0632\u06d5\u0642 \u062a\u0645\u0627\u0634\u0627\u06cc \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0645\u0648\u062d\u06d5\u0645\u0645\u06d5\u062f \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06d5 \u0648 \u0686\u06cc\u062a\u0631 \u0646\u06cc\u06cc\u06d5\u061b \u0644\u06d5 \u067e\u06ce\u0634 \u0626\u06d5\u0648\u06cc\u0634 \u0632\u06c6\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0647\u0627\u062a\u0648\u0648\u0646 \u0648 \u0686\u0648\u0648\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0645\u0631\u06ce \u06cc\u0627\u0646 \u0628\u06a9\u0648\u0698\u0631\u06ce\u060c \u0644\u06d5 \u0626\u06cc\u0633\u0644\u0627\u0645 \u067e\u0627\u0634\u06af\u06d5\u0632 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5\u061f \u0647\u06d5\u0631\u06a9\u06d5\u0633 \u067e\u0627\u0634\u06af\u06d5\u0632 \u0628\u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0632\u06cc\u0627\u0646\u06ce\u06a9 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0627\u06af\u06d5\u06cc\u0646\u06ce. \u062e\u0648\u062f\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u0633\u0648\u067e\u0627\u0633\u06af\u0648\u0632\u0627\u0631\u0627\u0646\u06cc \u062f\u06d5\u062f\u0627.',
    '\u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u06d5 \u0628\u06ce \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u062f\u0627 \u0646\u0627\u0645\u0631\u06ce. \u06a9\u06d5 \u0645\u0631\u062f\u0646\u06cc \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0648\u06d5\u062e\u062a\u06d5\u06a9\u06d5\u06cc \u0628\u06c6 \u062f\u0627\u0646\u0631\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u0648\u06d5 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5. \u06a9\u06ce\u0634 \u0647\u06d5\u0631 \u062e\u06c6\u0634\u06cc \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u0628\u06ce\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u062f\u0627\u062a\u06ce\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u06c6\u0634\u06cc \u0626\u06d5\u0648 \u062f\u0646\u06cc\u0627\u06cc \u06af\u06d5\u0631\u06d5\u06a9 \u0628\u06ce\u060c \u062e\u0648\u062f\u0627 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u067e\u06ce\u06a9\u062f\u06ce\u0646\u06ce. \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0648\u0627 \u0634\u0648\u06a9\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0632\u0648\u0648 \u062c\u06d5\u0632\u0627\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u06af\u06d5\u0644\u06cc \u062e\u0648\u0627\u067e\u06d5\u0631\u0633\u062a\u0627\u0646\u06cc \u0647\u06d5\u06a4\u0627\u06b5\u06cc\u0627\u0646 \u0686\u0648\u0648\u0646\u06d5 \u062e\u06d5\u0632\u0627\u060c \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u0628\u06d5\u06b5\u0627\u06cc \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u062a\u0648\u0648\u0634\u06cc \u0647\u0627\u062a\u0646\u060c \u0646\u06d5 \u0647\u06cc\u0686\u06cc\u0627\u0646\u06cc \u0633\u0633\u062a\u06d5\u0648\u06d5 \u06a9\u0631\u062f \u0646\u06d5 \u0632\u06d5\u0628\u0648\u0648\u0646\u06cc \u0695\u0648\u0648\u06cc \u062a\u06ce\u06a9\u0631\u062f\u0646. \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062e\u06c6\u0695\u0627\u06af\u0631\u0646\u060c \u062e\u0648\u0627 \u062e\u06c6\u0634\u06cc \u062f\u06ce.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc\u0627\u0646 \u062f\u0631\u06a9\u0627\u0646\u062f\u060c \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u062a\u06c6 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u0648 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0644\u0627\u062f\u0627\u0646\u0645\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u06d5 \u0648 \u062c\u06ce \u0647\u06d5\u0646\u06af\u0627\u0648\u0645\u0627\u0646 \u067e\u062a\u06d5\u0648\u062a\u0631 \u06a9\u06d5 \u0648 \u0628\u06d5 \u0633\u06d5\u0631 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u062f\u0627 \u0632\u0627\u06b5\u0645\u0627\u0646 \u0628\u06a9\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u06d5\u0645 \u062f\u0646\u06cc\u0627 \u0648 \u0686\u0627\u06a9\u06d5\u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u06d5\u0648 \u062f\u0646\u06cc\u0627\u06cc \u067e\u06ce\u062f\u0627\u0646. \u062e\u0648\u0627 \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u0634\u0648\u06ce\u0646 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u06a9\u06d5\u0648\u0646\u060c \u0628\u06d5\u0631\u06d5\u0648 \u067e\u0627\u0634\u0648\u0648 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u062a\u0648\u0648\u0634\u06cc \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u06cc \u062f\u06d5\u0628\u0646.',
    '\u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648 \u0686\u0627\u06a9\u062a\u0631 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0686\u0648\u0646\u06a9\u06d5 \u0628\u06ce \u0626\u06d5\u0648\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0628\u06c6 \u0647\u0627\u062a\u0628\u06ce \u0647\u0627\u0648\u06a9\u0627\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0646\u0627\u0633\u0646\u060c \u062a\u0631\u0633\u06ce \u062f\u0627\u0648\u06ce\u0698\u06cc\u0646\u06d5 \u062f\u06b5\u06cc\u0627\u0646\u061b \u0626\u06d5\u0646\u0648\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631\u06d5 \u0648 \u062c\u06ce\u06cc \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u062e\u0631\u0627\u067e\u062a\u0631\u06cc\u0646 \u062c\u06ce\u06af\u06d5 \u062f\u06d5\u0628\u06ce.',
    '\u062e\u0648\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u0627 \u0648 \u0628\u06d5\u062c\u06ce\u06cc \u0647\u06ce\u0646\u0627: \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u0648\u0648\u0631\u06cc \u062e\u0648\u0627 \u062f\u06d5\u062a\u0627\u0646 \u06a9\u0648\u0634\u062a\u0646\u060c \u062a\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u0626\u06d5\u0648 \u062a\u0627\u06b5\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u062a\u06d5\u0645\u0627\u06cc \u0628\u0648\u0648\u0646 \u0647\u0627\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u060c \u0626\u06cc\u062a\u0631 \u06a9\u06ce\u0634\u06d5\u0648 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u062a\u0627\u0646 \u0644\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u0628\u0648\u0648\u061b \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc\u062a\u0627\u0646 \u06a9\u0631\u062f. \u0626\u06cc \u0648\u0627 \u0647\u06d5\u0628\u0648\u0648 \u062d\u06d5\u0632\u06cc \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062f\u0646\u06cc\u0627 \u0628\u0648\u0648\u060c \u0626\u06cc \u0648\u0627\u0634 \u0647\u06d5\u0628\u0648\u0648\u0646 \u062e\u06c6\u0634\u06cc \u0626\u06d5\u0648 \u062f\u0646\u06cc\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0648\u06cc\u0633\u062a. \u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0631 \u062a\u0627 \u0628\u062a\u0627\u0646 \u0626\u06d5\u0632\u0645\u0648\u06ce\u060c \u0644\u06d5 \u0634\u06d5\u0695\u06cc\u0627\u0646\u06cc \u06af\u06ce\u0695\u0627\u0646\u06d5\u0648\u06d5. \u0648\u0627 \u0626\u06d5\u0648 \u0647\u06d5\u0644\u06d5\u0634 \u0644\u06ce\u062a\u0627\u0646 \u062e\u06c6\u0634 \u0628\u0648\u0648. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646\u06cc \u0686\u0627\u06a9\u06d5\u06cc\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u06a9\u06d5 \u0695\u0627\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f \u0648 \u0626\u0627\u0648\u0695\u062a\u0627\u0646 \u0648\u06d5\u0633\u06d5\u0631 \u0647\u06cc\u0686\u06a9\u06d5\u0633 \u0646\u06d5\u062f\u06d5\u062f\u0627\u0648\u06d5\u060c \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634 \u0644\u06d5 \u062f\u0648\u0627\u0648\u06d5 \u06af\u0627\u0632\u06cc \u062f\u06d5\u06a9\u0631\u062f\u0646\u061b \u062c\u0627 \u062e\u0648\u0627 \u062c\u06d5\u0632\u0627\u06cc \u062e\u06d5\u0645\u06cc \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u06d5\u0645 \u062f\u0627\u0646\u06d5\u0648\u06d5. \u0646\u06d5\u06a9\u0627 \u0644\u06d5 \u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u0648\u0627 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u0627\u0646 \u0686\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5\u0648\u0627 \u062a\u0648\u0648\u0634\u062a\u0627\u0646 \u0628\u0648\u0648\u06d5 \u062e\u06d5\u0641\u06d5\u062a \u0628\u062e\u06c6\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u067e\u0627\u0634 \u0626\u06d5\u0648 \u062e\u06d5\u0645\u06d5 \u0626\u0627\u0631\u0627\u0645\u06cc\u06cc\u06d5\u06a9\u06cc \u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631\u062a\u0627\u0646\u060c \u06a9\u06d5 \u0628\u0631\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u0626\u06c6\u0642\u0631\u06d5\u06cc \u062e\u06d5\u0648 \u0644\u06ce \u06a9\u06d5\u0648\u062a\u0646 \u0628\u0648\u0648\u060c \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u0648\u0648 \u062f\u0627\u0647\u0627\u062a\u061b \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u062e\u06c6\u062a\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u0648\u0648. \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u0634\u06cc\u0646 \u0628\u06cc\u0631\u0648\u0648 \u0644\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u06a9\u0631\u062f\u06d5\u0648\u06d5\u060c \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u0627\u062e\u06c6 \u0644\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0686\u0646 \u0628\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u061f \u0628\u06ce\u0698\u06d5: \u06af\u0634 \u06a9\u0627\u0631 \u0628\u06d5 \u062f\u06d5\u0633 \u062e\u0648\u062f\u0627\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u062f\u06b5\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u062a\u06c6 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0646\u0627\u06a9\u06d5\u0646\u061b \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u0628\u0648\u0648\u0627\u06cc\u06d5\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06ce\u0631\u06d5 \u0646\u06d5\u062f\u06d5\u06a9\u0648\u0698\u0631\u0627\u06cc\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u0634\u062a\u0627\u0646 \u0628\u0627\u0646\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u0648\u0634\u062a\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5\u060c \u062f\u06d5\u0647\u0627\u062a\u0646\u06d5\u0648\u06d5 \u062c\u06ce\u06cc \u062a\u062e\u06ce\u06b5 \u0628\u0648\u0648\u0646\u06cc \u062c\u06d5\u0646\u062f\u06d5\u06a9\u06d5\u06cc\u0627\u0646. \u062e\u0648\u0627 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u062f\u06b5\u062a\u0627\u0646 \u062a\u0627\u0642\u06cc \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u062f\u06b5\u062a\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u062f\u06d5\u0631\u06cc \u0647\u0627\u0648\u06ce. \u062e\u0648\u0627 \u0644\u06d5 \u0695\u0627\u0632\u06cc \u062f\u06b5\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u062a\u0627\u0646\u06d5 \u0648\u0627 \u0695\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f - \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u062f\u0648\u0648 \u06a9\u06c6\u0645\u06d5\u06b5 \u06af\u06d5\u06cc\u0634\u062a\u0646\u06d5 \u06cc\u06d5\u06a9 - \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0647\u06d5\u06b5\u06cc\u062e\u0644\u06cc\u0633\u06a9\u0627\u0646\u062f\u0646\u060c \u0644\u06d5 \u0628\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u06a9\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u061b \u062e\u0648\u0627\u06cc\u0634 \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc. \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u062e\u06c6\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06d5\u0628\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0646\u0627\u0633\u06cc \u0648 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0628\u0631\u0627\u06a9\u0627\u0646\u06cc\u0627\u0646 - \u06a9\u06d5 \u0644\u06d5 \u0633\u06d5\u0641\u06d5\u0631\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0634\u06d5\u0695\u06cc \u062f\u0648\u0698\u0645\u0646\u0627\u0646 \u0628\u0648\u0648\u0646 - \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u0627\u0645\u0627\u0646 \u0645\u0627\u0628\u0627\u0646\u060c \u0646\u06d5\u062f\u06d5\u0645\u0631\u062f\u0646\u060c \u0646\u06d5\u062f\u06d5\u06a9\u0648\u0698\u0631\u0627\u0646. \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u0628\u0648\u0648 \u062e\u0648\u062f\u0627 \u0628\u06cc\u06a9\u0627 \u0628\u06d5 \u0626\u0627\u062e \u0648 \u062f\u0627\u062e\u06cc \u0633\u06d5\u0631 \u062f\u06b5\u06cc\u0627\u0646. \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0698\u06cc\u0646\u06ce \u062f\u06d5\u062f\u0627 \u0648 \u062f\u06d5\u0634\u0645\u0631\u06ce\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u0628\u0645\u0631\u0646\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5 \u06a9\u0648\u0634\u062a \u0686\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06ce \u062e\u06c6\u0634\u0628\u0648\u0648\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0626\u06d5\u0648 \u0632\u06c6\u0631 \u0686\u0627\u06a9\u062a\u0631\u06d5 \u0644\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u06a9\u06c6\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0686 \u0628\u0645\u0631\u0646 \u0648 \u0686 \u0628\u06a9\u0648\u0698\u0631\u06ce\u0646\u060c \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u0644\u0627\u06cc \u062e\u0648\u0627 \u06a9\u06c6\u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627\u0695\u0627\u06cc\u06d5 \u06a9\u06d5 \u062a\u06c6 \u0647\u06ce\u0646\u062f\u06d5 \u0646\u06d5\u0631\u0645 \u0648\u0646\u06cc\u0627\u0646\u06cc \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce \u062f\u06b5\u0695\u06d5\u0642 \u0648 \u062a\u0648\u0646\u062f \u0648 \u062a\u06c6\u0633\u0646 \u0628\u0627\u06cc\u060c \u0628\u06b5\u0627\u0648\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u0631\u062f\u06cc. \u062a\u06c6 \u0628\u06cc\u0627\u0646 \u0628\u0648\u0648\u0631\u06d5 \u0648 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u062f\u0627\u0648\u0627\u06cc \u0628\u0648\u0648\u0631\u062f\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06a9\u06d5 \u0648 \u0644\u06d5 \u06a9\u0627\u0631 \u062a\u06d5\u06af\u0628\u06cc\u0631\u06cc\u0627\u0646 \u067e\u06ce \u0628\u06a9\u06d5. \u0648\u06d5\u062e\u062a\u06ce \u0644\u06ce \u0628\u0695\u0627\u06cc \u06a9\u0627\u0631\u06ce \u0628\u06a9\u06d5\u06cc\u060c \u0647\u06d5\u0631 \u0628\u06d5 \u062a\u06d5\u0645\u0627\u06cc \u062e\u0648\u0627 \u062a\u06ce\u06a9\u06d5\u0648\u06d5\u061b \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u062f\u06d5\u0633\u067e\u06ce\u0631\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0648 \u062f\u06d5\u0647\u0627\u0646\u0627 \u0628\u06ce\u060c \u06a9\u06d5\u0633 \u0628\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646\u062f\u0627 \u0632\u0627\u06b5 \u0646\u0627\u0628\u06ce\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0648\u0627\u0632\u06cc\u0634\u062a\u0627\u0646 \u0644\u06ce \u0628\u06ce\u0631\u06ce\u060c \u06a9\u06ce\u06cc\u06d5 \u067e\u0627\u0634\u0627\u0646 \u067e\u06ce\u06cc \u062f\u06d5\u06a9\u0631\u06ce \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0648 \u0628\u06a9\u0627\u061f \u0628\u0627 \u0647\u0627\u0646\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u06ce \u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0648\u06cc \u0628\u0633\u067e\u06ce\u0631\u0646.',
    '\u0628\u06c6 \u0647\u06cc\u0686 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u0646\u0627\u0634\u06ce \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06d5\u06b5\u06a9\u060c \u0641\u06ce\u06b5 \u0648 \u0641\u06d5\u0631\u06d5\u062c\u0627\u0646 \u0628\u0646\u0648\u06ce\u0646\u06ce\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0641\u06ce\u06b5\u0628\u0627\u0632\u06cc \u0648 \u062f\u06d5\u0633\u0628\u0695\u06cc \u0628\u06a9\u0627 - \u0628\u06d5\u0648 \u062a\u06d5\u0634\u0642\u06d5\u06b5\u06d5\u0648\u06d5 \u06a9\u06d5 \u062f\u06d5\u06cc\u06a9\u0627 - \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u062f\u06d5\u0628\u06cc\u0646\u062f\u0631\u06ce \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0644\u06d5 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u06cc \u0633\u0632\u0627 \u062f\u06d5\u062f\u0631\u06ce \u0648 \u0647\u06cc\u0686 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u0647\u06d5\u0631 \u0628\u06c6 \u0695\u06d5\u0632\u0627\u06cc \u062e\u0648\u0627 \u06a9\u06c6\u0634\u06cc\u0648\u06d5\u060c \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc \u0644\u06d5\u062e\u06c6\u06cc \u062a\u0648\u0648\u0695\u06d5\u06a9\u0631\u062f\u061f \u06a9\u06d5 \u0626\u06d5\u0646\u0648\u0627\u06cc \u0626\u06d5\u0645 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06d5\u0648 \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06cc \u062e\u0631\u0627\u067e\u06cc \u0647\u06d5\u0633.',
    '\u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0626\u06d5\u0648 \u062f\u0648\u0648\u062c\u0648\u0648\u0646\u0627\u0646\u06d5 \u062e\u0648\u062f\u0627 \u0686\u06d5\u0646\u062f \u067e\u0644\u06d5\u06cc \u062f\u0627\u0646\u0627\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0631\u062f\u06d5\u06cc\u0627\u0646 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06d5.',
    '\u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0628\u06d5\u0631 \u0645\u0646\u06d5\u062a\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u061b \u06a9\u06d5 \u06cc\u06d5\u06a9\u06ce\u06a9 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0627\u0646 \u0628\u06ce\u061b \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u067e\u0627\u06a9\u0698\u06cc \u0695\u0627\u06cc\u0627\u0646 \u062f\u06d5\u06af\u0631\u06ce \u0648 \u0644\u06d5 \u06a9\u062a\u06ce\u0628 \u0648 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u062f\u0627 \u0695\u0627\u06cc\u0627\u0646 \u062f\u06ce\u0646\u06ce\u061b \u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u0647\u06cc\u0686\u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06cc\u0686 \u0646\u06d5\u062f\u06d5\u0632\u0627\u0646\u06cc.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u0627\u062a\u06ce \u0632\u06cc\u0627\u0646\u0648\u0648 \u0644\u06d5 \u062f\u0648\u0698\u0645\u0646 \u067e\u06ce\u06af\u06d5\u06cc\u0634\u062a - \u06a9\u06d5 \u062c\u0627\u0631\u06ce\u06a9\u06cc\u0634 \u062f\u0648\u0648 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0648 \u0632\u06cc\u0627\u0646 \u0644\u06ce\u062f\u0627\u0646 - \u0647\u06cc\u0686 \u067e\u0631\u0633\u06cc\u062a\u0627\u0646: \u0632\u06cc\u0627\u0646\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u0686\u06cc \u0628\u0648\u0648\u061f \u0628\u06ce\u0698\u06d5: \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0628\u0648\u0648\u060c \u062e\u0648\u0627 \u0628\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u062a\u0648\u0648\u0634\u0648\u0648 \u0647\u0627\u062a - \u0648\u0627 \u062f\u0648\u0648 \u06a9\u06c6\u0645\u06d5\u06b5 \u0628\u06d5\u06cc\u06d5\u06a9 \u06af\u06d5\u06cc\u06cc\u0646 - [\u06af\u0634\u062a\u06cc] \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u060c \u0647\u06d5\u062a\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u0627\u061b',
    '\u062a\u0627 \u062f\u0648\u0648\u0695\u0648\u0648\u06af\u06d5\u0644\u06cc\u0634 \u0628\u0646\u0627\u0633\u06ce\u061b \u067e\u06ce\u0646 \u0648\u062a\u0631\u0627: \u0626\u06ce\u0648\u06d5\u0634 \u0648\u06d5\u0631\u0646 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u062e\u06d5\u0632\u0627 \u0628\u06a9\u06d5\u0646\u060c \u06cc\u0627\u0646 \u0628\u0628\u0646\u06d5 \u0647\u06ce\u0632\u06cc \u0628\u06d5\u0631\u06af\u0631\u06cc. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0646\u0632\u0627\u0646\u06cc\u0628\u0627 \u0634\u06d5\u0695\u0648\u0648 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0633\u06d5\u067e\u0627\u0648\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0648\u06d5\u062f\u0648\u0648\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u062a\u06cc\u0646. \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0644\u06d5 \u0628\u06ce \u062f\u06cc\u0646\u06cc \u0646\u0632\u06cc\u06a9\u062a\u0631 \u0628\u0648\u0648\u0646 \u062a\u0627 \u0644\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627. \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u062f\u06d5\u0645 \u062f\u06d5\u06cc\u06b5\u06ce\u0646\u060c \u0626\u06d5\u0648\u06d5 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0695\u0627\u0632\u06d5 \u0648 \u0644\u06d5 \u062f\u06b5\u06cc\u0627\u0646\u062f\u0627\u06cc\u06d5\u061b \u062e\u0648\u0627 \u0644\u06d5\u0648\u06d5\u06cc \u062f\u06d5\u06cc\u0634\u0627\u0631\u0646\u06d5\u0648\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0631\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u06d5\u0686\u0648\u0648\u0628\u0648\u0648\u0646\u06d5 \u062e\u06d5\u0632\u0627\u060c \u0644\u06d5\u0645\u06d5\u0695 \u0647\u0627\u0648\u06d5\u06b5\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06af\u0648\u062a: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0642\u0633\u06d5\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0628\u0627\u06cc\u0646 \u0646\u06d5\u062f\u06d5\u06a9\u0648\u0698\u0631\u0627\u0646. \u0628\u06ce\u0698\u06d5: \u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u062a \u062f\u06d5\u06a9\u06d5\u0646\u060c \u062e\u06c6 \u0644\u06d5 \u0645\u0631\u062f\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627\u062f\u0627 \u06a9\u0648\u0698\u0631\u0627\u0648\u0646\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u0628\u06ce \u0628\u06d5\u0645\u0631\u062f\u0648\u0648\u06cc\u0627\u0646 \u062d\u06cc\u0633\u0627\u0628 \u0628\u06a9\u06d5\u0646\u061b \u0647\u06d5\u0631 \u0632\u06cc\u0646\u062f\u0648\u0648\u0646 \u0648 \u062e\u0648\u062f\u0627 \u0628\u0698\u06cc\u0648\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06ce\u061b',
    '\u062f\u06b5\u06cc\u0627\u0646 \u062e\u06c6\u0634\u06d5 \u0628\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646. \u0634\u0627\u062f\u0646 \u0628\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u0627 \u0646\u06d5\u06af\u06d5\u06cc\u0634\u062a\u0648\u0648\u0646\u06d5 \u0626\u06d5\u0645\u0627\u0646 \u0648 \u0628\u06d5 \u062c\u06ce \u0645\u0627\u0648\u0646\u060c \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u0627\u0628\u06ce \u0648 \u0647\u06cc\u0686 \u062e\u06d5\u0645\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0631 \u0646\u0627\u0628\u06ce.',
    '\u062f\u06b5\u06cc\u0627\u0646 \u062e\u06c6\u0634\u06d5 \u0628\u06d5 \u0646\u06cc\u0639\u0645\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0632\u06cc\u0627\u062f\u06d5 \u0628\u06d5 \u0634\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648. \u0628\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u062e\u0648\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0648\u0646\u062f\u0627 \u0646\u0627\u06a9\u0627.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u067e\u0627\u0634 \u0628\u0631\u06cc\u0646\u062f\u0627\u0631 \u0628\u0648\u0648\u0646\u06cc\u0634 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0627\u0646 \u0628\u06d5\u062c\u06ce \u0647\u06ce\u0646\u0627\u060c \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0627\u0646\u06d5 \u0648\u0627 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0648 \u0644\u06d5 \u062e\u0648\u0627 \u062a\u0631\u0633\u0627\u0646\u060c \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u06ce\u06cc\u0627\u0646 \u0648\u062a\u0646: \u06af\u06d5\u0644\u06d5 \u06a9\u06c6\u0645\u06d5\u06af\u06cc\u0648 \u0644\u06ce \u06a9\u0631\u0627\u0648\u06d5 \u0648 \u0628\u06d5\u067e\u0627\u0646\u06d5\u0648\u06d5 \u062f\u06ce\u0646\u06d5 \u0633\u06d5\u0631\u062a\u0627\u0646\u060c \u0626\u06d5\u0634\u06ce \u062a\u0631\u0633\u0648\u0648 \u0644\u06ce\u06cc\u0627\u0646 \u0647\u06d5\u0628\u06ce\u061b \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5\u0647\u06ce\u0632\u062a\u0631 \u0628\u0648\u0648\u06d5 \u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u0645\u0627\u0646 \u0628\u06ce \u0628\u06d5\u0633\u0645\u0627\u0646\u06d5 \u0648 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u067e\u0634\u062a \u0648 \u067e\u06d5\u0646\u0627\u0645\u0627\u0646 \u062a\u06d5\u0646\u06cc\u0627 \u0626\u06d5\u0648\u06d5.',
    '\u0647\u0627\u0648\u0695\u06ce \u062f\u06d5\u06af\u06d5\u06b5 \u0646\u06cc\u0639\u0645\u06d5\u062a \u0648 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627\u062f\u0627 - \u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u062a\u0648\u0648\u0634\u06cc \u0632\u06cc\u0627\u0646 \u0628\u0646 - \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u06b5\u0648\u06d5\u062f\u0627\u06cc \u0695\u06d5\u0632\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u0646\u061b \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u0634\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u06af\u0631\u06cc\u0646\u06af\u06cc \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5.',
    '\u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc\u062f\u0627 \u0626\u06d5\u0648\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06d5\u06a4\u0648 [\u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5 \u0626\u06ce\u0648\u06d5] \u0644\u06d5 \u0647\u0627\u0648\u062f\u06d5\u0633\u062a\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u062a\u0631\u0633\u06ce\u0646\u06ce. \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648\u0627\u0646 \u062a\u0631\u0633\u0648\u0648 \u0646\u06d5\u0628\u06ce \u0648 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0646 \u062f\u06d5\u0628\u06ce \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u0644\u06d5 \u0645\u0646 \u0628\u062a\u0631\u0633\u0646.',
    '\u0628\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646 \u0645\u06d5\u0628\u06d5 \u06a9\u06d5 \u0628\u06d5 \u067e\u06d5\u0644\u06d5 \u0644\u06d5 \u062f\u0698\u06cc \u062f\u06cc\u0646 \u062a\u06ce \u062f\u06d5\u06a9\u06c6\u0634\u0646\u061b \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646\u06ce\u06a9\u06cc \u0644\u06d5 \u062e\u0648\u0627 \u0628\u062f\u06d5\u0646. \u062e\u0648\u0627 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u0628\u06d5\u0634\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u06d5\u0628\u06ce \u0648 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u0698\u0648\u0627\u0631 \u0628\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0628\u0627\u062a\u06cc \u0628\u0695\u0648\u0627 \u0628\u06d5 \u062e\u0648\u062f\u0627\u060c \u0628\u06ce \u0628\u0695\u0648\u0627\u06cc\u06cc \u0647\u06d5\u06b5\u062f\u06d5\u0628\u0698\u06ce\u0631\u0646\u060c \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627 \u0646\u0627\u062f\u06d5\u0646 \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0628\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 \u0648\u0627 \u0628\u06cc\u0631 \u0646\u06d5\u0628\u06d5\u0646 \u0626\u06d5\u0648 \u0645\u06c6\u06b5\u06d5\u062a\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u062f\u06d5\u06cc\u0646\u06ce \u0686\u0627\u06a9\u06d5 \u0628\u06c6\u06cc\u0627\u0646\u061b \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u0645\u06c6\u06b5\u06d5\u062a\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646 \u062a\u0627 \u062a\u0627\u0648\u0627\u0646\u06cc\u0627\u0646 \u0641\u0631\u06d5\u0648\u06d5 \u0628\u06ce. \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0626\u0627\u0648\u0695\u0648\u0648\u0628\u06d5\u0631\u06cc\u0634 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u062e\u0648\u0627 \u0647\u06cc\u0686 \u06a9\u0627\u062a\u06ce \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06cc \u0644\u06d5 \u0628\u0627\u0631\u0648\u062f\u06c6\u062e\u06ce\u06a9\u0627 - \u0648\u0627 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0646 - \u0646\u06d5\u0647\u06ce\u0634\u062a\u06c6\u062a\u06d5\u0648\u06d5\u061b \u0645\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u0646\u0627\u067e\u0627\u06a9\u0627\u0646 \u0644\u06d5 \u067e\u0627\u06a9\u0627\u0646 \u0647\u06d5\u06b5\u0627\u0648\u06ce\u0631\u06ce. \u062e\u0648\u062f\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u067e\u06ce\u0648\u0627\u0631 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0646\u0627\u06a9\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc\u062f\u0627\u060c \u06a9\u06ce\u06cc \u0628\u0648\u0648\u06ce \u0647\u06d5\u06b5\u06cc \u062f\u06d5\u0628\u0698\u06ce\u0631\u06ce. \u062f\u06d5\u0628\u06ce \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u0648 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06ce \u0648 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u0627\u0648\u06d5\u0695\u06d5\u0648 \u0647\u06ce\u0646\u0627 \u0648 \u062e\u06c6\u0634\u062a\u0627\u0646 \u067e\u0627\u0631\u0627\u0633\u062a\u060c \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u06cc\u06d5\u06a9\u062c\u0627\u0631 \u06af\u0695\u06cc\u0646\u06af\u0648\u0648 \u067e\u06ce \u062f\u06d5\u062f\u0631\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0686\u0646\u06c6\u06a9\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u0644\u06d5\u0648 \u0628\u06d5\u0634\u06d5\u06cc \u062e\u0648\u062f\u0627 \u067e\u06ce\u06cc \u062f\u0627\u0648\u0646\u060c \u062e\u06d5\u06cc\u0627\u0644 \u0646\u06d5\u06a9\u06d5\u0646 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0628\u06c6 \u0648\u0627\u0646 \u0642\u0627\u0632\u0627\u0646\u062c\u06d5\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u062e\u0631\u0627\u067e\u06d5. \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u062a\u06c6\u0642 \u062f\u06d5\u0645\u0644\u06cc\u0627\u0646\u062f\u0627 \u0648 [\u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5] \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0645\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0648\u062a\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06cc\u0633\u062a \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646 \u06af\u0648\u062a: \u062e\u0648\u0627 \u0647\u06d5\u0698\u0627\u0631\u06d5 \u0648 \u0626\u06ce\u0645\u06d5 \u062f\u0627\u0631\u0627\u06cc\u0646. \u0648\u0627 \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0645 \u06af\u0641\u062a\u06d5\u06cc\u0627\u0646 \u0648 \u0628\u06d5 \u0646\u0627\u062d\u06d5\u0642 \u06a9\u0648\u0634\u062a\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u062a\u06c6\u0645\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u06cc\u0646: \u0633\u0627 \u0628\u0686\u06ce\u0698\u0646 \u062f\u06d5\u0631\u062f\u06cc \u0628\u06d5 \u0626\u0627\u06af\u0631 \u0633\u0648\u0648\u062a\u0627\u0646\u06ce\u061b',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648 \u062c\u06c6\u0631\u06d5 \u06a9\u0627\u0631\u0627\u0646\u06d5\u0648 \u062f\u06d5\u06a9\u0631\u062f\u061b \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u063a\u06d5\u062f\u0631 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u06cc \u0646\u0627\u06a9\u0627.',
    '\u0626\u06d5\u0648\u06cc \u06af\u0648\u062a\u06cc\u0627\u0646: \u00ab\u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u067e\u06ce\u0646 \u0633\u067e\u06ce\u0631\u0627\u0648\u06d5 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u0647\u06cc\u0686 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u0646\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06d5\u06cc \u0628\u06d5 \u062d\u06d5\u06cc\u0648\u0627\u0646\u06ce \u0642\u0648\u0631\u0628\u0627\u0646\u06cc\u06cc\u06d5\u0648\u06d5 \u0628\u06ce\u062a\u06d5 \u0644\u0627\u0645\u0627\u0646\u060c \u0626\u0627\u06af\u0631 \u0628\u06cc\u062e\u0648\u0627\u00bb\u060c \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0642\u0633\u06d5\u0648 \u0695\u0627\u0633\u062a\u06d5\u060c \u0626\u06d5\u062f\u06cc \u0628\u06c6\u0686\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u0634 \u0645\u0646 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u06ce\u0646\u0627\u0646 \u0648 \u0626\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0633\u062a\u0627\u0634 \u0626\u06ce\u0648\u06d5 \u062f\u0627\u0648\u0627\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u0631\u062f\u06cc\u0627\u0646\u060c \u06a9\u06d5\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u06a9\u0648\u0634\u062a\u0648\u0648\u062a\u0627\u0646\u0646\u061f',
    '\u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0628\u0695\u0648\u0627 \u0628\u06d5 \u062a\u06c6 \u0646\u0627\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u067e\u06ce\u0634 \u062a\u06c6\u0634 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f. \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u0628\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0648 \u0646\u0648\u0633\u0631\u0627\u0648\u06af\u06d5\u0644 \u0648 \u06a9\u062a\u06ce\u0628\u06cc \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u0631\u06d5\u0648\u06d5 \u0647\u0627\u062a\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646.',
    '\u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u06ce\u06a9 \u062a\u0627\u0645\u06cc \u0645\u0631\u062f\u0646\u06ce \u062f\u06d5\u0686\u06ce\u0698\u06ce\u061b \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 \u0647\u06d5\u0642\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0646\u06ce\u0646\u06d5 \u0645\u0634\u062a. \u06a9\u06ce \u0644\u06d5 \u0626\u0627\u06af\u0631 \u06a9\u0644\u0627\u06a9\u0631\u06ce \u0648 \u0628\u0628\u0631\u06ce\u062a\u06d5\u0648\u06d5 \u0646\u06ce\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u0648\u06d5\u060c \u062f\u06d5\u06cc\u0628\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06d5. \u0626\u06d5\u0645 \u0698\u06cc\u0627\u0646\u06d5\u06cc \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627 \u0628\u0646\u06d5\u0645\u0627\u06cc \u0641\u0631\u06cc\u0648\u06d5.',
    '\u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06af\u06cc\u0627\u0646 \u0648 \u062f\u0627\u0631\u0627\u06cc\u06cc \u0628\u06d5\u0631 \u062a\u0627\u0642\u06cc\u06a9\u0627\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646 \u0648 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u0627\u062f\u06d5\u0646\u06ce\u0646\u060c \u0632\u06c6\u0631 \u0642\u0633\u06d5\u06cc \u0646\u0627\u062e\u06c6\u0634 \u062f\u06d5\u0628\u06cc\u0633\u0646 \u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u0632\u06c6\u0631\u06cc\u0634\u062a\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646. \u062c\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0646 \u0648 \u0644\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u0628\u06d5 \u067e\u0627\u0631\u06ce\u0632 \u0628\u0646\u060c \u06a9\u0627\u0631\u06ce \u062c\u0627\u0645\u06ce\u0631\u0627\u0646\u06d5 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646 \u0633\u0627\u0646\u062f \u06a9\u06d5 \u06a9\u062a\u06ce\u0628\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0628\u06d5 \u0645\u06d5\u0631\u062f\u0645\u06cc \u0695\u0627\u06af\u06d5\u06cc\u06ce\u0646\u0646 \u0648 \u0646\u06d5\u06cc\u0634\u0627\u0631\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u067e\u0627\u0634\u06af\u06d5\u0632 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u0646\u0631\u062e\u06ce\u06a9\u06cc \u06a9\u06d5\u0645\u06cc\u0627\u0646 \u0641\u0631\u06c6\u062a\u061b \u0626\u0627\u06cc \u06a9\u06d5 \u0633\u06d5\u0648\u062f\u0627\u06cc \u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0642\u06d5\u062a \u0648\u0627 \u0645\u06d5\u0632\u0627\u0646\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06b5 \u062e\u06c6\u0634 \u0626\u06d5\u06a9\u06d5\u0646 \u0628\u06d5 \u06a9\u0627\u0631\u06ce \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u0648 \u067e\u06ce\u06cc\u0627\u0646 \u062e\u06c6\u0634\u06d5 \u0628\u06c6 \u06a9\u0627\u0631\u06ce \u06a9\u06d5 \u0646\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f\u0648\u0648\u06d5 \u062a\u0627\u0631\u06cc\u0641 \u0628\u06a9\u0631\u06ce\u0646\u060c \u0644\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u0628\u0646\u061b \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646 \u0628\u06c6 \u0648\u0627\u0646\u06d5.',
    '\u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u060c \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0646\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0648 \u0626\u0627\u06b5\u0648\u06af\u06c6\u0695\u06cc \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698\u0627\u0646\u060c \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u067e\u062a\u06d5\u0648 \u0647\u06d5\u06cc\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062e\u06ce\u0648\u06cc \u062f\u06b5\u06cc \u0648\u0634\u06cc\u0627\u0631\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc - \u0686 \u0628\u06d5 \u0648\u06ce\u0633\u062a\u0627\u0648\u06cc \u0648 \u0686 \u0644\u06d5\u062c\u06ce\u0648\u06d5\u060c \u0628\u06d5 \u062f\u0627\u0646\u06cc\u0634\u062a\u0646\u060c \u0686 \u0644\u06d5 \u0633\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0634\u062a \u0695\u0627\u062f\u06d5\u0632\u06ce\u0646 - \u0647\u06d5\u0631 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u0633\u0627\u0632\u062f\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0695\u0627\u062f\u06d5\u0645\u06ce\u0646\u0646\u060c \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u062a\u06c6 \u0626\u06d5\u0645 \u062f\u06d5\u0632\u06af\u0627\u06cc\u06d5\u062a \u0628\u06d5 \u0641\u06cc\u0695\u06c6 \u0633\u0627\u0632 \u0646\u06d5\u062f\u0627\u0648\u06d5\u060c \u067e\u0627\u06a9\u06cc \u0648 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u0628\u06c6 \u062a\u06c6\u06cc\u06d5\u061b \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u062a \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5 \u0626\u0627\u06af\u0631\u0645\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062a\u06c6 \u0628\u06cc\u062e\u06d5\u06cc\u062a\u06d5 \u0626\u0627\u06af\u0631\u060c \u062a\u0648\u0648\u0634\u06cc \u0634\u0648\u0648\u0631\u06d5\u06cc\u06cc\u062a \u06a9\u0631\u062f\u0648\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0646\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0626\u06ce\u0645\u06d5 \u0628\u06cc\u0633\u062a\u0645\u0627\u0646 \u0628\u0627\u0646\u06af \u0695\u0627\u06cc\u06d5\u06b5\u06ce\u06a9 \u06af\u0627\u0632\u06cc \u062f\u06d5\u06a9\u0631\u062f \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u0628\u06ce\u0646\u06cc\u0646. \u0626\u06ce\u0645\u06d5\u0634 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u061b \u0633\u0627 \u0628\u06c6 \u062e\u06c6\u062a \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u0645\u0627\u0646 \u062e\u06c6\u0634 \u0628\u0628\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u0631\u0627\u067e\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u06d5\u060c \u0648\u06d5\u06cc\u06a9\u0648\u0698\u06ce\u0646\u06d5 \u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0646 \u0628\u0646 \u0645\u0631\u06ce\u0646\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0634 \u0628\u06d5 \u0626\u06ce\u0645\u06d5 \u0695\u06d5\u0648\u0627 \u0628\u0628\u06cc\u0646\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0632\u0648\u0627\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u062a\u06d5\u0648\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u062a \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5\u061b \u0634\u06d5\u0631\u0645\u06d5\u0632\u0627\u0631\u06cc \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u0645\u0627\u0646 \u0645\u06d5\u06a9\u06d5. \u062a\u06c6 \u0644\u06d5 \u0647\u06d5\u0631 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06ce \u0628\u06cc\u062f\u06d5\u06cc \u0644\u0627\u0631\u06cc\u062a \u0646\u06cc\u06cc\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0626\u0627\u0648\u0627 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0647\u0627\u062a\u060c \u06a9\u06d5: \u0645\u0646 \u06a9\u0631\u062f\u06d5\u06cc \u0647\u06cc\u0686 \u0645\u0631\u06c6\u06cc\u06d5\u06a9 - \u0644\u06d5 \u0646\u06ce\u0631 \u0648 \u0645\u06ce - \u0628\u06d5 \u0628\u06ce \u067e\u0627\u062f\u0627\u0634 \u0646\u0627\u0647\u06ce\u06b5\u0645\u06d5\u0648\u06d5\u061b \u06af\u0634\u062a\u0648\u0648 \u0644\u06d5 \u0644\u0627\u06cc \u0645\u0646 \u0686\u0648\u0646 \u06cc\u06d5\u06a9\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0632\u06ce\u062f\u06cc \u062e\u06c6 \u062f\u06d5\u0631\u06a9\u0631\u0627\u0648\u0646 \u0648 \u0644\u06d5 \u0695\u0627\u06cc \u0645\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u06cc\u0648\u06d5 \u0648 \u0686\u0648\u0648\u0646\u06d5 \u062e\u06d5\u0632\u0627 \u0648 \u062f\u0698\u06cc \u0645\u0646\u06cc\u0627\u0646 \u06a9\u0648\u0634\u062a\u0628\u06ce \u06cc\u0627\u0646 \u0628\u06d5 \u06a9\u0648\u0634\u062a \u0686\u0648\u0648\u0628\u0646\u060c \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u06af\u0648\u0646\u0627\u0647\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0648\u0698\u06ce\u0646\u0645\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646\u062e\u06d5\u0645\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc\u0627\u0646\u061b \u06a9\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u0647\u06d5\u0631\u06d5\u0628\u0627\u0634\u06cc\u0634 \u0648\u0627 \u0644\u0627\u06cc \u062e\u0648\u0627\u06cc\u06d5.',
    '\u0647\u0627\u062a \u0648 \u0686\u0648\u0648\u0646 \u0648 \u06af\u0695\u0648\u0641\u0695\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0644\u06d5 \u0648\u06b5\u0627\u062a\u0627\u0646\u060c \u0646\u06d5\u0686\u06ce \u0647\u06d5\u06b5\u062a \u062e\u06d5\u06b5\u06d5\u062a\u06ce\u0646\u06ce.',
    '\u0695\u0627\u0628\u0648\u0627\u0631\u062f\u0646\u06ce\u06a9\u06cc \u0647\u0646\u062f\u06a9\u06d5 \u0648 \u067e\u0627\u0634\u0627\u0646 \u0634\u0648\u06ce\u0646\u06cc\u0627\u0646 \u062c\u06d5\u062d\u06d5\u0646\u062f\u06d5\u0645\u06d5 \u0648 \u062c\u06ce\u06cc\u0627\u0646 \u06af\u06d5\u0644\u06ce\u06a9 \u0628\u06ce \u0632\u06d5\u0644\u06d5\u062a\u06d5.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u0627 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06d5\u060c \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u062a\u0627 \u0633\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646 \u0648 \u0645\u06cc\u0648\u0627\u0646\u062f\u0627\u0631\u06cc \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5. \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u062f\u06d5\u062f\u0631\u06ce \u0647\u06d5\u0631 \u0628\u06c6 \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646 \u0695\u06d5\u0648\u0627\u06cc\u06d5.',
    '\u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u06cc\u0634 \u06a9\u06d5\u0633\u0627\u0646\u06cc \u0648\u0627 \u0647\u06d5\u0646 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0628\u06d5\u0648 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0647\u0627\u062a\u06d5\u062e\u0648\u0627\u0631 \u0648 \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u060c \u062e\u0627\u06a9\u06d5\u0695\u0627\u0646 \u0644\u06d5 \u0647\u06d5\u0645\u0628\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0648 \u0628\u06d5\u06b5\u06af\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627 \u0628\u06d5 \u0646\u0631\u062e\u06cc \u06a9\u06d5\u0645 \u0633\u06d5\u0648\u062f\u0627 \u0646\u0627\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06ce. \u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u0632\u0648\u0648 \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0646\u060c \u0644\u06d5 \u062e\u06c6\u0695\u0627\u06af\u0631\u06cc\u0634\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u067e\u06ce\u0634\u06d5\u0648\u06d5 \u0628\u0646. \u0628\u06c6 \u067e\u0627\u0631\u0627\u0633\u062a\u0646\u06cc \u0633\u0646\u0648\u0648\u0631\u06af\u06d5\u0644 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0633\u0627\u0632 \u0648 \u062a\u06d5\u06cc\u0627\u0631 \u0628\u0646. \u0644\u06d5 \u062e\u0648\u0627\u0634 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce\u060c \u0628\u06d5\u0634\u06a9\u06d5\u0645 \u0695\u0632\u06af\u0627\u0631 \u0628\u0628\u0646.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u06cc\u06d5\u06a9 \u06a9\u06d5\u0633\u06d5\u0648\u06d5 \u062e\u0633\u062a\u06c6\u062a\u06d5\u0648\u06d5 \u0648 \u062c\u0648\u0648\u062a\u06d5\u06a9\u06d5\u0634\u06cc \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u062e\u0633\u062a\u06c6\u062a\u06d5\u0648\u06d5 \u0648 \u0644\u06d5\u0648 \u062f\u0648\u0648\u0627\u0646\u06d5\u0648\u06d5 \u067e\u06cc\u0627\u0648\u06cc \u0632\u06c6\u0631 \u0648 \u0698\u0646\u06cc \u0632\u06c6\u0631\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627. \u0644\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce\u061b \u0628\u06d5 \u0646\u0627\u0648\u06cc \u0626\u06d5\u0648\u060c \u0626\u06ce\u0648\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u0633\u0648\u06ce\u0646\u062f \u062f\u06d5\u062f\u06d5\u0646\u061b \u0626\u0627\u06af\u0627\u0648 \u0644\u06d5 \u062e\u0632\u0645\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0628\u06ce. \u062e\u0648\u0627 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0686\u0627\u0648\u06ce\u0631\u06d5 \u0628\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646\u06d5\u0648\u06d5.',
    '\u0645\u0627\u06b5\u06cc \u0647\u06d5\u062a\u06cc\u0648 \u0628\u062f\u06d5\u0646\u06d5\u0648\u06d5 \u0628\u06d5 \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646. \u0646\u0627\u067e\u0627\u06a9 \u0628\u06d5 \u067e\u0627\u06a9 \u0645\u06d5\u06af\u06c6\u0695\u0646\u06d5\u0648\u06d5 \u0648 \u0645\u0627\u06b5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062a\u06ce\u06a9\u06d5\u06b5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u06a9\u06d5 \u062a\u0627\u0648\u0627\u0646\u06ce\u06a9\u06cc \u0645\u06d5\u0632\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0645\u06d5\u0695 \u06a9\u06cc\u0698\u06d5 \u0647\u06d5\u062a\u06cc\u0648\u06d5\u06a9\u0627\u0646\u06d5\u0648\u06d5\u060c \u0644\u06d5\u0648\u06d5 \u062a\u0631\u0633\u0627\u0646 \u06a9\u06d5 \u0646\u06d5\u062a\u0648\u0627\u0646\u0646 \u0628\u06d5 \u062a\u06d5\u0631\u0632\u06ce\u06a9\u06cc \u062d\u06d5\u0642 \u0648 \u0695\u06d5\u0648\u0627 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u0628\u062c\u0648\u0648\u06b5\u06ce\u0646\u06d5\u0648\u06d5\u060c \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0648\u0627\u0632\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06ce\u0646\u0646 \u0648 \u0698\u0646\u0627\u0646\u06cc \u062a\u0631 - \u0686\u06d5\u0646\u062f\u06cc \u062e\u06c6\u062a\u0627\u0646 \u067e\u06ce\u062a\u0627\u0646 \u0628\u0627\u0634\u06d5 \u0648 \u067e\u06ce\u062a\u0627\u0646 \u062f\u06d5\u0634\u06ce\u0646 - \u0645\u0627\u0631\u06d5 \u0628\u06a9\u06d5\u0646\u061b \u062f\u0648\u0648\u0627\u0646 \u0628\u0646 \u06cc\u0627\u0646 \u0633\u06cc\u06cc\u0627\u0646 \u067e\u06ce\u06a9\u06d5\u0648\u06d5\u060c \u06cc\u0627 \u0686\u0648\u0627\u0631\u06cc\u0634 \u0628\u0646 \u0647\u06d5\u0631 \u0695\u06d5\u0648\u0627\u06cc\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u062a\u0631\u0633\u0627\u0646 \u06a9\u06d5 \u0646\u06d5\u062a\u0648\u0627\u0646\u0646 \u0698\u0646\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9 \u062a\u0645\u0627\u0634\u0627 \u0628\u06a9\u06d5\u0646\u060c \u0628\u0627 \u06cc\u06d5\u06a9\u06ce\u06a9 \u0628\u06ce \u0648\u06d5\u0644 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0627\u0628\u0648\u0648\u06ce\u0631\u0646 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5\u062a\u06d5 \u0645\u0648\u06b5\u06a9\u06cc \u062e\u06c6\u062a\u0627\u0646. \u0628\u06c6 \u062e\u06c6 \u0644\u06d5 \u063a\u06d5\u062f\u0631 \u067e\u0627\u0631\u0627\u0633\u062a\u0646\u060c \u0626\u06d5\u0645\u06d5 \u06a9\u0627\u0631\u06ce \u067e\u06d5\u0633\u0646\u062f\u06d5.',
    '\u0648\u06d5\u06a9 \u067e\u06ce\u0634\u06a9\u06d5\u0634\u06ce\u06a9 \u0645\u0627\u0631\u06d5\u06cc\u06cc\u06cc \u0698\u0646\u06d5\u06a9\u0627\u0646 \u0628\u062f\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0634\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0645\u0627\u0631\u06d5\u06cc\u06cc\u06cc\u06d5\u06a9\u06d5 \u0628\u06d5\u0646\u06af\u06c6 \u0628\u06d5\u062e\u0634\u06cc\u060c \u0628\u06cc\u062e\u06c6\u0646 \u0648 \u0646\u06c6\u0634\u06cc \u06af\u06cc\u0627\u0646\u0648\u0648 \u0628\u06ce.',
    '\u0645\u0627\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 - \u06a9\u06d5 \u062e\u0648\u0627 \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u0645\u0627\u06cc\u06d5\u06cc \u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u062a\u0627\u0646 - \u0645\u06d5\u062f\u06d5\u0646\u06d5 \u062f\u06d5\u0633\u062a \u06a9\u06d5\u0645\u0626\u0627\u0648\u06d5\u0632\u0627\u0646\u061b [\u0628\u06d5\u06b5\u0627\u0645] \u062c\u0644\u06a9 \u0648 \u0628\u0698\u06cc\u0648\u06cc\u0627\u0646 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u0627\u0628\u06cc\u0646 \u06a9\u06d5\u0646 \u0648 \u0628\u06d5 \u0695\u0648\u0648\u06cc \u062e\u06c6\u0634\u06d5\u0648\u06d5 \u0628\u06cc\u0627\u0646\u062f\u0648\u06ce\u0646\u0646.',
    '\u0647\u06d5\u062a\u06cc\u0648\u0627\u0646 \u062a\u0627\u0642\u06cc \u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u062a\u0627 \u062f\u06d5\u0628\u0646\u06d5 \u06af\u06d5\u0646\u062c\u06ce \u0695\u06d5\u0633\u06cc\u062f\u06d5\u061b \u062c\u0627 \u0626\u06d5\u0631 \u062f\u06cc\u062a\u0627\u0646 \u062e\u0627\u0633 \u0648 \u062e\u0631\u0627\u067e \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u062c\u0648\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 \u0645\u0627\u06b5\u06cc\u0627\u0646 \u0628\u062f\u06d5\u0646\u06d5\u0648\u06d5. \u0644\u06d5 \u062a\u0631\u0633\u06cc \u0626\u06d5\u0648\u06d5\u0634\u062f\u0627 \u0646\u06d5\u0648\u0627 \u06af\u06d5\u0648\u0631\u06d5 \u0628\u0628\u0646 - \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0648 \u0628\u06d5\u0644\u06d5\u0632 - \u0645\u0627\u06b5\u06cc\u0627\u0646 \u0645\u06d5\u062e\u06c6\u0646. \u06a9\u06ce \u0632\u06d5\u0646\u06af\u06cc\u0646\u06d5 \u062e\u06c6 \u067e\u0627\u0631\u06ce\u0632\u06ce\u060c \u06a9\u06ce\u0634 \u0646\u06d5\u062f\u0627\u0631\u06d5\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06cc\u06cc \u0698\u06cc\u0627\u0646\u06cc\u06d5\u062a\u06cc - \u0628\u06d5 \u062f\u0627\u0628\u06cc \u0695\u06c6\u0698 - \u0644\u06ce\u06cc \u0647\u06d5\u06b5\u06af\u0631\u06ce. \u0647\u06d5\u0631\u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u0645\u0627\u06b5\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646\u06d5\u0648\u06d5\u060c \u0628\u0627 \u0634\u0627\u06cc\u06d5\u062a \u0626\u0627\u06af\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06ce. \u0628\u06c6 \u0644\u06ce \u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5\u0634 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0633\u06d5.',
    '\u0644\u06d5 \u06a9\u06d5\u0644\u06d5\u067e\u0648\u0648\u0631\u06cc \u062f\u0627\u06cc \u0648 \u0628\u0627\u0628 \u0648 \u062e\u0632\u0645\u06cc \u0646\u0632\u06cc\u06a9 - \u06a9\u06d5\u0645 \u0628\u06ce \u06cc\u0627\u0646 \u0632\u06c6\u0631 - \u0646\u06ce\u0631\u06cc\u0646\u06d5 \u0628\u06d5\u0634\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u0645\u06ce\u0648\u06cc\u0646\u06d5\u0634 \u062a\u06cc\u0627 \u0628\u06d5\u0634\u062f\u0627\u0631\u06d5 \u0648 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u0695\u06cc\u0627\u0631 \u062f\u0631\u0627\u0648\u06d5.',
    '\u06af\u06d5\u0631 \u0644\u06d5 \u0648\u06d5\u062e\u062a\u06cc \u062f\u0627\u0628\u06d5\u0634\u06cc\u0646\u06cc \u06a9\u06d5\u0644\u06d5\u067e\u0648\u0648\u0631\u062f\u0627\u060c \u06a9\u06d5\u0633\u0648\u06a9\u0627\u0631 \u0648 \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646 \u0648 \u0628\u06ce\u0646\u06d5\u0648\u0627\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06ce \u0647\u06d5\u0628\u0648\u0648\u0646\u060c \u0644\u06d5\u0648 \u0645\u0627\u06b5\u06d5 \u0634\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0646\u06ce \u0648 \u0628\u06d5 \u0695\u0648\u0648\u06cc \u062e\u06c6\u0634\u06d5\u0648\u06d5 \u0628\u06cc\u0627\u0646\u062f\u0648\u06ce\u0646\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5\u067e\u0627\u0634 \u062e\u06c6\u06cc\u0627\u0646 \u0645\u0646\u062f\u0627\u06b5\u0627\u0646\u06cc \u0633\u06d5\u0631\u0648\u067e\u06ce\u0686\u06a9\u06d5\u06cc \u0628\u06ce\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0628\u06d5\u062c\u06ce \u062f\u06ce\u06b5\u0646\u060c \u0628\u0627 \u0644\u06d5 \u0648\u06d5\u0633\u06cc\u06d5\u062a \u06a9\u0631\u062f\u0646\u06ce\u062f\u0627\u060c \u062a\u0631\u0633\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627 \u0628\u06ce \u0648 \u0634\u062a\u06ce\u06a9\u06cc \u0648\u0627 \u0644\u06d5\u067e\u0627\u0634 \u062e\u06c6\u06cc\u0627\u0646 \u0695\u0627\u0633\u067e\u06ce\u0631\u0646\u060c \u0647\u06cc\u0686 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u062f\u0627\u0631\u0627\u06cc\u06cc\u06cc \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646 \u062f\u06d5\u062e\u06c6\u0646 \u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0626\u0627\u06af\u0631\u06ce \u062f\u06d5\u062e\u06d5\u0646\u06d5 \u0647\u06d5\u0646\u0627\u0648\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u06af\u0631\u06ce\u06a9\u06cc \u0628\u06d5 \u0647\u06d5\u06b5\u067e\u06d5 \u0648 \u06af\u0695 \u062f\u06d5\u0628\u0646.',
    '\u062e\u0648\u062f\u0627 \u067e\u06ce\u0648 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u06ce: \u06a9\u06d5\u0644\u06d5\u067e\u0648\u0648\u0631\u062a\u0627\u0646 \u0628\u0627 \u0628\u06c6 \u0646\u06ce\u0631\u06cc\u0646\u06d5 \u062f\u0648\u0648 \u0628\u06d5\u0634 \u0648 \u0628\u06c6 \u0645\u06ce\u0648\u06cc\u0646\u06d5 \u0646\u06cc\u0648\u06d5\u06cc \u0628\u06d5\u0634\u06cc \u0646\u06ce\u0631\u06cc\u0646\u06d5 \u0628\u06ce. \u06af\u06d5\u0631 \u0698\u0645\u0627\u0631\u06d5\u06cc \u0645\u06ce\u0648\u06cc\u0646\u06d5 \u0644\u06d5 \u062f\u0648\u0648\u0627\u0646 \u067e\u062a\u0631 \u0628\u0648\u0648\u0646\u060c \u062f\u0648\u0648 \u0628\u06d5\u0634 \u0644\u06d5 \u0633\u06ce \u0628\u06d5\u0634\u06cc \u0645\u06cc\u0631\u0627\u062a\u06d5\u06a9\u06d5 \u0626\u06d5\u0628\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0645\u06ce\u0648\u06cc\u0646\u06d5 \u06cc\u06d5\u06a9\u06ce \u0628\u0648\u0648\u060c \u0646\u06cc\u0648\u06d5\u06cc \u06a9\u06d5\u0644\u06d5\u067e\u0648\u0648\u0631 \u0628\u06c6 \u0626\u06d5\u0645\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0631\u062f\u0648\u0648 \u0639\u06d5\u0648\u0644\u0627\u062f\u06cc \u0644\u06d5\u067e\u0627\u0634 \u0628\u06d5\u062c\u06ce \u0645\u0627\u060c \u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u06cc\u060c \u0647\u06d5\u0631\u06a9\u0627\u0645\u06ce\u06a9\u06cc\u0627\u0646 \u0634\u06d5\u0634 \u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0648 \u0645\u0627\u06b5\u06d5 \u0647\u06d5\u06cc\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06cc\u0686 \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0646\u06cc\u06cc\u06d5 \u0648 \u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u06cc \u062f\u06d5\u0628\u06ce \u0645\u06cc\u0631\u0627\u062a\u06d5\u06a9\u06d5 \u0628\u06af\u0631\u0646\u060c \u0644\u06d5 \u0633\u06ce \u06a9\u0648\u062a\u060c \u06a9\u0648\u062a\u06ce\u06a9 \u0628\u06c6 \u062f\u0627\u06cc\u06a9\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0631\u062f\u0648\u0648 \u0628\u0631\u0627 \u0648 \u062e\u0648\u0634\u06a9\u06cc \u0628\u06d5\u062c\u06ce \u0647\u06ce\u0634\u062a\u0648\u0648\u0646\u060c \u067e\u0627\u0634 \u062f\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0642\u06d5\u0631\u0632\u0648\u0642\u06c6\u06b5\u06d5 \u0648 \u067e\u06ce\u06a9\u0647\u06ce\u0646\u0627\u0646\u06cc \u0648\u06d5\u0633\u06cc\u06d5\u062a\u06d5\u06a9\u06d5\u060c \u062f\u0627\u06cc\u06a9 \u0634\u06d5\u0634 \u06cc\u06d5\u06a9\u06cc \u067e\u06ce \u062f\u06d5\u0628\u0695\u06ce. \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646 \u0644\u06d5 \u0628\u0627\u0628\u0627\u0646 \u0648 \u0641\u0631\u0632\u06d5\u0646\u062f\u0627\u0646\u0648\u0648 \u06a9\u0627\u0645\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0647\u0631\u06d5 \u067e\u06ce\u062f\u0627\u0646\u0648\u0648 \u0646\u0632\u06cc\u06a9\u062a\u0631\u0646. \u0628\u0695\u06cc\u0627\u0631\u06ce\u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06c6\u0648 \u062f\u0627\u0646\u062f\u0631\u0627\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0644\u06d5\u0648 \u0645\u06cc\u0631\u0627\u062a\u06d5\u06cc \u0644\u06d5 \u0698\u0646\u0627\u0646\u0648\u0648 \u0628\u06d5\u062c\u06ce \u062f\u06d5\u0645\u06ce\u0646\u06ce - \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06cc\u0686 \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648 - \u0644\u06d5 \u062f\u0648\u0648 \u0628\u06d5\u0634 \u0628\u06d5\u0634\u06ce\u06a9\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0639\u06d5\u0648\u0644\u0627\u062f\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0628\u0648\u0648\u060c \u0626\u06ce\u0648\u06d5 \u0686\u0648\u0627\u0631 \u06cc\u06d5\u06a9\u0648\u0648 \u067e\u06ce \u062f\u06d5\u0628\u0695\u06ce\u061b \u067e\u0627\u0634 \u062f\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0642\u06d5\u0631\u0632\u0648\u0642\u06c6\u06b5\u06d5 \u0648 \u0648\u06d5\u0633\u06cc\u06d5\u062a\u06ce \u06a9\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0645\u0631\u062f\u0646 \u0648 \u0639\u06d5\u0648\u0644\u0627\u062f\u0648\u0648 \u0646\u06d5\u0628\u0648\u0648\u060c \u0698\u0646\u06d5\u06a9\u0627\u0646\u0648\u0648 \u0686\u0648\u0627\u0631 \u06cc\u06d5\u06a9\u06cc \u0645\u06cc\u0631\u0627\u062a\u0648\u0648 \u062f\u06d5\u06af\u0631\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0639\u06d5\u0648\u0644\u0627\u062f\u06cc\u0634\u062a\u0627\u0646 \u0647\u06d5\u0628\u0648\u0648\u060c \u0698\u0646\u062a\u0627\u0646 \u0647\u06d5\u0634\u062a \u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0628\u0695\u06ce\u061b \u067e\u0627\u0634 \u067e\u06ce\u06a9\u0647\u06ce\u0646\u0627\u0646\u06cc \u0648\u06d5\u0633\u06cc\u06d5\u062a\u062a\u0627\u0646 \u0648 \u062f\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0642\u06d5\u0631\u0632\u0648\u0642\u06c6\u06b5\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06cc\u0627\u0648\u06ce \u06cc\u0627\u06a9\u0648\u0648 \u0698\u0646\u06ce \u0645\u0631\u062f \u0648 \u0628\u0627\u0628 \u0648 \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0644\u06ce \u0628\u06d5\u062c\u06ce \u0646\u06d5\u0645\u0627 \u0648 \u0628\u0631\u0627\u06cc\u06d5\u06a9 \u06cc\u0627 \u062e\u0648\u0634\u06a9\u06ce\u06a9\u06cc \u0628\u0648\u0648\u060c \u0647\u06d5\u0631\u06cc\u06d5\u06a9 \u0644\u06d5\u0648\u0627\u0646 \u0634\u06d5\u0634 \u06cc\u06d5\u06a9\u06cc \u06a9\u06d5\u0644\u06d5\u067e\u0648\u0648\u0631\u06cc \u0647\u06d5\u0633. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u06cc\u06d5\u06a9\u06ce\u06a9 \u067e\u062a\u0631 \u0628\u0648\u0648\u0646\u060c \u0628\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0633\u06ce \u06cc\u06d5\u06a9\u06cc \u06a9\u06d5\u0644\u06d5\u067e\u0648\u0648\u0631 \u062f\u06d5\u06af\u0627\u061b \u067e\u0627\u0634 \u067e\u06ce\u06a9\u0647\u06ce\u0646\u0627\u0646\u06cc \u0648\u06d5\u0633\u06cc\u06d5\u062a \u0648 \u062f\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0642\u06d5\u0631\u0632. \u0646\u0627\u0634\u06ce \u0648\u06d5\u0633\u06cc\u06d5\u062a \u0628\u06d5 \u062c\u06c6\u0631\u06ce\u06a9 \u0628\u06ce \u06a9\u06d5 \u0628\u06d5 \u0626\u0627\u0646\u0642\u06d5\u0633\u062a \u0628\u06d5 \u0632\u06d5\u0631\u06d5\u0631\u06cc \u06a9\u06d5\u0644\u06d5\u067e\u0648\u0648\u0631\u06af\u0631\u0627\u0646 \u062a\u06d5\u0648\u0627\u0648 \u0628\u06ce. \u0626\u06d5\u0645 \u0628\u0695\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5\u06cc\u06d5 \u0648 \u062e\u0648\u062f\u0627\u0634 \u0628\u06c6 \u062e\u06c6\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0648 \u0644\u06d5\u0633\u06d5\u0631\u062e\u06c6\u06cc\u06d5.',
    '\u0626\u06d5\u0645 \u062f\u06d5\u0633\u062a\u0648\u0648\u0631\u0627\u0646\u06d5 \u0633\u0646\u0648\u0648\u0631\u0646\u060c \u062e\u0648\u0627 \u062f\u0627\u06cc\u0646\u0627\u0648\u06d5. \u0647\u06d5\u0631\u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0628\u06ce\u060c \u062f\u06d5\u06cc\u0646\u06ce\u0631\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646 \u0648 \u0644\u06d5\u0648\u067e\u06d5\u0695\u06cc \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06cc\u062f\u0627\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u0631\u0686\u06ce \u0648 \u0633\u0646\u0648\u0648\u0631\u06cc \u062e\u0648\u0627 \u0628\u06d5 \u0647\u06cc\u0686 \u0628\u06af\u0631\u06ce\u060c \u062f\u06d5\u06cc\u062e\u0627\u062a\u06d5 \u062f\u0648\u0648\u0695 \u0626\u0627\u06af\u0631\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u06ce \u0648 \u0628\u06d5\u0648\u067e\u06d5\u0695\u06cc \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u06d5\u0648\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u062f\u0631\u06ce.',
    '\u0647\u06d5\u0631 \u0698\u0646\u06ce\u06a9\u062a\u0627\u0646 \u06a9\u06d5 \u0647\u06d5\u0633\u062a\u0648\u0648 \u06a9\u0631\u062f \u0646\u0627\u0645\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u06c6 \u0641\u06ce\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0686\u0648\u0627\u0631 \u06a9\u06d5\u0633\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5\u0631\u0646\u06d5 \u0633\u06d5\u0631\u06ce - \u0644\u06d5\u0648 \u0639\u0627\u0646\u06d5\u062f\u0627 \u06a9\u06d5 \u0644\u06ce\u06a9 \u062c\u0648\u0648\u062a \u0628\u0648\u0648\u0646 - \u0648\u06d5\u062e\u062a\u06ce \u0626\u06d5\u0648\u0627\u0646 \u0647\u0627\u062a\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0627\u0646\u060c \u0647\u06d5\u0631 \u0644\u06d5 \u0645\u0627\u06b5\u062f\u0627 \u0642\u06d5\u062a\u06cc\u0633\u06cc\u0627\u0646 \u06a9\u06d5\u0646 \u062a\u0627 \u0628\u06d5 \u0645\u0631\u062f\u0646 \u0644\u06d5\u0646\u0627\u0648 \u062f\u06d5\u0686\u0646\u060c \u06cc\u0627\u0646 \u062a\u0627 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u0626\u06d5\u0648 \u062c\u0648\u0648\u062a\u06d5\u06cc \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06af\u0631\u062a \u0648 \u0695\u06ce\u06cc \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u06af\u0631\u062a\u06d5 \u0628\u06d5\u0631\u060c \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06b5\u06af\u0631\u0646. \u062e\u0648\u0627 \u0644\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u0648\u062f\u0627 \u062a\u06c6\u0628\u06d5 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0642\u0628\u0648\u0648\u06b5 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0646\u06d5\u0632\u0627\u0646\u06cc\u0646\u06d5\u0648\u06d5 \u062e\u0631\u0627\u067e\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0695\u0648\u0648 \u062f\u06d5\u062f\u0627 \u0648 \u06af\u0648\u0631\u062c\u06ce \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06cc\u062a\u0631 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0648\u06d5\u062e\u062a\u06cc \u06af\u06cc\u0627\u0646\u06d5\u06b5\u0627\u0648\u062f\u0627 \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06ce\u0645\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc\u0646. \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u06cc \u0642\u0628\u0648\u0648\u06b5 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u062f\u06d5\u0645\u0631\u0646. \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0648\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646\u0645\u0627\u0646 \u0633\u0627\u0632 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06c6\u062a\u0627\u0646 \u0695\u06d5\u0648\u0627 \u0646\u06cc\u06cc\u06d5 \u0698\u0646\u0627\u0646 \u0628\u06d5 \u0632\u06c6\u0631\u062f\u0627\u0631\u06cc \u0628\u06a9\u06d5\u0646\u06d5 \u0645\u0627\u06b5\u06cc \u0645\u06cc\u0631\u0627\u062a\u06cc. \u0646\u0627\u0628\u06ce \u0632\u06c6\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u0646\u0646 \u0628\u0695\u06ce\u06a9 \u0644\u06d5\u0648\u06d5\u06cc \u067e\u06ce\u062a\u0627\u0646 \u062f\u0627\u0648\u0646 \u062f\u06cc\u0633\u0627\u0646 \u0644\u06ce\u06cc\u0627\u0646 \u0628\u0633\u062a\u06ce\u0646\u0646\u06d5\u0648\u06d5\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u06a9\u0627\u0631\u06cc \u0628\u06ce\u0646\u0627\u0645\u0648\u0633\u06cc \u0628\u06a9\u06d5\u0646. \u0628\u06d5 \u062a\u06d5\u0631\u0632\u06ce\u06a9\u06cc \u062c\u0648\u0627\u0646 \u0648 \u0695\u06d5\u0648\u0627\u060c \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646\u0627 \u0628\u062c\u0648\u0648\u06b5\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u0634\u062a\u0627\u0646 \u0646\u06d5\u0647\u0627\u062a \u0644\u06ce\u06cc\u0627\u0646\u060c \u0634\u062a\u06cc \u0648\u0627 \u0647\u06d5\u0646 \u062d\u06d5\u0632\u06cc \u0644\u06ce \u0646\u06d5\u06a9\u06d5\u0646 \u0648 \u0686\u0627\u06a9\u06d5\u06cc \u0632\u06c6\u0631\u06cc \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06c6\u0646\u06af\u06c6 \u062a\u06ce\u062f\u0627 \u0628\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0648\u06cc\u0633\u062a\u0648\u0648 \u0698\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5 \u0698\u0646\u06ce\u06a9\u06cc \u062f\u06cc\u06a9\u06d5 \u0648\u06d5\u06af\u06c6\u0695\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0698\u0646\u06d5\u06a9\u06d5\u06cc \u067e\u06ce\u0634\u0648\u0648\u062a\u0627\u0646 \u06a9\u0644\u0648\u0648\u0631\u06ce\u06a9 \u062f\u0631\u0627\u0648\u062a\u0627\u0646 \u062f\u0627\u0628\u06ce\u060c \u0646\u0627\u0628\u06ce \u0644\u06ce\u06cc\u0627\u0646 \u0628\u0633\u062a\u06ce\u0646\u0646\u06d5\u0648\u06d5. \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 \u0628\u06d5 \u0628\u0648\u062e\u062a\u0627\u0646 \u0648 \u0628\u06d5 \u062e\u06c6 \u062a\u0648\u0648\u0634\u06cc \u06af\u0648\u0646\u0627\u062d\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u06a9\u0631\u062f\u0646 \u0628\u06cc\u0633\u062a\u06ce\u0646\u0646\u06d5\u0648\u06d5!\u061f',
    '\u062c\u0627 \u0686\u06c6\u0646\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u0633\u062a\u06ce\u0646\u0646\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u06cc\u06d5\u06a9\u06d5\u0648\u06d5 \u0628\u0648\u0648\u0646 \u0648 \u0647\u06d5\u0631 \u0644\u06d5 \u062c\u06ce\u0648\u0628\u0627\u0646\u06ce\u06a9\u062f\u0627 \u062e\u06d5\u0648\u062a\u0648\u0648\u0646\u061b \u062e\u06c6\u0634\u06cc\u062a\u0627\u0646 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u062f\u06cc\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u067e\u062a\u06d5\u0648\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0628\u06d5\u0633\u062a\u0648\u0648\u06d5!\u061f',
    '\u0698\u0646\u0627\u0646\u06ce\u06a9 \u0645\u06d5\u062e\u0648\u0627\u0632\u0646 \u06a9\u06d5 \u0628\u0627\u0648\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u062e\u0648\u0627\u0633\u062a\u0648\u0648\u06cc\u0627\u0646\u0646\u060c \u0645\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0628\u0648\u0648\u0631\u06cc\u0648\u06d5. \u0686\u0648\u0646\u06a9\u06d5 \u0633\u06d5\u0631\u062c\u06ce\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06d5 \u062f\u0627\u0648\u06ce\u0646\u067e\u06cc\u0633\u06cc \u062f\u0627\u062f\u06d5\u0646\u062f\u0631\u06ce \u0648 \u0632\u06c6\u0631 \u0634\u0648\u0648\u0631\u06d5\u06cc\u06cc\u06cc\u06d5 \u0648 \u0695\u06ce\u0628\u0627\u0632\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e\u06d5.',
    '\u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5\u06cc \u067e\u06ce\u0648 \u0646\u0627\u0634\u06ce\u0646 \u0648 \u0644\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646 \u062d\u06d5\u0631\u0627\u0645 \u06a9\u0631\u0627\u0648\u0646\u060c \u0628\u0631\u06cc\u06d5\u062a\u06cc\u0646 \u0644\u06d5: \u062f\u0627\u06cc\u06a9 \u0648 \u06a9\u0686 \u0648 \u062e\u0648\u0634\u06a9 \u0648 \u067e\u0644\u06a9 \u0648 \u062e\u0627\u06b5\u062a\u06cc\u06a9 \u0648 \u06a9\u0686\u06cc \u0628\u0631\u0627 \u0648 \u06a9\u0686\u06cc \u062e\u0648\u0634\u06a9 \u0648 \u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0633\u0627\u0648\u0627\u06cc\u06cc \u0634\u06cc\u0631\u06cc \u0626\u06d5\u0645\u0627\u0646\u0648\u0648 \u062e\u0648\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u06a9\u06cc\u0698\u06d5\u06cc \u0647\u0627\u0648\u0634\u06cc\u0631\u062a\u0627\u0646 \u0628\u0648\u0648\u06d5 \u0648 \u062e\u06d5\u0633\u0648\u0648\u062a\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u06a9\u06cc\u0698\u0627\u0646\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627\u0646\u0648\u0648 \u06a9\u0631\u062f\u0648\u0648\u0646 \u0648 \u0644\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u062e\u06d5\u0648\u062a\u0648\u0648\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u0646\u06d5\u062e\u06d5\u0648\u062a\u0648\u0648\u0646 \u0642\u06d5\u06cc\u062f\u06cc \u0646\u06cc\u06cc\u06d5. \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0698\u0646\u06cc \u06a9\u0648\u0695\u06cc\u0634\u062a\u0627\u0646\u061b \u06a9\u06d5 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0626\u06d5\u0648 \u06a9\u0648\u0695\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0628\u06ce\u061b \u062f\u0648\u0648 \u062e\u0648\u0634\u06a9\u06cc\u0634 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0647\u06ce\u0646\u0627\u0646 \u0695\u06d5\u0648\u0627 \u0646\u06cc\u06cc\u06d5\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0648 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u0627\u062a\u0628\u06ce. \u06a9\u06d5 \u062e\u0648\u0627 \u0644\u06d5 \u0647\u06d5\u06b5\u06d5 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0698\u0646\u06cc \u0628\u06d5\u0634\u0648\u0648\u062a\u0627\u0646 \u067e\u06ce \u0646\u0627\u0634\u06ce\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0648\u0648\u0646\u06d5\u062a\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646. \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0626\u06d5\u0645 \u0628\u0695\u06cc\u0627\u0631\u06d5\u0648 \u0628\u06c6 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5. \u062c\u06cc\u0627 \u0644\u06d5\u0645\u06d5 \u0695\u06ce\u0648 \u062f\u0631\u0627\u0648\u06d5 \u0628\u06d5 \u062f\u0631\u0627\u0648\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0698\u0646 \u0628\u06ce\u0646\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5 \u067e\u0627\u06a9\u06cc \u0628\u06ce\u060c \u0646\u06d5 \u0628\u06d5 \u062f\u0627\u0648\u06ce\u0646\u067e\u06cc\u0633\u06cc. \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0626\u06d5\u0648 \u0644\u06d5\u0632\u06d5\u062a\u06d5\u06cc \u0644\u06d5 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062f\u06d5\u0628\u06cc\u0646\u0646\u060c \u0626\u06d5\u0648 \u0645\u0627\u06b5\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06c6\u06cc\u0627\u0646\u06cc \u0628\u0695\u06cc\u0627\u0631 \u062f\u0627\u0648\u06d5 \u0648 \u06a9\u0631\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646\u060c \u0626\u06ce\u0648\u06d5 \u0628\u06cc\u062f\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u067e\u0627\u0634 \u062f\u06cc\u0627\u0631\u06cc\u06a9\u0631\u062f\u0646\u06cc \u0645\u0627\u0631\u06d5\u06cc\u06cc\u060c \u0644\u06d5 \u0646\u06ce\u0648 \u062e\u06c6\u062a\u0627\u0646\u062f\u0627 \u067e\u06ce\u06a9 \u0647\u0627\u062a\u0646\u060c \u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u0648\u0648 \u0646\u0627\u06af\u0627\u062a\u06ce \u0648 \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u06a9\u06ce\u0634 \u062f\u06d5\u0633\u062a\u06d5\u0646\u06af\u06d5 \u0648 \u0626\u06d5\u0648 \u0645\u0627\u06b5\u06d5\u06cc \u0628\u06c6 \u0647\u06d5\u06b5\u0646\u0627\u0633\u0648\u0648\u0695\u06ce \u06a9\u06d5 \u0698\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0626\u0627\u0632\u0627\u062f \u0628\u06ce\u0646\u06ce\u060c \u0628\u0627 \u0644\u06d5\u0648 \u06a9\u06cc\u0698\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06d5 \u0648\u0627 \u0628\u0648\u0648\u0646\u06d5\u062a\u06d5 \u0626\u06cc \u062e\u06c6\u062a\u0627\u0646\u060c \u0645\u0627\u0631\u06d5 \u0628\u06a9\u0627. \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0632\u06cc\u0627\u062a\u0631 \u0644\u06d5 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5. \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u062a\u06cc\u0631\u06d5\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u0646\u061b \u0628\u06d5 \u062f\u06d5\u0633\u062a\u0648\u0648\u0631\u06cc \u062e\u0627\u0648\u06d5\u0646\u06cc\u0627\u0646 \u0645\u0627\u0631\u06d5\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646. \u0686\u06c6\u0646 \u0628\u0627\u0648\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u062a\u0627\u0646\u062f\u0627 \u0645\u0627\u0631\u06d5\u06cc\u06cc\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u0628\u0695\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0628\u0627 \u06a9\u06d5\u0646\u06cc\u0632\u0627\u0646\u06ce \u062f\u0627\u0648\u06ce\u0646\u067e\u0627\u06a9 \u0628\u0646\u060c \u0646\u06d5 \u062f\u0627\u0648\u06ce\u0646\u062a\u06d5\u0631\u061b \u0644\u06d5 \u0698\u06ce\u0631\u06d5\u0648\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u0646\u06ce\u0631\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0628\u06ce. \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0647\u0627\u062a\u0646\u06d5 \u0628\u06d5\u0631 \u0695\u06a9\u06ce\u0641\u06cc \u0626\u06ce\u0648\u06d5\u060c \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u067e\u06cc\u0627\u0648\u06cc \u0646\u0627\u0645\u06d5\u062d\u0631\u06d5\u0645 \u062a\u06ce\u06a9\u06d5\u06b5 \u06a9\u0631\u062f\u060c \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06cc\u0627\u0646 \u0646\u06cc\u0648\u06d5\u06cc \u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5 \u062f\u06d5\u0628\u06ce \u06a9\u06d5 \u0634\u0648\u0648\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0626\u0627\u0632\u0627\u062f\u0646. \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc\u06d5 \u0695\u06d5\u0628\u06d5\u0646\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0646\u060c \u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u0628\u06c6\u062a\u0627\u0646 \u0628\u0627\u0634\u062a\u0631\u06d5. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u06b5\u06d5 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u0648\u0627 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5 \u0695\u06ce\u062a\u0627\u0646 \u0628\u06c6 \u0695\u0648\u0648\u0646 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0695\u06ce\u0648\u0634\u0648\u06ce\u0646\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06cc\u06cc\u0627\u0646\u06cc\u0634 \u0634\u0627\u0631\u06d5\u0632\u0627\u0648 \u06a9\u0627 \u0648 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc\u0648 \u0644\u06ce \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0627. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u062a\u06c6\u0628\u06d5\u062a\u0627\u0646 \u0644\u06ce \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0627. \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0648\u0627 \u0626\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646 \u0647\u06d5\u0648\u0627 \u0648 \u0647\u06d5\u0648\u06d5\u0633\u060c \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce - \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc - \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0644\u0627 \u0628\u062f\u06d5\u0646.',
    '\u062e\u0648\u0627 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5 \u0626\u06d5\u0631\u06a9\u062a\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0633\u0648\u0648\u06a9 \u0628\u06a9\u0627 \u0648 \u0645\u0631\u06c6\u0634 \u0628\u06ce \u0647\u06ce\u0632 \u0648 \u067e\u06ce\u0632\u06cc \u062f\u0631\u0648\u0633\u062a \u0628\u0648\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0645\u0627\u06b5 \u0648 \u062f\u0627\u0631\u0627\u06cc\u06cc\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc - \u0644\u06d5 \u062e\u0648\u062a\u0648\u062e\u06c6\u0695\u0627\u06cc\u06cc - \u0645\u06d5\u062e\u06c6\u0646\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5 \u0633\u06d5\u0648\u062f\u0627 \u0648 \u0645\u0627\u0645\u06b5\u06d5 \u0648 \u0628\u06d5 \u0695\u0627\u0632\u06cc \u0628\u0648\u0648\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u062a\u0627\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u0633\u0627 \u067e\u06ce\u062a\u0627\u0646 \u0695\u06d5\u0648\u0627\u06cc\u06d5. \u0628\u06d5\u0647\u06d5\u0642 \u0646\u06d5\u0628\u06ce\u060c \u0646\u0627\u0628\u06ce \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0634 \u0628\u06a9\u0648\u0698\u0646. \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u0646\u06cc\u0627\u0632\u06cc \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06cc \u0626\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u06a9\u0627\u0631\u0627\u0646\u06d5 \u0628\u06a9\u0627\u060c \u06af\u0631\u0641\u062a\u0627\u0631\u06cc \u0626\u0627\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u06cc\u0646. \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u0647\u0627\u0633\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648 \u062a\u0627\u0648\u0627\u0646\u06d5 \u0632\u06d5\u0644\u0627\u0645\u0627\u0646\u06d5\u06cc \u067e\u06ce\u0645\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u0646 \u0646\u06d5\u06cc\u06a9\u06d5\u0646 \u0648 \u062e\u06c6\u062a\u0627\u0646 \u062f\u0648\u0648\u0631\u06d5\u067e\u06d5\u0631\u06ce\u0632 \u0628\u06af\u0631\u0646\u060c \u0647\u06d5\u06b5\u06d5 \u0648 \u067e\u06d5\u06b5\u06d5\u06cc \u062f\u06cc\u06a9\u06d5\u062a\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0644\u0627 \u062f\u06d5\u0628\u06d5\u06cc\u0646 \u0648 \u062f\u06d5\u062a\u0627\u0646\u0628\u06d5\u06cc\u0646\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u0628\u06d5\u0642\u06d5\u062f\u0631 \u0648 \u0639\u06cc\u0632\u06d5\u062a.',
    '\u0646\u0627\u0628\u06ce \u0626\u0627\u0648\u0627\u062a \u0628\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u0628\u062e\u0648\u0627\u0632\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0646\u06af\u06c6\u06cc \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc \u062a\u0631 \u0633\u06d5\u0631 \u062e\u0633\u062a\u0648\u0648\u06d5. \u067e\u06cc\u0627\u0648\u0627\u0646 \u0628\u06d5\u067e\u06ce\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u06a9\u06c6\u0634\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646\u060c \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 [\u0647\u06d5\u0631 \u06a9\u0627\u0645] \u0628\u06d5\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5. \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0628\u06ce \u0647\u06d5\u0631 \u0644\u06d5\u0628\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0648\u06d5\u067e\u0627\u0695\u06ce\u0646\u060c \u0686\u0627\u06a9\u06d5\u062a\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5 \u0628\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u062f\u0627\u06cc \u0648 \u0628\u0627\u0628 \u0648 \u062e\u0632\u0645\u06d5 \u0632\u06c6\u0631 \u0646\u0632\u06cc\u06a9\u06d5\u06a9\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646\u0648\u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u06af\u0631\u06ce \u062f\u0627\u0646\u060c \u0647\u06d5\u0631\u06af\u0627 \u0645\u0631\u062f\u0646 \u0648 \u0645\u0627\u06b5\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u0648\u0648\u0627 \u0645\u0627\u0628\u0648\u0648\u060c \u0628\u0627 \u0628\u06d5\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce \u0628\u06af\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u067e\u06cc\u0627\u0648\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0698\u0646\u0627\u0646\u062f\u0627 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc\u0627\u0646 \u0647\u06d5\u0633\u060c \u06a9\u06d5 \u062e\u0648\u0627 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce \u0647\u06ce\u0698\u0627\u062a\u0631 \u0628\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5. \u062f\u06cc\u0627\u0631\u06d5 \u067e\u06cc\u0627\u0648\u0627\u0646 \u0626\u06d5\u0631\u06a9\u06cc \u0628\u06d5\u0695\u06ce\u0686\u0648\u0648\u0646\u06cc \u0645\u0627\u06b5\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0634\u0627\u0646\u06d5. \u062c\u0627 \u0698\u0646\u0627\u0646\u06cc \u0628\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u06af\u0648\u06ce\u0628\u06cc\u0633\u0646 \u0648 - \u0648\u06d5\u06a9 \u062e\u0648\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062f\u0627\u0648\u06d5 - \u062f\u0648\u0648\u0631 \u0644\u06d5 \u0634\u0648\u0648\u06cc\u0627\u0646 \u0634\u06d5\u0631\u0645\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646 \u0648 \u0626\u0627\u06af\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0645\u0627\u06b5\u06ce \u062f\u06d5\u0628\u06ce. \u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5\u0634 \u0644\u06d5 \u0626\u06d5\u0634\u0642\u06cc \u0628\u0648\u0648\u0646\u06cc\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u0646\u060c \u0646\u0633\u062d\u06d5\u062a \u06a9\u06d5\u0646 \u0648 \u062c\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u062c\u06cc\u0627 \u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06ce\u0634\u06cc\u0627\u0646 \u0628\u062f\u06d5\u0646\u060c \u062f\u06d5\u0628\u06ce. \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u060c \u0626\u06ce\u0648\u06d5 \u0628\u06cc\u0627\u0646\u0648\u0648\u06cc \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06cc\u0627\u0646 \u0645\u06d5\u062f\u06c6\u0632\u0646\u06d5\u0648\u06d5. \u062e\u0648\u0627 \u062c\u06ce \u0628\u06d5\u0631\u0632 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u0631\u0633\u0627\u0646 \u06a9\u06d5 \u0698\u0646 \u0648 \u0634\u0648\u0648 \u0644\u06ce\u06a9 \u062f\u0648\u0648\u062f\u06b5\u0646 \u0648 \u0648\u06ce\u06a9 \u0647\u06d5\u06b5\u0646\u0627\u06a9\u06d5\u0646\u060c \u06a9\u06d5\u06cc\u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9 \u0644\u06d5 \u06a9\u06d5\u0633\u0648\u06a9\u0627\u0631\u06cc \u067e\u06cc\u0627\u0648\u06d5\u06a9\u06d5 \u0648 \u06a9\u06d5\u0633\u0648\u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0698\u0646\u06d5\u06a9\u06d5 \u0628\u06b5\u0627 \u0628\u0628\u0646\u06d5 \u0646\u0627\u0648\u0628\u0698\u06cc\u06a9\u06d5\u0631\u061b \u0647\u06d5\u0631\u06af\u0627 \u0647\u06d5\u0631\u062f\u0648\u0648\u06a9 \u0644\u0627 \u0695\u0627\u0632\u06cc \u0628\u0648\u0648\u0646 \u067e\u06ce\u06a9 \u0628\u06ce\u0646\u06d5\u0648\u06d5\u060c \u062e\u0648\u0627 \u067e\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u060c \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0639\u06d5\u0628\u062f\u0627\u06cc\u06d5\u062a\u06cc\u06cc \u062e\u0648\u0627 \u0628\u06a9\u06d5\u0646 \u0648 \u0647\u06cc\u0686 \u0634\u062a \u0645\u06d5\u06a9\u06d5\u0646 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc. \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u062a\u0627\u0646 \u0648 \u062e\u0632\u0645\u06d5\u06a9\u0627\u0646 \u0648 \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646 \u0648 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646 \u0648 \u0647\u0627\u0648\u0633\u0627\u06cc \u062f\u06cc\u0648\u0627\u0631 \u0628\u06d5 \u062f\u06cc\u0648\u0627\u0631 \u0648 \u0647\u0627\u0648\u0633\u0627\u06cc \u062f\u0648\u0648\u0631 \u0648 \u0647\u06d5\u0648\u0627\u06b5\u06cc \u0647\u06d5\u0645\u06cc\u0634\u06d5\u06cc\u06cc\u062a\u0627\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u06ce\u062f\u0627 \u0645\u0627\u0648\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0648\u0648\u0646\u06d5\u062a\u06d5 \u0645\u0648\u06b5\u06a9\u062a\u0627\u0646\u060c \u062a\u0627 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0686\u0627\u06a9\u06d5 \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u0648\u0648\u062a \u0628\u06b5\u0646\u062f \u0648 \u062e\u06c6\u067e\u06d5\u0633\u0646\u062f\u0646\u060c \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686\u06a9\u0627\u0645\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0686\u06a9\u0648\u0648\u0633\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u06d5 \u067e\u06cc\u0634\u06d5 \u0648 \u062e\u06d5\u06b5\u06a9\u06cc\u0634 \u0628\u06c6 \u0695\u0698\u062f\u06cc \u0647\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646\u060c \u0626\u06d5\u0648 \u0628\u06d5\u0634\u06d5\u06cc \u062e\u0648\u062f\u0627 \u067e\u06ce\u06cc \u062f\u0627\u0648\u0646 \u062f\u06d5\u06cc\u0634\u06ce\u0631\u0646\u06d5\u0648\u06d5. [\u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5] \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u067e\u06ce\u0646\u06d5\u0632\u0627\u0646\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u0627\u0628\u0695\u0648\u0648\u0628\u06d5\u0631\u0645\u0627\u0646 \u0626\u0627\u0645\u0627\u062f\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06c6 \u0695\u0648\u0648\u0628\u06cc\u0646\u06cc \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645\u060c \u0645\u0627\u06b5\u06cc \u062e\u06c6 \u0628\u06d5\u062e\u062a \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u0646\u06cc\u06cc\u06d5\u060c [\u0647\u06d5\u0648\u0627\u06b5\u06d5\u062a\u06cc\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646]\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u06cc\u0627\u0631\u06cc \u0628\u06ce\u060c \u062e\u0631\u0627\u067e\u062a\u0631\u06cc\u0646 \u06cc\u0627\u0631\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0686\u06cc\u06cc\u0627\u0646 \u0644\u06ce\u062f\u06d5\u0647\u0627\u062a \u0626\u06d5\u0645\u0627\u0646\u06d5\u060c \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5\u0648 \u0645\u0627\u06b5\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0634\u06cc \u062f\u0627\u0648\u0646\u060c \u0628\u06cc\u0627\u0646\u0628\u06d5\u062e\u0634\u06cc\u0628\u0627\u061f \u062e\u0648\u0627 \u0628\u06d5 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u0627\u0634 \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u062e\u0648\u0627 \u0628\u06d5 \u0633\u06d5\u0646\u06af\u06cc \u0645\u06ce\u0631\u0648\u0648\u0644\u06d5\u06cc \u0632\u06d5\u0631\u062f\u06cc \u0648\u0631\u062f\u06cc\u0644\u06d5\u0634 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u0647\u06cc\u0686\u06a9\u06d5\u0633 \u0646\u0627\u06a9\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0645\u0628\u06d5\u0631 \u0628\u06d5 \u0647\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06cc\u06a9\u06d5\u0646\u060c \u062f\u0648\u0648 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u067e\u0627\u062f\u0627\u0634 \u062f\u06d5\u062f\u0627 \u0648 \u0633\u06d5\u0631\u06d5\u0695\u0627\u06cc \u0626\u06d5\u0648 \u067e\u0627\u062f\u0627\u0634 \u062f\u0627\u0646\u06d5\u0634 \u062e\u06d5\u06b5\u0627\u062a\u06cc \u06af\u0631\u06cc\u0646\u06af\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u062f\u06d5\u0628\u06ce \u0686\u06c6\u0646 \u0628\u06ce \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u0647\u0627\u0648\u0626\u0627\u06cc\u06cc\u0646 \u06cc\u06d5\u06a9\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u0646 \u06a9\u06d5 \u0628\u0628\u0646\u06d5 \u0634\u0627\u06cc\u06d5\u062a \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u061f \u062a\u06c6\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06ce\u06a9 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u06a9\u0644\u0627 \u0628\u0648\u0648\u0646 \u0648 \u0628\u06d5 \u0642\u0633\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u060c \u062d\u06d5\u0632\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u0695\u06ce\u06a9\u06cc \u0695\u0648\u0648\u06a9\u0627\u0631\u06cc \u0632\u06d5\u0648\u06cc \u0628\u0627\u0646\u061b \u0646\u0627\u0634\u062a\u0648\u0627\u0646\u0646 \u0647\u06cc\u0686 \u0642\u0633\u06d5\u06cc\u06ce\u06a9 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0648\u06d5\u0634\u06ce\u0631\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06d5\u0633\u06d5\u0631\u062e\u06c6\u0634\u06cc \u0644\u06d5 \u0646\u0648\u06ce\u0698\u06ce \u0646\u0632\u06cc\u06a9 \u0645\u06d5\u0628\u0646\u06d5\u0648\u06d5\u060c \u0647\u06d5\u062a\u0627 \u062a\u06ce\u0628\u06af\u06d5\u0646 \u062f\u06d5\u06b5\u06ce\u0646 \u0686\u06cc. \u0628\u06d5 \u0644\u06d5\u0634\u067e\u06cc\u0633\u06cc\u0634 \u0646\u0648\u06ce\u0698\u06ce \u0645\u06d5\u06a9\u06d5\u0646 - \u0645\u06d5\u06af\u06cc\u0646 \u0648\u06d5\u062e\u062a\u06ce \u0695\u0627\u06af\u0648\u0632\u06d5\u0631 \u0628\u0646 - \u062a\u0627 \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u062e\u06c6 \u062f\u06d5\u0634\u06c6\u0631\u0646 \u0646 \u0626\u06d5\u06af\u06d5\u0631 - \u062f\u0646\u06cc\u0627\u0628\u0648\u0648 - \u0646\u06d5\u062e\u06c6\u0634 \u0628\u0648\u0648\u0646 \u06cc\u0627\u0646 \u0695\u06ce\u0628\u0648\u0627\u0631 \u0628\u0648\u0648\u0646 \u06cc\u0627\u0646 \u06cc\u06d5\u06a9\u06ce\u06a9\u062a\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u06ce\u0634\u0627\u0648\u06ce \u0647\u0627\u062a\u06d5\u0648\u06d5 \u06cc\u0627\u0646 \u062a\u062e\u0648\u0646\u06cc \u0698\u0646\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0646 \u0648 \u062f\u06d5\u0633\u062a\u0648\u0648 \u0628\u06d5 \u0626\u0627\u0648 \u0695\u0627\u0646\u06d5\u06af\u06d5\u06cc\u0634\u062a\u060c \u062e\u06c6\u06b5\u06ce\u06a9\u06cc \u067e\u0627\u06a9 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u0646 \u0648 \u0695\u0648\u0648\u062e\u0633\u0627\u0631 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646\u06cc \u067e\u06ce \u0628\u0647\u06d5\u0646\u0648\u0648\u0646. \u062e\u0648\u062f\u0627 \u0686\u0627\u0648\u067e\u06c6\u0634\u06cc\u0648 \u0644\u06ce\u062f\u06d5\u06a9\u0627 \u0648 \u0644\u06ce\u0648 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062a \u0646\u06d5\u062f\u06cc\u0648\u06d5 \u06a9\u06d5 \u0628\u06d5\u0634\u06ce\u06a9 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5\u060c \u0686\u06c6\u0646 \u0695\u06ce \u0644\u06d5 \u062e\u06c6 \u0647\u06d5\u06b5\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u062f\u06d5\u0634\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0626\u06ce\u0648\u06d5\u0634 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0644\u0627 \u0628\u062f\u06d5\u0646\u061f',
    '\u062e\u0648\u062f\u0627 \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0646\u0627\u0633\u06ce\u060c \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u062a\u0627\u0646 \u0628\u06ce\u060c \u0628\u06d5\u0633\u06d5.',
    '\u0647\u06ce\u0646\u062f\u06ce \u06a9\u06d5\u0633 \u0644\u06d5 \u062c\u0648\u0648\u0644\u06d5\u06a9\u06d5\u06a9\u0627\u0646\u060c \u0648\u062a\u0627\u0631\u06cc \u062e\u0648\u0627 \u062f\u06d5\u06af\u06c6\u0695\u0646 \u0648 \u0642\u0633\u06d5\u06cc \u062a\u0631\u06cc \u062f\u06d5\u062e\u06d5\u0646\u06d5 \u062c\u06ce. \u0628\u06d5 \u062a\u06c6\u0634 \u062f\u06d5\u06b5\u06ce\u0646: \u0628\u06cc\u0633\u062a\u0645\u0627\u0646 \u0648 \u06af\u0648\u06ce\u0645\u0627\u0646 \u0646\u06d5\u062f\u0627\u061b \u062a\u06c6\u0634 \u0628\u0628\u06cc\u0633\u06d5: \u062f\u06d5\u06a9 \u0642\u06d5\u062a \u06af\u0648\u06ce\u062a \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0646\u06d5\u0628\u06ce! \u0632\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0644\u0627\u0644\u0686 \u062f\u06d5\u06af\u0631\u0646 \u0648 \u062a\u0627\u0646\u06d5 \u0644\u06d5 \u062f\u06cc\u0646\u062a \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0628\u06d5 \u062a\u06d5\u0634\u06d5\u0631 \u0626\u06ce\u0698\u0646: \u00ab\u0695\u0627\u0639\u0646\u0627\u00bb. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646 \u0628\u06cc\u0627\u0646\u06af\u0648\u062a\u0627\u06cc\u06d5: \u0628\u06cc\u0633\u062a\u0645\u0627\u0646 \u0648 \u0628\u06d5 \u0642\u0633\u06d5\u062a \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u0644\u06ce\u0645\u0627\u0646 \u0628\u06cc\u0633\u062a\u06cc \u0648 \u062f\u06d5\u0628\u06ce \u0686\u0627\u0648\u06ce\u06a9\u062a \u0644\u06ce\u0645\u0627\u0646 \u0628\u06ce\u060c \u0628\u06c6\u06cc\u0627\u0646 \u0686\u0627\u06a9\u062a\u0631 \u0648 \u0695\u0627\u0633\u062a\u062a\u0631 \u0628\u0648\u0648. \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0695\u06ce \u06af\u06c6\u0695\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0641\u0631\u06cc\u0646\u06cc \u062e\u06c6\u06cc \u0644\u06ce \u06a9\u0631\u062f\u0648\u0648\u0646 \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5 - \u06a9\u06d5\u0645\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u06d5\u0628\u06ce - \u0646\u0627\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646.',
    '\u0626\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u06a9\u062a\u06ce\u0628\u06cc \u062e\u0648\u062f\u0627\u0648 \u0628\u06c6 \u0647\u0627\u062a\u0628\u0648\u0648! \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646 \u0628\u06d5\u0645\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u0646\u0627\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0647\u06d5\u06cc\u06d5 \u0648\u0627\u0646 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u062f\u0627. \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5\u06cc \u062f\u06d5\u0645\u0648\u0686\u0627\u0648\u06af\u06d5\u0644\u06ce \u0633\u0627\u0641 \u0628\u06a9\u06d5\u06cc\u0646 \u0648 \u0648\u06d5\u06a9\u06cc \u067e\u0634\u062a\u06cc \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0644\u06ce \u06a9\u06d5\u06cc\u0646\u060c \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0634\u06d5\u0645\u06c6\u06cc\u0627\u0646 \u0634\u06a9\u0627\u0646\u062f\u060c \u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u0626\u06ce\u0645\u06d5 \u06a9\u06d5\u0648\u0646 \u0648 \u062e\u0648\u0627 \u0628\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u062f\u0627 \u0647\u06d5\u0631 \u0648\u06d5\u062f\u06cc \u062f\u06ce.',
    '\u062e\u0648\u062f\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u062e\u06c6\u0634 \u0646\u0627\u0628\u06ce \u06a9\u06d5 \u0628\u06ce\u0698\u0646 \u0647\u0627\u0648\u06a9\u0627\u0631\u06cc \u0647\u06d5\u06cc\u06d5. \u0644\u06d5\u0645\u06d5 \u0628\u06d5\u062f\u06d5\u0631 \u0647\u06d5\u0631\u06a9\u06ce \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce\u060c \u0644\u06ce\u06cc \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06b5\u06ce\u0646: \u062e\u0648\u0627 \u0647\u0627\u0648\u06a9\u0627\u0631\u06cc \u0647\u06d5\u06cc\u06d5\u060c \u0628\u0648\u062e\u062a\u0627\u0646\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06d5\u0645 \u062f\u06d5\u0631\u062f\u06d5\u0686\u06ce \u0648 \u062a\u0627\u0648\u0627\u0646\u06ce\u06a9\u06cc \u0645\u06d5\u0632\u0646 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5\u062a \u0646\u06d5\u0695\u0648\u0627\u0646\u06cc\u0648\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u067e\u0627\u06a9\u0627\u0646\u06d5 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u061f \u0646\u0627\u0632\u0627\u0646\u0646 \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5 \u0642\u06d5\u062f \u0647\u06d5\u0648\u062f\u0627\u06cc \u0628\u0627\u0631\u06cc\u06a9\u06cc \u0646\u0627\u0648\u06a9\u06d5\u062e\u0648\u0631\u0645\u0627\u0634 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0646\u0627\u06a9\u0627.',
    '\u062f\u06d5\u0628\u0695\u0648\u0627\u0646\u06d5 \u062f\u0631\u06c6\u06cc \u0686\u0644\u06c6\u0646 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u0627\u0648\u06d5 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646\u061b \u062a\u0627\u0648\u0627\u0646 \u0644\u06d5\u0645\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u062a\u0631 [\u0646\u06d5\u0628\u0648\u0648\u06d5 \u0648] \u0646\u0627\u0628\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062a \u0646\u06d5\u062f\u06cc\u0648\u06d5\u060c \u06a9\u06d5 \u0648\u0627 \u0628\u06d5\u0634\u06ce\u06a9 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5 \u0648 \u0628\u06d5 \u0628\u062a \u0648 \u062a\u0627\u063a\u0648\u0648\u062a\u06ce \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061f \u0626\u06ce\u0698\u0646: \u0695\u06ce \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0633\u06d5\u0631\u0695\u0627\u0633\u062a\u062a\u0631\u06d5!\u061f',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u06d5\u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u061b \u0647\u06d5\u0631\u06a9\u06ce \u062e\u0648\u0627 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u0644\u06ce \u0628\u06a9\u0627\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc \u0646\u0627\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06d5\u0648 \u0645\u0648\u06b5\u06a9\u06d5\u0648\u06d5 \u0647\u06d5\u06cc\u06d5\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06cc\u0634\u06cc\u0627\u0646\u0628\u06ce\u060c \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc \u062a\u06ce\u06a9\u0648\u06b5\u06cc \u062f\u06d5\u0646\u06a9\u06d5 \u062e\u0648\u0631\u0645\u0627\u06cc\u06d5\u06a9 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0646\u06cc\u06cc\u06d5.',
    '\u06cc\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0626\u06ce\u0631\u06d5\u06cc\u06cc \u0628\u06d5\u0645\u0627\u0646\u06d5 \u062f\u06d5\u0628\u06d5\u0646 \u06a9\u06d5 \u062e\u0648\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0648\u0648\u0646\u061f \u062e\u06c6 \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u06a9\u062a\u06ce\u0628 \u0648 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u0645\u0627\u0646 \u062f\u0627\u0648 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5\u0634\u0645\u0627\u0646 \u067e\u06ce \u0633\u067e\u0627\u0631\u062f\u0646.',
    '\u0628\u0695\u06ce\u06a9 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627 \u0648 \u0628\u0695\u06ce\u06a9\u062a\u0631 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06b5\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 [\u0628\u06d5\u0634\u06cc\u0627\u0646\u06d5] \u0628\u06d5\u0633\u06cc\u0634\u06cc\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u062d\u0627\u0634\u0627\u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u062f\u0627 \u0628\u06d5 \u0626\u0627\u06af\u0631 \u062f\u06d5\u06cc\u0627\u0646 \u0633\u0648\u0648\u062a\u06ce\u0646\u06cc\u0646 \u0647\u06d5\u062a\u0627 \u067e\u06ce\u0633\u062a\u06cc\u0627\u0646 \u062f\u0627\u067e\u0644\u06c6\u062e\u0627\u060c \u067e\u06ce\u0633\u062a\u06cc \u062a\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u06af\u06c6\u0695\u06cc\u0646 \u062a\u0627 \u0626\u0627\u0632\u0627\u0631 \u067e\u062a\u0631 \u0628\u0686\u06ce\u0698\u0646. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646\u06cc \u0628\u0695\u0648\u0627 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u0628\u06d5\u06cc\u0646\u06d5 \u0628\u0627\u063a\u0627\u062a\u06cc \u0648\u0627 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646\u061b \u0698\u0646\u0627\u0646\u06cc \u067e\u0627\u06a9 \u0644\u06d5 \u06af\u0634 \u0639\u06d5\u06cc\u0628 \u0648 \u0633\u06ce\u0628\u06d5\u0631\u06cc \u067e\u0695\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06ce.',
    '\u062e\u0648\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u0648\u0648 \u067e\u06ce\u062f\u06d5\u062f\u0627\u060c \u0647\u06d5\u0631\u0686\u06cc \u0645\u0627\u06b5\u06cc \u0626\u0627\u0645\u0627\u0646\u06d5\u062a\u06d5 \u0628\u06cc\u0627\u0646\u062f\u06d5\u0646\u06d5\u0648\u06d5 \u0628\u06d5 \u062e\u0627\u0648\u06d5\u0646\u06cc\u0627\u0646\u061b \u0647\u06d5\u0631\u06af\u0627\u06cc\u06d5\u06a9\u06cc\u0634 \u0628\u06d5\u06a9\u0627\u0631\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0695\u0627\u062f\u06d5\u06af\u06d5\u0646\u060c \u0628\u06ce \u0644\u0627\u06af\u0631\u06cc \u0648 \u0628\u06d5 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u067e\u06ce \u0695\u0627\u0628\u06af\u06d5\u0646. \u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u0628\u0627\u0634 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u062a\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9 \u062f\u06d5\u0628\u06cc\u0633\u06ce \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u06cc\u0634 \u062f\u06d5\u0628\u06cc\u0646\u06ce.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u0648 \u06a9\u0627\u0631\u0628\u06d5\u062f\u06d5\u0633\u062a\u0627\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u0646. \u0647\u06d5\u0631\u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0633\u06d5\u0631 \u0634\u062a\u06ce \u0644\u06d5 \u0646\u06ce\u0648 \u062e\u06c6\u062f\u0627 \u067e\u06ce\u06a9 \u0646\u06d5\u0647\u0627\u062a\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0628\u06d5\u062e\u0648\u062f\u0627 \u0648 \u0628\u06d5\u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u0647\u06d5\u06cc\u06d5\u060c \u06a9\u06ce\u0634\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062d\u06d5\u0648\u0627\u06b5\u06d5\u06cc \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0628\u06c6\u062a\u0627\u0646 \u0628\u0627\u0634\u062a\u0631\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0645\u06cc \u0686\u0627\u06a9\u062a\u0631\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u062f\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u062a \u0646\u06d5\u062f\u06cc\u0648\u06d5\u060c \u0648\u0627 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062e\u0633\u062a\u06c6\u062a\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0648 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u06cc \u0628\u06c6 \u062a\u06c6 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0648 \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5\u0631 \u0644\u06d5 \u062a\u06c6 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u060c \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u062f\u06d5\u0634\u06cc\u0627\u0646\u06d5\u0648\u06ce \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0631\u0646\u06d5 \u0644\u0627\u06cc \u062a\u0627\u063a\u0648\u0648\u062a\u061b \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5: \u062d\u0627\u0634\u0627\u06cc \u0644\u06ce\u06a9\u06d5\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0648\u0627\u06cc \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u0628\u06d5 \u06cc\u06d5\u06a9\u062c\u0627\u0631\u06cc \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06d5 \u0628\u06a9\u0627.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u062a\u0648\u0648 \u067e\u06ce\u062a\u0627\u0646 \u0628\u06b5\u06ce\u0646: \u0648\u06d5\u0631\u0646\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u062e\u0648\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce \u0648 \u0648\u06d5\u0631\u0646\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u060c \u062f\u0648\u0648\u0695\u0627\u0632\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u06a9\u06d5 \u0632\u06c6\u0631 \u0628\u06d5\u062a\u0648\u0646\u062f\u06cc \u0695\u0648\u0648\u06cc\u0627\u0646 \u0644\u06d5 \u062a\u06c6 \u0648\u06d5\u0631\u062f\u06d5\u06af\u06ce\u0695\u0646.',
    '\u0626\u06d5\u062f\u06cc \u0686\u06c6\u0646\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0647\u06d5\u0631\u06af\u0627 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9 \u0628\u0648\u0648\u0646\u060c \u062f\u06ce\u0646\u06d5 \u0644\u0627\u062a \u0648 \u0628\u06d5 \u062e\u0648\u0627 \u0633\u0648\u06ce\u0646 \u062f\u06d5\u062e\u06c6\u0646 \u06a9\u06d5: \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u0646\u06cc\u0627\u0632\u06cc \u0686\u0627\u06a9\u06d5 \u06a9\u0631\u062f\u0646 \u0648 \u0646\u06ce\u0648\u0627\u0646 \u062e\u06c6\u0634\u06cc \u0626\u06d5\u0648 \u0626\u0627\u06a9\u0627\u0631\u06d5\u0645\u0627\u0646 \u0695\u06d5\u0686\u0627\u0648 \u06a9\u0631\u062f\u061f',
    '\u062e\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0686\u06cc\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5. \u0626\u06d5\u062a\u06c6 \u0647\u06d5\u0631 \u0648\u0627\u0632\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06ce\u0646\u06d5 \u0648 \u0646\u0633\u062d\u06d5\u062a\u06cc\u0627\u0646 \u06a9\u06d5 \u0648 \u062a\u06d5\u0631\u0632\u06ce\u06a9\u06cc \u0648\u0627\u06cc\u0627\u0646 \u0628\u062f\u0648\u06ce\u0646\u06d5\u060c \u062f\u06b5\u06cc\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u062a \u0695\u0627\u06a9\u06ce\u0634\u06cc.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u0628\u06c6\u06cc\u06d5 \u0628\u0648\u0648\u06d5 \u06a9\u06d5 \u0628\u06d5\u062e\u06d5\u06b5\u06a9 \u0695\u0627\u0628\u06af\u06d5\u06cc\u06ce\u0646\u06ce\u060c \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u0627 \u062f\u06d5\u0628\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0642\u0628\u0648\u0648\u06b5 \u06a9\u06d5\u0646. \u062c\u0627 \u0626\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0644\u06d5\u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0626\u06d5\u0648 \u063a\u06d5\u062f\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6 \u06a9\u0631\u062f\u060c \u06af\u06d5\u0631 \u0628\u0647\u0627\u062a\u0628\u0627\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062a\u06c6 \u0648 \u062f\u0627\u0648\u0627\u06cc \u062e\u0648\u062f\u0627 \u0644\u06ce \u062e\u06c6\u0634\u0628\u0648\u0648\u0646\u06cc\u0627\u0646 \u0628\u06a9\u0631\u062f\u0627\u06cc\u06d5\u061b \u062a\u06c6\u06cc\u06d5\u06a9\u06cc\u0634 \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u060c \u062f\u0627\u0648\u0627\u06cc \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u0628\u0648\u0648\u0631\u062f\u0646\u062a \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06a9\u0631\u062f\u0628\u0627\u060c \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0626\u06d5\u0648\u0633\u0627 \u062f\u06d5\u06cc\u0627\u0646\u062f\u06cc \u06a9\u06d5 \u062e\u0648\u0627 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0642\u0628\u0648\u0648\u06b5 \u062f\u06d5\u06a9\u0627 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0646\u06d5\u062e\u06ce\u0631\u060c \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u0627\u0628\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646\u06cc \u0628\u0695\u0648\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0648\u06d5\u062e\u062a\u06ce \u06a9\u06d5 \u0644\u06d5 \u06a9\u06ce\u0634\u06d5 \u0648 \u0647\u06d5\u0631\u0627\u06cc \u0646\u06ce\u0648\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646\u0627 \u0628\u062a\u06a9\u06d5\u0646\u06d5 \u0642\u0627\u0632\u06cc \u0648 \u0644\u06d5 \u062f\u06b5\u06d5\u0648\u06d5 \u0628\u06d5\u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06ce \u062a\u06c6 \u0628\u06cc\u062f\u06d5\u06cc \u0695\u06d5\u062f\u0627\u06cc\u0627\u0646 \u0628\u06ce \u0648 \u0628\u06d5\u062a\u06d5\u0648\u0627\u0648\u06cc \u0645\u0644\u06a9\u06d5\u0686\u06cc \u0628\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5\u0645\u0627\u0646 \u0646\u0648\u0648\u0633\u06cc\u0628\u0627\u060c \u06a9\u0648\u0634\u062a\u0627\u0631\u06cc \u062e\u06c6\u0628\u06d5\u062e\u06c6 \u0628\u06a9\u06d5\u0646 \u06cc\u0627\u0646 \u0644\u06d5 \u0648\u06b5\u0627\u062a\u0648\u0648 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u0646 - \u06a9\u06d5\u0645\u06cc\u0627\u0646 \u0646\u06d5\u0628\u06ce - \u0646\u06d5\u06cc\u0627\u0646\u062f\u06d5\u06a9\u0631\u062f. \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u06af\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u0646\u06d5\u0633\u06cc\u062d\u06d5\u062a\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u0627 \u06a9\u0631\u062f\u0628\u0627\u06cc\u0627\u0646\u060c \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0686\u0627\u06a9\u06cc \u062f\u06d5\u0647\u06ce\u0646\u0627 \u0648 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u067e\u062a\u06d5\u0648\u062a\u0631 \u062f\u06d5\u0628\u0648\u0648.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5\u0648\u06d5 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u0645\u06d5\u0632\u0646\u0645\u0627\u0646 \u0628\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0628\u06d5\u062e\u0634\u06cc.',
    '\u0648\u0627\u0634\u0645\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f \u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0626\u06ce\u0645\u06d5\u0634 \u0634\u0627\u0631\u06d5\u0632\u0627\u0628\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0626\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u0628\u06ce\u060c \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0647\u0627\u0648\u0695\u06ce\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0648 \u062e\u0648\u062f\u0627\u067e\u06d5\u0631\u0633\u062a\u0627\u0646\u06cc \u0695\u0627\u0633\u062a\u06cc \u0648 \u0634\u06d5\u0647\u06cc\u062f\u0627\u0646 \u0648 \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u06d5\u06a9\u0627\u0646\u061b \u0686 \u0647\u0627\u0648\u0695\u06ce\u06cc\u0627\u0646\u06ce\u06a9\u06cc \u0686\u0627\u06a9\u06cc\u0634\u0646 \u0626\u06d5\u0645\u0627\u0646\u06d5!',
    '\u0626\u06d5\u0645 \u0686\u0627\u06a9\u06d5\u06cc\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0648 \u062e\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5 \u06a9\u06d5 \u0628\u06d5\u06a9\u0631\u062f\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0647\u06c6\u0634\u06cc \u062e\u06c6\u0648 \u0647\u06d5\u0628\u06ce \u0648 \u0626\u0627\u0645\u0627\u062f\u06d5 \u0628\u0646 \u062f\u06d5\u0633\u062a\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u060c \u06cc\u0627\u0646 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u062a\u06ce\u06a9\u0695\u0627\u06cc\u06cc\u060c \u0628\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc \u0648\u0627\u062a\u0627\u0646 \u062f\u06d5\u0646\u0627\u0648 \u062f\u0627\u0646 \u06a9\u06d5 \u0644\u06d5 \u0626\u0627\u0646\u0642\u06d5\u0633\u062a \u062e\u06c6 \u0648\u06d5\u062f\u0648\u0627 \u062f\u06d5\u062f\u0627 \u0648 \u0646\u0627\u06af\u0627\u062a\u06ce. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0686\u06c6\u0631\u062a\u0645\u06ce\u06a9 \u0628\u0646\u060c \u0626\u06ce\u0698\u06ce: \u062e\u0648\u062f\u0627 \u0645\u0646\u06cc \u0628\u06d5\u0686\u0627\u06a9\u06d5 \u062e\u0648\u06ce\u0646\u062f\u06d5\u0648\u06d5 \u06a9\u06d5 \u0645\u0646 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u0645.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0633\u0627\u06cc\u06d5\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627\u0648\u06d5 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06ce \u0628\u0627\u0634\u0648\u0648 \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u062a\u060c \u0648\u06d5\u06a9 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u062a\u0631\u0648\u0648 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0628\u06ce \u0648 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u062a\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u0628\u06ce\u060c \u0626\u06ce\u0698\u06ce: \u062e\u06c6\u0632\u06cc \u0645\u0646\u06cc\u0634 \u0695\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0628\u0627\u0645\u061b \u0626\u06d5\u0648\u0633\u0627 \u0645\u0646\u06cc\u0634 \u0644\u06d5\u0648 \u062e\u06ce\u0631\u0648\u0628\u06ce\u0631\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06d5 \u0628\u06d5\u0634\u062f\u0627\u0631 \u062f\u06d5\u0628\u0648\u0648\u0645.',
    '\u0628\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06c6 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u062f\u06d5\u0633 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0647\u06d5\u06b5\u062f\u06d5\u06af\u0631\u0646\u060c \u0628\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u0627\u062f\u0627. \u0626\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u0686\u06ce\u062a\u06d5 \u062e\u06d5\u0632\u0627 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627\u060c \u0686 \u0628\u06a9\u0648\u0698\u0631\u06ce \u0648 \u0686 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u06ce \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06ce.',
    '\u0628\u06c6\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627 \u0644\u06d5\u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0644\u06d5 \u0628\u06d5\u0631 \u0647\u06ce\u0632 \u0644\u06ce \u06af\u06cc\u0631\u0627\u0648\u06d5\u06a9\u0627\u0646\u061f \u0644\u06d5 \u0628\u06d5\u0631 \u067e\u06cc\u0627\u0648\u0627\u0646 \u0648 \u0698\u0646\u0627\u0646 \u0648 \u0645\u0646\u062f\u0627\u06b5\u0627\u0646\u06ce \u06a9\u06d5 \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0644\u06d5\u0645 \u0634\u0627\u0631\u06d5 \u0648\u0627 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0646\u060c \u0695\u0632\u06af\u0627\u0631\u0646 \u06a9\u06d5\u0648 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u062a\u06d5\u0648\u06d5 \u06cc\u0627\u0631 \u0648 \u06cc\u0627\u0648\u06d5\u0631\u06ce\u06a9\u0645\u0627 \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u0628\u06a9\u06d5!',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u062e\u06d5\u0632\u0627 \u0644\u06d5\u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062a\u0627\u063a\u0648\u0648\u062a \u0634\u06d5\u0695 \u062f\u06d5\u06a9\u06d5\u0646. \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u0686\u0646\u06d5 \u0634\u06d5\u0695\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06c6\u0633\u062a\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u0646\u060c \u0641\u06ce\u06b5\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0632\u06d5\u0628\u0648\u0648\u0646 \u0648 \u0628\u06ce \u0647\u06ce\u0632 \u0648 \u067e\u06ce\u0632\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062a \u0646\u06d5\u062f\u06cc\u0648\u06d5\u060c \u067e\u06ce\u06cc\u0627\u0646 \u06af\u0648\u062a\u0631\u0627: \u0647\u06d5\u062a\u0627 \u0644\u06d5 \u0645\u06d5\u06a9\u06a9\u06d5\u0646 \u0634\u06d5\u0695 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u062f\u06d5\u0646. \u06a9\u0627\u062a\u06ce \u0628\u06cc\u0633\u062a\u06cc\u0627\u0646 \u062e\u06d5\u0632\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5 \u0648 \u0632\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce \u0628\u0686\u0646\u06d5 \u0634\u06d5\u0695\u06ce\u060c \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5\u06cc \u06a9\u06d5\u062f\u06d5\u0686\u0646\u06d5 \u06af\u0698\u06cc\u0627\u0646 \u062a\u0631\u0633 \u0695\u06ce \u0646\u06cc\u0634\u062a\u060c \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u062a\u0631\u0633\u0627\u0648\u0646 \u0648 \u0628\u06af\u0631\u06d5 \u067e\u062a\u0631\u06cc\u0634 \u062f\u06d5\u062a\u0631\u0633\u0627\u0646\u061f \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0628\u06c6\u0686\u06cc \u062e\u06d5\u0632\u0627\u062a \u0628\u06c6 \u0646\u0648\u0648\u0633\u06cc\u0648\u06cc\u0646\u060c \u0686\u06cc \u062f\u06d5\u0628\u0648\u0648 \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u062a\u0631 \u062f\u0648\u0627\u062a \u062e\u0633\u062a\u0628\u0627\u0646\u061f \u0628\u06ce\u0698\u06d5: \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u06a9\u06d5\u0645 \u0648 \u06a9\u0648\u0631\u062a\u06d5 \u0648 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0698\u06cc\u0627\u0646\u06cc \u0626\u06d5\u0648\u0644\u0627 \u0632\u06c6\u0631 \u0628\u0627\u0634\u062a\u0631\u06d5 \u0648 \u0628\u06d5\u0642\u06d5\u062f \u062f\u0627\u0648\u06cc \u0632\u0631\u0627\u0648\u06cc \u0646\u0627\u0648 \u062f\u06d5\u0646\u06a9\u06d5 \u062e\u0648\u0631\u0645\u0627\u060c \u06a9\u06d5\u0633 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce \u0628\u0646 \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0642\u06d5\u06b5\u0627\u06cc \u0632\u06c6\u0631 \u0642\u0627\u06cc\u0645\u06cc\u0634 [\u062e\u06c6 \u0648\u06d5\u0634\u06ce\u0631\u0646]\u060c \u0645\u0631\u062f\u0646 \u062f\u06d5\u062a\u0627\u0646\u062f\u06c6\u0632\u06ce\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631\u06af\u0627 \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06ce\u062a\u06d5 \u0695\u06ce\u060c \u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5\u06cc\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u0646\u0627\u062e\u06c6\u0634\u06cc\u0634 \u0628\u0648\u0648\u0646\u060c \u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u0644\u06d5 \u062a\u06c6\u0695\u0627 \u0628\u0648\u0648. \u0628\u06ce\u0698\u06d5: \u062e\u06c6\u0634\u06cc \u0648 \u0646\u0627\u062e\u06c6\u0634\u06cc \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0633 \u062e\u0648\u0627\u06cc\u06d5. \u0626\u06d5\u0631\u06ce \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0686\u06cc\u06cc\u0627\u0646\u06d5\u061f \u0648\u06d5\u06a9\u0648\u0648 \u062f\u06d5\u0647\u06cc\u0686 \u0642\u0633\u06d5\u06cc\u06d5\u06a9 \u062a\u06ce\u0646\u06d5\u06af\u06d5\u0646 \u0648\u0627\u06cc\u06d5!',
    '\u062a\u0648\u0648\u0634\u06cc \u0647\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9 \u0628\u0628\u06cc\u060c \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5\u06cc\u06d5. \u0646\u0627 \u0644\u06d5\u0628\u0627\u0631\u06cc\u0634\u062a \u0628\u06ce\u062a\u06d5\u0695\u06ce\u060c \u0628\u0632\u0627\u0646\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062e\u06c6\u062a \u0695\u0627\u06cc\u06d5. \u062a\u06c6\u0645\u0627\u0646 \u06a9\u0644 \u06a9\u0631\u062f \u06a9\u06d5 \u0628\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0645\u06d5\u0631\u062f\u0645\u06d5\u0648\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u06ce. \u0647\u06ce\u0646\u062f\u06d5\u062a \u0628\u06d5\u0633\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0626\u0627\u06af\u0627\u06cc \u0644\u06ce\u062a \u0647\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u06d5\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u062e\u06c6\u06cc \u0644\u06ce \u0628\u0628\u0648\u06ce\u0631\u06ce\u060c \u0626\u06ce\u0645\u06d5 \u0628\u06c6\u06cc\u06d5\u0645\u0627\u0646 \u062a\u06c6 \u0646\u06d5\u0646\u0627\u0631\u062f \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631 \u0628\u06cc.',
    '[\u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u0647\u06d5\u0646] \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062a\u06c6 \u062f\u06d5\u0631\u0646\u0627\u0686\u06cc\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u06a9\u06d5 \u0644\u06ce\u062a \u062f\u0648\u0648\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u0646 - \u067e\u06ce\u0686\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062a\u06c6 - \u0634\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0627\u0648\u06ce\u0698\u06ce \u062f\u06d5\u06a9\u06d5\u0646. \u0647\u06d5\u0631 \u062e\u06d5\u06cc\u0627\u06b5\u06ce \u06a9\u06d5 \u0644\u06d5\u0634\u06d5\u0648\u062f\u0627 \u0628\u06d5\u062f\u06b5\u06cc\u0627\u0646\u062f\u0627 \u0695\u0627\u062f\u06d5\u0628\u0648\u0648\u0631\u06ce\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0646\u0648\u0648\u0633\u06ce. \u062a\u06c6 \u0626\u06d5\u0634\u06ce \u0648\u0627\u0632\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06ce\u0646\u06cc \u0648 \u062e\u06c6\u062a \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u067e\u06ce\u0631\u06cc \u0648 \u06a9\u0627\u0631\u0633\u0627\u0632\u062a \u062e\u0648\u062f\u0627 \u0628\u06ce \u0628\u06d5\u0633\u062a\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646 \u0648\u0631\u062f\u0646\u0627\u0628\u0646\u06d5\u0648\u06d5 \u0648 [\u0633\u06d5\u0631\u0646\u062c\u06cc \u0695\u0627\u0646\u06d5\u06a9\u06ce\u0634\u0627\u0648\u0646]\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0642\u0648\u0631\u0626\u0627\u0646 \u06a9\u0627\u0631\u06cc \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062a\u0631 \u0628\u0648\u0648\u0627\u06cc\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0645\u06d5\u0628\u06d5\u0633\u062a\u06cc \u0646\u0627\u0695\u06ce\u06a9 \u0648 \u062f\u0698\u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0627\u0646 \u0641\u0631\u06d5 \u062a\u06ce\u062f\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0647\u06d5\u0631\u06af\u0627\u06cc\u06d5\u06a9\u06cc \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0647\u06ce\u0645\u0646\u0627\u06cc\u06d5\u062a\u06cc\u060c \u06cc\u0627\u0646 \u0645\u06d5\u062a\u0631\u0633\u06cc\u060c \u062f\u06d5\u0646\u06af \u0648 \u0628\u0627\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u06af\u0627\u060c \u0633\u06d5\u0631\u0628\u06d5\u062e\u06c6 \u0644\u06d5 \u0644\u0627\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u06d5 \u0642\u0627\u0648. \u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u06a9\u0627\u0631\u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06cc\u0627\u0646\u06af\u0648\u062a\u0627\u06cc\u06d5\u060c \u062f\u06d5\u06cc\u0627\u0646\u0632\u0627\u0646\u06cc \u0695\u0627\u0633\u062a\u06d5\u0642\u06cc\u0646\u06d5\u06a9\u06d5\u06cc \u06a9\u0627\u0645\u06d5\u06cc\u06d5 \u0648 \u0626\u06d5\u0648 \u062c\u06c6\u0631\u06d5 \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u0627\u0646\u06d5 \u0644\u06d5 \u06a9\u0648\u06ce\u0648\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062f\u0627\u0648\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u0627\u062a\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u0627\u06cc\u06d5\u060c - \u06a9\u06d5\u0645\u062a\u0627\u0646 \u0646\u06d5\u0628\u06ce - \u062f\u06d5\u0628\u0648\u0648\u0646\u06d5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646.',
    '\u062a\u06c6 \u0644\u06d5\u0633\u06d5\u0631\u062a\u06d5 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u0686\u06cc\u06d5 \u062e\u06d5\u0632\u0627 \u0648 \u062a\u06c6 \u0647\u06d5\u0631 \u0626\u06d5\u0631\u06a9\u06cc \u062e\u06c6\u062a \u0644\u06d5\u0633\u06d5\u0631\u06d5. \u062f\u0646\u06d5\u0648 \u0647\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0628\u062f\u06d5\u060c \u0634\u0627\u06cc\u06d5\u062a \u062e\u0648\u0627 \u0644\u06d5 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0648 \u0632\u06d5\u0628\u0631\u0648\u0632\u06d5\u0646\u06af\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u0648\u0648\u067e\u0627\u0631\u06ce\u0632\u06ce. \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648\u0627\u0646 \u0628\u06d5\u06af\u0648\u0695\u062a\u0631 \u0648 \u0626\u0627\u0632\u0627\u062a\u0631\u06d5 \u0648 \u0644\u06d5 \u0628\u06d5\u06b5\u0627 \u0628\u06d5\u0633\u06d5\u0631\u0647\u06ce\u0646\u0627\u0646\u06cc\u0634 \u0644\u06d5\u0648\u0627\u0646 \u0628\u06d5\u062f\u06d5\u0633\u062a \u0648 \u0628\u0631\u062f\u062a\u0631\u06d5.',
    '\u0647\u06d5\u0631\u06a9\u06ce \u0628\u06ce\u062a\u06d5 \u062a\u06a9\u0627\u06a9\u0627\u0631 \u0648 \u062a\u06a9\u0627\u06cc \u0628\u06c6 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06d5 \u0628\u06ce\u060c \u062e\u06c6\u06cc\u0634\u06cc \u0628\u06d5\u0634\u06cc \u062a\u06ce\u062f\u0627 \u0647\u06d5\u06cc\u06d5. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062a\u06a9\u0627 \u0628\u06c6 \u062e\u0631\u0627\u067e\u06d5 \u0628\u06a9\u0627\u060c \u0626\u06d5\u0648\u06cc\u0634 \u0628\u06d5\u0634\u06cc \u0644\u06d5 \u062e\u0631\u0627\u067e\u06d5\u06a9\u06d5 \u067e\u06ce\u062f\u06d5\u06af\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0686\u0627\u06a9 \u0648 \u062e\u06c6\u0634\u06cc\u062a\u0627\u0646 \u0644\u06ce\u062f\u06d5\u06a9\u0627\u060c \u0626\u06ce\u0648\u06d5 \u0686\u0627\u06a9\u062a\u0631 \u0686\u0627\u06a9 \u0648 \u062e\u06c6\u0634\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06a9\u06d5\u0646\u061b \u06cc\u0627\u0646 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06d5\u0648 \u067e\u0631\u0633\u06a4\u06d5\u06a9\u06d5\u06cc \u0628\u062f\u06d5\u0646\u06d5\u0648\u06d5. \u062e\u0648\u0627 \u0628\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0628\u06ce. \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u0633\u062a\u0627\u0646\u06d5\u0648\u06d5\u062f\u0627 - \u06a9\u06d5 \u0647\u0627\u062a\u0646\u06cc \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 - \u0644\u06ce\u06a9\u062a\u0631\u0648\u0648 \u06a9\u06c6 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u062c\u0627 \u0642\u0633\u06d5\u06cc \u06a9\u06ce \u0644\u06d5 \u0642\u0633\u06d5\u06cc \u062e\u0648\u0627 \u0695\u0627\u0633\u062a\u0631\u06d5\u061f',
    '\u0626\u06ce\u0648\u06d5 \u0686\u06cc\u062a\u0627\u0646\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0648 \u062f\u0648\u0648\u0695\u0648\u0648\u0627\u0646\u06d5\u060c \u062e\u06c6\u062a\u0627\u0646 \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u062f\u0648\u0648 \u062a\u0627\u0642\u0645\u061f \u0644\u06d5 \u0628\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u060c \u0628\u06d5\u0631\u0627\u0648\u06d5\u0698\u0648\u0648\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 [\u0628\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u062f\u0627\u0646\u0627\u0648\u0646]. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 \u06a9\u06d5\u0633\u06ce \u062e\u0648\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u0644\u06ce \u06af\u0648\u0645 \u06a9\u0627\u060c \u0628\u06cc\u062e\u06d5\u0646\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u061f \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u0644\u06ce \u0628\u06af\u06c6\u0695\u06ce\u060c \u062a\u06c6 \u0646\u0627\u062a\u0648\u0627\u0646\u06cc \u0695\u06ce\u06af\u0627\u06cc\u06d5\u06a9\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u06cc.',
    '\u0626\u06d5\u0648 \u062f\u0648\u0648\u0695\u0648\u0648\u0627\u0646\u06d5 - \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06ce\u062f\u06cc\u0646\u0646 - \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u0626\u06ce\u0648\u06d5\u0634 \u0644\u06d5 \u062f\u06cc\u0646\u0648\u0648 \u0648\u06d5\u0631\u06af\u06ce\u0695\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0644\u06ce \u0628\u06ce. \u062c\u0627 \u062a\u0627 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0628\u06d5\u062f\u06d5\u0631 \u0646\u06d5\u06a9\u06d5\u0646 \u0648 \u0648\u06d5\u0644\u062a\u0627\u0646 \u0646\u06d5\u06cc\u06ce\u0646\u06d5 \u062e\u06d5\u0632\u0627\u06cc\u06d5\u060c \u0626\u06ce\u0648\u06d5 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0634\u06cc\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u060c \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce \u062a\u0648\u0648\u0634\u06cc\u0627\u0646 \u0628\u0648\u0648\u0646\u060c \u0695\u06ce\u06cc \u0647\u06d5\u06b5\u0627\u062a\u0646\u06cc\u0627\u0646 \u0645\u06d5\u062f\u06d5\u0646\u06ce \u0648 \u0641\u06d5\u0631\u0627\u0642\u06cc \u0695\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0628\u0695\u0646. \u062f\u06d5\u06af\u06d5\u06b5 \u0647\u06cc\u0686 \u06a9\u0627\u0645\u06cc\u0634 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u062a\u0627\u0646 \u0647\u06d5\u0631 \u0646\u06d5\u0628\u06ce \u0648 \u06cc\u0627\u0631 \u0648 \u06cc\u0627\u0648\u06d5\u0631 \u0644\u06d5 \u0646\u06ce\u0648\u06cc\u0627\u0646\u0627 \u0647\u06d5\u06b5\u0645\u06d5\u0628\u0698\u06ce\u0631\u0646.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0628\u0648\u0648\u0646\u06d5 \u0647\u0627\u0648\u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5\u061b \u06cc\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u0627\u0686\u0627\u0631\u06cc \u0647\u0627\u062a\u0648\u0648\u0646\u06d5 \u0628\u06d5\u0631 \u062f\u0627\u06b5\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0648 \u0644\u06d5\u0648 \u0646\u0627\u0648\u06d5\u062f\u0627 \u062f\u0648\u0648\u062f\u06b5 \u0645\u0627\u0648\u0646 \u0628\u06d5 \u06af\u0698 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0628\u0686\u0646 \u06cc\u0627 \u062a\u0648\u0648\u0634\u06cc \u062e\u0632\u0645 \u06a9\u0648\u0634\u062a\u0646 \u0628\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627\u060c \u0647\u06ce\u0646\u062f\u06d5\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u067e\u06ce\u062f\u06d5\u062f\u0627\u0646 \u06a9\u06d5 \u0628\u06d5 \u06af\u0698\u062a\u0627\u0646\u062f\u0627 \u0628\u0686\u0646\u06d5\u0648\u06d5 \u0648 \u0633\u06d5\u0631\u06cc\u0634 \u06a9\u06d5\u0648\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u0648\u0648\u0631 \u062e\u0633\u062a\u06c6\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u06af\u0698\u062a\u0627\u0646\u062f\u0627 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u0646 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0634\u06d5\u0695\u062a\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648\u062f\u0627 \u0646\u06d5\u0645\u06ce\u0646\u06ce\u060c \u062e\u0648\u062f\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u067e\u06ce \u0646\u06d5\u062f\u0627\u0648\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0631\u0646.',
    '\u062f\u06d5\u06af\u06d5\u06b5 \u06af\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u062a\u0631\u06cc\u0634\u0627 \u0626\u06ce\u0648\u06d5 \u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648 \u062f\u06d5\u0628\u0646 \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0647\u06d5\u0645 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0648 \u0647\u06d5\u0645 \u0644\u06d5\u062e\u0632\u0645 \u0648 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0626\u0627\u0631\u062e\u06d5\u06cc\u0627\u0646 \u0628\u0646\u061b \u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06ce\u06a9\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06ce\u06cc\u0627\u0646 \u0628\u06af\u06ce\u0695\u0646\u06d5\u0648\u06d5\u060c \u0628\u06ce \u0633\u06ce \u0648 \u062f\u0648\u0648 \u06a9\u0631\u062f\u0646 \u062f\u06d5\u0686\u0646\u06d5 \u0646\u0627\u0648 \u062e\u0632\u0645\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646. \u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06ce\u062a\u0627\u0646 \u0646\u06d5\u062a\u06d5\u06a9\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0633 \u0644\u06d5 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0647\u06d5\u06b5\u0646\u06d5\u06af\u0631\u0646\u060c \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce \u0628\u06d5\u06af\u06cc\u0631\u0648\u0648 \u0647\u06ce\u0646\u0627\u0646 \u0628\u06cc\u0627\u0646 \u06a9\u0648\u0698\u0646 \u0648 \u0645\u06d5\u0647\u06ce\u06b5\u0646 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u0648\u0648 \u0695\u0627\u06a9\u06d5\u0646. \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u062a\u06d5\u0648\u0627\u0648\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5 \u0628\u06d5 \u0626\u06ce\u0648\u06d5.',
    '\u0647\u06cc\u0686 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06ce \u0628\u06c6\u06cc \u0646\u06cc\u06cc\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06ce\u06a9\u06cc \u062a\u0631 \u0628\u06a9\u0648\u0698\u06ce\u060c \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5 \u0647\u06d5\u06b5\u06d5 \u0686\u0648\u0648\u0628\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5 \u0648\u060c \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06cc \u0628\u06d5\u0647\u06d5\u06b5\u06d5 \u0628\u06d5\u062f\u06d5\u0633\u062a \u06a9\u0648\u0698\u0631\u0627\u0648\u06d5\u060c \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u0628\u0631\u06cc \u0626\u06d5\u0648 \u06a9\u0648\u0634\u062a\u0646\u06d5 \u06a9\u06c6\u06cc\u0644\u06d5\u06cc\u06d5\u06a9\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0626\u0627\u0632\u0627\u062f \u0628\u06a9\u0627 \u0648 \u062e\u0648\u06ce\u0646\u0628\u0627\u06cc\u06cc\u0634\u06cc \u0628\u062f\u0627 \u0628\u06d5 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc. \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u067e\u06cc\u0627\u0648\u06d5\u062a\u06cc \u0628\u06a9\u06d5\u0646 \u0648 \u067e\u06ce\u06cc \u0628\u0628\u06d5\u062e\u0634\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u06a9\u0648\u0634\u062a\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5 \u0648 \u0633\u06d5\u0631 \u0628\u06d5 \u0647\u06c6\u0632\u06cc \u062f\u0648\u0698\u0645\u0646\u062a\u0627\u0646\u06d5\u060c \u0626\u06d5\u0648\u06cc \u0628\u06d5\u062f\u06d5\u0633\u062a\u06cc \u06a9\u0648\u0698\u0631\u0627\u0648\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u06d5\u062a\u06cc \u06a9\u06c6\u06cc\u0644\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u0626\u0627\u0632\u0627\u062f \u0628\u06a9\u0627\u062a \u0648 [\u0644\u06d5 \u062e\u0648\u06ce\u0646\u0628\u0627\u06cc\u06cc \u0648\u0627\u0632\u0628\u0647\u06ce\u0646\u0646]. \u0626\u06d5\u06af\u06d5\u0631 \u06a9\u0648\u0634\u062a\u06d5 \u0644\u06d5 \u0647\u06c6\u0632\u06ce\u06a9\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5 \u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0633\u0648\u06ce\u0646\u062f\u062e\u06c6\u0631\u0646\u060c \u062f\u06d5\u0628\u06ce \u062e\u0648\u06ce\u0646\u0628\u0627\u06cc\u06cc\u06d5\u06a9\u06d5\u06cc \u0628\u062f\u0631\u06ce \u0648 \u0628\u06d5\u0646\u062f\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06cc\u0634 \u0626\u0627\u0632\u0627\u062f \u0628\u06a9\u0627. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc \u0644\u06d5\u062f\u06d5\u0633 \u0642\u06d5\u0648\u0645\u06cc\u0648\u06d5 \u0648 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u0628\u06d5\u0646\u062f\u06d5 \u0626\u0627\u0632\u0627\u062f \u06a9\u0627\u060c \u0644\u06d5\u0633\u06d5\u0631\u06cc\u06d5\u062a\u06cc \u062f\u0648\u0648 \u0645\u0627\u0646\u06af\u06cc \u0695\u06d5\u0628\u06d5\u0642 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u06d5\u06a9 \u0628\u06d5 \u0695\u06c6\u0698\u0648\u0648 \u0628\u06ce. \u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u0644\u06ce \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0627. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0647\u06d5\u0631\u0644\u06d5 \u0626\u0627\u0646\u0642\u06d5\u0633\u062a \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06ce\u06a9 \u0628\u06a9\u0648\u0698\u06ce\u060c \u0644\u06d5 \u0628\u0631\u06cc \u0626\u06d5\u0648\u06d5 \u062a\u0627 \u0647\u06d5\u062a\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648 \u062c\u06d5\u062d\u06d5\u0646\u062f\u06d5\u0645 \u062f\u0627\u06cc\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u0644\u06ce\u06cc \u0628\u06d5 \u0695\u06a9\u062f\u0627 \u0686\u0648\u0648\u06d5 \u0648 \u0648\u06d5\u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u062e\u0633\u062a\u0648\u0648\u06d5 \u0648 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06ce\u06a9\u06cc \u062f\u0698\u0648\u0627\u0631\u06cc \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u062e\u062a\u06ce \u0628\u06c6 \u062e\u06d5\u0632\u0627 \u0628\u06d5 \u0695\u06ce\u062f\u0627 \u062f\u06d5\u0695\u06c6\u0646\u060c \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9 \u0644\u06ce\u06a9\u062f\u06d5\u0646\u06d5\u0648\u06d5. \u0646\u06d5\u06a9\u06d5\u0646 \u0644\u06d5 \u0628\u06d5\u0631 \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627\u060c \u0628\u06d5\u0648\u06d5\u06cc \u06a9\u06d5\u0641\u06d5\u0631\u0645\u0627\u0646\u0628\u06d5\u0631\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0631\u0628\u0695\u06cc\u0648\u06d5\u060c \u0628\u06ce\u0698\u0646: \u062a\u06c6 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0646\u06cc\u062a. \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0627\u06b5\u06cc \u062f\u0646\u06cc\u0627\u062a\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06d5\u060c \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06cc \u0632\u06c6\u0631 \u0648\u0627 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627. \u0626\u06ce\u0648\u06d5\u0634 \u0628\u06d5\u0631\u06ce \u0647\u06d5\u0631 \u0648\u0627\u0628\u0648\u0648\u0646 \u0648 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0646\u06d5\u0628\u0648\u0648\u061b \u062e\u0648\u062f\u0627 \u0626\u06ce\u0648\u06d5\u06cc \u0628\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc \u062e\u0648\u06ce\u0646\u062f\u06d5\u0648\u06d5. \u0633\u0627 \u0628\u06d5 \u0648\u0631\u062f\u06cc \u0644\u06d5 \u06a9\u0627\u0631\u06cc \u062e\u06c6 \u062e\u0648\u0631\u062f\u0628\u0646\u06d5\u0648\u06d5\u061b \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5\u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0645\u06ce\u0646\u0646 - \u062c\u06cc\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0648\u0642\u0648\u0633\u062a\u0627\u0646\u0646 - \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0686\u0646\u06d5 \u062c\u06d5\u0646\u06af \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0645\u0627\u06b5 \u0648 \u06af\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0644 \u06d5\u0695\u06ce\u06cc \u0626\u06d5\u0648\u062f\u0627 \u0628\u06d5\u062e\u062a \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9 \u0646\u06cc\u0646. \u062e\u0648\u062f\u0627 \u062e\u06d5\u0632\u0627\u06a9\u0627\u0631\u0627\u0646\u06cc \u0628\u06d5\u0631\u0627\u0645\u0628\u06d5\u0631 \u0628\u06d5 \u062e\u0627\u0646\u06d5\u0646\u0634\u06cc\u0646\u0627\u0646 - \u0628\u06d5 \u067e\u0644\u06d5\u06cc\u06d5\u06a9 - \u0633\u06d5\u0631\u062e\u0633\u062a\u0648\u0648\u06d5. \u062e\u0648\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0628\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5 \u06a9\u06d5 \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u06cc \u0644\u06d5 \u0645\u06d5\u06cc\u062f\u0627\u0646\u06cc \u062e\u06d5\u0632\u0627\u062f\u0627 \u0628\u0646\u060c \u0644\u06d5 \u0686\u0627\u0648 \u062e\u0627\u0646\u06d5\u0646\u0634\u06cc\u0646\u0627\u0646\u06d5\u06a9\u0627\u0646 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u0647\u06d5\u0631\u06d5 \u0645\u06d5\u0632\u0646\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06ce\u061b',
    '\u067e\u0644\u06d5 \u0648 \u067e\u0627\u06cc\u06d5\u06af\u06d5\u0644\u06ce\u06a9\u06cc \u0632\u06c6\u0631 - \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u0627\u0648\u06d5 - \u0647\u0627\u0648\u0695\u06ce \u062f\u06d5\u06af\u06d5\u06b5 \u0644\u06ce\u062e\u06c6\u0634\u0628\u0648\u0648\u0646 \u0648 \u0645\u06cc\u0647\u0631\u06d5\u0648\u0627\u0646\u06cc. \u062e\u0648\u0627 \u0628\u06d5\u062e\u0634\u06d5\u0646\u062f\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u062c\u06ce\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0645\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u063a\u06d5\u062f\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u06a9\u0627\u062a\u06ce \u0641\u0631\u06cc\u0634\u062a\u06d5\u06cc \u06af\u06cc\u0627\u0646 \u06a9\u06ce\u0634\u0627\u0646 \u067e\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u06b5\u06ce: \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0686 \u0628\u0627\u0631\u06ce\u06a9\u062f\u0627 \u0628\u0648\u0648\u0646\u061f \u062f\u06d5\u06b5\u06ce\u0646 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0647\u06ce\u0632 \u0648 \u0648\u0632\u06d5\u0644\u06ce\u06af\u06cc\u0631\u0627\u0648 \u0628\u0648\u0648\u06cc\u0646. \u067e\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u06b5\u06ce\u0646: \u0645\u06d5\u06af\u06d5\u0631 \u0633\u06d5\u0631\u0632\u06d5\u0648\u06cc\u0646\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u0627\u0648 \u0646\u06d5\u0628\u0648\u0648 \u062a\u0627 \u0644\u06d5 \u0632\u06ce\u062f\u06cc \u062e\u06c6 \u06a9\u06c6\u0686 \u06a9\u06d5\u0646 \u0648 \u0628\u0695\u06c6\u0646\u06d5 \u062c\u06ce\u06af\u0627\u06cc\u06d5\u06a9\u06cc \u062a\u0631\u061f \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u0648\u0698\u06d5\u06d5 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0626\u06d5\u0646\u0648\u0627\u06cc\u0627\u0646 \u0648 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc \u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u0647\u06d5\u0633.',
    '\u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648 \u0645\u06ce\u0631\u0648 \u0698\u0646 \u0648 \u0632\u0627\u0631\u06c6 \u0647\u06ce\u0632\u0644\u06ce\u06af\u06cc\u0631\u0627\u0648\u0627\u0646\u06d5\u06cc \u0647\u06cc\u0686 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u0628\u06ce \u0648 \u0628\u06d5 \u0695\u06ce\u06af\u06d5\u0634\u06cc\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u06cc\u0628\u06ce\u061b',
    '\u0647\u06cc\u0686 \u062f\u0648\u0648\u0631 \u0646\u06cc\u06cc\u06d5 \u062e\u0648\u062f\u0627 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u0628\u06d5\u062e\u0634\u06ce. \u062e\u0648\u062f\u0627 \u0686\u0627\u0648\u067e\u06c6\u0634\u06cc \u0644\u06d5 \u0644\u0627\u06cc\u06d5 \u0648 \u0628\u06d5\u062e\u0634\u06d5\u0646\u062f\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0644 \u06d5\u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u0648\u06d5\u062f\u0627 \u0628\u06ce\u060c \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u06af\u06d5\u0644\u06ce \u0634\u0648\u06ce\u0646\u06cc \u062d\u0627\u0648\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0641\u06d5\u0631\u062d\u0627\u0646\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u0628\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0632\u06ce\u062f\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0631\u0628\u06a9\u06d5\u0648\u06ce \u0648 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u062f\u0627 \u062f\u06d5\u0631\u0628\u06d5\u062f\u06d5\u0631 \u0628\u0648\u0648\u0628\u06ce \u0648 \u0644\u06d5 \u0626\u0627\u0648\u0627\u0631\u06d5\u06cc\u06cc\u062f\u0627 \u0628\u0645\u0631\u06ce\u060c \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u06d5\u0633\u062a\u06d5\u0628\u06d5\u0631\u06cc \u067e\u0627\u062f\u0627\u0634\u06cc\u06d5\u062a\u06cc \u0648 \u062e\u0648\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0631 \u0686\u0648\u0648\u0646\u06d5 \u0633\u06d5\u0641\u06d5\u0631 \u0648 \u062a\u0631\u0633\u0627\u0646 \u0644\u06d5\u0648\u06d5 \u06a9\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u062a\u06d5\u0634\u0642\u06d5\u06b5\u06d5\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646\u060c \u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u0648\u0648 \u0646\u0627\u06af\u0627\u062a\u06ce \u0646\u0645\u06ce\u0698\u062a\u0627\u0646 \u0647\u06d5\u06b5\u0628\u0642\u0631\u0686\u06ce\u0646\u0646. \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u0648\u0698\u0645\u0646\u062a\u0627\u0646\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6\u0634 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 [\u062f\u06d5\u0633\u06d5\u0641\u06d5\u0631\u062f\u0627 \u0628\u0648\u0648\u06cc] \u0628\u06c6 \u062e\u06c6\u062a \u0646\u0648\u06ce\u0698\u062a \u0628\u06c6 \u062f\u0627\u0628\u06d5\u0633\u062a\u0646\u060c \u0628\u0627 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06d5 \u0686\u06d5\u06a9\u06d5\u0648\u06d5 \u0646\u0648\u06ce\u0698 \u0644\u06d5 \u062f\u0648\u0627\u06cc \u062a\u06c6\u0648\u06d5 \u062f\u0627\u0628\u06d5\u0633\u062a\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0633\u0648\u0698\u062f\u06d5 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0628\u0686\u0646\u06d5 \u062f\u0648\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u067e\u0634\u062a\u06d5\u0648\u06d5 \u0628\u0648\u0648\u067e\u0627\u0631\u06ce\u0632\u0646. \u0626\u06d5\u0645\u062c\u0627\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u0648\u06ce\u0698\u062f\u0627 \u0628\u06d5\u0634\u062f\u0627\u0631 \u0646\u06d5\u0628\u0648\u0648\u0646\u060c \u0628\u06ce\u0646\u06d5 \u067e\u06ce\u0634 \u0648 \u0646\u0648\u06ce\u0698\u06ce \u062f\u06d5\u06af\u06d5\u06b5 \u062a\u06c6 \u062f\u0627\u0628\u06d5\u0633\u062a\u0646 \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06c6\u0634 \u0628\u0646 \u0644\u06d5 \u0686\u06d5\u06a9\u06d5\u06a9\u0627\u0646 \u0648 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646. \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u0646 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u06af\u0627 \u0648 \u0644\u06d5 \u0686\u06d5\u06a9 \u0648 \u0626\u06d5\u0633\u0628\u0627\u0628\u062a\u0627\u0646 \u0628\u0628\u0695\u06ce \u0648 \u0644\u06d5\u067e\u0695 \u067e\u06d5\u0644\u0627\u0645\u0627\u0631\u062a\u0627\u0646 \u0628\u062f\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0631\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u0633\u06d5\u063a\u06b5\u06d5\u062a\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627\u060c \u06cc\u0627\u0646 \u0646\u06d5\u062e\u06c6\u0634\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0686\u06d5\u06a9\u06d5\u06a9\u0627\u0646\u0648\u0648 \u062f\u0627\u0646\u06ce\u0646 \u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u0648\u0648 \u0646\u0627\u06af\u0627\u062a\u06ce\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u0648\u0634\u06cc\u0627\u0631 \u0628\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06cc\u0646 \u062f\u0648\u0698\u0645\u0646\u0646\u060c \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06cc \u0626\u0627\u0628\u0695\u0648\u0648\u0628\u06d5\u0631\u06cc \u0633\u0627\u0632 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u0646\u0648\u06ce\u0698\u06ce \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u060c \u0628\u06d5\u067e\u06ce\u0648\u06d5 \u0648 \u0628\u06d5 \u062f\u0627\u0646\u06cc\u0634\u062a\u0646 \u0648 \u06a9\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u0627\u06cc\u06d5\u06a9 \u0695\u0627\u062f\u06d5\u06a9\u0634\u06ce\u0646\u060c \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0644\u06d5 \u0628\u06cc\u0631 \u0646\u06d5\u0686\u06ce. \u0644\u06d5 \u0648\u06d5\u062e\u062a\u06cc \u062e\u0627\u062a\u0631\u062c\u06d5\u0645\u06cc\u0634\u062f\u0627 - \u06a9\u06d5 \u0647\u06cc\u0686 \u062a\u0631\u0633\u062a\u0627\u0646 \u0646\u06d5\u0645\u0627\u0648\u06d5 - \u0628\u06d5 \u0626\u0627\u0633\u0627\u06cc\u06cc \u0646\u0648\u06ce\u0698\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646. \u0646\u0648\u06ce\u0698 \u0644\u06d5 \u0633\u06d5\u0631 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06ce\u06a9\u06d5\u060c \u0648\u06d5\u062e\u062a\u06cc \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u06d5.',
    '\u0644\u06d5 \u062f\u0627\u0646\u06d5\u067e\u06d5\u06cc \u062f\u0648\u0698\u0645\u0646\u0627 \u0647\u06cc\u0686 \u0633\u0633\u062a\u06cc \u0645\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u0628\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 - \u0647\u06d5\u0631\u0648\u06d5\u06a9\u0648\u0648 \u0626\u06ce\u0648\u06d5 - \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u0628\u06cc\u0646\u0646. \u0626\u06ce\u0648\u06d5 \u0647\u0648\u0645\u06ce\u062f\u0648\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646 \u0626\u06d5\u0645\u06d5\u0634\u06cc\u0627\u0646 \u0644\u0627 \u0646\u06cc\u06cc\u06d5. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0644\u0627\u06cc \u062a\u06c6 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u060c \u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u062a\u06ce\u06cc\u062f\u0627\u06cc\u06d5 \u0695\u0627\u0633\u062a\u06d5. \u0633\u0627 \u062a\u06c6 \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5 - \u0648\u06d5\u06a9 \u062e\u0648\u062f\u0627 \u0695\u06ce\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0648\u06cc - \u0628\u06d5\u06a9\u0627\u0631\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0695\u0627\u0628\u06af\u06d5\u060c \u062e\u06c6\u0634\u062a \u0645\u06d5\u06a9\u06d5 \u0628\u06d5 \u0644\u0627\u06af\u0631\u06cc \u06af\u0632\u06cc\u06a9\u0627\u0631\u0627\u0646.',
    '\u0644\u06d5 \u0628\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u067e\u0627\u0695\u06ce\u0648\u06d5 \u06a9\u06d5 \u0644\u06ce\u062a \u062e\u06c6\u0634 \u0628\u06ce\u060c \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u06af\u06ce\u0631\u06d5 \u0648 \u06a9\u06ce\u0634\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0645\u06d5\u06a9\u06d5 \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06c6\u06cc\u0627\u0646 \u063a\u06d5\u06cc\u0627\u0646\u0646. \u062e\u0648\u0627 \u062d\u06d5\u0632 \u0646\u0627\u06a9\u0627 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0632\u06c6\u0631 \u063a\u06d5\u06cc\u0627\u0646 \u0648 \u06af\u0648\u0646\u0627\u0647\u0628\u0627\u0631\u0646.',
    '\u0634\u062a \u0644\u06d5 \u062e\u06d5\u06b5\u06a9 \u062f\u06d5\u0634\u0627\u0631\u0646\u06d5\u0648\u06d5\u060c \u062e\u06c6 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0628\u0634\u06ce\u0631\u0646\u06d5\u0648\u06d5\u061b \u062e\u0648\u062f\u0627\u0634 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646\u06d5 \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0634\u06d5\u0648\u0627\u0646\u06d5 \u0642\u0633\u06d5\u06af\u06d5\u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u067e\u06ce \u0642\u0627\u062f\u06ce \u0646\u06cc\u06cc\u06d5. \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0626\u0627\u06af\u0627\u06cc \u0644\u06ce\u06cc\u06d5.',
    '\u0648\u0627 \u06af\u0631\u06cc\u0645\u0627\u0646 \u062a\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627 \u0698\u06cc\u0627\u0648\u0646\u060c \u062a\u0648\u0627\u0646\u06cc\u062a \u0644\u0627\u06af\u06cc\u0631\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06a9\u06d5\u06cc \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5 \u062c\u0648\u0627\u0628 \u0628\u06cc\u061b \u0626\u06d5\u062f\u06cc \u06a9\u06ce \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06d5\u06cc\u06d5 - \u06a9\u06d5 \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u062e\u0648\u062f\u0627\u0648\u06d5 - \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u06cc\u0627\u0646 \u06a9\u06ce \u0647\u06d5\u06cc\u06d5 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0648\u06d5\u0633\u062a\u06c6 \u0628\u06af\u0631\u06ce\u061f',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u06a9\u0627\u0631\u06cc \u0646\u0627\u0644\u06d5\u0628\u0627\u0631 \u0628\u06a9\u0627\u060c \u06cc\u0627\u0646 \u063a\u06d5\u062f\u0631\u06ce\u06a9\u06cc \u0644\u06d5 \u062e\u06c6\u06cc \u0628\u06a9\u0627\u060c \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062e\u0648\u0627\u0632\u06ce \u06a9\u06d5 \u0628\u06cc\u0628\u06d5\u062e\u0634\u06ce\u060c \u062f\u06d5\u0628\u06cc\u0646\u06ce \u06a9\u06d5 \u062e\u0648\u0627 \u0644\u06ce\u06cc \u0628\u0648\u0648\u0631 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u06af\u0648\u0646\u0627\u062d\u06ce \u0628\u06a9\u0627\u060c \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u06d5\u06cc \u0632\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u06c6\u06cc \u062f\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u06ce \u0648 \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06a9\u06ce \u0628\u06d5 \u0647\u06d5\u06b5\u06d5 \u06cc\u0627\u0646 \u0628\u06d5\u0642\u06d5\u0633\u062a\u060c \u062a\u0627\u0648\u0627\u0646\u06ce\u06a9\u06cc \u0644\u06ce \u0695\u0648\u0648\u0628\u062f\u0627 \u0648 \u0628\u06cc\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0628\u06ce\u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u060c \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5 \u0628\u0627\u0631\u06cc \u062a\u0627\u0648\u0627\u0646\u06cc\u060c \u0628\u0648\u062e\u062a\u0627\u0646\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0634\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u0627 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc \u062a\u06c6 \u0646\u06d5\u0647\u0627\u062a\u0627\u06cc\u06d5\u060c \u0647\u06ce\u0646\u062f\u06ce \u0644\u06d5\u0648\u0627\u0646 \u0642\u0648\u0631\u0645\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062e\u06c6\u0634 \u06a9\u0631\u062f\u0628\u0648\u0648 \u0695\u06ce\u062a \u0644\u06ce \u0648\u0646 \u06a9\u06d5\u0646. \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646\u0646 \u06a9\u06d5 \u0695\u06ce\u06af\u06d5 \u0644\u06d5\u062e\u06c6 \u06af\u0648\u0645 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648 \u0646\u0627\u062a\u0627\u0646\u0646 \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646\u06ce\u06a9 \u0644\u06d5 \u062a\u06c6 \u0628\u062f\u06d5\u0646. \u062e\u0648\u0627 \u06a9\u062a\u06ce\u0628\u06cc \u0628\u06c6 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0641\u06ce\u0631 \u06a9\u0631\u062f\u0648\u0648\u06cc\u061b \u0632\u06c6\u0631 \u0634\u062a\u06cc \u0648\u0627\u06cc \u0641\u06ce\u0631\u06cc \u062a\u06c6 \u06a9\u0631\u062f \u06a9\u06d5 \u062a\u06c6 \u067e\u06ce\u0634\u0648\u0648 \u0646\u06d5\u062a \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646. \u0686\u0627\u06a9\u06d5\u06cc \u0632\u06c6\u0631 \u0647\u06ce\u0698\u0627 \u0648 \u06af\u0631\u06cc\u0646\u06af\u062a \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u0627\u0648\u06d5.',
    '\u0632\u06c6\u0631\u0628\u06d5\u06cc \u0633\u0631\u062a\u06d5 \u0648 \u0686\u067e\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0646\u06cc\u06cc\u06d5\u060c \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0628\u0627\u0633\u06cc \u062e\u06ce\u0631\u0627\u062a \u0648 \u06a9\u0627\u0631\u06cc \u0695\u06d5\u0648\u0627 \u0648 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0628\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0645\u06d5\u062d\u0632\u06cc \u0695\u06d5\u0632\u0627\u06cc \u062e\u0648\u062f\u0627 \u0686\u067e\u06d5 \u0628\u06a9\u0627\u060c \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u062f\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5 \u067e\u0627\u0634 \u0695\u0627\u0633\u062a\u06cc \u0628\u06c6 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646 \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u0648\u0648\u0646\u06cc \u0644\u06d5\u0645 \u062f\u06cc\u0646\u06d5\u060c \u067e\u06ce\u0686\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u062c\u0648\u0648\u06b5\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0695\u0686\u06d5\u06cc\u06d5\u06a9 \u0628\u06af\u0631\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u06ce \u06a9\u06d5 \u0695\u06ce\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u060c \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0648 \u0695\u06ce\u06cc\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u0628\u06d5\u06cc\u0646 \u06a9\u06d5 \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u06cc\u0628\u0698\u0627\u0631\u062f\u0648\u0648\u06d5. \u062a\u0627 \u062f\u06d5\u06cc\u062e\u06d5\u06cc\u0646\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0648 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc \u0632\u06c6\u0631 \u0646\u0627\u062e\u06c6\u0634 \u062f\u06d5\u0628\u06ce.',
    '\u062e\u0648\u062f\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u0628\u0648\u0648\u0631\u06ce \u06a9\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0628\u06c6 \u0628\u0646\u0627\u0633\u0646. \u0644\u06d5\u0648\u06d5 \u067e\u06ce\u0648\u06d5 \u0644\u06d5\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06c6 \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u06a9\u0627\u060c \u0639\u0627\u0641\u0648\u0648\u06cc \u062f\u06d5\u06a9\u0627. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u0627\u0628\u0646\u06ce \u0644\u06d5 \u0695\u0627\u062f\u06d5\u0628\u06d5\u062f\u06d5\u0631 \u06af\u0648\u0645\u0695\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5\u062c\u06cc\u0627\u062a\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u0646\u0627 \u062f\u06d5\u0628\u06d5\u0646\u06d5 \u0628\u06d5\u0631 \u0628\u06d5\u0631\u062f \u0648 \u06a9\u0695\u06d5\u06a9\u06cc \u0648\u0627 \u06a9\u06d5 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0648\u06cc \u0645\u06ce\u0648\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u062f\u06cc\u0627\u0631\u06d5 \u0647\u0627\u0648\u0627\u0631 \u062f\u06d5\u0628\u06d5\u0646\u06d5 \u0628\u06d5\u0631 \u0626\u06d5\u0648 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06d5\u06cc \u0633\u06d5\u0631\u0628\u0632\u06ce\u0648\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u062f\u06d5\u0631\u06a9\u0631\u0627\u0648\u06d5\u061b',
    '\u062e\u0648\u062f\u0627 \u0628\u06d5 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u06cc\u0634 \u0644\u06d5 \u062c\u0648\u0627\u0628\u0627 \u06af\u0648\u062a\u0648\u0648\u06cc\u06d5: \u0634\u06d5\u0631\u062a\u06d5 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u062a\u06c6 \u0628\u06d5\u0634\u06ce\u06a9 \u0628\u06c6 \u062e\u06c6\u0645 \u06af\u0644 \u062f\u06d5\u0645\u06d5\u0648\u06d5\u061b',
    '\u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0644\u06d5 \u0695\u0627\u0633\u062a\u06d5 \u0695\u06ce \u06cc\u0627\u0646 \u062f\u06d5\u0631\u062f\u06d5\u062e\u06d5\u0645 \u0648 \u062f\u06b5\u06cc\u0627\u0646 \u067e\u0631 \u062f\u06d5\u06a9\u06d5\u0645 \u0644\u06d5 \u0626\u0627\u0648\u0627\u062a \u0648 \u0628\u06c6 \u062e\u06c6\u0645 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u062f\u06d5\u0645 \u06af\u0648\u06ce\u0686\u06a9\u06d5\u06cc \u0648\u06b5\u0633\u0627\u062a\u06cc\u0627\u0646 \u062f\u0627\u062f\u0695\u0646 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0631\u062f\u06d5\u062e\u06d5\u0645 \u062a\u06d5\u0631\u062d \u0648 \u0628\u06cc\u0686\u0645\u06cc \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627 \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5\u060c \u062a\u06ce\u06a9\u06cc \u0628\u062f\u06d5\u0646 \u0648 \u0628\u06cc\u06af\u06c6\u0695\u0646. \u062c\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u0648\u062f\u0627\u06cc \u0646\u06d5\u0648\u06ce \u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0628\u0628\u06ce\u062a\u06d5 \u062f\u06c6\u0633\u062a\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0686\u06d5\u0646\u062f \u0632\u06cc\u0627\u0646\u0645\u06d5\u0646\u062f \u062f\u06d5\u0628\u06ce.',
    '\u0628\u06d5\u06b5\u06ce \u0628\u06d5\u06b5\u06ce\u0646\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u062f\u0627 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648. \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u06a9\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0628\u06d5\u0648\u0627\u0646\u06cc \u062f\u06d5\u062f\u0627\u060c \u0641\u0631\u06cc\u0648 \u0646\u06d5\u0628\u06ce \u0686\u06cc\u062a\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u062c\u06ce\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062c\u06d5\u062d\u06d5\u0646\u062f\u06d5\u0645\u06d5 \u0648 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06cc \u062a\u0631\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06d5 \u0628\u06d5 \u0628\u0695\u0648\u0627\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631 \u0686\u0627\u06a9\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u0628\u06d5\u06cc\u0646\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u0648\u0627\u062f\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0642\u06d5 \u0648 \u06a9\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0695\u0627\u0633\u06af\u06c6\u062a\u0631\u06d5\u061f',
    '\u0646\u06d5 \u0628\u06d5 \u0626\u0627\u0648\u0627\u062a\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0648\u0627 \u0648 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc\u06d5 \u0628\u06d5 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u062d\u06d5\u0633\u06ce\u0628\u0646. \u0647\u06d5\u0631 \u0645\u0631\u06c6\u06cc\u06d5\u06a9 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0646\u0627\u0644\u06d5\u0628\u0627\u0631 \u0628\u06a9\u0627\u060c \u0628\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc \u062e\u06c6\u06cc \u062c\u0632\u06cc\u0627 \u062f\u06d5\u062f\u0631\u06ce \u0648 \u062c\u06cc\u0627 \u0644\u06d5 \u062e\u0648\u0627 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u0627\u0628\u06ce \u062f\u06c6\u0633\u062a\u06cc \u0628\u06ce \u0648 \u062f\u06d5\u0647\u0627\u0648\u0627\u0631\u06cc \u0628\u06ce.',
    '\u0647\u06d5\u0631 \u0645\u0631\u06c6\u06cc\u06d5\u06a9 \u2013 \u0646\u06ce\u0631 \u0628\u06ce \u06cc\u0627\u0646 \u0645\u06ce \u2013 \u0647\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u06ce\u060c \u062f\u06d5\u0686\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u062a\u06b5\u06cc\u0634\u06cc \u0648\u0631\u062f\u06cc\u0644\u06d5\u06cc \u0646\u0627\u0648\u06a9\u06d5\u062e\u0648\u0631\u0645\u0627\u06cc\u06d5\u0634 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0686 \u062f\u06cc\u0646\u062f\u0627\u0631\u06ce\u06a9 \u062f\u06cc\u0646\u06cc \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0686\u0627\u062a\u0631\u06d5 \u06a9\u06d5 \u062e\u06c6\u06cc \u0628\u06d5 \u062e\u0648\u0627 \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u06d5 \u0648 \u06a9\u06d5\u0648\u062a\u06c6\u062a\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0626\u0627\u06cc\u06cc\u0646\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645\u06cc \u0644\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u0644\u0627\u062f\u06d5\u0631 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0647\u06d5\u0642 \u0647\u0627\u062a\u0648\u0648\u061f \u0626\u06d5\u0648 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0628\u0648\u0648\u06d5 \u062f\u06c6\u0633\u062a\u06cc.',
    '\u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5\u060c \u0647\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u0648\u0627\u06cc\u06d5 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0698\u0646\u06d5\u06a9\u0627\u0646\u06d5\u0648\u06d5 \u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646\u061b \u0628\u06ce\u0698\u06d5: \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 - \u06a9\u06d5 \u0628\u06c6\u062a\u0627\u0646 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u06ce\u062a\u06d5\u0648\u06d5 - \u062e\u0648\u062f\u0627 \u062c\u0648\u0627\u0628\u06cc \u0626\u06d5\u0648 \u067e\u0631\u0633\u06cc\u0627\u0631\u06d5 \u0648 \u062f\u06d5\u062f\u0627\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0648 \u06a9\u06cc\u0698\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u0647\u06d5\u062a\u06cc\u0648\u06cc \u0645\u0627\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u0645\u0627\u06b5\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u06d5\u0648\u06ce\u060c \u0646\u0627\u06cc\u0627\u0646\u062f\u06d5\u0646\u06ce \u0648 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u06cc\u0627\u0646 \u0645\u0627\u0631\u06d5 \u0628\u06a9\u06d5\u0646\u060c \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0626\u06d5\u0648 \u0645\u0646\u0627\u06b5\u0627\u0646\u06d5\u06cc \u0632\u06d5\u0628\u0648\u0648\u0646 \u0648 \u0628\u06ce \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u0646\u060c \u0644\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0647\u06d5\u0645\u0648\u0648 \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646\u062f\u0627 \u0628\u06d5 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u0695\u06d5\u0641\u062a\u0627\u0631 \u06a9\u06d5\u0646 \u0648 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631\u06a9\u0627\u0631\u06ce \u0686\u0627\u06a9 \u0628\u06a9\u06d5\u0646\u060c \u0644\u06ce\u062a\u0627\u0646 \u0695\u0648\u0648\u0646 \u0628\u06ce \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06ce\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0698\u0646\u06ce\u06a9 \u0647\u06d5\u0633\u062a\u06cc \u0628\u06d5\u0648\u06d5 \u06a9\u0631\u062f \u0645\u06ce\u0631\u062f\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0634\u0642\u06cc \u0628\u0648\u0648\u06d5 \u06cc\u0627 \u0646\u06d5 \u0695\u0648\u0648\u06cc \u0644\u06ce \u0648\u06d5\u0631\u062f\u06d5\u06af\u06ce\u0695\u06ce\u060c \u0642\u06d5\u06cc\u062f\u06ce \u0646\u0627\u06a9\u0627 \u0647\u06d5\u0648\u06b5\u06cc \u0644\u06d5 \u0647\u06d5\u06a4 \u0647\u0627\u062a\u0646 \u0628\u062f\u06d5\u0646\u061b \u0626\u0627\u0634\u062a \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0644\u06d5\u0648 \u062f\u0695\u062f\u06c6\u0646\u06af\u06cc\u06d5 \u0628\u0627\u0634\u062a\u0631\u06d5. \u062e\u06c6\u0634\u06d5\u0648\u06cc\u0633\u062a\u06cc \u0645\u0627\u06b5\u06cc \u062f\u0646\u06cc\u0627\u0634 \u0644\u06d5 \u0644\u0627\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u06ce \u0647\u06d5\u06cc\u06d5. \u062c\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc \u0628\u06a9\u06d5\u0646 \u0648 \u062e\u06c6 \u0644\u06d5 \u0647\u06d5\u06b5\u06d5 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646\u060c \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u0647\u06d5\u0648\u06b5\u06cc\u0634 \u0628\u062f\u06d5\u0646\u060c \u0642\u06d5\u062a \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0644\u06d5\u06af\u06d5\u06b5 \u0698\u0646\u0627\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646\u062f\u0627 \u0628\u06ce \u062c\u06cc\u0627\u0648\u0627\u0632\u06cc \u0695\u06d5\u0641\u062a\u0627\u0631 \u0628\u06a9\u06d5\u0646. \u0633\u0627 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u0648\u0648\u0631 \u0645\u06d5\u062e\u06d5\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u0647\u06d5\u06b5\u067e\u06d5\u0633\u0627\u0631\u062f\u06d5 \u0648 \u062a\u06d5\u062e\u0633\u06cc\u0631\u06cc\u0627\u0646 \u06a9\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u067e\u06ce\u06a9 \u0628\u06ce\u0646 \u0648 \u062e\u06c6 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646\u060c \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u0631\u062f\u0646\u06cc \u0644\u06d5 \u0644\u0627\u06cc\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0628\u06d5 \u06cc\u06d5\u06a9\u062c\u0627\u0631\u06cc\u0634 \u062f\u06d5\u0633 \u0644\u06ce\u06a9 \u0628\u06d5\u0631\u062f\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u0641\u06d5\u0631\u062d\u0627\u0646\u06cc \u0644\u06d5 \u0644\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9\u06cc\u0627\u0646 \u0628\u06ce \u0646\u06cc\u0627\u0632 \u062f\u06d5\u06a9\u0627 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u0627\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9 \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u06a9\u062a\u06ce\u0628\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u060c \u0626\u06ce\u0648\u06d5\u0634\u0645\u0627\u0646 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5 \u06a9\u06d5 \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u062e\u0648\u0627 \u0628\u062a\u0631\u0633\u0646. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u062d\u0627\u0634\u0627\u0634\u06cc \u0644\u06ce \u0628\u06a9\u06d5\u0646\u060c \u062f\u06cc\u0633\u0627\u0646 \u0647\u06d5\u0631\u0686\u06cc \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u062e\u0648\u0627 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u06af\u0634 \u06a9\u06d5\u0633 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u067e\u06d5\u0633\u0646\u062f\u06cc \u0628\u062f\u0631\u06ce.',
    '\u0626\u0627\u0631\u06ce \u0647\u06d5\u0631\u0686\u06cc \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u062f\u0627\u06cc\u06d5\u060c \u0633\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627\u06cc\u06d5. \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u06a9\u0627\u0631\u0633\u0627\u0632 \u0628\u06ce \u0628\u06d5\u0633\u06d5.',
    '\u0626\u06d5\u06cc \u0645\u06d5\u0631\u062f\u0645! \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u062d\u06d5\u0632 \u06a9\u0627\u060c \u06af\u0634\u062a\u0648\u0648 \u062a\u06d5\u0631\u062a \u0648 \u062a\u0648\u0646\u0627 \u062f\u06d5\u06a9\u0627 \u0648 \u063a\u06d5\u06cc\u0631\u06cc \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062c\u06ce\u06af\u06d5\u0648 \u062f\u0627\u062f\u06d5\u0645\u06d5\u0632\u0631\u06ce\u0646\u06ce \u0648 \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc \u0644\u06d5 \u062f\u06d5\u0633 \u062f\u06ce.',
    '\u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u062e\u06c6\u0634\u06cc \u0695\u0627\u0628\u0648\u06ce\u0631\u06ce\u060c \u0628\u0627 \u062a\u06ce\u0628\u06af\u0627 \u06a9\u06d5 \u062e\u06c6\u0634\u06cc \u062f\u0646\u06cc\u0627 \u0648 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u0648\u0627 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648 \u062e\u0648\u062f\u0627\u0634 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0628\u06cc\u0646\u06d5\u0631\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06d5 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u0648 \u0628\u06ce \u062c\u06cc\u0627\u0648\u0627\u0632\u06cc [\u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0695\u0627\u0628\u06af\u06d5\u0646] \u0648 \u0628\u06c6 \u062e\u0648\u062f\u0627\u0634 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0628\u062f\u06d5\u0646\u061b \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u0627\u0646\u0648\u0648 \u0628\u06d5 \u0632\u06cc\u0627\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0648 \u062f\u0627\u06cc\u06a9 \u0648 \u0628\u0627\u0628\u062a\u0627\u0646 \u0648 \u062e\u0632\u0645\u0627\u0646\u06cc\u0634\u0648\u0648 \u0628\u06ce. [\u0626\u06d5\u0631 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06c6\u062f\u0631\u0627\u0648\u0627\u0646] \u062f\u0627\u0631\u0627 \u0628\u0646 \u06cc\u0627\u062e\u06c6 \u0647\u06d5\u0698\u0627\u0631 \u0628\u0646\u060c \u062e\u0648\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06b5\u0633\u06c6\u0632\u062a\u0631\u06d5\u061b \u0645\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u062a\u0627\u0646\u060c \u0646\u06d5\u0648\u0627 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0644\u0627 \u0628\u062f\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0632\u0645\u0627\u0646 \u0628\u062e\u06d5\u0646\u06d5 \u0644\u0627\u060c \u06cc\u0627 \u062e\u06c6\u062a\u0627\u0646 \u0644\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u0627\u0646 \u0628\u062f\u0632\u0646\u06d5\u0648\u06d5\u060c \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u062e\u0648\u0627 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0626\u06d5\u0634\u06ce \u0628\u0695\u0648\u0627\u0648 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0647\u06d5\u0628\u06ce \u0648 \u0628\u0695\u0648\u0627\u0648 \u0628\u06d5\u0648 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5 \u0647\u06d5\u0628\u06ce \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u0634 \u0628\u06ce \u06a9\u06d5 \u0628\u06d5\u0631 \u0644\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc \u0648 \u06a9\u062a\u06ce\u0628\u0627\u0646\u06cc \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u062d\u0627\u0634\u0627 \u0628\u06a9\u0627\u060c \u0644\u06d5 \u0695\u0627\u062f\u06d5\u0628\u06d5\u062f\u06d5\u0631 \u06af\u0648\u0645\u0695\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0647\u06d5\u0648\u06d5\u06b5\u062c\u0627\u0631 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u067e\u0627\u0634\u0627\u0646 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u061b \u062f\u0648\u0648\u0628\u0627\u0631\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u0628\u06d5 \u0634\u0648\u06ce\u0646\u06cc\u0627 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u0644\u06ce \u06a9\u0631\u062f\u060c \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc\u0627\u0646 \u067e\u062a\u06d5\u0648\u062a\u0631 \u0628\u0648\u0648\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062e\u0648\u062f\u0627 \u0646\u0627\u06cc\u0627\u0646 \u0628\u06d5\u062e\u0634\u06ce \u0648 \u0695\u06ce\u06af\u0627\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0646\u0627\u062f\u0627.',
    '\u0645\u0632\u06af\u06ce\u0646\u06cc \u0628\u06d5 \u0628\u06d5\u062f\u0648\u0648\u0695\u0627\u0632\u0627\u0646 \u06a9\u06d5 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646 \u0648\u0627 \u0644\u06d5 \u0695\u06ce\u06cc\u0627\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0628\u0646\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0648 \u0644\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u062f\u06d5\u062a\u06d5\u06a9\u0646\u06d5\u0648\u06d5\u060c \u0626\u0627\u062e\u06c6 \u0628\u06d5 \u062a\u06d5\u0645\u0627\u0646 \u06a9\u06d5 \u0644\u0627\u06cc \u0648\u0627\u0646 \u0642\u06d5\u062f\u0631 \u0648 \u062d\u0648\u0631\u0645\u06d5\u062a\u06cc\u0627\u0646 \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u06ce\u061f \u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0642\u06d5\u062f\u0631 \u0648 \u0695\u06ce\u0632 \u0647\u06d5\u0631 \u0644\u0627\u06cc \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0647\u06cc\u0686\u06cc \u062a\u0631.',
    '\u0644\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u0627 \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u06d5: \u0647\u06d5\u0631\u06af\u0627\u06cc\u06d5\u06a9\u06cc \u067e\u06ce\u062a\u0627\u0646 \u0632\u0627\u0646\u06cc \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9 \u0647\u06d5\u0646 \u0644\u06d5 \u0648\u06d5\u062e\u062a\u06cc \u0642\u0648\u0631\u0626\u0627\u0646 \u062e\u0648\u06ce\u0646\u062f\u0646\u0627 \u062f\u06d5\u0633 \u062f\u06d5\u06a9\u06d5\u0646 \u0628\u06d5 \u06af\u0627\u06b5\u062a\u06d5\u0648 \u06af\u06d5\u067e \u0648 \u0628\u06d5 \u062f\u0631\u06c6\u06cc \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u06d5\u06cc \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0628\u06ce - \u0647\u06d5\u062a\u0627 \u0626\u06d5\u0648 \u0628\u0627\u0633\u06d5 \u0646\u06d5\u06af\u06c6\u0695\u0646 - \u0644\u06d5 \u06af\u06d5\u06b5\u06cc\u0627\u0646\u062f\u0627 \u062f\u0627\u0628\u0646\u06cc\u0634\u0646\u061b \u062f\u06d5\u0646\u0627 \u0626\u06ce\u0648\u06d5\u0634 \u0626\u06d5\u0648\u0633\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0628\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u06af\u0634 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0648\u0648\u0695\u0648\u0648 \u0628\u0646\u060c \u0648\u06ce\u0695\u0627\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0644\u06d5 \u062f\u0648\u0698\u06d5\u06d5 \u06a9\u06c6 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06ce\u062a\u0627\u0646 \u062f\u06d5\u062e\u06d5\u06a4\u062a\u06ce\u0646: \u0633\u0627 \u0647\u06d5\u0631\u06af\u0627 \u062e\u0648\u0627 \u0626\u06ce\u0648\u06d5\u06cc \u0633\u06d5\u0631\u062e\u0633\u062a\u060c \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5\u0634 \u0647\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u06cc\u0646\u061f \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u0628\u06d5\u0634\u06ce\u06a9\u062f\u0627 \u0628\u0627\u0648 \u062e\u06c6\u0634\u062a\u0631 \u0628\u0648\u0648\u0646\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0647\u0648\u0646\u06d5\u0631 \u0647\u0648\u0646\u06d5\u0631\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0648\u0648\u061b \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u062f\u06b5 \u062f\u06d5\u06af\u06d5\u06b5\u062a\u0627\u0646 \u0628\u0648\u0648\u06cc\u0646\u061b \u0626\u06ce\u0645\u06d5 \u0646\u06d5\u0646\u0647\u06ce\u0634\u062a \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0628\u062a\u0627\u0646\u06af\u06d5\u0646\u06ce \u0648 \u062f\u06d5\u0631\u06c6\u0633\u062a\u0648\u0648 \u0628\u06ce\u0646. \u062e\u0648\u062f\u0627 \u062f\u0648\u0627\u06cc\u06cc \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u06d5 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u062a\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0647\u06cc\u0686 \u0695\u06ce\u06af\u0627\u06cc\u06d5\u06a9 \u0646\u0627\u062f\u0627 \u0628\u06d5 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u06a9\u06d5 \u062f\u0698\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0628\u06ce.',
    '\u0628\u06d5 \u062f\u06b5 \u062f\u06d5\u063a\u06d5\u06b5\u0627\u0646\u06cc \u062f\u0648\u0648\u0695\u0648\u0648 \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u062e\u0648\u0627 \u0641\u0631\u06cc\u0648 \u062f\u06d5\u0646\u060c \u06a9\u06d5\u0686\u06cc \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0627\u0646\u062e\u06d5\u06b5\u06d5\u062a\u0646\u06ce. \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0686\u0646 \u0646\u0648\u06ce\u0698 \u062f\u0627\u0628\u06d5\u0633\u062a\u0646\u060c \u0628\u06d5 \u062a\u06d5\u0648\u06d5\u0632\u06d5\u0644\u06cc \u0647\u06d5\u06b5\u062f\u06d5\u0633\u062a\u0646\u061b \u0695\u0648\u0648\u0628\u06cc\u0646\u06cc \u0644\u06d5 \u062e\u06d5\u06b5\u06a9 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u06a9\u06d5\u0645\u06cc\u0627\u0646 \u0646\u06d5\u0628\u06ce \u06a9\u06d5\u0645\u062a\u0631 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627\u0634 \u062f\u06ce\u0646\u0646\u061b',
    '\u0648\u0627\u0632\u0648\u0627\u0632\u06cc\u0646 \u0644\u06d5\u0645 \u0646\u06ce\u0648\u0627\u0646\u06d5\u062f\u0627\u061b \u062f\u0648\u0648\u062f\u06b5\u0646 \u0628\u0686\u0646\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0645\u0627\u0646\u060c \u06cc\u0627\u0646 \u062e\u06c6\u0628\u062f\u06d5\u0646\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646. \u06a9\u06d5\u0633\u06ce \u062e\u0648\u0627 \u0695\u06ce\u06cc \u0644\u06ce \u0628\u06af\u06c6\u0695\u06ce\u060c \u062a\u06c6 \u0646\u0627\u062a\u0648\u0627\u0646\u06cc \u0647\u06cc\u0686 \u0695\u06ce\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u06cc.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0645\u06d5\u0628\u0646\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u062f\u0698\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u062f\u06d5\u0646 \u0628\u06d5 \u062e\u0648\u0627\u061f!',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0648\u0648\u0695\u0648\u0648\u0646\u060c \u0648\u0627\u0646 \u0644\u06d5 \u0642\u0627\u062a\u06cc \u0647\u06d5\u0631\u06d5\u0628\u06d5\u0631\u06d5\u0698\u06ce\u0631\u06cc \u062f\u0648\u0698\u06d5\u06d5. \u062a\u06c6 \u0646\u0627\u062a\u0648\u0627\u0646\u06cc \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u06cc.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0648\u0648\u0695\u0648\u0648\u0646 \u062f\u06d5\u0633\u062a \u0644\u06d5 \u062f\u0648\u0648\u0695\u0648\u0648\u06cc\u06cc \u0647\u06d5\u06b5\u06af\u0631\u0646 \u0648 \u062a\u06d5\u0648\u0627\u0648 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0628\u0628\u0646\u06d5\u0648\u06d5 \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0686\u0627\u06a9\u06d5 \u0628\u06af\u0631\u0646\u06d5 \u0628\u06d5\u0631 \u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u067e\u06ce\u0631\u0646 \u0648 \u0628\u06d5 \u062f\u06b5\u06cc \u067e\u0627\u06a9 \u0628\u0628\u0646\u06d5 \u062f\u06cc\u0646\u062f\u0627\u0631\u06cc \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc. \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u0648\u06cc\u0634 \u0648\u06ce\u0695\u0627\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u062f\u06d5\u0628\u0646 \u0648 \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u062f\u0627 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646 \u067e\u0627\u062f\u0627\u0634\u06cc \u0632\u06c6\u0631 \u06af\u0631\u06cc\u0646\u06af \u062f\u06d5\u062f\u0627.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0686\u0627\u06a9\u06d5\u06cc \u0626\u06d5\u0648\u062a\u0627\u0646 \u062f\u06d5\u0686\u0627\u0648\u062f\u0627 \u0628\u06ce\u060c \u062e\u0648\u0627 \u0686 \u06a9\u0627\u0631\u06cc \u0628\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u0627\u0648\u06d5\u061f \u062e\u0648\u062f\u0627 \u0633\u0648\u067e\u0627\u0633\u06cc \u067e\u06ce\u0632\u0627\u0646\u0627\u0646 \u0642\u0628\u0648\u0648\u06b5 \u062f\u06d5\u06a9\u0627 \u0648 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u062e\u0648\u062f\u0627 \u067e\u06ce\u06cc \u062e\u06c6\u0634 \u0646\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06cc \u0628\u06d5\u0631\u0632 \u0642\u0633\u06d5\u06cc \u0646\u0627\u062d\u06d5\u0632 \u0628\u062f\u0631\u06a9\u06ce\u0646\u06ce\u061b \u0645\u06d5\u06af\u06cc\u0646 \u063a\u06d5\u062f\u0631\u06cc \u0644\u06ce\u06a9\u0631\u0627 \u0628\u06ce. \u062e\u0648\u062f\u0627 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u0631 \u0626\u06ce\u0648\u06d5 \u0686\u0627\u06a9\u06d5 \u0628\u06a9\u06d5\u0646 - \u0626\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc\u06d5 \u0648 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u06ce \u06cc\u0627\u0646 \u0646\u0647\u06ce\u0646\u06cc - \u06cc\u0627\u0646 \u0644\u06d5 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0646\u0627\u0644\u06d5\u0628\u0627\u0631 \u0686\u0627\u0648\u0628\u067e\u06c6\u0634\u0646\u060c \u062e\u0648\u0627 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5 \u0628\u06c6 \u0639\u0627\u0641\u0648\u0648 \u06a9\u0631\u062f\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06b5\u06cc\u0627\u0646 \u062f\u0698\u06cc \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc\u06d5\u062a\u06cc \u0648 \u062f\u06cc\u0627\u0631\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0641\u06d5\u0631\u0642\u06ce \u062f\u0627\u0646\u06ce\u0646\u060c \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0628\u06d5\u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u062c\u06ce\u06af\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u0632\u0627\u0646\u06cc\u0646\u061b \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u0644\u06d5\u0645 \u0646\u06ce\u0648\u0627\u0646\u06d5 \u0695\u06ce\u06af\u06d5\u06cc\u06d5\u06a9 \u0628\u06c6 \u062e\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u0646.',
    '\u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 \u0648 \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u0627\u0628\u0695\u0648\u0648\u0628\u06d5\u0631\u0645\u0627\u0646 \u0626\u0627\u0645\u0627\u062f\u06d5\u0633.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06d5\u0633 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0648 \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u0627 \u0641\u06d5\u0631\u0642 \u0648 \u062c\u06cc\u0627\u0648\u0627\u0632\u06cc \u062f\u0627\u0646\u0627\u0646\u06ce\u0646\u060c \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u062f\u0627 \u062e\u0648\u062f\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06ce \u0648 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646\u060c \u0644\u06ce\u062a \u062f\u06d5\u062e\u0648\u0627\u0632\u0646 \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc\u0627\u0646 [\u0628\u06d5 \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc] \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0628\u06ce\u0646\u06cc. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0626\u06d5\u0648\u06cc \u0644\u06d5 \u0645\u0648\u0648\u0633\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0648\u06cc\u0633\u062a\u060c \u0632\u06c6\u0631 \u0644\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0634 \u06af\u0631\u06cc\u0646\u06af\u062a\u0631 \u0628\u0648\u0648. \u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u0648\u0627\u0645\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u067e\u06ce \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5! \u0644\u06d5 \u0633\u0632\u0627\u06cc \u0626\u06d5\u0645 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc\u06d5\u062f\u0627 \u0628\u06d5\u0631 \u0647\u06d5\u0648\u0631\u06d5\u062a\u0631\u06cc\u0634\u0642\u06d5 \u06a9\u06d5\u0648\u062a\u0646. \u062c\u0627\u0631\u06ce\u06a9\u06cc \u062a\u0631 - \u067e\u0627\u0634 \u062f\u06cc\u062a\u0646\u06cc \u06af\u06d5\u0644\u06ce\u06a9 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 - \u0686\u0648\u0646 \u0628\u0648\u0648\u0646\u06d5 \u06af\u0648\u06ce\u0631\u06d5\u06a9\u06d5 \u067e\u06d5\u0631\u0633\u062a. \u0686\u0627\u0648\u067e\u06c6\u0634\u06cc\u0645\u0627\u0646 \u0644\u06d5\u0648\u06d5\u0634\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u062f\u0627\u0631\u06cc\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627.',
    '\u0628\u06d5 \u0647\u06c6\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646\u06ce \u06a9\u06d5 \u062f\u0627\u06cc\u0627\u0646\u060c \u0686\u06cc\u0627\u06cc \u062a\u0648\u0648\u0631\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0648\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0631\u0632\u06cc \u0695\u0627\u06af\u0631\u062a\u061b \u067e\u06ce\u0645\u0627\u0646 \u0648\u062a\u0646: \u0628\u06d5 \u0633\u0648\u0698\u062f\u06d5\u0648\u06d5 \u0644\u06d5\u0648 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u06d5 \u0628\u0686\u0646\u06d5 \u0698\u0648\u0648\u0631\u06ce \u0648 \u067e\u06ce\u0634\u0645\u0627\u0646 \u0648\u062a\u0646 \u0646\u0627\u0628\u06ce \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0634\u06d5\u0645\u0645\u06d5\u062f\u0627 \u062f\u06d5\u0633\u062a \u062f\u0631\u06ce\u0698\u06cc\u0648 \u0644\u06ce \u0695\u0648\u0648 \u0628\u062f\u0627\u061b \u067e\u06d5\u06cc\u0645\u0627\u0646\u06ce\u06a9\u06cc \u067e\u062a\u06d5\u0648\u06cc\u0634\u0645\u0627\u0646 \u0644\u06ce \u0633\u062a\u0627\u0646\u062f\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u0634\u06a9\u0627\u0646\u062f\u0646\u06cc\u0627\u0646 \u0648 \u062e\u06c6\u0628\u0648\u0627\u0631\u062f\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627 \u0648 \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u06a9\u0648\u0634\u062a\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc\u0627\u0646 \u0648 \u06af\u0648\u062a\u0646\u06cc\u0627\u0646: \u00ab\u0626\u06ce\u0645\u06d5 \u062f\u06b5\u0645\u0627\u0646 \u062f\u0627\u062e\u0631\u0627\u0648\u06d5\u00bb\u061b [\u0628\u06d5\u0631 \u0646\u0641\u0631\u06cc\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062e\u0633\u062a\u0646.] \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u062f\u06b5\u06cc \u0628\u06d5 \u0645\u06c6\u0631 \u062f\u0627\u062e\u0633\u062a\u0628\u0648\u0648\u0646. \u0626\u06cc\u062a\u0631 \u0628\u06d5\u0634\u06cc \u06a9\u06d5\u0645\u06cc\u0627\u0646 \u0646\u06d5\u0628\u06ce\u060c \u0644\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0647\u06ce\u0646\u0627\u0646 \u0628\u06ce \u0628\u06d5\u0634\u0646.',
    '\u0644\u06d5 \u0628\u06d5\u0631 \u0644\u06d5 \u0695\u06ce \u062e\u0648\u0627 \u0644\u0627\u062f\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u0628\u0648\u062e\u062a\u0627\u0646\u06d5 \u06af\u06d5\u0648\u0631\u06d5 \u0628\u06c6 \u0645\u06d5\u0631\u06cc\u06d5\u0645\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u061b',
    '\u0644\u06d5 \u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u06ce\u0645\u06d5 \u0639\u06cc\u0633\u0627\u06cc \u06a9\u0648\u0695\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u0631\u062f - \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u0628\u0648\u0648 - \u06a9\u06d5\u0686\u06cc \u0646\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u06a9\u0648\u0634\u062a\u06cc\u0627\u0646\u060c \u0646\u06d5 \u062a\u0648\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0642\u06d5\u0646\u0627\u0631\u06d5\u06cc \u062f\u06d5\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0644\u06ce \u062a\u06ce\u06a9 \u0686\u0648\u0648\u0628\u0648\u0648\u060c \u0646\u06d5\u06cc\u0627\u0646\u0646\u0627\u0633\u06cc \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0645 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u062f\u0627 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646\u06d5\u060c \u0644\u06d5 \u0695\u0627\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u0648\u0648\u062f\u06b5\u0646 \u0644\u06d5\u0648 \u0628\u0627\u0631\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u0627\u0646\u06cc \u06af\u0648\u0645\u0627\u0646\u0646 \u0648 \u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u0646\u06d5\u06cc\u0627\u0646\u06a9\u0648\u0634\u062a\u0648\u0648\u06d5\u061b',
    '\u0628\u06d5\u06b5\u06a9\u0648\u0648 \u062e\u0648\u0627 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u06a9\u06ce\u0634\u0627\u06cc\u06d5\u0648\u06d5. \u062e\u0648\u0627 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u062e\u0627\u0648\u06d5\u0646\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0628\u0648\u0648\u06d5 \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0627\u0646 - \u0628\u06d5\u0631 \u0644\u06d5 \u0645\u06d5\u0631\u06af\u06cc - \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u0639\u06cc\u0633\u0627 \u062f\u06d5\u0647\u06ce\u0646\u06ce\u060c \u062f\u06cc\u0627\u0631\u06d5 [\u0639\u06cc\u0633\u0627\u0634] \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0634\u0627\u06cc\u06d5\u062a \u0644\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646.',
    '\u0628\u06d5\u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u062c\u0648\u0648\u0644\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a \u0648 \u0632\u06c6\u0631 \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648\u06d5\u0631\u06af\u06d5\u0695\u0627\u0646\u060c \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0628\u06c6\u06cc\u0627\u0646 \u0695\u06d5\u0648\u0627 \u06a9\u0631\u0627\u0628\u0648\u0648\u060c \u0628\u06ce \u0628\u06d5\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646.',
    '\u0647\u06d5\u0631\u0648\u0627 \u0628\u06d5 \u0647\u06c6\u06cc \u0633\u0648\u0648\u062a \u062e\u0648\u0627\u0631\u062f\u0646\u06cc\u0627\u0646\u060c \u06a9\u06d5 \u067e\u06ce\u06cc\u0627\u0646 \u06af\u0648\u062a\u0631\u0627\u0628\u0648\u0648 \u0645\u06d5\u06cc\u062e\u06c6\u0646 \u0648 \u0628\u06d5 \u0641\u06ce\u0695\u06c6 \u062f\u0627\u0631\u0627\u06cc\u06cc \u0645\u06d5\u0631\u062f\u0645\u06cc\u0627\u0646 \u062f\u06d5\u062e\u0648\u0627\u0631\u062f\u061b \u0626\u06ce\u0645\u06d5\u0634 \u0628\u06c6 \u0626\u06d5\u0648 \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u0627\u0646\u0646 \u0648 \u0633\u067e\u06b5\u0627\u0646\u06d5 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u06af\u06d5\u0644\u06ce \u0628\u06d5\u0698\u0627\u0646\u0645\u0627\u0646 \u0633\u0627\u0632 \u062f\u0627\u0648\u06d5.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631 \u0632\u0627\u0646\u0627\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0648 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0627\u0646 - \u0628\u06d5 \u062a\u0627\u06cc\u0628\u06d5\u062a \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0648\u0627 \u0646\u0648\u06ce\u0698 \u0628\u06d5 \u0628\u0627\u0634\u06cc \u0628\u06d5\u062c\u06ce \u062f\u06ce\u0646\u0646 - \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u0631\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0628\u06d5\u0631\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0695\u0648\u0627\u0646 \u0648 \u0628\u06d5\u0648\u06d5\u06cc \u0628\u06c6 \u062a\u06c6 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0648 \u0628\u06d5\u0648\u06d5\u0634 \u0648\u0627 \u067e\u06ce\u0634 \u062a\u06c6 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u060c \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06d5\u0633\u061b \u0628\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u06af\u0631\u06cc\u0646\u06af \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646\u0645\u0627\u0646 \u062e\u0633\u062a\u06d5 \u062f\u06b5\u062a\u060c \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0646\u0648\u0648\u062d \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u062f\u0648\u0627\u06cc \u0648\u06cc\u0634\u0645\u0627\u0646 \u0648\u0627\u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f. \u062e\u0633\u062a\u06cc\u0634\u0645\u0627\u0646\u06d5 \u062f\u06b5\u06cc \u067e\u0627\u06a9\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644 \u0648 \u0626\u06cc\u0633\u062d\u0627\u0642 \u0648 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628 \u0648 \u0646\u06d5\u0648\u06d5\u06a9\u0627\u0646 \u0648 \u0639\u06cc\u0633\u0627 \u0648 \u0626\u06d5\u06cc\u06cc\u0648\u0628 \u0648 \u06cc\u0648\u0646\u0633 \u0648 \u0647\u0627\u0631\u0648\u0648\u0646 \u0648 \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646\u061b \u0632\u06d5\u0628\u0648\u0648\u0631\u06cc\u0634\u0645\u0627\u0646\u062f\u0627 \u0628\u06d5 \u062f\u0627\u0648\u0648\u062f.',
    '\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u062f\u06cc\u06a9\u06d5\u0634\u0645\u0627\u0646 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f\u0646\u061b \u0686 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u0628\u0648\u0648\u0646 \u0648 \u0686\u0648\u0648\u0646 \u0648 \u0633\u06d5\u0631\u0628\u0647\u0648\u0631\u062f\u06cc\u0627\u0646\u0645\u0627\u0646 \u0628\u06c6 \u06af\u0648\u062a\u0648\u0648\u06cc \u0648 \u0686 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0645\u062f\u0648\u0627\u0646\u062f\u0648\u0648\u06cc. \u062e\u0648\u062f\u0627\u0634 \u0628\u06c6 \u062e\u06c6\u06cc - \u0695\u0627\u0633\u062a\u06d5\u0648\u062e\u06c6 - \u0645\u0648\u0648\u0633\u0627\u06cc \u062f\u0648\u0627\u0646\u062f\u0648\u0648\u0648\u06d5.',
    '\u06af\u0634 \u0644\u06d5 \u06af\u0634\u062a\u06cc\u0627\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0645\u0698\u062f\u06d5\u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0628\u0648\u0648\u0646. \u0647\u06d5\u062a\u0627 \u0644\u06d5 \u067e\u0627\u0634 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0626\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5 \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627 \u0646\u06d5\u0628\u06ce \u0628\u06c6 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u0626\u0627\u06af\u0627\u06cc \u0644\u06ce\u06cc\u06d5 \u0648 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u0646\u060c \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0632\u0627\u0646\u0633\u062a\u06cc \u062e\u06c6\u06cc \u0626\u06d5\u0645\u06d5\u06cc \u0628\u06c6 \u062a\u06c6 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631. \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0648\u0648\u0646\u06cc \u062e\u0648\u0627 \u0628\u06d5\u0633\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u06cc\u0646 \u062f\u06d5\u0631\u0686\u0648\u0648\u0646 \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u0627\u0633\u0627\u0646 \u062f\u06d5\u0628\u0695\u0646\u060c \u0695\u06ce \u0644\u06ce \u06af\u0648\u0645 \u0628\u0648\u0648\u06cc \u0644\u06d5 \u0695\u0627\u062f\u06d5\u0628\u06d5\u062f\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627 \u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u062e\u0648\u062f\u0627 \u0646\u06d5 \u0644\u06ce\u06cc\u0627\u0646 \u062e\u06c6\u0634 \u062f\u06d5\u0628\u06ce\u060c \u0646\u06d5 \u0695\u06ce\u06af\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627\u061b',
    '\u0645\u06d5\u06af\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u0695\u06ce\u06cc \u062c\u06d5\u062d\u06d5\u0646\u062f\u06d5\u0645\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0645\u06d5\u0634 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u0633\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0626\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u0628\u06d5\u0631\u0647\u06d5\u0642\u06d5 \u0648 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646\u0695\u0627 \u0647\u0627\u062a\u0648\u0648\u06d5. \u062f\u06d5\u0633\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc \u067e\u06ce \u0628\u06ce\u0646\u0646 \u0642\u0627\u0632\u0627\u0646\u062c\u062a\u0627\u0646\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0642\u0633\u06d5\u06cc \u062e\u0648\u062f\u0627\u0634 \u062f\u06d5\u0631\u0686\u0646\u060c \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646 \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5\u060c \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06a9\u06d5\u0633\u0627\u0646\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628! \u0647\u06d5\u06b5\u06d5\u0695\u06c6\u06cc\u06cc \u0644\u06d5 \u062f\u06cc\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646\u062f\u0627 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u062c\u06af\u06d5 \u0644\u06d5 \u0695\u0627\u0633\u062a \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0645\u06d5\u0628\u06ce\u0698\u0646. \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5 \u06a9\u06d5 \u0645\u06d5\u0633\u06cc\u062d\u06cc \u06a9\u0648\u0695\u06cc \u0645\u0631\u06cc\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0648 \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u06d5\u0641\u06d5\u0633\u06cc \u067e\u06cc\u0631\u06c6\u0632\u06cc \u062e\u06c6\u06cc \u0647\u0627\u0648\u06cc\u062a\u06cc\u06d5 \u0646\u0627\u0648 \u062a\u06c6\u0644\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645. \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646 \u0648 \u0645\u06d5\u0628\u06ce\u0698\u0646 \u0633\u06ce\u0646. \u0648\u0627\u0632 \u0644\u06d5\u0645 \u0642\u0633\u0627\u0646\u06d5 \u0628\u0647\u06ce\u0646\u0646\u061b \u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0648\u0627 \u0686\u0627\u06a9\u062a\u0631\u06d5. \u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631 \u062e\u0648\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0626\u06d5\u0648\u06cc\u0634 \u0644\u06d5\u0648 \u062a\u06c6\u0645\u06d5\u062a\u06d5 \u062f\u0648\u0648\u0631\u06d5 \u06a9\u06d5 \u0647\u06cc\u0686 \u0641\u0631\u0632\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u0647\u06d5\u0628\u06ce. \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5\u060c \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5. \u0628\u06c6 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627\u0646\u06cc \u06a9\u0627\u0631\u0648\u0628\u0627\u0631 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0628\u06d5\u0633\u06d5.',
    '\u0645\u06d5\u0633\u06cc\u062d \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5\u0648\u06d5 \u062d\u0627\u0634\u0627\u06cc \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u06d5 \u06a9\u06d5 \u0628\u06d5\u0646\u062f\u06d5\u06cc\u06d5\u06a9\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u0641\u0631\u0634\u062a\u0627\u0646\u06cc \u0646\u0632\u06cc\u06a9 \u0628\u06d5 \u062e\u0648\u062f\u0627\u0634\u060c \u0647\u06d5\u0631 \u0648\u06d5\u062a\u0631. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06d5 \u06a9\u0648\u06ce\u0644\u0627\u06cc\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u0695\u0627\u0632\u06cc \u0646\u06d5\u0628\u06ce\u062a \u0648 \u0628\u0627\u062f\u06cc \u0647\u06d5\u0648\u0627\u06cc\u06cc \u0628\u0646\u0648\u06ce\u0646\u06ce\u060c \u0647\u06d5\u0631 \u0628\u06cc\u0646\u0627 \u062e\u0648\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u06cc\u06d5\u06a9 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u06cc \u06a9\u06c6\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u0648\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u0633\u06d5\u0631\u0628\u0627\u0631\u06cc \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062e\u0648\u062f\u0627 \u067e\u062a\u0631 \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5 \u062f\u06d5\u06a9\u0627. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627\u0648\u06d5 \u0648 \u067e\u06cc\u0634\u06d5\u06cc\u0627\u0646 \u0644\u0648\u0648\u062a \u0628\u06d5\u0631\u0632\u06cc \u0628\u0648\u0648\u06d5\u060c \u062e\u0648\u062f\u0627 \u062a\u0648\u0648\u0634\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0648 \u0644\u06d5 \u062e\u0648\u0627 \u067e\u06ce\u0648\u06d5 \u0647\u06cc\u0686 \u062f\u06c6\u0633\u062a \u0648 \u0647\u0627\u0631\u06cc\u06a9\u0627\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0633 \u0646\u0627\u06a9\u06d5\u0648\u06ce.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0644\u06d5\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u0695\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5 \u0648 \u0695\u06c6\u0634\u0646\u0627\u06cc\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645\u0627\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce.',
    '\u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u0628\u06d5 \u0632\u0648\u0648\u06cc\u06d5\u06a9\u06cc \u0632\u0648\u0648 \u062f\u06d5\u06cc\u0627\u0646\u062e\u0627\u062a\u06d5 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc \u0648 \u0632\u06cc\u0627\u062a\u0631\u06cc\u0634. \u062f\u06d5\u0634\u06cc\u0627\u0646\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc.',
    '\u0644\u06d5\u0645\u06d5\u0695 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0628\u0627\u0648\u06a9 \u0648 \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0646\u06cc\u06cc\u06d5 \u0648 \u062f\u06d5\u0645\u0631\u06ce\u060c \u067e\u0631\u0633\u06cc\u0627\u0631\u062a \u0644\u06ce\u062f\u06d5\u06a9\u06d5\u0646\u061b \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648 \u062c\u0648\u0627\u0628\u06d5\u062a\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06d5\u0648\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06cc\u0627\u0648\u06ce \u0647\u06cc\u0686 \u0639\u06d5\u0648\u0644\u0627\u062f\u06cc \u0646\u06cc\u06cc\u06d5 \u0648 \u062f\u06d5\u0645\u0631\u06ce \u0648 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u062e\u0648\u0634\u06a9\u06ce\u06a9\u06cc \u0647\u06d5\u06cc\u06d5\u060c \u0646\u06cc\u0648\u06d5\u06cc \u0645\u06cc\u0631\u0627\u062a\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u0628\u0627. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0634\u06a9\u06d5\u06a9\u06d5 \u0628\u0645\u0631\u06ce \u0648 \u0647\u06cc\u0686 \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0644\u06d5 \u067e\u0627\u0634 \u0646\u06d5\u0628\u06ce\u060c \u0628\u0631\u0627\u06a9\u06d5\u06cc \u0645\u06cc\u0631\u0627\u062a\u06ce \u062f\u06d5\u0628\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0631\u062f\u0648\u0648 \u067e\u06cc\u0627\u0648\u06ce\u06a9\u06d5 \u0648 \u062f\u0648\u0648 \u062e\u0648\u0634\u06a9\u06cc \u0644\u06ce \u0645\u0627\u0646\u060c \u062f\u0648\u0648 \u0628\u06d5\u0634 \u0644\u06d5 \u0633\u06ce \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0686\u06d5\u0646\u062f \u062e\u0648\u0634\u06a9 \u0648 \u0686\u06d5\u0646\u062f \u0628\u0631\u0627 \u0644\u06d5 \u067e\u0627\u0634 \u0645\u0631\u062f\u0648\u0648 \u0628\u06d5 \u062c\u06ce \u0645\u0627\u0628\u0648\u0648\u0646 \u0648 \u0645\u0631\u062f\u0648\u0648 \u0641\u0631\u0632\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u0646\u06d5\u0628\u0648\u0648\u060c \u0647\u06d5\u0631 \u0646\u06ce\u0631\u06cc\u0646\u06d5\u06cc \u062f\u0648\u0648 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc \u0645\u06ce\u0648\u06cc\u0646\u06d5 \u0645\u06cc\u0631\u0627\u062a\u06ce \u062f\u06d5\u06af\u0631\u06ce. \u062e\u0648\u0627 \u0628\u06c6\u062a\u0627\u0646 \u0695\u0648\u0648\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u0646\u06d5 \u0648\u06d5\u06a9\u0648\u0648 \u0644\u06ce\u062a\u0627\u0646 \u062a\u06ce\u06a9 \u0628\u0686\u06ce. \u062e\u0648\u0627 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0631 \u0628\u06d5\u06b5\u06ce\u0646\u06ce\u06a9\u06cc \u06a9\u06d5 \u062f\u06d5\u06cc\u062f\u06d5\u0646\u060c \u067e\u06ce\u06a9\u06cc \u0628\u06ce\u0646\u0646. \u062e\u0648\u0627\u0631\u062f\u0646\u06cc \u06af\u06c6\u0634\u062a\u06cc \u0648\u06b5\u0633\u0627\u062a\u0648\u0648 \u0628\u06c6 \u0695\u06d5\u0648\u0627\u06cc\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0626\u06ce\u0698\u0646 \u062f\u06d5\u0631\u06cc\u0627\u0648\u06ce\u0646. \u0647\u06d5\u062a\u0627 \u0644\u06d5 \u0648\u06d5\u062e\u062a\u06cc \u0626\u06cc\u062d\u0631\u0627\u0645\u062f\u0627\u0646\u060c \u0695\u0627\u0648\u06ce \u0628\u06d5 \u0695\u06d5\u0648\u0627 \u0645\u06d5\u0632\u0627\u0646\u0646. \u062e\u0648\u062f\u0627 \u0686\u06c6\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06cc\u06cc\u06d5\u0648\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5\u0648 \u0695\u06ce\u0648\u0634\u0648\u06ce\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc \u062f\u0627\u0646\u0627\u0648\u06d5\u060c \u06a9\u0644\u0627 \u0645\u06d5\u0628\u0646. \u0634\u06d5\u0695 \u0644\u06d5 \u062d\u06d5\u0631\u0627\u0645\u06d5\u0645\u0627\u0646\u06af\u0627\u0646\u062f\u0627 \u0695\u06d5\u0648\u0627 \u0646\u06cc\u06cc\u06d5. \u0647\u06d5\u0631 \u0626\u0627\u0698\u06d5\u06b5\u06ce \u0646\u0627\u0648\u06cc \u0642\u0648\u0631\u0628\u0627\u0646\u06cc \u0644\u06d5 \u0633\u06d5\u0631\u06d5\u060c \u06cc\u0627\u0646 \u0642\u06d5\u06b5\u0627\u062f\u06d5\u06cc \u0644\u06d5 \u0645\u0644\u062f\u0627\u060c \u062f\u06d5\u0633\u062a\u06cc \u0628\u06c6 \u0645\u06d5\u0628\u06d5\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0626\u0627\u0632\u0627\u0631 \u0645\u06d5\u062f\u06d5\u0646 \u06a9\u06d5 \u0628\u06d5\u062a\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u0648\u0627 \u0695\u06c6\u0632\u06cc \u0648 \u0695\u06d5\u0632\u0627\u06cc \u062e\u0648\u0627 \u062f\u06d5\u0633 \u0628\u062e\u06d5\u0646. \u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u0626\u06cc\u062d\u0631\u0627\u0645 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u060c \u0695\u0627\u0648\u06a9\u0631\u062f\u0646\u0648\u0648 \u0628\u06c6 \u0695\u06d5\u0648\u0627\u06cc\u06d5. \u0695\u06a9\u06cc \u06a9\u06c6\u0646\u06cc\u0634 \u0647\u06d5\u06b5\u062a\u0627\u0646 \u0646\u06d5\u0646\u06ce\u060c \u06a9\u06d5 \u0646\u06d5\u06cc\u0627\u0646\u0647\u06ce\u0634\u062a \u0628\u06d5 \u0632\u06cc\u0627\u0631\u06d5\u062a\u06cc \u06a9\u0627\u0628\u06d5\u06cc \u0645\u0627\u06b5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06af\u06d5\u0646. \u0644\u06d5 \u0633\u06d5\u0631 \u0686\u0627\u06a9\u06d5 \u0648 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u06cc \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u0628\u062f\u06d5\u0646\u061b \u0646\u06d5\u06a9 \u0644\u06d5 \u0633\u06d5\u0631 \u062a\u0627\u0648\u0627\u0646 \u0648 \u063a\u06d5\u062f\u0631. \u0626\u06d5\u0634\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646\u060c \u0686\u0648\u0646\u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u0627\u0646 \u0632\u06c6\u0631 \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u062a\u06d5\u0645\u06ce \u062f\u06d5\u06a9\u0627.',
    '\u0644\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646 \u062d\u06d5\u0631\u0627\u0645 \u06a9\u0631\u0627\u0648\u06d5 \u0645\u0631\u062f\u0627\u0631 \u0648 \u062e\u0648\u06ce\u0646 \u0648 \u06af\u06c6\u0634\u062a\u06cc \u0628\u06d5\u0631\u0627\u0632 \u0648 \u0647\u06d5\u0631 \u0626\u0627\u0698\u06d5\u06b5\u06ce \u06a9\u06d5 \u0644\u06d5 \u0648\u06d5\u062e\u062a\u06cc \u0633\u06d5\u0631\u0698\u06ce \u06a9\u0631\u062f\u0646\u062f\u0627 \u0646\u0627\u0648\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627\u06cc \u0644\u06ce \u0628\u06ce\u0646\u0646 \u0648 \u062e\u06d5\u0646\u062f\u0642\u0627\u0648 \u0648 \u0628\u06d5\u0644\u06ce\u062f\u0627\u0646\u06ce \u062a\u06c6\u067e\u06ce\u0646\u0631\u0627\u0648 \u0648 \u0647\u06d5\u06b5\u062f\u06ce\u0631\u0627\u0648 \u0648 \u0628\u06d5 \u0634\u06d5\u0642\u06c6\u0646 \u062a\u06ce\u0647\u06d5\u06b5\u062f\u0631\u0627\u0648 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u0627\u0648\u0631 \u062f\u0695\u06cc\u0648\u06cc\u06d5\u062a\u06cc\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5\u0631 \u0644\u06d5 \u06af\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0686\u0648\u0648\u0646\u06cc \u0641\u0631\u06cc\u0627\u06cc \u06a9\u06d5\u0648\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u06a9\u06ce\u0644\u06d5\u06a9\u06d5 \u0628\u06d5\u0631\u062f\u0627\u0646\u062f\u0627 \u062f\u0627\u062f\u06d5\u0646\u0631\u06ce \u0648 \u0633\u06d5\u0631\u0698\u06ce \u062f\u06d5\u06a9\u0631\u06ce \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0642\u0648\u0645\u0627\u0631\u06cc \u062a\u06cc\u0631 \u062f\u0627\u0628\u06d5\u0634 \u062f\u06d5\u06a9\u0631\u06ce\u061b \u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06a9\u0627\u060c \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u0627. \u0626\u06d5\u0648\u0627 \u0626\u06d5\u0645\u0695\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0646\u0627\u0646\u0627\u0633\u0646 \u0644\u06d5 \u0632\u06cc\u0627\u0646 \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646 \u0628\u06d5 \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u060c \u0646\u0627\u0647\u0648\u0645\u06ce\u062f \u0628\u0648\u0648\u06af\u0646\u061b \u0644\u06d5 \u062e\u06c6\u0645 \u0628\u062a\u0631\u0633\u0646 \u0646\u06d5\u06a9 \u0644\u06d5 \u0648\u0627\u0646. \u0648\u0627 \u0644\u06d5\u0645\u0695\u06c6\u0648\u06d5 \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u062a\u0627\u0646\u0645 \u062a\u06d5\u0648\u0627\u0648 \u06a9\u0631\u062f \u0648 \u0628\u06d5 \u0628\u06ce \u06a9\u0648\u0631\u062a\u06cc \u0648 \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc \u0686\u0627\u06a9\u06d5\u0645 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u06a9\u0631\u062f\u061b \u0626\u06cc\u0633\u0644\u0627\u0645\u06cc\u0634\u0645 \u0628\u06c6 \u062f\u06cc\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f. \u06a9\u06ce\u0634 \u0644\u06d5 \u0628\u0631\u0633\u0627\u0646 \u0686\u0627\u0631\u06cc \u0646\u06d5\u0628\u06ce \u0648 \u0646\u06d5\u06cc\u06d5\u0648\u06ce \u062a\u0648\u0648\u0634\u06cc \u06af\u0648\u0646\u0627\u062d \u0628\u06ce\u060c \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u062e\u06d5\u062a\u0627\u067e\u06c6\u0634\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646: \u06a9\u0627\u0645\u06d5\u06cc\u06d5 \u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0628\u06c6\u06cc\u0627\u0646\u061f \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631 \u0634\u062a\u06ce \u06a9\u06d5 \u067e\u0627\u06a9\u06d5 \u0648 \u0647\u06d5\u0631 \u0646\u0686\u06cc\u0631\u06ce \u06a9\u06d5 \u062c\u0627\u0646\u06d5\u0648\u0631\u06cc \u0695\u0627\u0648\u06a9\u06d5\u0631\u062a\u0627\u0646 - \u06a9\u06d5 \u0641\u06ce\u0631\u06cc \u0695\u0627\u0648\u062a\u0627\u0646 \u06a9\u0631\u062f\u0628\u06ce \u0648\u06d5\u06a9 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0641\u06ce\u0631\u06cc \u0628\u0648\u0648\u0646 - \u0628\u06c6\u062a\u0627\u0646 \u0628\u06af\u0631\u0646\u061b \u0644\u06d5 \u06af\u06c6\u0634\u062a\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u062e\u06c6\u0646 \u0648 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0628\u06ce\u0646\u0646 \u0648 \u062a\u0631\u0633\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce. \u062e\u0648\u062f\u0627 \u0628\u06d5 \u067e\u06d5\u0644\u06d5 \u0628\u06d5 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u0648\u0627 \u0644\u06d5\u0645\u0695\u06c6\u0695\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u06cc \u062e\u0627\u0648\u06ce\u0646\u062a\u0627\u0646 \u0628\u06c6 \u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0648\u0627 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u062f\u06d5\u06cc\u062e\u06c6\u0646 \u067e\u06ce\u0648 \u0695\u06d5\u0648\u0627\u06cc\u06d5. \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u062e\u0648\u0627\u0631\u062f\u06d5\u0645\u06d5\u0646\u06cc \u0626\u06ce\u0648\u06d5\u0634 \u0628\u06c6 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u0695\u06d5\u0648\u0627\u06cc\u06d5. \u0698\u0646\u0627\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u0632\u0627\u062f \u0648 \u062f\u0627\u0648\u06ce\u0646\u067e\u0627\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0628\u06c6 \u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648 \u0698\u0646\u06d5 \u062f\u0627\u0648\u06ce\u0646\u067e\u0627\u06a9\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u06a9\u062a\u06ce\u0628\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5\u061b \u0628\u06d5\u0648 \u0645\u06d5\u0631\u062c\u06d5 \u06a9\u06d5 \u0645\u0627\u0631\u06d5\u06cc\u06cc\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u062f\u06d5\u0646\u06ce. \u062e\u06c6\u0634\u062a\u0627\u0646 \u062f\u06d5\u0628\u06ce \u062f\u0627\u0648\u06ce\u0646 \u067e\u0627\u06a9 \u0628\u0646 \u0646\u06d5 \u062f\u0627\u0648\u06ce\u0646 \u067e\u06cc\u0633. \u062f\u06c6\u0633\u062a\u06cc \u0645\u06ce\u0648\u06cc\u0646\u06d5\u0634\u062a\u0627\u0646 \u0644\u06d5 \u0698\u06ce\u0631\u06d5\u0648\u06d5 \u0646\u06d5\u0628\u06ce \u0647\u06d5\u0631\u06a9\u06d5\u0634 \u0644\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0647\u06ce\u0646\u0627\u0646 \u062d\u0627\u0634\u0627 \u0628\u06a9\u0627\u060c \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u0628\u06d5 \u0641\u06ce\u0695\u06c6 \u0686\u0648\u0648\u06d5 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 \u06cc\u06d5\u06a9\u06ce\u06a9\u06d5 \u0644\u06d5 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06ce\u0646\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u0695\u06cc\u0627\u0631\u06d5\u06cc \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646\u060c \u062f\u06d5\u0645\u0648\u0686\u0627\u0648 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0626\u0627\u0646\u06cc\u0634\u06a9\u0627\u0646 \u0628\u0634\u06c6\u0646\u061b \u062f\u06d5\u0633\u062a\u0648\u0648 \u0628\u06d5 \u062a\u06d5\u0695\u06cc \u0628\u0645\u0627\u06b5\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646\u062f\u0627 \u0648 \u067e\u0627\u06a9\u0627\u0646\u06cc\u0634\u062a\u0627\u0646 \u062a\u0627 \u0647\u06d5\u0631 \u062f\u0648\u0648 \u06af\u0648\u06ce\u0632\u06cc\u0646\u06af \u0628\u0634\u06c6\u0631\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u0644\u06d5\u0634\u067e\u06cc\u0633\u06cc \u0628\u0648\u0648\u0646\u060c \u062e\u06c6\u062a\u0627\u0646 \u062e\u0627\u0648\u06ce\u0646 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0646\u06d5\u062e\u06c6\u0634 \u06cc\u0627\u0646 \u0695\u06ce\u0628\u0648\u0627\u0631 \u0628\u0648\u0648\u0646 \u06cc\u0627\u0646 \u06cc\u06d5\u06a9\u06ce\u06a9\u062a\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u06ce\u0634\u0627\u0648\u06ce \u062f\u06ce\u062a\u06d5\u0648\u06d5 \u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0698\u0646\u0627\u0646 \u0648\u06ce\u06a9 \u06a9\u06d5\u0648\u062a\u0646 \u0648 \u062f\u06d5\u0633\u062a\u0648\u0648 \u0628\u06d5 \u0626\u0627\u0648 \u0695\u0627\u0646\u06d5\u06af\u06d5\u06cc\u0634\u062a\u060c \u0628\u06d5 \u062a\u06c6\u0632\u06cc \u067e\u0627\u06a9\u06cc \u0633\u06d5\u0631\u0632\u06d5\u0648\u06cc \u062f\u06d5\u0645 \u0648 \u0686\u0627\u0648 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0648 \u0628\u0647\u06d5\u0646\u0648\u0648\u0646. \u062e\u0648\u0627 \u0646\u0627\u06cc\u0647\u06d5\u0648\u06ce \u0628\u062a\u0627\u0646\u062e\u0627\u062a\u06d5 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5 \u062e\u0627\u0648\u06ce\u0646\u062a\u0627\u0646 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u0686\u0627\u06a9\u06d5\u062a\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5 \u0628\u06a9\u0627\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631 \u0628\u0646.',
    '\u0626\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc\u06d5 \u0644\u06d5 \u0628\u06cc\u0631 \u0645\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646 \u0648 \u0626\u06d5\u0648 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u062a\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u06ce \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u0627\u062a\u0627\u0646 \u0628\u06d5\u0633\u062a\u0648\u0648\u06d5 \u0648 \u06af\u0648\u062a\u0648\u0648\u062a\u0627\u0646\u06d5: \u0628\u06cc\u0633\u062a\u0645\u0627\u0646 \u0648 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0646. \u062a\u0631\u0633\u06cc\u0634\u062a\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06ce\u061b \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u0686\u06cc \u0628\u06d5 \u0646\u0627\u0648 \u062f\u06b5\u06cc\u0634\u0648\u0648\u062f\u0627 \u0628\u06ce \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06c6 \u062e\u0648\u0627 \u0695\u0627\u0633\u062a \u0648 \u0628\u06ce \u062e\u06d5\u0634 \u0628\u0646 \u0648 \u0628\u06d5 \u0647\u06d5\u0642\u0627\u0646\u06d5\u062a \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u06d5\u0646. \u062f\u0648\u0698\u0645\u0646\u06cc \u0648 \u0642\u06cc\u0646\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06ce\u06a9 \u0647\u06d5\u06b5\u062a\u0627\u0646 \u0646\u06d5\u0646\u06ce \u0628\u06d5 \u0647\u06d5\u0642\u0627\u0646\u06d5\u062a \u0695\u06d5\u0641\u062a\u0627\u0631 \u0646\u06d5\u06a9\u06d5\u0646. \u0628\u06d5 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u0648 \u0628\u06ce \u062c\u06cc\u0627\u0648\u0627\u0632\u06cc \u0644\u06d5\u06af\u06d5\u06b5 \u062e\u06d5\u06b5\u06a9\u0627 \u0695\u06d5\u0641\u062a\u0627\u0631 \u06a9\u0631\u062f\u0646 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0647\u06c6\u06cc \u067e\u062a\u0631 \u0646\u0632\u06cc\u06a9\u06d5\u0648\u06d5 \u0628\u0648\u0648\u0646 \u0628\u06d5 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u06cc. \u0644\u06d5 \u062e\u0648\u0627\u0634 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce \u0686\u0648\u0646\u06a9\u0648\u0648 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u062e\u0648\u062f\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u067e\u06ce\u062f\u0627\u0648\u0646 \u06a9\u06d5 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u06af\u0631\u06cc\u0646\u06af\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0628\u06d5\u062e\u0634\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0627\u0646\u06cc\u060c \u06af\u0634\u062a\u06cc\u0627\u0646 \u0647\u06d5\u0648\u0627\u06b5\u06cc \u0626\u0627\u06af\u0631\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc\u06d5 \u0648 \u0644\u06d5 \u0628\u06cc\u0631 \u0646\u06d5\u0686\u06ce \u06a9\u06d5\u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u06a9\u0631\u062f\u0646\u060c \u06a9\u06d5 \u0626\u06d5\u0648 \u062c\u0627\u0631\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9 \u0644\u06d5 \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u0648\u0648 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u0648\u0648 \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06cc\u062a\u0627\u0646 \u0628\u06c6 \u0628\u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u0648\u0627\u06cc \u06a9\u0631\u062f \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0646\u06d5\u0648\u06af\u0627\u062a\u06ce. \u0633\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce \u0648 \u0626\u06d5\u0634\u06ce \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u062e\u06c6\u06cc \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u067e\u06ce\u0631\u06ce.',
    '\u062e\u0648\u062f\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0628\u06d5\u0633\u062a. \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u0648\u0627\u0632\u062f\u06d5 \u06a9\u06d5\u0633\u0645\u0627\u0646 \u0628\u06c6 \u06a9\u0648\u06ce\u062e\u0627\u06cc\u06d5\u062a\u06cc \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f \u0648 \u062e\u0648\u0627 \u06af\u0648\u062a\u06cc: \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0645\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5\u0645. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u0648\u06ce\u0698 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u0627\u0646 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0633\u0648\u0648\u0631 \u0628\u0646\u060c \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0634\u0645 \u0628\u0627\u0648\u06d5\u0695\u062a\u0627\u0646 \u0628\u06ce \u0648 \u0642\u06d5\u062f\u0631\u06cc\u0627\u0646 \u0644\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0628\u06ce \u0648 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u0648\u0627\u0648 \u0628\u062f\u06d5\u0646 \u0628\u06d5 \u062e\u0648\u0627\u060c \u0626\u06d5\u0648\u0627 \u0645\u0646\u06cc\u0634 \u06a9\u0627\u0631\u06d5 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u067e\u0627\u06a9 \u0648 \u062e\u0627\u0648\u06ce\u0646 \u062f\u06d5\u06a9\u06d5\u0645\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u062a\u0627\u0646\u0628\u06d5\u0645\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634\u062a\u0627\u0646 \u0644\u06d5\u0645\u06d5\u0648\u0644\u0627 \u0695\u06ce \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u0628\u06af\u0631\u06ce\u062a\u06d5 \u0628\u06d5\u0631 [\u0628\u0627 \u0628\u0632\u0627\u0646\u06ce] \u06a9\u06d5 \u0644\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0644\u0627\u06cc \u062f\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u0628\u06d5\u0631 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u0628\u06d5\u0631\u067e\u06ce\u062f\u0627\u0646\u06cc\u0627\u0646\u060c \u0648\u06d5\u0628\u06d5\u0631 \u0646\u0641\u0631\u06cc\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062e\u0633\u062a\u0646 \u0648 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0645\u0627\u0646 \u062f\u06b5\u0695\u06d5\u0634 \u06a9\u0631\u062f\u0646\u060c \u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc \u0646\u0627\u0648\u06d5\u0631\u06c6\u06a9\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u062c\u06ce\u06cc \u062e\u06c6 \u0646\u06d5\u0647\u06ce\u0634\u062a\u061b \u062e\u06c6\u06cc\u0627\u0646 \u0634\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u062c\u06ce \u062f\u0627\u0646\u0627 \u0648 \u0632\u06c6\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0641\u06ce\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646 \u06a9\u0631\u0627\u0628\u0648\u0648\u060c \u0644\u06d5 \u0628\u06cc\u0631\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0631\u062f\u06d5\u0648\u06d5. \u0647\u06d5\u062a\u0627 \u0626\u06ce\u0633\u062a\u0627\u0634 - \u0648\u06d5\u06a9 \u062f\u06d5\u0628\u06cc\u0646\u06cc - \u062e\u06d5\u06cc\u0627\u0646\u06d5\u062a\u06a9\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0647\u06d5\u06cc\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0645\u06ce\u06a9 \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627. \u0633\u0627 \u062a\u06c6 \u0686\u0627\u0648\u06cc\u0627\u0646 \u0644\u06ce \u0628\u067e\u06c6\u0634\u06d5 \u0648 \u0628\u06cc\u0627\u0646\u0628\u06d5\u062e\u0634\u06d5. \u062e\u0648\u0627 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u06cc \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u0646.',
    '\u067e\u06d5\u06cc\u0645\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0633\u0627\u0646\u062f \u06a9\u06d5 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0645\u06d5\u0633\u06cc\u062d\u06cc\u0646. \u0644\u06d5\u0648 \u0628\u0695\u06cc\u0627\u0631\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u0627\u0646\u0645\u0627\u0646 \u062f\u0627\u0628\u0648\u0648\u060c \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0686\u06d5\u0646\u062f \u0628\u06d5\u0634\u06ce\u06a9\u06cc \u0628\u0648\u0648\u0627\u0631\u062f. \u0626\u06cc\u062a\u0631 \u0626\u06ce\u0645\u06d5\u0634 \u06a9\u0627\u0631\u06ce\u06a9\u0645\u0627\u0646 \u06a9\u0631\u062f\u060c \u0634\u06d5\u0695 \u0648 \u0626\u0627\u0698\u0627\u0648\u06d5 \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646 \u0628\u06d5\u0631\u062f\u06d5\u0648\u0627\u0645 \u0628\u06ce \u0647\u06d5\u062a\u0627 \u0642\u06cc\u0627\u0645\u06d5\u062a\u061b \u0644\u06d5 \u062c\u06d5\u0632\u0627\u06cc \u0626\u06d5\u0648 \u06a9\u0631\u062f\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u06a9\u0631\u062f\u06cc\u0627\u0646. \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5\u0634 \u062e\u0648\u062f\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0644\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u0648\u0627 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f.',
    '\u0626\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628! \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0626\u06d5\u0648\u06d5\u06cc \u0647\u0627\u062a\u06c6\u062a\u06d5 \u0644\u0627\u062a\u0627\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u06d5\u061b \u0632\u06c6\u0631 \u0644\u06d5\u0648 \u0645\u06d5\u0628\u06d5\u0633\u062a \u0648 \u0628\u0627\u0633 \u0648 \u062e\u0648\u0627\u0633\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u062e\u06c6 \u062d\u0627\u0634\u0627\u0631\u0648\u0648 \u062f\u0627\u060c \u0626\u06d5\u0648 \u0628\u06c6\u062a\u0627\u0646\u06cc \u062f\u06d5\u0631\u062f\u06d5\u062e\u0627\u062a \u0648 \u0644\u06ce\u062a\u0627\u0646\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u06d5\u06a9\u0627 \u0648 \u0644\u06d5 \u0632\u06c6\u0631\u0627\u0646\u06cc\u0634\u06cc \u0648\u0627\u0632 \u062f\u06ce\u0646\u06ce. \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u0627\u0648\u06d5 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u0648 \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u0631\u062a\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5.',
    '\u0628\u06d5\u0648 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u060c \u062e\u0648\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0634\u0648\u06ce\u0646\u06cc \u0695\u06d5\u0632\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u060c \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u06ce \u0647\u06ce\u0645\u0646\u06cc \u0648 \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u06c6\u06cc \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u06cc \u062f\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06d5\u062e\u0627 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0627 \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06af\u0648\u062a\u0648\u0648\u06cc\u0627\u0646\u06d5: \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u0645\u06d5\u0633\u06cc\u062d\u06cc \u06a9\u0648\u0695\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u062f\u06cc\u0646 \u0648\u06d5\u0631\u06af\u06d5\u0695\u0627\u0648\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0631 \u062e\u0648\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627 \u0645\u06d5\u0633\u06cc\u062d \u0648 \u062f\u0627\u06cc\u06a9\u06cc \u0645\u06d5\u0633\u06cc\u062d \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u06d5\u0631\u06ce\u060c \u06a9\u06ce \u062f\u06d5\u0633\u062a\u06cc \u0648\u06d5\u0628\u06d5\u0631 \u062f\u06d5\u0647\u06ce\u0646\u0627\u061f \u0645\u0648\u06b5\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0633\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0686\u06cc \u067e\u06ce \u062e\u06c6\u0634 \u0628\u06ce \u0648\u06d5\u062f\u06cc \u062f\u06ce\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u062c\u0648\u0648\u0644\u06d5\u06a9\u06d5\u06a9\u0627\u0646 \u0648 \u0641\u06d5\u0644\u06d5\u06a9\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0639\u06d5\u0648\u0644\u0627\u062f \u0648 \u062f\u06c6\u0633\u062a\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u062f\u06cc \u0686\u06c6\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u06af\u0648\u0646\u0627\u0647\u062a\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u062a\u0627\u0646 \u062f\u06d5\u062f\u0627\u061f \u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06d5\u0631 \u0645\u0631\u06c6\u0646\u060c \u0648\u06d5\u06a9\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u06cc \u06a9\u06d5\u06cc\u0641\u06cc \u0644\u06ce \u0628\u06ce\u060c \u0644\u06ce\u06cc \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u06a9\u06ce\u0634 \u0628\u062e\u0648\u0627\u0632\u06ce \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06c6 \u0642\u06d5\u0631\u0627\u0631 \u062f\u06d5\u062f\u0627 \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u0627\u06cc\u06d5 \u0645\u0648\u06b5\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u0634 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628! \u067e\u0627\u0634 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u0646\u060c \u0626\u06d5\u0648\u0627 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0626\u06ce\u0645\u06d5\u0648 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u0644\u0627\u060c \u0646\u0627\u0647\u06d5\u0642\u062a\u0627\u0646 \u0628\u06c6 \u0695\u0648\u0648\u0646 \u06a9\u0627\u062a\u06d5\u0648\u06d5. \u0646\u06d5\u06a9\u0627 \u0628\u06ce\u0698\u0646: \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0645\u0648\u0698\u062f\u06d5\u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u0645\u0627\u0646 \u0628\u06c6 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u0646\u061b \u062f\u06d5\u0633\u0627 \u0626\u06d5\u0648\u0627 \u0645\u0648\u0698\u062f\u06d5\u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u062a\u0627\u0646 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u0644\u0627 \u0648 \u062e\u0648\u0627 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u062f\u06d5\u0645\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0628\u06d5 \u0647\u06c6\u0632\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u06af\u06d5\u0644\u06c6! \u062f\u06d5\u0628\u06ce \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u0646\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646\u060c \u06a9\u06d5 \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u062a\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0648\u0648\u0646\u060c \u0686\u06d5\u0646\u062f\u06cc\u0646 \u067e\u0627\u062f\u0634\u0627\u06cc \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u062f\u0627\u0648\u06d5\u060c \u0628\u06d5\u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0646\u06d5\u062f\u0627\u0648\u06d5.',
    '\u06af\u06d5\u0644\u06c6! \u0628\u0686\u0646\u06d5 \u0626\u06d5\u0648 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 \u067e\u06cc\u0631\u06c6\u0632\u06d5\u06cc \u062e\u0648\u0627 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc \u0628\u0695\u06cc\u0627\u0631 \u062f\u0627\u0648\u06d5 \u0648 \u0628\u06c6\u0648 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u067e\u0627\u0634 \u0645\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5. \u06af\u06d5\u0631 \u0644\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u062e\u06c6 \u0628\u0628\u0648\u06ce\u0631\u0646\u060c \u0628\u06d5 \u0632\u06cc\u0627\u0646\u062a\u0627\u0646 \u062a\u06d5\u0648\u0627\u0648 \u062f\u06d5\u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0645\u0648\u0648\u0633\u0627! \u06af\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u0632\u06c6\u0631\u062f\u0627\u0631 \u0648 \u0645\u0644\u0647\u0648\u0695 \u0644\u06d5\u0648\u06ce\u062f\u0627\u0646. \u062a\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06ce \u062f\u06d5\u0631\u0646\u06d5\u0686\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0646\u0627\u0686\u06cc\u0646\u06d5 \u0646\u0627\u0648\u06cc\u06d5\u0648\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0648 \u0634\u0648\u06ce\u0646\u0627\u0646\u06d5 \u0628\u0686\u0646\u06d5 \u062f\u06d5\u0631\u06ce\u060c \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u062f\u06d5\u0686\u06cc\u0646\u06d5 \u0646\u0627\u0648\u06cc.',
    '\u062f\u0648\u0648 \u067e\u06cc\u0627\u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u0644\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u062a\u0631\u0633\u0627\u0646 \u0648 \u0628\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0648\u062a\u0628\u0648\u0648\u0646\u060c \u067e\u06ce\u06cc\u0627\u0646 \u06af\u0648\u062a\u0646: \u0644\u06d5 \u062f\u06d5\u0631\u0648\u0627\u0632\u06d5\u06cc \u0634\u0627\u0631 \u0628\u0686\u0646\u06d5 \u0698\u0648\u0648\u0631\u06d5\u0648\u06d5 \u0648 \u0647\u06ce\u0631\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0631\u0646\u061b \u0647\u06d5\u0631\u06af\u0627 \u067e\u0627\u062a\u0627\u0646 \u062e\u0633\u062a\u06d5 \u0626\u06d5\u0648 \u062f\u06cc\u0648\u060c \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646 \u0628\u06d5\u0634\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u067e\u06ce\u0631\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0645\u0648\u0648\u0633\u0627! \u0647\u06d5\u062a\u0627 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0648 \u0634\u0648\u06ce\u0646\u06d5 \u0628\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0632\u0627\u062a \u0646\u0627\u06a9\u06d5\u06cc\u0646 \u0628\u0686\u06cc\u0646\u06d5 \u0626\u06d5\u0648\u06ce. \u0626\u06d5\u0648\u0627 \u062e\u06c6\u062a \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u0686\u0646 \u062f\u06d5\u06af\u0698\u06cc\u0627\u0646 \u0695\u0627\u0628\u0686\u0646 \u0648 \u0686\u0627\u0631\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5 \u0644\u06ce\u0631\u06d5 \u0646\u06cc\u0634\u062a\u0648\u0648\u06cc\u0646 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc\u0646.',
    '\u0645\u0648\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u062a\u06c6 \u062f\u06d5\u0632\u0627\u0646\u06cc \u06a9\u06d5 \u0645\u0646 \u062f\u06d5\u0633\u062a\u0645 \u0647\u06d5\u0631 \u0628\u06d5 \u0633\u06d5\u0631 \u062e\u06c6\u0645 \u0648 \u0628\u0631\u0627\u06cc\u06d5\u06a9\u06d5\u0645\u062f\u0627 \u0695\u0627\u062f\u06d5\u06af\u0627 [\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0642\u0633\u06d5\u06cc \u0645\u0646 \u0646\u0627\u06a9\u06d5\u0646] \u0633\u0627 \u062a\u06c6 \u0645\u0646 \u0648 \u0626\u06d5\u0648 \u0647\u06c6\u0632\u06d5 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06d5 [\u06cc \u0645\u0646] \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u062c\u06cc\u0627\u06a9\u06d5\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u0627 \u0645\u0627\u0648\u06d5\u06cc \u0686\u0644 \u0633\u0627\u06b5 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06d5\u0645 \u0648\u06b5\u0627\u062a\u06d5 \u0628\u0695\u0627 \u0648 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0648\u06ce\u0644\u0627\u0646 \u0648\u0633\u06d5\u0631\u06af\u06d5\u0631\u062f\u0627\u0646 \u062f\u06d5\u0628\u0646. \u0628\u06c6 \u0626\u06d5\u0645 \u0647\u06c6\u0632\u06d5 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646 \u0648 \u0644\u06d5 \u0695\u06ce \u062f\u06d5\u0631\u0686\u0648\u0648\u06af\u06d5 \u062e\u06d5\u0645 \u0645\u06d5\u062e\u06c6.',
    '\u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u062a\u06c6 \u062f\u06d5\u0646\u06af \u0648 \u0628\u0627\u0633\u06cc \u0695\u0627\u0633\u062a\u06cc \u0695\u0648\u0648\u062f\u0627\u0648\u06cc \u0646\u06ce\u0648\u0627\u0646\u06cc \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9 \u06a9\u0648\u0695\u0627\u0646\u06cc \u0626\u0627\u062f\u06d5\u0645\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u06d5\u061b \u0647\u06d5\u0631\u06a9\u0627\u0645 \u0642\u0648\u0631\u0628\u0627\u0646\u06cc\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0644\u06d5 \u06cc\u06d5\u06a9\u06ce\u06a9\u06cc\u0627\u0646 \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0631\u0627 \u0648 \u0644\u06d5\u0648\u06cc \u062a\u0631\u06cc\u0627\u0646 \u0648\u06d5\u0631\u0646\u06d5\u06af\u06cc\u0631\u0627 [\u0626\u06d5\u0648\u06cc \u06a9\u06d5 \u0644\u06ce\u06cc \u0648\u06d5\u0631\u0646\u06d5\u06af\u06cc\u0631\u0627\u060c \u0647\u06d5\u0695\u06d5\u0634\u06d5\u06cc \u0644\u06d5\u0648\u06d5\u06a9\u06d5\u06cc \u062a\u0631 \u06a9\u0631\u062f] \u06af\u0648\u062a\u06cc: \u0645\u0646 \u062f\u06d5\u0628\u06ce \u0628\u062a \u06a9\u0648\u0698\u0645. \u06af\u0648\u062a\u06cc: \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0642\u0628\u0648\u0648\u06b5 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u062a\u0631\u0633\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6\u0634 \u062f\u06d5\u0633\u062a\u0645 \u0628\u06c6 \u062f\u0695\u06ce\u0698 \u0628\u06a9\u06d5\u06cc \u06a9\u06d5 \u0628\u0645\u06a9\u0648\u0698\u06cc\u060c \u0645\u0646 \u062f\u06d5\u0633\u062a\u06d5\u0648\u06d5\u06a9\u06d5\u0631\u06cc \u0646\u0627\u06a9\u06d5\u0645 \u062a\u06c6 \u0628\u06a9\u0648\u0698\u0645. \u0645\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 - \u06a9\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5 - \u062a\u0631\u0633\u0645 \u0647\u06d5\u06cc\u06d5.',
    '\u0645\u0646 \u062f\u06d5\u0645\u06d5\u0648\u06ce \u062a\u0627\u0648\u0627\u0646\u06cc \u06a9\u0648\u0634\u062a\u0646\u06cc \u0645\u0646 \u0648 \u062a\u0627\u0648\u0627\u0646\u06cc \u062e\u06c6\u062a \u0628\u06ce\u062a\u06d5 \u0645\u0644\u062a\u061b \u0626\u06d5\u0648\u0633\u0627 \u062a\u06c6 \u062f\u06d5\u0686\u06cc\u06cc\u06d5 \u062f\u06c6\u0632\u06d5\u0642 \u0648 \u062a\u0627\u0648\u0627\u0646\u06cc \u0646\u0627\u0647\u06d5\u0642 \u0626\u06d5\u0645\u06d5\u06cc\u06d5.',
    '\u0626\u06cc\u062a\u0631 \u062f\u06b5\u06cc \u0647\u06d5\u06b5\u06cc\u0646\u0627 \u06a9\u06d5 \u0628\u0631\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0628\u06a9\u0648\u0698\u06ce. \u06a9\u0648\u0634\u062a\u06cc \u0648 \u06a9\u06d5\u0648\u062a\u06d5 \u0646\u0627\u0648 \u0695\u06ce\u0632\u06d5\u06cc \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u06d5\u06a9\u0627\u0646.',
    '\u062e\u0648\u0627 \u0642\u06d5\u0644\u06d5\u0695\u06d5\u0634\u06d5\u06cc\u06d5\u06a9\u06cc \u0646\u0627\u0631\u062f\u060c \u062e\u06d5\u0631\u06cc\u06a9 \u0628\u0648\u0648 \u0632\u06d5\u0648\u06cc \u062f\u06d5\u06a9\u06c6\u06b5\u06cc \u0628\u06c6 \u0626\u06d5\u0645\u06d5\u06cc \u067e\u06ce\u06cc \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u0627 \u06a9\u06d5 \u0686\u06c6\u0646 \u062c\u06d5\u0646\u062f\u06d5\u06a9\u06cc \u0628\u0631\u0627\u06a9\u06d5\u06cc \u0644\u06d5 \u062e\u0627\u06a9\u0627 \u0628\u0634\u0627\u0631\u06ce\u062a\u06d5\u0648\u06d5. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0644\u06d5 \u062e\u06c6\u0645 \u0646\u06d5\u06af\u0628\u06d5\u062a\u06ce! \u0686\u06c6\u0646 \u0646\u06d5\u0645\u0632\u0627\u0646\u06cc \u0648\u06d5\u06a9 \u0642\u0627\u06b5\u0627\u0648 \u0628\u062c\u0648\u0648\u06b5\u06ce\u0645\u06d5\u0648\u06d5 \u0648 \u0644\u0627\u0634\u06d5\u06cc \u0628\u0631\u0627\u06cc \u062e\u06c6\u0645 \u0648\u06d5\u0634\u06ce\u0631\u0645. [\u0628\u06d5\u0645 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc] \u0647\u0627\u062a\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646\u0627\u0646.',
    '\u0647\u06c6\u06cc \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0633\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644 \u0646\u0627\u0631\u062f\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06ce \u062a\u0627\u0648\u0627\u0646\u06ce\u06a9\u06cc \u0628\u06a9\u0648\u0698\u06ce - \u06a9\u06d5 \u0646\u06d5 \u06af\u0648\u0646\u0627\u0647\u06cc \u06a9\u0631\u062f\u0628\u06ce \u0648 \u0646\u06d5 \u062e\u0631\u0627\u067e\u06d5\u06cc \u0644\u06d5 \u0632\u06d5\u0648\u06cc\u0646\u062f\u0627 \u0644\u06ce \u0695\u0648\u0648 \u062f\u0627\u0628\u06ce - \u0648\u06d5\u06a9 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u06a9\u06d5 \u06af\u0634 \u062e\u06d5\u06b5\u06a9\u06cc \u0633\u06d5\u0631\u0632\u06d5\u0648\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0628\u0631\u062f\u0628\u06ce \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u06ce \u062a\u0627\u0648\u0627\u0646\u06ce\u06a9 \u0695\u0632\u06af\u0627\u0631 \u0628\u06a9\u0627\u060c \u0648\u06d5\u06a9 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u0633\u06d5\u0631\u0632\u06d5\u0648\u06cc \u0644\u06d5 \u0645\u0631\u062f\u0646 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f\u0648\u0648\u06d5. \u067e\u06d5\u06cc\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u06af\u06d5\u0644\u06ce \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0695\u0648\u0648\u0646 \u0648 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u0627\u0646 \u0628\u0631\u062f\u06d5 \u0644\u0627\u06cc\u0627\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u06cc\u0627\u0646 - \u062f\u0648\u0627\u06cc \u0626\u06d5\u0645 \u06af\u0634\u062a\u06d5 - \u0647\u06d5\u06b5\u06d5\u0695\u06c6 \u0648 \u0633\u0646\u0648\u0648\u0631\u067e\u06d5\u0695\u06ce\u0646 \u0628\u0648\u0648\u0646.',
    '\u06af\u06c6\u0695\u06cc \u06af\u06cc\u0627\u0646\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0698\u0627\u06cc\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u0639\u06d5\u0631\u0632\u0627 \u062f\u06d5\u0628\u0646\u06d5 \u062e\u0631\u0627\u067e\u06a9\u0627\u0631\u060c \u06a9\u0648\u0698\u0631\u0627\u0646\u06d5 \u06cc\u0627\u0646 \u0644\u06d5 \u062f\u0627\u0631 \u062f\u0627\u0646\u06d5 \u06cc\u0627\u0646 \u0628\u0695\u06cc\u0646\u06cc \u062f\u06d5\u0633\u062a \u0648 \u0642\u0627\u0686\u06d5 \u067e\u06ce\u0686\u06d5\u0648\u0627\u0646\u06d5\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0648\u06b5\u0627\u062a \u062f\u0648\u0648\u0631 \u062e\u0631\u0627\u0646\u06d5\u061b \u06a9\u06d5 \u0626\u06d5\u0645 \u0633\u0632\u0627\u06cc\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0695\u06cc\u0633\u0648\u0627 \u0628\u0648\u0648\u0646\u06d5 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0648 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0631\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u062f\u0627 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062f\u0627\u0646\u06cc \u0628\u06d5\u0698\u0627\u0646\u06d5.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u06af\u06cc\u0631\u062a\u0627\u0646 \u06a9\u06d5\u0648\u0646\u060c \u0644\u06d5 \u06a9\u0627\u0631 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u0628\u0646\u06d5\u0648\u06d5 \u0648 \u062a\u06c6\u0628\u06d5 \u0628\u06a9\u06d5\u0646. \u0626\u06ce\u0648\u06d5\u0634 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0644\u06d5 \u067e\u0627\u0634 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u0648\u0648\u0646\u06cc\u0627\u0646 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u0634\u06ce \u0644\u06d5 \u062e\u0648\u0627 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce \u0648 \u0695\u06ce \u0646\u0632\u06cc\u06a9\u06d5\u0648 \u0628\u0648\u0648\u0646 \u0628\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u0695\u0627\u06cc \u0626\u06d5\u0648\u0627 \u0628\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627\u061b \u0628\u06d5\u0634\u06a9\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0695\u0632\u06af\u0627\u0631\u06cc \u0628\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0645\u0627\u06b5\u06d5 \u0648 \u0647\u06ce\u0646\u062f\u06d5\u06cc \u062a\u0631\u06cc\u0634\u06cc \u0695\u06d5\u06af\u06d5\u06b5 \u062e\u06d5\u0646 \u0648 \u0628\u06d5 \u0647\u06cc\u0648\u0627 \u0628\u0646 \u0644\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0695\u0632\u06af\u0627\u0631\u06cc\u0627\u0646 \u06a9\u0627 \u0648 \u062e\u06c6\u06cc\u0627\u0646\u06cc \u067e\u06ce \u0628\u06a9\u0695\u0646\u06d5\u0648\u06d5\u060c \u062c\u06ce\u06af\u06d5 \u0646\u0627\u06af\u0631\u06ce \u0648 \u0628\u06d5 \u0647\u06cc\u0686 \u0646\u0627\u0686\u06ce \u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u06af\u06d5\u0644\u06ce\u06a9 \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u062a\u0648\u0648\u0634 \u062f\u06d5\u0628\u06ce.',
    '\u06a9\u06c6\u0634\u0634 \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631 \u062f\u06d5\u0631\u0628\u0686\u0646\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631\u0686\u06cc \u0647\u06d5\u0648\u06b5 \u0628\u062f\u06d5\u0646 \u0628\u06ce \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u062a\u0627\u0646\u0646 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u0646 \u0648 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06ce \u0628\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062f\u0632\u06cc \u0628\u06a9\u0627 - \u0646\u06ce\u0631 \u0628\u06ce \u06cc\u0627\u0646 \u0645\u06ce \u0641\u06d5\u0631\u0642\u06cc \u0646\u06cc\u06cc\u06d5 - \u0644\u06d5 \u0633\u0632\u0627\u06cc \u062f\u0632\u06cc \u06a9\u0631\u062f\u0646\u06cc \u062f\u06d5\u0628\u06ce \u0647\u06d5\u0631 \u062f\u0648\u0648 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0628\u0628\u0695\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u062a\u06d5\u0646\u0628\u06ce \u06a9\u0631\u062f\u0646\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06a9\u06d5\u0634 \u062f\u0648\u0627\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0631\u062f\u0646 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u0628\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0695\u06ce\u06af\u0627\u06cc \u0686\u0627\u06a9\u06d5 \u0628\u06af\u0631\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u060c \u062e\u0648\u062f\u0627 \u062a\u06c6\u0628\u06d5\u06cc \u0642\u0628\u0648\u0648\u06b5 \u062f\u06d5\u06a9\u0627 \u0648 \u062e\u0648\u0627 \u0644\u06ce\u0628\u0648\u0648\u0631 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u062f\u06cc \u062a\u06c6 \u062e\u06c6\u062a \u0646\u06d5\u062a\u0632\u0627\u0646\u06cc\u0648\u06d5 \u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0645\u0648\u06b5\u06a9\u06cc \u062e\u0648\u062f\u0627\u0646\u061f \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce \u0644\u06ce\u06cc \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u06a9\u0627 \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u06d5\u062f\u0627\u061f \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9 \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u062e\u06c6\u062a \u0628\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06b5\u062a\u06d5\u0646\u06af \u0645\u06d5\u06a9\u06d5 \u06a9\u06d5 \u0628\u06c6 \u0644\u06d5 \u062f\u06cc\u0646 \u0648\u06d5\u0631\u06af\u06d5\u0695\u0627\u0646\u06ce \u067e\u06d5\u0644\u06d5\u06cc\u0627\u0646\u06d5 \u0648 \u062f\u06ce\u0646\u06d5 \u0644\u0627\u062a \u0648 \u0628\u06d5 \u062f\u06d5\u0645 \u062f\u06d5\u06b5\u06ce\u0646: \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u0628\u06d5 \u062a\u06c6 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u062f\u06b5\u06cc\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc \u0646\u06d5\u0647\u06ce\u0646\u0627\u0648\u06d5. \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0644\u06d5\u0648 \u062c\u0648\u0648\u0644\u06d5\u06a9\u0627\u0646\u06d5\u0634 \u062e\u06c6\u062a \u062a\u0648\u0648\u0634\u06cc \u062e\u06d5\u0645\u0628\u0627\u0631\u06cc \u0645\u06d5\u06a9\u06d5\u060c \u06a9\u06d5 \u06af\u0648\u06ce \u0628\u06c6 \u062f\u0631\u06c6 \u0647\u06d5\u06b5\u062f\u06d5\u062e\u06d5\u0646\u060c \u0641\u06ce\u0631 \u0628\u0648\u0648\u0646 \u0642\u0633\u06d5 \u0644\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u062a\u0631 \u062f\u06d5\u0628\u06cc\u0633\u0646 \u0648 \u062f\u06d5\u06cc \u0642\u06c6\u0632\u0646\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u0646\u06d5 \u0644\u0627\u06cc \u062a\u06c6\u060c \u062c\u06ce \u06af\u06c6\u0695\u06a9\u06ce \u0628\u06d5 \u0642\u0633\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0641\u06b5\u0627\u0646 \u0642\u0633\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u06af\u0648\u062a\u0646\u060c \u0648\u06d5\u0631\u06cc \u06af\u0631\u0646\u060c \u062f\u06d5\u0646\u0627 \u062e\u06c6\u06cc \u0644\u06ce \u0628\u067e\u0627\u0631\u06ce\u0632\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u0648\u062f\u0627 \u0648\u06cc\u0633\u062a\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06c6 \u0628\u0628\u0695\u06ce\u062a\u06d5\u0648\u06d5\u060c \u062a\u06c6 \u0646\u0627\u062a\u0648\u0627\u0646\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc \u0628\u06ce \u0646\u06cc\u0627\u0632 \u0628\u06a9\u06d5\u06cc \u0648 \u0647\u06cc\u0686 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06c6 \u0695\u06ce\u06a9 \u0628\u062e\u06d5\u06cc. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0627\u06cc\u06d5\u0648\u06ce \u062f\u06b5\u06cc\u0627\u0646 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u0648 \u067e\u06cc\u0633\u06cc \u0628\u0633\u0695\u06ce\u062a\u06d5\u0648\u06d5\u061b \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0633\u0648\u0648\u06a9\u06cc\u06cc\u06d5 \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u0627\u0646 \u062f\u0698\u0648\u0627\u0631 \u062f\u06d5\u0628\u06ce.',
    '\u0628\u06c6 \u062f\u0631\u06c6\u060c \u06af\u0648\u06ce \u0647\u06d5\u06b5\u062f\u06d5\u062e\u06d5\u0646 \u0648 \u0628\u06d5 \u062d\u06d5\u0631\u0627\u0645 \u062e\u0648\u0627\u0631\u062f\u0646\u06ce \u0641\u06ce\u0631\u0646. \u062c\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u062c\u0627\u0631\u06ce \u062f\u06ce\u0646\u06d5 \u0644\u0627\u062a\u060c \u062a\u06c6\u060c \u0628\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc\u0627\u0646 \u0695\u0627\u0628\u06af\u06d5 \u06cc\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u062a\u06ce \u0645\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0648\u0627\u0632\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06ce\u0646\u06cc\u060c \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646\u06ce\u06a9 \u0644\u06d5 \u062a\u06c6 \u0646\u0627\u062f\u06d5\u0646. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0628\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0695\u0627\u0628\u06af\u06d5\u06cc\u060c \u06a9\u06ce\u0634\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06ce \u0644\u0627\u06af\u0631\u06cc \u0648 \u0628\u06d5 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u0628\u06c6 \u067e\u06ce\u06a9 \u0628\u06ce\u0646\u06d5. \u062e\u0648\u062f\u0627 \u067e\u06cc\u0627\u0648\u06cc \u0628\u06ce \u062f\u06d5\u063a\u06d5\u0632 \u0648 \u0695\u0627\u0633\u062a \u0648 \u0695\u06d5\u0648\u0627\u06cc \u062e\u06c6\u0634 \u06af\u06d5\u0631\u06d5\u06a9\u0646.',
    '\u062c\u0627 \u0686\u06c6\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06ce\u0646\u06d5 \u0644\u0627\u062a \u06a9\u06d5 \u0628\u06d5 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u0695\u0627\u0628\u06af\u06d5\u06cc\u060c \u06a9\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0645\u0627\u0646 \u062a\u06d5\u0648\u0631\u0627\u062a \u0647\u06d5\u06cc\u06d5 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0648\u06ce\u062f\u0627\u0646\u060c \u06a9\u06d5\u0686\u06cc \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u067e\u06ce \u0695\u0627\u06af\u06d5\u06cc\u0634\u062a\u0646\u06d5\u06a9\u06d5\u06cc \u062a\u06c6 \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646\u061f \u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0695\u0648\u0627\u062f\u0627\u0631 \u0646\u06cc\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u06a9\u06d5 \u0626\u06d5\u0645 \u062a\u06d5\u0648\u0631\u0627\u062a\u06d5\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u060c \u0695\u0648\u0648\u0646\u0627\u06a9\u0627\u06cc\u06cc \u0648 \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646\u062f\u0627\u0646\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5. \u0647\u06d5\u0645\u0648\u0648 \u0626\u06d5\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0628\u0648\u0648\u0646\u060c \u0647\u06d5\u0631 \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0645 \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0645\u0648\u0648\u0633\u0627\u06cc\u06cc \u0628\u0648\u0648\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0634\u062a\u0648\u0648\u0646. \u062e\u0648\u0627\u0646\u0627\u0633\u0627\u0646\u06cc \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0648 \u0632\u0627\u0646\u0627 \u0628\u0644\u06cc\u0645\u06d5\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u06af\u06d5\u0644\u06ce\u06a9 \u0634\u062a\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u062e\u0648\u0627 \u0644\u06d5 \u0628\u06d5\u0631 \u0628\u0648\u0648\u061b \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc\u0634 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u067e\u06ce\u06cc \u06af\u0648\u062a\u0648\u0648\u0646: \u0646\u0627\u0628\u06ce \u0644\u06d5 \u062e\u06d5\u06b5\u06a9 \u0628\u062a\u0631\u0633\u0646\u061b \u0626\u06d5\u0634\u06ce \u0647\u06d5\u0631 \u062a\u0631\u0633\u0648\u0648 \u0644\u06d5 \u062e\u06c6\u0645 \u0628\u06ce \u0648 \u0646\u06d5\u0686\u0646 \u0628\u06d5\u06b5\u06af\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0645\u0646 \u0628\u06d5 \u0646\u0631\u062e\u06ce\u06a9\u06cc \u06a9\u06d5\u0645 \u0628\u0641\u0631\u06c6\u0634\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u067e\u06ce\u0686\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u062f\u0627\u060c \u062f\u0698\u06cc \u062e\u0648\u062f\u0627 \u0695\u0627\u0648\u06ce\u0633\u062a\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u062f\u06cc\u0646\u06cc \u0695\u0627\u0633\u062a \u0648\u06d5\u0631\u06af\u06d5\u0695\u0627\u0648\u06d5.',
    '\u0644\u06d5 \u062a\u06d5\u0648\u0631\u0627\u062a\u062f\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0695\u06cc\u0627\u0631\u0645\u0627\u0646\u062f\u0627\u060c \u06a9\u06d5 \u06af\u06cc\u0627\u0646\u06ce\u06a9 \u0644\u06d5 \u0628\u0627\u062a\u06cc \u06af\u06cc\u0627\u0646\u06ce\u06a9\u060c \u0686\u0627\u0648 \u0628\u06d5 \u0686\u0627\u0648 \u0648 \u06a9\u06d5\u067e\u06c6 \u0628\u06d5\u06a9\u06d5\u067e\u06c6\u060c \u06af\u0648\u06ce \u0628\u06d5\u06af\u0648\u06ce \u0648 \u062f\u062f\u0627\u0646 \u0644\u06d5 \u0628\u0627\u062a\u06cc \u062f\u062f\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u062e\u0645\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u062a\u06c6\u06b5\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0647\u06d5\u0633\u061b \u06a9\u06ce \u0644\u06d5 \u062a\u06c6\u06b5\u06d5 - \u0628\u06d5 \u062e\u06ce\u0631\u06cc \u062e\u06c6\u06cc - \u0648\u0627\u0632\u0628\u0647\u06ce\u0646\u06ce\u060c \u06af\u0648\u0646\u0627\u0647\u06cc \u067e\u06ce \u0647\u06d5\u06b5\u062f\u06d5\u0648\u06d5\u0631\u06ce. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u06ce\u0686\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5 - \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0647\u0627\u062a\u0648\u0648\u06d5 - \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u062f\u06d5\u0646\u060c \u063a\u06d5\u062f\u0631\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0646\u0627\u0647\u06d5\u0642\u0646.',
    '\u0644\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648\u0627\u0646 \u0639\u06cc\u0633\u0627\u06cc \u06a9\u0648\u0695\u06cc \u0645\u0631\u06cc\u06d5\u0645\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u061b \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u0648\u0648 \u0628\u06d5\u0648 \u062a\u06d5\u0648\u0631\u0627\u062a\u06d5\u06cc \u0648\u0627 \u0628\u06d5\u0631\u0644\u06d5 \u0626\u06d5\u0648 \u0647\u0627\u062a\u0628\u0648\u0648. \u0626\u06cc\u0646\u062c\u06cc\u0644\u06cc\u0634\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0639\u06cc\u0633\u0627\u061b \u06a9\u06d5 \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646\u062f\u0627\u0646 \u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5 \u0648 \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u062a\u06d5\u0648\u0631\u0627\u062a\u06cc\u0634\u06d5 \u06a9\u06d5 \u0628\u06d5\u0631 \u0644\u06d5\u0645 \u0647\u0627\u062a\u0628\u0648\u0648\u06d5 \u062e\u0648\u0627\u0631\u06ce \u0648 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627\u062a\u0631\u0633\u0646\u060c \u067e\u0695\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u062f\u06d5\u0628\u06ce \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u0627 \u0626\u06cc\u0646\u062c\u06cc\u0644\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u060c \u0628\u06d5 \u067e\u06ce\u06cc \u0646\u0627\u0648\u06d5\u0631\u06c6\u06a9\u06cc \u0626\u06cc\u0646\u062c\u06cc\u0644 - \u0648\u06d5\u06a9 \u062e\u0648\u062f\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631 - \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u062f\u06d5\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631 \u0644\u0627 \u0628\u062f\u06d5\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u06cc\u0627\u0646\u062f\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0628\u06d5 \u062e\u06d5\u06b5\u0627\u062a \u0628\u06c6 \u062a\u06c6 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u062a\u06ce\u06cc\u062f\u0627\u06cc\u06d5 \u0695\u0627\u0633\u062a\u06d5\u061b \u0628\u06d5 \u06a9\u062a\u06ce\u0628\u06d5 \u0628\u06d5\u0631\u06cc\u0646\u06d5\u06a9\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u0686\u0627\u0648\u062f\u06ce\u0631\u06cc\u0627\u0646\u06d5. \u0626\u06cc\u062a\u0631 \u062a\u06c6 \u0628\u06d5 \u067e\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u062e\u0648\u0627 \u0647\u06d5\u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u0628\u06d5 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06cc\u0627\u0646 \u0695\u0627\u0628\u06af\u06d5 \u0648 \u0628\u06d5 \u0644\u0627\u062f\u0627\u0646 \u0644\u06d5\u0648 \u0647\u06d5\u0642\u06d5\u06cc \u06a9\u06d5 \u0648\u0627 \u0628\u06c6\u062a \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0645\u06d5\u06a9\u06d5\u0648\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0648\u0627\u062a \u0648 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646. \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0695\u06ce \u0648 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u0645\u0627\u0646 \u062f\u0627\u0646\u0627\u0648\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627\u060c \u0647\u06d5\u0645\u0648\u0648\u06cc \u062f\u06d5\u06a9\u0631\u062f\u0646\u06d5 \u06cc\u06d5\u06a9 \u0626\u06c6\u0645\u06d5\u062a. \u0628\u06d5\u06b5\u0627\u0645 \u0648\u06cc\u0633\u062a\u06cc \u062e\u0648\u062f\u0627 \u0648\u0627\u0628\u0648\u0648 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u06cc \u062f\u0627\u0648\u0646 \u0628\u06d5 \u062a\u0627\u0642\u06cc\u062a\u0627\u0646 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5\u0634 \u0644\u06d5 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u06a9\u06ce \u0628\u06d5\u0631 \u06a9\u06ce \u0628\u06a9\u06d5\u0646. \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u062a\u0627\u0646 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627\u060c \u06a9\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0686\u06cc \u06a9\u06ce\u0634\u06d5\u0648 \u0644\u06d5 \u0646\u0627\u0648 \u067e\u06d5\u06cc\u062f\u0627 \u0628\u0628\u0648\u0648.',
    '\u062a\u06c6\u0634 \u0628\u06d5 \u067e\u06ce\u06cc \u0626\u06d5\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0628\u06c6\u062a \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u0628\u06d5 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06cc\u0627\u0646 \u0695\u0627\u0628\u06af\u06d5 \u0648 \u062f\u0648\u0627\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0648\u06d5. \u062e\u06c6\u062a\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0628\u067e\u0627\u0631\u06ce\u0632\u06d5\u060c \u0646\u06d5\u0648\u06d5\u06a9\u0627 \u0641\u0631\u06cc\u0648\u062a \u062f\u06d5\u0646 \u0648 \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0628\u06c6 \u062a\u06c6 \u0647\u0627\u062a\u060c \u0647\u06d5\u06b5\u06d5\u062a \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u060c \u0633\u0627 \u0628\u0632\u0627\u0646\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0646\u06cc\u0627\u0632\u06cc \u0648\u0627\u06cc\u06d5 \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u0627\u0632\u06ce\u06a9 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062a\u06c6\u06b5\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0632\u06c6\u0631\u06cc\u0634 \u0644\u06d5\u0648 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0644\u06d5 \u0695\u06ce \u062f\u06d5\u0631\u0686\u0648\u0648\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u062f\u0627\u0628\u06cc \u0632\u06d5\u0645\u0627\u0646\u06cc \u0646\u06d5\u0632\u0627\u0646\u06cc \u0628\u06d5\u0631 \u0644\u06d5 \u0626\u06cc\u0633\u0644\u0627\u0645 \u0628\u0698\u06cc\u06d5\u0646\u0646\u06d5\u0648\u06d5! \u062f\u06d5\u06cc\u0633\u0627 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0648\u0627 \u0644\u06d5 \u0628\u0695\u0648\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646\u0627 \u0647\u06cc\u0686 \u0634\u06a9 \u0648 \u06af\u0648\u0645\u0627\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u060c \u06a9\u0627\u0645\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0686\u0627\u062a\u0631\u06d5\u061f',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0645\u06d5\u0628\u0646\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u0626\u06d5\u0645 \u0645\u0648\u0648\u0633\u0627\u06cc\u06cc \u0648 \u0645\u06d5\u0633\u06cc\u062d\u06cc\u06cc\u0627\u0646\u06d5\u061b \u0628\u0627 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06c6\u0633\u062a\u06cc \u06cc\u06d5\u06a9\u062a\u0631 \u0628\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0628\u06ce\u062a\u06d5 \u062f\u06c6\u0633\u062a\u06cc\u0627\u0646\u060c \u0626\u06d5\u0648\u06cc\u0634 \u0644\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0698\u0645\u06ce\u0631\u062f\u0631\u06ce. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0646\u060c \u062e\u0648\u062f\u0627 \u0695\u06ce\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0646\u0627\u062f\u0627.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0646\u06d5\u062e\u06c6\u0634\u06cc \u0644\u06d5 \u062f\u06b5\u06cc\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c \u062f\u06d5\u0628\u06cc\u0646\u06cc \u06a9\u06d5 \u0628\u06d5\u0644\u06d5\u0632\u0646 \u0628\u06c6 \u0686\u0648\u0648\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646\u061b \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u062a\u0631\u0633\u06cc\u0646 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627 \u0628\u06cc\u0646 [\u0628\u06c6\u06cc\u06d5 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646]. \u0647\u06cc\u0686 \u062f\u0648\u0648\u0631 \u0646\u06cc\u06cc\u06d5 \u062e\u0648\u062f\u0627 \u0695\u06c6\u0698\u06ce \u0633\u06d5\u0631\u062a\u0627\u0646 \u0628\u062e\u0627\u060c \u06cc\u0627 \u062e\u06c6 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u062a\u0631 \u0628\u06a9\u0627. \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0648 \u0695\u0627\u0632\u06d5\u06cc \u062f\u06d5\u062f\u06b5\u06cc\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c \u062a\u0648\u0648\u0634\u06cc \u062e\u0648\u0633\u0627\u0631 \u0648 \u0632\u06cc\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u06a9\u06d5\u0633\u0627\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0634 \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06d5\u0628\u0648\u0648\u0646 \u0628\u06d5 \u0633\u0648\u06ce\u0646\u062f\u0627\u0646 \u0695\u06d5\u0634 \u062f\u06d5\u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u0647\u06d5\u062a\u0627\u0633\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5\u062a\u0627\u0646\u0646\u061f \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u06a9\u0631\u062f\u06cc\u0627\u0646 \u0628\u06d5 \u0641\u06cc\u0695\u06c6 \u0686\u0648\u0648\u061b \u062f\u0648\u0627\u06cc\u06cc\u06d5\u06a9\u06d5\u0634\u06cc \u062a\u0648\u0648\u0634\u06cc \u062e\u0648\u0633\u0627\u0631 \u0648 \u0632\u06cc\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0644\u06d5 \u062f\u06cc\u0646\u06cc \u062e\u06c6\u06cc \u067e\u0627\u0634\u06af\u06d5\u0632 \u0628\u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u062e\u0648\u0627 \u0644\u06d5\u0645\u06d5\u0648\u0644\u0627 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u0648\u0627 \u062f\u06d5\u062e\u0627\u062a\u06d5\u0695\u0648\u0648\u060c \u062e\u06c6\u0634\u06cc \u0628\u0648\u06ce\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u062e\u0648\u0627\u06cc\u0627\u0646 \u062e\u06c6\u0634 \u0628\u0648\u06ce \u0648 \u0644\u06d5 \u062d\u0627\u0646\u062f\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0646\u06d5\u0631\u0645 \u0648 \u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0632\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u062f\u06cc\u0646 \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u060c \u0633\u06d5\u0631\u0628\u06b5\u0646\u062f \u0648 \u062a\u0648\u0646\u062f \u0648 \u062a\u06cc\u0698\u0646\u061b \u062e\u06d5\u0632\u0627 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u062a\u06d5\u0634\u06d5\u0631\u06cc \u0633\u06d5\u0631\u06a9\u06c6\u0646\u06d5\u06a9\u06d5\u0631\u0627\u0646 \u0646\u0627\u062a\u0631\u0633\u0646. \u0626\u06d5\u0645\u06d5 \u0632\u06ce\u062f\u06d5 \u062e\u06d5\u06b5\u0627\u062a\u06ce\u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0628\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u067e\u06ce\u06cc \u062f\u06d5\u0628\u06d5\u062e\u0634\u06ce. \u062e\u0648\u0627 \u0632\u0627\u0646\u0627\u06cc\u06d5 \u0648 [\u0644\u06d5 \u0628\u06d5\u062e\u0634\u06cc\u0646] \u0632\u06c6\u0631 \u062f\u06d5\u0633 \u06af\u0648\u0634\u0627\u062f\u06d5.',
    '\u062f\u06c6\u0633\u062a\u06cc \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u06d5\u062a\u06cc \u0648 \u0626\u06d5\u0648 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u06a9\u06d5 \u0646\u0648\u06ce\u0698\u06a9\u06d5\u0631 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u0631\u0646 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06a9\u0695\u0646\u06c6\u0634\u062f\u0627\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc \u0648\u06d5\u0644 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u0627 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06ce \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5\u062a\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u060c \u0633\u06d5\u0631\u0628\u06d5 \u062e\u0648\u0627\u0646 \u0648 \u0647\u06d5\u0631 \u06af\u0631\u06c6\u06cc\u06d5 \u0633\u06d5\u0631\u0628\u06d5 \u062e\u0648\u0627 \u0628\u0646\u060c \u0633\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06af\u0627\u06b5\u062a\u06d5 \u0628\u06d5 \u062f\u06cc\u0646\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u062d\u0633\u06ce\u0628\u0646 - \u06a9\u06d5 \u067e\u06ce\u0634\u0648\u0648 \u0628\u06c6\u06cc\u0627\u0646 \u0647\u0627\u062a\u0648\u0648\u06d5 - \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc \u0645\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u0626\u06d5\u0634\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u06c6 \u0646\u0648\u06ce\u0698 \u06a9\u0631\u062f\u0646\u06ce \u0628\u0627\u0646\u06af \u0626\u06d5\u062f\u06d5\u0646\u060c \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06af\u0627\u06b5\u062a\u06d5\u06cc \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u0628\u06ce \u0626\u0627\u0648\u06d5\u0632\u0646 \u0648 \u062f\u06d5\u0647\u06cc\u0686 \u0646\u0627\u06af\u06d5\u0646.',
    '\u062a\u06c6 \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646 \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u0647\u06d5\u0631 \u0686\u0648\u0646\u06a9\u06d5 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u0628\u06d5\u0648 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0645\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5 \u0648 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u067e\u06ce\u0634 \u0642\u0648\u0631\u0626\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u0646\u060c \u0626\u06ce\u0648\u06d5 \u0695\u06a9\u062a\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u06cc\u06d5\u061f \u0626\u06ce\u0648\u06d5\u0634 \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631\u062a\u0627\u0646 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06d5\u06ce \u0644\u06d5\u0648\u0627\u0646\u06d5\u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u06a9\u06d5\u0645 \u06a9\u06d5 \u0633\u0632\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u0627\u0648\u06d5 \u0644\u06d5 \u0633\u0632\u0627\u06cc \u0648\u0627\u0646 \u062e\u0631\u0627\u067e\u062a\u0631\u06d5\u061f \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u0646 \u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u062e\u0648\u0627 \u06a9\u06d5\u0648\u062a\u0646 \u0648 \u062e\u0648\u062f\u0627 \u063a\u06d5\u0632\u06d5\u0628\u06cc \u0644\u06ce \u06af\u0631\u062a\u0646 \u0648\u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646\u06cc \u06a9\u0631\u062f\u06d5 \u0645\u06d5\u06cc\u0645\u0648\u0648\u0646\u061b \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646\u06cc \u06a9\u0631\u062f\u06d5 \u0628\u06d5\u0631\u0627\u0632\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0639\u06d5\u0628\u062f\u0627\u06cc\u06d5\u062a\u06cc \u0628\u062a\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f. \u0634\u0648\u06ce\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u06af\u0634 \u062c\u06ce\u06cc\u0627\u0646 \u062e\u0631\u0627\u067e\u062a\u0631\u06d5 \u0648 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u06ce\u06a9 \u06af\u0648\u0645\u0695\u0627\u062a\u0631 \u0628\u0648\u0648\u0646.',
    '\u06a9\u0627\u062a\u06ce \u062f\u06ce\u0646 \u0644\u0627\u062a\u0627\u0646 \u062f\u06d5\u06b5\u06ce\u0646: \u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06b5 \u0628\u06d5 \u062d\u0627\u0634\u0627 \u0647\u0627\u062a\u0646\u06d5 \u0698\u0648\u0648\u0631 \u0648 \u0628\u06d5 \u0628\u06ce \u0628\u0695\u0648\u0627 \u0648\u06d5\u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646. \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u0634\u0627\u0631\u062f\u06d5\u0648\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0631\u06d5.',
    '\u0632\u06c6\u0631\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u06a9\u06d5 \u0628\u06c6 \u062a\u0627\u0648\u0627\u0646 \u0648 \u062f\u06d5\u0633\u062a\u062f\u0631\u06ce\u0698\u06cc \u0648 \u062d\u06d5\u0631\u0627\u0645 \u062e\u06c6\u0631\u06cc \u067e\u06ce\u0634\u06d5\u0628\u0695\u06a9\u06d5\u06cc \u06cc\u06d5\u06a9\u062a\u0631 \u062f\u06d5\u062f\u06d5\u0646\u061b \u0626\u0627\u06a9\u0627\u0631\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u0646\u0627\u067e\u06d5\u0633\u06d5\u0646\u062f\u06d5.',
    '\u0626\u06d5\u062f\u06cc \u0628\u06c6\u0686\u06cc \u062e\u0648\u0627\u0646\u0627\u0633\u0627\u0646 \u0648 \u0632\u0627\u0646\u0627\u06a9\u0627\u0646\u06cc\u0627\u0646\u060c \u0644\u06d5 \u062a\u0627\u0648\u0627\u0646 \u0648 \u062d\u06d5\u0631\u0627\u0645\u062e\u06c6\u0631\u06cc \u0647\u06cc\u0686 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u0646\u0627\u0647\u06ce\u0646\u0646\u061f \u062f\u06d5\u0633\u06a9\u0631\u062f\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u0646\u0627\u067e\u06d5\u0633\u06d5\u0646\u062f\u06d5!',
    '\u062c\u0647\u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u0648\u062f\u0627 \u062f\u06d5\u0633\u062a\u06cc \u0628\u06d5\u0633\u062a\u0631\u0627\u0648\u06d5. \u062f\u06d5\u06a9 \u062f\u06d5\u0633\u06cc\u0627\u0646 \u0644\u06d5 \u0632\u06d5\u0646\u062c\u06cc\u0631 \u062e\u0631\u06ce \u0648 \u0628\u06d5 \u0646\u06d5\u062d\u0644\u06d5\u062a \u0628\u0646\u060c \u0628\u06c6 \u0626\u06d5\u0648\u06d5\u06cc \u0644\u06d5 \u062f\u06d5\u0645\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0686\u0648\u0648. [\u0628\u0627 \u0628\u0632\u0627\u0646\u0646] \u062f\u06d5\u0633\u062a\u06cc \u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u06af\u0648\u0634\u0627\u062f\u0646\u061b \u0686\u06c6\u0646\u06cc \u062e\u06c6\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u0628\u06a9\u0627 \u0628\u06d5\u062e\u062a \u062f\u06d5\u06a9\u0627. \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5 \u0626\u06d5\u0648\u06d5\u06cc \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u06d5\u0648\u06d5 \u0628\u06c6\u062a \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0644\u0627\u062f\u0627\u0646 \u0648 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u0632\u06c6\u0631\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u067e\u062a\u0631 \u062f\u06d5\u06a9\u0627. \u0626\u06ce\u0645\u06d5\u0634 \u0626\u06d5\u0648\u0627 \u0647\u06d5\u062a\u0627 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u0633\u062a\u0627\u0646\u06d5\u0648\u06d5\u060c \u0695\u06a9\u06d5\u0628\u06d5\u0631\u06cc \u0648 \u062f\u0648\u0698\u0645\u0646\u06cc\u0645\u0627\u0646 \u062e\u0633\u062a\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646. \u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0626\u0627\u06af\u0631\u06cc \u0634\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06af\u0631\u0633\u0627\u0646\u062f\u060c \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u06a9\u0648\u0698\u0627\u0646\u062f\u06cc\u06d5\u0648\u06d5. \u062a\u0627 \u0644\u06d5 \u0647\u06d5\u0631\u062f\u0646\u060c \u0647\u06d5\u0631 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u067e\u06cc\u0634\u06d5\u06cc\u0627\u0646\u06d5 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u062e\u0631\u0627\u067e\u06d5 \u0628\u06d5\u0695\u06ce\u0648\u06d5 \u0628\u06d5\u0631\u0646 \u0648 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062e\u0631\u0627\u067e\u06d5\u06a9\u0627\u0631\u0627\u0646 \u062e\u06c6\u0634\u06cc \u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0628\u0627 \u0648 \u062f\u06b5\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u0627\u0628\u0627\u060c \u0647\u06d5\u0631 \u06af\u0648\u0646\u0627\u0647\u06ce \u06a9\u0631\u062f\u0628\u0648\u0648\u06cc\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u0644\u06ce\u0645\u0627\u0646 \u062f\u06d5\u0633\u0695\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0646 \u0628\u0631\u062f\u0646\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u067e\u0695 \u0644\u06d5 \u062e\u06c6\u0634\u06cc \u0648 \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06cc.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u062a\u06d5\u0648\u0631\u0627\u062a \u0648 \u0626\u06cc\u0646\u062c\u06cc\u0644 \u0648 \u0626\u06d5\u0648\u06d5\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6\u06cc\u0627\u0646 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u062a\u06d5\u0648\u0627\u0648 \u062f\u06d5\u06a9\u0627\u0631\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0628\u0627\u060c \u0628\u06ce \u0633\u06c6 \u0644\u06d5 \u0644\u0627\u06cc \u0633\u06d5\u0631\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 [\u0632\u06d5\u0645\u06cc\u0646\u06cc] \u0698\u06ce\u0631 \u067e\u0627\u06cc\u0627\u0646\u0695\u0627 \u062e\u0648\u0627\u0631\u062f\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0628\u06cc\u0646 \u062f\u06d5\u06a9\u0631\u0627. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u0627\u0646\u06ce\u06a9\u0646\u060c \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u0645\u0627\u0645\u0646\u0627\u0648\u0646\u062c\u06cc\u0646 \u0648 \u0632\u06c6\u0631\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u062f\u06a9\u0631\u062f\u0627\u0631\u0646.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0644\u06d5 \u0644\u0627\u06cc\u0627\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0695\u0627 \u0628\u06c6\u062a \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0695\u0627\u06cc \u06af\u06d5\u06cc\u06ce\u0646\u06d5\u061b \u06af\u06d5\u0631 \u0648\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u0695\u0627\u0633\u067e\u06ce\u0631\u06cc\u062a \u067e\u06ce\u06a9 \u0646\u06d5\u0647\u06ce\u0646\u0627\u0648\u06d5. \u062e\u0648\u0627 \u0644\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5\u062a\u060c \u06a9\u06c6\u0645\u06d5\u06b5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0646\u0627\u06a9\u0627.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628! \u0626\u06d5\u0646\u06af\u06c6 \u0647\u06d5\u062a\u0627 \u0628\u06d5 \u067e\u06ce\u06cc \u062a\u06d5\u0648\u0631\u0627\u062a \u0648 \u0626\u06cc\u0646\u062c\u06cc\u0644 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6\u062a\u0627\u0646 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u0646\u06d5\u0628\u0632\u0648\u0648\u0646\u06d5\u0648\u06d5\u060c \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06cc\u0686 [\u062f\u06cc\u0646\u06ce\u06a9] \u0646\u06cc\u0646. \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5 \u0626\u06d5\u0648\u06d5\u06cc \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u06d5\u0648\u06d5 \u0628\u06c6\u062a \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0644\u0627\u062f\u0627\u0646 \u0648 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u0632\u06c6\u0631\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646\u060c \u067e\u062a\u0631 \u062f\u06d5\u06a9\u0627. \u062a\u06c6 \u0644\u06d5 \u0628\u06d5\u0631 \u0626\u06d5\u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5\u060c \u062e\u06d5\u0645 \u0645\u06d5\u062e\u06c6.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u0648\u0648\u0646\u06d5 \u062c\u0648\u0648\u0644\u06d5\u06a9\u06d5\u060c \u0633\u0648\u0628\u0628\u06cc\u06cc\u0627\u0646 \u0648 \u0641\u06d5\u0644\u06d5\u06a9\u0627\u0646\u06cc\u0634\u060c \u0647\u06d5\u0631\u06a9\u0627\u0645\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0647\u06d5\u0628\u06ce \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u0628\u06a9\u0627\u060c \u0646\u06d5\u0647\u06cc\u0686 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u062e\u06d5\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644 \u0628\u06d5\u06b5\u06ce\u0646\u0645\u0627\u0646 \u0633\u0627\u0646\u062f \u0648 \u0632\u06c6\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0644\u06d5\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06d5 \u067e\u06ce\u0686\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06b5\u062e\u0648\u0627\u0632\u06cc \u0648\u0627\u0646 \u0634\u062a\u06ce\u06a9\u06cc \u0628\u06c6 \u0628\u0647\u06ce\u0646\u0627\u0628\u0627\u0646\u060c \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646 \u062d\u0633\u06ce\u0628 \u062f\u06d5\u06a9\u0631\u062f\u061b \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u06a9\u0648\u0634\u062a\u0646 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u062f\u06d5\u0628\u0631\u062f.',
    '\u0644\u0627\u06cc\u0627\u0646 \u0648\u0627\u0628\u0648\u0648 \u0626\u06cc\u062a\u0631 \u0628\u06d5\u06b5\u0627\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634 \u0646\u0627\u0628\u06ce\u061b \u06a9\u0648\u06ce\u0631 \u0648 \u06a9\u06d5\u0695 \u0628\u0648\u0648\u0646 \u0646\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u062f\u06cc \u0648 \u0646\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u0628\u06cc\u0633\u062a. \u067e\u0627\u0634\u0627\u0646 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u061b \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646\u06cc\u06d5\u0634\u06cc \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0631\u062f\u0646. \u062f\u06cc\u0633\u0627\u0646\u06d5\u0648\u06d5 \u06af\u06d5\u0644\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646 \u06a9\u0648\u06ce\u0631 \u0648 \u06a9\u06d5\u0695 \u0628\u0648\u0648\u0646 \u062e\u0648\u062f\u0627\u0634 \u0626\u0627\u06a9\u0627\u0631\u06cc \u062f\u06d5\u062f\u06cc\u062a\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06b5\u06ce\u0646: \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u0645\u06d5\u0633\u06cc\u062d \u06a9\u0648\u0695\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u062f\u06cc\u0646 \u0648\u06d5\u0631\u06af\u06d5\u0695\u0627\u0648\u0646. \u06a9\u06d5\u0686\u06cc \u0645\u06d5\u0633\u06cc\u062d \u06af\u0648\u062a\u0648\u0648\u06cc\u06d5\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644! \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0628\u067e\u06d5\u0631\u0633\u062a\u0646 \u06a9\u06d5 \u0645\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u062f\u06d5 \u06a9\u0631\u062f \u0648 \u0626\u06ce\u0648\u06d5\u0634 \u0626\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0627\u0646\u062f\u0648\u0648\u06cc\u06d5\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u0628\u06a9\u0627\u060c \u062e\u0648\u0627 \u0628\u06d5\u0634\u06cc \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u062f\u06d5\u0628\u0695\u06ce \u0648 \u062c\u06ce\u06cc \u0626\u0627\u06af\u0631\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0646\u0627\u0647\u06d5\u0642\u06cc \u0628\u06a9\u0627\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u062f\u06d5\u0647\u0627\u0646\u0627 \u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u0648\u062f\u0627 \u0633\u06ce\u0647\u06d5\u0645\u06cc\u0646\u06d5 \u0644\u06d5 \u0633\u06ce \u06a9\u06d5\u0633\u0627\u0646\u060c \u0628\u06ce \u0633\u06c6 \u0644\u06d5 \u062f\u06cc\u0646 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627\u0648\u06d5. \u062c\u06af\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc \u062a\u0627\u0642\u0627\u0646\u06d5\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u062a\u0648\u0648 \u062f\u06d5\u0633 \u0647\u06d5\u06b5\u0646\u06d5\u06af\u0631\u0646 \u0644\u06d5\u0648 \u0642\u0633\u0627\u0646\u06d5 - \u0626\u06d5\u0648\u0627\u0646\u06cc\u0627\u0646\u06d5 \u06a9\u06d5 \u0648\u0627 \u0644\u06d5 \u062f\u06cc\u0646 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627\u0648\u06d5 - \u06af\u0631\u0641\u062a\u0627\u0631\u06cc \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646 \u062f\u06d5\u0628\u0646.',
    '\u0628\u06c6\u0686 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646\u06cc \u0646\u0627\u0628\u06d5\u0646\u06d5 \u0628\u06d5\u0631 \u0642\u0627\u067e\u06cc \u062e\u0648\u062f\u0627 \u0648 \u062f\u0627\u0648\u0627\u06cc \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc \u0644\u06ce \u0646\u0627\u06a9\u06d5\u0646\u061f \u06a9\u06d5 \u062e\u0648\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0645\u06d5\u0633\u06cc\u062d\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u0628\u06d5\u0648 \u0644\u0627\u0648\u06d5 \u0686\u06cc \u062a\u0631 \u0646\u06cc\u06cc\u06d5\u060c \u06a9\u06d5 \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06cc\u0634 \u0641\u0631\u06d5\u06cc\u06d5\u06a9 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062a\u0631 \u0647\u0627\u062a\u0648\u0648\u0646 \u0648 \u0686\u0648\u0648\u0646. \u062f\u0627\u06cc\u06a9\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u0698\u0646\u06ce\u06a9\u06cc \u0695\u0627\u0633\u062a \u0648 \u062f\u0631\u0648\u0633\u062a \u0628\u0648\u0648\u061b \u0647\u06d5\u0631\u062f\u0648\u0648\u06a9\u06cc\u0627\u0646 \u062e\u06c6\u0631\u0627\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u062e\u0648\u0627\u0631\u062f. \u062f\u06d5\u0628\u0695\u0648\u0627\u0646\u06d5 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646. \u062f\u06cc\u0633\u0627\u0646 \u062a\u06c6 \u0644\u06d5\u0648\u06d5 \u0695\u0627\u0645\u06ce\u0646\u06d5\u060c \u0686\u06c6\u0646\u06cc\u0646 \u0644\u06d5 \u0647\u06d5\u0642\u06ce \u0644\u0627\u062f\u06d5\u062f\u0631\u06ce\u0646\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0686\u0646 \u0634\u062a\u0627\u0646\u06cc \u0648\u0627 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646 \u06a9\u06d5 \u0646\u06d5 \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0632\u06cc\u0627\u0646\u0648\u0648 \u067e\u06ce \u0628\u06af\u06d5\u06cc\u06d5\u0646\u06ce \u0648 \u0646\u06d5 \u067e\u06ce\u06cc \u062f\u06d5\u06a9\u0631\u06ce \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062a\u06ce\u062f\u0627\u0628\u06ce\u061f \u0632\u06c6\u0631 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0641\u0631\u06d5\u0632\u0627\u0646\u0627\u0634 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5.',
    '\u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646\u060c \u0628\u06ce\u0698\u06d5: \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u0647\u06d5\u06b5\u06d5\u0695\u06c6\u06cc\u06cc \u0644\u06d5 \u062f\u06cc\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646\u062f\u0627 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0645\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5\u0631 \u0644\u06d5 \u0626\u06ce\u0633\u062a\u06d5 \u06af\u0648\u0645\u0695\u0627 \u0628\u0648\u0648\u0646 \u0648 \u0632\u06c6\u0631\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u06af\u0648\u0645\u0695\u0627 \u06a9\u0631\u062f \u0648 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06cc \u0695\u0627\u0633 \u06a9\u0644\u0627 \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648 \u062f\u06d5\u0633\u062a\u06d5 \u0644\u06d5 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644\u06ce \u06a9\u06d5 \u0644\u06d5 \u062f\u06cc\u0646 \u0644\u0627\u06cc\u0627\u0646 \u062f\u0627\u060c \u0644\u06d5 \u0633\u06d5\u0631 \u0632\u0645\u0627\u0646\u06cc \u062f\u0627\u0648\u062f \u0648 \u0639\u06cc\u0633\u0627\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645\u0695\u0627 \u0646\u06d5\u062d\u0644\u06d5\u062a \u06a9\u0631\u0627\u0646\u060c \u0686\u0648\u0646\u06a9\u0648\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u062c\u06ce \u0646\u06d5\u0647\u06ce\u0646\u0627 \u0648 \u067e\u06cc\u0634\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06cc \u0628\u0648\u0648.',
    '\u0644\u06d5\u0648 \u0695\u06d5\u0641\u062a\u0627\u0631\u06d5 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5 \u0648\u0627 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f\u060c \u0628\u06d5\u0631\u06af\u0631\u06cc\u06cc\u0627\u0646 \u0644\u06d5 \u06cc\u06d5\u06a9 \u0646\u06d5\u06a9\u0631\u062f. \u0626\u0627\u06cc \u06a9\u06d5 \u0686\u06d5\u0646\u062f \u062e\u0631\u0627\u067e\u06d5\u06cc\u0627\u0646 \u06a9\u0631\u062f!',
    '\u062a\u06c6 \u0632\u06c6\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u062f\u06d5\u0628\u0646\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646. \u0686\u06d5\u0646 \u062e\u0631\u0627\u067e\u06d5 \u0626\u06d5\u0648 \u062a\u06c6\u0634\u06d5\u06cc \u067e\u06ce\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062e\u0633\u062a: \u062a\u06c6\u0634\u06d5\u06cc \u062a\u0648\u0648\u0695\u06d5 \u0628\u0648\u0648\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0647\u06d5\u0631 \u0645\u0627\u0646\u06d5\u0648\u06d5 \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u062f\u0627.',
    '\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0628\u06c6 \u0626\u06d5\u0648 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u060c \u0628\u06d5 \u062f\u06b5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06d5\u0628\u0627\u06cc\u06d5\u060c \u062f\u06c6\u0633\u062a\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06d5\u062f\u06d5\u0628\u0648\u0648\u0646. \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631 \u06a9\u06d5\u0633 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0646\u0627\u06a9\u06d5\u0646.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u062c\u0648\u0648\u06d5\u06a9\u0627\u0646 \u0648 \u0634\u06d5\u0631\u06cc\u06a9 \u062f\u0627\u0646\u06d5\u0631\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627\u060c \u0644\u06d5 \u062f\u0648\u0634\u0645\u0646\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u0627 \u0633\u06d5\u0631\u0633\u06d5\u062e\u062a \u062a\u0631\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u06af\u0648\u062a\u06cc\u0627\u0646: \u00ab\u0626\u06ce\u0645\u06d5 \u0645\u06d5\u0633\u06ce\u062d\u06cc\u0646\u00bb\u060c \u067e\u062a\u0631 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062e\u06c6\u0634 \u06af\u06d5\u0631\u06d5\u06a9\u06d5. \u0686\u0648\u0646 \u0628\u0695\u06ce\u06a9\u06cc\u0627\u0646 \u0695\u06d5\u0628\u06d5\u0646 \u0648 \u06a9\u06d5\u0634\u06d5\u0646 \u0648 \u062e\u06c6\u0628\u06d5\u0632\u0644 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u06af\u0648\u06ce \u062f\u06d5\u06af\u0631\u0646 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u0628\u06cc\u0646\u06cc \u0626\u06d5\u0633\u0631\u06cc\u0646 \u0644\u06d5 \u0686\u0627\u0648\u06cc\u0627\u0646 \u062f\u06d5\u0648\u06d5\u0631\u06ce \u0648 \u0686\u0648\u0646\u06a9\u06d5 \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc\u06cc\u06d5\u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u0646\u060c \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0626\u06d5\u0648\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u060c \u0633\u0627 \u062a\u06c6\u0634 \u0646\u0627\u0648\u0645\u0627\u0646 \u0648\u06d5\u0644 \u0634\u0627\u06cc\u06d5\u062a\u0627\u0646\u062f\u0627 \u0628\u0646\u0648\u0648\u0633\u06d5.',
    '\u0626\u06ce\u0698\u0646: \u062c\u0627 \u0686\u06c6\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0628\u06d5 \u062e\u0648\u0627 \u0646\u0627\u0647\u06ce\u0646\u06cc\u0646 \u0648 \u0628\u0627\u0648\u06d5\u0695 \u0628\u06d5\u0645 \u0695\u0627\u0633\u062a\u06cc\u06d5 \u0646\u06d5\u06a9\u06d5\u06cc\u0646 \u06a9\u06d5 \u0626\u06ce\u0633\u062a\u0627 \u0628\u06d5 \u0626\u06ce\u0645\u06d5 \u06af\u06d5\u06cc\u0634\u062a\u061f \u06a9\u06d5\u0686\u06cc \u0647\u06cc\u0648\u0627\u0645\u0627\u0646 \u0626\u06d5\u0645\u06d5\u06cc\u06d5\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u0645\u0627\u0646 \u0628\u0645\u0627\u0646\u062e\u0627\u062a\u06d5 \u0646\u0627\u0648 \u06af\u0631\u06c6\u06cc \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646\u06d5\u0648\u06d5!',
    '\u0644\u06d5 \u0647\u06d5\u0645\u0628\u06d5\u0631 \u0626\u06d5\u0648 \u0642\u0633\u0627\u0646\u06d5\u062f\u0627 \u0626\u06d5\u0648\u0627\u0646 \u0648\u062a\u06cc\u0627\u0646: \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0627\u0646\u0628\u0627\u062a\u06d5 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646. \u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u0627\u06a9\u0627\u0631\u0628\u0627\u0634\u06d5\u06a9\u0627\u0646 \u0647\u06d5\u0631\u0648\u0627 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u0698 \u0628\u06d5 \u062f\u06cc\u0646 \u0648\u06d5\u0633\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0627\u0646\u06cc\u060c \u062f\u06cc\u0627\u0631\u06d5 \u062c\u06d5\u062d\u06d5\u0646\u062f\u06d5\u0645 \u062c\u06ce\u06cc\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627 \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0695\u06d5\u0648\u0627 \u062f\u06cc\u0648\u06d5 \u0648 \u067e\u0627\u06a9 \u0648 \u0628\u0627\u0634\u0646\u060c \u062e\u06c6\u062a\u0627\u0646\u06cc \u0644\u06ce \u0628\u06ce \u0628\u06d5\u0634 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u0634 \u067e\u062a\u0631 \u0645\u06d5\u0686\u0646\u060c \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce \u06a9\u06d5 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u062f\u06d5\u0631\u062f\u06d5\u0686\u0646.',
    '\u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u062e\u0648\u0627 \u0628\u06c6\u062a\u0627\u0646\u06cc \u062f\u0627\u0646\u0627\u0648\u06d5\u060c \u0644\u06d5\u0648\u06d5\u06cc \u0648\u0627 \u067e\u0627\u06a9 \u0648 \u0695\u06d5\u0648\u0627\u06cc\u06d5\u060c \u0628\u062e\u06c6\u0646 \u0648 \u0646\u06c6\u0634\u06cc \u06af\u06cc\u0627\u0646\u0648\u0648 \u0628\u06ce\u061b \u062a\u0631\u0633\u06cc\u0634\u0648\u0648 \u0644\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0628\u06ce \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u067e\u06ce \u0647\u06d5\u06cc\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0628\u06d5 \u0633\u0648\u06ce\u0646\u062f\u06cc \u0633\u06d5\u0631 \u0632\u0627\u0631\u06d5\u06a9\u06cc \u0648 \u062f\u0648\u0648\u0631 \u0644\u06d5 \u0645\u06d5\u0628\u06d5\u0633\u062a \u0628\u06d5 \u06af\u0648\u0646\u0627\u0647\u0628\u0627\u0631\u062a\u0627\u0646 \u062f\u0627\u0646\u0627\u0646\u06ce\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0633\u0648\u06ce\u0646\u062f\u06cc \u0644\u06d5 \u0646\u0627\u062e\u06cc \u062f\u06b5\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06ce \u0648 \u0646\u06d5\u06cc\u0628\u06d5\u0646\u06d5 \u0633\u06d5\u0631\u060c \u0628\u06d5 \u06af\u0648\u0646\u0627\u0647\u0628\u0627\u0631\u062a\u0627\u0646 \u062f\u0627\u062f\u06d5\u0646\u06ce \u0648 \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u0626\u06d5\u0648\u062f\u0627 \u062e\u0648\u0627\u0631\u062f\u0646 \u0628\u062f\u06d5\u0646 \u0628\u06d5 \u062f\u06d5 \u0647\u06d5\u0698\u0627\u0631\u061b \u0644\u06d5\u0648 \u062e\u0648\u0627\u0631\u062f\u0646\u06d5\u06cc \u0628\u06d5 \u0626\u0627\u0633\u0627\u06cc\u06cc \u062f\u06d5\u06cc\u062f\u06d5\u0646 \u0628\u06d5 \u062e\u06ce\u0632\u0627\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646. \u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce \u062c\u0644\u06a9\u06cc \u062f\u06d5 \u0647\u06d5\u0698\u0627\u0631 \u0628\u0627\u0631\u0628\u0648\u0648 \u0628\u062f\u06d5\u0646. \u06cc\u0627\u0646 \u06a9\u06c6\u0644\u06d5\u06cc\u06d5\u06a9 \u0695\u0632\u06af\u0627\u0631 \u0628\u06a9\u06d5\u0646\u061b \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc \u0646\u06d5\u0628\u0648\u0648\u060c \u0628\u0627 \u0633\u06ce \u0695\u06c6\u0698\u0627\u0646 \u0628\u06d5 \u0695\u06c6\u0698\u0648\u0648 \u0628\u06ce. \u0626\u06d5\u0645\u06d5 \u0642\u06d5\u0631\u06d5\u0628\u0648\u0648\u06cc \u0633\u0648\u06ce\u0646\u062a\u0627\u0646\u06d5 \u06a9\u06d5 \u0628\u06d5 \u0626\u0627\u0646\u0642\u06d5\u0633\u062a \u062e\u0648\u0627\u0631\u062f\u0628\u06ce\u062a\u0648\u0648. \u0633\u0648\u06ce\u0646\u062f\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646 \u0648 \u0628\u06d5 \u062c\u06ce\u06cc \u0628\u06ce\u0646\u0646. \u0626\u0627\u0648\u0627 \u062e\u0648\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06c6\u062a\u0627\u0646 \u0695\u0648\u0648\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u06cc \u0628\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0634\u06d5\u0631\u0627\u0628 \u062e\u06c6\u0631\u06cc \u0648 \u0642\u0648\u0645\u0627\u0631\u0628\u0627\u0632\u06cc \u0648 \u06a9\u06ce\u0644 \u067e\u06d5\u0631\u0633\u062a\u06cc \u0648 \u06af\u0631\u06ce \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u062a\u06cc\u0631\u0627\u0646 \u06a9\u0631\u062f\u0646\u060c \u0644\u06d5 \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u0646\u061b \u062e\u06c6\u062a\u0627\u0646\u06cc \u0644\u06ce \u0628\u067e\u0627\u0631\u06ce\u0632\u0646\u060c \u0628\u06d5\u0634\u06a9\u06d5\u0645 \u062a\u0648\u0648\u0634\u06cc \u0695\u0632\u06af\u0627\u0631\u06cc \u0628\u0646.',
    '\u0634\u06d5\u06cc\u062a\u0627\u0646 \u0645\u06d5\u0628\u06d5\u0633\u062a\u06cc \u0626\u06d5\u0645\u06d5\u06cc\u06d5\u060c \u0628\u06d5 \u0634\u06d5\u0631\u0627\u0628 \u062e\u0648\u0627\u0631\u062f\u0646 \u0648 \u0642\u0648\u0645\u0627\u0631 \u0626\u0627\u0698\u0627\u0648\u06d5\u062a\u0627\u0646 \u0628\u062e\u0627\u062a\u06d5 \u0646\u0627\u0648\u060c \u0644\u06d5 \u0646\u0645\u06ce\u0698 \u0648 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u0648\u06d5\u062a\u0627\u0646 \u06af\u06ce\u0695\u06ce. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0633 \u0644\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5 \u0628\u06d5\u0631\u062f\u06d5\u062f\u06d5\u0646\u061f',
    '\u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u0646 \u0648 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u0646 [\u0644\u06d5 \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc\u0634] \u062e\u06c6 \u067e\u0627\u0631\u06ce\u0632\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646 \u062f\u06d5\u0631\u0628\u0686\u0646\u060c \u062f\u06d5\u0628\u06ce \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062c\u06af\u06d5 \u0644\u06d5 \u067e\u06ce \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646 \u0647\u06cc\u0686\u06cc \u062a\u0631\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0648\u0648\u0646\u06d5\u062a\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u062e\u0648\u0627\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u06af\u0648\u0646\u0627\u0647\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5\u062a\u06d5 \u0633\u06d5\u0631\u06ce. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0633\u062a\u0627 \u062e\u06c6 \u067e\u0627\u0631\u06ce\u0632 \u0628\u0646 \u0648 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u0628\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0634 \u062e\u06c6 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646 \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u0645\u06ce\u0646\u0646 \u0648 \u0686\u0627\u06a9\u06d5\u06cc \u0628\u06a9\u06d5\u0646\u060c \u062e\u0648\u0627 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u06cc \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0648 \u0646\u0686\u06cc\u0631\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5\u062f\u06d5\u0633\u062a\u0627\u0646 \u06cc\u0627\u0646 \u0628\u06d5\u0646\u06ce\u0632\u06d5 \u0695\u0627\u0648\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06d5\u062a\u0627\u0642\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u0647\u06d5\u062a\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u0628\u0632\u0627\u0646\u06ce \u06a9\u06ce \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0644\u06ce\u06cc \u062f\u06d5\u062a\u0631\u0633\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5\u0648 \u0633\u0646\u0648\u0648\u0631\u06d5\u06cc \u06a9\u06d5 \u0628\u06c6\u062a\u0627\u0646 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u06d5 \u067e\u062a\u0631 \u0628\u0686\u06ce\u060c \u0628\u06d5\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u062a\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0626\u06cc\u062d\u0631\u0627\u0645\u062f\u0627\u0646\u060c \u0646\u0627\u0628\u06ce \u0647\u06cc\u0686 \u0646\u0686\u06cc\u0631\u06ce \u0628\u0648\u06af\u0698\u0646. \u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0626\u0627\u0646\u0642\u06d5\u0633\u062a \u0646\u0686\u06cc\u0631\u06ce \u0628\u06a9\u0648\u0698\u06ce \u0644\u06d5 \u0626\u06cc\u062d\u0631\u0627\u0645\u062f\u0627\u060c \u0644\u06d5\u0633\u06d5\u0631\u06cc\u06d5\u062a\u06cc \u0626\u0627\u0698\u06d5\u06b5\u06ce\u06a9\u06cc - \u06a9\u06d5 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u0626\u06d5\u0648 \u0646\u0686\u06cc\u0631\u06d5\u06cc \u0626\u06d5\u0648 \u0628\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u062f\u0648\u0648 \u0645\u0631\u06c6\u06cc \u0639\u0627\u062f\u06b5 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc\u06cc \u0628\u0633\u06d5\u0644\u0645\u06ce\u0646\u06ce - \u0628\u0647\u06ce\u0646\u06ce\u062a\u06d5 \u0645\u0627\u06b5\u06cc \u06a9\u0627\u0628\u06d5 \u0648 \u0644\u06d5\u0648\u06ce \u0628\u06cc\u06a9\u0627\u062a\u06d5 \u0642\u0648\u0631\u0628\u0627\u0646\u06cc\u061b \u06cc\u0627\u0646 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u0626\u06d5\u0648 \u0646\u0686\u06cc\u0631\u06d5\u060c \u062e\u0648\u0627\u0631\u062f\u0646 \u0628\u062f\u0627 \u0628\u06d5 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646\u061b \u06cc\u0627\u0646 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u0642\u06d5\u0631\u0633\u0627\u0646\u06cc \u0626\u06d5\u0648 \u0646\u0686\u06cc\u0631\u06d5 \u0686\u06d5\u0646\u062f \u0695\u06c6\u0698 \u062f\u0627\u0646\u06ce\u0646 \u0628\u06d5 \u0695\u06c6\u0698\u0648\u0648 \u0628\u06ce\u061b \u0647\u06d5\u062a\u0627 \u0633\u0632\u0627\u06cc \u0626\u06d5\u0645 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u06cc \u0628\u0686\u06ce\u0698\u06ce. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06a9\u0627\u0631\u06cc \u0695\u0627\u0628\u0631\u062f\u0648\u0648 \u0686\u0627\u0648 \u062f\u06d5\u067e\u06c6\u0634\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u062a\u06ce\u0647\u06d5\u06b5\u0686\u06ce\u062a\u06d5\u0648\u06d5\u060c \u062e\u0648\u0627 \u062a\u06c6\u06b5\u06d5\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u062a\u06c6\u06b5\u06d5\u0633\u06ce\u0646\u06d5.',
    '\u0695\u0627\u0648 \u0648 \u062e\u0648\u0627\u0631\u062f\u06d5\u0645\u06d5\u0646\u06cc \u0632\u0631\u06ce \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0695\u06d5\u0648\u0627 \u06a9\u0631\u0627\u0648\u06d5. \u0626\u06ce\u0648\u06d5 \u0648 \u0695\u06ce\u0628\u0648\u0627\u0631\u0627\u0646 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0628\u06cc\u0627\u0646\u06a9\u06d5\u0646\u06d5 \u0628\u0698\u06cc\u0648\u06cc \u062e\u06c6\u062a\u0627\u0646. \u0695\u0627\u0648\u06cc \u0628\u06d5\u0698\u06cc\u0634 \u0647\u06d5\u062a\u0627 \u0644\u06d5 \u0626\u06cc\u062d\u0631\u0627\u0645\u062f\u0627 \u062f\u06d5\u0628\u0646\u060c \u0695\u06ce\u0648 \u0646\u06cc\u06cc\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0628\u06c6 \u0628\u06d5\u0631\u0646. \u0644\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce \u06a9\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u06a9\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0645\u0627\u06b5\u06cc \u06a9\u0627\u0628\u06d5 [\u06cc\u0627\u0646\u06ce] \u00ab\u0628\u06cc\u062a \u0627\u0644\u062d\u0631\u0627\u0645\u00bb \u06cc \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u0647\u06c6\u06cc \u067e\u06ce\u06a9\u0647\u06ce\u0646\u0627\u0646\u06cc \u06a9\u0627\u0631\u06cc \u062e\u06d5\u0644\u06a9\u061b \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u062d\u06d5\u0631\u0627\u0645\u06d5\u0645\u0627\u0646\u06af \u0648 \u0642\u0648\u0631\u0628\u0627\u0646\u06cc\u06cc\u06d5 \u0628\u06ce \u0646\u06cc\u0634\u0627\u0646 \u0648 \u0642\u06d5\u06b5\u0627\u062f\u06d5 \u062f\u06d5\u0645\u0644\u06d5\u06a9\u0627\u0646\u06cc\u0634. \u0626\u06d5\u0645\u06d5 [\u0647\u06d5\u0645\u0648\u0648\u06cc] \u0628\u06c6 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u062a\u0627 \u0628\u0632\u0627\u0646\u0646 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u062f\u0627\u0646\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0648 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u062f\u06d5\u0628\u06ce \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0634 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0633\u0632\u0627\u062f\u0627\u0646\u062f\u0627 \u062a\u0648\u0646\u062f \u0648 \u062a\u06cc\u0698\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u0634\u06d5 \u06a9\u06d5 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0631\u06a9\u06ce \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u0647\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06d5\u06cc\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0695\u0627\u0628\u06af\u06d5\u0631\u06ce\u0646\u06ce. \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0634 \u062f\u06d5\u06cc\u0634\u06ce\u0631\u0646\u06d5\u0648\u06d5\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u067e\u06cc\u0633 \u0648 \u067e\u0627\u06a9 \u0648\u06d5\u06a9 \u06cc\u06d5\u06a9 \u0646\u06cc\u0646\u061b \u0628\u0627 \u0632\u06c6\u0631\u0627\u06cc\u06cc \u067e\u06cc\u0633\u06d5\u06a9\u06d5\u0634 \u0628\u06c6\u062a \u0633\u06d5\u0631 \u0633\u0648\u0648\u0695\u06ce\u0646 \u0628\u06ce. \u06a9\u06d5 \u0648\u0627\u0628\u0648\u0648 \u0626\u06d5\u06cc \u062f\u06b5 \u0648\u0634\u06cc\u0627\u0631\u0627\u0646! \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0647\u06cc\u0648\u0627\u06cc \u0695\u0632\u06af\u0627\u0631\u06cc\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0634 \u067e\u0631\u0633\u06cc\u0627\u0631 \u0645\u06d5\u06a9\u06d5\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06c6\u062a\u0627\u0646 \u0695\u0648\u0648\u0646 \u06a9\u06d5\u0646\u06d5\u0648\u06d5\u060c \u067e\u06ce\u06cc \u062a\u0648\u0648\u0634\u06cc \u062f\u06b5\u0645\u06d5\u0646\u062f\u06cc \u062f\u06d5\u0628\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u067e\u0631\u0633\u06d5\u06a9\u06d5\u0648 \u0647\u06d5\u06b5\u06af\u0631\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u06a9\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646 \u062f\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u0648\u06d5\u0631\u0627\u0645\u06d5\u06a9\u06d5\u0648 \u062f\u06d5\u062f\u0631\u06ce\u062a\u06d5\u0648\u06d5. \u062e\u0648\u0627 \u0644\u06d5\u0648 \u067e\u0631\u0633\u06cc\u0627\u0631\u06d5 \u0646\u0627\u0628\u06d5\u062c\u06ce\u06af\u0627\u0646\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0686\u0627\u0648\u06cc \u067e\u06c6\u0634\u06cc\u061b \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u0644\u06d5\u0633\u06d5\u0631\u062e\u06c6\u06cc\u06d5.',
    '\u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9 \u0644\u06d5 \u067e\u06ce\u0634 \u0626\u06ce\u0648\u06d5\u0634\u062f\u0627 \u0626\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u067e\u0631\u0633\u0627\u0646\u06d5\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0648 \u0647\u06d5\u0631 \u0644\u06d5 \u0628\u06d5\u0631 \u0626\u06d5\u0648 \u067e\u0631\u0633\u0627\u0646\u06d5\u060c \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627.',
    '\u062e\u0648\u062f\u0627 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u00ab\u0628\u06d5\u062d\u06cc\u0640\u0631\u06d5\u00bb \u0648 \u00ab\u0633\u0627\u0626-\u06cc\u0628\u0640\u06d5\u00bb \u0648 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u00ab\u062d\u0627\u0645\u00bb \u0648 \u00ab\u0648\u06d5\u06b5\u06cc\u0640\u0644\u06d5\u00bb \u0647\u06cc\u0686 \u0628\u0695\u06cc\u0627\u0631\u06ce\u06a9\u06cc \u0646\u06d5\u062f\u0627\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 - \u06a9\u06d5 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0628\u06ce \u0626\u0627\u0648\u06d5\u0632\u0646 - \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u067e\u06ce\u06cc\u0627\u0646 \u0628\u06af\u0648\u062a\u0631\u06ce \u06a9\u06d5: \u0628\u06ce\u0646 \u0628\u06d5\u0631\u06d5\u0648\u0644\u0627\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0648 \u0628\u06ce\u0646 \u0628\u06d5\u0631\u06d5\u0648\u0644\u0627\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u060c \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0626\u0627\u06cc\u06cc\u0646\u06d5\u0645\u0627\u0646 \u0628\u06d5\u0633\u06d5 \u06a9\u06d5 \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06cc \u0628\u0648\u0648\u0646\u061b \u06a9\u06d5 \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0632\u0627\u0646\u06cc \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc\u0627\u0646 \u0628\u06d5\u062f\u06cc \u0646\u06d5\u06a9\u0631\u062f!',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06c6\u0634\u062a\u0627\u0646 \u0628\u06d5 \u062e\u06c6\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06ce \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u062f\u06d5\u06af\u06d5\u06b5\u06cc \u062e\u0648\u0627 \u0628\u0646 \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u0646\u060c \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631 \u0632\u06cc\u0627\u0646\u0648\u0648 \u067e\u06ce \u0646\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u0646. \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u0634\u0648\u0648 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06d5\u0631 \u0642\u0627\u067e\u06cc \u062e\u0648\u062f\u0627\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u0648\u0648 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0626\u0627\u06a9\u0627\u0631\u062a\u0627\u0646 \u0686\u0644\u06c6\u0646 \u0628\u0648\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0631\u06af\u0627 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0645\u06d5\u0631\u06af \u062f\u06ce \u0648 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u0648\u06d5\u0633\u06cc\u06d5\u062a\u06ce \u0628\u06a9\u0627\u060c \u0628\u0627 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u062f\u0648\u0648 \u06a9\u06d5\u0633 - \u06a9\u06d5 \u062c\u06ce\u06af\u06d5\u06cc \u0628\u0695\u0648\u0627 \u0628\u0646 - \u0628\u06ce\u0646 \u0648 \u0644\u06ce\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0628\u0646. \u0626\u06d5\u0631 \u0644 \u06d5\u0633\u06d5\u0641\u06d5\u0631\u06cc\u0634\u062f\u0627 \u0628\u0648\u0648\u0646 \u0648 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627\u06cc \u0645\u06d5\u0631\u06af \u0647\u0627\u062a\u0646\u060c \u062f\u0648\u0648 \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u0644\u06d5 \u0648\u06d5\u0633\u06cc\u06d5\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0648 \u062f\u0648\u0648 \u0634\u0627\u06cc\u06d5\u062a\u0627\u0646\u06d5 \u0628\u06d5 \u06af\u0648\u0645\u0627\u0646 \u0628\u0648\u0648\u0646\u060c \u0647\u06d5\u062a\u0627 \u062f\u0648\u0627\u06cc \u0646\u0648\u06ce\u0698 \u0695\u0627\u06cc\u0627\u0646 \u06af\u0631\u0646 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u062f\u06d5\u0628\u06ce \u0633\u0648\u06ce\u0646\u062f\u06ce \u0628\u062e\u06c6\u0646 \u06a9\u06d5: \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0626\u06ce\u0645\u06d5 \u062f\u06cc\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0645\u0627\u06b5\u06cc \u062f\u0646\u06cc\u0627 \u0646\u0627\u0641\u0631\u06c6\u0634\u06cc\u0646 \u0648 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u06d5\u06a9\u06cc \u06a9\u06d5 \u062f\u06d5\u0628\u06ce \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0628\u06cc\u062f\u06d5\u06cc\u0646\u060c \u0646\u0627\u0645\u0627\u0646\u06d5\u0648\u06ce \u0646\u06d5\u06cc\u062f\u0631\u06a9\u06ce\u0646\u06cc\u0646\u061b \u0628\u0627 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u0626\u06d5\u0648 \u0695\u0627\u0633\u062a\u06cc \u0646\u06d5\u062f\u0631\u06a9\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0628\u06d5\u062e\u0632\u0645\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u06af\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u062f\u0631\u06a9\u0627\u0646\u062f\u0646\u06cc \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0648\u06d5\u0633\u06cc\u06d5\u062a\u06d5\u06a9\u06d5 - \u06a9\u06d5 \u062f\u06d5\u0628\u06ce \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc \u0628\u06ce\u0698\u06cc\u0646 - \u062e\u06c6 \u0628\u0628\u0648\u06ce\u0631\u06cc\u0646\u060c \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631 \u0628\u0628\u06cc\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0631\u0686\u0648\u0648 \u06a9\u06d5 \u062f\u0648\u0648 \u0634\u0627\u06cc\u06d5\u062a\u06d5\u06a9\u06d5\u06cc \u067e\u06ce\u0634\u0648\u0648 \u062e\u06c6\u06cc\u0627\u0646 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u062f\u0648\u0634\u0627\u06cc\u06d5\u062a\u06cc \u062a\u0631 - \u06a9\u06d5 \u0644\u06d5\u0648 \u062f\u0648\u0648\u0627\u0646\u06d5 \u0646\u0632\u06cc\u06a9\u062a\u0631 \u0644\u06d5 \u0645\u0631\u062f\u0648\u0648\u06d5\u06a9\u06d5 \u0628\u0646 - \u0628\u06ce\u0646\u06d5 \u067e\u06ce\u0634 \u0648 \u0633\u0648\u06ce\u0646\u062f\u06ce \u0628\u062e\u06c6\u0646 \u06a9\u06d5: \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u06cc\u062f\u06d5\u06cc\u0646\u060c \u0644\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u0648\u0648\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648 \u0695\u0627\u0633\u062a\u062a\u0631\u06d5 \u0648 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0644\u0627 \u0628\u062f\u06d5\u06cc\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u06a9\u0627\u0631\u0627\u0646\u06cc\u0646.',
    '\u0626\u06d5\u0645 \u0634\u06ce\u0648\u06d5\u06cc\u06d5 \u0628\u06c6 \u0628\u0627\u0634 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06cc \u0695\u0627\u0633\u062a\u06cc \u0646\u0632\u06cc\u06a9\u062a\u0631\u06d5\u061b \u06a9\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u0646 \u0628\u06d5\u0633\u0648\u06ce\u0646\u062f\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u062f\u06cc\u06a9\u06d5\u060c \u0626\u06d5\u0648 \u0633\u0648\u06ce\u0646\u062f\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u0627\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u0628\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u062f\u0627 \u0628\u062f\u0631\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0644\u06ce\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u0648\u06d5\u0634\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0634\u06ce \u0644\u06d5 \u062e\u0648\u0627 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce \u0648 \u0686\u06cc \u062f\u06d5\u06cc\u0628\u06cc\u0633\u0646 \u0628\u06cc\u06af\u0631\u0646\u06d5 \u06af\u0648\u06ce. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646 \u062e\u06c6 \u0628\u0648\u06ce\u0631\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0646\u0627\u06a9\u0627.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u062e\u0648\u0627 \u06af\u0634 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u06a9\u06c6\u0648\u06d5 \u062f\u06d5\u06a9\u0627 \u0648 \u0626\u06ce\u0698\u06ce: \u0628\u06ce\u0698\u0646 \u0686\u06c6\u0646\u062a\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06d5\u0648\u06d5 \u0647\u0627\u062a\u0646\u061f \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0647\u06cc\u0686 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0645\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u060c \u0647\u06d5\u0631 \u062a\u06c6 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0646\u06d5\u062f\u06cc\u0627\u0631\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc.',
    '[\u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u0634\u062a\u0627\u0646 \u0628\u0627 \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u06ce \u06a9\u06d5] \u062e\u0648\u0627 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0639\u06cc\u0633\u0627\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645! \u0648\u06d5\u0628\u06cc\u0631 \u062e\u06c6\u062a\u06cc \u0628\u06ce\u0646\u06d5\u0648\u06d5 \u0686\u06d5\u0646\u062f\u06d5\u0645 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u06c6\u062a \u0648 \u062f\u0627\u06cc\u06a9\u062a \u06a9\u0631\u062f\u061f \u0645\u0646 \u06af\u06cc\u0627\u0646\u06cc \u0647\u06d5\u0631\u06d5 \u067e\u06cc\u0631\u06c6\u0632\u0645 \u0646\u0627\u0631\u062f\u06d5 \u06cc\u0627\u0631\u06cc\u062a\u060c \u0644\u06d5 \u0633\u06d5\u0631 \u0628\u06ce\u0634\u06a9\u06d5 \u0648 \u0628\u06d5\u06af\u06d5\u0648\u0631\u06d5\u06cc\u06cc\u0634 \u062f\u06d5\u062a \u062a\u0648\u0627\u0646\u06cc \u062e\u06d5\u06b5\u06a9 \u0628\u062f\u0648\u06ce\u0646\u06cc. \u0645\u0646 \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u0646\u06cc \u062e\u06c6\u0645\u06d5\u0648\u06d5 \u0641\u06ce\u0631\u06cc \u06a9\u062a\u06ce\u0628 \u0648 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0648 \u062a\u06d5\u0648\u0695\u0627\u062a \u0648 \u0626\u06cc\u0646\u062c\u06cc\u0644\u0645 \u06a9\u0631\u062f\u06cc. \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5\u0634\u062a \u0647\u06d5\u0631 \u0644\u06d5 \u0628\u06cc\u0631\u0628\u06ce \u06a9\u06d5 \u0644\u06d5 \u062d\u06d5\u0695\u06cc \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u0645\u0646 \u0634\u06ce\u0648\u06d5\u06cc \u0645\u06d5\u0644\u06ce\u06a9\u062a \u0686\u06ce \u06a9\u0631\u062f \u0648 \u0641\u0648\u0648\u062a \u062a\u06ce\u06a9\u0631\u062f \u0648 \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u0645\u0646 \u0628\u0648\u0648 \u0628\u06d5 \u0628\u0627\u06b5\u062f\u0627\u0631\u06ce\u06a9 \u0648 \u062f\u06d5\u0641\u0695\u06cc. \u062f\u06cc\u0633\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u0645\u0646 \u0628\u0648\u0648 \u062a\u06c6 \u06a9\u0648\u06ce\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0632\u06af\u0645\u0627\u06af \u0648 \u0646\u06d5\u062e\u06c6\u0634\u06cc\u0646\u06cc \u0628\u06d5\u06b5\u06d5\u06a9\u06cc\u062a \u062e\u06c6\u0634 \u062f\u06d5\u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u0645\u0646 \u0645\u0631\u062f\u0648\u0648\u0634\u062a \u0632\u06cc\u0646\u062f\u0648\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5. \u0626\u06d5\u0648 \u062f\u06d5\u0645\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u0645 \u062f\u0627\u0628\u0648\u0648\u06cc\u060c \u0647\u0627\u062a\u06cc\u06cc\u06d5 \u0646\u0627\u0648 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u060c \u0646\u06d5\u0645\u0647\u06ce\u0634\u062a \u0632\u06cc\u0627\u0646\u062a \u0644\u06ce\u0628\u062f\u06d5\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce\u062a \u0646\u06d5\u0647\u06ce\u0646\u0627\u060c \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u06d5\u0648 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062c\u0627\u062f\u0648\u0648\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0646\u0648\u0648 \u0647\u06cc\u0686\u06cc \u062a\u0631 \u0646\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634 \u0645\u0646 - \u0647\u06d5\u0631 \u0628\u06d5 \u0647\u06cc\u0645\u0627\u06cc \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u062e\u06c6\u0645 - \u062d\u06d5\u0648\u0627\u0631\u06cc\u06cc\u06d5\u06a9\u0627\u0646\u0645 \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f: \u062f\u06d5\u0628\u06ce \u0628\u0695\u0648\u0627\u0648 \u0628\u06d5 \u0645\u0646 \u0647\u06d5\u0628\u06ce \u0648 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0645\u0646\u06cc\u0634 \u0628\u0627\u0648\u06d5\u0695 \u0628\u06a9\u06d5\u0646. \u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u0648\u0627\u06cc\u06d5! \u062a\u06c6 \u0634\u0627\u06cc\u06d5\u062a \u0628\u06d5 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u06cc \u0648 \u0647\u06d5\u0631 \u0645\u0644\u06a9\u06d5\u0686\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062a\u06c6\u06cc\u0646.',
    '\u06a9\u0627\u062a\u06ce \u062d\u06d5\u0648\u0627\u0631\u06cc\u06cc\u06d5\u06a9\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u0639\u06cc\u0633\u0627\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645! \u0626\u06d5\u0631\u06ce \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u062a\u06c6 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062f\u06ce \u062e\u0648\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0628\u06c6 \u0628\u0646\u06ce\u0631\u06ce\u061f \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0644\u06ce \u0628\u062a\u0631\u0633\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u06af\u06d5\u0631\u06d5\u06a9\u0645\u0627\u0646\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0646\u06cc \u0626\u0627\u0648\u0627 \u0628\u062e\u06c6\u06cc\u0646 \u0648 \u062f\u06b5\u0646\u06cc\u0627 \u0628\u06cc\u0646 \u0648 \u0628\u0627\u0648\u06d5\u0695 \u06a9\u06d5\u06cc\u0646 \u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u067e\u06ce\u062a \u06af\u0648\u062a\u06cc\u0646 \u0695\u0627\u0633\u062a\u06d5\u061b \u0626\u06ce\u0645\u06d5\u0634 \u0628\u06ce\u06cc\u0646\u06d5 \u067e\u0627\u06b5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06c6 \u062a\u06c6 \u062f\u06d5\u062f\u06d5\u0646.',
    '\u0639\u06cc\u0633\u0627\u06cc \u06a9\u0648\u0695\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0644\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5\u0648\u06d5 \u062a\u06c6 \u062e\u0648\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0695\u06ce \u06a9\u06d5\u060c \u06a9\u06d5 \u0628\u06c6 \u0647\u06d5\u0648\u06d5\u06b5 \u0648 \u062f\u0648\u0627\u06cc\u06cc\u0645\u0627\u0646 \u062c\u06ce\u0698\u0646\u06d5\u06cc\u06d5\u06a9 \u0628\u06ce \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9 \u0628\u06ce \u0644\u06d5 \u062a\u06c6\u0695\u0627 \u0648 \u0644\u06d5 \u0631\u06c6\u0632\u06cc \u0628\u06ce \u0628\u06d5\u0634\u0645\u0627\u0646 \u0645\u06d5\u06a9\u06d5. \u062a\u06c6 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u0695\u06c6\u0632\u06cc \u062f\u06d5\u0631\u06cc.',
    '\u062e\u0648\u062f\u0627 \u06af\u0648\u062a\u06cc: \u0645\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc \u0628\u06d5 \u0695\u06ce \u062f\u06d5\u06a9\u06d5\u0645\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062f\u0648\u0627\u06cc\u06d5 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062d\u0627\u0634\u0627\u06cc\u06d5 \u0628\u06a9\u0627\u060c \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u06d5\u06a9\u06cc \u0648\u0627\u06cc \u062f\u06d5\u062f\u06d5\u0645 \u06a9\u06d5 \u062a\u0627 \u0626\u06ce\u0633\u062a\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5\u062f\u0627 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce\u06a9\u0645 \u0626\u0627\u0648\u0627 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u0646\u06d5\u062f\u0627\u0628\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0631\u06ce \u0639\u06cc\u0633\u0627\u06cc \u06a9\u0648\u0695\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645! \u062a\u06c6 \u06af\u0648\u062a\u0648\u0648\u062a\u06d5 \u0628\u06d5\u0648 \u0645\u06d5\u0631\u062f\u0645\u06d5 - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u062f\u06d5\u0628\u06ce \u0626\u06ce\u0648\u06d5 \u0645\u0646 \u0648 \u062f\u0627\u06cc\u06a9\u0645 \u0648\u06d5\u06a9 \u062f\u0648\u0648 \u062e\u0648\u062f\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061f \u06af\u0648\u062a\u06cc: \u067e\u0627\u06a9\u06cc \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u0647\u06d5\u0631 \u0634\u06cc\u0627\u0648\u06cc \u062a\u06c6\u06cc\u06d5\u060c \u0645\u0646 \u0695\u06ce\u0645 \u0646\u0627\u06a9\u06d5\u0648\u06ce \u0647\u06cc\u0686 \u0634\u062a\u06ce \u0628\u06ce\u062c\u06af\u06d5 \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0628\u0628\u06ce\u0698\u0645. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u0642\u0633\u06d5\u0645 \u0628\u06a9\u0631\u062f\u0628\u0627 \u062e\u06c6\u062a \u062f\u06d5\u062a\u0632\u0627\u0646\u06cc. \u0647\u06d5\u0631\u0686\u06cc\u06cc\u06d5\u06a9\u06cc\u0634\u0645 \u0628\u06d5 \u0646\u0627\u062e\u06cc \u062f\u06b5\u062f\u0627 \u0628\u0628\u0648\u0648\u0631\u06ce\u060c \u062a\u06c6 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06cc\u061b \u062a\u06d5\u0646\u06cc\u0627 \u0645\u0646\u0645 \u06a9\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u062a \u0647\u06cc\u0686 \u0646\u0627\u0632\u0627\u0646\u0645\u061b \u0647\u06d5\u0631 \u062a\u06c6 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0646\u06d5\u062f\u06cc\u0627\u0631\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc.',
    '\u062a\u06c6 \u0686\u06c6\u0646\u062a \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0645\u0646 \u062f\u0627\u060c \u0645\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0645 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f. \u06af\u0648\u062a\u0645: \u062e\u0648\u062f\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646 \u06a9\u06d5 \u0626\u06d5\u0645\u0646\u06cc\u0634 \u0648 \u0626\u06ce\u0648\u06d5\u0634\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0627\u0646\u062f\u0648\u0648\u06d5. \u0647\u06d5\u062a\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0628\u0648\u0648\u0645\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0648 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u0648\u0648\u0645\u061b \u0644\u06d5\u0648\u0633\u0627\u0634\u06d5\u0648\u06d5 \u06a9\u06d5 \u0645\u0646\u062a \u0628\u06c6 \u062e\u06c6\u062a \u0628\u0631\u062f\u06d5\u0648\u06d5\u060c \u0628\u06c6 \u062e\u06c6\u062a \u0647\u06d5\u0631 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0627\u0646 \u0628\u0648\u0648\u06cc. \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u062e\u06c6\u062a \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc.',
    '\u06af\u06d5\u0631 \u0628\u062a\u06d5\u0648\u06ce \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06cc\u060c \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u062a\u0646\u061b \u06af\u06d5\u0631 \u0628\u0634\u062a\u06d5\u0648\u06ce \u0644\u06ce\u06cc\u0627\u0646 \u062e\u06c6\u0634 \u0628\u06ce\u060c \u062e\u06c6\u062a \u062f\u06d5\u0632\u0627\u0646\u06cc \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u062a\u06cc \u06a9\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u062a \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5 \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc.',
    '\u062e\u0648\u062f\u0627 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0695\u06c6 \u0695\u06c6\u0698\u06ce\u06a9\u06d5 \u0695\u0627\u0633\u062a\u06d5\u06a9\u0627\u0646 \u0695\u0627\u0633\u062a\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u06ce. \u0626\u06d5\u0648\u0627\u0646 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646.',
    '\u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5 - \u06af\u0634 \u0644\u06d5 \u06af\u0634 - \u0645\u0648\u06b5\u06a9\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u067e\u06d5\u0633\u0646 \u0648 \u0633\u0648\u067e\u0627\u0633 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06ce \u06a9\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u062a\u0627\u0631\u06cc\u06a9\u0627\u06cc\u06cc \u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u0627\u06cc\u06cc\u0634 \u0626\u06d5\u0648 \u067e\u06d5\u06cc\u062f\u0627\u06cc \u06a9\u0631\u062f. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u062f\u06ce\u0646 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5 \u0642\u0648\u0695 \u0686\u06ce \u06a9\u0631\u062f \u0648 \u0645\u0627\u0648\u06d5\u06cc \u0698\u06cc\u0646\u06cc \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u0646. \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634\u06cc \u0628\u0695\u06cc\u0627\u0631 \u062f\u0627\u0648\u06d5 \u0648 \u0646\u0627\u0648\u06cc \u0646\u0627\u0648\u06d5 [\u06a9\u06d5 \u0644\u06d5 \u0698\u06cc\u0627\u0646 \u0628\u06ce \u0628\u06d5\u0634 \u062f\u06d5\u0628\u0646]. \u06a9\u06d5\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06cc\u0633\u0627\u0646\u06d5\u06a9\u06d5 \u0647\u06d5\u0631 \u062f\u0648\u0648\u062f\u06b5\u0646.',
    '\u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u062e\u0648\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0648 \u0626\u0627\u0634\u06a9\u0631\u0627\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9 \u0628\u06cc\u06a9\u06d5\u0646 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u0647\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9 \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u0646 \u062e\u0648\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06ce \u0648 \u0628\u06ce\u062a\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u0626\u06cc\u0644\u0644\u0627 \u0647\u06d5\u0631 \u062d\u0627\u0634\u0627\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u062e\u06c6\u06cc\u0627\u0646\u06cc \u0644\u06ce \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u0626\u06d5\u0648\u0627 \u0648\u06d5\u062e\u062a\u06ce \u0626\u06d5\u0645 \u0695\u0627\u0633\u062a\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06c6\u0647\u0627\u062a\u060c \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f. \u0644\u06d5\u062f\u0648\u0627\u0695\u06c6\u0698\u062f\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u062f\u06d5\u0628\u0646 \u0644\u06d5\u0648\u0634\u062a\u0627\u0646\u06d5\u06cc \u0626\u06ce\u0633\u062a\u0627 \u06af\u06d5\u067e\u06cc \u062e\u06c6\u06cc \u067e\u06ce \u062f\u06d5\u062f\u06d5\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u06d5\u0646\u062f\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u067e\u06ce\u0634 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0628\u06d5\u0631\u06d5\u0645\u0627\u0646 \u0642\u0695 \u06a9\u0631\u062f\u0646\u061f \u0626\u06d5\u0648 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5\u0634 \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0628\u06d5\u0648\u0627\u0646\u0645\u0627\u0646 \u062f\u0627\u0628\u0648\u0648\u060c \u0628\u06d5\u0645\u0627\u0646\u0645\u0627\u0646 \u0646\u06d5\u062f\u0627\u0648\u06d5. \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u06a9\u06d5\u06cc\u06a9\u06d5\u0645 \u0648 \u0634\u06d5\u0633\u062a\u06d5\u0695\u06d5\u0647\u06ce\u06b5\u06d5 \u0628\u0627\u0631\u0627\u0646\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0628\u0627\u0631\u0627\u0646\u062f\u0646 \u0648 \u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u062f \u06a9\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0695\u0648\u0648\u0628\u0627\u0631\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u0698\u06cc\u0627\u0646. \u06a9\u06d5\u0686\u06cc \u0647\u06ce\u0646\u062f \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0642\u0695\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646 \u0648 \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646 - \u0644\u06d5 \u062c\u06ce\u06cc \u0626\u06d5\u0648\u0627\u0646 - \u0628\u06d5\u0631\u06d5\u06cc \u062a\u0631\u0645\u0627\u0646 \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0646\u062f\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u06a9\u0627\u063a\u06d5\u0632 \u0646\u0627\u0645\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u062a\u06c6 \u0646\u0627\u0631\u062f\u0628\u0627\u06cc\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0648 \u0628\u06d5\u062f\u06d5\u0633\u062a \u0647\u06d5\u06b5\u06cc\u0627\u0646 \u06af\u06b5\u06c6\u0641\u06cc\u0628\u0627\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 \u0647\u06d5\u0631 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0628\u0628\u06ce \u0648 \u0646\u06d5\u0628\u06ce \u062c\u0627\u062f\u0648\u0648 \u0646\u06d5\u0628\u06ce \u0686\u06cc \u062a\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0627 \u062f\u06d5\u0634\u06b5\u06ce\u0646: \u0626\u06d5\u062f\u06cc \u0628\u06c6\u0686\u06cc \u062e\u0648\u0627 \u0641\u0631\u0634\u062a\u06d5\u06cc \u0628\u06c6 \u0626\u06d5\u0648 \u0646\u06d5\u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0641\u0631\u0634\u062a\u06d5\u0634\u0645\u0627\u0646 \u0628\u0646\u0627\u0631\u062f\u0627\u06cc\u06d5\u060c \u0628\u06ce \u0633\u06c6 \u06a9\u0627\u0631 \u0644\u06d5 \u06a9\u0627\u0631 \u062f\u06d5\u062a\u0631\u0627\u0632\u0627 \u0648 \u0647\u06cc\u0686 \u0645\u06c6\u06b5\u06d5\u062a\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u062f\u06d5\u062f\u0631\u0627.',
    '\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u0645\u0627\u0646 \u0641\u0631\u0634\u062a\u06d5\u0634 \u0628\u0627\u060c \u0647\u06d5\u0631 \u062f\u06d5\u0628\u0648\u0648 \u062c\u0644\u06a9\u06cc \u067e\u06cc\u0627\u0648\u06ce\u06a9\u06cc \u062f\u06d5\u0628\u06d5\u0631 \u0628\u06a9\u06d5\u06cc\u0646 \u0648 \u0626\u06d5\u0648 \u062c\u0644\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u067e\u06c6\u0634\u0646 \u0626\u06d5\u0648 \u067e\u06c6\u0634\u06cc\u0628\u0627\u06cc.',
    '\u0628\u06d5 \u06af\u06d5\u0644\u06ce\u06a9 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6\u060c \u0695\u0634\u062e\u06d5\u0646 \u06a9\u0631\u0627 \u0648 \u0695\u0634\u062e\u06d5\u0646\u06a9\u06d5\u0631\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u06af\u06d5\u0634\u062a\u06ce \u0628\u06a9\u06d5\u0646\u060c \u062a\u0627 \u0628\u0632\u0627\u0646\u0646 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u06a9\u0648\u06ce \u06af\u06d5\u06cc\u0634\u062a.',
    '\u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5\u060c \u0645\u0627\u06b5\u06cc \u06a9\u06ce\u06cc\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0645\u0648\u0648 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u062e\u06c6\u06cc \u0646\u0648\u0648\u0633\u06cc\u0648\u06d5 \u06a9\u06d5 \u062f\u06d5\u0628\u06ce \u062e\u0627\u0648\u06d5\u0646 \u0628\u06d5\u0632\u06d5 \u0628\u06ce. \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a - \u06a9\u06d5 \u0628\u06ce\u06af\u0648\u0645\u0627\u0646\u06d5 \u0647\u0627\u062a\u0646\u06cc - \u06af\u0634\u062a\u0648\u0648 \u0644\u06ce\u06a9 \u06a9\u06c6 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06af\u06cc\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06c6\u0695\u0627\u0646\u062f\u0648\u0648\u06d5 \u0626\u06d5\u0648\u0627\u0646\u0646 \u0628\u0695\u0648\u0627\u0646\u0627\u0647\u06ce\u0646\u0646.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0644\u06d5 \u0634\u06d5\u0648\u06af\u0627\u0695 \u0648 \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u062f\u0627\u060c \u0647\u06d5\u062f\u0627 \u062f\u06d5\u062f\u0627 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u0628\u06d5 \u0633\u06d5\u0631\u0648\u06d5\u0631\u06cc \u062e\u06c6 \u0628\u0632\u0627\u0646\u0645\u060c \u06a9\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u062f\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0695\u06c6\u0632\u06cc \u0628\u06d5 \u0632\u06cc\u0646\u062f\u06d5\u0648\u06d5\u0631 \u062f\u06d5\u062f\u0627 \u0648 \u062e\u06c6\u06cc \u0644\u06d5 \u0628\u0698\u06cc\u0648 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0645\u0646 \u062f\u0631\u0627 \u06a9\u06d5 \u06cc\u06d5\u06a9\u06d5\u0645\u06cc\u0646 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u0645. \u0633\u0627 \u062a\u06c6 \u0642\u06d5\u062a \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06d5\u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u06cc.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645 \u0628\u06a9\u06d5\u0645\u060c \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0695\u06c6\u0698\u06d5 \u0647\u06d5\u0631\u06d5\u062f\u0698\u0648\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u067e\u0695 \u0645\u06d5\u062a\u0631\u0633\u06cc\u060c \u062a\u0631\u0633\u0645 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u062e\u0648\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0648\u0627\u0632\u0628\u06ce\u0646\u06ce\u060c \u0626\u06d5\u0648\u06d5 \u062f\u06cc\u0627\u0631\u06d5 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0633\u062a\u0648\u0648\u06d5\u061b \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0634 \u0695\u0632\u06af\u0627\u0631\u06cc\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u062a \u0628\u06d5 \u0633\u06d5\u0631 \u0628\u06ce\u0646\u06ce\u060c \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u06c6\u06cc \u06a9\u06d5\u0633 \u0644\u06d5 \u0633\u06d5\u0631\u062a\u06cc \u0648\u06d5\u0644\u0627 \u0646\u0627\u0628\u0627. \u06af\u06d5\u0631 \u0628\u06d5\u0686\u0627\u06a9\u06d5\u0634\u062a \u0648\u06d5\u062e\u0648\u06ce\u0646\u06ce\u060c \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u067e\u06ce \u062f\u06d5\u06a9\u0631\u06ce.',
    '\u0647\u06d5\u0631\u062e\u0648\u062f\u0627\u0634\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u062f\u06d5\u0633\u062a\u06cc \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u06a9\u0627\u0631\u0632\u0627\u0646\u06d5 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u0627\u0646\u06cc \u06a9\u06ce \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u0627\u0646\u06ce\u06a9 \u06af\u0631\u06cc\u0646\u06af\u062a\u0631\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0634\u0627\u06cc\u06d5\u062a\u06d5 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u0645\u0627\u0646\u062f\u0627\u061b \u0626\u06d5\u0648 \u0628\u06d5 \u062e\u06d5\u06b5\u0627\u062a \u0628\u06c6 \u0645\u0646\u06cc \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u062a\u0627 \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0648 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u06ce\u0646\u0645 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u06a9\u06d5 \u067e\u06ce \u062f\u06d5\u06af\u0627. \u0626\u06ce\u0648\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u06d5\u062f\u06d5\u0646 \u06a9\u06d5 \u0648\u06d5\u0644 \u062e\u0648\u062f\u0627 \u0686\u06d5\u0646\u062f \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u06cc \u062a\u0631\u06cc\u0634 \u0647\u06d5\u06cc\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0646\u0627\u062f\u06d5\u0645. \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce\u06a9\u06d5 \u0648 \u0645\u0646 \u0644\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u0627\u062f\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u0648\u0648\u0631 \u0648 \u0628\u06ce\u0632\u0627\u0631\u0645.',
    '\u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u06a9\u0648\u0695\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0627\u0634 \u062f\u06d5\u0646\u0627\u0633\u0646\u060c \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634 \u0648\u0627 \u062f\u06d5\u0646\u0627\u0633\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u062e\u0648\u0633\u0627\u0631 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0628\u0695\u0648\u0627 \u0646\u0627\u06a9\u06d5\u0646.',
    '\u06a9\u06ce\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631 \u0628\u06ce \u06a9\u06d5 \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646 \u06cc\u0627 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0646\u061f \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0647\u06d5\u0631 \u06a9\u06ce \u0646\u0627\u0647\u06d5\u0642 \u0628\u06ce\u060c \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u0628\u06ce.',
    '\u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u06a9\u06d5 \u06af\u0634\u062a\u06cc\u0627\u0646 \u06a9\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u0628\u0648\u0648\u060c \u0626\u06ce\u0698\u06cc\u0646: \u06a9\u0648\u0627\u0646\u06ce \u0628\u06d5 \u062e\u06d5\u06cc\u0627\u06b5 \u062f\u06d5\u0648\u06af\u0648\u062a \u0634\u06d5\u0631\u06cc\u06a9\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627\u061f \u062f\u06d5 \u0628\u06cc\u0627\u0646\u0647\u06ce\u0646\u0646.',
    '\u062c\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5 \u0647\u06cc\u0686 \u0639\u0648\u0632\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u0627\u0628\u06ce \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648\u06d5 \u0628\u06ce\u0698\u0646: \u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u062e\u0648\u062f\u0627 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0627\u0646\u062f\u0648\u06d5\u061b \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u0645\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u06c6\u062a\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0626\u06d5\u0648.',
    '\u0628\u0695\u0648\u0627\u0646\u06d5 \u0686\u06c6\u0646 \u062f\u0631\u06c6 \u0644\u06d5 \u0633\u06d5\u0631 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646! \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a\u060c \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0686\u0648\u0648\u0646.',
    '\u0626\u06cc \u0648\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u06af\u0648\u06ce\u062a \u067e\u06ce \u062f\u06d5\u062f\u0627. \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0645\u06d5 \u067e\u06d5\u0631\u062f\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631 \u062f\u06b5\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06a9\u06ce\u0634\u0627\u0648\u06d5 \u06a9\u06d5 \u062a\u06ce\u062a \u0646\u06d5\u06af\u06d5\u0646\u060c \u06af\u0631\u0627\u0646\u0627\u06cc\u06cc\u0634\u0645\u0627\u0646 \u062e\u0633\u062a\u06c6\u062a\u06d5 \u0646\u0627\u0648 \u06af\u0648\u06ce\u0686\u06a9\u06d5\u06cc\u0627\u0646 \u06a9\u06d5 [\u0646\u06d5\u0628\u06cc\u0633\u0646] \u0648 \u0647\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9 \u0628\u0628\u06cc\u0646\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0646. \u0626\u06cc \u0648\u0627\u0634 \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062f\u06ce\u0646\u06d5 \u0644\u0627\u062a \u062c\u0648\u0627\u0628\u06d5\u062c\u06d5\u0646\u06af\u06cc\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u0686\u06cc\u0631\u06c6\u06a9\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06d5 \u0648 \u0686\u06cc\u062a\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u0647\u06ce\u06b5\u0646 \u062e\u06d5\u06b5\u06a9 \u0628\u06ce\u0646\u06d5 \u0644\u0627\u06cc \u0648 \u0628\u06c6 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u062e\u06c6\u06cc \u0644\u06ce \u062f\u0648\u0648\u0631 \u062f\u06d5\u06af\u0631\u0646. \u0626\u06d5\u0645 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0628\u06d5 \u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0633\u062a\u06cc \u067e\u06ce \u0628\u06a9\u06d5\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u062f\u06d5\u0628\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0633\u0627 \u0628\u06cc\u0627\u0646\u0628\u06cc\u0646\u06cc \u06a9\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u06ce\u0648\u0627\u0631\u06cc \u0626\u0627\u06af\u0631 \u0695\u0627\u06af\u06cc\u0631\u0627\u0648\u0646\u060c \u0626\u06ce\u0698\u0646: \u062e\u06c6\u0632\u06cc \u062f\u06cc\u0633\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646\u06af\u06ce\u0695\u0627\u06cc\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0646 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f \u0648 \u0626\u06ce\u0645\u06d5\u0634 \u0644\u06d5 \u0695\u06ce\u0632\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0628\u0648\u0648\u06cc\u0646.',
    '\u0695\u0627\u0633\u062a\u06cc\u0634 \u0646\u0627\u06a9\u06d5\u0646\u060c \u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u0648\u0648\u06d5 \u06a9\u06d5 \u0644\u06d5 \u067e\u06ce\u0634\u0627 \u0644\u06d5 \u062f\u06b5\u062f\u0627 \u062d\u0627\u0634\u0627\u0631\u06cc\u0627\u0646 \u062f\u0627\u0628\u0648\u0648. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0634\u06af\u06d5\u0695\u0627\u0628\u0627\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u06af\u0648\u062a\u0648\u0631\u0627 \u0645\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0647\u06d5\u0631 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u06af\u0648\u062a\u06cc\u0634\u06cc\u0627\u0646: \u062c\u06af\u06d5 \u0644\u06d5\u0645 \u0698\u06cc\u0646\u06d5\u06cc \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627\u0645\u0627\u0646 \u0686\u06cc\u062a\u0631 \u0646\u06cc\u06cc\u06d5 \u0648 \u0626\u06ce\u0645\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648 \u0646\u0627\u0628\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u06af\u06d5\u0631 \u0628\u06cc\u0627\u0646 \u062f\u0648\u0648\u0646\u06cc \u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u062f\u0627 \u06a9\u06d5 \u0644\u06d5 \u0647\u06d5\u0645\u0628\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0695\u0627\u06af\u06cc\u0631\u0627\u0648\u0646\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u06ce: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u06d5\u0648 \u067e\u06ce \u0695\u0627\u0633\u062a \u0646\u06d5\u0628\u0648\u0648\u061f \u0626\u06ce\u0698\u0646: \u0628\u06d5\u06b5\u06ce\u060c \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06d5\u0646\u062f\u06d5\u0646 \u0695\u0627\u0633\u062a\u06cc \u0695\u0627\u0633\u062a\u06d5. \u0626\u06ce\u0698\u06ce: \u062f\u06d5 \u0626\u0627\u0632\u0627\u0631 \u0628\u0686\u06ce\u0698\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u062d\u0627\u0634\u0627\u06cc\u06d5\u06cc \u062f\u06d5\u062a\u0627\u0646\u06a9\u0631\u062f.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0632\u06cc\u0627\u0646\u0645\u06d5\u0646\u062f \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0647\u06cc\u0686 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f\u060c \u062f\u06ce\u0646\u06d5 \u0628\u06d5\u0631 \u0628\u0627\u0631\u06d5\u06af\u0627\u06cc \u062e\u0648\u062f\u0627. \u062a\u0627 \u0626\u06d5\u0648 \u0633\u0627\u062a\u06d5\u06cc \u0644\u06d5 \u067e\u0695 \u0633\u06d5\u06b5\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0695\u0627\u0628\u0648\u0648. \u0626\u06cc\u062a\u0631 \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u062d\u0627\u06b5\u06ce\u06a9\u062f\u0627 \u06a9\u06c6\u06b5\u06d5\u067e\u0634\u062a\u06cc \u0628\u0627\u0631\u06cc \u06af\u0631\u0627\u0646\u06cc \u06af\u0648\u0646\u0627\u0647\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u0634\u062a\u06d5\u060c \u0647\u06d5\u0631 \u0626\u0627\u062e \u0648 \u062f\u0627\u062e \u0648 \u067e\u06d5\u06a9\u06cc\u0627\u0646\u06d5\u061b \u0626\u06ce\u0698\u0646: \u062d\u06d5\u06cc\u0641\u06ce \u0626\u06ce\u0645\u06d5 \u0686\u06d5\u0646\u062f\u0646 \u062f\u0631\u06ce\u063a\u06cc \u06a9\u0631\u062f. \u0633\u0627 \u0628\u0632\u0627\u0646\u06d5 \u0686\u06d5\u0646\u062f \u0628\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u06af\u0631\u0627\u0646 \u0648 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5.',
    '\u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627 \u062c\u06af\u06d5 \u0644\u06d5 \u06af\u0627\u06b5\u062a\u06d5 \u0648 \u06af\u06d5\u067e \u0646\u06cc\u06cc\u06d5 \u0648 \u0645\u0627\u06b5\u06cc \u0626\u06d5\u0648\u0644\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062e\u0648\u0627\u062a\u0631\u0633 \u0648 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0646 \u0632\u06c6\u0631 \u0686\u0627\u06a9\u062a\u0631\u06d5. \u062f\u06d5\u0633\u0627 \u0628\u06c6 \u062f\u06d5\u0628\u06ce \u062a\u06ce \u0646\u06d5\u06af\u06d5\u0646\u061f!',
    '\u0626\u06ce\u0645\u06d5 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646 \u0626\u06d5\u0648 \u0642\u0633\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0628\u06c6\u062a \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0647\u06c6\u06cc \u062f\u06b5\u062a\u06d5\u0646\u06af\u06cc. \u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062a\u06c6 \u0648\u06d5\u062f\u0631\u06c6 \u0646\u0627\u062e\u06d5\u0646\u06d5\u0648\u06d5\u061b \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc\u062f\u0627 \u0626\u06d5\u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646\u06d5 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0641\u0631\u06d5 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0628\u06d5\u0631 \u0644\u06d5 \u062a\u06c6\u0634 - \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0626\u06ce\u0633\u062a\u06d5\u06cc \u062a\u06c6 - \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646 \u0698\u0645\u06ce\u0631\u0627\u0648\u0646. \u0648\u06ce\u0695\u0627\u06cc \u0628\u0627\u0648\u06d5\u0695 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u0627\u0646 \u0648 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06cc\u0634\u060c \u0647\u06d5\u0631\u0648\u0627 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0645\u0627\u0646\u06d5\u0648\u06d5\u061b \u0647\u06d5\u062a\u0627\u06a9\u0648\u0648 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u06af\u06d5\u06cc\u06cc\u0634\u062a. \u0633\u06c6\u0632\u06cc \u062e\u0648\u062f\u0627\u0634 \u0628\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce\u06a9 \u0646\u0627\u06af\u06c6\u0695\u062f\u0631\u06ce \u0648 \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u062a\u06c6 \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u062a \u0632\u0627\u0646\u06cc\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0644\u06d5 \u0644\u0627\u062a \u06af\u0631\u0627\u0646 \u062f\u06ce \u06a9\u06d5 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u062a\u06d5\u0648\u06d5 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u0646\u060c \u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u062a\u0648\u0627\u0646\u0627\u062a\u062f\u0627\u06cc\u06d5\u060c \u0695\u06d5\u0647\u06c6\u06b5\u06ce\u06a9\u06cc \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u0628\u06a9\u06c6\u06b5\u06d5\u060c \u06cc\u0627\u0646 \u067e\u06d5\u06cc\u0698\u06d5\u06cc\u06d5\u06a9 \u06a9\u06d5 \u067e\u06ce\u06cc \u0647\u06d5\u06b5\u0686\u06cc \u0628\u06c6 \u0626\u0627\u0633\u0645\u0627\u0646 \u0648 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u062a\u06d5\u0648 \u0628\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06ce\u0646\u06d5 [\u0628\u0695\u0648\u0627\u062a \u067e\u06ce \u0628\u06a9\u06d5\u0646]. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627\u060c \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0631\u062f. \u0626\u06cc\u062a\u0631 \u0646\u0627\u0634\u06ce \u062a\u06c6 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0646\u06d5\u0632\u0627\u0646\u0627\u0646 \u0628\u06cc.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06af\u0648\u06ce\u062a \u0644\u06ce \u062f\u06d5\u06af\u0631\u0646 \u06a9\u06d5 \u062f\u06d5\u0628\u06cc\u0633\u0646. \u0645\u0631\u062f\u0648\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u062e\u0648\u0627 \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u067e\u0627\u0634\u0627\u0646 \u06af\u0634\u062a\u06cc\u0627\u0646 \u0628\u06c6 \u0644\u0627\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0698\u0646: \u0628\u06c6\u0686\u06cc \u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9\u06cc \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0695\u0627 \u0628\u06c6 \u0646\u06d5\u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06d5\u06cc\u06d5 \u0647\u06d5\u0631 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 [\u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627] \u0628\u06d5 \u0695\u06ce\u06cc \u0628\u06a9\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631 \u0632\u06cc\u0646\u062f\u06d5\u0648\u06d5\u0631\u06ce \u0644\u06d5 \u0639\u06d5\u0631\u0632\u0627 \u062a\u06d5\u06a4\u06af\u06d5\u0695\u06cc\u06d5\u062a\u06cc \u0648 \u0647\u06d5\u0631 \u0645\u06d5\u0644\u06ce \u0628\u06d5 \u0628\u0627\u06b5\u0627\u0646 \u062f\u06d5\u0641\u0695\u06ce\u060c \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u06a9\u06c6\u0645\u06d5\u06b5\u0627\u0646\u06ce\u06a9\u0646 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06ce\u0648\u06d5. \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0647\u06cc\u0686 \u0634\u062a\u0645\u0627\u0646 \u0647\u06d5\u06b5 \u0646\u06d5\u0628\u0648\u0627\u0631\u062f\u0648\u0648\u06d5\u061b \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0634 \u06af\u0634 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u06c6\u0648\u06d5 \u0626\u06d5\u06a9\u0631\u06ce\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0627\u0646\u06cc\u060c \u06a9\u06d5\u0695 \u0648 \u0644\u0627\u06b5\u0646 \u0648 \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u06cc\u062f\u0627 \u062e\u0648\u0644 \u062f\u06d5\u062e\u06c6\u0646. \u062e\u0648\u062f\u0627 \u0628\u06d5\u06a9\u06d5\u06cc\u0641\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u0627\u060c \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u0695\u06ce \u062f\u06d5\u06af\u06c6\u0695\u06ce \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u06cc\u0634 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u0648\u0648 \u0628\u06c6 \u0628\u0646\u06ce\u0631\u06ce \u06cc\u0627\u0646 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0648 \u0644\u06ce \u0695\u0627\u0628\u06ce\u060c \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0633\u0627 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0647\u0627\u0646\u0627 \u0628\u06c6 \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u062f\u06d5\u0628\u06d5\u0646\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u062a\u0646\u060c \u0695\u0627\u0633\u062a\u06cc \u0628\u06b5\u06ce\u0646.',
    '\u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u0647\u0627\u0646\u0627 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u0628\u06d5\u06b5\u0627\u062a\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u0627 \u062f\u06d5\u0628\u0627 \u0648 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u062a\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u062f\u06d5\u0686\u06ce\u062a\u06d5\u0648\u06d5 \u06a9\u06d5 [\u0626\u06ce\u0633\u062a\u0627] \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0633\u06d5\u0631 \u06af\u06d5\u0644\u06ce\u06a9 \u0644\u06d5 \u06af\u06d5\u0644\u0627\u0646\u06cc \u067e\u06ce\u0634 \u062a\u06c6\u0634 [\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0645\u0627\u0646] \u0628\u06d5 \u0695\u06ce \u06a9\u0631\u062f\u0648\u0648\u0646. [\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06cc\u0627\u063a\u06cc \u062f\u06d5\u0628\u0648\u0648\u0646] \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627 \u0648 \u0646\u0633\u06cc\u0628\u06d5\u062a\u0627\u0646\u0645\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u0646\u060c \u062a\u0627 \u0628\u06d5\u0634\u06a9\u06ce\u06a9\u0648\u0648 \u0644\u06d5 \u0628\u06d5\u0631\u0645\u0627\u0646 \u0628\u0644\u0627\u06b5\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0628\u06c6\u0686\u06cc \u06a9\u06d5 \u0628\u06d5\u06b5\u0627\u0634\u0645\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06ce\u0646\u0627\u0646\u060c \u0644\u06d5 \u0628\u06d5\u0631\u0645\u0627\u0646 \u0646\u06d5\u067e\u0627\u0695\u0627\u0646\u06d5\u0648\u06d5\u061f \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0645\u0627\u0646 \u062f\u06b5\u0695\u06d5\u0634 \u0628\u0648\u0648\u0646 \u0648\u060c \u0634\u06d5\u06cc\u062a\u0627\u0646\u06cc\u0634 \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u06cc \u0648\u0627 \u0644\u06d5 \u0628\u06d5\u0631 \u0695\u0627\u0632\u0627\u0646\u062f\u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 [\u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u0633\u06d5\u0631 \u062d\u0627\u0634\u0627\u06cc\u06d5 \u0645\u0627\u0646].',
    '\u062c\u0627 \u0648\u06d5\u062e\u062a\u06ce \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u0627\u0646\u06d5\u06cc\u0627\u0646 - \u06a9\u06d5 \u06a9\u0631\u0627\u0628\u0648\u0648\u0646 - \u0644\u06d5 \u0628\u06cc\u0631\u06d5\u0648 \u0686\u0648\u0648\u060c \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0631\u06af\u0627\u06cc \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06cc\u0646 \u0628\u06c6 \u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5 \u0647\u06d5\u062a\u0627 \u06a9\u06d5\u0648\u062a\u0646\u06d5 \u062e\u06c6\u0634\u06cc \u0648 \u0634\u0627\u062f\u06cc\u061b \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5\u067e\u0695 \u0648 \u0646\u06d5\u06a9\u0627\u0648\u06ce\u06a9 \u0628\u06d5\u0631 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062e\u0633\u062a\u0646 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0646\u0627\u0647\u0648\u0645\u06ce\u062f \u0645\u0627\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06cc\u062a\u0631 \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u06cc\u0634\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0628\u0648\u0648\u060c \u062f\u0648\u0627\u0628\u0695\u0627\u0648 \u0628\u0648\u0648\u0646 \u0648 \u0633\u067e\u0627\u0633 \u0628\u06c6 \u062e\u0648\u0627\u06cc \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062f\u0646\u06cc\u0627.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06d5 \u0645\u0646 \u0628\u06ce\u0698\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u0647\u06d5\u0633\u062a\u06cc \u06af\u0648\u06ce \u0648 \u0633\u06c6\u0645\u0627\u06cc \u0686\u0627\u0648\u062a\u0627\u0646 \u0644\u06ce \u0628\u0633\u062a\u06ce\u0646\u06ce \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u062f\u06b5\u06af\u06d5\u0644\u06cc\u0634\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0645\u06c6\u0631 \u062f\u0627\u0628\u0646\u06ce\u060c \u06a9\u06ce \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce - \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u0627 - \u0628\u0648\u0648\u062f\u0627\u062a\u06d5\u0648\u06d5\u061f \u062f\u06d5\u0628\u0695\u0648\u0627\u0646\u06d5 \u0626\u06ce\u0645\u06d5 \u0686\u0644\u06c6\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0634\u06cc \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u061b \u0647\u06ce\u0634\u062a\u0627 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06ce \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06d5 \u0645\u0646 \u0628\u06ce\u0698\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u06b5\u0627\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5 \u067e\u0695\u06ce\u06a9 \u0648 \u0628\u06d5\u0646\u0647\u06ce\u0646\u06cc \u06cc\u0627\u0646 \u0628\u06d5 \u062a\u06d5\u0631\u0632\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u060c \u062a\u0648\u0648\u0634\u062a\u0627\u0646 \u0628\u0628\u06ce\u060c \u062c\u06cc\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u063a\u06d5\u062f\u0631\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u06ce \u0642\u0695 \u062f\u06d5\u0628\u06ce \u0648 \u062a\u06ce\u062f\u0627 \u062f\u06d5\u0686\u06ce\u061f',
    '\u0647\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u062f\u06d5\u0646\u06ce\u0631\u06cc\u0646 \u067e\u06ce\u06cc \u062f\u06d5\u0633\u067e\u06ce\u0631\u06cc\u0646 \u0645\u0698\u062f\u06d5\u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0628\u06ce\u061b \u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u0695\u0648\u0627\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc \u0695\u06d5\u0686\u0627\u0648 \u06a9\u0631\u062f\u060c \u0646\u06d5 \u0647\u06cc\u0686 \u062a\u0631\u0633\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u0647\u06cc\u0686 \u062e\u06d5\u0645\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06ce\u062a\u06d5\u0628\u06d5\u0631.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0626\u06d5\u0698\u0645\u0627\u0631\u062f\u060c \u0644\u06d5 \u0633\u0632\u0627\u06cc \u0626\u06d5\u0645 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u06cc\u06d5\u060c \u06af\u0631\u0641\u062a\u0627\u0631\u06cc \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u0628\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u0645 \u0646\u06d5\u06af\u0648\u062a: \u06a9\u0644\u06cc\u0644 \u062f\u0627\u0631\u06cc \u06af\u06d5\u0646\u062c\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627 \u0645\u0646\u0645\u061b \u0646\u06d5\u0634\u0645 \u06af\u0648\u062a\u0648\u0648\u06d5: \u0644\u06d5 \u0646\u0627\u062f\u06cc\u0627\u0631\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u0645\u061b \u067e\u06ce\u0634\u0645 \u0646\u06d5\u06af\u0648\u062a\u0648\u0648\u0646: \u0641\u0631\u06cc\u0634\u062a\u06d5\u0645. \u062a\u06d5\u0646\u0647\u0627 \u0645\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u062f\u06d5\u06a9\u06d5\u0645 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u067e\u06ce\u06cc \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u0645. \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u06a9\u0648\u06ce\u0631 \u0648 \u0686\u0627\u0648\u0633\u0627\u063a \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u0646\u061f \u062f\u06d5\u06cc\u0633\u0627 \u0628\u06c6 \u0628\u06cc\u0631 \u0646\u0627\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061f',
    '\u0628\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u062a\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u062a\u0631\u0633\u06ce\u0646\u06d5\u060c \u06a9\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u06a9\u06c6 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0644\u06d5 \u0642\u0627\u067e\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646\u0646\u061b \u06a9\u06d5 \u0644\u06d5\u0648 \u0628\u06d5\u062f\u06d5\u0631 \u0646\u06d5 \u0647\u06cc\u0686 \u062f\u06c6\u0633\u062a\u06cc\u0627\u0646 \u06af\u06cc\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u06ce \u0648 \u0646\u06d5 \u062a\u06a9\u0627\u06a9\u0627\u0631\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0645\u06d5\u062a\u06c6\u0631\u06ce\u0646\u06d5 \u0648 \u062f\u06d5\u0631\u06cc\u0627\u0646 \u0645\u06d5\u06a9\u06d5 \u06a9\u06d5 \u06af\u0634 \u0634\u06d5\u0648\u06d5\u06a9\u06cc \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646 \u0647\u0627\u0646\u0627 \u062f\u06d5\u0628\u06d5\u0646\u06d5 \u0628\u06d5\u0631 \u062e\u0648\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0648 \u0628\u06c6 \u0695\u06d5\u0632\u0627\u06cc \u0626\u06d5\u0648 \u06a9\u06c6\u0634\u0634 \u062f\u06d5\u06a9\u06d5\u0646. \u062a\u06c6 \u0647\u06cc\u0686 \u06a9\u0627\u0631\u062a \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0646\u06d5\u062f\u0627\u0628\u06ce\u061b \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0647\u06cc\u0686 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631 \u062a\u06c6\u0648\u06d5 \u0646\u06cc\u06cc\u06d5. \u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u06d5\u0631\u06a9\u06d5\u06cc\u060c \u063a\u06d5\u062f\u0631\u06ce\u06a9\u06d5 \u0644\u06d5 \u062e\u06c6\u062a\u06cc \u062f\u06d5\u06a9\u06d5\u06cc.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u06af\u06d5\u0631\u06d5\u06a9\u0645\u0627\u0646\u06d5 \u0647\u06ce\u0646\u062f\u06ce \u0644\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u062a\u0627\u0642\u06cc \u0648\u06d5\u06a9\u06d5\u0646\u060c \u0647\u06d5\u062a\u0627 \u0628\u06ce\u0698\u0646: \u0686\u0644\u06c6\u0646 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0646\u0627\u0648\u0645\u0627\u0646\u062f\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc \u0648\u06d5\u0628\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u062e\u0633\u062a\u0648\u0648\u06d5\u061f \u0626\u0627\u062e\u06c6 \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648\u0627\u0646 \u0628\u0627\u0634\u062a\u0631 \u0634\u0648\u06a9\u0631\u0628\u0698\u06ce\u0631\u0627\u0646 \u0646\u0627\u0646\u0627\u0633\u06ce\u061f',
    '\u0647\u06d5\u0631\u06af\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06ce\u0646\u06d5\u0644\u0627\u062a \u06a9\u06d5 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0646\u060c \u067e\u06cc\u0627\u0646 \u0628\u06ce\u0698\u06d5: \u062f\u06b5\u0646\u06cc\u0627\u0628\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u062e\u06c6\u06cc \u0646\u0648\u0648\u0633\u06cc\u0648\u06d5 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc\u062a\u0627\u0646 \u0628\u062e\u0627\u061b \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5 \u0626\u06ce\u0648\u06d5 - \u0644\u06d5 \u0646\u06d5\u0632\u0627\u0646\u06cc \u062e\u06c6\u06cc - \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e\u06cc \u0644\u06ce \u0695\u0648\u0648\u062f\u0627 \u0648 \u067e\u0627\u0634\u0627\u0646 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0628\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u0628\u06a9\u0627\u060c \u0644\u06d5 \u06af\u0648\u0646\u0627\u062d\u06d5\u06a9\u0627\u0646\u06cc \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646\u06cc\u0627\u0646 \u062a\u06d5\u0648\u0627\u0648 \u0628\u06c6 \u0634\u06cc \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0645\u0627\u0646\u06d5\u0648\u06ce \u0695\u06ce\u0628\u0627\u0632\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0695\u0648\u0648\u0646 \u0628\u0628\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0695\u06ce\u0645 \u067e\u06ce \u0646\u06d5\u062f\u0631\u0627\u0648\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u06a9\u06d5\u0645\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u0628\u067e\u06d5\u0631\u0633\u062a\u0645 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u0646. \u0628\u06ce\u0698\u06d5: \u0642\u06d5\u062a \u0645\u0646 \u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0626\u06ce\u0648\u06d5 \u0646\u0627\u06a9\u06d5\u0645. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u062a\u0648\u0648 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0626\u06ce\u0648\u06d5 \u06a9\u06d5\u0648\u0645\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0695\u06ce\u06af\u06d5\u0645 \u06af\u0648\u0645 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0644\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0648\u06d5\u062f\u06d5\u0631 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0645.',
    '\u0628\u06ce\u0698\u06d5: \u0648\u0627 \u0645\u0646 \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u06d5\u0648\u06d5\u060c \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u067e\u062a\u06d5\u0648\u0645 \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u0644\u0627\u06cc \u0645\u0646\u062a\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627. \u0626\u06d5\u0648\u06d5\u0634 \u0626\u06ce\u0648\u06d5\u06cc \u0628\u06c6 \u0628\u06d5\u0644\u06d5\u0632\u0646\u060c \u0647\u06cc\u0686\u06cc \u0644\u06d5 \u062f\u06d5\u0633 \u0645\u0646\u062f\u0627 \u0646\u06cc\u06cc\u06d5. \u0641\u06d5\u0631\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0695\u0627\u0633\u062a\u06cc\u0648 \u0628\u06c6 \u0628\u0627\u0633 \u062f\u06d5\u06a9\u0627. \u0686\u0627\u06a9\u062a\u0631 \u0644\u06ce\u06a9 \u062c\u0648\u062f\u0627\u06a9\u06d5\u0631\u06d5\u0648\u06d5\u0634 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u062f\u0627\u0648\u0627\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0628\u06c6\u06cc \u0628\u06d5 \u067e\u06d5\u0644\u06d5\u0646 \u0628\u06d5 \u062f\u06d5\u0633 \u0645\u0646 \u0628\u0627\u060c \u06a9\u0627\u0631\u06cc \u0646\u06ce\u0648\u0627\u0646 \u0645\u0646 \u0648 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u0632\u0648\u0648\u062f\u0627 \u062f\u0648\u0627\u06cc\u06cc \u062f\u06d5\u0647\u0627\u062a. \u0647\u06d5\u0631 \u062e\u0648\u0627 \u0628\u0627\u0634\u062a\u0631 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u062f\u06d5\u0646\u0627\u0633\u06ce.',
    '\u0647\u06d5\u0645\u0648\u0648 \u06a9\u0644\u06cc\u0644\u06cc \u0646\u0647\u06ce\u0646\u06cc \u0648 \u0628\u06d5\u0631\u067e\u06cc\u0648\u0627\u0631\u0627\u0646 \u0648\u0627 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06d5\u061b \u0644\u06d5 \u062e\u06c6\u06cc \u0628\u06d5\u062f\u06d5\u0631 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce \u0626\u0627\u06af\u0627\u06cc \u0644\u06ce \u0646\u06cc\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0632\u0631\u06cc\u0627 \u0648 \u0628\u06d5\u0698\u062f\u0627\u06cc\u06d5 \u0628\u0627\u0634 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce\u061b \u0647\u06cc\u0686 \u06af\u06d5\u06b5\u0627\u06cc\u06d5\u06a9 \u0644\u06d5 \u0647\u06cc\u0686 \u0634\u0648\u06ce\u0646\u06ce \u0628\u06ce \u0632\u0627\u0646\u06cc\u0646\u06cc \u0626\u06d5\u0648 \u0646\u0627\u0648\u06d5\u0631\u06ce. \u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u0646\u06a9\u06d5 \u0648 \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u0627\u06cc\u06cc \u0632\u06d5\u0648\u06cc\u062f\u0627 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u062a\u06d5\u0695\u06d5\u060c \u06cc\u0627\u0646 \u0632\u0648\u0647\u0627\u06cc\u06d5\u060c \u06af\u0634\u062a\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u062f\u06d5\u0641\u062a\u06d5\u0631\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0628\u06d5 \u0634\u06d5\u0648 \u0647\u06d5\u0633\u062a\u0648\u0648 \u0644\u06ce \u062f\u06d5\u0633\u062a\u06ce\u0646\u06ce \u0648 \u0644\u06d5 \u0646\u0627\u0648 \u0695\u06c6\u0698\u0627 \u0686\u06cc\u062a\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5. \u062c\u0627 \u0628\u06d5 \u0695\u06c6\u0698 \u0695\u0627\u0648 \u062f\u06d5\u067e\u06d5\u0695\u06ce\u0646\u06ce\u060c \u0647\u06d5\u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648 \u0628\u06d5 \u0633\u06d5\u0631 \u062f\u06d5\u0686\u06ce. \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u0686\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u06cc\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u0686\u0648\u0648 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u062f\u0627\u0631 \u0628\u06d5 \u0633\u06d5\u0631 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc\u0627. \u067e\u0627\u0633\u06d5\u0648\u0627\u0646\u0627\u0646\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627. \u0647\u06d5\u0631\u06af\u0627 \u0648\u06d5\u062e\u062a\u06cc \u0645\u0631\u062f\u0646\u0648\u0648 \u062f\u06ce\u060c \u067e\u06ce\u0645\u06ce\u0631\u062f\u06cc \u062e\u0648\u0627 \u0644\u06d5 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627\u0646\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062f\u0631\u06ce\u063a \u0646\u0627\u06a9\u0627.',
    '\u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646\u06af\u06d5\u0695\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u060c \u06a9\u06d5 \u0633\u06d5\u0631\u0648\u06d5\u0631\u06cc \u0695\u0627\u0633\u062a\u06d5\u0642\u06cc\u0646\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646\u06d5. \u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0626\u06d5\u0648\u06d5 \u0648 \u0632\u0648\u0648\u062a\u0631 \u0644\u06d5 \u0647\u06d5\u0631 \u062d\u06cc\u0633\u0627\u0628\u06af\u06d5\u0631\u06ce \u0628\u06d5 \u062d\u06cc\u0633\u0627\u0628\u06cc \u06af\u0634 \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u0628\u06ce\u0698\u06d5: \u06a9\u06ce \u0695\u0632\u06af\u0627\u0631\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u06cc \u0628\u06d5\u0698 \u0648 \u062f\u06d5\u0631\u06cc\u0627\u061f \u06a9\u06d5 \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0648 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0644\u06d5 \u0628\u06d5\u0631\u06cc \u062f\u06d5\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0626\u06d5\u0645\u062c\u0627\u0631\u06d5\u0634 \u0695\u0632\u06af\u0627\u0631\u0645\u0627\u0646 \u0628\u06a9\u0627\u060c \u0647\u06d5\u0631 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u0628\u0698\u06ce\u0631\u06cc\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u062e\u0648\u0627 \u0695\u0632\u06af\u0627\u0631\u0648\u0648 \u062f\u06d5\u06a9\u0627 \u0644\u06d5\u0648 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5 \u0648 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u06cc \u062f\u06cc\u0634\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u0633\u06d5\u0631\u0648\u0648\u062a\u0627\u0646\u06d5\u0648\u06d5 \u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u0698\u06ce\u0631\u067e\u0627\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0626\u0627\u0632\u0627\u0631\u062a\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0695\u06ce \u0628\u06a9\u0627\u061b \u06cc\u0627\u0646 \u0628\u062a\u0627\u0646\u06a9\u0627\u062a\u06d5 \u0686\u06d5\u0646\u062f \u062f\u06d5\u0633\u062a\u06d5\u0648 \u062a\u06ce\u06a9\u062a\u0627\u0646 \u0628\u06d5\u0631\u062f\u0627 \u0648 \u0626\u0627\u0632\u0627\u0631 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u0628\u0686\u06ce\u0698\u0646. \u062f\u06d5 \u0628\u0695\u0648\u0627\u0646\u06d5 \u0686\u06c6\u0646 \u0626\u06ce\u0645\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 - \u062c\u06c6\u0631\u06d5 \u062c\u06c6\u0631\u06d5 - \u0628\u06d5\u0648 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062a\u06ce\u0628\u06af\u06d5\u0646.',
    '\u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062a\u06c6 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06cc \u0648\u0627 \u0695\u0627\u0633\u062a\u06cc\u0634\u06cc\u0627\u0646 \u067e\u06ce \u062f\u0631\u06c6\u06cc\u06d5. \u0628\u06ce\u0698\u06d5: \u062e\u06c6 \u0645\u0646 \u0646\u06d5\u0628\u0648\u0648\u0645\u06d5 \u0647\u06d5\u0645\u06d5\u06a9\u0627\u0631\u06d5\u062a\u0627\u0646.',
    '\u0632\u06c6\u0631 \u0628\u06d5 \u0632\u0648\u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u062f\u06d5\u0628\u0646 \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u0627\u0633 \u0648 \u062e\u06d5\u0628\u06d5\u0631\u06ce\u06a9 \u062c\u06ce\u06af\u06d5\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u062e\u0631\u0627\u067e \u062f\u06d5\u062f\u0648\u06ce\u0646\u060c \u062a\u06c6 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u0628\u06af\u06ce\u0695\u06d5 \u062a\u0627 \u0628\u0627\u0631\u06cc \u0642\u0633\u06d5 \u062f\u06d5\u06af\u06c6\u0695\u0646 \u0648 \u0642\u0633\u06d5 \u0644\u06d5 \u0634\u062a\u06ce\u06a9\u06cc \u062a\u0631 \u062f\u06d5\u06a9\u06d5\u0646. \u06af\u06d5\u0631 \u0648\u0627 \u0695\u0648\u0648\u06cc\u062f\u0627 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631\u06cc \u0628\u0631\u062f\u06cc\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0631\u06af\u0627 \u0648\u06d5\u0628\u06cc\u0631\u062a \u0647\u0627\u062a\u06d5\u0648\u06d5 \u0626\u06cc\u062a\u0631 \u0648\u06d5\u0644 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u0695\u06c6\u0645\u06d5\u0646\u06cc\u0634\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u06ce \u062a\u0631\u0633\u0646 \u0648 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0646\u060c \u0647\u06cc\u0686 \u0626\u0648\u0628\u0627\u06b5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0633\u062a\u06c6 \u0646\u06cc\u06cc\u06d5. \u0628\u06d5\u06b5\u0627\u0645 \u062f\u06d5\u0628\u06ce \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc [\u0628\u06c6 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5\u06a9\u0627\u0646] \u0628\u06a9\u06d5\u0646\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646.',
    '\u062a\u06c6 \u0648\u0627\u0632\u0628\u0647\u06ce\u0646\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u06af\u06d5\u0645\u06d5 \u0648 \u06af\u0627\u06b5\u062a\u06d5 \u06af\u0631\u062a\u0648\u0648\u06d5 \u0648 \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627 \u062a\u06d5\u0648\u0627\u0648\u06ce\u06a9 \u0644\u06d5 \u062e\u0634\u062a\u06d5\u06cc \u0628\u0631\u062f\u0648\u0648\u0646. \u0628\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0628\u06cc\u0631\u06cc\u0627\u0646 \u062e\u06d5\u0648\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9 \u0628\u06a9\u06d5\u0648\u06ce\u062a\u06d5 \u0632\u06cc\u0646\u062f\u0627\u0646\u06cc \u0628\u06ce \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u06cc - \u0644\u06d5 \u062e\u0648\u0627\u0628\u06d5\u062f\u06d5\u0631 - \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc \u0646\u0627\u06cc\u06d5 \u0648 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc\u0634 \u062a\u06a9\u0627\u06cc \u0628\u06c6 \u0646\u0627\u06a9\u0627. \u0647\u06d5\u0631\u0686\u06cc \u0645\u0627\u06b5\u06cc \u062f\u0646\u06cc\u0627\u0634 \u0647\u06d5\u0628\u06ce \u0648 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648\u0628\u06ce \u0644\u06d5 \u0642\u06d5\u0631\u06d5\u0628\u0648\u0648\u06cc \u062e\u06c6\u06cc\u062f\u0627 \u0628\u06cc\u062f\u0627\u060c \u0642\u0628\u0648\u0648\u06b5 \u0646\u0627\u06a9\u0631\u06ce. \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0633\u06c6\u0646\u06af\u06d5\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u0648 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u06d5\u060c \u0644\u06d5 \u062f\u0648\u0698\u06d5\u0647\u06ce \u062f\u06d5\u0631\u0646\u0627\u06a9\u06d5\u0648\u0646 \u0648 \u0626\u0627\u0648\u06cc \u06a9\u0648\u06b5\u06cc\u0648 \u062f\u06d5\u062e\u06c6\u0646\u06d5\u0648\u06d5 \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 - \u0644\u06d5 \u062e\u0648\u0627 \u0628\u06d5\u062f\u06d5\u0631 - \u0647\u0627\u0648\u0627\u0631 \u0628\u06c6 \u0634\u062a\u0627\u0646\u06ce\u06a9 \u0628\u06d5\u0631\u06cc\u0646 \u0646\u06d5 \u0632\u06d5\u0631\u06d5\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06ce \u062f\u06d5\u062f\u06d5\u0646\u060c \u0646\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u0628\u06c6\u0645\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06af\u06d5 \u0628\u0648\u0648\u06cc\u0646\u060c \u0633\u06d5\u0631\u0644\u06d5 \u0646\u0648\u06ce \u067e\u0627\u0634\u06af\u06d5\u0632 \u0628\u06cc\u0646\u06d5\u0648\u06d5\u061f \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06d5\u0645\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u062f\u06ce \u06a9\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u062e\u06d5\u06b5\u06d5\u062a\u0627\u0646\u062f\u0648\u0648\u06cc\u06d5 \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u0632\u06d5\u0648\u06cc \u0633\u06d5\u0631\u06af\u06d5\u0631\u062f\u0627\u0646\u06d5. \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0647\u06d5\u0648\u0627\u06b5\u06cc \u0648\u0627\u06cc \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u067e\u06ce\u06cc \u0626\u06ce\u0698\u0646: \u0648\u06d5\u0631\u06d5 \u0644\u0627\u0645\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u062a \u0634\u0627\u0631\u06d5\u0632\u0627 \u062f\u06d5\u06a9\u06d5\u06cc\u0646. \u0628\u06ce\u0698\u06d5: \u0695\u06ce\u06cc \u0695\u0627\u0633\u062a \u0647\u06d5\u0631 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0648\u0648\u0628\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0626\u06ce\u0645\u06d5 \u062f\u0631\u0627\u0648\u06d5 \u062e\u06c6 \u0628\u062f\u06d5\u06cc\u0646\u06d5 \u062f\u06d5\u0633 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0695\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0641\u06d5\u0631\u0645\u0648\u0648\u0634\u06cc\u06d5 \u062f\u06d5\u0628\u06ce \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646\u061b \u062a\u0631\u0633\u06cc\u0634\u0648\u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce\u061b \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u062a\u0627\u0646 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u062e\u0695 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0628\u06c6 \u0695\u0627\u0633\u062a\u06cc\u060c \u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u0648\u06d5. \u0647\u06d5\u0631 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u0628\u06c6 \u062f\u0631\u0648\u0633\u062a \u0628\u0648\u0648\u0646\u06cc \u0647\u06d5\u0631 \u0634\u062a\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u062f\u0627: \u0628\u0628\u06d5! \u062f\u06d5\u0628\u06ce. \u0642\u0633\u06d5\u06cc \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0644\u0627\u0631\u06cc \u0646\u06cc\u06cc\u06d5 \u0648 \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0641\u0648\u0648 \u0628\u06d5 \u06a9\u06d5\u06b5\u06d5\u0634\u0627\u062e\u062f\u0627 \u062f\u06d5\u06a9\u0631\u06ce\u060c \u062a\u06d5\u0646\u06cc\u0627 \u062e\u06c6\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u062f\u0627\u0631\u06d5 \u0648 \u062f\u06cc\u0627\u0631 \u0648 \u0628\u06d5\u0631\u067e\u06cc\u0648\u0627\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0628\u06cc\u0631 \u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634 \u0628\u06a9\u06d5\u0631\u06d5\u0648\u06d5 \u06a9\u06d5 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0628\u06d5 \u0626\u0627\u0632\u06d5\u0631\u06cc \u0626\u0627\u067e\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0626\u06d5\u062a\u06c6 \u0628\u06c6\u0686\u06cc \u0626\u06d5\u0648 \u0628\u062a\u0627\u0646\u06d5 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u06cc\u061f \u0626\u06d5\u0645\u0646 \u062a\u06c6\u0634\u0648\u0648 \u06af\u06d5\u0644\u06d5\u06a9\u06d5\u0634\u062a \u062f\u06d5\u0628\u06cc\u0646\u0645 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u0648 \u062f\u0627\u0648\u06d5.',
    '[\u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u0634 \u0628\u0648\u0648 \u06a9\u06d5] \u0626\u06ce\u0645\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645\u0646\u062f\u0627 \u062f\u0627\u0645 \u0648 \u062f\u06d5\u0632\u06af\u0627\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0633\u06d5\u0631 \u0628\u06d5 \u06a9\u06ce\u06cc\u06d5\u060c \u062a\u0627 \u0644\u06d5 \u0695\u06ce\u0632\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5 \u0695\u0627\u0633\u062a\u06d5\u06a9\u0627\u0646 \u0628\u06ce.',
    '\u06a9\u06d5 \u0634\u06d5\u0648\u06cc \u0628\u06d5 \u0633\u06d5\u0631\u062f\u0627\u0647\u0627\u062a \u0648 \u062a\u0627\u0631\u06cc\u06a9 \u062f\u0627\u0647\u0627\u062a\u060c \u0626\u06d5\u0633\u062a\u06ce\u0631\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06d5\u062f\u06cc \u06a9\u0631\u062f. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u06d5. \u0647\u06d5\u0631\u06a9\u06d5 \u0644\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u0648 \u0626\u0627\u0648\u0627 \u0628\u0648\u0648\u060c \u06af\u0648\u062a\u06cc: \u0647\u06d5\u0631\u0686\u06cc \u0626\u0627\u0648\u0627 \u0628\u0628\u06ce \u062e\u06c6\u0634\u0645 \u0646\u0627\u0648\u06ce.',
    '\u0648\u06d5\u062e\u062a\u06ce \u06a9\u06d5 \u062f\u06cc\u062a\u06cc \u0645\u0627\u0646\u06af \u0647\u06d5\u06b5\u0627\u062a\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u06d5. \u06a9\u06d5 \u0626\u06d5\u0648\u06cc\u0634 \u0644\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u0648 \u0646\u06d5\u0645\u0627\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0695\u0627\u0633\u062a\u06d5\u0642\u0627\u0646\u06cc\u0645 \u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc\u0645 \u067e\u06ce \u0646\u06d5\u06b5\u06ce\u060c \u0645\u0646\u06cc\u0634 \u0644\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0645.',
    '\u06a9\u0627\u062a\u06ce \u062f\u06cc\u062a\u06cc \u0648\u0627 \u062e\u06c6\u0631 \u0644\u06d5 \u06a9\u06d5\u0644 \u0633\u06d5\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06cc \u062a\u0631 \u0645\u06d5\u0632\u0646\u062a\u0631\u06d5\u060c \u0695\u06d5\u0646\u06af\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u0645\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5 \u0628\u06ce. \u06a9\u06d5 \u062e\u06c6\u0631\u06cc\u0634 \u06a9\u06d5\u0648\u062a\u06d5 \u062e\u06c6\u0631\u0646\u0634\u06cc\u0646\u060c \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0645\u0646 \u0644\u06d5\u0648 \u06a9\u0627\u0631\u06d5 \u0648 \u06a9\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627\u0646\u06cc \u0645\u0646 \u062f\u06d5\u0646\u0627\u0633\u0646\u060c \u062a\u06d5\u06a9\u06cc\u0645\u06d5\u0648\u06d5.',
    '\u0648\u0627\u0645\u0646 - \u0628\u06d5 \u062f\u06b5\u067e\u0627\u06a9\u06cc - \u0695\u0648\u0648\u0645 \u06a9\u0631\u062f\u06d5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u06a9\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u062f\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06cc\u0645 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0645 \u0628\u0627\u0633\u06d5 \u0634\u06d5\u0695 \u0648 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u0641\u0631\u06c6\u0634\u062a. \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u0686\u06c6\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc\u06ce\u06a9\u06d5\u0648\u06d5 \u06a9\u06ce\u0634\u06d5 \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u06a9\u0631\u062f\u0648\u0648\u0645 \u0648 \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u0645 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u06d5\u0646\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0626\u06d5\u0648. \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u062d\u06d5\u0632\u06ce \u0628\u06a9\u0627 \u0647\u06d5\u0631\u0648\u0627 \u062f\u06d5\u0628\u06ce \u0648 \u0632\u0627\u0646\u0633\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0645\u0646 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u062f\u0627\u06af\u0631\u062a\u0648\u0648\u06d5. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0646\u0627\u0645\u06ce\u0646\u0646\u061f',
    '\u062c\u0627 \u0686\u06c6\u0646 \u062f\u06d5\u0628\u06ce \u0644\u06d5\u0648 \u0628\u062a\u0627\u0646\u06d5\u06cc - \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u0646 - \u062a\u0631\u0633\u0645 \u0647\u06d5\u0628\u06ce\u061f \u06a9\u06d5\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u062a\u0631\u0633\u0648\u0648 \u0646\u06cc\u06cc\u06d5 \u0648 \u0626\u06d5\u0648 \u0628\u062a\u0627\u0646\u06d5 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0632\u0627\u0646\u0646 \u0648 \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u062a\u06d5\u0648\u062a\u0627\u0646 \u0628\u06c6 \u0646\u06d5\u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0632\u0627\u0646\u06cc\u0646 \u0647\u06cc\u0686 \u0628\u06d5\u0634\u062a\u0627\u0646 \u0647\u06d5\u0628\u0627\u060c \u062f\u06d5\u062a\u0627\u0646\u0632\u0627\u0646\u06cc \u0644\u06d5 \u0645\u0646 \u0648 \u0644\u06d5\u0646\u06af\u06c6 \u06a9\u0627\u0645\u06d5\u0644\u0627\u0645\u0627\u0646 \u067e\u062a\u0631 \u0634\u06cc\u0627\u0648\u06cc \u0646\u06d5\u062a\u0631\u0633\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0628\u0695\u0648\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u062a\u06ce\u06a9\u06d5\u06b5\u06cc \u0646\u0627\u062d\u06d5\u0642\u06cc \u0646\u06d5\u06a9\u0631\u062f\u060c \u062f\u06b5\u0646\u06cc\u0627\u06cc\u06cc \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u0646.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0686\u06d5\u0646\u062f \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u062f\u0627\u0628\u0648\u0648\u0645\u0627\u0646\u06d5 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645\u060c \u06a9\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u0647\u06c6\u0632\u06cc \u062e\u06c6\u06cc \u0695\u0627\u0628\u0648\u0648\u06d5\u0633\u062a\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0626\u06ce\u0645\u06d5 \u062d\u06d5\u0632 \u0628\u06a9\u06d5\u06cc\u0646 \u067e\u0644\u06d5\u06cc \u0695\u06ce\u0632\u06cc \u0628\u0627\u06b5\u0627 \u062f\u06d5\u0628\u06d5\u06cc\u0646\u061b \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06cc\u0633\u062d\u0627\u0642 \u0648 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0628\u06d5\u062e\u0634\u06cc \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u0645\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u06cc \u062e\u06c6\u0645\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u060c \u0644\u06d5 \u067e\u06ce\u0634 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u0646\u0648\u0648\u062d\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u0645\u0627\u0646 \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646\u062f\u0627 \u0648 \u0644\u06d5 \u0646\u0627\u0648 \u0632\u0627\u0631\u0648\u0632\u06ce\u0686\u06cc \u0626\u06d5\u0648\u062f\u0627 \u062f\u0627\u0648\u062f \u0648 \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0634 \u0648 \u0626\u06d5\u06cc\u06cc\u0648\u0628 \u0648 \u06cc\u0648\u0633\u0641 \u0648 \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u0627\u0695\u0648\u0648\u0646\u0645\u0627\u0646 \u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u06ce\u06af\u0627. \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u0628\u0627\u0634 \u0628\u06d5 \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0632\u06d5\u06a9\u06d5\u0631\u06cc\u06cc\u0627\u0634 \u0648 \u06cc\u06d5\u062d\u06cc\u0627 \u0648 \u0639\u06cc\u0633\u0627\u060c \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0644\u06cc\u0627\u0633\u061b \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0644\u06d5 \u0645\u06ce\u0631\u062e\u0627\u0633\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644 \u0648 \u0626\u06d5\u0644\u06cc\u06d5\u0633\u06d5\u0639 \u0648 \u06cc\u0648\u0648\u0646\u0633 \u0648 \u0644\u0648\u0648\u062a\u06cc\u0634 \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627\u061b \u0633\u06d5\u0631 \u0644\u06d5\u0628\u06d5\u0631\u06cc\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646\u06ce\u060c \u0632\u06ce\u062f\u06d5\u062a\u0631 \u0642\u06d5\u062f\u0631 \u0644\u06ce\u0646\u0627\u0648\u06d5.',
    '\u0644\u06d5 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5 \u0648 \u0639\u06d5\u0648\u0644\u0627\u062f \u0648 \u0628\u0631\u0627\u06cc\u0627\u0646\u06cc\u0627\u0646\u060c \u0628\u0695\u06ce\u06a9\u06cc\u0627\u0646\u0645\u0627\u0646 \u0633\u06d5\u0631\u062e\u0633\u062a\u0646 \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u06cc \u0695\u0627\u0633\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646\u061b',
    '\u0626\u06d5\u0645 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc \u06a9\u0631\u062f\u0646\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5. \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce\u060c \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u0695\u06ce\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u0648\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u0628\u0627\u060c \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0633\u06d5\u0631\u067e\u0627\u06a9\u06cc \u0628\u06d5 \u0641\u06ce\u0695\u06c6 \u062f\u06d5\u0686\u0648\u0648.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u062e\u06c6\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627\u0648\u0646 \u0648 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u0645\u0627\u0646 \u0695\u0627\u0647\u06ce\u0646\u0627\u0646 \u0648 \u06a9\u0631\u062f\u0645\u0627\u0646\u0646 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u061b \u06af\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06ce\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0648\u0646\u06d5 \u062d\u0627\u0634\u0627\u06cc\u06d5\u060c \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u0648\u0627 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce\u06cc\u0627\u0646 \u0647\u06d5\u0628\u06ce \u0648 \u0647\u06cc\u0686\u06af\u0627 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0646\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0695\u06ce\u0646\u0645\u0648\u0648\u0646\u06cc \u06a9\u0631\u062f\u0646\u061b \u062a\u06c6\u0634 \u0647\u06d5\u0631 \u0644\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648\u0627\u0646 \u0628\u0695\u06c6! \u0628\u06ce\u0698\u06d5: \u062e\u06c6 \u0645\u0646 \u0647\u06cc\u0686 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u0645 \u0644\u06ce\u0648 \u0646\u0627\u0648\u06ce\u060c \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u06cc\u06d5\u06a9\u06d5 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u06cc \u0648\u0627 \u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc \u0628\u06c6 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce\u06a9 \u0646\u06d5\u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u0648\u06d5\u06a9 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u062e\u0648\u0627 \u0628\u0646\u0627\u0633\u0631\u06ce\u060c \u0646\u06d5\u06cc\u0627\u0646\u0646\u0627\u0633\u06cc. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u06a9\u06ce \u0626\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5 \u06a9\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0647\u06ce\u0646\u0627\u06cc \u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc \u0628\u0648\u0648 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645\u061f \u06a9\u062a\u06ce\u0628\u06ce \u06a9\u06d5 \u067e\u06d5\u0695\u06d5\u067e\u06d5\u0695\u06d5\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u06d5\u0634\u06cc \u06a9\u06d5\u0645\u06cc \u0646\u06cc\u0634\u0627\u0646\u06cc \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631\u06cc \u062f\u06d5\u0634\u0627\u0631\u0646\u06d5\u0648\u06d5! \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5 \u0634\u062a\u06af\u06d5\u0644\u06ce\u06a9\u062a\u0627\u0646 \u067e\u06ce \u0641\u06ce\u0631 \u06a9\u0631\u0627 \u06a9\u06d5 \u0646\u06d5 \u062e\u06c6 \u0646\u06d5 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u062a\u0627\u0646 \u0646\u06d5\u0648 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646. \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631\u062e\u0648\u062f\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5. \u062a\u06c6 \u0626\u06cc\u062a\u0631 \u0648\u0627\u0632\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06ce\u0646\u06d5 \u0628\u0627 \u0628\u06d5 \u06af\u0627\u06b5\u062a\u06d5 \u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5 \u062e\u06c6 \u062e\u06d5\u0631\u06cc\u06a9 \u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0628\u06d5 \u062e\u06d5\u06b5\u0627\u062a \u0628\u06c6 \u062a\u06c6 \u0646\u0627\u0631\u062f \u0648 \u0645\u06d5\u0645\u0628\u0627\u0631\u06d5\u06a9\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc\u0634\u06cc \u0644\u06d5 \u0644\u0627 \u0695\u0627\u0633\u062a\u06d5 \u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u0646\u061b \u062a\u0627 \u062a\u06c6 \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u0645\u06d5\u06a9\u06a9\u06d5 \u0648 \u062f\u06d5\u0648\u0631\u0648\u0628\u06d5\u0631\u06cc \u0628\u062a\u0631\u0633\u06ce\u0646\u06cc \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a \u0647\u06d5\u06cc\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u067e\u06ce \u062f\u06ce\u0646\u0646 \u0648 \u0647\u06c6\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u0648\u06ce\u0698\u06d5\u06a9\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0647\u06d5\u06cc\u06d5.',
    '\u06a9\u06ce \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5 \u062f\u0631\u06c6 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06a9\u0627\u061f \u06cc\u0627 \u0628\u06ce\u0698\u06ce \u0645\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u0645 \u067e\u06ce \u0695\u0627\u06af\u06d5\u06cc\u06d5\u0646\u0631\u0627\u0648\u06d5\u061b \u06a9\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0647\u06cc\u0686 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634\u06cc \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06c6 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u06d5\u061b \u06cc\u0627\u0646 \u0628\u06ce\u0698\u06ce \u0645\u0646\u06cc\u0634 \u0644\u06d5 \u0648\u06ce\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u060c \u062f\u06d5\u062a\u0648\u0627\u0646\u0645 \u0628\u06c6\u062a\u0627\u0646 \u0628\u0646\u06ce\u0631\u0645\u061f \u0628\u0631\u06cc\u0627 \u0648\u06d5\u062e\u062a\u06cc \u0626\u0627\u0648\u06cc\u0644\u06a9\u06d5\u062f\u0627\u0646 \u062a\u06c6 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u062a \u0628\u062f\u06cc\u0628\u0627 \u06a9\u06d5 \u0641\u0631\u06cc\u0634\u062a\u06d5 \u0644\u06ce\u06cc\u0627\u0646 \u0695\u0627\u0633\u0627\u0648\u06d5\u060c \u06a9\u06d5: \u06af\u06cc\u0627\u0646\u0648\u0648 \u0644\u06d5 \u0642\u0627\u06b5\u0628 \u062f\u06d5\u0631\u062e\u06d5\u0646\u061b \u0648\u0627 \u0626\u06d5\u0645\u0695\u06c6 \u0644\u06d5 \u062a\u06c6\u06b5\u06d5\u06cc \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u062f\u0631\u06c6\u062a\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a \u0648 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062d\u0627\u0634\u0627\u0648 \u062f\u06d5\u06a9\u0631\u062f\u060c \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u06d5\u0648\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u062a\u0627\u06a9 \u062a\u0627\u06a9\u06d5 \u0648 \u0628\u06d5 \u062a\u06d5\u0646\u06cc\u0627 \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627\u060c \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0647\u06d5\u0631\u0648\u0627\u0634 \u0647\u0627\u062a\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0644\u0627\u0645\u0627\u0646. \u0647\u06d5\u0631\u0686\u06cc\u0634 \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0628\u0648\u0648\u0646\u060c \u0644\u06d5 \u062f\u0648\u0627\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06d5\u062c\u06ce \u0645\u0627. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0646\u0627\u0628\u06cc\u0646\u0646 \u06a9\u06d5 \u0644\u0627\u062a\u0627\u0646 \u0648\u0627\u0628\u0648\u0648 \u062a\u06a9\u0627\u06a9\u0627\u0631\u0646 \u0648 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u0627\u062a\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0633\u062a\u0627 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u062c\u06cc\u0627 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0686\u06cc \u0628\u06d5 \u062e\u06d5\u06cc\u0627\u06b5 \u0633\u0627\u0632\u0648\u0648 \u062f\u0627\u0628\u0648\u0648\u060c \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0644\u06ce \u0628\u06d5 \u0647\u06cc\u0686\u06ce \u0686\u0648\u0648.',
    '\u062e\u0648\u062f\u0627 \u062f\u0627\u0646\u06d5 \u0648 \u062f\u06d5\u0646\u06a9\u06cc \u0645\u06cc\u0648\u06d5 \u062f\u06d5\u0642\u06d5\u06b5\u06ce\u0634\u06ce. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648 \u0644\u06d5 \u0645\u0631\u062f\u0648\u0648 \u062f\u06d5\u0631\u062f\u06d5\u062e\u0627 \u0648 \u0645\u0631\u062f\u0648\u0648 \u0644\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u0631\u062f\u06ce\u0646\u06ce. \u0626\u06d5\u0645\u06d5 \u062e\u0648\u0627\u06cc\u06d5\u060c \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0686\u06c6\u0646 [\u0644\u06d5 \u0695\u06ce\u06af\u0627] \u06a9\u0644\u0627 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u061f',
    '\u062a\u06ce\u06a9\u0648\u06b5\u06cc \u0634\u06d5\u0648\u06af\u0627\u0631 \u0644\u0627 \u062f\u06d5\u062f\u0627 \u0648 \u0633\u067e\u06ce\u062f\u06d5 \u0648 \u0628\u06c6 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0695\u0648\u0648. \u0634\u06d5\u0648\u06cc \u0628\u06c6 \u0626\u06c6\u0642\u0631\u06d5 \u06af\u0631\u062a\u0646 \u0648 \u062e\u06c6\u0631 \u0648 \u0647\u06d5\u06cc\u06a4\u06cc \u0628\u06c6 \u0698\u0645\u0627\u0631\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0633\u0627\u0632\u062f\u0627. \u062e\u0648\u062f\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0632\u0627\u0646\u0627 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0626\u06d5\u0633\u062a\u06ce\u0631\u06d5\u06cc \u0628\u06c6 \u062e\u0633\u062a\u0648\u0648\u0646\u06d5 \u0695\u0648\u0648\u061b \u062a\u0627 \u0628\u06d5\u0648\u0627\u0646\u06d5 - \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u06cc \u0628\u06d5\u0698 \u0648 \u0632\u0631\u06cc\u0627 - \u0695\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u0646. \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u0632\u0627\u0646\u0627\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0695\u0648\u0648\u0646\u06cc \u0634\u06cc \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5 \u062a\u0627\u0642\u06d5\u06a9\u06d5\u0633\u06ce\u06a9 \u062e\u0633\u062a\u06d5\u0648\u06d5\u061b \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9 \u06a9\u06c6\u06cc \u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u0627\u06cc \u0645\u06d5\u0632\u0631\u0627\u0646\u062f\u0648\u0648\u0646. \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u06ce \u062f\u06d5\u06af\u06d5\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0695\u0648\u0648\u0646\u06cc \u0634\u06cc \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0631\u0627\u0646\u062f\u060c \u0647\u06d5\u0645\u0648\u0648 \u0695\u0648\u0648\u06d5\u06a9\u0645\u0627\u0646 \u067e\u06ce \u0695\u0648\u0648\u0627\u0646\u062f\u060c \u0647\u06d5\u062a\u0627 \u0644\u06d5 \u0633\u06d5\u0648\u0632\u06d5 \u06af\u06cc\u0627\u06a9\u06d5\u0648\u06d5 \u062f\u06d5\u0646\u06a9 \u0648 \u06a9\u06d5\u0644\u0648\u0648\u06cc \u0633\u0648\u0627\u0631\u06cc \u06cc\u06d5\u06a9 \u0648 \u0644\u06d5 \u06a9\u06b5\u0648\u06a9\u06cc \u062f\u0627\u0631\u062e\u0648\u0631\u0645\u0627\u0648\u06d5 \u0647\u06ce\u0634\u0648\u0648\u06cc \u067e\u0695 \u0648 \u0634\u06c6\u0695\u06d5\u0648\u06d5 \u0628\u06c6 \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u0644\u06d5 \u0695\u06d5\u0632 \u0648 \u0632\u06d5\u06cc\u062a\u0648\u0648\u0646 \u0648 \u0628\u0646\u06d5\u0647\u06d5\u0646\u0627\u0631\u06cc \u0647\u0627\u0648\u0648\u06ce\u0646\u06d5 \u0648 \u0644\u06d5 \u06cc\u06d5\u06a9 \u062c\u06cc\u0627\u0645\u0627\u0646 \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627. \u0648\u06d5\u062e\u062a\u06ce \u0645\u06cc\u0648\u06d5\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u06af\u0627 \u0633\u06d5\u06cc\u0631\u06cc \u0628\u06a9\u06d5\u0646\u060c \u06a9\u06d5 \u0644\u06d5\u0648\u0627\u0646\u062f\u0627 \u06af\u06d5\u0644\u06ce\u06a9 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0646 \u0628\u06c6 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u062c\u0646\u06c6\u06a9\u0627\u0646 \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u0648\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u061b \u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646. \u0644\u06d5\u0648 \u0646\u06d5\u0632\u0627\u0646\u06cc \u06a9\u06d5 \u0647\u06d5\u06cc\u0627\u0646\u06d5\u060c \u062e\u06d5\u06cc\u0627\u06b5 \u062f\u06d5\u06a9\u06d5\u0646 \u062f\u06d5\u0628\u06ce \u06a9\u06c6\u0695 \u0648 \u06a9\u06cc\u0698\u06cc \u0647\u06d5\u0628\u0646. \u062e\u0648\u062f\u0627 \u067e\u0627\u06a9 \u0648 \u0628\u06d5\u0631\u0632\u062a\u0631\u06d5 \u0644\u06d5\u0648 \u0628\u0627\u0633\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646.',
    '\u062f\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u061b \u0686\u06c6\u0646 \u062f\u06d5\u0628\u06ce \u0639\u06d5\u0648\u0644\u0627\u062f\u06cc \u0647\u06d5\u0628\u06ce\u061f \u06a9\u06d5 \u0647\u06cc\u0686 \u0698\u0646\u06cc \u0646\u06d5\u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0647\u06d5\u06cc\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0633\u06a9\u0631\u062f\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0648 \u0626\u0627\u06af\u0627\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0647\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u062f\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0628\u06ce\u061b \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc\u0634 \u0626\u06d5\u0648 \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5. \u0644\u06d5 \u0633\u06d5\u0631\u062a\u0627\u0646\u06d5 \u0639\u06d5\u0628\u062f\u0627\u06cc\u06d5\u062a\u06cc \u0626\u06d5\u0648 \u0628\u06a9\u06d5\u0646 \u0648 \u0633\u06d5\u0631\u067e\u06d5\u0631\u0633\u062a\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0626\u06d5\u0648 \u0646\u0627\u06cc\u06d5\u062a\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u0648\u0627\u0646 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06cc\u0646\u06d5\u0631\u06ce\u06a9 \u062f\u06d5\u0628\u06cc\u0646\u06ce \u0648 \u06a9\u0627\u0631\u0646\u0627\u0633\u06a9 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648\u0695\u0627 \u0686\u0627\u0648\u06cc \u062f\u06b5\u062a\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06d5 \u0686\u0627\u0648\u06cc \u062f\u06b5 \u0634\u062a \u0628\u0628\u06cc\u0646\u06ce\u060c \u0628\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u062a\u06d5\u0648\u0627\u0648 \u062f\u06d5\u0628\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0686\u0627\u0648\u06cc \u062f\u06b5\u06cc \u0646\u0627\u0628\u06cc\u0646\u0627\u06cc\u06d5\u060c \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u06d5 \u0648 \u0645\u0646 \u06a9\u06ce\u0634\u06a9 \u06af\u0631\u06cc \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u0645.',
    '\u0626\u06ce\u0645\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0626\u0627\u0648\u0627 \u062c\u06c6\u0631\u0627\u0648\u062c\u06c6\u0631 \u0634\u06cc \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u061b \u0647\u06d5\u062a\u0627 \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u0627 \u0646\u06d5\u06cc\u0698\u0646 \u062e\u0648\u06d5\u0646\u06d5\u0648\u0627\u0631\u06cc! \u0647\u06d5\u062a\u0627 \u0695\u0648\u0648\u0646\u06cc\u0634\u06cc\u0627\u0646 \u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06ce \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648\u0695\u0627 \u0628\u06c6\u062a \u062f\u06ce\u060c \u067e\u06ce\u06a9\u06cc \u0628\u06ce\u0646\u06d5\u061b \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u06a9\u06d5\u0633 \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646\u06ce \u0646\u0627\u0634\u06ce. \u062a\u06c6 \u0648\u0627\u0632\u062a \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06ce\u0646\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627\u060c \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u0627\u0646 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f. \u0626\u06ce\u0645\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u06c6\u062a\u06d5 \u067e\u0627\u0633\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0646\u06d5\u0628\u0648\u0648\u06cc\u062a\u06d5 \u0647\u06d5\u0645\u06d5\u06a9\u0627\u0631\u06d5\u06cc\u0627\u0646.',
    '\u0642\u0633\u06d5\u06cc \u062e\u0631\u0627\u067e \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u062f\u0627\u0648\u0627 [\u06cc \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc] \u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u06d5\u0632\u0627\u0646\u0646\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0644\u06d5\u0628\u06d5\u0631 \u062f\u0698\u0627\u06cc\u06d5\u062a\u06cc \u062e\u06c6\u06cc\u0627\u0646\u060c \u062e\u0631\u0627\u067e \u0644\u06d5 \u0645\u06d5\u0695 \u062e\u0648\u062f\u0627 \u0628\u062f\u0648\u06ce\u0646. \u0626\u0627\u06a9\u0627\u0631\u06cc \u0647\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648 \u0695\u0627\u0632\u0627\u0646\u062f\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u062f\u0627 \u0647\u06d5\u0631 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0686\u06cc\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0628\u06d5 \u0633\u0648\u06ce\u0646\u062f\u06d5\u0648\u06d5 \u0695\u06d5\u0634 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u062a\u0627\u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06c6 \u0628\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u0628\u0695\u0648\u0627 \u062f\u06ce\u0646\u0646. \u0628\u0648\u0648\u0634 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646 \u0647\u06d5\u0631 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u0646\u061b \u06a9\u0648\u0648 \u062f\u06d5\u0632\u0627\u0646\u0646\u061f [\u0645\u0646 \u062f\u06d5\u0632\u0627\u0646\u0645] \u0626\u06d5\u06af\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u060c \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u062c\u0627\u0631\u06cc \u06cc\u06d5\u06a9\u06d5\u0645 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u060c [\u0626\u06ce\u0645\u06d5\u0634] \u062f\u06b5 \u0648 \u062f\u06cc\u062f\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u06a9\u0627\u0631 \u062f\u06d5\u062e\u06d5\u06cc\u0646. \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0647\u06d5\u0646 \u0648\u0627\u0632\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06ce\u0646\u06cc\u0646 \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u06cc\u06d5\u0648\u06d5 \u0633\u06d5\u0631\u06af\u06d5\u0631\u062f\u0627\u0646 \u0628\u0646.',
    '\u0641\u0631\u0634\u062a\u06d5\u0634\u0645\u0627\u0646 \u0628\u0646\u0627\u0631\u062f\u0627\u06cc\u06d5 \u0648 \u0645\u0631\u062f\u06cc\u06cc\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0642\u0633\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0628\u0627\u0646 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u0645\u0627\u0646 \u0644\u06d5 \u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0648\u06d5 \u062e\u0695 \u06a9\u0631\u062f\u0628\u0627\u0648\u06d5\u060c \u0647\u06ce\u0645\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06ce\u0646\u0627\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u062e\u0648\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627. \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0647\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u062f\u0648\u0698\u0645\u0646\u06ce\u06a9\u0645\u0627\u0646 - \u0644\u06d5 \u0646\u06ce\u0648 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0628\u0646\u06cc\u0627\u062f\u06d5\u0645 \u0648 \u062c\u0646\u062f\u06c6\u06a9\u06d5 - \u062f\u0627\u0646\u0627. \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646 - \u0628\u06c6 \u0641\u0631\u06cc\u0648\u062f\u0627\u0646 - \u0642\u0633\u06d5\u06cc \u0695\u0627\u0632\u0627\u0648\u06d5 \u0648 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u062a\u0631 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u0627\u0646\u062f. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632\u06cc \u0628\u06a9\u0631\u062f\u0627\u06cc\u06d5\u060c \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u06d5\u062f\u06d5\u06a9\u0631\u0627. \u0644\u06ce\u06cc\u0627\u0646 \u06af\u06d5\u0695\u06ce \u0628\u0627 \u0647\u06d5\u0631 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u0646.',
    '\u0628\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u0646\u06cc\u06cc\u06d5\u060c \u0628\u06d5 \u062f\u06b5 \u06af\u0648\u06ce \u0628\u06c6 \u0695\u0627\u06af\u0631\u0646 \u0648 \u067e\u06d5\u0633\u0646\u062f\u06cc \u06a9\u06d5\u0646 \u0648 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0628\u0627 \u0628\u06cc\u06a9\u06d5\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0695\u06d5\u0648\u0627\u06cc\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632\u062e\u0648\u0627 \u062f\u0627\u0648\u06d5\u0631\u06ce\u06a9\u06cc \u067e\u06d5\u06cc\u062f\u0627 \u0628\u06a9\u06d5\u0645\u061f \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u062f\u0648\u0648\u0631 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u062a\u06ce\u0628\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627\u0646\u060c \u0633\u0648\u0648\u0631 \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u062e\u06d5\u06b5\u0627\u062a\u06d5 \u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0695\u0627\u06cc\u06d5 \u0648 \u06af\u0634\u062a\u06cc \u0695\u0627\u0633\u062a\u06d5. \u06a9\u06d5 \u0648\u0627\u06cc\u06d5 \u062a\u06c6 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5 \u062f\u0648\u0648\u062f\u06b5\u0627\u0646 \u0645\u06d5\u0628\u06d5.',
    '\u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a - \u0644\u06d5 \u0628\u06ce \u0644\u0627\u06cc\u06d5\u0646\u06af\u0631\u06cc \u0648 \u0695\u0627\u0633\u062a\u06cc\u062f\u0627 - \u062a\u06d5\u0648\u0627\u0648 \u0648 \u0628\u06ce \u06a9\u06d5\u0645 \u0648 \u06a9\u0648\u0648\u0695\u06cc\u0646\u061b \u06a9\u06d5\u0633 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u0628\u06cc\u0627\u0646\u06af\u06c6\u0695\u06ce. \u0632\u06c6\u0631 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0647\u06d5\u0631\u06d5 \u0632\u0627\u0646\u0627\u0634 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u06a9\u06d5\u0648\u062a\u06cc\u06cc\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0642\u0633\u06d5\u06cc \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0648\u06cc\u0646\u06d5\u062f\u0627\u0646\u060c \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627\u062a \u062f\u06d5\u062f\u06d5\u0646\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5 \u0634\u0648\u06ce\u0646 \u062e\u06d5\u06cc\u0627\u06b5\u062f\u0627 \u062f\u06d5\u0695\u06c6\u0646 \u0648 \u0647\u06d5\u0631 \u0644\u06d5 \u067e\u0695\u0648\u067e\u0648\u0648\u0686 \u062e\u06d5\u0631\u06cc\u06a9\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0632\u06c6\u0631 \u0644\u06d5 \u062e\u06c6\u062a \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06ce\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627\u0648\u06d5 \u0648 \u0628\u0627\u0634\u062a\u0631\u06cc\u0634 \u0644\u06d5 \u062a\u06c6 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06ce \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0644\u06d5\u0628\u06d5\u0631\u06d5.',
    '\u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u0648 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u062a\u0627\u0646 \u0644\u06d5 \u0644\u0627 \u0695\u0627\u0633\u062a\u06d5\u060c \u0644\u06d5 \u06af\u06c6\u0634\u062a\u06cc \u0626\u0627\u0698\u06d5\u06b5\u06ce \u0628\u062e\u06c6\u0646 \u06a9\u06d5 \u062f\u06d5\u0645\u06cc \u0633\u06d5\u0631 \u0698\u06ce \u06a9\u0631\u062f\u0646\u06ce\u060c \u0646\u0627\u0648\u06cc \u062e\u0648\u0627\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u0627\u062a\u0648\u0648\u06d5.',
    '\u0628\u06c6\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0644\u06d5 \u06af\u06c6\u0634\u062a\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646 \u06a9\u06d5 \u0646\u0627\u0648\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u062f\u06ce\u061f \u06a9\u06d5 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u0634 \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u0628\u0627\u0633\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc \u0628\u06c6 \u06a9\u0631\u062f\u0648\u0648\u0646 \u06a9\u06d5 \u062e\u0648\u0627\u0631\u062f\u0646\u06cc\u0627\u0646 \u0646\u0627\u0695\u06d5\u0648\u0627\u06cc\u06d5\u060c - \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0646\u0627\u0686\u0627\u0631\u06cc\u06d5\u0648\u06d5 - \u0632\u06c6\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0644\u06d5 \u0646\u06d5\u0632\u0627\u0646\u06cc\u060c \u0645\u06d5\u0631\u062f\u0645 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0644\u0627 \u062f\u06d5\u062f\u06d5\u0646. \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0631\u06d5 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u062f\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u0646.',
    '\u0644\u06d5 \u062a\u0627\u0648\u0627\u0646\u06cc \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0648 \u0646\u0647\u06ce\u0646\u06cc \u062e\u06c6 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u0648\u0627 \u062e\u06c6\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u062a\u0627\u0648\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0633\u0632\u0627\u06cc \u062a\u0627\u0648\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u0646.',
    '\u0647\u06d5\u0631 \u0626\u0627\u0698\u06d5\u06b5\u06ce \u0644\u06d5 \u06a9\u0627\u062a\u06cc \u0633\u06d5\u0631 \u0698\u06ce \u06a9\u0631\u062f\u0646\u06ce \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u06d5\u06cc\u06d5\u060c \u0646\u0627\u0628\u06ce \u0644\u06d5 \u06af\u06c6\u0634\u062a\u06d5\u06a9\u06d5\u06cc \u0628\u062e\u06c6\u0646\u061b \u0626\u06d5\u0645\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0644\u0627\u062f\u0627\u0646\u06d5. \u0634\u06d5\u06cc\u062a\u0627\u0646\u06d5\u06a9\u0627\u0646 \u062f\u0646\u06d5\u06cc \u062f\u06c6\u0633\u062a\u0627\u0646\u06cc \u062e\u06c6\u062f\u06d5\u062f\u06d5\u0646\u060c \u062a\u0627 \u06a9\u06ce\u0634\u06d5\u062a\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06a9\u06d5\u0646. \u062c\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u06af\u0648\u06ce\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0646\u060c \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0634\u06d5\u0631\u06cc\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u0627\u0646\u0627\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0645\u0631\u062f\u0648\u0648 \u0628\u0648\u0648\u060c \u0626\u06ce\u0645\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u062e\u0633\u062a\u06d5 \u067e\u06ce\u0634\u06cc\u060c \u0628\u06d5 \u0633\u06d5\u0631\u0628\u06d5\u0633\u062a\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u0627 \u0628\u0648\u062e\u0644\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0648\u06d5\u06a9 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u062a\u0627\u0631\u06cc\u06a9\u06cc\u062f\u0627\u06cc\u06d5 \u0648 \u0647\u06cc\u0686 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06ce\u06a9\u06cc \u0646\u06cc\u06cc\u06d5\u061f \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062c\u0648\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0648\u0627 \u062a\u0627\u0648\u0627\u0646\u06a9\u0627\u0631\u0627\u0646\u06cc \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u0645\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u062a\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u060c \u062a\u0627 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u062e\u06d5\u06b5\u06a9 \u0641\u0631\u06cc\u0648\u062f\u06d5\u0646. \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc\u0634\u0627 \u0647\u06d5\u0631 \u0641\u06ce\u06b5 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0647\u06d5\u0633\u062a \u067e\u06ce \u0646\u0627\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u060c \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5\u0634 \u062a\u0627 \u0648\u06d5\u06a9 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u062e\u0648\u0627\u060c \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u06af\u0627\u0646\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0646\u06d5\u06cc\u06d5\u060c \u0646\u0627\u0645\u0627\u0646\u06a9\u0631\u06ce \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u06cc\u0646. \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686 \u06a9\u06d5\u0633\u06ce \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0647\u06d5\u06b5\u0628\u0698\u06ce\u0631\u06ce. \u0632\u06c6\u0631\u06cc \u067e\u06ce \u0646\u0627\u0686\u06ce \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0628\u0648\u0646 - \u0644\u06d5 \u0628\u06d5\u0631 \u0641\u06ce\u06b5\u0628\u0627\u0632\u06cc \u0648 \u062f\u06d5\u063a\u06d5\u06b5\u06cc\u0627\u0646 - \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u0627\u0648\u06d5 \u0633\u0648\u06a9\u06cc \u0648 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u062f\u0698\u0648\u0627\u0631\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634 \u062f\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u0648\u062f\u0627 \u062d\u06d5\u0632\u0628\u06a9\u0627\u060c \u0628\u06c6\u062e\u06c6\u06cc \u0695\u06ce\u06af\u06d5\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627\u0648 \u0628\u06c6\u0628\u0695\u0648\u0627 \u0628\u06d5 \u0626\u06cc\u0633\u0644\u0627\u0645\u06d5\u062a\u06cc \u062f\u06b5\u06cc \u06af\u0648\u0634\u0627\u062f\u06cc \u062f\u06d5\u062f\u0627\u062a\u06ce. \u06a9\u06ce\u0634 \u062d\u06d5\u0632\u0628\u06a9\u0627 \u06af\u0648\u0645\u0695\u0627\u06cc \u0628\u06a9\u0627\u060c \u0647\u06ce\u0646\u062f\u06d5 \u0646\u06d5\u0641\u06d5\u0633 \u062a\u06d5\u0646\u06af\u06cc \u062f\u06d5\u06a9\u0627\u060c \u0648\u06d5\u06a9 \u0628\u06d5\u0631\u06d5\u0648\u062d\u06d5\u0648\u0627 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u06ce. \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627 \u0646\u0627\u0647\u06ce\u0646\u0646 \u062a\u0648\u0648\u0634\u06cc \u0695\u0633\u0648\u0627\u06cc\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u0645\u06d5 \u0695\u06ce\u06a9\u06d5\u0695\u06ce\u06af\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u06cc\u06d5. \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u0633\u06cc\u062d\u06d5\u062a \u0628\u06d5\u0647\u0631\u06d5 \u062f\u06d5\u0628\u06d5\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0628\u0627\u0634\u06cc \u0695\u0648\u0648\u0646\u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646\u0695\u0627 \u0634\u0648\u06ce\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0626\u06d5\u0646\u0648\u0627\u06cc \u0647\u06ce\u0645\u0646\u0627\u06cc\u06d5\u062a\u06cc \u062f\u06d5\u0628\u06ce \u0648 \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc \u0626\u06d5\u0648\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u06a9\u06c6 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u0626\u06d5\u06cc \u062f\u06d5\u0633\u062a\u06d5\u0648\u06af\u0631\u06c6\u06cc \u062c\u0646\u062f\u06c6\u06a9\u0627\u0646! \u0626\u06ce\u0648\u06d5 \u0632\u06c6\u0631\u06cc\u0646\u06d5\u06cc \u0645\u0631\u06c6\u062a\u0627\u0646 \u06a9\u0631\u062f\u0628\u0648\u0648\u06d5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u062e\u06c6\u062a\u0627\u0646. \u062f\u06c6\u0633\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u0628\u06d5\u0631\u06d5\u06cc \u0645\u0631\u06c6\u062f\u0627 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0628\u0695\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u0628\u0695\u06ce\u06a9\u06cc \u062a\u0631 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0628\u0631\u062f \u062a\u0627 \u06af\u06d5\u06cc\u06cc\u0634\u062a\u06cc\u0646\u06d5 \u0626\u06d5\u0648 \u0645\u06c6\u06b5\u06d5\u062a\u06d5\u06cc \u06a9\u06d5 \u0628\u06c6\u0645\u0627\u0646\u062a \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u062f. \u06af\u0648\u062a\u06cc: \u062f\u06d5\u0633\u0627 \u062c\u06ce\u06af\u06d5\u06cc \u0647\u06d5\u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06cc\u062a\u0627\u0646 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u062e\u0648\u062f\u0627 - \u0628\u06d5 \u06a9\u06d5\u06cc\u0641\u06cc \u062e\u06c6\u06cc - \u0628\u06c6\u0648 \u0628\u06af\u06c6\u0695\u06ce. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u0627\u0648\u06d5\u0647\u0627\u06cc\u0646\u06d5 \u0628\u0695\u06ce\u06a9 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 - \u0644\u06d5 \u0633\u06c6\u0646\u06af\u06d5\u06cc \u0626\u0627\u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 - \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u0698\u06ce\u0631 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0628\u0695\u06ce\u06a9\u062a\u0631\u06cc\u0627\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062c\u0646\u06c6\u06a9\u06d5 \u0648 \u0645\u0631\u06c6! \u0645\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u06cc\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06ce\u06a9\u062a\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0628\u06c6 \u0646\u06d5\u0647\u0627\u062a\u0646\u060c \u06a9\u06d5 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646 \u0628\u06d5 \u0633\u06d5\u0631 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0648\u06d5\u062e\u0648\u06ce\u0646\u0646 \u0648 \u0644\u06d5\u0645 \u0695\u06c6\u0698\u06d5\u062a\u0627\u0646 \u0628\u062a\u0631\u0633\u06ce\u0646\u0646\u061f \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06d5\u06b5\u06ce\u060c \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646. \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627 \u0641\u0631\u06cc\u0648\u06cc \u062f\u0627\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u0627\u060c \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5.',
    '\u0628\u06c6\u06cc\u06d5 \u0626\u06d5\u0645\u06d5\u0645\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f \u06a9\u06d5 \u0628\u0632\u0627\u0646\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0647\u06cc\u0686 \u0634\u0627\u0631\u06ce\u06a9\u06cc \u0642\u0695 \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u0628\u06ce \u0626\u0627\u06af\u0627 \u0628\u0648\u0648\u0628\u06ce.',
    '\u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce - \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u0626\u0627\u06a9\u0627\u0631\u06cc \u062e\u06c6\u06cc - \u067e\u0644\u06d5\u0648\u067e\u0627\u06cc\u06d5\u06af\u06d5\u0644\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a \u0644\u06d5\u0648\u06d5\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u062e\u0627\u0641\u06b5 \u0646\u06cc\u06cc\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06ce \u0646\u06cc\u0627\u0632 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u0628\u06a9\u0627\u060c \u0646\u0627\u062a\u0627\u0646\u0647\u06ce\u06b5\u06ce \u0648 \u062d\u06d5\u0632\u06ce \u0628\u06a9\u0627\u060c \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06ce\u0648\u06d5 \u0686\u06cc\u0646\u06ce\u06a9\u06cc \u062a\u0631 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u0627\u062f\u06d5\u0646\u06ce\u061b \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u06a9\u06d5\u0633\u0627\u0646\u06cc \u062a\u0631 \u062e\u0633\u062a\u06c6\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0628\u0695\u06cc\u0627\u0631\u06ce \u0628\u06c6\u062a\u0627\u0646 \u062f\u0631\u0627\u060c \u0634\u06a9 \u0646\u06cc\u06cc\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u062f\u06d5\u0628\u06ce \u0648 \u0628\u06d5\u0633\u06d5\u0631\u0648\u0648 \u062f\u06ce \u0648 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u062e\u0648\u0627 \u0628\u06ce \u0647\u06ce\u0632 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0628\u06a9\u06d5\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u06af\u06d5\u0644\u06c6! \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06ce\u060c \u062f\u0631\u06ce\u063a \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0648\u0627 \u0645\u0646\u06cc\u0634 \u0647\u06d5\u0631\u0648\u0627 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u06a9\u0627\u0631\u06cc \u062e\u06c6\u0645\u0645. \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u062f\u0627 \u062a\u06ce \u062f\u06d5\u06af\u06d5\u0646 \u06a9\u06d5 \u0626\u06d5\u0646\u062c\u0627\u0645\u06cc [\u0628\u0627\u0634\u06cc] \u0626\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u0628\u06c6 \u06a9\u06ce \u062f\u06d5\u0628\u06ce. \u0647\u06d5\u0631 \u0686\u06c6\u0646\u06ce\u06a9 \u0628\u06ce\u060c \u062e\u0648\u0627 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u06a9\u0627.',
    '\u0644\u06d5 [\u0628\u06d5\u0631\u0648\u0628\u0648\u0648\u06cc \u0626\u06d5\u0648] \u06a9\u06ce\u06b5\u06af\u06d5 \u0648 \u0648\u06b5\u0633\u0627\u062a\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0628\u06d5\u0634\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u0627\u0646\u0627. \u062c\u0627 \u0628\u06d5 \u062e\u06d5\u06cc\u0627\u06b5 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u0628\u06d5\u0634\u06cc \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0645\u06d5\u0634 \u0647\u06cc \u0628\u062a\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646\u06d5. \u062e\u06c6 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0628\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646\u060c \u0646\u06d5\u062f\u06d5\u06a9\u06d5\u0648\u062a\u06d5 \u0644\u0627\u06cc \u0628\u06d5\u0647\u0631\u06d5\u06cc \u062e\u0648\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u062e\u0648\u0627 \u062f\u06d5\u0686\u0648\u0648\u06d5 \u0644\u0627\u06cc \u0628\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646! \u0646\u0627\u0634\u06cc\u0631\u06cc\u0646 \u062f\u0627\u0648\u06d5\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u062a\u06d5\u0648\u0627\u0646\u0627\u0646\u06cc \u0634\u06d5\u0631\u06cc\u06a9 \u062f\u0627\u0646\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627\u060c \u0648\u0627\u06cc\u0627\u0646\u06a9\u0631\u062f \u06a9\u06d5 \u0632\u06c6\u0631\u06ce\u06a9 \u0644\u06d5 \u0647\u0627\u0648\u0628\u06cc\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u060c \u06a9\u0648\u0634\u062a\u0646\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5\u0644\u0627\u0648\u06d5 \u062c\u0648\u0627\u0646 \u0648 \u0695\u06d5\u0648\u0627 \u0628\u06ce\u061b \u062a\u0627\u06a9\u0648\u0648 \u0644\u06d5 \u0628\u06d5\u06cc\u0646\u06cc\u0627\u0646 \u0628\u0628\u06d5\u0646 \u0648 \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u062a\u06ce\u06a9\u06d5\u06b5 \u0648 \u067e\u06ce\u06a9\u06d5\u06b5 \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u06a9\u06d5\u06cc\u0641\u06cc \u0644\u06ce \u0628\u0627\u060c \u0626\u06d5\u0648\u0627\u0646 \u0626\u0627\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f. \u0644\u06ce\u06cc\u0627\u0646 \u06af\u06d5\u0695\u06ce \u0647\u06d5\u0631 \u0628\u0648\u062e\u062a\u0627\u0646\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0628\u06cc\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u0628\u06d5 \u062e\u06d5\u06cc\u0627\u06b5 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a \u06a9\u06d5: \u0626\u06d5\u0645 \u0645\u0627\u06b5\u0627\u062a \u0648 \u06a9\u06ce\u06b5\u06af\u06d5 \u0642\u0627\u062e\u06d5\u0646. \u0628\u06ce \u0626\u06cc\u0632\u0646\u06cc \u0645\u06d5 \u0647\u06cc\u0686\u06a9\u06d5\u0633 \u0646\u0627\u0628\u06ce \u062a\u0627\u0645\u06cc\u0627\u0646 \u0628\u06a9\u0627. \u0626\u06d5\u0645 \u0645\u0627\u06b5\u0627\u062a\u06d5\u0634 \u06a9\u06d5\u0633 \u0646\u0627\u0628\u06ce \u0633\u0648\u0627\u0631\u06cc \u067e\u0634\u062a\u06cc\u0627\u0646 \u0628\u06ce. \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0645\u0627\u06b5\u0627\u062a\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u0644\u06d5 \u0648\u06d5\u062e\u062a\u06cc \u0633\u06d5\u0631 \u0698\u06ce \u06a9\u0631\u062f\u0646\u062f\u0627 \u0646\u0627\u0648\u06cc \u062e\u0648\u0627\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0646\u0627\u0647\u06ce\u0646\u0646 \u0648 \u062f\u0631\u06c6 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u0627\u0648\u06d5 \u062f\u06d5\u06a9\u06d5\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648 \u062f\u0631\u06c6 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u0646\u06d5\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627.',
    '\u062f\u06d5\u0634\u06cc\u0627\u0646\u06af\u0648\u062a \u06a9\u06d5: \u0686\u06cc \u0644\u06d5 \u067e\u0698\u062f\u0627\u0646\u06cc \u0626\u06d5\u0648 \u0626\u0627\u0698\u06d5\u06b5\u0627\u0646\u06d5\u062f\u0627\u06cc\u06d5\u060c \u0628\u06c6 \u0646\u06ce\u0631\u06cc\u0646\u06d5\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0648 \u0628\u06c6 \u0698\u0646\u0627\u0646\u0645\u0627\u0646 \u0646\u0627\u0695\u06d5\u0648\u0627\u06cc\u06d5! \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0631\u062f\u0627\u0631\u06d5\u0648\u06d5\u0628\u0648\u0648\u0634 \u0628\u06ce\u060c \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u062a\u06ce\u062f\u0627 \u0634\u06d5\u0631\u06cc\u06a9 \u062f\u06d5\u0628\u0646. \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u062e\u0648\u0627 \u0633\u0632\u0627\u06cc \u0626\u06d5\u0645 \u0642\u0633\u0627\u0646\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646 \u0648 \u0632\u06c6\u0631 \u062f\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 - \u0648\u0627 \u0644\u06d5\u0628\u06d5\u0631 \u06af\u06d5\u0648\u062c\u06cc \u0648 \u0646\u06d5\u0632\u0627\u0646\u06cc - \u0641\u0631\u0632\u06d5\u0646\u062f\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06cc\u0627\u0646 \u06a9\u0648\u0634\u062a\u060c \u0632\u06cc\u0627\u0646\u0628\u0627\u0631 \u0628\u0648\u0648\u0646 \u0648 \u0628\u06d5 \u0628\u0648\u062e\u062a\u0627\u0646 \u06a9\u0631\u062f\u0646 \u0628\u06c6 \u062e\u0648\u0627\u060c \u0626\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u062f\u0627 \u0628\u0648\u0648\u0646\u060c \u062d\u06d5\u0631\u0627\u0645\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u062f\u06cc\u0627\u0631\u06d5 \u0695\u06ce\u06cc\u0627\u0646 \u0644\u06ce \u062a\u06ce\u06a9 \u0686\u0648\u0648\u06d5 \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0646\u06cc\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0628\u0627\u063a\u0627\u062a\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5. \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u062f\u0627\u0631\u0628\u06d5\u0633\u062a\u06cc\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u0627\u0631\u0628\u06d5\u0633\u062a\u06cc \u0646\u0627\u0648\u06ce. \u062f\u0627\u0631\u06cc \u062e\u0648\u0631\u0645\u0627 \u0648 \u0645\u06d5\u0632\u0631\u0627 \u0648 \u06a9\u06ce\u06b5\u06af\u06d5\u06cc \u0628\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u060c \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0631\u062f\u0646\u0627 \u0644\u06ce\u06a9 \u062c\u06cc\u0627\u0646. \u0632\u06d5\u06cc\u062a\u0648\u0648\u0646 \u0648 \u062f\u0627\u0631\u0647\u06d5\u0646\u0627\u0631\u06cc\u0634\u06cc \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u06a9\u06d5 \u0628\u06d5 \u0695\u0648\u0627\u06b5\u06d5\u062a \u0648\u06d5\u06a9 \u06cc\u06d5\u06a9 \u0648 \u0644\u06d5 \u062a\u0627\u0645 \u0648 \u0695\u06d5\u0646\u06af\u0627 \u062c\u06cc\u0627\u0648\u0627\u0632\u0646. \u0647\u06d5\u0631\u06af\u0627 \u0645\u06cc\u0648\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06af\u0627\u060c \u0644\u06ce\u06cc \u0628\u062e\u06c6\u0646 \u0648 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0645\u06cc\u0648\u06d5\u0686\u0646\u06cc\u0646\u062f\u0627\u060c \u0628\u06d5\u0634\u06cc \u062e\u0648\u062f\u0627\u0634 \u062c\u0648\u06ce \u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646. \u06a9\u0627\u0631\u06ce\u06a9 \u0645\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0628\u06d5\u062f\u06d5\u0631 \u0628\u06ce\u061b \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce \u06a9\u06d5 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u062f\u06d5\u0631\u062f\u06d5\u0686\u0646.',
    '\u0644\u06d5 \u0645\u0627\u06b5\u0627\u062a\u06cc\u0634 \u0628\u0695\u06ce\u06a9\u06cc \u0628\u06c6 \u0628\u0627\u0631\u0644\u06ce\u0646\u0627\u0646 \u0648 \u0628\u0695\u06ce\u06a9\u06cc \u0628\u06c6 \u06a9\u0648\u06b5\u06a9\u06d5 \u0648 \u062e\u0648\u0631\u06cc\u060c \u0628\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627. \u0644\u06d5\u0648\u06d5\u06cc \u0648\u0627 \u062e\u0648\u062f\u0627 \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u0695\u06c6\u0632\u06cc \u0628\u06c6\u062a\u0627\u0646\u060c \u0628\u062e\u06c6\u0646 \u0648 \u0695\u06ce \u0648 \u0634\u0648\u06ce\u0646\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0645\u06d5\u06af\u0631\u0646\u06d5\u0628\u06d5\u0631\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u0648\u0698\u0645\u0646\u062a\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0634\u062a \u0633\u06d5\u0631 \u0645\u0627\u06b5\u0627\u062a\u06cc \u0646\u06ce\u0631 \u0648 \u0645\u06ce \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5: \u0645\u06d5\u0695 \u0648 \u0628\u06d5\u0631\u0627\u0646\u060c \u0646\u06ce\u0631\u06cc \u0648 \u0628\u0632\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u062e\u0648\u0627 \u0646\u06ce\u0631\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062d\u06d5\u0631\u0627\u0645 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061f \u06cc\u0627\u0646 \u0645\u06ce\u0648\u06cc\u0646\u06d5\u06a9\u0627\u0646\u061f \u06cc\u0627\u0646 \u0626\u06d5\u0648\u06cc \u0648\u0627 \u0644\u06d5 \u067e\u0632\u062f\u0627\u0646\u06cc \u0645\u06ce\u06cc\u06d5\u06a9\u0627\u0646\u062f\u0627\u061f! \u0628\u06d5\u06b5\u06af\u06d5\u0645 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633 \u0626\u06ce\u0698\u0646!',
    '[\u0686\u0648\u0627\u0631\u06cc \u062f\u06cc\u06a9\u06d5\u0634 \u0628\u0631\u06cc\u06d5\u062a\u06cc\u0646 \u0644\u06d5:] \u0644\u06c6\u06a9 \u0648 \u0633\u0627\u06cc\u06d5 \u0648 \u0686\u06ce\u06b5 \u0648 \u0645\u0627\u0646\u06af\u0627. \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u062e\u0648\u0627 \u0646\u06ce\u0631\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062d\u06d5\u0631\u0627\u0645 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061f \u06cc\u0627\u0646 \u0645\u06ce\u0648\u06cc\u0646\u06d5\u06a9\u0627\u0646\u061f \u06cc\u0627\u0646 \u0626\u06d5\u0648\u06cc \u0648\u0627 \u0644\u06d5 \u067e\u0632\u062f\u0627\u0646\u06cc \u0645\u06ce\u06cc\u06d5\u06a9\u0627\u0646\u062f\u0627\u061f \u06cc\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0648\u0627\u06cc \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u0648\u0648\u0646\u061f \u0633\u0627 \u06a9\u06ce \u0644\u06d5\u0648\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5 \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u06ce\u061b \u06a9\u06d5 \u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u0628\u06c6 \u062e\u06c6\u06cc \u0632\u0627\u0646\u0633\u062a\u06ce\u06a9\u06cc \u0647\u06d5\u0628\u06ce\u060c \u062a\u06ce \u0628\u06a9\u06c6\u0634\u06ce \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u06cc\u06a9\u06d5\u0634 \u06af\u0648\u0645\u0695\u0627 \u0628\u06a9\u0627\u061f \u062e\u0648\u062f\u0627 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u0633\u062a\u06d5\u0645\u06a9\u0627\u0631\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce\u06af\u06d5\u06cc \u062e\u06c6\u06cc \u0646\u0627\u06a9\u0627.',
    '\u0628\u06ce\u0698\u06d5: \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 \u06a9\u06d5 \u0648\u0627 \u067e\u06ce\u0645 \u0695\u0627\u06af\u06d5\u06cc\u06ce\u0646\u0631\u0627\u0648\u06d5\u060c \u0634\u062a\u06ce\u06a9\u06cc \u0648\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u0627\u06a9\u06d5\u0645 \u06a9\u06d5 \u062e\u0648\u0627\u0631\u062f\u0646\u06cc \u0644\u06d5 \u06a9\u06d5\u0633\u06ce\u06a9 \u062d\u06d5\u0631\u0627\u0645 \u06a9\u0631\u0627\u0628\u06ce\u060c \u0628\u06ce\u0698\u06af\u06d5 \u0644\u06d5 \u0645\u0631\u062f\u0627\u0631\u06d5\u0648\u06d5\u0628\u0648\u0648\u06cc\u0627\u0646 \u062e\u0648\u06ce\u0646\u06cc \u0695\u0698\u06ce\u0646\u062f\u0627\u0631\u0627\u0648 \u06cc\u0627\u0646 \u06af\u06c6\u0634\u062a\u06cc \u0628\u06d5\u0631\u0627\u0632 - \u06a9\u06d5 \u067e\u06cc\u0633\u06d5 - \u06cc\u0627\u0646 \u0626\u0627\u0698\u06d5\u06b5\u06ce \u06a9\u06d5 \u0628\u06d5 \u0646\u0627\u0648\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0633\u06d5\u0631 \u0628\u0695\u0627\u0648\u06d5. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 - \u0628\u06d5 \u0646\u0627\u0686\u0627\u0631\u06cc \u0628\u06d5 \u0628\u06ce \u0645\u06d5\u06cc\u0644\u06cc - \u0644\u06d5\u0648 \u0646\u0627\u0695\u06d5\u0648\u0627\u06cc\u0627\u0646\u06d5 \u0628\u062e\u0648\u0627 \u0648 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u062a\u06ce\u0631\u0628\u0648\u0648\u0646\u06cc \u062e\u06c6\u06cc \u067e\u062a\u0631 \u0646\u06d5\u062e\u0648\u0627\u060c \u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u062e\u0648\u0627 \u0628\u06d5\u062e\u0634\u06d5\u0646\u062f\u06d5\u06cc\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0632\u06cc\u0646\u062f\u06d5\u0648\u06d5\u0631\u06cc \u0646\u0627\u062e\u06c6\u0646 \u062f\u0627\u0631 \u0648 \u067e\u06cc\u0648\u06cc \u06af\u0627 \u0648 \u067e\u06d5\u0632\u0645\u0627\u0646\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0648\u0648\u0646\u06d5 \u0645\u0648\u0648\u0633\u0627\u06cc\u06cc\u060c \u062d\u06d5\u0631\u0627\u0645 \u06a9\u0631\u062f\u061b \u0645\u06d5\u06af\u06cc\u0646 \u067e\u06cc\u0648\u06ce \u06a9\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u0634\u062a\u06cc \u0626\u06d5\u0648 \u062f\u0648\u0648\u0627\u0646\u06d5 \u062c\u06ce\u06cc \u06af\u0631\u062a\u0648\u0648\u06d5\u061b \u06cc\u0627\u0646 \u0626\u06d5\u0648 \u067e\u06cc\u0648\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0695\u06cc\u062e\u06c6\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u06d5\u061b \u06cc\u0627\u0646 \u0626\u06d5\u0648 \u067e\u06cc\u0648\u06d5\u06cc \u062a\u06ce\u06a9\u06d5\u06b5\u0627\u0648\u06cc \u067e\u06ce\u0634\u06d5 \u0628\u0648\u0648\u06d5. \u0628\u06c6\u06cc\u06d5\u0634\u0645\u0627\u0646 \u0626\u06d5\u0648 \u0633\u0632\u0627\u06cc\u06d5 \u062f\u0627\u0646\u060c \u0646\u0627\u0647\u06d5\u0642 \u0628\u0648\u0648\u0646 \u0648 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u0631\u062f\u06d5\u0686\u0648\u0648\u0646. \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0626\u06ce\u0645\u06d5 \u0695\u0627\u0633\u062a \u0626\u06ce\u0698\u06cc\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0648\u062a\u06cc\u0627\u0646: \u062a\u06c6 \u0626\u06d5\u0645 \u0642\u0633\u0627\u0646\u06d5\u062a \u0695\u0627\u0633\u062a \u0646\u06cc\u06cc\u06d5\u060c \u0628\u06ce\u0698\u06d5: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u062e\u06ce\u0648\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc\u06ce\u06a9\u06cc \u0628\u06d5\u0631\u0628\u06b5\u0627\u0648\u06d5 \u0648 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06cc\u0634 \u0644\u06d5 \u0633\u0632\u0627\u06cc \u0644\u0627\u0646\u0627\u062f\u0631\u06ce\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0646\u0627\u0633\u0646\u060c \u0626\u06ce\u0698\u0646: \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627\u060c \u0626\u06ce\u0645\u06d5 \u0648 \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u0627\u0646\u0645\u0627\u0646\u060c \u0628\u062a \u067e\u06d5\u0631\u0633\u062a\u06cc\u0645\u0627\u0646 \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f\u061b \u0647\u06cc\u0686 \u0634\u062a\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u062d\u06d5\u0631\u0627\u0645 \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f. \u0626\u06d5\u0648\u06cc \u0628\u06d5\u0631\u0644\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0628\u0648\u0648\u0646\u060c \u0647\u06d5\u0631 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627\u060c \u062a\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u06a9\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0686\u06d5\u0634\u062a. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0632\u0627\u0646\u06cc\u0646 \u0634\u062a\u06ce\u06a9 \u0634\u06a9 \u062f\u06d5\u0628\u06d5\u0646\u060c \u0628\u06c6\u0645\u0627\u0646 \u062f\u06d5\u0631\u062e\u06d5\u0646! \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0648\u06d5\u0634\u0648\u06ce\u0646 \u06af\u0648\u0645\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u0647\u06d5\u0631 \u0644\u06d5\u062e\u06c6\u0648\u06d5 \u0642\u0633\u06d5 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0628\u06d5 \u0642\u06d5\u0648\u06d5\u062a \u0647\u06d5\u0631 \u0647\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627 \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646\u06cc \u062f\u06d5\u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5.',
    '\u0628\u06ce\u0698\u06d5 \u0626\u06d5\u0648 \u0634\u0627\u06cc\u06d5\u062a\u0627\u0646\u06d5\u062a\u0627\u0646 \u0628\u06ce\u0646\u0646 \u0648\u0627 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u06d5\u062f\u06d5\u0646: \u062e\u0648\u0627 \u0626\u06d5\u0645\u06d5\u06cc \u062d\u06d5\u0631\u0627\u0645 \u06a9\u0631\u062f\u0648\u0648\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u06d5\u0634\u06cc\u0627\u0646 \u062f\u0627\u060c \u062a\u06c6 \u0648\u06d5\u0644\u06cc\u0627\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0645\u06d5\u062f\u06d5 \u0648 \u0645\u06d5\u0686\u06c6\u0631\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0648\u0627 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u06ce \u0628\u0695\u0648\u0627\u0646 \u0648 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627 \u062f\u0631\u06c6\u06cc\u06d5 \u0648 \u0647\u0627\u0648\u062a\u0627 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0648\u06d5\u0631\u0646 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u0644\u06ce\u06cc \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u0632\u0627\u0646\u06cc\u0648\u0646\u060c \u0645\u0646 \u0628\u06c6\u062a\u0627\u0646\u06cc \u0628\u062e\u0648\u06ce\u0646\u0645\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u0627 \u0645\u06d5\u0632\u0627\u0646\u0646. \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u06cc \u062e\u06c6\u0634\u062a\u0627\u0646 \u0686\u0627\u06a9\u06d5 \u0628\u06a9\u06d5\u0646. \u0644\u06d5 \u062a\u0631\u0633\u06cc \u0644\u06d5 \u0628\u0631\u0633\u0627\u0646 \u0645\u0631\u062f\u0646\u060c \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0645\u06d5\u06a9\u0648\u0698\u0646\u061b \u0626\u06ce\u0645\u06d5 \u0628\u0698\u06cc\u0648 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0648 \u0628\u06d5\u0648\u0627\u0646\u06cc\u0634 \u062f\u06d5\u062f\u06d5\u06cc\u0646. \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0648 \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u062e\u06c6 \u062a\u0648\u0648\u0634\u06cc \u062e\u0631\u0627\u067e\u06d5 \u0645\u06d5\u06a9\u06d5\u0646. \u06a9\u06d5\u0633\u06cc\u0634 \u0645\u06d5\u06a9\u0648\u0698\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u06a9\u0648\u0634\u062a\u0646\u06cc \u062d\u06d5\u0631\u0627\u0645 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0647\u06d5\u0642\u06d5\u0648\u06d5 \u0628\u06ce. \u062e\u0648\u0627 \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0626\u06ce\u0648\u06d5 \u062a\u06ce \u0628\u06af\u06d5\u0646.',
    '\u0645\u06d5\u0686\u0646\u06d5 \u0642\u06d5\u0631\u06d5\u06cc \u0645\u0627\u06b5 \u0648 \u062f\u0627\u0631\u0627\u06cc\u06cc \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5 \u0686\u06d5\u0634\u0646\u06ce \u06a9\u06d5 \u0628\u0627\u0634\u062a\u0631 [\u0628\u067e\u0627\u0631\u06ce\u0632\u0631\u06ce]\u061b \u062a\u0627 \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u062e\u06c6\u06cc \u067e\u06ce \u062f\u06d5\u06af\u0627. \u0628\u0627 \u067e\u06ce\u0648\u0627\u0646\u06d5 \u0648 \u062a\u06d5\u0631\u0627\u0632\u0648\u0648\u0634\u062a\u0627\u0646 \u0628\u06d5\u0631\u0627\u0645\u0628\u06d5\u0631 \u0628\u06ce \u0648 \u06a9\u06d5\u0645 \u0648\u06a9\u0648\u0648\u0695\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06d5\u0628\u06ce. \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u062a\u0648\u0627\u0646\u0627\u06cc\u06cc \u0628\u06d5\u062f\u06d5\u0631 \u0647\u06cc\u0686 \u0626\u06d5\u0631\u06a9\u06ce \u0646\u0627\u062e\u06d5\u06cc\u0646\u06d5 \u0633\u06d5\u0631 \u06a9\u06d5\u0633. \u06a9\u0627\u062a\u06ce [\u0628\u06c6 \u0646\u0627\u0648\u0628\u0698\u06cc \u06cc\u0627\u0646 \u0628\u06c6 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u0627\u0646] \u0642\u0633\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0639\u06d5\u062f\u0627\u06b5\u06d5\u062a\u062a\u0627\u0646 \u0644\u06d5\u0628\u06d5\u0631 \u0686\u0627\u0648 \u0628\u06ce\u061b \u062c\u0627 \u0628\u0627 \u062f\u0698\u06cc \u062e\u0632\u0645\u06cc \u0646\u0632\u06cc\u06a9\u06cc \u062e\u06c6\u0634\u062a\u0627\u0646 \u0628\u06ce. \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u062e\u0648\u0627\u0634 - \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc - \u0628\u06d5 \u062c\u06ce \u0628\u06ce\u0646\u0646. \u062e\u0648\u0627 \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u0628\u06cc\u0631\u062a\u0627\u0646 \u0628\u06ce.',
    '\u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0645\u0646\u061b \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0695\u06ce\u06af\u0627 \u0628\u0686\u0646 \u0648 \u0645\u06d5\u0686\u0646\u06d5 \u0633\u06d5\u0631 \u0695\u06ce\u06af\u0627\u06cc\u0627\u0646\u06cc \u062a\u0631\u060c \u0646\u06d5\u0648\u0627 \u0644\u06d5 \u0695\u06ce\u06af\u0627\u06cc \u0626\u06d5\u0648 \u062f\u0648\u0648\u0631\u062a\u0627\u0646 \u0628\u062e\u06d5\u0646\u06d5\u0648\u06d5. \u062e\u0648\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u062e\u06c6\u062a\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646.',
    '\u06a9\u062a\u06ce\u0628\u06cc\u0634\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627\u060c \u0647\u06d5\u062a\u0627 \u0686\u0627\u06a9\u06d5\u0645\u0627\u0646 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0648\u0627 \u0695\u06ce\u06af\u0627\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u06af\u0631\u062a\u06c6\u062a\u06d5 \u067e\u06ce\u0634\u060c \u0628\u06ce \u06a9\u06d5\u0645 \u0648 \u06a9\u0648\u0695\u06cc \u0648 \u062a\u06d5\u0648\u0627\u0648 \u0628\u06ce\u061b \u062a\u0627 \u0628\u0627\u0633\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u0628\u06ce \u0648 \u0628\u0628\u06ce\u062a\u06d5 \u0647\u06c6\u06cc \u0695\u06ce \u0634\u0627\u0646\u062f\u0627\u0646 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u0627. \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0628\u06d5 \u062f\u06cc\u062f\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0628\u0695\u0648\u0627 \u0628\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u067e\u06cc\u0631\u06c6\u0632 \u0648 \u067e\u0695 \u0644\u06d5 \u067e\u06cc\u062a\u06d5\u0634\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631. \u06af\u0634\u062a\u0648\u0648 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0644\u06d5\u0648 \u0628\u06a9\u06d5\u0646 \u0648 \u062a\u0631\u0633\u06cc\u0634\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06ce\u0648\u062f\u0627 \u0628\u06ce.',
    '\u0646\u06d5\u0686\u0646 \u0628\u06ce\u0698\u0646 \u06a9\u062a\u06ce\u0628 \u0647\u06d5\u0631 \u0628\u06c6 \u062f\u0648\u0648 \u06a9\u06c6\u0645\u06d5\u06b5\u06cc \u0628\u06d5\u0631 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0648 \u0626\u06ce\u0645\u06d5 \u0647\u06cc\u0686 \u0626\u0627\u06af\u0627\u0645\u0627\u0646 \u0644\u06d5 \u0632\u0627\u0646\u0633\u062a\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u06d5.',
    '\u06cc\u0627\u062e\u06c6 \u0628\u06ce\u0698\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5\u0634 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646 \u06a9\u062a\u06ce\u0628\u0645\u0627\u0646 \u0628\u06c6 \u0628\u0647\u0627\u062a\u0627\u06cc\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc\u0645\u0627\u0646 \u067e\u062a\u0631 \u0644\u06d5\u0648\u0627\u0646 \u0628\u0648\u0648. \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0633\u062a\u0627 \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648\u0695\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0632\u06c6\u0631 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u062a\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5. \u0626\u06d5\u0645\u062c\u0627\u0631 \u062f\u06d5\u0628\u06ce \u06a9\u06ce \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631 \u0628\u06ce \u06a9\u06d5 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627 \u0628\u0695\u0648\u0627 \u0646\u06d5\u06a9\u0627 \u0648 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6 \u0628\u0628\u0648\u06ce\u0631\u06ce\u061f \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646 \u0648 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0646\u060c \u0632\u06c6\u0631 \u067e\u06ce \u0646\u0627\u0686\u06ce \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u0633\u0632\u0627\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc \u0686 \u0634\u062a\u06ce\u06a9 \u062f\u06d5\u06a9\u06d5\u0646 \u0628\u06ce\u0698\u06af\u06d5 \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0641\u0631\u06cc\u0634\u062a\u06d5\u06cc\u0627\u0646 \u0628\u06ce\u062a\u06d5 \u0644\u0627\u060c \u06cc\u0627\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062a\u06c6 [\u0628\u06c6 \u062e\u06c6\u06cc] \u0628\u06ce\u062a\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u0628\u0695\u06ce \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u061f \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0626\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06d5 \u0628\u06ce\u062a\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06d5\u0631\u06ce \u0628\u0695\u0648\u0627\u06cc \u0646\u06d5\u0647\u06ce\u0646\u0627 \u0628\u06ce \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc \u0646\u06d5\u0628\u0648\u0648 \u0628\u06ce\u060c \u0628\u0695\u0648\u0627\u0647\u06ce\u0646\u0627\u0646 \u062a\u0627\u0632\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u0627\u062f\u0627. \u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646\u060c \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u067e\u06d5\u0631\u0698\u0648\u0628\u06b5\u0627\u0648 \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u0644\u06ce\u06a9\u062a\u0631 \u062c\u06cc\u0627\u0648\u0627\u0632\u06cc\u0627\u0646 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646 \u0646\u06d5\u0628\u06cc. \u062e\u0648\u062f\u0627 \u0628\u06d5 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627 \u0648 \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u062f\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0644\u06d5\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9 \u0628\u06a9\u0627\u060c \u062f\u06d5 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u0631\u06ce\u062a\u06d5\u0648\u06d5. \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u0631\u0627\u067e\u06d5 \u0628\u06a9\u0627\u060c \u0628\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u062e\u0631\u0627\u067e\u06d5\u06a9\u06d5\u06cc \u0633\u0632\u0627\u062f\u06d5\u062f\u0631\u06ce \u0648 \u06a9\u06d5\u0633 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u062a\u06c6 \u0628\u06ce\u0698\u06d5: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646\u060c \u062e\u0633\u062a\u0648\u0648\u0645\u06cc\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06af\u06d5\u061b \u0633\u06d5\u0631 \u062f\u06cc\u0646\u06ce \u06a9\u06d5 \u067e\u0627\u06cc\u06d5\u062f\u0627\u0631\u06d5: \u062f\u06cc\u0646\u06cc \u0626\u06d5\u0648 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645\u06d5 \u0648\u0627 \u0644\u06d5 \u0646\u0627\u062d\u06d5\u0642 \u0644\u0627\u06cc\u062f\u0627\u0648\u06d5 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0647\u06cc\u0686 \u0634\u06d5\u0631\u06cc\u06a9\u06ce\u06a9\u06cc \u0628\u06c6 \u062e\u0648\u0627 \u0628\u0695\u06cc\u0627\u0631 \u0646\u06d5\u062f\u0627\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0646\u0645\u06ce\u0698\u06a9\u0631\u062f\u0646\u0645 \u0648 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627\u0646\u06cc \u0695\u06ce \u0648 \u0634\u0648\u06ce\u0646\u06cc \u062d\u06d5\u062c \u06a9\u0631\u062f\u0646\u0645 \u0648 \u0698\u06cc\u0646 \u0648 \u0645\u06d5\u0631\u06af\u0645\u060c \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u06a9\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0646\u06cc\u06cc\u06d5. \u0641\u06d5\u0631\u0645\u0627\u0646 \u0648\u0627 \u0628\u06d5 \u0645\u0646 \u062f\u0631\u0627\u0648\u06d5 \u0648 \u0645\u0646 \u0644\u06d5\u0695\u06ce\u0632\u06d5\u06cc \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u0627\u0646 \u06cc\u06d5\u06a9\u06d5\u0645\u06cc\u0646\u0645.',
    '\u0628\u06ce\u0698\u06d5: \u062c\u0627\u0686\u06c6\u0646 \u0644\u06d5 \u062e\u0648\u0627 \u0628\u06d5\u062f\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062a\u0631 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5 \u0628\u0632\u0627\u0646\u0645\u061f \u06a9\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u062e\u0631\u0627\u067e \u0628\u06a9\u0627\u060c \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc \u0633\u0632\u0627\u06cc \u062f\u06d5\u0628\u06cc\u0646\u06ce. \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0628\u0627\u0631\u06cc \u06af\u0631\u0627\u0646\u06cc \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062a\u0631 \u0646\u0627\u06af\u0631\u06ce\u062a\u06d5 \u06a9\u06c6\u06b5. \u062c\u06ce\u06cc \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0648\u0627 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646\u06d5 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0686\u06cc \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u067e\u06ce\u06a9 \u0646\u06d5\u062f\u06d5\u0647\u0627\u062a\u0646.',
    '\u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u06a9\u0631\u062f\u06d5 \u062c\u06ce\u0646\u0634\u06cc\u0646\u0627\u0646\u06cc \u0632\u06d5\u0648\u06cc\u0646 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u06cc \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc \u062a\u0631\u060c \u0686\u06d5\u0646 \u067e\u0644\u06d5 \u0628\u0631\u062f\u06d5 \u0633\u06d5\u0631\u06d5\u0648\u06d5 \u062a\u0627 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u067e\u06ce\u06cc \u062f\u0627\u0648\u0646 \u0628\u06d5 \u062a\u0627\u0642\u06cc\u062a\u0627\u0646 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06d5 \u067e\u06d5\u0644\u06d5 \u062a\u06c6\u06b5\u06d5 \u062f\u06d5\u0633\u062a\u06ce\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u06a9\u06d5 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0645\u06cc\u0645\u060c \u06b5\u0627\u062f.',
    '\u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06d5 \u0628\u06c6 \u062a\u06c6 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u062a\u0627 \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0648 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u0645\u06d5\u0631\u062f\u0645 \u0628\u0646\u06ce\u06cc \u0648 \u062e\u06c6\u062a \u062a\u0648\u0648\u0634\u06cc \u062f\u06b5\u062a\u06d5\u0646\u06af\u06cc \u0645\u06d5\u06a9\u06d5 \u0644\u06d5\u0648 \u0628\u0627\u0631\u06d5\u0648\u06d5\u061b \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0646\u0633\u062d\u06d5\u062a\u06cc\u0634\u06d5 \u0628\u06c6 \u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce \u062f\u06ce\u0646\u0646.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6\u062a\u0627\u0646 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u0634\u0648\u06ce\u0646\u06cc \u06a9\u06d5\u0648\u0646 \u0648 \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0645\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0633\u06d5\u0631\u067e\u06d5\u0631\u0633\u062a\u0627\u0646\u06cc \u062f\u06cc\u06a9\u06d5. \u0626\u06ce\u0648\u06d5 \u0632\u06c6\u0631 \u06a9\u06d5\u0645 \u0639\u06cc\u0628\u0631\u06d5\u062a \u062f\u06d5\u06af\u0631\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0634\u0627\u0631\u0645\u0627\u0646 \u0642\u0695 \u062a\u06ce \u062e\u0633\u062a\u0646. \u0648\u06d5\u062e\u062a\u06ce \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u063a\u06d5\u0632\u06d5\u0628\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0647\u0627\u062a - \u0644\u06d5 \u0634\u06d5\u0648\u062f\u0627 \u0628\u0648\u0648\u060c \u06cc\u0627\u0646 \u0646\u06cc\u0648\u06d5\u0695\u06c6 - \u062f\u06d5\u0633\u0627\u0646\u06d5\u0648\u06d5.',
    '\u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u063a\u06d5\u0632\u06d5\u0628\u0645\u0627\u0646 \u0644\u06ce \u06af\u0631\u062a\u0648\u0648\u0646\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0645\u0627 \u0628\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5\u06cc\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0645\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06ce \u0633\u06c6 \u067e\u0631\u0633\u06cc\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u0647\u06d5\u0645 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0648\u0648\u0646 \u0648 \u0647\u06d5\u0645 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u06cc \u0646\u0627\u0631\u062f\u0646\u0645\u0627\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5 - \u06a9\u06d5 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646 - \u0646\u0648\u0648\u06a9 \u0648 \u0628\u06d5\u062f\u06cc \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627 \u0628\u0627\u0633 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u0642\u06d5\u062a \u0644\u06ce\u06cc\u0627\u0646 \u0628\u06ce \u0626\u0627\u06af\u0627 \u0646\u06d5\u0628\u0648\u0648\u06cc\u0646.',
    '\u0647\u06d5\u06b5\u0633\u06d5\u0646\u06af\u0627\u0646\u062f\u0646\u06cc \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc\u060c \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u062f\u06ce\u062a\u06d5 \u06af\u06c6\u0695\u06ce. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u0627\u06cc \u062a\u06d5\u0631\u0627\u0632\u0648\u0648\u06cc\u0627\u0646 \u06af\u0631\u0627\u0646 \u0628\u06ce\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0632\u06af\u0627\u0631\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06d5\u06b5\u06a9\u06ce\u0634\u0627\u0646\u06ce\u062f\u0627 \u0633\u0648\u0648\u06a9 \u0628\u06ce\u060c \u0632\u06d5\u0631\u06d5\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5 \u0648 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0645\u06d5 \u063a\u06d5\u062f\u0631\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5 \u062c\u06ce \u06a9\u0631\u062f\u06d5\u0648\u06d5\u061b \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u0634\u062f\u0627 \u0628\u0698\u06cc\u0648\u0645\u0627\u0646 \u0628\u06c6 \u0628\u0695\u06cc\u0648\u0646\u06d5\u0648\u06d5. \u06a9\u06d5\u0645\u062a\u0627\u0646 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631\u0646.',
    '\u0626\u06ce\u0645\u06d5\u06cc\u0646 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u0633\u0627\u0632 \u062f\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646\u0627 \u0628\u06d5\u0648 \u0634\u06a9\u06b5\u06d5\u0645\u0627\u0646 \u062f\u06d5\u0631\u06ce\u0646\u0627\u0648\u0646\u061b \u067e\u0627\u0634\u0627\u0646 \u0628\u06d5 \u0641\u0631\u06cc\u0634\u062a\u0627\u0646\u0645\u0627\u0646 \u06af\u0648\u062a: \u0633\u06d5\u0631 \u0628\u06c6 \u0626\u0627\u062f\u06d5\u0645 \u0646\u06d5\u0648\u06cc \u0628\u06a9\u06d5\u0646. \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u062f\u0627\u0646\u06d5\u0648\u0627\u0646\u062f \u0626\u06cc\u0628\u0644\u06cc\u0633 \u0646\u06d5\u0628\u06ce\u061b \u0626\u06d5\u0648 \u0644\u06d5 \u06a9\u06c6\u0695\u06cc \u06a9\u0695\u0646\u06c6\u0634 \u0628\u06d5\u0631\u0627\u0646 \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u0627\u0648\u0627\u0631\u062f.',
    '\u06af\u0648\u062a\u06cc: \u06a9\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u0645 \u067e\u06ce \u062f\u0627\u06cc\u060c \u0644\u06d5 \u0628\u06d5\u0631 \u0686\u06cc \u062a\u06c6 \u0633\u0648\u0698\u062f\u06d5\u062a \u0646\u06d5\u0628\u0631\u062f\u061f \u06af\u0648\u062a\u06cc: \u0645\u0646 \u0644\u06d5\u0648 \u0647\u06ce\u0698\u0627\u062a\u0631\u0645\u061b \u0645\u0646\u062a \u0644\u06d5 \u0626\u0627\u06af\u0631 \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u060c \u0626\u06d5\u0648\u062a \u0644\u06d5 \u062d\u06d5\u0695\u06cc \u0633\u0627\u0632 \u062f\u0627\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0644\u06ce\u0631\u06d5\u0648\u06d5 \u062f\u0627\u06af\u06d5\u0695\u06ce\u061b \u0644\u06d5\u0645 \u0634\u0648\u06ce\u0646\u06d5\u062f\u0627 \u0695\u06ce\u062a \u0646\u0627\u06a9\u06d5\u0648\u06ce \u0628\u06d5 \u0633\u06d5\u0631 \u06a9\u06d5\u0633\u062f\u0627 \u0641\u06cc\u0632 \u0628\u0646\u0648\u06ce\u0646\u06cc. \u0633\u0627 \u0644\u06d5\u0645 \u0634\u0648\u06ce\u0646\u06d5\u062f\u0627 \u0645\u06d5\u0645\u06ce\u0646\u06d5\u060c \u062a\u06c6 \u0644\u06d5 \u0633\u0648\u0648\u06a9 \u0648 \u0686\u0631\u0648\u0648\u06a9\u0627\u0646\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u062a\u0627 \u0633\u06d5\u06b5\u0627 \u0695\u0627\u062f\u06d5\u0628\u06ce\u060c \u0645\u0627\u0648\u06d5\u0645 \u0628\u062f\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0648\u0627 \u062a\u06c6 \u0644\u06d5 \u0645\u0627\u0648\u06d5 \u062f\u0631\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0686\u0648\u0646\u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u062a \u0644\u0627 \u062f\u0627\u0645\u060c \u0645\u0646\u06cc\u0634 \u0628\u06c6\u06cc\u0627\u0646 \u0695\u06c6\u062f\u06d5\u0646\u06cc\u0634\u0645\u060c \u0695\u0627\u0633\u062a\u06d5 \u0695\u06ce\u06cc \u062a\u06c6\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06af\u0631\u0645.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u067e\u06ce\u0634 \u0648 \u067e\u0627\u0634\u06d5\u0648\u06d5 \u0628\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0686\u0645\u060c \u0695\u0627\u0633\u062a \u0648 \u0686\u06d5\u067e\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06af\u0631\u0645\u061b \u062c\u0627 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0632\u06c6\u0631\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u0634\u0648\u06a9\u0631 \u0646\u0627\u06a9\u06d5\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0644\u06ce\u0631\u06d5 \u0628\u0695\u06c6 \u062f\u06d5\u0631\u06ce! \u0646\u0627\u0648\u0632\u0695\u0627\u0648\u06cc \u062a\u0695\u06c6 \u06a9\u0631\u0627\u0648! \u062f\u06d5\u0628\u06ce \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0634\u0648\u06ce\u0646 \u062a\u06c6 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u060c \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u062a\u0627\u0646 \u0644\u06ce \u067e\u0695 \u0628\u06a9\u06d5\u0645.',
    '\u0626\u06d5\u06cc \u0626\u0627\u062f\u06d5\u0645! \u062a\u06c6\u0634 \u0648\u06ce\u0695\u0627\u06cc \u0698\u0646\u062a \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0628\u062d\u0627\u0648\u06ce\u0646\u06d5\u0648\u06d5\u060c \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce\u062f\u0627 \u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u062e\u06c6\u0646 \u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646\u061b \u0647\u06d5\u0631 \u062a\u062e\u0648\u0646\u06cc \u0626\u06d5\u0648 \u062f\u0627\u0631\u06cc \u0647\u0627\u0646\u0627 \u0645\u06d5\u06a9\u06d5\u0648\u0646\u060c \u062f\u06d5\u0646\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u062f\u06d5\u0628\u0646.',
    '\u0634\u06d5\u06cc\u062a\u0627\u0646 \u06a9\u06d5\u0648\u062a\u06d5 \u06a9\u06b5\u06cc\u0634\u06d5\u06cc\u0627\u0646 \u062a\u0627 \u0626\u06d5\u0648 \u0626\u06d5\u0646\u062f\u0627\u0645\u06d5\u06cc\u0627\u0646 - \u06a9\u06d5 \u067e\u06c6\u0634\u0631\u0627 \u0628\u0648\u0648 - \u0628\u06ce\u0646\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u06cc\u0627\u0646. \u06af\u0648\u062a\u06cc: \u062e\u0648\u062f\u0627 \u06a9\u06d5 \u0646\u06d5\u06cc\u062f\u06d5\u0647\u06ce\u0634\u062a \u0644\u06d5\u0648 \u062f\u0627\u0631\u06d5 \u0646\u0632\u06cc\u06a9 \u0628\u0628\u0646\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u0628\u0648\u0648 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u0628\u0646\u06d5 \u0641\u0631\u0634\u062a\u06d5\u061b \u06cc\u0627\u0646 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u0627 \u0628\u0645\u06ce\u0646\u0646.',
    '\u0633\u0648\u06ce\u0646\u062f\u06cc \u0628\u06c6 \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9\u0627\u0646\u06cc\u0634 \u062e\u0648\u0627\u0631\u062f \u06a9\u06d5: \u0645\u0646 \u0628\u06d5 \u062f\u06b5 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u062a\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u06a9\u06d5\u0645.',
    '\u0628\u06d5 \u0641\u0631\u06cc\u0648\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06cc \u062e\u0633\u062a\u06d5 \u062a\u06d5\u0645\u0627\u0648\u06d5. \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u0644\u06d5 \u0628\u06d5\u0631\u06cc \u062f\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0686\u06d5\u0634\u062a\u060c \u0639\u06d5\u06cc\u0628 \u0648 \u0639\u0627\u0631\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u06d5 \u062f\u06d5\u0634\u062a \u0648 \u06a9\u06c6\u0634\u0634\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0628\u06d5 \u06af\u06d5\u06b5\u0627\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a \u062f\u0627\u06cc\u067e\u06c6\u0634\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5 \u06af\u0627\u0632\u06cc \u06a9\u0631\u062f\u0646: \u0626\u0627\u062e\u0631 \u0645\u0646 \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u0645 \u0646\u06d5\u06af\u0648\u062a \u062a\u0648\u062e\u0646\u06cc \u0626\u06d5\u0645 \u062f\u0627\u0631\u06d5 \u0645\u06d5\u06a9\u06d5\u0648\u0646\u061f \u067e\u06ce\u0634\u0645 \u0695\u0627\u0646\u06d5\u06af\u06d5\u06cc\u0627\u0646\u062f\u0628\u0648\u0648\u0646 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u062f\u0648\u0698\u0645\u0646\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0646\u0627\u0647\u06d5\u0642\u06cc\u0645\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u06a9\u0631\u062f. \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0644\u06ce\u0645\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u0631\u06cc \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u062a \u067e\u06ce\u0645\u0627\u0646\u062f\u0627 \u0646\u06d5\u06cc\u06d5\u060c \u0628\u06ce \u0634\u06a9 \u0644\u06d5 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u062f\u0627\u06af\u06d5\u0695\u06ce\u0646\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u0626\u06ce\u0648\u06d5 \u062f\u0648\u0698\u0645\u0646\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0646. \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u062c\u06ce\u06af\u0627 \u0648 \u0645\u0627\u06cc\u06d5\u06cc \u0698\u06cc\u0627\u0646\u062a\u0627\u0646 \u0644\u06d5 \u0632\u06d5\u0648\u06cc \u062f\u06d5\u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u062f\u06d5\u0648\u06ce\u062f\u0627 \u062f\u06d5\u0698\u06cc\u0646 \u0648 \u0644\u06d5\u0648\u06ce\u0634 \u062f\u06d5\u0645\u0631\u0646\u061b \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u0634 \u062f\u06d5\u0648\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u062f\u06d5\u0631.',
    '\u0626\u06d5\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0626\u0627\u062f\u06d5\u0645! \u0626\u06ce\u0645\u06d5 \u062c\u0644\u06a9\u06cc \u0648\u0627\u0645\u0627\u0646 \u062f\u0627\u0648\u0646\u06ce \u06a9\u06d5 \u0639\u06d5\u06cc\u0628 \u0648 \u0639\u0627\u0631\u062a\u0627\u0646 \u062f\u0627\u067e\u06c6\u0634\u06ce \u0648 \u062c\u0648\u0627\u0646\u06cc\u0634\u062a\u0627\u0646 \u06a9\u0627\u061b \u0686\u0627\u06a9\u062a\u0631 \u0644\u06d5\u0648\u06cc\u0634 \u067e\u06c6\u0634\u0627\u06a9\u06cc \u062e\u06c6\u067e\u0627\u0631\u0627\u0632\u062a\u0646\u06d5. \u0626\u06d5\u0645\u06d5\u0634 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0628\u06cc\u0631\u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0626\u0627\u062f\u06d5\u0645! \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0646\u06d5\u062a\u0627\u0646\u062e\u06d5\u06b5\u06d5\u062a\u06ce\u0646\u06ce\u060c \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0628\u0627\u0648\u06a9 \u0648 \u062f\u0627\u06cc\u06a9\u062a\u0627\u0646\u06cc \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0628\u06d5 \u062f\u06d5\u0631\u06a9\u0631\u062f\u0646 \u062f\u0627 \u0648 \u062c\u0644 \u0648 \u0628\u06d5\u0631\u06af\u06cc \u0644\u06ce \u062f\u0627\u0695\u0646\u06cc\u0646 \u062a\u0627 \u0634\u06d5\u0631\u0645\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06cc\u0627\u0646 \u067e\u06ce\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u0627. \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0648 \u062f\u06d5\u0633\u062a\u06d5 \u0648 \u0628\u06d5\u0633\u062a\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u06a9\u06d5\u0648\u06d5 \u062f\u06d5\u062a\u0627\u0646\u0628\u06cc\u0646\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0646\u0627\u0628\u06cc\u0646\u0646. \u0626\u06ce\u0645\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u0646\u06cc\u06cc\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0646\u06cc\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u06a9\u0627\u0631\u06ce \u062e\u0631\u0627\u067e\u06cc \u0626\u0627\u0628\u0695\u0648\u0648\u0628\u06d5\u0631\u06cc\u0627\u0646 \u0644\u06ce \u0695\u0648\u0648 \u062f\u06d5\u062f\u0627\u060c \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u0628\u0627\u067e\u06cc\u0631\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062f\u06cc\u0648\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631 \u0626\u0627\u0648\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce \u062f\u0627\u0648\u06cc\u0646 \u0626\u0627\u0648\u0627 \u0628\u06a9\u06d5\u06cc\u0646. \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0628\u06c6 \u06a9\u0627\u0631\u06cc \u067e\u06cc\u0633 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0646\u0627\u062f\u0627. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0632\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u0648\u06d5 \u0634\u062a\u06ce\u06a9 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646 \u06a9\u06d5 \u0646\u0627\u06cc\u0632\u0627\u0646\u0646\u061f!',
    '\u062a\u06c6 \u0628\u06ce\u0698\u06d5: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u0627\u0645\u0628\u06d5\u0631\u06cc \u062f\u06d5\u062f\u0627. \u0644\u06d5 \u0647\u06d5\u0631 \u0645\u0632\u06af\u06d5\u0648\u062a\u06ce\u06a9\u0627 \u0695\u0648\u0648\u062a\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646 \u0648 \u0628\u06d5 \u062f\u06b5\u06ce\u06a9\u06cc \u067e\u0627\u06a9 \u0644\u06d5 \u0628\u06d5\u0631\u06cc \u0628\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5. \u0648\u06d5\u06a9 \u0628\u06d5\u0631\u06ce \u0626\u06ce\u0648\u06d5\u06cc \u062f\u0631\u0648\u0633\u062a \u06a9\u0631\u062f\u060c \u06af\u0634\u062a\u0648\u0648 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u0686\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce\u06af\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06ce\u06a9\u06cc\u0634 \u0647\u06d5\u0631 \u0634\u06cc\u0627\u0648\u06cc \u06af\u0648\u0645\u0695\u0627 \u0628\u0648\u0648\u0646\u0646\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u0628\u0631\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u0634\u06d5\u06cc\u0627\u062a\u06cc\u0646 \u0628\u0648\u0648\u0646\u06d5 \u062f\u06c6\u0633\u062a\u061b \u0644\u0627\u06cc\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646\u0646.',
    '\u0626\u06d5\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0626\u0627\u062f\u06d5\u0645! \u0647\u06d5\u0631\u06af\u0627 \u062f\u06d5\u0686\u0646 \u0628\u06c6 \u0646\u0648\u06ce\u0698 \u06a9\u0631\u062f\u0646\u060c \u062c\u0644\u06a9\u06cc \u062c\u0648\u0627\u0646 \u062f\u06d5\u0628\u06d5\u0631 \u0628\u06a9\u06d5\u0646\u061b \u062e\u0648\u0627\u0631\u062f\u0646\u06ce \u0628\u062e\u06c6\u0646 \u0648 \u0648\u06d5\u062e\u06c6\u0646 \u0648 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u062f\u06d5\u0631\u0645\u06d5\u0686\u0646. \u062e\u0648\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u062e\u06c6\u0634\u06cc \u0646\u0627\u06cc\u06d5 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u062f\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u06a9\u06ce\u06cc\u06d5\u060c \u062c\u0644\u06a9\u06cc \u062c\u0648\u0627\u0646 \u0648 \u062e\u0634\u06b5 \u0648 \u0628\u0698\u06cc\u0648\u06cc \u067e\u0627\u06a9\u0698\u06ce - \u06a9\u06d5 \u062e\u0648\u0627 \u0628\u06d5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0695\u06d5\u0648\u0627 \u062f\u06cc\u0648\u06d5 - \u0644\u0627\u06cc \u0648\u0627\u06cc\u06d5 \u06a9\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627\u06cc\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0634\u0627\u06cc\u0627\u0646\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627\u062f\u0627 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 \u0647\u06d5\u0631 \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u0628\u06d5 \u062e\u06c6\u06cc\u0627\u0646\u06d5. \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0632\u0627\u0646\u0627 \u0628\u0646 \u0695\u06c6\u0634\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u062a\u06d5\u0646\u06cc\u0627 \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc \u0646\u0627\u0695\u06d5\u0648\u0627 \u062f\u06cc\u0648\u06d5: \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0648 \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc\u060c \u0628\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0644\u0627\u062f\u0627\u0646\u060c \u0628\u06ce \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 \u0634\u06d5\u0631\u06cc\u06a9 \u062f\u0627\u0646\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0648 \u0626\u0627\u062e\u0627\u0641\u062a\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627\u060c \u0628\u06d5 \u0628\u06ce \u0632\u0627\u0646\u06cc\u0646.',
    '\u0644 \u0628\u06c6 \u0645\u06d5\u0631\u06af\u06cc \u0647\u06d5\u0631 \u0626\u06c6\u0645\u06d5\u062a\u06ce\u060c \u0648\u06d5\u062e\u062a\u06ce \u0647\u06d5\u06cc\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06cc \u0645\u0631\u062f\u0646\u06cc\u0627\u0646 \u0647\u0627\u062a\u060c \u0646\u06d5 \u062a\u0627\u0648\u06ce\u06a9 \u062f\u06d5\u06a9\u0631\u06ce \u067e\u06ce\u0634 \u06a9\u06d5\u0648\u0646\u060c \u0646\u06d5 \u062a\u0627\u0648\u06ce\u06a9\u06cc\u0634 \u062f\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0648\u0646.',
    '\u0626\u06d5\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0626\u0627\u062f\u06d5\u0645! \u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06ce \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u062f\u06ce\u0646\u06d5\u0644\u0627\u062a\u0627\u0646 \u062a\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646\u062a\u0627\u0646 \u0628\u06c6 \u0628\u0627\u0633 \u0628\u06a9\u06d5\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u062a\u0627\u0646\u06d5 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0646\u06d5 \u062a\u0631\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u062e\u06d5\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u06cc\u0634 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627 \u0648 \u0628\u06d5 \u0628\u0627\u062f\u06cc\u0647\u06d5\u0648\u0627\u06cc\u06cc \u0695\u0648\u0648\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06cc\u0627\u0631\u06cc \u0626\u0627\u06af\u0631\u0646 \u0648 \u0644\u06ce\u06cc \u062f\u06d5\u0631\u0646\u0627\u0686\u0646.',
    '\u06a9\u06ce \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5 \u06a9\u06d5 \u062f\u0631\u06c6 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646\u061b \u06cc\u0627\u062e\u06c6 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0628\u0695\u0648\u0627 \u0646\u0627\u06a9\u06d5\u0646\u061f \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0686\u06cc \u0644\u06d5 \u0686\u0627\u0631\u06d5\u06cc\u0627\u0646\u0627 \u0646\u0648\u0648\u0633\u0631\u0627 \u0628\u06ce\u060c \u062f\u06ce\u062a\u06d5 \u0695\u06ce\u06cc\u0627\u0646\u061b \u062a\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u06a9\u06d5 \u0641\u0631\u06cc\u0634\u062a\u06d5 \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u062f\u06ce\u062a\u06d5 \u0644\u0627\u06cc\u0627\u0646 \u0628\u06c6 \u06af\u06cc\u0627\u0646 \u06a9\u06ce\u0634\u0627\u0646 \u0648 \u062f\u06d5\u067e\u0631\u0633\u0646: \u06a9\u0648\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0647\u0627\u0646\u0627\u062a\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u062f\u06d5\u0647\u06ce\u0646\u0627\u0646\u061f \u0626\u06ce\u0698\u0646: \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06ce \u06af\u0648\u0645 \u06a9\u0631\u062f\u06cc\u0646. \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u062e\u06c6 \u062f\u06d5\u062f\u06d5\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5.',
    '\u0626\u06ce\u0698\u06ce: \u0648\u06ce\u0695\u0627\u06cc \u0626\u06c6\u0645\u06d5\u062a\u06af\u06d5\u0644\u06ce \u0648\u0627 \u0628\u06d5\u0631 \u0644\u06d5 \u0626\u06d5\u0646\u06af\u06c6 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0686\u0648\u0648\u0646 - \u0644\u06d5 \u0645\u0631\u06c6\u06a4 \u0648 \u0644\u06d5 \u062c\u0646\u062f\u06c6\u06a9\u06d5 - \u0628\u0695\u06c6\u0646\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631\u06d5\u0648\u06d5. \u0647\u06d5\u0631 \u0626\u06c6\u0645\u06d5\u062a\u06ce\u06a9 \u062f\u06d5\u0686\u06ce\u062a\u06d5 \u0646\u0627\u0648\u060c \u062a\u0648\u0648\u06a9 \u0648 \u0646\u06d5\u0641\u0631\u06cc\u0646 \u0644\u06d5 \u0647\u0627\u0648 \u0695\u06d5\u0648\u0634\u062a\u0627\u0646\u06cc \u062f\u06d5\u06a9\u0627. \u0648\u06d5\u062e\u062a\u06ce \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u06a9\u06c6 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u060c \u067e\u06d5\u06cc\u0695\u06d5\u0648\u0627\u0646\u06cc\u0627\u0646 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0695\u06ce\u0628\u06d5\u0631\u0627\u0646\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0626\u06d5\u0648\u0627\u0646 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0644\u06ce \u0634\u06ce\u0648\u0627\u0646\u062f\u06cc\u0646\u061b \u062f\u0648\u0648 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0644\u06d5 \u0626\u0627\u06af\u0631\u062f\u0627 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5! \u0626\u06ce\u0698\u06ce: \u0628\u06c6 \u06af\u0634\u062a \u062f\u0648\u0648 \u0686\u06d5\u0646\u062f\u0627\u0646\u06d5\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0648\u06d5 \u067e\u06ce \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0695\u06ce\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0648\u06d5 \u0647\u06cc\u0686\u062a\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0632\u06cc\u0627\u062a\u0631 \u0646\u06cc\u06cc\u06d5\u061b \u062f\u06d5\u0628\u0686\u06ce\u0698\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u0648\u0627 \u06a9\u0631\u062f\u0648\u0648\u062a\u0627\u0646\u06d5!',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627 \u0648 \u0628\u06d5 \u0641\u06cc\u0632\u06d5\u0648\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u060c \u062f\u06d5\u0631\u0648\u0627\u0632\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0632\u0645\u06ce\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u0627\u06a9\u0631\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0646\u0627\u0686\u0646\u06d5 \u0646\u0627\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u0648\u06d5 \u0647\u06d5\u062a\u0627 \u0648\u0634\u062a\u0631 \u0628\u06d5 \u06a9\u0648\u0646\u06cc \u062f\u06d5\u0631\u0632\u06cc\u062f\u0627 \u0646\u06d5\u0686\u06ce! \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u0633\u0632\u0627\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0695\u0627\u06cc\u06d5\u062e\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0626\u0627\u06af\u0631\u06d5 \u0648 \u0647\u06d5\u0631 \u0647\u0627\u06b5\u0627\u0648\u06cc\u0634 \u062f\u0627\u06cc \u067e\u06c6\u0634\u06cc\u0648\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0626\u0627\u0648\u0627\u06cc\u0627\u0646 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u06a9\u0627\u0631\u06af\u06d5\u0644\u06cc \u0686\u0627\u06a9\u06cc\u0627\u0646 \u0695\u06d5\u0686\u0627\u0648 \u06a9\u0631\u062f - \u06a9\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc\u0634 \u0626\u06d5\u0631\u06a9\u06cc \u0644\u06d5 \u0648\u0632\u06d5\u0628\u06d5\u062f\u06d5\u0631\u06cc \u0646\u0627\u062e\u06d5\u06cc\u0646\u06d5 \u0633\u06d5\u0631 - \u062f\u06d5\u06cc\u0627\u0646\u0628\u06d5\u06cc\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646.',
    '\u06a9\u06cc\u0646\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u0633\u06cc\u0646\u06d5\u06cc\u0627\u0646\u0627\u0646\u060c \u062f\u0627\u0645\u0627\u06b5\u06cc\u0648\u06d5 \u0648 \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u0627 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0626\u06ce\u0698\u0646: \u0633\u0648\u067e\u0627\u0633 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06ce \u06a9\u06d5 \u0626\u06ce\u0645\u06d5\u06cc \u0647\u06ce\u0646\u0627\u06cc\u06d5 \u0626\u06ce\u0631\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0646\u06d5\u06cc\u0647\u06ce\u0646\u0627\u0628\u0627\u06cc\u0646 \u0626\u06ce\u0645\u06d5 \u06a9\u0648\u0627 \u0695\u06ce\u0645\u0627\u0646 \u062f\u06d5\u0631 \u062f\u06d5\u06a9\u0631\u062f\u061b \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0647\u06d5\u0642 \u0647\u0627\u062a\u0646. [\u0644\u06d5\u0645 \u06a9\u0627\u062a\u06d5\u062f\u0627] \u06af\u0627\u0632\u06cc \u062f\u06d5\u06a9\u0631\u06ce\u0646: \u0626\u06d5\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5 \u067e\u06ce\u0648 \u062f\u0631\u0627\u0648\u06d5\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0627\u0631\u06ce\u06a9\u06d5 \u06a9\u06d5\u0648\u0627 \u06a9\u0631\u062f\u0648\u0648\u062a\u0627\u0646\u06d5.',
    '\u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u06cc\u06d5\u06a9\u0627\u0646 \u06af\u0627\u0632\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646: \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646 \u067e\u06ce\u06cc \u062f\u0627\u0648\u06cc\u0646 \u062c\u06ce \u0628\u06d5\u062c\u06ce \u0628\u0648\u0648\u061b \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5\u0634 - \u0648\u06d5\u06a9 \u0647\u06d5\u0695\u06d5\u0634\u06d5\u0648 \u0644\u06ce\u06a9\u0631\u0627 \u0628\u0648\u0648 - \u0647\u06d5\u0631\u0648\u0627 \u062f\u06d5\u0631\u0686\u0648\u0648\u061f \u0626\u06ce\u0698\u0646: \u0628\u06d5\u06b5\u06ce\u060c \u0628\u06d5 \u0633\u06d5\u0631\u0645\u0627\u0646 \u0647\u0627\u062a. \u0626\u06d5\u0648\u0633\u0627 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5\u0648 \u0646\u0627\u0648\u06d5\u062f\u0627 \u0628\u0627\u0646\u06af \u0695\u0627\u062f\u06ce\u06b5\u06ce: \u062f\u06d5\u06a9 \u0647\u06d5\u0631\u0686\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u06d5 \u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0648\u06ce\u061b',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0648\u0627\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646 \u06a9\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648 \u0628\u06d5 \u0695\u06c6\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u0646.',
    '\u067e\u06d5\u0631\u062f\u06d5\u06cc\u06d5\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648\u062f\u0627. \u067e\u06cc\u0627\u0648\u0627\u0646\u06ce\u06a9 \u0647\u06d5\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0639\u0631\u0627\u0641 \u0695\u0627\u0648\u06d5\u0633\u062a\u0627\u0648\u0646\u060c \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u06ce \u0628\u06d5 \u062a\u06d5\u0631\u062d \u0648 \u0628\u06cc\u0686\u0645 \u062f\u06d5\u0646\u0627\u0633\u0646\u061b \u062f\u06d5\u0646\u06af \u0644\u06d5\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u06cc\u0627\u0646\u06d5 \u062f\u06d5\u06a9\u06d5\u0646 - \u06a9\u06d5 \u0647\u06ce\u0634\u062a\u0627 \u0646\u06d5\u0686\u0648\u0648\u0646\u06d5\u062a\u06d5 \u0646\u06ce\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u0648\u06d5\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5 \u062a\u06d5\u0645\u0627\u0646 \u0628\u06cc\u0686\u0646\u06ce - \u0633\u06b5\u0627\u0648\u062a\u0627\u0646 \u0644\u06ce \u0628\u06ce.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0626\u0627\u0648\u0695 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u062f\u06d5\u062f\u06d5\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u062f\u06c6\u0698\u06d5\u0647\u06cc\u0646\u060c \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0644\u06d5 \u06af\u0631\u06c6\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u062f\u0648\u0648\u0631\u0645\u0627\u0646 \u0628\u06af\u0631\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0639\u0631\u0627\u0641\u0646 \u0644\u06d5\u0648 \u067e\u06cc\u0627\u0648\u0627\u0646\u06d5 - \u0648\u0627 \u0628\u06d5 \u0628\u06cc\u0686\u0645 \u062f\u06d5\u06cc\u0627\u0646\u0646\u0627\u0633\u0646 - \u0628\u0627\u0646\u06af \u062f\u06d5\u06a9\u06d5\u0646: \u06af\u0631\u062f\u06d5\u0648\u06d5 \u06a9\u06c6\u06cc\u06cc \u0648 \u0641\u06cc\u0632 \u0648 \u0644\u06d5 \u062e\u06c6\u0628\u0627\u06cc\u06cc \u0628\u0648\u0648\u0646\u062a\u0627\u0646 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0646\u06d5\u062f\u0627\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06cc\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0633\u0648\u06ce\u0646\u062f\u0648\u0648 \u062f\u06d5\u062e\u0648\u0627\u0631\u062f: \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627 \u0646\u0627\u06a9\u06d5\u0648\u0646\u061f \u062f\u06d5\u0633\u0627 \u0628\u0695\u06c6\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0648 \u0646\u06d5 \u0647\u06cc\u0686 \u062a\u0631\u0633\u0648\u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u06cc\u06d5 \u0646\u06d5 \u062e\u06d5\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u06cc\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u062e\u0648\u0627\u0632\u0646: \u0644\u06d5\u0648 \u0626\u0627\u0648\u06d5 \u06cc\u0627 \u0644\u06d5\u0648 \u0695\u06c6\u0632\u06cc\u06d5 \u062e\u0648\u0627 \u067e\u06ce\u062f\u0627\u0648\u0646 \u0634\u062a\u06ce\u06a9\u06cc\u0634 \u0628\u06d5\u0634\u06cc \u0645\u06d5 \u0628\u062f\u06d5\u0646. \u0626\u06ce\u0698\u0646: \u062e\u0648\u062f\u0627 \u0626\u06d5\u0645 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0634\u06cc \u0628\u06ce \u062f\u06cc\u0646\u0627\u0646 \u0646\u06d5\u062f\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u06af\u0627\u06b5\u062a\u06d5 \u0648 \u06af\u06d5\u067e \u062f\u06d5\u0632\u0627\u0646\u06cc \u0648 \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627 \u0641\u0631\u06cc\u0648\u06cc \u062f\u0627\u0646\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062a\u0648\u0648\u0634 \u0647\u0627\u062a\u0646\u06cc \u0626\u06d5\u0645\u0695\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u0626\u06ce\u0645\u06d5\u0634 \u0626\u06d5\u0645\u0695\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u062f\u06d5\u0628\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0648\u0627\u0646 \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u0645\u0627\u0646 \u0647\u0627\u0648\u0631\u062f\u0648\u0648\u06d5\u060c \u06a9\u06d5 \u0628\u06d5 \u0632\u0627\u0646\u0633\u062a\u06d5\u0648\u06d5 \u0695\u0648\u0648\u0646\u0645\u0627\u0646 \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5 \u0648 \u0695\u06ce\u0646\u0645\u0648\u0648\u0646\u06cc \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u06d5 \u0628\u06c6 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u06a9\u06d5 \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u062f\u06d5\u0695\u0648\u0627\u0646\u0646 \u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u062f\u0627\u060c \u0628\u06d5 \u0686\u0627\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06cc\u0628\u06cc\u0646\u0646\u061f \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0626\u0627\u06a9\u0627\u0645 \u0695\u0648\u0648\u0646 \u062f\u06d5\u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0648\u0627 \u0644\u06d5 \u067e\u06ce\u0634\u0627 \u0626\u06d5\u0645 \u0695\u06c6\u0698\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631\u06d5\u062e\u06c6 \u0628\u0631\u062f\u0628\u0648\u0648\u06d5\u0648\u06d5\u060c \u0626\u06ce\u0698\u0646: \u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u06af\u0648\u062a\u06cc\u0627\u0646\u060c \u0648\u06d5\u0695\u0627\u0633\u062a \u06af\u06d5\u0695\u0627\u061b \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0633\u062a\u06d5 \u06a9\u06d5\u0633 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u062a\u06a9\u0627\u06a9\u0627\u0631\u0645\u0627\u0646\u061f \u06cc\u0627\u062e\u06c6 \u062f\u06cc\u0633\u0627\u0646 \u0628\u06af\u06d5\u0695\u06ce\u06cc\u0646\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627\u060c \u062e\u062f\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u06af\u06c6\u0695\u06cc\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0628\u0627\u0634\u062a\u0631 \u0695\u06d5\u0686\u0627\u0648 \u06a9\u06d5\u06cc\u0646\u061f \u0626\u06d5\u0645\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06c6\u0695\u0627\u0646\u062f\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u062f\u0631\u06c6\u06cc \u06a9\u06d5 \u0647\u06d5\u06b5\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u0633\u062a - \u0633\u06d5\u0631 \u0644\u06d5 \u0628\u06d5\u0631\u06cc - \u0628\u06d5 \u0641\u06ce\u0695\u06c6 \u0686\u0648\u0648.',
    '\u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u062a\u0627\u0646 \u062e\u0648\u062f\u0627\u06cc\u06ce\u06a9\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0644\u06d5 \u0634\u06d5\u0634 \u0695\u06c6\u0698\u0627\u0646\u062f\u0627 \u062f\u0631\u0648\u0648\u0633 \u06a9\u0631\u062f. \u0626\u06d5\u0648 \u062c\u0627\u0631\u06d5\u06a9\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06cc \u062f\u0627\u0645\u06d5\u0632\u0631\u0627. \u0695\u06c6\u0698\u06ce \u0628\u06d5 \u0634\u06d5\u0648\u06ce \u062f\u06d5\u067e\u06c6\u0634\u06ce \u0648 \u0695\u06c6\u0698\u06cc\u0634 \u0628\u06d5\u0644\u06d5\u0632 \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u062a\u06d5\u0648\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0634\u06d5\u0648\u06ce. \u062e\u06c6\u0631 \u0648 \u0645\u0627\u0646\u06af \u0648 \u0626\u06d5\u0633\u062a\u06ce\u0631\u0627\u0646\u06cc \u0628\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0698\u06ce\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u062f\u0627\u0646. \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0646 \u0648 \u0695\u0627\u06af\u06d5\u06cc\u06cc\u0634\u062a\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5. \u0632\u06c6\u0631 \u0648 \u0632\u06d5\u0628\u06d5\u0646\u062f\u06d5\u06cc\u06d5 \u0648 \u062f\u0645\u06cc \u062e\u0648\u062f\u0627\u06cc\u06ce \u06a9\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u06af\u0634 \u062c\u06cc\u0647\u0627\u0646\u06d5.',
    '\u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0648 \u0628\u06d5 \u0633\u06d5\u0631\u06a9\u0632\u06cc \u0647\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u06ce \u0648 \u0644\u06d5 \u0628\u06d5\u0631\u06cc \u0628\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce \u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0626\u06d5\u0648 \u0644\u0627 \u062f\u06d5\u062f\u06d5\u0646.',
    '\u062e\u0631\u0627\u067e\u06d5\u0634 \u0645\u06d5\u06a9\u06d5\u0646 \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u062f\u0648\u0627\u06cc \u0628\u0627\u0634 \u0628\u0648\u0648\u0646\u06cc. \u0628\u06d5 \u062a\u0631\u0633\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u0647\u06cc\u0648\u0627\u06cc \u062a\u0648\u0648\u0634\u06cc \u0686\u0627\u06a9\u06d5 \u0628\u0648\u0648\u0646 \u0644\u06d5 \u0628\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0686\u0627\u06a9\u06d5 \u06a9\u0627\u0631\u0646\u060c \u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627 \u0646\u0632\u06cc\u06a9\u0646.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u067e\u06ce\u0634 \u0628\u0627\u0631\u0627\u0646\u06d5 - \u06a9\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06ce\u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 - \u0628\u0627\u06cc\u06d5\u06a9\u0627\u0646\u0648\u0648 \u0628\u06d5 \u0645\u0632\u06af\u06ce\u0646\u06cc \u0628\u06c6 \u0647\u06d5\u06b5 \u062f\u06d5\u06a9\u0627\u061b \u06a9\u0627\u062a\u06ce \u0626\u06d5\u0648 \u0628\u0627\u06cc\u0627\u0646\u06d5 \u0647\u06d5\u0648\u0631\u06cc \u0628\u0627\u0631\u06af\u0631\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0631\u0632\u06d5\u0648\u06d5 \u06a9\u0631\u062f\u060c \u0628\u06d5\u0631\u06d5\u0648 \u0648\u06b5\u0627\u062a\u06ce\u06a9\u06cc \u0645\u0631\u062f\u0648\u0648 \u062f\u06d5\u06cc\u062e\u06d5\u06cc\u0646\u06d5 \u0695\u06ce \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0626\u0627\u0648\u06cc \u067e\u06ce \u062f\u06d5\u0646\u06ce\u0631\u06cc\u0646\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u0626\u0627\u0648\u06d5\u06a9\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062c\u06c6\u0631\u06d5 \u0628\u06d5\u0631\u0648\u0628\u0648\u0648\u06cc \u0632\u06d5\u0645\u06cc\u0646 \u062f\u06d5\u0695\u0648\u06ce\u0646\u06cc\u0646. \u0647\u06d5\u0631 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5\u0634 \u0645\u0631\u062f\u0648\u0648 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u061b \u0634\u0627\u06cc\u06d5\u062a \u0626\u06ce\u0648\u06d5 \u0628\u06cc\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0632\u06d5\u0645\u06cc\u0646\u06cc \u0628\u0627\u0634 - \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062e\u06c6\u06cc - \u0695\u0648\u0648\u06d5\u06a9 \u0628\u06d5 \u0628\u0627\u0634\u06cc \u062f\u06d5\u0695\u0648\u06ce\u0646\u06ce. \u0632\u06d5\u0648\u06cc \u062e\u0631\u0627\u067e \u0647\u06d5\u0631 \u062f\u0695\u06a9 \u0648 \u062f\u0627\u06b5\u06cc \u0628\u06ce\u06a9\u0627\u0631\u06d5\u06cc \u0644\u06ce \u0634\u06cc\u0646 \u062f\u06d5\u0628\u06ce. \u0628\u06d5\u0645 \u062a\u06d5\u0631\u0632\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0628\u0627\u0633 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0633\u067e\u0627\u0633\u06a9\u0627\u0631\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0646\u0648\u0648\u062d\u0646 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5 \u0644\u0627\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc. \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u062f\u06d5\u0628\u06ce \u0626\u06ce\u0648\u06d5 \u0639\u06d5\u0628\u062f\u0627\u06cc\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646\u061b \u06a9\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0645 \u0626\u06ce\u0648\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u0645\u0646 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u0648 \u0644\u06ce \u062f\u06d5\u062a\u0631\u0633\u0645 \u06a9\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u0627\u0646\u06cc \u062f\u0698\u0648\u0627\u0631\u06cc \u062a\u06ce\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u067e\u06cc\u0627\u0648\u0645\u0627\u0642\u0648\u0648\u06b5\u0627\u0646\u06cc \u06af\u06d5\u0644\u06d5\u06a9\u06d5\u06cc [\u0644\u06d5 \u067e\u06d5\u0631\u0633\u06a4\u06cc \u0626\u06d5\u0648\u062f\u0627] \u06af\u0648\u062a\u06cc\u0627\u0646: \u0647\u06cc\u0686 \u06af\u0648\u0645\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0646\u06cc\u06cc\u06d5 \u062a\u06c6 \u062a\u0648\u0648\u0634\u06cc \u06af\u0648\u0645\u0695\u0627\u06cc\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u0648\u0648\u06af\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0645\u0646 \u06af\u0648\u0645\u0695\u0627 \u0646\u06cc\u0645\u061b \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06cc \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062f\u0646\u06cc\u0627\u0645.',
    '\u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u0646\u0645 \u0648 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0645 \u0648 \u0626\u06d5\u0648\u06cc \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0645\u0646 \u062f\u06d5\u06cc\u0632\u0627\u0646\u0645\u060c \u0626\u06ce\u0648\u06d5 \u0626\u0627\u06af\u0627\u062a\u0627\u0646 \u0644\u06ce \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0633\u06d5\u06cc\u0631 \u0628\u0648\u0648 \u06a9\u06d5 \u067e\u06d5\u0646\u062f\u06ce\u06a9 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0647\u0627\u062a \u0628\u06c6 \u067e\u06cc\u0627\u0648\u06ce\u06a9 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u061b \u0647\u06d5\u062a\u0627 \u0628\u062a\u0627\u0646\u062a\u0631\u0633\u06ce\u0646\u06ce\u062a \u0648 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0646\u061b \u0628\u06d5\u0634\u06a9\u06d5\u0645 \u0628\u06a9\u06d5\u0648\u0646\u06d5 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u061f',
    '\u0686\u06cc \u0648\u062a\u06cc \u0628\u06d5 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u062f\u0627\u0646\u0627 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u06cc \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648 \u0686\u0648\u0648\u0628\u0648\u0648\u0646\u06d5 \u0646\u0627\u0648 \u06af\u06d5\u0645\u06cc\u06d5\u06a9\u06d5\u0648\u06d5\u060c \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0644\u06d5 \u0626\u0627\u0648\u062f\u0627 \u0628\u06d5 \u062e\u0646\u06a9\u0627\u0646\u062f\u0646 \u062f\u0627\u060c \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u062f\u0631\u06c6 \u0628\u0648\u0648. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u062f\u06b5\u062f\u0627 \u06a9\u0648\u06ce\u0631 \u0628\u0648\u0648\u0646.',
    '\u0647\u0648\u0648\u062f\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u06c6 \u0647\u06c6\u0632\u06cc \u0639\u0627\u062f\u06cc \u0647\u0627\u0648\u062a\u06cc\u0631\u06d5\u06cc. \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0639\u06d5\u0628\u062f\u0627\u06cc\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646\u061b \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0626\u06ce\u0648\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u0686\u06cc\u062a\u0627\u0646\u06d5 \u0644\u06d5 \u062e\u0648\u0627 \u0646\u0627\u062a\u0631\u0633\u0646\u061f',
    '\u067e\u06cc\u0627\u0648\u0645\u0627\u0642\u0648\u0648\u06b5\u0627\u0646\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc - \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0646\u0627\u0633\u06cc - \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u062a\u06c6 \u0628\u06d5 \u0646\u06d5\u0641\u0627\u0645 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646 \u0648 \u067e\u06ce\u0645\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u062a\u06c6 \u0644\u06d5 \u062f\u0631\u06c6\u0632\u0646\u06d5\u06a9\u0627\u0646\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0646\u06d5\u0641\u0627\u0645 \u0646\u06cc\u0645\u060c \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u0645\u061b \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062f\u0646\u06cc\u0627 \u0695\u0627\u06cc \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u0645.',
    '\u067e\u06d5\u06cc\u0627\u0645\u06af\u06d5\u0644\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u0645. \u0645\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u0645\u06c6\u0686\u06cc\u0627\u0631\u06ce \u062f\u0631\u0648\u0648\u0633\u06a9\u0627\u0631\u0645.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0633\u06d5\u06cc\u0631 \u0628\u0648\u0648 \u06a9\u06d5 \u067e\u06d5\u0646\u062f\u06ce\u06a9 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0647\u0627\u062a \u0628\u06c6 \u067e\u06cc\u0627\u0648\u06ce\u06a9 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u060c \u0647\u06d5\u062a\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u0628\u0648\u0648\u062a\u0631\u0633\u06ce\u0646\u06ce\u061f \u0644\u06d5 \u0628\u06cc\u0631\u062a\u0627\u0646 \u0628\u06ce\u060c \u067e\u0627\u0634 \u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5 \u062c\u06ce \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0646\u062f\u0646 \u0648 \u0642\u06d5\u06b5\u0627\u0641\u06d5\u062a\u06cc \u0632\u06c6\u0631\u062a\u0631\u06cc\u0634\u06cc \u067e\u06ce\u062a\u0627\u0646 \u0628\u06d5\u062e\u0634\u06cc. \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0628\u06ce \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u062a\u0627\u0646\u06cc \u06a9\u0631\u062f\u060c \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u06a9\u06d5\u0646\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06af\u06d5\u0646\u06d5 \u0695\u0632\u06af\u0627\u0631\u06cc.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0647\u0627\u062a\u0648\u0648\u06cc [\u067e\u06ce\u0645\u0627\u0646 \u0628\u06ce\u0698\u06cc] \u062a\u06d5\u0646\u06cc\u0627 \u062e\u0648\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646 \u0648 \u0644\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646\u06d5 \u0648\u0627\u0632 \u0628\u06ce\u0646\u06cc\u0646 \u06a9\u06d5 \u0628\u0627\u0628 \u0648\u0628\u0627\u067e\u06cc\u0631\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0627\u0646\u0646\u061f \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0644\u06d5 \u0695\u0627\u0633\u062a \u0628\u06ce\u0698\u0627\u0646\u06cc\u060c \u0628\u06d5 \u0647\u06d5\u0631\u0686\u06cc\u0645\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u06ce\u0646\u06cc \u067e\u06ce\u06a9\u06cc \u0628\u06ce\u0646\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0628\u06ce \u0633\u06c6 \u0626\u06ce\u0648\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631 \u0648 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646 \u0628\u0648\u0648\u06af\u0646. \u062c\u0627 \u0686\u06c6\u0646 \u0626\u06ce\u0648\u06d5 \u0628\u06c6 \u0686\u06d5\u0646\u062f \u0634\u062a\u06ce \u06a9\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0648 \u0628\u0627\u0648\u0627\u0646\u062a\u0627\u0646 \u0646\u0627\u0648\u062a\u0627\u0646 \u0644\u06ce \u0646\u0627\u0648\u0646 \u0648 \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u062a\u06d5\u0648\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06c6 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u062c\u0648\u0627\u0628\u06d5\u062c\u06d5\u0646\u06af\u06cc\u0645 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u06d5\u0646\u061f \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646\u061b \u0626\u06d5\u0648\u0627 \u0645\u0646\u06cc\u0634 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0645.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0686\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5\u0648\u06d5 \u0647\u0648\u0648\u062f \u0648 \u0647\u0627\u0648\u0695\u06ce\u06a9\u0627\u0646\u06cc \u0647\u0648\u0648\u062f\u0645\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627 \u0648 \u0628\u06ce \u0628\u0695\u0648\u0627 \u0628\u0648\u0648\u0646\u060c \u06cc\u06d5\u06a9 \u062c\u06ce \u0628\u0646\u06d5\u0628\u0695\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646.',
    '\u0628\u06c6 \u0644\u0627\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f\u06cc\u0634 \u0633\u0627\u06b5\u062d\u06cc \u0628\u0631\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0695\u06ce \u06a9\u0631\u062f. \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0639\u06d5\u0628\u062f\u0627\u06cc\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646\u060c \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0626\u06ce\u0648\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u0626\u06d5\u0648\u0627 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648\u0695\u0627 \u0626\u06d5\u0645 \u0648\u0634\u062a\u0631\u06d5\u0648 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5\u061b \u0644\u06ce \u06af\u06d5\u0695\u06ce\u0646 \u0628\u0627 \u0644\u06d5 \u0632\u06d5\u0648\u06cc \u062e\u0648\u0627\u062f\u0627 \u0628\u0686\u06d5\u0631\u06ce\u060c \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646\u06ce\u06a9\u0648\u0648 \u0628\u06c6\u06cc \u0646\u06d5\u0628\u06ce\u061b \u062f\u06d5\u0646\u0627 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646 \u062f\u06d5\u0628\u0646.',
    '\u0644\u06d5 \u0628\u06cc\u0631\u062a\u0627\u0646 \u0628\u06ce \u067e\u0627\u0634 \u0647\u06c6\u0632\u06cc \u0639\u0627\u062f \u0626\u06ce\u0648\u06d5\u06cc \u06a9\u0631\u062f\u06d5 \u062c\u06ce\u0646\u0634\u06cc\u0646 \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646 \u062f\u0627\u06cc \u0645\u06d5\u0632\u0631\u0627\u0646\u062f\u0646\u061b \u0644\u06d5 \u067e\u06ce \u062f\u06d5\u0634\u062a\u0627\u0646 \u06a9\u06c6\u0634\u06a9\u0627\u0646\u062a\u0627\u0646 \u0628\u06d5\u0631\u0632 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u06a9\u06ce\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0634\u062f\u0627 \u062e\u0627\u0646\u0648\u0648\u062a\u0627\u0646 \u062f\u06d5\u062a\u0627\u0634\u06cc. \u0646\u06cc\u0639\u0645\u06d5\u062a\u06cc \u062e\u0648\u0627\u062a\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u06ce \u0648 \u067e\u06ce \u062f\u0627\u0645\u06d5\u06af\u0631\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0644\u06d5 \u0647\u06d5\u0631\u062f\u0627.',
    '\u0633\u06d5\u0631\u06af\u06d5\u0648\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc - \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062e\u06c6 \u0628\u0627\u06cc\u06cc \u0628\u0648\u0648\u0646 - \u0644\u0627\u06cc \u06a9\u06d5\u0645 \u062f\u06d5\u0633\u062a \u0648 \u0647\u06d5\u0698\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0628\u0648\u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u062f\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0633\u0627\u06b5\u06d5\u062d \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u06d5\u0648\u06d5 \u0695\u0627\u0633\u067e\u06ce\u0631\u0627\u0648\u06d5\u061f \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u06d5.',
    '\u062e\u06c6 \u0628\u06d5 \u0632\u0644 \u0632\u0627\u0646\u06d5\u06a9\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648\u0627\u0632\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u0648\u0634\u062a\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u067e\u06d5\u0644 \u067e\u06d5\u0644 \u06a9\u0631\u062f. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0633\u0627\u06b5\u062d! \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u060c \u0686\u06c6\u0646\u062a \u0647\u06d5\u0695\u06d5\u0634\u06d5 \u0644\u06ce \u06a9\u0631\u062f\u06cc\u0646 \u0628\u06d5 \u062c\u06ce\u06cc \u0628\u06ce\u0646\u06d5.',
    '\u0632\u06c6\u0631 \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u0628\u0648\u0648\u0645\u06d5\u0644\u06d5\u0631\u0632\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u061b \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648 \u0645\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0645\u0631\u062f\u0646.',
    '\u0633\u0627\u06b5\u062d \u0644\u06d5\u0648\u0627\u0646 \u062f\u0648\u0648\u0631 \u06a9\u06d5\u0648\u062a\u06d5\u0648\u06d5\u060c \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0645\u0646 \u067e\u06d5\u06cc\u0627\u0645\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u0645 \u067e\u06ce \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u060c \u0632\u06c6\u0631\u06cc\u0634\u0645 \u0646\u0633\u06ce\u062d\u06d5\u062a \u06a9\u0631\u062f\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u0645\u06c6\u0686\u06cc\u0627\u0631\u0627\u0646\u062a\u0627\u0646 \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce.',
    '\u0644\u0648\u0648\u062a\u06cc\u0634\u0645\u0627\u0646 \u0646\u0627\u0631\u062f. \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0626\u06ce\u0648\u06d5 \u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0634\u0648\u0648\u0631\u06d5\u06cc\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06d5\u0631 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u06a9\u06d5\u0633 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0646\u06d5\u06cc\u06a9\u0631\u062f\u0648\u0648\u06d5\u061f',
    '\u0626\u06ce\u0648\u06d5 \u0628\u06c6 \u062e\u06c6 \u0695\u06d5\u062d\u06d5\u062a \u06a9\u0631\u062f\u0646 - \u0644\u06d5 \u0628\u0631\u06cc \u0698\u0646\u0627\u0646 - \u067e\u06d5\u0644\u0627\u0645\u0627\u0631\u06cc \u067e\u06cc\u0627\u0648\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646. \u0695\u0627\u0633\u062a\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06c6 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u0648 \u062f\u06d5\u0631\u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0644\u0648\u0648\u062a \u0648\u06d5\u0631\u0627\u0645\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0628\u0648\u0648: \u0644\u06d5 \u0634\u0627\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0631\u06cc\u0627\u0646 \u06a9\u06d5\u0646\u061b \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06a9\u06d5\u0633\u06af\u06d5\u0644\u06ce\u06a9\u0646 \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0628\u06d5 \u067e\u0627\u06a9 \u0648 \u062e\u0627\u0648\u06ce\u0646\u06cc \u0628\u0698\u06cc\u0646!',
    '\u0626\u06cc\u062a\u0631 \u062e\u06c6\u06cc \u0648 \u06a9\u06d5\u0633 \u0648\u06a9\u0627\u0631\u06cc\u0645\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f\u061b \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u0698\u0646\u06d5\u06a9\u06d5\u06cc \u0646\u06d5\u0628\u06ce\u060c \u06a9\u06d5 \u06a9\u06d5\u0648\u062a\u06d5 \u062a\u06d5\u06a9 \u0628\u06d5 \u062c\u06ce\u0645\u0627\u0648\u0627\u0646.',
    '\u0628\u0627\u0631\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0628\u0627\u0631\u0627\u0646\u062f\u0646 \u0626\u06d5\u0645\u0645\u0627 \u0628\u0627\u0631\u0627\u0646! \u062f\u06d5 \u0628\u0695\u0648\u0627\u0646\u06d5 \u067e\u0627\u0634\u06d5 \u0695\u06c6\u0698\u06cc \u0626\u06d5\u0648 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06d5 \u0686\u06c6\u0646 \u0628\u0648\u0648!',
    '\u0628\u06c6 \u00ab\u0645\u06d5\u062f\u06cc\u06d5\u0646\u00bb \u06cc\u0634 \u0634\u0648\u0639\u06d5\u06cc\u0628\u0646 \u0646\u0627\u0631\u062f\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u06cc\u0634 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0648\u0648. \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0639\u06d5\u0628\u062f\u0627\u06cc\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646\u060c \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0626\u06ce\u0648\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u062a\u0627\u0646 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5. \u0628\u0627 \u067e\u06ce\u0648\u0627\u0646\u06d5 \u0648 \u062a\u06d5\u0631\u0627\u0632\u0648\u0648\u062a\u0627\u0646 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06ce \u0648 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06d5\u06b5\u06a9 \u0645\u06d5\u062f\u0632\u0646\u06d5\u0648\u06d5. \u0644\u06d5 \u0647\u06d5\u0631\u062f\u06cc\u0634\u0627 - \u062f\u0648\u0627\u06cc \u0626\u0627\u0648\u06d5\u062f\u0627\u0646 \u06a9\u0631\u062f\u0646\u06cc - \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0645\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u06af\u0631 \u0628\u0695\u0648\u0627\u0648 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u0628\u06ce \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0634\u062a\u0631\u06d5.',
    '\u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0695\u06ce\u06af\u0627\u06cc\u06d5\u06a9\u0627 \u062f\u0627\u0645\u06d5\u0646\u06cc\u0634\u0646 \u0628\u06c6 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631\u0647\u06ce\u0646\u0627\u0646\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0648\u0627 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0628\u06c6 \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0626\u06d5\u0648 \u0644\u0627\u06cc\u0627\u0646\u062f\u06d5\u0646 \u0648 \u0695\u06ce\u06af\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0627\u0646 \u0628\u06d5 \u0644\u0627\u0631 \u0648 \u0686\u06d5\u0641\u062a \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u0646. \u0644\u06d5 \u0628\u06cc\u0631\u062a\u0627\u0646 \u0628\u06ce \u0626\u06ce\u0648\u06d5 \u06a9\u06d5\u0645 \u0628\u0648\u0648\u0646\u060c \u0632\u06c6\u0631\u06cc \u06a9\u0631\u062f\u0646\u061b \u0644\u06ce \u0628\u0695\u0648\u0627\u0646\u0646 \u067e\u0627\u0634\u06d5 \u0695\u06c6\u0698\u06cc \u0628\u06d5\u062f\u0641\u06d5\u0695\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u06a9\u0648\u06ce \u06af\u06d5\u06cc\u06cc\u0634\u062a.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0628\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0645\u0646\u06cc \u067e\u06ce \u0695\u0627\u0633\u067e\u06ce\u0631\u0627\u0648\u06d5 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u062e\u06c6 \u0695\u0627\u0628\u06af\u0631\u0646 \u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u0644\u06d5 \u0628\u06d5\u06cc\u0646\u06cc \u0626\u06ce\u0645\u06d5\u062f\u0627 \u062f\u0627\u0648\u06d5\u0631\u06cc \u06a9\u0627. \u062e\u06c6\u0634\u06cc \u0686\u0627\u062a\u0631\u06cc\u0646 \u062f\u0627\u0648\u06d5\u0631\u06d5.',
    '\u06af\u0631\u06d5\u06af\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0647\u06c6\u0632\u06cc - \u06a9\u06d5 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0627\u06cc\u06cc \u0628\u0648\u0648 \u0628\u0648\u0648\u0646 - \u06af\u0648\u062a\u06cc\u0627\u0646: \u0634\u0648\u0639\u06d5\u06cc\u0628! \u0626\u06ce\u0645\u06d5 \u062a\u06c6\u0634 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0648\u06d5\u0644 \u062a\u06c6 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0644\u06d5 \u0634\u0627\u0631\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062f\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u06cc\u0646\u061b \u06cc\u0627\u0646 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u062f\u06cc\u0646\u06cc \u0645\u06d5. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632\u06cc\u0634 \u0646\u06d5\u06a9\u06d5\u06cc\u0646\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u06cc\u0646\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u062f\u06cc\u0646\u06cc \u0626\u06ce\u0648\u06d5\u060c \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06ce\u06cc \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f\u06cc\u0646\u060c \u062f\u0631\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u0648\u0648\u06d5. \u0695\u06d5\u0648\u0627 \u0646\u06cc\u06cc\u06d5 \u0626\u06cc\u062a\u0631 \u0628\u06af\u06d5\u0695\u06cc\u0646\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0626\u0627\u06cc\u06cc\u0646\u06d5\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce\u061b \u0632\u0627\u0646\u0633\u062a\u06cc \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u062f\u0627\u06af\u0631\u062a\u06c6\u062a\u06d5\u0648\u06d5\u061b \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5. \u0626\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u062a\u06c6 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u0645\u0627\u0646\u062f\u0627 \u062f\u0627\u0648\u06d5\u0631\u06cc \u0628\u06a9\u06d5\u061b \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u062f\u0627\u0648\u06d5\u0631 \u0647\u06d5\u0631 \u062e\u06c6\u062a\u06cc.',
    '\u0633\u06d5\u0631\u0632\u0644\u06d5\u06a9\u0627\u0646\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc - \u06a9\u06d5 \u0648\u0627 \u0644\u06d5 \u062f\u06cc\u0646 \u0644\u0627\u06cc\u0627\u0646 \u062f\u0627\u0628\u0648\u0648 - \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0634\u0648\u0639\u06d5\u06cc\u0628\u06ce \u0628\u06a9\u06d5\u0646\u060c \u0628\u06ce \u0633\u06c6 \u062a\u0648\u0648\u0634\u06cc \u0632\u06d5\u0631\u06d5\u0631 \u062f\u06d5\u0628\u0646.',
    '\u0626\u06cc\u062a\u0631 \u0626\u06d5\u0648\u0633\u0627 \u0628\u0648\u0648\u0645\u06d5\u0644\u06d5\u0631\u0632\u06d5\u06cc \u0648\u0627 \u0628\u06d5 \u062a\u06d5\u0648\u0698\u0645 \u062f\u0627\u06cc \u06af\u0631\u062a\u0646\u060c \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0686\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc \u0634\u0648\u0639\u06d5\u06cc\u0628\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627\u060c [\u0648\u0627 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0686\u0648\u0648\u0646] \u062f\u06d5\u062a\u06af\u0648\u062a \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc \u0634\u0648\u0639\u06d5\u06cc\u0628\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627\u060c \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631 \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0634\u0648\u0639\u06d5\u06cc\u0628 \u0695\u0648\u0648\u06cc \u0644\u06ce \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u0646 \u0648 \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0645\u0646 \u067e\u06d5\u06cc\u0627\u0645\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u06d5 \u0648 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u0634\u0645 \u0644\u06ce\u06a9\u0631\u062f\u0646. \u062f\u06d5\u0633\u0627 \u0686\u0644\u06c6\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646\u060c \u062e\u06d5\u0645\u06ce \u062f\u06d5\u062e\u06c6\u0645\u061f',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9 \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u0628\u06d5 \u0646\u06d5\u062e\u06c6\u0634\u06cc \u0648 \u0646\u0627\u06af\u0648\u0632\u0648\u0648\u0631\u06cc \u0628\u06d5 \u062a\u0627\u0642\u06cc\u0645\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u0646\u06d5\u0648\u06d5\u061b \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0628\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u062c\u0627\u0631\u06cc \u062f\u0648\u0627\u06cc\u06cc \u0646\u0627\u062e\u06c6\u0634\u06cc\u0645\u0627\u0646 \u0628\u06d5 \u062e\u06c6\u0634\u06cc \u0628\u06c6 \u06af\u06c6\u0695\u06cc\u0648\u0646\u06d5\u0648\u06d5\u060c \u062a\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc\u0627\u0646 \u0632\u06ce\u062f\u06d5 \u0628\u0648\u0648. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u06cc\u0634\u0645\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u062e\u06c6\u0634\u06cc \u0648 \u0646\u0627\u062e\u06c6\u0634\u06cc\u0627\u0646 \u0628\u0648\u0648\u0646. \u0626\u06cc\u062a\u0631 \u0644\u06d5 \u067e\u0695 - \u0628\u06d5\u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0633\u062a \u067e\u06ce \u0628\u06a9\u06d5\u0646 - \u06af\u0631\u062a\u0645\u0627\u0646\u0646.',
    '\u0646\u06cc\u0634\u062a\u06d5\u062c\u06ce\u06cc \u0626\u06d5\u0648 \u0628\u0627\u0698\u06ce\u0695\u0627\u0646\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627\u06cc\u0627\u0646 \u0628\u0646\u0627\u0633\u06cc\u0628\u0627 \u0648 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632 \u0628\u0627\u0646\u060c \u062f\u06d5\u0631\u0648\u0627\u0632\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u06a9\u06d5\u062a\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0648\u06d5 \u0628\u06d5 \u0695\u0648\u0648\u06cc\u0627\u0646\u062f\u0627 \u062f\u06d5\u06a9\u0631\u062f\u06d5\u0648\u06d5. \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0627\u0646\u06cc\u061b \u0644\u06d5 \u0633\u0632\u0627\u06cc \u0626\u06d5\u0645 \u0626\u0627\u06a9\u0627\u0631\u06d5\u06cc\u0627\u0646 \u06af\u0631\u062a\u0645\u0627\u0646\u0646.',
    '\u0626\u0627\u062e\u06c6 \u062e\u06d5\u06b5\u06a9\u06cc \u0626\u06d5\u0648 \u0634\u0627\u0631\u0627\u0646\u06d5 \u0646\u06d5\u062a\u0631\u0633\u0627 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0644\u06d5 \u0634\u06d5\u0648\u062f\u0627\u060c \u0628\u06d5\u06b5\u0627\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634 \u0628\u0628\u06ce \u0648 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u062c\u06d5\u0646\u06af\u06d5\u06cc \u062e\u06d5\u0648\u062f\u0627 \u0628\u0646\u061f',
    '\u0626\u06d5\u0648 \u0634\u0627\u0631\u0646\u0634\u06cc\u0646\u0627\u0646\u06d5 \u062f\u0627\u062e\u06c6 \u0646\u06d5\u062a\u0631\u0633\u0627 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0644\u06d5 \u062f\u06d5\u0645\u06cc \u0686\u06ce\u0634\u062a\u06d5\u0646\u06af\u0627\u0648\u062f\u0627 \u0648 \u0644\u06d5 \u062c\u06d5\u0646\u06af\u06d5\u06cc \u06af\u06d5\u0645\u06d5 \u06a9\u0631\u062f\u0646\u062f\u0627\u060c \u0626\u06ce\u0645\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627\u06cc\u0627\u0646 \u06a9\u06d5\u06cc\u0646\u061f',
    '\u0626\u0627\u062e\u06c6 \u0644\u06d5\u0648\u06d5 \u062f\u06b5\u0646\u06cc\u0627 \u0628\u0648\u0648\u0646 \u0644\u06d5 \u0645\u06d5\u06a9\u0631\u06cc \u062e\u0648\u062f\u0627 \u0695\u0632\u06af\u0627\u0631\u0646\u061f \u062e\u06c6 \u0628\u06d5 \u063a\u06d5\u06cc\u0631 \u0644\u06d5 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u060c \u06a9\u06d5\u0633 \u0644\u06d5 \u0645\u06d5\u06a9\u0631\u06cc \u062e\u0648\u0627 \u0647\u06d5\u0633\u062a\u06cc \u0647\u06ce\u0645\u0646\u06cc \u0646\u0627\u06a9\u0627.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0645 \u0639\u06d5\u0631\u0632\u06d5\u06cc\u0627\u0646 - \u067e\u0627\u0634 \u0646\u06d5\u0645\u0627\u0646\u06cc \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc - \u0628\u06d5 \u0645\u06cc\u0631\u0627\u062a \u0628\u06c6 \u062f\u06d5\u0645\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u06cc\u0648\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632 \u06a9\u06d5\u06cc\u0646 - \u0628\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u06ce \u06a9\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646 - \u062a\u06c6\u06b5\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0645\u06c6\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u062f\u06b5 \u062f\u0627\u062f\u06d5\u0646\u06ce\u06cc\u0646\u061b \u06a9\u06d5\u0647\u06d5\u0631 \u0646\u06d5\u062a\u0648\u0627\u0646\u0646 \u0628\u0628\u06cc\u0633\u0646\u061f',
    '\u0626\u06ce\u0645\u06d5 \u0628\u0695\u06ce\u06a9 \u0644\u06d5 \u0646\u06d5\u0642\u06b5\u06cc \u0626\u06d5\u0648 \u0628\u0627\u0698\u06ce\u0695\u0627\u0646\u06d5\u062a \u0628\u06c6 \u0628\u0627\u0633 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u061b \u0695\u0627\u0633\u062a \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0648\u06d5 \u0647\u0627\u062a\u06d5\u0644\u0627\u06cc\u0627\u0646\u061b \u0648\u06d5\u06a9 \u0644\u06d5\u067e\u06ce\u0634\u062f\u0627 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u060c \u0647\u06d5\u0631\u0648\u0627 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0645\u0627\u0646\u06d5\u0648\u06d5. \u0626\u0627\u0648\u0627 \u062e\u0648\u062f\u0627 \u0645\u06c6\u0631 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u062f\u06b5\u06cc \u0644\u06d5 \u062f\u06cc\u0646 \u0644\u0627\u062f\u06d5\u0631\u0627\u0646.',
    '\u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0645\u0627\u0646 \u0646\u06d5\u062f\u06cc \u06a9\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u062e\u06c6 \u067e\u06ce\u06a9 \u0628\u06ce\u0646\u0646\u061b \u0632\u06c6\u0631\u06cc\u0646\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u067e\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0645\u0648\u0648\u0633\u0627\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0633\u06d5\u0631\u0632\u0644\u06d5\u06a9\u0627\u0646\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc\u060c \u062f\u0698\u06cc\u0627\u0646 \u0648\u06ce\u0633\u062a\u0627\u0646. \u0633\u0627 \u0628\u0695\u0648\u0627\u0646\u06d5 \u067e\u0627\u0634\u06d5 \u0695\u06c6\u0698\u06cc \u0626\u06d5\u0648 \u0628\u06d5\u062f\u0641\u06d5\u0695\u0627\u0646\u06d5 \u0686\u0644\u06c6\u0646 \u0628\u0648\u0648.',
    '\u0645\u0648\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646! \u0645\u0646 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u0645 \u06a9\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u06af\u0634\u062a \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0648\u0627 \u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0644\u06d5 \u0695\u0627\u0633\u062a \u067e\u06ce\u0648\u06d5\u060c \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0646\u06d5\u06b5\u06ce\u0645. \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u0695\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u0645 \u0628\u06c6\u062a\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5. \u062a\u06c6 \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u0641\u0631\u0632\u06d5\u0646\u062f\u0627\u0646\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644 \u0648\u06ce\u0695\u0627\u06cc \u0645\u0646 \u06a9\u0644 \u06a9\u06d5\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc\u062a\u06d5 \u0648 \u0647\u06cc\u0686 \u0646\u06cc\u0634\u0627\u0646\u06d5\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06c6\u062a\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0646\u06cc\u0634\u0627\u0646\u06cc \u0628\u062f\u06d5.',
    '\u0645\u0648\u0648\u0633\u0627 \u0639\u0627\u0633\u0627\u06a9\u06d5\u06cc \u0641\u0695\u06ce \u062f\u0627\u061b \u06a9\u06d5\u0686\u06cc \u0644\u06d5 \u067e\u0695 \u0628\u0648\u0648 \u0628\u06d5 \u0645\u0627\u0631\u06ce\u06a9 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627.',
    '\u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6\u0634\u06cc \u0644\u06d5 \u0628\u0627\u062e\u06d5\u06b5\u06cc\u06d5\u0648\u06d5 \u0648\u06d5\u062f\u06d5\u0631\u062e\u0633\u062a\u061b \u0644\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u0648\u06cc \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u0627\u0646 \u0633\u067e\u06cc\u06d5\u06a9\u06cc \u0628\u06ce \u062e\u06d5\u0648\u0634 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a.',
    '\u0633\u06d5\u0631\u0632\u0644\u0627\u0646\u06cc \u0647\u06c6\u0632\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u062c\u0627\u062f\u0648\u06af\u06d5\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0632\u0627\u0646\u06d5.',
    '\u0648\u0627\u06cc \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u06af\u0634\u062a\u0648\u0648 \u0644\u06d5\u0645 \u0648\u06b5\u0627\u062a\u06d5 \u062f\u06d5\u0631\u06a9\u0627\u060c \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5 \u0686\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062a\u06c6 \u062e\u06c6\u06cc \u0648 \u0628\u0631\u0627\u06a9\u06d5\u06cc \u06af\u0644 \u0628\u062f\u06d5\u0648\u06d5\u061b \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u06cc \u06a9\u06c6\u06a9\u06d5\u0631\u06d5\u0648\u06d5\u0634 \u0628\u06d5\u0695\u06ce \u0628\u06a9\u06d5 \u0628\u06c6 \u0634\u0627\u0631\u06d5\u06a9\u0627\u0646.',
    '\u062a\u0627 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u0627\u0646\u06cc \u0632\u06c6\u0631 \u0632\u0627\u0646 \u0628\u06ce\u0646\u0646 \u0628\u06c6 \u0644\u0627\u062a.',
    '\u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u06a9\u06c6 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5. \u06af\u0648\u062a\u06cc\u0627\u0646: \u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u06cc\u0646\u060c \u062a\u06c6 \u062f\u06d5\u0628\u06ce \u062e\u06d5\u06b5\u0627\u062a\u0645\u0627\u0646 \u0628\u06a9\u06d5\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0628\u06d5\u06b5\u06ce\u060c \u0626\u06ce\u0648\u06d5 \u0628\u06ce \u0633\u06c6 \u0644\u06d5 \u0646\u0632\u06cc\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0645 \u062f\u06d5\u0628\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0645\u0648\u0648\u0633\u0627! \u062a\u06c6 \u062f\u0627\u0648\u06ce\u0698\u06cc \u06cc\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u0634 \u062a\u06c6 \u0628\u0647\u0627\u0648\u06ce\u06cc\u0646\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u0628\u0647\u0627\u0648\u06ce\u0698\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0647\u0627\u0648\u06cc\u062a\u06cc\u0627\u0646\u060c \u0686\u0627\u0648\u06cc \u062d\u06d5\u0634\u0627\u0645\u06d5\u062a\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u062e\u06d5\u06b5\u06d5\u062a\u0627 \u0648 \u0644\u06ce\u06cc\u0627\u0646 \u062a\u0631\u0633\u0627\u0646 \u0648 \u062c\u0627\u062f\u0648\u0648\u06cc \u0632\u06c6\u0631 \u0639\u0627\u062c\u0628\u0627\u062a\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u062e\u0633\u062a\u0645\u0627\u0646\u06d5 \u062f\u06b5\u06cc \u0645\u0648\u0648\u0633\u0627\u0648\u06d5\u060c \u06a9\u06d5: \u0639\u0627\u0633\u0627\u06a9\u06d5\u062a \u0628\u0647\u0627\u0648\u06ce\u0698\u06d5. \u0647\u06d5\u0631 \u0644\u06d5 \u062c\u06ce\u0648\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062c\u0627\u062f\u0648\u0648\u06a9\u0627\u0631\u06cc\u06cc\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u0645\u0627\u0634\u06cc \u0648 \u0647\u06d5\u06b5 \u0644\u0648\u0648\u0634\u06cc.',
    '\u0626\u06cc\u062a\u0631 \u06a9\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0647\u0627\u062a\u06d5 \u0695\u0648\u0648\u060c \u067e\u0695\u0648\u067e\u0648\u0648\u0686\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0686\u0631\u0648\u0648\u06a9 \u062f\u06d5\u0631\u0686\u0648\u0648.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u062a\u06ce\u0634\u06a9\u0627\u0646 \u0648 \u0628\u06d5 \u06a9\u0632\u06cc \u0648 \u0632\u06d5\u0628\u0648\u0648\u0646\u06cc \u062f\u0627\u0645\u0627\u0646.',
    '\u0647\u06d5\u0631\u0686\u06d5\u0646\u062f\u06cc \u062c\u0627\u062f\u0648\u06af\u06d5\u0631 \u0647\u06d5\u0628\u0648\u0648\u0646\u060c \u06a9\u0695\u0646\u06c6\u0634\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0648\u0627 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0628\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u06af\u0634 \u062f\u0646\u06cc\u0627\u061b',
    '\u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u0627\u0631\u0648\u0648\u0646.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648\u062a\u06cc: \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0645\u0646 \u0626\u06cc\u0632\u0646\u0648\u0648 \u0628\u062f\u06d5\u0645\u060c \u0626\u06ce\u0648\u06d5 \u0686\u06c6\u0646 \u0628\u0695\u0648\u0627\u0648 \u067e\u06ce \u0647\u06ce\u0646\u0627\u061f \u0626\u06ce\u0648\u06d5\u06cc \u0641\u06ce\u06b5\u0628\u0627\u0632 \u0648\u06cc\u0633\u062a\u0648\u0648\u062a\u0627\u0646\u06d5 \u0644\u06d5\u0645 \u0634\u0627\u0631\u06d5\u062f\u0627 \u0641\u06ce\u06b5\u06ce\u06a9 \u0628\u06a9\u06d5\u0646 \u062a\u0627 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u0644\u06ce \u0648\u06d5\u062f\u06d5\u0631\u0646\u06ce\u0646. \u0633\u0627 \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u062f\u0627 \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u067e\u06ce\u0686\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0633\u062a \u0648 \u067e\u06ce\u06cc\u06d5\u06a9\u0627\u0646\u0648\u0648 \u062f\u06d5\u0628\u0695\u0645. \u0644\u06ce\u0634\u062a\u0627\u0646 \u0695\u0648\u0648\u0646 \u0628\u06ce \u06af\u0634\u062a\u0648\u0648 \u0644\u06d5 \u0642\u06d5\u0646\u0627\u0631\u06d5 \u062f\u06d5\u062f\u06d5\u0645.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u0686\u06cc\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646.',
    '\u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u0695\u06a9\u062a \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0633\u062a\u0627\u0648\u06d5\u0648 \u0633\u0632\u0627\u0645\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u060c \u0686\u0648\u0646 \u06a9\u0627\u062a\u06ce \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0647\u0627\u062a\u0646\u060c \u062a\u06d5\u0648\u0627\u0648 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0646. \u0626\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u062e\u06c6\u0695\u0627\u06af\u0631\u06cc\u0645\u0627\u0646 \u0628\u062f\u06d5\u06cc\u06d5 \u0648 \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u067e\u0627\u06a9\u06d5\u0648\u06d5 \u0628\u0645\u0627\u0646 \u0645\u0631\u06ce\u0646\u06d5.',
    '\u0633\u06d5\u0631\u06af\u06d5\u0648\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0686\u06c6\u0646 \u062a\u06c6 \u0644\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0648\u0627\u0632 \u062f\u06d5\u0647\u06ce\u0646\u06cc\u060c \u0648\u06b5\u0627\u062a\u062a \u0644\u06ce \u0628\u0634\u06ce\u0648\u06ce\u0646\u0646 \u0648 \u062f\u06d5\u0633 \u0644\u06d5 \u062e\u06c6\u062a \u0648 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u06d5\u06a9\u0627\u0646\u06cc \u062a\u06c6 \u0628\u06d5\u0631\u062f\u06d5\u0646\u061f \u06af\u0648\u062a\u06cc: \u0628\u06d5\u0645 \u0632\u0648\u0648 \u06a9\u0648\u0695\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0648\u0698\u06cc\u0646 \u0648 \u0698\u0646\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 [\u0628\u06c6 \u0695\u0627\u0698\u06d5] \u062f\u06d5\u0647\u06ce\u06b5\u06cc\u0646\u06d5\u0648\u06d5. \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0632\u0627\u06b5\u06cc\u0646.',
    '\u0645\u0648\u0648\u0633\u0627 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0647\u06d5\u0631 \u067e\u0634\u062a \u0628\u06d5 \u062e\u0648\u0627 \u0628\u0628\u06d5\u0633\u062a\u0646 \u0648 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0646\u061b \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0647\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u0628\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u067e\u06ce\u06cc \u062f\u06d5\u0628\u06d5\u062e\u0634\u06ce \u0648 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0634 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u06a9\u06d5 \u062a\u06c6 \u0628\u06ce\u06cc\u060c \u0686\u06d5\u0648\u0633\u0627\u06cc\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u062a\u06c6\u0634 \u0647\u0627\u062a\u0648\u0648\u06cc\u060c \u0647\u06d5\u0631\u0648\u0627 \u062f\u06d5\u0645\u0627\u0646 \u0686\u06d5\u0648\u0633\u06ce\u0646\u0646\u06d5\u0648\u06d5. \u06af\u0648\u062a\u06cc: \u062f\u0648\u0648\u0631 \u0646\u06cc\u06cc\u06d5 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062f\u0648\u0698\u0645\u0646\u062a\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0628\u06d5\u0631\u06ce \u0648 \u0626\u06ce\u0648\u06d5 \u0628\u06a9\u0627\u062a\u06d5 \u062c\u06ce\u0646\u0634\u06cc\u0646 \u0644\u06d5\u0645 \u0639\u06d5\u0631\u0632\u06d5\u062f\u0627 \u0648 \u0628\u0695\u0648\u0627\u0646\u06ce \u0626\u06ce\u0648\u06d5 \u0686\u06c6\u0646 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u06a9\u0627\u0631\u06ce\u06a9\u0645\u0627\u0646 \u06a9\u0631\u062f \u06a9\u06d5 \u0644\u0627\u06af\u06cc\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0628\u06a9\u06d5\u0648\u0646\u06d5 \u0642\u0627\u062a\u06cc \u0648 \u0642\u0695\u06cc\u06cc\u06d5\u0648\u06d5 \u0648 \u062f\u0627\u0647\u0627\u062a\u06cc\u0627\u0646 \u06a9\u06d5\u0645\u062a\u0631 \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u06ce\u060c \u0628\u06d5\u0634\u06a9\u06d5 \u0628\u06cc\u0631 \u0644\u06d5 \u062e\u06c6 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u062e\u06c6\u0634\u06cc \u062f\u06d5\u0628\u0648\u0648\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u06a9\u0627\u0631\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627 \u0648 \u0686\u0648\u0631\u062a\u0645\u06cc\u0634 \u062f\u06d5\u0647\u0627\u062a\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u06d5\u0645\u06d5 \u0634\u0648\u0648\u0645\u06cc \u0645\u0648\u0648\u0633\u0627 \u0648 \u06cc\u0627\u0631\u0627\u0646\u06cc \u0645\u0648\u0648\u0633\u0627\u06cc\u06d5. \u062e\u06c6 \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5 \u0648 \u062e\u0631\u0627\u067e\u06d5\u06cc\u06d5\u060c \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5\u06cc\u06d5. \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0647\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06d5 \u0646\u06cc\u0627\u0632\u06cc \u062c\u0627\u062f\u0648\u0648 \u0644\u06ce\u06a9\u0631\u062f\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u06cc\u060c \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627\u062a \u067e\u06ce \u0646\u0627\u0647\u06ce\u0646\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u062c\u0627\u0631 \u0626\u06ce\u0645\u06d5 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u06a9\u06d5 \u0628\u0631\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u0644\u0627\u0641\u0627\u0648 \u0648 \u06a9\u0648\u0644\u0644\u06d5 \u0648 \u0626\u06d5\u0633\u067e\u06ce \u0648 \u062e\u0648\u06ce\u0646 \u0648 \u0628\u06d5\u0642 \u0628\u0648\u0648. \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0645\u0627\u0631\u0632\u0644\u06cc\u0627\u0646 \u0646\u0648\u0627\u0646 \u062f\u0648 \u06af\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631 \u0628\u0648\u0648\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u0627\u06cc\u0627\u0646\u06d5\u06cc\u0627\u0646 \u062f\u06cc\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0645\u0648\u0648\u0633\u0627! \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0648\u06d5\u067e\u0627\u0695\u06ce - \u0648\u06d5\u06a9 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0628\u06d5 \u062a\u06c6 \u062f\u0627\u0648\u06d5 - \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u062a\u0648\u0648 \u0628\u06d5\u06b5\u0627\u0645\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u0627\u0628\u06d5\u0631\u06ce\u060c \u0634\u06d5\u0631\u062a\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a \u067e\u06ce \u0628\u06a9\u06d5\u06cc\u0646 \u0648 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u06cc\u0634\u062a \u0695\u06d5\u06af\u06d5\u06b5 \u0628\u062e\u06d5\u06cc\u0646.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u06a9\u0627\u062a\u06ce \u0628\u06d5\u06b5\u0627\u0645\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u0627 \u0628\u0631\u062f\u0646 - \u062a\u0627 \u0626\u06d5\u0648 \u0645\u0627\u0648\u06d5 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u06d5 \u062a\u06ce\u067e\u06d5\u0695\u06cc\u0627\u0646 \u06a9\u0631\u062f - \u062c\u0627\u0631\u06ce \u062a\u0631 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u067e\u06ce \u062f\u0627.',
    '\u062c\u0627 \u062a\u06c6\u06b5\u06d5\u0645\u0627\u0646 \u0644\u06ce\u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u062f\u0627 \u0642\u0648\u0645\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646. \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627\u061b \u0647\u06d5\u0631 \u0628\u06d5 \u0644\u0627\u0634\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0646\u06d5\u0686\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0631\u0647\u06d5\u06b5\u0627\u062a \u0648 \u062e\u06c6\u0631\u0626\u0627\u0648\u0627\u06cc \u0626\u06d5\u0648 \u0648\u06b5\u0627\u062a\u06d5\u0645\u0627\u0646 - \u06a9\u06d5 \u0628\u06d5\u0631\u06d5\u06a9\u06d5\u062a\u0645\u0627\u0646 \u067e\u06ce \u062f\u0627\u0628\u0648\u0648 - \u06a9\u0631\u062f\u06d5 \u0628\u06d5\u0634\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0686\u06d5\u0648\u0633\u0627\u0628\u0648\u0648\u0646\u06d5\u0648\u06d5. \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0647\u06d5\u0631\u06d5 \u067e\u06cc\u0631\u06c6\u0632\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a - \u0628\u06ce \u06a9\u06d5\u0645 \u0648 \u06a9\u0648\u0695\u06cc - \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644 \u0647\u0627\u062a\u06d5 \u062c\u06ce\u061b \u0644\u06d5 \u0628\u0627\u062a\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631\u062a\u0646\u06cc\u0627\u0646. \u0647\u06d5\u0631\u0686\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u06cc\u0627\u0631\u0627\u0646\u06cc \u062f\u0627\u06cc\u0627\u0646 \u0646\u0627\u0648 \u0628\u06d5\u0631\u0632\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u0644\u06ce \u062a\u06ce\u06a9 \u0648 \u067e\u06ce\u06a9 \u062f\u0627\u0646.',
    '\u0628\u06d5\u0631\u06d5\u0628\u0627\u0628\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u0645\u0627\u0646 \u0644\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u06cc\u06d5 \u067e\u06d5\u0695\u0627\u0646\u062f\u06d5\u0648\u06d5. \u0626\u06d5\u0648\u0633\u0627 \u062a\u0648\u0648\u0634\u06cc \u0647\u06c6\u0632\u06ce\u06a9 \u0647\u0627\u062a\u0646\u060c \u0647\u06d5\u0631 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u067e\u06d5\u0631\u0633\u062a\u0646\u06cc \u0628\u062a\u06af\u06d5\u0644\u06cc\u0627\u0646 \u0628\u0648\u0648\u0646. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0645\u0648\u0648\u0633\u0627! \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0648\u06d5\u06a9\u0648\u0648 \u062e\u0648\u062f\u0627\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627\u06a9\u06d5! \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u0647\u06c6\u0632\u06ce\u06a9\u0646 \u06a9\u06d5 \u0647\u06cc\u0686 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648 \u0626\u0627\u06a9\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0628\u06ce \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0628\u06d5 \u0641\u06cc\u0695\u06c6\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u067e\u06d5\u0631\u0633\u062a\u0648\u06ce\u06a9\u0648\u0648 \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u0645! \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc \u0644\u06d5 \u0686\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u0626\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u060c \u0626\u06ce\u0648\u06d5\u06cc \u0628\u06d5\u0695\u06ce\u0632\u062a\u0631 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061f',
    '\u0644\u06d5 \u0628\u06cc\u0631 \u0645\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0695\u0632\u06af\u0627\u0631\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646 \u0644\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u0648\u0628\u06d5\u0633\u062a\u06d5\u06cc\u060c \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u0628\u06d5 \u0633\u06d5\u062e\u062a\u06cc \u0633\u0632\u0627 \u062f\u06d5\u062f\u0627\u0646\u061b \u0646\u06ce\u0631\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0648\u0634\u062a \u0648 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 [\u0628\u06d5 \u06cc\u06d5\u062e\u0633\u06cc\u0631\u06cc] \u062f\u06d5\u0647\u06ce\u0634\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u0627\u06cc\u06d5 \u0648 \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648\u0695\u0627 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06d5\u06a9\u06cc \u067e\u0695 \u0645\u06d5\u0632\u0646 \u0628\u0648\u0648.',
    '\u062f\u06d5\u06af\u06d5\u06b5 \u0645\u0648\u0648\u0633\u0627 \u0698\u0648\u0627\u0646\u06cc \u0633\u06ce \u0634\u06d5\u0648\u06d5\u0645\u0627\u0646 \u062f\u0627\u0646\u0627\u061b \u062f\u06d5\u06cc \u062f\u06cc\u06a9\u06d5\u0634\u0645\u0627\u0646 \u0628\u06c6 \u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631. \u0628\u06d5\u0645 \u0686\u06d5\u0634\u0646\u06d5 \u0645\u0627\u0648\u06d5\u06cc \u0698\u0648\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u060c \u0628\u0648\u0648 \u0628\u06d5 \u0686\u0644 \u0634\u06d5\u0648. \u0628\u06d5 \u0647\u0627\u0695\u0648\u0648\u0646\u06cc \u0628\u0631\u0627\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u062a\u06c6 \u0644\u06d5 \u0646\u0627\u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u0645\u062f\u0627 \u0628\u06d5 \u0648 \u0644\u06d5 \u062c\u06ce\u06cc \u0645\u0646 \u0628\u06d5\u0648 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u0628\u06a9\u06d5 \u0648 \u0644\u06d5 \u062f\u0648\u0648\u06cc \u062e\u0631\u0627\u067e\u06a9\u0627\u0631\u0627\u0646 \u0645\u06d5\u0686\u06c6.',
    '\u06a9\u06d5 \u0645\u0648\u0648\u0633\u0627 \u06af\u06d5\u06cc\u0634\u062a\u06d5 \u0698\u0648\u0627\u0646\u06af\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5\u06cc \u06a9\u06d5\u0648\u062a\u06d5 \u0626\u0627\u062e\u0627\u0641\u062a\u0646\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u0645\u06d5 \u062a\u06c6 \u0628\u0628\u06cc\u0646\u0645. \u06af\u0648\u062a\u06cc: \u0646\u0627\u062a\u0648\u0627\u0646\u06cc \u0628\u0645 \u0628\u06cc\u0646\u06cc\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0644\u06d5\u0645 \u06a9\u06d5\u0698\u06d5 \u0628\u0695\u0648\u0627\u0646\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u062e\u06c6\u06cc \u0646\u06d5\u0628\u0632\u0648\u0648\u062a\u060c \u0626\u06d5\u0648\u0633\u0627 \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u0628\u0645 \u0628\u06cc\u0646\u06cc. \u06a9\u06d5 \u062e\u06c6\u06cc \u0628\u06c6 \u06a9\u06d5\u0698\u06d5\u06a9\u06d5 \u062f\u06d5\u0631\u062e\u0633\u062a\u060c \u0647\u06d5\u0631 \u0628\u06d5 \u062c\u0627\u0631\u06ce \u06a9\u06d5\u0698\u06d5\u06a9\u06d5\u06cc \u067e\u0631\u06cc\u0634 \u067e\u0631\u06cc\u0634 \u06a9\u0631\u062f. \u0645\u0648\u0648\u0633\u0627 \u0628\u06d5 \u0628\u06ce \u0647\u06c6\u0634\u06cc \u06a9\u06d5\u0648\u062a. \u0648\u06d5\u062e\u062a\u06ce \u0647\u0627\u062a\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u062e\u06c6\u060c \u06af\u0648\u062a\u06cc: \u067e\u0627\u06a9\u06cc \u0647\u06d5\u0631 \u0628\u06c6 \u062a\u06c6\u06cc\u06d5\u060c \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u0645\u061b \u0645\u0646 \u0644\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u062f\u0627 \u06cc\u06d5\u06a9\u06d5\u0645\u06cc\u0646\u0645.',
    '\u06af\u0648\u062a\u06cc: \u0645\u0648\u0648\u0633\u0627 \u0645\u0646 \u062a\u06c6\u0645 - \u0628\u06d5 \u067e\u06d5\u06cc\u0627\u0645\u06d5\u06a9\u0627\u0646\u0645 \u0648 \u0626\u0627\u062e\u0627\u0641\u062a\u0646\u0645 - \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u060c \u062e\u0633\u062a\u06c6\u062a\u06d5 \u0633\u06d5\u0631\u061b \u0686\u0645 \u067e\u06ce\u062f\u0627\u0648\u06cc\u060c \u0648\u06d5\u0631\u06cc \u0628\u06af\u0631\u06d5 \u0648 \u0644\u06d5 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631\u0627\u0646 \u0628\u06d5.',
    '\u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9 \u0648 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0628\u06c6 \u06af\u06d5\u0644\u06d5\u06a9\u06d5\u06cc\u060c \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u0645\u0627\u0646 - \u0628\u06ce \u06a9\u06d5\u0645 \u0648 \u06a9\u0648\u0695\u06cc - \u0628\u06c6 \u0646\u0648\u0648\u0633\u06cc \u0648 \u0628\u06c6\u0645\u0627\u0646 \u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631 \u062a\u06d5\u062a\u06d5\u06b5\u0627\u0646. \u0645\u0648\u0648\u0633\u0627! \u0626\u0627\u06af\u0627\u062a \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06ce \u0648 \u062a\u0648\u0646\u062f\u06cc\u0627\u0646 \u0628\u06af\u0631\u06d5 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u062a \u0628\u062f\u06d5 \u0628\u0627 \u0647\u06d5\u0631\u06d5\u0686\u0627\u06a9\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u0631\u0646. \u0645\u0646 \u0632\u0648\u0648 \u067e\u06ce\u062a\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0645 \u062c\u06ce\u06af\u06d5 \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc \u0628\u06a9\u06d5\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u06a9\u0648\u06ce\u06cc\u06d5.',
    '\u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u0695\u0648\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u0628\u06d5 \u0646\u0627\u062d\u06d5\u0642 \u0644\u06d5 \u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646 \u0641\u06cc\u0632 \u062f\u06d5\u0646\u0648\u06ce\u0646\u0646\u060c \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0645 \u0648\u06d5\u0631\u062f\u06d5\u0633\u0648\u0648\u0695\u06ce\u0646\u0645\u061b \u0628\u06d5 \u0686\u06d5\u0634\u0646\u06ce\u06a9 \u06a9\u06d5 \u0647\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9 \u0628\u06cc\u0628\u06cc\u0646\u0646\u060c \u0646\u06d5\u06cc\u0633\u06d5\u0644\u0645\u06ce\u0646\u0646\u061b \u06cc\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0695\u06ce\u06cc \u0695\u0627\u0633\u062a \u0628\u0628\u06cc\u0646\u0646 \u067e\u06ce\u06cc\u062f\u0627 \u0646\u06d5\u0686\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u06af\u06d5\u0631 \u0695\u06ce\u06af\u0627\u06cc \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u0628\u0628\u06cc\u0646\u0646\u060c \u0628\u06cc\u06af\u0631\u0646\u06d5 \u0628\u06d5\u0631. \u0686\u0648\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0633\u06d5\u0631\u0646\u062c\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0627\u0646\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0648 \u0695\u06c6\u0698\u06cc \u067e\u06d5\u0633\u06b5\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627\u060c \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628 \u06d5\u0641\u06ce\u0695\u06c6 \u0686\u0648\u0648. \u0626\u0627\u062e\u06c6 \u062c\u06af\u06d5 \u0644\u06d5 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u06a9\u0631\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0633\u0632\u0627 \u062f\u06d5\u062f\u0631\u06ce\u0646\u061f',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0645\u0648\u0648\u0633\u0627 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648 - \u0644\u06d5 \u062e\u0634\u06b5 \u0648 \u0632\u06d5\u0646\u0628\u06d5\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 - \u06a9\u06c6\u062a\u06d5\u06b5\u06cc \u06af\u0648\u06ce\u0631\u06d5\u06a9\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u062f\u0631\u0648\u0648\u0633 \u06a9\u0631\u062f \u06a9\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u06cc\u0628\u06c6\u0695\u0627\u0646\u062f! \u0645\u06d5\u0631 \u0646\u06d5\u06cc\u0627\u0646\u062f\u06cc \u0626\u06d5\u0648 \u06af\u0648\u06ce\u0631\u06d5\u06a9\u06d5 \u0642\u0633\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5\u0627 \u0646\u0627\u06a9\u0627 \u0648 \u0695\u06ce\u06af\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06cc\u0634\u0627\u0646 \u0646\u0627\u062f\u0627\u061f \u0628\u06d5\u06b5\u06ce \u06a9\u06d5\u0648\u062a\u0646\u06d5 [\u067e\u06d5\u0631\u0633\u062a\u0646\u06cc \u0648] \u0633\u062a\u06d5\u0645\u06a9\u0627\u0631 \u0628\u0648\u0648\u0646.',
    '\u06a9\u0627\u062a\u06ce \u062a\u06d5\u0648\u0627\u0648 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u062f\u0627\u06cc\u06af\u0631\u062a\u0646 \u0648 \u062f\u06cc\u062a\u06cc\u0627\u0646 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0644\u06ce \u0634\u06ce\u0648\u0627\u0648\u06d5\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc\u0645\u0627\u0646 \u0646\u06d5\u062e\u0627 \u0648 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u0645\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u0631\u06ce\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06cc\u0646.',
    '\u0645\u0648\u0648\u0633\u0627 \u0648\u06d5\u062e\u062a\u06ce \u06af\u06d5\u0695\u0627\u06cc\u06d5\u0648\u06d5 \u0646\u0627\u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc\u060c \u0628\u06d5 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u0648 \u0628\u06d5 \u062f\u06b5 \u067e\u0695\u06cc \u062a\u06ce\u06cc \u0695\u0627\u062e\u0648\u0695\u06cc\u0646 \u06a9\u06d5: \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0645\u0646 \u0686\u06d5\u0646\u062f \u062c\u06ce\u0646\u0634\u06cc\u0646\u06cc \u062e\u0631\u0627\u067e \u0628\u0648\u0648\u0646\u061b \u0626\u0627\u062e\u0631 \u0628\u06c6\u0686\u06cc \u0695\u0627\u0646\u06d5\u0648\u06d5\u0633\u062a\u0627\u0646 \u062a\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06af\u0627\u061f \u062a\u06d5\u062a\u06d5\u06b5\u06d5\u06a9\u0627\u0646\u06cc \u062a\u0648\u0648\u0695\u0647\u06d5\u06b5\u062f\u0627 \u0648 \u0642\u0698\u06cc \u0628\u0631\u0627\u06cc\u06d5\u06a9\u06d5\u06cc \u06a9\u06ce\u0634\u0627 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u0628\u0631\u062f. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u062f\u0627\u06cc\u06a9 \u0628\u0631\u0627\u06a9\u06d5\u0645! \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u0645 \u0647\u06ce\u0632\u06cc\u0627\u0646 \u0644\u06ce \u0633\u06d5\u0646\u062f\u0645\u061b \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u0648\u0648 \u0628\u0634\u0645 \u06a9\u0648\u0698\u0646! \u062f\u0698\u0645\u0646\u0627\u0646 \u0628\u06d5 \u0645\u0646 \u062e\u06c6\u0634 \u0645\u06d5\u06a9\u06d5 \u0648 \u0645\u0646\u06cc\u0634 \u062f\u06d5\u06af\u06d5\u06b5 \u0646\u0627\u0647\u06d5\u0642\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646 \u062f\u0627\u0645\u06d5\u0646\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0644\u06d5 \u062e\u06c6\u0645 \u0648 \u0628\u0631\u0627\u0645 \u0628\u0628\u0648\u0648\u0631\u06d5 \u0648 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u062a\u0645\u0627\u0646 \u0628\u062e\u06d5. \u062a\u06c6 \u0644\u06d5 \u06af\u0634 \u062e\u0627\u0648\u06d5\u0646 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u0627\u0646 \u062f\u06b5\u0633\u06c6\u0632\u062a\u0631\u06cc.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u06af\u0648\u06ce\u0631\u06d5\u06a9\u06d5\u067e\u06d5\u0631\u0633\u062a\u060c \u0628\u06d5\u0631 \u063a\u06d5\u0632\u06d5\u0628\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u0644\u06d5 \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0632\u06d5\u0644\u06cc\u0644 \u062f\u06d5\u0628\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u06ce\u060c \u0626\u06ce\u0645\u06d5 \u0648\u0627\u06cc\u0627\u0646 \u0633\u0632\u0627 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0626\u0627\u06a9\u0627\u0631\u06cc\u0627\u0646 \u062e\u0631\u0627\u067e \u0628\u0648\u0648\u06d5\u060c \u067e\u0627\u0634\u0627\u0646 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u0628\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u06a9\u0627\u062a\u06ce \u0645\u0648\u0648\u0633\u0627 \u0644\u06d5 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u0647\u06ce\u062f\u06cc \u0628\u0648\u0648\u06d5\u060c \u062a\u06d5\u062a\u06d5\u06b5\u06d5\u06a9\u0627\u0646\u06cc \u0686\u0646\u06cc\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u062a\u06ce\u06cc\u0627\u0646\u062f\u0627 \u0695\u06ce \u0634\u0627\u0646\u062f\u0627\u0646 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0628\u0648\u0648 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u06ce \u062a\u0631\u0633\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0645\u0648\u0648\u0633\u0627 \u062d\u06d5\u0641\u062a\u0627 \u06a9\u06d5\u0633\u06cc \u0644\u06d5 \u0647\u06c6\u0632\u06a9\u06d5\u06cc \u062f\u06d5\u0633 \u0646\u06cc\u0634\u0627\u0646 \u06a9\u0631\u062f \u06a9\u06d5 \u0628\u06ce\u0646\u06d5 \u062f\u06cc\u062f\u06d5\u0646\u06cc \u0626\u06ce\u0645\u06d5. \u06a9\u0627\u062a\u06ce \u0628\u0648\u0648 \u0644\u06d5\u0631\u0632\u06d5 \u062f\u0627\u06cc\u06af\u0631\u062a\u0646\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u0645! \u0626\u06d5\u06af\u06d5\u0631 \u0645\u06d5\u06cc\u0644\u062a \u0644\u06d5 \u0633\u06d5\u0631 \u0628\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u062f\u06d5\u062a \u0645\u0631\u0627\u0646\u062f\u06cc\u0646. \u0626\u0627\u062e\u06c6 \u062f\u06d5\u062a\u06d5\u0648\u06ce \u0628\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc \u06a9\u06d5 \u0628\u06ce \u0626\u0627\u0648\u06d5\u0632\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u060c \u062a\u06c6 \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u06d5\u0631\u06cc\u061f \u0626\u06d5\u0645\u06d5\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06cc \u062e\u06c6\u062a\u06d5\u061b \u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u06c6\u062a \u06a9\u06d5\u0633\u0627\u0646\u06ce \u06af\u0648\u0645\u0695\u0627 \u062f\u06d5\u06a9\u06d5\u06cc\u062a \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u06cc\u0634 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u062a \u0634\u0627\u0631\u06d5\u0632\u0627 \u062f\u06d5\u06a9\u06d5\u06cc. \u06af\u06d5\u0648\u0631\u06d5\u0645\u0627\u0646 \u062a\u06c6\u06cc\u060c \u0628\u0645\u0627\u0646 \u0628\u0648\u0648\u0631\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u062a \u067e\u06ce\u0645\u0627\u0646\u062f\u0627 \u0628\u06ce\u062a\u06d5\u0648\u06d5. \u0628\u0627\u0634\u062a\u0631\u06cc\u0646 \u0628\u06d5\u062e\u0634\u06ce\u0646\u06d5\u0631 \u0647\u06d5\u0631\u062a\u06c6\u06cc.',
    '\u0644\u06d5 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0628\u0645\u0627\u0646\u062e\u0648\u06ce\u0646\u06d5\u0648\u06d5\u061b \u0626\u06ce\u0645\u06d5 \u0647\u0627\u062a\u0648\u0648\u06cc\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062a\u06c6. \u06af\u0648\u062a\u06cc: \u0647\u06d5\u0631\u06a9\u06ce \u0626\u0627\u0632\u0627\u0631 \u0628\u062f\u06d5\u0645 \u06a9\u06d5\u06cc\u0641\u06cc \u062e\u06c6\u0645\u06d5. \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u0634\u0645 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u062f\u0627\u06af\u0631\u062a\u0648\u0648\u06d5. \u0686\u0627\u06a9\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0646\u0648\u0648\u0633\u0645 \u0644\u06d5 \u062e\u0631\u0627\u067e\u06d5 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u062f\u06d5\u0646\u061b \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5\u061b',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06cc \u06a9\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06cc \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0646\u06d5\u06cc\u062e\u0648\u06ce\u0646\u062f\u0648\u0648\u06d5 \u0648 \u0644\u06d5 \u062a\u06d5\u0648\u0631\u0627\u062a \u0648 \u0626\u06cc\u0646\u062c\u06cc\u0644\u06cc\u0634 \u0646\u0627\u0648\u06cc \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5 \u0648 \u0647\u0627\u0646\u06d5\u06cc \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a \u0648 \u0646\u0627\u0647\u06ce\u06b5\u06ce \u062e\u0631\u0627\u067e\u06d5 \u0628\u06a9\u06d5\u0646\u061b \u067e\u0627\u06a9\u0698 \u0628\u06d5 \u0695\u06d5\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0648 \u06af\u06d5\u0645\u0627\u0631\u06cc \u067e\u06ce \u0646\u0627\u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0648 \u06af\u0631\u0627\u0646\u0627\u06cc\u06cc \u0632\u0646\u062c\u06cc\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u0627 \u062f\u06d5\u0628\u0627\u061b \u06a9\u06d5 \u0628\u06d5\u0648\u0627\u0646\u06cc\u0627\u0646 \u0634\u06d5\u062a\u06d5\u06a9 \u062f\u0627\u0628\u0648\u0648\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06d5 \u0642\u06d5\u062f\u0631\u06d5\u0648\u06d5 \u0644\u06ce\u06cc \u062f\u06d5\u0695\u0648\u0627\u0646\u0646\u061b \u0644\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062f\u0627\u0646\u0627\u0645\u06ce\u0646\u0646 \u0648 \u0644\u06d5 \u062f\u0648\u0648\u06cc \u0626\u06d5\u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc\u06d5 \u062f\u06d5\u0686\u0646 \u06a9\u06d5 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0632\u06af\u0627\u0631\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0628\u0632\u0627\u0646\u0646 \u0626\u06d5\u0645\u0646 \u062e\u0648\u062f\u0627 \u0695\u0627\u06cc \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u0645\u06d5 \u0644\u0627\u06cc \u0626\u06ce\u0648\u06d5\u060c \u06af\u0634 \u0644\u06d5 \u06af\u0634\u062a\u0648\u0648. \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0645\u0648\u06b5\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0626\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u0634 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5\u061b \u0644\u06d5 \u062e\u06c6\u06cc \u0628\u06d5\u062f\u06d5\u0631 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0628\u06ce. \u06af\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u062e\u0634\u06ce \u0648 \u06af\u06cc\u0627\u0646 \u062f\u06d5\u0633\u062a\u06ce\u0646\u06ce. \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0646\u06d5\u062e\u0648\u06ce\u0646\u062f\u06d5\u0648\u0627\u0631 \u0628\u0695\u0648\u0627 \u0628\u06a9\u06d5\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u06cc\u0634 \u062e\u06c6\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0628\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u062e\u0648\u062f\u0627 \u06af\u0648\u062a\u0648\u0648\u06cc\u06d5 \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u0627. \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0628\u0646\u060c \u0634\u0627\u06cc\u06d5\u062a \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce\u06af\u06d5 \u0628\u0646.',
    '\u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u06cc\u0634 \u0644\u06d5 \u0647\u06c6\u0698\u06cc \u0645\u0648\u0648\u0633\u0627\u062f\u0627 \u0647\u06d5\u0646\u06d5\u060c \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0695\u06ce \u062f\u06d5\u0646\u0648\u06ce\u0646\u0646 \u0648 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc\u0634 \u062f\u0627\u0648\u06d5\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0645\u0627\u0646 \u0628\u06d5 \u062f\u0648\u0627\u0632\u062f\u06d5 \u062f\u06d5\u0633\u062a\u06d5 [\u062f\u0648\u0627\u0632\u062f\u06d5 \u0626\u06c6\u0645\u06d5\u062a] \u0628\u06d5\u0634 \u0628\u06d5\u0634 \u06a9\u0631\u062f \u0648 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627 \u062f\u0627\u0648\u0627\u06cc \u0626\u0627\u0648\u06cc \u0648\u06d5\u062e\u0648\u0627\u0631\u062f\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0645\u0648\u0648\u0633\u0627 \u06a9\u0631\u062f\u060c \u0626\u06ce\u0645\u06d5 \u0645\u0648\u0648\u0633\u0627\u0645\u0627\u0646 \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f \u06a9\u06d5: \u0628\u06d5 \u062f\u0627\u0631\u0639\u0627\u0633\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u0633\u062a\u062a \u0644\u06d5\u0648 \u0628\u06d5\u0631\u062f\u06d5 \u062f\u06d5. \u062f\u0648\u0627\u0632\u062f\u06d5 \u06a9\u0627\u0646\u06cc \u0644\u06d5 \u0628\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u06d5 \u0647\u06d5\u06b5\u0642\u0648\u06b5\u06cc\u0646\u061b \u0647\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06cc \u062f\u06d5\u0631\u0627\u0648\u06cc \u062e\u06c6\u06cc \u0646\u0627\u0633\u06cc\u06d5\u0648\u06d5. \u0647\u06d5\u0648\u0631\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u0633\u06d5\u0631\u0648\u0648\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0633\u06d5\u06cc\u0648\u0627\u0646\u061b \u06af\u06d5\u0632\u06c6\u0634\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0634\u06ce\u0644\u0627\u0642\u06d5 \u0628\u06c6 \u0647\u06d5\u0646\u0627\u0631\u062f\u0646: \u0628\u062e\u06c6\u0646 \u0644\u06d5\u0648 \u0634\u062a\u06d5 \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u06d5\u062a\u06d5 \u0695\u06c6\u0632\u06cc\u062a\u0627\u0646. \u0647\u06cc\u0686 \u063a\u06d5\u062f\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06ce\u0645\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u061b \u0628\u06d5\u06b5\u0627\u0645 \u063a\u06d5\u062f\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '[\u0644\u06d5 \u0628\u06cc\u0631\u0648\u0648 \u0628\u06ce] \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634 \u06a9\u06d5 \u067e\u06cc\u0627\u0646 \u06af\u0648\u062a\u0631\u0627: \u0644\u06d5\u0648 \u0634\u0627\u0631\u06d5 \u0646\u06cc\u0634\u062a\u06d5\u062c\u06ce \u0628\u0646 \u0648 \u0644\u06d5 \u0647\u06d5\u0631 \u0644\u0627\u06cc\u06d5\u06a9 \u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce \u0628\u06cc\u062e\u06c6\u0646. \u0646\u0632\u0627 \u0628\u06a9\u06d5\u0646 \u06af\u0648\u0646\u0627\u0647\u0648\u0648 \u0644\u06ce \u0647\u06d5\u06b5\u0628\u0648\u06d5\u0631\u06ce \u0648 \u0644\u06d5 \u062f\u06d5\u0631\u06af\u0627\u0648\u06d5 \u06a9\u06d5 \u062f\u06d5\u0686\u0646\u06d5 \u0698\u0648\u0648\u0631\u060c \u0633\u0648\u0698\u062f\u06d5 \u0628\u06d5\u0631\u0646\u061b \u0647\u06d5\u0631 \u0647\u06d5\u06b5\u06d5\u06cc\u06ce \u06a9\u0631\u062f\u0648\u0648\u062a\u0627\u0646\u06d5 \u062f\u06d5\u062a\u0627\u0646\u0628\u06d5\u062e\u0634\u06cc\u0646. \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u0632\u06cc\u0627\u062a\u0631\u06cc\u0634 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646\u06cc\u0627\u0646 \u0626\u06d5\u0648 \u0642\u0633\u0627\u0646\u06d5\u06cc \u067e\u06ce\u06cc\u0627\u0646 \u0648\u062a\u0631\u0627 \u0628\u0648\u0648\u060c \u06af\u06c6\u0695\u06cc\u0627\u0646\u06d5 \u0628\u0627\u0631\u06ce\u06a9\u06cc \u062f\u06cc\u06a9\u06d5. \u0626\u06ce\u0645\u06d5\u0634 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc \u06a9\u0631\u062f\u0646\u06d5\u06cc\u0627\u0646\u060c \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646.',
    '\u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0648\u0628\u0627\u0698\u06ce\u0695\u06d5\u06cc \u0648\u0627 \u06a9\u06d5\u0648\u062a\u0628\u0648\u06d5 \u0642\u06d5\u0631\u0627\u062e\u06cc \u062f\u06d5\u0631\u06cc\u0627 \u0644\u06ce\u06cc\u0627\u0646 \u067e\u0631\u0633\u06d5\u060c \u06a9\u06d5 \u0628\u06d5 \u0634\u06d5\u0645\u0645\u06d5 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f. \u0686\u0648\u0646\u06a9\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0634\u06d5\u0645\u0645\u06d5\u062f\u0627 \u0645\u0627\u0633\u06cc \u06af\u06d5\u0644 \u0633\u06d5\u0631\u0627\u0648 \u062f\u06d5\u06a9\u06d5\u0648\u062a\u0646 \u0648 \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0634\u06d5\u0645\u0645\u06d5 \u0646\u06d5\u0628\u0648\u0648\u0627\u06cc\u06d5\u060c \u062f\u06cc\u0627\u0631\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627 \u062f\u06d5\u062f\u06d5\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u062f\u06d5\u06cc\u0627\u0646 \u0626\u06d5\u0632\u0645\u0648\u0648\u06cc\u0646.',
    '\u06a9\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u06cc\u0634 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06c6\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u06a9\u06d5\u0633\u0627\u0646\u06ce \u062e\u0648\u062f\u0627 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5 \u0642\u0695\u06cc\u0627\u0646 \u06a9\u0627 \u06cc\u0627\u0646 \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u0627\u060c \u0628\u06d5 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u062e\u06d5\u0631\u06cc\u06a9\u0646\u061f \u06af\u0648\u062a\u06cc\u0627\u0646: \u062a\u0627 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0634\u06d5\u0631\u0645\u06d5\u0632\u0627\u0631\u06cc \u0646\u06d5\u0628\u06cc\u0646 \u0648 \u0634\u0627\u06cc\u06d5\u062a \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0628\u062a\u0631\u0633\u0646.',
    '\u06a9\u06d5 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u06a9\u0631\u062f\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0646\u06d5\u062f\u0627\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0645\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f \u06a9\u06d5 \u062f\u0698\u06cc \u062e\u0631\u0627\u067e\u06d5 \u0648\u06ce\u0633\u062a\u0627\u0646\u061b \u0632\u06c6\u0631 \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0645\u0627\u0646\u062f\u0627 \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc \u067e\u06cc\u0634\u06d5 \u0628\u0648\u0648\u0646 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5\u062c\u06ce \u0646\u06d5\u0647\u06ce\u0646\u0627.',
    '\u06a9\u0627\u062a\u06ce \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc\u0627\u0646 \u06a9\u0631\u062f \u06a9\u06d5 \u067e\u06cc\u0627\u0646 \u06af\u0648\u062a\u0631\u0627 \u0628\u0648\u0648 \u0645\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u06af\u0648\u062a\u0645\u0627\u0646: \u0628\u0628\u0646\u06d5 \u0645\u06d5\u06cc\u0645\u0648\u0648\u0646 \u06af\u06d5\u0644\u06cc \u062a\u0695\u06c6 \u06a9\u0631\u0627\u0648!',
    '\u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634\u0648\u0648 \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u06ce \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062a\u06c6 \u0695\u0627\u06cc\u06af\u06d5\u06cc\u0627\u0646\u062f: \u062f\u06d5\u06cc\u0627\u0646\u06a9\u06d5\u0645\u06d5 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u06a9\u06d5\u0633\u0627\u0646\u06cc \u0648\u0627 \u06a9\u06d5 \u062a\u0627 \u0642\u06cc\u0627\u0645\u06d5\u062a - \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc - \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06d5 \u067e\u06d5\u0644\u06d5 \u062a\u06c6\u06b5\u06d5 \u062f\u06d5\u0633\u062a\u06ce\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06a9\u0627\u0631\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u06a9\u0631\u062f\u0645\u0627\u0646\u0646 \u0628\u06d5 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u0644\u06ce\u06a9\u062a\u0631 \u062c\u06cc\u0627. \u0647\u06ce\u0646\u062f\u06ce \u0644\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0686\u0627\u06a9 \u062f\u06d5\u0631\u0686\u0648\u0648\u0646\u061b \u0647\u06ce\u0646\u062f\u06ce \u062a\u0631 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u0646\u061b \u0628\u06d5 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0648 \u0628\u06d5 \u0646\u0627\u062e\u06c6\u0634\u06cc\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u060c \u0633\u0627 \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u062c\u06ce \u0646\u0634\u06cc\u0646\u0627\u0646\u06ce\u06a9\u06cc \u0628\u06d5\u062f\u0641\u06d5\u0695 \u0647\u0627\u062a\u0646\u06d5 \u062c\u06ce\u06af\u0627\u06cc\u0627\u0646 \u0648\u060c \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u06a9\u06d5\u0644\u06d5\u067e\u0648\u0648\u0631 \u0645\u0627\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u062a\u06d5\u0645\u0627\u062d\u06cc \u062f\u0646\u06cc\u0627 \u062a\u06ce \u062f\u06d5\u06a9\u06c6\u0634\u0627\u0646. \u062f\u06d5\u0634\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0632\u06c6\u0631 \u0632\u0648\u0648 \u0639\u0627\u0641\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u06cc\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06cc\u0633\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u0634\u062a\u06ce\u06a9 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u06af\u06cc\u0631 \u0628\u0627\u060c \u0648\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06af\u0631\u062a. \u0645\u06d5\u0631 \u0644\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u062f\u0627 \u0644\u06d5\u0648\u0627\u0646 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u0648\u06d5\u0631\u0646\u06d5\u06af\u06cc\u0631\u0627\u060c \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0628\u06d5\u0648 \u0644\u0627\u0648\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0646\u06d5\u062e\u06ce\u0648\u0646\u061f \u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u062a\u06ce\u0634\u06cc\u062f\u0627 \u0647\u06d5\u0628\u0648\u0648 \u062e\u0648\u06ce\u0646\u062f\u0628\u0648\u0648\u06cc\u0627\u0646. \u0645\u0627\u06b5\u06cc \u0626\u06d5\u0648\u0644\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0646 \u0632\u06c6\u0631 \u0628\u0627\u0634\u062a\u0631\u06d5\u060c \u0647\u06ce\u0634\u062a\u0627 \u0626\u06ce\u0648\u06d5 \u062a\u06ce \u0646\u0627\u06af\u06d5\u06cc\u06d5\u0646\u061f',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u06a9\u062a\u06ce\u0628 \u0695\u06d5\u0641\u062a\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0646\u0648\u06ce\u0698\u06cc\u0634 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0626\u06ce\u0645\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646 \u0648\u0646\u062f\u0627 \u0646\u0627\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5 \u06a9\u06d5 \u06a9\u06ce\u0648\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u0648\u06ce\u0646\u06d5\u06cc \u0633\u06d5\u06cc\u0648\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646\u062f\u0627 \u0695\u0627\u0647\u06d5\u0646\u06af\u0627\u0648\u062a \u0648 \u0644\u0627\u06cc\u0627\u0646 \u0648\u0627\u0628\u0648\u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646\u062f\u0627 \u062f\u06d5\u0695\u0648\u0648\u062e\u06ce [\u0648\u062a\u0645\u0627\u0646]: \u0626\u06d5\u0648\u06cc \u062f\u0627\u0648\u0645\u0627\u0646\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u067e\u06d5\u06cc\u062a \u0628\u06cc\u06af\u0631\u0646\u061b \u0686\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5 \u0644\u06d5 \u0628\u06cc\u0631\u062a\u0627\u0646 \u0628\u06ce\u061b \u062f\u06d5\u0634\u0642\u06d5\u0645 \u062e\u06c6\u062a\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0644\u06d5 \u067e\u0634\u062a\u06cc \u0639\u06d5\u0648\u0644\u0627\u062f\u06cc \u0626\u0627\u062f\u06d5\u0645 \u0632\u0627\u0631\u0648\u0632\u06cc\u0686\u06cc \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u060c \u0644\u06d5 \u0633\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u0627. \u06af\u0648\u062a\u06cc: \u0645\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646 \u0646\u06cc\u0645\u061f \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06d5\u06b5\u06ce\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0648\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0646. \u0646\u06d5\u06a9\u0627 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u06ce\u0698\u0646: \u0646\u06d5\u0645\u0627\u0646\u0632\u0627\u0646\u06cc.',
    '\u06cc\u0627\u0646 \u0646\u06d5\u0628\u06ce\u0698\u0646: \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0648\u0627 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u0627\u0646 [\u0628\u06c6 \u062e\u0648\u062f\u0627] \u062f\u0627\u0646\u0627\u060c \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u0627\u0646\u0645\u0627\u0646 \u0628\u0648\u0648\u0646 \u0648 \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0631\u06d5\u06cc \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0646\u061b \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0633\u062a\u0627 \u062a\u06c6 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0646\u0627\u062d\u06d5\u0642\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u0642\u0695\u06a9\u06d5\u06cc\u061f',
    '\u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0626\u0627\u0648\u0627 \u0695\u06c6\u0634\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u060c \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u06d5 \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646 \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc \u0628\u0648\u0648\u060c \u062e\u06c6\u06cc \u0644\u06ce \u0628\u0648\u0648\u0627\u0631\u062f\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0648\u06d5\u062f\u0648\u0648\u06cc \u062e\u06c6\u06cc \u062f\u0627 \u0648 \u06a9\u06d5\u0648\u062a\u06d5 \u0646\u0627\u0648 \u0695\u06ce\u0632\u06cc \u06af\u0648\u0645\u0695\u0627\u06cc\u0627\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u06af\u06d5\u0631 \u0628\u0645\u0627\u0646 \u0648\u06cc\u0633\u062a\u0627\u06cc\u06d5 \u0644\u06d5\u0645\u0627\u0646\u06d5\u0695\u0627 \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u06cc\u0645\u0627\u0646 \u062f\u06d5\u062f\u0627\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0628\u06d5 \u0639\u06d5\u0631\u0632\u06d5\u0648\u06d5 \u0686\u06d5\u0633\u067e\u0627\u0646\u062f \u0648 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u06cc \u0628\u0648\u0648. \u0646\u06d5\u0632\u06cc\u0644\u06d5\u06cc \u0626\u06d5\u0648 \u0648\u06d5\u06a9\u0648\u0648 \u0646\u06d5\u0632\u06cc\u0644\u06d5\u06cc \u0633\u06d5\u06af\u06ce\u06a9\u06d5 \u0628\u06c6\u06cc \u0628\u0686\u06cc \u0632\u0645\u0627\u0646 \u062f\u06d5\u0631\u062f\u06ce\u0646\u06ce \u0648 \u0644\u06ce\u0634\u06cc \u06af\u06d5\u0695\u06ce\u06cc \u0632\u0648\u0627\u0646 \u062f\u06d5\u06a9\u06ce\u0634\u06ce. \u0626\u06d5\u0645 \u0686\u06cc\u0631\u06c6\u06a9\u06d5 \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627. \u062a\u06c6 \u0644\u06d5\u0648 \u062a\u06d5\u0631\u0632\u06d5 \u0628\u0627\u0633\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u06af\u06ce\u0695\u06d5\u060c \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0628\u06cc\u0631 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0686\u06d5\u0646 \u062e\u0631\u0627\u067e\u06d5 \u0633\u06d5\u0631\u06af\u0648\u0631\u0648\u0634\u062a\u06d5\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0627\u0646\u06cc \u0648 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0628\u06a9\u0627\u060c \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a \u062f\u06d5\u06af\u0631\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u06ce \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u06af\u0648\u0645\u0695\u0627 \u0628\u06a9\u0627\u060c \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u06af\u06d5\u0644\u06ce\u06a9 \u0644\u06d5 \u062c\u0646\u06c6\u06a9\u06d5 \u0648 \u0644\u06d5 \u0645\u0631\u06c6\u0645\u0627\u0646 \u0628\u06c6 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627: \u062f\u06b5\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u067e\u06ce\u06cc \u062a\u06ce\u0646\u0627\u06af\u06d5\u0646\u061b \u0686\u0627\u0648\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u067e\u06ce\u06cc \u0646\u0627\u0628\u06cc\u0646\u0646\u061b \u06af\u0648\u06ce\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u067e\u06ce\u06cc \u0646\u0627\u0628\u06cc\u0633\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u0627\u0698\u06d5\u06b5\u0646\u061b \u0628\u06af\u0631\u06d5 \u0644\u06d5\u0648\u0627\u0646 \u06af\u0648\u0645\u0695\u0627\u062a\u0631\u0646\u061b \u0626\u0627\u06af\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06cc\u0686 \u0634\u062a \u0646\u06cc\u06cc\u06d5.',
    '\u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627\u060c \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0695\u0646\u062f \u0648 \u0644\u06d5\u0628\u0627\u0631\u0646\u061b \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0646\u0627\u0648\u0627\u0646\u06d5 \u0628\u0627\u0646\u06af\u06cc \u06a9\u06d5\u0646. \u06af\u0648\u06ce \u0645\u06d5\u062f\u06d5\u0646\u06d5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0646\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u0627\u0646\u0642\u06d5\u0633\u062a \u0628\u06c6 \u062f\u06d5\u06af\u06c6\u0695\u0646. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc\u0627\u0646 \u0633\u0632\u0627 \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u0644\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u062f\u0631\u0648\u0633\u062a\u0646 \u06a9\u0631\u062f\u0648\u0648\u0646\u060c \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9 \u0647\u06d5\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u0644\u0627\u06cc \u0647\u06d5\u0642 \u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631\u0646 \u0648 \u0628\u06d5 \u0647\u06d5\u0642\u06cc\u0634 \u062f\u0627\u0648\u06d5\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627\u060c \u0648\u0631\u062f\u06d5\u0648\u0631\u062f\u06d5 - \u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u0628\u06d5 \u062e\u06c6 \u0628\u0632\u0627\u0646\u0646 - \u0628\u06d5\u0631\u06d5\u0648 \u062a\u06ce\u062f\u0627 \u0686\u0648\u0648\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u06cc\u0646.',
    '\u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0645\u06c6\u06b5\u06d5\u062a\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u060c \u0686\u0648\u0646\u06a9\u06d5 \u062a\u06d5\u06af\u0628\u06cc\u0631\u0645 \u067e\u062a\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0628\u06d5 \u0628\u06cc\u0631\u06cc\u0627\u0646\u062f\u0627 \u0646\u06d5\u0647\u0627\u062a \u0647\u0627\u0648\u0695\u06ce\u06a9\u06d5\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u0634\u06ce\u062a \u0628\u0648\u0648\u0646 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u06d5\u061f \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5 \u0626\u06d5\u0648 \u0644\u06d5 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631\u0647\u06ce\u0646\u06d5\u0631\u06ce\u06a9 \u0628\u06d5\u062f\u06d5\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u062f\u0627\u0645 \u0648\u062f\u06d5\u0632\u06af\u0627\u06cc \u067e\u0695 \u0634\u06a9\u06c6\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0647\u06d5\u0631\u0686\u06cc \u062e\u0648\u0627 \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646\u060c \u0695\u0627 \u0646\u06d5\u0645\u0627\u0648\u0646\u061f \u0648\u06ce\u0634 \u062f\u06d5\u0686\u06ce \u0628\u06d5\u0645 \u0646\u0632\u06cc\u06a9\u0627\u0646\u06d5 \u0628\u06d5 \u0626\u0627\u06a9\u0627\u0645\u06cc \u0645\u06d5\u0631\u06af \u0628\u06af\u06d5\u0646. \u062f\u06d5\u06cc\u0633\u0627 \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5 \u0628\u06d5 \u06a9\u0627\u0645 \u0642\u0633\u06d5 \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u062e\u0648\u0627 \u0695\u06ce \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u06af\u06c6\u0695\u06ce\u060c \u0647\u06cc\u0686 \u0695\u06ce\u0628\u06d5\u0631\u06cc \u062f\u06d5\u0633 \u0646\u0627\u06a9\u06d5\u0648\u06ce. \u0648\u0627\u0632\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06ce\u0646\u06ce \u0628\u0627 \u0647\u06d5\u0631 \u0648\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u0628\u0632\u06ce\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u0633\u06d5\u0631\u06af\u06d5\u0631\u062f\u0627\u0646 \u0628\u0646.',
    '\u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0648\u06d5 \u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646: \u06a9\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u062f\u06ce\u061f \u0628\u06ce\u0698\u06d5: \u0632\u0627\u0646\u06cc\u0646\u06cc \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0645\u0646\u06d5\u061b \u0628\u06c6 \u062e\u06c6\u06cc \u0646\u06d5\u0628\u06ce \u06a9\u06d5\u0633 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u0648\u06d5\u062e\u062a\u06d5\u06a9\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u06a9\u0627\u061b \u0647\u06d5\u06b5\u06ce\u0646\u0627\u0646\u06cc \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u0632\u06c6\u0631 \u06af\u0631\u0627\u0646\u06d5 \u0648 \u0644\u06d5 \u0646\u06d5\u06a9\u0627\u0648\u06ce\u06a9 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0695\u0648\u0648\u062a\u0627\u0646 \u062a\u06ce \u062f\u06d5\u06a9\u0627. \u062c\u06c6\u0631\u06ce\u06a9 \u067e\u0631\u0633\u06cc\u0627\u0631\u062a \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u0648\u06d5\u06a9 \u062a\u06c6 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0628\u0632\u0627\u0646\u06cc. \u0628\u06ce\u0698\u06d5: \u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0645\u0646 \u062e\u0627\u0648\u06d5\u0646\u06cc \u0633\u0648\u0648\u062f \u0648 \u0632\u06cc\u0627\u0646\u06cc \u062e\u0648\u062f\u06cc \u062e\u06c6\u0645 \u0646\u06cc\u0645\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u062e\u0648\u062f\u0627 \u062d\u06d5\u0632 \u0628\u06a9\u0627. \u06af\u06d5\u0631 \u0646\u0627\u062f\u06cc\u0627\u0631\u0645 \u0632\u0627\u0646\u06cc\u0628\u0627\u0646 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0641\u0631\u06d5\u0645 \u06af\u06cc\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u062a \u0648 \u0632\u06cc\u0627\u0646\u06ce\u06a9\u0645 \u062a\u0648\u0648\u0634 \u0646\u06d5\u062f\u06d5\u0647\u0627\u062a. \u0645\u0646 \u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0648 \u0645\u0698\u062f\u06d5\u062f\u06d5\u0631\u0645 \u0628\u06c6 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5 \u06cc\u06d5\u06a9 \u06a9\u06d5\u0633 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0698\u0646\u06d5\u06a9\u06d5\u0634\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u0645\u0627\u06a9\u06cc \u0626\u06d5\u0648 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627\u060c \u06a9\u06d5 \u0644\u06d5\u0648\u0695\u0627 \u0628\u062d\u06d5\u0633\u06ce\u062a\u06d5\u0648\u06d5. \u0648\u06d5\u062e\u062a\u06ce [\u0626\u0627\u062f\u06d5\u0645] \u0648\u06d5\u0644 [\u062f\u0627\u06cc\u06a9\u06d5\u0648\u0627] \u0633\u06d5\u0631 \u062c\u06ce\u06cc \u06a9\u0631\u062f\u060c \u0628\u0627\u0631\u062f\u0627\u0631 \u0628\u0648\u0648 \u0628\u06d5 \u0628\u0627\u0631\u06ce\u06a9\u06cc \u0633\u0648\u0648\u06a9. \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u067e\u06ce\u06d5\u0648\u06d5 \u06af\u06d5\u0695\u0627. \u06a9\u06d5 \u0647\u06d5\u0633\u062a\u06cc \u0628\u06d5 \u0642\u0648\u0631\u0633 \u0628\u0648\u0648\u0646 \u06a9\u0631\u062f\u060c \u0647\u06d5\u0631\u062a\u06a9 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u067e\u0627\u0695\u0627\u0646\u06d5\u0648\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0632\u0627\u0631\u06c6\u06cc\u06ce \u0647\u06ce\u0698\u0627\u0645\u0627\u0646 \u067e\u06ce \u0628\u0628\u06d5\u062e\u0634\u06cc\u060c \u0644\u06d5 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646.',
    '\u06a9\u06d5 \u0632\u0627\u0631\u06c6\u06cc \u0647\u06ce\u0698\u0627\u06cc \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc\u0646\u060c \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u0627 \u067e\u06ce\u062f\u0627 \u0628\u0648\u0648\u0646\u060c \u0686\u06d5\u0646\u062f \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u061b \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u0628\u06d5\u0631\u0632\u062a\u0631\u06d5 \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0647\u06d5\u0628\u0646.',
    '\u0686\u06c6\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u06a9\u06d5\u0646 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06d5 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0647\u06cc\u0686 \u0634\u062a\u06ce \u062f\u0631\u0648\u0648\u0633\u062a \u0628\u06a9\u06d5\u0646 \u0648 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce \u062f\u0631\u0648\u0648\u0633\u062a \u0628\u06a9\u0631\u06ce\u0646.',
    '\u062a\u0648\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u062f\u06d5\u0646 \u0648 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062e\u06c6\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0646\u0627\u062f\u0631\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5\u0634 \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646 \u06a9\u06d5 \u0628\u06ce\u0646\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0647\u06cc \u062e\u0648\u062f\u0627\u060c \u062f\u0648\u0627\u0648 \u0646\u0627\u06a9\u06d5\u0648\u0646. \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646 \u06cc\u0627\u0646 \u062f\u06d5\u0646\u06af \u0646\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 - \u0628\u06d5 \u062c\u06ce \u062e\u0648\u062f\u0627 - \u0626\u06ce\u0648\u06d5 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0628\u06d5\u0646\u060c \u0639\u06d5\u0628\u062f\u06af\u06d5\u0644\u06ce\u06a9\u0646 \u0648\u06d5\u06a9 \u062e\u0648\u06d5\u062a\u0627\u0646. \u062f\u06d5\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u062a\u0646\u060c \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646 \u0628\u0627 \u0648\u06d5\u0631\u0627\u0645\u062a\u0627\u0646 \u0628\u062f\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u0627 \u0628\u0695\u06c6\u0646\u060c \u06cc\u0627\u0646 \u062f\u06d5\u0633\u062a\u06cc \u0695\u0627\u0633\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u06cc\u0627\u0646 \u0686\u0627\u0648\u06cc \u062f\u06cc\u062a\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u06cc\u0627\u0646 \u06af\u0648\u06ce\u06cc \u0628\u06cc\u0633\u062a\u0646\u06cc\u0627\u0646 \u067e\u06ce\u0648\u06d5\u06cc\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0628\u0627\u0646\u06af\u06cc \u0626\u06d5\u0648 \u0647\u0627\u0648\u0628\u06d5\u0634\u0627\u0646\u06d5\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646\u060c \u0626\u06d5\u0648\u0633\u0627 \u062f\u0627\u0648\u06cc \u0641\u06ce\u06b5\u0627\u0646\u0645 \u0628\u06c6 \u0628\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u0645\u0627\u0648\u06d5\u0645 \u0645\u06d5\u062f\u06d5\u0646.',
    '\u06af\u06d5\u0648\u0631\u06d5\u06cc \u0645\u0646 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u061b \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc \u0633\u06d5\u0631\u067e\u06d5\u0631\u0633\u062a\u06cc \u0686\u0627\u06a9\u0627\u0646 \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc - \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 - \u0626\u06ce\u0648\u06d5 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0628\u06d5\u0646\u060c \u0646\u0627\u0634\u06cc\u06d5\u0646 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062a\u0627\u0646 \u062f\u06d5\u0646 \u0648 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062e\u06c6\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0646\u0627\u062f\u0631\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u06ce\u06cc \u0695\u0627\u0633\u062a\u06cc\u0627\u0646 \u0628\u0627\u0646\u06af \u0628\u06a9\u06d5\u0646\u060c \u0644\u06ce\u0648 \u0646\u0627\u0628\u06cc\u0633\u062a\u0646\u061b \u062f\u06d5\u0628\u06cc\u0646\u06cc \u062a\u0648\u0627\u0634\u0627\u062a \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0646\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06cc\u062a\u0648\u0648\u0648\u06cc.',
    '\u0686\u0627\u0648 \u0628\u067e\u06c6\u0634\u06d5 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0628\u062f\u06d5 \u0648 \u0644\u06d5\u0648 \u0646\u06d5\u0632\u0627\u0646\u0627\u0646\u06d5 \u0648\u0627\u0632 \u0628\u06ce\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627\u06cc\u06d5\u06a9\u06cc\u0634 \u062e\u06d5\u06cc\u0627\u06b5\u0627\u062a\u06ce\u06a9\u06cc \u062e\u0631\u0627\u067e\u062a \u0644\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u062f\u06b5\u062f\u0627 \u0628\u0648\u0648\u0631\u06cc\u060c \u0647\u06d5\u0631 \u067e\u06d5\u0646\u0627 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631\u06d5\u061b \u06a9\u06d5 \u062e\u06c6\u06cc \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u0646\u060c \u0647\u06d5\u0631\u06af\u0627 \u062e\u06d5\u06cc\u0627\u06b5\u06cc \u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u0644\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06d5\u0648\u06d5 \u062a\u0648\u0648\u0634 \u062f\u06d5\u0628\u06ce\u060c \u06af\u0648\u0631\u062c\u06ce \u06cc\u0627\u062f\u06cc \u062e\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0628\u06d5\u0631\u0686\u0627\u0648\u06cc\u0627\u0646 \u0695\u0648\u0648\u0646 \u062f\u06d5\u0628\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0628\u0631\u0627\u06a9\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0631 \u06af\u0648\u0645\u0695\u0627 \u0628\u0646\u060c \u062a\u06ce \u062f\u06d5\u06a9\u06c6\u0634\u0646 \u0648 \u0644\u06d5 \u062a\u06d5\u0642\u0627\u0644\u0627\u062f\u0627\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u06a9\u06c6\u062a\u0627 \u0646\u0627\u06cc\u06d5\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0647\u06cc\u0686 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0646\u0627\u062f\u06d5\u06cc\u060c \u0626\u06ce\u0698\u0646: \u062a\u06c6 \u0628\u06c6 \u0644\u06d5 \u062e\u06c6\u062a\u06d5\u0648\u06d5 \u0695\u06ce\u06a9\u06cc \u0646\u0627\u062e\u06d5\u06cc\u061f \u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u062f\u06d5\u06a9\u06d5\u0645 \u06a9\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0695\u0627 \u067e\u06ce\u0645 \u062f\u06d5\u06af\u0627. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0686\u0627\u0648\u0695\u06c6\u0634\u0646 \u06a9\u06d5\u0631\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u0695\u0627 \u0648 \u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631\u0646 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0642\u0648\u0631\u0626\u0627\u0646 \u062f\u06d5\u062e\u0648\u06ce\u0646\u062f\u0631\u06ce\u060c \u06a9\u0695 \u0628\u0646 \u0648 \u06af\u0648\u06ce \u0644\u06ce \u0628\u062f\u06ce\u0631\u0646\u061b \u0634\u0627\u06cc\u06d5\u062a \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u0627 \u06a9\u06d5\u0648\u0646.',
    '\u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646\u060c \u0628\u06d5 \u067e\u0627\u0695\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u062a\u0631\u0633\u06d5\u0648\u06d5 - \u0628\u06d5 \u0628\u06ce \u0626\u06d5\u0648\u06d5 \u062f\u06d5\u0646\u06af \u0647\u06d5\u06b5\u06ce\u0646\u06cc - \u06cc\u0627\u062f\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06a9\u06d5 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06d5\u0628\u06d5 \u06a9\u06d5 \u0628\u06d5 \u0628\u06cc\u0631\u06cc\u0634\u06cc\u0627\u0646\u062f\u0627 \u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u067e\u06d5\u0633\u0646\u062f\u0646\u060c \u0628\u06ce \u0641\u06cc\u0632\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0633\u062a\u0646\u06cc \u0648 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u0633\u0648\u0698\u062f\u06d5 \u062f\u06d5\u0628\u06d5\u0646.',
    '\u0644\u06d5\u0645\u06d5\u0695 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06cc \u062c\u06d5\u0646\u06af\u06d5\u0648\u06d5 \u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646\u060c \u0628\u06ce\u0698\u06d5: \u062a\u0627\u06b5\u0627\u0646\u06cc \u062f\u0648\u0698\u0645\u0646\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0648 \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0626\u06ce\u0648\u06d5 \u062a\u0631\u0633\u0648\u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06ce \u0648 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u062a\u0627\u0646\u062f\u0627 \u067e\u06ce\u06a9 \u0648\u06d5\u0631\u0646 \u0648 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0628\u0646.',
    '\u062e\u0627\u0648\u06d5\u0646 \u0628\u0695\u0648\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0646 \u0647\u06d5\u0631 \u06a9\u06d5 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627 \u0628\u0631\u0627\u060c \u062f\u06b5\u06cc\u0627\u0646 \u062a\u0631\u0633\u06cc \u0695\u06ce \u062f\u06d5\u0646\u06cc\u0634\u06ce \u0648 \u06a9\u06d5\u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u0646\u060c \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u0647\u06ce\u0632\u062a\u0631 \u062f\u06d5\u0628\u06ce \u0648 \u062f\u06d5\u0631\u0628\u06d5\u0633\u062a \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0633\u067e\u06ce\u0631\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0646\u0648\u06ce\u0698 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u0628\u06d5\u0634\u0645\u0627\u0646 \u062f\u0627\u0648\u0646 \u0628\u06d5\u0634\u06cc \u0647\u06d5\u0698\u0627\u0631\u0627\u0646\u06cc\u0634 \u062f\u06d5\u062f\u06d5\u0646.',
    '\u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06cc \u0628\u06ce \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u067e\u0644\u06d5 \u0648 \u067e\u0627\u06cc\u06d5 \u0648 \u0644\u06ce \u062e\u06c6\u0634\u0628\u0648\u0648\u0646 \u0648 \u0628\u0698\u06cc\u0648\u06cc \u067e\u06d5\u0633\u0646\u062f\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a - \u0628\u06d5 \u0647\u06d5\u0642\u0627\u0646\u06d5\u062a - \u062a\u06c6\u06cc \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u062a \u0647\u06ce\u0646\u0627\u062f\u06d5\u0631\u061b \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0646\u0627\u0695\u0627\u0632\u06cc \u0628\u0648\u0648\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0695\u0627\u0633\u062a\u06cc \u0695\u0648\u0648\u0646 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u0634\u060c \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06ce\u0634\u06d5\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0648\u06d5\u06a9 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u0628\u06cc\u0646\u0646 \u06a9\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u0645\u0631\u062f\u0646\u06cc\u0627\u0646 \u062f\u0627\u0698\u06c6\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u062e\u0648\u0627 \u0633\u06c6\u0632\u06cc \u067e\u06ce\u062f\u0627\u0646 \u06a9\u06d5 \u06cc\u06d5\u06a9\u06ce\u06a9 \u0644\u06d5\u0648 \u062f\u0648\u0648 \u06a9\u06c6\u0645\u06d5\u06b5\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc\u06d5\u060c \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u062a\u0627\u0646 \u0628\u0648\u0648 \u062f\u06d5\u0633\u062a\u06d5 \u0628\u06ce \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5\u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u0646. \u062e\u0648\u062f\u0627\u0634 \u0648\u06cc\u0633\u062a\u06cc \u0647\u06d5\u0631 \u0686\u06c6\u0646\u06ce \u062e\u06c6\u06cc \u06af\u0648\u062a\u0648\u0648\u06cc\u06d5\u062a\u06cc \u0648\u06d5\u0695\u0627\u0633\u062a \u06af\u06d5\u0695\u06ce \u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u0646\u06d5\u0628\u0695 \u06a9\u0627.',
    '\u062a\u0627 \u0695\u0627\u0633\u062a\u06cc \u0633\u06d5\u0642\u0627\u0645 \u06af\u06cc\u0631 \u0628\u06a9\u0627 \u0648 \u067e\u0648\u0648\u0686 \u0648 \u0686\u0631\u0648\u0648\u06a9 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u06d5\u0631\u06ce\u061b \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06d5\u06a9\u0627\u0646 \u062d\u06d5\u0632\u06cc \u0644\u06ce \u0646\u06d5\u06a9\u06d5\u0646.',
    '\u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634\u062f\u0627 \u06a9\u06d5 \u0647\u0627\u0646\u0627\u062a\u0627\u0646 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u062f\u06d5\u0628\u0631\u062f\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u0627 \u0628\u06d5 \u0634\u0648\u06ce\u0646 \u06cc\u06d5\u06a9\u062f\u0627 \u0647\u06d5\u0632\u0627\u0631 \u0641\u0631\u06cc\u0634\u062a\u06d5 \u062f\u06d5\u0646\u06ce\u0631\u0645 \u0628\u06c6 \u06cc\u0627\u0631\u06cc\u062a\u0627\u0646.',
    '\u062e\u0648\u062f\u0627 \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u0634\u06cc \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u067e\u06ce\u062f\u0627\u0646\u060c \u0647\u06d5\u062a\u0627\u06a9\u0648\u0648 \u0645\u0648\u0698\u062f\u06d5\u06cc\u06d5\u06a9 \u0628\u06ce\u062a\u0648\u0648 \u062f\u06b5\u06af\u06d5\u0644\u062a\u0627\u0646 \u067e\u06ce \u0626\u06c6\u0642\u0631\u06d5 \u0628\u06af\u0631\u06ce. \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0634 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5\u06cc\u06d5. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u0634\u062a\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u06ce \u0648\u0627 \u0626\u06d5\u0648 \u0633\u0648\u0648\u06a9\u06d5\u062e\u06d5\u0648\u06d5\u06cc \u0644\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u06cc\u06d5\u0648\u06d5 \u0628\u06c6 \u0647\u06d5\u0646\u0627\u0631\u062f\u0646 \u0648 \u0628\u0648\u0648 \u0628\u06d5 \u0647\u06c6\u06cc \u062f\u06b5\u0646\u06cc\u0627\u06cc\u06cc\u062a\u0627\u0646. \u0628\u0627\u0631\u0627\u0646\u06cc\u0634\u06cc \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0628\u06c6 \u0628\u0627\u0631\u0627\u0646\u062f\u0646 \u062a\u0627 \u062e\u0627\u0648\u06ce\u0646\u062a\u0627\u0646 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u062e\u06d5\u06cc\u0627\u06b5\u0627\u062a\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u0648\u0648 \u0644\u06ce \u062f\u0648\u0648\u0631\u062e\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u062f\u06b5\u06af\u06d5\u0631\u0645\u062a\u0627\u0646 \u06a9\u0627 \u0648 \u0645\u06d5\u06cc\u062f\u0627\u0646\u06ce \u0628\u06d5 \u062c\u06ce \u0646\u06d5\u0647\u06ce\u06b5\u0646.',
    '\u062e\u0648\u0627 \u0641\u0631\u06cc\u0634\u062a\u06d5\u06cc \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646 \u06a9\u06d5: \u062e\u06c6\u0634\u0645 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5\u062f\u0627\u0645\u060c \u063a\u06cc\u0631\u06d5\u062a \u0648\u06d5\u0628\u06d5\u0631 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646 \u0628\u06ce\u0646\u0646\u060c \u0645\u0646\u06cc\u0634 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u062f\u06d5\u0646\u06ce\u0645. \u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0645\u0644\u06cc\u0627\u0646 \u062f\u06d5\u0646 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0626\u06d5\u0646\u062a\u06af\u0648\u0633\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0628\u067e\u06d5\u0695\u06ce\u0646\u0646.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0631\u0647\u06d5\u06b5\u0633\u062a\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0648\u06ce\u0633\u062a\u0627\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9 \u062f\u0698\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u0646\u060c \u062e\u0648\u0627 \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u0633\u0632\u0627 \u062f\u06d5\u062f\u0627.',
    '\u0628\u0627 \u0628\u06cc\u0686\u06ce\u0698\u0646! \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u06d5 \u0626\u0627\u06af\u0631\u06cc\u0634 \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u0648\u0648\u0646 - \u0628\u0627 \u0632\u06c6\u0631\u06cc\u0634 \u0628\u0646 - \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0628\u06d5\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06b5 \u0645\u06d5\u06cc\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u0648\u0627\u062f\u0627 \u062e\u06c6 \u0644\u06d5 \u0634\u06d5\u0695\u06ce \u0628\u062f\u0632\u06ce\u062a\u06d5\u0648\u06d5 - \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06c6 \u0634\u06d5\u0695 \u06a9\u0631\u062f\u0646 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u06a9\u06d5\u0648\u06d5\u060c \u06cc\u0627\u0646 \u0628\u06c6 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u06cc\u0627\u0631\u0627\u0646\u06cc - \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062e\u06c6 \u062a\u0648\u0648\u0695\u06d5 \u062f\u06d5\u06a9\u0627 \u0648 \u062c\u06ce\u06af\u06d5\u06cc \u0644\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u062f\u06d5\u0628\u06ce \u0648 \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06cc \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u062a\u0627\u0646 \u06a9\u0648\u0634\u062a\u0646\u061b \u062e\u0648\u0627 \u06a9\u0648\u0634\u062a\u0646\u06cc. \u06a9\u06d5 \u0647\u0627\u0648\u06cc\u0634\u062a\u062a\u060c \u0647\u0627\u0648\u06ce\u0698\u06d5\u0631\u06d5\u06a9\u06d5 \u062a\u06c6 \u0646\u06d5\u0628\u0648\u0648\u06cc\u061b \u062e\u0648\u0627 \u0647\u0627\u0648\u06cc\u0634\u062a\u06cc. \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u06d5\u0648\u06cc\u0633\u062a \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0628\u06d5 \u0628\u0627\u0634\u06cc \u062a\u0627\u0642\u06cc \u06a9\u0627\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0628\u06d5\u06b5\u06ce\u060c \u0626\u0627\u0648\u0627 \u062e\u0648\u062f\u0627 \u0641\u06ce\u06b5\u06cc \u062f\u06cc\u0646 \u062f\u0648\u0698\u0645\u0646\u0627\u0646 \u067e\u0648\u0648\u0686\u06d5\u06b5 \u062f\u06d5\u06a9\u0627.',
    '\u06af\u06d5\u0631 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06cc\u0633\u062a \u0633\u06d5\u0631\u06a9\u06d5\u0648\u0646\u060c \u0648\u0627 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0634\u06d5\u0695 \u0628\u06d5\u0633\u06cc\u0634 \u0628\u06a9\u06d5\u0646\u060c \u0642\u0627\u0632\u0627\u0646\u062c\u062a\u0627\u0646\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u062a\u06ce \u0647\u06d5\u06b5 \u0628\u0686\u0646\u06d5\u0648\u06d5\u060c \u0626\u06ce\u0645\u06d5\u0634 \u062a\u06ce \u0647\u06d5\u06b5 \u062f\u06d5\u0686\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u0627 \u062f\u06d5\u0633\u062a\u06d5\u0648\u0628\u06d5\u0633\u062a\u06d5\u062a\u0627\u0646 \u0632\u06c6\u0631 \u0628\u0646\u060c \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u062f\u0627. \u062e\u0648\u062f\u0627 \u0648\u06d5\u0644 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u0631\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0628\u0646 \u0648 \u0644\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u0626\u06d5\u0648 \u067e\u06ce\u062a\u0627\u0646 \u0626\u06ce\u0698\u06ce \u0648 \u062f\u06d5\u06cc\u0628\u06cc\u0633\u0646\u060c \u062e\u06c6 \u0645\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u0626\u06ce\u0648\u06d5 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06d5\u0628\u0646\u060c \u06af\u0648\u062a\u0648\u0648\u06cc\u0627\u0646\u06d5: \u06af\u0648\u06ce\u0645\u0627\u0646 \u0644\u06ce\u06cc\u06d5\u060c \u06a9\u06d5 \u0686\u06cc \u0647\u06d5\u0631 \u0646\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06cc\u0633\u062a\u0648\u0648\u06d5.',
    '\u062e\u0631\u0627\u062a\u0631\u06cc\u0646 \u0632\u06cc\u0646\u062f\u06d5\u0648\u06d5\u0631\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0695 \u0648 \u06b5\u0627\u06b5\u0627\u0646\u06d5\u0646 \u06a9\u06d5\u0648\u0627 \u0647\u06cc\u0686 \u062a\u06ce \u0646\u0627\u06af\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u06cc\u0632\u0627\u0646\u06cc\u0628\u0627 \u062e\u06ce\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0627\u0631\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u062f\u06d5\u06a9\u0631\u062f \u0628\u0628\u06cc\u0633\u0646. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u06af\u0648\u06ce \u0628\u06cc\u0633\u062a\u0646\u06cc\u0634\u06cc \u067e\u06ce \u0628\u062f\u0627\u0628\u0627\u0646\u060c \u0647\u06d5\u0631 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u062f\u06d5\u06af\u06ce\u0695\u0627 \u0648 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0648\u0627\u0631\u062f.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0631\u06af\u0627 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0644\u06ce\u0648 \u062f\u0627\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u0627\u0631\u06ce \u0628\u06a9\u06d5\u0646 \u0626\u06ce\u0648\u06d5\u06cc \u067e\u06ce \u0628\u0698\u0648\u0648\u0698\u06ce\u0646\u06d5\u0648\u06d5\u060c \u06af\u0648\u0631\u062c\u06ce \u0628\u06cc\u06a9\u06d5\u0646\u061b \u0626\u06ce\u0648\u06d5\u06cc \u067e\u06ce \u062f\u06d5\u0628\u0648\u0648\u0698\u06ce\u0646\u06d5\u0648\u06d5. \u0628\u0634\u0632\u0627\u0646\u0646 \u062e\u0648\u0627 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0644\u06d5\u0645\u067e\u06d5\u0631 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u0645\u0631\u06c6 \u0648 \u062f\u06b5\u06cc\u061b \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u0634\u062a\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u06a9\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0648 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u0634 \u062e\u06c6 \u067e\u0627\u0631\u06ce\u0632\u0646 \u06a9\u06d5 \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u0646\u0627\u062d\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u0628\u0634\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u062a\u06c6\u06b5\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062a\u0648\u0646\u062f \u0648 \u062a\u06cc\u0698\u06d5.',
    '\u0628\u06cc\u0631 \u0644\u06d5\u0648 \u062f\u06d5\u0645\u06d5 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0645 \u0648\u06b5\u0627\u062a\u06d5\u062f\u0627 \u06a9\u06d5\u0645\u06ce\u06a9\u06cc \u0628\u06ce \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0628\u0648\u0648\u0646\u061b \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062a\u0631 \u062f\u06d5\u0631\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646 \u0648 \u0628\u06ce \u062c\u06ce \u0648 \u0695\u06ce \u0628\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5. \u067e\u06d5\u0646\u0627\u06cc \u062f\u0627\u0646 \u0648 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062f\u0627\u0646 \u0648 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646 \u0648 \u0695\u06c6\u0632\u06cc \u067e\u0627\u06a9 \u0648 \u0695\u06d5\u0648\u0627\u06cc \u067e\u06ce \u062f\u0627\u0646\u061b \u0628\u06d5\u0634\u06a9\u06d5 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631 \u0628\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062f\u06d5\u063a\u06d5\u06b5\u06cc \u0645\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u0632\u0627\u0646\u0646 \u0626\u0627\u0645\u0627\u0646\u06d5\u062a\u0646\u060c \u063a\u06d5\u06cc\u0627\u0646\u06d5\u062a\u06cc \u062a\u06ce\u062f\u0627 \u0645\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u0634\u0632\u0627\u0646\u0646 \u0645\u0627\u06b5 \u0648 \u0645\u0646\u062f\u0627\u06b5\u062a\u0627\u0646 \u0647\u06c6\u06cc \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u0646\u061b \u067e\u0627\u062f\u0627\u0634\u06cc \u0632\u06c6\u0631 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646\u060c \u0626\u0627\u0645\u06ce\u0631\u06cc \u062c\u06cc\u0627 \u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5\u06cc \u067e\u0627\u06a9 \u0648 \u067e\u06cc\u0633\u062a\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u0627\u062a \u0648 \u0644\u06d5 \u062e\u0631\u0627\u067e\u06d5\u062a\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce. \u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u0686\u0627\u06a9\u06d5\u06cc \u0644\u06d5 \u0644\u0627\u06cc\u06d5.',
    '\u0628\u06ce\u062a\u06d5 \u0628\u06cc\u0631\u062a \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0648\u06cc\u0633\u062a\u06cc\u0627\u0646 \u0641\u06ce\u06b5\u06ce\u06a9\u062a \u0644\u06ce \u0628\u06a9\u06d5\u0646\u060c \u06cc\u0627\u0646 \u0628\u06d5\u0646\u062f\u062a \u06a9\u06d5\u0646\u060c \u06cc\u0627\u0646 \u0628\u062a\u06a9\u0648\u0698\u0646\u060c \u06cc\u0627\u0646 \u062f\u06d5\u0631\u062a \u06a9\u06d5\u0646. \u0626\u06d5\u0648\u0627\u0646 \u0641\u06ce\u06b5\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u062e\u0648\u0627\u0634 \u0641\u06ce\u06b5\u06cc \u06a9\u0631\u062f. \u062e\u0648\u0627\u0634 \u0686\u0627\u062a\u0631\u06cc\u0646 \u0641\u06ce\u06b5\u06d5\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u0646\u060c \u0626\u06ce\u0698\u0646: \u0628\u06cc\u0633\u0645\u0627\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632\u0645\u0627\u0646 \u0628\u06a9\u0631\u062f\u0627\u06cc\u06d5\u060c \u0626\u06ce\u0645\u06d5\u0634 \u0648\u06d5\u06a9 \u0626\u06d5\u0645\u06d5\u0645\u0627\u0646 \u0626\u06d5\u0648\u062a\u061b \u0644\u06d5 \u0686\u06d5\u0646\u062f \u0686\u06cc\u0631\u06c6\u06a9\u06cc \u06a9\u06d5\u0648\u0646\u0627\u0631\u0627 \u0628\u06d5\u062f\u06d5\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5! \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc\u06d5 \u0648 \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u0646 \u062a\u06c6\u0648\u06d5 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0648\u06d5\u0628\u06d5\u0631 \u062a\u06d5\u0648\u06d5\u0646 \u0628\u0627\u0631\u0627\u0646\u0645\u0627\u0646 \u062f\u06d5\u060c \u06cc\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646\u0645\u0627\u0646 \u0628\u06c6 \u0628\u0646\u06ce\u0631\u06d5.',
    '\u0647\u06d5\u062a\u0627 \u062a\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648\u062f\u0627 \u0628\u06ce\u060c \u062e\u0648\u062f\u0627 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u0627\u0646 \u0646\u0627\u062f\u0627. \u0647\u06d5\u062a\u0627 \u062f\u0627\u0648\u0627\u06cc \u0628\u0648\u0648\u0631\u062f\u0646 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06cc\u0634 \u0628\u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0646\u0627\u062f\u0627.',
    '\u062e\u0648\u062f\u0627 \u0686\u06c6\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0627\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u00ab\u0645\u0633\u062c\u062f\u0627\u0644\u062d\u0631\u0627\u0645\u00bb \u06cc\u0627\u0646 \u0644\u06d5 \u0632\u06cc\u0627\u0631\u06d5\u062a \u06a9\u06d5\u0631\u0627\u0646 \u06af\u0631\u062a\u0648\u0648\u06d5 \u0648 \u0647\u06d5\u06b5\u06cc\u0634 \u0646\u0627\u06af\u0631\u0646 \u062e\u0627\u0648\u06d5\u0646\u06cc \u0628\u0646\u061f \u062e\u0627\u0648\u06d5\u0646\u0627\u0646\u06cc \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0628\u0646 \u06a9\u06d5 \u062a\u0631\u0633\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u0646\u0648\u06ce\u0698\u06cc\u0627\u0646 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u0648\u062f\u0627\u062f\u0627 \u0647\u06d5\u0631 \u0628\u0631\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u0686\u06d5\u067e\u06b5\u06d5 \u0648 \u0641\u06cc\u062a\u0648\u0648\u06a9\u06ce\u0634\u0627\u0646\u06d5. \u0633\u0627 \u0644\u06d5 \u0633\u0632\u0627\u06cc \u0626\u06d5\u0648 \u0628\u0695\u0648\u0627 \u0646\u06d5\u0647\u06ce\u0646\u0627\u0646\u06d5\u062f\u0627\u060c \u0626\u06d5\u0634\u06ce \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631 \u0628\u0628\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062e\u06d5\u0631\u062c \u062f\u06d5\u06a9\u06d5\u0646 \u0695\u06ce\u06af\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0628\u06af\u0631\u0646. \u0626\u06d5\u0648 \u0645\u0627\u06b5 \u0628\u06d5\u062e\u062a \u06a9\u0631\u062f\u0646\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u06a9\u06d5\u0633\u06d5\u0631\u06ce\u06a9 \u0628\u06c6\u06cc\u0627\u0646 \u0648 \u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0631 \u0698\u06ce\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u0646. \u062e\u0695\u0648\u067e\u0695\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0644\u06d5 \u062f\u06c6\u0698\u06d5\u06d5 \u06a9\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 \u067e\u0627\u06a9 \u0648 \u067e\u06cc\u0633 \u0644\u06ce\u06a9 \u0647\u06d5\u06b5\u0627\u0648\u06ce\u0631\u06ce \u0648 \u067e\u06cc\u0633\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u06cc\u06d5\u06a9 \u062f\u0627\u0646\u06ce \u0648 \u0628\u06d5\u062a\u06ce\u06a9\u0695\u0627\u06cc\u06cc \u06a9\u06c6\u06af\u0627\u06cc\u0627\u0646 \u062f\u0627 \u0648 \u0628\u06cc\u0627\u0646\u062e\u0627\u062a\u06d5 \u062f\u06c6\u0698\u06d5\u0647\u06d5\u0648\u06d5\u061b \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631 \u0628\u0648\u0648\u06af\u0646.',
    '\u0628\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0633\u062a\u0627\u0634 \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0633 \u0647\u06d5\u06b5\u06af\u0631\u0646\u060c \u0647\u06d5\u0631\u0686\u06cc \u067e\u06ce\u0634\u0648\u0648 \u06a9\u0631\u062f\u0648\u0648\u062a\u0627\u0646\u06d5 \u0639\u0627\u0641\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce. \u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u062a\u06ce \u0647\u06d5\u06b5 \u0628\u0686\u0646\u06d5\u0648\u06d5 \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646 \u062f\u06cc\u0627\u0631\u06d5 \u0686 \u0628\u0648\u0648.',
    '\u0648\u06d5\u0644\u06cc\u0627\u0646 \u0634\u06d5\u0695 \u06a9\u06d5\u0646 \u0647\u06d5\u062a\u0627 \u0626\u0627\u0698\u0627\u0648\u06d5 \u0646\u0627\u0645\u06ce\u0646\u06ce \u0648 \u062f\u06cc\u0646 \u06af\u0634\u062a\u06cc \u062f\u06cc\u0646\u06cc \u062e\u0648\u0627 \u062f\u06d5\u0628\u06ce. \u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06d5\u0645\u06ce \u06af\u0631\u062a\u0648\u0648 \u0628\u0648\u0648\u0646 \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06cc\u06a9\u06d5\u0646 \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u060c \u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u062a\u0627\u0646 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u06af\u06d5\u0648\u0631\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u0627\u0634\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u062a\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u0628\u06d5\u0648\u06d5\u0634 \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0695\u06c6\u0698\u06cc \u0644\u06ce\u06a9 \u0647\u06d5\u06b5\u0627\u0648\u0627\u0631\u062f\u0646 \u0628\u06c6 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5\u062e\u0648\u0627\u0631\u060c \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u062f\u0648\u0648 \u06a9\u06c6\u0645\u06d5\u06b5 \u062a\u0648\u0648\u0634\u06cc \u06cc\u06d5\u06a9 \u0628\u0648\u0648\u0646\u061b \u062f\u06d5\u0628\u06ce \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0634 \u062a\u06ce \u0628\u06af\u06d5\u0646 \u0647\u06d5\u0631 \u062a\u0627\u06b5\u0627\u0646\u06ce\u06a9\u0648\u0648 \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u062a\u060c \u067e\u06ce\u0646\u062c \u06cc\u06d5\u06a9\u06ce\u06a9\u06cc \u0628\u06d5\u0634\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u0648 \u0628\u06c6 \u062e\u0632\u0645\u0627\u0646 \u0648 \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646 \u0648 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646 \u0648 \u0695\u06ce\u0628\u0648\u0627\u0631\u0627\u0646\u06d5. \u062e\u0648\u062f\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062f\u0627\u0648\u06ce\u0646\u06d5\u06cc \u0646\u0632\u06cc\u06a9\u062a\u0631 \u0648 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u062f\u0627\u0648\u06ce\u0646\u06d5\u06cc \u062f\u0648\u0648\u0631\u062a\u0631 \u062f\u0627\u0645\u06d5\u0632\u0631\u0627 \u0628\u0648\u0648\u0646\u060c \u06a9\u0627\u0631\u0648\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u062e\u0648\u0627\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u0648\u0648\u060c \u0626\u06d5\u06af\u06d5\u0631 \u06a9\u0627\u062a\u06cc \u067e\u06ce\u06a9 \u06af\u06d5\u06cc\u0634\u062a\u0646\u0648\u0648 \u062f\u0627\u0646\u0627\u0628\u0627\u060c \u0628\u06ce \u0633\u06c6 \u0644\u06d5 \u0633\u06d5\u0631 \u0698\u0648\u0627\u0646\u06af\u06d5 \u062a\u0648\u0648\u0634\u06cc \u06a9\u06ce\u0634\u06d5 \u062f\u06d5\u0628\u0648\u0648\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062f\u06d5\u0648\u0628\u0648 \u0647\u06d5\u0631\u0686\u06cc \u062e\u0648\u0627 \u0628\u0695\u06cc\u0627\u0631\u06cc \u062f\u0627\u0628\u0648\u0648 \u0647\u06d5\u0631 \u0695\u0648\u0648 \u0628\u062f\u0627. \u062a\u0627 \u0645\u0631\u062f\u0646 \u0648 \u0698\u06cc\u0646\u06cc \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u060c \u0628\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0648\u06d5 \u0628\u06ce. \u062e\u0648\u0627 \u062f\u06d5\u0628\u06cc\u0633\u06ce \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0644\u06d5 \u062e\u06d5\u0648\u062a\u0646\u062f\u0627 \u062e\u0648\u062f\u0627 \u0698\u0645\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u06a9\u06d5\u0645 \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u06cc\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0632\u06c6\u0631\u0627\u06cc\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0628\u0627\u0646\u060c \u0644\u06d5 \u062a\u0631\u0633\u0627\u0646 \u0632\u0631\u0627\u0648\u062a\u0627\u0646 \u062f\u06d5\u0686\u0648\u0648\u061b \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u062f\u0627 \u06a9\u06ce\u0634\u06d5\u062a\u0627\u0646 \u0644\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u0628\u0648\u0648. \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0626\u06ce\u0648\u06d5\u06cc \u067e\u0627\u0631\u0627\u0633\u062a\u060c \u062e\u06c6\u06cc \u0644\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u062f\u06b5\u0627\u0646\u06cc\u0634 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u06a9\u06d5 \u062a\u0648\u0648\u0634\u06cc \u06cc\u06d5\u06a9 \u0647\u0627\u062a\u0646\u060c \u0644\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u0648\u062a\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06cc \u06a9\u06d5\u0645\u062a\u0631 \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627 \u0648 \u0626\u06ce\u0648\u06d5\u0634\u06cc \u06a9\u06d5\u0645\u062a\u0631 \u0647\u06ce\u0646\u0627\u06cc\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646. \u062f\u06d5\u0628\u0648\u0648 \u062e\u0648\u062f\u0627 \u06a9\u0627\u0631\u06ce \u06a9\u06d5 \u0628\u0695\u06cc\u0627\u0631\u06cc \u062f\u0631\u0627\u0628\u0648\u0648 \u0628\u06ce\u0646\u06ce\u062a\u06d5 \u062c\u06ce. \u06a9\u0627\u0631\u06cc\u0634 \u0647\u06d5\u0631 \u062f\u06d5\u0686\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648.',
    '\u0626\u06d5\u06cc \u06af\u0631\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u0648\u0698\u0645\u0646\u0627\u0646 \u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648 \u062f\u06d5\u0628\u0646\u060c \u062e\u06c6 \u0628\u06af\u0631\u0646 \u0648 \u06af\u06d5\u0644\u06ce\u06a9 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646\u060c \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0626\u06ce\u0648\u06d5 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u0646.',
    '\u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062f\u06d5\u0631\u0645\u06d5\u0686\u0646. \u0646\u06d5\u0686\u0646 \u06a9\u06ce\u0634\u06d5 \u0648 \u0644\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u0628\u06ce \u0648 \u0633\u0633\u062a \u0628\u0628\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0647\u06ce\u0632 \u0648 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0628\u0686\u06ce. \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0646\u061b \u062e\u0648\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06c6\u0695\u0627\u06af\u0631\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0648\u06d5 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06d5\u0628\u0646\u060c \u0628\u06c6 \u062e\u06c6\u0628\u06cc\u0646\u06cc \u0648 \u0628\u06c6 \u0695\u0648\u0648\u0628\u06cc\u0646\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0645\u06d5\u0631\u062f\u0645 \u0644\u06d5 \u0632\u06ce\u062f\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u061b \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06cc\u0633\u062a \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631\u0628\u06d5\u0633\u062a \u0628\u06a9\u06d5\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0634\u06d5\u06cc\u062a\u0627\u0646 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5 \u0644\u06d5 \u0628\u06d5\u0631 \u062c\u0648\u0627\u0646 \u06a9\u0631\u062f\u0646\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0695\u06c6 \u0647\u06cc\u0686 \u0647\u06ce\u0632\u06ce\u06a9 \u062f\u06d5\u0631\u06c6\u0633\u062a\u0648\u0648\u0646\u0627\u06cc\u06d5\u060c \u0645\u0646\u06cc\u0634 \u062c\u06ce\u06af\u06d5\u06cc \u0647\u0627\u0646\u0627\u062a\u0627\u0646\u0645. \u0644\u06d5 \u06a9\u0627\u062a\u06ce\u06a9\u0627 \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9 \u0644\u0627 \u0644\u06ce\u06a9 \u0648\u06d5\u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u060c \u067e\u06ce \u062f\u0632\u06d5\u06cc \u06a9\u0631\u062f \u0648 \u0628\u06c6\u06cc \u062f\u06d5\u0631\u0686\u0648\u0648\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0646 \u0648\u06d5\u0644 \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u0645\u061b \u0645\u0646 \u0634\u062a\u06cc \u0648\u0627\u0645 \u062f\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648 \u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u062f\u06cc\u0627\u0631\u06d5\u061b \u0645\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u0645 \u0647\u06d5\u06cc\u06d5\u061b \u062a\u06c6\u06b5\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631\u06d5.',
    '\u062f\u0648\u0648\u0695\u0648\u0648\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06b5\u06cc \u062f\u06d5\u063a\u06d5\u0632\u062f\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 \u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5 \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062e\u06d5\u06b5\u06d5\u062a\u0627\u0648\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0647\u06d5\u0631 \u062e\u06c6 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u067e\u06ce\u0631\u06ce\u060c \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u06af\u06d5\u0631 \u0628\u0628\u06cc\u0646\u06cc \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0641\u0631\u06cc\u0634\u062a\u06d5 \u062f\u06d5\u06cc\u0627\u0646 \u0645\u0631\u06ce\u0646\u0646 \u0648 \u062f\u06cc\u0645 \u0648 \u067e\u0634\u062a\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0648\u062a\u0646\u06d5\u0648\u06d5 [\u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646]: \u062f\u06d5\u06cc\u0633\u0627 \u062f\u06d5\u0631\u062f\u06cc \u0628\u06d5 \u0626\u0627\u06af\u0631 \u0633\u0648\u0627\u062a\u0627\u0646 \u0628\u0686\u06ce\u0698\u0646.',
    '\u0626\u06d5\u0645\u06d5 \u0633\u0632\u0627\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u062a\u0627\u0646\u06a9\u0631\u062f\u061b \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u063a\u06d5\u062f\u0631 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u06cc \u0646\u0627\u06a9\u0627.',
    '[\u0626\u0627\u06a9\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u0631] \u0648\u06d5\u06a9 \u0626\u0627\u06a9\u0627\u0631\u06cc \u062f\u0627\u0631\u0648\u062f\u06d5\u0633\u062a\u06d5\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc\u06d5 \u0628\u06d5\u0631\u0644\u06d5\u0648\u0627\u0646 \u0628\u0648\u0648\u0646\u061b \u06a9\u06d5 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f. \u062e\u0648\u0627\u0634 \u0644\u06d5 \u0633\u0632\u0627\u06cc \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u063a\u06d5\u0632\u06d5\u0628\u06cc \u062e\u06c6\u06cc \u062e\u0633\u062a\u0646. \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0647\u06ce\u0632 \u0648 \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5 \u0648 \u062a\u06c6\u06b5\u06d5\u06cc \u0626\u06d5\u0648 \u06af\u06d5\u0644\u06ce \u062f\u0698\u0648\u0627\u0631\u06d5.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0648\u0648\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u0644\u06d5 \u0647\u06d5\u0631 \u06af\u06d5\u0644\u06ce \u0648\u06d5\u06a9\u0631\u062f\u0628\u06ce\u060c \u0647\u06d5\u062a\u0627 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06cc\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0646\u06d5\u06af\u06c6\u0695\u06cc\u0628\u06ce\u060c \u0642\u06d5\u062a \u062e\u0648\u062f\u0627 \u0644\u06ce\u06cc \u0646\u06d5\u06af\u06c6\u0695\u06cc\u0648\u0646. \u062e\u0648\u0627 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u06c6\u0631 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5 - \u0648\u06d5\u06a9 \u062f\u0627\u0631\u0648\u062f\u06d5\u0633\u062a\u06d5\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u067e\u06ce\u0634\u06cc\u0646\u06cc\u0627\u0646\u06cc\u0627\u0646 - \u0628\u06d5\u0648 \u062f\u06d5\u0631\u062f\u06d5 \u0686\u0648\u0648\u0646: \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0646\u06d5\u06a9\u0631\u062f\u061b \u0644\u06d5 \u0633\u06c6\u0646\u06af\u06d5\u06cc \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0642\u0695\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646. \u0647\u06d5\u0631\u0686\u06cc \u0633\u06d5\u0631 \u0628\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u06cc\u0634 \u0628\u0648\u0648\u0646\u060c \u0628\u0695\u06cc\u0627\u0631\u0645\u0627\u0646\u062f\u0627 \u06a9\u06d5 \u0644\u06d5 \u0626\u0627\u0648\u06ce\u062f\u0627 \u0628\u062e\u0646\u06a9\u06ce\u0646\u061b \u06a9\u06d5 \u0633\u06d5\u0631 \u0644\u06d5 \u0628\u06d5\u0631\u06cc\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642 \u0628\u0648\u0648\u0646.',
    '\u062e\u0631\u0627\u062a\u0631\u06cc\u0646 \u0632\u06cc\u0646\u062f\u06d5\u0648\u06d5\u0631\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5 \u0648 \u0626\u06cc\u062a\u0631\u06cc\u0634 \u0628\u0695\u0648\u0627 \u0646\u0627\u0647\u06ce\u0646\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06d5\u0633\u062a\u0646 \u0648 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646\u0627 \u0647\u06d5\u0645\u0648\u0648 \u062c\u0627\u0631\u0627\u0646 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062f\u06d5\u0648\u06d5\u0634\u06ce\u0646\u0646 \u0648 \u0647\u06cc\u0686 \u062a\u0631\u0633\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u060c',
    '\u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062a \u06a9\u06d5 \u0644\u06d5 \u0634\u06d5\u0695\u062f\u0627 \u0628\u06d5 \u06af\u06cc\u0631 \u0647\u06ce\u0646\u0627\u0646\u060c \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0647\u0627\u062a\u0648\u0648\u0646 \u067e\u0634\u062a\u06cc\u0627\u0646 \u0628\u06af\u0631\u0646\u060c \u0628\u0695\u06d5\u062a\u06ce\u0646\u06d5\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u062a\u06d5\u0645\u06ce \u06af\u0631\u062a\u0648\u0648 \u0628\u0628\u0646.',
    '\u06af\u06d5\u0631 \u0647\u06d5\u0633\u062a\u062a \u06a9\u0631\u062f \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u0644\u06d5\u0648 \u0633\u0648\u06ce\u0646\u062f\u062e\u06c6\u0631\u0627\u0646\u06d5 \u063a\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0686\u0648\u0648\u0646\u060c \u062a\u06c6\u0634 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646 \u0633\u0648\u06ce\u0646\u062f\u06cc\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u062f\u0627 \u0628\u062f\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u063a\u06d5\u06cc\u0627\u0646\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce\u0646.',
    '\u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0648\u0627 \u0628\u06cc\u0631 \u0646\u06d5\u0628\u06d5\u0646 \u0648\u06d5\u067e\u06ce\u0634 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u061b \u0626\u06d5\u0648\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u0646\u0627\u0628\u06d5\u0632\u06ce\u0646\u0646.',
    '\u062a\u0627 \u0628\u06c6\u0648 \u062f\u06d5\u06a9\u0631\u06ce \u0686\u06d5\u06a9 \u0648 \u062c\u0628\u06d5\u062e\u0627\u0646\u06d5\u06cc \u0634\u06d5\u0695 \u0648 \u0626\u06d5\u0633\u067e\u06cc \u0633\u0648\u0627\u0631\u06cc \u067e\u06d5\u06cc\u062f\u0627 \u0628\u06a9\u06d5\u0646\u061b \u062f\u0648\u0698\u0645\u0646\u06cc \u067e\u06ce \u0628\u062a\u0631\u0633\u06ce\u0646\u0646\u061b \u06a9\u06d5 \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u0634\u0646. \u063a\u06d5\u06cc\u0631\u06cc \u0648\u0627\u0646\u06cc\u0634 \u062f\u0648\u0698\u0645\u0646\u0648\u0648 \u0647\u06d5\u0646 \u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u0633\u0646\u060c \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0633\u06ce. \u0647\u06d5\u0631 \u0645\u0627\u06b5\u06ce\u06a9\u06cc \u0644\u06d5 \u0695\u0627\u0647\u06cc \u062e\u0648\u062f\u0627\u06cc \u0628\u06d5\u062e\u062a \u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0628\u0698\u06ce\u0631\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06cc\u0686 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0648 \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062f\u0627\u0648\u0627\u06cc \u0634\u06d5\u0695\u0628\u06d5\u0633\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0642\u0628\u0648\u06b5\u06cc \u06a9\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u062a \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u067e\u06ce\u0631\u06d5\u061b \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u06af\u0648\u06ce\u062f\u06ce\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u06af\u06d5\u0631 \u0648\u06cc\u0633\u062a\u06cc\u0634\u06cc\u0627\u0646 \u0641\u0631\u06cc\u0648\u062a \u062f\u06d5\u0646\u060c \u0628\u06d5\u0633\u062a\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u062a \u062f\u06d5\u06af\u06d5\u06b5\u06d5. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634 \u0647\u0627\u062a\u06d5 \u0647\u0627\u0648\u0627\u0631\u06cc \u062e\u06c6\u062a \u0648 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0648 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062f\u0627\u0646.',
    '\u0628\u06d5 \u062f\u06b5 \u0648\u0627\u0646\u06cc \u06a9\u0631\u062f\u06d5 \u0647\u06c6\u06af\u0631\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc. \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0647\u06d5\u0645\u0648\u0648\u062a \u0628\u06d5\u062e\u062a \u0628\u06a9\u0631\u062f\u0627\u06cc\u06d5\u060c \u0646\u06d5\u062a \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u0648\u0627 \u0644\u06ce\u06a9\u06cc\u0627\u0646 \u06a9\u06c6 \u0628\u06a9\u06d5\u06cc\u062a\u06d5\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0648\u0627 \u0644\u06ce\u06a9\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646 \u0628\u06d5 \u0647\u06c6\u06af\u0631\u061b \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0628\u06c6 \u062a\u06c6 \u0648 \u0628\u06c6 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06ce \u0634\u0648\u06ce\u0646\u062a \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u060c \u062e\u0648\u062f\u0627 \u0628\u06d5\u0633\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0647\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0628\u062f\u06d5 \u0628\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u06cc\u0633\u062a \u06a9\u06d5\u0633 \u0628\u0646 \u0648 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0646\u060c \u0647\u06ce\u0632\u062a\u0627\u0646 \u0628\u06d5 \u062f\u0648\u0648 \u0633\u06d5\u062f \u06a9\u06d5\u0633 \u062f\u06d5\u0634\u06a9\u06ce\u061b \u06af\u06d5\u0631 \u0633\u06d5\u062f \u06a9\u06d5\u0633\u062a\u0627\u0646 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0646\u060c - \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u0627 - \u062f\u0648\u0648 \u0647\u06d5\u0632\u0627\u0631 \u06a9\u06d5\u0633 \u0644\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0695\u0627\u062f\u06d5\u0646\u06ce\u0646\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u06cc \u0628\u06ce \u0626\u0627\u0648\u06d5\u0632\u0646.',
    '\u0626\u06ce\u0633\u062a\u0627 \u062e\u0648\u062f\u0627 \u06a9\u06d5 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0648\u0631\u06d5 \u0648 \u062a\u0648\u0627\u0646\u0627\u062a\u0627\u0646 \u06a9\u06d5\u0645 \u0628\u0648\u0648\u06d5\u060c \u06a9\u0627\u0631\u06cc \u0628\u06c6 \u0647\u0627\u0633\u0627\u0646\u062a\u0631 \u06a9\u0631\u062f\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0633\u06d5\u062f \u06a9\u06d5\u0633 \u0628\u0646 \u0648 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0646 - \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u0627 - \u062f\u06d5\u0631\u06c6\u0633\u062a\u06cc \u062f\u0648\u0648 \u0647\u06d5\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u06ce\u0646. \u062e\u0648\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06c6\u0695\u0627\u06af\u0631\u0627\u0646\u06d5.',
    '\u0628\u06c6 \u0647\u06cc\u0686 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u0646\u06d5\u0628\u0648\u0648\u06d5 \u062f\u06cc\u0644\u06cc \u0647\u06d5\u0628\u0646\u061b \u0645\u06d5\u06af\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06ce \u0644\u06d5 \u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646 \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u0628\u0648\u0648\u0628\u06ce\u062a\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a. \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce \u0645\u0627\u06b5\u06cc \u062f\u0646\u06cc\u0627\u062a\u0627\u0646 \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u06ce\u060c \u062e\u0648\u062f\u0627\u0634 \u067e\u0627\u0634\u06d5 \u0695\u06c6\u0698\u06cc \u062f\u06d5\u0648\u06ce. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0626\u06d5\u0645 \u0695\u0648\u0648\u062f\u0627\u0648\u06d5\u06cc \u067e\u06ce\u0634\u0648\u0648\u062a\u0631 \u0646\u06d5\u0646\u0648\u0648\u0633\u06cc\u0628\u0627\u06cc\u06d5\u060c \u0644\u06d5\u0645\u06d5\u06cc \u06a9\u06d5 \u0648\u06d5\u062f\u06d5\u0633\u062a\u0648\u0648 \u06a9\u06d5\u0648\u062a \u0626\u0627\u0632\u0627\u0631\u06cc \u0632\u06c6\u0631\u0648\u0648 \u062a\u0648\u0648\u0634 \u062f\u06d5\u0647\u0627\u062a.',
    '\u0626\u06d5\u0648 \u062a\u0627\u06b5\u0627\u0646\u06d5\u06cc \u06af\u0631\u062a\u0648\u0648\u062a\u0627\u0646\u06d5\u060c \u062d\u06d5\u06b5\u0627\u06b5 \u0648 \u0632\u06d5\u06b5\u0627\u06b5\u06d5 \u0628\u06cc\u062e\u06c6\u0646\u061b \u062a\u0631\u0633\u06cc\u0634\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u062a\u06c6 \u0628\u06d5\u0648 \u06cc\u06d5\u062e\u0633\u06cc\u0631\u0627\u0646\u06d5 \u0628\u06ce\u0698\u06d5 \u06a9\u06d5 \u062f\u06d5\u0628\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06cc \u0626\u06ce\u0648\u06d5\u062f\u0627\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0626\u0627\u06af\u0627\u06cc \u0644\u06ce \u0628\u06ce \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5\u060c \u0686\u0627\u06a9\u062a\u0631 \u0644\u06d5\u0648\u06d5\u06cc \u0644\u06ce\u0648 \u0633\u06d5\u0646\u0631\u0627\u0648\u06d5\u060c \u062f\u06d5\u0648\u062f\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0644\u06ce\u0648 \u062e\u06c6\u0634 \u062f\u06d5\u0628\u06ce. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0648\u0646\u06cc\u0627\u0632\u06d5\u0634 \u0628\u0646 \u06a9\u06d5 \u062f\u0631\u06c6 \u062f\u06d5\u06af\u06d5\u06b5 \u062a\u06c6 \u0628\u06a9\u06d5\u0646\u060c \u0632\u0648\u0648 \u0648\u06d5\u0644 \u062e\u0648\u062f\u0627\u0634 \u0646\u0627\u0695\u0627\u0633\u062a \u0628\u0648\u0648\u0646 \u0648 \u0626\u06d5\u062a\u06c6\u06cc \u0632\u0627\u06b5 \u06a9\u0631\u062f \u0628\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646\u0627. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0632\u06ce\u062f\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062c\u06ce \u0647\u06ce\u0634\u062a\u0648\u0648\u06d5 \u0648 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5 \u06af\u06cc\u0627\u0646 \u0648 \u0645\u0627\u06b5 \u062e\u06d5\u0632\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06cc\u0627\u0646 \u062d\u0627\u0648\u0627\u0646\u062f\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u0631\u06cc\u06a9\u0627\u0631 \u0628\u0648\u0648\u0646\u060c \u0628\u0648\u0648\u0646\u06d5 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06cc \u06cc\u06d5\u06a9\u062a\u0631. \u0626\u06d5\u0648 \u06af\u0631\u06c6\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0645\u0627\u0648\u0646\u06d5\u062a\u06d5\u0648\u06d5 \u0648 \u0646\u06d5\u0647\u0627\u062a\u0646\u06d5 \u062f\u06d5\u0631\u060c \u0647\u06d5\u062a\u0627 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u062f\u06d5\u0631\u0646\u06d5\u0686\u0646\u060c \u0646\u0627\u0628\u06ce \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646. \u0628\u06d5\u06b5\u0627\u0645 \u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u06cc\u0627\u0631\u06cc\u062f\u06d5 \u0644\u06d5\u0646\u06af\u06c6 \u0628\u062e\u0648\u0627\u0632\u0646\u060c \u0644\u06d5 \u0633\u06d5\u0631\u062a\u0627\u0646\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u06a9\u06c6\u0645\u06d5\u06af\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5 \u062f\u0698\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5 \u0647\u0627\u0648\u067e\u06d5\u06cc\u0645\u0627\u0646\u0646. \u0647\u06d5\u0631\u0686\u06cc \u0628\u06cc\u06a9\u06d5\u0646 \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u0628\u06d5\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc\u0634 \u062f\u06c6\u0633\u062a\u06cc \u0646\u0632\u06cc\u06a9\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0646. \u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 - \u0648\u06d5\u06a9 \u0645\u0646 \u067e\u06ce\u0645 \u06af\u0648\u062a\u0648\u0648\u0646 - \u0695\u06d5\u0641\u062a\u0627\u0631 \u0646\u06d5\u06a9\u06d5\u0646\u060c \u0628\u0634\u06ce\u0648\u06ce\u0646\u06cc \u0648 \u062e\u0631\u0627\u067e\u06d5\u06cc \u0632\u06c6\u0631 \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0695\u0648\u0648 \u062f\u06d5\u062f\u0627.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0644\u06d5 \u0648\u06b5\u0627\u062a \u0626\u0627\u0648\u0627\u0631\u06d5 \u0628\u0648\u0648\u0646 \u0648 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u062e\u06d5\u0632\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u067e\u06d5\u0646\u0627\u06cc\u0627\u0646 \u062f\u0627\u0646 \u0648 \u06a9\u06c6\u0645\u06d5\u06af\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u0695\u0627\u0633\u062a\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u062e\u06c6\u0634 \u0628\u0648\u0648\u06d5 \u0648 \u0628\u0698\u06cc\u0648\u06cc \u0628\u0627\u0634 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u067e\u0627\u0634\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u0628\u06d5 \u0626\u0627\u0648\u0627\u0631\u06d5\u06cc\u06cc \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646 \u0648 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u062e\u06d5\u0632\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u0646. \u0628\u06d5 \u06af\u0648\u06ce\u0631\u06d5\u06cc \u06a9\u062a\u06ce\u0628\u06cc \u062e\u0648\u062f\u0627\u060c \u062e\u0632\u0645 \u0628\u06c6 \u062e\u0632\u0645 \u0644\u06d5 \u0646\u0627\u0645\u06c6 \u0646\u0632\u06cc\u06a9\u062a\u0631\u06d5. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u062f\u06d5\u0631\u0628\u0695\u06cc\u0646\u06cc \u0628\u06ce\u0632\u0627\u0631\u06cc\u06cc\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u06d5\u0648\u06d5\u060c \u0644\u06d5\u0648 \u0634\u06d5\u0631\u06cc\u06a9 \u062f\u0627\u0646\u06d5\u0631\u0627\u0646\u06d5 \u0648\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u0647\u0627\u0648\u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0627\u0646.',
    '\u0647\u06d5\u062a\u0627 \u0686\u0648\u0627\u0631 \u0645\u0627\u0646\u06af \u0695\u06ce\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u0628\u06af\u06d5\u0695\u06ce\u0646\u061b \u062f\u06d5\u0628\u06ce \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0634 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u062e\u06c6\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0695\u0632\u06af\u0627\u0631 \u06a9\u06d5\u0646. \u062e\u0648\u0627 \u0647\u06d5\u0645\u0648\u0648 \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u06cc \u062f\u06cc\u0646 \u0695\u06cc\u0633\u0648\u0627 \u062f\u06d5\u06a9\u0627.',
    '\u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u062d\u06d5\u062c\u06cc \u0645\u06d5\u0632\u0646\u062f\u0627\u060c \u0628\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u06d5\u0646\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u0627 \u0644\u06d5 \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u0627\u0646 \u0628\u06ce\u0632\u0627\u0631\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0628\u0628\u0646\u06d5\u0648\u06d5\u060c \u0642\u0627\u0632\u0627\u0646\u062c\u062a\u0627\u0646\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u0642\u0628\u0648\u0648\u06b5\u06cc \u0646\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06ce\u062a\u0627\u0646 \u0695\u0648\u0648\u0646 \u0628\u06ce \u06a9\u06d5 \u0644\u06d5 \u062f\u06d5\u0633 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0646\u0627\u0686\u0646. \u0645\u0698\u062f\u06d5\u0634 \u0628\u062f\u06d5 \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06d5.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u0627\u0646\u06ce \u06a9\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646\u0648\u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06d5\u0633\u062a\u0648\u0648\u0646 \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0644\u0627\u0646\u06d5\u06a9\u06d5\u0648\u062a\u0648\u0648\u0646 \u0648 \u062f\u0698\u06cc \u0626\u06ce\u0648\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0627\u0648\u06d5\u060c \u0647\u06d5\u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u06a9\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06d5\u0686\u06ce\u060c \u0626\u06ce\u0648\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646\u062a\u0627\u0646 \u0645\u06d5\u0634\u06a9\u06ce\u0646\u0646. \u062e\u0648\u0627 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u06cc \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u0646.',
    '\u0647\u06d5\u0631\u06a9\u06d5 \u0645\u0627\u0646\u06af\u06d5\u062d\u06d5\u0631\u0627\u0645\u06d5\u06a9\u0627\u0646 \u0628\u0695\u0627\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648 \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u0627\u0646\u06d5\u0648 \u0644\u06d5 \u06a9\u0648\u06ce \u0648\u06d5\u062f\u06d5\u0633 \u06a9\u06d5\u0648\u06ce\u060c \u0644\u06d5 \u06af\u0631\u062a\u0646 \u0648 \u0628\u06d5\u0646\u062f \u06a9\u0631\u062f\u0646 \u0648 \u06a9\u0648\u0634\u062a\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0633 \u0645\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646 \u0648 \u0628\u06c6\u0633\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u0646\u06ce\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u0648\u0648\u0646\u06d5 \u0646\u0648\u06ce\u0698\u06a9\u06d5\u0631 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u0631\u060c \u0626\u06d5\u0648\u0633\u0627 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06b5\u06af\u0631\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062e\u06c6\u0634 \u062f\u06d5\u0628\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u06cc\u06d5\u06a9\u06ce\u06a9 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u0646 \u062e\u06c6\u06cc \u062e\u0633\u062a\u06d5 \u0628\u06d5\u0631 \u062f\u0627\u06b5\u062f\u06d5\u06cc \u062a\u06c6\u0648\u06d5\u060c \u067e\u06d5\u0646\u0627\u06cc \u0628\u062f\u06d5\u061b \u0628\u0627 \u06af\u0648\u062a\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u0628\u06cc\u0633\u06ce. \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0628\u06cc\u0646\u06ce\u0631\u06d5 \u0634\u0648\u06ce\u0646\u06ce \u06a9\u06d5 \u0646\u06d5\u062a\u0631\u0633\u06ce\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u06a9\u0648\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0626\u06d5\u0648 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062f\u06d5\u062f\u06d5\u0646 \u0628\u06d5 \u062c\u06ce\u06cc \u062f\u06ce\u0646\u0646\u061f \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06ce\u0634\u062a\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u0645\u0627\u0648\u0646\u060c \u06a9\u06d5 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0644\u06d5 \u067e\u06d5\u0646\u0627 \u00ab\u0645\u0633\u062c\u062f\u0627\u0644\u062d\u0631\u0627\u0645\u00bb \u067e\u06d5\u06cc\u0645\u0627\u0646\u0648\u0648 \u0628\u06d5\u0633\u062a\u061b \u062a\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u06a9\u06d5\u0645\u0627\u0646\u060c \u0626\u06ce\u0648\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u0634\u06a9\u06ce\u0646 \u0645\u06d5\u0628\u0646. \u062e\u0648\u0627 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u06cc \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u0646.',
    '\u0686\u06c6\u0646 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06ce\u06a9! \u0626\u06d5\u0648\u0627\u0646 \u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce \u0628\u0632\u0627\u0646\u0646 \u067e\u06ce\u0648 \u062f\u06d5\u0648\u06ce\u0631\u0646\u060c \u0628\u06d5 \u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u06af\u0648\u06ce \u0628\u062f\u06d5\u0646\u06d5 \u0633\u0648\u06ce\u0646\u062f\u062e\u06c6\u0631\u06cc \u06cc\u0627\u0646 \u062e\u0632\u0645\u0627\u06cc\u06d5\u062a\u06cc\u060c \u0628\u06d5 \u06af\u0698\u0648\u0648\u062f\u0627 \u062f\u06ce\u0646\u061f \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0645 \u062f\u06b5\u062e\u06c6\u0634\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0646\u06cc\u0627\u0632\u06cc \u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5\u061b \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634 \u0648 \u0628\u06d5\u062f\u0641\u06d5\u0695\u0646.',
    '\u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u0631\u062e\u06ce\u06a9\u06cc \u0647\u0646\u062f\u06a9 \u0641\u0631\u06c6\u062a\u061b \u0626\u06d5\u0648\u0633\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0633\u062a. \u0626\u0627\u06a9\u0627\u0631\u06cc \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0644\u06d5\u0645\u06d5\u0695 \u0647\u06cc\u0686 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06ce\u06a9\u060c \u0647\u0627\u0648\u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0648 \u062e\u0632\u0645\u0627\u06cc\u06d5\u062a\u06cc \u0695\u06d5\u0686\u0627\u0648 \u0646\u0627\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u0633\u062a\u062f\u0631\u06ce\u0698\u06cc \u067e\u06cc\u0634\u06d5\u06cc\u0627\u0646\u06d5.',
    '\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u06cc \u0648\u0627\u0632\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u0646\u0648\u06ce\u0698\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0648 \u0632\u06d5\u06a9\u0627\u062a\u06cc\u0627\u0646 \u062f\u0627\u060c \u0626\u06cc\u062a\u0631 \u0628\u0631\u0627\u06cc \u062f\u06cc\u0646\u06cc \u0626\u06ce\u0648\u06d5\u0646. \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0645 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u062e\u06d5\u06b5\u06a9\u06ce \u0695\u0648\u0648\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u0632\u0627\u0646\u0627 \u0628\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u062f\u0648\u0627\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646 \u0628\u06d5\u0633\u062a\u0646 \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u0648\u06d5\u0634\u0627\u0646\u062f \u0648 \u062a\u06d5\u0648\u0633\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06cc\u0646\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u06af\u0631\u062a\u060c \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u06cc\u06d5\u0644 \u06af\u06d5\u0648\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u06d5 \u0634\u06d5\u0695 \u0648\u06d5\u0631\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u0646 \u06a9\u06d5 \u0628\u06ce \u067e\u06d5\u06cc\u0645\u0627\u0646\u0646\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u062f\u06d5\u0633\u062a\u0648\u0648 \u0644\u06ce \u0647\u06d5\u06b5\u06af\u0631\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u06af\u0698 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 \u0646\u0627\u0686\u0646 \u06a9\u06d5 \u0644\u06d5 \u0633\u0648\u06ce\u0646\u062f \u067e\u0627\u0634\u06af\u06d5\u0632 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0646\u06cc\u0627\u0632\u06cc\u0627\u0646 \u0648\u0627\u0628\u0648\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6 \u062f\u06d5\u0631\u06a9\u06d5\u0646\u061b \u062f\u06d5\u0633 \u067e\u06ce\u0634\u06a9\u06d5\u0631\u06cc\u0634 [\u0644\u06d5 \u0634\u06d5\u0695\u06ce\u062f\u0627] \u0626\u06d5\u0648\u0627\u0646 \u06a9\u0631\u062f\u06cc\u0627\u0646\u061f \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u0648\u0648 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061f \u0628\u06c6 \u0644\u06ce \u062a\u0631\u0633\u0627\u0646\u060c \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648\u0627\u0646 \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5.',
    '\u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06af\u0698\u06cc\u0627\u0646 \u0695\u0627\u0628\u0686\u0646\u061b \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u06a9\u0627 \u0648 \u0698\u06ce\u0631\u06cc\u0627\u0646 \u062f\u06d5\u062e\u0627\u0648 \u0633\u06d5\u0631\u0648\u0648 \u062f\u06d5\u062e\u0627 \u0628\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646\u062f\u0627 \u0648 \u06a9\u0627\u0631\u06ce\u06a9 \u062f\u06d5\u06a9\u0627 \u06a9\u0648\u06b5\u06cc \u062f\u06b5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u062f\u0627\u0628\u0645\u0631\u06a9\u06ce.',
    '\u0695\u06a9\u06cc \u062f\u06b5\u06cc\u0627\u0646 \u062f\u0627\u062f\u06d5\u0645\u0627\u06b5\u06ce \u0648 \u062e\u0648\u062f\u0627 \u062d\u06d5\u0632 \u06a9\u0627\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u062f\u06d5\u0633 \u0647\u06d5\u06b5\u06af\u0631\u06ce\u060c \u0644\u06ce\u06cc \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u06ce. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u067e\u06ce\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u06a9\u06d5\u0633 \u062e\u06c6\u062a\u0627\u0646 \u062a\u06ce \u0646\u0627\u06af\u06d5\u06cc\u06d5\u0646\u06ce \u0648 \u062e\u0648\u0627\u0634 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0686\u0648\u0648\u0646\u06d5 \u062e\u06d5\u0632\u0627\u06cc\u06d5 \u0648 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce \u0695\u0627\u0632\u062f\u0627\u0631\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u06d5\u060c \u0628\u06ce \u0626\u0627\u06af\u0627\u06cc\u06d5\u061f \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0646\u0648\u06ce\u0698\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u0627 \u06a9\u0631\u062f\u0646 \u0628\u06c6 \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u0627\u0646 \u0646\u06d5\u0634\u06cc\u0627\u0648\u06d5. \u062e\u06c6\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u062e\u0648\u0627 \u0646\u0627\u0646\u0627\u0633\u06cc\u0646\u061b \u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0628\u06d5 \u0641\u06cc\u0695\u06c6 \u062f\u06d5\u0686\u06ce \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0644\u06d5 \u062f\u06c6\u0698\u06d5\u06d5 \u062f\u06d5\u0628\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0646\u0648\u06ce\u0698\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u0627 \u0628\u06a9\u06d5\u0646\u060c \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u0648\u06ce\u0698 \u062f\u0631\u0648\u0648\u0633\u062a \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u0631\u0646 \u0648 - \u0644\u06d5 \u062e\u0648\u0627 \u0646\u06d5\u0628\u06ce - \u062a\u0631\u0633\u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce \u0646\u06cc\u06cc\u06d5. \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0695\u06ce \u0632\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0628\u0646.',
    '\u0626\u0627\u0648 \u0644\u06d5 \u062d\u06d5\u062c\u0627\u062c \u0628\u06d5 \u0634\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0633\u06d5\u0631\u067e\u06d5\u0631\u0634\u062a\u06cc \u0645\u0627\u06b5\u06cc \u06a9\u0627\u0628\u06d5\u060c \u067e\u06ce\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0686\u0648\u0648\u0646\u06d5 \u062e\u06d5\u0632\u0627\u06cc\u06d5 \u0648 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u060c \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u06d5\u061f \u0644\u062c\u06d5\u0645 \u062e\u0648\u062f\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9 \u0646\u06cc\u0646. \u062e\u0648\u0627 \u06af\u0631\u06c6\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u0646\u0627\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06af\u06d5.',
    '\u0626\u06d5\u0648\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0648\u0627\u0631\u06d5\u06cc\u06cc \u0628\u0648\u0648\u0646 \u0648 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0686\u0648\u0648\u0646\u06d5 \u062e\u06d5\u0632\u0627\u06cc\u06d5 \u0648 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u0645\u0627\u06b5 \u0648 \u06af\u06cc\u0627\u0646\u06cc \u062e\u06c6 \u0647\u06d5\u06b5\u06af\u0631\u062a\u0648\u0648\u06d5\u060c \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u0644\u06d5 \u067e\u06ce\u0634\u062a\u0631\u0646 \u0648 \u0628\u06d5 \u0626\u0627\u0648\u0627\u062a\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0645\u0698\u062f\u06d5\u06cc \u0628\u06d5\u0632\u06d5 \u0648 \u0695\u06d5\u0632\u0627\u06cc [\u062e\u06c6\u06cc \u0648] \u0628\u0627\u063a\u0627\u062a\u06cc \u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u062f\u0627 \u06a9\u06d5 \u062f\u06d5\u0648\u06ce\u062f\u0627 \u062e\u06c6\u0634\u06cc \u067e\u0627\u06cc\u06d5\u062f\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646. \u062e\u0648\u062f\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u0632\u06c6\u0631 \u06af\u06d5\u0648\u0631\u06d5\u06cc \u0644\u06d5 \u0644\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u0631\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0628 \u0648 \u0628\u0631\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u0644\u0627\u06cc\u0627\u0646 \u0648\u0627\u0628\u0648\u0648 \u0646\u0627\u0628\u0627\u0648\u06d5\u0695\u06cc \u0644\u06d5 \u0628\u0627\u0648\u06d5\u0695\u0647\u06ce\u0646\u0627\u0646 \u0628\u0627\u0634\u062a\u0631\u06d5\u060c \u0626\u06ce\u0648\u06d5 \u062a\u062e\u0648\u0646\u06cc\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u062e\u06c6\u0634\u062a\u0627\u0646 \u0646\u06d5\u0648\u06ce\u0646\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0644\u06d5\u0646\u06af\u06c6 \u062e\u06c6\u0634\u06cc \u0628\u0648\u06ce\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0628 \u0648 \u06a9\u0648\u0695 \u0648 \u0628\u0631\u0627 \u0648 \u0698\u0646 \u0648 \u062e\u0632\u0645\u06d5\u06a9\u0627\u0646\u0648\u0648 \u0626\u06d5\u0648 \u0645\u0627\u06b5\u06d5\u06cc \u067e\u06d5\u06cc\u062f\u0627\u0648 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u06a9\u06d5\u0644 \u0648 \u067e\u06d5\u0644\u06cc \u0628\u0627\u0632\u0631\u06af\u0627\u0646\u06cc - \u06a9\u06d5 \u062f\u06d5\u062a\u0631\u0633\u0646 \u0644\u06d5 \u0628\u0631\u06d5\u0648 \u06a9\u06d5\u0648\u06ce - \u0648 \u062e\u0627\u0646\u0648\u0648\u0628\u06d5\u0631\u06d5\u06cc \u0695\u0646\u062f \u0648 \u062f\u06b5\u062e\u06c6\u0634\u06a9\u06d5\u0631\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646\u060c \u067e\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u062e\u06d5\u0632\u0627 \u06a9\u0631\u062f\u0646 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u0627\u062f\u0627 \u06af\u0631\u06cc\u0646\u06af\u062a\u0631\u06d5\u061b \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0628\u0646 \u062a\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u062f\u06ce. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u0646\u060c \u062e\u0648\u062f\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u0627\u0646 \u0646\u0627\u06a9\u0627.',
    '\u062e\u0648\u062f\u0627 \u0644\u06d5\u06af\u06d5\u0644\u06ce\u06a9 \u0634\u0648\u06ce\u0646\u0627\u0646\u062f\u0627 \u062f\u06d5\u0647\u0627\u0646\u0627\u0648 \u0647\u0627\u062a\u061b \u0695\u06c6\u0698\u06cc \u062d\u0648\u0646\u06d5\u06cc\u0646\u06cc\u0634 \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627\u061b \u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f\u06d5 \u0628\u06d5 \u0632\u06c6\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0646\u0627\u0632\u06cc\u0646\u060c \u06a9\u06d5 \u0686\u06cc \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0628\u06c6 \u0646\u06d5\u062f\u0627\u0646\u061b \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u06af\u0648\u0634\u0627\u062f\u06d5\u062a\u0627\u0646 \u0644\u06ce \u062a\u06d5\u0646\u06af \u0647\u06d5\u06b5\u0627\u062a. \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u067e\u0627\u0634\u06d5\u06a9\u0634\u06d5\u062a\u0627\u0646 \u06a9\u0631\u062f \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u062f\u0648\u0648\u0627 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5.',
    '\u067e\u0627\u0634\u0627\u0646 \u062e\u0648\u062f\u0627 \u062f\u06b5\u0646\u06cc\u0627\u06cc\u06cc \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0648 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0627\u0646 \u0628\u06d5 \u0695\u06ce \u06a9\u0631\u062f \u0648 \u0632\u06c6\u0631 \u067e\u06ce\u0634\u0645\u06d5\u0631\u06af\u06d5\u0634\u06cc \u062f\u0627\u0628\u06d5\u0632\u0627\u0646\u062f \u06a9\u06d5 \u0646\u06d5\u0648\u062f\u06d5\u062f\u06cc\u0646. \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc \u0633\u0632\u0627 \u062f\u0627 \u0648 \u0633\u0632\u0627\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5\u0634 \u0647\u06d5\u0631\u06a9\u06ce \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u0642\u0628\u0648\u0648\u06b5 \u062f\u06d5\u06a9\u0627 \u0648 \u062e\u0648\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0646\u0627\u0633\u0646 \u0646\u0627\u067e\u0627\u06a9\u0646\u060c \u0646\u0627\u0628\u06ce - \u067e\u0627\u0634 \u0626\u06d5\u0645\u0633\u0627\u06b5 - \u0644\u06d5 \u00ab\u0645\u0633\u062c\u062f\u0627\u0644\u062d\u0631\u0627\u0645\u00bb \u062e\u06c6 \u0646\u0632\u06cc\u06a9 \u06a9\u06d5\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0626\u0627\u062a\u0627\u062c\u06cc\u0634 \u062a\u0631\u0633\u0627\u0646\u060c \u062e\u0648\u0627 \u0628\u062e\u0648\u0627\u0632\u06ce \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0628\u06ce \u0646\u06cc\u0627\u0632 \u062f\u06d5\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0648\u06d5\u0644 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 \u0628\u062c\u06d5\u0646\u06af\u06d5\u0646 \u06a9\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u0627 \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627\u06cc\u0627\u0646 \u0632\u0627\u0646\u06cc\u0648\u06d5\u060c \u0626\u06d5\u0645\u0627\u0646 \u0628\u06d5 \u0695\u06d5\u0648\u0627\u06cc \u062f\u06d5\u0632\u0627\u0646\u0646 \u0648 \u0646\u0627\u06cc\u06d5\u0646\u06d5 \u0633\u06d5\u0631 \u062f\u06cc\u0646\u06cc \u0695\u0627\u0633\u062a\u06cc\u0634\u060c \u062a\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u0628\u06d5 \u0632\u06d5\u0644\u06cc\u0644\u06cc \u0648 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6\u060c \u0633\u06d5\u0631\u0627\u0646\u06d5\u06cc \u062e\u06c6 \u0628\u06d5\u0646\u06af\u06c6 \u062f\u06d5\u062f\u06d5\u0646.',
    '\u062c\u0648\u0648\u0644\u06d5\u06a9\u06d5\u06a9\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0639\u0648\u0632\u06d5\u06cc\u0631 \u06a9\u0648\u0695\u06cc \u062e\u0648\u0627\u06cc\u06d5. \u0641\u06d5\u0644\u06d5\u0634 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0645\u06d5\u0633\u06cc\u062d \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5. \u0626\u06d5\u0648 \u0642\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0645\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0626\u06d5\u06cc\u062f\u0631\u06a9\u06ce\u0646\u06ce\u060c \u0648\u06ce\u0646\u06d5\u06cc \u0642\u0633\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u0646. \u062e\u0648\u0627 \u0628\u06cc\u0627\u0646 \u06a9\u0648\u0698\u06ce\u060c \u0686\u06c6\u0646\u06cc\u0646 \u0644\u06d5 \u062d\u06d5\u0642\u06ce \u0644\u0627\u062f\u06d5\u062f\u0631\u06ce\u0646\u061f',
    '\u0644\u06d5 \u0628\u0631\u06cc \u062e\u0648\u062f\u0627\u060c \u0645\u06d5\u0644\u0627\u06a9\u0627\u0646 \u0648 \u0695\u06d5\u0628\u06d5\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0645\u06d5\u0633\u06cc\u062d\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u062e\u0648\u0627\u061b \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627 \u0628\u0648\u0648 \u0628\u06ce\u0698\u06af\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u06cc \u062a\u0627\u06a9 \u0648\u062a\u06d5\u0646\u06cc\u0627 \u0646\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646. \u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce. \u062e\u0648\u062f\u0627 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06d5 \u0648 \u0628\u06d5 \u062f\u0648\u0648\u0631\u06d5 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0647\u0627\u0648\u062a\u0627\u06cc \u0626\u06d5\u0648\u06cc \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0628\u06d5 \u0641\u0648\u0648\u06cc \u062f\u06d5\u0645\u06cc\u0627\u0646\u060c \u062a\u06cc\u0634\u06a9\u06cc \u0695\u06c6\u0634\u0646\u06cc\u0627\u06cc\u06cc \u062e\u0648\u062f\u0627 \u0648\u06d5\u06a9\u0648\u0698\u06ce\u0646\u0646\u061b \u062e\u0648\u062f\u0627\u0634 \u062e\u06c6\u06cc \u0628\u0695\u06cc\u0627\u0631\u06cc \u062f\u0627\u0648\u06d5 \u06a9\u06d5 \u0626\u06d5\u0645 \u062a\u06cc\u0634\u06a9\u06d5\u06cc \u0647\u06d5\u0631 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u06ce\u060c \u062c\u0627 \u0628\u0627 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u062d\u06d5\u0632 \u0646\u06d5\u06a9\u06d5\u0646.',
    '\u062e\u0648\u062f\u0627 \u0628\u06c6 \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646\u062f\u0627\u0646 \u0648 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u06cc \u062f\u06cc\u0646\u06cc \u0695\u0627\u0633\u062a\u060c \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u06c6\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u061b \u062a\u0627 \u06af\u0634 \u062f\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062f\u06cc\u06a9\u06d5 \u0628\u06d5\u0631\u062e\u06c6\u06cc \u0628\u062f\u0627\u061b \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u062d\u06d5\u0632 \u0646\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u0627\u062f\u06d5\u0646\u06ce\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0645\u06d5\u0644\u0627\u0686\u0627\u06a9 \u0648 \u0695\u06d5\u0628\u06d5\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u060c \u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 - \u0644\u06d5 \u0647\u06d5\u0648\u0627\u0646\u062a\u06d5 - \u0645\u0627\u06b5\u06cc \u0626\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5 \u062f\u06d5\u062e\u06c6\u0646 \u0648 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627\u0634\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0646\u0627\u062f\u06d5\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0632\u06ce\u0695 \u0648 \u0632\u06ce\u0648 \u06a9\u06c6 \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5\u062e\u062a\u06cc \u0646\u0627\u06a9\u06d5\u0646\u060c \u062a\u06c6 \u0644\u06d5\u0648 \u0626\u0627\u0632\u0627\u0631\u06d5 \u0628\u06d5\u0698\u0627\u0646\u06d5\u06cc \u062a\u0648\u0648\u0634\u06cc \u062f\u06ce\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0627\u0646 \u06a9\u06d5.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0626\u06d5\u0645 \u0632\u06ce\u0695 \u0648 \u0632\u06ce\u0648\u06d5 \u0628\u06d5 \u0626\u0627\u06af\u0631\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0633\u0648\u0648\u0631 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5\u060c \u062a\u0648\u06ce\u06b5 \u0648 \u06a9\u06d5\u0644\u06d5\u06a9\u06d5 \u0648 \u067e\u0634\u062a\u06cc\u0627\u0646\u06cc \u067e\u06ce \u062f\u0627\u063a \u062f\u06d5\u06a9\u0631\u06ce: \u062f\u06d5\u0631\u062f\u06cc \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u0628\u0686\u06ce\u0698\u0646 \u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646 \u0633\u06d5\u0631\u06cc\u06d5\u06a9\u0648\u0648\u0646\u0627\u0646.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u0648\u06d5 \u062e\u0648\u0627 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u062f\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0646\u0648\u0648\u0633\u06cc\u0648\u06cc\u06d5\u062a\u06cc \u0698\u0645\u0627\u0631\u06d5\u06cc \u0645\u0627\u0646\u06af\u06d5\u06a9\u0627\u0646 \u062f\u0648\u0627\u0632\u062f\u06d5\u0646\u061b \u0644\u06d5 \u0686\u0648\u0627\u0631\u06cc\u0627\u0646\u062f\u0627 \u0634\u06d5\u0695 \u062f\u06d5\u0648\u06d5\u0633\u062a\u06ce. \u0626\u06d5\u0645\u06d5 \u0695\u06d5\u0648\u0634\u062a\u06ce\u06a9\u06cc \u0695\u0627\u0633\u062a\u06d5\u060c \u0644\u06d5\u0645\u0627\u0646\u06d5\u062f\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0645 \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u0627\u0646\u06d5 \u0628\u06d5 \u062a\u06ce\u06a9\u0695\u0627\u06cc\u06cc \u0628\u06d5 \u06af\u0698\u0648\u0648\u062f\u0627 \u062f\u06ce\u0646\u060c \u0626\u06ce\u0648\u06d5\u0634 \u0628\u06d5 \u06af\u06d5\u0644\u06d5\u06a9\u06c6\u0645\u06d5\u06af\u06cc \u0628\u06d5 \u06af\u0698 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u0628\u0686\u0646\u06d5\u0648\u06d5\u061b \u0628\u06cc\u0634\u06cc \u0632\u0627\u0646\u0646 \u062e\u0648\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646\u06d5.',
    '\u0695\u0627\u06af\u0648\u06ce\u0632\u0627\u0646 \u0648 \u0626\u0627\u06b5\u0648\u06af\u06c6\u0695\u06cc \u0626\u06d5\u0648 \u0645\u0627\u0646\u06af\u0627\u0646\u06d5\u060c \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0647\u06c6\u06cc \u0632\u06ce\u062f\u06d5 \u0628\u0648\u0648\u0646\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce \u06af\u0648\u0645\u0695\u0627 \u062f\u06d5\u0628\u0646. \u0644\u06d5 \u0633\u0627\u06b5\u06ce\u06a9\u0627 \u0634\u06d5\u0695 \u0628\u06d5 \u0695\u06d5\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u0646 \u0648 \u0644\u06d5 \u0633\u0627\u06b5\u06ce\u06a9\u0627 \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627\u061b \u062a\u0627 \u0698\u0645\u0627\u0631\u06d5\u06cc \u0645\u0627\u0646\u06af\u06d5 \u0695\u06d5\u0648\u0627 \u0648 \u0646\u0627\u0695\u06d5\u0648\u0627\u06a9\u0627\u0646 \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u0627\u060c \u0626\u06d5\u0648\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0695\u06d5\u0648\u0627\u06cc \u0646\u06d5\u062f\u06cc\u0648\u06d5\u060c \u0695\u06d5\u0648\u0627\u06cc \u06a9\u06d5\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0626\u06d5\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5\u06cc \u0628\u06c6 \u062c\u0648\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u0646. \u062e\u0648\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0698\u06cc \u062f\u06cc\u0646\u0646\u060c \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0646\u0627\u06a9\u0627.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0686\u06cc\u062a\u0627\u0646\u06d5 \u06a9\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0648\u06d5\u062f\u06d5\u0631\u06a9\u06d5\u0648\u0646 \u0628\u06c6 \u062e\u06d5\u0632\u0627 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627\u062f\u0627\u060c \u062a\u06d5\u0645\u06d5\u06b5\u0646 \u0648 \u0644\u06d5 \u062c\u06ce\u06cc \u062e\u06c6 \u0646\u0627\u0628\u0632\u0648\u0648\u0646\u061f \u0626\u0627\u062e\u06c6 \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627\u062a\u0627\u0646 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u067e\u06ce \u062e\u06c6\u0634\u062a\u0631\u06d5\u061f! \u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06cc \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06cc \u0626\u06d5\u0648 \u062f\u0646\u06cc\u0627\u062a\u0627\u0646 \u0634\u062a\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u06a9\u06d5\u0645 \u0628\u0627\u06cc\u06d5\u062e\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u062a\u0648\u0648\u0634\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646 \u062f\u06ce\u0646 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u06cc \u062a\u0627\u0632\u06d5 \u0628\u0627\u0628\u06d5\u062a \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0634\u0648\u06ce\u0646\u06d5\u06a9\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0648 \u0644\u06d5\u0645 \u0628\u0627\u0631\u06d5\u0648\u06d5 \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646\u06ce\u06a9\u06cc \u067e\u06ce \u0646\u0627\u06af\u06d5\u06cc\u06d5\u0646\u0646. \u062e\u0648\u0627 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u0646\u0627\u062f\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc \u0647\u0627\u062a: \u0648\u06d5\u062e\u062a\u06ce \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0626\u06d5\u0648\u06cc\u0627\u0646 \u062f\u06d5\u0631\u06a9\u0631\u062f\u060c \u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0626\u06d5\u0634\u06a9\u06d5\u0648\u062a\u06d5\u06a9\u06d5\u062f\u0627 \u0628\u0648\u0648\u0646 \u0648 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce\u06a9 \u0628\u0648\u0648 \u0644\u06d5 \u062f\u0648\u0648 \u06a9\u06d5\u0633\u0627\u0646\u060c \u0626\u06d5\u0648\u06ce \u06a9\u0627\u062a\u06cc\u0634 \u0628\u06d5 \u0647\u06d5\u0648\u0627\u06b5\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u06af\u0648\u062a: \u062e\u06d5\u0645\u062a \u0646\u06d5\u0628\u06ce! \u0626\u06ce\u0645\u06d5 \u062e\u0648\u062f\u0627\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5\u06d5\u061b \u0626\u06c6\u0642\u0631\u06d5\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0628\u06c6 \u0647\u0627\u062a \u0648 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u067e\u06ce\u0634\u0645\u06d5\u0631\u06af\u06d5\u0634\u06cc \u0628\u06c6 \u0646\u0627\u0631\u062f \u06cc\u0627\u0631\u06cc \u0628\u06a9\u06d5\u0646\u061b \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u062a\u0627\u0646 \u062f\u06d5\u062f\u06cc\u062a\u0646. \u0642\u0633\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0648\u06d5\u0628\u0646 \u06a9\u06d5\u0648\u062a \u0648 \u0647\u06d5\u0631 \u0642\u0633\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0633\u06d5\u0631\u06d5. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0633\u0648\u0648\u06a9\u0628\u0627\u0631\u0628\u0646 \u06cc\u0627\u0646 \u06af\u0631\u0627\u0646\u0628\u0627\u0631 \u0628\u0646\u060c \u0628\u06c6 \u062e\u06d5\u0632\u0627\u06cc\u06d5 \u0695\u06ce \u062f\u0627\u0628\u06af\u0631\u0646 \u0648 \u0644\u06d5 \u06af\u06cc\u0627\u0646 \u0648 \u0645\u0627\u06b5 \u0628\u06d5\u062e\u062a \u06a9\u0631\u062f\u0646 \u062f\u0631\u06ce\u063a \u0645\u06d5\u06a9\u06d5\u0646. \u06af\u06d5\u0631 \u0628\u0632\u0627\u0646\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u062e\u06c6\u062a\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06ce \u0628\u06ce \u0695\u06d5\u0646\u062c \u0648 \u0633\u06d5\u0641\u06d5\u0631\u06cc \u0633\u0627\u0646\u0627 \u0644\u06d5 \u0695\u06ce \u0628\u0627\u060c \u0647\u0627\u0633\u0627\u0646 \u0628\u06d5 \u062f\u0648\u0648\u062a\u062f\u0627 \u062f\u06d5\u0647\u0627\u062a\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0645\u06d5\u0648\u062f\u0627\u06cc \u062f\u0648\u0648\u0631 \u0648 \u0633\u06d5\u062e\u062a\u06cc\u0627\u0646\u060c \u0644\u0627 \u06af\u0631\u0627\u0646 \u0647\u0627\u062a. \u062f\u06ce\u0646 \u0633\u0648\u06ce\u0646\u062f\u06ce \u0628\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u062e\u06c6\u0646 \u06a9\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0645\u0627\u0646 \u062a\u0648\u0627\u0646\u06cc\u0628\u0627\u060c \u0626\u06ce\u0645\u06d5\u0634 \u062f\u06d5\u06af\u06d5\u06b5\u062a\u0627\u0646 \u062f\u06d5\u0647\u0627\u062a\u06cc\u0646. [\u0628\u06d5\u0648 \u0633\u0648\u06ce\u0646\u062f\u06d5] \u062e\u06c6 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u062f\u06d5\u0628\u06d5\u0646. \u062e\u0648\u0627\u0634 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u0631\u06c6 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u062e\u0648\u0627 \u0628\u062a \u0628\u06d5\u062e\u0634\u06ce\u060c \u0628\u06c6\u0686 \u0626\u06cc\u0632\u0646\u062a \u062f\u0627\u0646\u061f \u0695\u0627\u0648\u06d5\u0633\u062a\u0627 \u0628\u0627\u06cc \u0647\u06d5\u062a\u0627 \u0628\u06c6\u062a \u0695\u06c6\u0634\u0646 \u0628\u0628\u0627\u0648\u06d5\u060c \u06a9\u0627\u0645 \u0695\u0627\u0633\u062a\u0646 \u0648 \u062f\u0631\u06c6\u0632\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0628\u0646\u0627\u0633\u06cc.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0628\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u060c \u0646\u0627\u06cc\u06d5\u0646 \u0626\u06cc\u0632\u0646\u062a \u0644\u06ce \u0628\u062e\u0648\u0627\u0632\u0646 \u062a\u0627 \u0628\u06d5 \u0645\u0627\u06b5 \u0648 \u06af\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u0627 \u062e\u06d5\u0632\u0627 \u0628\u06a9\u06d5\u0646. \u062e\u0648\u0627 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646 \u062f\u06d5\u0646\u0627\u0633\u06ce.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06ce\u0646 \u06a9\u06d5 \u0626\u06cc\u0632\u0646\u062a \u0644\u06ce \u0628\u062e\u0648\u0627\u0632\u0646\u060c \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u062f\u0648\u0627\u06cc\u06cc \u0646\u06cc\u06cc\u06d5 \u0648 \u0644\u06d5\u0648 \u0646\u0627\u0648\u06d5\u062f\u0627 \u062f\u0648\u0648\u062f\u06b5 \u0645\u0627\u0648\u0646 \u0648 \u0644\u06d5 \u06af\u0648\u0645\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u0647\u0627\u062a\u0648\u0686\u06c6\u06cc\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u06cc\u0627\u0646 \u0648\u06cc\u0633\u062a\u0627\u06cc\u06d5 \u062f\u06d5\u0631\u0686\u0646\u060c \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u0627\u0645\u0627\u062f\u06d5 \u062f\u06d5\u06a9\u0631\u062f\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0647\u0627\u062a\u0646\u06cc\u0627\u0646\u06cc \u067e\u06ce \u062e\u06c6\u0634 \u0646\u06d5\u0628\u0648\u0648\u061b \u0648\u0631\u06d5\u06cc \u0644\u06d5 \u062f\u06b5\u06cc\u0627\u0646 \u0628\u0631\u062f\u06d5 \u062f\u06d5\u0631. \u067e\u06ce\u06cc\u0627\u0646 \u06af\u0648\u062a\u0646: \u0626\u06ce\u0648\u06d5\u0634 \u0648\u06ce\u0695\u0627\u06cc \u0628\u06d5 \u062c\u06ce \u0645\u0627\u0648\u0627\u0646 \u0628\u06d5 \u062c\u06ce \u0645\u06ce\u0646\u0646.',
    '\u06af\u06d5\u0631 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06ce\u0648\u06d5\u0634 \u0647\u0627\u062a\u0628\u0627\u0646 \u0628\u06c6 \u062e\u06d5\u0632\u0627\u06cc\u06d5\u060c \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062a\u06ce\u06a9 \u062f\u06d5\u062f\u0627 \u0648 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0646\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u0698\u0627\u0648\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0631\u067e\u0627 \u062f\u06d5\u06a9\u0631\u062f. \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648\u062f\u0627 \u0628\u0648\u0648\u0646 \u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u0628\u06cc\u0633\u062a\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u0628\u06d5 \u0626\u0627\u06af\u0627\u06cc\u06d5.',
    '\u0628\u06d5\u0631\u06ce\u0634 \u067e\u0634\u06ce\u0648\u06cc\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5\u0648\u06d5 \u0648 \u06a9\u0627\u0631\u06cc \u062a\u06c6\u06cc\u0627\u0646 \u0628\u06d5\u0631\u0627\u0648\u06d5\u0698\u0648\u0648 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627. \u0647\u06d5\u062a\u0627 \u06a9\u0627\u0631\u06d5\u06a9\u06d5 \u0695\u0627\u0633\u062a \u0647\u0627\u062a \u0648 \u0628\u06d5 \u06a9\u0648\u06ce\u0631\u0627\u06cc\u06cc \u0686\u0627\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u062a\u06d5 \u062c\u06ce.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u0646\u060c \u062f\u06ce\u0646 \u067e\u06ce\u062a \u0626\u06ce\u0698\u0646: \u0626\u06cc\u0632\u0646\u0645\u0627\u0646 \u0628\u062f\u06d5 \u0628\u0627 \u0646\u06d5\u06cc\u06d5\u06cc\u0646\u061b \u0646\u06d5\u0648\u06d5\u06a9 \u062a\u0648\u0648\u0634\u06cc \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u0628\u06cc\u0646. \u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0647\u06d5\u0631 \u0626\u06ce\u0633\u062a\u0627 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u06d5\u062a\u06d5 \u0646\u0627\u0648 \u06af\u0648\u0646\u0627\u0647\u06d5\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u062f\u06c6\u0698\u06d5\u06d5 \u062f\u06d5\u0648\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u062f\u0627.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u062a \u0628\u06ce\u062a\u06d5 \u0695\u06ce\u06af\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06ce\u06cc\u0627\u0646 \u0646\u0627\u062e\u06c6\u0634\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u0634 \u0628\u06cc\u060c \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u0645\u0634\u0648\u0648\u0631\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062e\u0648\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0628\u06d5 \u062f\u06b5\u062e\u06c6\u0634\u06cc \u062e\u06c6\u062a \u0644\u06ce \u062f\u0648\u0648\u0631\u06d5\u067e\u06d5\u0631\u06ce\u0632 \u062f\u06d5\u06af\u0631\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06ce\u0698\u06af\u06d5 \u0626\u06d5\u0648\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06c6\u06cc \u0646\u0648\u0648\u0633\u06cc\u0628\u06cc\u0646 \u062a\u0648\u0648\u0634\u0645\u0627\u0646 \u0646\u0627\u06cc\u06d5\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0633\u06d5\u0631\u0648\u06d5\u0631\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u06d5 \u0648 \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0647\u06d5\u0631 \u062e\u06c6 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u067e\u06ce\u0631\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0646 \u062c\u06af\u06d5 \u0644\u06d5 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5\u0648 \u062f\u0648\u0648\u0627\u0646\u06d5\u06cc - \u06a9\u06d5 \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9\u06cc\u0627\u0646 \u0628\u0627\u0634\u06d5\u06a9\u0627\u0646\u0646 - \u0634\u062a\u06ce\u06a9\u06cc \u062a\u0631\u0645\u0627\u0646 \u062a\u0648\u0648\u0634 \u0628\u0628\u06ce\u061f \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc\u0646 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u060c \u06cc\u0627\u0646 \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06ce\u0645\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u0648 \u0628\u06c6 \u0628\u0646\u06ce\u0631\u06ce. \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646\u060c \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06d5 \u062e\u06c6\u0634\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06ce \u06cc\u0627\u0646 \u0628\u06d5 \u0632\u06c6\u0631\u06cc \u0645\u0627\u06b5 \u0628\u06d5\u062e\u062a \u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u0644\u06ce\u062a\u0627\u0646 \u0642\u0628\u0648\u0648\u06b5 \u0646\u0627\u06a9\u0627\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631 \u0628\u0648\u0648\u0646.',
    '\u0628\u06c6\u06cc\u06d5\u0634 \u0647\u06d5\u0631\u0686\u06cc \u0628\u06d5\u062e\u062a\u06cc \u0628\u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u0644\u06ce\u062a\u0627\u0646 \u0642\u0628\u0648\u0648\u06b5 \u0646\u0627\u06a9\u0627\u060c \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc\u060c \u0646\u0648\u06ce\u0698\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u062a\u06d5\u0646\u0628\u06d5\u06b5\u06cc \u0628\u06d5 \u062c\u06ce \u062f\u06ce\u0646\u0646 \u0648 \u0626\u06d5\u0648 \u0645\u0627\u06b5\u06d5\u0634 \u06a9\u06d5\u0648\u0627 \u062f\u06d5\u06cc\u0628\u06d5\u062e\u0634\u0646\u060c \u0632\u06c6\u0631 \u0628\u06d5 \u0646\u0627\u0628\u06d5\u062f\u06b5\u06cc \u062f\u06d5\u06cc\u062f\u06d5\u0646.',
    '\u0633\u06d5\u06cc\u0631\u062a \u0646\u06d5\u06cc\u06d5 \u0644\u06d5 \u062f\u0627\u0631\u0627\u06cc\u06cc \u0648 \u0632\u0627\u0631\u0648\u0632\u06cc\u0686\u06ce \u06a9\u06d5 \u0647\u06d5\u06cc\u0627\u0646\u06d5\u061b \u062e\u0648\u0627 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0628\u06d5\u0645\u0627\u0646\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u0627 \u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u0628\u0645\u0631\u0646.',
    '\u0628\u06d5 \u0633\u0648\u06ce\u0646\u062f\u06d5\u0648\u06d5 \u0695\u06d5\u0634 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5\u060c \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06ce\u0645\u06d5\u0634 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u06cc\u0646\u061b \u062f\u0631\u06c6\u0634 \u062f\u06d5\u06a9\u06d5\u0646 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646 \u06af\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u062a\u0631\u0633\u06d5\u0646\u06c6\u06a9\u0646.',
    '\u06af\u06d5\u0631 \u0626\u06d5\u0646\u0648\u0627\u06cc\u06d5\u06a9 \u06cc\u0627\u0646 \u0695\u06d5\u0647\u06c6\u06b5\u06ce\u06a9 \u06cc\u0627\u0646 \u0626\u06d5\u0634\u06a9\u06d5\u0648\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u0634\u06a9 \u0628\u0631\u0628\u0627\u060c \u0628\u06d5 \u0647\u06d5\u06b5\u06d5\u062f\u0627\u0648\u0627\u0646 \u0628\u06c6\u06cc \u062f\u06d5\u0686\u0648\u0648\u0646.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u062f\u06d5\u0642\u06d5 \u0628\u06d5\u0634\u06cc\u0646\u06d5\u0648\u06d5\u062a \u0628\u06d5 \u06af\u0627\u0632\u0646\u062f\u06d5\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u062f\u0631\u06ce\u060c \u0695\u0627\u0632\u06cc \u062f\u06d5\u0628\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06d5\u0631 \u0646\u06d5\u06a9\u06d5\u0648\u06ce\u060c \u062a\u0648\u0648\u0695\u06d5 \u062f\u06d5\u0628\u0646 \u0648 \u0628\u06c6\u06b5\u06d5 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0648 \u0628\u06d5\u0634\u06d5 \u0695\u0627\u0632\u06cc \u0628\u0648\u0648\u0646\u0627\u06cc\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0627\u0646\u062f\u0627\u062a\u06ce \u0648 \u0628\u06cc\u0627\u0646\u06af\u0648\u062a\u0627: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u0645\u0627\u0646 \u0628\u06d5\u0633\u06d5 \u0648 \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u0627 \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6 \u0628\u06d5\u0634\u0645\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646\u060c \u0686 \u062f\u06d5\u0642\u06d5\u0648\u0645\u0627\u061f',
    '\u0632\u06d5\u06a9\u0627\u062a \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u0628\u062f\u0631\u06ce\u062a\u06d5 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646 \u0648 \u06a9\u06d5\u0645 \u062f\u06d5\u0633\u062a\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u06d5\u0646\u061b \u0628\u06d5\u062e\u062a\u06cc\u0634 \u0628\u06a9\u0631\u06ce \u0628\u06c6 \u0647\u06c6\u06af\u0631\u06cc \u0646\u06d5\u06cc\u0627\u0631\u0627\u0646 \u0648 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f\u0646\u06cc \u06a9\u06c6\u06cc\u0644\u0627\u0646 \u0648 \u0642\u06d5\u0631\u0632\u062f\u0627\u0631\u0627\u0646 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0644\u06d5 \u0695\u06ce\u06af\u0627 \u0628\u06d5 \u062c\u06ce \u0645\u0627\u0648\u0627\u0646\u062f\u0627. \u0628\u0695\u06cc\u0627\u0631\u06ce\u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5\u061b \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627 \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0644\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 \u0626\u06cc \u0648\u0627\u0634 \u0647\u06d5\u06cc\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0646\u0627\u0695\u06d5\u062d\u06d5\u062a \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0626\u06ce\u0698\u0646: \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u06d5\u0628\u06cc\u0633\u06ce. \u0628\u06ce\u0698\u06d5: \u06af\u0648\u06ce \u0644\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06af\u0631\u06ce \u06a9\u06d5 \u0628\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5\u061b \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u061b \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06cc \u062a\u0631\u06cc\u0634 \u0628\u0695\u0648\u0627\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u0634\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u0648 \u0647\u06ce\u0646\u0627\u0648\u06d5. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0639\u06d5\u0632\u06cc\u06d5\u062a \u062f\u06d5\u062f\u06d5\u0646\u060c \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646 \u062f\u06ce\u0646.',
    '\u0633\u0648\u06ce\u0646\u062f\u0648\u0648 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062f\u06d5\u062e\u06c6\u0646\u060c \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0695\u0627\u0632\u06cc \u0628\u0646. \u0628\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u06a9\u0627\u0631\u06ce \u0628\u06a9\u06d5\u0646 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0695\u0627\u0632\u06cc \u0628\u0646.',
    '\u0628\u06c6 \u062f\u06d5\u0628\u06ce \u0646\u06d5\u06cc\u0627\u0646\u0632\u0627\u0646\u06cc\u0628\u06ce \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062f\u0698\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u0627 \u0628\u0648\u0648\u06d5\u0633\u062a\u06ce\u060c \u062a\u0627 \u0647\u06d5\u062a\u0627\u06cc\u06d5 \u0644\u06d5 \u0626\u0627\u06af\u0631\u06cc \u062f\u06c6\u0698\u06d5\u0647\u062f\u0627\u06cc\u06d5\u061b \u06a9\u06d5 \u0695\u06cc\u0633\u0648\u0627 \u0628\u0648\u0648\u0646\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06d5\u061f',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0648\u0648\u0695\u0648\u0648\u0646 \u062f\u06d5\u062a\u0631\u0633\u0646 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5\u0648\u0627\u0646 \u0633\u0648\u0648\u0695\u06d5\u062a\u06ce\u06a9 \u0628\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u062f\u06b5\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u06a9\u0627. \u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5 \u06af\u0627\u06b5\u062a\u06d5 \u0628\u06a9\u06d5\u0646\u060c \u0626\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u06cc \u0644\u06ce \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06ce\u0634\u06cc\u0627\u0646 \u0628\u067e\u0631\u0633\u06cc: \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u0686\u0646\u061f \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06ce\u0645\u06d5 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u06af\u06d5\u067e \u0648 \u06af\u0627\u06b5\u062a\u06d5 \u0628\u0648\u0648\u06cc\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u06af\u0627\u06b5\u062a\u06d5\u0648 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u0631\u062f\u061f',
    '\u062f\u0627\u0648\u0627\u06cc \u0644\u06ce \u0628\u0648\u0648\u0631\u062f\u0646\u06ce \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u067e\u0627\u0634 \u0628\u0695\u0648\u0627\u0647\u06ce\u0646\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5 \u062f\u0698\u06cc \u0626\u06cc\u0633\u0644\u0627\u0645\u06d5\u062a\u06cc\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u0648\u0648 \u062e\u06c6\u0634 \u0628\u0628\u06cc\u0646\u060c \u062f\u06d5\u0628\u06ce \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0626\u0627\u0632\u0627\u0631 \u0628\u062f\u0631\u06ce\u0646\u061b \u0644\u06d5 \u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631 \u0628\u0648\u0648\u0646.',
    '\u0646\u06ce\u0631 \u0648 \u0645\u06ce \u0644\u06d5\u0645 \u062f\u0648\u0648\u0695\u0648\u0648\u0627\u0646\u06d5 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u0646\u061b \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0631\u0627\u067e\u06d5 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0695\u06ce\u06af\u06d5 \u0644\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06af\u0631\u0646\u061b \u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06ce\u0631\u06a9\u0631\u062f\u0646 \u062f\u06d5\u0642\u0648\u0648\u0686\u06ce\u0646\u0646\u061b \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u062e\u0648\u062f\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u06a9\u0631\u062f. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0648\u0648\u0695\u0648\u0648\u06cc\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u0646.',
    '\u0628\u0695\u06cc\u0627\u0631\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u062f\u0648\u0648\u0695\u0648\u0648\u0627\u0646\u06cc \u0698\u0646 \u0648 \u067e\u06cc\u0627\u0648\u06cc\u0634 \u062f\u06d5\u06af\u0631\u06ce\u062a\u06d5\u0648\u06d5\u061b \u0628\u06d5 \u062a\u06ce\u06a9\u0695\u0627\u06cc\u06cc \u062f\u06d5\u06cc\u0627\u0646\u062e\u0627\u062a\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0648 \u062a\u0627 \u0647\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0633\u06d5\u061b \u0628\u06d5\u0631 \u0646\u06d5\u0641\u0631\u06cc\u0646\u06cc \u062e\u0648\u0627\u0634 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646 \u0648 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06cc\u0627\u0646 \u0628\u0646\u06d5\u06cc \u0646\u0627\u06cc\u06d5.',
    '\u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06ce\u0634 \u0626\u06ce\u0648\u06d5 \u0628\u0648\u0648\u0646\u060c \u0632\u06c6\u0631 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0647\u06ce\u0632\u062a\u0631 \u0628\u0648\u0648\u0646\u061b \u0645\u0627\u06b5 \u0648 \u0645\u0646\u062f\u0627\u06b5\u06cc\u0634\u06cc\u0627\u0646 \u067e\u062a\u0631. \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0648 \u0628\u06d5\u0634\u06d5\u06cc \u06a9\u06d5 \u0628\u0648\u0648\u06cc\u0627\u0646 \u06a9\u06d5\u06b5\u06a9\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u0631\u062a\u061b \u0626\u06ce\u0648\u06d5\u0634 \u0648\u06d5\u062a\u0631\u061b \u0647\u06d5\u0631 \u0648\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06ce\u0634 \u0626\u06ce\u0648\u06d5\u0634\u060c \u0644\u06d5\u0628 \u06d5\u0634\u06cc \u062e\u06c6 \u06a9\u06d5\u06b5\u06a9\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u0631\u062a. \u0626\u06d5\u0646\u06af\u06c6 \u062a\u0648\u0648\u0634\u06cc \u067e\u0695\u0648\u067e\u0648\u0648\u0686 \u0628\u0648\u0648\u0646\u061b \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0628\u0648\u0648\u0646. \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0648 \u0633\u06d5\u06b5\u0627\u062f\u0627 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u0641\u06ce\u0695\u06c6 \u0686\u0648\u0648\u061b \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631 \u0628\u0648\u0648\u0646.',
    '\u0626\u0627\u062e\u06c6 \u062f\u06d5\u0646\u06af \u0648 \u0628\u0627\u0633\u06cc \u0646\u0648\u0648\u062d \u0648 \u0639\u0627\u062f\u0648 \u0633\u06d5\u0645\u0648\u0648\u062f\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u0645\u06d5\u062f\u06cc\u06d5\u0646 \u0648 \u0645\u0648\u0626\u062a\u06d5\u0641\u06cc\u06a9\u06d5\u0648 \u0647\u0627\u0648\u0686\u06d5\u0631\u062e\u0627\u0646\u06cc \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06af\u06d5\u06cc\u0648\u06d5\u060c \u06a9\u06d5 \u0644\u06d5 \u067e\u06ce\u0634 \u0648\u0627\u0646\u062f\u0627 \u0698\u06cc\u0627\u0648\u0646\u061b \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u0686\u06d5\u0646\u062f \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0648\u06d5 \u0686\u0648\u0648\u0646\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u061f \u062e\u0648\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0646\u061b \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0698\u0646 \u0648 \u067e\u06cc\u0627\u0648\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u062f\u06c6\u0633\u062a\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0646\u061b \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0628\u06d5\u0631 \u0644\u06d5 \u06a9\u0627\u0631\u06cc \u06a9\u0631\u06ce\u062a \u062f\u06d5\u06af\u0631\u0646 \u0648 \u0646\u0648\u06ce\u0698 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u0646. \u062e\u0648\u0627 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u062e\u0627. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0628\u06d5 \u067e\u06cc\u0627\u0648\u0627\u0646 \u0648 \u0698\u0646\u0627\u0646\u06cc \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u062f\u0627\u0648\u06d5\u060c \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u062e\u06d5\u06b5\u0627\u062a \u062f\u06d5\u06a9\u0627\u060c \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0648\u06ce\u062f\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5. \u0686\u06d5\u0646\u062f\u06cc\u0646 \u062e\u0627\u0646\u0648\u0648\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u0627\u0634\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06cc \u0639\u06d5\u062f\u0646\u062f\u0627 \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u0646. \u0695\u06d5\u0632\u0627\u06cc \u062e\u0648\u062f\u0627\u0634\u060c \u06a9\u06d5 \u0644\u06d5 \u06af\u0634\u062a\u06cc \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5\u061b \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06cc \u0645\u06d5\u0632\u0646 \u0626\u06d5\u0645\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0648\u0648\u0695\u0648\u0648\u06cc\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06d5 \u0634\u06d5\u0695 \u0648\u06d5\u0631\u06d5 \u0648 \u0628\u06d5 \u062a\u0648\u0646\u062f \u0648 \u062a\u06cc\u0698\u06cc \u0695\u06d5\u0641\u062a\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06a9\u06d5. \u0626\u06d5\u0646\u0648\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06d5 \u0648 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc \u062f\u0698\u0648\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0633\u0648\u06ce\u0646\u062f\u06ce \u062f\u06d5\u062e\u06c6\u0646 \u06a9\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0631\u06a9\u0627\u0646\u062f\u0648\u0648\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u0642\u0633\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5\u06cc\u0627\u0646 \u06a9\u0631\u062f. \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u067e\u0627\u0634 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646 \u0628\u0648\u0648\u0646 \u0644\u06d5 \u062f\u06cc\u0646 \u067e\u06d5\u0634\u06cc\u0648\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u061b \u062e\u06d5\u06cc\u0627\u06b5\u06cc\u0627\u0646 \u062f\u0627\u0628\u0648\u0648\u06d5 \u0633\u06d5\u0631 \u0634\u062a\u06ce\u060c \u0628\u06c6\u06cc\u0627\u0646 \u0646\u06d5\u0644\u0648\u0627 \u067e\u06ce\u06a9\u06cc \u0628\u06ce\u0646\u0646. \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5\u0634 \u0695\u06a9\u06cc\u0627\u0646 \u0628\u06c6\u062a\u06d5\u0648\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u0627 \u0628\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646\u060c \u0646\u0627\u06af\u0648\u0632\u0648\u0648\u0631\u06cc \u0628\u06c6 \u0646\u06d5\u0647\u06ce\u0634\u062a\u0648\u0648\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0628\u0628\u0646\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0631 \u0628\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06d5 \u0648 \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u060c \u062e\u0648\u062f\u0627 \u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627 \u0648 \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627\u060c \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627 \u0648 \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u062f\u0627\u060c \u0646\u06d5 \u06a9\u06d5\u0633 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u060c \u0646\u06d5 \u06cc\u0627\u0648\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0633 \u062f\u06d5\u06a9\u06d5\u0648\u06ce.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0633\u062a \u06a9\u06d5: \u0634\u06d5\u0631\u062a\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0628\u06d5\u0634\u0645\u0627\u0646 \u0628\u062f\u0627\u062a\u06ce\u060c \u0626\u06ce\u0645\u06d5\u0634 \u062e\u06ce\u0631 \u0648 \u062e\u06ce\u0631\u0627\u062a \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u062f\u06d5\u0686\u06cc\u0646\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646\u06d5\u0648\u06d5.',
    '\u06a9\u06d5 \u062e\u0648\u0627 \u06a9\u0631\u062f\u06cc \u0648 \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0632\u06ce\u062f\u06d5\u06cc \u062f\u0627\u0646\u06ce\u060c \u0695\u0698\u062f\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u06a9\u0631\u062f \u0648 \u062f\u06cc\u0633\u0627\u0646 \u067e\u0634\u062a\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06a9\u0631\u062f \u0648 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0634\u06a9\u0627\u0646\u062f \u0648 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u0644\u06d5\u06cc\u06d5\u0644 \u062e\u0648\u062f\u0627 \u06a9\u0631\u062f\u060c \u062f\u0648\u0648\u0695\u0648\u0648\u06cc\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u062f\u06b5\u06cc\u0627\u0646\u062f\u0627 \u0647\u06ce\u0634\u062a\u06c6\u062a\u06d5\u0648\u06d5\u060c \u062a\u0627 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc \u062f\u06ce\u0646\u06d5 \u0628\u06d5\u0631 \u0628\u0627\u0631\u06d5\u06af\u0627\u06cc \u062e\u0648\u062f\u0627.',
    '\u0628\u06c6 \u0646\u0627\u0632\u0627\u0646\u0646 \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0695\u0627\u0632\u06cc\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0648 \u0626\u0627\u06af\u0627\u06cc \u0644\u06d5 \u0633\u0631\u062a\u06d5\u0634\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u062e\u06c6\u06cc \u0644\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0646\u0627\u062f\u06cc\u0627\u0631\u06d5\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u062a\u06d5\u0648\u0633 \u062f\u06d5\u06af\u0631\u0646\u06d5 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0627\u0646\u06ce \u06a9\u06d5 \u0632\u06ce\u062f\u06d5 \u0644\u06d5 \u0632\u06d5\u06a9\u0627\u062a\u060c \u062e\u06ce\u0631\u0627\u062a\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u061b \u062a\u0627\u0646\u06d5\u0634 \u062f\u06d5\u062f\u06d5\u0646 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0633 \u062f\u06d5\u0695\u0648\u0627 \u06a9\u06d5 \u062f\u06d5\u06cc\u0628\u06d5\u062e\u0634\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u06af\u0627\u06b5\u062a\u06d5\u0634\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u0646. \u062e\u0648\u062f\u0627 \u06af\u0627\u06b5\u062a\u06d5 \u0628\u06d5\u0645\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0644\u06ce \u062e\u06c6\u0634\u0628\u0648\u0648\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0648\u0627 \u0628\u06a9\u06d5\u06cc \u06cc\u0627\u0646 \u0646\u06d5\u06a9\u06d5\u06cc\u060c \u0628\u06ce \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u062d\u06d5\u0641\u062a\u0627 \u06a9\u06d5\u0695\u06d5\u062a\u06cc\u0634 \u0646\u0632\u0627\u06cc \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06a9\u06d5\u06cc\u060c \u062e\u0648\u0627 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06ce\u06cc\u0627\u0646 \u062e\u06c6\u0634 \u0646\u0627\u0628\u06ce\u061b \u0686\u0648\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u062e\u0648\u0627\u0634 \u06af\u0631\u06c6\u06cc \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u0627\u0646 \u0646\u0627\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0648\u06d5 \u062f\u06b5\u062e\u06c6\u0634\u0646 \u06a9\u06d5 \u0644\u06d5 \u0645\u0627\u06b5\u06d5\u062e\u06c6\u06cc\u0627\u0646 \u0645\u0627\u0646 \u0648 \u0644\u06d5 \u0647\u0627\u0648\u0695\u06ce \u0628\u0648\u0648\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0648\u0627\u0631\u062f \u0648 \u0646\u06d5\u06cc\u0627\u0646\u0648\u06cc\u0633\u062a \u06a9\u06d5 \u0645\u0627\u06b5 \u0648 \u06af\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u0627\u0647\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5\u062e\u062a \u06a9\u06d5\u0646 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0644\u06d5\u0645 \u06af\u06d5\u0631\u0645\u0627\u06cc\u06d5\u062f\u0627 \u062f\u06d5\u0631\u0645\u06d5\u06a9\u06d5\u0648\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06ce\u06af\u06d5\u06cc\u0634\u062a\u0646\u06cc\u0627\u0646 \u0647\u06d5\u0628\u0648\u0627\u06cc\u06d5\u060c \u062e\u06c6 \u0626\u0627\u06af\u0631\u06cc \u062c\u06d5\u062d\u06d5\u0646\u062f\u06d5\u0645 \u0632\u06c6\u0631 \u06af\u06d5\u0631\u0645\u062a\u0631\u06d5.',
    '\u0628\u0627 \u06a9\u06d5\u0645 \u0628\u062e\u06d5\u0646\u0646 \u0648 \u0632\u06c6\u0631 \u0628\u06af\u0631\u06cc\u0646 \u0644\u06d5 \u0633\u0632\u0627\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u062f\u0627 \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f.',
    '\u0626\u06d5\u0631 \u062e\u0648\u0627 \u062a\u06c6\u06cc \u0644\u06d5 \u062e\u06d5\u0632\u0627 \u06af\u06d5\u0695\u0627\u0646\u062f\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646 \u0648 \u0644\u06d5 \u062a\u06c6\u06cc\u0627\u0646 \u062f\u0627\u0648\u0627 \u06a9\u0631\u062f \u0626\u06cc\u0632\u0646\u06cc\u0627\u0646 \u0628\u062f\u06d5\u06cc \u0628\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627\u061b \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u0634\u06ce \u062f\u06d5\u06af\u06d5\u06b5\u0645\u0627\u0646 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u0646 \u0642\u06d5\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u0646 \u0646\u0627\u0686\u0646\u06d5 \u0634\u06d5\u0695\u06cc \u0647\u06cc\u0686 \u062f\u0648\u0698\u0645\u0646\u06ce\u06a9. \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0633\u06d5\u0631 \u0644\u06d5 \u0647\u06d5\u0648\u06d5\u06b5\u06ce \u062e\u0627\u0646\u06d5\u0646\u0634\u06cc\u0646\u06cc\u062a\u0627\u0646 \u0646\u06d5\u0642\u0627\u0646\u062f\u061b \u0626\u06ce\u0633\u062a\u0627\u0634 \u0647\u06d5\u0631 \u062e\u0627\u0646\u06d5\u0646\u0634\u06cc\u0646 \u0628\u0646\u061b \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0645\u0627\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0645\u0631\u06ce\u060c \u0646\u0627\u0628\u06ce \u0646\u0648\u06ce\u0698\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0628\u06a9\u06d5\u06cc \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u06af\u06c6\u0695\u06cc \u0695\u0627\u0645\u06d5\u0648\u06d5\u0633\u062a\u06d5. \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0648 \u06a9\u06d5 \u0645\u0631\u062f\u0646 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631 \u0628\u0648\u0648\u0646.',
    '\u062f\u0627\u0647\u0627\u062a \u0648 \u0639\u06d5\u0648\u0644\u0627\u062f\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0644\u0627\u062a\u06d5\u0648\u06d5 \u06af\u0631\u06cc\u0646\u06af \u0646\u06d5\u0628\u06ce. \u062e\u0648\u0627 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u0647\u06d5\u0631 \u0628\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u0627\u0646 \u062f\u0627 \u0648 \u0628\u06d5 \u0628\u06ce \u062f\u06cc\u0646\u06cc \u06af\u06cc\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0686\u06ce.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0633\u0648\u0648\u0695\u06d5\u062a\u06ce\u06a9 \u062f\u06ce\u062a\u06d5\u062e\u0648\u0627\u0631 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0628\u06d5 \u062e\u0648\u0627 \u0628\u0647\u06ce\u0646\u0646 \u0648 \u0644\u06d5 \u062a\u06d5\u06a9 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc\u062f\u0627 \u0628\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627\u060c \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06ce\u0646 \u0626\u06cc\u0632\u0646 \u0644\u06d5 \u062a\u06c6 \u062f\u06d5\u062e\u0648\u0627\u0632\u0646 \u0648 \u0626\u06ce\u0698\u0646: \u0648\u0627\u0632 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06ce\u0646\u06d5\u060c \u0628\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0644\u06d5 \u0645\u0627\u06b5 \u0628\u0645\u06ce\u0646\u06cc\u0646\u061b \u0648\u06d5\u06a9 \u062e\u0627\u0646\u06d5\u0646\u0634\u06cc\u0646\u0627\u0646\u06cc \u062a\u0631.',
    '\u0628\u06d5\u0648\u06d5 \u0695\u0627\u0632\u06cc \u0628\u0648\u0648\u0646 \u0648\u06d5\u0644 \u0628\u06d5 \u062c\u06ce \u0645\u0627\u0648\u06d5\u06a9\u0627\u0646 \u0628\u0646\u061b \u0645\u06c6\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631 \u062f\u06b5\u06d5\u0648\u06d5\u06cc\u06d5 \u0648 \u0647\u06cc\u0686 \u062a\u06ce \u0646\u0627\u06af\u06d5\u0646.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u062f\u0627\u0646 \u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0646\u060c \u0628\u06d5 \u0645\u0627\u06b5 \u0648 \u06af\u06cc\u0627\u0646 \u0686\u0648\u0648\u0646\u06d5 \u062e\u06d5\u0632\u0627\u061b \u062e\u06c6\u0634\u06cc \u0648 \u0686\u0627\u06a9\u06cc \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648 \u0695\u0632\u06af\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0626\u06d5\u0648\u0627\u0646\u0646.',
    '\u062e\u0648\u0627 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06cc \u0628\u06c6\u0648\u0627\u0646 \u0633\u0627\u0632 \u062f\u0627\u0648\u06d5 \u06a9\u06d5 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u062a\u0627 \u0647\u06d5\u062a\u0627\u06cc\u06d5\u06cc \u062a\u06ce\u062f\u0627 \u062f\u06d5\u0628\u0646. \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06cc \u06af\u06d5\u0648\u0631\u06d5.',
    '\u0639\u0627\u0631\u06d5\u0628\u06cc \u062f\u06d5\u0634\u062a\u06d5\u06a9\u06cc \u0647\u0627\u062a\u0648\u0648\u0646\u060c \u0628\u0695\u0648\u0628\u06d5\u0647\u0627\u0646\u06d5 \u062f\u06d5\u062a\u0627\u0634\u0646 \u0626\u06cc\u0632\u0646\u06cc\u0627\u0646 \u0628\u062f\u06d5\u06cc [\u0646\u06d5\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627]. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0647\u06d5\u06cc\u06d5\u060c \u0644\u06d5 \u0645\u0627\u06b5\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0695\u06c6\u0646\u06cc\u0634\u062a\u0648\u0648\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 - \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 - \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u062a\u0648\u0648\u0634 \u062f\u06d5\u0628\u0646.',
    '\u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06ce \u0647\u06ce\u0632 \u0648 \u0644\u06d5\u0634 \u0628\u06d5 \u0628\u0627\u0631 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0647\u06ce\u0646\u062f\u06d5 \u0634\u06a9 \u0646\u0627\u0628\u06d5\u0646 \u062e\u06ce\u0631\u0627\u062a \u0628\u06a9\u06d5\u0646\u060c \u0633\u0648\u0648\u0686\u06ce\u06a9 \u0646\u06cc\u06cc\u06d5\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0628\u06a9\u06d5\u0646. \u0647\u06cc\u0686 \u06af\u0644\u06d5\u06cc\u06d5\u06a9 \u0644\u06d5 \u0633\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06cc\u0686 \u06af\u0644\u06d5\u0634 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u06a9\u0631\u06ce \u06a9\u06d5 \u0647\u0627\u062a\u0646\u06d5 \u0644\u0627\u062a \u0647\u06d5\u06b5\u06cc\u0627\u0646\u06af\u0631\u06cc \u0648 \u06af\u0648\u062a\u062a: \u0686\u0627\u0631\u06d5\u0648\u0627\u06cc \u0648\u0627\u0645 \u0646\u06cc\u06cc\u06d5 \u0647\u06d5\u06b5\u062a\u0627\u0646\u06af\u0631\u06ce. \u0644\u06d5 \u062e\u06d5\u0645\u0628\u0627\u0631\u06cc \u0641\u0631\u0645\u06ce\u0633\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0686\u0627\u0648\u0627\u0646 \u062a\u0632\u0627 \u0648 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0647\u06cc\u0686\u06cc \u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648 \u0628\u06d5\u062e\u062a\u06cc \u0628\u06a9\u06d5\u0646.',
    '\u0695\u06ce\u06cc \u06af\u0627\u0632\u0646\u062f\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u0648\u0648\u062f\u06d5\u0646 \u0648 \u0645\u06d5\u0631\u06d5\u062e\u06d5\u0633\u06cc\u062a \u0644\u06ce \u062f\u06d5\u062e\u0648\u0627\u0632\u0646\u061b \u0628\u06d5\u0645\u06d5 \u0695\u0627\u0632\u06cc\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06d5 \u062c\u06ce \u0645\u0627\u0648\u06d5\u06a9\u0627\u0646 \u0628\u0646\u061b \u062e\u0648\u062f\u0627 \u0645\u06c6\u0631\u06cc \u0628\u06d5 \u0633\u06d5\u0631 \u062f\u06b5\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0648\u06d5 \u0648 \u0647\u06cc\u0686 \u062a\u06ce \u0646\u0627\u06af\u06d5\u0646.',
    '\u06a9\u0627\u062a\u06ce \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc\u0627\u0646\u060c \u0639\u0648\u0632\u0631\u062e\u0648\u0627\u06cc\u06cc\u062a\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646. \u0628\u06ce\u0698\u06d5: \u0647\u06cc\u0686 \u0639\u0648\u0632\u0631\u06cc \u067e\u06ce \u0646\u0627\u0648\u06ce\u060c \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u067e\u06ce \u0646\u0627\u06a9\u06d5\u06cc\u0646\u061b \u0644\u06d5\u0645\u06d5\u0695 \u0626\u06ce\u0648\u06d5 \u062e\u0648\u062f\u0627 \u062e\u06d5\u0628\u06d5\u0631\u06cc \u067e\u06ce \u062f\u0627\u0648\u06cc\u0646. \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u0646 \u0648 \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u0627 \u062f\u06d5\u0628\u0631\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u06cc\u0627 \u0648 \u0628\u06d5\u0631\u0628\u06ce\u0648\u0627\u0631\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5\u061b \u0626\u06d5\u0648\u0633\u0627 \u062e\u06d5\u0628\u06d5\u0631\u062f\u0627\u0631\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0686\u06c6\u0646 \u06a9\u0627\u0631\u06ce\u06a9\u062a\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc\u0627\u0646\u060c \u062f\u06ce\u0646 \u0648 \u0628\u06d5 \u062e\u0648\u0627\u062a\u0627\u0646 \u0633\u0648\u06ce\u0646\u062f \u062f\u06d5\u062f\u06d5\u0646 \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062a\u06ce\u0646\u06d5\u06af\u06d5\u06cc\u06d5\u0646\u0646. \u0626\u06ce\u0648\u06d5 \u0648\u0627\u0632 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06ce\u0646\u0646\u060c \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06af\u06d5\u0645\u0627\u0631\u0646 \u0648 \u0644\u06d5 \u0633\u0632\u0627\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06d5 \u062c\u06ce\u06af\u0627\u06cc\u0627\u0646.',
    '\u0645\u0644\u062a\u0627\u0646 \u0628\u06c6 \u0644\u06d5 \u0633\u0648\u06ce\u0646\u062f\u0627\u0646 \u062f\u06d5\u0646\u06ce\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u062e\u06c6\u0695\u0627\u0632\u06cc \u06a9\u06d5\u0646\u061b \u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5\u0634 \u0644\u06ce\u06cc\u0627\u0646 \u0695\u0627\u0632\u06cc \u0628\u0646\u060c \u062e\u0648\u0627 \u0644\u06d5 \u06af\u0631\u06c6\u06cc \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631 \u0695\u0627\u0632\u06cc \u0646\u0627\u0628\u06ce.',
    '\u0639\u0627\u0631\u06d5\u0628\u06d5\u06a9\u0627\u0646\u06cc \u062f\u06d5\u0634\u062a\u06d5\u06a9\u06cc \u0644\u06d5\u0645\u06d5\u0695 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc\u0646 \u0648 \u062f\u0648\u0648\u0695\u0648\u0648\u06cc\u06cc\u060c \u0628\u06ce \u0695\u06d5\u0632\u0627\u062a\u0631\u0646 \u0648 \u0628\u06c6 \u0633\u0646\u0648\u0648\u0631 \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u06cc\u0646\u06cc \u0647\u06d5\u0631\u0686\u06cc \u062e\u0648\u062f\u0627 \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u06a9\u06c6\u06b5\u06cc \u062a\u0631\u0646. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u062f\u06d5\u0634\u062a\u06d5\u06a9\u06cc\u0627\u0646\u06d5\u060c \u0647\u06d5\u0631\u0686\u06cc \u0628\u06cc\u062f\u06d5\u0646 \u0648\u0627\u06cc \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u062e\u0627\u0648\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0633\u06d5\u0646\u0631\u0627\u0648\u06d5\u061b \u0644\u06ce\u0648 \u062f\u06d5\u062e\u06d5\u0641\u062a\u06ce\u0646 \u06a9\u06d5 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631 \u0628\u06ce\u061b \u0628\u06d5\u06b5\u0627 \u0647\u06d5\u0631 \u0628\u06d5 \u0633\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06ce. \u062e\u0648\u062f\u0627 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648 \u062f\u06d5\u0634\u062a\u06d5\u06a9\u06cc\u0627\u0646\u06d5\u0634 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0628\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u0647\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0645 \u0695\u06ce\u06cc\u06d5\u062f\u0627 \u062f\u06d5\u06cc\u062f\u0627\u060c \u0628\u06c6 \u0646\u0632\u06cc\u06a9\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0628\u06c6 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u06d5 \u0646\u0632\u0627 \u0644\u06d5 \u0628\u06cc\u0631\u06cc\u0627\u0646 \u0646\u06d5\u06a9\u0627 \u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0647\u06d5\u0631\u0628\u06cc\u0646\u0627 \u0646\u0632\u06cc\u06a9 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u062e\u0627\u062a\u06d5 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc. \u062e\u0648\u062f\u0627 \u062f\u06cc\u0627\u0631\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u0628\u06d5\u062e\u0634 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u0648\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0647\u06d5\u0631 \u067e\u06ce\u0634\u0648\u0648 \u0626\u0627\u0648\u0627\u0631\u06d5 \u0628\u0648\u0648\u0646 \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631 \u0628\u0648\u0648\u0646\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u062f\u0627 \u062f\u0627\u06cc\u0627\u0646\u06d5 \u0633\u06d5\u0631 \u0634\u06c6\u067e\u06cc \u0626\u06d5\u0648\u0627\u0646\u060c \u0695\u0627\u0632\u06cc \u0628\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u062e\u0648\u0627 \u0695\u0627\u0632\u06cc\u0646 \u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06cc \u0628\u06c6 \u0633\u0627\u0632 \u062f\u0627\u0648\u0646 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u062a\u0627 \u0647\u06d5\u062a\u0627\u06cc\u06d5 \u062a\u06ce\u062f\u0627 \u062f\u06d5\u0628\u0646 \u0648 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06cc \u06af\u0631\u06cc\u0646\u06af.',
    '\u0644\u06d5\u0648 \u0639\u06d5\u0631\u06d5\u0628\u06d5 \u062f\u06d5\u0634\u062a\u06d5\u06a9\u06cc\u0627\u0646\u06d5\u06cc - \u06a9\u06d5 \u0644\u06d5 \u062f\u06d5\u0648\u0631\u0648\u0628\u06d5\u0631\u06cc \u0626\u06ce\u0648\u06d5\u0646 - \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u0645\u06d5\u062f\u06cc\u0646\u06d5 \u062f\u0648\u0648\u0695\u0648\u0648\u0646 \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u062f\u0648\u0648\u0695\u0648\u0648\u06cc\u06cc \u0695\u0627\u0647\u0627\u062a\u0648\u0648\u0646\u061b \u0626\u06d5\u062a\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u0646\u0627\u0633\u06cc\u060c \u0626\u06ce\u0645\u06d5 \u0632\u06c6\u0631 \u0628\u0627\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0646\u0627\u0633\u06cc\u0646. \u062f\u0648\u0648 \u062c\u0627\u0631 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646 \u0648 \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u062f\u0627 \u062f\u06d5\u06cc\u0627\u0646\u062e\u06d5\u06cc\u0646\u06d5 \u0628\u06d5\u0631 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u062f\u0698\u0648\u0627\u0631.',
    '\u0647\u06cc \u062a\u0631\u06cc\u0634 \u0647\u06d5\u0646 \u062f\u0627\u0646\u06cc\u0627\u0646 \u0646\u0627\u0648\u06d5 \u0628\u06d5 \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627\u060c \u06a9\u0627\u0631\u06ce \u0628\u0627\u0634 \u0648 \u06cc\u06d5\u06a9\u06ce \u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u062a\u06ce\u06a9\u06d5\u06b5 \u06a9\u0631\u062f\u061b \u0634\u0627\u06cc\u06d5\u062a \u062e\u0648\u062f\u0627 \u062a\u06c6\u0628\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0642\u0628\u0648\u0648\u06b5 \u0628\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0644\u06d5 \u062f\u0627\u0631\u0627\u06cc\u06cc\u06d5\u06a9\u06cc \u0647\u06d5\u06cc\u0627\u0646\u06d5 \u0633\u06d5\u062f\u06d5\u0642\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u06af\u0631\u06d5\u060c \u062a\u0627 \u0628\u06d5\u0648\u06d5 \u067e\u0627\u06a9 \u0648 \u062e\u0627\u0648\u06ce\u0646\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u06cc\u062a\u06d5\u0648\u06d5. \u0646\u0632\u0627\u06cc \u0628\u0627\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0647\u06d5\u0642 \u0628\u06a9\u06d5\u060c \u0646\u0632\u0627\u06cc \u062a\u06c6 \u0626\u06c6\u0642\u0631\u06d5\u06cc\u06d5 \u0628\u06c6 \u0648\u0627\u0646. \u062e\u0648\u062f\u0627 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0686\u06c6\u0646 \u062f\u06d5\u0628\u06ce \u0646\u06d5\u06cc\u0627\u0646\u0632\u0627\u0646\u06cc\u0628\u06ce \u062e\u0648\u062f\u0627 \u062a\u06c6\u0628\u06d5 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u06cc \u0642\u0628\u0648\u0648\u06b5 \u062f\u06d5\u06a9\u0627 \u0648 \u0633\u06d5\u062f\u06d5\u0642\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u06ce \u0648 \u062e\u0648\u062f\u0627 \u062a\u06c6\u0628\u06d5 \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0627\u0631 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5\u061f',
    '\u0628\u06ce\u0698\u06d5: \u062f\u06d5\u0633\u0627 \u0648\u06d5\u06a9\u0627\u0631 \u06a9\u06d5\u0648\u0646\u060c \u06a9\u0627\u0631\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0632\u0648\u0648\u06cc\u06d5\u06a9\u06cc \u0632\u0648\u0648 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u062f\u06cc\u0627\u0631 \u0626\u06d5\u062f\u0627 \u0648 \u0628\u06d5\u0648 \u0632\u0648\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0628\u0631\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0632\u0627\u0646\u0627 \u0628\u06d5 \u0646\u0627\u062f\u06cc\u0627\u0631\u06cc \u0648 \u062f\u06cc\u0627\u0631\u0627\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u0648\u0648 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0686\u06c6\u0646 \u06a9\u0627\u0631\u06ce\u06a9\u062a\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0686\u0627\u0648\u06d5\u0695\u06ce\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u0646. \u062c\u0627 \u06cc\u0627\u0646 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u060c \u06cc\u0627\u0646 \u062a\u06c6\u0628\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u06ce. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0648\u0627 \u0645\u0632\u06af\u06d5\u0648\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5 \u062a\u0627 \u0647\u06c6\u06cc \u0632\u06cc\u0627\u0646 \u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u0648 \u067e\u0698\u06af \u0648 \u0628\u06b5\u0627\u0648\u06cc \u0646\u06ce\u0648\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0628\u06ce \u0648 \u062a\u0627 \u0695\u0648\u0627\u0646\u06af\u06d5 \u0628\u06ce \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u062f\u0698\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0695\u0627\u0648\u06d5\u0633\u062a\u0627 \u0628\u0648\u0648\u0646\u060c \u0633\u0648\u06ce\u0646\u062f\u06cc\u0634 \u062f\u06d5\u062e\u06c6\u0646 \u06a9\u06d5: \u062c\u06af\u06d5 \u0644\u06d5 \u0686\u0627\u06a9\u06d5 \u06a9\u0631\u062f\u0646 \u0646\u06cc\u0627\u0632\u06ce\u06a9\u06cc \u062a\u0631\u0645\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u06af\u06d5\u061b \u062e\u0648\u0627 \u0634\u0627\u06cc\u06d5\u062a\u06d5 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0646\u0648\u06ce\u0698 \u0645\u06d5\u06a9\u06d5. \u0626\u06d5\u0648 \u0645\u0632\u06af\u06d5\u0648\u062a\u06d5\u06cc \u0695\u06c6\u0698\u06cc \u0628\u06d5\u0631\u06ce \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0633\u062a\u06cc \u062e\u0648\u062f\u0627\u062a\u0631\u0633\u06cc \u062f\u0627\u0645\u06d5\u0632\u0631\u0627 \u0628\u06ce\u060c \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5 \u06a9\u06d5 \u062a\u06c6 \u0646\u0648\u06ce\u0698\u06cc \u062a\u06ce\u062f\u0627 \u0628\u06a9\u06d5\u06cc. \u0644\u06d5\u0648\u06ce\u0646\u062f\u06d5\u0631\u06ce \u067e\u06cc\u0627\u0648\u06cc \u0648\u0627 \u0647\u06d5\u0646 \u062d\u06d5\u0632 \u062f\u06d5\u06a9\u06d5\u0646 \u0628\u06d5 \u067e\u0627\u06a9\u06cc \u0628\u0698\u06cc\u0646\u061b \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5 \u067e\u0627\u06a9\u0627\u0646 \u062e\u06c6\u0634\u06cc \u062f\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0647\u06cc\u0645\u06cc \u0645\u0632\u06af\u06d5\u0648\u062a\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0633\u062a\u06cc \u062e\u0648\u0627\u067e\u06d5\u0631\u0633\u062a\u06cc \u0648 \u0695\u06d5\u0632\u0627\u06cc \u062e\u0648\u062f\u0627 \u062f\u0627\u0695\u0634\u062a\u0628\u06ce \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5\u060c \u06cc\u0627\u0646 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u06a9\u06d5 \u0628\u0646\u0627\u063a\u06d5\u06cc \u0645\u0632\u06af\u06d5\u0648\u062a\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u06ce\u0648\u0627\u0631\u06cc \u06a9\u06d5\u0646\u062f\u0627\u06b5\u06cc \u0641\u0634\u06d5\u06b5\u06d5 \u0648 \u0695\u0648\u0648\u062e\u06d5\u06a9 \u062f\u0627\u0646\u0627\u0648\u06d5 \u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631 \u0648 \u06a9\u06b5\u067e\u06d5\u06cc \u062f\u06c6\u0698\u06d5\u06d5\u061f \u062e\u0648\u062f\u0627 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u0633\u062a\u06d5\u0645\u06a9\u0627\u0631\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0646\u0627\u06a9\u0627.',
    '\u0626\u06d5\u0648 \u062e\u0627\u0646\u0648\u0648\u06d5\u06cc \u0633\u0627\u0632\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5\u060c \u0647\u06d5\u0631 \u0645\u0627\u06cc\u06d5\u06cc \u062f\u06b5\u062e\u0648\u0631\u062a\u06cc\u0627\u0646\u06d5\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648 \u062f\u06b5\u0627\u0646\u06d5\u06cc\u0627\u0646 \u06a9\u0648\u062a \u06a9\u0648\u062a \u0628\u0628\u06ce. \u062e\u0648\u062f\u0627\u0634 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u062e\u0648\u062f\u0627 \u06af\u06cc\u0627\u0646 \u0648 \u0633\u0627\u0645\u0627\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06cc \u06a9\u0695\u06cc\u0648\u06d5 \u0648 \u0644\u06d5 \u0628\u0627\u062a\u06cc\u0627\u0646\u062f\u0627 \u062f\u06d5\u06cc\u0627\u0646\u062e\u0627\u062a\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u0648\u06d5. \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06cc\u0634 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u0627 \u0634\u06d5\u0695 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u062f\u06d5\u06a9\u0648\u0698\u0646 \u0648 \u062f\u06d5\u0634\u06cc\u0627\u0646\u06a9\u0648\u0698\u0646. \u0644\u06d5 \u062a\u06d5\u0648\u0695\u0627\u062a \u0648 \u0644\u06d5 \u0626\u06cc\u0646\u062c\u06cc\u0644 \u0648 \u0642\u0648\u0631\u0626\u0627\u0646\u06cc\u0634\u062f\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0648\u06d5\u0647\u0627 \u062f\u0631\u0627\u0648\u06d5. \u0633\u0627 \u06a9\u06ce \u0644\u06d5 \u062e\u0648\u0627 \u0628\u06c6 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627\u0646\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062f\u0631\u0648\u0648\u0633\u062a\u0631\u06d5\u061f \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0648 \u0633\u06d5\u0648\u062f\u0627\u06cc\u06d5\u0648 \u0645\u0632\u06af\u06ce\u0646\u06cc \u0628\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u0628\u062f\u06d5\u0646\u060c \u06a9\u06d5 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u06af\u06d5\u0648\u0631\u06d5\u0648 \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u062a\u0648\u0648\u06d5.',
    '\u062a\u06c6\u0628\u06d5\u06a9\u0627\u0631\u0627\u0646\u060c \u062e\u0648\u0627\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u062f\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u062f\u06d5\u0646\u061b \u0695\u06c6\u0698\u0648\u0648\u06af\u0631\u0646\u060c \u0628\u06d5 \u06a9\u0695\u0646\u06c6\u0634 \u0648 \u0633\u0648\u0698\u062f\u06d5\u0628\u06d5\u0631\u0646\u060c \u0647\u0627\u0646\u06d5\u06cc \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0628\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u062f\u06d5\u06af\u0631\u0646 \u0648 \u0633\u0646\u0648\u0648\u0631\u06cc \u062e\u0648\u0627 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646. \u0645\u0698\u062f\u06d5 \u0628\u062f\u06d5 \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646.',
    '\u0695\u06d5\u0648\u0627 \u0646\u06cc\u06cc\u06d5 \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0646\u060c \u062f\u0627\u0648\u0627\u06cc \u0628\u0648\u0648\u0648\u0631\u062f\u0646 \u0628\u06c6 \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u06d5\u06a9\u0627\u0646 \u0628\u06a9\u06d5\u0646 - \u062c\u0627 \u0628\u0627 \u062e\u0632\u0645\u06cc \u0646\u0632\u06cc\u06a9\u06cc\u0634 \u0628\u0646 - \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0633\u0648\u0648\u0631 \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u062c\u06d5\u062d\u06d5\u0646\u062f\u06d5\u0645 \u062c\u06ce\u06af\u06d5\u06cc\u0627\u0646\u06d5.',
    '\u06a9\u06d5 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u062f\u0627\u0648\u0627\u06cc \u0639\u0627\u0641\u0648\u0648 \u06a9\u0631\u062f\u0646\u06cc \u0628\u06c6 \u0626\u0627\u067e\u06cc \u062e\u06c6\u06cc \u06a9\u0631\u062f\u060c \u062a\u06d5\u0646\u06cc\u0627 \u0628\u06c6 \u0628\u06d5\u062c\u06ce \u0647\u06ce\u0646\u0627\u0646\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u06ce\u06a9 \u0628\u0648\u0648 \u06a9\u06d5 \u067e\u06ce\u06cc \u062f\u0627\u0628\u0648\u0648. \u0628\u06d5\u06b5\u0627\u0645 \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0633\u0648\u0648\u0631 \u0632\u0627\u0646\u06cc \u062f\u0698\u06cc \u062e\u0648\u0627\u06cc\u06d5\u060c \u0626\u06cc\u062a\u0631 \u062e\u06c6\u06cc \u062a\u06ce \u0646\u06d5\u06af\u06d5\u06cc\u0627\u0646\u062f. \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u06af\u06d5\u0644\u06ce\u06a9 \u0644\u06d5 \u062e\u0648\u0627\u062a\u0631\u0633 \u0648 \u0644\u06d5\u0633\u06d5\u0631\u062e\u06c6 \u0628\u0648\u0648.',
    '\u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u062e\u0648\u0627 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u06ce\u060c \u0626\u06cc\u062a\u0631 \u0647\u06d5\u062a\u0627 \u062f\u06cc\u0627\u0631\u06cc \u0646\u06d5\u06a9\u0627 \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u0686\u06cc \u0628\u06d5\u067e\u0627\u0631\u06ce\u0632 \u0628\u0646\u060c \u0644\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0646\u0627\u062e\u0627. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u062e\u0627\u0648\u06d5\u0646 \u0645\u0648\u06b5\u06a9\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06ce \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5. \u06af\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u062f\u06ce\u0646\u06ce \u0648 \u062f\u06d5\u0645\u0631\u06ce\u0646\u06ce \u0648 \u062c\u06cc\u0627 \u0644\u06d5 \u062e\u0648\u062f\u0627\u060c \u062f\u06c6\u0633\u062a \u0648 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0631\u0628\u06d5\u062f\u06d5\u0631\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062f\u06d5\u0631\u0646 \u0648 \u0644\u06d5 \u062c\u06d5\u0646\u06af\u06d5\u06cc \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5\u062f\u0627 \u0647\u06d5\u0631 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u06d5\u0648 \u0628\u0648\u0648\u0646\u060c \u062e\u06c6\u0634 \u0628\u0648\u0648\u06d5. \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646 \u0648\u06d5\u062e\u062a\u0627 \u0628\u0648\u0648 \u062f\u06b5\u06cc\u0627\u0646 \u06af\u0648\u0645\u0695\u0627 \u0628\u06ce\u060c \u062f\u06cc\u0633\u0627\u0646 \u062e\u0648\u0627 \u0639\u0627\u0641\u0648\u0648\u06cc \u06a9\u0631\u062f\u0646. \u062e\u0648\u062f\u0627 \u062f\u06b5\u0633\u06c6\u0632\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646\u0627 \u0645\u06cc\u0647\u0631\u06d5\u0648\u0627\u0646\u06d5.',
    '\u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0648 \u0633\u06ce \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06d5\u0632\u0627 \u062f\u0632\u06cc\u06d5\u0648\u06d5\u060c \u062a\u0627 \u0648\u0627\u06cc \u0644\u06ce\u0647\u0627\u062a \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0647\u06d5\u0631\u0627\u0648\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u062a\u06d5\u0646\u06af \u0647\u0627\u062a\u061b \u062e\u06c6\u0634\u06cc\u0627\u0646 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0647\u0627\u062a\u06ce \u0648 \u062a\u06ce\u06af\u06d5\u06cc\u06cc\u0634\u062a\u0646 \u0647\u06cc\u0686 \u067e\u06d5\u0646\u0627\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0646\u06d5\u0645\u0627\u0648\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0647\u06d5\u0631 \u067e\u06d5\u0646\u0627 \u0628\u06d5\u0648 \u0628\u06d5\u0631\u0646. \u067e\u0627\u0634\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634\u06cc \u0628\u06d5\u062e\u0634\u06cc \u06a9\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0648\u06d5\u06af\u06d5\u0695\u06ce\u0646. \u062e\u0648\u062f\u0627 \u062a\u06c6\u0628\u06d5 \u0642\u0628\u0648\u0648\u06b5 \u062f\u06d5\u06a9\u0627 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u0627 \u062a\u0631\u0633\u0648\u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0644\u06d5 \u0695\u06ce\u0632\u06cc \u0695\u0627\u0633\u062a\u0627\u0646\u062f\u0627 \u0628\u0698\u06cc\u0646.',
    '\u0645\u06d5\u062f\u06cc\u0646\u06d5\u06cc\u06cc \u0648 \u0626\u06d5 \u0648\u0639\u0627\u0631\u06d5\u0628\u06d5 \u062f\u06d5\u0634\u062a\u06d5\u06a9\u06cc\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u06d5\u0648\u0631\u06cc\u0627\u0646\u0646\u060c \u0646\u06d5\u062f\u06d5\u0628\u0648\u0648\u0627\u06cc\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0648\u06d5\u0644\u0627 \u0628\u0646\u0627\u06cc\u06d5 \u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5\u0648 \u0647\u06d5\u06b5\u0627\u0648\u0627\u0631\u062f\u0627\u06cc\u06d5\u061b \u0686\u0648\u0646\u06a9\u0648\u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u062a\u06cc\u0646\u06af\u06cc \u0648 \u0644\u06d5 \u0645\u0627\u0646\u06af\u06cc \u0648 \u0644\u06d5 \u0628\u0631\u0633\u06cc\u06cc\u06d5\u062a\u06cc - \u06a9\u06d5 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u062a\u0648\u0648\u0634\u06cc\u0627\u0646 \u062f\u06ce - \u0648 \u0647\u06d5\u0631 \u0647\u06d5\u0646\u06af\u0627\u0648\u06ce\u06a9\u06cc \u062f\u06d5\u06cc\u0647\u0627\u0648\u06ce\u0646\u060c \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u062a\u0648\u0648\u0695\u06d5 \u062f\u06d5\u06a9\u0627 \u0648 \u0647\u06d5\u0631 \u0632\u06d5\u0631\u0628\u06d5\u062a\u06ce \u0644\u06d5 \u062f\u0648\u0698\u0645\u0646\u06cc \u062f\u06d5\u0648\u06d5\u0634\u06ce\u0646\u0646\u060c \u06af\u0634\u062a\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0646\u0648\u0648\u0633\u06ce. \u062e\u0648\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u0627\u0646 \u0648\u0646\u062f\u0627 \u0646\u0627\u06a9\u0627.',
    '\u0647\u06d5\u0631\u0686\u06d5\u0646\u062f\u06d5 \u0645\u0627\u06b5 \u0628\u06d5\u062e\u062a \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06a9\u06d5\u0645 \u0648 \u0632\u06c6\u0631\u06cc \u0648 \u0644\u06d5 \u0647\u06d5\u0631\u06af\u06d5\u06b5\u06cc\u06d5\u06a9 \u062f\u06d5\u0631\u0628\u0627\u0632 \u062f\u06d5\u0628\u0646\u060c \u062e\u0648\u062f\u0627 \u0628\u06c6\u06cc\u0627\u0646\u06cc \u062f\u06d5\u0646\u0648\u0648\u0633\u06ce\u060c \u062a\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u0644\u06d5\u0648\u06d5 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0627\u0646 \u0628\u062f\u0627\u062a\u06ce.',
    '\u0646\u06d5 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0647\u06d5\u0645\u0648\u0648 \u062a\u06ce\u06a9\u0695\u0627 \u0648\u06d5\u062f\u06d5\u0631\u06a9\u06d5\u0648\u0646\u061b \u0628\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06af\u0627\u06cc\u06d5\u06a9 \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u0686\u0646\u06d5 \u062f\u06d5\u0631\u06ce\u060c \u062a\u0627 \u0644\u06d5 \u062f\u06cc\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627 \u062f\u06d5\u0628\u0646 \u0648 \u06a9\u06d5 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0648 \u0647\u06c6\u0632\u06cc\u0627\u0646 \u0628\u06cc\u0627\u0646\u062a\u0631\u0633\u06ce\u0646\u0646. \u0628\u06d5\u0634\u06a9\u0648\u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5\u06cc \u0647\u0627\u0648\u0633\u0627\u062a\u0627\u0646\u0646 \u0628\u06d5\u0634\u06d5\u0695 \u0648\u06d5\u0631\u0646\u061b \u0628\u0627 \u0632\u06d5\u0628\u0631 \u0648 \u0632\u06d5\u0646\u06af\u0648\u0648 \u0628\u0628\u06cc\u0646\u0646. \u0628\u06cc\u0634\u06cc\u0632\u0627\u0646\u0646 \u062e\u0648\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646\u06d5.',
    '\u0626\u06cc \u0648\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0646\u0627\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u062a\u0627 \u0633\u0648\u0648\u0695\u06d5\u062a\u06ce\u06a9 \u062f\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u0626\u06ce\u0698\u06ce: \u06a9\u06ce\u062a\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce \u067e\u062a\u06d5\u0648\u062a\u0631 \u0628\u0648\u0648\u06d5\u061f \u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5\u0647\u06ce\u0632\u062a\u0631 \u062f\u06d5\u0628\u06ce \u0648 \u06a9\u06d5\u06cc\u0641\u06ce \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u06cc \u062f\u06b5\u06cc\u0634\u06cc\u0627\u0646 \u0633\u0627\u063a \u0646\u06cc\u06cc\u06d5\u060c \u062d\u0627\u0634\u0627 \u062f\u06ce\u062a\u06d5 \u0633\u06d5\u0631 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u0648 \u0628\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u062f\u06d5\u0645\u0631\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u0627\u0628\u06cc\u0646\u0646 \u06a9\u06d5 \u0633\u0627\u06b5\u0627\u0646\u06d5 \u06a9\u06d5\u0695\u06d5\u062a\u06ce\u06a9 \u06cc\u0627\u0646 \u062f\u0648\u0648 \u06a9\u06d5\u0695\u06d5\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0628\u06d5\u0631 \u062a\u0627\u0642\u06cc\u06a9\u0627\u0631\u06cc\u061f \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u062f\u0627 \u0646\u06d5 \u062a\u0648\u0648\u0634\u06cc \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u062f\u06ce\u0646 \u0646\u06d5 \u067e\u06ce\u06cc \u062a\u06d5\u0645\u06ce \u06af\u0631\u062a\u0648\u0648 \u062f\u06d5\u0628\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0633\u0648\u0648\u0695\u06d5\u062a\u06ce\u06a9 \u062f\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631\u060c \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u062f\u06d5\u0695\u0648\u0627\u0646\u0646\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646: \u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u06ce\u061f \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5. \u062e\u0648\u0627 \u062f\u06b5\u06cc\u0627\u0646\u06cc \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u0648\u06d5\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u0628\u06ce \u0626\u0627\u0648\u06d5\u0632\u0627\u0646\u0646.',
    '\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u0647\u0627\u062a\u06d5 \u0644\u0627\u062a\u0627\u0646\u060c \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u06d5\u061b \u0647\u06d5\u0631 \u0628\u06d5\u06b5\u0627\u06cc\u06d5 \u0628\u06d5 \u0633\u06d5\u0631\u062a\u0627\u0646 \u0628\u06ce\u060c \u0644\u0627\u06cc \u06af\u0631\u0627\u0646\u06d5\u061b \u0632\u06c6\u0631\u06cc \u062f\u06b5 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06ce\u0648\u06d5\u06cc\u06d5\u061b \u062f\u06b5\u0633\u06c6\u0632\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u060c \u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u0645 \u0628\u06d5\u0633\u06d5\u060c \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce\u061b \u06a9\u0627\u0631\u06cc \u062e\u06c6\u0645 \u062f\u0627\u0648\u06d5\u062a\u06d5 \u062f\u06d5\u0633\u062a \u0626\u06d5\u0648\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u062e\u0648\u062f\u0627\u0646\u06cc \u062a\u06d5\u062e\u062a\u06cc \u0645\u06d5\u0632\u0646\u06d5.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0695\u0627\u060c \u0626\u06d5\u0645\u06d5 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u0646 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0648 \u062f\u0648\u0648\u0631 \u0644\u06d5 \u06af\u06c6\u0695\u0627\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0633\u06d5\u06cc\u0631 \u0645\u0627\u0648\u0646\u060c \u067e\u06cc\u0627\u0648\u06ce\u06a9\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5 \u06a9\u06d5: \u062a\u0631\u0633\u06ce \u0648\u06d5\u0628\u06d5\u0631 \u0645\u06d5\u0631\u062f\u0645 \u0646\u06ce \u0648 \u0645\u0698\u062f\u06d5 \u0628\u062f\u0627 \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646: \u067e\u0644\u06d5\u06cc \u0628\u06d5\u0631\u0632\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061f \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645 \u06a9\u0627\u0628\u0631\u0627\u06cc\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u06d5 \u0628\u06d5 \u0634\u06d5\u0634 \u0695\u06c6\u0698\u0627\u0646 \u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06cc \u062e\u06c6\u06cc \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0648\u06d5 \u0648 \u0628\u06d5\u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06cc \u0695\u0627\u062f\u06d5\u06af\u0627. \u06a9\u06ce \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0644\u0627\u06cc \u0626\u06d5\u0648 \u0628\u0628\u06ce\u062a\u06d5 \u062a\u06a9\u0627\u06a9\u0627\u0631\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06c6 \u062e\u06c6\u06cc \u0626\u06cc\u0632\u0646\u06cc \u0628\u062f\u0627\u061f \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5\u061b \u062f\u06d5\u0633\u0627 \u0639\u06d5\u0628\u062f\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06a9\u06d5\u0646. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0647\u06cc\u0686 \u0628\u06cc\u0631\u06cc \u0644\u06ce \u0646\u0627\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061f',
    '\u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u0631\u0648\u0633\u062a\u06d5\u060c \u0626\u06ce\u0648\u06d5 \u06af\u0634\u062a\u0648\u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0648 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u062f\u06d5\u0626\u0627\u0641\u0631\u06ce\u0646\u06ce \u0648 \u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u06cc\u06af\u06d5\u0695\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u061b \u062a\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0646 \u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u0695\u06d5\u0648\u0627\u06cc\u0627\u0646 \u0628\u0648\u0648\u06d5 - \u0628\u06ce \u062c\u06cc\u0627\u0648\u0627\u0632\u06cc - \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u062f\u0627\u062a\u06ce\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646 - \u0644\u06d5 \u0633\u0632\u0627\u06cc \u0646\u0627\u0633\u0648\u067e\u0627\u0633\u06cc\u062f\u0627 - \u0626\u0627\u0648\u06cc \u0632\u06c6\u0631 \u06af\u06d5\u0631\u0645 \u0648\u06d5\u062e\u06c6\u0646 \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0648 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u062e\u06c6\u0631\u06cc \u06a9\u0631\u062f\u06d5 \u062a\u06cc\u0634\u06a9\u062f\u06d5\u0631 \u0648 \u0647\u06d5\u06cc\u06a4\u06cc \u06a9\u0631\u062f\u06d5 \u062a\u0631\u06cc\u0641\u06d5\u062f\u0627\u0631 \u0648 \u0686\u06d5\u0646\u062f \u0642\u06c6\u0646\u0627\u063a\u06cc \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u062f\u061b \u062a\u0627 \u0647\u06d5\u0698\u0645\u0627\u0631\u06cc \u0633\u0627\u06b5\u06d5\u06a9\u0627\u0646 \u0648 \u062d\u06cc\u0633\u0627\u0628\u062a\u0627\u0646\u06cc \u067e\u06ce \u0628\u0632\u0627\u0646\u0646. \u062e\u0648\u0627 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5. \u0628\u06d5\u06b5\u06af\u06d5\u06a9\u0627\u0646 \u0634\u06cc \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0632\u0627\u0646\u0627\u0646.',
    '\u0644\u06d5 \u0647\u0627\u0645\u0648\u0634\u06c6\u06cc \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698 \u0648 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0648\u0648\u0646\u06cc\u060c \u0632\u06c6\u0631 \u0628\u06d5\u06b5\u06af\u06d5 \u0647\u06d5\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062e\u0648\u0627\u062a\u0631\u0633\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06cc\u062f\u0627\u0631\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0648\u06ce \u0648 \u0628\u06d5 \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627 \u0695\u0627\u0632\u06cc\u0646 \u0648 \u0644\u06d5\u0648 \u0628\u0627\u0631\u06d5\u0648\u06d5 \u062e\u0627\u062a\u0631\u062c\u06d5\u0645\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0644\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0647\u0627\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u060c',
    '\u0644\u06d5 \u0633\u0648\u06ce\u0646\u06af\u06d5\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0626\u0627\u06af\u0631 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u062c\u06ce\u06af\u06d5\u06cc\u0627\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06cc \u06a9\u06d5 \u0647\u06d5\u06cc\u0627\u0646\u06d5 \u062e\u0648\u0627 \u0695\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u0628\u0627\u062a\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u067e\u0695 \u0644\u06d5 \u062e\u06c6\u0634\u06cc \u0648 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627.',
    '\u0646\u0632\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06ce\u062f\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5: \u062e\u0648\u062f\u0627\u06cc\u06d5! \u067e\u0627\u06a9\u06cc \u0628\u06c6 \u062a\u06c6\u06cc\u06d5 \u0648 \u062e\u06c6\u0634 \u0648 \u0628\u06ce\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0633\u06b5\u0627\u0648\u06d5. \u062f\u0648\u0627\u0645\u06cc\u0646 \u0646\u0632\u0627\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u0633: \u0633\u067e\u0627\u0633 \u0628\u06c6 \u062e\u0648\u0627\u06cc \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u06af\u0634 \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0645\u06d5\u0631\u062f\u0645 \u0644\u06d5 \u062e\u06c6\u0634\u06cc \u0628\u06d5\u0644\u06d5\u0632\u0646\u060c \u062e\u0648\u062f\u0627\u0634 \u0628\u06c6 \u0632\u06cc\u0627\u0646 \u0644\u06ce\u062f\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0644\u06d5\u0632 \u0628\u0627\u06cc\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u062f\u06d5\u0645\u06ce\u06a9 \u0628\u0648\u0648 \u0646\u06d5\u062f\u06d5\u0645\u0627\u0646. \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627\u0632 \u062f\u06ce\u0646\u06cc\u0646 \u06a9\u06d5 \u062d\u06d5\u0632 \u0628\u06d5 \u062f\u06cc\u062f\u0627\u0631\u0645\u0627\u0646 \u0646\u0627\u06a9\u06d5\u0646\u061b \u0628\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u0633\u06d5\u0631\u06a9\u06ce\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u0633\u06d5\u0631\u06af\u06d5\u0631\u062f\u0627\u0646 \u0628\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5.',
    '\u0645\u0631\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9 \u067e\u06ce\u0633\u06cc\u0631\u06cc \u062f\u06d5\u06af\u0631\u06ce - \u0644\u06d5 \u0633\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0634\u062a \u06cc\u0627 \u0628\u06d5 \u0695\u0627\u06a9\u0634\u0627\u0646 \u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06ce\u0648\u06d5 - \u0644\u06d5\u0628\u06d5\u0631\u0645\u0627\u0646 \u062f\u06d5\u067e\u0627\u0695\u06ce\u062a\u06d5\u0648\u06d5. \u06a9\u0627\u062a\u06ce \u0628\u06d5\u06b5\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0644\u0627\u062f\u0627\u0646\u060c \u0648\u06d5\u0647\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0647\u06cc\u0686 \u0644\u06d5\u0628\u06d5\u0631\u0645\u0627\u0646 \u0646\u06d5\u067e\u0627\u0695\u0627\u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0628\u06c6 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9 \u062a\u0648\u0648\u0634\u06cc \u0628\u0648\u0648\u06d5! \u0626\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0647\u06d5\u06b5\u06d5\u0695\u06c6\u0646\u060c \u0695\u0627\u0632\u0627\u0646\u062f\u06c6\u062a\u06d5\u0648\u06d5.',
    '\u06af\u06d5\u0644\u06ce\u06a9 \u0628\u06d5\u0631\u06d5\u0645\u0627\u0646 - \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 - \u0644\u06d5\u0628\u06d5\u06cc\u0646 \u0628\u0631\u062f\u061b \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0627\u0646 \u06a9\u0631\u062f. \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062f\u06cc\u0627\u0631\u06cc\u06cc\u06d5\u0648\u06d5 \u0647\u0627\u062a\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u0647\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u0647\u06ce\u0646\u0627\u0646. \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0633\u0632\u0627 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 \u062c\u06ce\u0646\u0634\u06cc\u0646 \u06a9\u0631\u062f\u061b \u06a9\u06d5 \u0628\u0628\u06cc\u0646\u06cc\u0646 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0686\u06c6\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u06a9\u0627\u062a\u06ce \u0628\u06d5\u06b5\u06af\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0626\u0627\u0648\u0627\u062a\u06cc \u0686\u0627\u0648\u067e\u06ce\u06a9\u06d5\u0648\u062a\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0646\u06cc\u0646\u060c \u062f\u06d5\u06b5\u06ce\u0646: \u0642\u0648\u0631\u0626\u0627\u0646\u06ce\u06a9\u06cc \u062a\u0631\u0645\u0627\u0646 - \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0645\u06d5 - \u0628\u06c6 \u0628\u06ce\u0631\u06d5\u060c \u06cc\u0627\u0646 \u0628\u06cc\u06af\u06c6\u0695\u06d5. \u0628\u06ce\u0698\u06d5: \u0644\u06d5 \u0648\u0632\u06d5\u0645\u062f\u0627 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062e\u06c6\u0645\u06d5\u0648\u06d5 \u0628\u06cc\u06af\u06c6\u0695\u0645. \u0645\u0646 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06c6\u0645 \u0628\u06ce\u060c \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u062f\u06d5\u06a9\u06d5\u0645\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0645\u0646 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645 \u062f\u06d5\u0631\u0628\u0686\u0645 \u062a\u0631\u0633\u0645 \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0695\u06c6\u0698\u0627 \u0645\u06d5\u0632\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u06af\u06d5\u0631 \u062e\u0648\u0627 \u0628\u06cc\u0648\u06cc\u0633\u062a\u0627\u06cc\u06d5\u060c \u0628\u06c6\u062a\u0627\u0646\u0645 \u0646\u06d5\u062f\u06d5\u062e\u0648\u06ce\u0646\u062f\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u0641\u06ce\u0631\u06cc\u0634\u06cc \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f\u0646. \u0645\u0646 \u0628\u06d5\u0631\u0644\u06d5\u0648\u06d5\u0634 \u062a\u06d5\u0645\u06d5\u0646\u06ce\u06a9\u0645 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0695\u0627\u0628\u0648\u0627\u0631\u062f\u060c \u062f\u06d5\u06cc \u0633\u0627 \u0686\u06c6\u0646\u06d5 \u06a9\u06d5 \u062a\u06ce\u0646\u0627\u06af\u06d5\u0646\u061f',
    '\u06a9\u06ce \u0644\u06d5\u0648\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5 \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u06ce\u060c \u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc \u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0627\u0646\u06cc\u061f \u0628\u06ce\u06af\u0648\u0645\u0627\u0646\u06d5 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u0628\u0646.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0634\u062a\u0627\u0646\u06ce \u0648\u0627 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0646\u06d5 \u0632\u06cc\u0627\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u062f\u06d5\u0646 \u0646\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u0628\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061b \u062f\u06d5\u0634\u06b5\u06ce\u0646 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u06c6\u0645\u0627\u0646 \u062f\u06d5\u0628\u0646 \u0628\u06d5 \u062a\u06a9\u0627\u06a9\u0627\u0631. \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 \u062e\u0648\u0627 \u0644\u06d5 \u0634\u062a\u06ce\u06a9 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u06a9\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u067e\u06ce\u06cc \u0646\u06d5\u0632\u0627\u0646\u06cc\u0648\u0646\u061f \u062e\u0648\u062f\u0627 \u067e\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u0631\u0632\u062a\u0631\u06d5 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc.',
    '\u0647\u06d5\u0645\u0648\u0648 \u0645\u0631\u06c6\u060c \u06cc\u06d5\u06a9 \u062a\u06cc\u0631\u06d5 \u0628\u0648\u0648\u0646\u061b \u067e\u0627\u0634\u0627\u0646 \u0644\u06ce\u06a9 \u062c\u06cc\u0627\u0628\u0648\u0648\u0646\u06d5\u0648\u06d5. \u06af\u06d5\u0631 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u06af\u0641\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0646\u06d5\u0628\u0627\u060c \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0645\u0627\u06cc\u06d5\u06cc \u06a9\u06ce\u0634\u06d5\u06cc\u06d5 \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627\u060c \u0626\u06ce\u0633\u062a\u06d5 \u06cc\u06d5\u06a9\u0627\u06b5\u0627 \u06a9\u0631\u0627\u0628\u0648\u0648.',
    '\u062f\u06d5\u0634\u06b5\u06ce\u0646: \u0628\u06c6\u0686\u06cc \u067e\u06d5\u0631\u062c\u0648\u06ce\u06a9\u06cc \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u06d5\u0648\u06d5 \u0628\u06c6 \u0646\u06d5\u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0632\u0627\u0646\u0633\u062a\u06cc \u0646\u06d5\u062f\u06cc\u0627\u0631\u06cc \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5. \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646\u060c \u0626\u06d5\u0648\u0627 \u0645\u0646\u06cc\u0634 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0631\u06ce\u0632\u06d5\u06cc \u0686\u0627\u0648\u06d5\u0646\u0648\u0627\u0695\u0627\u0646\u0645.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u067e\u0627\u0634 \u0632\u06cc\u0627\u0646 \u062f\u06cc\u062a\u0646 - \u06a9\u06d5 \u0626\u06d5\u0648 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u062a\u0648\u0648\u0634\u06cc \u062f\u06ce\u0646 - \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u062a\u0627\u0645\u06cc \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062f\u06d5\u0686\u06ce\u0698\u0646\u060c \u0628\u06d5\u0644\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u062f\u0627 \u06af\u0632\u06cc \u0647\u06d5\u06cc\u06d5. \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u06af\u0632\u06cc \u0628\u06d5\u0644\u06d5\u0632\u062a\u0631\u06d5 \u0648 \u0647\u06d5\u0631 \u06af\u0632\u06d5\u06cc\u06d5\u06a9\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0626\u06d5\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u062f\u06d5\u06cc\u0646\u0648\u0648\u0633\u0646.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u062f\u06d5\u0631\u06cc\u0627 \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06d5\u0698\u0627 \u062f\u06d5\u062a\u0627\u0646\u06af\u06ce\u0695\u06ce. \u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u06a9\u06d5\u0634\u062a\u06cc \u062f\u0627\u0646 \u0648 \u0628\u06d5 \u0628\u0627\u06cc \u0644\u06d5\u0628\u06d5\u0631 \u062a\u06d5\u06a4\u06af\u06d5\u0695\u06cc\u0627\u0646\u06d5\u060c \u067e\u06ce\u06cc \u062f\u06b5\u062e\u06c6\u0634\u0646\u061b \u062a\u0627 \u0628\u0627\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u062a\u0648\u0646\u062f \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u0627 \u0648 \u0644\u06d5 \u06af\u0634 \u0644\u0627\u0648\u06d5 \u067e\u06ce\u0644\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06ce \u0648 \u0628\u0627\u0634 \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0648\u0631\u06cc\u0627\u0646 \u06af\u06cc\u0631\u0627\u0648\u06d5\u061b \u0628\u06d5\u0648\u067e\u06d5\u0695\u06cc \u0628\u0695\u0648\u0627\u06cc \u067e\u0627\u06a9\u06d5\u0648\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5: \u06af\u06d5\u0631 \u062a\u06c6 \u0644\u06d5\u0645\u06d5\u0645\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u06a9\u06d5\u06cc\u060c \u0644\u06d5 \u0633\u067e\u0627\u0633\u06af\u0648\u0632\u0627\u0631\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u062e\u0648\u062f\u0627 \u0646\u06d5\u062c\u0627\u062a\u06cc \u062f\u0627\u0646 - \u0628\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc - \u0644\u06d5\u0633\u06d5\u0631 \u0632\u06d5\u0648\u06cc \u06cc\u0627\u063a\u06cc \u062f\u06d5\u0628\u0646. \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0626\u06d5\u0645 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0632\u06cc\u0627\u0646\u06cc \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646\u06d5 \u0648 \u0628\u06c6 \u062e\u06c6\u0634\u06cc \u0698\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u061b \u067e\u0627\u0634\u0627\u0646 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u06a9\u06d5 \u0686\u0648\u0648 \u062f\u06d5\u06a9\u06d5\u0631\u062f.',
    '\u0647\u06d5\u06b5\u0628\u06d5\u062a \u0698\u06cc\u0627\u0646\u06cc \u0626\u06d5\u0648 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06d5\u0648 \u0626\u0627\u0648\u06d5 \u0648\u0627\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062d\u06d5\u0648\u0627 \u062f\u06d5\u06cc\u0628\u0627\u0631\u06ce\u0646\u06cc\u0646 \u0648 \u0695\u0648\u0648\u06d5\u06a9\u06cc \u062c\u0648\u0631\u0627\u0648\u062c\u06c6\u0631\u06cc \u0626\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u067e\u06ce \u062f\u06d5\u0695\u0648\u06ce\u061b \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0645\u0631\u06c6\u06a4 \u0648 \u0626\u0627\u0698\u06d5\u06b5 \u062f\u06d5\u06cc\u062e\u06c6\u0646. \u062a\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u0632\u06d5\u0645\u06cc\u0646 \u0695\u06d5\u0646\u06af\u0627\u0648\u0695\u06d5\u0646\u06af \u062f\u06d5\u0628\u06ce\u062a \u0648 \u062e\u06c6 \u062c\u0648\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0648 \u062e\u06ce\u0648\u06cc \u0632\u06d5\u0648\u06cc \u0644\u0627\u06cc\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0648\u0627\u0646\u062f\u0627\u06cc\u06d5. \u0647\u06d5\u062a\u0627 \u06a9\u0627\u062a\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc \u062f\u06ce\u062a\u06d5 \u0633\u06d5\u0631 - \u062c\u0627 \u0628\u06d5 \u0634\u06d5\u0648 \u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u062f\u0627 - \u0626\u0627\u0648\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u062f\u0631\u06d5\u0648\u06a9\u0631\u0627\u0648\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u062f\u0648\u06ce\u0646\u06ce \u0647\u06d5\u0631 \u0646\u06d5\u0628\u0648\u0648\u0628\u06ce! \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646 \u0634\u06cc \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0648\u0627\u0646\u06d5\u06cc \u0628\u06cc\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0628\u06c6 \u0645\u0627\u06b5\u06cc \u0647\u06ce\u0645\u0646\u06cc \u06af\u0627\u0632\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0626\u06d5\u0648 \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u0632\u06cc\u0627\u062a\u0631\u06cc\u0634\u061b \u062a\u06c6\u0632\u06cc \u0695\u06d5\u0634 \u0648 \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0646\u0627\u0646\u06cc\u0634\u06ce\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u0648\u062e\u0633\u0627\u0631\u06cc\u0627\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u06cc\u06d5\u06a9\u0627\u0646\u0646\u061b \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0633\u0632\u0627\u06cc\u0627\u0646 \u062e\u0631\u0627\u067e\u06d5\u06cc\u06d5\u06a9\u06cc \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06d5\u0648\u06d5 \u0648 \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u0634 \u0633\u06d5\u0631\u0628\u0627\u0631\u06cc\u0627\u0646\u06d5 \u0648 \u06a9\u06d5\u0633 \u0644\u06d5 \u062e\u0648\u0627\u06cc\u0627\u0646 \u0646\u0627\u067e\u0627\u0631\u06ce\u0632\u06ce\u061b \u0626\u06ce\u0698\u06cc \u0695\u0648\u0648\u06cc\u0627\u0646 \u0686\u06d5\u0646\u062f \u0628\u06d5\u062a\u06b5\u06ce\u06a9 \u0644\u06d5\u062a\u0627\u0631\u06cc\u06a9\u06d5\u0634\u06d5\u0648 \u062f\u0627\u06cc\u067e\u06c6\u0634\u06cc\u0648\u0646. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06cc\u0627\u0631\u06cc \u0626\u0627\u06af\u0631\u0646\u061b \u062a\u0627 \u0647\u06d5\u062a\u0627\u06cc\u06d5 \u062a\u06cc\u0627\u062f\u06d5\u0628\u0646.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u06a9\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u060c \u067e\u0627\u0634\u0627\u0646 \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0626\u06ce\u0698\u06cc\u0646 \u06a9\u06d5\u060c \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u0648\u0627 \u0632\u0627\u0646\u06cc: \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0634\u06d5\u0631\u06cc\u06a9\u0627\u0646\u0648\u0648 \u0644\u06d5 \u062c\u06ce \u0645\u06d5\u0628\u0632\u0648\u0648\u0646! \u0626\u06d5\u0648\u0633\u0627 \u0644\u06ce\u06a9\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062f\u0627\u0648\u06ce\u0631\u06cc\u0646. \u0634\u06d5\u0631\u06cc\u06a9\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646\u062f\u0648\u06ce\u0646\u0646: \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0626\u06ce\u0645\u06d5\u062a\u0627\u0646 \u0646\u06d5\u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u06d5.',
    '\u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u0648 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u062e\u0648\u062f\u0627\u0645\u0627\u0646 \u0634\u0627\u06cc\u06d5\u062a \u0628\u06ce \u0628\u06d5\u0633\u06d5\u061b \u0626\u06ce\u0645\u06d5 \u0647\u06cc\u0686 \u0626\u0627\u06af\u0627\u0646 \u0644\u06ce \u0646\u06d5\u0628\u0648\u0648\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u062a\u0627\u0646\u06cc\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0648 \u062c\u06ce\u06cc\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u06ce\u06a9 \u0628\u06d5 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u0627 \u062a\u06ce\u062f\u06d5\u067e\u06d5\u0695\u06ce\u061b \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u06a9\u06d5 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06cc \u062f\u06ce\u062a\u06d5\u0648\u06d5 \u0695\u0648\u0648. \u062f\u06d5\u06cc\u0627\u0646\u0628\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646\u061b \u0647\u06d5\u0631 \u062f\u0631\u06c6\u06cc \u0647\u06d5\u06b5\u06cc\u0627\u0646 \u0628\u06d5\u0633\u062a\u0628\u0648\u0648 \u0628\u06d5\u0641\u06cc\u0695\u06c6 \u0686\u0648\u0648.',
    '\u0628\u06ce\u0698\u06d5: \u06a9\u06ce\u06cc\u06d5 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0648\u06d5 \u0695\u06c6\u0632\u06cc\u0648 \u062f\u06d5\u062f\u0627\u061f \u06cc\u0627\u0646 \u062e\u0627\u0648\u06d5\u0646\u06cc \u0626\u06d5\u0645 \u06af\u0648\u06ce \u0648 \u0686\u0627\u0648\u0627\u0646\u06d5\u062a\u0627\u0646 \u06a9\u06ce\u06cc\u06d5\u061f \u0686 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0632\u06cc\u0646\u062f\u0648\u0648 \u0644\u06d5 \u0645\u0631\u062f\u0648\u0648 \u062f\u06d5\u0631\u062f\u06d5\u062e\u0627. \u0645\u0631\u062f\u0648\u0648 \u0644\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u0631\u062f\u06ce\u0646\u06ce \u0648 \u06a9\u06ce\u06cc\u06d5 \u0628\u06d5 \u06a9\u0627\u0631\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627\u061f \u0632\u0648\u0648 \u0626\u06ce\u0698\u0646: \u062e\u0648\u0627. \u0628\u06ce\u0698\u06d5: \u062f\u06d5\u06cc \u0628\u06c6 \u0644\u06ce\u06cc \u0646\u0627\u062a\u0631\u0633\u0646\u061f',
    '\u0628\u06d5\u06b5\u06ce\u060c \u0647\u06d5\u0631 \u062e\u0648\u0627 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc\u062a\u0627\u0646\u06d5\u061b \u0644\u06d5 \u067e\u0627\u0634 \u0695\u0627\u0633\u062a\u06cc \u0686 \u0647\u06d5\u06cc\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc\u061f \u062f\u06d5\u06cc \u0686\u06c6\u0646 \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0644\u0627\u062f\u06d5\u062f\u0631\u06ce\u0646\u061f!',
    '\u0626\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u0648\u062a\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u06d5\u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0648\u06d5\u0695\u0627\u0633\u062a \u06af\u06d5\u0695\u0627\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0628\u0695\u0648\u0627 \u0646\u0627\u0647\u06ce\u0646\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648 \u0634\u06d5\u0631\u06cc\u06a9\u0627\u0646\u06d5\u0648\u062f\u0627 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0646\u0648\u06ce \u0634\u062a\u06ce\u06a9 \u0686\u06ce \u0628\u06a9\u0627 \u0648 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc \u06a9\u0627\u062a\u06d5\u0648\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u067e\u06ce\u0634\u0648\u0648 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u0631 \u0686\u06ce \u062f\u06d5\u06a9\u0627 \u0648 \u062f\u0648\u0627\u06cc\u06d5\u0634 \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u062f\u06d5\u06cc \u0686\u06c6\u0646 \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0644\u0627\u062f\u06d5\u062f\u0631\u06ce\u0646\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648 \u0634\u06d5\u0631\u06cc\u06a9\u0627\u0646\u06d5\u0648\u062f\u0627 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u06a9\u06d5 \u0695\u06ce\u0628\u06d5\u0631\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0628\u06ce\u061f \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u0627\u0633\u062a \u0634\u0627\u0631\u06d5\u0632\u0627 \u062f\u06d5\u06a9\u0627. \u062f\u06d5\u0633\u0627 \u06a9\u06d5\u0633\u06ce\u06a9 \u06a9\u06d5 \u0695\u06ce\u0628\u06d5\u0631\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5\u060c \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u06a9\u06d5\u0648\u0646\u060c \u06cc\u0627\u0646 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06c6 \u062e\u06c6\u06cc \u0628\u06d5 \u0695\u06ce\u06af\u06d5 \u0646\u0627\u0632\u0627\u0646\u06ce\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0695\u06ce\u06af\u06d5\u06cc \u067e\u06ce \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u0646\u061f \u0686\u06cc\u062a\u0627\u0646\u06d5\u060c \u0686\u06c6\u0646 \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u06d5\u0646\u061f',
    '\u0632\u06c6\u0631\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u0634\u0648\u06ce\u0646 \u062e\u06d5\u06cc\u0627\u06b5 \u0648\u06ce\u06b5 \u062f\u06d5\u0628\u0646\u061b \u062e\u06c6 \u062e\u06d5\u06cc\u0627\u06b5\u06cc\u0634 \u0644\u06d5 \u062d\u0627\u0646\u062f \u0695\u0627\u0633\u062a\u06cc \u0686 \u0646\u0627\u0647\u06ce\u0646\u06ce. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0646\u0627\u06af\u0648\u0646\u062c\u06ce \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0647\u06cc \u062e\u0648\u062f\u0627 \u0646\u06d5\u0628\u06ce \u0648 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u0631\u0627\u0628\u06ce. \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u062f\u06d5\u06a9\u0627 \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u06a9\u06d5\u0648\u0627 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u0647\u0627\u062a\u0648\u0648\u06d5 \u0648 \u0626\u06ce\u0633\u062a\u0627 \u0644\u06d5\u0628\u06d5\u0631\u062f\u06d5\u0633\u062a\u06cc \u062f\u0627\u06cc\u06d5 \u0648 \u0695\u06c6\u0634\u0646\u06cc\u0634\u06cc \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u0647\u06cc\u0686 \u06af\u0648\u0645\u0627\u0646\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u06af\u0634 \u062c\u06cc\u0647\u0627\u0646 \u0695\u0627\u06cc\u06d5.',
    '\u06cc\u0627 \u0626\u06ce\u0698\u0646: \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u06cc\u0628\u06d5\u0633\u062a\u0648\u0648\u06d5. \u0628\u06ce\u0698\u06d5: \u06af\u06d5\u0631 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc\u062a\u0627\u0646\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062a\u0631 - \u06a9\u06d5 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u06af\u0627\u0632\u06cc \u0628\u06a9\u06d5\u0646 - \u062a\u0627\u0642\u06d5 \u0633\u0648\u0648\u0695\u06d5\u062a\u06ce\u06a9\u06cc \u0648\u06d5\u06a9 \u0626\u06d5\u0645 \u0648\u06d5\u062f\u06cc \u0628\u06ce\u0646\u0646.',
    '\u0646\u06d5\u062e\u06ce\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0634\u062a\u06ce\u06a9 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u06a9\u06d5 \u0647\u06cc\u0686\u06cc \u0644\u06ce \u062a\u06ce\u0646\u06d5\u06af\u06d5\u06cc\u0648\u0646 \u0648 \u0628\u06c6\u0634\u06cc\u0627\u0646 \u0644\u06ce\u06a9 \u0646\u06d5\u062f\u0631\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06ce\u0634 \u0626\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0628\u0648\u0648\u0646\u060c \u0628\u06d5\u0648 \u062f\u06d5\u0631\u062f\u06d5 \u0686\u0648\u0648\u0646. \u0633\u0627 \u0628\u0695\u0648\u0627\u0646\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u0628\u06d5 \u06a9\u0648\u06ce \u06af\u06d5\u06cc\u0634\u062a.',
    '\u0644\u06d5\u0648\u0627\u0646\u06cc\u0634\u062f\u0627 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce\u06cc\u06d5 \u0648 \u0647\u06d5\u0634\u06cc\u0627\u0646\u06d5 \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce \u0646\u06cc\u06cc\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06d5\u062f\u0641\u06d5\u0695\u0627\u0646 \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0646\u0627\u0633\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0698\u0646: \u062a\u06c6 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u060c \u0628\u06ce\u0698\u06d5: \u0626\u0627\u06a9\u0627\u0631\u06cc \u0645\u0646 \u0633\u06d5\u0631\u0628\u06d5\u0645\u0646\u06d5 \u0648 \u06a9\u0631\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0633\u06d5\u0631\u0628\u06d5\u062e\u06c6\u062a\u0627\u0646\u061b \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0631\u0645 \u062a\u06d5\u0631\u06cc\u06a9\u0646 \u0648 \u0645\u0646\u06cc\u0634 \u062a\u06d5\u0631\u06cc\u06a9\u0645 \u0644\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646.',
    '\u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0626\u06cc \u0648\u0627\u0634 \u0647\u06d5\u06cc\u06d5 \u06af\u0648\u06ce\u062a \u0644\u06ce\u062f\u06d5\u06af\u0631\u0646\u061b \u0626\u0627\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06cc\u0686 \u062a\u06ce\u0646\u06d5\u06af\u06d5\u0646\u060c \u062a\u06c6 \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u06af\u0648\u06ce \u0646\u06d5\u0628\u06cc\u0633\u062a\u0627\u0646 \u062a\u06ce\u0628\u06af\u06d5\u06cc\u06d5\u0646\u06cc\u061f',
    '\u062a\u06ce\u0634\u06cc\u0627\u0646\u062f\u0627\u06cc\u06d5 \u0644\u06ce\u062a \u062f\u06d5\u0695\u0648\u0627\u0646\u06ce\u061b \u0626\u0627\u062e\u06c6 \u062a\u06c6 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u06d5 - \u06a9\u0648\u06ce\u0631\u0627\u0646 \u06a9\u06d5 \u0686\u0627\u0648\u06cc \u062f\u06cc\u062a\u0646\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5 - \u0634\u0627\u0631\u06d5\u0632\u0627\u06a9\u06d5\u06cc\u061f',
    '\u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 - \u0628\u06d5 \u0647\u06cc\u0686 \u0626\u0627\u0648\u0627 - \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0646\u0627\u06a9\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0645\u06d5\u0631\u062f\u0645 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 - \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u062a\u0627\u0648\u06ce\u06a9 \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u0627 \u0645\u0627\u0628\u06ce\u062a\u0646\u06d5\u0648\u06d5 - \u062e\u06c6 \u0628\u06d5\u06cc\u06d5\u06a9\u062a\u0631 \u062f\u06d5\u0646\u0627\u0633\u06ce\u0646\u0646. \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0646 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u06cc\u062f\u0627\u0631\u06cc \u062e\u0648\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u06d5 \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce\u06af\u06d5\u0634 \u0646\u06d5\u0628\u0648\u0648\u06af\u0646.',
    '\u0626\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0645\u0627\u0646\u06d5\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u0645\u0627\u0646 \u067e\u06ce\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5\u060c \u0634\u0627\u0646\u062a \u0628\u062f\u06d5\u06cc\u0646\u060c \u06cc\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u0634\u062a \u0628\u0628\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646 \u0647\u06d5\u0631 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u0627\u0645\u0627\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u0634\u0627\u06cc\u06d5\u062a\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u06a9\u0627\u0631\u06cc\u0627\u0646.',
    '\u0647\u06d5\u0631 \u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9 \u0644\u06d5 \u0628\u0646\u06cc\u0627\u062f\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0628\u0648\u0648. \u06a9\u0627\u062a\u06ce \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0647\u0627\u062a\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u060c \u0628\u06ce \u062c\u06cc\u0627\u0648\u0627\u0632\u06cc \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0695\u06ce\u0648\u06d5\u0686\u0648\u0648\u06d5 \u0648 \u0647\u06cc\u0686 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0627\u0646 \u0644\u06ce \u0646\u06d5\u06a9\u0631\u0627\u0648\u06d5.',
    '\u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0642\u0633\u06d5\u0648 \u0695\u0627\u0633\u062a\u0646\u060c \u06a9\u06d5\u06cc \u0626\u06d5\u0645 \u0628\u06d5\u06b5\u06ce\u0646\u06d5 \u0628\u06d5 \u062c\u06ce \u062f\u06ce\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0645\u06d5\u06af\u06cc\u0646 \u062e\u0648\u062f\u0627 \u062d\u06d5\u0632 \u06a9\u0627\u060c \u062f\u06d5\u0646\u0627 \u0645\u0646 \u062e\u06c6\u0645 \u0644\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c \u0648 \u0632\u06cc\u0627\u0646\u06cc \u062e\u06c6\u0634\u0645 \u0646\u0627\u0632\u0627\u0646\u0645. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u0647\u06d5\u0631 \u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9 \u0645\u06d5\u0648\u062f\u0627\u06cc\u06d5\u06a9\u06cc \u062f\u06cc\u0627\u0631\u062f\u06d5\u06cc \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0645\u06d5\u0648\u062f\u0627\u06cc\u0627\u0646 \u062a\u06ce\u067e\u06d5\u0695\u06ce\u060c \u0628\u06c6 \u0633\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u062f\u0648\u0627 \u0646\u0627\u06a9\u06d5\u0641\u0646 \u0648 \u067e\u06ce\u0634 \u0646\u0627\u06a9\u06d5\u0648\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0634\u06d5\u0648\u06ce\u06a9 \u06cc\u0627\u0646 \u0695\u06c6\u0698\u06ce\u06a9\u062f\u0627 \u0628\u06d5\u06b5\u0627\u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0628\u06c6 \u0628\u06ce\u060c \u062a\u0627\u0648\u0627\u0646\u06a9\u0627\u0631 \u067e\u06d5\u0644\u06d5\u06cc \u0686\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062f\u0648\u0627\u062c\u0627\u0631 \u062f\u06d5\u0646\u0627\u0648 \u0628\u06d5\u06b5\u0627\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0648\u062a\u0646 - \u06a9\u06d5 \u0632\u06c6\u0631 \u067e\u06d5\u0644\u06d5\u062a\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u06a9\u0631\u062f - \u0626\u06d5\u0648\u0633\u0627 \u0628\u0695\u0648\u0627 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06ce\u0646\u0646\u061f',
    '\u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0628\u06ce\u0698\u0646 \u06a9\u06d5 \u0628\u06d5\u0646\u0627\u0647\u06d5\u0642 \u062c\u0648\u0648\u06b5\u0627\u0646\u06d5\u0648\u06d5: \u062f\u06d5 \u0628\u0686\u06ce\u0698\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u0644\u06d5\u0628\u0646\u06d5\u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u061b \u0626\u0627\u062e\u06c6 \u062c\u06cc\u0627 \u0628\u06c6 \u06a9\u0627\u0631\u0627\u0646\u06ce\u06a9\u06cc \u062f\u06d5\u062a\u0627\u0646\u06a9\u0631\u062f\u060c \u0633\u0632\u0627 \u062f\u06d5\u062f\u0631\u06ce\u0646\u061f',
    '\u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646: \u062f\u0627\u062e\u06c6 \u0695\u0627\u0633\u062a\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0628\u06d5\u06b5\u06ce\u060c \u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u0645 \u062a\u06d5\u0648\u0627\u0648 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0644\u06d5\u0628\u06d5\u0631\u06cc \u0695\u0627\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631\u06a9\u06ce \u0646\u0627\u0647\u06d5\u0642\u06cc \u06a9\u0631\u062f\u0628\u06ce\u060c \u06af\u06d5\u0631 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5 \u0645\u0627\u06b5\u06cc \u0626\u06d5\u0648\u0628\u0627\u060c \u0644\u06d5 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u062e\u06c6\u06cc\u0627 \u062f\u06d5\u06cc\u062f\u0627. \u06a9\u0627\u062a\u06ce\u06a9 \u0686\u0627\u0648\u06cc\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0632\u0627\u0631 \u06a9\u06d5\u0648\u062a\u060c \u067e\u06c6\u0634\u0645\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648\u06d5\u0634\u06ce\u0631\u0646. \u0628\u06d5 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u0628\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0695\u0627\u062f\u06d5\u06af\u06d5\u0646 \u0648 \u0647\u06cc\u0686 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u062f\u0627\u0646\u060c \u06af\u0634 \u0647\u06cc \u062e\u0648\u062f\u0627\u0646. \u0644\u06ce\u0634\u062a\u0627\u0646 \u0695\u0648\u0648\u0646 \u0628\u06ce \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627 \u0695\u0627\u0633\u062a\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u062e\u06c6\u06cc \u0698\u06cc\u0646 \u062f\u06d5\u062f\u0627 \u0648 \u06af\u06cc\u0627\u0646 \u062f\u06d5\u0633\u062a\u06ce\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u062f\u06d5\u0648\u06af\u06ce\u0695\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648\u0695\u0627 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u06d5\u06a9\u0648\u0648 \u0628\u06c6 \u06af\u06d5\u06cc\u0634\u062a\u061b \u0686\u0627\u0631\u06d5\u06cc \u062f\u06d5\u0631\u062f\u06cc \u062f\u06b5\u0627\u0646\u06cc\u0634\u06d5 \u0648 \u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u06d5 \u0628\u06c6 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0644\u06d5\u0648 \u0686\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u06d5 - \u06a9\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u06a9\u0631\u062f - \u06a9\u06d5\u06cc\u0641 \u062e\u06c6\u0634 \u0628\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u0644\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0648\u06d5\u06a9\u06c6\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06c6\u06cc \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f\u0648\u0648\u0646\u060c \u0646\u06c6\u0695\u06cc\u0648\u062a\u0627\u0646\u06d5\u061f \u06a9\u06d5 \u0628\u06d5\u0695\u06d5\u0648\u0627 \u0648 \u0646\u0627\u0695\u06d5\u0648\u0627 \u062f\u0627\u0628\u06d5\u0634\u0648\u0648\u06a9\u0631\u062f\u0646\u061f \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0626\u06cc\u0632\u0646\u06cc \u067e\u06ce\u062f\u0627\u0646 \u06cc\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646\u061f',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646\u060c \u0628\u06d5 \u06af\u0648\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u0686\u06c6\u0646 \u0628\u06ce\u061f \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0686\u0627\u06a9\u06d5\u06cc\u06d5 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5\u0634\u06cc \u0647\u06d5\u0631\u06d5 \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0646\u0627\u0633\u0648\u067e\u0627\u0633\u0646.',
    '\u0626\u06d5\u062a\u06c6 \u0644\u06d5 \u0647\u06d5\u0631 \u0628\u0627\u0631\u06ce\u06a9\u062f\u0627 \u0628\u06cc \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646 \u0628\u062e\u0648\u06ce\u0646\u06cc \u0648 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06cc\u06a9\u06d5\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06ce\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0646\u060c \u06a9\u0627\u062a\u06ce \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u0646. \u0647\u06d5\u0631\u0686\u06cc \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627 - \u0628\u0627 \u0644\u06d5 \u0633\u06d5\u0646\u06af\u06cc \u0645\u06ce\u0631\u0648\u0648\u0644\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0648\u0631\u062f\u06cc\u0644\u06d5\u0634 \u06cc\u0627\u0646 \u0648\u0631\u062a\u0631 \u06cc\u0627\u0646 \u062f\u0631\u0634\u062a\u062a\u0631 \u0628\u06ce - \u0647\u06cc\u0686\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062a\u06c6 \u067e\u06ce\u0648\u0627\u0631 \u0646\u0627\u0628\u06ce \u0648 \u0648\u0627 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u0631\u062f\u0627.',
    '\u0628\u0627 \u062a\u06ce\u0628\u06af\u06d5\u0646 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06c6\u0633\u062a\u06cc \u062e\u0648\u062f\u0627\u0646\u060c \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5 \u0648 \u062e\u06d5\u0645\u06cc\u0634 \u0646\u0627\u062e\u06c6\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u062e\u06c6 \u0644\u06d5\u06af\u0648\u0627\u0646\u0627\u0647\u0640 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646\u060c',
    '\u0645\u0698\u062f\u06d5 \u0628\u06c6 \u0648\u0627\u0646 \u06a9\u06d5 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u06af\u0641\u062a\u06cc \u062e\u0648\u062f\u0627 \u06af\u06c6\u0695\u0627\u0646\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0646\u0627\u06cc\u06d5\u061b \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06cc \u0645\u06d5\u0632\u0646.',
    '\u0642\u0633\u06d5\u06cc\u0627\u0646 \u062e\u06d5\u0645\u06af\u06cc\u0646\u062a \u0646\u06d5\u06a9\u0627\u061b \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0628\u06d5 \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5\u060c \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5. \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u06cc\u0634 \u06a9\u06d5 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646- \u062c\u06af\u06d5 \u0644\u06d5 \u062e\u0648\u0627 - \u062f\u06d5\u0628\u06d5\u0646\u06d5 \u0628\u06d5\u0631 \u0634\u06d5\u0631\u06cc\u06a9\u0627\u0646\u06cc\u060c \u0648\u06d5\u062f\u0648\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06d5\u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u061b \u0648\u06d5\u062f\u0648\u0648\u06cc \u062e\u06d5\u06cc\u0627\u06b5\u06cc \u062e\u0627\u0648 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646 \u0648 \u0644\u06d5 \u06af\u0648\u0645\u0627\u0646 \u0646\u06d5\u062a\u0631\u0627\u0632\u0627\u0648\u0646.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc\u060c \u0634\u06d5\u0648\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u06a9\u06d5 \u062a\u06ce\u06cc\u062f\u0627 \u0628\u062d\u06d5\u0633\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc\u0634\u06cc \u062f\u0627 \u0628\u06d5 \u0695\u06c6\u0698\u06ce\u061b \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0628\u06d5\u06b5\u06af\u06d5 \u0632\u06c6\u0631\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0698\u0646\u06d5\u0648\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u0648\u062f\u0627 \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u0648\u0648\u06d5! \u062e\u0648\u0627 \u0644\u06d5\u0648 \u062a\u06c6\u0645\u06d5\u062a\u0627\u0646\u06d5 \u067e\u0627\u06a9\u06d5 \u0648 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5. \u0647\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u06ce\u0648\u06d5 \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u062a\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0645 \u0642\u0633\u06d5 \u0646\u06cc\u06cc\u06d5\u061b \u0686\u06c6\u0646 \u0634\u062a\u06ce\u06a9\u06cc \u06a9\u06d5 \u0646\u0627\u06cc\u0632\u0627\u0646\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06ce\u0698\u0646\u061f',
    '\u0628\u06b5\u06ce: \u0626\u06d5\u0648\u06cc \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u0633\u062a\u0646\u060c \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u0628\u0646.',
    '\u0695\u0627\u0628\u0648\u0627\u0631\u062f\u0646\u06ce\u06a9\u06cc \u0647\u0646\u062f\u06a9\u06d5 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u061b \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u062f\u0627 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u0645\u0627\u0646 \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u060c \u0644\u06d5 \u0633\u0632\u0627\u06cc \u062d\u0627\u0634\u0627\u06a9\u0631\u062f\u0646\u06cc\u0627\u0646 \u0628\u06d5\u062a\u0648\u0646\u062f\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u062a\u06c6 \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u06d5\u06a9\u06d5\u06cc \u0646\u0648\u0648\u062d\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u06d5\u060c \u06a9\u06d5 \u0648\u0627 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u06af\u06d5\u0644\u06c6! \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0627\u0646\u06d5\u0648\u06d5\u0645 \u0648 \u0628\u06cc\u0631 \u0647\u06ce\u0646\u0627\u0646\u06cc \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u0648\u0627 \u0644\u06d5 \u0645\u0646\u0695\u0627 \u0644\u0627\u0648 \u06af\u0631\u0627\u0646 \u062f\u06ce\u060c \u0645\u0646 \u062e\u06c6\u0645 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0633\u067e\u0627\u0631\u062f \u0648 \u0626\u06ce\u0648\u06d5\u0634 \u062f\u06d5\u06af\u06d5\u06b5 \u0634\u06d5\u0631\u06cc\u06a9\u0627\u0646\u0648\u0648 \u06a9\u0627\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646\u06d5 \u06cc\u06d5\u06a9 \u0648 \u06a9\u0627\u0631\u0633\u0627\u0632\u06cc\u06cc\u06d5\u06a9\u06cc \u0648\u06d5\u0647\u0627 \u0628\u06a9\u06d5\u0646 \u0647\u06cc\u0686 \u062f\u0648\u0648\u062f\u06b5\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06d5\u0628\u06ce. \u0626\u06d5\u0648\u0633\u0627 \u067e\u06d5\u0644\u0627\u0645\u0627\u0631\u0645 \u062f\u06d5\u0646 \u0648 \u0645\u0627\u0648\u06d5\u0645 \u0645\u06d5\u062f\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0646\u06d5\u0634\u0647\u0627\u062a\u0646\u06d5 \u0695\u06d5\u062f\u0627\u06cc\u06d5\u060c \u0645\u0646 \u062f\u0627\u0648\u0627\u06cc \u0647\u06cc\u0686 \u067e\u0627\u062f\u0627\u0634\u062a\u06ce\u06a9\u0645 \u0644\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u067e\u0627\u062f\u0627\u0634\u0645 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0695\u0627\u06cc\u06d5. \u0645\u0646 \u0641\u06d5\u0631\u0645\u0627\u0646\u0645 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5 \u0645\u0644\u06a9\u06d5\u0686\u06cc \u0628\u06d5\u0631 \u0628\u0627\u0631\u06d5\u06af\u0627\u06cc \u0628\u0645.',
    '\u0647\u06d5\u0631 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0631\u06ce\u0646\u0627\u060c \u0626\u06d5\u0648\u0633\u0627 \u062e\u06c6\u06cc \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627 \u0628\u0648\u0648\u0646 - \u0644\u06d5 \u06af\u06d5\u0645\u06cc\u06d5\u062f\u0627 - \u0646\u06d5\u062c\u0627\u062a\u0645\u0627\u0646 \u062f\u0627\u0646 \u0648 \u06a9\u0631\u062f\u0645\u0627\u0646\u0646 \u0628\u06d5 \u062c\u06ce\u0646\u0634\u06cc\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0648\u06d5\u0698\u06ce\u0631 \u0626\u0627\u0648 \u062e\u0633\u062a \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0646\u06d5\u06a9\u0631\u062f. \u062f\u06d5 \u0628\u0695\u0648\u0627\u0646\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062a\u0631\u0633\u06ce\u0646\u062f\u0631\u0627 \u0628\u0648\u0648\u0646\u060c \u0686\u06cc\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06cc\u0634 \u0641\u0631\u06cc\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062f\u06cc\u0627\u0631\u06cc\u06d5\u0648\u06d5 \u0646\u0627\u0631\u062f\u06d5 \u0644\u0627\u06cc \u06af\u06d5\u0644\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0646\u06d5\u0647\u0627\u062a\u0646\u06d5 \u0633\u06d5\u0631 \u0628\u0695\u0648\u0627 \u0628\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u06a9\u06c6\u0646\u062f\u0627 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u0647\u06ce\u0646\u0627\u0628\u0648\u0648. \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u0645\u06c6\u0631 \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u0633\u06d5\u0631\u062f\u06b5\u06cc \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06a9\u06d5\u0631\u0627\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646 \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u0627\u0695\u0648\u0648\u0646\u0645\u0627\u0646 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f \u0648\u06ce\u0695\u0627\u06cc \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646\u060c \u0628\u06c6 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0633\u06d5\u0631\u0632\u0644\u06d5\u06a9\u0627\u0646\u06cc \u062f\u06cc\u0648\u0627\u0646\u06cc. \u062e\u06c6\u06cc\u0627\u0646 \u06af\u06d5\u0644\u06ce\u06a9 \u0628\u06d5\u0632\u0644 \u0632\u0627\u0646\u06cc \u0648 \u062a\u0627\u0648\u0627\u0646\u06a9\u0627\u0631 \u0628\u0648\u0648\u0646.',
    '\u06a9\u0627\u062a\u06ce \u0695\u0627\u0633\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u067e\u06ce \u06af\u06d5\u06cc\u0634\u062a\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062c\u0627\u062f\u0648\u0648\u06d5.',
    '\u0645\u0648\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u0648\u06d5\u062e\u062a\u06ce \u0626\u06d5\u0645 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u067e\u06ce\u06af\u06d5\u06cc\u0648\u06d5\u060c \u0626\u0627\u062e\u06c6 \u0628\u06d5\u0645\u06d5 \u0626\u06ce\u0698\u0646 \u062c\u0627\u062f\u0648\u0648\u061f \u06a9\u06d5 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631 \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u0628\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5 \u0647\u0627\u062a\u06cc\u06cc\u06d5 \u0644\u0627\u0645\u0627\u0646\u060c \u06a9\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0645\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06cc \u0628\u0648\u0648\u0646\u060c \u0648\u06d5\u0631\u0645\u0627\u0646 \u06af\u06ce\u0695\u06cc \u0648 \u0626\u0627\u063a\u0627\u06cc\u06d5\u062a\u06cc \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u06ce\u061f \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u067e\u06ce\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u06af\u0648\u062a\u06cc: \u0647\u06d5\u0645\u0648\u0648 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0641\u0631\u06d5\u0632\u0627\u0646\u0645 \u0644\u06ce \u06a9\u06c6\u0648\u06d5\u06a9\u06d5\u0646.',
    '\u06a9\u06d5 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0647\u0627\u062a\u0646\u060c \u0645\u0648\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u0686\u06cc\u062a\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u0628\u0627\u0648\u06ce\u0698\u0646\u060c \u0628\u06cc\u0647\u0627\u0648\u06ce\u0698\u0646.',
    '\u06a9\u06d5 \u0626\u0627\u0648\u06cc\u062a\u06cc\u0627\u0646\u060c \u0645\u0648\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u0647\u06d5\u0631\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u0647\u0627\u0648\u0631\u062f\u0648\u0648\u062a\u0627\u0646\u06d5 \u062c\u0627\u062f\u0648\u0648\u06d5 \u0648 \u062e\u0648\u0627 \u0644\u06ce\u062a\u0627\u0646\u06cc \u067e\u0648\u0648\u0686\u06d5\u06b5 \u062f\u06d5\u06a9\u0627. \u062e\u0648\u062f\u0627 \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e\u06a9\u0627\u0631\u0627\u0646 \u0695\u0627\u0633\u062a \u0646\u0627\u0647\u06ce\u0646\u06ce.',
    '\u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0648\u06d5\u0695\u0627\u0633\u062a \u06af\u06d5\u0695\u0627\u0646\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0634\u0627\u0646 \u062f\u06d5\u062f\u0627\u061b \u062c\u0627 \u0628\u0627 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06cc\u0634 \u062d\u06d5\u0632\u06cc \u0644\u06ce \u0646\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0686\u06d5\u0646\u062f \u062a\u0627\u0632\u06d5\u0644\u0627\u0648\u06ce\u06a9 \u0644\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u062a\u0631- \u0644\u06d5 \u062a\u0631\u0633\u06cc \u0695\u06a9 \u0644\u06ce \u0647\u06d5\u0633\u062a\u0627\u0646\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u0627\u0646\u06cc \u062f\u06cc\u0648\u0627\u0646\u06cc \u0626\u06d5\u0648- \u0646\u06d5\u06cc\u0627\u0646\u0648\u06ce\u0631\u0627 \u0628\u0695\u0648\u0627\u06cc\u06d5 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0628\u06ce\u0646\u0646. \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0644\u06d5\u0648 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u062c\u06ce \u0628\u06d5\u0631\u0632\u06ce\u06a9 \u0648 \u0644\u06d5 \u0695\u06ce\u0632\u06cc \u0647\u06d5\u06b5\u06d5\u0695\u06c6\u06cc\u0627\u0646 \u0628\u0648\u0648.',
    '\u0645\u0648\u0648\u0633\u0627 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u06af\u06d5\u0644\u06c6! \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0645\u0644\u06a9\u06d5\u0686\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u0646\u060c \u0633\u0627 \u067e\u0634\u062a\u0648\u0648 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0628\u0633\u067e\u06ce\u0631\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0633\u067e\u0627\u0631\u062f. \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646! \u0645\u06d5\u0645\u0627\u0646\u062e\u06d5\u0631\u06d5 \u0628\u06d5\u0631 \u0626\u0627\u0632\u0627\u0631 \u0648 \u0628\u06ce\u06af\u0627\u0631\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631\u0627\u0646.',
    '\u0644\u06d5 \u0633\u06c6\u0646\u06af\u06d5\u06cc \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u062a\u0695\u0627\u060c \u0695\u0632\u06af\u0627\u0631\u0645\u0627\u0646 \u06a9\u06d5\u060c \u0644\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646.',
    '\u0645\u0648\u0648\u0633\u0627\u0648 \u0628\u0631\u0627\u06cc\u0645\u0627\u0646 \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f\u060c \u06a9\u06d5: \u0626\u06ce\u0648\u06d5 \u0628\u06c6 \u0647\u06c6\u0632\u06cc \u062e\u06c6\u062a\u0627\u0646 - \u0644\u06d5 \u0645\u06cc\u0633\u0631\u062f\u0627 - \u0686\u06d5\u0646\u062f \u0645\u0627\u06b5\u06ce\u06a9 \u0628\u06a9\u06d5\u0646 \u0628\u06d5 \u0626\u06d5\u0646\u0648\u0627 \u0648 \u0645\u0627\u06b5\u0627\u0646\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646 \u0628\u06d5 \u0695\u0648\u06af\u06d5 \u0648 \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646 \u0648 \u0645\u0698\u062f\u06d5 \u062f\u06d5 \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646.',
    '\u0645\u0648\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u0644\u0627\u0645\u0627\u0646! \u062a\u06c6 \u0644\u06d5 \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5\u062f\u0627 \u0645\u0627\u06cc\u06d5\u06cc \u062e\u06d5\u0645\u06b5 \u0648 \u0645\u0627\u06b5\u06cc \u062f\u0646\u06cc\u0627\u062a\u062f\u0627 \u0628\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u067e\u06cc\u0627\u0648\u0627\u0646\u06cc \u062f\u06cc\u0648\u06d5\u062e\u0627\u0646\u06cc. \u062e\u0648\u062f\u0627\u0646\u06ce \u0645\u06d5! \u0647\u06d5\u0631 \u062a\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u062a\u06c6 \u0648\u0646\u062f\u0627 \u06a9\u06d5\u0646. \u062e\u0648\u062f\u0627\u0646\u06ce \u0645\u06d5! \u0645\u0627\u06b5\u06cc\u0627\u0646 \u0644\u06ce \u062a\u06d5\u0631\u062a \u0648 \u062a\u0648\u0648\u0646\u0627 \u06a9\u06d5 \u0648 \u062f\u06b5\u06cc\u0627\u0646 \u067e\u062a\u0631 \u0695\u06d5\u0642 \u0648 \u0695\u06d5\u0634 \u06a9\u06d5 \u0628\u06b5\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0646\u06d5\u0647\u06ce\u0646\u0646\u061b \u0647\u06d5\u062a\u0627 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646 \u0628\u06d5 \u0686\u0627\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0646\u0632\u0627\u062a\u0627\u0646 \u0642\u0628\u0648\u0648\u06b5 \u0628\u0648\u0648\u060c \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0645\u06d5\u06cc\u062f\u0627\u0646 \u062f\u06d5\u0631\u0645\u06d5\u0686\u0646 \u0648 \u0645\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u0648\u06d5\u0686\u06d5\u0648 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u0645\u0627\u0646 \u0644\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u06cc\u06d5 \u067e\u06d5\u0695\u0627\u0646\u062f\u06d5\u0648\u06d5. \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0644\u06d5\u0634\u06a9\u0631\u06d5\u06a9\u06d5\u0634\u06cc - \u0628\u06c6 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0648 \u062f\u06d5\u0633\u062a\u062f\u0631\u06ce\u0698\u06cc - \u0634\u0648\u06ce\u0646\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0646\u061b \u062a\u0627 \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0647\u0627\u062a \u0628\u062e\u06ce\u0646\u06a9\u06ce\u060c \u06af\u0648\u062a\u06cc: \u0645\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645 \u0647\u06ce\u0646\u0627\u061b \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce\u06cc\u06d5\u061b \u0645\u0646\u06cc\u0634 \u0644\u06d5 \u062a\u06d5\u0633\u0644\u06cc\u0645 \u0628\u0648\u0648\u06af\u0627\u0646\u0645.',
    '\u062c\u0627 \u062a\u0627\u0632\u06d5\u061f \u062a\u06c6\u06cc\u06d5\u06a9 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u06cc\u0627\u063a\u06cc \u0628\u0648\u0648\u06cc \u0648 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u062e\u0631\u0627\u067e\u06a9\u0627\u0631\u0627\u0646 \u0628\u0648\u0648\u06cc\u061f',
    '\u0626\u06d5\u0645\u0695\u06c6 \u0644\u0627\u0634\u062a - \u0628\u06d5 \u0632\u0631\u06ce\u062a\u06d5\u0648\u06d5 - \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u062a\u0627 \u0628\u06c6 \u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u067e\u0627\u0634\u06d5 \u062a\u06c6 \u062f\u06ce\u0646\u060c \u067e\u06d5\u0646\u062f\u06ce\u06a9 \u0628\u06ce\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0632\u06c6\u0631\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0646\u06cc\u0646.',
    '\u0626\u06cc\u062a\u0631 \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u0645\u0627\u0646 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9 \u062e\u06c6\u0634 \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0646\u062f \u0648 \u0695\u06c6\u0632\u06cc \u067e\u0627\u06a9 \u0648 \u0686\u0627\u06a9\u0645\u0627\u0646 \u062f\u0627\u0646\u06ce. \u0647\u06d5\u062a\u0627 \u0632\u0627\u0646\u0633\u062a\u06cc\u0627\u0646 \u0647\u0627\u062a\u06d5 \u0644\u0627\u060c \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u061b \u062e\u0648\u062f\u0627\u0646\u06cc \u062a\u06c6 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u0627 \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u0686\u06cc \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u0628\u0648\u0648.',
    '\u06af\u06d5\u0631 \u062a\u06c6 \u0644\u06d5\u0648\u06d5\u06cc \u0628\u06c6\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce \u0628\u06d5\u06af\u0648\u0645\u0627\u0646\u06cc\u060c \u0633\u0627 \u0628\u067e\u0631\u0633\u06d5 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6 \u06a9\u062a\u06ce\u0628\u06cc\u0627\u0646 \u062f\u06d5\u062e\u0648\u06ce\u0646\u062f. \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0695\u0627 \u0628\u06c6\u062a \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0628\u06ce \u0634\u06a9 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u062f\u0648\u0648\u062f\u06b5\u0627\u0646 \u0645\u06d5\u0628\u06d5.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u0634\u06ce \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u06cc \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627\u060c \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u062a\u06c6\u0634 \u062f\u06d5\u0686\u06cc\u06cc\u06d5 \u0695\u06ce\u0632\u06cc \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06af\u0641\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u06cc\u0627\u0646 \u0644\u06ce \u0647\u0627\u062a\u06d5 \u062f\u06cc\u060c \u0626\u06cc\u062a\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0646\u0627\u0647\u06ce\u0646\u0646.',
    '\u062c\u0627 \u0628\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u060c \u0647\u06d5\u062a\u0627\u06a9\u0648\u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646 \u0628\u06d5 \u0686\u0627\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u0646.',
    '\u0628\u06c6\u0686\u06cc \u0647\u06cc\u0686 \u0634\u0627\u0631\u06ce\u06a9 \u0646\u06d5\u0628\u0648\u0648 \u06a9\u06d5 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646 \u062a\u0627 \u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u06cc \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u0628\u06af\u06d5\u06cc\u06ce\u0646\u06ce\u060c \u0628\u06ce\u0698\u06af\u06d5 \u0644\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u06cc\u0648\u0648\u0646\u0648\u0633\u061f \u06a9\u06d5 \u06a9\u0627\u062a\u06ce \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u060c \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u0627\u0628\u0695\u0648\u0648\u0628\u06d5\u0631\u06d5\u0645\u0627\u0646\u060c \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627 \u0644\u06ce \u0644\u0627\u062f\u0627\u0646 \u0648 \u0648\u0627\u0646 \u06a9\u0631\u062f \u06a9\u06d5 \u0647\u06d5\u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u062e\u06c6\u0634 \u0695\u0627\u0628\u0648\u06ce\u0631\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u06a9\u06d5\u06cc\u0641\u06cc \u0644\u06ce \u0628\u0627\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0646\u060c \u062a\u06ce\u06a9\u0695\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u062f\u06d5\u0647\u06ce\u0646\u0627. \u0626\u0627\u062e\u06c6 \u062a\u06c6 \u062f\u06d5\u062a\u06d5\u0648\u06ce \u0628\u06d5 \u0632\u06c6\u0631 \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646\u061f',
    '\u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0646\u06d5\u0628\u0648\u0648\u06d5 \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u06ce\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u062f\u0627. \u062e\u0648\u0627 \u06a9\u0631\u06ce\u062a\u06cc \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0695\u06cc\u0627\u0631 \u062f\u0627\u0648\u06d5 \u06a9\u06d5 \u062a\u06ce \u0646\u0627\u06af\u06d5\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06d5\u0631\u06cc \u062e\u06c6 \u0628\u062f\u06d5\u0646\u06ce \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0686\u0647\u0627 \u0647\u06d5\u06cc\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0645 \u0647\u06d5\u0645\u0648\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0648 \u062a\u0631\u0633\u0627\u0646\u062f\u0646\u06d5 \u06a9\u0627\u0631 \u0646\u0627\u06a9\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u06af\u0631\u06c6\u06cc \u0628\u0695\u0648\u0627 \u0646\u06d5\u0647\u06ce\u0646\u0627\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0646\u06d5 \u0647\u06d5\u0631 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc \u0695\u06c6\u0698\u0627\u0646\u06ce\u06a9\u0646 \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0695\u06c6\u0698\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06d5 \u0628\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646 \u0628\u0648\u0648\u0646 \u0648 \u0646\u06d5\u0645\u0627\u0646\u061f \u0628\u06ce\u0698\u06d5: \u062f\u06d5\u0633\u0627 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646\u060c \u0626\u06d5\u0648\u0627 \u0645\u0646\u06cc\u0634 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0645.',
    '\u067e\u0627\u0634\u0627\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u0645\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u061b \u0686\u0648\u0646 \u0626\u06d5\u0648\u06d5 \u0626\u06d5\u0631\u06a9\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u06a9\u06d5\u06cc\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062f\u06cc\u0646\u06cc \u0645\u0646 \u0628\u06d5 \u06af\u0648\u0645\u0627\u0646\u0646\u060c \u0645\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0646 \u0648 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0646\u0627\u067e\u06d5\u0631\u0633\u062a\u0645. \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0645\u0646 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0645\u060c \u062f\u06d5\u0648\u0645\u0631\u06ce\u0646\u06ce \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634 \u0628\u06d5 \u0645\u0646 \u062f\u0631\u0627\u0648\u06d5 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0628\u0645.',
    '\u0695\u0648\u0648\u0634 \u0647\u06d5\u0631 \u06a9\u06d5 \u0695\u06d5\u0648\u0634\u062a\u06cc \u062e\u0648\u062f\u0627\u067e\u06d5\u0631\u0633\u062a\u06cc \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u0695\u06ce\u0632\u06cc \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u0627\u0646 \u0628\u06ce.',
    '\u062c\u06af\u06d5 \u0644\u06d5 \u062e\u0648\u0627 \u0647\u0627\u0646\u0627 \u0628\u06c6 \u0634\u062a\u06ce \u0648\u0627 \u0645\u06d5\u0628\u06d5 \u06a9\u06d5 \u0646\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0628\u06c6 \u062a\u06c6 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u0632\u06cc\u0627\u0646\u06cc \u0644\u06ce \u062f\u06d5\u0628\u06cc\u0646\u06cc. \u0626\u06d5\u06af\u06d5\u0631 \u06a9\u0631\u062f\u062a\u060c \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u06cc.',
    '\u062e\u0648\u062f\u0627\u0634 \u0626\u06d5\u06af\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u0632\u06cc\u0627\u0646\u06ce\u06a9\u062a \u0628\u06a9\u0627 - \u0628\u06ce\u0698\u06af\u06d5 \u0644\u06d5 \u062e\u06c6\u06cc - \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce \u0646\u06cc\u06cc\u06d5 \u0644\u0627\u06cc \u0628\u062f\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u0634\u062a\u06cc \u06af\u06d5\u0631\u06d5\u06a9 \u0628\u06ce\u060c \u06a9\u06d5\u0633 \u067e\u06ce\u0634 \u0628\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u0646\u0627\u06af\u0631\u06ce \u0648 \u0647\u06d5\u0631 \u0639\u06d5\u0628\u062f\u06ce\u06a9\u06cc \u0626\u06d5\u0648 \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u062a\u0648\u0648\u0634\u06cc \u062f\u06d5\u06a9\u0627. \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0648\u0627 \u0695\u0627\u0633\u062a\u06cc\u0648 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0647\u0627\u062a. \u0647\u06d5\u0631\u06a9\u06ce \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a \u0628\u06d5\u062f\u06cc \u06a9\u0627\u060c \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u0648\u0648\u0646 \u0628\u06c6 \u062e\u06c6\u06cc\u06d5\u0633\u06cc\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u06af\u0648\u0645\u0695\u0627 \u0628\u0628\u06ce\u060c \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u062f\u0698\u06cc \u062e\u06c6\u06cc\u06d5\u0633\u06cc. \u0645\u0646 \u0628\u0631\u06cc\u06a9\u0627\u0631\u06cc \u06a9\u06d5\u0633\u062a\u0627\u0646 \u0646\u06cc\u0645.',
    '\u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06ce \u0628\u06c6\u062a \u062f\u06d5\u0646\u06ce\u0631\u06cc\u0646\u060c \u0634\u0648\u06ce\u0646\u06cc \u06a9\u06d5\u0648\u06d5 \u0648 \u062e\u06c6\u0695\u0627\u0628\u06af\u0631\u06d5\u060c \u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u0627 \u0648 \u062e\u0648\u062f\u0627 \u0628\u0627\u0634\u062a\u0631\u06cc\u0646 \u062f\u0627\u0648\u06d5\u0631\u06d5.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0695\u0627. \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06a9\u0627\u0646\u06cc \u067e\u062a\u06d5\u0648 \u06a9\u0631\u0627\u0648\u0646 \u0648 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0632\u0627\u0646\u0627 \u0634\u06cc \u06a9\u0631\u0627\u0648\u0646.',
    '\u06a9\u06d5 \u0646\u0627\u0628\u06ce \u0647\u06cc\u0686 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u061b \u0645\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0648 \u0645\u0648\u0698\u062f\u06d5\u062f\u06d5\u0631\u0645.',
    '\u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646\u06cc\u0634 \u062f\u0627\u0648\u0627 \u0628\u06a9\u06d5\u0646\u060c \u0644\u06ce\u0648 \u0628\u0628\u0648\u0648\u0631\u06ce \u0648 \u0628\u0634\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648\u060c \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u0646\u0627\u0648\u0646\u0631\u0627\u0648 \u0698\u06cc\u0627\u0646\u06cc \u062e\u06c6\u0634\u062a\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0628\u06cc\u0646 \u06a9\u0627 \u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0627\u0648\u06d5\u0646 \u0647\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9 \u0686\u0627\u06a9\u06d5\u06cc \u0628\u06a9\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u067e\u0634\u062a\u06cc\u0634\u0648\u0648 \u0647\u06d5\u06b5 \u0628\u06a9\u06d5\u0646\u060c \u062a\u0631\u0633\u0645 \u0647\u06d5\u06cc\u06d5 \u0695\u06c6\u0698\u0627 \u0645\u06d5\u0632\u0646 \u0633\u0632\u0627\u0648 \u0628\u062f\u0631\u06ce.',
    '\u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u062e\u06c6\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u067e\u06ce \u062f\u06d5\u06a9\u0631\u06ce.',
    '\u062a\u06c6 \u0628\u0632\u0627\u0646\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0633\u0646\u06af\u06cc \u062e\u06c6 \u062e\u0648\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u062a\u0627 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u062f\u06cc\u0627\u0631 \u0646\u06d5\u0628\u0646\u061b \u062a\u06c6 \u0628\u0632\u0627\u0646\u06d5 \u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u062f\u0627 \u062e\u06c6 \u0628\u06d5 \u062c\u0644\u06a9\u06cc\u0627\u0646 \u062f\u0627\u062f\u06d5\u067e\u06c6\u0634\u0646\u060c \u0626\u06d5\u0648 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06cc\u067e\u06d5 \u0644\u06d5\u0645\u06d5\u0695\u0686\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0686\u06cc \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u06d5\u0628\u06ce\u0698\u0646\u061b \u062e\u06c6\u06cc \u0644\u06d5 \u0695\u0627\u0632\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062f\u06b5\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u062f\u06d5\u062c\u0648\u0648\u06b5\u06ce\u062a\u06d5\u0648\u06d5 \u06af\u0634\u062a\u06cc \u0695\u06c6\u0632\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u062e\u0648\u0627\u06cc\u06d5\u061b \u062f\u06d5\u0632\u0627\u0646\u06ce \u0628\u0646\u06a9\u06d5\u06cc \u0644\u06d5 \u06a9\u0648\u06ce\u06cc\u06d5 \u0648 \u0644\u06d5 \u06a9\u0648\u06ce\u062f\u0627 \u062e\u06c6\u06cc \u062d\u06d5\u0634\u0627\u0631 \u062f\u06d5\u062f\u0627\u061b \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631\u06cc \u0644\u06d5 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06cc \u062f\u06cc\u0627\u0631\u06cc\u062f\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u06a9\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0644\u06d5 \u0634\u06d5\u0634 \u0695\u06c6\u0698\u0627 \u062f\u0631\u0648\u0633\u062a \u06a9\u0631\u062f \u0648 \u062a\u06d5\u062e\u062a\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06cc \u0626\u06d5\u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u0648 \u0628\u0648\u0648\u061b \u062a\u0627 \u0628\u06d5\u062a\u0627\u0642\u06cc\u062a\u0627\u0646 \u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u06a9\u0627\u0645\u0648\u0648 \u0626\u0627\u06a9\u0627\u0631\u0648\u0648 \u0686\u0627\u06a9\u062a\u0631\u06d5. \u06af\u06d5\u0631 \u0648\u062a\u06cc\u0634\u062a: \u0644\u06d5 \u067e\u0627\u0634 \u0645\u0631\u062f\u0646 \u0626\u06ce\u0648\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062c\u0627\u062f\u0648\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u06c6 \u0645\u0627\u0648\u06d5\u06cc\u06ce \u06a9\u06d5\u0645\u06cc\u0634 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u062f\u0648\u0627 \u0628\u062e\u06d5\u06cc\u0646\u060c \u0626\u06ce\u0698\u0646: \u0686\u06cc \u0695\u06ce \u067e\u06ce \u0646\u06d5\u062f\u0627\u0648\u06d5\u061f \u0628\u0632\u0627\u0646\u0646 \u0695\u06c6\u0698\u06ce \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0628\u06af\u0627\u062a\u06d5 \u0644\u0627\u060c \u0626\u06cc\u062a\u0631 \u0644\u06ce\u0627\u0646 \u0646\u0627\u0648\u06d5\u06af\u06d5\u0695\u06ce\u060c \u0626\u06d5\u0648\u06cc \u06af\u06d5\u0645\u06d5\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u0647\u0627\u062a \u062f\u06d5\u0648\u0631\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u06af\u06d5\u0631 \u062a\u0627\u0645\u06cc \u062e\u06c6\u0634\u06cc\u06cc\u0645\u0627\u0646 \u0628\u06d5 \u0645\u0631\u06c6 \u0686\u06ce\u0698\u0627\u0646\u062f \u0648 \u067e\u0627\u0634\u0627\u0646 \u0644\u06ce\u0645\u0627\u0646 \u0626\u06d5\u0633\u062a\u0627\u0646\u062f\u06d5\u0648\u06d5\u060c \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0647\u0648\u0645\u06ce\u062f\u06cc \u0645\u0644 \u0644\u06d5 \u0646\u0627\u0633\u0648\u067e\u0627\u0633\u06cc \u062f\u06d5\u0646\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u067e\u0627\u0634\u06cc \u0644\u06ce \u0642\u06d5\u0648\u0645\u0627\u0646\u06cc\u0634 \u0634\u0627\u062f\u0645\u0627\u0646\u06cc\u0645\u0627\u0646 \u067e\u06ce \u062a\u0627\u0645\u0627\u0646\u062f\u060c \u0647\u06d5\u06b5\u0628\u06d5\u062a \u0626\u06ce\u0698\u06ce: \u0642\u06c6\u0631\u062a\u06d5\u06a9\u0627\u0646\u0645 \u0644\u06ce \u0695\u06d5\u0648\u06cc\u0648\u0646. \u0628\u06d5 \u06a9\u06d5\u06cc\u0641\u06d5 \u0648 \u0628\u06d5 \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u062f\u06d5\u06b5\u06ce.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646\u061b \u0628\u06d5\u0634\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u0648\u0648\u0631\u062f\u0646 \u0648 \u067e\u0627\u062f\u0627\u0634\u06cc \u0647\u06d5\u0631\u06d5 \u0645\u06d5\u0632\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0648\u06ce\u062f\u06d5\u0686\u06ce \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646 - \u06a9\u06d5 \u0628\u06c6 \u062a\u06c6 \u062f\u06ce - \u062e\u06c6 \u0628\u0628\u0648\u06ce\u0631\u06cc \u0648 \u0628\u0628\u06ce\u062a\u06d5 \u0645\u0627\u06cc\u06d5\u06cc \u062f\u06b5\u062a\u06d5\u0646\u06af\u06cc\u062a\u060c \u06a9\u06d5 \u067e\u06ce\u062a \u0626\u06ce\u0698\u0646: \u0686\u0645\u0627 \u06af\u06d5\u0646\u062c\u06ce \u0644\u06d5 \u0628\u0627\u06b5\u0627\u0648\u06d5 \u0628\u06c6\u06cc \u0646\u06d5\u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u061b \u06cc\u0627\u0646 \u0644\u0627\u0646\u06cc \u06a9\u06d5\u0645\u060c \u0641\u0631\u0634\u062a\u06ce\u06a9\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0646\u06d5\u0647\u0627\u062a\u061f \u062a\u06c6 \u062a\u06d5\u0646\u06cc\u0627 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06ce\u06a9\u06cc \u0648 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0686\u0627\u0648\u062f\u06ce\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06d5.',
    '\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0647\u06d5\u06b5\u06cc\u0628\u06d5\u0633\u062a\u06af\u06d5. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0642\u0633\u06d5\u0648 \u0695\u0627\u0633\u062a\u06d5\u060c \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062f\u06d5\u0633\u062a\u0648\u0648 \u062f\u06d5\u06a9\u06d5\u0648\u06ce - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u062f\u06d5\u0633\u0648\u0648\u0695\u06d5\u062a\u06cc \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u06d5 \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0626\u06d5\u0645 \u0628\u06ce\u0646\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u067e\u06d5\u0631\u0633\u06a4\u062a\u0627\u0646 \u062f\u0627\u0645\u0627\u0646\u060c \u0633\u0627 \u0628\u0632\u0627\u0646\u0646 \u0642\u0648\u0631\u0639\u0627\u0646 \u0644\u06d5 \u0632\u0627\u0646\u0633\u062a\u06cc \u062e\u0648\u0627\u0648\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0647\u06cc\u0686 \u0634\u062a\u06cc\u0634 \u0634\u06cc\u0627\u0648\u06cc \u067e\u06d5\u0631\u0633\u062a\u0646\u06ce \u0646\u06cc\u06cc\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0647\u06d5\u0631 \u062e\u06c6\u06cc. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u0633\u06d5\u0644\u0645\u06ce\u0646\u0646\u061f',
    '\u0647\u06d5\u0631\u06a9\u06ce \u0698\u06cc\u0646\u06cc \u0626\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0648 \u0632\u06d5\u0631\u0642 \u0648 \u0628\u06d5\u0631\u0642\u06cc \u0626\u06d5\u0648\u06cc \u062f\u06d5\u0648\u06ce\u060c \u0626\u06ce\u0645\u06d5 \u0645\u0632\u06d5\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 - \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u0647\u06d5\u0631 \u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06ce \u0648 \u0686\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u06a9\u06d5\u0645\u06d5\u0648\u06d5 \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0646 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u060c \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u0627\u06af\u0631\u060c \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u06a9\u0631\u062f\u06cc\u0627\u0646 \u0628\u06d5\u0641\u06ce\u0695\u06c6 \u0686\u0648\u0648.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u06d5\u0648\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0695\u0648\u0648\u0646\u06cc \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5 \u0648 \u0634\u0627\u06cc\u06d5\u062a\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06d5 \u0634\u0648\u06ce\u0646\u06cc\u0627 \u0647\u0627\u062a\u0648\u0648\u06d5 \u0648 \u06a9\u062a\u06ce\u0628\u06cc \u0645\u0648\u0648\u0633\u0627\u0634 - \u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5\u0648 \u067e\u06ce\u0634\u06d5\u0648\u0627 \u0648 \u062c\u06ce \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0628\u0648\u0648 - \u0628\u0627\u0633\u06cc \u062f\u06d5\u06a9\u0627\u060c [\u0648\u06d5\u0644 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06ce \u0628\u06d5\u06b5\u06af\u06d5\u0646 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u0646\u061f \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5] \u0628\u06d5\u0648 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0644\u06d5\u0648 \u06a9\u06c6\u0645\u06d5\u06b5\u0627\u0646\u06d5 \u062d\u0627\u0634\u0627\u06cc \u0644\u06ce \u0628\u06a9\u0627\u060c \u062c\u06ce\u06cc \u0644\u06d5 \u0626\u0627\u06af\u0631 \u062e\u06c6\u0634 \u06a9\u0631\u0627\u0648\u06d5. \u0633\u0627 \u062a\u06c6 \u0644\u06ce\u06cc \u0628\u06d5\u06af\u0648\u0645\u0627\u0646 \u0645\u06d5\u0628\u06d5\u060c \u0626\u06d5\u0645\u06d5 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0695\u0627\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0646.',
    '\u06a9\u06ce \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5 \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u0627 \u0647\u06d5\u06b5 \u0628\u0628\u06d5\u0628\u06d5\u0633\u062a\u06ce\u061f \u06af\u0634 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0695\u0627\u062f\u06d5\u0646\u06ce\u0646 \u0648 \u0634\u0627\u06cc\u06d5\u062a\u0627\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u06d5\u062f\u06d5\u0646 \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06d5 \u062f\u06d5\u0645 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0626\u06d5\u0648 \u062f\u0631\u06c6\u06cc\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a. \u062f\u06d5\u0628\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0648\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5\u0646 \u0695\u06ce\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u0627 \u062f\u06d5\u0628\u06d5\u0633\u062a\u0646 \u0648 \u0645\u06d5\u0631\u062f\u0645\u06cc \u067e\u06ce \u0644\u0627\u0695\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u0628\u06ce \u0628\u0695\u0648\u0627\u0646.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0695\u0627\u06a9\u06d5\u0646\u061b \u0644\u06d5 \u062e\u0648\u062f\u0627\u0634 \u067e\u06ce\u0648\u06d5 \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06c6\u0633\u062a \u062f\u06d5\u0633\u062a \u0646\u0627\u06a9\u06d5\u0648\u06ce. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u062f\u0648\u0648\u0633\u06d5\u0631\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u062a\u0648\u0627\u0646\u06cc\u0648\u06cc\u0627\u0646\u06d5 \u0628\u0628\u06cc\u0633\u0646\u060c \u0646\u06d5 \u062a\u0648\u0627\u0646\u06cc\u0648\u06cc\u0627\u0646\u06d5 \u0628\u0628\u06cc\u0646\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06c6\u0695\u0627\u0646\u062f\u061b \u0647\u06d5\u0631 \u062f\u0631\u06c6\u06cc\u06d5\u06a9\u06cc\u0634 \u0647\u06d5\u06b5\u06cc\u0627\u0646 \u0628\u06d5\u0633\u062a\u060c \u0628\u06d5\u0641\u06ce\u0695\u06c6 \u0686\u0648\u0648.',
    '\u0647\u06cc\u0686 \u06af\u0648\u0645\u0627\u0646 \u0644\u06d5\u0648\u06d5\u062f\u0627 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633 \u062f\u06c6\u0695\u0627\u0648\u062a\u0631\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646 \u0648 \u062f\u06b5\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u062f\u0627\u0633\u06d5\u06a9\u0646\u0627\u0648\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u062f\u06d5\u0686\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0648 \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5\u06cc \u062a\u06ce\u062f\u0627 \u062f\u06d5\u0628\u0646.',
    '\u0646\u06d5\u0632\u06cc\u0644\u06d5\u06cc \u0626\u06d5\u0645 \u062f\u0648\u0648 \u062a\u0627\u0642\u0645\u06d5 \u0648\u06d5\u06a9 \u0646\u06d5\u0632\u06cc\u0644\u06d5\u06cc \u06a9\u0648\u06ce\u0631 \u0648 \u06a9\u06d5\u0695 \u0648 \u0686\u0627\u0648\u0633\u0627\u063a \u0648 \u0698\u0646\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc\u06d5\u061b \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0686\u0648\u0646 \u06cc\u06d5\u06a9\u0646\u061f \u062f\u06d5\u06cc \u0628\u06c6\u0686\u06cc \u067e\u06d5\u0646\u062f \u0648\u06d5\u0631\u0646\u0627\u06af\u0631\u0646\u061f',
    '\u0646\u0648\u0648\u062d\u0645\u0627\u0646 \u0634\u0627\u0646\u062f\u06d5 \u0644\u0627\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc\u060c \u06a9\u06d5: \u0645\u0646 \u0628\u06c6\u062a\u0627\u0646 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645.',
    '\u062c\u06af\u06d5 \u0644\u06d5 \u062e\u0648\u0627 \u0645\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646\u0645 \u0646\u06d5\u0648\u06d5\u06a9 \u0695\u06c6\u0698\u06ce \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u0648\u0648 \u062a\u0648\u0648\u0634 \u0628\u06ce.',
    '\u06af\u0631\u06d5\u06af\u0631\u06d5\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc - \u06a9\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646 - \u06af\u0648\u062a\u06cc\u0627\u0646: \u0648\u06d5\u06a9 \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u062a \u0628\u06cc\u0646\u06cc\u0646\u060c \u062a\u06c6\u0634 \u0645\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u0648\u06d5\u06a9 \u062e\u06c6\u0645\u0627\u0646\u061b \u0626\u06d5\u0645\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0634\u0648\u06ce\u0646 \u062a\u06c6 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u060c \u0695\u0648\u0648\u062a\u06d5\u0648\u067e\u0648\u0648\u062a\u06d5\u06cc \u0628\u06ce \u0626\u0627\u0648\u06d5\u0632\u0646\u061b \u0646\u06d5\u0634\u0645\u0627\u0646 \u062f\u06cc\u0648\u06d5 \u0634\u062a\u06ce\u06a9\u062a\u0627\u0646 \u0644\u06d5\u0645\u06d5 \u0632\u06cc\u0627\u062a\u0631 \u0628\u06ce\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u0645\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u062f\u0631\u06c6 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u0698\u0645 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0628\u06d5 \u0645\u0646\u062f\u0627 \u0647\u0627\u062a \u0648 \u062e\u0648\u06ce\u0646\u062f\u0648\u0645\u06cc\u06d5\u0648\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0645 \u0644\u06d5\u0644\u0627\u06cc\u06d5 \u0648 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u06af\u0627\u062a\u0627\u0646 \u0644\u06ce \u0646\u06cc\u06cc\u06d5\u060c \u0686\u06c6\u0646 \u062f\u06d5\u0628\u06cc\u0646\u0646\u061f \u0626\u0627\u062e\u06c6 \u06af\u06d5\u0631 \u062e\u06c6\u062a\u0627\u0646 \u062d\u06d5\u0632 \u0646\u06d5\u06a9\u06d5\u0646\u060c \u062f\u06d5\u06a9\u0631\u06ce \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646\u062f\u0627 \u0628\u0633\u06d5\u067e\u06ce\u0646\u06cc\u0646\u061f',
    '\u06af\u06d5\u0644\u06c6! \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0634 \u0628\u0632\u0627\u0646\u0646 \u0647\u06cc\u0686 \u0645\u0632\u0645 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0648\u06ce\u061b \u067e\u0627\u062f\u0627\u0634\u06cc \u0645\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u062f\u06d5\u0631\u06cc\u0627\u0646 \u0646\u0627\u06a9\u06d5\u0645\u061b \u0628\u06ce \u0633\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06cc\u062f\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u0646. \u0628\u06d5\u06b5\u0627\u0645 \u0645\u0646 \u062f\u06d5\u0628\u06cc\u0646\u0645 \u0626\u06ce\u0648\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u0628\u06ce \u0626\u0627\u0648\u06d5\u0632\u0646.',
    '\u06af\u06d5\u0644\u06c6! \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0646 \u0644\u06d5\u0644\u0627\u06cc \u062e\u06c6\u0645\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0628\u06a9\u06d5\u0645\u060c \u06a9\u06ce \u0644\u06d5 \u062e\u0648\u0627 \u0695\u0632\u06af\u0627\u0631\u0645 \u062f\u06d5\u06a9\u0627\u061f \u0626\u0627\u062e\u06c6 \u0647\u06cc\u0686 \u0628\u06cc\u0631 \u0646\u0627\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061f',
    '\u067e\u06ce\u0634\u0648\u0648 \u0646\u0627\u06b5\u06ce\u0645 \u0645\u0646 \u06af\u06d5\u0646\u062c\u06cc\u0646\u06d5\u062f\u0627\u0631\u06cc \u062e\u0648\u062f\u0627\u0645\u061b \u0628\u06d5 \u067e\u06cc\u0648\u0627\u0631\u0627\u0646\u06cc\u0634 \u0646\u0627\u0632\u0627\u0646\u0645\u061b \u0646\u0627\u0634 \u0628\u06ce\u0698\u0645 \u0641\u0631\u0634\u062a\u06d5\u06cc\u06ce\u06a9\u0645\u061b \u0646\u0627\u0634\u06b5\u06ce\u0645 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0633\u0648\u0648\u06a9\u06cc \u0644\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u0695\u0648\u0627\u0646\u0646\u060c \u062e\u0648\u0627 \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0646\u0627\u06a9\u0627. \u062e\u0648\u0627 \u0686\u0627\u06a9\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06cc\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0648\u0627\u0628\u0645\u060c \u0626\u06d5\u0648\u0633\u0627 \u06cc\u06d5\u06a9 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u0645.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u0646\u0648\u0648\u062d! \u062f\u06d5\u0645 \u0628\u06d5\u062f\u06d5\u0645\u06d5\u062a \u0648\u06d5\u0644 \u06a9\u0631\u062f\u06cc\u0646 \u0648 \u062c\u0648\u0627\u0628\u06d5\u062c\u06d5\u0646\u06af\u06cc\u062a \u0641\u0631\u06d5\u06cc \u062e\u0627\u06cc\u0627\u0646\u062f\u061b \u06af\u06d5\u0631 \u0695\u0627\u0633\u062a \u0626\u06d5\u06a9\u06d5\u06cc\u060c \u0647\u06d5\u0631\u0686\u06cc \u0626\u06ce\u0698\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646 \u062f\u06ce\u060c \u067e\u06ce\u0646 \u0628\u0646\u0648\u06ce\u0646\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u062e\u0648\u062f\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632 \u06a9\u0627\u060c \u067e\u06ce\u0648 \u062f\u06d5\u0646\u0648\u06ce\u0646\u06ce \u0648 \u0646\u0627\u06a9\u0631\u06ce \u0644\u06d5\u0628\u06d5\u0631\u06cc \u0628\u0695\u06d5\u0648\u0646.',
    '\u0647\u06d5\u0631 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0645 \u0628\u06d5\u0647\u0631\u06d5 \u0646\u0627\u062f\u0627\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u0695\u06cc\u0627\u0631 \u0628\u062f\u0627 \u06af\u0648\u0645\u0695\u0627\u0648 \u0628\u06a9\u0627. \u062e\u06c6\u06cc \u062e\u0648\u062f\u0627\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u062f\u06d5\u0686\u0646\u06d5\u0648\u06d5.',
    '\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce\u0698\u0646: \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u06cc\u0628\u06d5\u0633\u062a. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0631 \u0647\u06d5\u06b5\u0645\u0628\u06d5\u0633\u062a\u0648\u0648\u06d5\u060c \u062a\u0627\u0648\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u062e\u06c6\u0645\u06d5\u061b \u0645\u0646\u06cc\u0634 \u0644\u06d5 \u062a\u0627\u0648\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0631\u06a9\u06d5\u0646\u0627\u0631\u0645.',
    '\u0628\u06d5 \u0646\u0648\u0648\u062d \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0631\u0627\u0628\u0648\u0648\u060c \u06a9\u06d5: \u0644\u06d5 \u0646\u0627\u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062a\u06c6\u062f\u0627\u060c \u062c\u06af\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062a\u0627 \u0626\u06ce\u0633\u062a\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u0628\u0695\u0648\u0627\u0646\u0627\u0647\u06ce\u0646\u06ce \u0648 \u0644\u06d5\u0648 \u0626\u0627\u06a9\u0627\u0631\u06d5\u06cc \u06a9\u06d5 \u0647\u06d5\u06cc\u0627\u0646\u06d5 \u063a\u06d5\u0645\u06af\u06cc\u0646 \u0645\u06d5\u0628\u06d5.',
    '\u0644\u06d5 \u0698\u06ce\u0631 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06cc \u0626\u06ce\u0645\u06d5 \u0648 \u0647\u0627\u0646\u0627\u06cc \u0626\u06ce\u0645\u06d5\u060c \u06af\u06d5\u0645\u06cc\u06d5 \u0633\u0627\u0632 \u06a9\u06d5 \u0648 \u0644\u06d5\u0645\u06d5\u0695 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06d5\u0645\u062f\u0648\u06ce\u0646\u06d5 \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u0628\u0648\u0648\u0646\u060c \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0628\u06ce \u0698\u06ce\u0631 \u0626\u0627\u0648 \u06a9\u06d5\u0648\u0646.',
    '\u062e\u06d5\u0631\u06cc\u06a9\u06cc \u06af\u06d5\u0645\u06cc\u06d5 \u0633\u0627\u0632\u06a9\u0631\u062f\u0646 \u0628\u0648\u0648\u061b \u0647\u06d5\u0631 \u0686\u06d5\u0646\u062f \u062e\u0627\u0648\u06d5\u0646 \u067e\u0627\u06cc\u06d5 \u0648 \u0645\u0627\u06cc\u06d5 \u0644\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0628\u06d5\u0644\u0627\u06cc\u062f\u0627 \u0695\u0627\u0628\u0628\u0631\u062f\u0646\u0627\u06cc\u06d5\u060c \u062d\u06d5\u0646\u06d5\u06a9\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u06a9\u0631\u062f. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u06af\u0627\u06b5\u062a\u06d5\u0645\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5 \u06af\u0627\u06b5\u062a\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0626\u06ce\u0645\u06d5\u0634 \u06af\u0627\u06b5\u062a\u06d5\u062a\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0644\u06d5\u0645\u06d5 \u0628\u06d5\u0648 \u0644\u0627\u0648\u06d5 \u062a\u06ce \u062f\u06d5\u06af\u06d5\u0646\u060c \u06a9\u06ce \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u0648\u0627 \u062f\u06ce \u0626\u0627\u0628\u0695\u0648\u0648\u06cc \u0628\u0686\u06ce \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0644\u06d5 \u0628\u0646\u06d5\u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0628\u06ce.',
    '\u062a\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u06a9\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u06af\u06d5\u06cc\u06cc \u0648 \u062a\u06d5\u0646\u062f\u0648\u0648\u0631 \u0628\u06d5 \u0646\u0647\u06ce\u0645 \u0632\u0627\u06cc\u06d5\u0648\u06d5\u061b \u06af\u0648\u062a\u0645\u0627\u0646: \u0644\u06d5 \u06af\u0634 \u0646\u06ce\u0631 \u0648 \u0645\u06ce\u0648\u06ce \u062f\u0648\u0648\u0627\u0646 \u0647\u06d5\u06b5\u06af\u0631\u06d5\u061b \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u062a \u0648 \u0647\u06d5\u0631\u06a9\u06ce \u0628\u0627\u0648\u06d5\u0695\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u0628\u06d5\u0631\u06ce \u0628\u0695\u06cc\u0627\u0631\u06cc \u0628\u06c6 \u062f\u0631\u0627\u0648\u06d5\u061b \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646\u060c \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0645\u06cc\u0627\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0644\u06d5 \u0646\u0627\u0648\u06cc\u062f\u0627 \u0633\u0648\u0627\u0631 \u0628\u0646! \u0695\u06d5\u0648\u0627\u0646\u06d5\u0628\u0648\u0648\u0646\u06cc \u0648 \u0644\u06d5\u0646\u06af\u06d5\u0631\u06cc\u060c \u0628\u06d5 \u0646\u0627\u0648 \u062e\u0648\u0627\u06cc\u06d5\u061b \u062e\u0648\u062f\u0627\u0646\u06cc \u0645\u0646 \u0628\u06d5\u0628\u0648\u0648\u0631\u06cc\u0646\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u06af\u06d5\u0645\u06cc\u06d5\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06cc \u062f\u06d5\u0628\u0631\u062f\u060c \u0628\u06d5 \u0646\u0627\u0648 \u067e\u06ce\u0644\u06cc \u0648\u0627\u062f\u0627 \u062f\u06d5\u0686\u0648\u0648 \u0648\u06d5\u06a9 \u0686\u06cc\u0627\u06cc\u0627\u0646. \u0646\u0648\u0648\u062d \u06af\u0627\u0633\u06cc \u06a9\u0631\u062f\u06d5 \u06a9\u0648\u0695\u06ce \u062e\u06c6 - \u06a9\u06d5 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u0686\u06d5\u067e\u06d5\u06a9 \u0628\u0648\u0648 -: \u06a9\u0648\u0695\u06cc\u0698\u06af\u06d5\u06a9\u06d5\u0645! \u0648\u06d5\u0644 \u0626\u06ce\u0645\u06d5\u062f\u0627 \u0633\u0648\u0627\u0631 \u0628\u0628\u06d5 \u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0645\u06d5\u0628\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0646 \u067e\u06d5\u0646\u0627 \u0628\u06d5 \u0686\u06cc\u0627\u06cc\u06d5\u06a9 \u062f\u06d5\u0628\u06d5\u0645 \u06a9\u06d5 \u0644\u06d5 \u0626\u0627\u0648\u0645 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u06ce. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0695\u06c6 \u0647\u06cc\u0686 \u067e\u0627\u0631\u0627\u0632\u062a\u0646 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0646\u06cc\u06cc\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u06cc \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06ce\u062f\u0627 \u0628\u06ce\u062a\u06d5\u0648\u06d5. \u0634\u06d5\u067e\u06c6\u0644 \u06a9\u06d5\u0648\u062a\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc\u0627\u0646 \u0648 \u062e\u0631\u0627\u06cc\u06d5 \u0695\u06ce\u0632\u06cc \u062e\u0646\u06a9\u0627\u0648\u0627\u0646.',
    '\u0626\u06d5\u0648\u062c\u0627 \u06af\u0648\u062a\u0631\u0627: \u0632\u06d5\u0645\u06cc\u0646! \u0626\u0627\u0648\u062a \u0647\u06d5\u06b5\u0642\u0648\u0695\u06ce\u0646\u06d5\u061b \u0628\u06d5\u0631\u0632\u0627\u06cc\u06cc! \u0628\u06d5\u0633 \u0628\u0628\u0627\u0631\u06ce\u0646\u06d5. \u0626\u0627\u0648 \u0695\u06c6\u0686\u0648\u0648 \u0628\u06d5 \u0646\u0627\u062e\u06cc \u0626\u0627\u062e\u0627 \u0648 \u06a9\u0627\u0631 \u0633\u06d5\u0631\u06cc \u06af\u0631\u062a\u061b \u0644\u06d5\u0633\u06d5\u0631 \u062c\u0648\u0648\u062f\u06cc \u0644\u06d5\u0646\u06af\u06d5\u0631\u06cc \u06af\u0631\u062a. \u0648\u062a\u0631\u0627: \u062a\u0627\u0631\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0646.',
    '\u0646\u0648\u0648\u062d\u06cc\u0634 \u0644\u06d5\u0628\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u067e\u0627\u0695\u0627\u06cc\u06d5\u0648\u06d5\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0645\u0646! \u06a9\u0648\u0695\u0645 \u06a9\u06d5\u0633\u0648\u06a9\u0627\u0631\u06cc \u0645\u0646\u06d5\u061b \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062a\u06c6\u0634 \u0647\u06d5\u0631 \u062f\u06ce\u062a\u06d5 \u062c\u06ce \u0648 \u062a\u06c6 \u0644\u06d5 \u06af\u0634 \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06d5\u06a9 \u0644\u06ce\u0632\u0627\u0646\u062a\u0631\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0646\u0648\u0648\u062d \u0626\u06d5\u0645 \u06a9\u0648\u0695\u06d5\u062a \u06a9\u06d5\u0633\u06cc \u062a\u06c6 \u0646\u06cc\u06cc\u06d5\u061b \u0626\u0627\u06a9\u0627\u0631\u06cc \u0646\u0627\u067e\u06d5\u0633\u0646\u062f\u06cc \u0647\u06d5\u0633\u061b \u0647\u06d5\u0631 \u0634\u062a\u06ce \u0686\u06cc \u0644\u06ce \u0646\u06d5\u0632\u0627\u0646\u06cc\u060c \u062f\u0627\u0648\u0627 \u0645\u06d5\u06a9\u06d5\u061b \u0645\u0646 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u062a \u0644\u06ce\u062f\u06d5\u06a9\u06d5\u0645 \u06a9\u06d5 \u0646\u06d5\u0686\u06cc\u06cc\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0646\u06d5\u0632\u0627\u0646\u0627\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u0645! \u0647\u06d5\u0631 \u062e\u06c6 \u0628\u06d5 \u062a\u06c6 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0645\u061b \u062f\u0627\u0648\u0627\u06cc \u0634\u062a\u06ce\u06a9\u062a \u0644\u06ce \u0628\u06a9\u06d5\u0645 \u06a9\u06d5 \u0646\u0627\u06cc\u0632\u0627\u0646\u0645\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u062a \u067e\u06ce\u0645\u062f\u0627 \u0646\u06d5\u06cc\u06d5 \u0648 \u0644\u06ce\u0645 \u0646\u06d5\u0628\u0648\u0648\u0631\u06cc\u060c \u06cc\u06d5\u06a9\u06ce\u06a9\u0645 \u0644\u06d5 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646.',
    '\u0648\u062a\u0631\u0627: \u0626\u06d5\u06cc \u0646\u0648\u0648\u062d! \u0628\u06d5 \u0633\u06b5\u0627\u0645\u06d5\u062a\u06cc \u0648 \u0628\u06d5\u0648 \u067e\u06cc\u062a\u06d5\u0648\u06d5 - \u06a9\u06d5 \u062f\u0627\u0648\u0645\u0627\u0646\u06d5 \u0628\u06d5 \u062a\u06c6 \u0648 \u0628\u06d5 \u0647\u0627\u0648\u0633\u06d5\u0641\u06d5\u0631\u0627\u0646\u062a - \u062f\u0627\u0628\u0648\u0648\u06d5\u0632\u06d5\u061b \u0686\u06cc\u0646 \u0648 \u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062f\u0648\u0627\u06cc\u06d5\u0634\u060c \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0645\u06d5\u0648\u062f\u0627\u06cc \u0698\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0626\u06d5\u062f\u06d5\u06cc\u0646\u06ce \u0648 \u067e\u0627\u0634\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06d5\u0648 \u0647\u06d5\u0648\u0627\u06b5\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631\u067e\u06ce\u0648\u0627\u0631\u0646 - \u06a9\u06d5 \u0628\u06d5 \u062a\u06c6\u06cc \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u06d5\u0646\u06cc\u0646 - \u0644\u06d5\u0645\u06d5\u0648\u0628\u06d5\u0631 \u0646\u06d5 \u062a\u06c6 \u062f\u06d5\u062a \u0632\u0627\u0646\u06cc\u0646 \u0646\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u062a. \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06d5\u060c \u062f\u0648\u0627\u0695\u06c6\u0698 \u0628\u06c6 \u062e\u0648\u062f\u06ce \u062a\u0631\u0633\u0627\u0646\u06d5.',
    '\u0628\u06c6 \u0644\u0627\u06cc \u0639\u0627\u062f\u06cc\u0634 \u0647\u0648\u0648\u062f\u0645\u0627\u0646 \u0646\u0627\u0631\u062f - \u06a9\u06d5 \u0628\u0631\u0627\u06cc\u0627\u0646 \u0628\u0648\u0648 - \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u067e\u06d5\u0631\u0633\u062a\u0646\u062f\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc \u062a\u0627\u06a9 \u0628\u0646\u061b \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0626\u06ce\u0648\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u061b \u0626\u06ce\u0648\u06d5 \u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0646\u06d5\u062e\u0634\u06ce\u0646\u0646.',
    '\u06af\u06d5\u0644\u06c6! \u0628\u06c6 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0645\u0632\u06ce\u06a9\u0645 \u0644\u06ce\u0648 \u0646\u0627\u0648\u06ce\u061b \u067e\u0627\u062f\u0627\u0634\u06cc \u0645\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0645\u0646\u06d5. \u0626\u0627\u062e\u06c6 \u0628\u06c6 \u062f\u06d5\u0628\u06ce \u062a\u06ce\u0646\u06d5\u06af\u06d5\u0646\u061f',
    '\u06af\u06d5\u0644\u06c6! \u062f\u0627\u0648\u0627\u06cc \u0644\u06ce \u0628\u0648\u0648\u0631\u062f\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u0628\u06a9\u06d5\u0646 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0644\u06d5\u0648 \u0628\u06d5\u0631\u0632\u06d5\u0648\u06d5 \u0628\u06d5 \u0695\u06ce\u0698\u0646\u06d5 \u0628\u0627\u0631\u0627\u0646\u062a\u0627\u0646 \u0628\u06c6 \u0628\u0646\u06ce\u0631\u06ce \u0648 \u067e\u06ce\u0632\u06ce \u062a\u0631 \u0628\u062f\u0627 \u0628\u06d5 \u0647\u06ce\u0632\u062a\u0627\u0646. \u0647\u06d5\u0631\u0648\u0627 \u0628\u06d5 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06cc \u0695\u0648\u0648 \u0648\u06d5\u0631\u0645\u06d5\u06af\u06ce\u0695\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u0647\u0648\u0648\u062f! \u062a\u06c6 \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u062a \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0628\u06c6 \u0646\u06d5\u0647\u06ce\u0646\u0627\u06cc\u0646\u061b \u0647\u06d5\u0631 \u0628\u06d5 \u0642\u0633\u06d5\u06cc \u062a\u06c6\u0634\u060c \u0645\u06d5 \u0644\u06d5 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u062f\u06d5\u0633 \u0628\u06d5\u0631\u0646\u0627\u062f\u06d5\u06cc\u0646\u061b \u0628\u0695\u0648\u0627\u06cc\u06d5\u0634 \u0628\u06d5 \u062a\u06c6 \u0646\u0627\u0647\u06ce\u0646\u06cc\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0626\u06ce\u0698\u06cc\u0646: \u0695\u06d5\u0646\u06af\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646\u060c \u062a\u0648\u0648\u0634\u06cc \u062a\u0648\u0648\u0634\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0628\u06cc. \u06af\u0648\u062a\u06cc: \u062e\u0648\u0627 \u0628\u06d5 \u0634\u0627\u06cc\u06d5\u062a \u062f\u06d5\u06af\u0631\u0645\u060c \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0634\u0627\u06cc\u06d5\u062a \u0628\u0646 \u0644\u06d5\u0648\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646 \u0628\u06d5\u0631\u06a9\u06d5\u0646\u0627\u0631\u0645\u061b',
    '\u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627. \u0633\u0627 \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0641\u06ce\u06b5\u0627\u0646\u0645 \u0644\u06ce \u0628\u062f\u06c6\u0632\u0646\u06d5\u0648\u06d5 \u0648 \u0645\u06d5\u0648\u062f\u0627\u0645 \u0645\u06d5\u062f\u06d5\u0646.',
    '\u0645\u0646 \u062e\u06c6\u0645 \u0633\u067e\u0627\u0631\u062f \u0628\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0645\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0627\u0646\u062f\u0648\u0648\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u0634 \u0626\u06d5\u0648\u06d5. \u0647\u06d5\u0631 \u0634\u062a\u06ce \u062f\u06d5\u062c\u0648\u0648\u06b5\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0698\u06cc \u0648 \u0698\u06cc\u0627\u0631\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0648 \u062f\u06cc\u0627\u0631\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0646\u06d5\u06cc\u06ce\u0646\u06d5 \u0695\u06d5\u062f\u0627\u06cc\u06d5\u0634\u060c \u0645\u0646 \u0628\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0695\u0627\u0633\u067e\u06ce\u0631\u0627\u0648\u0645 \u067e\u06ce\u0645 \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u061b \u062e\u0648\u062f\u0627\u0646\u06cc \u0645\u0646 \u0645\u06d5\u0631\u062f\u0645\u06ce \u062a\u0631 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u0627\u062f\u06d5\u0646\u06ce \u0648 \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646\u06cc\u0634\u06cc \u0644\u06ce \u0646\u0627\u062f\u06d5\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06d5.',
    '\u06a9\u0627\u062a\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0647\u0627\u062a - \u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5\u0648\u06d5 - \u0647\u0648\u0648\u062f \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648 \u0647\u0627\u0648\u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646\u060c \u0644\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0633\u06d5\u062e\u062a \u0646\u06d5\u062c\u0627\u062a\u0645\u0627\u0646 \u062f\u0627\u0646.',
    '\u0626\u06d5\u0648\u06d5\u0634 \u0639\u0627\u0634\u06cc\u0631\u06d5\u062a\u06cc \u0639\u0627\u062f \u0628\u0648\u0648\u060c \u06a9\u06d5 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0648 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u0644\u06d5\u0648\u06d5\u0632 \u0648 \u0644\u0627\u0633\u0627\u0631 \u0628\u0648\u0648 \u0634\u0648\u06ce\u0646\u06cc \u062f\u06d5\u06a9\u06d5\u0648\u062a\u0646.',
    '\u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0648 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u0633\u062a\u0627\u0646\u06d5\u0648\u06d5\u0634\u062f\u0627\u060c \u0646\u06d5\u0641\u0631\u06cc\u0646\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0634\u0648\u06ce\u0646\u06d5\u0648\u06d5. \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0646 \u06a9\u06d5 \u0647\u06c6\u0632\u06cc \u0639\u0627\u062f \u0644\u06d5\u0645\u06d5\u0695 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u062d\u0627\u0634\u0627 \u0628\u0648\u0648\u0646. \u0628\u0627 \u0628\u062a\u0627\u0631\u06ce\u0646 \u0639\u0627\u062f\u06d5\u06a9\u0627\u0646 \u06a9\u06d5 \u0647\u06c6\u0632\u06cc \u0647\u0648\u0648\u062f \u0628\u0648\u0648\u0646.',
    '\u0628\u06c6 \u0644\u0627\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f\u06cc\u0634 \u0628\u0631\u0627\u06cc\u0627\u0646 \u0633\u0627\u06b5\u062d\u0645\u0627\u0646 \u0646\u0627\u0631\u062f. \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc \u062a\u0627\u06a9 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06cc \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u061b \u0626\u06d5\u0648 \u0644\u06d5\u0645 \u062e\u0627\u06a9\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0646 \u0648 \u0648\u06cc\u0633\u062a\u06cc \u0626\u0627\u0648\u06d5\u062f\u0627\u0646\u06cc \u0628\u06a9\u06d5\u0646\u061b \u0644\u06ce\u0628\u0648\u0648\u0631\u062f\u0646\u06cc \u0644\u06ce \u0628\u062e\u0648\u0627\u0632\u0646\u061b \u067e\u0627\u0634\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u0648\u06d5\u06af\u06d5\u0695\u06ce\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u0646\u0632\u06cc\u06a9\u06d5 \u0648 \u062f\u06d5\u0641\u0631\u06cc\u0627\u0648\u062f\u06ce.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0633\u0627\u06b5\u062d! \u0628\u06d5\u0631\u0644\u06d5 \u0626\u06ce\u0633\u062a\u06ce \u062c\u06ce\u06af\u06d5\u06cc \u0647\u0648\u0645\u06ce\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0648\u0648\u06cc\u061b \u0626\u0627\u062e\u06c6 \u0695\u06ce \u0646\u0627\u062f\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0645\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u060c \u0628\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646\u061f \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u062a\u06c6 \u0626\u06ce\u0645\u06d5\u06cc \u0628\u06c6 \u0628\u0627\u0646\u06af \u062f\u06d5\u06a9\u06d5\u06cc\u060c \u0628\u06d5\u06af\u0648\u0645\u0627\u0646\u06cc\u0646 \u0648 \u062f\u0648\u0648\u062f\u06b5\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0626\u06d5\u0631 \u0645\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0695\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645 \u0647\u06ce\u0646\u0627\u0628\u06ce \u0648 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0648\u062a\u0628\u0645\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062f\u06d5\u0631\u0686\u0645\u060c \u06a9\u06ce \u0647\u06d5\u06cc\u06d5 \u0628\u06d5 \u0641\u0631\u06cc\u0627\u0645 \u0628\u06af\u0627\u061f \u0644\u06d5\u0646\u06af\u06c6\u0634 \u0647\u06cc\u0686\u0645 \u0646\u0627\u06cc\u06d5\u062a\u06d5\u0628\u06d5\u0631 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0632\u06d5\u0631\u06d5\u0631.',
    '\u06af\u06d5\u0644\u06c6! \u0626\u06d5\u0645\u06d5 \u0648\u0634\u062a\u0631 \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u061b \u0628\u06d5\u0695\u06d5\u06b5\u062f\u0627\u06cc \u06a9\u06d5\u0646 \u0644\u06d5 \u0632\u06d5\u0648\u06cc \u062e\u0648\u062f\u0627 \u0628\u0686\u06d5\u0631\u06ce \u0648 \u0628\u06d5 \u062e\u0631\u0627\u067e \u062f\u06d5\u0633\u062a\u06cc \u0628\u06c6 \u0645\u06d5\u0628\u06d5\u0646\u061b \u062f\u06d5\u0646\u0627 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0646\u0632\u06cc\u06a9 \u062f\u06d5\u062a\u0627\u0646\u06af\u0631\u06ce.',
    '\u06a9\u06d5\u0686\u06cc \u0644\u06d5\u062a \u0648 \u067e\u06d5\u062a\u06cc\u0634\u06cc\u0627\u0646 \u06a9\u0631\u062f. \u06af\u0648\u062a\u06cc: \u0633\u06ce \u0695\u06c6\u0698 \u0644\u06d5 \u0645\u0627\u06b5 \u062e\u06c6\u062a\u0627\u0646\u0627 \u0695\u0627\u06cc\u0628\u0648\u06ce\u0631\u0646\u061b \u0626\u06d5\u0645\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc [\u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648] \u0628\u06ce \u062f\u0631\u06c6\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0647\u0627\u062a\u060c \u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5\u0648\u06d5 \u0633\u0627\u06b5\u062d \u0648 \u0626\u06d5\u0648 \u0686\u06d5\u0646\u062f \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u0627 \u0647\u0627\u0648\u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646\u060c \u0644\u06d5 \u0695\u06cc\u0633\u0648\u0627 \u0628\u0648\u0648\u0646\u06cc \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0646\u06d5\u062c\u0627\u062a\u0645\u0627\u0646 \u062f\u0627\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u062e\u06ce\u0648\u06cc \u0647\u06ce\u0632 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0646\u0627\u0631\u06d5\u062a\u06d5\u06cc\u06ce \u0648\u0627\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u060c \u0644\u06d5 \u0646\u0627\u0648 \u0645\u0627\u06b5\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u062f\u06d5\u0645\u06d5\u0648\u0695\u0648\u0648 \u0647\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0695\u0627\u061b',
    '\u062f\u06d5\u062a\u06af\u0648\u062a \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u0646\u06d5\u0698\u06cc\u0627\u0648\u0646. \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0647\u06c6\u0632\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc [\u0645\u06d5\u0632\u0646\u06cc\u0627\u0646] \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u0647\u06d5\u06cc \u0644\u06d5\u0648\u06d5\u0634 \u0628\u06d5\u0648\u0644\u0627\u0648\u06d5\u062a\u0631 \u0686\u0646.',
    '\u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u0634 \u0645\u0698\u062f\u06d5\u06cc\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u061b \u06af\u0648\u062a\u06cc\u0627\u0646: \u0633\u06d5\u0644\u0627\u0645. \u0648\u062a\u06cc: \u0633\u06d5\u0644\u0627\u0645. \u0632\u06c6\u0631 \u067e\u06ce \u0646\u06d5\u0686\u0648\u0648 \u06af\u06c6\u0644\u06a9\u06ce\u06a9\u06cc \u0628\u0631\u06cc\u0627\u0646\u06cc \u0647\u0627\u0648\u0631\u062f.',
    '\u0648\u06d5\u062e\u062a\u06ce \u062f\u06cc\u062a\u06cc \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646\u06cc \u0628\u06c6 \u062f\u0631\u06ce\u0698 \u0646\u0627\u06a9\u06d5\u0646\u060c \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646\u06cc \u067e\u06ce \u0628\u06d5\u06b5\u0627\u062c\u06d5\u0648\u06cc \u0647\u0627\u062a \u0648 \u062a\u0631\u0633\u06cc \u0695\u06ce \u0646\u06cc\u0634\u062a. \u06af\u0648\u062a\u06cc\u0627\u0646: \u062a\u06c6 \u0626\u06d5\u0646\u062f\u06ce\u0634\u062a \u0646\u06d5\u0628\u06ce\u061b \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0647\u06c6\u0632\u06cc \u0644\u0648\u0648\u062a \u0628\u06d5\u0695\u06ce \u06a9\u0631\u0627\u0648\u06cc\u0646.',
    '\u0698\u0646\u06d5\u06a9\u06d5\u06cc - \u06a9\u06d5 \u0647\u06d5\u06b5\u0648\u06ce\u0633\u062a\u0627\u0628\u0648\u0648 - \u06a9\u06d5\u0646\u06cc \u0647\u0627\u062a\u06ce. \u0626\u06d5\u0648\u0633\u0627 \u0645\u0632\u06af\u06ce\u0646\u06cc\u0645\u0627\u0646 \u067e\u06ce \u062f\u0627 \u0628\u06d5 \u0626\u06cc\u0633\u062d\u0627\u0642 \u0648 \u0633\u06d5\u0631\u06d5\u0695\u0627\u06cc \u0626\u06cc\u0633\u062d\u0627\u0642 \u0628\u06d5 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628.',
    '\u0698\u0646\u06d5 \u06af\u0648\u062a\u06cc: \u0647\u0627\u06cc \u0644\u06d5 \u0645\u0646\u06ce! \u0626\u0627\u062e\u06c6 \u0645\u0646\u06cc \u067e\u06cc\u0631\u06ce\u0698\u0646 \u0645\u0646\u0627\u06b5\u0645 \u062f\u06d5\u0628\u06ce \u0648 \u0626\u06d5\u0645 \u0634\u0648\u0648\u06d5\u0634\u0645 \u067e\u06cc\u0631\u06d5\u0645\u06ce\u0631\u06d5\u061f \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0634\u062a\u06ce\u06a9\u06cc \u0633\u06d5\u06cc\u0631\u06d5!',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u062a \u067e\u06ce \u0633\u06d5\u06cc\u0631\u06d5\u061f \u062e\u06ce\u0632\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0645\u0627\u06b5\u06d5! \u0628\u06d5\u0632\u06d5 \u0648 \u067e\u06cc\u062a\u06cc \u062e\u0648\u062f\u0627\u062a\u0627\u0646 \u0646\u0633\u06cc\u0648 \u0628\u06ce. \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u06cc \u0648 \u067e\u06ce \u0647\u06d5\u06b5\u06af\u0648\u062a\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u06cc \u062f\u06d5\u0648\u06d5\u0634\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 - \u06a9\u06d5 \u062a\u0631\u0633\u06cc \u0634\u06a9\u0627 \u0648 \u0645\u0632\u06af\u06ce\u0646\u06cc\u06cc\u06d5\u06a9\u06d5\u0634\u06cc \u067e\u06ce \u06af\u06d5\u06cc\u0634\u062a - \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06c6\u0632\u06cc \u0644\u0648\u0648\u062a \u0633\u06a9\u0627\u06b5\u0627\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u06cc\u0646.',
    '\u06a9\u06d5 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0644\u06d5 \u0633\u06d5\u0631\u06d5\u062e\u06c6 \u0648 \u062f\u06b5\u0646\u06d5\u0631\u0645 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0628\u0648\u0648.',
    '\u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0644\u06d5\u0645\u06d5 \u0648\u0627\u0632 \u0628\u06ce\u0646\u06d5\u060c \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u062a \u0647\u0627\u062a\u06af\u06d5 \u0648 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u06cc \u0628\u06ce \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06ce.',
    '\u06a9\u0627\u062a\u06ce \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u06af\u06d5\u06cc\u0646\u06d5 \u0644\u0627\u06cc \u0644\u0648\u0648\u062a\u060c \u0628\u06d5 \u06af\u06d5\u06cc\u0634\u062a\u0646\u06cc\u0627\u0646 \u062f\u0698\u062f\u0627\u0645\u0627 \u0648 \u067e\u06ce\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u06d5 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0695\u06c6 \u06a9\u0627\u0631 \u062f\u0698\u0648\u0627\u0631\u06d5.',
    '\u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0628\u06d5 \u0647\u06d5\u06b5\u06d5\u062f\u0627\u0648\u0627\u0646 \u0644\u06ce\u06cc \u0648\u06d5\u06a9\u06c6 \u0628\u0648\u0648\u0646\u061b \u062f\u06d5\u067e\u06ce\u0634\u06cc\u0634\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0628\u06ce \u0646\u0627\u0645\u0648\u0648\u0633\u06cc \u0628\u0648\u0648. \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0626\u06d5\u0645 \u06a9\u06cc\u0698\u0627\u0646\u06d5\u0645 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u0646\u060c \u067e\u0627\u06a9\u0698\u062a\u0631\u0646\u061b \u062a\u0631\u0633\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0634\u06d5\u0631\u0645\u06d5\u0632\u0627\u0631\u06cc \u0645\u06cc\u0648\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0645 \u0645\u06d5\u06a9\u06d5\u0646. \u0626\u0627\u062e\u06c6 \u067e\u06cc\u0627\u06af\u06ce \u0626\u0627\u06a9\u0627\u0631\u0628\u0627\u0634\u062a\u0627\u0646 \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u06c6\u062a \u0626\u06d5\u0645\u06d5\u062a \u0632\u0627\u0646\u06cc\u0648\u06d5\u060c \u0647\u06cc\u0686 \u06a9\u0627\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06d5 \u06a9\u06cc\u0698\u06d5\u06a9\u0627\u0646\u06cc \u062a\u06c6 \u0646\u06cc\u06cc\u06d5\u061b \u062f\u06cc\u0627\u0631\u06d5 \u0628\u06c6 \u062e\u06c6\u0634\u062a \u062f\u06d5\u0632\u0627\u0646\u06cc \u0686\u06cc\u0645\u0627\u0646 \u062f\u06d5\u0648\u06ce\u061f',
    '\u06af\u0648\u062a\u06cc: \u0628\u0631\u06cc\u0627 \u0647\u06ce\u0632\u0645 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0634\u06a9\u0627\u06cc\u06d5\u061b \u06cc\u0627\u062e\u06c6 \u067e\u06d5\u0646\u0627\u0645 \u0628\u0628\u0631\u062f\u0627\u06cc\u06d5 \u0628\u06d5 \u062c\u06ce \u067e\u06d5\u0646\u0627\u06cc\u06d5\u06a9\u06cc \u062d\u06d5\u0633\u062a\u06d5\u0645.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u0644\u0648\u0648\u062a! \u0626\u06ce\u0645\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06cc \u062e\u0648\u062f\u0627\u062a\u06cc\u0646\u061b \u062f\u06d5\u0633\u062a\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5\u062a \u067e\u06ce \u0646\u0627\u06af\u0627. \u062a\u06c6 \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u0627\u06b5 \u0648 \u062e\u06ce\u0632\u0627\u0646\u062a - \u0644\u06d5 \u0686\u06d5\u0646\u062f \u0628\u06d5\u062a\u06b5\u06ce\u06a9\u06cc \u0634\u06d5\u0648\u06ce\u062f\u0627 - \u0628\u06a9\u06d5\u0648\u0646\u06d5 \u0695\u06ce\u061b \u06a9\u06d5\u0633\u062a\u0627\u0646 \u0626\u0627\u0648\u0695 \u0646\u06d5\u062f\u0627\u062a\u06d5\u0648\u06d5\u061b \u063a\u06d5\u06cc\u0631\u06cc \u0698\u0646\u062a\u060c \u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u062a\u0648\u0648\u0634\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06ce\u060c \u062a\u0648\u0648\u0634\u06cc \u0648\u06cc\u0634 \u062f\u06ce. \u0698\u0648\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06d5\u0631\u06cc \u0628\u06d5\u06cc\u0627\u0646\u06d5\u061b \u0626\u0627\u062e\u06c6 \u0628\u06d5\u06cc\u0627\u0646 \u0646\u0632\u06cc\u06a9 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u06a9\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u06af\u06d5\u06cc\u06cc\u0634\u062a\u060c \u0626\u06d5\u0648 \u062c\u06ce\u0645\u0627\u0646 \u0633\u06d5\u0631\u06d5\u0648\u0646\u062e\u0648\u0648\u0646 \u06a9\u0631\u062f. \u06af\u0632\u0645\u0648\u0648\u0644\u06d5\u0642\u0648\u0695\u06cc \u0628\u06d5\u0631\u062f\u0627\u0633\u0627\u0646 \u0628\u06d5 \u0628\u06ce \u0646\u0627\u0648\u0628\u0695 \u0628\u06c6 \u0628\u0627\u0631\u0627\u0646\u062f\u0646.',
    '\u0628\u06d5 \u062f\u0631\u0648\u0634\u0645\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0645\u0648\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u06a9\u0631\u0627\u0628\u0648\u0648\u0646. \u0628\u06d5\u06b5\u0627\u06cc \u0626\u0627\u0648\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u0647\u06cc\u0686 \u062f\u0648\u0648\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u0628\u06c6 \u0645\u06d5\u062f\u06cc\u06d5\u0646\u06cc\u0634 \u0634\u0648\u0639\u06d5\u06cc\u0628\u06cc \u0628\u0631\u0627\u06cc\u0627\u0646\u0645\u0627\u0646 [\u0646\u0627\u0631\u062f]. \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u062e\u0648\u062f\u0627\u06cc \u062a\u06d5\u0646\u06cc\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06cc \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u0644\u06d5 \u06a9\u06ce\u0634\u0627\u0646 \u0648 \u067e\u06ce\u0648\u0627\u0646\u06d5\u062f\u0627 \u06af\u0632\u06cc \u0645\u06d5\u06a9\u06d5\u0646. \u062e\u06c6\u0634 \u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u0648\u0648 \u062f\u06d5\u0628\u06cc\u0646\u0645 \u0648 \u0645\u0646 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u062a\u0631\u0633\u0645 \u0647\u06d5\u06cc\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u0627\u06af\u0631\u062a\u0627\u0646 \u0628\u06c6 \u0628\u06ce.',
    '\u06af\u06d5\u0644\u06c6! \u06a9\u06ce\u0634\u0627\u0646\u06d5 \u0648 \u067e\u06ce\u0648\u0627\u0646\u06d5 \u0648 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06ce\u061b \u0634\u062a\u06cc\u0634 \u06a9\u06d5\u0645 \u0645\u06d5\u062f\u06d5\u0646 \u0628\u06d5 \u062e\u06d5\u06b5\u06a9 \u0648 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc\u060c \u067e\u06ce \u062f\u0627\u0645\u06d5\u06af\u0631\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0628\u06ce\u060c \u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648 \u0628\u06c6\u0648 \u0628\u06ce\u06b5\u06ce\u062a\u06d5\u0648\u06d5 \u0628\u06c6\u062a\u0627\u0646 \u0686\u0627\u06a9\u06d5 \u0648 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06cc\u0648 \u0628\u06d5 \u0645\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0634\u0648\u0639\u06d5\u06cc\u0628! \u0645\u06d5\u0631 \u0646\u0648\u06ce\u0698\u06d5\u06a9\u06d5\u062a \u0647\u0627\u0646\u06d5\u062a \u062f\u06d5\u062f\u0627 \u0626\u06ce\u0645\u06d5 \u0648\u0627\u0632 \u0644\u06d5\u0648 \u0634\u062a\u06d5 \u0628\u06ce\u0646\u06cc\u0646 \u06a9\u06d5 \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u0627\u0646\u06cc \u0645\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u060c \u06cc\u0627\u0646 \u0648\u06d5\u06a9 \u062e\u06c6\u0645\u0627\u0646 \u062f\u06d5\u0645\u0627\u0646\u06d5\u0648\u06ce \u0644\u06d5 \u062f\u0627\u0631\u0627\u06cc\u06cc \u062e\u06c6\u0645\u0627\u0646\u062f\u0627 \u062f\u06d5\u0633\u06a9\u0627\u0631\u06cc \u0646\u06d5\u06a9\u06d5\u06cc\u0646\u061f \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u06a9\u06d5 \u062a\u06c6 \u067e\u06cc\u0627\u0648\u06ce\u06a9\u06cc \u0644\u06d5\u0633\u06d5\u0631\u06d5\u062e\u06c6 \u0648 \u0628\u06d5 \u0633\u06d5\u0644\u06cc\u0642\u06d5\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u0698\u0645 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06d5\u0647\u06ce\u0632\u0645 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0695\u0627 \u0644\u06d5\u0644\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0628\u0698\u06cc\u0648\u06cc \u0628\u0627\u0634\u06cc \u067e\u06ce\u062f\u0627\u0648\u0645\u060c \u0686\u06c6\u0646 \u062f\u06d5\u0628\u06cc\u0646\u0646\u061f \u0626\u06d5\u06af\u06d5\u0631 \u062f\u0698\u0648\u0648 \u062f\u06d5\u0648\u06ce\u0633\u062a\u0645 \u0648 \u0626\u06ce\u0698\u0645 \u0641\u06b5\u0627\u0646 \u06a9\u0627\u0631\u06d5 \u0645\u06d5\u06a9\u06d5\u0646\u060c \u062e\u06c6 \u0646\u06cc\u0627\u0632\u0645 \u0626\u06d5\u0648\u06d5 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u062e\u06c6\u0645 \u0628\u06cc\u06a9\u06d5\u0645. \u0646\u06cc\u0627\u0632\u06cc \u0645\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 - \u062a\u0627 \u062f\u06d5\u062a\u0648\u0627\u0646\u0645- [\u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u062a\u0627\u0646] \u0633\u0627\u0645\u0627\u0646 \u0628\u062f\u06d5\u0645. \u0628\u06d5 \u0626\u0627\u0648\u0627\u062a \u06af\u06d5\u06cc\u0634\u062a\u0646\u06cc\u0634\u0645 \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0698\u06cc\u0646\u06cc \u062e\u06c6\u0645 \u0628\u06d5\u0648 \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0645\u06d5\u0648\u06d5.',
    '\u06af\u06d5\u0644\u06c6! \u062f\u0698\u0648\u06ce\u0633\u062a\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062d\u0627\u0646\u062f\u06cc \u0645\u0646 \u062a\u0648\u0648\u0634\u06cc \u062a\u0627\u0648\u0627\u0646\u06ce\u06a9\u062a\u0627\u0646 \u0646\u06d5\u06a9\u0627 \u06a9\u06d5 \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u0627\u06cc \u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d \u0648 \u0647\u06c6\u0632\u06cc \u0647\u0648\u0648\u062f \u0648 \u0647\u06c6\u0632\u06cc \u0633\u0627\u06b5\u062d \u0628\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u0647\u0627\u062a\u060c \u0628\u06d5\u0633\u06d5\u0631\u0648\u0648 \u0628\u06ce. \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u06cc \u0647\u06c6\u0632\u06cc \u0644\u0648\u0648\u062a\u06cc\u0634 \u0644\u06d5\u0645\u06ce\u0698 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u062f\u0627\u0648\u0627 \u0628\u06a9\u06d5\u0646\u060c \u0644\u06ce\u0648 \u0628\u0628\u0648\u0648\u0631\u06ce\u061b \u067e\u0627\u0634\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u0648\u06d5\u06af\u06d5\u0695\u06ce\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u062f\u0644\u06c6\u06a4\u0627\u0646 \u0648 \u0645\u06cc\u0647\u0631\u06d5\u0648\u0627\u0646\u06d5.',
    '\u0648\u062a\u06cc\u0627\u0646: \u0634\u0648\u0639\u06d5\u06cc\u0628! \u0632\u06c6\u0631 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u062a\u06ce\u0646\u0627\u06af\u06d5\u06cc\u0646 \u06a9\u06d5 \u062a\u06c6 \u0626\u06ce\u0698\u06cc\u061b \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u0645\u0627\u0646 \u0628\u06ce \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u062a \u062f\u06d5\u0628\u06cc\u0646\u06cc\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0628\u06d5\u0631 \u062e\u0627\u062a\u0631\u06cc \u062e\u0632\u0645\u0627\u0646\u062a \u0646\u06d5\u0628\u0627\u060c \u0628\u06d5\u0631\u062f\u06d5\u0628\u0627\u0631\u0627\u0646\u0645\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u06cc\u061b \u062a\u06c6 \u062f\u06d5\u0631\u06c6\u0633\u062a\u06cc \u0626\u06ce\u0645\u06d5 \u0646\u0627\u06cc\u06d5\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0626\u0627\u062e\u06c6 \u062e\u0632\u0645\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646\u062a\u0627\u0646 \u067e\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u06af\u0631\u06cc\u0646\u06af\u062a\u0631\u06d5\u060c \u06a9\u06d5 \u062e\u0648\u062f\u0627\u062a\u0627\u0646 \u062e\u0633\u062a\u06c6\u062a\u06d5 \u067e\u0634\u062a \u06af\u0648\u06ce\u062a\u0627\u0646\u06d5\u0648\u06d5\u061f \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u062f\u06d5\u0648\u0631\u06d5\u06cc \u062f\u0627\u0648\u06d5.',
    '\u06af\u06d5\u0644\u06c6! \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648\u06d5\u06cc \u0644\u06d5 \u062f\u06d5\u0633\u062a\u0648\u0648 \u062f\u06ce \u0628\u06cc\u06a9\u06d5\u0646\u061b \u0645\u0646\u06cc\u0634 \u0648\u0627 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u06a9\u0627\u0631\u0645. \u0644\u06d5\u0648\u06d5 \u0628\u06d5\u0648\u0644\u0627\u0648\u06d5 \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06ce \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u0627\u0628\u0695\u0648\u0648\u0628\u06d5\u0631\u06d5\u06cc \u062f\u06ce\u062a\u06d5 \u0633\u06d5\u0631 \u0648 \u06a9\u06ce \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646 \u062f\u06d5\u0631\u062f\u06d5\u0686\u06ce. \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646\u061b \u0626\u06d5\u0648\u0627 \u0645\u0646\u06cc\u0634 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0645.',
    '\u06a9\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u06af\u06d5\u06cc\u06cc\u0634\u062a\u060c \u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5\u0648\u06d5 \u0634\u0648\u0639\u06d5\u06cc\u0628 \u0648 \u0647\u06d5\u0648\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06cc\u0645\u0627\u0646 \u0646\u06d5\u062c\u0627\u062a \u062f\u0627\u061b \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646\u06cc\u0634 \u0646\u06d5\u0639\u0631\u06d5\u062a\u06ce\u06a9 \u0648\u0627\u06cc \u062a\u0627\u0633\u0627\u0646\u062f\u0646 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u062f\u06d5\u0645\u06d5\u0648\u0695\u0648\u0648 \u067e\u0634\u0648\u0648\u06cc\u0627\u0646 \u0628\u0695\u0627\u061b',
    '\u062f\u06d5\u062a\u06af\u0648\u062a \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u0634\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u0646. \u0628\u0627 \u0628\u062a\u0627\u0631\u06ce\u0646 \u062e\u06d5\u06b5\u06a9\u06cc \u0645\u06d5\u062f\u06cc\u06d5\u0646\u061b \u0648\u06d5\u06a9\u0648\u0648 \u0647\u06c6\u0632\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f \u062a\u0627\u0631\u0627\u0646.',
    '\u0647\u06d5\u0631\u0648\u0627 \u0626\u06ce\u0645\u06d5 \u0645\u0648\u0648\u0633\u0627\u0645\u0627\u0646 \u0628\u06d5 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0648 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0646\u0627\u0631\u062f\u060c',
    '\u0628\u06d5\u0631\u06d5\u0648 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u067e\u06cc\u0627\u0648\u0627\u0646\u06cc \u062f\u06cc\u0648\u0627\u0646\u06cc. \u0626\u06d5\u0648\u06cc\u0634 \u0647\u06d5\u0631 \u06af\u0648\u06ce \u0628\u06d5\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0628\u0648\u0648\u0646\u061b \u06a9\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u06cc\u0634 \u062f\u0698\u06cc \u0695\u0627\u0633\u062a\u06cc \u0628\u0648\u0648.',
    '\u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0648\u06d5\u067e\u06ce\u0634 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u06d5\u0648\u06ce \u0648 \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u062f\u06d5\u0628\u0627\u062a\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631. \u0686\u06d5\u0646\u062f \u062e\u0631\u0627\u067e\u06d5 \u0626\u06d5\u0648 \u062c\u06ce\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u06cc\u0686\u0646\u06ce!',
    '\u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627 \u0648 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u062f\u0648\u0627\u06cc\u06cc\u0634\u060c \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc\u0627\u0646 \u0628\u06d5 \u0634\u0648\u06ce\u0646\u06d5\u0648\u06d5\u06cc\u06d5. \u0626\u0627\u06cc \u0644\u06d5\u0648 \u062e\u06d5\u06b5\u0627\u062a\u06d5 \u062f\u0632\u06ce\u0648\u06d5!',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0686\u06d5\u0646\u062f \u0647\u06d5\u0648\u0627\u06b5\u06ce\u06a9 \u0628\u0648\u0648\u0646 \u0644\u06d5\u0648 \u0634\u0627\u0631\u0627\u0646\u06d5 \u06a9\u06d5 \u0628\u06c6 \u062a\u06c6\u06cc \u062f\u06d5\u06af\u06ce\u0695\u06cc\u0646\u06d5\u0648\u06d5\u061b \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0647\u06d5\u0646 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5\u0628\u06d5\u06cc\u0646 \u0686\u0648\u0648\u06af\u0646.',
    '\u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0645\u0627\u0646 \u0644\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u06c6 \u06a9\u0631\u062f. \u06a9\u0627\u062a\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0695\u0627 \u0647\u0627\u062a\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06cc\u0627\u0646 \u0628\u0648\u0648\u0646 - \u062c\u06af\u06d5 \u0644\u06d5 \u062e\u0648\u0627 - \u0647\u06cc\u0686 \u06a9\u0627\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u06d5\u06a9\u0631\u062f\u0646 \u0648 - \u062c\u06cc\u0627 \u0644\u06d5 \u0632\u06cc\u0627\u0646 - \u0647\u06cc\u0686 \u0634\u062a\u06cc\u0627\u0646 \u0628\u06c6 \u0632\u06cc\u0627\u062f \u0646\u06d5\u06a9\u0631\u062f\u0646.',
    '\u0626\u0627\u0648\u0627\u06cc\u0646\u06d5\u06cc\u06d5 \u063a\u06d5\u0632\u06d5\u0628\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u060c \u06a9\u0627\u062a\u06ce \u0634\u0627\u0631\u0627\u0646\u06ce \u062f\u0627\u062f\u06d5\u06af\u0631\u06ce \u06a9\u06d5 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0646\u0627\u062d\u06d5\u0642\u0646. \u0626\u06d5\u0648 \u0626\u0627\u0632\u0627\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u062f\u0627\u060c \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646 \u0648 \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631\u06d5.',
    '\u0644\u06d5\u0645\u0627\u0646\u06d5\u062f\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0695\u06c6\u0698\u06cc \u067e\u06d5\u0633\u06b5\u0627\u0646\u06ce \u0628\u062a\u0631\u0633\u0646\u060c \u067e\u06d5\u0646\u062f\u06ce \u0647\u06d5\u06cc\u06d5\u061b \u06a9\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0645\u06d5\u0631\u062f\u0645 \u06af\u0634 \u0644\u06d5\u0648\u06ce \u06a9\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5\u061b \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u062f\u06cc\u0627\u0631 \u0648 \u0628\u06d5\u0631\u0686\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0648\u06d5\u062f\u0631\u06d5\u0646\u06af\u06cc \u0646\u0627\u062e\u06d5\u06cc\u0646\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06c6 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u06a9\u06d5\u0645.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u062f\u06ce\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0628\u06d5 \u0628\u06ce \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u062f\u0627 \u0645\u062a\u06d5\u0642 \u0646\u0627\u06a9\u0627\u061b \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0642\u06d5\u0631\u06d5\u0628\u06d5\u062e\u062a \u0648 \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631 \u0647\u06d5\u0646.',
    '\u0633\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0642\u06d5\u0631\u06d5\u0628\u06d5\u062e\u062a\u0646\u060c \u0644\u06d5 \u0626\u0627\u06af\u0631\u0627\u0646 \u0628\u06d5 \u0626\u0627\u062e \u0648 \u0647\u06d5\u0646\u06cc\u0633\u06a9 \u062f\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631 \u062f\u06d5\u0648\u06ce\u062f\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5\u060c \u0647\u06d5\u062a\u0627\u06a9\u0648\u0648 \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0647\u06d5\u0646\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u062f\u06af\u0627\u0631\u062a \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0631\u0686\u06cc \u062e\u06c6\u06cc \u0628\u06cc\u06d5\u0648\u06ce \u062f\u06d5\u06cc\u06a9\u0627.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u0646\u060c \u0647\u06d5\u0631 \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06ce\u062f\u0627 \u062f\u06d5\u0628\u0646\u060c \u0647\u06d5\u062a\u0627 \u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0647\u06d5\u0646\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce. \u0628\u06d5\u062e\u0634\u0634\u06ce\u06a9\u06d5 \u0646\u0627\u0628\u0695\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0633\u0627 \u062a\u06c6 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5\u0648\u06d5\u06cc \u0648\u0627\u0646 \u062f\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u062f\u0695\u062f\u06c6\u0646\u06af \u0645\u06d5\u0628\u06d5. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646 \u06a9\u06d5 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u06cc \u067e\u06ce\u0634\u0648\u0648\u06cc\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0627\u0646\u0646. \u0626\u06ce\u0645\u06d5\u0634 \u0628\u06d5 \u0628\u06ce \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc \u0628\u06d5\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06ce.',
    '\u0645\u06d5 \u06a9\u062a\u06ce\u0628\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0648 \u06a9\u06ce\u0634\u06d5\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u06b5\u0627\u06cc\u06cc\u0633\u0627. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0626\u06d5\u0648\u0633\u0627 \u0628\u0695\u06cc\u0627\u0631\u06cc \u0646\u06d5\u062f\u0627\u0628\u0627\u060c \u0632\u0648\u0648 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0695\u0627\u06cc\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0626\u06ce\u0633\u062a\u0627\u0634 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5\u0645\u060c \u0647\u06d5\u0631 \u062f\u0648\u0648\u062f\u06b5 \u0648 \u0628\u06d5\u06af\u0648\u0645\u0627\u0646\u0646.',
    '\u0628\u06ce\u06af\u0648\u0645\u0627\u0646\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0642\u06d5\u0631\u06d5\u0628\u0648\u0648\u06cc \u06a9\u0631\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u062f\u06d5\u062f\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0648\u06d5\u06a9 \u0641\u06d5\u0631\u0645\u0627\u0646\u062a \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5\u060c \u062e\u06c6\u0695\u0627\u06af\u0631\u06d5! \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5\u062a\u06d5\u06a9 \u062a\u06c6\u062f\u0627 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0648\u06d5\u06af\u06d5\u0695\u0627\u0648\u0646\u060c \u0628\u0627 \u0647\u06d5\u0631\u0648\u0627 \u0628\u0646\u061b \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc\u0634 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0646! \u0647\u06d5\u0631\u0686\u06cc \u0628\u06cc\u06a9\u06d5\u0646 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u0695\u0645\u0648\u0648\u062f\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06d5\u0628\u0646 \u063a\u06d5\u062f\u0631\u06ce \u062f\u06d5\u06a9\u06d5\u0646! \u062f\u06d5\u0646\u0627 \u0628\u06d5 \u0626\u0627\u06af\u0631 \u062f\u06d5\u0633\u0648\u0648\u062a\u06ce\u0646. \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0634 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u0648 \u0646\u0627\u06a9\u0627 \u0648 \u0626\u06cc\u062a\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u067e\u0627\u0634\u0627\u0646 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062a\u0627\u0646 \u0646\u0627\u062f\u0627.',
    '\u0644\u06d5 \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9 \u0626\u0627\u0644\u06cc \u0695\u06c6\u0698\u06ce\u062f\u0627 \u0648 \u0686\u06d5\u0646 \u0628\u06d5\u062a\u06b5\u06cc \u0633\u06d5\u0631\u06d5\u062a\u0627\u06cc \u0634\u06d5\u0648\u06ce\u060c \u0646\u06c6\u0698\u06ce \u0628\u06a9\u06d5\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0686\u0627\u06a9\u06d5 \u062e\u0631\u0627\u067e\u06d5\u06cc\u06d5 \u0644\u06d5\u0628\u06d5\u06cc\u0646 \u062f\u06d5\u0628\u06d5\u0646. \u0626\u06d5\u0645\u06d5 \u067e\u06d5\u0646\u062f\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06d5\u0646\u062f\u06af\u0631 \u0628\u0646.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u0628\u06af\u0631\u06d5\u061b \u0628\u06ce\u0633\u06c6 \u062e\u0648\u062f\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0648\u0646\u062f\u0627 \u0646\u0627\u06a9\u0627.',
    '\u0626\u06d5\u0645 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0686\u06cc\u0646\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631\u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0647\u0627\u062a\u0646 \u0648 \u0686\u0648\u0648\u0646\u060c \u0686\u06c6\u0646\u06d5 \u06a9\u06d5\u0633\u06ce \u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u0646\u060c \u0695\u06ce\u06af\u0627 \u0646\u06d5\u062f\u06d5\u0646 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u062e\u0631\u0627\u067e\u06d5 \u0628\u06d5\u0695\u06ce\u0648\u06d5 \u0628\u0686\u06ce\u061f \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0645\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0695\u0632\u06af\u0627\u0631\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646 \u0644\u06d5 \u0646\u06ce\u0648\u06cc\u0627\u0646\u0627. \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u06a9\u06d5\u0648\u062a\u0646\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u062e\u06c6\u0634\u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631 \u0628\u0648\u0648\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a - \u0628\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc - \u0686 \u062c\u0627\u0631 \u0642\u0695\u0627\u0646\u06cc \u0646\u06d5\u062e\u0633\u062a\u06d5 \u0626\u06d5\u0648 \u0634\u0627\u0631\u0627\u0646\u06d5\u06cc \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632\u06cc \u0628\u06a9\u0631\u062f\u0627\u06cc\u06d5\u060c \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u06cc \u06af\u0634\u062a\u06cc \u062f\u06d5\u06a9\u0631\u062f\u06d5 \u06cc\u06d5\u06a9 \u0626\u0627\u06cc\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5 \u0646\u0627\u062a\u06d5\u0628\u0627\u06cc\u06cc \u0647\u06d5\u0631 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5\u061b',
    '[\u062c\u0627] \u0645\u06d5\u06af\u06cc\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06ce\u06cc\u0627\u0646\u062f\u0627\u0628\u06ce\u062a\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u0648\u0648\u0646. \u0628\u0695\u06cc\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0634\u062a \u0647\u06d5\u0631 \u062f\u06ce\u062a\u06d5 \u062c\u06ce\u060c \u06a9\u06d5: \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u060c \u0644\u06d5 \u062c\u0646\u062f\u06c6\u06a9\u06d5 \u0648 \u0628\u0646\u06cc\u0627\u062f\u06d5\u0645 - \u0647\u06d5\u0645\u0648\u0648 - \u067e\u0695 \u062f\u06d5\u06a9\u06d5\u0645.',
    '\u0626\u06d5\u0645 \u062d\u06d5\u0645\u06a9\u06d5 \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u0627\u0646\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u062a \u0628\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u06a9\u06d5 \u062f\u06b5\u062f\u0627\u0631\u06cc\u062a \u0628\u062f\u06d5\u06cc\u0646\u06d5\u0648\u06d5. \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0628\u06c6 \u062a\u06c6 \u0647\u0627\u062a\u060c \u0695\u0627\u0633\u062a\u06cc \u0695\u0633\u062a\u06d5 \u0648 \u0628\u06c6 \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0648 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc \u062a\u06ce\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u060c \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u062f\u06d5\u0633\u062a\u0648\u0648 \u062f\u06ce \u0628\u06cc\u06a9\u06d5\u0646\u061b \u0648\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u06a9\u0627\u0631\u06cc \u062e\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0686\u06d5\u0645\u06d5\u0695\u0627\u0634 \u0628\u0646\u060c \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc\u0646.',
    '\u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0628\u06d5\u0631\u067e\u06ce\u0648\u0627\u0631\u06d5 - \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 - \u06af\u0634 \u0628\u06c6 \u062e\u0648\u062f\u0627\u0646\u061b \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9 \u0647\u06d5\u0631 \u0633\u06d5\u0631\u0628\u06d5\u0648\u06d5. \u0633\u0627 \u062a\u06c6 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u067e\u06d5\u0631\u0633\u062a\u06d5 \u0648 \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u062a \u0628\u06d5\u0648 \u0628\u0633\u067e\u06ce\u0631\u06d5. \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5 \u0647\u06cc\u0686\u06cc \u0628\u06ce \u0626\u0627\u06af\u0627 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0695\u0627. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u0646 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u0631\u06d5\u0648\u06d5.',
    '\u0642\u0648\u0631\u0626\u0627\u0646\u06ce\u06a9\u06d5 \u0628\u06d5 \u0639\u0627\u0631\u06d5\u0628\u06cc \u0626\u06ce\u0645\u06d5 \u0646\u0627\u0631\u062f\u0645\u0627\u0646\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0626\u06ce\u0648\u06d5 \u062a\u06ce\u0628\u06af\u06d5\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5\u062a\u06c6\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u06d5\u060c \u0634\u06cc\u0631\u06cc\u0646 \u062a\u0631\u06cc\u0646 \u0686\u06cc\u0631\u06c6\u06a9\u062a \u0628\u06c6 \u0648\u06d5\u062f\u06d5\u06af\u06ce\u0695\u06cc\u0646\u061b \u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u062a\u06c6 \u0647\u06d5\u0631 \u0626\u0627\u06af\u0627\u0634\u062a \u0644\u06ce \u0646\u06d5\u0628\u0648\u0648\u06d5.',
    '\u0648\u06d5\u062e\u062a\u06ce \u06cc\u0648\u0648\u0633\u0641 \u0628\u06d5 \u0628\u0627\u0648\u06a9\u06cc \u06af\u0648\u062a: \u0628\u0627\u0628\u06c6! \u0645\u0646 \u06cc\u0627\u0632\u062f\u06d5 \u0626\u06d5\u0633\u062a\u06ce\u0631\u06d5 \u0648 \u062e\u06c6\u0631 \u0648 \u0647\u06d5\u06cc\u06a4\u0645 \u0628\u06d5 \u062e\u06d5\u0648 \u062f\u06cc\u062a\u0646\u060c \u06a9\u06d5 \u0633\u06d5\u0631 \u0628\u06c6 \u0645\u0646 \u062f\u06d5\u0686\u06d5\u0645\u06ce\u0646\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u06a9\u0648\u0695\u06ce\u0698\u06af\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u0645! \u0626\u06d5\u0648 \u062e\u06d5\u0648\u0646\u06d5\u062a \u0644\u06d5 \u0644\u0627\u06cc \u0628\u0631\u0627\u06a9\u0627\u0646\u062a \u0645\u06d5\u06af\u06ce\u0695\u06d5\u0648\u06d5\u060c \u0646\u06d5\u0648\u06d5\u06a9 \u06af\u06d5\u0695\u06ce\u06a9\u062a \u067e\u06ce \u0628\u06a9\u06d5\u0646\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0628\u06c6\u0628\u0646\u06cc\u0627\u062f\u06d5\u0645 \u062f\u0648\u0698\u0645\u0646\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0645\u06d5 \u062f\u06cc\u0627\u0631\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0648\u06cc\u0633\u062a\u0648\u0648\u06cc\u06d5 \u0647\u06d5\u06b5\u062a \u0628\u0698\u06ce\u0631\u06ce \u0648 \u0641\u06ce\u0631\u062a \u0628\u06a9\u0627 \u0645\u0627\u0646\u0627\u06cc \u062e\u06d5\u0648\u0646\u0627\u0646 \u0644\u06ce\u06a9 \u062f\u06d5\u06cc\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062a\u06c6 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u062e\u0627\u0646\u06d5\u062f\u0627\u0646\u06cc \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628\u060c \u0686\u0627\u06a9\u06d5\u06cc \u0628\u06ce \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc \u0628\u06a9\u0627\u061b \u0648\u06d5\u06a9 \u0626\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06cc\u0633\u062d\u0627\u0642\u06cc \u0628\u0627\u0648\u0627\u0646\u06cc \u062a\u06c6\u06cc \u06a9\u0631\u062f. \u062f\u06cc\u0627\u0631\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0647\u06d5\u0631\u06d5 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0644\u06d5 \u0695\u0627\u0628\u0648\u0631\u062f\u06d5\u06cc \u0646\u0627\u0648 \u06cc\u0648\u0648\u0633\u0641 \u0648 \u0628\u0631\u0627\u06cc\u0627\u0646\u06cc\u060c \u067e\u06d5\u0646\u062f\u06cc \u0632\u06c6\u0631 \u0647\u06d5\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u067e\u0631\u0633\u0646.',
    '\u06a9\u0627\u062a\u06ce \u06af\u0648\u062a\u06cc\u0627\u0646: \u062f\u06cc\u0627\u0631\u06d5 \u06cc\u0648\u0648\u0633\u0641 \u0648 \u0628\u0631\u0627\u06a9\u06d5\u06cc \u0644\u0627\u06cc \u0628\u0627\u0648\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0634\u06d5\u0648\u06cc\u0633\u062a\u062a\u0631\u0646\u061b \u062e\u06c6 \u0626\u06ce\u0645\u06d5\u0634 \u062f\u06d5\u0633\u062a\u06d5\u067e\u06cc\u0627\u0648\u06ce\u06a9\u06cc\u0646. \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0628\u0627\u0628\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u06af\u0648\u0645\u0695\u0627 \u0628\u0648\u0648\u06d5.',
    '\u06cc\u0648\u0648\u0633\u0641 \u0628\u0648\u06af\u0698\u0646\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u0627 \u0641\u0695\u06ce\u06cc \u062f\u06d5\u0646\u061b \u0647\u06d5\u062a\u0627 \u0628\u0627\u0648\u06a9 \u0628\u0628\u06ce\u062a\u06d5 \u062a\u0627\u06cc\u0628\u06d5\u062a \u0628\u06d5 \u062e\u06c6\u062a\u0627\u0646. \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06cc\u0634 \u0626\u06ce\u0648\u06d5 \u0628\u0628\u0646 \u0628\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u0628\u06d5\u0631\u06a9\u06d5\u062a\u06cc.',
    '\u06cc\u06d5\u06a9\u06ce \u0644\u06d5\u0648\u0627\u0646 \u0647\u06d5\u06b5\u06cc\u062f\u0627\u06cc\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u0647\u06d5\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0644\u06d5 \u06cc\u0648\u0648\u0633\u0641 \u06a9\u0648\u0634\u062a\u0646 \u06af\u06d5\u0695\u06ce\u0646 \u0648 \u0628\u06cc\u062e\u06d5\u0646\u06d5 \u0628\u0646 \u0628\u06cc\u0631\u06d5\u06a9\u06d5\u0648\u06d5\u061b \u06a9\u0627\u0631\u0648\u0627\u0646\u0686\u06cc \u0647\u06d5\u06b5\u06cc\u06af\u0631\u0646\u06d5\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0627\u0628\u06c6! \u0686\u06c6\u0646\u06d5 \u06a9\u06d5 \u062a\u06c6 \u0644\u06d5\u0645\u06d5\u0695 \u06cc\u0648\u0648\u0633\u0641 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0631\u062e\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0628\u06cc\u061f \u062e\u06c6 \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0634\u0645\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06d5.',
    '\u0633\u0648\u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0645\u06d5\u06cc \u0628\u0646\u06ce\u0631\u06d5\u060c \u06af\u06d5\u0634\u062a \u0628\u06a9\u0627\u062a \u0648 \u06af\u06d5\u067e \u0628\u062f\u0627\u062a \u0648 \u062e\u06c6\u0645\u0627\u0646 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u06cc \u062f\u06d5\u0628\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06cc\u0628\u06d5\u0646\u06d5 \u062f\u06d5\u0631\u060c \u062a\u0648\u0648\u0634\u06cc \u06a9\u0648\u06b5 \u0648 \u06a9\u06d5\u0633\u06d5\u0631 \u062f\u06d5\u0628\u0645\u061b \u062a\u0631\u0633\u06cc\u0634\u0645 \u0647\u06d5\u0633 \u06af\u0648\u0631\u06af \u0628\u06cc\u062e\u0648\u0627 \u0648 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u06af\u0627\u062a\u0627\u0646 \u0644\u06ce \u0646\u06d5\u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u06af\u0648\u0631\u06af \u062e\u0648\u0627\u0631\u062f\u06cc \u2013 \u06a9\u06d5 \u062f\u06d5 \u062e\u06c6\u0631\u062a\u06cc\u0646 \u0648 \u067e\u06ce\u06a9\u06d5\u0648\u06d5\u06cc\u0646 - \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0645\u06d5 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u06cc\u0646.',
    '\u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0628\u0631\u062f\u06cc\u0627\u0646\u06d5 \u062f\u06d5\u0631\u06ce \u0648 \u0628\u06d5 \u06af\u06d5\u0644\u06d5\u06a9\u06c6\u0645\u06d5\u06af\u06cc \u0648\u06cc\u0633\u062a\u06cc\u0627\u0646 \u0628\u06cc\u062e\u06d5\u0646\u06d5 \u0628\u0646 \u0628\u06cc\u0631\u06d5\u06a9\u06d5\u0648\u06d5\u060c [\u0626\u06cc\u062a\u0631 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u062a\u06d5\u0648\u0627\u0648 \u06a9\u0631\u062f \u0648] \u0626\u06ce\u0645\u06d5\u0634 \u0626\u06d5\u0648\u0645\u0627\u0646 \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f \u06a9\u06d5: \u062a\u06c6 \u0644\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0633\u062a\u06cc \u067e\u06ce \u0646\u0627\u06a9\u06d5\u0646.',
    '\u0626\u06ce\u0648\u0627\u0631\u06ce \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u0627\u0646\u062f\u0627\u060c \u0628\u06c6 \u0644\u0627\u06cc \u0628\u0627\u0628\u06cc\u0627\u0646 - \u0628\u06d5 \u06af\u0631\u06cc\u0627\u0646\u06d5\u0648\u06d5 - \u0648\u06d5\u06af\u06d5\u0695\u0627\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0627\u0628\u06c6! \u0686\u0648\u0648\u0628\u0648\u0648\u06cc\u0646 \u063a\u0627\u0631\u063a\u0627\u0631\u06ce\u0646\u06ce \u0628\u06a9\u06d5\u06cc\u0646 \u0648 \u06cc\u0648\u0648\u0633\u0641\u06cc\u0634\u0645\u0627\u0646 \u0644\u0627\u06cc \u06a9\u06d5\u0644 \u0648 \u067e\u06d5\u0644\u0645\u0627\u0646 \u0628\u06d5 \u062c\u06ce\u0647\u06ce\u0634\u062a\u061b \u06af\u0648\u0631\u06af \u062e\u0648\u0627\u0631\u062f\u06cc. \u0647\u06d5\u0631 \u0686\u06d5\u0646\u062f \u0695\u0627\u0633\u062a\u06cc\u0634\u062a \u067e\u06ce \u062f\u06d5\u06b5\u06ce\u06cc\u0646\u060c \u0628\u0695\u0648\u0627\u06cc\u06d5 \u0628\u06d5 \u0626\u06ce\u0645\u06d5 \u0646\u0627\u06a9\u06d5\u06cc.',
    '\u06a9\u0631\u0627\u0633\u06d5\u06a9\u06d5\u0634\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0628\u0648\u0648\u061b \u062e\u0648\u06ce\u0646\u06d5 \u062f\u0631\u06c6\u0632\u0646\u06d5\u06cc \u067e\u06ce\u0648\u06d5 \u0628\u0648\u0648. \u06af\u0648\u062a\u06cc: \u062f\u06cc\u0627\u0631\u06d5 \u062f\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0644\u06d5\u0628\u06d5\u0631 \u062e\u06d5\u0645\u06b5\u0627\u0646\u062f\u0648\u0648\u0646. \u062e\u06c6\u0695\u0627\u06af\u0631\u06cc \u0628\u06ce \u0646\u0627\u0634\u0648\u06a9\u0631\u06cc [\u0686\u0627\u0631\u06cc \u0645\u0646\u06d5]\u061b \u0644\u06d5\u0645\u06d5\u0695 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0626\u06ce\u0698\u0646\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u0647\u0627\u0646\u0627\u0645\u06d5\u0648\u06d5 \u062f\u06ce.',
    '\u06a9\u0627\u0631\u0648\u0627\u0646\u06ce \u0647\u0627\u062a\u060c \u0626\u0627\u0648\u06a9\u06ce\u0634\u06cc\u0627\u0646 \u0646\u0627\u0631\u062f\u060c \u062f\u06c6\u06b5\u0686\u06d5\u06cc \u062f\u0627\u0647\u06ce\u0634\u062a: \u0645\u0698\u062f\u06d5 \u0626\u06d5\u0645\u06d5 \u06a9\u0648\u0695\u06cc\u0698\u06af\u06d5\u06cc\u06d5. \u0648\u06d5\u06a9\u0648\u0648 \u06a9\u0627\u06b5\u0627 \u062a\u0627\u0642\u06d5\u062a\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u062e\u0648\u0627 \u0644\u06d5 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0626\u0627\u06af\u0627 \u0628\u0648\u0648.',
    '\u0628\u06d5 \u0646\u0631\u062e\u06ce \u06a9\u06d5\u0645\u060c \u0628\u06d5 \u0686\u06d5\u0646\u062f \u062f\u0631\u06d5\u0645 \u0641\u0631\u06c6\u062a\u06cc\u0627\u0646 \u0648 \u0646\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0648\u06cc\u0633\u062a.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0644\u06d5 \u0645\u06cc\u0633\u0631 \u0633\u06d5\u0646\u062f\u06cc\u060c \u0628\u06d5 \u0698\u0646\u06ce \u062e\u06c6\u06cc \u0648\u062a: \u0642\u06d5\u062f\u0631\u06cc \u0628\u06af\u0631\u06d5 \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u0644\u06ce \u0628\u06a9\u06d5\u06cc\u0646\u061b \u06cc\u0627\u0646 \u0628\u06cc\u06a9\u06d5\u06cc\u0646\u06d5 \u0632\u0627\u0631\u06c6\u06cc \u062e\u06c6\u0645\u0627\u0646. \u0628\u06d5\u0645 \u0626\u0627\u0648\u0627\u06cc\u06d5 \u0644\u06d5\u0648 \u0647\u06d5\u0631\u062f\u06d5\u062f\u0627 \u06cc\u0648\u0648\u0633\u0641\u0645\u0627\u0646 \u0632\u06c6\u0631 \u0628\u0627\u0634 \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0646\u062f\u061b \u062a\u0627 \u0626\u06ce\u0645\u06d5 \u0641\u06ce\u0631\u06cc\u0634\u06cc \u0628\u06a9\u06d5\u06cc\u0646 \u0686\u06c6\u0646 \u062e\u06d5\u0648\u0646\u0627\u0646 \u0644\u06ce\u06a9 \u0628\u062f\u0627\u062a\u06d5\u0648\u06d5. \u062e\u0648\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u06a9\u0627\u0631\u06cc \u062e\u06c6\u06cc \u0632\u0627\u06b5\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0644\u06ce \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0628\u0648\u0648 \u0628\u06d5 \u067e\u06cc\u0627\u0648\u06cc \u062a\u06d5\u0648\u0627\u0648\u060c \u0633\u06d5\u0644\u06cc\u0642\u06d5 \u0648 \u0632\u0627\u0646\u0633\u062a\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627. \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u067e\u0627\u062f\u0627\u0634 \u0628\u06d5 \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u0626\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u0698\u0646\u06d5 \u0648\u0627 \u06cc\u0648\u0648\u0633\u0641 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u0626\u06d5\u0648\u0627 \u0628\u0648\u0648\u060c \u062a\u06d5\u0645\u0627\u06cc \u062a\u06ce\u06a9\u0631\u062f. \u062f\u06d5\u0631\u06af\u0627\u06a9\u0627\u0646\u06cc \u0644\u06ce \u06af\u0627\u06b5\u06d5 \u062f\u0627 \u0648 \u06af\u0648\u062a\u06cc: \u0628\u06d5\u0644\u06d5\u0632 \u0626\u0627\u0645\u0627\u062f\u06d5\u06cc\u06d5. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0646\u0627 \u0628\u06d5 \u062e\u0648\u062f\u0627\u061b \u0626\u06d5\u0648 \u0626\u0627\u063a\u0627\u0645\u06d5\u060c \u0642\u06d5\u062f\u0631\u06cc \u06af\u0631\u062a\u0648\u0648\u0645\u060c [\u0628\u06ce \u0626\u06d5\u0645\u06d5\u06af\u0627\u0646\u06cc \u062f\u0627\u0648\u06ce\u0646\u067e\u06cc\u0633] \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u0646\u060c \u0646\u0627\u062e\u06d5\u0644\u06d5\u0633\u06ce\u0646.',
    '\u0648\u06ce \u067e\u06d5\u0644\u0627\u0645\u0627\u0631\u06cc \u0626\u06d5\u0645\u06cc \u062f\u0627 \u0648 \u0626\u06d5\u0645\u06cc\u0634 \u06af\u06d5\u0631 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0646\u06d5\u062f\u06cc\u0628\u0627\u060c \u0626\u0627\u0648\u0642\u0627\u06cc \u062f\u06d5\u0628\u0648\u0648. \u0628\u06c6 \u0626\u06d5\u0645\u06d5 \u0628\u0648\u0648 \u06a9\u06d5 \u062e\u0631\u0627\u067e\u06d5 \u0648 \u0634\u0648\u0648\u0631\u06d5\u06cc\u06cc \u0644\u06ce \u0648\u06d5\u067e\u0631\u06cc\u0646\u06af\u06ce\u0646\u06cc\u0646\u061b \u0626\u06d5\u0645 \u06cc\u06d5\u06a9\u06ce \u0628\u0648\u0648 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5 \u062f\u06b5\u067e\u0627\u06a9\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646.',
    '\u0628\u06d5 \u06a9\u06ce\u0628\u06d5\u0631\u06a9\u06ce \u0647\u06d5\u0631 \u062f\u0648\u0648 \u06af\u06d5\u06cc\u0646\u06d5 \u0644\u0627\u06cc \u062f\u06d5\u0631\u06af\u0627\u06a9\u06d5\u061b \u06af\u062c\u06cc \u06cc\u0648\u0648\u0633\u0641\u06cc \u0644\u0627\u06cc \u067e\u0634\u062a\u06d5\u0648\u06d5 \u062f\u0627\u062f\u0695\u06cc \u0648 \u0644\u06d5 \u0628\u06d5\u0631\u062f\u06d5\u0631\u06cc\u060c \u062a\u0648\u0648\u0634\u06cc \u0633\u06d5\u0631\u0648\u06d5\u0631\u06cc \u0648\u06ce \u0647\u0627\u062a\u0646. \u06af\u0648\u062a\u06cc: \u0633\u0632\u0627\u06cc \u06a9\u06d5\u0633\u06ce \u0686\u06cc\u06cc\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u0627\u06b5 \u0648 \u062e\u06ce\u0632\u0627\u0646\u06cc \u062a\u06c6\u060c \u062a\u06d5\u0645\u0627\u062f\u0627\u0631\u06cc \u0634\u0648\u0648\u0631\u06d5\u06cc\u06cc \u0628\u06ce\u061b \u063a\u06d5\u06cc\u0631 \u0644\u06d5 \u0632\u06cc\u0646\u0627\u0646 \u06cc\u0627 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646\u061f',
    '\u067e\u06d5\u0631\u0633\u06a4\u06cc \u062f\u0627: \u0648\u06ce \u062a\u06d5\u0645\u0627\u06cc \u062f\u06d5\u0645\u0646 \u06a9\u0631\u062f\u0628\u0648\u0648. \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u062e\u0632\u0645\u06d5\u06a9\u0627\u0646\u06cc \u0648\u06ce \u0647\u06d5\u06b5\u06cc\u062f\u0627\u06cc\u06d5: \u06af\u06d5\u0631 \u06af\u062c\u06cc \u0626\u06d5\u0645 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06ce\u0634\u06d5\u0648\u06d5 \u062f\u0695\u0627\u0648\u06d5\u060c \u0648\u06ce \u0695\u0627\u0633\u062a \u0626\u06d5\u06a9\u0627 \u0648 \u0626\u06d5\u0645 \u06a4\u0695\u06c6\u06cc\u06d5\u061b',
    '\u06af\u06d5\u0631 \u06af\u062c\u06cc\u0634\u06cc \u0644\u06d5 \u067e\u0634\u062a\u06d5\u0648\u06d5 \u062f\u0627\u062f\u0695\u0627\u0648\u06d5\u060c \u0648\u06ce \u062f\u0631\u06c6\u06cc \u06a9\u0631\u062f\u060c \u0626\u06d5\u0648 \u0695\u0627\u0633\u062a \u062f\u06d5\u06a9\u0627.',
    '\u0648\u06d5\u062e\u062a\u06ce \u062f\u06cc\u062a\u06cc \u06a9\u0631\u0627\u0633\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u067e\u0634\u062a\u06d5\u0648\u06d5 \u062f\u0627\u062f\u0695\u0627\u0648\u06d5\u060c \u0648\u062a\u06cc: \u0626\u06d5\u0645\u06d5 \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0641\u06ce\u06b5\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5\u061b \u0641\u06ce\u06b5\u06cc \u0626\u06ce\u0648\u06d5\u0634 \u0632\u06c6\u0631 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06d5.',
    '\u06cc\u0648\u0648\u0633\u0641! \u0648\u0627\u0632 \u0644\u06d5\u0645 \u0628\u0627\u0633\u06d5 \u0628\u06ce\u0646\u06d5. \u062d\u0648\u0631\u0645\u06ce! \u062a\u06c6\u0634 \u0628\u0648\u0648\u0631\u062f\u0646 \u0644\u06d5 \u062e\u06d5\u062a\u0627\u062a \u062f\u0627\u0648\u0627 \u0628\u06a9\u06d5\u061b \u06a9\u06d5 \u062a\u06c6 \u0644\u06d5 \u062e\u06d5\u062a\u0627\u06a9\u0627\u0631\u0627\u0646\u06cc.',
    '\u0698\u0646\u0627\u0646\u06cc \u0634\u0627\u0631 \u0642\u0627\u0648\u06cc\u0627\u0646 \u062f\u0627\u062e\u0633\u062a: \u0698\u0646\u0627 \u0639\u06d5\u0632\u06cc\u0632 \u062d\u06d5\u0632 \u0644\u06d5 \u062e\u0648\u06b5\u0627\u0645\u06ce \u062e\u06c6 \u062f\u06d5\u06a9\u0627\u061b \u0644\u06d5 \u062f\u06b5\u062f\u0627\u0631\u06cc\u062f\u0627 \u0633\u0648\u0648\u062a\u0627\u0648\u06d5\u061b \u0648\u0627 \u062f\u06cc\u0627\u0631\u06d5 \u062a\u06d5\u0648\u0627\u0648 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u062f\u06d5\u0631\u0686\u0648\u0648\u06d5.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u062a\u06cc\u0632\u0648\u062a\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u0628\u06cc\u0633\u062a\u060c \u0644\u06d5 \u062f\u0648\u0648\u06cc \u0646\u0627\u0631\u062f\u0646\u061b \u06af\u06d5\u0644\u06ce \u0628\u0627\u06b5\u06af\u06d5 \u0648 [\u062e\u0648\u0627\u0631\u062f\u06d5\u0645\u06d5\u0646\u06cc] \u0644\u0627 \u062f\u0627\u0646\u0627\u0646 \u0648 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06d5\u06cc \u0686\u06d5\u0642\u06c6\u06cc\u06d5\u06a9\u06cc\u0634\u06cc \u067e\u06ce \u0633\u067e\u0627\u0631\u062f\u0646. \u06af\u0648\u062a\u06cc: \u0628\u06c6\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06d5 \u062f\u06d5\u0631\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5 \u062f\u06cc\u062a\u06cc\u0627\u0646 \u062d\u06d5\u067e\u06d5\u0633\u0627\u0646 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0695\u06cc \u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u0646\u0627\u0645\u06d5\u062e\u0648\u0627! \u0626\u06d5\u0645\u06d5 \u0645\u0631\u06c6 \u0646\u06cc\u06cc\u06d5 \u0648 \u0628\u06ce \u0633\u06c6 \u0641\u0631\u0634\u062a\u06d5\u06cc\u06ce \u062e\u06c6\u0634\u06d5\u0648\u06cc\u0633\u062a\u06d5.',
    '\u0648\u06ce \u06af\u0648\u062a: \u0626\u06d5\u0645\u06d5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0633\u06d5\u0631\u062f\u06d5\u0646\u0634\u06cc \u0645\u0646\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u061b \u0648\u06cc\u0633\u062a\u0645 \u0628\u0628\u06ce\u062a\u06d5 \u062f\u06c6\u0633\u062a\u0645 \u0648 \u062f\u06d5\u0633\u062a\u0645 \u062f\u06d5\u06af\u06d5\u06b5 \u062a\u06ce\u06a9\u06d5\u06b5 \u0628\u06a9\u0627\u060c \u062e\u06c6\u06cc \u0695\u0627\u06af\u0631\u062a \u0648 \u0628\u06c6\u0645 \u062a\u0627\u0648 \u0646\u06d5\u06a9\u0631\u0627\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u0644\u06d5\u0648\u0645 \u062f\u06d5\u0648\u06ce \u0628\u06cc\u06a9\u0627\u060c \u0646\u06d5\u06cc\u06a9\u0627\u060c \u062f\u06d5\u0628\u06ce \u0628\u0686\u06ce\u062a\u06d5 \u0632\u06cc\u0646\u062f\u0627\u0646\u06ce \u0648 \u0648\u06d5\u06a9 \u0626\u06ce\u0633\u06cc\u0631\u0627\u0646\u06cc \u0695\u0627\u0628\u0648\u06ce\u0631\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0632\u06cc\u0646\u062f\u0627\u0646\u06d5\u06a9\u06d5\u0645 \u067e\u06ce \u062e\u06c6\u0634\u062a\u0631\u06d5 \u0644\u06d5\u0648\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06ce\u0645 \u062f\u06d5\u062e\u0648\u0627\u0632\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u06af\u06d5\u0695\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0645 \u0644\u06ce \u0644\u0627 \u0646\u06d5\u062f\u06d5\u06cc\u060c \u062f\u06b5\u0645 \u062f\u06d5\u0686\u06ce \u0628\u06c6 \u0644\u0627\u06cc\u0627\u0646 \u0648 \u06cc\u06d5\u06a9\u06ce\u06a9 \u062f\u06d5\u0628\u0645 \u0644\u06d5 \u0646\u06d5\u0632\u0627\u0646\u0627\u0646.',
    '\u062e\u0648\u062f\u0627 \u0646\u0632\u0627\u06cc \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0631\u062f \u0648 \u06af\u06d5\u0695\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u0644\u06ce \u0644\u0627 \u062f\u0627\u060c \u06a9\u06d5 \u062e\u06c6\u06cc \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u067e\u0627\u0634\u0627\u0646 \u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u0627\u0634 \u0632\u0627\u0646\u06cc - \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06d5\u0634 - \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0632\u06cc\u0646\u062f\u0627\u0646\u06cc \u0628\u06ce.',
    '\u062f\u0648\u0648 \u062e\u0648\u06b5\u0627\u0645\u06cc\u0634 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648 \u0686\u0648\u0648\u0646\u06d5 \u0632\u06cc\u0646\u062f\u0627\u0646\u06ce. \u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u06af\u0648\u062a\u06cc: \u0645\u0646 \u062e\u06d5\u0648\u0646\u0645 \u062f\u06cc\u062a \u062a\u0631\u06ce \u062f\u06d5\u0648\u0634\u0645. \u0626\u06d5\u0648 \u062f\u06cc \u06af\u0648\u062a\u06cc: \u0645\u0646 \u0644\u06d5 \u062e\u06d5\u0648\u0645\u0627 \u0646\u0627\u0646\u0645 \u0628\u06d5\u0633\u06d5\u0631 \u0633\u06d5\u0631\u06d5\u0648\u06d5\u06cc\u06d5\u060c \u0645\u06d5\u0644 \u0644\u06ce\u06cc \u062f\u06d5\u062e\u06c6\u0646. \u062e\u06d5\u0648\u0646\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0644\u06ce\u06a9\u062f\u06d5\u0648\u06d5\u061b \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u062a\u06c6 \u0695\u0627\u062f\u06d5\u0628\u06cc\u0646\u06cc\u0646 \u06a9\u06d5 \u0644\u06d5 \u062e\u06ce\u0631\u06d5\u0648\u0645\u06d5\u0646\u062f\u06d5\u06a9\u0627\u0646 \u0628\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0628\u06d5\u0631\u0644\u06d5 \u0698\u06d5\u0645\u06d5 \u0646\u0627\u0646\u06ce \u06a9\u06d5 \u0628\u06c6\u062a\u0627\u0646 \u062f\u06ce \u0648 \u067e\u06ce\u0634 \u062e\u0648\u0627\u0631\u062f\u0646\u06cc\u060c \u0645\u0627\u0646\u0627\u06cc \u062e\u06d5\u0648\u0646\u06d5\u06a9\u06d5\u0648 \u067e\u06ce\u062f\u06d5\u06b5\u06ce\u0645\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u06d5\u0634 \u0644\u06d5\u0648 \u0628\u06d5\u0647\u0631\u0627\u0646\u06d5\u06cc\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0641\u06ce\u0631\u06cc \u06a9\u0631\u062f\u0648\u0648\u0645. \u0645\u0646 \u0626\u0627\u06cc\u0646\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0645 \u0648\u06d5\u0644\u0627\u0646\u0627\u06cc\u06d5\u060c \u06a9\u06d5 \u0646\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0644\u0627\u0634\u06cc\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a \u0646\u0627\u06cc\u06d5.',
    '\u0645\u0646 \u0634\u0648\u06ce\u0646 \u062f\u06cc\u0646\u06cc \u0628\u0627\u0648\u0627\u0646\u06cc \u062e\u06c6\u0645\u060c \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06cc\u0633\u062d\u0627\u0642 \u0648 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628 \u06a9\u06d5\u0641\u062a\u06af\u0645\u061b \u0628\u06d5\u0695\u06d5\u0648\u0627\u0645\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u06cc\u06af\u06d5 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u0627 \u0628\u0632\u0627\u0646\u06cc\u0646. \u0626\u06d5\u0645\u06d5\u0634 \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u0627\u0648\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u0648 \u0645\u06d5\u0631\u062f\u0645\u06cc \u06a9\u0631\u062f\u06af\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631 \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0647\u06d5\u0631 \u0646\u0627\u0634\u06a9\u0648\u0631\u0646.',
    '\u0626\u06d5\u0631\u06ce \u0626\u06ce\u0648\u06d5\u06cc \u062f\u0648\u0648 \u0647\u0627\u0648\u0628\u06d5\u0646\u062f\u0645! \u0626\u0627\u062e\u06c6 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u067e\u06ce \u0628\u0627\u0634\u062a\u0631\u06d5\u060c \u06cc\u0627 \u062e\u06c6 \u062e\u0648\u062f\u0627\u06cc \u062a\u0627\u06a9 \u0648 \u062a\u06d5\u0646\u06cc\u0627\u06cc \u0632\u0627\u06b5 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u0627\u061f',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0686\u06d5\u0646\u062f \u0634\u062a\u06ce\u06a9\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0646\u060c \u06a9\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0648 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u062a\u0627\u0646 - \u0633\u06d5\u0631\u0628\u06d5\u062e\u06c6 - \u0646\u0627\u0648\u062a\u0627\u0646 \u0644\u06ce \u0646\u0627\u0648\u0646\u061b \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u062a\u06d5\u0648\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u0627 \u0646\u06cc\u06cc\u06d5. \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0647\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062f\u0627\u0648\u06d5 \u06a9\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0645\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646. \u062f\u06cc\u0646\u06ce \u06a9\u06d5 \u0695\u0627\u0633\u062a \u0648 \u062f\u0631\u0648\u0633\u062a\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0626\u06d5\u06cc \u0647\u06d5\u0631 \u062f\u0648\u0648\u06cc \u0647\u0627\u0648\u0628\u06d5\u0646\u062f\u06cc\u062e\u0627\u0646\u06d5\u0645! \u06cc\u06d5\u06a9 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0645\u06d5\u06cc\u06af\u06ce\u0695\u06cc \u062e\u06ce\u0648\u06cc\u061b \u0626\u06d5\u0648\u06d5\u06a9\u06d5\u06cc \u062a\u0631 \u0644\u06d5 \u062f\u0627\u0631 \u062f\u06d5\u062f\u0631\u06ce\u060c \u0626\u06d5\u0648\u0633\u0627 \u0645\u06d5\u0644\u0627\u0646 \u0686\u06cc\u0646\u06d5\u06cc \u0644\u06d5 \u0633\u06d5\u0631\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646. \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u06a9\u06d5 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06cc\u0633\u062a \u0644\u06d5 \u0626\u06d5\u0646\u062c\u0627\u0645\u06d5\u06a9\u06d5\u06cc \u0628\u067e\u0631\u0633\u0646\u060c \u0648\u0627 \u0632\u0627\u0646\u062f\u0631\u0627 \u0648 \u0647\u06d5\u0631 \u0648\u06d5\u062f\u06cc\u0634 \u062f\u06ce.',
    '\u0628\u06d5 \u06cc\u06d5\u06a9\u06ce\u06a9 \u0644\u06d5\u0648 \u062f\u0648\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06c6\u06cc \u062f\u0627\u0646\u0627\u0628\u0648\u0648 \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u0628\u06ce\u060c \u0648\u062a\u06cc: \u06a9\u06d5 \u062f\u06d5\u0686\u06cc\u06d5 \u0644\u0627\u06cc \u0626\u0627\u063a\u0627\u062a\u060c \u0628\u0627\u0633\u0645 \u0628\u06a9\u06d5. \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0646\u06d5\u06cc\u0647\u06ce\u0634\u062a \u0644\u06d5 \u0628\u06cc\u0631\u06cc \u0628\u06ce\u060c \u0644\u06d5 \u0644\u0627\u06cc \u0626\u0627\u063a\u0627\u06cc \u0628\u0627\u0633\u06cc \u0628\u06a9\u0627\u061b \u062a\u0627 \u0686\u06d5\u0646\u062f \u0633\u0627\u06b5\u06ce\u06a9 \u0644\u06d5 \u06af\u0631\u062a\u0648\u0648\u062e\u0627\u0646\u06d5 \u0645\u0627\u06cc\u06d5\u0648\u06d5.',
    '\u0634\u0627 \u06af\u0648\u062a\u06cc: \u0644\u06d5 \u062e\u06d5\u0648\u0645\u0627 \u062f\u06cc\u062a\u0645\u060c \u062d\u06d5\u0648 \u06af\u0627\u06cc \u0642\u06d5\u06b5\u06d5\u0648 \u062d\u06d5\u0648 \u0686\u06ce\u06b5\u06cc \u0644\u06d5\u0695 \u062f\u06d5\u06cc\u0627\u0646\u062e\u0648\u0627\u0631\u062f\u0646\u061b \u062d\u06d5\u0648 \u06af\u0648\u06b5\u06d5\u062f\u06d5\u062e\u06b5\u06cc \u0634\u06cc\u0646\u06cc\u0634 \u0648 \u062d\u06d5\u0648\u062a\u06cc \u062f\u06cc\u06a9\u06d5\u06cc \u0648\u0634\u06a9\u06d5\u06b5\u0627\u062a\u0648\u0648. \u0626\u06d5\u06cc \u062f\u06cc\u0648\u06d5\u062e\u0627\u0646 \u0646\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646! \u0626\u06d5\u0645 \u062e\u06d5\u0648\u0646\u06d5\u0645 \u0628\u06c6 \u0644\u06ce\u06a9\u062f\u06d5\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06ce\u06a9\u062f\u06d5\u0631\u06d5\u0648\u06d5\u06cc \u062e\u06d5\u0648\u0646\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0647\u06d5\u0631 \u062e\u06d5\u0648\u0646\u06d5 \u062f\u0631\u06c6\u0632\u0646\u06d5\u0646\u061b \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0645 \u062a\u06d5\u0631\u0632\u06d5 \u062e\u06d5\u0648\u0646\u0627\u0646\u06d5 \u0633\u06d5\u0631 \u062f\u06d5\u0631\u0646\u0627\u06a9\u06d5\u06cc\u0646.',
    '\u0644\u06d5\u0648 \u062f\u0648\u0648 \u06a9\u06d5\u0633\u06d5 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u0627\u0628\u0648\u0648 - \u0628\u06d5 \u062f\u06ce\u0631\u0648\u0698\u0648\u0627\u0646 - \u062a\u0627\u0632\u06d5 \u0648\u06d5\u0628\u06cc\u0631\u06cc \u0647\u0627\u062a\u06d5\u0648\u06d5\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0646 \u0628\u06c6\u062a\u0627\u0646\u06cc \u0644\u06ce\u06a9\u062f\u06d5\u062f\u06d5\u0645\u06d5\u0648\u06d5\u061b \u0633\u0627 \u0628\u0645\u0646\u06ce\u0631\u0646.',
    '\u06cc\u0648\u0648\u0633\u0641! \u0645\u06ce\u0631\u06cc \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0695\u0627\u0633\u062a! \u062d\u06d5\u0648 \u06af\u0627\u06cc \u0642\u06d5\u06b5\u06d5\u0648 \u062d\u06d5\u0648 \u0686\u06ce\u06b5\u06cc \u0644\u06d5\u062c\u06d5\u0631 \u0626\u06d5\u06cc\u0627\u0646\u062e\u06c6\u0646\u060c \u062d\u06d5\u0648\u062a \u0648\u0634\u0648\u0648\u06cc \u0647\u06ce\u0634\u062a\u0627 \u0647\u06d5\u0631 \u0634\u06cc\u0646 \u0648 \u062d\u06d5\u0648\u062a\u06cc \u062f\u06cc\u06a9\u06d5\u0634 \u0648\u0634\u06a9\u06d5\u06b5\u0627\u062a\u0648\u0648\u0646. \u0626\u06d5\u0645 \u062e\u06d5\u0648\u0646\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0644\u06ce\u06a9\u062f\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0645\u0646 \u062f\u06d5\u0686\u0645\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u0645\u06d5\u0631\u062f\u0645\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u062a\u06ce\u0628\u06af\u06d5\u0646.',
    '\u0648\u062a\u06cc: \u062d\u06d5\u0648 \u0633\u0627\u06b5 \u0644\u06d5 \u06a9\u0634\u062a \u0648\u06a9\u0627\u06b5 \u0648\u0686\u0627\u0646 \u0646\u0627\u062f\u06d5\u0646\u061b \u0647\u06d5\u0631\u0686\u06cc\u06cc\u06d5\u06a9\u06cc \u062f\u0631\u0648\u0648\u062a\u0627\u0646\u06d5\u0648\u06d5 - \u06a9\u06d5\u0645\u06ce\u06a9 \u0646\u06d5\u0628\u06ce \u06a9\u06d5 \u0644\u06ce\u06cc \u062f\u06d5\u062e\u06c6\u0646 - \u0644\u06d5 \u0648\u0634\u0648\u0648\u06cc \u062e\u06c6\u06cc\u0627 \u0628\u06ce\u06b5\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0645\u06d5 \u062d\u06d5\u0648\u062a\u06cc \u0642\u0627\u062a\u06cc \u0648 \u0642\u0695\u06cc \u062f\u0627\u062f\u06ce\u061b \u0647\u06d5\u0631\u0686\u06cc \u067e\u06ce\u0634\u0648\u0648 \u0628\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5\u062a\u0627\u0646 \u0647\u06ce\u0634\u062a\u06c6\u062a\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0645\u0648\u0648\u06cc \u062f\u06d5\u062e\u06c6\u0646\u061b \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0645\u06ce \u0644\u06d5\u0648\u06d5\u06cc \u0642\u0627\u06cc\u0645 \u0647\u06d5\u06b5\u06cc\u062f\u06d5\u06af\u0631\u0646.',
    '\u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0645\u06d5 \u0626\u06d5\u0645\u062c\u0627\u0631\u06d5 \u0634\u0627\u06b5\u06ce\u06a9\u06cc \u0648\u0627 \u062f\u06ce\u060c \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633 \u0628\u0627\u0631\u0627\u0646 \u0648 \u0695\u0648\u0648\u06d5\u06a9 \u0645\u0634\u06d5\u06cc\u06d5 \u0648 \u0644\u06d5\u0648\u062f\u0627 \u0645\u0644 \u0644\u06d5 \u06af\u0648\u0634\u06cc\u0646 \u062f\u06d5\u0646\u06cc\u0646.',
    '\u0634\u0627 \u06af\u0648\u062a\u06cc: \u0628\u06c6\u0645 \u0628\u0647\u06ce\u0646\u0646. \u06a9\u0627\u062a\u06ce \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u06d5 \u0647\u0627\u062a\u060c \u06af\u0648\u062a\u06cc: \u0648\u06d5\u06af\u06d5\u0695\u06ce \u0644\u0627\u06cc \u0626\u0627\u063a\u0627\u062a \u0648 \u0644\u06ce\u06cc \u067e\u0631\u0633\u06d5\u060c \u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0628\u0695\u06cc\u060c \u0646\u06cc\u0627\u0632\u06cc\u0627\u0646 \u0686 \u0628\u0648\u0648\u061f \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0644\u06d5 \u062f\u06d5\u0647\u06c6\u06cc\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0648\u062a\u06cc: \u0628\u06c6\u0686\u06cc \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646 \u0628\u0648\u0648 \u06cc\u0648\u0648\u0633\u0641 \u0628\u06c6 \u062e\u06c6\u0648 \u062f\u06d5\u0633\u062a\u06d5\u0645\u06c6\u06a9\u06d5\u0646\u061f \u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u0648\u0627 \u0628\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0646\u06d5\u06a9\u0627\u060c \u0626\u06ce\u0645\u06d5 \u06a9\u0627\u0631\u06cc \u0634\u0648\u0648\u0631\u06d5\u06cc\u06cc\u0645\u0627\u0646 \u0644\u06ce \u0646\u06d5\u062f\u06cc\u0648\u06d5. \u0698\u0646\u0627 \u0639\u06d5\u0632\u06cc\u0632 \u0647\u06d5\u06b5\u06cc\u062f\u0627\u06cc\u06d5: \u0626\u06ce\u0633\u062a\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u0648\u0648\u061b \u0645\u0646 \u062a\u06d5\u0645\u0627\u062d\u0645 \u062f\u06d5\u0648 \u06a9\u0631\u062f\u0628\u0648\u0648\u061b \u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648 \u06af\u0648\u062a\u0648\u0648\u06cc\u06d5 \u0695\u0627\u0633\u062a\u06d5.',
    '[\u06cc\u0648\u0648\u0633\u0641 \u06af\u0648\u062a\u06cc:] \u0626\u06d5\u0645\u06d5\u0634 \u0628\u06c6\u06cc\u06d5 \u0628\u0648\u0648 \u0628\u0632\u0627\u0646\u06cc\u060c \u067e\u0627\u0634\u06d5\u0645\u0644\u06d5\u0634 \u063a\u06d5\u06cc\u0627\u0646\u06d5\u062a\u0645 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f \u0648 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0647\u06c6\u06cc \u062f\u06d5\u063a\u06d5\u06b5\u0628\u0627\u0632\u0627\u0646 \u0646\u0627\u062e\u0627\u062a\u06d5 \u0695\u06ce.',
    '\u0645\u0646\u06cc\u0634 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0647\u06cc\u0686 \u067e\u0627\u06a9\u0627\u0646\u06d5 \u0628\u06c6 \u062e\u06c6\u0645 \u0646\u0627\u06a9\u06d5\u0645. \u0646\u06d5\u0648\u0633 \u0647\u06d5\u0645\u0648\u0648 \u062e\u0631\u0627\u067e\u06d5\u06cc\u06d5\u06a9 \u0628\u06d5 \u067e\u06cc\u0627\u0648 \u062f\u06d5\u06a9\u0627\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06ce\u062f\u0627 \u0628\u06ce\u062a\u06d5\u0648\u06d5. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u062e\u06d5\u062a\u0627\u0628\u06d5\u062e\u0634 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0634\u0627 \u06af\u0648\u062a\u06cc: \u0628\u06cc\u0647\u06ce\u0646\u0646\u06d5 \u0644\u0627\u06cc \u0645\u0646\u060c \u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0645 \u0647\u06d5\u0631 \u0633\u06d5\u0631 \u0628\u06d5 \u0645\u0646 \u0628\u06ce. \u06a9\u0627\u062a\u06ce \u062f\u0648\u0627\u0646\u062f\u06cc\u060c \u06af\u0648\u062a\u06cc: \u062a\u06c6 \u0647\u06d5\u0631 \u0644\u06d5\u0645\u0695\u06c6\u06a9\u06d5\u0648\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0695\u06ce\u0632\u06cc \u0648 \u062c\u06ce\u06af\u0627\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0628\u0645\u06a9\u06d5 \u0628\u06d5 \u0633\u06d5\u0631\u06a9\u0627\u0631\u06cc \u0646\u06d5\u063a\u062f\u06cc\u0646\u06d5 \u0648 \u0628\u06d5\u0631\u0648\u0628\u0648\u0648\u06cc \u0648\u06b5\u0627\u062a\u061b \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06cc \u0644\u06ce\u062f\u06d5\u06a9\u06d5\u0645 \u0648 \u0644\u06ce\u0632\u0627\u0646\u06cc\u0634\u0645.',
    '\u0626\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u06cc\u0648\u0648\u0633\u0641\u0645\u0627\u0646 \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 [\u0628\u06d5 \u0695\u06ce\u0632\u06d5\u0648\u06d5] \u062c\u06ce \u06a9\u0631\u062f\u06d5\u0648\u06d5\u061b [\u062e\u06c6\u0634 \u0646\u0634\u06cc\u0646\u06ce\u06a9 \u0628\u0648\u0648] \u0644\u06d5 \u0647\u06d5\u0631 \u062c\u06ce \u062e\u06c6\u06cc \u0648\u06cc\u0633\u062a\u0628\u0627\u06cc \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0698\u06cc\u0627. \u0647\u06d5\u0631\u06a9\u06ce \u0626\u06ce\u0645\u06d5 \u0628\u0645\u0627\u0646\u06d5\u0648\u06ce \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u0645\u0627\u0646\u06cc \u062f\u06d5\u062e\u06d5\u06cc\u0646\u061b \u067e\u0627\u062f\u0627\u0634\u06cc \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u0627\u0646\u06cc\u0634 \u0648\u0646\u062f\u0627 \u0646\u0627\u06a9\u06d5\u06cc\u0646.',
    '\u062e\u06c6 \u062f\u06cc\u0627\u0631\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u06d5\u0648 \u0644\u0627\u0634 \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u0648 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0646.',
    '\u0628\u0631\u0627\u06cc\u0627\u0646\u06cc \u06cc\u0648\u0648\u0633\u0641 \u0647\u0627\u062a\u0646\u061b \u0628\u06c6 \u062f\u06cc\u062a\u0646\u06cc \u0648\u06d5\u0698\u0648\u0648\u0631 \u06a9\u06d5\u0648\u062a\u0646\u061b \u0646\u0627\u0633\u06cc\u0646\u06cc\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646 \u0626\u06d5\u0645\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0646\u0627\u0633\u06cc.',
    '\u062f\u0648\u0627\u06cc \u0626\u06d5\u0645\u06d5 \u0628\u0627\u0631\u06cc \u0628\u0627\u0631\u06a9\u0631\u062f\u0646\u060c \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u0628\u0631\u0627\u06cc\u06d5\u06a9\u06cc \u0628\u0627\u0648\u06a9\u06cc\u062a\u0627\u0646 \u0647\u06d5\u0633\u060c \u0628\u06c6\u0645\u06cc \u0628\u06ce\u0631\u0646\u061b \u0626\u06d5\u06cc \u0646\u06d5\u062a\u0627\u0646\u062f\u06cc \u0645\u0646 \u062e\u0627\u0646\u06d5\u062e\u0648\u06ce\u06cc \u0686\u0627\u06a9\u0645 \u0648 \u067e\u06ce\u0648\u0627\u0646\u06d5\u06a9\u06d5\u0634\u0645 \u062a\u06d5\u0648\u0627\u0648\u06d5\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0645 \u0628\u06c6 \u0646\u06d5\u0647\u06ce\u0646\u0646\u060c \u067e\u06ce\u0648\u0627\u0646\u06d5\u0648 \u0644\u06d5\u0644\u0627\u06cc \u0645\u0646 \u0646\u06cc\u06cc\u06d5 \u0648 \u0644\u06ce\u0634\u0645 \u0646\u0632\u06cc\u06a9 \u0645\u06d5\u06a9\u06d5\u0648\u0646\u06d5\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06d5 \u0641\u06ce\u06b5 \u0626\u06d5\u0648 \u0644\u06d5 \u0628\u0627\u0628\u06cc \u062f\u06d5\u0633\u062a\u06ce\u0646\u06cc\u0646 \u0648 \u0628\u06ce \u062e\u06d5\u0645 \u0628\u06d5\u060c \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u062e\u0648\u06b5\u0627\u0645\u0627\u0646\u06cc \u062e\u06c6\u06cc \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f: \u0626\u06d5\u0648 \u062f\u0631\u0627\u0648\u06d5\u06cc \u06a9\u06d5 \u062f\u0627\u0648\u06cc\u0627\u0646\u06d5\u060c \u0628\u06cc\u062e\u06d5\u0646\u06d5 \u0646\u0627\u0648\u0628\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u061b \u0634\u0627\u06cc\u06d5\u062a \u0626\u06d5\u06af\u06d5\u0631 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646\u060c \u0628\u06cc\u0646\u0627\u0633\u0646\u06d5\u0648\u06d5\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0626\u06d5\u0645\u062c\u0627\u0631\u06d5\u0634 \u0628\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u06a9\u06d5 \u0686\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u0628\u0627\u0648\u06d5\u06cc\u0627\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0627\u06a4\u06c6! \u0644\u06d5 \u067e\u06ce\u0648\u0627\u0646\u06d5 \u0628\u06ce \u0628\u06d5\u0634 \u06a9\u0631\u0627\u06cc\u0646\u061b \u0633\u0627 \u062a\u06c6 \u0628\u0631\u0627\u0646 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u0695\u06d5\u0648\u0627\u0646\u06d5\u06a9\u06d5\u061b \u062f\u06d5\u0634\u067e\u06ce\u0648\u06cc\u0646 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0634\u06cc \u0644\u06ce\u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u062c\u0627\u0631\u06cc \u067e\u06ce\u0634\u0648\u0648 \u0628\u0631\u0627\u06a9\u06d5\u06cc\u0645 \u067e\u06ce\u062a\u0627\u0646 \u0633\u067e\u0627\u0631\u062f\u060c \u0626\u06d5\u0648\u06cc\u0634 \u0628\u0633\u067e\u06ce\u0631\u0645 \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u061f \u0686\u0627\u06a9\u062a\u0631 \u067e\u0627\u0631\u06ce\u0632\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06b5\u0633\u06c6\u0632\u0627\u0646 \u062f\u06b5\u0633\u06c6\u0632\u062a\u0631\u06d5.',
    '\u06a9\u06d5 \u0628\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u060c \u062f\u0631\u0627\u06a4\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06d5\u0648\u06d5 \u06a9\u06d5 \u0628\u06c6\u06cc\u0627\u0646 \u0646\u0627\u0631\u062f\u0631\u0627\u0628\u06c6\u0648\u06d5. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0627\u06a4\u06c6! \u0686\u0645\u0627\u0646 \u062f\u06d5\u0648\u06cc\u061f \u0626\u06d5\u0645\u06d5 \u062f\u06d5\u0632\u0645\u0627\u06cc\u06d5\u06a9\u06d5\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u0628\u06c6\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5. \u0628\u0698\u06cc\u0648\u06cc \u062e\u06ce\u0632\u0627\u0646\u0645\u0627\u0646 \u062f\u06ce\u0646\u06cc\u0646\u061b \u0628\u0631\u0627\u06a9\u06d5\u0634\u0645\u0627\u0646 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u06cc\u0646\u061b \u067e\u06ce\u0648\u0627\u0646\u06d5\u06cc \u067e\u0634\u062a \u0648\u0634\u062a\u0631\u06ce\u06a9\u06cc\u0634 \u0632\u06ce\u062f\u06d5 \u062f\u06ce\u0646\u06cc\u0646\u061b \u0626\u06d5\u0645 \u067e\u06ce\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0647\u0646\u062f\u06a9\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0695\u06d5\u06af\u06d5\u06b5\u062a\u0627\u0646\u06cc \u0646\u0627\u062e\u06d5\u0645 \u062a\u0627 \u067e\u06d5\u06cc\u0645\u0627\u0646\u0645 \u0628\u06d5 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627 \u0646\u06d5\u062f\u06d5\u0646\u06ce\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u0628\u06c6\u0645 \u0628\u06ce\u0646\u0646\u06d5\u0648\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u06af\u0634 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06ce\u06a9\u0648\u0648 \u0644\u06ce\u06af\u06cc\u0631\u0627 \u0628\u06ce. \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u0627\u06cc\u06d5\u060c \u06af\u0648\u062a\u06cc: \u062e\u0648\u062f\u0627 \u0644\u06d5\u0645 \u0628\u0646\u0627\u0631\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06d5\u06a9\u0627\u0646\u0645! \u0644\u06d5 \u06cc\u06d5\u06a9 \u062f\u0631\u06af\u0627\u0648\u06d5 \u0645\u06d5\u0686\u0646\u06d5 \u0646\u0627\u0648\u060c \u0644\u06d5 \u062f\u06d5\u0631\u0648\u0627\u0632\u06d5\u06cc \u062c\u06cc\u0627\u062c\u06cc\u0627\u0648\u06d5 \u0628\u0686\u0646\u06d5 \u0698\u0648\u0648\u0631\u061b \u0626\u06d5\u0645\u0646 \u0646\u0627\u062a\u0648\u0627\u0646\u0645 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9 \u0644\u06d5 \u0642\u06d5\u0632\u0627\u06cc \u062e\u0648\u0627 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u0648\u0648\u0631 \u0628\u062e\u06d5\u0645\u06d5\u0648\u06d5\u061b \u0641\u06d5\u0631\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0628\u06d5\u0633\u061b \u062e\u06c6\u0645 \u0647\u06d5\u0631 \u0628\u06d5 \u0648\u06cc \u062f\u06d5\u0633\u067e\u06ce\u0631\u0645 \u0648 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u06cc\u0634 \u06a9\u06d5 \u0626\u06d5\u0648 \u0628\u06d5 \u06a9\u0627\u0631\u0633\u0627\u0632 \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u0628\u0627 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0628\u0633\u067e\u06ce\u0631\u0646.',
    '\u06a9\u0627\u062a\u06ce \u0644\u06d5\u0648 \u062c\u06ce\u06cc - \u06a9\u06d5 \u0628\u0627\u0628\u06cc\u0627\u0646 \u0695\u0627\u06cc \u0633\u067e\u0627\u0631\u062f\u0628\u0648\u0648\u0646 - \u0686\u0648\u0648\u0646\u06d5 \u0698\u0648\u0648\u0631\u06ce\u060c \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u06cc\u0627\u0646 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc \u0644\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062f\u0648\u0648\u0631 \u0646\u06d5\u062e\u0633\u062a\u06d5\u0648\u06d5\u060c \u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u0628\u06c6 \u0646\u06cc\u0627\u0632\u06ce\u06a9 \u0628\u0648\u0648 \u06a9\u06d5 \u0644\u06d5 \u062f\u06b5\u06cc \u06cc\u0627\u0642\u0648\u0648\u0628\u062f\u0627 \u0628\u0648\u0648 \u067e\u06ce\u06a9\u06cc \u0647\u06ce\u0646\u0627\u061b \u0626\u06d5\u0648\u06cc\u0634 \u0628\u06c6 \u062e\u06c6\u06cc \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0641\u06ce\u0631\u0645\u0627\u0646 \u06a9\u0631\u062f \u0632\u0627\u0646\u0627\u06cc\u06d5\u06a9 \u0628\u0648\u0648\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0632\u06c6\u0631\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0644\u06ce\u06cc \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u06a9\u0627\u062a\u06ce \u0644\u06d5 \u0645\u0627\u06b5\u06d5\u06a9\u06d5\u06cc \u06cc\u0648\u0648\u0633\u0641 \u0686\u0648\u0648\u0646\u06d5 \u0647\u0648\u0646\u062f\u0648\u0631\u060c \u0628\u0631\u0627\u06a9\u06d5\u06cc \u062e\u0633\u062a\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u06cc \u0648 \u06af\u0648\u062a\u06cc: \u0645\u0646 \u0628\u0631\u0627\u06cc\u06d5\u06a9\u06d5\u06cc \u062a\u06c6\u0645\u061b \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0648\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f \u062e\u06d5\u0645\u06af\u06cc\u0646 \u0645\u06d5\u0628\u06d5.',
    '\u06af\u0627\u06a4\u0627 \u0628\u0627\u0631\u06cc \u0628\u06c6 \u0628\u0627\u0631 \u06a9\u0631\u062f\u0646\u061b \u0632\u06d5\u0631\u06a9\u0627 \u0626\u0627\u0648\u06ce\u06cc \u062e\u0633\u062a\u06d5 \u062f\u0648\u0695\u0628\u0627\u0631\u06cc \u0628\u0631\u06a9\u06d5\u06cc\u061b \u067e\u0627\u0634\u0627\u0646 \u062c\u0627\u0695\u0686\u06cc\u06a9 \u062c\u0627\u0695\u06cc \u0695\u0627\u0647\u06ce\u0634\u062a: \u06a9\u0627\u0631\u0648\u0627\u0646\u06cc\u0646\u06d5! \u0626\u06ce\u0648\u06d5 \u062f\u0632\u06cc\u062a\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0695\u0648\u0648\u06cc\u0627\u0646 \u062a\u06ce\u06a9\u0631\u062f\u0646 \u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0648\u06d5 \u0686\u06cc\u062a\u0627\u0646 \u0644\u06ce \u06af\u0648\u0645 \u0628\u0648\u0648\u06d5\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0634\u06d5\u0631\u0628\u06d5\u06cc \u0626\u0627\u0648\u06cc \u0634\u0627\u0645\u0627\u0646 \u0644\u06ce \u06af\u0648\u0645 \u0628\u0648\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06cc \u0647\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0628\u0627\u0631\u06d5 \u062d\u0648\u0634\u062a\u0631\u06ce\u06a9\u06cc \u0647\u06d5\u06cc\u06d5\u061b \u0645\u0646\u06cc\u0634 \u062f\u06d5\u0633\u062a\u06d5\u0628\u06d5\u0631\u06cc \u062f\u06d5\u0628\u0645.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 - \u062e\u06c6\u0648 \u062f\u06d5\u0632\u0627\u0646\u0646 - \u0626\u06ce\u0645\u06d5 \u0628\u06c6\u06cc\u06d5 \u0646\u06d5\u0647\u0627\u062a\u06af\u06cc\u0646 \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0628\u06a9\u06d5\u06cc\u0646 \u0648 \u062f\u0632\u06cc \u0644\u06d5 \u0645\u06d5 \u0695\u0648\u0648\u06cc \u0646\u06d5\u062f\u0627\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u062f\u0631\u06c6\u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u0633\u0648\u0648\u0631\u06d5\u0648\u06d5 \u0628\u0648\u0648\u060c \u0633\u0632\u0627\u0648 \u0686\u06cc\u06cc\u06d5\u061f',
    '\u0648\u062a\u06cc\u0627\u0646: \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5 \u0628\u0627\u0631\u06cc\u062f\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u0627\u060c \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc \u0633\u0632\u0627\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc\u060c \u06a9\u06d5 \u0633\u0632\u0627\u062f\u0627\u0646\u06cc \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u067e\u06ce\u0634 \u0628\u0627\u0631\u06cc \u0628\u0631\u0627\u06a9\u06d5\u06cc \u0628\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u067e\u0634\u06a9\u0646\u06cc\u061b \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u0628\u0627\u0631\u06cc \u0628\u0631\u0627\u06a9\u06d5\u06cc \u0647\u06ce\u0646\u0627\u06cc\u06d5\u062f\u06d5\u0631. \u0626\u0627\u0648\u0627 \u0641\u06ce\u06b5\u0645\u0627\u0646 \u0628\u06c6 \u06cc\u0648\u0648\u0633\u0641 \u06a9\u0631\u062f. \u0644\u06d5 \u06cc\u0627\u0633\u0627\u06cc \u0626\u06d5\u0648 \u067e\u0627\u062f\u0634\u0627\u06cc\u06d5\u062f\u0627 \u0646\u06d5\u062f\u06d5\u0628\u0648\u0648\u0627\u06cc\u06d5 \u0628\u0631\u0627\u06a9\u06d5\u06cc \u06af\u0644 \u0628\u062f\u0631\u0627\u06cc\u06d5\u0648\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u062e\u0648\u062f\u0627 \u0648\u0627 \u062d\u06d5\u0632 \u0628\u06a9\u0627. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0626\u06ce\u0645\u06d5 \u062d\u06d5\u0632 \u06a9\u06d5\u06cc\u0646 \u067e\u0644\u06d5\u067e\u0644\u06d5 \u0628\u0627\u06b5\u0627\u06cc \u062f\u06d5\u0628\u06d5\u06cc\u0646. \u0644\u06d5 \u0628\u0627\u0646 \u0647\u06d5\u0645\u0648\u0648 \u0632\u0627\u0646\u0627\u06cc\u06ce \u0632\u0627\u0646\u0627\u062a\u0631\u06ce\u06a9 \u0647\u06d5\u0633.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u0626\u06ce\u0633\u062a\u0627\u06a9\u06d5 \u062f\u0632\u06cc \u062f\u06d5\u06a9\u0627\u060c \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u0634\u062f\u0627 \u0628\u0631\u0627\u06cc\u06d5\u06a9\u06cc \u0626\u06d5\u0648 \u062f\u0632\u06cc \u06a9\u0631\u062f. \u06cc\u0648\u0648\u0633\u0641 \u0626\u06d5\u0648\u06d5\u06cc \u062f\u06d5\u062f\u06b5 \u06af\u0631\u062a \u0648 \u0644\u06d5\u0648\u0627\u0646\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0646\u06d5\u06a9\u0631\u062f. \u06af\u0648\u062a\u06cc: \u062d\u0627\u06b5\u06cc \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648\u0627\u0646 \u062e\u0631\u0627\u062a\u0631\u06d5\u061b \u0626\u06d5\u0648 \u0628\u0627\u0633\u06d5\u0634 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u062e\u0648\u0627 \u0644\u06d5\u0646\u06af\u06c6\u06cc \u0686\u0627\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u0639\u06d5\u0632\u06cc\u0632! \u0628\u0627\u0648\u06a9\u06ce\u06a9\u06cc \u0632\u0648\u0631\u0647\u0627\u0646\u06cc \u0647\u06d5\u0633\u061b \u0644\u06d5 \u062c\u06ce\u06af\u06d5\u06cc \u0626\u06d5\u0648 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u06af\u0644 \u062f\u06d5\u0648\u06d5\u061b \u062a\u06c6 \u062f\u06d5\u0628\u06cc\u0646\u06cc\u0646 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u062e\u06ce\u0631\u06d5\u0648\u0645\u06d5\u0646\u062f\u0627\u0646\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0646\u0627 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u06a9\u06d5\u0633\u06ce \u06a9\u0627\u06b5\u0627\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06c6\u0632\u06cc\u0648\u06d5\u062a\u06d5\u0648\u06d5\u060c \u06af\u0644 \u062f\u06d5\u06cc\u0646\u06d5\u0648\u06d5. \u06af\u06d5\u0631 \u0648\u06d5\u0628\u06a9\u06d5\u06cc\u0646\u060c \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631\u0627\u0646\u06cc\u0646.',
    '\u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0644\u06ce\u06cc \u0646\u0627\u0647\u0648\u0645\u06ce\u062f \u0628\u0648\u0648\u0646\u060c \u0628\u06c6\u0633\u0631\u062a\u06d5 \u0686\u0648\u0648\u0646\u06d5 \u06a9\u06d5\u0646\u0627\u0631\u06ce\u06a9. \u06af\u06d5\u0648\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06cc\u062f\u0627\u06cc\u06d5: \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u062a\u0627\u0646\u0632\u0627\u0646\u06cc\u0648\u06d5 \u06a9\u06d5 \u0628\u0627\u0648\u06a9\u062a\u0627\u0646 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06ce\u06a9\u06cc \u0628\u06d5 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627 \u0644\u06ce \u0633\u0627\u0646\u062f\u0646\u061b \u0644\u06d5\u0648\u06d5 \u067e\u06ce\u0634\u06cc\u0634 \u062e\u0631\u0627\u067e\u06d5\u0648 \u0644\u06d5 \u0695\u0627\u062f\u06d5\u0628\u06d5\u062f\u06d5\u0631 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u06cc\u0648\u0648\u0633\u0641 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061f \u0633\u0627 \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5 \u062f\u06d5\u0631\u0646\u0627\u06a9\u06d5\u0648\u0645 \u0647\u06d5\u062a\u0627 \u0628\u0627\u0628\u0645 \u0626\u06cc\u0632\u0646\u0645 \u062f\u06d5\u062f\u0627\u060c \u06cc\u0627\u0646 \u062e\u0648\u0627 \u0628\u0695\u06cc\u0627\u0631\u0645 \u0628\u06c6 \u062f\u06d5\u062f\u0627\u061b \u0626\u06d5\u0648 \u062e\u06c6\u06cc \u0686\u0627\u06a9\u062a\u0631 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u0626\u06ce\u0648\u06d5 \u0628\u0686\u0646\u06d5 \u0644\u0627\u06cc \u0628\u0627\u0628\u062a\u0627\u0646\u061b \u0628\u06ce\u0698\u0646: \u0628\u0627\u0628\u06c6! \u06a9\u0648\u0695\u06d5\u06a9\u06d5\u062a \u062f\u0632\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u0634\u0627\u06cc\u06d5\u062a\u06cc\u0634\u0645\u0627\u0646 \u0646\u06d5\u062f\u0627 \u0645\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0632\u0627\u0646\u06cc\u0645\u0627\u0646\u061b \u0626\u0627\u06af\u0627\u0634\u0645\u0627\u0646 \u0644\u06d5 \u0646\u06d5\u062f\u06cc\u0627\u0631 \u0646\u06d5\u0628\u0648\u0648.',
    '\u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645\u06cc \u0626\u06d5\u0648 \u0634\u0627\u0631\u06d5 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5\u06cc \u0644\u06ce \u0628\u0648\u0648\u06cc\u0646 \u0644\u06d5\u0648 \u06a9\u0627\u0631\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u0647\u0627\u062a\u06cc\u0646\u06d5\u0648\u06d5\u060c \u067e\u0631\u0633\u06cc\u0627\u0631 \u0628\u06a9\u06d5\u061b \u0626\u06ce\u0645\u06d5 \u0628\u06ce\u0633\u06c6 \u0695\u0627\u0633\u062a \u062f\u06d5\u0628\u06ce\u0698\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0646\u06d5\u062e\u06ce\u0631\u060c \u0647\u06d5\u0648\u0627\u06cc \u062f\u06b5\u062a\u0627\u0646 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u0628\u06d5\u0631 \u062e\u06d5\u0645\u06b5\u0627\u0646\u062f\u0648\u0648\u0646. [\u062f\u06cc\u0633\u0627\u0646\u06d5\u06a9\u06d5] \u062e\u06c6\u0695\u0627\u06af\u0631\u06cc \u0628\u06ce \u0646\u0627\u0634\u06a9\u0648\u0631\u06cc\u061b \u0626\u06d5\u0648\u06d5 \u062e\u0648\u0627\u06cc\u06d5 \u0628\u06d5\u0634\u06a9\u0648\u0648 \u06af\u0634\u062a\u06cc\u0627\u0646 \u0628\u0647\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0645\u0646. \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06cc\u062a\u0631 \u0695\u0648\u0648\u06cc \u0644\u06d5\u0648\u0627\u0646 \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627 \u0648 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u062f\u0627\u062e\u0645 \u0628\u06c6 \u06cc\u0648\u0648\u0633\u0641 \u0648 \u0644\u06d5 \u0633\u06c6\u06cc \u06cc\u0627\u0646\u06cc \u062a\u0627\u0646\u06d5 \u067e\u06d5\u0695\u06cc\u06d5 \u0633\u06d5\u0631\u0686\u0627\u0648\u0627\u0646\u06cc \u0648 \u0647\u06d5\u0631\u0648\u0627 \u067e\u06cc\u0634\u06cc \u062f\u06d5\u062e\u0648\u0627\u0631\u062f\u06d5\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5\u060c \u062a\u06c6 \u0642\u06d5\u062a \u06cc\u0648\u0648\u0633\u0641 \u0644\u06d5 \u0628\u06cc\u0631 \u0646\u0627\u06a9\u06d5\u06cc\u061b \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0646\u06d5\u062e\u06c6\u0634\u062a \u062e\u0627 \u06cc\u0627\u0646 \u0628\u062a\u06a9\u0648\u0698\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u06af\u0627\u0632\u0646\u062f\u06d5 \u0648 \u062f\u06b5\u0645\u06d5\u0646\u062f\u06cc\u0645 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0626\u06d5\u062f\u0631\u06a9\u06ce\u0646\u0645 \u0648 \u0645\u0646 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u06af\u06d5\u0644\u06ce \u0634\u062a\u06cc \u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u0645 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06ce\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0646\u06cc\u0646.',
    '\u0695\u06c6\u06b5\u06d5\u06a9\u0627\u0646\u0645! \u0628\u0695\u06c6\u0646\u060c \u0628\u06d5\u06b5\u06a9\u06d5 \u0633\u06c6\u0631\u0627\u063a\u06ce\u06a9\u06cc \u06cc\u0648\u0648\u0633\u0641 \u0648 \u0628\u0631\u0627\u06a9\u06d5\u06cc \u0628\u06a9\u06d5\u0646\u061b \u0646\u0627\u0647\u0648\u0645\u06ce\u062f\u06cc\u0634 \u0645\u06d5\u0628\u0646 \u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u0627 \u0646\u0627\u0647\u0648\u0645\u06ce\u062f\u0646.',
    '\u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u06af\u06d5\u06cc\u0634\u062a\u0646\u06d5 \u0644\u0627\u06cc\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u0639\u06d5\u0632\u06cc\u0632! \u0647\u06d5\u0645 \u062e\u06c6\u0645\u0627\u0646 \u0647\u06d5\u0645 \u062e\u06ce\u0632\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5 \u0628\u06ce \u0628\u0698\u06cc\u0648\u06cc \u06a9\u06d5\u0644\u06d5\u0644\u0627\u06cc\u0646\u061b \u06a9\u06d5\u0645\u06ce\u06a9\u0645\u0627\u0646 \u062f\u0631\u0627\u0648 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0628\u06cc\u06a9\u06d5 \u0633\u06d5\u062f\u06d5\u0642\u06d5\u06cc \u0633\u06d5\u0631\u06cc \u062e\u06c6\u062a\u060c \u067e\u06ce\u0648\u0627\u0646\u06d5\u06cc \u062a\u06d5\u0648\u0627\u0648\u0645\u0627\u0646 \u062f\u06d5\u06cc\u06d5\u061b \u062e\u0648\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06ce\u0631\u06d5\u0648\u0645\u06d5\u0646\u062f\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u0646\u0627\u06a9\u0627.',
    '\u06af\u0648\u062a\u06cc: \u0626\u0627\u062e\u06c6 \u067e\u06ce\u0648 \u0632\u0627\u0646\u06cc\u0648\u06d5 \u0686\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631 \u06cc\u0648\u0648\u0633\u0641 \u0648 \u0628\u0631\u0627\u06a9\u06d5\u06cc \u0647\u06ce\u0646\u0627\u061f \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0646\u06d5\u0632\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u0627 \u062a\u0648 \u062e\u0648\u0627 \u0628\u06d5\u0695\u0627\u0633\u062a\u06d5! \u06cc\u0648\u0648\u0633\u0641 \u062e\u06c6\u062a\u06cc\u061f \u0648\u062a\u06cc: \u0628\u06d5\u06b5\u06ce\u060c \u062e\u06c6\u0645 \u06cc\u0648\u0648\u0633\u0641\u0645 \u0648 \u0628\u0631\u0627\u06a9\u06d5\u0634\u0645 \u0626\u06d5\u0648\u06d5\u062a\u0627\u0646\u06ce. \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631 \u0645\u0646\u06d5\u062a\u06cc \u06a9\u0631\u062f\u06cc\u0646\u061b \u0686\u0648\u0646\u06a9\u0648\u0648 \u0647\u06d5\u0631\u06a9\u06ce \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632 \u0648 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06ce\u060c \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u0627\u06a9\u0627\u0631 \u067e\u06d5\u0633\u0646\u062f\u0627\u0646 \u0648\u0646\u062f\u0627 \u0646\u0627\u06a9\u0627.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0648\u06d5\u062e\u0648\u062f\u0627 \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5\u060c \u062e\u0648\u0627 \u062a\u06c6\u06cc - \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u0626\u06ce\u0645\u06d5 \u2013 \u0633\u06d5\u0631\u062e\u0633\u062a\u0648\u0648\u06d5 \u0648 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u062e\u06d5\u062a\u0627\u06a9\u0627\u0631\u0627\u0646 \u0628\u0648\u0648\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0695\u06c6 \u06af\u0627\u0632\u0646\u062f\u06d5 \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06d5\u0645\u0627\u0648\u06d5\u061b \u062e\u0648\u062f\u0627 \u062e\u06d5\u062a\u0627\u062a\u0627\u0646 \u062f\u06d5\u067e\u06c6\u0634\u06ce \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06d5 \u06af\u0634 \u0628\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u06d5\u06a9 \u062f\u06b5\u0633\u06c6\u0632\u062a\u0631\u06d5.',
    '\u0626\u06d5\u0645 \u06a9\u0631\u0627\u0633\u06d5\u06cc \u0645\u0646 \u0628\u06d5\u0631\u0646\u06d5\u0648\u06d5\u060c \u0628\u06cc\u062e\u06d5\u0646\u06d5 \u0633\u06d5\u0631 \u062f\u06cc\u0645\u06cc \u0628\u0627\u0628\u06d5\u0645\u061b \u0686\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc \u0686\u0627 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5. \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646\u0645 \u0628\u06c6 \u0628\u06ce\u0646\u0646.',
    '\u0648\u06d5\u062e\u062a\u06ce \u06a9\u0627\u0631\u0648\u0627\u0646 \u0695\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0648\u0648\u060c \u0628\u0627\u0628\u06cc\u0627\u0646 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0646\u06d5\u06cc\u0698\u0646 \u0647\u0627\u062a\u06d5\u0631\u0627\u0646 \u067e\u0627\u062a\u06d5\u0631\u0627\u0646\u06cc \u0648\u062a\u060c \u0645\u0646 \u0628\u06c6\u0646\u06cc \u06cc\u0648\u0648\u0633\u0641\u0645 \u0628\u06c6 \u062f\u06ce.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06d5 \u062e\u0648\u0627\u060c \u062a\u06c6 \u0647\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u0695\u06ce \u0646\u06d5\u0632\u0627\u0646\u06cc\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u062a\u06cc.',
    '\u06a9\u06d5 \u0645\u0632\u06af\u06ce\u0646\u06cc\u062f\u06d5\u0631 \u0647\u0627\u062a\u06d5 \u0628\u06d5\u0631\u060c \u0647\u0627\u0648\u06cc\u062a\u06cc\u06d5 \u0633\u06d5\u0631 \u062f\u06d5\u0645\u0648\u0686\u0627\u0648\u06cc\u061b \u0686\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc \u0686\u0627\u06a9 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5. \u06af\u0648\u062a\u06cc: \u0645\u0646 \u0646\u06d5\u0648\u0627\u062a\u0645 \u067e\u06ce\u062a\u0627\u0646\u060c \u0626\u06d5\u0648\u06cc \u0645\u0646 \u0644\u06d5 \u062e\u0648\u0627\u06cc \u062f\u06d5\u0632\u0627\u0646\u0645\u060c \u0644\u06d5 \u0632\u0627\u0646\u0633\u062a\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u0648\u0648\u0631\u06d5\u061f',
    '\u0648\u062a\u06cc\u0627\u0646: \u0628\u0627\u06a4\u06c6! \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5\u060c \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a \u062f\u0627\u0648\u0627 \u06a9\u06d5 \u0644\u06ce\u0645\u0627\u0646 \u062e\u06c6\u0634 \u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0647\u06d5\u0631 \u0632\u0648\u0648 \u062f\u0627\u0648\u0627 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062e\u06c6\u0645 \u062f\u06d5\u06a9\u06d5\u0645 \u06a9\u06d5 \u0639\u0627\u0641\u0648\u0648\u062a\u0627\u0646 \u0628\u06a9\u0627 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0648\u06d5\u062e\u062a\u06ce \u06af\u06d5\u0647\u0627\u0646\u06d5 \u062c\u06d5\u0645 \u06cc\u0648\u0648\u0633\u0641\u060c \u062f\u0627\u06a9 \u0648 \u0628\u0627\u0628\u06cc \u0628\u0631\u062f\u06d5 \u0646\u06a9 \u062e\u06c6\u0648 \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u0631\u0646\u06d5 \u0645\u06cc\u0633\u0631 \u0648 - \u062e\u0648\u062f\u0627 \u06cc\u0627\u0631 \u0628\u06ce - \u0647\u06cc\u0686 \u0646\u0627\u0695\u06d5\u062d\u06d5\u062a\u06cc\u0648 \u062a\u0648\u0648\u0634 \u0646\u0627\u0628\u06ce.',
    '\u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u06cc \u0648\u06d5\u0628\u0627\u0646 \u062a\u06d5\u062e\u062a\u06d5\u0648\u06d5 \u0646\u06cc\u0634\u0627\u0646\u062f \u0648 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u06a9\u0695\u0646\u06c6\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u06a9\u0631\u062f. \u06af\u0648\u062a\u06cc: \u0628\u0627\u06a4\u06c6! \u0626\u06d5\u0645\u06d5 \u062e\u06d5\u0648\u0646\u06d5\u06a9\u06d5\u06cc \u067e\u06ce\u0634\u0648\u0648\u0645\u06d5\u061b \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645 \u0626\u0627\u06a9\u0627\u0645\u06d5\u06a9\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc \u0645\u0646 \u0648 \u0628\u0631\u0627\u06a9\u0627\u0646\u0645 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u06a9\u0631\u062f\u060c \u0632\u06c6\u0631 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5 \u06a9\u0631\u062f\u0648\u0648\u0645\u061b \u0644\u06d5 \u0632\u06cc\u0646\u062f\u0627\u0646\u06cc \u062f\u06d5\u0631\u0647\u06ce\u0646\u0627\u0645 \u0648 \u0647\u0646\u06af\u06c6\u0634\u06cc \u0644\u06d5\u0648 \u0633\u0627\u0631\u0627\u06cc\u06d5\u0648\u06d5 \u0647\u0627\u0648\u0631\u062f\u06d5\u0648\u06d5. \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u060c \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u06a9\u0627 \u062f\u06b5\u0627\u0648\u0627\u06cc\u06d5 \u0648 \u0628\u06c6 \u062e\u06c6\u06cc \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u0645! \u0644\u06d5\u0645 \u0648\u06b5\u0627\u062a\u06d5 \u062a\u06c6 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u062a \u067e\u06ce\u062f\u0627\u0648\u0645 \u0648 \u0641\u06ce\u0631\u062a \u06a9\u0631\u062f\u0645 \u0686\u0644\u06c6\u0646 \u062e\u06d5\u0648\u0646\u0627\u0646 \u0644\u06ce\u06a9\u062f\u06d5\u0645\u06d5\u0648\u06d5. \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc! \u0647\u06d5\u0631 \u062a\u06c6\u06cc \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a \u06af\u06d5\u0648\u0631\u06d5\u06cc \u0645\u0646\u06cc. \u0644\u06d5\u0633\u06d5\u0631 \u0628\u0695\u0648\u0627\u06cc \u062a\u06d5\u0648\u0627\u0648 \u0628\u06d5 \u062e\u06c6\u062a\u060c \u0628\u0645 \u0645\u0631\u06ce\u0646\u06d5 \u0648 \u0628\u0645\u06af\u06d5\u06cc\u06d5\u0646\u06d5\u0648\u06d5 \u0628\u06d5 \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0626\u06d5\u0648 \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u0627\u0646\u06d5\u06cc \u0646\u0627\u062f\u06cc\u0627\u0631\u0646 \u06a9\u06d5 \u0628\u06d5 \u062a\u06c6\u06cc \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u06cc\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u0648\u0627\u0646 \u06a9\u0627\u06a9\u06d5\u0648\u0628\u0631\u0627\u0644\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0641\u06ce\u06b5\u06ce \u0628\u06a9\u06d5\u0646\u060c \u062a\u06c6 \u0647\u06d5\u0631 \u0626\u0627\u06af\u0627\u0634\u062a \u0644\u06ce \u0646\u06d5\u0628\u0648\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0686\u06d5\u0646\u062f\u06d5 \u0632\u06c6\u0631\u06cc\u0634 \u067e\u0627\u067e\u0627\u0628\u06cc\u060c \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0628\u0695\u0648\u0627 \u0646\u0627\u0647\u06ce\u0646\u0646.',
    '\u062e\u06c6 \u062a\u06c6 \u0647\u06d5\u0631\u06af\u06cc\u0632 [\u0628\u06c6 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5] \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u062a \u0644\u06d5\u0648\u0627\u0646 \u0646\u0627\u0648\u06ce \u0648 \u0626\u06d5\u0645\u06d5 \u062a\u06d5\u0646\u06cc\u0627 \u067e\u06d5\u0646\u062f\u06ce\u06a9\u06d5 \u0628\u06c6 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627.',
    '\u0686\u06d5\u0646\u062f\u06d5 \u0632\u06d5\u062d\u0641\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06af\u06d5\u0644 \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u061b \u0628\u06d5\u0644\u0627\u06cc\u0627\u0646\u062f\u0627 \u0695\u0627\u062f\u06d5\u0628\u0631\u0646 \u0648 \u06af\u0648\u06ce\u06cc \u067e\u06ce \u0646\u0627\u062f\u06d5\u0646.',
    '\u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0628\u0695\u0648\u0627 \u0628\u06d5 \u062e\u0648\u0627 \u0646\u0627\u0647\u06ce\u0646\u0646 \u0648 \u0647\u06d5\u0631 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u06a9\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0628\u06c6\u0648\u06d5\u062f\u06c6\u0632\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0626\u0627\u0631\u062e\u06d5\u06cc\u0627\u0646\u0646 \u06a9\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062f\u0627\u067e\u06c6\u0634\u06d5\u0631\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u0627\u06cc\u0627\u061f \u06cc\u0627\u0646 \u06af\u0648\u0632\u0648\u06af\u0648\u0645\u0645\u06d5\u062a \u0633\u06d5\u06b5\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0695\u0627\u0646\u0627\u0628\u06ce\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u0647\u06d5\u0633\u062a\u06cc\u0634\u06cc \u067e\u06ce \u0646\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0645\u06d5 \u0695\u06ce\u06af\u0627\u06cc \u0645\u0646\u06d5\u061b \u0647\u06d5\u0645 \u062e\u06c6\u0645 \u0647\u06d5\u0645 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0634\u0648\u06ce\u0646\u0645 \u06a9\u06d5\u0641\u062a\u0648\u0648\u0646 - \u0628\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u0648\u06d5 - \u0628\u06c6 \u062e\u0648\u0627\u0646\u0627\u0633\u06cc\u060c \u06af\u0627\u0633\u06cc \u062f\u06d5\u06a9\u06d5\u0645. \u062e\u0648\u0627\u0634 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u067e\u0627\u06a9\u06d5 \u0648 \u062a\u0627\u06a9\u06d5 \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06cc\u0645 \u06a9\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u0645.',
    '\u067e\u06ce\u0634\u062a\u0631 \u0644\u06d5 \u062a\u06c6\u0634\u060c \u0647\u06d5\u0631 \u067e\u06cc\u0627\u0648\u0627\u0646\u06ce \u0644\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u0628\u0627\u0698\u06ce\u0695\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 - \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u062e\u06c6\u0645\u0627\u0646 - \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f. \u0628\u06c6\u0686\u06cc \u0646\u06d5\u0686\u0648\u0648\u0646 \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u06af\u06d5\u0634\u062a\u06ce \u0628\u06a9\u06d5\u0646\u060c \u062a\u0627 \u0628\u0628\u06cc\u0646\u0646 \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0686\u06c6\u0646 \u0628\u0648\u0648\u06af\u06d5\u061f \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u06ce \u062a\u0631\u0633\u0646\u060c \u0645\u0627\u06b5\u06cc \u0626\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u062e\u0627\u0633\u062a\u0631\u06d5. \u062f\u06d5\u06cc \u0633\u0627 \u0628\u06c6\u0686\u06d5 \u0647\u06d5\u0631 \u062a\u06ce \u0646\u0627\u06af\u06d5\u0646\u061f',
    '\u062a\u0627 \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0646\u0627\u0647\u0648\u0645\u06ce\u062f \u0628\u0648\u0648\u0646\u060c \u0645\u06d5\u0631\u062f\u0645\u06cc\u0634 \u0648\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06cc \u06a9\u06d5 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a. \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0648\u0627 \u062d\u06d5\u0632\u0645\u0627\u0646 \u06a9\u0631\u062f\u060c \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u0627\u0646 \u0648 \u062a\u0648\u0646\u062f \u0648 \u062a\u06cc\u0698\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5 \u0647\u06d5\u0645\u0628\u06d5\u0631 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0646\u0627\u0648\u06d5\u06af\u06d5\u0695\u06ce.',
    '\u0644\u06d5 \u0633\u06d5\u0631\u06af\u0648\u0631\u0648\u0634\u062a\u06d5\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5\u060c \u067e\u06d5\u0646\u062f \u0647\u06d5\u06cc\u06d5 \u0628\u06c6 \u0628\u06d5\u0626\u0627\u0648\u06d5\u0632\u0627\u0646. \u0648\u0627\u062a\u06d5\u06cc\u06d5\u06a9\u06cc \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u06d5 \u0646\u06cc\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 [\u0695\u0627\u0633\u062a\u06d5] \u0628\u0627\u0648\u06d5\u0695 \u062f\u06d5\u06a9\u0627 \u0628\u06d5\u0648\u06d5\u06cc \u0644\u06d5 \u0628\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06cc\u062f\u0627\u06cc\u06d5 \u0648 \u0634\u06cc \u06a9\u06d5\u0631\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06d5 \u0648 \u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u0646.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0645\u06cc\u0645\u060c \u0695\u0627. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9\u06cc \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u0646. \u0626\u06d5\u0648\u06d5\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u06d5\u0648\u06d5 \u0628\u06c6 \u062a\u06c6 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0695\u0627\u0633\u062a\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0628\u0695\u0648\u0627 \u0646\u0627\u06a9\u06d5\u0646.',
    '\u062e\u0648\u062f\u0627\u06cc\u06ce\u06a9\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06d5\u0631\u0632\u06c6 \u06a9\u0631\u062f\u060c\u060c \u0628\u06ce \u0647\u06d5\u0633\u062a\u0648\u0646\u062f\u06d5\u06a9\u060c \u06a9\u06d5 \u0644\u06ce\u0648 \u062f\u06cc\u0627\u0631\u0646\u061b \u062c\u0627 \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u0646\u06cc\u0634\u062a\u06d5 \u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a. \u062e\u06c6\u0631 \u0648 \u0647\u06d5\u06cc\u06a4\u06cc\u0634\u06cc \u062e\u0633\u062a\u06d5 \u06a9\u0627\u0631\u061b \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u062f\u06cc\u0627\u0631\u06a9\u0631\u0627\u0648 \u062f\u06ce\u0646 \u0648 \u062f\u06d5\u0686\u0646. \u062e\u06c6\u06cc \u0628\u06d5 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631 \u0695\u0627\u062f\u06d5\u06af\u0627 \u0648 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646 \u0634\u06cc \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062f\u06cc\u062f\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0628\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0645\u06cc\u0634\u06d5 \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u0695\u0627\u062e\u0633\u062a\u0648\u0648\u06d5 \u0648 \u06af\u06d5\u0644\u06ce \u06a9\u06ce\u0648\u0627\u062a \u0648 \u0695\u0648\u0648\u0628\u0627\u0631\u06cc \u0644\u06ce \u0626\u0627\u0641\u0631\u0627\u0646\u062f \u0648 \u0626\u06d5\u0648\u06af \u0634 \u0628\u06d5\u0631\u0648\u0628\u0648\u0648\u06d5 \u0632\u06c6\u0631\u06d5\u0634\u06cc - \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646\u06d5 \u062c\u0648\u0648\u062a\u06d5 - \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0628\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0634\u06d5\u0648 \u0628\u06d5\u0633\u06d5\u0631 \u0695\u06c6\u0698 \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u06ce\u0634\u06ce. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06af\u0634\u062a\u06cc \u0646\u06cc\u0634\u0627\u0646\u06d5\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0695\u0627\u062f\u06d5\u0645\u06ce\u0646\u0646.',
    '\u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u062f\u0627 \u06af\u06d5\u0644\u06ce\u06a9 \u067e\u0627\u0631\u0686\u06d5 \u0628\u06d5 \u067e\u06d5\u0646\u0627 \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u06cc\u06d5\u0648\u06d5 \u0647\u06d5\u0646. \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0628\u0627\u063a\u0627\u062a\u06cc \u0695\u06d5\u0632 \u0648 \u0632\u06d5\u0648\u06cc \u0648 \u0632\u0627\u0631 \u0648 \u062f\u0627\u0631\u062e\u0648\u0631\u0645\u0627\u06cc \u0644\u0641\u06cc \u06cc\u06d5\u06a9\u062a\u0631 \u0648 \u0644\u06ce\u06a9 \u062c\u0648\u062f\u0627\u061b \u06af\u0634 \u0644\u06d5 \u06cc\u06d5\u06a9 \u0626\u0627\u0648 \u067e\u0627\u0631\u0627\u0648 \u062f\u06d5\u0628\u0646\u061b [\u0628\u06d5\u06b5\u0627\u0645] \u062a\u0627\u0645 \u0648 \u0686\u06ce\u0698\u06d5\u06cc \u0628\u0695\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u0628\u0695\u06ce\u06a9\u06cc \u062a\u0631 \u0648\u06d5\u0633\u06d5\u0631 \u062f\u06d5\u062e\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0633\u06d5\u06cc\u0631 \u062f\u06d5\u0645\u06ce\u0646\u06cc\u060c \u0633\u06d5\u06cc\u0631 \u0648\u0627 \u0644\u06d5 \u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u062f\u0627 \u06a9\u06d5 \u062f\u06d5\u0628\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0628\u06cc\u0646\u06d5 \u0626\u0627\u062e\u060c \u0633\u06d5\u0631\u0644\u06d5\u0646\u0648\u06ce \u0633\u0627\u0632 \u062f\u06d5\u06a9\u0631\u06ce\u06cc\u0646\u06d5\u0648\u06d5\u061f \u0626\u0627 \u0626\u06d5\u0645\u0627\u0646\u06d5\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062a\u06c6\u0642\u06cc \u0626\u0627\u0633\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0645\u0644\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06cc\u0627\u0631\u06cc \u0626\u0627\u06af\u0631\u0646 \u0648 \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5\u06cc \u062a\u06ce\u062f\u0627 \u062f\u06d5\u0628\u0646.',
    '\u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u067e\u0627\u062f\u0627\u0634 \u0648 \u0686\u0627\u06a9\u06d5\u062a \u0644\u06ce \u0628\u062e\u0648\u0627\u0632\u0646\u060c \u0628\u06d5 \u0647\u06d5\u06b5\u067e\u06d5 \u0648 \u0644\u06d5\u0632\u060c \u0626\u0627\u0632\u0627\u0631\u062a \u0644\u06ce \u062f\u0627\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646. \u0628\u06d5\u0631\u0644\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0632\u06c6\u0631\u06cc \u0648\u0627 \u0628\u0648\u0648\u0646 \u062a\u0648\u0648\u0634\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u06a9\u0631\u0627\u0648\u0646\u061b \u06af\u06d5\u0631\u0686\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0633\u062a\u06d5\u0645\u06a9\u0627\u0631 \u0628\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06d5\u062e\u0634\u06d5\u0646\u062f\u06d5\u06cc\u06d5 \u0648 \u0644\u06d5 \u062a\u06c6\u06b5\u06d5\u0634\u062f\u0627 \u062a\u0648\u0646\u062f \u0648 \u062a\u06cc\u0698\u06d5.',
    '\u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u062f\u06cc \u0628\u06c6\u0686\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u06d5\u0648\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u062a\u06d5\u0648\u06cc \u0628\u06c6 \u0646\u06d5\u0647\u0627\u062a\u06af\u06d5\u061f \u062a\u06c6 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06ce\u06a9\u06cc \u0648 \u0647\u06cc\u0686\u06cc \u062a\u0631\u061b \u0628\u06c6 \u0647\u06d5\u0631 \u06af\u06d5\u0644\u06ce\u06a9 \u0644\u06d5 \u06af\u06d5\u0644\u0627\u0646\u06cc\u0634 \u0626\u06d5\u0634\u06ce \u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631\u06ce\u06a9 \u0647\u06d5\u0628\u06ce.',
    '\u0644\u06d5 \u062a\u06c6\u0644\u06cc \u0647\u06d5\u0631 \u0645\u06ce\u0686\u06a9\u06d5\u06cc\u06d5\u06a9\u062f\u0627 \u0647\u06d5\u0631\u0686\u06cc \u0647\u06d5\u06cc\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0648 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06cc \u062f\u06ce\u062a\u06d5\u0633\u06d5\u0631\u06ce \u0648 \u0686\u06cc\u0634\u06cc \u0644\u06ce \u06a9\u06d5\u0645 \u062f\u06d5\u0628\u06ce\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u0646\u06d5\u062f\u06cc\u0627\u0631\u0627\u0646 \u0648 \u0644\u06d5 \u062f\u06cc\u0627\u0631\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u062c\u06ce \u0628\u06d5\u0631\u0632\u06cc \u0647\u06d5\u0631\u06d5 \u0645\u06d5\u0632\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5\u0646\u06af\u06c6 \u067e\u06d5\u0646\u0627\u0645\u06d5\u06a9\u06cc \u0642\u0633\u06d5 \u0628\u06a9\u0627 \u0648 \u062f\u06d5\u0646\u06af \u0647\u06d5\u06b5\u0628\u0695\u06ce\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0634\u06d5\u0648\u062f\u0627 \u062e\u06c6\u067e\u06d5\u0646\u0627 \u062f\u0627 \u0648 \u0644\u06d5 \u0646\u0627\u0648 \u0695\u06c6\u0698\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u062f\u0627 \u0628\u06ce\u062a \u0648 \u0628\u0686\u06ce\u060c \u0644\u0627\u06cc \u0626\u06d5\u0645 \u06af\u0634\u062a\u06cc \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u06d5.',
    '\u0644\u06d5 \u067e\u06ce\u0634\u06cc\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06d5\u0648\u06d5\u060c \u0641\u0631\u06cc\u06d5 \u067e\u06d5\u06cc\u062c\u06c6\u0631\u06cc \u0648\u0627\u06cc \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5\u067e\u06ce\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u067e\u0627\u0631\u06ce\u0632\u0646. \u062e\u0648\u0627 \u0644\u06d5 \u0647\u06cc\u0686 \u06af\u06d5\u0644\u06ce\u06a9 \u0646\u0627\u06af\u06c6\u0695\u06ce\u060c \u062a\u0627 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06b5\u06cc\u0627\u0646 \u0646\u06d5\u06af\u06c6\u0695\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u06af\u06d5\u0631\u06d5\u06a9\u06cc \u0628\u06ce \u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627 \u0628\u0646\u060c \u0647\u06cc\u0686 \u0644\u06d5\u0645\u067e\u06d5\u0631\u06cc \u0646\u0627\u06cc\u06d5\u062a\u06d5 \u0628\u06d5\u0631 \u0648 \u062e\u0648\u0627\u0634 \u0646\u06d5\u0628\u06ce\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u0627\u0646 \u0646\u0627\u06a9\u0627.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc - \u0628\u06c6 \u062a\u0631\u0633\u0627\u0646 \u0648 \u062a\u06d5\u0645\u0627\u062f\u0627\u0631\u06cc - \u0628\u0631\u0648\u0648\u0633\u06a9\u06d5\u062a\u0627\u0646 \u0628\u06d5\u0631\u0686\u0627\u0648 \u062f\u06ce\u0646\u06ce \u0648 \u0647\u06d5\u0648\u0631\u06cc \u067e\u0695 \u062f\u0627\u062f\u06d5\u0648\u06d5\u0633\u062a\u06ce\u0646\u06ce.',
    '\u0647\u06d5\u0648\u0631\u06d5\u06a9\u0627\u0646 \u0648 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646 - \u0644\u06d5 \u0633\u0627\u0645\u06cc \u0626\u06d5\u0645 - \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646 \u062f\u06d5\u062f\u06d5\u0646. \u0647\u06d5\u0648\u0631\u06d5\u062a\u0631\u06cc\u0634\u0642\u06d5\u0634 \u062f\u06d5\u0646\u06ce\u0631\u06ce\u060c \u06a9\u06ce \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u062a\u0648\u0648\u0634\u06cc \u062f\u06d5\u06a9\u0627\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0645\u06d5\u0695 \u062e\u0648\u062f\u0627 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646\u06d5 \u0648 \u062e\u0648\u0627 \u0644\u06d5 \u062c\u0632\u06cc\u0627\u062f\u0627\u0646\u0627 \u06af\u06d5\u0644\u06ce \u062a\u0648\u0646\u062f\u0648\u062a\u06cc\u0698\u06d5.',
    '\u0646\u0632\u0627\u06cc \u0647\u06d5\u0642\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5 [\u062a\u06d5\u0646\u0647\u0627 \u0647\u0627\u0648\u0627\u0631 \u0644\u06d5 \u062e\u0648\u0627 \u0628\u06a9\u06d5\u0646]. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06af\u0627\u0633\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06d5\u0648\u06d5 \u0646\u0627\u06cc\u06d5. \u0648\u06d5\u06a9 \u06a9\u06d5\u0633\u06cc \u0648\u0627\u0646 \u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u062f\u0648\u0648\u0631 \u062f\u06d5\u0633\u062a\u06cc \u0628\u06c6 \u0626\u0627\u0648 \u0695\u0627\u0628\u062f\u06ce\u0631\u06ce \u0648 \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u0628\u06ce\u062a\u06d5 \u062f\u06d5\u0645\u06cc\u06d5\u0648\u06d5 \u0648 \u0646\u0627\u0634\u06cc\u06af\u0627\u062a\u06ce. \u0646\u0632\u0627\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u06af\u0634 \u067e\u0648\u0648\u0686\u06d5 \u0648 \u0628\u06d5\u0641\u06cc\u0695\u06c6 \u062f\u06d5\u0686\u06ce.',
    '\u0647\u06d5\u0631\u0686\u06cc\u06cc\u06d5\u06a9 \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0648\u06cc\u0646\u06d5 - \u062e\u0648\u0627\u0646\u06d5\u062e\u0648\u0627\u06cc\u06cc - \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0633\u06ce\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646\u060c \u06af\u0634\u062a\u06cc\u0627\u0646 \u0633\u0648\u0698\u062f\u06d5 \u0628\u06c6 \u062e\u0648\u0627 \u062f\u06d5\u0628\u06d5\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u06a9\u06ce\u06cc\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0646\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627. \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u06af\u06d5\u0648\u0631\u06d5\u06af\u06d5\u0644\u06ce\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646 \u0646\u06d5\u0642\u0627\u0646\u062f\u0648\u0648\u06d5\u060c \u06a9\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c \u0648 \u0632\u06cc\u0627\u0646\u06cc \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0633 \u0646\u06cc\u06cc\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u06a9\u0648\u06ce\u0631 \u0648 \u0628\u06cc\u0646\u0627 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631\u0646\u061f \u06cc\u0627\u062e\u06c6 \u062a\u0627\u0631\u06cc\u06a9\u06cc \u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u0633\u06d5\u0631\u0628\u06d5\u0633\u06d5\u0631\u0646\u061f \u06cc\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0634\u062a\u06cc \u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u0627 \u062f\u0627\u0646\u0627\u0648\u06d5 \u06a9\u06d5 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06d5\u0648 \u0686\u06ce\u06a9\u0631\u0627\u0648\u06cc\u0627\u0646 \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648\u060c \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u062f\u0631\u0648\u0633\u062a\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u062a\u06cc\u0627 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0644\u06ce \u0634\u06ce\u0648\u0627\u0648\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0647\u06d5\u06cc\u06d5\u060c \u06af\u0634\u062a\u06cc \u062f\u06d5\u0633\u06a9\u0627\u0631\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06cc\u0634 \u062a\u0627\u06a9 \u0648 \u062a\u06d5\u0646\u06cc\u0627\u06cc\u06d5 \u0648 \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u0632\u0627\u06b5\u06d5.',
    '\u0644\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u062f\u0627\u0695\u0698\u0627\u0646\u062f\u0648\u06d5\u060c \u067e\u0695\u0627\u0648\u067e\u0695\u06cc \u0628\u0627\u0631\u0633\u062a\u0627\u06cc\u06cc \u0646\u06d5\u0648\u0627\u06b5\u0627\u0646 \u062f\u06ce\u061b \u0644\u06d5\u0647\u06cc \u06a9\u06d5\u0641\u06ce\u06a9\u06cc \u0628\u06b5\u0646\u062f\u06cc \u0647\u0627\u062a\u06d5\u0633\u06d5\u0631\u060c \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0626\u06d5\u0648 \u06a9\u06d5\u0641\u06d5\u06cc \u0628\u06d5\u0647\u06c6\u06cc \u0626\u0627\u06af\u0631 \u062f\u06d5\u0645\u0627\u0646\u062f\u0646\u06ce\u060c \u062f\u06ce\u062a\u06d5 \u0633\u06d5\u0631 \u06a9\u06d5\u0631\u0633\u062a\u06d5\u06cc \u062e\u0634\u06b5 \u0648 \u0626\u06d5\u0633\u067e\u0627\u0628\u06cc \u062a\u0631. \u0626\u0627 \u0628\u06d5\u0645 \u062a\u06d5\u0631\u0632\u06d5 \u062e\u0648\u062f\u0627\u0634 \u0628\u0627\u0633\u06cc \u0695\u0627\u0633\u062a \u0648 \u062f\u06d5\u063a\u06d5\u06b5 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0695\u0648\u0648. \u062c\u0627 \u06a9\u06d5\u0641\u06d5\u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u062f\u06d5\u0686\u06ce \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u06a9\u06d5 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0647\u06d5\u0633 \u062f\u06d5\u0645\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u0627 \u0646\u0645\u0648\u0648\u0646\u06d5 \u062f\u06d5\u0647\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06cc \u062e\u0648\u062f\u0627\u0648\u06d5 \u0647\u0627\u062a\u0648\u0648\u0646\u060c \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5 \u062f\u06d5\u06a9\u0631\u06ce. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06cc\u06d5\u0648\u06d5 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u062f\u0627 \u0647\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06ce \u0648 \u0647\u06ce\u0646\u062f\u06d5\u06cc \u062f\u06cc\u0634 \u0648 \u0644\u06d5\u0628\u0627\u062a\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06cc \u0628\u062f\u06d5\u0646\u060c \u0632\u06c6\u0631 \u0628\u06d5 \u0633\u06d5\u062e\u062a\u06cc \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0628\u06d5\u0631 \u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0627\u0646 \u062c\u06ce\u06af\u0627\u06cc\u06d5\u061b \u06a9\u06d5 \u062e\u0631\u0627\u067e\u062a\u0631\u06cc\u0646 \u0626\u06d5\u0646\u0648\u0627\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u0628\u0695\u0648\u0627\u06cc \u0648\u0627\u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u06d5\u0648\u06d5 \u0628\u06c6 \u062a\u06c6 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0695\u0627\u0633\u062a\u06d5\u060c \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5\u0645 \u0628\u0627\u0631\u06d5 \u06a9\u0648\u062a \u0648 \u06a9\u0648\u06ce\u0631\u06d5\u061f \u0626\u06d5\u0645 \u0695\u0627\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u067e\u06ce\u06a9 \u062f\u06ce\u0646\u0646 \u0648 \u062a\u0627\u0632\u06d5 \u0644\u06ce\u06cc \u067e\u0627\u0634\u06af\u06d5\u0632 \u0646\u0627\u0628\u0646\u06d5\u0648\u06d5\u061b',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0647\u06d5\u0648\u062f\u0627\u06cc \u0646\u06ce\u0648\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 - \u0648\u06d5\u06a9 \u062e\u0648\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062f\u0627\u0648\u06d5 - \u0642\u0648\u062a \u0646\u0627\u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u0646 \u0648 \u0644\u06d5 \u062d\u06cc\u0633\u0627\u0628 \u06a9\u06ce\u0634\u0627\u0646\u06cc \u062f\u0698\u0648\u0627\u0631 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646\u0646\u061b',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0695\u0627\u0647\u06cc \u0695\u06d5\u0632\u0627\u06cc \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc\u0627\u0646 \u06af\u0631\u062a \u0648 - \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 - \u0644\u06d5\u0648 \u0628\u06d5\u0634\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0646\u060c \u062e\u06ce\u0631 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u062e\u0631\u0627\u067e\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0634\u06c6\u0646\u06d5\u0648\u06d5\u061b \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc \u062e\u06c6\u0634 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5.',
    '\u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u06cc\u0627\u0646 \u0648 \u0698\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u0634\u06cc\u0627\u0646 - \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u067e\u06d5\u0633\u0646\u062f \u0628\u0646 - \u0644\u06d5 \u0628\u0627\u063a\u0627\u062a\u06cc \u062f\u0648\u0648\u0631 \u0644\u06d5 \u0646\u06d5\u0645\u0627\u0646\u060c \u062f\u06d5\u0686\u0646\u06d5\u0698\u0648\u0648\u0631 \u0648 \u0641\u0631\u0634\u062a\u06d5 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u0648\u06d5 \u062f\u06d5\u0686\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0633\u0627\u06cc \u0647\u06ce\u0645\u0646\u06cc \u062f\u0627\u0628\u0646\u060c \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0648\u0648\u0646\u061b \u067e\u06cc\u0631\u06c6\u0632\u062a\u0627\u0646 \u0628\u06ce \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc \u0695\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u06a9\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u067e\u0627\u0634\u06af\u06d5\u0632 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0648\u062f\u0627\u06cc \u0646\u06ce\u0648\u0627\u0646 \u062e\u06c6 \u0648 \u062e\u0648\u062f\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u0633\u0627\u0646\u062f - \u06a9\u06d5 \u062e\u06c6\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce\u062f\u0627\u0628\u0648\u0648\u0646 \u0646\u06d5\u06cc\u067e\u0633\u06ce\u0646\u0646 - \u0648 \u0644\u06d5 \u0647\u06d5\u0631\u062f\u0627 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06ce \u0628\u06d5\u0634\u0646 \u0648 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc \u062f\u0698\u0648\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 - \u0686 \u0632\u06c6\u0631 \u0686 \u06a9\u06d5\u0645 - \u0628\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u0695\u06c6\u0632\u06cc \u062f\u06d5\u062f\u0627. \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u0698\u06cc\u0646\u06cc \u0633\u06d5\u0631\u062f\u0646\u06cc\u0627 \u062f\u06b5\u06cc\u0627\u0646 \u062e\u06c6\u0634\u06d5\u061b \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627\u0634 \u0644\u06d5\u0686\u0627\u0648 \u0698\u06cc\u0627\u0646\u06cc \u0626\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u0695\u0627\u0628\u0648\u0627\u0631\u062f\u0646\u06ce\u06a9\u06cc \u0647\u0646\u062f\u06a9\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0626\u06ce\u0698\u0646: \u0628\u0648\u0648\u0686\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06c6 \u0646\u06d5\u0647\u0627\u062a\u06af\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u06af\u0648\u0645\u0695\u0627\u06cc \u0626\u06d5\u06a9\u0627 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u0628\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0628\u06d5 \u06cc\u0627\u062f\u06cc \u062e\u0648\u0627 \u062f\u06b5\u06cc\u0627\u0646 \u0626\u0627\u0631\u0627\u0645 \u062f\u06d5\u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u06cc\u0627\u062f\u06cc \u062e\u0648\u0627 \u0626\u06c6\u0642\u0631\u06d5 \u0648 \u0626\u0627\u0631\u0627\u0645\u06cc \u062f\u06b5\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u062e\u06c6\u0634\u06cc \u0644\u06d5\u0648\u0627\u0646\u061b \u06a9\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc \u0628\u0627\u0634\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u0627\u0648\u0627 \u062a\u06c6\u0645\u0627\u0646 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648 \u06af\u06d5\u0644\u06d5\u060c \u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5\u0648\u0627\u0646 \u0632\u06c6\u0631 \u06af\u06d5\u0644\u06cc \u062a\u0631 \u0647\u0627\u062a\u0648\u0648\u0646 \u0648 \u0686\u0648\u0648\u0646\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0628\u06d5 \u062a\u06c6 \u06af\u06d5\u06cc\u0648\u06d5\u060c \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0628\u062e\u0648\u06ce\u0646\u06cc\u06d5\u0648\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627\u06cc \u0695\u06c6\u0632\u06cc \u062f\u06d5\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u06d5 \u0648 \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u06a9\u06d5\u0633 \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646\u06ce \u0646\u0627\u0628\u06ce \u0648 \u0645\u0646 \u062e\u06c6\u0645 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u062f\u06d5\u0686\u0645\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0642\u0648\u0631\u0626\u0627\u0646\u06ce \u0648\u0627\u0634 \u0647\u06d5\u0628\u0627 \u06a9\u06d5\u0698\u06cc \u0644\u06d5 \u062c\u06ce \u0644\u0627\u0628\u0631\u062f\u0628\u0627\u0646\u060c \u06cc\u0627\u0646 \u0632\u06d5\u0648\u06cc \u067e\u06ce \u0644\u06d5\u062a \u06a9\u0631\u0627\u0628\u0627\u060c \u06cc\u0627\u0646 \u0645\u0631\u062f\u0648\u0648\u06cc \u0648\u06d5\u0642\u0633\u06d5 \u0647\u06ce\u0646\u0627\u0628\u0627\u0646\u060c [\u0647\u06ce\u0645\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06ce\u0646\u0627.] \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0633 \u062e\u0648\u062f\u0627\u0646. \u0628\u06c6\u0686\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0626\u06d5\u0648\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u062d\u06d5\u0632\u06cc \u0644\u06ce \u0628\u0627\u060c \u06af\u0634 \u0645\u06d5\u0631\u062f\u0645\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0631\u062f\u061f \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631 - \u0644\u06d5\u0628\u06d5\u0631 \u06a9\u0627\u0631\u0648\u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 - \u06af\u0631\u0641\u062a\u0627\u0631\u06cc \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u06cc \u062f\u0698\u0648\u0627\u0631 \u062f\u06d5\u0628\u0646\u061b \u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u067e\u06d5\u0646\u0627 \u0645\u0627\u06b5 \u062e\u06c6\u06cc\u0627\u0646 \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u0627\u06cc\u06d5 \u062f\u06ce\u062a\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u061b \u062a\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u06ce\u062a\u06d5 \u062c\u06ce. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627\u0634 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u06c6\u06cc \u0646\u0627\u062f\u0627\u062a\u06d5 \u067e\u0627\u0634.',
    '\u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u067e\u06ce\u0634 \u062a\u06c6\u0634 \u06af\u0627\u06b5\u062a\u06d5 \u06a9\u0631\u0627. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0645 \u0645\u06c6\u06b5\u06d5\u062a \u062f\u0627 \u06a9\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646\u061b \u062a\u0627 \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u0627 \u06af\u0631\u062a\u0645\u0646\u061b \u0686\u06c6\u0646 \u062a\u06c6\u06b5\u06d5\u06cc\u06d5\u06a9\u0645 \u0644\u06ce \u0633\u0627\u0646\u062f\u0646!',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u06cc \u0686\u0627\u0648\u062f\u06ce\u0631\u06cc \u06a9\u0627\u0631\u0648\u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u06d5 [\u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062e\u06d5\u0628\u06d5\u0631\u06cc \u0644\u06d5 \u0647\u06cc\u0686 \u0646\u06cc\u06cc\u06d5]\u061f \u0634\u06d5\u0631\u06cc\u06a9 \u06af\u06d5\u0644\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0628\u0695\u06cc\u0627\u0631 \u062f\u0627\u0648\u06d5. \u0628\u06ce\u0698\u06d5 \u062f\u06d5\u0633\u0627 \u0646\u0627\u0648\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062f\u06d5\u0646. \u06cc\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u0627\u0632\u0648\u0648 \u0648\u0627\u06cc\u06d5 \u0626\u06d5\u0648 \u0644\u06d5 \u0634\u062a\u06ce\u06a9 - \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 - \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u06a9\u06d5\u0646\u060c \u06a9\u06d5\u0646\u0627\u06cc\u0632\u0627\u0646\u06ce\u061f! \u06cc\u0627 \u0646\u06d5 \u0626\u06d5\u0645\u06d5 \u0648\u062a\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u0695\u0648\u067e\u0648\u0648\u0686\u06d5\u061f \u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06d5\u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5 \u06af\u0632\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627 \u062e\u06d5\u0645\u06b5\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u0695\u06ce \u0695\u0627\u0633\u062a\u06cc\u0634 \u0644\u0627\u062f\u0631\u0627\u0648\u0646. \u0647\u06d5\u0631\u06a9\u06d5\u0634 \u062e\u0648\u062f\u0627 \u06af\u0648\u0645\u0695\u0627\u06cc \u0628\u06a9\u0627\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0646\u0627\u06a9\u0627.',
    '\u0647\u06d5\u062a\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627 \u062f\u06d5\u0698\u06cc\u0646\u060c \u0647\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06ce \u062f\u06d5\u0628\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0634 \u062f\u0698\u0648\u0627\u0631\u062a\u0631\u06d5 \u0648 \u06a9\u06d5\u0633 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u06ce.',
    '\u0626\u06d5\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u06cc \u0628\u06d5\u06b5\u06ce\u0646 \u0648\u0627\u06cc\u06d5 \u0628\u06d5\u0631 \u062e\u0648\u062f\u06ce \u062a\u0631\u0633\u0627\u0646 \u0628\u06a9\u06d5\u0648\u06ce\u060c \u062c\u06c6\u0628\u0627\u0631\u06cc \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u06c6\u0646\u060c \u062e\u0648\u0627\u0631\u062f\u06d5\u0645\u06d5\u0646\u06cc \u062f\u0648\u0627\u06cc\u06cc \u0646\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0633\u06ce\u0628\u06d5\u0631\u06cc\u0634 \u0647\u06d5\u06cc\u06d5. \u0626\u0627 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0626\u0627\u06a9\u0627\u0645\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627\u062a\u0631\u0633\u0627\u0646\u061b \u067e\u0627\u0634\u06d5\u0695\u06c6\u0698\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc\u0634 \u0626\u0627\u06af\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u06a9\u062a\u0627\u0648\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627\u0648\u0646\u060c \u0628\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0628\u06c6 \u062a\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u062f\u06b5\u06cc\u0627\u0646 \u062e\u06c6\u0634\u06d5. \u0628\u0695\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0628\u0695\u0648\u0627 \u0628\u06d5 \u0628\u0627\u0632\u06ce\u06a9\u06cc \u0646\u0627\u06a9\u06d5\u0646. \u0628\u06ce\u0698\u06d5: \u0641\u06d5\u0631\u0645\u0627\u0646\u0645 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0645 \u0648 \u06a9\u06d5\u0633\u06cc \u0644\u06ce \u0646\u06d5\u06a9\u06d5\u0645 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u061b \u0647\u0627\u0648\u0627\u0631 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u06d5\u0648 \u062f\u06d5\u0628\u06d5\u0645 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0645\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0645 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u0639\u06d5\u0631\u06d5\u0628\u06cc \u0628\u06c6 \u062a\u06c6 \u0647\u06d5\u0646\u0627\u0631\u062f. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648 \u0632\u0627\u0646\u0633\u062a\u06d5\u06cc \u0628\u06c6\u062a \u0647\u0627\u062a\u06af\u06d5\u060c \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0626\u06d5\u0645\u0627\u0646 \u06a9\u06d5\u0648\u06cc\u060c \u0626\u06cc\u062a\u0631 \u0646\u06d5\u062e\u0648\u062f\u0627 \u06cc\u0627\u0631\u062a\u06d5 \u0648 \u0646\u06d5 \u0626\u06d5\u0648\u06cc\u0634 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u062a\u06d5.',
    '\u0647\u06d5\u06b5\u0628\u06d5\u062a \u067e\u06ce\u0634 \u062a\u06c6\u0634 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u0645\u0627\u0646 \u0647\u06d5\u0646\u0627\u0631\u062f\u0648\u0648\u0646\u061b \u0695\u06ce\u06af\u06d5\u0634\u0645\u0627\u0646 \u062f\u0627\u0646 \u0698\u0646 \u0648 \u0646\u06d5\u062a\u06d5\u0648\u06d5\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0628\u0646. \u0647\u06cc\u0686 \u06cc\u06d5\u06a9 \u0644\u06d5\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06d5\u0634 \u0646\u06d5\u06cc\u0627\u0646\u062a\u0648\u0627\u0646\u06cc\u0648\u06d5 \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u0627\u060c \u0645\u06d5\u06af\u06cc\u0646 \u062e\u0648\u062f\u0627 \u0626\u06cc\u0632\u0646\u06cc \u062f\u0627\u0628\u06ce \u0648 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9 \u0648\u06d5\u062e\u062a\u06ce\u06a9\u06cc \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5.',
    '\u0644\u06d5 \u0646\u0627\u0648\u062f\u0627\u0646 \u0648 \u0647\u06ce\u06b5\u0627\u0646\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0633\u062a\u06d5 \u0628\u06d5 \u0648\u06cc\u0633\u062a\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0645\u0627\u06a9\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u062a\u06ce\u0628\u0627\u0646\u06cc\u0634 \u0648\u0627 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06d5.',
    '\u06cc\u0627\u0646 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u062a \u0644\u06d5\u0648\u06d5\u06cc \u0628\u06c6\u0645\u0627\u0646 \u0628\u0695\u06cc\u0627\u0631 \u062f\u0627\u0648\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u060c \u06cc\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u0646\u062a \u062f\u06d5\u0628\u06d5\u06cc\u0646\u06d5\u0648\u06d5. \u062a\u06c6 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u062a \u0644\u06d5\u0633\u06d5\u0631\u06d5 \u0695\u0627\u06af\u06d5\u06cc\u06ce\u0646\u06cc \u0648 \u0628\u06d5 \u062d\u06cc\u0633\u0627\u0628 \u0695\u0627\u06af\u06d5\u06cc\u0634\u062a\u0646\u06cc\u0634 \u0644\u06d5 \u0639\u06c6\u062f\u06d5\u06cc \u0645\u06d5.',
    '\u0686\u06c6\u0646 \u0647\u06ce\u0634\u062a\u0627 \u0646\u06d5\u06cc\u0627\u0646\u0632\u0627\u0646\u06cc\u0648\u06d5 \u0626\u06ce\u0645\u06d5 \u062f\u06cc\u0646 \u0648 \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 - \u0644\u06d5 \u06af\u0634 \u0644\u0627 - \u06a9\u06d5\u0645 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u06a9\u06d5\u0633 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u062f\u06d5\u0633 \u062f\u06d5\u06a9\u0627\u0631\u06cc \u062e\u0648\u062f\u0627 \u0628\u062f\u0627 \u0648 \u0632\u06c6\u0631 \u0632\u0648\u0648 \u0628\u06d5 \u062d\u06cc\u0633\u0627\u0628 \u0695\u0627\u062f\u06d5\u06af\u0627\u061f',
    '\u0626\u06d5\u0648\u0627\u0646\u06cc \u0628\u06d5\u0631\u0644\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0628\u0648\u0648\u0646\u060c \u06af\u06d5\u0644\u06ce\u06a9 \u0641\u06ce\u06b5\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06cc\u062a\u06d5\u0648\u06d5\u061b \u0641\u06ce\u06b5\u06cc\u0634 \u0647\u06d5\u0645\u0648\u0648 \u0647\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5. \u062e\u06c6\u06cc \u062f\u06d5\u0632\u0627\u0646\u06ce \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0686 \u06a9\u0627\u0631\u06ce\u06a9 \u062f\u06d5\u06a9\u0627 \u0648 \u0647\u06d5\u0631 \u0628\u06cc\u0646\u0627 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u062a\u06ce\u06af\u06d5\u06cc\u0634\u062a\u0646 \u06a9\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698 \u06a9\u06ce \u062f\u06d5\u06cc\u0628\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u067e\u06ce\u062a \u0626\u06ce\u0698\u0646: \u062a\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0646\u06cc\u062a. \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0648 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0632\u0627\u0646\u0633\u062a\u06cc \u06a9\u062a\u0627\u0648\u06cc \u0644\u06d5\u0644\u0627\u06cc\u06d5\u060c \u0628\u06c6 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0646\u06ce\u0648\u0627\u0646 \u0645\u0646 \u0648 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0633\u06d5.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0695\u0627. \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06d5 \u0628\u06c6 \u062a\u06c6\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u0647\u06d5\u062a\u0627 \u062e\u06d5\u06b5\u06a9\u06cc - \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 - \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u0648\u06d5\u06af\u0648\u06ce\u0632\u06ce\u061b \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0634\u0627\u06cc\u0627\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648 \u067e\u06d5\u0633\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u06cc.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u06af\u0634 \u0647\u06cc \u0626\u06d5\u0648\u0646. \u0648\u0627\u0648\u06d5\u06cc\u0644\u0627 \u0628\u06c6 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0644\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0632\u06c6\u0631 \u062a\u0648\u0646\u062f\u0648\u062a\u06cc\u0698.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06cc\u0646 \u0626\u06d5\u0648\u0644\u0627 \u067e\u06ce \u0628\u0627\u0634\u062a\u0631\u06d5\u060c \u062f\u06d5\u0628\u0646\u06d5 \u0644\u06d5\u0645\u067e\u06d5\u0631 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u062e\u0648\u0627\u0631\u0648\u062e\u06ce\u0686 \u0628\u06ce\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc\u062f\u0627 \u0632\u06c6\u0631 \u067e\u06ce\u0648\u06d5 \u0686\u0648\u0648\u0646.',
    '\u0647\u06cc\u0686 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0646\u06d5\u0646\u0627\u0631\u062f\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5 \u0632\u0648\u0627\u0646 \u06af\u06d5\u0644\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u061b \u062a\u0627 \u0647\u06d5\u0642\u06cc\u0627\u0646 \u0628\u06c6 \u0695\u0648\u0648\u0646 \u06a9\u0627\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u06af\u0648\u0645\u0695\u0627\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u06a9\u06ce\u0634\u06cc \u0628\u0648\u06ce \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0645\u0648\u0648\u0633\u0627\u0634\u0645\u0627\u0646 \u0648\u06ce\u0695\u0627\u06cc \u0686\u06d5\u0646\u062f \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0644\u06d5 \u062e\u06c6\u0645\u0627\u0646\u06d5\u0648\u06d5 \u0695\u0627\u0626\u06d5\u0633\u067e\u0627\u0631\u062f\u060c \u06a9\u06d5: \u0647\u06c6\u0632\u06cc \u062e\u06c6\u062a \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u0627\u06cc\u06cc \u0695\u0632\u06af\u0627\u0631 \u06a9\u06d5 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u0627\u06cc\u06cc \u0628\u06d5\u0631\u06d5\u061b \u0695\u06c6\u0698\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06cc\u0631 \u0628\u06ce\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06c6\u0695\u0627\u06af\u0631\u06ce\u06a9\u06cc \u0633\u0648\u067e\u0627\u0633\u06af\u0648\u0632\u0627\u0631 \u0632\u06c6\u0631 \u067e\u06d5\u0646\u062f\u06cc \u0628\u0627\u0634\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0645\u0648\u0648\u0633\u0627 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f: \u0626\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u06a9\u0631\u062f\u060c \u0644\u06d5 \u0628\u06cc\u0631 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u06a9\u06d5 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u062f\u0627\u0631\u0648\u062f\u06d5\u0633\u062a\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u0631\u06cc \u0647\u06ce\u0646\u0627\u0646\u061b \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u0646\u060c \u06a9\u0648\u0695\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0644\u06ce \u0633\u06d5\u0631 \u062f\u06d5\u0628\u0695\u06cc\u0646\u060c \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc\u06cc \u062f\u06d5\u0647\u06ce\u0634\u062a\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0645\u06d5\u0634 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06ce \u06af\u06d5\u0648\u0631\u06d5 \u0628\u0648\u0648 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648\u0695\u0627.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u0695\u0627\u06cc \u06af\u06d5\u06cc\u0627\u0646\u062f: \u0626\u06d5\u06af\u06d5\u0631 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0628\u0698\u06ce\u0631\u0628\u0646\u060c \u0628\u06c6\u0648 \u0632\u06cc\u0627\u062f \u062f\u06d5\u06a9\u06d5\u0645\u061b \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc \u0628\u06a9\u06d5\u0646\u060c \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u0645 \u0632\u06c6\u0631 \u062a\u0648\u0646\u062f\u0648\u062a\u06cc\u0698\u06d5.',
    '\u0645\u0648\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u062c\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0644\u06d5\u0645 \u0639\u06d5\u0631\u062f\u06d5\u06cc\u06d5 \u0646\u0627\u0633\u0648\u067e\u0627\u0633 \u0628\u0646\u060c \u062e\u0648\u0627 \u0646\u06cc\u0627\u0632\u06cc \u0628\u06d5 \u06a9\u06d5\u0633 \u0646\u06cc\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u0634\u06cc\u0627\u0648\u06cc \u062a\u0627\u0631\u06cc\u0641 \u06a9\u0631\u062f\u0646\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u062a\u0627\u0646 \u0646\u06d5\u0628\u06cc\u0633\u062a\u0648\u0648\u06d5 \u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0698\u06cc\u0627\u0648\u0646\u061f \u0648\u06d5\u06a9 \u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d \u0648 \u0647\u06c6\u0632\u06cc \u0639\u0627\u062f \u0648 \u0633\u06d5\u0645\u0648\u0648\u062f \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u0648\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u0648\u0648\u0646\u061b \u06a9\u06d5 \u062e\u0648\u0627 \u0646\u06d5\u0628\u06ce\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u0646\u0627\u06cc\u0627\u0646\u0632\u0627\u0646\u06ce. \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0632\u06c6\u0631 \u0626\u0627\u0634\u06a9\u0631\u0627\u0648\u06d5 \u0647\u0627\u062a\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646. \u06af\u0634\u062a\u06cc\u0627\u0646 \u062f\u06d5\u0633\u06cc\u0627\u0646 \u062e\u0633\u062a\u06d5 \u0646\u0627\u0648 \u062f\u06d5\u0645\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0695\u0627\u0633\u067e\u06ce\u0631\u0627\u0648\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u06cc\u0646\u061b \u0644\u06d5\u0648\u06d5\u0634 \u0626\u06ce\u0645\u06d5\u06cc \u0628\u06c6 \u0628\u0627\u0646\u06af \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0632\u06c6\u0631 \u0628\u06d5 \u06af\u0648\u0645\u0627\u0646\u06cc\u0646.',
    '\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062f\u0627\u0647\u06ce\u0646\u06cc \u0639\u06d5\u0631\u0632 \u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0628\u06d5 \u0634\u06a9\u0646\u061f \u0628\u0627\u0646\u06af\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u062a\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u062d\u0627\u0646\u0648\u0648 \u062e\u06c6\u0634 \u0628\u06ce \u0648 \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u062f\u06cc\u0627\u0631\u06a9\u0631\u0627\u0648 \u0644\u06ce\u0648 \u0695\u0627\u0648\u06d5\u0633\u062a\u06ce. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0628\u0646\u06cc\u0627\u062f\u06d5\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646\u06d5\u0646\u061b \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0645\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u060c \u0648\u06d5\u0631\u0645\u0627\u0646\u06af\u06ce\u0695\u0646\u061f \u0633\u0627 \u0647\u06d5\u0631 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06ce \u067e\u062a\u06d5\u0648\u062a\u0627\u0646 \u0644\u06d5 \u062c\u0627\u0646\u062a\u0627\u062f\u0627\u060c \u067e\u06ce\u0646 \u0628\u0646\u0648\u06ce\u0646\u0646.',
    '\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u0631\u06ce \u0626\u06ce\u0645\u06d5\u0634 \u0628\u0646\u06cc\u0627\u062f\u06d5\u0645\u06ce\u06a9\u06cc\u0646 \u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u0639\u06d5\u0628\u062f\u06ce\u06a9\u06cc \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u06a9\u0627\u060c \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06cc\u062e\u0648\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u061b \u0628\u06c6 \u0645\u06d5 \u0646\u0627\u0634\u06ce \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u0646\u06cc\u0646\u060c \u0645\u06d5\u06af\u06cc\u0646 \u062e\u06c6\u06cc \u0626\u06cc\u0632\u0646\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06ce. \u0628\u0627 \u0647\u06d5\u0631\u0686\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u062e\u06c6\u06cc\u0627\u0646 \u0628\u062f\u06d5\u0646\u06d5 \u062f\u06d5\u0633 \u062e\u0648\u062f\u0627.',
    '\u062c\u0627 \u0686\u06c6\u0646 \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0627\u0633\u067e\u06ce\u0631\u06cc\u0646\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0648\u06cc\u0646\u061f \u0647\u06d5\u0631 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc\u0634\u0645\u0627\u0646 \u0628\u062f\u06d5\u0646\u060c \u062e\u06c6\u0695\u0627\u062f\u06d5\u06af\u0631\u06cc\u0646. \u0628\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u062e\u0648\u0627 \u0628\u06d5 \u067e\u0634\u062a\u06cc\u0648\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u062e\u06c6\u06cc\u0627\u0646 \u0628\u062f\u06d5\u0646\u06d5 \u062f\u06d5\u0633 \u062e\u0648\u062f\u0627.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0698\u06cc \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u0646\u060c \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u06af\u0648\u062a: \u0626\u06ce\u0645\u06d5 \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646\u062a\u0627\u0646 \u0648\u06d5\u062f\u06d5\u0631 \u062f\u06d5\u0646\u06ce\u06cc\u0646\u060c \u06cc\u0627\u0646 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u06cc\u06cc\u0646\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u062a\u06ce\u06cc\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646: \u0626\u06ce\u0645\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9 \u0642\u0695 \u06a9\u06d5\u06cc\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0645 \u0639\u06d5\u0631\u0632\u06d5\u062f\u0627 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06ce \u062f\u0627\u062f\u06d5\u0645\u06d5\u0632\u0631\u06ce\u0646\u06cc\u0646\u061b \u0626\u06d5\u0645\u06d5\u0634 \u0644\u06d5\u0645\u06d5\u0695 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u06d5 \u06a9\u06d5 \u0644\u06d5 \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u06cc \u0645\u0646 \u0648 \u0644\u06d5 \u0647\u06d5\u0695\u06d5\u0634\u06d5\u06cc \u0645\u0646 \u062f\u06d5\u062a\u0631\u0633\u0646.',
    '\u062f\u0627\u0648\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0648\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u06a9\u0627. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u06a9\u06d5 \u0645\u0644\u0647\u0648\u0695 \u0648 \u0644\u0627\u0633\u0627\u0631\u06ce\u06a9 \u0628\u0648\u0648\u060c \u0646\u0627\u0647\u0648\u0645\u06ce\u062f \u0628\u0648\u0648\u061b',
    '\u0644\u06d5 \u062f\u0648\u0627\u0634\u06cc\u06d5\u0648\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06d5 \u0648 \u0626\u06d5\u0634\u06ce \u0632\u0648\u0648\u062e\u0627\u0648 \u0628\u062e\u0648\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0641\u0695\u06cc \u062f\u06d5\u06a9\u0627 \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5 \u0642\u06ce\u0632 \u0648 \u0628\u06ce\u0632\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u06af\u0634 \u0644\u0627\u0648\u06d5 \u0645\u06d5\u0631\u06af\u06cc \u0628\u06c6 \u062f\u06ce \u06a9\u06d5 \u0646\u0627\u0634\u0645\u0631\u06ce \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u06d5\u0698\u0627\u0646\u06cc\u0634 \u0628\u06d5 \u062f\u0648\u0627\u06cc\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062d\u0627\u0634\u0627\u062f\u06d5\u06a9\u06d5\u0646\u060c \u0646\u06d5\u0642\u06b5\u06cc\u0627\u0646 \u0646\u06d5\u0642\u06b5\u06cc \u0626\u06d5\u0648 \u0628\u0648\u06b5\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5\u0631\u06c6\u0698\u06cc \u0695\u06d5\u0634\u06d5\u0628\u0627\u062f\u0627 \u0628\u0627\u06cc \u0644\u06ce \u0647\u06d5\u06b5\u06a9\u0627. \u0644\u06d5\u0648\u06d5\u0634 \u0628\u06d5\u062f\u06d5\u0633\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9 \u0628\u0628\u06d5\u0646. \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u0627\u0646\u06ce \u06a9\u06d5 \u0632\u06c6\u0631 \u0644\u06d5 \u0695\u06ce \u0648\u06d5\u0644\u0627 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646.',
    '\u062a\u06c6 \u0646\u0627\u0628\u06cc\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc \u0645\u06d5\u0632\u0646 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0647\u06d5\u0631 \u0628\u06c6 \u0695\u0627\u0633\u062a\u06cc \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5\u061f \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06ce\u060c \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0628\u0627 \u0648 \u0633\u06d5\u0631\u0644\u06d5\u0646\u0648\u06ce \u0626\u06cc \u062a\u0631 \u0686\u06ce \u062f\u06d5\u06a9\u0627.',
    '\u06a9\u06d5 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0634 \u0644\u06d5\u0628\u06d5\u0631 \u062e\u0648\u062f\u0627 \u06af\u0631\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u06af\u0634\u062a \u0644\u06d5 \u06af\u0634\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06c6 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5. \u06a9\u06d5\u0645 \u062f\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u0628\u0627\u063a\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u0648\u0648\u06cc\u0646\u061b \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0631\u06ce \u0644\u06d5 \u0633\u0632\u0627\u062f\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u0645\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646\u061f \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0626\u06ce\u0645\u06d5\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce \u0628\u06a9\u0631\u062f\u0627\u06cc\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0645\u06d5\u0634 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u0695\u06d5\u06af\u06d5\u06b5 \u062e\u06c6 \u062f\u06d5\u062e\u0633\u062a. \u0626\u06ce\u0633\u062a\u0627 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u06cc\u06d5\u06a9\u0645\u0627\u0646 - \u0644\u06d5\u0633\u06d5\u0631\u062e\u06c6 \u0628\u06cc\u0646 \u06cc\u0627 \u0628\u06cc\u06a9\u06d5\u06cc\u0646 \u0628\u06d5 \u062f\u0627\u062f\u0648\u0647\u0627\u0648\u0627\u0631 - \u0647\u06cc\u0686 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06ce\u06a9 \u0646\u06d5\u0645\u0627\u0648\u06d5.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u06a9\u0627\u0631\u0644\u06d5\u06a9\u0627\u0631\u062a\u0631\u0627\u0632\u0627\u0646\u060c \u0634\u06d5\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06b5\u06ce: \u062e\u0648\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0695\u0627\u0633\u062a\u06cc \u067e\u06ce\u062f\u0627\u0646\u060c \u0645\u0646\u06cc\u0634 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u06c6\u0645 \u067e\u06ce\u062f\u0627\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062f\u0631\u06c6\u0645 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0646. \u0647\u06cc\u0686 \u0632\u06c6\u0631\u06ce\u06a9\u06cc\u0634\u0645 \u0628\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0646\u06d5\u0628\u0648\u0648\u061b \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648\u06d5 \u06af\u0627\u0632\u06cc\u0645 \u06a9\u0631\u062f\u0646\u060c \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u0645\u06d5\u0648\u06d5 \u0647\u0627\u062a\u0646. \u0626\u06ce\u0648\u06d5 \u0633\u06d5\u0631\u06a9\u06c6\u0646\u06d5\u06cc \u0645\u0646 \u0645\u06d5\u06a9\u06d5\u0646\u060c \u0633\u06d5\u0631\u0632\u06d5\u0646\u0634\u062a\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646\u061b \u0646\u0627\u0634\u062a\u0648\u0627\u0646\u0645 \u062f\u06d5\u0641\u0631\u06cc\u0627\u062a\u0627\u0646 \u0628\u06ce\u0645\u061b \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0628\u06d5 \u0641\u0631\u06cc\u0627\u0645 \u0628\u06af\u06d5\u0646. \u0644\u06d5\u0645\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u0645\u0646\u062a\u0627\u0646 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u0627 \u0632\u0627\u0646\u06cc\u060c \u062d\u0627\u0634\u0627\u0645 \u0647\u06d5\u06cc\u06d5. \u0628\u06ce\u06af\u0648\u0645\u0627\u0646\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631\u0646\u060c \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646\u060c \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0631\u06ce\u0646\u06d5 \u0646\u0627\u0648\u06d5\u0646\u062f \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u0695\u0648\u0648\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646 \u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u0633\u06d5\u0644\u0627\u0645\u060c \u062e\u06c6\u0634\u06cc \u0648 \u0628\u06ce\u0634\u06cc \u0644\u06ce\u06a9\u062a\u0631 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0646\u06d5\u0648\u062f\u06cc \u062e\u0648\u062f\u0627 \u0686\u0644\u06c6\u0646 \u0645\u06d5\u0633\u06d5\u0644 \u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u061f \u0642\u0633\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06d5 \u0695\u06ce \u0648 \u062c\u06ce\u060c \u0628\u06d5 \u0634\u06d5\u0646\u06af\u06d5\u062f\u0627\u0631\u06ce\u06a9 \u062f\u06d5\u0634\u0628\u0647\u06ce\u060c \u0695\u06d5\u06af\u06cc \u0642\u0627\u06cc\u0645 \u062f\u0627\u06a9\u0648\u062a\u0627\u0648\u06d5 \u0648 \u0644\u0642\u06cc \u0644\u06d5 \u062a\u06d5\u0634\u0642\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5.',
    '\u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0686\u0648\u0627\u0631 \u0641\u06d5\u0633\u06b5\u06d5 \u0645\u06cc\u0648\u06d5\u06cc \u062f\u06d5\u0628\u06ce. \u062e\u0648\u0627 \u0646\u06d5\u0632\u06cc\u0644\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u062e\u06d5\u06b5\u06a9\u06d5 \u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u067e\u06d5\u0646\u062f\u06cc \u0644\u06ce \u0648\u06d5\u0631\u06af\u0631\u0646.',
    '\u0646\u06d5\u0632\u06cc\u0644\u06d5\u06cc \u0642\u0633\u06d5\u06cc \u0646\u0627\u0628\u06d5\u062c\u06ce\u0634 \u0648\u06d5\u06a9\u0648\u0648 \u067e\u0646\u062c\u06ce\u06a9\u06cc \u0646\u0627\u062c\u0633\u0646\u06d5\u060c \u06a9\u06d5 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u0647\u06d5\u06b5\u06a9\u06d5\u0646\u0631\u0627 \u0628\u06ce \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0646\u06d5\u06af\u0631\u06ce.',
    '\u062e\u0648\u062f\u0627 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0648 \u0633\u06d5\u06b5\u0627\u062f\u0627\u060c \u0628\u06d5 \u0642\u0633\u06d5\u06cc \u067e\u062a\u06d5\u0648 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0627\u0646 \u062a\u0648\u0646\u062f\u0648\u0642\u0627\u06cc\u0645 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u0631\u06ce\u06af\u06d5 \u0644\u0627 \u062f\u06d5\u062f\u0627. \u062e\u0648\u062f\u0627 \u0686\u06c6\u0646\u06cc \u0628\u0648\u06ce \u062f\u06d5\u06cc\u06a9\u0627.',
    '\u0626\u06d5\u062f\u06cc \u0646\u06d5\u062a \u062f\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0646\u0627\u0634\u0648\u06a9\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627\u062f\u0627 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u06af\u06d5\u0644\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u0647\u06d5\u06b5\u062f\u06ce\u0631\u06cc \u0632\u06c6\u0631 \u0647\u06d5\u062a\u06d5\u0631 \u0695\u06d5\u062a\u0627\u0646\u062f\u0648\u0648\u06d5\u060c',
    '\u0644\u06d5 \u062f\u06c6\u0698\u06d5\u0647\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0686\u06c6\u0642\u06ce\u0646\u061b \u067e\u06cc\u0633\u062a\u0631\u06cc\u0646 \u0628\u0646\u06a9\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u0647\u0627\u0648\u062a\u0627\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u0648\u0627 \u0628\u0695\u06cc\u0627\u0631\u062f\u0627\u060c \u062a\u0627 [\u062e\u06d5\u06b5\u06a9\u06cc] \u0644\u06d5 \u0695\u06ce\u06cc \u06a9\u0644\u0627 \u06a9\u06d5\u0646. \u0628\u06ce\u0698\u06d5: \u062c\u0627\u0631\u06ce \u062e\u06c6\u0634 \u0695\u0627\u0628\u0648\u06ce\u0631\u0646! \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u062a\u0627\u0646 \u0626\u0627\u06af\u0631 \u062f\u06d5\u0628\u06ce.',
    '\u062a\u06c6 \u0628\u06d5 \u0639\u06d5\u0628\u062f\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u0645 \u0628\u06ce\u0698\u06d5 \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646 \u0648 \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u0628\u06d5\u0634\u0645\u0627\u0646 \u062f\u0627\u0648\u0646 - \u067e\u06d5\u0646\u0627\u0645\u06d5\u06a9\u06cc \u0648 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 - \u062e\u06ce\u0631\u06ce \u0628\u06a9\u06d5\u0646\u061b \u0628\u06d5\u0631\u0644\u06d5\u0648\u06d5 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u062f\u0627\u0628\u06ce \u06a9\u06d5 \u0646\u06d5 \u06a9\u0695\u06cc\u0646 \u0648 \u0641\u0631\u06c6\u0634\u062a\u0646\u06ce \u0644\u06d5 \u06af\u06c6\u0695\u06ce\u06cc\u06d5 \u0648 \u0646\u06d5 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc \u0648 \u0647\u06d5\u0648\u0627\u06b5\u06ce.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u062f\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u062f\u0627\u0628\u0627\u0631\u0627\u0646\u062f\u0648\u0648\u06d5\u060c \u0628\u06d5\u0631\u0648\u0628\u0648\u0648\u06cc \u0628\u0698\u06cc\u0648\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u067e\u06ce \u062f\u06d5\u0695\u0648\u06ce\u0646\u06ce\u061b \u06af\u06d5\u0645\u06cc\u06d5\u0634\u06cc \u062f\u0627\u0648\u06d5\u062a\u06d5 \u062f\u06d5\u0633\u062a\u0648\u0648 - \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u067e\u06ce\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648 - \u0644\u06d5 \u0632\u06d5\u0631\u06cc\u0627\u062f\u0627 \u0628\u06ce\u062a \u0648 \u0628\u0686\u06ce \u0648 \u0626\u06d5\u0645 \u0647\u06d5\u0645\u0648\u0648 \u0695\u0648\u0648\u0628\u0627\u0631\u0627\u0646\u06d5\u0634\u06cc \u0628\u06c6 \u0695\u0627\u0645 \u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u062e\u06c6\u0631 \u0648 \u0647\u06d5\u06cc\u06a4\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062e\u0633\u062a\u06c6\u062a\u06d5 \u0628\u06d5\u0631\u06a9\u0627\u0631\u061b \u0648\u06d5\u0633\u062a\u0627\u0646 \u0648 \u0648\u0686\u0627\u0646\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u0626\u06d5\u0645 \u0634\u06d5\u0648\u06af\u0627\u0631 \u0648 \u0695\u06c6\u0698\u06af\u0627\u0631\u06d5\u0634\u06cc \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u0645\u06c6 \u06a9\u0631\u062f.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06ce\u062a\u0627\u0646 \u062e\u0648\u0627\u0633\u062a\u0648\u0648\u06d5\u060c \u067e\u06ce\u06cc \u0695\u06d5\u0648\u0627 \u062f\u06cc\u0648\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u0646\u06d5 \u0633\u06d5\u0631 \u0698\u0645\u0627\u0631\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627\u060c \u0644\u06d5 \u0698\u0645\u0627\u0631\u062f\u0646 \u062f\u0627\u062f\u06d5\u0645\u06ce\u0646\u0646. \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u06a9\u06d5 \u0645\u0631\u06c6 \u06af\u06d5\u0644\u06ce \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631 \u0648 \u0633\u067e\u06b5\u06d5\u06cc\u06d5.',
    '\u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u06af\u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0647\u06ce\u0645\u0646\u0627\u06cc\u06cc \u0628\u062f\u06d5 \u0628\u06d5\u0645 \u0634\u0627\u0631\u06d5 \u0648 \u0645\u0646 \u0648 \u0632\u0627\u0631\u0648\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0634\u0645 \u0644\u06d5 \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u06cc \u062f\u0648\u0648\u0631\u062e\u06d5\u0648\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0626\u06d5\u0648 \u0628\u062a\u0627\u0646\u06d5 \u062e\u06d5\u06b5\u06a9\u06ce \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u0627\u061b \u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0634\u0648\u06ce\u0646 \u0645\u0646 \u06a9\u06d5\u0648\u062a\u060c \u0626\u06d5\u0648 \u0644\u06d5 \u062e\u06c6\u0645\u06d5\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06ce\u0645 \u06cc\u0627\u062e\u06cc \u0628\u0648\u0648\u060c \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u062a\u06c6 \u062e\u06d5\u062a\u0627 \u0628\u06d5\u062e\u0634\u06ce \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u0645\u0627\u0646! \u0645\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0634\u06cc\u0648\u06ce\u06a9\u06cc \u0642\u0627\u0642\u0695\u060c \u0644\u06d5 \u067e\u06d5\u0646\u0627 \u0645\u0627\u06b5\u06cc \u067e\u06cc\u0631\u06c6\u0632\u062a\u060c \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u0632\u0627\u0631\u06c6\u06b5\u06d5\u06a9\u0627\u0646\u0645 \u062c\u06ce \u06a9\u0631\u062f\u06d5\u0648\u06d5. \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0628\u06c6 \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0646\u0648\u06ce\u0698\u0627\u0646 \u0628\u06a9\u06d5\u0646. \u062f\u06d5\u0633\u0627 \u062a\u06c6\u0634 \u06a9\u0627\u0631\u06ce \u0648\u0627 \u0628\u06a9\u06d5 \u062e\u06d5\u06b5\u06a9 \u0628\u06d5 \u062f\u06b5 \u062e\u06c6\u0634\u06cc \u0628\u0648\u06ce\u0646 \u0648 \u0644\u06d5 \u0628\u0698\u06cc\u0648\u06cc\u0634 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0628\u062f\u06d5\u061b \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0634\u0648\u06a9\u0631\u06cc \u062a\u06c6 \u0628\u06a9\u06d5\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0647\u06d5\u0631\u0686\u06cc \u0646\u0647\u06ce\u0646\u06cc \u0648 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0628\u06ce\u060c \u062a\u06c6 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06cc \u0648 \u0647\u06d5\u0631\u0686\u06cc\u06cc\u06d5\u06a9 \u0647\u06d5\u06cc\u06d5 - \u0686 \u0644\u06d5 \u0639\u06d5\u0631\u0632 \u0686 \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646 - \u0647\u06cc\u0686\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627 \u06af\u0648\u0645 \u0646\u0627\u0628\u06ce.',
    '\u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 - \u0628\u06d5 \u067e\u06cc\u0631\u06cc - \u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644 \u0648 \u0626\u06cc\u0633\u062d\u0627\u0642\u06cc \u0628\u06d5 \u0645\u0646 \u0628\u06d5\u062e\u0634\u06cc\u0648\u06d5. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0646\u0632\u0627\u06cc \u0646\u0632\u0627\u06a9\u0627\u0631 \u062f\u06d5\u0628\u06cc\u0633\u06ce.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u06a9\u0627\u0631\u06ce \u0628\u06a9\u06d5 \u0645\u0646\u06cc\u0634 \u0648 \u0641\u0631\u0632\u06d5\u0646\u062f\u0627\u0646\u06cc\u0634\u0645 \u0633\u0648\u0648\u0631 \u0628\u06cc\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u0648\u06ce\u0698 \u062e\u0648\u06ce\u0646\u062f\u0646\u06ce. \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u062f\u0648\u0639\u0627\u06cc\u06d5\u06a9\u06d5\u0645 \u0642\u0628\u0648\u0648\u06b5 \u0628\u06a9\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0695\u06c6\u0698\u06cc \u0698\u0645\u0627\u0631 \u06a9\u06d5 \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u060c \u0644\u06d5 \u0645\u0646\u06cc\u0634 \u0648 \u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u0645 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06cc \u062f\u06cc\u06a9\u06d5\u0634 \u062e\u06c6\u0634 \u0628\u06d5.',
    '\u067e\u06ce\u062a \u0648\u0627\u0646\u06d5\u0628\u06ce \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06a9\u0631\u062f\u06d5\u06cc \u0632\u0627\u06b5\u0645\u0627\u0646 \u0628\u06ce \u0626\u0627\u06af\u0627\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062f\u06d5\u06cc\u0627\u0646\u0647\u06ce\u06b5\u06ce\u062a\u06d5\u0648\u06d5 \u062a\u0627 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5\u0648\u06cc\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0686\u0627\u0648 \u0645\u06c6\u0644\u06d5\u0642 \u062f\u06d5\u0648\u06d5\u0633\u062a\u0646.',
    '\u0644\u06d5 \u062a\u0631\u0633\u0627\u0646\u0627 \u0628\u06d5\u0644\u06d5\u0632 \u062f\u06d5\u0686\u0646\u061b \u0633\u06d5\u0631\u0648\u06af\u06d5\u0631\u062f\u0646\u06cc\u0627\u0646 \u0695\u06d5\u0642 \u0628\u0648\u0648\u06d5\u061b \u0686\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0646\u0627\u062a\u0631\u0648\u0648\u06a9\u06ce\u0646\u0646 \u0648 \u062f\u06b5\u06cc\u0627\u0646 \u0628\u06d5\u062a\u0627\u06b5 \u0648 \u062d\u06d5\u062a\u0627\u06b5\u06d5.',
    '\u0647\u06d5\u0695\u06d5\u0634\u06d5 \u0644\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5 \u0628\u06a9\u06d5\u060c \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u06a9\u06d5 \u062c\u0632\u06cc\u0627 \u062f\u06d5\u062f\u0631\u06ce\u0646. \u0646\u0627\u0647\u06d5\u0642\u06a9\u0627\u0631\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u062a\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0645\u06ce\u06a9 \u0645\u06c6\u06b5\u06d5\u062a\u0646 \u062f\u06d5 \u0647\u06d5\u062a\u0627 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u062a\u06d5\u0648\u06d5 \u0628\u06cc\u0646 \u0648 \u0648\u06d5\u0634\u0648\u06ce\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u06a9\u06d5\u0648\u06cc\u0646. \u0626\u0627\u062e\u0631 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648 \u0633\u0648\u06ce\u0646\u062f\u0648\u0648 \u062f\u06d5\u062e\u0648\u0627\u0631\u062f\u060c \u06a9\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0646\u0627\u0686\u0646\u061f',
    '\u0647\u06d5\u0631 \u0686\u0648\u0648\u0646\u06d5 \u062c\u06ce\u06af\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u0644\u06d5\u062e\u06c6 \u062f\u06d5\u06a9\u0631\u062f \u0648 \u062a\u06ce\u0634 \u06af\u06d5\u06cc\u0634\u062a\u0646 \u06a9\u06d5 \u0686\u06cc\u0645\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0648\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u0632\u06c6\u0631 \u0646\u06d5\u0632\u06cc\u0644\u06d5\u0634\u0645\u0627\u0646 \u0628\u06c6 \u06af\u0648\u062a\u0648\u0648\u0646.',
    '\u0645\u06d5\u06a9\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u0631 \u0647\u06ce\u0646\u0627 \u0648 \u0645\u06d5\u06a9\u0631\u06d5\u06a9\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u0648\u0648\u061b \u0647\u06d5\u0631 \u0686\u06d5\u0646\u062f \u0626\u06d5\u0648 \u0645\u06d5\u06a9\u0631\u06d5\u06cc \u0626\u06d5\u0648 \u06a9\u0631\u062f\u06cc\u0627\u0646\u060c \u0686\u06cc\u0627\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06d5\u06b5\u062f\u06d5\u062a\u06d5\u06a9\u0627\u0646.',
    '\u067e\u06ce\u062a \u0648\u0627\u0646\u06d5\u0628\u06ce \u062e\u0648\u0627 \u0644\u06d5\u0648 \u0628\u06d5\u06b5\u06ce\u0646\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u062e\u06c6\u06cc \u062f\u0627\u0648\u06d5\u060c \u0647\u06cc\u0686\u06af\u0627 \u067e\u0627\u0634\u06af\u06d5\u0632 \u062f\u06d5\u0628\u06ce\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u062a\u06c6\u06b5\u06d5\u0633\u062a\u06ce\u0646\u06d5.',
    '\u0695\u06c6\u0698\u06ce \u0632\u06d5\u0645\u06cc\u0646 \u062f\u06d5\u06af\u06c6\u0695\u062f\u0631\u06ce\u062a \u0648 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u06ce\u06a9\u06cc \u062a\u0631\u061b \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627\u061b \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06ce\u0646\u06d5 \u0628\u06d5\u0631 \u0628\u0627\u0631\u06d5\u06af\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc \u062a\u0627\u06a9 \u0648 \u062a\u06d5\u0646\u06cc\u0627 \u0648 \u0632\u0627\u06b5 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u0627.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0628\u06d5 \u0632\u06d5\u0646\u062c\u06cc\u0631 \u062c\u06d5\u0644\u06d5 \u06a9\u0631\u0627\u0648\u0646.',
    '\u067e\u06c6\u0634\u0627\u06a9\u06cc\u0627\u0646 \u0642\u06d5\u062a\u0631\u0627\u0646\u06cc \u0695\u06d5\u0634\u06d5 \u0648 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0626\u0627\u06af\u0631 \u062f\u0627\u06cc \u067e\u06c6\u0634\u06cc\u0648\u06d5.',
    '\u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc - \u0628\u06d5 \u06af\u0648\u06ce\u0631\u06d5\u06cc \u0626\u0627\u06a9\u0627\u0631\u06ce \u06a9\u0631\u062f\u06cc - \u062c\u0632\u06cc\u0627 \u0628\u062f\u0627. \u062e\u0648\u062f\u0627 - \u0628\u06d5\u0644\u06d5\u0632 - \u0628\u06d5 \u06af\u0634 \u062d\u06cc\u0633\u0627\u0628\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u0626\u06d5\u0645\u06d5 \u062c\u0627\u0631\u06ce\u06a9\u06d5 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u062a\u0627 \u0628\u06d5\u0648 \u0628\u06c6\u0646\u06d5\u0648\u06d5 \u0628\u062a\u0631\u0633\u0646 \u0648 \u0628\u06cc\u0634\u06cc\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0626\u06d5\u0648 \u062e\u0648\u0627\u06cc\u06d5\u06a9\u06cc \u06cc\u06d5\u06a9\u062a\u0627\u06cc\u06d5 \u0648 \u062a\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0644\u06ce \u0648\u06d5\u0631\u06af\u0631\u0646.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0695\u0627. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06d5\u06b5\u06af\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u0646 \u06a9\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0648 \u0686\u0627\u06a9 \u0648 \u062e\u0631\u0627\u067e \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u0647\u06cc\u0686 \u062f\u0648\u0648\u0631 \u0646\u06cc\u06cc\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0695\u06c6\u0698\u06ce \u0628\u06ce\u0698\u0646: \u062e\u06c6\u0632\u06cc \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646 \u0628\u0648\u0648\u06cc\u0646\u0627\u06cc\u06d5.',
    '\u0644\u06ce\u06cc\u0627\u0646 \u06af\u06d5\u0695\u06ce \u0628\u062e\u06c6\u0646 \u0648 \u062e\u06c6\u0634 \u0695\u0627\u0628\u0628\u0648\u06ce\u0631\u0646 \u0648 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u0633\u06d5\u0631\u06af\u06d5\u0631\u0645\u06cc\u0627\u0646 \u0628\u06a9\u0627\u061b \u0632\u06c6\u0631 \u0646\u0627\u062e\u0627\u06cc\u06d5\u0646\u06ce \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u0647\u06cc\u0686 \u0634\u0627\u0631\u06ce\u06a9\u0645\u0627\u0646 \u062a\u0648\u0648\u0646\u0627 \u0646\u06d5\u06a9\u0631\u062f\u060c \u0645\u06d5\u06af\u06cc\u0646 \u067e\u06ce\u0634\u0627\u0646 \u062f\u06cc\u0627\u0631\u062f\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u0648\u0648\u0633\u0631\u0627\u0628\u06ce.',
    '\u0647\u06cc\u0686 \u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9 \u0628\u06d5\u0631 \u0644\u06d5 \u0631\u06c6\u0698\u06cc \u062e\u06c6\u06cc \u0646\u0627\u0645\u0631\u06ce \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0648\u06d5\u067e\u0627\u0634\u06cc\u0634 \u0646\u0627\u062e\u0631\u06ce.',
    '\u0648\u062a\u06cc\u0634\u06cc\u0627\u0646: \u06a9\u0627\u0648\u0631\u0627\u06cc \u06a9\u06d5 \u0626\u06d5\u0648\u0634\u06cc \u00ab\u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5 \u0628\u06c6 \u0645\u0646 \u0628\u06d5 \u062e\u06d5\u06b5\u0627\u062a \u0647\u0627\u062a\u0648\u0648\u06d5\u00bb\u060c \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u062a\u06c6 \u067e\u06cc\u0627\u0648\u06ce \u0644\u06cc\u0648\u06d5\u06cc.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0642\u0633\u06d5\u06a9\u0627\u0646\u062a \u0695\u0627\u0633\u062a\u0646\u060c \u062f\u06d5\u06cc \u0628\u06c6 \u0641\u0631\u06cc\u0634\u062a\u06d5\u06a9\u0627\u0646 \u0646\u0627\u0647\u06ce\u0646\u06cc \u0628\u06c6 \u0644\u0627\u0645\u0627\u0646\u061f',
    '\u0626\u06ce\u0645\u06d5 \u0641\u0631\u06cc\u0634\u062a\u06d5 \u0646\u0627\u0646\u06ce\u0631\u06cc\u0646 \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5 \u0647\u06d5\u0642\u061b \u062c\u0627 \u0626\u06cc\u062a\u0631\u06cc\u0634 \u0642\u0627\u0646 \u0646\u0627\u062f\u0631\u06ce\u0646.',
    '\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u060c \u0647\u06d5\u0631 \u0626\u06ce\u0645\u06d5\u0634\u06cc\u0646 \u062f\u06d5\u06cc\u067e\u0627\u0631\u06ce\u0632\u06cc\u0646.',
    '\u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6\u0634 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u0646\u0627\u0648 \u06a9\u06c6\u0645\u06d5\u06b5\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u0646\u06d5.',
    '\u0647\u06cc\u0686 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u0646\u06d5\u0647\u0627\u062a \u0628\u06c6 \u0644\u0627\u06cc\u0627\u0646 \u0645\u06d5\u06af\u06d5\u0631\u060c \u062f\u0627\u06cc\u0627\u0646\u06d5 \u0628\u06d5\u0631 \u06af\u0627\u06b5\u062a\u06d5 \u0648 \u062a\u06d5\u0634\u06d5\u0631.',
    '\u0626\u06d5\u0645\u06d5\u0634 \u0626\u06ce\u0645\u06d5\u06cc\u0646 \u06a9\u06d5 \u062f\u06d5\u06cc\u062e\u06d5\u06cc\u0646\u06d5 \u062f\u06b5\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06d5\u0648\u06d5.',
    '\u0628\u0695\u0648\u0627\u06cc\u06d5 \u0628\u06d5 \u0645\u06d5 \u0646\u0627\u0647\u06ce\u0646\u0646 \u0648 \u0634\u06ce\u0648\u06d5\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0648\u0627\u0628\u0648\u0648\u06af\u06d5.',
    '\u062a\u06d5\u0646\u0627\u0646\u06d5\u062a \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0631\u06a9\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5 \u0628\u06c6 \u0628\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06ce \u0628\u0646\u060c',
    '\u0628\u06ce \u0634\u06a9 \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0686\u0627\u0648\u0645\u0627\u0646 \u0628\u06d5\u0633\u062a\u0631\u0627\u0648\u06d5 \u0648 \u062f\u06cc\u0627\u0631\u06d5 \u062c\u0627\u062f\u0648\u0648\u0645\u0627\u0646 \u0644\u06ce\u06a9\u0631\u0627\u0648\u06d5.',
    '\u0644\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5 \u0686\u06d5\u0646\u062f \u0628\u0631\u062c\u06ce\u06a9\u0645\u0627\u0646 \u0647\u06d5\u06b5\u0646\u0627\u0648\u06d5 \u0648 \u062e\u06d5\u0645\u06b5\u0627\u0646\u062f\u0648\u0648\u0645\u0627\u0646\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0633\u06d5\u06cc\u0631\u06ce \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0644\u06d5 \u06af\u0634 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06ce\u06a9\u06cc \u062a\u0695\u06c6\u06a9\u0631\u0627\u0648 \u067e\u0627\u0631\u0627\u0633\u062a.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06c6\u06b5\u06d5 \u062f\u06d5\u06a9\u0627\u061b \u06a9\u06d5 \u0626\u0627\u06af\u0631\u06ce\u06a9\u06cc \u0695\u0648\u0648\u0646\u0627\u06a9 \u0695\u0627\u0648\u06cc \u062f\u06d5\u0646\u06ce.',
    '\u0632\u06d5\u0648\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0695\u0627\u06cc\u06d5\u062e \u0648 \u06a9\u06ce\u0648\u0627\u062a\u06cc\u0634\u0645\u0627\u0646 \u0647\u0627\u0648\u06cc\u0634\u062a\u06d5 \u0646\u0627\u0648. \u0647\u06d5\u0645\u0648\u0648 \u062c\u06c6\u0631\u06d5 \u0695\u0648\u0648\u06d5\u06a9\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06d5 \u062a\u06d5\u06a9\u0648\u0648\u0632\u06cc \u062a\u06cc\u0627 \u0695\u0648\u0648\u0627\u0646\u062f.',
    '\u0645\u0627\u06cc\u06d5\u06cc \u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u0648 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u0631\u0627\u0646\u06cc \u062c\u06cc\u0627 \u0644\u06d5\u0646\u06af\u06c6 - \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0698\u06cc\u0648\u06cc\u0627\u0646 \u0646\u0627\u062f\u06d5\u0646 - \u0644\u06d5\u0648\u06ce\u062f\u0627 \u067e\u06ce\u06a9\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5.',
    '\u06af\u06d5\u0646\u062c\u06cc\u0646\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u06cc\u0634 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u06d5 \u0648 \u0626\u06d5\u0648\u06d5\u0634\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631.',
    '\u0628\u0627\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u0646 \u0626\u0627\u0648\u0633 \u0628\u06a9\u06d5\u0646\u061b \u0644\u06d5\u0648 \u0647\u06d5\u0648\u0627\u0634\u06d5 \u0626\u0627\u0648\u0645\u0627\u0646 \u0647\u06d5\u0646\u0627\u0631\u062f \u0648 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u067e\u06ce \u062a\u06ce\u0631\u0627\u0648 \u06a9\u0631\u062f. \u0646\u0627\u0634\u062a\u0648\u0627\u0646\u0646 \u0626\u06d5\u0648 \u0626\u0627\u0648\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u06c6 \u0695\u0627\u06af\u0631\u0646.',
    '\u0647\u06d5\u0631\u062e\u06c6\u0645\u0627\u0646\u06cc\u0646 \u06af\u06cc\u0627\u0646 \u0628\u06d5\u062e\u0634\u06cc\u0646 \u0648 \u06af\u06cc\u0627\u0646 \u0626\u06d5\u0633\u062a\u06ce\u0646\u06cc\u0646\u061b \u0626\u06ce\u0645\u06d5\u0634 \u0645\u06cc\u0631\u0627\u062a\u0628\u06d5\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648\u06cc\u0646.',
    '\u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0646 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631\u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0648\u0648\u0646. \u062f\u06d5\u0634\u0632\u0627\u0646\u06cc\u0646 \u06a9\u06ce\u0646 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06ce\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u06a9\u06c6 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u06a9\u0627\u0631\u0632\u0627\u0646 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0645\u0631\u06c6\u0645\u0627\u0646 \u062f\u0631\u0648\u0633\u062a \u06a9\u0631\u062f\u060c \u0644\u06d5 \u0642\u0648\u0695\u06d5 \u0695\u06d5\u0634\u06d5\u06cc \u0628\u06c6\u06af\u06d5\u0646\u06cc\u0648\u06cc \u0695\u06d5\u0642 \u0647\u06d5\u06b5\u0627\u062a\u0648\u0648.',
    '\u0644\u06d5\u0645\u06d5\u0648\u0628\u06d5\u0631\u06cc\u0634 \u062c\u0646\u06c6\u06a9\u06d5\u0645\u0627\u0646 \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0648\u0648\u06d5\u060c \u0644\u06d5 \u0626\u0627\u06af\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0647\u0627\u06b5\u0627\u0648.',
    '\u06a9\u0627\u062a\u06ce \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06d5 \u0641\u0631\u06cc\u0634\u062a\u0627\u0646\u06cc \u06af\u0648\u062a: \u0648\u0627 \u0645\u0646 \u0645\u0631\u06c6\u0645 \u0644\u06d5 \u0642\u0648\u0695\u06d5 \u0695\u06d5\u0634\u06d5\u06cc \u0628\u06c6\u06af\u06d5\u0646\u06cc\u0648\u06cc \u0648\u0634\u06a9\u06d5\u06b5\u0627\u062a\u0648\u0648 \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b',
    '\u0647\u06d5\u0631\u06af\u0627 \u0695\u06ce\u06a9 \u0648 \u067e\u06ce\u06a9\u0645 \u06a9\u0631\u062f \u0648 \u0644\u06d5 \u06af\u06cc\u0627\u0646\u06cc \u062e\u06c6\u0645 \u0641\u0648\u0648\u0645 \u067e\u06ce\u062f\u0627 \u06a9\u0631\u062f\u060c \u0626\u06ce\u0648\u06d5 \u0628\u06c6 \u0633\u0648\u0698\u062f\u06d5 \u0628\u06c6 \u0628\u0631\u062f\u0646 \u062f\u0627\u0648\u06d5\u0631\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0645\u06d5\u0648\u0695\u0648\u0648 \u0628\u0646.',
    '\u0641\u0631\u06cc\u0634\u062a\u06d5 \u06af\u0634 \u0633\u0648\u0698\u062f\u06d5\u06cc\u0627\u0646 \u0628\u0631\u062f\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06cc\u0628\u0644\u06cc\u0633\u060c \u0645\u0644\u06cc \u0646\u06d5\u062f\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u0633\u0648\u0698\u062f\u06d5\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0626\u06cc\u0628\u0644\u06cc\u0633! \u062a\u06c6 \u0686\u06cc\u062a\u06d5\u060c \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0645 \u0633\u0648\u0698\u062f\u06d5\u0628\u06d5\u0631\u0627\u0646\u06d5 \u0633\u0648\u0698\u062f\u06d5 \u0646\u0627\u0628\u06d5\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc: \u0645\u0646 \u0686\u06c6\u0646 \u0633\u0648\u0698\u062f\u06d5 \u062f\u06d5\u0628\u06d5\u0645 \u0628\u06c6 \u0645\u0631\u06c6\u06cc\u06d5\u06a9\u060c \u0644\u06d5 \u0642\u0648\u0695\u06d5 \u0695\u06d5\u0634\u06d5\u06cc \u0628\u06c6\u06af\u06d5\u0646\u06cc\u0648\u06cc \u0695\u06d5\u0642 \u0647\u06d5\u06b5\u0627\u062a\u0648\u0648\u062a \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5\u061f',
    '\u06af\u0648\u062a\u06cc: \u0633\u0627 \u0644\u06ce\u0631\u06d5 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u06d5\u060c \u062a\u06c6 [\u0644\u06d5 \u0644\u0627\u06cc \u0645\u0646] \u062f\u06d5\u0631\u06a9\u0631\u0627\u0648\u06cc.',
    '\u0646\u0641\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06c6\u06cc\u06d5\u060c \u062a\u0627 \u0695\u06c6\u0698\u06cc \u067e\u06ce \u0695\u0627\u06af\u06d5\u06cc\u0634\u062a\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0645\u06d5\u0648\u062f\u0627\u0645 \u0628\u062f\u06d5 \u062a\u0627 \u0695\u06c6\u0698\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0628\u0695\u06c6\u060c \u062a\u06c6 \u0644\u06d5 \u0645\u06c6\u06b5\u06d5\u062a \u062f\u0631\u0627\u0648\u0627\u0646\u06cc\u061b',
    '\u0647\u06d5\u062a\u0627 \u0648\u06d5\u062e\u062a\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0647\u06d5\u0631 \u0686\u0648\u0646\u06a9\u06d5 \u062a\u06c6 \u06af\u0648\u0645\u0695\u0627\u062a \u06a9\u0631\u062f\u0645\u060c \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0632\u06d5\u0645\u06cc\u0646 \u062e\u0631\u0627\u067e\u06d5\u06cc\u0627\u0646 \u0648\u06d5\u0647\u0627 \u0644\u06d5\u0628\u06d5\u0631 \u062f\u06d5\u062e\u06d5\u0645\u06b5\u06ce\u0646\u0645\u060c \u06af\u0634\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce \u0647\u06d5\u06b5\u06d5 \u062f\u06d5\u06a9\u06d5\u0645\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0639\u06d5\u0628\u062f\u06d5 \u062f\u06b5 \u0648 \u062f\u06d5\u0631\u0648\u0648\u0646 \u067e\u0627\u06a9\u06d5\u06a9\u0627\u0646\u062a.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u0645\u0646\u06d5.',
    '\u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0645\u0646 - \u062c\u06cc\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0634\u0648\u06ce\u0646 \u062a\u06c6 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646 \u0648 \u06af\u0648\u0645\u0695\u0627 \u0628\u0648\u0648\u0646 - \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06cc\u0646 \u062a\u06c6 \u0628\u062a\u0648\u0627\u0646\u06cc \u062f\u06d5\u0631\u06c6\u0633\u062a\u06cc\u0627\u0646 \u0628\u06ce\u06cc.',
    '\u062c\u06d5\u062d\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0634 \u062c\u06ce\u0698\u0648\u0627\u0646\u06ce\u06a9\u06cc \u0647\u06d5\u0645\u0648\u0648\u0627\u0646\u06cc\u06cc\u06d5\u0648\u060c',
    '\u062d\u06d5\u0648 \u062f\u0631\u06af\u0627\u06cc \u0647\u06d5\u0633. \u0628\u06c6 \u0647\u06d5\u0631 \u062f\u0631\u06af\u0627\u06cc\u06d5\u06a9 \u0644\u06d5\u0645\u0627\u0646\u06d5 \u067e\u0627\u0698\u06ce\u06a9\u06cc \u0644\u06ce \u0628\u06d5\u0634 \u06a9\u0631\u0627\u0648\u06d5.',
    '\u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u0627\u0646 \u0644\u06d5 \u0628\u0627\u063a\u0627\u062a \u0648 \u06a9\u0627\u0646\u06cc\u0627\u0648\u0627\u0646\u0646.',
    '\u0628\u0686\u0646\u06d5 \u0698\u0648\u0648\u0631\u06d5 \u0648 \u0628\u06d5 \u0647\u06ce\u0645\u0646\u06cc \u0648 \u062e\u0627\u062a\u0631\u062c\u06d5\u0645\u06cc.',
    '\u0647\u06d5\u0631 \u06a9\u06cc\u0646\u06d5\u06cc\u06d5\u06a9 \u0644\u06d5 \u0633\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u0628\u0648\u0648\u060c \u062f\u0627\u0646 \u0645\u0627\u06b5\u06cc. \u062f\u06d5\u0628\u0646\u06d5 \u0628\u0631\u0627\u0648 \u0644\u06d5 \u0628\u0627\u0646 \u062a\u06d5\u062e\u062a\u0627\u0646 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0647\u06d5\u0645\u0628\u06d5\u0631 \u0628\u06d5 \u06cc\u06d5\u06a9\u0646.',
    '\u0644\u06d5\u0648\u06ce\u062f\u0627 \u0646\u06d5\u0645\u0627\u0646\u062f\u0648\u0648 \u0628\u0648\u0648\u0646\u06d5 \u0648 \u0646\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06ce\u06cc \u062f\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u0646.',
    '\u062e\u06d5\u0648\u06d5\u0631 \u0628\u062f\u06d5 \u0628\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u0645\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u062e\u06c6\u0645\u0645 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u0645 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u0645.',
    '\u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u0634\u0645 \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646\u06d5.',
    '\u0628\u0627\u0633 \u0648 \u062e\u0648\u0627\u0633\u06cc \u0645\u06cc\u0648\u0627\u0646\u0627\u0646\u06cc \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u0698\u06d5.',
    '\u06a9\u0627\u062a\u06ce \u0644\u06ce\u06cc \u0686\u0648\u0648\u0646\u06d5 \u0698\u0648\u0648\u0631\u06d5\u0648\u06d5 \u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0633\u06d5\u0644\u0627\u0645. \u06af\u0648\u062a\u06cc \u0645\u06d5 \u0644\u06ce\u062a\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062a\u0631\u0633\u062a \u0695\u06ce \u0646\u06d5\u0646\u06cc\u0634\u06ce\u061b \u0626\u06ce\u0645\u06d5 \u0645\u0632\u06af\u06ce\u0646\u06cc\u062a \u0626\u06d5\u062f\u06d5\u06cc\u0646\u06ce \u062a\u06c6 \u06a9\u0648\u0695\u06cc \u0632\u06c6\u0631 \u0632\u0627\u0646\u062a \u062f\u06d5\u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0645\u0646\u06ce\u06a9\u06cc \u0647\u06ce\u0646\u062f\u06d5 \u067e\u06cc\u0631\u060c \u0628\u06d5 \u0686\u06cc\u0645 \u0645\u0632\u06af\u06ce\u0646\u06cc \u062f\u06d5\u062f\u06d5\u0646\u06ce\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0648 \u0645\u0698\u062f\u06d5\u06cc \u067e\u06ce\u0645\u0627\u0646\u062f\u0627\u06cc\u060c \u0695\u0627\u0633\u062a\u06d5 \u0648 \u062f\u06d5\u0628\u06ce \u0648 \u0644\u06d5 \u0647\u06d5\u06cc\u06a4\u06cc \u0628\u0695\u0627\u0648\u0627\u0646 \u0645\u06d5\u0628\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u06a9\u06ce \u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627 \u0628\u06ce \u0647\u0648\u0645\u06ce\u062f\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u06d5\u06a9\u0627\u0646\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u0627\u0646! \u06a9\u0627\u0631\u062a\u0627\u0646 \u0686\u06cc\u06cc\u06d5\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0633\u06d5\u0631 \u06af\u06d5\u0644\u06ce\u06a9\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06cc\u0646.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u062e\u0627\u0648\u062e\u06ce\u0632\u0627\u0646\u06cc \u0644\u0648\u0648\u062a\u060c \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u06cc\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0698\u0646\u06cc\u060c \u06a9\u06d5 \u0628\u0695\u06cc\u0627\u0631\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5 \u062f\u06d5\u0628\u06ce \u062c\u06ce \u0628\u0645\u06ce\u0646\u06ce.',
    '\u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5 \u0645\u06cc\u0648\u0627\u0646\u06cc \u0645\u0627\u06b5\u06cc \u0644\u0648\u0648\u062a\u060c',
    '\u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u0646 \u0646\u0627\u062a\u0627\u0646 \u0646\u0627\u0633\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0645\u06d5 \u0634\u062a\u06ce\u06a9\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06ce\u06cc \u062f\u0648\u0648\u062f\u06b5 \u0628\u0648\u0648\u0646.',
    '\u0695\u0627\u0633\u062a\u06cc\u0645\u0627\u0646 \u0628\u06c6 \u062a\u06c6 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0626\u06ce\u0645\u06d5 \u062f\u06cc\u0627\u0631\u06d5 \u062f\u0631\u06c6 \u0646\u0627\u06a9\u06d5\u06cc\u0646.',
    '\u06a9\u06d5 \u0686\u06d5\u0646\u062f \u0628\u06d5\u062a\u06b5\u06ce\u06a9 \u0644\u06d5 \u0634\u06d5\u0648 \u0628\u0648\u0648\u0631\u06cc\u060c \u062a\u06c6 \u062e\u0627\u0648\u062e\u06ce\u0632\u0627\u0646\u062a \u0628\u06d5\u0631\u06d5 \u0648\u060c \u062e\u06c6\u062a \u0644\u06d5 \u062f\u0648\u0627\u06cc\u0627\u0646 \u0648\u06d5\u0695\u06ce \u06a9\u06d5\u0648\u06d5. \u06a9\u06d5\u0633\u06cc\u0634 \u0626\u0627\u0648\u0695 \u0646\u06d5\u062f\u0627\u062a\u06d5\u0648\u06d5\u060c \u0628\u06c6 \u06a9\u0648\u06ce\u062a\u0627\u0646 \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0631\u06ce\u060c \u0628\u06c6 \u0648\u06ce \u0628\u0686\u0646.',
    '\u0646\u0648\u0648\u06a9 \u0648 \u0628\u06d5\u062f\u06cc \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u067e\u06ce\u0634 \u062f\u0627\u0646\u0627\u061b \u06a9\u06d5 \u0644\u06d5 \u062f\u06d5\u0645\u06cc \u0628\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0631 \u0628\u0648\u0648\u0646 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u0648\u0627\u0628\u0695\u0627\u0648 \u062f\u06d5\u0628\u0646.',
    '\u062e\u06d5\u06b5\u06a9\u06cc \u0634\u0627\u0631\u06cc\u0634 - \u0648\u06d5\u0647\u06d5\u0644\u0647\u06d5\u0644\u06d5 \u06a9\u06ce\u0634\u0627\u0646\u06d5\u0648\u06d5 - \u067e\u06d5\u06cc\u0627\u06cc\u0627\u0646 \u0628\u0648\u0648.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06cc\u0648\u0627\u0646\u0645\u0646\u060c \u0695\u0648\u0648 \u0632\u06d5\u0631\u062f\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0645 \u0645\u06d5\u06a9\u06d5\u0646.',
    '\u062a\u0631\u0633\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0626\u0627\u0628\u0695\u0648\u0648\u0645 \u0645\u06d5\u0628\u06d5\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u062f\u06cc \u067e\u06ce\u0646 \u0646\u06d5\u0648\u062a\u0648\u0648\u06cc \u0646\u0627\u0628\u06ce \u06a9\u06d5\u0633 \u062a\u062e\u0648\u0648\u0646\u062a \u06a9\u06d5\u0648\u06ce\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u0627 \u06a9\u06cc\u0698\u06d5\u06a9\u0627\u0646\u0645\u060c \u0686\u06cc\u062a\u0627\u0646 \u062f\u06d5\u0648\u06ce \u0648\u06d5\u0631\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0648\u0627\u0646\u06cc \u0628\u06a9\u06d5\u0646.',
    '\u062f\u06d5\u0628\u06d5\u06af\u06cc\u0627\u0646\u06cc \u062e\u06c6\u062a \u0633\u0648\u06ce\u0646\u062f\u06d5 \u06a9\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5\u0628\u06d5\u0631 \u0633\u06d5\u0631\u062e\u06c6\u0634\u06cc\u060c \u0633\u06d5\u0631\u06af\u06d5\u0631\u062f\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u062f\u06d5\u06af\u06d5\u06b5 \u06af\u0632\u06cc\u0646\u06af\u06cc \u0647\u06d5\u062a\u0627\u0648\u06ce \u0634\u0631\u06cc\u062e\u06d5 \u0648 \u06af\u0631\u0645\u06d5 \u06af\u0631\u062a\u0646\u06cc.',
    '\u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u0698\u06ce\u0631\u0648\u0695\u0648\u0648 \u06a9\u0631\u062f \u0648 \u0628\u0627\u0631\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u06a9\u06d5\u0633\u062a\u06d5\u06a9\u06ce - \u06a9\u06d5 \u0628\u0628\u0648\u0648\u06d5 \u0628\u06d5\u0631\u062f - \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u0627\u0628\u0627\u0631\u0627\u0646\u062f.',
    '\u0628\u06d5\u0631\u0627\u0633\u062a\u06cc \u0644\u06d5\u0645 \u0695\u0648\u0648\u062f\u0627\u0648\u06d5\u062f\u0627 \u0632\u06c6\u0631 \u0628\u06d5\u06b5\u06af\u06d5 \u0647\u06d5\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0648\u0634\u06cc\u0627\u0631\u0646.',
    '\u0626\u06ce\u0633\u062a\u0627\u0634 \u0634\u0648\u06ce\u0646\u06d5\u0648\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0695\u06ce\u06a9\u06cc \u0626\u0627\u0648\u06d5\u062f\u0627\u0646\u06d5.',
    '\u0626\u0627 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646 \u0632\u06c6\u0631\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u0646.',
    '\u062e\u0627\u0648\u06d5\u0646\u0627\u0646\u06cc \u0628\u06ce\u0634\u06d5 \u0648 \u062f\u0627\u0631\u06cc\u0634 \u0647\u06d5\u0631\u0648\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0628\u0648\u0648\u0646.',
    '\u062a\u06c6\u06b5\u06d5\u0646 \u0644\u06d5\u0648\u0627\u0646\u06cc\u0634 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9\u06cc\u0627\u0646 \u0648\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u06a9\u0627\u0631\u0648\u0627\u0646\u06d5\u0695\u06ce\u06cc\u06d5.',
    '\u062e\u06d5\u06b5\u06a9\u06cc \u00ab\u062d\u06cc\u062c\u0631\u00bb \u06cc\u0634 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646 \u0644\u06d5 \u0642\u06d5\u06b5\u06d5\u0645 \u062f\u0627.',
    '\u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0634\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646\u060c \u06a9\u06d5 \u0647\u06cc\u0686\u06cc\u0627\u0646 \u0644\u06ce \u0646\u06d5\u0633\u06d5\u0644\u0645\u0627\u0646\u062f\u0646.',
    '\u0644\u06d5 \u06a9\u0627\u0648\u0627\u0646\u062f\u0627 \u062e\u0627\u0646\u0648\u0648\u06af\u06d5\u0644\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u062a\u0627\u0634\u06cc\u060c \u0647\u06d5\u062a\u0627 \u0644\u06ce\u06cc\u0627\u0646 \u0626\u0627\u0631\u062e\u06d5\u06cc\u0627\u0646 \u0628\u0646.',
    '\u062a\u0627 \u0644\u06d5\u0628\u06d5\u0631\u06d5 \u0628\u06d5\u06cc\u0627\u0646\u06ce\u06a9\u062f\u0627 \u0634\u0631\u06cc\u062e\u06d5 \u0648 \u06af\u0631\u0645\u06d5 \u06af\u0631\u062a\u0646\u06cc.',
    '\u0647\u06d5\u0631\u0686\u06cc \u067e\u06d5\u06cc\u062f\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0628\u0648\u0648 \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06c6 \u0646\u06d5\u062f\u0627\u0646.',
    '\u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648 \u062f\u0648\u0648\u0627\u0646\u06d5\u06cc\u06d5\u060c \u0628\u06c6 \u0695\u0627\u0633\u062a\u06cc\u0645\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0646. \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u0647\u06d5\u0631 \u062f\u0627\u062f\u06ce\u061b \u0633\u0627 \u062a\u06c6 \u06af\u06d5\u0631\u06d5\u06a9 \u0686\u0627\u0648\u067e\u06c6\u0634\u06cc \u0628\u06a9\u06d5 \u0628\u06d5 \u067e\u06cc\u0634\u06d5 \u0648 \u0686\u0627\u0648\u067e\u06c6\u0634\u06cc\u062a \u0628\u06ce \u06af\u0627\u0632\u0646\u062f\u06d5 \u0628\u06ce.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0628\u06d5\u060c \u0647\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u06cc \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u062d\u06d5\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0641\u0627\u062a\u06cc\u062d\u0627\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5 \u0628\u06d5 \u062a\u06c6 \u0648 \u0642\u0648\u0631\u0626\u0627\u0646\u06cc \u06af\u06d5\u0648\u0631\u06d5\u0634 \u0647\u06d5\u0631\u0648\u06d5\u062a\u0631.',
    '\u0686\u0627\u0648\u062a \u0645\u06d5\u062f\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u06a9\u06d5 \u062f\u0627\u0648\u0645\u0627\u0646\u06d5 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06af\u06d5\u0644\u06ce \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0648 \u062e\u06d5\u0645\u06cc\u0627\u0646 \u0645\u06d5\u062e\u06c6\u061b \u062a\u06c6 \u0647\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0628\u062e\u06d5 \u0698\u06ce\u0631 \u0628\u0627\u06b5\u062a.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06cc \u0695\u0648\u0648\u0646 \u06a9\u06d5\u0631\u06d5\u0648\u06d5\u0645.',
    '\u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0646\u0627\u0631\u062f\u0645\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0634\u06a9\u06d5\u0631\u06d5\u06a9\u0627\u0646\u061b',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0642\u0648\u0631\u0626\u0627\u0646\u06cc\u0627\u0646 \u062f\u0627\u0628\u06d5\u0634 \u062f\u0627\u0628\u06d5\u0634 \u06a9\u0631\u062f.',
    '\u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u060c \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u062f\u06d5\u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f.',
    '\u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06ce\u06a9 \u0628\u06d5 \u062a\u06c6 \u062f\u06d5\u062f\u0631\u06ce\u060c \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u06a9\u06d5 \u0648 \u0644\u06d5 \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u0627\u0646 \u0648\u0627\u0632 \u0628\u06ce\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06af\u0627\u06b5\u062a\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u062a\u06c6 \u062f\u06d5\u06a9\u0631\u062f\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06ce\u062e\u06d5\u062a\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc - \u0648\u06ce\u0695\u0627\u06cc \u062e\u0648\u062f\u0627 - \u0634\u062a\u06cc \u062a\u0631\u06cc\u0634 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u062f\u0627 \u0644\u06ce\u06cc \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u06af\u0627\u0645\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062a\u06c6 \u0644\u06d5 \u06af\u0641\u062a\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u06b5\u062a\u06d5\u0646\u06af \u062f\u06d5\u0628\u06cc.',
    '\u0633\u0627 \u0647\u06d5\u0631 \u0634\u0648\u06a9\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u06ce \u0648 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0633\u0648\u0698\u062f\u06d5\u0628\u06d5\u0631\u0627\u0646 \u0628\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u067e\u06d5\u0631\u0633\u062a\u06d5 \u062a\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u06a9\u06d5 \u0634\u062a\u06d5 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5\u06a9\u06d5\u062a \u0644\u06ce \u0695\u0648\u0648\u062f\u06d5\u062f\u0627.',
    '\u0626\u06d5\u0648\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u062a\u060c \u067e\u06d5\u0644\u06d5\u06cc \u0645\u06d5\u06a9\u06d5\u0646. \u062e\u0648\u062f\u0627 \u067e\u0627\u06a9\u06d5 \u0648 \u06af\u06d5\u0644\u06ce\u06a9 \u0644\u06d5\u0648\u06d5 \u0628\u06d5\u0631\u0632\u062a\u0631\u06d5 \u06a9\u06d5 \u0628\u06ce\u0698\u0646 \u0647\u0627\u0648\u06a9\u0627\u0631\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646 \u0648\u06ce\u0695\u0627\u06cc \u0648\u06d5\u06b5\u0627\u0645\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648 - \u0628\u06d5\u067e\u06ce\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06ce \u06a9\u06d5 \u0626\u06d5\u06cc\u062f\u0627 - \u062f\u06d5\u0646\u06ce\u0695\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce \u0628\u06c6 \u0644\u0627\u06cc \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u06a9\u06d5 \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u06a9\u0627\u061b \u06af\u06d5\u0641\u0627\u0646 \u0644\u06d5 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5 \u0628\u06a9\u06d5\u0646 \u06a9\u06d5: \u0647\u06cc\u0686 \u0634\u062a\u06ce \u0634\u06cc\u0627\u0648\u06cc \u067e\u06d5\u0631\u0633\u062a\u0646\u06ce \u0646\u06cc\u06cc\u06d5 \u062c\u06af\u06d5 \u0644\u06d5 \u062e\u06c6\u0645\u061b \u062f\u06d5\u0628\u06ce \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u0645 \u0628\u062a\u0631\u0633\u0646.',
    '\u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0648\u06cc\u0646\u06d5\u06cc \u0647\u06d5\u0631 \u0628\u06c6 \u0695\u0627\u0633\u062a\u06cc \u062f\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5. \u0628\u06d5\u0631\u0632\u062a\u0631\u06d5 \u0648 \u062f\u0648\u0648\u0631\u06d5 \u0644\u06d5\u0648\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc.',
    '\u0628\u0646\u06cc\u0627\u062f\u06d5\u0645\u06cc \u0644\u06d5 \u062a\u0646\u06c6\u06a9\u06ce\u06a9 \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u061b \u06a9\u06d5\u0686\u06cc \u0628\u06c6\u062a\u06d5 \u062f\u0648\u0698\u0645\u0646\u06ce\u06a9\u06cc \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627.',
    '\u0648\u06b5\u0633\u0627\u062a\u06cc\u0634\u06cc \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u060c \u06a9\u06d5 \u06af\u06d5\u0631\u0645\u0627\u06cc\u06cc \u0648 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062a\u06ce\u062f\u0627 \u0648 \u0644\u06ce\u0634\u06cc\u0627\u0646 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u0644\u0627\u062a\u0627\u0646 \u062c\u0648\u0627\u0646 \u0648 \u0695\u0627\u0632\u0627\u0648\u06d5\u0646\u060c \u06a9\u0627\u062a\u06ce\u06a9 \u062f\u06d5\u06cc\u0627\u0646\u0628\u06d5\u0646 \u0628\u06c6 \u0644\u06d5\u0648\u06d5\u0695 \u0648 \u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u062f\u06d5\u06cc\u0627\u0646\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5.',
    '\u06af\u0631\u0627\u0646\u0628\u0627\u0631\u062a\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0648\u06ce\u0632\u06ce \u0628\u06c6\u062c\u06ce\u06af\u06d5\u06cc \u0648\u0627 \u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631 \u062f\u06d5\u0628\u0648\u0648 \u0628\u06cc\u06af\u06d5\u0646\u06ce. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0632\u06c6\u0631 \u062f\u06b5\u0633\u06c6\u0632 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0633\u067e \u0648 \u0626\u06ce\u0633\u062a\u0631 \u0648 \u06a9\u06d5\u0631\u06cc\u0634\u06cc\u060c \u0647\u06d5\u0645 \u0628\u06c6 \u0633\u0648\u0627\u0631\u06cc \u0647\u06d5\u0645 \u0628\u06c6 \u062e\u06c6 \u0628\u06d5 \u062c\u0648\u0627\u0646 \u0695\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u0686\u06ce \u06a9\u0631\u062f. \u0634\u062a\u06cc \u0648\u06d5\u0647\u0627\u0634 \u062f\u0631\u0648\u0633 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0646\u0627\u06cc\u0632\u0627\u0646\u0646.',
    '\u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06d5\u06cc \u0644\u06d5\u0633\u06d5\u0631\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a \u062f\u06d5\u0633\u062a \u0646\u06cc\u0634\u0627\u0646 \u0628\u06a9\u0627. \u0695\u06ce\u06af\u06d5\u06cc \u062e\u06ce\u0686 \u0648 \u062e\u0648\u0627\u0631\u06cc\u0634 \u0647\u06d5\u06cc\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc \u0628\u06cc\u0648\u06cc\u0633\u062a\u0628\u0627\u060c \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646\u06cc \u062f\u06d5\u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u0633\u06cc \u0644\u06d5\u0645 \u062d\u06d5\u0648\u0627\u06cc\u06d5 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u0628\u06c6 \u062f\u0627\u0628\u0627\u0631\u0627\u0646\u062f\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06ce\u06cc \u06a4\u06d5\u062f\u06d5\u062e\u06c6\u0646 \u0648 \u0695\u0648\u0648\u06d5\u06a9\u06cc\u0634\u06cc \u067e\u06ce \u0634\u06cc\u0646 \u062f\u06d5\u0628\u06ce \u0648 \u0644\u06d5\u0648 \u0695\u0648\u0648\u06d5\u06a9\u06d5\u060c \u0648\u06b5\u0633\u0627\u062a\u06d5\u06a9\u0627\u0646\u0648\u0648 \u062f\u06d5\u0686\u06d5\u0631\u0646.',
    '\u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0626\u0627\u0648\u06d5 \u0686\u0627\u0646\u062f\u06d5\u0645\u06d5\u0646\u06cc \u0648 \u0632\u06d5\u06cc\u062a\u0648\u0648\u0646 \u0648 \u0695\u06d5\u0632 \u0648 \u062f\u0627\u0631\u062e\u0648\u0631\u0645\u0627\u0648 \u0628\u06c6 \u062f\u06d5\u0695\u0648\u06ce\u0646\u06ce \u0648 \u06af\u0634 \u0628\u06d5\u0631\u0648\u0628\u06c6\u062a\u0627\u0646 \u067e\u06ce\u062f\u06d5\u062f\u0627. \u0626\u06d5\u0645\u06d5 \u0632\u06c6\u0631 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0695\u0627\u062f\u06d5\u0645\u06ce\u0646\u0646.',
    '\u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698\u06cc \u0628\u06c6 \u0695\u0627\u0645 \u06a9\u0631\u062f\u0646\u061b \u0647\u06d5\u06cc\u06a4 \u0648 \u062e\u06c6\u0631 \u0648 \u06af\u0634 \u0647\u06d5\u0633\u0627\u0631\u06d5\u0634 \u0645\u0644\u06a9\u06d5\u0686\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u0646. \u0644\u06d5\u0645\u06d5\u0634\u062f\u0627 \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0647\u06d5\u0646 \u0628\u06c6 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u06a9\u06d5 \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646.',
    '\u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0634 \u06af\u06d5\u0644\u06ce \u0634\u062a\u06cc \u0695\u06d5\u0646\u06af\u0627\u0648\u0695\u06d5\u0646\u06af\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0647\u06d5\u06cc\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0695\u0627\u062f\u06d5\u0645\u06ce\u0646\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0632\u06d5\u0631\u06cc\u0627\u06cc \u062e\u0633\u062a\u06c6\u062a\u06d5 \u0628\u06d5\u0631\u062f\u06d5\u0633\u062a\u0648\u0648 \u06a9\u06d5 \u06af\u06c6\u0634\u062a\u06cc \u062a\u0627\u0632\u06d5\u06cc \u0644\u06ce \u0628\u062e\u06c6\u0646\u061b \u062e\u0634\u06b5\u06cc\u0634\u06cc \u0644\u06ce\u06cc \u0628\u06ce\u0646\u0646\u06d5 \u062f\u06d5\u0631\u06ce \u0648 \u0628\u06d5 \u062e\u06c6\u062a\u0627\u0646\u06cc\u06d5\u0648\u06d5 \u0647\u06d5\u06b5\u0648\u0627\u0633\u0646. \u0626\u06d5\u0648\u0627 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u06a9\u06d5 \u06af\u06d5\u0645\u06cc\u06d5\u0634 \u0633\u0646\u06af\u06cc \u062f\u06d5\u0631\u06cc\u0627 \u062f\u06d5\u0642\u06d5\u06b5\u06ce\u0634\u0646\u061b \u062a\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u0626\u06d5\u0648\u062a\u0627\u0646 \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u06ce \u0648 \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0628\u0698\u06ce\u0631 \u0628\u0646.',
    '\u06a9\u06ce\u0648\u06d5 \u0647\u06d5\u06b5\u06d5\u0645\u0648\u0648\u062a\u06d5\u06a9\u0627\u0646\u06cc \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u06a9\u0631\u062f\u06d5 \u0644\u06d5\u0646\u06af\u06d5\u0631\u060c \u0647\u06d5\u062a\u0627 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u0644\u06d5\u0631\u0632\u06ce\u0646\u06ce \u0648 \u0686\u06c6\u0645 \u0648 \u0695\u06ce\u0628\u0627\u0632\u06cc\u0634\u06cc \u0634\u0627\u0646\u062f\u0627\u0646\u060c \u062a\u0627 \u0626\u06ce\u0648\u06d5 \u0695\u06ce\u0648 \u0628\u062f\u06c6\u0632\u0646\u06d5\u0648\u06d5.',
    '\u0646\u06cc\u0634\u0627\u0646\u06d5\u06af\u06d5\u0644 \u062f\u06cc\u06a9\u06d5\u0634 \u0647\u06d5\u0646 \u0648 \u0628\u06d5 \u0626\u06d5\u0633\u062a\u06ce\u0631\u06d5\u0634 \u0695\u06ce \u062f\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u0686\u06ce \u06a9\u0631\u062f\u0646\u06ce \u0648\u06d5\u0633\u062a\u0627\u06cc\u06d5\u060c \u0686\u0644\u06c6\u0646 \u0648\u06d5\u06a9 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0648\u0627\u06cc\u06d5\u060c \u0644\u06d5\u0648 \u0628\u0627\u0631\u06d5 \u0686\u06cc \u0644\u06d5 \u062f\u06d5\u0633 \u0646\u0627\u06cc\u06d5\u061f \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06d5\u0646\u062f \u0646\u0627\u06af\u0631\u0646\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631\u0686\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0628\u0698\u0645\u06ce\u0631\u0646\u060c \u0644\u06d5 \u0698\u0645\u0627\u0631\u062f\u0646 \u062f\u0648\u0627\u06cc\u06cc\u0648 \u0646\u0627\u06cc\u06d5. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0648\u06d5\u06cc\u0634\u06ce\u0631\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0634\u06cc \u0628\u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0628\u06d5\u0646 \u0648 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0646\u060c \u0644\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5 \u0647\u06cc\u0686 \u062f\u0631\u0648\u0633 \u06a9\u06d5\u0646\u061b \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u062f\u0631\u0648\u0633 \u062f\u06d5\u06a9\u0631\u06ce\u0646.',
    '\u0645\u0631\u062f\u0648\u0648\u06ce\u06a9\u0646 \u06af\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u0646\u06d5\u06a9\u0631\u0627\u0648\u06d5\u061b \u0647\u06d5\u0633\u062a\u06cc\u0634 \u0646\u0627\u06a9\u06d5\u0646 \u06a9\u06d5\u0646\u06af\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u062e\u0648\u062f\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce\u06a9\u06d5. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698 \u0646\u06cc\u06cc\u06d5\u060c \u062f\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u0698\u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0632\u0644 \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0634\u0627\u0631\u0646\u06d5\u0648\u06d5 \u0648 \u0686\u06cc \u062f\u06d5\u0631\u062f\u06d5\u062e\u06d5\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062e\u06c6\u0628\u06d5\u0632\u0644 \u0632\u0627\u0646\u0627\u0646 \u062e\u06c6\u0634\u06cc \u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627\u06cc\u06d5\u06a9\u06cc \u0644\u06ce\u06cc\u0627\u0646 \u067e\u0631\u0633\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646 \u06a9\u0627\u0645 \u0645\u06d5\u0628\u06d5\u0633\u062a\u06cc \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u061f \u0626\u06ce\u0698\u0646: \u0686\u06cc\u0631\u06c6\u06a9\u06cc \u067e\u06ce\u0634\u06cc\u0646\u0627\u0646.',
    '\u062f\u06d5\u0633\u0627 \u0628\u0627 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u0628\u0627\u0631\u06cc \u062a\u0627\u0648\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0647\u06d5\u06b5\u06af\u0631\u0646 \u06a9\u06d5 \u0628\u06d5 \u0628\u06ce \u0632\u0627\u0646\u0633\u062a\u060c \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u0627\u0646 \u0644\u0627 \u062f\u06d5\u062f\u06d5\u0646\u061b \u0626\u0627\u06cc \u0686\u06d5\u0646\u062f \u0628\u0627\u0631\u06ce \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5!',
    '\u0695\u0627\u0633\u062a\u062a \u062f\u06d5\u0648\u06ce \u0626\u06d5\u0648\u06cc \u0644\u06d5 \u067e\u06ce\u0634 \u0626\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0628\u0648\u0648\u0646\u060c \u062f\u06d5\u0647\u06c6\u0628\u0627\u0632 \u0628\u0648\u0648\u0646. \u062e\u0648\u062f\u0627 \u062f\u0627\u0645 \u0648 \u062f\u06d5\u0632\u06af\u0627\u06cc\u0627\u0646\u06cc \u0644\u06d5 \u0647\u06cc\u0645\u06d5\u0648\u06d5 \u0647\u06d5\u06b5\u062a\u06d5\u06a9\u0627\u0646\u062f \u0648 \u0645\u06cc\u0686\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0695\u0645\u0627 \u0648 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06d5\u0648\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0628\u0627\u0631\u06cc\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u0647\u06d5\u0633\u062a\u06cc\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f.',
    '\u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634\u062f\u0627 \u0626\u0627\u0628\u0695\u0648\u06cc\u0627\u0646 \u067e\u06ce\u0648\u06d5\u0646\u0627\u0647\u06ce\u06b5\u06ce \u0648 \u062f\u06d5\u06b5\u06ce: \u0644\u06d5\u06a9\u0648\u06ce\u0646 \u0626\u06d5\u0648 \u0634\u06d5\u0631\u06cc\u06a9\u0627\u0646\u06d5\u0645 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u06a9\u06ce\u0634\u06d5\u062a\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u062f\u06d5\u06a9\u0631\u062f\u0646\u061f \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u0632\u0627\u0646\u0633\u062a\u0646\u060c \u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u0631\u06c6 \u0626\u0627\u0628\u0695\u0648\u0648\u0686\u0648\u0648\u0646 \u0648 \u0628\u06d5\u062f\u0628\u06d5\u062e\u062a\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce \u0645\u06ce\u0631\u062f\u06cc \u062e\u0648\u062f\u0627 \u06af\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u0633\u062a\u06ce\u0646\u06ce \u0648 \u0633\u062a\u06d5\u0645\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0645\u0644 \u0695\u0627\u062f\u06d5\u06a9\u06ce\u0634\u0646 \u0648 \u0626\u06ce\u0698\u0646: \u0686\u06cc \u062e\u0631\u0627\u067e\u0646 \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u0628\u06d5\u06b5\u06ce\u060c \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u06a9\u0627\u0631\u0648\u0648 \u0686\u06c6\u0646 \u0628\u0648\u0648.',
    '\u0633\u0627 \u0644\u06d5 \u062f\u06d5\u0631\u06af\u0627\u06cc \u062f\u06c6\u0698\u06d5\u0647\u06d5\u0648\u06d5 \u0628\u0686\u0646\u06d5 \u0626\u06d5\u0648\u062f\u06cc\u0648\u060c \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u0628\u0646. \u0626\u0627\u06cc \u06a9\u06d5 \u062c\u06ce\u06cc \u0628\u0627\u062f\u06cc \u0647\u06d5\u0648\u0627\u06cc\u0627\u0646 \u0646\u0627\u0647\u06d5\u0645\u0648\u0627\u0631\u06d5.',
    '\u0644\u06d5 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u0627\u0646 \u062f\u06d5\u067e\u0631\u0633\u0646: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u0686\u06cc \u0628\u06d5 \u062e\u06d5\u06b5\u0627\u062a \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u06ce\u061f \u0626\u06ce\u0698\u0646: \u0647\u06d5\u0631\u0686\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u0627\u0634\u06d5. \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0686\u0627\u06a9\u06d5 \u0628\u0648\u0648\u0646\u060c \u0686\u0627\u06a9\u06d5 \u0647\u06d5\u06cc\u06d5 \u0648 \u062f\u06cc\u0627\u0631\u06d5 \u0628\u0627\u0631\u06d5\u06af\u0627\u06cc \u0626\u06d5\u0648 \u062f\u0646\u06cc\u0627\u0634 \u0628\u0627\u0634\u062a\u0631\u06d5 \u0648 \u0626\u0627\u06cc \u06a9\u06d5 \u0645\u0627\u06b5\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627\u062a\u0631\u0633\u0627\u0646 \u0686\u06d5\u0646\u062f \u062e\u06c6\u0634\u06d5!',
    '\u062f\u06d5\u0695\u06c6\u0646\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06cc \u0648\u0627 \u06a9\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0646\u0627\u0686\u06ce\u061b \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627\u061b \u0686\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u06a9\u06d5\u0646 \u062a\u06cc\u0627\u06cc\u06d5. \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u0627\u06cc\u0646\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0641\u0631\u0634\u062a\u06d5 \u06af\u06cc\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06ce\u0634\u06ce \u0648 \u0686\u0627\u06a9 \u0648 \u067e\u0627\u06a9 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u062f\u06d5\u0695\u06c6\u0646\u061b \u0626\u06ce\u0698\u0646: \u0633\u06b5\u0627\u0645\u06cc \u062e\u0648\u0627\u0648 \u0644\u06ce \u0628\u06ce \u0648 \u0644\u06d5\u0628\u0627\u062a\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u062f\u06d5 \u0628\u0686\u0646\u06d5 \u0646\u0627\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0641\u0631\u06cc\u0634\u062a\u0627\u0646 \u0628\u06ce\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u06cc\u0627\u0646 \u067e\u06ce \u0628\u06af\u0627\u060c \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06ce\u06a9\u06cc \u062a\u0631\u06cc\u0627\u0646 \u0647\u06d5\u0633\u061f \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0645 \u0626\u0627\u06a9\u0627\u0631\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0628\u0648\u0648. \u062e\u0648\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc \u0644\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0633\u0632\u0627\u06cc \u0626\u0627\u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u0647\u0627\u062a\u06d5 \u0695\u06ce\u06cc\u0627\u0646\u061b \u0626\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u06af\u0627\u06b5\u062a\u06d5\u062c\u0627\u0631\u06cc\u0627\u0646 \u0628\u0648\u0648 \u0626\u0627\u0628\u06b5\u06c6\u0642\u06d5\u06cc \u062f\u0627\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u0648\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0631 \u062e\u0648\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627\u060c \u0646\u06d5 \u0626\u06ce\u0645\u06d5 \u0648 \u0646\u06d5 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0645\u0627\u0646\u060c \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u06c6\u06cc \u0686\u0646 \u0646\u06d5\u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u061b \u0647\u06cc\u0686\u0645\u0627\u0646 \u0628\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u0645\u0627\u0646 \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u0646\u06d5\u062f\u06d5\u0632\u0627\u0646\u06cc. \u0695\u0627\u0628\u0631\u062f\u0648\u0648\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0647\u06d5\u0631 \u0648\u0644\u06c6\u0628\u0648\u0648\u0646. \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u062c\u06af\u06d5 \u0644\u06d5 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u06cc \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u060c \u0686\u06cc \u062f\u06cc\u06a9\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u0628\u06c6 \u0646\u0627\u0648 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06c6\u0645\u06d5\u06b5\u06d5\u06cc\u06d5\u06a9 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f\u060c \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646 \u0648 \u0644\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u062f\u0648\u0648\u0631\u06d5\u067e\u06d5\u0631\u06ce\u0632 \u0628\u0646. \u0628\u0627\u0632\u06ce \u0644\u06d5\u0648\u0627\u0646 \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u06a9\u0631\u062f\u0646\u061b \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u0628\u06d5 \u0628\u06d5\u0698\u0646\u06cc\u0627\u0646 \u0628\u0695\u0627. \u062f\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0631\u0632 \u0628\u06af\u06d5\u0695\u06ce\u0646 \u0648 \u062a\u0648\u0627\u0634\u0627 \u06a9\u06d5\u0646 \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646 \u0632\u0627\u0646\u0627\u0646 \u0628\u06d5 \u06a9\u0648\u06ce \u06af\u06d5\u06cc\u0634\u062a.',
    '\u0647\u06d5\u0631\u0686\u06d5\u0646\u062f\u06d5 \u0647\u06d5\u0648\u06b5 \u0628\u06c6 \u0634\u0627\u0631\u06d5\u0632\u0627\u0628\u0648\u0648\u0646\u06cc\u0627\u0646 \u0628\u062f\u06d5\u06cc\u060c \u06af\u0648\u0645\u0695\u0627\u06cc\u06d5\u06a9\u0627\u0646\u060c \u062e\u0648\u0627 \u0695\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0646\u0627\u062f\u0627 \u0648 \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u0628\u06d5 \u0633\u0648\u06ce\u0646\u062f\u06d5\u0648\u06d5 \u0695\u06d5\u0634 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u060c \u06a9\u06d5: \u0648\u06d5\u062e\u0648\u062f\u0627\u060c \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u06a9\u06d5 \u062f\u06d5\u0645\u0631\u06ce\u060c \u062a\u0627\u0632\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc \u0646\u0627\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631 \u0628\u06d5\u06b5\u06ce\u0646\u06ce \u062e\u0648\u062f\u0627 \u0628\u06cc\u062f\u0627\u060c \u0647\u06d5\u0631 \u062f\u06ce\u062a\u06d5 \u062c\u06ce\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0648 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u062f\u06d5\u0648\u06d5\u0646\u0627\u06af\u06d5\u0646.',
    '\u062a\u0627\u06a9\u0648\u0648 \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u0648\u0627 \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u06a9\u0631\u062f\u060c \u0626\u0627\u0634\u06a9\u0631\u0627 \u06a9\u0627 \u0648 \u0628\u0627 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u062a\u06ce\u0628\u06af\u06d5\u0646 \u06a9\u06d5\u0626\u06d5\u0648\u0627\u0646 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0645\u06d5 \u0647\u06d5\u0631\u0686\u06cc\u06cc\u06d5\u06a9 \u0628\u0645\u0627\u0646\u06d5\u0648\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u0628\u0628\u06ce\u060c \u0647\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06d5\u06cc\u06d5 \u06a9\u06d5 \u067e\u06ce\u06cc \u0628\u06ce\u0698\u06cc\u0646: \u0628\u0628\u06d5\u061b \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0628\u06d5\u0631 \u0632\u0648\u06b5\u0645\u06ce \u062a\u06d5\u0631\u06d5\u0628\u0648\u0648\u0646 \u0648 \u0626\u0627\u0648\u0627\u0631\u06d5\u06cc \u0695\u0627\u0647\u06cc \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u0646\u060c \u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u062c\u06ce\u06af\u06d5\u06cc\u06ce \u0628\u0627\u0634\u06cc\u0627\u0646 \u0626\u06d5\u062f\u06d5\u06cc\u0646\u06ce \u0648 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06cc\u0632\u0627\u0646\u0646\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u06d5\u0648\u0644\u0627\u0634 \u0628\u0627\u0634\u062a\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0648\u0648\u0646 \u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0626\u06d5\u0633\u067e\u0627\u0631\u062f.',
    '\u0644\u06d5 \u067e\u06ce\u0634 \u062a\u06c6\u0634\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5 - \u0626\u06cc\u0644\u0644\u0627\u0646 \u0648 \u0628\u06cc\u0644\u0644\u0627\u0646 - \u067e\u06cc\u0627\u0648 \u0628\u0648\u0648\u06d5 \u0648 \u0628\u06d5 \u0641\u0631\u0634\u062a\u06d5\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0648\u0648\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u0648\u0632\u0627\u0646\u06cc\u0648\u06d5\u060c \u0633\u0627 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0628\u067e\u0631\u0633\u0646\u060c \u06a9\u06d5 \u06a9\u062a\u06ce\u0628\u06cc\u0627\u0646 \u062e\u0648\u06ce\u0646\u062f\u06c6\u062a\u06d5\u0648\u06d5.',
    '\u0628\u06d5 \u0628\u06d5\u06b5\u06af\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u067e\u06d5\u0695\u06d5\u0646\u0648\u0648\u0633\u0631\u0627\u0648\u0627\u0646\u06d5\u0648\u06d5 [\u0626\u06d5\u0648\u0627\u0646\u0645\u0627\u0646 \u0646\u0627\u0631\u062f]. \u0648\u0627 \u0628\u06c6 \u062a\u06c6\u0634 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u0646 \u0646\u0627\u0631\u062f. \u062a\u0627 \u062e\u06d5\u06b5\u06a9\u06cc \u0644\u06ce \u062a\u06ce\u0628\u06af\u06d5\u06cc\u06ce\u0646\u06cc \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0628\u06cc\u0631 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0641\u06ce\u06b5\u0628\u0627\u0632\u06cc \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0628\u0648\u0648\u0646\u060c \u0626\u0627\u0631\u062e\u06d5\u06cc\u0627\u0646\u0646 \u06a9\u06d5 \u062e\u0648\u0627 \u0646\u0648\u0642\u0645\u06cc \u0639\u06d5\u0631\u0632\u06cc\u0627\u0646 \u0646\u0627\u06a9\u0627\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06d5\u0648\u06d5 - \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06cc\u0631\u06cc\u0634\u06cc \u0644\u06ce \u0646\u0627\u06a9\u06d5\u0646\u06d5\u0648\u06d5 - \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0628\u0627\u0631\u06ce\u061f',
    '\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06cc \u0647\u0627\u062a\u0648\u0686\u06c6\u06cc\u0627\u0646\u062f\u0627 \u062f\u06d5\u06af\u06cc\u0631\u06cc\u0627\u0646 \u062e\u0627 \u0648 \u0646\u06d5\u062a\u0648\u0627\u0646\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0632 \u06a9\u06d5\u0646\u061f',
    '\u06cc\u0627\u0646 \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u062a\u0648\u0648\u0634\u06cc \u062a\u0631\u0633\u0646\u060c \u0628\u06cc\u0627\u0646\u06af\u0631\u06ce\u061f \u0633\u0627 \u0647\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u06cc\u06d5 \u0648\u0627 \u062f\u06b5\u0633\u06c6\u0632 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062f\u0627\u062e\u0648\u0645 \u0628\u06d5 \u0686\u0627\u0648 \u0646\u06d5\u06cc\u0627\u0646\u062f\u06cc\u06af\u06d5\u060c \u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06ce \u062e\u0648\u0627 \u062f\u0627\u06cc\u0647\u0627\u0648\u0631\u062f\u06af\u06d5\u060c \u0633\u06ce\u0648\u06d5\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0695\u0627\u0633 \u0648 \u0686\u06d5\u067e\u0627 - \u0628\u06d5 \u0645\u0644\u06a9\u06d5\u0686\u06cc - \u0633\u0648\u0698\u062f\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u0627 \u0626\u06d5\u0648\u06d5\u0646\u061f',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0632\u06d5\u0648\u06cc\u0646\u062f\u0627 \u0628\u0632\u0627\u0648\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u0641\u0631\u06cc\u0634\u062a\u0627\u0646\u06cc\u0634\u060c \u0633\u0648\u0698\u062f\u06d5 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06d5\u0646 \u0648 \u0628\u0627\u063a\u0631\u06cc\u0634 \u0646\u06cc\u0646.',
    '\u062a\u0631\u0633\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u0627\u0646 \u0633\u06d5\u0631\u06cc\u0627\u0646\u061b \u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06ce\u06a9\u06cc \u0628\u06c6\u06cc\u0627\u0646 \u0628\u06ce\u060c \u0628\u06d5 \u062c\u06ce\u06cc \u062f\u06ce\u0646\u0646.',
    '\u062e\u0648\u0627 \u0648\u062a\u0648\u0648\u06cc\u06d5: \u0628\u0627 \u062f\u0648\u0648 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u062a\u0627\u0646 \u0646\u06d5\u0628\u06ce\u060c \u067e\u06d5\u0631\u0633\u062a\u0648\u0648 \u062a\u06d5\u0646\u06cc\u0627 \u06cc\u06d5\u06a9\u06ce\u06a9\u06d5 \u0648 \u0626\u06d5\u0634\u06ce \u0647\u06d5\u0631 \u0644\u06d5 \u0645\u0646 \u0628\u062a\u0631\u0633\u0646.',
    '\u0647\u06d5\u0631\u0686\u06cc\u0634 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0646\u062f\u0627 \u0647\u06d5\u0646\u060c \u0647\u06d5\u0631 \u0647\u06cc \u0626\u06d5\u0648\u0646 \u0648 \u062f\u06cc\u0646\u06cc \u0647\u06d5\u0645\u06cc\u0634\u06d5\u06cc\u06cc\u0634 \u0647\u06d5\u0631 \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u0626\u06d5\u0648\u06d5. \u0626\u0627\u062e\u06c6 \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646\u061f',
    '\u0647\u06d5\u0631 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9 \u0695\u0648\u0648 \u062a\u06ce\u062f\u06d5\u06a9\u0627 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5\u06cc\u06d5\u061b \u067e\u0627\u0634\u0627\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0648\u0631\u062a\u0645\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u060c \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u062f\u06d5\u0644\u0627\u06b5\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5 \u0628\u06d5\u06b5\u0627\u0634\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0644\u0627\u0628\u0631\u062f\u0646\u060c \u0647\u06ce\u0634\u062a\u0627 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u062a\u0627 \u0647\u06d5\u0631\u0686\u06cc \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0648\u0646 \u062d\u0627\u0634\u0627\u06cc \u0644\u06ce \u0628\u06a9\u06d5\u0646. \u0633\u0627 \u0695\u0627\u06cc \u0628\u0648\u06ce\u0631\u0646\u060c \u0632\u06c6\u0631 \u0646\u0627\u062e\u0627\u06cc\u06d5\u0646\u06ce \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646.',
    '\u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u0634 \u06a9\u06d5\u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0646\u060c \u0628\u06d5\u0634\u06ce\u06a9\u06cc \u062f\u06d5\u062f\u06d5\u0646 \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0647\u06cc\u0686 \u0642\u0627\u0632\u0627\u0646\u062c \u0648 \u0632\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u0628\u06cc\u0646\u0646. \u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u062e\u0648\u062f\u0627\u060c \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0631\u06c6 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u0646\u0648\u0648 \u0644\u06ce \u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5\u062a\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u06a9\u06cc\u0698\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u0627\u062f\u06d5\u0646\u06ce\u0646 - \u06a9\u06d5 \u0626\u06d5\u0648 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u067e\u0627\u06a9\u06d5 - \u0628\u06c6 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0686\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u0645\u0698\u062f\u06d5\u06cc \u06a9\u0686\u06ce \u0628\u06c6 \u06cc\u06d5\u06a9\u06ce\u06a9 \u0644\u06d5\u0645\u0627\u0646\u06d5 \u0628\u0686\u06ce\u060c \u0695\u0648\u062e\u0633\u0627\u0631\u06cc \u0695\u06d5\u0634 \u0647\u06d5\u06b5\u062f\u06d5\u06af\u06d5\u0695\u06ce \u0648 \u0644\u06d5\u0628\u06d5\u0631 \u062e\u06c6\u06cc \u067e\u06cc\u0634 \u062f\u06d5\u062e\u0648\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u0645\u06d5\u06cc\u0646\u06d5\u062a\u06cc \u0626\u06d5\u0648 \u0647\u06d5\u0648\u0627\u06b5\u06d5\u06cc \u067e\u06ce\u06cc\u062f\u0631\u0627\u0648\u06d5\u060c \u0644\u06d5 \u0647\u06c6\u0632\u06cc \u062e\u06c6\u06cc \u062a\u06d5\u0631\u06d5 \u062f\u06d5\u0628\u06ce\u061b \u0626\u0627\u062e\u06c6 \u0628\u06d5 \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06d5 \u062e\u06ce\u0648\u06cc \u0628\u06a9\u0627\u060c \u06cc\u0627\u0646 \u062f\u06d5\u0646\u0627\u062e\u06cc \u0626\u0627\u062e\u06cc \u0695\u0627\u06a9\u0627\u061f \u0626\u0627\u06cc \u0686\u06d5\u0646\u062f \u062e\u0631\u0627\u067e \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u06d5\u0646!',
    '\u0626\u06d5\u0645 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u062e\u0631\u0627\u067e\u0627\u0646\u06d5 \u0647\u06cc \u0648\u0627\u0646\u06d5\u06cc\u06d5\u060c \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u06d5 \u0646\u06cc\u06cc\u06d5. \u0646\u0645\u0648\u0648\u0646\u06d5\u06cc \u0628\u0627\u0634\u062a\u0631\u06cc\u0646 \u0626\u0627\u06a9\u0627\u0631\u060c \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0645\u06d5\u0631\u062f\u0645 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u0627\u0647\u06d5\u0642\u06cc \u06a9\u0631\u062f\u0646\u06cc\u0627\u0646 \u062e\u0648\u062f\u0627\u06af\u06cc\u0631 \u0628\u0627\u0646\u060c \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0647\u06d5\u062f\u06d5 \u062c\u0648\u0648\u0642\u06d5\u0648\u0627\u0631\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0695\u0627 \u0628\u0648\u0648\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062f\u06d5\u06cc\u0627\u0646\u0647\u06ce\u06b5\u06ce\u062a\u06d5\u0648\u06d5 \u0647\u06d5\u062a\u0627 \u0695\u06c6\u0698\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u0634 \u0648\u06d5\u062e\u062a\u06cc \u0647\u0627\u062a\u060c \u0646\u06d5 \u062a\u0627\u0648\u06ce\u06a9 \u0648\u06d5\u067e\u06ce\u0634\u06cc\u0627\u0646 \u062f\u06d5\u062e\u0627\u060c \u0646\u06d5 \u062f\u06d5\u0634\u06a9\u0631\u06ce \u0628\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0634\u062a\u06ce \u067e\u06d5\u0633\u0646\u062f\u06cc \u0646\u0627\u06a9\u06d5\u0646\u060c \u0644\u0627\u06cc\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0647\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u0632\u0645\u0627\u0646\u06cc \u0641\u06ce\u0631\u06d5 \u062f\u0631\u06c6\u0634\u06cc\u0627\u0646 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u062f\u0627\u062a\u06d5 \u067e\u0627\u06b5 \u062e\u06c6\u06cc\u0627\u0646. \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0628\u06d5\u0634\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0626\u0627\u06af\u0631\u06d5 \u0648 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06d5\u0634 \u062f\u06d5\u062f\u0631\u06ce\u0646\u06d5 \u0628\u06d5\u0631.',
    '\u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u062e\u0648\u062f\u0627\u060c \u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6\u0634 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u0645\u0627\u0646 \u0628\u06c6 \u0633\u06d5\u0631 \u06af\u06d5\u0644\u0627\u0646 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f\u0646. \u0634\u06d5\u06cc\u062a\u0627\u0646 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u06cc \u0628\u06c6 \u062e\u06d5\u0645\u06b5\u0627\u0646\u062f\u0646\u061b \u06a9\u06d5\u0648\u0627 \u0626\u06d5\u0645\u0631\u06c6\u0634 \u062f\u06c6\u0633\u062a\u06cc \u0646\u0632\u06cc\u06a9\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5\u060c \u0647\u06d5\u0631 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06d5 \u0648 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u0645\u0627\u0646 \u0626\u06d5\u0695\u0627\u06cc \u062a\u06c6 \u0647\u06d5\u0646\u0627\u0631\u062f\u06d5 \u0628\u06d5\u0631\u06c6\u060c \u06a9\u06d5 \u0628\u06d5\u06a9\u06ce\u0634\u06d5\u06cc \u0646\u06ce\u0648\u0627\u0646 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0695\u0627\u0628\u06af\u06d5\u06cc \u0648 \u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0628\u06ce \u0628\u06c6 \u06a9\u06d5\u0633\u0627\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695.',
    '\u062e\u0648\u0627 \u0626\u0627\u0648\u06cc \u0644\u06d5 \u062d\u06d5\u0648\u0627\u06cc\u06d5\u0648\u06d5 \u062f\u0627\u0628\u0627\u0631\u0627\u0646\u062f\u061b \u0632\u06d5\u0648\u06cc \u0645\u0631\u062f\u0648\u0648\u06cc \u067e\u06ce \u0698\u06cc\u0627\u0646\u062f\u06d5\u0648\u06d5. \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06af\u0648\u06ce \u0628\u06cc\u0633 \u062f\u06d5\u0628\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u0648\u06b5\u0633\u0627\u062a\u06cc\u0634\u0627 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06d5\u0646\u062f\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5: \u0644\u06d5 \u0646\u0627\u0648 \u0633\u06a9\u06cc\u0627\u0646\u060c \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc \u0695\u06cc\u062e \u0648 \u062e\u0648\u06ce\u0646\u0695\u0627\u060c \u0634\u06cc\u0631\u06ce\u06a9\u06cc \u067e\u0627\u06a9 \u0648 \u0695\u06d5\u0648\u0627\u0646\u0648\u0648 \u062f\u06d5\u0631\u062e\u0648\u0627\u0631\u062f \u0626\u06d5\u062f\u06d5\u06cc\u0646\u060c \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u06d5\u062e\u06c6\u0631\u06d5\u0648\u06d5\u0646 \u06af\u06c6\u0627\u0631\u0627\u06cc\u06d5.',
    '\u0644\u06d5 \u0628\u06d5\u0631\u0647\u06d5\u0645\u06cc \u062f\u0627\u0631\u06d5\u062e\u0648\u0631\u0645\u0627 \u0648 \u0695\u06d5\u0632\u06d5\u06a9\u0627\u0646\u06cc\u0634\u060c \u0633\u06d5\u0631\u062e\u06c6\u0634 \u06a9\u06d5\u0631\u06d5 \u0648 \u0628\u0698\u06cc\u0648\u06cc \u0628\u0627\u0634 \u0695\u06d5\u0646\u06ce\u0648 \u062f\u06ce\u0646\u0646. \u0644\u06d5\u0645\u06d5\u0634\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0647\u06d5\u06cc\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646.',
    '\u062e\u0648\u0627 \u0645\u06ce\u0634\u06d5\u0646\u06af\u0648\u06cc\u0646\u06cc \u0695\u0627\u0626\u06d5\u0633\u067e\u0627\u0631\u062f\u060c \u06a9\u06d5: \u0644\u06d5 \u06a9\u0627\u0648\u0627\u0646 \u0648 \u062f\u0627\u0631\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u0634\u0648\u06ce\u0646\u06cc \u0647\u06d5\u06b5\u06cc\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646\u060c \u0634\u0648\u06ce\u0646\u06cc \u0698\u06cc\u0646\u06cc \u062e\u06c6\u062a \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u06d5.',
    '\u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u06af\u0634 \u0628\u06d5\u0631\u0648\u0628\u0648\u0648 \u0628\u0698\u06cc\u0648\u062a \u0628\u0645\u0698\u06d5\u061b \u0626\u06cc\u062a\u0631 - \u0628\u06d5 \u0645\u0644\u06a9\u06d5\u0686\u06cc - \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062e\u06c6\u062a \u0628\u06af\u0631\u06d5\u0628\u06d5\u0631. \u0644\u06d5 \u062f\u06d5\u0631\u0648\u0648\u0646\u06cc \u062a\u0631\u0627\u0648\u06ce\u06a9\u06cc \u0647\u06d5\u0645\u06d5\u0695\u06d5\u0646\u06af\u06d5 \u062f\u06ce\u062a\u06d5 \u062f\u06d5\u0631\u06ce\u061b \u0645\u0641\u0627\u06cc \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u062a\u06ce\u062f\u0627\u06cc\u06d5\u061b \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u0634\u062f\u0627 \u0628\u06c6 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u0695\u0627\u062f\u06d5\u0645\u06ce\u0646\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0626\u06ce\u0648\u06d5\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u06d5\u0628\u0648\u0648 \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f \u0648 \u067e\u0627\u0634\u0627\u0646 \u06af\u06cc\u0627\u0646\u0648\u0648 \u0644\u06ce \u062f\u06d5\u0633\u062a\u06ce\u0646\u06ce. \u062a\u06ce\u062a\u0627\u0646\u062f\u0627\u06cc\u06d5 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u067e\u06cc\u0631\u06cc \u0632\u06d5\u0645\u06cc\u0646\u06af\u06cc\u0631\u060c \u062a\u0627 \u0644\u06d5\u067e\u0627\u0634 \u0632\u0627\u0646\u06cc\u0646\u06cc \u0632\u06c6\u0631 \u0634\u062a \u0647\u06cc\u0686 \u0634\u062a\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u0646\u06d5\u0645\u06ce\u0646\u06ce. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc\u06d5 \u0648 \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0695\u06c6\u0632\u06cc \u0647\u06ce\u0646\u062f\u06ce\u06a9\u062a\u0627\u0646\u06cc \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u067e\u062a\u0631 \u062f\u0627\u0648\u06d5. \u0626\u06d5\u0648\u06cc \u067e\u062a\u0631\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5\u060c \u0628\u06d5\u0634\u06cc \u0698\u06ce\u0631\u062f\u06d5\u0633\u062a\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u062f\u06d5\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0644\u06d5\u0648\u06d5\u0634\u062f\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u0646. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u062e\u0648\u0627 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u062c\u0633\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062c\u0648\u0648\u062a\u06d5\u06cc \u062f\u0627\u0646\u06ce\u061b \u0644\u06d5 \u0647\u0627\u0648\u062c\u0648\u0648\u062a\u0627\u0646 \u06a9\u0648\u0695 \u0648 \u0646\u06d5\u0648\u06d5\u06cc \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc\u0648\u0646 \u0648 \u0695\u06c6\u0632\u06cc \u067e\u06d5\u0633\u0646\u062f\u06cc \u067e\u06ce\u062f\u0627\u0648\u0646. \u0686\u06c6\u0646 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0648 \u0647\u06cc\u0686 \u0648 \u067e\u0648\u0648\u0686\u0627\u0646\u06d5 \u0647\u06d5\u06cc\u06d5 \u0648 \u0644\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u062e\u0648\u0627 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u060c \u0634\u062a\u06cc \u0648\u06d5\u0647\u0627 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646 \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0639\u06d5\u0631\u0632 \u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u062f\u0627 \u0647\u06cc\u0686 \u0628\u0698\u06cc\u0648\u06ce \u0634\u06a9 \u0646\u0627\u0628\u06d5\u0646 \u06a9\u06d5 \u0628\u06cc\u0627\u0646\u062f\u06d5\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u0646\u0627\u0634\u062a\u0648\u0627\u0646\u0646.',
    '\u0626\u06ce\u0648\u06d5 \u0646\u06d5\u0632\u06cc\u0644\u0627\u0646 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0645\u06d5\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5\u061b \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u06d5\u0632\u0627\u0646\u06ce \u0648 \u0626\u06ce\u0648\u06d5\u0646 \u06a9\u06d5 \u0647\u06cc\u0686\u06ce\u06a9 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u062e\u0648\u0627 \u0646\u06d5\u0632\u06cc\u0644\u06ce\u06a9 \u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5: \u0628\u06d5\u0646\u06cc\u06d5\u06a9 \u0647\u06d5\u06cc\u06d5 \u0647\u06cc\u0686 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0644\u06d5 \u062f\u06d5\u0633 \u0646\u0627\u06cc\u06d5\u061b \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0647\u06d5\u0633 \u0628\u0698\u06cc\u0648\u06cc \u0628\u0627\u0634\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5\u062a\u06ce\u060c \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0648 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u06d5\u062e\u062a\u06cc \u062f\u06d5\u06a9\u0627\u061b \u0626\u0627\u062e\u06c6 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9 \u062f\u06ce\u0646\u06d5\u0648\u06d5\u061f \u0633\u0648\u067e\u0627\u0633 \u0647\u06d5\u0631 \u0634\u0627\u06cc\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u0626\u06d5\u0645\u06d5\u0634 \u0628\u06d5 \u0646\u06d5\u0632\u06cc\u0644\u06d5 \u062f\u06ce\u0646\u06ce: \u062f\u0648\u0648 \u0645\u0631\u06c6 \u0647\u06d5\u0646\u061b \u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u0627\u06b5\u06d5 \u0648 \u0647\u06cc\u0686 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0644\u06d5 \u062f\u06d5\u0633 \u0646\u0627\u06cc\u06d5 \u0648 \u0633\u06d5\u0631\u0628\u0627\u0631\u06cc \u0626\u0627\u063a\u0627\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0648 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u0648\u06ce\u06d5\u06a9\u06cc \u0628\u06cc\u0646\u06ce\u0631\u06ce\u060c \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5 \u0646\u0627\u0647\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u061b \u0644\u06d5\u0644 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06c6 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u062f\u06d5\u062f\u0627 \u0648 \u062e\u06c6\u06cc\u0634\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5\u060c \u0686\u06c6\u0646 \u0686\u0648\u0646 \u06cc\u06d5\u06a9\u0646\u061f',
    '\u0647\u06d5\u0631 \u067e\u06cc\u0648\u0627\u0631\u06ce \u0644\u06d5 \u0626\u06d5\u0631\u0632 \u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5. \u0647\u0627\u062a\u0646\u06cc \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u06d5\u0634 \u0648\u06d5\u06a9 \u0686\u0627\u0648\u062a\u0631\u0648\u0648\u06a9\u0627\u0646\u06ce \u0648\u0627\u06cc\u06d5\u061b \u06cc\u0627 \u0646\u0632\u06cc\u06a9\u062a\u0631. \u062e\u0648\u0627 \u0647\u06ce\u0632\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u062f\u06d5\u0634\u06a9\u06ce.',
    '\u062e\u0648\u062f\u0627 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5 \u067e\u0632\u062f\u0627\u0646\u06cc \u062f\u0627\u06cc\u06a9\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0648\u06d5\u062f\u06d5\u0631\u062e\u0633\u062a\u061b \u06a9\u06d5\u0686\u06cc \u0647\u06cc\u0686\u0648\u0648 \u0646\u06d5\u062f\u06d5\u0632\u0627\u0646\u06cc. \u0686\u0627\u0648 \u0648 \u06af\u0648\u06ce\u0686\u06a9\u06d5 \u0648 \u062f\u06b5\u06cc \u062f\u0627\u0646\u06ce\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631 \u0628\u0646.',
    '\u0626\u0627\u062e\u06c6 \u067e\u06d5\u0644\u06d5\u0648\u06d5\u0631 \u0646\u0627\u0628\u06cc\u0646\u0646\u060c \u06a9\u06d5 \u0644\u06d5 \u062a\u06d5\u0634\u0642\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06ce \u0695\u0627\u0645 \u06a9\u0631\u0627\u0648\u0646\u061f \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627 \u06a9\u06ce \u0695\u0627\u06cc\u0627\u0646\u062f\u06d5\u06af\u0631\u06ce\u061f \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u0646 \u0628\u06c6 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646.',
    '\u062e\u0648\u062f\u0627 \u062e\u0627\u0646\u0648\u0648\u06cc \u0628\u06c6 \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u0646 \u06a9\u06d5 \u062a\u06ce\u0627\u0646\u062f\u0627 \u0628\u0633\u06ce\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0646\u0648\u0627\u0634\u06cc \u0644\u06d5 \u067e\u06ce\u0633\u062a\u06cc \u0648\u06b5\u0633\u0627\u062a \u0628\u06c6 \u0633\u0627\u0632 \u062f\u0627\u0648\u0646 \u06a9\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u06a9\u06c6\u0686 \u06a9\u0631\u062f\u0646\u0627 \u0648 \u0644\u06d5 \u06a9\u0627\u062a\u06cc \u0645\u0627\u0646\u06d5\u0648\u06d5\u0634\u062a\u0627\u0646\u0627\u060c \u0633\u0648\u0648\u06a9 \u0628\u06ce \u0628\u06c6\u062a\u0627\u0646. \u0644\u06d5 \u0647\u0631\u06cc\u0634 \u0648 \u0644\u0648\u0627\u0648 \u0645\u0648\u0648\u06cc\u0627\u0646\u060c \u06a9\u06d5\u0644 \u067e\u06d5\u0644\u06cc \u067e\u06ce\u0648\u06cc\u0633\u062a\u06cc \u0645\u0627\u06b5 \u0648 \u06a9\u0627\u06b5\u0627 \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u0645\u0627\u0648\u06d5\u06cc \u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u0648\u0648\u062f\u0627.',
    '\u062e\u0648\u0627 \u0644\u06d5\u0648\u06d5\u06cc \u062e\u0648\u06b5\u0642\u0627\u0646\u062f\u0648\u0648\u06cc\u06d5\u062a\u06cc\u060c \u0646\u0633\u06ce \u0632\u06c6\u0631\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u06a9\u06ce\u0648\u0627\u0646\u06cc\u0634 \u067e\u06d5\u0633\u06cc\u0648\u06cc \u0628\u06c6 \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u0646 \u0648 \u062c\u0644 \u0648 \u0628\u06d5\u0631\u06af\u06ce\u06a9\u06cc\u0634\u06cc \u0628\u06c6\u062a\u0627\u0646 \u0633\u0627\u0632 \u062f\u0627\u0648\u06d5 \u06a9\u06d5 \u0644\u06d5 \u06a9\u0627\u062a\u06cc \u0634\u06d5\u0695 \u0648 \u06af\u06d5\u0631\u0645\u0627\u062f\u0627 \u062f\u06d5\u0648\u067e\u0627\u0631\u06ce\u0632\u06ce. \u0626\u0627\u0648\u0627 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0647\u0631\u06d5\u06af\u06d5\u0644\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062a\u06d5\u0648\u0627\u0648 \u062f\u06d5\u06a9\u0627\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u06af\u0648\u06ce \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0628\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0648\u06d5\u0631\u0686\u06d5\u0631\u062e\u0627\u0646\u062f\u060c \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u060c \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06c6\u06cc\u06d5 \u0648 \u0647\u06cc\u0686\u06cc \u062a\u0631 \u0646\u0627.',
    '\u0628\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0632\u0627\u0646\u0646\u061b \u06a9\u06d5\u0686\u06cc \u062d\u0627\u0634\u0627\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0626\u06d5\u0645\u06d5\u06af \u0646\u06d5\u0646\u0627\u0633\u0646.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9 \u06cc\u06d5\u06a9\u06ce \u0628\u06c6 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u06ce\u0646\u06cc\u0646\u060c \u0626\u06d5\u0648\u0633\u0627 \u0626\u06cc\u0632\u0646\u06cc [\u0626\u0627\u062e\u0627\u0641\u062a\u0646] \u0628\u06d5 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0646\u0627\u062f\u0631\u06ce \u0648 \u0639\u0648\u0632\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u067e\u06d5\u0632\u06ce\u0631\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642 \u0628\u0648\u0648\u0646\u060c \u0626\u0627\u0632\u0627\u0631 \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u062f\u06cc\u061b \u0646\u06d5 \u0644\u06ce\u06cc\u0627\u0646 \u0633\u06a4\u06a9\u062a\u0631 \u0626\u06d5\u06a9\u0631\u06ce \u0648 \u0646\u06d5 \u0647\u06cc\u0686 \u0648\u0686\u0627\u0646\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u0631\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u0648\u062a: \u062e\u0648\u0627 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0647\u06d5\u0646\u060c \u0647\u06d5\u0631\u06a9\u06d5 \u0686\u0627\u0648\u06cc\u0627\u0646 \u0628\u06d5 \u0647\u0627\u0648\u0628\u06d5\u0634\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u060c \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0626\u06d5\u0645\u0627\u0646\u06d5\u0646 \u0647\u0627\u0648\u0628\u06d5\u0634\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u06a9\u06d5 \u062c\u06cc\u0627 \u0644\u06d5 \u062a\u06c6 \u0647\u0627\u0646\u0627\u0645\u0627\u0646 \u0626\u06d5\u0628\u0631\u062f\u06d5 \u0628\u06d5\u0631\u06cc\u0627\u0646. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06d5 \u062c\u0648\u0627\u0648\u06cc\u0627\u0646\u0627 \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0631\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0628\u06ce\u0698\u0646.',
    '\u0626\u06cc\u062a\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce\u062f\u0627 \u0646\u0627\u0686\u0627\u0631 \u062f\u06d5\u0628\u0646 \u0645\u0644\u06a9\u06d5\u0686\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0628\u0646. \u0626\u06d5\u0648\u06d5\u0634 \u06a9\u06d5\u0648\u0627 \u0647\u06d5\u06b5\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u0633\u062a\u060c \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u0641\u06ce\u0695\u06c6 \u062f\u06d5\u0631\u062f\u06d5\u0686\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0631\u0628\u06d5\u0633\u062a \u0626\u06d5\u06a9\u0631\u062f\u060c \u0644\u06d5 \u062c\u06d5\u0632\u0627\u06cc \u0626\u06d5\u0648 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc\u06d5\u062f\u0627 \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f - \u0642\u0627\u062a \u0644\u06d5\u0628\u0627\u0646 \u0642\u0627\u062a - \u0647\u06d5\u0631 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u067e\u062a\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0647\u06d5\u0631 \u06af\u06d5\u0644\u06ce \u06cc\u06d5\u06a9 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0634\u0627\u06cc\u06d5\u062a \u0695\u0627\u0633\u062a \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u060c \u062a\u06c6 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0626\u06ce\u0645\u06d5\u06cc \u0628\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u0634\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u060c \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0695\u0648\u0648\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0695\u06ce\u0646\u0648\u06ce\u0646\u06cc \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc \u0648 \u0645\u0698\u062f\u06d5\u06cc\u06d5 \u0628\u06c6 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u0627\u0646.',
    '\u062e\u0648\u0627 \u0628\u06d5 \u0639\u06d5\u062f\u0627\u06b5\u06d5\u062a \u0648 \u0686\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u062e\u0634\u06cc\u0646 \u0628\u06d5 \u062e\u0632\u0645 \u0648 \u06a9\u06d5\u0633\u0648\u06a9\u0627\u0631\u060c \u0641\u06d5\u0631\u0645\u0627\u0646 \u062f\u06d5\u062f\u0627 \u0648 \u0695\u06ce \u0646\u0627\u062f\u0627 \u0628\u06d5 \u0626\u0627\u06a9\u0627\u0631\u06cc \u067e\u06cc\u0633 \u0648 \u062e\u0631\u0627\u067e\u06d5 \u0648 \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06cc. \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u062a\u0627\u0646 \u0644\u06ce\u062f\u06d5\u06a9\u0627 \u0628\u06d5\u0634\u06a9\u0648\u0648 \u067e\u06d5\u0646\u062f\u06cc \u0644\u06ce \u0648\u06d5\u0631\u06af\u0631\u0646.',
    '\u0626\u06d5\u0648 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627\u06cc \u062f\u06d5\u0628\u06d5\u0633\u062a\u0646\u060c \u0628\u06d5 \u062c\u06ce \u0628\u06ce\u0646\u0646. \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u0627\u0646\u06cc\u0634\u0648\u0648 \u0645\u06d5\u0634\u06a9\u06ce\u0646\u0646\u061b \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0644\u06ce \u0628\u0695\u0627\u0648\u0646 \u067e\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06ce\u0646\u0646 \u0648 \u062e\u0648\u062f\u0627\u062a\u0627\u0646 \u0628\u06d5 \u0686\u0627\u0648\u062f\u06ce\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u0627\u0646\u0627\u0648\u06d5. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0647\u0627\u06cc \u0644\u06d5 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0646\u06d5\u0686\u0646 \u0648\u06d5\u06a9 \u0698\u0646\u06d5\u06a9\u06d5 \u0628\u06a9\u06d5\u0646\u060c \u06a9\u06d5 \u0695\u06cc\u0633\u06d5\u06a9\u06d5\u06cc - \u0644\u06d5 \u067e\u0627\u0634 \u06a9\u0631\u0698\u06cc - \u062e\u0627\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u067e\u0633 \u067e\u0633\u06cc \u06a9\u0631\u062f\u060c \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646\u06d5 \u0647\u06c6\u06cc \u0641\u0631\u06cc\u0648\u062f\u0627\u0646\u06ce\u061b \u0628\u06d5\u0648 \u0628\u06d5\u0647\u0627\u0646\u06d5\u06cc \u0641\u06b5\u0627\u0646\u06d5\u06af\u06d5\u0644 \u0644\u06d5 \u06af\u06d5\u0644\u06ce \u062a\u0631 \u0628\u0627\u0648\u062e\u06c6\u0634\u062a\u0631\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0645\u06d5 \u062a\u0627\u0642\u06cc\u0648 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u062a\u0627 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634\u062f\u0627 \u0628\u06c6\u062a\u0627\u0646\u06cc \u0695\u06c6\u0634\u0646 \u0628\u06a9\u0627\u062a\u06d5\u0648\u060c \u0644\u06d5\u0633\u06d5\u0631\u0686\u06cc \u067e\u06ce\u06a9 \u0646\u06d5\u062f\u06d5\u0647\u0627\u062a\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u06a9\u06d5\u06cc\u0641\u06cc \u0644\u06ce \u0628\u0627\u060c \u06af\u0634 \u062f\u06d5\u06cc\u06a9\u0631\u062f\u0646 \u0628\u06d5 \u0647\u0627\u0648 \u0626\u0627\u06cc\u06cc\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u06af\u0648\u0645\u0695\u0627\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u0644\u06d5 \u06a9\u06ce\u0634 \u062d\u06d5\u0632 \u06a9\u0627\u060c \u062f\u06d5\u06cc\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5. \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0644\u06ce\u062a\u0627\u0646 \u062f\u06d5\u067e\u0631\u0633\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0686\u06cc\u062a\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0628\u06d5 \u0633\u0648\u06ce\u0646\u062f\u0627\u0646\u0648\u0648 \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u0644\u06d5 \u062e\u0634\u062a\u06d5 \u0645\u06d5\u0628\u06d5\u0646\u061b \u0646\u06d5\u06a9\u0627 \u067e\u0627\u062a\u0627\u0646 \u067e\u0627\u0634 \u067e\u062a\u06d5\u0648 \u0628\u0648\u0648\u0646\u06cc \u0628\u062e\u0644\u06cc\u0633\u06a9\u06ce \u0648 \u0644\u06d5\u0628\u06d5\u0631 \u0695\u06ce\u06af\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0633\u062a\u0646\u06cc\u0634\u062a\u0627\u0646\u060c \u062a\u0627\u0645\u06cc \u0628\u06d5\u06b5\u0627 \u0628\u0686\u06ce\u0698\u0646 \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0632\u06c6\u0631\u0648\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06ce.',
    '\u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0628\u06d5 \u0646\u0631\u062e\u06cc \u0647\u0646\u062f\u06a9 \u0645\u06d5\u0641\u0631\u06c6\u0634\u0646\u061b \u06af\u06d5\u0631 \u062a\u06ce\u0628\u06af\u06d5\u0646\u060c \u0626\u06d5\u0648\u06cc \u0644\u06d5 \u062c\u06d5\u0645 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0632\u06c6\u0631 \u0628\u0627\u0634\u062a\u0631\u06d5.',
    '\u0626\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0644\u0627\u062a\u0627\u0646\u06d5 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u062f\u06d5\u0686\u06ce\u061b \u0626\u06d5\u0648\u06cc \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0647\u06d5\u0631 \u062f\u06d5\u0645\u06ce\u0646\u06ce \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0648\u0648\u0646\u060c \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 - \u0628\u06d5 \u0632\u06cc\u0627\u062f\u06d5\u0648\u06d5 - \u0628\u06c6\u06cc\u0627\u0646 \u062a\u06ce\u0647\u06d5\u06b5\u062f\u06ce\u0646\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u0631\u062f\u0627\u0631\u06cc \u0628\u0627\u0634\u06d5 \u0648 \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u06d5 - \u0686 \u0646\u06ce\u0631\u060c \u0686 \u0645\u06ce - \u0647\u06d5\u062a\u0627 \u062f\u06d5\u0698\u06cc \u0648\u0627\u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0628\u06d5 \u062e\u06c6\u0634\u06cc \u0628\u0698\u06cc\u061b \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0628\u0627\u0634\u062a\u0631 \u0644\u06d5\u0648\u06d5\u06cc \u0648\u0627 \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u060c \u0626\u06ce\u0645\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06ce.',
    '\u06a9\u0627\u062a\u06ce \u0642\u0648\u0631\u0626\u0627\u0646\u06ce \u062f\u06d5\u062e\u0648\u06ce\u0646\u06cc\u060c \u0647\u06d5\u0631 \u067e\u06d5\u0646\u0627 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631\u06d5 \u0644\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06cc \u062a\u0695\u06c6\u06a9\u0631\u0627\u0648.',
    '\u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0632\u06c6\u0631\u06ce\u06a9\u06cc \u0646\u06cc\u06cc\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u0648\u0627\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0626\u06d5\u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u0647\u06d5\u0631 \u0628\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 \u062f\u06d5\u0633\u062a\u06cc \u062f\u06d5\u0695\u0648\u0627\u060c \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u0648 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06ce \u062a\u0631 \u0648\u06d5\u06af\u06c6\u0695\u06cc - \u062e\u0648\u062f\u0627\u0634 \u062e\u06c6\u06cc \u0686\u0627\u06a9\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06cc \u062f\u06d5\u0646\u06ce\u0631\u06ce - \u0626\u06ce\u0698\u0646: \u062e\u06c6\u062a \u0647\u06d5\u06b5\u062a \u0628\u06d5\u0633\u062a\u0648\u0648\u06d5. \u0646\u0627 \u0648\u0627 \u0646\u06cc\u06cc\u06d5! \u0626\u06d5\u0648\u0627\u0646 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u06af\u06cc\u0627\u0646\u06d5 \u067e\u06cc\u0631\u06c6\u0632\u06d5\u06a9\u06d5\u060c \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0695\u0627 \u0647\u06ce\u0646\u0627\u0648\u06cc\u06d5\u061b \u0647\u06d5\u062a\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646 \u062f\u06b5\u0646\u06cc\u0627 \u06a9\u0627 \u0648 \u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631 \u0648 \u0645\u0698\u062f\u06d5 \u0628\u06ce \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u06d5 \u0628\u0627\u0634 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0628\u0646\u06cc\u0627\u062f\u06d5\u0645\u06ce \u0641\u06ce\u0631\u06cc \u062f\u06d5\u06a9\u0627. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0626\u06d5\u0648 \u0646\u06cc\u0627\u0632\u06cc\u0627\u0646\u06d5 \u0644\u06d5 \u0639\u0627\u0631\u06d5\u0628\u06cc\u062f\u0627 \u06a9\u06c6\u06b5\u06cc\u06d5 \u0648 \u0626\u0627\u0634\u06a9\u0631\u0627\u0634\u06d5 \u06a9\u06d5 \u0632\u0645\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0628\u06d5 \u0639\u0627\u0631\u06d5\u0628\u06cc\u06cc\u06d5\u06a9\u06cc \u0695\u06d5\u0648\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u06cc \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u0627\u0646 \u0646\u0627\u06a9\u0627 \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u0631\u06c6 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u0627\u0648\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06a9\u06d5 \u0628\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0646. \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u0628\u06ce \u0648 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u062d\u0627\u0634\u0627\u06cc \u0644\u06ce \u0628\u06a9\u0627\u060c [\u0633\u0632\u0627 \u062f\u06d5\u062f\u0631\u06ce] \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0633\u06ce \u0646\u0627\u0686\u0627\u0631 \u06a9\u0631\u06ce \u0648 \u0644\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u062f\u06b5\u0646\u06cc\u0627 \u0628\u06ce. \u0628\u06d5\u06b5\u0627\u0645 \u06a9\u06d5\u0633\u06ce \u0628\u06d5 \u062d\u0627\u0634\u0627\u06a9\u06d5\u06cc \u062f\u06b5\u06cc \u062e\u06c6\u0634 \u0628\u06ce\u060c \u0628\u06d5\u0631 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u062e\u0648\u0627 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646 \u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631 \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u0686\u0648\u0646 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06cc \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0632 \u067e\u062a\u0631 \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u061b \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627\u0634 \u06af\u0631\u06c6\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0646\u0627\u06a9\u0627.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0646 \u062e\u0648\u062f\u0627 \u0645\u06c6\u0631\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06b5\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0648\u06d5 \u0648 \u0686\u0627\u0648\u06cc \u0628\u06ce \u0633\u06c6 \u0648 \u06af\u0648\u06ce\u06cc \u06a9\u067e \u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u0626\u06d5\u0645\u0627\u0646\u06d5\u0646 \u0628\u06ce \u0626\u0627\u06af\u0627\u06cc\u06d5\u06a9\u0627\u0646.',
    '\u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce\u062f\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u0646 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0646.',
    '\u062c\u0627 \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u067e\u0627\u0634 \u0626\u06d5\u0648 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06d5 \u0633\u06d5\u062e\u062a\u0627\u0646\u06d5\u060c \u0632\u06ce\u062f\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u062c\u06ce\u0647\u06ce\u0634\u062a \u0648 \u0686\u0648\u0648\u0646\u06d5 \u062e\u06d5\u0632\u0627 \u0648 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0648\u0648\u0646\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a - \u067e\u0627\u0634 \u0626\u06d5\u0645\u0627\u0646\u06d5 - \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u06a9\u06ce\u0634\u06d5\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u06d5 \u067e\u06ce\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u06cc\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u062a\u06d5\u0648\u0627\u0648 \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u06ce \u0648 \u0647\u06cc\u0686 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u062e\u0648\u0627 \u0634\u0627\u0631\u06ce\u06a9\u06cc \u0646\u06d5\u0632\u06cc\u0644\u06d5 \u0647\u06ce\u0646\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0632\u06c6\u0631 \u0647\u06ce\u0645\u0646 \u0648 \u0628\u06ce\u0648\u06d5\u06cc \u0628\u0648\u0648\u0646\u061b \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0626\u0627\u0642\u0627\u0631\u06ce\u06a9\u06d5\u0648\u06d5 \u0641\u0631\u06d5 \u0628\u0698\u06cc\u0648\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0647\u0627\u062a\u061b \u06a9\u06d5\u0686\u06cc \u0644\u06d5\u0645 \u0647\u06d5\u0645\u0648\u0648 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u06a9\u0631\u062f\u0646\u060c \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f. \u0644\u06d5 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u0626\u06d5\u0648 \u06a9\u0631\u062f\u0627\u0631\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06d5\u0631\u06af\u06cc \u0628\u0631\u0633\u06ce\u062a\u06cc \u0648 \u062a\u0631\u0633\u06cc \u067e\u06ce\u0686\u0627\u0646.',
    '\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u0627\u062a\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646\u06cc\u0627\u0646 \u0632\u0627\u0646\u06cc. \u062c\u0627 \u0686\u0648\u0646\u06a9\u06d5 \u0647\u06ce\u0646\u062f\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u0628\u0648\u0648\u0646\u060c \u0647\u06d5\u0645\u0648\u0648 \u062a\u0648\u0648\u0634\u06cc \u062a\u0648\u0648\u0634\u06cc \u0647\u0627\u062a\u0646.',
    '\u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u062e\u0648\u0627 \u067e\u06ce\u06cc \u062f\u0627\u0648\u0646\u060c \u0628\u062e\u06c6\u0646\u060c \u062d\u06d5\u06b5\u0627\u06b5 \u0648 \u0632\u06d5\u06b5\u0627\u06b5\u06d5 \u0648 \u0647\u06d5\u0631 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0628\u0698\u06ce\u0631\u06cc \u0628\u06d5\u0647\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0628\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646.',
    '\u062e\u0648\u062f\u0627 \u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u067e\u0627\u0631\u06ce\u0632\u06cc \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u0646 \u0644\u06d5 \u0645\u0631\u062f\u0627\u0631 \u0648 \u06af\u06c6\u0634\u062a \u0628\u06d5\u0631\u0627\u0632 \u0648 \u062e\u0648\u06ce\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0646\u0627\u0648\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627\u06cc \u0644\u06ce \u062f\u06ce\u0646\u0646. \u06a9\u06ce\u0634 \u0646\u0627\u0686\u0627\u0631 \u0628\u0648\u0648\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc \u062a\u06ce\u0631\u06cc \u0628\u06a9\u0627 \u0648 \u0626\u06cc\u062a\u0631 \u062f\u06d5\u0633\u062a \u062f\u0631\u06ce\u0698\u06cc \u0646\u06d5\u06a9\u0627\u061b \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u06af\u0648\u0646\u0627\u0628\u06d5\u062e\u0634 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0645\u0627\u0646\u0648\u0648 \u0646\u06d5\u06af\u06d5\u0695\u06ce\u061b \u06a9\u06d5 \u0626\u06d5\u0645 \u0695\u06d5\u0648\u0627 \u0648 \u0626\u06d5\u0645 \u0646\u0627\u0695\u06d5\u0648\u0627\u0633\u061b \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u0627\u0648\u06d5 \u06a4\u0695 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646 \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u0628\u0646\u061b',
    '\u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06ce\u06a9\u06cc \u0647\u0646\u062f\u06a9 \u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062c\u0648\u0648\u0644\u06d5\u06a9\u06d5\u0634\u0646\u060c \u0626\u06d5\u0648 \u067e\u0627\u0631\u06ce\u0632\u06d5\u0645\u0627\u0646 \u062f\u0627\u0646\u0627\u0648\u06d5 \u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u0628\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u06af\u0648\u062a. \u0626\u06ce\u0645\u06d5 \u0647\u06cc\u0686 \u06af\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0645\u0627\u0646 \u0644\u06d5\u0648\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u061b \u0626\u06d5\u0648\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u06d5\u0632\u0627\u0646\u06cc \u062e\u0631\u0627\u067e\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0695\u0648\u0648 \u062f\u06d5\u062f\u0627 \u0648 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u067e\u0631\u06cc\u0646\u06af\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0628\u0646\u06d5 \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a - \u067e\u0627\u0634 \u0626\u06d5\u0645\u0627\u0646\u06d5 - \u06af\u0648\u0646\u0627\u0628\u06d5\u062e\u0634 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u067e\u06ce\u0634\u06d5\u0648\u0627\u06cc\u06ce\u06a9\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u0647\u06d5\u0642 \u0686\u0648\u0648\u06af \u0648 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u061b \u0646\u06d5 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0648\u0648 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0646\u0627\u0633\u0646.',
    '\u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0644\u06d5 \u0686\u0627\u0648\u062f\u0627 \u0628\u0648\u0648\u061b \u062e\u0648\u0627\u0634 \u0628\u06c6 \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u06cc\u0628\u0698\u0627\u0631\u062f \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u06a9\u0631\u062f.',
    '\u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0628\u06d5\u0647\u0631\u06d5\u0645\u0627\u0646 \u062f\u0627\u06cc\u06d5\u0648 \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627\u0634 \u0644\u06d5 \u0628\u06d5\u0631\u06a9\u06d5\u062a\u06cc\u0627\u0646\u06d5.',
    '\u067e\u0627\u0634\u0627\u0646 \u0628\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u060c \u06a9\u06d5: \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u0627\u06cc\u06cc\u0646\u06cc \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u0647\u06d5\u0642 \u0686\u0648\u0648\u06af \u0628\u06d5\u061b \u0626\u06d5\u0648 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06d5\u0628\u0648\u0648 \u06a9\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u0627 \u062f\u0627\u062f\u06d5\u0646\u06ce\u0646.',
    '\u0695\u06ce\u0632\u06af\u0631\u062a\u0646\u06cc \u0695\u06c6\u0698\u06cc \u0634\u06d5\u0645\u0645\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0695\u06cc\u0627\u0631 \u062f\u0631\u0627 \u06a9\u06d5 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u06d5\u06a9\u0631\u062f. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u062f\u06cc\u0627\u0631\u06d5 \u0628\u06d5\u0648 \u06a9\u06ce\u0634\u06d5 \u0695\u0627\u062f\u06d5\u06af\u0627 \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u0695\u0648\u0648\u06cc \u062f\u0627.',
    '\u0628\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0648\u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u067e\u06d5\u0633\u0646\u062f\u060c \u0645\u06d5\u0631\u062f\u0645 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u06ce\u06af\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u06af\u0627\u0632\u06cc \u0628\u06a9\u06d5 \u0648 \u0628\u06d5 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u0634\u06ce\u0648\u06d5 \u0644\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646\u0627 \u0628\u062f\u0648\u06ce. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0626\u06d5\u0648 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627\u0648\u06d5 - \u0644\u06d5 \u06af\u0634 \u06a9\u06d5\u0633 - \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0631\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0634\u06cc \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06ce\u06cc\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6\u06b5\u06d5 \u062f\u06d5\u0633\u062a\u06ce\u0646\u0646\u06d5\u0648\u06d5\u060c \u062f\u0627\u0631\u0628\u06d5\u062f\u0627\u0631 \u0628\u06ce \u0648 \u067e\u062a\u0631 \u0646\u06d5\u0628\u06ce\u061b \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u0695\u0627\u06af\u0631\u06cc\u0634 \u0628\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0628\u06c6 \u062e\u06c6\u0695\u0627\u06af\u0631\u06d5\u06a9\u0627\u0646 \u0632\u06c6\u0631 \u0628\u0627\u0634\u062a\u0631\u06d5.',
    '\u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06d5! \u062f\u06cc\u0627\u0631\u06d5 \u062e\u06c6\u0695\u0627\u06af\u0631\u062a\u0646\u06cc\u0634\u062a \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u062e\u06d5\u0645\u06cc\u0627\u0646 \u0645\u06d5\u062e\u06c6 \u0648 \u0644\u06d5\u0648 \u06af\u0632\u06cc \u0648 \u0641\u06ce\u06b5\u0627\u0646\u06d5\u0634 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u062f\u06b5\u062a\u06d5\u0646\u06af \u0645\u06d5\u0628\u06d5.',
    '\u0628\u06ce \u0633\u06c6 \u062e\u0648\u062f\u0627 \u0648\u06ce\u0695\u0627\u06cc \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646 \u0648 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u0627\u0646\u06d5.',
    '\u067e\u0627\u06a9\u06cc \u0648 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u0628\u06c6 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u0634\u06d5\u0648\u06ce\u06a9\u062f\u0627 \u0634\u06d5\u0648\u0695\u06c6\u06cc\u06cc \u0628\u06d5 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u06cc \u06a9\u0631\u062f\u061b \u0644\u06d5 \u00ab\u0645\u0633\u062c\u062f\u0627\u0644\u062d\u0631\u0627\u0645\u00bb\u06d5\u0648\u06d5 \u062a\u0627 \u00ab\u0645\u0633\u062c\u062f\u0627\u0644\u0627\u0642\u0635\u06cc\u0670\u00bb (\u0645\u0632\u06af\u06d5\u0648\u062a\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648\u0644\u0627\u062a\u0631)\u061b \u0626\u06d5\u0648 \u0645\u0632\u06af\u06d5\u0648\u062a\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u067e\u06cc\u062a \u0648 \u067e\u06cc\u0631\u06c6\u0632\u06cc\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5 \u0628\u06d5 \u062f\u06d5\u0648\u0631\u0648\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc\u061b \u0628\u06d5\u0648 \u0646\u06cc\u0627\u0632\u06d5\u06cc \u06a9\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646\u06cc \u067e\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u06cc\u0646. \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0628\u06cc\u0646\u0627\u06cc\u06d5.',
    '\u06a9\u062a\u06ce\u0628\u06cc\u0634\u0645\u0627\u0646 \u062f\u0627 \u0648\u06d5\u0645\u0648\u0648\u0633\u0627 \u0648 \u06a9\u0631\u062f\u0645\u0627\u0646\u06d5 \u0695\u06ce\u0646\u0648\u06ce\u0646\u06ce\u06a9 \u0628\u06c6 \u0628\u06d5\u0631\u06d5 \u0648 \u0647\u06d5\u0632\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u060c \u06a9\u06d5: \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0645\u0646 \u0628\u0627 \u0628\u0631\u06cc\u06a9\u0627\u0631\u06ce\u06a9\u062a\u0627\u0646 \u0646\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u06cc \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0646\u0648\u0648\u062d\u0627 \u0647\u06d5\u06b5\u0645\u0627\u0646 \u06af\u0631\u062a\u0646! \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0626\u06d5\u0648 \u0639\u06d5\u0628\u062f\u06ce \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631 \u0628\u0648\u0648.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u06d5\u062f\u0627 \u0626\u0627\u06af\u0627\u06cc\u06cc\u0645\u0627\u0646 \u0628\u06d5 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644 \u062f\u0627\u060c \u06a9\u06d5: \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 - \u0644\u06d5 \u062f\u0648\u0648 \u0647\u06d5\u0644\u062f\u0627 - \u0645\u0644 \u0644\u06d5 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u062f\u06d5\u0646\u06ce\u0646 \u0648 \u0632\u06c6\u0631 \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u0628\u0627\u062f\u06cc \u0647\u06d5\u0648\u0627\u06cc\u06cc \u062f\u06d5\u0646\u0648\u06ce\u0646\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0626\u06ce\u06a9\u06d5\u0645 \u0644\u06d5\u0645 \u062f\u0648\u0648 \u0647\u06d5\u0644\u0627\u0646\u06d5 \u0647\u0627\u062a\u06d5\u0628\u06d5\u0631\u060c \u0686\u06d5\u0646\u062f \u0639\u06d5\u0628\u062f\u06ce\u06a9\u06cc \u0647\u06d5\u0631\u06d5 \u0626\u0627\u0632\u0627\u06cc \u062e\u06c6\u0646 \u0646\u0627\u0631\u062f\u0646\u06d5 \u0633\u06d5\u0631\u060c \u06af\u0634 \u0648\u06b5\u0627\u062a\u06cc\u0627\u0646 \u0644\u06ce \u0626\u0627\u0698\u0646\u06cc\u0646. \u0626\u06d5\u0645 \u0628\u0695\u06cc\u0627\u0631\u06d5\u0634 \u062c\u06ce \u0628\u06d5\u062c\u06ce \u0628\u0648\u0648.',
    '\u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u06a9\u0627\u0631\u06ce \u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u062f\u060c \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0647\u06ce\u0632\u062a\u0631 \u0628\u0646\u061b \u0647\u06d5\u0698\u0645\u0627\u0631\u06cc\u0634\u062a\u0627\u0646 \u0641\u0631\u06d5\u062a\u0631 \u0628\u06ce \u0648 \u0628\u06d5 \u062f\u0627\u0631\u0627\u06cc\u06cc \u0648 \u0632\u0627\u0631\u0648\u0632\u06cc\u0686\u06cc\u0634 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc\u0645\u0627\u0646 \u067e\u06ce \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0686\u0627\u06a9\u06d5 \u0628\u06a9\u06d5\u0646\u060c \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u062e\u0631\u0627\u067e\u06d5\u062a\u0627\u0646 \u06a9\u0631\u062f\u060c \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u06d5. \u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u067e\u0627\u0634\u06cc\u0646 \u0647\u0627\u062a\u06d5 \u062c\u06ce\u060c \u062f\u06cc\u0633\u0627\u0646 \u062f\u06d5\u0646\u06ce\u0631\u06cc\u0646\u06d5 \u0633\u06d5\u0631\u062a\u0627\u0646\u060c \u0648\u0627\u062a\u0627\u0646 \u0644\u06ce\u06a9\u06d5\u0646 \u067e\u06d5\u0698\u0627\u0631\u06d5 \u0648 \u062e\u06d5\u0645 \u0644\u06d5 \u0695\u0648\u062e\u0633\u0627\u0631\u062a\u0627\u0646\u0627 \u062f\u06cc\u0627\u0631 \u0628\u06ce \u0648 \u062a\u0627 \u0628\u0634\u0686\u0646\u06d5 \u0645\u0632\u06af\u06d5\u0648\u062a\u06d5\u0648\u06d5 - \u0648\u06d5\u06a9 \u062c\u0627\u0631\u06cc \u067e\u06ce\u0634\u0648\u0648\u0634\u06cc \u0686\u0648\u0648\u0646\u06ce - \u0648 \u0647\u06d5\u0631 \u0686\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u06ce \u062a\u06d5\u0641\u0631\u0648\u062a\u0648\u0648\u0646\u0627\u06cc \u06a9\u06d5\u0646.',
    '\u0628\u06d5\u0634\u06a9\u0648\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u062a\u0627\u0646\u062e\u0627\u062a\u06d5 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc. \u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u062a\u06ce\u0647\u06d5\u06b5\u0686\u0646\u06d5\u0648\u06d5\u060c \u0626\u06ce\u0645\u06d5\u0634 \u062a\u06ce\u0647\u06d5\u06b5\u062f\u06d5\u0686\u06cc\u0646\u06d5\u0648\u06d5 \u0648\u060c \u062f\u06c6\u0698\u06d5\u0647\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0632\u06cc\u0646\u062f\u0627\u0646 \u0628\u06c6 \u06af\u0631\u06c6\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5 \u0695\u06ce\u06af\u0627\u06cc\u06d5\u06a9\u06cc \u067e\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627 \u0644\u06d5 \u06af\u0634 \u0695\u06ce\u06cc\u0627\u0646 \u0633\u06d5\u0631\u0695\u0627\u0633\u062a\u0631\u06d5. \u0628\u06d5\u0648 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0648\u0627 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u0645\u0648\u0698\u062f\u06d5 \u062f\u06d5\u062f\u0627: \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0633.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0628\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698 \u0646\u0627\u06a9\u06d5\u0646\u060c \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646\u0645\u0627\u0646 \u0628\u06c6 \u0633\u0627\u0632 \u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u0645\u0631\u06c6 \u0686\u06c6\u0646 \u062f\u06d5\u0628\u0648\u0648 \u0628\u062e\u0648\u0627\u0632\u06ce \u0686\u0627\u06a9\u06cc \u0628\u06c6 \u0628\u06ce\u060c \u0626\u0627\u0648\u0627\u0634 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u062e\u0631\u0627\u067e\u06cc \u0644\u06ce \u0628\u0642\u06d5\u0648\u0645\u06ce. \u0645\u0631\u06c6 \u0647\u06d5\u0631\u0648\u0627 \u0628\u06d5\u0644\u06d5\u0632 \u0628\u0648\u0648\u06d5.',
    '\u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698\u0645\u0627\u0646 \u0628\u06d5 \u062f\u0648\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u062f\u0627\u0646: \u0646\u06cc\u0634\u0627\u0646\u06cc \u0634\u06d5\u0648\u0645\u0627\u0646 \u062f\u0627\u06a9\u0648\u0698\u0627\u0646\u062f\u060c \u0646\u06cc\u0634\u0627\u0646\u06cc \u0695\u06c6\u0698\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0647\u06c6\u06cc \u062f\u06cc\u062a\u0646\u06ce\u061b \u062a\u0627 \u0628\u06af\u06d5\u0695\u06ce\u0646 \u0628\u06d5 \u0634\u0648\u06ce\u0646 \u0695\u06c6\u0632\u06cc \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u062f\u06d5\u06cc\u062f\u0627 \u0648 \u0644\u06d5 \u0698\u0645\u0627\u0631\u06cc \u0633\u0627\u06b5\u06d5\u06a9\u0627\u0646 \u0648 \u0698\u0645\u0627\u0631\u062f\u0646\u06ce \u0633\u06d5\u0631\u0648\u0648 \u062f\u06d5\u0631\u0686\u06ce\u061b \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u0645\u0627\u0646 \u0628\u06d5 \u0648\u0631\u062f\u06cc \u0634\u06cc \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5.',
    '\u0646\u0627\u0645\u06d5\u06cc \u0626\u0627\u06a9\u0627\u0631\u06cc \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u0645\u0627\u0646 - \u0648\u06d5\u06a9 \u062a\u06c6\u0642 - \u0644\u06d5 \u0626\u06d5\u0633\u062a\u06c6\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u06d5 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06cc \u0633\u06d5\u0631\u0626\u0627\u0648\u0627\u06b5\u06d5\u060c \u0634\u0627\u0646\u06cc \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0646\u0627\u0645\u06d5\u06cc \u062e\u06c6\u062a \u0628\u062e\u0648\u06ce\u0646\u06d5\u0631\u06d5\u0648\u06d5! \u0626\u06d5\u0645\u0631\u06c6 \u0626\u06cc\u062a\u0631 \u0647\u06d5\u0631 \u062e\u06c6\u062a \u062d\u06d5\u0633\u06ce\u0648\u06af\u06d5\u0631\u06cc \u062e\u06c6\u062a\u06cc.',
    '\u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce \u0628\u06ce\u060c \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc \u0628\u06c6 \u062e\u06c6\u06cc\u06d5\u062a\u06cc\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0695\u06ce \u062f\u06d5\u0631\u0686\u06ce\u060c \u0647\u06d5\u06b5\u06d5 \u0628\u0648 \u0646 \u0644\u06d5 \u062f\u0698\u06cc \u062e\u06c6\u06cc\u06d5. \u06a9\u06d5\u0633 \u06af\u0631\u0627\u0646\u0627\u06cc\u06cc \u06a9\u06d5\u0633\u06ce \u062a\u0631 \u0646\u0627\u06af\u0631\u06ce\u062a\u06d5 \u0645\u0644. \u062a\u0627 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634 \u0646\u06d5\u0646\u06ce\u0631\u06cc\u0646\u060c \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u0644\u06d5\u0645\u06d5\u0695\u0627 \u0646\u06cc\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06a9\u06d5 \u0648\u06cc\u0633\u062a\u0645\u0627\u0646 \u0634\u0627\u0631\u06ce\u06a9 \u0642\u0695 \u06a9\u06d5\u06cc\u0646\u060c \u062f\u06d5\u0648\u06b5\u06d5\u062a\u0645\u06d5\u0646\u06d5 \u062e\u06c6\u0634\u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0648\u0627\u0646\u060c \u062f\u0646\u06d5\u062f\u06d5\u062f\u06d5\u06cc\u0646 \u0628\u06c6 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0648 \u062e\u0631\u0627\u067e\u06a9\u0627\u0631\u06cc \u0648 \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u0627 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u0627\u062c\u06c6 \u062f\u06d5\u0628\u06ce\u061b \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u062a\u06ce\u06a9\u06cc \u062f\u06d5\u0647\u0627\u0695\u06cc\u0646.',
    '\u0632\u06c6\u0631 \u0628\u06d5\u0631\u06d5\u0634\u0645\u0627\u0646 \u0644\u06d5 \u067e\u0627\u0634 \u0646\u0648\u0648\u062d \u0644\u06d5\u0646\u0627\u0648\u0628\u0631\u062f\u0648\u06d5. \u0628\u06c6 \u062a\u06c6 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0628\u06d5\u0633\u06d5 \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5 \u06af\u0648\u0646\u0627\u062d\u0627\u0646\u06cc \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u0628\u06cc\u0646\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062e\u06c6\u0634\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u062f\u06d5\u0648\u06ce\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0628\u0645\u0627\u0646\u06d5\u0648\u06ce\u060c \u0626\u0627\u0648\u0627\u062a\u06d5\u06a9\u06d5\u06cc \u0632\u0648\u0648 \u067e\u06ce\u06a9 \u062f\u06ce\u0646\u06cc\u0646\u060c \u067e\u0627\u0634\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0628\u0695\u0648\u0648 \u0646\u06d5\u0645\u0627\u0648\u06cc \u062f\u06d5\u0631\u06a9\u0631\u0627\u0648\u06cc \u062f\u06c6\u0698\u06d5\u0647\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u06d5\u060c \u062f\u06d5\u06cc\u0628\u0631\u0698\u06ce\u0646\u06ce.',
    '\u06a9\u06ce\u0634 \u062e\u06c6\u0634\u06cc \u0626\u06d5\u0648 \u062f\u0646\u06cc\u0627\u06cc \u062f\u06d5\u0648\u06ce \u0648 - \u0628\u06d5 \u067e\u06ce\u06cc \u062a\u0648\u0627\u0646\u0627 - \u0628\u06c6\u06cc \u062f\u06d5\u06a9\u06c6\u0634\u06ce \u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u060c \u06a9\u06c6\u0634\u0634\u06cc\u0627\u0646 \u0628\u06d5\u0641\u06cc\u0695\u06c6 \u0646\u0627\u0686\u06ce \u0648 \u0644\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u06d5.',
    '\u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u06cc\u0627\u0646 - \u0686 \u0626\u06d5\u0648\u0627\u0646 \u0648 \u0686 \u0626\u06d5\u0645\u0627\u0646\u06d5 - \u0644\u06d5 \u062e\u06d5\u06b5\u0627\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06ce \u0628\u06d5\u0634 \u0646\u0627\u06a9\u06d5\u06cc\u0646. \u062e\u06d5\u06b5\u0627\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0647\u06cc\u0686 \u0644\u06d5\u0645\u067e\u06d5\u0631\u06cc \u0644\u06d5\u0628\u06d5\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u062f\u06d5\u0628\u0695\u0648\u0627\u0646\u06d5\u060c \u0686\u06c6\u0646 \u0628\u0627\u0632\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u0686\u0627\u0648 \u0628\u0627\u0632\u06ce\u06a9 \u0633\u06d5\u0631\u062e\u0633\u062a\u0648\u0648\u06d5\u061b \u062f\u06cc\u0627\u0631\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0634\u062f\u0627 \u067e\u0644\u06d5\u0648\u067e\u0627\u06cc\u06d5 \u0648 \u0633\u06d5\u0631\u062e\u0633\u062a\u0646 \u0644\u06d5\u0645\u06d5 \u0632\u06c6\u0631\u062a\u0631\u06d5.',
    '\u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0634\u062a\u06ce \u062a\u0631 \u0645\u06d5\u067e\u06d5\u0631\u0633\u062a\u06d5\u060c \u062f\u06d5\u0646\u0627 \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u0648\u06d5\u0628\u06d5\u0631 \u062a\u06d5\u0634\u06d5\u0631 \u062f\u0631\u0627\u0648\u06cc \u0648 \u062f\u06d5\u0631\u06a9\u0631\u0627\u0648\u06cc \u0695\u06c6\u062f\u06d5\u0646\u06cc\u0634\u06cc.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062f\u0627 \u06a9\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0648\u06cc \u0645\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u06cc\u0634 \u0686\u0627\u06a9\u06d5 \u0628\u06a9\u06d5\u0646. \u0647\u06d5\u0631\u06af\u0627 \u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u06cc\u0627\u0646 \u0647\u06d5\u0631\u062f\u0648\u0648\u06a9\u06cc\u0627\u0646 - \u062a\u0627 \u0644\u06d5 \u0644\u0627\u06cc \u062a\u06c6\u0646 - \u062a\u0648\u0648\u0634\u06cc \u062a\u06d5\u0645\u06d5\u0646\u06cc \u067e\u06cc\u0631\u06cc \u0628\u0648\u0648\u0646\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0626\u06c6\u0641\u06cc\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0645\u06d5\u06b5\u06ce \u0648 \u062a\u06ce\u0627\u0646 \u0645\u06d5\u062e\u0648\u0695\u06d5 \u0648 \u0628\u06d5 \u0632\u0645\u0627\u0646\u06cc \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0628\u062f\u0648\u06ce\u0646\u06d5.',
    '\u0628\u06d5 \u0628\u06ce \u0641\u06cc\u0632\u06cc \u0628\u06cc\u0627\u0646\u062e\u06d5 \u0698\u06ce\u0631 \u0628\u0627\u06b5\u06cc \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0648 \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06d5\u0645\u0627\u0646 \u0645\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u0634\u0644\u06a9\u06cc \u067e\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f\u060c \u062a\u06c6\u0634 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u062a\u06cc\u0627\u0646\u062e\u06d5.',
    '\u062e\u0648\u062f\u0627\u0646\u06ce \u0648\u06d5\u060c \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0686\u06ce\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u062f\u06b5\u062f\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce\u061b \u0626\u06d5\u06af\u06d5\u0631 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u062a\u0627\u0646 \u0686\u0627\u06a9 \u0628\u06ce\u060c \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u067e\u0631\u06cc\u0646\u06af\u06ce\u0646\u06d5\u0648\u06d5\u060c \u0628\u06d5 \u0628\u0648\u0648\u0631\u062f\u0646\u06d5.',
    '\u062e\u0632\u0645\u062a \u0628\u06d5\u0634\u06cc \u062e\u06c6\u06cc \u0628\u062f\u06d5\u0631\u06ce\u061b \u0647\u06d5\u0698\u0627\u0631 \u0648 \u0644\u06d5 \u0695\u06ce\u062f\u0627 \u0645\u0627\u0648\u06cc\u0634 \u0628\u06ce \u0628\u06d5\u0634 \u0645\u06d5\u06a9\u06d5\u061b \u062a\u06d5\u0648\u0627\u0648\u06cc\u0634 \u062f\u06d5\u0633 \u0628\u06b5\u0627\u0648 \u0645\u06d5\u0628\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0647\u06d5\u06b5\u06d5\u062e\u06d5\u0631\u062c\u0646\u060c \u062f\u06d5\u06af\u06d5\u06b5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u062f\u06d5\u0628\u0646\u06d5 \u0628\u0631\u0627\u061b \u0634\u06d5\u06cc\u062a\u0627\u0646\u06cc\u0634 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u060c \u0628\u06ce \u0626\u06d5\u0645\u06d5\u06af \u0648 \u0633\u067e\u06b5\u06d5 \u062f\u06d5\u0631\u0686\u0648\u0648.',
    '\u0626\u06d5\u0631 \u0646\u06d5\u0634\u062a\u0628\u0648\u0648 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06d5\u06cc \u0648 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0648\u0648\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc\u06d5\u0648\u06d5 \u062f\u06d5\u0631\u0648\u0648\u06ce\u06a9\u062a \u0644\u06ce \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u0628\u06d5 \u0632\u0645\u0627\u0646\u06ce\u06a9\u06cc \u0646\u06d5\u0631\u0645 \u0648 \u0646\u06cc\u0627\u0646 \u0628\u06cc\u0627\u0646\u062f\u0648\u06ce\u0646\u06d5.',
    '\u062f\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646\u062a \u062f\u06d5\u06af\u06d5\u0631\u062f\u0646\u062a \u0648\u06d5\u0631\u0645\u06d5\u0647\u06ce\u0646\u06d5 \u0648 \u062a\u06d5\u0648\u0627\u0648\u06cc\u0634 \u067e\u0627\u0646\u06cc\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0628\u06d5 \u0633\u06d5\u0631\u06a9\u06c6\u0646\u06d5\u06a9\u0631\u0627\u0648\u06cc \u0648 \u0639\u06d5\u0632\u0631\u06d5\u062a\u06d5\u0648\u06d5 \u062f\u06d5\u0645\u06ce\u0646\u06cc\u06d5\u0648\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06d5 \u067e\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u0646\u062f\u06d5 \u0695\u06c6\u0632\u06cc \u0628\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u062f\u0627 \u0628\u06d5\u0633\u062a\u06d5 \u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc\u061b \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u0628\u06cc\u0646\u06ce.',
    '\u0632\u0627\u0631\u06c6\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0644\u06d5 \u062a\u0631\u0633\u06cc \u0644\u06d5 \u0628\u0631\u0633\u0627\u0646 \u0645\u0631\u062f\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0645\u06d5\u0628\u06d5\u0646. \u0626\u06ce\u0645\u06d5\u06cc\u0646 \u0695\u06c6\u0632\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0648 \u0626\u06ce\u0648\u06d5\u0634 \u062f\u06d5\u062f\u06d5\u06cc\u0646. \u06a9\u0648\u0634\u062a\u0646\u06cc \u0648\u0627\u0646\u060c \u0647\u06d5\u06b5\u06d5 \u06a9\u0627\u0631\u06cc\u06cc\u06d5\u06a9\u06cc \u0645\u06d5\u0632\u0646\u06d5.',
    '\u062a\u062e\u0648\u0646\u06cc \u0632\u06cc\u0646\u0627\u062d \u0645\u06d5\u06a9\u06d5\u0648\u0646\u061b \u062f\u0627\u0648\u06ce\u0646\u067e\u06cc\u0633\u06cc \u0626\u0627\u06a9\u0627\u0631\u06cc \u0632\u06c6\u0631 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5 \u0648 \u0695\u06ce\u0628\u0627\u0632\u06ce\u06a9\u06cc \u0646\u0627\u0647\u06d5\u0645\u0648\u0627\u0631\u06d5.',
    '\u0628\u0646\u06cc\u0627\u062f\u06d5\u0645\u06ce \u06a9\u06d5 \u06a9\u0648\u0634\u062a\u0646\u06cc \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u0632\u0627\u0646\u062f\u0631\u0627\u0648\u06d5\u060c \u0645\u06d5\u06a9\u0648\u0698\u0646 \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0642. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0628\u06ce\u062a\u06d5 \u06a9\u0648\u0634\u062a\u0646\u060c \u062f\u06d5\u0633\u06d5\u06b5\u0627\u062a\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5\u062a\u06d5 \u062f\u06d5\u0633 \u062e\u0648\u06ce\u0646\u06af\u0631\u06d5\u06a9\u06d5\u06cc\u061b \u0646\u0627\u0634\u06ce \u0644\u06d5 \u062a\u06c6\u06b5\u06d5\u0633\u0627\u0646\u062f\u0646\u0627 \u0647\u06d5\u06b5\u06d5\u0695\u06c6 \u0628\u06ce \u0648 \u0644\u06d5\u0648 \u0628\u0627\u0631\u06d5\u0648\u06d5 \u0626\u0627\u0631\u06cc\u06a9\u0627\u0631\u06cc \u0628\u06c6 \u06a9\u0631\u0627\u0648\u06d5.',
    '\u0646\u06d5\u0686\u0646 \u062a\u062e\u0648\u0648\u0646 \u0645\u0627\u06b5\u06cc \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646 \u0628\u06a9\u06d5\u0648\u0646\u060c \u0645\u06d5\u06af\u06cc\u0646 \u062c\u06c6\u0631\u06ce \u06a9\u06d5 \u0628\u0627\u0634\u062a\u0631 \u0628\u06ce\u061b \u0647\u06d5\u062a\u0627 \u067e\u06ce\u062f\u06d5\u06af\u0627 \u0648\u062a\u06ce\u062f\u06d5\u06af\u0627. \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0634\u062a\u0627\u0646 \u0628\u06d5\u0631\u0646\u06d5 \u0633\u06d5\u0631\u06ce\u061b \u067e\u06d5\u06cc\u0645\u0627\u0646\u060c \u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u067e\u06ce\u0648\u0627\u0646\u06d5 \u062f\u06d5\u067e\u06ce\u0648\u0646\u060c \u0628\u0627 \u062a\u06d5\u06a9\u0648\u0648\u0632 \u0628\u06ce. \u06a9\u06ce\u0634\u0627\u0646\u06d5\u0634\u062a\u0627\u0646 \u0628\u06d5 \u062a\u06d5\u0631\u0627\u0632\u0648\u06cc \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06ce\u061b \u0648\u0627 \u0647\u06d5\u0645 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0634\u062a\u0631\u06d5\u060c \u0647\u06d5\u0645 \u0626\u0627\u06a9\u0627\u0645\u06cc \u067e\u06d5\u0633\u0646\u062f\u062a\u0631\u06d5.',
    '\u0645\u06d5\u06a9\u06d5\u0648\u06d5 \u0634\u0648\u06ce\u0646 \u0634\u062a\u06ce\u06a9\u06cc \u0648\u0627 \u06a9\u06d5 \u0646\u0627\u0632\u0627\u0646\u06cc \u0686\u06cc\u06cc\u06d5 \u0648 \u0686\u06c6\u0646\u06d5\u060c \u0686\u0648\u0646\u06a9\u06d5 \u06af\u0648\u06ce\u0686\u06a9\u06d5 \u0648 \u0686\u0627\u0648 \u0648 \u062f\u06b5\u06cc\u0634\u060c \u06af\u0634\u062a\u06cc\u0627\u0646 \u067e\u0631\u0633\u06cc\u0627\u0631\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06d5.',
    '\u0628\u06d5 \u0641\u06cc\u0632\u06d5\u0648\u06d5 \u0647\u06d5\u0646\u06af\u0627\u0648 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0631\u062f\u0627 \u0645\u06d5\u0646\u06ce\u061b \u062a\u06c6 \u0646\u0627\u062a\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0628\u0642\u06d5\u06b5\u06ce\u0634\u06cc \u0648 \u0644\u06d5 \u062f\u0631\u06ce\u0698\u06cc\u0634\u062f\u0627 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u06af\u06d5\u06cc\u06d5 \u0628\u06d5\u0646\u062f\u06d5\u0646\u0627\u0646.',
    '\u06af\u0634 \u0626\u06d5\u0645\u0627\u0646\u06d5 - \u062e\u0631\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 - \u0628\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0648\u06d5 \u0646\u0627\u067e\u06d5\u0633\u0646\u062f\u0646.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0628\u06d5\u0634\u06ce\u06a9\u0646 \u0644\u06d5\u0648 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u06cc\u0627\u0646\u06d5\u060c \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06d5 \u062a\u06c6\u06cc \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u06d5. \u0648\u06ce\u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u06cc \u062a\u0631 \u0645\u06d5\u067e\u06d5\u0631\u0633\u062a\u06d5\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u0644\u06c6\u0645\u06d5\u06a9\u0631\u0627\u0648\u06cc \u0648 \u0695\u0627\u0648\u0646\u0631\u0627\u0648\u06cc \u062a\u0648\u0648\u0695 \u0626\u06d5\u062f\u0631\u06ce\u06cc\u062a\u06d5 \u062f\u0648\u0695 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645.',
    '\u0626\u0627\u062e\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u060c \u0628\u06d5 \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u06a9\u0648\u0695\u06cc \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u062e\u0634\u06cc\u0648\u0646 \u0648 \u0641\u0631\u0634\u062a\u06d5 \u0645\u06ce\u0648\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634\u06cc \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u0627\u0646\u0627\u061f \u0642\u0633\u06d5\u06cc\u06d5\u06a9\u06cc \u0632\u06c6\u0631 \u0632\u0644 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0632\u06c6\u0631 \u0634\u062a\u0645\u0627\u0646 \u0634\u06cc \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5 \u0647\u06d5\u062a\u0627 \u0626\u06d5\u0645\u0627\u0646 \u067e\u06d5\u0646\u062f \u0648\u06d5\u0631\u06af\u0631\u0646\u060c \u06a9\u06d5\u0686\u06cc \u0647\u06d5\u0631 \u0695\u06d5\u0648\u06cc\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u067e\u062a\u0631 \u062f\u06d5\u06a9\u0627.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 - \u0628\u06d5\u0648 \u062c\u06c6\u0631\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0628\u06ce\u0698\u0646 - \u0648\u06ce\u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u062e\u0648\u0627\u06cc\u0627\u0646\u06ce\u06a9\u06cc \u062a\u0631 \u0628\u0628\u0648\u0648\u0646\u0627\u060c \u0626\u06d5\u0648\u0633\u0627 \u067e\u06d5\u06cc\u062c\u06c6\u0631 \u062f\u06d5\u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0695\u06ce\u06cc \u0686\u0648\u0648\u0646\u06d5\u0644\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646\u06cc \u062a\u06d5\u062e\u062a\u06cc\u0627\u0646 \u0648\u06d5\u062f\u06c6\u0632\u06cc\u0628\u0627.',
    '\u062e\u0648\u0627 \u0644\u06d5 \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc\u06cc\u0627\u0646 \u062f\u0648\u0648\u0631\u06d5 \u0648 \u0632\u06c6\u0631 \u0628\u06d5\u0631\u0632\u062a\u0631\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0627\u0633\u06cc \u0644\u06ce\u062f\u06d5\u06a9\u06d5\u0646.',
    '\u062d\u06d5\u0648\u062a \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c \u0647\u06d5\u0631 \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646. \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9 - \u0628\u06d5 \u067e\u06d5\u0633\u0646 \u062f\u0627\u0646\u06d5\u0648\u06d5 - \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u06a9\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0648\u06d5 \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0646\u0627\u0628\u06cc\u0633\u0646. \u0626\u06d5\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u062e\u06c6\u0648 \u0628\u06d5 \u0628\u0648\u0648\u0631\u062f\u0646\u06d5.',
    '\u062f\u06d5\u0645\u06ce \u06a9\u06d5 \u0642\u0648\u0631\u0639\u0627\u0646 \u062f\u06d5\u062e\u0648\u06ce\u0646\u06cc\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0646\u06ce\u0648 \u062a\u06c6\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u060c \u067e\u06d5\u0631\u062f\u06d5\u06cc\u06d5\u06a9\u06cc \u062f\u0627\u067e\u06c6\u0634\u06ce\u0646\u06d5\u0631 \u0695\u0627\u062f\u06d5\u0646\u06af\u06ce\u0648\u06cc\u0646.',
    '\u0633\u06d5\u0631\u067e\u06c6\u0634 \u06af\u06d5\u0644\u06ce\u06a9\u06cc \u0648\u0627\u0645\u0627\u0646 \u0646\u0627\u06cc\u06d5 \u0628\u0627\u0646 \u062f\u06b5\u06cc\u0627\u0646 \u06a9\u06d5 \u062a\u06ce\u06cc \u0646\u06d5\u06af\u06d5\u0646\u061b \u06af\u0648\u0647\u06d5\u06a9\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u06a9\u067e \u062f\u06d5\u0628\u0646. \u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u06a9\u06d5\u062f\u0627 \u0646\u0627\u0648\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u0627\u06a9\u062a \u0628\u06d5 \u062f\u06d5\u0645\u062f\u0627 \u062f\u06ce\u060c \u0628\u06d5 \u0642\u06cc\u0646\u06d5\u0648\u06d5 \u067e\u0634\u062a \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646 \u06af\u0648\u06ce \u0644\u06d5 \u0686\u06cc \u062f\u06d5\u06af\u0631\u0646\u060c \u0644\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u062f\u0627 \u06a9\u06d5 \u06af\u0648\u06ce \u0628\u06c6 \u062a\u06c6 \u0695\u0627\u062f\u06d5\u062f\u06ce\u0631\u0646\u060c \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634 \u06a9\u06d5 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u0633\u0631\u062a\u06d5 \u06a9\u0631\u062f\u0646\u0646. \u06a9\u0627\u062a\u06ce \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u062f\u06d5\u0628\u06ce\u0698\u0646: \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0645\u06ce\u0631\u06ce\u06a9\u06cc \u062c\u0627\u062f\u0648\u0648 \u0644\u06ce\u06a9\u0631\u0627\u0648.',
    '\u062f\u06d5\u0628\u0695\u0648\u0627\u0646\u06d5 \u0686\u06c6\u0646 \u0645\u06d5\u0632\u0645\u0648\u0648\u062f\u06d5\u062a \u0644\u06ce \u062f\u06d5\u062e\u0648\u06ce\u0646\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u0644\u06d5 \u0695\u06ce \u06a9\u0644\u0627\u0646\u060c \u06a9\u06d5 \u062a\u0627\u0632\u06d5 \u0695\u06ce\u06af\u06d5 \u062f\u06d5\u0631\u0646\u0627\u06a9\u06d5\u0646.',
    '\u062f\u06d5\u0634\u06b5\u06ce\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0633\u06a9 \u0648 \u067e\u0631\u0648\u0648\u0633\u06a9\u06cc\u0634 \u0628\u06cc\u0646\u060c \u0698\u0646\u06c6 \u062f\u0631\u0648\u0633\u062a \u062f\u06d5\u0628\u06cc\u0646\u06d5\u0648\u06d5\u061f',
    '\u0628\u06ce\u0698\u06d5: \u06a9\u0648\u0686\u06a9 \u06cc\u0627\u0646 \u0626\u0627\u0633\u0646 \u0628\u0646\u060c',
    '\u06cc\u0627\u0646 \u0686\u06ce \u0628\u0648\u0648\u06ce\u06a9 \u06a9\u06d5 \u0644\u06d5 \u062e\u06d5\u06cc\u0627\u06b5\u06cc \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0632\u0644 \u062f\u06d5\u0646\u0648\u06ce\u0646\u06ce. \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0698\u0646: \u06a9\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648\u0645\u0627\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648\u06cc \u06cc\u06d5\u06a9\u06d5\u0645 \u062c\u0627\u0631 \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f\u0646. \u0626\u06d5\u0648\u0633\u0627 \u0633\u06d5\u0631 \u062f\u06d5\u0644\u06d5\u0642\u06ce\u0646\u0646 \u0648 \u0626\u06ce\u0698\u0646: \u06a9\u06d5\u0646\u06af\u06ce\u061f \u0628\u06ce\u0698\u06d5: \u0634\u0627\u06cc\u06d5\u062a \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0628\u0627\u0646\u06af\u0648\u0648 \u0644\u06ce\u062f\u06d5\u06a9\u0627 \u0648 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u067e\u06d5\u0633\u0646 \u062f\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0633\u0648\u067e\u0627\u0633\u06d5\u0648\u06d5 \u067e\u06d5\u0631\u0633\u06a4\u06cc \u0644\u06ce \u0648\u06d5\u06af\u06d5\u0695\u06ce\u0646\u0646 \u0648 \u0648\u0627 \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646 - \u06a9\u06d5\u0645\u06ce\u06a9 \u0646\u06d5\u0628\u06ce - \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627 \u0646\u06d5\u0645\u0627\u0648\u0646\u06d5\u0648\u06d5.',
    '\u062a\u06c6 \u0628\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0645\u0646 \u0628\u06ce\u0698\u06d5 \u0686\u06cc \u0628\u0627\u0634\u062a\u0631\u06d5 \u0626\u06d5\u0648\u06d5 \u0628\u06ce\u0698\u0646\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0633\u06c6\u062f\u0631\u06d5\u06cc\u0648 \u0644\u06d5 \u0646\u06ce\u0648\u062f\u0627 \u062f\u06d5\u06a9\u0627. \u0626\u06d5\u0648 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06d5 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u0648\u0698\u0645\u0646\u06cc \u062c\u0633\u0646\u06cc \u0639\u06cc\u0646\u0633\u0627\u0646\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0631\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632 \u06a9\u0627 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc\u062a\u0627\u0646 \u062f\u06d5\u062e\u0627\u061b \u062d\u06d5\u0632\u06cc\u0634 \u0628\u06a9\u0627 \u062a\u0648\u0648\u0634\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627\u061b \u062a\u06c6\u0634\u0645\u0627\u0646 \u0646\u06d5\u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u0644\u0627\u06cc\u0627\u0646 \u0628\u0631\u06cc\u06a9\u0627\u0631 \u0628\u06cc.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u0632\u0627\u0646\u0627\u062a\u0631\u06d5. \u0644\u06d5 \u0646\u0627\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc \u062f\u06cc \u0628\u06d5\u0695\u06ce\u0632\u062a\u0631 \u06af\u0631\u062a\u060c \u0632\u06d5\u0628\u0648\u0648\u0631\u06cc\u0634\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u062f\u0627\u0648\u0648\u062f.',
    '\u0628\u06ce\u0698\u06d5: \u0647\u0627\u0646\u0627 \u0628\u06d5\u0631\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0648\u0627 - \u0628\u06d5 \u062c\u06ce \u062e\u0648\u062f\u0627 - \u0628\u06d5 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u062a\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646\u061b \u06a9\u06d5 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0628\u06d5\u06b5\u0627\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0644\u0627\u0628\u06d5\u0631\u0646\u060c \u067e\u06ce\u0634\u06cc\u0627\u0646 \u0646\u0627\u06a9\u0631\u06ce \u0628\u06c6 \u06cc\u06d5\u06a9\u06cc \u062a\u0631\u06cc \u0628\u0646\u06ce\u0631\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0628\u06d5\u0646\u060c \u06a9\u0627\u0645\u0627\u0646\u06cc\u0627\u0646\u06d5 \u0646\u0632\u06cc\u06a9\u062a\u0631\u0646\u060c \u0628\u06d5 \u062f\u0648\u0648\u06cc \u0647\u06c6\u062f\u0627 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646 \u06a9\u06d5 \u0628\u06af\u06d5\u0646 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0648 \u0647\u06cc\u0648\u0627\u06cc \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0626\u06d5\u0648\u06cc\u0627\u0646\u06d5 \u0648 \u0644\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u0634\u06cc \u062f\u06d5\u062a\u0631\u0633\u0646. \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0647\u06ce\u0698\u0627\u06cc\u06d5 \u0644\u06ce\u06cc \u0628\u06d5\u067e\u0627\u0631\u06ce\u0632 \u0628\u0646.',
    '\u067e\u06ce\u0634 \u0647\u0627\u062a\u0646\u06cc \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u060c \u0647\u06cc\u0686 \u0626\u0627\u0648\u06d5\u062f\u0627\u0646\u06cc \u0646\u0627\u0645\u06ce\u0646\u06ce \u062a\u06ce\u06a9\u06cc \u0646\u06d5\u062f\u06d5\u06cc\u0646 \u06cc\u0627\u0646 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u062a\u0648\u0648\u0634 \u0626\u0627\u0632\u0627\u0631 \u0646\u06d5\u06a9\u06d5\u06cc\u0646\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u0634 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u06d5\u062f\u0627 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0646\u0627\u0631\u062f\u0646\u060c \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u0628\u0648\u0648 \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0642\u06d5\u06b5\u06d5\u0645 \u062f\u06d5\u062f\u0627\u0646. \u0626\u0627\u0631\u0648\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u0627 \u0628\u06d5 \u0633\u06d5\u0645\u0648\u0648\u062f\u061b \u06a9\u06d5\u0686\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f. \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631\u0646\u0627\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u062d\u06d5\u0644\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u0648\u062a: \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062a\u06c6 \u062f\u06d5\u0648\u0631\u06d5\u06cc \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u062f\u0627\u0648\u06d5\u061b \u062e\u06d5\u0648\u0646\u06ce\u06a9\u06cc\u0634 \u06a9\u06d5 \u067e\u06ce\u0645\u0627\u0646 \u062f\u06cc\u062a\u06cc \u0648 \u0626\u06d5\u0648 \u062f\u0627\u0631\u06d5 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0642\u0648\u0631\u0639\u0627\u0646\u062f\u0627 \u0628\u0627\u0633\u06cc \u06a9\u0631\u0627\u0648\u06d5\u061b \u06af\u0634 \u0628\u06c6 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0628\u0648\u0648. \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u06ce\u0646\u06cc\u0646\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0647\u06d5\u0631 \u067e\u062a\u0631 \u0645\u0644\u0647\u0648\u0695\u06cc \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u06a9\u0627\u062a\u06ce \u0648\u062a\u0645\u0627\u0646\u06d5 \u0641\u0631\u06cc\u0634\u062a\u0627\u0646: \u0633\u0648\u0698\u062f\u06d5 \u0644\u06d5\u0628\u06d5\u0631 \u0626\u0627\u062f\u06d5\u0645 \u0628\u06d5\u0631\u0646\u060c \u0633\u0648\u0698\u062f\u06d5\u06cc\u0627\u0646 \u0628\u0631\u062f \u0626\u06cc\u0628\u0644\u06cc\u0633 \u0646\u06d5\u0628\u06ce. \u06af\u0648\u062a\u06cc: \u0686\u06c6\u0646 \u0645\u0646 \u0633\u0648\u0698\u062f\u06d5 \u062f\u06d5\u0628\u06d5\u0645\u06d5 \u0628\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0644\u06d5 \u062d\u06d5\u0695\u06cc\u062a \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5\u061f',
    '\u06af\u0648\u062a\u06cc: \u0628\u06d5 \u0645\u0646 \u0628\u06ce\u0698\u06d5 \u0626\u06d5\u0645 \u062a\u06d5\u0631\u062d \u0648 \u062f\u06cc\u062f\u0627\u0631\u06d5\u062a\u060c \u0644\u06d5 \u0645\u0646 \u0628\u06d5\u0632\u0644\u062a\u0631 \u0632\u0627\u0646\u06cc\u0648\u06d5\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u062a\u0627 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u06d5\u0631\u0645 \u0628\u06c6 \u0628\u06d5\u0631\u0647\u06d5\u06b5\u0627 \u0628\u06a9\u06d5\u06cc\u060c \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06a9\u06d5\u06cc - \u06a9\u06d5\u0645\u06ce\u06a9 \u0646\u06d5\u0628\u06ce - \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631\u06cc\u060c \u062f\u06d5\u0628\u06ce \u0628\u062e\u06d5\u0645\u06d5 \u0698\u06ce\u0631 \u0686\u0646\u06af\u0645.',
    '\u06af\u0648\u062a\u06cc: \u0628\u0695\u06c6! \u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0644\u06d5\u0648\u0627\u0646 \u0648\u06d5\u0634\u0648\u06ce\u0646 \u062a\u0648 \u0628\u06a9\u06d5\u0648\u06ce\u060c \u0633\u0632\u0627\u06cc \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u062f\u06c6\u0698\u06d5\u0647\u06d5 \u0648 \u0633\u0632\u0627\u06cc\u06d5\u06a9\u06cc \u067e\u0695\u0628\u06d5\u067e\u0695\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u0645\u06ce \u062a\u06c6 \u067e\u06ce\u06cc \u062f\u06d5\u0648\u06ce\u0631\u06cc\u060c \u0628\u0627\u0646\u06af\u06cc \u0628\u06a9\u06d5 \u0648 \u062f\u0646\u06d5\u06cc \u0628\u062f\u06d5 \u0648 \u0628\u06d5 \u0633\u0648\u0627\u0631 \u0648 \u067e\u06cc\u0627\u062f\u06d5\u06cc \u062e\u06c6\u062a\u06d5\u0648\u06d5 \u0628\u06af\u06d5 \u0633\u06d5\u0631\u06cc \u0648 \u0628\u0628\u06d5 \u0647\u0627\u0648\u0628\u06d5\u0634 \u0644\u06d5 \u0645\u0627\u06b5 \u0648 \u0632\u0627\u0631\u06c6\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u060c \u0633\u06c6\u0632 \u0628\u062f\u06d5 \u0648\u0627\u0646\u061b \u0633\u06c6\u0632\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646 \u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u062e\u06d5\u06b5\u06d5\u062a\u0627\u0646\u062f\u0646\u06d5.',
    '\u062a\u06c6 \u0628\u06d5\u0633\u06d5\u0631 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0645\u0646\u062f\u0627 \u0647\u06cc\u0686 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06ce\u06a9\u062a \u0646\u06cc\u06cc\u06d5\u061b \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u0631\u06cc\u06a9\u0627\u0631\u06cc\u0627\u0646\u06d5\u060c \u0628\u06d5\u0633\u06cc\u0627\u0646\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u0644\u06d5 \u062d\u0627\u0646\u062f \u0626\u06ce\u0648\u06d5 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5. \u06af\u06d5\u0645\u06cc\u06d5\u062a\u0627\u0646 \u0644\u06d5 \u0632\u06d5\u0631\u06cc\u0627\u06cc\u06d5\u062f\u0627 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u06af\u06d5\u0695 \u062a\u0627 \u0644\u06d5 \u0633\u06c6\u0646\u06af\u06d5\u06cc\u060c \u0628\u0698\u06cc\u0648\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u06ce.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u062f\u06d5\u0631\u06cc\u0627\u06cc\u06d5\u062f\u0627 \u062a\u0648\u0648\u0634\u06cc \u0641\u06d5\u0631\u062a\u06d5\u0646\u06d5\u06cc\u06d5\u06a9 \u0647\u0627\u062a\u0646\u060c \u0647\u0627\u0646\u0627 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9 \u0628\u06d5\u0631\u0646\u060c \u0628\u06d5\u0631 \u067e\u06ce\u0648\u0627\u0631\u06d5\u060c \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648. \u0647\u06d5\u0631\u06a9\u06d5 \u0695\u0632\u06af\u0627\u0631\u06cc \u06a9\u0631\u062f\u0646 \u0648 \u0686\u0648\u0648\u0646\u06d5 \u0633\u06d5\u0631 \u0628\u06d5\u0698\u060c \u0626\u0627\u0648\u0695\u06cc\u0634\u06cc \u0644\u06ce \u0646\u0627\u062f\u06d5\u0646\u06d5\u0648\u06d5\u061b \u0645\u0631\u06c6 \u0632\u06c6\u0631 \u0626\u06d5\u0645\u06d5\u06af \u0646\u06d5\u0646\u0627\u0633\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0644\u06d5\u0648\u06d5 \u0626\u0627\u0631\u062e\u06d5\u06cc\u0627\u0646\u0646 \u06a9\u06d5 \u0644\u06d5 \u0628\u06d5\u0698\u06d5\u06a9\u06d5 \u0695\u06c6\u0646\u0627\u0686\u0646 \u06cc\u0627\u0646 \u0628\u0627\u0647\u06c6\u0632\u062a\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06b5 \u0646\u0627\u06a9\u0627\u060c \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5\u0633\u062a\u0627\u0646 \u06af\u06cc\u0631 \u0646\u06d5\u06a9\u06d5\u0648\u06ce \u0641\u0631\u06cc\u0627\u0648 \u06a9\u06d5\u0648\u06ce\u061f',
    '\u06cc\u0627\u062e\u06c6 \u0644\u06d5\u0648\u06d5 \u0626\u0627\u0631\u062e\u06d5\u06cc\u0627\u0646 \u0628\u0648\u0648\u0646 \u062c\u0627\u0631\u06ce\u06a9\u06cc \u062a\u0631 \u0695\u06ce\u062a\u0627\u0646 \u062f\u06d5\u0648\u06ce \u0646\u0627\u062e\u0627\u062a\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 \u0628\u0627\u06cc\u06d5\u06a9\u06cc \u0628\u06d5\u0647\u06ce\u0632\u062a\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06b5 \u0628\u06a9\u0627 \u0648 \u0644\u06d5 \u062a\u06c6\u06b5\u06d5\u06cc \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u06cc\u062a\u0627\u0646 \u0646\u0648\u0642\u0645\u0648\u0648 \u0628\u06a9\u0627\u061f \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646\u06cc\u0634 \u0647\u06cc\u0686\u06a9\u06d5\u0633\u062a\u0627\u0646 \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u06d5\u0628\u06ce \u06a9\u06d5 \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0648 \u062f\u0627\u061f',
    '\u0626\u06ce\u0645\u06d5 \u0695\u06ce\u0632\u0645\u0627\u0646 \u0644\u06d5 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u0627\u062f\u06d5\u0645 \u06af\u0631\u062a \u0648 \u0644\u06d5 \u0648\u0634\u06a9\u0627\u0646\u06cc \u0648 \u0644\u06d5 \u0632\u06d5\u0631\u06cc\u0627\u062f\u0627 \u0647\u06d5\u06b5\u0645\u0627\u0646 \u06af\u0631\u062a\u0646\u061b \u0695\u06c6\u0632\u06cc \u067e\u0627\u06a9\u0698\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627\u0648\u0646\u061b \u0644\u06d5 \u0686\u0627\u0648 \u0632\u06c6\u0631 \u0644\u06d5 \u06a9\u0631\u062f\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0632\u06c6\u0631 \u067e\u062a\u0631 \u0642\u06d5\u062f\u0631\u0645\u0627\u0646 \u06af\u0631\u062a\u0648\u0648\u0646.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0647\u06d5\u0631 \u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9 \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 - \u0648\u06ce\u0695\u0627\u06cc \u0646\u0627\u0645\u06d5\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 - \u06af\u0627\u0633\u06cc \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u061b \u0647\u06d5\u0631\u06a9\u06ce \u0646\u0627\u0645\u06d5\u06cc \u062f\u06d5\u062f\u06d5\u0633\u062a\u06cc \u0695\u0627\u0633\u062a\u06cc \u0646\u0631\u0627\u0648\u06d5\u060c \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u0627\u0645\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u06d5\u0642\u06d5\u06cc \u062f\u0627\u0648\u06cc \u0632\u0631\u0627\u0648\u06cc \u0646\u0627\u0648 \u0646\u0627\u0648\u06a9\u06d5 \u062e\u0648\u0631\u0645\u0627\u0634 \u0647\u06d5\u0642\u06cc\u0627\u0646 \u0644\u06ce \u0632\u06d5\u0648\u062a \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0647\u06d5\u0631\u06a9\u06ce\u0634 \u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u06a9\u0648\u06ce\u0631\u06d5 \u060c \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0634\u062f\u0627 \u06a9\u06c6\u0631\u06d5\u06cc\u06d5 \u0648 \u067e\u062a\u0631\u06cc\u0634 \u0695\u06ce\u06af\u06d5\u06cc \u0644\u06ce \u06af\u0648\u0645 \u062f\u06d5\u0628\u06ce.',
    '\u0647\u06ce\u0646\u062f\u06d5\u06cc \u0646\u06d5\u0645\u0627\u0628\u0648\u0648 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06d5\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5\u06cc - \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u06cc - \u06a9\u0644\u0627\u062a \u06a9\u06d5\u0646 \u0648 \u0634\u062a\u06ce\u06a9\u06cc \u062a\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u0646\u06cc \u0648 \u062f\u0631\u06c6 \u0628\u06d5 \u0632\u0627\u0631\u06cc \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0647\u06d5\u06b5 \u0628\u0628\u06d5\u0633\u062a\u06cc\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u0633\u0627 \u062f\u06d5\u0628\u0648\u0648\u0646\u06d5 \u062f\u06c6\u0633\u062a\u062a.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u0645\u0627\u0646 \u062a\u06c6\u0645\u0627\u0646 \u062a\u0648\u0646\u062f \u0695\u0627\u0646\u06d5\u06af\u0631\u062a\u0627\u06cc\u06d5\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5\u0628\u0648\u0648 \u06a9\u06d5\u0645\u06ce\u06a9 \u0628\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u0628\u06ce\u06cc.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0648\u0627\u0634 \u0628\u0627\u060c \u0633\u0648\u0648\u0631 \u0628\u0632\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u0698\u06cc\u0646\u062f\u0627 \u062f\u0648\u0648 \u0642\u0627\u062a \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u0645\u0627\u0646 \u062f\u06d5\u062f\u0627\u06cc\u061b \u0644\u06d5 \u0645\u06d5\u0631\u06af\u06cc\u0634\u062f\u0627 \u0626\u0627\u0632\u0627\u0631\u06d5\u06a9\u06d5\u062a \u062f\u0648\u0648 \u0633\u06d5\u0631\u06d5 \u0628\u0648\u0648. \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5\u0633\u06cc \u0648\u0627\u0634\u062a \u0646\u06d5\u0628\u0648\u0648 - \u062f\u06d5\u0633\u06d5\u0631 \u0645\u06d5\u0695\u0627 - \u0628\u06ce\u062a\u06d5 \u0647\u0627\u0646\u0627\u062a.',
    '\u0647\u06ce\u0646\u062f\u06d5\u06cc \u0646\u06d5\u0645\u0627\u0628\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5\u0648 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0628\u062a\u062e\u06d5\u0646\u06d5 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5\u0648\u06d5\u060c \u062a\u0627\u06a9\u0648\u0648 \u0644\u06d5\u0648\u06ce \u0648\u06d5\u062f\u06d5\u0631\u062a \u0646\u06ce\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0648\u0627\u0634 \u0628\u0627\u060c \u0628\u06d5 \u0634\u06c6\u0646 \u062a\u06c6\u062f\u0627 - \u062f\u06d5\u0645\u06ce\u06a9 \u0646\u06d5\u0628\u06ce - [\u0626\u06d5\u0648\u0627\u0646\u06cc\u0634] \u062a\u06ce\u062f\u0627 \u0646\u06d5\u062f\u06d5\u0645\u0627\u0646.',
    '\u067e\u06ce\u0648\u062f\u0627\u0646\u06ce\u06a9\u06d5 [\u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5]\u060c \u06a9\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6\u0634 \u0628\u0648\u0648\u0646\u060c \u0646\u0627\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u0646\u061b \u06cc\u0627\u0633\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u06af\u06c6\u0695\u0627\u0646\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0646\u0627\u06cc\u06d5.',
    '\u0644\u06d5\u0648 \u0645\u0627\u0648\u06d5\u06cc \u062e\u06c6\u0631 \u06a9\u0644\u0627 \u062f\u06d5\u0628\u06ce \u0647\u06d5\u062a\u0627 \u062a\u0627\u0631\u06cc\u06a9\u0627\u0646\u06cc \u0634\u06d5\u0648\u06ce \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5 \u0648 \u0646\u0648\u06ce\u0698 \u062e\u06c6\u0646\u062f\u0646\u06cc \u0633\u067e\u06ce\u062f\u06d5\u0634\u062a \u0647\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631\u06d5. \u0646\u06c6\u0698\u062e\u06c6\u0646\u062f\u0646\u06cc \u0633\u067e\u06ce\u062f\u06d5 \u0628\u06d5\u0647\u06d5\u0695\u0645\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u0634\u06d5\u0648\u06cc\u0634\u062f\u0627 \u0628\u06c6\u06cc \u0647\u06d5\u0633\u062a\u06d5\u0648\u06d5\u061b \u0626\u06d5\u0645\u06d5 \u0632\u06cc\u0627\u062f\u06d5 \u0646\u0648\u06ce\u0698\u06cc \u062a\u06c6 \u0628\u06ce. \u0628\u06d5\u0634\u06a9\u06ce\u06a9\u0645 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0628\u062a\u0646\u06ce\u0631\u06ce\u062a\u06d5 \u062c\u06ce\u06af\u0627\u06cc\u06d5\u06a9\u06cc \u067e\u06d5\u0633\u0646\u062f \u06a9\u0631\u0627\u0648.',
    '\u0628\u0634\u067e\u0627\u0695\u06ce\u0648\u06d5 \u06a9\u06d5: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u0645\u0646! \u0695\u06ce\u06af\u06d5\u0645 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u0627\u0633\u062a\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u0628\u06d5\u0631\u06d5\u0648\u0695\u0627\u0633\u062a\u06cc\u0645 \u0628\u06d5 \u0695\u06ce \u0628\u06a9\u06d5\u061b \u0647\u06ce\u0632\u06ce\u06a9\u06cc \u0626\u0627\u0631\u06cc\u06a9\u0627\u0631\u06cc\u0634\u0645 \u0628\u06c6\u062f\u06cc\u0627\u0631\u06cc \u06a9\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648\u0627 \u0695\u0627\u0633\u062a\u06cc \u0647\u0627\u062a \u0648 \u0686\u0631\u0648\u0648\u06a9 \u0646\u06d5\u0645\u0627\u061b \u062f\u06cc\u0627\u0631\u06d5 \u0686\u0631\u0648\u0648\u06a9 \u0644\u06d5 \u0646\u06d5\u0645\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5\u060c \u0634\u062a\u06cc \u0648\u0627\u0648 \u0628\u06c6 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u06a9\u06d5 \u0645\u0641\u0627 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5 \u0628\u06c6 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646 \u0648 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u06cc\u0634 \u067e\u062a\u0631 \u067e\u06ce\u06cc \u0632\u06cc\u0627\u0646\u0628\u0627\u0631 \u062f\u06d5\u0628\u0646.',
    '\u0645\u0631\u06c6 \u0647\u06d5\u0631\u06af\u0627 \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u06cc \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06ce\u060c \u067e\u0634\u062a \u062a\u06ce\u062f\u06d5\u06a9\u0627 \u0648 \u0628\u0627\u062f\u06cc \u0647\u06d5\u0648\u0627\u06cc\u06cc \u062f\u06d5\u0646\u0648\u06ce\u0646\u06ce\u061b \u0647\u06d5\u0631\u06af\u0627 \u0628\u06d5\u06b5\u0627\u0634\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u0647\u0627\u062a\u060c \u062a\u0648\u0648\u0634\u06cc \u0646\u0627\u0647\u0648\u0645\u06ce\u062f\u06cc \u062f\u06d5\u0628\u06ce.',
    '\u0628\u06ce\u0698\u06d5: \u0695\u06d5\u0641\u062a\u0627\u0631\u06cc \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06d5\u067e\u06ce\u06cc \u0626\u0627\u06a9\u0627\u0631\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0632\u0627\u0646\u0627\u062a\u0631\u06d5\u060c \u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0634\u0627\u0631\u06d5\u0632\u0627\u062a\u0631\u06d5.',
    '\u0644\u06d5 \u062a\u06c6 \u062f\u06d5\u067e\u0631\u0633\u0646: \u06af\u06cc\u0627\u0646 \u0686\u06cc\u06cc\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u06af\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce\u061b \u0626\u06ce\u0648\u06d5 \u06a9\u06d5\u0645\u06ce\u06a9\u062a\u0627\u0646 \u0644\u06d5 \u0632\u0627\u0646\u0633\u062a \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u06a9\u06d5\u06cc\u0641\u0646 \u0644\u06ce \u0628\u06ce\u060c \u0647\u06d5\u0631\u0686\u06cc\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u067e\u06ce \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u06cc \u062f\u06d5\u06cc\u0628\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5\u0633\u06cc\u0634 \u0646\u06cc\u06cc\u06d5 \u062f\u0698\u06cc \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0626\u0627\u0631\u06cc\u06a9\u0627\u0631\u062a \u0628\u06ce.',
    '\u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06ce\u062a\u0627 \u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0632\u06c6\u0631\u06cc \u0686\u0627\u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u062a\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0631\u06c6 \u0648 \u062c\u0646\u060c \u06af\u0634 \u0648\u06d5\u06a9\u06c6 \u0628\u0646\u060c \u0648\u06ce\u0646\u06d5\u06cc \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0628\u06ce\u0646\u0646\u060c \u0644\u06d5 \u0634\u0628\u06cc \u0626\u06d5\u0648 \u062f\u0627\u062f\u06d5\u0645\u06ce\u0646\u0646\u061b \u0628\u0627 \u0647\u06d5\u0645\u0648\u0648\u0634\u06cc\u0627\u0646 \u067e\u0634\u062a\u06cc\u0648\u0627\u0646\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u0628\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0647\u06d5\u0631 \u0628\u0627\u0631\u06ce\u06a9\u06d5\u0648\u06d5 \u0644\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646\u062f\u0627 \u0646\u06d5\u0632\u06cc\u0644\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0647\u06ce\u0646\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 - \u062d\u0627\u0634\u0627 \u0646\u06d5\u0628\u06ce - \u0647\u06cc\u0686 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0695\u0648\u0627\u062a \u067e\u06ce \u0646\u0627\u0647\u06ce\u0646\u06cc\u0646 \u062a\u0627 \u0644\u06d5\u0645 \u0639\u06d5\u0631\u0632\u06d5 \u06a9\u0627\u0646\u06cc\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0647\u06d5\u06b5\u062f\u06d5\u0642\u0648\u06b5\u06ce\u0646\u06cc\u061b',
    '\u06cc\u0627\u0646 \u0628\u0627\u063a\u06ce\u06a9\u06cc \u062e\u0648\u0631\u0645\u0627 \u0648 \u0695\u06d5\u0632\u062a \u0647\u06d5\u0628\u06ce \u0648 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0648\u0627 \u0628\u06a9\u06d5\u06cc \u06a9\u06d5 \u062c\u06c6\u0628\u0627\u0631\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u0627 \u0641\u0695\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06ce\u061b',
    '\u06cc\u0627\u0646 - \u0648\u06d5\u06a9 \u062e\u06c6\u062a \u062e\u06d5\u06cc\u0627\u06b5\u062a \u062f\u06d5\u06a9\u0631\u062f - \u067e\u0627\u0698\u067e\u0627\u0698\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u0645\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u0627\u0628\u0648\u06d5\u0631\u06ce\u0646\u06d5\u061b \u06cc\u0627 \u062e\u0648\u062f\u0627 \u0648 \u0686\u06d5\u0646\u062f \u0641\u0631\u0634\u062a\u06d5\u06cc\u06d5\u06a9 \u0628\u06ce\u0646\u06d5 \u0648 \u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648\u0645\u0627\u0646 \u0628\u06a9\u06d5\u061b',
    '\u06cc\u0627\u0646 \u0645\u0627\u06b5\u06ce\u06a9\u062a \u0644\u06d5 \u0632\u06ce\u0695 \u0647\u06d5\u0628\u06ce\u061b \u06cc\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u062f\u0627 \u0647\u06d5\u06b5\u0628\u0686\u06cc \u0648 \u0633\u06d5\u0631\u06cc\u0634 \u06a9\u06d5\u0648\u06cc\u060c \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u06cc\u0646\u061b \u062a\u0627 \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06c6\u062a \u0628\u06c6 \u062f\u06ce\u0646\u06cc \u0648 \u062f\u06d5\u06cc\u062e\u0648\u06ce\u0646\u06cc\u0646\u06d5\u0648\u06d5. \u0628\u06ce\u0698\u06d5: \u067e\u0627\u06a9\u06cc \u0647\u06d5\u0631 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u0645\u06d5\u060c \u0626\u0627\u062e\u06c6 \u0645\u0646 \u0686\u06cc\u0645 \u0628\u0644 \u0644\u06d5 \u0645\u0631\u06c6\u06cc\u06ce \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u061f',
    '\u062a\u06d5\u0646\u06cc\u0627 \u0628\u06cc\u0627\u0646\u0648\u0648\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 - \u06a9\u06d5 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a \u0648 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f - \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0626\u06ce\u0698\u0646: \u062c\u0627 \u0686\u06c6\u0646 \u062e\u0648\u062f\u0627 \u0645\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0646\u0627\u0648\u062f\u06ce\u0631 \u062f\u06d5\u06a9\u0627\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0641\u0631\u0634\u062a\u06d5 \u0628\u06d5 \u0626\u0627\u0631\u062e\u06d5\u06cc\u0627\u0646\u06cc \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u062f\u0627 \u0695\u06c6\u06cc\u0634\u062a\u0628\u0627\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u0641\u0631\u0634\u062a\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0646\u0627\u0631\u062f\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0628\u06d5\u0633\u06d5 \u0628\u06c6 \u0634\u0627\u06cc\u06d5\u062a \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u0648 \u0626\u06ce\u0648\u06d5\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u0628\u06cc\u0646\u06ce.',
    '\u06a9\u06d5\u0633\u06ce \u062e\u0648\u062f\u0627 \u0695\u06ce\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u060c \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06d5\u061b \u06af\u0648\u0645\u0695\u0627\u0634\u06cc \u06a9\u0627\u060c \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f \u0627 \u062a\u06c6 \u062f\u06c6\u0633\u062a\u06cc\u0627\u0646 \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u0627\u06a9\u06d5\u06cc. \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 \u0628\u06d5 \u062f\u06d5\u0645\u06d5\u0648\u0695\u0648\u0648\u06cc\u06cc \u0648 \u06a9\u06c6\u0631\u06d5\u06cc\u06cc \u0648 \u0644\u0627\u06b5\u06cc \u0648 \u06a9\u06d5\u0695\u06cc\u060c \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u06a9\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0646\u0648\u0627\u06cc\u0627\u0646 \u062c\u06d5\u062d\u06d5\u0646\u062f\u06d5\u0645 \u062f\u06d5\u0628\u06ce. \u0647\u06d5\u062a\u0627 \u0626\u0627\u06af\u0631 \u062f\u06ce \u062f\u0627\u0645\u0631\u06a9\u06ce\u060c \u06a9\u06b5\u06d5\u06a9\u06d5 \u067e\u062a\u0631 \u0646\u06ce\u06b5 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0628\u0627 \u0626\u06d5\u0645\u06d5 \u06af\u06c6\u0695\u06cc \u06af\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0628\u06ce\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0645\u06d5 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0633\u06a9 \u0648 \u067e\u0631\u0648\u0648\u0633\u06a9\u06cc\u0634 \u0628\u0648\u0648\u06cc\u0646\u060c \u062f\u06cc\u0633\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u06cc\u0646\u06d5\u0648\u06d5\u061f',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646\u060c \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u062f\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0647\u06cc \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u062f\u0631\u0648\u0633 \u0628\u06a9\u0627\u061b \u0645\u0627\u0648\u06d5\u06cc\u0634\u06cc \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646 \u06a9\u06d5 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u06af\u0627\u061f \u0628\u06d5\u06b5\u0627\u0645 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0647\u06d5\u0631 \u062d\u0627\u0634\u0627 \u06a9\u0631\u062f\u0646 \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u062e\u06ce\u0648\u06cc \u06af\u06d5\u0646\u062c\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u0628\u0648\u0648\u0646\u0627\u06cc\u06d5\u060c \u0647\u06ce\u0645\u0627\u0646 \u062f\u06d5\u0633 \u0695\u0698\u062f\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f. \u0645\u0631\u06c6 \u0686\u06a9\u0648\u0648\u0633 \u0648 \u0642\u0695\u0646\u06cc\u0633\u06d5.',
    '\u0646\u06c6 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627\u061b \u0644\u06d5 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644 \u067e\u0631\u0633\u06d5\u060c [\u0626\u06d5\u0648 \u062f\u06d5\u0632\u0627\u0646\u0646.] \u06a9\u0627\u062a\u06ce \u06af\u06d5\u06cc\u0634\u062a\u06d5 \u0628\u06d5\u0631\u06d5\u0648\u060c \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u067e\u06ce\u06cc \u0648\u062a: \u0645\u0648\u0648\u0633\u0627! \u0645\u0646 \u0648\u0627 \u062e\u06d5\u06cc\u0627\u06b5 \u062f\u06d5\u06a9\u06d5\u0645 \u0626\u06d5\u062a\u06c6 \u062c\u0627\u062f\u0648\u0648\u062a \u0644\u06ce\u06a9\u0631\u0627\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u062a\u06c6 \u062e\u06c6\u062a \u0633\u0648\u0648\u0631 \u062f\u06d5\u0632\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0634\u062a\u0627\u0646\u06d5 \u06a9\u06d5\u0648\u0627 \u0632\u06d5\u0642 \u0644\u06d5\u0628\u06d5\u0631 \u0686\u0627\u0648\u0627\u0646\u0646\u060c \u0647\u06d5\u0631 \u062f\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0632\u06d5\u0648\u06cc\u0646 \u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0646\u0627\u0631\u062f\u0648\u0648\u0646\u06cc\u06d5 \u062e\u0648\u0627\u0631. \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646! \u0645\u0646\u06cc\u0634 \u0644\u0627\u0645 \u0648\u0627\u06cc\u06d5 \u062a\u06c6 \u062a\u06ce\u062f\u0627 \u062f\u06d5\u0686\u06cc.',
    '\u0648\u06cc\u0633\u062a\u06cc \u06af\u06d5\u0695\u06cc\u0627\u0646 \u062a\u06ce\u0647\u0627\u06b5\u06ce\u0646\u06ce \u0648 \u0644\u06d5\u0648 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u0646 \u06a9\u0627\u060c \u0626\u06ce\u0645\u06d5\u0634 \u0626\u06d5\u0645 \u0648 \u062e\u0695 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627 \u0628\u0648\u0648\u0646\u060c \u0646\u0648\u0642\u0645\u0646 \u06a9\u0631\u062f\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06cc\u0634 \u0628\u06d5 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u0645\u0627\u0646 \u06af\u0648\u062a: \u0646\u06cc\u0634\u062a\u06d5\u062c\u06ce \u0628\u0646 \u0644\u06d5\u0645 \u0648\u06b5\u0627\u062a\u06d5 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0645\u06ce\u06a9\u06cc\u0634 \u0633\u06d5\u06b5\u0627 \u0695\u0627\u0628\u0648\u0648\u060c \u0626\u06d5\u0648\u0633\u0627 \u06af\u0634\u062a\u0648\u0648 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u06a9\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0645\u06d5\u0645\u0627\u0646 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0646\u0627\u0631\u062f \u0648 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc\u0634 \u0647\u0627\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u061b \u0626\u06ce\u0645\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u0646\u0627\u0631\u062f \u0645\u0698\u062f\u06d5\u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0628\u06cc.',
    '\u0642\u0648\u0631\u0626\u0627\u0646\u06ce\u06a9\u06d5 \u0628\u06d5 \u062c\u06cc\u0627\u062c\u06cc\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u06d5\u061b \u062a\u0627 \u0647\u06ce\u062f\u06cc\u06a9\u0627 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645\u06cc \u0628\u062e\u0648\u06ce\u0646\u06cc\u06d5\u0648\u06d5 \u0648 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06cc \u0695\u06c6\u0698 \u0628\u06d5\u0695\u06ce\u0645\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u067e\u06ce \u0628\u06ce \u06cc\u0627 \u0646\u06d5\u0628\u06ce\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 [\u0644\u06d5\u0645\u06d5] \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u06a9\u0631\u0627\u0628\u0648\u0648\u0646\u060c \u0647\u06d5\u0631\u06af\u0627 \u0628\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0626\u06d5\u0631\u0632\u06cc\u0646\u06af \u062f\u06d5\u062e\u06d5\u0646\u06d5 \u0633\u06d5\u0631 \u0632\u06d5\u0648\u06cc \u0648 \u0633\u0648\u0698\u062f\u06d5 \u062f\u06d5\u0628\u06d5\u0646.',
    '\u0626\u06d5\u0634\u06b5\u06ce\u0646: \u067e\u0627\u06a9\u06cc \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u06d5\u060c \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646 \u0648\u0627 \u0648\u06d5\u062f\u06cc \u0647\u0627\u062a.',
    '\u062f\u06d5\u0645\u06d5\u0648\u0695\u0648\u0648 \u062e\u06c6 \u062a\u062e\u06ce\u06b5 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u062f\u06d5\u06af\u0631\u06cc\u0646 \u0648 \u0626\u06d5\u0645\u06d5\u0634 \u062e\u06c6 \u0628\u06d5\u06a9\u06d5\u0645 \u0632\u0627\u0646\u06cc\u0646\u06cc\u0627\u0646 \u067e\u062a\u0631 \u062f\u06d5\u06a9\u0627.',
    '\u0628\u06ce\u0698\u06d5: \u0647\u0627\u0648\u0627\u0631 \u0644\u06d5 \u062e\u0648\u0627 \u0628\u06a9\u06d5\u0646 \u06cc\u0627\u0646 \u0647\u0627\u0646\u0627 \u0628\u06d5 \u0695\u06d5\u062d\u0645\u0627\u0646 \u0628\u06d5\u0631\u0646\u060c \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce\u06a9\u06d5\u061b \u0626\u06d5\u0648 \u0646\u0627\u0648\u06cc \u0632\u06c6\u0631 \u067e\u06cc\u0631\u06c6\u0632\u06cc \u0647\u06d5\u0646. \u0644\u06d5 \u0646\u06c6\u0698\u062a\u062f\u0627 \u0645\u06d5\u0642\u0695\u06ce\u0646\u06d5 \u0648 \u0645\u06d5\u0634 \u0648\u0631\u062a\u06ce\u0646\u06d5! \u0634\u06ce\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u0646\u0627\u0648\u0646\u062c\u06cc \u0628\u06c6 \u062e\u06c6\u062a \u0695\u06d5\u0686\u0627\u0648 \u06a9\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0633\u0648\u067e\u0627\u0633 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06ce \u06a9\u06d5 \u0647\u06cc\u0686 \u0641\u0631\u0632\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u0628\u06c6 \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u0646\u06d5\u0628\u0698\u0627\u0631\u062f \u0648 \u0644\u06d5 \u0645\u0648\u06b5\u06a9\u06cc\u062f\u0627 \u0647\u06cc\u0686 \u0628\u0631\u0627\u0628\u06d5\u0634\u06ce\u06a9\u06cc \u0646\u06d5\u0628\u0648\u0648\u06d5 \u0648 \u0628\u06d5 \u0646\u0627\u0686\u0627\u0631\u06cc \u0647\u0627\u0646\u0627\u06cc \u0628\u06d5 \u06a9\u06d5\u0633 \u0646\u06d5\u0628\u0631\u062f\u0648\u0648\u06d5 \u0648 \u062a\u0627\u0631\u06cc\u0641\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06cc \u0628\u06a9\u06d5\u061b \u062a\u0627\u0631\u06cc\u0641\u06ce \u06a9\u06d5 \u0647\u06ce\u0698\u0627\u06cc \u0626\u06d5\u0648\u06d5.',
    '\u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u0628\u06c6 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u06cc \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u06ce \u0648 \u0647\u06cc\u0686 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06cc \u062a\u06ce \u0646\u06d5\u062e\u0633\u062a.',
    '[\u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc] \u0632\u06c6\u0631 \u067e\u062a\u06d5\u0648\u06d5\u060c \u062a\u0627 \u0644\u06d5 \u0633\u0632\u0627\u06cc \u062a\u0648\u0646\u062f\u0648\u062a\u06cc\u0698\u06cc - \u06a9\u06d5 \u0628\u06cc\u0646\u0648\u06ce\u0646\u06ce - \u062a\u0631\u0633\u06cc \u0648\u06d5\u0628\u06d5\u0631 \u0645\u06d5\u0631\u062f\u0645 \u0628\u0646\u06ce \u0648 \u0628\u06d5 \u0626\u06d5\u0648 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06d5\u06cc \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646 \u0645\u0698\u062f\u06d5 \u0628\u062f\u0627 \u06a9\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u0628\u0627\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce\u061b',
    '\u067e\u0627\u062f\u0627\u0634\u06ce \u0647\u06d5\u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06cc.',
    '\u0647\u06d5\u062a\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u062a\u0631\u0633\u06ce\u0646\u06cc \u06a9\u06d5 \u0648\u062a\u06cc\u0627\u0646: \u062e\u0648\u062f\u0627 \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0628\u06c6 \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u0648\u0648\u06d5.',
    '\u0646\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0686\u06cc\u06cc\u0627\u0646 \u0644\u06ce \u0632\u0627\u0646\u06cc\u0648\u06d5 \u0646\u06d5 \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646\u061b \u0642\u0633\u06ce\u06a9\u06cc \u06cc\u06d5\u06af\u062c\u0627\u0631 \u0632\u06d5\u0644\u0627\u0645\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06d5\u0645 \u062f\u06d5\u0631\u0686\u0648\u0648. \u0647\u06d5\u0631\u0686\u06cc \u0626\u06ce\u0698\u0646 \u0644\u06d5 \u06a4\u0695 \u067e\u06ce\u0648\u06d5\u060c \u0686\u06cc \u062a\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u0695\u06d5\u0646\u06af\u06d5 \u062a\u06c6 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627 \u0628\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0646\u0627\u06a9\u06d5\u0646\u060c \u062e\u06d5\u0645\u06ce \u0648\u0627 \u0628\u062e\u0648\u06cc\u060c \u062a\u06ce \u0628\u0686\u06cc.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc\u06d5 \u0626\u06ce\u0645\u06d5 \u06a9\u0631\u062f\u0646 \u0628\u06d5 \u0632\u06d5\u0646\u0628\u06d5\u0631\u06cc \u062a\u0627 \u0628\u06cc\u0627\u0646 \u0626\u06d5\u0632\u0645\u0648\u0648\u06cc\u0646 \u06a9\u0627\u0645\u06d5\u06cc\u0627\u0646 \u06a9\u0627\u0631\u0686\u0627\u06a9\u062a\u0631 \u062f\u06d5\u0628\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u06d5\u06cc \u0648\u0627 \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc\u06d5 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5 \u0628\u06cc\u0627\u0648\u0627\u0646\u06ce\u06a9\u06cc \u0648\u0634\u06a9\u0627\u0631\u06c6\u06cc \u0642\u0627\u0642\u0695.',
    '\u062a\u06c6 \u067e\u06ce\u062a \u0648\u0627\u0628\u0648\u0648 \u06a9\u06d5 \u06cc\u0627\u0631\u0627\u0646\u06cc \u063a\u0627\u0631 \u0648 \u0695\u06d5\u0642\u06cc\u0645\u060c \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u062f\u0627 \u0633\u06d5\u0645\u06d5\u0631\u06d5\u06cc\u06d5\u061f',
    '\u06a9\u0627\u062a\u06ce \u0644\u0627\u0648\u06d5\u06a9\u0627\u0646 \u067e\u06d5\u0646\u0627\u06cc\u0627\u0646 \u0628\u06c6 \u063a\u0627\u0631\u06d5\u06a9\u06d5 \u0628\u0631\u062f \u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646! \u062a\u06c6 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u062a\u0645\u0627\u0646 \u0628\u062e\u06d5 \u0648 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0645\u06d5\u06cc\u0633\u06d5\u0631\u06a9\u06d5.',
    '\u0644\u06d5\u0648 \u063a\u0627\u0631\u06d5\u062f\u0627 \u0647\u06d5\u062a\u0627 \u0686\u06d5\u0646\u062f \u0633\u0627\u06b5 \u0648 \u0632\u06d5\u0645\u0627\u0646\u0627\u0646\u060c \u06af\u0648\u06ce\u06cc \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646\u0645\u0627\u0646 \u0626\u0627\u062e\u0646\u06cc \u0648 [\u062f\u06d5\u062e\u06d5\u0648\u0695\u0627 \u0686\u0648\u0648\u0646].',
    '\u067e\u0627\u0634\u0627\u0646 \u0648\u06d5\u062e\u06d5\u0628\u06d5\u0631\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0646 \u062a\u0627 \u0628\u0632\u0627\u0646\u06cc\u0646 \u06a9\u0627\u0645 \u0644\u06d5 \u062f\u0648\u0648\u0644\u0627 - \u0647\u06d5\u0631 \u0628\u06d5 \u0647\u06d5\u0698\u0645\u0627\u0631 - \u0633\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0645\u0627\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0633\u06d5\u0631\u06af\u0648\u0631\u0648\u0634\u062a\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0626\u06d5\u0645\u0627\u0646\u062a \u0628\u06c6 \u0648\u06d5\u062f\u06d5\u06af\u06ce\u0695\u06cc\u0646: \u0686\u06d5\u0646\u062f \u062e\u0648\u0631\u062a\u06ce\u06a9 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0628\u06d5\u0647\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0628\u0648\u0648\u061b \u0626\u06ce\u0645\u06d5\u0634 \u067e\u062a\u0631 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc\u0645\u0627\u0646 \u062f\u0627\u0628\u0648\u0648\u0646\u06ce.',
    '\u0626\u0627\u0632\u0627\u06cc\u06cc\u0634\u0645\u0627\u0646 \u062e\u0633\u062a\u06d5 \u062f\u06b5\u06cc\u0627\u0646\u060c \u06a9\u06d5 \u0695\u0627\u067e\u06d5\u0695\u06ce\u0646. \u0626\u06d5\u0648\u0633\u0627 \u0648\u062a\u06cc\u0627\u0646: \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0646\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u0698\u06af\u06d5 \u0644\u06d5 \u0626\u06d5\u0648 \u0647\u0627\u0646\u0627 \u0628\u06c6 \u0647\u06d5\u0631 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u06ce\u06a9 \u0628\u06d5\u0631\u06cc\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc \u06af\u0648\u062a\u0646 \u0644\u0627\u0646\u062f\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0647\u06c6\u0632\u06cc \u0626\u06ce\u0645\u06d5\u0646\u060c \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0686\u06d5\u0646\u062f \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u06cc \u062a\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0633. \u0626\u06d5\u062f\u06cc \u0686\u06c6\u0646\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0647\u06ce\u0646\u0646\u061f \u0633\u0627 \u06a9\u06ce \u0644\u06d5\u0648\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5 \u06a9\u06d5 \u0628\u0648\u062e\u062a\u0627\u0646\u06ce \u0628\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u06a9\u0627\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648\u0627\u0646 \u0648 \u0644\u06d5\u0648 \u0686\u062a\u06af\u06d5\u0644\u06d5\u06cc \u0648\u0627 - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627 - \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0647\u06d5\u06b5\u0628\u0695\u0627\u0648\u0646\u060c \u0633\u0627 \u0628\u06d5\u0645 \u063a\u0627\u0631\u06d5 \u067e\u06d5\u0646\u0627 \u0628\u06d5\u0631\u0646\u061b \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0648\u0648 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc\u062a\u0627\u0646 \u062f\u06d5\u062e\u0627 \u0648 \u0647\u06c6\u06cc\u06d5\u06a9\u06cc \u0648\u0627\u0634 \u062f\u06d5\u0695\u06d5\u062e\u0633\u06ce\u0646\u06ce \u0628\u06d5 \u0626\u0627\u0648\u0627\u062a\u0648\u0648 \u0628\u06af\u06d5\u0647\u06ce\u0646\u06ce.',
    '\u06a9\u06d5 \u062e\u06c6\u0631 \u0647\u06d5\u06b5\u062f\u06ce\u060c \u0628\u0695\u0648\u0627\u0646\u06cc \u0644\u06d5 \u0626\u0627\u0644\u06cc \u0695\u0627\u0633\u062a\u06cc \u0626\u06d5\u0634\u06a9\u06d5\u0648\u062a\u06d5\u06a9\u06d5\u06cc\u0627\u0646\u06cc \u062f\u0627\u0648\u06d5\u061b \u0644\u06d5 \u062f\u06d5\u0645\u06cc \u0632\u06d5\u0631\u062f\u06d5\u067e\u06d5\u0695\u06cc\u0634\u062f\u0627\u060c \u0628\u06d5\u0631\u06d5\u0648 \u0686\u06d5\u067e \u0644\u06ce\u0627\u0646 \u0644\u0627\u062f\u06d5\u062f\u0627 \u0648 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u06af\u06c6\u0695\u0627\u06cc\u06cc\u06d5\u06a9\u06d5\u062f\u0627\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062e\u0648\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0628\u06a9\u0627\u060c \u0626\u06d5\u0648 \u0634\u0627\u0631\u06d5\u0632\u0627 \u062f\u06d5\u0628\u06ce\u062a \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u06af\u0648\u0645\u0695\u0627 \u0628\u06a9\u0627\u060c \u062f\u06c6\u0633\u062a\u06ce\u06a9\u06cc \u0695\u06ce\u0646\u0645\u0627\u06cc \u0628\u06c6 \u0646\u0627\u062f\u06c6\u0632\u0631\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u062a\u06c6 \u067e\u06ce\u062a \u0648\u0627\u06cc\u06d5 \u06a9\u06d5 \u0628\u06ce\u062f\u0627\u0631\u0646\u060c \u06a9\u06d5\u0686\u06cc \u062e\u06d5\u0648\u062a\u0648\u0648\u0646. \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0686\u06c6\u067e\u06d5\u062f\u0627 \u0647\u06d5\u06b5\u06af\u06ce\u0695\u0648\u06d5\u0631\u06af\u06ce\u0695\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u06cc\u0646. \u0633\u06d5\u06cc\u06d5\u06a9\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u0695\u06ce\u0633\u0645\u06d5\u06cc \u062f\u06d5\u0631\u06af\u0627\u06a9\u06d5\u0648\u06d5 \u0648\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0628\u0648\u0648. \u062a\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0648\u0648\u0628\u0627\u06cc \u0628\u062a \u062f\u06cc\u0628\u0627\u0646\u060c \u0628\u06d5 \u0642\u0627\u0686\u0627\u0646\u062f\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u062f\u0648\u0648\u0627 \u062f\u06d5\u06af\u06d5\u0695\u0627\u06cc\u06d5\u0648\u06d5 \u0648 \u0633\u06d5\u0631\u062a\u0627\u067e\u0627\u062a \u062a\u0631\u0633 \u062f\u0627\u06cc \u062f\u06d5\u06af\u0631\u062a.',
    '\u0626\u0627\u0648\u0627\u0634 \u0648\u06d5\u062e\u06d5\u0628\u06d5\u0631\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0646 \u062a\u0627 \u0644\u06d5\u06af\u06d5\u06b5 \u06cc\u06d5\u06a9\u062a\u0631\u0627 \u0628\u062f\u0648\u06ce\u0646. \u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0648\u062a\u06cc: \u0686\u06d5\u0646\u062f \u0645\u0627\u0646\u06d5\u0648\u06d5\u061f \u06af\u0648\u062a\u06cc\u0627\u0646: \u0695\u06c6\u0698\u06ce\u06a9 \u06cc\u0627\u0646 \u067e\u0627\u0698\u06d5 \u0695\u06c6\u0698\u06ce\u06a9 \u0645\u0627\u06cc\u0646\u06d5\u0648\u06d5. \u06af\u0648\u062a\u06cc\u0627\u0646: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0686\u0627\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06d5\u0646\u062f \u0645\u0627\u0646\u06d5\u0648\u06d5. \u062f\u06d5 \u06cc\u06d5\u06a9\u06ce\u06a9\u06cc \u0628\u06d5\u0645 \u062f\u0631\u0627\u0648\u06d5 \u0648 \u0628\u0646\u06ce\u0631\u0646\u06d5 \u0634\u0627\u0631 \u0628\u0627 \u0628\u0628\u06cc\u0646\u06ce \u06a9\u0627\u0645 \u062e\u0648\u0627\u0631\u062f\u06d5\u0645\u06d5\u0646\u06cc \u067e\u0627\u06a9\u062a\u0631\u06d5\u060c \u0628\u0698\u06cc\u0648\u06ce\u06a9\u062a\u0627\u0646 \u0644\u06d5\u0648 \u0628\u06c6 \u0628\u06ce\u0646\u06ce \u0648 \u0628\u06d5\u0632\u06cc\u062a\u06cc \u0628\u0628\u0632\u0648\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc\u0634 \u067e\u06ce\u062a\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0686\u0648\u0646\u06a9\u0648\u0648 \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u0646\u060c \u06cc\u0627 \u0628\u06d5\u0631\u062f\u06d5\u0628\u0627\u0631\u0627\u0646\u0648\u0648 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06cc\u0627 \u062f\u06d5\u062a\u0627\u0646\u0628\u06d5\u0646\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u062f\u06cc\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0695\u0632\u06af\u0627\u0631\u06cc\u062a\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u062f \u067e\u06ce\u06cc\u0627\u0646 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5. \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u062f\u0627 \u0645\u0644\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u0627\u0648\u06ce\u0698 \u06a9\u0631\u062f\u0646 \u0646\u0627\u061b \u06af\u0648\u062a\u06cc\u0627\u0646: \u0686\u0627\u0648\u06d5\u062e\u0627\u0646\u0648\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0631\u0648\u0633 \u06a9\u06d5\u0646\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0644\u06d5\u0646\u06af\u06c6\u06cc\u0627\u0646 \u0686\u0627\u062a\u0631 \u062f\u06d5\u0646\u0627\u0633\u06ce. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0645\u0627\u0646 \u0642\u0633\u06d5\u06cc\u0627\u0646 \u0632\u06c6\u0631\u062a\u0631 \u062f\u06d5\u0695\u06c6\u06cc\u06cc\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u062f\u06d5\u0628\u06ce \u0645\u0632\u06af\u06d5\u0648\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06a9\u06d5\u06cc\u0646.',
    '\u062f\u0628\u06ce\u0646: \u0633\u06ce\u0646 \u0648 \u0628\u06d5 \u0628\u06d5\u0634\u062f\u0627\u0631\u06d5\u06a9\u06d5\u0648\u06d5 \u0686\u0627\u0631\u0646\u061b \u0626\u06d5\u0634\u06b5\u06ce\u0646: \u067e\u06d5\u0646\u062c\u0646\u060c \u0628\u06d5 \u0633\u067e\u06b5\u06c6\u062a\u06d5\u06a9\u06d5\u0695\u0627 \u0634\u06d5\u0634\u0646. \u0644\u06d5 \u0646\u06d5\u062f\u06cc\u0648 \u062e\u06d5\u06cc\u0627\u06b5\u0627\u062a \u062f\u06d5\u06a9\u06d5\u0646 \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a \u0628\u0627\u0632\u06ce\u06a9\u06cc\u0634 \u0626\u06ce\u0698\u0646: \u062d\u06d5\u0648\u062a\u0646 \u0648\u06ce\u0695\u0627\u06cc \u0633\u06d5\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0646 \u0628\u06d5 \u0647\u06d5\u0634\u062a. \u0628\u06ce\u0698\u06d5 \u067e\u06ce\u06cc\u0627\u0646: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0686\u0627\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u0642\u0627\u0633\u0646. \u06a9\u06d5\u0645\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u06d5\u0628\u06ce \u0628\u06c6\u06cc \u0646\u06d5\u0686\u0648\u0648\u0646\u060c \u062a\u06c6 \u0644\u06d5\u0648 \u0628\u0627\u0631\u06d5 [\u0628\u0646\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u0628\u06cc\u0633\u06d5 \u0648] \u0632\u06c6\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u0628\u0627\u0633\u06d5\u06a9\u06d5 \u0645\u06d5\u0695\u06c6\u061b \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u0645\u06d5\u067e\u0631\u0633\u06d5.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u0645\u06d5\u06cc\u0698\u06d5: \u0645\u0646 \u0633\u06c6\u0632\u06cc \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0626\u06d5\u06a9\u06d5\u0645\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u062e\u0648\u0627\u062d\u06d5\u0632 \u06a9\u0627\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06ce. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0628\u06cc\u0631\u06cc\u0634\u062a \u0646\u06d5\u0645\u0627\u060c \u06cc\u0627\u062f\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06a9\u06d5 \u0648 \u0628\u06ce\u0698\u06d5: \u0628\u06d5\u0634\u06a9\u0648\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0695\u06ce\u06af\u0627\u06cc\u06d5\u06a9\u06cc \u0646\u0632\u06cc\u06a9\u062a\u0631 \u0644\u06d5\u0645\u06d5\u0645 \u0646\u06cc\u0634\u0627\u0646 \u0628\u0627.',
    '\u0633\u06ce\u0633\u06d5\u062f \u0633\u0627\u06b5\u0627\u0646 \u0644\u06d5 \u0626\u06d5\u0634\u06a9\u06d5\u0648\u062a\u06d5\u06a9\u06d5 \u0645\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0646\u06c6 \u0633\u0627\u06b5\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0632\u06ce\u062f\u06d5 \u06a9\u0631\u062f.',
    '\u0628\u06ce\u0698\u06d5: \u062e\u0648\u0627 \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06d5\u0646\u062f \u0645\u0627\u0646\u06d5\u0648\u06d5\u061b \u0628\u06d5\u0631\u067e\u06cc\u0648\u0627\u0631\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0644\u06d5\u0648\u0695\u0627 \u062f\u06cc\u0627\u0631\u06d5. \u067e\u06d5\u06a9 \u0686 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0628\u06cc\u0646\u0627\u06cc\u06d5! \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u06c6\u0633\u062a \u062f\u06d5\u0633 \u0646\u0627\u06a9\u06d5\u0648\u06ce \u0648 \u06a9\u06d5\u0633 \u0646\u0627\u06a9\u0627 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6\u06cc\u062f\u0627.',
    '\u0626\u06d5\u0648\u06d5\u06cc \u0628\u06c6\u062a \u0647\u0627\u062a\u06af\u06d5 \u062e\u0648\u0627\u0631\u06d5 \u0648 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u060c \u0628\u062e\u0648\u06ce\u0646\u06d5\u0648\u06d5. \u0646\u0627\u0634\u06ce \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0648\u062a\u0627\u0631\u06cc \u062e\u0648\u062f\u0627 \u0628\u06af\u06c6\u0695\u06ce \u0648 \u0644\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce \u062f\u06d5\u0647\u0627\u0646\u0627\u062a \u0646\u0627\u06cc\u06d5.',
    '\u062a\u06c6 \u0647\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5 \u06a9\u06d5 \u0633\u0628\u06d5\u06cc\u0646\u0627\u0646 \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646 \u0628\u06c6 \u062f\u06cc\u062f\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u0632\u0627\u0646 \u0648 \u062a\u06d5\u0646\u06cc\u0627 \u0695\u06d5\u062f\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0627\u0646 \u062f\u06d5\u0648\u06ce \u0648 \u0686\u0627\u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u06b5\u0645\u06d5\u0628\u0648\u06ce\u0631\u06d5 \u0628\u06c6 \u0698\u06cc\u0646\u06cc \u067e\u0695 \u0632\u06d5\u0631\u0642 \u0648 \u0628\u06d5\u0631\u0642\u06cc \u0626\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0648 \u06af\u0648\u06ce \u0644\u06d5 \u0645\u0633\u062a\u06cc \u06a9\u06d5\u0633\u06ce \u0645\u06d5\u0628\u06d5 \u06a9\u06d5 \u0646\u06d5\u0645\u0627\u0646\u0647\u06ce\u0634\u062a \u062f\u06b5\u06cc \u06cc\u0627\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u06a9\u0627 \u0648 \u0647\u06d5\u0631 \u0644\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u06cc\u06d5 \u0648 \u06a9\u0627\u0631\u06cc \u0644\u06d5 \u0695\u0627\u062f\u06d5 \u062f\u06d5\u0631\u0686\u0648\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u0695\u0627\u06cc\u06d5\u061b \u06a9\u06ce \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce\u062f\u06d5\u06a9\u0627\u060c \u0628\u06cc\u06a9\u0627 \u0648 \u06a9\u06ce\u0634 \u0628\u0695\u0648\u0627 \u0646\u0627\u06a9\u0627 \u0647\u06d5\u0631 \u0646\u06d5\u06cc\u06a9\u0627. \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631\u0627\u0646 \u06a9\u06b5\u06ce\u06a9\u06cc \u0648\u0627\u0645\u0627\u0646 \u0646\u06ce\u06b5 \u062f\u0627\u0648\u06d5\u060c \u06a9\u06d5 \u0628\u06b5\u06ce\u0633\u06d5\u06cc \u0647\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u06a9\u06c6\u0646 \u062f\u06d5\u0648\u0631\u06d5\u06cc \u06af\u0631\u062a\u0648\u0648\u0646\u061b \u062a\u0627 \u0647\u0627\u0648\u0627\u0631\u06cc \u0686\u0627\u0631\u06d5 \u062f\u06d5\u06a9\u06d5\u0646 \u0626\u0627\u0648\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0641\u0631\u06cc\u0627 \u062f\u06d5\u062e\u06d5\u0646 \u0648\u06d5\u06a9 \u0645\u0633\u06cc \u062a\u0627\u0648\u0627\u0648\u06d5 \u0648\u0627\u06cc\u06d5\u061b \u062f\u06d5\u0645 \u0648 \u0686\u0627\u0648\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0631\u0698\u06ce\u0646\u06ce \u0648 \u0648\u06d5\u062e\u0648\u0627\u0631\u062f\u0646\u06ce\u06a9\u06cc \u06a9\u0631\u06ce\u062a\u06d5 \u0648 \u0698\u06cc\u06af\u06d5\u06cc\u06ce \u0632\u06c6\u0631 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c [\u0628\u0627 \u0628\u0632\u0627\u0646\u0646] \u0626\u06ce\u0645\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u0648\u0646\u062f\u0627 \u0646\u0627\u06a9\u06d5\u06cc\u0646.',
    '\u0628\u06c6 \u0646\u0634\u06cc\u0645\u06d5\u0646 \u062f\u06d5\u06cc\u0627\u0646\u0628\u06d5\u06cc\u0646\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627. \u067e\u0627\u0631\u0686\u06d5\u06cc \u0633\u06d5\u0648\u0632\u06cc \u062a\u06d5\u0646\u06a9 \u0648 \u0633\u062a\u0648\u0648\u0631\u06cc \u0626\u0627\u0648\u0631\u06cc\u0634\u0645 \u062f\u06d5\u0628\u06d5\u0631 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0628\u06d5 \u0628\u0627\u0632\u0646\u06d5\u06cc \u0632\u06ce\u0695 \u062f\u06d5\u062e\u06d5\u0645\u06b5\u06ce\u0646 \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a\u0627\u0646 \u067e\u0627\u06b5 \u0626\u06d5\u06a9\u06d5\u0641\u0646\u061b \u0626\u0627\u06cc \u0686\u06d5\u0646\u062f \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u0628\u0627\u0634\u06d5 \u0648 \u0634\u0648\u06ce\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0686\u06d5\u0646\u062f \u0644\u06d5\u0628\u0627\u0631\u06d5!',
    '\u062a\u06c6 \u0646\u06d5\u0632\u06cc\u0644\u06d5\u06cc \u062f\u0648\u0648 \u0645\u0631\u06c6\u06a4\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u06af\u06ce\u0695\u06d5: \u062f\u0648\u0648 \u0695\u06d5\u0632\u0645\u0627\u0646 \u0628\u06d5 \u06cc\u06d5\u06a9\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u0627 \u0648 \u0628\u06d5 \u062f\u0627\u0631\u062e\u0648\u0631\u0645\u0627 \u062f\u06d5\u0648\u0631\u06d5\u0646 \u06af\u0631\u062a\u0646 \u0648 \u0646\u0627\u0648\u06cc\u0634\u06cc\u0627\u0646\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0645\u06d5\u0632\u0631\u0627.',
    '\u0626\u06d5\u0648 \u062f\u0648\u0648 \u0695\u06d5\u0632\u06d5 \u0628\u06d5\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06ce \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc \u062f\u06d5\u062f\u0627 \u0648 \u0644\u06d5 \u0646\u0627\u0648 \u0647\u06d5\u0631\u06a9\u0627\u0646 \u0634\u06d5\u0642\u06d5 \u062c\u06c6\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0695\u0627\u06a9\u06ce\u0634\u0627.',
    '\u062f\u0627\u0631\u0627\u06cc\u06cc \u062f\u06cc\u06a9\u06d5\u0634\u06cc \u0632\u06c6\u0631 \u0628\u0648\u0648\u061b \u06a9\u0627\u062a\u06ce \u062f\u06d5\u06af\u06d5\u06b5 \u0647\u06d5\u0648\u0627\u06b5\u06d5\u06a9\u06d5\u06cc\u062f\u0627 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u06af\u0641\u062a\u0648\u06af\u06c6\u06cc \u0628\u0648\u0648\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0646 \u0644\u06d5 \u062a\u06c6 \u062f\u0631\u0627\u0648\u0645 \u0632\u06c6\u0631\u062a\u0631\u06d5 \u0648 \u062f\u06d5\u0633\u062a \u0648 \u067e\u06ce\u0648\u06d5\u0646\u062f\u06cc\u0634\u0645 \u067e\u062a\u0631.',
    '\u0628\u06d5 \u0646\u06ce\u0648 \u0628\u0627\u063a\u06d5\u06a9\u06d5\u06cc\u062f\u0627 \u06af\u06d5\u0695\u0627 \u0648 \u0628\u06c6 \u062e\u06c6\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u062e\u06c6 \u06a9\u0631\u062f. \u06af\u0648\u062a\u06cc: \u0647\u06d5\u0631\u06af\u06cc\u0632 \u067e\u06ce\u0645 \u0648\u0627 \u0646\u06cc\u06cc\u06d5 \u0626\u06d5\u0645\u06d5\u0645 \u0644\u06d5\u062f\u06d5\u0633\u062a\u06cc \u062f\u06d5\u0631\u0628\u0686\u06ce.',
    '\u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0645 \u0633\u06d5\u06b5\u0627\u0634 \u0695\u0627 \u0628\u06ce. \u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0628\u0631\u0627\u0634\u0645\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u060c \u062f\u06d5\u0632\u0627\u0646\u0645 \u0644\u06d5\u0645\u06d5 \u0686\u0627\u06a9\u062a\u0631\u0645 \u062f\u06d5\u0633 \u062f\u06d5\u06a9\u06d5\u0648\u06ce.',
    '\u0647\u06d5\u0648\u0627\u06b5\u06d5\u06a9\u06d5\u06cc - \u06a9\u06d5 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u06af\u0641\u062a\u0648\u06af\u06c6 \u0628\u0648\u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u062f\u0627 - \u067e\u06ce\u06af\u0648\u062a: \u0626\u06d5\u0631\u06ce \u0686\u06c6\u0646 \u062a\u06c6 \u062d\u0627\u0634\u0627\u062a \u06a9\u0631\u062f \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u062a\u06c6\u06cc \u0644\u06d5 \u0626\u0627\u062e\u06ce \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f \u0648 \u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u062f\u06b5\u06c6\u067e\u06ce\u06a9 \u0648 \u062f\u0648\u0627\u06cc\u06cc \u06a9\u0631\u062f\u0648\u0648\u062a\u06cc \u0628\u06d5 \u067e\u06cc\u0627\u0648\u06ce\u061f',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0645\u0646 \u0647\u06d5\u0631 [\u062f\u06d5\u0628\u06ce\u0698\u0645] \u06a9\u06d5 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u06d5 \u0648 \u06a9\u06d5\u0633 \u0646\u0627\u06a9\u06d5\u0645 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0686\u0648\u0648\u06cc\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u06d5\u06a9\u06d5\u062a\u060c \u0628\u06c6\u0686\u06cc \u0646\u06d5\u062a\u06af\u0648\u062a: \u0626\u06d5\u0645\u06d5 \u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u062a\u0648\u0627\u0646\u0627\u06cc\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0633\u062a\u06d5 \u062a\u06c6 \u062f\u06d5\u0645\u0628\u06cc\u0646\u06cc \u06a9\u06d5 \u0628\u06d5 \u0645\u0627\u06b5 \u0648 \u0632\u0627\u0631\u06c6\u06b5\u06d5 \u0644\u06d5 \u062a\u06c6 \u06a9\u06d5\u0645\u062a\u0631\u0645\u060c',
    '\u0646\u0627\u0628\u06ce \u0628\u06d5 \u062f\u0648\u0648\u0631\u06cc \u0628\u0632\u0627\u0646\u06cc\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0644\u06d5 \u0628\u0627\u063a\u06cc \u062a\u06c6\u0645 \u0628\u0627\u0634\u062a\u0631\u06cc\u0634 \u0628\u06c6 \u0628\u0695\u06d5\u062e\u0633\u06ce\u0646\u06ce \u0648 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u06cc\u0634 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u0628\u0627\u063a\u06d5\u06a9\u06d5\u06cc \u062a\u06c6 \u0628\u06ce\u0646\u06ce \u0648 \u0644\u06ce\u062a \u0628\u0628\u06ce\u062a\u06d5 \u062f\u06d5\u0634\u062a\u06ce\u06a9\u06cc \u0628\u06ce\u0631\u0648\u0648\u0646\u06cc \u0642\u0627\u0642\u0695.',
    '\u06cc\u0627\u0646 \u0626\u0627\u0648\u06d5\u06a9\u06d5\u06cc \u0628\u06d5 \u0646\u0627\u062e\u06cc \u0626\u0627\u062e\u062f\u0627 \u0695\u06c6 \u0628\u0686\u06ce \u0648 \u0646\u06d5\u062a\u0648\u0627\u0646\u06cc \u062a\u0627\u0632\u06d5 \u067e\u06d5\u06cc\u062f\u0627\u06cc \u06a9\u06d5\u06cc.',
    '\u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u0631\u0648\u0628\u0648\u0648\u06cc \u0647\u06d5\u06b5\u0648\u06d5\u0631\u06cc \u0648 \u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u06af\u0634\u062a\u06cc \u0628\u06d5\u0633\u06d5\u0631\u06cc\u06d5\u06a9\u0627 \u0647\u0627\u062a\u0646\u061b \u06a9\u06d5 \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0647\u0627\u062a\u06d5 \u0646\u0627\u0648\u06cc\u060c \u0647\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0634\u06a9\u0627\u0646\u062f\u06d5\u0648\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u0645\u0627\u06b5\u06d5\u06cc \u0644\u06ce\u06cc \u0628\u06d5\u062e\u062a \u06a9\u0631\u062f. \u062f\u06d5\u06cc\u06af\u0648\u062a: \u0628\u0631\u06cc\u0627 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce\u06a9\u0645 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0646\u06d5\u0632\u0627\u0646\u06cc\u0628\u0627.',
    '\u0644\u06d5 \u062e\u0648\u0627 \u0628\u06d5\u062f\u06d5\u0631\u060c \u0647\u06cc\u0686 \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u06cc \u0648\u0627 \u0646\u06d5\u0628\u0648\u0648 \u0628\u06ce\u0646\u06d5 \u06cc\u0627\u0631\u06cc \u0648 \u0628\u06d5 \u0628\u06ce \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062f\u06d5\u0631 \u0645\u0627\u06cc\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u0648\u06ce\u0646\u062f\u06d5\u0631\u06ce \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062f\u06d5\u0631 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc \u0647\u06d5\u0642\u06d5\u061b \u067e\u0627\u062f\u0627\u0634\u06cc \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u062f\u0627\u062a \u0648 \u0633\u06d5\u0631\u0646\u062c\u0627\u0645\u06d5\u06a9\u06d5\u0634 \u0628\u0627\u0634\u062a\u0631\u06d5.',
    '\u0646\u06d5\u0632\u06cc\u0644\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0698\u06cc\u0646\u06cc \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0628\u06c6 \u0628\u06ce\u0646\u06d5\u0648\u06d5\u061b \u0648\u06d5\u06a9 \u0626\u0627\u0648\u06ce\u06a9\u06d5 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u062f\u0627\u0645\u0627\u0646 \u0628\u0627\u0631\u0627\u0646\u062f\u060c \u0647\u06d5\u0645\u0648\u0648 \u062a\u06d5\u0631\u0632\u06d5 \u0695\u0648\u0648\u06d5\u06a9\u06ce\u06a9\u06cc \u062a\u06d5\u06a4 \u0644\u0647\u06d5\u06a4\u06cc \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u060c \u0633\u06d5\u0631\u06d5\u0646\u062c\u0627\u0645\u06cc\u0634 \u0628\u0648\u0648 \u0628\u06d5 \u067e\u0648\u0648\u0634 \u0648 \u0628\u0627 \u067e\u0698\u06af\u0627\u0646\u062f\u06cc. \u062e\u0648\u0627 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u0627\u0646\u062f\u0627 \u062a\u0648\u0627\u0646\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0645\u0627\u06b5 \u0648 \u0645\u0646\u0627\u06b5 \u0632\u06d5\u0646\u0628\u06d5\u0631\u06cc \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5 \u0686\u0627\u06a9\u0627\u0646\u06d5\u0634 \u06a9\u06d5\u0648\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5\u060c \u067e\u0627\u062f\u0627\u0634\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u06d5\u0648\u06d5 \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5 \u0648 \u0628\u06ce\u062a\u0631\u06cc\u0634 \u062c\u06ce\u06af\u06d5\u06cc \u0647\u0648\u0645\u06ce\u062f\u0646.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0698\u0627\u0646 \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u0695\u06ce \u0648 \u062a\u06d5\u062e\u062a\u06cc \u0628\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0648 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u06a9\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u060c \u0628\u06ce \u0626\u06d5\u0648\u06d5 \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u0648\u06d5\u0645\u06ce\u0646\u06ce.',
    '\u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5 \u0695\u06cc\u0632 \u062f\u06d5\u062f\u0631\u06ce\u0646\u061b \u0648\u06d5\u06a9 \u06cc\u06d5\u06a9\u06d5\u0645\u062c\u0627\u0631\u0645\u0627\u0646 \u0686\u06ce \u06a9\u0631\u062f\u0646\u060c \u0647\u06d5\u0631 \u0626\u0627\u0648\u0627 \u0647\u0627\u062a\u0648\u0648\u0646\u06d5\u0644\u0627\u0645\u0627\u0646\u060c \u0644\u0627\u0634\u062a\u0627\u0646 \u0648\u0627\u0628\u0648\u0648 \u0644\u06d5 \u0698\u0648\u0627\u0646\u06ce\u06a9\u0648\u0648 \u0648\u06d5\u06a9\u06c6 \u0646\u0627\u06a9\u06d5\u06cc\u0646.',
    '\u062f\u06d5\u0641\u062a\u06d5\u0631\u06d5\u06a9\u06d5 \u062f\u06ce\u062a\u06d5 \u06af\u06c6\u0695\u06ce\u061b \u062f\u06d5\u0628\u06cc\u0646\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06d5\u06a9\u0627\u0646 - \u0644\u06d5\u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u062a\u06cc\u0627\u06cc\u06d5 - \u062a\u0631\u0633\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0695\u06ce \u0646\u06cc\u0634\u062a\u0648\u0648\u06d5\u061b \u062f\u06d5\u06b5\u06ce\u0646: \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u060c \u0626\u06d5\u0645\u06d5 \u0686 \u062a\u06c6\u062f\u06d5\u0641\u062a\u06d5\u0631\u06ce\u06a9\u06d5! \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9 - \u0648\u0631\u062f \u0648 \u062f\u0631\u0634\u062a - \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631 \u062a\u06cc\u0627 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5. \u0626\u06d5\u0648\u0633\u0627 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06cc \u062a\u06cc\u0627 \u062f\u06d5\u0628\u06cc\u0646\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0646\u0627\u06a9\u0627.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u0641\u0631\u06cc\u0634\u062a\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u06af\u0648\u062a: \u0633\u0648\u0698\u062f\u06d5 \u0648\u06d5\u0628\u06d5\u0631 \u0626\u0627\u062f\u06d5\u0645 \u0628\u06d5\u0631\u0646. \u0633\u0648\u0698\u062f\u06d5\u06cc\u0627\u0646 \u0628\u0631\u062f \u0626\u06cc\u0628\u0644\u06cc\u0633 \u0646\u06d5\u0628\u06ce\u061b \u0628\u0646\u06d5\u062c\u0646 \u0628\u0648\u0648\u060c \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc \u06a9\u0631\u062f. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062a\u0648\u062e\u0645\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0648 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc\u060c \u0628\u06ce \u0645\u0646 \u062f\u06d5\u06a9\u06d5\u0646 \u0628\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u062e\u06c6\u060c \u06a9\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u0648\u0698\u0645\u0646\u062a\u0627\u0646\u0646\u061f \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0644\u06d5\u0648 \u06cc\u0627\u0631\u0628\u06d5\u06cc\u0627\u0631\u06d5 \u067e\u06cc\u0633 \u062a\u06ce\u0634\u06a9\u0627\u0648\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0686\u06ce \u06a9\u0631\u062f\u0646\u06cc \u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0646\u06d5\u062f\u0627\u060c \u0647\u06cc\u0686 \u067e\u0631\u0633\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06d5\u0648\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u061b \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a \u0628\u06c6 \u0686\u06ce \u06a9\u0631\u062f\u0646\u06cc \u062e\u0648\u062f\u06cc \u062e\u06c6\u0634\u06cc\u0627\u0646. \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0626\u06d5\u0648\u06cc \u0695\u06ce\u06af\u06d5 \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u062a\u06ce\u06a9 \u062f\u06d5\u062f\u06d5\u0646\u060c \u0646\u06d5\u0645 \u06a9\u0631\u062f\u0648\u0648\u0646 \u0628\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u0645.',
    '\u0695\u06c6\u0698\u06ce \u0626\u06ce\u0698\u06ce: \u062f\u06d5\u0633\u0627 \u0628\u0627\u0646\u06af \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06a9\u06d5\u0646 \u06a9\u06d5 \u0628\u06d5 \u062e\u06d5\u06cc\u0627\u06b5 \u067e\u06ce\u062a\u0627\u0646 \u0648\u0627\u0628\u0648\u0648 \u0634\u06d5\u0631\u06cc\u06a9 \u0645\u0646\u0646\u061b \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u06a9\u06d5\u0646\u062f\u0627\u06b5\u06ce\u06a9\u06cc \u0647\u06d5\u0631\u0627\u0648\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc\u0627\u0646\u060c \u0647\u06d5\u0631 \u062c\u0648\u0627\u0628\u06cc\u0634\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0627\u0646\u06d5\u0648\u06d5.',
    '\u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0626\u0627\u06af\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0686\u0627\u0648\u06cc \u062e\u06c6 \u062f\u06cc\u061b \u0632\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u062a\u06ce\u06cc \u062f\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u0647\u06cc\u0686 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062c\u06c6\u0631\u06d5 \u0646\u06d5\u0632\u06cc\u0644\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0647\u06ce\u0646\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5\u061b \u0645\u0631\u06c6\u0634 \u0644\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u067e\u062a\u0631\u060c \u0648\u0627\u0632\u06cc \u0644\u06d5 \u06af\u06ce\u0631\u06d5 \u06a9\u0631\u062f\u0646\u06d5.',
    '\u0626\u06d5\u0648\u06d5\u06cc \u0646\u06d5\u06cc\u0647\u06ce\u0634\u062a \u06a9\u06d5 \u0626\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5 - \u067e\u0627\u0634 \u0647\u0627\u062a\u0646\u06cc \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646\u062f\u06d5\u0631 - \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646 \u0648 \u062f\u0627\u0648\u0627\u06cc \u0628\u0648\u0648\u0631\u062f\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646\u060c \u0626\u06d5\u0648\u06d5 \u0628\u0648\u0648 \u06a9\u06d5: [\u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0648\u0648\u0646] \u06cc\u0627 \u0628\u06d5 \u062f\u06d5\u0631\u062f\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u06cc\u0627\u0646 \u0628\u0686\u0646\u060c \u06cc\u0627\u0646 \u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u0628\u06d5 \u06af\u0698\u06cc\u0627\u0646\u062f\u0627 \u0628\u06ce.',
    '\u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u062f\u06d5\u0646\u06ce\u0631\u06cc\u0646\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06d5 \u0645\u0698\u062f\u06d5\u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0628\u0646. \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u0686\u06d5\u0642\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u062a\u0627 \u0647\u06d5\u0642\u06cc \u067e\u06ce \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0628\u06d5\u0631\u0646. \u0626\u06d5\u0648\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0645\u06d5 \u0648 \u0626\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5 \u06a9\u06d5\u0648\u0627 \u062a\u0631\u0633\u06cc \u0648\u06d5\u0628\u06d5\u0631 \u0646\u0627\u0648\u0646\u060c \u0647\u06d5\u0631 \u0628\u06d5 \u06af\u0627\u06b5\u062a\u06d5 \u0648 \u06af\u06d5\u067e \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u06a9\u06ce \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u062f\u0631\u0627\u0648 \u06af\u0648\u06ce \u0646\u06d5\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648\u06d5\u0634\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u0631\u062f\u06d5\u0648\u06d5 \u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06cc. \u0626\u06ce\u0645\u06d5 \u067e\u06d5\u0631\u062f\u06d5\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06b5\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06a9\u06ce\u0634\u0627\u0648\u06d5 \u06a9\u06d5 \u062a\u06ce\u0646\u06d5\u06af\u06d5\u0646\u061b \u06af\u0648\u06ce\u0634\u0645\u0627\u0646 \u0647\u06ce\u0646\u062f\u06d5 \u06af\u0631\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u0646 \u06a9\u06d5 \u0646\u06d5\u0628\u06cc\u0633\u0646. \u0686\u06d5\u0646\u062f \u0628\u06a9\u06c6\u0634\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u06cc\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u06c6\u062a \u0646\u0627\u06a9\u06d5\u0648\u0646\u06d5 \u0633\u06d5\u0631 \u0695\u06ce.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u06af\u0648\u0646\u0627\u062d \u0628\u06d5\u062e\u0634 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u06a9\u06ce \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e\u06cc \u06a9\u0631\u062f\u0628\u0627 \u0648 \u0626\u06d5\u0648\u06cc\u0634 \u0628\u06c6 \u0644\u06ce \u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5 \u0628\u06cc\u06af\u0631\u062a\u0628\u0627\u060c \u062f\u06d5\u0628\u0648\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u06af\u0648\u0632 \u0648 \u06af\u0648\u0645\u0645\u06d5\u062a \u0633\u0632\u0627\u06cc \u062f\u0627\u0628\u0627\u0646. \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0633\u062a\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u062f\u06d5\u0645\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u061b \u06a9\u06d5 \u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u0634 \u0647\u0627\u062a\u060c \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u062e\u06c6 \u062e\u06d5\u0644\u0627\u0633 \u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648 \u0634\u0627\u0631\u0627\u0646\u06d5\u0634 \u0647\u06d5\u0631\u06af\u0627 \u0645\u0644\u06cc\u0627\u0646 \u0644\u06d5 \u0632\u0648\u06b5\u0645 \u0646\u0627\u060c \u0642\u0695\u0645\u0627\u0646 \u062a\u06ce\u062e\u0633\u062a\u0646 \u0648 \u0628\u06c6 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0628\u0631\u062f\u0646\u06cc\u0627\u0646\u060c \u0642\u06d5\u06cc\u0631\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0644 \u0628\u06c6 \u062f\u0627\u0646\u0627\u0646.',
    '\u062c\u0627\u0631\u06ce \u0645\u0648\u0648\u0633\u0627\u060c \u0628\u06d5 \u0644\u0627\u0648\u06cc \u0628\u06d5\u0631\u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0645\u0646 \u0644\u06ce \u0628\u0695\u0627\u0648\u0645 \u0633\u06d5\u0641\u06d5\u0631 \u0628\u06a9\u06d5\u0645 \u062a\u0627 \u062f\u06d5\u06af\u06d5\u0645\u06d5 \u062c\u06d5\u0645\u0633\u06d5\u0631\u06cc \u0647\u06d5\u0631 \u062f\u0648\u0648 \u062f\u06d5\u0631\u06cc\u0627\u06a9\u0627\u0646\u061b \u06cc\u0627\u0646 \u0644\u06d5\u0648\u06ce\u0634 \u0648\u06ce\u0648\u06d5\u062a\u0631 \u0628\u0686\u0645.',
    '\u0648\u06d5\u062e\u062a\u06ce \u06af\u06d5\u06cc\u06cc\u0646\u06d5 \u0626\u06d5\u0648 \u062c\u06d5\u0645\u0633\u06d5\u0631\u06d5\u060c \u0645\u0627\u0633\u0627\u0648\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u06a9\u0631\u062f\u061b \u0626\u06d5\u0648\u06cc\u0634 \u0628\u06d5 \u0647\u06d5\u06b5\u0628\u0632\u06c6\u0642\u06cc\u0646\u06ce \u062e\u06c6\u06cc \u06af\u06d5\u06cc\u0627\u0646\u062f\u06d5\u0648\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u06a9\u06d5.',
    '\u06a9\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0632 \u0628\u0648\u0648\u0646 \u0645\u0648\u0648\u0633\u0627 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u06d5\u0633\u062a\u06d5\u06a9\u06d5\u06cc \u06af\u0648\u062a: \u0644\u06d5\u0648 \u0633\u06d5\u0641\u06d5\u0631\u06d5 \u0632\u06c6\u0631 \u0634\u06d5\u06a9\u06d5\u062a \u0628\u0648\u0648\u06cc\u0646\u060c \u06a9\u0627\u0646\u06ce \u062a\u0627\u0634\u062a\u06cc\u0627 \u0645\u06d5\u060c \u0648\u06d5\u0631\u06ce\u0646\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0644\u06d5 \u067e\u06d5\u0646\u0627 \u0628\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u062f\u0627 \u062f\u06d5\u0633\u0627\u06cc\u0646\u06d5\u0648\u06d5\u060c \u062f\u06cc\u062a\u062a \u0686\u0644\u06c6\u0646 \u0645\u0646 \u0645\u0627\u0633\u0627\u0648\u06d5\u06a9\u06d5\u0645 \u0644\u06d5 \u0628\u06cc\u0631 \u0686\u0648\u0648\u061f \u062f\u06cc\u0627\u0631\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0646\u06d5\u06cc\u0647\u06ce\u0634\u062a \u0648\u06d5\u0628\u06cc\u0631\u0645 \u0628\u06ce\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0645\u06cc\u0634 \u0648\u06d5\u06a9 \u0634\u062a\u06cc \u0633\u06d5\u0645\u06d5\u0631\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062f\u06d5\u0631\u06cc\u0627\u06cc \u06af\u0631\u062a\u06d5 \u0628\u06d5\u0631\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06ce\u0645\u06d5\u0634 \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0648\u0627\u0645\u0627\u0646 \u062f\u06d5\u0648\u06cc\u0633\u062a. \u0626\u06cc\u062a\u0631 \u0628\u06d5 \u0634\u06c6\u067e\u06cc \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u0628\u06d5 \u0686\u0627\u0648\u06af\u06ce\u0695\u0627\u0646\u060c \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u062a\u0648\u0648\u0634\u06cc\u0627\u0646 \u0647\u0627\u062a \u0628\u06d5\u062a\u0648\u0648\u0634\u06cc \u0639\u06d5\u0628\u062f\u06ce\u06a9 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u06a9\u06d5 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0646 \u062e\u0633\u062a\u0628\u0648\u0648 \u0632\u0627\u0646\u06cc\u0627\u0631\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u0645\u0627\u0646\u06d5\u0648\u06d5 \u067e\u06ce\u062f\u0627\u0628\u0648\u0648.',
    '\u0645\u0648\u0648\u0633\u0627 \u067e\u06ce\u06cc \u0648\u062a: \u0626\u0627\u062e\u06c6 \u062f\u06d5\u062a\u0648\u0627\u0646\u0645 \u062f\u0648\u0627\u062a \u06a9\u06d5\u0648\u0645 \u0648 \u0644\u06d5\u0648 \u0632\u0627\u0646\u0633\u062a\u06d5\u06cc \u0641\u06ce\u0631 \u06a9\u0631\u0627\u0648\u06cc \u0645\u0646\u06cc\u0634 \u0648\u06d5\u06a9\u0648\u0648 \u062e\u06c6\u062a \u0695\u0627\u0628\u06ce\u0646\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc: \u062a\u06c6 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u062a\u0627\u0646\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u0646\u0627 \u062f\u06d5\u0631\u06cc \u0628\u06d5\u0631\u06cc.',
    '\u0646\u06d5\u0645\u0627\u0632\u06d5 \u06af\u06d5\u0631 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc \u06a9\u0627\u0631\u06cc\u0634 \u0646\u06d5\u0628\u06cc\u060c \u0686\u06c6\u0646 \u062f\u06d5\u0631\u062f\u06d5\u0628\u06d5\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u062d\u06d5\u0632 \u06a9\u0627\u060c \u0628\u06d5 \u062e\u06c6\u0695\u0627\u06af\u0631\u0645 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0648 \u0647\u06cc\u0686 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u06cc\u06d5\u06a9\u062a \u0646\u0627\u06a9\u06d5\u0645.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0634\u0648\u06ce\u0646\u0645 \u06a9\u06d5\u0648\u062a\u06cc\u060c \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u0645 \u0644\u06ce \u0645\u06d5\u067e\u0631\u0633\u06d5 \u0647\u06d5\u062a\u0627 \u0628\u06c6 \u062e\u06c6\u0645 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0628\u0627\u0633\u062a \u0628\u06c6 \u062f\u06d5\u06a9\u06d5\u0645.',
    '\u0633\u0627 \u0695\u06c6\u06cc\u0634\u062a\u0646 \u062a\u0627 \u0647\u06d5\u0631 \u062f\u0648\u0648 \u0633\u0648\u0627\u0631\u06cc \u06af\u06d5\u0645\u06cc\u06d5 \u0628\u0648\u0648\u0646\u061b \u06a9\u0648\u0646\u06cc \u062a\u06ce\u06a9\u0631\u062f. \u06af\u0648\u062a\u06cc: \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u06a9\u0648\u0646\u062a \u06a9\u0631\u062f \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u0626\u0627\u0648\u0627 \u0642\u0648\u0645 \u06a9\u06d5\u06cc\u061f \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0646\u0627\u0634\u06cc\u0631\u06cc\u0646\u062a \u06a9\u0631\u062f.',
    '\u06af\u0648\u062a\u06cc: \u0626\u0627\u062e\u0631 \u0628\u06d5 \u062a\u06c6\u0645 \u0646\u06d5\u06af\u0648\u062a \u062a\u06c6 \u0646\u0627\u062a\u0648\u0627\u0646\u06cc \u0644\u06d5\u06af\u06d5\u06b5 \u0645\u0646\u0627 \u062e\u06c6\u0695\u0627\u0628\u06af\u0631\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc: \u0644\u06d5\u0645\u06d5\u06cc \u0644\u06d5 \u0628\u06cc\u0631\u0645 \u0686\u0648\u0648 \u0644\u06ce\u0645 \u0628\u0628\u0648\u0648\u0631\u06d5 \u0648 \u06af\u06d5\u0644\u06ce\u06a9\u0645 \u0633\u06d5\u062e\u062a \u0644\u06ce \u0645\u06d5\u06af\u0631\u06d5.',
    '\u0648\u06d5\u0695\u06ce \u06a9\u06d5\u0648\u062a\u0646\u060c \u062a\u0648\u0648\u0634\u06cc \u06a9\u0648\u0695\u06cc\u0698\u06af\u06d5\u06cc\u06d5\u06a9 \u0647\u0627\u062a\u0646\u060c \u06a9\u0648\u0634\u062a\u06cc. \u06af\u0648\u062a\u06cc: \u0686\u06c6\u0646 \u06af\u06cc\u0627\u0646\u06cc \u067e\u0627\u06a9\u062a \u0632\u0627\u06cc\u06d5 \u06a9\u0631\u062f \u06a9\u06d5 \u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5\u0633\u06cc \u06a9\u0648\u0634\u062a\u0628\u06ce\u061f \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0646\u0627\u0628\u06d5\u062c\u06ce\u062a \u06a9\u0631\u062f.',
    '\u06af\u0648\u062a\u06cc: \u0646\u06d5\u0645\u06af\u0648\u062a \u0646\u0627\u062a\u0648\u0627\u0646\u06cc \u0644\u06d5\u06af\u06d5\u06b5 \u0645\u0646\u0627 \u0647\u06d5\u06b5\u06a9\u06d5\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u067e\u0627\u0634 \u0626\u06d5\u0645\u062c\u0627\u0631\u06d5 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u0645 \u0644\u06d5 \u062a\u06c6 \u067e\u0631\u0633\u06cc\u060c \u0647\u0627\u0648\u0695\u06ce\u0645 \u0645\u06d5\u0628\u06d5 \u0648 \u0647\u06cc\u0686 \u06af\u0644\u06d5\u06cc\u06cc\u0634\u062a \u0644\u06ce \u0646\u0627\u06a9\u06d5\u0645.',
    '\u062f\u06cc\u0633\u0627\u0646 \u0695\u06c6\u06cc\u06cc\u0646\u060c \u0647\u06d5\u062a\u0627 \u06af\u06d5\u06cc\u06cc\u0646\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u0634\u0627\u0631\u06ce. \u0632\u06d5\u0648\u0627\u062f\u06cc\u0627\u0646 \u0644\u06ce \u062f\u0627\u0648\u0627 \u06a9\u0631\u062f\u0646\u061b \u0695\u0627\u0632\u06cc \u0646\u06d5\u0628\u0648\u0648\u0646 \u0695\u0627\u06cc\u0627\u0646\u06af\u0631\u0646. \u062f\u06cc\u0648\u0627\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06ce\u0646\u062f\u06d5\u0631\u06ce \u0686\u0627\u0648 \u067e\u06ce \u06a9\u06d5\u0648\u062a\u060c \u062e\u06d5\u0631\u06cc\u06a9 \u0628\u0648\u0648 \u0628\u06d5\u0644\u0627\u062f\u0627 \u062f\u06d5\u0647\u0627\u062a\u061b \u0695\u0627\u0633\u062a\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u062a\u0648\u06cc\u0633\u062a\u0628\u0627 \u06a9\u0631\u0627\u06cc \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u062a \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u062a.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u0627 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u062c\u06cc\u0627 \u062f\u06d5\u0628\u06cc\u0646\u06d5\u0648\u06d5\u061b \u0648\u0627 \u0626\u06ce\u0633\u062a\u06d5\u06a9\u06ce \u0646\u0647\u06ce\u0646\u06cc \u0626\u06d5\u0648 \u0695\u0648\u0648\u062f\u0627\u0648\u0627\u0646\u06d5\u062a \u0628\u06c6 \u0628\u0627\u0633 \u062f\u06d5\u06a9\u06d5\u0645\u060c \u06a9\u06d5 \u0646\u06d5\u062a \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u062e\u06c6 \u0628\u06af\u0631\u06cc.',
    '\u06af\u06d5\u0645\u06cc\u06d5\u06a9\u06d5 \u0628\u06c6 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646\u06ce\u06a9 \u0628\u0648\u0648\u060c \u0644\u06d5 \u062f\u06d5\u0631\u06cc\u0627 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u061b \u0648\u06cc\u0633\u062a\u0645 \u0632\u06d5\u062f\u06d5\u062f\u0627\u0631\u06cc \u0628\u06a9\u06d5\u0645\u060c \u06a9\u06d5 \u0644\u06d5\u0645 \u0644\u0627\u06cc\u0627\u0646 \u067e\u0627\u0634\u0627\u06cc\u06d5\u06a9 \u0628\u0648\u0648 \u0647\u06d5\u0645\u0648\u0648 \u06af\u06d5\u0645\u06cc\u06d5\u06a9\u06cc \u0632\u06d5\u0648\u062a \u062f\u06d5\u06a9\u0631\u062f.',
    '\u06a9\u0648\u0695\u06cc\u0698\u06af\u06d5\u06a9\u06d5\u0634 \u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u0628\u0648\u0648\u0646. \u062a\u0631\u0633\u0645 \u0628\u0648\u0648 \u0646\u0627\u0686\u0627\u0631\u06cc\u0627\u0646 \u0628\u06a9\u0627 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u062f\u06d5\u0631\u0686\u0646 \u0648 \u06a9\u0627\u0641\u0631 \u0628\u0628\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0648\u06cc\u0633\u062a\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u06cc\u060c \u0628\u06d5 \u0695\u06c6\u06b5\u06d5\u06cc\u06d5\u06a9\u06cc \u062f\u06b5 \u067e\u0627\u06a9 \u0648 \u0628\u06d5\u0632\u06d5\u062f\u0627\u0631\u062a\u0631 \u0628\u06c6\u06cc\u0627\u0646 \u062a\u06ce\u0647\u06d5\u06b5\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u062f\u06cc\u0648\u0627\u0631\u06d5\u06a9\u06d5\u0634 \u0628\u06c6 \u062f\u0648\u0648 \u06a9\u0648\u0695\u06cc\u0698\u06af\u06d5\u06cc \u0633\u06ce\u0648\u06cc \u0628\u0648\u0648 \u0644\u06d5\u0645 \u0628\u0627\u0698\u06ce\u0695\u06d5\u061b \u0644\u06d5 \u0698\u06ce\u0631\u06cc\u062f\u0627 \u06af\u06d5\u0646\u062c\u06ce \u0647\u06d5\u0628\u0648\u0648 \u0633\u06d5\u0631\u0628\u06d5\u0648\u0627\u0646 \u0628\u0648\u0648. \u0628\u0627\u0648\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0645\u06ce\u0631\u062e\u0627\u0633\u0627\u0646 \u0628\u0648\u0648. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0648\u06cc\u0633\u062a\u06cc \u0626\u06d5\u0645 \u062f\u0648\u0648\u0627\u0646\u06d5 \u067e\u06ce \u0628\u06af\u06d5\u0646 \u0648 \u06af\u06d5\u0646\u062c\u06cc\u0646\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0631\u06ce\u0646\u0646\u061b \u06a9\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06d5\u06a9 \u0628\u0648\u0648 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u06d5\u0648\u06d5 \u0648 \u0645\u0646 \u0647\u06cc\u0686\u0645 \u0644\u06d5 \u062e\u06c6\u0648\u06d5 \u0646\u06d5\u06a9\u0631\u062f. \u0626\u06d5\u0645\u06d5 \u0628\u0648\u0648 \u06a9\u06d5 \u062a\u06c6 \u0646\u06d5\u062a \u062a\u0627\u0646\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06cc \u062a\u0627 \u062f\u06d5\u0632\u0627\u0646\u06cc.',
    '\u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646: \u0626\u06d5\u062f\u06cc \u0632\u06cc\u0644 \u0642\u06d5\u0695\u0646\u06d5\u06cc\u0646 \u0686\u06c6\u0646 \u0628\u0648\u0648\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0648\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u0634\u062a\u06ce\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0645\u06d5\u0648\u06d5.',
    '\u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u062f \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u062f\u06d5\u0633\u062a\u06cc \u0628\u0695\u0648\u0627 \u0648 \u06af\u0634 \u0647\u06c6\u06cc\u06d5\u06a9\u06cc\u0634\u0645\u0627\u0646 \u0628\u06c6 \u0695\u06d5\u062e\u0633\u0627\u0646\u062f.',
    '\u0633\u0627 \u0628\u06d5 \u0634\u0648\u06ce\u0646 \u0645\u06d5\u0628\u06d5\u0633\u062a\u06cc\u062f\u0627 \u0686\u0648\u0648\u060c',
    '\u062a\u0627 \u06af\u06d5\u06cc\u0634\u062a\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u0631\u0646\u0634\u06cc\u0646\u061b \u0644\u0627\u06cc \u0648\u0627\u0628\u0648\u0648 \u0644\u06d5 \u0646\u0627\u0648 \u06a9\u0627\u0646\u06cc\u06cc\u06d5\u06a9\u06cc \u0642\u0648\u0695\u06d5 \u0695\u06d5\u0634\u06d5 \u0626\u0627\u0648\u0627 \u062f\u06d5\u0628\u06ce. \u0644\u06d5\u0648 \u0626\u0627\u0644\u06cc \u062a\u0648\u0648\u0634\u06cc \u0647\u06d5\u0632\u06ce\u06a9 \u0628\u0648\u0648. \u06af\u0648\u062a\u0645\u0627\u0646: \u0632\u06cc\u0644 \u0642\u06d5\u0695\u0646\u06d5\u06cc\u0646! \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u0626\u0627\u0632\u0627\u0631 \u0628\u062f\u06d5\u06cc \u0648 \u0628\u06c6\u0634\u062a \u0647\u06d5\u06cc\u06d5 \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u06af\u06d5\u06b5\u0627 \u0628\u06a9\u06d5\u06cc.',
    '\u0648\u062a\u06cc: \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0646\u0627\u0647\u06d5\u0642\u06cc \u0628\u06a9\u0627 - \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u0627 - \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u06d5\u062f\u06d5\u06cc\u0646 \u0648 \u067e\u0627\u0634\u0627\u0646\u06cc\u0634 \u062f\u06d5\u0628\u0631\u06ce\u062a\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u0648\u06cc\u0634 \u0628\u06d5\u062a\u0648\u0646\u062f\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u06d5\u062f\u0627.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u0686\u0627\u06a9 \u0628\u06d5\u0634\u06cc \u062f\u06d5\u0628\u06ce \u0648 \u06a9\u0627\u0631\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0647\u0627\u0633\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u062f\u06cc\u0633\u0627\u0646\u06cc\u0634 \u0695\u06ce\u06af\u06d5\u06cc \u06af\u0631\u062a\u06d5\u0628\u06d5\u0631.',
    '\u0647\u06d5\u062a\u0627 \u06af\u06d5\u06cc\u0634\u062a\u06d5 \u062e\u06c6\u0631\u0647\u06d5\u06b5\u0627\u062a. \u062f\u06cc\u062a\u06cc \u062a\u06cc\u0634\u06a9\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u06af\u06d5\u0644\u06ce\u06a9\u062f\u0627 \u062f\u06d5\u0628\u0627\u0631\u06ce \u06a9\u06d5 \u062c\u06cc\u0627 \u0644\u06d5\u0648 \u0647\u06cc\u0686 \u0633\u062a\u0627\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0646\u06d5\u062f\u0627\u0646\u06ce.',
    '\u062a\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648 \u0628\u06cc\u0631\u06cc \u0628\u06c6\u06cc \u062f\u06d5\u0686\u0648\u0648\u060c \u062f\u06d5\u0645\u0627\u0646\u0632\u0627\u0646\u06cc.',
    '\u062f\u06cc\u0633\u0627\u0646\u06cc\u0634 \u0695\u06ce\u06af\u06d5\u06cc \u06af\u0631\u062a\u06d5\u0628\u06d5\u0631.',
    '\u062a\u0627 \u06af\u06d5\u06cc\u0634\u062a\u06d5 \u0646\u0627\u0648 \u062f\u0648\u0648 \u0686\u06cc\u0627. \u0644\u06d5 \u067e\u06d5\u0646\u0627 \u062f\u0648\u0648 \u06a9\u06d5\u0698\u06d5\u06a9\u06d5\u062f\u0627 \u0647\u06c6\u0632\u06ce\u06a9\u06cc \u062f\u06cc\u060c \u06a9\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06d5\u0628\u0648\u0648\u0646 \u0644\u06d5 \u0647\u06cc\u0686 \u0632\u0645\u0627\u0646\u06ce\u06a9\u06cc \u062d\u0627\u06b5\u06cc \u0628\u0628\u0646.',
    '\u062f\u0648\u0627\u0646\u062f\u06cc\u0627\u0646\u060c \u06a9\u06d5: \u0626\u06d5\u06cc \u0632\u06cc\u0644 \u0642\u06d5\u0695\u0646\u06d5\u06cc\u0646! \u0639\u0627\u062c\u0648\u062c \u0648 \u0645\u0627\u062c\u0648\u062c \u0644\u06d5\u0645 \u0648\u06b5\u0627\u062a\u06d5\u062f\u0627 \u0628\u06d5\u062f\u0641\u06d5\u0695\u0646\u061b \u0626\u0627\u062e\u06c6 \u0628\u0627\u062c\u06ce\u06a9\u062a \u067e\u06ce \u0628\u062f\u06d5\u06cc\u0646 \u06a9\u06d5 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0631\u0628\u06d5\u0633\u062a\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0686\u06ce \u0628\u06a9\u06d5\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0628\u06c6\u06cc \u0695\u06d5\u062e\u0633\u0627\u0646\u062f\u0648\u0648\u0645\u060c \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5\u061b \u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0647\u06ce\u0632 [\u06cc \u062e\u06c6\u062a\u0627\u0646] \u06cc\u0627\u0631\u06cc\u0645 \u0628\u06a9\u06d5\u0646\u060c \u062a\u0627 \u0644\u06d5 \u0646\u06ce\u0648 \u0626\u06ce\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u0627 \u0628\u06d5\u0631\u0628\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u0648\u06d5\u062f\u06cc \u0628\u06ce\u0646\u0645.',
    '\u067e\u0627\u0698\u06d5\u0626\u0627\u0633\u0646\u0645 \u0628\u06c6 \u0628\u06ce\u0646\u0646. \u0648\u06d5\u062e\u062a\u06ce \u0647\u06d5\u0631 \u062f\u0648\u0648 \u0644\u0627\u0634\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u067e\u0695 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u060c \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u0628\u06cc\u062f\u06d5\u0645\u06ce\u0646\u0646\u061b \u0647\u06d5\u062a\u0627\u0648\u06d5\u06a9\u0648\u0648 \u06a9\u0631\u062f\u06cc\u06d5 \u0626\u0627\u06af\u0631. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u062c\u0627 \u067e\u0627\u0642\u0631\u06cc \u062a\u0648\u0627\u0648\u06d5\u0645 \u0628\u06c6 \u0628\u06ce\u0646\u0646\u060c \u062a\u0627 \u0628\u06d5\u0633\u06d5\u0631\u06cc\u0627 \u062f\u0627\u0628\u0631\u06ce\u0698\u0645.',
    '\u0626\u06cc\u062a\u0631 \u062a\u0627\u0632\u06d5 \u0628\u06c6\u06cc\u0627\u0646 \u0646\u06d5\u0644\u0648\u0627 \u0633\u06d5\u0631\u06cc \u06a9\u06d5\u0648\u0646 \u0648 \u0646\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u06a9\u0648\u0646\u06cc \u06a9\u06d5\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u06d5\u06a9\u06d5 \u0644\u06d5\u0644\u0627\u06cc\u0627\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0695\u0627\u061b \u062c\u0627 \u0647\u06d5\u0631\u06af\u0627 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u06a9\u06d5\u06cc \u0628\u06d5 \u062c\u06ce\u06af\u06d5\u06cc\u0634\u062a\u060c \u06af\u0634\u062a\u06cc \u062f\u06d5\u06a9\u0627\u062a\u06d5 \u06af\u06c6\u0695\u0627\u06cc\u06cc \u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u0647\u06d5\u0631 \u0648\u06d5\u062f\u06cc \u062f\u06ce.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0648\u0627\u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u06cc\u06d5\u06a9\u062a\u0631\u0627 \u067e\u06ce\u0644 \u0628\u062f\u06d5\u0646 \u062f\u06d5\u06a9\u06d5\u06b5\u06d5\u0634\u0627\u062e \u062f\u06d5\u0645\u06ce\u0646\u062f\u0631\u06ce \u0648 \u06af\u0634\u062a\u06cc\u0627\u0646 \u0644\u06ce\u06a9 \u06a9\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u062f\u06c6\u0698\u06d5\u06d5 \u0644\u06d5 \u0628\u06d5\u0631 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0695\u0627\u062f\u06d5\u0646\u06ce\u06cc\u0646\u061b \u0686 \u0695\u0627\u0646\u0627\u0646\u06ce!',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0686\u0627\u0648\u06cc\u0627\u0646 \u0644\u06d5 \u06cc\u0627\u062f\u06cc \u0645\u0646 \u0642\u0648\u0648\u0686\u0627\u0648 \u0628\u0648\u0648 \u0646\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u0628\u0628\u06cc\u0633\u0646.',
    '\u0626\u0627\u062e\u06c6 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u067e\u06ce\u06cc\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u2013 \u0644\u06d5\u0628\u0627\u062a\u06cc \u0645\u0646 - \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u0645 \u0628\u06a9\u06d5\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u0648\u06d5\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 [\u0647\u06cc\u0686 \u0642\u0627\u0632\u0627\u0646\u062c\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06ce]\u061f \u0626\u06ce\u0645\u06d5 \u062f\u06c6\u0698\u06d5\u0647\u0645\u0627\u0646 \u0628\u06c6 \u067e\u06d5\u0632\u06ce\u0631\u0627\u06cc\u06cc \u0644\u06d5\u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5 \u0633\u0627\u0632 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce \u067e\u06ce\u062a\u0627\u0646 \u0628\u06b5\u06ce\u0645\u060c \u06a9\u06ce\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0632\u06cc\u0627\u062a\u0631 \u0632\u06cc\u0627\u0646\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u06af\u0627\u061f',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u06d5\u0642\u0627\u0644\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0698\u06cc\u0627\u0646\u06cc \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u062f\u0627 \u0641\u06ce\u0695\u06c6\u0686\u0648\u0648\u06d5 \u0648 \u0644\u0627\u0634\u06cc\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0686\u0627\u06a9\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06d5\u0633\u06af\u06d5\u0644\u06ce\u06a9\u0646 \u06a9\u06d5 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646 \u0648 \u062f\u06cc\u062f\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0626\u06cc\u062a\u0631 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u06a9\u0631\u062f\u06cc\u0627\u0646 \u067e\u0648\u0648\u0686\u06d5\u06b5 \u0628\u0648\u0648\u06af\u06d5 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 \u0647\u06cc\u0686 \u0633\u06d5\u0646\u06af\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0646\u0627\u0646\u06ce\u06cc\u0646.',
    '\u0628\u06d5\u0647\u06c6\u06cc \u0626\u06d5\u0648 \u062d\u0627\u0634\u0627 \u06a9\u0631\u062f\u0646\u06d5 \u0648 \u06af\u06d5\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u0645 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u06af\u06c6\u0695\u06cc \u06af\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0628\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646\u060c \u062c\u06ce \u067e\u06d5\u0632\u06ce\u0631\u0627\u06cc\u06cc\u0627\u0646 \u0628\u0627\u063a\u0627\u062a\u06cc \u0641\u06cc\u0631\u062f\u06d5\u0648\u0633\u06d5.',
    '\u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646 \u0648 \u0646\u0627\u06cc\u0627\u0646\u06d5\u0648\u06ce \u062c\u06ce \u0628\u06af\u06c6\u0695\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06c6 \u0646\u0648\u0648\u0633\u06cc\u0646\u06cc \u0648\u062a\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u062f\u06d5\u0631\u06cc\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0645\u06d5\u0631\u06d5\u06a9\u06d5\u0641 \u0628\u06ce\u060c \u0628\u06d5\u0631\u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc \u067e\u06ce \u0647\u06ce\u0646\u0627\u0646\u06cc \u0648\u062a\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646\u060c \u0628\u06ce \u0634\u06a9 \u062f\u06d5\u0631\u06cc\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0686\u06c6\u0695\u06ce\u061b \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a \u06af\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06d5\u06cc \u062f\u06cc\u0634\u06cc \u062f\u06d5\u0641\u0631\u06cc\u0627 \u062e\u06d5\u06cc\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0645\u0631\u06c6\u06cc\u06d5\u06a9\u0645 \u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5\u061b \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u0645 \u0628\u06c6 \u062f\u06ce \u06a9\u06d5: \u062e\u0648\u062f\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06d5. \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5\u0646\u06af\u06c6 \u0628\u06c6 \u062f\u06cc\u062f\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u06d5 \u0626\u0627\u0648\u0627\u062a\u06d5\u060c \u0628\u0627 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u0628\u06a9\u0627 \u0648 \u0644\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0646\u06d5\u067e\u06d5\u0631\u0633\u062a\u06ce.',
    '\u06a9\u0627\u0641\u060c \u0647\u0627\u060c \u06cc\u0627\u060c \u0639\u06d5\u06cc\u0646\u060c \u0633\u0627\u062f.',
    '\u0628\u0627\u0633\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u06d5 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0632\u06d5\u06a9\u06d5\u0631\u06cc\u0627\u06cc \u062e\u0648\u06b5\u0627\u0645\u06cc \u062e\u06c6\u06cc.',
    '\u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5 \u06a9\u06d5 \u0647\u0627\u0646\u0627\u06cc \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u0631\u062f \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06ce\u06a9\u06cc \u0646\u0632\u0645 \u0648 \u0646\u06d5\u0648\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0645\u0646 \u0626\u06d5\u0648\u0627 \u067e\u06ce\u0634\u06d5\u0645 \u0633\u06cc\u0633 \u0628\u0648\u0648\u0646 \u0648 \u0633\u06d5\u0631\u0645 \u0644\u06d5\u0628\u06d5\u0631 [\u062f\u06d5\u0631\u062f\u06cc] \u067e\u06cc\u0631\u06cc \u062a\u0631\u0648\u0648\u0633\u06a9\u06d5\u06cc \u062f\u06ce. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u062a\u0627 \u0644\u06d5\u0628\u06d5\u0631\u062a \u067e\u0627\u0695\u0627\u0648\u0645\u06d5\u0648\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06ce\u062a \u0646\u0627\u0647\u0648\u0645\u06ce \u0646\u06d5\u0628\u0648\u0648\u0645.',
    '\u0645\u0646 \u0644\u06d5 \u067e\u0627\u0634 \u062e\u06c6\u0645 \u0644\u06d5 \u062e\u0632\u0645\u06d5\u06a9\u0627\u0646\u0645 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646\u0645\u061b \u06a9\u06d5\u06cc\u0648\u0627\u0646\u0648\u0648\u06d5\u06a9\u06d5\u0634\u0645 \u0646\u06d5\u0632\u06c6\u06a9\u06d5. \u0633\u0627 \u062a\u06c6 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u062e\u06c6\u062a\u06d5\u0648\u06d5 \u0641\u0631\u0632\u06d5\u0646\u062f\u06ce\u06a9\u0645 \u067e\u06ce \u0628\u0628\u06d5\u062e\u0634\u06d5\u061b',
    '\u0628\u0627 \u0645\u06cc\u0631\u0627\u062a\u06cc \u0645\u0646 \u0648 \u062e\u0627\u0646\u06d5\u062f\u0627\u0646\u06cc \u06cc\u0627\u0642\u0648\u0648\u0628 \u0628\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0648\u06ce. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0647\u06ce\u06a4\u06cc\u0645\u06d5 \u062a\u06c6\u0634 \u067e\u06d5\u0633\u0646\u062f\u06cc \u06a9\u06d5\u06cc.',
    '\u0626\u06d5\u06cc \u0632\u06d5\u06a9\u06d5\u0631\u06cc\u0627! \u0626\u06ce\u0645\u06d5 \u0645\u0632\u06af\u06ce\u0646\u06cc \u0645\u0646\u0627\u06b5\u06ce\u06a9 \u0628\u06d5 \u062a\u06c6 \u0626\u06d5\u062f\u06d5\u06cc\u0646\u060c \u0648\u06d5 \u0646\u0627\u0648 \u06cc\u06d5\u062d\u06cc\u0627\u061b \u06a9\u06d5 \u067e\u06ce\u0634\u062a\u0631 \u0644\u06d5\u0645\u060c \u06a9\u06d5\u0633\u0646 \u0646\u06d5\u06a9\u0631\u062f\u06d5 \u0647\u0627\u0648\u0646\u0627\u0648\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u062c\u0627 \u06a9\u0648\u0627 \u0645\u0646 \u0645\u0646\u0627\u06b5\u0645 \u062f\u06d5\u0628\u06ce! \u0644\u06d5\u0628\u06d5\u0631 \u067e\u06cc\u0631\u06cc \u06a9\u06d5\u0644\u06d5\u0644\u0627\u0645 \u0648 \u062e\u06ce\u0632\u0627\u0646\u06d5\u06a9\u06d5\u0634\u0645 \u0646\u06d5\u0632\u06c6\u06a9\u06d5.',
    '\u06af\u0648\u062a\u06cc: [\u0641\u06d5\u0631\u0645\u0627\u0646] \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u062a\u06c6 \u0626\u06ce\u0698\u06ce: \u0626\u06d5\u0645\u06d5 \u0644\u06d5\u0644\u0627\u06cc \u0645\u0646 \u0647\u0627\u0633\u0627\u0646\u06d5\u061b \u0626\u0627\u062e\u0631 \u062a\u06c6\u0634\u0645 \u0644\u06d5 \u0628\u06d5\u0631\u06ce\u062f\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u06d5\u0628\u0648\u0648\u0695\u0627 \u062f\u0631\u0648\u0648\u0633 \u06a9\u0631\u062f.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9\u0645 \u0628\u06c6 \u062f\u0627\u0628\u0646\u06ce. \u06af\u0648\u062a\u06cc: \u0646\u06cc\u0634\u0627\u0646 \u0628\u06d5\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062a\u0627 \u0633\u06ce \u0634\u06d5\u0648\u0627\u0646 - \u0628\u06ce \u0648\u06d5\u06cc \u0647\u06cc\u0686 \u0648\u06d5\u06cc\u06ce\u06a9\u062a \u0647\u06d5\u0628\u06ce - \u0642\u0633\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u06d5\u0631\u062f\u0645 \u0646\u0627\u06a9\u06d5\u06cc.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u0645\u06cc\u062d\u0631\u0627\u0628\u06d5\u06a9\u06d5\u0648\u06d5 \u0695\u06c6\u0648\u06cc\u0634\u062a\u06d5 \u0644\u0627\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0648 \u0628\u06d5 \u0647\u06cc\u0645\u0627\u06cc\u06d5 \u062a\u06ce\u06cc \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646 \u06a9\u06d5 \u0628\u06d5\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646 \u0647\u06d5\u0631 \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u06cc \u06cc\u06d5\u062d\u06cc\u0627! \u06a9\u062a\u06ce\u0628 \u062a\u0648\u0646\u062f \u0628\u06af\u0631\u06d5. \u0647\u06d5\u0631 \u0644\u06d5 \u0632\u0627\u0631\u06c6\u06a9\u0627\u06cc\u06d5\u062a\u06cc\u0634\u062f\u0627 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u0645\u0627\u0646 \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc.',
    '\u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06b5\u0633\u06c6\u0632\u06cc \u0648 \u0628\u06ce \u06af\u06d5\u0631\u062f\u06cc - \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5\u0648\u06d5 - \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u06cc\u0634 \u0628\u0648\u0648.',
    '\u0644\u06d5\u06cc\u06d5\u0644 \u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u06cc\u0634\u06cc\u062f\u0627 \u062e\u06c6\u0634\u0695\u06d5\u0641\u062a\u0627\u0631 \u0628\u0648\u0648\u061b \u0633\u06d5\u0631\u0628\u0632\u06ce\u0648\u06cc \u0648 \u0644\u0627\u0633\u0627\u0631\u06cc \u0646\u06d5\u0628\u0628\u0648\u06d5 \u067e\u06cc\u0634\u06d5\u06cc.',
    '\u0633\u06b5\u0627\u0645\u06cc \u0644\u06ce \u0628\u06ce\u060c \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0644\u06d5 \u062f\u0627\u06cc\u06a9 \u0628\u0648\u0648\u06d5 \u0648 \u0695\u06c6\u0698\u06ce \u062f\u06d5\u0645\u0631\u06ce \u0648 \u0695\u06c6\u0698\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0628\u0627\u0633\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645\u06cc\u0634 \u0628\u06ce\u0646\u06d5 \u06af\u06c6\u0695\u061b \u06a9\u0627\u062a\u06ce \u062f\u0648\u0648\u0631 \u0644\u06d5 \u06a9\u06d5\u0633\u0648\u06a9\u0627\u0631\u06cc\u060c \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u062e\u06c6\u0631\u06d5\u06b5\u0627\u062a\u06cc \u0628\u06c6 \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f.',
    '\u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u062e\u06c6\u06cc \u0648 \u0626\u06d5\u0645\u0627\u0646\u062f\u0627 \u067e\u06d5\u0631\u062f\u06d5\u06cc\u06d5\u06a9\u06cc \u0695\u0627\u0647\u06d5\u0646\u06af\u0627\u0648\u062a. \u0626\u06ce\u0645\u06d5 \u0695\u0648\u0648\u062d\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u061b \u0628\u0646\u06cc\u0627\u062f\u06d5\u0645\u06ce\u06a9\u06cc \u062a\u06d5\u0648\u0627\u0648\u06cc \u0647\u0627\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648.',
    '\u0648\u06ce \u06af\u0648\u062a: \u0626\u06d5\u0645\u0646 \u067e\u06d5\u0646\u0627 \u062f\u06d5\u0628\u06d5\u0645\u06d5 \u0628\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062a\u06c6 [\u0633\u0627] \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u06cc [\u0644\u06ce\u0645 \u0645\u06d5\u06cc\u06d5 \u067e\u06ce\u0634].',
    '\u0626\u06d5\u0648 \u06af\u0648\u062a: \u0626\u06d5\u0645\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0648\u06d5 \u0695\u0627\u0633\u067e\u06ce\u0631\u0627\u0648\u0645 \u0641\u0631\u0632\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u067e\u0627\u06a9 \u0648 \u062e\u0627\u0648\u06ce\u0646\u062a \u067e\u06ce \u0628\u0628\u06d5\u062e\u0634\u0645.',
    '\u0648\u06ce \u06af\u0648\u062a: \u0686\u06c6\u0646 \u0645\u0646\u0627\u06b5\u0645 \u062f\u06d5\u0628\u06ce\u060c \u06a9\u06d5 \u0647\u06cc\u0686 \u0645\u0631\u06c6\u06cc\u06d5\u06a9 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u0645\u0646\u06cc \u0628\u06d5 \u062f\u06d5\u0633\u062a \u0646\u06d5\u06af\u0631\u062a\u0648\u0648\u06d5 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062f\u0627\u0648\u06ce\u0646 \u062a\u06d5\u0695\u06cc\u0634 \u0646\u06d5\u0628\u0648\u0648\u0645.',
    '\u06af\u0648\u062a\u06cc: \u0641\u06d5\u0631\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0648\u062a\u0648\u0648\u06cc\u06d5. \u0626\u06d5\u0648\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u0645\u0646 \u0647\u0627\u0633\u0627\u0646\u06d5 \u0648 \u062f\u06d5\u0645\u0627\u0646\u06d5\u0648\u06ce \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9 \u0628\u06ce \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9 \u0628\u06ce \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645. \u0626\u06d5\u0645 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5\u0634 \u0647\u06d5\u0631 \u062f\u06ce\u062a\u06d5 \u062c\u06ce.',
    '\u062f\u06d5\u0646\u0627\u0648 \u062a\u06c6\u0644\u06cc \u0648\u06ce\u062f\u0627 \u06af\u0648\u0648\u0631\u0627 \u0648 \u0628\u06d5\u0648 \u0632\u06af\u06d5\u0648\u06d5 \u062e\u06c6\u06cc \u06af\u06d5\u06cc\u0627\u0646\u062f\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u062f\u0648\u0648\u0631.',
    '\u0698\u0627\u0646\u06cc \u0632\u0627\u0646\u06cc\u060c \u0648\u06ce \u0628\u0631\u062f\u06d5 \u0644\u0627\u06cc \u062f\u0627\u0631 \u062e\u0648\u0631\u0645\u0627\u06cc\u06ce. \u06af\u0648\u062a\u06cc: \u062e\u06c6\u0632\u06cc\u0627 \u0647\u06d5\u0631 \u0628\u06d5\u0631 \u0644\u06d5 \u0626\u06ce\u0633\u062a\u0627 \u062f\u06d5\u0645\u0631\u062f\u0645 \u0648 \u06cc\u06d5\u06a9 \u062c\u06ce \u0644\u06d5 \u0628\u06cc\u0631 \u062f\u06d5\u0686\u0648\u0648\u0645\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u062e\u0648\u0627\u0631\u0648\u0648\u06cc \u0648\u06ce\u0695\u0627 \u0628\u0627\u0646\u06af\u06cc \u06a9\u0631\u062f: \u062e\u06d5\u0645\u062a \u0646\u06d5\u0628\u06ce\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5 \u0698\u06ce\u0631 \u062a\u06c6\u0695\u0627 \u062c\u06c6\u06af\u06d5 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627.',
    '\u06a9\u06c6\u062a\u06d5\u0631\u06d5\u06cc \u0626\u06d5\u0648 \u062f\u0627\u0631\u062e\u0648\u0631\u0645\u0627\u06cc\u06d5\u0634 \u0628\u0647\u06d5\u0698\u06ce\u0646\u06d5\u060c \u062e\u0648\u0631\u0645\u0627\u06cc \u062a\u0627\u0632\u06d5 \u067e\u06ce \u06af\u06d5\u06cc\u0634\u062a\u0648\u0648\u062a \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u0627\u062f\u06d5\u0648\u06d5\u0631\u06ce\u0646\u06ce.',
    '\u0628\u06d5 \u0686\u0627\u0648\u06cc \u0695\u0648\u0648\u0646 \u0628\u062e\u06c6 \u0648 \u0648\u06d5\u062e\u06c6\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634\u062a \u0644\u06d5 \u0645\u0631\u06c6 \u0686\u0627\u0648 \u067e\u06ce \u06a9\u06d5\u0648\u062a \u0628\u06ce\u0698\u06d5 \u067e\u06ce\u06cc: \u0644\u06d5 \u062e\u06c6\u0645 \u06af\u0631\u062a\u0648\u0648\u06d5 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0626\u0627\u062e\u0627\u0641\u062a\u0646 \u0628\u062a\u06d5\u06a9\u0645\u06d5\u0648\u06d5 \u0648 \u0626\u06cc\u062a\u0631 \u0626\u06d5\u0645\u0695\u06c6 \u0647\u06cc\u0686 \u0645\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u0646\u06d5\u062f\u0648\u06ce\u0646\u0645.',
    '\u0647\u0627\u062a\u06d5\u0648\u06d5 \u0646\u0627\u0648 \u06a9\u06d5\u0633\u0648\u06a9\u0627\u0631\u06cc \u0648 \u0645\u0646\u0627\u06b5\u06ce\u06a9 \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0634\u06d5\u0648\u06d5. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0645\u0631\u06cc\u06d5\u0645! \u0642\u06d5\u062a \u062f\u06d5\u062a\u06c6\u0645\u0627\u0646 \u0648\u0627 \u0695\u0627\u0646\u06d5\u062f\u06cc\u062a \u0626\u06d5\u0645 \u0633\u06d5\u0645\u06d5\u0631\u06d5\u062a \u0644\u06ce \u0695\u0648\u0648 \u0628\u062f\u0627.',
    '\u062e\u0648\u0634\u06a9\u06cc \u0647\u0627\u0695\u0648\u0648\u0646! \u062a\u06c6 \u0646\u06d5 \u0628\u0627\u0648\u06a9\u062a \u067e\u06cc\u0627\u0648 \u062e\u0631\u0627 \u0628\u0648\u0648\u060c \u0646\u06d5 \u062f\u0627\u06cc\u06a9\u06cc\u0634\u062a \u062f\u0627\u0648\u06ce\u0646 \u062a\u06d5\u0695 \u0628\u0648\u0648.',
    '\u0647\u06cc\u0645\u0627\u06cc \u0628\u06c6 \u0645\u0646\u0627\u06b5\u06d5\u06a9\u06d5 \u06a9\u0631\u062f. \u0648\u062a\u06cc\u0627\u0646: \u062c\u0627 \u0686\u06c6\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0633\u0627\u0648\u0627\u06cc \u0633\u06d5\u0631 \u0628\u06ce\u0634\u06a9\u06d5\u062f\u0627 \u0628\u0626\u0627\u062e\u06ce\u0648\u06cc\u0646.',
    '[\u0645\u0646\u062f\u0627\u06b5\u06d5\u06a9\u06d5] \u0648\u062a\u06cc: \u0645\u0646 \u062e\u0648\u0644\u0627\u0645\u06cc \u062e\u0648\u062f\u0627\u0645\u061b \u06a9\u062a\u06ce\u0628\u06cc \u0628\u06d5 \u062e\u06d5\u06b5\u0627\u062a \u062f\u0627\u0645\u06ce \u0648 \u0645\u0646\u06cc \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631.',
    '\u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce\u0634 \u0628\u0645\u060c \u067e\u06ce \u0648 \u0642\u06d5\u062f\u06d5\u0645\u0645 \u067e\u06cc\u0631\u06c6\u0632 \u062f\u06d5\u06a9\u0627 \u0648 \u0695\u0627\u06cc \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u0645 - \u0647\u06d5\u062a\u0627 \u0632\u06cc\u0646\u062f\u0648\u0648\u0645 - \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0645 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u062f\u06d5\u0645.',
    '\u06af\u0648\u06ce \u0644\u06d5 \u0645\u0633\u062a\u06cc \u062f\u0627\u06cc\u06a9\u06cc\u0634\u0645 \u0628\u0645\u061b \u0646\u06d5\u0634\u06cc\u06a9\u0631\u062f\u0648\u0648\u0645 \u0628\u06d5 \u0645\u0644\u0647\u0648\u0695\u06ce\u06a9\u06cc \u0633\u06d5\u0631\u0628\u0632\u06ce\u0648.',
    '\u0633\u06b5\u0627\u0645\u0645 \u0644\u06ce \u0628\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0644\u06d5 \u062f\u0627\u06cc\u06a9 \u0628\u0648\u0648\u0645 \u0648 \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u062f\u06d5\u0645\u0631\u0645 \u0648 \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce\u0634 \u06a9\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648 \u0695\u0627\u062f\u06d5\u06a9\u0631\u06ce\u0645\u06d5\u0648\u06d5.',
    '\u0642\u0633\u06d5\u06cc \u062f\u0631\u0648\u0633\u062a \u0644\u06d5\u0645\u06d5\u0695 \u0639\u06cc\u0633\u0627\u06cc \u06a9\u0648\u0695\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646 \u062a\u06cc\u0627 \u062f\u0648\u0648\u062f\u06b5\u0646.',
    '\u0644\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0627\u0648\u06d5\u0634\u06ce\u062a\u06d5\u0648\u06d5 \u0632\u0627\u0631\u06c6\u06cc \u0647\u06d5\u0628\u06ce\u061b \u067e\u0627\u06a9\u06cc \u0648 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u0628\u06c6 \u0626\u06d5\u0648\u06d5. \u0647\u06d5\u0631\u06af\u0627 \u0648\u06cc\u0633\u062a\u06cc \u06a9\u0627\u0631\u06ce\u06a9 \u0628\u0628\u06ce\u060c \u0647\u06d5\u0631 \u067e\u06ce\u06cc \u0628\u06ce\u0698\u06ce: \u0628\u0628\u06d5! \u062f\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u0648 \u0626\u06ce\u0648\u06d5\u061b \u0633\u0627 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u0628\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0626\u06d5\u0645\u06d5 \u0695\u0627\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0633\u06d5\u0631\u0695\u0627\u0633\u062a\u06d5.',
    '\u0647\u06ce\u0646\u062f\u06ce \u0644\u06d5\u0648 \u062f\u06d5\u0633\u062a\u06d5\u0648\u0628\u06d5\u0633\u062a\u0627\u0646\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u062f\u0627\u060c \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u06a9\u06ce\u0634\u06d5 \u0648 \u0647\u06d5\u0631\u0627. \u062c\u0627 \u0648\u0627\u0648\u06d5\u06cc\u0644\u0627 \u0628\u06c6 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u060c \u0644\u06d5 \u06a9\u0627\u062a\u06cc \u062f\u06cc\u062a\u0646\u06cc \u0695\u06c6\u0698\u06d5 \u062f\u0698\u0648\u0627\u0631\u06d5\u06a9\u06d5.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u062f\u06ce\u0646\u06d5 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u06af\u06d5\u0644\u06ce\u06a9 \u0686\u0627\u0648\u0632\u06cc\u062a \u0648 \u06af\u0648\u06ce \u0633\u0648\u0648\u06a9\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0695\u06c6 \u0646\u0627\u0647\u06d5\u0642\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a \u0644\u0627\u06cc\u0627\u0646\u062f\u0627\u0648\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06cc \u062f\u0627\u062e \u0648 \u06a9\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u0628\u062a\u0631\u0633\u06ce\u0646\u06d5\u061b \u06a9\u0627\u062a\u06ce \u06a9\u0627\u0631 \u0644\u06d5 \u06a9\u0627\u0631 \u062f\u06d5\u062a\u0631\u0627\u0632\u06ce\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u06cc\u0648\u06d5 \u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0627\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0648\u0627 \u0628\u06d5\u0633\u06d5\u0631\u06cc\u06d5\u0648\u06d5\u06cc\u06d5 \u0644\u06d5 \u062f\u0648\u0627\u062c\u0627\u0631\u062f\u0627 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0645\u06ce\u0646\u06ce\u062a \u0648 \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u062e\u06c6\u0645\u0627\u0646.',
    '\u0644\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0628\u0627\u0633\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645\u06cc\u0634 \u0628\u06a9\u06d5\u060c \u06a9\u06d5 [\u0644\u06d5 \u06a9\u0627\u0631 \u0648 \u0648\u062a\u0627\u0631\u06cc\u062f\u0627] \u0647\u06d5\u0631 \u0695\u0627\u0633\u062a \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634 \u0628\u0648\u0648.',
    '\u0626\u06d5\u0648 \u0686\u0627\u062e\u06d5 \u0628\u06d5 \u0626\u0627\u067e\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0626\u0627\u067e\u06c6! \u062a\u06c6 \u0628\u06c6 \u0634\u062a\u06ce\u06a9\u06cc \u0648\u0627 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u06cc\u060c \u0646\u06d5 \u062f\u06d5\u0628\u06cc\u0633\u06ce \u0648 \u0646\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u06ce \u0648 \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9 \u0628\u06d5 \u062a\u06c6 \u0646\u0627\u062f\u0627.',
    '\u0626\u0627\u067e\u06c6! \u0626\u06d5\u0648\u06d5\u06cc \u0645\u0646 \u062f\u06d5\u06cc\u0632\u0627\u0646\u0645 \u062a\u06c6 \u0646\u0627\u06cc\u0632\u0627\u0646\u06cc\u061b \u0634\u0648\u06ce\u0646\u0645 \u06a9\u06d5\u0648\u06cc\u060c \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u062a \u062f\u06d5\u06a9\u06d5\u0645.',
    '\u0626\u0627\u067e\u06c6! \u0634\u06d5\u06cc\u062a\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a \u0645\u06d5\u0628\u06d5. \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc \u06a9\u0631\u062f.',
    '\u0626\u0627\u067e\u06c6! \u0645\u0646 \u0644\u06d5\u0648\u06d5 \u062f\u06d5\u062a\u0631\u0633\u0645\u060c \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631 \u0628\u06cc \u0648 \u0628\u0628\u06cc \u0628\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u06ce.',
    '[\u06af\u0648\u062a\u06cc:] \u0626\u0627\u062e\u06c6 \u0626\u06d5\u06cc \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645! \u062a\u06c6 \u0628\u06d5 \u0628\u06ce\u0632\u06cc \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0645\u0646 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u0645\u061f \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0633\u062a \u0644\u06d5\u0645\u06d5 \u0647\u06d5\u06b5\u0646\u06d5\u06af\u0631\u06cc\u060c \u0628\u06d5\u0631\u062f\u06d5\u0628\u0627\u0631\u0627\u0646\u062a \u062f\u06d5\u06a9\u06d5\u0645 \u0648 \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u062f\u0631\u06ce\u0698\u06cc\u0634 \u0644\u06d5 \u0644\u0627\u0645 \u0628\u0695\u06c6.',
    '\u06af\u0648\u062a\u06cc: \u0633\u06b5\u0627\u0645 \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06c6 \u0628\u06ce\u061b \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645 \u062f\u06d5\u062e\u0648\u0627\u0632\u0645 \u0644\u06ce\u062a \u0628\u0628\u0648\u0648\u0631\u06ce \u0648 \u0626\u06d5\u0648 \u062f\u06d5\u06af\u06d5\u06b5\u06d5 \u0645\u0646 \u0628\u06d5\u06a4\u06cc\u0627\u0646\u06d5.',
    '\u0645\u0646 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0644\u06d5\u0628\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06d5\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5\u060c \u062f\u0648\u0648\u0631 \u0626\u06d5\u06af\u0631\u0645 \u0648 \u06af\u0627\u0632\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u062f\u06d5\u06a9\u06d5\u0645\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0644\u06d5 \u06af\u0627\u0632\u06cc \u06a9\u0631\u062f\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u060c \u062a\u0648\u0648\u0634\u06cc \u0642\u06d5\u0631\u06d5\u0628\u06d5\u062e\u062a\u06cc \u0646\u06d5\u0628\u0645.',
    '\u062c\u0627 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u06d5\u0644\u0627 \u0646\u0627 \u0648 \u0648\u0627\u0632\u06cc \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u0647\u06ce\u0646\u0627 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 - \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u060c \u0626\u06cc\u0633\u062d\u0627\u0642 \u0648 \u06cc\u0627\u0642\u0648\u0648\u0628\u0645\u0627\u0646 \u062f\u0627\u06cc\u06d5 \u0648 \u06af\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631.',
    '\u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0634\u0645\u0627\u0646 \u062e\u0633\u062a\u0646 \u0648 \u0648\u0627\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f \u06a9\u06d5 \u0632\u06c6\u0631 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0686\u06ce.',
    '\u0628\u0627\u0633\u06cc \u0645\u0648\u0648\u0633\u0627\u0634 \u0647\u06d5\u0631 \u0644\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u062f\u0627 \u0628\u06a9\u06d5\u061b \u06a9\u06d5 \u0628\u06ce \u0633\u06c6 \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u06d5 \u0648 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u0648\u0648.',
    '\u0644\u06d5 \u0644\u0627\u06cc \u0695\u0627\u0633\u062a\u06cc \u0686\u06cc\u0627\u06cc \u062a\u0648\u0648\u0631\u06cc\u0634 \u06af\u0627\u0632\u06cc\u0645\u0627\u0646 \u06a9\u0631\u062f\u060c \u0644\u06d5 \u062e\u06c6\u0634\u0645\u0627\u0646 \u0646\u0632\u06cc\u06a9 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u06a9\u06d5 \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u062d\u0627\u06b5\u06cc \u06a9\u06d5\u06cc\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u0634\u0645\u0627\u0646\u06d5\u0648\u06d5 \u0647\u0627\u0695\u0648\u0648\u0646\u06cc \u0628\u0631\u0627\u06cc\u0645\u0627\u0646 \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u06cc\u0634 \u0647\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u0648\u0648.',
    '\u0644\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0628\u0627\u0633\u06cc \u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644\u06cc\u0634 \u0628\u06a9\u06d5\u061b \u0626\u06d5\u0648 \u0695\u0627\u0633\u062a \u0628\u0648\u0648 \u0644\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5 \u0628\u0648\u0648.',
    '\u06a9\u06d5\u0633\u0648\u06a9\u0627\u0631\u06cc \u0628\u06c6 \u0646\u0648\u06ce\u0698 \u06a9\u0631\u062f\u0646 \u0648 \u062e\u06ce\u0631\u06a9\u0631\u062f\u0646 \u0647\u06d5\u06b5\u062f\u06d5\u067e\u06ce\u0686\u0627 \u0648 \u062c\u06ce\u06af\u06d5\u06cc \u0695\u06d5\u0632\u0627\u0645\u06d5\u0646\u062f\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062e\u06c6\u06cc \u0628\u0648\u0648.',
    '\u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0644\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u062f\u0627 \u0644\u06d5\u0645\u06d5\u0695 \u0626\u06cc\u062f\u0631\u06cc\u0633\u06cc\u0634 \u0628\u0627\u062e\u06ce\u0648\u06d5\u061b \u06a9\u06d5 \u0626\u06d5\u0648 \u0647\u06d5\u0631\u06d5 \u0695\u0627\u0633\u062a \u0628\u06ce\u0698\u06ce\u06a9\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u0648\u0648\u061b',
    '\u0628\u06d5 \u067e\u0627\u06cc\u06d5\u06cc \u0628\u06d5\u0631\u0632\u06cc\u0634\u0645\u0627\u0646 \u06af\u06d5\u06cc\u0627\u0646\u062f.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u0627 \u06a9\u06d5\u0648\u062a\u0646\u061b \u0644\u06d5 \u0695\u06d5\u06af\u06d5\u0632\u06cc \u0628\u0627\u0628\u06d5\u062f\u06d5\u0645 \u0628\u0648\u0648\u0646 \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u062f\u06d5\u06af\u06d5\u06b5 \u0646\u0648\u0648\u062d\u0627 \u0647\u06d5\u06b5\u0646 \u06af\u0631\u062a\u0646 \u0648 \u0644\u06d5 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644 \u0648 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627\u0646 \u06a9\u0631\u062f\u0646 \u0648 \u0628\u06c6 \u062e\u06c6\u0645\u0627\u0646 \u0647\u06d5\u06b5\u0645\u0627\u0646 \u0628\u0698\u0627\u0631\u062f\u0646. \u0647\u06d5\u0631\u06af\u0627 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u0627\u06cc\u06d5\u0648\u06d5\u060c \u062f\u06d5\u0645\u06d5\u0648\u0695\u0648\u0648 \u062f\u06d5\u06a9\u06d5\u0648\u062a\u0646\u06d5 \u0632\u06d5\u0648\u06cc \u0628\u06c6 \u0633\u0648\u0698\u062f\u06d5 \u0648 \u0628\u06d5 \u06a9\u0648\u06b5 \u062f\u06d5\u06af\u0631\u06cc\u0627\u0646.',
    '\u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u0628\u0648\u0648\u0646\u06d5 \u062c\u06ce\u06af\u0631\u06cc\u0627\u0646\u060c \u0628\u06d5\u0644\u0627\u06cc \u0646\u0648\u06ce\u0698\u062f\u0627 \u0646\u06d5\u0686\u0648\u0648\u0646 \u0648 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631 \u0648 \u062e\u0648\u0633\u0627\u0631 \u062f\u06d5\u0628\u0646\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u06ce \u0648 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u0628\u067e\u0631\u06cc\u0646\u06af\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u06ce\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0686\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0648 \u0647\u06cc\u0686 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0628\u0627\u063a\u0627\u062a\u06ce \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u062e\u06c6\u0634\u06d5 \u0648 \u0644\u06d5 \u0646\u06d5\u062f\u06cc\u062a\u06d5 \u062e\u0648\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0628\u06d5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u062f\u0627\u0648\u06d5 \u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0626\u06d5\u0648 \u0647\u06d5\u0631 \u062f\u06ce\u062a\u06d5 \u062c\u06ce.',
    '\u0686\u06d5\u06b5\u062a\u06d5\u06cc \u0644\u0627\u0628\u06d5\u0644\u0627 \u0646\u0627\u0628\u06cc\u0633\u0646 \u0648 \u0647\u06d5\u0631 \u0633\u06b5\u0627\u0648\u06d5 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646 \u0698\u06d5\u0645\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u0627 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0626\u06d5\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u06cc\u0628\u06d5\u062e\u0634\u06cc\u0646 \u0628\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0645\u0627\u0646.',
    '\u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0646\u06d5\u0628\u06ce \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u062f\u0627\u0646\u0627\u0648\u06d5\u0632\u06cc\u0646\u06d5\u062e\u0648\u0627\u0631\u060c \u0647\u06d5\u0631\u0686\u06cc \u0626\u06ce\u0633\u062a\u0627 \u0644\u06d5 \u0644\u0627\u0645\u0627\u0646\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u062f\u0648\u0627\u0645\u0627\u0646\u06d5\u0648\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0645 \u0646\u0627\u0648\u06d5\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0647\u06cc\u0686 \u0634\u062a\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u0646\u06d5\u0686\u0648\u0648\u06d5.',
    '\u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5. \u0633\u0627 \u062a\u06c6\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u067e\u06d5\u0631\u0633\u062a\u06d5 \u0648 \u062f\u06d5\u0628\u06ce \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646\u06cc \u0626\u06d5\u0648 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06cc. \u0626\u0627\u062e\u06c6 \u0626\u0627\u06af\u0627\u062a \u0644\u06ce\u06cc\u06d5 \u0647\u0627\u0648\u0646\u0627\u0648\u06ce\u06a9\u06cc \u0647\u06d5\u0628\u06ce\u061f',
    '\u0645\u0631\u06c6 \u0626\u06ce\u0698\u06ce: \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0631\u062f\u0645\u060c \u0626\u0627\u062e\u06c6 \u0648\u06d5\u06a9 \u0632\u06cc\u0646\u062f\u0648\u0648 \u0628\u0648\u0648\u0645 \u0628\u06d5\u0631\u06ce\u060c \u0647\u06d5\u0631\u0648\u0627 \u062f\u06d5\u0645 \u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u062f\u06d5\u0631\u06ce\u061f',
    '\u0626\u06d5\u0648 \u0645\u0631\u06c6\u06cc\u06d5 \u0628\u06c6 \u0647\u06cc\u0686 \u0628\u06cc\u0631\u06ce\u06a9 \u0646\u0627\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0633\u06d5\u0631\u06d5\u062a\u0627 \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0645\u0627\u0646 \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f \u0648 \u0647\u06cc\u0686\u06cc\u0634 \u0646\u06d5\u0628\u0648\u0648\u061f',
    '\u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u060c \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u062f\u06d5\u06af\u06d5\u06b5 \u0634\u06d5\u06cc\u0627\u062a\u06cc\u0646 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u06a9\u06c6\u06cc\u0627\u0646 \u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0648\u0631\u0627\u0646\u062f\u06d5\u0648\u0631 \u0644\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0686\u06c6\u06a9 \u062f\u0627\u0628\u062f\u06d5\u0646.',
    '\u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0647\u06d5\u0631\u06af\u0631\u06c6\u06cc\u06d5\u06a9\u060c \u0626\u06d5\u0648\u06cc \u067e\u062a\u0631 \u062f\u0698\u06cc \u062e\u0648\u062f\u0627\u06cc \u0695\u06d5\u062d\u0645\u0627\u0646 \u0628\u0648\u0648\u06af\u0646\u060c \u0626\u06ce\u0645\u06d5 \u062c\u0648\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u062f\u0648\u0627\u062c\u0627\u0631 \u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631 \u062e\u06c6\u0645\u0627\u0646 \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646 \u06a9\u0627\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0686\u0648\u0648\u0646\u06d5 \u062f\u06c6\u0698\u06d5\u06d5 \u0647\u06ce\u0698\u0627\u062a\u0631\u0646.',
    '\u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u062a\u0627\u0646 \u2013 \u0626\u06cc\u0644\u0644\u0627 \u0648 \u0628\u06cc\u0644\u0644\u0627 \u2013 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u0628\u0686\u0646\u06d5 \u062c\u06d5\u062d\u06d5\u0646\u062f\u06d5\u0645\u060c \u0628\u0695\u06cc\u0627\u0631\u06ce\u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u062f\u0627\u0648\u06cc\u06d5\u062a\u06cc \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u062c\u06ce \u0628\u06d5\u062c\u06ce \u0628\u0628\u06ce.',
    '\u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627\u062a\u0631\u0633\u06d5\u06a9\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646. \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u06cc\u0634 \u2013 \u062f\u06d5\u0633\u062a\u06d5\u0648\u0626\u06d5\u0698\u0646\u06c6- \u0644\u06d5 \u0646\u0627\u0648\u06cc\u062f\u0627 \u062f\u06ce\u06b5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062f\u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u062f\u0631\u06ce\u060c \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0626\u06ce\u0698\u0646: \u06a9\u0627\u0645\u06d5\u0644\u0627 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u0634\u062a\u0631\u06d5 \u0648 \u062f\u06cc\u0648\u06d5\u062e\u0627\u0646\u06cc \u0644\u06d5\u0628\u0627\u0631\u062a\u0631\u06d5\u061f',
    '\u0626\u0627\u06cc \u06a9\u06d5 \u0686\u06d5\u0646\u062f \u0628\u06d5\u0631\u06d5\u0646 \u067e\u06ce\u0634 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u0631\u062f\u0648\u0648\u0646\u060c \u06a9\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06d5\u0644 \u067e\u06d5\u0644\u06cc \u0646\u0627\u0648\u0645\u0627\u06b5 \u0695\u06ce\u06a9\u062a\u0631 \u0648 \u0628\u06d5 \u062f\u06cc\u0645\u06d5\u0646 \u0644\u06d5\u0645\u0627\u0646 \u062c\u0648\u0627\u0646\u062a\u0631 \u0628\u0648\u0648\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc\u062f\u0627 \u062a\u0644 \u062f\u06d5\u062f\u0627\u060c \u0628\u0627 \u062e\u0648\u0627 \u067e\u06d5\u062a\u06cc \u0628\u06c6 \u0634\u0644 \u0628\u06a9\u0627 \u0648 \u0648\u06ce\u06b5\u06cc \u0628\u06a9\u0627\u061b \u062a\u0627 \u0626\u06d5\u0648 \u0633\u0627\u062a\u06d5 \u06a9\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u0646 \u0642\u06d5\u06cc\u0631\u0627\u0646\u06ce \u0628\u06c6\u06cc\u0627\u0646 \u062f\u0627\u0646\u0627\u0648\u0646 \u0633\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06cc \u0647\u0627\u062a\u061b \u06cc\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06d5 \u06cc\u0627\u0646 \u0633\u06d5\u06b5\u0627. \u0644\u06d5\u0648\u06d5 \u0628\u06d5\u0648\u0644\u0627\u0648\u06d5 \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646 \u06a9\u06ce \u0645\u0627\u06b5\u06d5\u06a9\u06d5\u06cc \u0646\u0627\u062e\u06c6\u0634\u062a\u0631\u06d5 \u0648 \u06a9\u06ce \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u06cc \u06a9\u06d5\u0645\u062a\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u06a9\u0631\u062f\u06af\u0646 \u067e\u062a\u0631 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u061b \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06c6 \u062e\u06ce\u0631\u06ce \u062f\u06d5\u0645\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u0628\u0627\u0634\u062a\u0631\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u0633\u06d5\u0631\u06d5\u0646\u062c\u0627\u0645\u06cc\u0634\u06cc \u0628\u0627\u0634\u062a\u0631\u06d5.',
    '\u06a9\u0627\u0648\u0631\u0627\u06a9\u06d5\u062a \u062f\u06cc\u060c \u062d\u0627\u0634\u0627\u06cc \u0647\u06d5\u0628\u0648\u0648 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u0645\u0627\u0646 \u0648 \u06af\u0648\u062a\u06cc: \u0644\u06ce\u0645 \u0695\u0648\u0648\u0646\u06d5 \u0645\u0627\u06b5 \u0648 \u0645\u0646\u0627\u06b5\u0645 \u0647\u06d5\u0631 \u062f\u06d5\u062f\u06d5\u0646\u06ce\u061f',
    '\u0626\u0627\u062e\u06c6 \u0646\u0647\u06ce\u0646\u06cc \u062f\u06d5\u0632\u0627\u0646\u06ce \u06cc\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06ce\u06a9\u06cc \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5\u061f',
    '\u0646\u06d5\u062e\u06ce\u0631\u060c \u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u06b5\u06ce \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u06cc\u0646\u0648\u0648\u0633\u06cc\u0646 \u0648 \u0645\u0627\u0648\u06d5\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062f\u0627\u0646\u06cc \u067e\u062a\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0626\u06ce\u0698\u06ce \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u0645\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u062e\u06c6\u06cc \u0628\u06d5 \u062a\u06d5\u0646\u06cc\u0627 \u062f\u06ce\u062a\u06d5 \u0644\u0627\u0645\u0627\u0646.',
    '\u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u0627\u060c \u0626\u06d5\u0648\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u0627\u0646\u06ce\u06a9\u06cc \u062a\u0631\u06cc\u0627\u0646 \u062f\u06d5\u067e\u0631\u0633\u062a\u060c \u062a\u0627 \u0628\u0628\u0646\u06d5 \u0647\u06c6\u06cc \u0633\u06d5\u0631\u0628\u06d5\u0631\u0632\u06cc\u06cc\u0627\u0646.',
    '\u0646\u06d5\u062e\u06ce\u0631\u060c \u062d\u0627\u0634\u0627 \u0644\u06d5 \u067e\u06d5\u0631\u0633\u062a\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u062f\u0698\u06cc\u0634\u06cc\u0627\u0646 \u0695\u0627\u062f\u06d5\u0648\u06d5\u0633\u062a\u0646.',
    '\u062a\u06c6 \u0646\u06d5\u062a\u0632\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0634\u06d5\u06cc\u0627\u062a\u06cc\u0646\u06cc\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u0633\u06d5\u0631 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u062e\u062a\u0648\u0648\u06a9\u06d5 \u0648 \u0647\u0627\u0646\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646\u061f',
    '\u0647\u06cc\u0686 \u067e\u06d5\u0644\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u0645\u06d5\u06a9\u06d5\u060c \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u0698\u0645\u0627\u0631\u062f\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06cc \u062f\u06d5\u0698\u0645\u06ce\u0631\u06cc\u0646.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u067e\u0627\u0646\u06d5\u0648\u06d5 \u062f\u06d5\u0628\u06d5\u06cc\u0646\u06d5 \u062f\u06cc\u062f\u0627\u0631\u06cc \u062e\u0648\u062f\u0627.',
    '\u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06cc\u0634 \u0628\u06c6 \u062f\u06c6\u0632\u06d5\u0642 [\u0648\u06d5\u06a9\u0648\u0648 \u06af\u0627\u06af\u06d5\u0644 \u0628\u06c6 \u0633\u06d5\u0631\u0627\u0648]\u060c \u062f\u06d5\u0645 \u062a\u06cc\u0646\u06af \u0695\u06d5\u067e\u06ce\u0686\u06d5\u06a9 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u062a\u06a9\u0627\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u0644\u06d5 \u062e\u0648\u0627 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0633\u0627\u0646\u062f\u0628\u06ce.',
    '\u0648\u062a\u0648\u0648\u0634\u06cc\u0627\u0646\u06d5: \u062e\u0648\u062f\u0627 \u0628\u0648\u06cc\u062a\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0641\u0631\u0632\u06d5\u0646\u062f.',
    '\u0626\u0627\u06cc \u06a9\u06d5 \u0642\u0633\u06d5\u06cc \u0695\u06d5\u0642\u062a\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0645\u062f\u0627 \u0647\u0627\u062a\u0648\u0648\u06d5!',
    '\u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0644\u06d5\u0648 [\u0642\u0633\u06d5\u06cc\u06d5] \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0647\u06d5\u06b5\u062a\u06d5\u06a9\u06ce\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0634\u06d5\u0642 \u0628\u0627 \u0648 \u06a9\u06ce\u0648\u06d5\u06a9\u0627\u0646 \u0644\u06d5\u0628\u06d5\u0631\u06cc\u06d5\u06a9 \u0628\u0686\u0646\u061b',
    '\u0644\u06d5\u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u06af\u0648\u062a\u06cc\u0627\u0646 \u062e\u0648\u062f\u0627 \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0647\u06d5\u0633.',
    '\u0644\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0627\u0648\u06d5\u0634\u06ce\u062a\u06d5\u0648\u06d5 \u06a9\u06d5 \u0647\u06cc\u0686 \u0641\u0631\u0632\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u0647\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0646 \u2013 \u06af\u0634 \u0644\u06d5 \u06af\u0634\u062a\u06cc \u2013 \u0628\u06d5 \u062e\u0648\u06b5\u0627\u0645\u06d5\u062a\u06cc \u062f\u06ce\u0646\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627.',
    '\u0633\u06d5\u0631 \u062d\u06cc\u0633\u0627\u0628\u06cc \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u0647\u06d5\u0698\u0645\u0627\u0631 \u0698\u0645\u0627\u0631\u062f\u0648\u0648\u0646\u06cc.',
    '\u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631\u06cc\u0627\u0646 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u060c \u062a\u0627\u06a9 \u0648 \u062a\u06d5\u0646\u06cc\u0627\u060c \u062f\u06ce\u0646\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0627\u062a\u06d5 \u062e\u06c6\u0634\u06d5\u0648\u06cc\u0633\u062a.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06c6\u06cc\u06d5 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u0646 \u0628\u06d5 \u0632\u0648\u0627\u0646\u06cc \u062e\u06c6\u062a\u06cc \u0647\u06ce\u0633\u0627\u0646\u06cc \u0695\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u0631\u062f\u060c \u062a\u0627 \u062a\u06c6 \u0628\u06d5\u0648\u06cc \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u0627\u0646 \u062f\u06b5\u062e\u06c6\u0634 \u06a9\u06d5\u06cc \u0648 \u06af\u06d5\u0644\u06cc \u062f\u0648\u0698\u0645\u0646\u06cc \u0633\u06d5\u0631\u0633\u06d5\u062e\u062a\u060c \u0628\u062a\u0631\u0633\u06ce\u0646\u06cc.',
    '\u0686\u06d5\u0646\u062f\u06cc\u0646 \u0628\u06d5\u0631\u06d5\u0646 \u0628\u06d5\u0631\u0644\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0642\u0695 \u062a\u06ce\u062e\u0633\u062a\u0648\u0648\u0646\u061b \u0626\u0627\u062e\u06c6 \u0647\u06d5\u0633\u062a \u0628\u06d5 \u0647\u06cc\u0686\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u060c \u06cc\u0627\u0646 \u0646\u0648\u0648\u0632\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u0628\u06cc\u0633\u06cc\u061f',
    '\u062a\u0627\u060c \u0647\u0627.',
    '\u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u0646 \u0628\u06c6 \u0646\u06d5\u0646\u0627\u0631\u062f\u06cc \u062a\u0648\u0648\u0634\u06cc \u0686\u06d5\u0648\u0633\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06cc.',
    '\u062a\u06d5\u0646\u0647\u0627 \u0628\u06cc\u0631\u0647\u06ce\u0646\u0627\u0646\u06d5\u0648\u06d5\u06cc\u06d5 \u0628\u06c6 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u062a\u0631\u0633\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646\u06cc \u062f\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5 \u0628\u06b5\u0646\u062f\u0627\u0646\u06d5\u0648\u06d5 \u0647\u0627\u062a\u06c6\u062a\u06d5\u062e\u0648\u0627\u0631.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a\u06cc \u062e\u06c6\u06cc \u062f\u0627\u0645\u06d5\u0632\u0631\u0627.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0648 \u0647\u06d5\u0631\u0686\u06cc\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c \u06af\u0634 \u0628\u06c6 \u0626\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0698\u06ce\u0631 \u062e\u0627\u06a9\u06cc\u0634\u062f\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0646\u06af\u06cc\u0634\u062a \u0647\u06d5\u06b5\u0628\u0695\u06cc\u060c \u0626\u06d5\u0648 \u0628\u06d5 \u0695\u0627\u0632 \u0648 \u0647\u06ce\u062f\u06cc\u06a9\u0627\u062a\u0631\u06cc\u0634 \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u062e\u0648\u062f\u0627\u06cc\u06ce\u06a9\u06d5 \u0644\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u06a9\u06d5\u0633 \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646\u06ce \u0646\u0627\u0628\u06ce\u061b \u0646\u0627\u0648\u06d5 \u0647\u06d5\u0631\u06d5 \u067e\u06cc\u0631\u06c6\u0632\u06d5\u06a9\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0633\u06d5\u0631\u06af\u0648\u0631\u0648\u0634\u062a\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627\u062a \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5\u061f',
    '\u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5 \u0626\u0627\u06af\u0631\u06ce\u06a9\u06cc \u062f\u06cc \u0648 \u0628\u06d5 \u0645\u0627\u06b5 \u0648 \u062e\u06ce\u0632\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u06af\u0648\u062a: \u0644\u06ce\u0631\u06d5 \u0645\u06d5\u0628\u0632\u0648\u0648\u0646\u060c \u0645\u0646 \u0626\u0627\u06af\u0631\u06ce\u06a9\u0645 \u0628\u06d5 \u062f\u06cc \u06a9\u0631\u062f\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0644\u06d5\u0648\u06ce \u062e\u06d5\u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u0631\u0645\u061b \u06cc\u0627\u0646 \u06a9\u06d5\u0633\u06ce\u06a9\u0645 - \u0628\u06c6 \u0634\u0627\u0631\u06d5\u0632\u0627 \u2013 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u0648\u06d5\u062f\u06d5\u0633 \u0628\u06a9\u06d5\u0648\u06ce.',
    '\u062f\u06d5\u0645\u06ce \u06af\u06d5\u06cc\u0634\u062a\u06d5 \u0628\u06d5\u0631 \u0626\u06ce\u06af\u0631\u060c \u06af\u0627\u0633\u06cc \u0644\u06ce\u06a9\u0631\u0627: \u0626\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627!',
    '\u0645\u0646\u060c \u0645\u0646\u0645 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0645. \u0634\u06d5\u06a9\u0627\u0644\u06d5\u06a9\u0627\u0646\u062a \u0628\u0627\u0648\u06ce\u0698\u06d5. \u062a\u06c6 \u0648\u0627\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0634\u06cc\u0648\u06cc \u067e\u06cc\u0631\u06c6\u0632\u06cc \u00ab\u062a\u0648\u0627\u00bb \u062f\u0627.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u0645\u0645 \u062a\u06c6\u0645 \u062f\u06d5\u0633 \u0646\u06cc\u0634\u0627\u0646 \u06a9\u0631\u062f\u061b \u0633\u0627 \u06af\u0648\u06ce\u062f\u06ce\u0631\u06cc \u0695\u0627\u0633\u067e\u06ce\u0631\u06cc \u0628\u06d5.',
    '\u0626\u06d5\u0645\u0646 \u062e\u0648\u0627\u0645\u060c \u062c\u06af\u06d5 \u0644\u06d5 \u0645\u0646 \u062e\u0648\u0627\u06cc \u062a\u0631 \u0646\u06cc\u06cc\u06d5\u061b \u0633\u0627 \u062a\u06c6 \u0647\u06d5\u0631 \u0645\u0646 \u0628\u067e\u06d5\u0631\u0633\u062a\u06d5 \u0648 \u0646\u0648\u06ce\u0698\u06ce \u0628\u06a9\u06d5 \u062a\u0627 \u0648\u06d5\u0628\u06cc\u0631 \u062e\u06c6\u062a\u0645 \u062e\u06d5\u06cc\u062a\u06d5\u0648\u06d5.',
    '\u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0647\u0627 \u0644\u06d5 \u0695\u06ce\u062f\u0627\u061b \u0645\u0646 \u062f\u06d5\u0645\u06d5\u0648\u06ce \u06a9\u06d5 \u062c\u0627\u0631\u06ce \u0626\u0627\u0634\u06a9\u0631\u0627 \u0646\u06d5\u0628\u06ce \u0648 \u0628\u06cc\u0634\u06ce\u0631\u0645\u06d5\u0648\u06d5\u060c \u062a\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06d5\u0631\u06d5\u06cc \u06a9\u06c6\u0634\u0634\u06cc \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u06ce.',
    '\u0626\u06d5\u0648\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc \u067e\u06ce\u06cc \u0646\u06cc\u06cc\u06d5 \u0648 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u06cc \u06a9\u06d5\u0648\u062a\u0648\u0648\u06d5\u060c \u0646\u06d5\u06a9\u0627 \u062a\u06c6\u06cc \u0644\u06ce \u0648\u06d5\u067e\u0631\u06cc\u0646\u06af\u06ce\u0646\u06ce \u0648 \u0628\u06d5\u06b5\u0627\u06cc \u0628\u06d5\u0644\u0627\u062a\u06d5\u062a \u062a\u0648\u0648\u0634 \u0628\u0648\u06ce.',
    '\u0626\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627! \u0626\u06d5\u06cc \u0626\u06d5\u0645\u06d5 \u0686\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0695\u0627\u0633\u062a\u062a \u062f\u0627\u06cc\u06d5\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u06d5 \u0639\u0627\u0633\u0627\u06a9\u06d5\u0645\u06d5\u060c \u0648\u06d5\u0628\u06d5\u0631 \u067e\u0634\u062a\u06cc \u062e\u06c6 \u062f\u06d5\u062f\u06d5\u0645 \u0648 \u06af\u06d5\u06b5\u0627\u06cc \u067e\u06ce \u062f\u0627\u062f\u06d5\u0648\u06d5\u0631\u06ce\u0646\u0645 \u0628\u06c6 \u0695\u0627\u0646\u06d5\u06a9\u06d5\u0645\u060c \u0686\u06d5\u0646\u062f \u0626\u06cc\u0634\u06cc \u062f\u06cc\u0634\u06cc \u067e\u06ce\u062f\u06d5\u06a9\u06d5\u0645.',
    '\u06af\u0648\u062a\u06cc: \u0645\u0648\u0648\u0633\u0627 \u0628\u06cc\u0647\u0627\u0648\u06ce\u0698\u06d5!',
    '\u06a9\u06d5 \u0647\u0627\u0648\u06cc\u0634\u062a\u06cc\u060c \u0644\u06d5\u067e\u0695 \u062f\u06cc\u062a\u06cc \u0648\u0627 \u0645\u0627\u0631\u06ce\u06a9\u06d5 \u0648 \u062f\u0634\u06ce\u06b5\u06d5\u0647\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0628\u06cc\u06af\u0631\u06d5 \u0648 \u062a\u0631\u0633\u062a \u0646\u06d5\u0628\u06ce\u061b \u0647\u06d5\u0631 \u0626\u06ce\u0633\u062a\u0627\u06a9\u06d5 \u062f\u06d5\u06cc\u062e\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u062a\u06d5\u0631\u062d\u06cc \u067e\u06ce\u0634\u0648\u0648\u06cc.',
    '\u062f\u06d5\u0633\u062a\u06cc\u0634\u062a \u0628\u062e\u06d5 \u0628\u0627\u062e\u06d5\u06b5\u062a \u2013 \u0628\u06d5 \u0628\u06ce \u0626\u0627\u0632\u0627\u0631 \u2013 \u062f\u06ce\u062a\u06d5 \u062f\u06d5\u0631\u06ce \u0648\u06d5\u06a9 \u0686\u06c6\u0695\u06cc \u0634\u06cc\u0631\u061b [\u0626\u06d5\u0645\u06cc\u0634] \u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9\u06cc \u062a\u0631\u06d5.',
    '\u062a\u0627 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0647\u06d5\u0631\u06d5 \u06af\u0631\u06cc\u0646\u06af\u06cc \u062e\u06c6\u0645\u0627\u0646\u062a \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u06cc\u0646.',
    '\u0695\u06ce\u06a9\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u060c \u06cc\u06d5\u06a9\u062c\u0627\u0631 \u0634\u0648\u0648\u06cc \u0644\u06ce \u0647\u06d5\u06b5\u06a9\u06ce\u0634\u0627\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u067e\u0634\u0648\u0648 \u062f\u0631\u06ce\u0698\u062a\u0631\u0645 \u0628\u06a9\u06d5.',
    '\u06a9\u0627\u0631\u06cc\u0634\u0645 \u0644\u06ce \u0633\u0627\u0646\u0627 \u0628\u06a9\u06d5.',
    '\u06af\u06cc\u0631\u06cc\u06d5\u06a9\u0645 \u0644\u06d5 \u0632\u0645\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c \u0628\u06c6\u0645 \u0695\u06d5\u0647\u0627 \u06a9\u06d5.',
    '\u062a\u0627 \u062f\u06d5\u0626\u0627\u062e\u0627\u0641\u062a\u0646\u0645 \u0628\u06af\u06d5\u0646.',
    '\u0644\u06d5 \u062e\u0632\u0645\u0627\u0646\u0645 \u0626\u0627\u0631\u06cc\u06a9\u0627\u0631\u06ce\u06a9\u0645 \u0628\u06c6 \u062f\u0627\u0646\u06ce.',
    '[\u0645\u06d5\u0628\u06d5\u0633\u062a] \u0647\u0627\u0695\u0648\u0648\u0646\u06cc \u0628\u0631\u0627\u0645\u06d5.',
    '\u067e\u0634\u062a\u06cc \u0645\u0646\u06cc \u067e\u06ce \u067e\u062a\u06d5\u0648 \u06a9\u06d5.',
    '\u0628\u06cc\u0634\u06cc\u06a9\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0644\u06d5 \u06a9\u0627\u0631\u0645.',
    '\u062a\u0627 \u0626\u06ce\u0645\u06d5\u060c \u0632\u06ce\u062f\u06d5 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u062a\u06c6 \u0628\u062f\u06d5\u06cc\u0646.',
    '\u06af\u06d5\u0644\u06ce\u06a9\u06cc\u0634 \u06cc\u0627\u062f\u06cc \u062a\u06c6 \u0628\u06a9\u06d5\u06cc\u0646.',
    '\u062a\u06c6 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06cc\u062a \u0644\u06ce \u06a9\u0631\u062f\u0648\u0648\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0645\u0648\u0648\u0633\u0627! \u0647\u06d5\u0631\u0686\u06cc \u062a\u06c6 \u0648\u06cc\u0633\u062a\u062a \u067e\u06ce\u062a \u062f\u0631\u0627.',
    '\u062c\u0627\u0631\u06ce \u062a\u0631\u06cc\u0634 \u062a\u06c6\u0645\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6 \u062e\u0633\u062a.',
    '\u06a9\u0627\u062a\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u0648\u06cc\u0633\u062a \u0628\u0648\u0648 \u0626\u0627\u0648\u06cc\u062a\u0645\u0627\u0646\u06d5 \u062f\u06b5\u06cc \u062f\u0627\u06a9\u062a\u061b',
    '\u06a9\u06d5: \u0628\u06cc\u062e\u06d5\u0631\u06d5 \u062f\u0627\u0631\u0628\u06d5\u0633\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u06cc\u0647\u0627\u0648\u06ce\u0698\u06d5\u0631\u06d5 \u0646\u0627\u0648 \u0632\u06ce\u0648\u06d5\u061b \u0628\u0627 \u0632\u06ce\u0634 \u0628\u06cc\u062e\u0627\u062a\u06d5 \u06a9\u06d5\u0646\u0627\u0631\u06ce \u0648 \u062f\u0698\u06ce\u06a9\u06cc \u0645\u0646 \u2013 \u06a9\u06d5 \u062f\u0648\u0698\u0645\u0646\u06ce\u06a9\u06cc \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u2013 \u0628\u06cc\u06af\u0631\u06ce\u062a\u06d5\u0648\u06d5. \u06a4\u06cc\u0627\u0646\u06cc \u062e\u06c6\u0645 \u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631 \u062a\u06c6\u060c \u062a\u0627 \u0644\u06d5 \u0698\u06ce\u0631 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06cc \u062e\u06c6\u0645 \u0628\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u06cc.',
    '\u06a9\u0627\u062a\u06ce \u062e\u0648\u0634\u06a9\u062a \u062f\u06d5\u0686\u0648\u0648 \u062f\u06d5\u06cc\u06af\u0648\u062a: \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 \u06a9\u06d5\u0633\u06ce\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u0628\u062f\u06c6\u0632\u0645\u06d5\u0648\u06d5 \u06a9\u06d5 \u0628\u06c6\u062a\u0627\u0646\u06cc \u0628\u06d5 \u062e\u06ce\u0648 \u0628\u06a9\u0627\u061f \u062a\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u0647\u06ce\u0646\u0627\u0645\u0627\u0646\u06cc\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062f\u0627\u06a9\u062a\u060c \u06a9\u06d5 \u0686\u0627\u0648\u06cc \u067e\u06ce\u062a \u0695\u0648\u0648\u0646 \u0628\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u062e\u06d5\u0645\u06ce \u0646\u06d5\u062e\u0648\u0627. \u0626\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627! \u0626\u06d5\u0645\u06d5\u0634 \u062f\u06d5\u0632\u0627\u0646\u06cc \u06cc\u06d5\u06a9\u06cc\u0634 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u062a \u0632\u0627\u06cc\u06d5 \u0628\u0648\u0648\u061b \u0644\u06d5\u0648 \u062e\u06d5\u0645\u06d5\u0634 \u0695\u0632\u06af\u0627\u0631\u0645\u0627\u0646 \u06a9\u0631\u062f\u06cc\u060c \u0686\u06d5\u0646\u062f \u062c\u0627\u0631\u06cc \u062f\u06cc\u0634 \u0626\u06d5\u0632\u0645\u0648\u0648\u0645\u0627\u0646\u06cc. \u0686\u06d5\u0646\u062f \u0633\u0627\u06b5\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u0645\u06d5\u062f\u06cc\u06d5\u0646\u0627 \u0645\u0627\u06cc\u061b \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0628\u0695\u06cc\u0627\u0631\u06cc \u062e\u06c6\u0646 \u0626\u06d5\u0648\u0627 \u0647\u0627\u062a\u06cc.',
    '\u0645\u0646 \u062a\u06c6\u0645 \u06a9\u0631\u062f\u06d5 \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u062e\u06c6\u0645.',
    '\u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06af\u06d5\u0644 \u0645\u0646\u06d5\u0648\u06d5 \u0644\u06d5\u0644 \u0628\u0631\u0627\u06a9\u06d5\u062a\u0627 \u0695\u06ce\u06a9\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0648\u06d5\u0628\u06cc\u0631 \u0647\u06ce\u0646\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0645\u0646 \u0633\u0633\u062a\u06cc \u0645\u06d5\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631\u062f\u0648\u0648\u06a9 \u0647\u06d5\u0695\u0646\u06d5 \u0646\u06a9 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u060c \u0626\u06d5\u0648 \u0644\u06d5 \u0695\u0627\u062f\u06d5\u06cc \u062f\u06d5\u0631\u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0646\u06d5\u0631\u0645 \u0648 \u0646\u06cc\u0627\u0646\u06cc \u0628\u062f\u0648\u06ce\u0646\u0646\u060c \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0628\u06cc\u0631 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u06cc\u0627\u0646 \u0628\u062a\u0631\u0633\u06ce.',
    '\u0648\u062a\u06cc\u0627\u0646: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0645\u06d5! \u062a\u0631\u0633\u0646 \u0647\u06d5\u06cc\u06d5 \u0644\u06ce\u0646 \u062f\u06d5\u0631\u0647\u06d5\u06b5\u0628\u06ce \u0648 \u06cc\u0627\u0646 \u062a\u06d5\u0648\u0627\u0648 \u0644\u06d5 \u0634\u06c6\u0631\u06cc \u062f\u06d5\u0631\u06a9\u0627.',
    '\u06af\u0648\u062a\u06cc: \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u0648\u0648 \u0646\u06d5\u0628\u06ce\u060c \u0645\u0646 \u0644\u06d5\u0644 \u0626\u06ce\u0648\u06d5\u0645. \u0626\u06d5\u0628\u06cc\u06d5\u0645\u060c \u0686\u0627\u0648\u06cc\u0634\u06cc\u0645 \u062a\u06ce\u0648\u06d5\u06cc\u06d5.',
    '\u0633\u0627 \u0628\u0686\u0646\u06d5 \u0644\u0627\u06cc \u0648 \u067e\u06ce\u06cc \u0628\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631\u062a\u06a9 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u06cc\u0646\u061b \u062a\u06c6 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u0645\u0627\u0646 \u0695\u06d5\u06af\u06d5\u06b5 \u0628\u062e\u06d5 \u0648 [\u0686\u06cc\u062a\u0631] \u0645\u06d5\u06cc\u0627\u0646 \u0686\u06d5\u0648\u0633\u06ce\u0646\u06d5\u0648\u06d5. \u0648\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u0634\u0646 \u0644\u06d5 \u0626\u0627\u0644\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0695\u0627 \u0628\u06c6 \u0647\u06ce\u0646\u0627\u0648\u06cc\u061b \u062f\u0631\u0648\u0648\u062f \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0628\u06ce\u060c \u0644\u06d5 \u0631\u0627\u0633\u062a\u06d5\u0695\u06ce\u060c \u0644\u0627\u0646\u06d5\u06a9\u06d5\u0648\u06ce.',
    '\u0628\u06d5 \u0626\u06ce\u0645\u06d5 \u0695\u0627\u06af\u06d5\u06cc\u06d5\u0646\u0631\u0627\u0648\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u0695\u0648\u0627 \u0646\u06d5\u06a9\u0627 \u0648 \u067e\u0634\u062a\u06cc \u062a\u06ce\u06a9\u0627\u060c \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u0631\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0645\u0648\u0648\u0633\u0627! \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u06a9\u06ce\u06cc\u06d5\u061f',
    '\u06af\u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0633\u06a9\u0631\u062f\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u0645\u0648\u0648 \u067e\u06ce\u062f\u0627\u0648\u06cc\u0633\u062a\u06cc \u062f\u0627\u0648\u06d5\u060c \u067e\u0627\u0634\u0627\u0646\u06cc\u0634 \u0695\u06ce\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0628\u06d5\u0631\u06d5\u0648 \u0686\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648 \u0686\u06c6\u0646 \u0628\u0648\u0648\u0646\u061f',
    '\u06af\u0648\u062a\u06cc: \u0644\u06d5 \u062f\u06d5\u0641\u062a\u06d5\u0631\u06ce\u06a9\u062f\u0627\u06cc\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0646\u06d5 \u0647\u06cc\u0686 \u0634\u062a\u06cc \u0644\u06ce \u06af\u0648\u0645 \u062f\u06d5\u0628\u06ce \u0648 \u0646\u06d5 \u0647\u06cc\u0686\u06cc\u0634\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u062f\u06d5\u0686\u06ce.',
    '\u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u0632\u06d5\u0648\u06cc \u0628\u06c6 \u06a9\u0631\u062f\u0646 \u0628\u06d5 \u0695\u0627\u06cc\u06d5\u062e \u0648 \u0695\u06ce\u0628\u0627\u0632\u06af\u06d5\u0644\u06ce\u06a9\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062a\u06ce\u0647\u06d5\u06b5\u06a9\u06ce\u0634\u0627\u0648\u060c \u0626\u0627\u0648\u06ce\u06a9\u06cc\u0634\u06cc \u0644\u06d5 \u062d\u06d5\u0648\u0627\u06cc\u06d5\u0648\u06d5 \u062f\u0627\u0628\u0627\u0631\u0627\u0646\u062f\u060c \u06a9\u06d5 \u0632\u06c6\u0631 \u062c\u0648\u0648\u062a\u06d5\u06cc \u062c\u06c6\u0631\u0627\u0648\u062c\u06c6\u0631\u06cc \u0695\u0648\u0648\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0631\u0647\u06ce\u0646\u0627.',
    '\u0628\u062e\u06c6\u0646 \u0648 \u0648\u06b5\u0633\u0627\u062a\u06d5\u06a9\u06d5\u0634\u062a\u0627\u0646 \u0628\u0686\u06d5\u0631\u06ce\u0646\u0646\u061b \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0628\u06c6 \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u0632\u06d5\u0648\u06cc\u0645\u0627\u0646 \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u0646\u060c \u062f\u06cc\u0633\u0627\u0646 \u062f\u06d5\u062a\u0627\u0646\u062e\u06d5\u06cc\u0646\u06d5 \u0646\u0627\u0648\u06cc \u0648 \u062c\u0627\u0631\u06ce\u06a9\u06cc \u062a\u0631 \u0644\u06d5\u0648\u06ce\u062a\u0627\u0646 \u062f\u06d5\u0631\u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u061b \u0626\u06d5\u0648 \u0647\u06d5\u0631 \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f \u0648 \u06af\u0648\u06ce\u06cc \u067e\u06ce \u0646\u06d5\u062f\u0627.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627! \u0626\u0627\u062e\u06c6 \u062a\u06c6 \u0647\u0627\u062a\u06cc\u06d5 \u0644\u0627\u0645\u0627\u0646\u060c \u062a\u0627 \u0628\u06d5 \u062c\u0627\u062f\u0648\u0648\u062a \u0644\u06d5 \u0639\u06d5\u0631\u0632\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062f\u06d5\u0631\u0645\u0627\u0646\u06a9\u06d5\u06cc\u061f',
    '\u0645\u06d5\u0631\u062c\u06d5 \u0626\u06ce\u0645\u06d5\u0634 \u062c\u0627\u062f\u0648\u0648\u06cc \u0648\u06d5\u06a9 \u0626\u06d5\u0645\u06d5\u062a \u0628\u06c6 \u0628\u06ce\u0646\u06cc\u0646. \u0633\u0627 \u062e\u06c6\u062a \u0628\u06c6 \u0626\u06ce\u0645\u06d5 \u0648 \u0628\u06c6 \u062e\u06c6\u0634\u062a\u0627\u0646 \u062c\u06ce \u0698\u0648\u0627\u0646\u06ce\u06a9\u06cc \u0644\u06d5\u062a\u06d5\u062e\u062a\u0627\u0646\u06ce \u0695\u06d5\u0686\u0627\u0648 \u0628\u06a9\u06d5\u061b \u0646\u06d5 \u0645\u06d5 \u0648 \u0646\u06d5 \u062a\u06c6\u060c \u0647\u06cc\u0686\u0645\u0627\u0646 \u062a\u06d5\u0646\u06d5\u062e\u06cc \u0644\u06ce \u0646\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u062c\u06ce \u0698\u0648\u0627\u0646 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0695\u0627\u0632\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06ce\u060c \u0644\u06d5 \u0648\u06d5\u062e\u062a\u06cc \u0686\u06ce\u0634\u062a\u06d5\u0646\u06af\u0627\u0648\u06ce\u062f\u0627 \u06a9\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u062e\u0695 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 [\u062c\u06a4\u0627\u062a\u06cc] \u062c\u06ce\u0647\u06ce\u0634\u062a \u0648 \u0641\u06ce\u06b5\u06cc \u062e\u06c6\u06cc [\u06af\u0634\u062a] \u06a9\u06c6 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u06af\u06d5\u0695\u0627\u06cc\u06d5\u0648\u06d5.',
    '\u0645\u0648\u0648\u0633\u0627 \u0628\u06d5 \u0641\u06ce\u06b5\u0628\u0627\u0632\u0627\u0646\u06cc \u06af\u0648\u062a: \u0646\u06d5\u0686\u0646 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u062f\u0627\u0648\u06d5 \u062f\u0631\u06c6 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u0646\u061b \u0628\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u06af\u0634\u062a\u0648\u0648 \u062a\u06d5\u0631\u062a \u0648 \u062a\u0648\u0648\u0646\u0627 \u062f\u06d5\u06a9\u0627. \u06a9\u06ce \u0628\u0648\u062e\u062a\u0627\u0646\u06ce \u0628\u06d5 \u062e\u0648\u0627 \u0628\u06a9\u0627\u060c \u062f\u06d5\u0645 \u0644\u06d5 \u067e\u0648\u0648\u0634\u06d5.',
    '\u0644\u06ce\u06cc\u0627\u0646 \u0628\u0648\u0648 \u0628\u06d5 \u0645\u0642\u06c6\u0645\u0642\u06c6 \u0648 \u0645\u0644\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u0631\u062a\u06d5\u06a9\u0631\u062f\u0646 \u0646\u0627.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u0648\u0648 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0628\u06d5 \u062c\u0627\u062f\u0648\u0648\u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06d5\u0631\u062f\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0631\u06a9\u06d5\u0646 \u0648 \u0646\u06d5\u0631\u06cc\u062a\u06d5 \u0647\u06d5\u0631\u06d5 \u0628\u0627\u0634\u06d5\u06a9\u06d5\u0648 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u06d5\u0631\u0646.',
    '\u0641\u06ce\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u06a9\u06c6 \u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u0648 \u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u0695\u06ce\u0632 \u0648\u06d5\u0631\u0646. \u0626\u06d5\u0645\u0695\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0633\u06d5\u0631\u06a9\u06d5\u0648\u06ce\u060c \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627! \u06cc\u0627\u0646 \u0628\u0627\u0648\u06ce\u060c \u06cc\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u0634 \u062a\u06c6 \u062f\u0627\u0648\u06ce\u0698\u06cc\u0646\u061f',
    '\u06af\u0648\u062a\u06cc: \u0646\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0648\u06ce\u0698\u0646. \u0644\u06d5\u067e\u0695 \u0645\u0648\u0648\u0633\u0627 \u2013 \u0644\u06d5 \u062c\u0627\u062f\u0648\u0648\u06cc \u0648\u0627\u0646 \u2013 \u0648\u0627\u06cc \u06af\u0648\u0645\u0627\u0646 \u0628\u0631\u062f \u067e\u06d5\u062a\u06d5\u06a9\u0627\u0646 \u0648 \u0639\u0627\u0633\u0627\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06ce\u0646 \u0648 \u062f\u06d5\u0686\u0646.',
    '\u0645\u0648\u0648\u0633\u0627 \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u062f\u06b5\u06cc \u062e\u06c6\u06cc\u0627 \u06a9\u06d5\u0645\u06ce\u06a9 \u062a\u0631\u0633\u0627.',
    '\u06af\u0648\u062a\u0646: \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u062a \u0646\u06d5\u0628\u06ce\u060c \u062a\u06c6 \u0633\u06d5\u0631\u062a\u0631\u06cc.',
    '\u0626\u06d5\u0648\u06cc \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0695\u0627\u0633\u062a\u062a\u062f\u0627 \u0628\u06cc\u0647\u0627\u0648\u06ce\u0698\u06d5\u060c \u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0645\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u0642\u0648\u06cc\u062a\u06cc \u062f\u06d5\u062f\u0627. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648 \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u060c \u062a\u06d5\u06b5\u06d5\u06a9\u06d5\u06cc \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u0627\u0646\u06d5\u061b \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u0627\u0646 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce \u0628\u0646 \u0633\u06d5\u0631\u0646\u0627\u06a9\u06d5\u0648\u0646.',
    '\u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631 \u0633\u0648\u0698\u062f\u06d5\u06cc\u0627\u0646 \u0628\u0631\u062f \u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0695\u0648\u0627\u0646 \u0628\u06d5\u0648\u06d5 \u0647\u06ce\u0646\u0627 \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u0627\u0695\u0648\u0648\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0627\u0646\u062f.',
    '\u06af\u0648\u062a\u06cc: \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0695\u06ce\u0648 \u0628\u062f\u06d5\u0645\u060c \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627. \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a \u0626\u06d5\u0648 \u0645\u06d5\u0632\u0646\u062a\u0627\u0646\u06d5 \u0648 \u062f\u0631\u0632\u06cc \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u06cc \u062f\u0627\u062f\u0627\u0646. \u0633\u0648\u0648\u0631 \u0628\u0632\u0627\u0646\u0646 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0633\u062a \u0648 \u067e\u0627\u06cc\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0628\u06d5 \u067e\u06ce\u0686\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0628\u0695\u0645 \u0648 \u0628\u06d5 \u06a9\u06c6\u062a\u06d5\u0631\u06d5\u06cc \u062f\u0627\u0631\u062e\u0648\u0631\u0645\u0627\u06cc\u0627\u0646\u0648\u0648 \u062f\u0627\u062f\u06d5\u06a9\u06d5\u0645\u061b \u0633\u0627 \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646 \u0626\u06ce\u0634\u0627\u0646\u062f\u0646\u06cc \u06a9\u06ce \u062a\u0648\u0646\u062f\u062a\u0631 \u0648 \u067e\u0695 \u06a9\u06ce\u0634\u062a\u0631\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06d5\u06cc \u062f\u06cc\u0648\u0645\u0627\u0646\u06d5\u060c \u0633\u0648\u06ce\u0646\u062f \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0645\u06d5\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u06c6 \u062a\u06c6 \u062f\u06d5\u0633\u0645\u0627\u0646 \u067e\u06ce \u0644\u06d5\u0648 \u0647\u06d5\u06b5\u0646\u0627\u06af\u06cc\u0631\u06ce\u061b \u0633\u0627 \u0647\u06d5\u0631\u0686\u06cc\u06cc\u06d5\u06a9\u06cc \u0644\u06d5 \u062f\u06d5\u0633\u062a\u062a \u062f\u06ce \u062f\u0631\u06cc\u063a \u0645\u06d5\u06a9\u06d5\u061b \u062a\u06c6 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u0644\u06d5 \u0698\u06cc\u0646\u06cc \u0626\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u062f\u0627 \u062f\u06d5\u0633\u062a\u062a \u062f\u06d5\u0695\u0648\u0627.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627\u0646 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0647\u06d5\u062a\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u0644\u06d5 \u0647\u06d5\u06b5\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u06ce \u0648 \u0644\u06d5\u0648 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u06cc\u06cc\u06d5\u0634\u0645\u0627\u0646 \u062e\u06c6\u0634 \u0628\u06ce\u061b \u06a9\u06d5 \u062a\u06c6 \u0628\u06d5\u0633\u06d5\u0631\u062a\u0627 \u0633\u06d5\u067e\u0627\u0646\u062f\u06cc\u0646. \u062e\u0648\u0627 \u0686\u0627\u062a\u0631\u06d5 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0634\u0645\u06ce\u0646\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06d5 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06cc \u0628\u0686\u06ce\u062a\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0628\u06d5\u0634\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06d5 \u0648 \u2013 \u0646\u06d5\u0645\u0631\u060c \u0646\u06d5\u0698\u06cc \u2013 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u06ce \u0648 \u0628\u0686\u06ce\u062a\u06d5 \u0644\u0627\u06cc\u060c \u0626\u06d5\u0646\u0648\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0648\u0627\u0646 \u0644\u06d5 \u067e\u0644\u06d5 \u0628\u06d5\u0631\u0632\u06d5\u06a9\u0627\u0646\u0627.',
    '\u0646\u0634\u06cc\u0645\u06d5\u0646\u06cc\u0627\u0646 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06d5 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646. \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u067e\u0627\u06a9\u0627\u0646.',
    '\u0626\u06cc\u062a\u0631 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u060c \u06a9\u06d5 \u0648\u06ce\u0695\u0627\u06cc \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646 \u0628\u06d5 \u0634\u06d5\u0648 \u0628\u0695\u06c6\u060c \u0695\u06ce\u06cc\u06d5\u06a9\u06cc \u0648\u0634\u06a9 \u0648 \u0628\u0631\u06cc\u0646\u06af\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06d5\u0631\u06cc\u0627 \u0628\u06c6 \u0695\u06d5\u0686\u0627\u0648 \u0628\u06a9\u06d5\u061b \u0646\u06d5 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646\u06cc \u06af\u0631\u062a\u0646 \u0628\u06d5\u0648 \u0646\u06d5 \u0628\u062a\u0631\u0633\u06d5.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0628\u06d5 \u062e\u06c6\u0648 \u0644\u06d5 \u0634\u06a9\u0631\u06d5\u0648\u06d5 \u06a9\u06d5\u0648\u062a\u06d5 \u062f\u0648\u0648\u06cc\u0627\u0646. \u067e\u06ce\u0644\u06cc \u062f\u06d5\u0631\u06cc\u0627 \u0647\u0627\u062a\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0648 \u062f\u0627\u06cc \u067e\u06c6\u0634\u06cc\u0646.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u06cc\u0634 \u0695\u06ce\u06cc \u0644\u06d5 \u06af\u06d5\u0644\u06d5\u06a9\u06d5\u06cc \u0647\u06d5\u06b5\u06d5 \u06a9\u0631\u062f \u0648 \u0646\u06d5\u06cc\u06a9\u0631\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u0627\u0646 \u06a9\u0627.',
    '\u0626\u06d5\u06cc \u0648\u06d5\u0686 \u0648 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644! \u0644\u06d5\u0628\u06d5\u0631 \u0686\u06d5\u0646\u06af\u06cc \u062f\u0648\u0698\u0645\u0646\u0648\u0648 \u0695\u0632\u06af\u0627\u0631\u0646 \u06a9\u0631\u062f\u0646\u061b \u0628\u06c6\u0634\u0645\u0627\u0646 \u062f\u0627\u0646\u0627\u0646 \u2013 \u0644\u06d5\u0644\u0627\u06cc \u0695\u0627\u0633\u062a\u06cc \u0686\u06cc\u0627\u06cc \u062a\u0648\u0648\u0631\u06ce \u2013 \u0628\u06af\u06d5\u0646\u06d5 \u0644\u0627\u0645\u061b \u06af\u06d5\u0632\u06c6 \u0648 \u0645\u06d5\u0644\u06cc \u0634\u06ce\u0644\u0627\u0642\u06d5\u0634\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646\u06d5 \u062e\u0648\u0627\u0631.',
    '\u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5 \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0646\u060c \u0628\u062e\u06c6\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u06b5\u06d5\u0695\u06c6\u06cc\u06cc \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0646\u06d5\u0648\u06d5\u06a9\u0627 \u0644\u06ce\u062a\u0627\u0646 \u062a\u0648\u0648\u0695\u06d5 \u0628\u0645. \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062a\u0648\u0648\u0634\u06cc \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc\u0645 \u0628\u06ce\u060c \u0647\u06d5\u06b5\u062f\u06ce\u0631\u0627\u0648\u06d5\u061b',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062a\u06c6\u0628\u06d5\u06a9\u0627\u0631 \u0648 \u0628\u0627\u0648\u06d5\u0631\u062f\u0627\u0631 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06af\u06d5 \u0628\u06ce\u060c \u0645\u0646 \u062f\u06d5\u06cc\u0628\u06d5\u062e\u0634\u0645.',
    '\u0626\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627! \u0626\u06d5\u0648\u06d5 \u0686\u06c6\u0646\u06d5 \u0644\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u062a \u0628\u06d5\u0644\u06d5\u0632\u062a\u0631 \u0628\u0648\u0648\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u067e\u06d5\u0644\u06d5\u0645 \u0628\u0648\u0648 \u0632\u0648\u0648 \u0628\u06af\u06d5\u0645 \u0628\u06d5 \u062a\u06c6\u060c \u0647\u06d5\u062a\u0627\u0648\u06d5\u06a9\u0648\u0648 \u0644\u06ce\u0645 \u0695\u0627\u0632\u06cc \u0628\u06cc\u061b \u0648\u0627 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0647\u0627\u0646 \u0644\u06d5 \u062f\u0648\u0648\u06cc \u0645\u0646.',
    '\u06af\u0648\u062a\u06cc: \u067e\u0627\u0634 \u062a\u06c6 \u0626\u06ce\u0645\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u062a\u0645\u0627\u0646 \u0626\u06d5\u0632\u0645\u0648\u0648. \u0633\u0627\u0645\u0631\u06cc \u0644\u06d5 \u0695\u06ce\u06cc \u062f\u06d5\u0631\u06a9\u0631\u062f\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0645\u0648\u0648\u0633\u0627 \u2013 \u0628\u06d5 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u0648 \u062f\u0627\u062e \u0644\u06d5 \u062f\u06b5\u06cc \u2013 \u0647\u0627\u062a\u06d5\u0648\u06d5 \u0628\u06c6 \u0646\u0627\u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc. \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0626\u0627\u062e\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0628\u0627\u0634\u06cc \u067e\u06ce \u0646\u06d5\u062f\u0627\u0646\u061f \u0628\u06c6\u0686\u06cc \u0645\u0646 \u0632\u06c6\u0631\u0645 \u067e\u06ce \u0686\u0648\u0648\u06d5\u060c \u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0648\u06cc\u0633\u062a\u0648\u0648 \u0628\u06d5\u0631 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0648\u0646\u060c \u0648\u0627 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0645\u0646\u062a\u0627\u0646 \u0634\u06a9\u0627\u0646\u062f\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u062e\u06c6\u0634\u06cc \u062e\u06c6 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u062a\u06c6\u0645\u0627\u0646 \u0646\u06d5\u0634\u06a9\u0627\u0646\u062f. \u0686\u06d5\u0646\u062f \u0628\u0627\u0631 \u062e\u0634\u06b5\u06cc \u06af\u06d5\u0644\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u06a9\u06c6\u06b5 \u0646\u0627\u0628\u0648\u0648\u06cc\u0646\u060c \u0646\u0627\u0686\u0627\u0631 \u0626\u0627\u0648\u06cc\u062a\u0646\u06d5 \u0646\u0627\u0648 \u0628\u06c6\u062a\u06d5\u061b \u0647\u06d5\u0631\u0648\u0627 \u0633\u0627\u0645\u0631\u06cc\u0634 \u062a\u06ce\u06cc \u0647\u0627\u0648\u06cc\u0634\u062a.',
    '\u0626\u06cc\u062a\u0631 \u06a9\u06c6\u062a\u06d5\u06b5\u06cc \u06af\u06c6\u0644\u06a9\u06ce\u06a9\u06cc \u0628\u06c6 \u062f\u06d5\u0631\u062e\u0633\u062a\u0646 \u06a9\u06d5 \u0626\u06d5\u06cc\u0628\u06c6\u0695\u0627\u0646\u062f. \u0626\u06d5\u0648\u0633\u0627 \u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u062e\u0648\u062f\u0627\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5 \u0648 \u062e\u0648\u0627\u06cc \u0645\u0648\u0648\u0633\u0627\u0634\u06d5\u061b [\u062c\u0627 \u0645\u0648\u0648\u0633\u0627 \u0626\u06d5\u0645\u06d5] \u0644\u06d5 \u0628\u06cc\u0631 \u0686\u0648\u0648.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u0627\u0628\u06cc\u0646\u0646\u060c \u0646\u06d5 \u062c\u0648\u0627\u0628\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0646\u06d5 \u0632\u06cc\u0627\u0646\u06ce\u06a9\u06cc \u0628\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c\u06ce\u061f',
    '\u0647\u0627\u0695\u0648\u0648\u0646\u06cc\u0634 \u0628\u06d5\u0631\u06ce \u067e\u06ce\u06cc \u0648\u062a\u0646: \u06af\u06d5\u0644\u06c6! \u0647\u06c6\u0646 \u0628\u06a4\u06cc \u06af\u06c6\u0644\u06a9\u06ce \u062e\u0627\u067e\u06ce\u0646\u0631\u0627\u0648\u0646\u061b \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646 \u062f\u06d5\u0647\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u0695\u06c6\u0632\u06cc\u062a\u0627\u0646\u06d5. \u062f\u06d5\u0648\u06d5\u0631\u0646 \u0648\u06d5\u062f\u0648\u0648\u06cc \u0645\u0646 \u06a9\u06d5\u0648\u0646 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0645\u0646 \u0628\u06d5 \u062c\u06ce \u0628\u06ce\u0646\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0644\u06ce\u06cc \u0648\u06d5\u0644\u0627 \u0646\u0627\u06a9\u06d5\u0648\u06cc\u0646\u060c \u0647\u06d5\u062a\u0627 \u0645\u0648\u0648\u0633\u0627 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u0645\u0627\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0647\u0627\u0695\u0648\u0648\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06cc\u062a\u062a \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u06a9\u06d5\u0648\u062a\u0646\u060c \u0686\u06cc \u0646\u06d5\u06cc\u0647\u06ce\u0634\u062a \u062a\u06c6\u060c',
    '\u0634\u0648\u06ce\u0646\u0645 \u06a9\u06d5\u0648\u06cc\u061f \u0626\u0627\u062e\u06c6 \u062a\u06c6\u0634 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0645\u0646 \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc\u062a \u06a9\u0631\u062f\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u062f\u0627\u06cc\u06a9 \u0628\u0631\u0627\u06a9\u06d5\u0645! \u0695\u06cc\u0634\u0645 \u0628\u06d5\u0631\u062f\u06d5 \u0648 \u0642\u0698\u0645 \u0645\u06d5\u06af\u0631\u06d5. \u0645\u0646 \u0644\u06d5\u0648\u06d5 \u062a\u0631\u0633\u0627\u0645 \u06a9\u06d5 \u0628\u06ce\u0698\u06cc: \u062a\u06c6 \u062c\u0648\u062f\u0627\u06cc\u06cc\u062a \u062e\u0633\u062a\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u0645\u0646 \u0648 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644 \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0642\u0633\u06d5\u0645 \u0695\u0627\u0646\u06d5\u0648\u06d5\u0633\u062a\u0627\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0633\u0627\u0645\u0631\u06cc! \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0686 \u0628\u0648\u0648 \u06a9\u0631\u062f\u062a\u061f',
    '\u06af\u0648\u062a\u06cc: \u0634\u062a\u06ce\u06a9\u0645 \u0628\u06d5\u062f\u06cc \u06a9\u0631\u062f\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06cc \u062f\u06cc \u0646\u06d5\u06cc\u0627\u0646\u062f\u06cc: \u0644\u06d5 \u062f\u06d5\u0648\u0633\u06cc \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u06d5\u062f\u0627 \u0644\u0627\u0648\u06ce\u0686\u06d5\u062e\u06c6\u06b5\u06ce\u06a9\u0645 \u0647\u06d5\u06b5\u06af\u0631\u062a\u060c \u0626\u06d5\u0648\u06cc\u0634\u0645 \u0647\u0627\u0648\u06cc\u0634\u062a\u06d5 \u0646\u0627\u0648\u06cc. \u0626\u0627\u0648\u06d5\u0647\u0627\u06cc\u0646\u06d5 \u0646\u06d5\u0641\u0633\u0645 \u0626\u06d5\u0648\u06d5\u06cc \u0628\u06c6 \u062e\u06d5\u0645\u06b5\u0627\u0646\u062f\u0645.',
    '\u06af\u0648\u062a\u06cc: \u062f\u06d5\u0633\u0627 \u0628\u0695\u06c6 \u06af\u0648\u0645 \u0628\u06d5! \u0626\u06d5\u0634\u06ce \u0647\u06d5\u062a\u0627 \u0698\u06cc\u0646\u062a \u0645\u0627\u0648\u06d5\u060c \u0647\u06d5\u0631 \u0628\u06ce\u0698\u06cc \u062a\u0648\u062e\u0646\u0645 \u0645\u06d5\u06a9\u06d5\u0648\u0646\u061b \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06ce\u06a9\u06cc\u0634\u062a \u0647\u06d5\u06cc\u06d5 \u0647\u06d5\u0631 \u062f\u06ce\u062a\u06d5 \u0695\u06ce\u062a. \u062f\u06d5\u0628\u0634 \u0646\u06c6\u0695\u06d5\u060c \u0626\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u062a\u06c6 \u062f\u06d5\u062a \u067e\u06d5\u0631\u0633\u062a \u0648 \u062f\u06d5\u062a \u06af\u0648\u062a: \u0642\u06d5\u062a \u0628\u06d5 \u062c\u06ce\u06cc \u0646\u0627\u0647\u06ce\u06b5\u0645\u060c \u0686\u062a\u06c6 \u0626\u0627\u06af\u0631\u06cc \u062f\u06d5\u062f\u06d5\u06cc\u0646 \u0648 \u062e\u06c6\u06b5\u06d5\u06a9\u06d5\u0634\u06cc \u0644\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u06cc\u06d5 \u062f\u06d5\u067e\u0695\u0698\u06ce\u0646\u06cc\u0646\u060c \u0628\u06d5 \u067e\u0695\u0698\u0627\u0646\u062f\u0646.',
    '\u067e\u06d5\u0631\u0633\u062a\u06cc\u0648 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0648 \u0647\u06cc\u0686 \u0634\u062a\u06cc \u062a\u0631 \u2013 \u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u2013 \u0647\u06ce\u0698\u0627\u06cc \u067e\u06d5\u0631\u0633\u062a\u0646\u06ce \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u0633\u06d5\u0631\u06af\u0648\u0631\u0648\u0634\u062a\u06d5\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u062a \u0628\u06c6 \u0626\u06ce\u0698\u06cc\u0646. \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u0645\u0627\u0646\u0695\u0627 \u062f\u0627\u0648\u06d5 \u0628\u06d5 \u062a\u06c6.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u06cc \u0644\u06ce \u0646\u06d5\u0628\u0627\u0646 \u06a9\u0627\u060c \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u0627\u0631\u06ce\u06a9\u06cc \u06af\u0631\u0627\u0646 \u0647\u06d5\u06b5\u062f\u06d5\u06af\u0631\u06ce.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5 \u0698\u06ce\u0631\u06cc \u062f\u06d5\u0631\u0646\u0627\u0686\u06cc. \u0626\u0627\u06cc \u06a9\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0686\u06d5\u0646\u062f \u0628\u0627\u0631\u06ce\u06a9\u06cc \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0648\u0627 \u0641\u0648\u0648 \u0628\u06d5 \u06a9\u06d5\u06b5\u06d5\u0634\u0627\u062e\u062f\u0627 \u062f\u06d5\u06a9\u0631\u06ce\u060c \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u06a9\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5 \u0648\u061b \u06a9\u06c6\u0631\u0646 \u0648 \u0634\u06cc\u0646 \u0647\u06d5\u06b5\u06af\u06d5\u0695\u0627\u0648\u0646.',
    '\u0647\u06ce\u062f\u06cc\u06a9\u0627 \u0628\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u062f\u06d5\u06b5\u06ce\u0646: \u062f\u06d5 \u0695\u06c6\u0698 \u0646\u06d5\u0628\u06ce\u060c \u0646\u06d5\u0645\u0627\u0648\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06ce\u06cc \u062f\u06d5\u062f\u0648\u06ce\u0646\u060c \u0626\u06ce\u0645\u06d5 \u067e\u062a\u0631 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0646. \u0644\u06d5 \u06af\u0634\u062a\u06cc\u0627\u0646 \u0647\u06ce\u0698\u0627\u062a\u0631 \u0626\u06ce\u0698\u06ce: \u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u0695\u06c6\u0698\u06ce\u06a9 \u0645\u0627\u0648\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0645\u06d5\u0695 \u06a9\u06ce\u0648\u0627\u0646 \u0644\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646. \u0628\u06ce\u0698\u06d5: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u0645\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0647\u0627\u0695\u06cc\u0646 \u062f\u06d5\u06cc\u0627\u0646\u0647\u0627\u0695\u06ce\u061b',
    '\u062f\u06d5\u06cc\u0627\u0646\u06a9\u0627\u062a\u06d5 \u062f\u06d5\u0634\u062a\u06ce\u06a9\u06cc \u06a9\u0627\u06a9\u06cc \u0628\u06d5 \u06a9\u0627\u06a9\u06cc\u061b',
    '\u0646\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0646\u062f\u06cc \u062a\u06cc\u0627 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0648 \u0646\u06d5 \u0647\u06cc\u0686 \u06a9\u06c6\u0633\u067e\u06ce.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0648\u06d5\u062f\u06d5\u0646\u06af \u06a9\u06d5\u0633\u06ce\u06a9\u06d5\u0648\u06d5 \u062f\u06d5\u0686\u0646\u060c \u06a9\u06d5 \u0628\u06d5 \u0647\u06cc\u0686 \u0628\u0627\u0631\u0627 \u062e\u0648\u0627\u0631 \u0646\u06cc\u06cc\u06d5. \u0644\u06d5\u0628\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0646\u06af\u06ce\u06a9 \u0646\u06d5\u0648\u06cc \u062f\u06d5\u0628\u06ce\u061b \u0686\u067e\u06d5\u0686\u067e \u0646\u06d5\u0628\u06ce \u0646\u0627\u0628\u06cc\u0633\u06cc.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u062a\u06a9\u0627 [\u0628\u06c6 \u06a9\u06d5\u0633] \u0628\u06d5\u0647\u0631\u06d5 \u0646\u0627\u062f\u0627\u060c \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0633\u06ce \u062e\u0648\u062f\u0627 \u0626\u06cc\u0632\u0646\u06cc \u062a\u06a9\u0627\u06cc \u0628\u062f\u0627 \u0648 \u0642\u0633\u06d5\u06a9\u06d5\u0634\u06cc \u067e\u06d5\u0633\u0646\u062f \u0628\u06a9\u0627.',
    '\u0626\u06d5\u0648 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06cc\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u0627\u062a \u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u0646 \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u0695 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0647\u06d5\u0645\u0648\u0648 \u0645\u06d5\u0631\u062f\u0645 \u0644\u0627\u0631\u06d5\u0645\u0644\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u0646 \u06a9\u06d5 \u0647\u06d5\u0631 \u0646\u0627\u0645\u0631\u06ce \u0648 \u067e\u0627\u06cc\u06d5\u062f\u0627\u0631\u06d5\u061b \u06a9\u06ce \u0646\u0627\u0647\u06d5\u0642\u06ce\u06a9\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0646\u0627\u0647\u0648\u0645\u06ce\u062f\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u06d5\u060c \u0646\u0627\u062a\u0631\u0633\u06ce \u06a9\u06d5 \u067e\u0627\u062f\u0627\u0634\u06d5\u06a9\u06d5\u06cc \u0632\u06d5\u0648\u062a \u06a9\u0631\u06ce \u06cc\u0627 \u06a9\u06d5\u0645\u062a\u0631 \u0628\u06ce.',
    '\u0647\u06d5\u0631 \u0628\u06d5\u0645 \u062a\u06d5\u0631\u0632\u06d5\u0634 \u0642\u0648\u0631\u0626\u0627\u0646\u06ce \u0628\u06d5 \u0639\u06d5\u0631\u06d5\u0628\u06cc\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u06af\u06d5\u0644\u06ce\u06a9 \u0645\u0627\u06cc\u06d5\u06cc \u062a\u0631\u0633\u0627\u0646\u062f\u0646\u06ce\u0646 \u062a\u06ce\u062e\u0633\u062a\u0648\u0648\u06d5\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u067e\u06ce\u06cc \u0628\u067e\u0631\u06cc\u0646\u06af\u06ce\u0646\u06d5\u0648\u06d5 \u06cc\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06cc\u0631 \u0628\u06ce\u0646\u0646\u06d5\u0648\u06d5.',
    '\u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u06d5 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u067e\u0627\u0648\u0634\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0642\u06cc\u0646\u06d5\u06cc\u06d5. \u062a\u06c6\u0634 \u0628\u06d5\u0631\u0644\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u062a\u06cc \u0695\u0627\u06af\u06d5\u06cc\u06ce\u0646\u06cc\u0646\u060c \u062f\u0628\u06c6 [\u062e\u0648\u06ce\u0646\u062f\u0646\u06cc] \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u067e\u06d5\u0644\u06d5\u062a \u0646\u06d5\u0628\u06ce \u0648 \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u06af\u06d5\u0634\u06d5 \u0628\u06d5 \u0632\u0627\u0646\u0633\u062a\u0645 \u0628\u062f\u06d5.',
    '\u0628\u06d5\u0631\u0644\u06d5 \u0626\u06ce\u0633\u062a\u0627\u0634 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u0627\u062f\u06d5\u0645 \u067e\u06d5\u06cc\u0645\u0627\u0646\u0646 \u0628\u06d5\u0633\u062a\u061b \u0644\u06d5 \u0628\u06cc\u0631\u06cc \u06a9\u0631\u062f \u0648 \u067e\u06ce\u06cc \u0646\u06d5\u06a9\u0631\u0627 \u067e\u06ce\u06a9\u06cc \u0628\u06ce\u0646\u06ce.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u0628\u06d5 \u0641\u0631\u0634\u062a\u0627\u0646\u0645\u0627\u0646 \u0648\u062a: \u0633\u0648\u0698\u062f\u06d5 \u0644\u06d5\u0628\u06d5\u0631 \u0626\u0627\u062f\u06d5\u0645 \u0628\u06d5\u0631\u0646. \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0633\u0648\u0698\u062f\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u0631\u062f \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06cc\u0628\u0644\u06cc\u0633\u061b \u0626\u06d5\u0648 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc \u06a9\u0631\u062f.',
    '\u06af\u0648\u062a\u0645\u0627\u0646: \u0626\u0627\u062f\u06d5\u0645! \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0645 \u0646\u06d5\u06cc\u0627\u0631\u06cc \u062a\u06c6\u06cc\u06d5\u060c \u0647\u06d5\u0645 \u0646\u06d5\u06cc\u0627\u0631\u06cc \u0698\u0646\u06d5\u06a9\u06d5\u062a\u06d5\u061b \u0644\u06d5\u0645 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u062a\u0627\u0646 \u062f\u06d5\u0631 \u0646\u06d5\u06a9\u0627 \u0648 \u062a\u0648\u0648\u0634\u06cc \u0642\u06d5\u0631\u06d5\u0628\u06d5\u062e\u062a\u06cc \u0628\u0628\u06cc.',
    '[\u0647\u06d5\u062a\u0627 \u0644\u06ce\u0631\u06d5\u06cc] \u0646\u06d5 \u0642\u06d5\u062a \u0695\u0648\u0648\u062a\u06cc \u0695\u0648\u0648\u062a \u062a\u06ce\u062f\u06d5\u06a9\u0627\u060c \u0646\u06d5 \u0628\u0631\u0633\u06ce\u062a\u06cc\u061b',
    '\u0646\u06d5 \u0642\u06d5\u062a \u062a\u06ce\u06cc\u062f\u0627 \u062a\u0648\u0648\u0646\u06cc \u062f\u06d5\u0628\u06cc\u060c \u0646\u06d5 \u062a\u0627\u0648\u0627\u0646 \u06af\u0627\u0632.',
    '\u0634\u06d5\u06cc\u062a\u0627\u0646 \u062f\u0646\u06d5\u06cc \u062e\u0631\u0627\u067e\u06d5\u06cc \u062f\u0627. \u06af\u0648\u062a\u06cc: \u0626\u0627\u062f\u06d5\u0645! \u06af\u06d5\u0631\u06d5\u06a9\u062a\u06d5 \u062f\u0627\u0631\u06cc \u0647\u06d5\u0631\u0645\u0627\u0646\u062a \u067e\u06ce \u0628\u06b5\u06ce\u0645 \u0648 \u0626\u06d5\u0648\u06cc \u0647\u06d5\u062a\u06d5 \u062f\u0648\u0627\u06cc\u06cc \u0646\u06d5\u06cc\u06d5\u061f',
    '\u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9 \u0644\u06d5\u0628\u06d5\u0631\u06cc \u062f\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062e\u0648\u0627\u0631\u062f. \u0634\u06d5\u0631\u0645\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u061b \u062e\u06d5\u0631\u06cc\u06a9 \u0628\u0648\u0648\u0646 \u0628\u06d5 \u06af\u06d5\u06b5\u0627\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a \u062e\u06c6 \u062f\u0627\u067e\u06c6\u0634\u0646. \u0626\u0627\u062f\u06d5\u0645 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0644\u0627\u06cc \u062f\u0627 \u0648\u060c \u0644\u06d5 \u0695\u06ce \u062f\u06d5\u0631\u0686\u0648\u0648.',
    '\u067e\u0627\u0634\u0627\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u060c \u0626\u06d5\u0648\u06cc \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f \u0648 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0631\u062f \u0648 \u0695\u06ce\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627.',
    '\u06af\u0648\u062a\u06cc: \u0647\u06d5\u0631\u062a\u06a9\u0648\u0648 \u0633\u06d5\u0631\u0628\u06d5\u0631\u06d5\u0698\u06ce\u0631 \u062f\u0627\u062e\u06c6\u0632\u0646\u06d5 \u0698\u06ce\u0631\u061b \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631 \u062f\u0698\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0646\u061b \u0626\u06d5\u0631 \u0644\u06d5 \u0645\u0646\u0695\u0627 \u0695\u06ce\u0646\u0648\u06ce\u0646\u06cc\u062a\u0627\u0646 \u0647\u0627\u062a\u06d5 \u0644\u0627\u060c \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0695\u06ce\u06af\u06d5\u06cc \u0645\u0646\u06cc \u06af\u0631\u062a\u06d5\u0628\u06d5\u0631\u06ce\u060c \u0626\u06cc\u062a\u0631 \u0646\u06d5 \u0695\u06ce \u0647\u06d5\u06b5\u06d5 \u062f\u06d5\u06a9\u0627\u060c \u0646\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0646\u06d5\u06af\u0628\u06d5\u062a\u06cc \u062f\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u06d5 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0645\u0646 \u0646\u06d5\u06a9\u0627\u060c \u0698\u06cc\u0646\u06cc \u0644\u06d5 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5\u062f\u0627\u06cc\u06d5 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 \u06a9\u06c6\u0631\u06d5 \u0644\u06d5 \u06af\u06c6\u0695\u06cc \u062f\u06d5\u0631\u062f\u06d5\u062e\u06d5\u06cc\u0646.',
    '\u0626\u06ce\u0698\u06ce: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0628\u06c6\u0686\u06cc \u0645\u0646\u062a \u0628\u06d5 \u06a9\u0648\u06ce\u0631\u06cc \u0632\u06cc\u0646\u062f\u0648\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u061f \u062e\u06c6 \u0645\u0646 \u0626\u06d5\u0648\u0633\u0627 \u0686\u0627\u0648\u0645 \u0633\u0627\u063a \u0628\u0648\u0648\u0646.',
    '\u0626\u06ce\u0698\u06ce: \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u062a\u06c6 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06af\u06d5\u0644 \u0626\u06ce\u0645\u06d5\u062a \u0628\u06c6\u0647\u0627\u062a \u0648 \u0644\u06d5 \u0628\u06cc\u0631\u06d5 \u062e\u06c6\u062a\u062a \u0628\u0631\u062f\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0645\u0695\u06c6\u0634 \u062a\u06c6 \u0644\u06d5 \u0628\u06cc\u0631 \u062f\u06d5\u0686\u06cc\u06cc\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06af\u06d5\u0644 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u0695\u0648\u0627 \u0646\u06d5\u06a9\u0627 \u0648 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u062e\u06c6\u06cc \u0644\u0627\u0628\u062f\u0627\u060c \u0626\u06d5\u0634\u06ce \u0626\u06d5\u0645 \u062f\u06d5\u0631\u062f\u06d5\u06cc \u0628\u062f\u06d5\u06cc\u0646\u06ce. \u062f\u06cc\u0627\u0631\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0634 \u062a\u0648\u0646\u062f\u062a\u0631 \u0648 \u0628\u06d5 \u0645\u06d5\u0648\u062f\u0627\u062a\u0631\u06d5.',
    '\u0645\u06d5\u06af\u06d5\u0631 \u0628\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0646\u06d5\u06a9\u06d5\u0648\u062a\u0648\u0648\u06d5 \u2013 \u0628\u06d5\u0631\u0644\u06d5\u0648\u0627\u0646 \u2013 \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u0634\u0648\u06ce\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u067e\u06cc\u0627 \u062f\u06ce\u0646 \u0648 \u062f\u06d5\u0686\u0646\u060c \u0686\u06d5\u0646\u062f \u0628\u06d5\u0631\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u0631\u062f\u0646. \u0647\u06d5\u0631 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0626\u0627\u0648\u06d5\u0632\u0646\u060c \u0632\u06c6\u0631 \u0628\u06d5\u06b5\u06af\u06d5 \u0647\u06d5\u0646.',
    '\u06af\u06d5\u0631 \u0628\u06d5\u06b5\u06ce\u0646\u06ce\u06a9 \u0628\u06d5\u0631\u0644\u06d5 \u0626\u06ce\u0633\u062a\u0627 \u0644\u06d5 \u067e\u06d5\u06c6\u06d5\u0631\u0646\u062f\u06d5\u062a\u0695\u0627 \u0646\u06d5\u0628\u0627\u060c \u0648\u06d5\u062e\u062a\u06ce\u06a9\u06cc\u0634 \u062f\u06cc\u0627\u0631\u06cc \u0646\u06d5\u06a9\u0631\u0627\u0628\u0627\u060c \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06d5\u0647\u0627\u062a.',
    '\u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0626\u06ce\u0698\u0646\u060c \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06d5. [\u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0645\u0627\u0646] \u2013 \u067e\u06ce\u0634 \u062a\u0627\u0648\u0647\u06d5\u06b5\u0627\u062a\u060c \u067e\u06ce\u0634 \u062e\u06c6\u0631\u0627\u0648\u0627 \u2013 \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u0626\u06d5\u0648 \u0628\u062f\u06d5 \u0648 \u0644\u06d5 \u0628\u06d5\u062a\u06b5\u06d5\u06a9\u0627\u0646\u06cc \u0634\u06d5\u0648\u06ce\u062f\u0627 \u0648 \u0644\u06d5 \u0647\u06d5\u0631 \u062f\u0648\u0648 \u0626\u0627\u0644\u06cc \u0695\u06c6\u0698\u06ce\u062f\u0627 \u0633\u06d5\u0631\u06af\u06d5\u0631\u0645\u06cc \u0633\u0648\u067e\u0627\u0633\u06cc \u0626\u06d5\u0648 \u0628\u06d5\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06d5 \u0626\u0627\u0648\u0627\u062a\u062a \u0628\u06af\u06d5\u06cc.',
    '\u0686\u0627\u0648\u062a \u0645\u06d5\u062f\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u06a9\u06d5 \u062f\u0627\u0648\u0645\u0627\u0646\u06d5 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06af\u06d5\u0644\u06ce \u0644\u06d5\u0648\u0627\u0646\u06d5\u060c \u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u062e\u06c6\u0634\u06cc \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u0648\u06cc\u0633\u062a\u0648\u0648\u0645\u0627\u0646\u06d5 \u0628\u06cc\u06cc\u0627\u0646 \u0626\u06d5\u0632\u0645\u0648\u06cc\u0646. \u0628\u0698\u06cc\u0648\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0686\u0627\u062a\u0631 \u0648 \u0628\u06d5\u0631\u062f\u06d5\u0648\u0627\u0645\u062a\u0631\u06d5.',
    '\u0628\u06d5 \u06a9\u06d5\u0633\u0648\u06a9\u0627\u0631\u06cc \u062e\u06c6\u062a \u0628\u06ce\u0698\u06d5 \u0646\u0648\u06ce\u0698\u0627\u0646 \u0628\u06a9\u06d5\u0646\u061b \u062e\u06c6\u0634\u062a \u0648\u0627\u0632\u06cc \u0644\u06ce \u0645\u06d5\u0647\u06ce\u0646\u06d5. \u0626\u06ce\u0645\u06d5 \u0695\u06c6\u0632\u06cc\u0646 \u0644\u06d5 \u062a\u06c6 \u0646\u0627\u0648\u06ce\u061b \u062e\u06c6\u0645\u0627\u0646 \u0628\u0698\u06cc\u0648 \u0628\u06d5 \u062a\u06c6 \u062f\u06d5\u062f\u06d5\u06cc\u0646. \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u0634\u062f\u0627 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u06cc \u0628\u06d5\u0647\u0631\u06d5 \u062f\u06d5\u062f\u0627.',
    '\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u0628\u06c6\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0646 \u0628\u06c6 \u0646\u0627\u0647\u06ce\u0646\u06cc\u061f \u0626\u06d5\u062f\u06cc \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u0646\u06d5\u0647\u0627\u062a\u06d5 \u0644\u0627\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0631\u0644\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0626\u06d5\u0648\u0627\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u0631\u062f\u0628\u0627\u060c \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u062a\u06c6 \u0628\u06d5\u0631\u0644\u06d5\u0648\u06d5 \u0648\u0627 \u0628\u06ce\u0686\u0627\u0631\u06d5 \u0648 \u0695\u06cc\u0633\u0648\u0627 \u0628\u0628\u06cc\u0646\u060c \u0628\u06c6\u0686\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06cc \u062e\u06c6\u062a \u0628\u06c6 \u0646\u06d5\u0646\u0627\u0631\u062f\u06cc\u0646\u060c \u062a\u0627 \u0628\u06d5 \u06af\u0648\u06ce\u0631\u06d5\u06cc \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062a\u06c6 \u0628\u06a9\u06d5\u06cc\u0646\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0645\u0648\u0648 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0646\u060c \u0633\u0627 \u0626\u06ce\u0648\u06d5\u0634 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646\u060c \u0647\u06d5\u0631\u0628\u06cc\u0646\u0627 \u0644\u06ce\u062a\u0627\u0646 \u062d\u0627\u06b5\u06cc \u0628\u0648\u0648: \u06a9\u06ce \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0644\u06d5\u0628\u06d5\u0631\u06d5 \u0648 \u0628\u06c6 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc \u0644\u06d5 \u0633\u06d5\u0631\u06d5.',
    '\u062e\u06d5\u06b5\u06a9 \u0626\u0627\u06af\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0646\u06cc\u06cc\u06d5\u060c \u0695\u06c6\u0698\u06cc \u0698\u0645\u0627\u0631\u06cc\u0627\u0646 \u0646\u0632\u06cc\u06a9\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0634 \u0646\u0627\u06a9\u06d5\u0646 \u0647\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u0631 \u067e\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u062a\u0627\u0632\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0628\u06ce\u060c \u062f\u06d5\u06cc \u0628\u06cc\u0633\u0646 \u0648 \u06af\u0648\u0631\u062c\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u06d5 \u06af\u0627\u06b5\u062a\u06d5\u062c\u0627\u0695\u061b',
    '\u062f\u06b5 \u062e\u0627\u0641\u06b5\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0628\u06d5 \u0633\u0631\u062a\u06d5\u0648\u06d5 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u06d5\u0634 \u0645\u0631\u06c6\u06cc\u06d5\u06a9 \u0648\u06d5\u06a9 \u062e\u06c6\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u061f \u062f\u06d5\u06cc \u0686\u06c6\u0646 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u062c\u0627\u062f\u0648\u0648 \u062f\u06d5\u0633\u062e\u06d5\u0631\u06c6 \u062f\u06d5\u0628\u0646 \u06a9\u06d5 \u0628\u06d5 \u0686\u0627\u0648\u06cc \u062e\u06c6 \u062f\u06d5\u0628\u06cc\u0646\u0646\u061f',
    '\u06af\u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u0645\u0646\u060c \u0644\u06d5 \u0647\u06d5\u0631 \u0642\u0633\u06ce \u0644\u06d5 \u0639\u06d5\u0631\u0632 \u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0628\u06a9\u0631\u06ce\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u06d5\u0648\u0646\u06d5 \u062f\u0631\u06c6\u0632\u0646\u06d5\u06cc\u06d5\u061b \u0646\u06d5\u062e\u06ce\u0631 \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u06cc\u0628\u06d5\u0633\u062a\u0648\u0648\u06d5\u061b \u062f\u06d5\u0634\u0628\u06ce \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0634\u0627\u0639\u06cc\u0631\u06ce\u06a9 \u0628\u06ce. \u062f\u06d5\u0633\u0627 \u0628\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06ce\u06a9\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u0627\u060c \u0648\u06d5\u06a9 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648.',
    '\u0628\u06d5\u0631\u0644\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0626\u06d5\u0648 \u0634\u0627\u0631\u0627\u0646\u06d5\u06cc \u0642\u0695\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646\u060c \u0647\u06d5\u0631 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627 \u0628\u0648\u0648\u061b \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646 \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0626\u06d5\u0648 \u0645\u06ce\u0631\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6\u0634 \u0695\u0627\u0645\u0627\u0646 \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u0646 \u2013 \u0647\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u062a\u06c6 \u2013 \u0626\u06ce\u0645\u06d5 \u0648\u06b5\u0627\u0645\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646. \u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646\u060c \u0644\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u06a9\u062a\u06ce\u0628\u0627\u0646 \u067e\u0631\u0633\u0646.',
    '\u0647\u06cc\u0686 \u0644\u06d5\u0634\u06cc \u0648\u0627\u0634\u0646 \u067e\u06ce \u0646\u06d5\u062f\u0627\u0646 \u06a9\u06d5 \u0647\u06cc\u0686 \u0646\u06d5\u062e\u06c6\u0646 \u0648 \u0646\u06d5\u0645\u0631 \u0628\u0646.',
    '\u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06ce\u06a9\u06cc \u2013 \u06a9\u06d5 \u067e\u06ce\u0646 \u062f\u0627\u0628\u0648\u0648\u0646 \u2013 \u0628\u06d5 \u062c\u06ce\u0646 \u0647\u06ce\u0646\u0627\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u062e\u06c6\u0645\u0627\u0646 \u0648\u06cc\u0633\u062a\u0645\u0627\u0646 \u0646\u06d5\u062c\u0627\u062a\u0646 \u062f\u0627\u0646\u061b \u0647\u06d5\u06b5\u06d5\u0695\u06c6\u0634\u0645\u0627\u0646 \u0642\u0695\u0627\u0646 \u062a\u06ce\u062e\u0633\u062a.',
    '\u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5 \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u0628\u0627\u0633\u06cc \u0626\u06ce\u0648\u06d5\u0634\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5. \u0686\u06cc\u062a\u0627\u0646\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631 \u062a\u06ce\u0646\u0627\u06af\u06d5\u0646\u061f',
    '\u0686\u06d5\u0646\u062f \u0628\u0627\u0698\u06ce\u0631\u06cc\u0634\u0645\u0627\u0646 \u0648\u06ce\u0695\u0627\u0646 \u06a9\u0631\u062f\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062a\u06cc\u0627 \u0628\u0648\u0648\u0646\u060c \u0633\u062a\u06d5\u0645\u06a9\u0627\u0631 \u0628\u0648\u0648\u0646\u061b \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646 \u062e\u06d5\u06b5\u06a9\u06cc \u062a\u0631\u0645\u0627\u0646 \u0644\u06ce \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0646\u062f.',
    '\u0647\u06d5\u0631 \u067e\u06ce \u062d\u06d5\u0633\u06cc\u0627\u0646 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u06af\u0698\u06cc\u0627\u0646 \u062f\u0627\u062f\u06ce\u06cc\u0646\u060c \u0644\u06d5\u067e\u0695\u06ce\u06a9\u062f\u0627 \u0644\u06d5 \u0626\u0627\u0648\u0627\u06cc\u06cc \u0695\u0627\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0626\u06ce\u0648\u06d5 \u0695\u0627\u0645\u06d5\u06a9\u06d5\u0646 \u0648 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u062e\u06c6\u0634\u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0648 \u0645\u0627\u06b5\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u067e\u0631\u0633\u06cc\u0627\u0631\u062a\u0627\u0646 \u0644\u06ce \u0628\u06a9\u0631\u06ce.',
    '\u0648\u062a\u06cc\u0627\u0646: \u0647\u06d5\u06cc \u0645\u0627\u06b5 \u0648\u06ce\u0631\u0627\u0646 \u062e\u06c6\u0645\u0627\u0646! \u062f\u06cc\u0627\u0631\u06d5 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u0628\u0648\u0648\u06cc\u0646.',
    '\u0647\u06d5\u0631 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0645\u06d5 \u062f\u06d5\u062f\u0648\u0627\u0646\u060c \u062a\u0627 \u0628\u06d5 \u062f\u06d5\u0631\u062f\u06cc \u0633\u0627\u0642\u06d5\u062a\u06d5\u06cc \u062f\u0648\u0648\u0631\u0627\u0648\u0645\u0627\u0646 \u0628\u0631\u062f\u0646 \u0648 \u062f\u0627\u0646 \u0645\u0631\u06a9\u0627\u0646\u062f\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627\u06cc\u06d5 \u0628\u06c6 \u0644\u06d5\u06cc\u0633\u062a\u06c6\u06a9 \u062f\u0631\u0648\u0633 \u0646\u06d5\u06a9\u0631\u062f.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u06af\u06d5\u0631\u06d5\u06a9\u0645\u0627\u0646 \u0628\u0627 \u0644\u06d5\u06cc\u0633\u062a\u06c6\u06a9\u06ce\u06a9 \u0647\u06d5\u06b5\u0628\u0698\u06ce\u0631\u06cc\u0646\u060c \u0647\u06d5\u0631 \u0644\u0627\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0647\u06d5\u06b5\u0645\u0627\u0646 \u062f\u06d5\u0628\u0698\u0627\u0631\u062f. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0645\u0627\u0646\u0648\u06cc\u0633\u062a\u0627\u06cc\u06d5 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5 \u0628\u06a9\u06d5\u06cc\u0646 [\u0647\u06d5\u0631 \u062f\u06d5\u0645\u0627\u0646\u06a9\u0631\u062f.]',
    '\u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06ce\u0645\u06d5 \u06af\u06d5\u0631\u06d5\u06a9\u0645\u0627\u0646\u06d5 \u067e\u0695\u0648\u067e\u0648\u0648\u0686 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0628\u0686\u06ce\u062a \u0648 \u0695\u0627\u0633\u062a\u06cc \u062c\u06ce\u06af\u06d5\u06cc \u0628\u06af\u0631\u06ce\u062a\u06d5\u0648\u06d5. \u0648\u0627\u0648\u06d5\u06cc\u0644\u0627 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc\u06d5 \u0644\u06d5\u0648 \u0628\u0627\u0633\u06cc \u062e\u0648\u062f\u0627\u06a9\u0631\u062f\u0646\u06d5\u0648.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc \u062f\u0627\u06cc\u06d5\u060c \u0633\u06d5\u0631\u0628\u06d5 \u062e\u0648\u0627\u06cc\u06d5\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648\u0646\u060c \u0644\u06d5 \u067e\u06d5\u0631\u0633\u062a\u0646\u06cc \u0628\u06d5 \u0641\u06cc\u0632 \u0646\u06cc\u0646\u061b \u0641\u0644\u06cc\u0634 \u0646\u0627\u0628\u0646.',
    '\u0628\u06d5 \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698 \u0647\u06d5\u0631 \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0633\u0633\u062a\u06cc \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u062e\u0648\u0627\u06af\u06d5\u0644\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0645 \u0633\u06d5\u0631 \u0647\u06d5\u0631\u062f\u06d5 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u0648\u0648\u06d5\u060c \u0645\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u06af\u06c6\u0695\u0627\u0646 \u0648\u06d5\u0698\u06cc\u06d5\u0646\u06ce\u0646\u061f',
    '\u0626\u06d5\u0631 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u062f\u0627 - \u0628\u06ce\u0698\u06af\u06d5 \u0644\u06d5 \u062e\u0648\u0627 - \u062e\u0648\u0627\u06af\u06d5\u0644\u06ce\u06a9\u06cc \u062a\u0631 \u0628\u0648\u0648\u0646\u0627\u06cc\u0627\u060c \u0628\u06ce \u0634\u06a9 \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9\u06cc\u0627\u0646 \u062a\u06ce\u06a9 \u062f\u06d5\u0686\u0648\u0648\u0646. \u062e\u0648\u062f\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062a\u06d5\u062e\u062a\u060c \u0632\u06c6\u0631 \u067e\u0627\u06a9\u062a\u0631\u06d5 \u0644\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06c6\u06cc \u062f\u06d5\u06b5\u06ce\u0646.',
    '\u0686\u06cc \u0626\u06d5\u0648 \u0628\u06cc\u06a9\u0627 \u0646\u0627\u0634\u06ce \u067e\u0631\u0633\u06cc\u0627\u0631\u06cc \u0644\u06ce \u0628\u06a9\u0631\u06ce \u0648 \u0626\u06d5\u0648\u0627\u0646\u0646 \u06a9\u06d5 \u0628\u06d5\u0631\u067e\u0631\u0633\u06cc\u0627\u0631\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 - \u0644\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627 - \u062e\u0648\u062f\u0627\u06af\u06d5\u0644\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06cc\u0648\u06d5\u062a\u06d5\u0648\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06ce\u0646\u0646\u061b \u0626\u06d5\u0645\u06d5 \u067e\u06d5\u0646\u062f\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u0646\u062f\u0627\u0646\u061b \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u067e\u06ce\u0634 \u0645\u0646 \u0628\u0648\u0648\u0646. \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646 - \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 - \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0633\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0646\u0627\u0686\u06ce \u0648 \u0644\u06ce\u06cc \u0646\u06d5\u0628\u0627\u0646\u0646.',
    '\u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6\u0634 \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u060c \u0695\u0627\u0645\u0627\u0646 \u0633\u067e\u0627\u0631\u062f\u060c \u06a9\u06d5: \u0647\u06cc\u0686 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u06ce\u06a9 \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0645\u0646 \u0646\u06cc\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u0645\u0646 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646.',
    '\u0648\u062a\u06cc\u0634\u06cc\u0627\u0646: \u062e\u0648\u062f\u0627 \u0695\u06c6\u06b5\u06d5\u06cc \u0647\u06d5\u0633. \u0646\u06d5\u062e\u06ce\u0631\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0695\u06ce\u0632\u06af\u06cc\u0631\u0627\u0648\u0646\u060c \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u067e\u0627\u06a9\u06d5.',
    '\u0644\u06d5 \u0642\u0633\u06d5 \u067e\u06ce\u0634\u06cc \u0646\u0627\u06a9\u06d5\u0648\u0646 \u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u067e\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u062f\u06d5\u062c\u0648\u06b5\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0633\u062a\u0627 \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0648 \u0644\u06d5\u0648\u06d5\u0634 \u06a9\u06d5\u0648\u0627 \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u06cc \u0695\u06d5\u0632\u0627\u06cc \u0646\u06d5\u0628\u06ce\u060c \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u062a\u06a9\u0627\u06cc \u0628\u06c6 \u0628\u06a9\u06d5\u0646\u061b \u0648\u0627\u06cc \u0644\u06ce \u062f\u06d5\u062a\u0631\u0633\u0646\u060c \u062f\u06d5\u0644\u06d5\u0631\u0632\u0646.',
    '\u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5\u0648\u0627\u0646 \u0628\u06ce\u0698\u06ce: \u0645\u0646\u06cc\u0634 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u0645 \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648\u061b \u0633\u0632\u0627\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u062f\u06c6\u0698\u06d5\u0647\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5\u0634 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0633\u0632\u0627 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u0627\u062e\u06c6 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0626\u06d5\u0645\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646\u060c \u06a9\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u06cc\u06d5\u06a9\u067e\u0627\u0631\u0686\u06d5 \u0628\u0648\u0648\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06ce\u06a9\u0645\u0627\u0646 \u0647\u06d5\u06b5\u0648\u06d5\u0634\u0627\u0646\u062f\u0646. \u0647\u06d5\u0631 \u062c\u0627\u0646\u06d5\u0648\u06d5\u0631\u06ce\u06a9\u06cc\u0634 \u0647\u06d5\u06cc\u06d5\u060c \u0644\u06d5 \u0626\u0627\u0648\u0645\u0627\u0646 \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061f \u0626\u0627\u062e\u06c6 \u0647\u06ce\u0634\u062a\u0627 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0646\u061f',
    '\u0644\u06d5\u0633\u06d5\u0631 \u0632\u06d5\u0648\u06cc\u0634 \u06a9\u06d5\u0698\u0648\u06a9\u06c6\u0645\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u060c \u0646\u06d5\u0648\u06d5\u06a9\u0627 \u0648\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0631\u0632\u06ce\u0646\u06ce \u0648 \u0686\u06d5\u0646\u062f \u0695\u06ce\u0628\u0627\u0632\u0645\u0627\u0646 \u0644\u06ce \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627 \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u0646.',
    '\u0626\u0627\u0633\u0645\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0645\u06cc\u0686\u06ce\u06a9\u06cc \u067e\u0627\u0631\u06ce\u0632\u0631\u0627\u0648\u060c \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0648 \u06af\u0634 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06d5\u06cc \u062a\u06ce\u06cc\u062f\u0627\u0646\u060c \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698 \u0648 \u062e\u06c6\u0631 \u0648 \u0647\u06d5\u06cc\u06a4\u06cc \u062f\u0631\u0648\u0633\u062a \u06a9\u0631\u062f \u0648 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u0645\u06d5\u0644\u06d5 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0648\u0627\u0645\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f \u0647\u06cc\u0686 \u0645\u0631\u06c6\u06cc\u06d5\u06a9 \u0628\u06d5\u0631 \u0644\u06d5 \u062a\u06c6 \u0647\u06d5\u0628\u06ce \u0648 \u0646\u06d5\u0645\u0631 \u0628\u06ce. \u0626\u0627\u062e\u0631 \u0686\u06c6\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0628\u0645\u0631\u06cc\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062a\u0627 \u0633\u06d5\u0631 \u062f\u06d5\u0645\u06ce\u0646\u0646\u061f',
    '\u0647\u06d5\u0645\u0648\u0648 \u06af\u06cc\u0627\u0646\u06ce\u06a9 \u062a\u0627\u0645\u06cc \u0645\u0631\u062f\u0646 \u0647\u06d5\u0631 \u062f\u06d5\u0686\u06ce\u0698\u06ce. \u0628\u06d5 \u062e\u0631\u0627\u067e\u06d5 \u0648 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0628\u06ce\u060c \u0628\u06d5 \u062a\u0627\u0642\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0648\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u0645\u0627\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u062a\u06c6 \u062f\u06d5\u0628\u06cc\u0646\u0646\u060c \u06af\u0627\u06b5\u062a\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u062a\u06c6 \u062f\u06d5\u06a9\u06d5\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u06d5 \u0628\u0627\u0633\u06cc \u0628\u062a\u06d5\u06a9\u0627\u0646\u0648\u0648 \u062f\u06d5\u06a9\u0627\u061f \u06a9\u06d5\u0686\u06cc \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u06cc\u0627\u062f\u06cc [\u062e\u0648\u062f\u0627\u06cc] \u062f\u06d5\u0647\u0646\u062f\u06d5 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0645\u0631\u06c6 \u0644\u06d5 \u067e\u06d5\u0644\u06d5 \u0686\u06ce \u0628\u0648\u0648\u06d5. \u0647\u06d5\u0631\u0628\u06cc\u0646\u0627 \u0632\u0648\u0648 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u06c6\u0645 \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0646\u061b \u0626\u06cc\u062a\u0631 \u0647\u06cc\u0686 \u067e\u06d5\u0644\u06d5\u0645 \u0644\u06ce \u0645\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06ce\u0698\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0695\u0627\u0633\u062a\u0627\u0646\u06d5\u060c \u062f\u06d5\u06cc \u06a9\u06d5\u06cc \u0626\u06d5\u0645 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u0648 \u067e\u06ce\u06a9 \u062f\u06ce\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0627\u0646 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc \u06a9\u06d5 \u0642\u06d5\u062a \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0626\u0627\u06af\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u0633\u06d5\u0631\u0648\u0686\u0627\u0648\u06cc\u0627\u0646 \u0648 \u0644\u06d5 \u067e\u0634\u062a\u06cc\u0627\u0646 \u0648\u06d5\u0644\u0627\u062f\u06d5\u0646 \u0648 \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5\u060c [\u0626\u06cc\u062a\u0631 \u0626\u0627\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f].',
    '\u0644\u06d5\u067e\u0695 \u062a\u06ce\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062f\u06d5\u06af\u06d5\u0695\u06ce \u0648 \u062a\u0648\u0648\u0634\u06cc \u0648\u0627\u0642 \u0648 \u0648\u0695\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u061b \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u062e\u06c6\u06cc \u0644\u06ce \u0644\u0627\u062f\u06d5\u0646 \u0648 \u0647\u06cc\u0686 \u0645\u06c6\u06b5\u06d5\u062a\u06cc\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0646\u0627\u062f\u06d5\u0646.',
    '\u0626\u06d5\u0648 \u06af\u0627\u06b5\u062a\u0627\u0646\u06d5 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6\u0634 \u0647\u06d5\u0631 \u06a9\u0631\u0627\u0648\u06d5. \u062c\u0627 \u0626\u06d5\u0648\u0633\u0627 \u06af\u0627\u06b5\u062a\u06d5\u062c\u0627\u0695\u06d5\u06a9\u0627\u0646\u060c \u0633\u0632\u0627\u06cc \u0626\u06d5\u0648 \u06af\u0627\u06b5\u062a\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f\u060c \u062f\u06d5\u0648\u0631\u06cc \u06af\u0631\u062a\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u06a9\u06ce\u06cc\u06d5 - \u0628\u06d5 \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698 - \u0626\u06ce\u0648\u06d5 \u0644\u06d5 [\u0626\u0627\u0632\u0627\u0631\u06cc] \u062e\u0648\u062f\u0627 \u0628\u067e\u0627\u0631\u06ce\u0632\u06ce\u061f \u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u06cc\u0627\u062f\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u06cc\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06ce\u0645\u06d5 - \u062e\u0648\u0627\u06af\u06d5\u0644\u06ce\u06a9\u06cc \u062a\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u0644\u06d5 \u0628\u06d5\u06b5\u0627\u06cc\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646\u061f! [\u0626\u06d5\u0648 \u062e\u0648\u0627\u06af\u06d5\u0644\u06d5] \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062e\u06c6\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0646\u0627\u062f\u0631\u06ce \u0648 \u0626\u06ce\u0645\u06d5\u0634 \u067e\u06d5\u0646\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0646\u0627\u062f\u06d5\u06cc\u0646.',
    '\u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06ce\u0645\u06d5 \u06a9\u0627\u0631\u06ce\u06a9\u0645\u0627\u0646 \u06a9\u0631\u062f \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0648 \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u0627\u0646\u06cc \u0648\u0627\u0646\u06cc\u0634 \u0695\u0627\u06cc \u0628\u0628\u0648\u06ce\u0631\u0646\u060c \u0647\u06d5\u062a\u0627\u0648\u06d5\u06a9\u0648\u0648 \u062a\u06d5\u0645\u06d5\u0646\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0628\u0631\u062f\u06d5\u0633\u06d5\u0631. \u0626\u06d5\u062f\u06cc \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646 \u0626\u06ce\u0645\u06d5 \u062f\u06ce\u06cc\u0646 \u0648 \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 - \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627 - \u0647\u06d5\u0631\u0648\u0627 \u0644\u06ce\u06cc \u06a9\u06d5\u0645 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u061f \u0626\u0627\u062e\u06c6 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631\u0646\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5\u06cc \u0628\u06c6\u0645 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u062f\u06d5\u0648\u062a\u0631\u0633\u06ce\u0646\u0645. \u0628\u06d5\u06b5\u0627\u0645 \u0628\u0627\u0646\u06af \u0628\u06d5 \u06af\u0648\u06ce\u06cc \u06a9\u06d5\u0695\u0627\u0646\u062f\u0627 \u0648 \u062a\u0631\u0633\u0627\u0646\u062f\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0646\u06cc\u06cc\u06d5.',
    '\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0633\u06c6\u0632\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0648\u06d5\u0644\u06d5\u0634 \u06a9\u06d5\u0648\u06ce\u060c \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u0647\u0627\u0648\u0627\u0631 \u0644\u06d5\u0645\u06d5\u060c \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u0627\u0647\u06d5\u0642 \u0628\u0648\u0648\u06cc\u0646.',
    '\u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u0686\u06d5\u0646\u062f\u06cc\u0646 \u062a\u06d5\u0631\u0627\u0632\u0648\u0648\u06cc \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0648\u06d5\u06a9\u0627\u0631 \u062f\u06d5\u062e\u06d5\u06cc\u0646. \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u0642\u06d5\u062f \u0633\u06d5\u0646\u06af\u06cc \u062a\u06c6\u0645\u06cc \u0648\u0631\u062f\u06cc\u0644\u06d5\u06cc \u062f\u0627\u0646\u06d5 \u062e\u06d5\u0631\u062f\u06d5\u0644\u06cc\u0634 \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u0695\u0648\u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u06a9\u06d5\u0633 \u0646\u0627\u06a9\u0631\u06ce. \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5\u0633\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631 \u062e\u06c6\u0645\u0627\u0646 \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u0646.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0645\u06d5 \u0647\u06c6\u06cc \u0647\u06d5\u06b5\u0627\u0648\u0627\u0631\u062f\u0646\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u0627\u0695\u0648\u0648\u0646\u061b \u06a9\u06d5 \u0695\u06c6\u0634\u0646\u06cc \u0648 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5 \u0628\u06c6 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u0646 \u0648 \u0644\u06d5 \u0633\u06d5\u06b5\u0627 \u0695\u0627\u0628\u0648\u0648\u0646 \u062f\u06d5\u0644\u06d5\u0631\u0632\u0646.',
    '\u0626\u06d5\u0645\u06d5\u0634 \u067e\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u067e\u06cc\u0631\u06c6\u0632\u06d5 \u06a9\u06d5 \u0646\u0627\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062e\u06c6\u062a\u0627\u0646\u06cc \u0644\u06ce \u0646\u06d5\u0628\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0644\u06d5\u0645\u06d5\u0648\u0628\u06d5\u0631\u06cc\u0634 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0645\u0627\u0646 \u0644\u06d5 \u06a9\u0627\u0631\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627 \u06a9\u0631\u062f \u0648 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0644\u06ce\u06cc \u0628\u06d5 \u0626\u0627\u06af\u0627 \u0628\u0648\u0648\u06cc\u0646.',
    '\u0644\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u062f\u0627 \u06a9\u06d5 \u0628\u06d5 \u0626\u0627\u067e\u06cc \u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u06af\u0648\u062a: \u0626\u06d5\u0645 \u06a9\u06c6\u062a\u06d5\u06b5\u0627\u0646\u06d5 \u0686 \u06a9\u0627\u0631\u06d5\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u0646\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0627\u06a4 \u0648 \u06a9\u0627\u06b5\u06cc \u0626\u06ce\u0645\u06d5\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u062a\u0648\u0648 \u062f\u06d5\u0648\u06ce\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u06cc \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u062e\u06c6\u062a\u0627\u0646 \u06af\u0648\u0645\u0695\u0627 \u0628\u0648\u0648\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u0695\u0627\u0633\u062a\u06ce\u06a9\u062a \u0628\u06c6 \u0647\u0627\u0648\u0631\u062f\u06af\u06cc\u0646\u060c \u06cc\u0627\u0646 \u0647\u06d5\u0631 \u06af\u0627\u06b5\u062a\u06d5\u0645\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u061b \u06a9\u06d5 \u062e\u06c6\u06cc \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646 \u0648 \u0645\u0646\u06cc\u0634 \u0648\u06ce\u0695\u0627\u06cc \u0634\u0627\u06cc\u06d5\u062a\u0627\u0646\u0645.',
    '\u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u062e\u0648\u062f\u0627\u060c \u0647\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u067e\u0634\u062a \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06ce\u0631\u06d5 \u0644\u0627\u0686\u0646\u060c \u062f\u06d5\u0628\u06ce \u06af\u06d5\u0695\u06ce\u06a9 \u062f\u06d5\u0648 \u0628\u062a\u0627\u0646\u06d5\u0648 \u0628\u0647\u0627\u06b5\u06ce\u0646\u0645.',
    '\u06af\u0634\u062a\u06cc \u062a\u06cc\u06a9\u06d5\u062a\u06cc\u06a9\u06d5 \u06a9\u0631\u062f\u0646\u060c \u0628\u062a\u06d5 \u0645\u06d5\u0632\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0628\u06ce\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u06a9\u06ce \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u062e\u0648\u0627\u06a9\u0627\u0646\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061f \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u0628\u0648\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u06cc\u0633\u062a\u0648\u0648\u0645\u0627\u0646\u06d5 \u0644\u0627\u0648\u06ce\u06a9 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0648\u0627\u061b \u06a9\u06d5 \u0646\u0627\u0648\u06cc\u0634\u06cc \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06cc\u0647\u06ce\u0646\u0646 \u0628\u0627 \u062e\u06d5\u06b5\u06a9\u06cc\u0634 \u0686\u0627\u0648\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06ce\u060c \u0634\u0627\u06cc\u06d5\u062a \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0634\u0627\u06cc\u06d5\u062a \u0628\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u06cc \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645! \u062a\u06c6 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u062a \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0628\u062a\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u06a9\u0631\u062f\u061f',
    '\u06af\u0648\u062a\u06cc: \u0646\u06d5\u062e\u06ce\u0631\u060c \u06af\u06d5\u0648\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc \u06a9\u0631\u062f\u061b \u062f\u06d5 \u0626\u06d5\u0648\u06d5\u062a\u0627\u0646\u06ce \u0644\u06ce\u06cc \u067e\u0631\u0633\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u067e\u06d5\u0631\u0633\u06a4\u062a\u0627\u0646 \u0628\u062f\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0632\u06c6\u0631 \u0628\u06d5 \u062e\u06c6\u062f\u0627 \u0634\u06a9\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc\u0627\u0646: \u062f\u06cc\u0627\u0631\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0633\u062a\u06d5\u0645\u06a9\u0627\u0631\u0646.',
    '\u067e\u0627\u0634\u0627\u0646 \u0633\u06d5\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u0627\u062e\u0633\u062a: \u062e\u06c6\u062a \u062f\u06d5\u062a\u0632\u0627\u0646\u06cc \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0642\u0633\u06d5 \u0646\u0627\u06a9\u06d5\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u0627\u062e\u06c6 - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u0626\u06ce\u0648\u06d5 \u0634\u062a\u06ce \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646 \u06a9\u06d5 \u0646\u06d5 \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u0648 \u067e\u06ce \u062f\u06d5\u062f\u06d5\u0646\u060c \u0646\u06d5 \u0632\u06cc\u0627\u0646\u06cc\u0634\u0648\u0648 \u0644\u06ce \u062f\u06d5\u062f\u06d5\u0646\u061f',
    '\u0628\u06ce\u0632\u0645 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0633\u062a\u0627\u0648\u06d5\u0648 \u0644\u06d5\u0648\u0634\u062a\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632\u062e\u0648\u062f\u0627\u0646 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u0646. \u0628\u06c6 \u062a\u06ce\u0646\u0627\u06af\u06d5\u0646\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u0648\u0648 \u062f\u06ce\u060c \u0628\u06cc\u0633\u0648\u0648\u062a\u06ce\u0646\u0646 \u0648 \u062e\u0648\u062f\u0627\u06a9\u0627\u0646\u0648\u0648 \u06cc\u0627\u0631\u06cc \u0628\u062f\u06d5\u0646.',
    '\u06af\u0648\u062a\u0645\u0627\u0646: \u0626\u0627\u06af\u0631! \u062f\u06d5\u0628\u06ce \u0633\u0627\u0631\u062f \u0648 \u0628\u06ce \u0632\u06cc\u0627\u0646 \u0628\u06cc \u0628\u06c6 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645.',
    '\u0648\u06cc\u0633\u062a\u06cc\u0627\u0646 \u062a\u06d5\u0634\u0642\u06d5\u06b5\u06ce\u06a9\u06cc \u067e\u06ce \u0628\u06a9\u06d5\u0646\u060c \u0632\u06c6\u0631 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u062a\u0631\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u06cc\u0634 \u0648 \u00ab\u0644\u0648\u0648\u062a\u00bb \u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u0639\u06d5\u0631\u0632\u06d5 \u0646\u06d5\u062c\u0627\u062a \u062f\u0627 \u06a9\u06d5 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627 \u067e\u06cc\u062a \u0648 \u067e\u06cc\u0631\u06c6\u0632\u06cc\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627.',
    '\u0626\u06cc\u0633\u062d\u0627\u0642\u06cc\u0634\u0645\u0627\u0646 \u0647\u06d5\u0631 \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc \u0648 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628\u06cc\u0634 \u0632\u06ce\u062f\u06d5 \u0628\u06d5\u0634\u06cc \u0628\u06ce \u0648 \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646 \u0628\u06d5 \u0645\u06ce\u0631\u062e\u0627\u0633.',
    '\u06a9\u0631\u062f\u06cc\u0634\u0645\u0627\u0646\u0646 \u0628\u06d5 \u067e\u06ce\u0634\u06d5\u0648\u0627 - \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 - \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0628\u06a9\u06d5\u0646 \u0648 \u0628\u06d5 \u0647\u06d5\u0645\u0648\u0648\u0634\u06cc\u0627\u0646\u0645\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f \u0644\u06d5 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0646\u06d5\u0648\u06ce\u0633\u062a\u0646 \u0648 \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u062f\u06d5\u0646 \u0648\u060c \u06af\u0634\u062a\u06cc\u0627\u0646 \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a.',
    '\u00ab\u0644\u0648\u0648\u062a\u00bb \u06cc\u0634\u0645\u0627\u0646 \u0641\u06ce\u0631\u06cc \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0648 \u0632\u0627\u0646\u0633\u062a\u06cc \u06a9\u0631\u062f. \u0695\u0632\u06af\u0627\u0631\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f \u0644\u06d5\u0648 \u0634\u0627\u0631\u06d5\u06cc \u0628\u06ce \u0646\u0627\u0645\u0648\u0648\u0633\u06cc \u062a\u06ce\u062f\u0627 \u062f\u06d5\u06a9\u0631\u0627. \u062e\u06d5\u06b5\u06a9\u06cc \u0626\u06d5\u0648\u06ce \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u062e\u0631\u0627\u067e\u06cc \u0644\u06d5 \u0695\u06ce\u0644\u0627\u062f\u06d5\u0631 \u0628\u0648\u0648\u0646.',
    '\u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0634\u0645\u0627\u0646 \u062e\u0633\u062a. \u0626\u06d5\u0648 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646 \u0628\u0648\u0648.',
    '\u00ab\u0646\u0648\u0648\u062d\u00bb \u06cc\u0634 \u0647\u06d5\u0631 \u0648\u06d5\u0628\u06cc\u0631 \u0628\u06ce\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u0644\u06d5\u0648\u06d5\u067e\u06ce\u0634 \u06af\u0627\u0632\u06cc \u06a9\u0631\u062f\u06cc\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06cc\u06d5\u0648\u06d5 \u0647\u0627\u062a\u06cc\u0646\u061b \u062e\u06c6\u06cc \u0648 \u06a9\u06d5\u0633\u0648\u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0645\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u06b5\u0627\u06cc \u06af\u06d5\u0648\u0631\u06d5 \u0646\u06d5\u062c\u0627\u062a \u062f\u0627.',
    '\u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc\u062f\u0627 - \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0698\u0645\u0627\u0631\u062f - \u0641\u0631\u06cc\u0627\u06cc \u06a9\u06d5\u0648\u062a\u06cc\u0646. \u06af\u06d5\u0644\u06ce\u06a9\u06cc \u06af\u06d5\u0644\u06ce\u06a9 \u0628\u06d5\u062f\u0641\u06d5\u0695 \u0628\u0648\u0648\u0646\u061b \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0648 \u062e\u0646\u06a9\u0627\u0646\u062f\u0646.',
    '\u0644\u06d5 \u062f\u0627\u0648\u0648\u062f \u0648 \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0634 \u0628\u0627\u0633\u06ce\u06a9 \u0628\u06a9\u06d5\u060c \u06a9\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u06a9\u06ce\u0634\u06d5\u06cc \u06a9\u06ce\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 \u0695\u0627\u06af\u06d5\u06cc\u0634\u062a\u0646\u060c \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0626\u0627\u0698\u06d5\u06b5\u06cc \u06af\u06d5\u0644\u06ce\u06a9 - \u06a9\u0627\u062a\u06cc \u0634\u06d5\u0648\u06ce - \u067e\u06ce\u06cc \u0648\u06d5\u0631\u0628\u0628\u0648\u0648\u0646. \u0626\u06ce\u0645\u06d5 \u0626\u0627\u06af\u0627\u0646 \u0644\u06d5 \u067e\u06ce \u0695\u0627\u06af\u06d5\u06cc\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u0648\u0648.',
    '\u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646\u0645\u0627\u0646 \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f \u06a9\u06d5 \u062f\u06d5\u0628\u06ce \u0686 \u0628\u06a9\u0627\u061b \u0628\u06d5 \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9\u0627\u0646 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0648 \u0632\u0627\u0646\u0633\u062a\u06cc\u0645\u0627\u0646 \u062f\u0627 \u0648 \u06a9\u06ce\u0648\u06d5\u06a9\u0627\u0646 \u0648 \u0645\u06d5\u0644\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u06a9\u06d5\u0648\u06cc \u06a9\u0631\u062f \u06a9\u06d5 \u0648\u06ce\u0695\u0627\u06cc \u062f\u0627\u0648\u0648\u062f\u060c \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u062f\u06cc \u062e\u0648\u062f\u0627 \u0628\u062f\u06d5\u0646\u061b \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0634 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0695\u0627\u0628\u0648\u0648.',
    '\u0641\u06ce\u0631\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f \u0632\u0631\u06ce\u062a\u0627\u0646 \u0628\u06c6 \u062f\u0631\u0648\u0633 \u0628\u06a9\u0627\u060c \u062a\u0627 \u0644\u06d5 \u0634\u06d5\u0695\u062f\u0627 \u0628\u0648\u0648\u067e\u0627\u0631\u06ce\u0632\u06ce. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0634\u0648\u06a9\u0631\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0628\u06c6 \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646 \u0628\u0627\u06cc \u0628\u06d5\u0647\u06ce\u0632\u0645\u0627\u0646 \u062f\u06d5\u0633\u062a\u06d5\u0645\u06c6 \u06a9\u0631\u062f\u060c \u06a9\u06d5 \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0628\u06af\u0627\u062a\u06d5 \u0626\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u067e\u06cc\u062a\u0645\u0627\u0646 \u062a\u06ce\u062e\u0633\u062a\u0648\u0648\u06d5 \u0648 \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0645\u06d5\u0695 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0648\u0648\u06cc\u0646.',
    '\u0644\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u0646\u06cc\u0634 \u0628\u0627\u0632\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u062f\u06d5\u0686\u0648\u0648\u0646\u06d5 \u0628\u0646 \u0626\u0627\u0648\u06ce \u0648 \u06a9\u0627\u0631\u06cc \u062a\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u06a9\u0631\u062f. \u0647\u06d5\u0631 \u0626\u06ce\u0645\u06d5\u0634 \u062f\u06d5\u0645\u0627\u0646\u067e\u0627\u0631\u0627\u0633\u062a\u0646.',
    '\u06cc\u0627\u062f\u06ce \u0644\u06d5 \u0626\u06d5\u06cc\u06cc\u0648\u0628\u06cc\u0634 \u0628\u06a9\u06d5\u061b \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u06af\u0627\u0632\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u06a9\u0631\u062f\u060c \u06a9\u06d5: \u062a\u0648\u0648\u0634\u06cc \u0645\u06d5\u06cc\u0646\u06d5\u062a\u06ce \u0647\u0627\u062a\u0648\u0648\u0645\u060c \u0626\u06d5\u062a\u06c6\u0634 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06b5\u0633\u06c6\u0632\u0627\u0646 \u062f\u06b5\u0633\u06c6\u0632\u062a\u0631\u06cc.',
    '\u0646\u0632\u0627\u0645\u0627\u0646 \u0644\u06ce \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0631\u062f \u0648 \u0648\u06d5\u0632\u06d5\u0646\u0645\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0644\u0627\u0628\u0631\u062f \u0648 \u062e\u06ce\u0632\u0627\u0646\u06d5\u06a9\u06d5\u06cc\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627\u0648\u06d5 \u0648 \u0647\u06ce\u0646\u062f\u06d5\u06cc \u062a\u0631\u06cc\u0634 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646. \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0628\u06ce \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0648 \u067e\u06d5\u0646\u062f\u06ce\u06a9\u06cc\u0634 \u0628\u06ce \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0647\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646.',
    '[\u06cc\u0627\u062f\u06cc\u0634 \u0628\u06a9\u06d5 \u0644\u06d5] \u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644 \u0648 [\u0644\u06d5] \u0626\u06cc\u062f\u0631\u06cc\u0633 \u0648 \u0632\u06d5\u0644\u06a9\u06cc\u0641\u0644\u061b \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062e\u0633\u062a\u0646\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u0645\u0627\u062e\u06c6\u06cc \u0645\u0627\u0633\u06cc\u0634 \u0628\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06cc\u0631\u061b \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u0695\u06c6\u06cc\u06cc \u0648 \u0647\u06d5\u0631 \u067e\u06ce\u06cc \u0648\u0627\u0628\u0648\u0648 \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u0631\u06c6\u0633\u062a\u06cc \u0626\u06d5\u0648 \u0646\u0627\u06cc\u06d5\u06cc\u0646\u061b \u062a\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u062a\u0627\u0631\u06cc\u06a9\u0627\u06cc\u06cc\u062f\u0627 \u0647\u0627\u0648\u0627\u0631\u06cc \u06a9\u0631\u062f: \u0644\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062a\u06c6 \u0647\u06cc\u0686 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u06cc \u0634\u06a9 \u0646\u0627\u0628\u06d5\u0645\u060c \u067e\u0627\u06a9\u06cc \u0648 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u0628\u06c6 \u062a\u06c6\u06cc\u06d5\u060c \u0645\u0646 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u0628\u0648\u0648\u0645.',
    '\u0633\u0627 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u0647\u0627\u062a\u06cc\u0646\u060c \u0644\u06d5 \u062e\u06d5\u0645\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f. \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0628\u0627\u0633\u06ce\u06a9\u06cc \u0632\u06d5\u06a9\u06d5\u0631\u06cc\u0627\u0634 \u0628\u06a9\u06d5\u060c \u06a9\u06d5 \u0647\u0627\u0648\u0627\u0631\u06cc \u0628\u0631\u062f\u06d5 \u0628\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0647\u06d5\u0631 \u0628\u06d5 \u062a\u06d5\u0646\u06ce\u0645 \u0645\u06d5\u0647\u06ce\u06b5\u06d5\u0648\u06d5 \u0648 \u062a\u06c6 \u0628\u0627\u0634\u062a\u0631\u06cc\u0646 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u06a9\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u0645\u06ce\u0646\u06cc.',
    '\u0646\u0632\u0627\u0645\u0627\u0646 \u0644\u06ce \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0631\u062f \u0648 \u06cc\u06d5\u062d\u06cc\u0627\u0645\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u062e\u0634\u0634 \u062f\u0627\u06cc\u06d5 \u0648 \u0698\u0646\u06d5\u06a9\u06d5\u06cc\u0645\u0627\u0646 \u0686\u0627\u06a9 \u06a9\u0631\u062f\u06d5\u0648\u06d5. \u0626\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06d5\u062f\u0627 \u0628\u06d5 \u0647\u06d5\u06b5\u067e\u06d5 \u0628\u0648\u0648\u0646\u061b \u0628\u06d5 \u0647\u0648\u0645\u06ce\u062f \u0648 \u0628\u06d5 \u062a\u0631\u0633\u06d5\u0648\u06d5 \u0644\u06d5\u0628\u06d5\u0631\u0645\u0627\u0646 \u062f\u06d5\u067e\u0627\u0695\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u062d\u0627\u0646\u062f \u0626\u06ce\u0645\u06d5 \u0644\u0627\u0631\u06d5\u0645\u0644 \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648 \u067e\u06cc\u0631\u06d5\u06a9\u06d5\u0634 \u06a9\u06d5 \u0648\u0627 \u0634\u06d5\u0631\u0645\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u067e\u0627\u0631\u0627\u0633\u062a\u060c \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0647\u06c6\u06cc \u0695\u0648\u0648\u062d\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0641\u0648\u0648\u0645\u0627\u0646 \u062a\u06ce\u06a9\u0631\u062f\u061b \u062e\u06c6\u06cc \u0648 \u06a9\u0648\u0695\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0645\u06d5 \u0626\u0627\u06cc\u06cc\u0646\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5 \u0648 \u06cc\u06d5\u06a9 \u0626\u0627\u06cc\u06cc\u0646\u06d5. \u0645\u0646\u06cc\u0634 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u0645\u060c \u0626\u06d5\u0634\u06ce \u0647\u06d5\u0631 \u0645\u0646 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u0628\u06d5\u0634 \u0628\u06d5\u0634 \u06a9\u0631\u062f. \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0644\u0627\u06cc \u0645\u06d5 \u0648\u06d5\u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06d5 \u062e\u06d5\u0631\u06cc\u06a9 \u0628\u06ce \u0648 \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695 \u0628\u06ce\u060c \u062a\u06d5\u0642\u0627\u0644\u0627\u06cc \u0628\u06d5\u0641\u06cc\u0695\u06c6 \u0646\u0627\u0686\u06ce \u0648 \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06c6\u06cc \u062f\u06d5\u0646\u0648\u0648\u0633\u06cc\u0646.',
    '\u0647\u06d5\u0631 \u0634\u0627\u0631\u06ce \u06a9\u06d5 \u0642\u0695\u0645\u0627\u0646 \u062a\u06ce\u062e\u0633\u062a\u060c \u062a\u0627\u0632\u06d5 \u0646\u0627\u0634\u06ce \u0628\u06d5\u0631\u06d5\u0648 \u062f\u0648\u0648\u0627 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u062a\u0627 \u0626\u06d5\u0648\u0633\u0627 \u062f\u0631\u06af\u0627 \u0644\u06d5 \u0626\u0627\u062c\u0648\u0648\u062c \u0628\u0627\u062c\u0648\u0648\u062c\u0627\u0646 \u062f\u06d5\u06a9\u0631\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u0648\u06ce\u0646\u06cc \u0628\u06d5\u0631\u0632\u06d5\u0648\u06d5 \u062f\u0627\u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646.',
    '\u0628\u06d5\u06b5\u06ce\u0646\u06d5 \u0695\u0627\u0633\u062a\u06d5\u06a9\u06d5\u0634 \u0646\u0632\u06cc\u06a9 \u0628\u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 - \u0644\u06d5 \u067e\u0695 - \u0686\u0627\u0648\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u0631\u062f\u06d5\u067e\u06d5\u0695\u06ce \u0648 \u0626\u06ce\u0698\u0646: \u0648\u0627\u0648\u06d5\u06cc\u0644\u0627 \u0644\u06d5 \u062e\u06c6\u0645\u0627\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0648\u06d5 \u0628\u06ce \u0626\u0627\u06af\u0627 \u0628\u0648\u0648\u06cc\u0646\u061b \u0646\u06d5\u062e\u06ce\u0631 [\u0626\u0627\u06af\u0627\u0634\u06cc\u0627\u0646 \u067e\u06ce \u062f\u0627\u0628\u0648\u0648\u06cc\u0646] \u0628\u06d5\u06b5\u0627\u0645 \u062e\u06c6\u0645\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0645\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0626\u06ce\u0648\u06d5 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc - \u0644\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u06af\u0634 \u0626\u06ce\u0632\u0646\u06af\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u0646 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u0628\u0686\u0646\u06d5 \u0646\u0627\u0648\u06cc\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u0627\u0646\u06d5 [\u0648\u06d5\u06a9 \u062e\u06c6\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646] \u062e\u0648\u0627 \u0628\u0648\u0648\u0646\u0627\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0646\u06d5\u062f\u06d5\u0686\u0648\u0648\u0646\u06d5 \u0646\u0627\u0648\u06cc. \u0626\u06ce\u0633\u062a\u0627 \u0647\u06d5\u0645\u0648\u0648 - \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5 - \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627\u0646.',
    '\u0644\u06d5\u0648\u06ce \u0647\u06d5\u0646\u06cc\u0633\u06a9\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0647\u06cc\u0686 \u0646\u0627\u0628\u06cc\u0633\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u0628\u0695\u06cc\u0627\u0631\u0646 \u062f\u0627 \u06a9\u06d5 \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u062f\u06c6\u0698\u06d5\u0647 \u0628\u06d5\u062f\u0648\u0648\u0631\u0646.',
    '\u06a9\u06b5\u067e\u06d5\u06cc \u06a9\u06b5\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0627\u0646 \u06af\u0648\u06ce \u0644\u06ce \u0646\u0627\u0628\u06ce \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062f\u06b5\u06cc\u0627\u0646\u06d5 - \u0647\u06d5\u062a\u0627\u0647\u06d5\u062a\u0627 - \u0647\u06d5\u0631 \u0647\u06d5\u06cc\u0627\u0646\u06d5.',
    '\u062a\u0631\u0633\u06d5 \u0647\u06d5\u0631\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u06a9\u06d5 \u062e\u06d5\u0645\u0628\u0627\u0631\u06cc\u0627\u0646 \u0646\u0627\u06a9\u0627. \u0641\u0631\u0634\u062a\u0627\u0646\u06cc\u0634 \u062a\u0648\u0648\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0646: \u0626\u06d5\u0648\u0627 \u0626\u06d5\u0645\u0695\u06c6 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u062a\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0628\u0648\u0648.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06ce \u0644\u06d5 \u0686\u06d5\u0634\u0646\u06cc \u062a\u06c6\u0645\u0627\u0631\u06cc \u0642\u0627\u0642\u06d5\u0632 \u0628\u06c6 \u0646\u0648\u0648\u0633\u06cc\u0646\u06ce\u060c \u0647\u06d5\u06b5\u062f\u06d5\u067e\u06ce\u0686\u06cc\u0646. \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u067e\u06ce\u0634\u0648\u0648 \u062f\u0631\u0648\u0633\u0645\u0627\u0646 \u06a9\u0631\u062f\u060c \u062f\u06cc\u0633\u0627\u0646 \u0633\u0627\u0632\u06cc \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5. \u0628\u06d5\u06b5\u06ce\u0646\u06ce\u06a9\u06d5 \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0634\u06cc \u06a9\u06d5\u06cc\u0646.',
    '\u0644\u06d5 \u0632\u06d5\u0628\u0648\u0648\u0631\u062f\u0627\u060c - \u06a9\u06d5 \u0644\u06d5 \u067e\u0627\u0634 \u062a\u06d5\u0648\u0695\u0627\u062a \u0628\u0648\u0648 - \u0646\u0648\u0648\u0633\u06cc\u0646: \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u0639\u06d5\u0628\u062f\u06d5 \u0647\u06ce\u0698\u0627\u06a9\u0627\u0646\u06cc \u0645\u06d5 \u062f\u06d5\u0645\u06ce\u0646\u06ce.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5\u0645\u0627\u0646\u06d5\u062f\u0627 \u0628\u0627\u0646\u06af\u06d5\u0648\u0627\u0632\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5 \u0628\u06c6 \u06af\u06d5\u0644\u06cc \u062e\u0648\u062f\u0627\u067e\u06d5\u0631\u0633\u062a\u0627\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u0646\u0627\u0631\u062f \u06a9\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0648 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9 \u0628\u06cc \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0647\u06d5\u0631 \u067e\u06ce\u0645 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u0646\u0631\u06ce \u0628\u06ce\u0698\u0645 \u062e\u0648\u0627\u062a\u0627\u0646 \u06cc\u06d5\u06a9\u06d5. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc \u0633\u06d5\u0644\u0645\u06ce\u0646\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u06cc\u060c \u0628\u06ce\u0698\u06d5: \u0648\u0627 \u0645\u0646 \u0628\u06d5 \u06af\u0634\u062a\u0627\u0646\u0645 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u060c \u0628\u06d5 \u0628\u06ce \u062c\u06cc\u0627\u0648\u0627\u0632\u06cc\u061b \u0646\u0627\u0634\u06cc\u0632\u0627\u0646\u0645 \u0626\u06d5\u0648\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u0648\u0648 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5\u060c \u0632\u0648\u0648 \u062f\u06ce\u062a\u06d5 \u062c\u06ce \u06cc\u0627\u0646 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u062f\u06d5\u062e\u0627\u06cc\u06d5\u0646\u06ce.',
    '\u0647\u06d5\u0631 \u0642\u0633\u06ce\u06a9\u06cc \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u06cc\u062f\u0631\u06a9\u06ce\u0646\u0646 \u06cc\u0627 \u0648\u06d5\u06cc\u0634\u06ce\u0631\u0646\u060c \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u0646\u0627\u0634\u06cc\u0632\u0627\u0646\u0645\u060c \u0634\u0627\u06cc\u06d5\u062a \u0626\u06d5\u0645\u06d5 \u0644\u06d5\u0645\u06d5\u0695 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06ce\u06a9 \u0628\u06ce \u0648 \u0647\u06d5\u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648 \u0695\u0627\u06cc \u0628\u0628\u0648\u06ce\u0631\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u062a\u06c6 \u0686\u06c6\u0646 \u0695\u0627\u0633\u062a\u06d5 [\u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0626\u06ce\u0645\u06d5] \u0695\u0627\u0628\u06af\u06d5 \u0648\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646 \u062e\u0648\u062f\u0627\u06cc \u0695\u06c6\u0632\u06cc \u062f\u06d5\u0647\u0646\u062f\u06d5\u06cc\u06d5 \u0648 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0648\u062a\u0627\u0631\u06cc \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u062f\u06d5\u062e\u0648\u0627\u0632\u06cc\u0646.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u0628\u062a\u0631\u0633\u0646\u060c \u0628\u0648\u0648\u0645\u06d5\u0644\u06d5\u0631\u0632\u06d5\u06cc \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0695\u0648\u0648\u062f\u0627\u0648\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u06af\u0631\u06cc\u0646\u06af\u06d5.',
    '\u0695\u06c6\u0698\u06ce \u0686\u0627\u0648\u062a\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u060c \u06af \u0634\u06cc\u0631\u062f\u06d5\u0631\u06ce\u06a9 \u0634\u06cc\u0631\u06d5\u062e\u06c6\u0631\u06d5\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u062f\u06d5\u0686\u06ce \u0648 \u0647\u06d5\u0631\u0686\u06cc \u062a\u06c6\u0644\u06cc \u0644\u06d5 \u0632\u06af\u062f\u0627\u06cc\u06d5 \u0628\u06d5\u0631\u062f\u0627\u0648\u06ce\u0698\u06ce. \u0648\u0627\u062f\u06d5\u0632\u0627\u0646\u0646 \u0647\u06d5\u0645\u0648\u0648 \u0626\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5 \u0633\u06d5\u0631\u062e\u06c6\u0634\u0646\u061b \u062e\u06c6 \u0645\u06d5\u0633\u062a\u06cc\u0634 \u0646\u06cc\u0646\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0626\u0627\u0632\u0627\u0631 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631\u06d5.',
    '\u0647\u06ce\u0646\u062f\u06ce \u06a9\u06d5\u0633 \u0647\u06d5\u0646\u060c \u0628\u06d5 \u0628\u06ce \u0626\u06d5\u0648\u06d5 \u0647\u06cc\u0686 \u062a\u06ce\u06af\u06d5\u06cc\u0628\u0646 \u06a9\u06ce\u0634\u06d5\u06cc \u0644\u06d5\u0645\u06d5\u0695 \u062e\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u062f\u0648\u0648\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06ce\u06a9\u06cc \u0628\u06d5\u062f\u0641\u06d5\u0695.',
    '\u0628\u06c6 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0628\u0695\u06cc\u0627\u0631 \u062f\u0631\u0627\u0648\u06d5 \u0648 \u0628\u06c6\u06cc \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u0628\u06ce\u062a\u06d5 \u062f\u06c6\u0633\u062a\u06cc\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0648 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u06cc \u062f\u06d5\u062f\u0627 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u0627\u06af\u0631\u06cc \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648\u0628\u0648\u0646\u06d5\u0648\u06d5 \u0628\u06d5\u06af\u0648\u0645\u0627\u0646\u0646\u060c \u0633\u0627 \u0628\u0632\u0627\u0646\u0646 \u0626\u06ce\u0645\u06d5 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u060c \u0647\u06d5\u0648\u06d5\u06b5 \u0644\u06d5 \u0626\u0627\u062e\u060c \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u062a\u0646\u06c6\u06a9\u06d5\u06cc\u06d5\u06a9 \u0648 \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u062c\u06d5\u06b5\u062a\u06d5\u062e\u0648\u06ce\u0646\u06ce\u06a9 \u0648 \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u067e\u0644\u06d5 \u06af\u06c6\u0634\u062a\u06ce\u06a9\u06cc \u0628\u06cc\u0686\u0645 \u067e\u06ce\u062f\u0631\u0627\u0648\u060c \u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u062f\u0631\u0627\u0648\u060c \u062a\u0627 \u067e\u06ce\u062a\u0627\u0646\u06cc \u0628\u0633\u06d5\u0644\u0645\u06ce\u0646\u0646. \u0686\u06cc\u0645\u0627\u0646 \u0628\u0648\u06ce \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u0646\u0627\u0648 \u067e\u0632\u062f\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u067e\u0627\u0634\u0627\u0646 \u0628\u06d5 \u0633\u0627\u0648\u0627\u06cc\u06cc \u062f\u06d5\u0648\u0647\u06ce\u0646\u06cc\u0646\u06d5 \u062f\u06d5\u0631\u06ce\u061b \u0626\u06d5\u0648\u0633\u0627 [\u0645\u0627\u0648\u06d5\u062a\u0627\u0646 \u067e\u06ce\u062f\u06d5\u062f\u06d5\u06cc\u0646] \u0647\u06d5\u062a\u0627 \u062f\u06d5\u06af\u06d5\u0646\u06d5 \u062a\u0648\u0627\u0646\u0627\u06cc\u06cc. \u0647\u06ce\u0646\u062f\u06ce \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0645\u0631\u0646 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u062a\u0627\u0646 \u062f\u06d5\u062e\u0631\u06ce\u0646\u06d5 \u067e\u06cc\u0631\u06cc \u0648 \u06a9\u06d5\u0644\u06d5\u0644\u0627\u06cc\u06cc\u060c \u0647\u06d5\u062a\u0627 \u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0644\u06d5 \u0628\u06cc\u0631\u06cc \u0628\u06a9\u0627. \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0634 \u062f\u06d5\u0628\u06cc\u0646\u06cc - \u0628\u06ce \u0647\u06d5\u0633\u062a \u0648 \u062e\u0648\u0633\u062a\u06d5 - \u0647\u06d5\u0631\u06af\u0627 \u0628\u0627\u0631\u0627\u0646\u0645\u0627\u0646 \u0644\u06ce \u0628\u0627\u0631\u0627\u0646\u062f\u060c \u0628\u06d5 \u062e\u06c6\u062f\u0627 \u062f\u06ce \u0648 \u06af\u06d5\u0634\u06d5 \u062f\u06d5\u06a9\u0627 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u062c\u0648\u0648\u062a\u06ce\u06a9\u06cc \u062f\u06cc\u0645\u06d5\u0646 \u062c\u0648\u0627\u0646\u060c \u0695\u0648\u0648\u06d5\u06a9 \u0644\u06d5 \u062e\u06c6\u06cc\u062f\u0627 \u062f\u06d5\u0695\u0648\u06ce\u0646\u06ce.',
    '\u0626\u06d5\u0645\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u0633 \u06a9\u06d5 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0642\u06d5 \u0648 \u0626\u06d5\u0645\u06cc\u0634\u06d5 \u0645\u0631\u06cc\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0648\u06d5\u062f\u06cc \u062f\u06ce \u0648 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5\u06af\u06b5\u06a9\u06c6\u062f\u0627\u0646 - \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 - \u062f\u06d5\u0631\u062f\u06d5\u062e\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u0646\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u0627 \u0647\u06cc \u0648\u0627\u0647\u06d5\u06cc\u06d5\u060c \u0644\u06d5\u0645\u06d5\u0695 \u062e\u0648\u062f\u0627 \u06a9\u06ce\u0634\u06d5 \u062f\u06d5\u06a9\u0627\u060c \u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u0686\u06cc \u0644\u06ce \u0628\u0632\u0627\u0646\u06ce\u061b \u0646\u06d5 \u0647\u06cc\u0686 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u06a9\u062a\u06ce\u0628\u06ce \u06a9\u06d5 \u0628\u06c6\u06cc \u0695\u06c6\u0634\u0646 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0628\u06d5 \u0641\u06cc\u0632 \u0645\u0644 \u0628\u06d5\u0644\u0627\u0648\u06d5 \u062f\u06d5\u0646\u06ce\u060c \u062a\u0627 \u062e\u06d5\u06b5\u06a9\u06cc \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u06a9\u0644\u0627 \u0628\u06a9\u0627. \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0628\u06d5\u0634\u06cc \u0647\u06d5\u0631 \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u06d5 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0698\u0627\u0646\u06cc \u0633\u0648\u0648\u062a\u0627\u0646\u06ce \u062f\u06d5\u0686\u06ce\u0698\u06ce.',
    '\u0626\u06d5\u0645\u06d5 \u0633\u0632\u0627\u06cc \u0626\u0627\u06a9\u0627\u0631 \u0648 \u06a9\u0631\u062f\u0627\u0631\u06d5\u06a9\u06d5\u062a\u06d5\u061b \u062e\u0648\u062f\u0627\u0634 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646 \u0646\u0627\u06a9\u0627.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u06d5\u062f\u0627 \u0626\u06cc \u0648\u0627\u0647\u06d5\u0646\u060c \u0628\u06d5 \u062f\u0648\u0648\u062f\u06b5\u06cc \u062e\u0648\u0627\u067e\u06d5\u0631\u0633\u062a\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9\u06cc \u062a\u0648\u0648\u0634 \u0647\u0627\u062a\u060c \u0628\u06d5\u0648\u06d5 \u0644\u06d5 \u062e\u0648\u0627 \u062f\u06b5\u0646\u06cc\u0627\u06cc\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u06b5\u0627\u0634\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u060c \u0628\u06d5\u0631\u06d5\u0648 \u067e\u0627\u0634 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5. \u062f\u0646\u06cc\u0627\u0634 \u0648 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634\u06cc \u0686\u0648\u0648\u060c \u062f\u0648\u0648\u0633\u06d5\u0631\u06d5 \u0632\u06d5\u0631\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5.',
    '\u0644\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627\u060c \u0647\u0627\u0646\u0627 \u0628\u06c6 \u0634\u062a\u06ce \u0648\u0627 \u062f\u06d5\u0628\u0627\u060c \u06a9\u06d5 \u0646\u06d5 \u0632\u06cc\u0627\u0646\u06cc \u0644\u06ce \u062f\u06d5\u062f\u0627 \u0646\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc\u0634\u06cc \u067e\u06ce \u062f\u06d5\u062f\u0627. \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u0644\u06d5 \u0695\u06ce \u0647\u06d5\u062a\u06b5\u06d5 \u0628\u0648\u0648\u0646.',
    '\u0647\u0627\u0646\u0627 \u0628\u06c6 \u06a9\u06d5\u0633\u06ce \u0648\u0627\u062f\u06d5\u0628\u0627\u060c \u0632\u06cc\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u0646\u0632\u06cc\u06a9\u062a\u0631\u06d5. \u0626\u0627\u06cc \u0686 \u06cc\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u062f\u06a9\u0627\u0631\u06d5 \u0648 \u0686 \u0647\u0627\u0648\u062f\u06d5\u0645\u06ce \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0627\u0646\u0628\u0627\u062a\u06d5 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u0686\u06c6\u0646\u06ce\u06a9\u06cc \u0628\u0648\u06ce \u062f\u06d5\u06cc\u06a9\u0627.',
    '\u06a9\u06ce \u067e\u06ce\u06cc \u0648\u0627\u06cc\u06d5 \u06a9\u06d5 \u062e\u0648\u0627 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0648 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc [\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631] \u0646\u0627\u06cc\u06d5\u060c \u0628\u0627 \u0628\u06d5 \u067e\u06d5\u062a\u06ce\u06a9 \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u0648\u0627\u0633\u06ce \u062a\u0627 \u062f\u06d5\u062e\u0646\u06a9\u06ce\u061b \u062f\u06d5\u0628\u0627 \u0628\u0632\u0627\u0646\u06ce \u0628\u06d5\u0645 \u0641\u06ce\u06b5\u06d5 \u0626\u06c6\u062e\u06d5\u06cc \u062f\u06b5\u06cc \u062f\u06ce\u061f',
    '\u0628\u06d5\u0648 \u0626\u0627\u0648\u0627\u06cc\u06d5\u0634 \u0626\u06d5\u0648 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u060c \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06af\u06d5\u0644\u06ce\u06a9\u06cc \u0695\u06c6\u0634\u0646 \u06a9\u06d5\u0631\u06d5\u0648\u06d5\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u0648\u0627 \u0628\u06cc\u06d5\u0648\u06ce\u060c \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0648\u0648\u0646\u06d5 \u062c\u0648\u0648\u0644\u06d5\u06a9\u06d5 \u0648 \u0633\u0648\u0628\u0628\u06cc \u0648 \u0645\u06d5\u0633\u06cc\u062d\u06cc \u0648 \u0645\u06d5\u062c\u0648\u0648\u0633\u06cc \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0646\u0627\u0633\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u062a\u06c6 \u0646\u0627\u0628\u06cc\u0646\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0648\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646 \u0648 \u062e\u06c6\u0631 \u0648 \u0647\u06d5\u06cc\u06a4 \u0648 \u0626\u06d5\u0633\u062a\u06ce\u0631\u06d5\u06a9\u0627\u0646 \u0648 \u06a9\u06c6\u0633\u0627\u0631 \u0648 \u062f\u0631\u06d5\u062e\u062a\u0627\u0646 \u0648 \u062c\u0627\u0646\u06d5\u0648\u06d5\u0631 \u0648 \u0632\u06c6\u0631 \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0633\u0648\u0698\u062f\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06d5\u0646\u061f \u0632\u06c6\u0631\u06ce\u06a9\u06cc\u0634 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u0695\u06cc\u0627\u0631\u062f\u0631\u0627\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u06a9\u06ce \u062a\u0648\u0648\u0634\u06cc \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06a9\u0627\u060c \u062a\u0627\u0632\u06d5 \u06a9\u06d5\u0633 \u0695\u06ce\u0632\u06cc \u0644\u06ce \u0646\u0627\u0646\u06ce. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u0686\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0628\u06ce \u062f\u06d5\u06cc\u06a9\u0627.',
    '\u0626\u06d5\u0645 \u062f\u0648\u0648\u0627\u0646\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646\u06d5: \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0648\u0648\u0646\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u060c \u06a9\u0631\u0627\u0633\u06ce\u06a9\u06cc \u0626\u0627\u06af\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0698\u0646\u06cc \u0628\u0695\u0627\u061b \u0626\u0627\u0648\u06cc \u06af\u06d5\u0631\u0645\u06cc \u062f\u0627\u062a\u0648\u06b5\u06ce\u0646\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u0695\u0627 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u06a9\u0631\u06ce.',
    '\u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0626\u0627\u0648\u06d5 \u0642\u0698\u06a9\u0648\u06b5\u0627\u0648\u06d5\u060c \u0647\u06d5\u0646\u0627\u0648\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06ce\u0633\u062a\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u062f\u0627\u062f\u06d5\u0628\u06d5\u06b5\u06c6\u062e\u06ce.',
    '\u0628\u06d5\u0631 \u06af\u0648\u0631\u0632\u06cc \u0626\u0627\u0633\u0646\u06cc\u0634 \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u062a\u0627 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06ce \u0644\u06d5 \u0645\u06d5\u06cc\u0646\u06d5\u062a \u0695\u0632\u06af\u0627\u0631 \u0628\u0628\u0646\u060c \u0647\u06d5\u0633 \u062f\u06d5\u062f\u0631\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0646\u0627\u0648\u06cc: \u062f\u06d5\u06cc \u0698\u0627\u0646\u06cc \u0626\u0627\u06af\u0631 \u0628\u0686\u06ce\u0698\u0646!',
    '\u0626\u06d5\u0648\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0627\u0646\u0628\u0627\u062a\u06d5 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627. \u062e\u0634\u06b5\u06cc\u0627\u0646 \u0628\u0627\u0632\u0646\u06d5\u06cc \u0644\u06d5 \u0632\u06ce\u0695 \u0648 \u0645\u0631\u0648\u0627\u0631\u06cc\u06cc\u06d5 \u0648 \u0628\u06d5\u0631\u06af\u06cc \u0628\u06d5\u0631\u06cc\u0627\u0646 \u0647\u06d5\u0648\u0631\u06d5\u0645\u06cc\u0634\u06d5.',
    '\u0628\u06d5\u0631\u06d5\u0648 \u0648\u062a\u0627\u0631\u06cc \u067e\u0627\u06a9\u0698 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627\u067e\u06d5\u0633\u0646\u062f\u060c \u0695\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u062f\u0631\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 \u0648 \u062f\u06d5\u0628\u0646\u06d5 \u0644\u06d5\u0645\u067e\u06d5\u0631 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0626\u06d5\u0648 \u00ab\u0645\u0633\u062c\u062f\u0627\u0644\u062d\u0631\u0627\u0645\u00bb \u06d5\u06cc \u06a9\u06d5 \u06a9\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u06d5\u062a\u06d5 [\u067e\u06d5\u0631\u0633\u06af\u06d5] \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9 - \u0686 \u0646\u06cc\u0634\u062a\u06d5\u062c\u06ce \u0648 \u0686 \u063a\u06d5\u0648\u0627\u0631\u06d5 - [\u062a\u0648\u0648\u0634\u06cc \u0633\u0632\u0627\u06cc \u062f\u0698\u0648\u0627\u0631 \u062f\u06d5\u0628\u0646]. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06cc \u0628\u06a9\u0627\u0648 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u0627\u060c \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646\u06cc \u067e\u06ce \u062f\u06d5\u0686\u06ce\u0698\u06cc\u0646.',
    '\u0626\u06d5\u0648\u062d\u0627\u0646\u06d5 \u06a9\u06d5 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0645\u0627\u0646 \u0644\u06d5 \u062c\u06ce \u0626\u06d5\u0645 \u0645\u0627\u06b5\u06d5 \u0626\u06d5\u0646\u0648\u0627\u062f\u0627\u060c \u06a9\u06d5: \u0646\u06d5\u06a9\u06d5\u06cc \u06a9\u06d5\u0633 \u0628\u06a9\u06d5\u06cc \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0645\u0646 \u0648 \u0645\u0627\u06b5\u06cc \u0645\u0646 \u062e\u0627\u0648\u06ce\u0646 \u06a9\u06d5\u0631\u06d5\u0648\u06d5\u060c \u0628\u06c6 \u062f\u06d5\u0648\u0631\u06d5\u062f\u06d5\u0631 \u0648 \u0648\u06ce\u0633\u062a\u0627\u0648 \u0648 \u06a9\u0695\u0646\u06c6\u0634\u06a9\u06d5\u0631 \u0648 \u0633\u0648\u0698\u062f\u06d5\u0628\u06d5\u0631\u0627\u0646.',
    '\u0628\u0627\u0646\u06af\u06d5\u0648\u0627\u0632\u06cc \u062e\u06d5\u06b5\u06a9 \u0628\u06a9\u06d5 \u0628\u06ce\u0646\u06d5 \u0632\u06cc\u0627\u0631\u06d5\u062a\u061b \u0628\u0627 \u0628\u06d5 \u067e\u06cc\u0627\u062f\u06d5\u06cc\u06cc \u0648 \u0628\u06d5 \u0633\u0648\u0627\u0631\u06cc\u060c \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a \u0644\u06d5\u0633\u06d5\u0631 \u0686\u0627\u0631\u06d5\u0648\u06ce\u06cc \u0644\u06d5 \u062c\u06d5\u0631\u06cc\u0634\u0695\u0627\u060c \u0644\u06d5 \u0695\u06ce\u06cc \u062f\u0648\u0648\u0631 \u0648 \u062f\u0631\u06ce\u0698\u06cc\u0634\u0695\u0627\u060c \u0628\u06d5 \u062a\u06c6 \u0628\u06af\u06d5\u0646.',
    '\u0628\u0627 \u0628\u06ce\u0646\u06d5 \u0628\u06d5\u0647\u0631\u06d5 \u0628\u0631\u062f\u0646\u06ce \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0644\u06d5 \u0686\u06d5\u0646\u062f \u0695\u06c6\u0698\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u060c \u0644\u06d5\u0633\u06d5\u0631 \u0648\u06b5\u0633\u0627\u062a\u06ce \u067e\u06ce\u06cc \u062f\u0627\u0648\u0646\u060c \u06cc\u0627\u062f\u06cc \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646. \u062e\u06c6\u062a\u0627\u0646 \u0644\u06d5 \u06af\u06c6\u0634\u062a\u06cc\u0627\u0646 \u0628\u062e\u06c6\u0646 \u0648 \u0628\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0644\u06ce\u06cc \u0628\u062e\u06c6\u0646 \u06a9\u06d5 \u062a\u0627\u0645\u06d5\u0632\u0631\u06c6\u0646 \u0648 \u0647\u06d5\u0698\u0627\u0631\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5 \u06af\u06d5\u0645\u0627\u0631 \u0644\u06d5 \u062e\u06c6 \u062f\u0648\u0648\u0631\u062e\u06d5\u0646\u06d5\u0648\u06d5 \u0648 \u0646\u06d5\u062f\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u067e\u06ce\u06a9 \u0628\u06ce\u0646\u0646 \u0648 \u0628\u0627 \u062e\u0648\u0644 \u0628\u062e\u06c6\u0646 \u0628\u06d5 \u062f\u06d5\u0648\u0631\u06cc \u0645\u0627\u06b5\u06cc \u06a9\u06d5\u0648\u0646\u0627\u0631\u0627.',
    '\u0626\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062f\u06d5\u0633\u062a\u0648\u0648\u0631\u0627\u062a\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5\u0695\u06ce\u0632 \u0628\u06af\u0631\u06ce\u060c \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u06d5\u0648\u06d5 \u0628\u06c6\u06cc \u0686\u0627\u062a\u0631\u06d5. \u067e\u0627\u062a\u0627\u06b5\u06cc\u0634\u062a\u0627\u0646 \u0628\u06c6 \u0695\u06d5\u0648\u0627\u06cc\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u06a9\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u06d5\u0646\u0631\u06ce. \u0644\u06d5 \u06af\u06d5\u0645\u0627\u0631\u06cc \u0628\u062a\u06d5\u06a9\u0627\u0646 \u062f\u0648\u0648\u0631\u06d5\u067e\u06d5\u0631\u06ce\u0632 \u0628\u0646\u061b \u0644\u06d5 \u062f\u0631\u06c6\u0634 \u062e\u06c6\u0628\u067e\u0627\u0631\u06ce\u0632\u0646.',
    '\u0628\u06d5 \u062f\u06b5\u06cc \u067e\u0627\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0628\u0646\u061b \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0628\u0646\u0627\u0633\u06ce\u060c \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0647\u06d5\u06b5\u062f\u06ce\u0631\u0627\u0648\u06d5 \u0648 \u0645\u06d5\u0644 \u062a\u06ce\u06cc \u0695\u06c6\u0628\u06ce\u0646\u060c \u06cc\u0627\u0646 \u0628\u0627\u06cc\u06d5\u06a9 \u0647\u06d5\u06b5\u06cc\u062f\u0627\u0634\u062a\u0628\u06ce \u0628\u06c6 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u062f\u0648\u0648\u0631\u06cc \u0686\u06d5\u067e\u06d5\u06a9.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0626\u06d5\u0648 \u067e\u06ce\u0648\u062f\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627 \u062f\u0627\u06cc\u0646\u0627\u0648\u06d5 \u0695\u06ce\u0632 \u0644\u06ce \u0628\u06af\u0631\u06ce\u060c \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u060c \u06a9\u06d5 \u0628\u06d5 \u062f\u06b5 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u062a\u0631\u0633\u06ce.',
    '\u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06ce \u0646\u0627\u0648\u062f\u06ce\u0631 \u06a9\u0631\u0627\u0648 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0648\u06b5\u0633\u0627\u062a\u0648\u0648 \u067e\u06ce\u062f\u06d5\u06af\u0627\u060c \u0626\u06d5\u0648\u0633\u0627 \u062c\u06ce\u06cc \u0633\u06d5\u0631\u0698\u06ce \u06a9\u0631\u062f\u0646\u06cc\u0627\u0646 \u0644\u0627\u06cc \u0645\u0627\u06b5\u06d5 \u06a9\u06d5\u0648\u0646\u0627\u0631\u0627\u06a9\u06d5\u06cc\u06d5.',
    '\u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u06cc\u0646\u0627\u0646 \u06cc\u0627\u0633\u0627\u06cc \u0642\u0648\u0631\u0628\u0627\u0646\u06cc \u06a9\u0631\u062f\u0646\u0645\u0627\u0646 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u062f\u061b \u062a\u0627\u0648\u06d5\u06a9\u0648\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0648\u06b5\u0633\u0627\u062a\u06ce \u06a9\u06d5 \u0628\u06c6 \u0695\u06c6\u0632\u06cc \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0648\u0646\u060c \u0626\u06d5\u0648\u0627\u0646 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627 \u0628\u06ce\u0646\u0646. \u062e\u0648\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u062a\u0627\u06a9 \u0648 \u062a\u06d5\u0646\u06cc\u0627\u06cc\u06d5\u061b \u062e\u06c6\u062a\u0627\u0646 \u0628\u062f\u06d5\u0646\u06d5 \u062f\u06d5\u0633\u062a \u0626\u06d5\u0645 \u0648 \u0645\u0698\u062f\u06d5 \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0628\u062f\u06d5 \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u06a9\u06d5\u0645 \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0647\u06d5\u062a\u0627 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06cc\u06d5\u0646\u060c \u062f\u06b5\u06cc\u0627\u0646 \u062a\u0631\u0633\u06cc \u0695\u06ce \u062f\u06d5\u0646\u06cc\u0634\u06ce \u0648 \u0647\u06d5\u0631\u06af\u0627 \u0628\u06d5\u06b5\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u060c \u062e\u06c6\u0695\u0627\u06af\u0631\u0646 \u0648 \u0646\u0648\u06ce\u0698 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0644\u06d5\u0648 \u0695\u06c6\u0632\u06cc \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u0646 \u062f\u0627\u0648\u0646\u060c \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc \u0628\u06d5\u062e\u062a \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u06c6\u0645\u0627\u0646 \u062f\u0627\u0646\u0627\u0646\u060c \u06a9\u06d5 \u0648\u0634\u062a\u0631\u06cc \u0642\u0648\u0631\u0628\u0627\u0646\u06cc \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5\u0631 \u06cc\u0627\u0633\u0627\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u061b \u0626\u06ce\u0648\u06d5\u0634 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u0628\u06d5\u0646. \u062a\u0627 \u0628\u06d5 \u067e\u06ce\u0648\u06d5\u0646\u060c \u0646\u0627\u0648\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06ce\u0646\u0646. \u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0634\u062a \u06a9\u06d5\u0648\u062a\u0646 \u0648 \u0633\u06d5\u0631\u0698\u06ce \u06a9\u0631\u0627\u0646\u060c \u0644\u06ce\u06cc\u0627\u0646 \u0628\u062e\u06c6\u0646\u061b \u062a\u0627\u0645\u06d5\u0632\u0631\u06c6\u06cc \u0634\u06d5\u0631\u0645\u06ce\u0648\u0646\u06cc\u0634 \u0648 \u067e\u0627\u0631\u0633\u06d5\u06a9\u06cc \u0644\u06ce \u0628\u06ce \u0628\u06d5\u0634 \u0645\u06d5\u06a9\u06d5\u0646. \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u0645\u06c6\u06a9\u0631\u062f\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631 \u0628\u0646.',
    '\u06af\u06c6\u0634\u062a \u0648 \u062e\u0648\u06ce\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0646\u0627\u0686\u06ce\u061b \u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0633\u062a\u06cc \u062a\u0631\u0633\u0648\u0648 \u0644\u06d5 \u062e\u0648\u0627\u06cc\u06d5 \u0628\u06d5\u0648 \u062f\u06d5\u06af\u0627. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u0627\u0648\u06d5\u0647\u0627\u06cc\u0646\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0645 \u06a9\u0631\u062f\u061b \u0647\u06d5\u062a\u0627 \u0628\u0627\u0633\u06cc \u0645\u06d5\u0632\u0646\u0627\u06cc\u06d5\u062a\u06cc \u0648 \u0633\u0648\u067e\u0627\u0633\u06cc \u062e\u0648\u062f\u0627\u06cc \u062e\u06c6 \u0628\u06a9\u06d5\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0645 \u0695\u06ce\u06af\u06d5\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0648\u0646. \u0645\u0698\u062f\u06d5\u0634 \u0628\u06d5 \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u0628\u062f\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0686\u0648\u0646\u06a9\u06d5 \u062e\u0648\u062f\u0627 \u06af\u0634 \u062e\u06d5\u06cc\u0627\u0646\u06cc \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u06cc \u0646\u0627\u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0634\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0633\u06d5\u067e\u0627\u0646\u062f\u0648\u0648\u0646\u060c \u0686\u0648\u0646\u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u0644\u06ce\u06a9\u0631\u0627\u0648\u06d5\u060c \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u062a\u06c6\u06b5\u06d5\u06cc \u062e\u06c6\u0648\u06d5\u06a9\u06d5\u0646. \u062e\u0648\u062f\u0627\u0634 \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0633\u06d5\u0631\u06cc\u0627\u0646 \u062e\u0627.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u0632\u06ce\u062f\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0631\u06a9\u0631\u0627\u0646\u060c \u06af\u0648\u0646\u0627\u0647\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0628\u0648\u0648 \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646 \u0648\u062a: \u062e\u0648\u062f\u0627 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u06cc \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u0695\u0627\u0648 \u0646\u06d5\u0646\u0627\u0628\u0627\u060c \u062f\u06ce\u0631 \u0648 \u06a9\u0644\u06cc\u0633\u0627 \u0648 \u06a9\u06d5\u0646\u0634\u062a \u0648 \u0645\u0632\u06af\u06d5\u0648\u062a\u06d5\u06a9\u0627\u0646 - \u06a9\u06d5 \u0644\u06d5\u0648\u0627\u0646\u062f\u0627 \u0641\u0631\u06d5 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u0631\u06ce - \u0648\u06ce\u0631\u0627\u0646 \u062f\u06d5\u06a9\u0631\u0627\u0646. \u062e\u0648\u0627 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06ce \u06a9\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u062f\u06d5\u0646\u061b \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u062e\u0648\u0627 \u0632\u06c6\u0631 \u0628\u06d5\u0647\u06ce\u0632 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0645 \u0632\u06d5\u0648\u06cc\u0646\u06d5\u062f\u0627 \u062f\u0627\u06cc\u0627\u0646\u0645\u06d5\u0632\u0631\u06ce\u0646\u06cc\u0646\u060c \u0646\u0645\u06ce\u0698\u06a9\u06d5\u0631 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u0631\u0646 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0628\u06d5\u0631 \u0644\u06d5 \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e \u062f\u06d5\u06af\u0631\u0646. \u0633\u06d5\u0631\u0626\u06d5\u0646\u062c\u0627\u0645\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u0627\u06cc\u06d5.',
    '\u06af\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0626\u06d5\u062a\u06c6 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646 \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u067e\u06ce\u0634 \u0626\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d \u0648 \u0639\u0627\u062f \u0648 \u0633\u06d5\u0645\u0648\u0648\u062f\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5 \u067e\u06cc\u0634\u06d5\u06cc\u0627\u0646 \u0628\u0648\u0648\u06d5.',
    '\u062f\u06cc\u0633\u0627\u0646 \u0647\u06c6\u0632\u06cc \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0648 \u0647\u06c6\u0632\u06cc \u0644\u0648\u0648\u062a\u06cc\u0634.',
    '\u062e\u06d5\u06b5\u06a9\u06cc \u0645\u06d5\u062f\u06cc\u06d5\u0646\u06cc\u0634 \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627. \u0645\u0648\u0648\u0633\u0627\u0634 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646 \u0632\u0627\u0646\u062f\u0631\u0627. \u0626\u06d5\u0648\u0633\u0627 \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0648\u0627\u0632\u0645\u0627\u0646 \u0644\u06d5\u0648 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5 \u0647\u06ce\u0646\u0627\u060c \u067e\u0627\u0634\u0627\u0646 \u063a\u06d5\u0632\u06d5\u0628\u0645 \u0644\u06ce \u06af\u0631\u062a\u0646\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0686\u06c6\u0646 \u063a\u06d5\u0632\u06d5\u0628 \u06af\u0631\u062a\u0646\u06ce\u06a9!',
    '\u0626\u0627\u06cc \u06a9\u06d5 \u0642\u0695\u0645\u0627\u0646 \u062f\u06d5\u0686\u06d5\u0646\u062f \u0634\u0627\u0631\u06cc \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u062e\u0633\u062a\u061b \u062f\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0628\u06d5\u0631\u062f\u0627 \u062f\u0631\u0627\u061b \u062f\u06d5\u0648\u0631\u06cc \u0686\u0627\u06b5\u0627\u0648\u06cc\u0627\u0646 \u0686\u06c6\u06b5 \u06a9\u0631\u0627\u061b \u06a9\u06c6\u0634\u06a9\u06cc \u067e\u062a\u06d5\u0648\u06cc\u0627\u0646 \u062a\u06ce\u06a9 \u062f\u0631\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0647\u06ce\u0634\u062a\u0627 \u0628\u06d5 \u0632\u06d5\u0648\u06cc\u062f\u0627 \u0646\u06d5\u06af\u06d5\u0695\u0627\u0648\u0646\u060c \u062a\u0627 \u0628\u0628\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06b5\u0627\u0646 \u0648 \u06af\u0648\u06ce\u0686\u06a9\u06d5\u06af\u06d5\u0644\u06ce \u06a9\u06d5 \u067e\u06ce \u062a\u06ce\u0628\u06af\u06d5\u0646 \u0648 \u0628\u0628\u06cc\u0633\u0646\u061f \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc\u062f\u0627 \u0686\u0627\u0648 \u06a9\u06c6\u0631 \u0646\u0627\u0628\u0646\u061b \u0626\u06d5\u0648 \u062f\u06b5\u0627\u0646\u06d5 \u06a9\u0648\u06ce\u0631 \u062f\u06d5\u0628\u0646 \u06a9\u06d5 \u062c\u06ce\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0633\u06ce\u0646\u06d5\u06a9\u0627\u0646\u06d5.',
    '\u0628\u06c6 \u0626\u0627\u0632\u0627\u0631\u06ce\u062a \u0644\u06ce \u0628\u06d5 \u067e\u06d5\u0644\u06d5\u0646\u061b \u062e\u0648\u062f\u0627\u0634 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u067e\u0627\u0634\u06af\u06d5\u0632 \u0646\u0627\u0628\u06ce. \u062a\u0627\u0642\u06d5 \u0695\u06c6\u0698\u06ce\u06a9 \u0644\u06d5 \u06a9\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u060c \u0628\u06d5 \u0642\u06d5\u062f \u0647\u06d5\u0632\u0627\u0631 \u0633\u0627\u06b5\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062d\u06cc\u0633\u0627\u0628\u06cc \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u06d5 \u0632\u06c6\u0631 \u0634\u0627\u0631\u0627\u0646 - \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u0628\u0648\u0648\u0646 - \u0645\u06c6\u06b5\u06d5\u062a\u0645 \u062f\u0627 \u0648 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646\u0627 \u0648\u06d5\u0628\u06d5\u0631 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u062e\u06c6\u0645 \u062e\u0633\u062a\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u06d5\u0631\u062f\u0645! \u0626\u06d5\u0645\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc\u0627\u0646 \u0695\u06d5\u0686\u0627\u0648 \u06a9\u0631\u062f\u060c \u0644\u06ce \u062e\u06c6\u0634 \u0628\u0648\u0648\u0646 \u0648 \u0628\u0698\u06cc\u0648\u06ce\u06a9\u06cc \u0628\u0627\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06c6\u0634\u0634\u06cc\u0627\u0646 \u06a9\u0631\u062f \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u06af\u06d5\u0634\u06d5 \u0646\u06d5\u06a9\u06d5\u0646\u060c \u067e\u06ce\u0634\u06cc\u0627\u0646 \u0648\u0627\u0628\u0648\u0648 \u06a9\u06d5 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u0645\u0627\u0646 \u0644\u06ce \u0628\u0633\u062a\u06ce\u0646\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06cc\u0627\u0631\u06cc \u062f\u06c6\u0698\u06d5\u0647\u0646.',
    '\u0644\u06d5 \u067e\u06ce\u0634 \u062a\u06c6\u0634\u062f\u0627 \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc \u0628\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u06d5 - \u0626\u06cc\u0644\u0644\u0627\u0646 \u0648 \u0628\u06cc\u0644\u0644\u0627\u0646 - \u0634\u06d5\u06cc\u062a\u0627\u0646 [\u0628\u06d5 \u0647\u06d5\u0644\u06cc \u0632\u0627\u0646\u06cc\u0648\u06d5 \u0648 \u0644\u06d5 \u062e\u06c6\u06cc\u06d5\u0648\u06d5] \u0634\u062a\u06ce\u06a9\u06cc \u062f\u06d5\u067e\u0627\u06b5 \u0626\u0627\u062e\u0646\u06cc\u0648\u06d5. \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u0641\u06d5\u0646\u062f \u0648 \u0641\u06ce\u06b5\u06d5\u06a9\u06d5\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u06cc \u067e\u0648\u0648\u0686 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648\u061b \u0644\u06d5 \u062f\u0648\u0627 \u062c\u0627\u0631\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u067e\u062a\u06d5\u0648 \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u062a\u0627 \u0648\u06d5\u0628\u06a9\u0627 \u0626\u06d5\u0648\u06d5\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0647\u0627\u0648\u06cc\u0634\u062a\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u0646\u0627\u0648\u06cc\u06d5\u0648\u06d5\u060c \u0628\u0628\u062a\u06d5 \u0645\u06ce\u0645\u06b5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06b5\u06cc \u062f\u06d5\u063a\u06d5\u0632\u062f\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u062f\u06b5\u06cc\u0627\u0646 \u0695\u06d5\u0642\u06d5. \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u0628\u06c6 \u062f\u0698\u0627\u06cc\u06d5\u062a\u06cc \u0632\u06c6\u0631 \u0686\u0648\u0648\u0646\u06d5\u067e\u06ce\u0634.',
    '\u062a\u0627 \u0632\u0627\u0646\u0627\u06a9\u0627\u0646\u06cc\u0634 \u0628\u0632\u0627\u0646\u0646\u060c \u0626\u06d5\u0645\u06d5 \u0695\u0627\u0633\u062a\u06d5 [\u0648] \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0695\u0627\u06cc\u06d5\u061b \u0626\u06cc\u062a\u0631 \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce \u0628\u06a9\u06d5\u0646 \u0648 \u062f\u06b5\u06cc\u0634\u06cc\u0627\u0646 \u067e\u06ce \u062f\u0627\u0628\u0645\u06d5\u0632\u0631\u06ce. \u0647\u06d5\u0631\u06a9\u06ce \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce\u06cc \u0647\u06d5\u06cc\u06d5\u060c \u0628\u06ce \u0633\u06c6 \u062e\u0648\u062f\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 \u0647\u06d5\u062a\u0627 \u0626\u06ce\u0633\u062a\u0627\u0634 \u0644\u06ce \u062f\u0648\u0648\u062f\u06b5\u0646\u060c \u062a\u0627 - \u06af\u0648\u0632 \u0648 \u06af\u0648\u0645\u0645\u06d5\u062a - \u0633\u06d5\u06b5\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0695\u0627\u062f\u06d5\u0628\u06ce\u061b \u06cc\u0627\u0646 \u0628\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u0647\u0648\u0645\u06ce\u062f\u0628\u0695 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0632\u0627\u0631\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0648 \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u06af\u0634\u062a\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627. \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u0648\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0695\u0648\u0627 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646\u060c \u0644\u06d5 \u0628\u0627\u063a\u0627\u062a\u06cc \u067e\u0695 \u062e\u06ce\u0631\u0648\u0628\u06ce\u0631 \u062f\u0627\u062f\u06d5\u0645\u06d5\u0632\u0631\u06ce\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0627\u0646\u06cc\u060c \u0628\u06d5\u0634\u06cc\u0627\u0646 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062f\u0627\u0646\u06ce\u06a9\u06cc \u0626\u0627\u0648\u0695\u0648\u0648\u0628\u06d5\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u0627\u0631\u06d5 \u0628\u0648\u0648\u0646\u060c \u067e\u0627\u0634\u0627\u0646 \u06a9\u0648\u0698\u0631\u0627\u0648\u0646 \u06cc\u0627\u0646 \u0645\u0631\u062f\u0648\u0648\u0646\u060c \u062e\u0648\u062f\u06ce \u0695\u06c6\u0632\u06cc\u06a9\u06d5 \u06af\u06d5\u0644\u06d5\u06a9 \u0695\u0646\u062f \u062f\u06d5\u062f\u0627\u062a\u06d5 \u0648\u0627\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06b5\u0627\u0648\u0627\u062a\u0631 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0695\u06c6\u0632\u06cc \u062f\u06d5\u0631\u06ce\u06a9\u06d5.',
    '\u0647\u06d5\u0631 \u062f\u06d5\u06cc\u0627\u0646\u0628\u0627\u062a\u06d5 \u0626\u06d5\u0648 \u0634\u0648\u06ce\u0646\u06d5\u06cc \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062d\u06d5\u0632\u06cc \u0644\u06ce \u06a9\u06d5\u0646. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5 \u0633\u06d5\u0631\u062e\u06c6\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06ce\u06a9\u0631\u0627\u0648\u06ce\u06a9 \u0628\u06c6 \u062a\u06c6\u06b5\u06d5\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0633 \u0628\u0628\u0632\u06ce\u0648\u06ce \u0648 \u062f\u06cc\u0633\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06ce \u0628\u06a9\u0631\u06ce\u060c \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u062e\u0648\u062f\u0627 \u06a9\u06c6\u0645\u06d5\u06a9\u06cc \u067e\u06ce\u062f\u06d5\u06a9\u0627. \u062e\u0648\u0627 \u0644\u06ce\u0628\u0648\u0631\u062f\u06d5 \u0648 \u06af\u0648\u0646\u0627 \u0628\u06d5\u062e\u0634\u06d5.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0634\u06d5\u0648 \u062f\u06d5\u0695\u06c6\u0698\u06ce \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u06ce\u0634\u06ce \u0648 \u0695\u06c6\u0698\u06cc\u0634 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0646\u0627\u0648 \u0634\u06d5\u0648\u06ce \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0626\u06d5\u0628\u06cc\u0646\u06ce \u0648 \u0626\u06d5\u0698\u0646\u06d5\u0648\u06ce.',
    '\u0686\u0648\u0646\u06a9\u0648\u0648 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0695\u0627\u0633\u062a\u06d5 \u0648\u060c \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5\u0648 \u0647\u0627\u0646\u0627\u06cc \u0628\u06c6 \u062f\u06d5\u0628\u06d5\u0646\u060c \u0644\u06d5 \u067e\u0695\u0648\u067e\u0648\u0648\u0686 \u0628\u06d5\u062f\u06d5\u0631 \u0646\u06cc\u06cc\u06d5 \u0648 \u0628\u0627\u06b5\u0627\u062f\u06d5\u0633\u062a\u06cc \u0647\u06d5\u0631\u06d5 \u0645\u06d5\u0632\u0646 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u0646\u06d5\u062a \u062f\u06cc\u0648\u06d5 \u062e\u0648\u0627 \u0628\u0627\u0631\u0627\u0646\u06cc \u0644\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5\u0648\u06d5 \u0628\u0627\u0631\u0627\u0646\u062f \u0648 \u0632\u06d5\u0648\u06cc \u067e\u06ce \u06a9\u06d5\u0633\u06a9 \u0647\u06d5\u06b5\u06af\u06ce\u0695\u0627\u061f \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0648\u0631\u062f\u06d5\u06a9\u0627\u0631\u06cc\u06cc\u06d5\u06a9 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0645\u0627\u06b5\u06cc \u0626\u06d5\u0648\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u062a\u0627\u0631\u06cc\u0641\u06cc \u0628\u06a9\u0631\u06ce.',
    '\u0626\u06d5\u06cc \u0646\u06d5\u062a \u062f\u06cc\u0648\u06d5\u060c \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc \u06a9\u06d5\u0648\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u06af\u06d5\u0645\u06cc\u06d5\u0634 \u0647\u06d5\u0631 \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648 [\u0644\u06d5 \u062f\u0648\u0695] \u062f\u06d5\u0631\u06cc\u0627\u062f\u0627 \u0695\u06d5\u0648\u0627\u0646\u06d5 \u0648\u060c \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06cc \u0695\u0627\u06af\u0631\u062a\u0648\u0648\u06d5 \u06a9\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u0639\u06d5\u0631\u0632\u0627 \u0646\u06d5\u06a9\u06d5\u0648\u06ce\u060c \u0645\u06d5\u06af\u06cc\u0646 \u062e\u06c6\u06cc \u0626\u06cc\u0632\u0646\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06ce\u061f \u0628\u06ce \u0633\u06c6 \u062e\u0648\u062f\u0627 \u0628\u06c6 \u0626\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u06d5\u060c \u0632\u06c6\u0631 \u062f\u06b5\u0633\u06c6\u0632 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0698\u06cc \u067e\u06ce\u062f\u0627\u0646 \u0648 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u062a\u0627\u0646\u0645\u0631\u06ce\u0646\u06ce\u060c \u067e\u0627\u0634\u0627\u0646 \u0698\u06cc\u0648 \u067e\u06ce\u062f\u06d5\u062f\u0627\u062a\u06d5\u0648\u06d5. \u0695\u0627\u0633\u062a\u06cc \u0645\u0631\u06c6 \u0646\u0627\u0633\u067e\u0627\u0633\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9 \u0695\u06ce\u0648\u0634\u0648\u06ce\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u06d5\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u067e\u06ce\u0648\u062f\u0627\u0646\u06d5 \u0628\u0695\u0648\u0627. \u0633\u0627 \u0646\u0627\u0628\u06ce \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06ce\u0634\u06d5\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06a9\u06d5\u0646. \u062a\u06c6 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u06af\u0627\u0632\u06cc \u0628\u06a9\u06d5 \u0648 \u0633\u0648\u0648\u0631 \u0628\u0632\u0627\u0646\u06d5 \u06a9\u06d5 \u062a\u06c6 \u0644\u06d5\u0633\u06d5\u0631 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u06a9\u06ce\u0634\u06d5\u0634\u06cc\u0627\u0646 \u067e\u06ce\u06a9\u0631\u062f\u06cc\u060c \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0631\u06d5 \u0644\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646.',
    '\u062e\u0648\u062f\u0627 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u0627\u060c \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0648 \u0628\u06c6 \u06cc\u06d5\u06a9\u0627\u06b5\u0627 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u06a9\u06ce\u0634\u06d5\u062a\u0627\u0646 \u0628\u0648\u0648.',
    '\u0645\u06d5\u0631\u062a\u06c6 \u0646\u06d5\u062a \u0632\u0627\u0646\u06cc\u0648\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u062f\u0627\u06cc\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce\u061f \u0626\u06d5\u0645\u06d5 \u06af\u0634 \u0644\u06d5 \u062f\u06d5\u0641\u062a\u06d5\u0631\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0628\u06c6 \u062e\u0648\u0627 \u0647\u0627\u0633\u0627\u0646\u06d5.',
    '\u0634\u062a\u06cc \u0648\u06d5\u0647\u0627\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646 \u06a9\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u062a\u06d5\u0648\u06cc \u0628\u06c6 \u0646\u06d5\u0646\u0627\u0631\u062f\u06c6\u062a\u06d5\u062e\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0648\u060c \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0647\u06cc\u0686\u06cc \u0644\u06ce \u0646\u0627\u0632\u0627\u0646\u0646 \u0648 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u06cc\u0634 \u0647\u06cc\u0686 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062f\u06d5\u0631\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u06a9\u0627\u062a\u06ce \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06a9\u0627\u0646\u06cc \u0645\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0644\u06d5 \u0686\u0695\u0648\u0686\u0627\u0648\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u062f\u0627\u060c \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062d\u0627\u0634\u0627 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0647\u06ce\u0631\u0634 \u0628\u06d5\u0631\u0646\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0646\u06d5\u0648\u06d5. \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u067e\u06ce\u062a\u0627\u0646 \u0628\u06ce\u0698\u0645 \u06a9\u06d5\u0686\u06cc \u0644\u06d5\u0645\u06d5 \u062e\u0631\u0627\u062a\u0631\u06d5\u061f \u0626\u06d5\u0648 \u0626\u0627\u0648\u0631\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062f\u0627\u0648\u06d5 \u0628\u06d5 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u061b \u067e\u06d5\u06a9 \u0644\u06d5\u0648 \u0626\u0627\u06a9\u0627\u0645\u06d5 \u062e\u0631\u0627\u0648\u06d5!',
    '\u0647\u06c6 \u062e\u06d5\u06b5\u06a9\u0646\u06c6! \u0645\u06d5\u0633\u06d5\u0644\u06ce\u06a9 \u0647\u06ce\u0646\u0631\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5 \u06af\u0648\u06ce \u0628\u06c6 \u0628\u06af\u0631\u0646: \u0644\u06d5 \u062e\u0648\u0627\u0628\u06d5\u062f\u06d5\u0631\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0647\u0627\u0646\u0627\u06cc \u067e\u06ce \u062f\u06d5\u0628\u06d5\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0648\u06d5\u06a9\u06c6 \u0628\u0646\u060c \u0646\u0627\u062a\u0627\u0646\u0646 \u0645\u06ce\u0634\u06ce \u0633\u0627\u0632 \u0628\u062f\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0645\u06ce\u0634\u06cc\u0634 \u0634\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0642\u0627\u067e\u06ce\u0646\u06ce\u060c \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0644\u06ce\u06cc \u0628\u0633\u062a\u06ce\u0646\u0646\u06d5\u0648\u06d5. \u062f\u0627\u0648\u0627\u06a9\u0627\u0631 \u0648 \u062f\u0627\u0648\u0627\u06a9\u0631\u0627\u0648 \u0686\u06d5\u0646\u062f \u0632\u06d5\u0628\u0648\u0648\u0646\u0646.',
    '\u062e\u0648\u0627\u06cc\u0627\u0646 - \u0648\u06d5\u06a9 \u0647\u06ce\u0698\u0627\u06cc \u0646\u0627\u0633\u06cc\u0646 \u0628\u06ce - \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5. \u062e\u0648\u062f\u0627 \u06cc\u06d5\u06a9\u062c\u0627\u0631 \u062a\u0648\u0627\u0646\u0627\u0648 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646 \u067e\u06d5\u06cc\u0627\u0645 \u0646\u06ce\u0631\u06cc \u0647\u06d5\u06b5\u062f\u06d5\u0628\u0698\u06ce\u0631\u06ce \u0648 \u0644\u06d5 \u0646\u06ce\u0648 \u0645\u0631\u06c6\u0634\u062f\u0627 \u0647\u06d5\u0631\u0648\u06d5\u062a\u0631. \u062e\u0648\u062f\u0627 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0628\u06cc\u0646\u0627\u06cc\u06d5.',
    '\u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0633\u062a\u0627 \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0648 \u0644\u06d5\u0648\u06d5\u0634 \u06a9\u06d5\u0648\u0627 \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06cc\u0634 \u0647\u06d5\u0631 \u062f\u06d5\u0686\u0646\u06d5\u0648\u06d5 \u0628\u06d5\u0631\u062f\u06d5\u0633\u062a\u06cc \u062e\u0648\u0627.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u06a9\u0695\u0646\u06c6\u0634 \u0648 \u0633\u0648\u0698\u062f\u06d5\u06cc \u0628\u06c6 \u0628\u06d5\u0631\u0646\u060c \u0686\u0627\u06a9\u06d5\u0634 \u0628\u06a9\u06d5\u0646\u061b \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0695\u0632\u06af\u0627\u0631 \u0628\u0628\u0646.',
    '\u0628\u06d5 \u062f\u06b5 \u0648 \u062f\u0627\u0648 - \u0648\u06d5\u06a9 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 - \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627. \u0626\u06d5\u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u061b \u0644\u06d5\u0645 \u062f\u06cc\u0646\u06d5\u0634\u062f\u0627 \u062a\u0648\u0648\u0634\u06cc \u0632\u06d5\u062d\u0645\u06d5\u062a\u06cc \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u0647\u06d5\u0631 \u0626\u0627\u06cc\u0646\u06cc \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u06cc \u0628\u0627\u0648\u06d5\u062a\u0627\u0646\u06d5\u061b \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u0648 \u0644\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u0634\u062f\u0627\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0646\u0627\u0648\u06cc \u0646\u0627\u0646 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u061b \u062a\u0627 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u06d5 \u0634\u0627\u06cc\u06d5\u062a \u0628\u06ce \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0648 \u0626\u06ce\u0648\u06d5\u0634 \u0634\u0627\u06cc\u06d5\u062a \u0628\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u062e\u06d5\u06b5\u06a9. \u0633\u0627 \u0646\u0648\u06ce\u0698 \u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u0646 \u0648 \u067e\u0634\u062a\u0648\u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0628\u06d5\u0633\u062a\u0646\u061b \u0626\u0627\u06cc \u0686\u06d5\u0646 \u06af\u06d5\u0648\u0631\u06d5 \u0648 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u06ce\u06a9\u06cc \u0647\u06ce\u0698\u0627\u06cc\u06d5.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u060c \u0695\u0632\u06af\u0627\u0631\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u0648\u06ce\u0698\u0627\u0646\u062f\u0627 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5\u06af\u06d5\u0648\u0631\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646\u061b',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0642\u0633\u06d5\u06cc \u067e\u0648\u0648\u0686 \u062f\u0648\u0648\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u061b',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u062f\u06d5\u0646\u061b',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0634\u06d5\u0631\u0645\u06cc \u062e\u06c6 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5 \u0647\u0627\u0648\u0633\u06d5\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u0626\u06d5\u0648\u06cc \u0645\u06b5\u06a9\u06cc \u062e\u06c6\u06cc\u0627\u0646\u0646\u061b \u06a9\u06d5 \u0646\u0627\u0628\u06ce \u0633\u06d5\u0631\u06a9\u06c6 \u0646\u06d5\u0628\u06a9\u0631\u06ce\u0646.',
    '\u0647\u06d5\u0631\u06a9\u06ce\u0634 \u062c\u06cc\u0627 \u0644\u06d5\u0645\u0627\u0646\u06d5\u06cc \u0628\u0648\u0648\u06ce\u060c \u0626\u06d5\u0648 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0644\u0627\u062f\u06d5\u0631\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 [\u0644\u06d5 \u0695\u0632\u06af\u0627\u0631\u0627\u0646\u0646] \u06a9\u06d5 \u0626\u0627\u0645\u0627\u0646\u06d5\u062a \u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06cc\u0627\u0646 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646\u061b',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06c6\u0698\u062e\u06c6\u0646\u062f\u0646\u06ce \u0633\u0648\u0648\u0631\u0646\u061b',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u06af\u0634\u062a\u06cc\u0627\u0646 \u0628\u06d5\u0634\u0628\u06d5\u0631\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5\u0634\u0628\u06d5\u0631\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a\u0646 \u0648 \u062a\u0627\u0647\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646.',
    '\u0626\u06ce\u0645\u06d5\u06cc\u0646 \u0645\u0631\u06c6\u0646 \u0644\u06d5 \u0642\u0648\u0695\u06ce\u06a9\u06cc \u062e\u06c6\u0634\u06a9\u0631\u0627\u0648 \u062f\u0631\u0648\u0633\u062a \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06a9\u0631\u062f\u0645\u0627\u0646\u06d5 \u062a\u0646\u06c6\u06a9\u06ce\u06a9\u060c \u0644\u06d5 \u0628\u0646\u06a9\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u062a\u06d5\u0648\u062f\u0627.',
    '\u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u062a\u0646\u06c6\u06a9\u06d5\u0648\u06d5\u060c \u062e\u0648\u06ce\u0646\u06cc \u067e\u0646\u062f\u0631\u0645\u0627\u0646 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627 \u0648 \u0644\u06d5\u0648 \u062e\u0648\u06ce\u0646\u06d5\u0648\u06d5 \u067e\u0644\u06d5 \u06af\u06c6\u0634\u062a\u06ce\u06a9\u0645\u0627\u0646 \u0633\u0627\u0632 \u06a9\u0631\u062f \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u067e\u0644\u06d5 \u06af\u06c6\u0634\u062a\u06d5\u06a9\u06d5 \u0647\u06d5\u0633\u062a\u06cc\u0646 \u0686\u06ce \u06a9\u0631\u062f\u061b \u0647\u06d5\u0633\u062a\u06cc\u06cc\u06d5\u06a9\u06d5\u0634\u0645\u0627\u0646 \u062e\u0633\u062a\u06d5 \u0646\u0627\u0648 \u067e\u06c6\u0634\u06d5\u0646\u06ce \u0644\u06d5 \u06af\u06c6\u0634\u062a. \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646\u0627 \u06a9\u0631\u0645\u0627\u0646\u06d5 \u0686\u06ce \u06a9\u0631\u0627\u0648\u06ce\u06a9\u06cc \u062a\u0631. \u062f\u06d5\u0633\u0695\u06d5\u0646\u06af\u06cc\u0646\u06cc \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u0627\u06cc\u06d5\u061b \u06a9\u06d5 \u0647\u06cc\u0686 \u0648\u06d5\u0633\u062a\u0627\u06cc\u06d5 \u067e\u06ce\u06cc \u0646\u0627\u06af\u0627.',
    '\u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u062f\u0627 \u062a\u0648\u0648\u0634\u06cc \u0645\u0631\u062f\u0646 \u062f\u06d5\u0628\u0646.',
    '\u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 \u0633\u06d5\u0631\u0644\u06d5\u0646\u0648\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5\u06cc\u0646 \u06a9\u06d5 \u0626\u06d5\u0645 \u062d\u06d5\u0648\u062a \u0646\u0647\u06c6\u0645\u06d5\u0646 \u0644\u06d5 \u0698\u0648\u0648\u0631 \u0626\u06ce\u0648\u06d5\u0695\u0627 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627 \u0648 \u0644\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u062f\u0631\u0648\u0633\u062a\u06cc \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0646.',
    '\u0644\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5 \u0628\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0626\u0627\u0648\u0645\u0627\u0646 [\u0628\u06c6\u062a\u0627\u0646] \u062f\u0627\u0628\u0627\u0631\u0627\u0646\u062f \u0648 \u062c\u06ce\u06af\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u067e\u06ce\u062f\u0627 \u0648 \u0647\u06d5\u0631 \u0626\u06ce\u0645\u06d5\u0634\u06cc\u0646 \u06a9\u06d5 \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc\u0646 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0628\u06d5\u0631\u06cc\u0646.',
    '\u0633\u0627 \u0628\u06d5\u0648 \u0626\u0627\u0648\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u062f\u0627\u0631\u062e\u0648\u0631\u0645\u0627 \u0648 \u0644\u06d5 \u0695\u06d5\u0632\u0627\u0646 \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0632\u06c6\u0631 \u0645\u06cc\u0648\u06d5\u062c\u0627\u062a\u0648\u0648 \u062f\u06d5\u062f\u0627\u062a\u06ce \u0648 \u0644\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u062f\u0627\u0631\u06ce\u06a9\u06cc\u0634 \u0647\u06d5\u0631 [\u0644\u06d5 \u0633\u06c6\u0646\u06af\u06d5\u06cc \u0626\u06d5\u0648] \u0644\u06d5 \u062a\u0648\u0648\u0631\u06cc \u0633\u06cc\u0646\u0627\u06cc\u06d5 \u062f\u06d5\u0695\u0648\u06ce\u060c \u06a9\u06d5 \u0695\u06c6\u0646\u06ce \u0628\u06d5\u0631\u0647\u06d5\u0645 \u062f\u06ce\u0646\u06ce\u062a \u0648 \u067e\u06ce\u062e\u06c6\u0631\u06cc\u0634\u06d5 \u0628\u06c6 \u062e\u06c6\u0631\u0627\u06cc\u0627\u0646.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0648\u06b5\u0633\u0627\u062a\u06cc\u0634 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u0631\u0633\u06ce\u06a9\u06d5: \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0648\u0627 \u0644\u06d5 \u062f\u06d5\u0631\u0648\u0646\u06cc\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c \u067e\u06ce\u062a\u0627\u0646 \u062f\u06d5\u0646\u06c6\u0634\u06ce\u0646\u06cc\u0646. \u0644\u06ce\u0627\u0646 \u062f\u06d5\u062e\u06c6\u0646 \u0648 \u0632\u06c6\u0631\u06cc\u0634 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u0628\u06c6\u0648\u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u06af\u06d5\u0645\u06cc \u0647\u06d5\u06b5\u062f\u06d5\u06af\u06cc\u0631\u06ce\u0646.',
    '\u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0646\u0648\u0648\u062d\u06cc\u0634\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0644\u0627\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc. \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u062a\u06d5\u0646\u06cc\u0627 \u062e\u0648\u062f\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce\u061b \u062f\u06d5\u0633\u0627 \u0628\u06c6 \u0647\u06cc\u0686 \u062a\u0631\u0633\u0648\u0648 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u06af\u0631\u06d5\u06af\u0631\u06d5\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648 - \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0646\u0627\u0633\u06cc - \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0628\u0646\u06cc\u0627\u062f\u06d5\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646\u06d5\u0633\u061b \u0646\u06cc\u0627\u0632\u06cc \u0648\u0627\u06cc\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0632\u0627\u06b5 \u0628\u06a9\u0627. \u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0648\u0648\u0627\u06cc\u06d5\u060c \u0641\u0631\u0634\u062a\u06ce\u06a9\u06cc \u062f\u06d5\u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u061b \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0627\u0646\u06cc\u0634 \u0634\u062a\u06cc \u0648\u0627\u0645\u0627\u0646 \u0646\u06d5\u0628\u06cc\u0633\u062a\u0648\u0648\u06d5.',
    '\u0626\u06d5\u0645 \u067e\u06cc\u0627\u0648\u06d5 \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06ce\u0648\u06d5\u06cc\u06d5\u060c \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0648\u0627 \u0628\u0627\u0634\u06d5 \u0686\u0627\u0648\u06ce\u0631\u06cc \u0628\u06a9\u0631\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645! \u0648\u0627 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646\u0645 \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u062a \u062f\u06d5\u0647\u0627\u0646\u0627\u0645 \u0648\u06d5\u0631\u06d5.',
    '\u0626\u06cc\u062a\u0631 \u062e\u06c6\u0645\u0627\u0646 \u062a\u06ce\u0645\u0627\u0646 \u06af\u06d5\u06cc\u0627\u0646\u062f\u060c \u06a9\u06d5: \u0644\u06d5 \u0698\u06ce\u0631 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06cc \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u060c \u06af\u06d5\u0645\u06cc\u06d5 \u0633\u0627\u0632 \u062f\u06d5. \u062c\u0627 \u06a9\u0627\u062a\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646\u0645\u0627\u0646 \u0647\u0627\u062a \u0648 \u0626\u0627\u0648 \u0644\u06d5 \u062a\u06d5\u0646\u062f\u0648\u0648\u0631\u062f\u0627 \u0632\u0627\u06cc\u06d5\u0648\u06d5\u061b \u0644\u06d5 \u0647\u06d5\u0631 \u062c\u0633\u0646\u06d5\u06cc \u062c\u0648\u0648\u062a\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u062e\u06d5 \u0646\u0627\u0648\u06cc. \u0648\u06ce\u0695\u0627\u06cc \u062e\u06ce\u0632\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u0634\u062a\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u0628\u0695\u06cc\u0627\u0631\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0631\u0627\u0648\u06d5 \u0648 \u0644\u06d5\u0645\u06d5\u0695 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0645 \u0645\u06d5\u062f\u0648\u06ce\u0646\u06d5 \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0644\u06d5 \u062e\u0646\u06a9\u0627\u0648\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u062e\u06c6\u062a \u0648 \u0647\u0627\u0648\u0695\u06ce\u06a9\u0627\u0646\u062a \u0644\u06d5\u0633\u06d5\u0631 \u06af\u06d5\u0645\u06cc\u06d5\u06a9\u06d5 \u062c\u06ce\u062a\u0627\u0646 \u06af\u0631\u062a\u060c \u0628\u06ce\u0698\u06d5: \u0633\u0648\u067e\u0627\u0633 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06ce \u06a9\u06d5 \u0644\u06d5 \u06a9\u06ce\u0634\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u0695\u0632\u06af\u0627\u0631\u06cc \u06a9\u0631\u062f\u06cc\u0646.',
    '\u0628\u0634\u06b5\u06ce: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645! \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u0628\u06d5 \u067e\u06cc\u062a\u0645 \u062f\u0627\u0628\u0628\u06d5\u0632\u06ce\u0646\u06d5 \u0648\u061b \u062a\u06c6 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0645\u06cc\u0648\u0627\u0646\u062f\u0627\u0631\u0627\u0646 \u0628\u0627\u0634\u062a\u0631 \u0645\u06cc\u0648\u0627\u0646 \u0628\u06d5\u062e\u06ce\u0648 \u062f\u06d5\u06a9\u06d5\u06cc.',
    '\u0644\u06d5\u0645\u06d5\u0634\u062f\u0627 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0628\u0648\u0648\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0645\u06d5\u0634 \u0648\u06cc\u0633\u062a\u0645\u0627\u0646 \u0628\u06d5 \u062a\u0627\u0642\u06cc \u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646 \u0686\u06cc\u0646\u06ce \u062a\u0631\u0645\u0627\u0646 \u0628\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646\u060c \u06a9\u06d5: \u062a\u06d5\u0646\u06cc\u0627 \u062e\u0648\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u06a9\u06d5\u0633\u06cc \u062a\u0631\u06cc \u0648\u0627\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0647\u06ce\u0698\u0627 \u0628\u06ce. \u0645\u06d5\u0631 \u0646\u0627\u062a\u0631\u0633\u0646\u061f',
    '\u067e\u06cc\u0627\u0648\u0645\u0627\u0642\u0648\u0648\u06b5\u0627\u0646\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc - \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0646\u0627\u0633\u06cc \u0648 \u062f\u06cc\u062f\u0627\u0631\u06cc \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u0627\u0646 \u067e\u06ce \u062f\u0631\u06c6 \u0628\u0648\u0648\u061b \u0644\u06d5 \u0698\u06cc\u0646\u06cc \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u0634\u062f\u0627 \u062e\u06c6\u0634\u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06cc\u0646 \u067e\u06ce\u062f\u0627 \u0628\u0648\u0648\u0646 - \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u0645\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u06d5 \u0648 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u0627\u0631\u062f\u0646 \u0648 \u062e\u0648\u0627\u0631\u062f\u0646\u06cc\u0634 \u0648\u06d5\u06a9 \u062e\u06c6\u062a\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0648\u06d5\u0634\u0648\u06ce\u0646 \u0645\u0631\u06c6\u06cc\u06ce \u0648\u06d5\u06a9 \u062e\u06c6\u062a\u0627\u0646 \u06a9\u06d5\u0648\u0646\u060c \u0644\u06ce\u062a\u0627\u0646 \u0695\u0648\u0648\u0646 \u0628\u06ce \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0646.',
    '\u0686\u06c6\u0646 \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0628\u06d5\u06b5\u06ce\u0646 \u0628\u062f\u0627\u060c \u0647\u06d5\u0631\u06af\u0627 \u0645\u0631\u062f\u0646 \u0648 \u0644\u0627\u0634\u06d5\u062a\u0627\u0646 \u0628\u0648\u0648 \u0628\u06d5 \u062e\u06c6\u06b5 \u0648 \u0686\u06d5\u0646\u062f \u067e\u06ce\u0634\u06d5\u06cc\u06d5\u06a9\u060c \u0633\u06d5\u0631\u0644\u06d5\u0646\u0648\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5\u061f',
    '\u0628\u0627 \u0648\u0627\u0634 \u0628\u06b5\u06ce \u0686\u06c6\u0646 \u0626\u0627\u0648\u06d5\u0632 \u0642\u0628\u0648\u0648\u06b5\u06cc \u062f\u06d5\u06a9\u0627\u061f',
    '\u0698\u06cc\u0646 \u0644\u06d5\u0648\u06d5 \u067e\u062a\u0631 \u0646\u06cc\u06cc\u06d5\u060c \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0647\u06d5\u0645\u0627\u0646\u06d5. \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u0645\u0631\u06cc\u0646 \u0648 \u062f\u06d5\u0698\u06cc\u0646\u061b [\u0647\u06d5\u0631 \u06a9\u06d5 \u0645\u0631\u062f\u06cc\u0646] \u062a\u0627\u0632\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648 \u0646\u0627\u0628\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0645 \u06a9\u0627\u0648\u0631\u0627\u06cc\u06d5 \u067e\u06d5\u06cc\u062f\u0627 \u0628\u0648\u0648\u06d5 \u062f\u0631\u06c6 \u0628\u06d5 \u0646\u0627\u0648 \u062e\u0648\u0627\u0648\u06d5 \u062f\u06d5\u06a9\u0627\u061b \u0626\u06ce\u0645\u06d5\u0634 \u0628\u0627\u0648\u06d5\u0695\u06cc \u067e\u06ce \u0646\u0627\u06a9\u06d5\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0648\u0627 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646\u0645 \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u062a \u062f\u06d5\u0647\u0627\u0646\u0627\u0645 \u0648\u06d5\u0631\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0647\u06d5\u0631 \u0632\u06c6\u0631 \u0628\u06d5\u0632\u0648\u0648\u06ce\u06a9\u06cc \u0632\u0648\u0648\u060c \u06cc\u0627\u0646\u06d5 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 - \u0648\u06d5\u06a9 \u0647\u06ce\u0698\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0648\u0648 - \u0646\u0627\u0631\u06d5\u062a\u06d5 \u062f\u0627\u06cc\u0627\u0646\u06cc \u06af\u0631\u062a \u0648 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u0627\u0648\u0645\u0627\u06b5\u06a9\u0646 \u0644\u06ce\u06a9\u0631\u062f\u0646. \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u062a\u06d5\u0631\u062a \u0648 \u062a\u0648\u0646\u0627 \u0628\u0646!',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646 \u0686\u06d5\u0646\u062f \u0628\u06d5\u0631\u06d5\u06cc \u062a\u0631\u0645\u0627\u0646 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627.',
    '\u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u0644\u06d5 \u0628\u0646\u06cc\u0627\u062f\u06d5\u0645 - \u06a9\u06d5 \u062f\u06ce\u062a\u06d5 \u0648\u06d5\u062e\u062a\u06cc \u0645\u0631\u062f\u0646\u06cc\u0627\u0646 - \u0646\u06d5 \u0648\u06d5\u067e\u06ce\u0634 \u062f\u06d5\u062e\u0631\u06ce\u0646 \u0646\u06d5 \u0648\u06d5\u067e\u0627\u0634.',
    '\u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f\u0648\u0648\u0646\u060c \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u0628\u06d5 \u0634\u0648\u06ce\u0646 \u06cc\u06d5\u06a9\u0627 \u0628\u06d5 \u062f\u06d5\u0631\u062f\u06ce\u06a9\u06cc \u0648\u0627\u0645\u0627\u0646 \u0628\u0631\u062f\u0648\u0648\u0646 \u06a9\u06d5 \u0628\u06d5 \u0686\u06cc\u0631\u06c6\u06a9 \u0628\u06cc\u0627\u0646\u06af\u06ce\u0695\u0646\u06d5\u0648\u06d5. \u0628\u0627 \u062f\u0648\u0648\u0631\u06a9\u06d5\u0648\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627 \u0646\u0627\u0647\u06ce\u0646\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646\u060c \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u0627\u0695\u0648\u0648\u0646\u06cc \u0628\u0631\u0627\u06cc\u0645\u0627\u0646 \u0648\u06ce\u0695\u0627\u06cc \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0648 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0646\u0627\u0631\u062f\u060c',
    '\u0628\u06c6 \u0644\u0627\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0633\u06d5\u0631\u0632\u0644\u0627\u0646\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc\u061b [\u0628\u06d5\u06b5\u0627\u0645] \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0632\u0644 \u0632\u0627\u0646\u06cc \u0648 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u0628\u06d5 \u062f\u06d5\u0639\u06cc\u06d5 \u0628\u0648\u0648\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062c\u0627 \u0686\u06c6\u0646 \u0628\u06d5 \u062f\u0648\u0648 \u0645\u0631\u06c6\u06cc \u0648\u06d5\u06a9\u06d5 \u062e\u06c6\u0646 \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc\u0627\u0646 [\u0628\u06d5 \u06af\u0634\u062a\u06cc] \u06a9\u06c6\u06cc\u0644\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646\u0646\u061f',
    '\u0647\u06d5\u0631 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646\u06cc\u0627\u0646 \u0632\u0627\u0646\u06cc\u0646 \u0648 \u0628\u0648\u0648 \u0628\u06d5 \u0645\u0627\u06cc\u06d5\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0686\u0648\u0648\u0646\u06cc\u0627\u0646.',
    '\u06a9\u062a\u06ce\u0628\u06cc\u0634\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627\u060c \u0628\u06d5\u0634\u06a9\u06ce\u06a9\u0645 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u0628\u0646.',
    '\u06a9\u0648\u0695\u06cc \u0645\u0631\u06cc\u06d5\u0645 \u0648 \u062f\u0627\u06cc\u06a9\u06cc\u0634\u06cc \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u06a9\u0631\u0627\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9 \u0648 \u0644\u0627\u06cc \u06af\u0631\u062f\u06ce\u06a9 \u062f\u0627\u0645\u0627\u0646 \u0645\u06d5\u0632\u0631\u0627\u0646\u062f\u0646\u061b \u06a9\u06d5 \u062c\u06ce \u062d\u06d5\u0633\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u06a9\u0627\u0646\u06cc\u0627\u0648\u06ce\u06a9\u06cc \u0644\u06ce \u0628\u0648\u0648.',
    '\u0626\u06d5\u06cc \u06a9\u06c6\u0645\u06d5\u06b5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646! \u0628\u0698\u06cc\u0648\u06cc \u067e\u0627\u06a9\u0698 \u0628\u062e\u06c6\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0646\u061b \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u0645.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0645\u06d5 \u062f\u06cc\u0646\u06cc \u0648\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062f\u06cc\u0646\u06ce\u06a9\u06cc \u06cc\u06d5\u06a9\u067e\u0627\u0631\u0686\u06d5\u06cc\u06d5 \u0648 \u0626\u06d5\u0645\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0626\u06ce\u0648\u06d5\u0645\u061b \u0633\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u0645 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce.',
    '\u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u062f\u0627 \u0644\u06d5\u062a\u0648\u067e\u0627\u0631 \u06a9\u0631\u062f. \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc\u0634 \u0644\u06d5\u0648\u0627\u0646\u06d5\u060c \u0628\u06d5\u0648 \u067e\u0634\u06a9\u06d5\u06cc \u0626\u06ce\u0633\u062a\u0627 \u0647\u06d5\u06cc\u0627\u0646\u06d5\u060c \u062f\u06b5\u06cc \u062e\u06c6\u0634\u06d5.',
    '\u0644\u06ce\u06cc\u0627\u0646 \u06af\u06d5\u0695\u06ce \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0644\u06d5 \u0632\u06d5\u0644\u06a9\u0627\u0648\u06cc \u0646\u06d5\u0632\u0627\u0646\u06cc \u062e\u06c6\u062f\u0627 \u0695\u06c6\u0628\u0686\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u067e\u06ce\u06cc\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u0645\u0627\u06b5 \u0648 \u06a9\u0648\u0695\u0627\u0646 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u060c \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u06a9\u06d5:',
    '\u067e\u06d5\u0644\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0646\u06cc\u0627\u0646\u061f \u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062a\u0631\u0633\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u0644\u06ce\u0648\u06d5\u0644\u06d5\u0631\u0632\u06ce \u062f\u06d5\u0628\u0646\u060c',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695 \u062f\u06d5\u06a9\u06d5\u0646\u060c',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0647\u06cc\u0686 \u0634\u06d5\u0631\u06cc\u06a9\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u0646\u0627\u06a9\u06d5\u0646 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646\u060c',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0647\u06d5\u0686\u06cc \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u062f\u0627\u0648\u06cc\u0627\u0646\u06d5\u060c \u0647\u06d5\u0631 \u062f\u06d5\u06cc\u062f\u06d5\u0646\u061b \u06a9\u06d5\u0686\u06cc \u0644\u06d5 \u0633\u0627\u0645\u06cc \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646\u060c \u062f\u06b5\u06d5\u06a9\u0648\u062a\u06d5\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634 \u062f\u06d5\u0628\u06ce\u061b',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u062f\u0627 \u0644\u06d5\u0632 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0628\u06c6 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0644\u06d5 \u067e\u06ce\u0634\u0646.',
    '\u0626\u06d5\u0631\u06a9\u06cc \u067e\u062a\u0631 \u0644\u06d5 \u062a\u0648\u0627\u0646\u0627 \u0646\u0627\u062e\u06d5\u06cc\u0646\u06d5 \u0633\u06d5\u0631 \u06a9\u06d5\u0633. \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u060c \u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u0648\u06ce\u062f\u0627\u06cc\u06d5 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0647\u06cc\u0686 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0645\u0627\u0646 \u062f\u06b5\u06cc\u0627\u0646 \u0626\u0627\u06af\u0627\u06cc \u0644\u06d5\u0645\u06d5 \u0646\u06cc\u06cc\u06d5 \u0648 \u062e\u062c\u06b5\u06cc \u06a9\u0627\u0631\u06af\u06d5\u0644\u06cc \u062a\u0631\u0646.',
    '\u062a\u0627 \u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u06cc \u06a9\u06d5 \u062e\u06c6\u0634\u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u06af\u0631\u0641\u062a\u0627\u0631\u06cc \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u061b \u0644\u06d5\u067e\u0695 \u062f\u06d5\u0633\u062a \u062f\u06d5\u06a9\u06d5\u0646 \u0628\u06d5 \u0647\u0627\u0648\u0627\u0631.',
    '\u0626\u06d5\u0645\u0695\u06c6 \u0626\u06cc\u062a\u0631 \u0647\u0627\u0648\u0627\u0631 \u0648 \u062f\u0627\u062f\u06cc \u067e\u06ce \u0646\u0627\u0648\u06ce\u061b \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u0641\u0631\u06cc\u0627\u062a\u0627\u0646 \u0646\u0627\u06cc\u06d5\u06cc\u0646.',
    '\u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u062a\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u062f\u0631\u0627\u06cc\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u067e\u06ce \u062f\u0632\u06d5\u062a\u0627\u0646 \u0628\u0648\u0648.',
    '\u062e\u06c6\u062a\u0627\u0646 \u0632\u06c6\u0631 \u0628\u06d5\u0632\u0644 \u062f\u06d5\u0632\u0627\u0646\u06cc \u0648 \u0644\u06d5\u0634\u06d5\u0648\u0646\u0634\u06cc\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646\u062f\u0627\u060c \u0647\u0627\u062a\u06d5\u0631\u0627\u0646 \u067e\u0627\u062a\u06d5\u0631\u0627\u0646\u062a\u0627\u0646 \u0628\u0648\u0648.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0645 \u0648\u062a\u06d5\u06cc\u06d5 \u0695\u0627\u0646\u06d5\u0645\u0627\u0648\u0646\u060c \u06cc\u0627\u0646 \u0626\u06d5\u0648\u06cc \u0628\u06c6 \u0648\u0627\u0646 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0628\u06c6 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u06cc \u067e\u06ce\u0634\u0648\u0648\u06cc\u0627\u0646 \u0646\u06d5\u0647\u0627\u062a\u0628\u0648\u0648.',
    '\u06cc\u0627\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0628\u0648\u0648\u060c \u06a9\u06d5 \u0648\u0627 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u0644\u06ce\u062f\u06d5\u06a9\u0631\u062f\u061f',
    '\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce\u0698\u0646: \u062c\u0646 \u062f\u06d5\u0633\u062a\u06cc \u0644\u06ce \u0648\u06d5\u0634\u0627\u0646\u062f\u0648\u0648\u06d5. \u0646\u06d5\u062e\u06ce\u0631\u060c \u06a9\u06d5 \u0626\u06d5\u0648 \u0695\u0627\u0633\u062a\u06cc \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u062a\u06cc \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0648\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0628\u0627\u060c \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c \u062e\u0631\u0627 \u062f\u06d5\u0628\u0648\u0648\u0646. \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646\u061b \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u062e\u06c6 \u062f\u06d5\u062a\u06d5\u06a9\u0646\u06d5\u0648\u06d5.',
    '\u06cc\u0627\u0646 \u062a\u06c6 \u0645\u0632\u062a \u0644\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0648\u06ce\u061f \u062e\u06c6 \u0645\u0632\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a \u0632\u06c6\u0631 \u0628\u0627\u0634\u062a\u0631\u06d5 \u0648 \u0628\u0627\u0634\u062a\u0631\u06cc\u0646 \u0695\u06c6\u0632\u06cc \u062f\u06d5\u0631 \u0626\u06d5\u0648\u06d5.',
    '\u062a\u06c6 \u0647\u06d5\u0631 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u06d5 \u0628\u06cc\u0627\u0646\u062e\u06d5\u06cc\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u06d5 \u0646\u06cc\u06cc\u06d5\u060c \u0647\u06d5\u0631 \u0644\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0644\u0627 \u062f\u06d5\u062f\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0634\u0645\u0627\u0646 \u0628\u062e\u0633\u062a\u0646\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u0634\u0645\u0627\u0646 \u0644\u0627\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u062f\u0627 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0644\u0627\u0633\u0627\u0631\u06cc \u0648 \u0633\u06d5\u0631\u06af\u06d5\u0631\u062f\u0627\u0646\u06cc \u062f\u06d5\u0645\u0627\u0646\u06d5\u0648\u06d5.',
    '\u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646\u061b \u06a9\u06d5\u0686\u06cc \u0644\u06d5 \u062d\u0627\u0646\u062f \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0646\u06d5 \u0644\u06d5 \u0641\u06cc\u0632 \u062f\u0627\u0648\u06d5\u0632\u06cc\u0646 \u0648 \u0646\u06d5 \u0645\u0644\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u0627\u0695\u0627\u0646\u06d5\u0648\u06d5 \u0646\u0627.',
    '\u062a\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u0645\u0627\u0646 \u062f\u0627\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u0632\u0627\u0646\u06cc\u0627\u0646 \u0647\u0648\u0645\u06ce\u062f \u0646\u06d5\u0645\u0627.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u0686\u0627\u0648 \u0648 \u062f\u06b5 \u0648 \u06af\u0648\u06ce\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0642\u06d5\u0631\u0627\u0631 \u062f\u0627\u0648\u06d5. \u06a9\u06d5\u0645 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631\u06cc \u062f\u06d5\u06a9\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u06a9\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0698\u06cc\u0646 \u062f\u06d5\u0628\u06d5\u062e\u0634\u06ce \u0648 \u062f\u06d5\u0634\u0645\u0631\u06ce\u0646\u06ce\u061b \u0626\u0627\u06b5\u0648\u06af\u06c6\u0695\u06cc \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698\u06cc\u0634 \u0628\u06d5 \u062f\u06d5\u0633 \u0626\u06d5\u0648\u06d5. \u062f\u06d5\u06cc \u0626\u0627\u062e\u0631 \u062a\u0627 \u06a9\u06d5\u06cc \u062a\u06ce\u0646\u0627\u06af\u06d5\u0646\u061f',
    '\u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06d5\u0648\u06cc\u0634 \u0648\u06d5\u06a9 \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc \u067e\u06ce\u0634\u0648\u0648 \u0626\u06ce\u0698\u0646.',
    '\u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0631 \u0645\u0631\u062f\u06cc\u0646 \u0648 \u0628\u0648\u0648\u06cc\u0646\u06d5 \u0626\u0627\u062e \u0648 \u0626\u06ce\u0633\u06a9 \u0648 \u067e\u0631\u0648\u0648\u0633\u06a9\u06ce\u06a9\u060c \u062f\u06cc\u0633\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u0628\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0648 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0645\u0627\u0646 - \u0628\u06d5\u0631\u06ce - \u0626\u06d5\u0645 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u0645\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u061b \u0626\u06d5\u0645 \u0642\u0633\u0627\u0646\u06d5 \u06af\u0634 \u0686\u06cc\u0631\u06c6\u06a9\u06cc \u067e\u06ce\u0634\u0648\u0648\u0646\u0627\u0646\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06cc\u0686 \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648\u06cc\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0633\u06d5\u0631 \u0628\u06d5 \u06a9\u06ce\u06cc\u06d5\u061f',
    '\u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u06b5\u06ce\u0646: \u0633\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627\u06cc\u06d5. \u0628\u06ce\u0698\u06d5: \u0647\u06cc\u0686 \u0628\u06cc\u0631 \u0646\u0627\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062d\u06d5\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062a\u06d5\u062e\u062a\u06cc \u0647\u06d5\u0631\u06d5 \u0645\u06d5\u0632\u0646 \u06a9\u06ce\u06cc\u06d5\u061f',
    '\u0626\u06ce\u0698\u0646: \u06af\u0634 \u062f\u06d5\u0633\u06a9\u0627\u0631\u06cc \u062e\u0648\u0627\u06cc\u06d5. \u0628\u06ce\u0698\u06d5: \u0628\u06c6 \u0647\u06cc\u0686 \u062a\u0631\u0633\u0648\u0648 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u0628\u06ce\u0698\u06d5: \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0647\u06d5\u0631 \u0634\u062a \u0628\u06d5 \u062f\u06d5\u0633 \u06a9\u06ce\u06cc\u06d5 \u0648 \u0626\u06d5\u0631 \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06ce\u06cc\u06d5\u060c \u067e\u06d5\u0646\u0627\u06cc \u067e\u06d5\u0646\u0627\u0628\u06d5\u0631\u06d5 \u0648 \u062f\u0698\u06cc \u0626\u06d5\u0648 \u067e\u06d5\u0646\u0627 \u0646\u0627\u062f\u0631\u06ce\u061f',
    '\u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5. \u0628\u06ce\u0698\u06d5: \u062f\u06d5\u06cc \u0686\u06c6\u0646 \u062f\u06d5\u062e\u06d5\u06b5\u06d5\u062a\u06ce\u0646\u061f',
    '\u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06ce\u0645\u06d5\u06cc\u0646 \u06a9\u06d5 \u0695\u0627\u0633\u062a\u06cc\u0645\u0627\u0646 \u0628\u06c6 \u0647\u06ce\u0646\u0627\u0648\u0646 \u0648 \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u062e\u0648\u0627 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0695\u06c6\u06b5\u06d5\u06cc \u0628\u06c6 \u062e\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u06d5\u06a9\u0631\u062f \u0648 \u0647\u06cc\u0686 \u062e\u06ce\u0648\u06cc\u0634\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0646\u06cc\u06cc\u06d5. \u062f\u06d5\u0646\u0627 \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u062e\u06ce\u0648\u06ce \u0647\u06d5\u0631\u0686\u06cc \u062e\u06c6\u06cc \u062f\u0631\u0648\u0633\u062a\u06cc \u062f\u06d5\u06a9\u0631\u062f\u060c \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u06d5\u0628\u0631\u062f \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u06cc\u0627\u0646\u062f\u0627 \u0632\u0627\u06b5 \u062f\u06d5\u0628\u0648\u0648\u0646. \u0626\u06d5\u0648 \u0628\u06d5 \u062f\u0648\u0648\u0631\u06d5 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0626\u06ce\u0698\u0646.',
    '\u0626\u06d5\u0648 \u0644\u06d5 \u067e\u06cc\u0648\u0627\u0631 \u0648 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5\u061b \u0628\u06d5\u0631\u0632\u062a\u0631\u06d5 \u0644\u06d5\u0648\u06d5\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0645\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5\u0648\u0627 \u0628\u0695\u06cc\u0627\u0631\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u0631\u06ce \u0647\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06cc \u0645\u0646\u06cc \u062f\u06d5\u062f\u06d5\u06cc\u060c',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0695\u06d5\u06af\u06d5\u06b5 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u0645 \u0645\u06d5\u062e\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0628\u0695\u06cc\u0627\u0631 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u060c \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc\u0646 \u0646\u06cc\u0634\u0627\u0646\u062a\u06cc \u0628\u062f\u06d5\u06cc\u0646.',
    '\u062a\u06c6 \u0648\u06d5\u0631\u0627\u0645\u06cc \u062e\u0631\u0627\u067e\u06d5 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0628\u062f\u06d5\u061b \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646 \u0686 \u0626\u06ce\u0698\u0646.',
    '\u062a\u06c6 \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0647\u0627\u0646\u0627\u0645 \u0628\u06d5 \u062a\u06c6\u060c \u0644\u06d5 \u062f\u0646\u06d5\u062f\u0627\u0646\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u0627\u0646.',
    '\u0647\u06d5\u0631 \u0647\u0627\u0646\u0627\u0634 \u062f\u06d5\u0628\u06d5\u0645\u06d5 \u0628\u06d5\u0631 \u062a\u06c6\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06ce\u0646 \u0628\u06d5\u0644\u0627\u0645\u06d5\u0648\u06d5.',
    '[\u0626\u06d5\u0648\u0627\u0646 \u0647\u06d5\u0631\u0648\u0627\u0646] \u062a\u0627 \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0645\u0631\u062f\u0646 \u062f\u06ce\u062a\u06d5 \u0633\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce\u06a9\u06cc\u0627\u0646\u060c \u0626\u06d5\u0648\u0633\u0627 \u062f\u06d5\u06b5\u06ce: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0648\u06d5\u0645 \u06af\u06ce\u0695\u06d5.',
    '\u0628\u06d5\u0634\u06a9\u0648\u0648 \u0626\u06d5\u0648 \u0626\u0627\u06a9\u0627\u0631\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u0644\u06ce\u06cc \u0646\u06d5\u0628\u0627\u0646 \u0628\u0648\u0648\u0645 \u0626\u06d5\u0648\u062c\u0627\u0631 \u0628\u06cc\u06a9\u06d5\u0645. \u0646\u06d5\u062e\u06ce\u0631\u060c \u0642\u0633\u06d5 \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u0627\u061b \u062a\u0627\u0632\u06d5 \u062a\u0627 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u0644\u06d5\u0645\u067e\u06d5\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627\u06cc\u06d5 \u0648 [\u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u06cc\u06cc\u06d5].',
    '\u0647\u06d5\u0631\u06af\u0627\u06cc\u06d5\u06a9\u06cc\u0634 \u062f\u06d5\u06a9\u06d5\u06b5\u06d5\u0634\u0627\u062e \u062f\u06d5\u0645\u06ce\u0646\u062f\u0631\u0627\u060c \u0646\u06d5 \u062e\u0632\u0645\u0627\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0645\u06ce\u0646\u06ce \u0648 \u0646\u06d5 \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u0644\u06d5 \u06a9\u06d5\u0633 \u062f\u06d5\u067e\u0631\u0633\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u06a9\u06ce\u0634\u0627\u0646\u06d5\u06cc \u06af\u0631\u0627\u0646\u062a\u0631\u06cc \u0628\u06ce\u060c \u0626\u06d5\u0648 \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u0695\u06ce\u0632\u06cc \u0695\u0632\u06af\u0627\u0631\u06d5\u06a9\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u06a9\u06ce\u0634\u0627\u0646\u06d5 \u06a9\u06d5\u0645\u06cc \u0647\u06ce\u0646\u0627\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06c6\u0695\u0627\u0648\u06d5\u06a9\u0627\u0646\u0646 \u0648 \u0644\u06d5 \u062f\u06c6\u0698\u06d5\u0647\u062f\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u0645 \u0648 \u0686\u0627\u0648\u06cc\u0627\u0646 \u0628\u06d5 \u0626\u0627\u06af\u0631 \u0647\u06d5\u06b5\u062f\u06d5\u067e\u0631\u0648\u0648\u0632\u06ce\u062a \u0648 \u0644\u06ce\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062f\u06d5\u0686\u0631\u0686\u06ce.',
    '\u0645\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0645\u06d5\u062a\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0646\u06d5\u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u0627\u06cc\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u0631\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0698\u0645\u0627\u0631\u062f\u0646\u061f',
    '\u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0646\u06d5\u06af\u0628\u06d5\u062a \u0628\u0695\u0633\u062a\u06cc \u0628\u0695\u06cc\u0646 \u0648 \u0626\u06ce\u0645\u06d5 \u062e\u06d5\u06b5\u06a9\u06ce\u06a9\u06cc \u06af\u0648\u0645\u0695\u0627 \u0628\u0648\u0648\u06cc\u0646.',
    '\u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u0644\u0627\u0645\u0627\u0646! \u0644\u06ce\u0631\u06d5\u0645\u0627\u0646 \u0628\u0647\u06ce\u0646\u06d5 \u062f\u06d5\u0631\u06ce\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0645\u062c\u0627\u0631\u06cc\u0634 \u0647\u06d5\u0631 \u0648\u0627\u0628\u0648\u0648\u06cc\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646.',
    '\u0626\u06ce\u0698\u06ce: \u0646\u0627\u0634\u06ce \u0645\u0646 \u0628\u062f\u0648\u06ce\u0646\u0646\u060c \u062a\u0627 \u0644\u06d5\u0648\u06ce\u0634\u062f\u0627\u0646 \u0646\u0627\u0628\u06ce \u0647\u06d5\u0631 \u0641\u0632\u06d5\u062a\u0627\u0646 \u0644\u06ce \u0628\u06ce.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u0645 \u0647\u06d5\u0631 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u060c \u062a\u06c6\u0634 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u062a \u067e\u06ce\u0645\u0627\u0646\u062f\u0627 \u0628\u06ce \u0648 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u06d5\u061b \u062a\u06c6 \u0644\u06d5 \u06af\u0634 \u06a9\u06d5\u0633 \u062f\u06b5\u0633\u06c6\u0632\u062a\u0631\u06cc.',
    '\u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u06af\u0627\u06b5\u062a\u06d5\u0648 \u067e\u06ce \u062f\u06d5\u06a9\u0631\u062f\u061b \u0647\u06d5\u062a\u0627 \u0626\u06d5\u0648\u0627\u0646 \u06cc\u0627\u062f\u06cc \u0645\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0648\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u06cc\u0646.',
    '\u0648\u0627 \u0645\u0646 \u0626\u06d5\u0645\u0695\u06c6 \u067e\u0627\u062f\u0627\u0634 \u0626\u06d5\u0648 \u062e\u06c6\u0695\u0627\u06af\u0631\u062a\u0646\u06d5\u0645 \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc\u0648\u0646\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u0628\u0646.',
    '\u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u06ce: \u0686\u06d5\u0646\u062f \u0633\u0627\u06b5 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u0645\u0627\u0646\u06d5\u0648\u06d5\u061f',
    '\u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0695\u06c6\u0698\u06ce\u06a9 \u06cc\u0627\u0646 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5 \u0695\u06c6\u0698\u06ce \u0645\u0627\u06cc\u0646\u06d5\u0648\u06d5\u061b \u0644\u06d5 \u062d\u06cc\u0633\u0627\u0628\u06af\u06d5\u0631\u0627\u0646 \u0628\u067e\u0631\u0633\u06d5.',
    '\u0626\u06ce\u0698\u06ce: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0648\u0648\u0632\u0627\u0646\u06cc\u0628\u0627\u060c \u06a9\u06d5\u0645\u06ce\u06a9 \u0646\u06d5\u0628\u06ce \u0646\u06d5\u0645\u0627\u0646\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0648\u0627\u0628\u0648\u0648 \u0628\u06d5 \u06af\u06d5\u0645\u06d5 \u062f\u0631\u0648\u0633\u062a\u0646 \u06a9\u0631\u062f\u0648\u0648\u0646\u060c \u0646\u0627\u0634\u0648\u0648\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u0645\u0627\u0646\u061f',
    '\u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u06cc \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5\u060c \u06a9\u06d5 \u067e\u0627\u0634\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0642\u06cc\u0646\u06d5\u06cc\u06d5\u061b \u0647\u06cc\u0686 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06ce\u06a9\u06cc\u0634 \u0628\u06ce\u0698\u06af\u06d5 \u0644\u06d5 \u0626\u06d5\u0648 \u0646\u06cc\u06cc\u06d5\u061b \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062a\u06d5\u062e\u062a\u06cc \u0647\u06ce\u0698\u0627.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 - \u0648\u06ce\u0695\u0627\u06cc \u062e\u0648\u062f\u0627 - \u0647\u0627\u0646\u0627 \u0628\u06c6 \u06a9\u06d5\u0633\u06ce \u062a\u0631 \u0628\u06d5\u0631\u06ce \u0648 \u0628\u06d5\u06b5\u06af\u06d5\u0634\u06cc \u0644\u06d5 \u062f\u06d5\u0633\u062a\u0627 \u0646\u06d5\u0628\u06ce\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062f\u06d5\u06cc\u06a9\u06ce\u0634\u06ce\u062a\u06d5 \u067e\u0627\u06cc \u062d\u06d5\u0633\u0627\u0628 \u0648 \u0626\u06d5\u0648 \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u0627\u0646\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u0628\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u0645! \u062a\u06c6 \u0628\u0645 \u0628\u06d5\u062e\u0634\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u062a \u067e\u06ce\u0645\u062f\u0627 \u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u062a\u06c6 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc.',
    '\u0633\u0648\u0648\u0695\u06d5\u062a\u06ce\u06a9\u06d5 \u0646\u0627\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0648 [\u0645\u06d5\u0628\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646] \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u0626\u06d5\u0631\u06a9\u06cc \u067e\u06ce\u0648\u06cc\u0633\u062a\u061b \u0686\u06d5\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0634\u0645\u0627\u0646 \u062a\u06ce\u062f\u0627 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0634\u06a9\u06ce\u06a9\u0648\u0648 \u067e\u06d5\u0646\u062f [\u06cc \u067e\u06ce] \u0628\u06af\u0631\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u062f\u0627\u0648\u06ce\u0646\u067e\u06cc\u0633\u0627\u0646\u06cc \u0645\u06ce\u0631 \u0648 \u0698\u0646 - \u0647\u06d5\u0631\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 - \u0628\u06d5 \u0644\u06ce\u062f\u0627\u0646\u06cc \u0633\u06d5\u062f \u062a\u0627\u0632\u06cc\u0627\u0646\u06d5 \u062a\u06d5\u0645\u06ce \u06a9\u06d5\u0646. \u0644\u06d5 \u0626\u0627\u06cc\u06cc\u0646\u06cc \u0633\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u062f\u0627\u060c \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0647\u0627\u062a\u0646 \u0628\u06ce \u062c\u06ce\u06cc\u06d5. \u0628\u0627 \u0686\u06d5\u0646\u062f \u06a9\u06d5\u0633\u06cc\u0634 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u0644\u06d5\u0648 \u0644\u06ce\u062f\u0627\u0646\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0646.',
    '\u0645\u06ce\u0631\u06cc \u062f\u0627\u0648\u06ce\u0646\u067e\u06cc\u0633 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u0698\u0646\u06cc \u062f\u0627\u0648\u06ce\u0646 \u062a\u06d5\u0695 \u0628\u062e\u0648\u0627\u0632\u06ce\u061b \u06cc\u0627\u0646 \u0698\u0646\u06cc \u0628\u062a\u067e\u06d5\u0631\u0633\u062a \u0628\u06ce. \u0698\u0646\u06cc \u062f\u0627\u0648\u06ce\u0646 \u062a\u06d5\u0695\u06cc\u0634 \u0647\u06d5\u0631\u0648\u0627: \u06cc\u0627\u0646 \u0645\u06ce\u0631\u062f \u0628\u06d5 \u062f\u0627\u0648\u06ce\u0646\u067e\u06cc\u0633 \u0628\u06a9\u0627\u060c \u06cc\u0627\u0646 \u0645\u06ce\u0631\u06cc \u0628\u062a\u067e\u06d5\u0631\u0633\u062a \u0628\u06ce \u0648 \u0644\u06d5 \u0628\u06c6 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06cc\u0634 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0628\u06d5\u0631\u0628\u06d5\u0633\u062a \u06a9\u0631\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0648\u062e\u062a\u0627\u0646\u06ce \u0628\u06c6 \u0698\u0646\u06cc \u062f\u0627\u0648\u06ce\u0646 \u067e\u0627\u06a9 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0686\u0648\u0627\u0631 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u0634 \u0646\u0627\u0647\u06ce\u0646\u0646\u060c \u0647\u06d5\u0634\u062a\u0627 \u0634\u06d5\u0644\u0627\u0642\u06cc\u0627\u0646 \u0644\u06ce \u0628\u062f\u06d5\u0646. \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u0628\u06ce \u0628\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u0627\u0646 \u0642\u0628\u0648\u0648\u06b5 \u0628\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u067e\u0627\u0634 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u062f\u06d5\u0631\u062f\u06d5\u0628\u0695\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0686\u0627\u06a9 \u062f\u06d5\u06a9\u06d5\u0646. \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u06a9\u06d5 \u062e\u0648\u0627 \u0644\u06ce \u0628\u0648\u0631\u062f\u06d5\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0648\u062e\u062a\u0627\u0646\u06cc \u062f\u0627\u0648\u06ce\u0646\u062a\u06d5\u0695\u06cc \u0628\u06d5 \u0698\u0646\u0627\u0646\u06cc \u062e\u06c6 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u063a\u06d5\u06cc\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0686\u06d5\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc [\u062a\u0631\u06cc\u0627\u0646] \u0646\u06cc\u06cc\u06d5\u060c \u0647\u06d5\u0631 \u06a9\u0627\u0645\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce \u0686\u0648\u0627\u0631 \u062c\u0627\u0631 \u062e\u0648\u062f\u0627\u06cc \u0644\u06ce \u0628\u06d5 \u0634\u0627\u06cc\u06d5\u062a \u0628\u06af\u0631\u06ce \u06a9\u06d5 \u0644\u06d5\u0645 \u0628\u0627\u0631\u06d5 \u062f\u0631\u06c6 \u0646\u0627\u06a9\u0627.',
    '\u0628\u0627 \u0628\u06c6 \u062c\u0627\u0631\u06cc \u067e\u06ce\u0646\u062c\u06d5\u0645 \u0628\u06ce\u0698\u06ce: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0645 \u0642\u0633\u06d5\u0645 \u062f\u0631\u06c6\u06cc\u06d5\u060c \u0628\u06d5\u0631 \u0646\u06d5\u0641\u0631\u06cc\u0646\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0648\u0645.',
    '\u0698\u0646\u06d5\u06a9\u06d5\u0634 \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u0627\u0646\u06ce \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u0628\u06ce\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u06cc\u0634 \u0647\u06d5\u062a\u0627 \u0686\u0648\u0627\u0631 \u06a9\u06d5\u0695\u06d5\u062a \u062e\u0648\u0627 \u0628\u06d5 \u0634\u0627\u06cc\u06d5\u062a \u0628\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u06a9\u06d5 \u0645\u06ce\u0631\u062f\u06d5\u06a9\u06d5\u06cc \u062f\u0631\u06c6 \u062f\u06d5\u06a9\u0627.',
    '\u0628\u0627 \u0628\u06c6 \u062c\u0627\u0631\u06cc \u067e\u06ce\u0646\u062c\u06d5\u0645\u06cc\u0646 \u0628\u06ce\u0698\u06ce: \u0626\u06d5\u06af\u06d5\u0631 \u0642\u0633\u06d5\u06cc \u06a9\u0627\u0628\u0631\u0627 \u0695\u0627\u0633\u062a \u0628\u06ce\u060c \u0628\u06d5\u0631 \u063a\u06d5\u0632\u06d5\u0628\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0648\u0645.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0648 \u062a\u06c6\u0628\u06d5\u067e\u06d5\u0632\u06ce\u0631\u06cc \u062e\u0648\u062f\u0627\u062a\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u0628\u0627\u06cc\u06d5\u060c [\u0626\u06ce\u0648\u06d5 \u0686\u062a\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06d5\u0647\u0627\u062a\u061f].',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648 \u0628\u0648\u062e\u062a\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u0628\u0648\u0648\u060c \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0628\u0648\u0648\u0646. \u067e\u06ce\u062a\u0627\u0646 \u0648\u0627\u0646\u06d5\u0628\u06ce \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062e\u0631\u0627\u067e \u062f\u06d5\u0628\u06ce\u060c \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0642\u0627\u0632\u0627\u0646\u062c\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0648 \u0647\u06d5\u0631\u06a9\u06ce\u0634 \u0644\u06d5\u0648\u0627\u0646 \u0686\u06d5\u0646\u062f \u0644\u06d5\u0645 \u062a\u0627\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0634\u062f\u0627\u0631\u06d5\u060c \u062a\u0627\u0648\u0627\u0646\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631\u06cc \u0626\u06d5\u0648 \u06af\u0648\u0646\u0627\u0647\u06d5\u06cc \u0648\u06d5\u0626\u06d5\u0633\u062a\u06c6 \u06af\u0631\u062a\u060c \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062f\u0698\u0648\u0627\u0631\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0698\u0646 \u0648 \u067e\u06cc\u0627\u06af\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u060c \u0628\u06c6\u0686\u06cc \u0647\u06d5\u0631\u06af\u0627 \u0626\u06d5\u0645 \u062f\u06d5\u0646\u06af\u06c6\u06cc\u06d5 \u0648 \u0628\u06d5 \u06af\u0648\u06ce \u06af\u06d5\u06cc\u0634\u062a\u060c \u0628\u06d5 \u0686\u0627\u06a9\u06cc\u0627\u0646 \u0644\u06ce\u06a9 \u0646\u06d5\u062f\u0627\u06cc\u06d5\u0648\u06d5 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0646\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a \u06a9\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5 \u0626\u06d5\u0645\u06d5 \u062f\u0631\u06c6\u06cc \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u062f\u06cc \u0628\u06c6\u0686\u06cc \u0686\u0648\u0627\u0631\u0634\u0627\u06cc\u06d5\u062a\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u06d5\u0647\u06ce\u0646\u0627\u061f \u06a9\u06d5 \u0647\u06d5\u0631 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u0648\u06d5 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06ce\u062a\u0627\u0646\u062f\u0627 \u0646\u06d5\u0647\u0627\u062a\u0628\u0627 \u0648 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5 \u0646\u06d5\u06a9\u0631\u062f\u0628\u0627\u0646\u060c \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0648 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0631\u06c6\u06cc\u06d5 \u0633\u0627\u0632\u062a\u0627\u0646 \u06a9\u0631\u062f - \u0633\u0648\u0648\u0631 \u0628\u0632\u0627\u0646\u0646 - \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u06cc\u06d5\u06a9\u062c\u0627\u0631 \u06af\u06d5\u0648\u0631\u06d5\u062a\u0627\u0646 \u062a\u0648\u0648\u0634 \u062f\u06d5\u0628\u0648\u0648.',
    '\u06a9\u0627\u062a\u06ce \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0633\u062a \u0648 \u0647\u06cc\u0686\u06cc\u0634\u0648\u0648 \u0644\u06ce \u0646\u06d5\u062f\u06d5\u0632\u0627\u0646\u06cc\u060c \u0647\u06d5\u0631 \u0628\u06d5\u062f\u06d5\u0645\u0648\u0648\u062f\u0627 \u062f\u06d5\u0647\u0627\u062a \u0648 \u0628\u06d5 \u0632\u0645\u0627\u0646\u0648\u0648 \u062f\u06d5\u062a\u0627\u0646\u0642\u06c6\u0632\u062a\u06d5\u0648\u06d5 \u0648 \u0644\u0627\u062a\u0627\u0646 \u0648\u0627\u0628\u0648\u0648 \u0632\u06c6\u0631 \u0647\u0627\u0633\u0627\u0646\u06d5\u061b \u06a9\u06d5 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0644\u062c\u06d5\u0645 \u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u06af\u06d5\u0648\u0631\u06d5 \u0628\u0648\u0648.',
    '\u0628\u06c6\u0686\u06cc \u0648\u06d5\u062e\u062a\u06ce \u0626\u06d5\u0648\u06d5\u062a\u0627\u0646 \u0628\u06cc\u0633\u062a\u060c \u0646\u06d5\u062a\u0627\u0646\u06af\u0648\u062a \u06a9\u06d5: \u0626\u06ce\u0645\u06d5 \u0686\u06cc\u0645\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0645 \u0642\u0633\u0627\u0646\u06d5 \u062f\u0627\u0648\u06d5\u061b \u067e\u0627\u06a9\u06cc \u0648\u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u0628\u06c6 \u062a\u06c6\u06cc\u06d5\u061b \u0626\u06d5\u0645\u06d5 \u062a\u06c6\u0645\u06d5\u062a\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0646\u0633\u06cc\u062d\u06d5\u062a\u0648\u0648 \u062f\u06d5\u06a9\u0627\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u067e\u06ce\u06cc \u0647\u06d5\u06cc\u06d5\u060c \u062c\u0627\u0631\u06ce\u06a9\u06cc \u062a\u0631 \u0628\u0648\u062e\u062a\u0627\u0646\u06cc \u0648\u0627 \u0647\u06d5\u06b5\u0646\u06d5\u0628\u06d5\u0633\u062a\u0646.',
    '\u062e\u0648\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0695\u0648\u0648\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062d\u06d5\u0632\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u0628\u0627\u0633\u06cc \u06a9\u0627\u0631\u06cc \u0628\u06ce \u0646\u0627\u0645\u0648\u0648\u0633\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u062f\u0627 \u0628\u0628\u06ce\u062a\u06d5 \u0642\u0627\u0648\u060c \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634\u0627 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce \u0648 \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0648 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u06af\u0627\u062a\u0627\u0646 \u0644\u06ce \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0645\u06cc\u0647\u0631\u06d5\u0648\u0627\u0646\u06cc \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc \u0626\u06d5\u0648\u062a\u0627\u0646 \u0646\u06d5\u0628\u0627\u060c [\u062f\u06cc\u0627\u0631\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u0628\u0648\u0648\u0646].',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0645\u06d5\u062f\u06d5\u0646\u06d5 \u0633\u06d5\u0631 \u0634\u06c6\u067e\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06af\u0627\u06a4 \u0644\u06d5\u062c\u06ce \u06af\u0627\u06a4\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646 \u062f\u0627\u0646\u06ce\u060c \u0628\u0627 \u0628\u0632\u0627\u0646\u06ce \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0646\u0627\u0634\u06cc\u0631\u06cc\u0646 \u0648 \u06a9\u0631\u06ce\u062a \u062f\u06d5\u062f\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06d5\u0628\u0627\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce\u06a9\u062a\u0627\u0646 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u067e\u0627\u06a9 \u0646\u06d5\u062f\u06d5\u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 - \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627 - \u06af\u06d5\u0645\u0627\u0631\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0644\u0627\u062f\u06d5\u0628\u0627 \u0648 \u062e\u0648\u062f\u0627 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0628\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u0627\u0648\u062a\u0627\u0646\u062f\u0627 \u062f\u06d5\u0648\u06b5\u06d5\u0645\u06d5\u0646\u062f\u0646\u060c \u0633\u0648\u06ce\u0646\u062f\u06ce \u0646\u06d5\u062e\u06c6\u0646 \u06a9\u06d5 \u062e\u0632\u0645\u0627\u0646 \u0648 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646 \u0648 \u0626\u0627\u0648\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u06ce \u0628\u06d5\u0634 \u062f\u06d5\u06a9\u06d5\u0646. \u0628\u06b5\u0627 \u0686\u0627\u0648\u06cc\u0627\u0646 \u0644\u06ce \u0628\u067e\u06c6\u0634\u0646 \u0648 \u0644\u06d5 \u0647\u06d5\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u0646\u061b \u0645\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u062d\u06d5\u0632 \u0646\u0627\u06a9\u06d5\u0646 \u062e\u0648\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u0648\u0648 \u0628\u0628\u0648\u0648\u0631\u06ce\u061f \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06c6 \u0698\u0646\u0627\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06cc \u062f\u0627\u0648\u06ce\u0646\u067e\u0627\u06a9\u06cc \u062f\u0648\u0648\u0631\u06d5 \u0626\u0627\u06af\u0627 [\u0633\u06d5\u0631\u0628\u06d5\u062e\u06c6] \u0628\u0648\u062e\u062a\u0627\u0646 \u0695\u06ce\u06a9 \u062f\u06d5\u062e\u06d5\u0646\u060c \u0644\u06d5 \u062f\u0646\u06cc\u0627\u0634 \u0648 \u0644\u06d5 \u0633\u06d5\u06b5\u0627\u0634\u0627\u060c \u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u0646\u061b \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u0634\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631\u06d5.',
    '\u0695\u06c6\u0698\u06ce \u062f\u06d5\u0628\u06ce \u06a9\u06d5 \u0632\u0645\u0627\u0646 \u0648 \u062f\u06d5\u0633\u062a \u0648 \u067e\u0627\u06cc\u0627\u0646\u060c \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f \u062f\u06d5\u0628\u0646\u06d5 \u0634\u0627\u06cc\u06d5\u062a.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0686\u06cc \u0644\u06cc\u0627\u0646 \u062f\u06d5\u0648\u06d5\u0634\u06ce\u062a\u06d5\u0648\u06d5\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646\u06cc \u062f\u06d5\u06a9\u0627. \u0633\u0627 \u062f\u06d5\u0632\u0627\u0646\u0646 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0642\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5.',
    '\u06a9\u06ce \u0646\u0627\u067e\u0627\u06a9\u0646 \u0628\u06c6 \u0646\u0627\u067e\u0627\u06a9\u0627\u0646. \u06cc\u0627 \u067e\u0627\u06a9\u06cc\u0634\u0646 \u0628\u06c6 \u067e\u0627\u06a9\u0627\u0646\u0646. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u0648\u0648\u0631\u0646 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0642\u0633\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u06d5\u06b5\u0628\u0628\u06d5\u0633\u062a\u0646\u061b [\u062e\u0648\u062f\u0627] \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u062e\u0634\u06cc\u0648\u06d5 \u0648 \u0628\u0698\u06cc\u0648\u06cc \u0628\u0627\u0634\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06ce.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u062c\u06cc\u0627 \u0644\u06d5\u0648\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06ce\u060c \u0633\u06d5\u0631 \u0628\u06d5 \u0647\u06cc\u0686 \u0645\u0627\u06b5\u0627\u0646\u062f\u0627 \u0645\u06d5\u06a9\u06d5\u0646 \u0647\u06d5\u062a\u0627 \u0626\u06cc\u0632\u0646\u06ce \u0646\u06d5\u062e\u0648\u0627\u0632\u0646 \u0648 \u0633\u06b5\u0627\u0648 \u0628\u06c6 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u062f\u0627\u0646\u06d5\u062e\u06d5\u0646. \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0634\u062a\u0631\u06d5 \u0648 \u0634\u0627\u06cc\u06d5\u062a \u067e\u06d5\u0646\u062f\u06cc \u0644\u06ce \u0648\u06d5\u0631\u0628\u06af\u0631\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u06a9\u06d5\u0633\u0648\u0648 \u062a\u06ce\u062f\u0627 \u0646\u06d5\u062f\u06cc\u062a\u060c \u0645\u06d5\u0686\u0646\u06d5 \u0698\u0648\u0648\u0631\u06ce \u062a\u0627 \u062e\u0627\u0648\u06d5\u0646\u06cc \u0626\u06cc\u0632\u0646\u0648\u0648 \u062f\u06d5\u062f\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u06af\u0648\u062a\u06cc\u0634\u06cc\u0627\u0646: \u0648\u06d5\u06af\u06d5\u0695\u06ce\u0646\u060c \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0634\u062a\u0631\u06d5. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0647\u06cc\u0686 \u0633\u06d5\u0631\u06a9\u06c6\u0646\u06d5\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5 \u0628\u0686\u0646\u06d5 \u0645\u0627\u06b5\u06ce\u06a9 \u06a9\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5 \u0648 \u06a9\u06d5\u0644 \u067e\u06d5\u0644\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5. \u0647\u06d5\u0631\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u0634\u06ce\u0631\u0646\u06d5\u0648\u06d5 \u0648 \u062e\u06c6\u06cc\u0627\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u062a\u06c6 \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0628\u06ce\u0698\u06d5\u061b \u0686\u0627\u0648 \u062f\u0627\u062e\u06d5\u0646 \u0648 \u0634\u0648\u0648\u0631\u06d5\u06cc\u06cc \u062e\u06c6 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646. \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0628\u0627\u0634\u062a\u0631\u06d5 \u0648 \u062e\u0648\u062f\u0627\u0634 \u0626\u0627\u06af\u0627\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0628\u06d5 \u0698\u0646\u0627\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06cc\u0634 \u0628\u06ce\u0698\u06d5 \u0628\u0627 \u0686\u0627\u0648\u06cc\u0627\u0646 \u062f\u0627\u062e\u06d5\u0646 \u0648 \u0634\u06d5\u0631\u0645\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646 \u0648 \u062e\u0634\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648\u06d5\u062f\u06d5\u0631 \u0646\u06d5\u062e\u06d5\u0646\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u06d5\u0631\u06ce\u0646. \u0628\u0627 \u0633\u06d5\u0631\u067e\u06c6\u0634\u06cc\u0627\u0646 \u062a\u0627 \u0633\u06d5\u0631 \u067e\u06ce\u0633\u06cc\u0631 \u062f\u0627\u0628\u067e\u06c6\u0634\u06ce \u0648 \u062e\u06d5\u0645\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0646\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5 \u0645\u06ce\u0631\u062f\u060c \u06cc\u0627\u0646 \u0628\u0627\u0628\u06c6\u06cc\u0627\u0646\u060c \u06cc\u0627 \u062e\u06d5\u0632\u0648\u0631\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0646\u06ce\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u0632\u0695\u06a9\u0648\u0695\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u0628\u0631\u0627\u060c \u06cc\u0627\u0646 \u0628\u0631\u0627\u0632\u0627\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u06a9\u0648\u0695\u0627\u0646\u06cc \u062e\u0648\u0634\u06a9\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u0698\u0646\u0627\u0646\u06cc \u0647\u0627\u0648\u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u0626\u06d5\u0648 \u0646\u06ce\u0631\u06cc \u062e\u0648\u06b5\u0627\u0645\u06cc\u0627\u0646\u0646\u060c \u06cc\u0627\u0646 \u0626\u06d5\u0648 \u067e\u06cc\u0627\u0648\u06cc \u0633\u06d5\u0631\u0628\u06d5\u0648\u0627\u0646\u0646 \u0648 \u0647\u06d5\u0631 \u0646\u06cc\u0627\u0632\u06cc\u0627\u0646 \u0628\u06d5 \u0698\u0646 \u0646\u06cc\u06cc\u06d5\u060c \u06cc\u0627\u0646 \u0645\u0646\u062f\u0627\u06b5\u0627\u0646\u06cc \u0646\u06d5\u06af\u06d5\u06cc\u0648\u0646\u06d5 \u0626\u06d5\u0648 \u062a\u06d5\u0645\u06d5\u0646\u06d5\u06cc \u0648\u0627\u0632\u06cc\u0627\u0646 \u0628\u0686\u06ce\u062a\u06d5 \u0645\u06ce\u0648\u06cc\u0646\u06d5. \u067e\u0627\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u0632\u06d5\u0648\u06cc\u062f\u0627 \u0646\u06d5\u062f\u06d5\u0646\u060c \u06a9\u06d5 \u0628\u0632\u0627\u0646\u0631\u06ce \u0686\u06c6\u0646 \u062e\u0634\u06b5\u06ce\u06a9\u06cc\u0627\u0646 \u0648\u06d5\u0634\u0627\u0631\u062f\u0648\u0648\u06d5. \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u062a\u0627\u0646 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u060c \u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06d5\u0634\u06a9\u0648\u0648 \u06af\u0634\u062a\u0648\u0648 \u0695\u0632\u06af\u0627\u0631 \u0628\u0628\u0646.',
    '\u0645\u06ce\u0648\u06cc\u0646\u06d5\u06cc \u0628\u06ce \u0645\u06ce\u0631\u062f\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5 \u0634\u0648\u0648 \u0628\u062f\u06d5\u0646. \u0639\u06d5\u0628\u062f \u0648 \u06a9\u06d5\u0646\u06cc\u0632\u06cc \u0647\u06ce\u0698\u0627\u0634\u062a\u0627\u0646 \u0628\u0632\u06d5\u0648\u062c\u06ce\u0646\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646\u06cc\u0634 \u0628\u0646\u060c \u062e\u0648\u062f\u0627 \u0628\u06d5 \u062f\u06b5\u0627\u0648\u0627\u06cc\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627 \u0628\u06ce \u0646\u06cc\u0627\u0632 \u062f\u06d5\u06a9\u0627. \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u0627\u0648\u06d5 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0628\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062a\u0648\u0627\u0646\u0627\u06cc \u0632\u06d5\u0645\u0627\u0648\u06d5\u0646\u062f\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u060c \u062e\u06c6\u0695\u0627 \u0628\u06af\u0631\u0646 \u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 - \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u06cc - \u062f\u06d5\u0631\u0648\u0648\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 [\u0648 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0645\u06d5\u06cc\u0633\u06d5\u0631 \u062f\u06d5\u06a9\u0627]. \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0645\u0627\u06b5\u06cc \u0626\u06ce\u0648\u06d5\u0646 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u067e\u0633\u0648\u0648\u0644\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u0646\u0648\u0648\u0633\u0631\u06ce\u060c \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06ce\u0631\u06ce\u06a9\u062a\u0627\u0646 \u062a\u06ce\u062f\u0627 \u062f\u06cc\u0646\u060c \u0628\u0627 \u067e\u0633\u0648\u0648\u0644\u06d5\u0648 \u0644\u06d5 \u0646\u0627\u0648\u062f\u0627 \u0628\u06ce \u0648 \u0634\u062a\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5\u0648 \u0645\u0627\u06b5\u06d5\u06cc \u062e\u0648\u062f\u0627 \u067e\u06ce\u06cc \u062f\u0627\u0648\u0646\u060c \u067e\u06ce \u0628\u0628\u06d5\u062e\u0634\u0646. \u0646\u06d5\u0686\u0646 \u06a9\u06d5\u0646\u06cc\u0632\u06d5\u06a9\u0627\u0646\u06cc\u0634\u062a\u0627\u0646 - \u06a9\u06d5 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u067e\u0627\u06a9 \u0628\u0698\u06cc\u0646 - \u0646\u0627\u0686\u0627\u0631 \u0628\u06a9\u06d5\u0646 \u062e\u06c6 \u0628\u0641\u0631\u06c6\u0634\u0646\u061b \u0647\u06d5\u0631 \u0647\u06d5\u062a\u0627 \u0645\u0627\u06b5\u06cc \u062f\u0646\u06cc\u0627\u062a\u0627\u0646 \u0648\u06d5\u062f\u06d5\u0633 \u06a9\u06d5\u0648\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0646\u0627\u0686\u0627\u0631\u06cc\u0627\u0646 \u06a9\u0627\u060c \u062e\u0648\u062f\u0627 \u067e\u0627\u0634 \u0646\u0627\u0686\u0627\u0631 \u06a9\u0631\u062f\u0646\u06cc\u0627\u0646\u060c \u06af\u0648\u0646\u0627\u0628\u06d5\u062e\u0634 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0646\u06cc\u0634\u0627\u0646\u062f\u06d5\u0631\u0645\u0627\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0631\u062f\u060c \u0648\u06ce\u0695\u0627\u06cc \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0633\u06d5\u0631\u06af\u0648\u0695\u0648\u0634\u062a\u06d5\u06cc \u0695\u0627\u0628\u0631\u062f\u0648\u0648\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u0634 \u0626\u06ce\u0648\u06d5 \u0648 \u067e\u06d5\u0646\u062f\u06ce\u06a9\u06cc\u0634 \u0628\u06c6 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u0627\u0646.',
    '\u062e\u0648\u0627 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5. \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u0626\u06d5\u0648 \u0648\u06d5\u06a9 \u062a\u0627\u0642\u06ce\u06a9\u06d5 \u0686\u0631\u0627\u06cc \u062a\u06ce\u062f\u0627 \u0647\u06d5\u06b5\u06a9\u0631\u0627 \u0628\u06ce\u060c \u0686\u0631\u0627\u06a9\u06d5 \u0644\u06d5 \u062c\u0627\u0645\u06ce\u06a9\u062f\u0627 \u0628\u06ce\u060c \u062c\u0627\u0645\u06d5\u06a9\u06d5 \u0648\u06d5\u06a9 \u0626\u06d5\u0633\u062a\u06ce\u0631\u06d5\u06cc\u06d5\u06a9 \u062a\u0631\u0648\u0648\u0633\u06a9\u06d5\u06cc \u0628\u06ce \u0648 \u0645\u0627\u06a9\u06cc \u0686\u0631\u0627 \u0644\u06d5 \u0632\u06d5\u06cc\u062a\u0648\u0648\u0646\u06ce\u06a9\u06cc \u0645\u0645\u0628\u0627\u0631\u06d5\u06a9 \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u062a\u0628\u06ce\u061b \u06a9\u06d5 \u062f\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u0646\u06d5 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0648 \u0646\u06d5 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0644\u06d5 \u0646\u0632\u0627\u0631\u06d5. \u062a\u06c6 \u0626\u06ce\u0698\u06cc \u0695\u06c6\u0646\u06cc \u0686\u0631\u0627\u06a9\u06d5 - \u0628\u06d5 \u0628\u06ce \u0626\u0627\u06af\u0631 \u062a\u062e\u0648\u0648\u0646 \u06a9\u06d5\u0648\u062a\u0646 - \u062a\u06cc\u0634\u06a9 \u062f\u06d5\u062f\u0627\u061b \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u0644\u06d5 \u0628\u0627\u0646 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u062e\u06c6\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627. \u062e\u0648\u062f\u0627 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0646\u06d5\u0632\u06cc\u0644\u0627\u0646 \u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5. \u062e\u0648\u0627 \u0628\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0644\u06d5 \u0646\u0627\u0648 \u0645\u0627\u06b5\u0627\u0646\u06ce\u06a9\u062f\u0627 \u062e\u0648\u062f\u0627 \u0626\u06cc\u0632\u0646\u06cc \u062f\u0627\u0648\u06d5\u060c \u06a9\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0695\u06ce\u0632\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06ce \u0648 \u0646\u0627\u0648\u06cc \u0626\u06d5\u0648\u06cc\u0627\u0646 \u062a\u06cc\u0627 \u0628\u0628\u0631\u06ce\u060c \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646 \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u062f\u06d5\u0646:',
    '\u067e\u06cc\u0627\u0648\u0627\u0646\u06ce \u06a9\u06d5 \u0646\u06d5 \u0628\u0627\u0632\u0631\u06af\u0627\u0646\u06cc \u0646\u06d5 \u0633\u06d5\u0648\u062f\u0627\u060c \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0646\u0648\u06ce\u0698 \u06a9\u0631\u062f\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u0627\u0646\u06cc\u0627\u0646\u060c \u0644\u06d5 \u0628\u06cc\u0631 \u0646\u0627\u0628\u0627\u061b \u062a\u0631\u0633\u06cc \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u06a9\u06d5 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u062f\u06b5 \u0648 \u0686\u0627\u0648 \u06af\u0634 \u0648\u06d5\u0631\u062f\u06d5\u0686\u06d5\u0631\u062e\u06ce\u0646.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u06d5\u06cc \u062e\u0648\u0627 \u0644\u06d5 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0628\u0627\u0634\u062a\u0631 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u0628\u062f\u0627\u062a\u06ce \u0648 \u0628\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0644\u06ce\u0634\u06cc\u0627\u0646 \u0632\u06cc\u0627\u062f \u06a9\u0627. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u0628\u06d5 \u0628\u06ce \u0647\u06d5\u0698\u0645\u0627\u0631 \u0695\u06c6\u0632\u06cc \u062f\u06d5\u062f\u0627.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646\u060c \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0648\u06d5\u06a9 \u0626\u06d5\u0648 \u062a\u0631\u0627\u0648\u06cc\u0644\u06a9\u06d5 \u0648\u0627\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0628\u06cc\u0627\u0648\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5. \u062a\u06cc\u0646\u0648\u0648 \u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0626\u0627\u0648\u06d5 \u0648 \u06a9\u06d5 \u062f\u06d5\u06cc\u06af\u0627\u062a\u06ce\u060c \u0626\u06d5\u0648\u0633\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0647\u06cc\u0686 \u0646\u06cc\u06cc\u06d5. \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0646\u0632\u06cc\u06a9\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0628\u06cc\u0646\u06ce \u0648 \u062d\u06cc\u0633\u0627\u0628\u06cc \u0626\u06d5\u0646\u06ce\u062a\u06d5 \u0645\u0633\u062a\u06cc. \u062e\u0648\u0627 \u0632\u0648\u0648 \u0628\u06d5 \u062d\u06cc\u0633\u0627\u0628 \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u06cc\u0627\u0646 \u0648\u06d5\u06a9 \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u0633\u062a\u0627\u0646\u06ce\u060c \u0644\u06d5 \u0632\u06d5\u0631\u06cc\u0627\u06cc\u06d5\u06a9\u06cc \u0628\u06ce \u067e\u06d5\u06cc\u062f\u0627\u060c \u067e\u06ce\u0644 \u0644\u06d5\u0633\u06d5\u0631 \u067e\u06ce\u0644 \u062f\u0627\u06cc \u067e\u06c6\u0634\u0646 \u0648 \u0644\u06d5 \u0644\u0627\u06cc \u0698\u0648\u0648\u0631\u0648\u0648\u0634\u06cc \u0647\u06d5\u0648\u0631 \u0628\u06ce\u061b \u062a\u0627\u0631\u06cc\u06a9\u0627\u06cc\u06cc \u0648 \u0647\u06d5\u0631\u06af\u0627 \u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0631\u0628\u062e\u0627\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0647\u06d5\u0631 \u0646\u06d5\u06cc\u0628\u06cc\u0646\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062e\u0648\u0627 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u0646\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0627\u060c \u0647\u06cc\u0686 \u0695\u0648\u0648\u0646\u0627\u06a9\u0627\u06cc\u06cc \u0634\u06a9 \u0646\u0627\u0628\u0627.',
    '\u062a\u06c6 \u0646\u06d5\u062a \u062f\u06cc\u0648\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u0626\u06d5\u0648 \u0645\u06d5\u0644\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u062d\u06d5\u0648\u0627 \u0644\u06d5\u0646\u06af\u06d5\u0631 \u062f\u06d5\u06af\u0631\u0646\u060c \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u062f\u06d5\u0646\u061f \u06af\u0634\u062a\u06cc\u0627\u0646 \u0646\u0632\u0627 \u0648 \u067e\u06d5\u0633\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u0646 \u0648 \u062e\u0648\u0627 \u0644\u06d5 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0645\u0648\u06b5\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0647\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u0634 \u0628\u06c6 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u0633.',
    '\u0626\u06d5\u062a\u06c6 \u0646\u06d5\u062a \u062f\u06cc\u0648\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0648\u0631\u06ce\u06a9 \u0644\u06d5 \u0633\u06d5\u0631\u06d5\u062e\u06c6 \u062f\u06d5\u0626\u0627\u0698\u0648\u06ce\u061b \u067e\u0627\u0634\u0627\u0646 \u0686\u06d5\u0646\u062f \u067e\u0627\u0631\u0686\u06d5\u06cc \u0644\u06ce\u06a9 \u062f\u06d5\u062f\u0627 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0627 \u0628\u06d5 \u0647\u06d5\u0648\u0631\u06ce\u06a9\u06cc \u067e\u0695\u061f \u0626\u06d5\u0648\u0633\u0627 \u062a\u06c6 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u062f\u06b5\u06c6\u067e\u06d5\u06cc \u0628\u0627\u0631\u0627\u0646\u06cc \u0644\u06ce \u062f\u06ce\u062a\u06d5 \u062f\u06d5\u0631\u061b [\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u0634\u06d5] \u0644\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5 \u0648 \u0644\u06d5\u0648 \u06a9\u06ce\u0648\u0627\u0646\u06d5 - \u06a9\u06d5 \u0644\u06d5\u0648\u06ce\u062f\u0627\u0646 - \u062a\u06d5\u06cc\u0631\u06c6\u06a9\u0627\u0646 \u062f\u0627\u062f\u06d5\u0628\u0627\u0631\u06ce\u0646\u06ce\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0626\u06d5\u0648 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce\u060c \u0628\u06d5\u0631\u06cc \u062f\u06d5\u062f\u0627 \u0648 \u06a9\u06ce\u0634 \u062d\u06d5\u0632 \u06a9\u0627 \u0628\u06d5\u0631\u06cc \u0646\u0627\u06a9\u06d5\u0648\u06ce\u061b \u0628\u0631\u0648\u0648\u0633\u06a9\u06d5\u06a9\u06d5\u06cc \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0633\u06c6\u0645\u0627 \u0644\u06d5 \u0686\u0627\u0648\u0627\u0646 \u0628\u0695\u0641\u06ce\u0646\u06ce.',
    '\u0626\u0627\u06b5\u0648\u06af\u06c6\u0695\u06cc \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698\u0627\u0646 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u06a9\u0627. \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u0634\u062f\u0627 \u067e\u06d5\u0646\u062f\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06ce \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u062c\u0627\u0646\u06d5\u0648\u06d5\u0631\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u0626\u0627\u0648 \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u0648\u06d5. \u0647\u06d5\u06cc\u06d5 \u0632\u06af\u06d5\u062e\u0634\u06a9\u06ce \u062f\u06d5\u06a9\u0627 \u0648\u060c \u0647\u06d5\u06cc\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0648\u0648 \u067e\u06ce \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u06cc\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0686\u0648\u0627\u0631 \u067e\u06d5\u0644\u0627\u0646\u06d5. \u062e\u0648\u0627 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u062d\u06d5\u0632\u06ce \u0628\u06a9\u0627 \u0633\u0627\u0632\u06cc \u0626\u06d5\u062f\u0627. \u062e\u0648\u062f\u0627 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0645\u06d5 \u0686\u06d5\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0646\u06cc\u0634\u0627\u0646\u062f\u06d5\u0631\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u06ce. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062d\u06d5\u0632 \u06a9\u0627\u060c \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u0634\u06b5\u06ce\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0646. \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0645\u06d5\u0634 \u0628\u0695\u06ce\u06a9\u06cc\u0627\u0646 \u0695\u06c6 \u0648\u06d5\u0631\u062f\u06d5\u06af\u06ce\u0695\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0628\u0695\u0648\u0627\u062f\u0627\u0631 \u0646\u06cc\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627\u06cc\u06d5\u06a9\u06cc\u0634 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0648 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06cc \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06a9\u06d5 \u062e\u06c6\u06cc \u0628\u06d5 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0695\u0627\u0628\u06af\u0627\u061b \u06a9\u06d5\u0686\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06ce \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u0648\u0627\u0646 \u0628\u06ce\u060c \u062f\u06ce\u0646\u06d5 \u0644\u0627\u06cc \u0648 \u062f\u06d5\u06cc \u0633\u06d5\u0644\u0645\u06ce\u0646\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u06d5\u063a\u06d5\u0632\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5\u060c \u06cc\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u0646 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06a9\u06d5\u0646\u061f \u0646\u0627\u060c \u0626\u06d5\u0648\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u0646.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0646\u060c \u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce \u0628\u0627\u0646\u06af \u062f\u06d5\u06a9\u0631\u06ce\u0646 \u06a9\u06d5 \u0628\u06ce\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0648 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06cc\u060c \u0647\u06d5\u062a\u0627 \u0628\u06d5 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0695\u0627\u0628\u06af\u0627\u061b \u06af\u0648\u0631\u062c\u06ce \u062f\u06d5\u06b5\u06ce\u0646: \u0628\u06cc\u0633\u062a\u0645\u0627\u0646 \u0648 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06cc\u0627\u0631\u06d5 \u0695\u0632\u06af\u0627\u0631\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0628\u06ce \u0648 \u062a\u0631\u0633 \u0648 \u0634\u06d5\u0631\u0645\u06cc \u0644\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u0628\u06ce\u060c \u0626\u06d5\u0648\u0627\u0646 \u0628\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u062a\u06d5\u0648\u06d5.',
    '\u0628\u06d5 \u0633\u0648\u06ce\u0646\u062f\u0627\u0646 \u0695\u06d5\u0634 \u0628\u0648\u0648\u0646\u06d5\u062a\u06d5\u0648\u06d5\u060c \u06a9\u06d5: \u0628\u06d5 \u062e\u0648\u062f\u0627\u060c \u0647\u06d5\u0631\u06af\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0627\u0646 \u067e\u06ce \u0628\u062f\u06d5\u06cc \u0628\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627\u060c \u062e\u06c6 \u0646\u0627\u06af\u0631\u0646. \u0628\u06ce\u0698\u06d5: \u0647\u06cc\u0686 \u0633\u0648\u06ce\u0646\u062f\u06cc \u067e\u06ce \u0646\u0627\u0648\u06ce\u061b \u0641\u06d5\u0631\u0645\u0627\u0646\u0628\u06d5\u0631\u062f\u0627\u0631\u06ce\u06a9\u06cc \u062e\u0627\u0633 \u0628\u0646. \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u0646. \u062c\u0627 \u06a9\u06d5 \u0647\u06d5\u0631 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0648\u06d5\u067e\u0627\u0634 \u062f\u0627\u060c \u0626\u06d5\u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0631\u06a9\u06cc \u062e\u06c6\u06cc \u0644\u06d5\u0633\u06d5\u0631\u06d5\u0648 \u0626\u06ce\u0648\u06d5\u0634 \u0626\u06d5\u0631\u06a9\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0642\u0633\u06d5\u06cc \u0626\u06d5\u0648 \u0628\u06a9\u06d5\u0646\u060c \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc\u06d5\u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646. \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634 \u0644\u06d5 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646 \u0628\u06d5\u0648\u0644\u0627\u0648\u06d5 \u0647\u06cc\u0686\u06cc \u062a\u0631\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u062e\u0648\u0627 \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062f\u0627: \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0627\u062a\u06d5 \u062c\u06ce\u06af\u06d5\u0646\u0634\u06cc\u0646\u061b \u0647\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u0686\u06c6\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u06cc \u06a9\u0631\u062f\u06d5 \u062c\u06ce\u0646\u0634\u06cc\u0646. \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u0634\u06cc\u0627\u0646 - \u06a9\u06d5 \u062e\u06c6\u06cc \u067e\u06d5\u0633\u0646\u062f\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5 - \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u0627\u0646 \u062f\u0627\u062f\u06d5\u0645\u06d5\u0632\u0631\u06ce\u0646\u06ce \u0648 \u062a\u0631\u0633\u06d5\u06a9\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u06a9\u0627\u062a\u06d5 \u062f\u06b5\u0646\u06cc\u0627\u06cc\u06cc [\u062a\u0627] \u062a\u06d5\u0646\u06cc\u0627 \u0645\u0646 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc\u0634 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0645\u0646 \u0646\u06d5\u0632\u0627\u0646\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5\u0645\u06d5 \u0628\u06d5\u0648\u0644\u0627 \u0633\u067e\u06b5\u06d5\u06cc\u06cc \u0628\u06a9\u0627\u062a\u06d5 \u067e\u06cc\u0634\u06d5\u060c \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0633\u06d5\u0631 \u0686\u06d5\u0648\u062a\u06d5\u06a9\u0627\u0646\u06d5.',
    '\u0646\u0648\u06ce\u0698\u06d5\u06a9\u06d5\u062a\u0627\u0646\u06cc\u0634 \u0628\u062e\u0648\u06ce\u0646\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u06d5\u0646 \u0648 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0628\u0646\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc \u062e\u0648\u0627\u06a9\u06d5\u0648\u0646.',
    '\u0648\u0627\u0645\u06d5\u0632\u0627\u0646\u0646 \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u060c \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0644\u06d5\u062f\u06d5\u0633\u062a\u0646 \u0695\u0627\u06a9\u06d5\u0646\u061b \u062c\u06ce\u06af\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0626\u0627\u06af\u0631\u06d5\u0648 \u0633\u06d5\u0631\u0626\u06d5\u0646\u062c\u0627\u0645\u06cc\u0627\u0646 \u0632\u06c6\u0631\u062e\u0631\u0627\u067e\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u06b5\u0627\u0645 \u0648 \u06a9\u06d5\u0646\u06cc\u0632\u06cc \u0626\u06ce\u0648\u06d5\u0646\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u06a9\u06d5 \u0628\u0644\u0648\u0648\u0642 \u0646\u06d5\u0628\u0648\u0648\u0646\u060c \u0633\u06ce \u062c\u0627\u0631 \u0626\u06cc\u0632\u0646\u0648\u0648 \u0644\u06ce \u0628\u062e\u0648\u0627\u0632\u0646: \u0644\u06d5 \u067e\u06ce\u0634 \u0646\u0648\u06ce\u0698\u06cc \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0648 \u0646\u06cc\u0645\u06d5\u0695\u06c6\u06cc\u0627\u0646\u060c \u06a9\u0627\u062a\u06ce \u0634\u0645\u06d5\u06a9\u0627\u0646 \u062f\u0627\u062f\u06d5\u0646\u06ce\u0646 \u0648 \u0644\u06d5 \u067e\u0627\u0634 \u0646\u0648\u06ce\u0698\u06cc \u062e\u06d5\u0648\u062a\u0646\u0627\u0646\u06cc\u0634\u061b \u06a9\u06d5 \u0626\u06d5\u0645 \u0633\u06ce \u06a9\u0627\u062a\u06d5 \u0648\u06d5\u062e\u062a\u06cc \u062a\u06d5\u0646\u06cc\u0627\u06cc\u06cc \u0626\u06ce\u0648\u06d5\u0633. \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u060c \u0646\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0642\u06d5\u06cc\u062f\u06ce \u062f\u06d5\u06a9\u0627\u060c \u0646\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u060c \u06a9\u06d5 \u0628\u06d5 \u062f\u06d5\u0648\u0631\u0648\u0628\u06d5\u0631\u062a\u0627\u0646\u062f\u0627 \u0628\u06ce\u0646\u061b \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u062a\u06ce\u06a9\u06d5\u06b5\u0646. \u0626\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u06d5\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce \u0645\u0646\u062f\u0627\u06b5\u06d5\u06a9\u0627\u0646\u0648\u0648 \u0628\u0644\u0648\u0648\u0642\u06cc\u0627\u0646 \u0628\u0648\u0648 - \u0626\u06d5\u0648\u06cc\u0634 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0628\u06d5\u0631\u06ce - \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u0626\u06cc\u0632\u0646 \u0628\u062e\u0648\u0627\u0632\u0646. \u0626\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u06d5\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5\u06cc \u0648\u06d5\u062e\u062a\u06cc \u0634\u0648\u0648\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0686\u0648\u0648\u06d5\u060c \u0647\u06cc\u0686 \u062a\u0627\u0648\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0633\u06d5\u0631\u067e\u06c6\u0634 \u0644\u06d5\u0633\u06d5\u0631 \u0641\u0695\u06ce \u062f\u06d5\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0646\u0627\u0628\u06ce \u062e\u0634\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u0646. \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0648\u06d5\u0647\u0627 \u0628\u0627\u0634\u062a\u0631\u06d5 \u067e\u0627\u06a9 \u0648 \u0634\u06d5\u0631\u0645\u06ce\u0648\u0646 \u0628\u0645\u06ce\u0646\u0646. \u062e\u0648\u062f\u0627 \u0626\u06d5\u0698\u0646\u06d5\u0648\u06ce \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0634\u06d5\u0644 \u0648 \u06a9\u0648\u06ce\u0631\u0627\u0646\u060c \u0644\u06d5\u0634 \u0628\u06d5\u0628\u0627\u0631 \u0648 \u0628\u0646\u0627\u0648\u0628\u0648\u06ce\u0631\u0627\u0646\u060c \u062e\u0648\u062f\u06cc \u062e\u06c6\u0634\u062a\u0627\u0646\u060c \u0647\u06cc\u0686 \u062a\u0627\u0648\u0627\u0646\u0648\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5 \u0644\u06d5 \u0645\u0627\u06b5 \u062e\u06c6\u062a\u0627\u0646 \u06cc\u0627\u0646 \u0628\u0627\u0648\u06a9\u0627\u0646\u062a\u0627\u0646\u060c \u06cc\u0627\u0646 \u062f\u0627\u06cc\u06a9\u0627\u0646\u062a\u0627\u0646\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u0628\u0631\u0627\u06cc\u0627\u0646\u062a\u0627\u0646\u060c \u06cc\u0627\u0646 \u0645\u0627\u06b5\u06cc \u0633\u06d5\u0631\u0628\u06d5 \u062e\u0648\u0634\u06a9\u0627\u0646\u0648\u0648\u060c \u06cc\u0627 \u0645\u0627\u06b5\u06cc \u0626\u0627\u067e \u0648 \u067e\u0644\u06a9\u0627\u0646\u0648\u0648\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0645\u0627\u06b5\u0627\u0646\u06cc \u062e\u0627\u06b5\u0627\u0646\u0648\u0648\u060c \u06cc\u0627\u0646 \u0645\u06cc\u0645\u0627\u0646\u0648\u0648\u060c \u06cc\u0627\u0646 \u0626\u06d5\u0648 \u0645\u0627\u06b5\u06d5\u06cc \u06a9\u0644\u06cc\u0644\u06d5\u06a9\u06d5\u06cc \u0644\u0627\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062f\u06c6\u0633\u062a\u06cc \u0686\u0627\u06a9\u0648\u0648\u060c \u062e\u0648\u0627\u0631\u062f\u0646 \u0628\u062e\u06c6\u0646. \u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u06cc\u0634\u062a\u0627\u0646 \u0646\u0627\u06af\u0627\u062a\u06ce \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0628\u062e\u06c6\u0646\u061b \u06cc\u0627\u0646 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u06d5 \u062a\u06d5\u0646\u06cc\u0627 \u0628\u062e\u0648\u0627. \u0647\u06d5\u0631\u06af\u0627\u0634 \u062f\u06d5\u0686\u0646\u06d5 \u0645\u0627\u06b5\u0627\u0646\u06d5\u0648\u06d5\u060c \u0633\u06b5\u0627\u0645\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u0628\u06a9\u06d5\u0646\u061b \u0633\u06b5\u0627\u0645\u06ce\u06a9\u06cc \u067e\u0627\u06a9 \u0648 \u067e\u06cc\u0631\u06c6\u0632 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u0627\u0648\u06d5. \u0626\u0627 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0695\u0648\u0648\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0626\u06ce\u0648\u06d5 \u062a\u06ce\u0628\u06af\u06d5\u0646.',
    '\u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc \u0628\u06c6 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5 \u0695\u06d5\u06af\u06d5\u06b5\u06cc \u06a9\u06d5\u0648\u0646\u060c \u0644\u0627 \u0646\u0627\u06a9\u06d5\u0648\u0646\u060c \u0647\u06d5\u062a\u0627 \u0626\u06cc\u0632\u0646\u06cc \u0644\u06ce \u062f\u06d5\u062e\u0648\u0627\u0632\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0644\u06ce\u062a \u062f\u06d5\u062e\u0648\u0627\u0632\u0646 \u0626\u06cc\u0632\u0646\u06cc\u0627\u0646 \u0628\u062f\u06d5\u06cc\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0647\u0627\u0648\u0631\u062f\u0648\u0648\u06d5. \u0647\u06d5\u0631\u06af\u0627 \u0628\u06c6 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0647\u0627\u062a\u0646 \u0626\u06cc\u0632\u0646\u062a \u0644\u06ce \u0628\u062e\u0648\u0627\u0632\u0646\u060c \u0647\u06d5\u0631 \u06a9\u0627\u0645\u06ce\u06a9\u06cc\u0627\u0646 \u0626\u06cc\u0632\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u060c \u06a9\u06d5\u06cc\u0641\u06cc \u062e\u06c6\u062a\u06d5 \u0648 \u0628\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627 \u062f\u0627\u0648\u0627\u06cc \u0644\u06ce \u0628\u0648\u0648\u0631\u062f\u0646\u06ce \u0628\u06a9\u06d5. \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u06af\u0627\u0632\u06cc \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u06a9\u0631\u062f\u0646 \u0648\u06d5\u06a9 \u06af\u0627\u0632\u06cc \u06a9\u0631\u062f\u0646 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u062d\u06d5\u0633\u06ce\u0628 \u0645\u06d5\u06a9\u06d5\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u062e\u06c6\u06cc \u067e\u06d5\u0646\u0627 \u062f\u06d5\u062f\u0627 \u0648 \u067e\u06ce \u062f\u0632\u06d5\u06cc \u062f\u06d5\u0631\u0628\u0627\u0632 \u0628\u0648\u0648\u0646\u06cc\u06d5\u062a\u06cc\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce. \u0628\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u062f\u0632\u0646\u06d5\u0648\u06d5\u060c \u062a\u0631\u0633\u06cc\u0627\u0646 \u0647\u06d5\u0628\u06ce \u06a9\u06d5 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0628\u06ce\u061b \u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646 \u06a9\u06d5\u0648\u0646.',
    '\u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646\u060c \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u062f\u0627\u06cc\u06d5\u060c \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645 \u0644\u06d5 \u0626\u06ce\u0633\u062a\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0648 \u0647\u06d5\u0645 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u0628\u0631\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5. \u0626\u06d5\u0648\u0633\u0627\u0634 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0644\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u0648\u0627\u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0645\u06d5\u0632\u0646\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06ce \u0626\u06d5\u0645 \u0641\u0648\u0695\u0642\u0627\u0646\u06d5\u06cc \u0628\u06c6 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u06cc \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u062a\u0627 \u0628\u0628\u06ce\u062a\u06d5 \u0647\u06c6\u06cc \u062a\u0631\u0633\u0627\u0646\u06ce \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062c\u06cc\u0647\u0627\u0646\u06ce.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u06a9\u06d5\u0633\u06cc \u0646\u06d5\u06a9\u0631\u062f\u06d5 \u0695\u06c6\u06b5\u06d5\u06cc \u062e\u06c6\u06cc\u061b \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06cc\u0634\u062f\u0627 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0646\u06d5\u0628\u0648\u0648\u06d5 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u0644\u06d5 \u0695\u0627\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u062f\u0631\u0648\u0633\u062a \u06a9\u0631\u062f \u0648 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u06af\u0631\u062a.',
    '\u0628\u06d5 \u062c\u06ce\u06af\u06d5\u06cc \u0686\u062a\u06af\u06d5\u0644\u06ce\u06a9\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u062e\u0648\u062f\u0627\u060c \u06a9\u06d5 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0634\u062a\u06ce\u06a9 \u0686\u06ce \u06a9\u06d5\u0646\u061b \u0628\u06c6 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0686\u06ce \u06a9\u0631\u0627\u0648\u0646. \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a\u06cc \u0642\u0627\u0632\u0627\u0646\u062c \u0648 \u0632\u06d5\u0631\u06d5\u0631\u06cc \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0633 \u0646\u06cc\u06cc\u06d5\u061b \u0646\u06d5 \u0645\u0631\u062f\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5 \u0648 \u0646\u06d5 \u0698\u06cc\u0627\u0646 \u0648 \u0646\u06d5 \u0698\u06cc\u0646\u06d5\u0648\u06d5\u06cc \u0644\u06d5 \u067e\u0627\u0634 \u0645\u0631\u062f\u0646.',
    '\u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc \u062f\u0631\u06c6\u06cc \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u06d5\u06cc \u062e\u06c6\u06cc\u06d5\u0633\u06cc \u0648 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u062a\u0631 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u06cc\u0627\u0631\u06cc\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5. \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0648\u062a\u06cc\u0627\u0646 \u062f\u0631\u06c6\u06cc\u06d5 \u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0686\u06cc\u0631\u06c6\u06a9\u06cc \u067e\u06ce\u0634\u06cc\u0646\u0627\u0646\u0646\u061b \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646 \u0628\u06c6\u06cc \u062f\u06d5\u06b5\u06ce\u0646 \u0648 \u0644\u06d5 \u0628\u06d5\u0631\u06cc \u062f\u06d5\u0646\u0648\u0648\u0633\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u060c \u0644\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06ce \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5\u061b \u0626\u06d5\u0648\u06cc\u0634 \u0644\u06d5 \u06af\u0648\u0646\u0627\u062d \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u06af\u0648\u062a\u0648\u0648\u0634\u06cc\u0627\u0646\u06d5: \u0626\u06d5\u0645\u06d5 \u0686\u06c6\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06d5 \u062e\u06c6\u0631\u0627\u06a9 \u062f\u06d5\u062e\u0648\u0627 \u0648 \u0628\u06d5 \u0628\u0627\u0632\u0627\u0695\u0627\u0646\u062f\u0627 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u061f \u0626\u06d5\u062f\u06cc \u0686\u06c6\u0646\u06d5 \u0641\u0631\u0634\u062a\u06ce\u06a9\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0646\u06d5\u0647\u0627\u062a\u060c \u06a9\u06d5 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u0645\u06d5\u0631\u062f\u0645 \u0628\u06ce\u0646\u06ce\u061f',
    '\u06cc\u0627\u0646 \u06af\u06d5\u0646\u062c\u06ce\u06a9\u06cc \u0628\u06c6 \u0647\u0627\u0648\u06cc\u0698\u0631\u0627\u0628\u0627\u06cc\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u06cc\u0627\u0646 \u0628\u0627\u063a\u06ce\u06a9\u06cc \u0647\u06d5\u0628\u0648\u0648\u0627\u06cc\u06d5 \u0644\u06ce\u06cc \u0628\u062e\u0648\u0627\u0631\u062f\u0627\u06cc\u06d5. \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646\u06cc\u0634 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0634\u0648\u06ce\u0646 \u067e\u06cc\u0627\u0648\u06ce \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646 \u0633\u06cc\u0639\u0631 \u0648 \u062c\u0627\u062f\u0648\u0648\u06cc \u0644\u06ce\u06a9\u0631\u0627\u0648\u06d5.',
    '\u062f\u06d5\u0628\u0695\u0648\u0627\u0646\u06d5 \u0686\u0644\u06c6\u0646 \u0646\u06d5\u0632\u06cc\u0644\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u062a\u06c6 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u0695\u06ce\u06cc \u0644\u0627\u06cc\u0627\u0646\u062f\u0627 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u0695\u06ce \u062f\u06d5\u0631\u0646\u0627\u06a9\u06d5\u0646.',
    '\u0645\u06d5\u0632\u0646\u0627\u06cc\u06d5\u062a\u06cc \u0647\u06d5\u0631 \u0628\u06c6 \u06a9\u06d5\u0633\u06ce \u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632 \u06a9\u0627 \u0644\u06d5\u0648\u06d5 \u0686\u0627\u062a\u0631\u062a \u062f\u06d5\u062f\u0627\u062a\u06ce. \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u062a \u067e\u06ce \u062f\u06d5\u0628\u06d5\u062e\u0634\u06ce \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0686\u06d5\u0646\u062f \u06a9\u06c6\u0634\u06a9\u06cc\u0634\u062a \u0628\u06c6 \u062f\u0627\u062f\u06d5\u0646\u06ce.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0698\u0645\u0627\u0631\u062f\u061b \u0626\u06ce\u0645\u06d5\u0634 \u06a9\u06b5\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u0645\u0627\u0646 \u0633\u0627\u0632 \u062f\u0627\u0648\u06d5 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u06d5\u0632\u0627\u0646\u06ce\u061b',
    '\u06a9\u06d5 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u062f\u0648\u0648\u0631\u06d5\u0648\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u0628\u06cc\u0646\u06ce\u060c \u0644\u0631\u0641\u06d5 \u0648 \u0646\u06ce\u06b5\u06d5\u06cc \u06a9\u06b5\u06cc \u0626\u0627\u06af\u0631\u06cc\u0627\u0646 \u062f\u06ce\u062a\u06d5 \u06af\u0648\u06ce.',
    '\u0647\u06d5\u0631\u06af\u0627\u06cc\u06d5\u06a9\u06cc\u0634 \u0634\u06d5\u062a\u06d5\u06a9 \u062f\u0631\u0627 \u0648 \u062f\u0627\u0648\u06ce\u0698\u0631\u06ce\u0646\u06d5 \u0646\u0627\u0648 \u062c\u06ce\u06af\u06d5\u06cc \u062a\u06d5\u0646\u06af\u06d5\u0628\u06d5\u0631\u0627\u06cc\u06cc \u062f\u06c6\u0698\u06d5\u0647\u06d5\u0648\u06d5\u060c \u0645\u0646\u06d5 \u0628\u06c6 \u0645\u0631\u062f\u0646\u06ce \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0645\u0695\u06c6 \u0626\u06cc\u062a\u0631 \u0647\u06d5\u0631 \u0645\u0631\u062f\u0646\u06ce \u062f\u0627\u0648\u0627 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u062f\u0627\u0648\u0627\u06cc \u06af\u06d5\u0644\u06ce \u0645\u0631\u062f\u0646 \u0628\u06a9\u06d5\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u06d5 \u0628\u0627\u0634\u06d5\u060c \u06cc\u0627\u0646 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc \u0647\u06d5\u0645\u06cc\u0634\u06d5\u06cc\u06cc\u061b \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0628\u06d5 \u062e\u0648\u062f\u06ce \u062a\u0631\u0633\u0627\u0646 \u062f\u0631\u0627\u0648\u06d5\u060c \u06a9\u06d5: \u067e\u0627\u062f\u0627\u0634 \u0648 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0627\u0646\u06d5\u061f',
    '\u0686\u06cc \u0628\u062e\u0648\u0627\u0632\u0646 \u0644\u06d5\u0648\u06ce \u0647\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u0648\u0647\u06d5\u0631 \u062a\u06ce\u062f\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646. \u0626\u06d5\u0645 \u0628\u06d5\u06b5\u06ce\u0646\u06d5 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0695\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062f\u06ce\u062a\u06d5\u062c\u06ce.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc - \u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u060c \u0644\u06d5 \u062c\u06ce\u06cc\u06d5\u06a9 \u06a9\u06c6 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u062f\u06d5\u067e\u0631\u0633\u06ce: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u0648\u0648\u0646 \u0626\u06d5\u0645 \u0639\u06d5\u0628\u062f\u0627\u0646\u06d5\u0645\u0648\u0648 \u0644\u0627\u0695\u06ce \u06a9\u0631\u062f\u060c \u06cc\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce \u06a9\u0644\u0627 \u0628\u0648\u0648\u0646\u061f',
    '\u0626\u06ce\u0698\u0646: \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc \u062a\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u061b \u0626\u06ce\u0645\u06d5 \u06a9\u06d5\u06cc \u0695\u06ce\u0645\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u06ce - \u0628\u06d5\u062f\u06d5\u0631\u0644\u06d5 \u062a\u06c6 - \u06a9\u06d5\u0633\u06ce \u0628\u06a9\u06d5\u06cc\u0646\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u062e\u06c6\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631 \u062e\u06c6\u062a \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0648 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u062a \u0628\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u06b5\u062f\u0627 \u06a9\u0631\u062f\u060c \u06a9\u06d5 \u06cc\u0627\u062f\u06cc \u062a\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u0686\u0648\u0648\u060c \u0628\u0648\u0648\u0646\u06d5 \u06af\u06d5\u0644\u06ce\u06a9\u06cc \u0648\u0627 \u0628\u06ce \u062e\u06ce\u0631.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u0642\u0633\u0627\u0646\u06d5\u062a\u0627\u0646\u060c \u0626\u06ce\u0648\u06d5\u06cc\u0627\u0646 \u0648\u06d5\u062f\u0631\u06c6 \u062e\u0633\u062a\u06d5\u0648\u06d5. \u062e\u06c6 \u0626\u06ce\u0633\u062a\u0627 \u0626\u06cc\u062a\u0631 \u0646\u0627\u062a\u0627\u0646\u0646 [\u0626\u0627\u0632\u0627\u0631 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646] \u0644\u0627\u0628\u062f\u06d5\u0646\u061b \u06cc\u0627\u0646 \u0647\u0627\u0646\u0627\u062f\u06d5\u0631\u06ce \u067e\u06d5\u06cc\u0627\u06a9\u06d5\u0646\u061b \u0647\u06d5\u0631 \u06a9\u0627\u0645\u06cc\u0634\u062a\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc \u06a9\u0627\u060c \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc \u067e\u06ce \u062f\u06d5\u0686\u06ce\u0698\u06cc\u0646.',
    '\u0644\u06d5 \u067e\u06ce\u0634 \u062a\u06c6\u0634\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u0647\u06d5\u0645\u0648\u0648 \u062e\u0648\u0627\u0631\u062f\u0646\u06cc\u0627\u0646 \u062e\u0648\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u0627\u0632\u0627\u0695\u0627\u0646 \u06af\u06d5\u0695\u0627\u0648\u0646\u061b \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646\u0645\u0627\u0646 \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u0645\u0627\u06cc\u06d5\u06cc \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06cc \u0647\u06ce\u0646\u062f\u06ce \u062a\u0631. \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062e\u06c6 \u0695\u0627\u062f\u06d5\u06af\u0631\u0646\u061f \u0648\u0627 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u062f\u06d5\u0628\u06cc\u0646\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u0647\u06cc\u0648\u0627 \u0646\u06cc\u0646 \u0628\u06d5 \u062f\u06cc\u062f\u0627\u0631\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u06af\u06d5\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06c6\u0686\u06cc \u0641\u0631\u0634\u062a\u06d5\u0645\u0627\u0646 \u0646\u06d5\u0647\u0627\u062a\u06d5 \u0644\u0627\u061f \u06cc\u0627\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0646 \u0646\u06d5\u062f\u06cc\u061f \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u067e\u0627\u06b5 \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0632\u0644 \u062f\u06d5\u0632\u0627\u0646\u0646 \u0648 \u0633\u06d5\u0631\u0628\u0632\u06ce\u0648\u06cc \u0648 \u0641\u06cc\u0632\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u0628\u0648\u0648\u06d5 \u06a9\u06d5 \u0626\u06d5\u0648 \u0633\u06d5\u0631\u06cc \u062f\u06cc\u0627\u0631\u06cc \u0646\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0641\u0631\u0634\u062a\u0627\u0646 \u0628\u06d5 \u0686\u0627\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u0646\u060c \u06a9\u06d5 \u0641\u0631\u0634\u062a\u06d5 \u062f\u06d5\u0646\u06af\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646: \u0647\u06d5\u06cc\u062f\u06ce \u06af\u0648\u0645 \u0628\u0646! \u0647\u06cc\u0686 \u0645\u0698\u062f\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0644\u0627\u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0628\u06cc\u062f\u06d5\u06cc\u0646 \u0628\u06d5 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u061b \u067e\u06ce\u0634\u06cc\u0627\u0646 \u062f\u06d5\u06b5\u06ce\u0646: \u0645\u0698\u062f\u06d5 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062d\u06d5\u0631\u0627\u0645\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u0695\u0627\u06cc \u062f\u06d5\u0646\u06ce\u0646 \u0648 \u0647\u06d5\u0631\u0648\u06d5\u06a9\u0648\u0648 \u062a\u06c6\u0632\u06cc \u0628\u0627\u0628\u0631\u062f\u06d5\u06b5\u06d5\u06cc \u067e\u06ce\u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0626\u06d5\u0646\u0648\u0627 \u0648 \u062c\u06ce\u06af\u06d5\u06cc \u062d\u06d5\u0633\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u06cc\u06d5\u06a9\u0627\u0646 \u0628\u0627\u0634\u062a\u0631\u06cc\u0646\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce\u0634 \u0626\u06d5\u0632\u0645\u0627\u0646 \u0628\u06d5 \u0647\u06d5\u0648\u0631 \u062f\u06d5\u0642\u06d5\u06b5\u0634\u06ce \u0648 \u0641\u0631\u06cc\u0634\u062a\u06d5\u06a9\u0627\u0646 - \u0628\u06d5 \u0647\u06d5\u0695\u06d5\u0645\u06d5 - \u0628\u06d5\u0631\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0631\u06ce\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06cc \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648\u060c \u0628\u06c6 \u06af\u06d5\u0644\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u067e\u0695 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5\u06cc\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u0634\u062f\u0627 \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06a9\u0627\u0631 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6\u06cc\u062f\u0627 \u062f\u06d5\u0646\u0648\u0648\u0633\u06ce \u0648 \u0626\u06ce\u0698\u06ce: \u0626\u06d5\u06cc \u06a9\u0627\u0634\u06a9\u0627\u06cc\u06d5 \u0645\u0646\u06cc\u0634 \u0648\u06d5\u0644 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0686\u0648\u0648\u0628\u0627\u0645\u06d5 \u0695\u06ce.',
    '\u0626\u06d5\u06cc \u0645\u062e\u0627\u0628\u0646\u060c \u062e\u06c6\u0632\u06cc \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u06d5\u0628\u0648\u0648\u0628\u0627\u0645 \u0628\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u0641\u06b5\u0627\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5\u0645 \u067e\u06ce \u06af\u06d5\u06cc\u0634\u062a\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0645\u0646\u06cc \u0644\u06ce \u06a9\u0644\u0627 \u06a9\u0631\u062f\u061b \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0645\u0631\u06c6 \u0628\u06d5\u0631\u06d5\u06b5\u0627 \u062f\u06d5\u06a9\u0627.',
    '\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634 \u0626\u06ce\u0698\u06ce: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u0645! \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u0645 \u0626\u06d5\u0645 \u0642\u0648\u0695\u0626\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0648\u06d5\u067e\u0634\u062a \u06af\u0648\u06ce \u062e\u0633\u062a.',
    '\u0647\u06d5\u0631\u0648\u0627\u0634 \u0628\u0648\u0648\u06d5\u061b \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0647\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u062f\u0698\u0645\u0646\u06ce\u06a9\u0645\u0627\u0646 - \u0644\u06d5 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 - \u0628\u06c6 \u062f\u0627\u0646\u0627\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06c6 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06cc \u06a9\u0631\u062f\u0646 \u0648 \u0647\u0627\u0631\u06cc\u06a9\u0627\u0631\u06cc \u0628\u06d5\u0633\u06cc \u062a\u06c6\u06cc\u06d5.',
    '\u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u062f\u0628\u06ce\u0698\u0646: \u0628\u06c6\u0686\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5 \u062a\u06ce\u06a9\u0695\u0627\u06cc\u06cc \u0626\u06d5\u0645 \u0642\u0648\u0695\u0626\u0627\u0646\u06d5\u06cc \u0628\u06c6 \u0646\u06d5\u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u061f \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0645\u06d5 \u0628\u0648\u0648 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u062f\u06b5\u062a\u06cc \u067e\u06ce \u062f\u0627\u0645\u06d5\u0632\u0631\u06ce\u0646\u06cc\u0646 \u0648 \u0628\u06d5\u062a\u06d5\u06a9\u0648\u0648\u0632\u06cc \u0648 \u0647\u06ce\u062f\u06cc\u06a9\u0627 \u0628\u06c6\u062a\u06cc \u0648\u06d5\u062e\u0648\u06ce\u0646\u06cc\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0646\u06d5\u0632\u06cc\u0644\u0627\u0646\u062a \u0628\u06c6 \u062f\u06ce\u0646\u0646\u06d5\u0648\u06d5\u060c \u0626\u06ce\u0645\u06d5 \u0695\u0627\u0633\u062a\u06cc\u062a \u0628\u06c6 \u062f\u06ce\u0646\u06cc\u0646 \u0648 [\u0646\u06d5\u0632\u06cc\u0644\u06d5\u06cc\u06d5\u06a9\u06cc] \u067e\u0695 \u0645\u0627\u0646\u0627\u062a\u0631 \u0644\u06d5 \u0626\u06cc \u0626\u06d5\u0648\u0627\u0646.',
    '\u06a9\u06d5\u0633\u06af\u06d5\u0644\u06ce \u06a9\u06d5 \u062f\u06d5\u0645\u06d5\u0648\u0695\u0648\u0648 \u0695\u0627\u062f\u06d5\u06a9\u06ce\u0634\u0631\u06ce\u0646 \u0628\u06c6 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0648 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u06a9\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5\u060c \u062e\u0631\u0627\u067e\u062a\u0631\u06cc\u0646 \u062c\u06ce\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u0644\u06d5 \u0695\u06ce \u06a9\u0644\u0627\u0646.',
    '\u06a9\u062a\u06ce\u0628\u06cc\u0634\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u0627\u0695\u0648\u0648\u0646\u06cc \u0628\u0631\u0627\u0634\u06cc\u0645\u0627\u0646 \u06a9\u0631\u062f \u0628\u06d5 \u0648\u06d5\u0632\u06cc\u0631\u06cc.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06af\u0648\u062a\u0645\u0627\u0646: \u0628\u0695\u06c6\u0646 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u0647\u06c6\u0632\u06d5 \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0698\u0645\u0627\u0631\u062f\u061b \u06a4\u06ce\u06af\u0627\u06a4\u06ce \u062a\u06ce\u06a9\u0645\u0627\u0646 \u0634\u06a9\u0627\u0646\u062f\u0646.',
    '\u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d\u06cc\u0634 - \u06a9\u0627\u062a\u06cc \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f - \u0646\u0648\u0642\u0645\u06cc \u0628\u0646\u0627\u0648\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646 \u0648 \u06a9\u0631\u062f\u0645\u0627\u0646\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0648 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u0633\u0627\u0632 \u062f\u0627\u0648\u06d5 \u0628\u06c6 \u0646\u0627\u0647\u06d5\u0642\u06d5\u06a9\u0627\u0646.',
    '\u0639\u0627\u062f \u0648 \u0633\u06d5\u0645\u0648\u0648\u062f \u0648 \u062e\u0627\u0648\u06d5\u0646\u06cc \u0686\u0627\u06b5\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0648 \u06af\u06d5\u0644\u06cc \u0628\u06d5\u0631\u06d5\u0648\u0686\u06cc\u0646\u06cc \u0646\u06ce\u0648\u0627\u0646\u06cc\u0627\u0646 [\u0647\u06d5\u0631 \u0628\u06d5\u0648 \u062f\u06d5\u0631\u062f\u06d5 \u0686\u0648\u0648\u0646].',
    '\u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648\u0627\u0646\u06cc\u0627\u0646 \u0686\u06d5\u0646\u062f \u0646\u06d5\u0632\u06cc\u0644\u06d5\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u062f\u0627 \u0648\u0646\u062c\u0695\u0628\u06d5\u0648\u0646\u062c\u0695\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646.',
    '\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0648 \u0634\u0627\u0631\u06d5\u062f\u0627 \u0695\u0627\u0628\u0631\u062f\u0648\u0648\u0646 \u06a9\u06d5 \u0628\u0627\u0631\u0627\u0646\u06d5\u06cc \u0628\u06d5\u06b5\u0627\u0645\u0627\u0646 \u0628\u06c6 \u0628\u0627\u0631\u0627\u0646\u062f\u0646\u061b \u0645\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0686\u0627\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0626\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06cc\u061f \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5 \u0647\u06cc\u0648\u0627\u06cc \u0632\u06cc\u0646\u062f\u0648\u0648 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0646\u06d5\u0628\u0648\u0648\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u062a\u06c6\u06cc\u0627\u0646 \u0686\u0627\u0648 \u067e\u06ce \u06a9\u06d5\u0648\u062a\u060c \u062f\u06d5\u062a\u06a9\u06d5\u0646\u06d5 \u06af\u06d5\u067e\u062c\u0627\u0695\u06cc \u062e\u06c6\u06cc\u0627\u0646: \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0695\u0627\u06cc \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5\u061f!',
    '\u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0648\u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0646 \u0644\u0627\u0645\u0627\u0646 \u0628\u062f\u0627\u060c \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u0645\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u0695\u0627\u0646\u06d5\u06af\u0631\u062a\u0627\u06cc\u06d5. \u062f\u0648\u0627\u062c\u0627\u0631 - \u06a9\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u0628\u06cc\u0646\u0646 - \u067e\u06ce\u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u06a9\u06ce \u067e\u062a\u0631 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a \u0647\u06d5\u06b5\u06d5 \u0628\u0648\u0648\u06d5.',
    '\u062a\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u062a \u062f\u06cc \u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u062f\u06d5\u06cc\u06a9\u0627\u062a\u06d5 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06cc \u062e\u06c6\u06cc\u061f \u0626\u0627\u062e\u06c6 \u0626\u06d5\u062a\u06c6 \u0628\u06c6 \u0626\u06d5\u0645\u06cc\u0634 \u0647\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06d5\u0628\u06d5\u0631\u06cc\u061f',
    '\u06cc\u0627\u0646 \u067e\u06ce\u062a \u0648\u0627\u06cc\u06d5 \u06a9\u06d5 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u06af\u0648\u06ce \u0634\u0644 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646\u061f \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u06ce\u0646\u06d5\u06cc \u0648\u06b5\u0633\u0627\u062a\u0646\u060c \u0628\u06af\u0631\u06d5 \u0644\u06d5\u0648\u06cc\u0634 \u06af\u0648\u0645\u0695\u0627\u062a\u0631\u0646.',
    '\u0628\u06c6 \u0646\u06d5\u062a \u062f\u06cc\u0648\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u062f\u0631\u06ce\u0698\u06d5 \u0628\u06d5 \u0633\u06ce\u0628\u06d5\u0631 \u062f\u06d5\u062f\u0627\u061f \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u06cc \u062d\u06d5\u0632\u06cc \u06a9\u0631\u062f\u0628\u0627\u060c \u0648\u0627\u06cc \u062f\u06d5\u06a9\u0631\u062f \u0644\u06d5 \u062c\u06ce \u0646\u06d5\u0628\u0632\u0648\u0648\u06ce. \u062e\u06c6\u0631\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc.',
    '\u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u062f\u0627 - \u06a9\u06d5\u0645\u06d5\u06a9\u06d5\u0645\u06d5 - \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u0648\u0646\u06cc \u062f\u06d5\u0628\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0634\u06d5\u0648\u06cc \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u067e\u06c6\u0634\u0627\u06a9 \u0648 \u062e\u06d5\u0648\u06cc \u0628\u06c6 \u0633\u0627\u0646\u06d5\u0648\u06d5 [\u062a\u0627\u0646] \u0648 \u0695\u06c6\u0698\u06cc\u0634\u06cc \u0628\u06c6 \u0647\u06d5\u06b5\u0633\u0627\u0646\u06d5\u0648\u06d5 [\u062a\u0627\u0646] \u062f\u0627\u0646\u0627\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0628\u0627\u06a9\u0627\u0646\u06cc \u0646\u0627\u0631\u062f\u060c \u06a9\u06d5 \u0645\u0632\u06af\u06ce\u0646\u06cc \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0626\u06d5\u0648\u062a\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u0646\u0646\u061b \u062c\u0627 \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u06cc\u06d5\u0648\u06d5 \u0628\u0627\u0631\u0627\u0646\u06ce\u06a9\u06cc \u067e\u0627\u06a9 \u0648 \u062e\u0627\u0648\u06ce\u0646\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u061b',
    '\u062a\u0627 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0647\u06ce\u0632 \u0644\u06ce \u0628\u0695\u0627\u0648 - \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0645 - \u0632\u06cc\u0646\u062f\u0648\u0648 \u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0648\u06b5\u0633\u0627\u062a \u0648 \u0632\u06c6\u0631 \u0644\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u0627\u0646\u06d5\u06cc - \u06a9\u06d5\u062e\u06c6\u0645\u0627\u0646 \u062f\u0631\u0648\u0633\u062a\u0646 \u06a9\u0631\u062f\u0648\u0648\u0646 - \u062a\u06ce\u0631\u0627\u0648 \u0628\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648\u06d5\u0634\u0645\u0627\u0646 \u0647\u06d5\u0631 \u062c\u06c6\u0631\u06d5\u062c\u06c6\u0631\u06d5 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc\u0627\u0646\u0627 \u062f\u0627\u0628\u06d5\u0634 \u06a9\u0631\u062f\u060c \u062a\u0627 \u067e\u06d5\u0646\u062f \u0628\u06af\u0631\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0647\u06d5\u0631 \u0641\u06ce\u0631\u0646 \u0646\u0627\u0634\u06a9\u0648\u0631\u06cc \u0628\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0646 \u06a9\u06d5\u06cc\u0641\u0646 \u0644\u06ce \u0628\u0627 \u0628\u06c6 \u0647\u06d5\u0631 \u0634\u0627\u0631\u06ce \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0648\u0627\u0645\u0627\u0646 \u0695\u0627\u062f\u06d5\u0633\u067e\u0627\u0631\u062f \u0628\u06cc\u0627\u0646 \u062a\u0631\u0633\u06ce\u0646\u06ce.',
    '\u0633\u0627 \u062a\u06c6 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u06d5 \u0642\u0633\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0645\u06d5\u06a9\u06d5 \u0648 \u0628\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0647\u06ce\u0632 \u0648 \u062a\u0648\u0627\u0646\u0627\u0648\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u06af\u0698\u06cc\u0627\u0646\u062f\u0627 \u0648\u06d5\u0631\u06d5\u0648\u06d5.',
    '\u062e\u0648\u0627 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u06a9\u06d5 \u062f\u0648\u0648 \u0632\u06d5\u0631\u06cc\u0627\u06cc \u0628\u06d5\u06cc\u06d5\u06a9 \u06af\u06d5\u06cc\u0627\u0646\u062f\u061b \u06cc\u06d5\u06a9\u06ce \u0634\u06cc\u0631\u0646\u060c \u06cc\u06d5\u06a9\u06ce \u0634\u06c6\u0631\u06cc \u0626\u0627\u0645\u0627\u06b5 \u062a\u0627\u06b5\u06d5 \u0648 \u0644\u06d5\u0645\u067e\u06d5\u0631\u06ce\u06a9 \u0648 \u0628\u06d5\u0631\u06af\u0631\u06ce\u06af\u06cc \u0632\u06c6\u0631 \u0626\u06d5\u0633\u062a\u0648\u0648\u0631\u06cc \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627 [\u06a9\u06d5 \u062a\u062e\u0648\u0648\u0646\u06cc \u06cc\u06d5\u06a9\u062a\u0631 \u0646\u06d5\u0628\u0646].',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0644\u06d5 [\u062a\u0646\u06c6\u06a9\u06ce] \u0626\u0627\u0648\u060c \u0645\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u06a9\u0631\u062f\u06cc\u06d5 \u0645\u0627\u06a9\u06cc \u062a\u06c6\u0631\u06d5\u0645\u06d5 \u0648 \u062e\u0632\u0645 \u067e\u06d5\u06cc\u062f\u0627 \u0628\u0648\u0648\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u067e\u06ce\u062f\u06d5\u06a9\u0631\u06ce.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0634\u062a\u06cc \u0648\u06d5\u0647\u0627 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646 \u06a9\u06d5 \u0646\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u062f\u0627\u060c \u0646\u06d5 \u0632\u06cc\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce\u062f\u06d5\u062f\u0627. \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u062e\u06c6 \u062f\u06d5\u06a9\u06d5\u0646\u06d5 \u067e\u0634\u062a\u06cc\u0648\u0627\u0646\u06cc \u062f\u0698\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u062a\u06d5\u0646\u06cc\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u06d5 \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u0645\u0698\u062f\u06d5\u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0628\u06cc.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5 \u0647\u06cc\u0686 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u0645 \u0644\u06d5\u0646\u06af\u06c6 \u0646\u0627\u0648\u06ce\u060c \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648\u06d5: \u06a9\u06ce \u067e\u06ce \u062e\u06c6\u0634 \u0628\u06ce\u060c \u0628\u06d5\u0631\u06d5\u0648 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u0695\u06ce \u062f\u06d5\u0631\u0628\u06a9\u0627.',
    '\u0633\u0627 \u062a\u06c6 \u062e\u06c6\u062a \u0628\u062f\u06d5 \u062f\u06d5\u0633 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0647\u06d5\u0631 \u0632\u06cc\u0646\u062f\u0648\u0648\u06d5 \u0648 \u0646\u0627\u0645\u0631\u06ce \u0648 \u0628\u06c6 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u06cc\u060c \u067e\u06ce\u06cc \u0647\u06d5\u06b5\u0628\u06b5\u06ce \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u06d5\u0633\u06d5 \u06a9\u06d5 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06cc \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0628\u06d5 \u0634\u06d5\u0634 \u0695\u06c6\u0698\u06cc \u062f\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5. \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06cc \u062e\u06c6\u06cc \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0648\u06d5\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u062e\u0648\u062f\u0627\u06cc \u0695\u06d5\u062d\u0645\u0627\u0646. \u0633\u0627 \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u067e\u0631\u0633\u06d5 \u06a9\u06d5 \u0686\u0627\u0632\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u067e\u06ce\u06cc\u0627\u0646 \u0628\u0648\u062a\u0631\u06ce \u06a9\u06d5: \u0628\u06d5\u0631\u06d5\u0648 \u0695\u06d5\u062d\u0645\u0627\u0646 \u0633\u0648\u0698\u062f\u06d5 \u0628\u06d5\u0631\u0646\u060c \u0626\u06ce\u0698\u0646: \u0695\u06d5\u062d\u0645\u0627\u0646 \u0686 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5\u061f \u062f\u06d5\u062a\u06d5\u0648\u06ce \u0647\u06d5\u0631\u06a9\u06ce \u062a\u06c6 \u0628\u06ce\u0698\u06cc\u060c \u0626\u06ce\u0645\u06d5 \u06a9\u0695\u0646\u06c6\u0634\u06cc \u0628\u06c6 \u0628\u06d5\u0631\u06cc\u0646\u061f \u0633\u06b5 \u0628\u0648\u0648\u0646\u06cc\u0634\u06cc\u0627\u0646 \u067e\u062a\u0631 \u062f\u06d5\u06a9\u0627.',
    '\u0633\u06d5\u0631\u0648\u06d5\u0631\u06cc \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc\u06d5 \u0686\u06d5\u0646\u062f \u0642\u06c6\u0646\u0627\u063a\u06cc \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627 \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0646\u062f \u0648 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0645\u062f\u0627 \u0686\u0631\u0627\u06cc\u06d5\u06a9 \u0648 \u0647\u06d5\u06cc\u06a4\u06ce\u06a9\u06cc \u062a\u0631\u06cc\u0641\u06d5\u062f\u0627\u0631\u06cc \u062c\u06ce \u06a9\u0631\u062f\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0646\u06c6\u0631\u06d5 \u0628\u06d5 \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698 \u062f\u06d5\u06a9\u0627\u060c \u0647\u06d5\u062a\u0627 \u06a9\u06d5\u0633\u06ce \u06af\u06d5\u0631\u06d5\u06a9\u06cc \u0628\u06ce \u0628\u06cc\u0631 \u06a9\u0627\u062a\u06d5\u0648\u06d5 \u06cc\u0627\u0646 \u0628\u06cc\u06d5\u0648\u06ce \u0633\u0648\u067e\u0627\u0633\u06a9\u0627\u0631 \u0628\u06ce.',
    '\u0639\u06d5\u0628\u062f\u06d5 \u0695\u0627\u0633\u062a\u06d5\u06a9\u0627\u0646\u06cc \u0695\u06d5\u062d\u0645\u0627\u0646 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0628\u06ce \u062f\u06d5\u0639\u06cc\u06d5 \u062f\u06d5\u0686\u0646\u06d5 \u0695\u06ce\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u0646\u06d5\u0632\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0628\u06cc\u0627\u0646\u062f\u0648\u06ce\u0646\u0646\u060c \u067e\u06d5\u0631\u0633\u06a4\u06cc \u0646\u06d5\u0631\u0645 \u0648 \u0646\u06cc\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0646 \u06a9\u06d5 \u0634\u06d5\u0648 \u062a\u0627 \u0695\u06c6\u0698\u060c \u0695\u0648\u0648\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0633\u0648\u0698\u062f\u06d5 \u062f\u06d5\u0628\u06d5\u0646 \u0648 \u0648\u06ce\u0633\u062a\u0627\u0648\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0646 \u06a9\u06d5\u0648\u0627 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646! \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062f\u0627\u0646\u06cc \u062f\u06c6\u0632\u06d5\u062e\u0645\u0627\u0646 \u0644\u06ce \u062f\u0648\u0648\u0631\u062e\u06d5\u0648\u06d5\u061b \u0686\u0648\u0646\u06a9\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u062f\u0648\u0627\u06cc\u06cc \u0646\u0627\u06cc\u06d5.',
    '\u06a9\u06d5 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0632\u06c6\u0631 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u0646\u0627\u0644\u06d5\u0628\u0627\u0631 \u0648 \u0632\u06c6\u0631 \u0626\u06d5\u0646\u0648\u0627\u06cc\u06d5\u06a9\u06cc \u062e\u0631\u0627\u067e\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0646 \u06a9\u06d5 \u0644\u06d5 \u06a9\u0627\u062a\u06cc \u0645\u0627\u06b5 \u0628\u06d5\u062e\u0634\u06cc\u0646\u0627\u060c \u0646\u06d5 \u06cc\u06d5\u06a9\u062c\u0627\u0631\u06cc \u062f\u06d5\u0633 \u0628\u06b5\u0627\u0648\u0646\u060c \u0646\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc\u0634 \u062f\u06d5\u0633 \u0642\u0648\u0648\u0686\u0627\u0648\u0646\u061b \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u0646\u0627\u0648\u06d5\u0646\u062f\u06d5\u062f\u0627\u060c \u0645\u0627\u0648\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0646 \u06a9\u06d5 \u0648\u06ce\u0695\u0627\u06cc \u062e\u0648\u0627\u060c \u0647\u06cc\u0686 \u0634\u062a\u06cc \u062a\u0631 \u0646\u0627\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0646\u0627\u06af\u0648\u0698\u0646 \u06a9\u06d5 \u062e\u0648\u0627 \u0695\u0698\u062a\u0646\u06cc \u062e\u0648\u06ce\u0646\u06cc \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u0632\u0627\u0646\u06cc\u0648\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5 \u0647\u06d5\u0642\u061b \u062a\u0648\u0648\u0634\u06cc \u062f\u0627\u0648\u06ce\u0646\u067e\u06cc\u0633\u06cc\u0634 \u0646\u0627\u0628\u0646. \u0647\u06d5\u0631\u06a9\u06ce \u0626\u06d5\u0645 \u06a9\u0627\u0631\u0627\u0646\u06d5 \u0628\u06a9\u0627\u060c \u062a\u0627\u0648\u0627\u0646\u06cc \u062e\u06c6\u06cc \u062f\u06ce\u062a\u06d5\u0648\u06d5 \u0695\u06ce\u06cc.',
    '\u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u062f\u0648\u0648 \u0686\u06d5\u0646\u062f\u0627\u0646\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u062f\u0631\u06ce \u0648 \u0628\u06d5 \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u062f\u06d5\u0645\u06ce\u0646\u06ce\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u062a\u06c6\u0628\u06d5 \u062f\u06d5\u06a9\u0627 \u0648 \u0628\u0627\u0648\u06d5\u0695 \u062f\u06ce\u0646\u06ce \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u062f\u06d5\u06a9\u0627\u061b \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u062e\u0631\u0627\u067e\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0628\u06c6 \u0648\u06d5\u062f\u06d5\u06af\u06c6\u0695\u06ce \u0648 \u062e\u0648\u0627 \u0644\u06ce\u0628\u0648\u0648\u0631\u062f\u06d5\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062a\u06c6\u0628\u06d5 \u0628\u06a9\u0627 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u0628\u06a9\u0627\u060c \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u062f\u06d5\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0646 \u06a9\u06d5 \u0628\u06d5 \u062f\u0631\u06c6\u060c \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0646\u0627\u062f\u06d5\u0646 \u0648 \u0647\u06d5\u0631\u06af\u0627 \u0628\u06d5 \u0644\u0627\u06cc \u06a9\u0627\u0631\u06cc \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u062f\u0627 \u0695\u0627\u062f\u06d5\u0628\u0648\u0648\u0631\u0646\u060c \u0645\u06d5\u0631\u062f\u0627\u0646\u06d5 \u062e\u06c6\u06cc \u0644\u06ce \u0644\u0627 \u062f\u06d5\u062f\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0646 \u06a9\u06d5 \u0647\u06d5\u0631\u06a9\u0627\u062a\u06ce \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06cc\u0631 \u062f\u06d5\u062e\u0631\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0648\u06d5\u06a9 \u06a9\u06d5\u0695 \u0648 \u06a9\u0648\u06ce\u0631 \u0646\u0627\u0648\u06ce\u0633\u062a\u0646 [\u0648 \u06af\u0648\u06ce \u067e\u06ce\u062f\u06d5\u062f\u06d5\u0646].',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0646 \u06a9\u06d5 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u06c6\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0698\u0646 \u0648 \u0645\u0646\u0627\u06b5\u0627\u0646\u06cc \u0648\u0627\u0645\u0627\u0646 \u067e\u06ce \u0628\u0628\u06d5\u062e\u0634\u06d5\u060c \u0628\u0628\u0646\u06d5 \u0645\u0627\u06cc\u06d5\u06cc \u0686\u0627\u0648\u0695\u0648\u0646\u06cc\u0645\u0627\u0646\u061b \u0628\u0634\u0645\u0627\u0646\u06a9\u06d5 \u067e\u06ce\u0634\u06d5\u0648\u0627 \u0628\u06c6\u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u06d5\u06a9\u0627\u0646.',
    '\u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631\u062a\u0646\u06cc\u0627\u0646\u060c \u0626\u06c6\u062f\u06d5 [\u06a9\u0627\u0646\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a] \u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u0631\u06ce\u062a \u0648 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u0631\u0648\u0648\u062f \u0648 \u0633\u06d5\u0644\u0627\u0645\u06d5\u0648\u06d5 \u062a\u06d5\u06a9\u06cc\u0627\u0646\u0627 \u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648 \u062f\u06d5\u0628\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0632\u06c6\u0631 \u0628\u0646\u06a9\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u0627\u0634\u06d5 \u0648 \u0632\u06c6\u0631 \u0646\u0634\u06cc\u0645\u06d5\u0646\u06ce\u06a9\u06cc \u062e\u06c6\u0634\u06d5.',
    '\u0626\u06d5\u062a\u06c6 \u0628\u06ce\u0698\u06d5: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0647\u06cc\u0686 \u0647\u06d5\u0648\u0627\u06b5\u062a\u0627\u0646 \u0644\u06ce \u0646\u0627\u067e\u0631\u0633\u06ce\u060c \u0626\u06d5\u0631 \u0644\u06d5 \u0628\u0631\u06cc \u0646\u06d5\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u0646\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646. \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0648\u06d5\u0634 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u0628\u06d5\u0648 \u0646\u06d5\u0647\u06ce\u0646\u0627 \u0648 \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u06af\u0631\u0641\u062a\u0627\u0631\u06cc \u0633\u0632\u0627 \u062f\u06d5\u0628\u0646 \u0648 \u0647\u06d5\u0631 \u0644\u06ce\u062a\u0627\u0646 \u062c\u0648\u06ce \u0646\u0627\u0628\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u062a\u0627\u060c \u0633\u06cc\u0646\u060c \u0645\u06cc\u0645.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u0631\u06d5\u0646.',
    '\u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0628\u06d5\u0648 \u062e\u06d5\u0645\u06d5 \u062e\u06c6\u062a \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0628\u06d5\u0631\u06cc\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u062d\u06d5\u0632\u06ce \u0628\u06a9\u06d5\u06cc\u0646 \u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0628\u06c6 \u062f\u06d5\u0646\u06ce\u0631\u06cc\u0646\u060c \u06a9\u06d5 \u0645\u0644\u06cc\u0627\u0646\u06cc \u0644\u06d5\u0628\u06d5\u0631 \u06a9\u06d5\u0686 \u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u0642\u0633\u06ce\u06a9\u06cc \u062a\u0627\u0632\u06d5 \u0628\u0627\u0628\u06d5\u062a \u0644\u06d5 \u0695\u06d5\u062d\u0645\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6\u06cc\u0627\u0646 \u062f\u06ce\u060c \u0695\u0648\u0648\u06cc\u0627\u0646\u06cc \u0644\u06ce \u0648\u06d5\u0631\u062f\u06d5\u06af\u06ce\u0695\u0646.',
    '[\u0626\u06d5\u0648\u0627\u0646] \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627\u061b \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5\u0634 \u0647\u06d5\u0631 \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u06cc \u0626\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u06af\u06d5\u06cc\u06ce\u060c \u06a9\u06d5 \u06af\u0627\u06b5\u062a\u06d5 \u0648 \u06af\u06d5\u067e\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u06a9\u0631\u062f.',
    '\u0626\u0627\u062e\u06c6 \u0632\u06d5\u0645\u06cc\u0646\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06cc\u0648\u06d5\u060c \u06a9\u06d5 \u0686\u06d5\u0646\u062f \u062c\u0648\u0648\u062a\u06d5\u06cc \u0695\u0648\u0648\u06d5\u06a9\u06cc \u067e\u0695 \u0644\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u0645\u0627\u0646 \u062a\u06ce\u062f\u0627 \u0695\u0648\u0648\u0627\u0646\u062f.',
    '\u0644\u06d5\u0645\u06d5\u062f\u0627 \u062f\u06d5\u0631\u0633\u06ce [\u0628\u0627\u0634] \u0647\u06d5\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5\u0634\u06cc \u0647\u06d5\u0631\u06d5 \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0628\u0695\u0648\u0627 \u0646\u06cc\u0646.',
    '\u062e\u06c6 \u062f\u06cc\u0627\u0631\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '[\u0628\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06cc\u0631] \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5 \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u0627\u0646\u06af\u0627\u0648\u0627\u0632\u06cc \u0644\u06d5 \u0645\u0648\u0648\u0633\u0627 \u06a9\u0631\u062f\u060c \u06a9\u06d5: \u0628\u0686\u06c6 \u0644\u0627\u06cc \u06a9\u06c6\u0645\u06d5\u06b5\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631\u0627\u0646\u061b',
    '\u0628\u06c6 \u0644\u0627\u06cc \u062f\u0627\u0631\u0648\u062f\u06d5\u0633\u062a\u06d5\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646. \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u062a\u0631\u0633\u0646\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u062a\u0631\u0633\u0645 \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5 \u06a4\u0695 \u0628\u06ce\u0698\u0645 \u0628\u0632\u0627\u0646\u0646.',
    '\u062f\u06b5\u0645 \u062a\u06d5\u0646\u06af \u062f\u06d5\u0628\u06ce\u062a \u0648 \u0632\u0645\u0627\u0646\u0645 \u06af\u06c6 \u0646\u0627\u06a9\u0627\u061b \u0695\u0627\u0633\u067e\u06ce\u0631\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0647\u0627\u0695\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u062a\u0627\u0648\u0627\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0645\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u062f\u06d5\u062a\u0631\u0633\u0645 \u0647\u06d5\u0631 \u0628\u0634\u0645 \u06a9\u0648\u0698\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0646\u06d5\u062e\u06ce\u0631\u060c \u0642\u06d5\u062a \u0648\u0627 \u0646\u06cc\u06cc\u06d5. \u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0631\u0646\u061b \u0626\u06ce\u0645\u06d5\u0634 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0647\u06d5\u06cc\u0646 \u0648 \u062f\u06d5\u0628\u06cc\u0633\u06cc\u0646.',
    '\u0633\u0627 \u0628\u06af\u06d5\u0646\u06d5 \u0646\u06a9 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0628\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u0634 \u062c\u06cc\u0647\u0627\u0646\u06cc\u0646.',
    '\u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u06ce\u0645\u0627\u0646 \u0695\u06d5\u06af\u06d5\u06b5 \u0628\u062e\u06d5.',
    '[\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646] \u06af\u0648\u062a\u06cc: \u062a\u06c6 \u0647\u06d5\u0631 \u0645\u0646\u062f\u0627\u06b5\u06d5\u06a9\u06d5 \u0646\u06d5\u0628\u0648\u0648\u06cc \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u062f\u06d5\u0646 \u06a9\u0631\u062f\u06cc \u0648 \u0686\u06d5\u0646\u062f \u0633\u0627\u06b5\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0646\u0627\u0648\u0645\u0627\u0646 \u0645\u0627\u06cc\u061f',
    '\u0626\u06d5\u0648\u06a9 \u0627\u0631\u06d5\u0634\u062a \u06a9\u0631\u062f \u06a9\u06d5 \u06a9\u0631\u062f\u062a\u061b \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u062a\u06c6 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0646\u0627\u0633\u0648\u067e\u0627\u0633\u0627\u0646\u06cc .',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u06d5\u0645 \u0644\u06d5 \u062f\u06d5\u0633 \u0642\u06d5\u0648\u0645 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0644\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u0627\u0646 \u0628\u0648\u0648\u0645.',
    '\u0644\u06d5 \u062a\u0631\u0633\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0632\u0645 \u062f\u0627\u061b \u062f\u0648\u0627\u06cc\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646\u060c \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc\u0645 \u0648 \u0645\u0646\u06cc \u062e\u0633\u062a\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06d5\u0648\u06d5.',
    '\u062a\u06c6 \u0645\u0646\u06d5\u062a\u06cc \u0626\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u0645 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u06d5\u0646\u06ce\u06cc\u060c \u06a9\u06d5 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644\u062a - \u0647\u06d5\u0645\u0648\u0648 - \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u062a\u061f!',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u06af\u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062c\u06cc\u0647\u0627\u0646 \u0648\u06d5\u0686\u06d5\u0626\u06ce\u0698\u0646\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06cc\u0633\u06d5\u0644\u0645\u06ce\u0646\u0646\u060c \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u062f\u06d5\u0648\u0631\u0648\u0628\u06d5\u0631\u06cc \u062e\u06c6\u06cc \u067e\u0631\u0633\u06cc: \u06af\u0648\u06ce\u062a\u0627\u0646 \u0644\u06ce\u06cc\u06d5\u061f',
    '\u06af\u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u06cc \u067e\u06ce\u0634\u0648\u0648\u062a\u0627\u0646\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u062f\u06cc\u0627\u0631\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u060c \u062c\u0646 \u062f\u06d5\u0633\u062a\u06cc \u0644\u06ce \u0648\u06d5\u0634\u0627\u0646\u062f\u0648\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u06af\u06d5\u0631 \u0626\u0627\u0648\u06d5\u0632\u062a\u0627\u0646 \u0647\u06d5\u0628\u06ce\u060c \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062e\u06c6\u0631\u0647\u06d5\u06b5\u0627\u062a \u0648 \u062e\u06c6\u0631\u0627\u0648\u0627 \u0648 \u0646\u06ce\u0648\u0627\u0646\u06cc \u0648\u0627\u0646\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5 \u0645\u0646 \u0628\u067e\u06d5\u0631\u0633\u062a\u06cc\u060c \u062e\u0627\u062a\u0631\u062c\u06d5\u0645 \u062f\u06d5\u0628\u06d5\u0646\u062f\u06ce\u062a \u062f\u06d5\u06a9\u06d5\u0645.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9\u06cc \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u0645 \u0628\u06c6 \u0647\u06ce\u0646\u0627\u0628\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0695\u0627\u0633\u062a\u0627\u0646\u06cc \u060c \u06a9\u0648\u0627\u061f \u0628\u06cc\u0647\u06ce\u0646\u06d5.',
    '\u0639\u0627\u0633\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u0633\u062a\u06cc \u0641\u0695\u06ce\u062f\u0627\u061b \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u0648\u0648 \u0628\u06d5 \u062d\u06d5\u0632\u06cc\u0627\u06cc\u06d5\u06a9.',
    '\u062f\u06d5\u0633\u062a\u06cc\u0634\u06cc \u062f\u06d5\u0631\u062e\u0633\u062a\u060c \u0628\u06c6 \u0648\u0627\u0646\u06d5\u06cc \u0644\u06ce\u06cc\u0627\u0646 \u0695\u0648\u0648\u0627\u0646\u06cc \u0633\u067e\u06cc \u062f\u06d5\u0686\u0648\u0648.',
    '\u0628\u06d5\u0633\u06d5\u0631 \u06af\u06d5\u0648\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062f\u06d5\u0648\u0631\u0648\u0628\u06d5\u0631 \u062e\u06c6\u06cc \u06af\u0648\u062a: \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0645\u06d5 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u06ce\u06a9\u06cc \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5\u062a\u06cc \u0628\u06d5 \u062c\u0627\u062f\u0648\u0648\u06cc \u062e\u06c6\u06cc\u060c \u0644\u06d5 \u0632\u06ce\u062f\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0631\u062a\u0627\u0646 \u06a9\u0627\u061b \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648 \u0628\u0627\u0631\u06d5 \u062f\u06d5\u06b5\u06ce\u0646 \u0686\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u06c6\u06cc \u0648 \u0628\u0631\u0627\u06a9\u06d5\u06cc \u0644\u06ce\u0631\u06d5 \u0628\u0645\u06ce\u0646\u0646\u061b \u062a\u06c6 \u0628\u0646\u06ce\u0631\u06d5 \u0644\u06d5 \u0634\u0627\u0631\u0627\u0646 \u062e\u06d5\u06b5\u06a9\u06cc \u0648\u06d5\u06a9\u06c6 \u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0645 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u06cc \u0632\u0627\u0646\u0627\u062a \u0628\u0647\u06ce\u0646\u0646\u06d5 \u0644\u0627.',
    '\u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648 \u06a9\u06c6 \u06a9\u0631\u0627\u0646\u06d5\u0648\u06d5.',
    '\u0628\u06d5 \u0645\u06d5\u0631\u062f\u0645\u06cc\u0634\u06cc\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5\u0634 \u06a9\u06c6 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0645 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u0627\u0646\u06d5 \u0628\u06cc\u0628\u06d5\u0646\u06d5\u0648\u06d5\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0626\u06ce\u0645\u06d5\u0634 \u0648\u06d5\u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06d5\u0648\u06cc\u0646.',
    '\u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u0627\u0646 - \u06a9\u06d5 \u0647\u0627\u062a\u0646 - \u0628\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u06cc\u0627\u0646 \u06af\u0648\u062a: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0628\u06cc\u0628\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0686\u06cc\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u06d5\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc: \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0646\u0632\u06cc\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0645 \u062f\u06d5\u0628\u0646.',
    '\u0645\u0648\u0648\u0633\u0627 \u0628\u06d5\u0648\u0627\u0646\u06cc \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f: \u0686\u06cc\u062a\u0627\u0646 \u0626\u0627\u0645\u0627\u062f\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0628\u06c6 \u0647\u0627\u0648\u06cc\u0634\u062a\u0646\u060c \u0628\u06cc\u0647\u0627\u0648\u06ce\u0698\u0646.',
    '\u062c\u0627 \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u067e\u06d5\u062a \u0648 \u062f\u0627\u0631\u062f\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0641\u0695\u06ce \u062f\u0627 \u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0633\u0648\u06ce\u0646\u062f\u0627 \u0628\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u060c \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0645\u0627\u0646 \u062f\u06d5\u06cc\u0628\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0645\u0648\u0648\u0633\u0627\u0634 \u062f\u0627\u0631\u062f\u06d5\u0633\u062a\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0641\u0695\u06ce \u062f\u0627. \u06af\u0648\u0632\u0648\u06af\u0648\u0645\u0645\u06d5\u062a\u060c \u0647\u06d5\u0645\u0648\u0648 \u0641\u06ce\u06b5\u06cc \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u0627\u0646\u06cc \u0686\u0646\u06cc\u06d5\u0648\u06d5.',
    '\u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u0627\u0646 [\u0628\u06d5 \u067e\u0627\u0646\u06d5\u0648\u06d5] \u06a9\u0695\u0646\u06c6\u0634\u06cc\u0627\u0646 \u0628\u0631\u062f.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u062f \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062c\u06cc\u0647\u0627\u0646\u06ce.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u0627\u0695\u0648\u0648\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0695\u06ce\u0648 \u0628\u062f\u06d5\u0645\u060c \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0627\u0648\u0627\u062a\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u061f \u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u06af\u06d5\u0648\u0631\u06d5\u062a\u0627\u0646\u06d5 \u0648 \u062e\u06c6\u06cc \u0641\u06ce\u0631\u06cc \u062c\u0627\u062f\u0648\u0648\u06cc \u06a9\u0631\u062f\u06af\u0646. \u062f\u0648\u0627\u06cc\u06cc \u062a\u06ce\u062a\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06cc\u06d5\u0646\u0645\u061b \u062f\u06d5\u0633\u062a\u0648\u0644\u0627\u0642\u0648\u0648 \u0628\u06d5 \u067e\u06ce\u0686\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0628\u0695\u0645 \u0648 \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631\u0648\u0648 \u0644\u06d5 \u062f\u0627\u0631 \u062f\u06d5\u062f\u06d5\u0645.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0645\u0646\u06d5\u062a\u062a \u0628\u06d5 \u067e\u0648\u0648\u0634\u06ce\u06a9\u061b \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5\u0631\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646 \u062f\u06d5\u0686\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06cc\u0648\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0646 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u0627\u0646\u0646 \u0628\u0628\u0648\u0648\u0631\u06ce\u061b \u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5 \u06af\u0634 \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627.',
    '\u0626\u06ce\u0645\u06d5 \u0645\u0648\u0648\u0633\u0627\u0645\u0627\u0646 \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f: \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u0645 \u0644\u06d5 \u0634\u06d5\u0648\u06ce\u062f\u0627 \u0648\u06d5\u0695\u06ce \u0628\u062e\u06d5\u060c \u06a9\u06d5 \u062f\u06cc\u0627\u0631\u06d5 \u0628\u06d5 \u062f\u0648\u0648\u062a\u0627\u0646\u062f\u0627 \u062f\u06ce\u0646.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648\u06d5\u06a9\u06c6\u06a9\u06d5\u0631\u0627\u0646\u06cc \u0646\u0627\u0631\u062f \u0628\u06c6 \u0634\u0627\u0631\u06d5\u06a9\u0627\u0646.',
    '[\u0648\u062a\u06cc:] \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06c6\u0645\u06d5\u06b5\u06d5\u06cc\u06d5\u06a9\u06cc \u0647\u06ce\u0646\u062f\u06a9\u0646.',
    '\u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u06a9\u06d5 \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u062a\u0648\u0648\u0695\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0648\u0631\u06cc\u0627\u06cc\u0627\u0646\u06cc\u0646.',
    '\u0626\u06cc\u062a\u0631 \u0628\u0627\u063a\u0627\u062a \u0648 \u06a9\u0627\u0646\u06cc\u0627\u0648\u0646 \u067e\u06ce \u062c\u06ce\u0647\u06ce\u0634\u062a\u0646.',
    '\u0648\u06ce\u0695\u0627\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06af\u06d5\u0646\u062c\u06cc\u0646\u06d5 \u0648 \u062e\u0627\u0646\u0648\u0648\u06d5 \u062e\u06c6\u0634\u06d5\u06a9\u0627\u0646.',
    '\u0647\u06d5\u0631 \u0628\u06d5\u0648 \u062c\u06c6\u0631\u06d5 \u0626\u06d5\u0645 \u0647\u06d5\u0645\u0648\u0648\u06d5\u0634\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644.',
    '\u0647\u06d5\u0631 \u06af\u0632\u06cc\u0646\u06af \u0644\u06d5 \u0633\u06d5\u0631\u0627\u0646\u06cc \u062f\u0627\u060c \u062f\u0648\u0627\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631 \u062f\u0648\u0648 \u06a9\u06c6\u0645\u06d5\u06b5 \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0627\u0646 \u062f\u06cc\u061b \u0647\u0627\u0648\u0695\u06ce\u06cc\u0627\u0646\u06cc \u0645\u0648\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc\u0627\u0646: \u067e\u06cc\u0633 \u067e\u06ce\u0648\u06d5 \u0628\u0648\u0648\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0646\u0627\u0628\u06ce \u0644\u06d5 \u0647\u06cc\u0686 \u0628\u062a\u0631\u0633\u0646\u060c \u0686\u0648\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u062f\u06d5\u06af\u06d5\u06b5\u06d5 \u0648 \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0631\u0648\u06ce\u06a9\u0645 \u0644\u06ce \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0645\u0648\u0648\u0633\u0627\u0645\u0627\u0646 \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f: \u0628\u06d5 \u0639\u0627\u0633\u0627\u06a9\u06d5\u062a \u0644\u06d5 \u0632\u06d5\u0631\u06cc\u0627 \u062f\u06d5\u061b \u0632\u06d5\u0631\u06cc\u0627 \u0642\u06d5\u06b5\u0634\u06cc \u0648 \u0647\u06d5\u0631 \u0644\u0627\u06cc\u06d5\u06a9\u06cc \u0648\u06d5\u06a9 \u0686\u06cc\u0627\u06cc\u06d5\u06a9\u06cc \u0645\u06d5\u0632\u0646 \u0628\u0648\u0648.',
    '\u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u062f \u0626\u06d5\u0648\u0627\u0646\u06cc \u062a\u0631\u06cc\u0634 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u0646\u0632\u06cc\u06a9 \u0628\u0646\u06d5\u0648\u06d5.',
    '\u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u06d5\u0631\u06a9\u06ce \u062f\u06d5\u06af\u06d5\u06b5\u06cc \u0628\u0648\u0648\u0646\u060c \u0646\u06d5\u062c\u0627\u062a\u0646 \u062f\u0627\u0646.',
    '\u067e\u0627\u0634\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06cc \u062f\u06cc\u06a9\u06d5\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0646\u06a9\u0627\u0646 \u062f\u0627.',
    '\u0626\u06d5\u0645 \u0695\u0648\u0648\u062f\u0627\u0648\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 \u0628\u0648\u0648\u061b \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0632\u06c6\u0631\u06cc\u0634 \u0647\u06ce\u0634\u062a\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627.',
    '\u062e\u06c6 \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0695\u0648\u0648\u062f\u0627\u0648\u06cc \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u0633\u06d5\u0631\u062f\u06d5\u0645\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u0626\u0627\u067e \u0648 \u0647\u06c6\u0632\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0626\u06d5\u0645\u06d5 \u0686\u06cc\u06cc\u06d5 \u062f\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u062a\u0627\u0646\u06ce\u06a9 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646 \u0648 \u0647\u06d5\u0631 \u0648\u0627\u0632\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u0647\u06ce\u0646\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0628\u06d5\u0646\u060c \u0644\u06ce\u0648 \u062f\u06d5\u0628\u06cc\u0633\u0646\u061f',
    '\u06cc\u0627\u062e\u06c6 \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u0648 \u067e\u06ce \u062f\u06d5\u062f\u06d5\u0646\u061f \u06cc\u0627\u0646 \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646\u0648\u0648 \u0644\u06ce\u062f\u06d5\u062f\u06d5\u0646\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0646\u06d5\u062e\u06ce\u0631\u060c \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u06cc \u0626\u06ce\u0645\u06d5\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a.',
    '\u06af\u0648\u062a\u06cc: \u0626\u0627\u062e\u06c6 \u0632\u0627\u0646\u06cc\u0648\u062a\u0627\u0646\u06d5 \u0686\u06cc\u0648 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u061f',
    '\u062e\u06c6\u062a\u0627\u0646 \u0648\u06ce\u0695\u0627\u06cc \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u062a\u0627\u0646\u061f',
    '\u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u062f\u0648\u0698\u0645\u0646\u06cc \u0645\u0646\u0646\u061b \u062c\u06af\u06d5 \u0644\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062d\u06d5\u0645\u0648\u0648 \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u06cc \u0645\u0646\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634 \u0634\u0627\u0631\u06d5\u0632\u0627\u0645 \u062f\u06d5\u06a9\u0627.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u062e\u0648\u0627\u0631\u062f\u06d5\u0645\u06d5\u0646\u06cc\u0645 \u067e\u06ce\u062f\u06d5\u062f\u0627\u062a \u0648 \u0626\u0627\u0648\u06cc\u0634\u0645 \u062f\u06d5\u0641\u0631\u06cc\u0627 \u062f\u06d5\u062e\u0627.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u0646\u06d5\u0633\u0627\u063a\u06cc\u0634 \u0628\u0645\u060c \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0686\u0627\u06a9\u0645 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u062f\u06d5\u0645 \u0645\u0631\u06ce\u0646\u06ce \u0648 \u067e\u0627\u0634\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648\u0645 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5 \u0647\u06cc\u0648\u0627\u0645 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0644\u06d5 \u0647\u06d5\u06b5\u06d5\u06a9\u0627\u0646\u0645 \u0628\u0628\u0648\u0648\u0631\u06ce.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u062e\u06c6\u062a \u0641\u06ce\u0631\u06cc \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u0645 \u0628\u06a9\u06d5 \u0648 \u0628\u0645\u06af\u06d5\u06cc\u0646\u06d5 \u0628\u06d5 \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646.',
    '\u0648\u06d5\u0628\u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0686\u06cc\u0646\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634\u0627 \u0646\u0627\u0648\u0645 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u0631\u0628\u0686\u06ce.',
    '\u0648\u0627\u0628\u06a9\u06d5 \u0645\u0646 \u0644\u06d5 \u0628\u06d5\u0634\u0628\u06d5\u0631\u0627\u0646\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc \u067e\u0695 \u0644\u06d5 \u062e\u06c6\u0634\u06cc \u0628\u0645.',
    '\u0644\u06d5 \u0626\u0627\u067e\u06cc \u0645\u0646\u06cc\u0634 \u0628\u0628\u0648\u0648\u0631\u06d5\u061b \u06a9\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0647\u06d5\u06b5\u06d5 \u06a9\u0631\u062f\u0628\u0648\u0648.',
    '\u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0632\u06cc\u0646\u062f\u0648\u0648\u0628\u0648\u0646\u06d5\u0648\u06d5\u0634 \u0695\u06cc\u0633\u0648\u0627\u0645 \u0645\u06d5\u06a9\u06d5.',
    '\u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u06a9\u06d5 \u0645\u0627\u06b5 \u0648 \u0645\u0646\u0627\u06b5 \u0628\u06d5 \u06a9\u0627\u0631 \u0646\u0627\u06cc\u06d5.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06d5\u06cc \u0648\u06d5\u0644 \u062f\u06b5\u06cc \u067e\u0627\u06a9\u060c \u0628\u0686\u06ce\u062a\u06d5 \u0628\u0627\u0631\u06d5\u06af\u0627\u06cc \u062e\u0648\u062f\u0627.',
    '\u06a9\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u0634 \u0628\u06c6 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646 \u0646\u0632\u06cc\u06a9\u06d5.',
    '\u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0634 \u062f\u06d5\u062e\u0631\u06ce\u062a\u06d5 \u0695\u0648\u0648 \u0628\u06c6 \u06af\u0648\u0645\u0695\u0627\u06cc\u0627\u0646.',
    '[\u06a9\u06d5 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5] \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0644\u06d5 \u06a9\u0648\u06ce\u0646 \u0626\u06d5\u0648\u06cc \u062f\u06d5\u0648\u067e\u06d5\u0631\u0633\u062a\u0646\u060c',
    '\u0644\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627\u061f \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648 \u062f\u06ce\u0646\u06d5 \u0647\u0627\u0646\u0627\u062a\u0627\u0646\u060c \u06cc\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646 \u0647\u0627\u0646\u0627 \u062f\u06d5\u062e\u0648\u0627\u0632\u0646\u061f',
    '\u0633\u0627 \u062f\u06d5\u0645\u06d5\u0648\u0695\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0648 \u06af\u0648\u0645\u0695\u0627\u06a9\u0627\u0646\u06cc\u0634 \u062f\u06d5\u0648\u06ce \u062f\u06d5\u062e\u0631\u06ce\u0646.',
    '\u0644\u06d5\u0634\u06a9\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u06cc\u0634 - \u0628\u06d5 \u062a\u06ce\u06a9\u0695\u0627\u06cc\u06cc -',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627 - \u0628\u06d5 \u06a9\u06ce\u0634\u06d5 - \u062f\u06ce\u0646 \u0648 \u062f\u0628\u06ce\u0698\u0646:',
    '\u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0644\u0627\u0695\u06ce \u0628\u0648\u0648\u06cc\u0646\u061b',
    '\u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062c\u06cc\u0647\u0627\u0646 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u06a9\u0631\u062f\u061b',
    '\u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06cc\u0634 \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u0627\u061b',
    '\u0626\u06ce\u0633\u062a\u0627\u0634 \u0646\u06d5 \u062a\u06a9\u0627\u06a9\u0627\u0631\u0645\u0627\u0646 \u0647\u06d5\u0646\u060c',
    '\u0646\u06d5 \u0647\u06d5\u0648\u0627\u06b5\u06ce\u06a9\u06cc \u062f\u06b5\u0633\u06c6\u0632\u06cc\u0634.',
    '\u062e\u06c6\u0632\u06cc \u062c\u0627\u0631\u06ce \u0648\u06d5\u06af\u06d5\u0695\u0627\u0628\u0627\u06cc\u0646\u060c \u0626\u06d5\u0648\u0633\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u062f\u06ce\u0646\u0627.',
    '\u0626\u0627 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9\u06cc [\u0628\u06d5\u0647\u06ce\u0632] \u0647\u06d5\u06cc\u06d5 \u0648 \u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646.',
    '\u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d\u06cc\u0634 \u0628\u06d5 \u0648\u062a\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc\u0627\u0646 \u0628\u0695\u0648\u0627 \u0646\u06d5\u06a9\u0631\u062f.',
    '\u06a9\u0627\u062a\u06ce \u0646\u0648\u0648\u062d\u06cc \u0628\u0631\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u0645\u06d5\u0631 \u0646\u0627\u062a\u0631\u0633\u0646\u061f',
    '\u0645\u0646 \u0628\u06c6\u062a\u0627\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06cc \u062c\u06ce \u0628\u0627\u0648\u06d5\u0695\u0645.',
    '\u062f\u06d5\u0633\u0627 \u0644\u06d5 \u062e\u0648\u0627 \u0628\u062a\u0631\u0633\u0646 \u0648 \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0645\u0646\u06cc\u0634 \u0628\u06a9\u06d5\u0646.',
    '\u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u0645 - \u0628\u06c6 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 - \u0644\u06ce\u062a\u0627\u0646 \u0646\u0627\u0648\u06ce\u061b \u067e\u0627\u062f\u0627\u0634\u06cc \u0645\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062c\u06cc\u0647\u0627\u0646 \u062f\u06d5\u06cc\u062f\u0627\u060c',
    '\u0626\u06cc\u062a\u0631 \u0647\u06d5\u0631 \u062a\u0631\u0633\u0648\u0648 \u0644\u06d5 \u062e\u0648\u0627 \u0628\u06ce \u0648 \u0628\u06d5 \u06af\u0648\u06ce\u0645 \u0628\u06a9\u06d5\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0686\u06c6\u0646 \u0628\u0695\u0648\u0627\u062a \u067e\u06ce\u062f\u06d5\u06a9\u06d5\u06cc\u0646\u061f \u062a\u06c6 \u0647\u06d5\u0631 \u0695\u0648\u0648\u062a\u06d5\u0648\u067e\u0648\u0648\u062a\u06d5\u062a \u0644\u06d5 \u062f\u0648\u0648\u0646!',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0646 \u0686\u0648\u0648\u0632\u0627\u0646\u0645 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0631\u06ce \u0686\u06cc\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u061f',
    '\u0626\u06d5\u0631 \u062a\u06ce\u0628\u06af\u06d5\u0646\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645 \u062f\u06d5\u0628\u06ce \u0628\u06d5 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0695\u0627\u0628\u06af\u0627.',
    '\u0644\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06cc\u0645 - \u0628\u06d5 \u0647\u06cc\u0686 \u0626\u0627\u0648\u0627 - \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0648\u06d5\u062f\u06d5\u0631\u0628\u0646\u06ce\u0645.',
    '\u0645\u0646 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u0646\u0648\u0648\u062d! \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u062a \u0628\u06d5\u0633 \u0646\u06d5\u06a9\u06d5\u06cc\u060c \u0626\u06d5\u0634\u06ce \u0633\u06d5\u0646\u06af\u06d5\u0633\u0627\u0631\u062a \u0628\u06a9\u06d5\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0645\u0646 \u0647\u06d5\u0631 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0645.',
    '\u0633\u0627 \u062a\u06c6 \u0644\u06d5 \u0646\u06ce\u0648 \u0645\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646 \u0695\u06ce\u06af\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0628\u06a9\u06d5\u0648\u06d5 \u0648 \u0645\u0646 \u0648 \u0626\u06d5\u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u0646\u0646\u060c \u0695\u0632\u06af\u0627\u0631 \u0628\u06a9\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u062e\u06c6\u06cc \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5\u06cc \u0628\u0648\u0648\u0646\u060c \u0644\u06d5 \u06a9\u06d5\u0634\u062a\u06cc\u06cc\u06d5 \u062a\u0698\u06cc \u06a9\u06d5\u062f\u0627 \u0646\u06d5\u062c\u0627\u062a\u0646 \u062f\u0627.',
    '\u0626\u06d5\u0648\u0627\u0646\u06cc \u062a\u0631\u06cc\u0634 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u0626\u0627\u0648\u06d5\u0648\u0626\u0627\u0648 \u0686\u0648\u0648\u0646 \u0648 \u062a\u0648\u0648\u0646\u0627 \u0628\u0648\u0648\u0646.',
    '\u0644\u06d5\u0645\u06d5\u0634\u062f\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 \u0647\u06d5\u06cc\u06d5\u061b [\u0628\u06d5\u06b5\u0627\u0645] \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06c6\u0632\u06cc \u0639\u0627\u062f\u06cc\u0634 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627.',
    '\u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0647\u0648\u0648\u062f\u06cc \u0628\u0631\u0627\u06cc\u0627\u0646 \u067e\u06ce\u06cc\u0627\u0646\u06cc \u06af\u0648\u062a: \u0686\u06c6\u0646\u06d5 \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u0648\u0646\u06cc\u06d5\u061f',
    '\u0645\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u062c\u06ce \u0628\u0627\u0648\u06d5\u0695\u0645.',
    '\u062a\u0631\u0633\u0648\u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce\u062a \u0648 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0645\u0646 \u062f\u06d5\u0631\u0645\u06d5\u0686\u0646.',
    '[\u0628\u06c6 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5] \u0645\u0646 \u0647\u06cc\u0686 \u067e\u0627\u062f\u0627\u0634\u0645 \u0644\u06ce\u0648 \u0646\u0627\u0648\u06ce\u061b \u067e\u0627\u062f\u0627\u0634\u06cc \u0645\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062c\u06cc\u0647\u0627\u0646 \u062f\u06d5\u06cc\u062f\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0647\u06d5\u0631 \u062c\u06ce\u06af\u0627\u06cc\u06d5\u06a9\u06cc \u0628\u06d5\u0631\u0632\u060c \u06a9\u06c6\u0634\u06a9\u06cc \u062a\u06d5\u0631\u0632 \u062f\u0631\u0648\u0633 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u06a9\u0627\u0631\u06af\u06d5\u0644\u06cc \u067e\u0695\u0648\u067e\u0648\u0648\u0686 \u0628\u0646\u061f',
    '\u06a9\u06c6\u0634\u06a9 \u0648 \u0642\u06d5\u06b5\u0627\u06cc \u0632\u06c6\u0631 \u067e\u062a\u06d5\u0648\u06cc\u0634 \u062f\u0627\u062f\u06d5\u0695\u06ce\u0698\u0646\u060c \u0644\u0627\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u0645\u06ce\u0646\u0646\u061f',
    '\u0647\u06d5\u0631\u06af\u0627 \u067e\u06d5\u0644\u0627\u0645\u0627\u0631\u06cc\u0634 \u062f\u06d5\u062f\u06d5\u0646\u060c \u0645\u0644\u0647\u0648\u0695\u0627\u0646\u06d5 \u0647\u06ce\u0631\u0634 \u062f\u06d5\u0628\u06d5\u0646.',
    '\u0633\u0627 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646 \u0648 \u0628\u06d5 \u06af\u0648\u06ce\u0645 \u0628\u06a9\u06d5\u0646.',
    '\u0628\u0627 \u062a\u0631\u0633\u0648\u0648 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0628\u06ce \u06a9\u06d5 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u0632\u0627\u0646\u0646\u060c \u06cc\u0627\u0631\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u0648\u06b5\u0633\u0627\u062a \u0648 \u06a9\u0648\u0695\u06af\u06d5\u0644\u06cc \u062f\u0627\u0648\u0646\u06ce.',
    '\u062f\u06d5\u06af\u06d5\u06b5 \u0628\u0627\u063a\u0627\u062a \u0648 \u06a9\u0627\u0646\u06cc\u0627\u0648\u06cc\u0634.',
    '\u0626\u06d5\u0645\u0646 \u0647\u06d5\u0631 \u0644\u06ce\u0648 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646\u0645\u060c \u0628\u06c6 \u0626\u0627\u0632\u0627\u0631\u06cc \u0695\u06c6\u0698\u06cc \u0645\u06d5\u0632\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u0645\u0627\u0646 \u0628\u06a9\u06d5\u06cc \u0648 \u0647\u06cc\u0686 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u0634\u0645\u0627\u0646 \u0646\u06d5\u06a9\u06d5\u06cc\u060c \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u06d5.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u06af\u0634 \u067e\u0695\u0648\u067e\u0648\u0648\u0686\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06cc\u06cc\u0627\u0646\u06d5.',
    '\u06a9\u06d5\u0633 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u06ce\u0645\u06d5 \u0646\u0627\u062f\u0627.',
    '\u0626\u06d5\u0648 \u0647\u06d5\u0631 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0642\u0695\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646. \u0644\u06d5\u0645\u06d5\u0634\u062f\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 \u0647\u06d5\u0628\u0648\u0648\u061b [\u0628\u06d5\u06b5\u0627\u0645] \u0632\u06c6\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06c6\u0632\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0633\u0627\u06b5\u062d\u06cc \u0628\u0631\u0627\u06cc\u0627\u0646 \u067e\u06ce\u06cc\u0627\u0646\u06cc \u06af\u0648\u062a: \u0686\u06c6\u0646\u06d5 \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u0645\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u062c\u06ce \u0628\u0627\u0648\u06d5\u0695\u0645.',
    '\u0633\u0627 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646 \u0648 \u0628\u06d5 \u06af\u0648\u06ce\u0645 \u0628\u06a9\u06d5\u0646.',
    '\u0645\u0646 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0627\u0631\u06cc \u062e\u06c6\u0645 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0648\u06ce\u061b \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062c\u06cc\u0647\u0627\u0646\u060c \u0628\u06c6 \u062e\u06c6\u06cc \u067e\u0627\u062f\u0627\u0634\u06cc \u0645\u0646 \u062f\u06d5\u062f\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0647\u06d5\u0631\u0648\u0627 \u0644\u06ce\u0648 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u060c \u0647\u06d5\u0631 \u0644\u06ce\u0631\u06d5 \u062f\u06b5\u0646\u06cc\u0627 \u0628\u0698\u06cc\u0646\u061f',
    '\u0644\u06d5 \u0628\u0627\u063a\u0627\u062a \u0648 \u0644\u06d5 \u06a9\u0627\u0646\u06cc\u0627\u0648\u0627\u0646\u061f',
    '\u06a9\u06ce\u06b5\u06af\u06d5\u06a9\u0627\u0646 \u0648 \u062f\u0627\u0631\u06d5\u062e\u0648\u0631\u0645\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0634\u06a9\u06c6\u0641\u06d5\u06cc \u0646\u06d5\u0631\u0645 \u0648 \u0646\u06c6\u06b5\u061f',
    '\u0647\u06d5\u0631 \u0628\u06d5 \u062e\u06c6\u0634\u06cc \u0648 \u0634\u0627\u062f\u0645\u0627\u0646\u06cc \u0645\u0627\u06b5\u0627\u0646 \u0644\u06d5 \u06a9\u0627\u0648\u0627\u0646 \u062f\u06d5\u062a\u0627\u0634\u0646.',
    '\u0633\u0627 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646 \u0648 \u0628\u06d5 \u06af\u0648\u06ce\u0645 \u0628\u06a9\u06d5\u0646.',
    '\u06af\u0648\u06ce\u0634 \u0645\u06d5\u062f\u06d5\u0646\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0647\u06d5\u06b5\u06d5\u0695\u06c6\u06cc\u06d5\u06a9\u0627\u0646\u061b',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u0647\u06d5\u0631 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0631\u0627\u067e\u06d5\u0646 \u0648 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06d5\u0633 \u0646\u0627\u06cc\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0628\u06ce \u0648 \u0646\u06d5\u0628\u06ce\u060c \u062c\u0627\u062f\u0648\u0648\u062a \u0644\u06ce\u06a9\u0631\u0627\u0648\u06d5.',
    '\u062a\u06c6\u0634 \u0647\u06d5\u0631 \u0645\u0631\u06c6\u06cc \u0648\u06d5\u06a9\u0648\u0648 \u062e\u06c6\u0645\u0627\u0646. \u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc\u062a\u06d5\u060c \u062f\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u06d5 \u0648\u0634\u062a\u0631\u06ce\u06a9\u06d5\u060c \u06a9\u06d5 \u0646\u06c6\u0631\u06d5\u0626\u0627\u0648\u06ce\u06a9\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u0626\u06ce\u0648\u06d5\u0634 \u0646\u06c6\u0631\u06d5\u0626\u0627\u0648\u06ce\u06a9\u062a\u0627\u0646 \u0647\u06d5\u0633\u061b \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u0627.',
    '\u0647\u06cc\u0686 \u0648\u06d5\u0632\u06d5\u0646\u06cc\u0634\u062a\u0627\u0646 \u0628\u06c6\u06cc \u0646\u06d5\u0628\u06ce\u060c \u062f\u06d5\u0646\u0627 \u062a\u0648\u0648\u0634\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0695\u06c6\u0698\u06cc \u0645\u06d5\u0632\u0646 \u062f\u06ce\u0646.',
    '\u067e\u06d5\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0648 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0628\u0695\u06cc \u0648 \u062f\u0648\u0627\u06cc\u06d5 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5.',
    '\u0626\u0627\u0632\u0627\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u06cc \u0644\u06ce \u06af\u0631\u062a\u0646. \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0647\u06d5\u0628\u0648\u0648\u061b \u06a9\u06d5\u0686\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06c6\u0632\u06cc \u0644\u0648\u0648\u062a\u06cc\u0634 \u0642\u0633\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u060c \u0628\u06d5 \u062f\u0631\u06c6 \u0698\u0645\u0627\u0631\u062f.',
    '\u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0644\u0648\u0648\u062a\u06cc \u0628\u0631\u0627\u06cc\u0627\u0646 \u067e\u06ce\u06cc\u0627\u0646\u06cc \u06af\u0648\u062a: \u0626\u06ce\u0648\u06d5 \u0628\u06c6 \u0647\u06cc\u0686 \u062a\u0631\u0633\u0648\u0648 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u0645\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u062c\u06ce \u0628\u0627\u0648\u06d5\u0695\u0645.',
    '\u0633\u0627 \u062a\u0631\u0633\u0648\u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0628\u06d5 \u06af\u0648\u06ce\u0645 \u0628\u06a9\u06d5\u0646.',
    '\u0628\u06c6 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0645\u0646 \u0647\u06cc\u0686 \u067e\u0627\u062f\u0627\u0634\u0645 \u0644\u06ce\u0648 \u0646\u0627\u0648\u06ce\u061b \u067e\u0627\u062f\u0627\u0634\u06cc \u0645\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062c\u06cc\u0647\u0627\u0646 \u062f\u06d5\u06cc\u062f\u0627.',
    '\u0626\u06ce\u0648\u06d5 \u0628\u06c6\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627 \u067e\u06d5\u0644\u0627\u0645\u0627\u0631\u06cc \u0646\u06ce\u0631\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646\u061f',
    '\u0644\u06d5 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634\u0648\u0648 \u0648\u0627\u0632 \u062f\u06ce\u0646\u0646\u060c \u06a9\u06d5 \u062e\u0648\u0627 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u0646\u061f \u0626\u06ce\u0648\u06d5 \u0647\u06c6\u0632\u06ce\u06a9\u06cc \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0644\u0627\u062f\u06d5\u0631\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u0644\u0648\u0648\u062a! \u06af\u06d5\u0631 \u0644\u06d5\u0645 \u0642\u0633\u0627\u0646\u06d5 \u0628\u06d5\u0633 \u0646\u06d5\u06a9\u06d5\u06cc\u060c \u0633\u0648\u0648\u0631 \u0628\u0632\u0627\u0646\u06d5 \u062f\u06d5\u0631\u062a \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0644\u06d5\u0648 \u0626\u0627\u06a9\u0627\u0631\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u06cc\u06d5\u06a9\u062c\u0627\u0631 \u0642\u06d5\u06b5\u0633\u0645.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0644\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0645\u0646 \u0648 \u062e\u06ce\u0632\u0627\u0646\u0645 \u0695\u0632\u06af\u0627\u0631 \u06a9\u06d5.',
    '\u0633\u0627 \u0626\u06d5\u0648\u06cc\u0634 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u062e\u0627\u0648\u0648\u062e\u06ce\u0632\u0627\u0646\u06cc\u0634\u06cc\u0645\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u062a\u0627\u0642\u06d5 \u067e\u06cc\u0631\u06ce\u0698\u0646\u06ce\u06a9 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u0627 \u0645\u0627\u06cc\u06d5\u0648\u06d5.',
    '\u067e\u0627\u0634\u0627\u0646 \u0626\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc \u062a\u0631\u0645\u0627\u0646 \u0642\u0695\u0627\u0646 \u062a\u06ce\u062e\u0633\u062a.',
    '\u0628\u0627\u0631\u0627\u0646\u06ce\u06a9\u06cc [\u0628\u06d5\u06b5\u0627] \u0645\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0628\u0627\u0631\u0627\u0646\u062f\u0646. \u0686\u06d5\u0646\u062f \u0628\u06d5 \u0632\u06d5\u0695 \u0628\u0648\u0648 \u0628\u0627\u0631\u0627\u0646\u06cc \u062a\u0631\u0633\u06ce\u0646\u0631\u0627\u0648\u06d5\u06a9\u0627\u0646.',
    '\u0626\u0627 \u0644\u06d5\u0645\u06d5\u0634\u062f\u0627 \u0628\u06d5\u06b5\u06af\u06d5 \u0647\u06d5\u0628\u0648\u0648\u061b \u06a9\u06d5\u0686\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0631 \u062e\u0627\u0648\u06d5\u0646\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u062d\u0644\u0627\u0646 \u0642\u0633\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0698\u0645\u0627\u0631\u062f.',
    '\u06a9\u0627\u062a\u06ce \u0634\u0648\u0639\u06d5\u06cc\u0628 \u067e\u06ce\u06cc\u0627\u0646\u06cc \u06af\u0648\u062a: \u0686\u06c6\u0646\u06d5 \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9\u0648\u0648 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u0633\u0627 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce \u0648 \u0628\u06d5 \u06af\u0648\u06ce\u0645 \u0628\u06a9\u06d5\u0646.',
    '\u0645\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u062c\u06ce \u0628\u0627\u0648\u06d5\u0695\u0645.',
    '\u0628\u06c6 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0645\u0646 \u0647\u06cc\u0686 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u0645 \u0644\u06ce\u0648 \u0646\u0627\u0648\u06ce\u061b \u067e\u0627\u062f\u0627\u0634\u06cc \u0645\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062f\u0646\u06cc\u0627 \u062f\u06d5\u06cc\u062f\u0627.',
    '\u0695\u0627\u0633\u062a \u0628\u0646 \u0644\u06d5 \u067e\u06ce\u0648\u0627\u0646\u06d5\u067e\u06ce\u0648\u0627\u0646 \u0648 \u0632\u06cc\u0627\u0646 \u0628\u06d5 \u06a9\u06d5\u0633 \u0645\u06d5\u06af\u06d5\u06cc\u06d5\u0646\u0646.',
    '\u06a9\u06ce\u0634\u0627\u0646\u06d5\u0634\u062a\u0627\u0646 \u0628\u06d5 \u062a\u06d5\u0631\u0627\u0632\u0648\u0648\u06cc \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06ce.',
    '\u0634\u062a \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0645\u06d5\u062f\u0632\u0646\u06d5\u0648\u06d5 \u0648 \u067e\u0627 \u062f\u0627\u0645\u06d5\u06af\u0631\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0644\u06d5 \u0647\u06d5\u0631\u062f\u0627.',
    '\u062f\u06d5\u0628\u06ce \u0628\u062a\u0631\u0633\u0646 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0648 \u06a9\u06c6\u0645\u06d5\u06b5\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u06cc \u0628\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0628\u06ce \u0648 \u0646\u06d5\u0628\u06ce\u060c \u062c\u0627\u062f\u0648\u0648\u062a \u0644\u06ce\u06a9\u0631\u0627\u0648\u06d5.',
    '\u062a\u06c6\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627 \u0645\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06ce\u0645\u06d5 \u0648 \u067e\u06ce\u0645\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u062a\u06c6\u0634 \u0647\u06d5\u0631 \u0644\u06d5 \u06a4\u0695\u0628\u06ce\u0698\u06d5\u06a9\u0627\u0646\u06cc.',
    '\u062f\u06d5\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0695\u0627\u0633\u062a \u0626\u06ce\u0698\u06cc\u060c \u0686\u06d5\u0646\u062f \u067e\u0627\u0698\u06ce\u06a9\u0645\u0627\u0646 - \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646 - \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0628\u0627\u0648\u06ce\u0698\u06d5 \u062e\u0648\u0627\u0631\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u067e\u062a\u0631 \u0626\u0627\u06af\u0627\u06cc \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0631\u0648\u0648 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06cc\u062a\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627\u060c \u062a\u0627 \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u0647\u06d5\u0648\u0631\u062f\u0627\u060c \u06af\u0695\u06d5\u0628\u0627\u0645\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06b5\u06a9\u0631\u062f\u0646\u061b \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u0627\u06cc\u06d5 \u0632\u06c6\u0631 \u0645\u06d5\u0632\u0646 \u0628\u0648\u0648.',
    '\u0644\u06d5\u0645\u06d5\u0634\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0647\u06d5\u0628\u0648\u0648\u061b \u06a9\u06d5\u0686\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0631 \u062e\u0627\u0648\u06d5\u0646\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u06af\u0634 \u062c\u06cc\u0647\u0627\u0646\u06d5\u061b \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u06cc \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631.',
    '\u00ab\u0695\u0648\u062d \u0627\u0644\u0627\u0645\u06cc\u0646\u00bb \u0647\u06ce\u0646\u0627\u06cc\u06d5 \u062e\u0648\u0627\u0631.',
    '\u0628\u06c6 \u0633\u06d5\u0631 \u062f\u06b5\u062a\u061b \u062a\u0627 \u062a\u06c6 \u0644\u06d5 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u0627\u0646 \u0628\u06cc.',
    '\u0628\u06d5 \u0632\u0648\u0648\u0627\u0646\u06ce\u06a9\u06cc \u0639\u06d5\u0631\u06d5\u0628\u06cc \u0695\u06d5\u0648\u0627\u0646 \u0648 \u0647\u0627\u0633\u0627\u0646.',
    '\u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u0634\u062f\u0627 \u0628\u0627\u0633\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u062f\u0627\u062e\u06c6 \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 \u0646\u06cc\u06cc\u06d5\u061f \u06a9\u06d5 \u0632\u0627\u0646\u0627\u06a9\u0627\u0646\u06cc \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u06cc\u0634\u060c \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0626\u0627\u06af\u0627\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u0627\u06cc\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0639\u0627\u0631\u06d5\u0628\u06cc \u0646\u06d5\u0632\u0627\u0646\u06ce\u06a9\u060c',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u06d5\u062e\u0648\u06ce\u0646\u062f\u0628\u0627\u060c \u0642\u06d5\u062a \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u062f\u06ce\u0646\u0627.',
    '\u0647\u06d5\u0631\u0648\u06d5\u0647\u0627\u06cc\u0646\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u062e\u0633\u062a\u06d5 \u062f\u06b5\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646.',
    '\u0647\u06d5\u0631 \u0628\u0695\u0648\u0627\u0634\u06cc \u067e\u06ce \u0646\u0627\u0647\u06ce\u0646\u0646\u060c \u0647\u06d5\u062a\u0627 \u0626\u0627\u0632\u0627\u0631\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646 \u0628\u06d5 \u0686\u0627\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0628\u06cc\u0646\u0646.',
    '\u0644\u06d5\u0646\u06d5\u06a9\u0627\u0648\u06cc\u0634 \u062f\u06ce\u062a\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u0647\u06d5\u0633\u062a\u06cc\u0634\u06cc \u067e\u06ce \u0646\u0627\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0645\u06c6\u06b5\u06d5\u062a\u0645\u0627\u0646 \u067e\u06ce\u062f\u06d5\u062f\u06d5\u0631\u06ce\u061f',
    '\u0628\u06c6 \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u060c \u067e\u06d5\u0644\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u062a\u06c6 \u067e\u06ce\u0645 \u0628\u06ce\u0698\u06d5 \u0626\u06d5\u0631 \u0628\u06ce\u06b5\u06cc\u0646 \u0686\u06d5\u0646 \u0633\u0627\u06b5 \u0695\u0627\u0628\u0648\u06ce\u0631\u0646\u060c',
    '\u0626\u06d5\u0645\u062c\u0627\u0631 \u0626\u06d5\u0648 \u0626\u0627\u0632\u0627\u0631\u06d5\u06cc \u06a9\u06d5 \u0644\u06ce \u062a\u0631\u0633\u06ce\u0646\u0631\u0627 \u0628\u0648\u0648\u0646\u060c \u0628\u06ce\u062a\u06d5\u0644\u0627\u06cc\u0627\u0646\u061b',
    '\u0626\u06d5\u0648 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06cc \u06a9\u06d5 \u0695\u0627\u06cc \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646\u060c \u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u062f\u0627\u061f',
    '\u0647\u06d5\u0631 \u0634\u0627\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0642\u0695 \u06a9\u0631\u062f\u0628\u06ce\u060c \u0633\u0648\u0648\u0631 \u0628\u0632\u0627\u0646\u06d5 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u0627\u0646\u06ce\u06a9\u06cc \u0628\u0648\u0648\u06d5\u061b',
    '\u06a9\u06d5 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0648\u06d5\u0631\u06af\u0631\u0646 \u0648\u060c \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0645\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0634\u0627\u06cc\u06d5\u062a\u06cc\u0646\u06cc\u0634 \u0646\u06d5\u06cc\u0627\u0646\u0647\u06ce\u0646\u0627\u0648\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce.',
    '\u0646\u06d5 \u0644\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u0648\u06d5\u0634\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0646\u06d5 \u062f\u06d5\u0634\u062a\u0648\u0627\u0646\u0646.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0633\u062a\u0646 \u0628\u06ce \u0628\u06d5\u0634 \u06a9\u0631\u0627\u0648\u0646.',
    '\u062a\u06c6 \u0648\u06ce\u0695\u0627\u06cc \u062e\u0648\u0627\u060c \u0647\u06cc\u0686 \u0634\u062a\u06cc \u062a\u0631 \u0645\u06d5\u067e\u06d5\u0631\u0633\u062a\u06d5\u061b \u062f\u06d5\u0646\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0628\u06cc \u06a9\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u0646\u0632\u06cc\u06a9\u062a\u0631\u06cc\u0646 \u062e\u0632\u0645\u06cc \u062e\u06c6\u0634\u062a \u0628\u062a\u0631\u0633\u06ce\u0646\u06d5.',
    '\u0628\u0627\u06b5\u062a \u0634\u06c6\u0695 \u06a9\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0634\u0648\u06ce\u0646\u062a \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646.',
    '\u062c\u0627 \u0626\u06d5\u0631 \u0628\u06d5 \u06af\u0648\u06ce\u06cc\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u06cc\u060c \u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0628\u06ce\u0632\u0627\u0631\u0645 \u0644\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u0634\u062a \u0628\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646 \u0628\u0633\u067e\u06ce\u0631\u06d5\u061b',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u06a9\u06d5 \u062a\u06c6 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06cc\u060c \u0648\u0627 \u0628\u06d5 \u067e\u06ce\u0648\u06d5 \u0647\u06d5\u06b5\u062f\u06d5\u0648\u06d5\u0633\u062a\u06cc.',
    '\u0647\u0627\u062a\u0648\u0686\u0648\u0648\u0634\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u0633\u0648\u0698\u062f\u06d5\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u062f\u0627 [\u0644\u06d5\u0628\u06d5\u0631 \u0686\u0627\u0648\u06cc\u06d5].',
    '\u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u0648\u0647\u06d5\u0648\u06ce \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u0648\u0648 \u06a9\u06d5\u0645 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u0646\u06cc \u062f\u06ce\u0646\u06d5 \u0644\u0627\u06cc \u06a9\u06ce\u061f',
    '\u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u062f\u0631\u06c6 \u0646\u06d5\u062e\u0634\u06cc\u0646\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631 \u062f\u0627\u062f\u06d5\u0628\u06d5\u0632\u0646.',
    '\u06af\u0648\u06ce \u062f\u06d5\u06af\u0631\u0646 \u0648 \u0632\u06c6\u0631\u06cc\u0627\u0646 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u0634\u0627\u0639\u06cc\u0631\u0627\u0646\u06cc\u0634\u060c \u06a9\u06ce \u0644\u06d5 \u062f\u0648\u0648\u06cc\u0627\u0646 \u062f\u06d5\u0686\u06ce \u06af\u0648\u0645\u0695\u0627\u0646.',
    '\u0645\u06d5\u0631 \u0646\u06d5\u062a \u062f\u06cc\u0648\u06d5 - \u0628\u06d5 \u0634\u06ce\u0648\u0627\u0648\u06cc - \u0633\u06d5\u0631 \u0628\u06d5 \u0647\u06d5\u0631 \u0634\u06cc\u0648\u06ce\u06a9\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0626\u06d5\u0648\u0627\u0646 \u0634\u062a\u06ce \u062f\u06d5\u0628\u06ce\u0698\u0646 \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06a9\u06d5\u0646.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646 \u0648 \u0632\u06c6\u0631 \u06cc\u0627\u062f\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0686\u06d5\u0648\u0633\u06ce\u0646\u0631\u0627\u0646\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0631 \u062a\u06c6\u06b5\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06d5\u0648\u06d5. \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u06cc\u0634 \u0632\u06c6\u0631 \u067e\u06ce \u0646\u0627\u0686\u06ce \u062f\u06d5\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u0646 \u06a9\u06d5 \u0633\u06d5\u0631\u06d5\u0646\u062c\u0627\u0645 \u06a9\u06ce\u0648\u06d5 \u062f\u06d5\u0686\u0646.',
    '\u062a\u0627\u060c \u0633\u06cc\u0646. \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0642\u0648\u0631\u0639\u0627\u0646\u06ce\u0646 \u0648 \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u0631\u06d5.',
    '\u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631\u06d5 \u0648 \u0645\u0698\u062f\u06d5\u062f\u06d5\u0631\u06d5 \u0628\u06c6 \u06af\u0631\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0648\u06ce\u0698\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u0631\u0646 \u0648 \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0648 \u062f\u0646\u06cc\u0627\u0646\u06d5 \u0628\u06ce\u060c \u06a9\u0631\u062f\u06d5\u06cc\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u0648 \u0695\u0627\u0632\u0627\u0646\u062f\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u0633\u06d5\u0631 \u0644\u06ce \u0634\u06ce\u0648\u0627\u0648\u06cc \u0645\u0627\u0648\u0646.',
    '\u0628\u06d5\u0634 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06cc \u0628\u06d5\u0698\u0627\u0646\u06d5 \u0648 \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627\u0634\u0627 \u0632\u06cc\u0627\u0646\u06cc \u0632\u06c6\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5.',
    '\u062a\u06c6 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u062a \u0647\u06d5\u0631 \u0644\u06d5 \u0644\u0627\u06cc \u06a9\u0627\u0631\u0632\u0627\u0646\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0632\u0627\u0646\u0627\u0648\u06d5\u060c \u067e\u06ce \u0641\u06ce\u0631 \u062f\u06d5\u06a9\u0631\u06ce.',
    '[\u0628\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06cc\u0631] \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634 \u06a9\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0628\u06d5 \u062e\u06ce\u0632\u0627\u0646\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0645\u0646 \u0626\u0627\u06af\u0631\u06ce\u06a9\u0645 \u0644\u06ce \u062f\u06cc\u0627\u0631\u06d5\u061b \u0626\u06d5\u0648\u0627 \u062f\u06d5\u0686\u0645 \u062e\u06d5\u0628\u06d5\u0631\u06ce\u06a9\u0648\u0648 \u0628\u06c6 \u062f\u06ce\u0646\u0645\u06d5\u0648\u06d5\u061b \u06cc\u0627\u0646 \u0628\u0632\u0648\u0648\u062a\u06ce\u06a9\u06cc \u0644\u06ce \u062f\u06ce\u0646\u0645\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u06af\u06d5\u0631\u0645\u062a\u0627\u0646 \u0628\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0644\u06ce\u06cc \u0646\u0632\u06cc\u06a9 \u06a9\u06d5\u0648\u062a\u06d5\u0648\u06d5\u060c \u0628\u0627\u0646\u06af \u06a9\u0631\u0627: \u067e\u06cc\u062a \u0648 \u067e\u06cc\u0631\u06c6\u0632\u06d5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5\u0648\u06ce\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648\u06cc \u0644\u06d5 \u062f\u06d5\u0648\u0631\u06d5\u06cc \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0648 \u067e\u0627\u06a9\u06cc \u0648 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u0628\u06c6 \u062e\u0648\u0627\u06cc\u06d5\u061b \u06a9\u06d5 \u062e\u0648\u062f\u0627\u0646\u06cc \u06af\u0634 \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627! \u0626\u06d5\u0648\u06d5 \u0645\u0646\u0645\u060c \u062e\u0648\u0627\u06cc \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646.',
    '\u0686\u06c6 \u062f\u06d5\u0633\u06d5\u06a9\u06d5\u0634\u062a \u0628\u0627\u0648\u06ce\u0698\u06d5! \u06a9\u0627\u062a\u06ce \u062f\u06cc\u062a\u06cc \u0647\u06d5\u0631\u0648\u06d5\u06a9\u0648\u0648 \u0645\u0627\u0631\u06ce\u06a9 \u0634\u06ce\u06b5\u06d5\u0647\u0627\u060c \u0647\u06d5\u06b5\u0627\u062a \u0628\u06ce \u0626\u0627\u0648\u0695\u062f\u0627\u0646\u06d5\u0648\u06d5. \u0645\u0648\u0648\u0633\u0627! \u062a\u0631\u0633\u062a \u0695\u06ce \u0646\u06d5\u0646\u06cc\u0634\u06ce\u060c \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0646\u0627\u0628\u06ce \u0644\u06d5 \u0644\u0627\u06cc \u0645\u0646 \u0628\u062a\u0631\u0633\u0646.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0648\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634 \u062e\u0631\u0627\u067e\u06d5\u06a9\u06d5\u06cc \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u06af\u06c6\u0695\u06cc\u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u062f\u06cc\u0627\u0631\u06d5 \u06a9\u06d5 \u0645\u0646 \u0644\u06ce\u0628\u0648\u0648\u0631\u062f\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u0645.',
    '\u062f\u06d5\u0633\u06cc\u0634\u062a \u0628\u062e\u06d5 \u0628\u0627\u062e\u06d5\u06b5\u062a [\u0648 \u062f\u06d5\u0631\u06cc\u062e\u06d5\u0648\u06d5]\u061b \u0628\u06d5 \u0633\u067e\u06cc\u06d5\u06a9\u06cc [\u0648\u06d5\u06a9 \u0686\u06c6\u0695\u06cc \u0634\u06cc\u0631] \u0628\u06d5 \u0628\u06ce \u0632\u06cc\u0627\u0646 \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u062f\u0627. \u0648\u06d5\u0644 \u0646\u06c6 \u0628\u06d5\u06b5\u06af\u06d5\u060c \u0628\u0686\u06c6 \u0628\u06c6 \u0644\u0627\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u062f\u0627\u0631 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06a9\u06d5\u06cc\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u0646.',
    '\u062c\u0627 \u0648\u06d5\u062e\u062a\u06ce\u06a9\u06cc\u0634 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0695\u0648\u0648\u0646 \u06a9\u06d5\u0631\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u06af\u06d5\u06cc\u06cc\u0634\u062a\u06d5 \u0644\u0627\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u062c\u0627\u062f\u0648\u0648\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u0644\u06d5 \u062f\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0628\u06d5 \u0695\u0627\u0633\u062a \u062f\u06d5\u0632\u0627\u0646\u06cc\u060c \u062e\u06c6\u0628\u06d5\u0632\u0644 \u0632\u0627\u0646\u06cc \u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc\u060c \u0628\u0648\u0648\u0646\u06d5 \u0647\u06c6\u06cc \u062d\u0627\u0634\u0627 \u06a9\u0631\u062f\u0646\u06cc\u0627\u0646. \u062c\u0627 \u0628\u0695\u0648\u0627\u0646\u06d5 \u067e\u0627\u0634\u06d5\u0695\u06c6\u0698\u06cc \u0628\u06d5\u062f\u0641\u06d5\u0695\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u06a9\u0648\u06ce \u06af\u06d5\u06cc\u0634\u062a!',
    '\u0647\u06d5\u0631 \u0626\u06ce\u0645\u06d5\u0634 \u0628\u0648\u0648\u06cc\u0646 \u06a9\u06d5 \u0632\u0627\u0646\u0633\u062a\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646 \u0648 \u062f\u0627\u0648\u0648\u062f. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0634\u0648\u06a9\u0648\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06ce \u0626\u06ce\u0645\u06d5\u06cc \u0644\u06d5 \u0686\u0627\u0648 \u0632\u06c6\u0631\u06ce \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u060c \u0628\u06d5\u0695\u06ce\u0632\u062a\u0631 \u06af\u0631\u062a.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u062f\u0627\u0648\u0648\u062f\u060c \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646 \u0628\u0648\u0648 \u0628\u06d5 \u062c\u06ce\u0646\u0634\u06cc\u0646. \u0628\u0627\u0646\u06af\u06d5\u0648\u0627\u0632\u06cc \u06a9\u0631\u062f: \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0626\u06ce\u0645\u06d5 \u0641\u06ce\u0631\u06cc \u0632\u0645\u0627\u0646\u06cc \u0645\u06d5\u0644\u0627\u0646 \u06a9\u0631\u0627\u0648\u06cc\u0646\u061b \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0627\u0648\u06cc\u0646\u061b \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0645\u06d5\u0634 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5.',
    '\u0633\u0648\u067e\u0627\u06cc \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646 - \u062a\u06ce\u06a9\u06d5\u06b5\u0627\u0648 \u0644\u06d5 \u062c\u0646\u062f\u06c6\u06a9\u06d5 \u0648 \u0645\u0631\u06c6 \u0648 \u0645\u06d5\u0644\u0627\u0646 - \u062f\u06d5\u0646\u06af \u062f\u0631\u0627\u0628\u0648\u0648\u0646\u060c \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5 \u0695\u06cc\u0632 \u062f\u06d5\u0631\u06c6\u06cc\u0634\u062a\u0646\u061b',
    '\u062a\u0627 \u06af\u06d5\u06cc\u0646\u06d5 \u0628\u0648\u0647\u0644\u06cc \u0645\u06ce\u0631\u0648\u0648\u0644\u0627\u0646. \u0645\u0631\u06c6\u0686\u06d5\u06cc\u06d5\u06a9 \u062f\u06d5\u0646\u06af\u06cc \u0645\u06ce\u0631\u0648\u0648\u0644\u06d5\u06a9\u0627\u0646\u06cc \u062f\u0627: \u0628\u0686\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0648 \u06a9\u0648\u0646\u06d5\u06a9\u0627\u0646\u0648\u0648! \u062a\u0627 \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646 \u0648 \u0644\u06d5\u0634\u06a9\u0631\u06cc - \u0628\u06d5 \u0628\u06ce \u0626\u06d5\u0648\u06d5 \u067e\u06ce \u0628\u062d\u06d5\u0633\u06ce\u0646 - \u062f\u06d5\u0628\u06d5\u0631 \u067e\u06ce\u06cc\u0627\u0646\u0648\u0648 \u0646\u06d5\u0628\u06d5\u0646.',
    '\u0626\u06cc\u062a\u0631 \u06a9\u06d5 \u0626\u06d5\u0648 \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc \u06af\u0648\u062a\u060c \u0628\u0632\u06d5\u06cc \u0647\u0627\u062a\u06ce. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0648\u0627\u0645 \u0695\u0627\u0628\u06ce\u0646\u06d5 \u0633\u0648\u067e\u0627\u0633\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u062a\u06c6 \u0628\u06a9\u06d5\u0645 \u06a9\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0645\u0646 \u0648 \u062f\u0627\u06a9 \u0648 \u0628\u0627\u0628\u0645\u062a \u06a9\u0631\u062f\u061b \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc \u0648\u0627\u0634\u0645 \u0628\u06ce\u060c \u067e\u06d5\u0633\u0646\u062f\u06cc \u06a9\u06d5\u06cc\u061b \u0647\u06d5\u0631 \u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0634\u062a\u06d5\u0648\u06d5 \u0628\u0645\u062e\u06d5 \u0695\u06ce\u0632\u06cc \u0639\u06d5\u0628\u062f\u06d5 \u06a9\u0627\u0631\u0686\u0627\u06a9\u06d5\u06a9\u0627\u0646\u062a\u06d5\u0648\u06d5.',
    '\u0686\u0627\u0648\u06cc \u0628\u06d5\u0645\u06d5\u0644\u0627\u0646\u062f\u0627 \u06af\u06ce\u0695\u0627\u060c \u06af\u0648\u062a\u06cc: \u0686\u06c6\u0646\u06d5 \u067e\u06d5\u067e\u0648\u0648\u0633\u06ce\u0645\u0627\u0646\u06d5 \u0646\u0627\u0628\u06cc\u0646\u0645\u060c \u0626\u0627\u062e\u06c6 \u062e\u06c6\u06cc \u062f\u0632\u06cc\u0648\u06d5\u062a\u06d5\u0648\u06d5\u061f',
    '\u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0645\u06d5 \u0626\u06d5\u0634\u06ce \u0633\u0632\u0627\u06cc \u0632\u06c6\u0631\u06cc \u0628\u062f\u06d5\u0645\u061b \u06cc\u0627\u0646 \u0645\u0644\u06cc \u0647\u06d5\u06b5\u062f\u06d5\u0642\u0631\u062a\u06ce\u0646\u0645\u060c \u06cc\u0627\u0646 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u06c6 \u0645\u0646 \u0628\u06ce\u0646\u06ce.',
    '\u0645\u0627\u0648\u06d5\u06cc\u06ce \u0632\u06c6\u0631\u06cc \u0646\u06d5\u062e\u0627\u06cc\u0627\u0646\u062f\u060c \u0647\u0627\u062a \u0648 \u0648\u062a\u06cc: \u0645\u0646 \u0634\u062a\u06ce\u06a9\u0645 \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u062a\u0648\u0648\u06d5 \u06a9\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u062a\u06c6 \u0646\u06d5\u06a9\u06d5\u0648\u062a\u0628\u0648\u0648\u061b \u0648\u0627 \u0645\u0646 \u0644\u06d5 \u0633\u06d5\u0628\u0627\u0648\u06d5 \u0647\u0627\u062a\u0648\u0648\u0645\u060c \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u06cc \u062f\u0631\u0648\u0633\u062a\u0645 \u0628\u06c6 \u0647\u06ce\u0646\u0627\u0648\u06cc.',
    '\u0645\u0646 \u0644\u06d5\u0648\u06ce \u0698\u0646\u06ce\u06a9\u0645 \u062f\u06cc\u0648\u06d5 \u067e\u0627\u0634\u0627\u06cc\u06d5 \u0628\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u062e\u0627\u0648\u06d5\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u06d5 \u0648 \u062a\u06d5\u062e\u062a\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u06af\u06d5\u0648\u0631\u06d5\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u062f\u06cc\u062a\u0645 \u06a9\u06d5 \u062e\u06c6\u06cc \u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc - \u0628\u06d5 \u062c\u06ce \u062e\u0648\u062f\u0627 - \u0633\u0648\u0698\u062f\u06d5 \u062f\u06d5\u0628\u06d5\u0646\u06d5 \u0628\u06d5\u0631\u062e\u06c6\u0631 \u0648 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06c6 \u062e\u06d5\u0645\u06b5\u0627\u0646\u062f\u0648\u0648\u0646 \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0647\u06d5\u06b5\u06d5\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0648\u0646 \u0648 \u0695\u06ce \u062f\u06d5\u0631\u0646\u0627\u06a9\u06d5\u0646.',
    '[\u0648\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5] \u0633\u0648\u0698\u062f\u06d5 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9 \u0646\u06d5\u0628\u06d5\u0646 \u06a9\u06d5 \u067e\u06ce\u0648\u0627\u0631\u06cc \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc \u062e\u06c6\u06cc\u0627 \u062f\u06d5\u06a9\u0627 \u0648\u060c \u0686\u06cc \u0648\u06d5\u06cc\u0634\u06ce\u0631\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u0628\u06a9\u06d5\u0646\u060c \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5\u0648 \u0628\u06d5\u062f\u06d5\u0631 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0628\u06ce \u0648 \u0626\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062a\u06d5\u062e\u062a\u06cc \u0647\u06d5\u0631\u06d5 \u0645\u06d5\u0632\u0646\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0695\u0648\u0627\u0646\u06cc\u0646 \u062a\u0627 \u0628\u0632\u0627\u0646\u06cc\u0646 \u0628\u06d5\u0695\u0627\u0633\u062a\u062a \u0628\u0648\u0648\u060c \u06cc\u0627\u0646 \u062a\u06c6\u0634 \u0647\u06d5\u0631 \u0644\u06d5 \u06a4\u0695 \u0628\u06ce\u0698\u0627\u0646\u06cc.',
    '\u0626\u06d5\u0645 \u0646\u0627\u0645\u06d5\u06cc\u06d5\u0645 \u0628\u06c6 \u0628\u06d5\u0631\u06d5 \u0648 \u0628\u06c6\u06cc\u0627\u0646 \u0628\u0627\u0648\u06ce\u0698\u06d5 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u062a\u06c6\u0632\u06ce\u06a9 \u062f\u0648\u0648\u0631\u06d5\u0648\u06d5\u06a9\u06d5\u0648\u06d5 \u0648 \u0628\u0646\u0648\u0627\u0695\u06d5 \u067e\u06d5\u0631\u0633\u06a4\u06cc\u0627\u0646 \u0686\u06cc\u06cc\u06d5\u061f',
    '\u0648\u06ce \u06af\u0648\u062a: \u0626\u06d5\u06cc \u067e\u06cc\u0627\u0648\u0645\u0627\u0642\u0648\u0648\u06b5\u06d5\u06a9\u0627\u0646! \u0646\u0627\u0645\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u0648\u06cc\u0634\u062a\u0648\u0648\u0645 \u0632\u06c6\u0631 \u06af\u0631\u06cc\u0646\u06af\u06d5\u061b',
    '\u06a9\u06d5 \u0644\u06d5 \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646\u06d5\u0648\u06d5\u06cc\u06d5 \u0648 \u0628\u06d5 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627\u06cc \u062f\u06d5\u0647\u0646\u062f\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0646\u0627\u0628\u06ce \u062e\u06c6\u062a\u0627\u0646 \u0644\u06d5 \u0645\u0646 \u0628\u06d5 \u0632\u0644\u062a\u0631 \u0628\u0632\u0627\u0646\u0646\u060c \u0648\u06d5\u0631\u0646\u06d5 \u0644\u0627\u0645 \u0648 \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u0648\u06d5 \u0628\u062f\u06d5\u0646.',
    '\u0648\u06ce \u06af\u0648\u062a: \u0626\u06d5\u06cc \u067e\u06cc\u0627\u0648\u0645\u0627\u0642\u0648\u0648\u06b5\u06d5\u06a9\u0627\u0646! \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0645\u060c \u0628\u06ce\u0698\u0646 \u0686 \u0628\u06a9\u06d5\u0645\u061f \u0647\u06d5\u062a\u0627 \u0626\u06ce\u0648\u06d5 \u06a9\u06c6 \u0646\u06d5\u0628\u0646\u06d5\u0648\u06d5\u060c \u0628\u0695\u06cc\u0627\u0631\u06cc \u0647\u06cc\u0686 \u06a9\u0627\u0631\u06ce \u0646\u0627\u062f\u06d5\u0645.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0645 \u0628\u06d5\u0647\u06ce\u0632\u06cc\u0646 \u0647\u06d5\u0645 \u0632\u06c6\u0631 \u0628\u0648\u06ce\u0631\u060c \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0633 \u062e\u06c6\u062a\u06d5\u060c \u062a\u06c6 \u0628\u06c6 \u062e\u06c6\u062a \u0628\u0695\u0648\u0627\u0646\u06d5 \u0686 \u0641\u06d5\u0631\u0645\u0627\u0646\u06ce\u06a9 \u062f\u06d5\u062f\u06d5\u06cc\u061f',
    '\u0648\u06ce \u06af\u0648\u062a: \u06af\u06d5\u0644\u06cc \u067e\u0627\u062f\u0634\u0627\u06cc\u0627\u0646 \u0628\u0686\u0646\u06d5 \u0646\u0627\u0648 \u0647\u06d5\u0631 \u0634\u0627\u0631\u06ce\u06a9\u06d5\u0648\u06d5\u060c \u062a\u06ce\u06a9\u06cc \u062f\u06d5\u062f\u06d5\u0646\u061b \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc - \u0647\u06d5\u0631 \u06a9\u0627\u0645 \u0628\u06d5 \u06af\u06d5\u0648\u0631\u06d5 \u0646\u0627\u0648\u06cc \u0647\u06d5\u0628\u06ce - \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u0646\u0646 \u0648 \u0626\u06d5\u0648 \u062a\u06d5\u0631\u0632\u06d5 \u06a9\u0627\u0631\u0627\u0646\u06d5 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0627 \u0645\u0646 \u062f\u06cc\u0627\u0631\u06cc\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0646\u06ce\u0631\u0645\u061b \u0686\u0627\u0648\u06d5\u0695\u06ce\u0645 \u06a9\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u0686\u06cc\u06cc\u06d5\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0647\u0627\u062a\u06d5 \u0644\u0627\u06cc \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646\u061b \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce \u0628\u06d5 \u0645\u0627\u06b5 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u0628\u06a9\u06d5\u0646\u061f \u0626\u06d5\u0648\u06cc \u062e\u0648\u062f\u0627 \u062f\u0627\u0648\u06cc\u06d5 \u0628\u06d5 \u0645\u0646\u060c \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u062f\u0627\u0648\u06d5 \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0648\u0627\u062f\u06cc\u0627\u0631\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u062f\u06cc\u0627\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06b5\u062e\u06c6\u0634\u0646.',
    '\u0648\u06d5\u06af\u06d5\u0695\u06ce \u0628\u0686\u06c6\u0648\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u0648\u0627 \u0645\u06d5 \u0628\u06d5 \u0644\u06d5\u0634\u06a9\u0631\u06ce\u06a9\u06d5\u0648\u06d5 \u062f\u06ce\u0646\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0631\u06c6\u0633\u062a\u06cc \u0646\u0627\u06cc\u06d5\u0646. \u0628\u06d5 \u0632\u06d5\u0644\u06cc\u0644\u06cc \u0648 \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u0634 \u0644\u06d5\u0648\u06ce\u0646\u062f\u06d5\u0631\u06ce \u062f\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062f\u06cc\u0648\u0627\u0646\u06ce! - \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0628\u06ce\u0646\u06d5 \u0626\u0627\u0645\u0627\u0646\u06ce - \u06a9\u06ce\u0648 \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u062a\u06d5\u062e\u062a\u06cc \u0626\u06d5\u0648 \u0698\u0646\u06d5\u0645 \u0628\u06c6 \u0628\u06ce\u0646\u06ce\u061f',
    '\u0644\u06c6\u0632\u0646\u062f\u06d5\u0631\u06ce\u06a9 \u0644\u06d5 \u062c\u0646\u06d5\u06a9\u0627\u0646 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0646 \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0644\u06d5 \u062c\u06ce\u06cc \u062e\u06c6\u062a \u0647\u06d5\u0633\u062a\u06cc \u0628\u06c6\u062a\u06cc \u062f\u06ce\u0646\u0645\u061b \u062a\u0648\u0627\u0646\u0627\u0645 \u0628\u06d5 \u0633\u06d5\u0631\u06cc\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u062f\u06d5\u0633\u067e\u0627\u06a9\u06cc\u0634\u0645.',
    '\u0626\u06d5\u0648\u06cc \u06a9\u06d5 \u0644\u06d5 \u06a9\u062a\u06ce\u0628 \u0634\u062a\u06ce\u06a9\u06cc \u062f\u06d5\u0632\u0627\u0646\u06cc\u060c \u0647\u06d5\u06b5\u06cc\u062f\u0627\u06cc\u06d5: \u0647\u06ce\u0646\u062f\u06d5\u06cc \u0686\u0627\u0648\u062a \u0628\u062a\u0631\u0648\u0648\u06a9\u06ce\u0646\u06cc \u0628\u06c6\u062a\u06cc \u062f\u06ce\u0646\u0645. \u06a9\u06d5 \u0644\u06ce \u0645\u06d5\u06cc\u0632\u0627\u0646\u062f\u060c \u0648\u0627 \u0644\u0627\u06cc\u06d5\u062a\u06cc \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u06d5\u0634 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646\u06d5\u060c \u0648\u06cc\u0633\u062a\u06cc \u062a\u0627\u0642\u06cc\u0645 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u0626\u0627\u062e\u06c6 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0628\u0698\u06ce\u0631\u0645 \u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0634\u0648\u06a9\u0631 \u0628\u06a9\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u06a9\u06ce\u0634 \u0633\u067e\u06b5\u06d5 \u0628\u06ce\u060c \u0626\u06d5\u0648\u0633\u0627\u06cc\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u0628\u06ce \u0646\u06cc\u0627\u0632 \u0648 \u062f\u06b5\u0627\u0648\u0627\u06cc\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u062a\u06d5\u062e\u062a\u06d5\u06a9\u06d5\u06cc \u0628\u06af\u06c6\u0695\u0646\u061b \u062a\u0627 \u0628\u0632\u0627\u0646\u06cc\u0646 \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0628\u06cc\u0646\u0627\u0633\u06ce\u062a\u06d5\u0648\u06d5 \u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u06cc \u0646\u0627\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u06a9\u0627\u062a\u06ce \u06af\u06d5\u06cc\u06cc\u0634\u062a\u06d5 \u0628\u06d5\u0631\u06d5\u0648\u06d5\u060c \u0648\u062a\u06cc\u0627\u0646: \u062a\u06d5\u062e\u062a\u06d5\u06a9\u06d5\u06cc \u062a\u06c6 \u0648\u0627\u0628\u0648\u0648\u061f \u06af\u0648\u062a\u06cc: \u0647\u06d5\u0631 \u0626\u06ce\u0698\u06cc \u062e\u06c6\u06cc\u06d5\u0633\u06cc\u061b \u0644\u06d5 \u0628\u06d5\u0631\u06cc\u0634\u06d5\u0648\u06d5 \u062f\u06d5\u062a\u0632\u0627\u0646\u06cc \u0648 \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u06af\u0648\u06ce \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u0648\u0648\u06cc\u0646.',
    '\u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0648\u06ce - \u063a\u06d5\u06cc\u0631\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5 - \u062f\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u060c \u0626\u06d5\u0648\u06cc [\u0644\u06d5\u0628\u0627\u0648\u06d5\u0695\u06cc \u0647\u06ce\u0646\u0627\u0646] \u06af\u06ce\u0695\u0627\u0628\u06c6\u0648\u06d5\u060c \u0686\u0648\u0646\u06a9\u06d5 \u067e\u06ce\u0634\u0648 \u0644\u06d5\u0695\u06ce\u0632\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u0648\u0648.',
    '\u067e\u06ce\u06cc \u0648\u062a\u0631\u0627: \u0648\u06d5\u0631\u06d5 \u0646\u0627\u0648 \u06a9\u06c6\u0634\u06a9. \u06a9\u06d5 \u0644\u06ce\u06cc \u0695\u0648\u0648\u0627\u0646\u06cc\u060c \u0647\u06d5\u0631 \u067e\u06ce\u06cc \u0648\u0627\u0628\u0648\u0648 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0648 \u0632\u06d5\u0628\u06d5\u0646\u062f\u06d5 \u0648 [\u062f\u0627\u0648\u06d5\u0633\u062a\u0627\u0648\u06d5 \u0648] \u0644\u0646\u06af\u06cc \u0647\u06d5\u06b5\u06a9\u0631\u062f. \u067e\u06ce\u06cc \u0648\u062a: \u0626\u06d5\u0645 \u06a9\u06c6\u0634\u06a9\u06d5 \u0644\u06d5 \u0634\u0648\u0648\u0634\u06d5\u06cc \u0695\u0648\u0648\u0646 \u0648 \u062d\u0648\u0644\u06cc \u062f\u0627\u0695\u0698\u0627\u0648\u06d5. \u06af\u0648\u062a\u06cc: \u062e\u0648\u062f\u0627\u0646\u06cc \u0698\u06cc\u0627\u0646\u0645! \u0645\u0646 \u062a\u0627 \u0626\u06ce\u0633\u062a\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0645 \u0644\u06d5 \u062e\u06c6\u0645 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0644\u06d5 \u0626\u06ce\u0633\u062a\u0627\u0648\u06d5 - \u0648\u06d5\u0644 \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646 - \u062e\u06c6\u0645 \u062f\u06d5\u062f\u06d5\u0645\u06d5 \u062f\u06d5\u0633 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u06af\u0634 \u062c\u06cc\u0647\u0627\u0646.',
    '\u0633\u0627\u06b5\u062d\u06cc\u0634\u0645\u0627\u0646 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5 \u0647\u06c6\u0632\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f\u061b \u06a9\u06d5 \u0647\u0627\u0648\u0695\u06d5\u06af\u06d5\u0632\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0648\u0648. \u06a9\u06d5: \u062f\u06d5\u0628\u06ce \u062e\u0648\u062f\u0627\u067e\u06d5\u0631\u0633\u062a \u0628\u0646. \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5 \u062f\u0648\u0648 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u062f\u0698\u06cc \u06cc\u06d5\u06a9.',
    '\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0686\u0627\u06a9\u06d5 \u0628\u0646 \u0628\u06c6 \u0628\u06d5\u0631\u06d5\u0648\u062e\u0631\u0627\u067e\u06d5 \u062f\u06d5\u0686\u0646\u061f \u0628\u06c6 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062f\u0627\u0648\u0627 \u0646\u0627\u06a9\u06d5\u0646 \u0639\u0627\u0641\u0648\u0648\u062a\u0627\u0646 \u06a9\u0627\u061f \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06ce\u062a\u0627\u0646\u062f\u0627 \u0628\u06ce.',
    '\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u062a\u06c6\u0634 \u0648 \u0626\u06d5\u0648\u06cc \u0644\u06d5\u06af\u06d5\u06b5 \u062a\u06c6\u0634\u0646\u060c \u0628\u06d5 \u0634\u0648\u0648\u0645 \u0648 \u0628\u06ce \u0641\u06d5\u0695 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646. \u06af\u0648\u062a\u06cc: \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u062f\u06d5\u0633 \u062e\u0648\u0627\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0648\u06d5 \u0647\u06c6\u0632\u06ce\u06a9\u0646 \u06a9\u06d5 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u06d5 \u0628\u06d5\u0631 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0648 \u0634\u0627\u0631\u06d5\u062f\u0627 \u0646\u06c6 \u062f\u06d5\u0633\u062a\u06d5\u06cc [\u0646\u06d5\u06af\u0648\u0646\u062c\u0627\u0648] \u0647\u06d5\u0628\u0648\u0648\u0646\u060c \u06a9\u06d5 \u0648\u06b5\u0627\u062a\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062a\u06ce\u06a9 \u062f\u06d5\u062f\u0627 \u0648 \u0628\u06c6 \u0644\u0627\u0628\u0631\u062f\u0646\u06cc \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u0647\u06cc\u0686 \u062a\u06d5\u0642\u0627\u0644\u0627\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u062f\u0627.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0648\u06d5 \u0633\u0648\u06ce\u0646\u062f \u0628\u062e\u06c6\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0645\u0634\u06d5\u0648 \u062e\u06c6\u06cc \u0648 \u0645\u0627\u06b5 \u0648 \u0645\u0646\u062f\u0627\u06b5\u06cc\u0634\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u062f\u06d5\u0628\u06d5\u06cc\u0646\u061b \u0628\u06d5 \u062e\u0648\u06ce\u0646\u06af\u0631\u0627\u0646\u06cc\u0634\u06cc \u0626\u06ce\u0698\u06cc\u0646: \u06a9\u06d5 \u0626\u06d5\u0648 \u062e\u06ce\u0632\u0627\u0646\u06d5 \u06a9\u0648\u0698\u0631\u0627\u0648\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0626\u0627\u06af\u0627\u0645\u0627\u0646 \u0644\u06ce \u0646\u06d5\u0628\u0648\u0648\u06d5 \u0648 \u0695\u0627\u0633\u062a\u06cc\u0634 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u062e\u06d5\u0631\u06cc\u06a9 \u0628\u0648\u0648\u0646 \u06a9\u06d5\u062a\u0646\u06ce \u0628\u06af\u06ce\u0695\u0646\u061b \u0626\u06ce\u0645\u06d5\u0634 \u06a9\u06d5\u062a\u0646\u06cc \u062e\u06c6\u0646 \u067e\u06ce \u06a9\u0631\u062f\u0646\u060c \u0628\u06ce \u0626\u06d5\u0648\u06d5 \u0647\u06d5\u0633\u062a\u06cc \u067e\u06ce\u0628\u06a9\u06d5\u0646.',
    '\u0633\u0627 \u0628\u0646\u06c6\u0695\u06d5\u060c \u0633\u06d5\u0631\u06d5\u0646\u062c\u0627\u0645\u06cc \u06a9\u06d5\u062a\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u06a9\u0648\u06ce \u06af\u06d5\u06cc\u0634\u062a: \u0628\u06d5\u0648\u0627\u0646\u06d5\u0648\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u0628\u0631\u062f.',
    '\u0626\u06ce\u0633\u062a\u0627 [\u062c\u06ce] \u0645\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc \u0648\u0627\u0646 - \u0644\u06d5\u0633\u06d5\u0631 \u0646\u0627\u0647\u06d5\u0642\u06cc \u06a9\u0631\u062f\u0646\u06cc\u0627\u0646 - \u0686\u06c6\u0644 \u0648 \u0647\u06c6\u06b5\u06d5 \u0648 \u062f\u06d5\u0631\u0633\u06ce\u06a9\u06cc\u0634\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f \u06a9\u06d5 \u0628\u0628\u0648\u0648\u0646\u06d5 \u062e\u0648\u062f\u0627\u0646\u0627\u0633 \u0648 \u0644\u06ce\u06cc \u062f\u06d5\u062a\u0631\u0633\u0627\u0646.',
    '\u0644\u0648\u0648\u062a\u06cc\u0634\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06cc\u0631 \u0628\u062e\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0648\u0627 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0648\u062a: \u0626\u06ce\u0648\u06d5 \u0686\u0644\u06c6\u0646 \u06a9\u0627\u0631\u06cc \u0628\u06ce \u0646\u0627\u0645\u0648\u0648\u0633\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06a9\u06d5\u0686\u06cc \u062f\u06d5\u0634\u0632\u0627\u0646\u0646 \u062e\u0631\u0627\u067e\u06d5\u061f',
    '\u0644\u06d5 \u0698\u0646\u0627\u0646 \u0648\u0627\u0632\u0648\u0648 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u067e\u06d5\u0644\u0627\u0645\u0627\u0631\u06cc \u067e\u06cc\u0627\u0648\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646\u061b \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0648\u06d5 \u0632\u06c6\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u062a\u06d5\u0646\u06cc\u0627 \u0648\u06d5\u0631\u0627\u0645\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5 \u0628\u0648\u0648: \u062e\u06ce\u0632\u0627\u0646\u06cc \u0644\u0648\u0648\u062a \u0644\u06d5 \u0634\u0627\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0631\u0628\u06a9\u06d5\u0646\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0686\u0627\u0648\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u0627\u0648\u06ce\u0646 \u067e\u0627\u06a9\u06cc\u06cc\u06d5.',
    '\u0633\u0627 \u0626\u06d5\u0648\u06cc\u0634 \u0648 \u062e\u06ce\u0632\u0627\u0646\u06cc\u0634\u06cc\u0645\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f\u061b \u0645\u06d5\u0631 \u0698\u0646\u06d5\u06a9\u06d5\u06cc\u060c \u06a9\u06d5 \u0628\u0695\u06cc\u0627\u0631\u0645\u0627\u0646 \u062f\u0627 \u0628\u0645\u06ce\u0646\u06ce.',
    '\u0628\u0627\u0631\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0628\u0627\u0631\u0627\u0646\u062f\u0646 \u0626\u06d5\u0645\u0645\u0627 \u0628\u0627\u0631\u0627\u0646\u061b \u0628\u0627\u0631\u0627\u0646\u06cc \u062a\u0631\u0633\u06ce\u0646\u0631\u0627\u0648\u06d5\u06a9\u0627\u0646 \u0686\u06d5\u0646 \u062e\u0631\u0627 \u0628\u0648\u0648!',
    '\u0628\u06ce\u0698\u06d5: \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0648 \u062f\u0631\u0648\u0648\u062f \u0644\u06d5\u0633\u06d5\u0631 \u0639\u06d5\u0628\u062f\u06d5 \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u06d5\u06a9\u0627\u0646\u06cc. \u0626\u0627\u062e\u06c6 \u062e\u0648\u062f\u0627 \u0686\u0627\u062a\u0631\u06d5 \u06cc\u0627 \u0626\u06d5\u0648\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u061f',
    '\u06cc\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0686\u0627\u062a\u0631\u06d5 \u06a9\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0626\u0627\u0648\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u061f \u06af\u06d5\u0644\u06ce\u06a9 \u0628\u0627\u062e\u0686\u06d5\u06cc \u0628\u06d5 \u062f\u06cc\u0645\u06d5\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0648\u06d5\u06a9\u06d5\u06cc \u0634\u06cc\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u06a9\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0646\u06d5\u062a\u0627\u0646 \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0628\u06ce\u0646\u0646\u061b \u0626\u0627\u062e\u06c6 \u0647\u06cc\u0686 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06ce\u06a9\u06cc \u0648\u06d5\u0644 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5\u061f \u0646\u06d5\u062e\u06ce\u0631 \u062f\u06cc\u0627\u0631\u06d5 \u06af\u06d5\u0644\u06ce\u06a9\u06cc \u0644\u06d5 \u0647\u06d5\u0642 \u0644\u0627\u062f\u06d5\u0631\u0646.',
    '\u06cc\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0686\u0627\u062a\u0631\u06d5 \u06a9\u06d5 \u0632\u06d5\u0648\u06cc \u06a9\u0631\u062f\u06d5 \u062c\u06ce\u06cc \u0633\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u06af\u0634 \u062c\u06c6\u0628\u0627\u0631 \u0648 \u0695\u0648\u0648\u0628\u0627\u0631\u06d5\u06cc \u062a\u06ce\u062f\u0627 \u06a9\u06ce\u0634\u0627 \u0648 \u06a9\u06d5\u0698 \u0648 \u06a9\u06c6\u06cc \u0644\u06ce \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627 \u0648 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u062f\u0648\u0648 \u062f\u06d5\u0631\u06cc\u0627\u062f\u0627 \u0628\u06d5\u0631\u0632\u06d5\u06a9\u06d5\u06cc \u062f\u0627\u0646\u0627\u061f \u0626\u0627\u062e\u06c6 \u0647\u06cc\u0686 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06ce\u06a9\u06cc \u0648\u06d5\u0644 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5\u061f \u0646\u06d5\u062e\u06ce\u0631 \u062f\u06cc\u0627\u0631\u06d5 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u06cc\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0686\u0627\u062a\u0631\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06ce \u0647\u0648\u0645\u06ce\u062f \u0628\u0695\u0627\u0648\u06ce\u06a9 \u0644\u06d5 \u0628\u06d5\u0631\u06cc \u0628\u067e\u0627\u0695\u06ce\u062a\u06d5\u0648\u06d5\u060c \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc \u062f\u06ce \u0648 \u0644\u06d5 \u0686\u06c6\u0631\u062a\u0645 \u0695\u0632\u06af\u0627\u0631 [\u06cc] \u062f\u06d5\u06a9\u0627 \u0648 \u062f\u06d5\u062a\u0627\u0646\u06a9\u0627\u062a\u06d5 \u062c\u06ce\u0646\u0634\u06cc\u0646 \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5\u061f \u0626\u0627\u062e\u06c6 \u0647\u06cc\u0686 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06ce\u06a9\u06cc \u0648\u06d5\u0644 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5\u061f \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06a9\u06d5\u0645 \u067e\u06d5\u0646\u062f \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u0646.',
    '\u06cc\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0686\u0627\u062a\u0631\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u06cc \u0628\u06d5\u0698 \u0648 \u062f\u06d5\u0631\u06cc\u0627\u060c \u0644\u06d5 \u0695\u06ce \u0634\u0627\u0631\u06d5\u0632\u0627\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0626\u06d5\u0648 \u0628\u0627\u06cc\u0627\u0646\u06d5 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0645\u0632\u06af\u06ce\u0646\u06cc \u062e\u06ce\u0631\u0648\u0628\u06ce\u0631\u06cc \u0626\u06d5\u0648\u06cc\u0627\u0646 \u067e\u06ce\u06cc\u06d5\u061f \u0626\u0627\u062e\u06c6 \u0647\u06cc\u0686 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06ce\u06a9\u06cc \u0648\u06d5\u0644 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5\u061f \u062e\u0648\u0627 \u0632\u06c6\u0631 \u0644\u06d5\u0648\u06d5\u06cc \u0628\u06d5\u0631\u0632\u062a\u0631\u06d5 \u06a9\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0626\u06d5\u0648.',
    '\u06cc\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0686\u0627\u062a\u0631\u06d5 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u0631 \u0648\u06d5\u062f\u06cc \u062f\u06ce\u0646\u06ce\u062a \u0648 \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u062f\u0627 \u062f\u06d5\u06cc\u0628\u0627\u062a\u06d5\u0648\u06d5\u061f \u0626\u06d5\u062f\u06cc \u06a9\u06ce\u06cc\u06d5 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0648\u06d5 \u0628\u0698\u06cc\u0648\u062a\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627\u061f \u0626\u0627\u062e\u06c6 \u0647\u06cc\u0686 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06ce\u06a9\u06cc \u0648\u06d5\u0644 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5\u061f \u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06ce\u0648 \u0648\u0627\u06cc\u06d5 \u0695\u0627\u0633\u062a\u0646\u060c \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0647\u06d5\u0646 - \u0644\u06d5 \u062e\u0648\u0627 \u0628\u06d5\u062f\u06d5\u0631 - \u0644\u06d5\u0628\u06d5\u0631 \u067e\u06ce\u0648\u0627\u0631 \u0628\u06ce \u062e\u06d5\u0628\u06d5\u0631\u0646. \u0647\u06d5\u0633\u062a\u06cc\u0634 \u0646\u0627\u06a9\u06d5\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0632\u0627\u0646\u0633\u062a\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u0695 \u0646\u0627\u06a9\u0627 \u0633\u06d5\u06b5\u0627 \u0695\u0627\u0628\u06ce\u060c \u062f\u0648\u0648\u062f\u06b5\u0646 \u062f\u06d5\u0628\u06ce \u06cc\u0627\u0646 \u0646\u0627\u0628\u06ce\u061f \u0626\u06d5\u0645 \u0686\u0627\u0648\u06cc \u062f\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u0648\u06d5\u062e\u062a\u06ce \u0626\u06ce\u0645\u06d5 \u0648 \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u0627\u0646\u0645\u0627\u0646 \u0628\u0648\u0648\u06cc\u0646\u06d5 \u062e\u06c6\u06b5\u06ce\u06a9 [\u0628\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc\u06cc \u0644\u06d5 \u06af\u06c6\u0695] \u062f\u06d5\u0645\u0627\u0646\u0647\u06ce\u0646\u0646\u06d5 \u062f\u06d5\u0631\u06ce\u061f',
    '\u0626\u06d5\u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u06cc \u0628\u06d5\u0645\u06d5\u06cc \u062f\u06d5\u062f\u06d5\u0646\u060c \u0644\u06d5\u0648\u06d5\u067e\u06ce\u0634\u06cc\u0634 \u0628\u06d5 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0646 \u062f\u0631\u0627\u0648\u06d5\u061b \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06af\u0634 \u0686\u06cc\u0631\u06c6\u06a9\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u06af\u06d5\u0634\u062a\u06ce \u0628\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u0628\u06a9\u06d5\u0646\u060c \u062a\u0627 \u0628\u0628\u06cc\u0646\u0646 \u0633\u06d5\u0631\u06d5\u0646\u062c\u0627\u0645\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0628\u06d5 \u06a9\u0648\u06ce \u06af\u06d5\u06cc\u0648\u06d5.',
    '\u062e\u06d5\u0645\u06cc\u0627\u0646 \u0645\u06d5\u062e\u0648\u06d5 \u0648 \u0644\u06d5\u0628\u06d5\u0631 \u06af\u0632\u06cc\u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06b5\u062a\u06d5\u0646\u06af \u0645\u06d5\u0628\u06d5!',
    '\u0626\u06ce\u0698\u0646: \u062f\u06d5\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u062a\u0646\u060c \u0626\u06d5\u0645 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u0648 \u06a9\u06d5\u06cc \u062f\u06ce\u062a\u06d5 \u062c\u06ce\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0695\u06d5\u0646\u06af\u06d5 \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u06cc - \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u067e\u06d5\u0644\u06d5\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646 - \u0632\u06c6\u0631 \u0644\u06ce\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0646\u0632\u06cc\u06a9 \u0628\u06ce.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0686\u0627\u06a9\u06d5\u06cc \u0632\u06c6\u0631\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5\u0648\u06d5 \u0647\u06d5\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0634\u06a9\u0648\u0631\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u0644\u06d5 \u062f\u06b5\u06cc\u0627\u0646\u062f\u0627 \u0648 \u0646\u0647\u06ce\u0646\u06cc\u06d5 \u0648 \u0644\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u062f\u06d5\u06cc\u062f\u0631\u06a9\u06ce\u0646\u0646\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0647\u06d5\u0631 \u067e\u06ce\u0648\u0627\u0631\u06ce \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5 - \u06af\u0634 \u0644\u06d5 \u06af\u0634\u062a\u06cc - \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc \u0695\u0648\u0648\u0646 \u06a9\u06d5\u0631\u06d5\u0648\u06d5\u062f\u0627 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0628\u06c6 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644\u06ce - \u0644\u06d5\u0645\u06d5\u0695 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u0647\u06d5\u0631\u0627\u06cc\u0627\u0646\u06d5 - \u0642\u0633\u06d5 \u062f\u06d5\u06a9\u0627.',
    '\u0628\u06ce \u0633\u06c6 \u0628\u06c6 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u060c \u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u06d5.',
    '\u0628\u06ce\u06af\u0648\u0645\u0627\u0646\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6\u06cc\u060c \u0628\u06d5 \u0646\u06ce\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0627\u062f\u06d5\u06af\u0627. \u06a9\u06c6\u06b5 \u0646\u06d5\u062f\u06d5\u0631 \u0648 \u0647\u06d5\u0631\u06d5 \u0632\u0627\u0646\u0627\u0634 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u062f\u06d5\u0633\u0627 \u062e\u06c6\u062a \u0628\u06d5 \u062e\u0648\u0627 \u0628\u0633\u067e\u06ce\u0631\u06d5\u061b \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u062a\u06c6 \u0648\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u062d\u06d5\u0642\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc.',
    '\u0628\u0627\u0646\u06af\u06d5\u0648\u0627\u0632\u062a \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0695\u0648\u0648 \u0644\u06d5 \u062a\u06c6 \u0648\u06d5\u0631\u062f\u06d5\u06af\u06ce\u0695\u0646\u060c \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u0645\u0631\u062f\u0648\u0648\u06cc \u06af\u06c6\u0695\u0633\u062a\u0627\u0646 \u0628\u062f\u0648\u06ce\u0646\u06cc\u061b \u06cc\u0627\u0646 \u0628\u06d5 \u06af\u0648\u06ce\u06cc \u06a9\u06d5\u0695\u062f\u0627 \u0628\u062e\u0648\u06ce\u0646\u06cc\u061b \u06a9\u06d5 \u0644\u06d5 \u0648\u0632\u06d5\u06cc \u062a\u06c6\u062f\u0627 \u0646\u06cc\u06cc\u06d5.',
    '\u062a\u06c6 \u0646\u0627\u062a\u06a9\u0631\u06ce \u06a9\u0648\u06ce\u0631\u0627\u0646 \u0644\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u06a9\u06d5\u06cc \u0648 \u0628\u06cc\u0627\u0646\u062e\u06d5\u06cc\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5. \u062a\u06c6 \u0647\u06d5\u0631 \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u062f\u06d5\u0646\u06af\u06cc \u062e\u06c6\u062a \u0628\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06af\u06d5\u06cc\u06d5\u0646\u06cc \u06a9\u06d5 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0628\u0695\u0648\u0627\u0646 \u0648 \u0645\u0644\u06a9\u06d5\u0686\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u0646.',
    '\u0647\u06d5\u0631 \u062f\u06d5\u0645\u06ce\u06a9\u06cc\u0634 \u0628\u06d5\u06b5\u06ce\u0646 [\u0645\u0627\u0646] \u0628\u06c6\u06cc\u0627\u0646 \u0647\u0627\u062a\u06d5 \u062c\u06ce\u060c \u062f\u06d5\u062d\u0628\u0627\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0628\u06c6 \u062f\u06d5\u0631\u062f\u06d5\u062e\u06d5\u06cc\u0646\u060c \u06a9\u06d5 \u062f\u06d5\u06af\u06b5\u06cc\u0627\u0646 \u0642\u0633\u06d5 \u062f\u06d5\u06a9\u0627 [\u0648 \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u06ce:] \u0645\u06d5\u0631\u062f\u0645 \u0628\u06d5 \u062f\u06b5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0633\u06d5\u0644\u0645\u0627\u0646\u062f.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0645\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0647\u06d5\u0631 \u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9\u062f\u0627\u060c \u062a\u0627\u0642\u0645\u06ce\u06a9 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0648\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u0645\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0647\u06ce\u0646\u0627 \u06a9\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u060c \u062c\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5 \u0695\u06cc\u0632 \u062f\u06d5\u06a9\u0631\u06ce\u0646 \u0648 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0631\u06ce\u0646.',
    '\u0647\u06d5\u062a\u0627 \u062f\u06d5\u06af\u06d5\u0646\u06d5 \u0628\u06d5\u0631\u06d5\u0648\u06d5\u060c \u0626\u06ce\u0698\u06ce: \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u062a\u0627\u0646 - \u0628\u06ce \u0626\u06d5\u0648\u06d5 \u0686\u06cc\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0632\u0627\u0646\u0646 - \u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0627\u0646\u06cc\u061f \u0695\u0627\u0633\u062a\u06cc \u0626\u06ce\u0648\u06d5 \u0686\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u061f',
    '\u0647\u06d5\u0631 \u0628\u06d5 \u0633\u0632\u0627\u06cc \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631 \u0628\u0648\u0648\u0646\u060c \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u06a9\u06d5 \u06a9\u06d5\u0648\u062a\u0646\u060c \u0647\u06d5\u0631 \u0645\u062a\u06d5\u0642\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u06cc\u06d5.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u0634\u06d5\u0648\u0645\u0627\u0646 \u062f\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646\u06cc \u062a\u06ce\u062f\u0627 \u0648\u06d5\u062d\u06d5\u0633\u06ce\u0646\u061b \u0695\u06c6\u0698\u06cc\u0634 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0628\u0628\u06cc\u0646\u0646\u061f \u0644\u06d5\u0645\u06d5\u0634\u062f\u0627 \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0647\u06d5\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627 \u062f\u06ce\u0646\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u062f\u06d5\u06a9\u06d5\u06b5\u06d5\u0634\u0627\u062e \u062f\u06d5\u0645\u06ce\u0646\u062f\u0631\u06ce\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5 - \u06af\u0644\u0627 - \u062a\u0631\u0633\u06cc\u0627\u0646 \u0695\u06ce \u062f\u06d5\u0646\u06cc\u0634\u06ce \u0648 \u0628\u06d5 \u0632\u06d5\u0644\u06cc\u0644\u06cc \u062f\u06ce\u0646\u06d5 \u0628\u06d5\u0631 \u0628\u0627\u0631\u06d5\u06af\u0627\u06cc \u062e\u0648\u062f\u0627\u061b \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0628\u06d5\u0631 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0648\u06ce.',
    '\u06a9\u06ce\u0648\u06cc\u0634\u062a \u0648\u0627 \u062f\u06ce\u0646\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u060c \u06a9\u06d5 \u0647\u06d5 \u0631\u0646\u0627\u0628\u0632\u0648\u0648\u0646\u061b \u06a9\u06d5\u0686\u06cc \u0648\u06d5\u06a9 \u0695\u06d5\u0648\u06cc\u0646\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0648\u0631 \u062f\u06d5\u0695\u06d5\u0648\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0645\u06d5\u0634 \u06a9\u0631\u06cc \u062e\u0648\u062f\u0627\u06cc\u06ce\u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u0632\u06c6\u0631 \u0628\u06d5 \u0686\u0627\u06a9\u06cc \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0644\u06d5 \u06a9\u0631\u062f\u06d5\u062a\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9 \u0628\u06a9\u0627\u060c \u0644\u06d5\u0648 \u0686\u0627\u062a\u0631\u06cc \u062f\u06ce\u062a\u06d5\u0648\u06d5 \u0695\u06ce \u0648 \u0644\u06d5\u0645\u06d5 \u062a\u0631\u0633\u06cc \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0626\u0627\u0631\u062e\u06d5\u06cc\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e\u06cc \u0644\u06ce \u0695\u0648\u0648 \u062f\u0627\u060c \u0644\u06d5\u067e\u06d5\u0648\u0695\u0648\u0648 \u062f\u06d5\u062e\u0631\u06ce\u062a\u06d5 \u0626\u0627\u06af\u0631. \u0626\u0627\u062e\u06c6 \u0632\u06cc\u0627\u062a\u0631 \u0644\u06d5 \u0642\u06d5\u0631\u06d5\u0628\u0648\u0648\u06cc \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u0648\u0648 \u0628\u06c6 \u062f\u06d5\u0628\u0695\u06ce\u062a\u06d5\u0648\u06d5\u061f',
    '\u0628\u06d5 \u0645\u0646 \u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646 \u062f\u0631\u0627\u0648\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0628\u0627\u0698\u06ce\u0695\u06d5 - \u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u06cc \u0695\u06ce\u0632\u06cc \u0644\u06ce \u0646\u0627\u0648\u06d5 - \u0628\u067e\u06d5\u0631\u0633\u062a\u0645. \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u06cc\u0634 \u0644\u06d5\u0648\u0695\u0627\u06cc\u06d5 \u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634\u0645 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u0627\u0646 \u0628\u0645\u061b',
    '\u0647\u06d5\u0631\u0648\u0627\u060c \u0642\u0648\u0695\u0639\u0627\u0646\u06cc\u0634 \u0628\u062e\u0648\u06ce\u0646\u0645. \u062c\u0627 \u06a9\u06ce \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce\u06af\u06d5 \u0628\u0648\u0648\u060c \u0647\u06d5\u0631 \u0628\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0634 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u06a9\u0644\u0627 \u0628\u0648\u0648\u060c \u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce\u06a9\u0645 \u0644\u06d5 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u0627\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0633\u0648\u067e\u0627\u0633 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u0626\u06d5\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u062a\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u0646\u0627\u0633\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646\u06d5.',
    '\u062a\u0627\u060c \u0633\u06cc\u0646\u060c \u0645\u06cc\u0645.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u06a9\u062a\u06ce\u0628\u06cc \u0695\u06c6\u0634\u0646\u06a9\u06d5\u0631\u06d5\u0648\u06d5\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u062a\u06c6\u060c \u0628\u06c6 [\u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc] \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627 \u062f\u06ce\u0646\u0646\u060c \u0628\u0627\u0633\u06cc \u0645\u0648\u0648\u0633\u0627 \u0648 \u0641\u06cc\u0695\u0639\u06d5\u0648\u0646 \u062f\u06d5\u062e\u0648\u06ce\u0646\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0628\u06ce \u0633\u06c6 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0644\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u0627 \u0628\u0627\u06b5\u0627\u062f\u06d5\u0633 \u0628\u0648\u0648\u061b \u062e\u06d5\u06b5\u06a9\u06cc \u0626\u06d5\u0648\u06ce \u06a9\u0631\u062f\u0628\u0648\u0648\u0646\u06d5 \u0686\u06d5\u0646\u062f \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u061b \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc \u0647\u06d5\u0631 \u0632\u06d5\u0628\u0648\u0648\u0646 \u062f\u06d5\u06a9\u0631\u062f\u060c \u06a9\u0648\u0695\u06d5\u06a9\u0627\u0646\u06cc \u0633\u06d5\u0631 \u062f\u06d5\u0628\u0695\u06cc\u0646 \u0648 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0695\u0627\u062f\u06d5\u06af\u0631\u062a\u0646. \u0644\u06d5 \u0628\u06d5\u062f\u0641\u06d5\u0695 \u0648 \u0628\u06d5\u0632\u06d5\u0695\u0627\u0646 \u0628\u0648\u0648.',
    '\u0628\u0695\u06cc\u0627\u0631\u0646 \u062f\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u0632\u06d5\u0628\u0648\u0648\u0646 \u06a9\u0631\u0627\u0646\u060c \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0648\u06d5\u06cc\u0627\u0646 \u062e\u0648\u06ce\u0646\u06cc\u0646 \u0648 \u0628\u06cc\u0627\u0646\u06a9\u06d5\u06cc\u0646\u06d5 \u067e\u06ce\u0634\u06d5\u0648\u0627 [\u06cc \u062e\u06d5\u06b5\u06a9]\u061b \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u0634\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0645\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0648 \u0639\u06d5\u0631\u0632\u06d5\u062f\u0627 \u062c\u06ce\u06af\u0627\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u0634 \u0628\u06a9\u06d5\u06cc\u0646 \u0648 \u0628\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0647\u0627\u0645\u0627\u0646 \u0648 \u0644\u06d5\u0634\u06a9\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u06cc\u0646\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u0627\u0646.',
    '\u0628\u06d5 \u062f\u0627\u06cc\u06d5 \u0645\u0648\u0648\u0633\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f: \u0634\u06cc\u0631\u06cc \u062f\u06d5\u06cc\u06d5 \u0648\u060c \u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06ce \u0644\u06ce\u06cc \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646 \u0628\u0648\u0648\u06cc\u060c \u0628\u06cc\u0647\u0627\u0648\u06ce\u0698\u06d5 \u0646\u0627\u0648 \u062f\u06d5\u0631\u06cc\u0627\u0648\u06d5 \u0648 \u0647\u06cc\u0686 \u0645\u06d5\u062a\u0631\u0633\u06d5 \u0648 \u062e\u06d5\u0645\u06cc\u0634 \u0645\u06d5\u062e\u06c6\u061b \u0626\u06ce\u0645\u06d5 \u0628\u06c6\u062a\u06cc \u062f\u06ce\u0646\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0634\u06cc\u06a9\u06d5\u06cc\u0646\u06d5 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646.',
    '\u062f\u06d5\u0633\u062a\u06d5\u0648\u0628\u06d5\u0633\u062a\u06d5\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0626\u06d5\u0648\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06af\u0631\u062a\u06d5\u0648\u06d5\u060c \u062a\u0627 \u0628\u0628\u06ce\u062a\u06d5 \u062f\u0648\u0698\u0645\u0646\u06cc\u0627\u0646 \u0648 \u0647\u06c6\u06cc \u0645\u06d5\u06cc\u0646\u06d5\u062a\u06cc\u0627\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0647\u0627\u0645\u0627\u0646 \u0648 \u062f\u06d5\u0633 \u0648 \u067e\u06ce\u0648\u06d5\u0646\u062f\u06d5\u06a9\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u06d5\u06b5\u06d5\u062a \u0686\u0648\u0648\u0646.',
    '\u067e\u06cc\u0631\u06d5\u06a9\u0627 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u06af\u0648\u062a\u06cc: \u0628\u06c6 \u0645\u0646 \u0648 \u062a\u06c6 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0645\u0627\u06cc\u06d5\u06cc \u0686\u0627\u0648\u0695\u0648\u0648\u0646\u06cc\u061b \u0648\u0627\u0632 \u0644\u06d5 \u06a9\u0648\u0634\u062a\u0646\u06cc \u0628\u0647\u06ce\u0646\u0646\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0633\u0648\u0648\u062f\u06cc \u0628\u06c6\u0645\u0627\u0646 \u0647\u06d5\u0628\u06ce\u060c \u06cc\u0627\u0646 \u0628\u06cc\u06a9\u06d5\u06cc\u0646\u06d5 \u06a9\u0648\u0695\u06cc \u062e\u06c6\u0645\u0627\u0646. \u06a9\u06d5\u0686\u06cc \u0647\u06cc\u0686 \u0626\u0627\u06af\u0627\u0634\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648.',
    '\u0626\u06cc\u062a\u0631 \u0626\u06d5\u0648\u0633\u0627 \u062f\u0627\u06cc\u06a9\u06cc \u0645\u0648\u0648\u0633\u0627 \u0647\u06cc\u0686 \u0647\u06c6\u0634\u06cc \u0644\u06d5 \u062f\u06b5\u062f\u0627 \u0646\u06d5\u0645\u0627 \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0648\u0648 \u06a9\u06d5 \u0626\u06d5\u0648 \u0695\u0627\u0632\u06d5 \u0628\u062f\u0631\u06a9\u06ce\u0646\u06ce\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u0626\u06ce\u0645\u06d5\u0634 \u062f\u06b5\u06cc \u0626\u06d5\u0648\u0645\u0627\u0646 \u0648\u0627 \u067e\u062a\u06d5\u0648 \u06a9\u0631\u062f \u062a\u0627 \u0644\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0628\u06ce.',
    '\u0628\u06d5 \u062e\u06c6\u0634\u06a9\u06cc \u06af\u0648\u062a: \u0634\u0648\u06ce\u0646\u06cc \u06a9\u06d5\u0648\u06d5! \u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0633\u062a\u06cc \u067e\u06ce \u0628\u06a9\u06d5\u0646\u060c \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u06a9\u0631\u062f \u062f\u0648\u0648\u0631\u0627\u0648\u062f\u0648\u0648\u0631 \u0686\u0627\u0648\u06cc \u067e\u06ce \u0628\u06a9\u06d5\u0648\u06ce.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u0648\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0646\u0627 \u0645\u06d5\u0645\u06a9\u06cc \u0647\u06cc\u0686 \u062a\u0627\u06cc\u06d5\u0646\u06ce\u06a9 \u0646\u06d5\u0645\u0698\u06ce. \u0648\u06ce \u06af\u06c6: \u0626\u0627\u062e\u06c6 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce \u0645\u0627\u06b5\u06ce\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u0628\u062f\u06c6\u0632\u0645\u06d5\u0648\u06d5 \u06a9\u06d5 \u0628\u06c6\u062a\u0627\u0646\u06cc \u0628\u06d5\u062e\u06ce\u0648 \u0628\u06a9\u0627 \u0648 \u0632\u06c6\u0631 \u0628\u0627\u0634\u06cc\u0634\u06cc \u067e\u06ce \u0695\u0627\u0628\u06af\u0627\u061f',
    '\u0646\u0627\u0631\u062f\u0645\u0627\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062f\u0627\u06cc\u06a9\u06cc\u060c \u062a\u0627 \u0686\u0627\u0648\u06cc \u067e\u06ce \u0695\u0648\u0648\u0646 \u0628\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u062e\u06d5\u0645\u06ce \u0646\u06d5\u062e\u0648\u0627\u061b \u062a\u0627 \u0628\u0634\u0632\u0627\u0646\u06ce \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627 \u0695\u0627\u0633\u062a\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0648\u06d5\u062e\u062a\u06ce \u06af\u06d5\u06cc\u06cc\u06d5 \u062a\u0627\u0641\u06cc \u062c\u0648\u0627\u0646\u06cc \u0648 \u067e\u06cc\u0627\u0648\u06ce \u062a\u06d5\u0648\u0627\u0648\u06cc \u0644\u06ce \u062f\u06d5\u0631\u0686\u0648\u0648\u060c \u0632\u0627\u0646\u0633\u062a \u0648 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u0646 \u062f\u0627\u06cc\u06d5 \u0648 \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '[\u062c\u0627\u0631\u06ce] \u0695\u0648\u0648\u06cc \u06a9\u0631\u062f\u06d5 \u0634\u0627\u0631\u06d5\u0648\u06d5\u060c \u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u0626\u0627\u06af\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0628\u06ce. \u0644\u06d5\u0648\u06ce \u062a\u0648\u0648\u0634\u06cc \u062f\u0648\u0648 \u067e\u06cc\u0627\u0648\u0627\u0646 \u0647\u0627\u062a\u060c \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0645\u0634\u062a \u0648 \u0645\u0695\u06cc\u0627\u0646 \u0628\u0648\u0648\u061b \u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06d5\u06af\u06d5\u0632\u06cc \u062e\u06c6\u06cc \u0648 \u0626\u06d5\u0648\u06cc \u062a\u0631\u06cc\u0627\u0646 \u0633\u06d5\u0631 \u0628\u06d5 \u062f\u0648\u0698\u0645\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0628\u0648\u0648. \u0626\u06d5\u0648\u06cc \u0647\u0627\u0648\u0695\u06d5\u06af\u06d5\u0632\u06cc \u062e\u06c6\u06cc \u0628\u0648\u0648\u060c \u062f\u0698\u06cc \u06a9\u0627\u0648\u0631\u0627 \u0646\u06d5\u06cc\u0627\u0631\u06d5\u06a9\u06d5 \u0647\u0627\u0646\u0627\u06cc \u067e\u06ce \u0628\u0631\u062f\u061b \u0645\u0648\u0648\u0633\u0627 \u0645\u0633\u062a\u06ce\u06a9\u06cc \u062a\u06ce \u0633\u0631\u06d5\u0648\u0627\u0646\u062f\u060c \u0645\u0631\u062e\u06d5\u06cc \u0628\u0695\u06cc. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5 \u062f\u06cc\u0627\u0631\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u067e\u06ce\u0645\u06cc \u06a9\u0631\u062f\u061b \u06a9\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u0648\u0698\u0645\u0646\u06d5 \u0648 \u0695\u06ce\u06af\u0627 \u062f\u06d5\u06af\u06c6\u0695\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0626\u06d5\u0645\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0645 \u0644\u06d5 \u062e\u06c6\u0645 \u06a9\u0631\u062f\u060c \u0639\u0627\u0641\u0648\u0648\u0645 \u0628\u06a9\u06d5. \u0626\u06cc\u062a\u0631 \u062e\u0648\u062f\u0627 \u062e\u06d5\u062a\u0627\u06cc \u0628\u06d5\u062e\u0634\u06cc\u061b \u06a9\u06d5 \u0626\u06d5\u0648 \u0644\u06d5 \u062e\u06d5\u062a\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0626\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u0646\u062a \u06a9\u0631\u062f\u060c \u0628\u0695\u06cc\u0627\u0631 \u0626\u06d5\u062f\u06d5\u0645 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u06d5\u0628\u0645\u06d5 \u067e\u0634\u062a\u06cc\u0648\u0627\u0646\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646.',
    '\u0644\u06d5\u0648 \u0634\u0627\u0631\u06d5\u062f\u0627 \u0628\u06d5 \u062a\u0631\u0633\u06d5\u0648\u06d5 \u0634\u06d5\u0648\u06cc \u0628\u0648\u0648\u0631\u06cc \u0648 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0648\u0648. \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u062f\u0648\u06ce\u0646\u06ce \u0647\u0627\u0646\u0627\u06cc \u067e\u06ce \u0628\u0631\u062f\u0628\u0648\u0648\u060c \u062f\u06cc\u0633\u0627\u0646 \u0647\u0627\u0648\u0627\u0631\u06cc \u0628\u06c6 \u0647\u06ce\u0646\u0627. \u0645\u0648\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u062f\u06cc\u0627\u0631\u06d5 \u062a\u06c6 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u062f\u06d5\u0631\u0686\u0648\u0648\u06cc.',
    '\u06a9\u0627\u062a\u06ce \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0686\u0648\u0648 \u06a9\u06d5 \u062f\u0648\u0698\u0645\u0646\u06cc \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9\u06cc\u0627\u0646 \u0628\u0648\u0648\u060c \u06af\u0648\u062a\u06cc: \u0645\u0648\u0648\u0633\u0627! \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u062f\u0648\u06ce\u0646\u06ce \u06cc\u06d5\u06a9\u06ce\u06a9\u062a \u06a9\u0648\u0634\u062a\u060c \u062f\u06d5\u062a\u06d5\u0648\u06ce \u0645\u0646\u06cc\u0634 \u0628\u06a9\u0648\u0698\u06cc\u061f \u062a\u06c6 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u06d5 \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5 \u0628\u0628\u06cc\u06d5 \u0645\u0644\u0647\u0648\u0695 \u0648 \u0646\u0627\u062a\u06d5\u0648\u06ce \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06cc \u0648\u0627 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u0627\u0646 \u0633\u0627\u0645\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646.',
    '\u067e\u06cc\u0627\u0648\u06ce\u06a9 \u0644\u06d5\u0648\u067e\u06d5\u0695\u06cc \u0634\u0627\u0631\u06d5\u0648\u06d5 \u0628\u06d5 \u0647\u06d5\u06b5\u06d5\u062f\u0627\u0648\u0627\u0646 \u06af\u06d5\u06cc\u0634\u062a\u06ce\u060c \u0648\u0627\u062a: \u0626\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627! \u0633\u06d5\u0631\u06af\u06d5\u0648\u0631\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062a\u06c6 \u0695\u0627\u0648\u06ce\u0698 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u062a\u06c6 \u0628\u06a9\u0648\u0698\u0646. \u0632\u0648\u0648 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0645\u06d5\u062a \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u067e\u06ce \u062f\u06d5\u06b5\u06ce\u0645.',
    '\u0628\u06d5 \u062a\u0631\u0633\u06d5\u0648\u06d5 \u0644\u06d5\u0648\u06ce \u0695\u06c6\u06cc\u06cc \u0648 \u0628\u06d5 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646\u06cc \u062f\u06d5\u06cc\u0695\u0648\u0627\u0646\u06cc. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0644\u06d5\u0645 \u06a9\u06c6\u0645\u06d5\u06b5\u06d5 \u0633\u062a\u06d5\u0645\u06a9\u0627\u0631\u06d5 \u0695\u0632\u06af\u0627\u0631\u0645 \u06a9\u06d5.',
    '\u06a9\u0627\u062a\u06ce \u0628\u06d5\u0631\u06d5\u0648 \u0645\u06d5\u062f\u06cc\u06d5\u0646 \u062f\u06d5\u0686\u0648\u0648\u060c \u06af\u0648\u062a\u06cc: \u0628\u06d5\u0634\u06a9\u0648\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u0645 \u0628\u06a9\u0627.',
    '\u06a9\u06d5 \u0647\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u0648\u06cc \u0645\u06d5\u062f\u06cc\u06d5\u0646\u060c \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u0644\u06d5 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u062f\u06cc\u062a \u0626\u0627\u0648 \u0647\u06d5\u06b5\u062f\u06ce\u0646\u062c\u0646. \u0644\u06d5\u0648 \u0644\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u0648\u0648 \u0698\u0646\u06cc \u062f\u06cc\u0646 [\u067e\u06d5\u0632\u06ce \u062e\u06c6] \u062f\u0648\u0648\u0631 \u062f\u06d5\u062e\u06d5\u0646\u06d5\u0648\u06d5. \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u0686\u0646\u061f \u06af\u0648\u062a\u06cc\u0627\u0646: \u0647\u06d5\u062a\u0627 \u0634\u06a4\u0627\u0646\u06d5\u06a9\u0627\u0646 \u067e\u06d5\u0632\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u0627\u0648\u062f\u0627\u0646\u06ce \u0646\u06d5\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5\u060c \u0626\u06ce\u0645\u06d5 \u067e\u06d5\u0632\u06ce \u062e\u06c6 \u0626\u0627\u0648 \u0646\u0627\u062f\u06d5\u06cc\u0646\u061b \u0628\u0627\u0648\u06a9\u06cc \u0626\u06ce\u0645\u06d5\u0634 \u0626\u06cc\u062e\u062a\u06cc\u0627\u0631\u06ce\u06a9\u06cc \u0632\u0648\u0631\u0647\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u0627\u0648\u06cc \u0628\u06c6 \u0647\u06d5\u06b5\u06ce\u0646\u062c\u0627\u0646\u061b \u067e\u0627\u0634\u0627\u0646 \u062e\u06c6\u06cc \u06af\u06d5\u06cc\u0627\u0646\u062f\u06d5 \u0633\u06ce\u0628\u06d5\u0631. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0645\u0646 \u0626\u0627\u062a\u0627\u062c\u06cc \u0626\u06d5\u0648 \u062e\u06ce\u0631\u06d5\u0645 \u06a9\u06d5 \u0628\u06c6\u0645 \u062f\u06d5\u0646\u06ce\u0631\u06cc.',
    '\u06cc\u06d5\u06a9 \u0644\u06d5\u0648 \u062f\u0648\u0648\u0627\u0646\u06d5 - \u06a9\u06d5 \u0628\u06d5 \u0634\u06d5\u0631\u0645\u06d5\u0648\u06d5 \u0695\u06ce \u062f\u06d5\u0686\u0648\u0648 - \u0647\u0627\u062a\u06d5 \u0644\u0627\u06cc \u0648 \u06af\u0648\u062a\u06cc: \u0628\u0627\u0648\u06a9\u0645 \u0628\u0627\u0646\u06af\u062a \u062f\u06d5\u06a9\u0627 \u062a\u0627 \u0628\u06c6 \u0626\u0627\u0648\u06ce \u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0645\u06d5\u062a \u0647\u06d5\u06b5\u06ce\u0646\u062c\u0627\u0648\u06d5\u060c \u06a9\u0631\u06ce\u062a \u062f\u0627\u062a\u06ce. \u0648\u06d5\u062e\u062a\u06ce \u0647\u0627\u062a\u0648\u0648 \u0695\u0648\u0648\u062f\u0627\u0648\u06d5\u06a9\u06d5\u06cc \u0628\u06c6 \u06af\u06ce\u0695\u0627\u0648\u06d5\u060c \u06af\u0648\u062a\u06cc: \u0626\u06cc\u062a\u0631 \u062a\u0631\u0633\u062a \u0646\u06d5\u0628\u06ce\u060c \u0644\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06cc \u0633\u062a\u06d5\u0645\u06a9\u0627\u0631\u0627\u0646 \u0695\u0632\u06af\u0627\u0631\u062a \u0628\u0648\u0648.',
    '\u06cc\u06d5\u06a9\u06ce \u0644\u06d5\u0648\u0627\u0646 \u06af\u0648\u062a\u06cc: \u0628\u0627\u0648\u06d5! \u0628\u06d5 \u06a9\u0631\u06ce\u06cc \u0628\u06af\u0631\u06d5\u061b \u0686\u0627\u062a\u0631\u06cc\u0646 \u0695\u06d5\u0646\u062c\u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u0628\u06d5 \u062a\u0627\u0642\u06d5\u062a \u0648 \u062c\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0645\u0646 \u062e\u06d5\u06cc\u0627\u06b5\u0645 \u0648\u0627\u06cc\u06d5 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5\u0648 \u062f\u0648\u0648 \u06a9\u06cc\u0698\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0645 \u0628\u062f\u06d5\u0645 \u0628\u06d5 \u062a\u06c6\u061b \u0628\u06d5\u0648 \u0645\u06d5\u0631\u062c\u06d5 \u0647\u06d5\u062a\u0627 \u0647\u06d5\u0634\u062a \u0633\u0627\u06b5\u0627\u0646 \u0695\u06d5\u0646\u062c\u0628\u06d5\u0631\u0645 \u0628\u06cc. \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5 \u0633\u0627\u06b5\u062a \u0628\u0631\u062f\u06d5 \u0633\u06d5\u0631\u060c \u06a9\u06d5\u06cc\u0641\u06cc \u062e\u06c6\u062a\u06d5 \u0648 \u0646\u0627\u0645\u06d5\u0648\u06ce \u0632\u06c6\u0631\u06cc\u062a \u0628\u06c6 \u0628\u06ce\u0646\u0645. \u06af\u06d5\u0631 \u062e\u0648\u0627 \u062d\u06d5\u0632 \u06a9\u0627 \u0632\u0648\u0648 \u062f\u06d5\u0632\u0627\u0646\u06cc \u06a9\u06d5 \u0644\u06d5 \u067e\u06cc\u0627\u0648\u06d5 \u0628\u0627\u0634\u06d5\u06a9\u0627\u0646\u0645.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645\u06d5 \u0628\u0695\u06cc\u0627\u0631\u06ce\u06a9 \u0628\u06ce \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc \u0645\u0646 \u0648 \u062a\u06c6\u062f\u0627\u061b \u0647\u06d5\u0631 \u06a9\u0627\u0645 \u0644\u06d5\u0648 \u062f\u0648\u0648 \u0645\u0627\u0648\u06d5\u0645 \u067e\u06d5\u0695\u0627\u0646\u062f\u060c \u0646\u0627\u0628\u06ce \u0646\u0627\u0647\u06d5\u0642\u06cc\u0645 \u0644\u06ce \u0628\u06a9\u0631\u06ce\u061b \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0631\u0686\u06cc \u0626\u06ce\u0698\u06cc\u0646\u060c \u0628\u0631\u06cc\u06a9\u0627\u0631\u06d5.',
    '\u0645\u0648\u0648\u0633\u0627 \u06a9\u06d5 \u0645\u0627\u0648\u06d5\u06a9\u06d5\u06cc \u067e\u06d5\u0695\u0627\u0646\u062f \u0648 \u062e\u06ce\u0632\u0627\u0646\u06cc \u0628\u0631\u062f \u0648 \u0695\u06c6\u06cc\u06cc\u060c \u0626\u0627\u06af\u0631\u06ce\u06a9\u06cc \u0644\u06d5 \u0626\u0627\u0644\u06cc \u062a\u0648\u0648\u0631\u06ce \u0628\u06d5\u062f\u06cc \u06a9\u0631\u062f. \u0628\u06d5 \u0645\u0627\u06b5 \u0648 \u0645\u0646\u062f\u0627\u06b5\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0695\u0627\u0628\u0648\u0648\u06d5\u0633\u062a\u0646\u060c \u0645\u0646 \u0626\u0627\u06af\u0631\u06ce\u06a9\u0645 \u0628\u06d5\u062f\u06cc \u06a9\u0631\u062f\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0644\u06d5\u0648\u06ce \u062e\u06d5\u0628\u06d5\u0631\u06ce\u06a9\u0648\u0648 \u0628\u06c6 \u0628\u0647\u06ce\u0646\u0645\u061b \u06cc\u0627\u0646 \u0686\u06c6\u06b5\u06ce\u06a9\u0648\u0648 \u0644\u06d5\u0648 \u0626\u0627\u06af\u0631\u06d5 \u0628\u06c6 \u0628\u06ce\u0646\u0645\u06d5\u0648\u06d5\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u062e\u06c6 \u06af\u06d5\u0631\u0645 \u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0648\u06d5\u062e\u062a\u06ce \u06af\u06d5\u06cc\u0634\u062a\u06d5 \u0644\u0627\u06cc \u0626\u0627\u06af\u0631\u060c \u0644\u0627\u06cc \u062f\u06d5\u0633\u062a\u06cc \u0695\u0627\u0633\u062a\u06cc \u062f\u06c6\u06b5\u06d5\u0648\u06d5 - \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06d5 \u067e\u06cc\u0631\u06c6\u0632\u06d5\u06a9\u06d5\u062f\u0627 - \u0644\u06d5 \u062f\u0627\u0631\u06d5\u06a9\u06d5\u0648\u06d5 \u0628\u0627\u0646 \u06a9\u0631\u0627: \u0626\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627! \u0645\u0646 \u0647\u06d5\u0631 \u062e\u0648\u062f\u06cc \u062e\u06c6\u0645\u060c \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u0645 \u06a9\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062c\u06cc\u0647\u0627\u0646\u0645.',
    '\u0639\u0627\u0633\u0627\u06a9\u06d5\u0634\u062a \u0641\u0695\u06ce \u0628\u062f\u06d5! \u0648\u06d5\u062e\u062a\u06ce \u062f\u06cc\u062a\u06cc \u0644\u06d5 \u0686\u06d5\u0634\u0646\u06cc \u0645\u0627\u0631\u06ce \u062f\u06d5\u0628\u0632\u0648\u06ce\u060c \u0647\u06d5\u06b5\u0627\u062a \u0648 \u0647\u06cc\u0686 \u0626\u0627\u0648\u0695\u06ce\u06a9\u06cc \u0646\u06d5\u062f\u0627\u06cc\u06d5\u0648\u06d5. \u0626\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627 \u0644\u06ce\u06cc \u0648\u06d5\u0631\u06d5 \u067e\u06ce\u0634 \u0648 \u062a\u0631\u0633\u062a \u0646\u06d5\u0628\u06ce\u060c \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646\u06ce\u06a9\u062a \u067e\u06ce \u0646\u0627\u06af\u0627.',
    '\u062f\u06d5\u0633\u062a\u06cc\u0634\u062a \u0628\u062e\u06d5 \u0628\u0627\u062e\u06d5\u06b5\u062a\u060c \u0686\u06d5\u0631\u0645\u0648\u0648\u06cc \u0628\u06ce \u0632\u06d5\u0631\u06d5\u0631 \u062f\u06ce\u062a\u06d5 \u062f\u06d5\u0631\u061b \u0628\u06c6 \u0626\u06d5\u0648\u06d5\u0634 \u062a\u0631\u0633\u062a \u0646\u06d5\u0645\u06ce\u0646\u06ce\u060c \u062f\u06d5\u0633\u062a\u062a \u0628\u0646\u06d5 \u0698\u06ce\u0631\u06cc \u0628\u0627\u06b5\u062a. \u0626\u06d5\u0645 \u062f\u0648\u0648\u0627\u0646\u06d5 \u062f\u0648\u0648 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0695\u0648\u0648\u0646\u0646\u060c \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0695\u0627 \u0628\u06c6 \u0644\u0627\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u062f\u0627\u0631\u0648\u062f\u06d5\u0633\u062a\u06d5\u06a9\u06d5\u06cc\u061b \u0626\u06d5\u0648\u0627\u0646 \u06af\u06d5\u0644\u06ce \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0645\u0646 \u06cc\u06d5\u06a9\u0645 \u0644\u06d5\u0648\u0627\u0646 \u06a9\u0648\u0634\u062a\u0648\u0648\u06d5\u061b \u0644\u06d5\u0648\u06d5 \u062f\u06d5\u062a\u0631\u0633\u0645 \u0628\u0645\u06a9\u0648\u0698\u0646.',
    '\u0628\u0631\u0627\u0645 \u0647\u0627\u0695\u0648\u0648\u0646\u060c \u0644\u06d5 \u0645\u0646 \u0632\u0645\u0627\u0646 \u0695\u06d5\u0648\u0627\u0646\u062a\u0631\u06d5\u061b \u0633\u0627 \u0626\u06d5\u0648\u06cc\u0634\u0645 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0644 \u06a9\u06d5\u060c \u06cc\u0627\u0648\u06d5\u0631\u0645 \u0628\u06ce \u0648 \u0642\u0633\u06d5\u06a9\u0627\u0646\u0645 \u0628\u0633\u06d5\u0644\u0645\u06ce\u0646\u06ce. \u0645\u0646 \u062a\u0631\u0633\u0645 \u0647\u06d5\u0633 \u067e\u06ce\u0645 \u0628\u06ce\u0698\u0646: \u062a\u06c6 \u062f\u0631\u06c6\u0632\u0646\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0648\u0627 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0628\u0631\u0627\u06a9\u06d5\u062a \u067e\u0634\u062a\u062a \u0628\u06af\u0631\u06ce\u061b \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0634\u0645\u0627\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06ce\u06a9\u0648\u0648 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06ce\u060c \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0646\u06d5\u06af\u0627\u061b \u0626\u06ce\u0648\u06d5 \u062f\u0648\u0648\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0644\u06d5 \u062f\u0648\u0648\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u0646 \u0633\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u0646.',
    '\u06a9\u0627\u062a\u06ce \u0645\u0648\u0648\u0633\u0627 \u0648\u06d5\u0644 \u0628\u06d5\u06b5\u06af\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06a9\u0627\u0646\u0645\u0627\u0646 \u0647\u0627\u062a\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u0628\u0644 \u0698 \u062c\u0627\u062f\u0648\u0648 \u0634\u062a\u06ce \u0646\u06cc\u06cc\u06d5 \u0648 \u0644\u06d5 \u062e\u06c6\u0648\u06d5 \u0695\u06ce\u06a9\u06cc \u062e\u0633\u062a\u0648\u0648\u06d5\u061b \u06a9\u06d5 \u0644\u06d5 \u0628\u0627\u0648\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u0634\u0645\u0627\u0646 \u0626\u06d5\u0645 \u0634\u062a\u06d5\u0645\u0627\u0646 \u0646\u06d5\u0628\u06cc\u0633\u062a\u0648\u0648\u06d5.',
    '\u0645\u0648\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0686\u0627\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06ce \u0644\u062c\u06d5\u0645 \u0626\u06d5\u0648 \u0695\u06ce\u0646\u0648\u06ce\u0646\u06cc \u0647\u0627\u0648\u0631\u062f\u0648\u0648\u06d5 \u0648 \u06a9\u06ce \u067e\u0627\u0634\u06d5\u0695\u06c6\u0698 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06cc \u062f\u06d5\u0628\u06ce. \u0628\u06ce \u0633\u06c6 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u0628\u0646.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u062f\u06cc\u0648\u06d5\u062e\u0627\u0646 \u0646\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646! \u0648\u0627 \u0646\u0627\u0632\u0627\u0646\u0645 \u063a\u06d5\u06cc\u0631 \u0644\u06d5 \u0645\u0646 \u062e\u0648\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0647\u06d5\u0628\u06ce\u061b \u0633\u0627 \u0647\u0627\u0645\u0627\u0646! \u062a\u06c6 \u062e\u0634\u062a\u06cc \u06a9\u0627\u06b5\u0645 \u0644\u06d5 \u06a9\u06b5\u062f\u0627 \u0628\u06c6 \u0628\u062e\u0627\u0631\u06ce\u0646\u06d5\u060c \u0642\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u0645 \u0628\u06c6 \u062f\u0631\u0648\u0633 \u06a9\u06d5\u060c \u062a\u0627 \u0644\u06d5\u0648\u06ce\u0695\u0627 \u0628\u0695\u0648\u0627\u0646\u0645\u06d5 \u062e\u0648\u0627\u06a9\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627\u061b \u06a9\u06d5\u0645\u0646 \u062a\u06d5\u0648\u0627\u0648 \u0644\u06d5 \u06a4\u0695 \u0628\u06ce\u0698\u0627\u0646\u06cc \u062f\u06d5\u0632\u0627\u0646\u0645.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0644\u06d5\u0634\u06a9\u0631\u06d5\u06a9\u06d5\u06a9\u0627\u0646\u06cc \u0644\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u060c \u0647\u06d5\u0631 \u0628\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0632\u0644\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06cc \u0648 \u0644\u0627\u06cc\u0627\u0646 \u0648\u0627\u0628\u0648\u0648 \u0646\u0627\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5.',
    '\u0633\u0627 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0644\u06d5\u0634\u06a9\u0631\u06cc\u0645\u0627\u0646 \u0648\u06d5\u06af\u06cc\u0631 \u0647\u06ce\u0646\u0627 \u0648 \u0647\u06d5\u06b5\u0645\u0627\u0646\u062f\u0627\u0634\u062a\u0646\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u0648\u06d5. \u062f\u06d5\u0633\u0627 \u0628\u0695\u0648\u0627\u0646\u06d5 \u0626\u0627\u06a9\u0627\u0645\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u0686\u0644\u06c6\u0646 \u0628\u0648\u0648.',
    '\u06a9\u0631\u062f\u06cc\u0634\u0645\u0627\u0646\u0646 \u0628\u06d5 \u067e\u06ce\u0634\u06d5\u0648\u0627\u06cc\u0627\u0646\u06ce\u06a9 \u06a9\u06d5 \u0648\u0627 \u0628\u0627\u0646\u06af \u0628\u06c6 \u0626\u0627\u06af\u0631 \u0695\u0627\u062f\u06d5\u0647\u06ce\u06b5\u0646 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0646\u06d5\u062d\u0644\u06d5\u062a\u0645\u0627\u0646 \u0648\u06d5\u0634\u0648\u06ce\u0646 \u062e\u0633\u062a\u0646 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 [\u0695\u0648\u0648\u0695\u06d5\u0634 \u0648] \u0628\u06cc\u0686\u0645 \u06a9\u0631\u06ce\u062a\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0686\u06cc\u0646 \u0648 \u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u0646 \u0642\u0695 \u062a\u06ce \u062e\u0633\u062a\u0646\u060c \u0626\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u0646 \u062f\u0627 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627 \u06a9\u06d5 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0628\u0628\u06ce\u062a\u06d5 \u0647\u06c6\u06cc \u0695\u06c6\u0634\u0646 \u0628\u06cc\u0646\u06cc \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce \u0628\u0648\u0648\u0646\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06cc\u0631 \u0644\u06d5 \u062e\u06c6 \u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u0633\u06d5\u0631\u062f\u06d5\u0645\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u0626\u06d5\u0631\u06a9\u06ce\u06a9 \u062f\u0627\u0646\u0627 \u0628\u06c6 \u0645\u0648\u0648\u0633\u0627\u060c \u0646\u06d5 \u062a\u06c6 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u0631\u0627\u0648\u0627\u06a9\u06d5\u06cc [\u062a\u0648\u0648\u0631] \u06d5\u0648\u06d5 \u0628\u0648\u0648\u06cc\u060c \u0646\u06d5 \u0647\u06cc\u0686 \u0626\u0627\u06af\u0627\u0634\u062a \u0644\u06ce \u0628\u0648\u0648\u06d5.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0645\u06d5 \u0632\u06c6\u0631 \u0628\u06d5\u0631\u06d5\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u06cc\u06d5 \u0695\u0648\u0648\u061b \u062a\u06d5\u0645\u06d5\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u062f\u0631\u06ce\u0698 \u0628\u0648\u0648. \u062a\u06c6 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u0645\u06d5\u062f\u06cc\u06d5\u0646 \u0646\u06d5\u0628\u0648\u0648\u06cc\u060c \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0645\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u06cc\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0646 \u062f\u06d5\u0646\u0627\u0631\u062f\u0646.',
    '\u0644\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u0634\u062f\u0627 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u06af\u0627\u0632\u06cc \u0645\u0648\u0648\u0633\u0627\u0646 \u06a9\u0631\u062f\u060c \u062a\u06c6 \u0644\u06d5 \u0646\u0632\u06cc\u06a9 \u062a\u0648\u0648\u0631\u06ce \u0646\u06d5\u0628\u0648\u0648\u06cc\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0645\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u06d5\u06a9\u06d5 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0695\u0627\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631 \u0644\u06d5 \u062a\u06c6 \u06a9\u06d5\u0633\u06cc \u0648\u0627 \u0646\u06d5\u0628\u0648\u0648\u06d5 \u062a\u0631\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u0647\u06ce\u0646\u06ce\u061b \u0626\u06d5\u062a\u06c6 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06ce\u0646\u06cc\u060c \u0628\u06cc\u0631 \u0644\u06d5 \u062e\u06c6 \u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0646\u06d5\u0628\u0627 \u06a9\u06d5 \u06a9\u0627\u062a\u06ce\u06a9\u06cc - \u0644\u06d5 \u0633\u06c6\u0646\u06af\u06d5\u06cc \u0626\u0627\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 - \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627\u06cc\u06d5 \u0628\u0646\u060c \u0628\u06ce\u0698\u0646: \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u062a\u06c6 \u0628\u06c6\u0686\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u062a \u0628\u06c6 \u0646\u06d5\u0646\u0627\u0631\u062f\u06cc\u0646\u060c \u062a\u0627 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062a\u06c6\u0628\u06a9\u06d5\u06cc\u0646 \u0648 \u0644\u06d5 \u0695\u06ce\u0632\u06cc \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0628\u06cc\u0646\u060c [\u0633\u0632\u0627\u062f\u0627\u0646\u0645\u0627\u0646 \u067e\u06ce\u0634 \u062f\u06d5\u062e\u0633\u062a\u0646].',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0648\u06d5\u062e\u062a\u06ce \u0626\u06d5\u0645 \u0695\u0627\u0633\u062a\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0628\u06c6 \u0647\u0627\u062a\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06c6\u0686\u06cc \u0626\u06d5\u0648\u06cc \u0628\u06c6 \u0645\u0648\u0648\u0633\u0627 \u0647\u0627\u062a\u0648\u0648\u06d5 \u0628\u06c6 \u0626\u06d5\u0645 \u0646\u06d5\u0647\u0627\u062a\u061f \u0626\u06d5\u062f\u06cc \u0626\u06d5\u0648\u06cc \u0644\u06d5\u0648\u06d5 \u067e\u06ce\u0634 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627 \u062f\u0631\u0627\u060c \u0626\u06d5\u0645\u0627\u0646 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0646\u06d5\u06a9\u0631\u062f\u061f \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u062f\u0648\u0648 \u062c\u0627\u062f\u0648\u0648\u0646 \u06a9\u06d5 \u067e\u0634\u062a\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0627\u0646 \u06af\u0631\u062a\u0648\u0648\u06d5 \u0648 \u06af\u0648\u062a\u06cc\u0634\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0646 \u062d\u0627\u0634\u0627\u0645\u0627\u0646\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u062a\u0646\u060c \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06ce\u06a9 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06ce\u0646\u0646 \u06a9\u06d5 \u0644\u06d5 \u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9\u0627\u0646\u06cc\u0627\u0646 \u067e\u062a\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06af\u06d5 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u0627\u061b \u0647\u06d5\u062a\u0627 \u0645\u0646 \u0634\u0648\u06ce\u0646\u06cc \u0628\u06a9\u06d5\u0648\u0645.',
    '\u06af\u06d5\u0631 \u067e\u06d5\u0631\u0633\u06a4\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0627\u06cc\u06d5\u0648\u06d5\u060c \u0633\u0627 \u0628\u0632\u0627\u0646\u06d5 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc\u0627\u0646\u0646\u061b \u06a9\u06ce\u0634 \u062e\u0648\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0646\u06d5\u06a9\u0627 \u0648 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u06d5\u0644\u0647\u0627\u06cc \u062f\u06b5\u06cc \u0628\u06ce\u060c \u06a9\u06d5\u0633\u06cc \u0644\u06ce \u06af\u0648\u0645\u0695\u0627\u062a\u0631 \u0646\u06cc\u06cc\u06d5. \u062e\u0648\u0627\u0634 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0646\u0627\u06a9\u0627.',
    '\u0644\u06d5 \u067e\u06d5\u0633\u062a\u0627 \u0626\u06d5\u0648 \u0648\u062a\u0627\u0631\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0647\u06d5\u0646\u0627\u0631\u062f\u0646\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u067e\u06d5\u0646\u062f [\u06cc \u0644\u06ce] \u0648\u06d5\u0631\u0628\u06af\u0631\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5\u0631 \u0644\u06d5 \u0647\u0627\u062a\u0646\u06cc \u0642\u0648\u0631\u0639\u0627\u0646\u060c \u0626\u06ce\u0645\u06d5 \u06a9\u062a\u06ce\u0628\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627\u0648\u0646\u060c \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u0642\u0648\u0631\u0639\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u062a\u0627 \u0628\u06c6\u06cc\u0627\u0646 \u0648\u06d5\u062f\u06d5\u062e\u0648\u06ce\u0646\u0646\u060c \u0626\u06ce\u0698\u0646: \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627 \u0648 \u0628\u06ce \u0633\u06c6 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646 \u0695\u0627\u06cc\u06d5. \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5\u0634 \u0628\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0626\u06ce\u0645\u06d5 \u0642\u0628\u0648\u0648\u06b5\u0646 \u06a9\u0631\u062f\u0628\u0648\u0648.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0686\u0648\u0648\u0646 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0648\u0648\u0646\u060c \u062f\u0648\u0648 \u062c\u0627\u0631 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u0631\u06ce. \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e\u06cc\u0634 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u0633\u0695\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0648\u0646\u060c \u062e\u06ce\u0631\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5 \u0648\u062a\u0627\u0631\u06cc \u0647\u06d5\u0631\u0632\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u06af\u0648\u06ce \u062f\u06d5\u06af\u0627\u060c \u0644\u06d5 \u06af\u0648\u06ce\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06cc \u062f\u06d5\u0631\u062f\u0627\u0648\u06ce\u0646 \u0648 \u0626\u06ce\u0698\u0646 \u06a9\u06d5: \u0626\u06ce\u0645\u06d5 \u06a9\u0627\u0631\u06cc \u062e\u06c6 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u0626\u06ce\u0648\u06d5\u0634 \u0626\u0627\u06a9\u0627\u0631\u06cc \u062e\u06c6\u0648 \u0647\u06d5\u06cc\u06d5\u060c \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0628\u06ce \u0648\u06d5\u0632\u06d5\u0646\u0646\u061b \u06a9\u06d5\u06cc\u0641\u0646 \u0628\u06d5\u0646\u06d5\u0632\u0627\u0646\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u062a\u06c6 \u0646\u0627\u062a\u0627\u0646\u06cc \u06a9\u06d5\u0633\u06ce \u0628\u06c6 \u062e\u06c6\u062a \u062e\u06c6\u0634\u062a \u0628\u0648\u06ce \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u06a9\u06d5\u06cc\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u06a9\u06ce \u0628\u062e\u0648\u0627\u0632\u06ce \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627. \u0647\u06d5\u0631 \u062e\u06c6\u0634\u06cc \u0686\u0627\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06ce \u0647\u06ce\u0698\u0627\u06cc \u0626\u06d5\u0645 \u0695\u06ce\u0628\u0627\u0632\u06d5\u06cc\u06d5.',
    '\u0626\u06ce\u0698\u0646: \u0626\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5 \u062a\u06c6 \u0628\u06cc\u0646 \u0648 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc \u062a\u06c6 \u0628\u06cc\u0646\u060c \u0644\u06d5 \u0632\u06ce\u062f\u06cc \u062e\u06c6\u0646 \u062a\u06d5\u0631\u06d5\u0646 \u062f\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u0647\u06ce\u0645\u0646\u0645\u0627\u0646 \u062f\u0627\u0646\u06d5\u0645\u06d5\u0632\u0631\u0627\u0646\u062f\u0648\u0648\u0646\u060c \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062c\u06c6\u0631\u06d5 \u0628\u06d5\u0631\u0648\u0628\u0648\u0648\u06cc \u0628\u06c6 \u062f\u06d5\u0646\u06ce\u0631\u0646 \u0648 \u0626\u06d5\u0645 \u0628\u0698\u06cc\u0648\u06d5 \u0644\u06d5\u0645\u06d5\u0695\u0627\u06cc\u06d5\u061f \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u0686\u06d5\u0646\u062f\u06d5 \u0634\u0627\u0631\u06cc\u0634\u0645\u0627\u0646 \u0642\u0695 \u06a9\u0631\u062f\u0646 [\u06a9\u06d5 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc] \u0628\u06d5 \u062e\u06c6\u0634\u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0633\u06d5\u0631\u062e\u06c6\u0634 \u0628\u0628\u0648\u0648\u0646. \u0626\u06d5\u0645\u06d5\u0634 \u0645\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646 - \u06a9\u06d5\u0645\u06ce\u06a9 \u0646\u06d5\u0628\u06ce - \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u0644\u06ce \u0646\u06cc\u0634\u062a\u06d5\u062c\u06ce \u0646\u06d5\u0628\u0648\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0645\u0627\u0646 \u0645\u0627\u0648\u0646\u06d5\u062a\u06d5\u0648\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0626\u06d5\u0648 \u0634\u0627\u0631\u0627\u0646\u06d5\u06cc \u0642\u0695 \u0646\u06d5\u06a9\u0631\u062f\u0646\u060c \u062a\u0627 \u0644\u06d5 \u0646\u0627\u0648\u06d5\u0646\u062f\u06cc \u0626\u06d5\u0648\u0627\u0646\u0627 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0628\u06c6 \u0647\u06d5\u0646\u0627\u0631\u062f\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0628\u062e\u0648\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0626\u06ce\u0645\u06d5 \u0647\u06cc\u0686 \u0634\u0627\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06d5 \u0642\u0695 \u0646\u06d5\u062f\u0627\u060c \u0645\u06d5\u0631 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642 \u0628\u0648\u0648 \u0628\u0646.',
    '\u0626\u06d5\u0648\u06cc \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u0631\u0627\u0648\u06d5 \u0647\u06d5\u0631 \u0645\u0627\u06cc\u06d5\u06cc \u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u062a\u0627\u0646\u06d5 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0648 \u0632\u06d5\u0645\u0628\u06d5\u0631\u06cc\u06d5\u062a\u06cc. \u0626\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u0644\u062c\u06d5\u0645 \u062e\u0648\u062f\u0627\u06cc\u06d5\u060c \u0686\u0627\u062a\u0631\u06d5 \u0648 \u0632\u06c6\u0631\u062a\u0631 \u062f\u06d5\u0645\u06ce\u0646\u06ce\u061b \u0626\u0627\u062e\u06c6 \u0644\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0634 \u062a\u06ce \u0646\u0627\u06af\u06d5\u0646\u061f',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0628\u0627\u0634\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627\u0648\u06d5 \u0648 \u0628\u06c6\u0634\u06cc \u067e\u06ce\u06a9 \u062f\u06ce\u060c \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u06d5\u0633\u06ce \u0647\u06d5\u0631 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0698\u06cc\u0627\u0646\u062f\u0648\u0648\u0645\u0627\u0646\u06d5\u060c \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u0646\u061f \u06a9\u06d5 \u0626\u06d5\u0645\u06d5\u0634\u06cc\u0627\u0646 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u062f\u06d5\u0647\u06ce\u0646\u06cc\u0646 \u0628\u06c6 \u062c\u0627\u0628\u062f\u0627\u0646\u06d5\u0648\u06d5\u061f',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0648\u060c \u0626\u06ce\u0698\u06ce: \u06a9\u0648\u0627 \u0634\u06d5\u0631\u06cc\u06a9\u06d5\u06a9\u0627\u0646\u0645\u060c \u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5 \u062e\u06d5\u06cc\u0627\u06b5\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u061f',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u06d5\u0628\u06d5\u0631 \u0628\u0695\u06cc\u0627\u0631\u06cc \u0626\u06ce\u0645\u06d5 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u060c \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631\u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u0645\u0627\u0646 \u0644\u0627\u062f\u0627\u0646\u061b \u0628\u06c6\u06cc\u06d5\u0634 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u0645\u0627\u0646 \u0644\u0627\u062f\u0627\u0646 \u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u0646 \u0695\u06ce\u0646 \u0644\u06ce \u062a\u06ce\u06a9\u0686\u0648\u0648 \u0628\u0648\u0648. \u0648\u0627 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u062a \u0626\u06ce\u0645\u06d5 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u06cc\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06d5\u067e\u06d5\u0631\u0633\u062a.',
    '\u067e\u06ce\u0634\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0634\u06d5\u0631\u06cc\u06a9\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0628\u0627\u0646\u06af \u0628\u06a9\u06d5\u0646. \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0647\u06cc\u0686 \u0648\u06d5\u0631\u0627\u0645\u06cc\u0627\u0646 \u0646\u0627\u062f\u06d5\u0646\u06d5\u0648\u06d5. \u0647\u06d5\u0633\u062a \u0628\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062f\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0626\u06ce\u0698\u0646: \u062e\u06c6\u0632\u06cc\u0627 \u0644\u06d5 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u0627\u0646 \u0628\u0648\u0648\u06cc\u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u0626\u06d5\u06a9\u0627 \u0648 \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u06ce: \u0686\u0644\u06c6\u0646 \u0648\u06d5\u062f\u06d5\u0646\u06af \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06d5\u0648\u06d5 \u0686\u0648\u0648\u0646\u061f',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0644\u06d5 \u0647\u06d5\u0648\u0627\u06b5\u0627\u0646 \u0632\u06d5\u06cc\u0646 \u06a9\u0648\u06ce\u0631 \u062f\u06d5\u0628\u0646. \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0646\u0627\u06cc\u0627\u0646\u06a9\u0631\u06ce \u067e\u0631\u0633\u06ce \u0628\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0634 \u0628\u06a9\u06d5\u0646.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u06cc \u06a9\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u06c6\u062a\u06d5\u0648\u06d5 \u0648 \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0695\u0648\u0648\u06cc \u062f\u06d5\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u0695\u0632\u06af\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0628\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce \u0648\u06d5\u062f\u06cc \u062f\u06ce\u0646\u06ce \u0648 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u0698\u06ce\u0631\u06ce [\u0628\u06d5\u06b5\u0627\u0645] \u0626\u06d5\u0648\u0627\u0646 \u0647\u06d5\u0642\u06cc \u0628\u0698\u0627\u0631\u062f\u0646\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u0644\u06d5\u0648 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062f\u06c6\u0632\u06cc\u0646\u06d5\u0648\u06d5\u0634\u06cc\u0627\u0646\u060c \u062e\u0648\u062f\u0627 \u067e\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u0631\u0632\u062a\u0631\u06d5.',
    '\u0647\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06cc\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627 \u067e\u06d5\u0646\u0627 \u062f\u0627\u0648\u06d5 \u0648 \u0686\u06cc\u0634 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0626\u06d5\u062f\u0631\u06a9\u06ce\u0646\u0646.',
    '\u062e\u0648\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0648 \u0644\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06ce\u0698\u0627\u06cc \u067e\u06d5\u0631\u0633\u062a\u0646\u06ce\u06a9 \u0646\u06cc\u06cc\u06d5. \u0644\u06d5 \u062f\u0646\u06cc\u0627\u0634 \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u0633\u0648\u067e\u0627\u0633 \u0647\u06d5\u0631 \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u0626\u06d5\u0648\u06d5\u061b \u0641\u06d5\u0631\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u06d5 \u0648 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u062f\u06d5\u0628\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0686\u06c6\u0646 \u0628\u06cc\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u0626\u06d5\u0631 \u062e\u0648\u0627 \u0634\u06d5\u0648\u062a\u0627\u0646 \u0647\u06ce\u0646\u062f\u06d5 \u0644\u06ce \u062f\u0631\u06ce\u0698 \u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u062a\u0627 \u0642\u06cc\u0627\u0645\u06d5\u062a \u0646\u06d5\u0628\u0695\u06ce\u062a\u06d5\u0648\u06d5\u061f \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u06a9\u0627\u0645 \u0634\u06cc\u0627\u0648\u06cc \u067e\u06d5\u0631\u0633\u062a\u0646\u06ce \u0647\u06d5\u0633 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc\u062a\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u0646\u06ce\u061f \u0645\u06d5\u0631 \u0646\u0627\u0628\u06cc\u0633\u0646\u061f!',
    '\u0628\u06ce\u0698\u06d5: \u0686\u06c6\u0646 \u0628\u06cc\u0631 \u062f\u06d5\u0628\u06d5\u0646 \u0626\u06d5\u0631 \u062e\u0648\u0627 \u0695\u06c6\u0698\u062a\u0627\u0646 \u0647\u06ce\u0646\u062f\u06d5 \u0644\u06ce \u062f\u0631\u06ce\u0698 \u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0647\u06d5\u062a\u0627 \u0642\u06cc\u0627\u0645\u06d5\u062a \u0628\u062e\u0627\u06cc\u06d5\u0646\u06ce\u061f \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0686 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06ce \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0634\u06d5\u0648\u06ce\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0628\u06ce\u0646\u06ce \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062a\u06ce\u06cc\u062f\u0627 \u0647\u06d5\u062f\u0627 \u0628\u062f\u06d5\u0646\u061f \u0645\u06d5\u0631 \u0646\u0627\u0628\u06cc\u0646\u0646\u061f!',
    '\u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627\u0648\u06d5\u06cc\u06d5\u060c \u06a9\u06d5 \u0634\u06d5\u0648\u06cc \u0628\u06c6 \u0647\u06d5\u062f\u0627\u062f\u0627\u0646 \u0648 \u0695\u06c6\u0698\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u0646\u06cc \u0626\u06d5\u0648 \u0695\u06c6\u0632\u06cc\u06cc\u06d5\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u062a\u0627\u0646\u062f\u0627\u062a\u06ce\u060c \u0628\u06c6 \u062f\u0627\u0646\u0627\u0646\u061b \u0628\u06d5\u0634\u06a9\u06d5\u0645 \u0626\u06ce\u0648\u06d5 \u0634\u0648\u06a9\u0631\u06ce \u0628\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u06a9\u06d5 \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0648 \u0626\u06ce\u0698\u06ce: \u06a9\u0648\u0627 \u0634\u06d5\u0631\u06cc\u06a9\u06d5\u06a9\u0627\u0646\u0645 \u06a9\u06d5 \u0628\u06d5 \u062e\u06d5\u06cc\u0627\u06b5\u0648\u0648\u062f\u0627 \u062f\u06d5\u0647\u0627\u062a\u061f',
    '\u0648\u06d5\u062e\u062a\u06ce \u0644\u06d5 \u0646\u0627\u0648 \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9 \u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u060c \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0698\u06cc\u0646: \u06a9\u0648\u0627\u0646\u06ce\u061f \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06ce\u0646\u0646\u061b \u062c\u0627 \u062a\u06ce \u062f\u06d5\u06af\u06d5\u0646 \u0695\u0627\u0633\u062a \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u0644\u0627\u06cc \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u06a9\u06d5 \u0647\u06d5\u06b5\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u0633\u062a \u0644\u06d5 \u062f\u06d5\u0633\u06cc\u0627\u0646 \u062f\u06d5\u0686\u06ce.',
    '\u0642\u0627\u0695\u0648\u0648\u0646 \u0644\u06d5 \u0647\u06c6\u0632\u06cc \u0645\u0648\u0648\u0633\u0627 \u0628\u0648\u0648\u060c \u06a9\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u06a9\u0631\u062f \u0628\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc \u06a9\u0631\u062f\u0646 \u0644\u06ce\u06cc\u0627\u0646. \u0647\u06ce\u0646\u062f\u0646 \u06af\u06d5\u0646\u062c\u06cc\u0646\u06d5 \u062f\u0627\u0628\u0648\u0648\u06cc\u06d5\u060c \u0647\u06d5\u0631\u062a\u06d5\u0646\u06cc\u0627 \u06a9\u0644\u06cc\u0644\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u06af\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0647\u06ce\u0632\u06cc \u0634\u06d5\u06a9\u06d5\u062a \u062f\u06d5\u06a9\u0631\u062f. \u06a9\u0627\u062a\u06ce \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u067e\u06ce\u06cc\u0627\u0646 \u06af\u0648\u062a: \u063a\u0644\u0644\u0648\u0648\u0631 \u0645\u06d5\u0628\u06d5! \u062e\u0648\u062f\u0627 \u067e\u06cc\u0627\u0648\u06cc \u0644\u06d5 \u062e\u06c6 \u062f\u06d5\u0631\u0686\u0648\u0648\u06af\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce\u0646\u061b',
    '\u0628\u06d5\u0648\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5 \u062a\u06c6\u06cc \u062f\u0627\u0648\u06d5\u060c \u0628\u06d5 \u0634\u0648\u06ce\u0646 \u0645\u0627\u06b5\u06cc \u0633\u06d5\u06b5\u0627\u0648\u06d5 \u0628\u06d5! \u0628\u06d5\u0634\u06cc \u062e\u06c6\u0634\u062a \u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0644\u06d5 \u0628\u06cc\u0631 \u0645\u06d5\u06a9\u06d5! \u0648\u06d5\u06a9 \u062e\u0648\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0648\u0648\u06cc\u060c \u0686\u0627\u06a9\u06d5\u06cc \u0628\u06a9\u06d5 \u0648 \u0644\u06d5\u0645 \u0639\u06d5\u0631\u0632\u06d5\u0634\u062f\u0627 \u0645\u06d5\u06a9\u06d5\u0648\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc! \u062e\u0648\u062f\u0627 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0645\u0646 \u0647\u06d5\u0645\u06d5\u060c \u06af\u0634\u062a \u0644\u06d5 \u0632\u0627\u0646\u0633\u062a\u06cc \u062e\u06c6\u0645 \u0695\u0627\u06cc\u06d5. \u0628\u06c6\u0686 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc \u0646\u06d5\u0632\u0627\u0646\u06cc\u0628\u0648\u0648 \u06a9\u06d5 \u067e\u06ce\u0634 \u0626\u06d5\u0648\u06cc\u0634 \u062e\u0648\u062f\u0627 \u0686\u06d5\u0646 \u0686\u06cc\u0646\u06cc \u0628\u06d5 \u0642\u0695\u062f\u0627\u061b \u06a9\u06d5 \u0644\u06d5\u0648\u06cc\u0634 \u0632\u06c6\u0631 \u0628\u06d5\u0647\u06ce\u0632\u062a\u0631 \u0648 \u0628\u06d5\u062f\u06d5\u0633\u062a \u0648 \u067e\u06ce\u0648\u06d5\u0646\u062f\u062a\u0631\u06cc\u0634 \u0628\u0648\u0648\u0646\u061f \u0644\u06d5\u0648 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06d5 - \u0644\u06d5\u0645\u06d5\u0695 \u062a\u0627\u0648\u0627\u0646\u06af\u06d5\u0644\u06cc\u0627\u0646 - \u0646\u0627\u067e\u0631\u0633\u0646\u06d5\u0648\u06d5.',
    '\u0628\u06d5 \u062a\u06d5\u0646\u062a\u06d5\u0646\u06d5 \u0648 \u0633\u06d5\u0646\u0633\u06d5\u0646\u06d5\u0648\u06d5 [\u0695\u06c6\u0698\u06ce] \u0647\u0627\u062a\u06d5 \u0646\u0627\u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u062e\u06c6\u0632\u06cc \u0645\u06d5\u0634 \u0648\u06d5\u06a9 \u0642\u0627\u0695\u0648\u0648\u0646 \u062f\u06d5\u0648\u06b5\u06d5\u0645\u06d5\u0646 \u0628\u0627\u06cc\u0646\u061b \u0626\u0627\u06cc \u0644\u06d5\u0648 \u0628\u06d5\u062e\u062a\u06d5\u06cc \u0626\u06d5\u0648 \u0647\u06d5\u06cc\u06d5\u062a\u06cc!',
    '\u0647\u06c6\u0632\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0647\u06d5\u06cc \u0645\u0627\u06b5 \u0648\u06ce\u0631\u0627\u0646! \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce\u062a \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u06ce\u060c \u0626\u06d5\u0648 \u062e\u06d5\u06b5\u0627\u062a\u06cc \u062e\u0648\u0627 \u062f\u06d5\u06cc\u062f\u0627\u062a\u06ce\u060c \u0632\u06c6\u0631 \u0686\u0627\u06a9\u062a\u0631\u06d5 \u0648 \u0626\u06d5\u0648 \u062e\u06d5\u06b5\u0627\u062a\u06d5\u0634 \u0647\u06d5\u0631 \u0628\u06d5\u0634\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631\u0627\u0646\u06d5.',
    '\u0633\u0627 \u0642\u0627\u0695\u0648\u0648\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0645\u0627\u06b5\u06cc\u06d5\u0648\u06d5 \u0695\u06c6\u0628\u0631\u062f \u0628\u06d5 \u0646\u0627\u062e\u06cc \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627. \u0647\u06cc\u0686 \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u06cc\u0634\u06cc \u0646\u06d5\u0628\u0648\u0648 \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc \u0628\u067e\u0627\u0631\u06ce\u0632\u06ce \u0648 \u062e\u06c6\u0634\u06cc \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u0646\u06d5\u062f\u0627.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0648\u06ce\u0646\u06ce \u062e\u06c6\u0632\u06af\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062e\u0648\u0627\u0633\u062a\u060c \u0695\u06c6\u0698\u06cc \u062f\u0648\u0627\u06cc\u06cc \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u067e\u06d5\u06a9\u0648\u0648 \u0644\u06d5\u0648 \u0633\u06d5\u06cc\u0631\u06d5! \u0648\u0627 \u062f\u06cc\u0627\u0631\u06d5 \u06a9\u06d5 \u0695\u06c6\u0632\u06cc \u0639\u06d5\u0628\u062f\u0627\u0646 - \u0632\u06c6\u0631 \u0628\u06ce \u06cc\u0627\u0646 \u06a9\u06d5\u0645 - \u0628\u06d5\u0633\u062a\u06d5 \u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0631 \u062e\u0648\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u0628\u06c6 \u0646\u06d5\u06a9\u0631\u062f\u0628\u0627\u06cc\u0646\u060c \u0626\u06ce\u0645\u06d5\u0634\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u062e\u06cc \u0632\u06d5\u0645\u06cc\u0646 \u0695\u06c6 \u062f\u06d5\u0628\u0631\u062f. \u0648\u0627 \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u0628\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0645\u0627\u06b5\u06d5\u06a9\u06d5\u06cc \u062f\u0648\u0627\u0695\u06c6\u0698\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u0627\u062f\u06d5\u0646\u06ce\u06cc\u0646\u060c \u06a9\u06d5 \u0644\u06d5\u0645 \u0639\u06d5\u0631\u0632\u06d5 \u0646\u0627\u06cc\u0627\u0646\u06d5\u0648\u06ce \u062e\u06c6 \u0628\u06d5 \u0632\u0644\u062a\u0631 \u0628\u0632\u0627\u0646\u0646 \u0648 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06cc \u06a9\u06d5\u0646. \u067e\u0627\u0634\u06d5\u0695\u06c6\u0698\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u06d5\u06a9\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06a9\u06ce \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0686\u0627\u06a9 \u0628\u06a9\u0627\u060c \u0686\u0627\u06a9\u062a\u0631 \u0644\u06d5\u0648\u06cc \u062f\u06ce\u062a\u06d5\u0648\u06d5 \u0695\u06ce\u061b \u0647\u06d5\u0631\u06a9\u06ce\u0634 \u0626\u0627\u06a9\u0627\u0631\u06cc \u062e\u0631\u0627 \u0628\u0646\u060c \u0647\u06d5\u0631 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u06a9\u0631\u062f\u06d5\u062e\u0631\u0627\u067e\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062c\u0632\u06cc\u0627 \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u06a9\u06d5 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u06cc \u06a9\u0631\u062f\u06d5 \u0626\u06d5\u0631\u06a9\u06cc \u0633\u06d5\u0631\u0634\u0627\u0646\u06cc \u062a\u06c6\u060c \u062e\u0627\u062a\u0631 \u062c\u06d5\u0645 \u0628\u06d5 \u062f\u06d5\u062a\u0628\u0627\u062a\u06d5\u0648\u06d5 \u0626\u06d5\u0648 \u0634\u0648\u06ce\u0646\u06d5\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u067e\u06ce\u062f\u0627\u06cc. \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u0632\u0627\u0646\u0627\u062a\u0631\u06d5 \u06a9\u06d5 \u06a9\u06ce \u0644\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u062f\u06d5\u0695\u0648\u0627 \u0648 \u06a9\u06ce \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u062f\u0627\u06cc\u06d5.',
    '\u062e\u06c6 \u0626\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a \u0646\u06d5\u0628\u0648\u0648\u0627\u06cc\u06d5\u060c \u062a\u06c6 \u0647\u06d5\u0631 \u0628\u06d5 \u0647\u06cc\u0648\u0627 \u0646\u06d5\u0628\u0648\u0648\u06cc \u06a9\u06d5 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u062a \u0628\u06c6 \u06a9\u0644 \u06a9\u0631\u06ce\u061b \u0633\u0627 \u062a\u06c6\u0634 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u067e\u0634\u062a\u06cc\u0648\u0627\u0646\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0645\u06d5\u06a9\u06d5!',
    '\u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u062a \u0628\u06c6 \u06a9\u0644 \u06a9\u0631\u0627\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u0628\u06ce \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u06b5\u06d5 \u0628\u06a9\u06d5\u06cc. \u0647\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6 \u06af\u0627\u0632\u06cc \u0628\u06a9\u06d5 \u0648 \u0647\u06cc\u0686 \u06af\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648 \u0628\u062a\u067e\u06d5\u0631\u0633\u062a\u0627\u0646\u06d5 \u0645\u06d5\u0628\u06d5.',
    '\u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 \u0647\u0627\u0646\u0627 \u0628\u06c6 \u0647\u06cc\u0686 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06ce\u06a9\u06cc \u062a\u0631 \u0645\u06d5\u0628\u06d5. \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u067e\u06d5\u0631\u0633\u062a\u06cc\u0648\u06ce\u06a9\u06cc \u062a\u0631 \u0646\u06cc\u06cc\u06d5. \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u062f\u06d5\u0686\u06ce. \u0641\u06d5\u0631\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u06d5\u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u0628\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0645\u06cc\u0645.',
    '\u0626\u0627\u062e\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0644\u0627\u06cc\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5 \u0628\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0626\u06cc\u062a\u0631 \u0648\u0627\u0632\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u0647\u06ce\u0646\u0631\u06ce \u0648 \u062a\u0648\u0648\u0634\u06cc \u0647\u06cc\u0686 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06ce \u0646\u0627\u0628\u0646\u061f',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631\u0644\u06d5\u0645\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5 \u062a\u0627\u0642\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5. \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u062e\u0648\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0695\u0627\u0633\u06cc\u0627\u0646 \u06af\u0648\u062a\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u0631\u06c6\u0632\u0646\u0646\u060c \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u062f\u06a9\u0627\u0631\u0646\u060c \u0648\u0627\u062f\u06d5\u0632\u0627\u0646\u0646 \u0646\u0627\u06a9\u06d5\u0648\u0646\u06d5 \u0628\u06d5\u0631 \u0642\u06d5\u0645\u0686\u06cc \u0626\u06ce\u0645\u06d5\u0648\u06d5\u061f \u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u0644\u06ce\u06a9 \u062f\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0647\u0648\u0645\u06ce\u062f\u06cc \u0648\u0627\u06cc\u06d5 \u0628\u06af\u0627 \u0628\u06d5 \u062f\u06cc\u062f\u0627\u0631\u06cc \u062e\u0648\u062f\u0627\u060c \u0648\u06d5\u062e\u062a\u06ce \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u062f\u0627\u06cc\u0646\u0627\u0648\u06d5\u060c \u0647\u06d5\u0631\u0628\u06cc\u0646\u0627 \u0647\u0627\u062a. \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062f\u06d5\u0686\u06ce\u062a\u06d5 \u062e\u06d5\u0632\u0627\u06cc\u06d5\u060c \u062e\u06d5\u0628\u0627\u062a\u06ce\u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u0627. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u062e\u0631\u0627\u067e\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0633\u0695\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u0627\u0634\u062a\u0631 \u0644\u06d5\u0648\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u060c \u0626\u06ce\u0645\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0645\u0631\u06c6\u0645\u0627\u0646 \u0695\u0627\u0633\u067e\u0627\u0631\u062f \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u0627\u06a9 \u0648\u0628\u0627\u0648\u06a9\u06cc \u062e\u06c6\u06cc \u0686\u0627\u06a9\u06d5 \u0628\u06a9\u0627. \u0647\u06d5\u0631\u06af\u0627 \u0626\u06d5\u0648\u0627\u0646 \u0647\u06d5\u06b5\u06cc\u0627\u0646 \u067e\u06ce\u0686\u0627\u06cc\u060c \u0647\u06d5\u0631 \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0646\u06d5\u0632\u0627\u0646\u06cc\u062a\u06d5\u0648\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u0645\u0646 \u0628\u0695\u06cc\u0627\u0631 \u0628\u062f\u06d5\u06cc\u060c \u062a\u06c6 \u0646\u0627\u0628\u06ce \u0628\u06d5 \u0642\u0633\u06d5\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u06cc. \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u062a\u0627\u0646 \u0647\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u0645\u061b \u0626\u06d5\u0648\u0633\u0627 \u062e\u06c6\u0645 \u062d\u0627\u06b5\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0645\u060c \u0626\u06ce\u0648\u06d5 \u0686 \u06a9\u0627\u0631\u06ce\u06a9\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u0695\u06d5\u06af\u06d5\u06b5 \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u062e\u06d5\u06cc\u0646.',
    '\u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645\u062f\u0627 \u0626\u06cc \u0648\u0627\u0634 \u0647\u06d5\u06cc\u06d5\u060c \u0626\u06ce\u0698\u06ce: \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5\u061b \u0647\u06d5\u0631\u06af\u0627\u0634 \u0644\u06d5\u0633\u06d5\u0631 \u062e\u0648\u062f\u0627\u0646\u0627\u0633\u06cc\u0646 \u0626\u0627\u0632\u0627\u0631 \u062f\u0631\u0627\u060c \u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0626\u0627\u0632\u0627\u0631\u06cc \u062e\u06d5\u06b5\u06a9 \u0648\u06d5\u06a9\u0648\u0648 \u0633\u0632\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648\u0627\u06cc\u06d5. \u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u06cc\u0627\u0631\u06cc\u06ce\u06a9 \u0628\u06ce\u060c \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5\u0634\u062a\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u0648\u0648\u06cc\u0646. \u0645\u06d5\u0631 \u062e\u0648\u0627 \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u0633\u06cc\u0646\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u062f\u0627\u06cc\u06d5\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0631 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u062e\u0648\u0627 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0628\u0627\u0634 \u062f\u06d5\u0646\u0627\u0633\u06ce\u062a \u0648 \u062f\u0648\u0648\u0695\u0627\u0632\u0627\u0646\u06cc\u0634 \u0628\u0627\u0634 \u062f\u06d5\u0646\u0627\u0633\u06ce.',
    '\u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u062f\u06d5\u06b5\u06ce\u0646: \u06a9\u06d5\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0695\u06ce\u06af\u0627\u06cc \u0626\u06ce\u0645\u06d5\u060c \u0626\u06c6\u0628\u0627\u06b5\u06cc \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0648\u06d5\u0645\u0644 \u062f\u06d5\u06af\u0631\u06cc\u0646\u061b \u06a9\u06d5 \u0646\u0627\u0634\u06a9\u0631\u06ce \u0647\u06cc\u0686 \u0626\u06c6\u0628\u0627\u06b5\u06cc \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u06cc\u0627\u0646 \u0648\u06d5\u0633\u062a\u06c6 \u0628\u06af\u0631\u0646\u061b \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u0628\u0627\u0631\u06cc \u06af\u0631\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0628\u0627\u0631\u06cc \u06af\u0631\u0627\u0646\u06cc \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u06cc\u0634 - \u0648\u06d5\u0644 \u0628\u0627\u0631\u06cc \u06af\u0631\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 - \u0647\u06d5\u06b5\u062f\u06d5\u06af\u0631\u0646 \u0648 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u0648\u062e\u062a\u0627\u0646 \u06a9\u0631\u062f\u0646\u06d5\u060c \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0628\u06d5\u0631 \u067e\u0631\u0633\u06cc\u0627\u0631\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0646\u0648\u0648\u062d\u0645\u0627\u0646 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5 \u0644\u0627\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc\u061b \u0647\u06d5\u0632\u0627\u0631 \u0633\u0627\u06b5\u06cc \u067e\u06d5\u0646\u062c\u0627 \u0633\u0627\u06b5 \u06a9\u06d5\u0645\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648\u0645\u0627\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u0627\u0647\u06d5\u0642 \u0628\u0648\u0648\u0646\u060c \u062a\u06c6\u0641\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0695\u0627\u0645\u0627\u06b5\u06cc.',
    '\u0626\u06ce\u0645\u06d5 \u062e\u06c6\u06cc \u0648 \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5 \u06af\u06d5\u0645\u06cc\u06d5\u06a9\u06d5\u062f\u0627 \u0628\u0648\u0648\u0646\u060c \u0646\u06d5\u062c\u0627\u062a\u0646 \u062f\u0627\u0646. \u0626\u06d5\u0645\u06d5\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u06cc\u0634 \u0648\u06d5\u0628\u06cc\u0631 \u0628\u06ce\u0646\u06d5\u061b \u06a9\u0627\u062a\u06ce \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0626\u06ce\u0648\u06d5 \u062e\u0648\u0627\u0628\u067e\u06d5\u0631\u0633\u062a\u0646 \u0648 \u0644\u06d5\u0648 \u0628\u062a\u0631\u0633\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06cc\u0632\u0627\u0646\u0646 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u062e\u06c6\u062a\u0627\u0646\u06d5.',
    '\u0644\u06d5 \u0628\u0631\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u062e\u0648\u062f\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646 \u060c \u0628\u062a \u06af\u06d5\u0644\u06ce\u06a9 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646 \u0648 \u062f\u0631\u06c6\u06cc \u06af\u06d5\u0648\u0631\u06d5\u0634 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646. \u0626\u06d5\u0648\u06cc \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u0646 \u0648 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0646\u060c \u062e\u06c6 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0695\u06c6\u0632\u06cc\u0648 \u0628\u062f\u06d5\u0646. \u0695\u06c6\u0632\u06cc \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062f\u0627\u0648\u0627 \u06a9\u06d5\u0646 \u0648 \u0628\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646 \u0648 \u0634\u0648\u06a9\u0631\u06cc \u0628\u06a9\u06d5\u0646. \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u062a\u0627\u0646 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695 \u0628\u06d5 \u0645\u0646 \u0646\u06d5\u06a9\u06d5\u0646\u060c \u06a9\u06c6\u0645\u06d5\u06b5\u0627\u0646\u06cc \u0628\u06d5\u0631 \u0644\u06d5 \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06d5\u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f. \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5 \u062c\u06af\u06d5 \u0644\u06d5 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u06cc \u067e\u06d5\u06cc\u0627\u0645\u06d5\u06a9\u06d5\u06cc - \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 - \u0647\u06cc\u0686\u06cc \u062a\u0631\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0647\u06ce\u0646\u062f\u06d5 \u0695\u0627\u0646\u06d5\u0645\u0627\u0648\u0646 \u06a9\u06d5 \u0628\u0632\u0627\u0646\u0646 \u062e\u0648\u062f\u0627 \u0686\u06c6\u0646 \u0634\u062a \u0648\u06d5\u062f\u06cc \u062f\u06ce\u0646\u06ce \u0648 \u067e\u0627\u0634 \u0646\u06d5\u0645\u0627\u0646 \u062f\u06d5\u06cc\u0647\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u061f \u0626\u06d5\u0645\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u0633\u0627\u0646\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u06af\u06d5\u0634\u062a\u06ce \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0628\u062f\u06d5\u0646 \u0648 \u062a\u0645\u0627\u0634\u0627 \u06a9\u06d5\u0646\u060c \u0686\u06ce \u06a9\u0631\u062f\u0646\u06cc \u0686\u06c6\u0646 \u062f\u06d5\u0633 \u067e\u06ce \u06a9\u0631\u062f\u061f \u067e\u0627\u0634\u0627\u0646 [\u0647\u06d5\u0631] \u062e\u0648\u0627 \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0646\u06cc\u061b \u067e\u0627\u0634\u06cc\u0646\u06cc\u0634 \u0648\u06d5\u062f\u06cc \u062f\u06d5\u0647\u06ce\u0646\u06ce. \u062e\u0648\u062f\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u06c6\u06cc\u060c \u0647\u06d5\u0631\u06a9\u06ce \u0626\u0627\u0632\u0627\u0631 \u0628\u062f\u0627 \u062f\u06d5\u06cc\u062f\u0627 \u0648\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u062e\u0627 \u0648\u060c \u0647\u06d5\u0631 \u062f\u06d5\u0648\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u062e\u06c6\u06cc.',
    '\u0644\u06d5 \u0639\u06d5\u0631\u0632 \u0648 \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06ce \u0628\u0646\u060c \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u062e\u06c6 \u0695\u0632\u06af\u0627\u0631 \u0628\u06a9\u06d5\u0646\u061b \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0634 \u0647\u06cc\u0686 \u0647\u0627\u0646\u0627 \u0648 \u06cc\u0627\u0631\u06cc\u06a9\u0627\u0631\u0648\u0648 \u0646\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0628\u06d5 \u062f\u06cc\u062f\u0627\u0631\u06cc\u060c \u0646\u0627\u0647\u0648\u0645\u06ce\u062f \u0628\u0648\u0648\u0646 \u0644\u06d5 \u0628\u06d5\u0632\u06d5\u0645 \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646 \u0628\u06d5\u0634\u06cc\u0627\u0646\u06d5.',
    '\u062a\u06d5\u0646\u06cc\u0627 \u0648\u06d5\u0631\u0627\u0645\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5 \u0628\u0648\u0648\u060c \u06a9\u06d5: \u0628\u06d5 \u06a9\u0648\u0634\u062a\u0646 \u0628\u06cc\u06a9\u0648\u0698\u0646 \u06cc\u0627\u0646 \u0628\u06cc\u0633\u0648\u0648\u062a\u06ce\u0646\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0626\u0627\u06af\u0631\u06cc \u067e\u0627\u0631\u0627\u0633\u062a. \u0626\u06d5\u0645 \u0695\u0648\u0648\u062f\u0627\u0648\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0695\u0648\u0627\u0646\u060c \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062a\u06ce\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0628\u0631\u06cc \u062e\u0648\u062f\u0627 \u0628\u06c6\u06cc\u06d5 \u0628\u0648\u062a\u0627\u0646 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u06a9\u06d5 \u0644\u06d5 \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06cc\u062f\u0627 \u0626\u06ce\u0648\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u062a\u0627\u0646 \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u061b \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u0627 \u062d\u0627\u0634\u0627 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0646\u06d5\u0641\u0631\u06cc\u0646 \u0628\u06c6 \u06cc\u06d5\u06a9\u062a\u0631 \u062f\u06d5\u0646\u06ce\u0631\u0646\u061b \u062c\u06ce\u0634\u062a\u0627\u0646 \u0644\u06d5 \u0626\u0627\u06af\u0631\u062f\u0627 \u062f\u06d5\u0628\u06ce \u0648 \u06a9\u06d5\u0633\u06cc\u0634 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062a\u0627\u0646 \u0646\u0627\u062f\u0627.',
    '\u0626\u06cc\u062a\u0631 \u0644\u0648\u0648\u062a \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce \u0647\u06ce\u0646\u0627. \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u0627 \u0645\u0646 \u06a9\u06c6\u0686 \u062f\u06d5\u06a9\u06d5\u0645 \u0628\u06d5\u0631\u06d5\u0648 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645. \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5 \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06cc\u0633\u062d\u0627\u0642 \u0648 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628\u0645\u0627\u0646 \u0628\u06d5 \u0648\u06cc \u0628\u06d5\u062e\u0634\u06cc \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0648 \u06a9\u062a\u06ce\u0628\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0628\u06d5\u0634\u06cc \u0646\u06d5\u062a\u06d5\u0648\u06d5\u06cc \u0626\u06d5\u0648\u061b \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u06ce\u0645\u06d5\u06cc \u067e\u06ce \u06af\u06d5\u06cc\u0634\u062a \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0634 \u0626\u06d5\u0648 \u0644\u06d5 \u067e\u06cc\u0627\u0648\u06d5\u0686\u0627\u06a9\u06d5\u06a9\u0627\u0646\u06d5.',
    '\u0628\u0627\u0633\u06cc \u00ab\u0644\u0648\u0648\u062a\u00bb \u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06a9\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u06af\u0648\u062a: \u0626\u06ce\u0648\u06d5 \u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648\u0646\u062f\u06d5 \u0634\u0648\u0648\u0631\u06d5\u06cc\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u06a9\u06d5\u0633 \u0628\u06d5\u0631 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0648\u0627\u06cc \u0646\u06d5\u06a9\u0631\u062f.',
    '\u0626\u06ce\u0648\u06d5 \u0633\u0648\u0627\u0631\u06cc \u067e\u06cc\u0627\u0648\u0627\u0646 \u062f\u06d5\u0628\u0646 \u0648 \u0695\u06ce\u06af\u0631\u06cc\u0648 \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u067e\u06cc\u0634\u06d5 \u0648 \u0644\u06d5 \u062f\u06cc\u0648\u06d5\u062e\u0627\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646\u062f\u0627 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0646\u0627\u0634\u06cc\u0631\u06cc\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u061f \u062a\u06d5\u0646\u06cc\u0627 \u0648\u06d5\u0631\u0627\u0645\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5 \u0628\u0648\u0648: \u0626\u06d5\u06af\u06d5\u0631 \u0642\u0633\u06d5\u06a9\u0627\u0646\u062a \u0695\u0627\u0633\u062a\u0646\u060c \u0626\u0627\u0632\u0627\u0631\u06cc \u062e\u0648\u0627\u0645\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u0646\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648 \u0647\u06c6\u0632\u06d5 \u0628\u06d5\u062f\u0641\u06d5\u0695\u06d5\u060c \u06cc\u0627\u0631\u06cc\u062f\u06d5 \u0644\u06d5 \u062a\u06c6 \u062f\u06d5\u062e\u0648\u0627\u0632\u0645.',
    '\u0626\u06d5\u0648\u0633\u0627\u0634 \u06a9\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0645\u0698\u062f\u06d5\u06cc\u0627\u0646 \u0628\u0631\u062f \u0628\u06c6 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u062f\u06d5\u0628\u06ce \u062e\u06d5\u06b5\u06a9\u06cc \u0626\u06d5\u0645 \u0634\u0627\u0631\u06d5 \u0642\u0695 \u0628\u06a9\u06d5\u06cc\u0646\u060c \u0686\u0648\u0646\u06a9\u06d5 \u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u0646.',
    '\u06af\u0648\u062a\u06cc: \u00ab\u0644\u0648\u0648\u062a\u00bb \u06cc\u0634 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u06d5. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646 \u06a9\u06ce\u06cc \u0644\u06ce\u06cc\u06d5\u060c \u062e\u06c6\u06cc \u0648 \u062e\u0627\u0648\u062e\u06ce\u0632\u0627\u0646\u06cc - \u062c\u06af\u06d5 \u0644\u06d5 \u0698\u0646\u06d5\u06a9\u06d5\u06cc - \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u06a9\u06d5 \u0626\u06d5\u0648 \u0644\u06d5 \u0628\u06d5 \u062c\u06ce \u0645\u0627\u0648\u0627\u0646\u06d5.',
    '\u0648\u06d5\u062e\u062a\u06ce \u06a9\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0686\u0648\u0648\u0646\u06d5 \u062c\u06d5\u0645 \u0644\u0648\u0648\u062a\u060c \u0628\u06d5 \u062f\u06cc\u062a\u0646\u06cc\u0627\u0646 \u0634\u06b5\u06d5\u0698\u0627 \u0648 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5\u06cc \u0647\u0627\u062a\u06ce. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0645\u06d5\u062a\u0631 \u0633\u06d5 \u0648 \u062e\u06d5\u0645 \u0645\u06d5\u062e\u06c6\u061b \u0626\u06ce\u0645\u06d5 \u062a\u06c6\u0634 \u0648 \u062e\u06ce\u0632\u0627\u0646\u06d5\u06a9\u06d5\u062a - \u063a\u06d5\u06cc\u0631\u06cc \u0698\u0646\u062a - \u0695\u0632\u06af\u0627\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u06a9\u06d5 \u0626\u06d5\u0648 \u0644\u06d5 \u0628\u06d5 \u062c\u06ce \u0645\u0627\u0648\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0633\u06d5\u0631 \u062e\u06d5\u06b5\u06a9\u06cc \u0626\u06d5\u0645 \u0634\u0627\u0631\u06d5 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9 - \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 - \u062f\u06d5\u0628\u0627\u0631\u06ce\u0646\u06cc\u0646\u060c \u0633\u0632\u0627\u06cc \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u0627\u0646\u06cc\u0627\u0646 \u0628\u06ce.',
    '\u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645\u0627\u0646 \u0644\u06ce \u0628\u06d5 \u062c\u06ce \u0647\u06ce\u0634\u062a\u061b \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0695\u0627\u062f\u06d5\u0645\u06ce\u0646\u0646.',
    '\u0628\u06c6 \u0645\u06d5\u062f\u06cc\u06d5\u0646\u06cc\u0634 \u0634\u0648\u0639\u06d5\u06cc\u0628\u06cc \u0628\u0631\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0644\u0627\u06cc\u0627\u0646. \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0626\u06d5\u0634\u06ce \u062e\u0648\u062f\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0628\u06d5\u062a\u06d5\u0645\u0627\u0634 \u0628\u0646 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0648 \u0644\u06ce \u0695\u0627\u062f\u06d5\u0628\u06ce\u061b \u0644\u06d5\u0645 \u0639\u06d5\u0631\u0632\u06d5\u0634\u062f\u0627 \u0645\u0644 \u0644\u06d5 \u062e\u0631\u0627\u067e\u06a9\u0627\u0631\u06cc \u0645\u06d5\u0646\u06ce\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f. \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5\u0631\u0632\u06d5 \u0648\u0627\u06cc \u062f\u0627\u06af\u0631\u062a\u0646\u060c \u06a9\u06d5 \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0695\u0627.',
    '\u0647\u06c6\u0632\u06cc \u0639\u0627\u062f \u0648 \u0633\u06d5\u0645\u0648\u0648\u062f\u06cc\u0634\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u0642\u0695 \u062f\u0627\u0646\u061b \u06a9\u06d5 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06d5\u0648\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0626\u06d5\u0645\u06d5\u062a\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0648\u0648\u06d5. \u0634\u06d5\u06cc\u062a\u0627\u0646 \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u06cc \u062c\u0648\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0646 \u0648 \u0695\u06ce\u06cc \u0644\u06ce \u06af\u0631\u062a\u0646\u060c \u062f\u06d5\u0634\u06cc\u0627\u0646 \u0632\u0627\u0646\u06cc [\u0647\u06d5\u0642 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642 \u0647\u06d5\u06b5\u0627\u0648\u06ce\u0631\u0646].',
    '\u0642\u0627\u0695\u0648\u0648\u0646\u06cc\u0634 \u0648 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u06cc\u0634 \u0648 \u0647\u0627\u0645\u0627\u0646\u06cc\u0634 [\u0647\u06d5\u0631 \u0628\u06d5\u0648 \u062f\u06d5\u0631\u062f\u06d5 \u0686\u0648\u0648\u0646]. \u062f\u06cc\u0627\u0631\u06d5 \u0645\u0648\u0648\u0633\u0627 \u06af\u06d5\u0644\u06ce \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u0628\u06c6\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u06c6\u06cc\u0627\u0646 - \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 - \u0644\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u0628\u06d5 \u0632\u0644\u062a\u0631 \u0632\u0627\u0646\u06cc. \u0646\u06d5\u0634\u06cc\u0627\u0646 \u062a\u0648\u0627\u0646\u06cc \u0628\u06d5 \u062f\u06d5\u0633 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u062f\u06d5\u0631\u0628\u0686\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631\u06cc\u06d5\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0645\u0627\u0646 \u0628\u06d5 \u062a\u0627\u0648\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06af\u0631\u062a. \u062c\u0627 \u0647\u06ce\u0646\u062f\u06cc\u06a9\u06cc\u0627\u0646 \u0628\u0627\u06cc \u0632\u06c6\u0631 \u062a\u0648\u0646\u062f\u06cc \u067e\u0695 \u0644\u06d5 \u0686\u06d5\u0648\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646\u060c \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u0627\u0646 \u0646\u0627\u0631\u06d5\u062a\u06d5\u06cc \u0632\u0644 \u062f\u0627\u06cc \u06af\u0631\u062a\u0646 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5 \u0646\u0627\u062e\u06cc \u0632\u06d5\u0648\u06cc\u062f\u0627 \u0695\u06c6 \u0628\u0631\u062f\u061b \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5 \u0626\u0627\u0648\u062f\u0627 \u0646\u0648\u0642\u0645 \u06a9\u0631\u062f\u0646. \u062e\u0648\u0627 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u062f\u06c6\u0633\u06af\u06d5\u0644\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u0648\u0648\u06d5 - \u06af\u06d5\u0631 \u0628\u0632\u0627\u0646\u0646 - \u0648\u06d5\u06a9\u0648\u0648 \u06a9\u0627\u06a9\u06b5\u06d5\u0645\u0648\u0648\u0634\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u06a9\u06d5 \u062e\u0627\u0646\u0648\u0648\u06cc \u0628\u06c6 \u062e\u06c6\u06cc \u062a\u06d5\u0646\u06cc\u0648\u06d5. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0627\u0646\u0648\u0648\u06cc \u06a9\u0627\u06a9\u06b5\u06d5\u0645\u0648\u0648\u0634\u0627\u0646 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0645\u0627\u06b5\u0627\u0646 \u0633\u0633 \u062a\u0631\u06d5.',
    '\u062e\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u06c6\u06cc\u061b \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0628\u06d5\u0646. \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u0646\u06d5\u0632\u06cc\u0644\u0627\u0646\u06d5 \u0628\u06c6 \u062e\u06d5\u06b5\u06a9 \u062f\u06ce\u0646\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0632\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0628\u06ce\u060c \u062a\u06ce\u06cc \u0646\u0627\u06af\u06d5\u0646.',
    '\u062e\u0648\u0627 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0628\u06d5 \u0647\u06d5\u0642\u0627\u0646\u06d5\u062a \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u0646. \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0626\u06d5\u0645\u06d5 \u067e\u06d5\u0646\u062f\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0646.',
    '\u0626\u06d5\u0648\u06cc \u0628\u06c6\u062a \u0628\u06d5\u0695\u06ce \u06a9\u0631\u0627\u0648\u06d5 - \u0644\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 - \u0628\u06cc\u062e\u0648\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u0646\u0648\u06ce\u0698\u06cc \u0628\u06a9\u06d5\u061b \u0646\u0645\u06ce\u0698 \u062e\u0648\u06ce\u0646\u062f\u0646 [\u0645\u0631\u06c6] \u0644\u06d5 \u06a9\u0627\u0631\u06cc \u0646\u0627\u0634\u06cc\u0631\u06cc\u0646 \u0648 \u0646\u0627\u0695\u06d5\u0648\u0627 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u06ce. \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0648\u062a\u0648\u0648\u06ce\u0698\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0648 \u067e\u06d5\u0695\u06cc \u0646\u06d5\u0631\u0645 \u0648 \u0646\u06cc\u0627\u0646\u06cc \u0642\u0633\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u062a\u06d5\u06a9\u0627 \u0628\u06a9\u06d5\u0646\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5\u062a\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc\u0627\u0646 \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5. \u0626\u06ce\u0648\u06d5 \u0628\u06ce\u0698\u0646: \u0628\u06d5\u0648 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5 \u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0645\u06d5 \u0647\u0627\u062a\u06d5 \u062e\u0648\u0627\u0631 \u0648 \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u061b \u062e\u0648\u062f\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u0648 \u062e\u0648\u062f\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce\u06a9\u06d5\u060c \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5\u0648 \u062f\u06d5\u0633\u067e\u06ce\u0631\u06cc\u0646.',
    '\u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u0634\u0645\u0627\u0646 \u0647\u06d5\u0646\u0627\u0631\u062f \u0628\u06c6 \u062a\u06c6\u061b \u062c\u0627 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u0626\u06ce\u0645\u06d5 \u06a9\u062a\u06ce\u0628\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627\u0648\u0646\u060c \u062f\u06d5\u06cc\u0633\u06d5\u0644\u0645\u06ce\u0646\u0646. \u0644\u06d5\u0648\u0627\u0646\u06cc\u0634\u062f\u0627 [\u062e\u06d5\u06b5\u06a9\u06cc \u0645\u06d5\u06a9\u06a9\u06d5] \u0626\u06cc \u0648\u0627 \u0647\u06d5\u0646 \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce \u062f\u06ce\u0646\u0646. \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc\u0634\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0646\u06cc\u06cc\u06d5 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627\u06cc \u0646\u06d5\u0628\u06ce.',
    '\u062a\u06c6 \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0642\u0648\u0631\u0639\u0627\u0646\u062a \u0628\u06c6 \u0628\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u0646\u06d5\u062a \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u0647\u06cc\u0686 \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc \u0648\u06d5\u062e\u0648\u06ce\u0646\u06cc \u06cc\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6\u062a \u0628\u06cc\u0646\u0648\u0648\u0633\u06cc\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0648\u0627\u0628\u0627\u060c \u062f\u06b5 \u0686\u06d5\u067e\u06d5\u06b5\u0627\u0646 \u06af\u0648\u0645\u0627\u0646\u06cc\u0627\u0646 \u0644\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u0628\u0648\u0648.',
    '\u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0642\u0648\u0631\u0639\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06af\u06d5\u0644\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0633 \u0644\u06d5 \u0633\u06cc\u0646\u06d5\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u062f\u0627\u060c \u06a9\u06d5 \u0632\u0627\u0646\u0633\u062a\u06cc\u0627\u0646 \u067e\u06ce \u062f\u0631\u0627\u0648\u06d5. \u063a\u06d5\u06cc\u0631 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06a9\u0627\u0631\u0627\u0646\u06cc\u0634\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0646\u06cc\u06cc\u06d5 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627\u06cc \u0646\u06d5\u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc\u0634\u06cc\u0627\u0646: \u0628\u06c6 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u06d5\u0648\u06d5 \u0646\u06cc\u0634\u0627\u0646 \u06af\u06d5\u0644\u06cc [\u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc] \u0628\u06c6 \u0646\u0627\u06cc\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u061f \u0628\u06ce\u0698\u06d5: \u0646\u06cc\u0634\u0627\u0646\u06d5\u06af\u06d5\u0644\u06cc \u0648\u0627 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u060c \u0645\u0646 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06cc \u0646\u06cc\u0634\u0627\u0646\u062f\u06d5\u0631\u0645\u060c \u0646\u06d5\u0647\u06cc\u0686\u06cc \u062a\u0631.',
    '\u0628\u06c6\u06cc\u0627\u0646 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0628\u06d5\u0633 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u062a\u06c6 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0648 \u06a9\u06d5 \u0628\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u06ce\u062a\u06d5\u0648\u06d5\u061f \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u062f\u0627 - \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627 \u062f\u06ce\u0646\u0646 - \u0628\u0632\u06cc \u0648 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u06a9\u06d5 \u062e\u0648\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0644\u06d5 \u0645\u0646 \u0648 \u0644\u06d5 \u0626\u06ce\u0648\u06d5\u060c \u0628\u06d5\u0633\u06d5. \u0647\u06d5\u0631\u0686\u06cc \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u060c \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u0695\u0648\u067e\u0648\u0648\u0686 \u0647\u06ce\u0646\u0627 \u0648\u060c \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0647\u06d5\u0631 \u0632\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6 \u062f\u0627\u0648\u06d5.',
    '\u062f\u0627\u0648\u0627\u062a \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u0632\u06c6\u0631 \u0632\u0648\u0648 \u0648\u0627 \u0628\u06a9\u06d5\u06cc \u0628\u06d5\u06b5\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0628\u06ce. \u0626\u06d5\u06af\u06d5\u0631 \u0648\u06d5\u062e\u062a \u0648 \u0633\u06d5\u062d\u0627\u062a\u06cc \u062f\u06cc\u0627\u0631\u06cc \u0646\u06d5\u06a9\u0631\u0627\u0628\u0627\u060c \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u0627\u062f\u06d5\u0628\u0627\u0631\u06cc. \u0647\u06d5\u0631\u0628\u06cc\u0646\u0627 \u0644\u06d5 \u0646\u06d5\u06a9\u0627\u0648\u06ce\u06a9\u062f\u0627 \u062a\u0648\u0648\u0634\u06cc\u0627\u0646 \u0647\u0627\u062a \u0648 \u067e\u06ce\u0634 \u0646\u0627\u062d\u06d5\u0633\u0646.',
    '\u0648\u0627 \u0628\u06d5 \u067e\u06d5\u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u062a \u0644\u06ce \u062f\u06d5\u062e\u0648\u0627\u0632\u0646\u060c \u06a9\u06d5\u0686\u06cc \u0628\u06ce \u0633\u06c6 \u062f\u06c6\u0698\u06d5\u06d5 \u062f\u06d5\u0648\u0631\u06d5\u06cc \u0644\u06d5\u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u062f\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0644\u06d5 \u0628\u0627\u0646 \u0633\u06d5\u0631 \u0648 \u0644\u06d5 \u0698\u06ce\u0631 \u067e\u0627\u0648\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u0627\u06cc\u0627\u0646 \u062f\u06d5\u067e\u06c6\u0634\u06ce\u062a \u0648 \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u06ce: \u062f\u06d5\u0633\u0627 \u0633\u0632\u0627\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u0686\u06ce\u0698\u0646.',
    '\u0626\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0695\u0648\u0627\u0646 \u0648 \u062e\u06c6 \u0628\u06d5 \u0639\u06d5\u062f\u06d5\u0645 \u062f\u06d5\u0632\u0627\u0646\u0646! \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0645\u0646 \u0628\u06d5\u0631\u0641\u0695\u06d5\u0647\u06d5\u060c \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u0645\u0646 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646.',
    '\u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u06ce\u06a9 [\u062a\u0627\u06b5\u0627\u0648\u06cc] \u0645\u06d5\u0631\u06af \u062f\u06d5\u0686\u06ce\u0698\u06ce\u061b \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646\u0627 \u062f\u06d5\u062a\u0627\u0646 \u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u0645\u0627\u0646.',
    '\u0626\u06d5\u0648\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648 \u0628\u0646\u060c \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u0627 \u062f\u06d5\u06cc\u0627\u0646\u0628\u06d5\u06cc\u0646\u06d5 \u0646\u0627\u0648 \u0698\u0648\u0648\u0631\u06af\u06d5\u0644\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u062a\u0627 \u0647\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646. \u0626\u0627\u06cc \u0686\u06d5\u0646 \u0686\u0627\u06a9\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06d5\u0628\u0627\u062a\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646!',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0648\u0648\u0646 \u0648 \u062a\u06d5\u0648\u0627\u0648 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5\u062a\u06d5 \u062f\u06d5\u0633 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646.',
    '\u0626\u0627\u06cc \u06a9\u06d5 \u0632\u06c6\u0631\u0646 \u062a\u06cc\u0631\u06d5\u06cc \u0626\u06d5\u0648 \u062c\u0627\u0646\u06d5\u0648\u06d5\u0631\u0627\u0646\u06d5\u060c \u0628\u0698\u06cc\u0648 \u0628\u06c6 \u062e\u06c6 \u067e\u06d5\u0646\u0627 \u0646\u0627\u062f\u06d5\u0646. \u062e\u0648\u062f\u0627 \u0628\u06d5\u0648\u0627\u0646 \u0648 \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u0634 \u0695\u06c6\u0632\u06cc \u062f\u06d5\u062f\u0627\u061b \u0628\u06c6 \u062e\u06c6\u06cc \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0644\u06d5\u0645\u0627\u0646\u06d5 \u067e\u0631\u0633\u06cc: \u06a9\u06ce \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u062e\u06c6\u0631 \u0648 \u0647\u06d5\u06cc\u06a4\u06cc \u062f\u06d5\u0633\u062a\u06d5\u0645\u06c6 \u06a9\u0631\u062f\u061f \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u062f\u06d5\u0628\u06ce\u0698\u0646: \u062e\u0648\u062f\u0627. \u062f\u06d5\u0633\u0627 \u0686\u0644\u06c6\u0646 [\u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0647\u06d5\u0642] \u06a9\u0644\u0627 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u061f',
    '\u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0686\u06c6\u0646\u06cc \u06af\u06d5\u0631\u06d5\u06a9 \u0628\u06ce\u060c \u0695\u06c6\u0632\u06cc \u0647\u06d5\u0631 \u06a9\u0627\u0645 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u06a9\u06d5\u0645 \u0648 \u0632\u06cc\u0627\u062f \u062f\u06d5\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0645\u0627\u0646\u06d5 \u0628\u067e\u0631\u0633\u06cc: \u06a9\u06ce\u06cc\u06d5 \u0648\u0627 \u0644\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5\u0648\u06d5 \u0628\u0627\u0631\u0627\u0646\u06d5 \u062f\u0627\u062f\u06d5\u0628\u0627\u0631\u06ce\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0626\u0627\u0648\u06d5 \u0632\u06d5\u0648\u06cc \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u067e\u0627\u0634 \u0645\u0631\u062f\u0646\u06cc\u061f \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u062f\u06d5\u0628\u06ce\u0698\u0646: \u062e\u0648\u062f\u0627. \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0628\u06c6 \u062e\u0648\u062f\u0627. \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u06af\u06d5\u0646.',
    '\u0626\u06d5\u0645 \u0698\u06cc\u0627\u0646\u06d5\u06cc \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0647\u06d5\u0631 \u06af\u06d5\u067e \u0648 \u06af\u0627\u06b5\u062a\u06d5\u06cc\u06d5 \u0648 \u0686\u06cc \u062a\u0631 \u0646\u06cc\u06cc\u06d5\u061b \u0698\u06cc\u0627\u0646\u06cc \u0695\u0627\u0633\u062a\u06d5\u0642\u06cc\u0646\u06d5 \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06cc\u0632\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0633\u0648\u0627\u0631\u06cc \u06af\u06d5\u0645\u06cc\u06d5 \u062f\u06d5\u0628\u0646\u060c \u0628\u06d5 \u062f\u06b5\u06cc \u067e\u0627\u06a9 \u0644\u06d5\u0628\u06d5\u0631 \u062e\u0648\u0627 \u062f\u06d5\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u0646\u06d5\u062c\u0627\u062a\u06cc \u062f\u0627\u0646 \u0648 \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u06cc\u06d5 \u0633\u06d5\u0631 \u0648\u0634\u06a9\u0627\u0646\u06cc\u060c \u062f\u06d5\u06b5\u06ce\u0646: \u062e\u0648\u062f\u0627 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0647\u06d5\u0646.',
    '\u0628\u0627 \u0644\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u0645\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u0646\u060c \u062d\u0627\u0634\u0627 \u0628\u06a9\u06d5\u0646\u061b \u0628\u0627 \u0695\u0627\u06cc \u0628\u0628\u0648\u06ce\u0631\u0646 \u0648 \u062e\u06c6\u0634 \u0628\u0698\u06cc\u0646\u060c \u0644\u06d5 \u067e\u0627\u0634\u06d5\u0695\u06c6\u0698\u0627 \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0646\u0627\u0628\u06cc\u0646\u0646 \u0645\u06d5\u06a9\u06a9\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u06a9\u0631\u062f\u0648\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0647\u06ce\u0645\u0646\u060c \u06a9\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u06d5\u0648\u0631\u0648\u0628\u06d5\u0631\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0695\u0641\u06ce\u0646\u062f\u0631\u06ce\u0646\u061f \u0686\u06c6\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5 \u067e\u0648\u0648\u0686 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0628\u06cc\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u06a9\u06ce \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5 \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u06ce\u060c \u06cc\u0627\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u062a\u06cc\u0634\u06cc \u0628\u06c6 \u062f\u06ce \u067e\u06ce\u06cc \u062f\u0631\u06c6\u06cc\u06d5\u061f \u0626\u0627\u062e\u06c6 \u0628\u06c6 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u062c\u06ce \u0644\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0646\u06d5\u0645\u0627\u0648\u06d5\u061f',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u0627\u06cc \u0626\u06ce\u0645\u06d5\u062f\u0627 \u0686\u0648\u0648\u0646\u06d5 \u062e\u06d5\u0632\u0627\u060c \u0626\u06ce\u0645\u06d5\u0634 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646\u062f\u0627 \u062f\u06d5\u0628\u06d5\u06cc\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0644\u0627\u06cc\u0627\u0646\u06af\u0631\u06cc \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0645\u06cc\u0645.',
    '\u0695\u06c6\u0645\u06cc \u0634\u06a9\u0627\u0646.',
    '\u0644\u06d5 \u0646\u0632\u06cc\u06a9\u062a\u0631 \u06a9\u06d5\u0648\u0634\u06d5\u0646\u06cc \u0626\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5. \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0645 \u0698\u06ce\u0631\u06a9\u06d5\u0648\u062a\u0646\u06d5\u06cc\u0627\u0646 \u062c\u0627\u0631\u06ce \u062a\u0631 \u0633\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5\u0648\u06d5\u061b',
    '\u0644\u06d5 \u0645\u0627\u0648\u06d5\u06cc \u06a9\u06d5\u0645\u062a\u0631 \u0644\u06d5 \u062f\u06d5 \u0633\u0627\u06b5. \u067e\u06ce\u0634 \u0626\u06d5\u0648 \u0634\u06a9\u0627\u0646\u06d5 \u0648 \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06d5\u0634\u060c \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631 \u06a9\u0627\u0631\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u06a9\u06d5\u06cc\u0641\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u060c',
    '\u0628\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5. \u062e\u0648\u062f\u0627 \u062d\u06d5\u0632 \u06a9\u0627 \u06cc\u0627\u0631\u06cc\u062f\u06d5 \u0628\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062f\u06d5\u062f\u0627. \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u0648\u062f\u0627\u06cc\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062f\u0627\u0648\u06d5 \u0648 \u0644\u06ce\u06cc \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0646\u0627\u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u062e\u06d5\u06b5\u06a9 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0644\u06d5 \u0695\u0648\u0627\u06b5\u06d5\u062a\u06cc \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u0646 \u0648 \u0647\u06cc\u0686 \u0626\u0627\u06af\u0627\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u0627\u0634\u06d5\u0695\u06c6\u0698\u06d5\u06a9\u06d5 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062a\u0627 \u0648\u06ce\u0633\u062a\u0627 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06cc\u0631\u062f\u0627 \u0646\u06d5\u0647\u0627\u062a \u06a9\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c \u0647\u06d5\u0631 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631\u0647\u06d5\u0645\u06cc \u0647\u06ce\u0646\u0627\u0648\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0645\u06cc \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646\u061f \u06a9\u06d5\u0686\u06cc \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0646 \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0647\u06cc\u0686 \u06af\u06d5\u0634\u062a \u0648 \u06af\u0648\u0632\u0627\u0631\u06cc\u0627\u0646 \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u062a\u0627 \u0628\u0695\u0648\u0627\u0646\u0646 \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0695\u0627\u0628\u0631\u062f\u0648\u0648 - \u06a9\u06d5 \u0644\u06d5\u0645\u0627\u0646 \u062a\u0648\u0627\u0646\u0627\u062a\u0631 \u0628\u0648\u0648\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u06cc\u0627\u0646 \u06a9\u06ce\u06b5\u0627\u0648 \u0626\u0627\u0648\u06d5\u062f\u0627\u0646 \u062a\u0631\u06cc\u0634\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06af\u06d5\u0644\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0648\u06d5 \u0647\u0627\u062a\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646 - \u0686\u06cc\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u0627\u062a\u061f \u062e\u0648\u062f\u0627 \u063a\u06d5\u062f\u0631\u06cc \u0644\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u063a\u06d5\u062f\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0631\u0627\u067e\u06d5\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u062e\u0631\u0627\u067e\u062a\u0631\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634 \u0647\u0627\u062a\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0627\u0646\u06cc \u0648 \u062d\u06d5\u0646\u06d5\u06a9\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u0631\u062f.',
    '\u062e\u0648\u0627 \u0626\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5 \u062f\u0631\u0648\u0633 \u062f\u06d5\u06a9\u0627 \u0648 \u062f\u0648\u0627\u062c\u0627\u0631\u06cc\u0634 \u062f\u06d5\u06cc\u0647\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5. \u0633\u06d5\u0631\u06d5\u0646\u062c\u0627\u0645\u06cc\u0634 \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0633\u06d5\u06b5\u0627 \u0695\u0627\u062f\u06d5\u0628\u06ce\u060c \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u062f\u0698 \u062f\u0627\u0645\u0627\u0648\u0646 \u0648 \u0645\u062a\u06d5\u0642\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0631 \u062f\u06d5\u0631\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0634 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0632\u0627\u0646\u06cc\u0646\u060c \u0646\u0627\u06cc\u06d5\u0646 \u062a\u06a9\u0627\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06a9\u06d5\u0646\u061b \u0644\u06d5\u0648 \u0634\u06d5\u0631\u06cc\u06a9\u0627\u0646\u06d5\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u062d\u0627\u0634\u0627\u06cc\u06d5 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a \u0647\u0627\u062a\u06d5 \u0695\u0648\u0648\u060c \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0628\u06d5\u0634 \u0628\u06d5\u0634 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5.',
    '\u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0633\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0695\u0648\u0627 \u0628\u0648\u0648\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u067e\u06d5\u0633\u0646\u062f\u06cc\u0627\u0646 \u0628\u0648\u0648 \u0628\u06ce\u060c \u062c\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u0627\u062e\u0686\u06d5\u06cc\u06d5 \u0648 \u06a9\u06d5\u06cc\u0641 \u0633\u0627\u0632\u0646.',
    '\u0626\u06d5\u0648\u06cc \u062e\u0648\u0627\u0634\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5 \u0648 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0648 \u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648 \u0628\u0648\u0648\u0646\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u0628\u06ce \u0628\u0695\u0648\u0627 \u0628\u0648\u0648\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0646\u06d5 \u067e\u06ce\u0634 \u0628\u06c6 \u0626\u0627\u0632\u0627\u0631.',
    '\u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u0631\u06d5\u0628\u06d5\u06cc\u0627\u0646\u0627\u0646 \u0648 \u0633\u06d5\u0631 \u0644\u06d5\u0646\u06af\u06c6\u0695\u06cc\u0634 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u062e\u0648\u062f\u0627 \u0628\u062f\u06d5\u0646.',
    '\u0633\u0648\u067e\u0627\u0633 \u0628\u06c6 \u0626\u06d5\u0648 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u060c \u0634\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0648 \u062f\u06d5\u0645\u06cc \u0646\u06cc\u0648\u06d5\u0695\u06c6\u0634 \u0647\u06d5\u0631 \u0648\u06d5\u062a\u0631.',
    '\u0632\u06cc\u0646\u062f\u06cc \u0644\u06d5 \u0645\u0631\u062f\u0648\u0648 \u062f\u06d5\u0631\u062f\u06d5\u062e\u0627 \u0648 \u0645\u0631\u062f\u0648\u0648 \u0644\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u0631\u062f\u06ce\u0646\u06ce\u061b \u0632\u06d5\u0648\u06cc\u0634 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u0645\u0631\u062f\u0648\u0648\u06d5\u060c \u06af\u06cc\u0627\u0646\u06cc \u0648\u06d5\u0628\u06d5\u0631 \u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06d5\u0631\u0648\u0627 \u062f\u06ce\u0646\u0646\u06d5 \u062f\u06d5\u0631\u06ce.',
    '\u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5 \u062e\u06c6\u06b5 \u0686\u06ce \u06a9\u0631\u062f \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0646\u0627\u06a9\u0627\u0648 \u0628\u0648\u0648\u0646\u06d5 \u0645\u0631\u06c6 \u0648 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0628\u06b5\u0627\u0648 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u060c \u0626\u0627\u0648\u062c\u0648\u0648\u062a\u06cc \u0628\u06c6 \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u0646\u061b \u06a9\u06d5 \u067e\u06ce\u06cc\u0627\u0646 \u0628\u062d\u06d5\u0633\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u062e\u06c6\u0634\u06d5\u0648\u06cc\u0633\u062a\u06cc \u0648 \u0626\u06d5\u0648\u06cc\u0646\u06cc \u062e\u0633\u062a\u06c6\u062a\u06d5 \u0646\u0627\u0648\u062a\u0627\u0646. \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0628\u06c6 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u06a9\u06d5 \u0628\u06d5 \u0648\u0631\u062f\u06cc \u0633\u06d5\u0631\u0646\u062c \u0628\u062f\u06d5\u0646\u060c \u0628\u06d5\u06b5\u06af\u06d5 \u0632\u06c6\u0631\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06d5\u0648\u06d5 \u062f\u0627\u0647\u06ce\u0646\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u0648\u06ce\u06a9 \u0646\u06d5\u0686\u0648\u0648\u0646\u06cc \u0632\u0645\u0627\u0646\u06cc \u067e\u06ce \u0626\u0627\u062e\u0627\u0641\u062a\u0646 \u0648 \u0695\u06d5\u0646\u06af \u0648 \u0695\u0648\u0648\u062a\u0627\u0646\u061b \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0628\u06d5\u06b5\u06af\u06d5 \u0632\u06c6\u0631\u0646 \u0628\u06c6 \u0632\u0627\u0646\u0627\u06cc\u0627\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc\u06d5\u062a\u06cc: \u062e\u06d5\u0648\u06cc \u0634\u06d5\u0648\u0627\u0646 \u0648 \u0695\u06c6\u0698\u0627\u0646\u062a\u0627\u0646\u060c \u06af\u06d5\u0695\u0627\u0646\u062a\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0633 \u062e\u0633\u062a\u0646\u06cc \u0695\u0633\u0642 \u0648 \u0695\u06c6\u0632\u06cc\u061b \u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u0633\u0627\u06cc\u06d5\u06cc \u0626\u06d5\u0648\u0695\u0627\u06cc\u06d5. \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0632\u06c6\u0631 \u0647\u06d5\u06cc\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0628\u06cc\u0633\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc\u06d5\u062a\u06cc: \u0628\u0631\u0648\u0648\u0633\u06a9\u06d5\u062a\u0627\u0646 \u067e\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627 \u062a\u0631\u0633 \u0648 \u0647\u0648\u0645\u06ce\u062f\u06cc \u067e\u06ce\u0648\u06d5\u06cc\u06d5\u061b \u0644\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5\u0634 \u0628\u0627\u0631\u0627\u0646\u0648\u0648 \u0628\u06c6 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627\u060c \u062a\u0627 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0645\u0631\u062f\u0648\u0648\u06cc \u067e\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648 \u06a9\u0627\u062a\u06d5\u0648\u06d5. \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0632\u06c6\u0631\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc\u06d5\u062a\u06cc \u06a9\u06d5 \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u060c \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0695\u0627\u0648\u06d5\u0633\u062a\u0627\u0648\u0646. \u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0631\u06af\u0627 - \u062a\u06d5\u0646\u06cc\u0627 \u06cc\u06d5\u06a9 \u062c\u0627\u0631 - \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0628\u0627\u0646\u06af\u0648\u0648 \u062f\u06d5\u06a9\u0627\u060c \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u067e\u0695 \u062f\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u0646.',
    '\u0647\u06d5\u0631\u06a9\u06ce \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u060c \u06a9\u0631\u06cc \u0626\u06d5\u0648\u0646\u061b \u06af\u0634\u062a\u06cc \u0628\u06c6 \u0626\u06d5\u0648 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5.',
    '\u062e\u06c6\u06cc \u0626\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u062f\u06ce\u0646\u06ce \u0648 \u062f\u0648\u0627\u062c\u0627\u0631\u06cc\u0634 \u062f\u06d5\u06cc\u0647\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u0647\u0627\u0633\u0627\u0646\u062a\u0631\u06cc\u0634\u06d5. \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0646\u0645\u0648\u0648\u0646\u06d5\u06cc \u0628\u0627\u0634\u062a\u0631\u06cc\u0646 \u0626\u0627\u06a9\u0627\u0631 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5. \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0645\u06d5\u0633\u06d5\u0644\u06ce\u06a9\u06cc \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0628\u06c6 \u0647\u06ce\u0646\u0627\u0648\u0646\u06d5\u0648\u06d5: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u067e\u06ce\u0648 \u06af\u06d5\u06cc\u06cc\u0648\u06d5\u060c \u0628\u06d5\u0646\u06cc\u06d5\u06a9\u0627\u0646\u0648\u0648 \u062f\u06d5\u06a9\u06d5\u0646\u06d5 \u0647\u0627\u0648\u0628\u06d5\u0634\u06cc \u062e\u06c6\u062a\u0627\u0646\u061f \u0647\u06d5\u0631 \u062f\u0648\u0648 \u0644\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9 \u062f\u06d5\u0628\u0646\u061f \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5 \u062a\u0631\u0633\u062a\u0627\u0646 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u0647\u06d5\u06cc\u06d5\u060c \u0626\u0627\u0648\u0627\u0634 \u062a\u0631\u0633\u0648\u0648 \u0644\u06d5\u0645\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061f \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646 - \u067e\u0648\u062e\u062a \u0648\u067e\u0627\u0631\u0627\u0648 - \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646\u060c \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u0695\u0648\u0648.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u0646\u060c \u0647\u06d5\u0631\u0686\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06d5 \u0644\u06d5 \u062f\u0648\u0648\u06cc \u062f\u06d5\u0686\u0646\u061b \u0686\u0634 \u0646\u0627\u0632\u0627\u0646\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u0648\u062f\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u0644\u06ce \u0628\u06af\u06c6\u0695\u06ce\u060c \u06a9\u06ce \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u06a9\u0627\u061f \u06a9\u06d5\u0633\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0647\u0627\u0646\u0627 \u0646\u0627\u06cc\u06d5.',
    '\u062a\u06c6 \u0647\u06d5\u0631 \u0695\u0648\u0648 \u0644\u06d5\u0645 \u062f\u06cc\u0646\u06d5 \u0628\u06a9\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u0647\u06d5\u0642 \u0647\u06d5\u0646\u06af\u0627\u0648 \u0628\u0646\u06ce\u061b \u062f\u06cc\u0646\u06ce\u06a9\u06cc \u0628\u0646\u06d5\u0695\u06d5\u062a\u06cc\u06cc\u06d5 \u0648 \u0644\u06d5 \u0633\u0631\u0648\u0634\u062a\u0627 \u062e\u0648\u0627 \u0626\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5\u06cc \u0647\u06d5\u0631 \u0628\u06c6 \u06cc\u06d5\u06a9\u062a\u0627\u067e\u06d5\u0631\u0633\u062a\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5. \u06a9\u0627\u0631\u06cc \u062e\u0648\u062f\u0627\u0634 \u06af\u06c6\u0695\u0627\u0646\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0646\u0627\u06cc\u06d5. \u062f\u06cc\u0646\u06cc \u067e\u062a\u06d5\u0648 \u0648 \u067e\u0627\u06cc\u06d5\u062f\u0627\u0631 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u0627\u067e\u06c6\u0631\u06d5\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0648 \u0644\u06ce\u06cc \u0628\u062a\u0631\u0633\u0646 \u0648 \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06d5\u0628\u0646 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06d5 \u062f\u06cc\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u06cc\u06d5\u06a9 \u062c\u0648\u062f\u0627 \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u062f\u06d5\u0633\u062a\u06d5 \u0628\u0648\u0648\u0646\u06d5\u062a\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9 \u0628\u06d5 \u0628\u06cc\u0631\u0648\u0628\u0627\u0648\u06d5\u0695\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06b5\u06cc\u0627\u0646 \u062e\u06c6\u0634\u06d5.',
    '\u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u06af\u06d5\u0631 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u060c \u0647\u0627\u0646\u0627 \u062f\u06d5\u0628\u06d5\u0646 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u062f\u0648\u0627\u06cc\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06cc \u06a9\u0631\u062f\u0646\u060c \u0646\u0627\u06a9\u0627\u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9 [\u0644\u06d5\u0648 \u062e\u06c6\u0634\u06cc\u06d5\u062f\u0627] \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u062f\u06d5\u0628\u0627 \u0626\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u06a9\u06d5\u0646 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u0646 \u06a9\u0631\u062f\u0648\u0648\u0646. \u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u0695\u0627\u06cc \u0628\u0628\u0648\u06ce\u0631\u0646\u060c \u0632\u06c6\u0631 \u0646\u0627\u062e\u0627\u06cc\u06d5\u0646\u06ce \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631 \u06a9\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u0645\u060c \u0642\u0633\u06d5 \u0628\u06a9\u0627\u061f',
    '\u0647\u06d5\u0631\u06af\u0627 \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0648 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u062a\u0627\u0645\u06cc \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06cc\u06cc\u0645\u0627\u0646 \u0686\u06d5\u0634\u062a\u060c \u067e\u06ce\u06cc \u0634\u0627\u062f \u062f\u06d5\u0628\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0633\u0632\u0627\u06cc \u06a9\u0631\u062f\u0627\u0631\u06cc \u062e\u0631\u0627\u067e\u06cc\u0627\u0646\u062f\u0627 \u062a\u0648\u0648\u0634\u06cc \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06cc \u0647\u0627\u062a\u0646\u060c \u0647\u06cc\u0686 \u0647\u0648\u0645\u06ce\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u0627\u0645\u06ce\u0646\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0646\u0627\u0631\u062f\u0646\u06cc \u0628\u0698\u06cc\u0648 \u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u0628\u06c6 \u06cc\u06d5\u06a9\u06ce \u0632\u06c6\u0631 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627 \u0648 \u0628\u06c6 \u06cc\u06d5\u06a9\u06ce \u06a9\u06d5\u0645\u061f \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0647\u06d5\u0646.',
    '\u06a9\u06d5\u0648\u0627\u06cc\u06d5 \u062a\u06c6 \u0628\u06d5\u0634\u06cc \u062e\u0632\u0645 \u0648 \u0647\u06d5\u0698\u0627\u0631 \u0648 \u0695\u06ce\u0628\u0648\u0627\u0631\u06cc \u0626\u0627\u062a\u0627\u062c\u060c \u0648\u06d5\u0644\u0627 \u0628\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u06d5\u0634\u06cc \u062e\u06c6\u06cc \u0628\u062f\u06d5\u06cc\u06d5. \u0626\u06d5\u0645 \u0626\u0627\u06a9\u0627\u0631\u06d5 \u067e\u06d5\u0633\u0646\u062a\u0631\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0695\u06d5\u0632\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0632\u06af\u0627\u0631\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648\u06d5\u06cc \u0628\u06d5 \u0646\u0627\u0648\u06cc \u0633\u0648\u0648 \u0626\u06d5\u06cc\u062f\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0645\u0627\u06b5\u06cc \u062e\u06d5\u06b5\u06a9\u062f\u0627 \u0632\u06ce\u062f\u06d5 \u0628\u0628\u06ce\u060c \u0644\u062c\u06d5\u0645 \u062e\u0648\u062f\u0627 \u0632\u06ce\u062f\u06d5 \u0646\u0627\u0628\u06ce. [\u0628\u06d5\u06b5\u0627\u0645] \u0626\u06d5\u0648 \u0645\u0627\u06b5\u06cc \u06a9\u06d5 \u0628\u06d5 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u06cc\u062f\u06d5\u0646 \u0648 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0695\u0627\u0632\u06cc \u06a9\u06d5\u0646\u060c \u0632\u06c6\u0631\u062a\u0631\u062a\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06af\u0627\u062a\u06d5\u0648\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0695\u06c6\u0632\u06cc \u067e\u06ce\u062f\u0627\u0646\u061b \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u062a\u0627\u0646\u0645\u0631\u06ce\u0646\u06ce \u0648 \u062f\u06cc\u0633\u0627\u0646 \u062f\u06d5\u062a\u0627\u0646\u062e\u0627\u062a\u06d5\u0648\u06d5 \u0698\u06cc\u0646. \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u06a9\u0631\u062f\u0648\u0648\u062a\u0627\u0646\u0646 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u060c \u0626\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u06a9\u0631\u06ce\u061f \u062e\u0648\u0627 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06d5 \u0648 \u0644\u06d5\u0648\u06d5 \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u062a\u0631\u06d5 \u06a9\u06d5 \u0628\u06b5\u06ce\u0646: \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u06af\u06d5\u0646\u062f\u06d5\u06a9\u0627\u0631\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 - \u0644\u06d5 \u0628\u06d5\u0698 \u0648 \u062f\u06d5\u0631\u06cc\u0627 - \u062e\u06c6\u06cc \u0646\u0648\u0648\u0627\u0646\u062f. \u062f\u06d5\u0628\u0627 \u0633\u0632\u0627\u06cc \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u067e\u06ce \u0628\u0686\u06ce\u0698\u06ce\u061b \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06d5 \u0639\u06d5\u0631\u0632\u0627 \u0628\u06af\u06d5\u0695\u06ce\u0646\u060c \u0633\u0627 \u0628\u0695\u0648\u0627\u0646\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0648\u0648\u0646\u060c \u0686\u06cc\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u0627\u062a\u061f \u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06cc.',
    '\u062f\u06d5\u0633\u0627 \u062a\u06c6 \u0695\u0648\u0648 \u06a9\u06d5 \u0644\u06d5\u0648 \u062f\u06cc\u0646\u06d5 \u067e\u0627\u06cc\u06d5\u062f\u0627\u0631\u06d5\u060c \u0628\u06d5\u0631 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0695\u06cc\u0627\u0631\u06cc \u062f\u0627\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u0648\u06d5\u062f\u06cc \u062f\u06ce. \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0628\u06b5\u0627\u0648\u06d5\u06cc \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631\u06a9\u06ce \u062e\u0648\u062f\u0627\u06cc \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5\u060c \u0647\u06d5\u0631 \u0632\u06cc\u0627\u0646\u06cc \u0644\u06d5 \u062e\u06c6\u06cc \u062f\u0627\u0648\u06d5\u061b \u06a9\u06ce\u0634 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc \u0628\u0648\u0648\u06d5\u060c \u062c\u06ce\u06af\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062e\u06c6\u0634 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0647\u06d5\u062a\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0627\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u0628\u062f\u0627\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u062e\u06c6\u0634\u06cc \u0644\u06d5 \u0646\u0627\u0634\u06a9\u0648\u0631\u06cc \u06a9\u06d5\u0631\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u06cc\u06d5\u06a9\u06ce\u06a9 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u0627\u06cc\u0627\u0646\u06cc \u0645\u0632\u06af\u06ce\u0646\u06cc \u062f\u06d5\u0631 \u0628\u06d5 \u0695\u06ce \u062f\u06d5\u06a9\u0627 \u062a\u0627 \u0644\u06d5 \u0628\u0632\u06cc \u062e\u06c6\u06cc \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0686\u06ce\u0698\u0646\u06ce. \u06af\u06d5\u0645\u06cc\u06d5\u0634 \u0647\u06d5\u0631 \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648 \u0628\u06ce\u0646 \u0648 \u0628\u0686\u0646 \u0648 \u0644\u06d5 \u0633\u0627\u06cc\u06d5\u06cc \u062f\u06d5\u0647\u0646\u062f\u06d5\u06cc\u06cc \u0626\u06d5\u0648\u060c \u0695\u06c6\u0632\u06cc \u062e\u06c6\u062a\u0627\u0646 \u067e\u06d5\u06cc\u062f\u0627 \u0628\u06a9\u06d5\u0646\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631 \u0628\u0646.',
    '\u0628\u06d5\u0631 \u0644\u06d5 \u062a\u06c6\u0634 \u0632\u06c6\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631 \u06af\u06d5\u0644\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u06af\u06d5\u0644\u06ce \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u067e\u062a\u06d5\u0648\u06cc\u0627\u0646 \u0628\u0631\u062f\u06d5 \u0644\u0627\u06cc\u0627\u0646. \u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0698\u06cc\u0627\u0646 \u0648\u06ce\u0633\u062a\u0627\u0646\u060c \u0648\u06d5\u0628\u06d5\u0631 \u062a\u06c6\u06b5\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u06a9\u06d5\u0648\u062a\u0646\u061b \u0626\u06d5\u0648\u06cc \u0628\u0695\u0648\u0627\u0634\u06cc\u0627\u0646 \u067e\u06ce\u06a9\u0631\u062f\u0646\u060c \u0647\u06d5\u0631 \u062e\u06c6\u0645\u0627\u0646 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc\u0645\u0627\u0646 \u062f\u0627\u0646.',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0626\u06d5\u0648 \u0628\u0627\u06cc\u0627\u0646\u06d5 \u0628\u06d5 \u0695\u06ce \u062f\u06d5\u06a9\u0627\u060c \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0648\u0631 \u062f\u06d5\u0628\u0632\u0648\u06ce\u0646\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u0686\u06c6\u0646\u06cc \u06af\u06d5\u0631\u06d5\u06a9 \u0628\u06ce\u060c \u0626\u06d5\u0648 \u0647\u06d5\u0648\u0631\u0627\u0646\u06d5 \u0644\u06d5 \u0626\u06d5\u0632\u0645\u0627\u0646\u0627 \u067e\u0695\u0698\u0648\u0628\u06b5\u0627\u0648 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u06af\u06d5\u0648\u0627\u06b5 \u06af\u06d5\u0648\u0627\u06b5\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627. \u06a9\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0644\u06d5\u0648\u0627\u0646\u06d5\u0648\u06d5 \u062f\u06b5\u06c6\u067e\u06d5 \u062f\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u0645\u06d5\u06cc\u0644\u06cc \u0628\u06ce\u0646\u06cc \u0648 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u0627\u0645\u06ce \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u0646\u06ce\u0631\u06ce\u060c \u062f\u06b5\u062e\u06c6\u0634 \u062f\u06d5\u0628\u0646.',
    '\u06a9\u06d5\u0686\u06cc \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0628\u0627\u0631\u0627\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u0628\u0627\u0631\u06ce\u060c \u0646\u0627\u0647\u0648\u0645\u06ce\u062f \u0628\u0648\u0648\u0646.',
    '\u062f\u06d5\u0628\u0695\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627 \u0686\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5! \u0632\u06d5\u0648\u06cc \u0645\u0631\u062f\u0648\u0648 \u0644\u06d5 \u0646\u0648\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u0632\u06cc\u0646\u062f\u0648\u0648 \u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5\u06cc \u0645\u0631\u06cc\u0627\u0646\u06cc\u0634 \u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648\u06d5. \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062a\u0648\u0627\u0646\u0627\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u06cc\u06d5\u06a9 \u0628\u0646\u06ce\u0631\u06cc\u0646 \u0628\u06d5\u0631\u0647\u06d5\u0645\u06cc\u0627\u0646 \u0632\u06d5\u0631\u062f \u0647\u06d5\u06b5\u0628\u06af\u06ce\u0695\u06ce\u060c \u0628\u06d5 \u0634\u0648\u06ce\u0646\u06cc\u062f\u0627 \u06af\u0627\u0632\u0646\u062f\u06d5 \u0648 \u0646\u0627\u0634\u06a9\u0648\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u062a\u06c6 \u0646\u0627\u062a\u0627\u0646\u06cc \u06a9\u0627\u0631\u06ce\u06a9 \u0628\u06a9\u06d5\u06cc \u0626\u06d5\u0645 \u0645\u0631\u06cc\u0627\u0646\u06d5 \u06af\u0648\u06ce\u062a \u067e\u06ce \u0628\u062f\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6 \u0644\u06d5 \u062a\u06c6 \u06a9\u06d5\u0695 \u0628\u06a9\u06d5\u0646\u060c \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f\u06d5 \u0628\u0627\u0646\u06af\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u06cc\u060c \u0644\u06ce\u062a \u0646\u0627\u0628\u06cc\u0633\u0646.',
    '\u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631\u06cc \u06a9\u0648\u06ce\u0631\u0627\u0646\u06cc\u0634 \u0646\u06cc\u062a\u060c \u0644\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u0648\u06d5\u06cc\u0627\u0646\u06af\u06ce\u0695\u06cc. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06ce\u062a \u062f\u06d5\u0628\u06cc\u0633\u0646\u060c \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0686\u06cc\u06cc\u0627\u0646 \u067e\u06ce \u0628\u06b5\u06ce\u06cc \u0644\u0627\u0631\u06d5\u0645\u0644\u0646.',
    '\u062e\u0648\u062f\u0627 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u060c \u0644\u06d5 \u0633\u06d5\u0631\u06d5\u062a\u0627\u062f\u0627 \u0628\u06ce \u0647\u06ce\u0632 \u0628\u0648\u0648\u0646\u061b \u067e\u0627\u0634 \u0628\u06ce \u0647\u06ce\u0632\u06cc\u060c \u06af\u0648\u0695\u06cc \u067e\u06ce\u062f\u0627\u0646\u061b \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646\u0627 \u0647\u06ce\u0632\u0648\u06af\u0648\u0695\u06cc \u0644\u06ce \u0633\u062a\u0627\u0646\u062f\u0646 \u0648 \u067e\u06cc\u0631\u06cc \u06a9\u0631\u062f\u0646. \u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0626\u0627\u0641\u0631\u06ce\u0646\u06ce\u060c \u0632\u0627\u0646\u0627 \u0648 \u062a\u0648\u0627\u0646\u0627 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0633\u06d5\u06b5\u0627 \u0695\u0627\u062f\u06d5\u0628\u06ce\u060c \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0633\u0648\u06ce\u0646\u062f\u06ce \u062f\u06d5\u062e\u06c6\u0646 \u062c\u06cc\u0627 \u0644\u06d5 \u0633\u0627\u062a\u06ce\u06a9 \u0646\u06d5\u0645\u0627\u0646\u06d5\u0648\u06d5. \u0626\u0627\u0648\u0627 \u0647\u06d5\u0631 \u0634\u0648\u06ce\u0646 \u062f\u0631\u06c6 \u06a9\u06d5\u0648\u062a\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0628\u06d5 \u0632\u0627\u0646\u0633\u062a \u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0646\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0647\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u062e\u0648\u062f\u0627 \u0646\u0648\u0648\u0633\u06cc\u0648\u06cc\u06d5\u060c \u062a\u0627 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0645\u0627\u0648\u0646\u06d5\u0648\u06d5\u061b \u0633\u0627 \u0626\u06d5\u0645\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u0648\u0632\u0627\u0646\u06cc\u0648\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5\u0648\u0627 \u063a\u06d5\u062f\u0631\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0628\u06d5\u0647\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc\u0627\u0646 \u067e\u06ce \u0646\u0627\u06af\u06d5\u06cc\u06d5\u0646\u06ce \u0648 \u0695\u06ce\u0634\u06cc\u0627\u0646 \u0646\u0627\u062f\u0631\u06ce \u062f\u0627\u0648\u0627\u06cc \u0644\u06ce \u062e\u06c6\u0634\u0628\u0648\u0648\u0646\u06ce \u0628\u06a9\u06d5\u0646.',
    '\u0648\u0627 \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u062c\u06c6\u0631\u06d5 \u0646\u06d5\u0632\u06cc\u0644\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5 \u0647\u06ce\u0646\u0627\u06cc\u06d5\u0648\u06d5. \u06a9\u06d5\u0686\u06cc \u0647\u06d5\u0631\u06af\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u0646\u06cc\u060c \u0628\u06ce \u0633\u06c6 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u060c \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0642\u0633\u06d5\u06cc \u067e\u0648\u0648\u0686 \u0628\u06d5\u0648 \u0644\u0627\u0648\u06d5 \u0686\u06cc \u062a\u0631\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u0627\u0648\u0627 \u062e\u0648\u062f\u0627 \u0645\u06c6\u0631 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u062f\u06b5\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u0646\u0627\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0628\u0632\u0627\u0646\u0646.',
    '\u0633\u0627 \u062e\u06c6\u062a \u0628\u06af\u0631\u06d5\u060c \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u06ce\u062a\u06d5 \u062c\u06ce\u061b \u0646\u06d5\u06a9\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0695\u062f\u06c6\u0646\u06af\u0646\u060c \u062a\u06c6 \u0644\u06d5 \u0695\u06ce\u06cc \u0647\u06d5\u0642 \u0628\u062a\u0631\u0627\u0632\u06ce\u0646\u0646.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0645\u06cc\u0645.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u06a9\u062a\u06ce\u0628\u06cc \u067e\u0695 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u0646\u061b',
    '\u0695\u06ce\u0634\u0627\u0646\u062f\u06d5\u0631 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u061b',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0648\u06ce\u0698\u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u062f\u06d5\u0633\u06d5\u0644\u0645\u06ce\u0646\u0646\u061b',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0648\u06d5\u0628\u06d5\u0631 \u0646\u0627\u0648\u0646\u061b \u0695\u0632\u06af\u0627\u0631\u0627\u0646\u0646\u061b',
    '\u0644\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u06d5\u062f\u0627 \u0626\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u06a9\u0695\u06cc\u0627\u0631\u06cc \u0642\u0633\u06d5\u06cc \u0628\u06ce\u062a\u0627\u0645\u06d5 \u0648 \u0644\u06d5 \u0646\u06d5\u0632\u0627\u0646\u06cc\u060c \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u062e\u06d5\u06b5\u06a9 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u0627 \u06a9\u0644\u0627 \u0628\u06a9\u0627 \u0648 \u062f\u06d5\u06cc\u06a9\u0627\u062a\u06d5 \u06af\u0627\u06b5\u062a\u06d5\u062c\u0627\u0695\u06cc \u062e\u06c6\u06cc. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u06a9\u0627\u0631\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0626\u06d5\u0634\u06ce \u0632\u06c6\u0631 \u0628\u06d5 \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0626\u0627\u0632\u0627\u0631 \u0628\u062f\u0631\u06ce\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646\u06cc \u0644\u06d5\u0695\u0648\u0648\u062f\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u06ce\u0646\u06d5\u0648\u06d5\u060c \u0628\u06d5 \u0641\u06cc\u0632\u06d5\u0648\u06d5 \u067e\u0634\u062a\u06cc \u0626\u06d5\u06a9\u0627\u062a\u06d5 \u0626\u06d5\u0648\u0644\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u06af\u0648\u06ce\u0646 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0647\u06d5\u0631\u062f\u0648\u0648 \u06af\u0648\u06ce\u06cc \u06af\u0631\u0627\u0646 \u0628\u0648\u0648 \u0628\u0646\u060c \u0648\u06d5\u06a9\u0648\u0648 \u0646\u06d5\u06cc\u0628\u06cc\u0633\u062a\u0628\u06ce \u0648\u0627\u06cc\u06d5. \u0633\u0627 \u062a\u06c6 \u0645\u0698\u062f\u06d5\u06cc \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0698\u0627\u0646\u0627\u0648\u06cc \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0628\u062f\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc\u0627\u0646 \u0695\u06d5\u0686\u0627\u0648 \u06a9\u0631\u062f\u060c \u0628\u06d5\u0634\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u0627\u063a\u0627\u062a\u06cc \u067e\u0695 \u062e\u06ce\u0631\u0648\u0628\u06ce\u0631\u06d5.',
    '\u0647\u06d5\u062a\u0627\u0633\u06d5\u0631 \u0647\u06d5\u0631\u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646. \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u0627 \u0647\u06d5\u0631 \u062f\u06ce\u062a\u06d5\u062c\u06ce\u061b \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0644\u06d5\u0648\u0628\u06d5\u0631\u0632\u06d5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u060c \u0628\u06d5\u0628\u06ce \u0626\u06d5\u0645\u06d5\u06cc \u0647\u06cc\u0686 \u0626\u06d5\u0633\u062a\u0648\u0646\u062f\u06d5\u06a9\u06ce \u0628\u0628\u06cc\u0646\u0646. \u06a9\u06ce\u0648\u0627\u062a\u06cc \u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646 \u06a9\u06d5 \u0644\u06d5\u0631\u0632\u06d5 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u06a9\u0627\u061b \u0647\u06d5\u0645\u0648\u0648 \u062c\u06c6\u0631\u06d5 \u062c\u0627\u0646\u06d5\u0648\u06d5\u0631\u06cc \u062f\u06d5\u0648\u06ce\u062f\u0627 \u0628\u06b5\u0627\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5. \u0626\u0627\u0648\u06ce\u06a9\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0628\u06c6 \u0695\u0698\u0627\u0646\u062f\u0646\u060c \u06a9\u06d5 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u062c\u0648\u0648\u062a\u06d5\u06cc \u06af\u0634 \u062c\u06c6\u0631\u06d5 \u0695\u0648\u0648\u06d5\u06a9\u06cc \u0628\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u0645\u0627\u0646 \u0634\u06cc\u0646 \u06a9\u0631\u062f\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0645\u06d5 \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u061b \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0645 \u062f\u06d5\u0646 \u063a\u06d5\u06cc\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0686\u06cc\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u0648\u0648\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u0646\u060c \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0695\u06ce \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06d5 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0644\u0648\u0642\u0645\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u062f\u0627 \u0695\u0627\u0647\u06ce\u0646\u0627\u060c \u06a9\u06d5: \u0644\u06d5\u0633\u06d5\u0631\u062a\u06d5 \u0647\u06d5\u0631 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u06cc\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0634\u0648\u06a9\u0631\u06ce \u062f\u06d5\u06a9\u0627\u060c \u0634\u0648\u06a9\u0631\u06d5\u06a9\u06d5\u06cc \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u0627\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646 \u0628\u06ce [\u0628\u0627 \u0628\u0632\u0627\u0646\u06ce] \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06cc\u062f\u0627 \u0634\u0627\u06cc\u0627\u0646\u06cc \u062a\u0627\u0631\u06cc\u0641 \u06a9\u0631\u062f\u0646\u06d5.',
    '\u06a9\u0627\u062a\u06ce \u0644\u0648\u0642\u0645\u0627\u0646 - \u06a9\u06d5 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u06a9\u0648\u0695\u06d5\u06a9\u06d5\u06cc \u0628\u0648\u0648 - \u067e\u06ce\u06af\u0648\u062a: \u0695\u06c6\u06b5\u06d5\u0645! \u06a9\u06d5\u0633 \u0645\u06d5\u06a9\u06d5 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627\u060c \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0628\u0695\u06cc\u0627\u0631\u062f\u0627\u0646\u060c \u0646\u0627\u0647\u06d5\u0642\u06cc\u06cc\u06d5\u06a9\u06cc \u0632\u06c6\u0631 \u0645\u06d5\u0632\u0646\u06d5.',
    '\u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0645\u0631\u06c6\u0645\u0627\u0646 \u06a9\u0631\u062f \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5\u062f\u0627\u06cc \u0648 \u0628\u0627\u0628\u06cc. \u062f\u0627\u06cc\u06a9\u06cc \u06a9\u06d5 \u0628\u06d5\u0648 \u0626\u0627\u0648\u0633 \u0628\u0648\u0648\u06d5\u060c \u0686\u06d5\u0646\u062f\u06cc \u06a9\u0648\u06ce\u0631\u06d5\u0648\u06d5\u0631\u06cc \u062f\u06cc\u0648\u06d5\u061b \u0644\u06d5 \u0645\u06d5\u0645\u06a9 \u0628\u0695\u06cc\u0646\u06d5\u0648\u06d5\u0634\u06cc \u062f\u0648\u0648 \u0633\u0627\u06b5 \u0648\u06d5\u062e\u062a\u06cc \u062e\u0627\u06cc\u0627\u0646\u062f\u0648\u0648\u06d5. \u06a9\u06d5: \u062a\u06c6 \u0633\u067e\u0627\u0633\u06cc \u0645\u0646 \u0628\u06a9\u06d5 \u0648 \u0633\u0648\u067e\u0627\u0633\u06cc \u062f\u0627\u06cc\u06a9 \u0648 \u0628\u0627\u0628\u062a \u06a9\u06d5\u060c \u062f\u0648\u0627\u062c\u0627\u0631 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0645\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u062f\u0646\u06d5\u06cc \u062a\u06c6\u06cc\u0627\u0646 \u062f\u0627\u060c \u0634\u062a\u06ce \u0628\u06a9\u06d5\u06cc \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u0645 - \u06a9\u06d5 \u062e\u06c6\u062a \u0647\u06cc\u0686\u06cc \u0644\u06ce \u0646\u0627\u0632\u0627\u0646\u06cc - \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u062c\u06ce \u0645\u06d5\u0647\u06ce\u0646\u06d5\u061b \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0628\u06d5\u062e\u06c6\u0634\u06cc \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u0628\u0698\u06cc \u0648 \u0647\u06d5\u0631 \u0644\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0695\u06c6 \u06a9\u06d5 \u0628\u06d5\u0631\u06d5\u0648\u0645\u0646 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5. \u0644\u06d5\u0626\u0627\u06a9\u0627\u0645\u0627 \u0647\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u0645\u0646\u060c \u0626\u06d5\u0648\u0633\u0627 \u062a\u06ce\u062a\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06cc\u06d5\u0646\u0645 \u0686\u0648\u0648 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u06a9\u0648\u0695\u06cc\u0698\u06af\u06d5\u06cc \u062e\u06c6\u0645! \u06a9\u0631\u062f\u06d5\u06cc \u0628\u06d5\u0642\u0627\u0633 \u062a\u06c6\u0645\u06cc \u062e\u06d5\u0631\u062f\u06d5\u0644 \u0644\u06d5\u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u06d5\u0631\u062f\u06ce\u06a9\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u062a\u06d5\u0634\u0642\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0628\u06ce\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0647\u06ce\u0646\u06ce\u062a\u06d5 \u06af\u06c6\u0695\u06ce. \u062e\u0648\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u0644\u06ce \u062f\u06cc\u0627\u0631\u06d5 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u06a9\u0648\u0695\u06cc\u0698\u06af\u06d5\u06cc \u062e\u06c6\u0645! \u0646\u0648\u06ce\u0698\u06ce \u0628\u06a9\u06d5 \u0648 \u0647\u0627\u0646\u0627\u06cc \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06d5 \u0628\u062f\u06d5 \u0648 \u0628\u06d5\u0631 \u0644\u06d5 \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e \u0628\u06af\u0631\u06d5 \u0648 \u0686\u062a \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u060c \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06d5\u061b \u0626\u06d5\u0645\u06d5 \u0644\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc\u06d5 \u062e\u0648\u0627 \u0648\u06cc\u0633\u062a\u0648\u0648\u06cc\u06d5\u062a\u06cc.',
    '\u0628\u06d5 \u0641\u06cc\u0632\u06d5\u0648\u06d5 \u0695\u0648\u0648 \u0644\u06d5 \u062e\u06d5\u06b5\u06a9 \u0648\u06d5\u0631\u0645\u06d5\u06af\u06ce\u0695\u06d5\u061b \u0628\u06d5 \u0644\u0648\u0648\u062a \u0628\u06d5\u0631\u0632\u06cc \u0644\u06d5\u0695\u06ce \u0645\u06d5\u0695\u06c6. \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0627\u062f\u06d5\u062f\u06d5\u0646\u061b \u0634\u0627\u06cc\u06cc \u0628\u06d5\u062e\u06c6\u0646.',
    '\u0634\u06ce\u0646\u06d5\u06cc\u06cc \u0628\u06d5\u0695\u06ce\u062f\u0627 \u0628\u0695\u06c6 \u0648 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06cc \u0646\u0632\u0645 \u0628\u067e\u06d5\u06cc\u0648\u06d5\u061b \u0686\u0646\u06a9\u06d5 \u062f\u06d5\u0646\u06af\u06cc \u0647\u06d5\u0631\u06d5 \u0646\u0627\u062e\u06c6\u0634 \u0644\u06d5 \u06a9\u06d5\u0631\u0627\u0646\u06d5\u0648\u06d5 \u062f\u06d5\u0631\u062f\u06d5\u0686\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u062a\u0627\u0646\u062f\u06cc\u0648\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u0631\u0634\u062a\u06ce \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc \u06a9\u06d5\u0648\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0648 \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0628\u06d5\u0647\u0631\u06d5\u06af\u06d5\u0644\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646\u0627 \u0695\u0698\u0627\u0646\u062f\u0648\u0648\u06d5\u061f \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5\u0634 \u06a9\u06d5\u0633\u06cc \u0648\u0627 \u0647\u06d5\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u062e\u0648\u062f\u0627 \u06a9\u06ce\u0634\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u0686\u06cc \u0644\u06ce \u0628\u0632\u0627\u0646\u0646\u060c \u06cc\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u0648\u0648 \u0628\u0646\u060c \u06cc\u0627\u0646 \u06a9\u062a\u06ce\u0628\u06ce\u06a9 \u0628\u06c6\u06cc\u0627\u0646 \u0695\u06c6\u0634\u0646 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06af\u0648\u062a\u0631\u06ce: \u0626\u06d5\u0648\u06cc \u062e\u0648\u062f\u0627 \u0646\u0627\u0631\u062f\u06cc\u06cc\u06d5 \u062e\u0648\u0627\u0631\u06ce \u0626\u06ce\u0648\u06d5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0644\u06ce \u0628\u06a9\u06d5\u0646\u061b \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc\u0646 \u06a9\u06d5 \u0628\u0627\u0648\u0627\u0646\u0645\u0627\u0646\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0628\u0648\u0648\u0646. \u0628\u0627 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06cc\u0634 \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u0628\u06a9\u0627 \u0628\u06c6\u0633\u06d5\u0631 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5 \u0626\u0627\u06af\u0631.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9 \u0628\u06d5\u062f\u06b5\u06ce\u06a9\u06cc \u067e\u0627\u06a9 \u0695\u0648\u0648\u06cc \u0644\u06d5 \u062e\u0648\u0627 \u0628\u06ce \u0648 \u0626\u0627\u06a9\u0627\u0631 \u0686\u0627\u06a9 \u0628\u06ce\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0626\u0627\u06b5\u0642\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u067e\u062a\u06d5\u0648 \u0626\u06d5\u0648 \u06af\u0631\u062a\u0648\u0648\u06cc\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0645\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9\u06cc\u0634 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u063a\u06d5\u0645\u0628\u0627\u0631\u062a \u0646\u06d5\u06a9\u0627. \u0647\u06d5\u0645\u0648\u0648 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u0645\u0627\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u062e\u06d5\u0628\u06d5\u0631\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u06d5\u06cc\u0646 \u06a9\u06d5 \u0686\u06cc\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f. \u062e\u0648\u062f\u0627 \u0644\u06d5\u0695\u0627\u0632\u06cc \u062f\u06b5\u0627\u0646\u06cc\u0634 \u0626\u0627\u06af\u0627\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u06a9\u06d5\u0645\u06ce \u0695\u0627\u06cc \u0628\u0648\u06ce\u0631\u0646\u060c \u067e\u0627\u0634\u0627\u0646 \u0646\u0627\u0686\u0627\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u0698\u0648\u0627\u0631\u06cc\u0627\u0646 \u0695\u0627\u062f\u06d5\u06a9\u06ce\u0634\u06cc\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u067e\u0631\u0633\u06cc: \u06a9\u06ce \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u061f \u0626\u06ce\u0698\u0646: \u062e\u0648\u062f\u0627. \u062a\u06c6\u0628\u06ce\u0698\u06d5: \u0634\u0648\u06a9\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631\u0634\u062a\u06ce\u06a9\u06cc \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u06af\u0634 \u0647\u06cc \u062e\u0648\u062f\u0627\u0646. \u062e\u0648\u0627 \u0644\u06d5\u06af\u0634\u062a\u0627\u0646 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5\u0648 \u0634\u0627\u06cc\u0627\u0646\u06cc \u062a\u0627\u0631\u06cc\u0641 \u06a9\u0631\u062f\u0646\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u062f\u0627\u0631 \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0647\u06d5\u0646 \u0628\u0628\u0646\u06d5 \u0642\u06d5\u06b5\u06d5\u0645\u060c \u0645\u06d5\u0631\u06a9\u06d5\u0641\u06cc\u0627\u0646 \u0644\u06d5\u0628\u06d5\u062d\u0631 \u0628\u06ce \u0648 \u062d\u06d5\u0648\u062a \u0628\u06d5\u062d\u0631\u06cc \u062f\u06cc\u0634 \u0628\u06ce\u0646\u06d5 \u067e\u0627\u06b5\u06cc\u060c \u0647\u06ce\u0634\u062a\u0627 \u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u0648\u0648\u0627\u06cc\u06cc \u0646\u0627\u06cc\u06d5. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0646 \u0648 \u0645\u0631\u0627\u0646\u062f\u0646 \u0648 \u0698\u06cc\u0627\u0646\u062f\u0646 \u0644\u06d5 \u06cc\u06d5\u06a9 \u0646\u06d5\u0641\u06d5\u0631 \u067e\u062a\u0631 \u0646\u06cc\u06cc\u06d5\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06cc\u0633\u06ce \u0648 \u062f\u06d5\u0628\u06cc\u0646\u06ce.',
    '\u0628\u06c6 \u0646\u06d5\u062a\u062f\u06cc\u0648\u06d5 \u062e\u0648\u0627 \u0634\u06d5\u0648 \u062f\u06d5\u0695\u06c6\u0698 \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u06ce\u0634\u06ce \u0648 \u0695\u06c6\u0698\u06cc\u0634 \u062f\u06d5\u0634\u06d5\u0648 \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u06ce\u0634\u06ce\u061b \u062e\u06c6\u0631\u0648 \u0647\u06d5\u06cc\u06a4\u06cc\u0634\u06cc \u06a9\u06d5\u0648\u06cc \u06a9\u0631\u062f\u061f \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06ce \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648 \u0644\u06d5 \u062a\u06d5\u06a4\u06af\u06d5\u0695\u062f\u0627\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u0628\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u0628\u06d5\u0645\u06d5 \u0628\u0632\u0627\u0646\u0646 \u062e\u0648\u062f\u0627 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0647\u0627\u0646\u0627 \u0628\u06c6 \u0647\u06d5\u0631\u0634\u062a\u06ce \u0628\u06d5\u0631\u0646 - \u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 - \u06a9\u0627\u0631\u06ce \u067e\u0648\u0648\u0686\u06d5. \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u06cc \u0647\u06d5\u0631\u06d5\u0645\u06d5\u0632\u0646\u060c \u0647\u06d5\u0631\u062e\u0648\u062f\u0627\u06cc\u06d5.',
    '\u062a\u06c6 \u0646\u06d5\u062a\u062f\u06cc\u0648\u06d5 \u0644\u06d5\u0633\u06c6\u0646\u06af\u06d5\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627\u0648\u06d5 \u06af\u06d5\u0645\u06cc\u06d5 \u0644\u06d5\u062f\u0648\u0695 \u062f\u06d5\u0631\u06cc\u0627\u062f\u0627 \u0644\u06d5 \u06af\u06d5\u0695\u0627\u0646\u062f\u0627\u06cc\u06d5\u061b \u062a\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u062a\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u0627\u061f \u0647\u06d5\u0631 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0632\u06c6\u0631 \u0628\u06d5\u06b5\u06af\u06d5 \u0647\u06d5\u0646 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06c6\u0695\u0627\u06af\u0631\u0627\u0646\u06cc \u0634\u0648\u06a9\u0631\u0628\u0698\u06ce\u0631.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u067e\u06ce\u0644\u06cc \u0632\u0644\u06cc \u0648\u06d5\u06a9 \u0647\u06d5\u0648\u0695\u06cc \u067e\u0695\u060c \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u06ce\u0634\u0646\u060c \u0628\u06d5 \u062f\u06b5\u067e\u0627\u06a9\u06cc \u0648 \u0628\u0695\u0648\u0627\u06cc \u0628\u06ce \u063a\u06d5\u0634 \u0644\u06d5\u0628\u06d5\u0631 \u062e\u0648\u062f\u0627 \u062f\u06d5\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5. \u0648\u06d5\u062e\u062a\u06ce \u0695\u0632\u06af\u0627\u0631\u06cc \u06a9\u0631\u062f\u0646 \u0648 \u06af\u06d5\u06cc\u0646\u06d5 \u0633\u06d5\u0631\u0628\u06d5\u0698\u060c \u0647\u06ce\u0646\u062f\u06ce \u0644\u06d5\u0648\u0627\u0646 \u0645\u0627\u0645 \u0646\u0627\u0648\u0646\u062c\u06cc\u0646. \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u062d\u0627\u0634\u0627\u06cc \u0646\u06cc\u06cc\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u06af\u06d5\u0644\u06cc \u0641\u0631\u06cc\u0648\u0628\u0627\u0632\u0627\u0646\u06cc \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce\u061b \u062a\u0631\u0633\u06cc \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u0634\u062a\u0627\u0646 \u0647\u06d5\u0628\u06ce \u06a9\u06d5 \u0647\u06cc\u0686 \u0628\u0627\u0648\u06a9\u06ce\u06a9 \u0626\u0648\u0628\u0627\u06b5\u06cc \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u062e\u06c6\u0634\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u06cc\u06d5 \u0648 \u0647\u06cc\u0686 \u0641\u0631\u0632\u06d5\u0646\u062f\u06ce\u06a9\u06cc\u0634 \u0628\u06c6 \u0628\u0627\u0628\u06cc \u0633\u0648\u0648\u062f\u06cc \u0646\u0627\u0628\u06ce. \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0646\u0627\u0628\u06ce \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0698\u06cc\u0646\u06cc \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627 \u0641\u0631\u06cc\u0648\u062a\u0627\u0646 \u062f\u0627. \u0646\u06d5\u0634\u0686\u0646 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u062e\u0634\u0646\u062f\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627 \u0641\u0631\u06cc\u0648\u062a\u0627\u0646 \u0628\u062f\u0627.',
    '\u0632\u0627\u0646\u0633\u062a\u06cc \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u0628\u0627\u0631\u0627\u0646\u06d5\u0634 \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0628\u0627\u0631\u06ce\u0646\u06ce \u0648 \u062f\u06d5\u0634\u0632\u0627\u0646\u06ce \u062a\u06c6\u0644\u06cc \u0632\u06af \u067e\u0695\u0627\u0646 \u0686\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5. \u06a9\u06d5\u0633 \u0646\u0627\u0632\u0627\u0646\u06ce \u06a9\u0627\u0631\u06cc \u0633\u0628\u06d5\u06cc \u0695\u06c6\u0698\u06cc \u0686\u06cc\u06cc\u06d5 \u0648 \u06a9\u06d5\u0633 \u0646\u0627\u0632\u0627\u0646\u06ce \u0644\u06d5 \u06a9\u0627\u0645\u06d5 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646 \u062f\u06d5\u0645\u0631\u06ce. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0644\u06cc\u0641\u060c \u0644\u0627\u0645\u060c \u0645\u06cc\u0645.',
    '\u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0644\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062f\u0646\u06cc\u0627\u0648\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5 \u0648 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0698\u0646: \u0647\u06d5\u0631 \u0644\u06d5\u062e\u06c6\u0648\u06d5 \u0626\u06d5\u0645 \u062f\u0631\u06c6\u06cc\u06d5\u06cc \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u0648\u0648\u06d5\u061f \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0695\u0627\u06cc\u06d5\u061b \u062a\u0627 \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0645\u060c \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u0647\u06c6\u0632\u06ce\u06a9 \u0628\u06ce\u0646\u06cc\u060c \u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6 \u06a9\u06d5\u0633 \u062a\u0631\u0633\u06cc \u0648\u06d5\u0628\u06d5\u0631 \u0646\u06d5\u0646\u0627\u0648\u0646\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06d5\u0695\u06ce\u06cc \u0695\u0627\u0633\u062a \u0628\u0632\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648\u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648 \u062f\u0648\u0648\u06d5\u062f\u0627 \u0628\u06d5 \u0634\u06d5\u0634 \u0695\u06c6\u0698 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06cc \u062e\u06c6\u06cc \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0648\u06d5. \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648\u060c \u06af\u06d5\u0648\u0631\u06d5 \u0648 \u062a\u06a9\u0627\u06a9\u0627\u0631\u06ce\u06a9\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5. \u062f\u06d5\u0633\u0627 \u0628\u06c6 \u0628\u06cc\u0631 \u0646\u0627\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061f',
    '\u0647\u06d5\u0631\u0686\u06cc \u06a9\u0627\u0631\u06d5 \u0644\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u067e\u06ce\u06a9\u06cc \u062f\u06ce\u0646\u06ce \u0648 \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u062f\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u0634\u06ce\u062a\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc\u061b \u06a9\u06d5\u0628\u06d5\u067e\u06ce\u06cc \u0698\u0645\u0627\u0631\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0632\u0627\u0631 \u0633\u0627\u06b5\u06d5.',
    '\u0626\u06d5\u0648\u06d5 \u0632\u0627\u0646\u0627 \u0628\u06d5 \u0646\u0627\u062f\u06cc\u0627\u0631 \u0648 \u062f\u06cc\u0627\u0631\u0627\u0646 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc- \u0628\u06d5\u0648\u067e\u06d5\u0695\u06cc \u0695\u06ce\u06a9\u06cc \u0648 \u0644\u06d5\u0628\u0627\u0631\u06cc - \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f \u0648 \u0645\u0631\u06c6\u0634\u06cc - \u0644\u06d5 \u0633\u06d5\u0631\u06d5\u062a\u0627\u062f\u0627 - \u0644\u06d5 \u0645\u0627\u06a9\u06cc \u0642\u0648\u0695 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u0644\u06d5\u067e\u0627\u0634\u0627\u0646 \u0646\u06d5\u062a\u06d5\u0648\u06d5\u06cc \u0626\u06d5\u0645\u06cc \u0644\u06d5\u067e\u0627\u06b5\u0627\u0648\u062a\u06d5\u06cc \u0686\u06c6\u0695\u06d5\u0626\u0627\u0648\u06ce\u06a9\u06cc \u06a9\u06d5\u0645 \u0628\u0627\u06cc\u06d5\u062e \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0695\u0627\u0633\u062a\u06d5\u0648\u067e\u0627\u0633\u062a\u06d5\u06cc \u06a9\u0631\u062f \u0648 \u0644\u06d5 \u06af\u06cc\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0641\u0648\u0648\u06cc \u067e\u06ce\u062f\u0627 \u06a9\u0631\u062f\u061b \u06af\u0648\u06ce\u0686\u06a9\u06d5 \u0648 \u0686\u0627\u0648 \u0648 \u062f\u06b5\u06cc\u0634\u06cc \u062f\u0627\u0648\u06d5 \u0628\u06d5 \u0626\u06ce\u0648\u06d5. \u06a9\u06d5\u0645\u062a\u0627\u0646 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0628\u0698\u06ce\u0631\u0646.',
    '\u06af\u0648\u062a\u06cc\u0634\u06cc\u0627\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u062e\u06cc \u0647\u06d5\u0631\u062f\u0627 \u06af\u0648\u0645 \u0628\u0648\u0648\u06cc\u0646\u060c \u0626\u0627\u062e\u06c6 \u062f\u06cc\u0633\u0627\u0646 \u062f\u06d5\u0698\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0644\u06d5\u0646\u0648\u06ce \u062f\u06d5\u0628\u06cc\u0646\u06d5\u0648\u06d5 \u0645\u0631\u06c6\u061f \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648 \u0641\u0631\u0634\u062a\u06d5\u06cc \u067e\u06ce\u0645\u06ce\u0631\u062f - \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u062f\u0631\u0627\u0648\u06d5\u062a\u06d5 \u062f\u06d5\u0633\u062a - \u062f\u06d5\u0648\u0645\u0631\u06ce\u0646\u06ce \u0648 \u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u0628\u0631\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648.',
    '\u06a9\u0627\u062a\u06ce \u062a\u06c6 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u0633\u06d5\u0631\u0634\u06c6\u0695\u06cc \u0628\u0628\u06cc\u0646\u06cc\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u0644\u0627\u0645\u0627\u0646! \u062f\u06cc\u062a\u0645\u0627\u0646 \u0648 \u0628\u06cc\u0633\u062a\u0645\u0627\u0646 \u0648 \u062a\u06d5\u0648\u0627\u0648 \u0628\u0627\u0648\u06d5\u0695\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0633\u0627 \u062a\u06c6 \u0628\u0645\u0627\u0646\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5\u061b \u0626\u06d5\u0645\u062c\u0627\u0631\u06d5 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632\u0645\u0627\u0646 \u0628\u06a9\u0631\u062f\u0627\u06cc\u06d5\u060c \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u0645\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648\u062e\u06c6 \u0634\u0627\u0631\u06d5\u0632\u0627 \u062f\u06d5\u06a9\u0631\u062f\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0645\u0646 \u0628\u06d5\u06b5\u06ce\u0646\u0645 \u062f\u0627\u0648\u06d5 \u06a9\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 - \u0644\u06d5\u062e\u06ce\u06b5\u06cc \u062c\u0646\u06c6\u06a9\u06d5\u0648 \u0645\u0631\u06c6 - \u062a\u0698\u06cc \u062f\u06d5\u06a9\u06d5\u0645.',
    '\u062f\u06d5\u0628\u0686\u06ce\u0698\u0646! \u0644\u06d5 \u0628\u06cc\u0631\u062a\u0627\u0646 \u0686\u0648\u0648 \u06a9\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0626\u06d5\u0645 \u0695\u06c6\u0698\u06d5 \u062f\u06d5\u0628\u0646\u061b \u0626\u06ce\u0645\u06d5\u0634 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0628\u06cc\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u0644\u06d5 \u0633\u06c6\u0646\u06af\u06d5\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u062f\u0627 \u062f\u06d5\u062a\u0627\u0646 \u06a9\u0631\u062f\u060c \u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0626\u0627\u0632\u0627\u0631 \u0628\u0686\u06ce\u0698\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0646 \u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06a9\u06d5 \u0647\u06d5\u0631\u06af\u0627 \u0626\u06d5\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06af\u06d5\u0644\u06cc\u0627\u0646\u06d5 \u0648\u06d5\u0628\u06cc\u0631 \u0628\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5\u060c \u062e\u06c6 \u062f\u06d5\u062e\u06d5\u0646\u06d5 \u0633\u06d5\u0631 \u0633\u0648\u0698\u062f\u06d5\u0648\u06d5 \u0648 \u0628\u06d5\u0633\u0648\u067e\u0627\u0633\u06d5\u0648\u06d5 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0626\u06d5\u062f\u06d5\u0646 \u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0632\u0644 \u0646\u0627\u0632\u0627\u0646\u0646\u061b',
    '\u0628\u0631\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u06ce \u062e\u06d5\u0641\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062c\u0627\u0695\u0632 \u062f\u06d5\u0628\u06ce \u0648 \u0628\u06d5 \u062a\u0631\u0633 \u0648 \u0628\u06d5 \u0647\u0648\u0645\u06ce\u062f\u06d5\u0648\u06d5 \u0647\u0627\u0646\u0627 \u0648\u06d5\u0628\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6 \u062f\u06d5\u0628\u06d5\u0646 \u0648\u060c \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0648\u0646 \u062e\u06ce\u0631\u06ce \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u06a9\u06d5\u0633 \u0646\u0627\u0632\u0627\u0646\u06ce \u0644\u06d5 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u0626\u06d5\u0648\u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u0686 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u0686\u0627\u0648\u0695\u06c6\u0634\u0646\u06d5\u0648\u06a9\u06d5\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u062a\u0627\u0642\u06d5\u062a \u06a9\u0631\u0627\u0648\u06d5.',
    '\u0686\u06c6\u0646 \u06a9\u06d5\u0633\u06ce \u0628\u0695\u0648\u0627\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5\u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627\u06cc \u062f\u0627\u0648\u06d5\u061f \u0648\u06d5\u06a9 \u06cc\u06d5\u06a9\u062a\u0631\u0646\u06cc\u0646.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u06af\u0646\u060c \u0644\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u060c \u0626\u06d5\u0646\u0648\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u0648\u0695\u0628\u0627\u063a\u0627\u062a\u06d5.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627\u0648\u06d5\u060c \u0628\u06b5\u06ce\u0633\u06d5\u06cc \u0626\u0627\u06af\u0631 \u0634\u0648\u06ce\u0646\u06cc\u0627\u0646\u06d5 \u0648 \u062a\u0627 \u0628\u062e\u0648\u0627\u0632\u0646 \u0644\u06ce\u06cc \u062f\u06d5\u0631\u0628\u0686\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631. \u067e\u06ce\u0634\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u062f\u06d5\u0628\u0686\u06ce\u0698\u0646 \u0698\u0627\u0646\u06cc \u0628\u06d5 \u0626\u0627\u06af\u0631 \u0633\u0648\u0648\u062a\u0627\u0646\u06ce\u060c \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u062f\u0631\u06c6\u062a\u0627\u0646 \u0632\u0627\u0646\u06cc.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u062c\u06af\u06d5 \u0626\u0627\u0632\u0627\u0631\u06d5 \u0647\u06d5\u0631\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u06a9\u06d5\u060c \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u0634\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0686\u06ce\u0698\u06cc\u0646\u061b \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u06a9\u06ce \u0644\u06d5\u0648\u06a9\u06d5\u0633\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5\u060c \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0648\u06d5\u0628\u06cc\u0631 \u0628\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5\u060c \u067e\u0627\u0634\u0627\u0646 \u0648\u0627\u0632\u06cc \u0644\u06ce \u0628\u0647\u06ce\u0646\u06ce\u061f \u062a\u06c6\u06b5\u06d5 \u0644\u06d5\u0648 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06d5 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u06a9\u062a\u06ce\u0628\u0645\u0627\u0646 \u0628\u06d5\u0645\u0648\u0648\u0633\u0627\u0634 \u062f\u0627\u0648\u06d5\u061b \u06a9\u0631\u062f\u06cc\u0634\u0645\u0627\u0646\u06d5 \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646\u062f\u06d5\u0631 \u0628\u06c6 \u0632\u0627\u0631\u0648\u0632\u06ce\u0686\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644. \u0633\u0627 \u062a\u06c6\u0634 \u0644\u06d5 \u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648 \u0628\u0648\u0648\u0646\u06cc \u0626\u06d5\u0645 [\u0642\u0648\u0631\u0626\u0627\u0646\u06d5] \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0628\u06d5.',
    '\u0644\u06d5\u0648\u0627\u0646\u06cc\u0634\u062f\u0627 \u0686\u06d5\u0646\u062f \u06a9\u06d5\u0633\u06ce\u06a9\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0695\u06ce\u0628\u06d5\u0631\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0695\u06ce\u0646\u0648\u06ce\u0646\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0628\u06a9\u06d5\u0646\u061b \u0686\u0648\u0646\u06a9\u06d5 \u062a\u0648\u0627\u0646\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631\u062a\u0646\u06cc\u0627\u0646 \u062a\u06ce\u062f\u0627\u0628\u0648\u0648\u061b \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0628\u0648\u0648.',
    '\u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 - \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 - \u062e\u06c6\u06cc \u0628\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5 \u0695\u0627\u062f\u06d5\u06af\u0627 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u067e\u06ce\u06a9 \u0646\u06d5\u062f\u06d5\u0647\u0627\u062a\u0646.',
    '\u0626\u06d5\u0648 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0628\u06d5\u0631\u06d5\u06cc \u0645\u0631\u06c6\u06cc\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0645\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u062f\u0627 \u062f\u06d5\u062e\u0648\u0644\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u0642\u0695 \u06a9\u0631\u062f\u0646\u060c \u0626\u0627\u062e\u06c6 \u0695\u0627\u0633\u062a\u06d5\u0642\u06cc\u0646\u06d5\u06cc \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0631\u062e\u0633\u062a\u061f \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0647\u06d5\u0646\u060c \u0645\u06d5\u0631 \u0646\u0627\u0628\u06cc\u0633\u0646\u061f!',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u0627\u0628\u06cc\u0646\u0646 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0632\u06d5\u0648\u06cc \u0642\u0627\u0642\u0695 \u0626\u0627\u0648 \u062f\u06d5\u0646\u06ce\u0631\u06cc\u0646\u060c \u0695\u0648\u0648\u06d5\u06a9\u06cc \u0648\u0627\u06cc \u067e\u06ce \u0634\u06cc\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u062a\u06ce\u0631\u06d5\u062e\u06c6\u0631\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0645\u0627\u06b5\u0627\u062a\u06cc\u0634\u06cc\u0627\u0646\u06d5\u061f \u0645\u06d5\u0631 \u0646\u0627\u0628\u06cc\u0646\u0646\u061f!',
    '\u0626\u06ce\u0698\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0642\u0633\u06d5\u0648 \u0695\u0627\u0633\u062a\u06d5\u060c \u0626\u06d5\u0648 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06d5 \u06a9\u06d5\u06cc \u062f\u06d5\u0628\u06ce\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0695\u06c6\u0698\u06cc \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06ce\u060c \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u0695\u0648\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u0642\u0627\u0632\u0627\u0646\u062c\u06ce\u06a9\u06cc \u0628\u06c6\u06cc\u0627\u0646 \u0646\u0627\u0628\u06ce\u061b \u0647\u06cc\u0686 \u0645\u06c6\u06b5\u06d5\u062a\u06cc\u0634\u06cc\u0627\u0646 \u0646\u0627\u062f\u0631\u06ce.',
    '\u0633\u0627 \u062a\u06c6 \u0648\u0627\u0632 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06ce\u0646\u06d5 \u0648 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u06d5\u061b \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634\u06d5 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0646.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u062a\u06c6 \u062a\u0631\u0633\u062a \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06ce \u0648 \u0645\u06d5\u0686\u06c6 \u0633\u06d5\u0631 \u0642\u0633\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627 \u0646\u0627\u0646\u0627\u0633\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0648\u0648\u0695\u0648\u0648\u06cc\u06cc \u062f\u06d5\u06a9\u06d5\u0646. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627 \u0648 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5 \u067e\u06ce\u06a9 \u0628\u06ce\u0646\u06d5\u060c \u06a9\u06d5 \u0644\u062c\u06d5\u0645 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6\u062a \u062f\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0647\u06d5\u0631\u062e\u06c6\u062a \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u067e\u06ce\u0631\u06d5\u060c \u062e\u0648\u0627 \u0628\u0631\u06cc\u06a9\u0627\u0631\u062a \u0628\u06ce\u060c \u0628\u06d5\u0633\u06d5.',
    '\u062e\u0648\u0627 \u0628\u06c6 \u0647\u06d5\u0646\u0627\u0648\u06cc \u0647\u06cc\u0686 \u067e\u06cc\u0627\u0648\u06ce\u060c \u062c\u0648\u0648\u062a\u06ce \u062f\u06b5\u06cc \u062f\u0627\u0646\u06d5\u0646\u0627\u0648\u06d5. \u0698\u0646\u0627\u0646\u06cc\u0634\u062a\u0627\u0646 \u06a9\u06d5 \u0628\u0627\u0646\u06af \u062f\u06d5\u06a9\u06d5\u0646: \u00ab\u0626\u06d5\u062a\u06c6 \u0648\u06d5\u06a9\u0648\u0648 \u062f\u0627\u06cc\u06a9\u06cc \u062e\u06c6\u0645\u06cc\u00bb \u0646\u06d5\u06cc\u06a9\u0631\u062f\u0648\u0648\u0646 \u0628\u06d5 \u062f\u0627\u06cc\u06a9\u06cc \u0626\u06ce\u0648\u06d5. \u0626\u06d5\u0648 \u0632\u0695\u06a9\u0648\u0695\u06d5\u06a9\u0627\u0646\u06cc\u0634\u062a\u0627\u0646\u06cc \u0646\u06d5\u06a9\u0631\u062f\u06c6\u062a\u06d5 \u06a9\u0648\u0695\u06cc \u0626\u06ce\u0648\u06d5 \u0648 \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0642\u0633\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646\u06d5 \u0648 \u0647\u06d5\u0631 \u0644\u06d5 \u0632\u0627\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646\u06d5\u0648\u06d5 \u062f\u0627\u06a9\u06d5\u0648\u062a\u0648\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u06cc \u0695\u0627\u0633\u062a\u06d5 \u062f\u06d5\u06cc\u06b5\u06ce \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0695\u06ce\u06af\u06d5 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u0647\u06d5\u0631 \u0628\u06d5 \u0646\u0627\u0648\u06cc \u0628\u0627\u0648\u06a9\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646\u061b \u0648\u0627 \u0644\u06d5 \u0628\u0627\u0631\u062a\u0631\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0628\u06cc\u0634\u06cc\u0627\u0646 \u0646\u0627\u0646\u0627\u0633\u0646\u060c \u0628\u0631\u0627\u06cc \u062f\u06cc\u0646\u06cc \u0648 \u062f\u06c6\u0633\u062a\u06cc \u0626\u06ce\u0648\u06d5\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06ce\u0634\u0648\u0648\u0634 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0628\u06d5 \u0647\u06d5\u06b5\u06d5\u0686\u0648\u0628\u0646\u060c \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u062a\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u0627\u0628\u06ce. \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u062f\u06b5\u06d5\u0648\u06d5 \u0648 \u0628\u06d5 \u0626\u0627\u0646\u0642\u06d5\u0633 \u0628\u06ce\u060c [\u0628\u06d5\u0631\u067e\u0631\u0633 \u0626\u06d5\u0628\u0646]. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u06c6 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646\u06cc\u0634 \u0641\u06d5\u0695\u0632\u062a\u0631\u06d5 \u0648\u061b \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634 \u062f\u0627\u06cc\u06a9\u06cc \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646\u0646. \u0648\u06d5\u06a9 \u0644\u06d5 \u0646\u0648\u0633\u0631\u0627\u0648\u06cc \u062e\u0648\u0627\u062f\u0627\u06cc\u06d5\u060c \u062e\u0648\u06ce\u0634 \u0648 \u062e\u0632\u0645\u0627\u0646 [\u0628\u06c6 \u0645\u06cc\u0631\u0627\u062a \u0628\u0631\u062f\u0646 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631] \u0634\u06cc\u0627\u0648\u062a\u0631\u0646 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0626\u0627\u0648\u0627\u0631\u06d5 \u0628\u0648\u0648\u0646\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0647\u06d5\u0631 \u0628\u062a\u0627\u0646\u06d5\u0648\u06ce \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5\u062f\u06c6\u0633\u062a\u0627\u0646\u0648\u0648 \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u0645\u06d5\u0634 \u0644\u06d5\u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5\u06a9\u06d5\u062f\u0627 \u062e\u06d5\u062a \u062f\u0631\u0627\u0648\u06d5.',
    '[\u0628\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0628\u06cc\u0631] \u0626\u06d5\u0648\u06ce \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0626\u06d5\u0633\u062a\u0627\u0646\u062f. \u0644\u06d5\u062a\u06c6 \u0648 \u0646\u0648\u0648\u062d \u0648 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0648 \u0645\u0648\u0648\u0633\u0627 \u0648 \u0639\u06cc\u0633\u0627\u06cc \u06a9\u0648\u0695\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645 \u0648 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0632\u06c6\u0631 \u067e\u062a\u06d5\u0648\u0645\u0627\u0646 \u0633\u0627\u0646\u062f.',
    '\u062a\u0627 \u0644\u06d5 \u0695\u0627\u0633\u062a\u0627\u0646 - \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 - \u067e\u0631\u0633\u06cc\u0627\u0631 \u0628\u06a9\u0627\u061b \u0628\u06c6 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646\u06cc \u0633\u0627\u0632 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5 \u0628\u06cc\u0631 \u0645\u06d5\u06a9\u06d5\u0646 \u0686\u06c6\u0646 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u06a9\u0631\u062f\u061b \u0644\u06d5\u0634\u06a9\u0631\u06af\u06d5\u0644\u06ce\u06a9\u062a\u0627\u0646 \u0647\u0627\u062a\u06d5 \u0633\u06d5\u0631\u061b \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0645\u0627\u0646 \u0648\u06ce\u0695\u0627\u06cc \u0686\u06d5\u0646\u062f \u0644\u06d5\u0634\u06a9\u0631 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646\u061b \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u062a\u0627\u0646 \u062f\u06d5\u062f\u06cc\u062a\u0646. \u0647\u06d5\u0631\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627 \u0628\u0627\u0634 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u0644\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u062f\u0627 \u06a9\u06d5 \u0644\u06d5 \u0698\u0648\u0648\u0631 \u0648 \u0644\u06d5 \u0698\u06ce\u0631\u06d5\u0648\u06d5 \u0628\u06c6\u062a\u0627\u0646 \u0647\u0627\u062a\u0646\u060c \u0686\u0627\u0648\u062a\u0627\u0646 \u0695\u06d5\u0634\u06a9\u06d5\u0648\u067e\u06ce\u0634\u06a9\u06d5\u06cc \u062f\u06d5\u06a9\u0631\u062f\u061b [\u062f\u06b5\u062a\u0627\u0646 \u0648\u06d5\u062a\u06d5\u067e\u06d5\u062a\u06d5\u067e \u06a9\u06d5\u0648\u062a \u0648] \u06af\u06d5\u06cc\u06cc\u0634\u062a\u06d5 \u0642\u0648\u0695\u0642\u0648\u0695\u0627\u06af\u06d5\u062a\u0627\u0646\u061b \u0632\u06c6\u0631 \u062e\u06d5\u06cc\u0627\u06b5\u06cc \u062c\u06c6\u0631\u0627\u0648\u062c\u06c6\u0631\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06d5 \u062f\u06b5\u062f\u0627 \u062f\u06d5\u0647\u0627\u062a.',
    '\u0644\u06d5\u0648\u0634\u0648\u06ce\u0646\u06d5\u062f\u0627 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0647\u0627\u062a\u06d5\u06af\u06c6\u0695\u06ce \u0648 \u0628\u06d5 \u0644\u06d5\u0631\u0632\u06cc\u0646\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0628\u06d5 \u0647\u06ce\u0632\u060c \u062f\u06b5\u06cc \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u06a9\u06d5\u0648\u062a\u06d5 \u0644\u06d5\u0631\u0632\u06d5.',
    '\u0644\u06d5\u0648 \u0639\u0627\u0646\u06d5\u0634\u062f\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0648\u0648\u0695\u0648\u0648 \u0628\u0648\u0648\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u063a\u06d5\u0632\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u062f\u06b5\u062f\u0627 \u0628\u0648\u0648\u060c \u0647\u06d5\u0631 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u06d5\u0648\u0628\u06d5\u06b5\u06ce\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062f\u06d5\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u0631 \u0628\u06c6 \u0641\u0631\u06cc\u0648\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0648\u0648.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646 \u06af\u0627\u0632\u06cc \u062f\u06d5\u06a9\u0631\u062f: \u0626\u06d5\u06cc \u062f\u0627\u0646\u06cc\u0634\u062a\u0648\u0627\u0646\u06cc \u0645\u06d5\u062f\u06cc\u0646\u06d5! \u0626\u06ce\u0631\u06d5 \u062c\u06ce\u06af\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u06cc\u06d5\u060c \u062f\u06d5\u06cc \u0648\u06d5\u06af\u06d5\u0695\u06ce\u0646. \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0647\u0627\u062a\u0646 \u0626\u06cc\u0632\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062f\u06d5\u062e\u0648\u0627\u0633\u062a: \u06a9\u06d5\u0633\u0645\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0645\u0627\u06b5\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u062f\u0627\u0646\u06d5\u0646\u0627\u0648\u06d5 \u0626\u0627\u06af\u0627\u06cc \u0644\u06ce \u0628\u06ce. \u0645\u0627\u06b5\u06cc\u0627\u0646 \u0628\u06ce \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631 \u0646\u06d5\u0628\u0648\u0648\u060c \u0647\u06d5\u0631 \u0646\u06cc\u0627\u0632\u06cc \u0695\u0627\u06a9\u0631\u062f\u0646\u06cc\u0627\u0646 \u0628\u0648\u0648.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0648\u0631\u06cc \u0645\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0644\u06ce \u06af\u06cc\u0631\u0627\u0628\u0627 \u0648 \u0628\u06cc\u0627\u0646 \u06af\u0648\u062a\u0627\u06cc\u06d5: \u0644\u06d5 \u062f\u06cc\u0646 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u0628\u0646\u06d5\u0648\u06d5\u061b \u0647\u06ce\u0646\u062f\u06d5 \u0632\u06c6\u0631\u06cc \u0646\u06d5\u062f\u06d5\u062e\u0627\u06cc\u0627\u0646\u062f\u060c \u0644\u06d5 \u062f\u06cc\u0646 \u067e\u0627\u0634\u06af\u06d5\u0632 \u062f\u06d5\u0628\u0648\u0648\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646 \u067e\u06ce\u0634\u062a\u0631 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u0627\u0628\u0648\u0648 \u0628\u06d5 \u062e\u0648\u062f\u0627\u060c \u06a9\u06d5 \u0644\u06d5\u0628\u06d5\u0631 \u062f\u0648\u0698\u0645\u0646 \u0646\u0627\u0628\u06d5\u0632\u0646\u061b \u062f\u06cc\u0627\u0631\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627\u062f\u0627\u0646 \u0644\u06ce \u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5\u0634\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0695\u0627\u06a9\u0631\u062f\u0646 \u0644\u06d5\u0645\u0631\u062f\u0646 \u06cc\u0627\u0646 \u0644\u06d5 \u06a9\u0648\u0634\u062a\u0646\u060c \u0647\u06cc\u0686 \u062f\u0627\u062f\u06ce\u06a9\u06cc \u0626\u06ce\u0648\u06d5 \u0646\u0627\u062f\u0627\u061b \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5\u0634 \u0647\u06d5\u0631 \u06a9\u06d5\u0645\u06ce\u06a9\u06cc \u062a\u0631 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0695\u0627\u06cc\u062f\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u062e\u0648\u0627\u0632\u06ce \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0628\u06ce\u0646\u06ce\u060c \u06cc\u0627\u0646 \u0686\u0627\u06a9\u06d5 \u0648 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5 \u0628\u06a9\u0627\u060c \u06a9\u06ce \u062f\u06d5\u0633\u062a\u06cc \u0648\u06d5\u0628\u06d5\u0631\u062f\u06d5\u0647\u06ce\u0646\u06ce \u0648 \u062f\u06d5\u0648\u067e\u0627\u0631\u06ce\u0632\u06ce\u061f \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0646\u06d5 \u0633\u06d5\u0631\u0648\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0633 \u062f\u06d5\u06a9\u06d5\u0648\u06ce \u0648 \u0646\u06d5 \u06a9\u06d5\u0633 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u0644\u06d5\u0646\u06af\u06c6 \u062f\u06d5\u0628\u0648\u0648\u0646\u06d5 \u0644\u06d5\u0645\u067e\u06d5\u0631 \u0648 \u06a9\u06ce\u0634 \u0628\u0648\u0648\u0646 \u062f\u06d5\u0646\u06af\u06cc \u0628\u0631\u0627\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627: \u00ab\u0648\u06d5\u0631\u0646\u06d5 \u0644\u0627\u0645\u0627\u0646\u00bb. \u06a9\u06d5\u0645\u062a\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5\u0645 \u0634\u06d5\u0695\u06d5\u062f\u0627 \u0628\u06d5\u0634\u062f\u0627\u0631 \u062f\u06d5\u0628\u0648\u0648\u0646.',
    '\u0644\u06d5\u062d\u0627\u0646\u062f \u0626\u06ce\u0648\u06d5 \u0686\u0627\u0648\u0686\u0646\u06c6\u06a9\u0646\u061b \u0647\u06d5\u0631\u06af\u0627 \u062a\u0631\u0633\u06ce \u062f\u06ce\u062a\u06d5\u067e\u06ce\u0634\u06ce\u060c \u062f\u06d5\u06cc\u0627\u0646\u0628\u06cc\u0646\u06cc \u0647\u06d5\u0631 \u0632\u06d5\u0642 \u0644\u06d5 \u062a\u06c6 \u062f\u06d5\u0695\u0648\u0627\u0646\u0646 \u0648 \u0686\u0627\u0648\u06cc\u0627\u0646 \u0633\u067e\u06cc \u0647\u06d5\u06b5\u062f\u06d5\u06af\u06d5\u0695\u06ce\u061b \u0648\u06d5\u06a9 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u062a\u0631\u0633\u06cc \u0645\u0631\u062f\u0646 \u0628\u06ce \u0647\u06c6\u0634 \u0628\u0648\u0648 \u0628\u06ce. \u0648\u06d5\u062e\u062a\u06ce \u062a\u0631\u0633\u06cc\u0634 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06d5\u0686\u06ce\u060c \u06af\u06d5\u0632\u0627\u0631\u06d5\u062a\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u06ce\u0634\u0646\u061b \u062f\u06d5\u0633\u062a \u0628\u06d5\u0632\u0645\u0627\u0646 \u0634\u0695\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0686\u0627\u0648\u0646\u06d5\u0632\u06ce\u0631\u06cc \u062a\u0627\u06b5\u0627\u0646\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0641\u06cc\u0695\u06c6\u0686\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0645\u06d5\u0634 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u0633\u0627\u0646\u06d5.',
    '\u0648\u0627 \u0626\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u062f\u0648\u0698\u0645\u0646\u06d5 \u0647\u0627\u0648\u062f\u06d5\u0633\u06d5\u06a9\u0627\u0646 \u0646\u06d5\u0695\u06c6\u06cc\u0634\u062a\u0648\u0648\u0646. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u0626\u06d5\u0648 \u0647\u0627\u0648\u062f\u06d5\u0633\u0627\u0646\u06d5 \u0628\u06ce\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0626\u0627\u0648\u0627\u062a \u0626\u06d5\u062e\u0648\u0627\u0632\u0646 \u0648\u06d5\u0644 \u0639\u0627\u0631\u06d5\u0648\u06d5 \u0686\u06c6\u06b5\u0646\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u062f\u0627 \u0628\u0627\u0646 \u0648 \u0647\u06d5\u0631 \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u0627\u0646 \u0628\u067e\u0631\u0633\u06cc\u0628\u0627. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648\u062a\u0627\u0646\u06cc\u0634 \u0645\u0627\u0628\u0627\u0646 - \u06a9\u06d5\u0645\u06cc\u0627\u0646 \u0646\u06d5\u0628\u06ce - \u0644\u06d5 \u0634\u06d5\u0695\u062f\u0627 \u0628\u06d5\u0634\u062f\u0627\u0631 \u0646\u06d5\u062f\u06d5\u0628\u0648\u0648\u0646.',
    '\u0628\u06ce \u0633\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u0633\u06d5\u0631\u0645\u06d5\u0634\u0642\u06cc \u0686\u0627\u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0648 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0647\u06cc\u0648\u0627\u062f\u0627\u0631\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u06cc\u0627\u062f\u06cc \u062e\u0648\u0627\u06cc\u06d5.',
    '\u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0686\u0627\u0648\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u06d5 \u0626\u06d5\u0648 \u062f\u0648\u0698\u0645\u0646\u06d5 \u0647\u0627\u0648\u062f\u06d5\u0633\u0627\u0646\u06d5\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0647\u06d5\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u0626\u06ce\u0645\u06d5 \u062f\u0627\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06d5\u06b5\u06ce\u0646\u06ce \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0628\u06cc\u062f\u06d5\u0646\u060c \u0695\u0627\u0633\u062a \u062f\u06d5\u0631\u062f\u06d5\u0686\u06ce \u0648 \u0626\u06cc\u062a\u0631 \u0628\u0695\u0648\u0627 \u0648 \u0647\u06d5\u0633\u062a\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u0631\u062f\u0646\u06cc\u0627\u0646 \u067e\u062a\u0631 \u06af\u06d5\u0634\u06d5\u06cc \u06a9\u0631\u062f.',
    '\u067e\u06cc\u0627\u0648\u06cc \u0648\u0627 \u0647\u06d5\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u062f\u0627\u060c \u0626\u06d5\u0648 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0628\u06d5\u062e\u0648\u0627\u06cc\u0627\u0646 \u062f\u0627\u060c \u067e\u06ce\u06a9\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u061b \u062c\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u06cc\u0627\u0646\u06d5 \u06af\u06cc\u0627\u0646\u06cc \u0628\u06d5\u062e\u0634\u06cc\u0648\u06d5 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0646 \u0648 \u062a\u06c6\u0632\u0642\u0627\u06b5\u06ce\u06a9\u06cc\u0634 [\u0633\u06c6\u0632\u06cc \u062e\u06c6\u06cc\u0627\u0646] \u0646\u06d5\u06af\u06c6\u0695\u06cc\u0648\u06d5.',
    '\u062a\u0627\u06a9\u0648\u0648 \u062e\u0648\u062f\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0695\u0627\u0633\u062a \u0628\u0648\u0648\u0646 \u0628\u062f\u0627\u062a\u06d5\u0648\u06d5\u061b \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u0648\u0648\u0695\u0648\u0648 \u0628\u0648\u0648\u0646 - \u06af\u06d5\u0631 \u062d\u06d5\u0632 \u0628\u06a9\u0627 - \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u0627\u060c \u06cc\u0627\u0646 \u062a\u06c6\u0628\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0642\u0628\u0648\u0648\u06b5 \u06a9\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u0648\u062f\u0627 \u062f\u06cc\u0646 \u062f\u0648\u0698\u0645\u0646\u06d5\u06a9\u0627\u0646\u06cc - \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648 \u0695\u06a9\u06d5\u06cc \u06a9\u06d5 \u0628\u0648\u0648\u06cc\u0627\u0646 - \u06a9\u0634\u0627\u0646\u062f\u06d5\u0648\u06d5 \u0648 \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0633 \u0646\u06d5\u06a9\u06d5\u0648\u062a. \u0644\u06d5 \u062c\u06d5\u0646\u06af\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0644\u0627\u06cc\u06d5\u060c \u0628\u06d5\u0633\u06cc\u0627\u0646\u06d5. \u062e\u0648\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5.',
    '\u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u06cc\u0634\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u0634\u062a\u06cc\u0648\u0627\u0646\u06cc \u062f\u0648\u0698\u0645\u0646\u0627\u0646 \u0628\u0648\u0648\u0646\u060c \u0644\u06d5 \u0642\u06d5\u06b5\u0627\u06cc \u0647\u06ce\u0646\u0627\u0646\u06d5 \u062e\u0648\u0627\u0631\u06ce \u0648 \u062a\u0631\u0633\u06cc \u062e\u0633\u062a\u06d5 \u062f\u06b5\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06ce\u0646\u062f\u06ce \u0644\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0626\u06ce\u0648\u06d5 \u06a9\u0648\u0698\u0631\u0627\u0646 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0628\u06d5 \u062f\u06cc\u0644 \u06af\u0631\u062a\u0646.',
    '\u0632\u06d5\u0648\u06cc\u06cc\u06d5\u06a9\u0627\u0646 \u0648 \u062e\u0627\u0646\u0648\u0648\u06d5\u06a9\u0627\u0646 \u0648 \u0633\u0627\u0645\u0627\u0646\u06cc\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0634 \u06a9\u06d5 \u067e\u0627\u062a\u0627\u0646 \u067e\u06ce\u062f\u0627 \u0646\u06d5\u0646\u0627\u0628\u0648\u0648\u060c \u062e\u0648\u062f\u0627 \u06af\u0634\u062a\u06cc \u062f\u0627\u0628\u06d5 \u0626\u06ce\u0648\u06d5. \u062e\u0648\u062f\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u062a \u0628\u062f\u0648\u06ce\u0646\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0698\u06cc\u0646 \u0648 \u0632\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062f\u0646\u06cc\u0627\u062a\u0627\u0646 \u062f\u06d5\u0648\u06ce\u060c \u062f\u06d5\u0633\u0627 \u0648\u06d5\u0631\u0646 \u062a\u0627 \u0647\u06d5\u0642\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u062f\u06d5\u0645\u06ce \u0648 \u0628\u06d5 \u0628\u06ce \u06af\u0627\u0632\u0646\u062f\u06d5 \u0648 \u0628\u06d5 \u062e\u06c6\u0634\u06cc \u0695\u0627\u0647\u06cc\u062a\u0627\u0646 \u06a9\u06d5\u0645.',
    '\u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u0645\u0627\u06b5\u06cc \u062f\u0648\u0627\u0695\u06c6\u0698\u062a\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06d5\u060c \u062e\u0648\u062f\u0627 \u0628\u06c6 \u06a9\u0627\u0631\u0686\u0627\u06a9\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u067e\u0627\u062f\u0627\u0634\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc \u062f\u0627\u0646\u0627\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u0647\u0627\u0648\u0633\u06d5\u0631\u0627\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0644\u06d5\u0646\u06af\u06c6 \u062e\u0631\u0627\u067e\u06d5\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u0644\u06ce \u0695\u0648\u0648\u0628\u062f\u0627\u060c \u062f\u0648\u0648\u0633\u06d5\u0631\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062f\u06d5\u062f\u0631\u06ce\u061b \u0626\u06d5\u0645\u06d5\u0634 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u0633\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06a9\u0627\u0645\u06cc\u0634\u062a\u0627\u0646 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0628\u06ce \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u06ce\u060c \u067e\u0627\u062f\u0627\u0634\u06d5\u06a9\u06d5\u06cc \u0628\u06d5\u062f\u0648\u0648\u0686\u06d5\u0646\u062f\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u06d5\u06cc\u0646 \u0648 \u0628\u0698\u06cc\u0648\u06ce\u06a9\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u0627\u0634\u0645\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u0647\u0627\u0648\u0633\u06d5\u0631\u0627\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u06a9 \u0698\u0646\u0627\u0646\u06cc \u062a\u0631 \u0646\u06cc\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u062a\u0627\u0646 \u0626\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646\u060c \u0632\u06c6\u0631 \u0628\u06d5 \u062e\u0627\u0648\u06cc \u0642\u0633\u06d5 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0646\u06d5\u0648\u06d5\u06a9 \u06a9\u06d5\u0633\u06ce \u063a\u06d5\u0631\u06d5\u0632\u06ce\u06a9\u06cc \u0644\u06d5 \u062f\u06b5\u062f\u0627 \u0628\u06ce\u060c \u0642\u0631\u0645\u06cc \u062e\u06c6\u0634 \u06a9\u0627\u061b \u0647\u06d5\u0631 \u0686\u06c6\u0646 \u067e\u06d5\u0633\u0646\u062f\u06d5 \u0628\u067e\u06d5\u06cc\u06a4\u0646.',
    '\u0644\u06d5 \u0645\u0627\u06b5 \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0631\u0645\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u0648\u06d5\u06a9 \u062f\u06d5\u0648\u0631\u0627\u0646\u06cc \u0632\u06d5\u0645\u0627\u0646\u06cc \u0646\u06d5\u0632\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u060c \u062e\u06d5\u0645\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0645\u06d5\u062f\u06d5\u0646. \u0646\u0648\u06ce\u0698\u0628\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u062f\u06d5\u0646\u061b \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062f\u06d5\u0631\u0645\u06d5\u0686\u0646. \u0626\u06ce\u0648\u06d5 \u06a9\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u0626\u06d5\u0645 \u0645\u0627\u06b5\u06d5\u0646\u060c \u062e\u0648\u0627 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5 \u0686\u06d5\u067e\u06d5\u06b5\u06cc\u062a\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0644\u0627\u0628\u0627 \u0648 \u067e\u0627\u06a9 \u0648 \u062e\u0627\u0648\u06ce\u0646\u062a\u0627\u0646 \u0695\u0627\u06af\u0631\u06ce.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u0648\u0627 \u0648 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0644\u06d5 \u0645\u0627\u06b5\u062a\u0627\u0646\u062f\u0627 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0641\u06ce\u0631\u06cc \u0628\u0646 \u0648 \u0628\u06cc\u06b5\u06ce\u0646\u06d5\u0648\u06d5. \u062e\u0648\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u0644\u06ce \u062f\u06cc\u0627\u0631\u06d5 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u067e\u06cc\u0627\u06af \u0648 \u0698\u0646\u06cc \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646 \u0648 \u0645\u06ce\u0631 \u0648 \u067e\u06cc\u0631\u06d5\u06a9\u06cc \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u060c \u067e\u06cc\u0627\u06af \u0648 \u0698\u0646\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u0628\u06d5\u0631 \u0648 \u067e\u06cc\u0627\u0648 \u0648 \u0698\u0646\u06cc \u0642\u0633\u06d5\u0695\u0627\u0633\u062a \u0648 \u067e\u06cc\u0627\u0648 \u0648 \u0698\u0646\u06ce \u0628\u06ce \u0641\u06cc\u0632 \u0648 \u062e\u06c6\u0628\u06d5\u0632\u0644 \u0646\u06d5\u0632\u0627\u0646\u060c \u067e\u06cc\u0627\u0648 \u0648 \u0698\u0646\u06cc \u0633\u06d5\u062f\u06d5\u0642\u06d5\u062f\u06d5\u0631 \u0648 \u067e\u06cc\u0627\u0648 \u0648 \u0698\u0646\u06cc \u0695\u06c6\u0686\u06d5\u0648\u0627\u0646 \u0648 \u067e\u06cc\u0627\u0648 \u0648 \u0698\u0646\u06cc \u0646\u0627\u0645\u0648\u0648\u0633 \u067e\u0627\u0631\u06ce\u0632 \u0648 \u0698\u0646 \u0648 \u067e\u06cc\u0627\u0648\u06ce \u06a9\u06d5 \u0632\u06c6\u0631 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0644\u06ce\u062e\u06c6\u0634\u0628\u0648\u0648\u0646 \u0648 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0646\u062f\u0631\u0627\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9 \u062f\u0627\u060c \u0647\u06cc\u0686 \u067e\u06cc\u0627\u0648\u06ce\u06a9 \u0648 \u0647\u06cc\u0686 \u0698\u0646\u06ce\u06a9\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u067e\u06d5\u0633\u0646\u062f\u06cc \u0646\u06d5\u06a9\u06d5\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062f\u06d5\u0631\u0686\u06ce\u060c \u0695\u06ce\u06af\u06d5\u06cc \u0632\u06c6\u0631 \u0647\u06d5\u06b5\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u0648\u06cc \u062e\u0648\u062f\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f \u0648 \u062a\u06c6\u0634 \u0686\u0627\u06a9\u06d5\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u062a\u06c6 \u067e\u06ce\u062a \u0626\u06d5\u0648\u062a: \u062f\u06d5\u0633 \u0644\u06d5\u0698\u0646\u06ce \u062e\u06c6 \u0628\u06d5\u0631\u0645\u06d5\u062f\u06d5 \u0648 \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u06cc. \u0634\u062a\u06ce\u06a9\u06cc\u0634\u062a \u0644\u06d5 \u062f\u06b5\u06cc \u062e\u06c6\u062f\u0627 \u062f\u06d5\u0634\u0627\u0631\u062f\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u0631 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u062f\u06d5\u06a9\u0631\u062f. \u0647\u06d5\u0631 \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645\u06cc\u0634 \u062f\u06d5\u062a\u0631\u0633\u0627\u06cc\u061b \u06a9\u06d5\u0686\u06cc \u062e\u0648\u062f\u0627 \u0634\u06cc\u0627\u0648\u062a\u0631 \u0628\u0648\u0648 \u0644\u06ce \u0628\u062a\u0631\u0633\u06cc. \u062c\u0627 \u06a9\u0627\u062a\u06ce \u0632\u06d5\u06cc\u062f \u0644\u06d5 \u0647\u0627\u0648\u062c\u0648\u0648\u062a\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u06af\u06d5\u06cc\u06cc\u0634\u062a\u060c \u0626\u06d5\u0648\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0647\u0627\u0648\u062c\u0648\u0648\u062a\u06cc \u062a\u06c6\u061b \u0647\u06d5\u062a\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627\u0633\u062a\u0646\u06cc \u0698\u0646\u06cc \u0632\u0695\u06a9\u0648\u0695\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 - \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5 \u062a\u06d5\u06b5\u0627\u0642\u06cc\u0627\u0646 \u062f\u0627\u0646 - \u0644\u06c6\u0645\u06d5 \u0646\u06d5\u06a9\u0631\u06ce\u0646\u060c \u0647\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06ce \u062e\u0648\u062f\u0627 \u0628\u06cc\u062f\u0627\u060c \u062f\u06d5\u0628\u06ce \u067e\u06ce\u06a9 \u0628\u06ce.',
    '\u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0642\u06d5\u06cc\u062f\u06cc \u0646\u06cc\u06cc\u06d5 \u06a9\u0627\u0631\u06ce\u06a9 \u0628\u06a9\u0627 \u06a9\u06d5 \u062e\u0648\u0627 \u0628\u06c6\u06cc \u0628\u0695\u06cc\u0627\u0631\u06cc \u062f\u0627\u0648\u06d5. \u0695\u06d5\u0641\u062a\u0627\u0631\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0695\u0627\u0628\u0631\u062f\u0648\u0634\u062f\u0627 \u0647\u06d5\u0631\u0648\u0627 \u0628\u0648\u0648\u06d5. \u062e\u0648\u0627 \u0628\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u062f\u0627 - \u0628\u06ce \u0632\u06cc\u0627\u062f \u0648 \u06a9\u06d5\u0645 - \u062f\u06d5\u0628\u06ce \u0628\u06a9\u0631\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u0646 \u0648 \u0644\u06d5\u0648 \u062f\u06d5\u062a\u0631\u0633\u0646\u061b \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u0646\u0627\u062a\u0631\u0633\u0646. \u062e\u0648\u062f\u0627 \u062d\u06d5\u0633\u06ce\u0628\u06af\u06d5\u0631 \u0628\u06ce\u060c \u0628\u06d5\u0633\u06d5.',
    '\u0645\u062d\u06d5\u0645\u0645\u06d5\u062f \u0628\u0627\u0628\u06cc \u0647\u06cc\u0686 \u067e\u06cc\u0627\u0648\u06ce\u06a9 \u0644\u06d5 \u067e\u06cc\u0627\u0648\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u06cc\u06d5. \u0628\u06d5\u06b5\u0627\u0645 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06cc \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0626\u0627\u062e\u0631\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0641\u0631\u06d5 \u06cc\u0627\u062f\u06cc \u062e\u0648\u0627 \u0628\u06a9\u06d5\u0646 [\u0648 \u0644\u06d5 \u06cc\u0627\u062f\u06cc \u0626\u06d5\u0648 \u0695\u0627\u0645\u06d5\u0648\u06d5\u0633\u062a\u0646].',
    '\u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0626\u06ce\u0648\u0627\u0631\u0627\u0646 \u0644\u06d5 \u067e\u0627\u06a9\u06cc \u0648 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u062e\u0648\u062f\u0627 \u062a\u0627\u0631\u06cc\u0641 \u0628\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u062f\u0631\u0648\u0648\u062f \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0646\u06ce\u0631\u06ce\u061b \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627\u0634 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc\u061b \u0647\u06d5\u062a\u0627\u0648\u06d5\u06a9\u0648\u0648 \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u06cc \u062f\u06d5\u0631\u062a\u0627\u0646\u0628\u06ce\u0646\u06ce \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc\u062a\u0627\u0646 \u0628\u06d5\u0631\u06ce. \u062e\u0648\u062f\u0627 \u0628\u06c6\u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5\u062f\u06ce\u0646\u06d5\u0628\u06d5\u0631 \u0628\u0627\u0631\u06d5\u06af\u0627\u06cc \u0626\u06d5\u0648\u060c \u0648\u06d5 \u0633\u06d5\u0644\u0627\u0645 \u0628\u06d5\u062e\u06ce\u0631\u06cc\u0627\u0646 \u062f\u06ce\u0646\u06ce \u0648 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u067e\u0695\u0628\u0627\u06cc\u06d5\u062e\u06cc \u0628\u06c6\u062f\u0627\u0646\u0627\u0648\u0646.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0626\u06ce\u0645\u06d5 \u0628\u06c6\u06cc\u06d5 \u0646\u0627\u0631\u062f\u0648\u0645\u0627\u0646\u06cc\u060c \u0634\u0627\u06cc\u06d5\u062a \u0648 \u0645\u0632\u06af\u06ce\u0646\u06cc \u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0628\u06cc.',
    '\u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u0626\u06d5\u0648\u060c \u062e\u06d5\u06b5\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627\u067e\u06d5\u0631\u0633\u062a\u06cc \u06af\u0627\u0632\u06cc \u0628\u06a9\u06d5\u06cc \u0648 \u0686\u0631\u0627\u06cc\u06d5\u06a9\u06cc \u062a\u06cc\u0634\u06a9\u062f\u06d5\u0631 \u0628\u06cc.',
    '\u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0645\u0698\u062f\u06d5\u0634 \u0628\u062f\u06d5\u060c \u06a9\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u0627\u0648\u06d5 \u0628\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u0632\u06c6\u0631 \u06af\u06d5\u0648\u0631\u06d5\u0648\u06d5 \u0626\u06d5\u062e\u0648\u06ce\u0646\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0648 \u062f\u0648\u0695\u0627\u0632\u06d5\u06a9\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u0628\u06cc\u06a9\u06d5\u06cc\u060c \u0645\u06d5\u06cc\u06a9\u06d5\u061b \u06af\u0648\u06ce \u0628\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0645\u06d5\u062f\u06d5. \u0647\u06d5\u0631 \u062e\u06c6\u062a \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u067e\u06ce\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0628\u0631\u06cc\u06a9\u0627\u0631 \u0628\u06ce \u0628\u06d5\u0633\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u0698\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u062a\u0627\u0646 \u0645\u0627\u0631\u06d5\u06a9\u0631\u062f \u0648 \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u0628\u0646\u0648\u0648\u0646 \u062a\u06d5\u06b5\u0627\u0642\u062a\u0627\u0646 \u062f\u0627\u0646\u060c \u0646\u0627\u0628\u06ce \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648 \u0642\u06d5\u062a\u06cc\u0633\u06cc\u0627\u0646 \u06a9\u06d5\u0646\u061b \u0628\u06d5\u0634\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u062f\u06d5\u0646\u06ce \u0648 \u0628\u06d5 \u0695\u0648\u0648\u062e\u06c6\u0634\u06cc \u0626\u0627\u0632\u0627\u062f\u06cc\u0627\u0646 \u06a9\u06d5\u0646.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5\u062a\u0645\u0627\u0646 \u0628\u06c6 \u0695\u06d5\u0648\u0627 \u062f\u06cc\u0648\u06cc\u060c \u06a9\u06d5 \u0645\u0627\u0631\u06d5\u06cc\u06cc\u062a \u067e\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u062a\u0627\u06b5\u0627\u0646\u06ce \u062f\u06d5\u0633 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646 \u0648 \u062e\u0648\u062f\u0627 \u06a9\u0631\u062f\u0648\u0648\u0646\u06cc\u06cc\u06d5 \u067e\u0634\u06a9\u06cc \u062a\u06c6. \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u062f\u06c6\u062a \u0645\u0627\u0645\u06d5\u06a9\u0627\u0646\u062a \u0648 \u062f\u06c6\u062a \u067e\u0644\u06a9\u0627\u0646\u062a \u0648 \u062f\u06c6\u062a \u062e\u0627\u06b5\u0627\u0646\u062a \u0648 \u06a9\u06cc\u0698\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0634\u06a9\u06cc \u062e\u0627\u06b5\u062a\u060c \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u062a\u06c6 \u0626\u0627\u0648\u0627\u0631\u06d5 \u0628\u0648\u0648\u0646\u060c \u0628\u06d5 \u062a\u06c6 \u062f\u06d5\u0634\u06ce\u0646. \u0647\u06d5\u0631 \u0698\u0646\u06ce\u06a9\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06cc\u0634 - \u0628\u06d5\u0648 \u0645\u06d5\u0631\u062c\u06d5 \u062e\u06c6\u06cc \u0628\u0628\u06d5\u062e\u0634\u06ce \u0628\u06d5\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634 \u06af\u06d5\u0631\u06d5\u06a9\u06cc \u0628\u06ce \u0645\u0627\u0631\u06d5\u06cc \u0628\u06a9\u0627 - \u0626\u06d5\u0645\u06d5\u0634 \u0647\u06d5\u0631 \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u062a\u06c6\u06cc\u06d5\u061b \u0646\u06d5\u06a9 \u0628\u06c6 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06cc \u062a\u0631. \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u0632\u0627\u0646\u06cc\u0648\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0648\u0627\u0646 \u0695\u06d5\u0648\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0628\u0646\u06d5 \u06a9\u06d5\u0646\u06cc\u0632\u06cc\u0627\u0646\u060c \u0686\u0644\u06c6\u0646 \u0628\u0695\u06cc\u0627\u0631\u06ce\u06a9\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5\u060c \u062a\u0627 \u0647\u06cc\u0686 \u06af\u06cc\u0631\u0648\u06af\u0631\u0641\u062a\u06ce\u06a9 \u0628\u06c6 \u062a\u06c6 \u0695\u0648\u0648 \u0646\u06d5\u062f\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062a\u06c6 \u0633\u06d5\u0631\u062c\u06ce\u06cc \u0647\u06d5\u0631\u06a9\u0627\u0645\u06cc\u0627\u0646 \u0648\u06d5\u062f\u0648\u0627 \u0628\u062e\u06d5\u06cc \u0648 \u06a9\u0627\u0645\u06cc\u0627\u0646 \u0628\u0627\u0646\u06af \u0628\u06a9\u06d5\u06cc\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u062a\u060c \u06a9\u06d5\u06cc\u0641\u06cc \u062e\u06c6\u062a\u06d5. \u0698\u0646\u06cc \u062a\u06d5\u062e\u0633\u06cc\u0631 \u06a9\u0631\u0627\u0648\u06cc\u0634\u062a \u0626\u0627\u0634\u062a \u06a9\u06d5\u06cc\u06d5\u0648\u06d5\u060c \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u062a \u0646\u0627\u06cc\u06d5\u062a\u06d5 \u0633\u06d5\u0631. \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u062f\u06b5\u062e\u06c6\u0634 \u0628\u0646 \u0648 \u062e\u06d5\u0645 \u0646\u06d5\u062e\u06c6\u0646 \u0648 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0695\u0627\u0632\u06cc \u0628\u0646 \u0628\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u062a \u062f\u0627\u0648\u0646. \u062e\u0648\u062f\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0626\u06ce\u0648\u06d5 \u0686\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u062f\u06b5\u062f\u0627\u062f\u06ce. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627 \u0648 \u0644\u06d5\u0633\u06d5\u0631\u062e\u06c6\u06cc\u06d5.',
    '\u062f\u0648\u0627\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc - \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u067e\u06ce\u06cc \u0695\u06d5\u0648\u0627 \u062f\u06cc\u0648\u06cc - \u0698\u0646\u0627\u0646\u06cc \u062a\u0631\u062a \u067e\u06ce \u0646\u0627\u0634\u06ce\u0646\u061b \u0628\u06c6\u0634\u062a \u0646\u06cc\u06cc\u06d5 \u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5\u062a \u0628\u06d5 \u0698\u0646\u06cc \u062a\u0631 \u0628\u06af\u06c6\u0695\u06cc\u06d5\u0648\u06d5\u061b \u0628\u0627 \u062f\u06d5\u0644\u0627\u06cc \u0648 \u062c\u0648\u0627\u0646\u06cc\u06cc\u06d5\u06a9\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06b5\u06cc \u062a\u06c6\u06cc \u0628\u06c6 \u062e\u06c6\u06cc \u06a9\u06ce\u0634\u0627\u0628\u06ce\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062a\u0627\u06b5\u0627\u0646 \u0628\u06d5\u0631\u062a\u06c6 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646. \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5\u0645\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0645\u06d5\u0686\u0646\u06d5 \u0698\u0648\u0648\u0631\u06ce\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06c6 \u062c\u06d5\u0645\u06ce\u06a9\u06cc \u062e\u0648\u0627\u0631\u062f\u0646 \u0628\u0627\u0646 \u06a9\u0631\u0627\u0628\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0648\u0648\u0634\u0646\u060c \u0646\u0627\u0628\u06ce \u0648\u06d5\u062e\u062a\u06ce\u06a9\u06cc \u0648\u0627\u0628\u0686\u0646 \u06a9\u06d5 \u0626\u06d5\u0648 \u062c\u06d5\u0645\u06d5 \u062e\u06c6\u0631\u0627\u06a9\u06d5 \u0626\u0627\u0645\u0627\u062f\u06d5 \u0646\u06d5\u0628\u0648\u0648\u06d5. \u0647\u06d5\u0631 \u06a9\u06d5 \u0698\u06d5\u0645\u06d5 \u0646\u0627\u0646\u06d5\u06a9\u06d5 \u0648 \u062e\u0648\u0627\u0631\u062f\u060c \u0628\u06b5\u0627\u0648\u06d5\u06a9\u06d5\u0646 \u0648 \u062e\u06c6 \u0628\u06d5 \u0642\u0633\u06d5\u0648\u06d5 \u062e\u06d5\u0631\u06cc\u06a9 \u0645\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u062a\u0627\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u062f\u0627 \u0648 \u0644\u06d5\u0628\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0634\u06d5\u0631\u0645 \u062f\u06d5\u06a9\u0627 \u062f\u06d5\u0646\u06af\u0648\u0628\u062f\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0642\u0633\u06d5\u06cc \u0695\u0627\u0633\u062a \u06af\u0648\u062a\u0646 \u0634\u06d5\u0631\u0645 \u0646\u0627\u06a9\u0627. \u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u062f\u0627\u0648\u0627\u06cc \u0634\u062a\u06ce\u06a9 \u0644\u06d5\u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0646\u0627\u0648\u0645\u0627\u06b5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u067e\u0634\u062a \u067e\u06d5\u0631\u062f\u06d5\u0648\u06d5 \u0628\u062e\u0648\u0627\u0632\u0646\u061b \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0644\u06d5 \u062f\u06b5\u067e\u0627\u06a9\u06cc \u0626\u06ce\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0646\u0632\u06cc\u06a9\u062a\u0631\u06d5. \u0646\u0627\u0628\u06ce \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u0632\u0627\u0631 \u0628\u0628\u06cc\u0646\u06ce\u061b \u0646\u0627\u0634\u0628\u06ce \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648 - \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5 - \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0627\u0631\u06d5 \u0628\u06a9\u06d5\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u0634\u062a\u06ce \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u0628\u06a9\u06d5\u0646\u060c \u06cc\u0627\u0646 \u0648\u06d5\u06cc\u0634\u06ce\u0631\u0646\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0698\u0646\u0627\u0646 \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0628\u0627\u0648\u06a9 \u0648 \u06a9\u0648\u0695\u0627\u0646\u06cc\u0627\u0646 \u0648 \u0628\u0631\u0627 \u0648 \u0628\u0631\u0627\u0632\u0627\u06cc\u06d5\u06a9\u0627\u0646 \u0648 \u062e\u0648\u0634\u06a9\u06d5\u0632\u0627\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0644\u06d5\u062f\u06cc\u062a\u0646\u06cc \u0626\u06d5\u0648\u0698\u0646\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u06cc\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646\u0646 \u0648 \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u06a9\u06d5\u0646\u06cc\u0632\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0695\u0648\u0648\u0646\u06d5\u067e\u06c6\u0634\u0646\u060c \u0642\u06d5\u06cc\u062f\u06cc \u0646\u06cc\u06cc\u06d5. \u0626\u06d5\u0634\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646\u060c \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0648 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627\u060c \u067e\u06d5\u0633\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u062f\u06d5\u062f\u06d5\u0646\u061b \u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0633\u0627 \u0626\u06ce\u0648\u06d5\u0634 \u067e\u06d5\u0633\u0646\u06cc \u0628\u062f\u06d5\u0646 \u0648 \u0628\u06d5\u062f\u06b5 \u0648 \u06af\u06cc\u0627\u0646 \u0633\u06b5\u0627\u0648\u06cc \u062e\u0648\u062f\u0627\u06cc \u0644\u06ce \u0628\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u062f\u06d5\u0646\u060c \u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u060c \u0644\u06d5 \u062f\u0646\u06cc\u0627\u0634 \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce\u060c \u0626\u0627\u0632\u0627\u0631\u06cc \u067e\u0695\u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u0646.',
    '\u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u06cc \u067e\u06cc\u0627\u0648 \u0648 \u0698\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 - \u0628\u06d5\u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0628\u06ce - \u062f\u06d5\u0695\u06d5\u0646\u062c\u06ce\u0646\u0646\u060c \u0628\u06ce \u0633\u06c6 \u062a\u0648\u0648\u0634\u06cc \u0628\u0648\u062e\u062a\u0627\u0646 \u0648 \u062a\u0627\u0648\u0627\u0646\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u0648\u0648\u06af\u0646.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0698\u0646\u06d5\u06a9\u0627\u0646\u062a \u0648 \u06a9\u0686\u06d5\u06a9\u0627\u0646\u062a \u0648 \u062e\u06ce\u0632\u0627\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u062d\u0627\u06b5\u06cc \u0628\u06a9\u06d5\u060c \u0628\u0627 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0686\u0627\u0631\u0634\u06ce\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u0627\u067e\u06c6\u0634\u0646\u061b \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0628\u0646\u0627\u0633\u0631\u06ce\u0646 \u0648 \u0626\u0627\u0632\u0627\u0631 \u0646\u06d5\u062f\u0631\u06ce\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062e\u06d5\u062a\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u062f\u0648\u0695\u0627\u0632\u06d5\u06a9\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u063a\u06d5\u0631\u06d5\u0632\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0634\u0627\u0631\u06cc \u0645\u06d5\u062f\u06cc\u0646\u06d5\u062f\u0627 [\u0644\u06d5 \u062f\u0698\u06cc \u062f\u06cc\u0646] \u062f\u06d5\u0646\u06af\u06c6 \u0628\u06b5\u0627\u0648 \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u060c \u062f\u06d5\u0633 \u0647\u06d5\u06b5\u0646\u06d5\u06af\u0631\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u062e\u06d5\u06cc\u0646\u06d5 \u0628\u06d5\u0631\u062f\u06d5\u0633\u062a\u06cc \u062a\u06c6 \u0648 \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u062f\u0627 - \u0628\u06ce\u062c\u06af\u06d5 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u0647\u0646\u062f\u06a9 - \u0644\u06d5\u0648 \u0634\u0627\u0631\u06d5\u062f\u0627 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0628\u0628\u0646 \u0628\u06d5 \u0647\u0627\u0648\u0633\u0627\u062a.',
    '\u0646\u06d5\u062d\u0644\u06d5\u062a \u0644\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u0631\u0627\u0648\u06d5\u0648 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce\u06a9\u0627 \u0648\u06d5\u06af\u06cc\u0631 \u06a9\u06d5\u0648\u0646\u060c \u062f\u06d5\u06af\u06cc\u0631\u06ce\u0646 \u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u06cc\u06d5\u06a9\u062c\u0627\u0631\u06cc \u062f\u06d5\u06a9\u0648\u0698\u0631\u06ce\u0646.',
    '\u0634\u06ce\u0648\u06d5\u06cc \u06a9\u0627\u0631\u06cc \u062e\u0648\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0695\u0627\u0628\u0631\u062f\u0648\u0648\u0634\u0627 \u0647\u06d5\u0631\u0648\u0627 \u0628\u0648\u0648\u06d5 \u0648 \u0634\u06ce\u0648\u06d5\u06cc \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06cc \u062e\u0648\u062f\u0627 \u06af\u06c6\u0695\u0627\u0646\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0646\u0627\u06cc\u06d5.',
    '\u0645\u06d5\u0631\u062f\u0645 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u062a \u0644\u06ce \u062f\u06d5\u067e\u0631\u0633\u0646\u061b \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce. \u062a\u06c6 \u0686\u0648\u0648\u0632\u0627\u0646\u06cc\u061f \u0648\u06ce\u062f\u06d5\u0686\u06ce \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5\u0628\u06ce.',
    '\u062e\u0648\u062f\u0627 \u0646\u06d5\u062d\u0644\u06d5\u062a \u0644\u06d5\u0648 \u062f\u06cc\u0646 \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u06d5 \u062f\u06d5\u06a9\u0627 \u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u0644\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u062f\u0627 \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u0646.',
    '\u062a\u0627\u0647\u06d5\u062a\u0627 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u062f\u06d5\u0628\u0646 \u0648 \u06a9\u06d5\u0633\u06cc\u0634 \u0646\u0627\u0628\u06ce \u0628\u06d5\u062f\u06c6\u0633\u062a \u0648 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u062f\u06d5\u0631\u06cc\u0627\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u062f\u06d5\u0645 \u0648\u0686\u0627\u0648\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u0627\u06af\u0631\u062f\u0627 \u0626\u06d5\u0645 \u062f\u06cc\u0648 \u0626\u06d5\u0648\u062f\u06cc\u0648\u06cc \u067e\u06ce\u062f\u06d5\u06a9\u0631\u06ce\u060c \u0626\u06ce\u0698\u0646: \u062e\u06c6\u0632\u06cc \u0628\u06d5\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u0628\u0627 \u0648 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u0627\u06cc\u0646.',
    '\u062f\u06d5\u0634\u06b5\u06ce\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0626\u06ce\u0645\u06d5 \u0648\u06d5\u062f\u0648\u0648\u06cc \u0626\u0627\u063a\u0627\u06a9\u0627\u0646 \u0648 \u06af\u06d5\u0648\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646 \u06a9\u06d5\u0648\u062a\u06cc\u0646\u061b \u0626\u06d5\u0648\u0627\u0646 \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u06af\u0648\u0645\u0695\u0627 \u06a9\u0631\u062f.',
    '\u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646! \u062f\u0648\u0648\u0633\u06d5\u0631\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0628\u062f\u06d5 \u0648 \u0641\u0631\u06d5 \u0628\u06d5\u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc\u0627\u0646 \u0628\u06a9\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06d5\u0628\u0646 \u06a9\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0645\u0648\u0648\u0633\u0627\u06cc\u0627\u0646 \u062f\u0627\u061b \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648 \u0628\u0648\u062e\u062a\u0627\u0646\u06d5\u06cc \u0628\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u060c \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc \u06a9\u0631\u062f\u061b \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u0634 \u067e\u06cc\u0627\u0648\u06ce \u0644\u06d5\u0695\u0648\u0648 \u0628\u0648\u0648.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646 \u0648 \u0642\u0633\u06d5\u06cc \u0695\u0627\u0633\u062a \u0648 \u067e\u062a\u06d5\u0648 \u0628\u06a9\u06d5\u0646\u061b',
    '\u062a\u0627 \u062e\u0648\u062f\u0627 \u06a9\u0627\u0631\u0648\u0648 \u0695\u0627\u0633\u062a \u0628\u06ce\u0646\u06ce \u0648 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u062a\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u0627 \u0628\u06ce\u060c \u0628\u06d5 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5 \u06af\u06d5\u06cc\u0634\u062a\u0648\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0645 \u0626\u0627\u0645\u0627\u0646\u06d5\u062a\u06cc\u06cc\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06ce\u0648\u0627\u0646\u06d5 \u0695\u0627\u0646\u0627\u060c \u0647\u06cc\u0686\u06cc\u0627\u0646 \u062f\u06d5\u062e\u06c6\u06cc\u0627\u0646 \u0695\u0627\u0646\u06d5\u062f\u06cc \u0628\u0686\u0646\u06d5 \u0698\u06ce\u0631\u06cc \u0648 \u0644\u06ce\u06cc \u062f\u06d5\u062a\u0631\u0633\u0627\u0646. \u067e\u0627\u0634\u0627\u0646 \u0645\u0631\u06c6 \u0648\u06d5\u0633\u062a\u06c6\u06cc \u062e\u06c6\u06cc \u06af\u0631\u062a\u060c \u06a9\u06d5 \u0646\u06d5\u06cc\u0632\u0627\u0646\u06cc \u0648 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u062e\u06c6\u06cc \u063a\u06d5\u062f\u0631\u06cc \u06a9\u0631\u062f.',
    '\u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 \u067e\u06cc\u0627\u06af \u0648 \u0698\u0646\u06d5 \u062f\u0648\u0695\u0627\u0632\u06d5\u06a9\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0634\u06d5\u0631\u06cc\u06a9\u0627\u0646 \u0628\u06c6 \u062e\u0648\u0627 \u062f\u06d5\u0646\u0627\u0633\u0646 - \u0646\u06ce\u0631\u0648\u0645\u06ce\u0648\u06cc\u0627\u0646 - \u0626\u0627\u0632\u0627\u0631 \u0628\u062f\u0627 \u0648 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06cc\u0634 - \u067e\u06cc\u0627\u0648 \u0628\u0646 \u06cc\u0627\u0646 \u0698\u0646 - \u0642\u0628\u0648\u0648\u06b5 \u0628\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5\u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u067e\u06d5\u0633\u0646 \u0648 \u0633\u0648\u067e\u0627\u0633 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06ce \u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u06d5 \u0648 \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627\u0634 \u0633\u067e\u0627\u0633 \u0648 \u067e\u06d5\u0633\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u06cc \u062f\u06d5\u0648\u06d5\u0634\u06ce\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u0686\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0632\u06d5\u0648\u06cc \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0632\u06d5\u0648\u06cc \u062f\u06d5\u0631\u06a9\u06d5\u0648\u06ce \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646 \u062f\u0627\u06a9\u06d5\u0648\u06ce \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06ce\u06cc\u06d5\u0648\u06d5 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u06ce\u060c \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5 \u0648 \u062e\u06d5\u062a\u0627\u0628\u06d5\u062e\u0634\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0646\u0627\u0646\u0627\u0633\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u06c6 \u0645\u06d5 \u0646\u0627\u06cc\u06d5. \u0628\u06ce\u0698\u06d5: \u0633\u0648\u06ce\u0646\u062f\u0645 \u0628\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u06a9\u06d5\u0648\u0627 \u0645\u0646\u06cc \u0695\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0632\u0627\u0646\u0627\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0646\u0647\u06ce\u0646\u06cc\u06d5\u06a9\u06d5\u060c \u0628\u06ce \u0634\u06a9 \u0633\u06d5\u06b5\u0627 \u0648 \u0644\u06ce \u0695\u0627\u062f\u06d5\u0628\u06ce. \u0647\u06d5\u0631\u0634\u062a\u06ce\u06a9\u06cc \u0628\u06d5 \u0642\u0648\u0631\u0633\u0627\u06cc\u06cc \u0645\u06ce\u0631\u0648\u0648\u0644\u06d5 \u0648\u0631\u062f\u06cc\u0644\u06d5\u06cc\u06d5\u06a9 \u0628\u06ce\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0648\u0686\u0648\u06a9\u062a\u0631 \u06cc\u0627\u0646 \u0645\u06d5\u0632\u0646\u062a\u0631 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u062a\u0627\u0642\u06cc \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u06af\u0648\u0645 \u0646\u0627\u0628\u06ce\u061b \u06af\u0634\u062a\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u0695\u0648\u0648\u0646 \u06a9\u06d5\u0631\u06d5\u0648\u06d5 \u0647\u06d5\u06b5\u06af\u06cc\u0631\u0627\u0648\u06d5.',
    '\u062a\u0627 \u0647\u06d5\u0631\u06a9\u06ce \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646\u060c \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u0628\u06a9\u0627. \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06ce \u062e\u06c6\u0634\u0628\u0648\u0648\u0646 \u0648 \u0695\u0633\u0642 \u0648 \u0695\u06c6\u0632\u06cc \u067e\u0627\u06a9\u0698 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062a\u06ce\u062f\u06d5\u06a9\u06c6\u0634\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0646\u06d5\u06a9\u0631\u06ce \u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u0628\u0648\u06ce\u0631\u0646\u060c \u0628\u06d5\u0634\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062f\u0627\u0646\u06ce \u0628\u06d5\u0698\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0632\u0627\u0646\u06cc\u0646 \u0628\u06d5\u0634 \u062f\u0631\u0627\u0648\u0646\u060c \u0633\u0648\u0648\u0631 \u062f\u06d5\u0632\u0627\u0646\u0646 \u0626\u06d5\u0648\u06cc \u0628\u06c6 \u062a\u06c6 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u067e\u06d5\u0633\u0646 \u062f\u0627\u0631\u0648 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 \u067e\u06cc\u0627\u0648\u06ce\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0633 \u0646\u06cc\u0634\u0627\u0646 \u06a9\u06d5\u06cc\u0646\u060c \u067e\u06ce\u062a\u0627\u0646 \u0626\u06ce\u0698\u06ce: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0633\u06a9 \u0648 \u067e\u0631\u0648\u0648\u0633\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0628\u06d5\u0631\u0628\u0627\u062f \u0628\u0628\u06ce\u060c \u0633\u06d5\u0631\u0644\u06d5\u0646\u0648\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5\u061f',
    '\u0626\u0627\u062e\u06c6 \u062f\u0631\u06c6\u06cc \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u0627\u0648\u06d5 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u0648\u0648\u06d5\u060c \u06cc\u0627\u0646 \u0634\u06ce\u062a \u0628\u0648\u0648\u06d5\u061f \u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u0646\u06cc\u06cc\u06d5\u060c \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u062f\u0627 \u062f\u06d5\u062a\u0644\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u0632\u06c6\u0631 \u0644\u06d5\u0695\u06ce \u062f\u0648\u0648\u0631\u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u067e\u06ce\u0634\u06d5\u0648\u06d5 \u0648 \u067e\u0627\u0634\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0646\u06d5\u0695\u0648\u0627\u0646\u06cc\u060c \u0644\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0628\u0645\u0627\u0646\u06d5\u0648\u06ce \u0648\u0627 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0632\u06d5\u0645\u06cc\u0646 \u0642\u0648\u0648\u062a\u06cc\u0627\u0646\u062f\u0627\u060c \u06cc\u0627\u0646 \u0686\u06d5\u0646\u062f \u06a9\u0648\u062a\u06ce\u06a9\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5\u062e\u0648\u0627\u0631\u06ce. \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 \u0647\u06d5\u06cc\u06d5 \u0628\u06c6 \u0647\u06d5\u0631 \u0639\u06d5\u0628\u062f\u06ce \u062f\u06d5\u062e\u0648\u0627\u0632\u06ce \u0628\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0632\u06ce\u062f\u06d5 \u0628\u06d5\u0634\u06ce\u06a9\u0645\u0627\u0646 \u062f\u0627\u0628\u0648\u0648 \u0628\u06d5 \u062f\u0627\u0648\u0648\u062f: \u0626\u06d5\u06cc \u06a9\u06ce\u0648\u06d5\u06a9\u0627\u0646 \u0648 \u0628\u0627\u06b5\u062f\u0627\u0631\u0627\u0646! \u0626\u06ce\u0648\u06d5\u0634 \u0648\u06d5\u0644 \u0626\u06d5\u0648 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u062e\u0648\u062f\u0627 \u0628\u062f\u06d5\u0646. \u0626\u0627\u0633\u0646\u06cc\u0634\u0645\u0627\u0646 \u0628\u06c6 \u0646\u06d5\u0631\u0645 \u06a9\u0631\u062f.',
    '\u06a9\u06d5 \u062a\u06c6 \u0628\u0627\u06b5\u0627\u067e\u06c6\u0634 \u0644\u06d5 \u0626\u0627\u0633\u0646 \u062f\u0631\u0648\u0633 \u0628\u06a9\u06d5\u061b \u06af\u06d5\u0648\u0686\u0646 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u062f\u0627 \u0628\u0646\u060c \u0628\u0627 \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0647\u06ce\u0698\u0627 \u0628\u0646\u061b \u062e\u06c6\u0645 \u06a9\u0627\u0631\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u0645.',
    '\u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0634 \u0628\u0627\u0645\u0627\u0646 \u0628\u06c6 \u06a9\u06d5\u0648\u06cc \u06a9\u0631\u062f\u0628\u0648\u0648\u061b \u062a\u06d5\u0646\u06cc\u0627 \u0644\u06d5 \u062f\u0627\u0646\u06cc \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0633\u06cc \u0695\u06c6\u0698\u06d5\u0695\u06ce \u0648 \u0633\u06d5\u0631\u0644\u06d5\u0646\u06af\u06c6\u0631\u06d5\u0634 \u0633\u06cc \u0695\u06c6\u0698\u06d5\u0695\u06ce \u0695\u06ce\u06af\u06d5\u06cc \u062f\u06d5\u0628\u0695\u06cc. \u0628\u06c6 \u0626\u06d5\u0648\u060c \u06a9\u0627\u0646\u06cc \u0645\u0633\u06cc\u0634\u0645\u0627\u0646 \u062a\u0627\u0648\u0627\u0646\u062f\u0628\u06c6\u0648\u06d5: \u0686\u06d5\u0646\u062f \u062c\u0646\u06c6\u06a9\u06d5\u0634 - \u0628\u06d5\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc - \u0644\u06d5\u0628\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06cc \u06a9\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u060c \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5\u0648\u0627\u0646 \u062e\u06c6\u06cc \u0628\u0648\u0627\u0631\u062f\u0628\u0627 \u0648 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0645\u06d5 \u062f\u06d5\u0631\u0686\u0648\u0648\u0628\u0627\u060c \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u0627\u06af\u0631\u0645\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0686\u06d5\u0634\u0627\u0646\u062f.',
    '\u0686\u06cc \u062f\u06d5\u0648\u06cc\u0633\u062a \u0626\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f. \u0647\u06d5\u0631 \u0644\u06d5 \u06a9\u06c6\u0634\u06a9 \u0648 \u062a\u06d5\u0644\u0627\u0631\u06d5\u0648\u06d5\u060c \u062a\u0627 \u06a9\u06c6\u062a\u06d5\u06b5 \u0648 \u0628\u06c6\u062a\u06cc \u0632\u0644\u06cc \u0648\u06ce\u0646\u06d5\u062d\u06d5\u0648\u0632 \u0648 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u062a\u06cc\u0627\u0646\u06cc \u062f\u0627\u0628\u06d5\u0633\u062a\u0631\u0627\u0648. \u0626\u06d5\u06cc \u0645\u0627\u06b5 \u0648 \u062e\u06ce\u0632\u0627\u0646\u06cc \u062f\u0627\u0648\u0648\u062f! \u0634\u0648\u06a9\u0631\u06ce \u0628\u06a9\u06d5\u0646\u061b \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u0645 \u06a9\u06d5\u0645\u062a\u0631\u06cc\u0627\u0646 \u0634\u0648\u06a9\u0631\u06ce \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0644\u06d5\u0648\u06a9\u0627\u062a\u06d5\u0634\u062f\u0627 \u06a9\u06d5 \u0645\u0631\u062f\u0646\u0645\u0627\u0646 \u0628\u06c6 \u0628\u0695\u06cc\u0627\u0631\u062f\u0627\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0645\u06c6\u0631\u0627\u0646\u06d5 \u0646\u06d5\u0628\u0648\u0627\u06cc\u06d5 \u0648 \u062f\u0627\u0631\u0639\u06d5\u0633\u0627\u06a9\u06d5\u06cc \u0646\u06d5\u062e\u0648\u0627\u0631\u062f\u0627\u06cc\u06d5\u060c \u06a9\u06d5\u0633 \u0628\u06d5 \u0645\u06d5\u0631\u06af\u06cc \u0646\u06d5\u062f\u06d5\u0632\u0627\u0646\u06cc. \u06a9\u0627\u062a\u06ce \u0695\u06d5\u062a\u06cc \u0628\u0631\u062f \u0648 \u06a9\u06d5\u0648\u062a\u060c \u0628\u06c6 \u062c\u0646\u06c6\u06a9\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u060c \u06af\u06d5\u0631 \u067e\u06ce\u0648\u0627\u0631\u06cc\u0627\u0646 \u0628\u0632\u0627\u0646\u06cc\u0628\u0627\u060c \u0644\u06d5\u0645 \u0626\u0627\u0632\u0627\u0631\u06d5 \u067e\u0695\u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u06d5 \u062f\u06d5\u0631\u0686\u0648\u0648\u0628\u0648\u0648\u0646.',
    '\u062c\u06ce\u06af\u0627 \u0648 \u0634\u0648\u06ce\u0646\u06cc \u062e\u06d5\u06b5\u06a9\u06cc \u0633\u06d5\u0628\u0627 \u0646\u0645\u0648\u0648\u0646\u06d5 \u0628\u0648\u0648\u0646\u061b \u062f\u0648\u0648 \u0695\u06cc\u0632\u06d5\u0628\u0627\u063a \u0644\u06d5 \u0644\u0627\u06cc \u0695\u0627\u0633\u062a \u0648 \u0644\u0627\u06cc \u0686\u06d5\u067e\u06d5\u0648\u06d5. \u0644\u06d5\u0645 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u067e\u06ce\u062f\u0627\u0648\u0646\u060c \u0628\u06d5 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0648\u06d5 \u0644\u06ce\u06cc \u0628\u062e\u06c6\u0646\u061b \u0648\u06b5\u0627\u062a\u06ce\u06a9\u06cc \u0647\u06ce\u0646\u062f\u06d5 \u062e\u06c6\u0634 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0648\u0627 \u062e\u06d5\u062a\u0627\u067e\u06c6\u0634!',
    '\u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0644\u0627\u0641\u0627\u0648\u06cc \u0626\u0627\u0648\u06cc \u0628\u06d5\u0646\u062f\u06cc\u0645\u0627\u0646 \u0628\u06d5\u0631\u062f\u0627 \u0633\u06d5\u0631\u06cc\u0627\u0646\u061b \u0644\u06d5 \u062c\u06ce\u06cc \u062f\u0648\u0648 \u0695\u06cc\u0632\u06d5\u0628\u0627\u062e\u06d5\u06a9\u0627\u0646\u060c \u0645\u06cc\u0648\u06d5\u06cc \u062f\u0627\u0631\u06cc \u062f\u0627\u0631\u0633\u06cc\u0648\u0627\u06a9 \u0648 \u06af\u06d5\u0632\u06cc\u0686\u0627\u0631 \u0648 \u06a9\u06d5\u0645\u06ce \u06a9\u0646\u0627\u0631\u0645\u0627\u0646 \u0628\u06c6 \u0647\u06ce\u0634\u062a\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646 \u0633\u067e\u06b5\u06d5\u06cc \u067e\u06ce\u0646\u06d5\u0632\u0627\u0646 \u0628\u0648\u0648\u0646\u060c \u0626\u06d5\u0645 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646\u061b \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u0633\u067e\u06b5\u06d5 \u0646\u06d5\u0628\u0646\u060c \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u0646\u06cc\u0646\u061f',
    '\u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646 \u0648 \u06af\u0648\u0646\u062f\u0627\u0646\u06ce \u067e\u06cc\u062a\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062a\u06ce\u062e\u0633\u062a\u0628\u0648\u0648\u0646\u060c \u0686\u06d5\u0646\u062f \u062f\u06ce\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u067e\u0627\u06b5 \u06cc\u06d5\u06a9\u062a\u0631 \u0626\u0627\u0648\u0627\u06a9\u0631\u062f \u0648 \u0645\u06d5\u0648\u062f\u0627\u06cc \u0646\u06ce\u0648\u0627\u0646\u06cc\u0627\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u062f\u0627\u0646\u0627. \u062f\u06d5\u0633\u0627 \u0628\u06d5 \u0634\u06d5\u0648\u0627\u0646 \u0648 \u0695\u06c6\u0698\u0627\u0646 - \u0628\u06d5\u0628\u06ce \u062a\u0631\u0633 - \u0628\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0632 \u0628\u0628\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0645\u06d5\u0648\u062f\u0627\u06cc \u0645\u0632\u06b5 \u062a\u0627 \u0645\u0632\u06b5\u0645\u0627\u0646 - \u0644\u06d5 \u0633\u06d5\u0641\u06d5\u0631\u062f\u0627 - \u0628\u06c6 \u062f\u0648\u0648\u0631\u062a\u0631 \u06a9\u06d5\u061b \u06a9\u06d5 \u063a\u06d5\u062f\u0631\u06ce\u06a9 \u0628\u0648\u0648 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f. \u0626\u06d5\u0648\u0633\u0627 \u06af\u0634\u062a\u0645\u0627\u0646 \u0648\u06d5\u0647\u0627 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u062f\u0627\u0628\u0695\u06cc\u060c \u0628\u0648\u0648\u0646\u06d5 \u067e\u06d5\u0646\u062f\u06cc \u0695\u0648\u0648\u06cc \u0632\u06d5\u0645\u0627\u0646\u06d5\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u06d5\u0634 \u0686\u06d5\u0646\u062f \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9 \u0628\u0648\u0648 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06c6\u0695\u0627\u06af\u0631\u06ce\u06a9\u06cc \u0633\u0648\u067e\u0627\u0633\u06af\u0648\u0632\u0627\u0631.',
    '\u0634\u06d5\u06cc\u062a\u0627\u0646 \u062f\u06cc \u06a9\u06d5 \u06af\u0648\u0645\u0627\u0646\u06cc \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5\u0648\u0627\u0646 \u0648\u06d5\u0695\u0627\u0633\u062a \u06af\u06d5\u0695\u0627\u060c \u0634\u0648\u06ce\u0646\u06cc \u06a9\u06d5\u0648\u062a\u0646\u061b \u0647\u06d5\u0631\u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9 \u0644\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0645\u0627\u0646.',
    '\u0646\u06d5\u06cc\u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u062f\u0627 \u0632\u0627\u06b5 \u0628\u06ce\u061b \u062a\u06d5\u0646\u06cc\u0627 \u0628\u06c6\u06cc\u06d5 \u0628\u0648\u0648 \u0628\u0632\u0627\u0646\u06cc\u0646 \u06a9\u06ce \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5\u0642\u06cc\u0627\u0645\u06d5\u062a \u0647\u06d5\u06cc\u06d5 \u0648 \u06a9\u06ce\u0634 \u0644\u06d5\u0648\u0627\u0646\u062f\u0627 \u0644\u06ce\u06cc \u062f\u0648\u0648\u062f\u06b5\u06d5. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5 \u0647\u0627\u0648\u0627\u0631 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646 \u06a9\u06d5 \u0628\u06d5 \u062e\u06d5\u06cc\u0627\u06b5 \u0628\u06d5 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u062a\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0633\u06d5\u0646\u06af\u06cc \u0645\u06ce\u0631\u0648\u0648\u0644\u06d5 \u0648\u0631\u062f\u06cc\u0644\u06d5\u06cc\u06d5\u06a9\u060c \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u0646\u0627\u0695\u0648\u0627 \u0648 \u0644\u06d5 \u0633\u0627\u0632\u062f\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u06d5 \u0648 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0627\u0648\u06d5.',
    '\u062a\u06a9\u0627\u0634 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u0628\u06d5\u0647\u0631\u06d5\u0646\u0627\u062f\u0627\u061b \u0645\u06d5\u0631 \u0628\u06c6 \u06a9\u06d5\u0633\u06ce\u06a9 \u0626\u06cc\u0632\u0646\u06cc \u0628\u062f\u0627. \u062c\u0627 \u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u06cc \u062a\u0631\u0633\u06cc \u062f\u06b5\u06cc\u0627\u0646 \u062f\u0627\u062f\u06d5\u0645\u0631\u06a9\u06ce\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0686\u06cc \u0648\u062a\u061f \u0626\u06ce\u0698\u0646: \u0695\u0627\u0633\u062a\u06cc \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0647\u06d5\u0631\u06d5 \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u06cc \u0645\u06d5\u0632\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u06a9\u06ce \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0639\u06d5\u0631\u0632\u06d5\u0648\u06d5 \u0628\u0698\u06cc\u0648 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u06af\u06d5\u06cc\u0646\u06ce\u061f \u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627\u061b \u0626\u06ce\u0645\u06d5 \u0648 \u0626\u06ce\u0648\u06d5\u0634 \u06cc\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u0646\u060c \u06cc\u0627\u0646 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631 \u06af\u0648\u0646\u0627\u062d\u06ce \u0628\u06cc\u06a9\u06d5\u06cc\u0646\u060c \u06a9\u06d5\u0633 \u0644\u06d5\u0646\u06af\u06c6 \u0646\u0627\u067e\u0631\u0633\u06ce\u062a\u06d5\u0648\u06d5\u061b \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u06a9\u06d5\u0633 \u0644\u06d5 \u0645\u06d5 \u0646\u0627\u067e\u0631\u0633\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u062a\u06c6 \u0628\u06ce\u0698\u06d5: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646 \u0644\u06ce\u06a9\u062a\u0631\u0645\u0627\u0646 \u06a9\u06c6\u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0631 \u0686\u0644\u06c6\u0646\u06ce \u0695\u0627\u0633\u062a\u06d5\u060c \u0626\u0627\u0648\u0627 \u0628\u06d5 \u0646\u0627\u0648\u0645\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627. \u062e\u06c6\u06cc \u0632\u0627\u0646\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u06d5\u06a9\u0627\u0631\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648\u0627\u0646\u06d5\u0645 \u0646\u06cc\u0634\u0627\u0646\u062f\u06d5\u0646 \u06a9\u06d5 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u062a\u0627\u0646 \u0632\u0627\u0646\u06cc\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062e\u0648\u0627 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u0646\u06d5\u0628\u0648\u0648\u06d5\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0647\u06d5\u0631\u062e\u0648\u0627 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u0646\u0627\u0631\u062f \u06a9\u06d5 \u0628\u06c6 \u06af\u0634 \u062e\u06d5\u06b5\u06a9\u06cc \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646 \u0645\u0698\u062f\u06d5\u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0628\u06cc\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u0627\u067e\u06c6\u0631\u06d5\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u062f\u06d5\u0634\u06b5\u06ce\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u062a\u0646\u060c \u0626\u06d5\u0645 \u0628\u06d5\u06b5\u06ce\u0646\u06d5 \u06a9\u06d5\u06cc \u062f\u06ce\u062a\u06d5 \u062c\u06ce\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0628\u0695\u06cc\u0627\u0631\u06d5 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0628\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u0628\u06af\u06d5\u0646\u060c \u0646\u06d5 \u062a\u0627\u0648\u06ce\u06a9\u062a\u0627\u0646 \u0648\u06d5\u062f\u0648\u0627 \u062f\u06d5\u062e\u06d5\u0646\u060c \u0646\u06d5 \u062a\u0627\u0648\u06ce\u06a9\u062a\u0627\u0646 \u0632\u0648\u0648\u062a\u0631 \u062f\u06d5\u0628\u06d5\u0646.',
    '\u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0646\u06d5 \u0628\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5 \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u0646\u06d5 \u0628\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5\u0648 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u0627\u0648\u0646. \u06af\u06d5\u0631 \u062a\u06c6\u0626\u06d5\u0648 \u0646\u0627\u0647\u06d5\u0642 \u067e\u06cc\u0634\u0627\u0646\u06d5\u062a \u0628\u062f\u06cc\u062a\u0627\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0647\u06d5\u0645\u0628\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0695\u0627\u06af\u06cc\u0631\u0627\u0648\u0646\u060c \u0647\u06ce\u0646\u062f\u06ce \u0626\u0627\u0648\u0695 \u0648\u06d5\u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0626\u0627\u062e\u06ce\u0648\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06ce \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0628\u0648\u0648\u0646\u060c \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u0628\u0648\u0648\u0646\u0627\u06cc\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u062f\u06d5\u0647\u06ce\u0646\u0627.',
    '\u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0628\u06d5 \u0628\u06ce \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5 \u0628\u0648\u0648\u06cc\u0646 \u0646\u06d5\u0645\u0627\u0646\u0647\u06ce\u0634\u062a \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0695\u06ce\u06cc \u0695\u0627\u0633\u062a\u0627 \u0628\u0695\u06c6\u0646\u060c \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646\u062f\u06d5\u0631\u062a\u0627\u0646 \u0647\u0627\u062a\u06d5 \u0644\u0627\u061f \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631 \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0628\u06ce \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u0648\u06d5\u0631\u0627\u0645\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 - \u06a9\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0628\u0648\u0648\u06af\u0646 - \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0634\u06d5\u0648\u0648\u0695\u06c6\u0698 \u0647\u06d5\u0631 \u06af\u06d5\u0695\u062a\u0627\u0646 \u062a\u06ce\u062f\u06d5\u0647\u0627\u06b5\u0627\u0646\u062f\u06cc\u0646 \u06a9\u06d5 \u062f\u06d5\u0628\u06ce \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc\u0646 \u0648 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0647\u0627\u0648\u062a\u0627\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u06cc\u0646. \u06a9\u0627\u062a\u06ce \u062f\u06cc\u062a\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u060c \u0626\u06ce\u0645\u06d5 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 - \u0632\u0646\u062c\u06cc\u0631 \u0644\u06d5 \u0645\u0644 - \u0695\u0627\u062f\u06d5\u06a9\u06ce\u0634\u06cc\u0646\u06d5 \u067e\u0627\u06cc \u062d\u06d5\u0633\u0627\u0648\u060c \u0628\u06d5\u062f\u06b5 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5. \u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u06cc \u0633\u0632\u0627 \u062f\u06d5\u062f\u0631\u06ce.',
    '\u0647\u06d5\u062a\u0627 \u0626\u06ce\u0633\u062a\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u0647\u06d5\u0631 \u0626\u0627\u0648\u06d5\u062f\u0627\u0646\u06cc\u06cc\u06d5\u06a9\u060c \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u062a\u0631\u0633\u06ce\u0646\u06ce\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0648\u067e\u06d5\u0695\u06cc \u062e\u06c6\u0634\u06cc\u062f\u0627 \u0698\u06cc\u0627\u0648\u0646\u060c \u06af\u0648\u062a\u0648\u0648\u06cc\u0627\u0646\u06d5: \u0628\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u06cc \u0695\u0627\u0633\u067e\u06ce\u0631\u0627\u0648\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u06cc\u0646.',
    '\u06af\u0648\u062a\u0648\u0648\u0634\u06cc\u0627\u0646\u06d5: \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0645\u06d5\u0695 \u0645\u0627\u06b5\u06cc \u062f\u0646\u06cc\u0627 \u0648 \u0639\u06d5\u0648\u0644\u0627\u062f\u06d5\u0648\u06d5 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u067e\u062a\u0631\u0645\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631 \u0646\u0627\u0628\u06cc\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0695\u06c6\u0632\u06cc\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u062f\u06d5\u06cc\u0646\u06ce\u0631\u06ce\u061b \u0632\u06c6\u0631 \u0648 \u06a9\u06d5\u0645\u06cc \u0628\u06d5\u0633\u062a\u06d5 \u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0633\u0627\u0645\u0627\u0646\u06cc\u0634 \u0648 \u0639\u06d5\u0648\u0644\u0627\u062f\u06cc \u0632\u06c6\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06cc\u0646 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0648 \u0646\u0632\u06cc\u06a9 \u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0633\u06ce \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u0626\u06ce\u0645\u06d5 \u0647\u06ce\u0646\u0627\u0628\u06ce \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0695\u06d5\u0686\u0627\u0648 \u0628\u06a9\u0627\u061b \u06a9\u06d5 \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u06a9\u0631\u062f\u06d5\u06cc\u0627\u0646 \u062f\u0648\u0648\u0633\u06d5\u0631\u06d5 \u067e\u0627\u062f\u0627\u0634 \u0626\u06d5\u06af\u0631\u0646 \u0648 \u0628\u06ce \u062f\u06d5\u063a\u062f\u06d5\u063a\u06d5 \u0644\u06d5 \u0626\u06c6\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a\u0627 \u062f\u06d5\u0633\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06c6 \u0628\u06ce \u0628\u0627\u06cc\u06d5\u062e \u06a9\u0631\u062f\u0646\u06cc \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u06a9\u06c6\u0634\u0634 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0628\u06d5 \u062f\u06d5\u0633 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u062f\u06d5\u0631\u0628\u0686\u0646\u060c \u0626\u06d5\u0634\u06ce \u0644\u06d5 \u0646\u0627\u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062f\u0627 \u0628\u062a\u0644\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u062a\u06c6 \u0628\u06ce\u0698\u06d5: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u0628\u0698\u06cc\u0648\u06cc \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062f\u06d5\u062f\u0627\u060c \u0632\u06c6\u0631 \u0648 \u06a9\u06d5\u0645\u06cc \u0628\u06d5\u0633\u062a\u06d5 \u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06ce\u0634\u06cc \u0628\u06d5\u062e\u06ce\u0631 \u0628\u062f\u06d5\u0646\u060c \u0628\u06c6\u062a\u0627\u0646 \u062a\u06ce\u0647\u06d5\u06b5\u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u0695\u06c6\u0632\u06cc \u062f\u06d5\u0631 \u0626\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0648\u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0627\u0646 \u06a9\u06c6\u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5\u0641\u0631\u06cc\u0634\u062a\u0627\u0646 \u0626\u06ce\u0698\u06ce: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0626\u06ce\u0648\u06d5\u06cc\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a\u0648\u06d5\u061f',
    '\u0626\u06ce\u0698\u0646: \u0647\u06d5\u0631\u062a\u06c6\u06cc \u0647\u06cc\u0686 \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc\u06cc\u06d5\u06a9\u062a \u0646\u06d5\u0628\u06ce\u061b \u0647\u06d5\u0631\u062e\u06c6\u062a \u06af\u06d5\u0648\u0631\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646\u06cc\u061b \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u0646\u0627\u0633\u06cc\u0646\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646 \u062c\u0646\u062f\u06c6\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a \u0648 \u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631\u06cc\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0628\u0648\u0648.',
    '\u062c\u0627 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u0646\u06d5 \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u06d5 \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646\u06ce\u06a9. \u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u063a\u06d5\u062f\u0631\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0626\u06ce\u0698\u06cc\u0646: \u0628\u0686\u06ce\u0698\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u0627\u06af\u0631\u060c \u06a9\u06d5 \u0628\u06d5 \u062f\u0631\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06cc.',
    '\u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u0627\u06cc\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0631 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0628\u0628\u06ce \u0648 \u0646\u06d5\u0628\u06ce \u0626\u06d5\u0645 \u067e\u06cc\u0627\u0648\u06d5 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5\u062a\u06cc \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u062a\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a \u067e\u06ce\u0634\u06cc\u0627\u0646 \u0628\u06af\u0631\u06ce\u061b \u06af\u0648\u062a\u06cc\u0634\u06cc\u0627\u0646 \u0626\u06d5\u0645\u06d5 \u062f\u0631\u06c6\u06cc\u06d5 \u0648 \u0695\u06ce\u06a9\u062e\u0631\u0627\u0648\u06d5. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5\u060c \u0648\u06d5\u062e\u062a\u06ce \u0626\u06d5\u0645 \u0695\u0627\u0633\u062a\u06d5\u06cc\u0627\u0646 \u0628\u06c6\u0647\u0627\u062a\u060c \u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 - \u0628\u0628\u06ce \u0648 \u0646\u06d5\u0628\u06ce - \u062c\u0627\u062f\u0648\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u06a9\u062a\u06ce\u0628\u06af\u06d5\u0644\u06ce\u06a9\u06cc\u0634\u0645\u0627\u0646 \u0646\u06d5\u062f\u0627\u0628\u0648\u0648\u0646\u06ce \u0628\u06cc\u0627\u0646\u062e\u0648\u06ce\u0646\u0646\u061b \u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6\u0634 \u0647\u06cc\u0686 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0645\u0627\u0646 \u0628\u06c6 \u0646\u06d5\u0646\u0627\u0631\u062f\u0648\u0648\u0646 \u06a9\u06d5 \u062a\u0631\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631\u0628\u06ce\u0646\u06ce.',
    '\u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5\u0634 - \u0648\u06d5\u06a9 \u0626\u06d5\u0645\u0627\u0646\u06d5 - \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646\u061b \u062f\u06d5\u06cc\u06d5\u06a9\u06cc \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06af\u06d5\u06cc\u0648\u06d5 \u06a9\u06d5 \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5\u0648\u0627\u0646\u0645\u0627\u0646 \u062f\u0627\u0628\u0648\u0648 \u0648 \u0628\u06d5\u06b5\u06ce\u060c \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u0645. \u0633\u0627 \u0645\u0646 \u0686\u06c6\u0646 \u062f\u06d5\u0631\u062f\u06ce\u06a9\u0645 \u067e\u06ce\u062f\u0627\u0646!',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0647\u06d5\u0631 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0645: \u0644\u06d5\u0628\u06d5\u0631 \u062e\u0648\u062f\u0627 \u06cc\u06d5\u06a9\u06d5\u06cc\u06d5\u06a9\u06d5 \u0648 \u062f\u0648\u0627\u0646 \u062f\u0648\u0627\u0646 \u0695\u0627\u0648\u06d5\u0633\u062a\u0646 \u0648 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0628\u06cc\u0631 \u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061b \u0647\u06d5\u0648\u0627\u06b5\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u0647\u06cc\u0686 \u0634\u06ce\u062a\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5\u061b \u0626\u06d5\u0648 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u0647\u0627\u062a\u0646\u06cc \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0628\u06d5 \u0698\u0627\u0646\u062a\u0627\u0646 \u0628\u062a\u0631\u0633\u06ce\u0646\u06ce.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0647\u06cc\u0686 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u0645 \u0644\u06ce\u062a\u0627\u0646 \u0646\u0627\u0648\u06ce\u061b \u0626\u06d5\u0648\u06cc \u062f\u06d5\u0628\u0648\u0648 \u0628\u06cc\u062f\u06d5\u0646 \u0628\u06d5 \u0645\u0646\u06cc\u0634 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u0645\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648 - \u0628\u06c6 \u062e\u06c6\u06cc - \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648\u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06ce \u0633\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0647\u06d5\u0642 \u062f\u06d5\u0646\u06ce\u0631\u06ce. [\u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc] \u0686\u0627\u0632\u0627\u0646\u06cc \u0646\u0647\u06ce\u0646\u06cc\u06d5\u06a9\u0627\u0646\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0695\u0627\u0633\u062a\u06cc \u062c\u06ce\u06cc \u067e\u0695\u0648\u067e\u0648\u0686\u06cc \u06af\u0631\u062a\u0648\u0648\u06d5 \u0648 \u0686\u0631\u0648\u06a9 \u0647\u06cc\u0686 \u0628\u0627\u0648\u06ce\u06a9\u06cc \u0646\u06d5\u0645\u0627 \u0648 \u062a\u0627\u0632\u06d5 \u0642\u06d5\u062a \u0646\u0627\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0631\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a \u06a9\u0644\u0627 \u0628\u0648\u0648\u0628\u0645\u060c \u0648\u06d5\u0628\u0627\u06b5\u06cc \u062f\u06ce\u062a\u06d5 \u0626\u06d5\u0633\u062a\u06c6\u06cc \u062e\u06c6\u0645\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0634\u0627\u0631\u06d5\u0632\u0627\u0634 \u0628\u0648\u0648\u0628\u06ce\u062a\u0645\u060c \u0647\u06c6\u06cc\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5\u06cc\u06d5 \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0628\u06c6\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u0645\u061b \u0626\u06d5\u0648\u06d5 \u062f\u06d5\u0628\u06cc\u0633\u06ce \u0648 \u0646\u0632\u06cc\u06a9\u06d5.',
    '\u0628\u0631\u06cc\u0627 [\u0626\u06d5\u0648\u0627\u0646\u06d5\u062a] \u0628\u062f\u06cc\u0628\u0627\u060c \u0648\u06d5\u062e\u062a\u06ce \u062a\u0631\u0633\u06cc\u0627\u0646 \u0695\u06ce \u0646\u06cc\u0634\u062a\u0648\u0648\u06d5 \u0648 \u0647\u06cc\u0686 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5 \u0648 \u0644\u06d5 \u067e\u06d5\u0646\u0627\u0648\u06d5 \u062f\u06d5\u06af\u06cc\u0631 \u0647\u0627\u062a\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u0648\u0627 \u0628\u0627\u0648\u06d5\u0695\u0645\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627. \u06a9\u06d5\u06cc \u062a\u0627\u0632\u06d5 \u0644\u06d5\u0648 \u062c\u06ce \u062f\u0648\u0648\u0631\u06d5\u0648\u06d5 \u0628\u06d5\u0648\u06d5 \u0695\u0627\u062f\u06d5\u06af\u06d5\u0646\u061f',
    '\u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u062f\u0648\u0648\u0631\u06d5\u0648\u06d5 \u0648 - \u0644\u06d5 \u0646\u06d5\u062f\u06cc\u062a\u06d5 - \u0647\u06d5\u0631 \u0628\u0648\u062e\u062a\u0627\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0647\u0627\u0648\u06cc\u0634\u062a.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u0648\u0648\u0631 \u062f\u06d5\u062e\u0631\u06ce\u0646\u06d5\u0648\u06d5 \u0644\u06d5\u0648\u06d5\u06cc \u0648\u0627 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0647\u06d5\u0631\u0648\u0627 \u06a9\u06d5 \u0647\u0627\u0648\u0695\u06ce\u06cc\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 - \u0628\u06d5\u0631\u0644\u06d5 \u0626\u06d5\u0648\u0627\u0646 - \u0628\u06d5\u0648 \u062f\u06d5\u0631\u062f\u06d5 \u0686\u0648\u0648\u0646. \u0626\u06d5\u0648\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u06af\u0648\u0645\u0627\u0646\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631 \u0628\u0648\u0648\u0646.',
    '\u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u062f\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0641\u0631\u06cc\u0634\u062a\u0627\u0646\u06cc \u062f\u0648\u0648 \u0628\u0627\u06b5 \u0648 \u0633\u06ce \u0628\u0627\u06b5 \u0648 \u0686\u0648\u0627\u0631 \u0628\u0627\u06b5\u06cc \u06a9\u0631\u062f\u0648\u0648\u062a\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5. \u0647\u06d5\u0631 \u0686\u06c6\u0646\u06cc \u0628\u06c6 \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u0644\u06d5 \u0628\u06d5\u0631\u0647\u06d5\u0645\u06cc \u0632\u06ce\u062f\u06d5 \u062f\u06d5\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9 \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0648\u06cc \u062e\u06ce\u0631\u06ce \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u06a9\u06d5\u0633 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u06af\u0627\u06b5\u06d5\u06cc \u0628\u062f\u0627 \u0648\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634 \u06af\u0627\u06b5\u06d5\u06cc \u0628\u062f\u0627 - \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u06cc - \u06a9\u06d5\u0633 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u0628\u06cc\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0644\u06d5 \u0628\u06cc\u0631\u062a\u0627\u0646 \u0628\u06ce \u062e\u0648\u062f\u0627 \u0686\u06d5\u0646\u062f\u06cc \u0686\u0627\u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u06cc\u06d5\u061b \u0626\u0627\u062e\u06c6 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u060c \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0626\u06d5\u0632\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0646\u0627 \u0628\u0698\u06cc\u0648\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u0627\u0628\u06cc\u0646 \u06a9\u0627\u061f \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u06c6\u06cc \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce\u061b \u0686\u0644\u06c6\u0646 \u0644\u06d5 \u0695\u06ce \u06a9\u0644\u0627 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u062a\u06c6 \u0628\u0695\u0648\u0627 \u0646\u0627\u06a9\u06d5\u0646\u060c \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6\u0634 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646 \u062f\u0627\u0646\u0631\u0627\u0646. \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u062e\u0648\u0627 \u062f\u06d5\u0628\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0628\u06ce \u0633\u06c6 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u0627 \u0695\u0627\u0633\u062a\u06d5\u061b \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627 \u0646\u06d5\u06a9\u0627 \u0628\u062a\u0627\u0646\u062e\u06d5\u06b5\u06d5\u062a\u06ce\u0646\u06ce \u0648 \u0646\u06d5\u06a9\u0627 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u062e\u0634\u06d5\u0646\u062f\u06d5\u06cc\u06cc \u062e\u0648\u062f\u0627\u062a\u0627\u0646 \u0641\u0631\u06cc\u0648 \u0628\u062f\u0627.',
    '\u0634\u06d5\u06cc\u062a\u0627\u0646 \u062f\u0648\u0698\u0645\u0646\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5\u060c \u0633\u0627 \u0626\u06ce\u0648\u06d5\u0634 \u0628\u0628\u0646\u06d5 \u062f\u0648\u0698\u0645\u0646\u06cc\u061b \u0626\u06d5\u0648 \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u062f\u0627\u0631\u0648\u062f\u06d5\u0633\u062a\u06d5\u06cc \u06af\u0627\u0632\u06cc \u062f\u06d5\u06a9\u0627\u060c \u062a\u0627 \u0644\u06d5 \u06cc\u0627\u0631\u0627\u0646\u06cc \u0626\u0627\u06af\u0631 \u0628\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646\u060c \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u062a\u0648\u0646\u062f\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce. \u0626\u06d5\u0648\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc\u0627\u0646 \u0695\u06d5\u0686\u0627\u0648 \u06a9\u0631\u062f\u060c \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u0639\u0627\u0641\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u0646 \u0648 \u067e\u0627\u062f\u0627\u0634\u06cc \u0647\u06ce\u0698\u0627\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u062e\u0631\u0627\u067e\u06d5\u06cc \u0628\u06c6\u062c\u0648\u0627\u0646 \u06a9\u0631\u0627\u0648\u06d5 \u0648 \u0628\u06c6 \u062e\u06c6\u06cc \u067e\u06d5\u0633\u0646\u062f\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5 [\u0648\u06d5\u06a9\u0648\u0648 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u0648\u0627 \u0646\u06d5\u0628\u06ce\u061f] \u062e\u0648\u0627 \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06af\u0648\u0645\u0695\u0627 \u062f\u06d5\u06a9\u0627\u062a \u0648 \u062d\u06d5\u0632\u06cc\u0634 \u0628\u06a9\u0627\u060c \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u0627. \u0646\u06d5\u06a9\u0627 \u0644\u06d5\u0628\u06d5\u0631 \u062e\u06d5\u0645\u06cc \u0626\u06d5\u0648\u0627\u0646\u060c \u062a\u06ce\u062f\u0627 \u0628\u0686\u06cc\u061b \u062e\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0686 \u06a9\u0627\u0631\u06ce\u06a9 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0626\u06d5\u0648 \u0628\u0627\u06cc\u0627\u0646\u06d5 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0648\u0631 \u062f\u06d5\u0628\u0632\u0648\u06ce\u0646\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0648\u0631 \u062f\u06d5\u0626\u0627\u0698\u0648\u06ce\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06ce\u06a9\u06cc \u0645\u0631\u062f\u0648\u0648\u061b \u0632\u06d5\u0648\u06cc \u0645\u0631\u062f\u0648\u0648 \u0628\u06d5\u0648\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648\u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u061b \u0648\u06d5\u0698\u06cc\u0627\u0646\u062f\u0646\u06cc \u067e\u0627\u0634\u06d5\u0695\u06c6\u0698\u06cc\u0634 \u0647\u06d5\u0631 \u0626\u0627\u0648\u0627\u06cc\u06d5.',
    '\u06a9\u06d5\u0633\u06ce \u0642\u06d5\u062f\u0631 \u0648 \u0695\u06ce\u0632\u06cc \u062f\u06d5\u0648\u06ce\u060c \u0695\u06ce\u0632 \u0648 \u0642\u06d5\u062f\u0631 \u06af\u0634\u062a\u06cc \u0644\u06d5 \u062f\u06d5\u0633\u062a \u062e\u0648\u062f\u0627\u062f\u0627\u06cc\u06d5. \u0648\u062a\u06d5\u06cc \u0628\u0627\u0634 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u0628\u06d5\u0631\u06d5\u0698\u0648\u0648\u0631 \u062f\u06d5\u0686\u06ce \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u0632\u06cc \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0641\u06ce\u06b5 \u0628\u06c6 \u062e\u0631\u0627\u067e\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u0633\u0632\u0627 \u062f\u06d5\u062f\u0631\u06ce\u0646 \u0648 \u0641\u06ce\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u0647\u06cc\u0686 \u062f\u06d5\u0686\u06ce.',
    '\u062e\u0648\u062f\u0627 \u0626\u06ce\u0648\u06d5\u06cc \u0647\u06d5\u0648\u06d5\u06b5 \u0644\u06d5 \u06af\u06b5\u060c \u067e\u0627\u0634\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u062a\u0646\u06c6\u06a9\u06d5\u06cc\u06d5\u06a9 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u061b \u067e\u0627\u0634\u0627\u0646 \u062c\u0648\u0648\u062a\u06d5\u06cc \u0644\u06ce \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627\u0646. \u0686\u06cc \u0644\u06d5 \u062a\u06c6\u0644\u06cc \u0645\u06ce\u0648\u06cc\u0646\u06d5\u062f\u0627 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0645\u06ce\u0648\u06cc\u0646\u06d5 \u062f\u06d5\u0632\u06ce\u060c \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce. \u06a9\u06ce \u0639\u0648\u0645\u0631\u06cc \u062f\u0631\u06ce\u0698\u06cc \u062f\u06d5\u0628\u06ce \u0648 \u06a9\u06ce \u062a\u06d5\u0645\u06d5\u0646\u06cc \u06a9\u0648\u0631\u062a\u06cc \u062f\u06d5\u0628\u06ce\u060c \u0647\u06d5\u0645\u0648\u0648 \u0644\u06d5\u062f\u06d5\u0641\u062a\u06d5\u0631 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5\u061b \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0647\u0627\u0633\u0627\u0646\u06d5.',
    '\u062f\u0648\u0648 \u062f\u06d5\u0631\u06cc\u0627 \u0647\u06d5\u0646 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9 \u0646\u06cc\u0646: \u0626\u06d5\u0645 \u0634\u06cc\u0631\u06cc\u0646\u06d5 \u0648 \u062a\u06cc\u0646\u0648\u0648\u0627\u06cc\u06d5\u062a\u06cc \u0626\u06d5\u0634\u06a9\u06ce\u0646\u06ce\u062a \u0648 \u06a4\u06d5\u062e\u0627\u0631\u0646\u06cc \u06af\u06c6\u0627\u0631\u0627\u06cc\u06d5\u060c \u0626\u06d5\u0648 \u0634\u06c6\u0631\u06ce\u06a9\u06cc \u062a\u0627\u06b5 \u0648 \u062a\u0641\u062a\u06d5\u061b \u0644\u06d5 \u0647\u06d5\u0631\u06a9\u0627\u0646\u06cc\u0634 \u06af\u06c6\u0634\u062a\u06cc \u062a\u0627\u0632\u06d5 \u0628\u06c6 \u062e\u0627\u0631\u0646\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0632\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0642\u06d5\u06b5\u06ce\u0634\u0646 \u062a\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0633\u0627\u06cc\u06d5\u06cc \u0626\u06d5\u0648\u062f\u0627 \u0695\u06c6\u0632\u06cc \u0628\u06c6 \u062e\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u0646 \u0648 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0628\u0628\u0698\u06ce\u0631\u0646.',
    '\u0634\u06d5\u0648 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0646\u0627\u0648 \u0695\u06c6\u0698\u06d5\u0648\u06d5 \u0648 \u0695\u06c6\u0698\u06cc\u0634 \u062f\u06d5\u0634\u06d5\u0648\u06ce \u0695\u0627\u062f\u06d5\u06a9\u0627\u061b \u062e\u06c6\u0631 \u0648 \u0647\u06d5\u06cc\u06a4\u06cc\u0634\u06cc \u06a9\u06d5\u0648\u06cc \u06a9\u0631\u062f\u061b \u0647\u06d5\u0631\u06a9\u0627\u0645\u06cc \u062a\u0627 \u06a9\u0627\u062a\u06cc \u062f\u06cc\u0627\u0631\u062f\u06d5 \u062f\u06ce\u0646 \u0648 \u062f\u06d5\u0686\u0646. \u0626\u06ce\u0648\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u062f\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u067e\u0627\u062f\u0634\u0627\u0647\u06cc\u0634 \u0647\u06d5\u0631 \u0644\u06d5 \u0626\u06d5\u0648 \u062f\u06d5\u0648\u06d5\u0634\u06ce\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u0626\u06ce\u0648\u06d5 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u0628\u06d5\u0646\u060c \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a\u06cc \u062a\u06c6\u062e\u06b5\u06cc \u062a\u06d5\u0646\u06a9\u06cc \u062f\u06d5\u0646\u06a9\u06cc \u062e\u0648\u0631\u0645\u0627\u06cc\u06d5 \u0634\u06a9 \u0646\u0627\u0628\u06d5\u0646.',
    '\u06af\u06d5\u0631 \u0647\u0627\u0648\u0627\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6\u0628\u06d5\u0631\u0646\u060c \u0647\u0627\u0648\u0627\u0631\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u0646\u0627\u0628\u06cc\u0633\u0646\u061b \u0628\u06cc\u0634\u06cc \u0628\u06cc\u0633\u0646\u060c \u0646\u0627\u0634\u06ce\u0646 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u062a\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06ce\u0646\u061b \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u0634 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u062a\u0627\u0646\u0646. \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9 \u062f\u06d5\u0632\u0627\u0646\u06ce - \u06a9\u06d5\u0633 \u0646\u0627\u062a\u0627\u0646\u06ce [\u0644\u06d5\u0648\u0627\u0646\u06d5] \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a \u06a9\u0627.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0626\u0627\u062a\u0627\u062c\u0646\u061b \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5 \u0648 \u0647\u06ce\u0698\u0627\u06cc\u06d5 \u067e\u06d5\u0633\u0646\u062f\u06cc \u0628\u062f\u0631\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632\u06a9\u0627\u060c \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u06cc\u0646 \u062f\u06d5\u0628\u0627\u062a \u0648 \u0633\u06d5\u0631\u0644\u06d5 \u0646\u0648\u06ce\u060c \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u06cc \u062a\u0631 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u06a9\u0627\u061b',
    '\u0626\u06d5\u0645\u06d5\u0634 \u0644\u0627\u06cc \u062e\u0648\u0627 \u062f\u0698\u0648\u0627\u0631 \u0646\u06cc\u06d5.',
    '\u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0628\u0627\u0631\u06cc \u06af\u0648\u0646\u0627\u0647\u06cc \u06a9\u06d5\u0633 \u0648\u06d5\u0633\u062a\u06c6 \u0646\u0627\u06af\u0631\u06ce. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06ce\u062a\u0648\u0648 \u06af\u0631\u0627\u0646 \u0628\u0627\u0631\u06ce\u06a9 \u0644\u06d5\u0628\u06d5\u0631 \u06cc\u06d5\u06a9\u06cc \u062a\u0631 \u0648\u06d5\u067e\u0627\u0695\u06ce: \u0634\u0627\u0646 \u0628\u062f\u0627\u062a\u06d5 \u0628\u06d5\u0631 \u0628\u0627\u0631\u06d5\u06a9\u06d5\u06cc - \u0628\u0627 \u062e\u0632\u0645\u06cc \u0646\u0632\u06cc\u06a9\u06cc\u0634\u06cc \u0628\u06ce - \u062e\u06c6 \u0646\u0627\u062e\u0627\u062a\u06d5 \u0698\u06ce\u0631 \u0628\u0627\u0631\u06cc \u0626\u06d5\u0648. \u062a\u06c6 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u062a \u0644\u06d5\u0633\u06d5\u0631\u06d5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0628\u062a\u0631\u0633\u06ce\u0646\u06cc \u06a9\u06d5 \u0646\u06d5\u062f\u06cc\u062a\u06d5 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u0646 \u0648 \u0646\u0645\u06ce\u0698 \u062f\u06d5\u06a9\u06d5\u0646. \u06a9\u06ce\u0634 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u0628\u06d5\u067e\u0627\u06a9\u0698\u06cc \u0628\u0698\u06cc\u060c \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06d5\u0648 \u067e\u0627\u06a9\u06cc\u06cc\u06d5 \u0628\u06d5\u0647\u0631\u06d5 \u062f\u06d5\u0628\u0627 \u0648 \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5.',
    '\u06a9\u0648\u06ce\u0631 \u0648 \u0686\u0627\u0648\u0633\u0627\u063a \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9 \u0646\u06cc\u0646.',
    '\u062a\u0627\u0631\u06cc\u06a9\u0627\u06cc\u06cc \u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u0627\u06cc\u06cc\u0634 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9 \u0646\u06cc\u0646.',
    '\u0628\u06d5\u0631\u0633\u06ce\u0628\u06d5\u0631 \u0648 \u06af\u06d5\u0631\u0645\u0627\u06cc \u062a\u0627\u0648\u06cc\u0634 \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9 \u0646\u06cc\u0646.',
    '\u06af\u06cc\u0627\u0646 \u0644\u06d5\u0628\u06d5\u0631\u0627\u0646 \u0648 \u0628\u06ce \u06af\u06cc\u0627\u0646\u06cc\u0634 \u0647\u0627\u0648\u0648\u06ce\u0646\u06d5 \u0646\u06cc\u0646. \u062e\u0648\u062f\u0627 \u062d\u06d5\u0632 \u06a9\u0627\u060c \u0648\u0627 \u062f\u06d5\u06a9\u0627 \u0647\u06d5\u0631\u06a9\u06d5\u0633 \u0628\u0628\u06cc\u0633\u06ce. \u062a\u06c6 \u0646\u0627\u062a\u0627\u0646\u06cc \u062f\u06d5\u0646\u06af\u062a \u0628\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0627\u0628\u06af\u0627 \u06a9\u06d5 \u062e\u0631\u0627\u0648\u0646\u06d5 \u0646\u0627\u0648 \u06af\u06c6\u0695\u06d5\u0648\u06d5.',
    '\u062a\u06c6 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u062a \u0644\u06d5\u0633\u06d5\u0631\u06d5 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u062e\u06d5\u06b5\u06a9\u06cc \u0628\u06ce\u0646\u06cc.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u062a\u06c6\u0645\u0627\u0646 \u0646\u0627\u0631\u062f \u0645\u0698\u062f\u06d5\u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0628\u06cc. \u0647\u06cc\u0686 \u06a9\u06c6\u0645\u06d5\u06b5\u062a\u06d5\u06cc\u06d5\u06a9\u06cc\u0634 \u0646\u06d5\u0628\u0648\u0648\u06d5 \u06a9\u06d5 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631\u0647\u06ce\u0646\u06d5\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u06d5\u0686\u0648\u0648\u0628\u06ce.',
    '\u06af\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062a\u06c6 \u0648\u06d5\u062f\u0631\u06c6 \u062f\u06d5\u062e\u06d5\u0646\u06d5\u0648\u06d5\u060c \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5\u0634 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u0631\u06c6\u0632\u0646 \u0698\u0645\u06ce\u0631\u0627\u061b \u06af\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0646\u06cc\u0634\u0627\u0646\u06d5\u06af\u06d5\u0644 \u067e\u062a\u06d5\u0648 \u0648 \u0628\u06d5\u0633\u06cc\u067e\u0627\u0631\u06d5 \u0648 \u06a9\u062a\u06ce\u0628\u06cc \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u062f\u06d5\u0631\u06d5\u0648\u06d5 \u0647\u0627\u062a\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u0645 \u0628\u06d5 \u063a\u06d5\u0632\u06d5\u0628 \u06af\u0631\u062a. \u062f\u06d5\u0633\u0627 \u0633\u0632\u0627\u06cc \u0645\u0646 \u0686\u0644\u06c6\u0646 \u0628\u0648\u0648\u061f',
    '\u0626\u0627\u062e\u06c6 \u0646\u06d5\u062a \u062f\u06cc\u060c \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u06cc \u0644\u06d5\u0648 \u0628\u06d5\u0631\u0632\u06d5\u0648\u06d5 \u0628\u0627\u0631\u0627\u0646\u062f\u0648\u0648\u06d5\u060c \u0645\u06cc\u0648\u06d5\u062c\u0627\u062a\u06cc \u0647\u06d5\u0645\u06d5\u0695\u06d5\u0646\u06af\u06d5\u0645\u0627\u0646 \u067e\u06ce \u0695\u0648\u0648\u0627\u0646\u062f\u061f \u0644\u06d5 \u06a9\u06ce\u0648\u0627\u0646\u06cc\u0634 \u0695\u06ce\u06af\u06d5\u0648\u0628\u0627\u0646\u06cc \u0633\u067e\u06cc \u0648 \u0633\u0648\u0648\u0631\u06cc \u062c\u06c6\u0631\u0627\u0648\u062c\u06c6\u0631\u06cc \u0628\u06d5\u0695\u06d5\u0646\u06af \u062c\u06cc\u0627\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0646\u061f \u0695\u06d5\u0634\u06cc \u0648\u06d5\u06a9 \u0642\u06d5\u0644\u06d5\u0695\u06d5\u0634\u06d5\u0634\u06cc\u0627\u0646 \u062a\u06ce\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0648 \u062c\u0627\u0646\u06d5\u0648\u06d5\u0631 \u0648 \u067e\u0627\u062a\u0627\u06b5\u06cc\u0634\u062f\u0627\u060c \u0647\u06d5\u0631 \u0648\u06d5\u0647\u0627\u06cc\u06d5\u061b \u0695\u06d5\u0646\u06af\u06cc\u0627\u0646 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u062c\u0648\u062f\u0627\u06cc\u06d5. \u0644\u06d5 \u0646\u0627\u0648 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u062f\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0641\u0631\u06d5\u0632\u0627\u0646\u0627\u0646 \u0644\u06ce\u06cc \u062f\u06d5\u062a\u0631\u0633\u0646. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u062e\u06d5\u062a\u0627 \u067e\u06c6\u0634\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u062a\u06ce\u0628\u06cc \u062e\u0648\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0648 \u0646\u0648\u06ce\u0698 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0648\u0646 - \u0628\u06d5\u0646\u0647\u06ce\u0646\u06cc \u0648 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06cc - \u062e\u06ce\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06d5 \u0647\u0648\u0645\u06ce\u062f\u06cc \u0633\u06d5\u0648\u062f\u0627\u06cc\u06d5\u06a9\u0646 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5 \u0628\u0631\u06d5\u0648 \u0646\u0627\u06a9\u06d5\u0648\u06ce.',
    '\u062a\u0627 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u06a9\u06c6\u0634\u0634\u06cc\u0627\u0646 \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce \u0628\u062f\u0627 \u0648 \u0644\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0632\u06ce\u062f\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u0633\u06d5\u0631 \u0628\u062e\u0627. \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0626\u06d5\u0648 \u062e\u06d5\u062a\u0627\u067e\u06c6\u0634 \u0648 \u067e\u06ce \u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0628\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u06d5\u060c \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5\u0648 \u06a9\u062a\u06ce\u0628\u0627\u0646\u06d5\u0634 \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0648 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06d5.',
    '\u062c\u0627 \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0645\u06cc\u0631\u0627\u062a \u0628\u06c6 \u0626\u06d5\u0648 \u0639\u06d5\u0628\u062f\u0627\u0646\u06d5\u0645\u0627\u0646 \u06a9\u06d5 \u0647\u06d5\u06b5\u0645\u0627\u0646 \u0628\u0698\u0627\u0631\u062f\u0646. \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634\u062f\u0627 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9 \u0647\u06d5\u0646 \u063a\u06d5\u062f\u0631\u06ce \u0644\u06d5 \u062e\u06c6 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0628\u0695\u06ce\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0645\u0627\u0645 \u0646\u0627\u0648\u0646\u062c\u06cc\u0646. \u0647\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u06cc\u0634 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9 \u0647\u06d5\u0646 - \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u0627 - \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06d5 \u067e\u06ce\u0634\u06d5\u0628\u0695\u06ce \u0644\u06d5 \u0647\u06d5\u0648\u0627\u06b5\u0627\u0646 \u062f\u06d5\u0628\u0695\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u0632\u06ce\u062f\u06d5 \u0628\u06d5\u0634\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0647\u06ce\u0698\u0627\u06cc\u06d5.',
    '\u062f\u06d5\u0686\u0646\u06d5\u0646\u0627\u0648\u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06d5\u0648\u06d5 \u06a9\u06d5 \u0646\u0627\u0641\u06d5\u0648\u062a\u06ce\u0646\u061b \u0644\u06d5\u0648\u06ce \u062f\u06d5\u06cc\u0627\u0646\u0695\u0627\u0632\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0648 \u0628\u0627\u0632\u0648\u0648\u0628\u06d5\u0646\u062f\u06cc\u0627\u0646 \u0644\u06d5 \u0632\u06ce\u0631 \u0648 \u0645\u0631\u0648\u0627\u0631\u06cc \u062f\u06d5\u0628\u06ce \u0648 \u067e\u06c6\u0634\u0627\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u06a4\u0631\u06d5\u0645\u06cc\u0634\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0698\u0646: \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u06a9\u06d5 \u0633\u06d5\u0631\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0646\u06d5\u0647\u06ce\u0634\u062a\u06cc\u0646\u061b \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646 \u0632\u06c6\u0631 \u062e\u06d5\u062a\u0627\u067e\u06c6\u0634 \u0648 \u067e\u06ce \u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u062e\u0648\u062f\u0627\u06cc\u06d5 \u0644\u06d5\u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0626\u06ce\u0645\u06d5\u06cc \u0644\u06d5 \u0645\u0627\u06b5\u06cc \u0647\u06d5\u0631\u0645\u0627\u0646\u062f\u0627 \u0646\u06cc\u0634\u062a\u06d5\u062c\u06ce \u06a9\u0631\u062f. \u0646\u06d5 \u0695\u06d5\u0646\u062c\u06ce \u0644\u06ce\u0631\u0627 \u062f\u06d5\u06a9\u06ce\u0634\u06cc\u0646\u060c \u0646\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0645\u0627\u0646\u062f\u0648\u0648\u06cc\u06cc \u062f\u06d5\u0628\u06cc\u0646.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0626\u0627\u06af\u0631\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0647\u06d5\u06cc\u06d5\u061b \u0646\u06d5 \u0695\u06ce\u06cc \u0645\u0631\u062f\u0646\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u062f\u06d5\u0646\u060c \u0646\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062f\u06c6\u0698\u06d5\u0647\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0633\u0648\u0648\u06a9 \u062f\u06d5\u06a9\u0631\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u0628\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0646\u06d5\u0632\u0627\u0646\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0648\u0627\u06cc\u0627\u0646 \u0633\u0632\u0627 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0628\u06d5 \u0632\u06c6\u0695\u06d5\u0632\u06c6\u0695 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u062f\u06d5\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5: \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0644\u06d5 \u062f\u06c6\u0698\u06d5\u06d5 \u0628\u0645\u0627\u0646\u0647\u06ce\u0646\u06d5\u062f\u06d5\u0631\u061b \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0631\u06cc \u067e\u06ce\u0634\u0648\u0648 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0628\u0648\u0648\u06cc\u0646\u06d5\u0648\u06d5\u061b \u0626\u06d5\u0645 \u062c\u0627\u0631\u06d5 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u062f\u06d5\u06a9\u06d5\u06cc\u0646. \u0626\u06d5\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u062a\u06d5\u0645\u06d5\u0646\u06cc \u0648\u0627 \u062f\u0631\u06ce\u0698\u0645\u0627\u0646 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u062f\u0627 \u06a9\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06cc\u0648\u06cc\u0633\u062a\u0627\u06cc\u06d5\u060c \u062f\u06d5\u06cc\u062a\u0648\u0627\u0646\u06cc \u067e\u06d5\u0646\u062f\u06cc \u0644\u06ce \u0628\u06af\u0631\u06ce\u061f \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06cc\u0634\u062a\u0627\u0646 \u0647\u0627\u062a\u06d5 \u0644\u0627! \u062f\u06d5 \u0626\u06d5\u0645 \u0626\u0627\u0632\u0627\u0631\u06d5 \u0628\u0686\u06ce\u0698\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc \u06a9\u0631\u062f\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u062f\u06d5\u0647\u0627\u0646\u0627 \u0646\u0627\u06cc\u06d5.',
    '\u062e\u0648\u0627 \u0646\u0647\u06ce\u0646\u06cc \u0626\u06d5\u0631\u0632 \u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0626\u06d5\u0632\u0627\u0646\u06ce \u0648 \u0644\u06d5 \u0695\u0627\u0632\u06cc \u062f\u06b5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5 \u062c\u06ce\u06af\u06d5\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 - \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 - \u062c\u06ce \u06a9\u0631\u062f\u06d5\u0648\u06d5. \u0646\u0627\u0633\u0648\u067e\u0627\u0633\u06cc \u0646\u0627\u0633\u067e\u0627\u0633\u0627\u0646\u060c \u0647\u06d5\u0631 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u067e\u062a\u0631 \u062f\u06d5\u06a9\u0627 \u0648 \u0628\u06ce \u0626\u06d5\u0645\u06d5\u06af\u06cc \u0628\u06ce \u0626\u06d5\u0645\u06d5\u06af\u0627\u0646\u060c \u0647\u06d5\u0631 \u0632\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0632\u06ce\u062f\u06d5 \u062f\u06d5\u06a9\u0627.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648\u0627\u0646\u06d5 - \u0644\u06d5 \u062c\u06ce\u06cc \u062e\u0648\u0627 - \u067e\u06ce\u062a\u0627\u0646\u060c \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u0645 \u062f\u06d5\u0646\u061b \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u06a9\u0627\u0645\u06d5\u0634\u062a\u06cc\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u061f \u06cc\u0627\u0646 \u0644\u06d5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0646\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646\u062f\u0627\u060c \u06a9\u06d5\u0646\u06af\u06ce \u0647\u06d5\u0645\u06a9\u0627\u0631\u06cc \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u0646\u061f \u06cc\u0627 \u062e\u0648\u062f \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06a9\u062a\u06ce\u0628\u06cc\u0627\u0646 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5 \u0648 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646\u06d5\u0648\u06d5\u061f \u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u06b5\u06d5\u062a\u0627\u0646\u062f\u0646 \u0628\u06d5 \u06cc\u06d5\u06a9 \u062f\u06d5\u062f\u06d5\u0646.',
    '\u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0646\u06d5\u06cc \u0695\u0627\u06af\u0631\u062a\u0648\u0648\u06d5 \u06a9\u06d5 \u0646\u0627\u06a9\u06d5\u0648\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u06a9\u06d5\u0648\u062a\u0646 - \u0644\u06d5\u0648 \u0628\u06d5\u062f\u06d5\u0631 - \u06a9\u06d5\u0633\u06cc \u0648\u0627 \u0646\u06cc\u06cc\u06d5 \u0695\u0627\u06af\u06cc\u0631\u06cc\u0627\u0646 \u06a9\u0627. \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06cc \u0628\u06d5 \u0634\u06ce\u0646\u06d5\u06cc\u06cc\u0646 \u0648 \u062e\u06d5\u062a\u0627\u067e\u06c6\u0634\u06d5.',
    '\u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc \u0633\u0648\u06ce\u0646\u062f\u06cc\u0627\u0646 \u062f\u06d5\u062e\u0648\u0627\u0631\u062f\u0646\u060c \u06a9\u06d5: \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5\u0645\u0627\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06ce\u06a9 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0628\u06ce\u062a\u06d5 \u0644\u0627\u0645\u0627\u0646\u060c \u067e\u062a\u0631 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06cc \u062a\u0631 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06af\u0631\u06cc\u0646\u06d5\u0628\u06d5\u0631. \u06a9\u06d5\u0686\u06cc \u0648\u06d5\u062e\u062a\u06ce \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06ce\u06a9 \u0647\u0627\u062a\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u0641\u0631\u06d5\u062a\u0631 \u0644\u06d5 \u062e\u0648\u0627 \u062f\u0648\u0648\u0631\u06d5\u0648 \u06a9\u06d5\u0641\u062a\u0646\u061b',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0628\u06d5\u0631 \u062e\u06c6\u0628\u06d5\u0632\u0644 \u0632\u0627\u0646\u06cc\u0646 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u062f\u0627 \u0648 \u0628\u06c6 \u0641\u0695\u0648\u0641\u06ce\u06b5\u061b \u0641\u0695\u0648\u0641\u06ce\u06b5\u06cc\u0634 \u0647\u06d5\u0631 \u06cc\u06d5\u062e\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646\u06cc \u062f\u06d5\u06af\u0631\u06ce. \u0626\u0627\u062e\u06c6 \u0628\u06ce\u0698\u06af\u06d5 \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u060c \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc \u0634\u062a\u06ce\u06a9 \u062a\u0631 \u0647\u06d5\u0646\u061f \u0626\u06d5\u0645\u06d5 \u0695\u06ce\u06af\u06d5\u0648\u0634\u0648\u06ce\u0646\u06cc \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0642\u06d5\u062a \u0646\u0627\u0628\u06cc\u0646\u06cc \u0695\u06ce \u0648 \u0634\u0648\u06ce\u0646\u06cc \u062e\u0648\u0627 \u0628\u06af\u06c6\u0695\u062f\u0631\u06ce \u0648 \u062c\u06ce \u0628\u06d5\u062c\u06ce \u0628\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0646\u06d5\u06af\u06d5\u0695\u0627\u0648\u0646\u060c \u062a\u0627 \u0628\u0628\u06cc\u0646\u0646 \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646 - \u06a9\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u06d5\u0647\u06ce\u0632\u062a\u0631 \u0628\u0648\u0648\u0646 - \u0686\u06cc\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u0627\u062a\u061f \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0644\u06d5 \u0646\u0627\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u0627\u0628\u06ce \u06a9\u06d5 \u0647\u06ce\u0632 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u062a\u06ce\u0646\u06ce\u061b \u0632\u0627\u0646\u0627 \u0648 \u062a\u0648\u0627\u0646\u0627 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5\u06cc - \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646 - \u0628\u06af\u0631\u062a\u0628\u0627\u060c \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u0634\u062a\u06cc \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u062c\u0648\u0642\u06d5\u0648\u0627\u0631\u06ce\u06a9\u06cc\u0634 \u0646\u06d5\u062f\u06d5\u0645\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0645\u06d5\u0648\u062f\u0627\u06cc \u062f\u0627\u0648\u06d5 \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06ce \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648 \u0628\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5\u0646\u06af\u06ce \u0626\u06d5\u0648 \u0645\u0627\u0648\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u0686\u0648\u0648\u060c \u062e\u06c6\u06cc \u062f\u06d5\u0632\u0627\u0646\u06ce \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0686\u06cc\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u06cc\u0627\u060c \u0633\u06cc\u0646.',
    '\u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u0642\u0648\u0695\u0626\u0627\u0646\u060c \u06a9\u06d5 \u0633\u06d5\u0631 \u0644\u06d5\u0628\u06d5\u0631 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u06cc\u06d5.',
    '\u062a\u06c6 \u06cc\u06d5\u06a9 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc.',
    '\u06a9\u06d5\u0648\u062a\u0648\u0648\u06cc\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06af\u06d5.',
    '\u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5 \u0647\u06d5\u0631\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631.',
    '\u062a\u0627 \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0648 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u06af\u06d5\u0644\u06ce\u06a9 \u0628\u06ce\u0646\u06cc \u06a9\u06d5 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u06cc \u067e\u06ce\u0634\u0648\u0648\u06cc\u0627\u0646 \u06a9\u06d5\u0633 \u0644\u06d5 \u062e\u0648\u0627\u06cc \u0646\u06d5\u062a\u0631\u0633\u0627\u0646\u062f\u0648\u0648\u0646 \u0648 \u0628\u06ce \u0626\u0627\u06af\u0627 \u0628\u0648\u0648\u0646.',
    '\u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u0627 \u0632\u06c6\u0631\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06af\u0631\u06ce\u062a\u06d5\u0648\u06d5\u061b \u0633\u0627 \u0626\u06d5\u0648\u0627\u0646 \u0628\u0695\u0648\u0627 \u0646\u0627\u0647\u06ce\u0646\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0645\u0644\u06cc\u0627\u0646 \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u0646\u0627\u0648 \u0632\u0646\u062c\u06cc\u0631\u06d5\u0648\u06d5 \u0648 \u062a\u0627 \u0686\u06d5\u0646\u06d5 \u0634\u06d5\u062a\u06d5\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u060c \u06a9\u06d5 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0628\u06c6\u0647\u06cc\u0686 \u0644\u0627\u06cc\u06d5\u06a9 \u0633\u06d5\u0631\u0628\u0628\u0632\u06ce\u0648\u0646\u061b \u0686\u0627\u0648\u06af\u06d5\u0644\u06cc\u0634\u06cc\u0627\u0646 \u062f\u0627\u0634\u06c6\u0695\u06cc\u0627\u06af\u06d5.',
    '\u0644\u06d5\u0645\u067e\u06d5\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5\u067e\u06ce\u0634\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u062f\u0627\u0646\u0627\u0648\u06d5 \u0648 \u0644\u06d5\u0645\u067e\u06d5\u0631\u06ce\u06a9 \u0644\u06d5 \u067e\u0634\u062a\u06cc\u0627\u0646\u06d5\u0648\u06d5\u061b \u062a\u0627\u0631\u0645\u0627\u06cc\u06cc\u0634\u0645\u0627\u0646 \u0647\u0627\u0648\u06cc\u0634\u062a\u06d5 \u0633\u06d5\u0631 \u06af\u0644\u06ce\u0646\u06d5\u06cc\u0627\u0646 \u06a9\u06d5 \u0646\u0627\u0628\u06cc\u0646\u0646.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0686 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u0647\u06ce\u0646\u06cc\u060c \u0686 \u0646\u06d5\u0647\u06ce\u0646\u06cc\u060c \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u06d5. \u0647\u06d5\u0631 \u0646\u0627\u0628\u0646 \u0628\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695.',
    '\u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u0647\u06ce\u0646\u0627\u0646\u06d5\u06a9\u06d5\u062a \u0647\u06d5\u0631 \u0628\u06c6 \u06a9\u06d5\u0633\u06ce\u06a9 \u0628\u0627\u0648\u06cc \u0647\u06d5\u06cc\u06d5\u060c \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5\u06cc\u06d5 \u0648 \u0644\u06d5 \u0646\u06d5\u062f\u06cc\u062a\u06d5 \u062a\u0631\u0633\u06cc \u062e\u0648\u062f\u0627\u06cc \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5. \u0633\u0627 \u062a\u06c6 \u0645\u0632\u06af\u06ce\u0646\u06cc \u067e\u06ce \u0628\u062f\u06d5 \u0639\u0627\u0641\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce \u0648 \u067e\u0627\u062f\u0627\u0634\u06cc \u0628\u0627\u0634\u06cc\u0634\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u0645\u0631\u06cc\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u0648 \u0634\u0648\u06ce\u0646\u06d5\u0648\u0627\u0631\u06cc\u0627\u0646\u060c \u06af\u0634 \u062f\u06d5\u0646\u0648\u0648\u0633\u06cc\u0646. \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u0645\u0627\u0646 \u0644\u06d5 \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc \u0695\u0648\u0648\u0646 \u06a9\u06d5\u0631\u06d5\u0648\u06d5 - \u0647\u06d5\u0631 \u0628\u06d5 \u0647\u06d5\u0698\u0645\u0627\u0631 - \u0647\u06d5\u0698\u0645\u0627\u0631\u062f\u0648\u0648\u06d5.',
    '\u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u06cc \u062e\u06d5\u06b5\u06a9\u06cc \u0646\u0627\u0648 \u0626\u0627\u0648\u0627\u06cc\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06ce\u0646\u06d5 \u0628\u06c6 \u0628\u06af\u06ce\u0695\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0686\u0648\u0648\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646.',
    '\u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u06cc \u06a9\u06d5 \u062f\u0648\u0648 \u06a9\u06d5\u0633\u0645\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f\u0646\u060c \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0646. \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0633\u06ce\u0647\u06d5\u0645\u06cc\u0634\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u0626\u06d5\u0648 \u062f\u0648\u0648\u0627\u0646\u06d5 \u0628\u062f\u0627. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5\u060c \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0645\u0631\u06c6\u06cc\u06d5\u06a9\u0646 \u0648\u06d5\u06a9 \u062e\u06c6\u0645\u0627\u0646\u061b \u062e\u0648\u0627 \u0647\u06cc\u0686\u06cc \u0646\u06d5\u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u061b \u0626\u06ce\u0648\u06d5 \u06a9\u0627\u0631\u062a\u0627\u0646 \u062f\u0631\u06c6 \u062f\u0627\u0646\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u06cc \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u067e\u06ce\u0631\u0627\u0648\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0645\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06d5\u060c \u067e\u06d5\u06cc\u0627\u0645\u06cc \u0626\u06d5\u0648 - \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 - \u0628\u06d5\u0646\u06af\u06c6 \u0628\u06b5\u06ce\u06cc\u0646\u061b \u0646\u06d5 \u0647\u06cc\u0686\u06cc \u062a\u0631.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u067e\u06ce\u0648\u0642\u06d5\u062f\u06d5\u0645\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06c6 \u0645\u06d5 \u0634\u0648\u0648\u0645\u06d5\u061b \u062f\u06d5\u0633 \u0647\u06d5\u06b5 \u0646\u06d5\u06af\u0631\u0646\u060c \u0628\u06d5\u0631\u062f\u06d5\u0628\u0627\u0631\u0627\u0646\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u0633\u06d5\u062e\u062a \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062a\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0634\u0648\u0645\u06cc\u0648 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646\u06d5\u060c \u0626\u0627\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0628\u06a9\u0631\u06ce\u0646 [\u0626\u0627\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646]\u061f \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06ce\u0648\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u0646 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u062e\u06c6 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0648\u0648\u0646.',
    '\u067e\u06cc\u0627\u06af\u06ce\u06a9 \u0644\u06d5\u0648 \u0633\u06d5\u0631\u06cc \u0634\u0627\u0631\u06d5\u0648\u06d5 \u0628\u06d5 \u063a\u0627\u0631 \u062f\u06d5\u0647\u0627\u062a\u060c \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0628\u062f\u0646\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0695\u0627\u0633\u067e\u06ce\u0631\u0627\u0648\u0646.',
    '\u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06a9\u06d5\u0646 \u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646 \u0648 \u0647\u06cc\u0686 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u0644\u06ce\u062a\u0627\u0646 \u0646\u0627\u0648\u06ce.',
    '\u0645\u0646 \u0628\u06c6 \u062f\u06d5\u0628\u06ce \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0645\u0646\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u060c \u0646\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u0645\u061b \u06af\u0634\u062a\u0648\u0648 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u0686\u0646\u06d5\u0648\u06d5\u061f',
    '\u0686\u06c6\u0646 \u0645\u0646 \u062f\u06d5\u0686\u0645 - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0645\u061b \u0647\u06d5\u0631\u06af\u0627 \u062e\u0648\u062f\u0627 \u06af\u06d5\u0631\u06d5\u06a9\u06cc \u0628\u06ce \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u0645 \u0628\u06d5\u0633\u06d5\u0631 \u0628\u06ce\u0646\u06ce\u060c \u062a\u06a9\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u0645 \u0628\u06c6 \u0646\u0627\u062f\u0627 \u0648 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0695\u0632\u06af\u0627\u0631\u0645 \u0628\u06a9\u06d5\u0646\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0648\u0627 \u0628\u06ce\u060c \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0645\u0646 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u06af\u0648\u0645\u0695\u0627 \u0628\u0648\u0648\u06af\u0645.',
    '\u0645\u0646 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u0645 \u0647\u06ce\u0646\u0627\u061b \u0633\u0627 \u0628\u0698\u0646\u06d5\u0648\u0646.',
    '\u0648\u062a\u0631\u0627: \u0648\u06d5\u0631\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u0648\u06d5. \u06af\u0648\u062a\u06cc: \u0628\u0631\u06cc\u0627 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u0634\u0645 \u0628\u06cc\u0632\u0627\u0646\u06cc\u0628\u0627\u060c',
    '\u06a9\u06d5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0645\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u062f\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0644\u06ce\u0645 \u062e\u06c6\u0634 \u0628\u0648\u0648\u06d5 \u0648 \u062e\u0633\u062a\u0648\u0648\u0645\u06cc\u06d5 \u0695\u06ce\u0632\u06cc \u0628\u06d5\u0695\u06ce\u0632\u0627\u0646.',
    '\u062f\u0648\u0627\u06cc \u0626\u06d5\u0648 \u0626\u06ce\u0645\u06d5 \u0647\u06cc\u0686 \u0644\u06d5\u0634\u06a9\u0631\u0645\u0627\u0646 - \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0695\u0627 - \u0646\u06d5\u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648\u060c [\u0628\u06d5\u0631\u0644\u06d5\u0645\u06d5\u0634] \u0647\u06d5\u0631 \u0646\u06d5\u0645\u0627\u0646 \u062f\u06d5\u0646\u0627\u0631\u062f.',
    '\u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u0646\u06d5\u0639\u0631\u06d5\u062a\u06d5\u06cc\u06d5\u06a9 \u0628\u0648\u0648\u060c \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u06a9\u0627\u0648 \u06af\u0634 \u062f\u0627\u0645\u0631\u06a9\u0627\u0646.',
    '\u0645\u062e\u0627\u0628\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u0639\u06d5\u0628\u062f\u0627\u0646\u06d5\u060c \u0647\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u060c \u0647\u06d5\u0631 \u06af\u0627\u06b5\u062a\u06d5\u0648\u06af\u06d5\u067e\u06cc\u0627\u0646 \u067e\u06ce\u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u0686\u06c6\u0646 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06d5\u06cc\u0627\u0646\u062f\u06cc\u0648\u0648\u06d5 \u06a9\u06d5 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0686\u06cc\u0646 \u0644\u06d5 \u0645\u0631\u06c6\u0645\u0627\u0646 - \u0644\u06d5 \u067e\u06ce\u0634 \u0626\u06d5\u0645\u0627\u0646 - \u0642\u0695 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u062a\u0627\u0632\u06d5 \u0646\u0627\u06cc\u06d5\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc\u0627\u0646.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u062f\u06d5\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u0645\u0627\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0632\u06d5\u0648\u06cc \u0695\u0627\u0628\u0645\u06ce\u0646\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u062a\u06ce\u062f\u0627\u061b \u0628\u06d5 \u0645\u0631\u062f\u0648\u0648\u06cc\u06cc \u0626\u06ce\u0645\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0646\u06a9\u0645\u0627\u0646 \u0644\u06ce \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u061b \u06a9\u06d5 \u062e\u06c6\u0631\u0627\u06a9\u06d5 \u0628\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0632\u06d5\u0648\u06cc\u0634 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0628\u0627\u063a\u06cc \u062e\u0648\u0631\u0645\u0627\u0628\u0646 \u0648 \u0695\u06d5\u0632\u06cc \u0632\u06c6\u0631\u0645\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u0686\u06d5\u0646\u062f \u06a9\u0627\u0646\u06cc\u0627\u0648\u0645\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06b5\u0642\u0648\u06b5\u0627\u0646\u062f.',
    '\u0647\u06d5\u062a\u0627\u06a9\u0648\u0648 \u0644\u06d5 \u0645\u06cc\u0648\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0644\u06d5 \u062f\u06d5\u0633\u0695\u06d5\u0646\u062c\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u062e\u06c6\u0646. \u0626\u0627\u062e\u06c6 \u0647\u06ce\u0634\u062a\u0627 \u0634\u0648\u06a9\u0631 \u0646\u0627\u06a9\u06d5\u0646\u061f',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u067e\u0627\u06a9 \u0648 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06d5\u060c \u06a9\u06d5 \u062c\u0648\u0648\u062a\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u061b \u0686 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 \u062f\u06d5\u0695\u0648\u06ce\u0646 \u0648 \u0686 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646\u060c \u0686 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0646\u0627\u06cc\u0632\u0627\u0646\u0646.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u060c \u0634\u06d5\u0648\u06cc\u0634 \u06cc\u06d5\u06a9 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06d5\u061b \u0626\u06ce\u0645\u06d5 \u0695\u06c6\u0698\u06cc \u0644\u06ce \u062f\u0627\u062f\u06d5\u0695\u0646\u06cc\u0646 \u0648 \u0644\u06d5 \u0646\u0627\u06a9\u0627\u0648 \u062a\u0627\u0631\u06cc\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u06ce.',
    '\u062e\u06c6\u0631\u06cc\u0634 \u0628\u06d5\u0631\u06d5\u0648 \u0628\u0646\u06d5\u06cc \u062e\u06c6\u06cc \u0644\u06d5 \u06af\u06d5\u0695\u0627\u0646\u062f\u0627\u06cc\u06d5\u061b \u06a9\u06d5 \u0644\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0632\u0627\u0646\u0627\u0648\u06d5\u06cc\u06d5\u060c \u0626\u06d5\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5\u06cc \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5.',
    '\u0647\u06d5\u06cc\u06a4\u06cc\u0634 \u0626\u06ce\u0645\u06d5 \u0628\u06c6\u0645\u0627\u0646 \u062f\u0627\u0646\u0627 \u0628\u06d5 \u0686\u06d5\u0646\u062f \u0642\u06c6\u0646\u0627\u063a\u0627 \u062a\u06ce\u067e\u06d5\u0695\u06ce\u061b \u062a\u0627 \u0648\u0627\u06cc \u0644\u06ce \u062f\u06ce\u060c \u0648\u06d5\u06a9 \u067e\u06d5\u0644\u06cc \u06a9\u06d5\u0648\u0646\u06cc \u0648\u0634\u06a9\u06d5\u06b5\u06cc \u062f\u0627\u0631\u06cc \u062e\u0648\u0631\u0645\u0627\u060c \u062f\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648.',
    '\u0646\u06d5 \u062e\u06c6\u0631 \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06ce\u0646\u062f\u06d5\u06cc \u0647\u06d5\u06cc\u06d5 \u0628\u06af\u0627 \u0628\u06d5 \u0645\u0627\u0646\u06af\u060c \u0646\u06d5 \u0634\u06d5\u0648 \u0648\u06d5\u067e\u06ce\u0634 \u0695\u06c6\u0698 \u062f\u06d5\u06a9\u06d5\u0648\u06ce. \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631\u06cc\u0634 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06ce\u06a9 \u0645\u06d5\u0644\u06d5 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0645\u06d5\u0634 \u0628\u06c6\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5: \u0647\u0627\u0648\u0695\u06d5\u06af\u06d5\u0632\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5 \u06af\u06d5\u0645\u06cc\u06d5\u06cc \u062a\u0698\u06cc \u06a9\u0631\u0627\u0648\u062f\u0627 \u0647\u06d5\u06b5\u06af\u0631\u062a\u0648\u0648\u06d5.',
    '\u0648\u06ce\u0646\u06d5\u06cc \u0626\u06d5\u0648\u06cc\u0634\u0645\u0627\u0646 \u062f\u0627\u0647\u06ce\u0646\u0627\u060c \u06a9\u06d5 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0633\u0648\u0627\u0631\u06cc \u0628\u0628\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632 \u06a9\u06d5\u06cc\u0646 \u0646\u0648\u0642\u0645\u06cc \u0628\u0646 \u0626\u0627\u0648\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0647\u06cc\u0686 \u0641\u0631\u06cc\u0627\u0695\u06d5\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u0627\u0628\u06ce\u062a \u0648 \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u06a9\u0631\u06ce\u0646.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0641\u0631\u06cc\u0627 \u0628\u06ce \u0648 \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0628\u06d5\u0631\u062e\u0648\u0631\u062f\u0627\u0631 \u0628\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u067e\u06ce\u06cc\u0627\u0646 \u0628\u06af\u0648\u062a\u0631\u06ce: \u0644\u06d5\u0648\u06cc \u0644\u06d5 \u0695\u0648\u0648\u0628\u06d5\u0695\u0648\u0648\u062a\u0627\u0646\u06d5 \u0648 \u0644\u06d5\u0648\u06d5\u0634 \u0644\u06d5 \u067e\u0634\u062a\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646\u06d5\u060c \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0628\u0646\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u0627 \u06a9\u06d5\u0648\u0646\u060c [\u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646].',
    '\u0647\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06ce \u0644\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646\u06d5\u0648\u06d5\u060c \u0626\u06cc\u0644\u0644\u0627 \u062f\u06d5\u0628\u06ce \u0695\u0648\u0648\u06cc \u062a\u06ce \u0646\u06d5\u06a9\u06d5\u0646.',
    '\u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u067e\u06ce\u06cc\u0627\u0646 \u0628\u0648\u0648\u062a\u0631\u06ce: \u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u06cc \u062e\u0648\u0627 \u067e\u06ce\u062f\u0627\u0648\u0646\u060c \u062e\u06ce\u0631\u06ce \u0628\u06a9\u06d5\u0646\u061b \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u06d5\u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0631\u0627\u06a9 \u0628\u06d5 \u06a9\u06d5\u0633\u06ce \u0628\u0628\u06d5\u062e\u0634\u06cc\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627 \u0628\u0698\u06cc\u0648\u06cc \u0628\u06c6 \u062f\u0627\u0628\u06cc\u0646 \u062f\u06d5\u06a9\u0631\u062f\u061f \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u06af\u0648\u0645\u0695\u0627\u0647\u06cc \u062f\u0627\u0646.',
    '\u062f\u06d5\u0634\u06b5\u06ce\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0642\u0633\u06d5\u0695\u0627\u0633\u062a\u0646\u060c \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u060c \u06a9\u06d5\u06cc \u0628\u06d5 \u062c\u06ce \u062f\u06ce\u061f',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc \u06cc\u06d5\u06a9 \u0646\u06d5\u0639\u0631\u06d5\u062a\u06d5\u0646 \u06a9\u06d5 \u0644\u06d5 \u06a9\u0627\u062a\u06ce\u06a9\u062f\u0627 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u0647\u06d5\u0631\u0627\u0648\u06a9\u06ce\u0634\u06d5\u06cc \u0646\u0627\u0648 \u062e\u06c6\u06cc\u0627\u0646\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u06af\u0631\u06ce.',
    '\u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0648\u06d5\u0633\u06cc\u06d5\u062a\u06cc\u0634 \u0628\u06a9\u06d5\u0646\u061b \u0695\u06ce\u0634\u06cc\u0627\u0646 \u0646\u0627\u0628\u06ce \u0628\u0686\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0648\u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06cc\u0627\u0646.',
    '\u0641\u0648\u0648\u0634 \u0628\u06d5 \u06a9\u06d5\u06b5\u06d5\u0634\u0627\u062e\u062f\u0627 \u062f\u06d5\u06a9\u0631\u06ce\u061b \u0646\u0627\u06af\u0627 \u06af\u0634\u062a \u0644\u06d5 \u06af\u06c6\u0695\u0627\u0646\u06d5\u0648\u06d5 \u062f\u06ce\u0646\u06d5 \u062f\u06d5\u0631 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0644\u06d5\u0632 \u062f\u06d5\u0695\u06c6\u0646.',
    '\u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u0647\u0627\u0648\u0627\u0631 \u0628\u06d5 \u0645\u0627\u06b5\u0645\u0627\u0646! \u06a9\u06ce \u0644\u06d5 \u0646\u0627\u0648 \u067e\u06ce\u062e\u06d5\u0641\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u0695\u0627\u06cc \u067e\u06d5\u0695\u0627\u0646\u062f\u06cc\u0646\u061f \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u06cc\u06d5 \u062e\u0648\u062f\u0627 \u062f\u0627\u06cc \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0695\u0627\u0633\u062a\u06cc\u0627\u0646 \u06af\u0648\u062a.',
    '\u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9 \u0646\u06d5\u0639\u0631\u06d5\u062a\u06d5\u06cc\u06d5 \u0648 \u0646\u0627\u06a9\u0627\u0648 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u06a9\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u06a9\u06d5\u0633 \u0646\u0627\u06a9\u0631\u06ce \u0648 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u062a\u0627\u0646\u06d5\u060c \u0633\u0632\u0627 \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a\u0646\u060c \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0633\u06d5\u0631\u06cc\u0627\u0646 \u06af\u06d5\u0631\u0645\u06d5 \u0648 \u0644\u06d5 \u062e\u06c6\u0634\u06cc \u062f\u0627\u0646.',
    '\u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u060c \u0644\u06d5\u0628\u06d5\u0631 \u0633\u06ce\u0628\u06d5\u0631\u060c \u0644\u06d5 \u062a\u06d5\u062e\u062a\u0627\u0646\u060c \u067e\u0627\u06b5 \u06a9\u06d5\u0641\u062a\u06af\u0646.',
    '\u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u0627 \u0647\u06d5\u0631 \u0645\u06cc\u0648\u06d5\u06cc\u06d5\u06a9 \u0648 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u062f\u0627\u0648\u0627\u06cc \u0628\u06a9\u06d5\u0646\u060c \u0626\u0627\u0645\u0627\u062f\u06d5\u06cc\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc\u0634 - \u0628\u06d5 \u0644\u06d5\u0628\u0632\u06cc \u062e\u06c6\u06cc - \u0633\u06b5\u0627\u0648\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0646\u06ce\u0631\u06ce.',
    '\u0626\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0646! \u0626\u06ce\u0648\u06d5\u0634 \u0626\u06d5\u0645\u0695\u06c6\u060c \u0626\u06d5\u0634\u06ce \u062e\u06c6\u062a\u0627\u0646 \u062c\u06cc\u0627 \u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u0631 \u0626\u06d5\u06cc \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u0627\u062f\u06d5\u0645! \u0645\u0646 \u0644\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u067e\u06d5\u06cc\u0645\u0627\u0646\u0645 \u062f\u06d5\u06af\u06d5\u06b5 \u0646\u06d5\u0628\u06d5\u0633\u062a\u0646 \u06a9\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06ce \u0646\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u061f \u0626\u06d5\u0648 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u0648\u0698\u0645\u0646\u062a\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u062a\u06d5\u0646\u06cc\u0627 \u062e\u06c6\u0645 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u06a9\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5.',
    '\u06af\u06d5\u0644\u06ce\u06a9\u06cc \u0632\u06c6\u0631\u06cc \u0644\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u0627\u061b \u0626\u0627\u062e\u06c6 \u0647\u06ce\u0634\u062a\u0627 \u0647\u06d5\u0631 \u062a\u06ce \u0646\u0627\u06af\u06d5\u0646\u061f',
    '\u0626\u06d5\u0645\u06d5 \u0626\u06d5\u0645 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06d5\u06cc\u06d5\u060c \u06a9\u06d5\u0648\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u062a\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u0631\u0627.',
    '\u0628\u06d5\u0647\u06c6\u06cc \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u06cc\u06d5\u06a9\u06d5\u062a\u0627\u0646\u060c \u0626\u06d5\u0645\u0695\u06c6 \u062f\u06d5\u0628\u06ce \u0628\u0686\u0646\u06d5 \u0646\u0627\u0648\u06cc.',
    '\u0626\u06d5\u0645\u0695\u06c6 \u0626\u06ce\u0645\u06d5 \u0645\u06c6\u0631 \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u0633\u06d5\u0631 \u062f\u06d5\u0645\u06cc\u0627\u0646 \u0648 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5\u0645\u0627\u0646 \u062f\u06d5\u067e\u06d5\u06cc\u0648\u0646\u061b \u067e\u0627\u06a9\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0646\u06d5 \u0634\u0627\u06cc\u06d5\u062a \u06a9\u06d5 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0686 \u0628\u0648\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632 \u06a9\u06d5\u06cc\u0646\u060c \u0686\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u06a9\u0648\u06ce\u0631 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u061b \u0628\u06d5\u0631\u06d5\u0648 \u0695\u06ce\u06af\u0627 \u0645\u0644\u06d5 \u062f\u06d5\u06a9\u06d5\u0646. \u0633\u0627 \u06a9\u0648\u0627 \u0633\u06c6\u0645\u0627\u06cc \u062f\u06cc\u062a\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u0628\u0627 - \u0647\u06d5\u0631 \u0644\u06d5 \u062c\u06ce\u06af\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 - \u062f\u06d5\u0645\u0627\u0646\u06a9\u0631\u062f\u0646\u06d5 \u0634\u062a\u06ce\u06a9\u06cc \u062a\u0631\u061b \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0646\u06d5\u06cc\u0627\u0646\u062a\u0648\u0627\u0646\u06cc\u0628\u0627 \u0628\u06ce\u0646\u06d5 \u067e\u06ce\u0634 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u062f\u0648\u0648\u0627\u0634 \u0646\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062a\u06d5\u0645\u06d5\u0646\u06cc \u0632\u06c6\u0631\u06cc \u0628\u062f\u06d5\u06cc\u0646\u06ce\u060c \u062a\u06d5\u0631\u062d \u0648 \u0628\u06cc\u0686\u0645\u06cc\u0634\u06cc \u062f\u06d5\u06af\u06c6\u0695\u06cc\u0646. \u0628\u06c6\u0686 \u0646\u0627\u062a\u0627\u0646\u06d5\u0648\u06ce \u062a\u06ce \u0628\u06af\u06d5\u0646\u061f',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0645\u0627\u0646 \u0641\u06ce\u0631\u06cc \u0634\u06ce\u0639\u0631 \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0647\u06cc\u0686 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06cc\u0634\u06cc \u067e\u06ce \u0646\u06cc\u06cc\u06d5. \u0626\u06d5\u0645\u06d5 \u062a\u06d5\u0646\u06cc\u0627 \u0628\u06c6 \u06cc\u0627\u062f \u0644\u06d5 \u062e\u0648\u062f\u0627 \u06a9\u0631\u062f\u0646\u06d5 \u0648 \u0642\u0648\u0631\u0626\u0627\u0646\u06ce\u06a9\u06d5 \u0634\u06ce\u0648\u06d5\u0698\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u062a\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u062f\u06b5 \u0632\u06cc\u0646\u062f\u0648\u0648\u0646\u060c \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0628\u062a\u0631\u0633\u06ce\u0646\u06cc \u0648 \u0642\u0633\u06d5\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u06d5 \u062c\u06ce \u0628\u06af\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0647\u06ce\u0634\u062a\u0627 \u0646\u06d5\u06cc\u0627\u0646\u062f\u06cc\u0648\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u062a\u0648\u0627\u0646\u0627\u0645\u0627\u0646 \u067e\u0627\u062a\u0627\u06b5\u0645\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u0646\u060c \u0626\u06d5\u0648\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5\u062a\u06d5 \u062e\u0627\u0648\u06d5\u0646\u06cc\u0627\u0646.',
    '\u0648\u0627 \u0628\u06c6\u0634\u0645\u0627\u0646 \u062f\u06d5\u0633\u062a\u06d5\u0645\u06c6 \u06a9\u0631\u062f\u0646\u060c \u06a9\u06d5 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0633\u0648\u0627\u0631\u06cc\u0627\u0646 \u0628\u0646 \u0648 \u0644\u06ce\u06cc\u0627\u0646 \u0628\u062e\u06c6\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5 \u0648\u0627\u0646\u06cc\u0634 \u06af\u06d5\u0644\u06ce\u06a9 \u062c\u06c6\u0631\u06d5 \u0628\u06d5\u0647\u0631\u06d5 \u062f\u06d5\u0628\u06d5\u0646\u061b \u0632\u06c6\u0631 \u0634\u062a\u06cc \u06a4\u06d5\u062e\u0627\u0631\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u0646. \u0647\u06ce\u0634\u062a\u0627 \u0647\u06d5\u0631 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0646\u0627\u06a9\u06d5\u0646.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0634\u062a\u06af\u06d5\u0644\u06ce\u06a9\u06cc \u062a\u0631\u06cc\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a\u061b \u0634\u0627\u06cc\u06d5\u062a\u0627 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0628\u06ce\u0646.',
    '\u0646\u0627\u0634\u062a\u0648\u0627\u0646\u0646 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0628\u06ce\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u062e\u0632\u0645\u06d5\u062a\u06cc\u0627\u0646\u060c \u0628\u06c6 \u067e\u06ce\u0634\u0645\u06d5\u0631\u06af\u06d5\u06cc\u06cc \u0626\u0627\u0645\u0627\u062f\u06d5\u0646.',
    '\u0648\u062a\u0627\u0631\u06cc\u0627\u0646 \u062f\u06b5\u062a\u06d5\u0646\u06af\u062a \u0646\u06d5\u06a9\u0627\u061b \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0628\u06cc\u0634\u06ce\u0631\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631\u0634\u062a\u06ce\u06a9\u06cc \u0628\u06cc\u062f\u0631\u06a9\u06ce\u0646\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06ce\u06cc\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0646.',
    '\u0645\u0631\u06c6 \u0686\u06c6\u0646 \u0647\u06ce\u0646\u062f\u06d5 \u0646\u0627\u0632\u0627\u0646\u06ce\u060c \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u062a\u0646\u06c6\u06a9\u06ce\u06a9\u0645\u0627\u0646 \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0648\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06ce\u0633\u062a\u0627 \u062f\u0648\u0698\u0645\u0646\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u0645\u06d5\u0633\u06d5\u0644\u06ce\u06a9\u06cc \u0628\u06c6 \u0626\u06ce\u0645\u06d5 \u0647\u06ce\u0646\u0627\u06cc\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0646\u06cc \u062e\u06c6\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u06a9\u0631\u062f\u061b \u0626\u06ce\u0698\u06ce: \u0626\u06ce\u0633\u06a9 \u0644\u06d5 \u067e\u0627\u0634 \u067e\u0648\u0648\u0627\u0646\u060c \u06a9\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061f',
    '\u0628\u06ce\u0698\u06d5: \u06a9\u06d5\u0633\u06ce \u0647\u06d5\u0648\u06d5\u06b5 \u062c\u0627\u0631 \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646\u060c \u0626\u06d5\u0648 \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061b \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062f\u0631\u0648\u0633\u062a \u06a9\u0631\u062f\u0646\u06ce\u06a9 \u062e\u06c6\u06cc \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u0644\u06d5 \u062f\u0627\u0631\u06cc \u0634\u06cc\u0646\u060c \u0626\u0627\u06af\u0631\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u0626\u06ce\u0648\u06d5\u0634 \u0647\u06d5\u0631 \u0644\u06d5\u0645 [\u062f\u06d5\u0633\u062a\u06d5\u0686\u06cc\u0644\u06d5] \u0626\u0627\u0631 \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06d5\u0628\u06ce \u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u062f\u0627\u0647\u06ce\u0646\u0627 \u0628\u06ce\u060c \u062a\u0648\u0627\u0646\u0627\u06cc \u0646\u06cc\u06cc\u06d5 \u0648\u06ce\u0646\u06d5\u06cc\u0627\u0646 \u062f\u0631\u0648\u0633 \u06a9\u0627\u062a\u06d5\u0648\u06d5\u061f \u0626\u0627\u0631\u06ce \u0644\u06d5 \u0648\u0632\u06d5\u06cc\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u06cc \u0641\u0631\u06d5\u0632\u0627\u0646\u0627 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0628\u06c6\u06cc \u067e\u06d5\u06cc\u062f\u0627 \u0628\u0628\u06ce\u060c \u0628\u06d5\u0641\u06d5\u0631\u0645\u0627\u0646 \u067e\u06ce\u06cc \u0626\u06ce\u0698\u06ce: \u0628\u0628\u06d5! \u0647\u06d5\u0631 \u062f\u06d5\u0633 \u0628\u06d5\u062c\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u0628\u06ce.',
    '\u067e\u0627\u06a9\u06cc \u0648 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u0628\u06c6 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0644\u06d5 \u0698\u06ce\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u062f\u0627\u06cc\u06d5 \u0648\u060c \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u0648\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5.',
    '\u0633\u0648\u06ce\u0646\u062f \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06c6 \u0695\u06ce\u0632\u0628\u06d5\u0633\u062a\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u06cc\u06d5\u06a9\u062a\u0631 \u0695\u0627\u062f\u06d5\u0648\u06d5\u0633\u062a\u0646\u060c',
    '\u0633\u0648\u06ce\u0646\u062f \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u067e\u06ce\u0634\u06cc \u062f\u06d5\u06af\u0631\u0646\u060c',
    '\u0633\u0648\u06ce\u0646\u062f \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0642\u0648\u0695\u0639\u0627\u0646 \u062e\u0648\u06ce\u0646\u0646\u060c',
    '\u062e\u0648\u062f\u0627\u06cc \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce\u06a9\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0695\u06c6\u0698\u06b5\u0627\u062a\u0627\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06cc \u0646\u0632\u06cc\u06a9\u0645\u0627\u0646 \u0628\u06d5 \u0626\u06d5\u0633\u062a\u06ce\u0631\u0627\u0646 \u0695\u0627\u0632\u0627\u0646\u062f\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u06af\u0634 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06ce \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u067e\u0627\u0631\u0627\u0633\u062a.',
    '[\u0626\u06cc\u062a\u0631] \u0644\u06d5 \u0628\u0627\u06b5\u0627\u0646\u0634\u06cc\u0646\u0627\u0646 \u06a9\u06c6\u06b5\u06d5 \u0646\u0627\u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u06af\u0634 \u0644\u0627\u0648\u06d5 \u0695\u0627\u0648 \u062f\u06d5\u0646\u0631\u06ce\u0646.',
    '\u0647\u06d5\u062a\u0627 \u062f\u0648\u0648\u0631\u06cc\u0627\u0646 \u0628\u062e\u06d5\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0645\u06cc\u0634\u06d5\u0634 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06d5\u06cc - \u0647\u06d5\u0631 \u0644\u06d5 \u067e\u0695 - \u0634\u062a\u06ce \u0628\u0695\u0641\u06ce\u0646\u06ce\u061b \u0626\u06d5\u0648\u06cc\u0634 \u0628\u06b5\u06ce\u0633\u06d5\u06cc \u06af\u0695\u06ce\u06a9\u06cc \u0634\u0648\u06ce\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u06ce.',
    '\u0633\u0627 \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u067e\u0631\u0633\u06d5: \u0626\u0627\u062e\u06c6 \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646 \u06af\u0631\u06cc\u0646\u06af\u062a\u0631\u06d5\u060c \u06cc\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u0648\u06d5\u062f\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u0646\u061f \u0626\u06ce\u0645\u06d5 \u0645\u0627\u06a9\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5 \u0642\u0648\u0695\u06ce\u06a9\u06cc \u067e\u0646\u062f\u0631\u06cc \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0644\u06a9\u0627\u0648 \u062f\u0627\u0695\u0698\u062a\u0648\u0648\u06d5.',
    '\u062a\u06c6 \u0644\u06d5\u0648\u06d5\u062f\u0627 \u0633\u06d5\u06cc\u0631 \u0645\u0627\u0648\u06cc \u0648 \u0626\u06d5\u0648\u0627\u0646 \u062d\u06d5\u0646\u06d5\u06a9\u06ce \u062e\u06c6 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0646\u06d5\u0633\u06cc\u062d\u06d5\u062a\u06cc\u0634 \u0628\u06a9\u0631\u06ce\u0646\u060c \u0646\u06d5\u0633\u06cc\u062d\u06d5\u062a \u062f\u06d5\u06af\u0648\u06ce \u0646\u0627\u06af\u0631\u0646.',
    '\u0647\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9 \u062f\u06d5\u0628\u06cc\u0646\u0646\u060c \u062f\u06d5\u062e\u0648\u0627\u0632\u0646 \u06af\u06d5\u0645\u06d5\u06cc \u067e\u06ce \u0628\u062f\u0631\u06ce.',
    '\u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u062c\u0627\u062f\u0648\u0648\u06d5 \u0648 \u0647\u06cc\u0686\u06cc \u062a\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5 - \u06a9\u06d5 \u0645\u0631\u062f\u06cc\u0646 \u0648 \u0628\u0648\u0648\u06cc\u0646\u06d5 \u062e\u06c6\u06b5 \u0648 \u0686\u06d5\u0646\u062f \u067e\u06ce\u0634\u06d5\u06cc\u06d5\u06a9 - \u0698\u0646\u0648\u0648 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u06cc\u0646\u06d5\u0648\u06d5\u061f',
    '\u0648\u06ce\u0695\u0627\u06cc \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u0627\u0646\u0645\u0627\u0646\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u0631\u06ce \u0648\u060c \u0626\u06ce\u0648\u06d5 \u0645\u0644\u06a9\u06d5\u0686 \u0648 \u0632\u06d5\u0628\u0648\u0648\u0646\u0646.',
    '\u062a\u06d5\u0646\u06cc\u0627 \u062a\u06ce\u0695\u0627 \u062e\u0648\u0695\u06cc\u0646\u06ce\u06a9\u06d5 \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0695\u0648\u0627\u0646\u0646.',
    '\u0626\u06ce\u0698\u0646: \u0647\u06d5\u06cc \u0645\u0627\u06b5 \u0648\u06ce\u0631\u0627\u0646 \u062e\u06c6\u0645\u0627\u0646! \u0626\u06d5\u0645\u06d5 \u0695\u06c6\u0698\u06d5\u06a9\u06d5\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u06d5.',
    '\u0628\u06d5\u06b5\u06ce\u060c \u0626\u06d5\u0645\u06d5 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc\u06d5 \u06a9\u06d5 \u067e\u0627\u06a9 \u0648 \u067e\u06cc\u0633 \u0647\u06d5\u06b5\u062f\u0627\u0648\u06ce\u0631\u0646 \u0648 \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06ce\u06a9 \u06a9\u06c6 \u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc \u067e\u06cc\u0634\u06d5 \u0628\u0648\u0648\u0646 \u0648 \u0644\u0641\u06cc \u0648\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u060c',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627. \u0626\u06d5\u0648\u0633\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u0646!',
    '\u0695\u0627\u0634\u06cc\u0627\u0646 \u0628\u06af\u0631\u0646! \u062f\u06d5\u0628\u06ce \u067e\u0631\u0633\u06cc\u0627\u0631\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06a9\u0631\u06ce.',
    '\u0686\u06cc\u062a\u0627\u0646\u06d5\u060c \u0628\u06c6\u0686\u06cc \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc \u06cc\u06d5\u06a9\u062a\u0631 \u0646\u0627\u06cc\u06d5\u0646\u061f!',
    '\u0626\u06d5\u0645\u0695\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06ce\u062f\u06d5\u0646\u06af \u0648 \u0645\u0644\u06a9\u06d5\u0686 \u0648\u06ce\u0633\u062a\u0627\u0648\u0646.',
    '\u0647\u06ce\u0646\u062f\u06ce \u0644\u06d5\u0648\u0627\u0646 \u0695\u0648\u0648 \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0644\u06ce\u06a9 \u062f\u06d5\u067e\u0631\u0633\u0646.',
    '\u0626\u06ce\u0698\u0646: \u0628\u06d5 \u0646\u06ce\u0648\u06cc \u062e\u06ce\u0631\u062e\u0648\u0627\u0632\u06cc\u060c \u0626\u06ce\u0645\u06d5\u062a\u0627\u0646 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0644\u0627\u062f\u0627.',
    '\u0626\u06ce\u0698\u0646: \u0646\u06d5\u062e\u06ce\u0631\u060c \u0647\u06d5\u0631 \u062e\u06c6\u062a\u0627\u0646 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648.',
    '\u0632\u06c6\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u06d5\u060c \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u062e\u06c6\u062a\u0627\u0646 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u0633\u06d5\u0631\u0628\u0632\u06ce\u0648 \u0628\u0648\u0648\u0646.',
    '\u0648\u0627 \u06af\u0641\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0648\u06d5\u0695\u0627\u0633\u062a \u06af\u06d5\u0695\u0627\u061b \u062f\u06cc\u0627\u0631\u06d5 \u062f\u06d5\u0628\u06ce \u0628\u06cc\u0634\u06cc \u0686\u06ce\u0698\u06cc\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u06af\u0648\u0645\u0695\u0627 \u06a9\u0631\u062f\u061b \u062e\u06c6\u0634\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0633\u0627 \u06af\u0648\u0645\u0695\u0627 \u0628\u0648\u0648\u06cc\u0646.',
    '\u06af\u0634 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u2013 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u2013 \u0628\u06c6 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062f\u0627\u0646 \u0647\u0627\u0648\u0628\u06d5\u0634\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u062c\u0632\u06cc\u0627\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0648\u062a\u0631\u0627: \u063a\u06d5\u06cc\u0631\u06d5\u0632\u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce\u060c \u062e\u06c6\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u0628\u06d5 \u0632\u0644 \u062f\u06d5\u0632\u0627\u0646\u06cc.',
    '\u062f\u06d5\u0634\u06cc\u0627\u0646 \u062f\u0631\u06a9\u0627\u0646\u062f: \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0634\u0627\u0639\u06cc\u0631\u06ce\u06a9\u06cc \u0634\u06ce\u062a \u0686\u06c6\u0646 \u0648\u0627\u0632\u062f\u06ce\u0646\u06cc\u0646 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u0645\u0627\u0646\u0646\u061f!',
    '\u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648 \u0647\u0627\u0648\u0631\u062f\u06cc\u060c \u0647\u06d5\u0645\u0648\u0648\u06cc \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0628\u0695\u0648\u0627\u0634\u06cc \u06a9\u0631\u062f \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u062e\u0648\u062f\u0627.',
    '\u0626\u06ce\u0648\u06d5 \u0628\u06ce \u0634\u06a9 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646 \u062f\u06d5\u0686\u06ce\u0698\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0628\u0627\u062a\u06cc \u06a9\u0627\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0633\u0632\u0627 \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0639\u06d5\u0628\u062f\u06d5 \u0695\u0627\u0633\u062a \u0648 \u067e\u0627\u06a9\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u061b',
    '\u0628\u0698\u06cc\u0648\u06cc \u0628\u0627\u0634 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u06d5\u061b',
    '\u0645\u06cc\u0648\u06d5\u062c\u0627\u062a\u06d5 \u0648 \u0632\u06c6\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0695\u06ce\u0632 \u0644\u06ce \u062f\u06d5\u06af\u06cc\u0631\u06ce.',
    '\u0644\u06d5 \u0628\u0627\u063a\u0627\u062a\u06cc \u067e\u0695 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06ce\u0631\u0648\u0628\u06ce\u0631\u06ce\u06a9.',
    '\u0644\u06d5 \u0647\u06d5\u0645\u0628\u06d5\u0631 \u06cc\u06d5\u06a9 \u0644\u06d5 \u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a\u0627\u0646 \u0695\u06c6\u062f\u06d5\u0646\u06cc\u0634\u0646.',
    '\u0644\u06d5\u0648 \u0628\u0627\u062f\u06d5\u06cc\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5\u0686\u0627\u0648\u06d5\u06cc \u0695\u06d5\u0648\u0627\u0646 \u0695\u0627\u062f\u06ce\u060c \u067e\u06cc\u0627\u06b5\u06d5\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0627 \u062f\u06d5\u06af\u06ce\u0695\u0646.',
    '\u0648\u06d5\u0695\u06d5\u0646\u06af \u0686\u06d5\u0631\u0645\u06af\u060c \u0647\u06d5\u0631 \u06a9\u06d5\u06cc\u0641\u06d5 \u0628\u06c6 \u0648\u06d5\u062e\u06c6\u0631\u06d5\u0648\u0627\u0646.',
    '\u0647\u06cc\u0686 \u0633\u06d5\u0631\u06ce\u0634\u06d5\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5 \u0648 \u0626\u0627\u0648\u06d5\u0632\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0631 \u0645\u06d5\u0633\u062a\u06cc \u0644\u06d5 \u062f\u06d5\u0633 \u0646\u0627\u062f\u06d5\u0646.',
    '\u0698\u0646\u06af\u06d5\u0644\u06ce\u06a9\u06cc \u0686\u0627\u0648\u06af\u06d5\u0648\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0644\u06d5 \u062d\u0627\u0646\u062f \u0646\u0627\u0645\u06c6\u060c \u0686\u0627\u0648 \u062f\u0627\u062f\u06d5\u062e\u06d5\u0646.',
    '\u0644\u0648\u0648\u0633 \u0648 \u067e\u0627\u06a9\u0698 \u0648\u06d5\u06a9\u0648\u0648 \u0647\u06ce\u06a9\u06cc \u0646\u0627\u0648 \u0647\u06ce\u0644\u06ce\u0646\u0646.',
    '\u0628\u06c6 \u0648\u062a\u0648\u0648\u06ce\u0698 \u0695\u0648\u0648\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u06cc.',
    '\u06cc\u06d5\u06a9\u06ce \u0644\u06d5\u0648 \u0646\u0627\u0648\u06d5\u062f\u0627 \u0626\u06ce\u0698\u06ce: \u0626\u06d5\u0645\u0646 \u0647\u06d5\u0645\u062f\u06d5\u0645\u06ce\u06a9\u0645 \u0647\u06d5\u0628\u0648\u0648.',
    '\u0644\u06ce\u06cc \u062f\u06d5\u067e\u0631\u0633\u06cc\u0645 \u062a\u06c6\u0634 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0645\u0631\u062f\u06cc\u0646\u060c \u0628\u0648\u0648\u06cc\u0646\u06d5 \u0626\u0627\u062e \u0648 \u0686\u06d5\u0646\u062f \u0647\u06d5\u0633\u062a\u06cc \u06cc\u06d5\u06a9\u060c \u0647\u06d5\u0631 \u062d\u06cc\u0633\u0627\u0628\u0645\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u06ce\u0634\u0646\u061f',
    '\u067e\u0631\u0633\u06cc: \u0626\u0627\u062e\u06c6 \u0647\u06cc\u0686 \u0626\u0627\u06af\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06cc\u06d5\u061f',
    '\u0626\u06d5\u0648\u0633\u0627 \u062e\u06c6\u0634\u06cc \u0686\u0627\u0648\u06cc \u06af\u06ce\u0695\u0627\u060c \u0644\u06d5\u062f\u0648\u0648\u0695 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u062f\u0627 \u062f\u06cc\u062a\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0628\u06d5 \u062e\u0648\u0627\u060c \u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a \u0628\u0648\u0648 \u0644\u06d5 \u0628\u06d5\u06cc\u0646\u0645 \u0628\u06d5\u0631\u06cc.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u0646\u06d5\u0628\u0648\u0648\u0627\u06cc\u06d5\u060c \u0626\u06ce\u0633\u062a\u0627 \u0645\u0646\u06cc\u0634 \u0648\u06ce\u0695\u0627\u06cc \u062a\u06c6 \u0628\u0648\u0648\u0645.',
    '\u062a\u06c6 \u0628\u0632\u0627\u0646\u06d5 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u062a\u0627\u0632\u06d5 \u0646\u0627\u0645\u0631\u06cc\u0646\u061b',
    '\u062c\u06cc\u0627 \u0644\u06d5 \u0645\u0631\u062f\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u0645\u0627\u0646. \u0647\u06cc\u0686 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u06a9\u06d5 \u0626\u06d5\u0645\u06d5\u0634 \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06cc\u06cc\u06d5\u06a9\u06cc \u0632\u06c6\u0631 \u0645\u06d5\u0632\u0646\u06d5.',
    '\u0626\u06d5\u0634\u06ce \u0628\u06c6 \u067e\u0627\u062f\u0627\u0634\u06cc \u0648\u06d5\u0647\u0627 \u062e\u06d5\u0628\u0627\u062a \u0628\u06a9\u06d5\u0646\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u06d5\u0628\u0627\u062a\u06a9\u0627\u0631\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645 \u067e\u06d5\u0632\u06ce\u0631\u0627\u06cc\u06cc\u06d5 \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5\u060c \u06cc\u0627\u062e\u06c6 \u062f\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u0698\u06d5\u0642\u0646\u06d5\u0645\u0648\u0648\u062a\u061f!',
    '\u0626\u06d5\u0648\u0645\u0627\u0646 \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u060c \u0628\u06c6\u06af\u0631\u06c6\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646.',
    '\u0626\u06d5\u0648 \u062f\u0627\u0631\u06ce\u06a9\u06d5 \u0644\u06d5\u062f\u0648\u0695 \u062a\u06d5\u062e\u062a\u06cc \u062f\u06c6\u0698\u06d5\u0647\u06d5\u0648\u06d5 \u062f\u06ce\u062a\u06d5 \u062f\u06d5\u0631\u06ce.',
    '\u0628\u06d5\u0631\u06ce\u06a9 \u062f\u06d5\u062f\u0627\u060c \u0644\u06d5 \u0628\u06cc\u0686\u0645\u06cc \u06a9\u06d5\u0644\u0644\u06d5\u0633\u06d5\u0631\u0627\u0646\u06cc \u0634\u06d5\u06cc\u0627\u062a\u06cc\u0646.',
    '\u062f\u06c6\u0698\u06d5\u0647\u06cc\u06d5\u06a9\u0627\u0646 \u0644\u06d5\u0648 \u062f\u06d5\u062e\u06c6\u0646 \u0648 \u0639\u0648\u0631\u06cc\u0627\u0646\u06cc \u067e\u06ce \u062f\u06d5\u0645\u0627\u0633\u06ce\u0646\u0646.',
    '\u067e\u0627\u0634\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648\u062e\u06c6\u0631\u0627\u06a9\u06d5 \u0644\u06cc\u062e\u0646\u0627\u0648\u06ce\u06a9\u06cc \u0647\u06d5\u0631\u06d5\u06af\u06d5\u0631\u0645 \u062f\u06d5\u062e\u06c6\u0646\u06d5\u0648\u06d5.',
    '\u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0631 \u06af\u0695 \u062f\u06d5\u062e\u06c6\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0646\u0627\u0648 \u062f\u06c6\u0698\u06d5\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646 \u062f\u06cc\u062a\u06cc\u0627\u0646 \u06a9\u06d5 \u0628\u0627\u0648 \u0628\u0627\u067e\u06cc\u0631\u0627\u0646\u06cc\u0627\u0646 \u06af\u0648\u0645\u0695\u0627\u0646\u061b',
    '\u0628\u06c6\u06cc\u06d5 \u0648\u0627 \u2013 \u0628\u06d5 \u0647\u06d5\u06b5\u06d5\u062f\u0627\u0648\u0627\u0646 \u2013 \u0628\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u067e\u06d5\u0644 \u062f\u06d5\u0628\u0646.',
    '\u0628\u06d5\u0631\u0644\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0632\u06c6\u0631\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u06af\u0648\u0645\u0695\u0627 \u0628\u0648\u0648\u0646.',
    '\u0632\u06c6\u0631\u06cc\u0634\u0645\u0627\u0646 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631\u0647\u06ce\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646.',
    '\u062f\u06d5\u0628\u0695\u0648\u0627\u0646\u06d5 \u2013 \u0644\u06d5 \u062f\u0648\u0627\u062c\u0627\u0631\u062f\u0627 \u2013 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062a\u0631\u0633\u06ce\u0646\u062f\u0631\u0627 \u0628\u0648\u0648\u0646\u060c \u0686\u06cc\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a.',
    '\u062c\u06cc\u0627 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u062f\u06b5\u067e\u0627\u06a9\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a.',
    '\u0646\u0648\u0648\u062d\u06cc\u0634 \u0647\u0627\u0648\u0627\u0631\u06cc \u0628\u06c6 \u0647\u06ce\u0646\u0627\u06cc\u0646\u061b \u0632\u06c6\u0631 \u0628\u0627\u0634\u06cc \u062f\u06d5\u0641\u0631\u06cc\u0627 \u06a9\u06d5\u0648\u062a\u06cc\u0646.',
    '\u062e\u06c6\u06cc \u0648 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0645\u0627\u0646 \u0644\u06d5 \u062e\u06d5\u0645\u06cc \u06af\u06d5\u0648\u0631\u06d5 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f.',
    '\u062a\u06d5\u0646\u06cc\u0627 \u0628\u06d5\u0631\u06d5\u0648 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06d5\u0648\u0645\u0627\u0646 \u0647\u06ce\u0634\u062a\u06d5\u0648\u06d5.',
    '\u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u062f \u0628\u06d5\u0631\u06d5\u06cc \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0634\u060c \u0647\u06d5\u0631 \u0646\u0627\u0648\u06cc \u0628\u06d5\u0686\u0627\u06a9\u06d5 \u0628\u06d5\u0631\u0646.',
    '\u0633\u06b5\u0627\u0648 \u0644\u06d5 \u0646\u0648\u062d \u0644\u06d5 \u0633\u06d5\u0631\u0627\u0646\u0633\u06d5\u0631\u06cc \u062f\u0646\u06cc\u0627\u062f\u0627.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u06cc\u06d5\u06a9\u06ce \u0628\u0648\u0648 \u0644\u06d5\u0648 \u0639\u06d5\u0628\u062f\u0627\u0646\u06d5\u0645 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u0645.',
    '\u067e\u0627\u0634\u0627\u0646 \u0626\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc \u062a\u0631\u0645\u0627\u0646 \u0648\u06d5\u0628\u0646 \u0626\u0627\u0648 \u062f\u0627.',
    '\u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u06cc\u0634 \u0644\u06d5 \u0634\u0648\u06ce\u0646 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0627\u0646\u06cc \u0626\u06d5\u0648 \u0628\u0648\u0648.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u062f\u06b5\u06cc \u067e\u0627\u06a9\u06d5\u0648\u06d5 \u0647\u0627\u062a\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc.',
    '\u0628\u06d5 \u0626\u0627\u067e\u06cc \u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0626\u06d5\u0648\u06d5 \u0686\u06cc\u0646 \u062f\u06d5\u06cc\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a\u0646\u061f',
    '\u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 \u0644\u06d5\u0628\u0631\u06cc \u062e\u0648\u062f\u0627\u067e\u06d5\u0631\u0633\u062a\u0646\u06ce\u060c \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061f!',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062f\u0646\u06cc\u0627 \u0628\u06cc\u0631\u0648\u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0686\u0644\u06c6\u0646\u06d5\u061f',
    '\u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631\u062e\u06c6\u06cc \u062f\u06d5\u0626\u06d5\u0633\u062a\u06ce\u0631\u0627\u0646\u06cc \u0647\u06d5\u06b5\u0695\u0648\u0627\u0646\u06cc.',
    '\u06af\u0648\u062a\u06cc: \u0628\u06ce \u0633\u06c6 \u0645\u0646 \u0646\u06d5\u062e\u06c6\u0634\u0645.',
    '\u0633\u0627 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06ce\u06cc \u062a\u06d5\u06a9\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u067e\u0634\u062a\u06cc\u0627\u0646 \u062a\u06ce\u06a9\u0631\u062f.',
    '\u067e\u06d5\u0646\u0627\u0645\u06d5\u06a9\u06cc \u062e\u06c6\u06cc \u06af\u06d5\u06cc\u0627\u0646\u062f\u06d5 \u0628\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u061b \u0644\u06ce\u06cc \u067e\u0631\u0633\u06cc\u0646: \u0626\u06ce\u0648\u06d5 \u0628\u06c6 \u0646\u0627\u062e\u06c6\u0646\u061f!',
    '\u0686\u06cc\u062a\u0627\u0646\u06d5 \u0628\u06c6 \u0642\u0633\u0627\u0646 \u0646\u0627\u06a9\u06d5\u0646\u061f!',
    '\u067e\u06d5\u0646\u0627\u0645\u06d5\u06a9\u06cc \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0695\u0627\u0633\u062a \u0645\u0633\u062a\u06ce\u06a9\u06cc \u062a\u06ce \u0633\u0631\u06d5\u0648\u0627\u0646\u062f\u0646.',
    '\u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0628\u06d5 \u0647\u06d5\u06b5\u06d5\u062f\u0627\u0648\u0627\u0646 \u0644\u06ce\u06cc \u0648\u06d5\u06a9\u06c6 \u0628\u0648\u0648\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0634\u062a\u06ce \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u06a9\u06d5 \u0647\u06d5\u0631\u062e\u06c6\u062a\u0627\u0646 \u062f\u0627\u06cc \u062f\u06d5\u062a\u0627\u0634\u0646\u061f',
    '\u06a9\u06d5 \u0647\u06d5\u0631 \u062e\u0648\u0627\u06cc\u06d5\u060c \u0626\u06ce\u0648\u06d5\u0634 \u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u062a\u0627\u0646\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0634\u0648\u0631\u06d5\u062f\u06cc\u0648\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06c6 \u0647\u06d5\u06b5\u0646\u06d5\u0686\u0646\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u062f\u06d5\u0626\u0627\u06af\u0631\u06cc \u0628\u0627\u0648\u06ce\u0646.',
    '\u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u0648\u0648 \u06af\u06d5\u0695\u06ce\u06a9\u06cc \u062a\u06ce \u0628\u0647\u0627\u06b5\u06ce\u0646\u0646\u060c \u0626\u06ce\u0645\u06d5 \u067e\u062a\u0631 \u0698\u06ce\u0631\u0645\u0627\u0646 \u062e\u0633\u062a\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0648\u0627 \u0645\u0646 \u0695\u0648\u0648 \u062f\u06d5\u06a9\u06d5\u0645\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u061b \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u0645 \u062f\u06d5\u06a9\u0627.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0641\u0631\u0632\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0645 \u067e\u06ce \u0628\u0628\u06d5\u062e\u0634\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u0644\u0627\u0648\u06ce\u06a9\u06cc \u0644\u06d5\u0633\u06d5\u0631\u062e\u06c6 \u0645\u0698\u062f\u06d5\u0645\u0627\u0646 \u067e\u06ce \u062f\u0627.',
    '\u06a9\u0627\u062a\u06ce \u06af\u06d5\u06cc\u06cc\u0634\u062a\u06d5 \u062a\u06d5\u0645\u06d5\u0646\u06ce\u06a9 \u06a9\u06d5 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648 \u062e\u06d5\u0628\u0627\u062a \u0628\u06a9\u0627\u060c \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u06a9\u0648\u0695\u06cc\u0698\u06af\u06d5\u06cc \u062e\u06c6\u0645! \u0645\u0646 \u0644\u06d5\u062e\u06d5\u0648\u0646\u0645\u062f\u0627 \u0648\u0627\u0645 \u062f\u06cc\u0648\u06d5 \u062a\u06c6 \u0633\u06d5\u0631 \u062f\u06d5\u0628\u0695\u0645\u061b \u062e\u06c6\u062a \u0686\u06c6\u0646\u06cc \u0644\u06ce\u06a9 \u062f\u06d5\u062f\u06d5\u06cc\u06d5\u0648\u06d5\u061f \u06af\u0648\u062a\u06cc: \u0628\u0627\u0628\u06c6! \u0647\u06d5\u0631\u0634\u062a\u06ce \u067e\u06ce\u062a \u0633\u067e\u06ce\u0631\u0627\u0648\u06d5 \u0628\u06d5 \u062c\u06ce\u06cc \u0628\u06ce\u0646\u06d5\u061b \u062e\u0648\u0627 \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u062a\u06ce \u062f\u06d5\u06af\u06d5\u06cc \u06a9\u06d5 \u0645\u0646\u06cc\u0634 \u0644\u06d5 \u062e\u06c6\u0695\u0627\u06af\u0631\u0627\u0646\u0645.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0647\u06d5\u0631\u062f\u0648\u0648 \u0628\u0695\u06cc\u0627\u0631\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0633\u06d5\u0644\u0645\u0627\u0646\u062f \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0646\u06cc \u062a\u062e\u06ce\u06b5\u06cc \u06a9\u0631\u062f\u060c',
    '\u06af\u0627\u0632\u06cc\u0646 \u06a9\u0631\u062f\u06ce: \u0626\u06d5\u06cc \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645!',
    '\u0626\u06d5\u0648\u0627 \u062e\u06d5\u0648\u0646\u06d5\u06a9\u06d5\u062a \u0648\u06d5\u062f\u06cc \u0647\u0627\u062a\u061b \u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0645\u06d5 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u0648\u0648.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0645\u0627\u0646 \u0628\u06d5 \u0642\u0648\u0631\u0628\u0627\u0646\u06cc\u06d5\u06a9\u06cc \u0647\u06d5\u0631\u06d5 \u06af\u06d5\u0648\u0631\u06d5 \u06a9\u0695\u06cc\u06cc\u06d5\u0648\u06d5.',
    '\u0648\u06d5\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f \u0628\u06d5\u0631\u06d5\u06cc \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u0647\u06d5\u0631 \u0646\u0627\u0648\u06cc \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0628\u06d5\u0631\u0646.',
    '\u0633\u06b5\u0627\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0628\u06ce.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0626\u06d5\u0648 \u06cc\u06d5\u06a9\u06ce \u0628\u0648\u0648 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5 \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646.',
    '\u0645\u0698\u062f\u06d5\u06cc \u0626\u06cc\u0633\u062d\u0627\u0642\u06cc\u0634\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627\u060c \u06a9\u06d5 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06cc \u0698\u06ce\u0647\u0627\u062a\u06cc.',
    '\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u062a\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u062e\u06c6\u06cc \u0648 \u0628\u06d5\u0626\u06cc\u0633\u062d\u0627\u0642\u06cc\u0634. \u0644\u06d5 \u0646\u0627\u0648 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06a9\u06d5\u06cc\u0627\u0646\u062f\u0627 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646 \u0648 \u062a\u06ce\u0634\u06cc\u0627\u0646\u062f\u0627 \u0628\u0648\u0648\u0646 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u063a\u06d5\u062f\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u0627\u0695\u0648\u0648\u0646\u06cc\u0634 \u0686\u0627\u06a9\u06d5\u0645\u0627\u0646 \u06a9\u0631\u062f.',
    '\u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5 \u0686\u06c6\u0631\u062a\u0645\u06cc \u0632\u0644 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f.',
    '\u0628\u06c6 \u062e\u06c6\u0645\u0627\u0646 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0645\u0627\u0646 \u062f\u0627\u0646 \u06a9\u06d5 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646.',
    '\u0626\u06d5\u0648\u06a9\u062a\u06ce\u0628\u06d5\u0634\u0645\u0627\u0646 \u0628\u06d5\u0648\u0627\u0646 \u062f\u0627\u060c \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc\u0634\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0646.',
    '\u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u062f \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0634\u062f\u0627 \u0647\u06d5\u0631 \u0646\u0627\u0648\u06cc\u0627\u0646 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0628\u0628\u0631\u06ce.',
    '\u0633\u06b5\u0627\u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u0645\u0648\u0648\u0633\u0627 \u0648 \u0647\u0627\u0695\u0648\u0648\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0631\u0627\u0646 \u067e\u0627\u062f\u0627\u0634 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0647\u06d5\u0631 \u062f\u0648\u0648\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0648 \u0639\u06d5\u0628\u062f\u0627\u0646\u06d5\u0645 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u0645.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u0626\u06d5\u0644\u06cc\u0627\u0633\u06cc\u0634 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0628\u06c6\u0686 \u0634\u06d5\u0631\u0645 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0627\u06a9\u06d5\u0646\u061f',
    '\u00ab\u0628\u06d5\u0639\u0644\u00bb [\u0628\u062a\u06d5] \u062f\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0644\u06d5 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u06d5\u0631 \u062f\u06d5\u062a\u06d5\u06a9\u0646\u06d5\u0648\u06d5\u061f',
    '\u0644\u06d5 \u062e\u0648\u0627 \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0628\u0627\u06a4\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648\u0634\u062a\u0627\u0646\u06d5.',
    '\u0633\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f \u0648 \u062f\u06ce\u0646\u06d5 \u0628\u06d5\u0631 \u0644\u06ce \u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0639\u06d5\u0628\u062f\u06d5 \u0695\u0627\u0633\u062a \u0648 \u067e\u0627\u06a9\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627.',
    '\u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u062f \u0644\u06d5\u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0634\u062f\u0627 \u0647\u06d5\u0631 \u0646\u0627\u0648\u06cc \u0628\u06d5\u0686\u0627\u06a9\u06d5 \u0628\u0628\u0631\u06ce.',
    '\u0633\u06b5\u0627\u0648\u06cc \u0645\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0644\u06cc\u0627\u0633.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u06cc\u06d5\u06a9\u06ce \u0628\u0648\u0648 \u0644\u06d5\u0648 \u0639\u06d5\u0628\u062f\u0627\u0646\u06d5\u0645 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u0645.',
    '\u0644\u0648\u0648\u062a\u06cc\u0634 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0628\u0648\u0648.',
    '\u062e\u06c6\u06cc \u0648 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06cc\u0634\u06cc\u0646 \u0648\u06ce\u06a9\u0695\u0627 \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u062a\u06d5\u0646\u06cc\u0627 \u067e\u06cc\u0631\u06ce\u0698\u0646\u06ce\u06a9 \u06a9\u06d5 \u0644\u0627\u06cc \u0628\u06d5 \u062c\u06ce \u0645\u0627\u0648\u06d5\u06a9\u0627\u0646 \u0645\u0627.',
    '\u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u0642\u0695 \u06a9\u0631\u062f\u0646.',
    '\u0628\u06ce \u0633\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u0627\u06cc \u0628\u06d5\u06cc\u0627\u0646\u06cc\u060c \u0628\u06d5\u0644\u0627\u06cc\u0627\u0646\u062f\u0627 \u0695\u0627\u062f\u06d5\u0628\u0648\u0648\u0631\u0646\u061b',
    '\u06cc\u0627\u0646 \u0634\u06d5\u0648\u0627\u0646\u06d5. \u0686\u06c6\u0646 \u0644\u06d5\u0648 \u0695\u0648\u0648\u062f\u0627\u0648\u06d5 \u062a\u06ce \u0646\u0627\u06af\u06d5\u0646\u061f!',
    '\u06cc\u0648\u0648\u0646\u0633\u06cc\u0634 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce\u06a9 \u0628\u0648\u0648 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u061b',
    '\u06a9\u06d5 \u0695\u0627\u06cc \u06a9\u0631\u062f\u06d5 \u0646\u0627\u0648 \u06af\u06d5\u0645\u06cc \u067e\u0695.',
    '\u0644\u06d5 \u067e\u0634\u06a9 \u062e\u0633\u062a\u0646 \u0628\u06d5\u0634\u062f\u0627\u0631 \u0628\u0648\u0648\u061b \u0626\u06d5\u0648 \u062f\u06c6\u0695\u0627\u0646\u062f\u06cc.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0645\u0627\u0633\u06cc\u06cc\u06d5\u06a9\u06cc \u0632\u06d5\u0644\u0627\u0645\u060c \u0647\u06d5\u06b5\u06cc \u0644\u0648\u0648\u0634\u06cc \u0648 \u0628\u06c6 \u062e\u06c6\u06cc \u0633\u06d5\u0631\u06a9\u06c6\u0646\u06d5\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u067e\u0627\u06a9\u06cc \u062e\u0648\u062f\u0627\u06cc \u0646\u06d5\u06af\u0648\u0648\u062a\u0628\u0627\u06cc\u06d5\u060c',
    '\u062a\u0627 \u0695\u06c6\u0698\u06ce \u0633\u06d5\u06b5\u0627 \u0695\u0627\u062f\u06d5\u0628\u0648\u0648\u060c \u0644\u06d5 \u0648\u0631\u06af\u06cc\u062f\u0627 \u062f\u06d5\u0645\u0627\u06cc\u06d5\u0648\u06d5.',
    '\u0641\u0695\u06ce\u0645\u0627\u0646 \u062f\u0627\u06cc\u06d5 \u062f\u06d5\u0634\u062a\u06ce\u06a9\u06cc \u0686\u06c6\u06b5 \u0648 \u0642\u0627\u0642\u0695\u061b \u0646\u06d5\u062e\u06c6\u0634\u06cc\u0634 \u0628\u0648\u0648.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0628\u0695\u06a9\u06d5\u06cc \u06a9\u0648\u0646\u062f\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u0644\u0627 \u0634\u06cc\u0646 \u06a9\u0631\u062f.',
    '\u0646\u0627\u0631\u062f\u0645\u0627\u0646\u06d5 \u0646\u0627\u0648 \u0633\u06d5\u062f \u0647\u06d5\u0632\u0627\u0631\u0627\u0646 \u06cc\u0627\u0646 \u0632\u06ce\u062f\u06d5\u062a\u0631.',
    '\u0647\u06d5\u0645\u0648\u0648 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627 \u0648 \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0698\u06cc\u0627\u0646\u06ce\u06a9\u06cc \u062e\u06c6\u0634\u0645\u0627\u0646 \u067e\u06ce\u062f\u0627\u0646.',
    '\u062f\u06d5\u0633\u0627 \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u067e\u0631\u0633\u06d5: \u062a\u06c6\u0628\u06ce\u0698\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u06a9\u06cc\u0698\u06cc \u0647\u06d5\u0646 \u0648 \u0626\u06cc \u0648\u0627\u0646 \u06a9\u0648\u0695\u0646\u061f!',
    '\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06ce \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0645\u06ce \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u061f',
    '\u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u06d5\u060c \u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u06cc\u06b5\u06ce\u0646 \u0647\u06d5\u0631 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646\u061b',
    '\u06a9\u06d5: \u062e\u0648\u062f\u0627 \u0645\u0646\u0627\u06b5\u06cc \u0628\u0648\u0648\u06d5. \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648 \u0628\u06d5\u062c\u06ce \u06a9\u0648\u0695\u060c \u06a9\u0686\u06cc \u0628\u0698\u0627\u0631\u062f\u0648\u0648\u06d5\u061f',
    '\u0686\u06cc\u062a\u0627\u0646\u06d5\u060c \u0686\u06c6\u0646 \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u06d5\u0646\u061f',
    '\u0626\u0627\u062e\u06c6 \u0647\u06cc\u0686 \u0628\u06cc\u0631 \u0646\u0627\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061f',
    '\u06cc\u0627\u0646 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u067e\u062a\u06d5\u0648\u062a\u0627\u0646 \u0644\u06d5 \u0644\u0627 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0642\u0633\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u062a\u06d5\u060c \u0633\u0627 \u06a9\u062a\u06ce\u0628\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06ce\u0646\u0646.',
    '\u062c\u0646\u06c6\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 \u06a9\u0631\u062f\u06d5 \u062e\u0632\u0645\u061b \u062c\u0646\u06c6\u06a9\u0627\u0646\u06cc\u0634 \u0628\u0627\u0634 \u062f\u06d5\u0632\u0627\u0646\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0647\u06ce\u0646\u0646\u06d5 \u0628\u06d5\u0631\u06ce.',
    '\u062e\u0648\u062f\u0627 \u062f\u0648\u0648\u0631\u06d5 \u0644\u06d5\u0648 \u0628\u0627\u0633\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u0628\u06d5 \u062f\u06b5\u067e\u0627\u06a9\u06cc \u0639\u06d5\u0628\u062f\u0627\u06cc\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0633\u0627 \u0648\u0627 \u0626\u06ce\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u0646\u060c',
    '\u06a9\u06d5\u0633\u062a\u0627\u0646 \u0628\u06c6 \u0628\u062a \u0628\u06d5 \u0641\u0631\u06cc\u0648 \u0646\u0627\u0686\u06ce\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u062f\u06d5\u0686\u06ce\u062a\u06d5 \u0646\u06ce\u0648 \u062f\u06c6\u0632\u06d5\u0642\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u0626\u06ce\u0645\u06d5\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u0627\u0645\u06ce\u06a9\u0645\u0627\u0646 \u062c\u06ce\u06af\u06d5\u06cc \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5\u06cc\u0646 \u06a9\u06d5 \u0628\u06c6 \u062e\u0648\u0627\u067e\u06d5\u0631\u0633\u062a\u06cc \u0695\u06ce\u0632 \u062f\u06d5\u0628\u06d5\u0633\u062a\u06cc\u0646.',
    '\u0626\u06ce\u0645\u06d5\u06cc\u0646 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5 \u062a\u0627\u0631\u06cc\u0641\u06cc \u0695\u0627\u0646\u0627\u0648\u06d5\u0633\u062a\u06cc\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u062e\u06c6 \u0647\u06d5\u0631 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a:',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5\u0634 \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u0646 \u067e\u06ce\u0634\u06cc\u0646\u0627\u0646\u06d5\u0648\u06d5\u060c \u06a9\u062a\u06ce\u0628\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u06c6\u0645\u0627\u0628\u0627\u060c',
    '\u0626\u06ce\u0633\u062a\u0627 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5 \u062f\u06b5\u067e\u0627\u06a9\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627 \u0628\u0648\u0648\u06cc\u0646.',
    '\u0644\u06d5\u06a9\u062a\u06ce\u0628\u06cc \u062e\u0648\u0627 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u0632\u06c6\u0631 \u0646\u0627\u062e\u0627\u06cc\u06d5\u0646\u06ce \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0628\u06d5 \u0631\u06ce\u0634 \u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u2013 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0639\u06d5\u0628\u062f\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u2013 \u0628\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0695\u0627\u06af\u06d5\u06cc\u06d5\u0646\u0631\u0627\u0648\u06d5:',
    '\u06a9\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u06cc\u0627\u0631\u06cc \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0647\u06d5\u0631 \u0633\u0648\u067e\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u0633\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u06ce.',
    '\u0633\u0627 \u062a\u06c6 \u0648\u0627\u0632\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06ce\u0646\u06d5 \u0647\u06d5\u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9.',
    '\u062c\u0627 \u0628\u06cc\u0627\u0646 \u0628\u06cc\u0646\u06d5\u061b \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u062e\u06c6 \u062f\u06d5\u0628\u06cc\u0646\u0646\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06ce \u06a9\u06d5 \u0632\u0648\u0648\u062a\u0631 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0628\u062f\u06d5\u06cc\u0646\u061f',
    '\u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc \u0628\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631 \u0648 \u0628\u06d5\u0631\u06cc \u062f\u0631\u06af\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06af\u0631\u06ce\u060c \u0633\u067e\u06ce\u062f\u06d5\u06cc \u062a\u0631\u0633\u06ce\u0646\u062f\u0631\u0627\u0648\u0627\u0646\u060c \u0626\u06d5\u0648\u0633\u0627 \u06af\u06d5\u0644\u06ce \u062e\u0631\u0627\u067e \u062f\u06d5\u0628\u06ce.',
    '\u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0647\u06d5\u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0648\u0627\u0632 \u0628\u06ce\u0646\u06d5.',
    '\u062c\u0627 \u0628\u0628\u06cc\u0646\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u062e\u06c6 \u062f\u06d5\u0628\u06cc\u0646\u0646\u06d5\u0648\u06d5.',
    '\u067e\u0627\u06a9\u06cc \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u061b \u0647\u06d5\u0631\u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5 \u0648 \u0644\u06d5\u0648 \u0642\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06af\u06d5\u0644\u06ce \u062f\u0648\u0648\u0631\u06d5.',
    '\u0633\u06b5\u0627\u0648\u06cc\u0634 \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646.',
    '\u0633\u0648\u067e\u0627\u0633\u06cc\u0634 \u0628\u06c6 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062f\u0646\u06cc\u0627.',
    '\u0633\u0627\u062f. \u0628\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5 \u06a9\u06d5 \u067e\u0695 \u0644\u06d5 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u06cc\u06d5 [\u0648 \u062f\u06d5\u0628\u06ce \u067e\u06d5\u0646\u062f\u06cc \u0644\u06ce \u0648\u06d5\u0631\u06af\u06cc\u0631\u06ce].',
    '\u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u060c \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0632\u0644 \u062f\u06d5\u0632\u0627\u0646\u0646 \u0648 \u0647\u06d5\u0631 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u062f\u0698\u0627\u06cc\u06d5\u062a\u06cc\u0646.',
    '\u067e\u06ce\u0634 \u0626\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0632\u06c6\u0631 \u0628\u06d5\u0631\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u0631\u062f\u0646\u061b \u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u0627\u0648\u0627\u0631\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062f\u06d5\u0631\u0641\u06d5\u062a \u0644\u06d5\u062f\u06d5\u0633 \u0686\u0648\u0648 \u0628\u0648\u0648.',
    '\u0626\u06d5\u0645\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u0633\u06d5\u06cc\u0631 \u0628\u0648\u0648\u06d5 \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5 \u0648 \u062a\u0631\u0633\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u062f\u06d5\u0646\u06ce\u061b \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0628\u06ce \u0646\u06d5\u0628\u06ce\u060c \u0626\u06d5\u0645 \u06a9\u0627\u0628\u0631\u0627\u06cc\u06d5 \u062c\u0627\u062f\u0648\u0648\u06af\u06d5\u0631\u06ce \u062f\u0631\u06c6\u0632\u0646\u06d5.',
    '\u0686\u06c6\u0646 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u0644\u06d5\u062c\u06ce\u06cc \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u0628\u062a\u0627\u0646\u06d5\u06cc \u062f\u06d5\u062a\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u060c \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061f \u0626\u06d5\u0645\u06d5 \u0632\u06c6\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0633\u06d5\u06cc\u0631\u06d5.',
    '\u06a9\u06d5\u06b5\u06d5\u067e\u06cc\u0627\u0648\u0627\u0646\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0646\u06d5 \u0695\u06ce [\u0648\u062a\u06cc\u0627\u0646 \u067e\u06ce\u06cc\u0627\u0646:] \u0628\u0695\u06c6\u0646 \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062a\u0627 \u0626\u06ce\u0633\u062a\u0627 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u062a\u0627\u0646\u0646\u060c \u0644\u0627 \u0645\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0646\u061b \u0628\u06ce \u0633\u06c6 \u0626\u06d5\u0645\u06d5 \u0648\u06cc\u0633\u062a\u06cc \u0645\u06d5\u06cc\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc \u062a\u0631\u062f\u0627 \u0634\u062a\u06ce \u0648\u0627\u0645\u0627\u0646 \u0646\u06d5\u0628\u06cc\u0633\u062a\u0648\u0648\u06d5\u061b \u0626\u06d5\u0645\u06d5 \u0695\u0627\u0633\u062a\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5 \u0648 \u0647\u06d5\u06b5\u0628\u06d5\u0633\u062a\u06d5\u06cc\u06d5.',
    '\u0686\u06c6\u0646\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646\u062f\u0627\u060c \u0642\u0648\u0631\u0626\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648 \u0647\u0627\u062a\u0648\u0648\u06d5\u061f \u062f\u0648\u0648\u062f\u06b5\u06cc\u0634\u0646 \u06a9\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646 \u0645\u0646 \u0646\u0627\u0631\u062f\u0628\u06ce\u062a\u0645. \u0647\u06ce\u0645\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0686\u06d5\u0634\u062a\u0648\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u06af\u06d5\u0646\u062c\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u062f\u06b5\u0627\u0648\u0627\u06cc \u062a\u06c6\u06cc\u0627\u0646 \u0644\u0627\u06cc\u06d5\u061f',
    '\u06cc\u0627\u0646 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u062f\u06d5 \u0628\u06d5\u0631\u062f\u06d5\u0633\u062a\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u0627\u06cc\u06d5\u061f \u062f\u06d5\u0633\u0627 \u0628\u0627 \u067e\u06d5\u06cc\u0698\u06d5 \u062f\u0627\u0646\u06ce\u0646 \u0648 \u0628\u06ce\u0646\u06d5 \u0633\u06d5\u0631\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5\u0634\u06a9\u0631\u06ce\u06a9\u0646 \u0644\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06d5\u06a9\u0627\u0646 \u06a9\u06d5 \u0644\u06d5\u0648\u06ce\u0646\u062f\u06d5\u0631\u06ce \u062a\u06ce \u062f\u06d5\u0634\u06a9\u06ce\u0646.',
    '\u0628\u06d5\u0631\u0644\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d \u0648 \u0647\u06c6\u0632\u06cc \u0639\u0627\u062f \u0648 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u06cc \u0686\u0648\u0627\u0631\u0646\u06cc\u062e\u06d5\u06a9\u06ce\u0634\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627.',
    '\u0647\u06c6\u0632\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f\u060c \u0647\u06c6\u0632\u06cc \u0644\u0648\u0648\u062a \u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u0646\u0627\u0648 \u0644\u06ce\u0631\u06d5\u0648\u0627\u0631\u06d5\u06a9\u06d5\u0634\u060c \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06d5 \u0628\u0648\u0648\u0646.',
    '\u06af\u0634 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5\u0644\u0627\u0648\u06d5 \u062f\u0631\u06c6\u0632\u0646 \u0628\u0648\u0648\u06d5 \u0648 \u0647\u06ce\u0698\u0627\u06cc \u062a\u06c6\u06b5\u06d5 \u0644\u06ce \u0633\u0627\u0646\u062f\u0646 \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0646 \u062a\u0627 \u0644\u06d5 \u0646\u06d5\u06a9\u0627\u0648 \u06af\u0631\u0645\u06cc\u0646\u06ce \u0648\u0627 \u067e\u06d5\u06cc\u062f\u0627 \u062f\u06d5\u0628\u06ce\u060c \u06a9\u06d5 \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a \u0628\u06d5\u0634\u06cc \u0647\u06d5\u0646\u06cc\u0633\u06a9 \u062f\u0627\u0646\u06ce\u06a9\u06cc\u0634 \u0648\u0686\u0627\u0646 \u0646\u0627\u062f\u0627.',
    '\u0647\u06d5\u0631 \u062f\u06d5\u06b5\u06ce\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646! \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u0628\u06ce\u060c \u067e\u0633\u0648\u0648\u0644\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u062f\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u06cc\u06b5\u06ce\u0646 \u0628\u0627 \u0628\u06cc\u06b5\u06ce\u0646\u060c \u062a\u06c6 \u062e\u06c6\u0695\u0627\u06af\u0631\u0628\u06d5 \u0648 \u062f\u0627\u0648\u0648\u062f \u0648\u06d5\u0628\u06cc\u0631\u062e\u06c6\u062a \u0628\u06ce\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0639\u06d5\u0628\u062f\u06ce\u06a9\u06cc \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0648\u0648\u061b \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646\u06cc \u0695\u0648\u0648\u06cc \u062a\u06ce \u062f\u06d5\u06a9\u0631\u062f\u060c \u0647\u06d5\u0631 \u067e\u06d5\u0646\u0627\u06cc \u0628\u06d5 \u0626\u06ce\u0645\u06d5 \u062f\u06ce\u0646\u0627.',
    '\u06a9\u06ce\u0648\u0627\u062a\u06cc\u0634\u0645\u0627\u0646 \u0641\u06ce\u0631 \u06a9\u0631\u062f\u0628\u0648\u0648\u060c \u06a9\u06d5 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648 \u0633\u06d5\u0631\u0644\u06d5\u0646\u06af\u06c6\u0631\u06d5 \u0648 \u0644\u06d5 \u06a9\u0627\u062a\u06cc \u062a\u0627\u0648 \u0647\u06d5\u06b5\u0627\u062a\u0646\u062f\u0627\u060c \u0647\u06d5\u0631 \u062a\u0627\u0631\u06cc\u0641\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646.',
    '\u0645\u06d5\u0644\u0627\u0646\u06cc\u0634 \u06a9\u06c6\u0645\u06d5\u06b5 \u062f\u06d5\u0628\u0648\u0648\u0646 \u0648 \u06af\u0634\u062a\u06cc\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u062f\u06d5\u0647\u0627\u062a\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06cc\u0645\u0627\u0646 \u0628 \u06c6\u067e\u062a\u06d5\u0648 \u06a9\u0631\u062f\u061b \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u0646\u062f\u0627 \u0628\u06d5\u0634\u0645\u0627\u0646 \u062f\u0627 \u0648 \u0628\u06d5 \u0642\u0633\u06d5\u06cc \u0626\u06d5\u0648\u060c \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06ce\u0634\u06d5 \u06a9\u06c6\u062a\u0627 \u062f\u06d5\u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u062a\u06c6 \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u06cc \u0626\u06d5\u0648 \u062f\u0627\u062f\u0628\u06d5\u0631\u0627\u0646\u06d5\u062a \u0628\u06cc\u0633\u062a\u060c \u06a9\u06d5 \u0644\u06d5\u0634\u0648\u0648\u0631\u06d5\u06cc \u0646\u0648\u06ce\u0698\u06af\u06d5\u06a9\u06d5\u06cc\u06d5\u0648\u06d5 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u061f',
    '\u06a9\u06d5 \u0644\u06d5 \u062f\u0627\u0648\u0648\u062f \u0648\u06d5\u0698\u0648\u0648\u0631\u06a9\u06d5\u0648\u062a\u0646\u060c \u0644\u06ce\u0627\u0646 \u062a\u0631\u0633\u0627. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646\u06cc\u062a \u0646\u06d5\u0628\u06ce\u060c \u062f\u0648\u0648 \u062f\u0627\u062f\u0628\u06d5\u0631\u06cc\u0646\u061b \u06cc\u06d5\u06a9 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u063a\u06d5\u062f\u0631\u06cc \u0644\u06d5\u0648\u06cc \u062a\u0631 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u0686\u06c6\u0646 \u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0626\u0627\u0648\u0627 \u0628\u06d5 \u0646\u0627\u0648\u0645\u0627\u0646 \u0695\u0627\u0628\u06af\u06d5 \u0648 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u0648\u0648\u0631\u06cc \u062e\u0648\u0627 \u0644\u0627 \u0645\u06d5\u062f\u06d5 \u0648 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5.',
    '\u0626\u06d5\u0645 \u0628\u0631\u0627\u06cc\u06d5\u0645 \u0646\u06d5\u0648\u06d5\u062f \u0648 \u0646\u06c6 \u067e\u06d5\u0632\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u0645\u0646 \u062a\u06d5\u0646\u06cc\u0627 \u067e\u06d5\u0632\u06ce\u06a9\u0645 \u0647\u06d5\u06cc\u06d5. \u06af\u0648\u062a\u06cc: \u0628\u06d5 \u0645\u0646\u06cc \u0628\u0633\u067e\u06ce\u0631\u06d5 \u0648\u060c \u0644\u06d5 \u06a9\u06ce\u0634\u06d5\u062f\u0627 \u0645\u0646 \u062f\u06d5\u0631\u06c6\u0633\u062a\u06cc \u0626\u06d5\u0648 \u0646\u06d5\u0647\u0627\u062a\u0645.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648 \u06a9\u06d5 \u067e\u06d5\u0632\u06d5\u06a9\u06d5\u06cc \u062a\u06c6 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0646\u0627\u0648 \u067e\u06d5\u0632\u06d5\u06a9\u0627\u0646\u06cc\u060c \u063a\u06d5\u062f\u0631\u06ce \u062f\u06d5\u06a9\u0627. \u0632\u06c6\u0631 \u0634\u06d5\u0631\u06cc\u06a9\u06d5 \u0645\u0627\u06b5\u06cc \u062a\u0631\u06cc\u0634 \u0641\u06ce\u06b5\u06ce \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u061b \u0626\u06d5\u0648\u06cc\u0634 \u06a9\u06d5\u0645\u0646. \u062f\u0627\u0648\u0648\u062f \u0632\u0627\u0646\u06cc \u0626\u06d5\u0632\u0645\u0648\u0648\u0645\u0627\u0646\u06d5\u061b \u0626\u06cc\u062a\u0631 \u062a\u0648\u0648\u0634 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646\u06cc \u0647\u0627\u062a\u061b \u0633\u0648\u0698\u062f\u06d5\u06cc \u0628\u0631\u062f \u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u067e\u0627\u0695\u0627\u0648\u06d5 \u06a9\u06d5 \u0628\u06cc\u0628\u06d5\u062e\u0634\u06ce.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0648 \u0647\u06d5\u06b5\u06d5\u06cc \u0628\u06d5\u062e\u0634\u06cc\u0645\u0627\u0646\u061b \u0644\u06d5 \u0646\u0632\u06cc\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5 \u0648 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0686\u0627\u06a9\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u062f\u0627\u0648\u0648\u062f! \u062c\u06ce \u0646\u0634\u06cc\u0646\u06cc\u0645\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646 \u062f\u0627\u0648\u06d5 \u0628\u06d5 \u062a\u06c6\u061b \u0628\u06d5 \u062a\u06d5\u0631\u0632\u06ce\u06a9\u06cc \u062e\u0648\u062f\u0627\u067e\u06d5\u0633\u0646\u062f\u060c \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0695\u0627\u0628\u06af\u06d5 \u0648 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u062a \u0645\u06d5\u06a9\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u0644\u0627\u062a \u0628\u062f\u0627. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u0646\u060c \u0686\u0648\u0646\u06a9\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631 \u0686\u0648\u0648\u060c \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062a\u0648\u0646\u062f \u0648 \u062a\u06cc\u0698\u06cc\u0627\u0646 \u0647\u06d5\u0633.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u0645\u0627\u0646 \u2013 \u0626\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0628\u06d5\u06cc\u0646\u06cc\u0627\u0646\u062f\u0627\u06cc\u06d5 \u2013 \u0628\u06d5 \u062e\u06c6\u0695\u0627\u06cc\u06cc \u062f\u0631\u0648\u0633 \u0646\u06d5\u06a9\u0631\u062f\u061b \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0648\u0627 \u0628\u06cc\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646. \u0647\u0627\u06cc \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0628\u06c6 \u0626\u06d5\u0648 \u0626\u0627\u06af\u0631\u06d5\u06cc \u062f\u06d5\u06cc\u0686\u0646\u06ce!',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u0647\u06d5\u0631 \u0648\u06d5\u06a9 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u0627\u062f\u06d5\u0646\u06ce\u06cc\u0646 \u06a9\u06d5 \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u062f\u0627 \u0628\u06d5\u062f\u0641\u06d5\u0695\u0646\u061f \u06cc\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646 \u0648\u06d5\u06a9 \u06af\u0648\u0646\u0627\u0647\u0628\u0627\u0631\u0627\u0646 \u062f\u06d5\u0698\u0645\u06ce\u0631\u06cc\u0646\u061f',
    '\u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5 \u067e\u0695 \u0644\u06d5 \u067e\u06cc\u062a \u0648\u067e\u06cc\u0631\u06c6\u0632\u06d5\u0645\u0627\u0646 \u0628\u06d5 \u062e\u06d5\u06b5\u0627\u062a \u0628\u06c6 \u062a\u06c6 \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u060c \u062a\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0695\u0627\u0628\u0645\u06ce\u0646\u0646 \u0648 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06b5\u0627\u0646 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0644\u06ce \u0648\u06d5\u0631\u06af\u0631\u0646.',
    '\u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646\u0645\u0627\u0646 \u0628\u06d5\u062e\u06d5\u06b5\u0627\u062a \u0628\u06d5\u062e\u0634\u06cc \u0628\u06d5\u062f\u0627\u0648\u0648\u062f. \u0626\u0627\u06cc \u06a9\u06d5\u0686\u06d5\u0646\u062f \u0639\u06d5\u0628\u062f\u06ce\u06a9\u06cc \u0628\u0627\u0634 \u0628\u0648\u0648! \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0647\u06d5\u0631 \u0695\u0648\u0648\u06cc \u062f\u06d5\u0645\u06d5\u0628\u0648\u0648.',
    '\u0633\u06d5\u0631\u0644\u06d5\u0646\u06af\u06c6\u0631\u06ce \u0648\u06d5\u062e\u062a\u06ce \u0626\u06d5\u0633\u067e\u0627\u0646\u06cc \u0695\u06d5\u0633\u06d5\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0631 \u0686\u0627\u0648\u0627\u0646\u06cc \u0695\u0627\u062f\u06d5\u0646\u0627\u060c',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u06cc\u0646\u06cc \u0695\u06d5\u0648\u06d5\u06a9\u06d5\u0645 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u0631\u062f\u0645\u061b \u062a\u0627 \u062e\u06c6\u0631 \u0644\u06d5 \u0626\u0627\u0633\u06c6 \u0646\u06d5\u062f\u06cc\u0648 \u0628\u0648\u0648.',
    '\u0628\u06cc\u0627\u0646 \u06af\u06ce\u0695\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u062a\u06cc\u062e\u060c \u0645\u0686\u0648\u0648\u0646 \u0648 \u0645\u0644\u06cc\u0627\u0646 \u067e\u06d5\u0695\u0627\u0646\u062f\u0646 [\u0648 \u06a9\u0631\u0627\u0646\u06d5 \u062e\u06ce\u0631].',
    '\u0626\u06ce\u0645\u06d5 \u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u062a\u0627\u0642\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u0644\u0627\u0634\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0647\u0627\u0648\u06cc\u0634\u062a\u06d5 \u0628\u0627\u0646 \u06a9\u0648\u0631\u0633\u06cc\u06d5\u06a9\u06d5\u06cc \u0648 \u062f\u06cc\u0633\u0627\u0646 \u0647\u06d5\u0631 \u0695\u0648\u0648\u06cc \u06a9\u0631\u062f\u06d5 \u0626\u06ce\u0645\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u0645! \u0639\u0627\u0641\u0648\u0648\u0645 \u0628\u06a9\u06d5 \u0648 \u0633\u0627\u0645\u0627\u0646\u06ce\u06a9\u06cc \u0648\u0627\u0645 \u0628\u062f\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0645\u0646 \u06af\u06cc\u0631 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u0646\u06d5\u06a9\u06d5\u0648\u06ce\u061b \u0628\u06d5\u062e\u0634\u06d5\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u06af\u06d5\u0648\u0631\u06d5 \u062a\u06c6\u06cc.',
    '\u0626\u06ce\u0645\u06d5\u0634 \u0628\u0627\u0645\u0627\u0646 \u0628\u06c6 \u06a9\u06d5\u0648\u06cc \u06a9\u0631\u062f\u061b \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648 \u2013 \u0628\u06d5 \u0646\u06d5\u0631\u0645\u06cc \u2013 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u0648\u06ce\u06cc\u06d5\u06a9 \u0628\u06cc\u0648\u06cc\u0633\u062a\u0627\u06cc\u06d5 \u062f\u06d5\u06a9\u06d5\u0648\u062a\u06d5 \u0695\u06ce.',
    '\u0647\u06d5\u0645\u0648\u0648 \u062c\u0633\u0646\u06d5 \u0634\u06d5\u06cc\u0627\u062a\u06cc\u0646\u06cc \u067e\u0627\u06b5\u06d5 \u0644\u06d5 \u0642\u0648\u0695\u06d5\u06a9\u0627\u0631\u06cc\u062f\u0627 \u0648 \u0628\u06cc\u0646\u06d5\u06a9\u06ce\u0634\u06cc\u0634\u060c',
    '\u0647\u06ce\u0646\u062f\u06ce \u062a\u0631\u06cc\u0634 \u2013 \u06a9\u06d5 \u062a\u06c6\u0642 \u0644\u06d5 \u0645\u0644 \u2013 \u0647\u0627\u062a\u0628\u0648\u0648\u0646\u06d5 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc.',
    '\u0633\u0627\u0645\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc\u0648\u06cc \u06a9\u06d5 \u0644\u06d5 \u0698\u0645\u0627\u0631\u06d5 \u062f\u06d5\u0631\u0686\u0648\u0648\u06d5\u061b \u0633\u0627 \u062a\u06c6\u06cc\u0627\u0646 \u0686\u0627\u06a9\u06d5\u06cc \u0644\u06ce \u0628\u06a9\u06d5\u060c \u06cc\u0627\u0646 \u0695\u0627\u06cc \u0628\u06af\u0631\u06d5.',
    '\u0633\u0648\u0644\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u0646\u0632\u06cc\u06a9\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5 \u0648 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0686\u0627\u06a9\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u0626\u06d5\u06cc\u06cc\u0648\u0628\u06cc\u0634 \u2013 \u06a9\u06d5 \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0648\u0648 \u2013 \u0628\u06cc\u0631 \u0628\u06a9\u06d5\u0648\u06d5. \u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u062f\u0627 \u06a9\u06d5 \u0647\u0627\u0648\u0627\u0631\u06cc \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u0647\u06ce\u0646\u0627: \u0626\u06d5\u0648\u0627 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0645\u0646\u06cc \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631 \u0648 \u0644\u06d5\u0634 \u0628\u06d5\u0628\u0627\u0631\u06cc \u06a9\u0631\u062f.',
    '\u067e\u0627\u06cc \u062e\u06c6\u062a \u0628\u0648\u06a9\u062a\u06d5 \u0628\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u0627\u060c \u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u0633\u06d5\u0631\u0686\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06d5 \u0628\u06c6 \u0645\u06d5\u0644\u06d5\u06a9\u0631\u062f\u0646 \u062f\u06d5\u0633\u062a \u062f\u06d5\u062f\u0627 \u0648 \u0686\u0627\u06cc\u06af\u06cc\u0634\u06d5\u060c \u062f\u06d5\u0634\u06a9\u0631\u06ce \u0644\u06ce\u06cc \u0628\u062e\u06c6\u06cc\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5\u0648\u06d5\u060c \u0648\u0631\u062f \u0648 \u062f\u0631\u0634\u062a\u06cc \u062e\u06ce\u0632\u0627\u0646\u06cc \u0648 \u0647\u06ce\u0646\u062f\u06d5\u06cc \u062a\u0631\u06cc\u0634\u0645\u0627\u0646 \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc\u061b \u062a\u0627 \u0628\u0628\u06ce\u062a\u06d5 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632\u0627\u0646.',
    '\u0642\u0648\u0644\u06ce \u062a\u0648\u06b5\u06cc \u0648\u0631 \u062f\u0648 \u0632\u0631\u0627\u0648 \u0628\u06af\u0631\u06d5 \u062f\u06d5\u0633\u062a\u062a \u0648 \u067e\u06ce \u0644\u06ce\u062f\u06d5\u060c \u0628\u0627 \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5\u062a \u0644\u06ce \u0646\u06d5\u06a9\u06d5\u0648\u06ce. \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0645\u0627\u0646 \u0628\u06d5 \u067e\u06cc\u0627\u0648\u06ce\u06a9\u06cc \u0628\u06d5 \u062d\u06d5\u0648\u0633\u06d5\u0644\u06d5 \u0647\u0627\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u061b \u0639\u06d5\u0628\u062f\u06cc \u0686\u0627\u06a9 \u0628\u0648\u0648\u061b \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0626\u06d5\u0648 \u062a\u06c6\u0628\u06d5\u06a9\u0627\u0631 \u0628\u0648\u0648.',
    '\u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646: \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06cc\u0633\u062d\u0627\u0642 \u0648 \u06cc\u06d5\u0639\u0642\u0648\u0648\u0628\u06cc\u0634 \u0648\u06d5\u0628\u06cc\u0631 \u0628\u06ce\u0631\u0627\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a \u0648 \u062f\u06b5 \u0695\u06c6\u0634\u0646 \u0628\u0648\u0648\u0646.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06cc\u0631 \u062e\u06d5\u06b5\u06a9 \u062f\u06ce\u0646\u0627\u06cc\u06d5\u0648\u06d5\u060c \u0628\u0648\u0648\u0646\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc\u0645\u0627\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0644\u06d5 \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u06d5 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0626\u06cc\u0633\u0645\u0627\u0639\u06cc\u0644 \u0648 \u0632\u06d5\u0644\u06a9\u06cc\u0641\u0644 \u0648 \u0626\u06d5\u0644\u06cc\u06d5\u0633\u06d5\u0639\u06cc\u0634\u062a \u0644\u06d5 \u0628\u06cc\u0631 \u0646\u06d5\u0686\u06ce\u061b \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u06cc\u0627\u0648\u0686\u0627\u06a9\u0627\u0646\u0646.',
    '\u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc\u06d5\u06a9\u06d5 \u0648\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u06ce \u062a\u0631\u0633 \u0628\u0648\u0648\u0646\u060c \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u062c\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u062e\u06c6\u0634\u06d5.',
    '\u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06d5 \u0646\u06d5\u0645\u0627\u0646\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0646\u0627\u06cc\u06d5 \u0648 \u062f\u06d5\u0631\u06af\u0627\u06cc\u0627\u0646 \u0628\u06c6 \u06a9\u0631\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0648\u06ce \u067e\u0627\u06b5\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5\u061b \u0647\u06d5\u0631 \u0645\u06cc\u0648\u06d5 \u0648 \u06a4\u06d5\u062e\u0627\u0631\u0646\u06ce\u06a9\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u06a9\u06d5\u0646\u060c \u062f\u0627\u0648\u0627\u06cc \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0632\u06c6\u0631 \u0645\u06ce\u0648\u06cc\u0646\u06d5\u06cc \u0647\u0627\u0648\u062a\u06d5\u0645\u06d5\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0646\u0627\u0645\u06c6 \u0686\u0627\u0648 \u062f\u0627\u062f\u06d5\u062e\u06d5\u0646.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u062a\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627 \u0628\u0648\u0648\u061b \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u062f\u06d5\u0648\u062f\u06d5\u0646\u06ce.',
    '\u0626\u06d5\u0645\u06d5 \u0628\u0698\u06cc\u0648\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0648\u0634\u06a9\u0627\u0648\u06cc\u0634\u06cc \u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0645\u06d5 \u0626\u0627\u0648\u0627. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u06cc \u0626\u06ce\u0645\u06d5 \u0644\u0627 \u062f\u06d5\u062f\u06d5\u0646\u060c \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u0634\u0648\u06ce\u0646\u06ce \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e.',
    '\u062f\u0648\u0695\u062f\u06c6\u0632\u06d5\u0642\u06d5 \u0648 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5.',
    '\u0628\u0627 \u062f\u06d5\u0648\u06ce\u062f\u0627 \u0626\u0627\u0648\u0627 \u06a9\u06d5\u0644\u06cc \u0648 \u06a9\u06ce\u0645 \u0648 \u0632\u0648\u0648\u062e\u0627\u0648 \u0628\u062e\u06c6\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0645 \u062a\u06d5\u0631\u0632\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u0627\u0646\u06d5\u060c \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0648\u06ce\u0646\u06d5\u06cc \u062a\u0631\u06cc\u0634 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0645 \u06af\u0631\u06c6\u06cc\u06d5\u0634 \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0632\u06c6\u0631 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5 \u062f\u06ce\u0646\u06d5 \u0646\u0627\u0648\u06cc\u061b \u0626\u0627\u06cc \u0628\u06d5 \u062e\u06ce\u0631 \u0646\u06d5\u06cc\u06d5\u0646! \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0626\u06d5\u0634\u06ce \u0647\u06d5\u0631 \u0644\u06d5 \u0626\u0627\u06af\u0631\u062f\u0627 \u0628\u0646.',
    '\u0626\u06ce\u0698\u0646: \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5 \u062e\u06ce\u0631 \u0646\u06d5\u06cc\u06d5\u0646! \u0626\u06ce\u0648\u06d5\u0646 \u067e\u06ce\u0634\u06d5\u06a9\u06cc \u0626\u06d5\u0648 \u0634\u0648\u06ce\u0646\u06d5 \u062e\u0631\u0627\u067e\u06d5 \u0648 \u0628\u06c6 \u0645\u06d5 \u0695\u06d5\u0686\u0627\u0648 \u06a9\u0631\u062f. \u0626\u06d5\u0646\u0648\u0627\u06cc\u06d5\u06a9\u06cc \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e\u06d5!',
    '\u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u06a9\u06ce \u067e\u06ce\u0634\u06d5\u06a9\u06cc \u0626\u06d5\u0645\u06d5\u06cc \u0628\u06c6 \u0626\u06ce\u0645\u06d5 \u0633\u0627\u0632 \u062f\u0627\u0648\u06d5\u060c \u062a\u06c6 \u062f\u0648\u0648\u0633\u06d5\u0631\u06d5 \u0628\u06d5 \u0626\u0627\u06af\u0631 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u062f\u06d5.',
    '\u062f\u06d5\u0634\u06b5\u06ce\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648 \u067e\u06cc\u0627\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u067e\u06cc\u0627\u0648\u062e\u0631\u0627\u067e\u0645\u0627\u0646 \u062f\u06d5\u0698\u0645\u0627\u0631\u062f\u0646\u060c \u0628\u06c6\u0686 \u0646\u0627\u0628\u06cc\u0646\u06cc\u0646\u061f',
    '\u06a9\u06d5 \u06af\u0627\u06b5\u062a\u06d5\u0645\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u0631\u062f\u0646\u061b \u06cc\u0627\u0646 \u0686\u0627\u0648 \u0644\u06d5 \u062f\u06cc\u062a\u0646\u06cc\u0627\u0646 \u06a9\u0648\u0644\u06d5\u061f',
    '\u0626\u06d5\u0645 \u06a9\u06ce\u0634\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u062f\u06c6\u0698\u0647\u06d5 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0626\u06d5\u0634\u06ce \u0647\u06d5\u0631 \u0648\u06d5\u062f\u06cc \u0628\u06ce.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u062e\u06c6\u0645 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06ce\u06a9\u0645\u061b \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u06cc \u062a\u0627\u06a9 \u0648 \u062a\u06d5\u0646\u06cc\u0627\u06cc \u0632\u0627\u06b5 \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u062f\u0627\u060c \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644 \u06d5\u0646\u06ce\u0648\u0627\u0646\u06cc\u0627\u0646 \u062f\u0627\u06cc\u06d5\u060c \u06a9\u06c6\u06b5 \u0646\u06d5\u062f\u06d5\u0631 \u0648 \u0644\u06ce \u0628\u0648\u0631\u062f\u06d5\u06cc\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0645\u06d5 \u062e\u06d5\u0628\u06d5\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u06af\u0631\u06cc\u0646\u06af\u06d5\u061b',
    '\u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062e\u06c6\u06cc \u0644\u06ce \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u06a9\u0627\u062a\u06ce \u0628\u0627\u06b5\u0627\u0646\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646 \u0628\u0646\u0627\u0634\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u067e\u06d5\u06cc\u062f\u0627 \u0628\u0648\u0648\u060c \u0645\u0646 \u0647\u06cc\u0686\u0645 \u0644\u06ce \u0646\u06d5\u0632\u0627\u0646\u06cc\u0648\u06d5.',
    '\u0628\u06c6\u06cc\u06d5 \u067e\u06ce\u0645 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u06d5\u0646\u0631\u06ce\u060c \u0686\u0648\u0646\u06a9\u06d5 \u062f\u06d5\u0628\u06ce \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u06ce\u0646\u0645.',
    '\u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062a\u06c6 \u0628\u06d5 \u0641\u0631\u06cc\u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f: \u0648\u0627 \u062e\u06d5\u0631\u06cc\u06a9\u0645 \u0645\u0631\u06c6\u06cc\u06d5\u06a9 \u0644\u06d5 \u0642\u0648\u0695 \u0686\u06ce \u0628\u06a9\u06d5\u0645\u061b',
    '\u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5\u0634\u06cc\u0645 \u062a\u06d5\u0648\u0627\u0648 \u06a9\u0631\u062f \u0648 \u0644\u06d5 \u06af\u06cc\u0627\u0646\u06cc \u062e\u06c6\u0645 \u0641\u0648\u0648\u0645 \u067e\u06ce\u062f\u0627 \u06a9\u0631\u062f\u060c \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0633\u0648\u0698\u062f\u06d5\u06cc \u0628\u06c6 \u0628\u06d5\u0631\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0641\u0631\u06cc\u0634\u062a\u06d5\u06a9\u0627\u0646 \u06af\u0634\u062a\u06cc\u0627\u0646 \u0633\u0648\u0698\u062f\u06d5\u06cc\u0627\u0646 \u0628\u0631\u062f\u061b',
    '\u0626\u06cc\u0628\u0644\u06cc\u0633 \u0646\u06d5\u0628\u06ce. \u062e\u06c6\u06cc \u0628\u06d5 \u0632\u0644 \u062f\u06cc \u0648 \u0633\u067e\u06b5\u06d5\u06cc\u06cc \u06a9\u0631\u062f.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0626\u06cc\u0628\u0644\u06cc\u0633! \u0686\u06cc \u0646\u06d5\u06cc\u0647\u06ce\u0634\u062a \u0633\u0648\u0698\u062f\u06d5 \u0628\u06d5\u0631\u06cc\u06d5 \u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u062f\u06d5\u0633\u062a\u06a9\u0631\u062f\u06cc \u062e\u06c6\u0645\u06d5\u061f \u0626\u0627\u062e\u06c6 \u0641\u06cc\u0632\u062a \u0695\u06ce\u06af\u06d5\u06cc \u0646\u06d5\u062f\u0627\u06cc\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u06d5\u06a9\u0627\u0646\u06cc\u061f',
    '\u06af\u0648\u062a\u06cc: \u062f\u06cc\u0627\u0631\u06d5 \u0645\u0646 \u062e\u06c6\u0645 \u0644\u06d5\u0648 \u0686\u0627\u06a9\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u0645\u060c \u0645\u0646 \u0644\u06d5 \u0626\u0627\u06af\u0631\u060c \u0626\u06d5\u0648\u062a \u0644\u06d5 \u0642\u0648\u0695 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u06af\u0648\u062a\u06cc: \u0644\u06ce\u0631\u06d5 \u0628\u0695\u06c6 \u062f\u06d5\u0631\u06ce\u060c \u0644\u06d5 \u0626\u06ce\u0633\u062a\u06d5\u0648\u06d5 \u062f\u06d5\u0631\u06a9\u0631\u0627\u0648\u06cc\u061b',
    '\u0646\u06d5\u0641\u0631\u06cc\u0646\u06cc \u0645\u0646\u062a \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06ce\u060c \u062a\u0627 \u0695\u06c6\u0698\u06ce \u0633\u06d5\u06b5\u0627 \u0695\u0627\u062f\u06d5\u0628\u06ce.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0642\u0627\u0646\u0645 \u0628\u062f\u06d5 \u062a\u0627 \u0695\u06c6\u0698\u06d5\u06a9\u06d5\u06cc \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u062a\u06c6 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0645\u06d5\u0648\u062f\u0627\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5\u061b',
    '\u0647\u06d5\u062a\u0627 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0648 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648.',
    '\u06af\u0648\u062a\u06cc: \u062f\u06d5\u0633\u0627 \u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u062a\u06c6\u060c \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631\u06cc\u0627\u0646 \u06af\u0648\u0645\u0695\u0627 \u062f\u06d5\u06a9\u06d5\u0645\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u067e\u0627\u06a9\u06cc \u062f\u06d5\u062a \u067e\u06d5\u0631\u0633\u062a\u0646.',
    '\u06af\u0648\u062a\u06cc: \u0633\u0627 \u0695\u0627\u0633\u062a \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0645\u0646 \u062f\u06d5\u0628\u06ce\u0698\u0645 \u0695\u0627\u0633\u062a\u06d5.',
    '\u062f\u06b5\u0646\u06cc\u0627\u0628\u06d5\u060c \u062a\u06c6\u0634 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0634\u0648\u06ce\u0646 \u062a\u06c6 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u060c \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u062a\u0627\u0646 \u0644\u06ce \u067e\u0695\u062f\u06d5\u06a9\u06d5\u0645.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0647\u06cc\u0686 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u0645 \u0644\u06ce\u062a\u0627\u0646 \u0646\u0627\u0648\u06ce\u061b \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0646\u06cc\u0645 \u06a9\u06d5 \u062f\u0631\u06c6 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646.',
    '\u0626\u06d5\u0645\u06d5 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc \u0648 \u067e\u06d5\u0646\u062f\u06d5 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u067e\u0627\u0634 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u06cc \u0695\u0627\u0633\u062a\u06cc \u0626\u06d5\u0648\u062a\u0627\u0646 \u0628\u06d5\u0644\u0627\u0648\u06d5 \u0695\u0648\u0648\u0646 \u062f\u06d5\u0628\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06d5 \u0648 \u0647\u0627\u062a\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631.',
    '\u0645\u06d5 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u0645\u0627\u0646 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0628\u06c6 \u062a\u06c6 \u0646\u0627\u0631\u062f. \u0633\u0627 \u062a\u06c6 \u0647\u06d5\u0631 \u062e\u0648\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u06d5 \u0648 \u0628\u06d5 \u062f\u06b5\u067e\u0627\u06a9\u06cc \u0647\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648 \u0628\u06d5.',
    '\u062a\u06c6 \u0628\u0632\u0627\u0646\u06d5 \u062f\u06cc\u0646\u06cc \u067e\u0627\u06a9\u0698 \u0647\u06d5\u0631 \u0647\u06cc \u062e\u0648\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u062c\u06ce \u0626\u06d5 \u0648 \u06af\u06d5\u0648\u0631\u06d5\u06af\u06d5\u0644\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06cc\u0648\u06d5\u062a\u06d5\u0648\u06d5\u060c \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646\u060c \u0644\u06d5 \u062e\u0648\u0627 \u0646\u0632\u06cc\u06a9\u0645\u0627\u0646 \u06a9\u06d5\u0646\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0648 \u062f\u0648\u0648\u0628\u06d5\u0631\u06d5\u06a9\u0627\u06cc\u06d5\u062a\u06cc\u06cc\u06d5 \u0695\u0627\u062f\u06d5\u06af\u0627\u060c \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0645\u0631\u06c6\u06cc \u062f\u0631\u06c6\u0632\u0646\u06cc \u0633\u067e\u06b5\u06d5\u060c \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627 \u0646\u0627\u06a9\u0627.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u06cc\u0648\u06cc\u0633\u062a\u0627\u06cc\u06d5 \u0641\u0631\u0632\u06d5\u0646\u062f\u06ce \u0628\u06c6 \u062e\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0627\u060c \u0644\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06d5\u06cc \u062f\u0631\u0648\u0633\u062a\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0628\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u06b5\u06cc \u062f\u06d5\u0628\u0698\u0627\u0631\u062f. \u067e\u0627\u06a9\u06cc \u0648 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u0628\u06c6 \u0626\u06d5\u0648\u06d5. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u062e\u0648\u0627\u06cc \u062a\u0627\u06a9 \u0648 \u062a\u06d5\u0646\u06cc\u0627\u06cc \u0632\u0627\u06b5 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06ce\u06a9\u0627.',
    '\u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0628\u06d5 \u0695\u0627\u0633\u062a \u0626\u06d5\u0648 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0634\u06d5\u0648\u06ce \u062f\u06d5\u0695\u06c6\u0698\u06ce \u062f\u06d5\u067e\u06ce\u0686\u06ce \u0648 \u0695\u06c6\u0698 \u062f\u06d5\u0634\u06d5\u0648\u06ce\u061b \u062e\u06c6\u0631 \u0648 \u0647\u06d5\u06cc\u06a4\u06cc\u0634\u06cc \u06a9\u06d5\u0648\u06cc \u06a9\u0631\u062f\u061b \u0647\u06d5\u0631\u06a9\u0627\u0645\u06cc \u062a\u0627 \u06a9\u0627\u062a\u06cc \u062f\u06cc\u0627\u0631\u062f\u06d5 \u062f\u06ce\u0646 \u0648 \u062f\u06d5\u0686\u0646. \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646! \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u062e\u06d5\u062a\u0627\u067e\u06c6\u0634 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0626\u06ce\u0648\u06d5\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u062a\u0627\u0642\u06d5 \u06a9\u06d5\u0633\u06ce\u06a9 \u0647\u06ce\u0646\u0627\u06cc\u06d5\u062f\u06cc \u0648 \u0698\u0646\u06d5\u06a9\u06d5\u0634\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u0645\u0627\u06a9\u06cc \u0626\u06d5\u0648 \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f. \u0647\u06d5\u0634\u062a \u0648\u06b5\u0641\u06cc\u0634\u06cc \u0644\u06d5 \u067e\u0627\u062a\u0627\u06b5 \u0628\u06c6 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0646. \u0626\u06ce\u0648\u06d5\u0634 \u0626\u0627\u0648\u0627 \u062f\u0631\u0648\u0633 \u062f\u06d5\u06a9\u0627\u060c \u06a9\u06d5 \u0644\u06d5 \u0632\u06af\u06cc \u062f\u0627\u06cc\u06a9\u0627\u0646\u0648\u0648\u062f\u0627 \u2013 \u062c\u0627\u0631 \u0644\u06d5 \u062f\u0648\u0648 \u062c\u0627\u0631 \u2013 \u0644\u06d5 \u0633\u06ce \u0642\u06c6\u0646\u0627\u063a\u06cc \u062a\u0627\u0631\u06cc\u06a9\u062f\u0627\u060c \u0647\u06d5\u06b5\u06af\u06ce\u0695 \u0648\u06d5\u0631\u06af\u06ce\u0695\u062a\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u0627. \u0626\u06d5\u0648\u06d5 \u062e\u0648\u062f\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646\u061b \u0641\u06d5\u0631\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0626\u06d5\u0648\u062f\u0627 \u0648 \u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0628\u06ce. \u062c\u0627 \u06a9\u06d5\u0648\u0627\u06cc\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u06a9\u0648\u06ce\u062a\u0627\u0646 \u0648\u06d5\u0631\u062f\u06d5\u06af\u06ce\u0695\u0646\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646\u060c \u062e\u0648\u0627 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5 \u0648 \u0628\u06d5 \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u06cc \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0695\u0627\u0632\u06cc \u0646\u0627\u0628\u06ce. \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u06a9\u06d5 \u0648 \u062f\u06d5\u0686\u0627\u0648\u062f\u0627 \u0628\u06ce\u060c \u0644\u06ce\u0648 \u0695\u0627\u0632\u06cc\u06d5. \u06a9\u06d5\u0633\u06cc\u0634 \u0628\u0627\u0631\u06cc \u06af\u0631\u0627\u0646\u06cc \u06a9\u06d5\u0633 \u0648\u06d5\u0633\u062a\u06c6 \u0646\u0627\u06af\u0631\u06ce. \u0644\u06d5\u0645\u06d5\u0648\u0644\u0627\u0648\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u062a\u0627\u0646 \u0628\u06c6 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0647\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u067e\u06ce\u062a\u0627\u0646 \u0626\u06ce\u0698\u06ce \u0686\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f. \u0644\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0646\u0627\u0648\u062f\u06b5\u0627\u0646\u06cc\u0634 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0645 \u0645\u0631\u06c6\u06cc\u06d5 \u06a9\u06d5 \u062a\u06d5\u0646\u06af\u0627\u0646\u06ce\u06a9\u06cc \u062f\u06d5\u06af\u0627\u062a\u06ce\u060c \u0695\u0648\u0648 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u0627\u062a \u0648 \u0644\u06d5\u0628\u06d5\u0631\u06cc \u062f\u06d5\u067e\u0627\u0695\u06ce\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631\u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u062e\u06c6\u06cc\u06d5\u0648\u0695\u0627 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06d5 \u0626\u06d5\u0648 \u06af\u06d5\u06cc\u0627\u0646\u062f\u060c \u0644\u06d5 \u0628\u06cc\u0631 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u0644\u06d5 \u0628\u06d5\u0631\u06cc \u067e\u0627\u0695\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5 \u0648 \u0634\u06d5\u0631\u06cc\u06a9\u0627\u0646\u06ce\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u062f\u06c6\u0632\u06ce\u062a\u06d5\u0648\u06d5\u060c \u062a\u0627 \u062e\u06d5\u06b5\u06a9\u06cc \u067e\u06ce \u06af\u0648\u0645\u0695\u0627 \u06a9\u0627. \u0628\u06ce\u0698\u06d5: \u0628\u06d5\u0648 \u0633\u067e\u06b5\u06d5\u06cc\u06cc\u06d5\u062a\u06d5\u0648\u06d5\u060c \u06a9\u06d5\u0645\u06ce\u06a9\u06cc \u062a\u0631\u06cc\u0634 \u0695\u0627\u0628\u0648\u06ce\u0631\u06d5\u061b \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u062a\u06c6 \u0644\u06d5 \u06cc\u0627\u0631\u0627\u0646\u06cc \u0626\u0627\u06af\u0631\u06cc.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u06d5 \u0648\u06d5\u0644 \u06a9\u06d5\u0633\u06ce\u06a9\u0627 \u06a9\u06d5 \u0634\u06d5\u0648\u06af\u0627\u0631\u0627\u0646 \u0695\u0648\u0648 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0695\u0627\u0648\u06d5\u0633\u062a\u0627\u0648\u06d5 \u0648 \u0633\u0648\u0698\u062f\u06d5 \u062f\u06d5\u0628\u0627 \u0648 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06d5 \u0648 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u062f\u06d5\u062a\u0631\u0633\u06ce \u0648 \u0647\u06cc\u0648\u0627\u06cc \u0648\u0627\u06cc\u06d5 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u0628\u06a9\u06d5\u0648\u06ce\u060c \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u0632\u0627\u0646\u0627 \u0648 \u0646\u06d5\u0632\u0627\u0646 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631\u0646\u061f \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u062f\u06b5 \u0632\u06cc\u0646\u062f\u0648\u0648\u0646\u060c \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0628\u06cc\u0631 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646! \u0626\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0695\u0648\u0627\u0646! \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u0628\u062a\u0631\u0633\u0646\u061b \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0698\u06cc\u0627\u0646\u06cc \u0626\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u062f\u0627 \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0686\u0627\u06a9\u06d5 \u0647\u06d5\u06cc\u06d5 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u062e\u0648\u062f\u0627 \u0641\u0695\u06d5\u0647\u06d5. \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631\u0646\u060c \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u0648 \u0628\u06d5 \u0628\u06ce \u062d\u06cc\u0633\u0627\u0628 \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u067e\u06ce\u0645 \u0633\u067e\u06ce\u0631\u0627\u0648\u06d5 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9 \u062e\u0648\u062f\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0645 \u0648 \u0628\u06d5 \u067e\u0627\u06a9\u0698\u06cc \u0647\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648 \u0628\u0645.',
    '\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634\u0645 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5 \u06a9\u06d5 \u06cc\u06d5\u06a9\u06d5\u0645\u06cc\u0646 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646 \u0628\u0645.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645 \u062f\u06d5\u0631\u0628\u0686\u0645\u060c \u0644\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5 \u062f\u06d5\u062a\u0631\u0633\u0645.',
    '\u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u067e\u06d5\u0631\u0633\u062a\u0645 \u0648 \u0628\u06d5 \u067e\u0627\u06a9\u0698\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u0628\u0645.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0648\u06d5\u0634 \u2013 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627- \u0686\u06cc\u062a\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a\u060c \u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u062a\u06c6 \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0646 \u06a9\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce\u062f\u0627\u060c \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0648 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06c6\u0695\u06ce\u0646\u0646\u061b \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0645\u06d5 \u0632\u06cc\u0627\u0646\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5.',
    '\u062a\u0648\u06ce \u0644\u06d5\u0633\u06d5\u0631 \u062a\u0648\u06ce \u06a9\u06b5\u067e\u06d5\u06cc \u0626\u0627\u06af\u0631- \u0648\u06ce\u0646\u06d5\u06cc \u0686\u06d5\u062a\u0631- \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u062f\u0627 \u0648\u06ce\u0633\u062a\u0627\u0648\u06d5\u061b \u0644\u06d5 \u0698\u06ce\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u0645 \u062a\u06d5\u0631\u0632\u06d5. \u062e\u0648\u062f\u0627 \u0628\u06d5\u0645\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u062a\u0631\u0633\u06ce\u0646\u06ce. \u0626\u06d5\u06cc \u0639\u06d5\u0628\u062f\u0627\u0646\u0645! \u0644\u06ce\u0645 \u0628\u062a\u0631\u0633\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0633\u062a\u0646\u06cc \u0628\u062a\u06d5\u06a9\u0627\u0646 \u062a\u06d5\u06a9\u06cc\u0648\u0646\u06d5\u0648\u06d5 \u0648\u060c \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u06af\u06d5\u0695\u0627\u0648\u0646\u06d5\u0648\u06d5\u060c \u062e\u06c6\u0634\u06cc \u0644\u06d5\u0648\u0627\u0646. \u0633\u0627 \u062a\u06c6\u0634 \u0628\u06d5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646 \u0645\u0698\u062f\u06d5 \u0628\u062f\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06af\u0648\u06ce \u0644 \u06d5\u0642\u0633\u0627\u0646 \u0695\u0627\u062f\u06d5\u062f\u06ce\u0631\u0646 \u0648 \u0628\u06d5 \u0634\u0648\u06ce\u0646 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0627\u0646\u062f\u0627 \u062f\u06d5\u0686\u0646\u060c \u0626\u06d5\u0648\u0627\u0646 \u062e\u0648\u062f\u0627 \u0695\u06ce\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0648\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632\u0646.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06c6 \u0628\u0695\u06cc\u0627\u0631 \u062f\u0631\u0627\u060c \u062a\u06c6 \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u2013 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631\u062f\u0627 \u0628\u06ce \u2013 \u0695\u0632\u06af\u0627\u0631\u06cc \u06a9\u06d5\u06cc\u061f',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u06cc \u0645\u06d5\u062a\u0631\u0633\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5\u060c \u0648\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0686\u06d5\u0646\u062f \u0626\u06c6\u062f\u06d5\u06cc\u06d5\u06a9\u062f\u0627\u060c \u0686\u06cc\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0686\u06cc\u0646 \u0633\u0627\u0632 \u06a9\u0631\u0627\u0648\u0646\u060c \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627. \u0626\u06d5\u0645\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0628\u06d5\u06b5\u06ce\u0646\u06ce \u0628\u06cc\u062f\u0627\u060c \u0644\u06ce\u06cc \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u0646\u0627\u0628\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u062f\u06cc \u0646\u06d5\u062a\u062f\u06cc\u060c \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648 \u0628\u06b5\u0646\u062f\u0627\u06cc\u06cc\u06d5\u0648\u06d5 \u0626\u0627\u0648 \u062f\u06d5\u0646\u06ce\u0631\u06ce \u0648 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0644\u06d5 \u0633\u06d5\u0631\u0686\u0627\u0648\u06d5\u06a9\u0627\u0646\u06d5\u0648\u06d5 \u0695\u06d5\u0648\u0627\u0646\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u06a9\u0634\u062a \u0648\u06a9\u0627\u06b5\u06cc \u0647\u06d5\u0645\u06d5\u0695\u06d5\u0646\u06af\u06d5\u06cc \u067e\u06ce \u062f\u06d5\u0695\u0648\u06ce\u0646\u06ce \u0648 \u067e\u0627\u0634 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0632\u06d5\u0631\u062f \u0648 \u0648\u0634\u06a9 \u0647\u06d5\u06b5\u062f\u06d5\u06af\u06d5\u0695\u06ce \u0648 \u0626\u06d5\u0648\u0633\u0627 \u062f\u06d5\u06cc\u06a9\u0627\u062a\u06d5 \u067e\u0648\u0648\u0634 \u0648 \u067e\u0627\u0631\u061f \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5\u0645\u06d5 \u067e\u06d5\u0646\u062f \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u0646.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u062e\u0648\u062f\u0627 \u062f\u06b5\u06cc \u0628\u06c6 \u0626\u06cc\u0633\u0644\u0627\u0645 \u06af\u0648\u0634\u0627\u062f \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u062f\u06cc\u0627\u0631\u06d5 \u0628\u06d5\u0631 \u062a\u06cc\u0634\u06a9\u06cc \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u06a9\u06d5\u0648\u062a\u0648\u0648\u06d5\u060c \u06a9\u06d5\u06cc \u062f\u06d5\u0628\u06ce \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06ce \u06a9\u06d5 \u062f\u06b5\u0695\u06d5\u0642\u0646\u061f \u0647\u0627\u0648\u0627\u0631\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u062f\u06b5\u0695\u06d5\u0642\u0646 \u0644\u06d5 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06af\u0648\u0645 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u062e\u0648\u0627 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u0642\u0633\u06d5\u06cc \u0647\u06d5\u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce: \u06a9\u062a\u06ce\u0628\u06ce \u06a9\u06d5] \u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc \u0644\u06d5 \u062c\u0648\u0627\u0646\u06cc\u06cc\u0627 [\u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u0646 \u0648 \u0647\u06d5\u0631 \u062f\u0648\u0648\u067e\u0627\u062a\u06d5\u0634 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u0631\u0633\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5\u060c \u0644\u06d5 \u0628\u06cc\u0633\u062a\u0646\u06cc \u0645\u0686\u0648\u0631\u06a9\u06cc\u0627\u0646 \u0628\u06d5 \u0644\u06d5\u0634\u06ce\u062f\u0627 \u062f\u06ce \u0648 \u067e\u0627\u0634\u0627\u0646 \u062f\u06b5 \u0648 \u06a9\u06d5\u0644\u06d5\u062e\u06cc\u0627\u0646- \u0628\u06d5\u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627- \u0626\u06c6\u0642\u0631\u06d5 \u062f\u06d5\u06af\u0631\u06ce. \u0626\u06d5\u0645\u06d5 \u0695\u06ce\u0646\u0648\u06ce\u0646\u06cc \u062e\u0648\u0627\u06cc\u06d5. \u06a9\u06ce \u067e\u06ce \u062e\u06c6\u0634 \u0628\u06ce\u060c \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u062f\u06d5\u06a9\u0627\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u062e\u0648\u0627 \u06af\u0648\u0645\u0695\u0627\u06cc \u0628\u06a9\u0627\u060c \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0646\u0627\u06a9\u0627.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u0646\u0627\u0686\u0627\u0631 \u062f\u06d5\u0628\u06ce \u0633\u0632\u0627\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc \u067e\u06d5\u0633\u06b5\u0627\u0646\u06ce\u060c \u0628\u06d5 \u0686\u0627\u0631\u06d5\u06cc \u062e\u06c6\u06cc \u0644\u06d5 \u062e\u06c6 \u0644\u0627\u062f\u0627\u060c \u062f\u06d5\u0628\u06ce \u0686\u06c6\u0646 \u0628\u06ce\u061f \u0628\u06d5 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u06cc\u0634 \u062f\u06d5\u0648\u062a\u0631\u06ce: \u062f\u06d5\u0628\u0686\u06ce\u0698\u0646 \u0633\u0632\u0627\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646!',
    '\u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0645\u0627\u0646\u06cc\u0634 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0646\u06d5\u0647\u06ce\u0646\u0627\u061b \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06d5\u0648\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0628\u0627\u0631\u06cc \u06a9\u06d5 \u0647\u06d5\u0631 \u0647\u06d5\u0633\u062a\u06cc\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f.',
    '\u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u062f\u06cc \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627\u062f\u0627 \u067e\u06ce \u0686\u06d5\u0634\u0627\u0646\u062f\u0646\u061b \u06af\u06d5\u0631 \u0628\u0632\u0627\u0646\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u0645\u06d5\u0632\u0646 \u062a\u0631\u06d5.',
    '\u0648\u0627\u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u062f\u0627 \u0628\u06c6 \u0626\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062c\u06c6\u0631\u06d5 \u0646\u06d5\u0632\u06cc\u0644\u06d5\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u06cc\u06d5\u0648\u06d5\u061b \u0634\u0627\u06cc\u06d5\u062a \u067e\u06d5\u0646\u062f\u06cc \u0644\u06ce \u0648\u06d5\u0631\u06af\u0631\u0646.',
    '\u0642\u0648\u0631\u0626\u0627\u0646\u06ce\u06a9\u06d5 \u0628\u06d5 \u0639\u0627\u0631\u06d5\u0628\u06ce \u0648 \u062e\u06ce\u0686 \u0648\u062e\u0648\u0627\u0631\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646.',
    '\u062e\u0648\u0627 \u062f\u0648\u0648 \u0645\u0631\u06c6 \u0628\u06d5 \u0646\u0645\u0648\u0648\u0646\u06d5 \u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5: \u06cc\u06d5\u06a9 \u062e\u0648\u0644\u0627\u0645\u06cc \u0686\u06d5\u0646\u062f \u06a9\u06d5\u0633\u06cc \u062f\u0698 \u0628\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u06d5\u060c \u0626\u06d5\u0648\u06cc \u062a\u0631\u06cc\u0627\u0646 \u062e\u0648\u0644\u0627\u0645\u06cc \u062a\u0627\u0642\u06d5 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5\u061b \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645 \u062f\u0648\u0648\u0627\u0646\u06d5 \u0648\u06d5\u06a9 \u06cc\u06d5\u06a9\u0646\u061f \u0633\u0648\u067e\u0627\u0633 \u0628\u06c6 \u062e\u0648\u0627\u0633. \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u062a\u06c6\u0634 \u062f\u06d5\u0645\u0631\u06cc \u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u062f\u06d5\u0645\u0631\u0646.',
    '\u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u0627 \u06a9\u06ce\u0634\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06ce\u062a\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648.',
    '\u0633\u0627 \u06a9\u06ce \u0644\u06d5\u0648\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5 \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u0627 \u0647\u06d5\u06b5\u0628\u0628\u06d5\u0633\u062a\u06ce \u0648 \u0626\u06d5\u0645 \u0695\u0627\u0633\u062a\u06d5\u06cc \u06a9\u06d5 \u0628\u06c6\u06cc \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0646\u06d5\u06cc \u0633\u06d5\u0644\u0645\u06ce\u0646\u06ce\u061f \u0626\u0627\u062e\u06c6 \u0628\u06c6 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0644\u06d5 \u062f\u06c6\u0698\u06d5\u0647\u062f\u0627 \u062c\u06ce\u06af\u06d5 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0695\u0627\u0633\u062a\u06d5\u06a9\u06d5\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u0695\u0627\u0633\u062a\u06cc \u0633\u06d5\u0644\u0645\u0627\u0646\u062f\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u062a\u0631\u0633\u0646.',
    '\u0647\u06d5\u0631\u0686\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u06a9\u06d5\u0646\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646 \u062f\u0627\u062a\u06ce\u061b \u067e\u0627\u062f\u0627\u0634\u06cc \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u0647\u06d5\u0631\u0648\u0627 \u062f\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 \u062e\u0631\u0627\u062a\u0631\u06cc\u0646 \u06a9\u0627\u0631\u06ce \u06a9\u06d5\u0648\u0627 \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5 \u0628\u0628\u06d5\u062e\u0634\u06ce\u062a \u0648 \u0628\u0627\u0634\u062a\u0631 \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc\u0627\u0646 \u067e\u06ce\u06a9 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u062f\u0627\u062a\u06ce.',
    '\u062a\u06c6 \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0646\u060c \u062f\u06d5\u062a\u0631\u0633\u06ce\u0646\u0646\u061b \u0645\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0633 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0626\u0627\u06af\u0627\u06cc \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u06cc \u0628\u06ce\u061f \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062e\u0648\u062f\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u0644\u06ce \u0628\u06af\u06c6\u0695\u06ce\u060c \u06a9\u06d5\u0633 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u0695\u06ce\u0628\u06d5\u0631\u06cc \u0628\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u0648\u062f\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u06a9\u0631\u062f\u0628\u06ce\u060c \u06a9\u06d5\u0633 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u06a9\u0644\u06cc \u06a9\u0627. \u0628\u06c6\u0686 \u0646\u0627\u0632\u0627\u0646\u0646 \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u062a\u06c6\u06b5\u06d5\u0633\u06ce\u0646\u06d5\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u067e\u0631\u0633\u06cc: \u06a9\u06ce \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0647\u06d5\u0631\u062f\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u061f \u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06ce\u0698\u0646: \u062e\u0648\u062f\u0627. \u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc- \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u2013 \u062f\u06d5\u06cc \u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0686\u06c6\u0646 \u062f\u06d5\u0628\u06cc\u0646\u0646\u061f \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u06af\u06d5\u0631\u06d5\u06a9\u06cc \u0628\u06ce\u060c \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u0645 \u0628\u06a9\u0627\u060c \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u06b5\u0627\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0645\u0646 \u0644\u0627 \u062f\u06d5\u0628\u06d5\u0646\u061f \u06cc\u0627\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc\u0645 \u0628\u062e\u0627\u060c \u0626\u06d5\u0648\u0627\u0646 \u067e\u06ce\u0634 \u0644\u06d5\u0648 \u0628\u06d5\u0632\u06d5 \u062f\u06d5\u06af\u0631\u0646\u061f \u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u0645 \u0628\u06d5\u0633\u06d5 \u0648 \u062e\u06c6\u0633\u067e\u06ce\u0631\u0627\u0646\u060c \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u06d5\u0648 \u062f\u06d5\u0633\u067e\u06ce\u0631\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u06af\u06d5\u0644\u06c6! \u0626\u06ce\u0648\u06d5 \u06a9\u0627\u0631\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646\u060c \u0645\u0646\u06cc\u0634 \u0648\u0627 \u06a9\u0627\u0631\u06cc \u062e\u06c6\u0645 \u062f\u06d5\u06a9\u06d5\u0645\u061b \u0626\u0627 \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5\u0634 \u062a\u06ce \u062f\u06d5\u06af\u06d5\u0646:',
    '\u06a9\u06ce\u06cc\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u0627\u0628\u0695\u0648\u0648\u0628\u06d5\u0631\u06d5 \u062f\u06ce \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0644\u06d5 \u0628\u0646\u06d5 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u06ce.',
    '\u0626\u06ce\u0645\u06d5\u06cc\u0646 \u06a9\u06d5 \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u0645\u0627\u0646 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06d5\u06a9\u06cc \u0628\u06c6 \u062a\u06c6 \u0695\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u06a9\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0644\u06d5 \u0633\u06c6\u0646\u06af\u06d5\u06cc \u0626\u06d5\u0648\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u06ce\u060c \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u06c6\u06cc \u062f\u06d5\u06af\u0627 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0695\u06ce \u0644\u0627 \u062f\u0627\u060c \u062f\u0698\u06cc \u062e\u06c6\u06cc \u062c\u0648\u0648\u06b5\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5 \u0648\u061b \u062a\u06c6 \u0628\u0631\u06cc\u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0646\u06cc.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062f\u06ce\u0646\u06d5 \u0645\u0631\u062f\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u062e\u06d5\u0648\u0695\u0627 \u0686\u0648\u0648\u0646 \u0648 \u0646\u06d5\u0645\u0631\u062f\u0648\u0648\u0646\u060c \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06af\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0631\u06ce\u061b \u0626\u06d5\u0648\u06cc \u062f\u06cc\u06a9\u06d5\u0634 \u062a\u0627 \u0648\u06d5\u062e\u062a\u06ce\u06a9\u06cc \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648 \u062f\u06ce\u06b5\u06ce\u062a\u06d5\u0648\u06d5. \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0647\u06d5\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0695\u0627\u062f\u06d5\u0645\u06ce\u0646\u0646.',
    '\u0626\u0627\u062e\u06c6 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u062a\u06a9\u0627\u06a9\u0627\u0631\u06af\u06d5\u0644\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061f \u0628\u06ce\u0698\u06d5: \u06af\u06d5\u0631\u0686\u06cc \u0646\u06d5 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5 \u0648 \u0646\u06d5 \u062a\u06ce\u0634 \u062f\u06d5\u06af\u06d5\u0646\u061f',
    '\u0628\u06ce\u0698\u06d5: \u062a\u06a9\u0627 \u2013 \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631 \u2013 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0633\u06d5\u0631 \u0628\u06d5\u0648\u0646. \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u062f\u0627 \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u0628\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0646\u0627\u0648\u06cc \u062e\u0648\u062f\u0627\u06cc \u06cc\u06d5\u06a9\u062a\u0627 \u0628\u06d5 \u06cc\u0627\u062f \u0628\u06a9\u0631\u06ce\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5\u0648 \u062f\u0646\u06cc\u0627 \u0646\u06cc\u06cc\u06d5\u060c \u062f\u06b5\u06cc\u0627\u0646 \u0628\u06d5\u0648 \u0646\u0627\u0648\u06d5 \u062f\u06d5\u0634\u06ce\u0648\u06ce\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0633\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0644\u0627 \u062f\u06d5\u06b5\u06ce\u0646\u060c \u0648\u06d5\u06a9\u06d5\u06cc\u0641 \u062f\u06ce\u0646.',
    '\u062a\u06c6\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u062e\u0648\u062f\u0627\u06cc \u0645\u06d5\u0632\u0646! \u0626\u06d5\u06cc \u062f\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646! \u0632\u0627\u0646\u0627 \u0628\u06d5 \u0646\u06d5\u062f\u06cc\u0627\u0631 \u0648 \u062f\u06cc\u0627\u0631\u0627\u0646! \u062a\u06c6\u06cc \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062e\u06c6\u062a \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc \u0648 \u0626\u06d5\u0648 \u06a9\u06ce\u0634\u06d5\u06cc \u0644\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0628\u0648\u0648\u060c \u06a9\u06c6\u062a\u0627\u06cc \u062f\u06d5\u06a9\u06d5\u06cc.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u06cc\u0634\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0628\u0648\u0648\u06d5\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0647\u06ce\u0646\u062f\u06d5\u06cc \u062a\u0631\u06cc\u0634 \u0647\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06ce\u060c \u0626\u06d5\u06cc\u06a9\u06d5\u0646\u06d5 \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646\u060c \u0644\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062e\u0631\u0627\u067e\u06cc \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u06d5 \u0695\u0632\u06af\u0627\u0631\u06cc\u0627\u0646 \u06a9\u0627\u061b \u0626\u06d5\u0648\u0633\u0627 \u0634\u062a\u06ce\u06a9\u06cc \u0648\u06d5\u0647\u0627\u06cc\u0627\u0646 \u2013 \u0644\u06d5\u062e\u0648\u062f\u0627\u0648\u06d5 \u2013 \u062f\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06cc\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6\u06cc \u0646\u06d5\u0686\u0648\u0648\u06d5.',
    '\u0626\u06d5\u0645\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0628\u0648\u0648 \u06a9\u06d5 \u0686\u06d5\u0646\u062f \u062e\u0631\u0627\u067e\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u061b \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u06af\u0627\u06b5\u062a\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u0631\u062f\u060c \u062f\u06d5\u0648\u0631\u06cc \u06af\u0631\u062a\u0648\u0648\u0646.',
    '\u0633\u0627 \u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06ce \u0645\u0631\u06c6 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9 \u062f\u06ce\u060c \u0644\u06d5 \u0628\u06d5\u0631\u0645\u0627\u0646 \u062f\u06d5\u067e\u0627\u0695\u06ce\u062a\u06d5\u0648\u06d5\u061b \u067e\u0627\u0634\u0627\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u067e\u06ce \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u060c \u0626\u06ce\u0698\u0646: \u0647\u0648\u0646\u06d5\u0631\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5. \u0647\u06cc\u0686 \u0646\u0627\u0632\u0627\u0646\u0646 \u0626\u06d5\u0645\u06d5 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062f\u06cc\u0627\u0631\u06d5 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u06d5\u0632\u0627\u0646\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06ce\u0634 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0628\u0648\u0648\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0642\u0633\u06d5\u06af\u06d5\u0644\u06cc\u0627\u0646\u06d5 \u06a9\u0631\u062f\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u06a9\u0631\u062f\u06cc\u0627\u0646\u060c \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06c6 \u0646\u06d5\u062f\u0627\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0633\u0632\u0627\u06cc \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06cc. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5\u062f\u0627 \u063a\u06d5\u062f\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u0631\u0627\u067e\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0631\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u062e\u06c6 \u0695\u0632\u06af\u0627\u0631 \u0628\u06a9\u06d5\u0646.',
    '\u0686\u06c6\u0646 \u0647\u06ce\u0634\u062a\u0627 \u0646\u06d5\u06cc\u0627\u0646\u0632\u0627\u0646\u06cc\u0648\u06d5 \u06a9\u06d5 \u0628\u0698\u06cc\u0648 \u0628\u06d5\u062f\u06d5\u0633\u062a \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u0632\u06c6\u0631 \u062f\u06d5\u062f\u0627 \u0648 \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u06a9\u06d5\u0645\u061f \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0647\u06d5\u0646 \u0628\u06c6 \u06af\u06d5\u0644\u06ce \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc \u0647\u06d5\u0628\u06ce.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646! \u0626\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u0644\u0627\u062f\u0627\u0646 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u060c \u0646\u0627\u0647\u06d5\u0642\u06cc \u0648 \u0644\u06d5\u062e\u06c6 \u06a9\u0631\u062f\u0648\u0648\u06d5! \u0646\u0627\u0628\u06ce \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u0648\u0627 \u0646\u0627\u0647\u0648\u0645\u06ce\u062f \u0628\u0646\u060c \u0686\u0648\u0646\u06a9\u06d5 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9 \u0686\u0627\u0648\u062f\u06d5\u067e\u06c6\u0634\u06ce\u061b \u0626\u06d5\u0648 \u0644\u06ce\u0628\u0648\u0631\u062f\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0648\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0648 \u062e\u06c6\u062a\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u0648\u06cc \u0628\u0633\u067e\u06ce\u0631\u0646\u060c \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0626\u0627\u0632\u0627\u0631\u062a\u0627\u0646 \u0628\u06c6 \u0628\u06ce \u0648 \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u062a\u0627\u0646 \u0646\u06d5\u06cc\u06d5.',
    '\u0628\u06a9\u06d5\u0648\u0646\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648 \u062f\u06d5\u0633\u062a\u0648\u0648\u0631\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u0628\u06c6\u062a\u0627\u0646\u06cc \u0646\u0627\u0631\u062f \u0648 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0628\u0627\u0634\u062a\u0631\u06d5\u061b \u0628\u06d5\u0631\u0644\u06d5\u0648\u06d5 \u0626\u0627\u0632\u0627\u0631\u062a\u0627\u0646 \u0628\u06c6\u0628\u06ce - \u0644 \u06d5\u0646\u06d5\u06a9\u0627\u0648\u06ce\u06a9 \u2013 \u06a9\u06d5 \u0647\u06d5\u0631 \u0647\u06d5\u0633\u062a\u06cc\u0634\u06cc \u067e\u06ce \u0646\u0627\u06a9\u06d5\u0646.',
    '\u0646\u06d5\u06a9\u0627 \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5\u0633\u06ce \u0628\u06b5\u06ce: \u0626\u06d5\u06cc \u0645\u062e\u0627\u0628\u0646\u060c \u0645\u0646 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u062f\u0631\u06ce\u063a\u06cc\u0645 \u06a9\u0631\u062f\u061b \u0695\u06d5\u06af\u06d5\u06b5 \u06af\u06d5\u067e \u067e\u06ce \u062f\u06d5\u0631\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0645.',
    '\u06cc\u0627\u0646 \u0628\u0628\u06ce\u0698\u06ce: \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631\u06d5\u0648\u062e\u06c6\u06cc \u0695\u06ce\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0628\u0627\u0645\u060c \u0645\u0646\u06cc\u0634 \u0644\u06d5 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u0627\u0646 \u0628\u0648\u0648\u0645.',
    '\u06cc\u0627\u0646 \u0648\u06d5\u062e\u062a\u06ce \u06a9\u06d5 \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u0628\u06cc\u0646\u06ce\u060c \u0628\u0628\u06ce\u0698\u06ce: \u0628\u0631\u06cc\u0627 \u0628\u06af\u06d5\u0695\u0627\u0628\u0627\u0645\u06d5\u0648\u06d5 \u0648 \u0628\u0686\u0648\u0648\u0645\u0627\u06cc\u06d5 \u0695\u06ce\u0632\u06cc \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646\u06d5\u0648\u06d5.',
    '\u062e\u06c6 \u06af\u06d5\u0644\u06ce\u06a9 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0645\u0646\u062a \u0628\u06c6 \u0647\u0627\u062a\u061b \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a \u067e\u06ce\u06cc\u0627\u0646 \u0646\u06d5\u0647\u0627\u062a \u0648 \u0644\u06d5 \u062e\u06c6\u0628\u06d5\u0632\u0644 \u0632\u0627\u0646\u06cc\u0646\u06cc \u062e\u06c6\u062a\u060c \u0686\u0648\u0648\u06cc\u06d5 \u0695\u06ce\u0632\u06cc \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u0627\u0646.',
    '\u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u062a\u06c6 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0631\u06c6\u06cc\u0627\u0646 \u0628\u06c6 \u062e\u0648\u0627 \u0647\u06d5\u06b5 \u062f\u06d5\u0628\u06d5\u0633\u062a\u060c \u0695\u0648\u0648\u06cc\u0627\u0646 \u0695\u06d5\u0634\u06d5. \u0626\u0627\u062e\u06c6 \u0628\u06c6 \u062e\u06c6\u0628\u06d5\u0632\u0644 \u0632\u0627\u0646\u0627\u0646 \u0634\u0648\u06ce\u0646\u06ce\u06a9 \u0644\u06d5 \u062f\u06c6\u0698\u06d5\u0647\u062f\u0627 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u0627\u0631\u0627\u0633\u062a \u2013 \u0644\u06d5\u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0628\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0632\u06af\u0627\u0631\u06cc\u062f\u0627 \u0686\u0648\u0648\u0646 \u2013 \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u0695\u0632\u06af\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u061b \u0646\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0646\u0627\u062e\u06c6\u0634\u06cc \u062f\u06d5\u0628\u0646\u060c \u0646\u06d5 \u062e\u06d5\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0647\u06d5\u06cc\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0633\u06a9\u0627\u0631\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u0627\u06af\u0627\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0647\u06d5\u06cc\u06d5.',
    '\u06a9\u0644\u06cc\u0644 \u06af\u06d5\u0644\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06ce \u0628\u06d5\u062f\u06d5\u0633\u062a \u0626\u06d5\u0648\u06d5. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0646 \u06a9\u06d5 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0645\u0646\u062a\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0634\u062a\u06ce\u06a9\u06cc \u062a\u0631 \u0628\u067e\u06d5\u0631\u0633\u062a\u0645\u061f \u0626\u06d5\u06cc \u0646\u06d5\u0632\u0627\u0646\u0627\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u062a\u06c6 \u0648 \u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5 \u062a\u06c6 \u0628\u0648\u0648\u0646\u060c \u0695\u0627\u0645\u0627\u0646 \u06af\u06d5\u06cc\u0627\u0646\u062f\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u0628\u067e\u06d5\u0631\u0633\u062a\u06cc\u060c \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u062a \u0644\u06ce \u067e\u0648\u0648\u0686\u06d5\u06b5 \u062f\u06d5\u0628\u06ce \u0648 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06cc.',
    '\u062a\u06d5\u0646\u0647\u0627 \u062e\u0648\u062f\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u06d5 \u0648 \u062f\u06d5\u06af\u06d5\u06b5 \u0634\u0648\u06a9\u0631\u0628\u0698\u06ce\u0631\u0627\u0646 \u0628\u06d5.',
    '\u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u2013 \u0648\u06d5\u06a9\u0648\u0648 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u2013 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5. \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0632\u06d5\u0645\u06cc\u0646 \u0647\u06d5\u0645\u0648\u0648 \u0648\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u0645\u0634\u062a\u06cc \u0626\u06d5\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06cc\u0634 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0626\u06d5\u0648 \u067e\u06ce\u0686\u0631\u0627\u0646\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u067e\u0627\u06a9 \u0648 \u0628\u06d5\u0631\u0632\u062a\u0631\u06d5 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc.',
    '\u062f\u06d5\u06a9\u06d5\u06b5\u06d5\u0634\u0627\u062e \u062f\u06d5\u0645\u06ce\u0646\u062f\u0631\u0627\u060c \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u0646\u060c \u0628\u06ce \u0647\u06c6\u0634 \u062f\u06d5\u0628\u0646\u061b \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0633\u06ce \u062e\u0648\u0627 \u062d\u06d5\u0632 \u0628\u06a9\u0627. \u062c\u0627\u0631\u06ce \u062a\u0631 \u0641\u0648\u0648\u06cc \u067e\u06ce\u062f\u0627 \u06a9\u0631\u0627\u060c \u06a9\u06d5\u0686\u06cc \u0644\u06d5\u067e\u0695 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0695\u0627\u0633\u062a\u06d5\u0648\u06d5 \u062f\u06d5\u0628\u0646 \u0648 \u0628\u06d5 \u0686\u0627\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u0646.',
    '\u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0634 \u0628\u06d5 \u062a\u06cc\u0634\u06a9\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062e\u06c6\u06cc \u0695\u0648\u0648\u0646\u0627\u06a9\u0627\u06cc\u06cc \u062a\u06ce \u062f\u06d5\u06af\u06d5\u0695\u06ce \u0648 \u06a9\u062a\u06ce\u0628\u06cc \u062e\u0648\u0627 \u062f\u06ce\u062a\u06d5 \u06af\u06c6\u0695 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u0627\u0646 \u062f\u06ce\u0646\u06d5\u0628\u06d5\u0631\u06ce \u0648 \u0686\u06c6\u0646 \u0695\u0627\u0633\u062a\u06d5\u060c \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u06d5\u0646 \u0648 \u063a\u06d5\u062f\u0631 \u0644\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0647\u06d5\u0631\u0686\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5 \u2013 \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u2013 \u067e\u0627\u062f\u0627\u0634\u06cc \u067e\u06ce \u062f\u06d5\u06af\u0627\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u0648\u0627 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0628\u06c6 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0628\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5 \u0695\u06d5\u067e\u06ce\u0686\u06d5\u06a9 \u062f\u06d5\u062f\u0631\u06ce\u0646. \u0647\u06d5\u0631 \u06a9\u06d5 \u0644\u06ce\u06cc \u0646\u0632\u06cc\u06a9 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u060c \u062f\u06d5\u0631\u06af\u0627\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u06a4\u06d5\u062f\u06d5\u06a9\u06d5\u0646\u061b \u06af\u0632\u06cc\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u062a\u0627\u0646 \u2013 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u2013 \u0628\u06c6 \u0646\u06d5\u0647\u0627\u062a\u0628\u0648\u0648\u060c \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u0646 \u0648 \u062a\u0631\u0633\u06cc \u062a\u0648\u0648\u0634\u0628\u0648\u0648\u0646\u06cc \u0626\u06d5\u0645\u0695\u06c6\u062a\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631\u0628\u06ce\u0646\u0646\u061f \u0626\u06ce\u0698\u0646: \u0628\u06d5\u06b5\u06ce\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646 \u0628\u06c6 \u0626\u06ce\u0645\u06d5 \u06a9\u06d5 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u06cc\u0646\u060c \u062c\u06ce\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0626\u06ce\u0698\u0646: \u0644\u06d5 \u062f\u06d5\u0631\u06af\u0627\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0628\u0686\u0646\u06d5\u0698\u0648\u0648\u0631\u06ce \u0648 \u062a\u0627 \u0647\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0628\u0646\u061b \u062c\u06ce\u06af\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0641\u06cc\u0632\u0646\u060c \u0686\u06d5\u0646\u062f \u062e\u0631\u0627\u067e\u06d5!',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0628\u0648\u0648\u06d5\u060c \u062f\u06d5\u0633\u062a\u06d5\u062f\u06d5\u0633\u062a\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0631\u06ce\u0646 \u0648 \u062f\u06d5\u0631\u06af\u0627\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u06a9\u0627\u0631\u0628\u06d5\u062f\u06d5\u0633\u062a\u0627\u0646\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a\u06ce \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u062f\u0631\u0648\u0648\u062f \u0644\u06d5\u0646\u06af\u06c6\u060c \u0628\u06d5\u062e\u06ce\u0631\u0647\u0627\u062a\u0646\u060c \u0628\u0686\u0646\u06d5\u0698\u0648\u0648\u0631\u06ce \u0648 \u0647\u06d5\u0631\u0648\u0647\u06d5\u0631\u060c \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0628\u0646.',
    '\u0626\u06ce\u0698\u0646: \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0628\u06c6 \u062e\u0648\u062f\u0627\u060c \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06ce \u0628\u06d5\u0626\u06ce\u0645\u06d5\u06cc \u062f\u0627\u060c \u0648\u06d5\u0695\u0627\u0633\u062a \u06af\u06d5\u0695\u0627 \u0648 \u0626\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u062f\u0627 \u0628\u06d5 \u0626\u06ce\u0645\u06d5 \u0648 \u0644\u06d5\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u0627\u060c \u06a9\u0648\u06ce \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u06a9\u06d5\u06cc\u0646 \u0644\u06ce\u06cc \u062f\u06d5\u0628\u06cc\u0646. \u0626\u06d5\u0648 \u067e\u0627\u062f\u0627\u0634\u06d5\u06cc \u062f\u0631\u0627\u0648\u06d5 \u0628\u06d5 \u062e\u06d5\u0628\u0627\u062a\u06a9\u0627\u0631\u0627\u0646 \u0686\u06d5\u0646\u062f\u06d5 \u0628\u0627\u0634\u06d5!',
    '\u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u062f\u06d5\u0628\u06cc\u0646\u06cc\u060c \u0644\u06d5 \u062f\u06d5\u0648\u0631\u06cc \u062a\u06d5\u062e\u062a \u062e\u0648\u0644 \u062f\u06d5\u062e\u06c6\u0646 \u0648 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646. \u0647\u06d5\u0631\u0686\u06c6\u0646 \u0695\u0627\u0633\u062a\u06d5\u060c \u0648\u0627 \u0628\u06d5 \u0646\u0627\u0648\u06cc\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0634\u062a\u0648\u0648\u0646 \u0648 \u0626\u06ce\u0698\u0646: \u0633\u0648\u067e\u0627\u0633 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u0634 \u062c\u06cc\u0647\u0627\u0646.',
    '\u062d\u0627\u060c \u0645\u06cc\u0645.',
    '\u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u062e\u0648\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0686\u0627\u0632\u0627\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u061b',
    '\u06a9\u06d5 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646\u06cc \u0642\u0628\u0648\u0648\u06b5 \u062f\u06d5\u06a9\u0627 \u0648 \u0644\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u062a\u0648\u0646\u062f\u0648\u062a\u06cc\u0698\u06d5. \u0686\u0627\u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0633 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0648 \u062c\u06af\u06d5 \u0644\u06d5 \u062e\u06c6\u06cc \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0628\u06ce \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u06ce\u062a\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce \u0644\u06d5\u0633\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06ce\u0634\u06d5 \u0646\u0627\u06a9\u0627. \u06af\u06d5\u0695 \u0648 \u062e\u0648\u0644\u06cc\u0627\u0646 \u0628\u06d5 \u0634\u0627\u0631\u0627\u0646\u062f\u0627\u060c \u0646\u06d5\u06a9\u0627 \u062a\u06c6 \u0647\u06d5\u06b5\u062e\u06d5\u06b5\u06d5\u062a\u06ce\u0646\u06ce.',
    '\u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d\u06cc\u0634 \u067e\u06ce\u0634 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0648 \u0686\u06d5\u0646\u062f \u06a9\u06c6\u0645\u06d5\u06b5\u06cc\u0634 \u0644\u06d5\u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u060c \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0646\u06d5\u0647\u06ce\u0646\u0627. \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u067e\u06d5\u0644\u0627\u0645\u0627\u0631\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u062f\u0627\u0648\u06d5 \u062a\u0627 \u0628\u06cc\u06af\u0631\u0646 \u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u06d5\u0646\u061b \u0628\u06d5 \u0642\u0633\u06d5\u06cc \u0647\u06cc\u0686\u06cc\u0634 \u0648\u06cc\u0633\u062a\u0648\u0648\u06cc\u0627\u0646\u06d5 \u062c\u06ce \u0628\u06c6 \u0695\u0627\u0633\u062a\u06cc \u0646\u06d5\u0647\u06ce\u06b5\u0646\u06d5\u0648\u06d5. \u0626\u06cc\u062a\u0631 \u0628\u06d5 \u063a\u06d5\u0632\u06d5\u0628 \u06af\u0631\u062a\u0648\u0648\u0645\u0646\u061b \u062f\u06d5\u0633\u0627 \u0633\u0632\u0627\u06cc \u0645\u0646 \u0686\u0644\u06c6\u0646 \u0628\u0648\u0648.',
    '\u0628\u06d5\u06b5\u06ce\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646\u060c \u0648\u0627 \u0647\u0627\u062a\u06d5 \u062c\u06ce \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u06d5\u062e\u062a\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06af\u0631\u062a\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u062f\u06d5\u0648\u0631\u06cc \u0626\u06d5\u0648\u062f\u0627\u0646\u060c \u0628\u06d5 \u0633\u0648\u067e\u0627\u0633\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u062e\u06d5\u0631\u06cc\u06a9\u0646 \u0648 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0648\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u062f\u0627\u0648\u0627\u06cc \u0628\u0648\u0631\u062f\u0646 \u0628\u06c6 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646! \u062f\u06cc\u0627\u0631\u06d5 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0648 \u0632\u0627\u0646\u06cc\u0646\u062a \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u062f\u0627\u06af\u0631\u062a\u0648\u0648\u06d5\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0695\u06ce\u06af\u06d5\u06cc \u062a\u06c6 \u062f\u06d5\u0628\u0646\u060c \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u06d5 \u0648 \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5 \u0626\u0627\u06af\u0631\u06cc\u0627\u0646 \u0628\u067e\u0627\u0631\u06ce\u0632\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646! \u0628\u06cc\u0634\u06cc\u0627\u0646\u0646\u06ce\u0631\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06cc \u0647\u06d5\u0645\u06cc\u0634\u06d5\u06cc\u06cc\u060c \u06a9\u06d5 \u062e\u06c6\u062a \u0628\u06d5\u06b5\u06ce\u0646\u062a \u067e\u06ce \u062f\u0627\u0648\u0646\u061b \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0628\u0627\u0648\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0648 \u0647\u0627\u0648\u0644\u0641\u06cc\u0627\u0646\u0646 \u0648 \u0628\u06d5\u0631\u06d5 \u0648 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u2013 \u06a9\u06d5 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06cc\u0627\u0646 \u0628\u0648\u0648\u06d5 \u2013 \u0648\u06d5\u0644 \u0626\u06d5\u0648\u0627\u0646 \u0628\u0646\u061b \u0647\u06d5\u0631 \u062e\u06c6\u062a \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc.',
    '\u0644\u06d5 \u062e\u0631\u0627\u067e\u06d5\u0634 \u0628\u06cc\u0627\u0646\u067e\u0627\u0631\u06ce\u0632\u06d5. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5 \u062e\u0631\u0627\u067e\u06d5 \u0628\u06cc\u067e\u0627\u0631\u06ce\u0632\u06cc\u060c \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062a\u06c6 \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u06d5\u0634 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0645\u06d5\u0632\u0646\u06d5.',
    '\u0647\u0627\u0648\u0627\u0631 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u062f\u06d5\u06a9\u0631\u06ce \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5: \u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648 \u0695\u06a9\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0647\u06d5\u062a\u0627\u0646\u06d5\u060c \u06af\u06d5\u0648\u0631\u06d5\u062a\u0631\u06d5\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0646\u06af\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0648 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce\u060c \u06a9\u06d5\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u062a\u06c6 \u062f\u0648\u0648 \u062c\u0627\u0631\u0627\u0646 \u0645\u0631\u0627\u0646\u062f\u0648\u0648\u062a\u06cc\u0646\u061b \u062f\u0648\u0648 \u062c\u0627\u0631\u0627\u0646\u06cc\u0634 \u0698\u06cc\u0627\u0646\u062a \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u06a9\u0631\u062f\u0648\u0648\u06cc\u0646\u061b \u067e\u06ce \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06cc \u062e\u06c6\u062f\u06d5\u0646\u06ce\u06cc\u0646\u060c \u0626\u0627\u062e\u06c6 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u0626\u06d5\u0645\u06d5\u062a\u0627\u0646 \u0628\u06c6\u06cc\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u060c \u0686\u0648\u0646\u06a9\u06d5 \u0647\u06d5\u0631\u06af\u0627 \u0645\u06d5\u0631\u062f\u0645 \u062e\u0648\u0627\u06cc\u0627\u0646 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a \u0648 \u0647\u06cc\u0686\u06cc \u062a\u0631 \u0646\u0627\u060c \u062d\u0627\u0634\u0627\u0648 \u062f\u06d5\u06a9\u0631\u062f\u061b \u0628\u06d5\u06b5\u0627\u0645 \u06a9\u0627\u062a\u06ce \u0634\u06d5\u0631\u06cc\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u062f\u0627\u062f\u06d5\u0646\u0627\u060c \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062a\u0627\u0646 \u062f\u06d5\u0647\u06ce\u0646\u0627. \u0641\u06d5\u0631\u0645\u0627\u0646 \u062a\u06d5\u0646\u06cc\u0627 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc \u0628\u06d5\u0631\u0632 \u0648 \u0645\u06d5\u0632\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u06c6\u06cc\u062a\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0646\u0648\u06ce\u0646\u06ce \u0648 \u0644\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5 \u0628\u0698\u06cc\u0648\u062a\u0627\u0646 \u0628\u06c6 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627. \u062a\u06d5\u0646\u06cc\u0627 \u06a9\u06d5\u0633\u06ce \u0644\u06d5\u0648 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc\u06cc\u06d5 \u062a\u06ce\u062f\u06d5\u06af\u0627\u060c \u06a9\u06d5 \u0695\u0648\u0648\u06cc \u0647\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0628\u06ce.',
    '\u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u062f\u06b5\u067e\u0627\u06a9\u06cc \u0647\u06d5\u0631 \u0647\u0627\u0646\u0627 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631\u0646 \u0648 \u062f\u06cc\u0646\u06cc \u067e\u0627\u06a9\u0698\u062a\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u0628\u06ce\u061b \u062c\u0627 \u0628\u0627 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc\u0634 \u067e\u06ce\u06cc \u062a\u0648\u0648\u0695\u06d5 \u0628\u0646.',
    '\u067e\u0644\u06d5\u06cc \u0628\u06d5\u0631\u0632 \u0648\u0627 \u0644\u0627\u06cc \u0626\u06d5\u0648\u0646\u060c \u062e\u0627\u0648\u06d5\u0646 \u062a\u06d5\u062e\u062a\u06d5\u061b \u0628\u06d5 \u0641\u0631\u0634\u062a\u06d5\u06cc \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc\u062f\u0627 \u0648\u06b5\u0627\u0645\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627\u061b \u062a\u0627 \u0644\u06d5 \u0695\u06c6\u0698\u06ce \u2013 \u06a9\u06d5\u062a\u0648\u0648\u0634\u06cc\u0627\u0646 \u062f\u06ce \u2013 \u0628\u06cc\u0627\u0646\u062a\u0631\u0633\u06ce\u0646\u06ce.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u2013 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u062f\u06ce\u0646\u06d5\u0648\u06d5 \u062f\u06d5\u0631 \u2013 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0647\u06ce\u0646\u06cc \u0646\u06cc\u06cc\u06d5. \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06cc \u0628\u06c6 \u06a9\u06ce\u06cc\u06d5\u061f \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc \u062a\u0627\u06a9 \u0648 \u062a\u06d5\u0646\u06cc\u0627\u06cc \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u0632\u0627\u06b5\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0647\u06d5\u0631\u0686\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06cc\u060c \u0633\u0632\u0627\u06cc \u06a9\u0627\u0631\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0628\u06cc\u0646\u06ce\u061b \u0626\u06d5\u0645 \u0695\u06c6\u0698\u06d5 \u0647\u06cc\u0686 \u063a\u06d5\u062f\u0631\u06ce\u06a9 \u0646\u06cc\u06cc\u06d5. \u062e\u0648\u062f\u0627 \u0628\u06d5\u0644\u06d5\u0632 \u0628\u06d5 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u062a\u06c6 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0628\u06cc\u0627\u0646\u062a\u0631\u0633\u06ce\u0646\u06d5 \u06a9\u06d5 \u0644\u06d5\u0646\u06d5\u06a9\u0627\u0648 \u062a\u0648\u0648\u0634\u06cc \u062f\u06d5\u0628\u0646. \u0644\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u06cc \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06b5\u06ce\u06a9 \u0644\u06d5 \u0642\u0648\u0695\u06af\u0627\u0646 \u0646\u0632\u06cc\u06a9 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0647\u06d5\u0631 \u067e\u06cc\u0634 \u062f\u06d5\u062e\u06c6\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0646 \u0646\u06d5 \u0647\u06cc\u0686 \u062f\u06c6\u0633\u062a\u06cc\u0627\u0646 \u062f\u06d5\u0633 \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u060c \u0646\u06d5 \u06a9\u06d5\u0633\u06ce \u062a\u06a9\u0627\u06cc \u0628\u06af\u06cc\u0631\u06ce.',
    '\u062e\u0648\u0627\u0634 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06ce \u0686\u0627\u0648\u06cc \u063a\u06d5\u06cc\u0627\u0646\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u0686\u06cc\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627 \u0634\u0627\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5.',
    '\u062e\u0648\u0627 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u0627. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u0646\u06d5 \u0628\u06d5\u0631 - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u2013 \u0647\u06cc\u0686 \u06a9\u0627\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u062f\u06d5\u0633 \u0646\u0627\u06cc\u06d5. \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0628\u06cc\u0633\u06d5\u0631\u06cc\u0634 \u0648 \u0628\u06cc\u0646\u0627\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0646\u06d5\u06af\u06d5\u0695\u0627\u0648\u0646\u060c \u062a\u0627 \u0628\u0632\u0627\u0646\u0646 \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0686\u062a\u06c6 \u0628\u0648\u0648\u06d5\u061f \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0647\u06ce\u0632\u062a\u0631 \u0628\u0648\u0648\u0646 \u0648 \u0644\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0634\u0648\u06ce\u0646\u06d5\u0648\u0627\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0632\u06c6\u0631\u062a\u0631\u06d5\u061b \u0644\u06d5\u062a\u06c6\u06b5\u06d5\u06cc \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062e\u0648\u0627 \u06af\u0631\u062a\u0646\u06cc \u0648 \u06a9\u06d5\u0633 \u0644\u06d5\u062e\u0648\u062f\u0627\u06cc \u0646\u06d5\u067e\u0627\u0631\u0627\u0633\u062a\u0646.',
    '\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0648\u06d5 \u0647\u0627\u062a\u0628\u0648\u0648\u0646\u06d5 \u0644\u0627 \u0648 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0628\u0648\u0648\u0646\u061b \u0626\u06d5\u0648\u06ce \u0686\u0627\u062e\u06ce \u062e\u0648\u0627 \u06af\u0631\u062a\u0646\u06cc. \u062e\u0648\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5 \u0648 \u0633\u0632\u0627\u062f\u0627\u0646\u06cc \u062a\u0648\u0646\u062f\u0648\u062a\u06cc\u0698\u06d5.',
    '] \u0644\u06d5\u0648 \u0628\u0627\u0631\u06d5\u0648\u06d5 \u0628\u06d5 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0648 \u0628\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0648\u06d5\u060c \u0645\u0648\u0648\u0633\u0627\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u060c',
    '\u0628\u06c6 \u0644\u0627\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0647\u0627\u0645\u0627\u0646 \u0648 \u0628\u06c6 \u0644\u0627\u06cc \u0642\u0627\u0695\u0648\u0648\u0646. \u0647\u06d5\u0645\u0648\u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u062c\u0627\u062f\u0648\u06af\u06d5\u0631\u06ce \u062f\u0631\u06c6\u0632\u0646\u06d5.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u062f\u06cc\u0646\u06cc \u0695\u0627\u0633\u062a\u06cc \u0628\u06c6 \u062f\u06d5\u0631\u062e\u0633\u062a\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u06a9\u0648\u0695\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u06d5\u0644 \u0626\u06d5\u0648 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0644\u06d5\u0646\u0627\u0648 \u0628\u06d5\u0631\u0646 \u0648 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0632\u06cc\u0646\u062f\u0648\u0648\u06cc\u06cc \u0628\u0647\u06ce\u06b5\u0646\u06d5\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0641\u06ce\u06b5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0633\u06d5\u0631\u06cc \u0646\u06d5\u06af\u0631\u062a\u0648\u0648\u06d5.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648\u062a\u06cc: \u0644\u06ce\u0645 \u06af\u06d5\u0695\u06ce\u0646 \u0645\u0648\u0648\u0633\u0627 \u0628\u06a9\u0648\u0698\u0645\u061b \u0628\u0627 \u0626\u06d5\u0648 \u0647\u0627\u0648\u0627\u0631 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u0628\u06d5\u0631\u06ce\u061b \u0645\u0646 \u062f\u06d5\u062a\u0631\u0633\u0645 \u062f\u06cc\u0646\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u0628\u06af\u06c6\u0695\u06ce\u062a \u0648 \u067e\u0634\u06ce\u0648\u06ce\u0646\u06cc \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u0628\u0646\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0645\u0648\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u0647\u0627\u0646\u0627 \u062f\u06d5\u0628\u06d5\u0645\u06d5 \u0628\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u2013 \u06a9\u06d5 \u0645\u0646\u06cc\u0634 \u0648 \u0626\u06ce\u0648\u06d5\u0634\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0627\u0646\u062f\u0648\u0648\u06d5 \u2013 \u0628\u0645\u067e\u0627\u0631\u06ce\u0632\u06ce \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06c6\u0628\u06d5\u0632\u0644\u0632\u0627\u0646\u06ce \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u0646\u06cc\u06cc\u06d5.',
    '\u067e\u06cc\u0627\u0648\u06ce \u0644\u06d5\u062e\u0632\u0645\u0627\u0646\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u2013 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u062e\u0648\u0627 \u0647\u06d5\u0628\u0648\u0648\u060c \u0628\u06d5\u06b5\u0627\u0645 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0626\u06d5\u0634\u0627\u0631\u062f\u06d5\u0648 \u2013 \u06af\u0648\u062a\u06cc: \u0626\u06ce\u0648\u06d5 \u0686\u0644\u06c6\u0646 \u067e\u06cc\u0627\u0648\u06ce \u0648\u06d5\u0647\u0627 \u062f\u06d5\u06a9\u0648\u0698\u0646\u060c \u0626\u06ce\u0698\u06ce \u062e\u0648\u0627 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u06d5 \u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0634\u06cc\u0695\u0627\u060c \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0632\u06c6\u0631 \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u0628\u06c6 \u0647\u0627\u0648\u0631\u062f\u0648\u0648\u0646\u061f \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u06b5\u06ce\u0646: \u062f\u0631\u06c6 \u062f\u06d5\u06a9\u0627\u060c \u0648\u06d5\u0628\u0627\u06b5 \u062f\u0631\u06c6\u06cc \u0628\u06d5 \u0626\u06d5\u0633\u062a\u06c6\u06cc \u062e\u06c6\u06cc. \u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u0642\u0633\u06d5\u06cc \u0695\u0627\u0633\u062a \u062f\u06d5\u06a9\u0627\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648 \u067e\u06ce\u062a\u0627\u0646 \u062f\u06d5\u06b5\u06ce\u060c \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646\u0648\u0648 \u0647\u06d5\u0631 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u061b \u0686\u0648\u0646\u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0645\u0631\u06c6\u06cc \u0644\u06d5 \u0695\u0627\u062f\u06d5 \u0644\u0627\u062f\u06d5\u0631\u06cc \u062f\u0631\u06c6\u0632\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0646\u0627\u06a9\u0627.',
    '\u06af\u06d5\u0644\u06c6! \u0626\u06d5\u0645\u0695\u06c6 \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u0633\u061b \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u0634\u0627 \u0626\u06ce\u0648\u06d5 \u0632\u0627\u06b5\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u0628\u06d5 \u06af\u0698\u0645\u0627\u0646\u062f\u0627 \u0628\u06ce\u060c \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06ce \u06cc\u0627\u0631\u06cc\u0645\u0627\u0646 \u062f\u06d5\u06a9\u0627\u061f \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u06af\u0648\u062a\u06cc: \u0686\u06cc \u062e\u06c6\u0645 \u0628\u06d5 \u0686\u0627\u06a9\u06cc \u062f\u06d5\u0628\u06cc\u0646\u0645\u060c \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u062a\u0627\u0646 \u067e\u06ce\u0634 \u062f\u06d5\u06a9\u06d5\u0648\u0645 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0695\u06ce\u06af\u06d5\u06cc \u06a9\u06d5 \u0645\u0646 \u0646\u06cc\u0634\u0627\u0646\u062a\u0627\u0646\u06cc \u062f\u06d5\u062f\u06d5\u0645\u060c \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5.',
    '\u06a9\u0627\u0648\u0631\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0695\u0648\u0627 \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0645\u0646 \u0644\u06d5\u0648\u06d5 \u062f\u06b5\u062e\u0648\u0631\u062a\u0645 \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u062e\u06d5\u06b5\u06a9\u06cc \u062a\u0631 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0628\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646 \u0628\u06ce\u061b',
    '\u0648\u06d5\u06a9 \u0695\u0648\u0648\u062f\u0627\u0648\u06cc \u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d \u0648 \u0639\u0627\u062f \u0648 \u0633\u06d5\u0645\u0648\u0648\u062f \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646 \u0628\u0648\u0648\u0646. \u062e\u0648\u0627 \u0646\u0627\u06cc\u06d5\u0648\u06ce \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646 \u0628\u06a9\u0627.',
    '\u06af\u06d5\u0644\u06c6! \u0644\u06ce\u062a\u0627\u0646 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646\u0645 \u0644\u06d5\u0648\u0695\u06c6\u0698\u06d5\u062f\u0627 \u0644\u06ce\u0648 \u0628\u0642\u06d5\u0648\u0645\u06ce \u06a9\u06d5 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0647\u0627\u0648\u0627\u0631 \u0647\u0627\u0648\u0627\u0631.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u067e\u0627\u0634 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0695\u0627\u062f\u06d5\u06a9\u06d5\u0646\u061b \u06a9\u06d5\u0633 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0695\u0632\u06af\u0627\u0631\u062a\u0627\u0646 \u0628\u06a9\u0627. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u0648\u062f\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u0644\u06ce \u0628\u06af\u06c6\u0695\u06ce\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0646\u0627\u06cc\u062e\u0627\u062a\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u0695\u06ce.',
    '\u0644\u06d5\u0645\u06d5\u0648 \u067e\u06ce\u0634\u06cc\u0634 \u06cc\u0648\u0648\u0633\u0641 \u0628\u06d5 \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0628\u06d5\u06b5\u06af\u06d5\u0648\u06d5 \u0647\u0627\u062a\u06d5 \u0646\u0627\u0648\u062a\u0627\u0646\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06a9\u0627\u0646\u06cc \u062f\u0648\u0648\u062f\u06b5 \u0628\u0648\u0648\u0646\u060c \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u067e\u06ce \u0646\u06d5\u0647\u06ce\u0646\u0627. \u0648\u06d5\u062e\u062a\u06ce \u06a9\u06d5 \u0645\u0631\u062f\u060c \u06af\u0648\u062a\u062a\u0627\u0646: \u0626\u06cc\u062a\u0631 \u067e\u0627\u0634 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u0646\u0627\u0646\u06ce\u0631\u06ce. \u062f\u06d5\u0633\u0627 \u0626\u0627\u0648\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9 \u062f\u0648\u0648\u062f\u06b5\u06cc \u0628\u06a9\u0627 \u0648 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0631\u0628\u0686\u06ce\u060c \u062e\u0648\u062f\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u0644\u06ce \u062f\u06d5\u06af\u06c6\u0695\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06ce \u0626\u06d5\u0648\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u0628\u06ce\u060c \u0644\u06d5\u0645\u06d5\u0695 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627 \u06a9\u06ce\u0634\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5\u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u06a9\u0627\u0631\u06cc \u0632\u06c6\u0631 \u0646\u0627\u0628\u06d5\u062c\u06ce \u062f\u06d5\u06a9\u06d5\u0646. \u062e\u0648\u0627 \u062f\u06b5\u06cc \u0647\u06d5\u0631 \u062e\u06c6\u0628\u06d5\u0632\u0644\u0632\u0627\u0646\u06ce\u06a9\u06cc \u0632\u06c6\u0631\u062f\u0627\u0631\u060c \u0648\u0627 \u0645\u06c6\u0631 \u062f\u06d5\u06a9\u0627.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0647\u0627\u0645\u0627\u0646! \u0628\u0686\u06c6 \u0642\u06d5\u06b5\u0627\u06cc\u06d5\u06a9 \u0628\u06c6 \u0645\u0646 \u0633\u0627\u0632 \u06a9\u06d5\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06af\u06d5\u0645 \u0628\u06d5\u0648 \u0626\u0627\u0633\u06c6 \u062f\u0648\u0648\u0631\u06d5\u062f\u06d5\u0633\u062a\u0627\u0646\u06d5\u061b',
    '\u0626\u0627\u0633\u06c6\u06a9\u0627\u0646\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646\u061b \u0628\u0695\u0648\u0627\u0646\u0645\u06d5 \u062e\u0648\u062f\u0627\u06a9\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627. \u0645\u0646\u06cc\u0634 \u062f\u06d5\u06b5\u06ce\u0645 \u062f\u0631\u06c6 \u062f\u06d5\u06a9\u0627\u061b \u0626\u0627\u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u06a9\u0627\u0631\u06cc \u06af\u06d5\u0646\u062f\u06cc \u062e\u06c6\u06cc \u0628\u06d5\u062c\u0648\u0627\u0646 \u0632\u0627\u0646\u06cc \u0648 \u0628\u0648\u0648 \u0628\u06d5 \u0644\u06d5\u0645\u067e\u06d5\u0631\u06cc \u0633\u06d5\u0631 \u0695\u06ce\u06af\u06d5\u06cc. \u0647\u06d5\u0631 \u0641\u06ce\u06b5\u06ce\u06a9\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0628\u06c6 \u062e\u06c6\u06cc \u062f\u06c6\u0632\u06cc\u06cc\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0641\u06ce\u0695\u06c6 \u0686\u0648\u0648.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u060c \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0634\u0648\u06ce\u0646\u0645 \u06a9\u06d5\u0648\u0646\u060c \u0645\u0646 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u062a\u0627\u0646 \u0634\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0645.',
    '\u06af\u06d5\u0644\u06c6! \u0626\u06d5\u0645 \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u0633\u06d5\u0631\u06ce\u06a9\u06d5 \u0648 \u062f\u06d5\u0628\u0695\u06ce\u062a\u06d5\u0648\u06d5\u061b \u0645\u0627\u06b5\u06cc \u0626\u06d5\u0648\u0644\u0627 \u06cc\u06d5\u06a9\u062c\u0627\u0631\u06cc\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u0631\u0627\u067e\u06d5 \u0628\u06a9\u0627\u060c \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u062e\u0631\u0627\u067e\u06d5\u06cc \u0633\u0632\u0627 \u062f\u06d5\u062f\u0631\u06ce. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0626\u0627\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0628\u0646 \u2013 \u0686 \u0646\u06ce\u0631\u060c \u0686 \u0645\u06ce \u2013 \u0648 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0686\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0648 \u0628\u06ce \u0698\u0645\u0627\u0631\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0628\u0698\u06cc\u0648\u06ce\u06a9\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u06af\u06d5\u0644\u06c6! \u0686\u06cc\u0645\u06d5\u060c \u0645\u0646 \u062f\u06d5\u0645\u06d5\u0648\u06ce \u0695\u0632\u06af\u0627\u0631\u062a\u0627\u0646 \u06a9\u06d5\u0645\u060c \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0645\u0646\u062a\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u0628\u0686\u0645\u06d5 \u0626\u0627\u06af\u0631\u061f',
    '\u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062d\u0627\u0634\u0627 \u0628\u06a9\u06d5\u0645 \u0648 \u0634\u062a\u06cc \u0648\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0645 \u0648 \u0628\u06cc\u0627\u0646\u06a9\u06d5\u0645 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u0627\u060c \u06a9\u06d5 \u0647\u06cc\u0686 \u0632\u0627\u0646\u0633\u062a\u06cc\u0645 \u067e\u06ce \u0646\u06cc\u06cc\u06d5\u061b \u06a9\u06d5\u0686\u06cc \u0645\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u0645\u06d5 \u0626\u06ce\u0648\u06d5 \u0695\u0648\u0648 \u0628\u06a9\u06d5\u0646\u06d5 \u0644\u0627\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u06af\u0648\u0646\u0627\u0647\u0640 \u0628\u06d5\u062e\u0634.',
    '\u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u062e\u0648\u0627\u0632\u0646 \u0645\u0646 \u0634\u062a\u06cc \u0648\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0645 \u06a9\u06d5 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0648 \u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u0647\u06cc\u0686 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0644\u06d5\u062f\u06d5\u0633 \u0646\u0627\u06cc\u06d5. \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u06cc\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0642\u0627\u0633 \u062f\u06d5\u0631\u062f\u06d5\u0686\u0646\u060c \u0647\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u062f\u06d5\u0628\u0646.',
    '\u062c\u0627\u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5 \u0642\u0633\u06d5\u06cc \u0645\u0646\u062a\u0627\u0646 \u062f\u06ce\u062a\u06d5\u0648\u06d5 \u0628\u06cc\u0631. \u06a9\u0627\u0631\u06cc \u062e\u06c6\u0645 \u0628\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u0633\u067e\u06ce\u0631\u0645\u061b \u062e\u0648\u062f\u0627 \u0686\u0627\u0648\u06cc \u0644\u06d5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u062e\u0648\u0627 \u0626\u06d5\u0648\u06cc \u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u06cc \u062e\u0631\u0627\u067e\u06cc \u0626\u06d5\u0648 \u0641\u06ce\u06b5\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f\u060c \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0633\u06d5\u0631 \u0628\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u06ce \u0628\u0648\u0648\u0646\u060c \u0628\u06d5\u06b5\u0627 \u062f\u06d5\u0646\u0627\u0648\u06d5\u062e\u06c6\u06cc \u06af\u0631\u062a\u0646.',
    '\u0647\u06d5\u0645\u0648\u0648 \u0633\u067e\u06ce\u062f\u06d5 \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646\u0628\u06d5\u0646\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u0648\u0631 \u0648 \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0633\u06d5\u06b5\u0627 \u0695\u0627\u062f\u06d5\u0628\u06ce [\u0641\u06d5\u0631\u0645\u0627\u0646 \u062f\u06d5\u062f\u0631\u06ce]: \u0633\u0627 \u062f\u06d5\u0633\u062a\u06d5\u0648 \u0628\u06d5\u0633\u062a\u06d5\u06a9\u06d5\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u060c \u0632\u06c6\u0631 \u0628\u06d5\u062a\u0648\u0646\u062f\u06cc \u0626\u0627\u0632\u0627\u0631 \u0628\u062f\u06d5\u0646.',
    '\u06a9\u06d5 \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5\u0646\u0627\u0648 \u0626\u0627\u06af\u0631\u062f\u0627 \u06a9\u06ce\u0634\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u06cc\u06d5\u06a9\u062a\u0631 \u062f\u06d5\u06a9\u06d5\u0646. \u0628\u06ce\u0647\u06ce\u0632\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0647\u06ce\u0632\u06d5\u06a9\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0634\u0648\u06ce\u0646 \u0626\u06ce\u0648\u06d5 \u06a9\u06d5\u0648\u062a\u0628\u0648\u0648\u06cc\u0646\u060c \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0631\u06ce \u0628\u06d5\u062c\u06ce\u06cc \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0648 \u0626\u0627\u06af\u0631\u06d5\u060c \u0628\u06d5\u0634\u06ce\u06a9 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646 \u0647\u06d5\u06b5\u06af\u0631\u0646\u061f',
    '\u0628\u06d5\u0647\u06ce\u0632\u06d5 \u0644\u06d5\u062e\u06c6\u0628\u0627\u06cc\u06cc\u06d5\u06a9\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0647\u06d5\u0645\u0648\u0648 \u0644\u06d5 \u0626\u0627\u06af\u0631\u062f\u0627\u06cc\u0646. \u062e\u0648\u062f\u0627 \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0648\u0627 \u0695\u0627\u062f\u06d5\u06af\u0627.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0686\u0648\u0648\u0646\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631\u06d5\u0648\u06d5\u060c \u0644\u06d5 \u06af\u0632\u06cc\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0626\u0627\u06af\u0631 \u062f\u0627\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646: \u0644\u06d5 \u0628\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06c6\u0646 \u0648\u06d5\u067e\u0627\u0695\u06ce\u0646\u060c \u0695\u06c6\u0698\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5\u0648 \u0626\u0627\u0632\u0627\u0631\u06d5 \u0628\u06c6 \u06a9\u06d5\u0645 \u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0648\u06d5 \u0646\u06d5\u0647\u0627\u062a\u06d5 \u0644\u0627\u061f \u0626\u06ce\u0698\u0646: \u0628\u06d5\u06b5\u06ce. \u0626\u06ce\u0698\u0646: \u062f\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0648\u06d5\u067e\u0627\u0695\u06ce\u0646\u061b \u062f\u06cc\u0627\u0631\u06d5 \u0646\u0632\u0627\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u060c \u0628\u06d5\u0634\u06cc \u0647\u06d5\u0631 \u0628\u06d5 \u0641\u06ce\u0695\u06c6 \u0686\u0648\u0648\u0646\u06d5.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u0645\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0648\u0627 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0644\u06d5 \u0698\u06cc\u0627\u0646\u06cc \u0626\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u062f\u0627 \u0648 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u0634\u062f\u0627 \u06a9\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u0627\u0646 \u0695\u0627\u0633 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5\u060c \u06cc\u0627\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0633\u0648\u0648\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u067e\u06ce \u0646\u0627\u06af\u06d5\u06cc\u06d5\u0646\u06ce \u0648 \u06af\u0634\u062a\u06cc\u0627\u0646 \u0628\u06d5\u0646\u06d5\u062d\u0644\u06d5\u062a \u06a9\u0631\u0627\u0648\u0646 \u0648 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u062a\u0631 \u0645\u0627\u06b5\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06c6\u06cc \u0695\u06ce\u0646\u0648\u06ce\u0646\u06cc\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0648 \u06a9\u0627\u0631\u06ce\u06a9\u0645\u0627\u0646 \u06a9\u0631\u062f \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u06d5 \u2013 \u0628\u06d5\u06a9\u06d5\u0644\u06d5\u067e\u0648\u0648\u0631 \u2013 \u0628\u06c6 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644 \u0628\u0645\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u06a9\u06d5 \u0695\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627 \u0648 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc\u06cc\u06d5 \u0628\u06c6 \u06a9\u06d5\u0633\u0627\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632.',
    '\u062e\u06c6 \u0695\u0627\u0628\u06af\u0631\u06d5\u060c \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u06ce\u062a\u06d5 \u062c\u06ce. \u0628\u06c6 \u06af\u0648\u0646\u0627\u0647\u062a \u062f\u0627\u0648\u0627\u06cc \u0644\u06ce\u0628\u0648\u0631\u062f\u0646\u06ce \u0628\u06a9\u06d5 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646 \u2013 \u0628\u06d5 \u0634\u0648\u06a9\u0631\u06d5\u0648\u06d5 \u2013 \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a \u0628\u062f\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0645\u06d5\u0695 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06ce\u0634\u06d5 \u062f\u06d5\u06a9\u06d5\u0646 \u2013 \u0628\u06d5\u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u0628\u06ce \u2013 \u062c\u06af\u06d5 \u0644\u06d5 \u0641\u06cc\u0632\u060c \u0647\u06cc\u0686 \u0634\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627 \u0646\u06cc\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u0646\u0627\u06af\u06d5\u0646. \u0633\u0627 \u062a\u06c6: \u067e\u06d5\u0646\u0627 \u0628\u06d5 \u062e\u0648\u0627 \u0628\u0628\u06d5\u061b \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u062f\u0627\u0647\u06ce\u0646\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u060c \u0644\u06d5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0646\u06cc \u0645\u06d5\u0631\u062f\u0645 \u06af\u0631\u06cc\u0646\u06af\u062a\u0631\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0645\u06d5\u0631\u062f\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0626\u06d5\u0645\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u06a9\u06c6\u0631 \u0648 \u0686\u0627\u0648\u0633\u0627\u063a \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9 \u0646\u06cc\u0646\u061b \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631 \u0686\u0627\u06a9\u0646\u060c \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0631\u0627\u067e\u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u062f\u0627 \u0646\u0627\u0628\u0646 \u0628\u06d5\u06cc\u06d5\u06a9. \u0686 \u0647\u06d5\u0646\u062f\u06a9\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06cc\u0631\u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0647\u0627\u062a\u0646\u06cc \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u06d5\u060c \u0647\u06cc\u0686 \u06af\u0648\u0645\u0627\u0646\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u06cc\u0646\u06d5\u06cc \u0626\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u06d5 \u0628\u0695\u0648\u0627 \u0646\u0627\u06a9\u06d5\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u06af\u0648\u062a\u0648\u0648\u06cc\u06d5: \u0644\u06d5 \u0628\u06d5\u0631\u0645 \u0628\u067e\u0627\u0695\u06ce\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u062a\u0627\u0646\u06d5\u0648\u06d5 \u062f\u06ce\u0645. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u2013 \u0647\u06d5\u0631 \u0644\u06d5\u0628\u06d5\u0631 \u062e\u06c6\u0628\u06d5\u0632\u0644\u0632\u0627\u0646\u06cc\u0646 \u2013 \u0646\u0627\u06cc\u0627\u0646\u06d5\u0648\u06ce \u0628\u0645\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0628\u06d5 \u0632\u06d5\u0628\u0648\u0648\u0646\u06cc \u062f\u06d5\u0686\u0646\u06d5 \u062f\u0648\u0695\u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0634\u06d5\u0648\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u060c \u06a9\u06d5 \u062a\u06ce\u06cc\u062f\u0627 \u0628\u062d\u06d5\u0633\u06ce\u0646\u06d5\u0648\u06d5 \u0648 \u0695\u06c6\u0698\u06cc\u0634\u06cc \u0628\u06c6 \u062f\u06cc\u062a\u0646 \u062f\u0627\u0646\u0627. \u062e\u0648\u062f\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0645\u06d5\u0631\u062f\u0645 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0646\u0627\u0633\u067e\u0627\u0633\u0646.',
    '\u0626\u06d5\u0645 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u062f\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06d5\u061b \u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0628\u06ce. \u062f\u06d5\u06cc \u0686\u06c6\u0646 \u0644\u06d5 \u0647\u06d5\u0642 \u06a9\u0644\u0627 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u061f',
    '\u0647\u06d5\u0631 \u0628\u06d5 \u0645\u062a\u06d5\u0631\u0632\u06d5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5\u0648\u06d5\u06cc \u0695\u0627\u0633\u062a\u06d5 \u06a9\u0644\u0627 \u062f\u06d5\u06a9\u0631\u06ce\u0646.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u06a9\u0631\u062f\u06d5 \u0646\u0634\u06cc\u0645\u06d5\u0646\u061b \u0626\u0627\u0633\u0645\u0627\u0646\u06cc\u0634\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06d5\u0631\u0632 \u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0648 \u0628\u06cc\u0686\u0645\u06cc \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u062f\u0627\u0648\u06d5\u060c \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06cc\u0686\u0645\u06ce\u06a9 \u062c\u0648\u0627\u0646\u062a\u0631\u06d5. \u0628\u0698\u06cc\u0648\u06cc \u0628\u0627\u0634\u06cc \u067e\u06ce \u062f\u0627\u0648\u0646. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u062e\u0648\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646. \u0628\u06d5\u0631\u0632\u06cc \u0648 \u067e\u06cc\u0631\u06c6\u0632\u06cc \u0628\u06c6 \u062e\u0648\u062f\u0627\u060c \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0632\u06cc\u0646\u062f\u0648\u0648\u06d5 \u0648 \u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u06cc\u06d5\u06a9\u06cc \u0646\u06cc\u06cc\u06d5. \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u062f\u06b5\u067e\u0627\u06a9\u06cc \u0647\u06d5\u0631 \u0647\u0627\u0646\u0627 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631\u0646 \u0648 \u062f\u06cc\u0646\u06cc \u067e\u0627\u06a9\u0698\u062a\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648 \u0628\u06ce. \u0633\u0648\u067e\u0627\u0633 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062c\u06cc\u0647\u0627\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u067e\u0627\u0634 \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u06af\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0695\u0627 \u0628\u06c6\u0645 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0695\u06ce\u0645 \u0646\u06d5\u062f\u0631\u0627\u0648\u06d5 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u0628\u067e\u06d5\u0631\u0633\u062a\u0645 \u06a9\u06d5 [\u062e\u0648\u0627 \u0646\u06cc\u0646 \u0648] \u0626\u06ce\u0648\u06d5 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0628\u06d5\u0646\u061b \u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u0645 \u067e\u06ce \u062f\u0631\u0627\u0648\u06d5 \u062e\u06c6\u0645 \u0628\u0633\u067e\u06ce\u0631\u0645 \u0628\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u06af\u0634 \u062c\u06cc\u0647\u0627\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f \u0644\u06d5 \u0642\u0648\u0695\u06ce\u06a9 \u0648 \u0644\u06d5\u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u062a\u0646\u06c6\u06a9\u06ce\u06a9 \u0648 \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u062e\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u062c\u06d5\u06b5\u062a\u06d5 \u0648 \u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u062a\u0627\u0646\u0647\u06ce\u0646\u06ce\u062a\u06d5 \u062f\u06d5\u0631 \u0628\u06d5 \u0633\u0627\u0648\u0627\u06cc\u06cc \u0648 \u067e\u0627\u0634\u0627\u0646 \u0628\u06d5\u062e\u06c6\u062f\u0627 \u062f\u06ce\u0646 \u0647\u06d5\u062a\u0627 \u062f\u06d5\u0628\u0646 \u0628\u06d5 \u062e\u06c6\u0631\u062a\u06ce\u06a9\u06cc \u062e\u0648\u0631\u062a \u0648 \u067e\u0627\u0634\u0627\u0646 \u0628\u06d5 \u067e\u06cc\u0631\u06cc \u062f\u06d5\u06af\u06d5\u0646 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u067e\u06cc\u0631 \u0628\u0628\u0646 \u062f\u06d5\u0645\u0631\u0646. \u0647\u06d5\u062a\u0627 \u062f\u06d5\u06af\u06d5\u0646 \u0628\u06d5 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u06a9\u06d5 \u0628\u06c6\u062a\u0627\u0646 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u06d5\u060c \u0634\u0627\u06cc\u06d5\u062a [\u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646] \u0695\u0627\u0645\u06ce\u0646\u0646.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0698\u06cc\u0646\u06ce \u062f\u06d5\u062f\u0627 \u0648 \u062f\u06d5\u0634\u0645\u0631\u06ce\u0646\u06ce. \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u06af\u06d5\u0631\u06d5\u06a9\u06cc \u0628\u06ce\u060c \u0647\u06d5\u0631 \u067e\u06ce\u06cc \u0626\u06ce\u0698\u06ce: \u0628\u0628\u06d5 \u0648\u060c \u062f\u06d5\u0628\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062a \u0646\u06d5\u062f\u06cc\u0648\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u06a9\u06ce\u0634\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0686\u0644\u06c6\u0646 \u0695\u06ce \u0644\u06d5 \u062e\u06c6 \u062f\u06d5\u06af\u06c6\u0695\u0646\u061f',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627 \u0628\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5 \u0648 \u0628\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0648\u0648\u0646\u060c \u0632\u06c6\u0631 \u0646\u0627\u062e\u0627\u06cc\u06d5\u0646\u06ce \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9 \u0686\u06d5\u0646\u062f \u062a\u06c6\u0642\u06cc \u062f\u06d5\u0645\u0644\u062f\u0627\u06cc\u06d5 \u0648 \u0628\u06d5 \u0632\u0646\u062c\u06cc\u0631 \u0695\u0627\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06ce\u0634\u0646\u060c',
    '\u0628\u06c6\u0646\u0627\u0648 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u06af\u06d5\u0631\u0645 \u0648 \u0644\u06d5\u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5 \u0644\u06d5\u0646\u0627\u0648 \u0626\u0627\u06af\u0631\u062f\u0627 \u06af\u0695 \u062f\u06d5\u06af\u0631\u0646.',
    '\u0626\u06d5\u0645 \u062c\u0627\u0631\u06d5 \u0644\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u067e\u0631\u0633\u0646: \u06a9\u0648\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u062f\u0627\u0646\u061b',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627. \u0626\u06ce\u0698\u0646: \u0644\u06d5\u0628\u06d5\u0631 \u0686\u0627\u0648\u0645\u0627\u0646 \u0648\u0646\u0646\u061b \u062e\u06c6 \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627\u0634 \u0628\u0648\u0648\u06cc\u0646\u060c \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627\u0645\u0627\u0646 \u0646\u06d5\u062f\u06d5\u067e\u06d5\u0631\u0633\u062a. \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u0627 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u06af\u0648\u0645\u0695\u0627 \u062f\u06d5\u06a9\u0627.',
    '\u0633\u0627 \u0626\u06d5\u0645\u06d5 \u06af\u06c6\u0695\u06cc \u06af\u06cc\u0627\u0646\u062a\u0627\u0646 \u0628\u06ce! \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646 \u0628\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u06a9\u06d5\u06cc\u0641\u0648\u0648 \u062f\u06d5\u06a9\u0631\u062f\u061b \u0628\u0627\u062f\u06cc \u0647\u06d5\u0648\u0627\u06cc\u06cc\u062a\u0627\u0646 \u062f\u06d5\u0646\u0648\u0627\u0646\u062f.',
    '\u0644\u06d5 \u062f\u06d5\u0631\u06af\u0627\u06a9\u0627\u0646\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0628\u0686\u0646\u06d5 \u0626\u06d5\u0648 \u062f\u06cc\u0648 \u0648 \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0628\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5. \u062c\u06ce\u06af\u06d5\u06cc \u062e\u06c6\u0628\u06d5\u0632\u0644\u0632\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0686\u06d5\u0646\u062f \u0646\u0627\u062e\u06c6\u0634\u06d5!',
    '\u0633\u0627 \u0628\u0648\u06ce\u0633\u062a\u06d5\u060c \u0686\u0648\u0646\u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u0627 \u0695\u0627\u0633\u062a\u06d5\u061b \u06cc\u0627\u0646 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u062a \u0644\u06d5\u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u06cc \u067e\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u060c \u0646\u06cc\u0634\u0627\u0646 \u0626\u06d5\u062f\u06d5\u06cc\u0646\u060c \u06cc\u0627\u0646 \u0645\u06d5\u0631\u06af\u062a \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u06ce\u0646\u06cc\u0646. [\u0647\u06d5\u0631\u0686\u06c6\u0646\u06ce\u06a9 \u0628\u06ce] \u0647\u06d5\u0645\u0648\u0648 \u0644\u0627\u06cc\u06d5\u06a9 \u0647\u06d5\u0631 \u062f\u06d5\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u0645\u0627\u0646.',
    '\u0644\u06d5 \u067e\u06ce\u0634 \u062a\u06c6\u0634\u062f\u0627 \u06af\u06d5\u0644\u06ce \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u0646. \u0633\u06d5\u0631\u0628\u0647\u0648\u0631\u062f\u06cc \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u0645\u0627\u0646 \u0628\u06c6 \u0628\u0627\u0633 \u06a9\u0631\u062f\u0648\u0648\u06cc\u061b \u0647\u06d5\u06cc\u0634\u0646 \u06a9\u06d5 \u0628\u0627\u0633\u0645\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u0648\u0648\u0646. \u0647\u06cc\u0686 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 \u0626\u06cc\u0632\u0646\u06cc \u0646\u06d5\u062f\u0627\u060c \u0647\u06cc\u0686 \u0646\u06cc\u0634\u0627\u0646\u06ce\u06a9 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u0627. \u0647\u06d5\u0631\u06af\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u062a\u060c \u062f\u06d5\u0628\u06ce \u0695\u0627\u0633\u062a\u06cc \u0628\u06d5 \u06a9\u0627\u0631\u06cc \u0645\u06d5\u0631\u062f\u0645 \u0695\u0627\u0628\u06af\u0627 \u0648\u061b \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u0695\u0648\u067e\u0648\u0648\u0686\u0646\u060c \u062a\u06ce\u062f\u0627 \u062f\u06d5\u0686\u0646.',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648\u06b5\u0633\u0627\u062a\u06cc \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627\u06a9\u0631\u062f\u0646\u060c \u0647\u06d5\u062a\u0627 \u0626\u06ce\u0648\u06d5 \u0633\u0648\u0627\u0631\u06cc\u0627\u0646 \u0628\u0646 \u0648 \u0644\u06ce\u06cc\u0627\u0646 \u0628\u062e\u06c6\u0646.',
    '\u06af\u06d5\u0644\u06ce\u06a9 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u062a\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u06cc\u06d5\u061b \u0626\u06d5\u0648 \u0646\u06cc\u0627\u0632\u06cc \u0644\u06d5 \u062f\u06b5\u062a\u0627\u0646\u062f\u0627\u060c \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u067e\u06ce\u06a9\u06cc \u062f\u06d5\u0647\u06ce\u0646\u0646 \u0648 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u06af\u06d5\u0645\u06cc\u06d5\u0634 \u0647\u06d5\u06b5\u062f\u06d5\u06af\u06cc\u0631\u06ce\u0646.',
    '\u0632\u06c6\u0631 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u062e\u06c6\u06cc\u060c \u062f\u06d5\u062e\u0627\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u06cc \u0626\u06ce\u0648\u06d5. \u0626\u06ce\u0648\u06d5 \u062d\u0627\u0634\u0627 \u0644\u06d5 \u06a9\u0627\u0645 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0646\u06d5\u06af\u06d5\u0695\u0627\u0648\u0646 \u06a9\u06d5 \u0628\u0632\u0627\u0646\u0646 \u0626\u0627\u06a9\u0627\u0645\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u06a9\u0648\u06ce \u06af\u06d5\u06cc\u0634\u062a\u061f \u06a9\u06d5 \u0644\u06d5\u0645\u0627\u0646\u06d5 \u0632\u06c6\u0631\u062a\u0631 \u0648 \u0628\u06d5\u0647\u06ce\u0632\u062a\u0631\u06cc\u0634 \u0628\u0648\u0648\u0646\u061b \u0634\u0648\u06ce\u0646\u06d5\u0648\u0627\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06d5\u0631\u062f\u0627 \u067e\u062a\u0631 \u0645\u0627\u0648\u06d5. \u0633\u0627 \u0647\u06d5\u0631\u0686\u06cc \u06a9\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u060c \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06c6 \u0646\u06d5\u062f\u0627\u0646.',
    '\u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0648\u06d5 \u0686\u0648\u0648\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u0628\u06d5\u0648 \u0647\u06c6\u06cc\u06d5 \u06a9\u06d5 \u0632\u06c6\u0631 \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u062f\u06b5\u06cc\u0627\u0646 \u062e\u06c6\u0634 \u0628\u0648\u0648\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u06af\u06d5\u0645\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u0631\u062f\u060c \u062f\u06d5\u0648\u0631\u06cc \u0644\u06ce\u062f\u0627\u0646.',
    '\u06a9\u0627\u062a\u06ce \u062f\u06cc\u062a\u06cc\u0627\u0646 \u0628\u06d5\u06b5\u0627\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u062f\u06ce\u062a\u06d5\u0633\u06d5\u0631\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06cc\u062a\u0631 \u062e\u0648\u0627\u06cc \u062a\u0627\u0642\u0627\u0646\u06d5 \u062f\u06d5\u0646\u0627\u0633\u06cc\u0646 \u0648 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0645\u0627\u0646\u06af\u0648\u062a: \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u062f\u0627\u0646\u060c \u062d\u0627\u0634\u0627\u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u067e\u0627\u0634 \u062f\u06cc\u062a\u0646\u06cc \u0633\u0632\u0627\u06cc \u0626\u06ce\u0645\u06d5\u060c \u0628\u0695\u0648\u0627\u0647\u06ce\u0646\u0627\u0646 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u067e\u06ce \u0646\u06d5\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646. \u0626\u06d5\u0645\u06d5 \u0695\u06ce\u0648\u0634\u0648\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0647\u06d5\u0631 \u0648\u0627 \u0628\u0648\u0648\u06d5. \u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u062f\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646\u060c \u0632\u06d5\u0631\u06d5\u0631\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u062d\u0627\u060c \u0645\u06cc\u0645.',
    '\u0647\u0627\u062a\u06c6\u062a\u06d5\u062e\u0648\u0627\u0631 \u0644\u06d5\u0648\u06a9\u06d5\u0633\u06d5\u0648\u06d5 \u06a9\u06d5 \u062f\u06d5\u0647\u0646\u062f\u06d5\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0628\u06d5\u0695\u0648\u0646\u06cc \u0634\u06cc \u06a9\u0631\u0627\u0648\u0646\u06d5\u062a\u06d5\u0648\u06d5\u061b \u0628\u06d5\u0632\u0645\u0627\u0646\u06cc \u0639\u06d5\u0631\u06d5\u0628\u06cc\u06d5\u061b \u0628\u06c6\u06af\u0631\u06c6\u06cc \u0645\u0631\u06c6\u06cc \u062f\u06d5\u06cc\u0632\u0627\u0646\u0646.',
    '\u0645\u0698\u062f\u06d5\u062f\u06d5\u0631\u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06d5\u0648 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0648\u0627\u0631\u062f\u0648 \u0626\u0627\u0645\u0627\u062f\u06d5 \u0646\u06cc\u0646 \u06a9\u06d5 \u0628\u06cc\u0628\u06cc\u0633\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u062a\u06c6 \u0628\u06c6\u0626\u06d5\u0648\u0645\u0627\u0646 \u0628\u0627\u0646\u06af \u062f\u06d5\u06a9\u06d5\u06cc\u060c \u062f\u06b5\u0645\u0627\u0646 \u067e\u06c6\u0634\u0634\u06cc \u0644\u06d5\u0633\u06d5\u0631\u06d5\u0648 \u06af\u0648\u06ce\u0645\u0627\u0646 \u06a9\u06d5\u0695\u06d5\u0648 \u067e\u06d5\u0631\u062f\u06d5\u06cc\u06d5\u06a9 \u0647\u06d5\u0633 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0648 \u062a\u06c6\u062f\u0627. \u062a\u06c6 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u06a9\u0627\u0631\u06cc \u062e\u06c6\u062a \u0628\u06d5\u0648\u060c \u0626\u06ce\u0645\u06d5\u0634 \u06a9\u0627\u0631\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646\u06cc\u0634 \u0645\u0631\u06c6\u06cc\u06d5\u06a9\u0645 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06ce\u0648\u06d5\u061b \u062e\u0648\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u0645 \u067e\u06ce\u062f\u06d5\u062f\u0627 \u067e\u06ce\u062a\u0627\u0646 \u0628\u06b5\u06ce\u0645 \u062a\u06d5\u0646\u06cc\u0627 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061b \u0695\u0648\u062a\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0645 \u0628\u06a9\u06d5\u0646 \u0648 \u062f\u0627\u0648\u0627\u06cc \u0628\u0648\u0631\u062f\u0646\u06cc \u0644\u06ce \u0628\u06a9\u06d5\u0646. \u0648\u0627\u0648\u06d5\u06cc\u0644\u0627 \u0628\u06c6\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5 \u062a\u06d5\u0646\u06cc\u0627 \u0646\u0627\u0632\u0627\u0646\u0646\u061b',
    '\u0626\u0648\u0627\u0646\u06d5 \u0646\u06d5 \u0632\u06d5\u06a9\u0627\u062a \u062f\u06d5\u062f\u06d5\u0646\u060c \u0646\u06d5\u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0695\u0648\u0627\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u0647\u06d5\u0631\u0648\u0647\u06d5\u0631 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u06a9\u06d5 \u0628\u06d5\u062f\u0648\u0695\u06c6\u0698 \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u060c \u062d\u0627\u0634\u0627\u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0647\u0627\u0648\u062a\u0627\u06cc \u0628\u06c6\u067e\u06d5\u06cc\u062f\u0627\u062f\u06d5\u06a9\u06d5\u0646\u061f \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062c\u06cc\u0647\u0627\u0646\u06d5.',
    '\u06a9\u06d5\u0698\u0648 \u06a9\u06c6\u0634\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u0628\u06d5\u0631\u0632 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u0648 \u067e\u06cc\u062a\u06cc \u062a\u06ce \u062e\u0633\u062a\u061b \u0647\u06d5\u0631 \u0644\u06d5\u0645\u0627\u0648\u06d5\u06cc \u0686\u0648\u0627\u0631\u0695\u06c6\u0698\u0627\u0646\u062f\u0627\u060c \u0628\u06c6\u0647\u06d5\u0631\u06a9\u06ce \u0628\u0698\u06cc\u0648\u06cc \u062f\u06d5\u0648\u06ce \u2013 \u0628\u06d5 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u2013 \u0628\u0698\u06cc\u0648\u06cc \u0628\u06c6\u062f\u0627\u0628\u06cc\u0646 \u06a9\u0631\u062f\u0646.',
    '\u067e\u0627\u0634\u0627\u0646 \u0695\u0648\u06cc \u0646\u0627 \u0644\u06d5\u0628\u06d5\u0631\u0632\u0627\u06cc\u06cc\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u062a\u0627\u0631\u06cc\u06a9\u0627\u0646 \u0628\u0648\u0648. \u0628\u06d5\u0648\u0627\u0646 \u0648 \u0628\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u06af\u0648\u062a: \u062e\u06c6\u0634 \u06cc\u0627 \u0646\u0627\u062e\u06c6\u0634\u060c \u062f\u06d5\u0628\u06ce \u0628\u06ce\u0646\u06d5 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u0645. \u06af\u0648\u062a\u06cc\u0627\u0646: \u0648\u0627 \u0628\u06d5\u062e\u06c6\u0634\u06cc \u0647\u0627\u062a\u06cc\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u062d\u06d5\u0648\u062a \u0626\u0627\u0633\u0645\u0627\u0646\u06cc \u0644\u06d5 \u062f\u0648\u0695\u06c6\u0698\u062f\u0627 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u061b \u0647\u06d5\u0631 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u06a9\u0627\u0631\u06cc \u062e\u06c6\u06cc \u0628\u06c6\u0646\u0627\u0631\u062f. \u0646\u0632\u06cc\u06a9\u062a\u0631 \u0626\u0627\u0633\u0645\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5 \u0686\u0631\u0627\u06cc\u0627\u0646 \u0695\u0627\u0632\u0627\u0646\u062f\u06d5\u0648\u06d5\u0648 \u067e\u0627\u0631\u0627\u0632\u062a\u0645\u0627\u0646. \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0632\u0627\u0646\u0627 \u0628\u0695\u06cc\u0627\u0631\u06cc \u0626\u06d5\u0648\u06a9\u0627\u0631\u06d5\u06cc \u062f\u0627\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0642\u0628\u0648\u06b5\u06cc\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u060c \u0628\u06ce\u0698\u06d5: \u0628\u06d5\u0648\u0647\u06d5\u0648\u0631\u06d5\u062a\u0631\u06cc\u0634\u0642\u06d5\u0648 \u062f\u06d5\u062a\u0631\u0633\u06ce\u0646\u0645 \u06a9\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0639\u0627\u062f\u0648 \u0633\u06d5\u0645\u0648\u0648\u062f\u06cc \u0648\u06d5\u0628\u06d5\u0631\u062f\u0627.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u2013 \u0686 \u0695\u0648\u0628\u06d5\u0695\u0648\u060c \u0686 \u067e\u0627\u0634\u0645\u0644\u06d5 \u2013 \u0644\u06ce\u06cc\u0627\u0646 \u0646\u0632\u06cc\u06a9 \u062f\u06d5\u0628\u0648\u0646\u06d5\u0648\u06d5 \u06a9\u06d5: \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627 \u0645\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645\u0627\u0646 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce\u0628\u0627\u060c \u0641\u0631\u06cc\u0634\u062a\u06d5\u06cc \u062f\u0627\u062f\u06d5\u0628\u06d5\u0632\u0627\u0646\u062f\u0646. \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u067e\u06ce \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u0646\u060c \u062d\u0627\u0634\u0627\u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u062c\u0627 \u0647\u06c6\u0632\u06cc \u0639\u0627\u062f \u0644\u06d5\u0646\u0627\u0695\u06d5\u0648\u0627 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5\u0648 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u0628\u06d5\u0632\u0644 \u0632\u0627\u0646\u06cc. \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u06a9\u0648\u0627\u061f \u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0647\u06ce\u0632\u062a\u0631\u06d5\u061f \u0646\u06d5\u06cc\u0627\u0646\u062f\u06cc\u0628\u0648 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u06af\u06d5\u0644\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0647\u06ce\u0632\u062a\u0631\u06d5\u061f \u0647\u06d5\u0631\u0648\u0627 \u0628\u06d5\u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0628\u06ce \u0628\u0695\u0648\u0627\u0628\u0648\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5 \u0695\u06d5\u0634\u06d5\u0628\u0627\u06cc\u06d5\u06a9\u06cc \u0628\u06d5\u0647\u06ce\u0632\u0645\u0627\u0646 \u2013 \u0644\u06d5\u0648\u0695\u06c6\u0698\u0627\u0646\u06d5\u062f\u0627 \u06a9\u06d5 \u0634\u0648\u0645\u0646 \u2013 \u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646\u061b \u062a\u0627 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u0627\u0628\u0695\u0648\u0628\u06d5\u0631\u06cc\u0627\u0646 \u2013 \u0644\u06d5\u0698\u06cc\u0627\u0646\u06cc \u0626\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u062f\u0627 \u2013 \u067e\u06ce \u0628\u0686\u06ce\u0698\u06cc\u0646. \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u06d5\u0648\u0644\u0627\u0634 \u0632\u06c6\u0631\u062a\u0631\u06d5\u0648 \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u0648\u062e\u06c6\u0645\u0627\u0646 \u0634\u0627\u0646\u06cc \u0647\u06c6\u0632\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f\u06cc\u0634 \u062f\u0627. \u0644\u06d5\u0628\u0627\u062a\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u0628\u0648\u0646\u06ce \u062d\u06d5\u0632\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0647\u06d5\u0631\u0646\u0627\u0628\u06cc\u0646\u0627\u0628\u0646. \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5\u0633\u06c6\u0646\u06af\u06d5\u06cc \u0626\u06d5\u0648\u06a9\u0627\u0631\u06cc \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f\u060c \u0647\u06d5\u0648\u0631\u06d5\u062a\u0631\u06cc\u0634\u0642\u06d5 \u06af\u0631\u062a\u0646\u06cc \u0648 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u0627\u0628\u0695\u0648\u0628\u06d5\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0647\u0627\u062a\u06d5\u062f\u06cc.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0695\u0632\u06af\u0627\u0631\u06a9\u0631\u062f \u06a9\u06d5 \u0628\u0628\u0648\u0646\u06d5 \u062e\u0648\u062f\u0627\u0646\u0627\u0633 \u0648 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u067e\u0627\u0631\u0627\u0633\u062a.',
    '\u0644\u06d5\u0648\u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0646\u06d5\u06cc\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u0627\u06af\u0631 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u06a9\u06c6\u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5\u0648 \u062f\u06d5\u0633\u062a\u06d5\u062f\u06d5\u0633\u062a\u06d5 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0631\u06ce\u0646\u060c',
    '\u0648\u06d5\u062e\u062a\u06ce \u062f\u06d5\u06af\u06d5\u0646\u06d5\u0644\u0627\u06cc \u0626\u0627\u06af\u0631\u060c \u0644\u06d5 \u0647\u06d5\u0631\u06a9\u0627\u0631\u06ce \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u060c \u06af\u0648\u06ce\u0686\u06a9\u06d5\u0648 \u0686\u06d5\u0645 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u067e\u0627\u0631\u0686\u06d5\u067e\u06ce\u0633\u062a\u06cc \u0644\u06d5\u0634\u06cc\u0627\u0646\u060c \u0644\u06d5\u062f\u0698\u06cc\u0627\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u06d5\u062f\u06d5\u0646.',
    '[\u0628\u06d5\u06af\u0644\u06d5\u06cc\u06cc\u06d5\u0648\u06d5] \u067e\u06ce\u0633\u062a\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0648\u06ce\u0646\u0646: \u0686\u06c6\u0646 \u062f\u0698\u0645\u0627\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u062a\u0627\u0646 \u062f\u0627\u061f \u0626\u06ce\u0698\u0646: \u062e\u0648\u0627 \u0648\u06d5\u0632\u0645\u0627\u0646\u06cc \u0647\u06ce\u0646\u0627\u06cc\u0646\u060c \u06a9\u06d5 \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0647\u06d5\u0645\u0648\u0634\u062a \u0628\u06ce\u0646\u06ce\u062a\u06d5 \u0642\u0633\u06d5\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0647\u06d5\u0648\u06d5\u06b5\u062c\u0627\u0631 \u0626\u06ce\u0648\u06d5\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u060c \u062f\u06d5\u0634\u0648\u06af\u06d5\u0695\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648.',
    '\u0644\u06d5\u0648\u0628\u0627\u0631\u06d5\u0648\u06d5 \u06a9\u06d5 \u06af\u0648\u06ce \u0648 \u0686\u0627\u0648\u0648 \u06af\u0634 \u067e\u06ce\u0633\u062a\u06cc \u0626\u06d5\u0646\u062f\u0627\u0645\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0634\u0627\u06cc\u06d5\u062a\u06cc\u062a\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u0628\u062f\u06d5\u0646\u060c \u0647\u06cc\u0686 \u062e\u06c6\u062a\u0627\u0646 \u062d\u0627\u0634\u0627\u0631\u0646\u06d5\u062f\u06d5\u062f\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0648\u0627\u0628\u0648 \u062e\u0648\u0627 \u0644\u06d5 \u0632\u06c6\u0631\u06ce \u0644\u06d5\u0648\u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0628\u06ce \u0626\u0627\u06af\u0627\u06cc\u06d5.',
    '\u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u0626\u06ce\u0648\u06d5 \u0648\u0627\u062a\u0627\u0646 \u06af\u0648\u0645\u0627\u0646 \u062f\u06d5\u0628\u0631\u062f\u060c \u06a9\u06d5 \u062a\u06d5\u0631\u062a \u0648\u062a\u0648\u0646\u0627\u06cc \u06a9\u0631\u062f\u0646 \u0648 \u0686\u0648\u0648\u0646\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u062e\u06c6\u0695\u0627\u06af\u0631\u0646\u060c \u0626\u06d5\u0648\u06d5 \u0626\u0627\u06af\u0631 \u062c\u06ce\u06af\u06d5\u06cc\u0627\u0646\u06d5. \u0626\u06d5\u0631 \u062f\u0627\u0648\u0627\u06cc \u0644\u06ce \u0628\u0648\u0631\u062f\u0646\u06cc\u0634 \u06a9\u06d5\u0646\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u06b5\u06ce\u06cc\u0627\u0646 \u0642\u0628\u0648\u0648\u06b5 \u0646\u0627\u06a9\u0631\u06ce.',
    '\u062f\u06c6\u0633\u062a\u06cc \u0648\u0627\u0634\u0645\u0627\u0646 [\u0644\u06d5 \u062f\u0646\u06cc\u0627] \u0628\u06c6\u067e\u06d5\u06cc\u062f\u0627\u06a9\u0631\u062f\u0646\u060c \u06a9\u0627\u0631\u06cc \u0626\u06ce\u0633\u062a\u06d5\u0648 \u0626\u0627\u06cc\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0628\u06c6\u062e\u06d5\u0645\u06b5\u0627\u0646\u062f\u0646. \u0628\u06c6 \u0626\u06d5\u0645\u0627\u0646 \u0648 \u0626\u06d5\u0648 \u062c\u0646\u06c6\u06a9\u06d5\u0648 \u0645\u0631\u06c6\u06a4\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631\u0644\u06d5\u0648\u0627\u0646 \u0628\u0648\u0648\u0646\u060c \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u062a\u06d5\u062c\u06ce\u061b \u0644\u06d5\u0628\u06d5\u0631\u0626\u06d5\u0648\u06d5\u06cc \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0628\u0648\u0646.',
    '\u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u06af\u0648\u06ce \u0628\u06c6\u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0645\u06d5\u06af\u0631\u0646\u061b \u0642\u0633\u06d5\u06cc \u0628\u06ce\u062a\u0627\u0645\u06cc \u062e\u06c6\u062a\u0627\u0646\u06cc \u062a\u06ce\u06a9\u06d5\u06b5 \u0628\u06a9\u06d5\u0646\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06d5\u0633\u06d5\u0631\u06cc\u0627 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u0646.',
    '\u0633\u0627 \u0645\u06d5\u0631\u062c \u0628\u06ce \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0646\u060c \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062f\u0698\u0648\u0627\u0631 \u0628\u0686\u06ce\u0698\u0646\u061b \u0644\u06d5\u0648\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u06a9\u0631\u062f\u060c \u062e\u0631\u0627\u067e\u062a\u0631 \u0626\u0627\u0632\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06cc\u0646.',
    '\u0633\u0632\u0627\u06cc \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5\u061b \u0644\u06d5\u0633\u06c6\u0646\u06af\u06d5\u06cc \u062d\u0627\u0634\u0627\u06a9\u0631\u062f\u0646\u06cc\u0627\u0646 \u0644\u06d5\u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u060c \u0626\u0627\u06af\u0631\u06cc \u0644\u06d5\u0628\u0646\u06d5 \u0646\u06d5\u0647\u0627\u062a\u0648 \u0626\u06d5\u0646\u0648\u0627\u06cc\u0627\u0646\u06d5.',
    '\u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u0644\u0627\u0645\u0627\u0646! \u0626\u06d5\u0648\u062f\u0648\u06a9\u06d5\u0633\u06d5 \u0644\u06d5 \u062c\u0646\u06c6\u06a9\u06d5\u0648 \u0645\u0631\u06c6\u06cc\u06d5\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u060c \u06a9\u06d5 \u0695\u06ce\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u06af\u06c6\u0695\u06cc\u061b \u0648\u06d5\u0698\u06ce\u0631\u067e\u0627\u06cc \u062e\u06c6\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646 \u0628\u0627 \u0644\u06d5\u0698\u06ce\u0631\u06cc \u0698\u06ce\u0631\u06d5\u0648\u06d5\u0628\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u06af\u0648\u062a\u0648\u06cc\u0627\u0646\u06d5: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0644\u06d5 \u06af\u0641\u062a\u06cc\u0627\u0646 \u0644\u0627\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0627\u0648\u06d5\u061b \u0641\u0631\u0634\u062a\u06d5 \u062f\u06ce\u0646\u06d5\u0644\u0627\u06cc\u0627\u0646 \u0648 \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0646\u06d5 \u0628\u062a\u0631\u0633\u0646\u060c \u0646\u06d5 \u062e\u06d5\u0645 \u0628\u062e\u06c6\u0646\u061b \u0645\u0698\u062f\u06d5\u062a\u0627\u0646 \u0628\u06ce \u06af\u06d5\u06cc\u0634\u062a\u0648\u0646\u06d5 \u0626\u06d5\u0648\u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u062a\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 - \u0644\u06d5 \u062f\u0646\u06cc\u0627\u0648\u0644\u06d5 \u0642\u06cc\u0627\u0645\u06d5\u062a \u2013 \u062f\u06c6\u0633\u062a\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u0646\u061b \u0644\u06d5\u0628\u06d5\u0647\u06d5\u0634\u062a\u0627 \u0647\u06d5\u0631\u0634\u062a\u06ce \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u06a9\u06d5\u0646 \u0648 \u0686\u06cc \u0628\u062e\u0648\u0627\u0632\u0646\u060c \u0626\u0627\u0645\u0627\u062f\u06d5\u06cc\u06d5.',
    '\u067e\u06d5\u0632\u06ce\u0631\u0627\u06cc\u06cc \u0644\u06d5 \u0644\u0627\u06cc\u06d5\u0646\u06cc \u06af\u0648\u0646\u0627\u0647\u0640 \u0628\u06d5\u062e\u0634\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u06a9\u06ce \u0644\u06d5\u0648\u06a9\u06d5\u0633\u06d5 \u0642\u0633\u06d5\u06cc \u0628\u06d5\u0695\u06ce \u0648\u062c\u06ce \u062a\u0631\u06d5\u060c \u06a9\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u0627 \u06af\u0627\u0632\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u062e\u06c6\u0634\u06cc \u0626\u0627\u06a9\u0627\u0631\u06cc \u067e\u06d5\u0633\u0646\u062f\u06d5\u0648 \u0626\u06ce\u0698\u06ce: \u0645\u0646\u06cc\u0634 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u0627\u0646\u0645\u061f',
    '\u0686\u0627\u06a9\u06d5\u0648 \u062e\u0631\u0627\u067e\u06d5 \u0647\u06d5\u0645\u0628\u06d5\u0631 \u0646\u06cc\u0646\u061b \u062a\u06c6 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0648\u06d5\u0631\u0627\u0645\u06cc \u0686\u0627\u06a9\u062a\u0631 \u0628\u062f\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0644\u06d5\u06af\u06d5\u06b5 \u062a\u06c6\u062f\u0627 \u062f\u0648\u0698\u0645\u0646\u06d5\u060c \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u0646\u0632\u06cc\u06a9\u062a.',
    '\u0626\u06d5\u0645 \u0626\u0627\u06a9\u0627\u0631\u06d5\u0634 \u0647\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9 \u062f\u06d5\u0628\u06ce \u06a9\u06d5 \u0628\u062a\u0648\u0627\u0646\u0646 \u062e\u06c6\u0695\u0627\u06af\u0631\u0628\u0646\u061b \u0647\u06d5\u0631\u06a9\u06ce\u0634 \u0626\u06d5\u0648\u0626\u0627\u06a9\u0627\u0631\u06d5\u06cc \u062a\u0648\u0634 \u0628\u06ce\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0641\u0631\u06d5 \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06d5.',
    '\u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06ce \u0644\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06d5\u0648\u06d5 \u062f\u0648\u0686\u0627\u0631\u06cc \u062e\u06d5\u06cc\u0627\u06b5\u0627\u062a\u06ce\u06a9 \u0628\u0648\u06cc\u060c \u06af\u0648\u0631\u062c\u06ce \u067e\u06d5\u0646\u0627 \u0628\u06d5\u062e\u0648\u0627\u0628\u06d5\u0631\u06d5\u061b \u06a9\u06d5 \u062e\u06c6\u06cc \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698\u0648\u0648 \u062e\u06c6\u0631\u0648 \u0645\u0627\u0646\u06af\u06cc\u0634 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u0646. \u0646\u06d5 \u0628\u06d5\u0631\u06d5\u0648\u062e\u06c6\u0631 \u0633\u0648\u0698\u062f\u06d5 \u0628\u06d5\u0631\u0646\u060c \u0646\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u0645\u0627\u0646\u06af\u061b \u0633\u0648\u0698\u062f\u06d5 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9 \u0628\u06d5\u0631\u0646 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0641\u06cc\u0632\u06cc\u0627\u0646 \u0646\u0648\u0627\u0646\u062f\u060c \u0626\u06d5\u0648\u06cc \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0646 \u2013 \u0628\u06d5\u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698 \u2013 \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u062f\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0648\u06d5\u0695\u06d5\u0632\u0646\u0627\u0628\u0646.',
    '\u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648 \u0632\u06d5\u0648\u06cc \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0645\u0627\u062a \u0628\u0648\u06d5\u0648 \u0647\u06d5\u0633\u062a\u06cc \u062f\u06d5\u0628\u06d5\u0631\u062f\u0627 \u0646\u06d5\u0645\u0627\u0648\u06d5. \u0647\u06d5\u0631\u06af\u0627 \u0628\u0627\u0631\u0627\u0646\u0645\u0627\u0646 \u0644\u06ce \u0628\u0627\u0631\u0627\u0646\u062f\u060c \u0695\u0627\u062f\u06d5\u0698\u06ce \u0648 \u062f\u06d5\u0628\u0648\u0698\u06ce\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0648\u06cc \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u06af\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631 \u0645\u0631\u062f\u0648\u0634 \u062f\u0627\u062f\u06d5\u06a9\u0627. \u062e\u0648\u062f\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0634\u062a\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5\u0648\u06d5\u06cc \u0695\u0627\u0633\u062a\u06d5\u060c \u06a9\u0644\u0627 \u06d5\u0628\u0646\u060c \u062e\u06c6 \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0646\u0627\u0634\u06ce\u0631\u0646\u06d5\u0648\u06d5. \u0626\u0627\u062e\u06c6 \u06a9\u06ce \u0644\u06d5\u06a9\u06ce \u0686\u0627\u062a\u0631\u06d5\u061f \u0626\u06d5\u0648\u06a9\u06d5\u0633\u06d5\u06cc \u062f\u06d5\u062e\u0631\u06ce\u062a\u06d5\u0626\u0627\u06af\u0631\u060c \u06cc\u0627\u0646 \u0626\u06d5\u0648\u06a9\u06d5\u0633\u06d5\u06cc \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u0628\u06ce \u062a\u0631\u0633 \u062f\u06ce\u061f \u0686\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062d\u06d5\u0632\u062f\u06d5\u06a9\u06d5\u0646 \u0628\u06cc\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648 \u0644\u06d5\u0647\u06d5\u0631\u0686\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06d5.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u06a9\u0627\u062a\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646\u06cc\u0627\u0646 \u0628\u06c6\u0647\u0627\u062a\u060c \u062f\u0698\u06cc \u0648\u06ce\u0633\u062a\u0627\u0646 \u0648 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u0647\u06ce\u0646\u0627\u061b \u06a9\u06d5 \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc \u0632\u06c6\u0631\u0628\u06d5\u0631\u0632\u0648 \u062c\u06ce\u06af\u06d5\u06cc \u0695\u06ce\u0632\u06d5.',
    '\u067e\u0695\u0648\u067e\u0648\u0686 \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0646\u0627\u0648\u06d5\u0633\u062a\u06ce \u2013 \u0686 \u0695\u0648\u0628\u06d5\u0695\u0648\u060c \u0686 \u067e\u0627\u0634\u0645\u0644\u06d5 \u2013 \u0626\u06d5\u0645\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06ce\u06a9 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u067e\u06d5\u0633\u0646\u06cc \u0628\u062f\u0631\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5\u062a\u06c6\u06cc \u0626\u06ce\u0698\u0646\u060c \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u067e\u06ce\u0634 \u062a\u06c6\u0634 \u0647\u06d5\u0631 \u0648\u062a\u0631\u0627\u0648\u06d5. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06ce\u06af\u0648\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u06af\u0648\u0646\u0627\u0647\u06a9\u0627\u0631\u0627\u0646 \u0639\u0627\u0641\u0648\u0628\u06a9\u0627\u0648 \u0628\u06c6\u0634\u06cc \u0647\u06d5\u06cc\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u0628\u062f\u0627.',
    '\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u0645\u0627\u0646 \u0628\u06d5 \u0639\u06d5\u062c\u06d5\u0645\u06cc \u0628\u0646\u0627\u0631\u062f\u0627\u06cc\u06d5\u060c \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0686\u06c6\u0646 \u0646\u06cc\u0634\u0627\u0646\u06cc \u0628\u06d5\u0695\u0648\u0646\u06cc \u0634\u06cc \u0646\u06d5\u06a9\u0631\u0627\u0648\u0646\u06d5\u062a\u06d5\u0648\u06d5\u061f \u062e\u06c6\u06cc \u0639\u06d5\u0631\u06d5\u0628\u06d5\u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0639\u06d5\u062c\u06d5\u0645\u06cc\u0646\u061f! \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0645\u06d5 \u0628\u06c6\u06a9\u06d5\u0633\u0627\u0646\u06ce \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0695\u06ce\u0628\u06d5\u0631\u0648 \u0686\u0627\u0631\u06d5\u06cc \u062f\u06d5\u0631\u062f\u0627\u0646\u06d5\u061b \u0626\u06d5\u0648\u06cc \u0628\u0695\u0648\u0627\u0634\u06cc \u067e\u06ce \u0646\u0627\u06a9\u06d5\u0646\u060c \u06af\u0648\u06ce \u06af\u0631\u0627\u0646\u0646 \u0644\u06d5\u0628\u06cc\u0633\u062a\u0646 \u0648 \u0686\u0627\u0648\u06cc\u0627\u0646 \u0644\u06d5\u062f\u06cc\u062a\u0646\u06cc \u06a9\u0648\u06ce\u0631\u06d5\u061b \u0647\u06d5\u0631 \u0626\u06ce\u0698\u06cc \u0644\u06d5\u062c\u06ce\u06cc \u062f\u0648\u0631\u06d5\u0648\u06d5 \u06af\u0627\u0632\u06cc \u062f\u06d5\u06a9\u0631\u06ce\u0646.',
    '\u06a9\u062a\u06ce\u0628\u06cc\u0634\u0645\u0627\u0646\u062f\u0627 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627\u060c \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u06cc \u06a9\u06ce\u0634\u06d5\u06a9\u0631\u0627. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u067e\u06ce\u0634\u0648\u062a\u0631 \u06af\u0641\u062a\u06cc \u0646\u06d5\u062f\u0627\u0628\u0627\u060c \u0628\u06d5\u06a9\u0627\u0631\u06cc\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u0634\u062a\u0646. \u0628\u06ce \u0633\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06ce\u06cc \u062f\u0648\u0648\u062f\u06b5\u0646 \u0648 \u0628\u06d5\u062f\u06af\u0648\u0645\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u062f\u06d5\u06a9\u0627 \u0648 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u0631\u0627\u067e\u06d5\u062f\u06d5\u06a9\u0627\u060c \u0644\u06d5\u062f\u0698\u06cc \u062e\u06c6\u06cc \u06a9\u0631\u062f\u0648\u06cc\u06d5\u062a\u06cc. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u063a\u06d5\u062f\u0631 \u0644\u06d5\u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0646\u0627\u06a9\u0627.',
    '\u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce \u06a9\u06d5\u06cc \u062f\u06d5\u0628\u06ce\u060c \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce. \u0628\u06d5\u0631\u0648\u0628\u0648\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0695\u0648\u06d5\u06a9\u06ce\u06a9 \u06a9\u06d5 \u0644\u06d5\u0634\u06a9\u06c6\u0641\u06d5 \u062f\u06ce\u0646\u06d5\u062f\u06d5\u0631\u0648 \u0647\u06d5\u0631 \u0645\u06ce\u0648\u06cc\u0646\u06d5\u06cc\u06d5\u06a9 \u0626\u0627\u0648\u0633\u06d5\u0648 \u0686\u06cc \u0644\u06d5\u0632\u06af\u062f\u0627\u0648 \u0686\u06cc \u0644\u06ce \u062f\u06d5\u0628\u06ce\u060c \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce. \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u0644\u06ce\u062f\u06d5\u06a9\u0627: \u0634\u06d5\u0631\u06cc\u06a9\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646 \u0644\u06d5\u06a9\u0648\u06ce\u0646\u061f \u0626\u06ce\u0698\u0646: \u0644\u0627\u06cc \u062a\u06c6 \u0626\u06d5\u06cc\u062f\u0631\u06a9\u06ce\u0646\u06cc\u0646\u061b \u06a9\u06d5\u0633\u0645\u0627\u0646 \u0626\u0627\u06af\u0627\u0645\u0627\u0646 \u0644\u06ce \u0646\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u062c\u06ce\u06af\u06d5\u06cc \u0647\u0627\u0646\u0627\u0648 \u0647\u0627\u0648\u0627\u0631\u06cc\u0627\u0646 \u0628\u0648\u0648\u0646\u060c \u0644\u06ce\u06cc\u0627\u0646 \u06af\u0648\u0645 \u0628\u0648\u0646 \u0648 \u062a\u06ce\u06af\u06d5\u06cc\u0634\u062a\u0648\u0646 \u06a9\u06d5 \u0647\u06cc\u0686 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u0645\u0631\u06c6 \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u0646\u0632\u0627 \u0628\u06c6\u0634\u062a\u06cc \u0628\u0627\u0634 \u0628\u06a9\u0627\u060c \u0648\u06d5\u0695\u06d5\u0632 \u0646\u0627\u0628\u06ce. \u0647\u06d5\u0631\u06af\u0627 \u062e\u0631\u0627\u067e\u06d5\u0634\u06cc \u062a\u0648\u0648\u0634 \u0647\u0627\u062a\u060c \u0647\u06cc\u0686 \u0626\u0627\u0648\u0627\u062a\u06ce\u06a9\u06cc \u0646\u0627\u0645\u06ce\u0646\u06ce \u0648 \u0646\u0627\u0647\u0648\u0645\u06ce\u062f\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u067e\u0627\u0634 \u062f\u06d5\u0631\u062f\u0648 \u0645\u06d5\u06cc\u0646\u06d5\u062a\u06ce \u062a\u0648\u0648\u0634\u06cc \u062f\u06ce\u060c \u062a\u0627\u0645\u06cc \u062e\u06c6\u0634\u06cc\u0645\u0627\u0646 \u067e\u06ce \u0686\u06d5\u0634\u0627\u0646\u062f\u060c \u0626\u06ce\u0698\u06ce: \u0626\u06d5\u0645\u06d5 \u0644\u06d5\u062e\u06c6\u0645 \u0695\u0627\u06cc\u06d5\u0648 \u0628\u0627\u0648\u06d5\u0695\u0646\u0627\u06a9\u06d5\u0645 \u0642\u06cc\u0627\u0645\u06d5\u062a \u0695\u0627\u0628\u06ce\u061b \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0634\u0645\u0628\u06d5\u0646\u06d5\u0648\u06d5 \u0628\u06c6\u0646\u0632\u06cc\u06a9 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645\u060c \u0633\u0648\u0648\u0631 \u062f\u06d5\u0632\u0627\u0646\u0645 \u067e\u062a\u0631 \u0686\u0627\u06a9\u06d5\u0645 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u0627. \u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0698\u06cc \u0626\u06ce\u0645\u06d5\u0646\u060c \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u060c \u062e\u06c6\u0645\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0686\u06ce\u0698\u06cc\u0646.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u0631\u06c6 \u0686\u0627\u06a9\u06d5\u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u067e\u06ce \u0646\u0627\u0632\u0627\u0646\u06ce \u0648 \u0641\u06cc\u0632\u062f\u06d5\u0646\u0648\u06ce\u0646\u06ce \u0648 \u0645\u0646 \u0645\u0646\u06cc\u06d5\u062a\u06cc\u061b \u0647\u06d5\u0631\u06af\u0627\u0634 \u062e\u0631\u0627\u067e\u06d5\u06cc \u062a\u0648\u0648\u0634 \u0628\u0628\u06ce\u060c \u0628\u06d5\u067e\u06d5\u0646\u0627\u0648\u06d5 \u0645\u0644 \u062f\u06d5\u0646\u06ce \u0644\u06d5\u067e\u0627\u0695\u0627\u0646\u06d5\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0645\u0646 \u0628\u06ce\u0698\u0646\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0645\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5\u0628\u06ce \u0648 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u067e\u06ce \u0646\u06d5\u06a9\u06d5\u0646\u060c \u062f\u06d5\u06cc \u06a9\u06ce \u0644\u06d5\u0648\u06d5 \u06af\u0648\u0645\u0695\u0627\u062a\u0631\u06d5 \u0647\u06d5\u062a\u0627\u0633\u06d5\u0631 \u0644\u0627\u0633\u0627\u0631\u06cc \u0628\u06a9\u0627\u061f',
    '\u0647\u0627\u06a9\u0627 \u0648\u0627\u0645\u0627\u0646 \u06a9\u0631\u062f \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5\u0626\u0627\u0633\u06c6\u06a9\u0627\u0646\u06cc \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u062f\u0627\u0648 \u0644\u06d5\u062e\u06c6\u0634\u06cc\u0627\u0646\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0628\u0628\u06cc\u0646\u0646\u061b \u0647\u06d5\u062a\u0627 \u0644\u06ce\u0627\u0646 \u0695\u0648\u0648\u0646 \u0628\u06ce\u062a\u06d5\u0648\u06d5 \u06a9\u06d5 \u0626\u06d5\u0648\u0695\u0627\u0633\u062a\u06d5. \u0626\u0627\u062e\u06c6 \u0647\u06ce\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0633 \u0646\u06cc\u06cc\u06d5\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0644\u06d5\u0647\u06d5\u0645\u0648\u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5\u061f',
    '\u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u06d5 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5\u062f\u06cc\u062f\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u062f\u0648\u0648\u062f\u06b5\u0646. \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u06d5 \u062e\u06c6\u06cc \u062f\u06d5\u0648\u0631\u06cc \u0644\u06d5 \u0647\u06d5\u0631\u0634\u062a \u062f\u0627\u0648\u06d5.',
    '\u062d\u0627\u060c \u0645\u06cc\u0645.',
    '\u0639\u06d5\u06cc\u0646\u060c \u0633\u06cc\u0646\u060c \u0642\u0627\u0641.',
    '\u062e\u0648\u062f\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0632\u0627\u0646\u0627 \u0644\u06d5\u06a9\u0627\u0631\u060c \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0626\u0627\u0648\u0627 \u0628\u06c6\u062a\u06c6\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0628\u06d5\u0631\u0644\u06d5\u062a\u06c6\u0634 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u06af\u0634 \u0628\u06c6\u0626\u06d5\u0648\u06d5\u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062c\u06ce \u0628\u06d5\u0631\u0632\u0648 \u0645\u06d5\u0632\u0646\u06d5.',
    '\u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u0631\u0632\u0628\u06d5\u0631\u0646 \u0648 \u0628\u06d5\u0633\u06d5\u0631\u06cc\u06d5\u06a9\u062f\u0627 \u062e\u0631\u0645\u06d5\u0628\u06a9\u06d5\u0646. \u0641\u0631\u06cc\u0634\u062a\u0627\u0646\u06cc\u0634 \u0628\u06d5\u0633\u0648\u067e\u0627\u0633\u06d5\u0648\u06d5 \u067e\u06d5\u0633\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646\u061b \u062f\u0627\u0648\u0627\u06cc \u0628\u06d5\u062e\u0634\u06cc\u0646 \u0628\u06c6 \u062e\u06d5\u06b5\u06a9\u06cc \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646 \u062f\u06d5\u06a9\u06d5\u0646. \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646 \u0626\u06d5\u0648\u06d5 \u0644\u06d5\u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648\u0646\u060c \u0628\u06d5\u062f\u06c6\u0633\u062a\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0632\u0627\u0646\u06cc\u0648\u0646 \u0648 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u067e\u06d5\u0631\u0633\u062a\u0646\u06cc\u0627\u0646\u0646\u060c \u062e\u0648\u0627 \u0626\u0627\u06af\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0648 \u0626\u06c6\u0628\u0627\u06b5\u06cc\u0627\u0646 \u0644\u06d5\u0633\u062a\u06c6\u06cc \u062a\u06c6\u0646\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u0648\u06d5\u0647\u0627\u06cc\u0646\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646\u06ce\u06a9\u06cc \u0639\u06d5\u0631\u06d5\u0628\u06cc\u0645\u0627\u0646 \u0628\u06c6 \u062a\u06c6 \u0647\u06d5\u0646\u0627\u0631\u062f\u060c \u0647\u06d5\u062a\u0627 \u062a\u06c6 \u0628\u06d5\u0648\u060c \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u062e\u06d5\u06b5\u06a9\u06cc \u0645\u06d5\u06a9\u06a9\u06d5\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u06d5\u0648\u0631\u0648\u0628\u06d5\u0631\u06cc \u0628\u06ce\u0646\u06cc \u0648 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u06a9\u06c6\u0628\u0648\u0646\u06d5\u0648\u06d5\u0634\u06cc\u0627\u0646 \u0628\u062a\u0631\u0633\u06ce\u0646\u06cc\u061b \u06a9\u06d5 \u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0648\u06d5\u062f\u06cc \u062f\u06ce. \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5\u0634\u06ce\u06a9 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u0646 \u0648 \u0628\u06d5\u0634\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0686\u0646\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u0627\u060c \u06af\u0634\u062a\u06cc \u062f\u06d5\u06a9\u0631\u062f\u06d5 \u0628\u06d5\u0631\u06d5\u06cc\u06d5\u06a9 \u0644\u06d5\u0626\u0627\u06cc\u06cc\u0646\u062f\u0627\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u06cc \u062d\u06d5\u0632\u06a9\u0627\u060c \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u062e\u0627. \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u06cc\u0634 \u062f\u06c6\u0633\u062a \u0648 \u06cc\u0627\u0631\u06cc\u06a9\u0627\u0631\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u062f\u06cc\u0648\u06d5\u062a\u06d5\u0648\u06d5 \u06a9\u06d5 \u062f\u06c6\u0633\u062a\u06cc\u0627\u0646 \u0628\u06ce\u061f \u062f\u06c6\u0633\u062a\u06cc \u0695\u0627\u0633\u062a\u06cc \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0648 \u0626\u06d5\u0648 \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0645\u0631\u06cc\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648\u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5\u0648 \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06d5\u0645\u0648\u0634\u062a\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0631\u0686\u06cc \u067e\u06ce\u06a9 \u0646\u06d5\u0647\u0627\u062a\u0646\u060c \u062e\u0648\u062f\u0627 \u0628\u06d5 \u0646\u0627\u0648\u062a\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627. \u0626\u06d5\u0648\u062e\u0648\u062f\u0627\u06cc\u06d5 \u0645\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u062f\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u0645\u0646 \u062e\u06c6\u0645 \u0647\u06d5\u0631 \u0628\u06d5\u0648\u0633\u067e\u0627\u0631\u062f\u0648\u06d5\u0648 \u0647\u06d5\u0631 \u0695\u0648\u062f\u06d5\u06a9\u06d5\u0645\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648.',
    '\u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0648 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u062c\u0648\u0648\u062a\u06d5\u06cc \u0644\u06ce \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u0646 \u0648 \u0644\u06d5 \u0645\u0627\u06b5\u0627\u062a\u06cc\u0634 \u0648\u0644\u0641\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f \u062a\u0627 \u0632\u06c6\u0631\u062a\u0631 \u067e\u06d5\u0631\u06d5\u0648 \u067e\u06ce \u0628\u062f\u0627. \u0647\u06cc\u0686 \u0634\u062a\u06ce \u0648\u06ce\u0646\u06d5\u06cc \u0626\u06d5\u0648 \u0646\u06cc\u06cc\u06d5\u061b \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0628\u06cc\u0646\u0627\u06cc\u06d5.',
    '\u06a9\u0644\u06cc\u0644\u0627\u0646\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0644\u062c\u06d5\u0645 \u0626\u06d5\u0648\u06d5. \u0695\u06c6\u0632\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06d5\u0645\u06d5\u06cc\u0644\u06cc \u0626\u06d5\u0648\u06d5 \u0628\u06c6\u06cc\u06d5\u06a9\u06ce \u0641\u0631\u06d5 \u062f\u06d5\u0646\u06ce\u0631\u06ce \u0648 \u0628\u06c6\u06cc\u06d5\u06a9\u06ce \u06a9\u06d5\u0645. \u0626\u0627\u06af\u0627\u06cc \u0644\u06d5\u0647\u06d5\u0645\u0648\u0634\u062a \u0647\u06d5\u06cc\u06d5.',
    '\u0695\u06ce\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0644\u06d5 \u062f\u06cc\u0646\u062f\u0627\u0631\u06cc \u0628\u06c6\u0646\u06af\u06c6 \u06a9\u06ce\u0634\u0627\u060c \u06a9\u06d5 \u0626\u06d5\u0633\u067e\u0627\u0631\u062f\u06cc \u0646\u0648\u0648\u062d\u06cc\u0634 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u0695\u06ce\u06af\u06d5 \u0628\u0695\u0648\u0627. \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u0634\u0645\u0627\u0646 \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u062a\u06c6 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u061b \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0648 \u0645\u0648\u0648\u0633\u0627\u0648 \u0639\u06cc\u0633\u0627\u0634\u0645\u0627\u0646 \u0695\u0627\u0626\u06d5\u0633\u067e\u0627\u0631\u062f\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0645 \u062f\u06cc\u0646\u06d5\u0628\u0646 \u0648 \u0645\u06d5\u0634\u06cc \u06a9\u06d5\u0646 \u0628\u06d5 \u0686\u06d5\u0646\u062f\u0628\u06d5\u0631\u06d5\u06a9\u06cc. \u0626\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u062e\u06c6\u06b5\u06a9\u06cc \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u0646\u060c \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u0627 \u067e\u06d5\u06cc\u062f\u0627\u062f\u06d5\u06a9\u06d5\u0646\u060c \u0632\u06c6\u0631\u062f\u0698\u0648\u0627\u0631\u06d5. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u06a9\u06ce \u0628\u062e\u0648\u0627\u0632\u06ce\u060c \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u0627 \u062f\u06d5\u06cc\u0628\u0698\u06ce\u0631\u06ce \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u0628\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0631\u06d5\u0648\u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u0646\u06d5\u0634 \u0628\u0628\u0648\u0646\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u062f\u06d5\u0633\u062a\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0648\u06d5\u062e\u062a\u06ce \u0628\u0627\u0633\u06cc \u0626\u06d5\u0645 \u062f\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u0632\u0627\u0646\u06cc. \u0626\u06ce\u0631\u06d5\u06cc\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u06cc\u0627\u0646\u062f\u0627 \u0644\u06d5 \u0628\u06d5\u0631 \u06cc\u06d5\u06a9\u06cc \u0647\u06d5\u06b5\u0648\u06d5\u0634\u0627\u0646\u062f\u0646. \u062e\u06c6\u0626\u06d5\u06af\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0632\u0648\u062a\u0631 \u0645\u0627\u0648\u06d5\u06cc \u0628\u06c6\u062f\u06cc\u0627\u0631\u06cc \u0646\u06d5\u06a9\u0631\u062f\u0646\u0627\u06cc\u06d5\u060c \u062f\u06d5\u0645\u06ce\u06a9 \u0628\u0648\u0648 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u06a9\u0631\u0627\u0628\u0648. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06c6\u0628\u06d5\u062c\u06ce \u0645\u0627\u060c \u0644\u06ce\u06cc \u06a9\u06d5\u0648\u062a\u0646\u06d5 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u06cc \u0648 \u06af\u0648\u0645\u0627\u0646\u06d5\u0648\u06d5.',
    '\u0633\u0627 \u0628\u06c6\u0626\u06d5\u0645\u06d5\u060c \u062a\u06c6 \u06af\u0627\u0632\u06cc \u0644\u06d5\u0645\u06d5\u0631\u062f\u0645 \u0628\u06a9\u06d5 \u0648 \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0641\u06d5\u0631\u0645\u0627\u0646\u062a \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5\u060c \u0647\u06d5\u0631 \u0633\u0648\u0631\u0628\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0648 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0648\u06d5. \u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0628\u06d5 \u0647\u06d5\u0631\u06a9\u062a\u06ce\u0628\u06ce \u062e\u0648\u062f\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5\u062e\u0648\u0627\u0631\u06ce\u060c \u0628\u0695\u0648\u0627\u0645 \u0647\u06d5\u06cc\u06d5\u061b \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634\u0645 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5\u060c \u0628\u06d5 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u0628\u06d5\u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u062a\u0627\u0646 \u0695\u0627\u0628\u06af\u06d5\u0645\u061b \u0626\u06ce\u0645\u06d5 \u0648 \u0626\u06ce\u0648\u06d5 \u062e\u0648\u062f\u0627 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646\u06d5\u061b \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u06c6\u062e\u06c6\u0645\u0627\u0646\u060c \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06c6\u062e\u06c6\u062a\u0627\u0646\u061b \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u0648 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u062c\u06ce \u0628\u06c6 \u0628\u06d5\u0647\u0627\u0646\u06d5 \u0646\u06d5\u0645\u0627\u0648\u06d5\u061b \u062e\u0648\u0627 \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u06a9\u06c6\u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u0648 \u0686\u0627\u0631\u06d5\u0646\u0648\u0633\u06cc\u0634 \u0647\u06d5\u0631 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0645\u06d5\u0695 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0628\u0695\u0648\u0628\u06d5\u0647\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06cc\u0634\u06cc\u06d5\u0648\u06d5 \u0647\u0627\u062a\u0648\u0648\u0646\u060c \u0647\u06d5\u0645\u0648\u0648 \u0628\u0695\u0648\u0628\u06d5\u0647\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0628\u06ce \u0628\u0627\u06cc\u06d5\u062e\u06d5\u0648 \u0648\u06d5\u0628\u06d5\u0631 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06cc \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646 \u0648 \u0628\u06d5\u062a\u0648\u0646\u062f\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u0628\u06c6\u0695\u0627\u0633\u062a\u06cc \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5\u062e\u0648\u0627\u0631\u06ce\u060c \u062a\u06d5\u0631\u0627\u0632\u0648\u0634\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0647\u06d5\u0646\u0627\u0631\u062f. \u062a\u06c6 \u0686\u0648\u0632\u0627\u0646\u06cc\u061f \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0647\u0627\u062a\u0646\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u0646\u0632\u06cc\u06a9 \u0628\u06ce.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5 \u0626\u06d5\u0648\u0695\u06c6\u0698\u06d5\u062f\u06ce\u060c \u067e\u06d5\u0644\u06d5 \u0644\u06d5\u0647\u0627\u062a\u0646\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0644\u06ce\u06cc \u062f\u06d5\u062a\u0631\u0633\u0646\u061b \u062f\u06d5\u0634\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0626\u06d5\u0648\u06d5 \u0695\u0627\u0633\u062a\u06d5. \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646 \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0633\u06d5\u06b5\u0627\u0695\u0627\u0628\u0648\u0646 \u062f\u0695\u062f\u06c6\u0646\u06af\u0646\u060c \u0632\u06c6\u0631 \u0644\u06d5\u0695\u06ce \u062f\u0648\u0631\u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u06d5\u0648\u06d5.',
    '\u062e\u0648\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0628\u06d5\u06a4\u06cc\u0627\u0646\u06d5. \u0628\u06d5\u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce\u060c \u0628\u0698\u06cc\u0648 \u062f\u06d5\u062f\u0627. \u062e\u0627\u0648\u06d5\u0646 \u0647\u06ce\u0632\u0648 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc\u0634 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062f\u0627\u0647\u0627\u062a\u06cc \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc \u0628\u0648\u06ce\u060c \u062f\u0627\u0647\u0627\u062a\u06cc \u067e\u062a\u0631 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06ce. \u06a9\u06ce\u0634 \u062f\u0627\u0647\u0627\u062a\u06cc \u0644\u06d5\u0645 \u062f\u0646\u06cc\u0627\u06cc\u06d5\u062f\u0627 \u06af\u06d5\u0631\u06d5\u06a9 \u0628\u06ce\u060c \u0644\u06d5\u0645. \u062f\u0646\u06cc\u0627 \u0628\u06d5\u0634\u06cc \u062f\u06d5\u062f\u06d5\u06cc\u0646 \u0648 \u0644\u06d5\u0648\u062f\u0646\u06cc\u0627 \u0628\u06d5\u0634\u06ce\u06a9\u06cc \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0634\u062a\u0627\u0646\u06ce \u0648\u0627 \u0628\u06d5\u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u0627 \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u06a9\u06d5 \u062f\u06cc\u0646\u06ce\u06a9\u06cc \u0648\u06d5\u0647\u0627\u06cc\u0627\u0646 \u0628\u06c6\u062f\u0627\u0647\u06ce\u0646\u0627\u0628\u0646 \u0648 \u062e\u0648\u062f\u0627 \u0626\u06cc\u062c\u0627\u0632\u06d5\u06cc \u0646\u06d5\u062f\u0627\u0628\u0646\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0695\u06cc\u0627\u0631\u06cc \u06cc\u06d5\u06a9\u062c\u0627\u0631\u06cc \u062f\u06d5\u0631\u0646\u06d5\u0686\u0648\u0628\u0627\u060c \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0695\u0627\u06cc\u06d5\u0648\u06d5. \u0646\u0627\u0647\u06d5\u0642\u06d5\u06a9\u0627\u0646 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0648 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u06cc\u060c \u0644\u06d5\u0626\u0627\u06a9\u0627\u0645\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u0646\u061b \u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631 \u062a\u0648\u0634\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce. \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5\u0628\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646\u060c \u0644\u06d5 \u0633\u06d5\u06cc\u0631\u0627\u0646\u06af\u0627\u06cc \u0628\u0627\u062e\u06d5\u06a9\u0627\u0646\u062f\u0627\u0646 \u0648 \u0686\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u0628\u06a9\u06d5\u0646\u060c \u0644\u06d5\u0626\u0627\u0644\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u067e\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u06af\u0627. \u0626\u06d5\u0645\u06d5\u0634 \u0628\u06d5\u062e\u0634\u0634\u06ce \u0645\u06d5\u0632\u0646\u06d5.',
    '\u0628\u06d5\u0645\u06d5 \u062e\u0648\u062f\u0627 \u0645\u0648\u0698\u062f\u06d5 \u0628\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062f\u06d5\u062f\u0627\u060c \u06a9\u06d5\u0628\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0645\u0646 \u0645\u0632\u06cc\u06d5\u06a9\u0645 \u0644\u06d5\u0646\u06af\u06c6 \u0646\u0627\u0648\u06ce\u060c \u0645\u06d5\u06af\u06cc\u0646 \u062e\u06c6\u0634 \u0648\u06cc\u0633\u062a\u0646\u06cc \u062e\u0632\u0645\u06d5\u0646\u0632\u06cc\u06a9\u06d5\u06a9\u0627\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0686\u0627\u06a9\u06d5\u06cc\u06d5\u06a9 \u0628\u06a9\u0627\u060c \u067e\u062a\u0631 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u06d5\u06cc\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0695\u06ce \u0648 \u0628\u06d5 \u0686\u0627\u06a9\u06d5\u06cc \u0639\u06d5\u0628\u062f\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce\u0698\u0646: \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u06ce. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce\u060c \u0645\u06c6\u0631 \u0628\u06d5 \u0633\u06d5\u0631 \u062f\u06b5\u062a\u06d5\u0648\u06d5 \u062f\u06d5\u0646\u06ce. \u062e\u0648\u062f\u0627 \u2013 \u0647\u06d5\u0631 \u0628\u06d5\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6\u06cc \u2013 \u067e\u0648\u0686 \u0644\u06d5\u0626\u0627\u0631\u0627\u062f\u0627 \u0646\u0627\u0647\u06ce\u06b5\u06ce \u0648 \u0695\u0627\u0633\u062a\u06cc \u0644\u06d5\u062c\u06ce\u060c \u062c\u06ce\u062f\u0627\u0631\u062f\u06d5\u06a9\u0627\u061b \u0626\u06d5\u0648 \u0628\u06d5\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u062f\u06b5\u0627\u0646\u062f\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u0644\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u0642\u0628\u0648\u06b5 \u062f\u06d5\u06a9\u0627\u0648 \u0644\u06d5\u06a9\u0627\u0631\u06d5 \u062e\u0631\u0627\u067e\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0686\u0627\u0648\u062f\u06d5\u067e\u06c6\u0634\u06ce \u0648 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u0646\u0632\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0642\u0628\u0648\u06b5 \u062f\u06d5\u06a9\u0627\u0648 \u0644\u06d5\u0686\u0627\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0628\u06d5\u0634\u06cc \u0632\u06c6\u0631\u062a\u0631\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06ce. \u0628\u06c6 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u06cc\u0634\u060c \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0632\u06c6\u0631\u062f\u0698\u0648\u0627\u0631 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u2013 \u0628\u06ce \u0633\u06d5\u0631\u0648\u0628\u06d5\u0631 \u2013 \u0628\u0698\u06cc\u0648\u06cc \u0628\u06c6\u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u0646\u0627\u0631\u062f\u0628\u0627\u060c \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u06cc\u0627\u063a\u06cc \u062f\u06d5\u0628\u0648\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631\u0628\u06d5\u0645\u06d5\u06cc\u0644\u06cc \u062e\u06c6\u06cc \u0648 \u0628\u06d5\u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627\u060c \u06a9\u06d5 \u062e\u06c6\u06cc \u0644\u06d5\u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0648 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06d5.',
    '\u0647\u06d5\u0631\u0626\u06d5\u0648\u06cc\u0634\u06d5 \u067e\u0627\u0634 \u0646\u0627\u0647\u0648\u0645\u06ce\u062f\u0628\u0648\u0646 \u0644\u06d5\u0628\u0627\u0631\u0627\u0646\u060c \u0628\u0627\u0631\u0627\u0646\u06d5 \u062f\u0627\u062f\u06d5\u0628\u0627\u0631\u06ce\u0646\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc \u062e\u06c6\u0634\u06cc \u0628\u06b5\u0627\u0648\u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631\u0626\u06d5\u0648\u06d5 \u0628\u06c6 \u0645\u06d5\u0632\u0646\u0627\u06cc\u06d5\u062a\u06cc \u0626\u06d5\u0634\u06ce \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u062f\u06cc \u0628\u062f\u0631\u06ce.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06d5\u0648\u0646: \u062f\u0627\u0647\u06ce\u0646\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0648 \u0626\u06d5\u0648\u0647\u06d5\u0645\u0648 \u062c\u0627\u0646\u06d5\u0648\u06d5\u0631\u0627\u0646\u06d5\u06cc \u2013 \u06a9\u06d5 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u0626\u06d5\u0648\u062f\u0648\u0627\u0646\u06d5\u062f\u0627 \u2013 \u0628\u06b5\u0627\u0648\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646\u06d5\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631\u06af\u0627\u0634 \u0628\u06c6\u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce\u060c \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06d5\u06cc\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u06a9\u06c6\u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9 \u0628\u0648\u0648\u0646\u060c \u0628\u06d5 \u0647\u06c6\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646\u06d5\u061b \u0644\u06d5\u0632\u06c6\u0631 \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u0627\u0646\u06cc\u0634\u062a\u0627\u0646 \u0686\u0627\u0648\u062f\u06d5\u067e\u06c6\u0634\u06ce.',
    '\u0646\u0627\u0634\u062a\u0648\u0627\u0646\u0646 \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5\u060c \u062e\u06c6 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062f\u0632\u0646\u06d5\u0648\u06d5. \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627\u0634\u060c \u0646\u06d5 \u0647\u06cc\u0686 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u0646\u06d5 \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u062a\u0627\u0646 \u062f\u06ce.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06d5\u0648\u0646: \u0626\u06d5\u0648\u06af\u06d5\u0645\u06cc\u0627\u0646\u06d5\u06cc \u2013 \u06a9\u06d5 \u0686\u06cc\u0627\u0626\u0627\u0633\u0627 \u2013 \u0644\u06d5\u0646\u0627\u0648\u062f\u06d5\u0631\u06cc\u0627 \u062f\u06ce\u0646 \u0648 \u062f\u06d5\u0686\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632\u06a9\u0627\u060c \u0628\u0627 \u0695\u0627\u062f\u06d5\u06af\u0631\u06ce \u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u062f\u06d5\u0631\u06cc\u0627 \u0628\u06d5\u0642\u06d5\u062a\u06cc\u0633\u06cc \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631\u0648 \u0633\u0648\u067e\u0627\u0633\u06a9\u0627\u0631\u0646\u060c \u0628\u06d5\u06b5\u06af\u06d5 \u0641\u0631\u06d5\u0646.',
    '\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u0632\u0627\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u0646\u0648\u0642\u0645 \u0628\u0648\u0646\u06cc\u0627\u0646 \u0628\u06a9\u0627. \u0644\u06d5\u0632\u06c6\u0631\u06ce \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce.',
    '\u062a\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u06a9\u06ce\u0634\u06d5\u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0647\u06cc\u0686 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u0631\u0627\u0648\u06d5\u060c \u0647\u06c6\u06cc \u0698\u06cc\u0627\u0646\u06d5 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u06cc\u06d5. \u0626\u06d5\u0648\u0634\u062a\u06d5\u0634 \u06a9\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u060c \u0628\u06c6\u0626\u06d5\u0648\u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06d5\u06cc \u0647\u06d5\u0631 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u0634\u062a \u0626\u06d5\u0633\u062a\u0648\u0631\u0646\u060c \u0628\u0627\u0634\u062a\u0631\u06d5\u0648 \u0628\u06d5\u067e\u06ce\u0632\u062a\u0631\u06cc\u0634\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u0646 \u0644\u06d5\u062a\u0627\u0648\u0627\u0646\u06cc \u06af\u06d5\u0648\u0631\u06d5 \u062f\u0648\u0631\u0646 \u0648 \u0644\u06d5 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u0634\u0648\u0631\u06d5\u06cc\u06cc \u062f\u06d5\u067e\u0631\u06cc\u0646\u06af\u06ce\u0646\u06d5\u0648\u06d5\u0648 \u0644\u06d5 \u062c\u06d5\u0646\u06af\u06d5\u06cc \u062a\u0648\u0695\u06d5\u0628\u0648\u0646\u06cc\u0627\u0646\u062f\u0627 \u0639\u06d5\u0641\u0648\u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0686\u0648\u0646 \u0648 \u0646\u0648\u06ce\u0698\u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u06a9\u0627\u0631\u06af\u06d5\u0644\u06cc\u0634\u06cc\u0627\u0646 \u0628\u06d5\u0695\u0627\u0648\u06ce\u0698\u06d5\u0648 \u0644\u06d5\u0648\u0628\u0698\u06cc\u0648\u06d5\u06cc \u067e\u06ce\u0645\u0627\u0646 \u0628\u06d5\u062e\u0634\u06cc\u0648\u0646\u060c \u062f\u06d5\u0628\u06d5\u062e\u0634\u0646.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0626\u06d5\u06af\u06d5\u0631 \u0632\u06c6\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06a9\u0631\u06ce\u060c \u062a\u06c6\u06b5\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0633\u0632\u0627 \u0628\u06c6 \u0647\u06d5\u0631\u062e\u0631\u0627\u067e\u06d5\u06cc\u06d5\u06a9\u060c \u062e\u0631\u0627\u067e\u06d5\u06cc\u06d5\u06a9\u06cc \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u06d5. \u06a9\u06ce\u0634 \u0644\u06d5\u062a\u06c6\u06b5\u06d5 \u0686\u0627\u0648\u0628\u067e\u06c6\u0634\u06ce \u0648 \u06a9\u06c6\u0634\u0634 \u0628\u06c6\u0626\u0627\u0634\u062a\u06cc \u0628\u06a9\u0627\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5. \u0628\u06ce \u0633\u06c6 \u062e\u0648\u062f\u0627 \u0646\u0627\u0647\u06d5\u0642\u06a9\u0627\u0631\u0627\u0646\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce\u0646.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u067e\u0627\u0634 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06ce\u06a9\u0631\u062f\u0646\u060c \u062a\u06c6\u06b5\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0633\u062a\u06ce\u0646\u0646\u06d5\u0648\u06d5\u060c \u062e\u06d5\u062a\u0627\u0628\u0627\u0631 \u0646\u06cc\u0646.',
    '\u062e\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc\u06d5 \u2013 \u0646\u0627\u0695\u06d5\u0648\u0627 \u2013 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5\u0645\u06d5\u0631\u062f\u0645 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06b5\u0627\u062a\u062f\u0627 \u0628\u06d5 \u0645\u0644\u06d5\u0648\u0695\u06cc\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0628\u06c6\u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062e\u06c6\u0695\u0627\u06af\u0631\u0628\u06ce \u0648 \u0644\u06d5\u062a\u06c6\u06b5\u06d5 \u0686\u0627\u0648\u067e\u06c6\u0634\u06cc \u0628\u06a9\u0627\u060c \u0626\u06d5\u0645\u06d5 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5\u06a9\u0627\u0631\u06d5 \u067e\u06d5\u0633\u0646\u062f\u06d5\u06a9\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u0648\u0627 \u0644\u06ce\u06cc \u06af\u06c6\u0695\u06cc\u0628\u06ce\u060c \u0626\u06cc\u062a\u0631 \u06a9\u06d5\u0633\u06cc \u0628\u06d5\u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648\u0627\u060c \u0646\u0627\u0628\u06ce \u0628\u06d5\u062f\u06c6\u0633\u062a. \u062a\u06c6 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0646 \u0648\u06d5\u062e\u062a\u06ce \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u0646\u060c \u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0647\u06cc\u0686 \u062f\u06d5\u0631\u06d5\u0631\u062a\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u0628\u06d5\u0646\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u06af\u0631\u060c \u062f\u06d5\u06cc\u0627\u0646\u0628\u06cc\u0646\u06cc \u0632\u06c6\u0631\u0628\u06d5\u0633\u06d5\u0631\u06a9\u0632\u06cc \u062f\u0627\u0645\u0627\u0648\u0646 \u0648 \u0644\u06d5\u0628\u0646\u06d5\u0648\u06d5 \u0686\u0627\u0648\u06d5\u0686\u0627\u0648\u06cc\u0627\u0646\u06d5. \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06cc\u0634 \u0626\u06ce\u0698\u0646: \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06cc\u0627\u0646\u060c \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u062f\u06c6\u0695\u0627\u0646\u062f\u0648\u06d5. \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0626\u06d5\u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc \u06a9\u06d5\u0631\u0627\u0646\u06d5\u060c \u0647\u06d5\u062a\u0627\u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062e\u0648\u062f\u0627 \u06af\u0648\u0645\u0695\u0627\u06cc \u0628\u06a9\u0627\u060c \u0647\u06cc\u0686 \u0695\u06ce\u06cc\u06d5\u06a9\u06cc \u0628\u06c6\u0646\u0627\u0645\u06ce\u0646\u06ce.',
    '\u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0695\u06c6\u0698\u06ce \u0628\u06ce \u06a9\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u0648\u06d5 \u0647\u06cc\u0686 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0628\u06c6\u0646\u06cc\u06d5\u060c \u0628\u06d5\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u06cc\u0686 \u067e\u06d5\u0646\u0627\u062a\u0627\u0646 \u062f\u06d5\u0633 \u0646\u0627\u06a9\u06d5\u0648\u06ce \u0648 \u0647\u06cc\u0686 \u062d\u0627\u0634\u0627\u062a\u0627\u0646 \u067e\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0633\u0627\u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0648\u0627\u0631\u062f\u060c \u0626\u06ce\u0645\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u0628\u06c6\u06cc\u06d5 \u0646\u06d5\u0646\u0627\u0631\u062f \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u067e\u0627\u0633\u062f\u0627\u0631\u0628\u06cc\u061b \u062a\u06d5\u0646\u06cc\u0627 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u062a \u0644\u06d5\u0633\u06d5\u0631\u06d5\u060c \u0645\u06d5\u0628\u06d5\u0633\u062a\u06d5\u06a9\u06d5 \u0695\u0627\u0628\u06af\u06d5\u06cc\u06ce\u0646\u06cc. \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631\u06af\u0627 \u06a9\u0627\u0631\u06ce\u06a9 \u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0645\u0631\u06c6 \u062a\u0627\u0645\u06cc \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0686\u06ce\u0698\u06ce\u060c \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06ce \u0648 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5 \u0647\u06c6\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u062e\u0631\u0627\u067e\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u06d5 \u0686\u06c6\u0631\u062a\u0645\u06ce\u06a9\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0631 \u062f\u06d5\u0632\u0627\u0646\u0646 \u067e\u06ce\u0646\u06d5\u0632\u0627\u0646 \u0628\u0646.',
    '\u0644\u06d5\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627 \u0647\u06d5\u0631\u062e\u0648\u062f\u0627\u06cc\u06d5\u0648 \u0686\u06cc \u0628\u062e\u0648\u0627\u0632\u06ce \u062f\u0631\u0648\u0633 \u062f\u06d5\u06a9\u0627\u061b \u0628\u06d5\u06cc\u06d5\u06a9\u06ce \u06a9\u0686 \u062f\u06d5\u0628\u06d5\u062e\u0634\u06ce\u062a \u0648 \u0628\u06d5\u06cc\u06d5\u06a9\u06ce \u06a9\u0648\u0695\u061b \u0626\u06cc\u062a\u0631 \u0628\u06d5\u0645\u06d5\u06cc\u0644\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u06cc\u0627\u0646 \u062c\u06a4\u06cc\u06a9\u06cc \u06a9\u0648\u0695\u0648 \u06a9\u0686\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06ce \u0648 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce\u060c \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646\u06cc\u0634 \u0646\u06d5\u0632\u06c6\u06a9 \u062f\u06d5\u06a9\u0627. \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0632\u0627\u0646\u0627\u06cc\u06d5\u0648 \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5\u0695\u0648\u0628\u06d5\u0695\u0648\u060c \u0646\u06d5\u062f\u0648\u0627\u0646\u062f\u0648\u06d5. \u06cc\u0627\u0646 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0644\u06d5\u0695\u0627\u06cc \u062f\u06b5\u06cc\u06d5\u0648\u06d5 \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u06cc\u0627\u0646 \u0644\u06d5\u067e\u0634\u062a \u067e\u06d5\u0631\u062f\u06d5\u0648\u06d5 \u0628\u0648\u0648\u06d5\u060c \u06cc\u0627\u0646 \u0642\u0627\u0633\u06cc\u062f\u06ce\u06a9\u06cc \u0646\u0627\u0631\u062f\u0648\u06d5\u0648 \u0626\u06cc\u0632\u0646\u06cc \u062f\u0627\u0648\u06d5 \u0686\u06cc \u062f\u06d5\u062e\u0648\u0627\u0632\u06ce \u0628\u06d5\u062f\u06b5\u06cc \u0695\u0627\u0628\u06af\u06d5\u06cc\u06ce\u0646\u06ce. \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062c\u06ce \u0628\u06d5\u0631\u0632\u0648 \u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5\u0634\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646\u060c \u06af\u06cc\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0644\u0627\u06cc \u062f\u06b5\u062a. \u062a\u06c6 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u0646\u06d5\u062a\u062f\u06d5\u0632\u0627\u0646\u06cc \u06a9\u062a\u06ce\u0628 \u06a9\u0627\u0645\u06d5\u0648 \u0628\u0695\u0648\u0627 \u0686\u06cc\u06cc\u06d5\u061f \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u06d5\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0645\u06d5\u0634\u062e\u06d5\u06b5\u06ce\u06a9 \u06a9\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u0645\u0627\u0646 \u2013 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u062e\u0648\u0627\u0632\u06cc\u0646 \u2013 \u0628\u06d5\u0648\u06d5 \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u0633\u06d5\u0631\u0695\u06ce\u06af\u0627. \u062a\u06c6\u0634 \u0644\u06d5\u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u060c \u0695\u06ce\u0628\u06d5\u0631\u06cc.',
    '\u0695\u06ce\u06af\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u060c \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0632\u06d5\u0645\u06cc\u0646\u06cc\u0634\u062f\u0627\u06cc\u06d5\u060c \u0647\u06d5\u0631 \u0628\u06c6\u0626\u06d5\u0648\u06d5. \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646! \u0647\u06d5\u0645\u0648\u06a9\u0627\u0631\u06ce\u06a9 \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u0686\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u062d\u0627\u060c \u0645\u06cc\u0645.',
    '\u0628\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u060c \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0634\u062a \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u0628\u0695\u06cc\u0627\u0631\u0645\u0627\u0646\u062f\u0627 \u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5 \u0639\u06d5\u0695\u06d5\u0628\u06cc \u0628\u06ce\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u062a\u0648\u0627\u0646\u0646 \u062a\u06ce \u0628\u06af\u06d5\u0646.',
    '\u0626\u06d5\u0645\u06d5\u0634 \u0644\u06d5 \u0645\u0627\u06a9\u06cc \u06a9\u062a\u06ce\u0628\u062f\u0627 \u2013 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u2013 \u06af\u06d5\u0644\u06ce \u067e\u0627\u06cc\u06d5 \u0628\u06d5\u0631\u0632\u06d5\u0648 \u067e\u0695\u06d5 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc.',
    '\u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u0626\u06ce\u0648\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u0628\u0644\u06d5\u0648\u06d5\u0632\u06cc\u0634 \u0628\u0646\u060c \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0645 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc\u06d5 \u0648\u0627\u0632\u062f\u06ce\u0646\u06cc\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0641\u0631\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u0644\u0627\u06cc \u06a9\u06c6\u0645\u06d5\u06b5\u0627\u0646\u06cc \u0628\u06d5\u0631\u0644\u06d5\u0626\u06ce\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646\u060c \u0647\u06d5\u0631\u06af\u0627\u06b5\u062a\u06d5\u06cc\u0627\u0646 \u067e\u06ce\u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0632\u06c6\u0631\u062f\u0627\u0631\u062a\u0631 \u0644\u06d5\u0648\u0627\u0646\u0645\u0627\u0646 \u0642\u0695\u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u0633\u06d5\u0631\u0628\u0647\u0648\u0631\u062f\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646 \u0695\u0627\u0628\u0631\u062f\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u067e\u0631\u0633\u06cc: \u06a9\u06ce \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u061f \u0626\u06ce\u0698\u0646: \u0628\u06ce \u0634\u06a9 \u062e\u0648\u062f\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0632\u0627\u0646\u0627 \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u06a9\u0631\u062f\u06d5\u0695\u0627\u06cc\u06d5\u062e \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0628\u06c6\u067e\u06ce\u062f\u0627\u06a9\u06ce\u0634\u0627\u0648\u0646\u061b \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0634\u0627\u0631\u06d5\u0632\u0627\u0628\u0628\u0646.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0626\u0627\u0648\u06cc \u0628\u0627\u0631\u0627\u0646\u06cc \u0646\u0627\u0631\u062f\u06d5\u062e\u0648\u0627\u0631\u061b \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06cc \u0645\u0631\u06cc\u0645\u0627\u0646 \u067e\u06ce \u0628\u0648\u0698\u0627\u0646\u062f\u06d5\u0648\u06d5. \u0626\u06ce\u0648\u06d5\u0634 \u0648\u0627 \u062f\u06d5\u062e\u0631\u06ce\u0646\u06d5\u062f\u06d5\u0631\u06ce.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0626\u06d5\u0645 \u0647\u06d5\u0645\u0648\u0648 \u062c\u0641\u062a\u06d5\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u061b \u06af\u06d5\u0645\u06cc \u0648 \u067e\u0627\u062a\u0627\u06b5\u06cc \u0648\u06d5\u0647\u0627\u0634\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0633\u0648\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0646.',
    '\u0626\u06d5\u0634\u06ce \u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u0634\u062a\u06cc\u0627\u0646 \u062d\u06d5\u0633\u0627\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u0644\u06d5\u0628\u06cc\u0631\u0628\u06ce \u0648 \u0628\u06ce\u0698\u0646: \u067e\u0627\u06a9\u06cc \u0648 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u0628\u06c6\u0626\u06d5\u0648\u06a9\u06d5\u0633\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0626\u06d5\u0645\u06d5\u06cc \u0695\u0627\u0645\u06cc \u0626\u06ce\u0645\u06d5 \u06a9\u0631\u062f\u061b \u062f\u06d5\u0646\u0627 \u0628\u06c6\u0645\u0627\u0646 \u0695\u0627\u0645 \u0646\u06d5\u062f\u06d5\u0628\u0648\u0646.',
    '\u0626\u06ce\u0645\u06d5\u0634 \u06af\u0634\u0645\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062f\u06d5\u0686\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0641\u0631\u0632\u06d5\u0646\u062f\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0646\u062f\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06c6\u062f\u0627\u0646\u0627! \u0695\u0627\u0633\u062a\u06cc \u0645\u0631\u06c6 \u0628\u06d5\u0626\u0627\u0634\u06a9\u0631\u0627 \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627 \u2013 \u06a9\u06d5 \u0647\u06d5\u0631\u062e\u06c6\u06cc \u062f\u0631\u0648\u0633\u062a\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u2013 \u06a9\u0686\u0627\u0646\u06cc \u062f\u0627\u0648\u06d5 \u0628\u06d5\u062e\u06c6\u06cc \u0648 \u06a9\u0648\u0695\u0627\u0646\u06cc\u0634\u06cc \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u0626\u06ce\u0648\u06d5\u061f!',
    '\u06a9\u06d5\u0686\u06cc \u0647\u06d5\u0631\u06af\u0627 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5\u0648\u0627\u0646 \u0645\u0698\u062f\u06d5\u06cc \u0626\u06d5\u0648\u06d5\u06cc \u067e\u06ce \u0628\u062f\u0631\u06ce \u0648\u0627 \u062e\u06c6\u06cc \u067e\u06ce\u06cc\u0648\u0627\u06cc\u06d5 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5\u060c \u0695\u0648\u062e\u0633\u0627\u0631\u06cc \u0695\u06d5\u0634 \u062f\u0627\u062f\u06d5\u06af\u06d5\u0695\u06ce \u0648 \u0644\u06d5\u0695\u06a9\u0627\u0646 \u067e\u06cc\u0634 \u062f\u06d5\u062e\u0648\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u062e\u0634\u06b5\u0627 \u0628\u06d5\u062e\u06ce\u0648\u06a9\u0631\u0627\u0628\u06ce \u0648 \u0644\u06d5\u0634\u06d5\u0695\u06cc\u0634\u0627 \u0646\u06d5\u062e\u06c6\u06cc\u0627\u0628\u06ce\u060c \u0628\u06c6 \u062e\u0648\u0627 \u062f\u06d5\u0628\u06ce\u061f',
    '\u0626\u06d5\u0648\u0641\u0631\u06cc\u0634\u062a\u0627\u0646\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0628\u06d5\u0646\u062f\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u060c \u0628\u06d5 \u0645\u06ce\u0648\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u062f\u0627\u0646\u0627\u0648\u0646. \u062f\u06d5\u0628\u06ce \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06ce \u0628\u0648\u0628\u0646 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u0648\u06d5\u062f\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0646! \u0634\u0627\u06cc\u06d5\u062a\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0646\u0648\u0633\u0631\u06ce \u0648 \u0644\u06ce\u0627\u0646 \u062f\u06d5\u067e\u0631\u0633\u0631\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0698\u0646: \u0626\u06d5\u0631 \u062e\u0648\u0627 \u062d\u06d5\u0632\u06cc \u06a9\u0631\u0628\u0627\u060c \u0626\u06ce\u0645\u06d5 \u0646\u06d5\u0645\u0627\u0646\u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646. \u0644\u06d5\u0645\u06d5\u0634\u062f\u0627 \u062f\u0648\u0631\u06d5 \u0632\u0627\u0646\u0633\u062a\u0646\u061b \u0647\u06d5\u0631 \u062f\u0631\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5 - \u0628\u06d5\u0631\u0644\u06d5 \u0642\u0648\u0695\u0626\u0627\u0646 - \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06d5\u0648\u0627\u0646 \u062f\u0627\u0648\u06d5\u060c \u06a9\u06d5 \u0626\u06ce\u0633\u062a\u0627 \u0628\u06cc\u06a9\u06d5\u0646\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u061f',
    '\u0628\u06d5\u06b5\u06a9\u06d5 \u0626\u06ce\u0698\u0646: \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0645\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u06cc\u0646\u06ce\u06a9 \u0695\u06c6\u06cc\u0634\u062a\u0648\u0646\u060c \u0626\u06ce\u0645\u06d5\u0634 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0646.',
    '\u0647\u06d5\u0631 \u0628\u06d5\u0645 \u062a\u06d5\u0631\u0632\u06d5 \u0628\u06d5\u0631\u0644\u06d5\u062a\u06c6\u0634 \u0647\u06d5\u0631\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u0647\u06d5\u0631 \u0626\u0627\u0648\u06d5\u062f\u0627\u0646\u06cc\u06d5\u06a9 \u06a9\u06d5 \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631\u0646\u06ce\u060c \u0647\u06d5\u0645\u0648\u0648 \u062e\u06c6\u0634\u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u060c \u06af\u0648\u062a\u0648\u06cc\u0627\u0646\u06d5: \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0645\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u06cc\u0646\u06ce\u06a9 \u0695\u06c6\u06cc\u0634\u062a\u0648\u0646\u060c \u0626\u06ce\u0645\u06d5\u0634 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0646.',
    '\u06af\u0648\u062a\u0648\u06cc\u06d5\u062a\u06cc: \u0626\u06d5\u062f\u06cc \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0646 \u0695\u06ce\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u0645 \u06a9\u06d5 \u0644\u06d5\u0695\u06ce\u06cc \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u062a\u0627\u0646 \u062f\u0631\u0648\u0633 \u062a\u0631 \u0628\u06ce\u061f \u06af\u0648\u062a\u0648\u06cc\u0627\u0646\u06d5: \u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06c6\u06cc \u0628\u06d5\u0695\u06ce \u06a9\u0631\u0627\u0648\u0646\u060c \u0628\u0627\u0648\u06d5\u0695\u0646\u0627\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5 \u062a\u06c6\u06b5\u06d5\u0645\u0627\u0646 \u0644\u06ce \u06a9\u0631\u062f\u0648\u0648\u0646\u06d5\u0648\u06d5. \u0633\u0627 \u0628\u0695\u0648\u0627\u0646\u06d5 \u0626\u0627\u06a9\u0627\u0645\u06cc \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646 \u0628\u06d5\u06a9\u0648\u06ce \u06af\u06d5\u06cc\u0634\u062a.',
    '\u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u06cc\u0634 \u0628\u06d5 \u0626\u0627\u067e \u0648 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u0645\u0646 \u0628\u06ce\u0632\u0627\u0631\u0645 \u0644\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5\u06a9\u06d5\u0633\u06ce \u0645\u0646\u06cc \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u06a9\u06d5 \u062e\u06c6\u06cc \u0695\u06ce\u06af\u06d5\u0645 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627.',
    '\u06a9\u0627\u0631\u06ce\u06a9\u06cc \u06a9\u0631\u062f \u0626\u06d5\u0645 \u0648\u062a\u0627\u0631\u06d5\u06cc \u0644\u06d5\u067e\u0627\u0634\u06d5\u062e\u06c6\u06cc \u0628\u06c6\u0646\u06d5\u062a\u06d5\u0648\u06d5\u06cc \u0647\u06d5\u0631\u0628\u0645\u06ce\u0646\u06ce\u061b \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0628\u067e\u0631\u06cc\u0646\u06af\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u0627\u0648\u06d5\u0646\u06cc\u0634 \u0648 \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634\u0645 \u0644\u06d5\u0698\u06cc\u0627\u0646 \u0628\u06d5\u0647\u0631\u06d5\u0648\u06d5\u0631 \u06a9\u0631\u062f\u060c \u062a\u0627 \u0626\u06d5\u0645 \u0695\u0627\u0633\u062a\u06d5\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u0631\u06cc\u0627\u0646 \u0647\u0627\u062a\u06d5\u0644\u0627.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0695\u0627\u0633\u062a\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06c6\u0647\u0627\u062a\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u062c\u0627\u062f\u0648\u06cc\u06d5\u06a9\u06d5\u0648 \u0626\u06ce\u0645\u06d5 \u0628\u0695\u0648\u0627\u0645\u0627\u0646 \u067e\u06ce\u06cc \u0646\u06cc\u06d5.',
    '\u06af\u0648\u062a\u06cc\u0634\u06cc\u0627\u0646: \u0628\u06c6 \u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5\u060c \u0628\u06c6\u067e\u06cc\u0627\u0648\u06ce\u06a9\u06cc \u0628\u06d5\u0648\u06d5\u062c\u06cc \u0626\u06d5\u0645 \u062f\u0648\u0628\u0627\u0698\u06ce\u0695\u06d5 \u0646\u06d5\u0647\u0627\u062a\u06d5\u062e\u0648\u0627\u0631.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u062f\u0627\u0628\u06d5\u0634 \u062f\u06d5\u06a9\u06d5\u0646\u061f \u0626\u06ce\u0645\u06d5 \u0628\u0698\u06cc\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646\u0645\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627 \u0628\u06c6\u0628\u06d5\u0634 \u06a9\u0631\u062f\u0648\u0646. \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06d5\u0686\u0627\u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u062a\u0631\u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u062a\u0627 \u0647\u06ce\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u0695\u0627\u0698\u06d5\u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u0628\u06a9\u06d5\u0646. \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0644\u06d5\u0648\u0634\u062a\u06d5\u06cc \u06a9\u06c6\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u060c \u0647\u06ce\u0698\u0627\u062a\u0631\u06d5.',
    '\u062e\u06c6\u0626\u06d5\u06af \u06d5\u0631 \u0628\u0695\u06cc\u0627\u0631\u0648\u0627\u0646\u06d5\u0628\u0627\u060c \u0645\u06d5\u0631\u062f\u0645 \u06cc\u06d5\u06a9 \u062a\u06cc\u0631\u06d5\u0628\u0646\u060c \u0648\u0627\u0645\u0646 \u062f\u06d5\u06a9\u0631\u062f \u0645\u06cc\u0686 \u0648\u0628\u0627\u0646 \u0648\u067e\u0644\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0644\u06d5\u0632\u06ce\u0648 \u0628\u0648\u0627\u06cc\u06d5\u0648 \u0628\u06d5\u0695\u06ce\u0632\u06d5\u067e\u0644\u06d5\u06a9\u0627\u0646\u0627\u0646\u062f\u0627 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u0646\u061b',
    '\u0647\u06d5\u0645\u0648\u062f\u0631\u06af\u0627\u06cc \u0645\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5\u0632\u06ce\u0648 \u0628\u0648\u0628\u0627\u0648 \u062a\u06d5\u062e\u062a\u06cc \u0632\u06ce\u0648\u06cc\u0646\u0645 \u062f\u06d5\u062f\u0627\u0646\u06ce\u060c \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u067e\u0627\u06b5 \u062f\u06d5\u0646\u06d5\u0648\u06d5\u061b',
    '\u0634\u062a\u06ce \u062a\u0631\u06cc \u0647\u06d5\u0645\u06d5\u0695\u06d5\u0646\u06af\u06cc\u0634. \u06af\u0634\u062a \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0628\u06c6\u062e\u06c6\u0634\u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06cc \u0698\u06cc\u0646\u06cc \u0626\u06d5\u0645 \u062f\u0646\u06cc\u0627\u0646\u061b \u0645\u0627\u06b5\u06cc \u0626\u06d5\u0648\u0644\u0627\u0634 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0627\u0646\u0627\u0648\u06d5 \u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u062a\u0631\u0633\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0644\u06d5\u0695\u0627\u0633\u062a \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u062d\u06d5\u0632\u062f\u06d5\u06a9\u0627 \u06a9\u0648\u06ce\u0631\u0627\u06cc\u06cc \u062f\u0627\u0628\u06ce\u060c \u0647\u06d5\u0645\u062f\u06d5\u0645\u06ce\u06a9\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u06cc \u0644\u06ce \u067e\u06d5\u06cc\u062f\u0627\u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06cc\u062a\u0631 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0647\u0627\u0648\u062f\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u0647\u06d5\u06b5\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0644\u0627\u0634\u06cc\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u06cc\u0627\u0646 \u062f\u06cc\u0648\u06d5\u062a\u06d5\u0648\u06d5.',
    '\u062a\u0627\u0626\u06d5\u0648\u06a9\u0627\u062a\u06d5\u06cc \u062f\u06ce\u062a\u06d5\u0644\u0627\u0645\u0627\u0646\u061b \u0626\u06ce\u0698\u06ce: \u062e\u06c6\u0632\u06cc \u0645\u06d5\u0648 \u062f\u0627\u06cc \u0646\u06ce\u0648\u0627\u0646\u06cc \u0645\u0646 \u0648 \u062a\u06c6\u060c \u0628\u06d5\u0642\u06d5\u062f \u0646\u06ce\u0648\u0627\u0646 \u062e\u06c6\u0631\u0647\u06d5\u06b5\u0627\u062a \u0648 \u062e\u06c6\u0631\u0646\u0634\u06cc\u0646 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u062f\u0648\u0631\u0628\u0627\u061b \u0686\u06d5\u0646\u062f \u0647\u06d5\u0645\u062f\u06d5\u0645\u06ce \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u0628\u0648\u06cc.',
    '\u0626\u06cc\u062a\u0631 \u0626\u06d5\u0645\u0695\u06c6 [\u0626\u06d5\u0645 \u0642\u0633\u0627\u0646\u06d5] \u062f\u0627\u062f\u0648\u0646\u0627\u062f\u0627\u061b \u0686\u0648\u0646\u06a9\u0648 \u0626\u06ce\u0648\u06d5 \u063a\u06d5\u062f\u0631\u062a\u0627\u0646 [\u0644\u06d5\u062e\u06c6] \u06a9\u0631\u062f\u0648\u0648\u06d5. \u0626\u06d5\u0634\u06ce \u06af\u0634\u062a\u0648 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u0628\u062f\u0631\u06ce\u0646.',
    '\u0626\u0627\u062e\u06c6 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u06d5 \u06af\u0648\u06ce \u06a9\u067e\u0627\u0646 \u0644\u06ce\u062a \u0628\u0628\u06cc\u0633\u0646\u061f \u06cc\u0627\u062e\u06c6 \u06a9\u0648\u06ce\u0631\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627 \u0644\u06d5\u06af\u0648\u0645\u0695\u0627\u06cc\u06cc\u062f\u0627\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0628\u06a9\u06d5\u06cc\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062a\u06c6 \u0644\u06d5\u0645 \u0646\u0627\u0648\u06d5 \u0644\u0627\u0628\u06d5\u06cc\u0646\u060c \u0626\u06ce\u0645\u06d5 \u062f\u06cc\u0627\u0631\u06d5 \u062a\u06c6\u06b5\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u06cc\u0627\u0646 \u0626\u06d5\u0648\u0634\u062a\u06d5\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u0645\u0627\u0646 \u0628\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627\u0648\u06d5\u060c \u0648\u0627\u062f\u06d5\u06a9\u06d5\u06cc\u0646 \u0628\u06c6 \u062e\u06c6\u062a \u0628\u06cc\u0628\u06cc\u0646\u06cc. \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0634\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u0632\u0627\u06b5\u06cc\u0646.',
    '\u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc \u0628\u06d5 \u062a\u06c6 \u0695\u0627\u06af\u06d5\u06cc\u06d5\u0646\u062f\u0631\u0627\u0648\u06d5 \u067e\u06ce \u062f\u0627\u0628\u06af\u0631\u06d5\u061b \u06a9\u06d5 \u062a\u06c6 \u0644\u06d5 \u0633\u06d5\u0631 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc.',
    '\u0626\u06d5\u0645 \u0642\u0648\u0695\u0626\u0627\u0646\u06d5 \u0628\u06c6\u062a\u06c6\u0634 \u0648 \u0628\u06c6\u0647\u06c6\u0632\u06d5\u06a9\u06d5\u0634\u062a \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0647\u06c6\u06cc \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc \u0648 \u0644\u06d5\u067e\u0627\u0634\u0627\u0646\u06cc\u0634 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0645 \u0644\u06ce\u0648 \u062f\u06d5\u067e\u0631\u0633\u0646.',
    '\u0644\u06d5\u0648\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06d5\u0634 \u067e\u0631\u0633\u06d5 \u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5\u062a\u06c6 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u0646 \u0626\u0627\u062e\u06c6 \u0645\u06d5 \u0628\u0695\u06cc\u0627\u0631\u0645\u0627\u0646 \u062f\u0627\u0648\u06d5 - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u0647\u06cc\u0686 \u0634\u062a\u06cc \u062a\u0631 \u067e\u06d5\u0631\u0633\u062a\u0648\u0628\u06ce\u061f',
    '\u0645\u0648\u0648\u0633\u0627\u0634\u0645\u0627\u0646 \u0648\u06d5\u0644 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0647\u06d5\u0646\u0627\u0631\u062f\u06d5 \u0644\u0627\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u067e\u06cc\u0627\u0648\u06d5 \u06a9\u0627\u0631\u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646\u06cc\u061b \u06af\u0648\u062a\u06cc: \u0645\u0646 \u0644\u06d5 \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u06af\u0634 \u062f\u0646\u06cc\u0627\u0648\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u0645.',
    '\u06a9\u0627\u062a\u06ce \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0645\u06d5\u06cc \u0644\u06d5 \u0628\u06d5\u0631 \u0695\u0627\u0646\u0627\u0646\u060c \u0644\u06d5\u067e\u0695 \u0647\u06d5\u0645\u0648\u0648 \u0644\u06d5 \u062a\u0631\u06cc\u0642\u06d5\u06cc \u067e\u06ce\u06a9\u06d5\u0646\u06cc\u0646\u06cc\u0627\u0646\u062f\u0627.',
    '\u0647\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u062f\u0627\u060c \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u067e\u06ce\u0634\u06d5\u0648\u06d5 \u06af\u0631\u06cc\u0646\u06af\u062a\u0631\u0628\u0648. \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646\u060c \u0628\u06d5\u0634\u06a9\u06d5\u0645\u06ce \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u06cc \u06a9\u0627\u06a9\u06cc \u062c\u0627\u062f\u0648\u06af\u06d5\u0631! \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u062e\u0648\u0627\u0632\u06d5\u060c \u0626\u06d5\u0648 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0628\u06d5\u062a\u06c6\u06cc \u062f\u0627\u0648\u06d5 \u067e\u06ce\u06a9\u06cc \u0628\u06ce\u0646\u06ce\u061b \u0626\u06d5\u0648\u0633\u0627 \u0628\u0627\u0648\u06d5\u0695\u062a \u067e\u06ce \u062f\u06ce\u0646\u06cc\u0646.',
    '\u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0628\u06d5\u06b5\u0627\u0634\u0645\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u0627\u0628\u0631\u062f\u0646\u060c \u0626\u06d5\u0648\u0627\u0646 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u0634\u06a9\u06ce\u0646\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0628\u06c6\u0698\u06ce\u0631\u062f\u06d5\u0633\u062a\u0627\u0646\u06cc \u062c\u0627\u0695\u06cc \u06a9\u06ce\u0634\u0627\u0648 \u06af\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0626\u0627\u062e\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0647\u06d5\u0631\u062f\u06cc \u0645\u06cc\u0633\u0631\u0648 \u0626\u06d5\u0648\u0686\u06c6\u0645\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0628\u06d5\u0631\u0645\u0646\u062f\u0627 \u0641\u0695\u06a9\u06d5\u06cc\u0627\u0646\u06d5\u060c \u0645\u0648\u06b5\u06a9\u06cc \u0645\u0646 \u0646\u06cc\u0646\u061f \u0626\u06ce\u0648\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0646\u06d5\u062a\u0627\u0646\u062f\u06cc\u0648\u06d5\u061f',
    '\u0645\u0646 \u0686\u0627\u06a9\u062a\u0631 \u0646\u06cc\u0645 \u0644\u06d5\u0648 \u06a9\u0627\u0648\u0631\u0627\u06cc\u06d5 \u06a9\u06d5 \u0647\u06cc\u0686 \u0646\u0645\u0648\u062f\u06ce\u06a9\u06cc \u0646\u06cc\u06d5\u0648 \u0628\u0627\u0634 \u0646\u0627\u0632\u0627\u0646\u06ce \u0642\u0633\u06d5\u0634 \u0628\u06d5\u0695\u06d5\u0648\u0627\u0646\u06cc \u0628\u06a9\u0627\u061f',
    '\u0626\u06d5\u062f\u06cc \u0628\u06c6\u0686\u06cc \u06af\u06d5\u0648\u06cc \u0632\u06ce\u0695\u06cc \u0644\u06d5\u062d\u06d5\u0648\u0627\u0648\u06d5 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0641\u0695\u06ce \u0646\u06d5\u062f\u0631\u0627\u0648\u06d5\u060c \u06cc\u0627\u0646 \u0641\u0631\u06cc\u0634\u062a\u06d5 \u0628\u06d5\u06af\u06d5\u0644 \u062f\u06d5\u06af\u06d5\u06b5\u06cc \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u0646\u061f',
    '\u0632\u0627\u0646\u06cc \u06a9\u0627\u0631\u06cc \u0698\u06ce\u0631\u062f\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646\u06cc \u0633\u06a4\u06a9\u06d5\u061b \u06af\u0634 \u0628\u06d5\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u06cc\u0627\u0646 \u06a9\u0631\u062f. \u0626\u06d5\u0648 \u0647\u06c6\u0632\u06ce\u06a9 \u0628\u0648\u0648\u0646 \u0644\u06d5\u0695\u06ce \u0644\u0627\u062f\u06d5\u0631.',
    '\u06a9\u06d5 \u0647\u06d5\u0631\u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0695\u06a9 \u0647\u06d5\u0633\u062a\u0627\u0646\u062f\u060c \u062a\u06c6\u06b5\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0644\u06ce \u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5\u0648 \u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5\u062e\u0646\u06a9\u0627\u0646 \u062f\u0627\u0646.',
    '\u06a9\u0627\u0631\u06ce\u06a9\u0645\u0627\u0646 \u06a9\u0631\u062f \u0628\u0628\u0646\u06d5 \u0686\u06cc\u0631\u06c6\u06a9 \u0648 \u0646\u0645\u0648\u0648\u0646\u06d5 \u0628\u06c6\u0626\u06d5\u0648\u0627\u0646\u06cc \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc \u062f\u06ce\u0646.',
    '\u0626\u06d5\u0648\u062f\u06d5\u0645\u06d5\u0634 \u06a9\u06d5 \u0633\u06d5\u0631\u0628\u0647\u0648\u0631\u062f\u06cc \u06a9\u0648\u0695\u06cc \u0645\u0631\u06cc\u06d5\u0645 \u0647\u0627\u062a\u06d5\u06af\u06c6\u0695\u06ce\u060c \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062a\u06c6 \u0644\u06d5\u062e\u06c6\u0634\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06cc\u0627\u0646 \u0628\u06d5\u0647\u06d5\u0631\u0627.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0633\u062a\u0648\u0645\u0627\u0646\u0646 \u0644\u06d5\u0648 \u0686\u0627\u06a9\u062a\u0631\u0646\u06cc\u0646\u061f \u0626\u06d5\u0645\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u06af\u06ce\u0686\u06d5\u06b5\u06ce \u0647\u06ce\u0646\u0627\u06cc\u06d5 \u067e\u06ce\u0634\u060c \u0686\u0648\u0646 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u0646 \u0634\u06d5\u0695 \u062f\u06d5\u0641\u0631\u06c6\u0634\u0646.',
    '\u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0646\u06d5\u0628\u0648\u0648 \u0639\u06d5\u0628\u062f\u06ce\u06a9 \u0646\u06d5\u0628\u06ce\u061b \u0626\u06ce\u0645\u06d5 \u0686\u0627\u06a9\u06d5\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f \u0648 \u06a9\u0631\u062f\u0645\u0627\u0646 \u0628\u06d5\u0646\u0645\u0648\u0648\u0646\u06d5\u06cc\u06d5\u06a9 \u0628\u06c6 \u0648\u06d5\u0686 \u0648 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644.',
    '\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0645\u06d5\u06cc\u0644\u0645\u0627\u0646 \u0644\u06ce \u0628\u0627\u06cc\u06d5\u060c \u0686\u06d5\u0646\u062f \u0641\u0631\u06cc\u0634\u062a\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u062f\u06cc \u062f\u06ce\u0646\u0627\u0648 \u062f\u06d5\u0645\u0627\u0646\u06a9\u0631\u062f\u0646 \u0628\u06d5\u062c\u06ce \u0646\u0634\u06cc\u0646 \u0644\u06d5\u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646.',
    '\u0626\u06d5\u0648 \u06cc\u06d5\u06a9\u06ce\u06a9\u06d5 \u0644\u06d5\u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a. \u0634\u06a9\u062a \u0644\u06d5\u0647\u0627\u062a\u0646\u06cc \u0646\u06d5\u0628\u06ce \u0648 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u0645\u0646 \u0628\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0645\u06d5 \u0695\u06ce\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0695\u0627\u0633\u062a\u06d5.',
    '\u0634\u06d5\u06cc\u062a\u0627\u0646\u06cc\u0634 \u0695\u06ce\u06af\u0627\u0648 \u0644\u06ce \u0646\u06d5\u06af\u0631\u06ce\u061b \u0626\u06d5\u0648 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u0648\u0698\u0645\u0646\u062a\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0639\u06cc\u0633\u0627\u0634 \u0628\u06d5\u0686\u06d5\u0646\u062f \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u0631\u06d5\u0648\u06d5 \u0647\u0627\u062a\u0628\u0648\u060c \u06af\u0648\u062a\u06cc: \u0645\u0646 \u0648\u0627 \u0695\u06ce\u06af\u06d5\u0648 \u0634\u0648\u06ce\u0646\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u0645 \u0628\u06c6\u0647\u06ce\u0646\u0627\u0648\u0646\u061b \u062a\u0627 \u0628\u0695\u06ce\u06a9\u06cc\u0634 \u0644\u06d5\u0648\u06d5\u062a\u0627\u0646 \u0628\u06c6\u0695\u0648\u0646 \u06a9\u06d5\u0645\u06d5\u0648\u06d5 \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u06cc \u06a9\u06ce\u0634\u06d5\u062a\u0627\u0646\u06d5\u061b \u0633\u0627 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u0648 \u0647\u06d5\u0628\u06ce \u0648 \u0634\u0648\u06ce\u0646 \u0645\u0646 \u06a9\u06d5\u0648\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646 \u0648 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631\u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u0628\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0626\u06d5\u0645\u06d5 \u0695\u06ce\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0695\u0627\u0633\u062a\u06d5.',
    '\u062c\u0627 \u06a9\u06c6\u0645\u06d5\u06b5\u0627\u0646 \u06a9\u06ce\u0634\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u067e\u06d5\u06cc\u062f\u0627\u0628\u0648. \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u063a\u06d5\u062f\u0631\u06ce\u0628\u06a9\u06d5\u0646 \u0648\u0627\u0648\u06d5\u06cc\u0644\u0627\u06cc\u06d5 \u0644\u06d5\u0626\u0627\u0632\u0627\u0631\u06cc \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u062a\u0648\u0648\u0634.',
    '\u0626\u0627\u062e\u06c6 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc \u0686\u06cc \u062a\u0631 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u06a9\u06d5 \u0644\u06d5\u067e\u0695 \u0644\u06ce\u0627\u0646 \u0695\u0648\u062f\u06d5\u062f\u0627\u0648 \u067e\u06ce\u0634 \u0646\u0627\u062d\u06d5\u0633\u0646\u061f',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06cc\u0627\u0631\u0627\u0646 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0628\u0646\u06d5 \u0646\u06d5\u06cc\u0627\u0631\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u061b \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u06d5\u06a9\u0627\u0646.',
    '\u0626\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0647\u06d5\u0631\u062e\u06c6\u0645\u062a\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a\u0648\u06d5! \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0646\u06d5 \u062a\u0631\u0633\u06ce\u06a9\u062a\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u06cc\u06d5\u060c \u0646\u06d5 \u062e\u06d5\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u0626\u06d5\u0648\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0628\u06d5\u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u0648\u0646\u060c',
    '\u0628\u0686\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u061b \u0644\u06d5\u0626\u06ce\u0648\u06d5\u0648 \u0644\u06d5 \u0647\u0627\u0648\u0633\u06d5\u0631\u0627\u0646\u0648 \u062e\u06c6\u0634 \u0695\u0627\u062f\u06d5\u0628\u0631\u06ce.',
    '\u0628\u06d5 \u0633\u06cc\u0646\u06cc \u0648 \u0632\u06d5\u0631\u06a9\u06cc \u0632\u06ce\u0648\u06cc\u0646\u06d5 \u0644\u06d5 \u062f\u06d5\u0648\u0631\u06cc\u0627\u0646\u0627 \u062f\u06ce\u0646 \u0648 \u062f\u06d5\u0686\u0646. \u0647\u06d5\u0631\u0686\u06cc \u062f\u06b5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u0686\u0627\u0648 \u0644\u06d5\u0632\u06d5\u062a\u06cc \u0644\u06ce \u062f\u06d5\u0628\u06cc\u0646\u06ce\u060c \u062a\u06ce\u062f\u0627\u0647\u06d5\u06cc\u06d5\u0648 \u0626\u06ce\u0648\u06d5\u0634 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0646\u0627\u0645\u0631\u0646.',
    '\u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5 \u0647\u06c6\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646\u060c \u0648\u0627 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0645\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5.',
    '\u0645\u06cc\u0648\u06d5\u06cc \u0632\u06c6\u0631\u06cc \u062a\u06ce\u062f\u0627\u0647\u06d5\u06cc\u06d5\u0648 \u0628\u06c6\u0626\u06ce\u0648\u06d5\u06cc\u06d5\u0648 \u0644\u06ce\u0627\u0646 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646\u06cc\u0634 \u0647\u06d5\u062a\u0627\u0633\u06d5\u0631 \u0644\u06d5\u0626\u0627\u0632\u0627\u0631\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u062f\u0627\u0646 \u0648 \u062f\u06d5\u0631\u0646\u0627\u0686\u0646.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u0648\u0686\u0627\u0646\u06cc\u0627\u0646 \u067e\u06ce \u0646\u0627\u062f\u0631\u06ce \u0648 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0628\u06d5\u0646\u0627\u0647\u0648\u0645\u06ce\u062f\u06cc \u062f\u0627\u0645\u0627\u0648\u0646.',
    '\u0647\u06cc\u0686 \u063a\u06d5\u062f\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u06c6\u06cc\u0627\u0646 \u063a\u06d5\u062f\u0631\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0647\u0627\u0646\u0627\u062f\u06d5\u0628\u06d5\u0646 \u0628\u06c6\u06af\u0632\u06cc\u0631\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06ce: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0626\u06d5\u0645 \u0698\u06cc\u0646\u06d5 \u0686\u06d5\u0631\u0645\u06d5\u0633\u06d5\u0631\u06d5\u0645\u0627\u0646 \u0628\u06c6\u0646\u06d5\u0647\u06ce\u06b5\u06ce. \u0626\u06ce\u0698\u06ce: \u0647\u06d5\u0631 \u0644\u06ce\u0631\u06d5 \u062f\u06d5\u0645\u06ce\u0646\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0695\u0627\u0633\u062a\u06cc\u0645\u0627\u0646 \u0628\u06c6\u0647\u06ce\u0646\u0627\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0632\u06c6\u0631 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u062a\u06cc\u0648 \u062f\u06d5\u062f\u0632\u0627.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u06a9\u0627\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0633\u0648\u0631\u0628\u0648\u0646\u060c \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0644\u06d5 \u0633\u06d5\u0631 \u06a9\u0627\u0631\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0633\u0648\u0631\u06cc\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u067e\u06ce\u0627\u0646 \u0648\u0627\u0628\u0648 \u0626\u06ce\u0645\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0648 \u0633\u0631\u062a\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u0628\u06cc\u0633\u06cc\u0646\u061f \u062f\u06d5\u06cc\u0628\u06cc\u0633\u06cc\u0646 \u0648 \u0686\u06d5\u0646\u062f\u06ce\u06a9\u06cc\u0634\u0645\u0627\u0646 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u0648\u0646 \u0648\u0627\u0646 \u0644\u06d5\u0644\u0627\u06cc\u0627\u0646 \u0648 \u062f\u06d5\u0646\u0648\u0648\u0633\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0631 \u062e\u0648\u0627 \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc \u0628\u06ce\u060c \u0645\u0646 \u06cc\u06d5\u06a9\u06d5\u0645\u06cc\u0646 \u06a9\u06d5\u0633\u06ce\u06a9\u0645 \u06a9\u06d5 \u062f\u06d5\u06cc\u067e\u06d5\u0631\u06d5\u0633\u062a\u0645.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0639\u06d5\u0631\u0634\u060c \u0647\u06cc\u0686 \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc\u06d5\u06a9\u06cc \u0646\u06cc\u06d5\u0648\u060c \u0628\u06d5\u062f\u0648\u0631\u06d5 \u0644\u06d5\u0648 \u062a\u06c6\u0645\u06d5\u062a\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06c6\u06cc \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u0646.',
    '\u0644\u06ce\u0627\u0646 \u06af\u06d5\u0695\u06ce \u0628\u0627\u0647\u06d5\u0631\u067e\u0695\u0648\u067e\u0648\u0686 \u0628\u0648\u06ce\u0698\u0646 \u0648 \u062e\u06d5\u0631\u06cc\u06a9\u06cc \u06af\u0627\u06b5\u062a\u06d5\u06a9\u0631\u062f\u0646 \u0628\u0646\u061b \u0647\u06d5\u062a\u0627 \u062a\u0648\u0648\u0634\u06cc \u0626\u06d5\u0648\u0695\u06c6\u0698\u06d5 \u062f\u06ce\u0646 \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06ce\u062f\u0627 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u06cc\u0634\u062f\u0627 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5\u0648 \u0632\u06c6\u0631 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u067e\u0627\u06a9\u06cc \u0648 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06cc \u0628\u06c6\u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0645 \u062f\u0648\u0627\u0646\u06d5\u062f\u0627\u06cc\u06d5\u060c \u06af\u0634 \u0628\u06c6\u0626\u06d5\u0648\u06d5\u061b \u0626\u06d5\u0648 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0686 \u0648\u06d5\u062e\u062a\u06ce \u0633\u06d5\u06b5\u0627\u0695\u0627\u062f\u06d5\u0628\u06ce \u0648 \u062f\u06d5\u062a\u0627\u0646\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648.',
    '\u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632\u062e\u0648\u0627\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0646\u06a9\u0627\u0631\u0646 \u0628\u0628\u0646\u06d5\u062a\u06a9\u0627\u06a9\u0627\u0631\u061b \u0645\u06d5\u0631 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u06a9\u06d5 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0648 \u0632\u0627\u0646\u06cc\u0646\u06d5\u0648\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u067e\u0631\u0633\u06cc\u060c \u06a9\u06ce \u0626\u06d5\u0648\u0627\u0646\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u060c \u0626\u06ce\u0698\u0646: \u062e\u0648\u062f\u0627. \u062f\u06d5\u06cc \u0686\u06c6\u0646 \u0644\u06d5\u0647\u06d5\u0642 \u06a9\u0644\u0627\u062f\u06d5\u0628\u0646\u061f',
    '\u0646\u0632\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u06a9\u06d5: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u0645\u0646! \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u062e\u06d5\u06b5\u06a9\u06ce\u06a9\u0646 \u0626\u0627\u0645\u0627\u062f\u06d5\u0646\u06cc\u0646 \u0628\u0695\u0648\u0627\u0628\u06ce\u0646\u0646.',
    '\u0633\u0627 \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627\u0632\u0628\u06ce\u0646\u06d5\u0648 \u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0633\u062a\u0627 \u0628\u06ce \u062a\u0631\u0633 \u0628\u0646\u060c \u0644\u06d5\u0645\u06d5\u0648\u0628\u06d5\u0648\u0644\u0627\u0648\u06d5 \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u062d\u0627\u060c \u0645\u06cc\u0645.',
    '\u0628\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0634\u062a \u0695\u0648\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c',
    '\u062f\u06d5\u0634\u06d5\u0648\u06ce\u06a9\u06cc \u067e\u0695\u0644\u06d5\u067e\u06cc\u062a \u0648 \u067e\u06cc\u0631\u06c6\u0632\u06cc\u062f\u0627 \u0626\u06ce\u0645\u06d5 \u0646\u0627\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u06d5\u062a\u06d5\u062e\u0648\u0627\u0631. \u0626\u06ce\u0645\u06d5 \u0648\u06cc\u0633\u062a\u0645\u0627\u0646 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u0645\u06d5\u0631\u062f\u0645 \u0628\u0646\u06ce\u0646.',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u0634\u062f\u0627 \u0647\u06d5\u0631\u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a\u06d5 \u0628\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc\u060c \u0647\u0627\u0648\u06ce\u0631\u06a9\u0631\u0627.',
    '\u0641\u06d5\u0631\u0645\u0627\u0646\u06d5\u0648 \u0644\u06d5\u0626\u06ce\u0645\u06d5\u0695\u0627\u06cc\u06d5\u0648 \u0626\u06ce\u0645\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5 \u062f\u06d5\u0646\u06ce\u0631\u06cc\u0646.',
    '\u0628\u06d5\u0632\u06d5\u06cc\u06cc\u06d5\u06a9\u06d5 \u0644\u06d5\u0626\u0627\u0644\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0695\u0627. \u0647\u06d5\u0631\u062e\u06c6\u06cc \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0648 \u0647\u06d5\u0631\u0686\u06cc \u06a9\u06d5 \u0644\u06d5\u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627\u06cc\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6\u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0634\u06ce. \u06af\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u0648\u0646\u06d5\u0648\u06d5\u0631\u062f\u06ce\u0646\u06ce \u0648 \u062f\u06d5\u0634\u0645\u0631\u06ce\u0646\u06ce. \u0626\u06d5\u0648 \u0626\u06ce\u0648\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u062f\u06d5\u06a9\u0631\u062f\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u0627\u0648\u0627\u0646\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u0634\u062a\u0627\u0646\u06d5.',
    '\u06a9\u06d5\u0686\u06cc \u0647\u06ce\u0634\u062a\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u0627\u0648 \u062f\u0648\u0648\u062f\u06b5\u06cc \u062f\u0627\u0646 \u0648 \u06af\u06d5\u0645\u06d5\u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc \u0626\u06d5\u0648\u0695\u06c6\u0698\u06d5\u0628\u06d5 \u06a9\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646 \u062f\u0648\u06a9\u06d5\u06b5\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u0644\u06ce \u067e\u06d5\u06cc\u062f\u0627\u062f\u06d5\u0628\u06ce.',
    '\u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631\u0628\u06d5\u0698\u0627\u0646\u06d5\u0648 \u0647\u06d5\u0645\u0648\u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0627\u062f\u06d5\u067e\u06c6\u0634\u06ce.',
    '\u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u06af\u0644\u0627\u0645\u0627\u0646! \u0626\u06d5\u0648 \u067e\u06d5\u062a\u0627\u06cc\u06d5\u0646 \u0644\u06d5\u0633\u06d5\u0631\u0644\u0627\u0628\u06d5\u061b \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627.',
    '\u06a9\u0648\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06af\u0648\u06ce \u0644\u06d5\u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u062f\u06d5\u06af\u0631\u0646\u061f \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u0647\u0627\u062a\u06d5\u0644\u0627\u06cc\u0627\u0646\u060c \u0647\u06d5\u0645\u0648\u0634\u062a\u06cc \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646.',
    '\u067e\u0627\u0634\u0627\u0646 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u0648 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0641\u06ce\u0631\u06a9\u0631\u0627\u0648\u06d5\u060c \u0634\u06ce\u062a\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u062a\u0627\u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u06a9\u06d5\u0645\u060c \u0626\u06d5\u0645 \u067e\u06d5\u062a\u0627\u06cc\u06d5 \u0648\u06d5\u0644\u0627\u062f\u06d5\u0628\u06d5\u06cc\u0646\u061b \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 [\u0633\u06d5\u0631 \u0695\u06ce\u06cc \u067e\u06ce\u0634\u0648\u062a\u0627\u0646].',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u06d5\u06b5\u0645\u06d5\u062a\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u06a9\u06d5 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u0626\u06ce\u0645\u06d5 \u062a\u06c6\u06b5\u06d5\u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06c6\u0632\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5\u0631 \u0644\u06d5\u0645\u0627\u0646 \u062a\u0627\u0642\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0695\u06ce\u0632 [\u0645\u0627\u0646] \u0647\u0627\u062a\u06d5\u0644\u0627\u06cc\u0627\u0646.',
    '\u06a9\u06d5 \u0626\u06d5\u0645 \u0628\u06d5\u0646\u062f\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646\u06d5\u0645 \u067e\u06ce \u0628\u0633\u067e\u06ce\u0631\u0646\u061b \u0645\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce \u062c\u06ce \u0628\u0627\u0648\u06d5\u0695\u0645.',
    '\u0644\u06d5\u062d\u0627\u0646\u062f \u062e\u0648\u062f\u0627 \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5\u0632\u0644\u062a\u0631 \u0645\u06d5\u0628\u06cc\u0646\u0646\u061b \u0645\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645 \u0647\u0627\u0648\u0631\u062f\u0648\u06d5.',
    '\u0645\u0646 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062e\u06c6\u0645 \u0648 \u0626\u06ce\u0648\u06d5 \u067e\u06d5\u0646\u0627\u062f\u06d5\u0628\u06d5\u0645 \u0644\u06d5\u0648\u06d5 \u06a9\u06d5 \u0628\u0645\u06a9\u0648\u0698\u0646.',
    '\u0626\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0634\u0645 \u067e\u06ce \u0646\u0627\u06a9\u06d5\u0646\u060c \u062e\u06c6\u062a\u0627\u0646\u0645 \u0644\u06ce \u062f\u0648\u0631\u062e\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u062f\u0648\u0627\u062c\u0627\u0631 \u0647\u0627\u0646\u0627\u06cc \u0628\u06c6\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u0647\u06ce\u0646\u0627\u060c \u06a9\u06d5: \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0646.',
    '\u0633\u0627\u062a\u06c6 \u0648\u06d5\u0644 \u0626\u06d5\u0645 \u0639\u06d5\u0628\u062f\u0627\u0646\u06d5\u06cc \u0645\u0646 \u0628\u06d5\u0634\u06d5\u0648 \u0628\u0695\u06c6\u061b \u0686\u0648\u0646 \u0626\u06d5\u0648\u0627\u0646 \u062f\u0648\u0627\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u0646.',
    '\u0628\u06d5\u0626\u0627\u0631\u0627\u0645\u06cc \u0644\u06d5\u062f\u06d5\u0631\u06cc\u0627\u06a9\u06d5 \u0628\u067e\u06d5\u0695\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0626\u06d5\u0648 \u0644\u06d5\u0634\u06a9\u0631\u06d5 \u062e\u0646\u06a9\u0627\u0648\u0646.',
    '\u06af\u06d5\u0644\u06ce \u06a9\u0627\u0646\u06cc \u0648 \u0628\u0627\u063a\u0627\u062a\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u0627\u0634 \u0628\u06d5\u062c\u06ce \u0645\u0627\u0646.',
    '\u0641\u0631\u06d5 \u06a9\u06ce\u06b5\u06af\u06d5\u0648 \u06a9\u06c6\u0634\u06a9 \u0648 \u0633\u06d5\u0631\u0633\u06d5\u0631\u0627\u06cc \u0628\u06d5\u0646\u0631\u062e\u06cc\u0634.',
    '\u062e\u06ce\u0631\u0648\u0628\u06ce\u0631\u06ce\u06a9\u06cc\u0634 \u06a9\u06d5\u062a\u06ce\u062f\u0627 \u0644\u06d5\u0648\u067e\u06d5\u0695\u06cc \u062e\u06c6\u0634\u06cc\u062f\u0627 \u062f\u06d5\u0698\u06cc\u0627\u0646.',
    '\u0626\u0627\u0628\u06d5\u0645 \u062c\u06c6\u0631\u06d5\u0648\u061b \u062f\u0627\u0645\u0627\u0646 \u0628\u06d5 \u06af\u06d5\u0644\u06ce\u06a9\u06cc \u062f\u06cc\u06a9\u06d5.',
    '\u0646\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646 \u0634\u06cc\u0646\u06cc \u0628\u06c6\u06af\u06ce\u0695\u0627\u0646\u060c \u0646\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u06cc\u0634. \u0647\u06d5\u0631 \u0645\u06c6\u06b5\u06d5\u062a\u06cc\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u062f\u0631\u0627.',
    '\u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5\u0648\u0626\u0627\u0632\u0627\u0631\u06cc \u067e\u0695\u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0695\u0632\u06af\u0627\u0631 \u06a9\u0631\u062f:',
    '\u0644\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u06ce\u061b \u06a9\u06d5 \u062e\u06c6\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0632\u0644 \u062f\u06d5\u0632\u0627\u0646\u06cc \u0648 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0628\u0648 \u06a9\u06d5 \u0644\u06d5\u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u062f\u06d5\u0631\u062f\u06d5\u0686\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5\u062f\u0627\u0646\u0633\u062a\u06d5 - \u0644\u06d5\u0686\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u0647\u06d5\u0645\u0648\u062f\u0646\u06cc\u0627 - \u0628\u06d5\u0695\u06ce\u0632\u062a\u0631\u06af\u0631\u062a.',
    '\u0647\u06ce\u0646\u062f\u06d5\u0634\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646\u06d5\u062f\u0627\u0646\u06ce \u06a9\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u060c \u0628\u0628\u0646 \u0628\u06d5\u0645\u0627\u06cc\u06d5\u06cc \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06ce.',
    '\u0648\u0627 \u0626\u06ce\u0633\u062a\u0627 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0626\u06ce\u0698\u0646:',
    '\u0647\u06d5\u0631 \u0628\u06c6 \u06cc\u06d5\u06a9\u06d5\u0645\u062c\u0627\u0631 \u06a9\u06d5 \u062f\u06d5\u0645\u0631\u06cc\u0646\u060c \u062a\u0627\u0632\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648 \u0646\u0627\u0628\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc\u062a\u0627\u0646\u06d5\u060c \u0628\u0627\u0648\u0627\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0628\u06c6\u0628\u06ce\u0646\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u0627\u0634\u062a\u0631\u0646\u060c \u06cc\u0627\u062e\u06c6 \u06af\u06d5\u0644\u06cc \u00ab\u062a\u0648\u0628\u0628\u06d5\u0639\u00bb \u0648 \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u061f \u06a9\u06d5 \u0642\u0695\u0627\u0646\u0645\u0627\u0646 \u062a\u06ce \u062e\u0633\u062a\u0648\u0646\u061b \u0686\u0648\u0646 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0628\u0648\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648\u062f\u0648\u0627\u0646\u06d5\u062f\u0627\u060c \u0628\u06c6 \u06af\u06d5\u0645\u06d5\u0645\u0627\u0646 \u0633\u0627\u0632\u0646\u06d5\u062f\u0627\u0648\u06d5\u061b',
    '\u0647\u06d5\u0631 \u0628\u06c6\u0695\u0627\u0633\u062a\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0698\u0648\u0627\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u06b5\u0627\u0648\u0627\u0631\u062f\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0695\u06c6\u0698\u06d5 \u062f\u06c6\u0633\u062a \u0644\u06d5\u0645\u06d5\u0695 \u062f\u06c6\u0633\u062a\u06cc \u0647\u06cc\u0686 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0644\u06d5\u062f\u06d5\u0633 \u0646\u0627\u06cc\u06d5\u0648 \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0633\u06ce \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc \u062e\u0648\u062f\u0627\u06a9\u06d5\u0648\u06ce\u061b \u06a9\u06d5 \u0647\u06d5\u0631\u062e\u06c6\u06cc \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0628\u06d5\u0631\u06cc \u062f\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u0698\u06d5\u0642\u0646\u06d5\u0645\u0648\u062a\u060c',
    '\u062e\u0648\u0627\u0631\u062f\u0646\u06cc \u06af\u0648\u0646\u0627\u0647\u0628\u0627\u0631\u0627\u0646\u06d5.',
    '\u06a9\u06d5 \u0686\u06d5\u0634\u0646\u06cc \u062e\u06b5\u062a\u06d5\u06cc \u0695\u06c6\u0646 \u0632\u06d5\u06cc\u062a\u0648\u0646\u060c \u0644\u06d5 \u0646\u0627\u0648 \u0633\u06a9\u0627\u0646\u062f\u0627 \u062f\u06ce\u062a\u06d5\u06a9\u0648\u06b5\u061b',
    '\u0648\u06d5\u06a9 \u06a9\u0648\u06b5\u06cc\u0646\u06cc \u0626\u0627\u0648\u06cc \u06af\u06d5\u0631\u0645.',
    '\u0633\u0627 \u0628\u06cc\u06af\u0631\u0646 \u0648 \u0695\u0627\u06cc \u0628\u06a9\u06ce\u0634\u0646 \u0628\u06c6\u0646\u0627\u0648\u06d5\u0695\u0627\u0633\u062a\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645.',
    '\u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5\u0634 \u0647\u06d5\u0631 \u0628\u06c6\u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u060c \u0626\u0627\u06a4\u0627\u06a9\u06d5\u0644\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627\u06a9\u06d5\u0646.',
    '\u062f\u06d5\u0628\u0686\u06ce\u0698\u06d5! \u062a\u06c6\u06cc\u06d5\u06a9\u06cc \u06a9\u06d5 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0647\u06ce\u0698\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0628\u0648\u06cc.',
    '\u0626\u06d5\u0648\u06d5 \u0626\u06d5\u0648\u0634\u062a\u06d5 \u0647\u0627\u062a\u06d5\u062f\u06cc \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06ce\u06cc \u0628\u06d5\u06af\u0648\u0645\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648\u0627 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0648\u0627\u0646 \u0644\u06d5\u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u0647\u06ce\u0645\u0646\u062f\u0627.',
    '\u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc \u06a9\u0627\u0646\u06cc\u0627\u0648\u0627\u0646 \u0648 \u0628\u0627\u063a\u0627\u062a\u0627\u0646\u062f\u0627\u060c',
    '\u067e\u0627\u0631\u0686\u06d5\u06cc \u0646\u0627\u0633\u06a9 \u0648 \u0626\u06d5\u0633\u062a\u0648\u0631\u06cc \u0644\u06d5\u0647\u06d5\u06a4\u0631\u06d5\u0645\u06cc\u0634 \u0644\u062e\u06c6\u062f\u06d5\u06a9\u06d5\u0646\u061b \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631\u0628\u06d5\u06cc\u06d5\u06a9\u0646.',
    '\u0632\u06c6\u0631\u06cc\u0634 \u06a9\u06cc\u0698\u06cc \u0686\u0627\u0648\u06a9\u06d5\u0698\u0627\u06b5\u06cc \u0633\u067e\u06cc \u0648 \u0633\u06c6\u06b5\u0645\u0627\u0646 \u0628\u06c6\u0647\u0627\u0648\u0633\u06d5\u0631\u06cc \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc\u0648\u0646.',
    '\u0644\u06d5\u0648\u06ce\u062f\u0627 \u0647\u06d5\u0631\u0645\u06cc\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06d5\u062f\u06b5\u06cc\u0627\u0646\u06d5\u060c \u0628\u06d5\u0626\u0627\u0631\u062e\u06d5\u06cc\u0627\u0646\u06cc \u062f\u06d5\u062e\u0648\u0627\u0632\u0646.',
    '\u062c\u06af\u06d5 \u0644\u06d5 \u0645\u06d5\u0631\u06af\u06cc \u0647\u06d5\u0648\u06d5\u06b5\u06ce\u060c \u0644\u06d5\u0648\u0634\u0648\u06ce\u0646\u06d5\u062f\u0627 \u062a\u0627\u0645\u06cc \u0645\u0631\u062f\u0646\u06ce \u0646\u0627\u0686\u06ce\u0698\u0646 \u0648 \u0644\u06d5\u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u06c6\u0698\u06d5\u0647\u06cc\u0634\u06cc \u067e\u0627\u0631\u0627\u0632\u062a\u06af\u0646.',
    '\u0686\u0627\u06a9\u06d5\u06cc\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u06a9\u0631\u062f\u0648\u06cc\u06d5\u062a\u06cc. \u0626\u06d5\u0645\u06d5\u0634 \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06cc\u06d5\u06a9\u06cc \u0632\u06c6\u0631\u06af\u0631\u06cc\u0646\u06af\u06d5.',
    '\u0647\u06d5\u0631\u0628\u06c6\u06cc\u06d5 \u0648\u0627 \u0628\u06d5\u0647\u0627\u0633\u0627\u0646\u06cc \u0628\u06d5\u0632\u0645\u0627\u0646\u06cc \u062a\u06c6\u0645\u0627\u0646 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f\u060c \u06a9\u06d5 \u0634\u0627\u06cc\u06d5\u062a \u0628\u06cc\u0631\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0633\u0627 \u062a\u06c6 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc \u0628\u06a9\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0648\u0627 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0646.',
    '\u062d\u0627\u060c \u0645\u06cc\u0645.',
    '\u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u06a9\u0627\u0631\u0632\u0627\u0646 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06cc.',
    '\u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u060c \u0628\u06d5\u06b5\u06af\u06d5 \u0632\u06c6\u0631\u0646 \u0628\u06c6\u06a9\u06d5\u0633\u0627\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695.',
    '\u0644\u06d5\u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0648 \u0628\u06b5\u0627\u0648\u0628\u0648\u0646\u06cc \u062c\u0627\u0646\u06d5\u0648\u06d5\u0631\u0627\u0646\u060c \u0686\u06d5\u0646\u062f \u0628\u06d5\u06b5\u06af\u06d5 \u0647\u06d5\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0628\u0695\u0648\u0627\u062f\u0627 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u0646.',
    '\u0644\u06d5\u0647\u0627\u062a\u0648\u0686\u0648\u06cc \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698\u0648\u0648 \u0644\u06d5\u0648 \u0695\u06c6\u0632\u06cc \u06cc\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5\u0648\u06d5 \u0628\u06d5\u0695\u06ce\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u0632\u06d5\u0648\u06cc \u0645\u0631\u062f\u0648\u06cc \u0628\u06d5\u0648\u0647\u06c6\u06cc\u06d5 \u0632\u06cc\u0646\u062f\u0648\u0648\u06a9\u0631\u062f\u06d5\u0648\u06d5\u0648 \u0644\u06d5\u0647\u06d5\u06b5\u06a9\u0631\u062f\u0646\u06cc \u0628\u0627\u06cc\u06d5\u06a9\u0627\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0647\u06d5\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646 \u06a9\u06d5 \u0628\u06c6\u062a\u06cc \u062f\u06d5\u062e\u0648\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648\u06cc \u0695\u0627\u0633\u062a\u06d5. \u062f\u06d5\u06cc \u0633\u0627 \u0644\u06d5 \u067e\u0627\u0634 \u0628\u0627\u0633\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0628\u0627\u0633\u06cc \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u060c \u0628\u06d5\u06a9\u0627\u0645 \u0642\u0633\u06d5 \u0628\u0695\u0648\u0627\u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0647\u0627\u06cc \u0644\u06d5\u0648\u06a9\u06d5\u0633\u06d5\u06cc \u0628\u0648\u062e\u062a\u0627\u0646 \u06a9\u06d5\u0631\u0648 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06d5!',
    '\u0647\u06d5\u0631\u06af\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u06ce\u062a\u06d5\u0648\u06d5\u060c \u062e\u06c6\u06cc \u062f\u06d5\u06cc\u0628\u06cc\u0633\u06ce. \u06a9\u06d5\u0686\u06cc \u062f\u0648\u0627\u06cc\u06d5 - \u0628\u06d5 \u0647\u06c6\u06cc \u0644\u06d5\u062e\u06c6\u0628\u0627\u06cc\u06cc \u0628\u0648\u0646\u06cc - \u067e\u06ce \u062f\u0627\u062f\u06d5\u06af\u0631\u06ce \u0648 \u0626\u06ce\u0698\u06cc \u0647\u06d5\u0631 \u0646\u06d5\u0634\u06cc \u0628\u06cc\u0633\u062a\u0648\u06d5. \u0633\u0627 \u062a\u06c6 \u0645\u0632\u06af\u06ce\u0646\u06cc \u067e\u06ce \u0628\u062f\u06d5 \u06a9\u06d5 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc \u0628\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u06ce.',
    '\u0626\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc\u0634 \u0644\u06d5\u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u060c \u0641\u06ce\u0631\u0628\u0628\u06ce \u06af\u0627\u06b5\u062a\u06d5\u06cc \u067e\u06ce\u062f\u06d5\u06a9\u0627. \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9 \u0628\u06d5\u0634\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u062a\u06d5\u0648\u0627\u0648 \u0626\u0627\u0628\u0695\u0648\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0627.',
    '\u062f\u06c6\u0698\u06d5\u0647\u06cc\u0634\u06cc\u0627\u0646 \u0648\u0627 \u0628\u06d5\u062f\u0648\u0627\u0648\u06d5. \u0646\u06d5 \u0626\u06d5\u0648\u0634\u062a\u06d5\u06cc \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u06af\u06cc\u0631\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u060c \u0646\u06d5 \u062f\u06c6\u0633\u062a\u0627\u0646\u06ce\u06a9 - \u063a\u06d5\u06cc\u0631\u06d5\u0632\u062e\u0648\u0627 - \u0647\u06d5\u06b5\u06cc\u0627\u0646 \u0628\u0698\u0627\u0631\u062f\u060c \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u0646. \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0633\u06d5\u062e\u062a\u06cc\u0634 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0645\u06d5 \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646\u062f\u06d5\u0631\u06ce\u06a9\u06d5. \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u062d\u0627\u0634\u0627 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u062f\u0648\u0698\u0648\u0627\u0631\u0648 \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u0633.',
    '\u062e\u0648\u0627 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u06a9\u06d5\u0648\u06cc \u06a9\u0631\u062f\u060c \u06a9\u06d5 \u0644\u06d5\u06af\u0648\u06ce\u0631\u06d5\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u060c \u06af\u06d5\u0645\u06cc \u0644\u06d5\u0646\u0627\u0648\u062f\u0627 \u0628\u06af\u06d5\u0695\u06ce \u0648 \u0628\u06c6\u0686\u0627\u06a9\u06d5\u06cc \u0626\u06d5\u0648 \u062a\u06ce \u0628\u06a9\u06c6\u0634\u0646\u061b \u0628\u06d5\u0634\u06a9\u06d5 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631\u0628\u0646.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u062f\u0627\u0646\u060c \u06af\u0634 \u0647\u06cc \u0626\u06d5\u0648\u0646\u061b \u0626\u06d5\u0648 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc \u06a9\u06d5\u0648\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646. \u06a9\u06d5 \u0644\u06d5\u0645\u06d5\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646 \u0632\u06c6\u0631\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0695\u0627\u062f\u06d5\u0645\u06ce\u0646\u0646.',
    '\u062a\u06c6 \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0628\u06ce\u0698\u06d5: \u0628\u0627 \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0628\u0628\u06d5\u062e\u0634\u0646 \u06a9\u06d5 \u0628\u06c6 \u0695\u06c6\u0698\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5\u0647\u06cc\u0648\u0627\u0646\u06cc\u0646\u061b \u062a\u0627 \u0628\u06d5 \u0647\u06d5\u0631\u06af\u0631\u06c6\u06cc\u06d5\u06a9 - \u0628\u06d5 \u067e\u06ce\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 - \u067e\u0627\u062f\u0627\u0634 \u0628\u062f\u0627.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u06a9\u0627\u0631\u06cc \u0628\u0627\u0634 \u062f\u06d5\u06a9\u0627\u060c \u0628\u06c6\u062e\u06c6\u06cc\u06d5\u062a\u06cc\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u062e\u0631\u0627\u067e\u06d5 \u062f\u06d5\u06a9\u0627\u060c \u062f\u0698\u06cc \u062e\u06c6\u06cc \u06a9\u0631\u062f. \u067e\u0627\u0634\u0627\u0646 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u062f\u06d5\u0628\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u06a9\u062a\u06ce\u0628 \u0648 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0648 \u067e\u0644\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u06cc\u06d5\u062a\u06cc\u0645\u0627\u0646\u062f\u0627 \u0628\u06d5\u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u061b \u0628\u0698\u06cc\u0648\u06cc \u067e\u0627\u06a9 \u0648 \u0686\u0627\u06a9\u06cc\u0634\u0645\u0627\u0646 \u067e\u06ce \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646. \u0644\u06d5\u0686\u0627\u0648 \u0647\u06d5\u0645\u0648\u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u0634 \u0633\u06d5\u0631\u0645\u0627\u0646 \u062e\u0633\u062a\u0646.',
    '\u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5\u062f\u06cc\u0646\u06d5\u06a9\u06d5\u0634\u06cc\u0627\u0646 \u0632\u06c6\u0631\u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u0631\u0645\u0627\u0646 \u067e\u06ce \u0633\u067e\u0627\u0631\u062f\u0646. \u0644\u06d5\u0646\u0627\u0648\u06d5\u062e\u06c6\u0634 \u06af\u0631\u0641\u062a\u0627\u0631\u06cc \u06a9\u06ce\u0634\u06d5 \u0646\u06d5\u0628\u0648\u0648\u0646\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0648\u06d5\u062e\u062a\u06ce \u06a9\u06d5 \u0644\u06d5\u0645 \u062f\u06cc\u0646\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0648\u0648\u0646\u061b \u0626\u06d5\u0648\u06cc\u0634 \u0647\u06d5\u0631 \u0644\u06d5 \u0628\u06d5\u0631 \u0626\u06ce\u0631\u06d5\u06cc\u06cc \u0646\u06ce\u0648\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0628\u06d5\u0648\u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627\u060c \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u06cc \u067e\u06ce\u06a9 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648\u0646.',
    '\u062f\u0648\u0627\u062c\u0627\u0631 \u0626\u06d5\u062a\u06c6\u0645\u0627\u0646 \u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631 \u0695\u06ce\u06af\u06d5\u0648\u0634\u0648\u06ce\u0646\u06ce\u06a9 \u0644\u06d5\u0645 \u0626\u0627\u06cc\u06cc\u0646\u06d5\u061b \u0634\u0648\u06ce\u0646\u06cc \u06a9\u06d5\u0648\u06d5\u0648 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u0626\u06d5\u0648 \u0646\u06d5\u0632\u0627\u0646\u0627\u0646\u06d5 \u0645\u06d5\u06a9\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646 \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0644\u06d5\u062e\u0648\u062f\u0627\u062a \u0628\u06ce \u0646\u06cc\u0627\u0632\u06a9\u06d5\u0646. \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u062f\u06c6\u0633\u062a\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u0646\u061b \u062e\u0648\u0627\u0634 \u062f\u06c6\u0633\u062a\u06cc \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0645 \u0642\u0648\u0695\u0626\u0627\u0646\u06d5 \u0633\u06c6\u0645\u0627\u06cc \u0686\u0627\u0648\u06d5 \u0628\u06c6\u0645\u06d5\u0631\u062f\u0645 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0648 \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646\u062f\u06d5\u0631\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0628\u0695\u0648\u0627\u062f\u0627 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u062f\u06a9\u0627\u0631\u0646\u060c \u0648\u0627\u062f\u06d5\u0632\u0627\u0646\u0646 \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0698\u06cc \u0648 \u0645\u06d5\u0631\u06af\u06cc\u0627\u0646\u062f\u0627 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc\u0627\u0646 \u062f\u0627\u062f\u06d5\u0646\u06ce\u06cc\u0646 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646\u061f \u0632\u06c6\u0631\u062e\u0631\u0627\u067e\u06cc \u062a\u06ce\u06af\u06d5\u06cc\u0634\u062a\u0648\u0646.',
    '\u062e\u0648\u0627 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u060c \u062a\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0686\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u0648\u06d5\u0631\u06af\u0631\u06ce\u062a\u06d5\u0648\u06d5\u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u062a\u06c6 \u0626\u06d5\u0648\u06a9\u06d5\u0633\u06d5\u062a \u062f\u06cc\u0648\u06d5 \u06a9\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u061f \u062e\u0648\u062f\u0627 - \u06a9\u06d5 \u0626\u0627\u06af\u0627\u06cc \u0644\u06ce \u0647\u06d5\u0628\u0648 - \u0695\u06ce\u06cc \u0644\u06ce \u06af\u06c6\u0695\u06cc \u0648 \u06af\u0648\u06ce\u0686\u06a9\u06d5\u0648 \u062f\u06b5\u06cc\u0634\u06cc \u0645\u06c6\u0631\u06a9\u0631\u062f\u0648 \u0644\u06ce\u06b5\u0627\u06cc\u06cc \u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631\u0686\u0627\u0648\u06cc. \u0633\u0627 \u06a9\u06ce \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u067e\u0627\u0634 \u062e\u0648\u062f\u0627\u060c \u0695\u06ce\u06cc \u0646\u06cc\u0634\u0627\u0646\u062f\u0627\u061f \u062f\u06d5\u06cc \u0628\u06c6\u0686\u06cc \u0628\u06cc\u0631\u0646\u0627\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061f',
    '\u0647\u06d5\u0631\u0648\u0627 \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u06ce\u0698\u06af\u06d5 \u0644\u06d5\u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627\u0645\u0627\u0646 \u0686\u06cc\u062a\u0631\u0646\u06cc\u06d5. \u0647\u06d5\u0631 \u062f\u06d5\u0645\u0631\u06cc\u0646 \u0648 \u062f\u06d5\u0698\u06cc\u0646\u06d5\u0648\u06d5\u0648 \u0647\u06d5\u0631 \u0633\u0631\u0648\u0634\u062a \u062f\u06d5\u0645\u0627\u0646 \u0645\u0631\u06ce\u0646\u06ce. \u0647\u06cc\u0686. \u0634\u062a\u06ce \u0644\u06d5\u0645\u06d5\u0634 \u0646\u0627\u0632\u0627\u0646\u0646\u061b \u0647\u06d5\u0631 \u0644\u06d5\u062e\u06c6\u0648\u06d5 \u06af\u0648\u0645\u0627\u0646 \u062f\u06d5\u0628\u06d5\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627\u0634 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06a9\u0627\u0646\u06cc \u0645\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u0646\u060c \u0628\u0695\u0648\u0628\u06cc\u0627\u0646\u0648 \u062f\u06d5\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5\u0648 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0642\u0633\u06d5\u0648 \u0695\u0627\u0633\u062a\u0646\u060c \u0628\u0627\u0628 \u0648 \u06a9\u0627\u06b5\u0645\u0627\u0646 \u0628\u06c6\u0628\u06ce\u0646\u0646\u06d5\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u062e\u0648\u062f\u0627 \u0698\u06cc\u0648 \u067e\u06ce\u062f\u06d5\u062f\u0627\u0648 \u0644\u06d5\u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u062a\u0627\u0646\u0645\u0631\u06ce\u0646\u06ce \u0648 \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u0627 - \u06a9\u06d5 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631\u062f\u0627\u062f\u06ce - \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u06a9\u06c6\u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0647\u0627\u06cc \u0644\u06ce \u0646\u06cc\u06d5.',
    '\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u0627\u06cc\u06d5. \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0633\u06d5\u06b5\u0627 \u0695\u0627\u062f\u06d5\u0628\u06ce\u060c \u0626\u06d5\u0645 \u062a\u06d5\u0631\u0632\u06d5 \u0647\u06d5\u0631\u0632\u06d5\u0648\u06ce\u0698\u0627\u0646\u06d5 \u0632\u06d5\u0631\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0646.',
    '\u062f\u06d5\u0634\u0628\u06cc\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9 \u0628\u06d5\u0695\u06ce\u0632 \u0628\u06d5 \u0633\u06d5\u0631 \u0686\u06c6\u06a9 \u062f\u0627\u0647\u0627\u062a\u0648\u0648\u0646\u061b \u0647\u06d5\u0631\u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9\u06cc\u0634 \u0628\u0627\u0646\u06af \u062f\u06d5\u06a9\u0631\u06ce\u060c \u0628\u06c6\u062f\u06cc\u062a\u0646\u06cc \u0633\u06cc\u0627\u06cc\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc. \u0626\u06d5\u0648\u0627 \u0626\u06d5\u0645\u0695\u06c6 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0626\u06d5\u062f\u0631\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0645\u06d5 \u06a9\u062a\u06ce\u0628\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u06d5\u060c \u06a9\u06d5 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0648 \u0628\u06d5\u0632\u06d5\u0631\u06d5\u062f\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u062f\u0648\u06ce. \u0647\u06d5\u0631\u0686\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u0626\u06ce\u0648\u06d5\u0634 \u0628\u0648\u0648\u060c \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u062f\u06d5\u0646\u0648\u0633\u06cc\u0646.',
    '\u0633\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646\u060c \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06cc\u0627\u0646\u062e\u0627\u062a\u06d5 \u0628\u06d5\u0631\u0628\u06d5\u0632\u06d5\u06cc\u06cc\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06cc\u06d5\u06a9\u06cc \u0632\u06c6\u0631\u06af\u0631\u06cc\u0646\u06af\u06d5.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 [\u067e\u06ce\u06cc\u0627\u0646 \u0626\u06d5\u0648\u062a\u0631\u06ce:] \u0626\u0627\u062e\u06c6 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646\u062a\u0627\u0646 \u0628\u06c6\u0646\u06d5\u062f\u06d5\u062e\u0648\u06ce\u0646\u0631\u0627\u06cc\u06d5\u0648\u06d5 \u06a9\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5\u0632\u0644 \u062f\u06d5\u0632\u0627\u0646\u06cc \u0648 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0628\u0648\u0646\u061f',
    '\u0647\u06d5\u0631\u06af\u0627\u0634 \u0648\u062a\u0631\u0627\u060c \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627 \u0695\u0627\u0633\u062a\u06d5\u0648 \u0633\u06d5\u06b5\u0627\u0695\u0627\u0628\u0648\u0646 \u0647\u06cc\u0686 \u06af\u0648\u0645\u0627\u0646\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5\u060c \u0626\u06ce\u0648\u06d5 \u0648\u062a\u062a\u0627\u0646: \u0647\u06d5\u0631\u0646\u0627\u0632\u0627\u0646\u06cc\u0646 \u0642\u06cc\u0627\u0645\u06d5\u062a \u0686\u06cc\u06cc\u06d5\u061f \u0644\u0627\u0645\u0627\u0646 \u062e\u06d5\u06cc\u0627\u06b5\u06ce\u06a9\u06cc \u062e\u0627\u0648\u06d5\u0648 \u062f\u06b5\u0645\u0627\u0646 \u0642\u0646\u06cc\u0627\u062a\u06cc \u067e\u06ce \u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06a9\u0627\u0631\u06ce\u06a9\u06cc \u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u0644\u06d5\u062f\u06d5\u0633 \u0647\u0627\u062a\u0628\u0648\u060c \u0626\u0627\u0634\u06a9\u0631\u0627 \u0646\u06cc\u0634\u0627\u0646\u06cc\u0627\u0646 \u062f\u0631\u0627. \u0626\u06d5\u0648\u0634\u062a\u0627\u0646\u06d5\u0634 \u062d\u06d5\u0646\u06d5\u06a9\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u06a9\u0631\u062f\u060c \u062f\u06d5\u0648\u0631\u06cc \u0644\u06ce\u062f\u0627\u0646.',
    '\u067e\u06ce\u06cc\u0627\u0646 \u0648\u062a\u0631\u0627: \u0648\u0627 \u0626\u06d5\u0645\u0695\u06c6 \u0644\u06d5 \u0628\u06cc\u0631\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u061b \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5 \u062a\u0648\u0634 \u0647\u0627\u062a\u0646\u06cc \u0626\u06d5\u0645\u0695\u06c6\u062a\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0647\u0627\u062a\u06d5\u0628\u06cc\u0631. \u062c\u06ce\u062a\u0627\u0646 \u0644\u06d5\u0646\u0627\u0648\u0626\u0627\u06af\u0631\u062f\u0627\u06cc\u06d5\u061b \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc\u0634 \u06cc\u0627\u0631\u06cc\u062a\u0627\u0646 \u0646\u0627\u06a9\u0627.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u062a\u0627\u0646 \u06a9\u0631\u062f\u0628\u0648\u06d5 \u062c\u06ce \u06af\u06d5\u0645\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646. \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627 \u0626\u06ce\u0648\u06d5\u06cc \u0647\u06d5\u06b5\u062e\u06d5\u06b5\u06d5\u062a\u0627\u0646\u062f\u0628\u0648. \u0626\u06cc\u062a\u0631 \u0626\u06d5\u0645\u0695\u06c6 \u0644\u06d5\u0626\u0627\u06af\u0631 \u062f\u06d5\u0631\u06cc\u0627\u0646 \u0646\u0627\u062e\u06d5\u0646 \u0648 \u0695\u06ce\u06af\u06d5 \u0646\u06cc\u06cc\u06d5 \u062f\u0627\u0648\u0627\u06cc \u0644\u06ce \u0628\u0648\u0631\u062f\u0646\u06cc\u0634 \u0628\u06a9\u06d5\u0646.',
    '\u0633\u0627 \u0633\u0648\u067e\u0627\u0633 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627\u06cc\u06d5\u061b \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062c\u06cc\u0647\u0627\u0646\u06cc\u0634\u06d5.',
    '\u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc\u0634\u060c \u0634\u06a9\u06c6\u0648 \u06af\u06d5\u0648\u0695\u06d5\u06cc\u06cc \u0628\u06c6\u0626\u06d5\u0648\u06d5\u0648 \u0647\u06d5\u0631\u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u062d\u0627\u060c \u0645\u06cc\u0645.',
    '\u0626\u06d5\u0645 \u06a9\u062a\u06ce\u0628\u06d5 \u0644\u06d5\u062e\u0648\u062f\u0627\u06cc \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627.',
    '\u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0628\u06c6\u0695\u0627\u0633\u062a\u06cc \u0648\u06d5\u062f\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0646 \u0648 \u0645\u06d5\u0648\u062f\u0627\u06cc\u0627\u0646 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u06d5. \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5\u060c \u0644\u06d5\u0686\u06cc \u062a\u0631\u0633\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06ce\u0631\u0646\u060c \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0626\u06ce\u0648\u06d5 - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 - \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u0646\u06d5\u0628\u06d5\u0695\u06ce\u060c \u0686\u06cc\u06cc\u0627\u0646 \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5 \u0686\u06ce \u06a9\u0631\u062f\u0648\u0648\u06d5\u061f \u06cc\u0627\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0628\u06d5\u0634\u062f\u0627\u0631\u0628\u0648\u06af\u0646\u060c \u0646\u06cc\u0634\u0627\u0646\u0645 \u062f\u06d5\u0646. \u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc\u062a\u0627\u0646\u06d5\u060c \u06a9\u062a\u06ce\u0628\u06ce\u06a9 \u0628\u06d5\u0631\u0644\u06d5\u0645 [\u0642\u0648\u0695\u0626\u0627\u0646\u06d5] \u0647\u0627\u062a\u0628\u06ce\u060c \u06cc\u0627\u0646 \u0642\u0633\u06d5\u06cc \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0632\u0627\u0646\u0633\u062a - \u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5\u06a9\u06d5\u0648\u0646\u0627\u0631\u0627\u0648\u06d5 \u0645\u0627\u0648\u06d5 - \u0628\u06c6\u0645\u06cc \u0628\u06ce\u0631\u0646.',
    '\u06a9\u06ce\u0634 \u0644\u06d5\u0648\u06a9\u06d5\u0633\u06d5 \u06af\u0648\u0645\u0695\u0627\u062a\u0631\u06d5 \u0647\u0627\u0646\u0627 \u0628\u06c6\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u0627 \u062f\u06d5\u0628\u0627\u061f \u0628\u06c6\u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0647\u06d5\u062a\u0627 \u0642\u06cc\u0627\u0645\u06d5\u062a \u067e\u06d5\u0631\u0633\u06a4\u06cc \u067e\u06ce \u0646\u0627\u062f\u0627\u062a\u06d5\u0648\u06d5\u0648 \u0647\u06d5\u0631 \u0626\u0627\u06af\u0627\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u067e\u0627\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0645\u06d5\u0631\u062f\u0645 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u06a9\u06c6\u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0628\u0646\u06d5 \u062f\u0648\u0698\u0645\u0646\u06cc\u0627\u0646 \u0648 \u062d\u0627\u0634\u0627\u062f\u06d5\u06a9\u06d5\u0646 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0633\u062a\u0648\u06cc\u0627\u0646\u0646',
    '\u0647\u06d5\u0631\u06af\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627\u06a9\u0627\u0646\u06cc \u0645\u06d5\u06cc\u0627\u0646 \u0628\u06c6\u0648\u06d5\u062e\u0648\u06ce\u0646\u0646 - \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u06a9\u0627\u062a\u06ce \u0642\u0648\u0695\u0626\u0627\u0646\u06cc\u0627\u0646 \u0628\u06c6\u0647\u0627\u062a \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u0644\u06ce \u06a9\u0631\u062f - \u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0628\u06d5\u0626\u0627\u0634\u06a9\u0631\u0627 \u062c\u0627\u062f\u0648\u06d5.',
    '\u06cc\u0627\u0646 \u062f\u0628\u06ce\u0698\u0646: \u0647\u06d5\u0631 \u0628\u06c6\u062e\u06c6\u06cc \u0647\u06d5\u06b5\u06cc \u0628\u06d5\u0633\u062a\u0648\u06d5. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06d5\u0631 \u0628\u06c6\u062e\u06c6\u0645 \u0647\u06d5\u06b5\u0645 \u0628\u06d5\u0633\u062a\u0628\u06ce\u060c \u067e\u06ce\u062a\u0627\u0646 \u0646\u0627\u06a9\u0631\u06ce \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0628\u06c6\u0645\u0646 \u0628\u06a9\u06d5\u0646\u061b \u062e\u0648\u062f\u0627\u0634 \u067e\u062a\u0631 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0644\u06d5\u0648 \u062a\u0648\u0627\u0646\u062c\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u0642\u0648\u0695\u0639\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u0631\u0646\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u06d5\u0633\u06d5 \u0628\u06c6 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u0645\u0646 \u0648 \u0626\u06ce\u0648\u06d5\u062f\u0627\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u06af\u0648\u0646\u0627\u0647\u0628\u06d5\u062e\u0634\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06d5\u060c \u0645\u0646 \u06cc\u06d5\u06a9\u06ce \u0646\u0627\u0626\u0627\u0633\u0627\u06cc\u06cc \u0646\u06cc\u0645\u061b \u0646\u0627\u0634\u0632\u0627\u0646\u0645 \u0644\u06d5 \u0645\u0646 \u0628\u0627 \u0644\u06d5\u0646\u06af\u06c6 \u0686 \u062f\u06d5\u0642\u06d5\u0648\u0645\u06ce. \u0645\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0645 \u0644\u06d5\u0633\u06d5\u0631\u06d5 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0626\u06d5\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5\u0628\u0645 \u06a9\u06d5 \u067e\u06ce\u0645 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u06d5\u0646\u062f\u0631\u06ce. \u0645\u0646 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u0628\u06d5\u0626\u0627\u0634\u06a9\u0631\u0627 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u0645 \u0648 \u0686\u06cc \u062a\u0631\u06cc\u0634 \u0646\u06cc\u0645.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u0686 \u062f\u06d5\u06b5\u06ce\u0646 \u0626\u06d5\u0631 [\u0626\u06d5\u0645 \u0642\u0648\u0695\u0626\u0627\u0646\u06d5] \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u0628\u06ce \u0648 \u0626\u06ce\u0648\u06d5 \u062d\u0627\u0634\u0627\u06cc \u0644\u06ce \u0628\u06a9\u06d5\u0646 \u0648 \u06cc\u06d5\u06a9 \u0644\u06d5\u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06cc\u0633\u0695\u0627\u0626\u06cc\u0644\u06cc\u0634 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0628\u062f\u0627 \u0644\u06d5 \u0633\u06d5\u0631 \u0648\u06ce\u0646\u06d5\u06a9\u06d5\u06cc \u0648 \u0628\u0695\u0648\u0627\u0628\u06ce\u0646\u06ce \u0648 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u062e\u06c6\u0628\u06d5\u0632\u0644 \u0632\u0627\u0646 \u0628\u0646\u061f! \u062e\u0648\u062f\u0627 \u062e\u06ce\u06b5\u06cc \u063a\u06d5\u062f\u0631\u06a9\u06d5\u0631\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648\u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u0646\u0627\u06a9\u0627.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0628\u06c6\u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc \u0628\u0627\u0634\u06cc \u062a\u06ce\u062f\u0627\u0647\u06d5\u0628\u0627\u060c \u0628\u06d5\u0631\u0644\u06d5\u0626\u06ce\u0645\u06d5\u06cc \u0628\u06c6\u0646\u06d5\u062f\u06d5\u0686\u0648\u0646. \u0626\u06ce\u0633\u062a\u0627 \u06a9\u06d5 \u0646\u06d5\u06cc\u06af\u06d5\u06cc\u0634\u062a\u0648\u0646\u06ce\u060c \u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u062f\u0631\u06c6\u06cc\u06d5\u06a9\u06cc \u06a9\u06d5\u0648\u0646\u0627\u0631\u0627\u06cc\u06d5.',
    '\u0644\u06d5 \u067e\u06ce\u0634 \u0626\u06d5\u0645\u06cc\u0634 \u06a9\u062a\u06ce\u0628\u06cc \u0645\u0648\u0648\u0633\u0627 \u067e\u06ce\u0634\u06d5\u0648\u0627\u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u0628\u0648. \u0626\u06d5\u0645 \u0642\u0648\u0695\u0626\u0627\u0646\u06d5 \u0626\u06d5\u0648\u06cc\u0634\u06cc \u0628\u06d5\u0695\u0627\u0633\u062a \u0632\u0627\u0646\u06cc\u0648\u06d5\u0648 \u0628\u06d5\u0626\u06d5\u0632\u0645\u0627\u0646\u06cc \u0639\u06d5\u0695\u06d5\u0628\u06cc \u06cc\u06d5\u061b \u062a\u0627 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u0628\u062a\u0631\u0633\u06ce\u0646\u06ce \u0648 \u0645\u0698\u062f\u06d5\u0628\u06ce \u0628\u06c6\u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0648\u062a\u0648\u06cc\u0627\u0646\u06d5: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u0634\u062f\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u0633\u06d5\u0631 \u0642\u0633\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0645\u0627\u0646\u060c \u0646\u06d5 \u062a\u0631\u0633\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u06cc\u06d5\u060c \u0646\u06d5 \u062e\u06c6\u0645 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0644\u06d5\u0642\u06d5\u0631\u06d5\u0628\u0648\u06cc \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u0646 \u0648 \u0647\u06d5\u062a\u0627\u0633\u06d5\u0631 \u0647\u06d5\u0631 \u062a\u06ce\u06cc\u062f\u0627\u062f\u06d5\u0628\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u0645\u0631\u06c6\u0645\u0627\u0646 \u0626\u06d5\u0633\u067e\u0627\u0631\u062f \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u0627\u06cc\u06a9 \u0648 \u0628\u0627\u0628\u06cc \u062e\u06c6\u06cc \u0686\u0627\u06a9\u06d5\u0628\u06a9\u0627. \u062f\u0627\u06cc\u06a9\u06cc \u0628\u06d5\u062a\u06c6\u0644\u06cc \u0647\u06d5\u06b5\u06cc \u06af\u0631\u062a\u060c \u0695\u06d5\u0646\u062c\u06cc \u06a9\u06ce\u0634\u0627\u061b \u06a9\u06d5 \u062f\u0627\u0634\u06cc \u0646\u0627\u060c \u0695\u06d5\u0646\u062c\u06cc \u06a9\u06ce\u0634\u0627. \u0647\u06d5\u06b5\u06af\u0631\u062a\u0646 \u0648 \u0644\u06d5\u0645\u06d5\u0645\u06a9 \u0628\u0695\u06cc\u0646\u06d5\u0648\u06d5\u0634\u06cc \u0633\u06ce \u0647\u06d5\u06cc\u06a4\u06cc \u0628\u0631\u062f\u061b \u0647\u06d5\u062a\u0627 \u06af\u06d5\u06cc\u0634\u062a\u06d5 \u062e\u0648\u0631\u062a\u0627\u0646\u06cc \u0648 \u062a\u06d5\u0645\u06d5\u0646\u06cc \u06af\u06d5\u06cc\u06cc\u0634\u062a\u06d5 \u0686\u0644 \u0633\u0627\u06b5\u061b \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u062e\u06c6\u062a \u0695\u0627\u0645 \u0647\u06ce\u0646\u06d5\u060c \u0633\u067e\u0627\u0633\u06cc \u0626\u06d5\u0648\u0686\u0627\u06a9\u06d5 \u0628\u06a9\u06d5\u0645 \u06a9\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0645\u0646 \u0648 \u062f\u0627\u06cc\u06a9 \u0648 \u0628\u0627\u0648\u06a9\u0645\u062a \u06a9\u0631\u062f\u061b \u06a9\u0627\u0631\u06cc \u0648\u0627 \u0647\u06ce\u0698\u0627\u0645 \u0641\u06ce\u0631\u0628\u06a9\u06d5 \u062a\u06c6 \u0695\u0627\u0632\u06cc \u06a9\u0627\u061b \u062a\u06c6\u0631\u06d5\u0645\u06d5\u0634\u0645 \u0644\u06d5\u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06d5 \u0695\u0627\u0628\u06ce\u0646\u06d5\u061b \u0648\u0627 \u0628\u06d5\u0631\u06d5\u0648\u062a\u06c6 \u06af\u06d5\u0695\u0627\u0645\u06d5\u0648\u06d5\u0648 \u062e\u06c6\u0645 \u0647\u06d5\u0631 \u0628\u06d5 \u062a\u06c6 \u0626\u06d5\u0633\u067e\u0627\u0631\u062f\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0628\u0646\u060c \u0626\u06ce\u0645\u06d5 \u06a9\u0627\u0631\u06d5\u0628\u0627\u0634\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u06cc\u0646 \u0648 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06cc\u0634\u06cc\u0627\u0646 \u062e\u06c6\u0634 \u062f\u06d5\u0628\u06cc\u0646 \u0648 \u0648\u06d5\u0644 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u0628\u0646. \u0626\u06d5\u0648\u0628\u06d5\u06b5\u06ce\u0646\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u06cc\u0627\u0646 \u062f\u0631\u0627\u0648\u06d5\u060c \u0695\u0627\u0633\u062a\u06d5.',
    '\u0626\u06d5\u0648\u0645\u0631\u06c6\u06cc\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5 \u0628\u0627\u0628 \u0648 \u062f\u0627\u06a9\u06cc \u062f\u06d5\u06af\u0648\u062a: \u0644\u0627\u0645 \u0644\u06ce \u0644\u0627\u062f\u06d5\u0646! \u0626\u0627\u062e\u06c6 \u0628\u06d5\u06b5\u06ce\u0646\u0645 \u067e\u06ce \u062f\u06d5\u062f\u06d5\u0646 \u062c\u0627\u0631\u06ce \u062a\u0631 \u0632\u06cc\u0646\u062f\u0648\u0648\u062f\u06d5\u0628\u0645\u06d5\u0648\u06d5\u0648 \u0644\u06d5\u06af\u06c6\u0695 \u062f\u06d5\u0631\u0645 \u062f\u06d5\u0647\u06ce\u0646\u0646\u06d5\u0648\u06d5\u061f \u0686\u06d5\u0646\u062f \u0628\u06d5\u0631\u06d5\u0648\u0686\u06cc\u0646 \u0644\u06d5 \u067e\u06ce\u0634 \u0645\u0646\u062f\u0627 \u0647\u0627\u062a\u0648\u0648\u0646 \u0648\u0686\u0648\u0646. \u062f\u0627\u06a9 \u0648 \u0628\u0627\u0628\u06cc\u0634 \u0647\u06d5\u0631\u067e\u06d5\u0646\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u062f\u06d5\u0628\u0631\u062f [\u0648\u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a:] \u0647\u06d5\u06cc \u0645\u0627\u06b5 \u0648\u06ce\u0631\u0627\u0646 \u0628\u0695\u0648\u0627\u0628\u06ce\u0646\u06d5! \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0695\u0627\u0633\u062a\u06d5. \u0626\u06d5\u0648\u06cc\u0634 \u062f\u06d5\u06cc\u06af\u0648\u062a: \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5 \u0647\u06d5\u0631\u0686\u06cc\u0631\u06c6\u06a9\u06cc \u067e\u06ce\u0634\u06cc\u0646\u0627\u0646\u06d5\u0648 \u0686\u06cc \u062a\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0647\u06d5\u0631 \u0628\u06d5\u062f\u06d5\u0631\u062f\u06cc \u06a9\u06c6\u0645\u06d5\u06b5\u0627\u0646\u06ce\u06a9 - \u0644\u06d5\u062c\u0646\u062f\u06c6\u06a9\u06d5\u0648 \u0645\u0631\u06c6\u06cc\u0627\u0646 - \u0686\u0648\u0646\u060c \u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5\u0648\u0627\u0646 \u0641\u06d5\u0648\u062a\u0627\u0646 \u0648 \u062a\u0648\u0648\u0634 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0626\u06ce\u0645\u06d5 \u0647\u0627\u062a\u0646 \u0648 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0628\u0648\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06c6\u0647\u06d5\u0631\u06cc\u06d5\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u067e\u0644\u06d5\u0648\u067e\u0627\u06cc\u06d5\u0645\u0627\u0646 \u062f\u0627\u0646\u0627\u0648\u06d5. \u062a\u0627 \u062e\u0648\u0627 \u0628\u06d5\u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0628\u06d5\u06af\u0648\u06ce\u0631\u06d5\u06cc \u06a9\u0627\u0631\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u067e\u0627\u062f\u0627\u0634 \u0628\u0627\u062a. \u0646\u0627\u0647\u06d5\u0642\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u06a9\u0631\u06ce.',
    '\u0695\u06c6\u0698\u06ce \u0628\u06d5\u0631\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0626\u0627\u06af\u0631 \u0695\u0627\u062f\u06d5\u0646\u06ce\u0646 [\u067e\u06ce\u0627\u0646 \u062f\u06d5\u06b5\u06ce\u0646:] \u062e\u06c6\u0634\u06cc \u062f\u0646\u06cc\u0627\u0648 \u062f\u06d5\u0631\u0628\u0627\u0632 \u06a9\u0631\u062f \u0648 \u0628\u06d5\u0628\u0627\u0634\u06cc \u0628\u06d5\u0647\u0631\u06d5\u062a\u0627\u0646 \u0644\u06ce \u0628\u0631\u062f\u061b \u0626\u06d5\u0645\u0695\u06c6\u0634 \u0632\u06c6\u0631 \u0628\u06d5\u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u062f\u0631\u06ce\u0646\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646 - \u0628\u06d5\u062e\u06c6\u0695\u0627\u06cc\u06cc - \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5\u0632\u0644 \u0632\u0627\u0646\u06cc \u0648 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u0648\u0644\u0627\u062f\u0627\u0628\u0648.',
    '\u0628\u0631\u0627\u06cc \u0639\u0627\u062f\u06cc\u0634 \u0648\u06d5\u0628\u0631\u0628\u06ce\u0646\u06d5\u060c \u06a9\u06d5 \u0644\u06d5 \u0626\u06d5\u062d\u0642\u0627\u0641 \u062a\u0631\u0633\u06cc \u0648\u06d5\u0628\u06d5\u0631 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0646\u0627: \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0628\u06ce \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0645\u0646 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646\u0645 \u06a9\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u06af\u06d5\u0648\u0631\u06d5\u062f\u0627 \u0626\u0627\u0632\u0627\u0631\u0628\u0628\u06cc\u0646\u0646. \u0628\u06d5\u0631\u0644\u06d5 \u0626\u06d5\u0648 \u0648 \u0644\u06d5 \u067e\u0627\u0634 \u0626\u06d5\u0648\u06cc\u0634 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u0627\u0646 \u0647\u0627\u062a\u0646 \u0648 \u0686\u0648\u0648\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u0627\u062e\u06c6 \u0647\u06d5\u0631 \u0628\u06c6\u06cc\u06d5 \u0647\u0627\u062a\u0648\u06cc\u06d5 \u0644\u0627\u0645\u0627\u0646\u060c \u0644\u0627\u0645\u0627\u0646 \u0628\u062f\u06d5\u06cc \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a\u06cc\u0646\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0642\u0633\u06d5\u0695\u0627\u0633\u062a\u0627\u0646\u06cc\u060c \u0626\u06d5\u0648\u0647\u06d5\u0695\u06d5\u0634\u06d5\u06cc \u0644\u06d5\u0626\u06ce\u0645\u06d5\u062a \u06a9\u0631\u062f\u060c \u0628\u06d5\u062c\u06ce\u06cc \u0628\u06ce\u0646\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u0648\u06cc\u0634 \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce\u061b \u0645\u0646 \u0628\u06d5\u0686\u06cc \u0695\u0627\u0633\u067e\u06ce\u0631\u0627\u0648\u0645\u060c \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u06d5\u0646\u0645\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0648\u06d5\u06a9 \u0645\u0646 \u062f\u06d5\u062a\u0627\u0646\u0628\u06cc\u0646\u0645\u060c \u0626\u06ce\u0648\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u06a9\u0627\u062a\u06ce \u0644\u06d5 \u0644\u0627\u06cc \u0634\u06cc\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u0648\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06cc\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u0628\u0627\u0631\u0627\u0646\u0645\u0627\u0646 \u0628\u06c6\u062f\u06d5\u0628\u0627\u0631\u06ce\u0646\u06ce. \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0647\u06d5\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06c6\u06cc \u0628\u06d5\u067e\u06d5\u0644\u06d5\u0628\u0648\u0646\u061b \u0647\u06d5\u0648\u0631\u06cc \u0628\u0627\u06cc\u06d5\u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u062f\u0627\u0646\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc \u0644\u06d5\u06af\u06d5\u06b5\u062f\u0627\u06cc\u06d5\u061b',
    '\u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u060c \u0647\u06d5\u0645\u0648\u0634\u062a \u062a\u06ce\u06a9 \u0648\u067e\u06ce\u06a9 \u062f\u06d5\u062f\u0627. \u0626\u06cc\u062a\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a \u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u062c\u06ce \u0645\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u062f\u0631\u0627. \u0626\u06ce\u0645\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u06c6\u0645\u06d5\u06b5\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0626\u0627\u0648\u0627 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0644\u06d5\u0648\u0634\u0648\u06ce\u0646\u06d5\u062f\u0627 \u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06ce\u06a9\u0645\u0627\u0646 \u062f\u0627\u0628\u0648\u0646\u06ce\u060c \u06a9\u06d5 \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u0646\u06d5\u062f\u0627\u0648\u06d5. \u06af\u0648\u06ce \u0648 \u0686\u0627\u0648\u0648 \u062f\u06b5\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5\u0648\u0627\u0646\u06cc\u0634 \u062f\u0627\u0628\u0648\u061b \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u06af\u0648\u06ce \u0648 \u0686\u0627\u0648\u0648 \u062f\u06b5\u06cc\u0627\u0646 \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0633 \u0646\u06d5\u06a9\u06d5\u0648\u062a\u061b \u06a9\u06d5 \u0646\u0631\u062e\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6\u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u0627\u0646\u06d5\u062f\u06d5\u0646\u0627\u0648 \u0626\u06d5\u0648\u06d5\u06cc \u062d\u06d5\u0646\u06d5\u06a9\u06cc\u0627\u0646 \u067e\u06ce\u062f\u06d5\u06a9\u0631\u062f\u060c \u062f\u06d5\u0648\u0631\u06cc \u0644\u06ce\u062f\u0627\u0646.',
    '\u0641\u0631\u06d5 \u0644\u06d5\u0648 \u0628\u0627\u0698\u06ce\u0695\u0627\u0646\u06d5\u0645\u0627\u0646 \u0642\u0695 \u062a\u06ce \u062e\u0633\u062a\u0648\u0646 \u06a9\u06d5 \u0647\u06d5\u0631 \u0644\u06d5\u062f\u06d5\u0648\u0631\u06cc \u0626\u06ce\u0648\u06d5\u0628\u0648\u0646. \u0647\u06d5\u0645\u0648\u0648 \u062c\u06c6\u0631\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u0645\u0627\u0646 \u0628\u06c6\u0695\u0627\u0646\u0627\u0648\u0646\u060c \u06a9\u06d5 \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u062f\u06cc \u0628\u06c6\u0686\u06cc \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0627\u0646\u067e\u06d5\u0631\u0633\u062a \u062a\u0627 \u0628\u06d5\u0648\u0647\u06c6\u06cc\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u06cc\u0627\u0646 \u0646\u0632\u06cc\u06a9 \u06a9\u06d5\u0646\u06d5\u0648\u06d5\u060c \u0647\u06cc\u0686 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0647\u0627\u062a\u0646\u061f \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u062f\u06d5\u0633\u06cc\u0627\u0646 \u0686\u0648\u0646\u061b \u0626\u06d5\u0645\u06d5\u0634 \u0644\u06d5\u0648\u062f\u0631\u06c6\u06cc\u0627\u0646\u06d5\u0628\u0648 \u06a9\u06d5 \u0628\u06c6\u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u06cc\u0627\u0646 \u0628\u06d5\u0633\u062a\u0648\u0646.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634 \u0644\u06d5\u062c\u0646\u062f\u06c6\u06a9\u0627\u0646\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5\u0644\u0627\u06cc \u062a\u06c6\u060c \u06a9\u06d5 \u06af\u0648\u06ce \u0644\u06d5\u0645 \u0642\u0648\u0695\u0626\u0627\u0646\u06d5 \u0628\u06af\u0631\u0646\u060c \u06a9\u0627\u062a\u06ce \u0647\u0627\u062a\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0645\u0627\u062a \u0628\u0646 \u0648 \u06af\u0648\u06ce \u0628\u062f\u06d5\u0646. \u0648\u06d5\u062e\u062a\u06ce \u0628\u06cc\u0633\u062a\u06cc\u0627\u0646\u060c \u0647\u0627\u062a\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0648 \u062e\u06ce\u06b5\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0648 \u062a\u0631\u0633\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631\u0646\u0627\u0646.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u06af\u06d5\u0644\u06c6! \u0646\u0627\u0648\u06d5\u0631\u06c6\u06a9\u06cc \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6\u0648\u06d5\u062e\u0648\u06ce\u0646\u062f\u0648\u06cc\u0646\u060c \u06a9\u06d5 \u067e\u0627\u0634 \u0645\u0648\u0648\u0633\u0627 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u061b \u0628\u06d5 \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0634\u06cc \u0628\u0695\u0648\u0627\u0647\u06d5\u06cc\u06d5\u061b \u0647\u0627\u0646\u06d5 \u0628\u06c6\u0695\u0627\u0633\u062a\u06cc \u062f\u06d5\u062f\u0627\u062a \u0648 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627.',
    '\u06af\u06d5\u0644\u06c6! \u0648\u06d5\u0631\u0646 \u0628\u06d5 \u062f\u06d5\u0646\u06af \u0626\u06d5\u0645 \u06a9\u06d5\u0633\u06d5\u0648\u06d5 \u0628\u0686\u0646 \u06a9\u06d5 \u06af\u0627\u0632\u06cc \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u0627 \u0648 \u0628\u0695\u0648\u0627\u06cc \u067e\u06ce\u06a9\u06d5\u0646. \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u0648 \u062e\u06c6\u0634 \u062f\u06d5\u0628\u06ce \u0648 \u0644\u06d5\u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06cc \u0628\u06d5\u0698\u0627\u0646\u062a\u0627\u0646 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u06d5 \u062f\u06d5\u0646\u06af \u0626\u06d5\u0648\u06a9\u06d5\u0633\u06d5\u0648\u06d5 \u0646\u06d5\u0686\u06ce - \u06a9\u06d5 \u0628\u06d5\u0631\u06d5\u0648\u062e\u0648\u062f\u0627 \u0628\u0627\u0646\u06af \u062f\u06d5\u06a9\u0627 - \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06cc \u062f\u06d5\u0633 \u0646\u0627\u06a9\u06d5\u0648\u06ce \u0648 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u062f\u06d5\u0647\u0627\u0646\u0627 \u0646\u0627\u06cc\u06d5. \u06a9\u06ce\u0634 \u0626\u0627\u0648\u0627\u0628\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0695\u06ce \u06cc\u0627\u0646 \u0644\u06ce \u06af\u06c6\u0695\u0627\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0646\u06d5\u06cc\u0627\u0646\u0632\u0627\u0646\u06cc \u0626\u06d5\u0648\u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648 \u0644\u06d5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646\u062f\u0627 \u062a\u0648\u0648\u0634\u06cc \u0646\u0627\u062a\u06d5\u0648\u0627\u0646\u06cc \u0646\u06d5\u0628\u0648\u0648\u060c \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0632\u06cc\u0646\u062f\u0648\u0648\u0634 \u06a9\u0627\u062a\u06d5\u0648\u06d5\u0648 \u0628\u0634 \u0645\u0631\u06ce\u0646\u06ce\u061f \u0626\u0627\u0631\u06ce \u0626\u06d5\u0648 \u0644\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646\u060c \u0644\u0627\u06cc \u0626\u0627\u06af\u0631 \u0695\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0646\u06ce\u0646 [\u0648 \u067e\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u06b5\u06ce\u0646:] \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645 \u0634\u062a\u06d5 \u0695\u0627\u0633\u062a \u0646\u06cc\u06cc\u06d5\u061f \u0626\u06ce\u0698\u0646: \u0695\u0627\u0633\u062a\u06d5\u060c \u0633\u0648\u06ce\u0646\u062f \u0628\u06d5\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646. \u0626\u06ce\u0698\u06ce: \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631\u062d\u0627\u0634\u0627\u062a\u0627\u0646 \u0644\u06ce \u06a9\u0631\u062f\u060c \u0633\u0627 \u0626\u06d5\u0648\u0626\u0627\u0632\u0627\u0631\u06d5 \u0628\u0686\u06ce\u0698\u0646.',
    '\u062a\u06c6\u0634 \u0647\u06d5\u0631\u0648\u06d5\u06a9\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5 \u0644\u06ce \u0628\u0695\u0627\u0648\u0648 \u0646\u06d5\u0628\u06d5\u0632\u06d5\u06a9\u0627\u0646\u060c \u0633\u0648\u0631\u0628\u06d5 \u0644\u06d5 \u0633\u06d5\u0631 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u062a \u0648 \u062e\u06c6\u0695\u0627\u06af\u0631\u0628\u06d5\u061b \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u062e\u06c6\u0695\u0627\u06af\u0631\u0628\u0648\u0646 . \u0628\u06c6\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u067e\u06d5\u0644\u06d5\u062a \u0646\u06d5\u0628\u06ce\u061b \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06cc \u0628\u0695\u06cc\u0627\u0631\u062f\u0631\u0627\u0648\u06d5 \u0628\u06ce\u0646\u06d5\u0644\u0627\u0645\u0627\u0646\u060c \u0648\u0627\u062f\u06d5\u0632\u0627\u0646\u0646 \u0645\u0627\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0633\u0627\u0639\u06d5\u062a\u06ce \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u062e\u0627\u06cc\u0627\u0646\u062f\u0648\u06d5. \u0626\u06d5\u0645\u06d5\u0634 \u067e\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u062a\u06d5\u0648\u0627\u0648\u06d5. \u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce\u06a9 - \u0628\u06ce\u0698\u06af\u06d5 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u06d5\u06a9\u0627\u0646 - \u0628\u06d5\u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u0644\u06d5\u0628\u06d5\u06cc\u0646 \u062f\u06d5\u0686\u06ce\u061f',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0698\u06cc \u062e\u0648\u0627 \u0628\u0648\u0648\u0646 \u0648 \u0644\u06d5\u0645\u067e\u06d5\u0631\u06ce\u06a9 \u0628\u0648\u0648\u0646 \u0644\u06d5 \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627\u060c \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u06cc \u0644\u06ce \u067e\u0648\u0686\u06d5\u06b5 \u06a9\u0631\u062f.',
    '\u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u06a9\u06d5 \u062e\u0648\u0627\u0646\u0627\u0633 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646 \u0648 \u0628\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u0634 - \u06a9\u06d5 \u0695\u0627\u0633\u062a\u06d5 \u0648 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0645\u0648\u062d\u06d5\u0645\u0645\u06d5\u062f \u0628\u06d5 \u062e\u06d5\u06b5\u0627\u062a \u0647\u0627\u062a\u06c6\u062a\u06d5\u062e\u0648\u0627\u0631\u06ce - \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u06cc \u067e\u06c6\u0634\u06cc \u0648 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06cc \u0698\u06cc\u0627\u0646\u06cc\u0627\u0646\u06cc \u0628\u06c6 \u0633\u0627\u0645\u0627\u0646 \u062f\u0627\u0646.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u062f\u0698\u0627\u06cc\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f\u060c \u0634\u0648\u06ce\u0646 \u06a9\u0627\u0631\u06cc \u0646\u0627\u0695\u06d5\u0648\u0627 \u06a9\u06d5\u0648\u062a\u0646. \u0626\u06d5\u0648\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0634\u0646 \u0644\u06d5\u062f\u0648\u06cc \u0626\u06d5\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u06d5 \u062f\u06d5\u0686\u0646\u061b \u06a9\u06d5 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646\u0695\u0627\u06cc\u06d5. \u0626\u0627\u0648\u0627 \u062e\u0648\u062f\u0627 \u0686\u06d5\u0646\u062f \u0646\u0645\u0648\u0648\u0646\u06d5 \u0628\u06c6 \u0626\u06d5\u0645 \u062e\u06d5\u06b5\u06a9\u06d5 \u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06cc\u062a\u0631 \u0647\u06d5\u0631\u06af\u0627 \u062a\u0648\u0648\u0634\u06cc \u0626\u06d5\u0648 \u0628\u06ce \u062f\u06cc\u0646\u0627\u0646\u06d5 \u0628\u0648\u0648\u0646\u060c \u0644\u06d5 \u0645\u0644\u06cc\u0627\u0646 \u062f\u06d5\u0646\u061b \u067e\u0627\u0634 \u0648\u0646\u062c\u0695 \u0648\u0646\u062c\u0695 \u06a9\u0631\u062f\u0646\u06cc\u0627\u0646 \u0634\u06d5\u062a\u06d5\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0646. \u0626\u06d5\u0648\u0633\u0627 \u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u0626\u0627\u0632\u0627\u062f\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646\u061b \u06cc\u0627 \u0628\u06d5\u0631\u062e\u0648\u06ce\u0646\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0633\u062a\u06ce\u0646\u0646\u061b \u0647\u06d5\u062a\u0627 \u0634\u06d5\u0695 \u06a9\u06c6\u062a\u0627\u06cc\u06cc \u067e\u06ce \u062f\u06ce. \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u06cc\u0648\u06cc\u0633\u062a\u0628\u0627\u060c \u0628\u06c6 \u062e\u06c6\u06cc \u062a\u06c6\u06b5\u06d5\u06cc \u0644\u06ce \u062f\u06d5\u0633\u062a\u0627\u0646\u062f\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0648\u06cc\u0633\u062a\u06cc \u0628\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u062a\u0627\u0646 \u0628\u0626\u06d5\u0632\u0645\u0648\u06ce. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u06a9\u0648\u0698\u0631\u0627\u0648\u0646\u060c \u06a9\u0631\u062f\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u0641\u06cc\u0695\u06c6 \u0646\u0627\u062f\u0627.',
    '\u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u0695\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5\u062f\u0627 \u0648 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06cc \u0698\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u062a\u06d5\u06a9\u0648\u0648\u0632 \u062f\u06d5\u06a9\u0627.',
    '\u062f\u06d5\u0634\u06cc\u0627\u0646\u0628\u0627\u062a\u06d5 \u0626\u06d5\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u0634\u06d5\u06a9\u06cc \u0628\u06c6\u06cc \u0628\u0627\u0633 \u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u06cc\u0627\u0631\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646\u060c \u062e\u0648\u062f\u0627\u0634 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062a\u0627\u0646 \u062f\u06d5\u062f\u0627 \u0648 \u0644\u06d5 \u0628\u06d5\u0631 \u0646\u06d5\u06cc\u0627\u0631\u0627\u0646 \u0646\u0627\u0628\u06d5\u0632\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u06cc\u0646 \u062f\u0648\u0698\u0645\u0646\u0646\u060c \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0646\u06d5\u06af\u0628\u06d5\u062a\u06cc \u062f\u06d5\u0628\u06ce \u0648 \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0644\u06ce \u067e\u0648\u0686 \u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0645\u06d5\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u060c \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u0648\u06cc\u0634 \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0644\u06ce \u062a\u06ce\u06a9 \u062f\u0627\u0648\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0646\u06d5\u06af\u06d5\u0695\u0627\u0648\u0646\u060c \u062a\u0627 \u0628\u0695\u0648\u0627\u0646\u0646 \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0686\u06cc\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u061f \u062e\u0648\u062f\u0627 \u062a\u06d5\u0631\u062a \u0648 \u062a\u0648\u0646\u0627\u06cc \u06a9\u0631\u062f\u0646\u061b \u0628\u06c6 \u0644\u06d5 \u062f\u06cc\u0646 \u0644\u0627\u062f\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0626\u0627\u0648\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0633\u06d5\u0631\u0648\u06d5\u0631\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0628\u06ce \u0633\u06d5\u0631\u0648\u06d5\u0631\u0646.',
    '\u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06cc \u0626\u0627\u06a9\u0627\u0631\u067e\u06d5\u0633\u0646\u062f\u060c \u062f\u06d5\u0646\u06ce\u0631\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u06d5\u0634\u06cc\u0627\u0646 \u0646\u0627\u0633\u06cc\u0648\u06d5\u060c \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0695\u0627\u06cc \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646 \u0648 \u062f\u06d5\u0644\u06d5\u0648\u06d5\u0695\u06ce\u0646\u061b \u0648\u06d5\u06a9 \u0644\u06d5\u0648\u06d5\u0695\u0627\u0646\u06cc \u0626\u0627\u0698\u06d5\u06b5 \u0648 \u062c\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u0627\u06af\u0631\u062f\u0627\u06cc\u06d5.',
    '\u0641\u0631\u06d5 \u0628\u0627\u0698\u06ce\u0695\u06cc \u0648\u0627 \u0647\u06d5\u0628\u0648\u0648\u06d5 \u0644\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u0628\u0627\u0698\u06ce\u0695\u06d5\u06a9\u06d5\u06cc \u062a\u06c6 - \u0626\u06d5\u0648\u06d5\u06cc \u062a\u06c6\u06cc\u0627\u0646 \u0644\u06ce \u0648\u06d5\u062f\u06d5\u0631\u0646\u0627 - \u06af\u06d5\u06b5\u06ce\u06a9 \u0628\u06d5\u0647\u06ce\u0632 \u0648 \u06af\u0648\u0695\u062a\u0631 \u0628\u0648\u0648\u0646\u060c \u0642\u0695\u0645\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0647\u0627\u062a.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u06d5\u0648\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0695\u0648\u0648\u0646\u06cc \u0628\u06c6 \u0647\u0627\u062a\u0628\u06ce\u060c \u0648\u06d5\u06a9 \u06a9\u06d5\u0633\u06ce\u06a9\u06d5 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u062e\u0631\u0627\u067e\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u062e\u06d5\u0645\u06b5\u0627\u0648\u06d5 \u0648 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u061f',
    '\u0626\u06d5\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u06cc \u062e\u0648\u0627 \u0628\u06d5 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u0627\u0646\u06cc \u0628\u06d5\u06b5\u06ce\u0646 \u062f\u0627\u0648\u06d5\u060c \u0686\u06c6\u0646 \u0686\u06c6\u0646\u06cc\u06cc\u06d5\u061f \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0695\u0648\u0628\u0627\u0631\u06cc \u0648\u0627\u06cc \u062a\u06ce\u062f\u0627\u060c \u0626\u0627\u0648\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0634\u0644\u0648\u06ce \u0646\u0627\u0628\u06ce. \u0686\u06d5\u0646\u062f \u0695\u0648\u0628\u0627\u0631\u06cc \u0634\u06cc\u0631\u06cc \u062a\u06ce\u062f\u0627\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062a\u0627\u0645\u06cc \u0646\u06d5\u06af\u06c6\u0695\u0627\u0648\u06d5. \u0686\u06d5\u0646\u062f\u06cc\u0646 \u0695\u0648\u0628\u0627\u0631\u06cc \u0634\u06d5\u0631\u0627\u0628\u06cc \u067e\u06ce\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627\u060c \u062a\u0627 \u0648\u06d5\u06cc\u062e\u06c6\u0646 \u06a9\u06d5\u06cc\u0641\u06cc \u067e\u06ce\u062f\u06d5\u06a9\u06d5\u0646. \u0686\u06d5\u0646\u062f \u0695\u0648\u0628\u0627\u0631\u06cc\u0634 \u0644\u06d5 \u0647\u06d5\u0646\u06af\u06a4\u06cc \u067e\u0627\u06b5\u0627\u0641\u062a\u06d5 \u0647\u06d5\u0646. \u0647\u06d5\u0631 \u0645\u06cc\u0648\u06d5\u06cc\u06d5\u06a9\u06cc\u0634 \u0628\u062e\u0648\u0627\u0632\u0646\u060c \u062a\u06ce\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u061b \u062e\u06c6\u0634 \u0628\u0648\u0648\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0634\u06cc\u0627\u0646. \u0686\u06c6\u0646 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u06d5\u0628\u0646\u06d5 \u0644\u0646\u06af\u06d5\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u06a9\u06d5 \u062a\u0627 \u0647\u06d5\u062a\u0627 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u0626\u0627\u06af\u0631\u062f\u0627 \u0628\u0646 \u0648 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u06d5 \u0626\u0627\u06a4\u0627\u06a9\u06d5\u0644\u0627\u0646\u062f\u06cc \u0648\u06d5\u062e\u06c6\u0646 \u0648 \u0695\u06cc\u062e\u06c6\u06b5\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u062a \u0648 \u067e\u06d5\u062a \u06a9\u0627.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06ce\u0646 \u06af\u0648\u06ce\u062a \u0644\u06ce\u062f\u06d5\u06af\u0631\u0646\u061b \u0648\u06d5\u062e\u062a\u06ce \u0644\u06d5 \u0644\u0627\u06cc \u062a\u06c6 \u062f\u06d5\u0631\u062f\u06d5\u0686\u0646 \u0644\u06d5 \u0632\u0627\u0646\u06cc\u0627\u0631\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u067e\u0631\u0633\u0646: \u0626\u06ce\u0633\u062a\u06d5 \u0686\u06cc \u0648\u062a\u061f \u062e\u0648\u062f\u0627 \u0645\u06c6\u0631\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06b5\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0648\u06d5 \u0648 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0647\u0627\u062a\u0648\u0648\u0646\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5 \u0695\u06ce\u060c \u067e\u062a\u0631 \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0648 \u062a\u0631\u0633\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u067e\u062a\u0631 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u062f\u06b5\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0646 \u06af\u0648\u0632\u0648\u06af\u0648\u0645\u0645\u06d5\u062a \u0626\u06ce\u0633\u062a\u06d5 \u0633\u06d5\u06b5\u0627\u06cc\u0627\u0646 \u0644\u06ce\u0695\u0627 \u0628\u06ce\u061f \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0633\u062a\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06d5\u06cc\u062f\u0627 \u0628\u0648\u0648\u0646\u061b \u0648\u06d5\u062e\u062a\u06ce \u0633\u06d5\u06b5\u0627\u0634\u06cc\u0627\u0646 \u0644\u06ce\u0695\u0627\u0628\u0648\u0648\u060c \u067e\u06d5\u0646\u062f\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0633\u0648\u0648\u062f\u06cc \u0686\u06cc\u06cc\u06d5\u061f',
    '\u0633\u0627 \u0628\u0632\u0627\u0646\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0628\u06ce. \u0628\u06c6 \u0647\u06d5\u06b5\u06d5\u06cc \u062e\u06c6\u062a \u0648 \u067e\u06cc\u0627\u0648\u0627\u0646 \u0648 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u060c \u062f\u0627\u0648\u0627\u06cc \u0644\u06ce \u062e\u06c6\u0634 \u0628\u0648\u0648\u0646\u06ce \u0628\u06a9\u06d5. \u062e\u0648\u0627 \u0644\u06d5 \u0647\u06d5\u06b5\u06af\u06ce\u0695 \u0648\u06d5\u0631\u06af\u06ce\u0695\u062a\u0627\u0646 \u0648 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u0647\u06d5\u0645\u06cc\u0634\u06d5\u06cc\u06cc\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0626\u06ce\u0698\u0646: \u0628\u06c6 \u0686\u06d5 \u0633\u0648\u0648\u0631\u06d5\u062a\u06ce\u06a9 \u0646\u0627\u06cc\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u061f \u0626\u06d5\u0645\u062c\u0627\u0631\u06d5 \u06a9\u06d5 \u0633\u0648\u0648\u0631\u06d5\u062a\u06ce\u06a9\u06cc \u067e\u062a\u06d5\u0648 \u0647\u0627\u062a \u0648 \u0644\u06d5\u0645\u06d5\u0695 \u062e\u06d5\u0632\u0627\u0648\u06d5 \u0628\u0627\u0633\u06cc \u06a9\u0631\u062f\u060c \u062a\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u063a\u06d5\u0632\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0648\u0627\u062a \u0644\u06ce \u062f\u06d5\u0695\u0648\u0627\u0646\u0646\u060c \u0648\u06d5\u06a9 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u062a\u0631\u0633\u06cc \u0645\u0631\u062f\u0646 \u062a\u0648\u0648\u0634\u06cc \u0641\u06ce\u062f\u0627\u0631\u06cc \u0647\u0627\u062a\u0628\u06ce. \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627 \u0628\u0627\u0634\u062a\u0631\u06d5.',
    '[\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5] \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0648 \u067e\u06d5\u06cc\u06a4\u06cc\u0646\u06cc \u0628\u06d5\u062c\u06ce \u0648 \u0695\u06ce\u06cc\u06d5 [\u0647\u06ce\u0698\u0627\u062a\u0631\u06d5]. \u0647\u06d5\u0631\u06af\u0627\u0634 \u062e\u06d5\u0632\u0627 \u0628\u0695\u06cc\u0627\u0631 \u062f\u0631\u0627\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0695\u0627\u0633\u062a \u0628\u0646\u060c \u0647\u06d5\u0631 \u0628\u06d5 \u0642\u0627\u0632\u0627\u0646\u062c\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u0648\u0648 \u0648\u06d5\u062f\u06d5\u0633 \u06a9\u06d5\u0648\u062a\u060c \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u062e\u0631\u0627\u067e\u06d5 \u0695\u06ce\u0648\u06d5 \u062f\u06d5\u0628\u06d5\u0646 \u0648 \u067e\u06d5\u06cc\u0648\u06d5\u0646\u062f\u06cc \u062e\u0632\u0645\u0627\u06cc\u06d5\u062a\u06cc\u062a\u0627\u0646 \u062f\u06d5\u0686\u0695\u06ce\u0646\u0646\u061f',
    '\u0633\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0631 \u0646\u06d5\u062d\u0644\u06d5\u062a\u06cc \u062e\u0633\u062a\u0648\u0648\u0646\u061b \u06af\u0648\u06ce\u06cc\u0627\u0646 \u06a9\u06d5\u0695 \u0648 \u0686\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc \u0644\u06ce \u06a9\u06c6\u0631 \u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0648 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0695\u0627\u0646\u0627\u0645\u06ce\u0646\u0646\u060c \u06cc\u0627\u0646 \u062f\u06b5\u06cc\u0627\u0646 \u06af\u0627\u06b5\u06d5 \u062f\u0631\u0627\u0648\u06d5\u061f',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u067e\u0627\u0634 \u0644\u06d5 \u062f\u06cc\u0646 \u0634\u0627\u0631\u06d5\u0632\u0627 \u0628\u0648\u0648\u0646 \u0648\u06d5\u0631\u06af\u06d5\u0695\u0627\u0648\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646 \u062f\u0646\u06d5\u06cc \u062f\u0627\u0648\u0646 \u0648 \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06c6 \u062e\u06d5\u0645\u06b5\u0627\u0646\u062f\u0648\u0648\u0646.',
    '\u0686\u0648\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062d\u06d5\u0632\u06cc\u0627\u0646 \u0644\u06d5\u0648 \u0634\u062a\u06d5 \u0646\u06cc\u06cc\u06d5 \u06a9\u06d5 \u062e\u0648\u0627 \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce\u060c \u0648\u062a\u0648\u0648\u06cc\u0627\u0646\u06d5: \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0628\u0627\u0632\u06d5 \u06a9\u0627\u0631\u06ce\u06a9\u062f\u0627 \u0628\u06d5 \u0642\u0633\u06d5\u0648 \u062f\u06d5\u06a9\u06d5\u06cc\u0646. \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5\u0648\u06d5\u06cc \u062f\u06d5\u06cc\u0634\u0627\u0631\u0646\u06d5\u0648\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u062f\u06d5\u0628\u06ce \u0686\u06c6\u0646 \u0628\u0646 \u0626\u06d5\u0648 \u0633\u06d5\u0631\u062f\u06d5\u0645\u06d5\u06cc \u0641\u0631\u0634\u062a\u06d5 \u06af\u06cc\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06ce\u0634\u06ce \u0648 \u062f\u06d5 \u0695\u0648\u062e\u0633\u0627\u0631 \u0648 \u067e\u0634\u062a\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0633\u0631\u06d5\u0648\u06ce\u0646\u06ce\u061f',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0648\u06d5\u0634\u0648\u06ce\u0646 \u0634\u062a\u06ce\u06a9\u06cc \u0648\u0627 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc \u062a\u0648\u0648\u0695\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0628\u06d5 \u0695\u06d5\u0632\u0627\u06cc \u0626\u06d5\u0648 \u0695\u0627\u0632\u06cc \u0646\u06d5\u0628\u0648\u0648\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u0648\u06cc\u0634 \u06a9\u0627\u0631\u06cc\u0627\u0646\u06cc \u0644\u06ce \u067e\u0648\u0686 \u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u063a\u06d5\u0631\u06d5\u0632\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u06b5\u062f\u0627\u06cc\u06d5\u060c \u0644\u0627\u06cc\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u062e\u0648\u062f\u0627 \u0695\u06a9 \u0648 \u06a9\u06cc\u0646\u06d5\u06cc\u0627\u0646 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0646\u0627\u06a9\u0627\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632\u0645\u0627\u0646 \u0628\u06a9\u0631\u062f\u0627\u06cc\u06d5\u060c \u062f\u06d5\u0645\u0627\u0646\u0647\u06ce\u0646\u0627\u0646\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u062a \u0648 \u0626\u06cc\u062a\u0631 \u0647\u06d5\u0631 \u0628\u06d5 \u0648\u0631\u062f \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0644\u06d5 \u0628\u06cc\u0686\u0645\u06cc\u0627\u0646\u060c \u0628\u0627\u0634 \u062f\u06d5\u0648\u0646\u0627\u0633\u06cc\u0646. \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0628\u06d5 \u0695\u0627\u0648\u06ce\u0698\u06cc \u0626\u0627\u062e\u0627\u0641\u062a\u0646\u06cc\u0634 \u062f\u06d5\u06cc\u0627\u0646\u0646\u0627\u0633\u06ce. \u062e\u0648\u0627\u0634 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646\u06cc \u0626\u0627\u06af\u0627 \u0644\u06ce\u06cc\u06d5.',
    '\u0628\u06d5 \u062a\u0627\u0642\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5 \u062a\u0627 \u0628\u0632\u0627\u0646\u06cc\u0646\u060c \u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062a\u06ce\u06a9\u06c6\u0634\u0627\u0648\u06d5 \u0648 \u06a9\u06ce\u062a\u0627\u0646 \u062e\u06c6\u0695\u0627\u06af\u0631\u062a\u0631 \u0628\u0648\u0648\u06d5. \u0628\u06d5\u0631\u0627\u0648\u0631\u062f\u06cc \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u06cc \u0626\u06ce\u0648\u06d5\u0634 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646 \u0648 \u0628\u0648\u0648\u0646\u06d5 \u0644\u06d5\u0645\u067e\u06d5\u0631 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5 \u0632\u0627\u0646\u06cc\u0628\u0648\u0648\u0634\u06cc\u0627\u0646 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0632\u06af\u0627\u0631 \u0628\u0648\u0648\u0646 \u06a9\u0627\u0645\u06d5\u06cc\u06d5\u060c \u0647\u06d5\u0631 \u062f\u0698\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648\u06d5\u0633\u062a\u0627\u0646\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u062a\u0627\u0646\u0646 \u0632\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0628\u06af\u06d5\u06cc\u06d5\u0646\u0646 \u0648 \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u067e\u0648\u0686\u06d5\u06b5 \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062f\u06d5\u0631 \u0645\u06d5\u0686\u0646 \u0648 \u06a9\u0627\u0631\u062a\u0627\u0646 \u067e\u0648\u0686\u06d5\u06b5 \u0645\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u06cc\u0646 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627 \u0648 \u0628\u0648\u0648\u0646\u06d5 \u0644\u06d5\u0645\u067e\u06d5\u0631 \u0644\u06d5\u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0648 \u062f\u0648\u0627\u06cc\u06cc \u0628\u06d5\u0628\u06ce \u062f\u06cc\u0646\u06cc \u0645\u0631\u062f\u0646\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062e\u0648\u0627 \u0644\u06ce\u06cc\u0627\u0646 \u062e\u06c6\u0634 \u0646\u0627\u0628\u06ce.',
    '\u0633\u0633 \u0645\u06d5\u0628\u0646\u06d5\u0648\u06d5 \u0648 \u062f\u0627\u0648\u0627\u06cc \u0634\u06d5\u0695\u0648\u06cc\u0633\u062a\u0627\u0646\u06ce \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0647\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627\u0646\u061b \u0626\u06ce\u0648\u06d5 \u062e\u0648\u062f\u0627\u062a\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5\u06d5 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06d5\u0628\u0627\u062a\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u0644\u06ce \u06a9\u06d5\u0645 \u0646\u0627\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0698\u06cc\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5\u060c \u0647\u06d5\u0645\u0648\u0648 \u0647\u06d5\u0631 \u06af\u0627\u06b5\u062a\u06d5 \u0648 \u06af\u06d5\u067e\u06ce\u06a9\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627\u0648 \u0647\u06d5\u0628\u06ce \u0648 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632 \u0628\u0646\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u062f\u0627\u062a\u06ce \u0648 \u0647\u06cc\u0686 \u0645\u0627\u06b5\u06cc\u0634\u062a\u0627\u0646 \u0644\u06ce \u0646\u0627\u062e\u0648\u0627\u0632\u06ce.',
    '\u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u0632\u06c6\u0631 \u0628\u06d5 \u0642\u06cc\u0631\u0633\u06cc\u0686\u0645\u06d5\u06cc\u06cc\u0634 \u062f\u0627\u0648\u0627\u06cc \u0645\u0627\u06b5\u06ce\u06a9\u062a\u0627\u0646 \u0644\u06ce \u0628\u06a9\u0627\u060c \u0695\u0698\u062f\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0695\u06a9\u0648\u0648\u0646\u06cc\u062a\u0627\u0646 \u062f\u06d5\u0631\u062f\u06d5\u062e\u0627.',
    '\u0628\u0627 \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0646\u061b \u0628\u0627\u0646\u06af\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u0645\u0627\u06b5 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u0627 \u0628\u06d5\u062e\u062a \u06a9\u06d5\u0646. \u0626\u06cc \u0648\u0627\u0634\u062a\u0627\u0646 \u062f\u06d5\u0646\u0627\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0695\u0698\u062f\u06cc \u062f\u06d5\u06a9\u0627\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0695\u0698\u062f\u06cc \u062f\u06d5\u06a9\u0627\u060c \u0695\u0698\u062f\u06cc \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u0627. \u062e\u0648\u0627 \u0644\u06d5 \u06af\u0634 \u06a9\u06d5\u0633 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5\u061b \u0626\u06ce\u0648\u06d5 \u0698\u0627\u0631 \u0648 \u0628\u06d5\u0644\u06d5\u0646\u06af\u0627\u0632\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u067e\u0634\u062a\u06cc\u0634\u06cc \u062a\u06ce \u0628\u06a9\u06d5\u0646\u060c \u0686\u06cc\u0646\u06ce\u06a9\u06cc \u062a\u0631 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06af\u06c6\u0695\u06ce\u062a\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u0686\u0646.',
    '\u0626\u06ce\u0645\u06d5\u06cc\u0646 \u06a9\u06d5 \u0648\u06d5\u0633\u06d5\u0631\u0645\u0627\u0646 \u062e\u0633\u062a\u06cc\u060c \u0628\u06d5\u0648 \u0633\u06d5\u0631\u062e\u0633\u062a\u0646\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u06d5.',
    '\u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u06b5\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u062a \u0648 \u0644\u06d5\u0648 \u0647\u06d5\u06b5\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0648\u06d5\u0628\u06d5\u0648\u0644\u0627\u0648\u06d5 \u062a\u0648\u0648\u0634\u06cc\u0627\u0646 \u062f\u06ce\u06cc\u060c \u0639\u0627\u0641\u0648\u0648\u062a \u0628\u06a9\u0627 \u0648 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u0648\u0648\u0631 \u0644\u06d5 \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u06a9\u0627 \u0648 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u062a \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u0627.',
    '\u062a\u0627 \u062e\u0648\u062f\u0627 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc\u062a \u0628\u062f\u0627\u060c \u0628\u06d5\u062a\u06d5\u0631\u0632\u06ce \u0648\u0627 \u06a9\u06d5 \u0628\u0628\u06cc\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0626\u06c6\u0642\u0631\u06d5 \u0648 \u0647\u06d5\u062f\u0627\u06cc \u062f\u0627 \u0628\u06d5 \u062f\u06b5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646\u061b \u062a\u0627 \u0648\u06ce\u0695\u0627\u06cc \u0628\u0695\u0648\u0627\u06cc \u0647\u06d5\u06cc\u0627\u0646\u06d5\u060c \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0642\u06d5\u0648\u06d5\u062a\u062a\u0631 \u0628\u06ce. \u0644\u06d5\u0634\u06a9\u0631\u06af\u06d5\u0644\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc\u0634\u060c \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627\u0646 \u0648 \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u067e\u06cc\u0627\u0648 \u0648 \u0698\u0646\u0627\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0628\u06d5\u0695\u06ce \u06a9\u0627\u062a\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u062a\u0627\u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646 \u0648 \u062e\u06d5\u062a\u0627 \u0648 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0639\u0627\u0641\u0648\u0648 \u0628\u06a9\u0627. \u0644\u062c\u06d5\u0645 \u062e\u0648\u062f\u0627\u060c \u0626\u06d5\u0645 \u0695\u0648\u0648\u062f\u0627\u0648\u06d5 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06ce\u06a9\u06cc \u0645\u06d5\u0632\u0646 \u0628\u0648\u0648.',
    '\u0647\u06d5\u062a\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u0695\u0648\u0648\u0628\u06cc\u0646\u0627\u0646\u06cc \u067e\u06cc\u0627\u0648 \u0648 \u0698\u0646 \u0648 \u0634\u06d5\u0631\u06cc\u06a9 \u0628\u06c6 \u062e\u0648\u062f\u0627\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646\u060c \u0646\u06ce\u0695 \u0648 \u0645\u06ce\u0648\u06cc\u0627\u0646 - \u06a9\u06d5 \u0633\u06d5\u0628\u0627 \u0628\u06d5 \u062e\u0648\u0627 \u06af\u0648\u0645\u0627\u0646\u06cc \u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0631\u062f - \u0626\u0627\u0632\u0627\u0631 \u0628\u062f\u0627. \u0632\u06c6\u0631\u062e\u0631\u0627\u067e\u06cc\u0627\u0646 \u0644\u06ce \u0642\u06d5\u0648\u0645\u0627\u0648\u06d5 \u0648 \u062e\u0648\u0627 \u063a\u06d5\u0632\u06d5\u0628\u06cc \u0644\u06ce \u06af\u0631\u062a\u0648\u0648\u0646 \u0648 \u0646\u06d5\u062d\u0644\u06d5\u062a \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0648 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u0646 \u0648 \u062f\u0648\u0627\u0695\u06c6\u0698\u06ce\u06a9\u06cc \u0695\u06d5\u0634\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5\u0634\u06a9\u0631\u06d5\u06a9\u0627\u0646 - \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 - \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627\u0646 \u0648 \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u06a9\u0644 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0648 \u0645\u0648\u0698\u062f\u06d5\u062f\u06d5\u0631 \u0648 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0628\u06cc.',
    '\u0626\u06d5\u0634\u06ce \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0644\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062f\u0631\u06ce\u063a \u0646\u06d5\u06a9\u06d5\u0646 \u0648 \u0695\u06ce\u0632\u06cc \u0634\u0627\u06cc\u0627\u0646\u06cc \u0644\u06ce\u06af\u0631\u0646 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646 \u067e\u06d5\u0633\u0646\u06cc \u0628\u062f\u06d5\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u06d5\u06cc\u0645\u0627\u0646 \u0644\u06d5\u06af\u06d5\u06b5 \u062a\u06c6 \u062f\u06d5\u0628\u06d5\u0633\u062a\u0646\u060c \u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u0627 \u062f\u06d5\u0628\u06d5\u0633\u062a\u0646. \u062f\u06d5\u0633\u062a\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0633\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06cc \u0626\u0627\u0648\u06d5\u0646\u0695\u0627\u06cc\u06d5. \u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc \u0628\u06d5\u062c\u06ce \u0646\u06d5\u0647\u06ce\u0646\u06ce\u060c \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u0634\u06a9\u06ce\u0646\u06cc \u0632\u06d5\u0631\u06d5\u0631 \u062f\u06d5\u06a9\u0627\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0626\u06d5\u0648 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06d5\u06cc - \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 \u0628\u06d5\u0633\u062a\u0648\u0648\u06cc\u06d5 - \u0628\u06d5\u062c\u06ce \u0628\u06ce\u0646\u06ce\u060c \u0644\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u06af\u06d5\u0648\u0695\u06d5\u06cc \u062f\u06d5\u062f\u0627\u062a\u06ce.',
    '\u0626\u06d5\u0648 \u0639\u06d5\u0631\u06d5\u0628\u06d5 \u06a9\u06c6\u0686\u06d5\u0631\u0627\u0646\u06d5\u06cc \u062f\u0648\u0627\u06a9\u06d5\u0648\u062a\u0628\u0648\u0648\u0646\u060c \u0648\u06d5 \u062a\u06c6 \u0626\u06ce\u0698\u0646: \u062f\u0627\u0631\u0627\u06cc\u06cc \u0648 \u0645\u0627\u06b5 \u0648 \u062e\u06ce\u0632\u0627\u0646\u0645\u0627\u0646 \u0646\u06d5\u06cc\u0627\u0646\u0647\u06ce\u0634\u062a \u0628\u06af\u06d5\u06cc\u0646\u06d5 \u0626\u06ce\u0648\u06d5\u061b \u062f\u0627\u0648\u0627\u06cc \u0628\u06d5\u062e\u0634\u06cc\u0646\u0645\u0627\u0646 \u0628\u06c6 \u0628\u06a9\u06d5. \u0633\u06d5\u0631\u0632\u0627\u0631\u06d5\u06a9\u06cc \u0634\u062a\u06ce \u0626\u06ce\u0698\u0646: \u06a9\u06d5 \u0644\u06d5 \u062f\u06b5\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0646\u0627\u06cc\u06d5. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u0632\u06cc\u0627\u0646\u06ce\u06a9 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u060c \u06cc\u0627\u0646 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u067e\u06ce \u0628\u062f\u0627\u060c \u06a9\u06ce \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u062f\u06d5\u0633 \u062f\u06d5 \u06a9\u0627\u0631\u06cc \u062e\u0648\u062f\u0627 \u0628\u062f\u0627\u061f \u0628\u06d5\u06b5\u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0646\u06d5\u062e\u06ce\u0631 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0648\u0627 \u0628\u0648\u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062a\u0627\u0632\u06d5 \u0646\u0627\u06cc\u06d5\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0648 \u0645\u0627\u06b5 \u0648 \u062e\u06ce\u0632\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646. \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u06b5\u06cc\u0634 \u0626\u06d5\u0645 \u062e\u06d5\u06cc\u0627\u06b5\u06d5\u0648 \u0628\u06d5 \u0634\u062a\u06ce\u06a9\u06cc \u0628\u0627\u0634 \u062f\u06d5\u0632\u0627\u0646\u06cc. \u06af\u0648\u0645\u0627\u0646\u06ce\u06a9\u06cc \u0628\u06ce\u062c\u0627\u0648 \u062f\u06d5\u0628\u0631\u062f\u061b \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0648\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0628\u06ce \u062e\u06ce\u0631 \u0628\u0648\u0648\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u0695\u0648\u0627\u06cc \u0646\u06d5\u0628\u06ce \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u060c \u0626\u06ce\u0645\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u0628\u06ce \u062f\u06cc\u0646\u0627\u0646\u06d5\u060c \u06a9\u06b5\u06cc \u0626\u0627\u06af\u0631\u0645\u0627\u0646 \u0646\u06ce\u06b5 \u062f\u0627\u0648\u06d5.',
    '\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0628\u06d5 \u062f\u06d5\u0633 \u062e\u0648\u0627\u06cc\u06d5\u061b \u06a9\u06ce \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u0639\u06d5\u0641\u0648\u0648\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u06a9\u06ce\u0634 \u062d\u06d5\u0632 \u06a9\u0627 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062f\u06d5\u062f\u0627 \u0648 \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0648\u0627 \u06a9\u06d5\u0648\u062a\u0628\u0648\u0648\u0646\u060c \u0626\u06ce\u0633\u062a\u0627 \u0626\u06ce\u0698\u0646: \u0647\u06d5\u0631\u06af\u0627 \u0628\u06d5\u0631\u06d5\u0648 \u062a\u0627\u06b5\u0627\u0646\u06ce\u06a9 \u0686\u0648\u0648\u0646\u060c \u0644\u06ce\u0645\u0627\u0646 \u06af\u06d5\u0695\u06ce\u0646 \u0626\u06ce\u0645\u06d5\u0634 \u0628\u06d5\u0634\u0648\u06ce\u0646 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0628\u06ce\u06cc\u0646. \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u0642\u0633\u06d5\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06af\u06c6\u0695\u0646. \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0634\u0648\u06ce\u0646 \u0626\u06ce\u0645\u06d5 \u0646\u0627\u06cc\u06d5\u0646\u061b \u062e\u0648\u062f\u0627 \u067e\u06ce\u0634\u06d5\u06a9\u06cc \u06af\u0648\u062a\u0648\u0648\u06cc\u06d5. \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0698\u0646: \u0646\u06d5\u062e\u06ce\u0631 \u062f\u06cc\u0627\u0631\u06d5 \u0644\u06d5\u062d\u0627\u0646\u062f \u0626\u06ce\u0645\u06d5 \u0686\u0627\u0648\u0686\u0646\u06c6\u06a9\u0646. \u0646\u06d5\u062e\u06ce\u0631 \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06d5\u0645 \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646.',
    '\u0628\u06d5\u0648 \u0639\u06d5\u0631\u06d5\u0628\u06d5 \u06a9\u06c6\u0686\u06d5\u0631\u0627\u0646\u06d5\u06cc - \u06a9\u06d5 \u067e\u06ce\u0634\u0648\u0648 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u0632\u06cc\u06d5\u0648\u06d5 - \u0628\u06ce\u0698\u06d5: \u0647\u06d5\u0631 \u0644\u06d5\u0645 \u0628\u06d5\u06cc\u0646\u0627\u0646\u06d5\u062f\u0627 \u0628\u0627\u0646\u06af\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u0686\u0646\u06d5 \u0633\u06d5\u0631 \u06af\u06d5\u0644\u06ce\u06a9\u06cc \u06af\u06d5\u0695\u0646\u0627\u0633\u061b \u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0634\u06d5\u0695\u062a\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u06cc\u0627\u0646 \u062e\u06c6 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u0648\u06d5 \u062f\u06d5\u062f\u06d5\u0646. \u062c\u0627 \u0626\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u0646\u060c \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u0628\u0627\u0634 \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u0634\u062a\u0627\u0646 \u0628\u062f\u0632\u0646\u06d5\u0648\u06d5 - \u0648\u06d5\u06a9 \u067e\u06ce\u0634\u0648\u0648 \u062e\u06c6\u062a\u0627\u0646 \u062f\u0632\u06cc\u06d5\u0648\u06d5 - \u062f\u06d5\u062a\u0627\u0646\u062e\u0627\u062a\u06d5 \u0628\u06d5\u0631 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u06d5\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646.',
    '\u06af\u0627\u0632\u0646\u062f\u06d5 \u0644\u06d5 \u06a9\u06c6\u0631 \u0646\u0627\u06a9\u0631\u06ce\u061b \u06af\u0644\u06d5\u06cc\u06cc\u0634 \u0644\u06d5\u0633\u06d5\u0631 \u0634\u06d5\u0644 \u0646\u06cc\u06cc\u06d5\u061b \u0644\u06d5\u0634 \u0628\u06d5\u0628\u0627\u0631\u06cc\u0634 \u0642\u06d5\u06cc\u062f\u06ce \u0646\u0627\u06a9\u0627. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u06d5\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0626\u06d5\u0648 \u0628\u06a9\u0627\u060c \u062f\u06d5\u06cc\u0646\u06ce\u0631\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u06c6 \u0628\u0628\u0648\u06ce\u0631\u06ce\u060c \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc \u062f\u06d5\u062f\u0627.',
    '\u0626\u06d5\u0648 \u062f\u06d5\u0633\u062a\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06cc \u06a9\u06d5 \u0648\u0627 \u0644\u06d5\u0698\u06ce\u0631 \u062f\u0627\u0631\u06d5\u06a9\u06d5\u062f\u0627 \u062f\u06d5\u06af\u06d5\u06b5\u062a\u0627 \u0628\u0648\u0648\u0646\u06d5 \u0647\u0627\u0648\u067e\u06d5\u06cc\u0645\u0627\u0646\u060c \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0695\u0627\u0632\u06cc \u0628\u0648\u0648\u06d5\u061b \u062f\u06d5\u06cc\u0632\u0627\u0646\u06cc \u0686\u06cc\u06cc\u0627\u0646 \u0644\u06d5\u062f\u06b5\u062f\u0627\u06cc\u06d5. \u0633\u0627 \u0626\u06c6\u0642\u0631\u06d5 \u0648 \u062f\u06b5\u0646\u06cc\u0627\u06cc\u06cc \u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0648 \u0628\u06d5 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06ce\u06a9\u06cc \u0646\u0632\u06cc\u06a9 \u067e\u0627\u062f\u0627\u0634\u06cc \u062f\u0627\u0646\u061b',
    '\u0645\u0627\u06b5\u06cc \u062a\u0627\u06b5\u0627\u0646\u06cc \u0632\u06c6\u0631\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0633 \u062f\u06d5\u06a9\u06d5\u0648\u06ce. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u062e\u0648\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u06af\u06d5\u0644\u06ce \u062a\u0627\u06b5\u0648\u0648\u06cc \u067e\u06ce\u062a\u0627\u0646 \u062f\u0627\u0648\u06d5 \u06a9\u06d5 \u062f\u06d5\u06cc\u06af\u0631\u0646. \u0628\u06d5\u06b5\u0627\u0645 \u0632\u0648\u0648\u062a\u0631 \u0626\u06d5\u0645\u06d5\u06cc \u062f\u0627 \u067e\u06ce\u062a\u0627\u0646 \u0648 \u0646\u06d5\u06cc\u0647\u06ce\u0634\u062a \u062f\u06d5\u0633\u062a\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u062a\u0627\u0646 \u0628\u06af\u0627\u062a\u06ce \u0648 \u0647\u06d5\u062a\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u06cc\u0634 \u0628\u06ce \u0628\u06c6 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646 \u0648 \u0628\u06c6 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0632\u06c6\u0631\u062a\u0631 \u0634\u0627\u0631\u06d5\u0632\u0627\u062a\u0627\u0646 \u0628\u06a9\u0627.',
    '\u062a\u0627\u06b5\u0627\u0646\u06ce\u06a9\u06cc \u062a\u0631\u06cc\u0634 \u0647\u06d5\u06cc\u06d5\u060c \u0647\u06ce\u0634\u062a\u0627 \u062f\u06d5\u0633\u062a\u06cc\u0627\u0646 \u067e\u06ce\u06cc \u0646\u06d5\u06af\u06d5\u06cc\u0648\u06d5 \u0648 \u062e\u0648\u062f\u0627 \u0628\u0627\u0634\u06cc \u0626\u0627\u06af\u0627 \u0644\u06ce\u06cc\u06d5 \u0648 \u062e\u0648\u0627 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06cc \u067e\u06ce \u062f\u06d5\u06a9\u0631\u06ce\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u062f\u06cc\u0646 \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u06d5 \u0634\u06d5\u0695\u06cc\u0634\u06cc\u0627\u0646 \u0648\u06d5\u0644 \u0628\u06a9\u0631\u062f\u0628\u0627\u0646\u060c \u0633\u0648\u0648\u0631 \u0628\u0632\u0627\u0646\u0646 \u067e\u0634\u062a\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f\u06d5 \u0645\u06d5\u06cc\u062f\u0627\u0646 \u0648 \u0695\u0627\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f. \u0644\u06d5\u067e\u0627\u0634\u0627\u0646\u06cc\u0634 \u0646\u06d5 \u062f\u06c6\u0633\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0633 \u062f\u06d5\u06a9\u06d5\u0648\u062a \u0648 \u0646\u06d5 \u06a9\u06d5\u0633 \u062f\u06d5 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u062f\u06d5\u0647\u0627\u062a.',
    '\u06cc\u0627\u0633\u0627\u06cc\u06d5\u06a9\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0648 \u0644\u06d5\u067e\u06ce\u0634\u0648\u0648\u0634\u062f\u0627 \u0647\u06d5\u0631 \u0648\u0627\u0628\u0648\u0648\u06d5 \u0648 \u0644\u06d5\u0648 \u06cc\u0627\u0633\u0627\u06cc \u062e\u0648\u062f\u0627 \u062f\u0627\u06cc\u0646\u0627\u0648\u06d5\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u06af\u06c6\u0695\u0627\u0646\u06ce\u06a9 \u0646\u0627\u0628\u06cc\u0646\u06cc.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634 \u0628\u0648\u0648 \u0644\u06d5\u062f\u06d5\u0633\u062a \u0626\u06d5\u0648\u0627\u0646\u06cc \u067e\u0627\u0631\u0627\u0632\u062a\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634\u06cc \u0644\u06d5\u062f\u06d5\u0633 \u0626\u06ce\u0648\u06d5\u061b \u0648\u06d5\u062e\u062a\u06ce \u0644\u06d5 \u0626\u0627\u0642\u0627\u0631\u06cc \u0645\u06d5\u06a9\u06a9\u06d5 \u062e\u0648\u062f\u0627 \u06a9\u0631\u062f\u06cc \u0648 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u062f\u0627 \u0632\u0627\u06b5 \u0628\u0648\u0648\u0646. \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u0648\u0648 \u062f\u06d5\u06a9\u0631\u062f\u060c \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u062f\u06cc\u062a.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u0627\u06cc \u062f\u06cc\u0646 \u0644\u0627\u06cc\u0627\u0646 \u062f\u0627 \u0648 \u0628\u0648\u0648\u0646\u06d5 \u0644\u06d5\u0645\u067e\u06d5\u0631 \u0648 \u0646\u06d5\u06cc\u0627\u0646\u0647\u06ce\u0634\u062a \u0628\u0686\u0646\u06d5 \u06a9\u0627\u0628\u06d5\u061b \u0695\u06ce\u0634\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0627 \u0642\u0648\u0631\u0628\u0627\u0646\u06cc\u06d5\u06a9\u06d5\u062a\u0627\u0646 - \u06a9\u06d5 \u06af\u0644\u06cc\u0627\u0646 \u062f\u0627\u0628\u0648\u0648\u06d5 - \u0628\u06af\u0627\u062a\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u062e\u06c6\u06cc. [\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06cc\u0632\u0646\u0645\u0627\u0646 \u0628\u062f\u0627\u06cc\u06d5 \u0644\u06d5 \u0645\u06d5\u06a9\u06a9\u06d5 \u062f\u06d5\u06af\u0698\u06cc\u0627\u0646 \u0695\u0627\u0686\u0646] \u0644\u06d5\u0648\u0627\u0646\u06d5\u0628\u0648\u0648 \u0632\u06c6\u0631 \u0645\u06ce\u0631 \u0648 \u0698\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 - \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u062a\u0627\u0646\u0646\u0627\u0633\u06cc\u0628\u0648\u0648\u0646 - \u062f\u06d5\u0628\u06d5\u0631 \u067e\u06ce\u06cc\u0627\u0646\u0648\u062f\u0627 \u0628\u0686\u0646 \u0648 \u0628\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u0632\u0627\u0646\u0646\u060c \u0628\u0628\u0646\u06d5 \u0645\u0648\u0634\u06a9\u0648\u06b5\u06c6\u0632\u0645\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u06a9\u06ce \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u062f\u06d5\u06a9\u0627\u060c \u062f\u06d5\u06cc\u062e\u0627\u062a\u06d5 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u06cc. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0648 \u0634\u0648\u06ce\u0646\u06d5 \u0644\u0627\u0686\u0648\u0648\u0628\u0627\u0646\u060c \u0628\u06ce \u0633\u06c6 \u062f\u06cc\u0646 \u062f\u0648\u0698\u0645\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646 \u062f\u0648\u0686\u0627\u0631 \u062f\u06d5\u06a9\u0631\u062f\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u062f\u06cc\u0646 \u062f\u0648\u0698\u0645\u0646\u06d5\u06a9\u0627\u0646\u060c \u0628\u0627\u062f\u06cc \u0647\u06d5\u0648\u0627\u06cc\u06cc \u0646\u06d5\u0632\u0627\u0646\u0627\u0646\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0646\u0648\u0627\u0646\u062f\u060c \u062e\u0648\u062f\u0627 \u0626\u06c6\u0642\u0631\u06d5 \u0648 \u0647\u06ce\u0645\u0646\u0627\u06cc\u06d5\u062a\u06cc \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u06ce \u0628\u06c6 \u0646\u0627\u0648 \u062f\u06b5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06cc [\u06cc\u0627\u0631\u0627\u0646\u06cc] \u0648 \u0648\u0627\u06cc \u06a9\u0631\u062f \u06a9\u06d5 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632 \u0628\u0646 \u0648 \u0628\u06c6 \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0634 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633 \u0634\u06cc\u0627\u0648\u062a\u0631 \u0648 \u0647\u06ce\u0698\u0627\u062a\u0631 \u0628\u0648\u0648\u0646. \u062e\u0648\u0627 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u062e\u0648\u0627 \u0626\u06d5\u0648 \u062e\u06d5\u0648\u0646\u06d5\u06cc \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u062f\u06cc\u0628\u0648\u0648\u06cc\u060c \u0648\u06d5\u0695\u0627\u0633\u062a \u06af\u06ce\u0695\u0627: \u062e\u0648\u0627 \u062d\u06d5\u0632 \u06a9\u0627 \u0628\u06d5\u0628\u06ce \u0647\u06cc\u0686 \u062a\u0631\u0633\u06ce\u06a9 \u062f\u06d5\u0686\u0646\u06d5 \u0646\u0627\u0648 \u0645\u0627\u06b5\u06cc \u06a9\u0627\u0628\u06d5\u0648\u06d5 \u0648 \u067e\u06c6\u0695\u06cc \u062e\u06c6 \u06a9\u0648\u0631\u062a \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u060c \u06cc\u0627\u0646 \u062f\u06d5\u06cc\u062a\u0627\u0634\u0646. \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0646\u06d5\u0648\u0632\u0627\u0646\u06cc\u0628\u0648\u0648\u060c \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06cc. \u062c\u06af\u06d5 \u0644\u06d5\u0645\u06d5\u0634 \u0628\u06d5\u0648 \u0632\u0648\u0648\u0627\u0646\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06ce\u06a9 \u062f\u06d5\u0628\u0646.',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u06c6\u06cc \u0646\u0627\u0631\u062f \u0648 \u0695\u06ce\u0646\u0648\u06ce\u0646\u06cc \u0648 \u062f\u06cc\u0646\u06cc \u0695\u0627\u0633\u062a\u06cc\u0634\u06cc \u0695\u06d5\u06af\u06d5\u06b5 \u062e\u0633\u062a\u061b \u062a\u0627 \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06cc\u0646\u06ce\u06a9\u0627 \u0632\u0627\u06b5\u06cc \u0628\u06a9\u0627. \u062e\u0648\u0627\u0634 \u0628\u06c6 \u0626\u0627\u06af\u0627 \u0644\u06ce\u0628\u0648\u0648\u0646 \u0628\u06d5\u0633\u06d5.',
    '\u0645\u0648\u062d\u06d5\u0645\u0645\u06d5\u062f \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062e\u0648\u062f\u0627\u0633\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u0646\u060c \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u062f\u06cc\u0646 \u062f\u0648\u0698\u0645\u0646\u0627\u0646 \u062a\u0648\u0646\u062f \u0648 \u062a\u06cc\u0698 \u0648 \u0644\u06d5 \u0646\u0627\u0648\u062e\u06c6\u062f\u0627 \u062f\u0644\u06c6\u06a4\u0627\u0646\u0646. \u0644\u06d5 \u06a9\u0695\u0646\u06c6\u0634 \u0648 \u0633\u0648\u0698\u062f\u06d5 \u0686\u0627\u0648\u062a \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u0648\u0646. \u0647\u06d5\u0631 \u0628\u06d5\u0647\u06ce\u06a4\u06cc \u0695\u06d5\u0632\u0627\u0645\u06d5\u0646\u062f\u06cc \u0648 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646. \u062f\u0631\u06c6\u0634\u0645\u06cc \u0633\u0648\u0698\u062f\u06d5 \u0628\u0631\u062f\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u0646\u0627\u0648\u0686\u0627\u0648\u0627\u0646\u06d5\u0648\u06d5 \u0647\u06d5\u06cc\u06d5\u061b \u0648\u06ce\u0646\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u062a\u06d5\u0648\u0631\u0627\u062a\u062f\u0627\u06cc\u06d5 \u0648 \u0646\u0645\u0648\u0648\u0646\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u06cc\u0646\u062c\u06cc\u0644\u062f\u0627. \u0634\u0628\u06ce\u0646\u0631\u0627\u0648\u06d5 \u0628\u06d5 \u0686\u0646\u0627\u0648\u06ce\u06a9 \u062a\u06cc\u0634\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u0698\u0648\u0648\u0631 \u0647\u06d5\u06b5\u0686\u0648\u0648\u0628\u06ce \u0648 \u0648\u06d5\u0686\u06cc \u0641\u0631\u06d5\u06cc \u062f\u06d5\u0631\u06a9\u0631\u062f\u0628\u06ce \u0648 \u0633\u062a\u0648\u0648\u0631 \u0628\u0648\u0648\u0628\u06ce \u0648 \u0644\u06d5\u0633\u06d5\u0631\u0644\u0627\u0633\u06a9 \u062e\u06c6\u06cc \u06af\u0631\u062a\u0628\u06ce \u0648 \u0648\u06d5\u0631\u0632\u06ce\u0695\u0627\u0646 \u0633\u06d5\u06cc\u0631\u06cc \u0628\u0645\u06ce\u0646\u0646\u061b \u062a\u0627 \u0686\u0627\u0648\u0686\u0646\u06c6\u06a9\u0627\u0646\u06cc \u0633\u067e\u06b5\u06d5\u06cc \u067e\u06ce \u0642\u06d5\u06b5\u0633 \u06a9\u0627. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631 \u0686\u0627\u06a9\u0646\u060c \u062e\u0648\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0644\u06ce \u0628\u0648\u0648\u0631\u062f\u0646 \u0648 \u067e\u0627\u062f\u0627\u0634\u06ce \u06af\u06d5\u0648\u0631\u06d5\u06cc \u067e\u06ce \u062f\u0627\u0648\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0646\u06d5\u0686\u0646 \u0648\u06d5\u067e\u06ce\u0634 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u06a9\u06d5\u0648\u0646\u061b \u0628\u0627 \u062a\u0631\u0633\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06ce\u060c \u062e\u0648\u062f\u0627 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0646\u0627\u0634\u06ce \u062f\u06d5\u0646\u06af\u062a\u0627\u0646 \u0633\u06d5\u0631 \u062f\u06d5\u0646\u06af\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u06a9\u06d5\u0648\u06ce. \u062f\u06d5\u0646\u06af\u06cc\u0634 \u0647\u06ce\u0646\u062f\u06d5 \u0628\u06d5\u0631\u0632 \u0645\u06d5\u06a9\u06d5\u0646\u060c \u0648\u06d5\u06a9 \u0648\u06d5\u0644 \u06cc\u06d5\u06a9\u062a\u0631\u0627 \u062f\u0627\u062e\u06ce\u0648\u0646\u061b \u06a9\u06d5 \u06a9\u0627\u0631\u062a\u0627\u0646 \u0644\u06ce \u067e\u0648\u0648\u0686\u06d5\u06b5 \u0628\u0628\u06ce \u0648 \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u062c\u06d5\u0645 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0646\u06af\u06cc\u0627\u0646 \u0646\u0632\u0645 \u062f\u06d5\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0646 \u062e\u0648\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u06cc\u060c \u062f\u06b5\u06cc \u062a\u0627\u0642\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646\u06d5\u0648\u06d5. \u0644\u06ce \u062e\u06c6\u0634 \u0628\u0648\u0648\u0646 \u0648 \u067e\u0627\u062f\u0627\u0634\u06cc \u0645\u06d5\u0632\u0646 \u0628\u06c6 \u0648\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0648 \u062f\u06cc\u0648\u06cc \u0647\u06c6\u062f\u06d5\u06a9\u0627\u0646\u06d5\u0648\u06d5 \u0628\u0627\u0646\u06af\u062a \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0626\u0627\u0648\u06d5\u0632\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0695\u0627\u06af\u0631\u062a\u0627\u06cc\u06d5 \u0647\u06d5\u062a\u0627 \u062f\u06d5\u0631 \u062f\u06d5\u0686\u0648\u0648\u06cc \u0628\u06c6 \u0644\u0627\u06cc\u0627\u0646\u060c \u0628\u06ce \u0634\u06a9 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0628\u0627\u0634\u062a\u0631 \u0628\u0648\u0648. \u062e\u0648\u0627 \u062f\u06d5\u0634\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0644\u0627\u062f\u06d5\u0631\u06ce\u06a9 \u062f\u06d5\u0646\u06af \u0648 \u0628\u0627\u0633\u06ce\u06a9\u062a\u0627\u0646 \u0628\u06c6 \u062f\u06ce\u0646\u06ce\u060c \u0628\u06d5 \u0648\u0631\u062f\u06cc \u0644\u06ce\u06cc \u0648\u0631\u062f \u0628\u0646\u06d5\u0648\u06d5\u061b \u0646\u06d5\u06a9\u0627 \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0646\u06d5\u0632\u0627\u0646\u06cc\u0648\u06d5 \u06af\u06d5\u0644\u06ce\u06a9\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u0632\u06cc\u0627\u0646 \u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u06a9\u0631\u062f\u06d5\u062a\u0627\u0646 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0628\u0646.',
    '\u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u0626\u06d5\u0645\u06d5\u0634 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u062a\u0627\u0646 \u0644\u06d5\u0646\u0627\u0648\u062f\u0627\u06cc\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5 \u0632\u06c6\u0631\u06a9\u0627\u0631\u0627\u0646\u062f\u0627 \u0634\u0648\u06ce\u0646\u062a\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0628\u0627\u060c \u062a\u0648\u0648\u0634\u06cc \u0646\u0627\u0695\u06d5\u062d\u06d5\u062a\u06cc \u062f\u06d5\u0628\u0648\u0648\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0626\u06d5\u06a4\u06cc\u0646\u06cc \u0628\u0695\u0648\u0627 \u0628\u06d5 \u062f\u06cc\u0646\u06cc\u062f\u0627 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u06d5\u06cc \u0644\u06d5 \u062f\u06b5\u062a\u0627\u0646\u062f\u0627 \u062e\u06d5\u0645\u06b5\u0627\u0646\u062f. \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u06cc \u0648 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u0627\u0646 \u0648 \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc \u0644\u06d5 \u0644\u0627\u062a\u0627\u0646 \u062f\u0632\u06ce\u0648 \u06a9\u0631\u062f\u0648\u0648\u06d5. \u0644\u06d5 \u0695\u06ce \u0634\u0627\u0631\u06d5\u0632\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0646.',
    '\u0626\u06d5\u0645\u06d5\u0634 \u0628\u06d5\u062e\u0634\u0634 \u0648 \u0686\u0627\u06a9\u06d5\u06cc\u06d5 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0648\u061b \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u062f\u0648\u0648 \u06af\u0631\u06c6 \u062f\u06d5 \u06af\u0698 \u06cc\u06d5\u06a9\u0695\u0627 \u0686\u0648\u0646\u060c \u0626\u06ce\u0648\u06d5 \u0626\u0627\u0634\u062a\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061b \u0626\u06d5\u06af\u06d5\u0631 \u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9\u06cc \u0628\u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5\u0648\u06cc \u062a\u0631 \u0628\u06a9\u0627\u060c \u0626\u06ce\u0648\u06d5 \u062f\u06d5 \u06af\u0698 \u0626\u06d5\u0648\u0695\u0627 \u0686\u0646 \u06a9\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u0634\u06d5\u0695 \u0628\u06a9\u0627\u061b \u0647\u06d5\u062a\u0627 \u0628\u06c6 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0645\u0644 \u06a9\u06d5\u0686 \u062f\u06d5\u06a9\u0627. \u0626\u06d5\u0648\u0633\u0627 - \u0628\u06d5\u0628\u06ce \u0644\u0627\u06cc\u06d5\u0646\u06af\u06cc\u0631\u06cc - \u067e\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06ce\u0646\u0646 \u0648 \u0628\u06d5 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u0628\u06d5 \u06a9\u06ce\u0634\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0695\u0627\u0628\u06af\u06d5\u0646. \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u06d5\u0646\u060c \u062e\u0648\u0627 \u062e\u06c6\u0634\u06cc \u062f\u06ce.',
    '\u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u0631\u0627\u06cc \u06cc\u06d5\u06a9\u0646\u061b \u0628\u0631\u0627\u06a9\u0627\u0646\u062a\u0627\u0646 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0626\u0627\u0634\u062a \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0634\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u06a9\u06d5\u0648\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u0627 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u062a\u0627\u0646 \u06af\u0627\u06b5\u062a\u06d5 \u0628\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0627\u0646 \u0646\u06d5\u06a9\u06d5\u0646\u061b \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06af\u06d5\u067e\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u06d5\u0646\u060c \u0644\u06d5 \u06af\u06d5\u067e \u062f\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0628\u0627\u0634\u062a\u0631 \u0628\u0646. \u0698\u0646\u06cc\u0634 \u0647\u06d5\u0631\u0648\u0627\u061b \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0628\u0646\u06d5 \u06af\u0627\u06b5\u062a\u06d5\u062c\u0627\u0631\u060c \u0644\u06d5\u0648\u06cc \u06af\u0627\u06b5\u062a\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u060c \u067e\u06d5\u0633\u0646\u062f\u062a\u0631 \u0628\u0646. \u0644\u06d5 \u0633\u06cc\u0644\u06d5\u06cc \u0686\u0627\u0648 \u062f\u0627\u06af\u0631\u062a\u0646 \u0648 \u062a\u06d5\u0634\u06d5\u0631 \u062f\u06d5\u06cc\u06d5\u06a9\u062a\u0631 \u06af\u0631\u062a\u0646 \u0648 \u0646\u0627\u0648 \u0648 \u0646\u0627\u062a\u06c6\u0631\u06d5 \u0644\u06ce\u06a9 \u0647\u06d5\u06b5\u062f\u0627\u0646\u060c \u062f\u06d5\u0633 \u0647\u06d5\u06b5\u06af\u0631\u0646\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0644\u06d5 \u067e\u0627\u0634 \u0628\u0695\u0648\u0627 \u0647\u06ce\u0646\u0627\u0646\u060c \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u0627\u0646\u060c \u0628\u06d5\u062f \u0646\u06ce\u0648\u06cc\u06d5 \u0648 \u0646\u0627\u067e\u06d5\u0633\u06d5\u0646\u062f\u06d5. \u06a9\u06ce\u0634 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0646\u06d5\u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u06d5\u06a9\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0644\u06d5 \u0632\u06c6\u0631\u06ce\u06a9 \u0644\u06d5 \u06af\u0648\u0645\u0627\u0646\u0627\u0646 \u062e\u06c6 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646. \u0686\u0648\u0646\u06a9\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u06af\u0648\u0645\u0627\u0646\u06cc \u0648\u0627 \u0647\u06d5\u0646 \u06af\u0648\u0646\u0627\u0647\u0646. \u0644\u06d5 \u06a9\u0627\u0631\u0648\u0628\u0627\u0631\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0634 \u0645\u06d5\u06a9\u06c6\u06b5\u0646\u06d5\u0648\u06d5. \u067e\u0627\u0634\u06d5\u0645\u0644\u06d5\u0634 \u062e\u0631\u0627\u067e\u06d5\u06cc \u06cc\u06d5\u06a9\u062a\u0631 \u0645\u06d5\u0628\u06ce\u0698\u0646. \u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u062a\u0627\u0646 \u0648\u0627\u0632\u06cc \u0644\u06ce\u06cc\u06d5 \u06a9\u06d5 \u06af\u06c6\u0634\u062a\u06cc \u0628\u0631\u0627\u06cc \u062e\u06c6\u06cc \u0628\u06d5 \u0645\u0631\u062f\u0648\u06cc\u06cc \u0628\u062e\u0648\u0627\u061f \u062f\u06cc\u0627\u0631\u06d5 \u0642\u06cc\u0632\u062a\u0627\u0646 \u0644\u06ce \u062f\u06ce\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0634\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646. \u062e\u0648\u0627 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u0642\u0628\u0648\u06b5\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u062e\u06d5\u06b5\u06a9\u06cc\u0646\u06d5! \u0647\u06d5\u0631 \u0644\u06d5\u0646\u06ce\u0631 \u0648 \u0645\u06ce\u0648\u06ce\u06a9\u06d5\u0648\u06d5 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u06a9\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u0646 \u0628\u06d5 \u0686\u06d5\u0646\u062f \u06af\u06d5\u0644\u06ce\u06a9 \u0648 \u0686\u06d5\u0646\u062f \u0647\u06c6\u0632\u06ce\u06a9 \u0647\u06d5\u062a\u0627 \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u0628\u0646\u0627\u0633\u0646. \u0628\u06d5\u0695\u06ce\u0632\u062a\u0631\u06cc\u0646\u062a\u0627\u0646 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u060c \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u062a\u0631\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646\u0646. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc\u06d5 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0639\u06d5\u0631\u06d5\u0628\u06d5\u06a9\u0627\u0646\u06cc \u062f\u06d5\u0634\u062a\u06d5\u06a9\u06cc \u06af\u0648\u062a\u06cc\u0627\u0646: \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627. \u0628\u06ce\u0698\u06d5: \u0647\u06ce\u0634\u062a\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0628\u06ce\u0698\u0646: \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u0648\u06d5 \u062f\u0627\u0648\u06d5\u061b \u0647\u06ce\u0645\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc \u0626\u06cc\u0633\u0644\u0627\u0645\u06d5\u062a\u06cc \u0646\u06d5\u0686\u06c6\u062a\u06d5 \u0646\u0627\u0648 \u062f\u06b5\u062a\u0627\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0626\u06d5\u0648 \u0628\u0646\u060c \u0644\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u062a\u0627\u0646 \u0647\u06cc\u0686 \u0634\u062a\u06ce \u06a9\u06d5\u0645 \u0646\u0627\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0646 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u062a\u0648\u0648\u0634\u06cc \u062f\u0648\u0648\u062f\u06b5\u06cc \u0646\u06d5\u0628\u0648\u0648\u0646 \u0648 \u0628\u06d5 \u06af\u06cc\u0627\u0646 \u0648 \u0645\u0627\u06b5 \u0644\u06d5\u0695\u06ce\u06cc \u062e\u0648\u062f\u0627 \u062a\u06ce\u06a9\u06c6\u0634\u0627\u0648\u0646 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc\u0627\u0646\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u06af\u06d5\u0631\u06d5\u06a9\u062a\u0627\u0646\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648 \u062f\u06cc\u0646\u06d5\u06cc \u0647\u06d5\u062a\u0627\u0646\u06d5\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u06a9\u06d5\u0646\u061f \u06a9\u06d5 \u0686\u06cc \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u062e\u0648\u0627 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0686\u0648\u0646\u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646 \u060c \u0645\u0646\u06d5\u062a \u0628\u06d5\u0633\u06d5\u0631 \u062a\u06c6\u062f\u0627 \u062f\u06d5\u06a9\u06d5\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u062a \u0626\u06d5\u06a9\u06d5\u0646 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u0644\u06d5 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646 \u0628\u0648\u0648\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646\u060c \u0645\u0646\u06d5\u062a \u0628\u06d5\u0633\u06d5\u0631 \u0645\u0646\u062f\u0627 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u062f\u0627 \u0645\u0646\u06d5\u062a \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06a9\u0627\u060c \u06a9\u06d5 \u0695\u06ce\u06af\u0627\u06cc \u0628\u0695\u0648\u0627 \u0647\u06ce\u0646\u0627\u0646\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0648\u0646.',
    '\u0647\u06d5\u0631 \u0646\u0647\u06ce\u0646\u06cc \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u0686\u06cc \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u0642\u0627\u0641\u060c \u0633\u0648\u06ce\u0646\u062f \u0628\u06d5\u0645 \u0642\u0648\u0695\u0626\u0627\u0646\u06d5 \u0628\u06d5\u0695\u06ce\u0632\u06d5\u060c',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06d5\u0633\u0648\u0695\u0645\u0627 \u06a9\u06d5 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631\u0647\u06ce\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06c6\u067e\u06d5\u06cc\u062f\u0627\u0628\u0648\u06d5\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u0634\u062a\u06ce \u0633\u06d5\u06cc\u0631\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u0645\u0631\u062f\u06cc\u0646 \u0648 \u0628\u0648\u0648\u06cc\u0646\u06d5 \u0645\u0634\u062a\u06ce \u062e\u06c6\u06b5\u060c [\u062f\u06d5\u0698\u06cc\u0646\u06d5\u0648\u06d5\u061f] \u0626\u06d5\u0645 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc\u06d5 \u062f\u0648\u0631\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631 \u0632\u0648\u0632\u0627\u0646\u06cc\u0648\u0645\u0627\u0646\u06d5 \u0632\u06d5\u0648\u06cc \u0686\u06d5\u0646\u062f\u06cc\u0627\u0646 \u0644\u06ce \u06a9\u06d5\u0645 \u062f\u06d5\u06a9\u0627\u061b \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5\u0644\u0627\u06cc\u06d5 \u0647\u06d5\u0645\u0648\u0634\u062a\u06ce\u06a9\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5.',
    '\u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06d5 \u0626\u06d5\u0645 \u0695\u0627\u0633\u062a\u06d5\u06cc\u0627\u0646 \u0647\u0627\u062a\u06d5\u0644\u0627\u060c \u0647\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f\u0648 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0626\u0627\u06b5\u06c6\u0632\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0628\u06c6 \u0639\u0627\u0633\u0645\u0627\u0646\u06cc\u0627\u0646 \u0646\u06d5\u0695\u0648\u0627\u0646\u06cc\u0648\u06d5\u060c \u06a9\u06d5\u0628\u06d5\u0633\u06d5\u0631 \u0633\u06d5\u0631\u06cc\u0627\u0646\u06d5\u0648\u06d5\u06cc\u06d5\u060c \u0686\u062a\u06c6 \u0648\u06d5\u062f\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u0648 \u0686\u0644\u06c6\u0646\u0645\u0627\u0646 \u0695\u0627\u0632\u0627\u0646\u062f\u06c6\u062a\u06d5\u0648\u06d5\u0648 \u0647\u06cc\u0686 \u06a9\u06d5\u0644\u06ce\u0646\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u0626\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u0634\u0645\u0627\u0646 \u0695\u0627\u062e\u0633\u062a\u0648\u06d5\u0648 \u06a9\u06ce\u0648\u067e\u0627\u0631\u06d5\u0645\u0627\u0646 \u062a\u06ce \u062e\u0633\u062a\u0648\u06d5\u0648 \u062c\u0648\u0648\u062a\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0695\u0648\u06d5\u06a9\u06ce\u06a9\u06cc \u0628\u06d5\u0631\u062f\u06b5\u0645\u0627\u0646 \u062f\u06d5\u0646\u0627\u0648\u062f\u0627 \u0695\u0648\u0627\u0646\u062f\u061b',
    '\u062a\u0627 \u0686\u0627\u0648\u06cc \u062f\u06b5 \u0695\u06c6\u0634\u0646 \u06a9\u0627\u062a \u0648 \u0628\u0628\u06ce\u062a\u06d5 \u0647\u06c6\u06cc \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc \u0628\u06c6\u0647\u06d5\u0631\u0639\u06d5\u0628\u062f\u06ce\u06a9 \u062d\u06d5\u0632\u0628\u06a9\u0627 \u0628\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0648\u0628\u06d5\u0631\u0632\u06d5\u0648\u06d5 \u0626\u0627\u0648\u06ce \u0628\u06d5\u067e\u06cc\u062a \u0648 \u0628\u06d5\u0647\u0631\u06d5\u0645\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0698\u06ce\u0631 \u0646\u0627\u0631\u062f\u061b \u0641\u0631\u06d5\u0628\u0627\u063a\u0627\u062a\u0645\u0627\u0646 \u067e\u06ce \u0695\u0648\u0627\u0646\u062f\u061b \u0626\u06d5\u0648\u0644\u0628\u06d5\u0634 \u06a9\u06d5 \u062f\u0631\u06d5\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u061b',
    '\u062f\u0627\u0631\u06d5\u062e\u0648\u0631\u0645\u0627\u06cc \u0628\u06d5\u0631\u0632\u06d5\u0628\u0627\u06b5\u0627\u0634 \u0628\u06d5 \u0647\u06ce\u0634\u0648\u0648\u06cc \u0628\u0632\u0645\u0627\u0631\u06a9\u0648\u062a\u06d5\u0648\u06d5\u061b',
    '\u0628\u0698\u06cc\u0648\u06d5 \u0628\u06c6\u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646 \u0648 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06cc \u0645\u0631\u06cc\u0634\u0645\u0627\u0646 \u067e\u06ce \u0628\u0648\u0698\u0627\u0646\u062f\u06d5\u0648\u06d5\u061b \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06cc\u0634 \u0647\u06d5\u0631\u0648\u06d5\u0647\u0627\u06cc\u06d5.',
    '\u0628\u06d5\u0631\u0644\u06d5\u0648\u0627\u0646\u06d5\u0634\u060c \u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d \u0648 \u062e\u0648\u062f\u0627\u0646\u06cc \u0626\u0627\u0648\u06cc \u0628\u06cc\u0631\u06d5\u06a9\u06d5\u0648 \u0633\u06d5\u0645\u0648\u0648\u062f\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06ce \u0628\u0695\u0648\u0627\u0628\u0648\u0646.',
    '\u0647\u06c6\u0632\u06cc \u0639\u0627\u062f\u0648 \u062f\u06d5\u0633\u062a\u06d5\u0648\u062f\u0627\u06cc\u06d5\u0631\u06d5\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0647\u06c6\u0632\u06cc \u0644\u0648\u0648\u062a\u06cc\u0634\u060c',
    '\u06af\u0631\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u062d\u0644\u06d5\u06a9\u0627\u0646 \u0648 \u0698\u06ce\u0631\u062f\u06d5\u0633\u062a\u06d5\u06a9\u0627\u0646\u06cc \u062a\u0648\u0628\u0628\u06d5\u0639\u06cc\u0634\u060c \u0647\u06cc\u0686\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646\u06d5\u06a9\u0631\u062f\u061b \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0695\u06d5\u0634\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646 \u0648\u06d5\u0695\u0627\u0633\u062a \u06af\u06d5\u0695\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0646\u06cc \u067e\u06ce\u0634\u06cc\u0646 \u0645\u0627\u0646\u062f\u0648\u0628\u0628\u0648\u06cc\u0646\u061f \u0628\u06d5\u06b5\u06a9\u0648\u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u06af\u0648\u0645\u0627\u0646 \u0628\u0648\u0646 \u06a9\u06d5 \u062f\u06d5\u062a\u0648\u0627\u0646\u06cc\u0646 \u0633\u06d5\u0631\u0644\u06d5\u0646\u0648\u06ce \u0648\u06d5\u062f\u06cc \u0628\u0647\u06ce\u0646\u06cc\u0646.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06ce\u0645\u06d5 \u0645\u0631\u06c6\u0645\u0627\u0646 \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u0648\u0648\u06d5\u0648 \u062f\u06d5\u0634\u0632\u0627\u0646\u06cc\u0646 \u0686\u06c6\u0646 \u062e\u06d5\u06cc\u0627\u06b5\u0627\u062a\u06cc \u0628\u06d5\u062f\u06b5\u062f\u0627\u062f\u06ce. \u0626\u06ce\u0645\u06d5 \u0628\u06c6\u0626\u06d5\u0648 \u0644\u06d5 \u0634\u0627\u062f\u06d5\u0645\u0627\u0631\u06cc \u0644\u0627\u0645\u0644\u06cc \u0646\u0632\u06cc\u06a9\u062a\u0631\u06cc\u0646.',
    '\u0626\u06d5\u0648\u06ce \u06a9\u0627\u062a\u06ce \u0644\u06d5 \u0644\u0627\u06cc \u0695\u0627\u0633\u062a \u0648 \u0686\u06d5\u067e\u06cc\u0634\u06d5\u0648\u06d5 \u062c\u0648\u062a\u06ce \u0641\u0631\u0634\u062a\u06d5 \u0695\u06c6\u0646\u06cc\u0634\u062a\u0648\u0646 [\u0628\u06c6\u06cc \u062f\u06d5\u0646\u0648\u0648\u0633\u0646].',
    '\u0647\u06d5\u0631 \u0648\u0631\u062a\u06d5\u06cc \u0644\u06d5 \u062f\u06d5\u0645 \u062f\u06d5\u0631\u0628\u0686\u06ce\u060c \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06ce\u06a9\u06cc \u0644\u0627\u0647\u06d5\u06cc\u06d5.',
    '\u0628\u0648\u0631\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0626\u0627\u0648\u06cc\u0644\u06a9\u06d5\u062f\u0627\u0646\u060c \u0648\u0627\u062f\u06cc\u0627\u0631\u06cc\u062f\u0627\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u0634\u06d5 \u06a9\u06d5 \u062a\u06c6 \u0646\u06d5\u062a \u062f\u06d5\u0648\u06cc\u0633\u062a \u062a\u0648\u0634\u06cc \u0628\u06cc.',
    '\u0641\u0648 \u0628\u06d5 \u06a9\u06d5\u06b5\u06d5\u0634\u0627\u062e\u0627\u062f\u06d5\u06a9\u0631\u06ce\u061b \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce\u061b \u062f\u06d5\u0628\u06ce \u0647\u06d5\u0695\u06d5\u0634\u06d5 \u0648\u06d5\u062f\u06cc \u0628\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062f\u06ce\u060c \u06cc\u06d5\u06a9\u06ce\u06a9 \u0648\u06d5\u067e\u06ce\u0634\u06d5\u062e\u06c6\u06cc \u062f\u0627\u0648\u06d5\u0648 \u06cc\u06d5\u06a9\u06ce\u06a9\u06cc\u0634 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u062f\u06d5\u062f\u0627.',
    '\u062a\u06c6 \u0644\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06ce \u0647\u06d5\u0648\u0627\u06b5 \u0628\u0648\u06cc\u061b \u0648\u0627 \u0633\u06d5\u0631\u067e\u06c6\u0634\u0645\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u0627\u062f\u0627\u06cc\u061b \u0686\u0627\u0648\u062a \u0626\u06d5\u0645\u0695\u06c6 \u0632\u06c6\u0631 \u0628\u0695\u062f\u06d5\u06a9\u0627.',
    '\u0641\u0631\u0634\u062a\u06d5\u06a9\u06d5\u06cc \u0647\u0627\u0648\u0695\u06ce\u06cc \u0626\u06ce\u0698\u06ce: \u0648\u0627 \u0644\u06d5 \u0644\u0627\u06cc \u0645\u0646 \u0626\u0627\u0645\u0627\u062f\u06d5\u06cc\u06d5.',
    '\u0628\u06d5 \u0647\u06d5\u0631\u062f\u0648\u06a9\u062a\u0627\u0646\u060c \u0647\u06d5\u0645\u0648\u0648 \u0633\u067e\u06b5\u06d5\u06cc\u06d5\u06a9\u06cc \u0644\u0627\u0633\u0627\u0631\u060c \u0628\u0627\u0648\u06ce\u0698\u0646\u06d5 \u0646\u0627\u0648 \u062f\u06c6\u0698\u06d5\u0647\u06d5\u0648\u06d5.',
    '\u06a9\u06d5 \u0628\u06d5\u0631\u06cc \u0644\u06d5 \u0686\u0627\u06a9\u06d5 \u06af\u0631\u062a \u0648 \u0644\u06d5\u0695\u06ce\u06cc \u0644\u0627\u062f\u0627\u0648 \u062e\u06d5\u06b5\u06a9\u06cc\u0634\u06cc \u0648\u06d5\u06af\u0648\u0645\u0627\u0646 \u062f\u06d5\u062e\u0633\u062a.',
    '\u06a9\u06d5\u0633\u06ce \u0628\u0648 \u06a9\u06d5\u0648\u06ce\u0695\u0627\u06cc \u062e\u0648\u062f\u0627\u060c \u0634\u062a\u06cc \u062f\u06cc\u06a9\u06d5\u0634\u06cc \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a. \u0633\u0627 \u0628\u06d5\u062a\u0648\u0646\u062f\u06cc \u0628\u06cc\u0627\u0646\u062e\u06d5\u0646\u06d5 \u0628\u06d5\u0631 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u06cc\u06cc \u0647\u0627\u0648\u0695\u06ce\u06cc\u06d5\u062a\u06cc \u0626\u06ce\u0698\u06ce: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u0644\u0627\u0645\u0627\u0646! \u062f\u0646\u06d5\u06cc \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc\u0645 \u0646\u06d5\u062f\u0627\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631\u062e\u06c6\u06cc \u0644\u06d5\u0648\u067e\u06d5\u0695\u06cc \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc\u062f\u0627\u0628\u0648.',
    '\u0626\u06ce\u0698\u06ce: \u0644\u0627\u06cc \u0645\u0646 \u06a9\u06ce\u0634\u06d5\u0645\u06d5\u06a9\u06d5\u0646\u061b \u0645\u0646 \u067e\u06ce\u0634\u06d5\u06a9\u06cc \u0647\u06d5\u0695\u06d5\u0634\u06d5\u0645 \u067e\u06ce \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0646.',
    '\u0628\u06d5\u06b5\u06ce\u0646\u06cc \u0645\u0646 \u06af\u06c6\u0695\u0627\u0646\u06cc \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u0646\u0627\u06cc\u06d5\u0648 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5\u0639\u06d5\u0628\u062f\u0627\u0646 \u0646\u0627\u06a9\u06d5\u0645.',
    '\u0695\u06c6\u0698\u06ce \u062f\u06d5\u0628\u06ce \u06a9\u06d5 \u0628\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06ce \u0626\u06ce\u0698\u06cc\u0646: \u0626\u0627\u062e\u06c6 \u067e\u0695 \u0628\u0648\u06cc\u061f \u0626\u06ce\u0698\u06ce: \u0626\u0627\u062e\u06c6 \u067e\u062a\u0631\u06cc\u0634 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u0634 \u0644\u06d5 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646 \u0646\u0632\u06cc\u06a9 \u0628\u06c6\u062a\u06d5\u0648\u06d5\u0648 \u062f\u0648\u0631\u0646\u06cc\u06d5.',
    '\u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u0648\u062e\u0648\u062f\u0627 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u0648 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0628\u0648\u0646\u060c \u0633\u06c6\u0632\u062f\u0631\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0644\u06d5\u062e\u0648\u062f\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062a\u0631\u0633\u0627\u0648\u0646 \u0648 \u0628\u06d5\u062f\u06b5\u06ce\u06a9\u06cc \u067e\u0627\u06a9\u06d5\u0648\u06d5 \u0647\u0627\u062a\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc.',
    '\u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0628\u0686\u0646\u06d5\u0698\u0648\u0631\u06d5\u0648\u06d5\u0648 \u0628\u06ce \u062a\u0631\u0633 \u0628\u0646\u061b \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u0631\u0645\u0627\u0646\u06ce.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u0634\u06cc \u0628\u06a9\u06d5\u0646 \u062a\u06ce\u06cc\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648 \u0632\u06ce\u062f\u06d5 \u0644\u06d5\u0648\u06cc\u0634\u0645\u0627\u0646 \u0644\u0627\u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u06af\u06d5\u0644\u06ce\u06a9 \u0686\u06cc\u0646 \u0648 \u0628\u06d5\u0631\u06d5\u0646 - \u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062f\u0627\u0628\u0648\u0646 - \u0642\u0695\u062a\u06ce \u062e\u0633\u062a\u0648\u0646\u060c \u0644\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0628\u06d5\u0647\u06ce\u0632\u062a\u0631 \u0628\u0648\u0646 \u0648 \u0632\u06c6\u0631\u0648\u06b5\u0627\u062a\u06cc\u0627\u0646 \u067e\u0634\u06a9\u0646\u06cc\u0648\u0646\u061b \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0647\u06cc\u0686 \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06cc\u0627\u0646 \u0628\u06c6\u0695\u06d5\u062e\u0633\u0627\u061f',
    '\u0626\u06d5\u0645 \u0648\u062a\u0627\u0631\u06d5 \u0628\u06c6\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062f\u06b5 \u0632\u06cc\u0646\u062f\u0648\u0648\u06d5\u060c \u06cc\u0627\u0646 \u0628\u0627\u0634\u06cc \u06af\u0648\u06ce \u0644\u06ce \u062f\u06d5\u062f\u06ce\u0631\u06ce \u0648 \u0628\u06cc\u0631\u06cc\u0634\u06cc \u0647\u06d5\u0631 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06d5\u060c \u062f\u06d5\u0628\u06ce\u062a\u06d5\u0647\u06c6\u06cc \u067e\u06d5\u0646\u062f\u06af\u0631\u062a\u0646 \u0648 \u0628\u06cc\u0631\u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0628\u06d5 \u0634\u06d5\u0634 \u0695\u06c6\u0698 \u0648\u06d5\u062f\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648 \u0647\u06cc\u0686 \u0645\u0627\u0646\u062f\u06af\u06cc\u0634\u0645\u0627\u0646 \u062a\u0648\u0648\u0634 \u0646\u06d5\u0647\u0627\u062a.',
    '\u0633\u0627 \u062e\u06c6\u0628\u06af\u0631\u06d5 \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0626\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0626\u06ce\u0698\u0646. \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a \u0628\u062f\u06d5\u061b \u0644\u06d5\u067e\u06ce\u0634 \u06af\u0632\u06cc\u0646\u06af\u06cc \u0647\u06d5\u062a\u0627\u0648\u062f\u0627\u0648 \u0628\u06d5\u0631\u0644\u06d5\u062f\u06d5\u0645\u06cc \u0695\u06c6\u0698\u0627\u0648\u0627\u0628\u0648\u0646.',
    '\u0644\u06d5 \u0634\u06d5\u0648\u06cc\u0634\u062f\u0627 \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u0626\u06d5\u0648\u0628\u062f\u06d5\u0648 \u062f\u0648\u0627\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0633\u0648\u0698\u062f\u06d5\u0628\u0631\u062f\u0646\u06cc\u0634.',
    '\u0647\u06d5\u0631\u062f\u06d5\u0645\u06ce\u06a9\u06cc\u0634 \u0628\u0627\u0646\u06af \u0695\u0627\u0647\u06ce\u06b5\u06ce\u06a9 \u0644\u06d5\u062c\u06ce\u06af\u0627\u06cc\u06d5\u06a9\u06cc \u0646\u0632\u06cc\u06a9\u0695\u0627 \u06af\u0627\u0632\u06cc \u062f\u06d5\u06a9\u0627\u060c \u06af\u0648\u06ce\u06cc \u0628\u06c6\u0628\u06af\u0631\u06d5.',
    '\u0644\u06d5\u0648\u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0646\u06d5\u0639\u0631\u06d5\u062a\u06d5\u0695\u0627\u0633\u062a\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u06af\u0648\u06ce \u0644\u06ce \u062f\u06d5\u0628\u06ce\u060c \u0626\u06d5\u0645\u06d5 \u0695\u06c6\u0698\u06cc \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u0698\u06cc \u062f\u06d5\u0628\u06d5\u062e\u0634\u06cc\u0646 \u0648 \u062f\u06d5\u0645\u0631\u06ce\u0646\u06cc\u0646\u061b \u0648\u06d5\u06af\u06d5\u0695\u06cc\u0634 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u062f\u06d5\u0642\u06d5\u06b5\u0634\u06ce\u062a\u06d5\u0648\u06d5\u0648 \u0628\u06d5\u0644\u06d5\u0632 \u062f\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0647\u0627\u0648\u06ce\u0698\u06ce\u060c \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0648\u06d5\u06a9\u06c6\u06a9\u0631\u062f\u0646\u06ce\u060c \u0644\u0627\u0645\u0627\u0646 \u0634\u062a\u06ce\u06a9\u06cc \u0647\u0627\u0633\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646 \u0686 \u0626\u06ce\u0698\u0646\u061b \u062a\u06c6 \u0632\u06c6\u0631\u062f\u0627\u0631\u06ce\u06a9 \u0646\u06cc \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5 \u0647\u06d5\u0695\u06d5\u0634\u06d5 \u062a\u0631\u0633\u06cc \u0647\u06d5\u06cc\u06d5\u060c \u0628\u06d5\u0645 \u0642\u0648\u0695\u0626\u0627\u0646\u06d5 \u062a\u06c6 \u0648\u06d5\u0628\u06cc\u0631\u06cc \u0628\u062e\u06d5\u0631\u06d5\u0648\u06d5.',
    '\u0628\u06d5 \u0628\u0627\u06cc\u0627\u0646\u06d5\u06cc \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0628\u06b5\u0627\u0648\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u060c',
    '\u0628\u06d5\u0648 \u0647\u06d5\u0648\u0631\u0627\u0646\u06d5\u06cc \u06af\u0631\u0627\u0646 \u0628\u0627\u0631\u0646\u060c',
    '\u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5\u0647\u0627\u0633\u0627\u0646\u06cc \u0695\u06ce \u062f\u06d5\u067e\u06ce\u0648\u0646\u060c',
    '\u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u0627\u0631\u062f\u0627\u0628\u06d5\u0634 \u06a9\u06d5\u0631\u0646\u060c',
    '\u0647\u06d5\u0631 \u0628\u06d5\u06b5\u06ce\u0646\u06ce \u06a9\u06d5 \u067e\u06ce\u062a\u0627\u0646\u06cc \u062f\u06d5\u062f\u06d5\u0646\u060c \u0695\u0627\u0633\u062a\u06d5.',
    '\u0695\u06c6\u0698\u06cc \u062c\u06d5\u0632\u0627 \u0647\u06d5\u0631 \u0695\u0648\u062f\u06d5\u062f\u0627.',
    '\u0628\u06d5\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06cc \u0695\u06ce \u0695\u06ce \u06cc\u06d5 [\u0648 \u0634\u06d5\u067e\u06c6\u0644\u0627\u0646 \u062f\u06d5\u062f\u0627]\u060c',
    '\u0642\u0633\u06d5\u0648 \u06cc\u06d5\u06a9\u062a\u0631 \u0646\u0627\u06af\u0631\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u0642\u0648\u0695\u0626\u0627\u0646 \u062f\u06d5\u062a\u06d5\u06a9\u06ce\u062a\u06d5\u0648\u06d5\u060c \u0647\u06d5\u0631\u06a9\u06ce \u062a\u06d5\u06a9\u06ce\u0631\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u06a9 \u0628\u06d5\u06af\u0648\u0634\u062a \u0686\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u0631\u06c6\u0646\u06d5\u062e\u0634\u06cc\u0646\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646\u06d5 \u06af\u06ce\u0698\u0627\u0648\u06cc \u0646\u06d5\u0632\u0627\u0646\u06cc \u0648 \u0626\u0627\u06af\u0627\u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06cc\u0686\u06ce\u06a9 \u0646\u06cc\u06cc\u06d5.',
    '\u067e\u0631\u0633 \u062f\u06d5\u06a9\u06d5\u0646: \u06a9\u06d5\u0646\u06af\u06ce \u0695\u06c6\u0698\u06cc \u062c\u06d5\u0632\u0627\u06cc\u06d5 \u062f\u06ce\u061f',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u062e\u06d5\u0646\u06d5 \u0633\u06d5\u0631\u0626\u0627\u06af\u0631.',
    '\u0628\u0686\u06ce\u0698\u0646 \u0626\u06d5\u0648 \u0626\u0627\u0632\u0627\u0631\u06d5\u062a\u0627\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06c6\u06cc \u0628\u06d5\u067e\u06d5\u0644\u06d5\u0628\u0648\u0646.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0646\u060c \u0634\u0648\u06ce\u0646\u06cc\u0627\u0646 \u0628\u0627\u063a\u0627\u062a \u0648 \u06a9\u0627\u0646\u06cc\u0627\u0648\u0646.',
    '\u0647\u06d5\u0631\u0686\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06c6\u06cc \u062f\u0627\u0646\u0627\u0648\u0646\u060c \u0648\u06d5\u0631\u06cc \u062f\u06d5\u06af\u0631\u0646\u061b \u0626\u06d5\u0648\u0627\u0646 \u067e\u06ce\u0634\u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646.',
    '\u0634\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06d5\u0645\u062a\u0631 \u062f\u06d5\u062e\u06d5\u0648\u062a\u0646.',
    '\u0628\u06d5\u0631\u06cc \u0628\u06d5\u06cc\u0627\u0646\u060c \u062f\u0627\u0648\u0627\u06cc \u0644\u06ce \u0628\u0648\u0631\u062f\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0644\u06d5\u0645\u0627\u06b5\u06cc\u0627\u0646\u062f\u0627 \u0628\u06d5\u0634\u06ce \u0647\u06d5\u0628\u0648\u0648 \u0628\u06c6 \u067e\u0627\u0631\u0633\u06d5\u06a9\u0627\u0646 \u0648 \u0628\u06ce \u0628\u06d5\u0634\u0627\u0646.',
    '\u0644\u06d5\u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0628\u06d5\u06b5\u06af\u06d5 \u0632\u06c6\u0631\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0628\u0695\u0648\u0627\u062f\u0627 \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631\u0648\u06d5\u0647\u0627 \u0644\u06d5 \u0646\u0627\u0648 \u062e\u06c6\u0634\u062a\u0627\u0646\u062f\u0627\u061b \u0626\u0627\u062e\u06c6 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0646\u0627\u0695\u0648\u0627\u0646\u0646\u061f',
    '\u0695\u06c6\u0632\u06cc\u062a\u0627\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u062a\u0627\u0646 \u067e\u06ce\u062f\u06d5\u062f\u0631\u06ce\u060c \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5.',
    '\u0633\u0627 \u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0626\u06d5\u0632\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u060c \u0626\u06d5\u0645\u06d5 \u0695\u0627\u0633\u062a\u06d5\u061b \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0628\u06c6\u062e\u06c6\u062a\u0627\u0646 \u062f\u0627\u062e\u06ce\u0648\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0628\u0627\u0633\u06cc \u0645\u06cc\u0648\u0627\u0646\u06d5\u0628\u06d5\u0695\u06ce\u0632\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u062a \u0628\u06c6\u0647\u0627\u062a\u0648\u0648\u06d5\u061f',
    '\u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0644\u06ce\u06cc \u0648\u06d5\u0698\u0648\u0631 \u06a9\u06d5\u0648\u062a\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0633\u06d5\u0644\u0627\u0645. \u06af\u0648\u062a\u06cc: \u0633\u06d5\u0644\u0627\u0645\u061b \u062f\u06d5\u0633\u062a\u06d5\u06cc\u06d5\u06a9\u06cc \u0646\u06d5\u0646\u0627\u0633\u0631\u0627\u0648\u0646.',
    '\u067e\u06d5\u0646\u0627\u0645\u06d5\u06a9\u06cc \u062e\u06c6\u06cc \u06af\u06d5\u06cc\u0627\u0646\u062f\u06d5 \u062e\u0627\u0648 \u062e\u06ce\u0632\u0627\u0646\u06cc \u0648 \u06af\u0648\u06ce\u0631\u06d5\u06a9\u06d5\u06cc\u06ce \u0686\u0627\u0642\u06cc \u0647\u0627\u0648\u0631\u062f.',
    '\u0648\u06d5\u062e\u062a\u06ce \u06a9\u06d5 \u0644\u06ce\u06cc \u0646\u0632\u06cc\u06a9 \u06a9\u0631\u062f\u0646\u060c \u06af\u0648\u062a\u06cc: \u0644\u06d5\u0628\u06d5\u0631\u0686\u06cc\u06d5 \u0646\u0627\u062e\u06c6\u0646\u061f',
    '\u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u062a\u0631\u0633\u06ce\u06a9\u06cc \u0695\u06ce \u0646\u06cc\u0634\u062a. \u06af\u0648\u062a\u06cc\u0627\u0646: \u062a\u06c6 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646 \u0645\u06d5\u0628\u06d5\u0648 \u0628\u0648\u0648\u0646\u06cc \u0644\u0627\u0648\u06ce\u06a9\u06cc \u0632\u0627\u0646\u0627\u06cc\u0627\u0646 \u0645\u0698\u062f\u06d5 \u067e\u06ce\u062f\u0627.',
    '\u0698\u0646\u06d5\u06a9\u06d5\u06cc \u0628\u06d5\u0647\u0627\u0648\u0627\u0631\u06d5\u0648\u06d5 \u0647\u0627\u062a \u0648 \u067e\u0648\u06a9\u06ce\u06a9\u06cc \u0644\u06d5\u062e\u06c6\u06cc \u0646\u0627\u0648 \u06af\u0648\u062a\u06cc: \u0645\u0646 \u067e\u06cc\u0631\u06ce\u0698\u0646\u06ce\u06a9\u0645 \u0628\u06ce \u0632\u06a9 \u0648\u0632\u0627.',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0626\u0627\u0648\u0627\u06cc\u0646\u06d5\u06cc \u06af\u0648\u062a\u0648\u06d5\u060c \u0626\u06d5\u0648\u062e\u06c6\u06cc \u06a9\u0627\u0631\u0632\u0627\u0646 \u0648 \u0632\u0627\u0646\u0627\u06cc\u06d5.',
    '\u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06a9\u0627\u0646! \u06a9\u0627\u0631\u062a\u0627\u0646 \u0686\u06cc\u06cc\u06d5\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u06a9\u0644 \u06a9\u0631\u0627\u0648\u06cc\u0646 \u0628\u06c6\u0633\u06d5\u0631 \u0647\u06c6\u0632\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646.',
    '\u062a\u0627 \u06af\u0632\u0645\u0648\u0644\u06d5 \u0642\u0648\u0695\u06cc \u0695\u06d5\u0642 \u0628\u0648\u06cc \u0648\u06d5\u06a9 \u0628\u06d5\u0631\u062f\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0628\u0627\u0631\u06ce\u0646\u06cc\u0646.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0647\u06d5\u0645\u0648\u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0644\u06ce\u062f\u0627\u0648\u0646\u060c \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0644\u0627\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0644\u06ce \u0648\u06d5\u062f\u06d5\u0631\u0646\u0627.',
    '\u0626\u06ce\u0645\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062a\u06d5\u0646\u06cc\u0627 \u0645\u0627\u06b5\u06ce\u06a9\u060c \u062e\u0648\u062f\u0627\u0646\u0627\u0633\u0645\u0627\u0646 \u062f\u06d5\u0633 \u0646\u06d5\u06a9\u06d5\u0648\u062a.',
    '\u0644\u06d5\u0648\u0634\u0648\u06ce\u0646\u06d5\u0634\u062f\u0627 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0628\u06d5\u062c\u06ce \u0647\u06ce\u0634\u062a\u060c \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5\u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06cc \u0628\u06d5\u0698\u0627\u0646 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5\u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u06cc \u0645\u0648\u0648\u0633\u0627\u0634\u062f\u0627\u060c \u06a9\u06d5 \u0628\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0648\u06d5 \u0628\u06d5\u0695\u06ce\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0644\u0627\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646.',
    '\u0628\u06d5\u0648\u0646\u06cc\u0627\u0632\u06d5 \u06a9\u06d5 \u062e\u0627\u0648\u06d5\u0646\u06cc \u0647\u06ce\u0632\u0648\u067e\u06ce\u0632\u06d5\u060c \u0628\u0627\u06cc\u06d5\u062e\u06cc \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0646\u06d5\u062f\u0627\u0648 \u06af\u0648\u062a\u06cc: \u062c\u0627\u062f\u0648\u06af\u06d5\u0631\u06ce\u06a9\u06d5 \u06cc\u0627\u0646 \u0644\u06cc\u0648\u06d5\u0628\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u062e\u06c6\u06cc \u0648 \u0644\u06d5\u0634\u06a9\u0631\u06d5\u06a9\u06d5\u06cc\u0645\u0627\u0646 \u06af\u0631\u062a \u0648 \u0641\u0695\u06ce\u0645\u0627\u0646 \u062f\u0627\u0646\u06d5 \u0646\u0627\u0648 \u062f\u06d5\u0631\u06cc\u0627\u0648\u06d5\u0648 \u06af\u0644\u06d5\u06cc\u06cc\u0634 \u0644\u06d5\u062e\u06c6\u06cc \u06a9\u0631\u0627.',
    '\u0644\u06d5\u0645\u06d5\u0695\u0639\u0627\u062f\u06cc\u0634\u060c \u06af\u0695\u06d5\u0628\u0627\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646.',
    '\u0647\u06d5\u0631\u0634\u062a\u06ce \u06a9\u06d5 \u0647\u0627\u062a\u06d5\u0628\u06d5\u0631\u06cc\u060c \u0648\u06d5\u06a9 \u067e\u06cc\u0634\u06d5\u06cc \u067e\u0648\u0627\u0648\u06cc \u0644\u06ce\u06a9\u0631\u062f.',
    '\u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0647\u06c6\u0632\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f\u06cc\u0634\u060c \u06a9\u06d5 \u067e\u06ce\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u06d5\u0646\u062f\u0631\u0627: \u0647\u06d5\u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0695\u0627\u06cc\u0628\u0648\u06ce\u0631\u0646.',
    '\u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627\u061b \u0647\u06d5\u062a\u0627 \u062f\u06cc\u062a\u06cc\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u0647\u06d5\u0648\u0631\u06d5\u062a\u0631\u06cc\u0634\u0642\u06d5\u06cc\u06ce \u06a9\u06d5\u0648\u062a\u0646.',
    '\u0646\u06d5\u06cc\u0627\u0646\u06a9\u0631\u0627 \u0695\u0627\u0633\u062a \u0628\u0646\u06d5\u0648\u06d5\u0648 \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0647\u0627\u062a.',
    '\u0647\u06c6\u0632\u06cc - \u0646\u0648\u0648\u062d\u06cc\u0634 \u0644\u06d5\u067e\u06ce\u0634\u0648\u0695\u0627 - [\u0628\u06d5\u0631\u063a\u06d5\u0632\u06d5\u0628\u06cc \u0626\u06ce\u0645\u06d5 \u06a9\u06d5\u0648\u062a\u0646] \u06a9\u06d5 \u06af\u06d5\u0644\u06ce\u06a9\u06cc \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u0645 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0628\u06d5 \u0647\u06ce\u0632\u06cc \u062e\u0648\u062f\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648 \u0628\u06ce \u0633\u06c6 \u067e\u06d5\u0631\u06d5\u0634\u06cc \u067e\u06ce\u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0632\u06d5\u0645\u06cc\u0646\u06cc\u0634 \u0626\u06ce\u0645\u06d5 \u0695\u0627\u0645\u0627\u0646 \u062e\u0633\u062a\u061b \u0626\u0627\u06cc \u06a9\u06d5 \u0686\u06d5\u0646\u062f \u0695\u0627\u062e\u06d5\u0631\u06cc \u0628\u0627\u0634\u06cc\u0646.',
    '\u0644\u06d5\u0647\u06d5\u0631\u0634\u062a\u06ce \u062c\u0648\u062a\u06ce\u06a9\u0645\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0628\u06cc\u0631\u06cc \u0644\u06ce \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0633\u0627 \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0628\u06d5\u0695\u0627\u06a9\u0631\u062f\u0646 \u0628\u06d5\u0631\u06d5\u0648\u062e\u0648\u0627\u0628\u0646\u060c \u06a9\u06d5 \u0644\u06d5\u0648\u0695\u0627 \u0645\u0646 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u062f\u06ce\u0646\u0645.',
    '\u0648\u06ce\u0695\u0627\u06cc \u062e\u0648\u062f\u0627\u0634 \u0647\u06cc\u0686 \u0634\u062a\u06cc \u062a\u0631 \u0645\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u061b \u0626\u06d5\u0648 \u0628\u06d5 \u0645\u0646\u06cc \u0633\u067e\u0627\u0631\u062f\u0648\u06d5 \u06d5 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062a\u0631\u0633\u06cc \u0626\u06d5\u0648\u062a\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06ce\u0646\u0645.',
    '\u0628\u06d5\u0631\u0644\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0647\u06d5\u0631\u0648\u0627\u0628\u0648\u06d5\u061b \u0647\u06d5\u0631\u06a9\u0627\u062a\u06ce \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6\u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u06af\u0648\u062a\u0648\u06cc\u0627\u0646\u06d5: \u06cc\u0627\u0646 \u062c\u0627\u062f\u0648\u06af\u06d5\u0631\u06d5\u060c \u06cc\u0627\u0646 \u0634\u06ce\u062a\u06d5.',
    '\u0645\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0648\u0642\u0633\u06d5 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u0648\u06d5\u061f [\u0646\u06d5\u060c \u0648\u0627\u0646\u06cc\u06d5] \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06c6\u062e\u06c6\u06cc\u0627\u0646 \u06af\u06d5\u0644\u0627\u0646\u06ce\u06a9\u06cc \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u0648\u0646.',
    '\u0633\u0627 \u062a\u06c6 \u0648\u0627\u0632 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0628\u06ce\u0646\u06d5\u061b \u06a9\u06d5\u0633\u06cc\u0634 \u0633\u06d5\u0631\u06a9\u06c6\u0646\u06d5\u06cc \u062a\u06c6 \u0646\u0627\u06a9\u0627.',
    '\u062a\u06c6 \u0647\u06d5\u0631 \u0648\u06d5\u0628\u06cc\u0631\u06cc\u0627\u0646 \u0628\u062e\u06d5\u0648\u06d5\u061b \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc \u0628\u06d5\u0647\u0631\u06d5\u062f\u06d5\u062f\u0627 \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646.',
    '\u0645\u0646 \u0647\u06d5\u0631\u0628\u06c6\u06cc\u06d5 \u062c\u0646\u062f\u06c6\u06a9\u06d5\u0648 \u0645\u0631\u06c6\u0645 \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u060c \u0628\u0645\u067e\u06d5\u0631\u0633\u062a\u0646.',
    '\u0645\u0646 \u0647\u06cc\u0686 \u0695\u06c6\u0632\u06cc\u0645 \u0644\u06d5\u0648\u0627\u0646 \u0646\u0627\u0648\u06ce \u0648 \u0646\u0627\u0645\u06af\u06d5\u0631\u06d5\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062e\u06c6\u0631\u0627\u06a9\u0645 \u062f\u06d5\u0646\u06ce.',
    '\u0647\u06d5\u0631\u062e\u0648\u062f\u0627 \u0695\u06c6\u0632\u06cc \u062f\u06d5\u0646\u06ce\u0631\u06ce \u0648 \u062e\u0648\u062f\u0627\u0646 \u0647\u06ce\u0632\u06ce\u06a9\u06cc \u067e\u062a\u06d5\u0648\u06d5.',
    '\u0628\u06d5\u0647\u0631\u06d5\u06cc \u0626\u06d5\u0648\u063a\u06d5\u062f\u0631\u06a9\u06d5\u0631\u0627\u0646\u06d5\u0634 \u0648\u06d5\u06a9 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u062f\u06d5\u0628\u06ce\u060c \u06a9\u06d5 \u0647\u0627\u0648\u0628\u0627\u0648\u06d5\u0695\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0648\u0646\u061b \u0626\u06cc\u062a\u0631 \u0644\u06ce\u0645 \u0628\u06d5\u067e\u06d5\u0644\u06d5 \u0646\u06d5\u0628\u0646.',
    '\u0648\u0627\u0648\u06d5\u06cc\u0644\u0627 \u0628\u06c6 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u060c \u0644\u06d5\u0648\u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0631\u0627\u0648\u06d5.',
    '\u0628\u06d5 \u06a9\u06ce\u0648\u06cc \u062a\u0648\u0648\u0631\u060c',
    '\u0628\u06d5 \u06a9\u062a\u06ce\u0628\u06ce \u06a9\u06d5 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5\u060c',
    '\u0644\u06d5 \u06a9\u06d5\u0648\u06d5\u06b5\u060c \u0628\u06b5\u0627\u0648 \u0628\u06c6\u062a\u06d5\u0648\u06d5\u061b',
    '\u0628\u06d5 \u0645\u0627\u06b5\u06d5 \u0626\u0627\u0648\u06d5\u062f\u0627\u0646\u06d5\u06a9\u06d5\u060c',
    '\u0628\u06d5\u0648 \u0628\u0627\u0646\u06d5\u06cc \u0628\u06b5\u0646\u062f \u06a9\u0631\u0627\u0648\u06d5\u060c',
    '\u0628\u06d5\u0648 \u062f\u06d5\u0631\u06cc\u0627\u06cc\u06d5\u06cc \u0628\u06b5\u0642 \u0626\u06d5\u062f\u0627\u060c',
    '\u0626\u0627\u0632\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0647\u06d5\u0631 \u0695\u0648\u0648 \u062f\u06d5\u062f\u0627\u061b',
    '\u06a9\u06d5\u0633\u06cc\u0634 \u067e\u06ce\u0634\u06cc \u067e\u06ce \u0646\u0627\u06af\u06cc\u0631\u06ce.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646 \u062a\u06ce\u06a9\u06d5\u0648\u06d5\u062f\u0627\u0648\u06ce\u060c',
    '\u0647\u06d5\u0645\u0648\u0648 \u0686\u06cc\u0627 \u0644\u06d5 \u0695\u06c6\u06cc\u06cc\u0646\u062f\u0627 \u067e\u06d5\u0644\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u061b',
    '\u0626\u06cc\u062a\u0631 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0686\u0648\u0648\u0628\u0648\u0648\u0646\u06d5 \u0646\u0627\u0648 \u067e\u0695 \u0648 \u067e\u0648\u0648\u0686 \u0648 \u06af\u06d5\u067e\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627\u061b',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0628\u06d5\u062f\u06d5\u06b5\u06d5\u06a9\u062f\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u0627\u06af\u0631\u06cc \u062f\u06c6\u0632\u06d5\u0642\u06cc\u0627\u0646 \u0695\u0627\u062f\u06d5\u06a9\u06ce\u0634\u0646.',
    '\u0626\u06d5\u0648\u06d5\u062a\u0627\u0646\u06ce \u0626\u06d5\u0648 \u0626\u0627\u06af\u0631\u06d5\u06cc \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f \u0647\u06d5\u0628\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0645\u06d5 \u062c\u0627\u062f\u0648\u0648\u06d5\u060c \u06cc\u0627\u0646 \u0626\u06ce\u0648\u06d5 \u06a9\u06c6\u0631 \u0628\u0648\u0648\u0646\u06d5 \u0648 \u0646\u0627\u0628\u06cc\u0646\u0646\u061f',
    '\u0628\u0686\u0646\u06d5 \u0646\u0627\u0648\u06cc\u061b \u0647\u06d5\u06b5 \u0628\u06a9\u06d5\u0646 \u06cc\u0627\u062e\u06c6 \u0647\u06d5\u06b5 \u0646\u06d5\u06a9\u06d5\u0646\u060c \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u06a9\u0631\u062f\u0648\u0648\u062a\u0627\u0646\u06d5\u060c \u0633\u0632\u0627 \u062f\u06d5\u062f\u0631\u06ce\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u0627 \u062e\u0648\u062f\u06ce \u062a\u0631\u0633 \u0628\u0648\u0648\u0646\u060c \u0648\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0648 \u062e\u06c6\u0634\u06cc\u062f\u0627.',
    '\u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u0646 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u067e\u06ce\u06cc \u0628\u06d5\u062e\u0634\u06cc\u0648\u0646\u061b \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0634\u06cc \u067e\u0627\u0631\u0627\u0632\u062a\u0646.',
    '\u0644\u06d5 \u0647\u06d5\u0645\u0628\u06d5\u0631 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u0628\u062e\u06c6\u0646 \u0648 \u0648\u06d5\u062e\u06c6\u0646\u060c \u0646\u06c6\u0634\u062a\u0627\u0646 \u0628\u06ce.',
    '\u0644\u06d5 \u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a\u06cc \u0644\u06d5\u067e\u06d5\u0646\u0627\u06cc \u06cc\u06d5\u06a9\u060c \u067e\u0627\u06b5 \u06a9\u06d5\u0641\u062a\u0648\u0648\u0646 \u0648 \u06a9\u06cc\u0698\u06cc \u0633\u067e\u06cc \u0686\u0627\u0648\u0628\u06d5\u06b5\u06d5\u06a9\u0645\u0627\u0646 \u067e\u06ce \u0628\u06d5\u062e\u0634\u06cc\u0648\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u0634\u06cc\u0627\u0646 \u0686\u0648\u0648\u0646\u06d5 \u0633\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0626\u06d5\u0648\u0627\u0646\u060c \u0645\u06d5 \u062a\u06c6\u0631\u06d5\u0645\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0648\u06d5\u06a9 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u06cc\u0646\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0648 \u0644\u06d5\u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0647\u06cc\u0686 \u0634\u062a\u06ce \u06a9\u06d5\u0645 \u0646\u0627\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5. \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u06ce \u0628\u0627\u0631\u0645\u062a\u06d5\u06cc \u06a9\u0627\u0631\u06cc \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0647\u06d5\u0631 \u0645\u06cc\u0648\u06d5\u06cc\u06d5\u06a9 \u0648 \u0647\u06d5\u0631 \u06af\u06c6\u0634\u062a\u06ce \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u067e\u06ce\u0645\u0627\u0646 \u062f\u0627\u0648\u0646.',
    '\u067e\u06cc\u0627\u06b5\u06d5 \u067e\u06ce\u06a9\u062a\u0631 \u062f\u06d5\u06af\u06c6\u0695\u0646\u06d5\u0648\u06d5 \u0648 \u0646\u06d5 \u0647\u06d5\u0631\u0632\u06d5\u0648\u06ce\u0698\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5 \u0648 \u0646\u06d5 \u0647\u06c6\u06cc \u062a\u0648\u0648\u0634\u06cc \u06af\u0648\u0646\u0627\u0647\u0640 \u0628\u0648\u0648\u0646\u06cc.',
    '\u062a\u0627\u0632\u06d5 \u0644\u0627\u0648\u0627\u0646\u06cc \u062e\u0648\u06b5\u0627\u0645\u06cc\u0627\u0646 \u0628\u06d5\u062f\u06d5\u0648\u0631\u062f\u0627 \u062f\u06ce\u0646 \u0648 \u062f\u06d5\u0686\u0646\u061b \u0647\u06d5\u0631 \u0626\u06ce\u0698\u06cc \u062f\u0627\u0646\u06d5\u06cc \u0645\u0631\u0648\u0627\u0631\u06cc\u0646 \u0644\u06d5 \u0642\u0627\u067e\u06c6\u0695\u062f\u0627.',
    '\u0695\u0648\u0648 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0648\u062a\u0648\u0648\u06ce\u0698\u06cc\u0627\u0646\u06d5.',
    '\u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u0644\u06d5\u0646\u0627\u0648 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646 \u0628\u0648\u0648\u06cc\u0646.',
    '\u0626\u06cc\u062a\u0631 \u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u06cc\u0646 \u0648 \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u06af\u0695\u06d5 \u0648 \u0647\u0627\u06b5\u0627\u0648\u06cc \u067e\u0627\u0631\u0627\u0632\u062a\u06cc\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06d5\u0631\u06ce \u0647\u0627\u0648\u0627\u0631\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06d5\u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u0647\u06d5\u0631\u0626\u06d5\u0648\u06cc\u0634\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0686\u0627\u06a9\u06d5\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0633\u0627 \u0648\u06d5\u0628\u06cc\u0631\u06cc\u0627\u0646 \u0628\u062e\u06d5\u0631\u06d5\u0648\u06d5 - \u0644\u06d5 \u0633\u0627\u06cc\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0695\u0627 - \u062a\u06c6 \u0646\u06d5 \u0641\u0627\u06b5 \u0628\u06cc\u0646\u06cc \u0646\u06d5 \u062f\u06cc\u0646\u06cc.',
    '\u06cc\u0627\u0646 \u067e\u06ce\u062a \u0626\u06ce\u0698\u0646: \u0628\u0648\u06ce\u0698\u06ce\u06a9\u06d5 \u0648 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc\u0646 \u0647\u0627\u06a9\u0627 \u0634\u062a\u06ce\u06a9\u06cc \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06ce\u0648\u06d5 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646\u060c \u0626\u06d5\u0648\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0648\u06d5\u0644 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0644\u06d5 \u0695\u0648\u0648\u06cc \u062e\u06d5\u0648\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0626\u06ce\u0698\u0646\u060c \u06cc\u0627\u0646 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc \u0628\u0644\u06d5\u0648\u06d5\u0632\u0646\u061f',
    '\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646 \u0647\u06d5\u06b5\u06cc \u0628\u06d5\u0633\u062a\u0648\u0648\u06d5. \u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06d5\u0648\u0627\u0646 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0627\u0633\u062a \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u0627 \u0648\u062a\u0627\u0631\u06ce \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06d5\u0645 \u0642\u0648\u0695\u0626\u0627\u0646\u06d5 \u0628\u06ce\u0646\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u0646\u06d5\u0628\u0648\u0648 \u0647\u0627\u062a\u0648\u0648\u0646\u06d5 \u062f\u06cc\u061f \u06cc\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u061f',
    '\u06cc\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u0646 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u061f \u0646\u06d5\u062e\u06ce\u0631\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0646\u0627\u0633\u06d5\u0644\u0645\u06ce\u0646\u0646.',
    '\u06cc\u0627\u0646 \u06af\u06d5\u0646\u062c\u06cc\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0648\u0627 \u0644\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u061f \u06cc\u0627\u0646 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u0645\u06d5 \u06a9\u0627\u0631\u06d5\u0646\u061f',
    '\u06cc\u0627\u0646 \u067e\u06d5\u06cc\u0698\u06d5\u06cc\u06ce \u0648\u0627\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0628\u06cc\u0646\u06ce\u0646 \u0648 \u06af\u0648\u06ce \u0695\u0627\u0628\u062f\u06ce\u0631\u0646\u061f \u06a9\u06ce \u0644\u06d5\u0648\u0627\u0646 \u06a9\u06c6\u06b5\u06d5\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0628\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06d5\u06a9\u06cc \u0695\u0648\u0648\u0646 \u0628\u06ce\u0646\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06cc\u0698\u06d5\u06a9\u0627\u0646 \u0628\u06c6 \u062e\u0648\u0627\u0646 \u0648 \u06a9\u0648\u0695\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0628\u06d5\u0634\u06cc \u0626\u06ce\u0648\u06d5\u0646\u061f',
    '\u06cc\u0627\u0646 \u0628\u06c6 \u0645\u0632\u06d5 \u062f\u0627\u0648\u0627\u06cc \u0634\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u06cc\u060c \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0698\u06ce\u0631\u06cc \u062f\u06d5\u0631 \u0646\u06d5\u0686\u0646 \u0648 \u0628\u06c6\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u06af\u0631\u0627\u0646 \u0628\u0648\u06d5\u0633\u062a\u06ce\u061f',
    '\u06cc\u0627\u0646 \u0646\u0647\u06ce\u0646\u06cc \u0648\u0627 \u0644\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u062f\u06d5\u0646\u0648\u0648\u0633\u0646\u061f',
    '\u06cc\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u0641\u06ce\u06b5\u06ce\u06a9 \u0628\u06a9\u06d5\u0646\u061f \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u0641\u06ce\u06b5\u06cc\u0627\u0646 \u0644\u06ce\u06a9\u0631\u0627\u0648\u06d5.',
    '\u06cc\u0627\u0646 \u0628\u06ce\u0698\u06af\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u060c \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u06cc\u06d5\u06a9\u06cc \u062a\u0631\u06cc\u0627\u0646 \u0647\u06d5\u0633\u061f \u062e\u0648\u0627 \u067e\u0627\u06a9\u062a\u0631\u06d5 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062f\u0627\u062f\u06d5\u0646\u06ce\u0646\u061f',
    '\u06af\u06d5\u0631 \u0628\u0634\u0628\u06cc\u0646\u0646 \u067e\u0627\u0631\u0686\u06d5\u06cc\u06d5\u06a9 \u0644\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5 \u062f\u0627\u062f\u06d5\u06a9\u06d5\u0648\u06ce\u060c \u0626\u06ce\u0698\u0646: \u0647\u06d5\u0648\u0631\u06ce\u06a9\u06cc \u067e\u0695\u067e\u0634\u062a\u06d5.',
    '\u0633\u0627 \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627\u0632\u0628\u06ce\u0646\u06d5\u060c \u062a\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0695\u06c6\u0698\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0628\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0647\u06d5\u0633\u062a\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u0631\u062f\u0627 \u0646\u0627\u0645\u06ce\u0646\u06ce.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u062d\u06cc\u0644\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0646\u0627\u062f\u0627\u062a\u06ce \u0648 \u06a9\u06d5\u0633\u06cc\u0634 \u062f\u06d5 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06cc\u0634\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0647\u06d5\u0642\u06cc \u0628\u0648\u0648\u06d5\u060c \u062c\u06af\u06d5 \u0644\u06d5\u0645\u06d5\u0634 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u06ce \u062a\u0631\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0628\u06c6 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06d5\u060c \u062a\u06c6 \u0628\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u06cc \u0626\u06ce\u0645\u06d5\u0648\u06d5\u06cc\u061b \u0647\u06d5\u0631\u06a9\u0627\u062a\u06ce \u0644\u06d5\u062c\u06ce \u0695\u0627\u062f\u06d5\u0628\u06cc\u060c \u067e\u06d5\u0633\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u062f\u06d5.',
    '\u0644\u06d5\u0634\u06d5\u0648\u06cc\u0634\u062f\u0627 \u0634\u0648\u06a9\u0631\u06cc \u0628\u06a9\u06d5 \u0648 \u067e\u06d5\u0633\u0646\u06cc \u0628\u062f\u06d5\u061b \u0644\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u0634\u062f\u0627 \u06a9\u06d5 \u0647\u06d5\u0633\u0627\u0631\u06d5 \u0626\u0627\u0648\u0627 \u062f\u06d5\u0628\u0646.',
    '\u0628\u06d5 \u0626\u06d5\u0633\u062a\u06ce\u0631\u06d5 \u06a9\u0627\u062a\u06ce \u0628\u06d5\u0631\u06d5\u0648 \u0626\u0627\u0633\u06c6 \u062f\u06d5\u062e\u0632\u06ce\u060c',
    '\u06cc\u0627\u0631\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u0646\u06d5 \u06af\u0648\u0645 \u0628\u0648\u0648\u06d5\u060c \u0646\u06d5 \u0695\u06ce\u0634\u06cc \u0647\u06d5\u06b5\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u06cc \u0646\u0627\u062e\u06ce\u0648\u06ce.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0626\u06ce\u0698\u06ce\u060c \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5 \u062a\u06ce\u06af\u06d5\u06cc\u06d5\u0646\u0631\u0627\u0648\u06d5.',
    '\u0641\u0631\u0634\u062a\u06d5\u06cc\u06ce \u0641\u0631\u06d5 \u062a\u0648\u0627\u0646\u0627 \u0641\u06ce\u0631\u06cc \u06a9\u0631\u062f.',
    '\u0641\u0631\u0634\u062a\u06d5\u06cc\u06ce \u0628\u06d5\u0626\u0627\u0648\u06d5\u0632 \u0648 \u0641\u0631\u06d5 \u0632\u0627\u0646\u0627\u060c \u06a9\u06d5 \u0628\u06d5 \u0634\u06a9\u06b5\u06cc \u062e\u06c6\u06cc \u0695\u0627\u0648\u06ce\u0633\u062a\u0627.',
    '[\u0647\u06d5\u0648\u06d5\u06b5 \u062c\u0627\u0631\u06ce] \u0644\u06d5 \u0626\u0627\u0633\u06c6\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u06d5\u0631\u0632 \u0628\u0648\u0648\u061b',
    '\u0644\u06d5 \u067e\u0627\u0634\u0627 \u0646\u0632\u06cc\u06a9 \u06a9\u06d5\u0648\u062a\u06d5\u0648\u06d5 \u0648 \u0634\u06c6\u0695\u06d5\u0648\u06d5 \u0628\u0648\u0648\u061b',
    '\u0628\u06d5\u0642\u06d5\u062f \u0645\u0627\u06b5\u06cc \u062f\u0648\u0648\u06a9\u06d5\u0648\u0627\u0646\u0627\u0646 \u06cc\u0627 \u0646\u0632\u06cc\u06a9\u062a\u0631\u061b',
    '\u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0645\u06d5\u0628\u06d5\u0633\u062a\u06ce \u06a9\u06d5 \u0648\u0627 \u0648\u06cc\u0633\u062a\u06cc \u0628\u06d5 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u06cc \u0695\u0627\u06cc \u06af\u06d5\u06cc\u06d5\u0646\u06ce\u060c \u067e\u06ce\u06cc \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f.',
    '\u062f\u06b5\u06cc \u0626\u06d5\u0648 \u0647\u06d5\u0631 \u0686\u06cc \u062f\u06cc\u062a\u0648\u0648\u06cc\u06d5\u060c \u062f\u0631\u06c6 \u0646\u06d5\u0628\u0648\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0648\u06cc \u062f\u06cc\u0648\u06cc\u06d5 \u0626\u06ce\u0648\u06d5 \u06a9\u06ce\u0634\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u062c\u0627\u0631\u06ce\u06a9\u06cc \u062f\u06cc\u06a9\u06d5\u0634\u06cc \u062f\u06cc\u0648\u06d5\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5\u0634 \u062f\u0627\u067e\u06d5\u0695\u06cc\u0648\u06d5\u061b',
    '\u0644\u06d5\u0644\u0627\u06cc \u062f\u0627\u0631\u06d5\u06a9\u0646\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648 \u067e\u06d5\u0695\u06d5\u0648\u06d5\u060c',
    '\u06a9\u06d5 \u0644\u06d5\u067e\u0627\u06b5 \u0634\u0648\u06ce\u0646\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5.',
    '\u0644\u06d5 \u06a9\u0627\u062a\u06ce\u06a9\u0627 \u06a9\u0646\u0627\u0631\u06d5\u06a9\u06d5 \u062f\u0627\u06cc \u067e\u06c6\u0634\u06cc\u0648\u06d5\u060c \u0626\u06d5\u0648\u06cc \u06a9\u06d5 \u062f\u06d5\u0628\u06ce \u062f\u0627\u06cc \u067e\u06c6\u0634\u06ce.',
    '\u0633\u06c6\u0645\u0627\u06cc \u0644\u06ce \u0646\u06d5\u06af\u0648\u06ce\u0632\u062a\u06c6\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06cc\u0686 \u0695\u06d5\u0634\u06a9\u06d5 \u0648 \u067e\u06ce\u0634\u06a9\u06d5\u0634\u06cc \u0646\u06d5\u06a9\u0631\u062f.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0632\u06c6\u0631\u06cc\u0634 \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u06a9\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u062f\u06cc\u062a\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0644\u0627\u062a \u0648 \u0639\u0648\u0632\u0632\u0627\u062a\u0627\u0646 \u062f\u06cc\u0648\u06d5\u061f',
    '\u0633\u06ce\u0647\u06d5\u0645\u06cc\u0634\u06cc\u0627\u0646 \u06a9\u06d5 \u0645\u06d5\u0646\u0627\u062a\u06d5 \u0648 \u06cc\u06d5\u06a9\u06ce \u062a\u0631 \u0644\u06d5 \u0628\u0648\u062a\u06d5\u06a9\u0627\u0646\u06d5\u061f',
    '\u0626\u0627\u062e\u06c6 \u0632\u0627\u0631\u06c6\u06cc \u0626\u06ce\u0648\u06d5 \u0646\u06ce\u0631\u06d5 \u0648 \u062e\u0648\u0627\u0634 \u06a9\u06cc\u0698\u06cc \u0647\u06d5\u0633\u061f',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0648\u0627 \u0628\u06ce\u060c \u0628\u06d5\u0634 \u06a9\u0631\u062f\u0646\u06ce \u0646\u0627\u0695\u06d5\u0648\u0627\u06cc\u06d5.',
    '\u062a\u06d5\u0646\u06cc\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0686\u06d5\u0646\u062f \u0646\u0627\u0648\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0648 \u0628\u0627\u0628 \u0648 \u0628\u0627\u067e\u06cc\u0631\u062a\u0627\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u062a\u0627\u0646 \u067e\u06ce \u0646\u0627\u0648\u0646\u0627\u0648\u0646 \u0648 \u062e\u0648\u062f\u0627\u0634 \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u067e\u062a\u06d5\u0648\u06cc \u0644\u06d5\u0645\u06d5\u0695 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0648\u06d5 \u0646\u06d5\u0646\u0627\u0631\u062f. \u0647\u06d5\u0631 \u0648\u06d5\u062f\u0648\u0648\u06cc \u06af\u0648\u0645\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u062f\u06b5\u06cc\u0627\u0646 \u062f\u06d5\u062e\u0648\u0627\u0632\u06ce\u061b \u06a9\u06d5 \u0686\u06cc \u0644\u06d5\u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0634\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0695\u06ce\u06af\u06d5\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u0631\u0627\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0645\u0631\u06c6 \u0686\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u06a9\u0627\u060c \u0628\u06c6\u06cc \u067e\u06ce\u06a9 \u062f\u06ce\u061f',
    '\u0626\u0627\u062e\u0631\u06cc\u0646 \u0648 \u0647\u06d5\u0648\u06d5\u06b5\u06cc\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627\u0646.',
    '\u0644\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5\u062f\u0627\u060c \u0686\u06d5\u0646\u062f \u0628\u06d5 \u0686\u06d5\u0646\u062f \u0641\u0631\u06cc\u0634\u062a\u06d5 \u0647\u06d5\u06cc\u06d5\u061b \u06a9\u06d5\u0633\u06cc\u0627\u0646 \u062a\u06a9\u0627\u06cc \u0644\u06ce \u0646\u0627\u06af\u06cc\u0631\u06ce\u061b \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0633\u06ce \u062f\u0648\u0627\u06cc\u06cc \u062e\u0648\u062f\u0627 \u0626\u06cc\u0632\u0646\u06cc \u0628\u062f\u0627 \u0648 \u062a\u06a9\u0627 \u0628\u06c6 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06a9\u0627 \u06a9\u06d5 \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u06a9\u0627 \u0648 \u067e\u06ce \u0695\u0627\u0632\u06cc \u0628\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u0695\u0648\u0627 \u0628\u06d5 \u067e\u0627\u0634\u06d5\u0695\u06c6\u0698 \u0646\u0627\u06a9\u06d5\u0646\u060c \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u0646\u0627\u0648\u06cc \u06a9\u0686\u0627\u0646 \u0646\u0627\u0648 \u062f\u06d5\u0628\u06d5\u0646.',
    '\u06a9\u06d5 \u0647\u06cc\u0686\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u0632\u0627\u0646\u0646\u061b \u0647\u06d5\u0631 \u0648\u06d5\u0634\u0648\u06ce\u0646 \u06af\u0648\u0645\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u06af\u0648\u0645\u0627\u0646\u06cc\u0634 \u062c\u06ce\u06cc \u0695\u0627\u0633\u062a \u0646\u0627\u06af\u0631\u06ce.',
    '\u062a\u06c6 \u0644\u06d5\u0648 \u0645\u0631\u06c6\u06cc\u06d5 \u0648\u0627\u0632 \u0628\u06ce\u0646\u06d5 \u06a9\u06d5 \u0644\u06d5 \u06cc\u0627\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u06a9\u0631\u062f\u0646\u060c \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u06ce \u0648 \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5 \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5\u060c \u0647\u06cc\u0686\u06cc \u062a\u0631\u06cc \u0646\u06d5\u0648\u06cc\u0633\u062a\u0648\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u0646\u062f\u06d5\u06cc \u0644\u06ce \u062f\u06d5\u0632\u0627\u0646\u0646. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0632\u0627\u0646\u0627\u062a\u0631\u06d5 \u06a9\u06d5 \u06a9\u06ce \u0695\u06ce\u06af\u06d5\u06cc \u0644\u06ce \u062a\u06ce\u06a9\u0686\u0648\u0648\u06d5 \u0648 \u06a9\u06ce\u0634 \u0644\u06d5\u0633\u06d5\u0631 \u0695\u06ce\u06af\u06d5\u06cc \u0626\u06d5\u0648 \u0686\u0648\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u06af\u0634 \u0628\u06c6 \u062e\u0648\u062f\u0627\u0646\u061b \u062a\u0627 \u0633\u0632\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u062f\u0627 \u06a9\u06d5 \u062e\u0631\u0627\u067e\u06d5\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0686\u0627\u06a9\u06d5\u06cc \u0628\u0627\u0634\u06cc\u0634 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u0628\u06a9\u0627.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u0648\u0648\u0631 \u062f\u06d5\u06af\u0631\u0646 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u06cc \u0645\u06d5\u0632\u0646 \u0648 \u06a9\u0627\u0631\u06cc \u06a9\u0631\u06ce\u062a\u061b \u0645\u06d5\u0631 \u06af\u0648\u0646\u0627\u0647\u06cc \u0648\u0631\u062f\u06cc\u0644\u06d5 \u0628\u06ce. \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0631\u0641\u0631\u06d5\u0647\u06d5 \u0648 \u0626\u06d5\u0648\u06cc\u0634 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0628\u0627\u0634\u062a\u0631 \u062f\u06d5\u0646\u0627\u0633\u06ce\u061b \u0648\u06d5\u062e\u062a\u06ce \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5 \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u06a9\u06d5 \u062f\u06d5\u067e\u06ce\u0634\u062f\u0627 \u067e\u06cc\u0632\u06d5\u06cc\u06d5\u06a9 \u0628\u0648\u0648\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0632\u06af\u06cc \u062f\u0627\u06cc\u06a9\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646. \u0647\u06cc\u0686 \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0628\u06c6 \u062e\u06c6\u06cc \u0628\u0627\u0634\u062a\u0631\u062a\u0627\u0646 \u062f\u06d5\u0646\u0627\u0633\u06ce \u06a9\u06d5 \u06a9\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u0627\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u062a\u06c6 \u0626\u06d5\u0648 \u0645\u0631\u06c6\u06cc\u06d5\u062a \u062f\u06cc \u06a9\u06d5 \u067e\u0634\u062a\u06cc \u0647\u06d5\u06b5\u06a9\u0631\u062f \u0648 \u0695\u06c6\u06cc\u06cc\u061f',
    '\u06a9\u06d5\u0645\u06cc \u0628\u06d5\u062e\u0634\u06cc \u0648 \u0686\u0631\u0648\u0648\u06a9\u06cc \u06a9\u0631\u062f.',
    '\u0626\u0627\u062e\u06c6 \u0628\u06d5 \u0646\u06d5\u062f\u06cc\u0627\u0631 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0648 \u062e\u06c6\u06cc \u062f\u06d5\u0628\u06cc\u0646\u06ce\u061f',
    '\u06cc\u0627\u0646 \u0644\u06d5\u0648\u06d5 \u0626\u0627\u06af\u0627 \u0646\u06d5\u06a9\u0631\u0627\u0648\u06d5\u060c \u0644\u06d5 \u0695\u0648\u0648\u067e\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0648\u0648\u0633\u0627 \u0686\u06cc \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u0646\u061f',
    '\u06cc\u0627\u0646 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u060c \u06a9\u06d5 \u0626\u06d5\u0631\u06a9\u06cc \u062e\u06c6\u06cc \u0628\u06d5 \u062a\u06d5\u0648\u0627\u0648\u06cc \u0628\u06d5\u062c\u06ce \u0647\u06ce\u0646\u0627\u061f',
    '\u06a9\u06d5 \u06a9\u06d5\u0633 \u0626\u06c6\u0628\u0627\u06b5\u06cc \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u0648\u06d5\u0633\u062a\u06c6 \u0646\u0627\u06af\u0631\u06ce\u061b',
    '\u0645\u0631\u06c6 \u0628\u06c6 \u062e\u06c6\u06cc \u062e\u06d5\u0628\u0627\u062a \u0646\u06d5\u06a9\u0627\u060c \u0647\u06cc\u0686 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u067e\u06ce \u0646\u0627\u06af\u0627.',
    '\u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc\u0634 \u0645\u0627\u0646\u062f\u0648\u0648 \u0628\u06ce\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u0631 \u067e\u06ce\u062f\u06d5\u06af\u0627.',
    '\u0644\u06d5\u067e\u0627\u0634\u0627\u0646\u06cc\u0634 - \u0628\u06d5\u0628\u0627\u0634\u062a\u0631\u06cc\u0646 \u0634\u06ce\u0648\u06d5 - \u067e\u0627\u062f\u0627\u0634\u06cc \u067e\u06ce\u062f\u06d5\u062f\u0631\u06ce.',
    '\u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u0634 \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062f\u06d5\u062e\u0627\u062a\u06d5 \u062e\u06d5\u0646\u06cc\u0646 \u0648 \u062f\u06d5\u06af\u0631\u06cc\u0646\u06ce.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0698\u06cc\u0627\u0646 \u062f\u06d5\u062f\u0627 \u0648 \u062f\u06d5\u0634\u0645\u0631\u06ce\u0646\u06ce.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0648\u06b5\u0641\u06cc \u0646\u06ce\u0631 \u0648 \u0645\u06ce\u0648\u06cc\u0646\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b',
    '\u0644\u06d5\u0648 \u062a\u0646\u06c6\u06a9\u06d5\u06cc \u06a9\u06d5 \u0695\u0698\u0627\u0648\u06d5.',
    '\u0644\u06d5\u0633\u06d5\u0631\u06cc\u06d5\u062a\u06cc \u062f\u06cc\u0633\u0627\u0646 \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0647\u06cc\u0686 \u0646\u0627\u06af\u0648\u0632\u0648\u0648\u0631\u06cc \u0646\u06d5\u0647\u06ce\u0634\u062a \u0648 \u062f\u06d5\u0633\u0645\u0627\u06cc\u06d5\u0634\u06cc \u0628\u06d5 \u0626\u06ce\u0648\u06d5\u062f\u0627.',
    '\u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u0634\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u06d5\u0644\u0627\u0648\u06ce\u0698\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0626\u0627\u0633\u06d5\u0648\u0627\u0631\u06cc \u0639\u0627\u062f\u06cc \u06cc\u06d5\u06a9\u06d5\u0645\u06cc \u0628\u0695\u06cc\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0633\u06d5\u0645\u0648\u0648\u062f\u06cc\u0634 \u062c\u0648\u0648\u0642\u06d5\u0648\u0627\u0631\u06cc \u0646\u06d5\u0647\u06ce\u0634\u062a\u0648\u0648\u06d5.',
    '\u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d\u06cc\u0634 - \u0644\u06d5\u0628\u06d5\u0631\u06ce\u062f\u0627 - \u06a9\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631 \u0648 \u0644\u0627\u0633\u0627\u0631\u062a\u0631 \u0628\u0648\u0648\u0646.',
    '\u0626\u0627\u0648\u0627\u06cc\u06cc \u0647\u06c6\u0632\u06cc \u0644\u0648\u0648\u062a\u06cc\u0634\u06cc \u0633\u06d5\u0631\u06d5\u0648\u0698\u06ce\u0631 \u06a9\u0631\u062f.',
    '\u0647\u06d5\u0631\u0686\u06cc \u062f\u06d5\u0628\u0648\u0648 \u0628\u06d5\u0631\u062e\u06c6 \u0628\u062f\u0631\u06ce\u060c \u0648\u06d5\u0628\u0646 \u062e\u06c6\u06cc\u062f\u0627.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u06a9\u06d5 \u0626\u06d5\u0648 \u0647\u06d5\u0645\u0648\u0648 \u0686\u0627\u06a9\u06d5\u06cc \u0647\u06d5\u06cc\u06d5\u060c \u062a\u06c6 \u0644\u06d5 \u06a9\u0627\u0645\u06cc\u0627\u0646 \u0628\u06d5 \u06af\u0648\u0645\u0627\u0646\u06cc\u061f',
    '\u0626\u06d5\u0645\u06d5 \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631\u0647\u06ce\u0646\u06d5\u0631\u0627\u0646\u06d5\u061b \u0648\u06d5\u06a9 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u0627\u0646\u06cc \u067e\u06ce\u0634\u0648\u0648.',
    '\u0695\u0627\u0628\u0648\u0648\u0646\u06cc \u0633\u06d5\u06b5\u0627 \u0646\u0632\u06cc\u06a9\u06d5.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u06a9\u06d5\u0633 \u067e\u06d5\u0631\u062f\u06d5\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0644\u0627\u0646\u0627\u062f\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0645 \u0648\u062a\u0627\u0631\u06d5 \u0633\u06d5\u06cc\u0631 \u0645\u0627\u0648\u0646\u061f',
    '\u067e\u06ce\u06a9\u06d5\u0646\u06cc\u0646\u062a\u0627\u0646\u06d5 \u0648 \u0646\u0627\u06af\u0631\u06cc\u06d5\u0646\u061f',
    '\u0647\u06d5\u0631 \u0628\u06d5 \u06af\u0627\u06b5\u062a\u06d5 \u0695\u0627\u06cc \u062f\u06d5\u0628\u0648\u06ce\u0631\u0646.',
    '\u062f\u06d5\u0633\u0627 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0633\u0648\u0698\u062f\u06d5 \u0628\u06d5\u0631\u0646 \u0648 \u0628\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646.',
    '\u0648\u0627 \u067e\u06d5\u0633\u06b5\u0627\u0646 \u0646\u0632\u06cc\u06a9 \u06a9\u06d5\u0648\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u06cc\u06a4 \u0628\u0648\u0648 \u0628\u06d5 \u062f\u0648\u0648 \u06a9\u06d5\u0631\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9 \u0628\u0628\u06cc\u0646\u0646\u060c \u0628\u0695\u0648\u0627 \u0646\u0627\u06a9\u06d5\u0646 \u0648 \u062f\u0628\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u062c\u0627\u062f\u0648\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06cc\u062a\u0631 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627 \u0648 \u0634\u0648\u06ce\u0646 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u06d5\u0648\u062a\u0646. \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06d5\u0634 \u0634\u0648\u06ce\u0646\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0646\u06af \u0648 \u0628\u0627\u0633\u06cc \u0628\u06d5\u0631\u06ce \u0628\u06cc\u0633\u062a\u0648\u0648\u0646\u060c \u06a9\u06d5 \u062f\u06d5\u0628\u0648\u0648 \u062a\u0631\u0633\u06cc\u0627\u0646 \u0628\u06ce\u062a\u06d5 \u0628\u06d5\u0631.',
    '\u0626\u06d5\u0645\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u062a\u06d5\u0648\u0627\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0645 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u0627\u0646\u06d5 \u0628\u06d5\u0647\u0631\u06d5 \u0646\u0627\u062f\u06d5\u0646!',
    '\u0633\u0627 \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627\u0632 \u0628\u06ce\u0646\u06d5\u061b \u0695\u06c6\u0698\u06ce \u0628\u0627\u0646\u06af\u062f\u06d5\u0631 \u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627 \u0628\u06c6 \u0695\u0648\u0648\u062f\u0627\u0648\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0646\u0627\u062e\u06c6\u0634.',
    '\u0686\u0627\u0648\u0634\u06c6\u0695 \u0648 \u06a9\u0632\u060c \u0644\u06d5 \u06af\u06c6\u0695\u0633\u062a\u0627\u0646 \u062f\u06d5\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u061b \u0626\u06ce\u0698\u06cc \u06a9\u0648\u0644\u0644\u06d5\u0646 \u0628\u06b5\u0627\u0648 \u0628\u0648\u0648\u06af\u0646.',
    '\u0647\u06d5\u06b5\u06d5\u062f\u0627\u0648\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u0628\u0627\u0646\u06af\u062f\u06d5\u0631\u06d5\u06a9\u06d5 \u062f\u06d5\u0686\u0646. \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u0686\u06d5\u062a\u0648\u0648\u0646\u06d5.',
    '\u0628\u06d5\u0631\u0644\u06d5\u0645\u0627\u0646\u06d5\u0634 \u0647\u06c6\u0632\u06cc \u0646\u0648\u0648\u062d \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648. \u0639\u06d5\u0628\u062f\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0648 \u0646\u0627\u0628\u0648\u0648 \u062f\u0631\u06c6\u0632\u0646 \u0648 \u0648 \u06af\u0648\u062a\u06cc\u0627\u0646 \u062f\u06cc\u0646\u06d5 \u0648 \u06af\u06d5\u0641\u06cc\u0627\u0646 \u0644\u06ce \u06a9\u0631\u062f.',
    '\u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u067e\u0627\u0695\u0627\u0648\u06d5\u060c \u06a9\u06d5: \u0645\u0646 \u0634\u06a9\u0633\u062a\u0645 \u062e\u0648\u0627\u0631\u062f\u0648\u06d5\u060c \u0647\u0627\u0646\u0627\u0645 \u0628\u06d5 \u062a\u06c6.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0631\u06af\u0627\u06a9\u0627\u0646\u06cc \u0628\u06d5\u0631\u0632\u0627\u06cc\u06cc\u0645\u0627\u0646 \u0628\u06c6 \u0628\u0627\u0631\u0627\u0646\u06ce\u06a9\u06cc \u0628\u06d5\u0644\u0648\u0648\u0632\u06d5\u0648 \u062e\u0633\u062a\u06d5 \u0633\u06d5\u0631 \u067e\u0634\u062a.',
    '\u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0634 \u0686\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0645\u0627\u0646 \u062a\u06c6\u0642\u0627\u0646\u062f\u0646. \u0626\u0627\u0648 \u06af\u06d5\u06cc\u0646\u06d5 \u06cc\u06d5\u06a9\u061b \u0628\u06d5\u0648 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u06a9\u06d5 \u0628\u0695\u06cc\u0627\u0631\u06cc \u0628\u06c6 \u062f\u0631\u0627\u0628\u0648\u0648.',
    '\u0626\u06d5\u0648\u0645\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0645\u06ce\u0631\u06ce \u0647\u06d5\u06b5\u06af\u0631\u062a\u060c \u06a9\u06d5 \u0644\u06d5 \u0686\u06d5\u0646\u062f \u062a\u06d5\u062e\u062a\u06d5 \u0648 \u0628\u0632\u0645\u0627\u0631\u0627\u0646 \u0644\u06ce\u06a9 \u062f\u0631\u0627\u0628\u0648\u0648.',
    '\u0644\u06d5\u0698\u06ce\u0631 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0648\u06d5\u06af\u06d5\u0695 \u06a9\u06d5\u0648\u062a\u060c \u0628\u06c6 \u067e\u0627\u062f\u0627\u0634 \u062f\u0627\u0646 \u0628\u06d5 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0628\u06d5 \u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646.',
    '\u0628\u06d5\u062c\u06ce\u0645\u0627\u0646 \u0647\u06ce\u0634\u062a \u06a9\u06d5 \u0628\u0628\u06ce\u062a\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc\u06d5\u06a9. \u0626\u0627\u062e\u06c6 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u062f\u06d5\u0633\u0627 \u0686\u06c6\u0646 \u0628\u0648\u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u0648 \u0647\u06d5\u0695\u06d5\u0634\u06d5\u06a9\u0627\u0646\u0645\u061f',
    '\u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0633\u0627\u0646\u0627 \u06a9\u0631\u062f \u06a9\u06d5 \u0628\u06cc\u0631\u06cc \u0644\u06ce \u0628\u06a9\u0631\u06ce\u062a\u06d5\u0648\u06d5. \u0626\u0627\u062e\u06c6 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u0639\u0627\u062f\u06cc\u0634 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627\u061b \u062f\u06d5\u0633\u0627 \u0686\u06c6\u0646 \u0628\u0648\u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u0648 \u0647\u06d5\u0695\u06d5\u0634\u06d5\u06a9\u0627\u0646\u0645\u061f',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u0634\u0648\u0648\u0645\u062f\u0627\u060c \u0628\u0627\u06cc\u06d5\u06a9\u06cc \u0633\u0627\u0631\u062f \u0648 \u0628\u06d5\u0647\u06ce\u0632\u06cc \u0628\u06ce \u0648\u0686\u0627\u0646\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646.',
    '\u062e\u06d5\u06b5\u06a9\u06d5\u06a9\u06d5\u06cc \u0644\u06d5\u062c\u06ce \u062f\u06d5\u0631 \u062f\u06ce\u0646\u0627\u0646\u061b \u062f\u06d5\u062a\u0648\u062a \u06a9\u06c6\u062a\u06d5\u0631\u06d5\u06cc \u062f\u0627\u0631 \u062e\u0648\u0631\u0645\u0627\u0646\u060c \u0628\u06d5 \u0695\u06cc\u0634\u06d5\u0648\u06d5 \u0647\u06d5\u06b5\u06a9\u06ce\u0634\u0631\u0627\u0648\u0646.',
    '\u062f\u06d5\u0633\u0627 \u0686\u06c6\u0646 \u0628\u0648\u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u0648 \u0647\u06d5\u0695\u06d5\u0634\u06d5\u06a9\u0627\u0646\u0645\u061f',
    '\u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0633\u0627\u0646\u0627 \u06a9\u0631\u062f \u06a9\u06d5 \u0628\u06cc\u0631\u06cc \u0644\u06ce \u0628\u06a9\u0631\u06ce\u062a\u06d5\u0648\u06d5. \u0626\u0627\u062e\u06c6 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u0633\u06d5\u0645\u0648\u0648\u062f\u06cc\u0634 \u0628\u06d5 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u0627\u0646 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646.',
    '\u0648\u062a\u06cc\u0627\u0646: \u062c\u0627 \u0686\u06c6\u0646 \u0648\u06d5 \u0634\u0648\u06ce\u0646 \u0645\u0631\u06c6\u06cc\u06d5\u06a9 \u062f\u06d5\u06a9\u06d5\u0648\u06cc\u0646\u060c \u0644\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u0648 \u0628\u06d5 \u062a\u06d5\u0646\u06cc\u0627 \u0628\u06ce\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0648\u0627 \u0628\u06ce\u060c \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u0648 \u062f\u06d5\u0628\u06d5\u0646\u06af\u06cc\u062f\u0627\u06cc\u0646.',
    '\u0686\u06c6\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u0647\u06d5\u0645\u0648\u0648 \u0626\u06ce\u0645\u06d5\u062f\u0627\u060c \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648 \u062f\u06ce\u061f \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u067e\u06cc\u0627\u0648\u06ce \u062f\u0631\u06c6\u0632\u0646\u06cc \u062e\u06c6\u067e\u06d5\u0633\u0646\u062f\u06d5.',
    '\u0633\u0628\u06d5\u06cc \u0695\u06c6\u0698\u06ce \u067e\u06ce \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06ce \u06a4\u0695\u0628\u06ce\u0698\u06cc \u062e\u06c6\u067e\u06d5\u0633\u0646\u062f\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0633\u0627\u06cc\u06d5 \u0648\u0634\u062a\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06d5\u0646\u06ce\u0631\u06cc\u0646 \u06a9\u06d5 \u0628\u06cc\u0627\u0646\u0626\u06d5\u0632\u0645\u0648\u0648\u06cc\u0646. \u0633\u0627 \u062a\u06c6 \u0647\u06d5\u0631 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0648 \u062e\u06c6\u062a \u0695\u0627\u06af\u0631\u06d5.',
    '\u067e\u06ce\u0634\u06cc\u0627\u0646 \u0628\u06ce\u0698\u06d5 \u06a9\u06d5 \u0626\u0627\u0648\u06d5\u06a9\u06d5 \u0644\u06d5\u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0628\u06d5\u0634 \u06a9\u0631\u0627\u0648\u06d5\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0644\u06d5 \u0646\u06c6\u0631\u06d5 \u0626\u0627\u0648\u06cc \u062e\u06c6\u06cc \u0626\u0627\u0645\u0627\u062f\u06d5 \u0628\u06ce.',
    '\u06af\u0627\u0633\u06cc \u0647\u06d5\u0648\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u0626\u06d5\u0648\u06cc\u0634 \u0647\u0627\u062a \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0648 \u067e\u06d5\u0644\u06cc [\u0648\u0634\u062a\u0631\u06d5\u06a9\u06d5\u06cc] \u0642\u0648\u062a \u06a9\u0631\u062f.',
    '\u062f\u06d5\u0633\u0627 \u0686\u06c6\u0646 \u0628\u0648\u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u0648 \u0647\u06d5\u0695\u06d5\u0634\u06d5\u06a9\u0627\u0646\u0645\u061f',
    '\u0646\u06d5\u0639\u0631\u06d5\u062a\u06d5\u06cc\u06d5\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0646\u0627\u0631\u062f\u0646\u061b \u0648\u06d5\u06a9 \u067e\u0648\u0648\u0634 \u0648 \u067e\u0627\u0631\u06cc \u0628\u06c6\u0698\u06c6\u06cc\u0627\u0646 \u0644\u06ce \u0628\u06d5\u0633\u06d5\u0631 \u0647\u0627\u062a.',
    '\u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0633\u0627\u0646\u0627 \u06a9\u0631\u062f \u06a9\u06d5 \u0628\u06cc\u0631\u06cc \u0644\u06ce \u0628\u06a9\u0631\u06ce\u062a\u06d5\u0648\u06d5. \u0626\u0627\u062e\u06c6 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u0647\u06c6\u0632\u06cc \u0644\u0648\u0648\u062a\u06cc\u0634 \u0647\u06d5\u0645\u0628\u06d5\u0631 \u0628\u06d5 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06d5\u06a9\u0627\u0646 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646.',
    '\u06af\u06ce\u0698\u06d5\u06b5\u0648\u0648\u06a9\u06d5\u06cc \u0632\u06cc\u062e \u0648 \u0686\u06d5\u0648\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646\u061b \u062c\u06af\u06d5 \u0644\u06d5 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06cc \u0644\u0648\u0648\u062a - \u06a9\u06d5 \u0633\u0648\u0648 \u0632\u0648\u0648 \u0695\u0632\u06af\u0627\u0631\u0645\u0627\u0646 \u06a9\u0631\u062f\u0646.',
    '\u0644\u06d5 \u0686\u0627\u06a9\u06d5 \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc \u062e\u06c6\u0646\u0695\u0627\u061b \u0626\u0627\u0648\u0627 \u0686\u0627\u06a9\u06d5\u06cc \u0633\u0648\u067e\u0627\u0633\u06a9\u0627\u0631\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u062a\u0631\u0633\u0627\u0646\u062f\u0646\u06cc \u0644\u06d5 \u0632\u06d5\u0628\u0631\u06cc \u0647\u06d5\u06b5\u0645\u06d5\u062a\u06cc \u0626\u06ce\u0645\u06d5\u061b [\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u0627\u0646] \u0644\u06d5\u0648 \u06af\u06d5\u0641\u0627\u0646\u06d5 \u0628\u06d5\u06af\u0648\u0645\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06cc\u0633\u062a \u06a9\u0627\u0631\u06cc \u062e\u0631\u0627\u067e \u062f\u06d5\u06af\u06d5\u06b5 \u0645\u06cc\u0648\u0627\u0646\u0627\u0646\u06cc \u0628\u06a9\u06d5\u0646\u061b \u0633\u06c6\u0645\u0627\u06cc \u0686\u0627\u0648\u0627\u0646\u0645 \u0644\u06ce \u0633\u0627\u0646\u062f\u0646. \u062f\u06d5 \u0628\u0686\u06ce\u0698\u0646 \u0626\u0627\u0632\u0627\u0631 \u0648 \u0647\u06d5\u0695\u06d5\u0634\u06d5\u06a9\u0627\u0646\u0645.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u0628\u06d5\u0631\u06d5\u0628\u06d5\u06cc\u0627\u0646\u062f\u0627\u060c \u0626\u0627\u0632\u0627\u0631\u06cc \u0644\u06d5\u0628\u0646\u06d5 \u0646\u06d5\u0647\u0627\u062a\u0648\u0648 \u0647\u0627\u062a\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646.',
    '\u0633\u0627 \u0628\u0686\u06ce\u0698\u0646 \u0626\u0627\u0632\u0627\u0631 \u0648 \u0647\u06d5\u0695\u06d5\u0634\u06d5\u06a9\u0627\u0646\u0645.',
    '\u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0633\u0627\u0646\u0627 \u06a9\u0631\u062f \u06a9\u06d5 \u0628\u06cc\u0631\u06cc \u0644\u06ce \u0628\u06a9\u0631\u06ce\u062a\u06d5\u0648\u06d5. \u0626\u0627\u062e\u06c6 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u0627\u0646 \u0647\u0627\u062a\u06cc\u0634\u0646\u06d5 \u0644\u0627\u06cc \u062f\u06d5\u0633\u062a\u06d5 \u0648 \u062f\u0627\u06cc\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646.',
    '\u0633\u06d5\u0631\u0644\u06d5\u0628\u06d5\u0631\u06cc \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u0632\u0627\u0646\u06cc. \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5 \u06af\u0631\u062a\u0645\u0627\u0646\u0646\u060c \u0628\u06d5 \u06af\u0631\u062a\u0646\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u062f\u0627\u0631\u06cc \u062a\u0648\u0627\u0646\u0627.',
    '\u0626\u0627\u062e\u06c6 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06cc \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc \u062a\u0631 \u0686\u0627\u062a\u0631\u0646\u060c \u06cc\u0627\u0646 \u0644\u06d5\u0646\u0627\u0648 \u06a9\u062a\u06ce\u0628\u06d5\u06a9\u0627\u0646\u062f\u0627 \u067e\u0627\u06a9\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a\u06af\u06d5\u061f',
    '\u06cc\u0627\u0646 \u062f\u0628\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u06af\u0634\u0645\u0627\u0646 \u067e\u06ce\u06a9\u06d5\u0648\u06d5\u06cc\u0646 \u0648 \u0633\u06d5\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u06cc\u0646\u061f',
    '\u0647\u06d5\u0631 \u0628\u06cc\u0646\u0627 \u0626\u06d5\u0648 \u0626\u0627\u067e\u06c6\u0631\u06d5\u06cc\u06d5 \u0644\u06d5 \u0634\u06d5\u0695 \u0634\u06a9\u0627\u0648\u060c \u067e\u0634\u062a \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u0695\u0627 \u0628\u06a9\u06d5\u0646.',
    '\u0628\u06d5\u06b5\u06a9\u06d5 \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u067e\u06d5\u0633\u06b5\u0627\u0646\u06cc\u0634 \u06a9\u06c6 \u0628\u0628\u0646\u06d5\u0648\u06d5\u061b \u06a9\u06d5 \u0628\u0627\u0631\u06cc \u0626\u06d5\u0648 \u062d\u06d5\u0633\u062a\u06d5\u0645\u062a\u0631\u06d5 \u0648 \u062a\u0627\u06b5\u062a\u0631\u06cc\u0634\u062a\u0627\u0646 \u0644\u06ce \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce.',
    '\u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0644\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u0648 \u062f\u06d5\u0628\u06d5\u0646\u06af\u06cc\u062f\u0627\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u062f\u06d5\u0645\u06d5\u0648\u0695\u0648\u0648 \u0644\u06d5 \u0626\u0627\u06af\u0631\u062f\u0627 \u0695\u06d5\u06a9\u06ce\u0634 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u060c \u062f\u06d5 \u0628\u0686\u06ce\u0698\u0646 \u06af\u06b5\u067e\u06d5\u06cc \u0626\u0627\u06af\u0631\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645.',
    '\u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u0631\u0645\u0627\u0646 \u0628\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0648\u0648\u06d5.',
    '\u06a9\u0627\u0631\u06cc \u0626\u06ce\u0645\u06d5 \u06cc\u06d5\u06a9\u062c\u0627\u0631\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u0644\u06d5 \u0686\u0627\u0648\u062a\u0631\u0648\u06a9\u0627\u0646\u06ce\u06a9\u062f\u0627 \u06a9\u06c6\u062a\u0627\u06cc\u06cc \u062f\u06ce.',
    '\u067e\u06ce\u0634\u0648\u0648\u0634 \u0632\u06c6\u0631\u06cc \u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u0642\u0695 \u062a\u06ce \u062e\u0633\u062a\u0648\u0648\u0646. \u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633 \u0628\u06d5 \u0628\u06cc\u0631\u06cc\u062f\u0627 \u062f\u06ce\u061f',
    '\u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u06a9\u0631\u062f\u0648\u0648\u06cc\u0627\u0646\u06d5\u060c \u0648\u0627 \u0644\u06d5\u0646\u0627\u0648 \u062f\u06d5\u0641\u062a\u06d5\u0631\u0627\u0646\u062f\u0627\u06cc\u06d5.',
    '\u06a9\u06d5\u0645 \u0648 \u0632\u06c6\u0631\u06cc \u062e\u06d5\u062a \u062f\u0631\u0627\u0648\u06d5.',
    '\u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0627\u0646 \u0644\u06d5 \u0628\u0627\u063a\u0627\u062a \u0648 \u062f\u06d5\u0645 \u0686\u06c6\u0645\u0627\u0646\u0646.',
    '\u0644\u06d5 \u0646\u0634\u06cc\u0645\u06d5\u0646\u06cc \u067e\u06d5\u0633\u0646\u062f \u0648 \u0644\u0627\u06cc \u062e\u0648\u0646\u06a9\u0627\u0631\u06cc \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a.',
    '\u062e\u0648\u0627\u06cc \u062f\u06b5\u0627\u0648\u0627\u060c',
    '\u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0631\u0633 \u062f\u0627\u0648\u06d5.',
    '\u0645\u0631\u06c6\u06cc \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u061b',
    '\u0644\u06d5 \u0626\u0627\u062e\u0627\u0641\u062a\u0646\u06cc \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f.',
    '\u062e\u06c6\u0631 \u0648 \u0647\u06d5\u06cc\u06a4\u06cc\u0634 - \u0628\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 - \u062f\u06ce\u0646 \u0648 \u062f\u06d5\u0686\u0646.',
    '\u06af\u06cc\u0627 \u0648\u0631\u062f\u06cc\u0644\u06d5 \u0648 \u0628\u06d5 \u0633\u0627\u0642\u06d5\u062a\u06d5\u0634 \u0633\u0648\u0698\u062f\u06d5 \u062f\u06d5\u0628\u06d5\u0646.',
    '\u0639\u06d5\u0632\u0645\u0627\u0646\u06cc \u0628\u06d5\u0631\u0632 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u062a\u06d5\u0631\u0627\u0632\u0648\u0648\u06cc \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0646\u062f\u0648\u0648\u06d5\u061b',
    '\u06a9\u06d5 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u062f\u06d5\u0631 \u0646\u06d5\u0686\u0646.',
    '\u06a9\u06ce\u0634\u0627\u0646\u06d5\u062a\u0627\u0646 \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06ce \u0648 \u0646\u06d5\u06a9\u0627 \u062a\u06d5\u0631\u0627\u0632\u0648\u0648\u062a\u0627\u0646 \u0633\u06d5\u0631 \u06a9\u0627.',
    '\u0626\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5.',
    '\u0645\u06cc\u0648\u06d5\u06cc \u062a\u06ce\u062f\u0627 \u0648 \u062f\u0627\u0631\u06d5 \u062e\u0648\u0631\u0645\u0627\u06cc \u062e\u0627\u0648\u06d5\u0646 \u067e\u0634\u06a9\u06c6\u0698.',
    '\u062f\u06d5\u0646\u062f\u06a9\u06cc \u0644\u06d5\u0646\u0627\u0648 \u06a9\u06d5\u0644\u0648\u0648\u062f\u0627 \u0648 \u0634\u06cc\u0646\u06a9\u06d5\u06cc \u0628\u06c6\u0646 \u062e\u06c6\u0634.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0645\u0631\u06c6\u06cc \u0644\u06d5 \u062d\u06d5\u0695\u06cc \u0632\u0648\u0647\u0627 \u0628\u0648\u0648\u06cc \u0686\u06d5\u0634\u0646\u06cc \u0633\u0648\u0627\u06b5\u06d5\u062a\u060c \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u062c\u0646\u06c6\u06a9\u06d5\u0634\u06cc \u0644\u06d5 \u0628\u06b5\u06ce\u0633\u06d5\u06cc \u0628\u06ce \u062f\u0648\u0648\u06a9\u06d5\u06b5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0647\u06d5\u0631\u062f \u0648 \u0634\u0648\u06ce\u0646\u06cc \u062e\u06c6\u0631\u0647\u06d5\u06b5\u0627\u062a \u0648 \u062e\u06c6\u0631\u0646\u0634\u06cc\u0646\u06cc \u0695\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u062c\u06d5\u0645\u0633\u06d5\u0631\u06cc \u062f\u0648\u0648 \u062f\u06d5\u0631\u06cc\u0627\u06cc\u0627\u0646\u06cc \u06af\u06d5\u06cc\u0627\u0646\u062f\u06d5 \u06cc\u06d5\u06a9.',
    '\u0628\u06d5\u0631\u0632\u06d5\u06a9\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0646\u06ce\u0648\u0627\u0646\u062f\u0627 \u06a9\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u06cc\u06d5\u06a9\u062a\u0631\u062f\u0627 \u0646\u0627\u06cc\u06d5\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0644\u06d5 \u0647\u06d5\u0631\u062f\u0648\u0648\u06a9\u0627\u0646 \u0645\u0631\u0627\u0631\u06cc \u0648 \u0645\u06d5\u0631\u062c\u0627\u0646 \u062f\u06d5\u0631 \u062f\u06d5\u0686\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u06af\u06d5\u0695\u06c6\u06a9\u0627\u0646\u06cc \u0628\u06d5 \u0628\u0627\u062f\u06d5\u0648\u06d5\u06cc \u0686\u06cc\u0627 \u0626\u0627\u0633\u0627 - \u0644\u06d5 \u062f\u06d5\u0631\u06cc\u0627\u062f\u0627 - \u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0647\u06d5\u0631\u06a9\u06ce \u0698\u06cc\u0646\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u060c \u0646\u0627\u0645\u06ce\u0646\u06ce.',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0634\u06a9\u06c6\u06cc \u0628\u06d5\u0631\u0632 \u0648 \u0628\u06d5\u0695\u06ce\u0632\u062a \u062f\u06d5\u0645\u06ce\u0646\u06ce.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u0646\u060c \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u06af\u0634 \u0695\u06c6\u0698\u06ce \u0628\u06d5 \u06a9\u0627\u0631\u06ce\u06a9\u06d5\u0648\u06d5 \u062e\u06d5\u0631\u06cc\u06a9\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06d5 \u06a9\u0627\u0631\u062a\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u0646\u060c \u0626\u06d5\u06cc \u06af\u0631\u06c6\u06cc \u0645\u0631\u06c6 \u0648 \u062c\u0646\u06c6\u06a9\u0627\u0646!',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0626\u06d5\u06cc \u06af\u0631\u06c6\u06cc \u0645\u0631\u06c6 \u0648 \u062c\u0646\u06c6\u06a9\u06d5! \u06af\u06d5\u0631 \u062a\u0648\u0627\u0646\u06cc\u062a\u0627\u0646 \u0644\u06d5 \u06af\u0634 \u0626\u0627\u0633\u06c6\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06ce \u062f\u06d5\u0631\u0628\u0627\u0632 \u0628\u0628\u0646\u060c \u062f\u06d5\u0633\u0627 \u0628\u0686\u0646\u061b \u0646\u0627\u0634\u062a\u0648\u0627\u0646\u0646 \u0644\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u0631 \u0628\u0686\u0646\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u06af\u0695\u06cc \u0628\u06d5 \u062f\u0648\u0648\u06a9\u06d5\u06b5 \u0648 \u0628\u06ce \u062f\u0648\u0648\u06a9\u06d5\u06b5\u06cc \u0626\u0627\u06af\u0631 \u062f\u0627\u062a\u0627\u0646 \u062f\u06d5\u06af\u0631\u06ce\u061b \u0626\u06d5\u0633\u0627 \u06a9\u06d5\u0633 \u062f\u06d5\u0647\u0627\u0646\u0627\u0648\u0646\u0627\u06cc\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0633\u0627 \u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce \u0626\u06d5\u0645 \u0639\u06d5\u0632\u0645\u0627\u0646\u06d5 \u0642\u06d5\u06b5\u0634\u06cc \u0628\u0631\u062f \u0648 \u0628\u06d5 \u0695\u06d5\u0646\u06af\u06cc \u06af\u0648\u06b5 \u0648 \u06a9\u06d5\u0648\u06b5\u06cc \u0633\u0648\u0648\u0631 \u0647\u0627\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u060c',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 - \u0644\u06d5 \u0645\u0631\u06c6 \u0648 \u062c\u0646\u06c6\u06a9\u06d5 - \u0628\u06c6 \u06af\u0648\u0646\u0627\u0647\u06ce \u06a9\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5\u060c \u0646\u0627\u06cc\u06d5\u062a\u06d5 \u0628\u06d5\u0631\u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0628\u06d5 \u062a\u06d5\u062d\u0631 \u0648 \u0632\u06d5\u0695\u0627 \u062f\u06d5\u0646\u0627\u0633\u0631\u06ce\u0646\u061b \u0626\u06cc\u062a\u0631 \u0644\u06d5 \u0642\u0698\u06cc \u0633\u06d5\u0631\u06cc\u0627\u0646 \u0648 \u0644\u06d5 \u0642\u0627\u0686\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0626\u06d5\u0646\u06d5\u0648\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062f\u06c6\u0698\u06d5\u0647\u06d5\u06cc\u06d5 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0648\u0627 \u0646\u06d5\u0628\u0648\u0648 \u0647\u06d5\u06cc\u06d5.',
    '\u0644\u06d5\u0646\u0627\u0648\u06cc\u062f\u0627 \u0648 \u0644\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u06a9\u06d5\u0644\u06cc\u062f\u0627 \u062f\u06d5\u062e\u0648\u0644\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u0631\u0633\u0627\u0648\u06d5\u060c \u062f\u0648\u0648 \u0628\u0627\u063a \u0647\u06d5\u06cc\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0647\u06d5\u0631 \u062f\u0648\u0648 \u062e\u0627\u0648\u06d5\u0646 \u062f\u0631\u06d5\u062e\u062a\u06cc \u0628\u06d5 \u067e\u06d5\u0644\u06a9 \u0648 \u067e\u06c6\u067e\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0644\u06d5 \u0647\u06d5\u0631 \u062f\u0648\u0648\u062f\u0627 \u062f\u0648\u0648 \u0633\u06d5\u0631\u0686\u0627\u0648\u06d5\u06cc \u0695\u06d5\u0648\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u06af\u0634 \u0645\u06cc\u0648\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u062a\u06ce\u062f\u0627\u06cc\u06d5\u060c \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0648\u0644\u0641\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0644\u06d5\u0633\u06d5\u0631 \u0695\u0627\u06cc\u06d5\u062e\u06ce \u067e\u0627\u06b5 \u06a9\u06d5\u0641\u062a\u0648\u0648\u0646\u060c \u06a9\u06d5 \u0626\u0627\u0633\u06d5\u0695\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u0627\u0648\u0631\u06cc\u0634\u0645\u06cc \u0626\u06d5\u0633\u062a\u0648\u0648\u0631\u06d5 \u0648 \u0628\u06d5\u0631\u06cc \u06af\u06d5\u06cc\u06cc\u0634\u062a\u0648\u0648\u06cc \u0647\u06d5\u0631 \u062f\u0648\u0648 \u0628\u0627\u063a\u060c \u0644\u06d5\u0648\u0627\u0646 \u0646\u0632\u06cc\u06a9 \u06a9\u06d5\u0648\u062a\u06c6\u062a\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u06a9\u06cc\u0698\u06c6\u06b5\u06d5\u06cc \u0647\u06ce\u0646\u062f \u062f\u0627\u0648\u06ce\u0646 \u067e\u0627\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u0646\u0627\u0648\u062f\u0627\u06cc\u06d5\u060c \u06a9\u06d5 \u0628\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646 \u06a9\u06d5\u0633\u06ce - \u0645\u0631\u06c6 \u06cc\u0627\u0646 \u062c\u0646\u06c6\u06a9\u06d5 - \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062f\u06d5\u0633\u062a\u06cc \u0628\u06c6 \u0646\u06d5\u0628\u0631\u062f\u0648\u0648\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0647\u06ce\u0646\u062f\u06d5 \u062c\u0648\u0627\u0646\u0646\u060c \u0626\u06ce\u0698\u06cc \u06cc\u0627\u0642\u0648\u0648\u062a \u0648 \u0645\u06d5\u0631\u062c\u0627\u0646\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0626\u0627\u062e\u06c6 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u060c \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0686\u0627\u06a9\u06d5 \u062f\u0627\u0646\u06d5\u0648\u06d5\u06cc\u06d5\u061f',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0645\u0627\u0646 \u062f\u0648\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc \u062a\u0631\u06cc\u0634 \u0647\u06d5\u06cc\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u06a9\u06d5\u0633\u06a9\u06cc \u062a\u06c6\u062e\u0646\u060c \u0628\u06d5 \u0695\u0648\u0627\u06b5\u06d5\u062a \u0695\u06d5\u0634 \u062f\u06d5\u0646\u0648\u06ce\u0646\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u062c\u0648\u0648\u062a\u06ce \u06a9\u0627\u0646\u06cc \u0644\u06d5\u0648\u0627\u0646\u062f\u0627 \u0647\u06d5\u0646\u060c \u0641\u06cc\u0686\u0642\u06d5 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0644\u06d5\u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0645\u06cc\u0648\u06d5\u062c\u0627\u062a \u0648 \u062f\u0627\u0631\u06d5 \u062e\u0648\u0631\u0645\u0627 \u0648 \u0647\u06d5\u0646\u0627\u0631 \u0647\u06d5\u06cc\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0644\u06d5\u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627\u0646 \u06af\u06d5\u0644\u06ce \u06a9\u06cc\u0698\u06cc \u0626\u0627\u06a9\u0627\u0631 \u0686\u0627\u06a9 \u0648 \u0628\u06d5 \u062f\u06cc\u0645\u06d5\u0646 \u062c\u0648\u0627\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0632\u06d5\u0631\u06cc \u0686\u0627\u0648\u0695\u06d5\u0634 \u0644\u06d5\u0646\u0627\u0648 \u062e\u06ce\u0648\u06d5\u062a\u0627\u0646\u062f\u0627 \u062f\u06d5\u0698\u06cc\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u06a9\u06d5 \u0628\u06d5\u0631\u0644\u06d5\u0648\u0627\u0646 \u0647\u06cc\u0686 \u0645\u0631\u06c6 \u0648 \u0647\u06cc\u0686 \u062c\u0646\u06c6\u06a9\u06d5\u06cc\u06d5\u06a9\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062f\u06d5\u0633\u062a\u06cc \u0628\u06c6 \u0646\u06d5\u0628\u0631\u062f\u0648\u0648\u0646.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0644\u06d5\u0633\u06d5\u0631 \u0695\u0627\u06cc\u06d5\u062e\u06cc \u06a9\u06d5\u0633\u06a9 \u0648 \u062c\u0648\u0627\u0646 \u067e\u0627\u06b5 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u061b \u06a9\u06d5 \u0648\u06ce\u0646\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u062f\u0627 \u0646\u06d5\u0628\u06cc\u0646\u0631\u0627\u0648\u06d5.',
    '\u062f\u06d5\u06cc\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u06a9\u0627\u0645 \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0648 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0645\u0645\u0628\u0627\u0631\u06d5\u06a9\u06d5 \u0646\u0627\u0648\u06cc \u067e\u0627\u06a9\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u061b \u062e\u0627\u0648\u06d5\u0646 \u0634\u06a9\u06c6 \u0648 \u0634\u0627\u06cc\u0627\u0646\u06cc \u0695\u06ce\u0632 \u0644\u06ce \u06af\u0631\u062a\u0646\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0695\u0648\u0648\u062f\u0627\u0648\u06d5\u06a9\u06d5 \u0695\u0648\u0648\u06cc \u062f\u0627\u060c',
    '\u06a9\u06d5\u0633 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u0644\u06d5\u0648 \u0695\u0648\u0648\u062f\u0627\u0648\u06d5 \u062d\u0627\u0634\u0627 \u0628\u06a9\u0627.',
    '\u0628\u06d5\u0631\u0632 \u062f\u06d5\u06a9\u0627 \u0648 \u0646\u0632\u0645 \u062f\u06d5\u06a9\u0627\u061b',
    '\u06a9\u0627\u062a\u06ce \u0632\u06d5\u0648\u06cc\u0646 \u0695\u0627\u062f\u06d5\u062a\u06b5\u06d5\u06a9\u06ce \u0628\u06d5 \u0695\u0627\u062a\u06b5\u06d5\u06a9\u0627\u0646\u060c',
    '\u06a9\u06d5\u0698\u06cc\u0634 \u062f\u06d5\u0647\u0627\u0695\u06ce\u0646 \u0628\u06d5 \u0647\u0627\u0695\u06cc\u0646\u060c',
    '\u0644\u06d5 \u0686\u06d5\u0634\u0646\u06cc \u062a\u06c6\u0632 \u0628\u06b5\u0627\u0648 \u062f\u06d5\u0628\u0646\u060c',
    '\u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5 \u0633\u06ce \u062a\u06cc\u0631\u06d5:',
    '\u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0633\u062a\u06cc \u0695\u0627\u0633\u062a\u06cc \u0648 \u0628\u06d5\u062e\u062a\u06cc\u0627\u0631\u0646.',
    '\u062a\u06cc\u0631\u06d5\u06cc \u062a\u0631\u06cc\u0627\u0646 \u062f\u06d5\u0633\u062a\u06cc \u0686\u06d5\u067e\u06cc \u0648 \u0646\u06d5\u06af\u0628\u06d5\u062a\u0628\u0627\u0631\u0646.',
    '\u062a\u06cc\u0631\u06d5\u06cc\u06d5\u06a9\u06cc\u0634 \u0644\u06d5 \u067e\u06ce\u0634\u06d5\u0648\u06d5\u0646\u060c \u0648\u06d5\u067e\u06ce\u0634 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646.',
    '\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u0626\u06ce\u0645\u06d5\u0648\u06d5 \u0646\u0632\u06cc\u06a9 \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646.',
    '\u0644\u06d5 \u0628\u0627\u063a\u0627\u062a\u06cc \u062a\u0698\u06cc \u0644\u06d5 \u062e\u06c6\u0634\u06cc\u062f\u0627 \u062f\u06d5\u0698\u06cc\u0646.',
    '\u0628\u06d5\u0634\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u067e\u06ce\u0634\u06cc\u0646\u06d5\u060c',
    '\u06a9\u06d5\u0645\u06ce\u06a9\u06cc\u0634 \u0644\u06d5\u0648\u0627\u0646\u06cc \u067e\u0627\u0634\u06cc\u0646\u060c',
    '\u0644\u06d5\u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a\u06cc \u062c\u06d5\u0648\u0627\u0647\u06cc\u0631 \u0628\u06d5\u0646\u062f\u060c',
    '\u0647\u06d5\u0645\u0628\u06d5\u0631 \u0628\u06d5\u06cc\u06d5\u06a9 \u067e\u0627\u06b5 \u062f\u06d5\u06a9\u06d5\u0641\u0646.',
    '\u062a\u0627\u0632\u06d5 \u0644\u0627\u0648\u06cc \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0644\u0627\u0648\u060c \u0644\u06d5 \u062f\u06d5\u0648\u0631\u06d5\u06cc\u0627\u0646 \u062f\u06ce\u0646 \u0648 \u062f\u06d5\u0686\u0646.',
    '\u0628\u06d5 \u0632\u06d5\u0631\u06a9 \u0648 \u0628\u06d5 \u062a\u0648\u0646\u06af\u06d5 \u0648 \u067e\u06cc\u0627\u06b5\u06d5\u060c \u0628\u0627\u062f\u06d5\u06cc \u0695\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0633\u06d5\u0631\u0686\u0627\u0648\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646\u06ce.',
    '\u0646\u06d5 \u0644\u06d5 \u062e\u0648\u0645\u0627\u0631 \u0633\u06d5\u0631\u06cc\u0627\u0646 \u062f\u06ce\u0634\u06ce \u0648 \u0646\u06d5 \u0626\u0627\u0648\u06d5\u0632\u06cc\u0634 \u0644\u06d5\u062f\u06d5\u0633\u062a \u0626\u06d5\u062f\u06d5\u0646.',
    '\u0645\u06cc\u0648\u06d5\u062c\u0627\u062a\u06cc\u0634\u060c \u0647\u06d5\u0631\u06a9\u0627\u0645\u06cc \u0647\u06d5\u06b5\u06cc \u062f\u06d5\u0628\u0698\u06ce\u0631\u0646\u060c',
    '\u06af\u06c6\u0634\u062a\u06cc \u0645\u06d5\u0644\u06cc\u0634\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c',
    '\u06a9\u06cc\u0698\u06cc \u0633\u067e\u06cc \u0686\u0627\u0648 \u0628\u06d5\u06b5\u06d5\u06a9\u06cc\u0634\u060c',
    '\u0647\u06d5\u0645\u0648\u0648 \u0626\u06ce\u0698\u06cc \u0645\u0631\u0627\u0631\u06cc\u0646 \u0644\u06d5\u0646\u0627\u0648 \u0642\u0627\u067e\u06c6\u0695\u062f\u0627\u060c',
    '\u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646\u06d5.',
    '\u06a9\u06d5 \u0644\u06d5\u0648\u06ce\u062f\u0627\u0646\u060c \u0646\u06d5 \u0642\u0633\u06d5\u06cc \u0647\u06d5\u0631\u0632\u06d5 \u062f\u06d5\u0628\u06cc\u0633\u0646\u060c \u0646\u06d5 \u0648\u062a\u0627\u0631\u06ce\u06a9\u06cc \u0646\u0627\u0695\u06d5\u0648\u0627.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u062f\u0631\u0648\u0648\u062f \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u06a9\u06d5\u0646 \u0648 \u0648\u06d5\u0631\u0627\u0645\u06cc \u067e\u06ce \u0628\u062f\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u06cc \u062a\u06cc\u0631\u06d5\u06cc \u062f\u06d5\u0633\u062a\u06d5\u0695\u0627\u0633\u062a\u0646\u061b \u0626\u06d5\u0648\u0627\u0646 \u0686\u06c6\u0646 \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u06ce\u06a9\u0646\u061f',
    '\u0644\u0627\u06cc \u062f\u0627\u0631 \u06a9\u0646\u0627\u0631\u06cc \u0628\u06ce \u0686\u06d5\u0642\u06b5\u0646.',
    '\u0645\u06c6\u0632\u06cc \u067e\u0627\u06b5 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u062f\u0631\u0627\u0648\u06cc\u0634\u060c',
    '\u0633\u06ce\u0628\u06d5\u0631\u06ce\u06a9\u06cc \u0628\u06ce \u0628\u0695\u0627\u0646\u06d5\u0648\u06d5\u0634\u060c',
    '\u0626\u0627\u0648\u06cc\u0634 \u0647\u06d5\u0631\u0648\u0647\u06d5\u0631 \u0695\u06d5\u0648\u0627\u0646\u06d5\u060c',
    '\u0645\u06cc\u0648\u06d5\u0634 \u0632\u06c6\u0631 \u0648 \u0632\u06d5\u0628\u06d5\u0646\u062f\u06d5\u06cc\u06d5\u060c',
    '\u0646\u06d5 \u062f\u0648\u0627\u06cc\u06cc \u062f\u06ce \u0648 \u0646\u06d5 \u067e\u0627\u0648\u0627\u0646\u06d5\u060c',
    '\u0646\u0648\u06ce\u0646 \u0648 \u0628\u0627\u0646\u06cc \u0628\u06d5\u0631\u0632 \u0648 \u0646\u06d5\u0631\u0645\u060c',
    '\u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u06a9\u06cc\u0698\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0628\u06d5\u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u0628\u06d5\u0631\u0647\u06d5\u0645 \u0647\u06ce\u0646\u0627\u061b',
    '\u0628\u0695\u06cc\u0627\u0631\u0645\u0627\u0646 \u0628\u06c6 \u062f\u0627\u0646 \u0647\u06d5\u0631 \u06a9\u0686 \u0628\u0646.',
    '\u0634\u0648\u0648 \u062e\u06c6\u0634\u06d5\u0648\u06cc\u0633\u060c \u0647\u0627\u0648\u062a\u06d5\u0645\u06d5\u0646\u0646\u060c',
    '\u0628\u06d5\u0634\u06cc \u062f\u06d5\u0633\u062a\u06d5\u0695\u0627\u0633\u062a\u06cc\u06cc\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u061b',
    '\u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u067e\u0627\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u06cc \u0686\u06d5\u067e\u0646\u060c \u062a\u0648\u0648\u0634\u06cc \u0686 \u0646\u06d5\u06af\u0628\u06d5\u062a\u06ce\u06a9 \u0647\u0627\u062a\u0648\u0648\u0646\u061f',
    '\u0648\u0627\u0646 \u0644\u06d5 \u0646\u0627\u0648\u06d5\u0646\u062f \u06af\u0695\u06d5\u0628\u0627 \u0648 \u0626\u0627\u0648\u0627\u06a9\u06d5\u0644\u06cc\u062f\u0627.',
    '\u0686\u0695\u06d5 \u062f\u0648\u0648\u06a9\u06d5\u06b5 \u0633\u06ce\u0628\u06d5\u0631\u06cc\u0627\u0646\u06d5.',
    '\u0646\u06d5 \u0641\u06ce\u0646\u06a9\u0627\u06cc\u06cc \u062a\u06ce\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0646\u06d5 \u0644\u06d5\u0628\u06d5\u0631\u06cc \u062f\u06d5\u0633\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u062e\u06c6\u0634 \u06af\u0648\u0632\u06d5\u0631\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u0644\u06d5\u0633\u06d5\u0631 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u06cc \u06af\u06d5\u0648\u0631\u06d5 \u067e\u06ce\u062f\u0627\u06af\u0631 \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u0634\u06cc\u0627\u0646\u0648\u062a: \u0626\u0627\u062e\u06c6 \u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0628\u0648\u0648\u06cc\u0646\u06d5 \u062e\u06c6\u06b5 \u0648 \u0686\u06d5\u0646\u062f \u067e\u06ce\u0634\u06d5\u06cc\u06d5\u06a9\u060c \u062f\u06cc\u0633\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u06cc\u0646\u06d5\u0648\u06d5\u061f',
    '\u06cc\u0627\u0646 \u0628\u0627\u06a4 \u0648 \u06a9\u0627\u0644\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06ce \u0634\u06a9\u060c \u0626\u06d5\u0648\u06cc \u0644\u06d5\u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u0628\u0648\u0648\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc \u0647\u0627\u062a\u0648\u0648\u0646\u060c',
    '\u0628\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5 \u06a9\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5\u061b \u0644\u06d5 \u0698\u0648\u0627\u0646\u06af\u06d5\u06cc \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u06cc \u06af\u0648\u0645\u0695\u0627\u06cc\u0627\u0646\u06cc \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695!',
    '\u062f\u06d5\u0628\u06ce \u0628\u06d5\u0631\u06cc \u062f\u0627\u0631\u06cc \u0698\u06d5\u0642\u0646\u06d5\u0645\u0648\u0648\u062a\u06ce \u0628\u062e\u06c6\u0646.',
    '\u06af\u06d5\u062f\u06d5\u062a\u0627\u0646\u06cc \u0644\u06ce \u067e\u0695 \u0628\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u0627\u0648\u0627\u06a9\u06d5\u0644\u06cc \u0628\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648\u062f\u0627 \u0648\u06d5\u062e\u06c6\u0646\u061b',
    '\u0648\u06d5\u06a9\u0648\u0648 \u0686\u06c6\u0646 \u0648\u0634\u062a\u0631\u06cc \u062a\u06cc\u0646\u0648\u0648 \u0628\u06ce\u062a\u06d5 \u0633\u06d5\u0631 \u0626\u0627\u0648.',
    '\u0695\u06c6\u0698\u06cc \u0698\u0645\u0627\u0631 \u0648\u0627\u06cc\u0627\u0646 \u0645\u06cc\u0648\u0627\u0646\u062f\u0627\u0631\u06cc \u062f\u06d5\u06a9\u0631\u06ce.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u062f\u0631\u0648\u0633. \u06a9\u0631\u062f\u060c \u0633\u0627 \u0628\u06c6 \u0686\u06cc \u0628\u0627\u0648\u06d5\u0695\u062a\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u061f',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0626\u06d5\u0648 \u0626\u0627\u0648\u06cc \u0634\u0627\u0648\u06d5\u062a\u06d5\u0648 \u062f\u06cc\u0648\u06d5\u060c \u06a9\u06d5 \u062f\u06d5\u06cc\u0695\u06ce\u0698\u0646\u061f',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u062f\u06cc \u062f\u06ce\u0646\u0646\u060c \u06cc\u0627 \u0646\u06d5 \u0626\u06ce\u0645\u06d5 \u0648\u06d5\u062f\u06cc \u062f\u06ce\u0646\u06cc\u0646\u061f',
    '\u0626\u06ce\u0645\u06d5 \u0628\u0695\u06cc\u0627\u0631\u06cc \u0645\u06d5\u0631\u06af\u0645\u0627\u0646 \u0628\u06c6\u062a\u0627\u0646 \u062f\u0627\u0648\u06d5 \u0648 \u06a9\u06d5\u0633 \u0648\u06d5\u067e\u06ce\u0634 \u0626\u06ce\u0645\u06d5 \u0646\u0627\u06a9\u06d5\u0648\u06ce\u061b',
    '\u06a9\u06d5 \u0628\u06d5 \u0686\u06cc\u0646\u06ce\u06a9\u06cc \u0648\u06d5\u06a9 \u0626\u06ce\u0648\u06d5 \u0648\u06d5\u062a\u0627\u0646 \u06af\u06c6\u0695\u06cc\u0646 \u0648 \u062a\u06d5\u0631\u0632\u06ce\u06a9\u062a\u0627\u0646 \u0633\u0627\u0632 \u0628\u062f\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u060c \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0647\u0627\u06cc\u062a\u0627\u0646 \u0644\u06ce \u0646\u06cc\u06cc\u06d5\u061f',
    '\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5 \u062f\u0631\u0648\u0633\u062a \u0628\u0648\u0648\u0646\u06cc \u06cc\u06d5\u06a9\u06d5\u0645 \u062c\u0627\u0631\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u0646\u061b \u062f\u06d5\u06cc \u0628\u06c6 \u0647\u06cc\u0686 \u0628\u06cc\u0631 \u0646\u0627\u06a9\u06d5\u0646\u06d5\u0648\u06d5\u061f',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648 \u062a\u06c6\u0645\u06d5\u06cc \u062f\u06d5\u06cc\u0686\u06ce\u0646\u0646\u060c \u062f\u06cc\u062a\u0648\u0648\u062a\u0627\u0646\u06d5\u061f',
    '\u067e\u06ce\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0626\u06ce\u0648\u06d5\u0646 \u06a9\u06d5 \u0628\u06d5\u0631\u0647\u06d5\u0645\u06cc \u062f\u06ce\u0646\u0646\u060c \u06cc\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u0628\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0695\u0648\u06ce\u0646\u06cc\u0646\u061f',
    '\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0628\u0645\u0627\u0646\u0648\u06cc\u0633\u062a\u0627\u06cc\u06d5\u060c \u062f\u06d5\u0645\u0627\u0646\u06a9\u0631\u062f\u06d5 \u067e\u0648\u0648\u0634\u06cc \u0628\u06d5\u0631\u0628\u0627\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0633\u06d5\u06cc\u0631\u0648\u0648 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u062f\u06d5\u0648\u06af\u0648\u062a: \u0626\u06ce\u0645\u06d5 \u062f\u06c6\u0695\u0627\u0646\u062f\u0645\u0627\u0646\u061b',
    '\u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0628\u06ce \u0628\u06d5\u0634 \u0645\u0627\u0648\u06cc\u0646\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648 \u0626\u0627\u0648\u06d5\u062a\u0627\u0646 \u062f\u06cc\u0648\u06d5\u060c \u062f\u06d5\u06cc\u062e\u06c6\u0646\u06d5\u0648\u06d5\u061f',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648 \u0647\u06d5\u0648\u0631\u06d5\u0648\u06d5 \u0646\u0627\u0631\u062f\u0648\u0648\u062a\u0627\u0646\u06d5\u060c \u06cc\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u0628\u06c6\u0645\u0627\u0646 \u0628\u0627\u0631\u0627\u0646\u062f\u0648\u0648\u0646\u061f',
    '\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0628\u0645\u0627\u0646\u0648\u06cc\u0633\u062a\u0627\u06cc\u06d5\u060c \u062f\u06d5\u0645\u0627\u0646\u06a9\u0631\u062f \u0628\u06d5 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u062a\u0627\u06b5\u061b \u062f\u06d5\u06cc\u0633\u0627 \u0628\u06c6 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5 \u0646\u0627\u06a9\u06d5\u0646\u061f',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648 \u0626\u0627\u06af\u0631\u06d5\u0648 \u062f\u06cc\u0648\u06d5 \u0647\u06d5\u06b5\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0626\u06ce\u0648\u06d5 \u062f\u0627\u0631\u06cc \u0626\u0627\u06af\u0631\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u060c \u06cc\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u0648\u06d5\u062f\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u061f',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0647\u06c6\u06cc \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc \u0648 \u0628\u06d5\u0695\u06ce\u0648\u06d5\u0686\u0648\u0648\u0646\u06cc \u0698\u06cc\u0627\u0646\u06cc \u0647\u06d5\u0631\u062f\u06d5\u06af\u06d5\u0695\u0627\u0646.',
    '\u0633\u0627 \u062a\u06c6 \u0646\u0627\u0648\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u06d5\u0632\u0646\u06cc \u062e\u06c6\u062a\u060c \u0647\u06d5\u0631 \u0628\u06d5 \u067e\u0627\u06a9\u06cc \u0648 \u062f\u0648\u0648\u0631\u06d5 \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc \u0628\u06ce\u0646\u06d5.',
    '\u062f\u06d5\u0633\u0627 \u0633\u0648\u06ce\u0646\u062f\u0645 \u0628\u06d5\u0648 \u0634\u0648\u06ce\u0646\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0633\u062a\u06ce\u0631\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06b5\u062f\u06ce\u0646 \u0648 \u0626\u0627\u0648\u0627 \u062f\u06d5\u0628\u0646\u060c',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646 - \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06cc\u0632\u0627\u0646\u0646 - \u0626\u06d5\u0645\u06d5 \u0633\u0648\u06ce\u0646\u062f\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06d5.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0626\u06d5\u0645\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646\u06cc \u0628\u06d5\u0695\u06ce\u0632\u06d5.',
    '\u0644\u06d5 \u0646\u0627\u0648\u06d5\u0631\u06c6\u06a9\u06cc \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06ce\u06a9 \u0648\u06d5\u0634\u06ce\u0631\u0627\u0648\u06d5.',
    '\u063a\u06d5\u06cc\u0631\u06d5\u0632 \u067e\u0627\u06a9\u0627\u0646 \u06a9\u06d5\u0633 \u0646\u0627\u062a\u0648\u0627\u0646\u06ce \u062f\u06d5\u0633\u062a\u06cc \u0644\u06ce\u062f\u0627.',
    '\u0644\u06d5 \u062f\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u062f\u0646\u06cc\u0627\u0648\u06d5 \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0645 \u0642\u0633\u06d5 \u06af\u06d5\u0645\u06d5\u062a\u0627\u0646 \u062f\u06ce\u062a\u06ce\u061f',
    '\u062f\u06cc\u06a9\u06d5\u0646\u06d5 \u0628\u0698\u06cc\u0648\u06cc \u062e\u06c6\u062a\u0627\u0646\u060c \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u067e\u06ce \u0646\u0627\u0647\u06ce\u0646\u0646\u061f',
    '\u0626\u06d5\u062f\u06cc \u0633\u0627\u062a\u06ce \u0645\u0631\u062f\u0646 \u0642\u0648\u0695\u0642\u0648\u0695\u0627\u06af\u06d5\u06cc \u06af\u0631\u062a\u0648\u0648\u0646\u060c',
    '\u0626\u06ce\u0648\u06d5\u0634 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc \u0645\u06d5\u0631\u06af\u0646.',
    '\u0626\u06ce\u0645\u06d5\u0634 \u0626\u06d5\u0648\u0633\u0627 \u0632\u06c6\u0631 \u0644\u06d5\u0646\u06af\u06c6\u06cc \u0644\u06ce \u0646\u0632\u06cc\u06a9\u062a\u0631\u06cc\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0647\u06d5\u0631 \u0626\u06ce\u0648\u06d5\u0646 \u0646\u0627\u0628\u06cc\u0646\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06ce\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0646\u0627\u06a9\u06d5\u0648\u0646\u06d5 \u0628\u06d5\u0631 \u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5\u060c',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0644\u0627\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0695\u0627\u0633\u062a\u0646\u060c \u062f\u06d5 \u0644\u06d5 \u0645\u0631\u062f\u0646\u06cc \u0648\u06d5\u06af\u06ce\u0695\u0646.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u06d5\u06cc \u0646\u0632\u06cc\u06a9\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u06d5 \u0648 \u062f\u06d5\u0645\u0631\u06ce\u060c',
    '\u0644\u06d5 \u067e\u0627\u062f\u0627\u0634\u0627 \u062f\u06d5\u0633\u06ce\u062a\u06d5\u0648\u06d5 \u0648\u060c \u062f\u06d5\u0686\u062a\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc \u067e\u0695 \u062e\u06c6\u0634\u06cc.',
    '\u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0628\u0648\u0648 \u06a9\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u0633\u062a\u06d5 \u0695\u0627\u0633\u062a\u0627\u0646\u0646\u060c',
    '\u062f\u0631\u0648\u0648\u062f \u0628\u06c6 \u062a\u06c6\u060c \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u0633\u062a\u06d5 \u0695\u0627\u0633\u062a\u0646.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0648\u0648 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0646\u06d5\u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u06af\u0648\u0645\u0695\u0627 \u0628\u0648\u0648\u06af\u0646\u060c',
    '\u0644\u06d5 \u0626\u0627\u0648\u0627\u06a9\u06d5\u0644\u06cc \u0645\u06cc\u0648\u0627\u0646\u0646.',
    '\u062f\u06d5\u0634\u06cc\u0628\u06d5\u0646\u06d5 \u062f\u06c6\u0698\u06d5\u0647\u06d5\u0648\u06d5.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0695\u0627\u0633\u062a \u0647\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u06cc\u06d5.',
    '\u0633\u0627 \u062a\u06c6 \u0646\u0627\u0648\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u06d5\u0632\u0646\u06cc \u062e\u06c6\u062a\u060c \u0647\u06d5\u0631 \u0628\u06d5\u067e\u0627\u06a9\u06cc \u0648 \u062f\u0648\u0648\u0631\u06d5 \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc \u0628\u06ce\u0646\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u060c \u06af\u0634\u062a\u06cc \u067e\u06d5\u0633\u0646\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u062f\u06d5\u0646\u061b \u0626\u06d5\u0648\u06cc\u0634 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648\u0646. \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0627 \u0648 \u062f\u06d5\u0634\u0645\u0631\u06ce\u0646\u06ce \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u067e\u06ce\u0634\u0648\u0648 \u0626\u06d5\u0648\u06d5 \u0648\u060c \u062f\u0648\u0627\u06cc\u06cc \u0626\u06d5\u0648\u06d5 \u0648\u060c \u0644\u06d5\u0628\u06d5\u0631 \u0686\u0627\u0648\u06d5 \u0648 \u0646\u0627\u062f\u06cc\u0627\u0631\u06d5 \u0648\u060c \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u06d5\u0645 \u06af\u0634 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0647\u06d5\u0631\u062f\u06d5\u06cc \u0628\u06d5 \u0634\u06d5\u0634 \u0695\u06c6\u0698\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u0644\u06d5\u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5\u060c \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06cc \u062f\u0627\u0645\u06d5\u0632\u0631\u0627. \u0647\u06d5\u0631\u0686\u06cc \u0628\u06d5 \u0632\u06d5\u0648\u06cc\u062f\u0627 \u0695\u06c6\u0686\u06ce \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06ce\u06cc \u0648\u06d5\u062f\u06d5\u0631 \u0628\u06a9\u06d5\u0648\u06ce \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646 \u062f\u06ce\u062a\u06d5 \u062e\u0648\u0627\u0631\u060c \u0647\u06d5\u0631\u0686\u06cc \u067e\u06cc\u0627 \u0633\u06d5\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u060c \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0648 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce \u0628\u0646\u060c \u062f\u06d5\u06af\u06d5\u06b5\u062a\u0627\u0646\u06d5 \u0648 \u062e\u0648\u0627 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u062a\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u06ce.',
    '\u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0647\u06d5\u0631 \u0628\u06d5\u062f\u06d5\u0633\u062a \u0626\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9 \u062f\u06d5\u0628\u0631\u06ce\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627.',
    '\u0634\u06d5\u0648 \u062f\u06d5 \u0695\u06c6\u0698\u06ce \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u06ce\u0634\u06ce \u0648 \u0695\u06c6\u0698\u06ce \u062f\u06d5\u0646\u0627\u0648 \u0634\u06d5\u0648 \u0695\u0627\u062f\u06d5\u06a9\u0627 \u0648 \u0644\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062f\u06b5\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u0627 \u0648 \u0644\u06d5\u0648 \u0645\u0627\u06b5\u06d5\u06cc \u0628\u06c6\u06cc \u0647\u06ce\u0634\u062a\u0648\u0648\u0646\u06d5\u0648\u06d5\u060c \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5\u062e\u062a \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0626\u06ce\u0648\u06d5\u060c \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646 \u0648 \u0645\u0627\u06b5 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06d5\u062e\u0634\u0646\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u0645\u06d5\u0632\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u062f\u06cc \u0686\u06cc\u062a\u0627\u0646\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0646\u0627\u06a9\u06d5\u0646 \u0628\u06d5 \u062e\u0648\u0627\u061f \u06a9\u06d5 \u0686\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u0634 \u062f\u0627\u0648\u0627\u062a\u0627\u0646 \u0644\u06ce \u062f\u06d5\u06a9\u0627\u060c \u062a\u0627 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc\u062a\u0627\u0646 \u0647\u06d5\u0628\u06ce\u060c \u0626\u06ce\u0648\u06d5 \u067e\u06d5\u06cc\u0645\u0627\u0646\u0648\u0648 \u067e\u06ce\u062f\u0627\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u0631\u06d5\u06cc \u0628\u06c6 \u0633\u06d5\u0631 \u0639\u06d5\u0628\u062f\u06cc \u062e\u06c6\u06cc \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u060c \u062a\u0627 \u0644\u06d5 \u062a\u0627\u0631\u06cc\u06a9\u06cc \u062f\u06d5\u0631\u0648\u0648 \u062e\u0627 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc\u062a\u0627\u0646 \u0628\u06d5\u0631\u06ce. \u06a9\u06d5 \u062e\u0648\u0627 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06b5\u0633\u06c6\u0632\u06ce\u06a9\u06cc \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062f\u06d5\u06cc \u0686\u06cc\u062a\u0627\u0646\u06d5 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0644\u06d5\u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5\u062e\u062a \u0646\u0627\u06a9\u06d5\u0646\u061f \u06a9\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9 \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc\u062f\u0627\u0646\u060c \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5\u067e\u06ce\u0634 \u06af\u0631\u062a\u0646\u06cc \u0645\u06d5\u06a9\u06a9\u06d5\u062f\u0627 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5\u062e\u062a \u06a9\u0631\u062f \u0648 \u062f\u06d5\u06af\u0698 \u0646\u06d5\u06cc\u0627\u0631\u06d5\u06a9\u0627\u0646\u0695\u0627 \u0686\u0648\u0648\u0646\u060c \u067e\u0627\u06cc\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u06d5\u0631\u0632\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061b \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0648\u06d5\u06a9 \u06cc\u06d5\u06a9 \u0646\u06cc\u0646 \u06a9\u06d5 \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc \u0628\u06d5\u062e\u062a \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u062f\u06d5\u06af\u0698 \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u0695\u0627 \u0686\u0648\u0648\u06d5. \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u0634\u06cc\u0627\u0646 \u062e\u0648\u062f\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u067e\u06ce \u062f\u0627\u0648\u0646 \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5 \u062f\u06d5\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u06a9\u06ce\u06cc \u0648\u0627 \u0647\u06d5\u06cc\u06d5 \u0648\u0627\u0645\u06ce \u0695\u06d5\u0648\u0627 \u0628\u062f\u0627 \u0628\u06d5 \u062e\u0648\u0627 \u062a\u0627 \u0686\u06d5\u0646\u062f \u0642\u0627\u062a\u06cc \u0628\u062f\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u067e\u0627\u062f\u0627\u0634\u06cc \u0628\u0627\u0634\u06cc\u0634\u06cc \u0647\u06d5\u0628\u06ce\u061f',
    '\u0695\u06c6\u0698\u06ce\u06a9 \u062f\u06ce \u06a9\u06d5 \u062a\u06c6 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0645\u06ce\u0631\u0648\u0698\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u060c \u062a\u06cc\u0634\u06a9 \u062f\u0627\u0646\u06d5\u0648\u06d5\u06cc \u06a9\u0631\u062f\u06d5\u06cc\u0627\u0646 \u067e\u06ce\u0634 \u06a9\u06d5\u0648\u062a\u0648\u0648\u06d5 \u0648 \u0644\u06d5\u0644\u0627\u06cc \u0695\u0627\u0633\u062a\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u062f\u06d5\u0695\u0648\u0627. \u062e\u06c6\u0634\u06cc \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0626\u06d5\u0645\u0695\u06c6\u06a9\u06d5 \u062f\u06d5\u0686\u0646\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9\u06d5\u0648\u06d5 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u0631\u0648\u0647\u06d5\u0631\u060c \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646. \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0632\u06c6\u0631 \u06af\u0631\u06cc\u0646\u06af.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u062f\u0648\u0695\u0627\u0632\u06d5\u06a9\u0627\u0646\u06cc \u0645\u06ce\u0631\u0648\u0698\u0646\u060c \u0648\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06ce\u0648\u06d5 \u0644\u0627\u0645\u0627\u0646 \u0644\u06ce \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5 \u062a\u0627 \u062a\u0631\u0648\u0648\u0633\u06a9\u06ce\u06a9 \u0644\u06d5 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc\u062a\u0627\u0646 \u0648\u06d5\u0631\u06af\u0631\u06cc\u0646. \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0628\u06d5\u0631\u06d5\u0648 \u062f\u0648\u0627\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0648\u06d5\u06af\u06d5\u0695\u06ce\u0646\u060c \u0644\u06d5\u0648\u06ce \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u06d5\u0646. \u0626\u06cc\u062a\u0631 \u0626\u06d5\u0648\u0633\u0627 \u0634\u0648\u0648\u0631\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u0644\u06d5\u0645\u067e\u06d5\u0631\u060c \u062f\u0631\u06af\u0627\u06cc \u0647\u06d5\u06cc\u06d5\u061b \u0644\u06d5 \u0698\u0648\u0648\u0631\u06d5\u0648\u06d5\u06cc \u0628\u06d5\u0632\u06d5\u0648\u06cc\u06cc\u06d5 \u0648 \u0644\u06d5 \u062f\u06d5\u0631\u06d5\u0648\u06d5\u06cc \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u06d5.',
    '\u0628\u0627\u0646\u06af\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5\u0634 \u0647\u06d5\u0631 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648\u06cc\u0646\u061f \u0626\u06ce\u0698\u0646: \u0626\u0627\u0631\u06ce\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0648\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u062a\u0648\u0648\u0634\u06cc \u0628\u06d5\u06b5\u0627 \u06a9\u0631\u062f \u0648 \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0648\u0648\u0646 \u0648 \u062f\u0648\u0648\u062f\u06b5\u06cc \u0644\u06ce\u0648 \u0646\u06d5\u06af\u06d5\u0695\u0627 \u0648 \u0628\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648 \u0641\u0631\u06cc\u0648\u062a\u0627\u0646 \u062e\u0648\u0627\u0631\u062f\u061b \u0647\u06d5\u062a\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u062a\u061b \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0626\u06ce\u0648\u06d5\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0627\u062f\u06cc \u0647\u06d5\u0648\u0627 \u06a9\u0631\u062f.',
    '\u0626\u06cc\u062a\u0631 \u0626\u06d5\u0645\u0695\u06c6 \u0646\u06d5 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0648 \u0646\u06d5 \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646 \u0633\u06d5\u0631\u0627\u0646\u06d5 \u0648\u06d5\u0631\u0646\u0627\u06af\u06cc\u0631\u06ce. \u062c\u06ce\u0648 \u0626\u0627\u06af\u0631\u06d5 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u062a\u0627\u0646 \u067e\u06ce \u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0648 \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u062a\u0627\u0646 \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e\u06d5.',
    '\u0628\u06c6 \u06a9\u06d5\u0633\u0627\u0646\u06ce \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u060c \u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648 \u062f\u06d5\u0645\u06d5 \u0646\u06d5\u06af\u06d5\u06cc\u0648\u06d5 \u06a9\u06d5 \u062f\u06b5\u06cc\u0627\u0646 \u0628\u06c6 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0628\u06c6 \u0626\u06d5\u0648 \u0695\u0627\u0633\u062a\u06d5\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06cc \u0628\u06a9\u0648\u0631\u06a9\u06ce\u0646\u06ce \u0648 \u0628\u06d5\u062f\u06d5\u0631\u062f\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06d5\u0686\u0646 \u06a9\u06d5 \u062f\u06d5 \u067e\u06ce\u0634\u062f\u0627 \u0626\u06ce\u0645\u06d5 \u06a9\u062a\u06ce\u0628\u0645\u0627\u0646 \u062f\u0627\u0628\u0648\u0648\u0646\u06ce \u0648 \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5\u067e\u0627\u0634 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc \u0632\u06c6\u0631\u060c \u062a\u0648\u0648\u0634\u06cc \u062f\u06b5\u0695\u06d5\u0642\u06cc \u0647\u0627\u062a\u0646 \u0648 \u0632\u06c6\u0631\u0628\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u06cc\u0627\u0646 \u062f\u0627\u061f',
    '\u0626\u06d5\u0648\u06d5 \u0628\u0632\u0627\u0646\u0646\u060c \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u067e\u0627\u0634 \u0645\u0631\u062f\u0646\u06cc. \u0632\u06c6\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645\u0627\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0695\u0648\u0648\u0646 \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0634\u06a9\u06d5\u0645\u06cc \u0626\u06ce\u0648\u06d5 \u062a\u06ce \u0628\u06af\u06d5\u0646.',
    '\u0628\u06ce \u0633\u06c6 \u067e\u06cc\u0627\u0648\u0627\u0646 \u0648 \u0698\u0646\u0627\u0646\u06cc \u062e\u06ce\u0631\u0648\u0645\u06d5\u0646\u062f \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0648\u0627\u0645\u06cc \u0695\u06d5\u0648\u0627 \u062f\u06d5\u062f\u06d5\u0646 \u0628\u06d5 \u062e\u0648\u0627\u060c \u0686\u06d5\u0646\u062f \u0642\u0627\u062a\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06af\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u067e\u0627\u062f\u0627\u0634\u06cc \u0628\u0627\u0634\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695 \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0644\u0627\u06cc\u0627\u0646 \u0646\u06d5\u062f\u0627\u0648\u06d5. \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u06a9\u0648\u0698\u0631\u0627\u0648\u0646\u060c \u0648\u0627\u0646 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0648 \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06af\u0627 \u0648 \u0695\u06c6\u0634\u0646\u0627\u06a9\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0646\u0627\u0633\u067e\u0627\u0633\u0646 \u0648 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0646\u060c \u0626\u06d5\u0648\u0627\u0646 \u06cc\u0627\u0631\u0627\u0646\u06cc \u0626\u0627\u06af\u0631\u0646.',
    '\u0628\u0632\u0627\u0646\u0646\u060c \u0698\u06cc \u0644\u06d5 \u062f\u0646\u06cc\u0627\u06cc\u06d5 \u0647\u06d5\u0631 \u06af\u06d5\u0645\u06d5 \u0648 \u0628\u0627\u062f\u06cc \u0647\u06d5\u0648\u0627\u06cc\u06d5 \u0648 \u0628\u06c6 \u062e\u06c6 \u0628\u06d5 \u062c\u0648\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0646 \u0648 \u062e\u06c6 \u0644\u06d5\u0633\u06d5\u0631 \u06cc\u06d5\u06a9\u062a\u0631\u0695\u0627\u0646\u0627\u0646 \u0648 \u0641\u0631\u06d5 \u0628\u0648\u0648\u0646\u06cc \u0639\u06d5\u0648\u0644\u0627\u062f \u0648 \u062f\u0627\u0631\u0627\u06cc\u06cc\u062a\u0627\u0646\u06d5 \u0648 \u0648\u06d5\u06a9 \u0686\u0646\u0627\u0648\u06ce\u06a9 \u0628\u06d5 \u0647\u06c6\u06cc \u0628\u0627\u0631\u0627\u0646\u06d5\u0648\u06d5 \u0634\u06cc\u0646 \u0628\u0648\u0648\u06d5 \u0648 \u0648\u06d5\u0631\u0632\u06ce\u0631\u06cc \u067e\u06ce \u062e\u06d5\u0646\u06cc \u0628\u0648\u0648\u06d5 \u0648 \u0628\u06d5\u0631\u06d5\u0628\u06d5\u0631\u06d5 \u0648\u0634\u06a9 \u062f\u06d5\u0628\u06ce \u0648 \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0632\u06d5\u0631\u062f \u0647\u06d5\u06b5\u06af\u06d5\u0695\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u062f\u0627 \u0628\u06c6\u062a\u06d5 \u06a9\u0627\u0648\u06a9\u06c6\u062a\u06cc \u0628\u06d5\u0631 \u0628\u0627. \u0644\u06d5\u0648 \u0644\u0627\u0634 \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u0698\u0648\u0627\u0631\u0648\u0648 \u0644\u06ce \u062e\u06c6\u0634 \u0628\u0648\u0648\u0646 \u0648 \u0695\u06d5\u0632\u0627\u0645\u06d5\u0646\u062f\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5. \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u0645\u0627\u06cc\u06d5\u06cc \u062e\u06d5\u06b5\u06d5\u062a\u0627\u0646\u062f\u0646\u06d5 \u0648 \u0686\u06cc \u062a\u0631 \u0646\u06cc\u06cc\u06d5.',
    '\u0695\u06a9\u06d5 \u0628\u06a9\u06d5\u0646 \u0628\u06c6 \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5 \u0648 \u0626\u06d5\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06d5\u0634 \u06a9\u06d5 \u067e\u0627\u0646\u0627\u06cc\u06cc\u060c \u0628\u06d5 \u067e\u0627\u0646\u0627\u06cc\u06cc \u0626\u06d5\u0645 \u0639\u06d5\u0632\u0645\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc\u06d5 \u0648 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0626\u0627\u0645\u0627\u062f\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5. \u0626\u06d5\u0645 \u0686\u0627\u06a9\u06d5\u06cc\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5\u06cc\u06d5 \u0648 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0628\u06c6 \u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0628\u06ce. \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9 \u0695\u0648\u0648 \u062f\u06d5\u062f\u0627 \u0644\u06d5\u0645 \u0633\u06d5\u0631\u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u0647\u06d5\u0631 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9 \u06a9\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u062e\u0648\u062f\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06ce - \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u0628\u0647\u06ce\u0646\u06cc\u0646 - \u0644\u06d5 \u062f\u06d5\u0641\u062a\u06d5\u0631\u06ce\u06a9\u062f\u0627 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5 \u0648 \u0626\u06d5\u0645\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u0633\u0627\u0646\u06d5.',
    '\u0647\u06d5\u062a\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u06c6 \u0634\u062a\u06ce \u0644\u06d5 \u062f\u06d5\u0633\u062a\u0648\u0648 \u062f\u06d5\u0686\u06ce\u060c \u062e\u06d5\u0645 \u0646\u06d5\u062e\u06c6\u0646 \u0648 \u0628\u06d5 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u0648\u0648 \u062f\u06b5\u062e\u06c6\u0634 \u0646\u06d5\u0628\u0646. \u062e\u0648\u0627 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0634 \u0646\u0627\u0648\u06ce \u06a9\u06d5 \u0628\u06d5 \u0641\u06cc\u0632\u0646 \u0648 \u0634\u0627\u0646\u0627\u0632\u06cc \u0628\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646:',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0695\u0698\u062f\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u062e\u06d5\u06b5\u06a9\u06cc\u0634 \u0628\u06c6 \u0695\u0698\u062f\u06cc \u0647\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0644\u0627\u0633\u0627\u0631\u06cc \u062f\u06d5\u06a9\u0627\u060c \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u0633\u0627 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5 \u0648 \u0647\u06d5\u0631 \u0634\u06cc\u0627\u0648\u06cc \u062a\u0627\u0631\u06cc\u0641 \u06a9\u0631\u062f\u0646\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u0645\u0627\u0646 \u0646\u0627\u0631\u062f \u0628\u06d5 \u0686\u06d5\u0646\u062f \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0648\u06d5 \u0648 \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u060c \u06a9\u062a\u06ce\u0628 \u0648 \u0647\u06c6\u06cc \u0647\u06d5\u06b5\u0633\u06d5\u0646\u06af\u0627\u0646\u062f\u0646\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06d5 \u062e\u0648\u0627\u0631\u061b \u0647\u06d5\u062a\u0627 \u062e\u06d5\u06b5\u06a9 \u0628\u06ce \u062c\u06cc\u0627\u0648\u0627\u0632\u06cc \u0644\u06d5\u0646\u0627\u0648 \u06cc\u06d5\u06a9\u0627 \u0695\u06d5\u0641\u062a\u0627\u0631 \u0628\u06a9\u06d5\u0646. \u0626\u0627\u0633\u0646\u06cc\u0634\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u060c \u06a9\u06d5 \u0647\u06ce\u0632\u06cc \u0628\u0695\u0634\u062a\u06cc \u0632\u06c6\u0631\u06d5 \u0648 \u0632\u06c6\u0631 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u062a\u06ce\u062f\u0627 \u0648\u061b \u062a\u0627 \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u0628\u0632\u0627\u0646\u06ce \u06a9\u06ce \u0644\u06d5 \u0646\u06d5\u062f\u06cc\u0627\u0631 \u062f\u06ce\u062a\u06d5 \u06cc\u0627\u0631\u06cc \u062e\u06c6\u06cc \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0647\u06ce\u0632 \u0648 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0646\u0648\u0648\u062d \u0648 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f\u061b \u067e\u0644\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u06cc\u06d5\u062a\u06cc \u0648 \u06a9\u062a\u06ce\u0628\u06cc\u0634\u0645\u0627\u0646 \u062f\u0627\u0628\u0648\u0648 \u0628\u06d5 \u0646\u06d5\u062a\u06d5\u0648\u06d5\u06a9\u06d5\u06cc\u0627\u0646. \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06b5\u06a9\u06d5\u0648\u062a \u0648 \u0644\u06d5\u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u0628\u0648\u0648\u0646.',
    '\u0644\u06d5\u067e\u0627\u0634\u0627\u0646 \u0628\u06d5\u0634\u0648\u06ce\u0646 \u0626\u06d5\u0648\u0627\u0646\u062f\u0627 \u06af\u06d5\u0644\u06ce\u06a9 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u0646\u061b \u062f\u0648\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0639\u06cc\u0633\u0627\u06cc \u06a9\u0648\u0695\u06cc \u0645\u0631\u06cc\u06d5\u0645\u0646 \u0646\u0627\u0631\u062f \u0648 \u0626\u06cc\u0646\u062c\u06cc\u0644\u0645\u0627\u0646 \u067e\u06ce \u062e\u06d5\u06b5\u0627\u062a \u06a9\u0631\u062f. \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc \u0648 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u0634\u0645\u0627\u0646 \u062e\u0633\u062a\u06d5 \u062f\u06b5\u06cc \u067e\u06d5\u06cc\u0695\u06d5\u0648\u0627\u0646\u06cc\u061b \u0626\u06d5\u0648 \u0695\u06d5\u0628\u06d5\u0646\u06cc\u06d5\u0634 \u06a9\u06d5 \u0647\u06d5\u06cc\u0627\u0646\u06d5 \u0626\u06ce\u0645\u06d5 \u0628\u06c6\u0645\u0627\u0646 \u0646\u06d5\u0646\u0648\u0648\u0633\u06cc\u0648\u0646\u060c \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5. \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0634\u06cc\u0627\u0646 \u0628\u06c6 \u0695\u06d5\u0632\u0627\u0645\u06d5\u0646\u062f\u06cc \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u061b \u0628\u06c6\u0634\u06cc\u0627\u0646 \u0646\u06d5\u06a9\u0631\u0627 \u0648\u06d5\u06a9 \u067e\u06ce\u0648\u06cc\u0633\u062a\u06d5 \u067e\u06ce\u06a9\u06cc \u0628\u06ce\u0646\u0646. \u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0648\u0627\u0646\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0628\u0648\u0648\u0646\u060c \u067e\u0627\u062f\u0627\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646\u0645\u0627\u0646 \u062f\u0627\u0646\u06ce\u061b \u0632\u06c6\u0631\u0628\u06d5\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631 \u0628\u0648\u0648\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u062a\u0631\u0633\u0648\u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0628\u0695\u0648\u0627 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0626\u06d5\u0648 \u0628\u06ce\u0646\u0646. \u062e\u0648\u0627 \u062f\u0648\u0648 \u0633\u06d5\u0631\u06d5 \u0686\u0627\u06a9\u06d5\u062a\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5 \u062f\u06d5\u06a9\u0627 \u0648 \u062a\u06cc\u0631\u06ce\u0698\u06ce\u06a9\u062a\u0627\u0646 \u0648\u06d5\u067e\u06ce\u0634 \u062f\u06d5\u062e\u0627\u061b \u0628\u06d5 \u0647\u06c6\u06cc \u0626\u06d5\u0648 \u0628\u06d5\u0695\u06ce\u062f\u0627 \u0628\u0695\u06c6\u0646 \u0648 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u0648\u0648 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u062e\u06d5\u062a\u0627\u067e\u06c6\u0634 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u0648\u0627 \u0646\u06d5\u0632\u0627\u0646\u0646 \u0644\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0626\u06d5\u0648\u0627\u0646 \u0647\u06cc\u0686 \u0634\u062a\u06cc\u0627\u0646 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5. \u0686\u0627\u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u06c6\u06cc \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce \u0628\u06d5 \u0686\u0627\u06a9\u06d5 \u062f\u06d5\u062e\u0648\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u0686\u0627\u06a9\u06d5\u06cc \u06a9\u06d5 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u060c \u0632\u06c6\u0631 \u0647\u06ce\u0698\u0627\u06cc\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0642\u0633\u06d5\u06cc \u0626\u06d5\u0648 \u0698\u0646\u06d5\u06cc \u0628\u06cc\u0633\u062a \u06a9\u06d5 \u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u0645\u06ce\u0631\u062f\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u062a\u06c6 \u06a9\u06ce\u0634\u06d5\u06cc \u062f\u06d5\u06a9\u0631\u062f \u0648 \u06af\u0627\u0632\u0646\u062f\u06d5\u06cc \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u06a9\u0631\u062f. \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0628\u06cc\u0633\u062a \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0648\u062a\u0648\u0648\u06ce\u0698\u062a\u0627\u0646 \u0628\u0648\u0648\u061b \u062e\u0648\u062f\u0627\u0634 \u0628\u06cc\u0633\u06d5\u0631 \u0648 \u0628\u06cc\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u0626\u06ce\u0648\u06d5\u060c \u0628\u06d5 \u067e\u06cc\u0631\u06d5\u06a9\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u00ab\u062a\u06c6 \u062f\u0627\u06cc\u06a9\u0645\u06cc\u00bb\u061b \u0698\u0646\u06d5\u06a9\u0627\u0646 \u0646\u0627\u0628\u0646 \u0628\u06d5 \u062f\u0627\u06a9\u06cc\u0627\u0646\u061b \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5 \u062f\u0627\u06a9\u06cc\u0627\u0646\u0646 \u06a9\u06d5 \u062f\u06cc\u0648\u06cc\u0627\u0646\u0646\u061b \u0626\u06d5\u0648\u0627\u0646 \u0642\u0633\u06d5\u06cc \u062e\u0631\u0627\u067e \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0628\u0648\u062e\u062a\u0627\u0646\u06d5 \u0647\u06d5\u06b5\u06cc \u062f\u06d5\u0628\u06d5\u0633\u062a\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062e\u06d5\u062a\u0627\u067e\u06c6\u0634\u06d5.',
    '\u062c\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0647\u0627\u0648\u062c\u0648\u0648\u062a\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646 \u00ab\u062a\u06c6 \u062f\u0627\u06cc\u06a9\u0645\u06cc\u00bb \u0648 \u067e\u0627\u0634\u06cc \u067e\u06d5\u0634\u06cc\u0645\u0627\u0646 \u062f\u06d5\u0628\u0646\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5\u06cc \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0628\u0646\u0648\u0648\u0646\u060c \u0626\u06d5\u0634\u06ce \u06a9\u06c6\u0644\u06d5\u06cc\u06d5\u06a9 \u0626\u0627\u0632\u0627\u062f \u06a9\u06d5\u0646. \u062e\u0648\u0627 \u0648\u0627\u0648 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u062f\u06d5\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0647\u06d5\u0631\u06a9\u06ce \u0646\u06d5\u06cc\u0628\u0648\u0648\u060c \u0628\u0627 \u062f\u0648\u0648 \u0645\u0627\u0646\u06af\u06cc \u06cc\u06d5\u06a9 \u0644\u06d5\u062f\u0648\u0648 \u06cc\u06d5\u06a9\u060c \u0628\u06d5\u0695\u06c6\u0698\u0648\u0648 \u0628\u06ce\u061b \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u0628\u06af\u06d5\u0646 \u0628\u06d5 \u06cc\u06d5\u06a9\u062a\u0631. \u06a9\u06d5\u0633\u06ce \u0626\u06d5\u0645\u06d5\u0634\u06cc \u0646\u06d5\u062a\u0648\u0627\u0646\u06cc\u060c \u062e\u06c6\u0631\u0627\u06a9 \u0628\u06d5 \u0634\u06ce\u0633\u062a \u0647\u06d5\u0698\u0627\u0631 \u0628\u062f\u0627. \u0626\u06d5\u0645\u06d5 \u0628\u06c6\u06cc\u06d5 \u06a9\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062a\u0627\u0646 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0647\u06d5\u0628\u06ce \u0648 \u0626\u06d5\u0645\u06d5 \u0633\u0646\u0648\u0648\u0631\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5. \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5\u060c \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0698\u0627\u06cc\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u062f\u0648\u0686\u0627\u0631\u06cc \u0646\u0627\u0686\u0627\u0631\u06cc \u062f\u06d5\u0628\u0646\u061b \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646 \u0642\u06d5\u0631\u06d5\u0628\u06d5\u062e\u062a \u0628\u0648\u0648\u0646. \u0626\u06ce\u0645\u06d5 \u06af\u06d5\u0644\u06ce\u06a9 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u0632\u06c6\u0631 \u0626\u0627\u0634\u06a9\u0631\u0627\u0645\u0627\u0646 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u060c \u0626\u06d5\u0648\u06cc \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0646\u0627\u06a9\u0627\u060c \u0628\u06d5\u0634\u06cc\u0627\u0646 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u062f\u0627\u0646\u06ce\u06a9\u06cc \u0626\u0627\u0648\u0695\u0648\u0648\u0628\u06d5\u0631\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u062e\u0648\u0627 - \u062a\u06ce\u06a9\u0695\u0627 - \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u06ce \u0686\u06cc\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u062e\u0648\u0627 \u06a9\u0631\u062f\u06d5\u06cc\u0627\u0646\u06cc \u0698\u0645\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06d5 \u0628\u06cc\u0631\u06cc\u0627\u0646 \u0686\u0648\u0648\u06d5. \u062e\u0648\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u0634\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u062a\u06c6 \u0646\u06d5\u062a\u0632\u0627\u0646\u06cc\u0648\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0648\u06cc \u0628\u06ce\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce\u061f \u0647\u06d5\u062a\u0627 \u0633\u06ce \u06a9\u06d5\u0633 \u0628\u06d5 \u06cc\u06d5\u06a9\u06d5\u0648\u06d5 \u0686\u067e\u06d5 \u0628\u06a9\u06d5\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0648 \u0686\u0648\u0627\u0631\u06d5\u0645\u06cc\u0646\u06cc\u0627\u0646\u06d5 \u0648 \u067e\u06ce\u0646\u062c \u0628\u0646 \u0626\u06d5\u0648 \u0634\u06d5\u0634\u06d5\u0645\u06cc\u0646\u06cc\u0627\u0646\u06d5 \u0648 \u06a9\u06d5\u0645\u062a\u0631 \u0628\u0646 \u0648 \u0632\u06ce\u062f\u06d5\u062a\u0631 \u0628\u0646\u060c \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0648\u06ce \u0628\u0646\u060c \u0626\u06d5\u0648 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646\u06d5 \u0648 \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u06cc\u0627\u0645\u06d5\u062a\u06ce\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u06ce \u0686\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f. \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0626\u06d5\u06cc \u062a\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062a \u0646\u06d5\u062f\u06cc\u0648\u06d5\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0648\u062a\u0631\u0627: \u0686\u067e\u06d5 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u06cc \u06a9\u06d5 \u0648\u062a\u06cc\u0627\u0646 \u0645\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0647\u06d5\u0631 \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u0646\u0627\u0695\u06d5\u0648\u0627 \u0686\u067e\u06d5\u06cc\u0627\u0646\u06d5 \u0648 \u06af\u0648\u0646\u0627 \u0648 \u0633\u06d5\u0631\u067e\u06ce\u0686\u06cc \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0644\u06d5 \u0642\u0633\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062f\u06d5\u0631 \u062f\u06d5\u0686\u0646\u061f \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u062a\u06c6\u0634 \u062f\u06d5\u0628\u0646\u060c \u0626\u06d5\u0648 \u0633\u06d5\u0644\u0627\u0645\u06d5\u06cc \u0644\u06d5 \u062a\u06c6\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5\u0648 \u0633\u06d5\u0644\u0627\u0645\u06d5 \u062c\u06cc\u0627\u0648\u0627\u0632\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u062a\u06c6\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5. \u0644\u06d5\u067e\u0627\u06b5 \u062e\u06c6\u06cc\u0627\u0646\u06cc\u0634\u062f\u0627 \u0626\u06ce\u0698\u0646: \u062f\u06d5\u06cc \u0628\u06c6 \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0645 \u0648\u062a\u06d5\u06cc\u06d5 \u062e\u0648\u062f\u0627 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u0645\u0627\u0646 \u0646\u0627\u062f\u0627\u061f \u062f\u06d5\u0686\u0646\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0628\u06d5\u0633\u06cc\u0627\u0646\u06d5 \u0648 \u0633\u06d5\u0631\u06d5\u0646\u062c\u0627\u0645\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0631\u06af\u0627 \u0626\u06ce\u0648\u06d5 \u0686\u067e\u06d5\u062a\u0627\u0646 \u06a9\u0631\u062f\u060c \u0628\u06c6 \u06af\u0648\u0646\u0627\u0647\u0640 \u0648 \u062f\u06d5\u0633\u062f\u0631\u06ce\u0698\u06cc \u0648 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0644\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0633\u0631\u062a\u06d5 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u0686\u067e\u06d5\u06a9\u06d5\u0634\u062a\u0627\u0646 \u0628\u06c6 \u0686\u0627\u06a9\u06cc \u0648 \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u06cc \u0628\u06ce \u0648 \u0644\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u062a\u0631\u0633\u0648\u0648 \u0647\u06d5\u0628\u06ce \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u06a9\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0633\u0631\u062a\u06d5 \u0644\u06d5 \u0634\u06d5\u06cc\u062a\u0627\u0646\u06d5\u0648\u06d5\u06cc\u06d5\u060c \u0647\u06d5\u062a\u0627 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06cc \u067e\u06ce \u062e\u06d5\u0645\u06af\u06cc\u0646 \u06a9\u0627\u061b \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u0628\u06d5\u0628\u06ce \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u062f\u0627\u060c \u0647\u06cc\u0686 \u0632\u06cc\u0627\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u0646\u0627\u062f\u0627. \u0628\u0627 \u0647\u06d5\u0631\u06a9\u06ce \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u067e\u06ce\u0631\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u06a9\u06c6\u0695 \u067e\u06ce\u0627\u0646 \u0648\u062a\u0646: \u062c\u06ce\u06af\u06d5\u0648\u06d5 \u06a9\u06d5\u0646\u060c \u062c\u06ce\u06af\u06d5\u0648\u06d5 \u06a9\u06d5\u0646! \u0647\u06d5\u062a\u0627 \u062e\u0648\u062f\u0627\u0634 \u062c\u06ce\u06af\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5. \u06af\u06d5\u0631 \u06af\u0648\u062a\u06cc\u0634\u06cc\u0627\u0646: \u0695\u0627\u0633\u062a\u06d5\u0648\u06d5 \u0628\u0646\u060c \u0695\u0627\u0633\u062a\u06d5\u0648\u06d5 \u0628\u0646! \u062e\u0648\u062f\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0646\u06af\u06c6 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0632\u0627\u0646\u06cc\u0627\u0631\u06cc \u0628\u06d5\u0634\u062f\u0627\u0631\u0646\u060c \u0628\u06d5 \u0686\u06d5\u0646\u062f \u067e\u0644\u06d5 \u0628\u06d5\u0631\u0632\u062a\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u06cc\u06a9\u06d5\u0646\u060c \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0631\u06af\u0627 \u0648\u06cc\u0633\u062a\u0648\u0648 \u0633\u0631\u062a\u06d5 \u0648\u06d5\u0644 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0628\u06a9\u06d5\u0646\u060c \u0628\u06d5\u0631\u0644\u06d5 \u0633\u0631\u062a\u06d5 \u06a9\u0631\u062f\u0646\u06d5\u06a9\u06d5 \u062e\u06ce\u0631\u06ce\u06a9 \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062e\u0627\u0633\u062a\u0631 \u0648 \u067e\u0627\u06a9\u0698\u062a\u0631\u06d5. \u06af\u06d5\u0631 \u0646\u06d5\u0634\u062a\u0627\u0646\u0628\u0648\u0648\u060c \u0626\u06d5\u0648\u0627 \u062e\u0648\u062f\u0627 \u0644\u06ce\u0648 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0644\u06d5\u0628\u06d5\u0631\u062a\u0627\u0646 \u06af\u0631\u0627\u0646 \u0647\u0627\u062a \u0628\u06d5\u0631\u0644\u06d5 \u0633\u0631\u062a\u06d5 \u062e\u06ce\u0631\u06ce \u0628\u06a9\u06d5\u0646\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0645\u06d5\u0634\u062a\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f \u0648 \u062e\u0648\u062f\u0627 \u0644\u06d5\u0645\u06d5 \u0639\u0627\u0641\u0648\u0648\u06cc \u06a9\u0631\u062f\u0646\u060c \u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u062f\u06d5\u0646 \u0648 \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0628\u0646. \u062e\u0648\u0627 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u062a\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u062a \u0646\u06d5\u062f\u06cc\u062a \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u063a\u06d5\u0632\u06d5\u0628\u06cc \u0644\u06ce \u06af\u0631\u062a\u0648\u0648\u0646\u061f \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u06cc\u0646\u061b \u0644\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0646\u06cc\u0646\u061b \u0628\u06d5 \u062f\u0631\u06c6 \u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u062e\u06c6\u0646 \u0648 \u062f\u06d5\u0634\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u062f\u0631\u06c6 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u062e\u0648\u062f\u0627 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc \u0628\u06c6 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0633\u0627\u0632\u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e\u06d5.',
    '\u0633\u0648\u06ce\u0646\u062f\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0628\u0648\u0648\u06d5 \u0645\u06d5\u062a\u0627\u06b5\u06ce\u06a9 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0628\u0648\u0648\u0646\u06d5 \u0644\u06d5\u0645\u067e\u06d5\u0631\u06cc \u0695\u06ce\u06cc \u062e\u0648\u062f\u0627\u061b \u0633\u0627 \u0628\u06c6\u06cc\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0626\u0627\u0628\u0695\u0648\u0648\u0628\u06d5\u0631 \u0647\u06d5\u06cc\u06d5.',
    '\u0646\u06d5 \u0633\u0627\u0645\u0627\u0646\u060c \u0646\u06d5 \u0695\u06c6\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 - \u0644\u06d5 \u0628\u06d5\u0631\u0627\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u0648\u06d5 - \u0646\u0627\u062a\u0648\u0627\u0646\u0646 \u0647\u06cc\u0686\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0646 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646.',
    '\u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u06af\u0634\u062a\u06cc\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648 \u062c\u0627\u0631 \u0633\u0648\u06ce\u0646\u062f\u06ce \u0628\u06c6 \u0626\u06d5\u0648 \u062f\u06d5\u062e\u06c6\u0646\u060c \u0648\u06d5\u06a9\u0648\u0648 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0633\u0648\u06ce\u0646\u062f \u0626\u06d5\u062e\u06c6\u0646\u061b \u0644\u0627\u0634\u06cc\u0627\u0646 \u0648\u0627\u06cc\u06d5 \u0634\u062a\u06ce\u06a9\u06cc\u0627\u0646 \u062f\u06d5 \u0628\u0627\u0631\u0627\u0646\u062f\u0627\u06cc\u06d5. \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u0634\u06d5\u06cc\u062a\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u0632\u0627\u06b5 \u0628\u0648\u0648\u06d5 \u0648 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627\u06cc \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u0631\u062f\u0648\u0648\u0646\u061b \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06c6\u0645\u06d5\u06b5\u06d5\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u0646. \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06d5\u06a9\u06d5\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646 \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0698\u0627\u06cc\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633 \u0632\u06d5\u0628\u0648\u0648\u0646\u062a\u0631\u0646.',
    '\u062e\u0648\u062f\u0627 \u062e\u06c6\u06cc \u0628\u0695\u06cc\u0627\u0631\u06cc \u062f\u0627\u0648\u06d5: \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u0645\u0646 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u0645 \u0633\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u06cc\u0646. \u062e\u0648\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06d5.',
    '\u062a\u06c6 \u0646\u0627\u0628\u06cc\u0646\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0628\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u060c \u0646\u0627\u0628\u06ce \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u0628\u0646\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u067e\u06ce\u0686\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u0646. \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0648\u06a9\u06cc\u0634\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u0641\u0631\u0632\u06d5\u0646\u062f\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u0628\u0631\u0627\u06cc\u0627\u0646\u060c \u06cc\u0627\u0646 \u0644\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0695\u0648\u0627\u06cc \u0644\u06d5 \u062f\u06b5\u06cc\u0627\u0646 \u062c\u06ce\u06af\u06cc\u0631 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0628\u06d5 \u0647\u06ce\u0632\u06ce\u06a9\u06cc \u062a\u0627\u06cc\u0628\u06d5\u062a\u06cc \u062e\u06c6\u06cc \u06cc\u0627\u0631\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646\u061b \u062f\u06d5\u06cc\u0627\u0646 \u0646\u06ce\u0631\u06ce\u062a\u06d5 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646\u061b \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0644\u06ce \u0695\u0627\u0632\u06cc \u0628\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0695\u0627\u0632\u06cc\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06d5\u06cc \u062e\u0648\u062f\u0627\u0646\u061b \u0628\u0627\u0634 \u0628\u0632\u0627\u0646\u0646 \u0647\u06d5\u0631 \u06a9\u06c6\u0645\u06d5\u06b5\u06d5\u06cc \u062e\u0648\u0627 \u0695\u0632\u06af\u0627\u0631\u0646.',
    '\u0647\u06d5\u0631 \u0628\u06d5 \u067e\u0627\u06a9\u06cc \u0648 \u0628\u06ce \u0639\u06d5\u06cc\u0628\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u06b5\u06ce\u0646\u060c \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627 \u0647\u06d5\u0646\u061b \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u06a9\u06d5 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06cc \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u0627\u0646\u06cc\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u0628\u0648\u0648\u0646 - \u0644\u06d5 \u0648\u06d5\u062f\u06d5\u0631\u0646\u0627\u0646\u06cc \u0647\u06d5\u0648\u06d5\u06b5\u062f\u0627 - \u0644\u06d5 \u0632\u06ce\u062f\u06cc \u062e\u06c6 \u062a\u06d5\u0631\u06d5 \u06a9\u0631\u062f\u0646. \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0648\u0627 \u0646\u06d5\u0628\u0648\u0648 \u06a9\u06d5 \u062f\u06d5\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u061b \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0648\u0627\u06cc\u0627\u0646 \u062e\u06d5\u06cc\u0627\u06b5 \u062f\u06d5\u06a9\u0631\u062f \u0642\u06d5\u06b5\u0627\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u06ce. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06cc \u0648\u0627\u0648\u06d5 \u067e\u0627\u0631\u06ce\u0632\u06cc \u0628\u0631\u062f\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u060c \u06a9\u06d5 \u0644\u06d5 \u062e\u06d5\u06cc\u0627\u06b5\u06cc\u0634\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648. \u0648\u0627\u06cc \u062a\u0631\u0633 \u0647\u0627\u0648\u06cc\u0634\u062a\u06d5 \u062f\u06b5\u06cc\u0627\u0646\u060c \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06cc \u062e\u06c6 \u0648 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u060c \u06a9\u0627\u0648\u0644 \u062f\u06d5\u06a9\u0631\u062f. \u0633\u0627 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0631\u0633 \u0648\u06d5\u0631\u06af\u0631\u0646\u060c \u0626\u06d5\u06cc \u06af\u0631\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632\u0627\u0646!',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0626\u0627\u0648\u0627\u0631\u06d5\u06cc\u06cc \u0644\u06d5 \u0633\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0646\u06d5\u0646\u0648\u0648\u0633\u06cc\u0628\u0627\u060c \u0647\u06d5\u0631 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u06d5\u06a9\u0631\u062f\u0646 \u0648 \u0644\u06d5\u0648 \u062f\u0646\u06cc\u0627\u0634 \u0628\u06d5\u0634\u06cc\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5 \u0626\u0627\u06af\u0631 \u0628\u0648\u0648.',
    '\u0626\u06d5\u0645\u06d5\u0634 \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062f\u0698\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0628\u0648\u0648\u0646. \u0647\u06d5\u0631\u06a9\u06d5\u0634 \u062f\u0698\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0626\u06d5\u0648 \u0628\u06ce\u060c \u062a\u06c6\u06b5\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631\u06d5.',
    '\u0647\u06d5\u0631 \u062f\u0627\u0631\u062e\u0648\u0631\u0645\u0627\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u0628\u0695\u06cc\u060c \u06cc\u0627\u0646 \u0644\u06ce\u06cc \u06af\u06d5\u0695\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0695\u06d5\u06af\u06cc \u062e\u06c6 \u0628\u0645\u06ce\u0646\u0646\u060c \u0647\u06d5\u0645\u0648\u0648\u06cc \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u0648\u0648\u061b \u062a\u0627 \u0626\u06d5\u0648 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u0627\u0646\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06a9\u0627.',
    '\u0647\u06d5\u0631 \u062a\u0627\u06b5\u0627\u0646\u06ce\u06a9 \u0644\u06d5 \u062f\u0627\u0646\u06cc\u0634\u062a\u0648\u0648\u06cc \u0626\u06d5\u0648 \u06af\u0648\u0646\u062f\u0627\u0646\u06d5 \u06a9\u06d5 \u062e\u0648\u0627 \u062f\u0627\u06cc \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u06cc\u0646 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0628\u06d5 \u0633\u0648\u0627\u0631\u06cc \u0626\u06d5\u0633\u067e \u06cc\u0627\u0646 \u0628\u06d5 \u0648\u0634\u062a\u0631\u06d5\u0648\u06d5 \u0628\u06c6\u06cc \u0686\u0648\u0648\u0646. \u0628\u06d5\u06b5\u0627\u0645 \u062e\u0648\u0627 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc - \u0626\u06d5\u06af\u06d5\u0631 \u062d\u06d5\u0632 \u06a9\u0627 - \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0632\u0627\u06b5 \u062f\u06d5\u06a9\u0627. \u062e\u0648\u0627 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u062a\u0627\u06b5\u0627\u0646\u06ce \u0644\u06d5 \u062f\u0627\u0646\u06cc\u0634\u062a\u0648\u0648\u06cc \u0626\u06d5\u0648 \u0634\u0627\u0631\u0627\u0646\u06d5 \u062e\u0648\u0627 \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5\u060c \u0628\u06c6 \u062e\u0648\u062f\u0627 \u0648 \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc\u06d5 \u0648 \u0628\u06c6 \u062e\u0632\u0645\u06d5 \u0646\u0632\u06cc\u06a9\u06d5\u06a9\u0627\u0646 \u0648 \u0647\u06d5\u062a\u06cc\u0648\u0627\u0646 \u0648 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646 \u0648 \u0695\u06ce\u0628\u0648\u0627\u0631\u0627\u0646\u06d5\u061b \u0646\u06d5\u06a9 \u0628\u0628\u06ce\u062a\u06d5 \u0633\u06d5\u0631\u0645\u0627\u06cc\u06d5\u06cc \u0626\u0627\u06b5 \u0648 \u06af\u06c6\u0631 \u06a9\u0631\u062f\u0646 \u0644\u06d5 \u0646\u0627\u0648 \u062f\u06d5\u0648\u06b5\u06d5\u0645\u06d5\u0646\u062f\u06d5\u06a9\u0627\u0646\u0648\u0648\u061b \u0647\u06d5\u0631\u0686\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u062f\u06d5\u0648\u062f\u0627\u062a\u06ce\u060c \u0644\u06ce\u06cc \u0648\u06d5\u0631\u06af\u0631\u0646\u061b \u0644\u06d5 \u0647\u06d5\u0631\u0686\u06cc\u0634\u062a\u0627\u0646 \u0628\u06ce \u0628\u06d5\u0634 \u06a9\u0627\u060c \u062f\u06d5\u0633\u062a\u06cc \u0628\u06c6 \u0645\u06d5\u0628\u06d5\u0646. \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646\u061b \u06a9\u06d5 \u062a\u06c6\u06b5\u06d5\u06cc \u062e\u0648\u0627 \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631\u06d5.',
    '[\u0626\u06d5\u0648 \u062a\u0627\u06b5\u0627\u0646\u06d5] \u0628\u06c6 \u0626\u06d5\u0648 \u0647\u06d5\u0698\u0627\u0631\u06d5 \u062f\u0648\u0648\u0631\u06d5 \u0648\u06b5\u0627\u062a\u0627\u0646\u06d5\u06cc\u06d5\u060c \u06a9\u06d5 \u0644\u06d5 \u0645\u0627\u06b5 \u0648 \u0632\u06ce\u062f\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0631 \u06a9\u0631\u0627\u0648\u0646\u061b \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc \u0628\u06d5\u0647\u0631\u06d5 \u0648 \u0695\u06d5\u0632\u0627\u0645\u06d5\u0646\u062f\u06cc \u062e\u0648\u0627\u0646 \u0648 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0695\u0627\u0633\u062a\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u0645\u06d5\u062f\u06cc\u0646\u06d5 \u0646\u06cc\u0634\u062a\u06d5\u062c\u06ce \u0628\u0648\u0648\u0646 \u0648 \u0628\u06d5 \u062f\u06b5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u060c \u062d\u06d5\u0632 \u0628\u06d5\u0648 \u0626\u0627\u0648\u0627\u0631\u0627\u0646\u06d5 \u062f\u06d5\u06a9\u06d5\u0646 \u06a9\u06d5 \u067e\u06d5\u0646\u0627\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u0646 \u0648 \u0628\u06d5 \u062f\u06b5\u06cc\u0634 \u0626\u06ce\u0631\u06d5\u06cc\u06cc \u0646\u0627\u0628\u06d5\u0646 \u0628\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0628\u06d5\u0648\u0627\u0646 \u062f\u0631\u0627\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u067e\u06ce\u0634\u062a\u0631 \u062f\u06d5\u0632\u0627\u0646\u0646\u060c \u0647\u06d5\u0631 \u0686\u06d5\u0646\u062f \u062f\u06d5\u0633\u062a \u062a\u06d5\u0646\u06af\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5. \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u06cc\u0634 \u062e\u06c6 \u0644\u06d5 \u0695\u0698\u062f\u06cc \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0632\u06af\u0627\u0631\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u062f\u0648\u0627\u06cc \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0647\u0627\u062a\u0648\u0648\u0646\u060c \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u0644\u06d5 \u0626\u06ce\u0645\u06d5 \u0648 \u0644\u06d5\u0648 \u0628\u0631\u0627\u06cc\u0627\u0646\u06d5\u0645\u0627\u0646 \u0648\u0627 \u067e\u06ce\u0634 \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u062e\u06c6\u0634 \u0628\u06d5\u061b \u06a9\u06cc\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0645\u06d5\u062e\u06d5 \u062f\u06b5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u0628\u0648\u0648\u0646. \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646! \u062a\u06c6\u06cc \u062f\u06b5\u0633\u06c6\u0632\u06cc \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc.',
    '\u0626\u06d5\u062f\u06cc \u0646\u06d5\u062a\u062f\u06cc \u062f\u0648\u0648\u0695\u0627\u0632\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u0647\u0627\u0648\u0633\u0648\u0646\u0641\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 - \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u0648 \u062f\u0698\u06cc \u062f\u06cc\u0646 \u0628\u0648\u0648\u0646 - \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0631 \u06a9\u0631\u0627\u0646\u060c \u0628\u06ce\u06af\u0648\u0645\u0627\u0646\u06d5 \u0626\u06ce\u0645\u06d5\u0634 \u0648\u06d5\u0644\u062a\u0627\u0646 \u062f\u06d5\u0631\u062f\u06d5\u0686\u06cc\u0646 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u06d5 \u0632\u06cc\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u06af\u0648\u06ce\u0695\u0627\u06cc\u06d5\u06b5\u06cc \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06ce \u0646\u0627\u0628\u06cc\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5 \u06af\u0698\u06cc\u0634\u062a\u0627\u0646 \u0695\u0627\u0686\u0648\u0648\u0646\u060c \u0626\u06ce\u0645\u06d5 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc\u062a\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646. \u062e\u0648\u0627\u0634 \u0634\u0627\u06cc\u06d5\u062a\u06d5 \u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u0631\u06cc\u0634 \u06a9\u0631\u0627\u0646\u060c \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5\u06cc\u0627\u0646 \u062f\u06d5\u0631 \u0646\u0627\u0686\u0646\u061b \u0626\u06d5\u06af\u06d5\u0631 \u0648\u06d5\u0644\u06cc\u0634\u06cc\u0627\u0646 \u0628\u062c\u06d5\u0646\u06af\u0646\u060c \u0626\u06d5\u0648\u0627\u0646 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u06cc\u0627\u0646 \u0646\u0627\u062f\u06d5\u0646\u061b \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0646\u060c \u067e\u0634\u062a\u06cc\u0627\u0646 \u062a\u06ce \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0647\u06d5\u06b5\u062f\u06ce\u0646\u061b \u0644\u06d5 \u067e\u0627\u0634\u0627\u0646\u06cc\u0634 \u06a9\u06d5\u0633\u06ce \u062f\u06d5 \u0647\u0627\u0646\u0627\u06cc\u0627\u0646 \u0646\u0627\u06cc\u06d5.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u062f\u06b5\u06cc\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u067e\u062a\u0631 \u062f\u06d5\u062a\u0631\u0633\u06ce \u062a\u0627 \u0644\u06d5 \u062e\u0648\u0627\u061b \u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u06cc \u0648\u0627\u0646 \u062a\u06ce \u0646\u0627\u06af\u06d5\u0646.',
    '\u0628\u06d5 \u062a\u06ce\u06a9\u0695\u0627\u06cc\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u06af\u0698\u062a\u0627\u0646\u062f\u0627 \u0646\u0627\u06cc\u06d5\u0646\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5\u0646\u0627\u0648 \u0626\u06d5\u0648 \u0634\u0627\u0631\u0627\u0646\u06d5\u06cc \u0642\u06d5\u06b5\u0627\u06cc \u0632\u06c6\u0631 \u067e\u062a\u06d5\u0648\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061b \u0644\u06d5\u0648 \u062f\u06cc\u0648 \u062f\u06cc\u0648\u0627\u0631\u0627\u0646\u06d5\u0648\u06d5. \u0646\u0627\u06a9\u06c6\u06a9\u06cc \u062a\u0648\u0646\u062f \u06a9\u06d5\u0648\u062a\u06c6\u062a\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc\u0627\u0646\u06d5\u0648\u06d5\u061b \u062a\u06c6 \u067e\u06ce\u062a \u0648\u0627\u06cc\u06d5 \u0628\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5 \u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u06af\u0631\u062a\u0648\u0648\u06d5\u060c \u0628\u06d5\u06b5\u0627\u0645 \u062f\u06b5\u06cc\u0627\u0646 \u0644\u06ce\u06a9 \u0628\u06b5\u0627\u0648\u06d5\u061b \u0686\u0648\u0646 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0645\u06d5\u0631\u062f\u0645\u0627\u0646\u06ce\u06a9\u06cc \u0646\u06d5\u0641\u0627\u0645\u0646.',
    '\u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u060c \u06a9\u06d5 \u067e\u06ce\u0634 \u0626\u06d5\u0648\u0627\u0646 - \u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 - \u0626\u0627\u06a9\u0627\u0645\u06cc \u06a9\u0631\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0686\u06d5\u0634\u062a. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06ce.',
    '\u0648\u06d5\u06a9\u0648\u0648 \u0634\u06d5\u06cc\u062a\u0627\u0646\u060c \u06a9\u0627\u062a\u06ce \u0628\u06d5 \u0628\u0646\u06cc\u0627\u062f\u06d5\u0645\u06cc \u06af\u0648\u062a: \u062e\u0648\u0627 \u0645\u06d5\u0646\u0627\u0633\u06d5\u061b \u0648\u06d5\u062e\u062a\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u06d5\u06a9\u06cc\u06d5\u0648\u06d5\u060c \u06af\u0648\u062a\u06cc: \u0644\u06ce\u0645 \u0646\u0632\u06cc\u06a9 \u0645\u06d5\u0628\u06d5\u0648\u06d5\u060c \u0645\u0646 \u0644\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u062f\u06d5\u062a\u0631\u0633\u0645 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u06af\u0634 \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u0633\u06d5\u0631\u0626\u06d5\u0646\u062c\u0627\u0645\u06cc \u0647\u06d5\u0631\u062f\u0648\u0648\u06a9\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u06a9\u06d5 \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5\u060c \u0644\u06d5 \u0626\u0627\u06af\u0631\u062f\u0627 \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5. \u0633\u0632\u0627\u06cc \u0646\u0627\u0647\u06d5\u0642\u0627\u0646 \u0626\u06d5\u0645\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u0634\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646\u061b \u0628\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u06cc \u062e\u06c6\u06cc\u060c \u0628\u06c6 \u0633\u0628\u06d5\u06cc\u0646\u06ce \u0686\u06d5\u06cc \u0648\u06d5 \u067e\u06ce\u0634\u06d5 \u062e\u06c6\u06cc \u062e\u0633\u062a\u0648\u0648\u06d5 \u0648 \u062a\u0631\u0633\u062a\u0627\u0646 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0628\u06ce. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0646\u06d5\u0686\u0646 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0646 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0644\u06d5\u0628\u06cc\u0631 \u06a9\u0631\u062f \u0648 \u062e\u0648\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0644\u06d5\u0628\u06cc\u0631\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0631\u062f\u06d5\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0646\u060c \u0648\u06d5\u0644 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u0646\u060c \u0648\u06d5\u06a9\u0648\u0648 \u06cc\u06d5\u06a9 \u0646\u06cc\u0646. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u0646\u060c \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u0646 \u0646\u0627\u0631\u062f\u0628\u0627\u06cc\u06d5\u062a\u06d5 \u0633\u06d5\u0631 \u06a9\u06d5\u0698\u06ce\u06a9\u060c \u062a\u06c6 \u062f\u06d5\u062a\u062f\u06cc \u06a9\u06d5 \u062f\u0627\u0647\u0627\u062a\u0648\u0648\u06d5 \u0648 \u0644\u06d5 \u062a\u0631\u0633\u06cc \u062e\u0648\u0627 \u0644\u06d5\u062a \u0644\u06d5\u062a \u0628\u0648\u0648\u06d5. \u0626\u06d5\u0645 \u0646\u0645\u0648\u0648\u0646\u0627\u0646\u06d5 \u0628\u06c6 \u0645\u06d5\u0631\u062f\u0645 \u062f\u06ce\u0646\u06cc\u0646\u06d5\u0648\u06d5\u060c \u0628\u06d5\u0634\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646 \u0695\u0627\u0628\u0645\u06ce\u0646\u0646.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u06cc \u0626\u06d5\u0648\u062a\u06c6\u06cc\u06d5 \u06a9\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u06a9\u06d5\u0633 \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646\u06ce \u0646\u0627\u0628\u06ce\u061b \u0644\u06d5 \u0646\u06d5\u062f\u06cc\u0627\u0631 \u0648 \u0644\u06d5 \u062f\u06cc\u0627\u0631\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u062f\u06b5\u0627\u0648\u0627\u06cc\u06d5 \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 - \u06a9\u06d5 \u0644\u06d5\u0648 \u0628\u06d5\u062f\u06d5\u0631 \u0634\u06cc\u0627\u0648\u06cc \u067e\u06d5\u0631\u0633\u062a\u0646\u06ce\u06a9 \u0646\u06cc\u06cc\u06d5 - \u067e\u0627\u062f\u0634\u0627\u06cc\u06d5\u061b \u0644\u06d5 \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc \u0628\u06d5 \u062f\u0648\u0648\u0631\u06d5\u061b \u0633\u06d5\u0631\u0686\u0627\u0648\u06d5\u06cc \u0647\u06ce\u0645\u0646\u0627\u06cc\u06d5\u062a\u06cc\u060c \u0647\u06ce\u0645\u0646\u06cc \u062f\u06d5\u0631\u06d5\u061b \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0695\u0627\u062f\u06d5\u06af\u0627\u061b \u06af\u0634 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0628\u06d5\u062f\u06d5\u0633 \u0626\u06d5\u0648\u06d5\u061b \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u062f\u0627 \u0632\u0627\u06b5\u06d5\u061b \u0647\u06cc\u0686 \u0634\u062a\u06ce \u062f\u06d5\u0631\u06c6\u0633\u062a\u06cc \u0646\u0627\u06cc\u06d5. \u067e\u0627\u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u062e\u0648\u062f\u0627\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u06cc \u062f\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc \u0648\u06ce\u0646\u06d5\u06af\u06d5\u0631\u061b \u0646\u0627\u0648\u06cc \u0647\u06d5\u0631\u06d5 \u062c\u0648\u0627\u0646 \u0628\u06c6 \u0626\u06d5\u0648\u06d5\u061b \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0644\u06d5 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u0626\u06d5\u0648 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0645\u06d5\u0628\u0646\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u06cc \u0645\u0646 \u0648 \u062e\u06c6\u062a\u0627\u0646. \u0626\u06ce\u0648\u06d5 \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u0648 \u0628\u06d5\u0648\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u0646\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5\u0648 \u062f\u06cc\u0646\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0642\u06cc\u0646\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5 [\u0648] \u0644\u06d5\u0628\u06d5\u0631 \u0626\u06d5\u0648\u06d5 \u0628\u06d5 \u062e\u0648\u062f\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u0626\u06ce\u0648\u06d5 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0644\u06d5 \u0632\u06ce\u062f\u06cc \u062e\u06c6 \u062f\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0646. \u0626\u06d5\u0631 \u0628\u06c6 \u062e\u06d5\u0632\u0627 \u0644\u06d5 \u0695\u0627\u06cc \u0645\u0646 \u0648 \u0695\u06d5\u0632\u0627\u0645\u06d5\u0646\u062f\u06cc\u0645 \u062f\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0648\u0646\u060c [\u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0646]. \u067e\u06d5\u0646\u0627\u0645\u06d5\u06a9\u06cc \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u062e\u06c6\u0634\u0645\u0627\u0646 \u062f\u06d5\u0648\u06ce\u0646\u061b \u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0645\u0646 \u0644\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u062f\u06d5\u06cc\u0634\u06ce\u0631\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u0626\u0627\u0634\u06a9\u0631\u0627\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0631\u0645. \u06a9\u06ce \u0644\u06d5\u0646\u06af\u06c6 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5 \u0628\u06a9\u0627\u060c \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc \u0644\u06ce \u062a\u06ce\u06a9\u0686\u0648\u0648\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5 \u06af\u06cc\u0631\u06cc\u0627\u0646 \u0628\u06a9\u06d5\u0648\u0646\u060c \u062f\u0648\u0698\u0645\u0646\u0627\u06cc\u06d5\u062a\u06cc\u062a\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u061b \u0628\u06c6 \u0626\u0627\u0632\u0627\u0631\u062a\u0627\u0646 \u062f\u06d5\u0633\u062a \u0648 \u0632\u0645\u0627\u0646 \u0648\u06d5 \u06a9\u0627\u0631 \u062f\u06d5\u062e\u06d5\u0646\u061b \u062d\u06d5\u0632 \u062f\u06d5\u06a9\u06d5\u0646 \u0644\u06d5 \u062f\u06cc\u0646 \u0648\u06d5\u0631\u06af\u06d5\u0695\u06ce\u0646.',
    '\u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u060c \u0646\u06d5 \u062e\u0632\u0645\u0627\u0646\u0648\u0648 \u0646\u06d5 \u062c\u06af\u06d5\u0631\u06af\u06c6\u0634\u06d5\u06a9\u0627\u0646\u06cc\u0634\u062a\u0627\u0646 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062f\u06d5 \u0641\u0631\u06cc\u0627\u062a\u0627\u0646 \u0646\u0627\u06cc\u06d5\u0646\u061b \u0644\u06ce\u06a9\u062a\u0627\u0646 \u062c\u0648\u062f\u0627 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0686\u06cc \u0628\u06cc\u06a9\u06d5\u0646 \u062e\u0648\u062f\u0627 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648 \u0628\u0648\u0648\u0646\u060c \u0628\u06c6\u0646\u06af\u06c6 \u0646\u0645\u0648\u0648\u0646\u06d5\u06cc \u0632\u06c6\u0631 \u0628\u0627\u0634\u0646\u061b \u06a9\u06d5 \u0628\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648\u062a: \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0646\u06af\u06c6 \u0648 \u0644\u06d5\u0648 \u0634\u062a\u0627\u0646\u06d5\u06cc - \u0644\u06d5 \u062e\u0648\u0627 \u0628\u06d5\u062f\u06d5\u0631 - \u062f\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0628\u06d5 \u062f\u0648\u0648\u0631\u06cc\u0646 \u0648 \u0628\u0627\u0648\u06d5\u0695\u0645\u0627\u0646 \u0628\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u06d5\u0645\u0627 \u0648\u060c \u062a\u0627 \u0647\u06d5\u062a\u0627\u06cc\u06d5 \u062f\u0648\u0698\u0645\u0646\u0627\u06cc\u06d5\u062a\u06cc \u0648 \u0695\u06a9\u0648\u0648\u0646\u06cc \u06a9\u06d5\u0648\u062a\u06c6\u062a\u06d5 \u0646\u06ce\u0648\u0627\u0646\u0645\u0627\u0646\u06d5\u0648\u06d5\u061b \u062a\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0628\u06d5 \u062e\u0648\u062f\u0627\u06cc \u062a\u0627\u0642\u0627\u0646\u06d5 \u062f\u06ce\u0646\u0646. \u062c\u06cc\u0627 \u0644\u06d5\u0648\u06d5 \u06a9\u06d5 \u0626\u06cc\u0628\u0631\u0627\u0647\u06cc\u0645 \u0628\u06d5 \u0626\u0627\u067e\u06ce \u062e\u06c6 \u06af\u06c6\u062a: \u062f\u0627\u0648\u0627\u06cc \u062e\u06c6\u0634\u0628\u0648\u0648\u0646\u062a \u0628\u06c6 \u062f\u06d5\u06a9\u06d5\u0645\u061b \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062a\u06c6 \u0647\u06cc\u0686\u06cc \u062a\u0631\u0645 \u0628\u06c6 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627 \u0646\u0627\u06a9\u0631\u06ce. \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af \u0644\u0627\u0645\u0627\u0646! \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u062a\u06c6 \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646 \u0695\u0648\u0648\u0645\u0627\u0646 \u062f\u06d5 \u062a\u06c6\u06cc\u06d5 \u0648 \u0648\u06d5\u06af\u06d5\u0695\u0627\u0646\u06cc \u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06d5\u0631\u06d5\u0648 \u062a\u06c6\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648\u0645\u0627\u0646! \u0645\u06d5\u0645\u0627\u0646\u062e\u06d5 \u0628\u06d5\u0631 \u062a\u06cc\u0631 \u0648 \u062a\u0627\u0646\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062a\u06c6 \u0646\u0627\u0646\u0627\u0633\u0646\u061b \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06cc\u0634\u0645\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u06d5\u061b \u0647\u06d5\u0631 \u062e\u06c6\u062a \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06cc.',
    '\u0628\u06ce\u06af\u0648\u0645\u0627\u0646\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0645\u0648\u0648\u0646\u06d5\u06cc \u0628\u0627\u0634\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0648 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u06a9\u06d5 \u0647\u0648\u0645\u06ce\u062f\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0647\u06d5\u06cc\u06d5. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062e\u06c6 \u062f\u06d5\u0628\u0648\u06ce\u0631\u06ce \u060c \u062e\u0648\u062f\u0627 \u067e\u06d5\u06a9\u06cc \u067e\u06ce \u0646\u0627\u06a9\u06d5\u0648\u06ce \u0648 \u0647\u06d5\u0631 \u0634\u0627\u06cc\u0627\u0646\u06cc \u067e\u06d5\u0633\u0646 \u062f\u0627\u0646\u06d5.',
    '\u0634\u0627\u06cc\u06d5\u062f \u062e\u0648\u062f\u0627 \u0644\u06d5\u0646\u0627\u0648 \u0626\u06ce\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u0648\u0698\u0645\u0646\u0627\u06cc\u06d5\u062a\u06cc\u0648 \u062f\u06d5\u06a9\u0631\u062f\u0646\u060c \u062f\u06c6\u0633\u062a\u0627\u06cc\u06d5\u062a\u06cc \u062f\u0627\u0645\u06d5\u0632\u0631\u06ce\u0646\u06ce. \u062e\u0648\u0627 \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5 \u0648 \u062e\u0648\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0628\u06d5\u0631\u062a\u0627\u0646 \u0644\u06ce \u0646\u0627\u06af\u0631\u06ce \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u06af\u0698 \u0626\u06ce\u0648\u06d5\u062f\u0627 \u0646\u06d5\u0686\u0648\u0648\u0646 \u0648 \u0644\u06d5 \u0632\u06ce\u062f \u0648\u06d5\u062f\u06d5\u0631\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0648\u0646\u060c \u0686\u0627\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5 \u0628\u06a9\u06d5\u0646 \u0648 \u0628\u06d5 \u06cc\u06d5\u06a9\u0633\u0627\u0646\u06cc \u0695\u06d5\u0641\u062a\u0627\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5\u062f\u0627 \u0628\u06a9\u06d5\u0646. \u062e\u0648\u0627 \u0645\u0631\u06c6\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0626\u06cc\u0646\u0633\u0627\u0641\u06cc \u062e\u06c6\u0634 \u06af\u06d5\u0631\u06d5\u06a9\u06d5.',
    '\u062a\u06d5\u0646\u06cc\u0627 \u0647\u06d5\u0631 \u0695\u06ce\u06af\u06d5\u062a\u0627\u0646 \u0646\u0627\u062f\u0627 \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u0628\u0646\u06d5 \u062f\u06c6\u0633\u062a \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u06cc\u0646\u060c \u0634\u06d5\u0695\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5\u062a\u0627\u0646\u0627 \u06a9\u0631\u062f \u0648 \u0626\u06ce\u0648\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0632\u06ce\u062f\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0626\u0627\u0648\u0627\u0631\u06d5 \u06a9\u0631\u062f. \u0644\u06d5\u0633\u06d5\u0631 \u062f\u06d5\u0631\u06a9\u0631\u062f\u0646\u06cc \u0626\u06ce\u0648\u06d5\u0634\u060c \u0628\u0648\u0648\u0646\u06d5 \u067e\u0634\u062a\u06cc\u0648\u0627\u0646\u06cc \u06cc\u06d5\u06a9\u062a\u0631. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u0628\u0628\u06ce\u062a\u06d5 \u062f\u06c6\u0633\u062a\u06cc\u0627\u0646\u060c \u062f\u06cc\u0627\u0631\u06d5 \u063a\u06d5\u062f\u0631\u06ce \u0644\u06d5 \u062e\u06c6 \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u0698\u0646\u06cc \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u06cc \u0626\u0627\u0648\u0627\u0631\u06d5 \u062f\u06d5\u06af\u0627\u062a\u06d5 \u0644\u0627\u062a\u0627\u0646\u060c \u0628\u06cc\u0627\u0646\u062e\u06d5\u0646\u06d5 \u0628\u06d5\u0631 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06d5\u0648\u06d5. \u062e\u0648\u0627 \u0644\u06d5 \u0628\u06cc\u0631 \u0648 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0631\u06d5. \u06af\u06d5\u0631 \u0646\u0627\u0633\u06cc\u062a\u0627\u0646\u0646 \u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0628\u0695\u0648\u0627\u062f\u0627\u0631\u0627\u0646\u060c \u0628\u06c6 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc\u0627\u0646 \u0645\u06d5\u0646\u06ce\u0631\u0646\u06d5\u0648\u06d5. \u0646\u06d5 \u0626\u06d5\u0645\u0627\u0646 \u0628\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0634\u06ce\u0646 \u0648 \u0646\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0628\u06d5\u0645\u0627\u0646 \u062f\u06d5\u0634\u06ce\u0646. \u0647\u06d5\u0631\u0686\u06cc\u0634 \u0628\u06d5\u062e\u062a\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0628\u06cc\u0627\u0646\u062f\u06d5\u0646\u06d5\u0648\u06d5. \u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0645\u0627\u0631\u06d5\u06cc\u0627\u0646 \u06a9\u06d5\u0646 \u0648 \u0645\u0627\u0631\u06d5\u06cc\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u062f\u06d5\u0646\u06ce\u060c \u0647\u06cc\u0686 \u06af\u0648\u0646\u0627\u0647\u06ce\u06a9\u062a\u0627\u0646 \u0646\u0627\u06af\u0627\u062a\u06ce. \u0698\u0646\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc\u0634 \u0644\u0627\u06cc \u062e\u06c6\u062a\u0627\u0646 \u06af\u0644 \u0645\u06d5\u062f\u06d5\u0646\u06d5\u0648\u06d5\u061b \u0647\u06d5\u0631\u0686\u06cc\u062a\u0627\u0646 \u0644\u06ce \u0628\u06d5\u062e\u062a \u06a9\u0631\u062f\u0648\u0648\u0646 \u0628\u06cc\u0633\u06ce\u0646\u0646\u06d5\u0648\u06d5\u061b \u0628\u0627 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631\u0686\u06ce\u06a9\u06cc \u0628\u06d5\u062e\u062a\u06cc\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u062f\u0627\u0648\u0627\u06cc \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u0645\u06d5 \u062f\u06d5\u0633\u062a\u0648\u0648\u0631\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5 \u0646\u06ce\u0648\u0627\u0646\u062a\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u0627. \u062e\u0648\u062f\u0627 \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0698\u0646\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0695\u06d5\u0648\u06cc \u0628\u06c6 \u0644\u0627\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0648 \u0626\u06ce\u0648\u06d5 \u062a\u0627\u06b5\u0627\u0646\u062a\u0627\u0646 \u0644\u06ce \u06af\u0631\u062a\u0646\u060c \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u0648\u0627 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u0627\u062a\u0648\u0648\u0646\u060c \u0647\u06ce\u0646\u062f\u06d5\u0628\u062f\u06d5\u0646 \u06a9\u06d5 \u062f\u0627\u0648\u06cc\u0627\u0646\u06d5. \u062a\u0631\u0633\u06cc\u0634\u0648\u0648 \u0644\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0628\u06ce \u06a9\u06d5 \u0628\u0695\u0648\u0627\u062a\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0626\u06d5\u06af\u06d5\u0631 \u0698\u0646\u0627\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u062f\u06ce\u0646\u06d5 \u0644\u0627\u062a \u0648 \u067e\u06d5\u06cc\u0645\u0627\u0646 \u062f\u06d5\u062f\u06d5\u0646 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc - \u0644\u06d5 \u062e\u0648\u0627 \u0628\u06d5\u062f\u06d5\u0631 - \u0646\u06d5\u067e\u06d5\u0631\u0633\u062a\u0646 \u0648 \u062a\u0648\u0648\u0634\u06cc \u062f\u0632\u06cc \u0648 \u062d\u06cc\u0632\u06cc \u0646\u06d5\u0628\u0646 \u0648 \u0645\u0646\u0627\u06b5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0646\u06d5\u06a9\u0648\u0698\u0646 \u0648 \u0645\u0646\u0627\u06b5\u06cc \u062a\u0631 \u0628\u06d5 \u062f\u0631\u06c6 \u0646\u06d5\u06a9\u06d5\u0646\u06d5 \u0645\u0646\u062f\u0627\u06b5 \u0645\u06ce\u0631\u062f\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0648 \u0628\u06c6 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062a\u06c6 \u062f\u06d5\u0631\u0646\u06d5\u0686\u0646\u060c \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u062a\u06c6\u0634 \u067e\u06d5\u06cc\u0645\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u0628\u06d5\u0633\u062a\u06d5 \u0648 \u0644\u06ce\u062e\u06c6\u0634\u0628\u0648\u0648\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062f\u0627\u0648\u0627 \u0628\u06a9\u06d5. \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0645\u06d5\u0628\u0646\u06d5 \u062f\u06c6\u0633\u062a\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u0627 \u063a\u06d5\u0632\u06d5\u0628\u06cc \u0644\u06ce \u06af\u0631\u062a\u0648\u0648\u0646\u060c \u0644\u06d5 \u0647\u0627\u062a\u0646\u06cc \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0646\u0627\u0647\u0648\u0645\u06ce\u062f \u0628\u0648\u0648\u0646\u061b \u0648\u06d5\u06a9\u0648\u0648 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u0645\u0631\u06cc\u06cc\u06d5\u06a9\u0627\u0646\u06cc \u06af\u06c6\u0695\u0633\u062a\u0627\u0646 \u0646\u0627\u0647\u0648\u0645\u06ce\u062f\u0646.',
    '\u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u062f\u06cc \u062e\u0648\u0627 \u062f\u06d5\u062f\u06d5\u0646 \u0648 \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06c6 \u062f\u0627\u062e\u06ce\u0648\u0646 \u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u0628\u06d5\u062f\u0648\u0648\u062f\u0627 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u062e\u0648\u062f\u0627 \u0632\u06c6\u0631 \u0644\u06d5\u0648\u06d5 \u062a\u0648\u0648\u0695\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0634\u062a\u06ce\u06a9 \u0628\u06b5\u06ce\u0646 \u0648 \u0648\u0644\u06c6 \u0646\u06d5\u06a9\u06d5\u0646.',
    '\u062e\u0648\u0627 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce \u06a9\u06d5 \u0628\u06c6 \u062e\u06d5\u0632\u0627 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627\u060c \u0695\u06cc\u0632 \u062f\u06d5\u0628\u06d5\u0633\u062a\u0646\u061b \u0626\u06ce\u0698\u06cc \u062f\u06cc\u0648\u0627\u0631\u06cc \u0642\u0648\u0695\u0642\u0648\u0634\u0645\u06d5.',
    '\u06a9\u06d5 \u0645\u0648\u0648\u0633\u0627 \u0628\u06d5 \u0647\u06c6\u0632\u06cc \u062e\u06c6\u06cc \u06af\u0648\u062a: \u06af\u06d5\u0644\u06c6! \u0628\u06c6\u0686\u06cc\u0645 \u0626\u0627\u0632\u0627\u0631 \u062f\u06d5\u062f\u06d5\u0646\u060c \u0633\u0648\u0648\u0631 \u062f\u06d5\u0634\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0645\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u0645 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06ce\u0648\u06d5\u061f \u0648\u06d5\u062e\u062a\u06ce \u0626\u06d5\u0648\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u0627\u062f\u0627\u060c \u062e\u0648\u062f\u0627\u0634 \u062f\u06b5\u06cc \u0648\u0627\u0646\u06cc \u0644\u0627\u062f\u0627. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u06ce \u062f\u06d5\u0631\u062f\u06d5\u0686\u0646\u060c \u062e\u0648\u062f\u0627 \u0695\u06ce\u06af\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0646\u0627\u062f\u0627.',
    '\u06a9\u06d5 \u0639\u06cc\u0633\u0627\u06cc \u06a9\u0648\u0695\u06cc \u0645\u0631\u06cc\u06d5\u0645\u06cc\u0634 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644! \u0626\u06d5\u0645\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u0645 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06ce\u0648\u06d5\u061b \u0626\u06d5\u0648 \u062a\u06d5\u0648\u0695\u0627\u062a\u06d5 \u062f\u06d5\u0633\u06d5\u0644\u0645\u06ce\u0646\u0645 \u06a9\u06d5 \u0644\u06d5\u067e\u06ce\u0634 \u0645\u0646\u062f\u0627 \u0647\u0627\u062a\u0648\u0648\u06d5\u061b \u0645\u0698\u062f\u06d5\u0648 \u0644\u06ce \u0628\u06ce \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u0644\u06d5\u067e\u0627\u0634 \u0645\u0646 \u062f\u06ce \u0648\u06d5 \u0646\u0627\u0648 \u0626\u06d5\u062d\u0645\u06d5\u062f. \u0648\u06d5\u062e\u062a\u06ce \u0628\u06d5 \u0686\u06d5\u0646\u062f \u0646\u06cc\u0634\u0627\u0646\u06d5\u06cc \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u0631\u06d5\u0648\u06d5 \u0647\u0627\u062a\u06d5 \u0644\u0627\u06cc\u0627\u0646\u060c \u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0628\u06d5\u0626\u0627\u0634\u06a9\u0631\u0627 \u062c\u0627\u062f\u0648\u0648\u06d5.',
    '\u0633\u0627 \u06a9\u06ce \u0644\u06d5\u0648\u06d5 \u0646\u0627\u0647\u06d5\u0642\u062a\u0631\u06d5 \u062f\u0631\u06c6 \u0628\u06c6 \u062e\u0648\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u06d5\u0633\u062a\u06ce\u060c \u0644\u06d5 \u0648\u06d5\u062e\u062a\u06ce\u06a9\u0627 \u06a9\u06d5 \u0628\u06c6\u0626\u06cc\u0633\u0644\u0627\u0645 \u06af\u0627\u0632\u06cc \u062f\u06d5\u06a9\u0631\u06ce\u061f \u062e\u0648\u062f\u0627 \u06af\u0631\u06c6\u06cc \u063a\u06d5\u062f\u0631\u06a9\u0627\u0631\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627 \u0646\u0627\u06a9\u0627.',
    '\u06af\u06d5\u0631\u06d5\u06a9\u06cc\u0627\u0646\u06d5 \u062a\u06cc\u0634\u06a9\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631 \u0628\u06d5 \u0641\u0648\u0648\u06cc \u062f\u06d5\u0645 \u0648\u06d5\u06a9\u0648\u0698\u06ce\u0646\u0646. \u062e\u0648\u0627\u0634 \u062a\u06cc\u0634\u06a9\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u0631 \u0633\u06d5\u0631\u062f\u06d5\u062e\u0627\u061b \u062c\u0627 \u0628\u0627 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc\u0634 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0646\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u06c6\u06cc \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f\u060c \u0628\u06c6 \u0695\u06ce\u0628\u06d5\u0631\u06cc \u0648 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u06cc \u062f\u06cc\u0646\u06cc \u0695\u0627\u0633\u062a\u060c \u062a\u0627 \u0633\u06d5\u0631\u06cc \u062e\u0627 \u0628\u06d5 \u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06cc\u0646\u06d5\u06a9\u0627\u0646\u062f\u0627\u061b \u062c\u0627 \u0628\u0627 \u0628\u062a \u067e\u06d5\u0631\u0633\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0646\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06d5\u06ce \u0645\u0627\u0645\u06d5\u06b5\u06d5\u06cc\u06d5\u06a9\u062a\u0627\u0646 \u067e\u06ce \u0628\u06ce\u0698\u0645 \u06a9\u06d5 \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646 \u0695\u0632\u06af\u0627\u0631\u0648\u0648 \u06a9\u0627\u061f',
    '\u0628\u0695\u0648\u0627\u0648 \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0626\u06d5\u0648 \u0628\u06ce\u0646\u0646 \u0648 \u0628\u06d5 \u062f\u0627\u0631\u0627\u06cc\u06cc \u0648 \u06af\u06cc\u0627\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u062a\u06ce \u0628\u06a9\u06c6\u0634\u0646\u061b \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0628\u0627\u0634\u062a\u0631\u06d5 \u06af\u06d5\u0631 \u0628\u06cc\u0632\u0627\u0646\u0646.',
    '\u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u0648\u0648 \u062f\u06d5\u0628\u0648\u0631\u06ce \u0648 \u062f\u06d5\u062a\u0627\u0646\u0628\u0627\u062a\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u062e\u0627\u0646\u0648\u0648\u06cc \u0632\u06c6\u0631\u062e\u06c6\u0634 \u0648\u062e\u0627\u0648\u06ce\u0646\u062a\u0627\u0646 \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc \u0647\u06d5\u0645\u06cc\u0634\u06d5\u06cc\u06cc\u062f\u0627 \u062f\u06d5\u062f\u0627\u062a\u06ce\u061b \u0626\u06d5\u0645\u06d5 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06d5.',
    '\u0634\u062a\u06ce \u062a\u0631\u06cc\u0634 - \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062d\u06d5\u0632\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646 - \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062f\u0627\u0646\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0648 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u06ce\u06a9\u06cc \u0646\u0632\u06cc\u06a9\u06cc\u0634. \u0626\u06d5\u0645 \u0645\u0698\u062f\u06d5\u06cc\u06d5 \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0628\u062f\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u06cc\u0627\u0631\u06cc\u06a9\u0627\u0631\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u0646\u060c \u0648\u06d5\u06a9\u0648\u0648 \u0639\u06cc\u0633\u0627\u06cc \u06a9\u0648\u0695\u06cc \u0645\u06d5\u0631\u06cc\u06d5\u0645 \u0628\u06d5 \u06cc\u0627\u0631\u0627\u0646\u06cc \u062d\u06d5\u0648\u0627\u0631\u06cc\u06cc \u06af\u0648\u062a: \u06a9\u06ce \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06ce\u062a\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u06cc \u0645\u0646\u061f \u062f\u06d5\u0633\u062a\u06d5\u06cc \u062d\u06d5\u0648\u0627\u0631\u06cc\u06cc\u06d5\u06a9\u0627\u0646 \u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u06cc\u0646. \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0633\u0631\u0627\u0626\u06cc\u0644 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0627\u0646 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695 \u0645\u0627\u0646. \u0626\u06cc\u062a\u0631 \u0626\u06ce\u0645\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u0646 \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u06a9\u0631\u062f\u060c \u062a\u0627 \u062f\u06d5\u0631\u06c6\u0633\u062a\u06cc \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0647\u0627\u062a\u0646.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0646\u0627\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u060c \u067e\u06d5\u0633\u0646\u06cc \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u062f\u06d5\u062f\u06d5\u0646 \u06a9\u06d5 \u067e\u0627\u062f\u0634\u0627\u06cc\u06d5\u06a9\u06cc \u067e\u0627\u06a9 \u0648 \u062f\u0648\u0648\u0631 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc \u0648 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0644\u06d5\u0646\u0627\u0648 \u0646\u06d5\u062e\u0648\u06ce\u0646\u062f\u06d5\u0648\u0627\u0631\u0627\u0646\u062f\u0627 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06cc \u0644\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06c6 \u06a9\u0644 \u06a9\u0631\u062f\u0646\u060c \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06d5\u0648\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u06ce \u0648 \u0644\u06d5 \u06a9\u062a\u06ce\u0628 \u0648 \u0644\u06d5 \u06a9\u0627\u0631\u0632\u0627\u0646\u06cc \u0695\u0627\u06cc\u0627\u0646\u0628\u06ce\u0646\u06ce\u061b \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648\u062f\u0627 \u0644\u06d5\u0648 \u067e\u06d5\u0695\u06cc \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc\u062f\u0627 \u0628\u0648\u0648\u0646\u061b',
    '\u0628\u06c6 \u0647\u06ce\u0646\u062f\u06ce \u062a\u0631\u06cc\u0634 \u0644\u06d5\u0645\u0627\u0646\u06d5\u060c \u0648\u0627 \u0647\u06ce\u0634\u062a\u0627 \u067e\u06ce\u06cc\u0627\u0646 \u0646\u06d5\u06af\u06d5\u06cc\u0634\u062a\u0648\u0648\u0646. \u062e\u0648\u062f\u0627 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0645\u06d5 \u0686\u0627\u06a9\u06d5\u06cc\u06d5 \u0644\u06d5 \u062e\u0648\u0627\u0648\u06d5\u060c \u062d\u06d5\u0632\u06cc \u0644\u06ce \u0628\u06ce \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc \u062f\u06d5\u06a9\u0627. \u0686\u0627\u06a9\u06d5\u06cc\u06ce \u06a9\u06d5 \u0644\u06d5\u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u060c \u0632\u06c6\u0631 \u0647\u06ce\u0698\u0627\u06cc\u06d5.',
    '\u0633\u06d5\u0631\u06af\u0648\u0631\u0648\u0634\u062a\u06d5\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u06d5\u0648\u0631\u0627\u062a\u06cc\u0627\u0646 \u067e\u06ce \u0647\u06d5\u06b5\u06af\u0631\u062a\u0648\u0648\u0646 \u0648 \u0644\u06d5\u0628\u06d5\u0631 \u062f\u06d5\u0631\u0686\u0648\u0648\u0646\u060c \u0648\u06d5\u06a9\u0648\u0648 \u0646\u06d5\u0642\u06b5\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0631\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0686\u06d5\u0646\u062f \u06a9\u062a\u06ce\u0628\u06cc \u0644\u06ce \u0628\u0627\u0631\u06d5. \u0633\u06d5\u0631\u06af\u0648\u0632\u06d5\u0634\u062a\u06ce \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f. \u062e\u0648\u062f\u0627\u0634 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u0627\u0647\u06d5\u0642\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627 \u0646\u0627\u06a9\u0627.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062c\u0648\u0648\u0646! \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0648\u0627\u06cc\u06d5 - \u0632\u06cc\u0627\u062f \u0644\u06d5 \u0645\u0631\u06c6 - \u0647\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u062f\u06c6\u0633\u062a\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u0646\u060c \u062f\u06d5\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u06af\u0641\u062a\u0648\u0648 \u0695\u0627\u0633\u062a\u06d5\u060c \u0645\u0631\u062f\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u062e\u0648\u0627\u0632\u0646.',
    '\u0647\u06ce\u0646\u062f\u06cc\u0627\u0646 \u062e\u0631\u0627\u067e\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06d5\u060c \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0645\u0631\u062f\u0646\u06ce \u0646\u0627\u062e\u0648\u0627\u0632\u0646\u060c \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u06a9\u0627\u0631\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648 \u0645\u06d5\u0631\u06af\u06d5\u06cc \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0644\u06ce\u06cc \u0695\u0627\u062f\u06d5\u06a9\u06d5\u0646\u060c \u0647\u06d5\u0631 \u062f\u06d5\u0648\u06af\u0627\u062a\u06ce \u0648 \u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0631 \u062f\u06d5\u062a\u0627\u0646\u0628\u06d5\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u0632\u0627\u0646\u0627\u06cc \u0646\u06d5\u062f\u06cc\u0627\u0631 \u0648 \u062f\u06cc\u0627\u0631\u0627\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u067e\u06ce\u062a\u0627\u0646 \u0626\u06ce\u0698\u06ce \u0686\u06cc\u0648 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a\u06ce \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u06cc\u0646\u06cc \u0628\u06c6 \u0646\u0648\u06ce\u0698\u06cc \u0647\u06d5\u06cc\u0646\u06cc \u0628\u0627\u0646\u06af \u0626\u06d5\u062f\u0631\u06ce\u060c \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0644\u06d5\u0632 \u0628\u06d5\u0631\u06d5\u0648 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u0628\u0686\u0646 \u0648 \u06a9\u0695\u06cc\u0646 \u0648 \u0641\u0631\u06c6\u062a\u0646 \u0648\u06ce\u06b5 \u06a9\u06d5\u0646\u061b \u06af\u06d5\u0631 \u0628\u06cc\u0632\u0627\u0646\u0646\u060c \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062e\u0627\u0633\u062a\u0631\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5\u0646\u0648\u06ce\u0698\u06cc \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u060c \u0628\u06d5 \u0647\u06d5\u0631\u062f\u06ce\u062f\u0627 \u0628\u06b5\u0627\u0648 \u0628\u0628\u0646 \u0648 \u0628\u06c6 \u0686\u0627\u06a9\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06c6\u0634\u0646 \u0648 \u06af\u06d5\u0644\u06ce\u06a9 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u0628\u06a9\u06d5\u0646\u061b \u0634\u0627\u06cc\u06d5\u062a\u0627 \u0695\u0632\u06af\u0627\u0631\u06cc\u062a\u0627\u0646 \u0628\u06ce.',
    '\u0647\u06d5\u0631\u06af\u0627 \u062f\u06cc\u062a\u06cc\u0627\u0646 \u0645\u0627\u0645\u06b5\u06d5\u06cc\u06d5 \u06cc\u0627\u0646 \u06af\u06d5\u0645\u06d5\u06cc\u06d5\u060c \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648\u06ce \u0628\u06b5\u0627\u0648 \u062f\u06d5\u0628\u0646 \u0648 \u062a\u06c6 \u0628\u06d5\u067e\u06ce\u0648\u06d5 \u0628\u06d5\u062c\u06ce \u062f\u06ce\u06b5\u0646. \u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648\u06cc \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u060c \u0644\u06d5 \u06af\u06d5\u0645\u06d5\u0634 \u0648 \u0644\u06d5\u0628\u0627\u0632\u0631\u06af\u0627\u0646\u06cc\u0634 \u0686\u0627\u062a\u0631\u06d5 \u0648 \u062e\u0648\u0627 \u0686\u0627\u06a9\u062a\u0631\u06cc\u0646 \u0695\u06c6\u0632\u06cc\u062f\u06d5\u0631\u06d5.',
    '\u0648\u06d5\u062e\u062a\u06ce \u062f\u0648\u0648\u0695\u0648\u0648 \u062f\u06ce\u0646\u06d5 \u0644\u0627\u06cc \u062a\u06c6\u060c \u0626\u06ce\u0698\u0646: \u0634\u0627\u06cc\u06d5\u062a\u06cc\u062a \u0628\u06c6 \u062f\u06d5\u062f\u06d5\u06cc\u0646 \u06a9\u06d5 \u062a\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u06cc\u061b \u062e\u0648\u062f\u0627\u0634 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06d5 \u062a\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0626\u06d5\u0648\u06cc \u0648 \u062e\u0648\u0627 \u06af\u06d5\u0648\u0627\u0647\u06d5 \u06a9\u06d5 - \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc - \u0647\u06d5\u0631\u0686\u06cc \u062f\u0648\u0648\u0695\u0648\u0648\u0646 \u062f\u0631\u06c6\u0632\u0646\u0646.',
    '\u0633\u0648\u06ce\u0646\u062f\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06c6\u062a\u06d5 \u0642\u06d5\u06b5\u063a\u0627\u0646 \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u0644\u06d5\u0645\u067e\u06d5\u0631\u0646 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0648 \u0626\u0627\u06a9\u0627\u0631\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u0646\u0627\u0644\u06d5\u0628\u0627\u0631\u06d5.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u060c \u0628\u06d5\u0631\u06ce \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u067e\u0627\u0634\u0627\u0646 \u0644\u06d5 \u062f\u06cc\u0646 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627\u0648\u06d5\u060c \u062c\u0627 \u0633\u06d5\u0631\u062f\u06b5\u06cc\u0627\u0646 \u0645\u06c6\u0631\u06a9\u0631\u0627\u0648\u06d5 \u0648 \u062f\u06d5 \u0647\u06cc\u0686 \u0646\u0627\u06af\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u0648\u06cc\u0634\u062a \u067e\u06ce \u06a9\u06d5\u0648\u062a\u0646\u060c \u0644\u06d5 \u0642\u06d5\u06b5\u0627\u0641\u06d5\u062a\u06cc\u0627\u0646 \u0633\u06d5\u06cc\u0631\u062a \u062f\u06ce. \u06a9\u06d5 \u062f\u06d5\u0634\u067e\u06d5\u06cc\u06a4\u0646 \u0648 \u062a\u06c6 \u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0633\u06cc\u060c \u0626\u06ce\u0698\u06cc \u062f\u0627\u0631\u0646 \u0628\u06d5 \u062f\u06cc\u0648\u0627\u0631 \u0647\u06d5\u06b5\u067e\u06d5\u0633\u06ce\u0631\u0627\u0648\u0646. \u0647\u06d5\u0631 \u0647\u06d5\u0631\u0627\u06cc\u06d5\u06a9 \u0644\u06d5 \u062f\u0698\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u0646. \u0626\u06d5\u0648 \u062f\u0648\u0698\u0645\u0646\u0646\u061b \u062a\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u0644\u06ce \u0628\u067e\u0627\u0631\u06ce\u0632\u06d5. \u062e\u0648\u0627 \u0628\u06cc\u0627\u0646\u06a9\u0648\u0698\u06ce\u061b \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0695\u0648\u0648 \u062f\u06d5\u06a9\u0648\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06ce\u0634\u06cc\u0627\u0646 \u0628\u0648\u062a\u0631\u06ce: \u0648\u06d5\u0631\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u062f\u0627\u0648\u0627\u06cc \u0628\u06d5\u062e\u0634\u06cc\u0646\u0648\u0648 \u0628\u06c6 \u062f\u06d5\u06a9\u0627\u061b \u0633\u06d5\u0631 \u0628\u0627\u062f\u06d5\u062f\u06d5\u0646. \u062f\u06d5\u0634\u06cc\u0627\u0646\u0628\u06cc\u0646\u06cc \u0641\u06cc\u0632 \u062f\u06d5\u0646\u0648\u06ce\u0646\u0646 \u0648 \u062e\u06c6 \u0644\u0627\u062f\u06d5\u062f\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u062a\u06c6 \u062f\u0627\u0648\u0627\u06cc \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u0628\u06a9\u06d5\u06cc\u060c \u06cc\u0627\u0646 \u062f\u0627\u0648\u0627\u06cc \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc\u0627\u0646 \u0646\u06d5\u06a9\u06d5\u06cc\u060c \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u062a\u06d5\u0648\u0641\u06cc\u0631\u06cc \u0646\u06cc\u06cc\u06d5. \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062e\u0648\u062f\u0627 \u0646\u0627\u06cc\u0627\u0646\u0628\u06d5\u062e\u0634\u06ce. \u062e\u0648\u0627 \u06af\u0631\u06c6\u06cc \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648\u062e\u06c6\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627 \u0646\u0627\u06a9\u0627.',
    '\u0647\u06d5\u0631\u0626\u06d5\u0645\u0627\u0646\u06d5\u0634\u0646 \u06a9\u06d5 \u0626\u06ce\u0698\u0646: \u0634\u062a\u06ce \u0645\u06d5\u062f\u06d5\u0646 \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0644\u0627\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627\u0646\u060c \u0647\u06d5\u062a\u0627 \u0628\u06b5\u0627\u0648\u06d5\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646. \u0647\u06d5\u0645\u0648\u0648 \u0633\u0627\u0645\u0627\u0646 \u0648 \u06af\u06d5\u0646\u062c\u06cc\u0646\u06d5\u06cc \u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0634 \u0633\u06d5\u0631 \u0628\u06d5 \u062e\u0648\u062f\u0627\u0646\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648 \u062f\u0648\u0648\u0695\u0627\u0632\u0627\u0646\u06d5 \u0647\u06cc\u0686\u06cc \u062a\u06ce\u0646\u0627\u06af\u06d5\u0646.',
    '\u0626\u06ce\u0698\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u06af\u06d5\u0695\u0627\u06cc\u0646\u06d5\u0648\u06d5 \u0628\u06c6 \u0645\u06d5\u062f\u06cc\u0646\u06d5\u060c \u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u0627\u0646\u060c \u0695\u0648\u0648\u062a \u0648 \u0644\u0627\u062a\u06cc \u0644\u06ce \u062f\u06d5\u0631\u062f\u06d5\u06a9\u06d5\u0646. \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc\u0634 \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u0648\u0627 \u0648 \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0648 \u0626\u0627\u067e\u06c6\u0631\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648 \u062f\u0648\u0648\u0695\u0627\u0632\u0627\u0646\u06d5 \u0647\u06cc\u0686\u06ce \u0646\u0627\u0632\u0627\u0646\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0633\u0627\u0645\u0627\u0646 \u0648 \u0632\u0627\u0631\u0648\u06b5\u06d5\u06a9\u0627\u0646\u062a\u0627\u0646 \u06cc\u0627\u062f\u06cc \u062e\u0648\u0627\u062a\u0627\u0646 \u0646\u0627\u0628\u06ce \u0644\u06d5 \u0628\u06cc\u0631 \u0628\u06d5\u0631\u06ce\u062a\u06d5\u0648\u06d5. \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u060c \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u0646.',
    '\u0644\u06d5\u0648 \u0628\u0698\u06cc\u0648\u06d5\u0634 \u06a9\u06d5 \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u0645\u0627\u0646 \u0628\u06d5\u062e\u0634\u06cc\u0648\u0646\u060c \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc \u0628\u06d5\u062e\u062a \u0628\u06a9\u06d5\u0646\u061b \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5\u06cc \u06cc\u06d5\u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0645\u0631\u062f\u0646 \u062a\u06d5\u06a9 \u0628\u06d5\u062a\u06d5\u06a9\u06cc\u06d5\u0648\u06d5 \u062f\u0627 \u0648\u060c \u0628\u06ce\u0698\u06ce: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0628\u06c6\u0686\u06cc \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06ce \u0646\u0632\u06cc\u06a9 \u0645\u06d5\u0648\u062f\u0627\u062a \u0646\u06d5\u062f\u0627\u0645\u060c \u06a9\u06d5 \u0626\u06d5\u0648\u0633\u0627 \u062e\u06ce\u0631\u0627\u0646 \u0628\u06a9\u06d5\u0645 \u0648 \u0644\u06d5 \u0695\u06cc\u0632\u06cc \u06a9\u0627\u0631 \u0686\u0627\u06a9\u06d5\u06a9\u0627\u0646 \u0628\u0645\u061f',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0648\u06d5\u062e\u062a\u06cc \u0645\u0631\u062f\u0646\u06cc \u0647\u0627\u062a\u0628\u06ce\u060c \u062e\u0648\u0627 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0648\u06d5 \u062f\u0631\u06d5\u0646\u06af\u06cc \u0646\u0627\u062e\u0627. \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u0644\u06d5\u0646\u0627\u0648\u06cc \u0626\u06d5\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc\u06d5\u060c \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u062f\u06d5\u0646. \u0647\u06d5\u0631 \u0628\u06c6 \u062e\u06c6\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0648 \u0633\u067e\u0627\u0633 \u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0648 \u0695\u06d5\u0648\u0627\u06cc\u06d5 \u0648 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9 \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5. \u0633\u0627 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u062a\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695\u0648\u0648 \u067e\u06ce \u0646\u06d5\u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0628\u06d5\u0628\u0695\u0648\u0627\u06cc\u06d5. \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce\u06a9\u06cc\u0634 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u0639\u06d5\u0632\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0648\u06ce\u0646\u06d5\u06cc \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u06a9\u06ce\u0634\u0627\u0648\u06d5 \u0648 \u0634\u06a9\u06b5\u06ce \u0641\u0631\u06d5 \u062c\u0648\u0627\u0646\u06cc \u062f\u0627\u0646\u06ce \u0648 \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u062f\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0646\u0627\u0648 \u0639\u06d5\u0632\u0645\u0627\u0646\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u06cc\u06d5 \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce\u061b \u0647\u06d5\u0631 \u0634\u062a\u06ce\u06a9\u06cc\u0634 \u0628\u06cc\u0634\u06ce\u0631\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u0634\u06a9\u0631\u0627 \u06a9\u06d5\u0646\u060c \u0626\u06d5\u0648 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0648 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0646\u0647\u06ce\u0646\u06cc\u06cc \u062f\u06b5\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0646\u06af\u0648\u0628\u0627\u0633\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0648 \u067e\u06ce \u0646\u06d5\u06af\u06d5\u06cc\u0648\u06d5 \u06a9\u06d5 \u067e\u06ce\u0634\u0648\u0648 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646\u060c \u0633\u0632\u0627\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u0686\u06d5\u0634\u062a \u0648 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646 \u0628\u0648\u0648\u0646.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u06b5\u06af\u06d5\u06cc \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u0631\u06d5\u0648\u06d5 \u0647\u0627\u062a\u0646\u06d5 \u0644\u0627\u06cc\u0627\u0646. \u0626\u06d5\u0648\u0633\u0627 \u0648\u062a\u06cc\u0627\u0646: \u062c\u0627 \u0686\u06c6\u0646 \u0645\u0631\u06c6 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646 \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646 \u0645\u06d5 \u062f\u06d5\u0646\u061f \u0626\u06d5\u0648\u0633\u0627 \u062d\u0627\u0634\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f \u0648 \u0695\u0648\u0648\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627 \u0648 \u062e\u0648\u062f\u0627\u0634 \u067e\u06d5\u06a9\u06cc \u067e\u06ce \u0646\u06d5\u06a9\u06d5\u0648\u062a \u0648 \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5 \u0648 \u0647\u06d5\u0631\u0634\u0627\u06cc\u0627\u0646\u06cc \u067e\u06d5\u0633\u0646 \u062f\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0648\u0627 \u0628\u0648\u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0632\u06cc\u0646\u062f\u0648\u0648 \u0646\u0627\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5. \u0628\u06ce\u0698\u06d5: \u0626\u0627\u0631\u06ce \u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645\u060c \u0628\u06ce \u0633\u06c6 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5\u061b \u067e\u06ce\u0634\u062a\u0627\u0646 \u0626\u06ce\u0698\u0646 \u06a9\u06d5 \u0686\u06a9\u0627\u0631\u06ce\u06a9\u062a\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0645\u06d5\u0634 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0647\u0627\u0633\u0627\u0646\u06d5.',
    '\u062f\u06d5\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u0695\u0648\u0627 \u0628\u06ce\u0646\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0648 \u0628\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0648 \u0628\u06d5\u0648 \u0695\u0648\u0648\u0646\u0627\u06a9\u06cc\u06cc\u06d5 \u06a9\u06d5 \u0647\u06d5\u0646\u0627\u0631\u062f\u0648\u0648\u0645\u0627\u0646\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631. \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u062f\u06d5\u06cc\u06a9\u06d5\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 - \u0644\u06d5 \u0633\u06d5\u06b5\u0627\u062f\u0627 - \u06a9\u06c6\u0648\u06d5 \u062f\u06d5\u06a9\u0627\u060c \u0626\u06d5\u0648\u06d5 \u0695\u06c6\u0698\u06cc \u062a\u06ce\u0634\u06a9\u0627\u0646\u06d5. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631 \u0686\u0627\u06a9\u06d5\u060c \u0647\u06d5\u0631 \u062e\u0631\u0627\u067e\u06d5\u06cc\u06d5 \u06a9\u0631\u062f\u0648\u0648\u06cc\u06d5\u060c \u0695\u0627\u06cc\u062f\u06d5\u0645\u0627\u06b5\u06ce \u0648 \u062f\u06d5\u06cc\u0646\u06ce\u0631\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646. \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06cc \u06af\u06d5\u0648\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u06cc\u0627\u0646 \u0646\u06d5\u0646\u0627\u0633\u06cc\u0648\u06d5 \u0648 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0646\u06d5\u06a9\u0631\u062f\u060c \u0626\u06d5\u0648\u0627\u0646 \u06cc\u0627\u0631\u0627\u0646\u06cc \u0626\u0627\u06af\u0631\u0646 \u0648 \u0647\u06d5\u062a\u0627 \u0633\u06d5\u0631 \u0647\u06d5\u0631 \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0646 \u0648 \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e\u06d5.',
    '\u06a9\u06d5\u0633 \u0647\u06cc\u0686 \u0628\u06d5\u06b5\u0627\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u0646\u0627\u06cc\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u062e\u0648\u062f\u0627 \u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u062f\u0627. \u0647\u06d5\u0631\u06a9\u06ce\u0634 \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06ce\u060c \u062f\u06b5\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u0646 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0686\u06cc \u0648\u062a \u0648\u0627 \u0628\u06a9\u06d5\u0646. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u0634\u0648\u0648 \u0628\u0628\u0648\u06ce\u0631\u0646\u060c \u0644\u06d5 \u0633\u06d5\u0631 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0626\u06ce\u0645\u06d5\u060c \u0647\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06d5\u06cc\u06d5 - \u0628\u06d5\u0626\u0627\u0634\u06a9\u0631\u0627 - \u0695\u0627\u0628\u06af\u06d5\u06cc\u06ce\u0646\u06ce.',
    '\u062e\u0648\u062f\u0627\u06cc \u0645\u06d5\u0632\u0646\u060c \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0628\u06ce. \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0628\u0627 \u062e\u06c6 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0633\u067e\u06ce\u0631\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5 \u0698\u0646\u0627\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u0648 \u0645\u0646\u0627\u06b5\u0627\u0646\u0648\u0648 \u062f\u0698\u0645\u0646\u062a\u0627\u0646\u0646\u061b \u062e\u06c6\u062a\u0627\u0646\u06cc\u0627\u0646 \u0644\u06ce \u0628\u067e\u0627\u0631\u06ce\u0632\u0646. \u0626\u06d5\u06af\u06d5\u0631 \u0686\u0627\u0648\u06cc\u0627\u0646 \u0644\u06ce \u0628\u067e\u06c6\u0634\u0646 \u0648 \u0648\u0627\u0632 \u0628\u06ce\u0646\u0646 \u0648 \u0628\u06cc\u0627\u0646\u0628\u06d5\u062e\u0634\u0646\u060c \u062e\u0648\u062f\u0627\u0634 \u0644\u06d5\u06af\u0648\u0646\u0627\u0647\u0640 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648 \u062f\u0627\u0631\u0627\u06cc\u06cc \u0648 \u0645\u0646\u0627\u06b5\u0627\u0646\u06d5\u06cc \u0647\u06d5\u062a\u0627\u0646\u0646\u060c \u0645\u0627\u06cc\u06d5\u06cc \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u0646. \u067e\u0627\u062f\u0627\u0634\u06ce \u06a9\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5\u060c \u0626\u06d5\u0648 \u0647\u06ce\u0698\u0627\u06cc\u06d5.',
    '\u062f\u06d5\u0628\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646 \u062a\u0627 \u062f\u06d5\u062a\u0648\u0627\u0646\u0646\u061b \u06af\u0648\u06ce \u0695\u0627\u062f\u06ce\u0631 \u0648 \u0628\u06d5\u0631\u0641\u06d5\u0631\u0645\u0627\u0646 \u0628\u0646\u061b \u0645\u0627\u06b5 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u0628\u0628\u06d5\u062e\u0634\u0646\u060c \u0628\u06c6\u062a\u0627\u0646 \u0686\u0627\u06a9\u06d5 \u0648\u061b \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0695\u0698\u062f\u06cc \u062e\u06c6 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0695\u0632\u06af\u0627\u0631\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0648\u0627\u0645\u06ce \u0695\u06d5\u0648\u0627 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0695\u06d5\u0648\u0627 \u0628\u0628\u06cc\u0646\u0646\u060c \u0686\u06d5\u0646\u062f \u0642\u0627\u062a \u067e\u06ce\u062a\u0627\u0646 \u0626\u06d5\u062f\u0627\u062a\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u0648\u0648 \u062e\u06c6\u0634 \u062f\u06d5\u0628\u06ce. \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0628\u06c6 \u0634\u0648\u06a9\u0631 \u062f\u06d5\u0628\u06ce \u0648 \u0644\u06d5\u0633\u06d5\u0631\u062e\u06c6\u06cc\u06d5.',
    '\u0644\u06d5 \u067e\u06d5\u0646\u0627\u0645\u06d5\u06a9\u06cc \u0648 \u062e\u06c6\u06cc\u0627\u06cc\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5\u061b \u0628\u06d5\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a \u0648 \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0626\u06d5\u06af\u06d5\u0631 \u0698\u0646\u0627\u0646\u0648\u0648 \u062a\u06d5\u06b5\u0627\u0642 \u062f\u0627\u0646\u060c \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u06d5\u0646 \u0648 \u0628\u06cc\u0628\u0698\u06ce\u0631\u0646. \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u0634\u06d5\u0631\u0645 \u0628\u06a9\u06d5\u0646. \u0644\u06d5 \u0645\u0627\u06b5 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0631\u06cc\u0627\u0646 \u0645\u06d5\u06a9\u06d5\u0646\u061b \u062f\u06d5\u0631\u06cc\u0634 \u0646\u06d5\u0686\u0646. \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u06a9\u0627\u0631\u06ce\u06a9\u06cc \u0634\u0648\u0648\u0631\u06d5\u06cc\u06cc \u0628\u06a9\u06d5\u0646. \u0626\u06d5\u0645\u06d5 \u06a9\u06d5\u0648\u0634\u06d5\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0648 \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5 \u06a9\u06d5\u0648\u0634\u06d5\u0646\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5\u0648\u0644\u0627\u062a\u0631 \u0686\u06ce\u060c \u0646\u0627\u0647\u06d5\u0642\u06cc \u0644\u06d5\u062e\u06c6\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5. \u062a\u06c6 \u0686 \u0632\u0627\u0646\u06cc\u060c \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc\u06d5 \u0644\u06d5\u067e\u0627\u0634 \u0626\u06d5\u0645\u06d5 \u062e\u0648\u0627 \u0634\u062a\u06ce \u062a\u0631 \u0648\u06d5\u062f\u06cc \u0628\u06ce\u0646\u06ce.',
    '\u0633\u0627 \u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce \u0645\u0627\u0648\u06d5\u06a9\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0628\u0631\u062f\u060c \u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5 \u062a\u06d5\u0631\u0632\u06ce\u06a9\u06cc \u067e\u06d5\u0633\u0646\u062f \u0695\u0627\u06cc\u0627\u0646\u06af\u0631\u0646 \u06cc\u0627\u0646 \u0644\u06ce\u06cc\u0627\u0646 \u062c\u06cc\u0627 \u0628\u0628\u0646\u06d5\u0648\u06d5\u061b \u0628\u06d5 \u0634\u06ce\u0648\u06d5\u06cc\u06ce \u06a9\u06d5 \u0644\u06d5\u0646\u0627\u0648 \u062e\u06c6\u062a\u0627\u0646\u062f\u0627 \u0628\u0627\u0648\u06d5. \u062f\u0648\u0648 \u06a9\u06d5\u0633\u06cc \u0695\u0627\u0633\u062a \u0648 \u062f\u0631\u0648\u0633\u062a\u06cc\u0634 \u0647\u06d5\u0631 \u0644\u06d5 \u062e\u06c6\u062a\u0627\u0646 \u0644\u06d5\u0645 \u06a9\u0627\u0631\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u06a9\u06d5\u0646\u060c \u06a9\u06d5 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06c6 \u062e\u0648\u0627 \u0628\u062f\u06d5\u0646. \u0647\u06d5\u0631\u06a9\u06ce \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u062e\u0648\u0627 \u0648 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0647\u06d5\u06cc\u06d5\u060c \u0628\u06d5\u0645\u06d5 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u062f\u06d5\u06a9\u0631\u06ce\u0646. \u0647\u06d5\u0631\u06a9\u06ce\u0634 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u06ce\u060c \u062f\u06d5\u0631\u0648\u06ce\u06a9\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06d5\u0648\u06d5 \u0628\u0698\u06cc\u0648\u06cc \u0628\u06c6 \u062f\u06d5\u0646\u06ce\u0631\u06ce \u06a9\u06d5 \u0628\u06d5 \u062e\u06d5\u06cc\u0627\u06b5\u06cc\u062f\u0627 \u0646\u0627\u06cc\u06d5. \u0647\u06d5\u0631\u06a9\u06ce \u062e\u06c6\u06cc \u0628\u06d5 \u062e\u0648\u0627 \u0628\u0633\u067e\u06ce\u0631\u06ce\u060c \u0626\u06d5\u0648\u06cc \u0628\u06d5\u0633\u06d5\u061b \u062e\u0648\u0627 \u0628\u06d5 \u0645\u06d5\u0628\u06d5\u0633\u062a\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u06af\u0627. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06c6 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc\u06d5\u06a9\u06cc \u062f\u0627\u0646\u0627\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06d5\u0648 \u06a9\u06d5 \u0644\u06d5 \u0639\u0648\u0632\u0631 \u067e\u06ce\u062f\u0627\u0647\u0627\u062a\u0646 \u0646\u0627\u0647\u06ce\u0645\u0646\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0639\u0648\u0632\u0631 \u0646\u0627\u0634\u06c6\u0646 - \u0626\u06d5\u0631 \u0634\u06a9\u062a\u0627\u0646 \u0628\u0648\u0648 - \u0626\u06d5\u0634\u06ce \u0633\u06ce \u0645\u0627\u0646\u06af \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695 \u0628\u0646. \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0633\u06a9\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u0645\u0627\u0648\u06d5\u06cc \u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u06cc\u06cc\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u0627\u0646\u0627\u0646\u06cc \u0633\u06a9\u06d5\u06a9\u06d5\u06cc\u0627\u0646\u06d5. \u0647\u06d5\u0631\u06a9\u06ce\u0634 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u06ce\u060c \u062e\u0648\u062f\u0627 \u06a9\u0627\u0631\u06cc \u0647\u0627\u0633\u0627\u0646 \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u0645\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5\u06cc \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u06ce. \u0647\u06d5\u0631\u06a9\u06ce\u0634 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u06ce\u060c \u062e\u06d5\u062a\u0627\u06a9\u0627\u0646\u06cc \u062f\u0627\u062f\u06d5\u067e\u06c6\u0634\u06ce \u0648 \u067e\u0627\u062f\u0627\u0634\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc \u062f\u06d5\u062f\u0627\u062a\u06ce.',
    '\u0644\u06d5 \u0647\u06d5\u0631\u06a9\u0648\u06ce\u062f\u0627 \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0698\u06cc\u0646 - \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u062f\u06d5\u0633 \u0695\u06c6\u06cc\u0646\u062a\u0627\u0646 - \u062c\u06ce\u06cc \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5. \u0647\u06ce\u0646\u062f\u06d5\u0634\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u0628\u06c6 \u0645\u06d5\u0647\u06ce\u0646\u0646 \u06a9\u06d5 \u0628\u06cc\u0627\u0646\u062e\u06d5\u0646\u06d5 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5\u0648\u06d5. \u06af\u06d5\u0631 \u0645\u0646\u0627\u06b5\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0633\u06a9\u062f\u0627\u060c \u0626\u06ce\u0648\u06d5 \u0644\u06ce\u06cc\u0627\u0646 \u0628\u06d5\u062e\u062a \u0628\u06a9\u06d5\u0646 \u062a\u0627 \u0633\u06a9\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u062f\u0627\u062f\u06d5\u0646\u06ce\u0646. \u0626\u06d5\u0648\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0634\u06cc\u0631\u06cc\u0627\u0646 \u062f\u0627 \u0628\u06d5\u0645\u0646\u062f\u0627\u06b5\u06d5\u06a9\u06d5\u0648\u060c \u06a9\u0631\u0627\u06cc \u062e\u06c6\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0628\u062f\u06d5\u0646\u06ce \u0648 \u0644\u06d5\u0646\u0627\u0648 \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5\u062a\u06d5\u0631\u0632\u06ce \u0695\u06d5\u0648\u0627 \u067e\u06ce\u06a9 \u0648\u06d5\u0631\u0646. \u062e\u06c6 \u0626\u06d5\u06af\u06d5\u0631 \u067e\u06ce\u06a9\u06cc\u0634 \u0646\u06d5\u0647\u0627\u062a\u0646\u060c \u0628\u0627 \u06cc\u06d5\u06a9\u06ce \u062a\u0631 \u0628\u06c6\u06cc \u0634\u06cc\u0631 \u0628\u062f\u0627.',
    '\u06a9\u06ce \u062f\u0627\u0631\u0627\u06cc\u06d5 \u0644\u06d5\u062f\u0627\u0631\u0627\u06cc\u06cc\u06cc \u062e\u06c6\u06cc \u0628\u06d5\u062e\u062a \u06a9\u0627 \u0648 \u06a9\u06ce\u0634 \u0628\u0698\u06cc\u0648\u06cc \u06a9\u06d5\u0645 \u067e\u06ce \u06af\u06d5\u06cc\u0648\u06d5\u060c \u0644\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc \u062e\u0648\u062f\u0627 \u067e\u06ce\u06cc\u062f\u0627\u0648\u06d5 \u0628\u06d5\u062e\u062a \u0628\u06a9\u0627. \u062e\u0648\u062f\u0627 \u0626\u06d5\u0631\u06a9 \u0646\u0627\u062e\u0627\u062a\u06d5 \u0633\u06d5\u0631 \u06a9\u06d5\u0633\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5\u0648\u06d5\u0646\u062f\u06d5\u06cc \u067e\u06ce\u06cc \u062f\u0627\u0648\u06d5. \u062e\u0648\u0627 \u0632\u0648\u0648 \u0644\u06d5\u067e\u0627\u0634 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5 \u0641\u06d5\u0631\u062d\u0627\u0646\u06cc \u062f\u06ce\u0646\u06ce.',
    '\u0634\u0627\u0631\u06af\u06d5\u0644\u06cc \u0632\u06c6\u0631 \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646\u06cc \u0648\u06d5\u062f\u06d5\u0631 \u06a9\u06d5\u0648\u062a\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u067e\u06ce\u0645\u0627\u0646 \u0628\u0698\u0627\u0631\u062f\u0646\u06d5\u0648\u06d5\u061b \u0628\u0698\u0627\u0631\u062f\u0646\u06d5\u0648\u06d5\u06cc\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u062a\u0648\u0646\u062f \u0648 \u0626\u0627\u0632\u0627\u0631\u0645\u0627\u0646 \u062f\u0627\u0646 \u0628\u06d5 \u0626\u0627\u0632\u0627\u0631\u062f\u0627\u0646\u06ce\u06a9\u06cc \u062f\u0698\u0648\u0627\u0631.',
    '\u0626\u06cc\u062a\u0631 \u0626\u0627\u06a9\u0627\u0645\u06cc \u0626\u0627\u06a9\u0627\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0686\u06d5\u0634\u06cc \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u0631\u0647\u06d5\u0645\u06cc \u06a9\u0631\u062f\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0632\u06cc\u0627\u0646 \u0628\u0648\u0648.',
    '\u062e\u0648\u062f\u0627 \u0628\u06c6\u06cc\u0627\u0646 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u0632\u06c6\u0631\u062a\u0648\u0646\u062f \u0648\u062a\u06cc\u0698\u06cc \u0626\u0627\u0645\u0627\u062f\u06d5 \u06a9\u0631\u062f. \u062f\u06d5\u0633\u0627 \u0626\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u0626\u06d5\u0634\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646. \u0626\u06d5\u0648\u0627 \u062e\u0648\u062f\u0627 \u0642\u0648\u0631\u0639\u0627\u0646\u06cc \u0628\u06c6 \u0646\u0627\u0631\u062f\u0648\u0648\u0646\u06d5 \u062e\u0648\u0627\u0631.',
    '\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06d5 \u06a9\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5 \u0695\u0648\u0648\u0646 \u06a9\u06d5\u0631\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0648\u0627\u062a\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u06ce\u060c \u062a\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u0648 \u0626\u0627\u06a9\u0627\u0631 \u0686\u0627\u06a9\u0646 \u0644\u06d5\u062a\u0627\u0631\u06cc\u06a9\u0627\u06cc\u06cc \u062f\u06d5\u0631\u0628\u062e\u0627 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0695\u06c6\u0634\u0646\u0627\u06a9\u06cc\u0627\u0646 \u0628\u06d5\u0631\u06ce \u0648 \u0647\u06d5\u0631\u06a9\u06ce\u0634 \u0628\u06d5 \u062e\u0648\u0627\u06cc \u0628\u0695\u0648\u0627 \u0647\u06d5\u0628\u06ce \u0648 \u0626\u0627\u06a9\u0627\u0631 \u0686\u0627\u06a9 \u0628\u06ce\u060c \u062f\u06d5\u06cc\u0646\u06ce\u0631\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u0631 \u0648 \u0647\u06d5\u0631\u060c \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646. \u062f\u06cc\u0627\u0631\u06d5 \u062e\u0648\u062f\u0627 \u0628\u0698\u06cc\u0648\u06ce \u0628\u0627\u0634\u06cc \u062f\u06d5\u062f\u0627\u062a\u06ce.',
    '\u0626\u06d5\u0648\u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u062d\u06d5\u0648\u062a \u0639\u06d5\u0632\u0645\u0627\u0646 \u0648 \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634\u060c \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0628\u06d5\u0646\u06ce\u0648\u0627\u0646\u06cc\u0627\u0646\u062f\u0627 \u062f\u06ce\u062a \u0648 \u062f\u06d5\u0686\u06ce. \u062a\u0627 \u0628\u0632\u0627\u0646\u0646 \u062e\u0648\u0627 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5 \u0648 \u0632\u0627\u0646\u0633\u062a\u06cc \u062e\u0648\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u062f\u06d5\u0648\u0631\u06d5 \u062f\u0627\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0628\u06c6 \u0626\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u062e\u0648\u062f\u0627 \u0628\u06d5 \u062a\u06c6\u06cc \u0695\u06d5\u0648\u0627 \u062f\u06cc\u0648\u06d5 - \u0628\u06c6 \u062f\u06b5\u062f\u0627\u0631\u06cc\u06cc \u0698\u0646\u06d5\u06a9\u0627\u0646\u062a - \u0644\u06d5 \u062e\u06c6\u062a\u06cc \u0646\u0627\u0695\u06d5\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u06cc\u061f \u062e\u0648\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u0627 \u062e\u0648\u062f\u0627 \u0628\u0695\u06cc\u0627\u0631\u06cc \u062f\u0627 \u0628\u06d5 \u0647\u06d5\u06b5\u0648\u06d5\u0634\u0627\u0646\u06cc \u0633\u0648\u06ce\u0646\u062f\u0627\u0646\u0648\u0648. \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627 \u0645\u06d5\u0632\u0646\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5 \u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0632\u0627\u0646\u0627\u06cc \u0644\u06d5\u06a9\u0627\u0631\u0632\u0627\u0646\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u062c\u0627\u0631\u06ce \u06a9\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0644\u0627\u06cc \u0628\u0695\u06ce \u0644\u06d5 \u0647\u0627\u0648\u0633\u06d5\u0631\u0627\u0646\u06cc \u0642\u0633\u06ce\u06a9\u06cc \u0628\u06d5 \u0633\u0631\u062a\u06d5 \u062f\u0631\u06a9\u0627\u0646\u062f. \u062c\u0627 \u0648\u06d5\u062e\u062a\u06ce \u0648\u06ce \u0626\u06d5\u0648\u06d5\u06cc \u0648\u062a \u0628\u06d5 [\u0647\u06d5\u0648\u06ce\u06a9\u06d5\u06cc] \u0648 \u062e\u0648\u0627\u0634 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062a\u06ce\u06af\u06d5\u06cc\u0627\u0646\u062f\u061b \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06ce \u0644\u06d5\u0648 \u0646\u0647\u06ce\u0646\u06cc\u06cc\u06d5\u06cc \u06af\u0648\u062a \u0648 \u0644\u06d5 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc \u0648\u0627\u0632\u06cc \u0647\u06ce\u0646\u0627. \u0648\u06ce \u06af\u06c6: \u0626\u06d5\u0645\u06d5 \u06a9\u06ce \u0628\u06d5 \u062a\u06c6\u06cc \u06af\u0648\u062a\u061f \u06af\u0648\u062a\u06cc: \u0632\u0627\u0646\u0627\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u067e\u06ce\u06cc \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0645.',
    '\u0628\u0627\u0634 \u0626\u06d5\u0648\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0647\u06d5\u0631\u062f\u0648\u0648\u06a9\u062a\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u0627 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5. \u062f\u06cc\u0627\u0631\u06d5 \u062f\u06b5\u062a\u0627\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u0646\u0627\u0695\u06d5\u0648\u0627 \u0644\u0627\u06cc \u062f\u0627\u0648\u06d5. \u06af\u06d5\u0631 \u0644\u06d5 \u062f\u0698\u06cc \u0626\u06d5\u0645 \u0628\u0628\u0646\u06d5 \u06cc\u06d5\u06a9\u060c \u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u06ce \u0645\u06d5\u0632\u0646\u06cc\u06d5\u062a\u06cc \u0648 \u0644\u06d5\u0645\u06d5 \u0628\u06d5\u0648\u0644\u0627 \u062c\u0648\u0628\u0631\u0627\u0626\u06cc\u0644 \u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u06cc \u0628\u06d5\u0631\u06a9\u06d5\u062a\u06cc \u0648 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646\u060c \u067e\u0634\u062a\u06cc \u062f\u06d5\u06af\u0631\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u062a\u06d5\u06b5\u0627\u0642 \u0628\u062f\u0627\u060c \u0634\u0627\u06cc\u06d5\u062a\u0627 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0626\u06d5\u0648 \u0698\u0646\u0627\u0646\u06cc \u0644\u06d5\u0646\u06af\u06c6 \u0628\u0627\u0634\u062a\u0631\u06cc - \u0644\u06d5\u0628\u0627\u062a\u06cc \u0626\u06ce\u0648\u06d5 - \u0628\u062f\u0627\u062a\u06ce\u061b \u0698\u0646\u0627\u0646\u06ce\u06a9\u06cc \u06af\u06d5\u0631\u062f\u0646\u06a9\u06d5\u0686\u06cc \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0644\u06d5 \u062e\u0631\u0627\u067e\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u062e\u0648\u0627\u067e\u06d5\u0631\u0633\u062a\u0627\u0646\u06cc \u0695\u06c6\u0698\u0648\u0648\u06af\u0631\u060c \u0644\u06d5 \u0628\u06ce\u0648\u06d5\u0698\u0646\u0627\u0646 \u0648 \u06a9\u06cc\u0698\u0627\u0646.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u062e\u06c6\u062a\u0627\u0646 \u0648 \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06cc\u0634\u062a\u0627\u0646 \u0644\u06d5\u0648 \u0626\u0627\u06af\u0631\u06d5 \u0628\u067e\u0627\u0631\u06ce\u0632\u0646 \u06a9\u06d5 \u0633\u0648\u0648\u062a\u06d5\u0646\u06cc \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u0648 \u0644\u06d5 \u062a\u06d5\u0648\u06d5\u0646\u06d5. \u0641\u0631\u06cc\u0634\u062a\u06d5\u06cc \u0628\u06d5 \u0632\u06d5\u0628\u0631 \u0648 \u0632\u06d5\u0646\u06af \u0648 \u062a\u0648\u0646\u062f\u0648\u062a\u06cc\u0698\u06cc \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u06d5\u0646\u061b \u062e\u0648\u0627 \u0686\u06c6\u0646\u06cc\u0627\u0646 \u0641\u06d5\u0631\u0645\u0627\u0646 \u067e\u06ce \u0628\u062f\u0627\u060c \u0644\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062f\u06d5\u0631 \u0646\u0627\u06a9\u06d5\u0648\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0628\u06d5\u0648\u0627\u0646\u06cc \u062f\u06d5\u0633\u067e\u06ce\u0631\u06ce \u0628\u06d5\u062c\u06ce\u06cc \u062f\u06ce\u0646\u0646.',
    '\u0626\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633 \u0628\u0648\u0648\u0646! \u0626\u06d5\u0645\u0695\u06c6 \u062a\u06d5\u0648\u0627\u0632\u06cc \u067e\u06ce \u0646\u0627\u0648\u06ce. \u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u06a9\u0631\u062f\u0648\u0648\u062a\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u062c\u06d5\u0632\u0627\u062a\u0627\u0646 \u062f\u06d5\u062f\u0631\u06ce.',
    '\u0626\u06d5\u06cc \u06af\u06d5\u0644\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695\u0627\u0646! \u0628\u06d5\u0631\u06d5\u0648 \u062e\u0648\u0627 \u0628\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5\u060c \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc \u0695\u0627\u0633\u062a \u0648 \u062f\u0631\u0648\u0633\u062a\u061b \u0634\u0627\u06cc\u06d5\u062a\u0627 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u06af\u0648\u0646\u0627\u0647\u0648\u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0644\u0627\u0628\u06d5\u0631\u06ce \u0648 \u0628\u0648\u0648\u0646\u06ce\u0631\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0628\u0627\u063a\u0627\u062a\u06ce\u06a9 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5\u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627. \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u062e\u0648\u0627 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0648\u06d5\u0644 \u0626\u06d5\u0648 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0633\u0648\u0648\u06a9 \u0648 \u0647\u06d5\u0646\u0627\u0633\u06d5 \u0633\u0627\u0631\u062f \u0646\u0627\u06a9\u0627 \u0648 \u062a\u06cc\u0634\u06a9\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u067e\u06ce\u0634\u06cc\u0627\u0646 \u0648 \u0644\u06d5 \u0626\u0627\u0644\u06cc\u06cc \u0695\u0627\u0633\u062a\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u062a\u06d5\u06a4\u06af\u06d5\u0695\u06cc\u06d5\u062a\u06cc \u0648 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646! \u062a\u06cc\u0634\u06a9\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0632\u06cc\u0627\u062a\u0631 \u06a9\u06d5 \u0648 \u0644\u06ce\u0645\u0627\u0646 \u062e\u06c6\u0634 \u0628\u06d5\u061b \u062e\u06c6\u062a \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc.',
    '\u0626\u06d5\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631! \u0648\u06d5\u0644 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5 \u0648 \u0626\u06d5\u0648 \u062f\u0648\u0648\u0695\u0627\u0632\u0627\u0646\u06d5 \u062c\u06d5\u0646\u06af \u0628\u06a9\u06d5\u060c \u0632\u06d5\u0628\u0631 \u0648 \u0632\u06d5\u0646\u06af\u06cc\u0634\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u06d5. \u0626\u06d5\u0646\u0648\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0626\u0627\u06af\u0631\u06d5 \u0648 \u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u062e\u0631\u0627\u067e\u06d5.',
    '\u062e\u0648\u0627 \u0698\u0646\u06cc \u0646\u0648\u0648\u062d \u0648 \u0698\u0646\u06cc \u0644\u0648\u0648\u062a - \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0646 - \u0628\u06d5 \u0646\u0645\u0648\u0648\u0646\u06d5 \u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5. \u0647\u06d5\u0631\u062a\u06a9 \u0644\u06d5\u0628\u0646 \u062f\u0648\u0648 \u0639\u06d5\u0628\u062f\u06cc \u0686\u0627\u06a9\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u0648\u0648\u0646\u060c \u063a\u06d5\u06cc\u0627\u0646\u06d5\u062a\u06cc\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u06a9\u0631\u062f\u0646. \u0646\u06d5\u06cc\u0627\u0646\u062a\u0648\u0627\u0646\u06cc \u0644\u06d5 \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686 \u0634\u062a\u06cc\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5 \u0628\u06a9\u06d5\u0646. \u0648\u062a\u0631\u0627: \u0626\u06ce\u0648\u06d5\u0634 \u0628\u0695\u06c6\u0646\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631\u06d5\u0648\u06d5\u060c \u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06cc\u0686\u0646\u06ce.',
    '\u062e\u0648\u062f\u0627 \u0698\u0646\u06d5\u06a9\u06d5\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u06cc\u0634 - \u0628\u06c6\u06a9\u06d5\u0633\u0627\u0646\u06cc \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 - \u0628\u06d5 \u0646\u0645\u0648\u0648\u0646\u06d5 \u062f\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5. \u06a9\u06d5 \u0644\u06d5\u0628\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u067e\u0627\u0695\u0627\u06cc\u06d5\u0648\u06d5: \u062a\u06c6 \u0644\u06d5 \u0644\u0627\u06cc \u062e\u06c6\u062a \u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u0627 \u0645\u0627\u06b5\u06ce\u06a9\u0645 \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u0628\u06a9\u06d5 \u0648 \u0695\u0632\u06af\u0627\u0631\u0645 \u06a9\u06d5 \u0644\u06d5 \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u0626\u06d5\u0648\u061b \u0644\u06d5 \u06a9\u06c6\u0645\u06d5\u06b5\u06cc \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u06cc\u0634\u0645 \u0695\u0627\u0647\u06cc \u06a9\u06d5.',
    '\u0645\u06d5\u0631\u06cc\u06d5\u0645\u06cc \u06a9\u0686\u06cc \u0639\u06cc\u0645\u0695\u0627\u0646\u06cc\u0634\u060c \u0626\u06d5\u0648\u06d5\u06cc \u0634\u06d5\u0631\u0645\u06cc \u0644\u06d5 \u06a9\u0627\u0631\u06cc \u0634\u0648\u0648\u0631\u06d5\u06cc\u06cc \u067e\u0627\u0631\u0627\u0633\u062a. \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0641\u0648\u0648\u06cc \u062e\u06c6\u0645\u0627\u0646 \u062a\u06ce\u0645\u0627\u0646 \u062f\u06d5\u0645\u0627\u0646\u062f\u061b \u0628\u06d5 \u0648\u062a\u0627\u0631 \u0648 \u06a9\u062a\u06ce\u0628\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u0695\u0648\u0627\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u0644\u06d5 \u0695\u06ce\u0632\u06d5\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u0628\u06d5\u0631\u0627\u0646 \u0628\u0648\u0648.',
    '\u0645\u06d5\u0632\u0646\u0627\u06cc\u06d5\u062a\u06cc \u0648 \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u06cc \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc\u060c \u062e\u0648\u0646\u06a9\u0627\u0631\u06cc \u0628\u06d5 \u062f\u06d5\u0633 \u062e\u06c6\u06cc\u06d5\u062a\u06cc \u0648 \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062a\u0648\u0627\u0646\u0627\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0645\u0631\u062f\u0646 \u0648 \u0698\u06cc\u0646\u06cc \u062f\u0627\u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u062a\u0627 \u0626\u06ce\u0648\u06d5 \u062a\u0627\u0642\u06cc \u06a9\u0627\u062a\u06d5\u0648\u06d5 \u06a9\u0627\u0645\u062a\u0627\u0646 \u0626\u0627\u06a9\u0627\u0631\u06cc \u062c\u0648\u0627\u0646\u062a\u0631\u06d5. \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0644\u06ce\u0628\u0648\u0631\u062f\u06d5\u06cc \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a.',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u06d5\u06cc \u062d\u06d5\u0648 \u0626\u0627\u0633\u0645\u0627\u0646\u06cc \u0686\u06cc\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0686\u06cc\u0646\u06cc \u0633\u0627\u0632\u062f\u0627\u0648\u06d5. \u0644\u06d5 \u062f\u06d5\u0633\u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u062f\u0627 \u0647\u06cc\u0686 \u0646\u0627\u0695\u06ce\u06a9\u06cc\u06cc\u06d5\u06a9 \u0646\u0627\u0628\u06cc\u0646\u06cc. \u062c\u0627\u0631\u06ce\u06a9\u06cc \u062a\u0631 \u062a\u0645\u0627\u0634\u0627 \u06a9\u06d5\u060c \u0626\u0627\u062e\u06c6 \u0642\u06d5\u06b5\u0634\u06ce\u06a9\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5\u061f',
    '\u062f\u06cc\u0633\u0627\u0646\u06d5\u0648\u06d5 \u0686\u0627\u0648\u06cc \u067e\u06ce\u062f\u0627 \u0628\u06af\u06ce\u0695\u06d5\u0648\u06d5. \u0633\u06c6\u0645\u0627\u06cc \u0686\u0627\u0648\u062a \u0628\u06d5 \u062f\u0627\u0645\u0627\u0648\u06cc \u0648 \u0628\u06d5 \u0695\u0627\u0645\u0627\u0648\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u062e\u06c6\u062a \u062f\u06d5\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06cc \u062f\u0646\u06cc\u0627\u0645\u0627\u0646 \u0628\u06d5 \u0632\u06c6\u0631 \u0686\u0631\u0627 \u0695\u0627\u0632\u0627\u0646\u062f\u06d5\u0648\u06d5. \u06a9\u0631\u062f\u06cc\u0634\u0645\u0627\u0646\u0646 \u0628\u06d5 \u06a9\u06d5\u0631\u0633\u062a\u06d5\u06cc \u0695\u0627\u0648\u06d5\u062f\u0648\u0648 \u0646\u0627\u0646\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u0627\u0646. \u0626\u0627\u0632\u0627\u0631\u06cc \u06af\u0695\u067e\u06d5\u062f\u0627\u0631\u06cc\u0634\u0645\u0627\u0646 \u0628\u06c6 \u0633\u0627\u0632 \u06a9\u0631\u062f\u0648\u0646.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u060c \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0647\u06d5\u06cc\u06d5 \u0648 \u0626\u0627\u06cc \u0686 \u0626\u06d5\u0646\u062c\u0627\u0645\u06ce \u062e\u0631\u0627\u067e\u06d5!',
    '\u06af\u06d5\u0631 \u062f\u06d5\u06cc\u0627\u0646\u0647\u0627\u0648\u06cc\u0646\u06d5 \u0646\u0627\u0648\u06cc\u060c \u0646\u06ce\u06b5\u06d5\u0646\u06ce\u06b5\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0627\u0646 \u06af\u0648\u06ce \u0644\u06ce \u062f\u06d5\u0628\u06ce \u0648 \u0647\u06d5\u0631 \u0647\u06d5\u06b5\u062f\u06d5\u0686\u06ce.',
    '\u0648\u06d5\u062e\u062a\u06d5 \u0644\u06d5 \u062f\u0627\u062e\u0627 \u0634\u06d5\u0642 \u0628\u06d5\u0631\u06ce. \u062a\u0627 \u062c\u0648\u0648\u0646\u06ce\u06a9\u06cc \u062a\u06ce \u062f\u0627\u0648\u06ce\u0698\u0646\u060c \u06af\u0632\u06cc\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u062a\u0627\u0646 \u0646\u06d5\u0647\u0627\u062a\u06d5 \u0644\u0627\u061f',
    '\u0626\u06ce\u0698\u0646: \u0628\u06d5\u06b5\u06ce\u060c \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631 \u0647\u0627\u062a\u0628\u0648\u0648\u06d5 \u0644\u0627\u0645\u0627\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u0628\u0627\u0648\u06d5\u0695\u0645\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f \u0648 \u06af\u0648\u062a\u0645\u0627\u0646: \u062e\u0648\u0627 \u0647\u06cc\u0686 \u0634\u062a\u06cc \u0646\u06d5\u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631\u061b \u0626\u06ce\u0648\u06d5 \u0628\u06ce \u0633\u06c6 \u0644\u06d5\u0646\u0627\u0648 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc \u06af\u06d5\u0648\u0631\u06d5\u062f\u0627\u0646.',
    '\u0626\u06d5\u0634\u06b5\u06ce\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u06af\u0648\u06ce \u0628\u06cc\u0633 \u0628\u0627\u06cc\u0646 \u06cc\u0627\u062e\u06c6 \u062a\u06ce \u06af\u06d5\u06cc\u0628\u0627\u06cc\u0646\u060c \u0646\u06d5\u062f\u06d5\u0628\u0648\u0648\u06cc\u0646\u06d5 \u06cc\u0627\u0631\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0626\u0627\u06af\u0631\u062f\u0627\u0646\u06d5.',
    '\u0626\u0627\u0648\u0627 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u067e\u06ce \u0644\u06ce \u062f\u06d5\u0646\u06ce\u0646. \u0647\u06d5\u06cc \u0644\u06d5 \u0626\u0627\u0631\u0627\u062f\u0627 \u0646\u06d5\u0645\u06ce\u0646\u0646 \u0626\u06d5\u0645 \u0647\u06d5\u0648\u0627\u06b5\u0627\u0646\u06cc \u062f\u06c6\u0698\u06d5\u0647\u06d5!',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc - \u0644\u06d5 \u0646\u06d5\u062f\u06cc\u062f\u06d5 - \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0626\u06d5\u062a\u0631\u0633\u0646\u060c \u0644\u06ce\u062e\u06c6\u0634\u0628\u0648\u0648\u0646 \u0648 \u067e\u0627\u062f\u0627\u0634\u06cc \u06af\u06d5\u0648\u0631\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06ce\u0648\u06d5 \u0633\u0631\u062a\u06d5 \u0642\u0633\u06d5 \u0628\u06a9\u06d5\u0646\u060c \u06cc\u0627\u0646 \u0648\u06d5 \u062f\u06d5\u0646\u06af\u06cc \u0628\u06d5\u0631\u0632\u06cc \u0628\u06ce\u0698\u0646\u060c \u0626\u06d5\u0648 \u0644\u06d5 \u0695\u0627\u0632\u06ce \u06a9\u06d5 \u0628\u06d5 \u062f\u06b5\u0627\u0646\u062f\u0627 \u062f\u06d5\u0628\u0648\u0648\u0631\u06ce \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0648\u0648\u06cc\u06d5\u060c \u0686\u06c6\u0646 \u0646\u0627\u0632\u0627\u0646\u06ce\u061f \u0648\u0631\u062f\u06cc\u0644\u06d5\u0628\u06cc\u0646 \u0648 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u060c \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u06cc \u0628\u06c6 \u06a9\u06d5\u0648\u06cc \u06a9\u0631\u062f\u0648\u0648\u0646\u060c \u0634\u0627\u0646\u06d5\u0648\u0634\u0627\u0646 \u062a\u06ce\u06cc\u062f\u0627 \u0628\u06af\u06d5\u0695\u06ce\u0646 \u0648 \u0644\u06d5 \u0628\u0698\u06cc\u0648\u06d5\u06a9\u06d5\u06cc \u0628\u062e\u06c6\u0646. \u0647\u06d5\u0633\u062a\u0627\u0646\u06d5\u0648\u06d5\u0634 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5 \u062a\u0631\u0633\u0648\u0648 \u0646\u06cc\u06cc\u06d5\u060c \u06a9\u06d5 \u0628\u06d5 \u0647\u06d5\u0631\u062f\u0627 \u0695\u06c6\u062a\u0627\u0646 \u0628\u06d5\u0631\u06ce \u0648 \u0644\u06d5\u067e\u0695 \u0632\u06d5\u0648\u06cc\u0646 \u0628\u06ce\u062a\u06d5 \u0644\u06d5\u0631\u06cc\u0646\u061f',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0644\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06d5 \u062a\u0631\u0633\u0648\u0648 \u0646\u06cc\u06cc\u06d5\u060c \u0628\u0627\u0695\u0646\u06ce\u06a9\u06cc \u0632\u06cc\u062e \u0648 \u0686\u06d5\u0648\u062a\u0627\u0646 \u0644\u06ce \u0647\u06d5\u06b5\u0628\u06a9\u0627\u061f \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u0632\u0648\u0648 \u067e\u06ce\u062f\u06d5\u0632\u0627\u0646\u0646 \u0647\u06d5\u0695\u06d5\u0634\u06d5\u06cc \u0645\u0646 \u0686\u062a\u06c6 \u0628\u0648\u0648\u06d5.',
    '\u062e\u06c6 \u0628\u06d5\u0631 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0647\u06d5\u0631 \u0647\u06d5\u0628\u0648\u0648\u0646 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646\u06cc\u0627\u0646 \u0628\u0695\u0648\u0627 \u0646\u06d5\u0628\u0648\u0648\u06d5\u061b \u0633\u0627 \u0686\u06c6\u0646\u06cc\u0627\u0646 \u0628\u06d5 \u06af\u0698\u062f\u0627 \u0647\u0627\u062a\u0645.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646 \u0626\u06d5\u0648 \u0645\u06d5\u0644\u0627\u0646\u06d5\u06cc\u0627\u0646 \u0646\u06d5\u062f\u06cc\u0648\u0646 \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u0648\u0648\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0648\u06d5 \u0644\u06d5\u0646\u06af\u06d5\u0631\u06cc\u0627\u0646\u06d5 \u0648 \u0628\u0627\u06b5 \u0644\u06ce\u06a9 \u062f\u06d5\u062f\u06d5\u0646\u061f \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u062e\u0648\u062f\u0627 \u06a9\u06ce \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u0695\u0627\u06af\u06cc\u0631\u06cc\u0627\u0646 \u06a9\u0627\u061f \u062e\u0648\u062f\u0627 \u0628\u06c6 \u062e\u06c6\u06cc \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0628\u06d5\u063a\u06d5\u06cc\u0631\u06cc \u0626\u06d5\u0648 \u06a9\u06ce\u06cc\u06d5 \u0633\u0648\u067e\u0627 \u0648 \u06cc\u0627\u0631\u06cc\u062f\u06d5\u062f\u06d5\u0631\u062a\u0627\u0646 \u0628\u06ce\u061f \u0646\u06d5\u062e\u06ce\u0631 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 [\u0628\u06d5\u062e\u06c6\u0628\u06cc\u0646\u06cc] \u062e\u06d5\u06b5\u06d5\u062a\u0627\u0648\u0646.',
    '\u0626\u06d5\u062f\u06cc \u06a9\u06ce\u06cc\u06d5 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0628\u0698\u06cc\u0648\u062a\u0627\u0646 \u062f\u06d5\u062f\u0627\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0698\u06cc\u0648\u06cc \u0644\u06ce \u0628\u0695\u06cc\u0646\u061f \u0646\u06d5\u062e\u06ce\u0631 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u062e\u06c6 \u0628\u06d5 \u0632\u0644 \u0632\u0627\u0646\u06cc\u0646 \u0648 \u0695\u06d5\u0648\u06cc\u0646\u06d5\u0648\u06d5 \u067e\u06ce\u062f\u0627\u06af\u0631\u0646.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u062f\u06d5\u0645\u06d5\u0648\u0695\u0648\u0648 \u0628\u06d5 \u0647\u06d5\u0631\u062f\u0627 \u062f\u06d5\u062e\u0634\u06ce \u0686\u0627\u06a9\u062a\u0631 \u0628\u06d5 \u0695\u06ce\u06af\u06d5 \u062f\u06d5\u0632\u0627\u0646\u06ce\u060c \u06cc\u0627\u0646 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0628\u06d5 \u0626\u0627\u0633\u0627\u06cc\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u06af\u06c6\u0634 \u0648 \u0686\u06d5\u0645 \u0648 \u062f\u06b5\u06cc \u062f\u0627\u0646\u06ce. \u0626\u06ce\u0648\u06d5 \u06a9\u06d5\u0645\u062a\u0631 \u0634\u0648\u06a9\u0631\u06ce \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u062f\u0627 \u067e\u06d5\u0631\u0698 \u0648 \u0628\u06b5\u0627\u0648 \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5\u061b \u0644\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0634 \u06a9\u06c6 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u062f\u06d5\u0634\u06b5\u06ce\u0646: \u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u0633\u062a\u0646\u060c \u0626\u06d5\u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06d5\u0648 \u06a9\u06d5\u06cc \u062f\u06ce\u062a\u06d5 \u062c\u06ce\u061f',
    '\u0628\u06ce\u0698\u06d5: \u062a\u06d5\u0646\u06cc\u0627 \u062e\u0648\u062f\u0627 \u0644\u06d5\u0645\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5. \u0645\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5\u0645 \u0644\u06d5\u0633\u06d5\u0631\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u0628\u06ce\u0698\u0645: \u062f\u06d5\u0628\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u062a\u0631\u0633\u0646.',
    '\u062c\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u0644\u06d5 \u0646\u0632\u06cc\u06a9\u06d5\u0648\u06d5 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u0646\u060c \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0686\u0695 \u0648 \u0686\u0627\u0648\u06cc\u0627\u0646 \u06af\u0631\u0698 \u062f\u06d5\u0628\u06ce \u0648 \u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0648\u0627 \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u0627\u0648\u0627\u062a\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06d5 \u0645\u0646 \u0628\u06ce\u0698\u0646 \u0626\u06d5\u06af\u06d5\u0631\u060c \u062e\u0648\u062f\u0627 \u0645\u0646 \u0648 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0644\u06d5\u06af\u06d5\u06b5 \u0645\u0646\u0646 \u0644\u06d5\u0646\u0627\u0648 \u0628\u06d5\u0631\u06ce\u060c \u06cc\u0627\u0646 \u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u06cc \u062e\u06c6\u06cc\u0645\u0627\u0646 \u062e\u0627\u060c \u062f\u06d5\u06cc \u0633\u0627 \u06a9\u06ce \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0644\u06d5 \u0626\u06ce\u0634 \u0648 \u0698\u0627\u0646 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u06ce\u061f',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0648\u06d5 \u062e\u0648\u0627\u06cc \u062f\u06b5\u0627\u0648\u0627\u061b \u0628\u0627\u0648\u06d5\u0695\u06cc\u0645\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u062e\u06c6\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5\u061b \u0626\u06d5\u0646\u06af\u06c6\u0634 \u0647\u06d5\u0631 \u0628\u06cc\u0646\u0627 \u0632\u0627\u0646\u06cc\u062a\u0627\u0646 \u06a9\u06ce \u0626\u0627\u0634\u06a9\u0631\u0627 \u06af\u0648\u0645\u0695\u0627 \u0628\u0648\u0648\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0628\u06d5 \u0645\u0646 \u0628\u06ce\u0698\u0646 \u0626\u06d5\u06af\u06d5\u0631 \u0626\u0627\u0648\u06d5\u06a9\u06d5\u062a\u0627\u0646 \u0695\u06c6 \u0686\u0648\u0648 \u0628\u06d5\u0646\u0627\u062e\u06cc \u0632\u06d5\u0648\u06cc\u0646\u0627\u060c \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06ce \u0626\u0627\u0648\u06cc \u0632\u06d5\u06b5\u0627\u06b5\u06cc \u0695\u06d5\u0648\u0627\u0646\u062a\u0627\u0646 \u062f\u06d5 \u0641\u0631\u06cc\u0627 \u062f\u06d5\u062e\u0627\u061f',
    '\u0646\u0648\u0646\u060c \u0633\u0648\u06ce\u0646\u062f \u0648\u06d5 \u0628\u0646\u0648\u0648\u0633\u060c \u0628\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u062f\u06ce\u062a\u06d5 \u0646\u06a4\u06cc\u0633\u06cc\u0646\u060c',
    '\u0648\u06d5\u0633\u0627\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u060c \u062a\u06c6 \u0644\u06ce\u0648\u06d5 \u0646\u06cc\u062a.',
    '\u062a\u06c6 \u067e\u0627\u062f\u0627\u0634\u06cc \u0648\u0627\u0634\u062a \u0647\u06d5\u06cc\u06d5 \u062f\u0648\u0627\u06cc\u06cc \u0646\u0627\u06cc\u06d5.',
    '\u0626\u0627\u06a9\u0627\u0631\u06cc\u0634\u062a \u0641\u0631\u06d5 \u0628\u06d5\u0631\u0632\u06d5.',
    '\u0632\u0648\u0648 \u062f\u06d5\u0628\u06cc\u0646\u06cc\u060c \u0626\u06d5\u0648\u06cc\u0634 \u0686\u0627\u0648\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u0641\u06ce\u060c',
    '\u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0634\u06ce\u062a\u0627\u06cc\u06d5\u062a\u06cc\u062a\u0627\u0646 \u062a\u06ce\u062f\u0627\u06cc\u06d5.',
    '\u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0686\u0627\u062a\u0631 \u0628\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06d5 \u0695\u0627\u06cc \u0626\u06d5\u0648\u06cc\u0627\u0646 \u06af\u0648\u0645 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u0628\u0627\u0634\u062a\u0631\u06cc\u0634\u06cc \u0626\u0627\u06af\u0627 \u0644\u06ce\u06cc\u06d5\u060c \u06a9\u06ce\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u0695\u06ce\u0646.',
    '\u0633\u0627 \u062a\u06c6 \u0646\u0627\u0634\u06ce \u0628\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0626\u06d5\u0648 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06d5 \u0628\u06a9\u06d5\u06cc.',
    '\u062d\u06d5\u0632 \u062f\u06d5\u06a9\u06d5\u0646 \u06af\u06d5\u0631 \u062a\u06c6 \u0646\u06d5\u0631\u0645 \u0628\u06cc\u060c \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0646\u06cc\u0627\u0646\u06cc \u0628\u0646\u0648\u06ce\u0646\u0646.',
    '\u0646\u0627\u0628\u06ce \u06af\u0648\u06ce\u0634 \u0628\u06d5\u0648 \u06a9\u06d5\u0633\u06d5 \u0628\u062f\u06d5\u06cc\u060c \u06a9\u06d5 \u0632\u06c6\u0631 \u0633\u06c6\u0646\u062f\u062e\u06c6\u0631\u06d5\u060c \u062e\u0648\u06ce\u0695\u06cc\u06cc\u06d5\u061b',
    '\u0647\u06d5\u0631 \u062a\u0627\u0646\u06d5 \u0644\u06d5 \u062e\u06d5\u06b5\u06a9 \u062f\u06d5\u062f\u0627 \u0648 \u0642\u0633\u06d5 \u0628\u06c6 \u0626\u06d5\u0645 \u0648 \u0626\u06d5\u0648 \u062f\u06d5\u0628\u0627\u061b',
    '\u0644\u06d5 \u06a9\u0627\u0631\u06cc \u0686\u0627\u06a9\u06d5 \u0644\u06d5\u0645\u067e\u06d5\u0631\u06d5\u061b \u0634\u06d5\u0695\u0641\u0631\u06c6\u0634\u06d5\u060c \u062a\u0627\u0648\u0627\u0646\u06a9\u06d5\u0631\u06d5\u061b',
    '\u0695\u06d5\u0632\u0627 \u0642\u0648\u0631\u0633\u06d5\u060c \u0644\u06d5\u067e\u0627\u0634 \u0626\u06d5\u0645\u06d5\u0634 \u0632\u06c6\u06b5\u06ce\u06a9\u06d5 \u062e\u06c6\u06cc \u0633\u06d5\u067e\u0627\u0646\u062f\u0648\u0648\u06d5\u061b',
    '\u0647\u06d5\u0631 \u0628\u06d5\u0648 \u0647\u06c6\u06cc\u06d5 \u06a9\u06d5 \u0632\u06d5\u0646\u06af\u06cc\u0646\u06d5 \u0648 \u0686\u06d5\u0646\u062f \u06a9\u0648\u0695\u06cc \u0647\u06d5\u0646\u060c',
    '\u06af\u06d5\u0631 \u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u0646\u060c \u0626\u06ce\u0698\u06ce: \u0626\u06d5\u0645\u06d5 \u0686\u06cc\u0631\u06c6\u06a9\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06d5.',
    '\u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u0633\u06d5\u0631\u06cc \u0628\u06ce\u06a4\u0644\u06cc \u062f\u0627\u063a \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0645\u0627\u0646 \u0626\u06d5\u0632\u0645\u0648\u0648\u060c \u0648\u06d5\u06a9 \u062e\u0627\u0648\u06d5\u0646\u0627\u0646\u06cc \u0628\u0627\u063a\u06d5\u06a9\u06d5\u0646 \u0628\u06d5\u0631 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646 \u062f\u0627\u060c \u06a9\u06d5 \u0633\u0648\u06ce\u0646\u062f\u06cc\u0627\u0646 \u062e\u0648\u06ce\u0646\u062f: \u0633\u06c6\u0632\u06cc \u0628\u06d5\u0631\u06cc \u0628\u0627\u062e\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0695\u0646\u0646.',
    '\u00ab\u0626\u06d5\u06af\u06d5\u0631 \u062e\u0648\u0627 \u062d\u06d5\u0632 \u06a9\u0627\u00bb \u0634\u06cc\u0627\u0646 \u0646\u06d5\u0648\u062a.',
    '\u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062e\u06d5\u0648\u062a\u0628\u0648\u0648\u0646\u060c \u0644\u06d5\u0644\u0627\u06cc\u06d5\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0695\u0627 \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u06cc \u0628\u06c6 \u062f\u0627\u0628\u0627\u0631\u06cc.',
    '\u0626\u06cc\u062a\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06ce\u06a9\u06cc \u0633\u0648\u0648\u062a\u0627\u0648\u06cc \u0642\u0627\u0642\u0695\u06cc \u0644\u06ce \u0647\u0627\u062a.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0633\u06c6\u0632\u06cc\u060c \u0647\u06d5\u0645\u0648\u0648 \u0628\u0627\u0646\u06af\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0627\u0646 \u06a9\u0631\u062f:',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0645\u06cc\u0648\u06d5\u0695\u0646\u0646 \u0647\u06d5\u0631 \u0628\u06d5\u06cc\u0627\u0646\u06cc \u0628\u06af\u06d5\u0646\u06d5 \u0633\u06d5\u0631 \u06a9\u06ce\u06b5\u06af\u06d5\u06cc \u062e\u06c6\u062a\u0627\u0646.',
    '\u0695\u06ce\u0648\u06d5 \u0686\u0648\u0648\u0646 \u0648 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0686\u067e\u06d5\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f:',
    '\u06a9\u06d5 \u0646\u0627\u0628\u06ce \u0626\u06d5\u0645\u0695\u06c6 \u0686 \u0647\u06d5\u0698\u0627\u0631 \u0628\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0628\u0627\u062e\u06d5\u06a9\u06d5\u06cc \u0626\u06ce\u0648\u06d5.',
    '\u0628\u0695\u06cc\u0627\u0631\u06cc\u0627\u0646 \u062f\u0627 \u06a9\u06d5 \u0645\u06d5\u0631\u062c\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u062c\u06ce \u0628\u06ce\u0646\u0646\u061b \u0648\u0627\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06cc \u06a9\u06d5 \u062f\u06d5\u062a\u0627\u0646\u0646.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0686\u0627\u0648\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0627 \u06af\u06ce\u0695\u0627\u060c \u0648\u062a\u06cc\u0627\u0646: \u0695\u06ce\u0645\u0627\u0646 \u0644\u06ce \u062a\u06ce\u06a9\u0686\u0648\u0648\u06d5.',
    '\u0646\u06d5\u062e\u06ce\u0631\u060c \u0626\u06ce\u0645\u06d5\u06cc\u0646 \u0628\u06d5\u0634 \u0628\u0695\u0627\u0648\u06cc\u0646.',
    '\u0628\u06d5\u0626\u0627\u0648\u06d5\u0632\u062a\u0631\u06cc\u0646\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u062f\u06cc \u067e\u06ce\u0645 \u0646\u06d5\u0648\u062a\u0646 \u0628\u06c6 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc \u062e\u0648\u0627 \u0646\u0627\u062f\u06d5\u0646!\u061f',
    '\u06af\u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u062f\u0648\u0648\u0631 \u0644\u06d5 \u06a9\u06d5\u0645\u0627\u06cc\u06d5\u0633\u06cc \u062f\u06d5\u0632\u0627\u0646\u06cc\u0646\u061b \u0626\u06ce\u0645\u06d5 \u0646\u0627\u0647\u06d5\u0642\u06cc\u0645\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0695\u0648\u0648\u06cc\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u06cc\u06d5\u06a9 \u0648 \u0633\u06d5\u0631\u06a9\u06c6\u0646\u06d5\u06cc \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u0648\u062a\u06cc\u0627\u0646: \u0647\u06d5\u06cc \u0645\u0627\u06b5 \u0648\u06ce\u0631\u0627\u0646 \u062e\u06c6\u0645\u0627\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u06b5\u06d5\u0695\u06c6\u06cc\u06cc\u0645\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0634\u0627\u06cc\u06d5\u062a\u0627 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645\u0627\u0646 \u0644\u06d5\u0648\u06d5 \u0686\u0627\u062a\u0631\u0645\u0627\u0646 \u0628\u062f\u0627\u062a\u06ce\u061b \u0626\u0648\u0645\u06ce\u062f\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645\u0627\u0646\u06d5.',
    '\u0626\u0627\u0632\u0627\u0631\u06cc [\u062f\u0646\u06cc\u0627] \u0626\u0627\u0648\u0627\u06cc\u06d5. \u06af\u06d5\u0631 \u0628\u0632\u0627\u0646\u0646\u060c \u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u0648\u0627\u0695\u06c6\u0698 \u062f\u0698\u0648\u0627\u0631\u062a\u0631\u06d5.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0646\u060c \u0644\u062c\u06d5\u0645 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u0627\u063a\u0627\u062a\u06cc \u067e\u0695 \u062e\u06c6\u0634\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646 \u0648\u06d5\u06a9\u0648\u0648 \u0628\u06d5\u062f\u06a9\u0627\u0631\u0627\u0646 \u062f\u0627\u062f\u06d5\u0646\u06ce\u06cc\u0646\u061f',
    '\u0686\u06cc\u062a\u0627\u0646\u06d5\u061f \u06a9\u0648\u0648 \u067e\u06ce \u0695\u0627\u062f\u06d5\u06af\u06d5\u0646\u061f',
    '\u0626\u0627\u062e\u06c6 \u06a9\u062a\u06ce\u0628\u06ce\u06a9\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u062f\u06d5\u06cc\u062e\u0648\u06ce\u0646\u0646\u06d5\u0648\u06d5\u061f',
    '\u0647\u06d5\u0631\u0686\u06cc \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u062a\u0627\u0646\u06d5\u0648\u06ce \u062a\u06ce\u06cc\u062f\u0627 \u0647\u06d5\u06cc\u06d5\u061f',
    '\u06cc\u0627\u0646 \u0633\u0648\u06ce\u0646\u062f\u06ce\u06a9\u062a\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u0647\u06d5\u062a\u0627 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u06d5 \u0647\u06d5\u0631\u0686\u06cc\u062a\u0627\u0646 \u062f\u06d5\u0648\u06ce \u0626\u06d5\u0648 \u0628\u06ce\u061f',
    '\u0644\u06ce\u06cc\u0627\u0646 \u067e\u0631\u0633\u06d5: \u06a9\u06ce \u062f\u06d5\u0633\u062a\u06d5\u0628\u06d5\u0631\u06cc \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0633\u061f',
    '\u06cc\u0627\u0646 \u0686\u06d5\u0646\u062f \u0634\u06d5\u0631\u06cc\u06a9\u06ce\u06a9\u06cc\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u061f \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u062a\u0646\u060c \u0628\u0627 \u0634\u06d5\u0631\u06cc\u06a9\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0628\u06ce\u0646\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce - \u06a9\u06d5 \u0644\u06d5 \u062a\u0627\u0648\u0627\u0646 - \u062f\u06d5\u0631\u067e\u06ce \u062f\u06d5 \u0644\u06cc\u0646\u06af\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u0646 \u0648 \u067e\u06ce\u06cc\u0627\u0646 \u062f\u06d5\u06b5\u06ce\u0646 \u0633\u0648\u0698\u062f\u06d5 \u0628\u0628\u06d5\u0646 \u0648 \u0646\u0627\u062a\u0648\u0627\u0646\u0646.',
    '\u0686\u0627\u0648 \u062f\u0627\u062f\u06d5\u062e\u06d5\u0646 \u0648 \u0632\u06d5\u0628\u0648\u0648\u0646\u06cc \u062f\u0627\u06cc\u0627\u0646\u062f\u06d5\u06af\u0631\u06ce\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u0633\u0627\u0642 \u0648\u0686\u0627\u0642\u06cc\u0634 \u0628\u0648\u0648\u0646\u060c \u067e\u06ce\u06cc\u0627\u0646 \u0648\u062a\u0648\u0648\u0646 \u0633\u0648\u0698\u062f\u06d5 \u0628\u06d5\u0631\u0646.',
    '\u062a\u06c6 \u0645\u0646 \u0628\u06ce\u06b5\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5 \u0628\u0627\u0648\u06d5\u0695 \u0646\u0627\u06a9\u06d5\u0646\u061b \u06a9\u06d5\u0645 \u06a9\u06d5\u0645 \u0628\u06c6\u06cc\u0627\u0646 \u0644\u06d5\u062f\u0648\u0648 \u062f\u06d5\u062f\u06d5\u0645\u060c \u06a9\u06d5 \u0647\u06d5\u0631 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u062d\u06d5\u0633\u06ce\u0646.',
    '\u0645\u06d5\u0648\u062f\u0627 \u0648 \u0645\u0627\u0648\u06d5\u0634\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u06d5\u0645\u061b \u0641\u06ce\u06b5\u0645 \u0632\u06c6\u0631 \u0628\u06d5\u0695\u06ce\u0648\u062c\u06ce\u06cc\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u062a\u06c6 \u06a9\u0631\u0627\u06cc\u06d5\u06cc \u06a9\u0627\u0631\u062a \u0644\u06d5\u0648\u0627\u0646 \u062f\u06d5\u0648\u06ce\u060c \u06a9\u06d5 \u0644\u06d5\u0698\u06ce\u0631 \u0628\u0627\u0631\u06cc \u062f\u06d5\u0631\u0646\u06d5\u0686\u0646\u061f',
    '\u06cc\u0627\u0646 \u062a\u06d5\u062a\u06d5\u06b5\u06d5\u06cc \u0646\u0647\u06ce\u0646\u06cc\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5 \u0644\u0627\u06cc\u06d5 \u0648 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u062e\u06d5\u062a\u06cc \u0644\u06ce \u062f\u06d5\u062f\u06d5\u0646\u061f',
    '\u062f\u06d5\u0633\u0627 \u062a\u06c6 \u0647\u06d5\u0631 \u0628\u06c6 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06d5\u061b \u0646\u06d5\u0686\u06cc \u0648\u06d5\u06a9 \u06cc\u0627\u0631\u06c6\u06cc \u0645\u0627\u0633\u06cc \u0628\u06cc\u060c \u06a9\u06d5 \u0644\u06d5 \u062f\u0627\u062e\u0627\u0646\u0627 \u0647\u06d5\u0631 \u067e\u06cc\u0634\u06cc \u062f\u06d5\u062e\u0648\u0627\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u062f\u06d5\u067e\u0627\u0695\u0627\u06cc\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0631 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0648\u06d5 \u0641\u0631\u06cc\u0627\u06cc \u0646\u06d5\u06a9\u06d5\u0648\u062a\u0627\u06cc\u06d5\u060c \u0628\u06ce \u0633\u06c6 \u0628\u06d5 \u0633\u06d5\u0631\u06a9\u06c6\u0646\u06d5\u06a9\u0631\u0627\u0648\u06cc \u0641\u0695\u06ce \u0626\u06d5\u062f\u0631\u0627\u06cc\u06d5 \u0695\u06c6\u062a\u06d5\u0646\u06ce\u06a9.',
    '\u0626\u06d5\u0648\u0633\u0627 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u060c \u0626\u06d5\u0648\u06cc \u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f \u0648 \u06a9\u0631\u062f\u06cc \u0628\u06d5 \u06cc\u06d5\u06a9 \u0644\u06d5 \u067e\u06cc\u0627\u0648\u062e\u0627\u0633\u0627\u0646.',
    '\u0648\u06d5\u062e\u062a\u06ce \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0642\u0648\u0631\u0639\u0627\u0646\u06cc\u0627\u0646 \u0628\u06cc\u0633\u060c \u0647\u06d5\u0631 \u0648\u06d5\u062e\u062a\u0627 \u0628\u0648\u0648 \u0628\u06d5 \u0686\u0627\u0648\u0627\u0646\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062a\u062f\u06ce\u0631\u0646. \u062f\u06d5\u0634\u06cc\u0627\u0646\u06af\u0648\u062a: \u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u062f\u06cc\u0646\u06d5.',
    '\u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc\u06cc\u06d5\u06a9\u06cc\u0634\u06d5 \u0628\u06c6 \u0647\u06d5\u0645\u0648\u0648 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627\u06cc\u06d5.',
    '\u06a9\u0627\u0631\u06d5\u0633\u0627\u062a\u06d5\u06a9\u06d5 \u0695\u0648\u0648 \u062f\u06d5\u062f\u0627.',
    '\u06a9\u0627\u0645 \u06a9\u0627\u0631\u06d5\u0633\u0627\u062a\u061f',
    '\u062a\u06c6 \u0686\u0648\u0632\u0627\u0646\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u0633\u0627\u062a\u06d5 \u0686\u0644\u06c6\u0646\u06d5\u061f',
    '\u0633\u06d5\u0645\u0648\u0648\u062f \u0648 \u0639\u0627\u062f \u0695\u0648\u0648\u062f\u0627\u0648\u06d5 \u062f\u06b5\u06a9\u0648\u062a\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627.',
    '\u0647\u06c6\u0632\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f \u0628\u06d5 \u0646\u06d5\u0639\u0631\u06d5\u062a\u06ce\u06a9\u06cc \u0628\u06ce \u0648\u06ce\u0646\u06d5\u060c \u0642\u0695\u0627\u0646\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc \u0639\u0627\u062f\u06cc\u0634 \u0628\u0648\u0648\u060c \u0628\u06d5 \u0628\u0627\u06cc\u06d5\u06a9\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u06d5\u0647\u06ce\u0632 \u0644\u06d5\u0628\u06d5\u06cc\u0646 \u0686\u0648\u0648\u0646.',
    '\u0645\u0627\u0648\u06d5\u06cc \u062d\u06d5\u0648 \u0634\u06d5\u0648 \u0648 \u0647\u06d5\u0634 \u0695\u06c6\u0698\u0627\u0646\u060c \u0628\u06d5\u0628\u06ce \u0648\u0686\u0627\u0646\u060c \u0646\u0627\u0631\u062f\u06cc\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646. \u0644\u06d5\u0645 \u0645\u0627\u0648\u06d5\u062f\u0627 \u0644\u0627\u0634\u06d5\u06cc\u0627\u0646 \u0644\u06ce \u062a\u062e\u06ce\u06b5 \u0628\u0628\u0648\u0648\u0646\u061b \u062f\u06d5\u062a\u0648\u062a \u06a9\u06c6\u062a\u06d5\u0631\u06d5\u06cc \u062f\u0627\u0631\u062e\u0648\u0631\u0645\u0627\u06cc \u0646\u0627\u0648 \u0632\u0695\u0627\u0648\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u062a\u06c6 \u067e\u0627\u0634\u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06cc\u0646\u06cc\u061f',
    '\u0641\u06cc\u0695\u0639\u06d5\u0648\u0646\u06cc\u0634 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06cc\u0634 \u0628\u0648\u0648\u0646 \u0648 \u0645\u06d5\u0631\u062f\u0645\u06cc \u0634\u0627\u0631\u06d5 \u0698\u06ce\u0631\u0648\u0695\u0648\u0648 \u06a9\u0631\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0634\u060c \u062e\u06d5\u062a\u0627\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u062f\u0698\u0627\u06cc\u06d5\u062a\u06cc\u06cc \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f. \u0633\u0627 \u06af\u0631\u062a\u06cc\u0627\u0646\u06cc\u060c \u06af\u0631\u062a\u0646\u06ce \u0644\u06d5 \u0695\u0627\u062f\u06d5\u0628\u06d5\u062f\u06d5\u0631.',
    '\u0626\u06ce\u0645\u06d5 - \u06a9\u0627\u062a\u06ce \u0644\u06d5\u0647\u06cc \u0647\u0627\u062a \u0648 \u0628\u0648\u0627\u0631\u06cc \u0628\u0695\u06cc\u060c \u0644\u06d5\u0648 \u0634\u062a\u06d5\u062f\u0627 \u06a9\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u0626\u0627\u0648\u062f\u0627 \u062f\u0627\u062f\u06d5\u06af\u06d5\u0695\u06ce - \u0647\u06d5\u06b5\u0645\u0627\u0646\u06af\u0631\u062a\u0646.',
    '\u062a\u0627 \u0628\u06cc\u06a9\u06d5\u06cc\u0646\u06d5 \u0628\u06cc\u0631\u0647\u06ce\u0646\u06d5\u0631\u06d5\u0648\u06d5 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u0648 \u0626\u06d5\u0648 \u06af\u0648\u06ce\u06cc\u0627\u0646\u06d5\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0647\u06d5\u0633\u062a\u0646\u060c \u0648\u06d5\u0631\u06cc\u0628\u06af\u0631\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631\u06af\u0627 \u062a\u06d5\u0646\u06cc\u0627 \u062c\u0627\u0631\u06ce\u06a9 \u062f\u06d5 \u06a9\u06d5\u06b5\u06d5\u0634\u0627\u062e \u062f\u06d5\u0645\u06ce\u0646\u062f\u0631\u0627\u060c',
    '\u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u06a9\u06d5\u0698\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0647\u06d5\u06b5\u06af\u06cc\u0631\u0627\u0646 \u0648 \u06cc\u06d5\u06a9\u062c\u0627\u0631\u06d5\u06a9\u06cc \u062a\u06ce\u06a9 \u062f\u06d5\u062a\u06d5\u067e\u06ce\u0646\u060c',
    '\u0633\u0627 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u06d5\u06a9\u06d5 \u0695\u0648\u0648 \u062f\u06d5\u062f\u0627.',
    '\u0626\u06d5\u0645 \u0639\u0627\u0633\u0645\u0627\u0646\u0627\u0646\u06d5 \u0642\u06d5\u06b5\u0634 \u062f\u06d5\u0628\u0627 \u0648 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0641\u0634\u06d5\u06b5\u06d5\u06cc\u06d5.',
    '\u0641\u0631\u0634\u062a\u06d5 \u062f\u06d5\u0686\u0646\u06d5 \u06a9\u06d5\u0646\u0627\u0631\u0627\u0646 \u0648 \u062a\u06d5\u062e\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u060c \u0644\u06d5\u0633\u06d5\u0631 \u0633\u06d5\u0631\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u0648\u06d5 - \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 - \u0647\u06d5\u0634\u062a \u0641\u0631\u0634\u062a\u06d5 \u0647\u06d5\u06b5\u06cc\u062f\u06d5\u06af\u0631\u0646.',
    '\u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0626\u06ce\u0648\u06d5 \u0695\u0627\u062f\u06d5\u0646\u06ce\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0628\u06c6 \u0634\u0627\u0631\u062f\u0646\u06d5\u0648\u06d5\u06cc\u06d5 \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062d\u06d5\u0634\u0627\u0631 \u0646\u0627\u062f\u0631\u06ce.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631\u06a9\u06ce \u0646\u0627\u0645\u06d5\u06cc \u06a9\u0631\u062f\u06d5\u06cc \u0646\u0631\u0627\u0628\u06ce\u062a\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0695\u0627\u0633\u062a\u06cc\u060c \u0626\u06ce\u0698\u06ce: \u0648\u06d5\u0631\u0646 \u0646\u0627\u0645\u06d5\u06a9\u06d5\u06cc \u0645\u0646 \u0628\u062e\u0648\u06ce\u0646\u0646\u06d5\u0648\u06d5.',
    '\u0645\u0646 \u0633\u0648\u0648\u0631 \u062f\u06d5\u0645\u0632\u0627\u0646\u06cc \u06a9\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0644\u06ce\u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5 \u0647\u06d5\u0631 \u062f\u06d5\u0628\u0645.',
    '\u0626\u06cc\u062a\u0631 \u0626\u06d5\u0648 \u0644\u06d5\u0646\u0627\u0648 \u0698\u06cc\u0627\u0646\u06ce\u06a9\u06cc \u062e\u06c6\u0634\u062f\u0627\u06cc\u06d5\u061b',
    '\u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06ce\u06a9\u06cc \u0628\u06d5\u0631\u0632\u0627\u06cc\u06d5\u061b',
    '\u0645\u06cc\u0648\u06d5\u06cc \u0648\u0627 \u0644\u06d5\u0628\u06d5\u0631 \u062f\u06d5\u0633\u062a\u0627\u06cc\u06d5.',
    '\u0633\u0627 \u0628\u062e\u06c6\u0646 \u0648 \u0628\u062e\u06c6\u0646\u06d5\u0648\u06d5 \u0646\u06c6\u0634\u06cc \u06af\u06cc\u0627\u0646\u0648\u0648\u061b \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u0627\u0646\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u0627\u0646\u06cc \u0695\u0627\u0628\u0631\u062f\u0648\u0648 \u06a9\u0631\u062f\u0648\u0648\u062a\u0627\u0646\u06d5.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0646\u0627\u0645\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u0646\u06ce\u062a\u06d5 \u0644\u06d5\u067e\u06cc \u0686\u06d5\u067e\u06cc\u06d5\u0648\u06d5\u060c \u0626\u06ce\u0698\u06ce: \u062e\u06c6\u0632\u06cc \u0646\u0627\u0645\u06d5\u06cc \u062e\u06c6\u0645\u0645 \u067e\u06ce \u0646\u06d5\u062f\u0631\u0627\u06cc\u06d5.',
    '\u0646\u06d5\u0634\u0645\u0632\u0627\u0646\u06cc\u0628\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u0645 \u0686\u06cc\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5.',
    '\u062e\u06c6\u0632\u06cc \u0626\u06d5\u0648\u0633\u0627 \u0645\u0631\u062f\u0646\u0645 \u06cc\u06d5\u06a9\u062c\u0627\u0631\u06cc \u0628\u0648\u0627\u06cc\u06d5.',
    '\u0633\u0627\u0645\u0627\u0646\u0645 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0628\u06c6\u0645 \u0646\u06d5\u0628\u0648\u0648.',
    '\u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u0645 \u0648\u0627 \u0644\u06d5\u0628\u06d5\u06cc\u0646 \u0686\u0648.',
    '\u0633\u0627 \u0628\u06cc\u06af\u0631\u0646 \u0648 \u0632\u0646\u062c\u06cc\u0631\u06cc \u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0628\u06c6 \u062f\u06c6\u0698\u06d5\u0647\u06cc \u0628\u0628\u06d5\u0646.',
    '\u067e\u0627\u0634\u0627\u0646 \u0644\u06d5\u0646\u0627\u0648 \u0632\u0646\u062c\u06cc\u0631\u06ce\u06a9\u06cc \u062d\u06d5\u0641\u062a\u06ce \u06af\u06d5\u0632\u06cc \u0634\u06d5\u062a\u06d5\u06a9 \u062f\u06d5\u0646 \u0648 \u0695\u06d5\u06a9\u06ce\u0634\u06cc \u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0628\u06d5 \u062e\u0648\u062f\u0627\u06cc \u0645\u06d5\u0632\u0646 \u0646\u06d5\u062f\u06ce\u0646\u0627.',
    '\u0628\u06c6 \u0646\u0627\u0646 \u062f\u0627\u0646 \u0628\u06d5 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646\u06cc\u0634 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc \u062f\u0646\u06d5 \u0646\u06d5\u062f\u06d5\u062f\u0627.',
    '\u062c\u0627 \u0626\u06d5\u0645\u0695\u06c6 \u0644\u06ce\u0631\u0648\u06a9\u0627\u0646\u06d5\u062f\u0627\u060c \u0686 \u062f\u06c6\u0633\u062a\u06cc \u0646\u06cc\u0646.',
    '\u062e\u0648\u0627\u0631\u062f\u0646 \u0646\u06cc\u06cc\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06ce\u0645\u0627\u0648\u06cc \u0632\u0627\u0645\u062f\u0627\u0631\u0627\u0646.',
    '\u0646\u0627\u06cc\u062e\u06c6\u0646\u060c \u0645\u06d5\u06af\u06cc\u0646 \u062e\u06d5\u062a\u0627\u06a9\u0627\u0631\u0627\u0646.',
    '\u0633\u0627 \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5 \u0628\u06d5\u0648 \u0634\u062a\u06d5\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u0646\u060c',
    '\u0628\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u06cc\u0628\u06cc\u0646\u0646\u060c',
    '\u0626\u06d5\u0648\u06d5 \u0642\u0633\u06d5\u06cc \u0695\u0627\u0633\u067e\u06ce\u0631\u0627\u0648\u06ce \u0628\u06d5\u0631\u06a9\u06d5\u062a\u06cc\u06cc\u06d5.',
    '\u0646\u06d5 \u0648\u062a\u06d5\u06cc \u0647\u06d5\u0633\u062a\u06cc\u0627\u0631\u06cc\u0634\u06d5\u061b \u06a9\u06d5\u0645\u062a\u0631 \u0628\u0627\u0648\u06d5\u0695\u06ce \u062f\u06d5\u0647\u06ce\u0646\u0646.',
    '\u0648\u062a\u06d5\u06cc \u0641\u0627\u06b5\u06af\u0631\u06d5\u0648\u06d5\u0634 \u0646\u06cc\u06cc\u06d5. \u06a9\u06d5\u0645 \u0648\u0627\u06cc\u06d5 \u0628\u06cc\u0631 \u0628\u06a9\u06d5\u0646\u06d5\u0648\u06d5.',
    '\u0646\u0627\u0631\u062f\u0631\u0627\u0648\u06d5\u062a\u06d5 \u062e\u0648\u0627\u0631\u06d5\u0648\u06d5\u060c \u0644\u06d5 \u0648\u06d5\u062f\u06cc\u0647\u06ce\u0646\u06cc \u06af\u0634 \u062c\u06cc\u0647\u0627\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0647\u06ce\u0646\u062f\u06ce \u0644\u06d5 \u0642\u0633\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06d5 \u062f\u06d5\u0645 \u0626\u06ce\u0648\u06d5\u0648\u06d5 \u06a9\u0631\u062f\u0628\u0627\u060c',
    '\u06af\u0648\u0645\u0627\u0646 \u0646\u06cc\u06d5\u060c \u0628\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u062a\u0648\u0627\u0646\u0627 \u062f\u06d5\u0645\u0627\u0646\u06af\u0631\u062a.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0695\u06d5\u06af\u06cc \u0644\u0627\u0645\u0644\u06cc\u0634\u06cc\u0645\u0627\u0646 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u0695\u06cc.',
    '\u06a9\u06d5\u0633\u06cc\u0634 \u0644\u06d5\u0646\u06af\u06c6 \u0646\u06d5\u06cc\u062f\u06d5\u062a\u0648\u0627\u0646\u06cc \u0644\u06d5\u0645\u067e\u06d5\u0631\u06cc \u0628\u06ce.',
    '\u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc\u06cc\u06d5 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u06c6\u067e\u0627\u0631\u06ce\u0632\u0646.',
    '\u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u0626\u06ce\u0645\u06d5 \u062f\u06d5\u0634\u0632\u0627\u0646\u06cc\u0646 \u062a\u06ce\u062a\u0627\u0646\u062f\u0627 \u0647\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0646\u06d5\u06a9\u06d5\u0646.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0645\u06d5 \u062d\u06d5\u0633\u0631\u06d5\u062a\u06cc\u0634\u06d5 \u0628\u06c6 \u0626\u06d5\u0645 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06d5.',
    '\u06a9\u06d5 \u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0695\u0627\u0633\u062a\u06ce\u06a9\u06d5 \u0647\u06cc\u0686 \u06af\u0648\u0645\u0627\u0646\u06ce\u06a9\u06cc \u062a\u06ce\u062f\u0627 \u0646\u06cc\u06cc\u06d5.',
    '\u062a\u06c6 \u0628\u06d5 \u06cc\u0627\u062f\u06cc \u0646\u0627\u0648\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u06d5\u0648\u0631\u06d5\u062a\u060c \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc\u06cc \u0626\u06d5\u0648 \u0628\u062f\u06d5.',
    '\u06cc\u06d5\u06a9 \u062f\u0627\u0648\u0627\u06cc \u06a9\u0631\u062f \u0626\u0627\u0632\u0627\u0631\u06ce \u0628\u06ce\u060c',
    '\u0628\u06c6 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06d5\u062f\u0648\u0648\u0631\u06d5\u06a9\u0627\u0646\u060c \u0647\u06cc\u0686 \u0644\u06d5\u0645\u067e\u06d5\u0631\u06cc \u0646\u06d5\u06cc\u06d5\u062a\u06d5 \u0628\u06d5\u0631.',
    '\u0644\u06d5 \u062e\u0648\u0627\u06cc \u062e\u06ce\u0648\u06cc \u0686\u06d5\u0646\u062f \u067e\u0644\u06d5\u0648\u06d5.',
    '\u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646 \u0648 \u062c\u0648\u0628\u0695\u0627\u0626\u06cc\u0644 \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u0627 \u0628\u06d5\u0631\u06d5\u0648 \u0626\u06d5\u0648 \u067e\u06ce \u0647\u06d5\u06b5\u0627 \u062f\u06d5\u0686\u0646\u060c \u06a9\u06d5 \u0695\u06ce\u06cc \u067e\u06d5\u0646\u062c\u0627 \u0647\u06d5\u0632\u0627\u0631 \u0633\u0627\u06b5\u06d5.',
    '\u062a\u06c6 \u062e\u06c6 \u0628\u06af\u0631\u06d5 \u0628\u06d5\u062e\u06c6\u06af\u0631\u062a\u0646\u06ce\u06a9\u06cc \u067e\u06d5\u0633\u0646\u062f.',
    '\u0626\u06d5\u0648\u0627\u0646 \u0628\u06d5 \u062f\u0648\u0648\u0631\u06cc \u062f\u06d5\u0628\u06cc\u0646\u0646.',
    '\u0626\u06ce\u0645\u06d5\u0634 \u0646\u0632\u06cc\u06a9\u06cc \u062f\u06d5\u0628\u06cc\u0646\u06cc\u0646.',
    '\u0695\u06c6\u0698\u06ce \u0639\u0627\u0633\u0645\u0627\u0646 \u0648\u06d5\u06a9 \u062d\u06d5\u0644\u0627\u0646\u062f\u06cc \u06a9\u0627\u0646\u0632\u0627 \u062f\u06d5\u0628\u06ce\u061b',
    '\u06a9\u06d5\u0698\u0627\u0646\u06cc\u0634 \u0648\u06d5\u06a9 \u0647\u0631\u06cc \u062f\u06d5\u0628\u0646\u061b',
    '\u0626\u0627\u0648\u0627\u06b5 \u0644\u06d5 \u0626\u0627\u0648\u0627\u06b5 \u0646\u0627\u067e\u0631\u0633\u06ce\u061b',
    '\u0644\u06d5\u0628\u06d5\u0631 \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0627\u0646 \u0695\u0627\u062f\u06d5\u0646\u06ce\u0646\u061b \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631 \u0641\u0631\u06d5 \u062d\u06d5\u0632 \u062f\u06d5\u06a9\u0627 - \u0628\u06c6 \u0695\u0632\u06af\u0627\u0631\u06cc \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce - \u0644\u06d5\u0628\u0627\u062a\u06cc \u062e\u06c6\u06cc\u060c \u06a9\u0648\u0695\u06d5\u06a9\u0627\u0646\u06cc\u060c',
    '\u0647\u0627\u0648\u0633\u06d5\u0631\u06cc\u0634\u06cc\u060c \u0628\u0631\u0627\u06a9\u06d5\u0634\u06cc\u060c',
    '\u0626\u06d5\u0648 \u0628\u06d5\u0634\u06d5 \u0644\u06d5 \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u0634\u06cc - \u06a9\u06d5 \u0626\u06d5\u0648 \u062f\u06d5 \u067e\u06d5\u0646\u0627\u06cc \u062e\u06c6 \u062f\u06d5\u06af\u0631\u0646',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0632\u06d5\u0648\u06cc\u0634 \u0647\u06d5\u06cc\u06d5\u060c \u06af\u0634\u062a\u06cc \u0628\u062f\u0627 \u0648 \u0695\u0632\u06af\u0627\u0631 \u0628\u0628\u06ce.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u0628\u06ce\u060c \u0626\u06d5\u0645\u06d5 \u0628\u06b5\u06ce\u0633\u06d5\u06cc \u0626\u0627\u06af\u0631\u06d5.',
    '\u067e\u06ce\u0633\u062a\u06cc \u0633\u06d5\u0631 \u0626\u0627\u0632\u0627\u06cc \u0626\u06d5\u0646\u062f\u0627\u0645\u06cc \u062f\u0627\u062f\u06d5\u0695\u0646\u06ce.',
    '\u0628\u0627\u0646\u06af\u06ce\u0634\u062a\u0646\u06cc \u06a9\u06d5\u0633\u06ce \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u067e\u0634\u062a\u06cc \u06a9\u0631\u062f\u06d5 \u062f\u06cc\u0646\u06d5\u0648\u06d5 \u0648 \u0626\u0627\u0648\u0695\u06cc \u0648\u06ce \u0646\u06d5\u062f\u0627\u06cc\u06d5\u0648\u06d5\u061b',
    '\u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u06a9\u06c6\u06cc \u062f\u06d5\u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u06cc\u062e\u0633\u062a\u06d5 \u0646\u0627\u0648 \u0633\u0646\u0648\u0648\u0642\u06d5\u0648\u06d5.',
    '\u0628\u06ce \u0633\u06c6 \u0645\u0631\u06c6 \u0628\u06ce \u062a\u0627\u0628\u0634\u062a \u0648 \u0632\u06c6\u0631 \u0686\u0627\u0648\u0686\u0646\u06c6\u06a9\u060c \u0686\u06ce \u06a9\u0631\u0627\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u062a\u0648\u0648\u0634\u06cc \u0646\u0627\u06af\u0648\u0632\u0648\u0648\u0631\u06cc \u0628\u0648\u0648\u060c \u0647\u0627\u0648\u0627\u0631 \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u062a\u06d5 \u062e\u06c6\u0634\u06cc\u0634\u060c \u0686\u06a9\u0648\u0648\u0633\u06ce\u06a9\u06d5.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0646\u06c6\u0698\u062e\u06c6\u0646\u0646\u061b',
    '\u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0642\u06d5\u062f \u0646\u0627\u06cc\u06d5\u06b5\u0646 \u0646\u0648\u06ce\u0698\u06cc\u0627\u0646 \u0628\u0686\u06ce\u061b',
    '\u0626\u06d5\u0648\u06cc \u0644\u06d5 \u0633\u0627\u0645\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646\u062f\u0627 \u0628\u06d5\u0634\u06ce\u06a9 \u0647\u06d5\u06cc\u06d5\u060c',
    '\u0628\u06c6 \u067e\u0627\u0631\u0633\u06d5\u06a9 \u0648 \u062f\u0648\u0648\u0631\u06d5\u0628\u06d5\u0634\u0627\u0646\u061b',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5 \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc\u0627\u0646 \u0628\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0647\u06d5\u06cc\u06d5\u061b',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0646\u06cc\u06af\u06d5\u0631\u0627\u0646\u0646\u061b',
    '\u06a9\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646\u060c \u0628\u06c6 \u0626\u0627\u0631\u062e\u06d5\u06cc\u0627\u0646 \u0628\u0648\u0648\u0646\u06ce \u0646\u0627\u0628\u06ce.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u062c\u06ce \u0634\u06d5\u0631\u0645\u06cc\u0627\u0646 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5 \u0695\u0648\u0648\u06cc \u0698\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u06cc\u0627\u0646 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5\u0646\u06cc\u0632\u06cc\u0627\u0646\u0646\u060c \u0626\u06d5\u0648\u0633\u0627 \u0633\u06d5\u0631\u06a9\u06c6\u0646\u06d5 \u0646\u0627\u06a9\u0631\u06ce\u0646.',
    '\u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5\u0645\u0627\u0646\u06d5 \u0628\u062e\u0648\u0627\u0632\u06ce\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5 \u0642\u0627\u0633 \u0644\u0627\u062f\u06d5\u0631\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0695\u0627\u06af\u0631\u06cc \u0626\u0627\u0645\u0627\u0646\u06d5\u062a\u06cc \u0648 \u0628\u06d5\u06b5\u06ce\u0646\u06cc\u0627\u0646\u0646\u061b',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u0634\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06d5\u0695\u0627\u0633\u062a\u06cc \u062f\u06d5\u062f\u06d5\u0646\u061b',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5\u0633\u06d5\u0631 \u0646\u0645\u06ce\u0698\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u0646\u061b',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0644\u06d5\u0646\u0627\u0648 \u0628\u0627\u062e\u0627\u0646\u062f\u0627 \u0628\u06d5 \u0695\u06ce\u0632\u06d5\u0648\u06d5 \u0648\u06d5\u0631\u06af\u06cc\u0631\u0627\u0648\u0646.',
    '\u062f\u06d5\u06cc \u0626\u06d5\u0648 \u0628\u06ce\u062f\u06cc\u0646\u0627\u0646\u06d5 \u0686\u06cc\u0627\u0646\u06d5\u061f \u062f\u06ce\u0646\u06d5 \u0644\u0627\u062a \u0648 \u0628\u06d5 \u0645\u06c6\u0646\u06ce\u06a9\u06d5\u0648\u06d5 \u062f\u06d5\u0695\u0648\u0627\u0646\u0646.',
    '\u062f\u06d5\u0633\u062a\u06d5 \u062f\u06d5\u0633\u062a\u06d5 \u0644\u06d5 \u0626\u0627\u0644\u06cc\u06cc \u0695\u0627\u0633\u062a \u0648\u0686\u06d5\u067e\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0647\u06d5\u0631 \u06cc\u06d5\u06a9\u06ce\u06a9 \u0644\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06d5\u062a\u06d5\u0645\u0627\u06cc\u06d5 \u06a9\u06d5 \u0628\u0686\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc \u0628\u06d5 \u062e\u06ce\u0631 \u0648 \u0628\u06ce\u0631\u061f',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u0634\u06ce\u061b \u0626\u06d5\u0648\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u06cc\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0644\u06d5 \u0686\u06cc\u0645\u0627\u0646 \u062f\u0631\u0648\u0633 \u06a9\u0631\u062f\u0648\u0648\u0646.',
    '\u0633\u0627 \u0633\u0648\u06ce\u0646\u062f \u0626\u06d5\u062e\u06c6\u0645 \u0628\u06d5 \u062f\u0627\u0646\u06d5\u0631\u06cc \u062e\u06c6\u0631\u0647\u06d5\u06b5\u0627\u062a \u0648 \u062e\u06c6\u0631\u0626\u0627\u0648\u0627\u06cc\u0627\u0646\u060c \u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0648\u0632\u06d5\u0645\u0627\u0646\u062f\u0627\u06cc\u06d5\u060c',
    '\u06a9\u06d5 \u0628\u06cc\u0627\u0646\u06af\u06c6\u0695\u06cc\u0646 \u0648 \u0686\u0627\u062a\u0631\u06cc\u0627\u0646 \u0628\u06ce\u0646\u06cc\u0646\u06d5 \u062c\u06ce\u061b \u06a9\u06d5\u0633 \u067e\u06ce\u0634\u06cc \u0626\u06ce\u0645\u06d5\u0634 \u0646\u0627\u06a9\u06d5\u0648\u06ce.',
    '\u0633\u0627 \u062a\u06c6 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627\u0632\u0628\u06ce\u0646\u06d5 \u0628\u0627 \u0647\u06d5\u0631 \u0686\u06d5\u0646\u06d5\u0686\u06d5\u0646 \u0628\u06a9\u06d5\u0646 \u0648 \u06af\u06d5\u067e\u06ce \u0628\u062f\u06d5\u0646\u060c \u062a\u0627 \u062a\u0648\u0648\u0634\u06cc \u0695\u06c6\u0698\u06cc \u062e\u06c6 \u062f\u06d5\u0628\u0646 \u06a9\u06d5 \u0648\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc\u0627\u0646 \u067e\u06ce \u0626\u06d5\u062f\u0631\u06ce.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0644\u06d5 \u06af\u06c6\u0695\u0633\u062a\u0627\u0646 \u062f\u06ce\u0646\u06d5 \u062f\u06d5\u0631\u06ce\u060c \u0647\u06ce\u0646\u062f \u0628\u06d5\u067e\u06d5\u0644\u06d5\u0646 \u0626\u06ce\u0698\u06cc \u0628\u06d5\u0631\u06d5\u0648 \u0628\u062a\u0627\u0646 \u062f\u06d5\u0686\u0646.',
    '\u0686\u0627\u0648\u0634\u06c6\u0695\u0646 \u0648 \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u062f\u06d5\u0648\u0631\u06cc \u062f\u0627\u0648\u0646\u061b \u0626\u06d5\u0645\u06d5 \u0626\u06cc\u062a\u0631 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0648\u0627 \u0628\u06d5\u06b5\u06ce\u0646\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u062f\u0631\u0627.',
    '\u0626\u06ce\u0645\u06d5 \u0646\u0648\u0648\u062d\u0646 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f\u06d5 \u0644\u0627\u06cc \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u06cc\u060c \u06a9\u06d5: \u0647\u06c6\u0632\u06d5\u06a9\u06d5\u062a \u0628\u062a\u0631\u0633\u06ce\u0646\u06d5 \u0628\u06d5\u0631 \u0644\u06d5\u0648\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646 \u0628\u0628\u0646.',
    '\u0648\u062a\u06cc: \u06af\u06d5\u0644\u06c6! \u0645\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06ce\u06a9\u06cc \u0626\u0627\u0634\u06a9\u0631\u0627\u0645 \u0648 [\u067e\u06ce\u0648 \u0626\u06ce\u0698\u0645]:',
    '\u062e\u0648\u0627\u067e\u06d5\u0631\u0633\u062a\u06cc \u0628\u06a9\u06d5\u0646 \u0648 \u0644\u06ce\u06cc \u0628\u062a\u0631\u0633\u0646 \u0648 \u0628\u06d5 \u06af\u0648\u06ce\u0645 \u0628\u06a9\u06d5\u0646.',
    '\u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646\u0648 \u062f\u06d5\u0628\u0648\u0631\u06ce \u0648 \u062f\u06d5\u062a\u0627\u0646\u0647\u06ce\u06b5\u06ce \u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06ce \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u061b \u0686\u0648\u0646 \u0647\u06d5\u0631\u06af\u0627 \u062e\u0648\u0627 \u0626\u0627\u06a9\u0627\u0645\u06cc \u0647\u0627\u062a\u060c \u0648\u06d5 \u062f\u0648\u0627 \u0646\u0627\u062e\u0631\u06ce\u060c \u0628\u0631\u06cc\u0627 \u0626\u06d5\u0648\u06d5\u062a\u0627\u0646 \u0632\u0627\u0646\u06cc\u0628\u0627.',
    '\u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0645\u0646! \u0628\u06d5 \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698 \u0628\u0627\u0646\u06af\u0645 \u06a9\u0631\u062f\u0646\u060c',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0628\u06d5 \u0628\u0627\u0646\u06af \u06a9\u0631\u062f\u0646\u06cc \u0645\u0646\u060c \u067e\u062a\u0631 \u0644\u06d5 \u067e\u06ce\u0634\u0648\u0648 \u0695\u06d5\u0648\u06d5\u06a9 \u0628\u0648\u0648\u0646.',
    '\u0647\u06d5\u0631\u0686\u06d5\u0646\u062f\u06cc \u0645\u0646 \u062a\u06ce\u0645 \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u060c \u06a9\u06d5 \u062a\u06c6 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u06cc\u0627\u0646 \u0628\u0628\u0648\u0648\u0631\u06cc\u060c \u06af\u06c6\u0634\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06d5 \u062a\u0628\u0644\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u0626\u0627\u062e\u0646\u06cc \u0648 \u062e\u06c6\u06cc\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5 \u062c\u0644\u06a9\u0627\u0646 \u067e\u06ce\u0686\u0627 \u0648 \u067e\u06ce\u06cc\u0627\u0646 \u062f\u0627\u06af\u0631\u062a \u0648 \u062e\u06c6\u06cc\u0627\u0646\u06cc\u0627\u0646 \u0632\u06c6\u0631 \u0628\u06d5 \u0632\u0644 \u0632\u0627\u0646\u06cc.',
    '\u0647\u06d5\u0645\u06cc\u0633\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0646\u06af\u06cc \u0628\u06d5\u0631\u0632 \u0628\u0627\u0646\u06af\u0645 \u06a9\u0631\u062f\u0646.',
    '\u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u0634\u062f\u0627\u060c \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0648 \u067e\u06d5\u0646\u0627\u0645\u06d5\u06a9\u06cc \u062a\u06ce\u0645 \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646.',
    '\u0647\u06d5\u0631 \u067e\u06ce\u0645 \u06af\u0648\u062a\u0646: \u062f\u0627\u0648\u0627\u06cc \u0628\u0648\u0631\u062f\u0646 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u0627\u0646 \u0628\u06a9\u06d5\u0646\u061b \u0626\u06d5\u0648 \u0628\u06ce\u06af\u0648\u0645\u0627\u0646 \u0632\u06c6\u0631 \u0644\u06ce\u0628\u0648\u0631\u06d5.',
    '\u062a\u0627 \u0644\u06d5 \u062d\u06d5\u0648\u0627\u0648\u06d5 \u0628\u0627\u0631\u0627\u0646\u06ce\u06a9 \u067e\u06d5\u06b5\u06d5\u062f\u06d5\u0631\u062a\u0627\u0646 \u0628\u06c6 \u0628\u0646\u06ce\u0631\u06ce.',
    '\u0628\u06d5 \u062f\u0627\u0631\u0627\u06cc\u06cc \u0648 \u0628\u06d5 \u06a9\u0648\u0695\u0627\u0646\u06cc\u0634 \u06cc\u0627\u0631\u06cc\u0648 \u0628\u062f\u0627 \u0648 \u0628\u0627\u063a\u0627\u062a\u0648\u0648 \u0628\u06c6 \u067e\u06d5\u06cc\u062f\u0627 \u0628\u06a9\u0627 \u0648 \u062c\u06c6\u0628\u0627\u0631\u062a\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062f\u06cc \u0628\u06ce\u0646\u06ce.',
    '\u0686\u06c6\u0646 \u0644\u06d5 \u0634\u06a9\u06c6\u06cc \u062e\u0648\u0627 \u0646\u0627\u062a\u0631\u0633\u0646\u061f',
    '\u06a9\u06d5\u0686\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u067e\u0644\u06d5\u067e\u0644\u06d5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0628\u06d5 \u0686\u0627\u0648 \u0646\u06d5\u062a\u0627\u0646\u062f\u06cc\u0648\u06d5 \u0686\u062a\u06c6 \u062e\u0648\u062f\u0627 \u062d\u06d5\u0648 \u0628\u06d5\u0631\u0632\u0627\u06cc\u06cc\u06cc - \u062a\u0648\u06ce \u0644\u06d5\u0633\u06d5\u0631 \u062a\u0648\u06ce - \u0626\u0627\u0641\u0631\u0627\u0646\u062f\u0648\u0648\u06d5\u061f',
    '\u0644\u06d5\u0646\u0627\u0648\u06cc\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06a4\u06cc \u062a\u0631\u06cc\u0641\u06d5\u062f\u0627\u0631 \u06a9\u0631\u062f \u0648 \u062e\u06c6\u0631\u06cc\u0634\u06cc \u06a9\u0631\u062f\u06d5 \u0686\u0631\u0627\u06cc\u06d5\u06a9.',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0626\u06ce\u0648\u06d5\u06cc \u0644\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0695\u0648\u0627\u0646\u062f\u060c \u0628\u06d5 \u0695\u0648\u0627\u0646\u062f\u0646.',
    '\u067e\u0627\u0634\u0627\u0646 \u062f\u06d5\u0648\u0628\u0627\u062a\u06d5\u0648\u06d5 \u0646\u0627\u0648\u06cc \u0648 \u062f\u06cc\u0633\u0627\u0646 \u062f\u06d5\u0631\u0648\u0648 \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5\u060c \u0628\u06d5 \u062f\u06d5\u0631\u06a9\u0631\u062f\u0646.',
    '\u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0647\u06d5\u0631\u062f\u06cc \u0628\u06c6 \u06a9\u0631\u062f\u0648\u0648\u0646 \u0628\u06d5 \u0695\u0627\u06cc\u06d5\u062e\u061b',
    '\u06a9\u06d5 \u0647\u0648\u0648\u0646 \u0644\u06d5 \u0695\u06ce\u06af\u06d5 \u067e\u0627\u0646 \u0648 \u0628\u06d5\u0631\u06cc\u0646\u06d5\u06a9\u0627\u0646\u0627\u060c \u0628\u06ce\u0646 \u0648 \u0628\u0686\u0646.',
    '\u0646\u0648\u0648\u062d \u0648\u062a\u06cc: \u0626\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0644\u06d5 \u0645\u0646 \u06cc\u0627\u062e\u06cc \u0628\u0648\u0648\u0646\u061b \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0634\u0648\u06ce\u0646 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5\u0648\u062a\u0648\u0648\u0646 \u06a9\u06d5 \u0633\u0627\u0645\u0627\u0646 \u0648 \u0641\u0631\u0632\u06d5\u0646\u062f\u0627\u0646\u06cc - \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0632\u06d5\u0631\u06d5\u0631 - \u0647\u06cc\u0686\u06cc \u0644\u06d5\u0648\u0627\u0646 \u0632\u06ce\u062f\u06d5 \u0646\u06d5\u06a9\u0631\u062f.',
    '\u0641\u06ce\u06b5\u06cc\u0634\u06cc\u0627\u0646 \u06a9\u0631\u062f\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0641\u06ce\u06b5\u06ce\u06a9\u06cc \u062c\u0627\u0646\u0627\u0646\u06d5.',
    '\u0648\u062a\u0648\u0648\u0634\u06cc\u0627\u0646\u06d5: \u0644\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06d5\u0631\u0633\u062a\u0648\u0648\u062a\u0627\u0646\u0646 \u0645\u06d5\u062a\u06d5\u06a9\u0646\u06d5\u0648\u06d5 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0644\u06d5 \u00ab\u0648\u06d5\u062f\u062f\u00bb \u0648 \u0644\u06d5 \u00ab\u0633\u0648\u0627\u0639\u00bb \u062f\u06d5\u0633 \u0628\u06d5\u0631 \u0645\u06d5\u062f\u06d5\u0646\u061b \u0644\u06d5 \u00ab\u06cc\u06d5\u063a\u0648\u0633\u00bb \u0648 \u00ab\u06cc\u06d5\u0639\u0648\u0642\u00bb \u0648 \u00ab\u0646\u06d5\u0633\u0631\u00bb\u06cc\u0634 \u0647\u06d5\u0631 \u0648\u06d5\u062a\u0631.',
    '\u0632\u06c6\u0631\u06cc\u0634\u06cc\u0627\u0646 \u06af\u0648\u0645\u0695\u0627 \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b \u062f\u06d5 \u062a\u06c6\u0634 - \u0628\u06d5\u062c\u06af\u06d5 \u0644\u06d5 \u06af\u0648\u0645\u0695\u0627\u06cc\u06cc - \u0647\u06cc\u0686\u06ce \u0644\u06d5\u0645 \u0633\u062a\u06d5\u0645\u06a9\u0627\u0631\u0627\u0646\u06d5 \u0632\u06ce\u062f\u06d5 \u0645\u06d5\u06a9\u06d5.',
    '\u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0628\u06d5 \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u060c \u0628\u06d5 \u0626\u0627\u0648 \u062e\u0646\u06a9\u0627\u0646\u061b \u0626\u06d5\u0648\u0633\u0627 \u062e\u0631\u0627\u0646\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631. \u0626\u06cc\u062a\u0631 \u0644\u06d5\u0645 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u0628\u06d5\u062f\u06d5\u0631\u060c \u06cc\u0627\u0631\u06cc\u062f\u06d5\u0631\u06cc\u0627\u0646 \u067e\u06d5\u06cc\u062f\u0627 \u0646\u06d5\u06a9\u0631\u062f.',
    '\u0646\u0648\u0648\u062d\u06cc\u0634 \u0648\u062a\u06cc: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645! \u0644\u06d5\u0645 \u0633\u06d5\u0631 \u0647\u06d5\u0631\u062f\u06d5 \u0626\u06d5\u0648\u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062a\u06c6 \u0646\u0627\u0646\u0627\u0633\u0646\u060c \u062c\u0648\u0648\u0642\u06d5\u0648\u0627\u0631\u06cc\u0627\u0646 \u0644\u06ce \u0645\u06d5\u0647\u06ce\u06b5\u06d5.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u06af\u06d5\u0631 \u0628\u06cc\u0627\u0646\u0647\u06ce\u06b5\u06cc\u060c \u0639\u06d5\u0628\u062f\u0627\u0646\u062a \u06af\u0648\u0645\u0695\u0627 \u062f\u06d5\u06a9\u06d5\u0646 \u0648 \u0647\u06d5\u0631 \u0632\u0627\u0631\u06c6\u06cc\u06d5\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0628\u0628\u06ce\u060c \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631 \u0648 \u0633\u067e\u06b5\u06d5 \u062f\u06d5\u0628\u06ce.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u0645! \u0645\u0646 \u0648 \u062f\u0627\u06a9 \u0648 \u0628\u0627\u0628\u06cc \u0645\u0646 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06d5\u0648\u06d5 \u0644\u06d5 \u0645\u0627\u06b5\u0645\u062f\u0627\u0646 \u0648 \u067e\u06cc\u0627\u0648\u06d5\u06a9\u0627\u0646 \u0648 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc \u062e\u0627\u0648\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0639\u06d5\u0641\u0648\u0648 \u0628\u06a9\u06d5 \u0648 \u0646\u0627\u0647\u06d5\u0642\u0627\u0646\u06cc\u0634 \u0628\u06d5 \u062c\u0627\u0631\u06ce \u062a\u06d5\u0631\u062a \u0648 \u062a\u0648\u0648\u0646\u0627 \u06a9\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u067e\u06ce\u0645 \u0695\u0627\u06af\u06d5\u06cc\u06d5\u0646\u0631\u0627\u0648\u06d5\u060c \u0686\u06d5\u0646\u062f \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u062c\u0646\u06c6\u06a9\u0627\u0646 \u06af\u0648\u06ce\u06cc\u0627\u0646 \u0647\u06d5\u06b5\u062e\u0633\u062a\u061b \u0626\u06d5\u0648\u0633\u0627 \u0648\u062a\u06cc\u0627\u0646: \u0626\u06ce\u0645\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646\u06ce \u0633\u06d5\u06cc\u0631\u0645\u0627\u0646 \u0628\u06cc\u0633\u062a.',
    '\u0628\u06c6 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u06d5 \u0695\u06ce\u0628\u06d5\u0631\u06d5 \u0648 \u0626\u06ce\u0645\u06d5 \u0628\u0627\u0648\u06d5\u0695\u0645\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627 \u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u06a9\u06d5\u0633\u06ce \u0646\u0627\u06a9\u06d5\u06cc\u0646\u06d5 \u0634\u06d5\u0631\u06cc\u06a9\u06cc \u062e\u0648\u062f\u0627.',
    '\u0632\u06c6\u0631 \u0628\u06b5\u0646\u062f\u06d5 \u0634\u06a9\u06c6\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0626\u06ce\u0645\u06d5\u061b \u0646\u06d5 \u0698\u0646\u06cc \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0646\u06d5 \u0641\u0631\u0632\u06d5\u0646\u062f\u06ce\u06a9.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0628\u06ce\u0626\u0627\u0648\u06d5\u0632\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u0648\u0627 \u0642\u0633\u06d5\u06cc \u062a\u0648\u0695\u0695\u06d5\u0647\u0627\u062a\u06cc \u062f\u06d5\u06a9\u0631.',
    '\u0626\u06ce\u0645\u06d5 \u067e\u06ce\u0645\u0627\u0646 \u0648\u0627 \u0628\u0648\u0648 \u0647\u06cc\u0686\u06a9\u06d5\u0633 \u2013 \u0644\u06d5 \u0645\u0631\u06c6\u06a4 \u0648 \u0644\u06d5 \u062c\u0646\u06c6\u06a9\u06d5 \u2013 \u0647\u06cc\u0686 \u062f\u0631\u06c6\u06cc\u06d5\u06a9 \u0628\u06d5 \u062f\u06d5\u0645 \u062e\u0648\u0627\u0648\u06d5 \u0647\u06d5\u06b5\u0646\u0627\u0628\u06d5\u0633\u062a\u0646.',
    '\u062f\u06d5\u0634\u0645\u0627\u0646\u0632\u0627\u0646\u06cc \u067e\u06cc\u0627\u0648\u0627\u0646\u06ce \u0644\u06d5 \u0628\u06d5\u0631\u06d5\u06cc \u0645\u0631\u06c6\u060c \u067e\u06d5\u0646\u0627 \u062f\u06d5\u0628\u06d5\u0646 \u0628\u06d5 \u067e\u06cc\u0627\u0648\u0627\u0646\u06ce \u0644\u06d5 \u062c\u0646\u06c6\u06a9\u06d5 \u0648 \u0633\u06d5\u0631\u06a9\u06ce\u0634\u06cc\u06cc\u0627\u0646 \u067e\u062a\u0631 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 - \u0647\u06d5\u0631 \u0648\u06d5\u06a9\u0648\u0648 \u0626\u06ce\u0648\u06d5 - \u0644\u0627\u06cc\u0627\u0646 \u0648\u0627 \u0628\u0648\u0648 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u062e\u0648\u062f\u0627 \u0647\u06cc\u0686\u06a9\u06d5\u0633 \u0632\u06cc\u0646\u062f\u0648\u0648 \u0646\u0627\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5 \u06af\u06d5\u06cc\u0646\u06d5 \u0626\u06d5\u0645 \u0639\u06d5\u0632\u0645\u0627\u0646\u06d5\u060c \u062f\u06cc\u0645\u0627\u0646 \u067e\u0695\u06d5 \u0644\u06d5 \u067e\u0627\u0633\u062f\u0627\u0631\u06cc \u062a\u0648\u0646\u062f \u0648 \u062a\u06c6\u06b5 \u0648 \u0626\u06d5\u0633\u062a\u06ce\u0631\u06d5\u06af\u06d5\u0644\u06ce \u0695\u0627\u062f\u06d5\u062e\u0634\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u0686\u06d5\u0646\u062f \u0634\u0648\u06ce\u0646\u06ce\u06a9\u06d5\u0648\u06d5 \u0695\u06c6\u062f\u06d5\u0646\u06cc\u0634\u062a\u06cc\u0646 \u06a9\u06c6\u06b5\u06d5 \u0628\u06a9\u06d5\u06cc\u0646\u061b \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0644\u06d5\u0645\u06d5 \u0628\u06d5\u0648 \u0644\u0627 \u06a9\u06c6\u06b5\u06d5 \u0628\u06a9\u0627\u060c \u0645\u06d5\u0634\u062e\u06d5\u06b5\u06ce\u06a9\u06cc \u062f\u06d5\u0628\u06cc\u0646\u06ce \u0644\u06d5 \u0628\u06c6\u0633\u06d5\u06cc\u062f\u0627\u06cc\u06d5.',
    '\u062a\u06ce\u0634 \u0646\u06d5\u06af\u06d5\u06cc\u0648\u06cc\u0646 \u0626\u0627\u062e\u06c6 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u062f\u0627\u0646\u060c \u0628\u0695\u06cc\u0627\u0631\u06cc \u062e\u0631\u0627\u067e \u062f\u0631\u0627\u0648\u06d5\u060c \u06cc\u0627\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0648\u06cc\u0633\u062a\u0648\u0648\u06cc\u06d5 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u0627.',
    '\u0644\u06d5\u0646\u0627\u0648 \u0645\u06d5\u0634\u062f\u0627 \u06a9\u0627\u0631\u0686\u0627\u06a9 \u0647\u06d5\u0646 \u0648 \u062a\u06ce\u0634\u0645\u0627\u0646\u062f\u0627 \u0647\u06d5\u06cc\u06d5 \u0648\u0627 \u0646\u06cc\u06cc\u06d5\u061b \u0626\u06ce\u0645\u06d5 \u06af\u06d5\u0644\u06ce \u0695\u06ce\u06af\u06d5\u0645\u0627\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u062e\u06c6 \u062f\u0627\u0648\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0648\u06d5 \u062a\u06ce\u06af\u06d5\u06cc\u0634\u062a\u0648\u0648\u06cc\u0646 \u06a9\u06d5 \u0644\u06d5 \u0632\u06d5\u0645\u06cc\u0646 \u062f\u06d5\u0631\u06d5\u0642\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u0646\u0627\u06cc\u06d5\u06cc\u0646\u061b \u062a\u06d5\u0646\u0627\u0646\u06d5\u062a \u0628\u06d5 \u0647\u06d5\u06b5\u0627\u062a\u0646\u06cc\u0634 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0626\u06d5\u0648 \u0695\u0632\u06af\u0627\u0631 \u0646\u0627\u0628\u06cc\u0646.',
    '\u0648\u06d5\u062e\u062a\u06ce \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648 \u0695\u06ce \u0646\u06cc\u0634\u0627\u0646\u062f\u06d5\u0631\u06d5\u0645\u0627\u0646 \u0628\u06cc\u0633\u062a\u060c \u0628\u0627\u0648\u06d5\u0695\u06cc \u062e\u06c6\u0645\u0627\u0646 \u067e\u06ce \u0647\u06ce\u0646\u0627. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u0695\u0648\u0627\u06cc \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u06ce\u060c \u0646\u06d5 \u0644\u06d5 \u06a9\u06d5\u0645 \u0628\u0648\u0648\u0646\u06ce \u062f\u06d5\u062a\u0631\u0633\u06ce\u060c \u0646\u06d5 \u0644\u06d5 \u063a\u06d5\u062f\u0631 \u0644\u06ce \u06a9\u0631\u062f\u0646\u06ce.',
    '\u0648\u0627 \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0645\u0627\u0646 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u06cc\u0646\u060c \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5 \u0695\u06ce \u0644\u0627\u062f\u06d5\u0631\u061b \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0645\u0648\u0633\u0648\u06b5\u0645\u0627\u0646\u0646\u060c \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633\u062a\u06cc\u0627\u0646 \u062f\u06cc\u0648\u06d5\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0695\u0627\u0633 \u0644\u0627\u06cc\u0627\u0646 \u062f\u0627\u0648\u06d5\u060c \u062f\u06d5\u0628\u0646\u06d5 \u0626\u06ce\u0632\u0646\u06af\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645.',
    '\u06af\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0647\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06d5\u0645 \u0695\u06ce\u06af\u06d5\u06cc\u06d5 \u0628\u0646\u060c \u0628\u06ce\u06af\u0648\u0645\u0627\u0646\u06d5 \u0644\u06d5 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u0632\u06d5\u0628\u06d5\u0646\u062f\u06d5 \u062a\u06ce\u0631\u0627\u0648\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0647\u06d5\u062a\u0627 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0628\u06cc\u0627\u0646\u0626\u06d5\u0632\u0645\u0648\u0648\u06cc\u0646\u060c \u0647\u06d5\u0631\u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u06cc\u0627\u062f\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u06a9\u0644\u0627 \u0628\u0628\u06ce\u060c \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u062f\u0698\u0648\u0627\u0631\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u062f\u06ce\u0646\u06ce.',
    '\u0645\u0632\u06af\u06d5\u0648\u062a\u06d5\u06a9\u0627\u0646 \u0645\u0627\u06b5\u06cc \u062e\u0648\u062f\u0627\u0646\u061b \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u0648\u062f\u0627\u060c \u0647\u0627\u0648\u0627\u0631\u06cc \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u0645\u06d5\u06a9\u06d5\u0646',
    '\u0644\u06d5\u0648 \u062f\u06d5\u0645\u06d5\u0634\u062f\u0627 \u06a9\u06d5 \u0639\u06d5\u0628\u062f\u06cc \u062e\u0648\u062f\u0627 \u0647\u06d5\u06b5\u0648\u06ce\u0633\u062a\u0627 \u0646\u0648\u06ce\u0698\u06ce \u0628\u06a9\u0627\u060c \u0647\u06d5\u0631\u0648\u062e\u062a \u0628\u0648\u0648 \u0644\u06d5\u0633\u06d5\u0631\u06cc \u0628\u0628\u0646\u06d5 \u067e\u0627\u0631\u0686\u06d5\u06cc\u06d5\u06a9.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u0645\u0646 \u0647\u06d5\u0631 \u0647\u0627\u0648\u0627\u0631 \u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u0645 \u062f\u06d5\u06a9\u06d5\u0645 \u0648 \u06a9\u06d5\u0633\u06cc \u0644\u06ce \u0646\u0627\u06a9\u06d5\u0645 \u0628\u06d5 \u0634\u06d5\u0631\u06cc\u06a9.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u0644\u06d5 \u062f\u06d5\u0633\u062a\u0645 \u0646\u0627\u06cc\u06d5 \u0632\u06cc\u0627\u0646\u06ce\u06a9\u0645 \u0628\u06c6\u062a\u0627\u0646 \u0628\u0628\u06ce\u061b \u06cc\u0627\u0646 \u0628\u0648\u0648\u062e\u06d5\u0645\u06d5 \u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 - \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5 \u062e\u0648\u062f\u0627 - \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u067e\u06d5\u0646\u0627\u0645 \u0646\u0627\u062f\u0627 \u0648 \u0644\u0627\u06cc \u0626\u06d5\u0648 \u0646\u06d5\u0628\u06ce\u060c \u0647\u06cc\u0686 \u0626\u06d5\u0646\u0648\u0627\u06cc\u06d5\u06a9\u06cc \u0634\u06a9 \u0646\u0627\u0628\u06d5\u0645.',
    '\u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u06ce\u06a9 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0648 \u06af\u06d5\u06cc\u0627\u0646\u062f\u0646\u06cc \u067e\u06d5\u06cc\u0627\u0645\u0627\u0646\u06cc. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0646\u0627\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u06cc \u062e\u0648\u062f\u0627 \u0648 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0628\u06a9\u0627\u060c \u0628\u06d5\u0634\u06cc \u0626\u0627\u06af\u0631\u06cc \u062f\u06c6\u0698\u06d5\u0647\u06d5\u061b \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5.',
    '\u062a\u0627 \u0626\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u06cc \u0626\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u062f\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u0627\u0646 \u06a9\u06d5 \u0628\u06d5\u06b5\u06ce\u0646\u06cc\u0627\u0646 \u067e\u06ce \u062f\u0631\u0627\u0648\u06d5. \u062c\u0627 \u062f\u06d5\u0632\u0627\u0646\u0646 \u06a9\u06ce \u06cc\u0627\u0648\u06d5\u0631\u06cc \u0628\u06ce \u0647\u06ce\u0632\u062a\u0631\u06d5 \u0648 \u0644\u06d5 \u0698\u0645\u0627\u0631\u06d5\u0634\u062f\u0627 \u06a9\u06d5\u0645\u062a\u0631\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0647\u06cc\u0686 \u0626\u0627\u06af\u0627\u0645 \u0644\u06ce \u0646\u06cc\u06cc\u06d5 \u0626\u06d5\u0648\u06cc \u0628\u06d5\u06b5\u06ce\u0646\u0648\u0648 \u067e\u06ce \u062f\u06d5\u062f\u0631\u06ce\u060c \u0646\u0632\u06cc\u06a9\u06d5 \u06cc\u0627\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0645\u06d5\u0648\u062f\u0627\u06cc\u06d5\u06a9\u06cc \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u06d5.',
    '\u0644\u06d5 \u0646\u06d5\u062f\u06cc\u0627\u0631\u0627\u0646 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u0648 \u0646\u0647\u06ce\u0646\u06cc\u06cc \u062e\u06c6\u06cc \u0628\u06c6 \u06a9\u06d5\u0633\u06ce \u0626\u0627\u0634\u06a9\u0631\u0627 \u0646\u0627\u06a9\u0627\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0628\u06c6 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06ce\u06a9\u06cc \u0644\u06ce\u06cc \u0695\u0627\u0632\u06cc \u0628\u06ce\u061b \u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5 \u067e\u06ce\u0634 \u0648\u067e\u0627\u0634\u06cc\u06d5\u0648\u06d5 \u067e\u0627\u0633\u06d5\u0648\u0627\u0646\u0627\u0646 \u0628\u06d5\u0695\u06ce \u062f\u06d5\u06a9\u0627.',
    '\u062a\u0627 \u0628\u0632\u0627\u0646\u06ce \u0628\u06d5 \u062f\u0631\u0648\u0633\u062a\u06cc \u067e\u06d5\u06cc\u0627\u0645\u06af\u06d5\u0644\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u0695\u0627\u06af\u06d5\u06cc\u0627\u0646\u062f. \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc\u0634\u06cc\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc \u0647\u06d5\u0698\u0645\u0627\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06d5\u06cc \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u062e\u06c6\u062a \u0628\u06d5 \u062c\u0644\u06a9\u0627\u0646 \u062f\u0627\u067e\u06c6\u0634\u06cc\u0648\u06d5!',
    '\u0634\u06d5\u0648 \u0647\u06d5\u0633\u062a\u06d5\u0648\u06d5\u061b \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0645\u06ce\u060c',
    '\u0644\u06d5 \u0646\u06cc\u0645\u06d5\u06a9\u06d5\u06cc\u061b \u06cc\u0627\u0646 \u06a9\u06d5\u0645\u06ce\u06a9\u06cc \u0644\u06ce \u062f\u0627\u0628\u0695\u06d5\u061b',
    '\u06cc\u0627\u0646 \u06a9\u06d5\u0645\u06ce\u06a9\u06cc \u062f\u06d5\u0633\u06d5\u0631 \u0628\u062e\u06d5. \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u0634 \u0628\u06d5 \u0644\u06d5\u0633\u06d5\u0631\u062e\u06c6\u06cc\u06cc \u0628\u062e\u0648\u06ce\u0646\u06d5 \u0648 \u0628\u06d5 \u0695\u06d5\u0648\u0627\u0646\u06cc.',
    '\u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u0626\u06ce\u0645\u06d5 \u0648\u062a\u0627\u0631\u06ce \u06af\u0631\u0627\u0646\u062a \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u0633\u06d5\u0631.',
    '\u0634\u06d5\u0648\u0646\u062e\u0648\u0648\u0646\u06cc \u0628\u06c6 \u0695\u0627\u0645\u0627\u0646\u06ce \u062c\u06ce\u062f\u0627\u0631\u062a\u0631\u06d5 \u0648 \u0628\u06c6 \u0648\u062a\u0627\u0631\u06cc\u0634 \u0644\u06d5\u0628\u0627\u0631\u062a\u0631\u06d5.',
    '\u0644\u06d5 \u0695\u06c6\u0698\u06cc\u0634\u062f\u0627 \u0645\u06d5\u0648\u062f\u0627\u06cc \u0647\u0627\u062a\u0648\u0686\u06c6\u062a \u062f\u0631\u06ce\u0698\u06d5.',
    '\u0647\u06d5\u0631 \u0646\u0627\u0648\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a \u0628\u06d5 \u06cc\u0627\u062f \u0628\u06a9\u06d5\u0648 \u0644\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648\u060c \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0628\u062a\u06d5\u06a9\u06d5\u0648\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062e\u06c6\u0631\u0647\u06d5\u06b5\u0627\u062a \u0648\u062e\u06c6\u0631\u0627\u0648\u0627\u06cc\u06d5\u061b \u0628\u06d5 \u063a\u06d5\u06cc\u0631\u06d5\u0632 \u0626\u06d5\u0648 \u0647\u06cc\u0686 \u0634\u062a \u0628\u06c6 \u067e\u06d5\u0631\u0633\u062a\u0646 \u0646\u0627\u0628\u06ce. \u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0628\u0631\u06cc\u06a9\u0627\u0631 \u0628\u06af\u0631\u06d5.',
    '\u0628\u06c6 \u0626\u06d5\u0648 \u0642\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0626\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06d5. \u0644\u06ce\u0634\u06cc\u0627\u0646 \u0644\u0627\u062f\u06d5\u061b \u0628\u06d5 \u0644\u0627\u0644\u06ce\u062f\u0627\u0646\u06ce\u06a9\u06cc \u067e\u06d5\u0633\u0646\u062f.',
    '\u0644\u06d5 \u0645\u0646\u06cc\u0634 \u06af\u06d5\u0695\u06ce \u062f\u06d5\u06af\u06d5\u06b5 \u0626\u06d5\u0648 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646\u06d5\u06cc \u0646\u0627\u0632\u067e\u06d5\u0631\u0648\u06d5\u0631\u062f\u06d5. \u062a\u06c6\u0634 \u0645\u06d5\u0648\u062f\u0627\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u062f\u06d5\u06cc\u06d5.',
    '\u0628\u06ce \u0633\u06c6 \u0632\u06d5\u0646\u062c\u06cc\u0631\u06cc \u0633\u06d5\u0646\u06af\u06cc\u0646 \u0648 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u0644\u06d5 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u06d5.',
    '\u062e\u06c6\u0631\u0627\u06a9\u06ce\u06a9\u06cc \u06af\u06d5\u0631\u0648\u0648\u06af\u0631\u06cc\u0634\u060c \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0628\u06d5\u0698\u0627\u0646\u06cc\u0634.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0628\u06c6\u0645 \u0626\u06d5\u0644\u06d5\u0631\u0632\u06ce \u0648 \u06a9\u06d5\u0698 \u062f\u06d5\u0647\u06d5\u0698\u06ce\u0646\u060c \u06a9\u06ce\u0648\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u0628\u0646\u06d5 \u06af\u0631\u062f\u0648\u0644\u06a9\u06d5\u06cc \u0695\u06d5\u0645\u06b5\u06cc \u062e\u0632\u06c6\u06a9.',
    '\u0626\u06ce\u0645\u06d5 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06c6 \u0626\u06ce\u0648\u06d5 \u06a9\u0644 \u06a9\u0631\u062f\u060c \u0634\u0627\u06cc\u06d5\u062a \u0628\u06ce \u0628\u06d5\u0633\u06d5\u0631\u062a\u0627\u0646\u06d5\u0648\u06d5\u061b \u0647\u06d5\u0631\u0648\u06d5\u06a9 \u0628\u06c6 \u0626\u0627\u0644\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9\u0645\u0627\u0646 \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0628\u06d5\u06cc\u0627 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06d5\u06a9\u06d5\u06cc \u0646\u06d5\u062f\u06d5\u06a9\u0631\u062f\u061b \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0645\u06d5\u0634 \u0626\u0627\u0632\u0627\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0698\u0627\u0646\u0645\u0627\u0646 \u062f\u0627.',
    '\u0633\u0627 \u0626\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0628\u06ce\u062f\u06cc\u0646\u06cc \u0645\u0627\u0646\u06d5\u0648\u06d5\u060c \u0686\u0644\u06c6\u0646 \u062e\u06c6\u062a\u0627\u0646 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0646 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0648\u0627 \u0645\u0646\u062f\u0627\u06b5\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u067e\u06cc\u0631 \u062f\u06d5\u06a9\u0627.',
    '\u0639\u06d5\u0632\u0645\u0627\u0646\u06cc \u067e\u06ce \u0634\u06d5\u0642\u0627\u0631 \u062f\u06d5\u0628\u06ce. \u0628\u06d5\u06b5\u06ce\u0646\u06cc \u062e\u0648\u0627\u0634 \u0647\u06d5\u0631 \u062f\u06ce\u062a\u06d5 \u062c\u06ce.',
    '\u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc\u06cc\u06d5\u06a9\u06d5 [\u0628\u06c6\u0645\u06d5\u0631\u062f\u0645]\u061b \u06a9\u06ce \u062d\u06d5\u0632 \u0628\u06a9\u0627\u060c \u0628\u06d5\u0631\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u0695\u06ce\u06af\u06d5 \u062f\u06d5\u0631 \u062f\u06d5\u06a9\u0627.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5 \u06a9\u06d5 \u062a\u06c6 \u0648 \u0647\u06ce\u0646\u062f\u06ce\u06a9 \u0644\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u062f\u06d5\u06af\u06d5\u06b5 \u062a\u06c6\u062f\u0627\u0646\u060c \u0646\u0632\u06cc\u06a9 \u062f\u0648\u0648 \u0628\u06d5\u0634 \u0644\u06d5 \u0633\u06ce \u0628\u06d5\u0634\u06cc \u0626\u06d5\u0648 \u0634\u06d5\u0648\u06af\u0627\u0631\u06d5 \u0648 \u0646\u06cc\u0648\u06d5\u06cc\u06cc \u0648 \u0633\u06ce \u06cc\u06d5\u06a9\u06cc\u0634\u06cc\u060c \u0628\u06c6 \u0634\u06d5\u0648\u0646\u0648\u06ce\u0698\u0627\u0646 \u0647\u06d5\u06b5\u062f\u06d5\u0633\u062a\u0646\u06d5\u0648\u06d5. \u0647\u06d5\u0631 \u062e\u0648\u062f\u0627\u0634\u06d5 \u06a9\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u0628\u06c6 \u0634\u06d5\u0648 \u0648 \u0695\u06c6\u0698\u0627\u0646 \u062f\u0627\u062f\u06d5\u0646\u06ce. \u0626\u06d5\u0648 \u062f\u06d5\u0632\u0627\u0646\u06ce \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u06c6\u062a\u0627\u0646 \u0646\u0627\u0698\u0645\u06ce\u0631\u062f\u0631\u06ce. \u0626\u06d5\u0648\u0627 \u0686\u0627\u0648\u067e\u06c6\u0634\u06cc\u06cc \u0644\u06ce \u06a9\u0631\u062f\u0646. \u062f\u06d5\u0633\u0627 \u0647\u06d5\u0631 \u0644\u06d5 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5\u06a9\u06d5 - \u0686\u06d5\u0646\u062f\u06cc \u0628\u06c6\u0648 \u062f\u06d5\u0644\u0648\u06ce - \u0628\u062e\u0648\u06ce\u0646\u0646. \u062e\u0648\u0627 \u062f\u06d5\u06cc\u0632\u0627\u0646\u06ce \u0647\u06ce\u0646\u062f\u06ce\u06a9\u0648\u0648 \u0646\u06d5\u062e\u06c6\u0634 \u062f\u06d5\u06a9\u06d5\u0648\u0646\u061b \u0647\u06ce\u0646\u062f\u06ce\u06a9\u06cc\u0634\u062a\u0627\u0646 \u0628\u06c6 \u0628\u0698\u06cc\u0648\u06ce \u062e\u0648\u062f\u0627 \u0628\u06cc\u062f\u0627\u060c \u0628\u06d5\u0648\u06b5\u0627\u062a\u0627 \u0628\u06b5\u0627\u0648 \u062f\u06d5\u0628\u0646\u061b \u06a9\u06c6\u0645\u06d5\u06b5\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u0695\u0627\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u0686\u0646\u06d5 \u062e\u06d5\u0632\u0627\u061b \u0633\u0627 \u0686\u06d5\u0646\u062f\u06cc \u0628\u06c6\u062a\u0627\u0646 \u0628\u0648\u0648\u0632 \u062f\u06d5\u062e\u0648\u0627\u060c \u0644\u06ce\u06cc \u0628\u062e\u0648\u06ce\u0646\u0646. \u0646\u0648\u06ce\u0698\u0628\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u062f\u06d5\u0646 \u0648 \u0645\u0627\u06b5\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u0648\u0627\u0648 - \u0628\u06d5 \u062f\u06d5\u0633\u062a\u06d5\u0648\u0627\u0648\u06ce\u06a9\u06cc \u067e\u06d5\u0633\u0646\u062f - \u0628\u062f\u06d5\u0646 \u0628\u06d5 \u062e\u0648\u0627. \u0647\u06d5\u0631 \u067e\u06ce\u0634 \u0628\u0627\u0631\u06ce \u0628\u06d5\u0695\u06ce\u06cc \u0628\u06a9\u06d5\u0646\u060c \u0628\u0627\u0634\u062a\u0631\u06d5\u06a9\u06d5\u06cc \u0644\u0627\u06cc \u062e\u0648\u062f\u0627 \u067e\u06ce\u0648 \u062f\u06d5\u06af\u0627\u062a\u06d5\u0648\u06d5\u061b \u0648\u06ce\u0695\u0627\u06cc \u067e\u0627\u062f\u0627\u0634\u06cc \u06af\u06d5\u0648\u0631\u06d5\u062a\u0631. \u0647\u06d5\u0631 \u0628\u0648\u0648\u0631\u062f\u0646 \u0644\u06d5 \u062e\u0648\u0627 \u0628\u062e\u0648\u0627\u0632\u0646\u061b \u062e\u0648\u0627 \u0644\u06d5 \u06af\u0648\u0646\u0627\u0647\u0627\u0646 \u062e\u06c6\u0634 \u062f\u06d5\u0628\u06ce \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06d5.',
    '\u0626\u06d5\u06cc \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062c\u0644\u06a9\u062a \u0628\u06d5\u0633\u06d5\u0631 \u062e\u06c6 \u062f\u0627\u062f\u0627\u0648\u06d5!',
    '\u0695\u0627\u0628\u06d5\u060c \u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06ce\u0646\u06d5.',
    '\u0695\u0627\u0634\u06af\u06d5\u06cc\u06ce\u0646\u06d5 \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0645\u06d5\u0632\u0646\u06d5.',
    '\u0628\u06d5\u0631\u06af\u06cc\u0634\u062a \u062e\u0627\u0648\u06ce\u0646 \u0695\u0627\u06af\u0631\u06d5.',
    '\u0644\u06d5 \u0647\u06d5\u0631\u0686\u06cc \u0646\u0627\u067e\u0627\u06a9\u06d5 \u062f\u0648\u0648\u0631 \u0628\u06d5.',
    '\u0645\u06d5\u0634\u0628\u06d5\u062e\u0634\u06d5 \u0648 \u067e\u06ce\u062a \u0648\u0627 \u0628\u06ce \u0628\u06c6\u0634\u06d5.',
    '\u0628\u06c6 \u062e\u0627\u062a\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u062f\u06d5 \u06a9\u06d5\u06b5\u06d5\u0634\u0627\u062e \u062f\u06d5\u0645\u06ce\u0646\u062f\u0631\u0627\u060c',
    '\u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u062a\u0648\u0648\u0634\u06d5\u061b',
    '\u0628\u06c6 \u0628\u06ce\u062f\u06cc\u0646\u0627\u0646 \u0646\u06d5 \u0647\u0627\u0633\u0627\u0646\u06d5.',
    '\u062a\u06c6 \u0644\u06ce\u0645 \u06af\u06d5\u0695\u06ce \u0628\u06c6 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0645\u0646 \u062a\u06d5\u0646\u06cc\u0627 \u0648\u06d5\u062f\u06cc\u0645 \u0647\u06ce\u0646\u0627\u0648\u06d5\u061b',
    '\u062f\u0627\u0631\u0627\u06cc\u06cc \u0641\u0631\u06d5\u0634\u0645 \u062f\u0627\u06cc\u06d5\u061b',
    '\u0686\u06d5\u0646\u062f \u06a9\u0648\u0695\u06ce\u06a9\u06cc\u0634 \u06a9\u06d5 \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u061b',
    '\u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631\u06ce\u06a9\u0645 \u0628\u06c6 \u0695\u06ce\u06a9 \u062e\u0633\u062a\u061b',
    '\u0647\u06ce\u0634\u062a\u0627 \u0647\u06d5\u0631 \u0686\u0627\u0648\u06cc \u0644\u06d5\u0648\u06d5\u06cc\u06d5 \u0628\u06c6\u06cc \u067e\u062a\u0631 \u06a9\u06d5\u0645.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632\u061b \u0686\u0648\u0646 \u0628\u06c6 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u0645\u0627\u0646 \u0644\u0627\u0633\u0627\u0631 \u0628\u0648\u0648\u06d5.',
    '\u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06ce \u0648\u0627\u06cc \u062f\u06d5\u06a9\u06d5\u0645\u060c \u067e\u06ce\u06cc \u0647\u06d5\u06b5\u0628\u0686\u06ce.',
    '\u0626\u06d5\u0648 \u0647\u06d5\u0631 \u0695\u0627\u0645\u0627 \u0648 \u0647\u06d5\u06b5\u06cc\u0633\u06d5\u0646\u06af\u0627\u0646\u062f.',
    '\u0626\u06d5\u06cc \u0645\u06d5\u0631\u06af\u06cc \u0628\u06ce\u061b \u062e\u06c6\u06cc \u0686\u06c6\u0646\u06cc \u0644\u06ce\u06a9\u062f\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5\u061f',
    '\u062f\u06cc\u0633\u0627\u0646 \u0646\u06d5\u0698\u06cc\u061b \u0686\u06c6\u0646\u06cc \u0628\u06c6 \u0686\u0648\u0648\u061f',
    '\u0644\u06d5\u067e\u0627\u0634\u0627\u0646\u0627 \u062a\u0648\u0627\u0634\u0627\u06cc \u06a9\u0631\u062f.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u0695\u0648\u0648\u06cc \u06af\u0631\u0698 \u06a9\u0631\u062f \u0648 \u062a\u0631\u0634 \u0647\u06d5\u06b5\u06af\u06d5\u0695\u0627.',
    '\u0644\u06d5\u067e\u0627\u0634\u0627\u0646 \u067e\u0634\u062a\u06cc \u0647\u06d5\u06b5\u06a9\u0631\u062f \u0648 \u0641\u06cc\u0632\u06cc \u0646\u0648\u0627\u0646\u062f.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06af\u0648\u062a\u06cc: \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5 - \u0628\u0628\u06ce \u0648 \u0646\u06d5\u0628\u06ce - \u062c\u0627\u062f\u0648\u0648\u06ce\u06a9\u06d5 \u0644\u06d5\u06a9\u06c6\u0646\u06d5\u0648\u06d5 \u062f\u06d5\u06cc\u06af\u06ce\u0695\u0646\u06d5\u0648\u06d5.',
    '\u0628\u0628\u06ce \u0648 \u0646\u06d5\u0628\u06ce\u060c \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u0648\u062a\u06d5\u06cc \u0645\u0631\u06c6\u06cc\u06d5.',
    '\u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5 \u062f\u06d5\u06cc\u0647\u0627\u0648\u06ce\u0645\u06d5 \u062f\u06c6\u0632\u06d5\u0642\u06d5\u0648\u06d5.',
    '\u06a9\u0648\u0627 \u0626\u06d5\u0632\u0627\u0646\u06cc \u062f\u06c6\u0632\u06d5\u0642 \u0686\u06cc\u06cc\u06d5\u061f',
    '\u0646\u06d5 \u062f\u06d5\u0647\u06ce\u06b5\u06ce \u0648 \u0646\u06d5 \u0648\u0627\u0632 \u062f\u06ce\u0646\u06ce.',
    '\u067e\u06ce\u0633\u062a\u06cc \u0645\u0631\u06c6 \u0695\u06d5\u0634\u06d5\u0648 \u062f\u06d5\u06a9\u0627 \u0648 \u062f\u06d5\u0628\u0631\u0698\u06ce\u0646\u06ce.',
    '\u0646\u06c6\u0632\u062f\u06d5 \u0641\u0631\u0634\u062a\u06d5\u06cc \u0644\u06d5\u0633\u06d5\u0631\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0628\u06ce\u0698\u06af\u06d5 \u0644\u06d5 \u0641\u0631\u06cc\u0634\u062a\u0627\u0646\u060c \u06a9\u06d5\u0633\u0645\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u06c6\u062a\u06d5 \u06a9\u0627\u0631\u0628\u06d5\u062f\u06d5\u0633\u062a\u06cc \u0626\u0627\u06af\u0631. \u0628\u06c6\u06cc\u06d5\u0634 \u0698\u0645\u0627\u0631\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u0648\u0627 \u062f\u0627\u0646\u0627\u0648\u06d5 \u062a\u0627 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646 \u0628\u0626\u06d5\u0632\u0645\u0648\u0648\u06cc\u0646. \u062a\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u06a9\u062a\u06ce\u0628\u06cc\u0627\u0646 \u067e\u06ce \u062f\u0631\u0627\u0648\u06d5\u060c \u0647\u06cc\u0686 \u06af\u0648\u0645\u0627\u0646\u06ce\u06a9\u06cc\u0627\u0646 \u0646\u06d5\u0645\u06ce\u0646\u06ce. \u062a\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695\u0646\u060c \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0644\u06d5 \u0632\u06ce\u062f\u06d5\u06cc\u06cc \u0628\u06ce\u061b \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u06a9\u062a\u06ce\u0628\u06cc\u0627\u0646 \u067e\u06ce \u062f\u0631\u0627\u0648\u06d5 \u0648 \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u0627\u0646\u060c \u062f\u0648\u0648\u062f\u06b5 \u0646\u06d5\u0628\u0646. \u062a\u0627 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062f\u06d5\u063a\u06d5\u0632\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06d5\u062f\u06b5\u062f\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u062e\u0648\u062f\u06ce\u0646\u06d5\u0646\u0627\u0633\u0646\u060c \u0628\u06ce\u0698\u0646: \u062e\u0648\u062f\u0627 \u0644\u06d5\u0645 \u0646\u0645\u0648\u0648\u0646\u06d5 \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0646\u06d5 \u0686\u06d5\u06cc \u0648\u06cc\u0633\u062a\u06af\u06d5\u061f \u0626\u0627\u0648\u0627 \u062e\u0648\u062f\u0627 \u0647\u06d5\u0631\u06a9\u06ce\u06cc \u0628\u0648\u06ce \u06af\u0648\u0645\u0695\u0627\u06cc \u062f\u06d5\u06a9\u0627 \u0648 \u06a9\u06ce\u06cc \u0628\u0648\u06ce \u0634\u0627\u0631\u06d5\u0632\u0627\u06cc \u062f\u06d5\u06a9\u0627. \u067e\u06ce\u0634\u0645\u06d5\u0631\u06af\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u0634 - \u0644\u06d5 \u062e\u0648\u0627 \u0628\u06d5\u062f\u06d5\u0631 - \u06a9\u06d5\u0633 \u0698\u0645\u0627\u0631\u06d5\u06cc\u0627\u0646 \u0646\u0627\u0632\u0627\u0646\u06ce. \u0628\u0627\u0633\u06cc \u062f\u06c6\u0632\u06d5\u0642 \u0647\u06d5\u0631 \u0628\u06d5\u062a\u06d5\u0646\u06cc\u0627 \u0628\u06c6 \u0628\u06cc\u0631\u06d5\u0648\u06d5\u0631\u06cc \u0645\u0631\u06c6\u06cc\u06d5.',
    '\u0646\u06d5\u0686\u0646 \u0628\u0627\u0648\u06d5\u0695\u06cc \u067e\u06ce \u0646\u06d5\u06a9\u06d5\u0646\u061b \u0633\u0648\u06ce\u0646\u062f \u0628\u06d5\u0645 \u0647\u06d5\u06cc\u06a4\u06d5\u061b',
    '\u0628\u06d5\u0648 \u0634\u06d5\u0648\u06af\u0627\u0631\u06d5\u0634 \u06a9\u06d5 \u0695\u0627\u062f\u06d5\u06a9\u0627\u060c',
    '\u0628\u06d5\u0648 \u0633\u067e\u06ce\u062f\u06d5\u06cc \u0695\u0648\u0648\u0628\u06d5\u0646\u062f\u06cc \u062e\u06c6\u06cc \u0648\u06d5\u0644\u0627 \u062f\u06d5\u062f\u0627\u060c',
    '\u0626\u06d5\u0645 \u0626\u0627\u06af\u0631\u06d5 \u06cc\u06d5\u06a9 \u0644\u06d5 \u0628\u06d5\u06b5\u0627 \u06af\u06d5\u0648\u0631\u06d5\u06a9\u0627\u0646\u06d5\u061b',
    '\u062a\u0631\u0633 \u0648\u06d5\u0628\u06d5\u0631 \u0645\u0631\u06c6 \u0647\u06ce\u0646\u06d5.',
    '\u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce \u0628\u06ce\u062a\u06d5 \u067e\u06ce\u0634\u06ce\u060c \u06cc\u0627\u0646 \u0628\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5 \u062f\u0648\u0627\u0648\u06d5.',
    '\u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u06ce\u06a9 \u0644\u06d5 \u06af\u0631\u06c6\u06cc \u06a9\u0631\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u062f\u0627\u06cc\u06d5.',
    '\u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u062f\u06d5\u0633\u062a\u06d5 \u0695\u0627\u0633\u062a\u06cc\u06d5\u06a9\u0627\u0646\u060c',
    '\u0644\u06d5\u0646\u0627\u0648 \u0628\u0627\u062e\u0627\u0646 \u067e\u0631\u0633\u06ce \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u060c',
    '\u0633\u06d5\u0628\u0627\u0631\u06d5\u062a \u0628\u06d5 \u06af\u0648\u0646\u0627\u0647\u0628\u0627\u0631\u0627\u0646:',
    '\u0686\u06cc \u0626\u06ce\u0648\u06d5\u06cc \u062f\u06d5 \u062f\u06c6\u0632\u06d5\u0642 \u06a9\u06ce\u0634\u0627\u061f',
    '\u0626\u06ce\u0698\u0646: \u0626\u06ce\u0645\u06d5 \u0644\u06d5\u0695\u06ce\u0632\u06d5\u06cc \u0646\u0648\u06ce\u0698\u06a9\u06d5\u0631\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u06cc\u0646\u061b',
    '\u062e\u06c6\u0631\u0627\u06a9\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646 \u0646\u06d5\u062f\u06d5\u0628\u06d5\u062e\u0634\u06cc\u061b',
    '\u0644\u06d5 \u06a9\u06c6\u0695\u06cc \u0647\u06d5\u0631\u0632\u06d5\u0628\u06ce\u0698\u0627\u0646\u062f\u0627\u060c \u0626\u06ce\u0645\u06d5\u0634 \u0644\u06d5 \u0647\u06d5\u0631\u0632\u06d5\u0648\u06ce\u0698\u0627\u0646 \u0628\u0648\u0648\u06cc\u0646\u061b',
    '\u0628\u0627\u0648\u06d5\u0695\u06cc\u0634\u0645\u0627\u0646 \u0628\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u06cc\u06d5 \u0646\u06d5\u0628\u0648\u0648\u061b',
    '\u062a\u0627 \u0645\u0631\u062f\u0646\u0645\u0627\u0646 \u0644\u06ce \u0645\u06cc\u0648\u0627\u0646 \u0628\u0648\u0648.',
    '\u0626\u06cc\u062a\u0631 \u062a\u06a9\u0627\u06cc \u062a\u06a9\u0627\u06a9\u0627\u0631\u0627\u0646\u060c \u0647\u06cc\u0686 \u0628\u06d5\u0647\u0631\u06d5\u06cc\u06d5\u06a9\u06cc\u0627\u0646 \u0628\u06c6 \u0646\u0627\u062f\u0627.',
    '\u0686\u06cc\u06cc\u0627\u0646 \u0628\u0648\u0648\u060c \u0644\u06d5 \u0648\u06d5\u0628\u06cc\u0631\u0647\u06ce\u0646\u06d5\u0648\u06d5 \u0695\u06d5\u0648\u06cc\u0646\u06d5\u0648\u06d5\u061f',
    '\u062f\u06d5\u062a\u0648\u062a \u0626\u06d5\u0648\u0627\u0646 \u06af\u06c6\u0695\u06d5\u062e\u06d5\u0631\u06af\u06d5\u0644\u06cc \u0695\u06d5\u06a4\u06c6\u06a9\u0646\u060c',
    '\u06a9\u06d5 \u0644\u06d5 \u062a\u0631\u0633\u06cc \u0634\u06ce\u0631 \u0647\u06d5\u06b5\u0627\u062a\u0648\u0648\u0646.',
    '\u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0647\u06d5\u0631\u06cc\u06d5\u06a9 \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0648\u0627\u06cc\u0627\u0646 \u06af\u06d5\u0631\u06d5\u06a9 \u0628\u0648\u0648 \u0646\u0627\u0645\u06d5\u06cc \u0648\u06d5\u06a9\u0631\u06cc\u06cc \u0628\u06c6 \u0628\u06ce\u0646\u0646.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632\u060c \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u067e\u06d5\u0633\u06b5\u0627\u0646 \u0646\u0627\u062a\u0631\u0633\u0646.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5 \u0628\u06cc\u0631\u0647\u06ce\u0646\u06d5\u0631\u06d5.',
    '\u06a9\u06ce \u06a9\u06d5\u06cc\u0641\u06cc \u0644\u06ce \u0628\u06ce \u0648\u06d5\u06cc\u062e\u0648\u06ce\u0646\u06ce.',
    '\u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc\u0634 \u0646\u0627\u06cc\u062e\u0648\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5 \u0645\u06d5\u06af\u06cc\u0646 \u062e\u0648\u062f\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce. \u062e\u0648\u062f\u0627 \u0634\u06cc\u0627\u0648\u06cc \u0644\u06ce \u062a\u0631\u0633\u0627\u0646\u06d5 \u0648 \u0628\u06c6 \u062e\u06d5\u062a\u0627\u0628\u06d5\u062e\u0634\u06cc\u0634 \u0647\u06ce\u0698\u0627\u06cc\u06d5.',
    '\u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5\u0645 \u0628\u06d5 \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627\u060c',
    '\u0633\u0648\u06ce\u0646\u062f\u06cc\u0634 \u062f\u06d5\u062e\u06c6\u0645 \u0628\u06d5 \u0648\u06ce\u0698\u062f\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u0695\u06d5\u062e\u0646\u06d5\u06af\u0631\u060c',
    '\u0686\u06c6\u0646 \u0645\u0631\u06c6 \u0648\u0627 \u062e\u06d5\u06cc\u0627\u06b5 \u062f\u06d5\u06a9\u0627 \u0626\u06ce\u0645\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u067e\u06ce\u0634\u06d5\u06af\u06d5\u0644\u06cc \u0644\u06d5 \u06cc\u06d5\u06a9\u062f\u06cc \u06a9\u06c6 \u0646\u0627\u06a9\u06d5\u06cc\u0646\u06d5\u0648\u06d5\u061f',
    '\u0628\u06d5\u06b5\u06a9\u0648\u0648 \u062a\u0648\u0627\u0646\u0627\u06cc\u06cc\u0645\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0633\u06d5\u0631\u067e\u06d5\u0646\u062c\u06d5\u06a9\u0627\u0646\u06cc\u0634\u06cc \u0633\u0627\u0632 \u06a9\u06d5\u06cc\u0646.',
    '\u0646\u06d5\u062e\u06ce\u0631 \u0645\u0631\u06c6 \u0647\u06d5\u0631 \u062f\u06d5\u06cc\u06d5\u0648\u06ce \u062a\u0627\u0648\u0627\u0646 \u0628\u062e\u0627\u062a\u06d5 \u067e\u06ce\u0634\u06d5 \u062e\u06c6\u06cc.',
    '\u0626\u06d5\u0648 \u062f\u06d5\u067e\u0631\u0633\u06ce: \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u0695\u0627\u0628\u0648\u0648\u0646 \u06a9\u06d5\u06cc \u062f\u06ce\u061f',
    '\u0647\u06d5\u0631\u06af\u0627 \u0686\u0627\u0648 \u0628\u06d5 \u0645\u06c6\u0644\u06d5\u0642 \u0648\u06d5\u0633\u062a\u0627\u061b',
    '\u0645\u0627\u0646\u06af\u06cc\u0634 \u06af\u06cc\u0631\u0627\u061b',
    '\u062e\u06c6\u0631 \u0648 \u0645\u0627\u0646\u06af\u06cc\u0634 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u06a9\u06c6 \u06a9\u0631\u0627\u0646\u06d5\u0648\u06d5\u061b',
    '\u0645\u0631\u06c6 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u062f\u0628\u06ce\u0698\u06ce: \u0695\u06ce\u06af\u06d5\u06cc \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06ce \u06a9\u0648\u0627\u0646\u06ce\u061f',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632\u060c \u067e\u06d5\u0646\u0627\u06af\u0627\u06cc\u06d5\u06a9 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0645\u06d5\u06a9\u06c6 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0645\u0631\u06c6 \u0647\u06d5\u0648\u0627\u06b5\u06cc \u067e\u06ce \u062f\u06d5\u062f\u0631\u06ce\u060c \u0686\u06cc\u06cc \u067e\u06ce\u0634\u06d5\u06a9\u06cc \u0628\u06d5\u0695\u06ce \u06a9\u0631\u062f \u0648 \u0686\u06cc\u0634\u06cc \u0644\u06d5\u062f\u0648\u0627 \u0628\u06d5\u062c\u06ce \u0645\u0627\u0648\u06d5.',
    '\u0646\u06d5\u062e\u06ce\u0631 \u0645\u0631\u06c6 \u0628\u06d5 \u0648\u06cc\u0698\u062f\u0627\u0646\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0632\u0627\u0646\u06ce\u061b',
    '\u0628\u0627 \u06af\u06d5\u0644\u06ce\u06a9\u06cc\u0634 \u0628\u0695 \u0648 \u0628\u06cc\u0627\u0646\u0648 \u0628\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u062a\u06c6 \u0632\u0645\u0627\u0646\u06cc \u0644\u06ce \u0645\u06d5\u062e\u06d5 \u06af\u06d5\u0695\u060c \u062a\u0627 \u0628\u06d5\u067e\u06d5\u0644\u06d5 \u0628\u06cc\u062e\u0648\u06ce\u0646\u06cc\u06d5\u0648\u06d5\u061b',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0631\u06a9\u06cc \u06a9\u06c6\u06a9\u0631\u062f\u0646 \u0648 \u0648\u06d5\u062e\u0648\u06ce\u0646\u062f\u0646\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0645\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0626\u06ce\u0645\u06d5 \u062e\u0648\u06ce\u0646\u062f\u0645\u0627\u0646\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627\u06a9\u06d5 \u062a\u06c6 \u0644\u06d5\u062f\u0648\u0627\u06cc\u06cc\u062f\u0627 \u0628\u06cc\u062e\u0648\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u067e\u0627\u0634\u0627\u0646\u06cc\u0634 \u0634\u06cc \u06a9\u0631\u062f\u0646\u06d5\u0648\u06d5\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0645\u06d5\u06cc\u06d5.',
    '\u0646\u06d5\u062e\u06ce\u0631\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0647\u0648\u0648\u0646 \u0647\u06d5\u0631 \u062f\u0646\u06cc\u0627\u062a\u0627\u0646 \u062e\u06c6\u0634 \u062f\u06d5\u0648\u06ce\u061b',
    '\u062f\u0648\u0627\u0695\u06c6\u0698\u06cc\u0634 \u0648\u06d5\u0644\u0627 \u062f\u06d5\u0646\u06ce\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0641\u0631\u06d5 \u0695\u0648\u0648\u0647\u06d5\u0646 \u06af\u06d5\u0634\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u061b',
    '\u0686\u0627\u0648 \u062f\u06d5\u06af\u06ce\u0695\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646.',
    '\u062f\u06cc\u0645\u06cc\u0634 \u0647\u06d5\u0646\u06d5 \u062a\u0631\u0634 \u0648\u062a\u0641\u062a\u0646\u061b',
    '\u0686\u0627\u0648\u06d5\u0646\u06c6\u0695\u0646 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc \u06a9\u06d5\u0645\u06d5\u0631\u0634\u06a9\u06ce\u0646\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631 \u0628\u06ce\u0646\u0646.',
    '\u0646\u06d5\u062e\u06ce\u0631\u060c \u0647\u06d5\u0631 \u0628\u06d5\u062e\u06c6 \u0646\u0627\u0632\u0627\u0646\u06ce \u062a\u0627 \u0645\u06d5\u0631\u06af \u0628\u06d5\u0631\u0628\u06cc\u0646\u06af\u06cc \u062f\u06d5\u06af\u0631\u06ce.',
    '\u0648\u062a\u0631\u0627\u0648\u06cc\u0634\u06d5: \u06a9\u06d5\u0633\u06cc \u0641\u0631\u06cc\u0627\u0695\u06d5\u0633\u06cc \u06a9\u06ce\u06cc\u06d5\u061f',
    '\u0644\u06ce\u0634\u06cc \u0695\u0648\u0648\u0646\u06d5 \u0686\u0627\u062e\u06cc \u062c\u06cc\u0627 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u06cc\u06d5.',
    '\u0644\u06cc\u0646\u06af \u0648 \u0644\u06d5\u062a\u06d5\u0631\u06cc\u0634\u06cc\u0627\u0646 \u062a\u06ce\u06a9 \u0647\u0627\u06b5\u0627\u0646.',
    '\u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc\u06d5 \u062c\u06ce \u0647\u0627\u0698\u0648\u0648\u062a\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u06cc\u06d5.',
    '\u0646\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u0695\u0648\u0627\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5 \u0648 \u0646\u06d5 \u0646\u0645\u06ce\u0698\u06cc\u0634\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0648\u062a\u0648\u0648\u06cc\u06d5 \u062f\u0631\u06c6\u06cc\u06d5 \u0648 \u062e\u06c6\u06cc \u0628\u0648\u0627\u0631\u062f\u0648\u0648\u06d5.',
    '\u067e\u0627\u0634\u06cc - \u0628\u06d5 \u062e\u06c6\u0628\u0627\u062f\u0627\u0646\u06d5\u0648\u06d5 - \u0686\u0648\u0648 \u0628\u06c6 \u0644\u0627\u06cc \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06d5\u06a9\u06d5\u06cc.',
    '\u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u062a\u06c6 \u0634\u06cc\u0627\u0648\u062a\u0631 \u0648 \u0644\u06d5\u0628\u0627\u0631\u062a\u0631\u06d5.',
    '\u062c\u0627\u0631\u06ce \u062a\u0631\u06cc\u0634\u060c \u0626\u06d5\u0645\u06d5 \u0628\u06c6 \u062a\u06c6 \u0634\u06cc\u0627\u0648\u062a\u0631 \u0648 \u0644\u06d5\u0628\u0627\u0631\u062a\u0631\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0645\u0631\u06c6 \u0647\u06d5\u0631 \u0644\u0627\u06cc \u0648\u0627\u06cc\u06d5 \u0628\u06d5\u062e\u06c6\u0695\u0627\u06cc\u06cc \u0648\u06b5\u06cc \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648 \u062f\u06b5\u06c6\u067e\u06ce\u06a9 \u0646\u06d5\u0628\u0648\u0648 \u06a9\u06d5 \u062f\u06d5\u0695\u0698\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u062a\u06c6\u0644\u06d5\u0648\u06d5\u061f',
    '\u062f\u0648\u0627\u06cc\u06d5 \u0628\u0648\u0648 \u0628\u06d5 \u062c\u06d5\u06b5\u062a\u06d5 \u062e\u0648\u06ce\u0646 \u0648 \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f \u0648 \u0633\u0627\u0632\u06cc \u062f\u0627\u061f',
    '\u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u062a\u0646\u06c6\u06a9\u06d5\u0648\u06d5 \u062c\u0648\u0648\u062a\u06d5\u06cc \u0646\u06ce\u0631 \u0648 \u0645\u06ce\u0648\u06cc \u0686\u06ce \u06a9\u0631\u062f.',
    '\u0626\u0627\u062e\u06c6 \u06a9\u06d5\u0633\u06ce \u0648\u0627 \u0646\u0627\u062a\u0627\u0646\u06ce \u0645\u0631\u062f\u0648\u0648 \u0632\u06cc\u0646\u062f\u0648\u0648 \u0628\u06a9\u0627\u062a\u06d5\u0648\u06d5\u061f',
    '\u0628\u06ce\u06af\u0648\u0645\u0627\u0646\u06d5 \u0645\u0627\u0648\u06d5\u06cc\u06d5\u06a9 \u0644\u06d5\u0645 \u0695\u06c6\u0698\u06af\u0627\u0631\u06d5 \u0628\u06d5\u0633\u06d5\u0631 \u0645\u0631\u06c6\u062f\u0627 \u062a\u06ce\u067e\u06d5\u0695\u06cc \u0648 \u0626\u06d5\u0648 \u0634\u062a\u06ce \u0646\u06d5\u0628\u0648\u0648 \u0628\u0627\u0633 \u0628\u06a9\u0631\u06ce.',
    '\u0626\u06ce\u0645\u06d5 \u0645\u0631\u06c6\u0646 \u0644\u06d5 \u062a\u0646\u06c6\u06a9\u06ce\u06a9\u06cc \u0626\u0627\u0648\u06ce\u062a\u06d5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627 \u062a\u0627 \u0628\u06cc\u0626\u06d5\u0632\u0645\u0648\u0648\u06cc\u0646\u061b \u06af\u0648\u06ce\u06cc \u0628\u06cc\u0633\u062a\u0646 \u0648 \u0633\u06c6\u0645\u0627\u06cc \u062f\u06cc\u062a\u0646\u06cc\u0634\u0645\u0627\u0646 \u067e\u06ce \u062f\u0627.',
    '\u062e\u06c6\u0645\u0627\u0646 \u0695\u06ce\u06af\u06d5\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u061b \u062c\u0627 \u06cc\u0627\u0646 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0628\u0698\u06ce\u0631\u06d5 \u06cc\u0627\u0646 \u067e\u06ce\u0646\u06d5\u0632\u0627\u0646.',
    '\u0626\u06ce\u0645\u06d5 \u0632\u0646\u062c\u06cc\u0631 \u0648 \u062a\u06c6\u0642\u06ce\u06a9\u06cc \u06af\u06d5\u0644\u06ce \u0632\u06c6\u0631 \u0648 \u0626\u0627\u06af\u0631\u06ce \u0628\u06b5\u06ce\u0633\u06d5\u062f\u0627\u0631\u0645\u0627\u0646 \u0628\u06c6 \u0628\u06ce\u062f\u06cc\u0646\u06d5\u06a9\u0627\u0646 \u0633\u0627\u0632 \u062f\u0627\u0648\u06d5.',
    '\u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u0644\u06d5 \u0628\u0627\u062f\u06d5\u06cc\u06d5\u06a9 \u062f\u06d5\u062e\u06c6\u0646\u06d5\u0648\u06d5\u060c \u0626\u0627\u0648\u06cc\u062a\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u0686\u06d5\u0645\u06d5\u06cc \u06a9\u0627\u0641\u0648\u0648\u0631\u06d5\u0648\u06d5\u06cc\u06d5.',
    '\u0686\u06d5\u0645\u06d5\u06cc\u06ce\u06a9\u06d5 \u0639\u06d5\u0628\u062f\u0627\u0646\u06cc \u062e\u0648\u0627 \u0644\u06d5\u0648 \u062f\u06d5\u0646\u06c6\u0634\u0646\u061b \u0628\u06d5 \u0641\u06cc\u0686\u0642\u06d5\u0634 \u0647\u06d5\u06b5\u06cc\u062f\u06d5\u0642\u0648\u06b5\u06ce\u0646\u0646.',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u062e\u06c6 \u062f\u06d5\u06af\u0631\u0646 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u061b \u062a\u0631\u0633\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5 \u0695\u06c6\u0698\u06ce \u0647\u06d5\u06cc\u06d5 \u06a9\u06d5 \u0628\u06d5\u06b5\u0627\u06a9\u06d5\u06cc \u0628\u06c6 \u06af\u0644\u0627 \u0626\u06d5\u062a\u06d5\u0646\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0648 \u062e\u06c6\u0631\u0627\u06a9\u06d5\u06cc \u06a9\u06d5 \u062e\u06c6\u06cc\u0627\u0646 \u062d\u06d5\u0632\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06d5 \u0647\u06d5\u0698\u0627\u0631 \u0648 \u0628\u06d5 \u0647\u06d5\u062a\u06cc\u0648 \u0648 \u0628\u06d5 \u062f\u06cc\u0644 \u06af\u06cc\u0631\u0627\u0648\u06cc \u062f\u06d5\u0628\u06d5\u062e\u0634\u0646.',
    '\u0626\u06ce\u0645\u06d5 - \u0647\u06d5\u0631 \u0644\u06d5 \u0695\u0627\u0647\u06cc \u062e\u0648\u062f\u0627 - \u0626\u06d5\u0645 \u062e\u06c6\u0631\u0627\u06a9\u06d5\u062a\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646\u06ce\u060c \u0646\u06d5 \u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u0645\u0627\u0646 \u0644\u06ce\u0648 \u062f\u06d5\u0648\u06ce \u0648 \u0646\u06d5 \u0634\u0648\u06a9\u0631\u0627\u0646\u06d5\u0634.',
    '\u0626\u06ce\u0645\u06d5 \u062a\u0631\u0633\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062e\u06c6\u0645\u0627\u0646 \u0647\u06d5\u06cc\u06d5 \u0644\u06d5 \u0695\u06c6\u0698\u06ce\u06a9\u0627 \u06a9\u06d5 \u0632\u06c6\u0631 \u062a\u0648\u0648\u0634 \u0648 \u0646\u06d5\u06af\u0628\u06d5\u062a \u0648 \u067e\u0695 \u0645\u06d5\u062a\u0631\u0633\u06cc\u06cc\u06d5.',
    '\u062e\u0648\u062f\u0627 \u0644\u06d5 \u0628\u06d5\u06b5\u0627\u06cc \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u067e\u0627\u0631\u0627\u0633\u062a\u0646\u06cc \u0648 \u062a\u0648\u0648\u0634\u06cc \u06af\u06d5\u0634\u06d5 \u0648 \u062e\u06c6\u0634\u06cc\u06cc \u06a9\u0631\u062f\u0646.',
    '\u0686\u0648\u0646\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u062e\u06c6\u0695\u0627\u06af\u0631 \u0628\u0648\u0648\u0646 \u060c \u0644\u06d5 \u067e\u0627\u062f\u0627\u0634\u0627 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0648 \u062d\u06d5\u0631\u06cc\u0631\u06cc \u067e\u06ce \u062f\u0627\u0646.',
    '\u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u0627 \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06d5\u062e\u062a \u067e\u0627\u06b5 \u0626\u06d5\u06a9\u06d5\u0641\u0646 \u0648 \u0646\u06d5 \u062a\u06cc\u0646\u06cc \u062a\u0627\u0648\u06ce \u062f\u06d5\u0628\u06cc\u0646\u0646\u060c \u0646\u06d5 \u0633\u06d5\u0631\u0645\u0627\u06cc\u06ce.',
    '\u0633\u06ce\u0648\u06d5\u0631\u06cc \u062f\u0631\u06d5\u062e\u062a\u06d5\u06a9\u0627\u0646\u06cc \u0628\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u0627 \u0634\u06c6\u0695 \u0628\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0645\u06cc\u0648\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0644\u06d5\u0628\u06d5\u0631 \u062f\u06d5\u0633\u062a\u06d5.',
    '\u0628\u06d5 \u062f\u06d5\u0641\u0631\u06cc \u0632\u06ce\u0648 \u0648 \u0628\u06d5\u0686\u06d5\u0646\u062f\u06cc\u0646 \u0632\u06d5\u0631\u06a9\u06cc \u0645\u06cc\u0646\u0627\u060c \u0628\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646.',
    '\u0632\u06d5\u0631\u06a9\u06cc \u0632\u06ce\u0648\u06cc\u0646 \u06a9\u06d5 \u0695\u0627\u0633 \u0628\u06d5 \u0642\u0627\u0633 \u0633\u0627\u0632\u06cc\u0627\u0646 \u062f\u0627\u0648\u0646.',
    '\u0628\u06d5\u0648 \u0632\u06d5\u0631\u06a9\u0627\u0646\u06d5 \u0628\u0627\u062f\u06d5\u06cc\u06ce\u06a9\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u0646\u06c6\u0634\u06cc\u0646\u060c \u0626\u0627\u0648\u06cc\u062a\u06d5\u06a9\u06d5\u06cc \u0632\u06d5\u0646\u062c\u06d5\u0641\u06cc\u0644\u06d5.',
    '\u0686\u06d5\u0645\u06d5\u06cc\u06ce\u06a9\u06d5 \u0628\u06d5\u0646\u0627\u0648\u06cc\u062f\u0627 \u0695\u06d5\u0648\u0627\u0646 \u0626\u06d5\u0628\u06ce\u061b \u0646\u0627\u0648 \u0646\u0631\u0627\u0648\u06d5 \u0628\u06d5 \u0633\u06d5\u0644\u0633\u06d5\u0628\u06cc\u0644.',
    '\u0644\u0627\u0648\u0627\u0646\u06ce - \u06a9\u06d5 \u0647\u06d5\u0631 \u0628\u06d5 \u0644\u0627\u0648\u06cc \u062f\u06d5\u0645\u06ce\u0646\u0646\u06d5\u0648\u06d5 - \u0628\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0626\u06d5\u06af\u06d5\u0695\u06ce\u0646\u061b \u06af\u06d5\u0631 \u0628\u06cc\u0627\u0646\u0628\u06cc\u0646\u06cc \u0648\u0627 \u0626\u06d5\u0632\u0627\u0646\u06cc \u0645\u0631\u0627\u0631\u06cc \u067e\u0695\u0698\u06ce\u0646\u062f\u0631\u0627\u0648\u0646.',
    '\u06af\u06d5\u0631 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06cc\u060c \u0647\u06d5\u0631 \u062e\u06ce\u0631 \u0648 \u062e\u06c6\u0634\u06cc \u062f\u06d5\u0628\u06cc\u0646\u06cc \u0648 \u0642\u06d5\u06b5\u06d5\u0645\u0695\u06d5\u0648\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u06af\u0648\u0634\u0627\u062f.',
    '\u067e\u0627\u0631\u0686\u06d5\u06cc \u062a\u06d5\u0646\u06a9 \u0648 \u0633\u0641\u062a\u06cc \u06a9\u06d5\u0633\u06a9\u06cc\u0627\u0646 \u062f\u06d5\u0628\u06d5\u0631\u062f\u0627\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0647\u06d5\u06a4\u0631\u06d5\u0645\u06cc\u0634 \u0686\u06ce \u0628\u0648\u0648\u06d5 \u0648 \u0628\u06d5 \u0632\u06c6\u0631 \u0645\u0648\u0648\u0686\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0632\u06ce\u0648\u06cc\u0634 \u0695\u0627\u0632\u0627\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646 \u0628\u0627\u062f\u06d5\u06cc \u067e\u0627\u06a9\u06cc \u067e\u06ce \u0646\u06c6\u0634\u0627\u0648\u0646.',
    '\u0626\u06d5\u0645\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u06ce\u0648\u06d5\u06cc\u06d5 \u0648 \u062a\u06d5\u0642\u06d5\u0644\u0627\u062a\u0627\u0646 \u0634\u0627\u06cc\u0627\u0646\u06cc \u0695\u06d5\u0632\u0627\u0645\u06d5\u0646\u062f\u06cc\u06cc\u06d5.',
    '\u0626\u06ce\u0645\u06d5\u06cc\u0646 \u06a9\u06d5 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5\u0645\u0627\u0646 - \u0648\u0631\u062f\u06d5 \u0648\u0631\u062f\u06d5 \u0648 \u0628\u06d5\u0634 \u0628\u06d5\u0634 - \u0628\u06c6 \u062a\u06c6 \u0647\u06d5\u0646\u0627\u0631\u062f\u0648\u0648\u06d5.',
    '\u0633\u0627 \u062e\u06c6 \u0628\u06af\u0631\u06d5 \u0628\u06c6 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u061b \u0647\u06d5\u0631\u06af\u06cc\u0632 \u06af\u0648\u06ce \u0644\u06d5\u0648\u0627\u0646\u06d5 \u0645\u06d5\u06af\u0631\u06d5 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631 \u06cc\u0627 \u067e\u06ce\u0646\u06d5\u0632\u0627\u0646\u0646.',
    '\u0647\u06d5\u0631 \u0646\u0627\u0648\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a \u0628\u06d5\u06cc\u0627\u062f \u0628\u06a9\u06d5\u061b \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5\u0631\u06d5\u0628\u06d5\u06cc\u0627\u0646\u0627\u0646 \u0648 \u0626\u06ce\u0648\u0627\u0631\u0627\u0646\u06cc\u0634.',
    '\u0628\u06d5 \u0634\u06d5\u0648\u06cc\u0634 \u0633\u0648\u0698\u062f\u06d5\u06cc \u0628\u06c6 \u0628\u06d5\u0631\u06d5 \u0648 \u0644\u06d5 \u062f\u0631\u06ce\u0698\u0627\u06cc\u06cc\u06cc \u0634\u06d5\u0648\u06af\u0627\u0631\u06cc\u0634\u062f\u0627 \u0647\u06d5\u0631 \u067e\u06d5\u0633\u0646\u06cc \u067e\u0627\u06a9\u06cc\u06cc \u0626\u06d5\u0648 \u0628\u062f\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646 \u062d\u06d5\u0632\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5 \u062f\u0646\u06cc\u0627\u06cc \u0628\u06ce\u0628\u06d5\u0642\u0627\u06cc\u06d5 \u0648 \u0695\u06c6\u0698\u06ce \u06af\u0631\u0627\u0646 \u062f\u06d5\u062e\u06d5\u0646\u06d5 \u067e\u0627\u0634 \u062e\u06c6\u06cc\u0627\u0646\u06d5\u0648\u06d5!',
    '\u0626\u06ce\u0645\u06d5 \u0648\u06d5\u062f\u06cc\u0645\u0627\u0646 \u0647\u06ce\u0646\u0627\u0648\u0646 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u0626\u0627\u0632\u0627\u06cc \u0628\u06d5\u0646\u062f\u06d5\u0646\u06cc\u0627\u0646\u0645\u0627\u0646 \u062f\u0627\u067e\u06ce\u0686\u0627\u0648\u0646\u061b \u0647\u06d5\u0631\u06af\u0627\u0634 \u062d\u06d5\u0632 \u06a9\u06d5\u06cc\u0646\u060c \u0628\u06d5 \u0686\u06cc\u0646\u06ce\u06a9\u06cc \u0648\u06d5\u06a9 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0627\u0646 \u0648\u06d5\u062f\u06d5\u06af\u06c6\u0695\u06cc\u0646.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u06cc\u0631\u0647\u06ce\u0646\u06d5\u0631\u06ce\u06a9\u0646\u060c \u0647\u06d5\u0631\u06a9\u06ce \u062d\u06d5\u0632 \u06a9\u0627 \u0695\u06ce\u06af\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u06d5\u062f\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u062d\u06d5\u0632 \u06a9\u0631\u062f\u0646\u06cc\u0634 \u0628\u06d5\u0646\u06af\u06c6 \u0646\u06cc\u06cc\u06d5\u060c \u062c\u0627 \u0645\u06d5\u06af\u06cc\u0646 \u062e\u0648\u062f\u0627 \u062d\u06d5\u0632 \u0628\u06a9\u0627. \u0632\u0627\u0646\u0627\u06cc \u06a9\u0627\u0631\u0632\u0627\u0646 \u0647\u06d5\u0631 \u062e\u06c6\u06cc\u06d5\u062a\u06cc.',
    '\u0647\u06d5\u0631\u06a9\u06ce\u06cc \u062d\u06d5\u0632 \u06a9\u0627 \u0648\u06d5\u0628\u06d5\u0631 \u0628\u06d5\u0632\u06d5\u06cc\u06cc\u06cc \u062e\u06c6 \u062f\u06d5\u062e\u0627 \u0648 \u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0646\u0627\u0647\u06d5\u0642\u0646\u060c \u062c\u0632\u06cc\u0627\u06cc \u0628\u06d5\u0698\u0627\u0646\u06cc \u0633\u0627\u0632 \u062f\u0627\u0648\u06d5.',
    '\u0633\u0648\u06ce\u0646\u062f \u0628\u06d5\u0648\u0627\u0646\u06d5\u06cc - \u06cc\u06d5\u06a9 \u0644\u06d5 \u062f\u0648\u0627\u06cc \u06cc\u06d5\u06a9 - \u0646\u0627\u0631\u062f\u0631\u0627\u0648\u0646\u060c',
    '\u0628\u06d5\u0648 \u0628\u0627 \u062a\u0648\u0646\u062f\u0627\u0646\u06d5\u0634 \u0647\u06d5\u06b5\u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06d5 \u0647\u06d5\u06b5\u06a9\u0631\u062f\u0646\u060c',
    '\u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0626\u06d5\u067e\u0695\u0698\u06ce\u0646\u0646\u060c \u0628\u06d5\u067e\u0695\u0698\u0627\u0646\u062f\u0646\u060c',
    '\u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u06d5 \u0647\u06d5\u06b5\u0648\u06d5\u0698\u0627\u0631\u062f\u0646 \u0647\u06d5\u06b5\u062f\u0627\u0648\u06ce\u0631\u0646\u060c',
    '\u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u06cc\u0627\u062f\u06cc \u062e\u0648\u062f\u0627 \u062f\u06d5\u062e\u06d5\u0646\u06d5 \u0628\u06d5\u0631 \u062f\u06b5\u0627\u0646\u06d5\u0648\u06d5\u060c',
    '\u0628\u06c6 \u0628\u06d5\u0647\u0627\u0646\u06d5 \u0644\u06ce \u0642\u06d5\u062a\u0627\u0646\u062f\u0646 \u06cc\u0627\u0646 \u062a\u0631\u0633\u0627\u0646\u062f\u0646\u061b',
    '\u06a9\u06d5 \u0647\u06d5\u0631 \u0686\u06cc \u0633\u06c6\u0632\u0648\u0648 \u067e\u06ce \u062f\u06d5\u062f\u0631\u06ce\u060c \u0647\u06d5\u0631 \u062f\u06ce\u062a\u06d5 \u062c\u06ce.',
    '\u0633\u0627 \u0647\u06d5\u0631 \u062f\u06d5\u0645\u06ce \u0626\u06d5\u0633\u062a\u06ce\u0631\u06d5\u06a9\u0627\u0646 \u06a9\u0648\u0698\u0627\u0646\u06d5\u0648\u06d5\u060c',
    '\u0647\u06d5\u0631\u06af\u0627\u0634 \u06a9\u06d5\u0644\u06ce\u0646 \u062f\u06d5\u06a9\u06d5\u0648\u06ce\u062a\u06d5 \u0639\u06d5\u0632\u0645\u0627\u0646\u06d5\u0648\u06d5\u060c',
    '\u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u06a9\u06d5\u0698\u0627\u0646 \u0647\u06d5\u0698\u0627\u0646 \u0648 \u062a\u06ce\u06a9 \u0695\u0648\u0648\u062e\u0627\u0646\u060c',
    '\u0647\u06d5\u0631\u06af\u0627\u0634 \u0648\u0627\u062f\u06d5 \u0628\u06c6 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u0627\u0646 \u062f\u0627\u0646\u062f\u0631\u0627\u060c',
    '\u0628\u06c6 \u06a9\u0627\u0645\u06d5 \u0695\u06c6\u0698 \u062f\u0648\u0627\u062e\u0631\u0627\u0648\u06d5\u061f',
    '\u0628\u06c6 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u06b5\u0627\u0648\u0627\u0631\u062f\u0646\u06ce.',
    '\u06a9\u0648\u0648 \u0632\u0627\u0646\u06cc\u0648\u062a\u06d5 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u06b5\u0627\u0648\u0627\u0631\u062f\u0646 \u0686\u06cc\u06cc\u06d5\u061f',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646.',
    '\u0626\u06d5\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u0645\u0627\u0646 \u0642\u0695 \u0646\u06d5\u06a9\u0631\u062f\u061f',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0634\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u067e\u0627\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u0634 \u0628\u06d5 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06d5\u06a9\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0648\u06d5\u0646 \u0644\u06d5 \u0626\u0627\u0648\u06ce\u06a9\u06cc \u0628\u06ce\u0628\u0627\u06cc\u06d5\u062e \u0648\u06d5\u062f\u06cc \u0646\u06d5\u0647\u06ce\u0646\u0627\u061f',
    '\u0644\u06d5 \u062c\u06ce\u06cc\u06d5\u06a9\u06cc \u067e\u062a\u06d5\u0648\u062f\u0627 \u062c\u06ce\u0646 \u0646\u06d5\u06a9\u0631\u062f\u06d5\u0648\u06d5\u061f',
    '\u062a\u0627 \u0645\u0627\u0648\u06d5\u06cc\u06ce \u062f\u06cc\u0627\u0631\u06cc\u06a9\u0631\u0627\u0648\u061f',
    '\u062e\u06c6 \u062a\u0648\u0627\u0646\u06cc\u0645\u0627\u0646\u061b \u0626\u0627\u06cc \u06a9\u06d5 \u0686\u06d5\u0646\u062f\u06d5 \u0686\u0627\u06a9\u0645\u0627\u0646 \u062a\u0648\u0627\u0646\u06cc.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5\u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646.',
    '\u0626\u06d5\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u0632\u06d5\u0645\u06cc\u0646\u0645\u0627\u0646 \u0646\u06d5\u06a9\u0631\u062f\u06d5 \u0645\u06d5\u06a9\u06c6\u061f',
    '\u0628\u06c6 \u0698\u06cc\u0627\u0648\u0627\u0646 \u0648 \u0646\u06d5\u0645\u0627\u0648\u0627\u0646\u061f',
    '\u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce\u0634\u062f\u0627 \u0686\u06d5\u0646 \u0686\u06cc\u0627\u06cc \u0628\u06d5\u0631\u0632\u0646 \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0646\u062f \u0648 \u0626\u0627\u0648\u06cc \u062e\u06c6\u0634\u0646 \u067e\u06ce \u0646\u06c6\u0634\u0627\u0646\u062f\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646.',
    '\u0648\u06d5 \u0695\u06ce \u06a9\u06d5\u0648\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u0634\u062a\u06ce \u06a9\u06d5 \u0628\u06d5 \u062f\u0631\u06c6\u062a\u0627\u0646 \u062f\u06d5\u0632\u0627\u0646\u06cc.',
    '\u0695\u06ce\u0648\u06d5 \u0628\u0686\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u0633\u06ce\u0628\u06d5\u0631\u06cc \u062f\u0648\u0648\u06a9\u06d5\u06b5\u06ce\u06a9\u06cc \u0633\u06ce \u0686\u0648\u06a9\u06b5\u06d5\u061b',
    '\u0633\u0627\u06cc\u06d5 \u0646\u0627\u062f\u0627 \u0648 \u0645\u0631\u06c6\u0634 \u0644\u06d5 \u06af\u0695 \u0648\u06d5\u0644\u0627 \u0646\u0627\u062f\u0627.',
    '\u0645\u0627\u06a9\u06cc \u062f\u0648\u0648\u06a9\u06d5\u06b5 \u067e\u0695\u06cc\u0634\u06a9\u06d5\u06cc \u0648\u0627 \u062f\u06d5\u0647\u0627\u0648\u06ce\u0698\u06ce \u06a9\u06c6\u0634\u06a9 \u0626\u0627\u0633\u0627\u06cc\u06cc\u061b',
    '\u0647\u06d5\u0631 \u062f\u06d5\u0628\u06ce\u0698\u06cc \u0648\u0634\u062a\u0631\u0627\u0646\u06cc \u0628\u06d5\u0631\u06af\u0646 \u0632\u06d5\u0631\u062f\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646.',
    '\u0626\u06d5\u0645\u0695\u06c6 \u0695\u06c6\u0698\u06ce\u06a9\u06d5 \u0646\u0627\u067e\u06d5\u06cc\u06a4\u0646.',
    '\u0695\u06ce\u06af\u06d5\u06cc \u062a\u06d5\u0648\u0627\u0632\u06cc\u0634\u06cc\u0627\u0646 \u0646\u0627\u062f\u06d5\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646.',
    '\u0626\u06d5\u0645\u0695\u06c6 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u06b5\u0627\u0648\u0627\u0631\u062f\u0646\u06d5\u061b \u0626\u06ce\u0648\u06d5\u0645\u0627\u0646 \u0648\u06d5\u0644 \u067e\u06ce\u0634\u06cc\u0646\u06cc\u0627\u0646 \u06a9\u06c6 \u06a9\u0631\u062f\u06d5\u0648\u06d5.',
    '\u0633\u0627 \u0626\u06d5\u06af\u06d5\u0631 \u0641\u06ce\u06b5\u06ce\u06a9\u062a\u0627\u0646 \u0647\u06d5\u06cc\u06d5\u060c \u0648\u06d5\u0631\u0646 \u0644\u06d5 \u062e\u06c6\u0645\u06cc \u062f\u06d5\u06a9\u0627\u0631 \u06a9\u06d5\u0646\u061b',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062e\u0648\u062f\u06ce\u062a\u0631\u0633\u0646\u060c \u0644\u06d5\u0646\u06ce\u0648\u0627\u0646\u06cc \u0633\u06ce\u0628\u06d5\u0631\u0627\u0646 \u0648 \u06a9\u0627\u0646\u06cc\u0627\u0648\u0627\u0646\u0646\u061b',
    '\u0645\u06cc\u0648\u06d5\u0647\u0627\u062a\u06cc\u0634 \u0647\u06d5\u0631\u0686\u06d5\u0646\u062f\u06cc \u062f\u06b5\u06cc\u0627\u0646 \u062f\u0628\u06ce\u0698\u06d5.',
    '\u0626\u06d5\u0645\u06d5\u0634 \u067e\u0627\u062f\u0627\u0634\u06cc \u0626\u0627\u06a9\u0627\u0631\u0648\u0648\u060c \u0633\u0627 \u0628\u062e\u06c6\u0646 \u0648 \u0628\u062e\u06c6\u0646\u06d5\u0648\u06d5\u061b \u0646\u06c6\u0634\u06cc \u06af\u06cc\u0627\u0646\u0648\u0648.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u0627\u0648\u0627 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0627\u0631\u0686\u0627\u06a9\u0627\u0646 \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646.',
    '\u0626\u06ce\u0648\u06d5\u0634 \u06af\u0631\u06c6\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u0646! \u0628\u062e\u06c6\u0646 \u0648 \u06a9\u06d5\u0645\u06ce \u0695\u0627\u0628\u0648\u06ce\u0631\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u067e\u06ce\u06cc\u0627\u0646 \u0628\u06ce\u0698\u06ce: \u06a9\u0695\u0646\u0648\u0648\u0634 \u0628\u06d5\u0631\u0646\u060c \u06a9\u0695\u0646\u0648\u0648\u0634 \u0646\u0627\u0628\u06d5\u0646.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u0628\u06ce\u0628\u0627\u0648\u06d5\u0695\u0627\u0646.',
    '\u062f\u06d5\u0633\u0627 \u0626\u06d5\u0648\u0627\u0646 - \u067e\u0627\u0634\u06cc \u0642\u0648\u0631\u0639\u0627\u0646 - \u0628\u06d5\u06a9\u0627\u0645 \u0642\u0633\u06d5 \u0628\u0695\u0648\u0627 \u062f\u06d5\u06a9\u06d5\u0646\u061f',
    '\u0644\u06d5 \u0686\u06cc \u0644\u06d5 \u06cc\u06d5\u06a9\u062f\u06cc \u062f\u06d5\u067e\u0631\u0633\u0646\u061f',
    '\u0644\u06d5 \u062f\u06d5\u0646\u06af\u0648\u0648\u0628\u0627\u0633\u06d5\u06a9\u06d5\u06cc \u0645\u06d5\u0632\u0646\u061b',
    '\u0626\u06d5\u0648\u06d5\u06cc \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u06cc \u0645\u0634\u062a\u0648\u0645\u0695\u06cc\u0627\u0646\u06d5.',
    '\u0646\u06d5\u062e\u06ce\u0631 [\u0647\u06cc\u0686 \u067e\u0631\u0633\u06cc \u067e\u06ce \u0646\u0627\u0648\u06ce] \u0632\u0648\u0648 \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u0647\u06d5\u0645\u06cc\u0633\u0627\u0646 [\u067e\u0631\u0633\u06cc \u067e\u06ce \u0646\u0627\u0648\u06ce] \u0632\u0648\u0648 \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u0626\u06d5\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u0646 \u0646\u06d5\u06a9\u0631\u062f\u06d5 \u0695\u0627\u06cc\u06d5\u062e\u061f',
    '\u06a9\u06d5\u0698 \u0648 \u0686\u06cc\u0627\u06a9\u0627\u0646\u06cc\u0634 \u0633\u06cc\u0646\u06af\u0627\u0646\u061f',
    '\u0626\u06ce\u0648\u06d5\u0634\u0645\u0627\u0646 \u062c\u0648\u0648\u062a \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u062e\u06d5\u0648\u06cc\u0634\u062a\u0627\u0646\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0647\u06c6\u06cc \u062d\u06d5\u0633\u0627\u0646\u06d5\u0648\u06d5\u062a\u0627\u0646.',
    '\u0634\u06d5\u0648\u06cc\u0634\u0645\u0627\u0646 \u062f\u0627\u0646\u0627 \u0628\u06c6 \u067e\u06c6\u0634\u0627\u06a9.',
    '\u0695\u06c6\u0698\u06cc\u0634\u0645\u0627\u0646 \u06a9\u0631\u062f\u06d5 \u0647\u06c6\u06cc \u0698\u06cc\u0627\u0646.',
    '\u0644\u06d5 \u0633\u06d5\u0631\u0648\u0648\u0634\u062a\u0627\u0646 \u062d\u06d5\u0648\u062a\u06cc \u067e\u062a\u06d5\u0648\u0645\u0627\u0646 \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0646\u062f.',
    '\u0686\u0631\u0627\u06cc\u06ce \u0632\u06c6\u0631 \u0628\u06d5 \u0648\u0631\u0634\u06d5\u0648\u0631\u0634\u0645\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u0644\u06d5\u0648 \u0647\u06d5\u0648\u0631\u0627\u0646\u06d5 - \u06a9\u06d5 \u0628\u0627\u0631\u0627\u0646\u06d5\u06cc\u0627\u0646 \u067e\u06ce\u0648\u06d5\u06cc\u06d5 - \u0626\u0627\u0648\u06cc \u0628\u06d5\u062e\u0648\u0695\u0645\u0627\u0646 \u062f\u0627\u0695\u0698\u0627\u0646\u062f\u061b',
    '\u0647\u06d5\u062a\u0627 \u062f\u06d5\u062e\u06b5 \u0648 \u0695\u0648\u06d5\u06a9\u06cc \u067e\u06ce \u0628\u062e\u06d5\u06cc\u0646\u06d5 \u0695\u0648\u0648.',
    '\u062f\u0627\u0631\u0633\u062a\u0627\u0646\u06cc \u0686\u0695 \u0648 \u067e\u0695\u06cc\u0634.',
    '\u0628\u06ce \u0633\u06c6 \u0695\u06c6\u0698\u06cc \u0647\u06d5\u06b5\u0627\u0648\u0627\u0631\u062f\u0646\u060c \u06a9\u0627\u062a\u06cc \u0628\u06c6 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc \u062f\u06d5 \u06a9\u06d5\u06b5\u06d5\u0634\u0627\u062e \u062f\u06d5\u0645\u06ce\u0646\u062f\u0631\u0627\u060c \u0626\u06d5\u0648\u0633\u0627 \u067e\u06c6\u0644 \u067e\u06c6\u0644 \u067e\u06d5\u06cc\u062f\u0627\u0648 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0645 \u0639\u06d5\u0632\u0645\u0627\u0646\u06d5 \u062f\u06d5\u06a9\u0631\u06ce\u062a\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u0631\u06af\u0627 \u062f\u06d5\u0631\u06af\u0627 \u062f\u06d5\u0628\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u06a9\u06d5\u0698\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u062e\u06c6 \u062e\u0632\u06ce\u0646\u0631\u0627\u0648\u0646 \u0648 \u06af\u0634\u062a\u06cc \u0628\u0648\u0648\u0646\u06d5 \u062a\u0631\u0627\u0648\u06cc\u0644\u06a9\u06d5.',
    '\u0628\u06ce\u0634\u06a9 \u062f\u06c6\u0698\u06d5\u0647\u0640 \u0628\u06d5\u0631\u06d5\u06af\u06d5\u06cc\u06d5\u061b',
    '\u0628\u06c6 \u0633\u06d5\u0631\u0628\u0632\u06ce\u0648\u06d5\u06a9\u0627\u0646\u060c \u062c\u06ce \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5\u06cc\u06d5.',
    '\u0644\u06d5\u0648\u06ce\u062f\u0627 \u0628\u06c6 \u0645\u0627\u0648\u06d5\u06cc\u06ce \u0632\u06c6\u0631 \u0642\u06d5\u062a\u06cc\u0633 \u062f\u06d5\u0628\u0646.',
    '\u0644\u06d5\u0648\u06ce\u062f\u0627 \u0646\u06d5 \u062e\u06d5\u0648 \u062f\u06d5\u0686\u06ce\u0698\u0646 \u0646\u06d5 \u06a4\u06d5\u062e\u0627\u0631\u0646.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u0627\u0648\u06d5\u06a9\u0648\u06b5 \u0648 \u0632\u0648\u0648\u062e\u0627\u0648.',
    '\u067e\u0627\u062f\u0627\u0634\u06ce\u06a9\u06cc \u0647\u06d5\u0645\u0628\u06d5\u0631 [\u0628\u06d5\u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646].',
    '\u0686\u0648\u0646 \u0626\u06d5\u0648\u0627\u0646 \u0647\u06ce\u06a4\u06cc\u062f\u0627\u0631 \u0646\u06d5\u0628\u0648\u0648\u0646 \u0644\u06ce\u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5\u06cc\u06d5\u06a9 \u0647\u06d5\u0628\u06ce.',
    '\u0646\u06cc\u0634\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u0627\u0646 - \u0632\u06c6\u0631 \u0628\u06d5 \u062a\u0648\u0646\u062f\u06cc - \u0628\u06d5 \u062f\u0631\u06c6 \u062f\u0627\u0646\u0627.',
    '\u0698\u0645\u0627\u0631\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a\u06cc\u0634\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06cc \u0644\u06d5\u0644\u0627\u06cc\u06d5.',
    '\u0633\u0627 \u0628\u0686\u06d5\u0698\u0646\u061b \u0626\u06d5\u0648\u06cc \u0628\u06c6\u062a\u0627\u0646\u06cc \u0632\u06cc\u0627\u062f \u0626\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u062f\u0627\u0646\u06d5.',
    '\u0628\u06c6 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u0627\u0646\u06cc\u0634 \u062c\u06ce\u06af\u06d5\u06cc \u0628\u06d5 \u0626\u0627\u0648\u0627\u062a \u06af\u06d5\u06cc\u06cc\u0634\u062a\u0646 \u0647\u06d5\u06cc\u06d5:',
    '\u0628\u0627\u063a\u0633\u062a\u0627\u0646 \u0648 \u0695\u06ce\u0632\u06cc \u0695\u06d5\u0632\u0627\u0646.',
    '\u067e\u06c6\u0644\u06d5 \u06a9\u06cc\u0698\u06cc \u0645\u06d5\u0645\u06a9 \u062e\u0695\u06cc \u0647\u0627\u0648\u062a\u06d5\u0645\u06d5\u0646\u06cc\u0634.',
    '\u067e\u06cc\u0627\u06b5\u06d5\u06cc \u067e\u0695\u06cc\u0634.',
    '\u0644\u06d5\u0648\u06ce \u0647\u06d5\u0648\u0627\u0646\u062a\u06d5 \u0646\u0627\u0628\u06cc\u0633\u0646 \u0648 \u06cc\u06d5\u06a9\u062f\u06cc\u0634 \u0646\u0627\u062e\u06d5\u0646\u06d5 \u062f\u0631\u06c6\u0648\u06d5.',
    '\u062e\u06d5\u06b5\u0627\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u060c \u0628\u06d5\u062e\u0634\u0634\u06cc \u0628\u06d5\u0633\u06d5\u0631\u0648\u0628\u06d5\u0631\u06d5.',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0639\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646 \u0648 \u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5\u0646\u0627\u0648 \u0626\u06d5\u0648 \u062f\u0648\u0627\u0646\u06d5\u06cc\u06d5\u061b \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0695\u06c6\u0632\u06cc \u062f\u06d5\u0631\u06d5\u060c \u0647\u06cc\u0686\u06cc\u0627\u0646 \u0646\u0627\u0634\u06ce\u0646 \u0628\u06cc\u062f\u0648\u0648\u0646\u0646 \u0648 \u0628\u06cc\u062f\u0648\u06ce\u0646\u0646.',
    '\u0695\u06c6\u0698\u06ce\u06a9\u06cc \u06a9\u06d5 \u062c\u0648\u0628\u0695\u0627\u0626\u06cc\u0644 \u0648 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646 \u0647\u06d5\u0645\u0648\u0648 \u0628\u06d5 \u0695\u06ce\u0632 \u0695\u0627\u0648\u06d5\u0633\u062a\u0627\u0648\u0646\u061b \u0646\u0627\u0626\u0627\u062e\u06ce\u0648\u0646 \u0645\u06d5\u06af\u06cc\u0646 \u06a9\u06d5\u0633\u06ce \u0644\u06d5\u0648\u0695\u0627 \u0648\u062f\u0645\u06cc \u067e\u06ce \u062f\u0631\u0627\u0628\u06ce\u060c \u0626\u06d5\u0648\u06cc\u0634 \u0642\u0633\u06ce\u06a9\u06cc \u0686\u0627\u06a9 \u0628\u06a9\u0627.',
    '\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0695\u06c6\u0698\u06cc \u0695\u0627\u0633\u062a\u06cc\u06cc\u06d5\u061b \u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u067e\u06ce\u06cc \u062e\u06c6\u0634\u06d5\u060c \u0628\u0627 \u0695\u06ce\u0628\u0627\u0632\u06cc \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u0628\u06af\u0631\u06ce\u062a\u06d5 \u0628\u06d5\u0631.',
    '\u0626\u06ce\u0645\u06d5 \u0644\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u06ce\u06a9\u06cc \u06a9\u06d5 \u0646\u0632\u06cc\u06a9\u06d5\u060c \u062a\u0631\u0633\u0646 \u0648\u06d5\u0628\u06d5\u0631 \u0626\u06ce\u0648\u06d5 \u0646\u0627\u0648\u06d5. \u0648\u06ce\u0695\u06c6 \u0645\u0631\u06c6 \u0686\u0627\u0648 \u0628\u06c6 \u0626\u06d5\u0648 \u0634\u062a\u06d5 \u062f\u06d5\u06af\u06ce\u0695\u06ce \u06a9\u06d5 \u067e\u06ce\u0634\u06d5 \u062e\u06c6\u06cc \u0646\u0627\u0631\u062f\u0648\u0648\u06cc\u06d5\u062a\u06cc. \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06cc\u0634 \u062e\u0648\u062f\u0627\u06cc \u0646\u06d5\u0646\u0627\u0633\u06cc \u0647\u06d5\u0631 \u062f\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u06a9\u0627\u0634\u06a9\u0627\u06cc\u06d5 \u0626\u0627\u062e \u0628\u0648\u0648\u0645\u0627\u06cc\u06d5.',
    '\u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5 \u0628\u06d5\u0648 \u0647\u06d5\u0633\u0627\u0631\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0647\u06d5\u06b5\u062f\u06ce\u0646 \u0648 \u0626\u0627\u0648\u0627 \u062f\u06d5\u0628\u0646\u060c',
    '\u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0644\u06d5 \u0626\u0627\u0633\u06c6\u06cc\u06ce \u0628\u06c6 \u0626\u0627\u0633\u06c6\u06cc\u06d5\u06a9\u06cc \u062a\u0631 \u062f\u06d5\u0686\u0646\u060c',
    '\u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0648\u06d5\u06a9 \u0645\u06d5\u0644\u06d5\u0648\u0627\u0646 \u0645\u06d5\u0644\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c',
    '\u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u06c6 \u067e\u06ce\u0634\u0628\u0695\u06a9\u06ce \u067e\u06d5\u0644\u06d5 \u062f\u06d5\u06a9\u06d5\u0646\u060c',
    '\u0633\u0648\u06ce\u0646\u062f \u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062a\u06d5\u06a4\u0628\u06cc\u0631\u06cc \u06a9\u0627\u0631\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u0646\u060c',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0644\u06d5\u0631\u0632\u06ce\u0646 [\u0632\u06d5\u0645\u06cc\u0646] \u062f\u06d5\u0644\u06d5\u0631\u0632\u06ce\u0646\u06ce.',
    '\u0626\u06cc\u062a\u0631\u06cc\u0634\u06cc \u0628\u06d5\u062f\u0648\u0648\u062f\u0627 \u062f\u06ce.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u062f\u06b5\u0627\u0646\u06ce\u06a9 \u0632\u06c6\u0631 \u0647\u06d5\u0631\u0627\u0633\u0627\u0646\u0646.',
    '\u0686\u0627\u0648\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0633\u06cc\u0633 \u0648 \u06a9\u0632\u0646.',
    '\u0626\u06ce\u0698\u0646: \u0626\u0627\u062e\u06c6 \u062f\u06d5\u0645\u0627\u0646\u06af\u06ce\u0695\u0646\u06d5\u0648\u06d5 \u0633\u06d5\u0631 \u0632\u06d5\u0645\u06cc\u0646\u061f',
    '\u0644\u06d5\u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5 \u06a9\u06d5 \u0686\u06d5\u0646\u062f \u067e\u06ce\u0634\u06d5\u06cc\u06ce \u06a9\u0644\u06c6\u0631 \u0628\u0648\u0648\u06cc\u0646\u061f',
    '\u0626\u06ce\u0698\u0646: \u0626\u06cc\u062a\u0631 \u0626\u06d5\u06af\u06d5\u0631 \u0648\u0627 \u0628\u06ce\u060c \u0626\u06d5\u0645 \u0648\u06d5\u06af\u06d5\u0695\u0627\u0646\u06d5 \u062f\u06c6\u0695\u0627\u0646\u06d5.',
    '\u06a9\u06d5\u0686\u06cc \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06cc \u06cc\u06d5\u06a9 \u0646\u0627\u0631\u06d5\u062a\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5\u067e\u0695\u060c \u0648\u06d5\u0648 \u0628\u06cc\u0627\u0648\u0627\u0646\u06d5 \u0626\u06d5\u06a9\u06d5\u0648\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0633\u06d5\u0631\u06af\u0648\u0631\u0648\u0634\u062a\u06d5\u06cc \u0645\u0648\u0648\u0633\u0627\u062a \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5\u061f',
    '\u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u0627\u0633\u06cc \u06a9\u0631\u062f \u0644\u06d5 \u0634\u06cc\u0648\u06cc \u067e\u06cc\u0631\u06c6\u0632\u06cc \u00ab\u0637\u0648\u0627\u00bb.',
    '\u0628\u06d5\u0631\u06d5\u0648 \u0641\u06cc\u0695\u0639\u06d5\u0648\u0646 \u0695\u06ce \u06a9\u06d5\u0648\u06d5\u061b \u0626\u06d5\u0648 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u062f\u06d5\u0631\u0686\u0648\u0648\u06d5.',
    '\u067e\u06ce\u0634\u06cc \u0628\u06ce\u0698\u06d5: \u0626\u0627\u062e\u06c6 \u062a\u0648\u0627\u06cc \u067e\u0627\u06a9 \u0628\u0628\u06cc\u06d5\u0648\u06d5\u061f',
    '\u0645\u0646\u06cc\u0634 \u0628\u06d5\u0631\u06d5\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a \u0695\u06ce\u062a \u0634\u0627\u0646 \u062f\u06d5\u0645 \u0648 \u0644\u06ce\u06cc \u0628\u062a\u0631\u0633\u06cc\u061f',
    '\u0626\u06d5\u0648\u0633\u0627 \u0628\u06d5\u06b5\u06af\u06d5 \u0647\u06d5\u0631\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u06a9\u06d5\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627.',
    '\u0628\u0627\u0648\u06d5\u0695\u06cc\u0634\u06cc \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f \u0648 \u0626\u06d5\u0634\u0642\u06cc\u0627 \u0628\u0648\u0648.',
    '\u0644\u06d5\u067e\u0627\u0634\u0627 \u067e\u0634\u062a\u06cc \u062a\u06ce \u06a9\u0631\u062f \u0648 \u062f\u06d5\u0633\u062a\u06cc \u06a9\u0631\u062f\u06d5 \u062a\u06d5\u0642\u0627\u0644\u0627 \u062f\u0627\u0646.',
    '\u0626\u06cc\u062a\u0631 \u062e\u06d5\u06b5\u06a9\u06cc \u06a9\u06c6 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u062c\u0627\u0695\u06cc \u06a9\u06ce\u0634\u0627.',
    '\u0648\u062a\u06cc: \u0626\u06d5\u0645\u0646 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0647\u06d5\u0631\u06d5 \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u062a\u0627\u0646\u0645.',
    '\u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u06af\u06cc\u0631\u06c6\u062f\u06d5\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u067e\u06d5\u0633\u06b5\u0627\u0646 \u0648 \u062f\u0646\u06cc\u0627\u06cc \u06a9\u0631\u062f.',
    '\u0628\u06ce\u06af\u0648\u0645\u0627\u0646\u06d5 \u0644\u06d5\u0645 \u0628\u0627\u0633\u06d5\u062f\u0627 \u062f\u06d5\u0631\u0632\u06ce \u0647\u06d5\u06cc\u06d5 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0626\u06d5\u062a\u0631\u0633\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u0686\u06ce \u06a9\u0631\u062f\u0646\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u0698\u0648\u0627\u0631\u062a\u0631\u06d5\u060c \u06cc\u0627\u0646 \u0626\u06d5\u0648 \u0626\u06d5\u0632\u0645\u06ce\u0646\u06d5\u06cc \u0626\u06d5\u0648 \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5\u061f',
    '\u0642\u06d5\u0648\u0627\u0631\u06d5\u06cc \u0628\u06d5\u0631\u0632 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648 \u0644\u06d5\u0628\u0627\u0631\u06cc \u06a9\u0631\u062f.',
    '\u0634\u06d5\u0648\u06d5\u06a9\u06d5\u0634\u06cc \u062a\u0627\u0631\u06cc\u06a9 \u06a9\u0631\u062f \u0648 \u0686\u06ce\u0634\u062a\u06d5\u0646\u06af\u0627\u0648\u06cc\u0634\u06cc \u0644\u06ce \u062f\u06d5\u0631\u062e\u0633\u062a.',
    '\u0644\u06d5\u067e\u0627\u0634 \u0626\u06d5\u0648\u06d5 \u0626\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u06cc \u0628\u06d5 \u062a\u0644\u062f\u0627\u0646\u06d5\u0648\u06d5 \u067e\u0627\u0646 \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u0627\u0648\u06cc \u0644\u06ce \u0647\u06ce\u0646\u0627\u06cc\u06d5 \u062f\u06d5\u0631 \u0648 \u062c\u06ce \u0644\u06d5\u0648\u06d5\u0695\u06cc \u0644\u06ce \u067e\u06d5\u06cc\u062f\u0627 \u06a9\u0631\u062f.',
    '\u06a9\u06ce\u0648\u0627\u062a\u06cc\u0634\u06cc \u0628\u06d5\u067e\u062a\u06d5\u0648\u06cc \u0644\u06ce \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0646\u062f.',
    '\u0628\u06c6 \u06a9\u0627\u0645 \u0648\u0695\u06d5\u0648\u0627 \u0644\u06ce \u0628\u0648\u0648\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0648 \u0628\u06c6 \u0626\u0627\u0698\u06d5\u06b5\u06cc\u0634\u062a\u0627\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u0628\u06d5\u06b5\u0627 \u0647\u06d5\u0631\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u06a9\u06d5 \u0647\u0627\u062a\u060c',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0645\u0631\u06c6 \u0648\u06d5 \u0628\u06cc\u0631\u06cc \u062f\u06ce\u062a\u06d5\u0648\u06d5 \u0686\u06cc\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5.',
    '\u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0634 \u0628\u06c6 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062f\u06d5\u0628\u06cc\u0646\u06ce\u060c \u062f\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648.',
    '\u062c\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0633\u06d5\u0631\u0628\u0632\u06ce\u0648\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0648\u060c',
    '\u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627\u06cc \u0628\u0698\u0627\u0631\u062f\u0648\u0648\u06d5\u060c',
    '\u062f\u06cc\u0627\u0631\u06d5 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u062c\u06ce\u06cc\u06d5\u062a\u06cc.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u06af\u06d5\u0648\u0631\u06d5\u06cc\u06cc\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u062a\u0631\u0633\u0627\u0648\u06d5 \u0648 \u0644\u06d5 \u0626\u0627\u0631\u06d5\u0632\u0648\u0648\u06cc \u062f\u06b5\u06cc \u062e\u06c6\u06cc \u0648\u0627\u0632\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a \u0626\u06d5\u0646\u0648\u0627\u06cc \u0626\u06d5\u0648\u06d5.',
    '\u06b5\u06ce\u062a \u062f\u06d5\u067e\u0631\u0633\u0646: \u0695\u06c6\u0698\u06cc \u0633\u06d5\u06b5\u0627 \u06a9\u06d5\u06cc \u0648 \u0644\u06d5 \u06a9\u0648\u06ce \u062f\u0627\u062f\u06d5\u0645\u06d5\u0632\u0631\u06ce\u061f',
    '\u062a\u06c6 \u0686\u06c6\u0646 \u0644\u06d5\u0645 \u0628\u0627\u0631\u06d5\u0648\u06d5 \u062f\u06d5\u062f\u0648\u06ce\u06cc\u061f',
    '\u0647\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u06a9\u0627\u062a\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u062a\u06c6 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u062a\u0631\u0633\u06ce\u0646\u06d5\u0631\u06cc \u0628\u06c6 \u06a9\u06d5\u0633\u06ce \u06a9\u06d5 \u0644\u06ce\u06cc \u062f\u06d5\u062a\u0631\u0633\u06ce.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06a9\u06d5 \u062f\u06d5\u06cc\u0628\u06cc\u0646\u0646\u060c \u0648\u0627 \u062a\u06ce\u062f\u06d5\u06af\u06d5\u0646 \u0628\u06d5\u062f\u06d5\u0631 \u0644\u06d5 \u0626\u06ce\u0648\u0627\u0631\u06d5\u06cc \u0695\u06c6\u0698\u06ce\u06a9 \u06cc\u0627\u0646 \u0686\u06ce\u0634\u062a\u0627\u0646\u06ce \u0646\u06d5\u0645\u0627\u0648\u0646\u06d5\u0648\u06d5.',
    '\u06af\u0631\u0698 \u0628\u0648\u0648\u060c \u0695\u0648\u0648\u0634\u06cc \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627!',
    '\u06a9\u06d5 \u0628\u06ce \u0686\u0627\u0648\u06d5\u06a9\u06d5 \u0647\u0627\u062a\u06d5 \u0644\u0627\u06cc.',
    '\u062a\u06c6 \u0686\u0648\u0632\u0627\u0646\u06cc! \u0628\u06d5\u0634\u06a9\u06d5 \u0648\u06cc\u0633\u062a\u06cc \u067e\u0627\u0628\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u06cc\u0627 \u067e\u06d5\u0646\u062f \u0628\u06af\u0631\u06ce \u0648 \u0626\u06d5\u0648 \u067e\u06d5\u0646\u062f\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u067e\u06ce \u0628\u062f\u0627.',
    '\u06a9\u06d5 \u0686\u06cc \u0626\u06d5\u0648\u06cc \u062e\u06c6\u06cc \u0628\u06ce \u0646\u06cc\u0627\u0632 \u062f\u06d5\u0632\u0627\u0646\u06ce\u060c',
    '\u0647\u06d5\u0631 \u0628\u06d5\u0648\u06d5\u0648\u06d5 \u062e\u062c\u06b5 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u067e\u0627\u06a9\u06cc\u0634 \u0646\u06d5\u0628\u06ce\u062a\u06d5\u0648\u06d5\u060c \u06af\u0648\u0646\u0627\u0647\u06ce \u0644\u06d5\u0633\u06d5\u0631 \u062a\u06c6 \u0646\u06cc\u06cc\u06d5.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u06a9\u06d5\u0633\u06ce \u0628\u06d5\u0647\u06d5\u06b5\u06d5\u062f\u0627\u0648\u0627\u0646 \u062f\u06ce\u062a\u06d5 \u0644\u0627\u062a\u060c',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u062a\u0631\u0633\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u062a\u06c6 \u062e\u06c6\u062a\u06cc \u0644\u06ce \u062f\u06d5\u062e\u0627\u0641\u06b5\u06ce\u0646\u06cc.',
    '\u0642\u06d5\u062a \u0648\u0627 \u0645\u06d5\u06a9\u06d5! \u0686\u0648\u0646 \u0626\u06d5\u0645 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06d5 \u067e\u06d5\u0646\u062f\u0646.',
    '\u06a9\u06ce \u062d\u06d5\u0632 \u0628\u06a9\u0627 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0644\u06ce \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u06ce.',
    '\u0644\u06d5 \u0646\u0627\u0648\u06d5\u0646\u062f\u06cc \u0644\u0627\u067e\u06d5\u0695\u06d5\u06a9\u0627\u0646\u06cc \u0647\u06ce\u0698\u0627\u062f\u0627\u060c',
    '\u0628\u06d5 \u067e\u0627\u06a9\u0698\u06cc \u0647\u06d5\u06b5\u06af\u06cc\u0631\u0627\u0648\u0646.',
    '\u0628\u06d5 \u062f\u06d5\u0633\u062a\u06cc \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u06d5\u06af\u06d5\u0644\u06cc\u060c',
    '\u0647\u06d5\u06b5\u0628\u0698\u0627\u0631\u062f\u06d5\u06cc \u0628\u06d5\u0626\u06d5\u0645\u06d5\u06af [\u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5\u062a\u06d5\u0648\u06d5].',
    '\u062f\u06d5\u06a9 \u0626\u06d5\u0648 \u0645\u0631\u06c6\u06cc\u06d5 \u0628\u06d5\u06a9\u0648\u0634 \u0686\u06ce\u060c \u06a9\u06d5 \u0647\u06cc\u0686\u06cc \u062f\u06d5\u0686\u0627\u0648\u0627\u0646\u062f\u0627 \u0646\u06cc\u06cc\u06d5.',
    '\u0644\u06d5 \u0686\u06cc \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5\u061f',
    '\u0644\u06d5 \u062f\u06b5\u06c6\u067e\u06ce\u06a9 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627 \u0648 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u0634\u06cc \u0628\u06c6 \u062f\u0627\u0646\u0627\u0648\u06d5.',
    '\u067e\u0627\u0634\u0627\u0646 \u0695\u06ce\u06af\u06d5\u0634\u06cc \u0628\u06c6 \u062e\u06c6\u0634 \u06a9\u0631\u062f.',
    '\u062c\u0627 \u0645\u0631\u0627\u0646\u062f\u06cc \u0648 \u0644\u06d5 \u06af\u06c6\u0695\u06cc \u0646\u0627.',
    '\u062f\u0648\u0627 \u062c\u0627\u0631 \u0647\u06d5\u0631 \u06af\u0627 \u0645\u06d5\u06cc\u0644\u06cc \u0644\u06ce \u0628\u06ce\u060c \u062f\u06cc\u0633\u0627\u0646 \u0632\u06cc\u0646\u062f\u0648\u0648\u06cc \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0646\u06d5\u062e\u06ce\u0631! \u0628\u06d5 \u0686\u06cc \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06ce \u062f\u0627\u0648\u06d5\u060c \u0647\u06ce\u0634\u062a\u0627 \u0628\u06d5\u062c\u06ce\u06cc \u0646\u06d5\u0647\u06ce\u0646\u0627\u0648\u06d5.',
    '\u062f\u06d5\u06cc \u0628\u0627 \u0645\u0631\u06c6 \u0628\u0695\u0648\u0627\u0646\u06ce\u062a\u06d5 \u062e\u06c6\u0631\u0627\u06a9\u06cc \u062e\u06c6\u06cc.',
    '\u0626\u06ce\u0645\u06d5 \u062e\u06c6\u0645\u0627\u0646 \u0626\u0627\u0648\u06d5\u06a9\u06d5\u0645\u0627\u0646 \u0628\u06c6 \u0695\u0698\u062a\u0648\u0648\u06d5\u060c \u0628\u06d5 \u0695\u0698\u0627\u0646\u062f\u0646.',
    '\u067e\u0627\u0634\u0627\u0646 \u0626\u06d5\u0645 \u0647\u06d5\u0631\u062f\u06d5\u0634\u0645\u0627\u0646 \u0642\u06d5\u06b5\u0627\u0634\u062a\u060c \u0628\u06d5 \u0642\u06d5\u06b5\u0627\u0634\u062a\u0646.',
    '\u0626\u06d5\u0648\u0633\u0627 \u062f\u0627\u0646\u06d5\u0634\u0645\u0627\u0646 \u0644\u06ce \u0695\u0648\u0627\u0646\u062f.',
    '\u0695\u06d5\u0632 \u0648 \u0633\u06d5\u0648\u0632\u0627\u06cc\u06cc\u0634 \u0647\u06d5\u0631\u0648\u06d5\u062a\u0631.',
    '\u0628\u0646\u06d5 \u0632\u06d5\u06cc\u062a\u0648\u0648\u0646 \u0648 \u062f\u0627\u0631 \u062e\u0648\u0631\u0645\u0627\u0634.',
    '\u0628\u0627\u063a\u0633\u062a\u0627\u0646\u06cc \u0686\u0695 \u0648 \u067e\u0695\u06cc\u0634.',
    '\u0645\u06cc\u0648\u06d5 \u0648 \u0644\u06d5\u0648\u06d5\u0695\u06cc \u0686\u0627\u06cc\u06d5\u0631\u06cc\u0634.',
    '\u0628\u06c6 \u0628\u0698\u06cc\u0648\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0648 \u0645\u0627\u06b5\u0627\u062a\u06d5\u06a9\u06d5\u062a\u0627\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627\u0634 \u06af\u0648\u0695\u0698\u0646\u06cc \u0628\u06d5\u0642\u06d5\u0648\u0647\u0627\u062a.',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0628\u0631\u0627 \u0644\u06d5 \u0628\u0631\u0627\u06cc \u0695\u0627 \u062f\u06d5\u06a9\u0627.',
    '\u0644\u06d5 \u062f\u0627\u06cc\u06a9\u06cc\u0634\u06cc \u0648 \u0644\u06d5 \u0628\u0627\u0648\u06cc\u0634\u06cc.',
    '\u0644\u06d5 \u0647\u0627\u0648\u0633\u06d5\u0631\u06cc \u0648 \u0644\u06d5 \u062c\u06af\u06d5\u0631\u06af\u06c6\u0634\u06d5\u06a9\u0627\u0646\u06cc\u0634\u06cc.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06a9\u0627\u0631\u06cc \u062e\u06c6\u06cc \u0647\u06d5\u06cc\u06d5 \u0648 \u0646\u0627\u06cc\u067e\u06d5\u0631\u0698\u06ce\u062a\u06d5 \u0633\u06d5\u0631 \u06a9\u06d5\u0633\u06cc \u062a\u0631.',
    '\u062f\u06cc\u0645\u06cc \u0648\u0627 \u0647\u06d5\u0646 - \u0644\u06d5\u0648\u0695\u06c6\u0698\u06d5\u062f\u0627 - \u0633\u0648\u0648\u0631 \u0648 \u0633\u067e\u06cc \u062f\u06cc\u0627\u0631\u06cc \u062f\u06d5\u062f\u06d5\u0646.',
    '\u062f\u06d5\u0645 \u0628\u06d5 \u067e\u06ce\u06a9\u06d5\u0646\u06cc\u0646 \u0648 \u0634\u0627\u062f\u0646.',
    '\u0647\u06ce\u0646\u062f\u06ce \u0695\u0648\u0634 \u0647\u06d5\u0646 - \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce - \u062a\u06c6\u0632\u06cc \u0645\u06d5\u06cc\u0646\u06d5\u062a\u06cc\u0627\u0646 \u0644\u06ce \u0628\u0627\u0631\u0646.',
    '\u0642\u0648\u0631\u0645 \u0648 \u0633\u06cc\u0627\u0646 \u062f\u0627\u06cc \u067e\u06c6\u0634\u06cc\u0648\u0646.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u0646 \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u0627\u0646\u06cc \u0644\u06d5 \u062f\u06cc\u0646 \u0644\u0627\u062f\u06d5\u0631.',
    '\u0648\u06d5\u062e\u062a\u06ce \u06a9\u06d5 \u062e\u06c6\u0631 \u062a\u06c6\u067e\u06d5\u06b5 \u06a9\u0631\u0627 [\u0648 \u0695\u06d5\u0634 \u0647\u06d5\u06b5\u06af\u06d5\u0695\u0627]\u060c',
    '\u0648\u06ce\u062c\u0627 \u0647\u06d5\u0645\u0648\u0648 \u0647\u06d5\u0633\u0627\u0631\u06d5\u06a9\u0627\u0646 \u067e\u06d5\u0698\u0645\u06d5\u0631\u0627\u0646 \u0648 \u0648\u06d5\u0631\u06ce\u0646\u062f\u06d5\u0631\u0627\u0646\u060c',
    '\u06a9\u06d5\u0698\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u0634\u0648\u06ce\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u062e\u0632\u06ce\u0646\u062f\u0631\u0627\u0646\u060c',
    '\u0644\u06d5\u0648 \u06a9\u0627\u062a\u06d5\u0634\u0627 \u0648\u0634\u062a\u0631\u0627\u0646\u06cc \u0644\u06d5\u0633\u06d5\u0631 \u0632\u0627\u0646\u06ce \u0628\u06ce \u062e\u06c6 \u06a9\u0631\u0627\u0646\u060c',
    '\u0647\u06d5\u0631\u06af\u0627\u0634 \u0644\u0627\u0648\u0631 \u0648 \u062c\u0627\u0646\u06d5\u0648\u06d5\u0631 \u0644\u06d5 \u0626\u0627\u0642\u0627\u0631\u06ce\u06a9\u0627 \u06a9\u06c6 \u06a9\u0631\u0627\u0646\u060c',
    '\u0647\u06d5\u0644\u06ce \u0647\u06d5\u0645\u0648\u0648 \u062f\u06d5\u0631\u06cc\u0627\u06a9\u0627\u0646\u06cc\u0634 \u0647\u06d5\u06b5\u0686\u0648\u0646 \u0648 \u062a\u06ce\u06a9\u06d5\u06b5 \u0628\u06d5 \u06cc\u06d5\u06a9 \u0628\u0648\u0648\u0646\u060c',
    '\u06a9\u0627\u062a\u06ce \u0647\u06d5\u0645\u0648\u0648 \u06af\u06cc\u0627\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0648\u0644\u0641\u06cc \u06cc\u06d5\u06a9 \u0628\u0648\u0648\u0646\u060c',
    '\u0644\u06d5 \u062f\u06c6\u062a\u06cc \u0632\u06cc\u0646\u062f\u06d5 \u0628\u06d5 \u06af\u06c6\u0695\u06cc\u0634 \u067e\u0631\u0633\u06cc\u0627\u0631 \u06a9\u0631\u0627:',
    '\u0628\u06d5 \u06a9\u06cc\u0698\u0627\u0646 \u06af\u0648\u0646\u0627\u0647\u0640 \u06af\u0648\u0698\u0627\u0648\u06d5\u061f',
    '\u0647\u06d5\u0631\u06af\u0627\u0634 \u0646\u0627\u0645\u06d5\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06a9\u0627\u0646 \u06a9\u0631\u0627\u0646\u06d5\u0648\u06d5\u060c',
    '\u06a9\u06d5 \u0626\u0627\u0633\u0645\u0627\u0646\u06cc\u0634 \u0648\u06d5\u0644\u0627 \u0628\u0631\u0627\u060c',
    '\u0647\u06d5\u0631 \u062f\u06d5\u0645\u06ce\u06a9\u06cc\u0634 \u06a9\u06b5\u06cc \u062f\u06c6\u0698\u06d5\u0647\u06ce \u0646\u06ce\u06b5 \u062f\u0631\u0627\u060c',
    '\u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc\u0634 \u0648\u06d5 \u0646\u0632\u06cc\u06a9 \u062e\u0631\u0627\u060c',
    '\u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u06ce \u062f\u06d5\u0632\u0627\u0646\u06ce \u06a9\u06d5 \u0686\u06cc \u0644\u06d5\u06af\u06d5\u06b5 \u062e\u06c6\u06cc\u0627 \u0647\u06ce\u0646\u0627\u0648\u06d5.',
    '\u0633\u0627 \u0633\u0648\u06ce\u0646\u062f \u0626\u06d5\u062e\u06c6\u0645 \u0628\u06d5\u0648 \u0647\u06d5\u0633\u0627\u0631\u06d5 \u06af\u06d5\u0695\u06c6\u06a9\u0627\u0646\u06d5\u06cc\u060c',
    '\u062e\u0648\u0644 \u062f\u06d5\u062e\u06c6\u0646 \u0648 \u062f\u06d5\u06af\u06d5\u0695\u06ce\u0646\u06d5\u0648\u06d5 \u0646\u0627\u0648 \u0644\u0627\u0646\u06d5\u061b',
    '\u0628\u06d5\u0645 \u0634\u06d5\u0648\u06d5 \u06a9\u06d5 \u062a\u0627\u0631\u06cc\u06a9 \u062f\u0627\u062f\u06ce\u060c',
    '\u0628\u06d5 \u0633\u067e\u06ce\u062f\u06d5\u060c \u06a9\u0627\u062a\u06ce \u06a9\u06d5 \u0647\u06d5\u0646\u0627\u0633\u06d5\u06cc \u067e\u06ce\u062f\u0627 \u062f\u06ce\u060c',
    '\u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5 - \u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 - \u0642\u0633\u06d5\u06cc \u0695\u0627\u0633\u067e\u06ce\u0631\u0627\u0648\u06ce\u06a9\u06cc \u0632\u06c6\u0631 \u0645\u06d5\u0632\u0646\u06d5.',
    '\u0632\u06c6\u0631 \u0628\u06d5\u0647\u06ce\u0632\u06d5\u060c \u0644\u0627\u06cc \u062e\u0648\u062f\u06ce \u062a\u06d5\u062e\u062a\u060c \u062c\u06ce\u062f\u0627\u0631\u06d5.',
    '\u0642\u0633\u06d5\u06cc \u062f\u06d5\u0695\u0648\u0627\u060c \u0644\u06d5\u0648\u06ce \u062c\u06ce\u06af\u06d5\u06cc \u0645\u062a\u0645\u0627\u0646\u06d5\u06cc\u06d5.',
    '\u0647\u0627\u0648\u0695\u06ce\u06a9\u06d5\u0634\u062a\u0627\u0646 \u0644\u06ce\u0648\u06d5 \u0646\u06cc\u06cc\u06d5.',
    '\u0628\u06ce \u0633\u06c6\u06cc\u06d5 \u06a9\u06d5 \u0644\u06d5 \u0626\u0627\u0633\u06c6\u06cc\u06d5 \u0628\u06d5 \u0695\u06c6\u0634\u0646\u06cc \u062f\u06cc\u062a\u0648\u0648\u06cc\u06d5\u062a\u06cc.',
    '\u0644\u06d5 \u062f\u0631\u06a9\u0627\u0646\u062f\u0646\u06cc \u0646\u0647\u06ce\u0646\u06cc\u0634 \u0686\u06a9\u0648\u0648\u0633 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u06cc \u0626\u06ce\u0698\u06ce\u060c \u0647\u06cc\u0686\u06cc \u0642\u0633\u06d5\u06cc \u0634\u06d5\u06cc\u062a\u0627\u0646\u06cc \u062f\u06d5\u0631\u06a9\u0631\u0627\u0648 \u0646\u06cc\u06cc\u06d5.',
    '\u0633\u0627 \u0626\u06ce\u0648\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u06a9\u0648\u06ce \u062f\u06d5\u0686\u0646\u061f',
    '\u0626\u06d5\u0645 \u0642\u0648\u0695\u0626\u0627\u0646\u06d5 \u0647\u06d5\u0631\u067e\u06d5\u0646\u062f\u06d5 \u0628\u06c6 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u0646\u06cc\u0627.',
    '\u0628\u06c6 \u0647\u06d5\u0631\u06a9\u0627\u0645\u062a\u0627\u0646 \u06a9\u06d5 \u0628\u06cc\u06d5\u0648\u06ce \u0634\u0648\u06ce\u0646\u06cc \u0695\u06ce\u06af\u06d5\u06cc \u0633\u06d5\u0631\u0695\u0627\u0633 \u06a9\u06d5\u0648\u06ce.',
    '\u0628\u06d5\u0648\u06cc\u0633\u062a\u0646\u06cc \u062e\u06c6\u0634\u062a\u0627\u0646 \u0646\u06cc\u06cc\u06d5\u060c \u0645\u06d5\u06af\u06cc\u0646 \u062e\u0648\u062f\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06af\u0634 \u062c\u06cc\u0647\u0627\u0646\u06ce\u060c \u062e\u06c6\u06cc \u062d\u06d5\u0632 \u0628\u06a9\u0627.',
    '\u0647\u06d5\u0631\u06af\u0627 \u06a9\u06d5 \u0639\u06d5\u0632\u0645\u0627\u0646 \u0642\u06d5\u06b5\u06d5\u0634\u0627\u060c',
    '\u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u0626\u06d5\u0633\u062a\u06ce\u0631\u0627\u0646 \u0648\u06d5\u0631\u06cc\u0646\u060c',
    '\u0647\u06d5\u0631 \u062f\u06d5\u0645\u06ce\u06a9\u06cc\u0634 \u0632\u06d5\u0631\u06cc\u0627 \u062a\u06d5\u0642\u06cc\u0646 \u0648 \u0644\u06ce\u06a9 \u062f\u0631\u0627\u0646\u060c',
    '\u0647\u06d5\u0631 \u0633\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u06af\u06c6\u0695\u0633\u062a\u0627\u0646 \u0698\u06ce\u0631 \u0648 \u0695\u0648\u0648 \u06a9\u0631\u0627\u0646\u060c',
    '\u0647\u06d5\u0645\u0648\u0648 \u06a9\u06d5\u0633\u06ce \u067e\u06ce\u062f\u06d5\u0632\u0627\u0646\u06ce \u0686\u06d5\u06cc \u067e\u06ce\u0634 \u062e\u0633\u062a \u0648 \u0686\u06d5\u06cc \u0628\u06d5\u062c\u06ce \u0647\u06ce\u0634\u062a.',
    '\u0626\u06d5\u06cc \u0628\u0646\u06cc\u0627\u062f\u06d5\u0645! \u0686\u06cc \u062a\u06c6\u06cc \u0644\u06d5\u062d\u0627\u0646\u062f \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u06d5\u062e\u0634\u0646\u062f\u06d5\u062a \u0628\u0627\u062f\u06cc \u0647\u06d5\u0648\u0627 \u06a9\u0631\u062f\u061f',
    '\u0626\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f\u06cc \u0648 \u0626\u06d5\u0646\u062f\u0627\u0645\u06d5\u06a9\u0627\u0646\u06cc \u0648\u06ce\u062e\u0633\u062a\u06cc \u0648 \u0695\u06ce\u06a9\u06cc \u06a9\u0631\u062f\u06cc.',
    '\u0628\u06d5 \u0647\u06d5\u0631 \u0634\u06a9\u06b5\u06ce\u06a9\u06cc\u0634 \u062e\u06c6\u06cc \u0648\u06cc\u0633\u062a\u06cc\u060c \u062f\u06d5\u0631\u06cc \u062e\u0633\u062a\u06cc.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u0626\u06d5\u0645\u06d5\u0648 \u0646\u0627\u06cc\u06d5\u062a\u06d5 \u0628\u06cc\u0631\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06ce\u0648\u06d5 \u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5\u0648 \u0644\u0627 \u062f\u06d5\u0631\u06d5\u0648\u06d5.',
    '[\u0648\u06d5\u0644 \u0626\u06d5\u0648\u06d5\u0634\u0627] \u0686\u06d5\u0646\u062f \u067e\u0627\u0633\u06af\u0631\u062a\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u06d5\u0648\u06d5.',
    '\u062e\u0627\u0648\u06d5\u0646 \u0695\u06ce\u0632\u0646\u060c \u0646\u06a4\u06cc\u0633\u0627\u0631\u0646\u061b',
    '\u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u0627\u0631\u06ce \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u06a9\u06d5\u0646\u060c \u0626\u0627\u06af\u06d5\u062f\u0627\u0631\u0646.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u0634\u06cc\u062f\u0627\u0646.',
    '\u0628\u06d5\u062f\u06a9\u0627\u0631\u0627\u0646\u06cc\u0634 \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u06cc\u0646.',
    '\u0695\u06c6\u0698\u06cc \u0698\u0645\u0627\u0631 \u062f\u06ce\u0646\u06d5 \u0646\u0627\u0648\u06cc.',
    '\u0646\u0627\u0634\u062a\u0627\u0646\u0646 \u062e\u06c6\u06cc \u0644\u06ce \u0628\u0628\u0648\u06ce\u0631\u0646.',
    '\u06a9\u0648 \u0632\u0627\u0646\u06cc\u0648\u062a\u06d5 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06cc \u0698\u0645\u0627\u0631 \u0686\u06cc\u06cc\u06d5\u061f',
    '\u0647\u06d5\u0645\u06cc\u0633\u0627\u0646 \u062a\u06c6 \u06a9\u0648\u062a \u0632\u0627\u0646\u06cc\u0648\u06d5 \u0695\u06c6\u0698\u06cc \u0698\u0645\u0627\u0631 \u0686\u062a\u06c6\u06cc\u06d5\u061f',
    '\u0695\u06c6\u0698\u06ce\u06a9\u06d5 \u06a9\u06d5\u0633 \u0628\u06c6 \u06a9\u06d5\u0633\u06cc \u062a\u0631 \u0647\u06cc\u0686 \u0634\u062a\u06ce\u06a9\u06cc \u0644\u06d5\u062f\u06d5\u0633\u062a \u0646\u0627\u06cc\u06d5 \u0648 \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0641\u06d5\u0631\u0645\u0627\u0646 \u0647\u06d5\u0631 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u062e\u0648\u0627\u06cc\u06d5.',
    '\u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u06a9\u06d5\u0645 \u0641\u0631\u06c6\u0634\u0627\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0628\u06d5 \u067e\u06ce\u0648\u0627\u0646\u06d5 \u0634\u062a\u06ce \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645 \u062f\u06d5\u0633\u06ce\u0646\u0646\u060c \u0626\u06d5\u0634\u06ce \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u067e\u06ce \u0628\u062f\u06d5\u0646.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0628\u06c6\u06cc\u0627\u0646 \u067e\u06ce\u0648\u0627\u0646\u060c \u06cc\u0627\u0646 \u0628\u06d5 \u06a9\u06ce\u0634\u0627\u0646\u06d5\u06cc\u0627\u0646 \u067e\u06ce\u062f\u0627\u0646\u060c \u0644\u06ce\u06cc \u06a9\u06d5\u0645 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0646\u0627\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0647\u06d5\u0631 \u0632\u06cc\u0646\u062f\u0648\u0648 \u062f\u06d5\u06a9\u0631\u06ce\u0646\u06d5\u0648\u06d5\u061f',
    '\u0628\u06c6 \u0695\u06c6\u0698\u06ce\u06a9\u06cc \u06af\u06d5\u0648\u0631\u06d5 \u0648 \u06af\u0631\u0627\u0646\u061f',
    '\u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u06cc \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0645\u06d5\u0631\u062f\u0645 \u0628\u06c6 \u062f\u06cc\u062f\u0627\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0645\u0648\u0648 \u062f\u0646\u06cc\u0627 \u0695\u0627\u062f\u06d5\u0648\u06d5\u0633\u062a\u0646.',
    '\u0646\u06d5\u062e\u06ce\u0631\u060c \u0628\u0627\u0648\u06d5\u0695 \u0628\u06d5\u0645\u06d5 \u0646\u0627\u06a9\u06d5\u0646\u061b \u0628\u0627 \u0628\u0632\u0627\u0646\u0646 \u06a9\u06d5 \u0633\u06cc\u0627\u06cc\u06cc \u0626\u06d5\u0645 \u0628\u06ce \u0641\u06d5\u0695\u0627\u0646\u06d5 \u0648\u0627 \u0644\u06d5 \u00ab\u0633\u0650\u062c\u0651\u064a\u0646\u00bb.',
    '\u06a9\u0648\u0648 \u0632\u0627\u0646\u06cc\u0648\u062a\u06d5 \u00ab\u0633\u0650\u062c\u0651\u064a\u0646\u00bb \u0686\u06cc\u06cc\u06d5\u061f',
    '\u06a9\u062a\u06ce\u0628\u06ce \u0646\u06cc\u0634\u0627\u0646\u06d5\u062f\u0627\u0631\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0644\u06d5 \u0628\u06ce \u0628\u0627\u0648\u06d5\u0695\u0627\u0646 \u0647\u0627\u0648\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u0695\u06c6\u0698\u06cc \u0644\u06ce\u067e\u0631\u0633\u06cc\u0646\u06d5\u0648\u06d5\u06cc\u0627\u0646 \u067e\u06ce \u062f\u0631\u06c6\u06cc\u06d5.',
    '\u0647\u06d5\u0631 \u0645\u0644\u0647\u0648\u0695\u06cc \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06cc\u0634 \u0626\u06d5\u0648\u06d5\u06cc \u0628\u06d5 \u0644\u0627\u0648\u06d5 \u062f\u0631\u06c6\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5\u06cc \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u0646\u060c \u0626\u06ce\u0698\u06ce: \u0626\u06d5\u0645\u06d5 \u0686\u06cc\u0631\u06c6\u06a9\u06cc \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06d5.',
    '\u06a9\u06d5 \u0648\u0627\u0634 \u0646\u06cc\u06cc\u06d5\u061b \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646 \u0698\u06d5\u0646\u06af\u06cc \u06a9\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0628\u06c6\u062a\u06d5 \u062a\u0648\u06ce\u0698\u06ce\u06a9\u06cc \u0633\u06d5\u0631 \u062f\u06b5\u06cc\u0627\u0646.',
    '\u0647\u06cc\u0686 \u0648\u0627 \u0646\u06cc\u06cc\u06d5! \u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0648\u0627\u0646 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0644\u06d5 [\u0628\u06d5\u0632\u06d5\u06cc\u06cc] \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc\u0627\u0646\u060c \u0641\u0631\u06d5 \u062f\u0648\u0648\u0631\u0646.',
    '\u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u0627 \u0626\u06d5\u0648\u0627\u0646 \u2013 \u0628\u06ce \u0633\u06c6 \u2013 \u062f\u06d5\u0686\u0646\u06d5 \u062f\u06c6\u0698\u06d5\u0647\u0640.',
    '\u067e\u06ce\u06cc\u0627\u0646 \u0626\u06ce\u0698\u0646: \u0626\u06d5\u0645\u06d5 \u0647\u06d5\u0648\u06d5 \u06a9\u06d5 \u0626\u06ce\u0648\u06d5 \u067e\u06ce\u062a\u0627\u0646 \u062f\u0631\u06c6 \u0628\u0648\u0648.',
    '\u0628\u0627 \u0628\u0634\u0632\u0627\u0646\u0646 \u0633\u06cc\u0627\u06cc\u06cc \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u00ab\u0639\u0644\u0651\u064a\u0651\u064a\u0646\u00bb \u062f\u0627 [\u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5].',
    '\u06a9\u0648\u0648 \u062f\u06d5\u0632\u0627\u0646\u06cc\u060c \u00ab\u0639\u0644\u0651\u064a\u0651\u064a\u0646\u00bb \u0686\u06d5\u0633\u061f',
    '\u06a9\u062a\u06ce\u0628\u06ce \u0646\u06cc\u0634\u0627\u0646\u06d5\u062f\u0627\u0631\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06ce \u0646\u0632\u06cc\u06a9 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0628\u06ce\u060c \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce [\u0648 \u0644\u06ce\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5].',
    '\u0628\u06d5 \u0695\u0627\u0633\u062a\u06cc \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u06d5\u06a9\u0627\u0646 \u0644\u06d5 \u062e\u06c6\u0634\u06cc\u062f\u0627\u0646.',
    '\u0648\u06d5\u0628\u0627\u0646 \u062a\u06d5\u062e\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u0686\u0627\u0648 \u062f\u06d5\u06af\u06ce\u0695\u0646.',
    '\u0644\u06d5 \u0686\u0627\u0631\u06d5\u06cc\u0627\u0646 \u062a\u06d5\u0695\u0648 \u0628\u06d5\u0644\u06cc \u062e\u06c6\u0634\u06af\u0648\u0632\u06d5\u0631\u0627\u0646\u06cc \u062f\u06d5\u0632\u0627\u0646\u06cc.',
    '\u0628\u0627\u062f\u06d5\u06cc\u06d5\u06a9\u06cc \u0633\u06d5\u0631\u0628\u06d5\u0645\u06c6\u0631\u06cc\u0627\u0646 \u0628\u06c6 \u06a4\u06d5\u062e\u0627\u0631\u0646\u06ce \u062f\u06d5\u062f\u06d5\u0646\u06ce.',
    '\u0645\u06c6\u0631\u06d5\u06a9\u06d5\u06cc \u0633\u06d5\u0631\u06cc \u0644\u06d5 \u0645\u06cc\u0634\u06a9\u06d5\u061b \u0628\u0627 \u0626\u06d5\u0648\u06cc \u062d\u06d5\u0632\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u06d5\u0646\u060c \u0628\u06c6 \u0634\u062a\u06ce \u0648\u0627 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u06a9\u06ce\u0628\u06d5\u0631\u06a9\u06ce \u0628\u06a9\u06d5\u0646.',
    '\u0626\u0627\u0648\u06ce\u062a\u06d5\u0634\u06cc \u0644\u06d5 \u062a\u06d5\u0633\u0646\u06cc\u0645\u06d5.',
    '\u0686\u06d5\u0645\u06d5\u06cc\u06d5\u06a9\u06d5\u060c \u0626\u06d5\u0648\u06cc \u0646\u0632\u06cc\u06a9 \u062e\u0631\u0627\u0648\u0646\u06d5\u0648\u06d5\u060c \u0644\u06d5\u0648 \u0633\u06d5\u0631\u0686\u0627\u0648\u06d5 \u062f\u06d5\u062e\u06c6\u0646\u06d5\u0648\u06d5.',
    '\u062a\u0627\u0648\u0627\u0646\u06a9\u0627\u0631\u0627\u0646 \u2013 \u0644\u06d5\u0633\u06d5\u0631 \u062f\u0646\u06cc\u0627 \u2013 \u0628\u06d5 \u062f\u06cc\u0646\u062f\u0627\u0631\u0627\u0646 \u067e\u06ce\u062f\u06d5\u06a9\u06d5\u0646\u06cc\u0646.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0628\u06d5 \u0644\u0627\u06cc\u0627\u0646\u062f\u0627 \u062f\u06d5\u0686\u0648\u0648\u0646\u060c \u0686\u0627\u0648\u06cc\u0627\u0646 \u0644\u06d5 \u06cc\u06d5\u06a9\u062a\u0631 \u062f\u0627\u062f\u06d5\u06af\u0631\u062a.',
    '\u0647\u06d5\u0631\u06a9\u0627\u062a\u06ce\u06a9\u06cc\u0634 \u062f\u06d5\u0686\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u06a9\u06d5\u0633\u0627\u0646\u06cc\u0627\u0646\u060c \u0628\u06d5 \u062d\u06d5\u0646\u06d5\u06a9 \u062f\u06d5\u06cc\u0627\u0646\u06af\u06ce\u0695\u0627\u0648\u06d5.',
    '\u06a9\u06d5 \u0686\u0627\u0648\u06cc\u0634\u06cc\u0627\u0646 \u067e\u06ce \u062f\u06d5\u06a9\u06d5\u0648\u062a\u0646\u060c \u062f\u06d5\u06cc\u0627\u0646\u06af\u0648\u062a: \u0626\u06d5\u0645\u0627\u0646\u06d5 \u06af\u0648\u0645\u0695\u0627\u0646.',
    '\u0647\u06cc\u0686 \u06a9\u0627\u0645\u06cc\u0634 \u0695\u0627\u0646\u06d5\u0633\u067e\u06ce\u0631\u0627\u0628\u0648\u0648\u0646 \u06a9\u06d5 \u062f\u06d5\u0628\u06ce \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06cc\u0627\u0646 \u0628\u0646.',
    '\u0648\u0627 \u0626\u06d5\u0645\u0695\u06c6\u0634 \u062e\u0648\u062f\u0627\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0628\u06d5 \u0628\u06ce \u062f\u06cc\u0646\u06d5\u06a9\u0627\u0646 \u0626\u06d5\u062e\u06d5\u0646\u0646.',
    '\u0644\u06d5\u0628\u0627\u0646\u06cc \u062a\u06d5\u062e\u062a\u06d5\u06a9\u0627\u0646\u06d5\u0648\u06d5 \u0686\u0627\u0648 \u062f\u06d5\u06af\u06ce\u0695\u0646.',
    '\u0626\u0627\u062e\u06c6 \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u067e\u0627\u062f\u0627\u0634\u06cc \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0648\u06d5\u0631\u06af\u0631\u062a\u0648\u0648\u06d5\u061f',
    '\u0647\u06d5\u0631\u06af\u0627 \u0639\u06d5\u0632\u0645\u0627\u0646 \u062a\u06d5\u0631\u06d5\u06a9\u06cc \u0628\u0631\u062f\u060c',
    '\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0695\u0627\u0647\u06ce\u0646\u06d5\u0631\u06cc\u0634\u06cc \u0628\u06d5\u062c\u06ce \u0647\u06ce\u0646\u0627 \u0648 \u062f\u06d5\u0628\u06ce \u0648\u0627\u0634 \u0628\u06ce.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0632\u06d5\u0645\u06cc\u0646\u06cc\u0634 \u0695\u0627\u062e\u0631\u0627\u060c',
    '\u0647\u06d5\u0631\u0686\u06cc \u062a\u06ce\u06cc\u062f\u0627 \u0628\u0648\u0648 \u0641\u0695\u06ce\u06cc \u062f\u0627 \u0648 \u0647\u06cc\u0686 \u0634\u062a\u06cc \u062f\u06d5\u0646\u0627\u0648\u062f\u0627 \u0646\u06d5\u0645\u0627\u060c',
    '\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062e\u06c6\u06cc \u0628\u06d5\u062c\u06ce \u0647\u06ce\u0646\u0627\u0648 \u062f\u06d5\u0628\u06ce \u0648\u0627\u0634 \u0628\u06ce.',
    '\u0626\u06d5\u06cc \u0645\u0631\u06c6! \u0695\u06d5\u0646\u062c\u06cc \u0632\u06c6\u0631 \u062f\u06d5\u062f\u06d5\u06cc \u0628\u06af\u06d5\u06cc \u0628\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u062e\u06c6\u062a\u061b \u0633\u0627 \u062a\u0648\u0648\u0634\u06cc \u062f\u06ce\u06cc.',
    '\u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u0644\u0627\u06cc \u0695\u0627\u0633\u062a\u06cc\u06d5\u0648\u06d5 \u067e\u06ce \u0628\u062f\u0631\u06ce\u060c',
    '\u0644\u06d5\u0648\u06d5 \u0628\u06d5\u0648 \u0644\u0627 \u067e\u0631\u0633\u06cc\u0627\u0631\u06ce\u06a9\u06cc \u06a9\u06d5\u0645 \u0648 \u0647\u0627\u0633\u0627\u0646\u06cc \u0644\u06ce \u062f\u06d5\u06a9\u0631\u06ce.',
    '\u0628\u06d5 \u062f\u06b5\u062e\u06c6\u0634\u06cc\u0634 \u062f\u06d5\u0686\u062a\u06d5\u0648\u06d5 \u0644\u0627\u06cc \u06a9\u06d5\u0633 \u0648 \u06a9\u0627\u0631\u06cc.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u06cc \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u0626\u0627\u0644\u06cc \u067e\u0634\u062a\u06cc\u06d5\u0648\u06d5 \u0628\u0648\u0648\u0628\u06ce\u060c',
    '\u0626\u06d5\u0648 \u062f\u0648\u0627\u062c\u0627\u0631 \u0647\u0627\u0648\u0627\u0631\u06cc \u0645\u06d5\u0631\u06af\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u06a9\u0627.',
    '\u062f\u06d5\u0634\u0686\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u06a9\u06b5\u067e\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u0644\u06d5 \u0646\u0627\u0648 \u0645\u0627\u06b5 \u0648 \u0645\u0646\u062f\u0627\u06b5\u06cc\u0627 \u0632\u06c6\u0631 \u0628\u06d5\u06a9\u06d5\u06cc\u0641 \u0628\u0648\u0648.',
    '\u0626\u06d5\u0648 \u067e\u06ce\u06cc \u0648\u0627 \u0628\u0648\u0648 \u06a9\u06d5 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u0646\u0627\u06af\u06d5\u0695\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0626\u0627\u0631\u06ce \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0626\u0627\u06af\u0627\u06cc \u0644\u06d5 \u062d\u0627\u06b5\u06cc \u0628\u0648\u0648.',
    '\u0633\u0627 \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5 \u0628\u06d5 \u0633\u0648\u0648\u0631\u0627\u06cc\u06cc \u062f\u06d5\u0648\u0631\u06cc \u0626\u0627\u0633\u06c6\u060c',
    '\u0628\u06d5\u0645 \u0634\u06d5\u0648\u06af\u0627\u0631\u06d5 \u0648 \u0628\u06d5 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u062f\u0627\u06cc \u062f\u06d5\u067e\u06c6\u0634\u06ce\u060c',
    '\u0628\u06d5\u0648 \u0645\u0627\u0646\u06af\u06d5 \u0648\u06d5\u062e\u062a\u06ce \u067e\u0695 \u062f\u06d5\u0628\u06ce\u060c',
    '\u0626\u06ce\u0648\u06d5 \u0628\u06ce \u0634\u06a9 \u0644\u06d5 \u0628\u0627\u0631\u06ce\u06a9\u06d5\u0648\u06d5 \u062a\u0648\u0648\u0634\u06cc \u0628\u0627\u0631\u06ce \u062a\u0631 \u062f\u06d5\u0628\u0646.',
    '\u0686\u06cc\u0627\u0646\u06d5 \u0626\u06d5\u0645\u0627\u0646\u06d5 \u0628\u0627\u0648\u06d5\u0695\u06ce \u0647\u06d5\u0631 \u0646\u0627\u0647\u06ce\u0646\u0646\u061f',
    '\u0647\u06d5\u0631 \u062f\u06d5\u0645\u06ce\u06a9\u06cc\u0634 \u0642\u0648\u0695\u0626\u0627\u0646\u06cc\u0627\u0646 \u0644\u06d5\u0644\u0627 \u062f\u06d5\u062e\u0648\u06ce\u0646\u0646\u060c \u0633\u0648\u0698\u062f\u06d5 \u0646\u0627\u0628\u06d5\u0646\u061f',
    '\u0646\u06d5\u062e\u06ce\u0631! \u062e\u0648\u062f\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06d5 \u062f\u0631\u06c6\u0634\u06cc \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u062e\u0648\u0627\u0634 \u0644\u06d5\u0648\u06cc \u0644\u06d5 \u062f\u06b5\u06cc\u0627\u0646\u062f\u0627\u06cc\u06d5 [\u0644\u06d5 \u06af\u0634 \u06a9\u06d5\u0633] \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u062a\u0631\u06d5.',
    '\u062a\u06c6\u0634 \u0645\u0632\u06af\u06ce\u0646\u06cc \u0628\u06d5\u0645\u0627\u0646 \u0628\u062f\u06d5\u060c \u062a\u0648\u0648\u0634\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u0628\u06d5\u0698\u0627\u0646 \u062f\u06ce\u0646\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u0628\u06d5\u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0646\u060c \u06a9\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u062f\u0648\u0627\u06cc\u06cc \u0646\u0627\u06cc\u06d5.',
    '\u0628\u06d5\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5 \u0628\u06d5\u06a9\u06d5\u0644\u0648\u0627\u0646\u06d5\u060c',
    '\u0628\u06d5 \u0695\u06c6\u0698\u06d5 \u062f\u06cc\u0627\u0631\u06cc \u06a9\u0631\u0627\u0648\u06d5\u06a9\u06d5\u0634\u060c',
    '\u0628\u06d5 \u0628\u06cc\u0646\u06d5\u0631 \u0648 \u0628\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u062f\u06ce\u062a\u06d5 \u0628\u06d5\u0631\u0686\u0627\u0648\u0627\u0646\u060c',
    '\u0642\u0695\u0627\u0646 \u06a9\u06d5\u0648\u062a\u06d5 \u0646\u06ce\u0648\u0627\u0646\u06cc \u062e\u06d5\u0646\u062f\u06d5\u06a9 \u0644\u06ce\u062f\u06d5\u0631\u0627\u0646.',
    '\u0647\u06d5\u0631 \u0646\u06ce\u06b5\u06d5\u06cc \u0628\u0648\u0648 \u06af\u0695\u06cc \u0628\u06d5\u0631\u0632\u06cc \u0626\u0627\u06af\u0631\u0628\u06d5\u0631\u0627\u0646.',
    '\u062e\u0648\u062f\u06cc \u062e\u06c6\u0634\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u06cc\u06d5\u0648\u06d5 \u0695\u06c6\u0646\u06cc\u0634\u062a\u0628\u0648\u0648\u0646.',
    '\u0686\u0627\u0648\u06d5\u062f\u06ce\u0631\u06cc \u0626\u06d5\u0648 \u06a9\u0627\u0631\u06d5\u0634 \u0628\u0648\u0648\u0646 \u06a9\u06d5 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06d5\u06a9\u0627\u0646\u06cc\u0627\u0646 \u062f\u06d5\u06a9\u0631\u062f.',
    '\u062a\u06d5\u0646\u06cc\u0627 \u0628\u06c6\u06cc\u06d5\u0634 \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u06d5 \u0695\u06a9\u062f\u0627 \u0686\u0648\u0648\u0628\u0648\u0648\u0646\u060c \u0647\u06d5\u0631 \u0686\u0648\u0646\u06a9\u06d5 \u062e\u0648\u0627\u06cc \u0647\u06d5\u0631\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a\u06cc \u0634\u0627\u06cc\u0627\u0646\u06cc \u062a\u0627\u0631\u06cc\u0641 \u06a9\u0631\u062f\u0646\u06cc\u0627\u0646 \u067e\u06d5\u0631\u0633\u062a\u0628\u0648\u0648.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0626\u0627\u0633\u0645\u0627\u0646\u06d5\u06a9\u0627\u0646 \u0648 \u0632\u06d5\u0645\u06cc\u0646\u06cc \u0644\u06d5\u0628\u06d5\u0631\u062f\u06d5\u0633\u062a\u06d5 \u0648 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u0628\u06d5\u0626\u0627\u06af\u0627\u06cc\u06d5.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u067e\u06cc\u0627\u0648\u06d5\u06a9\u0627\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u0648 \u0698\u0646\u06d5\u06a9\u0627\u0646\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631\u06cc\u0627\u0646 \u062e\u0633\u062a\u0628\u0648\u0648\u06d5 \u0646\u0627\u0648 \u0626\u0627\u06af\u0631\u06d5\u0648\u06d5 \u0648 \u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u0634\u062f\u0627 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646 \u0646\u06d5\u0628\u0628\u0648\u0648\u0646\u06d5\u0648\u06d5\u060c \u0628\u06ce \u0644\u06d5 \u0626\u0627\u0632\u0627\u0631\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645\u060c \u062a\u06c6\u06b5\u06cc \u0634\u06d5\u0648\u0627\u062a\u06d5\u06a9\u06d5\u0634 \u0647\u06d5\u06cc\u06d5.',
    '\u0628\u06c6 \u0626\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u062f\u06cc\u0646\u062f\u0627\u0631 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0646\u060c \u0628\u0627\u063a\u0627\u062a\u06ce \u0647\u06d5\u0633 \u062c\u06c6\u0628\u0627\u0631\u06cc\u0627\u0646 \u0628\u06d5 \u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627. \u0626\u06d5\u0645\u06d5\u06cc\u06d5 \u062f\u06d5\u0633\u06a9\u06d5\u0648\u062a\u06cc \u0645\u06d5\u0632\u0646.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646 \u0647\u06d5\u06b5\u0645\u06d5\u062a \u0628\u0631\u062f\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0632\u06c6\u0631 \u062d\u06d5\u0633\u062a\u06d5\u0645\u06d5.',
    '\u0626\u06d5\u0648 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0648\u06d5\u062f\u06cc \u062f\u06d5\u0647\u06ce\u0646\u06ce \u0648 \u062f\u0648\u0648\u0628\u0627\u0631\u06d5\u0634\u06cc \u062f\u06d5\u06a9\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634\u06d5 \u0639\u0627\u0641\u0648 \u062f\u06d5\u06a9\u0627 \u0648 \u0628\u06d5 \u06a4\u06cc\u0627\u0646\u06d5.',
    '\u062e\u0648\u062f\u0627\u0646\u06cc \u062a\u06d5\u062e\u062a\u06cc \u0628\u06d5 \u0642\u06d5\u062f\u0631\u06d5.',
    '\u0632\u06c6\u0631 \u0628\u06d5\u06a9\u0627\u0631\u06d5 \u0628\u06c6 \u0647\u06d5\u0631 \u0634\u062a\u06ce \u06af\u06d5\u0631\u06d5\u06a9\u06cc \u0628\u06ce.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u062a\u06c6 \u0647\u06d5\u0648\u0627\u06b5 \u0648 \u0628\u0627\u0633\u06cc \u0634\u06d5\u0695\u06a9\u06d5\u0631\u06d5\u06a9\u0627\u0646\u062a \u067e\u06ce\u06af\u06d5\u06cc\u06cc\u0634\u062a\u061f',
    '\u0644\u06d5\u0634\u06a9\u0631\u06d5\u06a9\u0627\u0646\u06cc \u0641\u06cc\u0631\u0639\u06d5\u0648\u0646 \u0648 \u0647\u06c6\u0632\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f\u061f',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0628\u06ce \u062f\u06cc\u0646\u0646 \u0644\u06d5\u0645\u0627\u0646\u06d5 \u0647\u06d5\u0631 \u062d\u0627\u0634\u0627 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u062e\u0648\u0627\u0634 \u0644\u06d5 \u0644\u0627\u06cc \u067e\u0634\u062a\u06d5\u0648\u06d5\u06cc\u0627\u0646\u0695\u0627 \u062f\u06d5\u0648\u0631\u06cc \u062f\u0627\u0648\u0646.',
    '\u0626\u0627\u0634\u06a9\u0631\u0627\u06cc\u06d5 \u0626\u06d5\u0645 \u0642\u0648\u0631\u0626\u0627\u0646\u06d5 \u0632\u06c6\u0631 \u0628\u06d5\u0695\u06ce\u0632\u06d5.',
    '\u0644\u06d5 \u062a\u06d5\u062a\u06d5\u06b5\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u06d5\u0631\u0632\u06cc \u067e\u0627\u0631\u0627\u0633\u062a\u0648\u0648 [\u0695\u0648\u0648\u0646\u0648\u0648\u0633 \u06a9\u0631\u0627\u0648\u06d5].',
    '\u0628\u06d5\u0648 \u0639\u06d5\u0632\u0645\u0627\u0646\u06d5 \u0648 \u0628\u06d5\u0648\u06d5\u06cc \u06a9\u06d5 \u0634\u06d5\u0648 \u062e\u06c6 \u0695\u0627\u062f\u06d5\u0646\u06ce\u060c',
    '\u06a9\u0648\u0648 \u062f\u06d5\u0632\u0627\u0646\u06cc \u0626\u06d5\u0648 \u0634\u06d5\u0648\u06af\u06d5\u0631\u062f\u06d5 \u06a9\u0627\u0645\u06d5\u06cc\u0627\u0646\u06d5\u061f',
    '\u0626\u06d5\u0633\u062a\u06ce\u0631\u06d5\u06a9\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u06af\u06d5\u0634\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u06af\u06cc\u0627\u0646\u06cc \u062f\u06d5\u0628\u06d5\u0631\u062f\u0627\u060c \u067e\u0627\u0633\u06d5\u0648\u0627\u0646\u06cc \u0647\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631\u06d5.',
    '\u0628\u0627 \u0645\u0631\u06c6 \u0644\u06d5 \u062e\u06c6 \u0695\u0627\u0645\u06ce\u0646\u06ce \u06a9\u06d5 \u0644\u06d5 \u0686\u06cc \u0648\u06d5\u062f\u06cc \u0647\u0627\u062a\u0648\u0648\u06d5.',
    '\u0644\u06d5 \u062a\u0631\u0627\u0648\u06ce \u0641\u0695\u06a9\u06d5 \u062f\u06d5\u06a9\u0627.',
    '\u0644\u06d5 \u0646\u06ce\u0648\u0627\u0646 \u067e\u0634\u062a \u0648 \u0628\u06d5\u0631\u0645\u0644\u0627\u0646 \u062f\u06ce\u062a\u06d5 \u062f\u06d5\u0631\u06ce.',
    '\u062e\u0648\u0627 \u062f\u06d5\u062a\u0648\u0627\u0646\u06ce \u062c\u0627\u0631\u06ce \u062a\u0631 \u0628\u06cc\u0647\u06ce\u0646\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u06a9\u06d5 \u0646\u0647\u06ce\u0646\u06cc \u062e\u06c6\u06cc\u0627 \u062f\u06d5\u0628\u0646.',
    '\u0626\u06d5\u0648 \u0646\u06d5 \u0647\u06cc\u0686 \u0647\u06ce\u0632\u06ce\u06a9\u06cc \u0647\u06d5\u06cc\u06d5\u060c \u0646\u06d5 \u06cc\u0627\u0631\u06cc\u06a9\u0627\u0631.',
    '\u0628\u06d5\u0648 \u062d\u06d5\u0648\u0627\u06cc\u06d5\u06cc \u0626\u0627\u0648 \u0647\u06d5\u06b5\u062f\u06d5\u06af\u0631\u06ce \u0648 \u062f\u06d5\u06cc\u062f\u0627\u062a\u06d5\u0648\u06d5\u060c',
    '\u0628\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0634 \u06a9\u06d5 \u0632\u06c6\u0631 \u062f\u0631\u0632 \u0648 \u06a9\u06d5\u0644\u06ce\u0646\u06cc \u0647\u06d5\u0646\u060c',
    '\u0626\u06d5\u0645 \u0642\u0648\u0631\u0639\u0627\u0646\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u06ce\u0634\u06d5 \u062f\u06d5\u0628\u0695\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06cc\u0686 \u0642\u0633\u06d5\u06cc \u0644\u0627\u0642\u0631\u062f\u06cc\u0634 \u0646\u06cc\u06cc\u06d5.',
    '\u0626\u06d5\u0648 \u062e\u06d5\u0631\u06cc\u06a9\u0646 \u0641\u06ce\u06b5\u06ce\u06a9 \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0648\u0627 \u0645\u0646\u06cc\u0634 \u0641\u06ce\u06b5\u06cc \u062e\u06c6\u0645 \u062f\u06d5\u06a9\u06d5\u0645.',
    '\u0633\u0627 \u062a\u06c6 \u0645\u0627\u0648\u06d5\u06cc \u0626\u06d5\u0645 \u062f\u06cc\u0646 \u062f\u0648\u0698\u0645\u0646\u0627\u0646\u06d5 \u0628\u062f\u06d5\u060c \u06a9\u06d5\u0645\u06ce\u06a9\u06cc\u0627\u0646 \u0644\u06ce \u0695\u0627\u0628\u0648\u06d5\u0633\u062a\u06d5.',
    '\u067e\u06d5\u0633\u0646\u06cc \u0646\u0627\u0648\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u067e\u0627\u06cc\u06d5\u0628\u06d5\u0631\u0632\u062a \u0628\u062f\u06d5.',
    '\u0626\u06d5\u0648\u06d5 \u062f\u0646\u06cc\u0627\u06cc \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0646\u062f \u0648 \u0644\u06d5\u0628\u0627\u0631\u06cc \u06a9\u0631\u062f.',
    '\u0628\u06c6 \u0647\u06d5\u0631 \u0634\u062a \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5\u06cc \u062f\u0627\u0646\u0627 \u0648 \u0695\u06ce\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627.',
    '\u0626\u06d5\u0648\u06d5 \u0686\u0627\u06cc\u06d5\u0631\u06cc \u062e\u0633\u062a\u06d5 \u0695\u0648\u0648.',
    '\u06a9\u0631\u062f\u06cc\u06d5 \u0634\u06cc\u0646\u06cc \u0626\u0627\u0645\u0627\u06b5 \u0695\u06d5\u0634 \u0648 \u067e\u0648\u0648\u0634 \u0648 \u067e\u0627\u0631\u06cc\u0634.',
    '\u0626\u06ce\u0645\u06d5 \u0626\u06d5\u0648\u06d5\u062a \u067e\u06ce \u062f\u06d5\u062e\u0648\u06ce\u0646\u06cc\u0646\u060c \u0626\u06cc\u062a\u0631 \u062a\u06c6 \u0644\u06d5 \u0628\u06cc\u0631\u062a \u0646\u0627\u0686\u06ce.',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0634\u062a\u06ce \u062e\u0648\u0627 \u062d\u06d5\u0632 \u0628\u06a9\u0627\u061b \u0626\u06d5\u0648 \u0644\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u0648 \u0646\u0647\u06ce\u0646\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5.',
    '\u0644\u06d5 \u0628\u06d5\u0631\u06cc\u0634\u062a \u0647\u0627\u0633\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646\u060c \u0647\u06d5\u062a\u0627 \u0647\u0627\u0633\u0627\u0646\u062a\u0631\u06cc\u0646 \u0695\u06ce\u06af\u06d5 \u067e\u06d5\u06cc\u062f\u0627 \u0628\u06a9\u06d5\u06cc.',
    '\u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0628\u06a9\u06d5\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0628\u06d5\u0647\u0631\u06d5 \u0628\u062f\u0627.',
    '\u06a9\u06d5\u0633\u06ce \u0644\u06d5 \u062e\u0648\u0627 \u062a\u0631\u0633\u06cc \u0647\u06d5\u0628\u06ce\u060c \u0647\u06d5\u0631 \u0632\u0648\u0648 \u067e\u06d5\u0646\u062f\u06cc \u0644\u06ce \u0648\u06d5\u0631\u062f\u06d5\u06af\u0631\u06ce.',
    '\u0628\u06d5\u062f\u0641\u06d5\u0695\u06d5\u06a9\u06d5\u0634 \u0647\u06d5\u0631 \u062e\u06c6\u06cc \u0644\u06ce \u062f\u0648\u0648\u0631 \u062f\u06d5\u062e\u0627\u062a\u06d5\u0648\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u06a9\u06d5 \u062f\u06d5\u0686\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u0626\u0627\u0631\u06cc \u06af\u06d5\u0648\u0631\u06d5.',
    '\u067e\u0627\u0634\u0627\u0646 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0646\u06d5 \u062f\u06d5\u0645\u0631\u06ce\u060c \u0646\u06d5 \u0632\u06cc\u0646\u062f\u06cc\u0634\u06d5.',
    '\u062f\u06cc\u0627\u0631\u06cc\u0634\u06d5 \u0647\u06d5\u0631 \u06a9\u06ce \u067e\u0627\u06a9 \u0698\u06cc\u0627\u060c \u0695\u0632\u06af\u0627\u0631 \u0628\u0648\u0648\u06d5.',
    '\u06cc\u0627\u062f\u06cc \u0646\u06ce\u0648\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u06a9\u0631\u062f \u0648 \u0646\u0648\u06ce\u0698\u06cc \u0628\u06d5\u062c\u06ce \u0647\u06ce\u0646\u0627.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06ce\u0648\u06d5 \u0698\u06cc\u0646\u06cc \u062f\u0646\u06cc\u0627 \u0647\u06d5\u06b5\u062f\u06d5\u0628\u0698\u06ce\u0631\u0646.',
    '\u06a9\u06d5 \u0686\u06cc \u0644\u06d5\u0648 \u0644\u0627\u060c \u0628\u0627\u0634\u062a\u0631 \u0648 \u0628\u06d5\u067e\u06ce\u0632\u062a\u0632 \u0647\u06d5\u06cc\u06d5.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0626\u06d5\u0645\u06d5 \u0644\u06d5 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5 \u067e\u06ce\u0634\u06cc\u0646\u06d5\u06a9\u0627\u0646\u06cc\u0634\u062f\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06d5\u06cc\u06ce \u06a9\u06d5 \u062f\u0631\u0627\u0648\u0646 \u0628\u06d5 \u0626\u06cc\u0628\u0695\u0627\u0647\u06cc\u0645 \u0648 \u0628\u06d5 \u0645\u0648\u0648\u0633\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0628\u0627\u0633\u06cc \u0628\u06d5\u06b5\u0627\u06cc \u06af\u06d5\u0648\u0631\u06d5\u062a \u0628\u06c6 \u0647\u0627\u062a\u0648\u0648\u06d5\u060c \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0634\u062a \u062f\u0627\u062f\u06d5\u067e\u06c6\u0634\u06ce\u061f',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u06af\u06d5\u0644\u06ce \u0695\u0648\u062e\u0633\u0627\u0631 \u0628\u0632\u0695\u06a9\u0627\u0648\u0646.',
    '\u0644\u06d5 \u06a9\u0627\u0631 \u06a9\u0631\u062f\u0646\u062f\u0627 \u0686\u06d5\u0648\u0633\u0627\u0648\u0646.',
    '\u06af\u0631\u0641\u062a\u0627\u0631\u06cc \u0626\u0627\u0648\u0631\u06ce\u06a9\u06cc \u0633\u0648\u062a\u06ce\u0646\u06d5\u0631 \u062f\u06d5\u0628\u0646.',
    '\u06a4\u06d5\u062e\u0627\u0631\u0646\u06cc\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631\u0686\u0627\u0648\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u06af\u06d5\u0631\u0645\u06d5\u0648\u06d5 \u0628\u06c6 \u062f\u06ce\u0646\u0646.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u0628\u0698\u06cc\u0648\u06cc\u0627\u0646 \u0646\u0627\u062f\u06d5\u0646\u06ce\u060c \u0645\u06d5\u06af\u06cc\u0646 \u0644\u06d5 \u062f\u0695\u06a9\u06d5\u0634\u06cc\u0644\u0627\u0646\u06ce.',
    '\u0642\u06d5\u06b5\u06d5\u0648 \u0646\u0627\u06a9\u0627\u0648 \u0644\u06d5 \u0646\u06ce\u0632\u06cc\u0634 \u0628\u06ce \u0646\u06cc\u0627\u0632 \u0646\u0627\u06a9\u0627.',
    '\u0695\u0648\u062e\u0633\u0627\u0631\u06cc\u0634 \u0647\u06d5\u0646 \u062e\u06c6\u0634 \u0648 \u06af\u06d5\u0634\u0646.',
    '\u0695\u0627\u0632\u06cc\u0646 \u0644\u06d5 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u06cc \u062e\u06c6\u06cc\u0627\u0646\u061b',
    '\u0644\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc \u0628\u06d5\u0631\u0632 \u0648 \u062a\u06d5\u0631\u0632\u0627.',
    '\u06a9\u06d5 \u0644\u06d5\u0648\u06ce\u062f\u0627 \u0642\u0633\u06d5\u06cc \u067e\u0695 \u0648 \u067e\u0648\u0686 \u0646\u0627\u0628\u06cc\u0633\u0646.',
    '\u0686\u06d5\u0645\u06d5\u06cc \u0695\u0648\u0648\u0646 \u0648 \u0695\u06d5\u0648\u0627\u0646\u06cc \u0641\u0631\u06d5\u06cc \u062a\u06ce\u062f\u0627\u06cc\u06d5.',
    '\u062a\u06d5\u062e\u062a\u06cc \u062a\u06ce\u062f\u0627 \u062f\u0627\u0646\u062f\u0631\u0627\u0648\u0646.',
    '\u0632\u06d5\u0631\u06a9\u06cc\u0634\u06cc\u0627\u0646 \u0644\u06d5\u0644\u0627 \u062f\u0627\u0646\u0627\u0648\u0646.',
    '\u0628\u0627\u06b5\u06af\u06d5\u06cc \u0646\u06d5\u0631\u0645\u06cc\u0634 \u0695\u06cc\u0632 \u06a9\u0631\u0627\u0648\u0646.',
    '\u0632\u06c6\u0631\u06cc\u0634 \u0695\u0627\u06cc\u06d5\u062e \u0695\u0627\u062e\u0631\u0627\u0648\u0646.',
    '\u0626\u06d5\u0645\u0627\u0646\u06d5 \u0686\u06c6\u0646 \u0695\u0627\u0646\u06d5\u0645\u0627\u0648\u0646\u060c \u06a9\u06d5 \u0648\u0634\u062a\u0631 \u0686\u0644\u06c6\u0646 \u0686\u06ce \u0628\u0648\u0648\u06d5\u061f',
    '\u0644\u06d5\u0648 \u0639\u06d5\u0632\u0645\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0686\u06c6\u0646 \u0648\u0627 \u0628\u06b5\u06cc\u0646\u062f \u06a9\u0631\u0627\u0648\u06d5\u061f',
    '\u0644\u06d5\u0648 \u06a9\u06d5\u0698\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0686\u06c6\u0646 \u0648\u06d5\u0647\u0627 \u062f\u0627\u0645\u06d5\u0632\u0631\u0627\u0648\u06d5\u061f',
    '\u0644\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0634 \u06a9\u06d5 \u0686\u06c6\u0646 \u0648\u0627 \u0647\u06d5\u0631\u0627\u0648 \u0695\u0627\u062e\u0631\u0627\u0648\u06d5\u061f',
    '\u0633\u0627 \u062a\u06c6 \u0626\u0627\u0645\u06c6\u0698\u06af\u0627\u0631\u06cc \u0628\u06a9\u06d5\u060c \u0626\u06d5\u062a\u06c6 \u0647\u06d5\u0631 \u062a\u06d5\u0646\u06cc\u0627 \u067e\u06d5\u0646\u062f\u06ce\u0631\u06cc.',
    '\u062a\u06c6 \u0644\u06d5\u0633\u06d5\u0631\u06cc\u0627\u0646 \u0646\u06d5\u0628\u0648\u0648\u06cc\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u062f\u06d5\u0633\u062a\u06d5\u06b5\u0627\u062a.',
    '\u0645\u06d5\u0631 \u0626\u06d5\u0648 \u06a9\u06d5\u0633\u06d5\u06cc \u0695\u0648\u0648 \u0648\u06d5\u0631\u06af\u06ce\u0695\u06ce \u0648 \u0646\u06d5\u06cc\u06d5\u0648\u06ce \u062e\u0648\u062f\u0627 \u0628\u0646\u0627\u0633\u06ce.',
    '\u0626\u06d5\u0648\u0633\u0627 \u062e\u0648\u062f\u0627 \u0628\u06d5 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5\u06cc\u06ce \u06af\u06d5\u0648\u0631\u06d5\u062a\u0631 \u0633\u0632\u0627\u06cc \u0626\u06d5\u062f\u0627.',
    '\u0628\u06ce \u06af\u0648\u0645\u0627\u0646\u06d5 \u06a9\u06d5 \u0647\u06d5\u0645\u0648\u0648\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0628\u06c6 \u0644\u0627\u06cc \u0626\u06ce\u0645\u06d5 \u062f\u06ce\u0646\u06d5\u0648\u06d5.',
    '\u0644\u06d5 \u062f\u0648\u0627\u06cc\u06cc\u062f\u0627 \u062e\u06c6\u0645\u0627\u0646 \u0628\u06d5 \u06a9\u0627\u0631\u06cc\u0627\u0646 \u0695\u0627\u062f\u06d5\u06af\u06d5\u06cc\u0646.',
    '\u0633\u06c6\u0646\u062f\u06d5\u06a9\u06d5 \u0628\u06d5 \u0628\u06d5\u0631\u06cc \u0628\u06d5\u06cc\u0627\u0646\u060c',
    '\u0644\u06d5 \u0634\u06d5\u0648\u0627\u0646\u06cc\u0634\u062f\u0627 \u0628\u06d5 \u062f\u06d5\u06cc\u0627\u0646\u060c',
    '\u0628\u06d5 \u062c\u0648\u0648\u062a\u060c \u0628\u06d5 \u062a\u0627\u06a9\u060c',
    '\u0628\u06d5\u0648 \u0634\u06d5\u0648\u06af\u0627\u0631\u06d5 \u06a9\u06d5\u0695\u0627\u062f\u06d5\u0628\u0631\u06ce\u060c',
    '\u0626\u0627\u062e\u06c6 \u0633\u0648\u06ce\u0646\u062f\u06cc \u0648\u06d5\u0647\u0627 \u062e\u0648\u0627\u0631\u062f\u0646\u060c \u0628\u06c6 \u062e\u0627\u0648\u06d5\u0646 \u0626\u0627\u0648\u06d5\u0632 \u0628\u06d5\u0633 \u0646\u06cc\u06cc\u06d5\u061f',
    '\u062e\u06c6 \u062f\u06cc\u062a\u062a \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0686\u06cc \u06a9\u0631\u062f \u0628\u06d5\u0631\u0627\u0646\u0628\u06d5\u0631 \u0647\u06c6\u0632\u06cc \u0639\u0627\u062f\u061b',
    '\u0628\u06d5\u0631\u06d5\u06cc \u0626\u06cc\u0631\u06d5\u0645 \u062e\u06ce\u0648\u06cc \u062e\u0627\u0646\u0648\u0648\u06cc \u0626\u06d5\u0633\u062a\u0648\u0648\u0646\u062f\u0627\u0631\u06cc [\u062a\u06d5\u0631\u0632 \u0648 \u06af\u0648\u0634\u0627\u062f]\u061b',
    '\u0626\u06d5\u0648 \u0645\u0627\u06b5\u0627\u0646\u06d5\u06cc \u0648\u06ce\u0646\u06d5\u06cc\u0627\u0646 \u0644\u06d5 \u0634\u0627\u0631\u0627\u0646\u06cc\u0634\u062f\u0627 \u0686\u06ce \u0646\u06d5\u06a9\u0631\u0627\u0628\u0648\u0648.',
    '\u0647\u06c6\u0632\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f\u06cc\u0634\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u062f\u06c6\u06b5 \u0648 \u06af\u06d5\u06b5\u06cc\u06d5\u06a9\u0627\u0646\u062f\u0627 \u0628\u06d5\u0631\u062f\u06cc\u0627\u0646 \u062a\u0627\u0634\u06cc.',
    '\u0641\u06cc\u0631\u0639\u06d5\u0648\u0646\u06cc\u0634\u060c \u062e\u06ce\u0648\u06cc \u06a9\u06c6\u0634\u06a9\u0627\u0646\u06cc \u06af\u06d5\u0648\u0631\u06d5 \u0648 \u0628\u06b5\u0646\u062f\u061b',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u0648\u06b5\u0627\u062a\u0627\u0646\u062f\u0627 \u06cc\u0627\u062e\u06cc \u0628\u0628\u0648\u0648\u0646.',
    '\u062e\u0631\u0627\u067e \u0647\u06d5\u0628\u0648\u0648\u060c \u0626\u06d5\u0648\u0627\u0646 \u0632\u06ce\u062f\u06d5\u062a\u0631\u06cc\u0634\u06cc\u0627\u0646 \u06a9\u0631\u062f.',
    '\u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6 \u0648\u06d5\u0628\u06d5\u0631 \u0642\u06d5\u0645\u0686\u06cc \u0626\u0627\u0632\u0627\u0631\u06cc \u062f\u0627\u0646.',
    '\u0628\u06ce \u0633\u06c6 \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062a\u06c6\u060c \u0647\u06d5\u0645\u0648\u0648 \u0626\u06d5\u0648\u0627\u0646\u06d5 \u062f\u06d5\u0628\u06cc\u0646\u06ce.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0645\u0631\u06c6 \u0647\u06d5\u0631 \u06a9\u0627\u062a\u06ce\u06a9\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062f\u06d5\u06cc\u062e\u0627\u062a\u06d5 \u0628\u06d5\u0631 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06d5\u0648\u06d5 \u0648 \u0695\u06ce\u0632\u06cc \u0644\u06ce \u062f\u06d5\u06af\u0631\u06ce \u0648 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u067e\u06ce \u062f\u06d5\u06af\u06d5\u06cc\u06d5\u0646\u06ce\u060c \u0626\u06ce\u0698\u06ce: [\u0626\u06d5\u0648\u0627] \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0642\u06d5\u062f\u0631\u06cc \u06af\u0631\u062a\u0645.',
    '\u0626\u06d5\u06af\u06d5\u0631\u06cc\u0634 \u2013\u0647\u06d5\u0631 \u0628\u06c6 \u0626\u06d5\u0632\u0645\u0648\u0648\u0646\u06ce\u2013 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0628\u0698\u06cc\u0648\u06cc \u0644\u06ce \u06a9\u06d5\u0645 \u06a9\u0631\u062f\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u0633\u0627 \u0626\u06ce\u0698\u06ce: \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u0645 \u0633\u0648\u0648\u06a9\u0627\u06cc\u06d5\u062a\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u0647\u06ce\u0646\u0627\u0645.',
    '\u0646\u06d5\u062e\u06ce\u0631\u060c \u0628\u06d5\u06b5\u06a9\u0648\u0648 \u0626\u06ce\u0648\u06d5 \u0695\u06ce\u0632 \u0644\u06d5 \u0647\u06d5\u062a\u06cc\u0648 \u0646\u0627\u0646\u06ce\u0646.',
    '\u0628\u06c6 \u0646\u0627\u0646 \u0628\u06d5 \u0647\u06d5\u0698\u0627\u0631 \u0628\u06d5\u062e\u0634\u06cc\u0646\u06cc\u0634\u060c \u0626\u06ce\u0648\u06d5 \u0647\u0627\u0646\u06d5\u06cc \u06cc\u06d5\u06a9\u062a\u0631 \u0646\u0627\u062f\u06d5\u0646.',
    '\u0645\u06cc\u0631\u0627\u062a\u06cc\u0634 \u0628\u06d5 \u0647\u06d5\u06b5\u067e\u06d5 \u062f\u06d5\u062e\u06c6\u0646.',
    '\u062e\u06c6\u0634\u06d5\u0648\u06cc\u0633\u062a\u0646\u06cc \u0645\u0627\u06b5\u06cc\u0634\u062a\u0627\u0646 \u0644\u06d5 \u0626\u06d5\u0646\u062f\u0627\u0632\u06d5 \u062f\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0648\u0648\u06d5.',
    '\u0646\u0627\u0628\u06ce \u0648\u0627\u0628\u06ce! \u0647\u06d5\u0631\u06af\u0627 \u0632\u06d5\u0645\u06cc\u0646 \u0632\u06c6\u0631 \u0628\u06d5 \u0633\u06d5\u062e\u062a\u06cc \u062a\u06ce\u06a9 \u0634\u06a9\u06ce\u0646\u0631\u06ce \u0648\u060c',
    '\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06ce \u0648 \u0641\u0631\u06cc\u0634\u062a\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u067e\u06c6\u0644 \u0628\u06d5 \u067e\u06c6\u0644 \u0695\u06cc\u0632 \u0628\u0628\u06d5\u0633\u062a\u0646\u060c',
    '\u062f\u06c6\u0698\u06d5\u0647\u06cc\u0634 \u0628\u06ce\u0646\u0646\u06d5 \u067e\u06ce\u0634\u06d5\u0648\u06d5\u060c \u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0645\u0631\u06c6 \u062e\u06c6\u06cc \u0648\u06d5\u0628\u06cc\u0631 \u062f\u06ce\u062a\u06d5\u0648\u06d5\u061b \u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0645 \u0648\u06d5\u0628\u06cc\u0631 \u0647\u0627\u062a\u0646\u06d5\u0648\u06d5\u060c \u06a9\u0648\u0627 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u067e\u06ce \u062f\u06d5\u06af\u06d5\u06cc\u06ce\u0646\u06ce\u061f',
    '\u0626\u06ce\u0698\u06ce: \u062e\u06c6\u0632\u06cc \u0628\u06c6 \u0698\u06cc\u0627\u0646\u0645 \u0686\u0627\u06a9\u06d5\u0645 \u0648\u06d5\u067e\u06ce\u0634 \u062e\u06c6\u0645 \u062e\u0633\u062a\u0628\u0627.',
    '\u0633\u0627 \u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u06a9\u06d5\u0633 \u0648\u06d5\u06a9 \u0626\u06d5\u0648 \u062c\u06d5\u0632\u0631\u06d5\u0628\u06d5 \u0646\u0627\u062f\u06d5\u0646.',
    '\u06a9\u06d5\u0633\u06cc\u0634 \u0648\u06d5\u06a9 \u0626\u06d5\u0648 \u0628\u06d5 \u0632\u0646\u062c\u06cc\u0631 \u0634\u06d5\u062a\u06d5\u06a9 \u0646\u0627\u062f\u0631\u06ce.',
    '\u062a\u06c6\u0634 \u0626\u06d5\u06cc \u06af\u06cc\u0627\u0646\u06cc \u0644\u06d5\u062e\u06d5\u0645 \u0695\u06d5\u062e\u0633\u06cc\u0648!',
    '\u0628\u06d5 \u062f\u06b5\u062e\u06c6\u0634\u06cc \u0628\u0686\u06c6\u0648\u06d5 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a\u061b \u0686\u0648\u0646 \u0626\u06d5\u0648\u062a \u0644\u06d5 \u062e\u06c6 \u0695\u0627\u0632\u06cc \u06a9\u0631\u062f.',
    '\u0633\u0627 \u062a\u06c6\u0634 \u0695\u06d5\u06af\u06d5\u06b5 \u0639\u06d5\u0628\u062f\u06d5\u06a9\u0627\u0646\u06cc \u0645\u0646 \u0628\u06a9\u06d5\u0648\u06d5.',
    '[\u0648\u06ce\u0695\u0627\u06cc \u0626\u06d5\u0648\u0627\u0646] \u0628\u0686\u0648\u06d5 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc \u0645\u0646\u06d5\u0648\u06d5.',
    '\u0633\u06c6\u0646\u062f\u06ce \u062f\u06d5\u062e\u06c6\u0645 \u0628\u06d5\u0645 \u0628\u0627\u0698\u06ce\u0695\u06d5\u060c',
    '\u062a\u06c6\u0634 \u0646\u06cc\u0634\u062a\u06d5\u062c\u06ce\u06cc \u0626\u06d5\u0645 \u0628\u0627\u0698\u06ce\u0695\u06d5\u06cc\u061b',
    '\u0628\u06d5 \u0628\u0627\u0628 \u0648 \u0646\u06d5\u062a\u06d5\u0648\u06d5\u06a9\u06d5\u0634\u06cc\u060c',
    '\u0626\u06ce\u0645\u06d5 \u062c\u0633\u0646\u0627\u062a\u06cc \u0645\u0631\u06c6\u0645\u0627\u0646 \u062f\u06d5\u06af\u06d5\u06b5 \u062e\u06d5\u0645\u0627\u0646 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06ce\u0633\u062a\u0627 \u0648\u0627 \u0628\u06cc\u0631 \u062f\u06d5\u0628\u0627 \u0647\u06d5\u0631\u06af\u06cc\u0632 \u06a9\u06d5\u0633 \u062f\u06d5\u0631\u06c6\u0633\u062a\u06cc \u0646\u0627\u06cc\u06d5\u061f',
    '\u0626\u06ce\u0698\u06ce: \u0633\u0627\u0645\u0627\u0646\u06ce\u06a9\u06cc \u0632\u06c6\u0631\u0645 \u0644\u06d5\u062f\u06d5\u0633 \u062f\u0627\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0648\u0644\u06c6 \u062e\u06d5\u06cc\u0627\u06b5 \u062f\u06d5\u06a9\u0627 \u06a9\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0626\u06d5\u0648\u06cc \u0646\u06d5\u062f\u06cc\u0648\u06d5\u061f',
    '\u0626\u06d5\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u062f\u0648\u0648 \u062f\u06cc\u062f\u06d5\u0645\u0627\u0646 \u067e\u06ce \u0646\u06d5\u062f\u0627\u0648\u06d5\u061f',
    '\u0632\u0645\u0627\u0646\u06ce\u06a9\u06cc\u0634 \u0648 \u062c\u0648\u0648\u062a\u06ce \u0644\u06ce\u0648\u061f',
    '\u0695\u06ce\u06af\u06d5\u06cc \u0647\u0627\u062a \u0648 \u0646\u06d5\u0647\u0627\u062a\u06cc\u0634\u0645\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0646\u06d5\u062f\u0627\u061f',
    '\u0648\u06d5\u0644\u06ce\u06a9\u0627\u0646 \u0626\u06d5\u0648 \u062e\u06c6\u06cc \u0644\u06d5 \u0695\u06ce\u06af\u06d5\u06cc \u0647\u06d5\u06b5\u06d5\u062a \u0646\u06d5\u062f\u0627\u061f',
    '\u06a9\u0648\u0648 \u062f\u06d5\u0632\u0627\u0646\u06cc \u0645\u06d5\u0628\u06d5\u0633 \u0644\u06d5 \u0695\u06ce\u06cc \u0647\u06d5\u06b5\u06d5\u062a \u0686\u06cc\u06cc\u06d5\u061f',
    '\u0626\u0627\u0632\u0627\u062f\u06a9\u0631\u062f\u0646\u06cc \u06a9\u06c6\u06cc\u0644\u06d5\u06cc\u06d5.',
    '\u06cc\u0627 \u0646\u0627\u0646 \u062f\u0627\u0646\u06d5\u060c \u0644\u06d5 \u0695\u06c6\u0698\u06cc \u0642\u0627\u062a\u06cc \u0648 \u0642\u0695\u06cc\u062f\u0627.',
    '\u0628\u06d5 \u0647\u06d5\u062a\u06cc\u0648\u06ce\u06a9\u06cc \u0628\u06ce \u06a9\u06d5\u0633\u06cc \u0646\u0632\u06cc\u06a9\u06cc \u062e\u06c6\u06cc.',
    '\u06cc\u0627\u0646 \u0647\u06d5\u0698\u0627\u0631\u06ce\u06a9\u06cc \u062e\u0627\u06a9\u06d5\u0633\u06d5\u0631.',
    '\u062f\u06cc\u0633\u0627\u0646 \u0626\u06d5\u0628\u06ce \u0644\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u06ce \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0628\u06c6 \u062e\u06c6\u06af\u0631\u06cc \u0648 \u062f\u0644\u06c6\u06a4\u0627\u0646\u06cc\u060c \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0627\u0646 \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5.',
    '\u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u062e\u062a\u06d5\u0648\u06d5\u0631\u0627\u0646\u0646.',
    '\u06a9\u06d5\u0633\u0627\u0646\u06ce\u06a9\u06cc\u0634 \u06a9\u06d5 \u0628\u06d5 \u0646\u06cc\u0634\u0627\u0646\u0627\u0646\u06cc \u0626\u06ce\u0645\u06d5 \u0628\u06ce \u0628\u0695\u0648\u0627 \u0628\u0648\u0648\u0646\u060c \u0626\u06d5\u0648\u0627\u0646\u06d5 \u0628\u06d5\u062f\u0628\u06d5\u062e\u062a\u06d5\u06a9\u0627\u0646\u0646.',
    '\u0686\u0627\u0631\u06d5\u0646\u0648\u0648\u0633\u06cc\u0627\u0646 \u0626\u0627\u06af\u0631\u06ce \u0628\u06ce \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06d5.',
    '\u0633\u0648\u06ce\u0646\u062f \u0628\u06d5 \u062e\u06c6\u0631 \u0648 \u0628\u06d5 \u062a\u06cc\u0634\u06a9\u06cc\u060c',
    '\u0628\u06d5\u0645 \u0647\u06d5\u06cc\u06a4\u06d5\u0634 \u06a9\u06d5 \u0628\u06d5\u062f\u0648\u0648\u06cc\u062f\u0627 \u062f\u06ce\u060c',
    '\u0633\u06c6\u0646\u062f \u0628\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u0634 \u06a9\u06d5 \u062a\u06cc\u0641 \u062a\u06cc\u0641\u06d5\u06cc \u0647\u06d5\u062a\u0627\u0648 \u062f\u06d5\u062f\u0627\u060c',
    '\u0628\u06d5\u0648 \u0634\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u062f\u0627\u06cc \u062f\u06d5\u067e\u06c6\u0634\u06ce\u060c',
    '\u0628\u06d5\u0648 \u0639\u0627\u0633\u0645\u0627\u0646\u06d5 \u0648 \u0628\u06d5\u0648\u06d5\u06cc \u0626\u06d5\u0648\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u060c',
    '\u0628\u06d5\u0648 \u0632\u06d5\u0645\u06cc\u0646\u06d5 \u0648 \u0628\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u0648\u0627 \u067e\u0627\u0646\u06cc \u0695\u0627\u062e\u0633\u062a\u060c',
    '\u0628\u06d5 \u06af\u06cc\u0627\u0646\u06cc \u0645\u0631\u06c6 \u0633\u0648\u06ce\u0646\u062f\u06d5\u06a9\u06d5\u0648 \u0628\u06d5\u0648\u06d5\u0634 \u06a9\u06d5 \u0695\u06ce\u06a9\u06cc \u062e\u0633\u062a\u0648\u06d5\u060c',
    '\u0626\u06d5\u0648\u0633\u0627 \u0647\u06d5\u0645\u0648\u0648 \u062a\u0627\u0648\u0627\u0646\u0628\u0627\u0631\u06cc \u0648 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u06cc \u062f\u06d5 \u0648\u06cc\u0698\u062f\u0627\u0646\u06cc \u06af\u06d5\u06cc\u0627\u0646\u062f\u0648\u0648\u06d5.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0647\u06d5\u0631 \u06a9\u06ce \u06af\u06cc\u0627\u0646\u06cc \u062e\u0627\u0648\u06ce\u0646 \u0695\u0627\u06af\u0631\u062a\u0648\u0648\u06d5\u060c \u0695\u0632\u06af\u0627\u0631 \u0628\u0648\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u062f\u06d5 \u06af\u0648\u0646\u0627\u0647\u06cc\u06d5\u0648\u06d5 \u067e\u06ce\u0686\u0627\u0648\u06d5\u060c \u062f\u06c6\u0695\u0627\u0646\u062f\u0648\u0648\u06cc\u06d5.',
    '\u0647\u06c6\u0632\u06cc \u0633\u06d5\u0645\u0648\u0648\u062f \u0686\u0648\u0646 \u0644\u06d5 \u0695\u0627\u062f\u06d5 \u0644\u0627\u06cc\u0627\u0646 \u062f\u0627\u0628\u0648\u0648\u060c \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u0628\u06d5 \u062e\u0648\u062f\u0627 \u0646\u06d5\u0628\u0648\u0648.',
    '\u06a9\u06d5 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u0686\u0627\u0631\u06d5 \u0695\u06d5\u0634\u062a\u0631 \u0695\u0627\u0633\u062a\u06d5\u0648\u06d5 \u0628\u0648\u0648.',
    '\u0626\u06d5\u0648\u0633\u0627 \u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06cc \u062e\u0648\u062f\u0627 \u067e\u06ce\u06cc\u0627\u0646\u06cc \u0648\u062a: \u0644\u06d5 \u062d\u0648\u0634\u062a\u0631 \u062e\u0648\u0627 \u0648 \u0644\u06d5 \u0626\u0627\u0648\u062f\u0627\u0646\u06cc \u0626\u0627\u06af\u0627\u062f\u0627\u0631 \u0628\u0646.',
    '\u062c\u0627 \u0628\u0627\u0648\u06d5\u0695\u06cc\u0627\u0646 \u067e\u06ce \u0646\u06d5\u06a9\u0631\u062f \u0648 \u067e\u06d5\u06cc\u0627\u0646 \u06a9\u0631\u062f. \u0626\u06d5\u0648\u0633\u0627 \u06a9\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646\u060c \u0644\u06d5 \u0633\u06c6\u0646\u06af\u06d5\u06cc \u06af\u0648\u0646\u0627\u0647\u06d5\u06a9\u06d5\u06cc\u0627\u0646\u060c \u0628\u06d5\u06b5\u0627\u06cc\u06d5\u06a9\u06cc \u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646\u060c \u0647\u06d5\u0645\u0648\u0648\u06cc \u0695\u06ce\u06a9\u06cc \u0632\u06d5\u0648\u06cc \u06a9\u0631\u062f\u0646.',
    '\u0644\u06d5 \u0626\u0627\u06a9\u0627\u0645\u06cc \u0626\u06d5\u0645 \u06a9\u0627\u0631\u06d5\u0634\u06cc \u062a\u0631\u0633\u06cc \u0646\u06cc\u06cc\u06d5.',
    '\u0633\u06c6\u0646\u062f \u0628\u06d5\u0648 \u0634\u06d5\u0648\u06d5 \u06a9\u0627\u062a\u06ce \u0647\u06d5\u0631 \u0634\u062a \u062f\u0627\u062f\u06d5\u067e\u06c6\u0634\u06ce\u060c',
    '\u0628\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u0634 \u06a9\u06d5\u0645 [\u0644\u06d5\u0633\u06d5\u0631 \u062f\u0646\u06ce] \u062e\u06c6 \u0695\u0627\u062f\u06d5\u0646\u06ce\u060c',
    '\u0628\u06d5\u0648\u06d5\u0634 \u06a9\u06d5\u0645 \u06a9\u06d5 \u0646\u06ce\u0631 \u0648 \u0645\u06ce\u0648\u06cc \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u060c',
    '\u062e\u06d5\u0628\u0627\u062a\u06ce \u0648\u06d5 \u062c\u06c6\u0631\u0627\u0648\u062c\u06c6\u0631\u06d5.',
    '\u06a9\u06ce \u0644\u06d5 \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u0628\u06d5\u062e\u0634\u06ce \u0648 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u062a\u0631\u0633\u0627\u060c',
    '\u0628\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u0627\u0634\u06cc\u0634 \u0628\u0695\u0648\u0627\u06cc \u0647\u06d5\u06cc\u06d5\u060c',
    '\u0628\u06d5\u0631\u06d5\u0648 \u062c\u06ce\u06af\u06d5\u06cc \u0633\u0627\u0646\u06d5\u0648\u06d5 \u0695\u06ce\u06cc \u0647\u0627\u0633\u0627\u0646 \u062f\u06d5\u06a9\u06d5\u06cc\u0646.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u0648\u06cc \u0695\u0698\u062f\u06cc \u06a9\u0631\u062f \u0648 \u0648\u062a\u06cc: \u06a9\u0627\u0631\u0645 \u0628\u06d5 \u06a9\u06d5\u0633 \u0646\u06cc\u06cc\u06d5\u060c',
    '\u067e\u0627\u062f\u0627\u0634\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u0627\u0634\u06cc\u0634\u06cc \u067e\u06ce \u062f\u0631\u06c6 \u0628\u0648\u0648\u060c',
    '\u0695\u06ce\u06af\u06d5 \u0647\u06d5\u0631\u06d5 \u062f\u0698\u0648\u0627\u0631\u06d5\u06a9\u06d5\u06cc \u062f\u06d5\u062e\u06d5\u06cc\u0646\u06d5 \u0628\u06d5\u0631.',
    '\u0626\u06d5\u06af\u06d5\u0631 \u0645\u0631\u062f\u06cc\u0634 \u0645\u0627\u06b5\u06d5\u06a9\u06d5\u06cc \u0633\u0648\u0648\u062f\u06cc \u067e\u06ce \u0646\u0627\u062f\u0627.',
    '\u0695\u06ce \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0626\u06ce\u0645\u06d5\u0633.',
    '\u062f\u0646\u06cc\u0627 \u0648 \u0633\u06d5\u06b5\u0627\u0634 \u0647\u06cc \u0626\u06ce\u0645\u06d5\u06cc\u06d5.',
    '\u0644\u06d5 \u0626\u0627\u06af\u0631\u06cc \u0628\u06b5\u06ce\u0633\u06d5\u062f\u0627\u0631 \u0626\u06ce\u0648\u06d5\u0645 \u062a\u0631\u0633\u0627\u0646\u062f.',
    '\u06a9\u06ce \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648\u0627\u0646 \u0628\u06ce \u0641\u06d5\u0695\u062a\u0631\u06d5\u060c \u062a\u06ce\u06cc \u062f\u06d5\u06a9\u06d5\u0648\u06ce.',
    '\u0626\u06d5\u0648\u06cc \u06a9\u06d5 \u0628\u0695\u0648\u0627\u06cc \u0646\u06d5\u06a9\u0631\u062f \u0648 \u0695\u0648\u0648\u06cc \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627.',
    '\u0647\u06d5\u0631\u06a9\u06ce\u0634 \u067e\u0627\u0631\u06ce\u0632\u06af\u0627\u0631\u062a\u0631\u06cc\u0646\u06d5\u060c \u0644\u06ce\u06cc \u062f\u0648\u0648\u0631 \u062f\u06d5\u0628\u06ce.',
    '\u0626\u06d5\u0648\u06cc \u0645\u0627\u06b5\u06cc \u062e\u06c6\u06cc \u062f\u06d5\u0628\u06d5\u062e\u0634\u06ce\u060c \u0647\u06d5\u062a\u0627 \u0648\u06d5\u06a9\u0648\u0648 \u067e\u0627\u06a9 \u0628\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0628\u06c6\u06cc\u06d5\u0634\u06cc \u0628\u06d5 \u06a9\u06d5\u0633 \u0646\u06d5\u0628\u06d5\u062e\u0634\u06cc \u06a9\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc \u0628\u062f\u0631\u06ce\u062a\u06d5\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u0648\u06cc\u0633\u062a\u0648\u0648\u06cc\u06d5 \u0695\u06d5\u0632\u0627\u0645\u06d5\u0646\u062f\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u06d5\u0631\u0632\u06cc \u0628\u06d5\u062f\u06d5\u0633 \u0628\u06ce\u0646\u06ce.',
    '\u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5\u0634 \u0695\u0627\u0632\u06cc \u062f\u06d5\u0628\u06ce.',
    '\u0633\u06c6\u0646\u062f\u06ce \u062f\u06d5\u062e\u06c6\u0645 \u0628\u06d5 \u0686\u06ce\u0634\u062a\u06d5\u0646\u06af\u0627\u0648\u060c',
    '\u0628\u06d5 \u062c\u06d5\u0646\u06af\u06d5\u06cc \u0647\u06d5\u062f\u0627 \u062f\u0627\u0646\u06cc \u0634\u06d5\u0648\u060c',
    '\u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0646\u06d5 \u0648\u0627\u0632\u06cc \u0644\u06d5 \u062a\u06c6 \u0647\u06ce\u0646\u0627\u0648\u06d5\u060c \u0646\u06d5 \u0644\u06ce\u062a \u0642\u06d5\u06b5\u0633\u06d5.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u062f\u0648\u0627\u0695\u06c6\u0698 \u0628\u06c6 \u062a\u06c6 \u0644\u06d5 \u062f\u0646\u06cc\u0627 \u0686\u0627\u062a\u0631\u06d5.',
    '\u0628\u06d5\u0645 \u0632\u0648\u0648\u0627\u0646\u06d5\u0634 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0686\u0627\u06a9\u06d5\u06cc \u0648\u06d5\u0647\u0627\u062a \u062f\u06d5\u06af\u06d5\u06b5 \u062f\u06d5\u06a9\u0627\u060c \u0695\u0627\u0632\u06cc \u062f\u06d5\u0628\u06cc.',
    '\u0686\u0645\u0627 \u0646\u06d5 \u0633\u06ce\u0648\u06cc\u06d5\u06a9 \u0628\u0648\u0648\u06cc\u060c \u0626\u06d5\u0648 \u067e\u06d5\u0646\u0627\u06cc \u067e\u06ce\u062f\u0627\u06cc\u061f',
    '\u062f\u06cc \u06af\u0648\u0645\u0695\u0627\u06cc\u0648 \u0695\u06ce\u06cc \u0646\u06cc\u0634\u0627\u0646 \u062f\u0627\u06cc\u061f',
    '\u062f\u06cc \u0647\u06d5\u0698\u0627\u0631\u06cc\u0648 \u0644\u06d5 \u0626\u0627\u062a\u0627\u062c\u06cc \u062f\u06d5\u0631\u06cc \u062e\u0633\u062a\u06cc\u061f',
    '\u0633\u0627 \u062a\u06c6\u0634 \u0647\u06d5\u062a\u06cc\u0648 \u0645\u06d5\u0695\u06d5\u0646\u062c\u06ce\u0646\u06d5\u061b',
    '\u062f\u06d5\u0631\u06c6\u0632\u06d5\u06a9\u06d5\u0631 \u0645\u06d5\u062a\u0627\u0631\u06ce\u0646\u06d5\u061b',
    '\u062a\u06c6 \u0647\u06d5\u0631 \u0628\u0627\u0633\u06cc \u0686\u0627\u06a9\u06d5\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06a9\u06d5.',
    '\u0626\u06d5\u062f\u06cc \u0626\u06ce\u0645\u06d5 \u062a\u06c6\u0645\u0627\u0646 \u0633\u06cc\u0646\u06d5 \u0641\u0695\u06d5\u0647\u0640 \u0646\u06d5\u06a9\u0631\u062f\u061f',
    '\u0642\u0648\u0631\u0633\u0627\u06cc\u06cc\u0634\u0645\u0627\u0646 \u0644\u06d5\u0633\u06d5\u0631 \u0634\u0627\u0646\u0627\u0646\u062a \u0644\u0627 \u0646\u06d5\u0628\u0631\u062f\u061f',
    '\u0626\u06d5\u0648\u06d5\u06cc \u067e\u0634\u062a\u06cc \u0686\u06d5\u0645\u0627\u0646\u062f\u0628\u0648\u0648\u06cc\u061f',
    '\u0646\u0627\u0648\u0628\u0627\u0646\u06af\u06cc\u0634\u062a\u0645\u0627\u0646 \u0628\u06d5\u0631\u0632\u06d5\u0648\u06d5 \u06a9\u0631\u062f.',
    '\u0644\u06d5\u067e\u0627\u0634 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5 \u0641\u06d5\u0631\u062d\u0627\u0646\u06cc\u0646\u061b',
    '\u0626\u0627\u0631\u06ce\u060c \u0644\u06d5 \u067e\u0627\u0634 \u062a\u06d5\u0646\u06af\u0627\u0646\u06d5 \u0641\u06d5\u0631\u062d\u0627\u0646\u06cc \u0647\u06d5\u06cc\u06d5.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0644\u06d5 \u06a9\u0627\u0631\u062a \u0642\u0648\u062a\u0627\u0631 \u0628\u0648\u0648\u06cc\u060c \u0695\u0627\u0633\u062a\u06d5\u0648\u06d5 \u0628\u06d5 [\u0628\u06c6 \u06a9\u0627\u0631\u06ce\u06a9 \u062a\u0631].',
    '\u0626\u06d5\u0648\u0633\u0627 \u0644\u06d5\u0628\u06d5\u0631 \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a \u0628\u0644\u0627\u06b5\u06ce\u0648\u06d5.',
    '\u0633\u06c6\u0646\u062f \u0628\u06d5 \u0647\u06d5\u0698\u06cc\u0631\u060c \u0628\u06d5 \u0632\u06d5\u06cc\u062a\u0648\u0648\u0646\u06cc\u0634\u060c',
    '\u0633\u06c6\u0646\u062f\u06cc\u0634 \u0628\u06d5 \u062a\u0648\u0648\u0631\u06cc \u0633\u06cc\u0646\u0627\u06cc\u06d5\u060c',
    '\u0628\u06d5\u0645 \u0628\u0627\u0698\u06ce\u0695\u06d5\u0634 \u06a9\u06d5 \u0645\u06d5\u062a\u0631\u0633\u06cc \u0648\u06d5\u0628\u06d5\u0631 \u0646\u0627\u06cc\u06d5\u060c',
    '\u0626\u06ce\u0645\u06d5 \u0645\u0631\u06c6\u0646 \u0644\u06d5 \u0695\u06ce\u06a9\u062a\u0631\u06cc\u0646 \u0634\u06ce\u0648\u06d5\u062f\u0627 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627\u0648\u06d5.',
    '\u067e\u0627\u0634\u0627\u0646 \u06af\u06ce\u0695\u0627\u0648\u0645\u0627\u0646\u06d5\u062a\u06d5\u0648\u06d5 \u0628\u06c6 \u067e\u0644\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u06d5\u0631\u06d5\u0698\u06ce\u0631\u061b',
    '\u0645\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u0627\u0648\u06d5\u0695\u062f\u0627\u0631 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0646\u060c \u06a9\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u062f\u0648\u0627\u06cc\u06cc \u0646\u0627\u06cc\u06d5.',
    '[\u0633\u0627 \u0626\u06d5\u06cc \u0645\u0631\u06c6!] \u0686\u06cc \u062a\u06c6\u06cc \u062f\u06d5\u0633\u062e\u06d5\u0631\u06c6 \u06a9\u0631\u062f\u0648\u0648\u06d5 \u06a9\u06d5 \u0698\u06cc\u0646\u06d5\u0648\u06d5\u062a \u067e\u06ce \u062f\u0631\u06c6\u06cc\u06d5\u061f',
    '\u0626\u0627\u062e\u06c6 \u062e\u0648\u062f\u0627 \u0644\u06d5 \u0647\u06d5\u0645\u0648\u0648 \u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc\u0627\u0646 \u0628\u0627\u0634\u062a\u0631 \u0628\u06d5 \u06a9\u0627\u0631\u0627\u0646 \u0695\u0627\u0646\u0627\u06af\u0627\u061f',
    '\u0648\u0627 \u0628\u06d5\u0646\u0627\u0648 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a! \u06a9\u06d5 \u0632\u06cc\u0646\u062f\u06d5\u0648\u06d5\u0631 \u06a9\u0627\u0631\u06cc \u0626\u06d5\u0648\u06d5.',
    '\u062c\u0633\u0646\u06cc \u0645\u0631\u06c6\u06cc \u0644\u06d5 \u062c\u06b5\u062a\u06d5\u06cc\u06ce \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u0627.',
    '\u0628\u062e\u06c6\u0646\u06d5 \u06a9\u06d5 \u062e\u0648\u062f\u0627\u0646\u06cc \u062a\u06c6 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633 \u062f\u06d5\u0647\u0646\u062f\u06d5\u062a\u0631\u06d5.',
    '\u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5\u06cc \u0628\u06d5 \u0647\u06c6\u06cc \u0628\u0646\u0648\u0648\u0633\u06cc \u0695\u0627\u0647\u06ce\u0646\u0627.',
    '\u0645\u0631\u06c6\u06cc \u0644\u06d5 \u0634\u062a\u06ce \u0695\u0627\u0647\u06ce\u0646\u0627 \u06a9\u06d5 \u0632\u0648\u0648\u062a\u0631 \u067e\u06ce\u06cc \u0646\u06d5\u0632\u0627\u0646\u06cc\u0628\u0648\u0648.',
    '\u0646\u06d5\u062e\u06ce\u0631\u060c \u062f\u06cc\u0627\u0631\u06d5 \u0645\u0631\u06c6 \u0633\u06d5\u0631\u0628\u0632\u06ce\u0648\u06cc \u062f\u06d5\u06a9\u0627\u061b',
    '\u06a9\u0627\u062a\u06ce \u062f\u06cc\u062a\u06cc \u0644\u06d5 \u062e\u06d5\u06b5\u06a9\u06cc \u062f\u06cc \u0628\u06ce \u0646\u06cc\u0627\u0632\u06d5.',
    '\u062f\u06cc\u0627\u0631\u06d5 \u0628\u06c6 \u0644\u0627\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u062a\u06d5 \u06af\u06d5\u0695\u0627\u0646\u06d5\u0648\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648 [\u0632\u06d5\u0644\u0627\u0645\u06d5\u062a] \u0628\u06cc\u0646\u06cc \u0628\u06d5\u0631\u06cc \u062f\u06d5\u06af\u0631\u062a\u060c',
    '\u0644\u06d5 \u0628\u06d5\u062f\u06d5\u06cc\u06ce \u06a9\u06d5 \u062e\u06d5\u0631\u06cc\u06a9 \u0628\u0648\u0648 \u0646\u06c6\u0698\u06cc \u062f\u06d5\u06a9\u0631\u062f\u061f',
    '\u06a9\u0648\u0648 \u0626\u06d5\u0628\u06cc\u0646\u06cc \u0626\u06d5\u06af\u06d5\u0631 \u0644\u06d5\u0633\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0695\u06ce \u0628\u0627\u060c',
    '\u06cc\u0627\u0646 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u0628\u06d5 \u0644\u06d5\u062e\u0648\u062f\u0627\u062a\u0631\u0633\u06cc \u062f\u0627\u0628\u0627\u061f',
    '\u0647\u06cc\u0686 \u0628\u06cc\u0631\u062a \u0644\u06ce \u06a9\u0631\u062f\u06c6\u062a\u06d5\u0648\u06d5\u060c \u0626\u06d5\u06af\u06d5\u0631 \u0628\u0627\u0648\u06d5\u0695\u06cc \u0646\u06d5\u0647\u06ce\u0646\u0627 \u0648 \u0695\u0648\u06cc \u0648\u06d5\u0631\u06af\u06ce\u0695\u0627\u060c [\u062e\u06c6\u06cc \u062a\u06cc\u0627 \u062f\u06d5\u0628\u0627]\u061f',
    '\u0645\u06d5\u06af\u06d5\u0631 \u0626\u06d5\u0648 \u0646\u06d5\u06cc\u062f\u06d5\u0632\u0627\u0646\u06cc \u06a9\u06d5 \u062e\u0648\u062f\u0627 \u062f\u06d5\u0628\u06cc\u0646\u06ce\u061f',
    '\u0646\u0627\u060c \u0626\u06d5\u0631 \u0647\u06d5\u0631 \u0648\u0627\u0632\u06cc \u0646\u06d5\u0647\u06ce\u0646\u0627\u060c \u0644\u06d5 \u067e\u06ce\u0634\u06d5\u0633\u06d5\u0631\u06cc \u062f\u06d5\u0646\u06d5\u0648\u06cc\u0646\u061b',
    '\u067e\u06ce\u0634\u06d5\u0633\u06d5\u0631\u06cc \u062f\u0631\u06c6\u06a9\u06d5\u0631\u06cc \u062e\u06d5\u062a\u0627\u06a9\u0627\u0631\u06cc.',
    '\u062f\u06d5\u0628\u0627 \u0626\u06d5\u0648\u0633\u0627 \u0647\u0627\u0648\u0627\u0631 \u0628\u06c6 \u06cc\u0627\u0646\u06d5\u06a9\u06d5\u06cc \u0628\u06d5\u0631\u06ce.',
    '\u0626\u06ce\u0645\u06d5\u0634 \u06af\u0632\u06cc\u0631\u06cc \u062f\u06c6\u0698\u06d5\u0647\u06cc \u0644\u06ce \u062f\u06d5\u0646\u06af \u062f\u06d5\u062f\u06d5\u06cc\u0646.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u067e\u06d5\u06cc\u0695\u06d5\u0648\u06cc \u0644\u06d5\u0648 \u0645\u06d5\u06a9\u06d5 \u0648 \u0633\u0648\u0698\u062f\u06d5 \u0628\u06d5\u0631\u06d5\u0648 \u0646\u0632\u06cc\u06a9 \u0628\u06d5\u0648\u06d5.',
    '\u0626\u06ce\u0645\u06d5\u06cc\u0646 \u06a9\u06d5 \u0626\u06d5\u0645 \u0642\u0648\u0695\u0639\u0627\u0646\u06d5\u0645\u0627\u0646 \u0644\u06d5 \u0634\u06d5\u0648\u06cc \u0647\u06d5\u0631\u06d5 \u0628\u06d5\u0695\u06ce\u0632\u062f\u0627 \u0646\u0627\u0631\u062f\u06c6\u062a\u06d5 \u062e\u0648\u0627\u0631.',
    '\u06a9\u0648\u0648 \u062f\u06d5\u0632\u0627\u0646\u06cc \u0626\u06d5\u0648 \u0634\u06d5\u0648\u06d5 \u0628\u06d5\u0695\u06ce\u0632\u06d5 \u06a9\u0627\u0645\u06d5\u061f',
    '\u0634\u06d5\u0648\u06ce\u06a9\u06d5 \u0647\u06ce\u0646\u062f\u06d5 \u0628\u06d5\u0642\u06d5\u062f\u0631\u06d5\u060c \u0647\u06d5\u0632\u0627\u0631 \u0645\u0627\u0646\u06af \u0628\u0627\u0631\u062a\u06d5\u0642\u0627\u06cc \u0646\u0627\u06a9\u0627.',
    '\u0644\u06d5\u0648 \u0634\u06d5\u0648\u06d5\u062f\u0627 \u0641\u0631\u0634\u062a\u06d5\u06a9\u0627\u0646 \u0648 \u062c\u0648\u0628\u0695\u0627\u0626\u06cc\u0644 \u2013 \u0628\u06d5 \u0626\u06cc\u0632\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646 \u2013 \u062f\u0627\u062f\u06d5\u0648\u06d5\u0632\u0646 \u0648 \u0647\u06d5\u0645\u0648\u0648 \u06a9\u0627\u0631 \u062c\u06ce\u0628\u06d5\u062c\u06ce \u062f\u06d5\u06a9\u06d5\u0646.',
    '\u0647\u06d5\u0631 \u062e\u06c6\u0634\u06cc \u0648 \u0647\u06ce\u0645\u0646\u0627\u06cc\u06d5\u062a\u06cc\u06d5 \u0647\u06d5\u062a\u0627 \u0628\u0648\u0648\u0644\u06ce\u06b5\u06d5\u06cc \u0628\u06d5\u06cc\u0627\u0646\u06cc.',
    '\u0626\u06d5\u0648\u0627\u0646 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0627\u0646\u06d5\u06cc \u0644\u06d5 \u062f\u06cc\u0646 \u062f\u06d5\u0631\u0686\u0648\u0648\u0646\u060c \u0648\u06d5\u0644 \u0626\u06d5\u0648\u0627\u0646\u06d5\u06cc \u0628\u062a \u067e\u06d5\u0631\u0633\u062a \u0628\u0648\u0648\u0646\u060c \u0646\u06d5\u062f\u06d5\u062a\u0631\u0627\u0632\u0646 \u062a\u0627 \u0628\u06d5\u06b5\u06af\u06d5\u06cc\u06ce \u0695\u0648\u0648\u0646\u06cc\u0627\u0646 \u0628\u06c6 \u062f\u06ce.',
    '\u067e\u06ce\u063a\u06d5\u0645\u0628\u06d5\u0631\u06ce\u06a9 \u0644\u06d5 \u062e\u0648\u062f\u0627\u0648\u06d5 \u0686\u06d5\u0646\u062f \u0695\u0648\u0648\u067e\u06d5\u0695\u06ce\u06a9\u06cc \u067e\u0627\u06a9\u0698\u06cc\u0627\u0646 \u0628\u06c6 \u0648\u06d5\u062e\u0648\u06ce\u0646\u06ce.',
    '\u06af\u06d5\u0644\u06ce\u06a9 \u0646\u0648\u0648\u0633\u0631\u0627\u0648\u06cc \u0628\u06d5\u0646\u0631\u062e\u06cc\u0627\u0646 \u062a\u06cc\u0627 \u0647\u06d5\u06cc\u06d5.',
    '\u0626\u06d5\u0648\u06d5\u0646\u06d5\u06cc \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628\u0646\u060c \u0644\u06d5\u0628\u06d5\u0631 \u06cc\u06d5\u06a9 \u0647\u06d5\u06b5\u0646\u06d5\u0648\u06d5\u0634\u0627\u0628\u0648\u0648\u0646\u061b \u0645\u06d5\u06af\u06cc\u0646 \u0648\u06d5\u062e\u062a\u06ce \u0626\u06d5\u0645 \u0628\u06d5\u06b5\u06af\u06d5 \u0695\u0648\u0648\u0646\u06d5\u06cc\u0627\u0646 \u0628\u06c6 \u0647\u0627\u062a.',
    '\u0641\u06d5\u0631\u0645\u0627\u0646\u06cc\u0634\u06cc\u0627\u0646 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u067e\u06ce \u062f\u0631\u0627\u0628\u0648\u0648 \u06a9\u06d5 \u0626\u06d5\u0628\u06ce \u062e\u0648\u0627\u067e\u06d5\u0631\u0633\u062a \u0628\u0646 \u0648 \u0644\u06d5 \u062f\u06cc\u0646 \u0626\u06d5\u0648\u062f\u0627 \u062f\u06b5\u067e\u0627\u06a9 \u0628\u0646 \u0648 \u0628\u06d5\u0631\u06d5\u0648 \u0647\u06d5\u0642\u0627\u0646\u06d5\u062a \u0628\u0695\u06c6\u0646 \u0648 \u0646\u0648\u06ce\u0698 \u0628\u06a9\u06d5\u0646 \u0648 \u0632\u06d5\u06a9\u0627\u062a \u0628\u062f\u06d5\u0646\u061b \u06a9\u06d5 \u0626\u06d5\u0645\u06d5 \u062f\u06cc\u0646\u06cc \u062f\u0631\u0648\u0633\u062a\u06d5.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u06cc \u06a9\u06d5 \u0648\u0627 \u0626\u06d5\u0647\u0644\u06cc \u06a9\u062a\u06ce\u0628 \u0628\u0648\u0648\u0646 \u0648 \u0644\u06d5 \u062f\u06cc\u0646 \u062f\u06d5\u0631\u0686\u0648\u0648\u0646\u060c \u062f\u06d5\u06af\u06d5\u06b5 \u0628\u062a \u067e\u06d5\u0631\u0633\u062a\u06d5\u06a9\u0627\u0646\u062f\u0627 \u062a\u0627\u0647\u06d5\u062a\u0627\u06cc\u06d5\u060c \u0644\u06d5 \u0626\u0627\u06af\u0631\u06cc \u062c\u06d5\u0647\u06d5\u0646\u062f\u06d5\u0645 \u062f\u0627\u0646. \u0626\u06d5\u0645\u0627\u0646 \u062e\u0631\u0627\u067e\u062a\u0631\u06cc\u0646 \u06a9\u06d5\u0633\u0646.',
    '\u0626\u06d5\u0648 \u06a9\u06d5\u0633\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0628\u0648\u0648\u0646\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u0626\u0627\u06a9\u0627\u0631\u0686\u0627\u06a9\u0646\u060c \u0628\u0627\u0634\u062a\u0631\u06cc\u0646 \u06a9\u06d5\u0633 \u0647\u06d5\u0631 \u0626\u06d5\u0648\u0627\u0646\u0646.',
    '\u0644\u06d5 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc\u0627\u0646\u06d5\u0648\u06d5 \u067e\u0627\u062f\u0627\u0634\u06cc\u0627\u0646 \u0628\u06d5\u0647\u06d5\u0634\u062a\u06cc \u0639\u06d5\u062f\u0646\u06d5\u061b \u062c\u06c6\u0628\u0627\u0631\u06cc \u0628\u06d5\u0628\u06d5\u0631\u062f\u0627 \u062f\u06d5\u0695\u0648\u0627 \u0648 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u0647\u06d5\u0631 \u0644\u06d5\u0648\u06ce \u062f\u06d5\u0628\u0646. \u062e\u0648\u062f\u0627 \u0644\u06d5\u0648\u0627\u0646 \u0695\u0627\u0632\u06cc \u0628\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u0627\u0646\u06cc\u0634 \u0644\u06d5 \u062e\u0648\u062f\u0627 \u0695\u0627\u0632\u06cc\u0646. \u06a9\u06ce \u0644\u06d5 \u062e\u0648\u062f\u0627\u06cc \u062e\u06c6\u06cc \u062a\u0631\u0633\u0627\u0648\u06d5\u060c \u0626\u06d5\u0645\u06d5\u06cc \u062f\u06d5\u0628\u06ce.',
    '\u0647\u06d5\u0631\u06af\u0627 \u0632\u06d5\u0645\u06cc\u0646 \u0628\u06d5 \u0628\u0648\u0648\u0645\u06d5\u0644\u06d5\u0631\u0632\u06d5 \u062a\u0648\u0646\u062f\u06d5\u06a9\u06d5\u06cc \u0647\u0627\u062a\u06d5 \u0644\u06d5\u0631\u0632\u06cc\u0646\u060c',
    '\u0626\u06d5\u0645 \u0632\u06d5\u0645\u06cc\u0646\u06d5\u0634 \u06af\u0631\u0627\u0646\u0628\u0627\u0631\u06cc \u062e\u06c6\u06cc \u0648\u06d5\u062f\u06d5\u0631\u062e\u0633\u062a\u060c',
    '\u0645\u0631\u06c6\u0634 \u067e\u0631\u0633\u06cc: \u0686\u06cc \u0642\u06d5\u0648\u0645\u0627\u0648\u06d5\u060c \u0628\u06c6 \u0648\u0627 \u062f\u06d5\u06a9\u0627\u061f',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u0628\u06d5\u0633\u06d5\u0631\u0647\u0627\u062a\u06d5\u06a9\u06d5\u06cc \u0628\u0627\u0633 \u062f\u06d5\u06a9\u0627.',
    '\u06a9\u06d5 \u0641\u06d5\u0631\u0645\u0627\u0646\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u06ce\u0646\u06cc \u0628\u06d5\u062c\u06ce \u062f\u06ce\u0646\u06ce.',
    '\u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u0645\u06d5\u0631\u062f\u0645 \u0628\u06d5 \u067e\u06d5\u0695\u0698 \u0648 \u0628\u06b5\u0627\u0648\u06cc \u062f\u06ce\u0646\u06d5 \u062f\u06d5\u0631\u06ce \u062a\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06d5 \u06a9\u0627\u0631\u06cc \u062e\u06c6\u06cc\u0627\u0646 \u0646\u06cc\u0634\u0627\u0646 \u0628\u062f\u06d5\u0646.',
    '\u0633\u0627 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce \u0628\u06d5 \u0633\u06d5\u0646\u06af\u0627\u06cc\u06cc \u0645\u06ce\u0631\u0648\u0648\u0644\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0648\u0631\u062f\u06cc\u0644\u06d5\u060c \u0686\u0627\u06a9\u06d5\u06cc \u0647\u06d5\u0628\u0648\u0648\u0628\u06ce\u060c \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0628\u06d5 \u0642\u0648\u0631\u0633\u0627\u06cc\u06cc \u0645\u06ce\u0631\u0648\u0648\u0644\u06d5\u06cc \u0647\u06d5\u0631\u06d5 \u0648\u0631\u062f\u06cc\u0644\u06d5\u060c \u062e\u0631\u0627\u067e\u06d5\u06cc \u0628\u0648\u0648\u0628\u06ce\u060c \u062f\u06d5\u06cc\u0628\u06cc\u0646\u06ce.',
    '\u0628\u06d5\u0648 \u0686\u0627\u0631\u06d5\u0648\u0627\u06cc \u0628\u06d5\u0644\u06cc\u0646\u06af\u062f\u0627\u0646\u06ce \u062f\u06d5\u0646\u0631\u06a9\u06ce\u0646\u0646\u060c',
    '\u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u06a9\u06d5 \u0698\u06ce\u0631\u06cc \u067e\u0627\u06cc\u0627\u0646 \u0626\u0627\u0648\u0631\u06cc\u0646\u06af \u062f\u06ce\u0646\u0646\u060c',
    '\u0633\u06c6\u0646\u062f \u0628\u06d5\u0648\u0627\u0646\u06d5\u0634 \u0628\u06d5\u0631\u06cc \u0628\u06d5\u06cc\u0627\u0646 \u0647\u06ce\u0631\u0634 \u062f\u06d5\u0628\u06d5\u0646\u060c',
    '\u062c\u0627 \u0644\u06d5\u0648\u06ce \u062a\u06c6\u0632 \u0647\u06d5\u06b5\u062f\u06d5\u0633\u062a\u06ce\u0646\u0646\u060c',
    '\u062f\u06d5\u0634\u06af\u06d5\u0646\u06d5 \u0646\u0627\u0648 \u0626\u0627\u067e\u06c6\u0631\u06d5\u0648\u06d5\u060c',
    '\u0695\u0627\u0633\u062a\u06cc \u0645\u0631\u06c6\u060c \u0644\u06d5 \u0695\u0627\u0633\u062a\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u06cc \u067e\u06ce \u0646\u06d5\u0632\u0627\u0646\u06d5\u061b',
    '\u062e\u06c6\u0634\u06cc \u0628\u0627\u0634 \u0626\u06d5\u0645\u06d5 \u062f\u06d5\u0632\u0627\u0646\u06ce.',
    '\u0644\u06d5 \u0626\u06d5\u0648\u06cc\u0646\u06cc \u0645\u0627\u06b5\u06cc \u062f\u0646\u06cc\u0627 \u06af\u06d5\u0644\u06ce \u0695\u0698\u062f\u06d5.',
    '\u0626\u0627\u062e\u06c6 \u0626\u06d5\u0648\u06d5\u0646\u062f\u06d5 \u0646\u0627\u0632\u0627\u0646\u06ce\u060c \u06a9\u06d5\u06cc \u0647\u06d5\u0631\u06a9\u06ce \u0644\u06d5 \u06af\u06c6\u0695\u0633\u062a\u0627\u0646 \u062f\u06d5\u0631\u062e\u0631\u0627\u0646 \u0648 \u0628\u06b5\u0627\u0648 \u06a9\u0631\u0627\u0646\u060c',
    '\u0647\u06d5\u0631\u0686\u06cc \u0644\u06d5 \u0646\u0627\u0648 \u062f\u06b5\u0627\u0646\u06cc\u0634\u062f\u0627\u06cc\u06d5 \u0632\u0627\u0646\u062f\u0631\u0627\u0646\u060c',
    '\u0626\u06d5\u0648\u06ce \u0695\u06c6\u0698\u06ce \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u0644\u06d5 \u0646\u0648\u0648\u06a9 \u0648 \u0628\u06d5\u062f \u0626\u0627\u06af\u0627\u062f\u0627\u0631\u06d5\u061f',
    '\u0626\u06d5\u0648 \u062f\u06b5\u06a9\u0648\u062a\u06d5\u060c',
    '\u062f\u06b5\u06a9\u0648\u062a \u0686\u06cc\u06cc\u06d5\u061f',
    '\u06a9\u0648\u0648\u062a \u0632\u0627\u0646\u06cc\u0648\u06d5 \u062f\u06b5\u06a9\u0648\u062a \u0686\u06cc\u06cc\u06d5\u061f',
    '\u0626\u06d5\u0648 \u0695\u06c6\u0698\u06d5 \u06af\u0634 \u0626\u06d5\u0648 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u0648\u06d5\u06a9 \u067e\u06d5\u067e\u0648\u0648\u0644\u06d5\u06cc \u067e\u0695\u0698\u0627\u0648 \u062f\u06d5\u0628\u0646.',
    '\u06a9\u06ce\u0648\u06d5\u06a9\u0627\u0646\u06cc\u0634 \u0648\u06d5\u06a9 \u062e\u0648\u0631\u06cc \u0634\u06cc \u06a9\u0631\u0627\u0648 \u062f\u06d5\u0628\u0646.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u062a\u0627\u06cc \u062a\u06d5\u0631\u0627\u0632\u0648\u0648\u06cc \u062f\u0627\u0647\u0627\u062a\u0648\u0648\u06d5\u060c',
    '\u062a\u0648\u0648\u0634\u06cc \u0698\u06cc\u0627\u0646\u06cc \u062e\u06c6\u0634 \u0628\u0648\u0648\u06d5.',
    '\u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc\u0634 \u0644\u06d5 \u06a9\u06ce\u0634\u0627\u0646\u06d5 \u0633\u0648\u0648\u06a9\u06cc \u0647\u06ce\u0646\u0627\u060c',
    '\u0626\u06d5\u0648\u06d5 \u0626\u06d5\u0646\u0648\u0627\u06cc \u00ab\u0647\u0627\u0648\u06cc\u06d5\u00bb\u06cc\u06d5.',
    '\u06a9\u0648\u0648 \u0632\u0627\u0646\u06cc\u0648\u062a\u06d5 \u0644\u06d5 \u00ab\u0647\u0627\u0648\u06cc\u06d5\u00bb \u0645\u06d5\u0628\u06d5\u0633 \u0686\u06cc\u06cc\u06d5\u061f',
    '\u0626\u0627\u06af\u0631\u06ce\u06a9\u06cc \u0628\u06d5\u0646\u06ce\u06b5\u06d5\u06cc\u06d5.',
    '\u0634\u0627\u0646\u0627\u0632\u06cc\u062a\u0627\u0646 \u0628\u06d5 \u0641\u0631\u06d5\u06cc\u06cc [\u0645\u0627\u06b5 \u0648 \u0645\u0646\u062f\u0627\u06b5] \u0626\u06ce\u0648\u06d5\u06cc \u0628\u06d5 \u062e\u06c6\u06cc\u06d5\u0648\u06d5 \u062e\u0627\u0641\u06b5\u0627\u0646\u062f.',
    '\u062a\u0627 \u0647\u06d5\u0645\u0648\u0648\u062a\u0627\u0646 \u0628\u0648\u0648\u0646\u06d5 \u0645\u06cc\u0648\u0627\u0646\u06cc \u06af\u06c6\u0695\u0633\u062a\u0627\u0646.',
    '\u0632\u06c6\u0631 \u0646\u0627\u062e\u0627\u06cc\u06d5\u0646\u06ce \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u062c\u0627\u0631\u06ce \u062a\u0631\u06cc\u0634\u060c \u0632\u06c6\u0631 \u0646\u0627\u062e\u0627\u06cc\u06d5\u0646\u06ce \u062f\u06d5\u0632\u0627\u0646\u0646.',
    '\u0628\u06d5\u06b5\u0627\u0645 \u0626\u06d5\u06af\u06d5\u0631 \u0695\u0627\u0633\u062a\u06d5\u0642\u06cc\u0646\u06d5\u0648 \u0628\u0632\u0627\u0646\u06cc\u0628\u0627\u060c',
    '\u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u06c6\u0698\u06d5\u0647\u062a\u0627\u0646 \u062f\u06d5\u062f\u06cc.',
    '\u062f\u06cc\u0633\u0627\u0646\u06d5\u06a9\u06d5\u0634 \u0647\u06d5\u0631 \u0628\u06d5 \u0626\u0627\u0634\u06a9\u0631\u0627 \u062f\u06d5\u062a\u0627\u0646 \u062f\u06cc.',
    '\u0644\u06d5\u067e\u0627\u0634\u0627\u0646 \u0647\u06d5\u0631 \u0644\u06d5\u0648 \u0695\u06c6\u0698\u06d5\u062f\u0627 \u062f\u06d5\u0631\u0628\u0627\u0631\u06d5\u06cc \u062e\u06c6\u0634\u06cc \u062f\u0646\u06cc\u0627\u0648\u06d5 \u0644\u06ce\u0648 \u062f\u06d5\u067e\u0631\u0633\u0646.',
    '\u0633\u06c6\u0646\u062f \u0628\u06d5 \u0647\u06d5\u0695\u06d5\u062a\u06cc \u0626\u06ce\u0648\u0627\u0631\u06d5\u060c',
    '\u0645\u0631\u06c6 \u06af\u0634\u062a\u06cc \u0632\u06cc\u0627\u0646\u0628\u0627\u0631\u06d5\u061b',
    '\u0645\u06d5\u06af\u06cc\u0646 \u0626\u06d5\u0648\u06cc \u0628\u0648\u0648\u0646\u06d5\u062a\u06d5 \u062e\u0627\u0648\u06d5\u0646 \u0628\u0627\u0648\u06d5\u0695 \u0648 \u06a9\u0627\u0631\u0686\u0627\u06a9 \u0628\u0648\u0648\u0646 \u0648 \u06cc\u06d5\u06a9\u062a\u0631\u06cc\u0634\u06cc\u0627\u0646 \u0695\u0627\u0633\u062a \u062f\u0646\u06d5\u062f\u0627\u0648\u06d5 \u0648 \u0628\u06c6 \u062e\u06c6\u06af\u0631\u06cc \u0695\u0627\u0633\u067e\u0627\u0631\u062f\u0648\u0648\u06d5.',
    '\u0647\u0627\u0648\u0627\u0631\u06d5 \u0644\u06d5 \u0647\u06d5\u0631 \u06a9\u06d5\u0633\u06ce\u06a9\u06cc \u0628\u06d5 \u062a\u06d5\u0634\u06d5\u0631\u06cc \u062e\u0648\u0633\u067e\u06a9\u06d5\u0631\u06d5\u061b',
    '\u0626\u06d5\u0648\u06cc \u0645\u0627\u06b5\u06cc \u06a9\u06c6 \u06a9\u0631\u062f\u06d5\u0648\u06d5 \u0648 \u062f\u06d5\u06cc\u0628\u0698\u06ce\u0631\u06ce.',
    '\u062e\u06d5\u06cc\u0627\u06b5 \u062f\u06d5\u06a9\u0627 \u062f\u0631\u0627\u0648\u06d5\u06a9\u06d5\u06cc \u0644\u06d5 \u0645\u0631\u062f\u0646\u06cc \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u06ce.',
    '\u0646\u0627 \u0646\u0627\u060c \u0628\u0627 \u0648\u0627 \u062e\u06d5\u06cc\u0627\u06b5 \u0646\u06d5\u06a9\u0627\u061b \u0628\u06ce \u0634\u06a9 \u0641\u0695\u06ce \u062f\u06d5\u062f\u0631\u06ce\u062a\u06d5 \u0646\u0627\u0648 \u00ab\u062d\u0648\u062a\u06d5\u0645\u06d5\u00bb\u0648\u06d5.',
    '\u06a9\u0627 \u062f\u06d5\u0632\u0627\u0646\u06cc \u0646\u06cc\u0627\u0632 \u0644\u06d5\u0648 \u00ab\u062d\u0648\u062a\u06d5\u0645\u06d5\u00bb \u0686\u06cc\u06cc\u06d5\u061f',
    '\u0626\u0627\u06af\u0631\u06ce\u06a9\u06d5 \u062e\u0648\u062f\u0627 \u0647\u06d5\u06b5\u06cc \u06af\u06cc\u0631\u0633\u0627\u0646\u062f\u06cc\u06d5.',
    '\u0628\u06d5 \u0633\u06d5\u0631 \u067e\u06d5\u0695\u06d5\u06cc \u062f\u06b5\u0627\u0646\u062f\u0627 \u062f\u06ce.',
    '\u06a9\u06d5 \u0626\u06d5\u0645\u06d5\u06cc\u0627\u0646 \u0628\u06d5\u0633\u06d5\u0631\u062f\u0627 \u06af\u0627\u06b5\u06d5\u062f\u0631\u0627\u0648\u06d5\u061b',
    '\u0644\u06d5 \u0626\u06d5\u0633\u062a\u0648\u0648\u0646\u06d5\u06af\u06d5\u0644\u06ce\u06a9\u06cc \u0628\u06d5\u0631\u0632 \u0648 \u0628\u06b5\u0646\u062f\u0627.',
    '\u0626\u06d5\u062f\u06cc \u0646\u06d5\u062a\u062f\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0686 \u067e\u06d5\u0646\u062f\u06ce\u06a9\u06cc \u0628\u06d5\u0633\u06d5\u0631 \u0641\u06cc\u0644 \u0633\u0648\u0627\u0631\u06d5\u06a9\u0627\u0646 \u0647\u06ce\u0646\u0627\u061f',
    '\u0626\u06d5\u062f\u06cc \u0641\u06ce\u06b5 \u0648 \u06af\u0632\u06cc\u06d5\u06a9\u0627\u0646\u06cc \u0647\u06d5\u0645\u0648\u0648 \u0644\u06ce \u067e\u0648\u0648\u0686\u06d5\u06b5 \u0646\u06d5\u06a9\u0631\u062f\u0646\u061f',
    '\u062f\u06d5\u0633\u062a\u06d5 \u062f\u06d5\u0633\u062a\u06d5 \u0628\u0627\u06b5\u062f\u0627\u0631\u06cc \u0647\u06d5\u0646\u0627\u0631\u062f\u06d5 \u0633\u06d5\u0631\u06cc\u0627\u0646.',
    '\u06af\u0632\u0645\u0648\u0648\u0644\u06d5 \u0642\u0648\u0695\u06cc \u0648\u06d5\u06a9 \u0628\u06d5\u0631\u062f\u06cc\u0627\u0646 \u062a\u06ce\u062f\u06d5\u06af\u0631\u062a\u0646\u061b',
    '\u062a\u0627 \u0628\u06d5 \u062f\u06d5\u0631\u062f\u06cc \u06a9\u0627\u06cc\u06d5\u06a9\u06cc \u062e\u0648\u0631\u0627\u0648\u06cc \u0628\u0631\u062f\u0646.',
    '\u0628\u06c6 \u067e\u06ce\u06a9\u06d5\u0648\u06d5 \u0645\u0627\u0646\u06cc \u0642\u0648\u0631\u06d5\u06cc\u0634\u060c',
    '\u06a9\u06d5 \u0633\u0627\u06b5\u0627\u0646\u06d5 \u062f\u0648\u0648 \u062c\u0627\u0631 \u062f\u06d5\u0686\u0646 \u0628\u06d5\u0631\u06d5\u0648 \u0632\u06c6\u0632\u0627\u0646 \u0648 \u06af\u06d5\u0631\u0645\u06cc\u0627\u0646\u060c',
    '\u0628\u0627 \u062e\u0648\u062f\u0627\u0646\u06cc \u0626\u06d5\u0645 \u062e\u0627\u0646\u0648\u0648\u06d5 \u0628\u067e\u06d5\u0631\u0633\u062a\u0646\u061b',
    '\u06a9\u06d5 \u0626\u06d5\u0648\u0627\u0646\u06cc \u0644\u06d5 \u0646\u06cc\u0632\u06cc \u0628\u06ce \u0646\u06cc\u0627\u0632 \u06a9\u0631\u062f \u0648 \u062f\u06b5\u0646\u06cc\u0627\u06cc \u06a9\u0631\u062f\u0646 \u0646\u06d5\u062a\u0631\u0633\u0646.',
    '\u0626\u0627\u062e\u06c6 \u062a\u06c6 \u0626\u06d5\u0648 [\u0632\u06d5\u0644\u0627\u0645]\u06d5\u062a \u062f\u06cc \u0695\u06c6\u0698\u06cc \u062c\u06d5\u0632\u0627\u06cc \u0644\u0627 \u062f\u0631\u06c6 \u0628\u0648\u0648\u061f',
    '\u0647\u06d5\u0631 \u0626\u06d5\u0648\u06cc\u0634 \u0628\u0648\u0648 \u06a9\u06d5 \u0647\u06d5\u062a\u06cc\u0648\u06cc [\u0628\u06ce \u062f\u06d5\u0631\u06d5\u062a\u0627\u0646\u06cc] \u0626\u06d5\u0695\u0627\u0646\u06cc.',
    '\u0628\u06c6 \u0646\u0627\u0646 \u062f\u0627\u0646 \u0628\u06d5 \u0647\u06d5\u0698\u0627\u0631\u0627\u0646\u06cc\u0634 \u0647\u0627\u0646\u06d5 \u0646\u0627\u062f\u0627.',
    '\u0647\u0627\u0648\u0627\u0631 \u0628\u06c6 \u0626\u06d5\u0648 \u0646\u06c6\u0698\u06a9\u06d5\u0631\u0627\u0646\u06d5\u06cc\u060c',
    '\u0647\u06c6\u0634 \u0628\u06d5 \u0646\u06c6\u0698\u06d5\u06a9\u06d5\u06cc\u0627\u0646 \u0646\u0627\u062f\u06d5\u0646.',
    '\u0626\u06d5\u0648\u06cc \u062f\u06d5\u0633\u062a\u06d5\u06cc \u0695\u0648\u0648\u0628\u06cc\u0646\u0627\u0646\u0646\u060c',
    '\u062f\u06d5\u0628\u0646\u06d5 \u0628\u06d5\u0631\u06af\u0631 \u0644\u06d5 \u0628\u0627\u0631\u0628\u0648\u0648 \u062f\u0627\u0646 \u0648 \u0632\u0628\u0627\u0631\u06d5\u0634.',
    '\u0626\u06ce\u0645\u06d5 \u0632\u06c6\u0631 \u0634\u062a\u0645\u0627\u0646 \u062f\u0627 \u0628\u06d5 \u062a\u06c6.',
    '\u0633\u0627 \u062a\u06c6 \u0628\u06c6 \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u06cc \u062e\u06c6\u062a \u0646\u0645\u06ce\u0698 \u0648 \u0642\u0648\u0631\u0628\u0627\u0646\u06cc \u0628\u06a9\u06d5.',
    '\u0626\u06d5\u0648\u06cc \u0644\u06ce\u062a \u0628\u06d5\u0646\u0627\u062a\u06c6\u0631\u06d5\u06cc\u06d5\u060c \u0628\u06c6\u062e\u06c6\u06cc \u0648\u06d5\u062c\u0627\u062e\u06cc \u06a9\u06c6\u0631\u06d5\u06cc\u06d5.',
    '\u0628\u06ce\u0698\u06d5: \u0626\u06d5\u06cc \u062e\u0648\u0627\u0646\u06d5\u0646\u0627\u0633\u06d5\u06a9\u0627\u0646!',
    '\u0626\u06d5\u0648\u06cc \u0626\u06ce\u0648\u06d5 \u062f\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646\u060c \u0646\u0627\u06cc\u067e\u06d5\u0631\u0633\u062a\u0645.',
    '\u0626\u06ce\u0648\u06d5\u0634 \u0626\u06d5\u0648\u06cc \u062f\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u0645\u060c \u0646\u0627\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646.',
    '\u0626\u06d5\u0648\u06d5\u0634 \u067e\u06d5\u0631\u0633\u062a\u0648\u062a\u0627\u0646\u06d5 \u0646\u0627\u06cc\u067e\u06d5\u0631\u0633\u062a\u0645.',
    '\u0626\u06ce\u0648\u06d5\u0634 \u0626\u06d5\u0648\u06cc \u062f\u06d5\u06cc\u067e\u06d5\u0631\u0633\u062a\u0645\u060c \u0646\u0627\u06cc\u067e\u06d5\u0631\u0633\u062a\u0646.',
    '\u062f\u06cc\u0646\u06cc \u062e\u06c6\u062a\u0627\u0646 \u0628\u06c6 \u062e\u06c6\u062a\u0627\u0646 \u0648 \u0645\u0646\u06cc\u0634 \u062f\u06cc\u0646\u06cc \u062e\u06c6\u0645 \u0628\u06c6 \u062e\u06c6\u0645\u06d5.',
    '\u0647\u06d5\u0631\u06a9\u0627\u062a\u06ce \u06cc\u0627\u0631\u0645\u06d5\u062a\u06cc \u062e\u0648\u062f\u0627 \u06af\u06d5\u06cc\u06cc\u0634\u062a\u06d5 \u0626\u06ce\u0648\u06d5 \u0648 \u0633\u06d5\u0631\u06a9\u06d5\u0648\u062a\u0646\u060c',
    '\u062e\u06d5\u06b5\u06a9\u06cc\u0634\u062a \u062f\u06cc \u2013 \u06af\u0631\u06c6 \u06af\u0631\u06c6 \u2013 \u062f\u06ce\u0646\u06d5 \u0646\u0627\u0648 \u062f\u06cc\u0646\u06cc \u062e\u0648\u062f\u0627\u0648\u06d5\u060c',
    '\u0647\u06d5\u0631 \u062a\u0627\u0631\u06cc\u0641 \u0648 \u0634\u0648\u06a9\u0631\u06cc \u067e\u06d5\u0631\u0648\u06d5\u0631\u0646\u062f\u06d5\u062a \u0628\u06a9\u06d5 \u0648 \u062f\u0627\u0648\u0627\u06cc \u0628\u0648\u0631\u062f\u0646\u06cc \u0644\u06ce \u0628\u06a9\u06d5. \u0626\u06d5\u0648 \u0647\u06d5\u0645\u06cc\u0634\u06d5 \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u06cc \u067e\u06d5\u0698\u06cc\u0648\u0627\u0646\u0627\u0646 \u0642\u0628\u0648\u0648\u06b5 \u062f\u06d5\u06a9\u0627.',
    '\u0626\u06d5\u0628\u0648\u0648\u0644\u06d5\u0647\u06d5\u0628 \u0647\u06d5\u0631 \u062f\u0648\u0648 \u062f\u06d5\u0633\u062a\u06cc \u0634\u06d5\u0644\u06d5 \u0628\u06ce \u0648 \u0639\u06d5\u0645\u0631\u06cc \u0646\u06d5\u0645\u06ce\u0646\u06ce.',
    '\u0646\u06d5 \u0633\u0627\u0645\u0627\u0646\u06cc \u0648 \u0646\u06d5 \u0626\u06d5\u0648\u06cc \u067e\u06d5\u06cc\u062f\u0627\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5 \u0628\u06d5\u0647\u0631\u06d5\u06cc \u0628\u06c6\u062f\u0627.',
    '\u0647\u06d5\u0631\u0628\u06cc\u0646\u0627 \u0626\u0627\u06af\u0631\u06cc \u0628\u06d5\u06af\u0695 \u0626\u06d5\u06cc\u0633\u0648\u0648\u0632\u06ce\u0646\u06ce.',
    '\u0698\u0646\u06d5\u06a9\u06d5\u0634\u06cc \u062f\u06d5\u0633\u062a\u06d5\u0686\u06cc\u0644\u06d5 \u0628\u06d5 \u06a9\u06c6\u06b5 \u062f\u06ce\u0646\u06ce.',
    '\u06af\u0648\u0632\u06d5\u0631\u0647\u06c6\u0646\u06cc \u0633\u06cc\u0631\u0645\u06d5\u06cc \u062f\u0648\u0648\u0628\u0627\u062f\u06cc \u062f\u06d5\u0645\u0644\u062f\u0627.',
    '\u0628\u06ce\u0698\u06d5 \u0626\u06d5\u0648 \u062e\u0648\u062f\u0627\u06cc\u06d5 \u062a\u0627\u06a9\u06d5.',
    '\u062e\u0648\u062f\u0627\u06cc\u06d5\u06a9\u06cc \u0628\u06ce \u0646\u06cc\u0627\u0632 \u0648 \u062c\u06ce \u0646\u06cc\u0627\u0632\u06d5.',
    '\u06a9\u06d5\u0633 \u0644\u06d5\u0648 \u0646\u06d5\u0628\u0648\u0648\u06d5 \u0648 \u0626\u06d5\u0648\u06cc\u0634 \u0644\u06d5 \u0647\u06cc\u0686 \u06a9\u06d5\u0633 \u0646\u06d5\u0632\u0627\u0648\u06d5.',
    '\u0647\u06d5\u0631\u06af\u06cc\u0632 \u0647\u06cc\u0686 \u06a9\u06d5\u0633\u06cc\u0634 \u0646\u06d5\u06cc\u062a\u0648\u0627\u0646\u06cc\u0648\u06d5 \u0647\u06d5\u0645\u0628\u06d5\u0631\u06cc \u0628\u06ce.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u062e\u06c6 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0645\u060c \u0628\u06d5 \u0648\u06d5\u062f\u06cc \u0647\u06ce\u0646\u06cc \u0632\u0627 \u0644\u06d5 \u0645\u0627\u06a9\u060c',
    '\u0644\u06d5 \u0648\u06d5\u0632\u06d5\u0646\u06cc \u0647\u06d5\u0631\u0686\u06cc \u062f\u0631\u0648\u0633\u062a\u06cc \u06a9\u0631\u062f\u0648\u0648\u06d5\u061b',
    '\u0644\u06d5 \u0695\u0648\u0634\u06d5\u06cc \u0634\u06d5\u0648\u060c \u06a9\u0627\u062a\u06ce \u062a\u0627\u0631\u06cc\u06a9\u0627\u06cc\u06cc \u062f\u0627\u062f\u06ce\u061b',
    '\u0644\u06d5 \u062a\u06c6\u0634\u06cc \u062c\u0627\u062f\u0648\u06af\u06d5\u0631\u0627\u0646\u06cc \u067e\u0641 \u062f\u06d5\u06a9\u06d5\u0646\u06d5 \u0686\u06d5\u0646\u062f \u06af\u0631\u06ce\u06cc\u06d5\u06a9\u061b',
    '\u0644\u06d5 \u06af\u06ce\u0686\u06d5\u06b5\u06cc \u0626\u06ce\u0631\u06d5\u06cc\u06cc \u0628\u06d5\u0631\u060c \u0648\u06d5\u062e\u062a\u06ce \u06a9\u06d5 \u0626\u06ce\u0631\u06d5\u06cc\u06cc \u062f\u06d5\u0628\u0627.',
    '\u0628\u06ce\u0698\u06d5: \u0645\u0646 \u062e\u06c6 \u062f\u06d5\u067e\u0627\u0631\u06ce\u0632\u0645 \u0628\u06d5 \u062e\u0648\u062f\u0627\u0646\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5\u060c',
    '\u0641\u06d5\u0631\u0645\u0627\u0646\u0695\u06d5\u0648\u0627\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5\u060c',
    '\u062e\u0648\u062f\u0627\u06cc [\u06af\u06d5\u0648\u0631\u06d5\u06cc] \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5\u060c',
    '\u0644\u06d5 \u0628\u06d5\u06b5\u0627\u06cc \u0648\u0627\u0632\u0648\u0627\u0632\u06cc\u0647\u06ce\u0646\u06cc \u062e\u06c6\u067e\u06d5\u0646\u0627\u062f\u06d5\u0631\u061b',
    '\u0626\u06d5\u0648\u06cc \u062f\u06b5\u06cc \u0626\u06d5\u0645 \u0645\u06d5\u0631\u062f\u0645\u06d5 \u06a9\u0631\u0645\u06ce \u062f\u06d5\u06a9\u0627\u061b',
    '\u0644\u06d5 \u062c\u0646\u06c6\u06a9\u06d5 \u0648 \u0644\u06d5 \u0645\u06d5\u0631\u062f\u0645\u06cc\u0634.',
  ],
};
export default hazhar;
